var book = {
	"name": "1 Letopisu",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<span>1</span> Adam, Šét, Enóš,",
			"2": "<span>2</span> Kénan, Mahalalel, Jered,",
			"3": "<span>3</span> Henoch, Metúšelach, Lámech,",
			"4": "<span>4</span> Noe, Šém, Chám a Jefet.",
			"5": "<span>5</span> Synové Jefetovi: Gomer a Mágog a Mádaj, Jávan a Túbal, Mešek a Tíras.",
			"6": "<span>6</span> Synové Gomerovi: Aškenaz, Dífat a Togarma.",
			"7": "<span>7</span> Synové Jávanovi: Elíša a Taršíš, Kitejci a Ródanci.",
			"8": "<span>8</span> Synové Chámovi: Kúš a Misrajim, Pút a Kenaan.",
			"9": "<span>9</span> Synové Kúšovi: Seba, Chavila a Sabta, Raema a Sabteka. Synové Raemovi: Šeba a Dedán.",
			"10": "<span>10</span> Kúš pak zplodil Nimroda; ten se stal na zemi prvním bohatýrem.",
			"11": "<span>11</span> Misrajim zplodil Lúďany a Anámce, Lehábany a Naftúchany",
			"12": "<span>12</span> i Patrúsany a Kaslúchany - z nich vzešli Pelištejci - a Kaftórce.",
			"13": "<span>13</span> Kenaan zplodil Sidóna, svého prvorozeného, a Chéta,",
			"14": "<span>14</span> Jebúsejce, Emorejce a Girgašejce",
			"15": "<span>15</span> též Chivejce, Arkejce a Síňana,",
			"16": "<span>16</span> Arváďana, Semárce a Chamáťana.",
			"17": "<span>17</span> Synové Šémovi: Élam a Ašúr, Arpakšád a Lúd a Aram, Ús a Chůl, Geter a Mešek.",
			"18": "<span>18</span> Arpakšád zplodil Šelacha a Šelach zplodil Hebera.",
			"19": "<span>19</span> Heberovi se narodili dva synové, jméno jednoho bylo Peleg (to je Rozčlenění), neboť za jeho dnů byla zeně rozčleněna, a jméno jeho bratra bylo Joktán.",
			"20": "<span>20</span> Joktán pak zplodil Almódada a Šelefa, Chasarmáveta a Jeracha,",
			"21": "<span>21</span> Hadórama, Úzala a Diklu,",
			"22": "<span>22</span> Ébala, Abímaela a Šebu,",
			"23": "<span>23</span> Ofíra, Chavílu a Jóbaba; ti všichni jsou synové Joktánovi.",
			"24": "<span>24</span> Šém, Arpakšád, Šelach,",
			"25": "<span>25</span> Heber, Peleg, Reú,",
			"26": "<span>26</span> Serúg, Náchor, Terach,",
			"27": "<span>27</span> Abram, to je Abraham.",
			"28": "<span>28</span> Synové Abrahamovi: Izák a Izmael.",
			"29": "<span>29</span> Toto jsou jejich rodopisy: Izmaelův prvorozený Nebajót, dále Kédar a Abdeel a Mibsám,",
			"30": "<span>30</span> Mišma a Dúma a Masa, Chadad a Téma,",
			"31": "<span>31</span> Jetúr, Náfiš a Kedma. To jsou synové Izmaelovi.",
			"32": "<span>32</span> Synové Abrahamovy ženiny Ketúry: ta porodila Zimrána a Jokšána, Medána a Midjána, Jišbáka a Šúacha. Synové Jokšánovi: Šeba a Dedán.",
			"33": "<span>33</span> Synové Midjánovi: Éfa a Éfer a Chanók, Abída a Eldáa; ti všichni jsou synové Ketúřini.",
			"34": "<span>34</span> Abraham zplodil Izáka. Synové Izákovi: Ezau a Izrael.",
			"35": "<span>35</span> Synové Ezauovi: Elífaz, Reúel a Jeúš, Jaelam a Kórach.",
			"36": "<span>36</span> Synové Elífazovi: Téman, Ómar, Sefí a Gátam, Kénaz a Timna a Amálek.",
			"37": "<span>37</span> Synové Reúelovi: Nachat, Zerach, Šama a Miza.",
			"38": "<span>38</span> Synové Seírovi: Lótan a Šóbal, Sibeón a Ana a Dišón, Eser a Díšan.",
			"39": "<span>39</span> Synové Lótanovi: Chorí a Hómam; Lótanova sestra byla Timna.",
			"40": "<span>40</span> Synové Šóbalovi: Alján a Manachat a Ébal, Šefí a Ónam. Synové Sibeónovi: Aja a Ana.",
			"41": "<span>41</span> Synové Anovi: Dišón. Synové Dišónovi: Chamrán a Ešbán, Jitrán a Keran.",
			"42": "<span>42</span> Synové Eserovi: Bilhán a Zaavan a Jaakan. Synové Díšanovi: Ús a Aran.",
			"43": "<span>43</span> Toto jsou králové, kteří kralovali v edómské zemi, dříve než kraloval král synům izraelským: Bela, syn Beórův, a jeho město se jmenovalo Dinhaba.",
			"44": "<span>44</span> Když Bela zemřel, stal se po něm králem Jóbab, syn Zerachův, z Bosry.",
			"45": "<span>45</span> Když zemřel Jóbab, stal se po něm králem Chušam z témanské země.",
			"46": "<span>46</span> Když zemřel Chušam, stal se po něm králem Hadad, syn Bedadův, který porazil Midjána na Moábském poli. Jeho město se jmenovalo Avít.",
			"47": "<span>47</span> Když zemřel Hadad, stal se po něm králem Samla z Masreky.",
			"48": "<span>48</span> Když zemřel Samla, stal se po něm králem Šaúl z Rechobótu nad Řekou.",
			"49": "<span>49</span> Když zemřel Šaúl, stal se po něm králem Baal-chanan, syn Akbórův.",
			"50": "<span>50</span> Když zemřel Baal-chanan, stal se po něm králem Hadad. Jeho město se jmenovalo Paí a jméno jeho ženy bylo Mehetabel; byla to dcera Matredy, vnučka Mé-zahabova.",
			"51": "<span>51</span> Když Hadad zemřel, stali se pohlaváry Edómu: pohlavár Timna, pohlavár Alja, pohlavár Jetet,",
			"52": "<span>52</span> pohlavár Oholíbama, pohlavár Ela, pohlavár Pínon,",
			"53": "<span>53</span> pohlavár Kenaz, pohlavár Téman, pohlavár Mibsár,",
			"54": "<span>54</span> pohlavár Magdíel a pohlavár Iram. To byli edómští pohlaváři."
		},
		"2": {
			"1": "<span>1</span> Toto jsou synové Izraelovi: Rúben, Šimeón, Lévi a Juda, Isachar a Zabulón,",
			"2": "<span>2</span> Dan, Josef a Benjamín, Neftalí, Gád a Ašer.",
			"3": "<span>3</span> Synové Judovi: Ér, Ónan a Šela; tito tři se mu narodili z dcery Šúovy, Kenaanky. Judův prvorozený Ér však byl v očích Hospodinových zlý, a proto jej Hospodin usmrtil.",
			"4": "<span>4</span> Judova snacha Támar mu porodila Peresa a Zeracha. Všech Judových synů bylo pět.",
			"5": "<span>5</span> Synové Peresovi: Chesrón a Chámul.",
			"6": "<span>6</span> Synové Zerachovi: Zimrí, Étan a Héman, Kalkol a Dára, celkem pět.",
			"7": "<span>7</span> Synové Karmího: Akár (to je Zkáza), který přivedl na Izraele zkázu tím, že se dopustil zpronevěry při provádění klatby.",
			"8": "<span>8</span> Synové Étanovi: Azarjáš.",
			"9": "<span>9</span> Synové Chesrónovi, kteří se mu narodili: Jerachmeel, Rám a Kelúbaj.",
			"10": "<span>10</span> Rám zplodil Amínadaba a Amínadab zplodil Nachšóna, předáka Judovců.",
			"11": "<span>11</span> Nachšón zplodil Salmu a Salma zplodil Bóaza.",
			"12": "<span>12</span> Bóaz zplodil Obéda a Obéd zplodil Jišaje.",
			"13": "<span>13</span> Jišaj zplodil prvorozeného Elíaba, druhého Amínadaba, třetího Šimeu,",
			"14": "<span>14</span> čtvrtého Netaneela, pátého Radaje,",
			"15": "<span>15</span> šestého Osema, sedmého Davida",
			"16": "<span>16</span> a jejich sestry Serúju a Abígajilu. Synové Serújini: Abšaj, Jóab a Asáel; tito tři.",
			"17": "<span>17</span> Abígajil porodila Amasu; otec Amasův byl Jeter Izmaelský.",
			"18": "<span>18</span> Káleb, syn Chesrónův, zplodil tyto syny s manželkou Azúbou a s Jeriótou: Ješera, Šóbaba a Ardóna.",
			"19": "<span>19</span> Když Azúba zemřela, vzal si Káleb Efratu a ta mu porodila Chúra.",
			"20": "<span>20</span> Chúr zplodil Urího a Urí zplodil Besaleela.",
			"21": "<span>21</span> Potom Chesrón vešel k dceři Makíra, otce Gileádova, a vzal si ji; bylo mu šedesát let, když mu porodila Segúba.",
			"22": "<span>22</span> Segúb zplodil Jaíra; ten měl třiadvacet měst v gileádské zemi.",
			"23": "<span>23</span> Gešúr a Aram však jim vzali Jaírovy vesnice, Kenat a jeho osady, šedesát měst. Ti všichni jsou synové Makíra, otce Gileádova.",
			"24": "<span>24</span> Po Chesrónově smrti vešel Káleb k Efratě. Chesrónova manželka byla Abíja; porodila mu Ašchúra, otce Tekóje.",
			"25": "<span>25</span> Synové Jerachmeela, Chesrónova prvorozeného, byli: prvorozený Rám, pak Búna a Oren a Osen a Achijáš.",
			"26": "<span>26</span> Jerachmeel měl ještě druhou manželku, jménem Atáru; ta byla matkou Ónamovou.",
			"27": "<span>27</span> Synové Ráma, prvorozeného Jerachmeelova, byli: Maas, Jamín a Eker.",
			"28": "<span>28</span> Synové Ónamovi byli: Šamaj a Jáda. Synové Šamajovi: Nádab a Abíšur.",
			"29": "<span>29</span> Jméno Abíšurovy manželky bylo Abíchajil; ta mu porodila Achbána a Molída.",
			"30": "<span>30</span> Synové Nádabovi: Seled a Apajim. Seled zemřel bez synů.",
			"31": "<span>31</span> Synové Apajimovi: Jiší. Synové Jišího: Šešan. Synové Šešanovi: Achlaj.",
			"32": "<span>32</span> Synové Jády, Šamajova bratra: Jeter a Jónatan; Jeter zemřel bez synů.",
			"33": "<span>33</span> Synové Jónatanovi: Pelet a Záza. To byli synové Jerachmeelovi.",
			"34": "<span>34</span> Šešan neměl syny, jenom dcery. Měl však egyptského otroka, který se jmenoval Jarcha.",
			"35": "<span>35</span> I dal Šešan svému otroku Jarchovi za manželku svou dceru a ta mu porodila Ataje.",
			"36": "<span>36</span> Ataj zplodil Nátana a Nátan zplodil Zábada.",
			"37": "<span>37</span> Zábad zplodil Eflála a Eflál zplodil Obéda.",
			"38": "<span>38</span> Obéd zplodil Jehúa a Jehú zplodil Azarjáše.",
			"39": "<span>39</span> Azarjáš zplodil Chelesa a Cheles zplodil Eleásu.",
			"40": "<span>40</span> Eleása zplodil Sismaje a Sismaj zplodil Šalúma.",
			"41": "<span>41</span> Šalúm zplodil Jekamjáše a Jekamjáš zplodil Elíšamu.",
			"42": "<span>42</span> Synové Káleba, bratra Jerachmeelova: Méša, jeho prvorozený, ten byl otcem Zífa, a synové Maréši, otce Chebrónova.",
			"43": "<span>43</span> Synové Chebrónovi: Kórach, Tapúach, Rekem a Šema.",
			"44": "<span>44</span> Šema zplodil Rachama, otce Jorkoamova, a Rékem zplodil Šamaje.",
			"45": "<span>45</span> Syn Šamajův: Maón; Maón byl otec Bét-surův.",
			"46": "<span>46</span> Kálebova ženina Éfa porodila Chárana, Mósu a Gázeza. Cháran zplodil Gázeza.",
			"47": "<span>47</span> Synové Johdajovi: Regem, Jótam a Géšan, Pelet, Éfa a Šaaf.",
			"48": "<span>48</span> Kálebova ženina Maaka porodila Šebera a Tirchanu.",
			"49": "<span>49</span> Porodila též Šaafa, otce Madmany, Ševu, otce Makbeny i otce Gibeje; Kálebova dcera byla Aksa.",
			"50": "<span>50</span> To byli synové Kálebovi. Syn Chúra, prvorozeného Efratina: Šóbal, otec Kirjat-jearímu,",
			"51": "<span>51</span> Salma, otec Betléma, Cháref, otec Bét-gáderu.",
			"52": "<span>52</span> Syny Šóbala, otce Kirjat-jearímu, byli Haróe, polovina Menúchoťanů",
			"53": "<span>53</span> a čeledi kirjatjearímské: Jitrejci, Pútejci, Šumatejci a Mišraejci, z kterých vzešli Soreatejci a Eštaólci.",
			"54": "<span>54</span> Synové Salmovi: Betlémané a Netófané, Atróťané, Bét-joábané a polovina Manachaťanů a Sorejci.",
			"55": "<span>55</span> Čeledi sóferské, obývající Jaebes: Tireaťané, Šimeaťané, Súkaťané; to jsou Kinejci, pocházející z Chamata, otce Bét-rekabova."
		},
		"3": {
			"1": "<span>1</span> Toto jsou synové Davidovi, kteří se mu narodili v Chebrónu: prvorozený Amnón z Achínóamy Jizreelské, druhý Daníjel z Abígajily Karmelské,",
			"2": "<span>2</span> třetí Abšalóm, syn Maaky, dcery gešúrského krále Talmaje, čtvrtý Adoníjáš, syn Chagíty,",
			"3": "<span>3</span> pátý Šefatjáš z Abítaly, šestý Jitreám z jeho manželky Egly.",
			"4": "<span>4</span> Šest se mu jich narodilo v Chebrónu, kde kraloval sedm roků a šest měsíců, třicet tři roky pak kraloval v Jeruzalémě.",
			"5": "<span>5</span> A tito se mu narodili v Jeruzalémě: Simea, Sóbab, Nátan a Šalomoun, tito čtyři z Bat-šúy, dcery Amíelovy.",
			"6": "<span>6</span> Též Jibchár, Elíšáma a Elífelet,",
			"7": "<span>7</span> Nógah, Nefeg a Jafía,",
			"8": "<span>8</span> Elíšáma, Eljáda a Elífelet, těchto devět.",
			"9": "<span>9</span> Ti všichni jsou synové Davidovi kromě synů ženin; Támar byla jejich sestra.",
			"10": "<span>10</span> Syn Šalomounův byl Rechabeám a jeho syn byl Abijáš, jeho syn Ása, jeho syn Jóšafat,",
			"11": "<span>11</span> jeho syn Jóram, jeho syn Achazjáš, jeho syn Jóaš,",
			"12": "<span>12</span> jeho syn Amasjáš, jeho syn Azarjáš, jeho syn Jótam,",
			"13": "<span>13</span> jeho syn Achaz, jeho syn Chizkijáš, jeho syn Menaše,",
			"14": "<span>14</span> jeho syn Amón, jeho syn Jóšijáš.",
			"15": "<span>15</span> Synové Jóšijášovi: prvorozený Jóchanan, druhý Jójakím, třetí Sidkijáš, čtvrtý Šalúm.",
			"16": "<span>16</span> Synové Jójakímovi: jeho syn Jekonjáš, jeho syn Sidkijáš.",
			"17": "<span>17</span> Synové vězně Jekonjáše: jeho syn Šealtíel,",
			"18": "<span>18</span> dále Malkíram, Pedajáš a Šenasar, Jekamjáš, Hóšama a Nedabjáš.",
			"19": "<span>19</span> Synové Pedajášovi: Zerubábel a Šimeí. Zerubábelův syn byl Mešulám a Chananjáš a jejich sestra byla Šelomít.",
			"20": "<span>20</span> Dále Chašúba, Óhel, Berekjáš, Chasadjáš, Júšab-chesed, těchto pět.",
			"21": "<span>21</span> Syn Chananjášúv: Pelatjáš a Ješajáš, synové Refajášovi, synové Arnánovi, synové Obadjášovi, synové Šekanjášovi.",
			"22": "<span>22</span> Synové Šekanjášovi: Šemajáš. Synové Šemajášovi: Chatúš, Jigál a Baríach, Nearjáš a Šafat, těchto šest.",
			"23": "<span>23</span> Syn Nearjášův: Eljóenaj, Chizkijáš a Azríkam, tito tři.",
			"24": "<span>24</span> Synové Eljóenajovi: Hódavjáš, Eljašíb a Pelajáš, Akúb a Jóchanan, Delajáš a Ananí, těchto sedm."
		},
		"4": {
			"1": "<span>1</span> Synové Judovi: Peres, Chesrón a Karmí, Chúr a Šóbal.",
			"2": "<span>2</span> Reajáš, syn Šóbalův, zplodil Jachata a Jachat zplodil Achúmaje a Lahada. To jsou čeledi soreatejské.",
			"3": "<span>3</span> Z otce Étama jsou tito: Jizreel, Jišma a Jidbáš; jejich sestra se jmenovala Haslelponí.",
			"4": "<span>4</span> Penúel byl otec Gedóra a Ezer otec Chušího. To jsou synové Chúra, prvorozeného Efratina, otce Betléma.",
			"5": "<span>5</span> Ašchúr, otec Tekóje, měl dvě manželky: Cheleu a Naaru.",
			"6": "<span>6</span> Naara mu porodila Achuzama, Chefera, Témního a Achaštarího. To jsou synové Naařini.",
			"7": "<span>7</span> Synové Cheleini: Seret, Jesochar a Etnán.",
			"8": "<span>8</span> Kós zplodil Anúba a Sóbebu a čeledi Acharchela, syna Harumova.",
			"9": "<span>9</span> Jaebes byl váženější než jeho bratři; jeho matka ho pojmenovala Jaebes se slovy: \"Porodila jsem ho s trápením.\"",
			"10": "<span>10</span> Jaebes vzýval Boha Izraele takto: \"Kéž bys mi dal požehnání a rozmnožil mé území! Kéž by tvá ruka byla se mnou a zbavila mě zlého, aby na mne nedolehlo trápení!\" A Bůh splnil, oč žádal.",
			"11": "<span>11</span> Kelúb, bratr Šúchův, zplodil Mechíra; ten je otec Eštónův.",
			"12": "<span>12</span> Eštón zplodil Bét-ráfu, Paseacha a Techinu, otce Ír-nachaše. To jsou mužové Réky.",
			"13": "<span>13</span> Synové Kenazovi: Otníel a Serajáš. Synové Otníelovi: Chatat.",
			"14": "<span>14</span> Meónótaj zplodil Ofru a Serajáš zplodil Jóaba, otce Gé-charašímu (to je Údolí řemeslníků); byli tam totiž řemeslníci.",
			"15": "<span>15</span> Synové Káleba, syna Jefunova: Irú, Éla a Naam. Synové Élovi: Kenaz.",
			"16": "<span>16</span> Synové Jehalelélovi: Zíf a Zífa, Tírja a Asarel.",
			"17": "<span>17</span> Syn Ezrův: Jeter a Mered, Efer a Jalón. Jeter zplodil Mirjama a Šamaje a Jišbacha, otce Eštemóova.",
			"18": "<span>18</span> Jeho manželka Jehúdija porodila Jereda, otce Gedóru, a Chebera, otce Sóka, a Jekutíela, otce Zanóachu. To jsou synové Bitji, dcery faraónovy, kterou pojal Mered.",
			"19": "<span>19</span> Synové Hódijášovy manželky, sestry Nachama, otce Keílova: Hagarmí a Eštemóa Maakatský.",
			"20": "<span>20</span> Synové Šímónovi: Amnón a Rina, Ben-chanan a Tilón. Synové Jišího: Zóchet a Ben-zóchet.",
			"21": "<span>21</span> Synové Šély, syna Judova: Ér, otec Léky, a Laeda, otec Maréši, a čeledi pracující v dílně na bělostné plátno v Bét-ašbéji,",
			"22": "<span>22</span> Jokím a mužové Kozeby a Jóaš a Sáraf, kteří opanovali Moába, a Jašúbí-lechem. To jsou starobylé příběhy.",
			"23": "<span>23</span> Byli to hrnčíři, sadaři a vinaři, sídlili tam u krále v jeho službách.",
			"24": "<span>24</span> Synové Šimeónovi: Nemúel a Jamín, Jaríb, Zerach a Šaúl,",
			"25": "<span>25</span> jeho syn Šalúm, jeho syn Mibsám, jeho syn Mišma.",
			"26": "<span>26</span> Synové Mišmovi: jeho syn Chamúel, jeho syn Zakúr, jeho syn Šimeí.",
			"27": "<span>27</span> Šimeí měl šestnáct synů a šest dcer. Jeho bratři neměli mnoho synů; celá jejich čeleď se nerozmnožila tak jako Judovci.",
			"28": "<span>28</span> Sídlili v Beer-šebě, Móladě a Chasar-šúalu,",
			"29": "<span>29</span> v Bilze, v Esemu a v Tóladu,",
			"30": "<span>30</span> v Bétúelu, Chormě a Siklagu,",
			"31": "<span>31</span> v Bét-markabótu, v Chasar-súsimu, v Bét-bireji a v Šaarajimu. To byla jejich města až do Davidova kralování.",
			"32": "<span>32</span> Jejich dvorce byly v Étamu a Ajinu, v Rimónu, v Tokenu a v Ašanu, v pěti městech.",
			"33": "<span>33</span> Všechny jejich dvorce, které ležely kolem těchto měst až k Baalu, byly jejich sídlišti; byly zaznamenány v rodovém seznamu.",
			"34": "<span>34</span> Dále to byl Mešóbab a Jamlék a Jóša, syn Amasjášův",
			"35": "<span>35</span> Jóel a Jehú, syn Jóšibjáše, syna Serajáše, syna Asíelova,",
			"36": "<span>36</span> a Eljóenaj, Jaakoba a Ješóchajáš, Asajáš a Adíel, Jesímiel a Benajáš",
			"37": "<span>37</span> a Zíza, syn Šifího, syna Alóna, syna Jedajáše, syna Šimrího, syna Šemajášova.",
			"38": "<span>38</span> Tito jmenovitě uvedení byli předáky ve svých čeledích; jejich otcovský rod se mnohonásobně rozmohl.",
			"39": "<span>39</span> Proto šli směrem ke Gedóru na východ od údolí, aby hledali pastvu pro svůj brav.",
			"40": "<span>40</span> Našli pastvu tučnou a dobrou a zemi na všechny strany otevřenou, poklidnou a pokojnou; shledali, že tamní předešlí obyvatelé pocházejí z Cháma.",
			"41": "<span>41</span> Tito jmenovitě zapsaní přišli za dnů judského krále Jechizkijáše a pobořili jejich stany i příbytky, které tam nalezli, zničili je jako klaté, jak je tomu dodnes. Usadili se tam místo nich, protože tam byla pastva pro jejich brav.",
			"42": "<span>42</span> Dále z nich, totiž ze Šimeónovců, odešlo pět set mužů do pohoří Seíru; v jejich čele byli Pelatjáš, Nearjáš, Refajáš a Uzíel, synové Jišeího.",
			"43": "<span>43</span> Pobili pozůstatek Amálekovců, kteří kdysi vyvázli; tam sídlí dodnes."
		},
		"5": {
			"1": "<span>1</span> Synové Rúbena, prvorozeného syna Izraelova; byl sice prvorozený, ale když poskvrnil lože svého otce, bylo jeho prvorozenství přeneseno na syny Josefa, syna Izraelova, takže zůstal bez zápisu prvorozenství do rodového seznamu.",
			"2": "<span>2</span> Převahu mezi svými bratry měl Juda; z něho měl vzejít vévoda, ale prvorozenství náleželo Josefovi.",
			"3": "<span>3</span> Synové Rúbena, prvorozeného syna Izraelova: Chanók a Palú, Chesrón a Karmí.",
			"4": "<span>4</span> Synové Jóelovi: jeho syn Šemajáš, jeho syn Góg, jeho syn Šimeí,",
			"5": "<span>5</span> jeho syn Míka, jeho syn Reajáš, jeho syn Baal",
			"6": "<span>6</span> a jeho syn Beera, kterého dal přestěhovat asyrský král Tilgat-pilneser; byl rúbenským předákem.",
			"7": "<span>7</span> Jeho bratři podle čeledí, zapsaní v rodových seznamech podle svých rodopisů: náčelník Jeíel a Zekarjáš",
			"8": "<span>8</span> a Bela, syn Azaza, syna Šemy, syna Jóelova; ten sídlil v Aróeru a až k Nebó a Baal-meónu;",
			"9": "<span>9</span> na východě pak sídlil až k okraji stepi při řece Eufratu; jejich stáda se totiž v gileádské zemi rozmnožila.",
			"10": "<span>10</span> Za dnů Saulových vedli válku s Hagrejci, kteří jim padli do rukou; pak se usadili v jejich stanech po celé východní straně Gileádu.",
			"11": "<span>11</span> Gádovci sídlili naproti nim v bášanské zemi až k Salce.",
			"12": "<span>12</span> Jóel byl náčelník a Šafam druhý po něm, pak Jaenaj a Šafat v Bášanu.",
			"13": "<span>13</span> Jejich bratři po otcovských rodech: Míkael, Mešulám a Šeba, Jóraj a Jaekán, Zía a Eber, těchto sedm.",
			"14": "<span>14</span> To byli synové Abíchajila, syna Chúrího, syna Jaróacha, syna Gileáda, syna Míkaela, syna Ješíšaje, syna Jachdóa, syna Búzova.",
			"15": "<span>15</span> Achí, syn Abdíela, syna Gúního, byl náčelník otcovského rodu.",
			"16": "<span>16</span> Sídlili v Gileádu, v Bášanu a jeho vesnicích a na všech pastvinách šáronských, kam až vybíhají.",
			"17": "<span>17</span> Ti všichni byli zapsáni do rodových seznamů za dnů judského krále Jótama a za dnů izraelského krále Jarobeáma.",
			"18": "<span>18</span> Rúbenovců, Gádovců a polovice Manasesova kmene, udatných mužů nosících štít a meč a lučištníků vycvičených k boji, schopných vycházet do boje, bylo čtyřicet čtyři tisíce sedm set šedesát.",
			"19": "<span>19</span> Vedli boj s Hagrejci, s Jetúrem, s Nafíšem a s Nódabem.",
			"20": "<span>20</span> Dostalo se jim proti nim pomoci, takže jim byli vydáni do rukou Hagarejci i všichni jejich spojenci; úpěli v boji k Bohu a on jejich prosby přijal, protože v něho doufali.",
			"21": "<span>21</span> Odvedli jejich stáda: padesát tisíc velbloudů, dvě stě padesát tisíc kusů bravu a dva tisíce oslů i sto tisíc osob.",
			"22": "<span>22</span> Padlých, skolených mečem, bylo mnoho, neboť ten boj byl od Boha. Sídlili tam pak místo nich až do zajetí.",
			"23": "<span>23</span> Synové poloviny kmene Manasesova sídlili v zemi od Bášánu k Baal-chermónu, totiž k Seníru a Chermónskému pohoří; bylo jich mnoho.",
			"24": "<span>24</span> Toto byli náčelníci otcovského rodu: Efer, Jišeí, Elíel a Azríel, Jirmejáš a Hódavjáš a Jachdíel, stateční bohatýři, mužové proslulí, náčelníci otcovských rodů.",
			"25": "<span>25</span> Avšak zpronevěřili se Bohu svých otců a smilnili s božstvy národů země, které Bůh před nimi vyhladil.",
			"26": "<span>26</span> Bůh Izraele tedy probudil ducha asyrského krále Púla, totiž ducha asyrského krále Tilgat-pilnesera; ten je dal přestěhovat, totiž Rúbenovce, Gádovce a polovinu kmene Manasesova, a přivedl je k Chalachu, Chabóru a Háře a k řece Gózanu, kde jsou dodnes. [ (I Chronicles 5:27) Synové Léviho: Geršón, Kehat a Merarí. ] [ (I Chronicles 5:28) Synové Kehatovi: Amrám, Jishár, Chebrón a Uzíel. ] [ (I Chronicles 5:29) Synové Amrámovi: Áron a Mojžíš; a Mirjam. Synové Áronovi: Nádab a Abíhú, Eleazar a Ítamar. ] [ (I Chronicles 5:30) Eleazar zplodil Pinchasa, Pinchas zplodil Abíšúu, ] [ (I Chronicles 5:31) Abíšúa zplodil Bukího, Bukí zplodil Uzího, ] [ (I Chronicles 5:32) Uzí zplodil Zerachjáše, Zerachjáš zplodil Merajóta, ] [ (I Chronicles 5:33) Merajót zplodil Amarjáše, Amarjáš zplodil Achítúba, ] [ (I Chronicles 5:34) Achítúb zplodil Sádoka, Sádok zplodil Achímaasa, ] [ (I Chronicles 5:35) Achímaas zplodil Azarjáše, Azarjáš zplodil Jóchanana, ] [ (I Chronicles 5:36) Jóchanan zplodil Azarjáše; to je ten, který sloužil jako kněz v domě, jejž vybudoval Šalomoun v Jeruzalémě. ] [ (I Chronicles 5:37) Azarjáš zplodil Amarjáše, Amarjáš zplodil Achítúba, ] [ (I Chronicles 5:38) Achítúb zplodil Sádoka, Sádok zplodil Šalúma, ] [ (I Chronicles 5:39) Šalúm zplodil Chilkijáše, Chilkijáš zplodil Azarjáše, ] [ (I Chronicles 5:40) Azarjáš zplodil Serajáše a Serajáš zplodil Jósadaka; ] [ (I Chronicles 5:41) Jósadak pak šel do zajetí, když Hospodin prostřednictvím Nebúkadnesara dal přestěhovat Judu a Jeruzalém. ]"
		},
		"6": {
			"1": "<span>1</span> Synové Léviho: Geršóm, Kehat a Merarí.",
			"2": "<span>2</span> Toto jsou jména synů Geršómových: Libní a Šimeí.",
			"3": "<span>3</span> Synové Kehatovi: Amrám, Jishár, Chebrón a Uzíel.",
			"4": "<span>4</span> Synové Merarího: Machlí a Muší. To jsou čeledi Léviho podle otcovských rodů.",
			"5": "<span>5</span> Ke Geršómovi patří: jeho syn Libní, jeho syn Jachat, jeho syn Zima,",
			"6": "<span>6</span> jeho syn Jóach, jeho syn Idó, jeho syn Zerach, jeho syn Jeotraj.",
			"7": "<span>7</span> Synové Kehatovi: jeho syn Amínadab, jeho syn Kórach, jeho syn Asír,",
			"8": "<span>8</span> jeho syn Elkána, jeho syn Ebjásaf, jeho syn Asír,",
			"9": "<span>9</span> jeho syn Tachat, jeho syn Uríel, jeho syn Uzijáš, jeho syn Šaúl.",
			"10": "<span>10</span> A synové Elkánovi: Amasaj a Achímót.",
			"11": "<span>11</span> Elkána: Synové Elkánovi: jeho syn Sófaj, jeho syn Nachat,",
			"12": "<span>12</span> jeho syn Elíab, jeho syn Jerócham, jeho syn Elkána.",
			"13": "<span>13</span> A synové Samuelovi: prvorozený Vašní a Abijáš.",
			"14": "<span>14</span> Synové Merarího: Machlí, jeho syn Libní, jeho syn Šimeí, jeho syn Uza,",
			"15": "<span>15</span> jeho syn Šimea, jeho syn Chagijáš, jeho syn Asajáš.",
			"16": "<span>16</span> Toto jsou ti, které ustanovil David, aby zpívali v domě Hospodinově od chvíle, kdy tam spočinula schrána.",
			"17": "<span>17</span> Přisluhovali zpěvem před příbytkem stanu setkávání až do doby, kdy Šalomoun vybudoval v Jeruzalémě dům Hospodinův. Ve své službě stáli podle svého pořádku.",
			"18": "<span>18</span> Ti, kteří tu stáli, a jejich synové: Ze synů Kehatových: Héman, zpěvák, syn Jóela, syna Samuela,",
			"19": "<span>19</span> syna Elkány, syna Jeróchama, syna Elíela, syna Tóacha,",
			"20": "<span>20</span> syna Súfa, syna Elkány, syna Machata, syna Amasaje,",
			"21": "<span>21</span> syna Elkány, syna Jóela, syna Azarjáše, syna Sefanjáše,",
			"22": "<span>22</span> syna Tachata, syna Asíra, syna Ebjásafa, syna Kóracha,",
			"23": "<span>23</span> syna Jishára, syna Kehata, syna Léviho, syna Izraelova.",
			"24": "<span>24</span> A jeho bratr Asaf, stojící mu po pravici, Asaf, syn Berekjáše, syna Šimey,",
			"25": "<span>25</span> syna Míkaela, syna Baasejáše, syna Malkijáše,",
			"26": "<span>26</span> syna Etního, syna Zeracha, syna Adajáše,",
			"27": "<span>27</span> syna Étana, syna Zimy, syna Šimeího,",
			"28": "<span>28</span> syna Jachata, syna Geršóma, syna Léviho.",
			"29": "<span>29</span> A jejich bratří, synové Merarího, kteří jim stáli po levici: Étan, syn Kišího, syna Abdího, syna Malúka,",
			"30": "<span>30</span> syna Chašabjáše, syna Amasjáše, syna Chilkijáše,",
			"31": "<span>31</span> syna Amsího, syna Baního, syna Šemera,",
			"32": "<span>32</span> syna Machlího, syna Mušího, syna Merarího, syna Léviho.",
			"33": "<span>33</span> A jejich bratří Lévijci byli pověřeni veškerou službou při příbytku Božího domu.",
			"34": "<span>34</span> Áron pak a jeho synové obraceli v dým, co bylo přinášeno na oltář pro zápalné oběti a na oltář pro kadidlo při veškerém díle ve velesvatyni, a vykonávali smírčí obřady za Izraele podle všeho toho, co přikázal Mojžíš, Boží služebník.",
			"35": "<span>35</span> Toto jsou synové Áronovi: jeho syn Eleazar, jeho syn Pinchas, jeho syn Abíšua,",
			"36": "<span>36</span> jeho syn Bukí, jeho syn Uzí, jeho syn Zerachjáš,",
			"37": "<span>37</span> jeho syn Merajót, jeho syn Amarjáš, jeho syn Achítúb,",
			"38": "<span>38</span> jeho syn Sádok, jeho syn Achímaas.",
			"39": "<span>39</span> A toto jsou jejich sídliště při jejich hradištích na jejich území, sídliště pro syny Áronovy, pro čeleď kehatskou, neboť jim připadl první los.",
			"40": "<span>40</span> Dali jim Chebrón v zemi judské s okolními pastvinami.",
			"41": "<span>41</span> Avšak pole u města a dvorce dali Kálebovi, synu Jefunovu.",
			"42": "<span>42</span> Synům Áronovým dali útočištná města Chebrón, Libnu s pastvinami, Jatír, Eštemóu s pastvinami,",
			"43": "<span>43</span> Chílez s pastvinami, Debír s pastvinami,",
			"44": "<span>44</span> Ašán s pastvinami a Bét-šemeš s pastvinami.",
			"45": "<span>45</span> Od pokolení Benjamínova dostali Gebu s pastvinami, Alemet s pastvinami a Anatót s pastvinami. Všech jejich měst pro jejich čeledi bylo třináct.",
			"46": "<span>46</span> Zbývající Kehatovci podle čeledí svého pokolení dostali losem deset měst od poloviny pokolení Manasesova.",
			"47": "<span>47</span> Geršómovci dostali pro své čeledi třináct měst od pokolení Isacharova, od pokolení Ašerova, od pokolení Neftalíova a od pokolení Manasesova v Bášanu.",
			"48": "<span>48</span> Meraríovci dostali losem pro své čeledi dvanáct měst od pokolení Rúbenova, od pokolení Gádova a od pokolení Zabulónova.",
			"49": "<span>49</span> Izraelci dali Léviovcům města s pastvinami.",
			"50": "<span>50</span> Od pokolení Judovců, od pokolení Šimeónovců a od pokolení Benjamínovců jim losem přidělili tato města, která uvedli jmenovitě.",
			"51": "<span>51</span> Některé z čeledí Kehatovců dostaly města od pokolení Efrajimova na jeho území.",
			"52": "<span>52</span> Přidělili jim útočištná města Šekem s pastvinami v pohoří Efrajimském, Gezer s pastvinami,",
			"53": "<span>53</span> Jokmeám s pastvinami, Bét-chorón s pastvinami,",
			"54": "<span>54</span> Ajalón s pastvinami a Gat-rimón s pastvinami.",
			"55": "<span>55</span> Od poloviny pokolení Manasesova dostaly zbývající čeledi Kehatovců Anér s pastvinami a Bileám s pastvinami.",
			"56": "<span>56</span> Geršómovci podle čeledí dostali od poloviny pokolení Manasesova Gólan v Bášanu s pastvinami a Aštarót s pastvinami.",
			"57": "<span>57</span> Od pokolení Isacharova Kedeš s pastvinami, Dobrat s pastvinami,",
			"58": "<span>58</span> Rámot s pastvinami a Aném s pastvinami.",
			"59": "<span>59</span> Od pokolení Ašerova Mášal s pastvinami, Abdón s pastvinami,",
			"60": "<span>60</span> Chúkok s pastvinami a Rechób s pastvinami.",
			"61": "<span>61</span> Od pokolení Neftalího Kedeš v Galileji s pastvinami, Chamón s pastvinami a Kirjatajim s pastvinami.",
			"62": "<span>62</span> Zbývající Meraríovci dostali od pokolení Zabulónova Rimónó s pastvinami, Tábor s pastvinami,",
			"63": "<span>63</span> na druhé straně Jordánu pak, na východ od Jordánu, proti Jerichu, od pokolení Rúbenova Beser ve stepi s pastvinami, Jahsu s pastvinami,",
			"64": "<span>64</span> Kedemót s pastvinami a Mefaat s pastvinami.",
			"65": "<span>65</span> A od pokolení Gádova Rámot v Gileádu s pastvinami, Machanajim s pastvinami,",
			"66": "<span>66</span> Chešbón s pastvinami a Jaezer s pastvinami."
		},
		"7": {
			"1": "<span>1</span> K synům Isacharovým patřil Tóla a Púa, Jašúb a Šimrón, tito čtyři.",
			"2": "<span>2</span> Synové Tólovi: Uzí, Refajáš, Jeríel, Jachmaj, Jibsám a Šemúel, náčelníci otcovských rodů Tólových, stateční bohatýři, podle svých rodopisů; jejich počet za dnů Davidových činil dvacet dva tisíce šest set.",
			"3": "<span>3</span> Synové Uzího: Jizrachjáš. Synové Jizrachjášovi: Míkael, Obadjáš, Jóel, Jišijáš, těch pět, všichni náčelníci.",
			"4": "<span>4</span> Při nich bylo třicet šest tisíc mužů ve vojenských bojových houfech, podle svých rodopisů po otcovských rodech; měli totiž mnoho žen a synů.",
			"5": "<span>5</span> A jejich bratří, statečných bohatýrů, po všech čeledech Isacharových bylo osmdesát sedm tisíc; všichni byli zapsáni do seznamů rodů.",
			"6": "<span>6</span> Benjamín: Bela, Beker a Jedíael, tito tři.",
			"7": "<span>7</span> A synové Belovi: Esbón, Uzí, Uzíel, Jerimót a Írí, těchto pět, náčelníci otcovských rodů, stateční bohatýři; do seznamu rodů jich bylo zapsáno dvacet dva tisíce třicet čtyři.",
			"8": "<span>8</span> Synové Bekerovi: Zemíra, Jóaš, Elíezer, Eljóenaj, Omrí, Jeremót, Abijáš, Anatót a Alemet; tito všichni jsou synové Bekerovi.",
			"9": "<span>9</span> Do seznamu rodů, podle svých rodopisů byli zapsáni jako náčelníci otcovských rodů, stateční bohatýři, celkem dvacet tisíc dvě stě.",
			"10": "<span>10</span> Synové Jedíaelovi: Bilhán. A synové Bilhánovi: Jeúš, Binjamín, Ehúd, Kenaana, Zétan, Taršíš a Achíšachar.",
			"11": "<span>11</span> Tito všichni jsou synové Jedíaelovi, podle náčelníků rodů, stateční bohatýři, sedmnáct tisíc dvě stě těch, kteří byli schopní k vojenské službě a vycházet do boje.",
			"12": "<span>12</span> Synové Írovi: Šupím a Chupím. Synové Achérovi: Chuším.",
			"13": "<span>13</span> Synové Neftalího: Jachasíel, Gúní, Jeser a Šalúm, synové Bilhy.",
			"14": "<span>14</span> Synové Manasesovi: Asríel, kterého mu porodila jeho žena; jeho aramejská ženina porodila Makíra, otce Gileádova.",
			"15": "<span>15</span> A Makír vzal ženu pro Chupíma a Šupíma; jméno jeho sestry bylo Maaka. Jméno druhorozeného bylo Selofchad; a Selofchad měl jen dcery.",
			"16": "<span>16</span> Maaka, žena Makírova, porodila syna a pojmenovala ho Pereš; jeho bratr se jmenoval Šereš; a jeho synové: Úlam a Rekem.",
			"17": "<span>17</span> Synové Úlamovi: Bedán. To jsou synové Gileáda, syna Makírova, syna Manasesova.",
			"18": "<span>18</span> Jeho sestra Moleket porodila Íšhóda, Abíezera a Machlu.",
			"19": "<span>19</span> A synové Šemídovi byli Achján, Šekem, Likchí a Aníam.",
			"20": "<span>20</span> Synové Efrajimovi: Šútelach, jeho syn Bered, jeho syn Tachat, jeho syn Eleada, jeho syn Tachat,",
			"21": "<span>21</span> jeho syn Zábad, jeho syn Šútelach, Ezer a Elead. Ale povraždili je domorodci, muži z Gatu, neboť přitáhli, aby zabrali jejich stáda.",
			"22": "<span>22</span> Po mnoho dnů truchlil jejich otec Efrajim a jeho bratři přišli, aby ho potěšili.",
			"23": "<span>23</span> Pak vešel ke své ženě, ta otěhotněla a porodila syna; dal mu jméno Bería (to je V neštěstí), neboť byla v jeho domě v neštěstí.",
			"24": "<span>24</span> Jeho dcerou byla Šeera; ta vybudovala Bét-chorón Dolní a Horní a Uzen-šeeru.",
			"25": "<span>25</span> A jeho syn byl Refach a Rešef, jeho syn Telach, jeho syn Tachan,",
			"26": "<span>26</span> jeho syn Laedán, jeho syn Amíhud, jeho syn Elíšama,",
			"27": "<span>27</span> jeho syn Nón, jeho syn Jozue.",
			"28": "<span>28</span> Jejich trvalé vlastnictví a sídliště: Bét-el s vesnicemi, na východě Naarán, na západě Gezer s vesnicemi, Šekem s vesnicemi, až do Aje s vesnicemi.",
			"29": "<span>29</span> V rukou synů Manasesových byl Bét-šeán s vesnicemi, Taenak s vesnicemi, Megido s vesnicemi, Dór s vesnicemi. V těch místech sídlili synové Josefa, syna Izraelova.",
			"30": "<span>30</span> Synové Ašerovi: Jimna, Jišva, Jišví, Bería a jejich sestra Serach.",
			"31": "<span>31</span> Synové Beríovi: Cheber a Malkíel; to je otec Birzavitův.",
			"32": "<span>32</span> Cheber zplodil Jafleta, Šómera, Chótama a jejich sestru Šúu.",
			"33": "<span>33</span> Synové Jafletovi: Pásak, Bimhál a Ašvát; to jsou synové Jafletovi.",
			"34": "<span>34</span> Synové Šemerovi: Achí a Rohga, Jechuba a Arám.",
			"35": "<span>35</span> Synové Helema, jeho bratra: Sófach, Jimna, Šéleš a Ámal.",
			"36": "<span>36</span> Synové Sófachovi: Súach, Charnefer, Šúal, Berí a Jimra,",
			"37": "<span>37</span> Beser, Hód, Šama, Šilša, Jitrán a Beéra.",
			"38": "<span>38</span> Synové Jeterovi: Jefune, Pispa a Ara.",
			"39": "<span>39</span> Synové Ulovi: Árach, Chaníel a Risja.",
			"40": "<span>40</span> Ti všichni jsou synové Ašerovi, náčelníci otcovských rodů, zkušení stateční bohatýři, náčelníci předáků. Počet zapsaných do seznamu rodů pro vojenskou službu a do boje činil dvacet šest tisíc mužů."
		},
		"8": {
			"1": "<span>1</span> Benjamín zplodil Belu, svého prvorozeného, Abšela jako druhého, Achracha jako třetího,",
			"2": "<span>2</span> Nóchu jako čtvrtého a Ráfu jako pátého.",
			"3": "<span>3</span> Synové Belovi byli: Adár, Géra, Abíhúd,",
			"4": "<span>4</span> Abíšúa, Naamán, Achóach,",
			"5": "<span>5</span> Géra, Šefúfan a Chúram.",
			"6": "<span>6</span> To jsou synové Echúdovi, náčelníci rodů obyvatel Geby, kteří je přestěhovali do Manachatu,",
			"7": "<span>7</span> totiž Naamán, Achijáš a Géra; ten je přestěhoval; též zplodil Uzu a Achíchuda.",
			"8": "<span>8</span> Šacharajim zplodil syny na Moábském poli poté, co propustil své ženiny Chúšimu a Baaru;",
			"9": "<span>9</span> se svou ženou Chodeší zplodil Jóbaba, Sibju, Méšu, Malkáma,",
			"10": "<span>10</span> Jeúsa, Sekejáše a Mirmu; to jsou jeho synové, náčelníci rodů.",
			"11": "<span>11</span> S Chúšimou zplodil Abítúba a Elpaala.",
			"12": "<span>12</span> Synové Elpaalovi: Eber, Mišeám, Šemed - ten vybudoval Óno a Lód s vesnicemi -,",
			"13": "<span>13</span> Bería a Šema; to jsou náčelníci rodů obyvatel Ajalónu; ti zahnali na útěk obyvatele Gatu.",
			"14": "<span>14</span> Dále Achjó, Šášak a Jeremót,",
			"15": "<span>15</span> Zebadjáš, Arad a Eder,",
			"16": "<span>16</span> Míkael, Jišpa a Jócha, synové Beríovi,",
			"17": "<span>17</span> Zebadjáš, Mešulám, Chizkí a Cheber,",
			"18": "<span>18</span> Jišmeraj, Jizlía a Jóbab, synové Elpaalovi,",
			"19": "<span>19</span> Jakím, Zikrí a Zabdí,",
			"20": "<span>20</span> Elíenaj, Siletaj a Elíel,",
			"21": "<span>21</span> Adajáš, Berejáš a Šimrat, synové Šimeího,",
			"22": "<span>22</span> Jišpán, Eber a Elíel,",
			"23": "<span>23</span> Ebdón, Zikrí a Chánan,",
			"24": "<span>24</span> Chananjáš, Élam a Antótijáš,",
			"25": "<span>25</span> Jifdejáš a Penúel, synové Šášakovi,",
			"26": "<span>26</span> Šamšeraj, Šecharjáš a Ataljáš,",
			"27": "<span>27</span> Jaarešjáš, Elijáš a Zikrí, synové Jerochámovi.",
			"28": "<span>28</span> To jsou náčelníci rodů podle rodopisů, náčelníci, kteří sídlili v Jeruzalémě.",
			"29": "<span>29</span> V Gibeónu pak sídlili: Otec Gibeónu, jeho žena se jmenovala Maaka",
			"30": "<span>30</span> a jeho prvorozený syn Abdón, pak Súr, Kíš, Baal a Nádab,",
			"31": "<span>31</span> Gedór, Achjó a Zeker.",
			"32": "<span>32</span> Miklót zplodil Šimeu. Také ti sídlili naproti svým bratřím v Jeruzalémě, se svými bratry.",
			"33": "<span>33</span> Nér zplodil Kíše, Kíš zplodil Saula, Saul zplodil Jónatana, Malkíšúu, Abínádaba a Ešbaala.",
			"34": "<span>34</span> Synem Jónatanovým byl Meríb-baal. Meríb-baal zplodil Míku.",
			"35": "<span>35</span> Synové Míkovi: Pítón, Melek, Taeréa a Achaz.",
			"36": "<span>36</span> Achaz zplodil Jójadu, Jójada zplodil Alemeta, Azmáveta a Zimrího, Zimrí zplodil Mósu.",
			"37": "<span>37</span> Mósa zplodil Bineu; jeho syn byl Ráfa, jeho synem Eleása a jeho synem Ásel.",
			"38": "<span>38</span> Ásel měl šest synů. Toto jsou jejich jména: Azríkam, Bokrú, Jišmáel, Šearjáš, Obadjáš a Chánan. Ti všichni jsou synové Áselovi.",
			"39": "<span>39</span> Synové jeho bratra Ešeka: Úlam, jeho prvorozený, druhý Jeúš a třetí Elífelet.",
			"40": "<span>40</span> Synové Úlamovi byli stateční bohatýři, lučištníci; měli mnoho synů a vnuků, celkem sto padesát. Ti všichni pocházeli ze synů Benjamínových."
		},
		"9": {
			"1": "<span>1</span> Všichni z Izraele byli zapsáni do seznamu rodů. Zde jsou ti, kdo byli zapsáni do Knihy králů izraelských. Juda pro svou zpronevěru byl přestěhován do Babylóna.",
			"2": "<span>2</span> Kdo dříve sídlili v městech svého trvalého vlastnictví, Izrael, kněží, levité a chrámoví nevolníci,",
			"3": "<span>3</span> pocházející z Judovců, Benjamínovců, Efrajimovců a Manasesovců, sídlili nyní v Jeruzalémě:",
			"4": "<span>4</span> Útaj, syn Amíhuda, syna Omrího, syna Imrího, syna Báního ze synů Peresa, syna Judova.",
			"5": "<span>5</span> Ze Šíloanů Asajáš, prvorozený, a jeho synové.",
			"6": "<span>6</span> Ze synů Zerachových Jeúel a jejich bratří, celkem šest set devadesát.",
			"7": "<span>7</span> Ze synů Benjamínových Salú, syn Mešuláma, syna Hódavjáše, syna Hasenuova,",
			"8": "<span>8</span> a Jibnejáš, syn Jerochámův, a Éla, syn Uzího, syna Mikrího, a Mešulám, syn Šefatjáše, syna Reúela, syna Jibnijášova,",
			"9": "<span>9</span> a jejich bratří podle rodopisů, celkem devět set padesát šest. Ti všichni muži byli náčelníci otcovských rodů.",
			"10": "<span>10</span> Z kněží: Jedajáš, Jójaríb a Jakín,",
			"11": "<span>11</span> Azarjáš, syn Chilkijáše, syna Mešuláma, syna Sádoka, syna Merajóta, syna Achítúba, představeného Božího domu.",
			"12": "<span>12</span> Adajáš, syn Jerocháma, syna Pašchúra, syna Malkijášova, Maesaj, syn Adíela, syna Jachzéry, syna Mešuláma, syna Mešilemíta, syna Imerova,",
			"13": "<span>13</span> a jejich bratří, náčelníci otcovských rodů, tisíc sedm set šedesát statečných bohatýrů pro služebné dílo Božího domu.",
			"14": "<span>14</span> Z lévijců: Šemajáš, syn Chašúba, syna Azríkama, syna Chašabjáše ze synů Merarího.",
			"15": "<span>15</span> Bakbakar, Chereš a Gálal, Matanjáš, syn Míky, syna Zikrího, syna Asafova.",
			"16": "<span>16</span> Obadjáš, syn Šemajáše, syna Gálala, syna Jedútúnova, a Berekjáš, syn Ásy, syna Elkánova, bydlící ve dvorcích nétofských.",
			"17": "<span>17</span> Vrátní: Šalúm, Akúb, Talmón a Achíman a jejich bratří; Šalúm byl náčelníkem",
			"18": "<span>18</span> a je až dosud při východní královské bráně. To jsou vrátní z táborů Léviovců.",
			"19": "<span>19</span> Šalúm, syn Kóreho, syna Ebjásafa, syna Kórachova, a jeho bratří z rodu Kórachovců byli správci služebného díla a střežili prahy stanu; jejich otcové byli správci Hospodinova tábora a střežili vstup.",
			"20": "<span>20</span> A Pinchas, syn Eleazarův, byl dříve jejich představeným; Hospodin byl s ním.",
			"21": "<span>21</span> Zekarjáš, syn Mešelemjášův, byl vrátným při vchodu do stanu setkávání.",
			"22": "<span>22</span> Všech prověřených vrátných prahů bylo dvě stě dvanáct. Byli zapsáni do seznamu rodů ve svých dvorcích. David a Samuel, vidoucí, je ustanovili k stálé službě.",
			"23": "<span>23</span> Oni a jejich synové byli na stráži u bran domu Hospodinova, domu stanu.",
			"24": "<span>24</span> Vrátní byli na čtyřech stranách: na východě, na západě, na severu a na jihu.",
			"25": "<span>25</span> A jejich bratří měli přicházet ze svých dvorců vždy ve stanovené době, aby spolu s nimi sloužili po sedm dnů.",
			"26": "<span>26</span> Tito byli ustanoveni k stálé službě: čtyři bohatýrští vrátní; byli to lévijci, byli správci komor a pokladů Božího domu.",
			"27": "<span>27</span> Jiní ponocovali kolem Božího domu, neboť na nich bylo držet stráž a ráno co ráno otvírat.",
			"28": "<span>28</span> Další měli na starosti služebné náčiní, v určitém počtu je přinášeli dovnitř a vynášeli ven.",
			"29": "<span>29</span> Někteří měli pod dohledem náčiní a svaté předměty, bílou mouku, víno a olej, kadidlo a vonné látky.",
			"30": "<span>30</span> Někteří kněžští synové mísili masti z vonných látek.",
			"31": "<span>31</span> A Matitjáš z lévijců, prvorozený Kórachovce Šalúma, byl ustanoven k stálé službě u pánví.",
			"32": "<span>32</span> Někteří Kehatovci, někteří z jejich bratří, měli na starosti rovnání předkladných chlebů, měli je připravovat vždycky v den odpočinku.",
			"33": "<span>33</span> Toto jsou zpěváci, náčelníci lévijských rodů; dleli v komorách, uvolněni od jiných prací. Ve dne i v noci byli totiž v pohotovosti ke svému dílu.",
			"34": "<span>34</span> Toto jsou náčelníci lévijských rodů podle rodopisů, náčelníci, kteří sídlili v Jeruzalémě.",
			"35": "<span>35</span> V Gibeónu sídlili: otec Gibeónu Jeíel, jeho žena se jmenovala Maaka",
			"36": "<span>36</span> a jeho prvorozený syn Abdón, pak Súr, Kíš, Baal, Nér a Nádab,",
			"37": "<span>37</span> Gedór a Achjó, Zekarjáš a Miklót.",
			"38": "<span>38</span> Miklót pak zplodil Šimeama. Také ti sídlili naproti svým bratřím v Jeruzalémě se svými bratry.",
			"39": "<span>39</span> Nér zplodil Kíše a Kíš zplodil Saula. Saul zplodil Jónatana a Malkíšúu a Abínádaba a Eš-baala.",
			"40": "<span>40</span> Synem Jónatanovým byl Meríb-baal. Meríb-baal zplodil Míku.",
			"41": "<span>41</span> Synové Míkovi: Pitón, Melek a Tachréa.",
			"42": "<span>42</span> Achaz pak zplodil Jaeru, Jaera zplodil Alemeta, Azmáveta a Zimrího, Zimrí zplodil Mósu.",
			"43": "<span>43</span> Mósa zplodil Bineu; jeho synem byl Refajáš, jeho synem byl Eleása, jeho synem byl Ásel.",
			"44": "<span>44</span> Ásel měl šest synů. Toto jsou jejich jména: Azríkam, Bokrú, Jišmáel, Šearjáš, Obadjáš a Chánan. Toto jsou synové Áselovi."
		},
		"10": {
			"1": "<span>1</span> Pelištejci bojovali proti Izraeli. Izraelští muži před Pelištejci utíkali a padali pobiti v pohoří Gilbóa.",
			"2": "<span>2</span> Pelištejci se pustili za Saulem a za jeho syny. I pobili Pelištejci Saulovy syny Jónatana, Abínádaba a Malkíšúu.",
			"3": "<span>3</span> Pak zesílil boj proti Saulovi. Objevili ho lukostřelci a postřelili ho.",
			"4": "<span>4</span> Saul řekl svému zbrojnoši: \"Vytas meč a probodni mě jím, než přijdou ti neobřezanci, aby mě nezneuctili.\" Zbrojnoš však nechtěl, velmi se bál. Saul tedy uchopil meč a nalehl na něj.",
			"5": "<span>5</span> Když zbrojnoš viděl, že Saul zemřel, také on nalehl na meč a zemřel.",
			"6": "<span>6</span> Tak zemřeli Saul, jeho tři synové a celý jeho dům, zemřeli zároveň.",
			"7": "<span>7</span> Když všichni izraelští muži, kteří byli v dolině, viděli, že bojovníci utekli a že Saul i jeho synové zemřeli, opouštěli svá města a utíkali. Pelištejci přišli a usadili se v nich.",
			"8": "<span>8</span> Pelištejci přišli druhého dne, aby obrali pobité, a našli Saula a jeho syny padlé v pohoří Gilbóa.",
			"9": "<span>9</span> Obrali ho a odnesli jeho hlavu a jeho zbroj a poslali kolovat po pelištejské zemi jako radostné poselství pro své modlářské stvůry i pro lid.",
			"10": "<span>10</span> Jeho zbroj uložili v domě svých bohů a jeho lebku přibili v domě Dágonově.",
			"11": "<span>11</span> Všichni z Jábeše v Gileádu uslyšeli o všem, co se Saulem provedli Pelištejci.",
			"12": "<span>12</span> Všichni válečníci se vypravili, odnesli mrtvé tělo Saulovo i mrtvá těla jeho synů, přinesli je do Jábeše a pochovali jejich kosti pod posvátným stromem v Jábeši. Poté se postili sedm dní.",
			"13": "<span>13</span> Tak zemřel Saul pro svoji zpronevěru; zpronevěřil se Hospodinu, protože nedbal na Hospodinovo slovo. Dokonce se dotazoval věštího ducha.",
			"14": "<span>14</span> Hospodina se nedotazoval, proto jej Hospodin usmrtil a převedl království na Davida, syna Jišajova."
		},
		"11": {
			"1": "<span>1</span> Celý Izrael se shromáždil k Davidovi do Chebrónu. Řekli: \"Hle, jsme tvá krev a tvé tělo.",
			"2": "<span>2</span> Už tenkrát, když byl králem Saul, vyváděl a přiváděl jsi Izraele ty. Tobě Hospodin, tvůj Bůh, řekl: »Ty budeš pást Izraele, můj lid, ty budeš vévodou nad mým izraelským lidem.«\"",
			"3": "<span>3</span> Pak přišli všichni izraelští starší ke králi do Chebrónu a David s nimi v Chebrónu uzavřel před Hospodinem smlouvu. I pomazali Davida za krále nad Izraelem podle Hospodinova slova skrze Samuela.",
			"4": "<span>4</span> David šel s celým Izraelem na Jeruzalém, to je na Jebús; obyvatelé té země byli Jebúsejci.",
			"5": "<span>5</span> Obyvatelé Jebúsu Davidovi řekli: \"Sem nevstoupíš.\" Ale David dobyl skalní pevnost Sijón, to je Město Davidovo.",
			"6": "<span>6</span> David řekl:\"Kdo udeří na Jebúsejce první, stane se vůdcem a velitelem.\" První pronikl nahoru Jóab, syn Serújin, a tak se stal vůdcem.",
			"7": "<span>7</span> David se usadil ve skalní pevnosti; proto ji nazvali Město Davidovo.",
			"8": "<span>8</span> Pak vybudoval město kolem dokola počínaje od Miló. Ostatní části města opravil Jóab.",
			"9": "<span>9</span> David se stále více vzmáhal a Hospodin zástupů byl s ním.",
			"10": "<span>10</span> Toto byli vůdci Davidových bohatýrů, kteří byli při něm jako mocná opora jeho království spolu s celým Izraelem; ustanovili jej králem podle Hospodinova slova o Izraeli.",
			"11": "<span>11</span> Toto je výčet Davidových bohatýrů: Jášobeám, syn Chachmóního, vůdce osádek. Ten zamával kopím a naráz jich proklál na tři sta.",
			"12": "<span>12</span> Po něm Eleazar, syn Dóda Achóchijského; to byl jeden z těch tří bohatýrů.",
			"13": "<span>13</span> Ten byl s Davidem v Pas-damímu, když se tam Pelištejci shromáždili k boji. Byl tam díl pole, celý osetý ječmenem. Lid utíkal před Pelištejci.",
			"14": "<span>14</span> Bohatýři se postavili doprostřed toho dílu pole, osvobodili jej a Pelištejce pobili. Hospodin způsobil veliké vysvobození.",
			"15": "<span>15</span> Tři z těch třiceti vůdců sestoupili na skálu k Davidovi do jeskyně Adulámu. Pelištejský tábor ležel v dolině Refájců.",
			"16": "<span>16</span> David byl tehdy ve skalní skrýši a výsostný znak Pelištejců byl tenkrát u Betléma.",
			"17": "<span>17</span> Tu David zatoužil: \"Kdo mi dá napít vody z betlémské studny, která je u brány?\"",
			"18": "<span>18</span> Ti tři vtrhli do pelištejského tábora, načerpali vodu z betlémské studny, která je u brány, a přinesli ji Davidovi. David ji však nechtěl pít, nýbrž vykonal jí úlitbu Hospodinu.",
			"19": "<span>19</span> Řekl: \"Můj Bože, nechť jsem dalek toho, abych udělal něco takového. Což mohu pít krev těch mužů? Nasadili svůj život. Přinesli mi ji s nasazením života.\" Proto se jí nechtěl napít. To vykonali ti tři bohatýři.",
			"20": "<span>20</span> Také Abšaj, bratr Jóabův, byl vůdce tří. I on zamával kopím a proklál jich na tři sta. Mezi těmi třemi byl nejproslulejší.",
			"21": "<span>21</span> Z těch tří byl váženější než druzí dva a byl jejich velitelem, ale oněm třem prvním se nevyrovnal.",
			"22": "<span>22</span> Benajáš, syn Jójady, syna zdatného muže z Kabseelu, muže mnoha činů, ubil dva moábské reky a sestoupil a ubil v jámě lva; byl tehdy sníh.",
			"23": "<span>23</span> Ubil též Egypťana vysokého pět loket; Egypťan měl v ruce kopí jako tkalcovské vratidlo. Sestoupil k němu s holí, vytrhl Egypťanovi kopí z ruky a jeho vlastním kopím ho zabil.",
			"24": "<span>24</span> Toto vykonal Benajáš, syn Jójadův. A byl mezi těmi třemi bohatýry proslulý.",
			"25": "<span>25</span> Z těch třiceti byl nejváženější, ale oněm třem prvním se nevyrovnal. David ho přidělil ke své tělesné stráži.",
			"26": "<span>26</span> Následují stateční bohatýři Asáel, bratr Jóabův, Elchánan, syn Dóda z Betléma,",
			"27": "<span>27</span> Šamót Harórský, Cheles Pelónský,",
			"28": "<span>28</span> Íra, syn Íkeše Tekójského, Abíezer Anatótský,",
			"29": "<span>29</span> Sibekaj Chúšatský, Ílaj Achóchejský,",
			"30": "<span>30</span> Mahraj Netófský, Cheled, syn Baany Netófského,",
			"31": "<span>31</span> Ítaj, syn Ríbaje z Gibeje Benjamínovců, Benajáš Pireatónský,",
			"32": "<span>32</span> Chúraj z úvalů Gaašských, Abíel Arbátský,",
			"33": "<span>33</span> Azmávet Bacharúmský, Eljachba Šaalbónský.",
			"34": "<span>34</span> Proslulí byli Jónatan Gizónský, syn Šagéa Hararského,",
			"35": "<span>35</span> Achíam, syn Sakara Hararského, Elífal, syn Úrův,",
			"36": "<span>36</span> Chefer Mekeratský, Achijáš Pelónský,",
			"37": "<span>37</span> Chesró Karmelský, Naaraj, syn Ezbajův,",
			"38": "<span>38</span> Jóel, bratr Nátanův, Mibchár, syn Hagrího,",
			"39": "<span>39</span> Selek Amónský, Nachraj Berótský, zbrojnoš Jóaba, syna Serújina,",
			"40": "<span>40</span> Íra Jitrejský, Gáreb Jitrejský,",
			"41": "<span>41</span> Urijáš Chetejský, Zábad, syn Achlájův,",
			"42": "<span>42</span> Adína, syn Šízy Rúbenského, vůdce Rúbenců, s nimiž bylo oněch třicet,",
			"43": "<span>43</span> dále Chánan, syn Maakův, a Jóšafat Mitnejský,",
			"44": "<span>44</span> Uzijáš Ašterátský, Šama a Jeíel, synové Chótama Aróerského,",
			"45": "<span>45</span> Jedíael, syn Šimrího, a jeho bratr Jócha Tisejský,",
			"46": "<span>46</span> Elíel Machavímský, Jeríbaj a Jóšavjáš, synové Elnaamovi, a Jitma Moábský,",
			"47": "<span>47</span> Elíel a Obéd a Jaasíel Mesóbajští."
		},
		"12": {
			"1": "<span>1</span> Toto jsou bohatýři, kteří přišli za Davidem do Siklagu, kde se zdržoval před Saulem, synem Kíšovým, jeho spolubojovníci.",
			"2": "<span>2</span> Byli vyzbrojeni lukem, bojovali pravou i levou rukou, kamením i šípy z luku. Pocházeli z Benjamína, ze Saulových bratří.",
			"3": "<span>3</span> Vůdcem byl Achíezer, pak Jóaš, synové Šemaje Gibeatského, Jezíel a Pelet, synové Azmávetovi, Beraka a Jehú Anatótský,",
			"4": "<span>4</span> Jišmajáš Gibeónský, jeden z třiceti bohatýrů a nad třiceti,",
			"5": "<span>5</span> Jirmejáš a Jachazíel, Jóchanan a Józabad Gederatský,",
			"6": "<span>6</span> Eleúzaj a Jerímót, Baaljáš, Šemarjáš a Šefatjáš Charífský,",
			"7": "<span>7</span> Elkána a Jišijáš, Azarel, Jóezer a Jášobeám, Korchíjci,",
			"8": "<span>8</span> Jóela a Zebadjáš, synové Jerocháma z Gedóru.",
			"9": "<span>9</span> Z Gádovců přešli k Davidovi do skalní skrýše na poušť udatní bohatýři, schopní bojovníci vyzbrojení štítem a oštěpem, s tváří lvů a hbitostí gazel na horách.",
			"10": "<span>10</span> Vůdcem byl Ezer, druhý byl Obadjáš, třetí Elíab,",
			"11": "<span>11</span> čtvrtý Mišmana, pátý Jirmejáš,",
			"12": "<span>12</span> šestý Ataj, sedmý Elíel,",
			"13": "<span>13</span> osmý Jóchanan, devátý Elzabad,",
			"14": "<span>14</span> desátý Jirmejáš, jedenáctý Makbanaj.",
			"15": "<span>15</span> To byli Gádovci, vůdcové vojska, ti mladší nad setninou, ti starší nad plukem.",
			"16": "<span>16</span> To jsou ti, kteří překročili Jordán v prvním měsíci, když se rozvodnil a vylil ze svých břehů, kteří rozehnali nepřátele po všech dolinách na východ i na západ.",
			"17": "<span>17</span> Také jiní z Benjamínovců a Judejců přišli k Davidovi do skalní skrýše.",
			"18": "<span>18</span> David jim vyšel vstříc a takto je oslovil: \"Přicházíte-li ke mně v pokoji, abyste mi pomohli, ze srdce rád se s vámi spojím. Jestliže mě však chcete vyzradit mým protivníkům, i když na mých rukou nelpí násilí, ať to Bůh našich otců vidí a ztrestá.\"",
			"19": "<span>19</span> Tu vyzbrojil duch Boží Amasaje, vůdce osádek. Řekl: \"Jsme s tebou, Davide, jsme při tobě, synu Jišajův. Pokoj, pokoj tobě, pokoj i tvým pomocníkům, vždyť tvůj Bůh ti pomáhá.\" David je přijal a ustanovil je vůdci oddílů.",
			"20": "<span>20</span> Také někteří z Manasea odpadli k Davidovi, když vytáhl s Pelištejci do boje proti Saulovi. Nepomáhali však Pelištejcům, protože pelištejská knížata se usnesla poslat Davida pryč s odůvodněním: \"Za cenu našich hlav odpadne ke svém pánu Saulovi.\"",
			"21": "<span>21</span> Když se ubíral do Siklagu, odpadli k němu z Manasesa Adnach, Józabad, Jedíael, Míkael, Józabad, Elíhu a Siltaj, vůdci šiků z Manasesa.",
			"22": "<span>22</span> Ti pomohli Davidovi proti nepřátelské hordě. Všichni to byli udatní bohatýři. Stali se ve vojsku veliteli.",
			"23": "<span>23</span> Den ze dne přicházely Davidovi posily, takže jeho tábor vzrostl jako tábor Boží.",
			"24": "<span>24</span> Toto je výčet oddílů vyzbrojených k vojenské službě, těch, kteří přišli k Davidovi do Chebrónu, aby na něho podle Hospodinova rozkazu přenesli Saulovo království.",
			"25": "<span>25</span> Judovců ozbrojených štítem a oštěpem šest tisíc osm set, vyzbrojených k vojenské službě;",
			"26": "<span>26</span> ze Šimeónovců sedm tisíc jedno sto bohatýrů udatných v boji;",
			"27": "<span>27</span> z Léviovců čtyři tisíce šest set;",
			"28": "<span>28</span> Jójada, vévoda Áronovců, měl s sebou tři tisíce sedm set mužů.",
			"29": "<span>29</span> Mládenec Sádok byl udatný bohatýr. Z domu jeho otce bylo dvaadvacet velitelů.",
			"30": "<span>30</span> Z Benjamínovců, Saulových bratří, tři tisíce. Většina jich totiž dosud vykonávala strážní službu domu Saulovu.",
			"31": "<span>31</span> Z Efrajimovců dvacet tisíc osm set udatných bohatýrů, proslulých ve svých otcovských rodech.",
			"32": "<span>32</span> Z poloviny pokolení Manasesova osmnáct tisíc, kteří byli uvedeni jménem a určeni k tomu, aby Davida ustanovili králem.",
			"33": "<span>33</span> Z Isacharovců přišli znalci časů, kteří poučovali Izraele, co má dělat. Jejich vůdců bylo dvě stě; všichni jejich bratří si řídili jejich pokyny.",
			"34": "<span>34</span> Ze Zabulóna vytáhlo vojsko připravené k boji se všemi válečnými zbraněmi, padesát tisíc mužů odhodlaných bez váhání nastoupit.",
			"35": "<span>35</span> Z Neftalího tisíc velitelů a s nimi třicet sedm tisíc mužů ozbrojených štítem a kopím.",
			"36": "<span>36</span> Z Dana dvacet osm tisíc šest set mužů připravených k boji.",
			"37": "<span>37</span> Z Ašera vytáhlo vojsko čtyřicet tisíc mužů připravených k boji.",
			"38": "<span>38</span> Ze Zajordání z Rúbena, Gáda a z poloviny kmene Manasesova sto dvacet tisíc mužů v plné zbroji do boje.",
			"39": "<span>39</span> Ti všichni byli bojovníci odhodlaní nastoupit do bitevní řady; přišli v pokoji do Chebrónu, aby ustanovili Davida králem nad celým Izraelem. Též všechen ostatní Izrael hodlal jednomyslně Davida ustanovit králem.",
			"40": "<span>40</span> Pobyli tam s Davidem tři dny. Jedli a pili, co jim jejich bratří připravili. [ (I Chronicles 12:41) A jejich sousedé až od Isachara, Zabulóna a Neftalího přiváželi na oslech, velbloudech, na mezcích i dobytčatech chléb, mouku, pletence sušených fíků, sušené hrozny, víno a olej a přiváděli množství skotu a bravu, neboť v Izraeli zavládla radost. ]"
		},
		"13": {
			"1": "<span>1</span> David se radil s veliteli nad tisíci a nad sty, s každým vojevůdcem.",
			"2": "<span>2</span> David řekl celému shromáždění Izraele: \"Pokládáte-li to za vhodné a za pokyn od Hospodina, našeho Boha, pošleme vzkaz svým ostatním bratřím do všech izraelských území a současně i kněžím a lévijcům do jejich měst a na jejich pastviny, aby se k nám shromáždili.",
			"3": "<span>3</span> Přeneseme k nám schránu našeho Boha; za dnů Saulových jsme ji nevyhledávali.\"",
			"4": "<span>4</span> Celé shromáždění s tím souhlasilo, všechen lid ten návrh pokládal za správný.",
			"5": "<span>5</span> Proto David svolal celý Izrael, od ramene řeky Egyptské až k cestě do Chamátu, aby přenesli Boží schránu z Kirjat-jearímu.",
			"6": "<span>6</span> Pak David a celý Izrael táhli do Baaly u Kirjat-jearímu v Judsku, aby odtud vynesli schránu Boha Hospodina, který sídlí nad cheruby, jehož jméno je vzýváno.",
			"7": "<span>7</span> Vezli Boží schránu na novém povozu z domu Abínádabova. Povoz řídili Uza a Achjó.",
			"8": "<span>8</span> David a celý Izrael bujaře křepčili před Bohem a zpívali za doprovodu citar, harf, bubínku, cymbálů a pozounů.",
			"9": "<span>9</span> Když přišli ke Kídonovu humnu, napřáhl Uza ruku, aby uchopil schránu, protože spřežení vybočilo z cesty.",
			"10": "<span>10</span> Hospodin vzplanul proti Uzovi hněvem a zabil ho, protože napřáhl ruku na schránu. Zemřel tam před Bohem.",
			"11": "<span>11</span> Též David vzplanul, že se Hospodin prudce obořil na Uzu, a proto nazval to místo Peres-uza (to je Uzovo zbořenisko); jmenuje se tak dodnes.",
			"12": "<span>12</span> V onen den pojala Davida bázeň před Bohem. Řekl: \"Jak bych mohl vnést Boží schránu až k sobě?\"",
			"13": "<span>13</span> Proto David Boží schránu nepřenesl k sobě do Města Davidova, nýbrž ji dal dopravit do domu Obéd-edóma Gatského.",
			"14": "<span>14</span> Boží schrána zůstala při domě Obéd-edómově; byla v jeho domě po tři měsíce. Hospodin požehnal Obéd-edómovu domu i všemu, co mu patřilo."
		},
		"14": {
			"1": "<span>1</span> Týrský král Chíram poslal k Davidovi posly; poslal mu cedrové dřevo, zedníky a tesaře, aby mu postavili dům.",
			"2": "<span>2</span> David poznal, že jej Hospodin potvrdil za krále nad Izraelem, že jeho království bude kvůli jeho izraelskému lidu velice povzneseno.",
			"3": "<span>3</span> David si v Jeruzalémě vzal další ženy a zplodil další syny a dcery.",
			"4": "<span>4</span> Toto jsou jména těch, kteří se mu narodili v Jeruzalémě: Šamúa a Šóbab, Nátan a Šalomoun,",
			"5": "<span>5</span> Jibchár, Elíšua a Elpelet,",
			"6": "<span>6</span> Nógah, Nefeg a Jafía,",
			"7": "<span>7</span> Elíšama, Beeljáda a Elífelet.",
			"8": "<span>8</span> Když Pelištejci uslyšeli, že David byl pomazán za krále nad celým Izraelem, vytáhli všichni Pelištejci Davida hledat. David o tom uslyšel a vytáhl proti nim.",
			"9": "<span>9</span> Pelištejci přitáhli a vpadli do doliny Refájců.",
			"10": "<span>10</span> David se doptával Boha: \"Mám proti Pelištejcům vytáhnout? Vydáš mi je do rukou?\" Hospodin mu odpověděl: \"Vytáhni, vydám ti je do rukou!\"",
			"11": "<span>11</span> Vystoupili tedy do Baal-perasímu a tam je David pobil. David prohlásil: \"Bůh jako prudké vody prolomil mým prostřednictvím řady mých nepřátel.\" Proto pojmenovali to místo Baal-perasím (to je Pán průlomu).",
			"12": "<span>12</span> Pelištejci tam zanechali své bohy a David je dal spálit ohněm.",
			"13": "<span>13</span> Pelištejci potom znovu vpadli do doliny.",
			"14": "<span>14</span> David se opět doptával Boha. Bůh mu odpověděl: \"Netáhni za nimi. Obejdi je a napadni je směrem od balzámovníků.",
			"15": "<span>15</span> Jakmile uslyšíš v korunách bazámovníků šelest kroků, vyrazíš do boje, neboť Bůh vyjde před tebou a pobije tábor Pelištejců.\"",
			"16": "<span>16</span> David vykonal, co mu Bůh přikázal. Pobili tábor Pelištejců od Gibeónu až do Gezeru.",
			"17": "<span>17</span> David se stal proslulým ve všech zemích. Hospodin způsobil, že strach z něho dolehl na všechny národy."
		},
		"15": {
			"1": "<span>1</span> David nastavěl v Městě Davidově domy pro sebe, připravil i místo pro Boží schránu a postavil pro ni stan.",
			"2": "<span>2</span> Tehdy David řekl: \"Boží schránu nesmí nést nikdo kromě lévijců, protože je vyvolil Hospodin, aby nosili Hospodinovu schránu a vždycky u ní přisluhovali.\"",
			"3": "<span>3</span> David svolal celý Izrael do Jeruzaléma, aby vynesli Hospodinovu schránu na místo, které pro ni připravil.",
			"4": "<span>4</span> David tedy shromáždil Áronovce a lévijce:",
			"5": "<span>5</span> Z Kehatovců předáka Uríela a sto dvacet jeho bratří.",
			"6": "<span>6</span> Z Meraríovců předáka Asajáše a dvě stě dvacet jeho bratří.",
			"7": "<span>7</span> Z Geršómovců předáka Jóela a sto třicet jeho bratří.",
			"8": "<span>8</span> Z Elísáfanovců předáka Šemajáše a dvě stě jeho bratří.",
			"9": "<span>9</span> Z Chebrónovců předáka Elíela a osmdesát jeho bratří.",
			"10": "<span>10</span> Z Uzíelovců předáka Amínadaba a sto dvanáct jeho bratří.",
			"11": "<span>11</span> David povolal kněze Sádoka a Ebjátara a lévijce Uríela, Asajáše, Jóela, Šemajáše, Elíela a Amínadaba.",
			"12": "<span>12</span> Nařídil jim: \"Vy jste představitelé lévijských rodů. Posvěťte se spolu se svými bratry. Vynesete schránu Hospodina, Boha Izraele, na místo, které jsem pro ni připravil.",
			"13": "<span>13</span> Že jste při tom ponejprv nebyli, Hospodin, náš Bůh, se prudce na nás obořil, protože jsme se ho nedotázali podle řádu.\"",
			"14": "<span>14</span> Kněží a lévijci se tedy posvětili a vynesli schránu Hospodina, Boha Izraele.",
			"15": "<span>15</span> Léviovci nesli Boží schránu na ramenou na sochorech, jak podle Hospodinova slova přikázal Mojžíš.",
			"16": "<span>16</span> David také lévijským předákům nařídil, aby ustanovili své bratry zpěváky, kteří by radostně hlaholili na hudební nástroje, harfy, citary a zvučné cymbály.",
			"17": "<span>17</span> I ustanovili lévijci Hémana, syna Jóelova, a z jeho bratří Asafa, syna Berekjášova, a z Meraríovců, jejich bratří, Étana, syna Kúšajášova.",
			"18": "<span>18</span> A na střídání s nimi jejich bratry Zakarjáše, Bena, Jaazíela, Šemíramóta, Jechíela, Uního, Elíaba, Benajáše, Maasejáše, Matitjáše, Elíflehúa, Miknejáše, Obéd-edóma a Jeíela, vrátné.",
			"19": "<span>19</span> Zpěváci Héman, Asaf a Étan zvučně hráli na měděné cymbály.",
			"20": "<span>20</span> Zekarjáš, Azíel, Šemíramót, Jechíel, Uní, Elíab, Maasejáš a Benajáš doprovázeli vysoký zpěv na harfy,",
			"21": "<span>21</span> Matitjáš, Elíflehú, Miknejáš, Obéd-edóm, Jeíel a Azazjáš doprovázeli hluboký zpěv na citary.",
			"22": "<span>22</span> Kenanjáš, předák lévijců, určených k přenášení schrány, byl pověřen dozorem při přenášení; vyznal se v těchto věcech.",
			"23": "<span>23</span> Berekjáš a Elkána byli při schráně vrátnými.",
			"24": "<span>24</span> Kněží Šebanjáš, Jóšafat, Netaneel, Amasaj, Zekarjáš, Benajáš a Elíezer troubili před Boží schránou na pozouny. Obéd-edóm a Jechijáš byli také vrátnými při schráně.",
			"25": "<span>25</span> David, izraelští starší a velitelé nad tisíci radostně vystupovali se schránou Hospodinovy smlouvy z Obéd-edómova domu.",
			"26": "<span>26</span> Protože Bůh prokázal svou pomoc lévijcům, kteří nesli schránu Hospodinovy smlouvy, obětovali sedm býčků a sedm beranů.",
			"27": "<span>27</span> David byl oděn pláštěnkou z bělostného plátna, stejně tak všichni lévijci, kteří nesli schránu, zpěváci a Kenanjáš, předák zpěváků při přenášení. David měl na sobě též lněný efód.",
			"28": "<span>28</span> Celý Izrael vystupoval se schránou Hospodinovy smlouvy za ryčného troubení polnic a za zvuku pozounů, cymbálů, harf a citar.",
			"29": "<span>29</span> Když schrána Hospodinovy smlouvy vstupovala do Města Davidova, Míkal, dcera Saulova, se právě dívala z okna. Viděla krále Davida, jak poskakuje a křepčí, a v srdci jím pohrdla."
		},
		"16": {
			"1": "<span>1</span> Boží schránu přinesli a umístili ji uprostřed stanu, který pro ni David postavil. I přinášeli před Bohem zápalné a pokojné oběti.",
			"2": "<span>2</span> Když David dokončil obětování zápalných a pokojných obětí, požehnal lidu v Hospodinově jménu.",
			"3": "<span>3</span> Pak podělil každého z Izraele, každého muže i ženu, bochníčkem chleba a datlovým a hrozinkovým koláčem.",
			"4": "<span>4</span> Potom určil lévijce, kteří by přisluhovali u Hospodinovy schrány a připomínali Hospodina, Boha Izraele, vzdávali mu chválu a oslavovali jej.",
			"5": "<span>5</span> Asaf byl představeným, jeho zástupcem byl Zekarjáš. Jeíel, Šemíramót, Jechíel, Matitjáš, Elíab, Benajáš, Obéd-edóm a Jeíel hráli na harfy a citary, Asaf na zvučné cymbály.",
			"6": "<span>6</span> Kněží Benajáš a Jachazíel hráli každodenně na pozouny před schránou Boží smlouvy.",
			"7": "<span>7</span> Tehdy onoho dne nařídil David poprvé, aby Asaf a jeho bratří vzdávali Hospodinu chválu:",
			"8": "<span>8</span> Chválu vzdejte Hospodinu a vzývejte jeho jméno, uvádějte národům ve známost jeho skutky,",
			"9": "<span>9</span> zpívejte mu, pějte žalmy, přemýšlejte o všech jeho divech,",
			"10": "<span>10</span> honoste se jeho svatým jménem, ať se raduje srdce těch, kteří hledají Hospodina!",
			"11": "<span>11</span> Dotazujte se na vůli Hospodinovu a jeho moc, jeho tvář hledejte ustavičně.",
			"12": "<span>12</span> Připomínejte divy, jež vykonal, jeho zázraky a rozsudky jeho úst,",
			"13": "<span>13</span> potomkové Izraele, jeho služebníka, Jákobovi synové, jeho vyvolení!",
			"14": "<span>14</span> Je to Hospodin, náš Bůh, kdo soudí celou zemi.",
			"15": "<span>15</span> Věčně pamatujte na jeho smlouvu, na slovo, jež přikázal tisícům pokolení.",
			"16": "<span>16</span> Uzavřel ji s Abrahamem, přísahou ji stvrdil Izákovi,",
			"17": "<span>17</span> stanovil ji Jákobovi jako nařízení, Izraeli jako smlouvu věčnou:",
			"18": "<span>18</span> \"Dám ti kenaanskou zemi za dědičný úděl!\"",
			"19": "<span>19</span> Na počet vás byla malá hrstka, byli jste tam hosty.",
			"20": "<span>20</span> Putovali od jednoho pronároda ke druhému, z jednoho království dál k jinému lidu.",
			"21": "<span>21</span> On však nedovolil nikomu, aby je utlačoval, káral kvůli nim i krále:",
			"22": "<span>22</span> \"Nesahejte na mé pomazané, ublížit mým prorokům se chraňte!\"",
			"23": "<span>23</span> Zpívej Hospodinu, celá země! Zvěstujte den ze dne jeho spásu,",
			"24": "<span>24</span> vypravujte mezi pronárody o jeho slávě, mezi všemi národy o jeho divech,",
			"25": "<span>25</span> neboť Hospodin je veliký, nejvyšší chvály hodný, budí bázeň, je nad všechny bohy.",
			"26": "<span>26</span> Všechna božstva národů jsou bůžci, ale Hospodin učinil nebe.",
			"27": "<span>27</span> Před jeho tváří velebná důstojnost, moc a potěšení na svatém místě jeho.",
			"28": "<span>28</span> Lidské čeledi, přiznejte Hospodinu, přiznejte Hospodinu slávu a moc,",
			"29": "<span>29</span> přiznejte Hospodinu slávu jeho jména! Přineste dar, vstupte před něj, v nádheře svatyně se klaňte Hospodinu!",
			"30": "<span>30</span> Svíjej se před ním, celá země! Pevně je založen svět, nic jím neotřese.",
			"31": "<span>31</span> Nebesa se zaradují, rozjásá se země, mezi národy se bude říkat: \"Hospodin kraluje!\"",
			"32": "<span>32</span> Moře i s tím, co je v něm, se rozburácí, pole zazní jásotem, i všechno, co je na něm.",
			"33": "<span>33</span> Tehdy zaplesají stromy v lese vstříc Hospodinu, že přichází soudit zemi.",
			"34": "<span>34</span> Chválu vzdejte Hospodinu, protože je dobrý, jeho milosrdenství je věčné.",
			"35": "<span>35</span> Rcete: Zachraň nás, ó Bože, naše spáso, shromáždi a vytrhni nás z pronárodů; tvému svatému jménu budeme vzdávat chválu, budeme tě chválit chvalozpěvem.",
			"36": "<span>36</span> Požehnán buď Hospodin, Bůh Izraele, od věků až na věky!\" A všechen lid ať řekne: \"Amen. Chvalte Hospodina!\"",
			"37": "<span>37</span> Tam před schránou Hospodinovy smlouvy zanechal David Asafa a jeho bratry, aby každodenně, podle denního rozvrhu, před schránou přisluhovali.",
			"38": "<span>38</span> Obéd-edóma a jeho šedesát osm bratří i Obéd-edóma, syna Jedítúnova, a Chósu zanechal jako vrátné.",
			"39": "<span>39</span> Kněze Sádoka a jeho bratry kněze zanechal před Hospodinovým příbytkem na posvátném návrší v Gibeónu,",
			"40": "<span>40</span> aby každodenně zrána i zvečera obětovali Hospodinu na oltáři zápalné oběti, a to podle všeho toho, co je zapsáno v Hospodinově zákoně, který vydal Izraeli.",
			"41": "<span>41</span> S nimi byli Héman a Jedútún a ostatní čistí, kteří byli uvedeni jmény, aby vzdávali chválu Hospodinu, neboť jeho milosrdenství je věčné.",
			"42": "<span>42</span> Héman a Jedútún měli s sebou pozouny, zvučné cymbály a nástroje k Božímu zpěvu. Jedútúnovci byli u brány.",
			"43": "<span>43</span> I odešel všechen lid, každý do svého domu. A David se obrátil ke svému domu, aby mu udělil požehnání."
		},
		"17": {
			"1": "<span>1</span> Když David už sídlil ve svém domě, řekl proroku Nátanovi: \"Hle, já sídlím v domě cedrovém a schrána Hospodinovy smlouvy je pod stanovými houněmi.\"",
			"2": "<span>2</span> Nátan Davidovi odvětil: \"Udělej vše, co máš na srdci, neboť Bůh je s tebou.\"",
			"3": "<span>3</span> Ale té noci se stalo Boží slovo k Nátanovi:",
			"4": "<span>4</span> \"Jdi a řekni mému služebníku Davidovi: Toto praví Hospodin: Ty mi nebudeš budovat dům, v němž bych sídlil.",
			"5": "<span>5</span> Nesídlil jsem v domě od toho dne, kdy jsem vyvedl Izraele, až do dne tohoto. Putoval jsem se stanem, ale příbytek jsem neměl.",
			"6": "<span>6</span> Ať jsem přecházel s celým Izraelem kudykoli, zdalipak jsem kdy řekl některému z izraelských soudců, jemuž jsem přikázal pást můj lid: »Proč mi nezbudujete cedrový dům?«",
			"7": "<span>7</span> Nyní tedy promluvíš takto k mému služebníku Davidovi: Toto praví Hospodin zástupů: Vzal jsem tě z pastvin od stáda, abys byl vévodou nad mým lidem, nad Izraelem.",
			"8": "<span>8</span> Byl jsem s tebou, ať jsi šel kamkoli. Vyhladil jsem před tebou všechny tvé nepřátele. Tvé jméno jsem učinil tak veliké, jako je jméno velikánů na zemi.",
			"9": "<span>9</span> I svému lidu, Izraeli, jsem připravil místo a zasadil jej; tam bude bydlet a už nikdy nebude znepokojován, už jej nebudou vysávat bídáci jako dřív.",
			"10": "<span>10</span> Ode dnů, kdy jsem správou svého izraelského lidu pověřil soudce, podrobil jsem ti všechny nepřátele. Oznamuji ti, že Hospodin vybuduje dům tobě.",
			"11": "<span>11</span> Až se naplní tvé dny a ty odejdeš ke svým otcům, dám po tobě povstat tvému potomku z tvých synů a upevním jeho království.",
			"12": "<span>12</span> Ten mi vybuduje dům a já upevním jeho trůn navěky.",
			"13": "<span>13</span> Já mu budu Otcem a on mi bude synem. Svoje milosrdenství mu neodejmu, jako jsem odňal tomu, který byl před tebou.",
			"14": "<span>14</span> Ustanovím jej ve svém domě a ve svém království navěky. Jeho trůn bude navěky upevněn.\"",
			"15": "<span>15</span> Nátan k Davidovi promluvil ve smyslu všech těchto slov a celého tohoto vidění.",
			"16": "<span>16</span> Král David pak vešel, usedl před Hospodinem a řekl: \"Co jsem, Hospodine Bože, a co je můj dům, že jsi mě přivedl až sem?",
			"17": "<span>17</span> A i to bylo v tvých očích málo, Bože. Dokonce přislibuješ domu svého služebníka dlouhá léta. Shlédl jsi na mě, jako bych byl člověk vysoké hodnosti, Hospodine Bože.",
			"18": "<span>18</span> Co může David ještě dodat k slávě, kterou jsi dal svému služebníku? Vždyť ty znáš svého služebníka.",
			"19": "<span>19</span> Hospodine, kvůli svému služebníku a podle svého srdce jsi učinil celou tuto velikou věc a dal o všech těchto velikých věcech vědět.",
			"20": "<span>20</span> Hospodine, není žádného jako ty, není Boha kromě tebe, podle toho všeho, co jsme na vlastní uši slyšeli.",
			"21": "<span>21</span> Kdo je jako tvůj izraelský lid, jediný pronárod na zemi, jejž si Bůh přišel vykoupit za lid; učinil sis jméno velikými a hroznými činy, když jsi vypudil pronárody před svým lidem, který sis vykoupil z Egypta.",
			"22": "<span>22</span> Ty jsi stanovil, aby tvůj izraelský lid byl tvým lidem navěky, a sám ses jim stal, Hospodine, Bohem.",
			"23": "<span>23</span> Nyní tedy, Hospodine Bože, ať se navěky věrné ukáže tvé slovo, které jsi promluvil o svém služebníku a o jeho domu. Učiň, jak jsi promluvil.",
			"24": "<span>24</span> Ať se navěky osvědčí jako věrné a veliké tvé jméno, ať se říká: Hospodin zástupů, Bůh Izraele, je Izraeli Bohem. A dům tvého služebníka Davida ať je před tebou pevný.",
			"25": "<span>25</span> Neboť ty, Bože, jsi svému služebníku ohlásil, že mu vybuduješ dům. Proto tvůj služebník našel odvahu modlit se před tebou.",
			"26": "<span>26</span> Ano, Hospodine, ty sám jsi Bůh. Přislíbil jsi svému služebníku takové dobrodiní.",
			"27": "<span>27</span> Nyní tedy požehnej laskavě domu svého služebníka, aby trval před tebou navěky. Dáš-li mu, Hospodine, požehnání, bude požehnán navěky!\""
		},
		"18": {
			"1": "<span>1</span> Potom David porazil Pelištejce a podrobil si je a vyrval Gat a jeho vesnice z rukou Pelištejců.",
			"2": "<span>2</span> Porazil i Moábce. Moábci se stali Davidovými otroky a odváděli dávky.",
			"3": "<span>3</span> David také porazil u Chamátu Hadad-ezera, krále Sóby, když táhl, aby nad řekou Eufratem vztyčil znamení své moci.",
			"4": "<span>4</span> David zajal tisíc jeho koní k vozům a sedm tisíc jezdců a dvacet tisíc pěšáků. Všechny koně k vozům David ochromil, zanechal koně jen ku stu vozům.",
			"5": "<span>5</span> Hadad-ezerovi, králi Sóby, přišli na pomoc Aramejci z Damašku. David pobil z Aramejců dvaadvacet tisíc mužů.",
			"6": "<span>6</span> Pak David umístil do damašského Aramu výsostná znamení. Tak se i Aramejci stali Davidovými otroky a odváděli dávky. Hospodin Davida zachraňoval, ať šel kamkoli.",
			"7": "<span>7</span> David také pobral zlaté štíty, které na sobě měli Hadad-ezerovi služebníci, a přinesl je do Jeruzaléma.",
			"8": "<span>8</span> Z Tibchatu a Kúnu, Hadad-ezerových měst, pobral David velké množství bronzu; z něho udělal Šalomoun bronzové moře a sloupy i bronzové nádoby.",
			"9": "<span>9</span> Když Toú, král Chamátu, uslyšel, že David porazil celé vojsko Hadad-ezera, krále Sóby,",
			"10": "<span>10</span> poslal svého syna Hadórama ke králi Davidovi, aby mu popřál pokoje a dobrořečil mu za jeho boj a vítězství nad Hadad-ezerem, že jej porazil; Hadad-ezer vedl totiž s Toúem války. Hadóram přinesl všelijaké předměty zlaté, stříbrné a měděné.",
			"11": "<span>11</span> I ty král David zasvětil Hospodinu se stříbrem a zlatem, které vzal od všech pronárodů, od Edómců, Moábců, Amónovců, Pelištejců a Amáleka.",
			"12": "<span>12</span> Abšaj, syn Serújin, pobil z Edómců v Solném údolí osmnáct tisíc mužů.",
			"13": "<span>13</span> V Edómu umístil David výsostná znamení. Všichni Edómci se stali Davidovými otroky. Hospodin Davida zachraňoval, ať šel kamkoli.",
			"14": "<span>14</span> David kraloval nad celým Izraelem a prosazoval právo a spravedlnost pro všechen svůj lid.",
			"15": "<span>15</span> Jóab, syn Serújin, byl vrchním velitelem, Jóšafat, syn Achílúdův, byl kancléřem.",
			"16": "<span>16</span> Sádok, syn Achítúbův, a Abímelek, syn Ebjátarův, byli kněžími. Šavša byl písařem.",
			"17": "<span>17</span> Benajáš, syn Jójadův, byl nad Keretejci a Peletejci. Davidovi synové byli pobočníky krále."
		},
		"19": {
			"1": "<span>1</span> Potom se stalo, že zemřel král Amónovců Náchaš a po něm se ujal království jeho syn.",
			"2": "<span>2</span> I řekl David: \"Prokáži milosrdenství Chanúnovi, synu Náchašovu. Vždyť jeho otec prokazoval milosrdenství mně.\" David tedy poslal posly, aby ho potěšil v zármutku nad otcem. Davidovi služebníci přišli k Chanúnovi do země Amónovců, aby ho potěšili.",
			"3": "<span>3</span> Amónovští velitelé však Chanúna podněcovali: \"Tobě se zdá, že David chce uctít tvého otce? Že k tobě poslal těšitele? Nepřišli k tobě jeho služebníci spíše proto, aby prozkoumali a rozvrátili zemi?\"",
			"4": "<span>4</span> Chanún dal tedy Davidovy služebníky oholit, dal uříznout jejich roucho do poloviny, až do rozkroku, a tak je propustil.",
			"5": "<span>5</span> I šli. Když to o těch mužích oznámili Davidovi, poslal jim naproti, protože ti muži byli velice zhanobeni. Král nařídil: \"Zůstaňte v Jerichu, dokud vám brady neobrostou; pak se vrátíte.\"",
			"6": "<span>6</span> Amónovci viděli, že zavinili Davidovu nelibost. Proto Chanún s Amónovci poslal tisíc talentů stříbra, aby si najali koně k vozům a jezdce od Aramejců z Dvojříčí, Aramejců z Maaky a ze Sóby.",
			"7": "<span>7</span> Najali si třicet dva tisíce koní k vozům od krále z Maaky a jeho lid. Ti přitáhli a utábořili se proti Médebě. Také Amónovci se shromáždili ze svých měst a přitáhli do boje.",
			"8": "<span>8</span> Když to David uslyšel, poslal Jóaba s celým vojem bohatýrů.",
			"9": "<span>9</span> Amónovci vytáhli a seřadili se k boji u vchodu do města. Králové, kteří přitáhli, stáli stranou v poli.",
			"10": "<span>10</span> Když Jóab spatřil, že má proti sobě bitevní řady vpředu i vzadu, vybral si nejlepší ze všech izraelských mladíků a seřadil je proti Aramejcům.",
			"11": "<span>11</span> Zbytek lidu podřídil svému bratru Abšajovi. I seřadili se proti Amónocům.",
			"12": "<span>12</span> Řekl: \"Budou-li mít Aramejci nade mnou převahu, přijdeš mi na pomoc. Budou-li mít Amónovci převahu nad tebou, přijdu ti na pomoc já.",
			"13": "<span>13</span> Buď rozhodný! Vzchopme se! Za náš lid, za města našeho boha! Hospodin nechť učiní, co uzná za dobré.\"",
			"14": "<span>14</span> Jóab a lid, který byl s ním, se dali do boje proti Aramejcům; ti před ním utekli.",
			"15": "<span>15</span> Když Amónovci viděli, že Aramejci utíkají, utekli také oni před jeho bratrem Abšajem a vešli do města. Jóab pak přišel do Jeruzaléma.",
			"16": "<span>16</span> Když Aramejci viděli, že jsou Izraelem poraženi, poslali posly, aby přivedli Aramejce, kteří byli za Řekou. V jejich čele byl Šófak, velitel Hadad-ezerova vojska.",
			"17": "<span>17</span> Oznámili to Davidovi a on shromáždil celý Izrael, překročil Jordán, přitáhl k nim a seřadil se proti nim. David se seřadil k boji proti Aramejcům a ti s ním bojovali.",
			"18": "<span>18</span> Aramejci se však dali před Izraelem na útěk. David pobil koně od sedmi tisíc aramejských vozů a čtyřicet tisíc pěšáků, usmrtil též Šófaka, velitele vojska.",
			"19": "<span>19</span> Když Hadad-ezerovi služebníci viděli, že jsou Izraelem poraženi, uzavřeli s Davidem příměří a sloužili mu. Aramejci pak už nikdy nechtěli jít Amónovcům na pomoc."
		},
		"20": {
			"1": "<span>1</span> Na přelomu roku, v době, kdy králové táhnou do boje, vedl Jóab vojenskou výpravu, hubil zemi Amónovců a oblehl Rabu. David však zůstal v Jeruzalémě. Jóab udeřil na Rabu a rozbořil ji.",
			"2": "<span>2</span> David sňal z hlavy jejich Krále korunu. Shledal, že váží talent zlata; byl do ní vsazen drahokam. Bývala pak na hlavě Davidově. Z města odvezl též velké množství kořisti.",
			"3": "<span>3</span> Lid, který byl v něm, odvedl a přidělil k pilám, železným špičákům a sekyrám. Tak David naložil se všemi městy Amónovců. Pak se David i všechen lid vrátili do Jeruzaléma.",
			"4": "<span>4</span> O něco později došlo k boji s Pelištejci v Gezeru. Tehdy ubil Sibekaj Chúšatský Sipaje z rodu obrů; ti byli pokořeni.",
			"5": "<span>5</span> Když se znovu strhl boj s Pelištejci, Elchánan, syn Jaírův, ubil Lachmího, bratra Goliáše Gatského. Násada jeho kopí byla jako tkalcovské vratidlo.",
			"6": "<span>6</span> A opět se strhl boj v Gatu. Tam byl obrovitý muž, který měl po šesti prstech, celkem čtyřiadvacet prstů. Ten také pocházel z obrů.",
			"7": "<span>7</span> Když tupil Izraele, ubil ho Jónatan, syn Šimey, Davidova bratra.",
			"8": "<span>8</span> Ti pocházeli z gatských obrů. Padli do rukou Davida a rukou jeho služebníků."
    },
		"21": {
			"1": "<span>1</span> Proti Izraeli povstal satan a podnítil Davida, aby sečetl Izraele.",
			"2": "<span>2</span> David poručil Jóabovi a předákům lidu: \"Jděte, spočítejte Izraele od Beer-šeby až k Danu a podejte mi zprávu, chci znát jejich počet.\"",
			"3": "<span>3</span> Jóab králi namítl: \"Nechť Hospodin zvětší svůj lid třeba stokrát. Což nejsou ti všichni, králi, můj pane, služebníky mého pána? Proč to chce můj pán zjistit? Proč se má Izrael provinit?\"",
			"4": "<span>4</span> Královo rozhodnutí bylo však pro Jóaba nezvratné. Jóab vyšel a prošel celý Izrael. Pak přišel do Jeruzaléma.",
			"5": "<span>5</span> Jóab odevzdal Davidovi celkový součet lidu: Všeho Izraele bylo jeden milión a sto tisíc mužů schopných tasit meč a Judy čtyři sta sedmdesát tisíc mužů schopných tasit meč.",
			"6": "<span>6</span> Léviho a Benjamína do nich Jóab nezapočítal, neboť královo rozhodnutí pokládal za ohavnost.",
			"7": "<span>7</span> Byla to zlá věc i v očích Božích, proto Bůh Izraele ranil.",
			"8": "<span>8</span> David volal k Bohu: \"Velmi jsem zhřešil, že jsem učinil tuto věc. Nyní přenes prosím vinu svého služebníka, neboť jsem si počínal jako velký pomatenec.\"",
			"9": "<span>9</span> Hospodin promluvil ke Gádovi, Davidovu vidoucímu:",
			"10": "<span>10</span> \"Jdi a promluv k Davidovi: Toto praví Hospodin: Chystám na tebe trojí. Jedno z toho si vyber a já tak s tebou naložím.\"",
			"11": "<span>11</span> Gád přišel k Davidovi a řekl mu: \"Toto praví Hospodin: Zvol si:",
			"12": "<span>12</span> Tři roky hladu, nebo tři měsíce být ničen protivníky a stíhán mečem nepřátel, anebo po tři dny Hospodinův meč, totiž mor v zemi, a Hospodinův anděl bude šířit zkázu po celém izraelském území. Nuže tedy, co mám vyřídit tomu, který mě poslal?\"",
			"13": "<span>13</span> David Gádovi odvětil: \"Je mi velmi úzko. Nechť prosím upadnu do rukou Hospodinu, neboť jeho slitování je přenesmírné, jen ať nepadnu do rukou lidských.\"",
			"14": "<span>14</span> Hospodin tedy dopustil na Izraele mor. I padlo z Izraele sedmdesát tisíc mužů.",
			"15": "<span>15</span> Bůh vyslal k Jeruzalému anděla, aby v něm šířil zkázu. Ale když šířil zkázu, Hospodin shlédl a pojala ho lítost nad tím zlem. Řekl andělu, jenž šířil zkázu: \"Dost! Již přestaň!\" Hospodinův anděl stál právě u humna Ornána Jebúsejského.",
			"16": "<span>16</span> David se rozhlédl a uviděl Hospodinova anděla, jak stojí mezi zemí a nebem. V ruce měl tasený meč, napřažený na Jeruzalém. David a starší, zahalení v žíněná roucha, padli tváří k zemi.",
			"17": "<span>17</span> David volal k Bohu: \"Což jsem sčítání lidu nenařídil já sám? Já sám jsem zhřešil, já sám jsem se dopustil takového zla! Co však učinily tyto ovce? Hospodine, můj Bože, buď tedy tvá ruka proti mně a proti domu mého otce, jen ať ta pohroma nestíhá tvůj lid!\"",
			"18": "<span>18</span> Hospodinův anděl nařídil Gádovi, aby Davidovi řekl: \"Ať David vystoupí a vystaví Hospodinu oltář na humně Ornána Jebúsejského.\"",
			"19": "<span>19</span> David vystoupil podle Gádova slova, jež promluvil v Hospodinově jménu.",
			"20": "<span>20</span> Ornán se obrátil a spatřil toho anděla; jeho čtyři synové, kteří byli s ním, se skrývali a Ornán mlátil pšenici.",
			"21": "<span>21</span> Když David došel k Ornánovi, Ornán vzhlédl a uviděl Davida. Vyšel z humna a dvakrát se Davidovi poklonil tváří k zemi.",
			"22": "<span>22</span> David Ornánovi řekl: \"Přenech mi to místo, své humno. Chci na něm vybudovat Hospodinu oltář. Dej mi je za plnou cenu stříbra, ať je od lidu odvrácena pohroma.\"",
			"23": "<span>23</span> Ornán Davidovi odvětil: \"Vezmi si je. Ať král, můj pán, udělá, co uzná za dobré. Hle, přidám dobytek k zápalným obětem a smyky jako dříví, též pšenici jako obětní dar. To vše dám.\"",
			"24": "<span>24</span> Ale král David Ornánovi řekl: \"Nikoli. Odkoupím to od tebe za plnou cenu stříbra. Vždyť nemohu přinést Hospodinu, co je tvé, nemohu obětovat zápalnou oběť darovanou.\"",
			"25": "<span>25</span> David tedy dal Ornánovi za to místo obnos šesti set šekelů zlata.",
			"26": "<span>26</span> I vybudoval tam David Hospodinu oltář, obětoval zápalné a pokojné oběti a vzýval Hospodina. Ten mu odpověděl ohněm seslaným z nebe na oltář pro zápalné oběti.",
			"27": "<span>27</span> Hospodin pak poručil andělu, aby schoval meč do pochvy.",
			"28": "<span>28</span> V té době, kdy David zjistil, že jej Hospodin na humně Ornána Jebúsejského vyslyšel, začal tam obětovat.",
			"29": "<span>29</span> Ale Hospodinův příbytek, který zhotovil Mojžíš na poušti, i oltář pro zápalné oběti byly v té době na posvátném návrší v Gibeónu.",
			"30": "<span>30</span> Tam David nemohl chodit, aby se dotazoval Hospodina, neboť byl mečem Hospodinova anděla přestrašen."
		},
		"22": {
			"1": "<span>1</span> David prohlásil: \"Zde bude dům Hospodina Boha, zde bude mít Izrael oltář k zápalným obětem.",
			"2": "<span>2</span> David poručil soustředit ty, kdo pobývali v izraelské zemi jako hosté, a udělal z nich lamače, kteří připravovali kvádry pro stavbu Božího domu.",
			"3": "<span>3</span> David také připravil mnoho železa na čepy pro křídla bran a na skoby a tak mnoho mědi, že se ani nedala zvážit.",
			"4": "<span>4</span> Také cedrového dřeva bezpočet; to množství cedrového dřeva přiváželi Davidovi Sidóňané a Týřané.",
			"5": "<span>5</span> David řekl: \"Můj syn Šalomoun je ještě mladíček útlého věku. Má-li vybudovat Hospodinův dům do mohutné výše, aby proslul nádherou po všech zemích, musím pro to konat přípravy.\" Proto David ještě před svou smrtí vykonal mnoho příprav.",
			"6": "<span>6</span> Pak zavolal svého syna Šalomouna a přikázal mu, aby Hospodinu, Bohu Izraele, vybudoval dům.",
			"7": "<span>7</span> David Šalomounovi řekl: \"Můj synu, já sám jsem měl v úmyslu vybudovat dům pro jméno Hospodina, svého Boha.",
			"8": "<span>8</span> Stalo se však ke mně slovo Hospodinovo: »Prolil jsi mnoho krve, vedl jsi velké války. Nebudeš budovat dům pro mé jméno, protože jsi na zemi přede mnou prolil mnoho krve.",
			"9": "<span>9</span> Hle, narodí se ti syn. Ten bude mužem odpočinutí. Jemu dám odpočinout od všech jeho okolních nepřátel. Vždyť jeho jméno bude Šalomoun (to je Pokojný). Za jeho dnů poskytnu Izraeli pokoj a mír.",
			"10": "<span>10</span> On vybuduje dům pro mé jméno. On se stane mým synem a já mu budu Otcem. Jeho královský trůn nad Izraelem upevním navěky.«",
			"11": "<span>11</span> Nuže, můj synu, nechť je s tebou Hospodin. Se zdarem vybuduješ dům Hospodina, svého Boha, jak to o tobě vyřkl.",
			"12": "<span>12</span> Kéž ti Hospodin dá prozíravost a rozumnost, až tě ustanoví nad Izraelem, abys dbal na zákon Hospodina, svého Boha.",
			"13": "<span>13</span> Jen tehdy budeš mít zdar, budeš-li bedlivě plnit nařízení a řády, které Hospodin přikázal Mojžíšovi pro Izraele. Buď rozhodný a udatný! Neboj se a neděs!",
			"14": "<span>14</span> Hle, i ve svém trápení jsem pro Hospodinův dům připravil sto tisíc talentů zlata, milión talentů stříbra, mědi a železa tolik, že se ani nedá zvážit. Připravil jsem též dřevo a kámen. A ty k tomu ještě přidáš.",
			"15": "<span>15</span> S tebou bude při díle mnoho dělníků, lamačů, kameníků a tesařů, každý, kdo je dovedný v nějakém díle.",
			"16": "<span>16</span> Zlata, stříbra, mědi a železa je bezpočet. Dej se do práce a nechť je s tebou Hospodin!\"",
			"17": "<span>17</span> David přikázal všem izraelským předákům, aby jeho synu Šalomounovi byli nápomocni:",
			"18": "<span>18</span> \"Což není Hospodin, váš Bůh, s vámi? Dal vám odpočinout ode všech okolních nepřátel. Vydal mi do rukou obyvatele země. Země je podmaněna Hospodinu a jeho lidu.",
			"19": "<span>19</span> Nyní se tedy ze srdce a z duše dotazujte Hospodina, svého Boha, a dejte se do budování svatyně Boha Hospodina, abyste mohli vnést schránu Hospodinovy smlouvy a svaté Boží náčiní do domu, který bude pro Hospodinovo jméno vybudován.\""
		},
		"23": {
			"1": "<span>1</span> Když David zestárl a byl sytý dnů, ustanovil za krále nad Izraelem svého syna Šalomouna.",
			"2": "<span>2</span> Shromáždil všechny izraelské velitele, kněze a lévijce.",
			"3": "<span>3</span> Lévijci byli sečteni od třicetiletých výše; seznam jednotlivců činil třicet osm tisíc mužů.",
			"4": "<span>4</span> Z nich bylo čtyřiadvacet tisíc pověřeno dohledem nad dílem domu Hospodinova, šest tisíc bylo správci a soudci,",
			"5": "<span>5</span> čtyři tisíce vrátnými a čtyři tisíce oslavovaly Hospodina na nástroje, které dal k oslavování Hospodina udělat.",
			"6": "<span>6</span> David je rozdělil do tříd",
			"7": "<span>7</span> podle Geršóna, Kehata a Merarího, synů Léviho. Ke Geršónovi patří Laedán a Šimeí.",
			"8": "<span>8</span> Synové Laedánovi byli tři: Přední byl Jechíel, pak Zétam a Jóel.",
			"9": "<span>9</span> Synové Šimeího: Šelómít, Chazíel a Háran. Ti tři byli v Laedánových rodech přední.",
			"10": "<span>10</span> Synové Šimeího: Jáchat, Zina, Jeúš a Bería. To byli čtyři synové Šimeího.",
			"11": "<span>11</span> Jáchat byl přední, Zína druhý; Jeúš a Bería neměli mnoho synů, byli tedy v jednom rodovém soupisu.",
			"12": "<span>12</span> Synové Kehatovi byli čtyři: Amrám, Jishár, Chebrón a Uzíel.",
			"13": "<span>13</span> Synové Amrámovi: Áron a Mojžíš. Áron byl oddělen, aby střežil svatost velesvatyně, on i jeho synové, aby pálil před Hospodinem kadidlo a přisluhoval mu a navěky v jeho jménu udílel požehnání.",
			"14": "<span>14</span> Mojžíš byl muž Boží. Jeho synové byli jmenováni s kmenem Léviho.",
			"15": "<span>15</span> Synové Mojžíšovi byli Geršóm a Elíezer.",
			"16": "<span>16</span> Synové Geršómovi: přední Šebúel.",
			"17": "<span>17</span> Synové Elíezerovi: přední Rechabjáš. Elíezer neměl jiné syny, zato synové Rechabjášovi se velmi rozmnožili.",
			"18": "<span>18</span> Synové Jishárovi: přední Šelómít.",
			"19": "<span>19</span> Synové Chebrónovi: přední Jeriáš, druhý Amarjáš, třetí Jachazíel, čtvrtý Jekameám.",
			"20": "<span>20</span> Synové Uzíelovi: přední Míka, druhý Jišijáš.",
			"21": "<span>21</span> Synové Merarího: Machlí a Múši. Synové Machlího: Eleazar a Kíš.",
			"22": "<span>22</span> Eleazar zemřel, aniž měl syny; měl dcery. Vzali si je jejich bratranci, synové Kíšovi.",
			"23": "<span>23</span> Synové Múšiho byli tři: Machlí, Éder a Jeremót.",
			"24": "<span>24</span> To jsou Léviovci podle svých otcovských rodů, přední v rodech, povolaní do služby a zapsaní do jmenného seznamu podle jednotlivců, konající služebné dílo pro Hospodinův dům, a to od dvacetiletých výše.",
			"25": "<span>25</span> David totiž řekl: \"Hospodin, Bůh Izraele, dal svému lidu odpočinutí. Navěky bude přebývat v Jeruzalémě.",
			"26": "<span>26</span> Lévijci už tedy nebudou nosit příbytek a všechno náčiní potřebné k jeho obsluze.\"",
			"27": "<span>27</span> Proto podle posledního Davidova rozkazu byli do seznamu zahrnuti Léviovci od dvacetiletých výše.",
			"28": "<span>28</span> Byli dáni k ruce Áronovým synům, aby sloužili v Hospodinově domě, na jeho nádvořích a v komorách, aby dbali na čistotu všeho svatého a konali v Božím domě službu;",
			"29": "<span>29</span> aby měli na starosti rovnání předkladných chlebů, bílou mouku pro přídavné oběti, oplatky nekvašených chlebů, pánve a mísy, různé odměrky a míry;",
			"30": "<span>30</span> aby nastoupili každé ráno k vzdávání chval a oslavování Hospodina, a právě tak i večer;",
			"31": "<span>31</span> aby před Hospodinem ustavičně přisluhovali při obětování zápalných obětí Hospodinu ve dnech odpočinku, o novoluních a v určených časech, a to v tom počtu, jak určuje jejich řád.",
			"32": "<span>32</span> Konali strážní službu při stanu setkávání, strážní službu při svatyni a strážní službu u svých bratří, synů Áronových, při službě v domě Hospodinově."
		},
		"24": {
			"1": "<span>1</span> Třídy synů Áronových: Synové Áronovi byli Nádab, Abíhú, Eleazar a Ítamar.",
			"2": "<span>2</span> Nádab a Abíhú zemřeli dříve než jejich otec a neměli syny. Jako kněží sloužili Eleazar a Ítamar.",
			"3": "<span>3</span> David i Sádok ze synů Eleazarových a Achímelek ze synů Ítamarových je rozdělili do služeb podle jejich povolání.",
			"4": "<span>4</span> Ukázalo se, že synové Eleazarovi jsou co do počtu předních mužů četnější než synové Ítamarovi. Podle toho je rozdělili: předních mužů ze synů Eleazarových bylo podle otcovských rodů šestnáct, synů Ítamarových podle otcovských rodů osm.",
			"5": "<span>5</span> Rozdělili je navzájem losem, neboť předáci svatyně a předáci Boží měli být ze synů Eleazarových a ze synů Ítamarových.",
			"6": "<span>6</span> Písař z Léviho rodu Šemajáš, syn Netaneelův, sepsal lévijce před králem a předáky, před knězem Sádokem a Achímelekem, synem Ebjátarovým, a předními z otcovských rodů kněžských a lévijských. Jeden rod byl určen Eleazarovi a ještě jeden, jiný byl určen Ítamarovi.",
			"7": "<span>7</span> První los padl na Jójaríba, druhý na Jedajáše,",
			"8": "<span>8</span> třetí na Chárima, čtvrtý na Seórima,",
			"9": "<span>9</span> pátý na Malkijáše, šestý na Mijámina,",
			"10": "<span>10</span> sedmý na Kósa, osmý na Abijáše,",
			"11": "<span>11</span> devátý na Jéšuu, desátý na Šekanjáše,",
			"12": "<span>12</span> jedenáctý na Eljašíba, dvanáctý na Jákima,",
			"13": "<span>13</span> třináctý na Chupu, čtrnáctý na Ješebába,",
			"14": "<span>14</span> patnáctý na Bilgu, šestnáctý na Iméra,",
			"15": "<span>15</span> sedmnáctý na Chezíra, osmnáctý na Pisesa,",
			"16": "<span>16</span> devatenáctý na Petachjáše, dvacátý na Jechezkéla,",
			"17": "<span>17</span> jedenadvacátý na Jakína, dvaadvacátý na Gamúla,",
			"18": "<span>18</span> třiadvacátý na Delajáše, čtyřiadvacátý na Maazjáše.",
			"19": "<span>19</span> Toto jsou povolaní do služby, aby vstupovali do Hospodinova domu podle svého řádu za dozoru svého otce Árona, jak mu jej přikázal Hospodin, Bůh Izraele.",
			"20": "<span>20</span> Ostatní Léviovci: ze synů Amrámových Šúbael, ze synů Šúbaelových Jechdejáš.",
			"21": "<span>21</span> Za Rechabjáše: přední ze synů Rechabjášových Jišijáš.",
			"22": "<span>22</span> Za Jishára: Šelomót, ze synů Šelomótových Jáchat.",
			"23": "<span>23</span> Synové Jerijášovi: Amarjáš byl druhý, Jachazíel třetí, Jekameám čtvrtý.",
			"24": "<span>24</span> Synové Uzíelovi: Míka. Ze synů Míkových: Šámir.",
			"25": "<span>25</span> Bratr Míkův byl Jišijáš. Ze synů Jišijášových Zekarjáš.",
			"26": "<span>26</span> Synové Merarího: Machlí a Múši, synové jeho syna Jaazijáše.",
			"27": "<span>27</span> Synové Merarího: za Jaazijáše jeho syn a Šóham, Zákur a Ibrí.",
			"28": "<span>28</span> Za Machlího Eleazar, ale ten neměl syny.",
			"29": "<span>29</span> Za Kíše synové Kíšovi, Jerachmeel.",
			"30": "<span>30</span> Synové Múšiho: Machlí, Éder a Jerímót. To jsou Léviovci podle svých otcovských rodů.",
			"31": "<span>31</span> Ty rovněž vylosovali, předního stejně jako nejmladšího bratra, aby byli po boku svým bratřím, synům Áronovým, před králem Davidem i před Sádokem a Achímelekem a předními muži z otcovských rodů kněžských a lévijských."
		},
		"25": {
			"1": "<span>1</span> David a velitelé vojska přidělili též službu synům Asafovým, Hémanovým a Jedútúnovým, aby vyhlašovali proroctví při citaře, harfě a při cymbálech. Seznam mužů, konajících služebné dílo:",
			"2": "<span>2</span> Ze synů Asafových Zákur, Josef, Netanjáš a Asaréla. Synové Asafovi byli k ruce Asafovi, když z králova pověření vyhlašoval proroctví.",
			"3": "<span>3</span> Za Jedútúna šest Jedútúnových synů: Gedaljáš, Serí, Ješajáš, Chašabjáš a Matitjáš; byli k ruce svému otci Jedútúnovi, jenž vyhlašoval proroctví při citaře k chvále a oslavě Hospodina.",
			"4": "<span>4</span> Za Hémana synové Hémanovi: Bukijáš, Matanjáš, Uzíel, Šebúel, Jerímót, Chananjáš, Chananí, Elíata, Gidaltí, Rómamtí-ezer, Jošbekáša, Malótí, Hótir a Machazíót.",
			"5": "<span>5</span> Ti všichni jsou synové Hémana, králova vidoucího, podle Božích slov o pozdvižení rohu. Bůh dal Hémanovi čtrnáct synů a tři dcery.",
			"6": "<span>6</span> Ti všichni byli k ruce svému otci při zpěvu v Hospodinově domě s cymbály, haframi a citarami, ke službě v domě Božím z králova pověření, k ruce Asafovi, Jedútúnovi a Hémanovi.",
			"7": "<span>7</span> Jejich počet spolu s jejich bratry vyučenými Hospodinovu zpěvu byl dvě stě osmdesát osm, samých mistrů.",
			"8": "<span>8</span> Losy pro strážní službu vrhli jak malému, tak velikému, mistru i učedníkovi.",
			"9": "<span>9</span> První z Asafova rodu byl vylosován Josef, druhý Gedaljáš; ten a jeho bratří a synové, celkem dvanáct mužů.",
			"10": "<span>10</span> Třetí Zákur, jeho synové a bratří, celkem dvanáct.",
			"11": "<span>11</span> Čtvrtý Jisrí, jeho synové a bratří, celkem dvanáct.",
			"12": "<span>12</span> Pátý Netanjáš, jeho synové a bratří, celkem dvanáct.",
			"13": "<span>13</span> Šestý Bukijáš, jeho synové a bratří, celkem dvanáct.",
			"14": "<span>14</span> Sedmý Jesaréla, jeho synové a bratří, celkem dvanáct.",
			"15": "<span>15</span> Osmý Ješajáš, jeho synové a bratří, celkem dvanáct.",
			"16": "<span>16</span> Devátý Matanjáš, jeho synové a bratří, celkem dvanáct.",
			"17": "<span>17</span> Desátý Šimeí, jeho synové a bratří, celkem dvanáct.",
			"18": "<span>18</span> Jedenáctý Azarel, jeho synové a bratří, celkem dvanáct.",
			"19": "<span>19</span> Dvanáctý Chašabjáš, jeho synové a bratří, celkem dvanáct.",
			"20": "<span>20</span> Podle třináctého losu Šúbael, jeho synové a bratří, celkem dvanáct.",
			"21": "<span>21</span> Podle čtrnáctého Matitjáš, jeho synové a bratří, celkem dvanáct.",
			"22": "<span>22</span> Podle patnáctého Jeremót, jeho synové a bratří, celkem dvanáct.",
			"23": "<span>23</span> Podle šestnáctého Chananjáš, jeho synové a bratří, celkem dvanáct.",
			"24": "<span>24</span> Podle sedmnáctého Jošbekáša, jeho synové a bratří, celkem dvanáct.",
			"25": "<span>25</span> Podle osmnáctého Chananí, jeho synové a bratří, celkem dvanáct.",
			"26": "<span>26</span> Podle devatenáctého Malótí, jeho synové a bratří, celkem dvanáct.",
			"27": "<span>27</span> Podle dvacátého Elijata, jeho synové a bratří, celkem dvanáct.",
			"28": "<span>28</span> Podle dvacátého prvého Hótir, jeho synové a bratří, celkem dvanáct.",
			"29": "<span>29</span> Podle dvacátého druhého Gidaltí, jeho synové a bratří, celkem dvanáct.",
			"30": "<span>30</span> Podle dvacátého třetího Machaziót, jeho synové a bratří, celkem dvanáct.",
			"31": "<span>31</span> Podle dvacátého čtvrtého Rómamtí-ezer, jeho synové a bratří, celkem dvanáct."
		},
		"26": {
			"1": "<span>1</span> Oddíly vrátných; z Kórachovců: Mešelemjáš, syn Kóreho ze synů Asafových.",
			"2": "<span>2</span> Synové Mešelemjášovi: prvorozený Zekarjáš, druhý Jedíael, třetí Zebadjáš, čtvrtý Jatníel,",
			"3": "<span>3</span> pátý Élam, šestý Jóchanan, sedmý Eljóenaj.",
			"4": "<span>4</span> Synové Obéd-edómovi: prvorozený Šemajáš, druhý Józabad, třetí Jóach, čtvrtý Sákar, pátý Netaneel,",
			"5": "<span>5</span> šestý Amíel, sedmý Jisakar, osmý Peúletaj, jemuž Bůh požehnal.",
			"6": "<span>6</span> Jeho synu Šemajášovi se narodili synové, kteří se stali vladaři svého rodu, neboť to byli udatní bohatýři.",
			"7": "<span>7</span> Synové Šemajášoi: Otní a jeho bratři Refael, Obéd a Elzábad, ti stateční; Elíhú a Semakjáš.",
			"8": "<span>8</span> Ti všichni pocházeli ze synů Obéd-edómových; jejich synové a bratři byli stateční muži, schopní k službě, celkem dvaašedesát Obéd-edómovců.",
			"9": "<span>9</span> Též Mešelemjáš měl osmnáct synů a bratří, statečných mužů.",
			"10": "<span>10</span> Synové Chósy ze synů Merarího: Šimrí byl přední, ačkoli nebyl prvorozený, ale jeho otec ho ustanovil za předního,",
			"11": "<span>11</span> druhý byl Chilkijáš, třetí Tebaljáš, čtvrtý Zekarjáš; všech synů a bratří Chósových bylo třináct.",
			"12": "<span>12</span> To jsou oddíly vrátných, přední z mužů, kteří drželi stráže po boku svých bratří při službě v Hospodinově domě.",
			"13": "<span>13</span> Jak o malém, tak o velkém losovali podle jejich otcovského rodu a podle jednotlivých bran.",
			"14": "<span>14</span> Los východní brány padl Šelemjášovi; jeho syn Zekarjáš byl prozíravý rádce. Losovali a na něj padl los severní brány.",
			"15": "<span>15</span> Na Obéd-edóma pak jižní a na jeho syny los zásobárny.",
			"16": "<span>16</span> Na Šupíma a Chósa západní brána, která vede k silnici vzhůru; stráž stála vedle stráže.",
			"17": "<span>17</span> Na východní straně bylo šest lévijců, na severní straně denně čtyři, na jižní straně denně čtyři, při zásobárně po dvou.",
			"18": "<span>18</span> V sloupořadí na západní straně čtyři, na silnici u sloupořadí dva.",
			"19": "<span>19</span> To jsou oddíly vrátných z Kórachovců a Meraríovců.",
			"20": "<span>20</span> Další lévijci: Achijáš byl nad sklady Božího domu i nad sklady svatých věcí.",
			"21": "<span>21</span> Synové Laedánovi, Geršónovci, přední z rodu Laedánova: Laedán Geršónský měl Jechíelího.",
			"22": "<span>22</span> Synové Jechíelího Zétam a jeho bratr Jóel byli nad sklady Hospodinova domu.",
			"23": "<span>23</span> Amrámci, Jishárci, Chebrónci a Ozíelci:",
			"24": "<span>24</span> Šebúel, syn Geršóma, syna Mojžíšova, se stal představeným nad sklady.",
			"25": "<span>25</span> Jeho bratr Elíezer měl syny Rechabjáše, Ješajáše, Jórama, Zikrího a Šelomóta.",
			"26": "<span>26</span> Ten Šelomót a jeho bratři byli nad všemi sklady svatých věcí, které oddělil jako svaté král David a přední z rodů, velitelé nad tisíci a nad sty a jiní velitelé vojska.",
			"27": "<span>27</span> Z válečné kořisti to oddělili jako svaté ku podpoře Hospodinova domu.",
			"28": "<span>28</span> Všechno, co oddělil jako svaté vidoucí Samuel, Saul, syn Kíšův, Abnér, syn Nérův, Jóab, syn Serújin, cokoli kdo oddělil jako svaté, vkládal do rukou Šelomóta a jeho bratří.",
			"29": "<span>29</span> Z Jishárců Kenanjáš a jeho synové byli určeni pro práci mimo chrám, za správce a soudce nad Izraelem.",
			"30": "<span>30</span> Z Chebrónců Chašabjáš a jeho bratří, tisíc sedm set statečných, byli určeni k dozoru nad Izraelem od Zajordání na západ pro všechno dílo Hospodinovo a pro královskou službu.",
			"31": "<span>31</span> U Chebrónců byl Jerijáš přední z Chebrónců, jejich čeledí a rodů. Ve čtyřicátém roce Davidova kralování byli vyhledáváni a mezi nimi nalezeni stateční bohatýři v gileádském Jaezeru.",
			"32": "<span>32</span> Jeho statečních bratří bylo dva tisíce sedm set předních osob z rodů. Král David je ustanovil nad Rúbenovci, Gádovci a nad polovinou kmene Manases ve všech věcech Božích i královských."
		},
		"27": {
			"1": "<span>1</span> Izraelci podle příslušného seznamu, přední z rodů, velitelé nad tisíci a nad sty a správci, kteří byli ve službách krále pro různé úkoly oddílů, jak měly nastupovat a končit službu, měsíc po měsíci, po všechny měsíce v roce. Každý oddíl měl dvacet čtyři tisíce mužů.",
			"2": "<span>2</span> Nad prvním oddílem pro první měsíc byl Jášobeám, syn Zabdíelův; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"3": "<span>3</span> Ze synů Peresových byl předním ze všech velitelů vojska pro první měsíc.",
			"4": "<span>4</span> Nad oddílem pro druhý měsíc byl Dódaj Achóchijský se svým oddílem a vévoda Miklót; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"5": "<span>5</span> Velitelem třetího voje pro třetí měsíc byl Benajáš, syn předního kněze Jójady; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"6": "<span>6</span> Týž Benajáš byl jedním ze třiceti bohatýrů a byl nad těmi třiceti. V jeho oddílu byl jeho syn Amízábad.",
			"7": "<span>7</span> Čtvrtý pro čtvrtý měsíc byl Asáel, bratr Jóabův, a po něm jeho syn Zebadjáš; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"8": "<span>8</span> Pátý pro pátý měsíc měl velitele Šamhúta Jizrašského; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"9": "<span>9</span> Šestý pro šestý měsíc byl Íra, syn Íleše Tekójského; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"10": "<span>10</span> Sedmý pro sedmý měsíc byl Cheles Pelónský z Efrajimovců; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"11": "<span>11</span> Osmý pro osmý měsíc byl Sibekaj Chúšatský ze Zerachejců; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"12": "<span>12</span> Devátý pro devátý měsíc byl Abíezer Anatótský z Benjamínců; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"13": "<span>13</span> Desátý pro desátý měsíc byl Mahraj Netófský ze Zerachejců; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"14": "<span>14</span> Jedenáctý pro jedenáctý měsíc byl Benajáš Pireatónský z Efrajimovců; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"15": "<span>15</span> Dvanáctý pro dvanáctý měsíc byl Cheldaj Netófský z Otníela; jeho oddíl měl dvacet čtyři tisíce mužů.",
			"16": "<span>16</span> Izraelským kmenům veleli: Rúbenovi vévoda Elíezer, syn Zikríův, Šimeónovi Šefatjáš, syn Maakův,",
			"17": "<span>17</span> Lévimu Chašabjáš, syn Kemúelův, Áronovi Sádok,",
			"18": "<span>18</span> Judovi Elíhú z bratří Davidových, Isacharovi Omrí, syn Míkaelův,",
			"19": "<span>19</span> Zabulónovi Jišmajáš, syn Obadjášův, Neftalímu Jerímót, syn Azríelův,",
			"20": "<span>20</span> Efrajimovcům Hóšea, syn Azazjášův, polovině kmene Manases Jóel, syn Pedajášův,",
			"21": "<span>21</span> druhé polovině Manasesa v Gileádu Jidó, syn Zekarjášův, Benjamínovi Jaasíel, syn Abnérův,",
			"22": "<span>22</span> Danovi Azarel, syn Jeróchamův. To byli velitelé izraelských kmenů.",
			"23": "<span>23</span> David ani nepořizoval součet od dvacetiletých níže, neboť Hospodin řekl, že Izraele rozmnoží jako nebeské hvězdy.",
			"24": "<span>24</span> Jóab, syn Serújin, sice začal se sčítáním, ale nedokončil je. Kvůli tomu totiž postihlo Izraele Hospodinovo rozlícení. Proto se jejich součet ani do součtu v letopisech krále Davida nedostal.",
			"25": "<span>25</span> Nad královskými sklady byl Azmávet, syn Adíelův, nad sklady na polích, ve městech, vesnicích a věžích Jónatan, syn Uzijášův.",
			"26": "<span>26</span> Nad těmi, kdo pracovali na polích, nad rolníky byl Ezrí, syn Kelúbův,",
			"27": "<span>27</span> nad vinicemi Šimeí Rámatský a nad sklady vína na vinicích Zabdí Šifmejský,",
			"28": "<span>28</span> nad olivami a fíkovníky v nížině Baal-chanan Gederský a nad sklady oleje Jóaš,",
			"29": "<span>29</span> nad skotem, který se pásl v Šáronu, Šitraj Šáronský a nad skotem v dolinách Šáfat, syn Adlajův,",
			"30": "<span>30</span> nad velbloudy Obíl Izmaelský, nad oslicemi Jechdejáš Meronótský,",
			"31": "<span>31</span> nad ovcemi a kozami Jazíz Hagrejský. Ti všichni byli předními správci jmění krále Davida.",
			"32": "<span>32</span> Jónatan, Davidův strýc, rádce, mistr a písař spolu s Jechíelem, synem Chakmóního, byli s královými syny.",
			"33": "<span>33</span> Achítofel byl královým rádcem a Chúšaj Arkíjský královým přítelem;",
			"34": "<span>34</span> po Achítofelovi Jójada, syn Benajášův, a Ebjátar. Velitelem králova vojska byl Jóab."
		},
		"28": {
			"1": "<span>1</span> David shromáždil do Jeruzaléma všechny izraelské předáky, předáky kmenů a předáky oddílů, kteří byli v králových službách, též velitele nad tisíci a nad sty i správce jmění a stád krále a jeho synů spolu s dvořany a bohatýry, každého statečného bohatýra.",
			"2": "<span>2</span> Král David povstal a řekl: \"Slyšte mě, moji bratří a můj lide! Já sám jsem měl v úmyslu vybudovat dům odpočinutí pro schránu Hospodinovy smlouvy, pro podnož nohou našeho Boha. Už jsem začal se stavebními přípravami.",
			"3": "<span>3</span> Ale Bůh mi řekl: »Ty nemůžeš vybudovat dům pro mé jméno, neboť jsi vedl mnoho bojů a prolil jsi mnoho krve.«",
			"4": "<span>4</span> Hospodin, Bůh Izraele, si vyvolil z celého domu mého otce mne, abych byl navěky králem nad Izraelem. Z Judy si totiž vyvolil vévodu a z domu Judova dům mého otce a mezi syny mého otce našel zalíbení ve mně. Tak mě ustanovil králem nad celým Izraelem.",
			"5": "<span>5</span> Ze všech mých synů, a Hospodin mi dal synů mnoho, si vyvolil mého syna Šalomouna, aby usedl na Hospodinův královský trůn nad Izraelem.",
			"6": "<span>6</span> Řekl mi: »Tvůj syn Šalomoun, ten vybuduje můj dům a má nádvoří, neboť jsem si ho vyvolil za syna. Já mu budu Otcem.",
			"7": "<span>7</span> Jeho království upevním navěky, jestliže bude rozhodně dodržovat mé příkazy a právní ustanovení tak jako dnes.«",
			"8": "<span>8</span> A nyní před zraky celého Izraele, Hospodinova shromáždění, a před sluchem našeho Boha: Bedlivě se vždy dotazujte na všechny příkazy Hospodina, svého Boha, abyste si udrželi tu dobrou zemi a mohli ji předat svým synům do dědictví navěky.\"",
			"9": "<span>9</span> \"Ty pak, můj synu Šalomoune, poznávej Boha svého otce a služ mu celým srdcem a ochotnou myslí. Hospodin zkoumá srdce všech, postřehne každý výtvor mysli. Budeš-li se ho dotazovat, dá se ti najít, jestliže ho opustíš, odvrhne tě navždy.",
			"10": "<span>10</span> Nyní hleď, Hospodin tě vyvolil, abys mu vybudoval dům, svatyni. Buď rozhodný a jednej!\"",
			"11": "<span>11</span> David předal svému synu Šalomounovi plán předsíně, jednotlivých částí domu, jeho pokladnic a přístřešků, jeho vestavěných pokojíků i domu pro příkrov schrány,",
			"12": "<span>12</span> též plán všeho, jak si to představoval, nádvoří Hospodinova domu a všech okolních komor, skladů při božím domě a skladů pro svaté věci,",
			"13": "<span>13</span> seznam tříd kněží a lévijců, rozpis veškerého služebného díla při Hospodinově domě, soupis všeho náčiní k službě při Hospodinově domě.",
			"14": "<span>14</span> Pro veškeré zlaté náčiní k různým službám předal zlato podle váhy, pro veškeré stříbrné náčiní, pro veškeré náčiní k různým službám stříbro podle váhy,",
			"15": "<span>15</span> pro zlaté svícny a zlaté kahánky na nich zlato podle váhy svícnu a kahánků, pro stříbrné svícny stříbro podle váhy svícnu a kahánků, na každý svícen zvlášť podle potřeby;",
			"16": "<span>16</span> dále zlato podle váhy pro stoly na rovnání předkladných chlebů, na každý stůl zvlášť, též stříbro na stříbrné stoly,",
			"17": "<span>17</span> na vidlice, kropenky i konve zlato čisté a zlato podle váhy na koflíky, na každý koflík zvlášť, též stříbro podle váhy na stříbrné koflíky, na každý koflík zvlášť,",
			"18": "<span>18</span> na kadidlový oltář přetavené zlato podle váhy; dále plán vozu a zlatých cherubů, rozprostírajících křídla a zastírajících schránu Hospodinovy smlouvy.",
			"19": "<span>19</span> \"To vše bylo zapsáno rukou Hospodinovou, abych mohl prozíravě připravit plán veškerého díla.\"",
			"20": "<span>20</span> David řekl svému synu Šalomounovi: \"Buď rozhodný a udatný, jednej! Neboj se a neděs! Hospodin Bůh, můj Bůh, bude s tebou. Nenechá tě klesnout a neopustí tě, dokud veškeré dílo prací na Hospodinově domě nedokončíš.",
			"21": "<span>21</span> A tu jsou třídy kněží a lévijců pro veškerou práci při Božím domě. S tebou pak budou předáci a všechen lid při veškerém díle, aby se všechna práce konala dobrovolně a moudře a podle každého tvého slova.\""
		},
		"29": {
			"1": "<span>1</span> Potom řekl král David celému shromáždění: \"Mého syna Šalomouna si vyvolil Bůh jako jediného, ač je mladíček útlého věku a dílo je obrovské. Nejde totiž o hrad pro člověka, ale pro Boha Hospodina.",
			"2": "<span>2</span> Pro dům svého Boha jsem připravil, co jsem mohl: zlato pro zlaté náčiní, stříbro pro stříbrné, měď pro měděné, železo pro železné, dřevo pro dřevěné, kameny karneolové pro vsazování, kameny pestře zbarvené a různé drahokamy i množství mramoru.",
			"3": "<span>3</span> A jelikož mám v domě svého Boha zalíbení, přidávám pro dům svého Boha i ze svého vlastního jmění zlato a stříbro navíc k tomu všemu, co jsem připravil pro svatyni:",
			"4": "<span>4</span> tři tisíce talentů ofírského zlata, sedm tisíc talentů přetaveného stříbra k obložení stěn domu,",
			"5": "<span>5</span> pro různé věci ze zlata a pro různé věci ze stříbra, pro veškeré dílo vyráběné řemeslníky. Kdo by chtěl dnes něco ze svého dobrovolně obětovat pro Hospodina?\"",
			"6": "<span>6</span> Předáci rodů izraelských kmenů, velitelé nad tisíci a nad sty i královští úředníci přinášeli dobrovolné dary.",
			"7": "<span>7</span> Na dílo Božího domu odevzdali pět tisíc talentů zlata a deset tisíc darejků, deset tisíc talentů stříbra a osmnáct tisíc talentů mědi a sto tisíc talentů železa.",
			"8": "<span>8</span> Kdo pak měl nějaké drahé kamení, odevzdal je na poklad Hospodinova domu do rukou Jechíela Geršónského.",
			"9": "<span>9</span> Lid se radoval z toho, co bylo dobrovolně darováno, že z celého srdce se odevzdávaly dobrovolné dary Hospodinu. Také král David se převelice radoval.",
			"10": "<span>10</span> I dobrořečil David Hospodinu před zraky celého shromáždění. Řekl: \"Požehnán jsi, Hospodine, Bože Izraele, našeho otce, od věků až na věky.",
			"11": "<span>11</span> Hospodine, tvá je velikost a bohatýrská síla, skvělost, stálost a velebnost, všechno, co je na nebi a na zemi, je tvé. Hospodine, tvé je království, ty jsi vyvýšen nade vším jako hlava.",
			"12": "<span>12</span> Bohatství a sláva pocházejí od tebe, ty panuješ nade vším, máš v rukou moc a bohatýrskou sílu, vyvýšení a utvrzení všeho je v tvých rukou.",
			"13": "<span>13</span> Nyní, Bože náš, vzdáváme ti chválu a oslavujeme tvé skvělé jméno.",
			"14": "<span>14</span> Vždyť co jsem já a co je můj lid, že máme možnost takto přinášet dobrovolné dary? Od tebe pochází všechno. Dáváme ti, co jsme přijali z tvých rukou.",
			"15": "<span>15</span> My jsme před tebou jen hosté a příchozí jako všichni naši otcové. Naše dny na zemi jsou jako stín a naděje není.",
			"16": "<span>16</span> Hospodine, Bože náš, všechno toto množství, jež jsme připravili, abychom vybudovali dům tobě, tvému svatému jménu, pochází z tvé ruky. Tobě patří všechno.",
			"17": "<span>17</span> Poznal jsem, můj Bože, že ty zkoumáš srdce a že máš zalíbení v přímosti. Přinesl jsem všechny tyto dobrovolné dary z přímého srdce. A nyní vidím, jak radostně ti přináší dobrovolné dary tvůj lid, který je zde.",
			"18": "<span>18</span> Hospodine, Bože Abrahama, Izáka a Izraele, našich otců, zachovej navěky to, co si tvůj lid ve svém srdci předsevzal, a jejich srdce si připrav pro sebe.",
			"19": "<span>19</span> Dej, ať můj syn Šalomoun z celého srdce dbá na tvé příkazy, na tvá svědectví a na tvá nařízení, aby vykonal všechno, co je třeba k vybudování hradu, k němuž jsem vykonal přípravy.\"",
			"20": "<span>20</span> Potom David vyzval celé shromáždění: \"Dobrořečte Hospodinu, svému Bohu!\" A celé shromáždění dobrořečilo Hospodinu, Bohu svých otců, padli na kolena a klaněli se Hospodinu i králi.",
			"21": "<span>21</span> Druhého dne připravili Hospodinu oběti. Obětovali Hospodinu oběti zápalné: tisíc býků, tisíc beranů a tisíc beránků s příslušnými úlitbami, množství obětí za celého Izraele.",
			"22": "<span>22</span> Onoho dne jedli a pili před Hospodinem s velikou radostí. Potom vyhlásili podruhé Šalomouna, syna Davidova, králem a pomazali jej Hospodinu za vévodu a Sádoka za kněze.",
			"23": "<span>23</span> I dosedl Šalomoun na Hospodinův trůn jako král místo svého otce Davida a provázel ho zdar a celý Izrael ho poslouchal;",
			"24": "<span>24</span> též všichni předáci a bohatýři, také všichni synové Davida se králi Šalomounovi podrobili.",
			"25": "<span>25</span> Hospodin Šalomouna před zraky celého Izraele nesmírně vyvýšil. Dal mu takovou královskou velebnost, jakou před ním neměl žádný král nad Izraelem.",
			"26": "<span>26</span> David, syn Jišajův, kraloval nad celým Izraelem.",
			"27": "<span>27</span> Kraloval nad Izraelem čtyřicet let, sedm let kraloval v Chebrónu a třicet tři léta kraloval v Jeruzalémě.",
			"28": "<span>28</span> I zemřel v utěšeném stáří nasycen dny, bohatstvím a slávou. Po něm kraloval jeho syn Šalomoun.",
			"29": "<span>29</span> Příběhy krále Davida jsou od začátku až do konce zapsány v příbězích Samuela, vidoucího, v příbězích proroka Nátana a v příbězích Gáda, jenž měl dar vidění,",
			"30": "<span>30</span> spolu se vším, co se týká jeho království a bohatýrských činů i všeho, co se s ním a s Izraelem i se všemi královstvími okolních zemí v oněch časech událo."
		}
	}
};
module.exports = book;