var book = {
	"name": "Psalter",
	"numChapters": 150,
	"chapters": {
		"1": {
			"1": "<span>1</span> \tWohl dem, der nicht wandelt im Rat der Gottlosen noch tritt auf den Weg der Sünder noch sitzt, wo die Spötter sitzen,",
			"2": "<span> 2 </span> \tsondern hat Lust am Gesetz des HERRN und sinnt über seinem Gesetz Tag und Nacht!",
			"3": "<span> 3 </span> \tDer ist wie ein Baum, gepflanzt an den Wasserbächen, der seine Frucht bringt zu seiner Zeit, und seine Blätter verwelken nicht. Und was er macht, das gerät wohl.",
			"4": "<span> 4 </span> \tAber so sind die Gottlosen nicht, sondern wie Spreu, die der Wind verstreut.",
			"5": "<span> 5 </span> \tDarum bestehen die Gottlosen nicht im Gericht noch die Sünder in der Gemeinde der Gerechten.",
			"6": "<span> 6 </span> \tDenn der HERR kennt den Weg der Gerechten, aber der Gottlosen Weg vergeht."
		},
		"2": {
			"1": "<span>1</span> \tWarum toben die Heiden und murren die Völker so vergeblich?",
			"2": "<span> 2 </span> \tDie Könige der Erde lehnen sich auf, und die Herren halten Rat miteinander wider den HERRN und seinen Gesalbten:",
			"3": "<span> 3 </span> \t«Lasset uns zerreißen ihre Bande und von uns werfen ihre Stricke!»",
			"4": "<span> 4 </span> \tAber der im Himmel wohnt, lachet ihrer, und der Herr spottet ihrer.",
			"5": "<span> 5 </span> \tEinst wird er mit ihnen reden in seinem Zorn, und mit seinem Grimm wird er sie schrecken:",
			"6": "<span> 6 </span> \t«Ich aber habe meinen König eingesetzt auf meinem heiligen Berg Zion.»",
			"7": "<span> 7 </span> \tKundtun will ich den Ratschluß des HERRN. Er hat zu mir gesagt: «Du bist mein Sohn, heute habe ich dich gezeugt.",
			"8": "<span> 8 </span> \tBitte mich, so will ich dir Völker zum Erbe geben und der Welt Enden zum Eigentum.",
			"9": "<span> 9 </span> \tDu sollst sie mit einem eisernen Zepter zerschlagen, wie Töpfe sollst du sie zerschmeißen.»",
			"10": "<span> 10 </span> \tSo seid nun verständig, ihr Könige, und laßt euch warnen, ihr Richter auf Erden!",
			"11": "<span> 11 </span> \tDienet dem HERRN mit Furcht und küßt seine Füße mit Zittern,",
			"12": "<span> 12 </span> \tdaß er nicht zürne und ihr umkommt auf dem Wege; denn sein Zorn wird bald entbrennen. Wohl allen, die auf ihn trauen!"
		},
		"3": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, ALS ER VOR SEINEM SOHN ABSALOM FLOH.",
			"2": "<span> 2 </span> \tAch HERR, wie sind meiner Feinde so viel und erheben sich so viele gegen mich!",
			"3": "<span> 3 </span> \tViele sagen von mir: Er hat keine Hilfe bei Gott. SELA.",
			"4": "<span> 4 </span> \tAber du, HERR, bist der Schild für mich, du bist meine Ehre und hebst mein Haupt empor.",
			"5": "<span> 5 </span> \tIch rufe mit meiner Stimme zum HERRN, so erhört er mich von seinem heiligen Berge. SELA.",
			"6": "<span> 6 </span> \tIch liege und schlafe und erwache; denn der HERR hält mich.",
			"7": "<span> 7 </span> \tIch fürchte mich nicht vor vielen Tausenden, die sich ringsum wider mich legen.",
			"8": "<span> 8 </span> \tAuf, HERR, und hilf mir, mein Gott! Denn du schlägst alle meine Feinde auf die Backe und zerschmetterst der Gottlosen Zähne.\r\n[9]\tBei dem HERRN findet man Hilfe. Dein Segen komme über dein Volk! SELA."
		},
		"4": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, BEIM SAITENSPIEL.",
			"2": "<span> 2 </span> \tErhöre mich, wenn ich rufe, Gott meiner Gerechtigkeit, der du mich tröstest in Angst; sei mir gnädig und erhöre mein Gebet!",
			"3": "<span> 3 </span> \tIhr Herren, wie lange soll meine Ehre geschändet werden? Wie habt ihr das Eitle so lieb und die Lüge so gern! SELA.",
			"4": "<span> 4 </span> \tErkennet doch, daß der HERR seine Heiligen wunderbar führt; der HERR hört, wenn ich ihn anrufe.",
			"5": "<span> 5 </span> \tZürnet ihr, so sündiget nicht; redet in eurem Herzen auf eurem Lager und seid stille. SELA.",
			"6": "<span> 6 </span> \tOpfert, was recht ist, und hoffet auf den HERRN.",
			"7": "<span> 7 </span> \tViele sagen: «Wer wird uns Gutes sehen lassen?» HERR, laß leuchten über uns das Licht deines Antlitzes!",
			"8": "<span> 8 </span> \tDu erfreust mein Herz, ob jene auch viel Wein und Korn haben.\r\n[9]\tIch liege und schlafe ganz mit Frieden; denn allein du, HERR, hilfst mir, daß ich sicher wohne."
		},
		"5": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, ZUM FLÖTENSPIEL.",
			"2": "<span> 2 </span> \tHERR, höre meine Worte, merke auf mein Reden!",
			"3": "<span> 3 </span> \tVernimm mein Schreien, mein König und mein Gott; denn ich will zu dir beten.",
			"4": "<span> 4 </span> \tHERR, frühe wollest du meine Stimme hören, frühe will ich mich zu dir wenden und aufmerken.",
			"5": "<span> 5 </span> \tDenn du bist nicht ein Gott, dem gottloses Wesen gefällt; wer böse ist, bleibt nicht vor dir.",
			"6": "<span> 6 </span> \tDie Ruhmredigen bestehen nicht vor deinen Augen; du bist feind allen Übeltätern.",
			"7": "<span> 7 </span> \tDu bringst die Lügner um; dem HERRN sind ein Greuel die Blutgierigen und Falschen.",
			"8": "<span> 8 </span> \tIch aber darf in dein Haus gehen durch deine große Güte und anbeten vor deinem heiligen Tempel in deiner Furcht.",
			"9": "<span> 9 </span> \tHERR, leite mich in deiner Gerechtigkeit um meiner Feinde willen; ebne vor mir deinen Weg!",
			"10": "<span> 10 </span> \tDenn in ihrem Munde ist nichts Verläßliches; ihr Inneres ist Bosheit. Ihr Rachen ist ein offenes Grab; mit ihren Zungen heucheln sie.",
			"11": "<span> 11 </span> \tSprich sie schuldig, Gott, daß sie zu Fall kommen durch ihre Ränke. Stoße sie aus um ihrer vielen Übertretungen willen; denn sie sind widerspenstig gegen dich.",
			"12": "<span> 12 </span> \tLaß sich freuen alle, die auf dich trauen; ewiglich laß sie rühmen, denn du beschirmest sie. Fröhlich laß sein in dir, die deinen Namen lieben!\r\n[13]\tDenn du, HERR, segnest die Gerechten, du deckest sie mit Gnade wie mit einem Schilde."
		},
		"6": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, BEIM SAITENSPIEL AUF ACHT SAITEN.",
			"2": "<span> 2 </span> \tAch HERR, strafe mich nicht in deinem Zorn und züchtige mich nicht in deinem Grimm!",
			"3": "<span> 3 </span> \tHERR, sei mir gnädig, denn ich bin schwach; heile mich, HERR, denn meine Gebeine sind erschrocken",
			"4": "<span> 4 </span> \tund meine Seele ist sehr erschrocken. Ach du, HERR, wie lange!",
			"5": "<span> 5 </span> \tWende dich, HERR, und errette mich, hilf mir um deiner Güte willen!",
			"6": "<span> 6 </span> \tDenn im Tode gedenkt man deiner nicht; wer wird dir bei den Toten danken?",
			"7": "<span> 7 </span> \tIch bin so müde vom Seufzen; ich schwemme mein Bett die ganze Nacht und netze mit meinen Tränen mein Lager.",
			"8": "<span> 8 </span> \tMein Auge ist trübe geworden vor Gram und matt, weil meiner Bedränger so viele sind.",
			"9": "<span> 9 </span> \tWeichet von mir, alle Übeltäter; denn der HERR hört mein Weinen.",
			"10": "<span> 10 </span> \tDer HERR hört mein Flehen; mein Gebet nimmt der HERR an.\r\n[11]\tEs sollen alle meine Feinde zuschanden werden und sehr erschrecken; sie sollen umkehren und zuschanden werden plötzlich."
		},
		"7": {
			"1": "<span>1</span> \tEIN KLAGELIED DAVIDS, DAS ER DEM HERRN SANG WEGEN DER WORTE DES KUSCH, DES BENJAMINITERS.",
			"2": "<span> 2 </span> \tAuf dich, HERR, mein Gott, traue ich! Hilf mir von allen meinen Verfolgern und errette mich,",
			"3": "<span> 3 </span> \tdaß sie nicht wie Löwen mich packen und zerreißen, weil kein Retter da ist.",
			"4": "<span> 4 </span> \tHERR, mein Gott, hab ich solches getan und ist Unrecht an meinen Händen,",
			"5": "<span> 5 </span> \thab ich Böses vergolten denen, die friedlich mit mir lebten, oder geschädigt, die mir ohne Ursache feind waren,",
			"6": "<span> 6 </span> \tso verfolge mich der Feind und ergreife mich und trete mein Leben zu Boden und lege meine Ehre in den Staub. SELA.",
			"7": "<span> 7 </span> \tSteh auf, HERR, in deinem Zorn, erhebe dich wider den Grimm meiner Feinde! Wache auf, mir zu helfen, der du Gericht verordnet hast,",
			"8": "<span> 8 </span> \tso werden die Völker sich um dich sammeln; du aber throne über ihnen in der Höhe!",
			"9": "<span> 9 </span> \tDer HERR ist Richter über die Völker. Schaffe mir Recht, HERR, nach meiner Gerechtigkeit und Unschuld!",
			"10": "<span> 10 </span> \tLaß der Gottlosen Bosheit ein Ende nehmen, aber die Gerechten laß bestehen; denn du, gerechter Gott, prüfest Herzen und Nieren.",
			"11": "<span> 11 </span> \tGott ist der Schild über mir, er, der den frommen Herzen hilft.",
			"12": "<span> 12 </span> \tGott ist ein gerechter Richter und ein Gott, der täglich strafen kann.",
			"13": "<span> 13 </span> \tWahrlich, wieder hat einer sein Schwert gewetzt und seinen Bogen gespannt und zielt.",
			"14": "<span> 14 </span> \tDoch sich selber hat er tödliche Waffen gerüstet und feurige Pfeile bereitet.",
			"15": "<span> 15 </span> \tSiehe, er hat Böses im Sinn, mit Unrecht ist er schwanger und wird Lüge gebären.",
			"16": "<span> 16 </span> \tEr hat eine Grube gegraben und ausgehöhlt – und ist in die Grube gefallen, die er gemacht hat.",
			"17": "<span> 17 </span> \tSein Unrecht wird auf seinen Kopf kommen und sein Frevel auf seinen Scheitel fallen.\r\n[18]\tIch danke dem HERRN um seiner Gerechtigkeit willen und will loben den Namen des HERRN, des Allerhöchsten."
		},
		"8": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, AUF DER GITTITH.",
			"2": "<span> 2 </span> \tHERR, unser Herrscher, wie herrlich ist dein Name in allen Landen, der du zeigst deine Hoheit am Himmel!",
			"3": "<span> 3 </span> \tAus dem Munde der jungen Kinder und Säuglinge hast du eine Macht zugerichtet um deiner Feinde willen, daß du vertilgest den Feind und den Rachgierigen.",
			"4": "<span> 4 </span> \tWenn ich sehe die Himmel, deiner Finger Werk, den Mond und die Sterne, die du bereitet hast:",
			"5": "<span> 5 </span> \twas ist der Mensch, daß du seiner gedenkst, und des Menschen Kind, daß du dich seiner annimmst?",
			"6": "<span> 6 </span> \tDu hast ihn wenig niedriger gemacht als Gott, mit Ehre und Herrlichkeit hast du ihn gekrönt.",
			"7": "<span> 7 </span> \tDu hast ihn zum Herrn gemacht über deiner Hände Werk, alles hast du unter seine Füße getan:",
			"8": "<span> 8 </span> \tSchafe und Rinder allzumal, dazu auch die wilden Tiere,",
			"9": "<span> 9 </span> \tdie Vögel unter dem Himmel und die Fische im Meer und alles, was die Meere durchzieht.\r\n[10]\tHERR, unser Herrscher, wie herrlich ist dein Name in allen Landen!"
		},
		"9": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, NACH DER WEISE «SCHÖNE JUGEND».",
			"2": "<span> 2 </span> \tIch danke dem HERRN von ganzem Herzen und erzähle alle deine Wunder.",
			"3": "<span> 3 </span> \tIch freue mich und bin fröhlich in dir und lobe deinen Namen, du Allerhöchster,",
			"4": "<span> 4 </span> \tdaß meine Feinde zurückweichen mußten; sie sind gestürzt und umgekommen vor dir.",
			"5": "<span> 5 </span> \tDenn du führst mein Recht und meine Sache, du sitzest auf dem Thron, ein rechter Richter.",
			"6": "<span> 6 </span> \tDu schiltst die Heiden und bringst die Gottlosen um; ihren Namen vertilgst du auf immer und ewig.",
			"7": "<span> 7 </span> \tDer Feind ist vernichtet, zertrümmert für immer, die Städte hast du zerstört; jedes Gedenken an sie ist vergangen.",
			"8": "<span> 8 </span> \tDer HERR aber bleibt ewiglich; er hat seinen Thron bereitet zum Gericht,",
			"9": "<span> 9 </span> \ter wird den Erdkreis richten mit Gerechtigkeit und die Völker regieren, wie es recht ist.",
			"10": "<span> 10 </span> \tDer HERR ist des Armen Schutz, ein Schutz in Zeiten der Not.",
			"11": "<span> 11 </span> \tDarum hoffen auf dich, die deinen Namen kennen; denn du verlässest nicht, die dich, HERR, suchen.",
			"12": "<span> 12 </span> \tLobet den HERRN, der zu Zion wohnt; verkündigt unter den Völkern sein Tun!",
			"13": "<span> 13 </span> \tDenn der nach Blutschuld fragt, gedenkt der Elenden und vergißt nicht ihr Schreien.",
			"14": "<span> 14 </span> \tHERR, sei mir gnädig; sieh an mein Elend unter meinen Feinden, der du mich erhebst aus den Toren des Todes,",
			"15": "<span> 15 </span> \tdaß ich erzähle all deinen Ruhm, in den Toren der Tochter Zion fröhlich sei über deine Hilfe.",
			"16": "<span> 16 </span> \tDie Heiden sind versunken in der Grube, die sie gegraben, ihr Fuß ist gefangen im Netz, das sie gestellt hatten.",
			"17": "<span> 17 </span> \tDer HERR hat sich kundgetan und Gericht gehalten. Der Gottlose ist verstrickt in dem Werk seiner Hände. ZWISCHENSPIEL. SELA.",
			"18": "<span> 18 </span> \tDie Gottlosen sollen zu den Toten fahren, alle Heiden, die Gott vergessen!",
			"19": "<span> 19 </span> \tDenn er wird den Armen nicht für immer vergessen; die Hoffnung der Elenden wird nicht ewig verloren sein.",
			"20": "<span> 20 </span> \tHERR, steh auf, daß nicht Menschen die Oberhand gewinnen; laß alle Heiden vor dir gerichtet werden!\r\n[21]\tLege, HERR, einen Schrecken auf sie, daß die Heiden erkennen, daß sie Menschen sind. SELA."
		},
		"10": {
			"1": "<span>1</span> \tHERR, warum stehst du so ferne, verbirgst dich zur Zeit der Not?",
			"2": "<span> 2 </span> \tWeil der Gottlose Übermut treibt, müssen die Elenden leiden; sie werden gefangen in den Ränken, die er ersann.",
			"3": "<span> 3 </span> \tDenn der Gottlose rühmt sich seines Mutwillens, und der Habgierige sagt dem HERRN ab und lästert ihn.",
			"4": "<span> 4 </span> \tDer Gottlose meint in seinem Stolz, Gott frage nicht danach. «Es ist kein Gott» sind alle seine Gedanken.",
			"5": "<span> 5 </span> \tEr fährt fort in seinem Tun immerdar. Deine Gerichte sind ferne von ihm, er handelt gewaltsam an allen seinen Feinden.",
			"6": "<span> 6 </span> \tEr spricht in seinem Herzen: «Ich werde nimmermehr wanken, es wird für und für keine Not haben.»",
			"7": "<span> 7 </span> \tSein Mund ist voll Fluchens, voll Lug und Trug; seine Zunge richtet Mühsal und Unheil an.",
			"8": "<span> 8 </span> \tEr sitzt und lauert in den Höfen, er mordet die Unschuldigen heimlich, seine Augen spähen nach den Armen.",
			"9": "<span> 9 </span> \tEr lauert im Verborgenen wie ein Löwe im Dickicht, er lauert, daß er den Elenden fange; er fängt ihn und zieht ihn in sein Netz.",
			"10": "<span> 10 </span> \tEr duckt sich, kauert nieder, und durch seine Gewalt fallen die Unglücklichen.",
			"11": "<span> 11 </span> \tEr spricht in seinem Herzen: «Gott hat's vergessen, er hat sein Antlitz verborgen, er wird's nimmermehr sehen.»",
			"12": "<span> 12 </span> \tSteh auf, HERR! Gott, erhebe deine Hand! Vergiß die Elenden nicht!",
			"13": "<span> 13 </span> \tWarum soll der Gottlose Gott lästern und in seinem Herzen sprechen: «Du fragst doch nicht danach»?",
			"14": "<span> 14 </span> \tDu siehst es doch, denn du schaust das Elend und den Jammer; es steht in deinen Händen. Die Armen befehlen es dir; du bist der Waisen Helfer.",
			"15": "<span> 15 </span> \tZerbrich den Arm des Gottlosen und Bösen und suche seine Bosheit heim, daß man nichts mehr davon finde.",
			"16": "<span> 16 </span> \tDer HERR ist König immer und ewig; die Heiden sollen aus seinem Lande verschwinden.",
			"17": "<span> 17 </span> \tDas Verlangen der Elenden hörst du, HERR; du machst ihr Herz gewiß, dein Ohr merkt darauf,",
			"18": "<span> 18 </span> \tdaß du Recht schaffest den Waisen und Armen, daß der Mensch nicht mehr trotze auf Erden."
		},
		"11": {
			"1": "<span>1</span> \tVON DAVID, VORZUSINGEN.\r\nIch traue auf den HERRN. Wie sagt ihr denn zu mir: «Flieh wie ein Vogel auf die Berge?",
			"2": "<span> 2 </span> \tDenn siehe, die Gottlosen spannen den Bogen und legen ihre Pfeile auf die Sehnen, damit heimlich zu schießen auf die Frommen.",
			"3": "<span> 3 </span> \tJa, sie reißen die Grundfesten um; was kann da der Gerechte ausrichten?»",
			"4": "<span> 4 </span> \tDer HERR ist in seinem heiligen Tempel, des HERRN Thron ist im Himmel. Seine Augen sehen herab, seine Blicke prüfen die Menschenkinder.",
			"5": "<span> 5 </span> \tDer HERR prüft den Gerechten und den Gottlosen; wer Unrecht liebt, den haßt seine Seele.",
			"6": "<span> 6 </span> \tEr wird regnen lassen über die Gottlosen Feuer und Schwefel und Glutwind ihnen zum Lohne geben.",
			"7": "<span> 7 </span> \tDenn der HERR ist gerecht und hat Gerechtigkeit lieb. Die Frommen werden schauen sein Angesicht."
		},
		"12": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, AUF ACHT SAITEN.",
			"2": "<span> 2 </span> \tHilf, HERR! Die Heiligen haben abgenommen, und gläubig sind wenige unter den Menschenkindern.",
			"3": "<span> 3 </span> \tEiner redet mit dem andern Lug und Trug, sie heucheln und reden aus zwiespältigem Herzen.",
			"4": "<span> 4 </span> \tDer HERR wolle ausrotten alle Heuchelei und die Zunge, die hoffärtig redet,",
			"5": "<span> 5 </span> \tdie da sagen: «Durch unsere Zunge sind wir mächtig, uns gebührt zu reden! Wer ist unser Herr?»",
			"6": "<span> 6 </span> \t«Weil die Elenden Gewalt leiden und die Armen seufzen, will ich jetzt aufstehen», spricht der HERR, «ich will Hilfe schaffen dem, der sich danach sehnt.»",
			"7": "<span> 7 </span> \tDie Worte des HERRN sind lauter wie Silber, im Tiegel geschmolzen, geläutert siebenmal.",
			"8": "<span> 8 </span> \tDu, HERR, wollest sie bewahren und uns behüten vor diesem Geschlecht ewiglich!\r\n[9]\tDenn Gottlose gehen allenthalben einher, weil Gemeinheit herrscht unter den Menschenkindern."
		},
		"13": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tHERR, wie lange willst du mich so ganz vergessen? Wie lange verbirgst du dein Antlitz vor mir?",
			"3": "<span> 3 </span> \tWie lange soll ich sorgen in meiner Seele und mich ängsten in meinem Herzen täglich? Wie lange soll sich mein Feind über mich erheben?",
			"4": "<span> 4 </span> \tSchaue doch und erhöre mich, HERR, mein Gott! Erleuchte meine Augen, daß ich nicht im Tode entschlafe,",
			"5": "<span> 5 </span> \tdaß nicht mein Feind sich rühme, er sei meiner mächtig geworden, und meine Widersacher sich freuen, daß ich wanke.",
			"6": "<span> 6 </span> \tIch aber traue darauf, daß du so gnädig bist; mein Herz freut sich, daß du so gerne hilfst. Ich will dem HERRN singen, daß er so wohl an mir tut."
		},
		"14": {
			"1": "<span>1</span> \tVON DAVID, VORZUSINGEN. Die Toren sprechen in ihrem Herzen: «Es ist kein Gott.» Sie taugen nichts; ihr Treiben ist ein Greuel; da ist keiner, der Gutes tut.",
			"2": "<span> 2 </span> \tDer HERR schaut vom Himmel auf die Menschenkinder, daß er sehe, ob jemand klug sei und nach Gott frage.",
			"3": "<span> 3 </span> \tAber sie sind alle abgewichen und allesamt verdorben; da ist keiner, der Gutes tut, auch nicht einer.",
			"4": "<span> 4 </span> \tWill denn das keiner der Übeltäter begreifen, die mein Volk fressen, daß sie sich nähren, aber den HERRN rufen sie nicht an?",
			"5": "<span> 5 </span> \tDa erschrecken sie sehr; denn Gott ist bei dem Geschlecht der Gerechten.",
			"6": "<span> 6 </span> \tEuer Anschlag wider den Armen wird zuschanden werden; denn der HERR ist seine Zuversicht.",
			"7": "<span> 7 </span> \tAch daß die Hilfe aus Zion über Israel käme und der HERR sein gefangenes Volk erlöste! So würde Jakob fröhlich sein und Israel sich freuen."
		},
		"15": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. HERR, wer darf weilen in deinem Zelt? Wer darf wohnen auf deinem heiligen Berge?",
			"2": "<span> 2 </span> \tWer untadelig lebt und tut, was recht ist, und die Wahrheit redet von Herzen,",
			"3": "<span> 3 </span> \twer mit seiner Zunge nicht verleumdet, wer seinem Nächsten nichts Arges tut und seinen Nachbarn nicht schmäht;",
			"4": "<span> 4 </span> \twer die Verworfenen für nichts achtet, aber ehrt die Gottesfürchtigen; wer seinen Eid hält, auch wenn es ihm schadet;",
			"5": "<span> 5 </span> \twer sein Geld nicht auf Zinsen gibt und nimmt nicht Geschenke wider den Unschuldigen."
		},
		"16": {
			"1": "<span>1</span> \tEIN GÜLDENES KLEINOD DAVIDS. Bewahre mich, Gott; denn ich traue auf dich.",
			"2": "<span> 2 </span> \tIch habe gesagt zu dem HERRN: Du bist ja der Herr! Ich weiß von keinem Gut außer dir.",
			"3": "<span> 3 </span> \tAn den Heiligen, die auf Erden sind, an den Herrlichen hab ich all mein Gefallen.",
			"4": "<span> 4 </span> \tAber jene, die einem andern nachlaufen, werden viel Herzeleid haben. Ich will das Blut ihrer Trankopfer nicht opfern noch ihren Namen in meinem Munde führen.",
			"5": "<span> 5 </span> \tDer HERR ist mein Gut und mein Teil; du erhältst mir mein Erbteil.",
			"6": "<span> 6 </span> \tDas Los ist mir gefallen auf liebliches Land; mir ist ein schönes Erbteil geworden.",
			"7": "<span> 7 </span> \tIch lobe den HERRN, der mich beraten hat; auch mahnt mich mein Herz des Nachts.",
			"8": "<span> 8 </span> \tIch habe den HERRN allezeit vor Augen; steht er mir zur Rechten, so werde ich fest bleiben.",
			"9": "<span> 9 </span> \tDarum freut sich mein Herz, und meine Seele ist fröhlich; auch mein Leib wird sicher liegen.",
			"10": "<span> 10 </span> \tDenn du wirst mich nicht dem Tode überlassen und nicht zugeben, daß dein Heiliger die Grube sehe.",
			"11": "<span> 11 </span> \tDu tust mir kund den Weg zum Leben: Vor dir ist Freude die Fülle und Wonne zu deiner Rechten ewiglich."
		},
		"17": {
			"1": "<span>1</span> \tEIN GEBET DAVIDS. HERR, höre die gerechte Sache, merk auf mein Schreien, vernimm mein Gebet von Lippen, die nicht trügen.",
			"2": "<span> 2 </span> \tSprich du in meiner Sache; deine Augen sehen, was recht ist.",
			"3": "<span> 3 </span> \tDu prüfst mein Herz und suchst es heim bei Nacht; du läuterst mich und findest nichts. Ich habe mir vorgenommen, daß mein Mund sich nicht vergehe.",
			"4": "<span> 4 </span> \tIm Treiben der Menschen bewahre ich mich vor gewaltsamen Wegen durch das Wort deiner Lippen.",
			"5": "<span> 5 </span> \tErhalte meinen Gang auf deinen Wegen, daß meine Tritte nicht gleiten.",
			"6": "<span> 6 </span> \tIch rufe zu dir, denn du, Gott, wirst mich erhören; neige deine Ohren zu mir, höre meine Rede!",
			"7": "<span> 7 </span> \tBeweise deine wunderbare Güte, du Heiland derer, die dir vertrauen gegenüber denen, die sich gegen deine rechte Hand erheben.",
			"8": "<span> 8 </span> \tBehüte mich wie einen Augapfel im Auge, beschirme mich unter dem Schatten deiner Flügel",
			"9": "<span> 9 </span> \tvor den Gottlosen, die mir Gewalt antun, vor meinen Feinden, die mir von allen Seiten nach dem Leben trachten.",
			"10": "<span> 10 </span> \tIhr Herz haben sie verschlossen, mit ihrem Munde reden sie stolz.",
			"11": "<span> 11 </span> \tWo wir auch gehen, da umgeben sie uns; ihre Augen richten sie darauf, daß sie uns zu Boden stürzen,",
			"12": "<span> 12 </span> \tgleichwie ein Löwe, der nach Raub lechzt, wie ein junger Löwe, der im Versteck sitzt.",
			"13": "<span> 13 </span> \tHERR, mache dich auf, tritt ihm entgegen und demütige ihn! Errette mich vor dem Gottlosen mit deinem Schwert,",
			"14": "<span> 14 </span> \tvor den Leuten, HERR, mit deiner Hand, vor den Leuten dieser Welt, die ihr Teil haben schon im Leben, denen du den Bauch füllst mit deinen Gütern, deren Söhne auch noch satt werden und ihren Kindern ein Übriges hinterlassen.",
			"15": "<span> 15 </span> \tIch aber will schauen dein Antlitz in Gerechtigkeit, ich will satt werden, wenn ich erwache, an deinem Bilde."
		},
		"18": {
			"1": "<span>1</span> \tVON DAVID, DEM KNECHT DES HERRN, DER ZUM HERRN DIE WORTE DIESES LIEDES REDETE, ALS IHN DER HERR ERRETTET HATTE VON DER HAND ALLER SEINER FEINDE UND VON DER HAND SAULS; VORZUSINGEN.",
			"2": "<span> 2 </span> \tUND ER SPRACH: Herzlich lieb habe ich dich, HERR, meine Stärke!",
			"3": "<span> 3 </span> \tHERR, mein Fels, meine Burg, mein Erretter; mein Gott, mein Hort, auf den ich traue, mein Schild und Berg meines Heiles und mein Schutz!",
			"4": "<span> 4 </span> \tIch rufe an den HERRN, den Hochgelobten, so werde ich vor meinen Feinden errettet.",
			"5": "<span> 5 </span> \tEs umfingen mich des Todes Bande, und die Fluten des Verderbens erschreckten mich.",
			"6": "<span> 6 </span> \tDes Totenreichs Bande umfingen mich, und des Todes Stricke überwältigten mich.",
			"7": "<span> 7 </span> \tAls mir angst war, rief ich den HERRN an und schrie zu meinem Gott. Da erhörte er meine Stimme von seinem Tempel, und mein Schreien kam vor ihn zu seinen Ohren.",
			"8": "<span> 8 </span> \tDie Erde bebte und wankte, und die Grundfesten der Berge bewegten sich und bebten, da er zornig war.",
			"9": "<span> 9 </span> \tRauch stieg auf von seiner Nase und verzehrend Feuer aus seinem Munde; Flammen sprühten von ihm aus.",
			"10": "<span> 10 </span> \tEr neigte den Himmel und fuhr herab, und Dunkel war unter seinen Füßen.",
			"11": "<span> 11 </span> \tUnd er fuhr auf dem Cherub und flog daher, er schwebte auf den Fittichen des Windes.",
			"12": "<span> 12 </span> \tEr machte Finsternis ringsum zu seinem Zelt; in schwarzen, dicken Wolken war er verborgen.",
			"13": "<span> 13 </span> \tAus dem Glanz vor ihm zogen seine Wolken dahin mit Hagel und Blitzen.",
			"14": "<span> 14 </span> \tDer HERR donnerte im Himmel, und der Höchste ließ seine Stimme erschallen mit Hagel und Blitzen.",
			"15": "<span> 15 </span> \tEr schoß seine Pfeile und streute sie aus, sandte Blitze in Menge und jagte sie dahin.",
			"16": "<span> 16 </span> \tDa sah man die Tiefen der Wasser, und des Erdbodens Grund ward aufgedeckt vor deinem Schelten, HERR, vor dem Odem und Schnauben deines Zornes.",
			"17": "<span> 17 </span> \tEr streckte seine Hand aus von der Höhe und faßte mich und zog mich aus großen Wassern.",
			"18": "<span> 18 </span> \tEr errettete mich von meinen starken Feinden, von meinen Hassern, die mir zu mächtig waren;",
			"19": "<span> 19 </span> \tsie überwältigten mich zur Zeit meines Unglücks; aber der HERR ward meine Zuversicht.",
			"20": "<span> 20 </span> \tEr führte mich hinaus ins Weite, er riß mich heraus; denn er hatte Lust zu mir.",
			"21": "<span> 21 </span> \tDer HERR tut wohl an mir nach meiner Gerechtigkeit, er vergilt mir nach der Reinheit meiner Hände.",
			"22": "<span> 22 </span> \tDenn ich halte die Wege des HERRN und bin nicht gottlos wider meinen Gott.",
			"23": "<span> 23 </span> \tDenn alle seine Rechte hab ich vor Augen, und seine Gebote werfe ich nicht von mir,",
			"24": "<span> 24 </span> \tsondern ich bin ohne Tadel vor ihm und hüte mich vor Schuld.",
			"25": "<span> 25 </span> \tDarum vergilt mir der HERR nach meiner Gerechtigkeit, nach der Reinheit meiner Hände vor seinen Augen.",
			"26": "<span> 26 </span> \tGegen die Heiligen bist du heilig, und gegen die Treuen bist du treu,",
			"27": "<span> 27 </span> \tgegen die Reinen bist du rein, und gegen die Verkehrten bist du verkehrt.",
			"28": "<span> 28 </span> \tDenn du hilfst dem elenden Volk, aber stolze Augen erniedrigst du.",
			"29": "<span> 29 </span> \tJa, du machst hell meine Leuchte, der HERR, mein Gott, macht meine Finsternis licht.",
			"30": "<span> 30 </span> \tDenn mit dir kann ich Kriegsvolk zerschlagen und mit meinem Gott über Mauern springen.",
			"31": "<span> 31 </span> \tGottes Wege sind vollkommen, die Worte des HERRN sind durchläutert. Er ist ein Schild allen, die ihm vertrauen.",
			"32": "<span> 32 </span> \tDenn wer ist Gott, wenn nicht der HERR, oder ein Fels, wenn nicht unser Gott?",
			"33": "<span> 33 </span> \tGott rüstet mich mit Kraft und macht meine Wege ohne Tadel.",
			"34": "<span> 34 </span> \tEr macht meine Füße gleich den Hirschen und stellt mich auf meine Höhen.",
			"35": "<span> 35 </span> \tEr lehrt meine Hände streiten und meinen Arm den ehernen Bogen spannen.",
			"36": "<span> 36 </span> \tDu gibst mir den Schild deines Heils, und deine Rechte stärkt mich, und deine Huld macht mich groß.",
			"37": "<span> 37 </span> \tDu gibst meinen Schritten weiten Raum, daß meine Knöchel nicht wanken.",
			"38": "<span> 38 </span> \tIch will meinen Feinden nachjagen und sie ergreifen und nicht umkehren, bis ich sie umgebracht habe.",
			"39": "<span> 39 </span> \tIch will sie zerschmettern, daß sie nicht mehr aufstehen können; sie müssen unter meine Füße fallen.",
			"40": "<span> 40 </span> \tDu rüstest mich mit Stärke zum Streit; du wirfst unter mich, die sich gegen mich erheben.",
			"41": "<span> 41 </span> \tDu treibst meine Feinde in die Flucht, daß ich vernichte, die mich hassen.",
			"42": "<span> 42 </span> \tSie rufen – aber da ist kein Helfer – zum HERRN, aber er antwortet ihnen nicht.",
			"43": "<span> 43 </span> \tIch will sie zerstoßen zu Staub vor dem Winde, ich werfe sie weg wie Unrat auf die Gassen.",
			"44": "<span> 44 </span> \tDu hilfst mir aus dem Streit des Volkes und machst mich zum Haupt über Heiden; ein Volk, das ich nicht kannte, dient mir.",
			"45": "<span> 45 </span> \tEs gehorcht mir mit gehorsamen Ohren; Söhne der Fremde müssen mir huldigen.",
			"46": "<span> 46 </span> \tDie Söhne der Fremde verschmachten und kommen mit Zittern aus ihren Burgen.",
			"47": "<span> 47 </span> \tDer HERR lebt! Gelobt sei mein Fels! Der Gott meines Heils sei hoch erhoben,",
			"48": "<span> 48 </span> \tder Gott, der mir Vergeltung schafft und zwingt die Völker unter mich,",
			"49": "<span> 49 </span> \tder mich errettet von meinen Feinden. Du erhöhst mich über die, die sich gegen mich erheben; du hilfst mir von den Frevlern.",
			"50": "<span> 50 </span> \tDarum will ich dir danken, HERR, unter den Heiden und deinem Namen lobsingen,\r\n[51]\tder seinem Könige großes Heil gibt und Gnade erweist seinem Gesalbten, David, und seinem Hause ewiglich."
		},
		"19": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tDie Himmel erzählen die Ehre Gottes, und die Feste verkündigt seiner Hände Werk.",
			"3": "<span> 3 </span> \tEin Tag sagt's dem andern, und eine Nacht tut's kund der andern,",
			"4": "<span> 4 </span> \tohne Sprache und ohne Worte; unhörbar ist ihre Stimme.",
			"5": "<span> 5 </span> \tIhr Schall geht aus in alle Lande und ihr Reden bis an die Enden der Welt.\r\nEr hat der Sonne ein Zelt am Himmel gemacht;",
			"6": "<span> 6 </span> \tsie geht heraus wie ein Bräutigam aus seiner Kammer und freut sich wie ein Held, zu laufen ihre Bahn.",
			"7": "<span> 7 </span> \tSie geht auf an einem Ende des Himmels und läuft um bis wieder an sein Ende, und nichts bleibt vor ihrer Glut verborgen.",
			"8": "<span> 8 </span> \tDas Gesetz des HERRN ist vollkommen und erquickt die Seele. Das Zeugnis des HERRN ist gewiß und macht die Unverständigen weise.",
			"9": "<span> 9 </span> \tDie Befehle des HERRN sind richtig und erfreuen das Herz. Die Gebote des HERRN sind lauter und erleuchten die Augen.",
			"10": "<span> 10 </span> \tDie Furcht des HERRN ist rein und bleibt ewiglich. Die Rechte des HERRN sind Wahrheit, allesamt gerecht.",
			"11": "<span> 11 </span> \tSie sind köstlicher als Gold und viel feines Gold, sie sind süßer als Honig und Honigseim.",
			"12": "<span> 12 </span> \tAuch läßt dein Knecht sich durch sie warnen; und wer sie hält, der hat großen Lohn.",
			"13": "<span> 13 </span> \tWer kann merken, wie oft er fehlet? Verzeihe mir die verborgenen Sünden!",
			"14": "<span> 14 </span> \tBewahre auch deinen Knecht vor den Stolzen, daß sie nicht über mich herrschen; so werde ich ohne Tadel sein und rein bleiben von großer Missetat.\r\n[15]\tLaß dir wohlgefallen die Rede meines Mundes und das Gespräch meines Herzens vor dir, HERR, mein Fels und mein Erlöser."
		},
		"20": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tDer HERR erhöre dich in der Not, der Name des Gottes Jakobs schütze dich!",
			"3": "<span> 3 </span> \tEr sende dir Hilfe vom Heiligtum und stärke dich aus Zion!",
			"4": "<span> 4 </span> \tEr gedenke all deiner Speisopfer, und dein Brandopfer sei ihm angenehm! SELA.",
			"5": "<span> 5 </span> \tEr gebe dir, was dein Herz begehrt, und erfülle alles, was du vorhast!",
			"6": "<span> 6 </span> \tDann wollen wir jubeln, weil er dir hilft; im Namen unsres Gottes erheben wir das Banner. Der HERR gewähre dir alle deine Bitten!",
			"7": "<span> 7 </span> \tNun weiß ich, daß der HERR seinem Gesalbten hilft und ihn erhört von seinem heiligen Himmel, seine rechte Hand hilft mit Macht.",
			"8": "<span> 8 </span> \tJene verlassen sich auf Wagen und Rosse; wir aber denken an den Namen des HERRN, unsres Gottes.",
			"9": "<span> 9 </span> \tSie sind niedergestürzt und gefallen, wir aber stehen und halten stand.\r\n[10]\tHilf, HERR, du König! Er wird uns erhören, wenn wir rufen."
		},
		"21": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tHERR, der König freut sich in deiner Kraft, und wie sehr fröhlich ist er über deine Hilfe!",
			"3": "<span> 3 </span> \tDu erfüllst ihm seines Herzens Wunsch und verweigerst nicht, was sein Mund bittet. SELA.",
			"4": "<span> 4 </span> \tDenn du überschüttest ihn mit gutem Segen, du setzest eine goldene Krone auf sein Haupt.",
			"5": "<span> 5 </span> \tEr bittet dich um Leben; du gibst es ihm, langes Leben für immer und ewig.",
			"6": "<span> 6 </span> \tEr hat große Herrlichkeit durch deine Hilfe; Pracht und Hoheit legst du auf ihn.",
			"7": "<span> 7 </span> \tDenn du setzest ihn zum Segen ewiglich, du erfreust ihn mit Freude vor deinem Antlitz.",
			"8": "<span> 8 </span> \tDenn der König hofft auf den HERRN und wird durch die Güte des Höchsten fest bleiben.",
			"9": "<span> 9 </span> \tDeine Hand wird finden alle deine Feinde, deine Rechte wird finden, die dich hassen.",
			"10": "<span> 10 </span> \tDu wirst es mit ihnen machen wie im Feuerofen, wenn du erscheinen wirst. Der HERR wird sie verschlingen in seinem Zorn; Feuer wird sie fressen.",
			"11": "<span> 11 </span> \tIhre Nachkommen wirst du tilgen vom Erdboden und ihre Kinder aus der Zahl der Menschen.",
			"12": "<span> 12 </span> \tDenn sie gedachten, dir Übles zu tun, und machten Anschläge, die sie nicht ausführen konnten.",
			"13": "<span> 13 </span> \tDenn du wirst machen, daß sie den Rücken kehren; mit deinem Bogen wirst du auf ihr Antlitz zielen.\r\n[14]\tHERR, erhebe dich in deiner Kraft, so wollen wir singen und loben deine Macht."
		},
		"22": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, NACH DER WEISE «DIE HIRSCHKUH, DIE FRÜH GEJAGT WIRD».",
			"2": "<span> 2 </span> \tMein Gott, mein Gott, warum hast du mich verlassen? Ich schreie, aber meine Hilfe ist ferne.",
			"3": "<span> 3 </span> \tMein Gott, des Tages rufe ich, doch antwortest du nicht, und des Nachts, doch finde ich keine Ruhe.",
			"4": "<span> 4 </span> \tDu aber bist heilig, der du thronst über den Lobgesängen Israels.",
			"5": "<span> 5 </span> \tUnsere Väter hofften auf dich; und da sie hofften, halfst du ihnen heraus.",
			"6": "<span> 6 </span> \tZu dir schrien sie und wurden errettet, sie hofften auf dich und wurden nicht zuschanden.",
			"7": "<span> 7 </span> \tIch aber bin ein Wurm und kein Mensch, ein Spott der Leute und verachtet vom Volke.",
			"8": "<span> 8 </span> \tAlle, die mich sehen, verspotten mich, sperren das Maul auf und schütteln den Kopf:",
			"9": "<span> 9 </span> \t«Er klage es dem HERRN, der helfe ihm heraus und rette ihn, hat er Gefallen an ihm.»",
			"10": "<span> 10 </span> \tDu hast mich aus meiner Mutter Leibe gezogen; du ließest mich geborgen sein an der Brust meiner Mutter.",
			"11": "<span> 11 </span> \tAuf dich bin ich geworfen von Mutterleib an, du bist mein Gott von meiner Mutter Schoß an.",
			"12": "<span> 12 </span> \tSei nicht ferne von mir, denn Angst ist nahe; denn es ist hier kein Helfer.",
			"13": "<span> 13 </span> \tGewaltige Stiere haben mich umgeben, mächtige Büffel haben mich umringt.",
			"14": "<span> 14 </span> \tIhren Rachen sperren sie gegen mich auf wie ein brüllender und reißender Löwe.",
			"15": "<span> 15 </span> \tIch bin ausgeschüttet wie Wasser, alle meine Knochen haben sich voneinander gelöst; mein Herz ist in meinem Leibe wie zerschmolzenes Wachs.",
			"16": "<span> 16 </span> \tMeine Kräfte sind vertrocknet wie eine Scherbe, und meine Zunge klebt mir am Gaumen, und du legst mich in des Todes Staub.",
			"17": "<span> 17 </span> \tDenn Hunde haben mich umgeben, und der Bösen Rotte hat mich umringt; sie haben meine Hände und Füße durchgraben.",
			"18": "<span> 18 </span> \tIch kann alle meine Knochen zählen; sie aber schauen zu und sehen auf mich herab.",
			"19": "<span> 19 </span> \tSie teilen meine Kleider unter sich und werfen das Los um mein Gewand.",
			"20": "<span> 20 </span> \tAber du, HERR, sei nicht ferne; meine Stärke, eile, mir zu helfen!",
			"21": "<span> 21 </span> \tErrette meine Seele vom Schwert, mein Leben von den Hunden!",
			"22": "<span> 22 </span> \tHilf mir aus dem Rachen des Löwen und vor den Hörnern wilder Stiere – du hast mich erhört!\r\nLuther übersetzte: «und errette mich von den Einhörnern».",
			"23": "<span> 23 </span> \tIch will deinen Namen kundtun meinen Brüdern, ich will dich in der Gemeinde rühmen:",
			"24": "<span> 24 </span> \tRühmet den HERRN, die ihr ihn fürchtet; ehret ihn, ihr alle vom Hause Jakob, und vor ihm scheuet euch, ihr alle vom Hause Israel!",
			"25": "<span> 25 </span> \tDenn er hat nicht verachtet noch verschmäht das Elend des Armen und sein Antlitz vor ihm nicht verborgen; und als er zu ihm schrie, hörte er's.",
			"26": "<span> 26 </span> \tDich will ich preisen in der großen Gemeinde, ich will meine Gelübde erfüllen vor denen, die ihn fürchten.",
			"27": "<span> 27 </span> \tDie Elenden sollen essen, daß sie satt werden; und die nach dem HERRN fragen, werden ihn preisen; euer Herz soll ewiglich leben.",
			"28": "<span> 28 </span> \tEs werden gedenken und sich zum HERRN bekehren aller Welt Enden und vor ihm anbeten alle Geschlechter der Heiden.",
			"29": "<span> 29 </span> \tDenn des HERRN ist das Reich, und es herrscht unter den Heiden.",
			"30": "<span> 30 </span> \tIhn allein werden anbeten alle, die in der Erde schlafen; vor ihm werden die Knie beugen alle, die zum Staube hinabfuhren und ihr Leben nicht konnten erhalten.",
			"31": "<span> 31 </span> \tEr wird Nachkommen haben, die ihm dienen; vom Herrn wird man verkündigen Kind und Kindeskind.\r\n[32]\tSie werden kommen und seine Gerechtigkeit predigen dem Volk, das geboren wird. Denn er hat's getan."
		},
		"23": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. Der HERR ist mein Hirte, mir wird nichts mangeln.",
			"2": "<span> 2 </span> \tEr weidet mich auf einer grünen Aue und führet mich zum frischen Wasser.",
			"3": "<span> 3 </span> \tEr erquicket meine Seele. Er führet mich auf rechter Straße um seines Namens willen.",
			"4": "<span> 4 </span> \tUnd ob ich schon wanderte im finstern Tal, fürchte ich kein Unglück; denn du bist bei mir, dein Stecken und Stab trösten mich.",
			"5": "<span> 5 </span> \tDu bereitest vor mir einen Tisch im Angesicht meiner Feinde. Du salbest mein Haupt mit Öl und schenkest mir voll ein.",
			"6": "<span> 6 </span> \tGutes und Barmherzigkeit werden mir folgen mein Leben lang, und ich werde bleiben im Hause des HERRN immerdar."
		},
		"24": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. Die Erde ist des HERRN und was darinnen ist, der Erdkreis und die darauf wohnen.",
			"2": "<span> 2 </span> \tDenn er hat ihn über den Meeren gegründet und über den Wassern bereitet.",
			"3": "<span> 3 </span> \tWer darf auf des HERRN Berg gehen, und wer darf stehen an seiner heiligen Stätte?",
			"4": "<span> 4 </span> \tWer unschuldige Hände hat und reines Herzens ist, wer nicht bedacht ist auf Lug und Trug und nicht falsche Eide schwört:",
			"5": "<span> 5 </span> \tder wird den Segen vom HERRN empfangen und Gerechtigkeit von dem Gott seines Heiles.",
			"6": "<span> 6 </span> \tDas ist das Geschlecht, das nach ihm fragt, das da sucht dein Antlitz, Gott Jakobs. SELA.",
			"7": "<span> 7 </span> \tMachet die Tore weit und die Türen in der Welt hoch, daß der König der Ehre einziehe!\r\nGemeint sind die Tore des Tempels – vielleicht: uralte Pforten.",
			"8": "<span> 8 </span> \tWer ist der König der Ehre? Es ist der HERR, stark und mächtig, der HERR, mächtig im Streit.",
			"9": "<span> 9 </span> \tMachet die Tore weit und die Türen in der Welt hoch, daß der König der Ehre einziehe!",
			"10": "<span> 10 </span> \tWer ist der König der Ehre? Es ist der HERR Zebaoth; er ist der König der Ehre. SELA."
		},
		"25": {
			"1": "<span>1</span> \tVON DAVID. Nach dir, HERR, verlanget mich.",
			"2": "<span> 2 </span> \tMein Gott, ich hoffe auf dich; laß mich nicht zuschanden werden, daß meine Feinde nicht frohlocken über mich.",
			"3": "<span> 3 </span> \tDenn keiner wird zuschanden, der auf dich harret; aber zuschanden werden die leichtfertigen Verächter.",
			"4": "<span> 4 </span> \tHERR, zeige mir deine Wege und lehre mich deine Steige!",
			"5": "<span> 5 </span> \tLeite mich in deiner Wahrheit und lehre mich! Denn du bist der Gott, der mir hilft; täglich harre ich auf dich.",
			"6": "<span> 6 </span> \tGedenke, HERR, an deine Barmherzigkeit und an deine Güte, die von Ewigkeit her gewesen sind.",
			"7": "<span> 7 </span> \tGedenke nicht der Sünden meiner Jugend und meiner Übertretungen, gedenke aber meiner nach deiner Barmherzigkeit, HERR, um deiner Güte willen!",
			"8": "<span> 8 </span> \tDer HERR ist gut und gerecht; darum weist er Sündern den Weg.",
			"9": "<span> 9 </span> \tEr leitet die Elenden recht und lehrt die Elenden seinen Weg.",
			"10": "<span> 10 </span> \tDie Wege des HERRN sind lauter Güte und Treue für alle, die seinen Bund und seine Gebote halten.",
			"11": "<span> 11 </span> \tUm deines Namens willen, HERR, vergib mir meine Schuld, die so groß ist!",
			"12": "<span> 12 </span> \tWer ist der Mann, der den HERRN fürchtet? Er wird ihm den Weg weisen, den er wählen soll.",
			"13": "<span> 13 </span> \tEr wird im Guten wohnen, und sein Geschlecht wird das Land besitzen.",
			"14": "<span> 14 </span> \tDer HERR ist denen Freund, die ihn fürchten; und seinen Bund läßt er sie wissen.",
			"15": "<span> 15 </span> \tMeine Augen sehen stets auf den HERRN; denn er wird meinen Fuß aus dem Netze ziehen.",
			"16": "<span> 16 </span> \tWende dich zu mir und sei mir gnädig; denn ich bin einsam und elend.",
			"17": "<span> 17 </span> \tDie Angst meines Herzens ist groß; führe mich aus meinen Nöten!",
			"18": "<span> 18 </span> \tSieh an meinen Jammer und mein Elend und vergib mir alle meine Sünden!",
			"19": "<span> 19 </span> \tSieh, wie meiner Feinde so viel sind und zu Unrecht mich hassen.",
			"20": "<span> 20 </span> \tBewahre meine Seele und errette mich; laß mich nicht zuschanden werden, denn ich traue auf dich!",
			"21": "<span> 21 </span> \tUnschuld und Redlichkeit mögen mich behüten; denn ich harre auf dich.",
			"22": "<span> 22 </span> \tGott, erlöse Israel aus aller seiner Not!"
		},
		"26": {
			"1": "<span>1</span> \tVON DAVID. HERR, schaffe mir Recht, denn ich bin unschuldig! Ich hoffe auf den HERRN, darum werde ich nicht fallen.",
			"2": "<span> 2 </span> \tPrüfe mich, HERR, und erprobe mich, erforsche meine Nieren und mein Herz!",
			"3": "<span> 3 </span> \tDenn deine Güte ist mir vor Augen, und ich wandle in deiner Wahrheit.",
			"4": "<span> 4 </span> \tIch sitze nicht bei heillosen Leuten und habe nicht Gemeinschaft mit den Falschen.",
			"5": "<span> 5 </span> \tIch hasse die Versammlung der Boshaften und sitze nicht bei den Gottlosen.",
			"6": "<span> 6 </span> \tIch wasche meine Hände in Unschuld und halte mich, HERR, zu deinem Altar,",
			"7": "<span> 7 </span> \tdir zu danken mit lauter Stimme und zu verkündigen alle deine Wunder.",
			"8": "<span> 8 </span> \tHERR, ich habe lieb die Stätte deines Hauses und den Ort, da deine Ehre wohnt.",
			"9": "<span> 9 </span> \tRaffe meine Seele nicht hin mit den Sündern noch mein Leben mit den Blutdürstigen,",
			"10": "<span> 10 </span> \tan deren Händen Schandtat klebt und die gern Geschenke nehmen.",
			"11": "<span> 11 </span> \tIch aber gehe meinen Weg in Unschuld. Erlöse mich und sei mir gnädig!",
			"12": "<span> 12 </span> \tMein Fuß steht fest auf rechtem Grund. Ich will den HERRN loben in den Versammlungen."
		},
		"27": {
			"1": "<span>1</span> \tVON DAVID. Der HERR ist mein Licht und mein Heil; vor wem sollte ich mich fürchten? Der HERR ist meines Lebens Kraft; vor wem sollte mir grauen?",
			"2": "<span> 2 </span> \tWenn die Übeltäter an mich wollen, um mich zu verschlingen, meine Widersacher und Feinde, sollen sie selber straucheln und fallen.",
			"3": "<span> 3 </span> \tWenn sich auch ein Heer wider mich lagert, so fürchtet sich dennoch mein Herz nicht; wenn sich Krieg wider mich erhebt, so verlasse ich mich auf ihn.",
			"4": "<span> 4 </span> \tEines bitte ich vom HERRN, das hätte ich gerne: daß ich im Hause des HERRN bleiben könne mein Leben lang, zu schauen die schönen Gottesdienste des HERRN und seinen Tempel zu betrachten.",
			"5": "<span> 5 </span> \tDenn er deckt mich in seiner Hütte zur bösen Zeit, er birgt mich im Schutz seines Zeltes und erhöht mich auf einen Felsen.",
			"6": "<span> 6 </span> \tUnd nun erhebt sich mein Haupt über meine Feinde, die um mich her sind; darum will ich Lob opfern in seinem Zelt, ich will singen und Lob sagen dem HERRN.",
			"7": "<span> 7 </span> \tHERR, höre meine Stimme, wenn ich rufe; sei mir gnädig und erhöre mich!",
			"8": "<span> 8 </span> \tMein Herz hält dir vor dein Wort: «Ihr sollt mein Antlitz suchen.» Darum suche ich auch, HERR, dein Antlitz.",
			"9": "<span> 9 </span> \tVerbirg dein Antlitz nicht vor mir, verstoße nicht im Zorn deinen Knecht! Denn du bist meine Hilfe; verlaß mich nicht und tu die Hand nicht von mir ab, Gott, mein Heil!",
			"10": "<span> 10 </span> \tDenn mein Vater und meine Mutter verlassen mich, aber der HERR nimmt mich auf.",
			"11": "<span> 11 </span> \tHERR, weise mir deinen Weg und leite mich auf ebener Bahn um meiner Feinde willen.",
			"12": "<span> 12 </span> \tGib mich nicht preis dem Willen meiner Feinde! Denn es stehen falsche Zeugen wider mich auf und tun mir Unrecht ohne Scheu.",
			"13": "<span> 13 </span> \tIch glaube aber doch, daß ich sehen werde die Güte des HERRN im Lande der Lebendigen.",
			"14": "<span> 14 </span> \tHarre des HERRN! Sei getrost und unverzagt und harre des HERRN!"
		},
		"28": {
			"1": "<span>1</span> \tVON DAVID. Wenn ich rufe zu dir, HERR, mein Fels, so schweige doch nicht, daß ich nicht, wenn du schweigst, gleich werde denen, die in die Grube fahren.",
			"2": "<span> 2 </span> \tHöre die Stimme meines Flehens, wenn ich zu dir schreie, wenn ich meine Hände aufhebe zu deinem heiligen Tempel.",
			"3": "<span> 3 </span> \tRaffe mich nicht hin mit den Gottlosen und mit den Übeltätern, die freundlich reden mit ihrem Nächsten und haben Böses im Herzen.",
			"4": "<span> 4 </span> \tGib ihnen nach ihrem Tun und nach ihren bösen Taten; gib ihnen nach den Werken ihrer Hände; vergilt ihnen, wie sie es verdienen.",
			"5": "<span> 5 </span> \tDenn sie wollen nicht achten auf das Tun des HERRN noch auf die Werke seiner Hände; darum wird er sie niederreißen und nicht wieder aufbauen.",
			"6": "<span> 6 </span> \tGelobt sei der HERR; denn er hat erhört die Stimme meines Flehens.",
			"7": "<span> 7 </span> \tDer HERR ist meine Stärke und mein Schild; auf ihn hofft mein Herz, und mir ist geholfen. Nun ist mein Herz fröhlich, und ich will ihm danken mit meinem Lied.",
			"8": "<span> 8 </span> \tDer HERR ist seines Volkes Stärke, Hilfe und Stärke für seinen Gesalbten.",
			"9": "<span> 9 </span> \tHilf deinem Volk und segne dein Erbe und weide und trage sie ewiglich!"
		},
		"29": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. Bringet dar dem HERRN, ihr Himmlischen, bringet dar dem HERRN Ehre und Stärke!",
			"2": "<span> 2 </span> \tBringet dar dem HERRN die Ehre seines Namens, betet an den HERRN in heiligem Schmuck!",
			"3": "<span> 3 </span> \tDie Stimme des HERRN erschallt über den Wassern, der Gott der Ehre donnert, der HERR, über großen Wassern.",
			"4": "<span> 4 </span> \tDie Stimme des HERRN ergeht mit Macht, die Stimme des HERRN ergeht herrlich.",
			"5": "<span> 5 </span> \tDie Stimme des HERRN zerbricht die Zedern, der HERR zerbricht die Zedern des Libanon.",
			"6": "<span> 6 </span> \tEr läßt hüpfen wie ein Kalb den Libanon, den Sirjon wie einen jungen Wildstier.",
			"7": "<span> 7 </span> \tDie Stimme des HERRN sprüht Feuerflammen;",
			"8": "<span> 8 </span> \tdie Stimme des HERRN läßt die Wüste erbeben; der HERR läßt erbeben die Wüste Kadesch.",
			"9": "<span> 9 </span> \tDie Stimme des HERRN läßt Eichen wirbeln und reißt Wälder kahl. In seinem Tempel ruft alles: «Ehre!»",
			"10": "<span> 10 </span> \tDer HERR hat seinen Thron über der Flut; der HERR bleibt ein König in Ewigkeit.",
			"11": "<span> 11 </span> \tDer HERR wird seinem Volk Kraft geben; der HERR wird sein Volk segnen mit Frieden."
		},
		"30": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, EIN LIED ZUR EINWEIHUNG DES TEMPELS.",
			"2": "<span> 2 </span> \tIch preise dich, HERR; denn du hast mich aus der Tiefe gezogen und lässest meine Feinde sich nicht über mich freuen.",
			"3": "<span> 3 </span> \tHERR, mein Gott, als ich schrie zu dir, da machtest du mich gesund.",
			"4": "<span> 4 </span> \tHERR, du hast mich von den Toten heraufgeholt; du hast mich am Leben erhalten, aber sie mußten in die Grube fahren.",
			"5": "<span> 5 </span> \tLobsinget dem HERRN, ihr seine Heiligen, und preiset seinen heiligen Namen!",
			"6": "<span> 6 </span> \tDenn sein Zorn währet einen Augenblick und lebenslang seine Gnade. Den Abend lang währet das Weinen, aber des Morgens ist Freude.",
			"7": "<span> 7 </span> \tIch aber sprach, als es mir gut ging: Ich werde nimmermehr wanken.",
			"8": "<span> 8 </span> \tDenn, HERR, durch dein Wohlgefallen hattest du mich auf einen hohen Fels gestellt. Aber als du dein Antlitz verbargest, erschrak ich.",
			"9": "<span> 9 </span> \tZu dir, HERR, rief ich, und zum Herrn flehte ich:",
			"10": "<span> 10 </span> \tWas nützt dir mein Blut, wenn ich zur Grube fahre? Wird dir auch der Staub danken und deine Treue verkündigen?",
			"11": "<span> 11 </span> \tHERR, höre und sei mir gnädig! HERR, sei mein Helfer!",
			"12": "<span> 12 </span> \tDu hast mir meine Klage verwandelt in einen Reigen, du hast mir den Sack der Trauer ausgezogen und mich mit Freude gegürtet,\r\n[13]\tdaß ich dir lobsinge und nicht stille werde. HERR, mein Gott, ich will dir danken in Ewigkeit."
		},
		"31": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tHERR, auf dich traue ich, laß mich nimmermehr zuschanden werden, errette mich durch deine Gerechtigkeit!",
			"3": "<span> 3 </span> \tNeige deine Ohren zu mir, hilf mir eilends! Sei mir ein starker Fels und eine Burg, daß du mir helfest!",
			"4": "<span> 4 </span> \tDenn du bist mein Fels und meine Burg, und um deines Namens willen wollest du mich leiten und führen.",
			"5": "<span> 5 </span> \tDu wollest mich aus dem Netze ziehen, das sie mir heimlich stellten; denn du bist meine Stärke.",
			"6": "<span> 6 </span> \tIn deine Hände befehle ich meinen Geist; du hast mich erlöst, HERR, du treuer Gott.",
			"7": "<span> 7 </span> \tIch hasse, die sich halten an nichtige Götzen; ich aber hoffe auf den HERRN.",
			"8": "<span> 8 </span> \tIch freue mich und bin fröhlich über deine Güte, daß du mein Elend ansiehst und nimmst dich meiner an in Not",
			"9": "<span> 9 </span> \tund übergibst mich nicht in die Hände des Feindes; du stellst meine Füße auf weiten Raum.",
			"10": "<span> 10 </span> \tHERR, sei mir gnädig, denn mir ist angst! Mein Auge ist trübe geworden vor Gram, matt meine Seele und mein Leib.",
			"11": "<span> 11 </span> \tDenn mein Leben ist hingeschwunden in Kummer und meine Jahre in Seufzen. Meine Kraft ist verfallen durch meine Missetat, und meine Gebeine sind verschmachtet.",
			"12": "<span> 12 </span> \tVor all meinen Bedrängern bin ich ein Spott geworden, eine Last meinen Nachbarn und ein Schrecken meinen Bekannten. Die mich sehen auf der Gasse, fliehen vor mir.",
			"13": "<span> 13 </span> \tIch bin vergessen in ihrem Herzen wie ein Toter; ich bin geworden wie ein zerbrochenes Gefäß.",
			"14": "<span> 14 </span> \tDenn ich höre, wie viele über mich lästern: Schrecken ist um und um! Sie halten Rat miteinander über mich und trachten danach, mir das Leben zu nehmen.",
			"15": "<span> 15 </span> \tIch aber, HERR, hoffe auf dich und spreche: Du bist mein Gott!",
			"16": "<span> 16 </span> \tMeine Zeit steht in deinen Händen. Errette mich von der Hand meiner Feinde und von denen, die mich verfolgen.",
			"17": "<span> 17 </span> \tLaß leuchten dein Antlitz über deinem Knecht; hilf mir durch deine Güte!",
			"18": "<span> 18 </span> \tHERR, laß mich nicht zuschanden werden; denn ich rufe dich an. Die Gottlosen sollen zuschanden werden und hinabfahren zu den Toten und schweigen.",
			"19": "<span> 19 </span> \tVerstummen sollen die Lügenmäuler, die da reden wider den Gerechten frech, stolz und höhnisch.",
			"20": "<span> 20 </span> \tWie groß ist deine Güte, HERR, die du bewahrt hast denen, die dich fürchten, und erweisest vor den Leuten denen, die auf dich trauen!",
			"21": "<span> 21 </span> \tDu birgst sie in deinem Schutz vor den Rotten der Leute, du deckst sie in der Hütte vor den zänkischen Zungen.",
			"22": "<span> 22 </span> \tGelobt sei der HERR, denn er hat seine wunderbare Güte mir erwiesen in einer festen Stadt.",
			"23": "<span> 23 </span> \tIch sprach wohl in meinem Zagen: Ich bin von deinen Augen verstoßen. Doch du hörtest die Stimme meines Flehens, als ich zu dir schrie.",
			"24": "<span> 24 </span> \tLiebet den HERRN, alle seine Heiligen! Die Gläubigen behütet der HERR und vergilt reichlich dem, der Hochmut übt.\r\n[25]\tSeid getrost und unverzagt alle, die ihr des HERRN harret!"
		},
		"32": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DAVIDS. Wohl dem, dem die Übertretungen vergeben sind, dem die Sünde bedeckt ist!",
			"2": "<span> 2 </span> \tWohl dem Menschen, dem der HERR die Schuld nicht zurechnet, in dessen Geist kein Trug ist!",
			"3": "<span> 3 </span> \tDenn als ich es wollte verschweigen, verschmachteten meine Gebeine durch mein tägliches Klagen.",
			"4": "<span> 4 </span> \tDenn deine Hand lag Tag und Nacht schwer auf mir, daß mein Saft vertrocknete, wie es im Sommer dürre wird. SELA.",
			"5": "<span> 5 </span> \tDarum bekannte ich dir meine Sünde, und meine Schuld verhehlte ich nicht. Ich sprach: Ich will dem HERRN meine Übertretungen bekennen. Da vergabst du mir die Schuld meiner Sünde. SELA.",
			"6": "<span> 6 </span> \tDeshalb werden alle Heiligen zu dir beten zur Zeit der Angst. Darum, wenn große Wasserfluten kommen, werden sie nicht an sie gelangen.",
			"7": "<span> 7 </span> \tDu bist mein Schirm, du wirst mich vor Angst behüten, daß ich errettet gar fröhlich rühmen kann. SELA.",
			"8": "<span> 8 </span> \t«Ich will dich unterweisen und dir den Weg zeigen, den du gehen sollst; ich will dich mit meinen Augen leiten.»",
			"9": "<span> 9 </span> \tSeid nicht wie Rosse und Maultiere, die ohne Verstand sind, denen man Zaum und Gebiß anlegen muß; sie werden sonst nicht zu dir kommen.",
			"10": "<span> 10 </span> \tDer Gottlose hat viel Plage; wer aber auf den HERRN hofft, den wird die Güte umfangen.",
			"11": "<span> 11 </span> \tFreuet euch des HERRN und seid fröhlich, ihr Gerechten, und jauchzet, alle ihr Frommen."
		},
		"33": {
			"1": "<span>1</span> \tFreuet euch des HERRN, ihr Gerechten; die Frommen sollen ihn recht preisen.",
			"2": "<span> 2 </span> \tDanket dem HERRN mit Harfen; lobsinget ihm zum Psalter von zehn Saiten!",
			"3": "<span> 3 </span> \tSinget ihm ein neues Lied; spielt schön auf den Saiten mit fröhlichem Schall!",
			"4": "<span> 4 </span> \tDenn des HERRN Wort ist wahrhaftig, und was er zusagt, das hält er gewiß.",
			"5": "<span> 5 </span> \tEr liebt Gerechtigkeit und Recht; die Erde ist voll der Güte des HERRN.",
			"6": "<span> 6 </span> \tDer Himmel ist durch das Wort des HERRN gemacht und all sein Heer durch den Hauch seines Mundes.",
			"7": "<span> 7 </span> \tEr hält die Wasser des Meeres zusammen wie in einem Schlauch und sammelt in Kammern die Fluten.",
			"8": "<span> 8 </span> \tAlle Welt fürchte den HERRN, und vor ihm scheue sich alles, was auf dem Erdboden wohnet.",
			"9": "<span> 9 </span> \tDenn wenn er spricht, so geschieht's; wenn er gebietet, so steht's da.",
			"10": "<span> 10 </span> \tDer HERR macht zunichte der Heiden Rat und wehrt den Gedanken der Völker.",
			"11": "<span> 11 </span> \tAber der Ratschluß des HERRN bleibt ewiglich, seines Herzens Gedanken für und für.",
			"12": "<span> 12 </span> \tWohl dem Volk, dessen Gott der HERR ist, dem Volk, das er zum Erbe erwählt hat!",
			"13": "<span> 13 </span> \tDer HERR schaut vom Himmel und sieht alle Menschenkinder.",
			"14": "<span> 14 </span> \tVon seinem festen Thron sieht er auf alle, die auf Erden wohnen.",
			"15": "<span> 15 </span> \tEr lenkt ihnen allen das Herz, er gibt acht auf alle ihre Werke.",
			"16": "<span> 16 </span> \tEinem König hilft nicht seine große Macht; ein Held kann sich nicht retten durch seine große Kraft.",
			"17": "<span> 17 </span> \tRosse helfen auch nicht; da wäre man betrogen; und ihre große Stärke errettet nicht.",
			"18": "<span> 18 </span> \tSiehe, des HERRN Auge achtet auf alle, die ihn fürchten, die auf seine Güte hoffen,",
			"19": "<span> 19 </span> \tdaß er sie errette vom Tode und sie am Leben erhalte in Hungersnot.",
			"20": "<span> 20 </span> \tUnsre Seele harrt auf den HERRN; er ist uns Hilfe und Schild.",
			"21": "<span> 21 </span> \tDenn unser Herz freut sich seiner, und wir trauen auf seinen heiligen Namen.",
			"22": "<span> 22 </span> \tDeine Güte, HERR, sei über uns, wie wir auf dich hoffen."
		},
		"34": {
			"1": "<span>1</span> \tVON DAVID, ALS ER SICH WAHNSINNIG STELLTE VOR ABIMELECH UND DIESER IHN VON SICH TRIEB UND ER WEGGING.",
			"2": "<span> 2 </span> \tIch will den HERRN loben allezeit; sein Lob soll immerdar in meinem Munde sein.",
			"3": "<span> 3 </span> \tMeine Seele soll sich rühmen des HERRN, daß es die Elenden hören und sich freuen.",
			"4": "<span> 4 </span> \tPreiset mit mir den HERRN und laßt uns miteinander seinen Namen erhöhen!",
			"5": "<span> 5 </span> \tAls ich den HERRN suchte, antwortete er mir und errettete mich aus aller meiner Furcht.",
			"6": "<span> 6 </span> \tDie auf ihn sehen, werden strahlen vor Freude, und ihr Angesicht soll nicht schamrot werden.",
			"7": "<span> 7 </span> \tAls einer im Elend rief, hörte der HERR und half ihm aus allen seinen Nöten.",
			"8": "<span> 8 </span> \tDer Engel des HERRN lagert sich um die her, die ihn fürchten, und hilft ihnen heraus.",
			"9": "<span> 9 </span> \tSchmecket und sehet, wie freundlich der HERR ist. Wohl dem, der auf ihn trauet!",
			"10": "<span> 10 </span> \tFürchtet den HERRN, ihr seine Heiligen! Denn die ihn fürchten, haben keinen Mangel.",
			"11": "<span> 11 </span> \tReiche müssen darben und hungern; aber die den HERRN suchen, haben keinen Mangel an irgendeinem Gut.",
			"12": "<span> 12 </span> \tKommt her, ihr Kinder, höret mir zu! Ich will euch die Furcht des HERRN lehren.",
			"13": "<span> 13 </span> \tWer möchte gern gut leben und schöne Tage sehen?",
			"14": "<span> 14 </span> \tBehüte deine Zunge vor Bösem und deine Lippen, daß sie nicht Trug reden.",
			"15": "<span> 15 </span> \tLaß ab vom Bösen und tu Gutes; suche Frieden und jage ihm nach!",
			"16": "<span> 16 </span> \tDie Augen des HERRN merken auf die Gerechten und seine Ohren auf ihr Schreien.",
			"17": "<span> 17 </span> \tDas Angesicht des HERRN steht wider alle, die Böses tun, daß er ihren Namen ausrotte von der Erde.",
			"18": "<span> 18 </span> \tWenn die Gerechten schreien, so hört der HERR und errettet sie aus all ihrer Not.",
			"19": "<span> 19 </span> \tDer HERR ist nahe denen, die zerbrochenen Herzens sind, und hilft denen, die ein zerschlagenes Gemüt haben.",
			"20": "<span> 20 </span> \tDer Gerechte muß viel erleiden, aber aus alledem hilft ihm der HERR.",
			"21": "<span> 21 </span> \tEr bewahrt ihm alle seine Gebeine, daß nicht eines zerbrochen wird.",
			"22": "<span> 22 </span> \tDen Gottlosen wird das Unglück töten, und die den Gerechten hassen, fallen in Schuld.\r\n[23]\tDer HERR erlöst das Leben seiner Knechte, und alle, die auf ihn trauen, werden frei von Schuld."
		},
		"35": {
			"1": "<span>1</span> \tVON DAVID. HERR, führe meine Sache wider meine Widersacher, bekämpfe, die mich bekämpfen!",
			"2": "<span> 2 </span> \tErgreife Schild und Waffen und mache dich auf, mir zu helfen!",
			"3": "<span> 3 </span> \tZücke Speer und Streitaxt wider meine Verfolger! Sprich zu mir: Ich bin deine Hilfe!",
			"4": "<span> 4 </span> \tEs sollen sich schämen und zum Spott werden, die mir nach dem Leben trachten; es sollen zurückweichen und zuschanden werden, die mein Unglück wollen.",
			"5": "<span> 5 </span> \tSie sollen werden wie Spreu vor dem Winde, und der Engel des HERRN stoße sie weg.",
			"6": "<span> 6 </span> \tIhr Weg soll finster und schlüpfrig werden, und der Engel des HERRN verfolge sie.",
			"7": "<span> 7 </span> \tDenn ohne Grund haben sie mir ihr Netz gestellt, ohne Grund mir eine Grube gegraben.",
			"8": "<span> 8 </span> \tUnversehens soll ihn Unheil überfallen; sein Netz, das er gestellt hat, fange ihn selber, zum eigenen Unheil stürze er hinein.",
			"9": "<span> 9 </span> \tAber meine Seele soll sich freuen des HERRN und fröhlich sein über seine Hilfe.",
			"10": "<span> 10 </span> \tAlle meine Gebeine sollen sagen: HERR, wer ist dir gleich? Der du den Elenden rettest vor dem, der ihm zu stark ist, und den Elenden und Armen vor seinen Räubern.",
			"11": "<span> 11 </span> \tEs treten falsche Zeugen auf; sie fordern von mir, wovon ich nichts weiß.",
			"12": "<span> 12 </span> \tSie vergelten mir Gutes mit Bösem, um mich in Herzeleid zu bringen.",
			"13": "<span> 13 </span> \tIch aber zog einen Sack an, wenn sie krank waren, tat mir wehe mit Fasten und betete immer wieder von Herzen.",
			"14": "<span> 14 </span> \tAls wäre es mein Freund und Bruder, so ging ich einher; wie einer Leid trägt über seine Mutter, so beugte ich mich in Trauer.",
			"15": "<span> 15 </span> \tSie aber freuen sich, wenn ich wanke, und rotten sich zusammen; sie rotten sich heimlich zum Schlag wider mich, sie lästern und hören nicht auf.",
			"16": "<span> 16 </span> \tSie lästern und spotten immerfort und knirschen wider mich mit ihren Zähnen.",
			"17": "<span> 17 </span> \tHerr, wie lange willst du zusehen? Errette doch meine Seele vor ihrem Unheil, mein Leben vor den jungen Löwen!",
			"18": "<span> 18 </span> \tIch will dir danken in großer Gemeinde; unter vielem Volk will ich dich rühmen.",
			"19": "<span> 19 </span> \tLaß sich nicht über mich freuen, die mir zu Unrecht feind sind; laß nicht mit den Augen spotten, die mich ohne Grund hassen!",
			"20": "<span> 20 </span> \tDenn sie reden nicht, was zum Frieden dient, und ersinnen falsche Anklagen wider die Stillen im Lande.",
			"21": "<span> 21 </span> \tSie sperren das Maul weit auf wider mich und sprechen: «Da, da, wir haben es gesehen!»",
			"22": "<span> 22 </span> \tHERR, du hast es gesehen, schweige nicht; Herr, sei nicht ferne von mir!",
			"23": "<span> 23 </span> \tWache auf, werde wach, mir Recht zu schaffen und meine Sache zu führen, mein Gott und Herr!",
			"24": "<span> 24 </span> \tHERR, mein Gott, verhilf mir zum Recht nach deiner Gerechtigkeit, daß sie sich nicht über mich freuen.",
			"25": "<span> 25 </span> \tLaß sie nicht sagen in ihrem Herzen: «Da, da! das wollten wir.» Laß sie nicht sagen: «Wir haben ihn verschlungen.»",
			"26": "<span> 26 </span> \tSie sollen sich schämen und zuschanden werden, alle die sich meines Unglücks freuen; sie sollen in Schmach und Schande sich kleiden, die sich wider mich rühmen.",
			"27": "<span> 27 </span> \tJubeln und freuen sollen sich, die mir gönnen, daß ich recht behalte, und immer sagen: Der HERR sei hoch gelobt, der seinem Knecht so wohl will!",
			"28": "<span> 28 </span> \tUnd meine Zunge soll reden von deiner Gerechtigkeit und dich täglich preisen."
		},
		"36": {
			"1": "<span>1</span> \tVON DAVID, DEM KNECHT DES HERRN, VORZUSINGEN.",
			"2": "<span> 2 </span> \tEs sinnen die Übertreter auf gottloses Treiben im Grund ihres Herzens. Es ist keine Gottesfurcht bei ihnen.",
			"3": "<span> 3 </span> \tUnd doch hat Gott den Weg vor ihnen geebnet, um ihre Schuld aufzufinden und zu hassen.",
			"4": "<span> 4 </span> \tAlle ihre Worte sind falsch und erlogen, verständig und gut handeln sie nicht mehr.",
			"5": "<span> 5 </span> \tSie trachten auf ihrem Lager nach Schaden und stehen fest auf dem bösen Weg und scheuen kein Arges.",
			"6": "<span> 6 </span> \tHERR, deine Güte reicht, so weit der Himmel ist, und deine Wahrheit, so weit die Wolken gehen.",
			"7": "<span> 7 </span> \tDeine Gerechtigkeit steht wie die Berge Gottes und dein Recht wie die große Tiefe. HERR, du hilfst Menschen und Tieren.",
			"8": "<span> 8 </span> \tWie köstlich ist deine Güte, Gott, daß Menschenkinder unter dem Schatten deiner Flügel Zuflucht haben!",
			"9": "<span> 9 </span> \tSie werden satt von den reichen Gütern deines Hauses, und du tränkst sie mit Wonne wie mit einem Strom.",
			"10": "<span> 10 </span> \tDenn bei dir ist die Quelle des Lebens, und in deinem Lichte sehen wir das Licht.",
			"11": "<span> 11 </span> \tBreite deine Güte über die, die dich kennen, und deine Gerechtigkeit über die Frommen.",
			"12": "<span> 12 </span> \tLaß mich nicht kommen unter den Fuß der Stolzen, und die Hand der Gottlosen vertreibe mich nicht!\r\n[13]\tSiehe da, sie sind gefallen, die Übeltäter, sind gestürzt und können nicht wieder aufstehen."
		},
		"37": {
			"1": "<span>1</span> \tVON DAVID. Entrüste dich nicht über die Bösen, sei nicht neidisch auf die Übeltäter.",
			"2": "<span> 2 </span> \tDenn wie das Gras werden sie bald verdorren, und wie das grüne Kraut werden sie verwelken.",
			"3": "<span> 3 </span> \tHoffe auf den HERRN und tu Gutes, bleibe im Lande und nähre dich redlich.",
			"4": "<span> 4 </span> \tHabe deine Lust am HERRN; der wird dir geben, was dein Herz wünscht.",
			"5": "<span> 5 </span> \tBefiehl dem HERRN deine Wege und hoffe auf ihn, er wird's wohl machen",
			"6": "<span> 6 </span> \tund wird deine Gerechtigkeit heraufführen wie das Licht und dein Recht wie den Mittag.",
			"7": "<span> 7 </span> \tSei stille dem HERRN und warte auf ihn. Entrüste dich nicht über den, dem es gut geht, der seinen Mutwillen treibt.",
			"8": "<span> 8 </span> \tSteh ab vom Zorn und laß den Grimm, entrüste dich nicht, damit du nicht Unrecht tust.",
			"9": "<span> 9 </span> \tDenn die Bösen werden ausgerottet; die aber des HERRN harren, werden das Land erben.",
			"10": "<span> 10 </span> \tNoch eine kleine Zeit, so ist der Gottlose nicht mehr da; und wenn du nach seiner Stätte siehst, ist er weg.",
			"11": "<span> 11 </span> \tAber die Elenden werden das Land erben und ihre Freude haben an großem Frieden.",
			"12": "<span> 12 </span> \tDer Gottlose droht dem Gerechten und knirscht mit seinen Zähnen wider ihn.",
			"13": "<span> 13 </span> \tAber der Herr lacht seiner; denn er sieht, daß sein Tag kommt.",
			"14": "<span> 14 </span> \tDie Gottlosen ziehen das Schwert und spannen ihren Bogen, daß sie fällen den Elenden und Armen und morden die Frommen.",
			"15": "<span> 15 </span> \tAber ihr Schwert wird in ihr eigenes Herz dringen, und ihr Bogen wird zerbrechen.",
			"16": "<span> 16 </span> \tDas Wenige, das ein Gerechter hat, ist besser als der Überfluß vieler Gottloser.",
			"17": "<span> 17 </span> \tDenn der Gottlosen Arm wird zerbrechen, aber der HERR erhält die Gerechten.",
			"18": "<span> 18 </span> \tDer HERR kennt die Tage der Frommen, und ihr Gut wird ewiglich bleiben.",
			"19": "<span> 19 </span> \tSie werden nicht zuschanden in böser Zeit, und in der Hungersnot werden sie genug haben.",
			"20": "<span> 20 </span> \tDenn die Gottlosen werden umkommen; und die Feinde des HERRN, wenn sie auch sind wie prächtige Auen, werden sie doch vergehen, wie der Rauch vergeht.",
			"21": "<span> 21 </span> \tDer Gottlose muß borgen und bezahlt nicht, aber der Gerechte ist barmherzig und kann geben.",
			"22": "<span> 22 </span> \tDenn die Gesegneten des HERRN erben das Land; aber die er verflucht, werden ausgerottet.",
			"23": "<span> 23 </span> \tVon dem HERRN kommt es, wenn eines Mannes Schritte fest werden, und er hat Gefallen an seinem Wege.",
			"24": "<span> 24 </span> \tFällt er, so stürzt er doch nicht; denn der HERR hält ihn fest an der Hand.",
			"25": "<span> 25 </span> \tIch bin jung gewesen und alt geworden und habe noch nie den Gerechten verlassen gesehen und seine Kinder um Brot betteln.",
			"26": "<span> 26 </span> \tEr ist allezeit barmherzig und leiht gerne, und sein Geschlecht wird zum Segen sein.",
			"27": "<span> 27 </span> \tLaß ab vom Bösen und tu Gutes, so bleibst du wohnen immerdar.",
			"28": "<span> 28 </span> \tDenn der HERR hat das Recht lieb und verläßt seine Heiligen nicht. Ewiglich werden sie bewahrt, aber das Geschlecht der Gottlosen wird ausgerottet.",
			"29": "<span> 29 </span> \tDie Gerechten werden das Land ererben und darin wohnen allezeit.",
			"30": "<span> 30 </span> \tDer Mund des Gerechten redet Weisheit, und seine Zunge lehrt das Recht.",
			"31": "<span> 31 </span> \tDas Gesetz seines Gottes ist in seinem Herzen; seine Tritte gleiten nicht.",
			"32": "<span> 32 </span> \tDer Gottlose lauert dem Gerechten auf und gedenkt, ihn zu töten.",
			"33": "<span> 33 </span> \tAber der HERR läßt ihn nicht in seinen Händen und läßt ihn vor Gericht nicht zum Schuldigen werden.",
			"34": "<span> 34 </span> \tHarre auf den HERRN und halte dich auf seinem Weg, so wird er dich erhöhen, daß du das Land erbest; du wirst es sehen, daß die Gottlosen ausgerottet werden.",
			"35": "<span> 35 </span> \tIch sah einen Gottlosen, der pochte auf Gewalt und machte sich breit und grünte wie eine Zeder.",
			"36": "<span> 36 </span> \tDann kam ich wieder vorbei; siehe, da war er dahin. Ich fragte nach ihm; doch ward er nirgends gefunden.",
			"37": "<span> 37 </span> \tBleibe fromm und halte dich recht; denn einem solchen wird es zuletzt gut gehen.",
			"38": "<span> 38 </span> \tDie Übertreter aber werden miteinander vertilgt, und die Gottlosen werden zuletzt ausgerottet.",
			"39": "<span> 39 </span> \tAber der HERR hilft den Gerechten, er ist ihre Stärke in der Not.",
			"40": "<span> 40 </span> \tUnd der HERR wird ihnen beistehen und sie erretten; er wird sie von den Gottlosen erretten und ihnen helfen; denn sie trauen auf ihn."
		},
		"38": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, ZUM GEDENKOPFER.",
			"2": "<span> 2 </span> \tHERR, strafe mich nicht in deinem Zorn und züchtige mich nicht in deinem Grimm!",
			"3": "<span> 3 </span> \tDenn deine Pfeile stecken in mir, und deine Hand drückt mich.",
			"4": "<span> 4 </span> \tEs ist nichts Gesundes an meinem Leibe wegen deines Drohens und ist nichts Heiles an meinen Gebeinen wegen meiner Sünde.",
			"5": "<span> 5 </span> \tDenn meine Sünden gehen über mein Haupt; wie eine schwere Last sind sie mir zu schwer geworden.",
			"6": "<span> 6 </span> \tMeine Wunden stinken und eitern um meiner Torheit willen.",
			"7": "<span> 7 </span> \tIch gehe krumm und sehr gebückt; den ganzen Tag gehe ich traurig einher.",
			"8": "<span> 8 </span> \tDenn meine Lenden sind ganz verdorrt; es ist nichts Gesundes an meinem Leibe.",
			"9": "<span> 9 </span> \tIch bin matt geworden und ganz zerschlagen; ich schreie vor Unruhe meines Herzens.",
			"10": "<span> 10 </span> \tHerr, du kennst all mein Begehren, und mein Seufzen ist dir nicht verborgen.",
			"11": "<span> 11 </span> \tMein Herz erbebt, meine Kraft hat mich verlassen, und das Licht meiner Augen ist auch dahin.",
			"12": "<span> 12 </span> \tMeine Lieben und Freunde scheuen zurück vor meiner Plage, und meine Nächsten halten sich ferne.",
			"13": "<span> 13 </span> \tDie mir nach dem Leben trachten, stellen mir nach; und die mein Unglück suchen, bereden, wie sie mir schaden; sie sinnen auf Trug den ganzen Tag.",
			"14": "<span> 14 </span> \tIch bin wie taub und höre nicht, und wie ein Stummer, der seinen Mund nicht auftut.",
			"15": "<span> 15 </span> \tIch muß sein wie einer, der nicht hört und keine Widerrede in seinem Munde hat.",
			"16": "<span> 16 </span> \tAber ich harre, HERR, auf dich; du, Herr, mein Gott, wirst erhören.",
			"17": "<span> 17 </span> \tDenn ich denke: Daß sie sich ja nicht über mich freuen! Wenn mein Fuß wankte, würden sie sich hoch rühmen wider mich.",
			"18": "<span> 18 </span> \tDenn ich bin dem Fallen nahe, und mein Schmerz ist immer vor mir.",
			"19": "<span> 19 </span> \tSo bekenne ich denn meine Missetat und sorge mich wegen meiner Sünde.",
			"20": "<span> 20 </span> \tAber meine Feinde leben und sind mächtig; die mich zu Unrecht hassen, derer sind viele.",
			"21": "<span> 21 </span> \tDie mir Gutes mit Bösem vergelten, feinden mich an, weil ich mich an das Gute halte.",
			"22": "<span> 22 </span> \tVerlaß mich nicht, HERR, mein Gott, sei nicht ferne von mir!\r\n[23]\tEile, mir beizustehen, Herr, du meine Hilfe!"
		},
		"39": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, FÜR JEDUTHUN.",
			"2": "<span> 2 </span> \tIch habe mir vorgenommen: Ich will mich hüten, daß ich nicht sündige mit meiner Zunge; ich will meinem Mund einen Zaum anlegen, solange ich den Gottlosen vor mir sehen muß.",
			"3": "<span> 3 </span> \tIch bin verstummt und still und schweige fern der Freude und muß mein Leid in mich fressen.",
			"4": "<span> 4 </span> \tMein Herz ist entbrannt in meinem Leibe; wenn ich daran denke, brennt es wie Feuer. So rede ich denn mit meiner Zunge:",
			"5": "<span> 5 </span> \t«HERR, lehre mich doch, daß es ein Ende mit mir haben muß und mein Leben ein Ziel hat und ich davon muß.",
			"6": "<span> 6 </span> \tSiehe, meine Tage sind eine Handbreit bei dir, und mein Leben ist wie nichts vor dir. Wie gar nichts sind alle Menschen, die doch so sicher leben! SELA.",
			"7": "<span> 7 </span> \tSie gehen daher wie ein Schatten und machen sich viel vergebliche Unruhe; sie sammeln und wissen nicht, wer es einbringen wird.»",
			"8": "<span> 8 </span> \tNun, Herr, wessen soll ich mich trösten? Ich hoffe auf dich.",
			"9": "<span> 9 </span> \tErrette mich aus aller meiner Sünde und laß mich nicht den Narren zum Spott werden.",
			"10": "<span> 10 </span> \tIch will schweigen und meinen Mund nicht auftun; denn du hast es getan.",
			"11": "<span> 11 </span> \tWende deine Plage von mir; ich vergehe, weil deine Hand nach mir greift.",
			"12": "<span> 12 </span> \tWenn du den Menschen züchtigst um der Sünde willen, so verzehrst du seine Schönheit wie Motten ein Kleid. Wie gar nichts sind doch alle Menschen. SELA.",
			"13": "<span> 13 </span> \tHöre mein Gebet, HERR, und vernimm mein Schreien, schweige nicht zu meinen Tränen; denn ich bin ein Gast bei dir, ein Fremdling wie alle meine Väter.\r\n[14]\tLaß ab von mir, daß ich mich erquicke, ehe ich dahinfahre und nicht mehr bin."
		},
		"40": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tIch harrte des HERRN, und er neigte sich zu mir und hörte mein Schreien.",
			"3": "<span> 3 </span> \tEr zog mich aus der grausigen Grube, aus lauter Schmutz und Schlamm, und stellte meine Füße auf einen Fels, daß ich sicher treten kann;",
			"4": "<span> 4 </span> \ter hat mir ein neues Lied in meinen Mund gegeben, zu loben unsern Gott. Das werden viele sehen und sich fürchten und auf den HERRN hoffen.",
			"5": "<span> 5 </span> \tWohl dem, der seine Hoffnung setzt auf den HERRN und sich nicht wendet zu den Hoffärtigen und denen, die mit Lügen umgehen!",
			"6": "<span> 6 </span> \tHERR, mein Gott, groß sind deine Wunder und deine Gedanken, die du an uns beweisest; dir ist nichts gleich! Ich will sie verkündigen und davon sagen, wiewohl sie nicht zu zählen sind.",
			"7": "<span> 7 </span> \tSchlachtopfer und Speisopfer gefallen dir nicht, aber die Ohren hast du mir aufgetan. Du willst weder Brandopfer noch Sündopfer.",
			"8": "<span> 8 </span> \tDa sprach ich: Siehe, ich komme; im Buch ist von mir geschrieben:",
			"9": "<span> 9 </span> \tDeinen Willen, mein Gott, tue ich gern, und dein Gesetz hab ich in meinem Herzen.",
			"10": "<span> 10 </span> \tIch verkündige Gerechtigkeit in der großen Gemeinde. Siehe, ich will mir meinen Mund nicht stopfen lassen; HERR, das weißt du.",
			"11": "<span> 11 </span> \tDeine Gerechtigkeit verberge ich nicht in meinem Herzen; von deiner Wahrheit und von deinem Heil rede ich. Ich verhehle deine Güte und Treue nicht vor der großen Gemeinde.",
			"12": "<span> 12 </span> \tDu aber, HERR, wollest deine Barmherzigkeit nicht von mir wenden; laß deine Güte und Treue allewege mich behüten.",
			"13": "<span> 13 </span> \tDenn es haben mich umgeben Leiden ohne Zahl. Meine Sünden haben mich ereilt; ich kann sie nicht überblicken. Ihrer sind mehr als Haare auf meinem Haupt, und mein Herz ist verzagt.",
			"14": "<span> 14 </span> \tLaß dir's gefallen, HERR, mich zu erretten; eile, HERR, mir zu helfen!",
			"15": "<span> 15 </span> \tSchämen sollen sich und zuschanden werden, die mir nach dem Leben stehen, mich umzubringen. Es sollen zurückweichen und zuschanden werden, die mir mein Unglück gönnen.",
			"16": "<span> 16 </span> \tSie sollen in ihrer Schande erschrecken, die über mich schreien: Da, da!",
			"17": "<span> 17 </span> \tLaß deiner sich freuen und fröhlich sein alle, die nach dir fragen; und die dein Heil lieben, laß allewege sagen: Der HERR sei hoch gelobt!\r\n[18]\tDenn ich bin arm und elend; der Herr aber sorgt für mich. Du bist mein Helfer und Erretter; mein Gott, säume doch nicht!"
		},
		"41": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tWohl dem, der sich des Schwachen annimmt! Den wird der HERR erretten zur bösen Zeit.",
			"3": "<span> 3 </span> \tDer HERR wird ihn bewahren und beim Leben erhalten und es ihm lassen wohl gehen auf Erden und ihn nicht preisgeben dem Willen seiner Feinde.",
			"4": "<span> 4 </span> \tDer HERR wird ihn erquicken auf seinem Lager; du hilfst ihm auf von aller seiner Krankheit.",
			"5": "<span> 5 </span> \tIch sprach: HERR, sei mir gnädig! Heile mich; denn ich habe an dir gesündigt.",
			"6": "<span> 6 </span> \tMeine Feinde reden Arges wider mich: «Wann wird er sterben und sein Name vergehen?»",
			"7": "<span> 7 </span> \tSie kommen, nach mir zu schauen, und meinen's doch nicht von Herzen; sondern sie suchen etwas, daß sie lästern können, gehen hin und tragen's hinaus auf die Gasse.",
			"8": "<span> 8 </span> \tAlle, die mich hassen, flüstern miteinander über mich und denken Böses über mich:",
			"9": "<span> 9 </span> \t«Unheil ist über ihn ausgegossen; wer so daliegt, wird nicht wieder aufstehen.»",
			"10": "<span> 10 </span> \tAuch mein Freund, dem ich vertraute, der mein Brot aß, tritt mich mit Füßen.",
			"11": "<span> 11 </span> \tDu aber, HERR, sei mir gnädig und hilf mir auf, so will ich ihnen vergelten.",
			"12": "<span> 12 </span> \tDaran merke ich, daß du Gefallen an mir hast, daß mein Feind über mich nicht frohlocken wird.",
			"13": "<span> 13 </span> \tMich aber hältst du um meiner Frömmigkeit willen und stellst mich vor dein Angesicht für ewig.\r\n[14]\tGelobt sei der HERR, der Gott Israels, von Ewigkeit zu Ewigkeit! Amen! Amen!"
		},
		"42": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DER SÖHNE KORAH, VORZUSINGEN.",
			"2": "<span> 2 </span> \tWie der Hirsch lechzt nach frischem Wasser, so schreit meine Seele, Gott, zu dir.",
			"3": "<span> 3 </span> \tMeine Seele dürstet nach Gott, nach dem lebendigen Gott. Wann werde ich dahin kommen, daß ich Gottes Angesicht schaue?",
			"4": "<span> 4 </span> \tMeine Tränen sind meine Speise Tag und Nacht, weil man täglich zu mir sagt: Wo ist nun dein Gott?",
			"5": "<span> 5 </span> \tDaran will ich denken und ausschütten mein Herz bei mir selbst: wie ich einherzog in großer Schar, mit ihnen zu wallen zum Hause Gottes mit Frohlocken und Danken in der Schar derer, die da feiern.",
			"6": "<span> 6 </span> \tWas betrübst du dich, meine Seele, und bist so unruhig in mir? Harre auf Gott; denn ich werde ihm noch danken, daß er meines Angesichts Hilfe und mein Gott ist.",
			"7": "<span> 7 </span> \tMein Gott, betrübt ist meine Seele in mir, darum gedenke ich an dich aus dem Land am Jordan und Hermon, vom Berge Misar.",
			"8": "<span> 8 </span> \tDeine Fluten rauschen daher, und eine Tiefe ruft die andere; alle deine Wasserwogen und Wellen gehen über mich.",
			"9": "<span> 9 </span> \tAm Tage sendet der HERR seine Güte, und des Nachts singe ich ihm und bete zu dem Gott meines Lebens.",
			"10": "<span> 10 </span> \tIch sage zu Gott, meinem Fels: Warum hast du mich vergessen? Warum muß ich so traurig gehen, wenn mein Feind mich dränget?",
			"11": "<span> 11 </span> \tEs ist wie Mord in meinen Gebeinen, wenn mich meine Feinde schmähen und täglich zu mir sagen: Wo ist nun dein Gott?\r\n[12]\tWas betrübst du dich, meine Seele, und bist so unruhig in mir? Harre auf Gott; denn ich werde ihm noch danken, daß er meines Angesichts Hilfe und mein Gott ist."
		},
		"43": {
			"1": "<span>1</span> \tGott, schaffe mir Recht und führe meine Sache wider das unheilige Volk und errette mich von den falschen und bösen Leuten!",
			"2": "<span> 2 </span> \tDenn du bist der Gott meiner Stärke: Warum hast du mich verstoßen? Warum muß ich so traurig gehen, wenn mein Feind mich dränget?",
			"3": "<span> 3 </span> \tSende dein Licht und deine Wahrheit, daß sie mich leiten und bringen zu deinem heiligen Berg und zu deiner Wohnung,",
			"4": "<span> 4 </span> \tdaß ich hingehe zum Altar Gottes, zu dem Gott, der meine Freude und Wonne ist, und dir, Gott, auf der Harfe danke, mein Gott.",
			"5": "<span> 5 </span> \tWas betrübst du dich, meine Seele, und bist so unruhig in mir? Harre auf Gott; denn ich werde ihm noch danken, daß er meines Angesichts Hilfe und mein Gott ist."
		},
		"44": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DER SÖHNE KORAH, VORZUSINGEN.",
			"2": "<span> 2 </span> \tGott, wir haben mit unsern Ohren gehört, unsre Väter haben's uns erzählt, was du getan hast zu ihren Zeiten, in alten Tagen.",
			"3": "<span> 3 </span> \tDu hast mit deiner Hand die Heiden vertrieben, sie aber hast du eingesetzt; du hast die Völker zerschlagen, sie aber hast du ausgebreitet.",
			"4": "<span> 4 </span> \tDenn sie haben das Land nicht eingenommen durch ihr Schwert, und ihr Arm half ihnen nicht, sondern deine Rechte, dein Arm und das Licht deines Angesichts; denn du hattest Wohlgefallen an ihnen.",
			"5": "<span> 5 </span> \tDu bist es, mein König und mein Gott, der du Jakob Hilfe verheißest.",
			"6": "<span> 6 </span> \tDurch dich wollen wir unsre Feinde zu Boden stoßen, in deinem Namen niedertreten, die sich gegen uns erheben.",
			"7": "<span> 7 </span> \tDenn ich verlasse mich nicht auf meinen Bogen, und mein Schwert kann mir nicht helfen;",
			"8": "<span> 8 </span> \tsondern du hilfst uns von unsern Feinden und machst zuschanden, die uns hassen.",
			"9": "<span> 9 </span> \tTäglich rühmen wir uns Gottes und preisen deinen Namen ewiglich. SELA.",
			"10": "<span> 10 </span> \tWarum verstößest du uns denn nun und lässest uns zuschanden werden und ziehst nicht aus mit unserm Heer?",
			"11": "<span> 11 </span> \tDu lässest uns fliehen vor unserm Feind, daß uns berauben, die uns hassen.",
			"12": "<span> 12 </span> \tDu gibst uns dahin wie Schlachtschafe und zerstreust uns unter die Heiden.",
			"13": "<span> 13 </span> \tDu verkaufst dein Volk um ein Nichts und hast mit ihrem Kaufgeld nichts gewonnen.",
			"14": "<span> 14 </span> \tDu machst uns zur Schmach bei unsern Nachbarn, zu Spott und Hohn bei denen, die um uns her sind.",
			"15": "<span> 15 </span> \tDu machst uns zum Sprichwort unter den Heiden, läßt die Völker das Haupt über uns schütteln.",
			"16": "<span> 16 </span> \tTäglich ist meine Schmach mir vor Augen, und mein Antlitz ist voller Scham,",
			"17": "<span> 17 </span> \tweil ich sie höhnen und lästern höre und muß die Feinde und Rachgierigen sehen.",
			"18": "<span> 18 </span> \tDies alles ist über uns gekommen; und wir haben doch dich nicht vergessen, an deinem Bund nicht untreu gehandelt.",
			"19": "<span> 19 </span> \tUnser Herz ist nicht abgefallen noch unser Schritt gewichen von deinem Weg,",
			"20": "<span> 20 </span> \tdaß du uns so zerschlägst am Ort der Schakale und bedeckst uns mit Finsternis.",
			"21": "<span> 21 </span> \tWenn wir den Namen unsres Gottes vergessen hätten und unsre Hände aufgehoben zum fremden Gott:",
			"22": "<span> 22 </span> \twürde das Gott nicht erforschen? Er kennt ja unsres Herzens Grund.",
			"23": "<span> 23 </span> \tDoch um deinetwillen werden wir täglich getötet und sind geachtet wie Schlachtschafe.",
			"24": "<span> 24 </span> \tWache auf, Herr! Warum schläfst du? Werde wach und verstoß uns nicht für immer!",
			"25": "<span> 25 </span> \tWarum verbirgst du dein Antlitz, vergissest unser Elend und unsre Drangsal?",
			"26": "<span> 26 </span> \tDenn unsre Seele ist gebeugt zum Staube, unser Leib liegt am Boden.\r\n[27]\tMache dich auf, hilf uns und erlöse uns um deiner Güte willen!"
		},
		"45": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DER SÖHNE KORAH, VORZUSINGEN, NACH DER WEISE «LILIEN», EIN BRAUTLIED.",
			"2": "<span> 2 </span> \tMein Herz dichtet ein feines Lied, einem König will ich es singen; meine Zunge ist ein Griffel eines guten Schreibers:",
			"3": "<span> 3 </span> \tDu bist der Schönste unter den Menschenkindern, voller Huld sind deine Lippen; wahrlich, Gott hat dich gesegnet für ewig.",
			"4": "<span> 4 </span> \tGürte dein Schwert an die Seite, du Held, und schmücke dich herrlich!",
			"5": "<span> 5 </span> \tEs möge dir gelingen in deiner Herrlichkeit. Zieh einher für die Wahrheit in Sanftmut und Gerechtigkeit, so wird deine rechte Hand Wunder vollbringen.",
			"6": "<span> 6 </span> \tScharf sind deine Pfeile, daß Völker vor dir fallen; sie dringen ins Herz der Feinde des Königs.",
			"7": "<span> 7 </span> \tGott, dein Thron bleibt immer und ewig; das Zepter deines Reichs ist ein gerechtes Zepter.",
			"8": "<span> 8 </span> \tDu liebst Gerechtigkeit und hassest gottloses Treiben; darum hat dich der Herr, dein Gott, gesalbt mit Freudenöl wie keinen deinesgleichen.",
			"9": "<span> 9 </span> \tDeine Kleider sind lauter Myrrhe, Aloe und Kassia; aus Elfenbeinpalästen erfreut dich Saitenspiel.",
			"10": "<span> 10 </span> \tIn deinem Schmuck gehen Töchter von Königen; die Braut steht zu deiner Rechten in Goldschmuck aus Ophir.",
			"11": "<span> 11 </span> \tHöre, Tochter, sieh und neige dein Ohr: Vergiß dein Volk und dein Vaterhaus!",
			"12": "<span> 12 </span> \tDen König verlangt nach deiner Schönheit; denn er ist dein Herr, und du sollst ihm huldigen.",
			"13": "<span> 13 </span> \tDie Tochter Tyrus kommt mit Geschenken; die Reichen im Volk suchen deine Gunst.",
			"14": "<span> 14 </span> \tDie Königstochter ist mit Perlen geschmückt; sie ist mit goldenen Gewändern bekleidet.",
			"15": "<span> 15 </span> \tMan führt sie in gestickten Kleidern zum König; Jungfrauen folgen ihr, ihre Gespielinnen führt man zu dir.",
			"16": "<span> 16 </span> \tMan führt sie hin mit Freude und Jubel; sie ziehen ein in des Königs Palast.",
			"17": "<span> 17 </span> \tAn deiner Väter Statt werden deine Söhne sein; die wirst du zu Fürsten setzen in aller Welt.\r\n[18]\tIch will deinen Namen kund machen von Kind zu Kindeskind; darum werden dir danken die Völker immer und ewig."
		},
		"46": {
			"1": "<span>1</span> \tEIN LIED DER SÖHNE KORAH, VORZUSINGEN, NACH DER WEISE «JUNGFRAUEN».",
			"2": "<span> 2 </span> \tGott ist unsre Zuversicht und Stärke, eine Hilfe in den großen Nöten, die uns getroffen haben.",
			"3": "<span> 3 </span> \tDarum fürchten wir uns nicht, wenngleich die Welt unterginge und die Berge mitten ins Meer sänken,",
			"4": "<span> 4 </span> \twenngleich das Meer wütete und wallte und von seinem Ungestüm die Berge einfielen. SELA.\r\nVermutlich stand der Kehrvers von Vers 8 und 12 auch an dieser Stelle.",
			"5": "<span> 5 </span> \tDennoch soll die Stadt Gottes fein lustig bleiben mit ihren Brünnlein, da die heiligen Wohnungen des Höchsten sind.",
			"6": "<span> 6 </span> \tGott ist bei ihr drinnen, darum wird sie fest bleiben; Gott hilft ihr früh am Morgen.",
			"7": "<span> 7 </span> \tDie Heiden müssen verzagen und die Königreiche fallen, das Erdreich muß vergehen, wenn er sich hören läßt.",
			"8": "<span> 8 </span> \tDer HERR Zebaoth ist mit uns, der Gott Jakobs ist unser Schutz. SELA.",
			"9": "<span> 9 </span> \tKommt her und schauet die Werke des HERRN, der auf Erden solch ein Zerstören anrichtet,",
			"10": "<span> 10 </span> \tder den Kriegen steuert in aller Welt, der Bogen zerbricht, Spieße zerschlägt und Wagen mit Feuer verbrennt.",
			"11": "<span> 11 </span> \tSeid stille und erkennet, daß ich Gott bin! Ich will der Höchste sein unter den Heiden, der Höchste auf Erden.\r\n[12]\tDer HERR Zebaoth ist mit uns, der Gott Jakobs ist unser Schutz. SELA."
		},
		"47": {
			"1": "<span>1</span> \tEIN PSALM DER SÖHNE KORAH, VORZUSINGEN.",
			"2": "<span> 2 </span> \tSchlagt froh in die Hände, alle Völker, und jauchzet Gott mit fröhlichem Schall!",
			"3": "<span> 3 </span> \tDenn der HERR, der Allerhöchste, ist heilig, ein großer König über die ganze Erde.",
			"4": "<span> 4 </span> \tEr beugt die Völker unter uns und Völkerschaften unter unsere Füße.",
			"5": "<span> 5 </span> \tEr erwählt uns unser Erbteil, die Herrlichkeit Jakobs, den er lieb hat. SELA.",
			"6": "<span> 6 </span> \tGott fährt auf unter Jauchzen, der HERR beim Hall der Posaune.",
			"7": "<span> 7 </span> \tLobsinget, lobsinget Gott, lobsinget, lobsinget unserm Könige!",
			"8": "<span> 8 </span> \tDenn Gott ist König über die ganze Erde; lobsinget ihm mit Psalmen!",
			"9": "<span> 9 </span> \tGott ist König über die Völker, Gott sitzt auf seinem heiligen Thron.\r\n[10]\tDie Fürsten der Völker sind versammelt als Volk des Gottes Abrahams; denn Gott gehören die Starken auf Erden; er ist hoch erhaben."
		},
		"48": {
			"1": "<span>1</span> \tEIN PSALMLIED DER SÖHNE KORAH.",
			"2": "<span> 2 </span> \tGroß ist der HERR und hoch zu rühmen in der Stadt unsres Gottes, auf seinem heiligen Berge.",
			"3": "<span> 3 </span> \tSchön ragt empor der Berg Zion, daran sich freut die ganze Welt, der Gottesberg fern im Norden, die Stadt des großen Königs.",
			"4": "<span> 4 </span> \tGott ist in ihren Palästen, er ist bekannt als Schutz.",
			"5": "<span> 5 </span> \tDenn siehe, Könige waren versammelt und miteinander herangezogen.",
			"6": "<span> 6 </span> \tSie haben sich verwundert, als sie solches sahen; sie haben sich entsetzt und sind davongestürzt.",
			"7": "<span> 7 </span> \tZittern hat sie da erfaßt, Angst wie eine Gebärende.",
			"8": "<span> 8 </span> \tDu zerbrichst die großen Schiffe durch den Sturm vom Osten.",
			"9": "<span> 9 </span> \tWie wir es gehört haben, so sehen wir es an der Stadt des HERRN Zebaoth, an der Stadt unsres Gottes: Gott erhält sie ewiglich. SELA.",
			"10": "<span> 10 </span> \tGott, wir gedenken deiner Güte in deinem Tempel.",
			"11": "<span> 11 </span> \tGott, wie dein Name, so ist auch dein Ruhm bis an der Welt Enden. Deine Rechte ist voll Gerechtigkeit.",
			"12": "<span> 12 </span> \tDessen freue sich der Berg Zion, und die Töchter Judas seien fröhlich, weil du recht richtest.",
			"13": "<span> 13 </span> \tZiehet um Zion herum und umschreitet es, zählt seine Türme;",
			"14": "<span> 14 </span> \thabt gut acht auf seine Mauern, durchwandert seine Paläste, daß ihr den Nachkommen davon erzählt:\r\n[15]\tWahrlich, das ist Gott, unser Gott für immer und ewig. Er ist's, der uns führet."
		},
		"49": {
			"1": "<span>1</span> \tEIN PSALM DER SÖHNE KORAH, VORZUSINGEN, NACH DER WEISE «JUGEND».",
			"2": "<span> 2 </span> \tHöret zu, alle Völker; merket auf, alle, die in dieser Zeit leben,",
			"3": "<span> 3 </span> \teinfache Leute und Herren, reich und arm, miteinander!",
			"4": "<span> 4 </span> \tMein Mund soll Weisheit reden, und was mein Herz sagt, soll verständig sein.",
			"5": "<span> 5 </span> \tIch will einem Spruch mein Ohr neigen und mein Rätselwort kundtun beim Klang der Harfe.",
			"6": "<span> 6 </span> \tWarum sollte ich mich fürchten in bösen Tagen, wenn mich die Missetat meiner Widersacher umgibt,",
			"7": "<span> 7 </span> \tdie sich verlassen auf Hab und Gut und pochen auf ihren großen Reichtum?",
			"8": "<span> 8 </span> \tKann doch keiner einen andern auslösen oder für ihn an Gott ein Sühnegeld geben",
			"9": "<span> 9 </span> \t- denn es kostet zuviel, ihr Leben auszulösen; er muß davon abstehen ewiglich -,",
			"10": "<span> 10 </span> \tdamit er immer weiterlebe und die Grube nicht sehe.",
			"11": "<span> 11 </span> \tNein, er wird sehen: auch die Weisen sterben, so wie die Toren und Narren umkommen; sie müssen ihr Gut andern lassen.",
			"12": "<span> 12 </span> \tGräber sind ihr Haus immerdar, ihre Wohnung für und für, und doch hatten sie große Ehre auf Erden.",
			"13": "<span> 13 </span> \tEin Mensch in seiner Herrlichkeit kann nicht bleiben, sondern muß davon wie das Vieh.",
			"14": "<span> 14 </span> \tDies ist der Weg derer, die so voll Torheit sind, und das Ende aller, denen ihr Gerede so wohl gefällt. SELA.",
			"15": "<span> 15 </span> \tSie liegen bei den Toten wie Schafe, der Tod weidet sie; aber die Frommen werden gar bald über sie herrschen, und ihr Trotz muß vergehen; bei den Toten müssen sie bleiben.",
			"16": "<span> 16 </span> \tAber Gott wird mich erlösen aus des Todes Gewalt; denn er nimmt mich auf. SELA.",
			"17": "<span> 17 </span> \tLaß es dich nicht anfechten, wenn einer reich wird, wenn die Herrlichkeit seines Hauses groß wird.",
			"18": "<span> 18 </span> \tDenn er wird nichts bei seinem Sterben mitnehmen, und seine Herrlichkeit wird ihm nicht nachfahren.",
			"19": "<span> 19 </span> \tEr freut sich wohl dieses guten Lebens, und man preist dich, wenn es dir gut geht.",
			"20": "<span> 20 </span> \tAber doch fahren sie ihren Vätern nach und sehen das Licht nimmermehr.\r\n[21]\tEin Mensch in seiner Herrlichkeit kann nicht bleiben, sondern muß davon wie das Vieh."
		},
		"50": {
			"1": "<span>1</span> \tEIN PSALM ASAPHS. Gott, der HERR, der Mächtige, redet und ruft der Welt zu vom Aufgang der Sonne bis zu ihrem Niedergang.",
			"2": "<span> 2 </span> \tAus Zion bricht an der schöne Glanz Gottes.",
			"3": "<span> 3 </span> \tUnser Gott kommt und schweiget nicht. Fressendes Feuer geht vor ihm her und um ihn her ein mächtiges Wetter.",
			"4": "<span> 4 </span> \tEr ruft Himmel und Erde zu, daß er sein Volk richten wolle:",
			"5": "<span> 5 </span> \t«Versammelt mir meine Heiligen, die den Bund mit mir schlossen beim Opfer.»",
			"6": "<span> 6 </span> \tUnd die Himmel werden seine Gerechtigkeit verkünden; denn Gott selbst ist Richter. SELA .",
			"7": "<span> 7 </span> \t«Höre, mein Volk, laß mich reden; Israel, ich will wider dich zeugen: Ich, Gott, bin dein Gott.",
			"8": "<span> 8 </span> \tNicht deiner Opfer wegen klage ich dich an – sind doch deine Brandopfer täglich vor mir.",
			"9": "<span> 9 </span> \tIch will von deinem Hause Stiere nicht nehmen noch Böcke aus deinen Ställen.",
			"10": "<span> 10 </span> \tDenn alles Wild im Walde ist mein und die Tiere auf den Bergen zu Tausenden.",
			"11": "<span> 11 </span> \tIch kenne alle Vögel auf den Bergen; und was sich regt auf dem Felde, ist mein.",
			"12": "<span> 12 </span> \tWenn mich hungerte, wollte ich dir nicht davon sagen; denn der Erdkreis ist mein und alles, was darauf ist.",
			"13": "<span> 13 </span> \tMeinst du, daß ich Fleisch von Stieren essen wolle oder Blut von Böcken trinken?",
			"14": "<span> 14 </span> \tOpfere Gott Dank und erfülle dem Höchsten deine Gelübde",
			"15": "<span> 15 </span> \tund rufe mich an in der Not, so will ich dich erretten, und du sollst mich preisen.»",
			"16": "<span> 16 </span> \tAber zum Gottlosen spricht Gott: «Was hast du von meinen Geboten zu reden und nimmst meinen Bund in deinen Mund,",
			"17": "<span> 17 </span> \tda du doch Zucht hassest und wirfst meine Worte hinter dich?",
			"18": "<span> 18 </span> \tWenn du einen Dieb siehst, so läufst du mit ihm und hast Gemeinschaft mit den Ehebrechern.",
			"19": "<span> 19 </span> \tDeinen Mund lässest du Böses reden, und deine Zunge treibt Falschheit.",
			"20": "<span> 20 </span> \tDu sitzest und redest wider deinen Bruder; deiner Mutter Sohn verleumdest du.",
			"21": "<span> 21 </span> \tDas tust du, und ich schweige; da meinst du, ich sei so wie du. Aber ich will dich zurechtweisen und es dir vor Augen stellen.",
			"22": "<span> 22 </span> \tBegreift es doch, die ihr Gott vergesset, damit ich nicht hinraffe, und kein Retter ist da!",
			"23": "<span> 23 </span> \tWer Dank opfert, der preiset mich, und da ist der Weg, daß ich ihm zeige das Heil Gottes.»"
		},
		"51": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN,",
			"2": "<span> 2 </span> \tALS DER PROPHET NATHAN ZU IHM KAM, NACHDEM ER ZU BATHSEBA EINGEGANGEN WAR.",
			"3": "<span> 3 </span> \tGott, sei mir gnädig nach deiner Güte, und tilge meine Sünden nach deiner großen Barmherzigkeit.",
			"4": "<span> 4 </span> \tWasche mich rein von meiner Missetat, und reinige mich von meiner Sünde;",
			"5": "<span> 5 </span> \tdenn ich erkenne meine Missetat, und meine Sünde ist immer vor mir.",
			"6": "<span> 6 </span> \tAn dir allein habe ich gesündigt und übel vor dir getan, auf daß du recht behaltest in deinen Worten und rein dastehst, wenn du richtest.",
			"7": "<span> 7 </span> \tSiehe, ich bin als Sünder geboren, und meine Mutter hat mich in Sünden empfangen.",
			"8": "<span> 8 </span> \tSiehe, dir gefällt Wahrheit, die im Verborgenen liegt, und im Geheimen tust du mir Weisheit kund.",
			"9": "<span> 9 </span> \tEntsündige mich mit Ysop, daß ich rein werde; wasche mich, daß ich schneeweiß werde.",
			"10": "<span> 10 </span> \tLaß mich hören Freude und Wonne, daß die Gebeine fröhlich werden, die du zerschlagen hast.",
			"11": "<span> 11 </span> \tVerbirg dein Antlitz vor meinen Sünden, und tilge alle meine Missetat.",
			"12": "<span> 12 </span> \tSchaffe in mir, Gott, ein reines Herz, und gib mir einen neuen, beständigen Geist.",
			"13": "<span> 13 </span> \tVerwirf mich nicht von deinem Angesicht, und nimm deinen heiligen Geist nicht von mir.",
			"14": "<span> 14 </span> \tErfreue mich wieder mit deiner Hilfe, und mit einem willigen Geist rüste mich aus.",
			"15": "<span> 15 </span> \tIch will die Übertreter deine Wege lehren, daß sich die Sünder zu dir bekehren.",
			"16": "<span> 16 </span> \tErrette mich von Blutschuld, Gott, der du mein Gott und Heiland bist, daß meine Zunge deine Gerechtigkeit rühme.",
			"17": "<span> 17 </span> \tHerr, tu meine Lippen auf, daß mein Mund deinen Ruhm verkündige.",
			"18": "<span> 18 </span> \tDenn Schlachtopfer willst du nicht, ich wollte sie dir sonst geben, und Brandopfer gefallen dir nicht.",
			"19": "<span> 19 </span> \tDie Opfer, die Gott gefallen, sind ein geängsteter Geist, ein geängstetes, zerschlagenes Herz wirst du, Gott, nicht verachten.\r\n[20]\tTu wohl an Zion nach deiner Gnade, baue die Mauern zu Jerusalem.\r\n[21]\tDann werden dir gefallen rechte Opfer, Brandopfer und Ganzopfer; dann wird man Stiere auf deinem Altar opfern."
		},
		"52": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DAVIDS, VORZUSINGEN,",
			"2": "<span> 2 </span> \tALS DOËG, DER EDOMITER, KAM UND ZEIGTE ES SAUL AN UND SPRACH: DAVID IST IN AHIMELECHS HAUS GEKOMMEN.",
			"3": "<span> 3 </span> \tWas rühmst du dich der Bosheit, du Tyrann, da doch Gottes Güte noch täglich währt?",
			"4": "<span> 4 </span> \tDeine Zunge trachtet nach Schaden wie ein scharfes Schermesser, du Betrüger!",
			"5": "<span> 5 </span> \tDu liebst das Böse mehr als das Gute und redest lieber Falsches als Rechtes. SELA .",
			"6": "<span> 6 </span> \tDu redest gern alles, was zum Verderben dient, mit falscher Zunge.",
			"7": "<span> 7 </span> \tDarum wird dich auch Gott für immer zerstören, dich zerschlagen und aus deinem Zelte reißen und aus dem Lande der Lebendigen ausrotten. SELA.",
			"8": "<span> 8 </span> \tUnd die Gerechten werden es sehen und sich fürchten und werden seiner lachen:",
			"9": "<span> 9 </span> \t«Siehe, das ist der Mann, der nicht auf Gott sein Vertrauen setzte, sondern verließ sich auf seinen großen Reichtum und war mächtig, Schaden zu tun.»\r\n[10]\tIch aber werde bleiben wie ein grünender Ölbaum im Hause Gottes; ich verlasse mich auf Gottes Güte immer und ewig.\r\n[11]\tIch will dir danken ewiglich, denn du hast es getan. Ich will harren auf deinen Namen vor deinen Heiligen, denn du bist gütig."
		},
		"53": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DAVIDS, VORZUSINGEN, ZUM REIGENTANZ.",
			"2": "<span> 2 </span> \tDie Toren sprechen in ihrem Herzen: «Es ist kein Gott.» Sie taugen nichts; ihr Freveln ist ein Greuel; da ist keiner, der Gutes tut.",
			"3": "<span> 3 </span> \tGott schaut vom Himmel auf die Menschenkinder, daß er sehe, ob jemand klug sei und nach Gott frage.",
			"4": "<span> 4 </span> \tAber sie sind alle abgefallen und allesamt verdorben; da ist keiner, der Gutes tut, auch nicht einer.",
			"5": "<span> 5 </span> \tWollen denn die Übeltäter sich nichts sagen lassen, die mein Volk fressen, daß sie sich nähren, Gott aber rufen sie nicht an?",
			"6": "<span> 6 </span> \tSie fürchten sich da, wo nichts zu fürchten ist; doch Gott zerstreut die Gebeine derer, die dich bedrängen. Du machst sie zuschanden, denn Gott hat sie verworfen.\r\n[7]\tAch daß die Hilfe aus Zion über Israel käme und Gott sein gefangenes Volk erlöste! So würde Jakob sich freuen und Israel fröhlich sein."
		},
		"54": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DAVIDS, VORZUSINGEN, BEIM SAITENSPIEL,",
			"2": "<span> 2 </span> \tALS DIE MÄNNER VON SIPH KAMEN UND ZU SAUL SPRACHEN: DAVID HÄLT SICH BEI UNS VERBORGEN.",
			"3": "<span> 3 </span> \tHilf mir, Gott, durch deinen Namen und schaffe mir Recht durch deine Kraft.",
			"4": "<span> 4 </span> \tGott, erhöre mein Gebet, vernimm die Rede meines Mundes.",
			"5": "<span> 5 </span> \tDenn Stolze erheben sich gegen mich, und Gewalttäter trachten mir nach dem Leben; sie haben Gott nicht vor Augen. SELA .",
			"6": "<span> 6 </span> \tSiehe, Gott steht mir bei, der Herr erhält mein Leben.",
			"7": "<span> 7 </span> \tEr wird die Bosheit meinen Feinden vergelten. Vertilge sie um deiner Treue willen!\r\n[8]\tSo will ich dir ein Freudenopfer bringen und deinen Namen, HERR, preisen, daß er so tröstlich ist.\r\n[9]\tDenn du errettest mich aus aller meiner Not, daß mein Auge auf meine Feinde herabsieht."
		},
		"55": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DAVIDS, VORZUSINGEN, BEIM SAITENSPIEL.",
			"2": "<span> 2 </span> \tGott, höre mein Gebet und verbirg dich nicht vor meinem Flehen.",
			"3": "<span> 3 </span> \tMerke auf mich und erhöre mich, wie ich so ruhelos klage und heule,",
			"4": "<span> 4 </span> \tda der Feind so schreit und der Gottlose mich bedrängt; denn sie wollen Unheil über mich bringen und sind mir heftig gram.",
			"5": "<span> 5 </span> \tMein Herz ängstet sich in meinem Leibe, und Todesfurcht ist auf mich gefallen.",
			"6": "<span> 6 </span> \tFurcht und Zittern ist über mich gekommen, und Grauen hat mich überfallen.",
			"7": "<span> 7 </span> \tIch sprach: O hätte ich Flügel wie Tauben, daß ich wegflöge und Ruhe fände!",
			"8": "<span> 8 </span> \tSiehe, so wollte ich in die Ferne fliehen und in der Wüste bleiben. SELA.",
			"9": "<span> 9 </span> \tIch wollte eilen, daß ich entrinne vor dem Sturmwind und Wetter.",
			"10": "<span> 10 </span> \tMache ihre Zunge uneins, Herr, und verwirre sie; denn ich sehe Frevel und Hader in der Stadt.",
			"11": "<span> 11 </span> \tDas geht Tag und Nacht um auf ihren Mauern, und Mühsal und Unheil ist drinnen.",
			"12": "<span> 12 </span> \tVerderbnis regiert darin, Lügen und Trügen weicht nicht aus ihren Gassen.",
			"13": "<span> 13 </span> \tWenn mein Feind mich schmähte, wollte ich es ertragen; wenn einer, der mich haßt, groß tut wider mich, wollte ich mich vor ihm verbergen.",
			"14": "<span> 14 </span> \tAber nun bist du es, mein Gefährte, mein Freund und mein Vertrauter,",
			"15": "<span> 15 </span> \tdie wir freundlich miteinander waren, die wir in Gottes Haus gingen inmitten der Menge!",
			"16": "<span> 16 </span> \tDer Tod übereile sie, daß sie lebendig zu den Toten fahren; denn es ist lauter Bosheit bei ihnen.",
			"17": "<span> 17 </span> \tIch aber will zu Gott rufen, und der HERR wird mir helfen.",
			"18": "<span> 18 </span> \tAbends und morgens und mittags will ich klagen und heulen; so wird er meine Stimme hören.",
			"19": "<span> 19 </span> \tEr erlöst mich von denen, die an mich wollen, und schafft mir Ruhe; denn ihrer sind viele wider mich.",
			"20": "<span> 20 </span> \tGott wird hören und sie demütigen, der allewege bleibet. SELA. Denn sie werden nicht anders und wollen Gott nicht fürchten.",
			"21": "<span> 21 </span> \tSie legen ihre Hände an ihre Freunde und entheiligen ihren Bund.",
			"22": "<span> 22 </span> \tIhr Mund ist glatter als Butter, und haben doch Krieg im Sinn; ihre Worte sind linder als Öl und sind doch gezückte Schwerter.",
			"23": "<span> 23 </span> \tWirf dein Anliegen auf den HERRN; der wird dich versorgen und wird den Gerechten in Ewigkeit nicht wanken lassen.\r\n[24]\tUnd du, Gott, wirst sie hinunterstoßen in die tiefe Grube. Die Blutgierigen und Falschen werden ihr Leben nicht bis zur Hälfte bringen. Ich aber hoffe auf dich."
		},
		"56": {
			"1": "<span>1</span> \tEIN GÜLDENES KLEINOD DAVIDS, VORZUSINGEN, NACH DER WEISE «DIE STUMME TAUBE UNTER DEN FREMDEN», ALS IHN DIE PHILISTER IN GATH ERGRIFFEN HATTEN.",
			"2": "<span> 2 </span> \tGott, sei mir gnädig, denn Menschen stellen mir nach; täglich bekämpfen und bedrängen sie mich.",
			"3": "<span> 3 </span> \tMeine Feinde stellen mir täglich nach; denn viele kämpfen gegen mich voll Hochmut.",
			"4": "<span> 4 </span> \tWenn ich mich fürchte, so hoffe ich auf dich.",
			"5": "<span> 5 </span> \tIch will Gottes Wort rühmen; auf Gott will ich hoffen und mich nicht fürchten. Was können mir Menschen tun?",
			"6": "<span> 6 </span> \tTäglich fechten sie meine Sache an; alle ihre Gedanken suchen mir Böses zu tun.",
			"7": "<span> 7 </span> \tSie rotten sich zusammen, sie lauern und haben acht auf meine Schritte, wie sie mir nach dem Leben trachten.",
			"8": "<span> 8 </span> \tSollten sie mit ihrer Bosheit entrinnen? Gott, stoß diese Leute ohne alle Gnade hinunter!",
			"9": "<span> 9 </span> \tZähle die Tage meiner Flucht, sammle meine Tränen in deinen Krug; ohne Zweifel, du zählst sie.",
			"10": "<span> 10 </span> \tDann werden meine Feinde zurückweichen, wenn ich dich anrufe. Das weiß ich, daß du mein Gott bist.",
			"11": "<span> 11 </span> \tIch will rühmen Gottes Wort; ich will rühmen des HERRN Wort.",
			"12": "<span> 12 </span> \tAuf Gott hoffe ich und fürchte mich nicht; was können mir Menschen tun?",
			"13": "<span> 13 </span> \tIch habe dir, Gott, gelobt, daß ich dir danken will.\r\n[14]\tDenn du hast mich vom Tode errettet, meine Füße vom Gleiten, daß ich wandeln kann vor Gott im Licht der Lebendigen."
		},
		"57": {
			"1": "<span>1</span> \tEIN GÜLDENES KLEINOD DAVIDS, VORZUSINGEN, NACH DER WEISE «VERTILGE NICHT», ALS ER VOR SAUL IN DIE HÖHLE FLOH.",
			"2": "<span> 2 </span> \tSei mir gnädig, Gott, sei mir gnädig! Denn auf dich traut meine Seele, und unter dem Schatten deiner Flügel habe ich Zuflucht, bis das Unglück vorübergehe.",
			"3": "<span> 3 </span> \tIch rufe zu Gott, dem Allerhöchsten, zu Gott, der meine Sache zum guten Ende führt.",
			"4": "<span> 4 </span> \tEr sende vom Himmel und helfe mir von der Schmähung dessen, der mir nachstellt. SELA. Gott sende seine Güte und Treue.",
			"5": "<span> 5 </span> \tIch liege mitten unter Löwen; verzehrende Flammen sind die Menschen, ihre Zähne sind Spieße und Pfeile und ihre Zungen scharfe Schwerter.",
			"6": "<span> 6 </span> \tErhebe dich, Gott, über den Himmel und deine Herrlichkeit über alle Welt!",
			"7": "<span> 7 </span> \tSie haben meinen Schritten ein Netz gestellt und meine Seele gebeugt; sie haben vor mir eine Grube gegraben – und fallen doch selbst hinein. SELA.",
			"8": "<span> 8 </span> \tMein Herz ist bereit, Gott, mein Herz ist bereit, daß ich singe und lobe.",
			"9": "<span> 9 </span> \tWach auf, meine Seele, wach auf, Psalter und Harfe, ich will das Morgenrot wecken!",
			"10": "<span> 10 </span> \tHerr, ich will dir danken unter den Völkern, ich will dir lobsingen unter den Leuten.",
			"11": "<span> 11 </span> \tDenn deine Güte reicht, so weit der Himmel ist, und deine Wahrheit, so weit die Wolken gehen.\r\n[12]\tErhebe dich, Gott, über den Himmel und deine Herrlichkeit über alle Welt!"
		},
		"58": {
			"1": "<span>1</span> \tEIN GÜLDENES KLEINOD DAVIDS, VORZUSINGEN, NACH DER WEISE «VERTILGE NICHT».",
			"2": "<span> 2 </span> \tSprecht ihr in Wahrheit Recht, ihr Mächtigen? Richtet ihr in Gerechtigkeit die Menschenkinder?",
			"3": "<span> 3 </span> \tNein, mutwillig tut ihr Unrecht im Lande, und eure Hände treiben Frevel.",
			"4": "<span> 4 </span> \tDie Gottlosen sind abtrünnig vom Mutterschoß an, die Lügner gehen irre von Mutterleib an.",
			"5": "<span> 5 </span> \tSie sind voller Gift wie eine giftige Schlange, wie eine taube Otter, die ihr Ohr verschließt,",
			"6": "<span> 6 </span> \tdaß sie nicht höre die Stimme des Zauberers, des Beschwörers, der gut beschwören kann.",
			"7": "<span> 7 </span> \tGott, zerbrich ihnen die Zähne im Maul, zerschlage, HERR, das Gebiß der jungen Löwen!",
			"8": "<span> 8 </span> \tSie werden vergehen wie Wasser, das verrinnt. Zielen sie mit ihren Pfeilen, so werden sie ihnen zerbrechen.",
			"9": "<span> 9 </span> \tSie gehen dahin, wie Wachs zerfließt, wie eine Fehlgeburt, die die Sonne nicht sieht.",
			"10": "<span> 10 </span> \tEhe eure Töpfe das Dornfeuer spüren, reißt alles der brennende Zorn hinweg.",
			"11": "<span> 11 </span> \tDer Gerechte wird sich freuen, wenn er solche Vergeltung sieht, und wird seine Füße baden in des Gottlosen Blut;\r\n[12]\tund die Leute werden sagen: Ja, der Gerechte empfängt seine Frucht, ja, Gott ist noch Richter auf Erden."
		},
		"59": {
			"1": "<span>1</span> \tEIN GÜLDENES KLEINOD DAVIDS, VORZUSINGEN, NACH DER WEISE «VERTILGE NICHT», ALS SAUL HINSANDTE UND SEIN HAUS BEWACHEN LIESS, UM IHN ZU TÖTEN.",
			"2": "<span> 2 </span> \tErrette mich, mein Gott, von meinen Feinden und schütze mich vor meinen Widersachern.",
			"3": "<span> 3 </span> \tErrette mich von den Übeltätern und hilf mir von den Blutgierigen!",
			"4": "<span> 4 </span> \tDenn siehe, HERR, sie lauern mir auf; Starke rotten sich wider mich zusammen ohne meine Schuld und Missetat.",
			"5": "<span> 5 </span> \tIch habe nichts verschuldet; sie aber laufen herzu und machen sich bereit. Erwache, komm herbei und sieh darein!",
			"6": "<span> 6 </span> \tDu, HERR, Gott Zebaoth, Gott Israels, wache auf und suche heim alle Völker! Sei keinem von ihnen gnädig, die so verwegene Übeltäter sind. SELA.",
			"7": "<span> 7 </span> \tJeden Abend kommen sie wieder, heulen wie die Hunde und laufen in der Stadt umher.",
			"8": "<span> 8 </span> \tSiehe, sie geifern mit ihrem Maul; Schwerter sind auf ihren Lippen: «Wer sollte es hören?»",
			"9": "<span> 9 </span> \tAber du, HERR, wirst ihrer lachen und aller Völker spotten.",
			"10": "<span> 10 </span> \tMeine Stärke, zu dir will ich mich halten; denn Gott ist mein Schutz.",
			"11": "<span> 11 </span> \tGott erzeigt mir reichlich seine Güte, Gott läßt mich auf meine Feinde herabsehen.",
			"12": "<span> 12 </span> \tBringe sie nicht um, daß es mein Volk nicht vergesse; zerstreue sie aber mit deiner Macht, Herr, unser Schild, und stoß sie hinunter!",
			"13": "<span> 13 </span> \tDas Wort ihrer Lippen ist nichts als Sünde; darum sollen sie sich fangen in ihrer Hoffart mit all ihren Flüchen und Lügen.",
			"14": "<span> 14 </span> \tVertilge sie ohne alle Gnade, vertilge sie, daß sie nicht mehr da sind! Laß sie innewerden, daß Gott Herrscher ist in Jakob bis an die Enden der Erde. SELA.",
			"15": "<span> 15 </span> \tJeden Abend kommen sie wieder, heulen wie die Hunde und laufen in der Stadt umher.",
			"16": "<span> 16 </span> \tSie laufen hin und her nach Speise und murren, wenn sie nicht satt werden.",
			"17": "<span> 17 </span> \tIch aber will von deiner Macht singen und des Morgens rühmen deine Güte; denn du bist mir Schutz und Zuflucht in meiner Not.\r\n[18]\tMeine Stärke, dir will ich lobsingen; denn Gott ist mein Schutz, mein gnädiger Gott."
		},
		"60": {
			"1": "<span>1</span> \tEIN GÜLDENES KLEINOD DAVIDS, VORZUSINGEN, NACH DER WEISE «LILIE DES ZEUGNISSES», ZUR BELEHRUNG,",
			"2": "<span> 2 </span> \tALS ER MIT DEN ARAMÄERN VON MESOPOTAMIEN UND MIT DEN ARAMÄERN VON ZOBA KRIEG FÜHRTE; ALS JOAB UMKEHRTE UND DIE EDOMITER IM SALZTAL SCHLUG, ZWÖLFTAUSEND MANN.",
			"3": "<span> 3 </span> \tGott, der du uns verstoßen und zerstreut hast und zornig warst, tröste uns wieder;",
			"4": "<span> 4 </span> \tder du die Erde erschüttert und zerrissen hast, heile ihre Risse; denn sie wankt.",
			"5": "<span> 5 </span> \tDu ließest deinem Volk Hartes widerfahren, du gabst uns einen Wein zu trinken, daß wir taumelten.",
			"6": "<span> 6 </span> \tDu hast doch ein Zeichen gegeben denen, die dich fürchten, damit sie fliehen können vor dem Bogen. SELA.",
			"7": "<span> 7 </span> \tDaß deine Freunde errettet werden, dazu hilf mit deiner Rechten und erhöre uns!",
			"8": "<span> 8 </span> \tGott hat in seinem Heiligtum geredet: Ich will frohlocken; ich will Sichem verteilen und das Tal Sukkoth ausmessen;",
			"9": "<span> 9 </span> \tGilead ist mein, mein ist Manasse, Ephraim ist der Schutz meines Hauptes, Juda ist mein Zepter.",
			"10": "<span> 10 </span> \tMoab ist mein Waschbecken, meinen Schuh werfe ich auf Edom, Philisterland, jauchze mir zu!",
			"11": "<span> 11 </span> \tWer wird mich führen in die feste Stadt? Wer geleitet mich nach Edom?",
			"12": "<span> 12 </span> \tWirst du es nicht tun, Gott, der du uns verstoßen hast und ziehst nicht aus, Gott, mit unserm Heer?\r\n[13]\tSchaff uns Beistand in der Not; denn Menschenhilfe ist nichts nütze.\r\n[14]\tMit Gott wollen wir Taten tun. Er wird unsre Feinde niedertreten."
		},
		"61": {
			"1": "<span>1</span> \tVON DAVID, VORZUSINGEN, BEIM SAITENSPIEL.",
			"2": "<span> 2 </span> \tHöre, Gott, mein Schreien und merke auf mein Gebet!",
			"3": "<span> 3 </span> \tVom Ende der Erde rufe ich zu dir; denn mein Herz ist in Angst; du wollest mich führen auf einen hohen Felsen.",
			"4": "<span> 4 </span> \tDenn du bist meine Zuversicht, ein starker Turm vor meinen Feinden.",
			"5": "<span> 5 </span> \tLaß mich wohnen in deinem Zelte ewiglich und Zuflucht haben unter deinen Fittichen. SELA.",
			"6": "<span> 6 </span> \tDenn du, Gott, hörst mein Gelübde und gibst mir teil am Erbe derer, die deinen Namen fürchten.",
			"7": "<span> 7 </span> \tDu wollest dem König langes Leben geben, daß seine Jahre währen für und für,",
			"8": "<span> 8 </span> \tdaß er immer throne vor Gott. Laß Güte und Treue ihn behüten!\r\n[9]\tSo will ich deinem Namen lobsingen ewiglich, daß ich meine Gelübde erfülle täglich."
		},
		"62": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN, FÜR JEDUTHUN.",
			"2": "<span> 2 </span> \tMeine Seele ist stille zu Gott, der mir hilft.",
			"3": "<span> 3 </span> \tDenn er ist mein Fels, meine Hilfe, mein Schutz, daß ich gewiß nicht fallen werde.",
			"4": "<span> 4 </span> \tWie lange stellt ihr alle einem nach, wollt alle ihn morden, als wäre er eine hangende Wand und eine rissige Mauer?",
			"5": "<span> 5 </span> \tSie denken nur, wie sie ihn stürzen, haben Gefallen am Lügen; mit dem Munde segnen sie, aber im Herzen fluchen sie. SELA.",
			"6": "<span> 6 </span> \tAber sei nur stille zu Gott, meine Seele; denn er ist meine Hoffnung.",
			"7": "<span> 7 </span> \tEr ist mein Fels, meine Hilfe und mein Schutz, daß ich nicht fallen werde.",
			"8": "<span> 8 </span> \tBei Gott ist mein Heil und meine Ehre, der Fels meiner Stärke, meine Zuversicht ist bei Gott.",
			"9": "<span> 9 </span> \tHoffet auf ihn allezeit, liebe Leute, schüttet euer Herz vor ihm aus; Gott ist unsre Zuversicht. SELA.",
			"10": "<span> 10 </span> \tAber Menschen sind ja nichts, große Leute täuschen auch; sie wiegen weniger als nichts, soviel ihrer sind.",
			"11": "<span> 11 </span> \tVerlaßt euch nicht auf Gewalt und setzt auf Raub nicht eitle Hoffnung; fällt euch Reichtum zu, so hängt euer Herz nicht daran.",
			"12": "<span> 12 </span> \tEines hat Gott geredet, ein Zweifaches habe ich gehört: Gott allein ist mächtig,\r\n[13]\tund du, Herr, bist gnädig; denn du vergiltst einem jeden, wie er's verdient hat."
		},
		"63": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, ALS ER IN DER WÜSTE JUDA WAR.",
			"2": "<span> 2 </span> \tGott, du bist mein Gott, den ich suche. Es dürstet meine Seele nach dir, mein ganzer Mensch verlangt nach dir aus trockenem, dürrem Land, wo kein Wasser ist.",
			"3": "<span> 3 </span> \tSo schaue ich aus nach dir in deinem Heiligtum, wollte gerne sehen deine Macht und Herrlichkeit.",
			"4": "<span> 4 </span> \tDenn deine Güte ist besser als Leben; meine Lippen preisen dich.",
			"5": "<span> 5 </span> \tSo will ich dich loben mein Leben lang und meine Hände in deinem Namen aufheben.",
			"6": "<span> 6 </span> \tDas ist meines Herzens Freude und Wonne, wenn ich dich mit fröhlichem Munde loben kann;",
			"7": "<span> 7 </span> \twenn ich mich zu Bette lege, so denke ich an dich, wenn ich wach liege, sinne ich über dich nach.",
			"8": "<span> 8 </span> \tDenn du bist mein Helfer, und unter dem Schatten deiner Flügel frohlocke ich.",
			"9": "<span> 9 </span> \tMeine Seele hängt an dir; deine rechte Hand hält mich.",
			"10": "<span> 10 </span> \tSie aber trachten mir nach dem Leben, mich zu verderben; sie werden in die Tiefen der Erde hinunterfahren.",
			"11": "<span> 11 </span> \tSie werden dem Schwert dahingegeben und den Schakalen zur Beute werden.\r\n[12]\tAber der König freut sich in Gott. Wer bei ihm schwört, der darf sich rühmen; denn die Lügenmäuler sollen verstopft werden."
		},
		"64": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tHöre, Gott, meine Stimme in meiner Klage, behüte mein Leben vor dem schrecklichen Feinde.",
			"3": "<span> 3 </span> \tVerbirg mich vor den Anschlägen der Bösen, vor dem Toben der Übeltäter,",
			"4": "<span> 4 </span> \tdie ihre Zunge schärfen wie ein Schwert, mit ihren giftigen Worten zielen wie mit Pfeilen,",
			"5": "<span> 5 </span> \tdaß sie heimlich schießen auf den Frommen; plötzlich schießen sie auf ihn ohne alle Scheu.",
			"6": "<span> 6 </span> \tSie verstehen sich auf ihre bösen Anschläge und reden davon, wie sie Stricke legen wollen, und sprechen: Wer kann sie sehen?",
			"7": "<span> 7 </span> \tSie haben Böses im Sinn und halten's geheim, sind verschlagen und haben Ränke im Herzen.",
			"8": "<span> 8 </span> \tDa trifft sie Gott mit dem Pfeil, plötzlich sind sie zu Boden geschlagen.",
			"9": "<span> 9 </span> \tIhre eigene Zunge bringt sie zu Fall, daß ihrer spotten wird, wer sie siehet.",
			"10": "<span> 10 </span> \tUnd alle Menschen werden sich fürchten und sagen: Das hat Gott getan! und erkennen, daß es sein Werk ist.\r\n[11]\tDie Gerechten werden sich des HERRN freuen und auf ihn trauen, und alle frommen Herzen werden sich seiner rühmen."
		},
		"65": {
			"1": "<span>1</span> \tEIN PSALMLIED DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tGott, man lobt dich in der Stille zu Zion, und dir hält man Gelübde.",
			"3": "<span> 3 </span> \tDu erhörst Gebet; darum kommt alles Fleisch zu dir.",
			"4": "<span> 4 </span> \tUnsre Missetat drückt uns hart; du wollest unsre Sünde vergeben.",
			"5": "<span> 5 </span> \tWohl dem, den du erwählst und zu dir lässest, daß er in deinen Vorhöfen wohne; der hat reichen Trost von deinem Hause, deinem heiligen Tempel.",
			"6": "<span> 6 </span> \tErhöre uns nach der wunderbaren Gerechtigkeit, Gott, unser Heil, der du bist die Zuversicht aller auf Erden und fern am Meer;",
			"7": "<span> 7 </span> \tder du die Berge festsetzest in deiner Kraft und gerüstet bist mit Macht;",
			"8": "<span> 8 </span> \tder du stillst das Brausen des Meeres, das Brausen seiner Wellen und das Toben der Völker,",
			"9": "<span> 9 </span> \tdaß sich entsetzen, die an den Enden wohnen, vor deinen Zeichen. Du machst fröhlich, was da lebet im Osten wie im Westen.",
			"10": "<span> 10 </span> \tDu suchst das Land heim und bewässerst es und machst es sehr reich; Gottes Brünnlein hat Wasser die Fülle. Du lässest ihr Getreide gut geraten; denn so baust du das Land.",
			"11": "<span> 11 </span> \tDu tränkst seine Furchen und feuchtest seine Schollen; mit Regen machst du es weich und segnest sein Gewächs.",
			"12": "<span> 12 </span> \tDu krönst das Jahr mit deinem Gut, und deine Fußtapfen triefen von Segen.",
			"13": "<span> 13 </span> \tEs triefen auch die Auen in der Steppe, und die Hügel sind erfüllt mit Jubel.\r\n[14]\tDie Anger sind voller Schafe, und die Auen stehen dick mit Korn, daß man jauchzet und singet."
		},
		"66": {
			"1": "<span>1</span> \tEIN PSALMLIED, VORZUSINGEN. Jauchzet Gott, alle Lande!",
			"2": "<span> 2 </span> \tLobsinget zur Ehre seines Namens; rühmet ihn herrlich!",
			"3": "<span> 3 </span> \tSprecht zu Gott: Wie wunderbar sind deine Werke! Deine Feinde müssen sich beugen vor deiner großen Macht.",
			"4": "<span> 4 </span> \tAlles Land bete dich an und lobsinge dir, lobsinge deinem Namen. SELA.",
			"5": "<span> 5 </span> \tKommt her und sehet an die Werke Gottes, der so wunderbar ist in seinem Tun an den Menschenkindern.",
			"6": "<span> 6 </span> \tEr verwandelte das Meer in trockenes Land, sie konnten zu Fuß durch den Strom gehen. Darum freuen wir uns seiner.",
			"7": "<span> 7 </span> \tEr herrscht mit seiner Gewalt ewiglich, seine Augen schauen auf die Völker. Die Abtrünnigen können sich nicht erheben. SELA.",
			"8": "<span> 8 </span> \tLobet, ihr Völker, unsern Gott, laßt seinen Ruhm weit erschallen,",
			"9": "<span> 9 </span> \tder unsre Seelen am Leben erhält und läßt unsere Füße nicht gleiten.",
			"10": "<span> 10 </span> \tDenn, Gott, du hast uns geprüft und geläutert, wie das Silber geläutert wird;",
			"11": "<span> 11 </span> \tdu hast uns in den Turm werfen lassen, du hast auf unsern Rücken eine Last gelegt,",
			"12": "<span> 12 </span> \tdu hast Menschen über unser Haupt kommen lassen, wir sind in Feuer und Wasser geraten. Aber du hast uns herausgeführt und uns erquickt.",
			"13": "<span> 13 </span> \tDarum will ich in dein Haus gehen mit Brandopfern und dir meine Gelübde erfüllen,",
			"14": "<span> 14 </span> \twie ich meine Lippen aufgetan habe und mein Mund geredet hat in meiner Not.",
			"15": "<span> 15 </span> \tIch will dir Brandopfer bringen von fetten Schafen mit dem Opferrauch von Widdern; ich will opfern Rinder mit Böcken. SELA.",
			"16": "<span> 16 </span> \tKommt her, höret zu, alle, die ihr Gott fürchtet; ich will erzählen, was er an mir getan hat.",
			"17": "<span> 17 </span> \tZu ihm rief ich mit meinem Munde und pries ihn mit meiner Zunge.",
			"18": "<span> 18 </span> \tWenn ich Unrechtes vorgehabt hätte in meinem Herzen, so hätte der Herr nicht gehört.",
			"19": "<span> 19 </span> \tAber Gott hat mich erhört und gemerkt auf mein Flehen.",
			"20": "<span> 20 </span> \tGelobt sei Gott, der mein Gebet nicht verwirft noch seine Güte von mir wendet."
		},
		"67": {
			"1": "<span>1</span> \tEIN PSALMLIED, VORZUSINGEN, BEIM SAITENSPIEL.",
			"2": "<span> 2 </span> \tGott sei uns gnädig und segne uns, er lasse uns sein Antlitz leuchten, SELA .",
			"3": "<span> 3 </span> \tdaß man auf Erden erkenne seinen Weg, unter allen Heiden sein Heil.",
			"4": "<span> 4 </span> \tEs danken dir, Gott, die Völker, es danken dir alle Völker.",
			"5": "<span> 5 </span> \tDie Völker freuen sich und jauchzen, daß du die Menschen recht richtest und regierst die Völker auf Erden. SELA.",
			"6": "<span> 6 </span> \tEs danken dir, Gott, die Völker, es danken dir alle Völker.",
			"7": "<span> 7 </span> \tDas Land gibt sein Gewächs; es segne uns Gott, unser Gott!\r\n[8]\tEs segne uns Gott, und alle Welt fürchte ihn!"
		},
		"68": {
			"1": "<span>1</span> \tEIN PSALMLIED DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tGott steht auf; so werden seine Feinde zerstreut, und die ihn hassen, fliehen vor ihm.",
			"3": "<span> 3 </span> \tWie Rauch verweht, so verwehen sie; wie Wachs zerschmilzt vor dem Feuer, so kommen die Gottlosen um vor Gott.",
			"4": "<span> 4 </span> \tDie Gerechten aber freuen sich und sind fröhlich vor Gott und freuen sich von Herzen.",
			"5": "<span> 5 </span> \tSinget Gott, lobsinget seinem Namen! Macht Bahn dem, der durch die Wüste einherfährt; er heißt HERR. Freuet euch vor ihm!",
			"6": "<span> 6 </span> \tEin Vater der Waisen und ein Helfer der Witwen ist Gott in seiner heiligen Wohnung,",
			"7": "<span> 7 </span> \tein Gott, der die Einsamen nach Hause bringt, der die Gefangenen herausführt, daß es ihnen wohlgehe; aber die Abtrünnigen läßt er bleiben in dürrem Lande.",
			"8": "<span> 8 </span> \tGott, als du vor deinem Volk herzogst, als du einhergingst in der Wüste, SELA.",
			"9": "<span> 9 </span> \tda bebte die Erde, und die Himmel troffen vor Gott – am Sinai -, vor Gott, dem Gott Israels.",
			"10": "<span> 10 </span> \tDu gabst, Gott, einen gnädigen Regen, und dein Erbe, das dürre war, erquicktest du,",
			"11": "<span> 11 </span> \tdaß deine Herde darin wohnen konnte. Gott, du labst die Elenden in deiner Güte.",
			"12": "<span> 12 </span> \tDer Herr gibt ein Wort – der Freudenbotinnen ist eine große Schar - :",
			"13": "<span> 13 </span> \tDie Könige der Heerscharen fliehen, sie fliehen, und die Frauen teilen die Beute aus.",
			"14": "<span> 14 </span> \tWenn ihr zu Felde liegt, glänzt es wie Flügel der Tauben, die wie Silber und Gold schimmern.",
			"15": "<span> 15 </span> \tAls der Allmächtige dort Könige zerstreute, damals fiel Schnee auf dem Salmon.",
			"16": "<span> 16 </span> \tEin Gottesberg ist Basans Gebirge, ein Gebirge, reich an Gipfeln, ist Basans Gebirge.",
			"17": "<span> 17 </span> \tWas seht ihr scheel, ihr Berge, ihr Gipfel, auf den Berg, wo es Gott gefällt zu wohnen? Ja, dort wird der HERR immerdar wohnen.",
			"18": "<span> 18 </span> \tGottes Wagen sind vieltausendmal tausend; der Herr zieht ein ins Heiligtum vom Sinai her.",
			"19": "<span> 19 </span> \tDu bist aufgefahren zur Höhe und führtest Gefangne gefangen; du hast Gaben empfangen unter den Menschen; auch die Abtrünnigen müssen sich, Gott, vor dir bücken.",
			"20": "<span> 20 </span> \tGelobt sei der Herr täglich. Gott legt uns eine Last auf, aber er hilft uns auch. SELA.",
			"21": "<span> 21 </span> \tWir haben einen Gott, der da hilft, und den HERRN, der vom Tode errettet.",
			"22": "<span> 22 </span> \tJa, Gott wird den Kopf seiner Feinde zerschmettern, den Schädel der Gottlosen, die da fortfahren in ihrer Sünde.",
			"23": "<span> 23 </span> \tDer Herr hat gesagt: Aus Basan will ich sie wieder holen, aus der Tiefe des Meeres will ich sie holen,",
			"24": "<span> 24 </span> \tdaß du deinen Fuß im Blut der Feinde badest und deine Hunde es lecken.",
			"25": "<span> 25 </span> \tMan sieht, Gott, wie du einherziehst, wie du, mein Gott und König, einherziehst im Heiligtum.",
			"26": "<span> 26 </span> \tDie Sänger gehen voran, am Ende die Spielleute, in der Mitte die Jungfrauen, die da Pauken schlagen.",
			"27": "<span> 27 </span> \t«Lobet Gott in den Versammlungen, den HERRN, die ihr von Israel herstammt.»",
			"28": "<span> 28 </span> \tBenjamin, der Jüngste, geht ihnen voran, die Fürsten Judas mit ihren Scharen, die Fürsten Sebulons, die Fürsten Naphthalis.",
			"29": "<span> 29 </span> \tEntbiete, Gott, deine Macht, die Macht, Gott, die du an uns bewiesen hast",
			"30": "<span> 30 </span> \tvon deinem Tempel her; um Jerusalems willen werden dir Könige Geschenke bringen.",
			"31": "<span> 31 </span> \tBedrohe das Tier im Schilf, die Rotte der Mächtigen, die Gebieter der Völker; tritt nieder, die das Silber lieb haben, zerstreue die Völker, die gerne Krieg führen.",
			"32": "<span> 32 </span> \tAus Ägypten werden Gesandte kommen; Mohrenland wird seine Hände ausstrecken zu Gott.",
			"33": "<span> 33 </span> \tIhr Königreiche auf Erden, singet Gott, lobsinget dem Herrn! SELA.",
			"34": "<span> 34 </span> \tEr fährt einher durch die Himmel, die von Anbeginn sind. Siehe, er läßt seine Stimme erschallen, eine gewaltige Stimme.",
			"35": "<span> 35 </span> \tGebt Gott die Macht! Seine Herrlichkeit ist über Israel und seine Macht in den Wolken.\r\n[36]\tWundersam ist Gott in seinem Heiligtum; er ist Israels Gott. Er wird dem Volke Macht und Kraft geben. Gelobt sei Gott!"
		},
		"69": {
			"1": "<span>1</span> \tVON DAVID, VORZUSINGEN, NACH DER WEISE «LILIEN».",
			"2": "<span> 2 </span> \tGott, hilf mir! Denn das Wasser geht mir bis an die Kehle.",
			"3": "<span> 3 </span> \tIch versinke in tiefem Schlamm, wo kein Grund ist; ich bin in tiefe Wasser geraten, und die Flut will mich ersäufen.",
			"4": "<span> 4 </span> \tIch habe mich müde geschrien, mein Hals ist heiser. Meine Augen sind trübe geworden, weil ich so lange harren muß auf meinen Gott.",
			"5": "<span> 5 </span> \tDie mich ohne Grund hassen, sind mehr, als ich Haare auf dem Haupte habe. Die mir zu Unrecht feind sind und mich verderben wollen, sind mächtig. Ich soll zurückgeben, was ich nicht geraubt habe.",
			"6": "<span> 6 </span> \tGott, du kennst meine Torheit, und meine Schuld ist dir nicht verborgen.",
			"7": "<span> 7 </span> \tLaß an mir nicht zuschanden werden, die deiner harren, Herr, HERR Zebaoth! Laß an mir nicht schamrot werden, die dich suchen, Gott Israels!",
			"8": "<span> 8 </span> \tDenn um deinetwillen trage ich Schmach, mein Angesicht ist voller Schande.",
			"9": "<span> 9 </span> \tIch bin fremd geworden meinen Brüdern und unbekannt den Kindern meiner Mutter;",
			"10": "<span> 10 </span> \tdenn der Eifer um dein Haus hat mich gefressen, und die Schmähungen derer, die dich schmähen, sind auf mich gefallen.",
			"11": "<span> 11 </span> \tIch weine bitterlich und faste, und man spottet meiner dazu.",
			"12": "<span> 12 </span> \tIch habe einen Sack angezogen, aber sie treiben ihren Spott mit mir.",
			"13": "<span> 13 </span> \tDie im Tor sitzen, schwatzen von mir, und beim Zechen singt man von mir.",
			"14": "<span> 14 </span> \tIch aber bete zu dir, HERR, zur Zeit der Gnade; Gott, nach deiner großen Güte erhöre mich mit deiner treuen Hilfe.",
			"15": "<span> 15 </span> \tErrette mich aus dem Schlamm, daß ich nicht versinke, daß ich errettet werde von denen, die mich hassen, und aus den tiefen Wassern;",
			"16": "<span> 16 </span> \tdaß mich die Flut nicht ersäufe und die Tiefe nicht verschlinge und das Loch des Brunnens sich nicht über mir schließe.",
			"17": "<span> 17 </span> \tErhöre mich, HERR, denn deine Güte ist tröstlich; wende dich zu mir nach deiner großen Barmherzigkeit",
			"18": "<span> 18 </span> \tund verbirg dein Angesicht nicht vor deinem Knechte, denn mir ist angst; erhöre mich eilends.",
			"19": "<span> 19 </span> \tNahe dich zu meiner Seele und erlöse sie, erlöse mich um meiner Feinde willen.",
			"20": "<span> 20 </span> \tDu kennst meine Schmach, meine Schande und Scham; meine Widersacher sind dir alle vor Augen.",
			"21": "<span> 21 </span> \tDie Schmach bricht mir mein Herz und macht mich krank. Ich warte, ob jemand Mitleid habe, aber da ist niemand, und auf Tröster, aber ich finde keine.",
			"22": "<span> 22 </span> \tSie geben mir Galle zu essen und Essig zu trinken für meinen Durst.",
			"23": "<span> 23 </span> \tIhr Tisch werde ihnen zur Falle, zur Vergeltung und zum Strick.",
			"24": "<span> 24 </span> \tIhre Augen sollen finster werden, daß sie nicht sehen, und ihre Hüften laß immerfort wanken.",
			"25": "<span> 25 </span> \tGieß deine Ungnade über sie aus, und dein grimmiger Zorn ergreife sie.",
			"26": "<span> 26 </span> \tIhre Wohnstatt soll verwüstet werden, und niemand wohne in ihren Zelten.",
			"27": "<span> 27 </span> \tDenn sie verfolgen, den du geschlagen hast, und reden gern von dem Schmerz dessen, den du hart getroffen hast.",
			"28": "<span> 28 </span> \tLaß sie aus einer Schuld in die andre fallen, daß sie nicht kommen zu deiner Gerechtigkeit.",
			"29": "<span> 29 </span> \tTilge sie aus dem Buch des Lebens, daß sie nicht geschrieben stehen bei den Gerechten.",
			"30": "<span> 30 </span> \tIch aber bin elend und voller Schmerzen. Gott, deine Hilfe schütze mich!",
			"31": "<span> 31 </span> \tIch will den Namen Gottes loben mit einem Lied und will ihn hoch ehren mit Dank.",
			"32": "<span> 32 </span> \tDas wird dem HERRN besser gefallen als ein Stier, der Hörner und Klauen hat.",
			"33": "<span> 33 </span> \tDie Elenden sehen es und freuen sich, und die Gott suchen, denen wird das Herz aufleben.",
			"34": "<span> 34 </span> \tDenn der HERR hört die Armen und verachtet seine Gefangenen nicht.",
			"35": "<span> 35 </span> \tEs lobe ihn Himmel und Erde, die Meere mit allem, was sich darin regt.",
			"36": "<span> 36 </span> \tDenn Gott wird Zion helfen und die Städte Judas bauen, daß man dort wohne und sie besitze.\r\n[37]\tUnd die Kinder seiner Knechte werden sie erben, und die seinen Namen lieben, werden darin bleiben."
		},
		"70": {
			"1": "<span>1</span> \tVON DAVID, VORZUSINGEN, ZUM GEDENKOPFER.",
			"2": "<span> 2 </span> \tEile, Gott, mich zu erretten, HERR, mir zu helfen!",
			"3": "<span> 3 </span> \tEs sollen sich schämen und zuschanden werden, die mir nach dem Leben trachten; sie sollen zurückweichen und zum Spott werden, die mir Übles wünschen;",
			"4": "<span> 4 </span> \tsie sollen umkehren um ihrer Schande willen, die über mich schreien: Da, da!",
			"5": "<span> 5 </span> \tLaß deiner sich freuen und fröhlich sein alle, die nach dir fragen; und die dein Heil lieben, laß allewege sagen: Hoch gelobt sei Gott!\r\n[6]\tIch aber bin elend und arm; Gott, eile zu mir! Du bist mein Helfer und Erretter; HERR, säume nicht!"
		},
		"71": {
			"1": "<span>1</span> \tHERR, ich traue auf dich, laß mich nimmermehr zuschanden werden.",
			"2": "<span> 2 </span> \tErrette mich durch deine Gerechtigkeit und hilf mir heraus, neige deine Ohren zu mir und hilf mir!",
			"3": "<span> 3 </span> \tSei mir ein starker Hort, zu dem ich immer fliehen kann, der du zugesagt hast, mir zu helfen; denn du bist mein Fels und meine Burg.",
			"4": "<span> 4 </span> \tMein Gott, hilf mir aus der Hand des Gottlosen, aus der Hand des Ungerechten und Tyrannen.",
			"5": "<span> 5 </span> \tDenn du bist meine Zuversicht, HERR, mein Gott, meine Hoffnung von meiner Jugend an.",
			"6": "<span> 6 </span> \tAuf dich habe ich mich verlassen vom Mutterleib an; du hast mich aus meiner Mutter Leibe gezogen. Dich rühme ich immerdar.",
			"7": "<span> 7 </span> \tIch bin für viele wie ein Zeichen; aber du bist meine starke Zuversicht.",
			"8": "<span> 8 </span> \tLaß meinen Mund deines Ruhmes und deines Preises voll sein täglich.",
			"9": "<span> 9 </span> \tVerwirf mich nicht in meinem Alter, verlaß mich nicht, wenn ich schwach werde.",
			"10": "<span> 10 </span> \tDenn meine Feinde reden über mich, und die auf mich lauern, beraten sich miteinander",
			"11": "<span> 11 </span> \tund sprechen: Gott hat ihn verlassen; jagt ihm nach und ergreift ihn, denn da ist kein Erretter!",
			"12": "<span> 12 </span> \tGott, sei nicht ferne von mir; mein Gott, eile, mir zu helfen!",
			"13": "<span> 13 </span> \tSchämen sollen sich und umkommen, die meiner Seele feind sind; mit Schimpf und Schande sollen überschüttet werden, die mein Unglück suchen.",
			"14": "<span> 14 </span> \tIch aber will immer harren und mehren all deinen Ruhm.",
			"15": "<span> 15 </span> \tMein Mund soll verkündigen deine Gerechtigkeit, täglich deine Wohltaten, die ich nicht zählen kann.",
			"16": "<span> 16 </span> \tIch gehe einher in der Kraft Gottes des HERRN; ich preise deine Gerechtigkeit allein.",
			"17": "<span> 17 </span> \tGott, du hast mich von Jugend auf gelehrt, und noch jetzt verkündige ich deine Wunder.",
			"18": "<span> 18 </span> \tAuch im Alter, Gott, verlaß mich nicht, und wenn ich grau werde, bis ich deine Macht verkündige Kindeskindern und deine Kraft allen, die noch kommen sollen.",
			"19": "<span> 19 </span> \tGott, deine Gerechtigkeit reicht bis zum Himmel; der du große Dinge tust, Gott, wer ist dir gleich?",
			"20": "<span> 20 </span> \tDu lässest mich erfahren viele und große Angst und machst mich wieder lebendig und holst mich wieder herauf aus den Tiefen der Erde.",
			"21": "<span> 21 </span> \tDu machst mich sehr groß und tröstest mich wieder.",
			"22": "<span> 22 </span> \tSo will auch ich dir danken mit Saitenspiel für deine Treue, mein Gott; ich will dir zur Harfe lobsingen, du Heiliger Israels.",
			"23": "<span> 23 </span> \tMeine Lippen und meine Seele, die du erlöst hast, sollen fröhlich sein und dir lobsingen.",
			"24": "<span> 24 </span> \tAuch meine Zunge soll täglich reden von deiner Gerechtigkeit; denn zu Schmach und Schande werden, die mein Unglück suchen."
		},
		"72": {
			"1": "<span>1</span> \tVON SALOMO. Gott, gib dein Gericht dem König und deine Gerechtigkeit dem Königssohn,",
			"2": "<span> 2 </span> \tdaß er dein Volk richte mit Gerechtigkeit und deine Elenden rette.",
			"3": "<span> 3 </span> \tLaß die Berge Frieden bringen für das Volk und die Hügel Gerechtigkeit.",
			"4": "<span> 4 </span> \tEr soll den Elenden im Volk Recht schaffen und den Armen helfen und die Bedränger zermalmen.",
			"5": "<span> 5 </span> \tEr soll leben, solange die Sonne scheint und solange der Mond währt, von Geschlecht zu Geschlecht.",
			"6": "<span> 6 </span> \tEr soll herabfahren wie der Regen auf die Aue, wie die Tropfen, die das Land feuchten.",
			"7": "<span> 7 </span> \tZu seinen Zeiten soll blühen die Gerechtigkeit und großer Friede sein, bis der Mond nicht mehr ist.",
			"8": "<span> 8 </span> \tEr soll herrschen von einem Meer bis ans andere, und von dem Strom bis zu den Enden der Erde.",
			"9": "<span> 9 </span> \tVor ihm sollen sich neigen die Söhne der Wüste, und seine Feinde sollen Staub lecken.",
			"10": "<span> 10 </span> \tDie Könige von Tarsis und auf den Inseln sollen Geschenke bringen, die Könige aus Saba und Scheba sollen Gaben senden.",
			"11": "<span> 11 </span> \tAlle Könige sollen vor ihm niederfallen und alle Völker ihm dienen.",
			"12": "<span> 12 </span> \tDenn er wird den Armen erretten, der um Hilfe schreit, und den Elenden, der keinen Helfer hat.",
			"13": "<span> 13 </span> \tEr wird gnädig sein den Geringen und Armen, und den Armen wird er helfen.",
			"14": "<span> 14 </span> \tEr wird sie aus Bedrückung und Frevel erlösen, und ihr Blut ist wert geachtet vor ihm.",
			"15": "<span> 15 </span> \tEr soll leben, und man soll ihm geben vom Gold aus Saba. Man soll immerdar für ihn beten und ihn täglich segnen.",
			"16": "<span> 16 </span> \tVoll stehe das Getreide im Land bis oben auf den Bergen; wie am Libanon rausche seine Frucht. In den Städten sollen sie grünen wie das Gras auf Erden.",
			"17": "<span> 17 </span> \tSein Name bleibe ewiglich; solange die Sonne währt, blühe sein Name. Und durch ihn sollen gesegnet sein alle Völker, und sie werden ihn preisen.",
			"18": "<span> 18 </span> \tGelobt sei Gott der HERR, der Gott Israels, der allein Wunder tut!",
			"19": "<span> 19 </span> \tGelobt sei sein herrlicher Name ewiglich, und alle Lande sollen seiner Ehre voll werden! Amen! Amen!",
			"20": "<span> 20 </span> \tZU ENDE SIND DIE GEBETE DAVIDS, DES SOHNES ISAIS."
		},
		"73": {
			"1": "<span>1</span> \tEIN PSALM ASAPHS. Gott ist dennoch Israels Trost für alle, die reines Herzens sind.",
			"2": "<span> 2 </span> \tIch aber wäre fast gestrauchelt mit meinen Füßen; mein Tritt wäre beinahe geglitten.",
			"3": "<span> 3 </span> \tDenn ich ereiferte mich über die Ruhmredigen, als ich sah, daß es den Gottlosen so gut ging.",
			"4": "<span> 4 </span> \tDenn für sie gibt es keine Qualen, gesund und feist ist ihr Leib.",
			"5": "<span> 5 </span> \tSie sind nicht in Mühsal wie sonst die Leute und werden nicht wie andere Menschen geplagt.",
			"6": "<span> 6 </span> \tDarum prangen sie in Hoffart und hüllen sich in Frevel.",
			"7": "<span> 7 </span> \tSie brüsten sich wie ein fetter Wanst, sie tun, was ihnen einfällt.",
			"8": "<span> 8 </span> \tSie achten alles für nichts und reden böse, sie reden und lästern hoch her.",
			"9": "<span> 9 </span> \tWas sie reden, das soll vom Himmel herab geredet sein; was sie sagen, das soll gelten auf Erden.",
			"10": "<span> 10 </span> \tDarum fällt ihnen der Pöbel zu und läuft ihnen zu in Haufen wie Wasser.",
			"11": "<span> 11 </span> \tSie sprechen: Wie sollte Gott es wissen? Wie sollte der Höchste etwas merken?",
			"12": "<span> 12 </span> \tSiehe, das sind die Gottlosen; die sind glücklich in der Welt und werden reich.",
			"13": "<span> 13 </span> \tSoll es denn umsonst sein, daß ich mein Herz rein hielt und meine Hände in Unschuld wasche?",
			"14": "<span> 14 </span> \tIch bin doch täglich geplagt, und meine Züchtigung ist alle Morgen da.",
			"15": "<span> 15 </span> \tHätte ich gedacht: Ich will reden wie sie, siehe, dann hätte ich das Geschlecht deiner Kinder verleugnet.",
			"16": "<span> 16 </span> \tSo sann ich nach, ob ich's begreifen könnte, aber es war mir zu schwer,",
			"17": "<span> 17 </span> \tbis ich ging in das Heiligtum Gottes und merkte auf ihr Ende.",
			"18": "<span> 18 </span> \tJa, du stellst sie auf schlüpfrigen Grund und stürzest sie zu Boden.",
			"19": "<span> 19 </span> \tWie werden sie so plötzlich zunichte! Sie gehen unter und nehmen ein Ende mit Schrecken.",
			"20": "<span> 20 </span> \tWie ein Traum verschmäht wird, wenn man erwacht, so verschmähst du, Herr, ihr Bild, wenn du dich erhebst.",
			"21": "<span> 21 </span> \tAls es mir wehe tat im Herzen und mich stach in meinen Nieren,",
			"22": "<span> 22 </span> \tda war ich ein Narr und wußte nichts, ich war wie ein Tier vor dir.",
			"23": "<span> 23 </span> \tDennoch bleibe ich stets an dir; denn du hältst mich bei meiner rechten Hand,",
			"24": "<span> 24 </span> \tdu leitest mich nach deinem Rat und nimmst mich am Ende mit Ehren an.",
			"25": "<span> 25 </span> \tWenn ich nur dich habe, so frage ich nichts nach Himmel und Erde.",
			"26": "<span> 26 </span> \tWenn mir gleich Leib und Seele verschmachtet, so bist du doch, Gott, allezeit meines Herzens Trost und mein Teil.",
			"27": "<span> 27 </span> \tDenn siehe, die von dir weichen, werden umkommen; du bringst um alle, die dir die Treue brechen.",
			"28": "<span> 28 </span> \tAber das ist meine Freude, daß ich mich zu Gott halte und meine Zuversicht setze auf Gott den HERRN, daß ich verkündige all dein Tun."
		},
		"74": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG ASAPHS. Gott, warum verstößest du uns für immer und bist so zornig über die Schafe deiner Weide?",
			"2": "<span> 2 </span> \tGedenke an deine Gemeinde, die du vorzeiten erworben und dir zum Erbteil erlöst hast, an den Berg Zion, auf dem du wohnest.",
			"3": "<span> 3 </span> \tRichte doch deine Schritte zu dem, was so lange wüste liegt. Der Feind hat alles verheert im Heiligtum.",
			"4": "<span> 4 </span> \tDeine Widersacher brüllen in deinem Hause und stellen ihre Zeichen darin auf.",
			"5": "<span> 5 </span> \tHoch sieht man Äxte sich heben wie im Dickicht des Waldes.",
			"6": "<span> 6 </span> \tSie zerschlagen all sein Schnitzwerk mit Beilen und Hacken.",
			"7": "<span> 7 </span> \tSie verbrennen dein Heiligtum, bis auf den Grund entweihen sie die Wohnung deines Namens.",
			"8": "<span> 8 </span> \tSie sprechen in ihrem Herzen: Laßt uns sie ganz unterdrücken! Sie verbrennen alle Gotteshäuser im Lande.",
			"9": "<span> 9 </span> \tUnsere Zeichen sehen wir nicht, kein Prophet ist mehr da, und keiner ist bei uns, der etwas weiß.",
			"10": "<span> 10 </span> \tAch, Gott, wie lange soll der Widersacher noch schmähen und der Feind deinen Namen immerfort lästern?",
			"11": "<span> 11 </span> \tWarum ziehst du deine Hand zurück? Nimm deine Rechte aus dem Gewand und mach ein Ende!",
			"12": "<span> 12 </span> \tGott ist ja mein König von alters her, der alle Hilfe tut, die auf Erden geschieht.",
			"13": "<span> 13 </span> \tDu hast das Meer gespalten durch deine Kraft, zerschmettert die Köpfe der Drachen im Meer.",
			"14": "<span> 14 </span> \tDu hast dem Leviathan die Köpfe zerschlagen und ihn zum Fraß gegeben dem wilden Getier.",
			"15": "<span> 15 </span> \tDu hast Quellen und Bäche hervorbrechen lassen und ließest starke Ströme versiegen.",
			"16": "<span> 16 </span> \tDein ist der Tag und dein ist die Nacht; du hast Gestirn und Sonne die Bahn gegeben.",
			"17": "<span> 17 </span> \tDu hast dem Land seine Grenze gesetzt; Sommer und Winter hast du gemacht.",
			"18": "<span> 18 </span> \tSo gedenke doch, HERR, wie der Feind schmäht und ein törichtes Volk deinen Namen lästert.",
			"19": "<span> 19 </span> \tGib deine Taube nicht den Tieren preis; das Leben deiner Elenden vergiß nicht für immer.",
			"20": "<span> 20 </span> \tGedenke an den Bund; denn die dunklen Winkel des Landes sind voll Frevel.",
			"21": "<span> 21 </span> \tLaß den Geringen nicht beschämt davongehen, laß die Armen und Elenden rühmen deinen Namen.",
			"22": "<span> 22 </span> \tMach dich auf, Gott, und führe deine Sache; denk an die Schmach, die dir täglich von den Toren widerfährt.",
			"23": "<span> 23 </span> \tVergiß nicht das Geschrei deiner Feinde; das Toben deiner Widersacher wird je länger, je größer."
		},
		"75": {
			"1": "<span>1</span> \tEIN PSALMLIED ASAPHS, VORZUSINGEN, NACH DER WEISE «VERTILGE NICHT».",
			"2": "<span> 2 </span> \tWir danken dir, Gott, wir danken dir und verkündigen deine Wunder, daß dein Name so nahe ist.",
			"3": "<span> 3 </span> \t«Wenn meine Zeit gekommen ist, werde ich recht richten.",
			"4": "<span> 4 </span> \tDie Erde mag wanken und alle, die darauf wohnen, aber ich halte ihre Säulen fest.» SELA.",
			"5": "<span> 5 </span> \tIch sprach zu den Ruhmredigen: Rühmt euch nicht so! und zu den Gottlosen: Pochet nicht auf Gewalt!",
			"6": "<span> 6 </span> \tPocht nicht so hoch auf eure Gewalt, redet nicht so halsstarrig!",
			"7": "<span> 7 </span> \tDenn es kommt nicht vom Aufgang und nicht vom Niedergang, nicht von der Wüste und nicht von den Bergen,",
			"8": "<span> 8 </span> \tsondern Gott ist Richter, der diesen erniedrigt und jenen erhöht.",
			"9": "<span> 9 </span> \tDenn der HERR hat einen Becher in der Hand, mit starkem Wein voll eingeschenkt. Er schenkt daraus ein, und die Gottlosen auf Erden müssen alle trinken und sogar die Hefe schlürfen.",
			"10": "<span> 10 </span> \tIch aber will verkündigen ewiglich und lobsingen dem Gott Jakobs:\r\n[11]\tEr wird alle Gewalt der Gottlosen zerbrechen, daß die Gewalt des Gerechten erhöht werde."
		},
		"76": {
			"1": "<span>1</span> \tEIN PSALMLIED ASAPHS, VORZUSINGEN, BEIM SAITENSPIEL.",
			"2": "<span> 2 </span> \tGott ist in Juda bekannt, in Israel ist sein Name herrlich.",
			"3": "<span> 3 </span> \tSo erstand in Salem sein Zelt und seine Wohnung in Zion.",
			"4": "<span> 4 </span> \tDort zerbricht er die Pfeile des Bogens, Schild, Schwert und Streitmacht. SELA.",
			"5": "<span> 5 </span> \tDu bist herrlicher und mächtiger als die ewigen Berge.",
			"6": "<span> 6 </span> \tBeraubt sind die Stolzen und in Schlaf gesunken, und allen Kriegern versagen die Hände.",
			"7": "<span> 7 </span> \tVon deinem Schelten, Gott Jakobs, sinken in Schlaf Roß und Wagen.",
			"8": "<span> 8 </span> \tFurchtbar bist du! Wer kann vor dir bestehen, wenn du zürnest?",
			"9": "<span> 9 </span> \tWenn du das Urteil lässest hören vom Himmel, erschrickt das Erdreich und wird still,",
			"10": "<span> 10 </span> \twenn Gott sich aufmacht zu richten, daß er helfe allen Elenden auf Erden. SELA.",
			"11": "<span> 11 </span> \tWenn Menschen wider dich wüten, bringt es dir Ehre; und wenn sie noch mehr wüten, bist du auch noch gerüstet.",
			"12": "<span> 12 </span> \tTut Gelübde dem HERRN, eurem Gott, und haltet sie! Alle, die ihr um ihn her seid, bringt Geschenke dem Furchtbaren,\r\n[13]\tder den Fürsten den Mut nimmt und furchtbar ist unter den Königen auf Erden."
		},
		"77": {
			"1": "<span>1</span> \tEIN PSALM ASAPHS, VORZUSINGEN, FÜR JEDUTHUN.",
			"2": "<span> 2 </span> \tIch rufe zu Gott und schreie um Hilfe, zu Gott rufe ich, und er erhört mich.",
			"3": "<span> 3 </span> \tIn der Zeit meiner Not suche ich den Herrn; meine Hand ist des Nachts ausgereckt und läßt nicht ab; denn meine Seele will sich nicht trösten lassen.",
			"4": "<span> 4 </span> \tIch denke an Gott – und bin betrübt; ich sinne nach – und mein Herz ist in Ängsten. SELA.",
			"5": "<span> 5 </span> \tMeine Augen hältst du, daß sie wachen müssen; ich bin so voll Unruhe, daß ich nicht reden kann.",
			"6": "<span> 6 </span> \tIch gedenke der alten Zeit, der vergangenen Jahre.",
			"7": "<span> 7 </span> \tIch denke und sinne des Nachts und rede mit meinem Herzen, mein Geist muß forschen.",
			"8": "<span> 8 </span> \tWird denn der Herr auf ewig verstoßen und keine Gnade mehr erweisen?",
			"9": "<span> 9 </span> \tIst's denn ganz und gar aus mit seiner Güte, und hat die Verheißung für immer ein Ende?",
			"10": "<span> 10 </span> \tHat Gott vergessen, gnädig zu sein, oder sein Erbarmen im Zorn verschlossen? SELA.",
			"11": "<span> 11 </span> \tIch sprach: Darunter leide ich, daß die rechte Hand des Höchsten sich so ändern kann.",
			"12": "<span> 12 </span> \tDarum denke ich an die Taten des HERRN, ja, ich denke an deine früheren Wunder",
			"13": "<span> 13 </span> \tund sinne über alle deine Werke und denke deinen Taten nach.",
			"14": "<span> 14 </span> \tGott, dein Weg ist heilig. Wo ist ein so mächtiger Gott, wie du, Gott, bist?",
			"15": "<span> 15 </span> \tDu bist der Gott, der Wunder tut, du hast deine Macht bewiesen unter den Völkern.",
			"16": "<span> 16 </span> \tDu hast dein Volk erlöst mit Macht, die Kinder Jakobs und Josephs. SELA.",
			"17": "<span> 17 </span> \tDie Wasser sahen dich, Gott, die Wasser sahen dich und ängstigten sich, ja, die Tiefen tobten.",
			"18": "<span> 18 </span> \tWasser ergossen sich aus dem Gewölk, die Wolken donnerten, und deine Pfeile fuhren einher.",
			"19": "<span> 19 </span> \tDein Donner rollte, Blitze erhellten den Erdkreis, die Erde erbebte und wankte.",
			"20": "<span> 20 </span> \tDein Weg ging durch das Meer und dein Pfad durch große Wasser; doch niemand sah deine Spur.\r\n[21]\tDu führtest dein Volk wie eine Herde durch die Hand des Mose und Aaron."
		},
		"78": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG ASAPHS. Höre, mein Volk, meine Unterweisung, neiget eure Ohren zu der Rede meines Mundes!",
			"2": "<span> 2 </span> \tIch will meinen Mund auftun zu einem Spruch und Geschichten verkünden aus alter Zeit.",
			"3": "<span> 3 </span> \tWas wir gehört haben und wissen und unsre Väter uns erzählt haben,",
			"4": "<span> 4 </span> \tdas wollen wir nicht verschweigen ihren Kindern; wir verkündigen dem kommenden Geschlecht den Ruhm des HERRN und seine Macht und seine Wunder, die er getan hat.",
			"5": "<span> 5 </span> \tEr richtete ein Zeugnis auf in Jakob und gab ein Gesetz in Israel und gebot unsern Vätern, es ihre Kinder zu lehren,",
			"6": "<span> 6 </span> \tdamit es die Nachkommen lernten, die Kinder, die noch geboren würden; die sollten aufstehen und es auch ihren Kindern verkündigen,",
			"7": "<span> 7 </span> \tdaß sie setzten auf Gott ihre Hoffnung und nicht vergäßen die Taten Gottes, sondern seine Gebote hielten",
			"8": "<span> 8 </span> \tund nicht würden wie ihre Väter, ein abtrünniges und ungehorsames Geschlecht, dessen Herz nicht fest war und dessen Geist sich nicht treu an Gott hielt,",
			"9": "<span> 9 </span> \twie die Söhne Ephraims, die den Bogen führten, abfielen zur Zeit des Streits;",
			"10": "<span> 10 </span> \tsie hielten den Bund Gottes nicht und wollten nicht in seinem Gesetz wandeln",
			"11": "<span> 11 </span> \tund vergaßen seine Taten und seine Wunder, die er ihnen erwiesen hatte.",
			"12": "<span> 12 </span> \tVor ihren Vätern tat er Wunder in Ägyptenland, im Gefilde von Zoan.",
			"13": "<span> 13 </span> \tEr zerteilte das Meer und ließ sie hindurchziehen und stellte das Wasser fest wie eine Mauer.",
			"14": "<span> 14 </span> \tEr leitete sie am Tage mit einer Wolke und die ganze Nacht mit einem hellen Feuer.",
			"15": "<span> 15 </span> \tEr spaltete die Felsen in der Wüste und tränkte sie mit Wasser in Fülle;",
			"16": "<span> 16 </span> \ter ließ Bäche aus den Felsen kommen, daß sie hinabflossen wie Wasserströme.",
			"17": "<span> 17 </span> \tDennoch sündigten sie weiter wider ihn und empörten sich in der Wüste gegen den Höchsten;",
			"18": "<span> 18 </span> \tsie versuchten Gott in ihrem Herzen, als sie Speise forderten für ihr Gelüste,",
			"19": "<span> 19 </span> \tund redeten wider Gott und sprachen: Kann Gott wohl einen Tisch bereiten in der Wüste?",
			"20": "<span> 20 </span> \tSiehe, er hat wohl den Felsen geschlagen, daß Wasser strömten und Bäche sich ergossen; kann er aber auch Brot geben und seinem Volk Fleisch verschaffen?",
			"21": "<span> 21 </span> \tDa der HERR das hörte, entbrannte er im Grimm, und Feuer brach aus in Jakob, und Zorn kam über Israel,",
			"22": "<span> 22 </span> \tweil sie nicht glaubten an Gott und nicht hofften auf seine Hilfe.",
			"23": "<span> 23 </span> \tUnd er gebot den Wolken droben und tat auf die Türen des Himmels",
			"24": "<span> 24 </span> \tund ließ Manna auf sie regnen zur Speise und gab ihnen Himmelsbrot.",
			"25": "<span> 25 </span> \tBrot der Engel aßen sie alle, er sandte ihnen Speise in Fülle.",
			"26": "<span> 26 </span> \tEr ließ wehen den Ostwind unter dem Himmel und erregte durch seine Stärke den Südwind",
			"27": "<span> 27 </span> \tund ließ Fleisch auf sie regnen wie Staub und Vögel wie Sand am Meer;",
			"28": "<span> 28 </span> \tmitten in das Lager fielen sie ein, rings um seine Wohnung her.",
			"29": "<span> 29 </span> \tDa aßen sie und wurden sehr satt; und was sie verlangten, gewährte er ihnen.",
			"30": "<span> 30 </span> \tSie hatten ihr Verlangen noch nicht gestillt, ihre Speise war noch in ihrem Munde,",
			"31": "<span> 31 </span> \tda kam der Zorn Gottes über sie und brachte ihre Vornehmsten um und schlug die Besten in Israel nieder.",
			"32": "<span> 32 </span> \tZu dem allen sündigten sie noch mehr und glaubten nicht an seine Wunder.",
			"33": "<span> 33 </span> \tDarum ließ er ihre Tage dahinschwinden ins Nichts und ihre Jahre in Schrecken.",
			"34": "<span> 34 </span> \tWenn er den Tod unter sie brachte, suchten sie Gott und fragten wieder nach ihm",
			"35": "<span> 35 </span> \tund dachten daran, daß Gott ihr Hort ist und Gott, der Höchste, ihr Erlöser.",
			"36": "<span> 36 </span> \tDoch betrogen sie ihn mit ihrem Munde und belogen ihn mit ihrer Zunge.",
			"37": "<span> 37 </span> \tIhr Herz hing nicht fest an ihm, und sie hielten nicht treu an seinem Bunde.",
			"38": "<span> 38 </span> \tEr aber war barmherzig und vergab die Schuld und vertilgte sie nicht und wandte oft seinen Zorn ab und ließ nicht seinen ganzen Grimm an ihnen aus.",
			"39": "<span> 39 </span> \tDenn er dachte daran, daß sie Fleisch sind, ein Hauch, der dahinfährt und nicht wiederkommt.",
			"40": "<span> 40 </span> \tWie oft trotzten sie ihm in der Wüste und betrübten ihn in der Einöde!",
			"41": "<span> 41 </span> \tSie versuchten Gott immer wieder und kränkten den Heiligen Israels.",
			"42": "<span> 42 </span> \tSie dachten nicht an die Taten seiner Hand, an den Tag, als er sie erlöste von den Feinden,",
			"43": "<span> 43 </span> \twie er seine Zeichen in Ägypten getan hatte und seine Wunder im Lande Zoan;",
			"44": "<span> 44 </span> \tals er ihre Ströme in Blut verwandelte, daß sie aus ihren Flüssen nicht trinken konnten;",
			"45": "<span> 45 </span> \tals er Ungeziefer unter sie schickte, das sie fraß, und Frösche, die ihnen Verderben brachten,",
			"46": "<span> 46 </span> \tund ihr Gewächs den Raupen gab und ihre Saat den Heuschrecken;",
			"47": "<span> 47 </span> \tals er ihre Weinstöcke mit Hagel schlug und ihre Maulbeerbäume mit Schloßen;",
			"48": "<span> 48 </span> \tals er ihr Vieh preisgab dem Hagel und ihre Herden dem Wetterstrahl;",
			"49": "<span> 49 </span> \tals er die Glut seines Zornes unter sie sandte, Grimm und Wut und Drangsal, eine Schar Verderben bringender Engel;",
			"50": "<span> 50 </span> \tals er seinem Zorn freien Lauf ließ und ihre Seele vor dem Tode nicht bewahrte und ihr Leben preisgab der Pest;",
			"51": "<span> 51 </span> \tals er alle Erstgeburt in Ägypten schlug, die Erstlinge ihrer Kraft in den Zelten Hams.",
			"52": "<span> 52 </span> \tEr ließ sein Volk ausziehen wie Schafe und führte sie wie eine Herde in der Wüste;",
			"53": "<span> 53 </span> \tund er leitete sie sicher, daß sie sich nicht fürchteten; aber ihre Feinde bedeckte das Meer.",
			"54": "<span> 54 </span> \tEr brachte sie zu seinem heiligen Lande, zu diesem Berge, den seine Rechte erworben hat,",
			"55": "<span> 55 </span> \tund vertrieb vor ihnen her die Völker und verteilte ihr Land als Erbe und ließ in ihren Zelten die Stämme Israels wohnen.",
			"56": "<span> 56 </span> \tAber sie versuchten Gott und trotzten dem Höchsten und hielten seine Gebote nicht;",
			"57": "<span> 57 </span> \tsie wichen zurück und waren treulos wie ihre Väter und versagten wie ein schlaffer Bogen;",
			"58": "<span> 58 </span> \tsie erzürnten ihn mit ihren Höhen und reizten ihn zum Zorn mit ihren Götzen.",
			"59": "<span> 59 </span> \tAls das Gott hörte, entbrannte sein Grimm, und er verwarf Israel so sehr,",
			"60": "<span> 60 </span> \tdaß er seine Wohnung in Silo dahingab, das Zelt, in dem er unter Menschen wohnte;",
			"61": "<span> 61 </span> \ter gab seine Macht in Gefangenschaft und seine Herrlichkeit in die Hand des Feindes;",
			"62": "<span> 62 </span> \ter übergab sein Volk dem Schwert und ergrimmte über sein Erbe.",
			"63": "<span> 63 </span> \tIhre junge Mannschaft fraß das Feuer, und ihre Jungfrauen mußten ungefreit bleiben.",
			"64": "<span> 64 </span> \tIhre Priester fielen durchs Schwert, und die Witwen konnten die Toten nicht beweinen.",
			"65": "<span> 65 </span> \tDa erwachte der Herr wie ein Schlafender, wie ein Starker, der beim Wein fröhlich war,",
			"66": "<span> 66 </span> \tund schlug seine Feinde hinten und hängte ihnen ewige Schande an.",
			"67": "<span> 67 </span> \tEr verwarf das Zelt Josephs und erwählte nicht den Stamm Ephraim,",
			"68": "<span> 68 </span> \tsondern erwählte den Stamm Juda, den Berg Zion, den er liebhat.",
			"69": "<span> 69 </span> \tEr baute sein Heiligtum wie Himmelshöhen, wie die Erde, die er gegründet hat für immer,",
			"70": "<span> 70 </span> \tund erwählte seinen Knecht David und nahm ihn von den Schafhürden;",
			"71": "<span> 71 </span> \tvon den säugenden Schafen holte er ihn, daß er sein Volk Jakob weide und sein Erbe Israel.",
			"72": "<span> 72 </span> \tUnd er weidete sie mit aller Treue und leitete sie mit kluger Hand."
		},
		"79": {
			"1": "<span>1</span> \tEIN PSALM ASAPHS. Gott, es sind Heiden in dein Erbe eingefallen; die haben deinen heiligen Tempel entweiht und aus Jerusalem einen Steinhaufen gemacht.",
			"2": "<span> 2 </span> \tSie haben die Leichname deiner Knechte den Vögeln unter dem Himmel zu fressen gegeben und das Fleisch deiner Heiligen den Tieren im Lande.",
			"3": "<span> 3 </span> \tSie haben ihr Blut vergossen um Jerusalem her wie Wasser, und da war niemand, der sie begrub.",
			"4": "<span> 4 </span> \tWir sind bei unsern Nachbarn eine Schmach geworden, zu Spott und Hohn bei denen, die um uns her sind.",
			"5": "<span> 5 </span> \tHERR, wie lange willst du so sehr zürnen und deinen Eifer brennen lassen wie Feuer?",
			"6": "<span> 6 </span> \tSchütte deinen Grimm auf die Völker, die dich nicht kennen, und auf die Königreiche, die deinen Namen nicht anrufen.",
			"7": "<span> 7 </span> \tDenn sie haben Jakob gefressen und seine Stätte verwüstet.",
			"8": "<span> 8 </span> \tRechne uns die Schuld der Väter nicht an, erbarme dich unser bald, denn wir sind sehr elend.",
			"9": "<span> 9 </span> \tHilf du uns, Gott, unser Helfer, um deines Namens Ehre willen! Errette uns und vergib uns unsre Sünden um deines Namens willen!",
			"10": "<span> 10 </span> \tWarum läßt du die Heiden sagen: «Wo ist nun ihr Gott?» Laß unter den Heiden vor unsern Augen kundwerden die Vergeltung für das Blut deiner Knechte, das vergossen ist.",
			"11": "<span> 11 </span> \tLaß vor dich kommen das Seufzen der Gefangenen; durch deinen starken Arm erhalte die Kinder des Todes",
			"12": "<span> 12 </span> \tund vergilt unsern Nachbarn siebenfach auf ihr Haupt ihr Schmähen, mit dem sie dich, Herr, geschmäht haben.",
			"13": "<span> 13 </span> \tWir aber, dein Volk, die Schafe deiner Weide, danken dir ewiglich und verkünden deinen Ruhm für und für."
		},
		"80": {
			"1": "<span>1</span> \tEIN PSALM ASAPHS, VORZUSINGEN, NACH DER WEISE «LILIEN DES ZEUGNISSES».",
			"2": "<span> 2 </span> \tDu Hirte Israels, höre, der du Joseph hütest wie Schafe! Erscheine, der du thronst über den Cherubim,",
			"3": "<span> 3 </span> \tvor Ephraim, Benjamin und Manasse! Erwecke deine Kraft und komm uns zu Hilfe!",
			"4": "<span> 4 </span> \tGott, tröste uns wieder und laß leuchten dein Antlitz, so genesen wir.",
			"5": "<span> 5 </span> \tHERR, Gott Zebaoth, wie lange willst du zürnen, während dein Volk zu dir betet?",
			"6": "<span> 6 </span> \tDu speisest sie mit Tränenbrot und tränkest sie mit einem großen Krug voll Tränen.",
			"7": "<span> 7 </span> \tDu lässest unsre Nachbarn sich um uns streiten, und unsre Feinde verspotten uns.",
			"8": "<span> 8 </span> \tGott Zebaoth, tröste uns wieder; laß leuchten dein Antlitz, so genesen wir.",
			"9": "<span> 9 </span> \tDu hast einen Weinstock aus Ägypten geholt, hast vertrieben die Völker und ihn eingepflanzt.",
			"10": "<span> 10 </span> \tDu hast vor ihm Raum gemacht und hast ihn lassen einwurzeln, daß er das Land erfüllt hat.",
			"11": "<span> 11 </span> \tBerge sind mit seinem Schatten bedeckt und mit seinen Reben die Zedern Gottes.",
			"12": "<span> 12 </span> \tDu hast seine Ranken ausgebreitet bis an das Meer und seine Zweige bis an den Strom.",
			"13": "<span> 13 </span> \tWarum hast du denn seine Mauer zerbrochen, daß jeder seine Früchte abreißt, der vorübergeht?",
			"14": "<span> 14 </span> \tEs haben ihn zerwühlt die wilden Säue und die Tiere des Feldes ihn abgeweidet.",
			"15": "<span> 15 </span> \tGott Zebaoth, wende dich doch! Schaue vom Himmel und sieh darein, nimm dich dieses Weinstocks an!",
			"16": "<span> 16 </span> \tSchütze doch, was deine Rechte gepflanzt hat, den Sohn, den du dir großgezogen hast!",
			"17": "<span> 17 </span> \tSie haben ihn mit Feuer verbrannt wie Kehricht; vor dem Drohen deines Angesichts sollen sie umkommen.",
			"18": "<span> 18 </span> \tDeine Hand schütze den Mann deiner Rechten, den Sohn, den du dir großgezogen hast.",
			"19": "<span> 19 </span> \tSo wollen wir nicht von dir weichen. Laß uns leben, so wollen wir deinen Namen anrufen.\r\n[20]\tHERR, Gott Zebaoth, tröste uns wieder; laß leuchten dein Antlitz, so genesen wir."
		},
		"81": {
			"1": "<span>1</span> \tVON ASAPH, VORZUSINGEN, AUF DER GITTITH.",
			"2": "<span> 2 </span> \tSinget fröhlich Gott, der unsre Stärke ist, jauchzet dem Gott Jakobs!",
			"3": "<span> 3 </span> \tHebt an mit Psalmen und laßt hören die Pauken, liebliche Zithern und Harfen!",
			"4": "<span> 4 </span> \tBlaset am Neumond die Posaune, am Vollmond, am Tag unsres Festes!",
			"5": "<span> 5 </span> \tDenn das ist eine Satzung für Israel und eine Ordnung des Gottes Jakobs.",
			"6": "<span> 6 </span> \tDas hat er zum Zeugnis gesetzt für Joseph, als Er auszog wider Ägyptenland.\r\nEine Sprache höre ich, die ich bisher nicht kannte:",
			"7": "<span> 7 </span> \tIch habe ihre Schultern von der Last befreit und ihre Hände vom Tragkorb erlöset.",
			"8": "<span> 8 </span> \tAls du mich in der Not anriefst, half ich dir heraus und antwortete dir aus der Wetterwolke und prüfte dich am Haderwasser. SELA.",
			"9": "<span> 9 </span> \tHöre, mein Volk, ich will dich ermahnen. Israel, du sollst mich hören!",
			"10": "<span> 10 </span> \tKein andrer Gott sei unter dir, und einen fremden Gott sollst du nicht anbeten!",
			"11": "<span> 11 </span> \tIch bin der HERR, dein Gott, der dich aus Ägyptenland geführt hat: Tu deinen Mund weit auf, laß mich ihn füllen!",
			"12": "<span> 12 </span> \tAber mein Volk gehorcht nicht meiner Stimme, und Israel will mich nicht.",
			"13": "<span> 13 </span> \tSo hab ich sie dahingegeben in die Verstocktheit ihres Herzens, daß sie wandeln nach eigenem Rat.",
			"14": "<span> 14 </span> \tWenn doch mein Volk mir gehorsam wäre und Israel auf meinem Wege ginge!",
			"15": "<span> 15 </span> \tDann wollte ich seine Feinde bald demütigen und meine Hand gegen seine Widersacher wenden!",
			"16": "<span> 16 </span> \tUnd die den HERRN hassen, müßten sich vor ihm beugen, aber Israels Zeit würde ewiglich währen,\r\n[17]\tund ich würde es mit dem besten Weizen speisen und mit Honig aus dem Felsen sättigen."
		},
		"82": {
			"1": "<span>1</span> \tEIN PSALM ASAPHS. Gott steht in der Gottesgemeinde und ist Richter unter den Göttern.",
			"2": "<span> 2 </span> \t«Wie lange wollt ihr Unrecht richten und die Gottlosen vorziehen? SELA.",
			"3": "<span> 3 </span> \tSchaffet Recht dem Armen und der Waise und helft dem Elenden und Bedürftigen zum Recht.",
			"4": "<span> 4 </span> \tErrettet den Geringen und Armen und erlöst ihn aus der Gewalt der Gottlosen.»",
			"5": "<span> 5 </span> \tSie lassen sich nichts sagen und sehen nichts ein, sie tappen dahin im Finstern. Darum wanken alle Grundfesten der Erde.",
			"6": "<span> 6 </span> \t«Wohl habe ich gesagt: Ihr seid Götter und allzumal Söhne des Höchsten;",
			"7": "<span> 7 </span> \taber ihr werdet sterben wie Menschen und wie ein Tyrann zugrunde gehen.»",
			"8": "<span> 8 </span> \tGott, mache dich auf und richte die Erde; denn du bist Erbherr über alle Heiden!"
		},
		"83": {
			"1": "<span>1</span> \tEIN PSALMLIED ASAPHS.",
			"2": "<span> 2 </span> \tGott, schweige doch nicht! Gott, bleib nicht so still und ruhig!",
			"3": "<span> 3 </span> \tDenn siehe, deine Feinde toben, und die dich hassen, erheben das Haupt.",
			"4": "<span> 4 </span> \tSie machen listige Anschläge wider dein Volk und halten Rat wider die, die bei dir sich bergen.",
			"5": "<span> 5 </span> \t«Wohlan!» sprechen sie, «laßt uns sie ausrotten, daß sie kein Volk mehr seien und des Namens Israel nicht mehr gedacht werde!»",
			"6": "<span> 6 </span> \tDenn sie sind miteinander eins geworden und haben einen Bund wider dich gemacht:",
			"7": "<span> 7 </span> \tdie in den Zelten von Edom und Ismael wohnen, Moab und die Hagariter,",
			"8": "<span> 8 </span> \tGebal, Ammon und Amalek, die Philister mit denen von Tyrus;",
			"9": "<span> 9 </span> \tauch Assur hat sich zu ihnen geschlagen, sie helfen den Söhnen Lot. SELA.",
			"10": "<span> 10 </span> \tMach's mit ihnen wie mit Midian, wie mit Sisera, mit Jabin am Bach Kison,",
			"11": "<span> 11 </span> \tdie vertilgt wurden bei Endor und wurden zu Mist auf dem Acker.",
			"12": "<span> 12 </span> \tMache ihre Fürsten wie Oreb und Seeb, alle ihre Edlen wie Sebach und Zalmunna,",
			"13": "<span> 13 </span> \tdie auch einmal sagten: Wir wollen das Land Gottes einnehmen.",
			"14": "<span> 14 </span> \tMein Gott, mache sie wie verwehende Blätter, wie Spreu vor dem Winde.",
			"15": "<span> 15 </span> \tWie ein Feuer den Wald verbrennt und wie eine Flamme die Berge versengt,",
			"16": "<span> 16 </span> \tso verfolge sie mit deinem Sturm und erschrecke sie mit deinem Ungewitter.",
			"17": "<span> 17 </span> \tBedecke ihr Angesicht mit Schande, daß sie, HERR, nach deinem Namen fragen müssen.",
			"18": "<span> 18 </span> \tSchämen sollen sie sich und erschrecken für immer und zuschanden werden und umkommen.\r\n[19]\tSo werden sie erkennen, daß du allein HERR heißest und der Höchste bist in aller Welt."
		},
		"84": {
			"1": "<span>1</span> \tEIN PSALM DER SÖHNE KORAH, VORZUSINGEN, AUF DER GITTITH.",
			"2": "<span> 2 </span> \tWie lieb sind mir deine Wohnungen, HERR Zebaoth!",
			"3": "<span> 3 </span> \tMeine Seele verlangt und sehnt sich nach den Vorhöfen des HERRN; mein Leib und Seele freuen sich in dem lebendigen Gott.",
			"4": "<span> 4 </span> \tDer Vogel hat ein Haus gefunden und die Schwalbe ein Nest für ihre Jungen – deine Altäre, HERR Zebaoth, mein König und mein Gott.",
			"5": "<span> 5 </span> \tWohl denen, die in deinem Hause wohnen; die loben dich immerdar. SELA.",
			"6": "<span> 6 </span> \tWohl den Menschen, die dich für ihre Stärke halten und von Herzen dir nachwandeln!",
			"7": "<span> 7 </span> \tWenn sie durchs dürre Tal ziehen, wird es ihnen zum Quellgrund, und Frühregen hüllt es in Segen.",
			"8": "<span> 8 </span> \tSie gehen von einer Kraft zur andern und schauen den wahren Gott in Zion.",
			"9": "<span> 9 </span> \tHERR, Gott Zebaoth, höre mein Gebet; vernimm es, Gott Jakobs! SELA.",
			"10": "<span> 10 </span> \tGott, unser Schild, schaue doch; sieh doch an das Antlitz deines Gesalbten!",
			"11": "<span> 11 </span> \tDenn ein Tag in deinen Vorhöfen ist besser als sonst tausend. Ich will lieber die Tür hüten in meines Gottes Hause als wohnen in der Gottlosen Hütten.",
			"12": "<span> 12 </span> \tDenn Gott der HERR ist Sonne und Schild; der HERR gibt Gnade und Ehre. Er wird kein Gutes mangeln lassen den Frommen.\r\n[13]\tHERR Zebaoth, wohl dem Menschen, der sich auf dich verläßt!"
		},
		"85": {
			"1": "<span>1</span> \tEIN PSALM DER SÖHNE KORAH, VORZUSINGEN.",
			"2": "<span> 2 </span> \tHERR, der du bist vormals gnädig gewesen deinem Lande und hast erlöst die Gefangenen Jakobs;",
			"3": "<span> 3 </span> \tder du die Missetat vormals vergeben hast deinem Volk und alle seine Sünde bedeckt hast; SELA.",
			"4": "<span> 4 </span> \tder du vormals hast all deinen Zorn fahren lassen und dich abgewandt von der Glut deines Zorns:",
			"5": "<span> 5 </span> \thilf uns, Gott, unser Heiland, und laß ab von deiner Ungnade über uns!",
			"6": "<span> 6 </span> \tWillst du denn ewiglich über uns zürnen und deinen Zorn walten lassen für und für?",
			"7": "<span> 7 </span> \tWillst du uns denn nicht wieder erquicken, daß dein Volk sich über dich freuen kann?",
			"8": "<span> 8 </span> \tHERR, erweise uns deine Gnade und gib uns dein Heil!",
			"9": "<span> 9 </span> \tKönnte ich doch hören, was Gott der HERR redet, daß er Frieden zusagte seinem Volk und seinen Heiligen, damit sie nicht in Torheit geraten.",
			"10": "<span> 10 </span> \tDoch ist ja seine Hilfe nahe denen, die ihn fürchten, daß in unserm Lande Ehre wohne;",
			"11": "<span> 11 </span> \tdaß Güte und Treue einander begegnen, Gerechtigkeit und Friede sich küssen;",
			"12": "<span> 12 </span> \tdaß Treue auf der Erde wachse und Gerechtigkeit vom Himmel schaue;",
			"13": "<span> 13 </span> \tdaß uns auch der HERR Gutes tue, und unser Land seine Frucht gebe;\r\n[14]\tdaß Gerechtigkeit vor ihm her gehe und seinen Schritten folge."
		},
		"86": {
			"1": "<span>1</span> \tEIN GEBET DAVIDS. HERR, neige deine Ohren und erhöre mich; denn ich bin elend und arm.",
			"2": "<span> 2 </span> \tBewahre meine Seele, denn ich bin dein. Hilf du, mein Gott, deinem Knechte, der sich verläßt auf dich.",
			"3": "<span> 3 </span> \tHerr, sei mir gnädig; denn ich rufe täglich zu dir.",
			"4": "<span> 4 </span> \tErfreue die Seele deines Knechts; denn nach dir, Herr, verlangt mich.",
			"5": "<span> 5 </span> \tDenn du, Herr, bist gut und gnädig, von großer Güte allen, die dich anrufen.",
			"6": "<span> 6 </span> \tVernimm, HERR, mein Gebet und merke auf die Stimme meines Flehens!",
			"7": "<span> 7 </span> \tIn der Not rufe ich dich an; du wollest mich erhören!",
			"8": "<span> 8 </span> \tHerr, es ist dir keiner gleich unter den Göttern, und niemand kann tun, was du tust.",
			"9": "<span> 9 </span> \tAlle Völker, die du gemacht hast, werden kommen und vor dir anbeten, Herr, und deinen Namen ehren,",
			"10": "<span> 10 </span> \tdaß du so groß bist und Wunder tust und du allein Gott bist.",
			"11": "<span> 11 </span> \tWeise mir, HERR, deinen Weg, daß ich wandle in deiner Wahrheit; erhalte mein Herz bei dem einen, daß ich deinen Namen fürchte.",
			"12": "<span> 12 </span> \tIch danke dir, Herr, mein Gott, von ganzem Herzen und ehre deinen Namen ewiglich.",
			"13": "<span> 13 </span> \tDenn deine Güte ist groß gegen mich, du hat mich errettet aus der Tiefe des Todes.",
			"14": "<span> 14 </span> \tGott, es erheben sich die Stolzen gegen mich, und eine Rotte von Gewalttätern trachtet mir nach dem Leben und haben dich nicht vor Augen.",
			"15": "<span> 15 </span> \tDu aber, Herr, Gott, bist barmherzig und gnädig, geduldig und von großer Güte und Treue.",
			"16": "<span> 16 </span> \tWende dich zu mir und sei mir gnädig; stärke deinen Knecht mit deiner Kraft und hilf dem Sohn deiner Magd!",
			"17": "<span> 17 </span> \tTu ein Zeichen an mir, daß du's gut mit mir meinst, daß es sehen, die mich hassen, und sich schämen, weil du mir beistehst, HERR, und mich tröstest."
		},
		"87": {
			"1": "<span>1</span> \tEIN PSALMLIED DER SÖHNE KORAH. Sie ist fest gegründet auf den heiligen Bergen.",
			"2": "<span> 2 </span> \tDer HERR liebt die Tore Zions mehr als alle Wohnungen in Jakob.",
			"3": "<span> 3 </span> \tHerrliche Dinge werden in dir gepredigt, du Stadt Gottes. SELA.",
			"4": "<span> 4 </span> \tIch zähle Ägypten und Babel zu denen, die mich kennen, auch die Philister und Tyrer samt den Mohren: «Die sind hier geboren.»",
			"5": "<span> 5 </span> \tDoch von Zion wird man sagen: «Mann für Mann ist darin geboren»; und er selbst, der Höchste, erhält es.",
			"6": "<span> 6 </span> \tDer HERR spricht, wenn er aufschreibt die Völker: «Die sind hier geboren.» SELA.",
			"7": "<span> 7 </span> \tUnd sie singen beim Reigen: Alle meine Quellen sind in dir!"
		},
		"88": {
			"1": "<span>1</span> \tEIN PSALMLIED DER SÖHNE KORAH, VORZUSINGEN, ZUM REIGENTANZ IM WECHSEL, EINE UNTERWEISUNG HEMANS, DES ESRACHITERS.\r\nDie Übersetzung ist unsicher. Luther übersetzte: «VON DER SCHWACHHEIT DER ELENDEN».",
			"2": "<span> 2 </span> \tHERR, Gott, mein Heiland, ich schreie Tag und Nacht vor dir.",
			"3": "<span> 3 </span> \tLaß mein Gebet vor dich kommen, neige deine Ohren zu meinem Schreien.",
			"4": "<span> 4 </span> \tDenn meine Seele ist übervoll an Leiden, und mein Leben ist nahe dem Tode.",
			"5": "<span> 5 </span> \tIch bin denen gleich geachtet, die in die Grube fahren, ich bin wie ein Mann, der keine Kraft mehr hat.",
			"6": "<span> 6 </span> \tIch liege unter den Toten verlassen, wie die Erschlagenen, die im Grabe liegen, derer du nicht mehr gedenkst und die von deiner Hand geschieden sind.",
			"7": "<span> 7 </span> \tDu hast mich hinunter in die Grube gelegt, in die Finsternis und in die Tiefe.",
			"8": "<span> 8 </span> \tDein Grimm drückt mich nieder, du bedrängst mich mit allen deinen Fluten. SELA.",
			"9": "<span> 9 </span> \tMeine Freunde hast du mir entfremdet, du hast mich ihnen zum Abscheu gemacht. Ich liege gefangen und kann nicht heraus,",
			"10": "<span> 10 </span> \tmein Auge sehnt sich aus dem Elend. HERR, ich rufe zu dir täglich; ich breite meine Hände aus zu dir.",
			"11": "<span> 11 </span> \tWirst du an den Toten Wunder tun, oder werden die Verstorbenen aufstehen und dir danken? SELA.",
			"12": "<span> 12 </span> \tWird man im Grabe erzählen deine Güte und deine Treue bei den Toten?",
			"13": "<span> 13 </span> \tWerden denn deine Wunder in der Finsternis erkannt oder deine Gerechtigkeit im Lande des Vergessens?",
			"14": "<span> 14 </span> \tAber ich schreie zu dir, HERR, und mein Gebet kommt frühe vor dich:",
			"15": "<span> 15 </span> \tWarum verstößt du, HERR, meine Seele und verbirgst dein Antlitz vor mir?",
			"16": "<span> 16 </span> \tIch bin elend und dem Tode nahe von Jugend auf; ich erleide deine Schrecken, daß ich fast verzage.",
			"17": "<span> 17 </span> \tDein Grimm geht über mich, deine Schrecken vernichten mich.",
			"18": "<span> 18 </span> \tSie umgeben mich täglich wie Fluten und umringen mich allzumal.\r\n[19]\tMeine Freunde und Nächsten hast du mir entfremdet, und meine Verwandten hältst du fern von mir."
		},
		"89": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG ETHANS, DES ESRACHITERS.",
			"2": "<span> 2 </span> \tIch will singen von der Gnade des HERRN ewiglich und seine Treue verkünden mit meinem Munde für und für;",
			"3": "<span> 3 </span> \tdenn ich sage: Für ewig steht die Gnade fest; du gibst deiner Treue sicheren Grund im Himmel.",
			"4": "<span> 4 </span> \t«Ich habe einen Bund geschlossen mit meinem Auserwählten, ich habe David, meinem Knechte, geschworen:",
			"5": "<span> 5 </span> \tIch will deinem Geschlecht festen Grund geben auf ewig und deinen Thron bauen für und für.» SELA.",
			"6": "<span> 6 </span> \tUnd die Himmel werden, HERR, deine Wunder preisen und deine Treue in der Gemeinde der Heiligen.",
			"7": "<span> 7 </span> \tDenn wer in den Wolken könnte dem HERRN gleichen und dem HERRN gleich sein unter den Himmlischen?",
			"8": "<span> 8 </span> \tGott ist gefürchtet in der Versammlung der Heiligen, groß und furchtbar über alle, die um ihn sind.",
			"9": "<span> 9 </span> \tHERR, Gott Zebaoth, wer ist wie du? Mächtig bist du, HERR, und deine Treue ist um dich her.",
			"10": "<span> 10 </span> \tDu herrschest über das ungestüme Meer, du stillest seine Wellen, wenn sie sich erheben.",
			"11": "<span> 11 </span> \tDu hast Rahab zu Tode geschlagen und deine Feinde zerstreut mit deinem starken Arm.",
			"12": "<span> 12 </span> \tHimmel und Erde sind dein, du hast gegründet den Erdkreis und was darinnen ist.",
			"13": "<span> 13 </span> \tNord und Süd hast du geschaffen, Tabor und Hermon jauchzen über deinen Namen.",
			"14": "<span> 14 </span> \tDu hast einen gewaltigen Arm, stark ist deine Hand, und hoch ist deine Rechte.",
			"15": "<span> 15 </span> \tGerechtigkeit und Gericht sind deines Thrones Stütze, Gnade und Treue gehen vor dir einher.",
			"16": "<span> 16 </span> \tWohl dem Volk, das jauchzen kann! HERR, sie werden im Licht deines Antlitzes wandeln;",
			"17": "<span> 17 </span> \tsie werden über deinen Namen täglich fröhlich sein und in deiner Gerechtigkeit herrlich sein.",
			"18": "<span> 18 </span> \tDenn du bist der Ruhm ihrer Stärke, und durch deine Gnade wirst du unser Haupt erhöhen.",
			"19": "<span> 19 </span> \tDenn dem HERRN gehört unser Schild und dem Heiligen in Israel unser König.",
			"20": "<span> 20 </span> \tDamals hast du geredet durch ein Gesicht zu deinem Heiligen und gesagt: Ich habe einen Helden erweckt, der helfen soll, ich habe erhöht einen Auserwählten aus dem Volk.",
			"21": "<span> 21 </span> \tIch habe gefunden meinen Knecht David, ich habe ihn gesalbt mit meinem heiligen Öl.",
			"22": "<span> 22 </span> \tMeine Hand soll ihn erhalten, und mein Arm soll ihn stärken.",
			"23": "<span> 23 </span> \tDie Feinde sollen ihn nicht überwältigen und die Ungerechten ihn nicht demütigen;",
			"24": "<span> 24 </span> \tsondern ich will seine Widersacher vor ihm zerschlagen und, die ihn hassen, zu Boden stoßen.",
			"25": "<span> 25 </span> \tAber meine Treue und Gnade soll bei ihm sein, und sein Haupt soll erhöht sein in meinem Namen.",
			"26": "<span> 26 </span> \tSeine Hand laß ich herrschen über das Meer und seine Rechte über die Ströme.",
			"27": "<span> 27 </span> \tEr wird mich nennen: Du bist mein Vater, mein Gott und Hort, der mir hilft.",
			"28": "<span> 28 </span> \tUnd ich will ihn zum erstgeborenen Sohn machen, zum Höchsten unter den Königen auf Erden.",
			"29": "<span> 29 </span> \tIch will ihm ewiglich bewahren meine Gnade, und mein Bund soll ihm fest bleiben.",
			"30": "<span> 30 </span> \tIch will ihm ewiglich Nachkommen geben und seinen Thron erhalten, solange der Himmel währt.",
			"31": "<span> 31 </span> \tWenn aber seine Söhne mein Gesetz verlassen und in meinen Rechten nicht wandeln,",
			"32": "<span> 32 </span> \twenn sie meine Ordnungen entheiligen und meine Gebote nicht halten,",
			"33": "<span> 33 </span> \tso will ich ihre Sünde mit der Rute heimsuchen und ihre Missetat mit Plagen;",
			"34": "<span> 34 </span> \taber meine Gnade will ich nicht von ihm wenden und meine Treue nicht brechen.",
			"35": "<span> 35 </span> \tIch will meinen Bund nicht entheiligen und nicht ändern, was aus meinem Munde gegangen ist.",
			"36": "<span> 36 </span> \tIch habe einmal geschworen bei meiner Heiligkeit und will David nicht belügen:",
			"37": "<span> 37 </span> \t«Sein Geschlecht soll ewig bestehen und sein Thron vor mir wie die Sonne,",
			"38": "<span> 38 </span> \twie der Mond, der ewiglich bleibt, und wie der treue Zeuge in den Wolken.» SELA.",
			"39": "<span> 39 </span> \tAber nun hast du verstoßen und verworfen und zürnst mit deinem Gesalbten!",
			"40": "<span> 40 </span> \tDu hast zerbrochen den Bund mit deinem Knecht und seine Krone entweiht in den Staub.",
			"41": "<span> 41 </span> \tDu hast eingerissen alle seine Mauern und hast zerstört seine Festungen.",
			"42": "<span> 42 </span> \tEs berauben ihn alle, die vorübergehen; er ist seinen Nachbarn ein Spott geworden.",
			"43": "<span> 43 </span> \tDu erhöhst die Rechte seiner Widersacher und erfreust alle seine Feinde.",
			"44": "<span> 44 </span> \tAuch hast du die Kraft seines Schwerts weggenommen und lässest ihn nicht siegen im Streit.",
			"45": "<span> 45 </span> \tDu hast seinem Glanz ein Ende gemacht und seinen Thron zu Boden geworfen.",
			"46": "<span> 46 </span> \tDu hast die Tage seiner Jugend verkürzt und ihn bedeckt mit Schande. SELA.",
			"47": "<span> 47 </span> \tHERR, wie lange willst du dich so verbergen und deinen Grimm wie Feuer brennen lassen?",
			"48": "<span> 48 </span> \tGedenke, wie kurz mein Leben ist, wie vergänglich du alle Menschen geschaffen hast!",
			"49": "<span> 49 </span> \tWo ist jemand, der da lebt und den Tod nicht sähe, der seine Seele errette aus des Todes Hand? SELA.",
			"50": "<span> 50 </span> \tHerr, wo ist deine Gnade von einst, die du David geschworen hast in deiner Treue?",
			"51": "<span> 51 </span> \tGedenke, Herr, an die Schmach deiner Knechte, die ich trage in meinem Herzen von all den vielen Völkern,",
			"52": "<span> 52 </span> \tmit der, HERR, deine Feinde dich schmähen, mit der sie schmähen hinter deinem Gesalbten her!\r\n[53]\tGelobt sei der HERR ewiglich! Amen! Amen!"
		},
		"90": {
			"1": "<span>1</span> \tEIN GEBET DES MOSE, DES MANNES GOTTES. Herr, du bist unsre Zuflucht für und für.",
			"2": "<span> 2 </span> \tEhe denn die Berge wurden und die Erde und die Welt geschaffen wurden, bist du, Gott, von Ewigkeit zu Ewigkeit.",
			"3": "<span> 3 </span> \tDer du die Menschen lässest sterben und sprichst: Kommt wieder, Menschenkinder!",
			"4": "<span> 4 </span> \tDenn tausend Jahre sind vor dir wie der Tag, der gestern vergangen ist, und wie eine Nachtwache.",
			"5": "<span> 5 </span> \tDu lässest sie dahinfahren wie einen Strom, sie sind wie ein Schlaf, wie ein Gras, das am Morgen noch sproßt,",
			"6": "<span> 6 </span> \tdas am Morgen blüht und sproßt und des Abends welkt und verdorrt.",
			"7": "<span> 7 </span> \tDas macht dein Zorn, daß wir so vergehen, und dein Grimm, daß wir so plötzlich dahin müssen.",
			"8": "<span> 8 </span> \tDenn unsre Missetaten stellst du vor dich, unsre unerkannte Sünde ins Licht vor deinem Angesicht.",
			"9": "<span> 9 </span> \tDarum fahren alle unsre Tage dahin durch deinen Zorn, wir bringen unsre Jahre zu wie ein Geschwätz.",
			"10": "<span> 10 </span> \tUnser Leben währet siebzig Jahre, und wenn's hoch kommt, so sind's achtzig Jahre, und was daran köstlich scheint, ist doch nur vergebliche Mühe; denn es fähret schnell dahin, als flögen wir davon.",
			"11": "<span> 11 </span> \tWer glaubt's aber, daß du so sehr zürnest, und wer fürchtet sich vor dir in deinem Grimm?",
			"12": "<span> 12 </span> \tLehre uns bedenken, daß wir sterben müssen, auf daß wir klug werden.",
			"13": "<span> 13 </span> \tHERR, kehre dich doch endlich wieder zu uns und sei deinen Knechten gnädig!",
			"14": "<span> 14 </span> \tFülle uns frühe mit deiner Gnade, so wollen wir rühmen und fröhlich sein unser Leben lang.",
			"15": "<span> 15 </span> \tErfreue uns nun wieder, nachdem du uns so lange plagest, nachdem wir so lange Unglück leiden.",
			"16": "<span> 16 </span> \tZeige deinen Knechten deine Werke und deine Herrlichkeit ihren Kindern.",
			"17": "<span> 17 </span> \tUnd der Herr, unser Gott, sei uns freundlich und fördere das Werk unsrer Hände bei uns. Ja, das Werk unsrer Hände wollest du fördern!"
		},
		"91": {
			"1": "<span>1</span> \tWer unter dem Schirm des Höchsten sitzt und unter dem Schatten des Allmächtigen bleibt,",
			"2": "<span> 2 </span> \tder spricht zu dem HERRN: Meine Zuversicht und meine Burg, mein Gott, auf den ich hoffe.",
			"3": "<span> 3 </span> \tDenn er errettet dich vom Strick des Jägers und von der verderblichen Pest.",
			"4": "<span> 4 </span> \tEr wird dich mit seinen Fittichen decken, und Zuflucht wirst du haben unter seinen Flügeln. Seine Wahrheit ist Schirm und Schild,",
			"5": "<span> 5 </span> \tdaß du nicht erschrecken mußt vor dem Grauen der Nacht, vor den Pfeilen, die des Tages fliegen,",
			"6": "<span> 6 </span> \tvor der Pest, die im Finstern schleicht, vor der Seuche, die am Mittag Verderben bringt.",
			"7": "<span> 7 </span> \tWenn auch tausend fallen zu deiner Seite und zehntausend zu deiner Rechten, so wird es doch dich nicht treffen.",
			"8": "<span> 8 </span> \tJa, du wirst es mit eigenen Augen sehen und schauen, wie den Gottlosen vergolten wird.",
			"9": "<span> 9 </span> \tDenn der HERR ist deine Zuversicht, der Höchste ist deine Zuflucht.",
			"10": "<span> 10 </span> \tEs wird dir kein Übel begegnen, und keine Plage wird sich deinem Hause nahen.",
			"11": "<span> 11 </span> \tDenn er hat seinen Engeln befohlen, daß sie dich behüten auf allen deinen Wegen,",
			"12": "<span> 12 </span> \tdaß sie dich auf den Händen tragen und du deinen Fuß nicht an einen Stein stoßest.",
			"13": "<span> 13 </span> \tÜber Löwen und Ottern wirst du gehen und junge Löwen und Drachen niedertreten.",
			"14": "<span> 14 </span> \t«Er liebt mich, darum will ich ihn erretten; er kennt meinen Namen, darum will ich ihn schützen.",
			"15": "<span> 15 </span> \tEr ruft mich an, darum will ich ihn erhören; ich bin bei ihm in der Not, ich will ihn herausreißen und zu Ehren bringen.",
			"16": "<span> 16 </span> \tIch will ihn sättigen mit langem Leben und will ihm zeigen mein Heil.»"
		},
		"92": {
			"1": "<span>1</span> \tEIN PSALMLIED FÜR DEN SABBATTAG.",
			"2": "<span> 2 </span> \tDas ist ein köstlich Ding, dem HERRN danken und lobsingen deinem Namen, du Höchster,",
			"3": "<span> 3 </span> \tdes Morgens deine Gnade und des Nachts deine Wahrheit verkündigen",
			"4": "<span> 4 </span> \tauf dem Psalter mit zehn Saiten, mit Spielen auf der Harfe.",
			"5": "<span> 5 </span> \tDenn, HERR, du lässest mich fröhlich singen von deinen Werken, und ich rühme die Taten deiner Hände.",
			"6": "<span> 6 </span> \tHERR, wie sind deine Werke so groß! Deine Gedanken sind sehr tief.",
			"7": "<span> 7 </span> \tEin Törichter glaubt das nicht, und ein Narr begreift es nicht.",
			"8": "<span> 8 </span> \tDie Gottlosen grünen wie das Gras, und die Übeltäter blühen alle – nur um vertilgt zu werden für immer!",
			"9": "<span> 9 </span> \tAber du, HERR, bist der Höchste und bleibest ewiglich.",
			"10": "<span> 10 </span> \tDenn siehe, deine Feinde, HERR, siehe, deine Feinde werden umkommen, und alle Übeltäter sollen zerstreut werden.",
			"11": "<span> 11 </span> \tAber mich machst du stark wie den Wildstier und salbst mich mit frischem Öl.",
			"12": "<span> 12 </span> \tMit Freude sieht mein Auge auf meine Feinde herab und hört mein Ohr von den Boshaften, die sich gegen mich erheben.",
			"13": "<span> 13 </span> \tDer Gerechte wird grünen wie ein Palmbaum, er wird wachsen wie eine Zeder auf dem Libanon.",
			"14": "<span> 14 </span> \tDie gepflanzt sind im Hause des HERRN, werden in den Vorhöfen unsres Gottes grünen.",
			"15": "<span> 15 </span> \tUnd wenn sie auch alt werden, werden sie dennoch blühen, fruchtbar und frisch sein,\r\n[16]\tdaß sie verkündigen, wie der HERR es recht macht; er ist mein Fels, und kein Unrecht ist an ihm."
		},
		"93": {
			"1": "<span>1</span> \tDer HERR ist König und herrlich geschmückt; der HERR ist geschmückt und umgürtet mit Kraft. Er hat den Erdkreis gegründet, daß er nicht wankt.",
			"2": "<span> 2 </span> \tVon Anbeginn steht dein Thron fest; du bist ewig.",
			"3": "<span> 3 </span> \tHERR, die Wasserströme erheben sich, die Wasserströme erheben ihr Brausen, die Wasserströme heben empor die Wellen;",
			"4": "<span> 4 </span> \tdie Wasserwogen im Meer sind groß und brausen mächtig; der HERR aber ist noch größer in der Höhe.",
			"5": "<span> 5 </span> \tDein Wort ist wahrhaftig und gewiß; Heiligkeit ist die Zierde deines Hauses, HERR, für alle Zeit."
		},
		"94": {
			"1": "<span>1</span> \tHERR, du Gott der Vergeltung, du Gott der Vergeltung, erscheine!",
			"2": "<span> 2 </span> \tErhebe dich, du Richter der Welt; vergilt den Hoffärtigen, was sie verdienen!",
			"3": "<span> 3 </span> \tHERR, wie lange sollen die Gottlosen, wie lange sollen die Gottlosen prahlen?",
			"4": "<span> 4 </span> \tEs reden so trotzig daher, es rühmen sich alle Übeltäter.",
			"5": "<span> 5 </span> \tHERR, sie zerschlagen dein Volk und plagen dein Erbe.",
			"6": "<span> 6 </span> \tWitwen und Fremdlinge bringen sie um und töten die Waisen",
			"7": "<span> 7 </span> \tund sagen: Der HERR sieht's nicht, und der Gott Jakobs beachtet's nicht.",
			"8": "<span> 8 </span> \tMerkt doch auf, ihr Narren im Volk! Und ihr Toren, wann wollt ihr klug werden?",
			"9": "<span> 9 </span> \tDer das Ohr gepflanzt hat, sollte der nicht hören? Der das Auge gemacht hat, sollte der nicht sehen?",
			"10": "<span> 10 </span> \tDer die Völker in Zucht hält, sollte der nicht Rechenschaft fordern - er, der die Menschen Erkenntnis lehrt?",
			"11": "<span> 11 </span> \tAber der HERR kennt die Gedanken der Menschen: sie sind nur ein Hauch!",
			"12": "<span> 12 </span> \tWohl dem, den du, HERR, in Zucht nimmst und lehrst ihn durch dein Gesetz,",
			"13": "<span> 13 </span> \tihm Ruhe zu schaffen vor bösen Tagen, bis dem Gottlosen die Grube gegraben ist.",
			"14": "<span> 14 </span> \tDenn der HERR wird sein Volk nicht verstoßen noch sein Erbe verlassen.",
			"15": "<span> 15 </span> \tDenn Recht muß doch Recht bleiben, und ihm werden alle frommen Herzen zufallen.",
			"16": "<span> 16 </span> \tWer steht mir bei wider die Boshaften? Wer tritt zu mir wider die Übeltäter?",
			"17": "<span> 17 </span> \tWenn der HERR mir nicht hülfe, läge ich bald am Orte des Schweigens.",
			"18": "<span> 18 </span> \tWenn ich sprach: Mein Fuß ist gestrauchelt, so hielt mich, HERR, deine Gnade.",
			"19": "<span> 19 </span> \tIch hatte viel Kummer in meinem Herzen, aber deine Tröstungen erquickten meine Seele.",
			"20": "<span> 20 </span> \tDu hast ja nicht Gemeinschaft mit dem Richterstuhl der Bösen, die das Gesetz mißbrauchen und Unheil schaffen.",
			"21": "<span> 21 </span> \tSie rotten sich zusammen wider den Gerechten und verurteilen unschuldig Blut.",
			"22": "<span> 22 </span> \tAber der HERR ist mein Schutz, mein Gott ist der Hort meiner Zuversicht.",
			"23": "<span> 23 </span> \tUnd er wird ihnen ihr Unrecht vergelten und sie um ihrer Bosheit willen vertilgen; der HERR, unser Gott, wird sie vertilgen."
		},
		"95": {
			"1": "<span>1</span> \tKommt herzu, laßt uns dem HERRN frohlocken und jauchzen dem Hort unsres Heils!",
			"2": "<span> 2 </span> \tLaßt uns mit Danken vor sein Angesicht kommen und mit Psalmen ihm jauchzen!",
			"3": "<span> 3 </span> \tDenn der HERR ist ein großer Gott und ein großer König über alle Götter.",
			"4": "<span> 4 </span> \tDenn in seiner Hand sind die Tiefen der Erde, und die Höhen der Berge sind auch sein.",
			"5": "<span> 5 </span> \tDenn sein ist das Meer, und er hat's gemacht, und seine Hände haben das Trockene bereitet.",
			"6": "<span> 6 </span> \tKommt, laßt uns anbeten und knien und niederfallen vor dem HERRN, der uns gemacht hat.",
			"7": "<span> 7 </span> \tDenn er ist unser Gott, und wir das Volk seiner Weide und Schafe seiner Hand.\r\nWenn ihr doch heute auf seine Stimme hören wolltet:",
			"8": "<span> 8 </span> \t«Verstocket euer Herz nicht, wie zu Meriba geschah, wie zu Massa in der Wüste,",
			"9": "<span> 9 </span> \two mich eure Väter versuchten und prüften und hatten doch mein Werk gesehen.",
			"10": "<span> 10 </span> \tVierzig Jahre war dies Volk mir zuwider, daß ich sprach: Es sind Leute, deren Herz immer den Irrweg will und die meine Wege nicht lernen wollen,",
			"11": "<span> 11 </span> \tso daß ich schwor in meinem Zorn: Sie sollen nicht zu meiner Ruhe kommen.»"
		},
		"96": {
			"1": "<span>1</span> \tSinget dem HERRN ein neues Lied; singet dem HERRN, alle Welt!",
			"2": "<span> 2 </span> \tSinget dem HERRN und lobet seinen Namen, verkündet von Tag zu Tag sein Heil!",
			"3": "<span> 3 </span> \tErzählet unter den Heiden von seiner Herrlichkeit, unter allen Völkern von seinen Wundern!",
			"4": "<span> 4 </span> \tDenn der HERR ist groß und hoch zu loben, mehr zu fürchten als alle Götter.",
			"5": "<span> 5 </span> \tDenn alle Götter der Völker sind Götzen; aber der HERR hat den Himmel gemacht.",
			"6": "<span> 6 </span> \tHoheit und Pracht sind vor ihm, Macht und Herrlichkeit in seinem Heiligtum.",
			"7": "<span> 7 </span> \tIhr Völker, bringet dar dem HERRN, bringet dar dem HERRN Ehre und Macht!",
			"8": "<span> 8 </span> \tBringet dar dem HERRN die Ehre seines Namens, bringet Geschenke und kommt in seine Vorhöfe!",
			"9": "<span> 9 </span> \tBetet an den HERRN in heiligem Schmuck; es fürchte ihn alle Welt!",
			"10": "<span> 10 </span> \tSagt unter den Heiden: Der HERR ist König. Er hat den Erdkreis gegründet, daß er nicht wankt. Er richtet die Völker recht.",
			"11": "<span> 11 </span> \tDer Himmel freue sich, und die Erde sei fröhlich, das Meer brause und was darinnen ist;",
			"12": "<span> 12 </span> \tdas Feld sei fröhlich und alles, was darauf ist; es sollen jauchzen alle Bäume im Walde",
			"13": "<span> 13 </span> \tvor dem HERRN; denn er kommt, denn er kommt, zu richten das Erdreich. Er wird den Erdkreis richten mit Gerechtigkeit und die Völker mit seiner Wahrheit."
		},
		"97": {
			"1": "<span>1</span> \tDer HERR ist König; des freue sich das Erdreich und seien fröhlich die Inseln, soviel ihrer sind.",
			"2": "<span> 2 </span> \tWolken und Dunkel sind um ihn her, Gerechtigkeit und Gericht sind seines Thrones Stütze.",
			"3": "<span> 3 </span> \tFeuer geht vor ihm her und verzehrt ringsum seine Feinde.",
			"4": "<span> 4 </span> \tSeine Blitze erleuchten den Erdkreis, das Erdreich sieht es und erschrickt.",
			"5": "<span> 5 </span> \tBerge zerschmelzen wie Wachs vor dem HERRN, vor dem Herrscher der ganzen Erde.",
			"6": "<span> 6 </span> \tDie Himmel verkündigen seine Gerechtigkeit, und seine Herrlichkeit sehen alle Völker.",
			"7": "<span> 7 </span> \tSchämen sollen sich alle, die den Bildern dienen und sich der Götzen rühmen. Betet ihn an, alle Götter!",
			"8": "<span> 8 </span> \tZion hört es und ist froh, und die Töchter Judas sind fröhlich, weil du, HERR, recht regierest.",
			"9": "<span> 9 </span> \tDenn du, HERR, bist der Höchste über allen Landen, du bist hoch erhöht über alle Götter.",
			"10": "<span> 10 </span> \tDie ihr den HERRN liebet, hasset das Arge! Der Herr bewahrt die Seelen seiner Heiligen; aus der Hand der Gottlosen wird er sie erretten.",
			"11": "<span> 11 </span> \tDem Gerechten muß das Licht immer wieder aufgehen und Freude den frommen Herzen.",
			"12": "<span> 12 </span> \tIhr Gerechten, freut euch des HERRN und danket ihm und preiset seinen heiligen Namen!"
		},
		"98": {
			"1": "<span>1</span> \tEIN PSALM. Singet dem HERRN ein neues Lied, denn er tut Wunder. Er schafft Heil mit seiner Rechten und mit seinem heiligen Arm.",
			"2": "<span> 2 </span> \tDer HERR läßt sein Heil kund werden; vor den Völkern macht er seine Gerechtigkeit offenbar.",
			"3": "<span> 3 </span> \tEr gedenkt an seine Gnade und Treue für das Haus Israel, aller Welt Enden sehen das Heil unsres Gottes.",
			"4": "<span> 4 </span> \tJauchzet dem HERRN, alle Welt, singet, rühmet und lobet!",
			"5": "<span> 5 </span> \tLobet den HERRN mit Harfen, mit Harfen und mit Saitenspiel!",
			"6": "<span> 6 </span> \tMit Trompeten und Posaunen jauchzet vor dem HERRN, dem König!",
			"7": "<span> 7 </span> \tDas Meer brause und was darinnen ist, der Erdkreis und die darauf wohnen.",
			"8": "<span> 8 </span> \tDie Ströme sollen frohlocken, und alle Berge seien fröhlich",
			"9": "<span> 9 </span> \tvor dem HERRN; denn er kommt, das Erdreich zu richten. Er wird den Erdkreis richten mit Gerechtigkeit und die Völker, wie es recht ist."
		},
		"99": {
			"1": "<span>1</span> \tDer HERR ist König, darum zittern die Völker; er sitzt über den Cherubim, darum bebt die Welt.",
			"2": "<span> 2 </span> \tDer HERR ist groß in Zion und erhaben über alle Völker.",
			"3": "<span> 3 </span> \tPreisen sollen sie deinen großen und wunderbaren Namen, - denn er ist heilig -",
			"4": "<span> 4 </span> \tund die Macht des Königs, der das Recht lieb hat. Du hast bestimmt, was richtig ist, du schaffest Gericht und Gerechtigkeit in Jakob.\r\nLuther übersetzte: «Im Reich dieses Königs hat man das Recht lieb.»",
			"5": "<span> 5 </span> \tErhebet den HERRN, unsern Gott, betet an vor dem Schemel seiner Füße; denn er ist heilig.",
			"6": "<span> 6 </span> \tMose und Aaron unter seinen Priestern, und Samuel unter denen, die seinen Namen anrufen, die riefen den HERRN an, und er erhörte sie.",
			"7": "<span> 7 </span> \tEr redete mit ihnen in der Wolkensäule; sie hielten seine Gesetze und Gebote, die er ihnen gab.",
			"8": "<span> 8 </span> \tHERR, du bist unser Gott, du erhörtest sie; du, Gott, vergabst ihnen und straftest ihr Tun.",
			"9": "<span> 9 </span> \tErhebet den HERRN, unsern Gott, und betet an auf seinem heiligen Berge; denn der HERR, unser Gott, ist heilig."
		},
		"100": {
			"1": "<span>1</span> \tEIN PSALM ZUM DANKOPFER. Jauchzet dem HERRN, alle Welt!",
			"2": "<span> 2 </span> \tDienet dem HERRN mit Freuden, kommt vor sein Angesicht mit Frohlocken!",
			"3": "<span> 3 </span> \tErkennet, daß der HERR Gott ist! Er hat uns gemacht und nicht wir selbst zu seinem Volk und zu Schafen seiner Weide.",
			"4": "<span> 4 </span> \tGehet zu seinen Toren ein mit Danken, zu seinen Vorhöfen mit Loben; danket ihm, lobet seinen Namen!",
			"5": "<span> 5 </span> \tDenn der HERR ist freundlich, und seine Gnade währet ewig und seine Wahrheit für und für."
		},
		"101": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. Von Gnade und Recht will ich singen und dir, HERR, Lob sagen.",
			"2": "<span> 2 </span> \tIch handle umsichtig und redlich, daß du mögest zu mir kommen; ich wandle mit redlichem Herzen in meinem Hause.",
			"3": "<span> 3 </span> \tIch nehme mir keine böse Sache vor; ich hasse den Übertreter und lasse ihn nicht bei mir bleiben.",
			"4": "<span> 4 </span> \tEin falsches Herz muß von mir weichen. Den Bösen kann ich nicht leiden.",
			"5": "<span> 5 </span> \tWer seinen Nächsten heimlich verleumdet, den bring ich zum Schweigen. Ich mag den nicht, der stolze Gebärde und hoffärtige Art hat.",
			"6": "<span> 6 </span> \tMeine Augen sehen nach den Treuen im Lande, daß sie bei mir wohnen; ich habe gerne fromme Diener.",
			"7": "<span> 7 </span> \tFalsche Leute dürfen in meinem Hause nicht bleiben, die Lügner gedeihen nicht bei mir.",
			"8": "<span> 8 </span> \tJeden Morgen bring ich zum Schweigen alle Gottlosen im Lande, daß ich alle Übeltäter ausrotte aus der Stadt des HERRN."
		},
		"102": {
			"1": "<span>1</span> \tEIN GEBET FÜR DEN ELENDEN, WENN ER VERZAGT IST UND SEINE KLAGE VOR DEM HERRN AUSSCHÜTTET.",
			"2": "<span> 2 </span> \tHERR, höre mein Gebet und laß mein Schreien zu dir kommen!",
			"3": "<span> 3 </span> \tVerbirg dein Antlitz nicht vor mir in der Not, neige deine Ohren zu mir; wenn ich dich anrufe, so erhöre mich bald!",
			"4": "<span> 4 </span> \tDenn meine Tage sind vergangen wie ein Rauch, und meine Gebeine sind verbrannt wie von Feuer.",
			"5": "<span> 5 </span> \tMein Herz ist geschlagen und verdorrt wie Gras, daß ich sogar vergesse, mein Brot zu essen.",
			"6": "<span> 6 </span> \tMein Gebein klebt an meiner Haut vor Heulen und Seufzen.",
			"7": "<span> 7 </span> \tIch bin wie die Eule in der Einöde, wie das Käuzchen in den Trümmern.",
			"8": "<span> 8 </span> \tIch wache und klage wie ein einsamer Vogel auf dem Dache.",
			"9": "<span> 9 </span> \tTäglich schmähen mich meine Feinde, und die mich verspotten, fluchen mit meinem Namen.",
			"10": "<span> 10 </span> \tDenn ich esse Asche wie Brot und mische meinen Trank mit Tränen",
			"11": "<span> 11 </span> \tvor deinem Drohen und Zorn, weil du mich hochgehoben und zu Boden geworfen hast.",
			"12": "<span> 12 </span> \tMeine Tage sind dahin wie ein Schatten, und ich verdorre wie Gras.",
			"13": "<span> 13 </span> \tDu aber, HERR, bleibst ewiglich und dein Name für und für.",
			"14": "<span> 14 </span> \tDu wollest dich aufmachen und über Zion erbarmen; denn es ist Zeit, daß du ihm gnädig seist, und die Stunde ist gekommen",
			"15": "<span> 15 </span> \t- denn deine Knechte wollten gerne, daß es gebaut würde, und es jammert sie, daß es in Trümmern liegt -,",
			"16": "<span> 16 </span> \tdaß die Heiden den Namen des HERRN fürchten und alle Könige auf Erden deine Herrlichkeit.",
			"17": "<span> 17 </span> \tJa, der HERR baut Zion wieder und erscheint in seiner Herrlichkeit.",
			"18": "<span> 18 </span> \tEr wendet sich zum Gebet der Verlassenen und verschmäht ihr Gebet nicht.",
			"19": "<span> 19 </span> \tDas werde geschrieben für die Nachkommen; und das Volk, das er schafft, wird den HERRN loben.",
			"20": "<span> 20 </span> \tDenn er schaut von seiner heiligen Höhe, der HERR sieht vom Himmel auf die Erde,",
			"21": "<span> 21 </span> \tdaß er das Seufzen der Gefangenen höre und losmache die Kinder des Todes,",
			"22": "<span> 22 </span> \tdaß sie in Zion verkünden den Namen des HERRN und sein Lob in Jerusalem,",
			"23": "<span> 23 </span> \twenn die Völker zusammenkommen und die Königreiche, dem HERRN zu dienen.",
			"24": "<span> 24 </span> \tEr demütigt auf dem Wege meine Kraft, er verkürzt meine Tage.",
			"25": "<span> 25 </span> \tIch sage: Mein Gott, nimm mich nicht weg in der Hälfte meiner Tage! Deine Jahre währen für und für.",
			"26": "<span> 26 </span> \tDu hast vorzeiten die Erde gegründet, und die Himmel sind deiner Hände Werk.",
			"27": "<span> 27 </span> \tSie werden vergehen, du aber bleibst; sie werden alle veralten wie ein Gewand; wie ein Kleid wirst du sie wechseln, und sie werden verwandelt werden.",
			"28": "<span> 28 </span> \tDu aber bleibst, wie du bist, und deine Jahre nehmen kein Ende.\r\n[29]\tDie Söhne deiner Knechte bleiben wohnen, und ihr Geschlecht wird vor dir gedeihen."
		},
		"103": {
			"1": "<span>1</span> \tVON DAVID. Lobe den HERRN, meine Seele, und was in mir ist, seinen heiligen Namen!",
			"2": "<span> 2 </span> \tLobe den HERRN, meine Seele, und vergiß nicht, was er dir Gutes getan hat:",
			"3": "<span> 3 </span> \tder dir alle deine Sünde vergibt und heilet alle deine Gebrechen,",
			"4": "<span> 4 </span> \tder dein Leben vom Verderben erlöst, der dich krönet mit Gnade und Barmherzigkeit,",
			"5": "<span> 5 </span> \tder deinen Mund fröhlich macht, und du wieder jung wirst wie ein Adler.",
			"6": "<span> 6 </span> \tDer HERR schafft Gerechtigkeit und Recht allen, die Unrecht leiden.",
			"7": "<span> 7 </span> \tEr hat seine Wege Mose wissen lassen, die Kinder Israel sein Tun.",
			"8": "<span> 8 </span> \tBarmherzig und gnädig ist der HERR, geduldig und von großer Güte.",
			"9": "<span> 9 </span> \tEr wird nicht für immer hadern noch ewig zornig bleiben.",
			"10": "<span> 10 </span> \tEr handelt nicht mit uns nach unsern Sünden und vergilt uns nicht nach unsrer Missetat.",
			"11": "<span> 11 </span> \tDenn so hoch der Himmel über der Erde ist, läßt er seine Gnade walten über denen, die ihn fürchten.",
			"12": "<span> 12 </span> \tSo fern der Morgen ist vom Abend, läßt er unsre Übertretungen von uns sein.",
			"13": "<span> 13 </span> \tWie sich ein Vater über Kinder erbarmt, so erbarmt sich der HERR über die, die ihn fürchten.",
			"14": "<span> 14 </span> \tDenn er weiß, was für ein Gebilde wir sind; er gedenkt daran, daß wir Staub sind.",
			"15": "<span> 15 </span> \tEin Mensch ist in seinem Leben wie Gras, er blüht wie eine Blume auf dem Felde;",
			"16": "<span> 16 </span> \twenn der Wind darüber geht, so ist sie nimmer da, und ihre Stätte kennet sie nicht mehr.",
			"17": "<span> 17 </span> \tDie Gnade aber des HERRN währt von Ewigkeit zu Ewigkeit über denen, die ihn fürchten, und seine Gerechtigkeit auf Kindeskind",
			"18": "<span> 18 </span> \tbei denen, die seinen Bund halten und gedenken an seine Gebote, daß sie danach tun.",
			"19": "<span> 19 </span> \tDer HERR hat seinen Thron im Himmel errichtet, und sein Reich herrscht über alles.",
			"20": "<span> 20 </span> \tLobet den HERRN, ihr seine Engel, ihr starken Helden, die ihr seinen Befehl ausrichtet, daß man höre auf die Stimme seines Wortes!",
			"21": "<span> 21 </span> \tLobet den HERRN, alle seine Heerscharen, seine Diener, die ihr seinen Willen tut!",
			"22": "<span> 22 </span> \tLobet den HERRN, alle seine Werke, an allen Orten seiner Herrschaft!"
		},
		"104": {
			"1": "<span>1</span> \tLobe den HERRN, meine Seele! HERR, mein Gott, du bist sehr herrlich; du bist schön und prächtig geschmückt.",
			"2": "<span> 2 </span> \tLicht ist dein Kleid, das du anhast. Du breitest den Himmel aus wie einen Teppich;",
			"3": "<span> 3 </span> \tdu baust deine Gemächer über den Wassern. Du fährst auf den Wolken wie auf einem Wagen und kommst daher auf den Fittichen des Windes,",
			"4": "<span> 4 </span> \tder du machst Winde zu deinen Boten und Feuerflammen zu deinen Dienern;",
			"5": "<span> 5 </span> \tder du das Erdreich gegründet hast auf festen Boden, daß es bleibt immer und ewiglich.",
			"6": "<span> 6 </span> \tMit Fluten decktest du es wie mit einem Kleide, und die Wasser standen über den Bergen.",
			"7": "<span> 7 </span> \tAber vor deinem Schelten flohen sie, vor deinem Donner fuhren sie dahin.",
			"8": "<span> 8 </span> \tDie Berge stiegen hoch empor, und die Täler senkten sich herunter zum Ort, den du ihnen gegründet hast.",
			"9": "<span> 9 </span> \tDu hast eine Grenze gesetzt, darüber kommen sie nicht und dürfen nicht wieder das Erdreich bedecken.",
			"10": "<span> 10 </span> \tDu lässest Wasser in den Tälern quellen, daß sie zwischen den Bergen dahinfließen,",
			"11": "<span> 11 </span> \tdaß alle Tiere des Feldes trinken und das Wild seinen Durst lösche.",
			"12": "<span> 12 </span> \tDarüber sitzen die Vögel des Himmels und singen unter den Zweigen.",
			"13": "<span> 13 </span> \tDu feuchtest die Berge von oben her, du machst das Land voll Früchte, die du schaffest.",
			"14": "<span> 14 </span> \tDu lässest Gras wachsen für das Vieh und Saat zu Nutz den Menschen, daß du Brot aus der Erde hervorbringst,",
			"15": "<span> 15 </span> \tdaß der Wein erfreue des Menschen Herz und sein Antlitz schön werde vom Öl und das Brot des Menschen Herz stärke.",
			"16": "<span> 16 </span> \tDie Bäume des HERRN stehen voll Saft, die Zedern des Libanon, die er gepflanzt hat.",
			"17": "<span> 17 </span> \tDort nisten die Vögel, und die Reiher wohnen in den Wipfeln.",
			"18": "<span> 18 </span> \tDie hohen Berge geben dem Steinbock Zuflucht und die Felsklüfte dem Klippdachs.",
			"19": "<span> 19 </span> \tDu hast den Mond gemacht, das Jahr danach zu teilen; die Sonne weiß ihren Niedergang.",
			"20": "<span> 20 </span> \tDu machst Finsternis, daß es Nacht wird; da regen sich alle wilden Tiere,",
			"21": "<span> 21 </span> \tdie jungen Löwen, die da brüllen nach Raub und ihre Speise suchen von Gott.",
			"22": "<span> 22 </span> \tWenn aber die Sonne aufgeht, heben sie sich davon und legen sich in ihre Höhlen.",
			"23": "<span> 23 </span> \tSo geht dann der Mensch aus an seine Arbeit und an sein Werk bis an den Abend.",
			"24": "<span> 24 </span> \tHERR, wie sind deine Werke so groß und viel! Du hast sie alle weise geordnet, und die Erde ist voll deiner Güter.",
			"25": "<span> 25 </span> \tDa ist das Meer, das so groß und weit ist, da wimmelt's ohne Zahl, große und kleine Tiere.",
			"26": "<span> 26 </span> \tDort ziehen Schiffe dahin; da sind große Fische, die du gemacht hast, damit zu spielen.",
			"27": "<span> 27 </span> \tEs warten alle auf dich, daß du ihnen Speise gebest zur rechten Zeit.",
			"28": "<span> 28 </span> \tWenn du ihnen gibst, so sammeln sie; wenn du deine Hand auftust, so werden sie mit Gutem gesättigt.",
			"29": "<span> 29 </span> \tVerbirgst du dein Angesicht, so erschrecken sie; nimmst du weg ihren Odem, so vergehen sie und werden wieder Staub.",
			"30": "<span> 30 </span> \tDu sendest aus deinen Odem, so werden sie geschaffen, und du machst neu die Gestalt der Erde.",
			"31": "<span> 31 </span> \tDie Herrlichkeit des HERRN bleibe ewiglich, der HERR freue sich seiner Werke!",
			"32": "<span> 32 </span> \tEr schaut die Erde an, so bebt sie; er rührt die Berge an, so rauchen sie.",
			"33": "<span> 33 </span> \tIch will dem HERRN singen mein Leben lang und meinen Gott loben, solange ich bin.",
			"34": "<span> 34 </span> \tMein Reden möge ihm wohlgefallen. Ich freue mich des HERRN.",
			"35": "<span> 35 </span> \tDie Sünder sollen ein Ende nehmen auf Erden und die Gottlosen nicht mehr sein."
		},
		"105": {
			"1": "<span>1</span> \tDanket dem HERRN und rufet an seinen Namen; verkündigt sein Tun unter den Völkern!",
			"2": "<span> 2 </span> \tSinget und spielet ihm, redet von allen seinen Wundern!",
			"3": "<span> 3 </span> \tRühmet seinen heiligen Namen; es freue sich das Herz derer, die den HERRN suchen!",
			"4": "<span> 4 </span> \tFraget nach dem HERRN und nach seiner Macht, suchet sein Antlitz allezeit!",
			"5": "<span> 5 </span> \tGedenket seiner Wunderwerke, die er getan hat, seiner Zeichen und der Urteile seines Mundes,",
			"6": "<span> 6 </span> \tdu Geschlecht Abrahams, seines Knechts, ihr Söhne Jakobs, seine Auserwählten!",
			"7": "<span> 7 </span> \tEr ist der HERR, unser Gott, er richtet in aller Welt.",
			"8": "<span> 8 </span> \tEr gedenkt ewiglich an seinen Bund, an das Wort, das er verheißen hat für tausend Geschlechter,",
			"9": "<span> 9 </span> \tan den Bund, den er geschlossen hat mit Abraham, und an den Eid, den er Isaak geschworen hat.",
			"10": "<span> 10 </span> \tEr stellte ihn auf für Jakob als Satzung und für Israel als ewigen Bund",
			"11": "<span> 11 </span> \tund sprach: «Dir will ich das Land Kanaan geben, das Los eures Erbteils»,",
			"12": "<span> 12 </span> \tals sie noch gering waren an Zahl, nur wenige und Fremdlinge im Lande.",
			"13": "<span> 13 </span> \tUnd sie zogen von Volk zu Volk, von einem Königreich zum andern.",
			"14": "<span> 14 </span> \tEr ließ keinen Menschen ihnen Schaden tun und wies Könige zurecht um ihretwillen:",
			"15": "<span> 15 </span> \t«Tastet meine Gesalbten nicht an, und tut meinen Propheten kein Leid!»",
			"16": "<span> 16 </span> \tUnd er ließ eine Hungersnot ins Land kommen und nahm weg allen Vorrat an Brot.",
			"17": "<span> 17 </span> \tEr sandte einen Mann vor ihnen hin; Joseph wurde als Knecht verkauft.",
			"18": "<span> 18 </span> \tSie zwangen seine Füße in Fesseln, sein Leib mußte in Eisen liegen,",
			"19": "<span> 19 </span> \tbis sein Wort eintraf und die Rede des HERRN ihm recht gab.",
			"20": "<span> 20 </span> \tDa sandte der König hin und ließ ihn losgeben, der Herr über Völker, er gab ihn frei.",
			"21": "<span> 21 </span> \tEr setzte ihn zum Herrn über sein Haus, zum Herrscher über alle seine Güter,",
			"22": "<span> 22 </span> \tdaß er seine Fürsten unterwiese nach seinem Willen und seine Ältesten Weisheit lehrte.",
			"23": "<span> 23 </span> \tUnd Israel zog nach Ägypten, und Jakob ward ein Fremdling im Lande Hams.",
			"24": "<span> 24 </span> \tUnd der Herr ließ sein Volk sehr wachsen und machte sie mächtiger als ihre Feinde.",
			"25": "<span> 25 </span> \tDiesen verwandelte er das Herz, daß sie seinem Volk gram wurden und Arglist übten an seinen Knechten.",
			"26": "<span> 26 </span> \tEr sandte seinen Knecht Mose und Aaron, den er erwählt hatte.",
			"27": "<span> 27 </span> \tDie taten seine Zeichen unter ihnen und seine Wunder im Lande Hams.",
			"28": "<span> 28 </span> \tEr ließ Finsternis kommen und machte es finster; doch sie blieben ungehorsam seinen Worten.",
			"29": "<span> 29 </span> \tEr verwandelte ihre Wasser in Blut und tötete ihre Fische.",
			"30": "<span> 30 </span> \tIhr Land wimmelte von Fröschen bis in die Kammern ihrer Könige.",
			"31": "<span> 31 </span> \tEr gebot, da kam Ungeziefer, Stechmücken in all ihr Gebiet.",
			"32": "<span> 32 </span> \tEr gab ihnen Hagel statt Regen, Feuerflammen in ihrem Lande",
			"33": "<span> 33 </span> \tund schlug ihre Weinstöcke und Feigenbäume und zerbrach die Bäume in ihrem Gebiet.",
			"34": "<span> 34 </span> \tEr gebot, da kamen Heuschrecken geflogen und gekrochen ohne Zahl;",
			"35": "<span> 35 </span> \tsie fraßen alles, was da wuchs in ihrem Lande, und fraßen auch die Frucht ihres Ackers.",
			"36": "<span> 36 </span> \tEr schlug alle Erstgeburt in Ägypten, alle Erstlinge ihrer Kraft.",
			"37": "<span> 37 </span> \tEr führte sie heraus mit Silber und Gold; es war kein Gebrechlicher unter ihren Stämmen.",
			"38": "<span> 38 </span> \tÄgypten wurde froh, daß sie auszogen; denn Furcht vor ihnen war auf sie gefallen.",
			"39": "<span> 39 </span> \tEr breitete eine Wolke aus, sie zu decken, und ein Feuer, des Nachts zu leuchten.",
			"40": "<span> 40 </span> \tSie baten, da ließ er Wachteln kommen, und er sättigte sie mit Himmelsbrot.",
			"41": "<span> 41 </span> \tEr öffnete den Felsen, da flossen Wasser heraus, daß Bäche liefen in der dürren Wüste.",
			"42": "<span> 42 </span> \tDenn er gedachte an sein heiliges Wort und an Abraham, seinen Knecht.",
			"43": "<span> 43 </span> \tSo führte er sein Volk in Freuden heraus und seine Auserwählten mit Jubel",
			"44": "<span> 44 </span> \tund gab ihnen die Länder der Heiden, daß sie die Güter der Völker gewannen,",
			"45": "<span> 45 </span> \tdamit sie seine Gebote hielten und seine Gesetze bewahrten. Halleluja!"
		},
		"106": {
			"1": "<span>1</span> \tHalleluja! Danket dem HERRN; denn er ist freundlich, und seine Güte währet ewiglich.",
			"2": "<span> 2 </span> \tWer kann die großen Taten des HERRN alle erzählen und sein Lob genug verkündigen?",
			"3": "<span> 3 </span> \tWohl denen, die das Gebot halten und tun immerdar recht!",
			"4": "<span> 4 </span> \tHERR, gedenke meiner nach der Gnade, die du deinem Volk verheißen hast; erweise an uns deine Hilfe,",
			"5": "<span> 5 </span> \tdaß wir sehen das Heil deiner Auserwählten und uns freuen, daß es deinem Volke so gut geht, und uns rühmen mit denen, die dein eigen sind.",
			"6": "<span> 6 </span> \tWir haben gesündigt samt unsern Vätern, wir haben unrecht getan und sind gottlos gewesen.",
			"7": "<span> 7 </span> \tUnsre Väter in Ägypten wollten deine Wunder nicht verstehen. Sie gedachten nicht an deine große Güte und waren ungehorsam am Meer, am Schilfmeer.",
			"8": "<span> 8 </span> \tEr aber half ihnen um seines Namens willen, daß er seine Macht beweise.",
			"9": "<span> 9 </span> \tEr schalt das Schilfmeer, da wurde es trocken, und führte sie durch die Tiefen wie durch trockenes Land",
			"10": "<span> 10 </span> \tund half ihnen aus der Hand dessen, der sie haßte, und erlöste sie von der Hand des Feindes.",
			"11": "<span> 11 </span> \tUnd die Wasser ersäuften ihre Widersacher, daß nicht einer übrigblieb.",
			"12": "<span> 12 </span> \tDa glaubten sie an seine Worte und sangen sein Lob.",
			"13": "<span> 13 </span> \tAber sie vergaßen bald seine Werke, sie warteten nicht auf seinen Rat.",
			"14": "<span> 14 </span> \tUnd sie wurden lüstern in der Wüste und versuchten Gott in der Einöde.",
			"15": "<span> 15 </span> \tEr aber gab ihnen, was sie erbaten, und sandte ihnen genug, bis ihnen davor ekelte.",
			"16": "<span> 16 </span> \tUnd sie empörten sich wider Mose im Lager, wider Aaron, den Heiligen des HERRN.",
			"17": "<span> 17 </span> \tDie Erde tat sich auf und verschlang Dathan und deckte zu die Rotte Abirams,",
			"18": "<span> 18 </span> \tund Feuer wurde unter ihrer Rotte angezündet, die Flamme verbrannte die Gottlosen.",
			"19": "<span> 19 </span> \tSie machten ein Kalb am Horeb und beteten das gegossene Bild an",
			"20": "<span> 20 </span> \tund verwandelten die Herrlichkeit ihres Gottes in das Bild eines Ochsen, der Gras frißt.",
			"21": "<span> 21 </span> \tSie vergaßen Gott, ihren Heiland, der so große Dinge in Ägypten getan hatte,",
			"22": "<span> 22 </span> \tWunder im Lande Hams und schreckliche Taten am Schilfmeer.",
			"23": "<span> 23 </span> \tUnd er gedachte, sie zu vertilgen, wäre nicht Mose gewesen, sein Auserwählter; der trat vor ihm in die Bresche, seinen Grimm abzuwenden, daß er sie nicht verderbe.",
			"24": "<span> 24 </span> \tUnd sie achteten das köstliche Land gering; sie glaubten seinem Worte nicht",
			"25": "<span> 25 </span> \tund murrten in ihren Zelten; sie gehorchten der Stimme des HERRN nicht.",
			"26": "<span> 26 </span> \tDa erhob er seine Hand wider sie, daß er sie niederschlüge in der Wüste",
			"27": "<span> 27 </span> \tund würfe ihre Nachkommen unter die Heiden und zerstreute sie in die Länder.",
			"28": "<span> 28 </span> \tUnd sie hängten sich an den Baal-Peor und aßen von den Opfern für die Toten",
			"29": "<span> 29 </span> \tund erzürnten den Herrn mit ihrem Tun. Da brach die Plage herein über sie.",
			"30": "<span> 30 </span> \tDa trat Pinhas hinzu und vollzog das Gericht; da wurde der Plage gewehrt;",
			"31": "<span> 31 </span> \tdas wurde ihm gerechnet zur Gerechtigkeit von Geschlecht zu Geschlecht ewiglich.",
			"32": "<span> 32 </span> \tUnd sie erzürnten den Herrn am Haderwasser, und Mose ging es übel um ihretwillen;",
			"33": "<span> 33 </span> \tdenn sie erbitterten sein Herz, daß ihm unbedachte Worte entfuhren.",
			"34": "<span> 34 </span> \tAuch vertilgten sie die Völker nicht, wie ihnen der HERR doch geboten hatte,",
			"35": "<span> 35 </span> \tsondern sie ließen sich ein mit den Heiden und lernten ihre Werke",
			"36": "<span> 36 </span> \tund dienten ihren Götzen; die wurden ihnen zum Fallstrick.",
			"37": "<span> 37 </span> \tUnd sie opferten ihre Söhne und ihre Töchter den bösen Geistern",
			"38": "<span> 38 </span> \tund vergossen unschuldig Blut, das Blut ihrer Söhne und Töchter, die sie opferten den Götzen Kanaans, so daß das Land mit Blutschuld befleckt war.",
			"39": "<span> 39 </span> \tSie machten sich unrein mit ihren Werken und wurden abtrünnig durch ihr Tun.",
			"40": "<span> 40 </span> \tDa entbrannte der Zorn des HERRN über sein Volk, und sein Erbe wurde ihm zum Abscheu.",
			"41": "<span> 41 </span> \tEr gab sie in die Hand der Heiden, daß über sie herrschten, die ihnen gram waren.",
			"42": "<span> 42 </span> \tUnd ihre Feinde ängsteten sie, und sie wurden gedemütigt unter ihre Hand.",
			"43": "<span> 43 </span> \tEr rettete sie oftmals; aber sie erzürnten ihn mit ihrem Vorhaben und schwanden dahin um ihrer Missetat willen.",
			"44": "<span> 44 </span> \tDa sah er ihre Not an, als er ihre Klage hörte,",
			"45": "<span> 45 </span> \tund gedachte an seinen Bund mit ihnen, und es reute ihn nach seiner großen Güte.",
			"46": "<span> 46 </span> \tUnd er ließ sie Barmherzigkeit finden bei allen, die sie gefangen hielten.",
			"47": "<span> 47 </span> \tHilf uns, HERR, unser Gott, und bring uns zusammen aus den Heiden, daß wir preisen deinen heiligen Namen und uns rühmen, daß wir dich loben können!",
			"48": "<span> 48 </span> \tGelobt sei der HERR, der Gott Israels, von Ewigkeit zu Ewigkeit, und alles Volk spreche: Amen! Halleluja!"
		},
		"107": {
			"1": "<span>1</span> \tDanket dem HERRN; denn er ist freundlich, und seine Güte währet ewiglich.",
			"2": "<span> 2 </span> \tSo sollen sagen, die erlöst sind durch den HERRN, die er aus der Not erlöst hat,",
			"3": "<span> 3 </span> \tdie er aus den Ländern zusammengebracht hat von Osten und Westen, von Norden und Süden.",
			"4": "<span> 4 </span> \tDie irre gingen in der Wüste, auf ungebahntem Wege, und fanden keine Stadt, in der sie wohnen konnten,",
			"5": "<span> 5 </span> \tdie hungrig und durstig waren und deren Seele verschmachtete,",
			"6": "<span> 6 </span> \tdie dann zum HERRN riefen in ihrer Not, und er errettete sie aus ihren Ängsten",
			"7": "<span> 7 </span> \tund führte sie den richtigen Weg, daß sie kamen zur Stadt, in der sie wohnen konnten:",
			"8": "<span> 8 </span> \tdie sollen dem HERRN danken für seine Güte und für seine Wunder, die er an den Menschenkindern tut,",
			"9": "<span> 9 </span> \tdaß er sättigt die durstige Seele und die Hungrigen füllt mit Gutem.",
			"10": "<span> 10 </span> \tDie da sitzen mußten in Finsternis und Dunkel, gefangen in Zwang und Eisen,",
			"11": "<span> 11 </span> \tweil sie Gottes Geboten ungehorsam waren und den Ratschluß des Höchsten verachtet hatten,",
			"12": "<span> 12 </span> \tso daß er ihr Herz durch Unglück beugte und sie dalagen und ihnen niemand half,",
			"13": "<span> 13 </span> \tdie dann zum HERRN riefen in ihrer Not, und er half ihnen aus ihren Ängsten",
			"14": "<span> 14 </span> \tund führte sie aus Finsternis und Dunkel und zerriß ihre Bande:",
			"15": "<span> 15 </span> \tdie sollen dem HERRN danken für seine Güte und für seine Wunder, die er an den Menschenkindern tut,",
			"16": "<span> 16 </span> \tdaß er zerbricht eherne Türen und zerschlägt eiserne Riegel.",
			"17": "<span> 17 </span> \tDie Toren, die geplagt waren um ihrer Übertretung und um ihrer Sünde willen,",
			"18": "<span> 18 </span> \tdaß ihnen ekelte vor aller Speise und sie todkrank wurden,",
			"19": "<span> 19 </span> \tdie dann zum HERRN riefen in ihrer Not, und er half ihnen aus ihren Ängsten,",
			"20": "<span> 20 </span> \ter sandte sein Wort und machte sie gesund und errettete sie, daß sie nicht starben:",
			"21": "<span> 21 </span> \tdie sollen dem HERRN danken für seine Güte und für seine Wunder, die er an den Menschenkindern tut,",
			"22": "<span> 22 </span> \tund sollen Dank opfern und seine Werke erzählen mit Freuden.",
			"23": "<span> 23 </span> \tDie mit Schiffen auf dem Meere fuhren und trieben ihren Handel auf großen Wassern,",
			"24": "<span> 24 </span> \tdie des HERRN Werke erfahren haben und seine Wunder auf dem Meer,",
			"25": "<span> 25 </span> \twenn er sprach und einen Sturmwind erregte, der die Wellen erhob,",
			"26": "<span> 26 </span> \tund sie gen Himmel fuhren und in den Abgrund sanken, daß ihre Seele vor Angst verzagte,",
			"27": "<span> 27 </span> \tdaß sie taumelten und wankten wie ein Trunkener und wußten keinen Rat mehr,",
			"28": "<span> 28 </span> \tdie dann zum HERRN schrien in ihrer Not, und er führte sie aus ihren Ängsten",
			"29": "<span> 29 </span> \tund stillte das Ungewitter, daß die Wellen sich legten",
			"30": "<span> 30 </span> \tund sie froh wurden, daß es still geworden war und er sie zum erwünschten Lande brachte:",
			"31": "<span> 31 </span> \tdie sollen dem HERRN danken für seine Güte und für seine Wunder, die er an den Menschenkindern tut,",
			"32": "<span> 32 </span> \tund ihn in der Gemeinde preisen und bei den Alten rühmen.",
			"33": "<span> 33 </span> \tEr machte Bäche trocken und ließ Wasserquellen versiegen,",
			"34": "<span> 34 </span> \tdaß fruchtbares Land zur Salzwüste wurde wegen der Bosheit derer, die dort wohnten.",
			"35": "<span> 35 </span> \tEr machte das Trockene wieder wasserreich und gab dem dürren Lande Wasserquellen",
			"36": "<span> 36 </span> \tund ließ die Hungrigen dort bleiben, daß sie eine Stadt bauten, in der sie wohnen konnten,",
			"37": "<span> 37 </span> \tund Äcker besäten und Weinberge pflanzten, die jährlich Früchte trugen.",
			"38": "<span> 38 </span> \tUnd er segnete sie, daß sie sich sehr mehrten, und gab ihnen viel Vieh.",
			"39": "<span> 39 </span> \tAber sie wurden gering an Zahl und geschwächt von der Last des Unglücks und des Kummers.",
			"40": "<span> 40 </span> \tEr schüttete Verachtung aus auf die Fürsten und ließ sie irren in der Wüste, wo kein Weg ist;",
			"41": "<span> 41 </span> \taber die Armen schützte er vor Elend und mehrte ihr Geschlecht wie eine Herde.",
			"42": "<span> 42 </span> \tDas werden die Frommen sehen und sich freuen, und aller Bosheit wird das Maul gestopft werden.",
			"43": "<span> 43 </span> \tWer ist weise und behält dies? Der wird merken, wieviel Wohltaten der HERR erweist."
		},
		"108": {
			"1": "<span>1</span> \tEIN PSALMLIED DAVIDS.",
			"2": "<span> 2 </span> \tGott, mein Herz ist bereit, ich will singen und spielen. Wach auf, meine Seele!",
			"3": "<span> 3 </span> \tWach auf, Psalter und Harfe! Ich will das Morgenrot wecken.",
			"4": "<span> 4 </span> \tIch will dir danken, HERR, unter den Völkern, ich will dir lobsingen unter den Leuten.",
			"5": "<span> 5 </span> \tDenn deine Gnade reicht, so weit der Himmel ist, und deine Treue, so weit die Wolken gehen.",
			"6": "<span> 6 </span> \tErhebe dich, Gott, über den Himmel und deine Herrlichkeit über alle Lande!",
			"7": "<span> 7 </span> \tLaß deine Freunde errettet werden, dazu hilf mit deiner Rechten und erhöre uns!",
			"8": "<span> 8 </span> \tGott hat in seinem Heiligtum geredet: Ich will frohlocken; ich will Sichem verteilen und das Tal Sukkoth ausmessen.",
			"9": "<span> 9 </span> \tGilead ist mein, Manasse ist auch mein, und Ephraim ist der Schutz meines Haupts, Juda ist mein Zepter.",
			"10": "<span> 10 </span> \tMoab ist mein Waschbecken, ich will meinen Schuh auf Edom werfen, über die Philister will ich jauchzen.",
			"11": "<span> 11 </span> \tWer wird mich führen in die feste Stadt? Wer wird mich nach Edom leiten?",
			"12": "<span> 12 </span> \tWirst du es nicht tun, Gott, der du uns verstoßen hast und ziehst nicht aus, Gott, mit unserm Heer?",
			"13": "<span> 13 </span> \tSchaff uns Beistand vor dem Feind; denn Menschenhilfe ist nichts nütze.\r\n[14]\tMit Gott wollen wir Taten tun. Er wird unsre Feinde niedertreten."
		},
		"109": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN. Gott, mein Ruhm, schweige nicht!",
			"2": "<span> 2 </span> \tDenn sie haben ihr gottloses Lügenmaul wider mich aufgetan. Sie reden wider mich mit falscher Zunge",
			"3": "<span> 3 </span> \tund reden giftig wider mich allenthalben und streiten wider mich ohne Grund.",
			"4": "<span> 4 </span> \tDafür, daß ich sie liebe, feinden sie mich an; ich aber bete.",
			"5": "<span> 5 </span> \tSie erweisen mir Böses für Gutes und Haß für Liebe.",
			"6": "<span> 6 </span> \tGib ihm einen Gottlosen zum Gegner, und ein Verkläger stehe zu seiner Rechten.",
			"7": "<span> 7 </span> \tWenn er gerichtet wird, soll er schuldig gesprochen werden, und sein Gebet werde zur Sünde.",
			"8": "<span> 8 </span> \tSeiner Tage sollen wenige werden, und sein Amt soll ein andrer empfangen.",
			"9": "<span> 9 </span> \tSeine Kinder sollen Waisen werden und sein Weib eine Witwe.",
			"10": "<span> 10 </span> \tSeine Kinder sollen umherirren und betteln und vertrieben werden aus ihren Trümmern.",
			"11": "<span> 11 </span> \tEs soll der Wucherer alles fordern, was er hat, und Fremde sollen seine Güter rauben.",
			"12": "<span> 12 </span> \tUnd niemand soll ihm Gutes tun, und niemand erbarme sich seiner Waisen.",
			"13": "<span> 13 </span> \tSeine Nachkommen sollen ausgerottet werden, ihr Name soll schon im zweiten Glied getilgt werden.",
			"14": "<span> 14 </span> \tDer Schuld seiner Väter soll gedacht werden vor dem HERRN, und seiner Mutter Sünde soll nicht getilgt werden.",
			"15": "<span> 15 </span> \tDer HERR soll sie nie mehr aus den Augen lassen, und ihr Andenken soll ausgerottet werden auf Erden,",
			"16": "<span> 16 </span> \tweil er so gar keine Barmherzigkeit übte, sondern verfolgte den Elenden und Armen und den Betrübten, ihn zu töten.",
			"17": "<span> 17 </span> \tEr liebte den Fluch, so komme er auch über ihn; er wollte den Segen nicht, so bleibe er auch fern von ihm.",
			"18": "<span> 18 </span> \tEr zog den Fluch an wie sein Hemd; der dringe in ihn hinein wie Wasser und wie Öl in seine Gebeine;",
			"19": "<span> 19 </span> \ter werde ihm wie ein Kleid, das er anhat, und wie ein Gürtel, mit dem er allezeit sich gürtet.",
			"20": "<span> 20 </span> \tSo geschehe denen vom HERRN, die wider mich sind und die Böses reden wider mich.",
			"21": "<span> 21 </span> \tAber du, HERR, sei du mit mir um deines Namens willen; denn deine Gnade ist mein Trost: errette mich!",
			"22": "<span> 22 </span> \tDenn ich bin arm und elend; mein Herz ist zerschlagen in mir.",
			"23": "<span> 23 </span> \tIch fahre dahin wie ein Schatten, der schwindet, und werde abgeschüttelt wie Heuschrecken.",
			"24": "<span> 24 </span> \tMeine Knie sind schwach vom Fasten, und mein Leib ist mager und hat kein Fett.",
			"25": "<span> 25 </span> \tIch bin ihnen zum Spott geworden; wenn sie mich sehen, schütteln sie den Kopf.",
			"26": "<span> 26 </span> \tSteh mir bei, HERR, mein Gott! Hilf mir nach deiner Gnade,",
			"27": "<span> 27 </span> \tund laß sie innewerden, daß dies deine Hand ist und du, HERR, das tust.",
			"28": "<span> 28 </span> \tFluchen sie, so segne du. Erheben sie sich gegen mich, so sollen sie zuschanden werden; aber dein Knecht soll sich freuen.",
			"29": "<span> 29 </span> \tMeine Widersacher sollen mit Schmach angezogen und mit ihrer Schande bekleidet werden wie mit einem Mantel.",
			"30": "<span> 30 </span> \tIch will dem HERRN sehr danken mit meinem Munde und ihn rühmen vor der Menge.",
			"31": "<span> 31 </span> \tDenn er steht dem Armen zur Rechten, daß er ihm helfe von denen, die ihn verurteilen."
		},
		"110": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. Der HERR sprach zu meinem Herrn: «Setze dich zu meiner Rechten, bis ich deine Feinde zum Schemel deiner Füße mache.»",
			"2": "<span> 2 </span> \tDer HERR wird das Zepter deiner Macht ausstrecken aus Zion. Herrsche mitten unter deinen Feinden!",
			"3": "<span> 3 </span> \tWenn du dein Heer aufbietest, wird dir dein Volk willig folgen in heiligem Schmuck. Deine Söhne werden dir geboren wie der Tau aus der Morgenröte.",
			"4": "<span> 4 </span> \tDer HERR hat geschworen, und es wird ihn nicht gereuen: «Du bist ein Priester ewiglich nach der Weise Melchisedeks.»",
			"5": "<span> 5 </span> \tDer Herr zu deiner Rechten wird zerschmettern die Könige am Tage seines Zorns.",
			"6": "<span> 6 </span> \tEr wird richten unter den Heiden, wird viele erschlagen, wird Häupter zerschmettern auf weitem Gefilde.",
			"7": "<span> 7 </span> \tEr wird trinken vom Bach auf dem Wege, darum wird er das Haupt emporheben."
		},
		"111": {
			"1": "<span>1</span> \tHalleluja! Ich danke dem HERRN von ganzem Herzen im Rate der Frommen und in der Gemeinde.",
			"2": "<span> 2 </span> \tGroß sind die Werke des HERRN; wer sie erforscht, der hat Freude daran.",
			"3": "<span> 3 </span> \tWas er tut, das ist herrlich und prächtig, und seine Gerechtigkeit bleibt ewiglich.",
			"4": "<span> 4 </span> \tEr hat ein Gedächtnis gestiftet seiner Wunder, der gnädige und barmherzige HERR.",
			"5": "<span> 5 </span> \tEr gibt Speise denen, die ihn fürchten; er gedenkt ewig an seinen Bund.",
			"6": "<span> 6 </span> \tEr läßt verkündigen seine gewaltigen Taten seinem Volk, daß er ihnen gebe das Erbe der Heiden.",
			"7": "<span> 7 </span> \tDie Werke seiner Hände sind Wahrheit und Recht; alle seine Ordnungen sind beständig.",
			"8": "<span> 8 </span> \tSie stehen fest für immer und ewig; sie sind recht und verläßlich.",
			"9": "<span> 9 </span> \tEr sendet eine Erlösung seinem Volk; er verheißt, daß sein Bund ewig bleiben soll. Heilig und hehr ist sein Name.",
			"10": "<span> 10 </span> \tDie Furcht des HERRN ist der Weisheit Anfang. Klug sind alle, die danach tun. Sein Lob bleibet ewiglich."
		},
		"112": {
			"1": "<span>1</span> \tHalleluja! Wohl dem, der den HERRN fürchtet, der große Freude hat an seinen Geboten!",
			"2": "<span> 2 </span> \tSein Geschlecht wird gewaltig sein im Lande; die Kinder der Frommen werden gesegnet sein.",
			"3": "<span> 3 </span> \tReichtum und Fülle wird in ihrem Hause sein, und ihre Gerechtigkeit bleibt ewiglich.",
			"4": "<span> 4 </span> \tDen Frommen geht das Licht auf in der Finsternis von dem Gnädigen, Barmherzigen und Gerechten.",
			"5": "<span> 5 </span> \tWohl dem, der barmherzig ist und gerne leiht und das Seine tut, wie es recht ist!",
			"6": "<span> 6 </span> \tDenn er wird ewiglich bleiben; der Gerechte wird nimmermehr vergessen.",
			"7": "<span> 7 </span> \tVor schlimmer Kunde fürchtet er sich nicht; sein Herz hofft unverzagt auf den HERRN.",
			"8": "<span> 8 </span> \tSein Herz ist getrost und fürchtet sich nicht, bis er auf seine Feinde herabsieht.",
			"9": "<span> 9 </span> \tEr streut aus und gibt den Armen; seine Gerechtigkeit bleibt ewiglich. Seine Kraft wird hoch in Ehren stehen.",
			"10": "<span> 10 </span> \tDer Gottlose wird's sehen, und es wird ihn verdrießen; mit den Zähnen wird er knirschen und vergehen. Denn was die Gottlosen wollen, das wird zunichte."
		},
		"113": {
			"1": "<span>1</span> \tHalleluja! Lobet, ihr Knechte des HERRN, lobet den Namen des HERRN!",
			"2": "<span> 2 </span> \tGelobt sei der Name des HERRN von nun an bis in Ewigkeit!",
			"3": "<span> 3 </span> \tVom Aufgang der Sonne bis zu ihrem Niedergang sei gelobet der Name des HERRN!",
			"4": "<span> 4 </span> \tDer HERR ist hoch über alle Völker; seine Herrlichkeit reicht, so weit der Himmel ist.",
			"5": "<span> 5 </span> \tWer ist wie der HERR, unser Gott, im Himmel und auf Erden?",
			"6": "<span> 6 </span> \tDer oben thront in der Höhe, der herniederschaut in die Tiefe,",
			"7": "<span> 7 </span> \tder den Geringen aufrichtet aus dem Staube und erhöht den Armen aus dem Schmutz,",
			"8": "<span> 8 </span> \tdaß er ihn setze neben die Fürsten, neben die Fürsten seines Volkes;",
			"9": "<span> 9 </span> \tder die Unfruchtbare im Hause zu Ehren bringt, daß sie eine fröhliche Kindermutter wird. Halleluja!"
		},
		"114": {
			"1": "<span>1</span> \tAls Israel aus Ägypten zog, das Haus Jakob aus dem fremden Volk,",
			"2": "<span> 2 </span> \tda wurde Juda sein Heiligtum, Israel sein Königreich.",
			"3": "<span> 3 </span> \tDas Meer sah es und floh, der Jordan wandte sich zurück.",
			"4": "<span> 4 </span> \tDie Berge hüpften wie die Lämmer, die Hügel wie die jungen Schafe.",
			"5": "<span> 5 </span> \tWas war mit dir, du Meer, daß du flohest, und mit dir, Jordan, daß du dich zurückwandtest?",
			"6": "<span> 6 </span> \tIhr Berge, daß ihr hüpftet wie die Lämmer, ihr Hügel, wie die jungen Schafe?",
			"7": "<span> 7 </span> \tVor dem Herrn erbebe, du Erde, vor dem Gott Jakobs,",
			"8": "<span> 8 </span> \tder den Felsen wandelte in einen See und die Steine in Wasserquellen!"
		},
		"115": {
			"1": "<span>1</span> \tNicht uns, HERR, nicht uns, sondern deinem Namen gib Ehre um deiner Gnade und Treue willen!",
			"2": "<span> 2 </span> \tWarum sollen die Heiden sagen: Wo ist denn ihr Gott?",
			"3": "<span> 3 </span> \tUnser Gott ist im Himmel; er kann schaffen, was er will.",
			"4": "<span> 4 </span> \tIhre Götzen aber sind Silber und Gold, von Menschenhänden gemacht.",
			"5": "<span> 5 </span> \tSie haben Mäuler und reden nicht, sie haben Augen und sehen nicht.",
			"6": "<span> 6 </span> \tSie haben Ohren und hören nicht, sie haben Nasen und riechen nicht,",
			"7": "<span> 7 </span> \tsie haben Hände und greifen nicht, Füße haben sie und gehen nicht, und kein Laut kommt aus ihrer Kehle.",
			"8": "<span> 8 </span> \tDie solche Götzen machen, sind ihnen gleich, alle, die auf sie hoffen.",
			"9": "<span> 9 </span> \tAber Israel hoffe auf den HERRN! Er ist ihre Hilfe und Schild.",
			"10": "<span> 10 </span> \tDas Haus Aaron hoffe auf den HERRN! Er ist ihre Hilfe und Schild.",
			"11": "<span> 11 </span> \tDie ihr den HERRN fürchtet, hoffet auf den HERRN! Er ist ihre Hilfe und Schild.",
			"12": "<span> 12 </span> \tDer HERR denkt an uns und segnet uns; er segnet das Haus Israel, er segnet das Haus Aaron.",
			"13": "<span> 13 </span> \tEr segnet, die den HERRN fürchten, die Kleinen und die Großen.",
			"14": "<span> 14 </span> \tDer HERR segne euch je mehr und mehr, euch und eure Kinder!",
			"15": "<span> 15 </span> \tIhr seid die Gesegneten des HERRN, der Himmel und Erde gemacht hat.",
			"16": "<span> 16 </span> \tDer Himmel ist der Himmel des HERRN; aber die Erde hat er den Menschenkindern gegeben.",
			"17": "<span> 17 </span> \tDie Toten werden dich, HERR, nicht loben, keiner, der hinunterfährt in die Stille;",
			"18": "<span> 18 </span> \taber wir loben den HERRN von nun an bis in Ewigkeit. Halleluja!"
		},
		"116": {
			"1": "<span>1</span> \tIch liebe den HERRN, denn er hört die Stimme meines Flehens.",
			"2": "<span> 2 </span> \tEr neigte sein Ohr zu mir; darum will ich mein Leben lang ihn anrufen.",
			"3": "<span> 3 </span> \tStricke des Todes hatten mich umfangen, des Totenreichs Schrecken hatten mich getroffen; ich kam in Jammer und Not.",
			"4": "<span> 4 </span> \tAber ich rief an den Namen des HERRN: Ach, HERR, errette mich!",
			"5": "<span> 5 </span> \tDer HERR ist gnädig und gerecht, und unser Gott ist barmherzig.",
			"6": "<span> 6 </span> \tDer HERR behütet die Unmündigen; wenn ich schwach bin, so hilft er mir.",
			"7": "<span> 7 </span> \tSei nun wieder zufrieden, meine Seele; denn der HERR tut dir Gutes.",
			"8": "<span> 8 </span> \tDenn du hast meine Seele vom Tode errettet, mein Auge von den Tränen, meinen Fuß vom Gleiten.",
			"9": "<span> 9 </span> \tIch werde wandeln vor dem HERRN im Lande der Lebendigen.",
			"10": "<span> 10 </span> \tIch glaube, auch wenn ich sage: Ich werde sehr geplagt.",
			"11": "<span> 11 </span> \tIch sprach in meinem Zagen: Alle Menschen sind Lügner.",
			"12": "<span> 12 </span> \tWie soll ich dem HERRN vergelten all seine Wohltat, die er an mir tut?",
			"13": "<span> 13 </span> \tIch will den Kelch des Heils nehmen und des HERRN Namen anrufen.",
			"14": "<span> 14 </span> \tIch will meine Gelübde dem HERRN erfüllen vor all seinem Volk.",
			"15": "<span> 15 </span> \tDer Tod seiner Heiligen wiegt schwer vor dem HERRN.",
			"16": "<span> 16 </span> \tAch, HERR, ich bin dein Knecht, ich bin dein Knecht, der Sohn deiner Magd; du hast meine Bande zerrissen.",
			"17": "<span> 17 </span> \tDir will ich Dank opfern und des HERRN Namen anrufen.",
			"18": "<span> 18 </span> \tIch will meine Gelübde dem HERRN erfüllen vor all seinem Volk",
			"19": "<span> 19 </span> \tin den Vorhöfen am Hause des HERRN, in dir, Jerusalem. Halleluja!"
		},
		"117": {
			"1": "<span>1</span> \tLobet den HERRN, alle Heiden! Preiset ihn, alle Völker!",
			"2": "<span> 2 </span> \tDenn seine Gnade und Wahrheit waltet über uns in Ewigkeit. Halleluja!"
		},
		"118": {
			"1": "<span>1</span> \tDanket dem HERRN; denn er ist freundlich, und seine Güte währet ewiglich.",
			"2": "<span> 2 </span> \tEs sage nun Israel: Seine Güte währet ewiglich.",
			"3": "<span> 3 </span> \tEs sage nun das Haus Aaron: Seine Güte währet ewiglich.",
			"4": "<span> 4 </span> \tEs sagen nun, die den HERRN fürchten: Seine Güte währet ewiglich.",
			"5": "<span> 5 </span> \tIn der Angst rief ich den HERRN an; und der HERR erhörte mich und tröstete mich.",
			"6": "<span> 6 </span> \tDer HERR ist mit mir, darum fürchte ich mich nicht; was können mir Menschen tun?",
			"7": "<span> 7 </span> \tDer HERR ist mit mir, mir zu helfen; und ich werde herabsehen auf meine Feinde.",
			"8": "<span> 8 </span> \tEs ist gut, auf den HERRN vertrauen und nicht sich verlassen auf Menschen.",
			"9": "<span> 9 </span> \tEs ist gut, auf den HERRN vertrauen und nicht sich verlassen auf Fürsten.",
			"10": "<span> 10 </span> \tAlle Heiden umgeben mich; aber im Namen des HERRN will ich sie abwehren.",
			"11": "<span> 11 </span> \tSie umgeben mich von allen Seiten; aber im Namen des HERRN will ich sie abwehren.",
			"12": "<span> 12 </span> \tSie umgeben mich wie Bienen, sie entbrennen wie ein Feuer in Dornen; aber im Namen des HERRN will ich sie abwehren.",
			"13": "<span> 13 </span> \tMan stößt mich, daß ich fallen soll; aber der HERR hilft mir.",
			"14": "<span> 14 </span> \tDer HERR ist meine Macht und mein Psalm und ist mein Heil.",
			"15": "<span> 15 </span> \tMan singt mit Freuden vom Sieg in den Hütten der Gerechten: Die Rechte des HERRN behält den Sieg!",
			"16": "<span> 16 </span> \tDie Rechte des HERRN ist erhöht; die Rechte des HERRN behält den Sieg!",
			"17": "<span> 17 </span> \tIch werde nicht sterben, sondern leben und des HERRN Werke verkündigen.",
			"18": "<span> 18 </span> \tDer HERR züchtigt mich schwer; aber er gibt mich dem Tode nicht preis.",
			"19": "<span> 19 </span> \tTut mir auf die Tore der Gerechtigkeit, daß ich durch sie einziehe und dem HERRN danke.",
			"20": "<span> 20 </span> \tDas ist das Tor des HERRN; die Gerechten werden dort einziehen.",
			"21": "<span> 21 </span> \tIch danke dir, daß du mich erhört hast und hast mir geholfen.",
			"22": "<span> 22 </span> \tDer Stein, den die Bauleute verworfen haben, ist zum Eckstein geworden.",
			"23": "<span> 23 </span> \tDas ist vom HERRN geschehen und ist ein Wunder vor unsern Augen.",
			"24": "<span> 24 </span> \tDies ist der Tag, den der HERR macht; laßt uns freuen und fröhlich an ihm sein.",
			"25": "<span> 25 </span> \tO HERR, hilf! O HERR, laß wohlgelingen!",
			"26": "<span> 26 </span> \tGelobt sei, der da kommt im Namen des HERRN! Wir segnen euch, die ihr vom Hause des HERRN seid.",
			"27": "<span> 27 </span> \tDer HERR ist Gott, der uns erleuchtet. Schmückt das Fest mit Maien bis an die Hörner des Altars!",
			"28": "<span> 28 </span> \tDu bist mein Gott, und ich danke dir; mein Gott, ich will dich preisen.",
			"29": "<span> 29 </span> \tDanket dem HERRN; denn er ist freundlich, und seine Güte währet ewiglich."
		},
		"119": {
			"1": "<span>1</span> \tWohl denen, die ohne Tadel leben, die im Gesetz des HERRN wandeln!",
			"2": "<span> 2 </span> \tWohl denen, die sich an seine Mahnungen halten, die ihn von ganzem Herzen suchen,",
			"3": "<span> 3 </span> \tdie auf seinen Wegen wandeln und kein Unrecht tun.",
			"4": "<span> 4 </span> \tDu hast geboten, fleißig zu halten deine Befehle.",
			"5": "<span> 5 </span> \tO daß mein Leben deine Gebote mit ganzem Ernst hielte.",
			"6": "<span> 6 </span> \tWenn ich schaue allein auf deine Gebote, so werde ich nicht zuschanden.",
			"7": "<span> 7 </span> \tIch danke dir mit aufrichtigem Herzen, daß du mich lehrst die Ordnungen deiner Gerechtigkeit.",
			"8": "<span> 8 </span> \tDeine Gebote will ich halten; verlaß mich nimmermehr!",
			"9": "<span> 9 </span> \tWie wird ein junger Mann seinen Weg unsträflich gehen? Wenn er sich hält an deine Worte.",
			"10": "<span> 10 </span> \tIch suche dich von ganzem Herzen; laß mich nicht abirren von deinen Geboten.",
			"11": "<span> 11 </span> \tIch behalte dein Wort in meinem Herzen, damit ich nicht wider dich sündige.",
			"12": "<span> 12 </span> \tGelobet seist du, HERR! Lehre mich deine Gebote!",
			"13": "<span> 13 </span> \tIch will mit meinen Lippen erzählen alle Weisungen deines Mundes.",
			"14": "<span> 14 </span> \tIch freue mich über den Weg, den deine Mahnungen zeigen, wie über großen Reichtum.",
			"15": "<span> 15 </span> \tIch rede von dem, was du befohlen hast, und schaue auf deine Wege.",
			"16": "<span> 16 </span> \tIch habe Freude an deinen Satzungen und vergesse deine Worte nicht.",
			"17": "<span> 17 </span> \tTu wohl deinem Knecht, daß ich lebe und dein Wort halte.",
			"18": "<span> 18 </span> \tÖffne mir die Augen, daß ich sehe die Wunder an deinem Gesetz.",
			"19": "<span> 19 </span> \tIch bin ein Gast auf Erden; verbirg deine Gebote nicht vor mir.",
			"20": "<span> 20 </span> \tMeine Seele verzehrt sich vor Verlangen nach deinen Ordnungen allezeit.",
			"21": "<span> 21 </span> \tDu schiltst die Stolzen; verflucht sind, die von deinen Geboten abirren.",
			"22": "<span> 22 </span> \tWende von mir Schmach und Verachtung; denn ich halte mich an deine Mahnungen.",
			"23": "<span> 23 </span> \tFürsten sitzen da und reden wider mich; aber dein Knecht sinnt nach über deine Gebote.",
			"24": "<span> 24 </span> \tIch habe Freude an deinen Mahnungen; sie sind meine Ratgeber.",
			"25": "<span> 25 </span> \tMeine Seele liegt im Staube; erquicke mich nach deinem Wort.",
			"26": "<span> 26 </span> \tIch erzähle dir meine Wege, und du erhörst mich; lehre mich deine Gebote.",
			"27": "<span> 27 </span> \tLaß mich verstehen den Weg deiner Befehle, so will ich reden von deinen Wundern.",
			"28": "<span> 28 </span> \tIch gräme mich, daß mir die Seele verschmachtet; stärke mich nach deinem Wort.",
			"29": "<span> 29 </span> \tHalte fern von mir den Weg der Lüge und gib mir in Gnaden dein Gesetz.",
			"30": "<span> 30 </span> \tIch habe erwählt den Weg der Wahrheit, deine Weisungen hab ich vor mich gestellt.",
			"31": "<span> 31 </span> \tIch halte an deinen Mahnungen fest; HERR, laß mich nicht zuschanden werden!",
			"32": "<span> 32 </span> \tIch laufe den Weg deiner Gebote; denn du tröstest mein Herz.",
			"33": "<span> 33 </span> \tZeige mir, HERR, den Weg deiner Gebote, daß ich sie bewahre bis ans Ende.",
			"34": "<span> 34 </span> \tUnterweise mich, daß ich bewahre dein Gesetz und halte es von ganzem Herzen.",
			"35": "<span> 35 </span> \tFühre mich auf dem Steig deiner Gebote; denn ich habe Gefallen daran.",
			"36": "<span> 36 </span> \tNeige mein Herz zu deinen Mahnungen und nicht zur Habsucht.",
			"37": "<span> 37 </span> \tWende meine Augen ab, daß sie nicht sehen nach unnützer Lehre, und erquicke mich auf deinem Wege.",
			"38": "<span> 38 </span> \tErfülle deinem Knecht dein Wort, daß ich dich fürchte.",
			"39": "<span> 39 </span> \tWende von mir die Schmach, die ich scheue; denn deine Ordnungen sind gut.",
			"40": "<span> 40 </span> \tSiehe, ich begehre deine Befehle; erquicke mich mit deiner Gerechtigkeit.",
			"41": "<span> 41 </span> \tHERR, laß mir deine Gnade widerfahren, deine Hilfe nach deinem Wort,",
			"42": "<span> 42 </span> \tdaß ich antworten kann dem, der mich schmäht; denn ich verlasse mich auf dein Wort.",
			"43": "<span> 43 </span> \tUnd nimm ja nicht von meinem Munde das Wort der Wahrheit; denn ich hoffe auf deine Ordnungen.",
			"44": "<span> 44 </span> \tIch will dein Gesetz halten allezeit, immer und ewiglich.",
			"45": "<span> 45 </span> \tUnd ich wandle fröhlich; denn ich suche deine Befehle.",
			"46": "<span> 46 </span> \tIch rede von deinen Zeugnissen vor Königen und schäme mich nicht.",
			"47": "<span> 47 </span> \tIch habe Freude an deinen Geboten, sie sind mir sehr lieb,",
			"48": "<span> 48 </span> \tund hebe meine Hände auf zu deinen Geboten, die mir lieb sind, und rede von deinen Weisungen.",
			"49": "<span> 49 </span> \tDenke an das Wort, das du deinem Knecht gabst, und laß mich darauf hoffen.",
			"50": "<span> 50 </span> \tDas ist mein Trost in meinem Elend, daß dein Wort mich erquickt.",
			"51": "<span> 51 </span> \tDie Stolzen treiben ihren Spott mit mir; dennoch weiche ich nicht von deinem Gesetz.",
			"52": "<span> 52 </span> \tHERR, wenn ich an deine ewigen Ordnungen denke, so werde ich getröstet.",
			"53": "<span> 53 </span> \tZorn erfaßt mich über die Gottlosen, die dein Gesetz verlassen.",
			"54": "<span> 54 </span> \tDeine Gebote sind mein Lied im Hause, in dem ich Fremdling bin.",
			"55": "<span> 55 </span> \tHERR, ich denke des Nachts an deinen Namen und halte dein Gesetz.",
			"56": "<span> 56 </span> \tDas ist mein Schatz, daß ich mich an deine Befehle halte.",
			"57": "<span> 57 </span> \tIch habe gesagt: HERR, das soll mein Erbe sein, daß ich deine Worte halte.",
			"58": "<span> 58 </span> \tIch suche deine Gunst von ganzem Herzen; sei mir gnädig nach deinem Wort.",
			"59": "<span> 59 </span> \tIch bedenke meine Wege und lenke meine Füße zu deinen Mahnungen.",
			"60": "<span> 60 </span> \tIch eile und säume nicht, zu halten deine Gebote.",
			"61": "<span> 61 </span> \tDer Gottlosen Stricke umschlingen mich; aber dein Gesetz vergesse ich nicht.",
			"62": "<span> 62 </span> \tZur Mitternacht stehe ich auf, dir zu danken für die Ordnungen deiner Gerechtigkeit.",
			"63": "<span> 63 </span> \tIch halte mich zu allen, die dich fürchten und deine Befehle halten.",
			"64": "<span> 64 </span> \tHERR, die Erde ist voll deiner Güte; lehre mich deine Gebote.",
			"65": "<span> 65 </span> \tDu tust Gutes deinem Knecht, HERR, nach deinem Wort.",
			"66": "<span> 66 </span> \tLehre mich heilsame Einsicht und Erkenntnis; denn ich glaube deinen Geboten.",
			"67": "<span> 67 </span> \tEhe ich gedemütigt wurde, irrte ich; nun aber halte ich dein Wort.",
			"68": "<span> 68 </span> \tDu bist gütig und freundlich, lehre mich deine Weisungen.",
			"69": "<span> 69 </span> \tDie Stolzen erdichten Lügen über mich, ich aber halte von ganzem Herzen deine Befehle.",
			"70": "<span> 70 </span> \tIhr Herz ist völlig verstockt; ich aber habe Freude an deinem Gesetz.",
			"71": "<span> 71 </span> \tEs ist gut für mich, daß du mich gedemütigt hast, damit ich deine Gebote lerne.",
			"72": "<span> 72 </span> \tDas Gesetz deines Mundes ist mir lieber als viel tausend Stück Gold und Silber.",
			"73": "<span> 73 </span> \tDeine Hand hat mich gemacht und bereitet; unterweise mich, daß ich deine Gebote lerne.",
			"74": "<span> 74 </span> \tDie dich fürchten, sehen mich und freuen sich; denn ich hoffe auf dein Wort.",
			"75": "<span> 75 </span> \tHERR, ich weiß, daß deine Urteile gerecht sind; in deiner Treue hast du mich gedemütigt.",
			"76": "<span> 76 </span> \tDeine Gnade soll mein Trost sein, wie du deinem Knecht zugesagt hast.",
			"77": "<span> 77 </span> \tLaß mir deine Barmherzigkeit widerfahren, daß ich lebe; denn ich habe Freude an deinem Gesetz.",
			"78": "<span> 78 </span> \tAch, daß die Stolzen zuschanden würden, die mich mit Lügen niederdrücken! Ich aber sinne nach über deine Befehle.",
			"79": "<span> 79 </span> \tAch, daß sich zu mir hielten, die dich fürchten und deine Mahnungen kennen!",
			"80": "<span> 80 </span> \tMein Herz bleibe rechtschaffen in deinen Geboten, damit ich nicht zuschanden werde.",
			"81": "<span> 81 </span> \tMeine Seele verlangt nach deinem Heil; ich hoffe auf dein Wort.",
			"82": "<span> 82 </span> \tMeine Augen sehnen sich nach deinem Wort und sagen: Wann tröstest du mich?",
			"83": "<span> 83 </span> \tIch bin wie ein Weinschlauch im Rauch; doch deine Gebote vergesse ich nicht.",
			"84": "<span> 84 </span> \tWie lange soll dein Knecht noch warten? Wann willst du Gericht halten über meine Verfolger?",
			"85": "<span> 85 </span> \tDie Stolzen graben mir Gruben, sie, die nicht tun nach deinem Gesetz.",
			"86": "<span> 86 </span> \tAll deine Gebote sind Wahrheit; sie aber verfolgen mich mit Lügen; hilf mir!",
			"87": "<span> 87 </span> \tSie haben mich fast umgebracht auf Erden; ich aber verlasse deine Befehle nicht.",
			"88": "<span> 88 </span> \tErquicke mich nach deiner Gnade, daß ich halte die Mahnung deines Mundes.",
			"89": "<span> 89 </span> \tHERR, dein Wort bleibt ewiglich, so weit der Himmel reicht;",
			"90": "<span> 90 </span> \tdeine Wahrheit währet für und für. Du hast die Erde fest gegründet, und sie bleibt stehen.",
			"91": "<span> 91 </span> \tSie steht noch heute nach deinen Ordnungen; denn es muß dir alles dienen.",
			"92": "<span> 92 </span> \tWenn dein Gesetz nicht mein Trost gewesen wäre, so wäre ich vergangen in meinem Elend.",
			"93": "<span> 93 </span> \tIch will deine Befehle nimmermehr vergessen; denn du erquickst mich damit.",
			"94": "<span> 94 </span> \tIch bin dein, hilf mir; denn ich suche deine Befehle.",
			"95": "<span> 95 </span> \tDie Gottlosen lauern mir auf, daß sie mich umbringen; ich aber merke auf deine Mahnungen.",
			"96": "<span> 96 </span> \tIch habe gesehen, daß alles ein Ende hat, aber dein Gebot bleibt bestehen.",
			"97": "<span> 97 </span> \tWie habe ich dein Gesetz so lieb! Täglich sinne ich ihm nach.",
			"98": "<span> 98 </span> \tDu machst mich mit deinem Gebot weiser, als meine Feinde sind; denn es ist ewiglich mein Schatz.",
			"99": "<span> 99 </span> \tIch habe mehr Einsicht als alle meine Lehrer; denn über deine Mahnungen sinne ich nach.",
			"100": "<span> 100 </span> \tIch bin klüger als die Alten; denn ich halte mich an deine Befehle.",
			"101": "<span> 101 </span> \tIch verwehre meinem Fuß alle bösen Wege, damit ich dein Wort halte.",
			"102": "<span> 102 </span> \tIch weiche nicht von deinen Ordnungen; denn du lehrest mich.",
			"103": "<span> 103 </span> \tDein Wort ist meinem Munde süßer als Honig.",
			"104": "<span> 104 </span> \tDein Wort macht mich klug; darum hasse ich alle falschen Wege.",
			"105": "<span> 105 </span> \tDein Wort ist meines Fußes Leuchte und ein Licht auf meinem Wege.",
			"106": "<span> 106 </span> \tIch schwöre und will's halten: Die Ordnungen deiner Gerechtigkeit will ich bewahren.",
			"107": "<span> 107 </span> \tIch bin sehr gedemütigt; HERR, erquicke mich nach deinem Wort!",
			"108": "<span> 108 </span> \tLaß dir gefallen, HERR, das Opfer meines Mundes, und lehre mich deine Ordnungen.",
			"109": "<span> 109 </span> \tMein Leben ist immer in Gefahr; aber dein Gesetz vergesse ich nicht.",
			"110": "<span> 110 </span> \tDie Gottlosen legen mir Schlingen; ich aber irre nicht ab von deinen Befehlen.",
			"111": "<span> 111 </span> \tDeine Mahnungen sind mein ewiges Erbe; denn sie sind meines Herzens Wonne.",
			"112": "<span> 112 </span> \tIch neige mein Herz, zu tun deine Gebote immer und ewiglich.",
			"113": "<span> 113 </span> \tIch hasse die Wankelmütigen und liebe dein Gesetz.",
			"114": "<span> 114 </span> \tDu bist mein Schutz und mein Schild; ich hoffe auf dein Wort.",
			"115": "<span> 115 </span> \tWeichet von mir, ihr Übeltäter! Ich will mich halten an die Gebote meines Gottes.",
			"116": "<span> 116 </span> \tErhalte mich durch dein Wort, daß ich lebe, und laß mich nicht zuschanden werden in meiner Hoffnung.",
			"117": "<span> 117 </span> \tStärke mich, daß ich gerettet werde, so will ich stets Freude haben an deinen Geboten.",
			"118": "<span> 118 </span> \tDu verwirfst alle, die von deinen Geboten abirren; denn ihr Tun ist Lug und Trug.",
			"119": "<span> 119 </span> \tDu schaffst alle Gottlosen auf Erden weg wie Schlacken, darum liebe ich deine Mahnungen.",
			"120": "<span> 120 </span> \tIch fürchte mich vor dir, daß mir die Haut schaudert, und ich entsetze mich vor deinen Gerichten.",
			"121": "<span> 121 </span> \tIch übe Recht und Gerechtigkeit; übergib mich nicht denen, die mir Gewalt antun wollen.",
			"122": "<span> 122 </span> \tTritt ein für deinen Knecht und tröste ihn, daß mir die Stolzen nicht Gewalt antun!",
			"123": "<span> 123 </span> \tMeine Augen sehnen sich nach deinem Heil und nach dem Wort deiner Gerechtigkeit.",
			"124": "<span> 124 </span> \tHandle mit deinem Knechte nach deiner Gnade und lehre mich deine Gebote.",
			"125": "<span> 125 </span> \tIch bin dein Knecht: Unterweise mich, daß ich verstehe deine Mahnungen.",
			"126": "<span> 126 </span> \tEs ist Zeit, daß der HERR handelt; sie haben dein Gesetz zerbrochen.",
			"127": "<span> 127 </span> \tDarum liebe ich deine Gebote mehr als Gold und Feingold.",
			"128": "<span> 128 </span> \tDarum halte ich alle deine Befehle für recht, ich hasse alle falschen Wege.",
			"129": "<span> 129 </span> \tDeine Mahnungen sind Wunderwerke; darum hält sie meine Seele.",
			"130": "<span> 130 </span> \tWenn dein Wort offenbar wird, so erfreut es und macht klug die Unverständigen.",
			"131": "<span> 131 </span> \tIch tue meinen Mund weit auf und lechze, denn mich verlangt nach deinen Geboten.",
			"132": "<span> 132 </span> \tWende dich zu mir und sei mir gnädig, wie du pflegst zu tun denen, die deinen Namen lieben.",
			"133": "<span> 133 </span> \tLaß meinen Gang in deinem Wort fest sein und laß kein Unrecht über mich herrschen.",
			"134": "<span> 134 </span> \tErlöse mich von der Bedrückung durch Menschen, so will ich halten deine Befehle.",
			"135": "<span> 135 </span> \tLaß dein Antlitz leuchten über deinen Knecht, und lehre mich deine Gebote.",
			"136": "<span> 136 </span> \tMeine Augen fließen von Tränen, weil man dein Gesetz nicht hält.",
			"137": "<span> 137 </span> \tHERR, du bist gerecht, und deine Urteile sind richtig.",
			"138": "<span> 138 </span> \tDu hast deine Mahnungen geboten in Gerechtigkeit und großer Treue.",
			"139": "<span> 139 </span> \tIch habe mich fast zu Tode geeifert, weil meine Widersacher deine Worte vergessen.",
			"140": "<span> 140 </span> \tDein Wort ist ganz durchläutert, und dein Knecht hat es lieb.",
			"141": "<span> 141 </span> \tIch bin gering und verachtet; ich vergesse aber nicht deine Befehle.",
			"142": "<span> 142 </span> \tDeine Gerechtigkeit ist eine ewige Gerechtigkeit, und dein Gesetz ist Wahrheit.",
			"143": "<span> 143 </span> \tAngst und Not haben mich getroffen; ich habe aber Freude an deinen Geboten.",
			"144": "<span> 144 </span> \tDeine Mahnungen sind gerecht in Ewigkeit; unterweise mich, so lebe ich.",
			"145": "<span> 145 </span> \tIch rufe von ganzem Herzen; erhöre mich, HERR; ich will deine Gebote halten.",
			"146": "<span> 146 </span> \tIch rufe zu dir, hilf mir; ich will mich an deine Mahnungen halten.",
			"147": "<span> 147 </span> \tIch komme in der Frühe und rufe um Hilfe; auf dein Wort hoffe ich.",
			"148": "<span> 148 </span> \tIch wache auf, wenn's noch Nacht ist, nachzusinnen über dein Wort.",
			"149": "<span> 149 </span> \tHöre meine Stimme nach deiner Gnade; HERR, erquicke mich nach deinem Recht.",
			"150": "<span> 150 </span> \tMeine arglistigen Verfolger nahen; aber sie sind fern von deinem Gesetz.",
			"151": "<span> 151 </span> \tHERR, du bist nahe, und alle deine Gebote sind Wahrheit.",
			"152": "<span> 152 </span> \tLängst weiß ich aus deinen Mahnungen, daß du sie für ewig gegründet hast.",
			"153": "<span> 153 </span> \tSieh doch mein Elend und errette mich; denn ich vergesse dein Gesetz nicht.",
			"154": "<span> 154 </span> \tFühre meine Sache und erlöse mich; erquicke mich durch dein Wort.",
			"155": "<span> 155 </span> \tDas Heil ist fern von den Gottlosen; denn sie achten deine Gebote nicht.",
			"156": "<span> 156 </span> \tHERR, deine Barmherzigkeit ist groß; erquicke mich nach deinem Recht.",
			"157": "<span> 157 </span> \tMeiner Verfolger und Widersacher sind viele; ich weiche aber nicht von deinen Mahnungen.",
			"158": "<span> 158 </span> \tIch sehe die Verächter, und es tut mir wehe, daß sie dein Wort nicht halten.",
			"159": "<span> 159 </span> \tSiehe, ich liebe deine Befehle; HERR, erquicke mich nach deiner Gnade.",
			"160": "<span> 160 </span> \tDein Wort ist nichts als Wahrheit, alle Ordnungen deiner Gerechtigkeit währen ewiglich.",
			"161": "<span> 161 </span> \tFürsten verfolgen mich ohne Grund; aber mein Herz fürchtet sich nur vor deinen Worten.",
			"162": "<span> 162 </span> \tIch freue mich über dein Wort wie einer, der große Beute macht.",
			"163": "<span> 163 </span> \tLügen bin ich feind, und sie sind mir ein Greuel; aber dein Gesetz habe ich lieb.",
			"164": "<span> 164 </span> \tIch lobe dich des Tages siebenmal um deiner gerechten Ordnungen willen.",
			"165": "<span> 165 </span> \tGroßen Frieden haben, die dein Gesetz lieben; sie werden nicht straucheln.",
			"166": "<span> 166 </span> \tHERR, ich warte auf dein Heil und tue nach deinen Geboten.",
			"167": "<span> 167 </span> \tMeine Seele hält sich an deine Mahnungen und liebt sie sehr.",
			"168": "<span> 168 </span> \tIch halte deine Befehle und deine Mahnungen; denn alle meine Wege liegen offen vor dir.",
			"169": "<span> 169 </span> \tHERR, laß mein Klagen vor dich kommen; unterweise mich nach deinem Wort.",
			"170": "<span> 170 </span> \tLaß mein Flehen vor dich kommen; errette mich nach deinem Wort.",
			"171": "<span> 171 </span> \tMeine Lippen sollen dich loben; denn du lehrst mich deine Gebote.",
			"172": "<span> 172 </span> \tMeine Zunge soll singen von deinem Wort; denn alle deine Gebote sind gerecht.",
			"173": "<span> 173 </span> \tLaß deine Hand mir beistehen; denn ich habe erwählt deine Befehle.",
			"174": "<span> 174 </span> \tHERR, mich verlangt nach deinem Heil, und an deinem Gesetz habe ich Freude.",
			"175": "<span> 175 </span> \tLaß meine Seele leben, daß sie dich lobe, und dein Recht mir helfen.",
			"176": "<span> 176 </span> \tIch bin wie ein verirrtes und verlorenes Schaf; suche deinen Knecht, denn ich vergesse deine Gebote nicht."
		},
		"120": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Ich rufe zu dem HERRN in meiner Not, und er erhört mich.",
			"2": "<span> 2 </span> \tHERR, errette mich von den Lügenmäulern, von den falschen Zungen.",
			"3": "<span> 3 </span> \tWas soll er dir antun, du falsche Zunge, und was dir noch geben?",
			"4": "<span> 4 </span> \tScharfe Pfeile eines Starken und feurige Kohlen!",
			"5": "<span> 5 </span> \tWeh mir, daß ich weilen muß unter Meschech; ich muß wohnen bei den Zelten Kedars!",
			"6": "<span> 6 </span> \tEs wird meiner Seele lang, zu wohnen bei denen, die den Frieden hassen.",
			"7": "<span> 7 </span> \tIch halte Frieden; aber wenn ich rede, so fangen sie Streit an."
		},
		"121": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Ich hebe meine Augen auf zu den Bergen. Woher kommt mir Hilfe?",
			"2": "<span> 2 </span> \tMeine Hilfe kommt vom HERRN, der Himmel und Erde gemacht hat.",
			"3": "<span> 3 </span> \tEr wird deinen Fuß nicht gleiten lassen, und der dich behütet, schläft nicht.",
			"4": "<span> 4 </span> \tSiehe, der Hüter Israels schläft und schlummert nicht.",
			"5": "<span> 5 </span> \tDer HERR behütet dich; der HERR ist dein Schatten über deiner rechten Hand,",
			"6": "<span> 6 </span> \tdaß dich des Tages die Sonne nicht steche noch der Mond des Nachts.",
			"7": "<span> 7 </span> \tDer HERR behüte dich vor allem Übel, er behüte deine Seele.",
			"8": "<span> 8 </span> \tDer HERR behüte deinen Ausgang und Eingang von nun an bis in Ewigkeit!"
		},
		"122": {
			"1": "<span>1</span> \tVON DAVID, EIN WALLFAHRTSLIED. Ich freute mich über die, die mir sagten: Lasset uns ziehen zum Haus des HERRN!",
			"2": "<span> 2 </span> \tNun stehen unsere Füße in deinen Toren, Jerusalem.",
			"3": "<span> 3 </span> \tJerusalem ist gebaut als eine Stadt, in der man zusammenkommen soll,",
			"4": "<span> 4 </span> \twohin die Stämme hinaufziehen, die Stämme des HERRN, wie es geboten ist dem Volke Israel, zu preisen den Namen des HERRN.",
			"5": "<span> 5 </span> \tDenn dort stehen die Throne zum Gericht, die Throne des Hauses David.",
			"6": "<span> 6 </span> \tWünschet Jerusalem Glück! Es möge wohl gehen denen, die dich lieben!",
			"7": "<span> 7 </span> \tEs möge Frieden sein in deinen Mauern und Glück in deinen Palästen!",
			"8": "<span> 8 </span> \tUm meiner Brüder und Freunde willen will ich dir Frieden wünschen.",
			"9": "<span> 9 </span> \tUm des Hauses des HERRN willen, unseres Gottes, will ich dein Bestes suchen."
		},
		"123": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Ich hebe meine Augen auf zu dir, der du im Himmel wohnest.",
			"2": "<span> 2 </span> \tSiehe, wie die Augen der Knechte auf die Hände ihrer Herren sehen, wie die Augen der Magd auf die Hände ihrer Frau, so sehen unsre Augen auf den HERRN, unsern Gott, bis er uns gnädig werde.",
			"3": "<span> 3 </span> \tSei uns gnädig, HERR, sei uns gnädig; denn allzusehr litten wir Verachtung.",
			"4": "<span> 4 </span> \tAllzusehr litt unsere Seele den Spott der Stolzen und die Verachtung der Hoffärtigen."
		},
		"124": {
			"1": "<span>1</span> \tVON DAVID, EIN WALLFAHRTSLIED. Wäre der HERR nicht bei uns – so sage Israel -,",
			"2": "<span> 2 </span> \twäre der HERR nicht bei uns, wenn Menschen wider uns aufstehen,",
			"3": "<span> 3 </span> \tso verschlängen sie uns lebendig, wenn ihr Zorn über uns entbrennt;",
			"4": "<span> 4 </span> \tso ersäufte uns Wasser, Ströme gingen über unsre Seele,",
			"5": "<span> 5 </span> \tes gingen Wasser hoch über uns hinweg.",
			"6": "<span> 6 </span> \tGelobt sei der HERR, daß er uns nicht gibt zum Raub in ihre Zähne!",
			"7": "<span> 7 </span> \tUnsre Seele ist entronnen wie ein Vogel dem Netze des Vogelfängers; das Netz ist zerrissen, und wir sind frei.",
			"8": "<span> 8 </span> \tUnsre Hilfe steht im Namen des HERRN, der Himmel und Erde gemacht hat."
		},
		"125": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Die auf den HERRN hoffen, werden nicht fallen, sondern ewig bleiben wie der Berg Zion.",
			"2": "<span> 2 </span> \tWie um Jerusalem Berge sind, so ist der HERR um sein Volk her von nun an bis in Ewigkeit.",
			"3": "<span> 3 </span> \tDenn der Gottlosen Zepter wird nicht bleiben über dem Erbteil der Gerechten, damit die Gerechten ihre Hand nicht ausstrecken zur Ungerechtigkeit.",
			"4": "<span> 4 </span> \tHERR, tu wohl den Guten und denen, die frommen Herzens sind.",
			"5": "<span> 5 </span> \tDie aber abweichen auf ihre krummen Wege, wird der HERR dahinfahren lassen mit den Übeltätern. Friede sei über Israel!"
		},
		"126": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Wenn der HERR die Gefangenen Zions erlösen wird, so werden wir sein wie die Träumenden.",
			"2": "<span> 2 </span> \tDann wird unser Mund voll Lachens und unsre Zunge voll Rühmens sein. Dann wird man sagen unter den Heiden: Der HERR hat Großes an ihnen getan!",
			"3": "<span> 3 </span> \tDer HERR hat Großes an uns getan; des sind wir fröhlich.",
			"4": "<span> 4 </span> \tHERR, bringe zurück unsre Gefangenen, wie du die Bäche wiederbringst im Südland.",
			"5": "<span> 5 </span> \tDie mit Tränen säen, werden mit Freuden ernten.",
			"6": "<span> 6 </span> \tSie gehen hin und weinen und streuen ihren Samen und kommen mit Freuden und bringen ihre Garben."
		},
		"127": {
			"1": "<span>1</span> \tVON SALOMO, EIN WALLFAHRTSLIED. Wenn der HERR nicht das Haus baut, so arbeiten umsonst, die daran bauen. Wenn der HERR nicht die Stadt behütet, so wacht der Wächter umsonst.",
			"2": "<span> 2 </span> \tEs ist umsonst, daß ihr früh aufsteht und hernach lange sitzet und esset euer Brot mit Sorgen; denn seinen Freunden gibt er es im Schlaf.",
			"3": "<span> 3 </span> \tSiehe, Kinder sind eine Gabe des HERRN, und Leibesfrucht ist ein Geschenk.",
			"4": "<span> 4 </span> \tWie Pfeile in der Hand eines Starken, so sind die Söhne der Jugendzeit.",
			"5": "<span> 5 </span> \tWohl dem, der seinen Köcher mit ihnen gefüllt hat! Sie werden nicht zuschanden, wenn sie mit ihren Feinden verhandeln im Tor."
		},
		"128": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Wohl dem, der den HERRN fürchtet und auf seinen Wegen geht!",
			"2": "<span> 2 </span> \tDu wirst dich nähren von deiner Hände Arbeit; wohl dir, du hast's gut.",
			"3": "<span> 3 </span> \tDein Weib wird sein wie ein fruchtbarer Weinstock drinnen in deinem Hause, deine Kinder wie junge Ölbäume um deinen Tisch her.",
			"4": "<span> 4 </span> \tSiehe, so wird gesegnet der Mann, der den HERRN fürchtet.",
			"5": "<span> 5 </span> \tDer HERR wird dich segnen aus Zion, daß du siehst das Glück Jerusalems dein Leben lang",
			"6": "<span> 6 </span> \tund siehst Kinder deiner Kinder. Friede sei über Israel!"
		},
		"129": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Sie haben mich oft bedrängt von meiner Jugend auf – so sage Israel -,",
			"2": "<span> 2 </span> \tsie haben mich oft bedrängt von meiner Jugend auf; aber sie haben mich nicht überwältigt.",
			"3": "<span> 3 </span> \tDie Pflüger haben auf meinem Rücken geackert und ihre Furchen langgezogen.",
			"4": "<span> 4 </span> \tDer HERR, der gerecht ist, hat der Gottlosen Stricke zerhauen.",
			"5": "<span> 5 </span> \tAch daß zuschanden würden und zurückwichen alle, die Zion gram sind!",
			"6": "<span> 6 </span> \tAch daß sie würden wie das Gras auf den Dächern, das verdorrt, ehe man es ausrauft,",
			"7": "<span> 7 </span> \tmit dem der Schnitter seine Hand nicht füllt noch der Garbenbinder seinen Arm;",
			"8": "<span> 8 </span> \tund keiner, der vorübergeht, soll sprechen: Der Segen des HERRN sei über euch! Wir segnen euch im Namen des HERRN."
		},
		"130": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Aus der Tiefe rufe ich, HERR, zu dir.",
			"2": "<span> 2 </span> \tHerr, höre meine Stimme! Laß deine Ohren merken auf die Stimme meines Flehens!",
			"3": "<span> 3 </span> \tWenn du, HERR, Sünden anrechnen willst – Herr, wer wird bestehen?",
			"4": "<span> 4 </span> \tDenn bei dir ist die Vergebung, daß man dich fürchte.",
			"5": "<span> 5 </span> \tIch harre des HERRN, meine Seele harret, und ich hoffe auf sein Wort.",
			"6": "<span> 6 </span> \tMeine Seele wartet auf den Herrn mehr als die Wächter auf den Morgen; mehr als die Wächter auf den Morgen",
			"7": "<span> 7 </span> \thoffe Israel auf den HERRN! Denn bei dem HERRN ist die Gnade und viel Erlösung bei ihm.",
			"8": "<span> 8 </span> \tUnd er wird Israel erlösen aus allen seinen Sünden."
		},
		"131": {
			"1": "<span>1</span> \tVON DAVID, EIN WALLFAHRTSLIED. HERR, mein Herz ist nicht hoffärtig, und meine Augen sind nicht stolz. Ich gehe nicht um mit großen Dingen, die mir zu wunderbar sind.",
			"2": "<span> 2 </span> \tFürwahr, meine Seele ist still und ruhig geworden wie ein kleines Kind bei seiner Mutter; wie ein kleines Kind, so ist meine Seele in mir.",
			"3": "<span> 3 </span> \tIsrael, hoffe auf den HERRN von nun an bis in Ewigkeit!"
		},
		"132": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Gedenke, HERR, an David und all seine Mühsal,",
			"2": "<span> 2 </span> \tder dem HERRN einen Eid schwor und gelobte dem Mächtigen Jakobs:",
			"3": "<span> 3 </span> \tIch will nicht in mein Haus gehen noch mich aufs Lager meines Bettes legen,",
			"4": "<span> 4 </span> \tich will meine Augen nicht schlafen lassen noch meine Augenlider schlummern,",
			"5": "<span> 5 </span> \tbis ich eine Stätte finde für den HERRN, eine Wohnung für den Mächtigen Jakobs.",
			"6": "<span> 6 </span> \tSiehe, wir hörten von ihr in Ephratha, wir haben sie gefunden im Gefilde von Jaar.",
			"7": "<span> 7 </span> \tWir wollen in seine Wohnung gehen und anbeten vor dem Schemel seiner Füße.",
			"8": "<span> 8 </span> \tHERR, mache dich auf zur Stätte deiner Ruhe, du und die Lade deiner Macht!",
			"9": "<span> 9 </span> \tDeine Priester laß sich kleiden mit Gerechtigkeit und deine Heiligen sich freuen.",
			"10": "<span> 10 </span> \tWeise nicht ab das Antlitz deines Gesalbten um deines Knechtes David willen!",
			"11": "<span> 11 </span> \tDer HERR hat David einen Eid geschworen, davon wird er sich wahrlich nicht wenden: Ich will dir auf deinen Thron setzen einen, der von deinem Leibe kommt.",
			"12": "<span> 12 </span> \tWerden deine Söhne meinen Bund halten und mein Gebot, das ich sie lehren werde, so sollen auch ihre Söhne auf deinem Thron sitzen ewiglich.",
			"13": "<span> 13 </span> \tDenn der HERR hat Zion erwählt, und es gefällt ihm, dort zu wohnen.",
			"14": "<span> 14 </span> \t«Dies ist die Stätte meiner Ruhe ewiglich; hier will ich wohnen, denn das gefällt mir.",
			"15": "<span> 15 </span> \tIch will ihre Speise segnen und ihren Armen Brot genug geben.",
			"16": "<span> 16 </span> \tIhre Priester will ich mit Heil kleiden, und ihre Heiligen sollen fröhlich sein.",
			"17": "<span> 17 </span> \tDort soll dem David aufgehen ein mächtiger Sproß, ich habe meinem Gesalbten eine Leuchte zugerichtet;",
			"18": "<span> 18 </span> \tseine Feinde will ich in Schande kleiden, aber über ihm soll blühen seine Krone.»"
		},
		"133": {
			"1": "<span>1</span> \tVON DAVID, EIN WALLFAHRTSLIED. Siehe, wie fein und lieblich ist's, wenn Brüder einträchtig beieinander wohnen!",
			"2": "<span> 2 </span> \tEs ist wie das feine Salböl auf dem Haupte Aarons, das herabfließt in seinen Bart, das herabfließt zum Saum seines Kleides,",
			"3": "<span> 3 </span> \twie der Tau, der vom Hermon herabfällt auf die Berge Zions! Denn dort verheißt der HERR den Segen und Leben bis in Ewigkeit."
		},
		"134": {
			"1": "<span>1</span> \tEIN WALLFAHRTSLIED. Wohlan, lobet den HERRN, alle Knechte des HERRN, die ihr steht des Nachts im Hause des HERRN!",
			"2": "<span> 2 </span> \tHebet eure Hände auf im Heiligtum und lobet den HERRN!",
			"3": "<span> 3 </span> \tDer HERR segne dich aus Zion, der Himmel und Erde gemacht hat!"
		},
		"135": {
			"1": "<span>1</span> \tHalleluja! Lobet den Namen des HERRN, lobet, ihr Knechte des HERRN,",
			"2": "<span> 2 </span> \tdie ihr steht im Hause des HERRN, in den Vorhöfen am Hause unsres Gottes!",
			"3": "<span> 3 </span> \tLobet den HERRN, denn der HERR ist freundlich; lobsinget seinem Namen, denn er ist lieblich!",
			"4": "<span> 4 </span> \tDenn der HERR hat sich Jakob erwählt, Israel zu seinem Eigentum.",
			"5": "<span> 5 </span> \tJa, ich weiß, daß der HERR groß ist und unser HERR über allen Göttern.",
			"6": "<span> 6 </span> \tAlles, was er will, das tut er im Himmel und auf Erden, im Meer und in allen Tiefen;",
			"7": "<span> 7 </span> \tder die Wolken läßt aufsteigen vom Ende der Erde, der die Blitze samt dem Regen macht, der den Wind herausführt aus seinen Kammern;",
			"8": "<span> 8 </span> \tder die Erstgeburten schlug in Ägypten bei den Menschen und beim Vieh",
			"9": "<span> 9 </span> \tund ließ Zeichen und Wunder kommen über dich, Ägyptenland, über den Pharao und alle seine Knechte;",
			"10": "<span> 10 </span> \tder viele Völker schlug und tötete mächtige Könige,",
			"11": "<span> 11 </span> \tSihon, den König der Amoriter, und Og, den König von Basan, und alle Königreiche in Kanaan,",
			"12": "<span> 12 </span> \tund gab ihr Land zum Erbe, zum Erbe seinem Volk Israel.",
			"13": "<span> 13 </span> \tHERR, dein Name währet ewiglich, dein Ruhm, HERR, währet für und für.",
			"14": "<span> 14 </span> \tDenn der HERR schafft Recht seinem Volk und wird seinen Knechten gnädig sein.",
			"15": "<span> 15 </span> \tDie Götzen der Heiden sind Silber und Gold, gemacht von Menschenhänden.",
			"16": "<span> 16 </span> \tSie haben Mäuler und reden nicht, sie haben Augen und sehen nicht,",
			"17": "<span> 17 </span> \tsie haben Ohren und hören nicht, auch ist kein Odem in ihrem Munde.",
			"18": "<span> 18 </span> \tDie solche Götzen machen, sind ihnen gleich, alle, die auf sie hoffen.",
			"19": "<span> 19 </span> \tDas Haus Israel lobe den HERRN! Lobet den HERRN, ihr vom Hause Aaron!",
			"20": "<span> 20 </span> \tIhr vom Hause Levi, lobet den HERRN! Die ihr den HERRN fürchtet, lobet den HERRN!",
			"21": "<span> 21 </span> \tGelobt sei der HERR aus Zion, der zu Jerusalem wohnt! Halleluja!"
		},
		"136": {
			"1": "<span>1</span> \tDanket dem HERRN; denn er ist freundlich, denn seine Güte währet ewiglich.",
			"2": "<span> 2 </span> \tDanket dem Gott aller Götter, denn seine Güte währet ewiglich.",
			"3": "<span> 3 </span> \tDanket dem Herrn aller Herren, denn seine Güte währet ewiglich.",
			"4": "<span> 4 </span> \tDer allein große Wunder tut, denn seine Güte währet ewiglich.",
			"5": "<span> 5 </span> \tDer die Himmel mit Weisheit gemacht hat, den seine Güte währet ewiglich.",
			"6": "<span> 6 </span> \tDer die Erde über den Wassern ausgebreitet hat, denn seine Güte währet ewiglich.",
			"7": "<span> 7 </span> \tDer große Lichter gemacht hat, denn seine Güte währet ewiglich:",
			"8": "<span> 8 </span> \tdie Sonne, den Tag zu regieren, denn seine Güte währet ewiglich;",
			"9": "<span> 9 </span> \tden Mond und die Sterne, die Nacht zu regieren, denn seine Güte währet ewiglich.",
			"10": "<span> 10 </span> \tDer die Erstgeborenen schlug in Ägypten, denn seine Güte währet ewiglich;",
			"11": "<span> 11 </span> \tund führte Israel von dort heraus, denn seine Güte währet ewiglich;",
			"12": "<span> 12 </span> \tmit starker Hand und ausgerecktem Arm, denn seine Güte währet ewiglich.",
			"13": "<span> 13 </span> \tDer das Schilfmeer teilte in zwei Teile, denn seine Güte währet ewiglich;",
			"14": "<span> 14 </span> \tund ließ Israel mitten hindurchgehen, denn seine Güte währet ewiglich;",
			"15": "<span> 15 </span> \tder den Pharao und sein Heer ins Schilfmeer stieß, denn seine Güte währet ewiglich.",
			"16": "<span> 16 </span> \tDer sein Volk führte durch die Wüste, denn seine Güte währet ewiglich.",
			"17": "<span> 17 </span> \tDer große Könige schlug, denn seine Güte währet ewiglich;",
			"18": "<span> 18 </span> \tund brachte mächtige Könige um, denn seine Güte währet ewiglich;",
			"19": "<span> 19 </span> \tSihon, den König der Amoriter, denn seine Güte währet ewiglich;",
			"20": "<span> 20 </span> \tund Og, den König von Basan, denn seine Güte währet ewiglich;",
			"21": "<span> 21 </span> \tund gab ihr Land zum Erbe, denn seine Güte währet ewiglich;",
			"22": "<span> 22 </span> \tzum Erbe seinem Knecht Israel, denn seine Güte währet ewiglich.",
			"23": "<span> 23 </span> \tDer an uns dachte, als wir unterdrückt waren, denn seine Güte währet ewiglich;",
			"24": "<span> 24 </span> \tund uns erlöste von unsern Feinden, denn seine Güte währet ewiglich.",
			"25": "<span> 25 </span> \tDer Speise gibt allem Fleisch, denn seine Güte währet ewiglich.",
			"26": "<span> 26 </span> \tDanket dem Gott des Himmels, denn seine Güte währet ewiglich."
		},
		"137": {
			"1": "<span>1</span> \tAn den Wassern zu Babel saßen wir und weinten, wenn wir an Zion gedachten.",
			"2": "<span> 2 </span> \tUnsere Harfen hängten wir an die Weiden dort im Lande.",
			"3": "<span> 3 </span> \tDenn die uns gefangen hielten, hießen uns dort singen und in unserm Heulen fröhlich sein: «Singet uns ein Lied von Zion!»",
			"4": "<span> 4 </span> \tWie könnten wir des HERRN Lied singen in fremdem Lande?",
			"5": "<span> 5 </span> \tVergesse ich dich, Jerusalem, so verdorre meine Rechte.",
			"6": "<span> 6 </span> \tMeine Zunge soll an meinem Gaumen kleben, wenn ich deiner nicht gedenke, wenn ich nicht lasse Jerusalem meine höchste Freude sein.",
			"7": "<span> 7 </span> \tHERR, vergiß den Söhnen Edom nicht, was sie sagten am Tage Jerusalems: «Reißt nieder, reißt nieder bis auf den Grund!»",
			"8": "<span> 8 </span> \tTochter Babel, du Verwüsterin, wohl dem, der dir vergilt, was du uns angetan hast!",
			"9": "<span> 9 </span> \tWohl dem, der deine jungen Kinder nimmt und sie am Felsen zerschmettert!"
		},
		"138": {
			"1": "<span>1</span> \tVON DAVID. Ich danke dir von ganzem Herzen, vor den Göttern will ich dir lobsingen.",
			"2": "<span> 2 </span> \tIch will anbeten vor deinem heiligen Tempel und deinen Namen preisen für deine Güte und Treue; denn du hast deinen Namen und dein Wort herrlich gemacht über alles.",
			"3": "<span> 3 </span> \tWenn ich dich anrufe, so erhörst du mich und gibst meiner Seele große Kraft.",
			"4": "<span> 4 </span> \tEs danken dir, HERR, alle Könige auf Erden, daß sie hören das Wort deines Mundes;",
			"5": "<span> 5 </span> \tsie singen von den Wegen des HERRN, daß die Herrlichkeit des HERRN so groß ist.",
			"6": "<span> 6 </span> \tDenn der HERR ist hoch und sieht auf den Niedrigen und kennt den Stolzen von ferne.",
			"7": "<span> 7 </span> \tWenn ich mitten in der Angst wandle, so erquickest du mich und reckst deine Hand gegen den Zorn meiner Feinde und hilfst mir mit deiner Rechten.",
			"8": "<span> 8 </span> \tDer HERR wird meine Sache hinausführen. HERR, deine Güte ist ewig. Das Werk deiner Hände wollest du nicht lassen."
		},
		"139": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN. HERR, du erforschest mich und kennest mich.",
			"2": "<span> 2 </span> \tIch sitze oder stehe auf, so weißt du es; du verstehst meine Gedanken von ferne.",
			"3": "<span> 3 </span> \tIch gehe oder liege, so bist du um mich und siehst alle meine Wege.",
			"4": "<span> 4 </span> \tDenn siehe, es ist kein Wort auf meiner Zunge, das du, HERR, nicht schon wüßtest.",
			"5": "<span> 5 </span> \tVon allen Seiten umgibst du mich und hältst deine Hand über mir.",
			"6": "<span> 6 </span> \tDiese Erkenntnis ist mir zu wunderbar und zu hoch, ich kann sie nicht begreifen.",
			"7": "<span> 7 </span> \tWohin soll ich gehen vor deinem Geist, und wohin soll ich fliehen vor deinem Angesicht?",
			"8": "<span> 8 </span> \tFühre ich gen Himmel, so bist du da; bettete ich mich bei den Toten, siehe, so bist du auch da.",
			"9": "<span> 9 </span> \tNähme ich Flügel der Morgenröte und bliebe am äußersten Meer,",
			"10": "<span> 10 </span> \tso würde auch dort deine Hand mich führen und deine Rechte mich halten.",
			"11": "<span> 11 </span> \tSpräche ich: Finsternis möge mich decken und Nacht statt Licht um mich sein -,",
			"12": "<span> 12 </span> \tso wäre auch Finsternis nicht finster bei dir, und die Nacht leuchtete wie der Tag. Finsternis ist wie das Licht.",
			"13": "<span> 13 </span> \tDenn du hast meine Nieren bereitet und hast mich gebildet im Mutterleibe.",
			"14": "<span> 14 </span> \tIch danke dir dafür, daß ich wunderbar gemacht bin; wunderbar sind deine Werke; das erkennt meine Seele.",
			"15": "<span> 15 </span> \tEs war dir mein Gebein nicht verborgen, als ich im Verborgenen gemacht wurde, als ich gebildet wurde unten in der Erde.",
			"16": "<span> 16 </span> \tDeine Augen sahen mich, als ich noch nicht bereitet war, und alle Tage waren in dein Buch geschrieben, die noch werden sollten und von denen keiner da war.",
			"17": "<span> 17 </span> \tAber wie schwer sind für mich, Gott, deine Gedanken! Wie ist ihre Summe so groß!",
			"18": "<span> 18 </span> \tWollte ich sie zählen, so wären sie mehr als der Sand: Am Ende bin ich noch immer bei dir.",
			"19": "<span> 19 </span> \tAch Gott, wolltest du doch die Gottlosen töten! Daß doch die Blutgierigen von mir wichen!",
			"20": "<span> 20 </span> \tDenn sie reden von dir lästerlich, und deine Feinde erheben sich mit frechem Mut.",
			"21": "<span> 21 </span> \tSollte ich nicht hassen, HERR, die dich hassen, und verabscheuen, die sich gegen dich erheben?",
			"22": "<span> 22 </span> \tIch hasse sie mit ganzem Ernst; sie sind mir zu Feinden geworden.",
			"23": "<span> 23 </span> \tErforsche mich, Gott, und erkenne mein Herz; prüfe mich und erkenne, wie ich's meine.",
			"24": "<span> 24 </span> \tUnd sieh, ob ich auf bösem Wege bin, und leite mich auf ewigem Wege."
		},
		"140": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS, VORZUSINGEN.",
			"2": "<span> 2 </span> \tErrette mich HERR, von den bösen Menschen; behüte mich vor den Gewalttätigen,",
			"3": "<span> 3 </span> \tdie Böses planen in ihrem Herzen und täglich Streit erregen.",
			"4": "<span> 4 </span> \tSie haben scharfe Zungen wie Schlangen, Otterngift ist unter ihren Lippen. SELA.",
			"5": "<span> 5 </span> \tBewahre mich, HERR, vor der Hand der Gottlosen; behüte mich vor den Gewalttätigen, die mich zu Fall bringen wollen.",
			"6": "<span> 6 </span> \tDie Hoffärtigen legen mir Schlingen und breiten Stricke aus zum Netz und stellen mir Fallen auf den Weg. SELA.",
			"7": "<span> 7 </span> \tIch aber sage zum HERRN: Du bist mein Gott; HERR, vernimm die Stimme meines Flehens!",
			"8": "<span> 8 </span> \tHERR, meine starke Hilfe, du beschirmst mein Haupt zur Zeit des Streits.",
			"9": "<span> 9 </span> \tHERR, gib dem Gottlosen nicht, was er begehrt! Was er sinnt, laß nicht gelingen, sie könnten sich sonst überheben. SELA.",
			"10": "<span> 10 </span> \tDas Unglück, über das meine Feinde beraten, komme über sie selber.",
			"11": "<span> 11 </span> \tEr möge feurige Kohlen über sie schütten; er möge sie stürzen in Gruben, daß sie nicht mehr aufstehen.",
			"12": "<span> 12 </span> \tEin böses Maul wird kein Glück haben auf Erden; ein frecher, böser Mensch wird verjagt und gestürzt werden.",
			"13": "<span> 13 </span> \tDenn ich weiß, daß der HERR des Elenden Sache führen und den Armen Recht schaffen wird.\r\n[14]\tJa, die Gerechten werden deinen Namen preisen, und die Frommen werden vor deinem Angesicht bleiben."
		},
		"141": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. HERR, ich rufe zu dir, eile zu mir; vernimm meine Stimme, wenn ich dich anrufe.",
			"2": "<span> 2 </span> \tMein Gebet möge vor dir gelten als ein Räucheropfer, das Aufheben meiner Hände als ein Abendopfer.",
			"3": "<span> 3 </span> \tHERR, behüte meinen Mund und bewahre meine Lippen!",
			"4": "<span> 4 </span> \tNeige mein Herz nicht zum Bösen, gottlos zu leben mit den Übeltätern; ich mag nicht essen von ihren leckeren Speisen.",
			"5": "<span> 5 </span> \tDer Gerechte schlage mich freundlich und weise mich zurecht; das wird mir wohltun wie Balsam auf dem Haupte. Mein Haupt wird sich dagegen nicht wehren. Doch ich bete stets, daß jene mir nicht Schaden tun.",
			"6": "<span> 6 </span> \tIhre Anführer sollen hinabgestürzt werden vom Felsen; dann wird man merken, wie richtig meine Worte gewesen sind.",
			"7": "<span> 7 </span> \tIhre Gebeine werden zerstreut bis zur Pforte des Todes, wie wenn einer das Land pflügt und zerwühlt.",
			"8": "<span> 8 </span> \tJa, auf dich, HERR, sehen meine Augen; ich traue auf dich, gib mich nicht in den Tod dahin.",
			"9": "<span> 9 </span> \tBewahre mich vor der Schlinge, die sie mir gelegt haben, und vor der Falle der Übeltäter.",
			"10": "<span> 10 </span> \tDie Gottlosen sollen miteinander in ihr eigenes Netz fallen; mich aber laß entrinnen."
		},
		"142": {
			"1": "<span>1</span> \tEINE UNTERWEISUNG DAVIDS, ALS ER IN DER HÖHLE WAR, EIN GEBET.",
			"2": "<span> 2 </span> \tIch schreie zum HERRN mit meiner Stimme, ich flehe zum HERRN mit meiner Stimme.",
			"3": "<span> 3 </span> \tIch schütte meine Klage vor ihm aus und zeige an vor ihm meine Not.",
			"4": "<span> 4 </span> \tWenn mein Geist in Ängsten ist, so nimmst du dich meiner an. Sie legen mir Schlingen auf dem Wege, den ich gehe.",
			"5": "<span> 5 </span> \tSchau zur Rechten und sieh: da will niemand mich kennen. Ich kann nicht entfliehen, niemand nimmt sich meiner an.",
			"6": "<span> 6 </span> \tHERR, zu dir schreie ich und sage: Du bist meine Zuversicht, mein Teil im Lande der Lebendigen.",
			"7": "<span> 7 </span> \tHöre auf meine Klage, denn ich werde sehr geplagt. Errette mich von meinen Verfolgern, denn sie sind mir zu mächtig.\r\n[8]\tFühre mich aus dem Kerker, daß ich preise deinen Namen. Die Gerechten werden sich zu mir sammeln, wenn du mir wohltust."
		},
		"143": {
			"1": "<span>1</span> \tEIN PSALM DAVIDS. HERR, erhöre mein Gebet, vernimm mein Flehen um deiner Treue willen, erhöre mich um deiner Gerechtigkeit willen,",
			"2": "<span> 2 </span> \tund geh nicht ins Gericht mit deinem Knecht; denn vor dir ist kein Lebendiger gerecht.",
			"3": "<span> 3 </span> \tDenn der Feind verfolgt meine Seele und schlägt mein Leben zu Boden, er legt mich ins Finstere wie die, die lange schon tot sind.",
			"4": "<span> 4 </span> \tUnd mein Geist ist in Ängsten, mein Herz ist erstarrt in meinem Leibe.",
			"5": "<span> 5 </span> \tIch denke an die früheren Zeiten; ich sinne nach über all deine Taten und spreche von den Werken deiner Hände.",
			"6": "<span> 6 </span> \tIch breite meine Hände aus zu dir, meine Seele dürstet nach dir wie ein dürres Land. SELA.",
			"7": "<span> 7 </span> \tHERR, erhöre mich bald, mein Geist vergeht; verbirg dein Antlitz nicht vor mir, daß ich nicht gleich werde denen, die in die Grube fahren.",
			"8": "<span> 8 </span> \tLaß mich am Morgen hören deine Gnade; denn ich hoffe auf dich. Tu mir kund den Weg, den ich gehen soll; denn mich verlangt nach dir.",
			"9": "<span> 9 </span> \tErrette mich, mein Gott, von meinen Feinden; zu dir nehme ich meine Zuflucht.",
			"10": "<span> 10 </span> \tLehre mich tun nach deinem Wohlgefallen, denn du bist mein Gott; dein guter Geist führe mich auf ebner Bahn.",
			"11": "<span> 11 </span> \tHERR, erquicke mich um deines Namens willen; führe mich aus der Not um deiner Gerechtigkeit willen,",
			"12": "<span> 12 </span> \tund vernichte meine Feinde um deiner Güte willen und bringe alle um, die mich bedrängen; denn ich bin dein Knecht."
		},
		"144": {
			"1": "<span>1</span> \tVON DAVID. Gelobt sei der HERR, mein Fels, der meine Hände kämpfen lehrt und meine Fäuste, Krieg zu führen,",
			"2": "<span> 2 </span> \tmeine Hilfe und meine Burg, mein Schutz und mein Erretter, mein Schild, auf den ich traue, der Völker unter mich zwingt.",
			"3": "<span> 3 </span> \tHERR, was ist der Mensch, daß du dich seiner annimmst, und des Menschen Kind, daß du ihn so beachtest?",
			"4": "<span> 4 </span> \tIst doch der Mensch gleichwie nichts; seine Zeit fährt dahin wie ein Schatten.",
			"5": "<span> 5 </span> \tHERR, neige deinen Himmel und fahre herab; rühre die Berge an, daß sie rauchen.",
			"6": "<span> 6 </span> \tSende Blitze und streue sie aus, schick deine Pfeile und jage sie dahin,",
			"7": "<span> 7 </span> \tstreck aus deine Hand von der Höhe. Erlöse mich und errette mich aus großen Wassern, aus der Hand der Fremden,",
			"8": "<span> 8 </span> \tderen Mund unnütz redet und deren rechte Hand trügt.",
			"9": "<span> 9 </span> \tGott, ich will dir ein neues Lied singen, ich will dir spielen auf dem Psalter von zehn Saiten,",
			"10": "<span> 10 </span> \tder du den Königen Sieg gibst und erlösest deinen Knecht David vom mörderischen Schwert.",
			"11": "<span> 11 </span> \tErlöse mich und errette mich aus der Hand der Fremden, deren Mund Falsches redet und deren rechte Hand trügt.",
			"12": "<span> 12 </span> \tUnsere Söhne seien wie Pflanzen, die aufschießen in ihrer Jugendkraft – unsere Töchter wie Säulen, geschnitzt für Paläste -",
			"13": "<span> 13 </span> \tunsere Kammern gefüllt, daß sie Vorrat geben, einen nach dem andern – unsere Schafe, daß sie Tausende werfen und Zehntausende auf unsern Triften -",
			"14": "<span> 14 </span> \tunsere Rinder, daß sie tragen ohne Schaden und Verlust – und kein Klagegeschrei sei auf unsern Gassen. -",
			"15": "<span> 15 </span> \tWohl dem Volk, dem es so ergeht! Wohl dem Volk, dessen Gott der HERR ist!"
		},
		"145": {
			"1": "<span>1</span> \tEIN LOBLIED DAVIDS. Ich will dich erheben, mein Gott, du König, und deinen Namen loben immer und ewiglich.",
			"2": "<span> 2 </span> \tIch will dich täglich loben und deinen Namen rühmen immer und ewiglich.",
			"3": "<span> 3 </span> \tDer HERR ist groß und sehr zu loben, und seine Größe ist unausforschlich.",
			"4": "<span> 4 </span> \tKindeskinder werden deine Werke preisen und deine gewaltigen Taten verkündigen.",
			"5": "<span> 5 </span> \tSie sollen reden von deiner hohen, herrlichen Pracht und deinen Wundern nachsinnen;",
			"6": "<span> 6 </span> \tsie sollen reden von deinen mächtigen Taten und erzählen von deiner Herrlichkeit;",
			"7": "<span> 7 </span> \tsie sollen preisen deine große Güte und deine Gerechtigkeit rühmen.",
			"8": "<span> 8 </span> \tGnädig und barmherzig ist der HERR, geduldig und von großer Güte.",
			"9": "<span> 9 </span> \tDer HERR ist allen gütig und erbarmt sich aller seiner Werke.",
			"10": "<span> 10 </span> \tEs sollen dir danken, HERR, alle deine Werke und deine Heiligen dich loben",
			"11": "<span> 11 </span> \tund die Ehre deines Königtums rühmen und von deiner Macht reden,",
			"12": "<span> 12 </span> \tdaß den Menschen deine gewaltigen Taten kundwerden und die herrliche Pracht deines Königtums.",
			"13": "<span> 13 </span> \tDein Reich ist ein ewiges Reich, und deine Herrschaft währet für und für.\r\nDer HERR ist getreu in all seinen Worten und gnädig in allen seinen Werken.",
			"14": "<span> 14 </span> \tDer HERR hält alle, die da fallen, und richtet alle auf, die niedergeschlagen sind.",
			"15": "<span> 15 </span> \tAller Augen warten auf dich, und du gibst ihnen ihre Speise zur rechten Zeit.",
			"16": "<span> 16 </span> \tDu tust deine Hand auf und sättigst alles, was lebt, nach deinem Wohlgefallen.",
			"17": "<span> 17 </span> \tDer HERR ist gerecht in allen seinen Wegen und gnädig in allen seinen Werken.",
			"18": "<span> 18 </span> \tDer HERR ist nahe allen, die ihn anrufen, allen, die ihn ernstlich anrufen.",
			"19": "<span> 19 </span> \tEr tut, was die Gottesfürchtigen begehren, und hört ihr Schreien und hilft ihnen.",
			"20": "<span> 20 </span> \tDer HERR behütet alle, die ihn lieben, und wird vertilgen alle Gottlosen.",
			"21": "<span> 21 </span> \tMein Mund soll des HERRN Lob verkündigen, und alles Fleisch lobe seinen heiligen Namen immer und ewiglich."
		},
		"146": {
			"1": "<span>1</span> \tHalleluja! Lobe den HERRN, meine Seele!",
			"2": "<span> 2 </span> \tIch will den HERRN loben, solange ich lebe, und meinem Gott lobsingen, solange ich bin.",
			"3": "<span> 3 </span> \tVerlasset euch nicht auf Fürsten; sie sind Menschen, die können ja nicht helfen.",
			"4": "<span> 4 </span> \tDenn des Menschen Geist muß davon, und er muß wieder zu Erde werden; dann sind verloren alle seine Pläne.",
			"5": "<span> 5 </span> \tWohl dem, dessen Hilfe der Gott Jakobs ist, der seine Hoffnung setzt auf den HERRN, seinen Gott,",
			"6": "<span> 6 </span> \tder Himmel und Erde gemacht hat, das Meer und alles, was darinnen ist; der Treue hält ewiglich,",
			"7": "<span> 7 </span> \tder Recht schafft denen, die Gewalt leiden, der die Hungrigen speiset.\r\nDer HERR macht die Gefangenen frei.",
			"8": "<span> 8 </span> \tDer HERR macht die Blinden sehend. Der HERR richtet auf, die niedergeschlagen sind. Der HERR liebt die Gerechten.",
			"9": "<span> 9 </span> \tDer HERR behütet die Fremdlinge und erhält Waisen und Witwen; aber die Gottlosen führt er in die Irre.",
			"10": "<span> 10 </span> \tDer HERR ist König ewiglich, dein Gott, Zion, für und für. Halleluja!"
		},
		"147": {
			"1": "<span>1</span> \tHalleluja! Lobet den HERRN! Denn unsern Gott loben, das ist ein köstlich Ding, ihn loben ist lieblich und schön.",
			"2": "<span> 2 </span> \tDer HERR baut Jerusalem auf und bringt zusammen die Verstreuten Israels.",
			"3": "<span> 3 </span> \tEr heilt, die zerbrochenen Herzens sind, und verbindet ihre Wunden.",
			"4": "<span> 4 </span> \tEr zählt die Sterne und nennt sie alle mit Namen.",
			"5": "<span> 5 </span> \tUnser Herr ist groß und von großer Kraft, und unbegreiflich ist, wie er regiert.",
			"6": "<span> 6 </span> \tDer HERR richtet die Elenden auf und stößt die Gottlosen zu Boden.",
			"7": "<span> 7 </span> \tSingt dem HERRN ein Danklied und lobt unsern Gott mit Harfen,",
			"8": "<span> 8 </span> \tder den Himmel mit Wolken bedeckt und Regen gibt auf Erden; der Gras auf den Bergen wachsen läßt,",
			"9": "<span> 9 </span> \tder dem Vieh sein Futter gibt, den jungen Raben, die zu ihm rufen.",
			"10": "<span> 10 </span> \tEr hat keine Freude an der Stärke des Rosses und kein Gefallen an den Schenkeln des Mannes.",
			"11": "<span> 11 </span> \tDer HERR hat Gefallen an denen, die ihn fürchten, die auf seine Güte hoffen.",
			"12": "<span> 12 </span> \tPreise, Jerusalem, den HERRN; lobe, Zion, deinen Gott!",
			"13": "<span> 13 </span> \tDenn er macht fest die Riegel deiner Tore und segnet deine Kinder in deiner Mitte.",
			"14": "<span> 14 </span> \tEr schafft deinen Grenzen Frieden und sättigt dich mit dem besten Weizen.",
			"15": "<span> 15 </span> \tEr sendet sein Gebot auf die Erde, sein Wort läuft schnell.",
			"16": "<span> 16 </span> \tEr gibt Schnee wie Wolle, er streut Reif wie Asche.",
			"17": "<span> 17 </span> \tEr wirft seine Schloßen herab wie Brocken; wer kann bleiben vor seinem Frost?",
			"18": "<span> 18 </span> \tEr sendet sein Wort, da schmilzt der Schnee; er läßt seinen Wind wehen, da taut es.",
			"19": "<span> 19 </span> \tEr verkündigt Jakob sein Wort, Israel seine Gebote und sein Recht.",
			"20": "<span> 20 </span> \tSo hat er an keinem Volk getan; sein Recht kennen sie nicht. Halleluja!"
		},
		"148": {
			"1": "<span>1</span> \tHalleluja! Lobet im Himmel den HERRN, lobet ihn in der Höhe!",
			"2": "<span> 2 </span> \tLobet ihn, alle seine Engel, lobet ihn, all sein Heer!",
			"3": "<span> 3 </span> \tLobet ihn, Sonne und Mond, lobet ihn, alle leuchtenden Sterne!",
			"4": "<span> 4 </span> \tLobet ihn, ihr Himmel aller Himmel und ihr Wasser über dem Himmel!",
			"5": "<span> 5 </span> \tDie sollen loben den Namen des HERRN; denn er gebot, da wurden sie geschaffen.",
			"6": "<span> 6 </span> \tEr läßt sie bestehen für immer und ewig; er gab eine Ordnung, die dürfen sie nicht überschreiten.",
			"7": "<span> 7 </span> \tLobet den HERRN auf Erden, ihr großen Fische und alle Tiefen des Meeres,",
			"8": "<span> 8 </span> \tFeuer, Hagel, Schnee und Nebel, Sturmwinde, die sein Wort ausrichten,",
			"9": "<span> 9 </span> \tihr Berge und alle Hügel, fruchttragende Bäume und alle Zedern,",
			"10": "<span> 10 </span> \tihr Tiere und alles Vieh, Gewürm und Vögel,",
			"11": "<span> 11 </span> \tihr Könige auf Erden und alle Völker, Fürsten und alle Richter auf Erden,",
			"12": "<span> 12 </span> \tJünglinge und Jungfrauen, Alte mit den Jungen!",
			"13": "<span> 13 </span> \tDie sollen loben den Namen des HERRN; denn sein Name allein ist hoch, seine Herrlichkeit reicht, so weit Himmel und Erde ist.",
			"14": "<span> 14 </span> \tEr erhöht die Macht seines Volkes. Alle seine Heiligen sollen loben, die Kinder Israel, das Volk, das ihm dient. Halleluja!"
		},
		"149": {
			"1": "<span>1</span> \tHalleluja! Singet dem HERRN ein neues Lied; die Gemeinde der Heiligen soll ihn loben.",
			"2": "<span> 2 </span> \tIsrael freue sich seines Schöpfers, die Kinder Zions seien fröhlich über ihren König.",
			"3": "<span> 3 </span> \tSie sollen loben seinen Namen im Reigen, mit Pauken und Harfen sollen sie ihm spielen.",
			"4": "<span> 4 </span> \tDenn der HERR hat Wohlgefallen an seinem Volk, er hilft den Elenden herrlich.",
			"5": "<span> 5 </span> \tDie Heiligen sollen fröhlich sein und preisen und rühmen auf ihren Lagern.",
			"6": "<span> 6 </span> \tIhr Mund soll Gott erheben; sie sollen scharfe Schwerter in ihren Händen halten,",
			"7": "<span> 7 </span> \tdaß sie Vergeltung üben unter den Heiden, Strafe unter den Völkern,",
			"8": "<span> 8 </span> \tihre Könige zu binden mit Ketten und ihre Edlen mit eisernen Fesseln,",
			"9": "<span> 9 </span> \tdaß sie an ihnen vollziehen das Gericht, wie geschrieben ist. Solche Ehre werden alle seine Heiligen haben. Halleluja!"
		},
		"150": {
			"1": "<span>1</span> \tHalleluja! Lobet Gott in seinem Heiligtum, lobet ihn in der Feste seiner Macht!",
			"2": "<span> 2 </span> \tLobet ihn für seine Taten, lobet ihn in seiner großen Herrlichkeit!",
			"3": "<span> 3 </span> \tLobet ihn mit Posaunen, lobet ihn mit Psalter und Harfen!",
			"4": "<span> 4 </span> \tLobet ihn mit Pauken und Reigen, lobet ihn mit Saiten und Pfeifen!",
			"5": "<span> 5 </span> \tLobet ihn mit hellen Zimbeln, lobet ihn mit klingenden Zimbeln!",
			"6": "<span> 6 </span> \tAlles, was Odem hat, lobe den HERRN! Halleluja!"
		}
	}
};
module.exports = book;