var book = {
	"name": "اشعيا",
	"numChapters": 66,
	"chapters": {
		"1": {
			"1": "<span>1 </span> &nbsp;رؤیای‌ اشعیا ابن‌ آموص‌ كه‌ آن‌ را درباره‌یهودا و اورشلیم‌، در روزهای‌ عزّیا و یوتام‌ و آحاز و حزقیا، پادشاهان‌ یهودا دید.",
			"2": "<span>2 </span> &nbsp;ای‌ آسمان‌ بشنو و ای‌ زمین‌ گوش‌ بگیر زیرا خداوند سخن‌ می‌گوید. پسران‌ پروردم‌ و برافراشتم‌ امّا ایشان‌ بر من‌ عصیان‌ ورزیدند.",
			"3": "<span>3 </span> &nbsp;گاو مالك‌ خویش‌ را و الاغ‌ آخور صاحب‌ خود را می‌شناسد، امّا اسرائیل‌ نمی‌شناسند و قوم‌ من‌ فهم‌ ندارند.",
			"4": "<span>4 </span> &nbsp;وای‌ بر امّت‌ خطاكار و قومی‌ كه‌ زیر بار گناه‌ می‌باشند و بر ذریت‌ شریران‌ و پسران‌ مفسد. خداوند را ترك‌ كردند و قدّوس‌ اسرائیل‌ را اهانت‌ نمودند و بسوی‌ عقب‌ منحرف‌ شدند.",
			"5": "<span>5 </span> &nbsp;چرا دیگر ضرب‌ یابید و زیاده‌ فتنه‌ نمایید؟ تمامی‌ سر بیمار است‌ و تمامی‌ دل‌ مریض‌.",
			"6": "<span>6 </span> &nbsp;از كف‌ پا تا به‌ سر در آن‌ تندرستی‌ نیست‌ بلكه‌ جراحت‌ و كوفتگی‌ و زخم‌ متعفّن‌، كه‌ نه‌ بخیه‌ شده‌ و نه‌ بسته‌ گشته‌ و نه‌ با روغن‌ التیام‌ شده‌ است‌.",
			"7": "<span>7 </span> &nbsp;ولایت‌ شما ویران‌ و شهرهای‌ شما به‌ آتش‌ سوخته‌ شده‌ است‌. غریبان‌، زمین‌ شما را در نظر شما می‌خورند و آن‌ مثل‌ واژگونی‌ بیگانگان‌ خراب‌ گردیده‌ است‌.",
			"8": "<span>8 </span> &nbsp;و دختر صهیون‌ مثل‌ سایه‌بان‌ در تاكستان‌ و مانند كپر در بوستان‌ خیار و مثل‌ شهر محاصره‌ شده‌، متروك‌ است‌.",
			"9": "<span>9 </span> &nbsp;اگر یهوه‌صبایوت‌ بقیه‌ اندكی‌ برای‌ ما وا نمی‌گذاشت‌، مثل‌ سدوم‌ می‌شدیم‌ و مانند عموره‌ می‌گشتیم‌.",
			"10": "<span>10 </span> &nbsp;ای‌ حاكمان‌ سدوم‌ كلام‌ خداوند را بشنوید و ای‌ قوم‌ عموره‌ شریعت‌ خدای‌ ما را گوش‌ بگیرید.",
			"11": "<span>11 </span> &nbsp;خداوند می‌گوید از كثرت‌ قربانی‌های‌ شما مرا چه‌ فایده‌ است‌؟ از قربانی‌های‌ سوختنی‌ قوچها و پیه‌ پرواریها سیر شده‌ام‌ و به‌ خون‌ گاوان‌ و بره‌ها و بزها رغبت‌ ندارم‌.",
			"12": "<span>12 </span> &nbsp;وقتی‌ كه‌ می‌آیید تا به‌ حضور من‌ حاضر شوید، كیست‌ كه‌ این‌ را از دست‌ شما طلبیده‌ است‌ كه‌ دربار مرا پایمال‌ كنید؟",
			"13": "<span>13 </span> &nbsp;هدایای‌ باطل‌ دیگر میاورید. بخور نزد من‌ مكروه‌ است‌ و غرّه‌ ماه‌ و سَبَّت‌ و دعوت‌ جماعت‌ نیز. گناه‌ را با محفل‌ مقدّس‌ نمی‌توانم‌ تحمّل‌ نمایم‌.",
			"14": "<span>14 </span> &nbsp;غرّه‌ها و عیدهای‌ شما را جان‌ من‌ نفرت‌ دارد؛ آنها برای‌ من‌ بار سنگین‌ است‌ كه‌ از تحمّل‌ نمودنش‌ خسته‌ شده‌ام‌.",
			"15": "<span>15 </span> &nbsp;هنگامی‌ كه‌ دستهای‌ خود را دراز می‌كنید، چشمان‌ خود را از شما خواهم‌ پوشانید و چون‌ دعای‌ بسیار می‌كنید، اجابت‌ نخواهم‌ نمود زیرا كه‌ دستهای‌ شما پر از خون‌ است.",
			"16": "<span>16 </span> &nbsp;خویشتن‌ را شسته‌، طاهر نمایید و بدی‌ اعمال‌ خویش‌ را از نظر من‌ دور كرده‌، از شرارت‌ دست‌ بردارید.",
			"17": "<span>17 </span> &nbsp;نیكوكاری‌ را بیاموزید و انصاف‌ را بطلبید. مظلومان‌ را رهایی‌ دهید، یتیمان‌ را دادرسی‌ كنید و بیوه‌ زنان‌ را حمایت‌ نمایید.",
			"18": "<span>18 </span> &nbsp;خداوند می‌گوید: «بیایید تا با همدیگر محاجه‌ نماییم‌. اگر گناهان‌ شما مثل‌ ارغوان‌ باشد مانند برف‌ سفید خواهد شد و اگر مثل‌ قرمز سرخ‌ باشد، مانند پشم‌ خواهد شد.",
			"19": "<span>19 </span> &nbsp;اگر خواهش‌ داشته‌، اطاعت‌ نمایید، نیكویی‌ زمین‌ را خواهید خورد.",
			"20": "<span>20 </span> &nbsp;امّا اگر ابا نموده‌، تمرّد كنید، شمشیر شما را خواهد خورد»، زیرا كه‌ دهان‌ خداوند چنین‌ می‌گوید.",
			"21": "<span>21 </span> &nbsp;شهر امین‌ چگونه‌ زانیه‌ شده‌ است‌. آنكه‌ از انصاف‌ مملّو می‌بود و عدالت‌ در وی‌ سكونت‌ می‌داشت‌، امّا حال‌ قاتلان‌.",
			"22": "<span>22 </span> &nbsp;نقره‌ تو به‌ دُرد مبدّل‌ شده‌، و شراب‌ تو از آب‌ ممزوج‌ گشته‌ است‌.",
			"23": "<span>23 </span> &nbsp;سروران‌ تو متمرّد شده‌ و رفیق‌ دزدان‌ گردیده‌، هریك‌ از ایشان‌ رشوه‌ را دوست‌ می‌دارند و در پی‌ هدایا می‌روند. یتیمان‌ را دادرسی‌ نمی‌نمایند و دعوی‌ بیوه‌ زنان‌ نزد ایشان‌ نمی‌رسد.",
			"24": "<span>24 </span> &nbsp;بنابراین‌، خداوند یهوه‌ صبایوت‌، قدیر اسرائیل‌ می‌گوید: «هان‌ من‌ از خصمان‌ خود استراحت‌ خواهم‌ یافت‌ و از دشمنان‌ خویش‌ انتقام‌ خواهم‌ كشید.",
			"25": "<span>25 </span> &nbsp;و دست‌ خود را بر تو برگردانیده‌، دُرْد تو را بالكلّ پاك‌ خواهم‌ كرد، و تمامی‌ ریمت‌ را دور خواهم‌ ساخت‌.",
			"26": "<span>26 </span> &nbsp;و داوران‌ تو را مثل‌ اوّل‌ و مشیران‌ تو را مثل‌ ابتدا خواهم‌ برگردانید و بعد از آن‌، به‌ شهر عدالت‌ و قریه‌ امین‌ مسمّی‌ خواهی‌ شد.»",
			"27": "<span>27 </span> &nbsp;صهیون‌ به‌ انصاف‌ فدیه‌ داده‌ خواهد شد و انابت‌ كنندگانش‌ به‌ عدالت‌.",
			"28": "<span>28 </span> &nbsp;و هلاكت‌ عاصیان‌ و گناهكاران‌ با هم‌ خواهد شد و آنانی‌ كه‌ خداوند را ترك‌ نمایند، نابود خواهند گردید.",
			"29": "<span>29 </span> &nbsp;زیرا ایشان‌ از درختان‌ بلوطی‌ كه‌ شما خواسته‌ بودید خجل‌ خواهند شد و از باغاتی‌ كه‌ شما برگزیده‌بودید رسوا خواهند گردید.",
			"30": "<span>30 </span> &nbsp;زیرا شما مثل‌ بلوطی‌ كه‌ برگش‌ پژمرده‌ و مانند باغی‌ كه‌ آب‌ نداشته‌ باشد خواهید شد.",
			"31": "<span>31 </span> &nbsp;و مرد زورآور پُرزه‌ كتان‌ و عملش‌ شعله‌ خواهد شد و هردوی‌ آنها با هم‌ سوخته‌ خواهند گردید و خاموش‌ كننده‌ای‌ نخواهد بود."
		},
		"2": {
			"1": "<span>1 </span> &nbsp;كلامی‌ كه‌ اشعیا ابن‌ آموص‌ دربارۀ یهودا و اورشلیم‌ دید.",
			"2": "<span>2 </span> &nbsp;و در ایام‌ آخر واقع‌ خواهد شد كه‌ كوه‌ خانه‌ خداوند برقلّه‌ كوهها ثابت‌ خواهد شد و فوق‌ تلّها برافراشته‌ خواهد گردید و جمیع‌ امّت‌ها بسوی‌ آن‌ روان‌ خواهند شد.",
			"3": "<span>3 </span> &nbsp;و قوم‌های‌ بسیار عزیمت‌ كرده‌، خواهند گفت‌: «بیایید تا به‌ كوه‌ خداوند و به‌ خانه‌ خدای‌ یعقوب‌ برآییم‌ تا طریق‌های‌ خویش‌ را به‌ ما تعلیم‌ دهد و به‌ راههای‌ وی‌ سلوك‌ نماییم‌.» زیرا كه‌ شریعت‌ از صهیون‌ و كلام‌ خداوند از اورشلیم‌ صادر خواهد شد.",
			"4": "<span>4 </span> &nbsp;و او امّت‌ها را داوری‌ خواهد نمود و قوم‌های‌ بسیاری‌ را تنبیه‌ خواهد كرد و ایشان‌ شمشیرهای‌ خود را برای‌ گاوآهن‌ و نیزه‌های‌ خویش‌ را برای‌ ارّه‌ها خواهند شكست‌ و امّتی‌ بر امّتی‌ شمشیر نخواهد كشید و بار دیگر جنگ‌ را نخواهند آموخت‌.",
			"5": "<span>5 </span> &nbsp;ای‌ خاندان‌ یعقوب‌ بیایید تا در نور خداوند سلوك‌ نماییم‌.",
			"6": "<span>6 </span> &nbsp;زیرا قوم‌ خود یعنی‌ خاندان‌ یعقوب‌ را ترك‌ كرده‌ای‌، چونكه‌ از رسوم‌ مشرقی‌ مملّو و مانندفلسطینیان‌ فالگیر شده‌اند و با پسران‌ غربا دست‌ زده‌اند،",
			"7": "<span>7 </span> &nbsp;و زمین‌ ایشان‌ از نقره‌ و طلا پر شده‌ و خزاین‌ ایشان‌ را انتهایی‌ نیست‌، و زمین‌ ایشان‌ از اسبان‌ پر است‌ و ارابه‌های‌ ایشان‌ را انتهایی‌ نیست‌؛",
			"8": "<span>8 </span> &nbsp;و زمین‌ ایشان‌ از بتها پر است‌؛ صنعت‌ دستهای‌ خویش‌ را كه‌ به‌ انگشتهای‌ خود ساخته‌اند سجده‌ می‌نمایند.",
			"9": "<span>9 </span> &nbsp;و مردم‌ خم‌ شده‌ و مردان‌ پست‌ می‌شوند. لهذا ایشان‌ را نخواهی‌ آمرزید.",
			"10": "<span>10 </span> &nbsp;از ترس‌ خداوند و از كبریای‌ جلال‌ وی‌ به‌ صخره‌ داخل‌ شده‌، خویشتن‌ را در خاك‌ پنهان‌ كن‌.",
			"11": "<span>11 </span> &nbsp;چشمان‌ بلند انسان‌ پست‌ و تكبّر مردان‌ خم‌ خواهد شد و در آن‌ روز خداوند به‌ تنهایی‌ متعال‌ خواهد بود.",
			"12": "<span>12 </span> &nbsp;زیرا كه‌ برای‌ یهوه‌ صبایوت‌ روزی‌ است‌ كه‌ بر هرچیزِ بلند و عالی‌ خواهد آمد و بر هرچیز مرتفع‌، و آنها پست‌ خواهد شد؛",
			"13": "<span>13 </span> &nbsp;و بر همه‌ سروهای‌ آزاد بلند و رفیع‌ لبنان‌ و بر تمامی‌ بلوطهای‌ باشان‌؛",
			"14": "<span>14 </span> &nbsp;و بر همه‌ كوههای‌ عالی‌ و بر جمیع‌ تلّهای‌ بلند؛",
			"15": "<span>15 </span> &nbsp;و بر هربرج‌ مرتفع‌ و بر هر حصار منیع‌؛",
			"16": "<span>16 </span> &nbsp;و برهمه‌ كشتیهای‌ ترشیش‌ و برهمه‌ مصنوعات‌ مرغوب‌؛",
			"17": "<span>17 </span> &nbsp;و كبریای‌ انسان‌ خم‌ شود و تكبّر مردان‌ پست‌ خواهد شد. و در آن‌ روز خداوند به‌ تنهایی‌ متعال‌ خواهد بود،",
			"18": "<span>18 </span> &nbsp;و بتها بالكلّ تلف‌ خواهند شد.",
			"19": "<span>19 </span> &nbsp;و ایشان‌ به‌ مغاره‌های‌ صخره‌ها و حفره‌های‌ خاك‌ داخل‌ خواهند شد، به‌ سبب‌ ترس‌ خداوند و كبریای‌ جلال‌ وی‌ هنگامی‌ كه‌ او برخیزد تا زمین‌ را متزلزل‌ سازد.",
			"20": "<span>20 </span> &nbsp;در آن‌ روز مردمان‌، بتهای‌ نقره‌ و بتهای‌ طلای‌ خود را كه‌ برای‌ عبادت‌ خویش‌ ساخته‌اند، نزد موش‌كوران‌ و خفّاشها خواهند انداخت‌،",
			"21": "<span>21 </span> &nbsp;تا به‌ مغاره‌های‌ صخره‌ها و شكافهای‌سنگ‌ خارا داخل‌ شوند، به‌ سبب‌ ترس‌ خداوند و كبریای‌ جلال‌ وی‌ هنگامی‌ كه‌ او برخیزد تا زمین‌ را متزلزل‌ سازد.",
			"22": "<span>22 </span> &nbsp;شما از انسانی‌ كه‌ نفس‌ او در بینی‌اش‌ می‌باشد، دست‌ بركشید زیرا كه‌ او به‌ چه‌ چیز محسوب‌ می‌شود؟"
		},
		"3": {
			"1": "<span>1 </span> &nbsp;زیرا اینك‌ خداوند یهوه‌ صبایوت‌ پایه‌ و ركن‌ را از اورشلیم‌ و یهودا، یعنی‌ تمامی‌ پایه‌ نان‌ و تمامی‌ پایه‌ آب‌ را دور خواهد كرد،",
			"2": "<span>2 </span> &nbsp;و شجاعان‌ و مردان‌ جنگی‌ و داوران‌ و انبیا و فالگیران‌ و مشایخ‌ را،",
			"3": "<span>3 </span> &nbsp;و سرداران‌ پنجاهه‌ و شریفان‌ و مشیران‌ و صنعت‌گران‌ ماهر و ساحران‌ حاذق‌ را.",
			"4": "<span>4 </span> &nbsp;و اطفال‌ را بر ایشان‌ حاكم‌ خواهم‌ ساخت‌ و كودكان‌ بر ایشان‌ حكمرانی‌ خواهند نمود.",
			"5": "<span>5 </span> &nbsp;و قوم‌ مظلوم‌ خواهند شد، هركس‌ از دست‌ دیگری‌ و هرشخص‌ از همسایه‌ خویش‌. و اطفال‌ بر پیران‌ و پَستان‌ بر شریفان‌ تمرّد خواهند نمود.",
			"6": "<span>6 </span> &nbsp;چون‌ شخصی‌ به‌ برادر خویش‌ در خانه‌ پدرش‌ متمسّك‌ شده‌، بگوید: «تو را رُخوُت‌ هست‌؟ پس‌ حاكم‌ ما شو و این‌ خرابی‌ در زیر دست‌ تو باشد»،",
			"7": "<span>7 </span> &nbsp;در آن‌ روز او آواز خود را بلند كرده‌، خواهد گفت‌: «من‌ علاج‌ كننده‌ نتوانم‌ شد زیرا در خانه‌ من‌ نه‌ نان‌ و نه‌ لباس‌ است‌ پس‌ مرا حاكم‌ قوم‌ مسازید.»",
			"8": "<span>8 </span> &nbsp;زیرا اورشلیم‌ خراب‌ شده‌ و یهودا منهدم‌ گشته‌ است‌، از آن‌ جهت‌ كه‌ لسان‌ و افعال‌ ایشان‌ به‌ ضدّ خداوند می‌باشد تا چشمان‌ جلال‌ او را به‌ ننگ‌ آورند.",
			"9": "<span>9 </span> &nbsp;سیمای‌ رویهای‌ ایشان‌ به‌ ضدّ ایشان‌ شاهد است‌ و مثل‌ سدوم‌ گناهان‌ خود رافاش‌ كرده‌، آنها را مخفی‌ نمی‌دارند. وای‌ بر جانهای‌ ایشان‌ زیرا كه‌ به‌ جهت‌ خویشتن‌ شرارت‌ را بعمل‌ آورده‌اند.",
			"10": "<span>10 </span> &nbsp;عادلان‌ را بگویید كه‌ ایشان‌ را سعادتمندی‌ خواهد بود زیرا از ثمره‌ اعمال‌ خویش‌ خواهند خورد.",
			"11": "<span>11 </span> &nbsp;وای‌ بر شریران‌ كه‌ ایشان‌ را بدی‌ خواهد بود چونكه‌ مكافات‌ دست‌ ایشان‌ به‌ ایشان‌ كرده‌ خواهد شد.",
			"12": "<span>12 </span> &nbsp;و امّا قوم‌ من‌، كودكان‌ بر ایشان‌ ظلم‌ می‌كنند و زنان‌ بر ایشان‌ حكمرانی‌ می‌نمایند. ای‌ قوم‌ من‌، راهنمایان‌ شما گمراه‌ كنندگانند و طریق‌ راههای‌ شما را خراب‌ می‌كنند.",
			"13": "<span>13 </span> &nbsp;خداوند برای‌ مُحاجّه‌ برخاسته‌ و به‌ جهت‌ داوری‌ قومها ایستاده‌ است‌.",
			"14": "<span>14 </span> &nbsp;خداوند با مشایخ‌ قوم‌ خود و سروران‌ ایشان‌ به‌ محاكمه‌ درخواهد آمد، زیرا شما هستید كه‌ تاكستانها را خورده‌اید و غارت‌ فقیران‌ در خانه‌های‌ شما است‌.",
			"15": "<span>15 </span> &nbsp;خداوند یهوه‌ صبایوت‌ می‌گوید: «شما را چه‌ شده‌ است‌ كه‌ قوم‌ مرا می‌كوبید و رویهای‌ فقیران‌ را خرد می‌نمایید؟»",
			"16": "<span>16 </span> &nbsp;و خداوند می‌گوید: «از این‌ جهت‌ كه‌ دختران‌ صهیون‌ متكبّرند و با گردن‌ افراشته‌ و غمزات‌ چشم‌ راه‌ می‌روند و به‌ ناز می‌خرامند و به‌ پایهای‌ خویش‌ خلخالها را به‌ صدا می‌آورند.»",
			"17": "<span>17 </span> &nbsp;بنابراین‌ خداوند فرق‌ سر دختران‌ صهیون‌ را كَل‌ خواهد ساخت‌ و خداوند عورت‌ ایشان‌ را برهنه‌ خواهد نمود.",
			"18": "<span>18 </span> &nbsp;و در آن‌ روز خداوند زینت‌ خلخالها و پیشانی‌ بندها و هلالها را دور خواهد كرد.",
			"19": "<span>19 </span> &nbsp;و گوشواره‌ها و دستبندها و روبندها را،",
			"20": "<span>20 </span> &nbsp;و دستارها و زنجیرها و كمربندها و عطردانها وتعویذها را،",
			"21": "<span>21 </span> &nbsp;و انگشترها و حلقه‌های‌ بینی‌ را،",
			"22": "<span>22 </span> &nbsp;و رخوت‌ نفیسه‌ و رداها و شالها و كیسه‌ها را،",
			"23": "<span>23 </span> &nbsp;و آینه‌ها و كتان‌ نازك‌ و عمامه‌ها و بُرقِع‌ها را.",
			"24": "<span>24 </span> &nbsp;و واقع‌ می‌شود كه‌ به‌ عوض‌ عطریات‌، عفونت‌ خواهد شد و به‌ عوض‌ كمربند، ریسمان‌ و به‌ عوض‌ مویهای‌ بافته‌، كَلی‌ و به‌ عوض‌ سینه‌بند، زنّار پلاس‌ و به‌ عوض‌ زیبایی‌، سوختگی‌ خواهد بود.",
			"25": "<span>25 </span> &nbsp;مردانت‌ به‌ شمشیر و شجاعانت‌ در جنگ‌ خواهند افتاد.",
			"26": "<span>26 </span> &nbsp;و دروازه‌های‌ وی‌ ناله‌ و ماتم‌ خواهند كرد، و او خراب‌ شده‌، بر زمین‌ خواهد نشست‌."
		},
		"4": {
			"1": "<span>1 </span> &nbsp;و در آن‌ روز هفت‌ زن‌ به‌ یك‌ مرد متمسّك شده‌، خواهند گفت‌: «نان‌ خود را خواهیم‌ خورد و رخت‌ خود را خواهیم‌ پوشید، فقط‌ نام‌ تو بر ما خوانده‌ شود و عار ما را بردار.»",
			"2": "<span>2 </span> &nbsp;در آن‌ روز شاخه‌ خداوند زیبا و جلیل‌ و میوه‌ زمین‌ به‌ جهت‌ ناجیان‌ اسرائیل‌ فخر و زینت‌ خواهد بود.",
			"3": "<span>3 </span> &nbsp;و واقع‌ می‌شود كه‌ هركه‌ در صهیون‌ باقی‌ ماند و هر كه‌ در اورشلیم‌ ترك‌ شود، مقدّس‌ خوانده‌ خواهد شد یعنی‌ هركه‌ در اورشلیم‌ در دفتر حیات‌ مكتوب‌ باشد.",
			"4": "<span>4 </span> &nbsp;هنگامی‌ كه‌ خداوند چرك‌ دختران‌ صهیون‌ را بشوید و خون‌ اورشلیم‌ را به‌ روح‌ انصاف‌ و روح‌ سوختگی‌ را از میانش‌ رفع‌ نماید،",
			"5": "<span>5 </span> &nbsp;خداوند بر جمیع‌ مساكن‌ كوه‌ صهیون‌ و بر محفلهایش‌ ابر و دود در روز و درخشندگی‌ آتشِ مشتعل‌ در شب‌ خواهد آفرید، زیرا كه‌ بر تمامی‌ جلال‌ آن‌ پوششی‌ خواهد بود.",
			"6": "<span>6 </span> &nbsp;و در وقت‌ روز سایه‌بانی‌ به‌ جهت‌ سایه‌ از گرما و به‌ جهت‌ ملجاء و پناهگاه‌ از طوفان‌ و باران‌ خواهد بود."
		},
		"5": {
			"1": "<span>1 </span> &nbsp;سرود محبوب‌ خود را دربارۀ تاكستانش برای‌ محبوب‌ خود بسرایم‌. محبوب‌ من‌ تاكستانی‌ در تلّی‌ بسیار بارور داشت‌،",
			"2": "<span>2 </span> &nbsp;و آن‌ را كنده‌ از سنگها پاك‌ كرده‌ و مو بهترین‌ در آن‌ غرس‌ نمود و برجی‌ در میانش‌ بنا كرد و چرخشتی‌ نیز در آن‌ كَند. پس‌ منتظر می‌بود تا انگور بیاورد؛ امّا انگور بد آورد.",
			"3": "<span>3 </span> &nbsp;پس‌ الا´ن‌ ای‌ ساكنان‌ اورشلیم‌ و مردان‌ یهودا، در میان‌ من‌ و تاكستان‌ من‌ حكم‌ كنید.",
			"4": "<span>4 </span> &nbsp;برای‌ تاكستان‌ من‌ دیگر چه‌ توان‌ كرد كه‌ در آن‌ نكردم‌؟ پس‌ چون‌ منتظر بودم‌ كه‌ انگور بیاورد، چرا انگور بد آورد؟",
			"5": "<span>5 </span> &nbsp;لهذا الا´ن‌ شما را اعلام‌ می‌نمایم‌ كه‌ من‌ به‌ تاكستان‌ خود چه‌ خواهم‌ كرد. حصارش‌ را برمی‌دارم‌ و چراگاه‌ خواهد شد؛ و دیوارش‌ را منهدم‌ می‌سازم‌ و پایمال‌ خواهد گردید.",
			"6": "<span>6 </span> &nbsp;و آن‌ را خراب‌ می‌كنم‌ كه‌ نه‌ پازش‌ و نه‌ كنده‌ خواهد شد و خار و خس‌ در آن‌ خواهد رویید، و ابرها را امر می‌فرمایم‌ كه‌ بر آن‌ باران‌ نباراند.",
			"7": "<span>7 </span> &nbsp;زیرا كه‌ تاكستان‌ یهوه‌ صبایوت‌ خاندان‌ اسرائیل‌ است‌ و مردان‌ یهودا نهال‌ شادمانی‌ او می‌باشند. و برای‌ انصاف‌ انتظار كشید و اینك‌ تعدّی‌ و برای‌ عدالت‌ و اینك‌ فریاد شد.",
			"8": "<span>8 </span> &nbsp;وای‌ بر آنانی‌ كه‌ خانه‌ را به‌ خانه‌ ملحق‌ و مزرعه‌ را به‌ مزرعه‌ ملصق‌ سازند تا مكانی‌ باقی‌ نماند. و شما در میان‌ زمین‌ به‌ تنهایی‌ ساكن‌ می‌شوید.",
			"9": "<span>9 </span> &nbsp;یهوه‌ صبایوت‌ در گوش‌ من‌ گفت‌: «به‌ درستی‌ كه‌ خانه‌های‌ بسیار خراب‌ خواهد شد، و خانه‌های‌ بزرگ‌ و خوش‌نما غیرمسكون‌ خواهدگردید.",
			"10": "<span>10 </span> &nbsp;زیرا كه‌ ده‌ جفت‌ گاو زمین‌ یك‌ بَت‌ خواهد آورد و یك‌ حومر تخم‌ یك‌ ایفه‌ خواهد داد.»",
			"11": "<span>11 </span> &nbsp;وای‌ بر آنانی‌ كه‌ صبح‌ زود برمی‌خیزند تا در پی‌ مسكرات‌ بروند، و شب‌ دیر می‌نشینند تا شرابْ ایشان‌ را گرم‌ نماید",
			"12": "<span>12 </span> &nbsp;و در بزمهای‌ ایشان‌ عود و بربط‌ و دفّ و نای‌ و شراب‌ می‌باشد. اما به‌ فعل‌ خداوند نظر نمی‌كنند و به‌ عمل‌ دستهای‌ وی‌ نمی‌نگرند.",
			"13": "<span>13 </span> &nbsp;بنابراین‌ قوم‌ من‌ به‌ سبب‌ عدم‌ معرفت‌ اسیر شده‌اند و شریفان‌ ایشان‌ گرسنه‌ و عوام‌ ایشان‌ از تشنگی‌ خشك‌ گردیده‌.",
			"14": "<span>14 </span> &nbsp;از این‌ سبب‌ هاویه‌ حرص‌ خود را زیاد كرده‌ و دهان‌ خویش‌ را بی‌حّد باز نموده‌ است‌ و جلال‌ و جمهور و شوكت‌ ایشان‌ و هر كه‌ در ایشان‌ شادمان‌ باشد در آن‌ فرو می‌رود.",
			"15": "<span>15 </span> &nbsp;و مردم‌ خم‌ خواهند شد و مردان‌ ذلیل‌ خواهند گردید و چشمان‌ متكبّران‌ پست‌ خواهد شد.",
			"16": "<span>16 </span> &nbsp;و یهوه‌ صبایوت‌ به‌ انصاف‌ متعال‌ خواهد بود و خدای‌ قدّوس‌ به‌ عدالت‌ تقدیس‌ كرده‌ خواهد شد.",
			"17": "<span>17 </span> &nbsp;آنگاه‌ بره‌های‌ (غربا) در مرتع‌های‌ ایشان‌ خواهند چرید و غریبان‌ ویرانه‌های‌ پرواریهای‌ ایشان‌ را خواهند خورد.",
			"18": "<span>18 </span> &nbsp;وای‌ برآنانی‌ كه‌ عصیان‌ را به‌ ریسمانهای‌ بطالت‌ و گناه‌ را گویا به‌ طناب‌ ارابه‌ می‌كشند.",
			"19": "<span>19 </span> &nbsp;و می‌گویند باشد كه‌ او تعجیل‌ نموده‌، كار خود را بشتاباند تا آن‌ را ببینیم‌. و مقصود قدّوس‌ اسرائیل‌ نزدیك‌ شده‌، بیاید تا آن‌ را بدانیم‌.",
			"20": "<span>20 </span> &nbsp;وای‌ بر آنانی‌ كه‌ بدی‌ را نیكویی‌ و نیكویی‌ را بدی‌ می‌نامند، كه‌ ظلمت‌ را به‌ جای‌ نور و نور را به‌ جای‌ ظلمت‌ می‌گذارند، و تلخی‌ را به‌ جای‌ شیرینی‌ و شیرینی‌ را به‌ جای‌ تلخی‌ می‌نهند.",
			"21": "<span>21 </span> &nbsp;وای‌ برآنانی‌ كه‌ در نظر خود حكیمند، و پیش‌ روی‌ خود فهیم‌می‌نمایند.",
			"22": "<span>22 </span> &nbsp;وای‌ بر آنانی‌ كه‌ برای‌ نوشیدن‌ شراب‌ زورآورند، و به‌ جهت‌ ممزوج‌ ساختن‌ مسكرات‌ مردان‌ قوّی‌ می‌باشند.",
			"23": "<span>23 </span> &nbsp;كه‌ شریران‌ را برای‌ رشوه‌ عادل‌ می‌شمارند، و عدالت‌ عادلان‌ را از ایشان‌ برمی‌دارند.",
			"24": "<span>24 </span> &nbsp;بنابراین‌ به‌ نهجی‌ كه‌ شراره‌ آتش‌ كاه‌ را می‌خورد و علف‌ خشك‌ در شعله‌ می‌افتد، همچنان‌ ریشه‌ ایشان‌ عفونت‌ خواهد شد و شكوفه‌ ایشان‌ مثل‌ غبار برافشانده‌ خواهد گردید. چونكه‌ شریعت‌ یهوه‌ صبایوت‌ را ترك‌ كرده‌، كلام‌ قدّوس‌ اسرائیل‌ را خوار شمرده‌اند.",
			"25": "<span>25 </span> &nbsp;بنابراین‌ خشم‌ خداوند بر قوم‌ خود مشتعل‌ شده‌ و دست‌ خود را بر ایشان‌ دراز كرده‌، ایشان‌ را مبتلا ساخته‌ است‌. و كوهها بلرزیدند و لاشهای‌ ایشان‌ در میان‌ كوچه‌ها مثل‌ فضلات‌ گردیده‌اند. با وجود این‌ همه‌، غضب‌ او برنگردید و دست‌ وی‌ تا كنون‌ دراز است‌.",
			"26": "<span>26 </span> &nbsp;و عَلَمی‌ به‌ جهت‌ امّت‌های‌ بعید برپا خواهد كرد. و از اقصای‌ زمین‌ برای‌ ایشان‌ صفیر خواهد زد. و ایشان‌ تعجیل‌ نموده‌، بزودی‌ خواهند آمد،",
			"27": "<span>27 </span> &nbsp;و درمیان‌ ایشان‌ احدی‌ خسته‌ و لغزش‌ خورنده‌ نخواهد بود و احدی‌ نه‌ پینگی‌ خواهد زد و نه‌ خواهد خوابید. و كمربند كمر احدی‌ از ایشان‌ باز نشده‌، دوّال‌ نعلین‌ احدی‌ گسیخته‌ نخواهد شد.",
			"28": "<span>28 </span> &nbsp;كه‌ تیرهای‌ ایشان‌، تیز و تمامی‌ كمانهای‌ ایشان‌، زده‌ شده‌ است‌. سُمهای‌ اسبان‌ ایشان‌ مثل‌ سنگ‌ خارا و چرخهای‌ ایشان‌ مثل‌ گردباد شمرده‌ خواهد شد.",
			"29": "<span>29 </span> &nbsp;غرّش‌ ایشان‌ مثل‌ شیر ماده‌ و مانند شیران‌ ژیان‌ غرّش‌ خواهند كرد و ایشان‌ نعره‌ خواهند زد و صید را گرفته‌، بسلامتی‌ خواهند برد و رهاننده‌ای‌نخواهد بود.",
			"30": "<span>30 </span> &nbsp;و در آن‌ روز بر ایشان‌ مثل‌ شورش‌ دریا شورش‌ خواهند كرد. و اگر كسی‌ به‌ زمین‌ بنگرد، اینك‌ تاریكی‌ و تنگی‌ است‌ و نور در افلاك‌ آن‌ به‌ ظلمت‌ مبدّل‌ شده‌ است‌."
		},
		"6": {
			"1": "<span>1 </span> &nbsp;در سالی‌ كه‌ عزّیا پادشاه‌ مرد، خداوند را دیدم‌ كه‌ بر كرسی‌ بلند و عالی‌ نشسته‌ بود، و هیكل‌ از دامنهای‌ وی‌ پر بود.",
			"2": "<span>2 </span> &nbsp;و سرافین‌ بالای‌ آن‌ ایستاده‌ بودند كه‌ هر یك‌ از آنها شش‌ بال‌ داشت‌، و با دو از آنها روی‌ خود را می‌پوشانید و با دو پایهای‌ خود را می‌پوشانید و با دو پرواز می‌نمود.",
			"3": "<span>3 </span> &nbsp;و یكی‌ دیگری‌ را صدا زده‌، می‌گفت‌: «قدّوس‌ قدّوس‌ قدّوس‌ یهوه‌ صبایوت‌، تمامی‌ زمین‌ از جلال‌ او مملّو است‌.»",
			"4": "<span>4 </span> &nbsp;و اساس‌ آستانه‌ از آواز او كه‌ صدا می‌زد می‌لرزید و خانه‌ از دود پر شد.",
			"5": "<span>5 </span> &nbsp;پس‌ گفتم‌ :«وای‌ بر من‌ كه‌ هلاك‌ شده‌ام‌ زیرا كه‌ مرد ناپاك‌ لب‌ هستم‌ و در میان‌ قوم‌ ناپاك‌ لب‌ ساكنم‌ و چشمانم‌ یهوه‌ صبایوت‌ پادشاه‌ را دیده‌ است‌.»",
			"6": "<span>6 </span> &nbsp;آنگاه‌ یكی‌ از سرافین‌ نزد من‌ پرید و در دست‌ خود اخگری‌ كه‌ با انبر از روی‌ مذبح‌ گرفته‌ بود، داشت‌.",
			"7": "<span>7 </span> &nbsp;و آن‌ را بر دهانم‌ گذارده‌، گفت‌ كه‌ «اینك‌ این‌ لبهایت‌ را لمس‌ كرده‌ است‌ و عصیانت‌ رفع‌ شده‌ و گناهت‌ كفّاره‌ گشته‌ است‌.»",
			"8": "<span>8 </span> &nbsp;آنگاه‌ آواز خداوند را شنیدم‌ كه‌ می‌گفت‌: «كه‌ را بفرستم‌ و كیست‌ كه‌ برای‌ ما برود؟» گفتم‌: «لبیك‌ مرا بفرست‌.»",
			"9": "<span>9 </span> &nbsp;گفت‌: «برو و به‌ این‌ قوم‌ بگو البّته‌ خواهید شنید، امّا نخواهید فهمید و هرآینه‌خواهید نگریست‌ امّا درك‌ نخواهید كرد.",
			"10": "<span>10 </span> &nbsp;دل‌ این‌ قوم‌ را فربه‌ ساز و گوشهای‌ ایشان‌ را سنگین‌ نما و چشمان‌ ایشان‌ را ببند، مبادا با چشمان‌ خود ببینند و با گوشهای‌ خود بشنوند و با دل‌ خود بفهمند و بازگشت‌ نموده‌، شفا یابند.»",
			"11": "<span>11 </span> &nbsp;پس‌ من‌ گفتم‌: «ای‌ خداوند تا به‌ كی‌؟» او گفت‌: «تا وقتی‌ كه‌ شهرها ویران‌ گشته‌، غیر مسكون‌ باشد و خانه‌ها بدون‌ آدمی‌ و زمین‌ خراب‌ و ویران‌ شود.",
			"12": "<span>12 </span> &nbsp;و خداوند مردمان‌ را دور كند و در میان‌ زمین‌ خرابیهای‌ بسیار شود.",
			"13": "<span>13 </span> &nbsp;امّا باز عشری‌ در آن‌ خواهد بود و آن‌ نیز بار دیگر تلف‌ خواهد گردید. مثل‌ درخت‌ بلوط‌ و چنار كه‌ چون‌ قطع‌ می‌شود، كُنده‌ آنها باقی‌ می‌ماند، همچنان‌ ذریت‌ مقدّس‌ كُنده‌ آن‌ خواهد بود.»"
		},
		"7": {
			"1": "<span>1 </span> &nbsp;و در ایام‌ آحاز بن‌ یوتام‌ بن‌ عزّیا پادشاه‌یهودا، واقع‌ شد كه‌ رصین‌، پادشاه‌ آرام‌ و فقح‌بن‌ رملیا، پادشاه‌ اسرائیل‌، بر اورشلیم‌ برآمدند تا با آن‌ جنگ‌ نمایند، امّا نتوانستند آن‌ را فتح‌ نمایند.",
			"2": "<span>2 </span> &nbsp;و به‌ خاندان‌ داود خبر داده‌، گفتند كه‌ اَرام‌ در افرایم‌ اردو زده‌اند، و دل‌ او و دل‌ مردمانش‌ بلرزید به‌ طوری‌ كه‌ درختان‌ جنگل‌ از باد می‌لرزد.",
			"3": "<span>3 </span> &nbsp;آنگاه‌ خداوند به‌ اشعیا گفت‌: «تو با پسر خود شآریاشوب‌ به‌ انتهای‌ قنات‌ بركه‌ فوقانی‌ به‌ راه‌ مزرعه‌ گازر به‌ استقبال‌ آحاز بیرون‌ شو.",
			"4": "<span>4 </span> &nbsp;و وی‌ را بگو: باحذر و آرام‌ باش‌ مترس‌ و دلت‌ ضعیف‌ نشود از این‌ دو دُمِ مشعلِ دودافشان‌، یعنی‌ از شدّت‌ خشم‌ رصین‌ و اَرام‌ و پسر رملیا.",
			"5": "<span>5 </span> &nbsp;زیرا كه‌ ارام‌ با افرایم‌ و پسر رملیا برای‌ ضرر تو مشورت‌كرده‌، می‌گویند:",
			"6": "<span>6 </span> &nbsp;بر یهودا برآییم‌ و آن‌ را محاصره‌ كرده‌، به‌ جهت‌ خویشتن‌ تسخیر نماییم‌ و پسر طبئیل‌ را در آن‌ به‌ پادشاهی‌ نصب‌ كنیم‌.»",
			"7": "<span>7 </span> &nbsp;خداوند یهوه‌ چنین‌ می‌گوید كه‌ «این‌ بجا آورده‌ نمی‌شود و واقع‌ نخواهد گردید.",
			"8": "<span>8 </span> &nbsp;زیرا كه‌ سر اَرام‌، دمشق‌ و سر دمشق‌، رصین‌ است‌ و بعد از شصت‌ و پنج‌ سال‌ افرایم‌ شكسته‌ می‌شود به‌ طوری‌ كه‌ دیگر قومی‌ نخواهد بود.",
			"9": "<span>9 </span> &nbsp;و سر افرایم‌ سامره‌ و سر سامره‌ پسر رملیا است‌ و اگر باور نكنید هرآینه‌ ثابت‌ نخواهید ماند.»",
			"10": "<span>10 </span> &nbsp;و خداوند بار دیگر آحاز را خطاب‌ كرده‌، گفت‌:",
			"11": "<span>11 </span> &nbsp;«آیتی‌ به‌ جهت‌ خود از یهوه‌ خدایت‌ بطلب‌. آن‌ را یا از عمق‌ها بطلب‌ یا از اعلی‌ علیین‌ بالا.»",
			"12": "<span>12 </span> &nbsp;آحاز گفت‌: «نمی‌طلبم‌ و خداوند را امتحان‌ نخواهم‌ نمود.»",
			"13": "<span>13 </span> &nbsp;گفت‌: «ای‌ خاندان‌ داود بشنوید! آیا شما را چیزی‌ سهل‌ است‌ كه‌ مردمان‌ را بیزار كنید بلكه‌ می‌خواهید خدای‌ مرا نیز بیزار كنید.",
			"14": "<span>14 </span> &nbsp;بنابراین‌ خود خداوند به‌ شما آیتی‌ خواهد داد: اینك‌ باكره‌ حامله‌ شده‌، پسری‌ خواهد زایید و نام‌ او را عمّانوئیل‌ خواهد خواند.",
			"15": "<span>15 </span> &nbsp;كره‌ و عسل‌ خواهد خورد تا آنكه‌ ترك‌ كردن‌ بدی‌ و اختیار كردن‌ خوبی‌ را بداند.",
			"16": "<span>16 </span> &nbsp;زیرا قبل‌ از آنكه‌ پسر، ترك‌ نمودن‌ بدی‌ و اختیار كردن‌ خوبی‌ را بداند، زمینی‌ كه‌ شما از هر دو پادشاه‌ آن‌ می‌ترسید، متروك‌ خواهد شد.",
			"17": "<span>17 </span> &nbsp;خداوند بر تو و بر قومت‌ و بر خاندان‌ پدرت‌ ایامی‌ را خواهد آورد كه‌ از ایامی‌ كه‌ افرایم‌ از یهودا جدا شد تا حال‌ نیامده‌ باشد یعنی‌ پادشاه‌ آشور را.»",
			"18": "<span>18 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ خداوند برای‌ مگسهایی‌ كه‌ به‌كناره‌های‌ نهرهای‌ مصرند و زنبورهایی‌ كه‌ در زمین‌ آشورند صفیر خواهد زد.",
			"19": "<span>19 </span> &nbsp;و تمامی‌ آنها برآمده‌، در وادیهای‌ ویران‌ و شكافهای‌ صخره‌ و بر همه‌ بوته‌های‌ خاردار و بر همه‌ مرتع‌ها فرود خواهند آمد.",
			"20": "<span>20 </span> &nbsp;و در آن‌ روز خداوند به‌ واسطه‌ استره‌ای‌ كه‌ از ماورای‌ نهر اجیر می‌شود یعنی‌ به‌ واسطه‌ پادشاه‌ آشور، موی‌ سر و موی‌ پایها را خواهد تراشید و ریش‌ هم‌ سترده‌ خواهد شد.",
			"21": "<span>21 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ شخصی‌ یك‌ گاو جوان‌ و دو گوسفند زنده‌ نگاه‌ خواهد داشت‌.",
			"22": "<span>22 </span> &nbsp;و از فراوانی‌ شیری‌ كه‌ می‌دهند كره‌ خواهد خورد زیرا هركه‌ درمیان‌ زمین‌ باقی‌ ماند، خوراكش‌ كره‌ و عسل‌ خواهد بود.",
			"23": "<span>23 </span> &nbsp;و در آن‌ روز هر مكانی‌ كه‌ هزار مو به‌ جهت‌ هزار پاره‌ نقره‌ داده‌ می‌شد، پر از خار و خس‌ خواهد بود.",
			"24": "<span>24 </span> &nbsp;با تیرها و كمانها مردم‌ به‌ آنجا خواهند آمد زیرا كه‌ تمامی‌ زمین‌ پر از خار و خس‌ خواهد شد.",
			"25": "<span>25 </span> &nbsp;و جمیع‌ كوههایی‌ كه‌ با بیل‌ كنده‌ می‌شد، از ترس‌ خار و خس‌ به‌ آنجا نخواهند آمد بلكه‌ گاوان‌ را به‌ آنجا خواهند فرستاد و گوسفندان‌ آن‌ را پایمال‌ خواهند كرد."
		},
		"8": {
			"1": "<span>1 </span> &nbsp;و خداوند مرا گفت‌: «لوحی‌ بزرگ‌ به‌ جهت خود بگیر و بر آن‌ با قلم‌ انسان‌ برای‌ مهیر شلال‌ حاش‌ بز بنویس‌.",
			"2": "<span>2 </span> &nbsp;و من‌ شهود امین‌ یعنی‌ اوریای‌ كاهن‌ و زكریا ابن‌ یبَركیا را به‌ جهت‌ خود برای‌ شهادت‌ می‌گیرم‌.»",
			"3": "<span>3 </span> &nbsp;پس‌ من‌ به‌ نبیه‌ نزدیكی‌ كردم‌ و او حامله‌ شده‌، پسری‌ زایید. آنگاه‌ خداوند به‌ من‌ گفت‌: «او را مَهیر شَلال‌ حاش‌ بَز بنام‌،",
			"4": "<span>4 </span> &nbsp;زیرا قبل‌ از آنكه‌ طفل‌بتواند ای‌ پدرم‌ و ای‌ مادرم‌ بگوید، اموال‌ دمشق‌ و غنیمت‌ سامره‌ را پیش‌ پادشاه‌ آشور به‌ یغما خواهند برد.»",
			"5": "<span>5 </span> &nbsp;و خداوند بار دیگر مرا باز خطاب‌ كرده‌، گفت‌:",
			"6": "<span>6 </span> &nbsp;«چونكه‌ این‌ قوم‌ آبهای‌ شیلوه‌ را كه‌ به‌ ملایمت‌ جاری‌ می‌شود خوار شمرده‌، از رصین‌ و پسر رملیا مسرور شده‌اند،",
			"7": "<span>7 </span> &nbsp;بنابراین‌ اینك‌ خداوند آبهای‌ زورآور بسیار نهر یعنی‌ پادشاه‌ آشور و تمامی‌ حشمت‌ او را بر ایشان‌ برخواهد آورد و او از جمیع‌ جویهای‌ خود برخواهد آمد و از تمامی‌ كناره‌های‌ خویش‌ سرشار خواهد شد،",
			"8": "<span>8 </span> &nbsp;و بر یهودا تجاوز نموده‌، سیلان‌ كرده‌، عبور خواهد نمود تا آنكه‌ به‌ گردنها برسد و بالهای‌ خود را پهن‌ كرده‌، طول‌ و عرض‌ ولایتت‌ را ای‌ عمّانوئیل‌ پر خواهد ساخت‌.»",
			"9": "<span>9 </span> &nbsp;به‌ هیجان‌ آیید ای‌ قومها و شكست‌ خواهید یافت‌ و گوش‌ گیرید ای‌ اقصای‌ زمین‌ و كمر خود را ببندید و شكست‌ خواهید یافت‌. كمر خود را ببندید و شكست‌ خواهید یافت‌.",
			"10": "<span>10 </span> &nbsp;با هم‌ مشورت‌ كنید و باطل‌ خواهد شد و سخن‌ گویید و بجا آورده‌ نخواهد شد زیرا خدا با ما است‌.",
			"11": "<span>11 </span> &nbsp;چونكه‌ خداوند با دست‌ قوّی‌ به‌ من‌ چنین‌ گفت‌ و مرا تعلیم‌ داد كه‌ به‌ راه‌ این‌ قوم‌ سلوك‌ ننمایم‌ و گفت‌:",
			"12": "<span>12 </span> &nbsp;«هرآنچه‌ را كه‌ این‌ قوم‌ فتنه‌ می‌نامند، شما آن‌ را فتنه‌ ننامید و از ترس‌ ایشان‌ ترسان‌ و خائف‌ مباشید.",
			"13": "<span>13 </span> &nbsp;یهوه‌ صبایوت‌ را تقدیس‌ نمایید و او ترس‌ و خوف‌ شما باشد.",
			"14": "<span>14 </span> &nbsp;و او (برای‌ شما) مكان‌ مقدّس‌ خواهد بود امّا برای‌ هردو خاندان‌ اسرائیل‌ سنگ‌ مصادم‌ و صخره‌ لغزش‌ دهنده‌ و برای‌ ساكنان‌ اورشلیم‌ دام‌ و تله‌.",
			"15": "<span>15 </span> &nbsp;و بسیاری‌ از ایشان‌ لغزش‌ خورده‌، خواهند افتاد و شكسته‌ شده‌ و بدام‌ افتاده‌، گرفتار خواهند گردید.»",
			"16": "<span>16 </span> &nbsp;شهادت‌ را به‌ هم‌ بپیچ‌ و شریعت‌ را در شاگردانم‌ مختوم‌ ساز.",
			"17": "<span>17 </span> &nbsp;و من‌ برای‌ خداوند كه‌ روی‌ خود را از خاندان‌ یعقوب‌ مخفی‌ می‌سازد انتظار كشیده‌، امیدوار او خواهم‌ بود.",
			"18": "<span>18 </span> &nbsp;اینك‌ من‌ و پسرانی‌ كه‌ خداوند به‌ من‌ داده‌ است‌، از جانب‌ یهوه‌ صبایوت‌ كه‌ در كوه‌ صهیون‌ ساكن‌ است‌ به‌ جهت‌ اسرائیل‌ آیات‌ و علامات‌ هستیم‌.",
			"19": "<span>19 </span> &nbsp;و چون‌ ایشان‌ به‌ شما گویند كه‌ از اصحاب‌ اجنّه‌ و جادوگرانی‌ كه‌ جیك‌ جیك‌ و زمزم‌ می‌كنند سؤال‌ كنید، (گویید) «آیا قوم‌ از خدای‌ خود سؤال‌ ننمایند و آیا از مردگان‌ به‌ جهت‌ زندگان‌ سؤال‌ باید نمود؟»",
			"20": "<span>20 </span> &nbsp;به‌ شریعت‌ و شهادت‌ (توجّه‌ نمایید) واگر موافق‌ این‌ كلام‌ سخن‌ نگویند، پس‌ برای‌ ایشان‌ روشنایی‌ نخواهد بود.",
			"21": "<span>21 </span> &nbsp;و با عُسرت‌ و گرسنگی‌ در آن‌ خواهند گشت‌ و هنگامی‌ كه‌ گرسنه‌ شوند خویشتن‌ را مشوّش‌ خواهند ساخت‌ و پادشاه‌ و خدای‌ خود را لعنت‌ كرده‌، به‌ بالا خواهند نگریست‌.",
			"22": "<span>22 </span> &nbsp;و به‌ زمین‌ نظر خواهند انداخت‌ و اینك‌ تنگی‌ و تاریكی‌ و ظلمتِ پریشانی‌ خواهد بود و به‌ تاریكی‌ غلیظ‌ رانده‌ خواهند شد."
		},
		"9": {
			"1": "<span>1 </span> &nbsp;لیكن‌ برای‌ او كه‌ در تنگی‌ می‌بود، تاریكی‌نخواهد شد. در زمان‌ پیشین‌ زمین‌ زبولون‌ و زمین‌ نفتالی‌ را ذلیل‌ ساخت‌، امّا در زمان‌ آخر آن‌ را به‌ راه‌ دریا به‌ آن‌ طرف‌ اردنّ در جلیل‌ امّت‌هامحترم‌ خواهد گردانید.",
			"2": "<span>2 </span> &nbsp;قومی‌ كه‌ در تاریكی‌ سالك‌ می‌بودند، نور عظیمی‌ خواهند دید و بر ساكنان‌ زمین‌ سایه‌ موت‌ نور ساطع‌ خواهد شد.",
			"3": "<span>3 </span> &nbsp;تو قوم‌ را بسیار ساخته‌، شادی‌ ایشان‌ را زیاد گردانیدی‌. به‌ حضور تو شادی‌ خواهند كرد مثل‌ شادمانی‌ وقت‌ درو و مانند كسانی‌ كه‌ در تقسیم‌ نمودن‌ غنیمت‌ وجد می‌نمایند.",
			"4": "<span>4 </span> &nbsp;زیرا كه‌ یوغ‌ بار او را و عصای‌ گردنش‌ یعنی‌ عصای‌ جفا كننده‌ وی‌ را شكستی‌ چنانكه‌ در روز مدیان‌ كردی‌.",
			"5": "<span>5 </span> &nbsp;زیرا همه‌ اسلحه‌ مسلحّان‌ در غوغا است‌ و رُخُوت‌ ایشان‌ به‌ خون‌ آغشته‌ است‌، امّا برای‌ سوختن‌ و هیزم‌ آتش‌ خواهند بود.",
			"6": "<span>6 </span> &nbsp;زیرا كه‌ برای‌ ما ولدی‌ زاییده‌ و پسری‌ به‌ ما بخشیده‌ شد و سلطنت‌ بر دوش‌ او خواهد بود و اسم‌ او عجیب‌ و مشیر و خدای‌ قدیر و پدر سرمدی‌ و سرور سلامتی‌ خوانده‌ خواهد شد.",
			"7": "<span>7 </span> &nbsp;ترقّی‌ سلطنت‌ و سلامتی‌ او را بر كرسی‌ داود و بر مملكت‌ وی‌ انتها نخواهد بود تا آن‌ را به‌ انصاف‌ و عدالت‌ از الا´ن‌ تا ابدالا´باد ثابت‌ و استوار نماید. غیرت‌ یهوه‌ صبایوت‌ این‌ را بجا خواهد آورد.",
			"8": "<span>8 </span> &nbsp;خداوند كلامی‌ نزد یعقوب‌ فرستاد و آن‌ بر اسرائیل‌ واقع‌ گردید.",
			"9": "<span>9 </span> &nbsp;و تمامی‌ قوم‌ خواهند دانست‌ یعنی‌ افرایم‌ و ساكنان‌ سامره‌ كه‌ از غرور و تكبّر دل‌ خود می‌گویند:",
			"10": "<span>10 </span> &nbsp;«خشتها افتاده‌ است‌ امّا با سنگهای‌ تراشیده‌ بنا خواهیم‌ نمود؛ چوبهای‌ افراغ‌ در هم‌ شكست‌ امّا سرو آزاد بجای‌ آنها می‌گذاریم‌.»",
			"11": "<span>11 </span> &nbsp;بنابراین‌ خداوند دشمنان‌ رصین‌ را بضدّ او خواهد برافراشت‌ و خصمان‌ او راخواهد برانگیخت‌.",
			"12": "<span>12 </span> &nbsp;اَرامیان‌ را از مشرق‌ و فلسطینیان‌ را از مغرب‌ و ایشان‌ اسرائیل‌ را با دهان‌ گشوده‌ خواهند خورد. امّا با این‌ همه‌، خشم‌ او برگردانیده‌ نشده‌ و دست‌ او هنوز دراز است‌.",
			"13": "<span>13 </span> &nbsp;و این‌ قوم‌ بسوی‌ زننده‌ خودشان‌ بازگشت‌ ننموده‌ و یهوه‌ صبایوت‌ را نطلبیده‌اند.",
			"14": "<span>14 </span> &nbsp;بنابراین‌ خداوند سر و دم‌ و نخل‌ و نی‌ را از اسرائیل‌ در یك‌ روز خواهد برید.",
			"15": "<span>15 </span> &nbsp;مرد پیر و مرد شریف‌ سر است‌ و نبی‌ای‌ كه‌ تعلیم‌ دروغ‌ می‌دهد، دُم‌ می‌باشد.",
			"16": "<span>16 </span> &nbsp;زیرا كه‌ هادیان‌ این‌ قوم‌ ایشان‌ را گمراه‌ می‌كنند و پیروان‌ ایشان‌ بلعیده‌ می‌شوند.",
			"17": "<span>17 </span> &nbsp;از این‌ سبب‌ خداوند از جوانان‌ ایشان‌ مسرور نخواهد شد و بر یتیمان‌ و بیوه‌ زنان‌ ایشان‌ ترحّم‌ نخواهد نمود. چونكه‌ جمیع‌ ایشان‌ منافق‌ و شریرند و هر دهانی‌ به‌ حماقت‌ متكلّم‌ می‌شود با اینهمه‌ غضب‌ او برگردانیده‌ نشده‌ و دست‌ او هنوز دراز است‌.",
			"18": "<span>18 </span> &nbsp;زیرا كه‌ شرارت‌ مثل‌ آتش‌ می‌سوزاند و خار و خس‌ را می‌خورد، و در بوته‌های‌ جنگل‌ افروخته‌ شده‌، دود غلیظ‌ پیچان‌ می‌شود.",
			"19": "<span>19 </span> &nbsp;از غضب‌ یهوه‌ صبایوت‌ زمین‌ سوخته‌ شده‌ است‌ و قوم‌، هیزمِ آتش‌ گشته‌اند و كسی‌ بر برادر خود شفقت‌ ندارد.",
			"20": "<span>20 </span> &nbsp;از جانب‌ راست‌ می‌ربایند و گرسنه‌ می‌مانند و از طرف‌ چپ‌ می‌خورند و سیر نمی‌شوند و هركس‌ گوشت‌ بازوی‌ خود را می‌خورد.",
			"21": "<span>21 </span> &nbsp;منسّی‌ افرایم‌ را و افرایم‌ منسّی‌ را و هر دوی‌ ایشان‌ بضّد یهودا متحّد می‌شوند. با اینهمه‌ غضب‌ او برگردانیده‌ نشده‌ و دست‌ او هنوز دراز است‌."
		},
		"10": {
			"1": "<span>1 </span> &nbsp;وای‌ بر آنانی‌ كه‌ احكام‌ غیر عادله‌ را جاری‌ می‌سازند و كاتبانی‌ كه‌ ظلم‌ رامرقوم‌ می‌دارند،",
			"2": "<span>2 </span> &nbsp;تا مسكینان‌ را از داوری‌ منحرف‌ سازند و حقّ فقیران‌ قوم‌ مرا بربایند تا آنكه‌ بیوه‌ زنان‌ غارت‌ ایشان‌ بشوند و یتیمان‌ را تاراج‌ نمایند.",
			"3": "<span>3 </span> &nbsp;پس‌ در روز بازخواست‌ در حینی‌ كه‌ خرابی‌ از دور می‌آید، چه‌ خواهید كرد و بسوی‌ كه‌ برای‌ معاونت‌ خواهید گریخت‌ و جلال‌ خود را كجا خواهید انداخت‌؟",
			"4": "<span>4 </span> &nbsp;غیر از آنكه‌ زیر اسیران‌ خم‌ شوند و زیر كشتگان‌ بیفتند. با اینهمه‌ غضب‌ او برگردانیده‌ نشده‌ و دست‌ او هنوز دراز است‌.",
			"5": "<span>5 </span> &nbsp;وای‌ بر آشور كه‌ عصای‌ غضب‌ من‌ است‌ و عصایی‌ كه‌ در دست‌ ایشان‌ است‌ خشم‌ من‌ می‌باشد.",
			"6": "<span>6 </span> &nbsp;او را بر امّت‌ منافق‌ می‌فرستم‌ و نزد قوم‌ مغضوب‌ خود مأمور می‌دارم‌، تا غنیمتی‌ بربایند و غارتی‌ ببرند و ایشان‌ را مثل‌ گِل‌ كوچه‌ها پایمال‌ سازند.",
			"7": "<span>7 </span> &nbsp;امّا او چنین‌ گمان‌ نمی‌كند و دلش‌ بدینگونه‌ قیاس‌ نمی‌نماید، بلكه‌ مراد دلش‌ این‌ است‌ كه‌ امّت‌های‌ بسیار را هلاك‌ و منقطع‌ بسازد.",
			"8": "<span>8 </span> &nbsp;زیرا می‌گوید آیا سرداران‌ من‌ جمیعاً پادشاه‌ نیستند؟",
			"9": "<span>9 </span> &nbsp;آیا كَلنو مثل‌ كركمیش‌ نیست‌ و آیا حمات‌ مثل‌ ارفاد نی‌، و آیا سامره‌ مانند دمشق‌ نمی‌باشد،",
			"10": "<span>10 </span> &nbsp;چنانكه‌ دست‌ من‌ بر ممالك‌ بتها استیلا یافت‌؟ و بتهای‌ تراشیده‌ آنها از بتهای‌ اورشلیم‌ و سامره‌ بیشتر بودند؟",
			"11": "<span>11 </span> &nbsp;پس‌ آیا به‌ نهجی‌ كه‌ به‌ سامره‌ و بتهایش‌ عمل‌ نمودم‌ به‌ اورشلیم‌ و بتهایش‌ چنین‌ عمل‌ نخواهم‌ نمود؟",
			"12": "<span>12 </span> &nbsp;و واقع‌ خواهد شد بعد از آنكه‌ خداوند تمامی‌ كار خود را با كوه‌ صهیون‌ و اورشلیم‌ به‌ انجام‌ رسانیده‌ باشد كه‌ من‌ از ثمره‌ دل‌ مغرورپادشاه‌ آشور و از فخر چشمان‌ متكبّر وی‌ انتقام‌ خواهم‌ كشید.",
			"13": "<span>13 </span> &nbsp;زیرا می‌گوید: «به‌ قوّت‌ دست‌ خود و به‌ حكمت‌ خویش‌ چونكه‌ فهیم‌ هستم‌ این‌ را كردم‌ و حدود قومها را منتقل‌ ساختم‌ و خزاین‌ ایشان‌ را غارت‌ نمودم‌ و مثل‌ جبّار سروران‌ ایشان‌ را به‌ زیر انداختم‌.",
			"14": "<span>14 </span> &nbsp;و دست‌ من‌ دولت‌ قوم‌ها را مثل‌ آشیانه‌ای‌ گرفته‌ است‌ و به‌ طوری‌ كه‌ تخمهای‌ متروك‌ را جمع‌ كنند، من‌ تمامی‌ زمین‌ را جمع‌ كردم‌. و كسی‌ نبود كه‌ بال‌ را بجنباند یا دهان‌ خود را بگشاید یا جك‌ جك‌ بنماید.»",
			"15": "<span>15 </span> &nbsp;آیا تبر بر كسی‌ كه‌ به‌ آن‌ می‌شكند فخر خواهد نمود یا ارّه‌ بر كسی‌ كه‌ آن‌ را می‌كشد افتخار خواهد كرد، كه‌ گویا عصا بلند كننده‌ خود را بجنباند یا چوب‌ دست‌ آنچه‌ را كه‌ چوب‌ نباشد بلند نماید؟",
			"16": "<span>16 </span> &nbsp;بنابراین‌ خداوند یهوه‌ صبایوت‌ بر فربهان‌ او لاغری‌ خواهد فرستاد و زیر جلال‌ او سوختنی‌ مثل‌ سوختن‌ آتش‌ افروخته‌ خواهد شد.",
			"17": "<span>17 </span> &nbsp;و نور اسرائیل‌ نار و قدّوس‌ وی‌ شعله‌ خواهد شد، و در یكروز خار و خسش‌ را سوزانیده‌، خواهد خورد.",
			"18": "<span>18 </span> &nbsp;و شوكت‌ جنگل‌ و بستان‌ او هم‌ روح‌ و هم‌ بدن‌ را تباه‌ خواهد ساخت‌ و مثل‌ گداختن‌ مریض‌ خواهد شد.",
			"19": "<span>19 </span> &nbsp;و بقیه‌ درختان‌ و جنگلش‌ قلیل‌ العدد خواهد بود كه‌ طفلی‌ آنها را ثبت‌ تواند كرد.",
			"20": "<span>20 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ بقیه‌ اسرائیل‌ و ناجیان‌ خاندان‌ یعقوب‌ بار دیگر بر زننده‌ خودشان‌ اعتماد نخواهند نمود، بلكه‌ بر خداوند كه‌ قدّوس‌ اسرائیل‌ است‌ به‌ اخلاص‌اعتماد خواهند نمود.",
			"21": "<span>21 </span> &nbsp;و بقیه‌ای‌ یعنی‌ بقیه‌ یعقوب‌ بسوی‌ خدای‌ قادر مطلق‌ بازگشت‌ خواهند كرد.",
			"22": "<span>22 </span> &nbsp;زیرا هرچند قوم‌ تو اسرائیل‌ مثل‌ ریگ‌ دریا باشند، فقط‌ از ایشان‌ بقّیتی‌ بازگشت‌ خواهند نمود. هلاكتی‌ كه‌ مقدّر است‌ به‌ عدالت‌ مجرا خواهد شد،",
			"23": "<span>23 </span> &nbsp;زیرا خداوند یهوه‌ صبایوت‌ هلاكت‌ و تقدیری‌ در میان‌ تمام‌ زمین‌ به‌ عمل‌ خواهد آورد.",
			"24": "<span>24 </span> &nbsp;بنابراین‌ خداوند یهوه‌ صبایوت‌ چنین‌ می‌گوید: «ای‌ قوم‌ من‌ كه‌ در صهیون‌ ساكنید از آشور مترسید، اگر چه‌ شما را به‌ چوب‌ بزند و عصای‌ خود را مثل‌ مصریان‌ بر شما بلند نماید.",
			"25": "<span>25 </span> &nbsp;زیرا بعد از زمان‌ بسیار كمی‌، غضب‌ تمام‌ خواهد شد و خشم‌ من‌ برای‌ هلاكت‌ ایشان‌ خواهد بود.»",
			"26": "<span>26 </span> &nbsp;و یهوه‌ صبایوت‌ تازیانه‌ای‌ بر وی‌ خواهد برانگیخت‌ چنانكه‌ در كشتار مدّیان‌ بر صخره‌ غراب‌. و عصای‌ او بر دریا خواهد بود و آن‌ را بلند خواهد كرد به‌ طوری‌ كه‌ بر مصریان‌ كرده‌ بود.",
			"27": "<span>27 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ بار او از دوش‌ تو و یوغ‌ او از گردن‌ تو رفع‌ خواهد شد و یوغ‌ از فربهی‌ گسسته‌ خواهد شد.",
			"28": "<span>28 </span> &nbsp;او به‌ عیات‌ رسید و از مجرون‌ گذشت‌ و در مكماش‌ اسباب‌ خود را گذاشت‌.",
			"29": "<span>29 </span> &nbsp;از معبر عبور كردند و در جبع‌ منزل‌ گزیدند، اهل‌ رامه‌ هراسان‌ شدند و اهل‌ جبعه‌ شاؤل‌ فرار كردند.",
			"30": "<span>30 </span> &nbsp;ای‌ دختر جلیم‌ به‌ آواز خود فریاد برآور! ای‌ لیشه‌ و ای‌ عناتوت‌ فقیر گوش‌ ده‌!",
			"31": "<span>31 </span> &nbsp;مدمینه‌ فراری‌ شدند و ساكنان‌ جیبیم‌ گریختند.",
			"32": "<span>32 </span> &nbsp;همین‌ امروز در نوب‌ توقّف‌ می‌كند و دست‌ خود را بر جبل‌ دختر صهیون‌ و كوه‌ اورشلیم‌ دراز می‌سازد.",
			"33": "<span>33 </span> &nbsp;اینك‌ خداوند یهوه‌ صبایوت‌ شاخه‌ها را با خوف‌ قطع‌خواهد نمود و بلند قدّان‌ بریده‌ خواهند شد و مرتفعان‌ پست‌ خواهند گردید،",
			"34": "<span>34 </span> &nbsp;و بوته‌های‌ جنگل‌ به‌ آهن‌ بریده‌ خواهد شد و لبنان‌ به‌ دست‌ جبّاران‌ خواهد افتاد."
		},
		"11": {
			"1": "<span>1 </span> &nbsp;و نهالی‌ از تنه‌ یسَّی‌ بیرون‌ آمده‌،شاخه‌ای‌ از ریشه‌هایش‌ خواهد شكفت‌.",
			"2": "<span>2 </span> &nbsp;و روح‌ خداوند بر او قرار خواهد گرفت‌، یعنی‌ روح‌ حكمت‌ و فهم‌ و روح‌ مشورت‌ و قوّت‌ و روح‌ معرفت‌ و ترس‌ خداوند.",
			"3": "<span>3 </span> &nbsp;و خوشی‌ او در ترس‌ خداوند خواهد بود و موافق‌ رؤیت‌ چشم‌ خود داوری‌ نخواهد كرد و بر وفق‌ سمع‌ گوشهای‌ خویش‌ تنبیه‌ نخواهد نمود.",
			"4": "<span>4 </span> &nbsp;بلكه‌ مسكینان‌ را به‌ عدالت‌ داوری‌ خواهد كرد و به‌ جهت‌ مظلومانِ زمین‌ براستی‌ حكم‌ خواهد نمود. و جهان‌ را به‌ عصای‌ دهان‌ خویش‌ زده‌، شریران‌ را به‌ نفخه‌ لبهای‌ خود خواهد كُشت‌.",
			"5": "<span>5 </span> &nbsp;و كمربند كمرش‌ عدالت‌ خواهد بود و كمربند میانش‌ امانت‌.",
			"6": "<span>6 </span> &nbsp;و گرگ‌ با بره‌ سكونت‌ خواهد داشت‌ و پلنگ‌ با بزغاله‌ خواهد خوابید و گوساله‌ و شیر و پرواری‌ با هم‌، و طفل‌ كوچك‌ آنها را خواهد راند.",
			"7": "<span>7 </span> &nbsp;و گاو با خرس‌ خواهد چرید و بچه‌های‌ آنها با هم‌ خواهند خوابید و شیر مثل‌ گاو كاه‌ خواهد خورد.",
			"8": "<span>8 </span> &nbsp;و طفل‌ شیرخواره‌ بر سوراخ‌ مار بازی‌ خواهد كرد و طفلِ از شیر باز داشته‌ شده‌ دست‌ خود را بر خانه‌ افعی‌ خواهد گذاشت‌.",
			"9": "<span>9 </span> &nbsp;و در تمامی‌ كوه‌مقدّس‌ من‌ ضرر و فسادی‌ نخواهند كرد زیرا كه‌ جهان‌ از معرفت‌ خداوند پر خواهد بود مثل‌ آبهایی‌ كه‌ دریا را می‌پوشاند.",
			"10": "<span>10 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ ریشه‌ یسَّی‌ به‌ جهت‌ عَلَمِ قوم‌ها برپا خواهد شد و امّت‌ها آن‌ را خواهند طلبید و سلامتی‌ او با جلال‌ خواهد بود.",
			"11": "<span>11 </span> &nbsp;و در آن‌ روز واقع‌ خواهد گشت‌ كه‌ خداوند بار دیگر دست‌ خود را دراز كند تا بقیه‌ قوم‌ خویش‌ را كه‌ از آشور و مصر و فتروس‌ و حبش‌ و عیلام‌ و شنعار و حَمات‌ و از جزیره‌های‌ دریا باقی‌ مانده‌ باشند باز آورد.",
			"12": "<span>12 </span> &nbsp;و به‌ جهت‌ امّت‌ها عَلَمی‌ برافراشته‌، رانده‌شدگان‌ اسرائیل‌ را جمع‌ خواهد كرد، و پراكندگان‌ یهودا را از چهار طرف‌ جهان‌ فراهم‌ خواهد آورد.",
			"13": "<span>13 </span> &nbsp;و حَسَد افرایم‌ رفع‌ خواهد شد و دشمنان‌ یهودا منقطع‌ خواهند گردید. افرایم‌ بر یهودا حسد نخواهد بُرد و یهودا افرایم‌ را دشمنی‌ نخواهد نمود.",
			"14": "<span>14 </span> &nbsp;و به‌ جانب‌ مغرب‌ بر دوش‌ فلسطینیان‌ پریده‌، بنی‌ مشرق‌ را با هم‌ غارت‌ خواهند نمود. و دست‌ خود را بر اَدُوم‌ و مُوْآب‌ دراز كرده‌، بنی‌ عمّون‌ ایشان‌ را اطاعت‌ خواهند كرد.",
			"15": "<span>15 </span> &nbsp;و خداوند زبانه‌ دریای‌ مصر را تباه‌ ساخته‌، دست‌ خود را با بادِ سوزان‌ بر نهر دراز خواهد كرد، و آن‌ را با هفت‌ نهرش‌ خواهد زد و مردم‌ را با كفش‌ به‌ آن‌ عبور خواهد داد.",
			"16": "<span>16 </span> &nbsp;و به‌ جهت‌ بقیه‌ قوم‌ او كه‌ از آشور باقی‌ مانده‌ باشند، شاه‌راهی‌ خواهد بود، چنانكه‌ به‌ جهت‌ اسرائیل‌ در روز بر آمدن‌ ایشان‌ از زمین‌ مصر بود."
		},
		"12": {
			"1": "<span>1 </span> &nbsp;و در آن‌ روز خواهی‌ گفت‌ كه‌ «ای‌ خداوند تو را حمد می‌گویم‌ زیرا به‌ من‌ غضبناك‌ بودی‌، امّا غضبت‌ برگردانیده‌ شده‌، مرا تسلّی‌ می‌دهی‌.",
			"2": "<span>2 </span> &nbsp;اینك‌ خدا نجات‌ من‌ است‌ بر او توكّل‌ نموده‌، نخواهم‌ ترسید. زیرا یاه‌ یهُوَه‌ قوّت‌ و تسبیح‌ من‌ است‌ و نجات‌ من‌ گردیده‌ است‌.»",
			"3": "<span>3 </span> &nbsp;بنابراین‌ با شادمانی‌ از چشمه‌های‌ نجات‌ آب‌ خواهید كشید.",
			"4": "<span>4 </span> &nbsp;و در آن‌ روز خواهید گفت‌: « خداوند را حمد گویید و نام‌ او را بخوانید و اعمال‌ او را در میان‌ قوم‌ها اعلام‌ كنید و ذكر نمایید كه‌ اسم‌ او متعال‌ می‌باشد.",
			"5": "<span>5 </span> &nbsp;برای‌ خداوند بسرایید زیرا كارهای‌ عظیم‌ كرده‌ است‌ و این‌ در تمامی‌ زمین‌ معروف‌ است.",
			"6": "<span>6 </span> &nbsp;ای‌ ساكنه‌ صهیون‌ صدا را برافراشته‌، بسرای‌ زیرا قدّوس‌ اسرائیل‌ در میان‌ تو عظیم‌ است‌.»"
		},
		"13": {
			"1": "<span>1 </span> &nbsp;وحی‌ دربارۀ بابل‌ كه‌ اشعیا ابن‌ آموص آن‌ را دید.",
			"2": "<span>2 </span> &nbsp;عَلَمی‌ بر كوه‌ خشك‌ برپا كنید و آواز به‌ ایشان‌ بلند نمایید، با دست‌ اشاره‌ كنید تا به‌ درهای‌ نجبا داخل‌ شوند.",
			"3": "<span>3 </span> &nbsp;من‌ مقدّسان‌ خود را مأمور داشتم‌ و شجاعان‌ خویش‌ یعنی‌ آنانی‌ را كه‌ در كبریای‌ من‌ وجد می‌نمایند به‌ جهت‌ غضبم‌ دعوت‌ نمودم‌.",
			"4": "<span>4 </span> &nbsp;آواز گروهی‌ در كوه‌ها مثل‌ آواز خلق‌ كثیر. آواز غوغای‌ ممالك‌ امّت‌ها كه‌ جمع‌ شده‌ باشند. یهوه‌ صبایوت‌ لشكر را برای‌ جنگ‌سان‌ می‌بیند.",
			"5": "<span>5 </span> &nbsp;ایشان‌ از زمین‌ بعید و از كرانه‌های‌ آسمان‌ می‌آیند. یعنی‌ خداوند با اسلحه‌ غضب‌ خود تا تمامی جهان‌ را ویران‌ كند.",
			"6": "<span>6 </span> &nbsp;وِلوِلَه‌ كنید زیرا كه‌ روز خداوند نزدیك‌ است‌، مثل‌ هلاكتی‌ از جانب‌ قادر مطلق‌ می‌آید.",
			"7": "<span>7 </span> &nbsp;از این‌ جهت‌ همه‌ دستها سست‌ می‌شود و دلهای‌ همه‌ مردم‌ گداخته‌ می‌گردد.",
			"8": "<span>8 </span> &nbsp;و ایشان‌ متحیر شده‌، اَلَمها و دردهای‌ زه‌ بر ایشان‌ عارض‌ می‌شود، مثل‌ زنی‌ كه‌ می‌زاید درد می‌كشند. بر یكدیگر نظر حیرت‌ می‌اندازند و رویهای‌ ایشان‌ رویهای‌ شعله‌ور می‌باشد.",
			"9": "<span>9 </span> &nbsp;اینك‌ روز خداوند با غضب‌ و شدّت‌ خشم‌ و ستمكیشی‌ می‌آید، تا جهان‌ را ویران‌ سازد و گناهكاران‌ را از میانش‌ هلاك‌ نماید.",
			"10": "<span>10 </span> &nbsp;زیرا كه‌ ستارگان‌ آسمان‌ و برجهایش‌ روشنایی‌ خود را نخواهند داد. و آفتاب‌ در وقت‌ طلوع‌ خود تاریك‌ خواهد شد و ماه‌ روشنایی‌ خود را نخواهد تابانید.",
			"11": "<span>11 </span> &nbsp;و من‌ ربع‌ مسكون‌ را به‌ سبب‌ گناه‌ و شریران‌ را به‌ سبب‌ عصیان‌ ایشان‌ سزا خواهم‌ داد، و غرور متكبّران‌ را تباه‌ خواهم‌ ساخت‌ و تكبّر جبّاران‌ را به‌ زیر خواهم‌ انداخت‌.",
			"12": "<span>12 </span> &nbsp;و مردم‌ را از زر خالص‌ و انسان‌ را از طلای‌ اُوفیر كمیابتر خواهم‌ گردانید.",
			"13": "<span>13 </span> &nbsp;بنابراین‌ آسمان‌ را متزلزل‌ خواهم‌ ساخت‌ و زمین‌ از جای‌ خود متحرّك‌ خواهد شد. در حین‌ غضب‌ یهوه‌ صبایوت‌ و در روز شدّت‌ خشم‌ او.",
			"14": "<span>14 </span> &nbsp;و مثل‌ آهوی‌ رانده‌ شده‌ و مانند گله‌ای‌ كه‌ كسی‌ آن‌ را جمع‌ نكند خواهند بود. و هركس‌ به‌ سوی‌ قوم‌ خود توجّه‌ خواهد نمود و هر شخص‌ به‌ زمین‌ خویش‌ فرار خواهد كرد.",
			"15": "<span>15 </span> &nbsp;و هركه‌ یافت‌ شود با نیزه‌ زده‌ خواهد شد و هركه‌ گرفته‌ شود باشمشیر خواهد افتاد.",
			"16": "<span>16 </span> &nbsp;اطفال‌ ایشان‌ نیز در نظر ایشان‌ به‌ زمین‌ انداخته‌ شوند و خانه‌های‌ ایشان‌ غارت‌ شود و زنان‌ ایشان‌ بی‌عصمت‌ گردند.",
			"17": "<span>17 </span> &nbsp;اینك‌ من‌ مادیان‌ را بر ایشان‌ خواهم‌ برانگیخت‌ كه‌ نقره‌ را به‌ حساب‌ نمی‌آورند و طلا را دوست‌ نمی‌دارند.",
			"18": "<span>18 </span> &nbsp;و كمانهای‌ ایشان‌ جوانان‌ را خرد خواهد كرد. و بر ثمره‌ رَحِم‌ ترحّم‌ نخواهند نمود و چشمان‌ ایشان‌ بر اطفال‌ شفقت‌ نخواهد كرد.",
			"19": "<span>19 </span> &nbsp;و بابِل‌ كه‌ جلال‌ ممالك‌ و زینت‌ فخر كلدانیان‌ است‌، مثل‌ واژگون‌ ساختن‌ خدا سدوم‌ و عموره‌ را خواهد شد.",
			"20": "<span>20 </span> &nbsp;و تا به‌ ابد آباد نخواهد شد و نسلاً بعد نسل‌ مسكون‌ نخواهد گردید. و اَعراب‌ در آنجا خیمه‌ نخواهند زد و شبانان‌ گله‌ها را در آنجا نخواهند خوابانید.",
			"21": "<span>21 </span> &nbsp;بلكه‌ وحوش‌ صحرا در آنجا خواهند خوابید و خانه‌های‌ ایشان‌ از بومها پر خواهد شد. شترمرغ‌ در آنجا ساكن‌ خواهد شد و غولان‌ در آنجا رقص‌ خواهند كرد،",
			"22": "<span>22 </span> &nbsp;و شغالها در قصرهای‌ ایشان‌ و گرگها در كوشكهای‌ خوش‌نما صدا خواهند زد و زمانش‌ نزدیك‌ است‌ كه‌ برسد و روزهایش‌ طول‌ نخواهد كشید."
		},
		"14": {
			"1": "<span>1 </span> &nbsp;زیرا خداوند بر یعقوب‌ ترحّم‌ فرموده‌،اسرائیل‌ را بار دیگر خواهد برگزید و ایشان‌ را در زمینشان‌ آرامی‌ خواهد داد. و غربا با ایشان‌ ملحق‌ شده‌، با خاندان‌ یعقوب‌ ملصق‌ خواهند گردید.",
			"2": "<span>2 </span> &nbsp;و قوم‌ها ایشان‌ را برداشته‌، به‌ مكان‌ خودشان‌ خواهند آورد. و خاندان‌ اسرائیل‌ ایشان‌ را در زمین‌ خداوند برای‌ بندگی‌ و كنیزی‌، مملوك‌ خود خواهند ساخت‌. و اسیركنندگان‌ خود را اسیر كرده‌، بر ستمكاران‌ خویش ‌حكمرانی‌ خواهند نمود.",
			"3": "<span>3 </span> &nbsp;و در روزی‌ كه‌ خداوند تو را از اَلَم‌ و اضطرابت‌ و بندگی‌ سخت‌ كه‌ بر تو می‌نهادند خلاصی‌ بخشد، واقع‌ خواهد شد",
			"4": "<span>4 </span> &nbsp;كه‌ این‌ مثل‌ را بر پادشاه‌ بابل‌ زده‌، خواهی‌ گفت‌: چگونه‌ آن‌ ستمكار تمام‌ شد و آن‌ جور پیشه‌ چگونه‌ فانی‌ گردید!",
			"5": "<span>5 </span> &nbsp;خداوند عصای‌ شریران‌ و چوگان‌ حاكمان‌ را شكست‌.",
			"6": "<span>6 </span> &nbsp;آنكه‌ قوم‌ها را به‌ خشم‌ با صدمه‌ متوالی‌ می‌زد و بر امّت‌ها به‌ غضب‌ با جفای‌ بیحّد حكمرانی‌ می‌نمود،",
			"7": "<span>7 </span> &nbsp;تمامی‌ زمین‌ آرام‌ شده‌ و ساكت‌ گردیده‌اند و به‌ آواز بلند ترنّم‌ می‌نمایند.",
			"8": "<span>8 </span> &nbsp;صنوبرها نیز و سروهای‌ آزاد لبنان‌ دربارۀ تو شادمان‌ شده‌، می‌گویند: «از زمانی‌ كه‌ تو خوابیده‌ای‌ قطع‌ كننده‌ای‌ بر ما برنیامده‌ است‌.»",
			"9": "<span>9 </span> &nbsp;هاویه‌ از زیر برای‌ تو متحرّك‌ است‌ تا چون‌ بیایی‌ تو را استقبال‌ نماید، و مردگان‌ یعنی‌ جمیع‌ بزرگان‌ زمین‌ را برای‌ تو بیدار می‌سازد، و جمیع‌ پادشاهان‌ امّت‌ها را از كرسیهای‌ ایشان‌ برمی‌دارد.",
			"10": "<span>10 </span> &nbsp;جمیع‌ اینها تو را خطاب‌ كرده‌، می‌گویند: «آیا تو نیز مثل‌ ما ضعیف‌ شده‌ای‌ و مانند ما گردیده‌ای‌؟»",
			"11": "<span>11 </span> &nbsp;جلال‌ تو و صدای‌ بربطهای‌ تو به‌ هاویه‌ فرود شده‌ است‌. كِرمها زیر تو گسترانیده‌ شده‌ و مورها تو را می‌پوشانند.",
			"12": "<span>12 </span> &nbsp;ای‌ زهره‌ دختر صبح‌ چگونه‌ از آسمان‌ افتاده‌ای‌؟ ای‌ كه‌ امّت‌ها را ذلیل‌ می‌ساختی‌ چگونه‌ به‌ زمین‌ افكنده‌ شده‌ای‌؟",
			"13": "<span>13 </span> &nbsp;و تو در دل‌ خود می‌گفتی‌: «به‌ آسمان‌ صعود نموده‌، كرسی‌ خود را بالای‌ ستارگان‌ خدا خواهم‌ افراشت‌، و بر كوه‌ اجتماع‌ در اطراف‌ شمال‌ جلوس‌ خواهم‌نمود.",
			"14": "<span>14 </span> &nbsp;بالای‌ بلندیهای‌ ابرها صعود كرده‌، مثل‌ حضرت‌ اعلی‌ خواهم‌ شد.»",
			"15": "<span>15 </span> &nbsp;لكن‌ به‌ هاویه‌ به‌ اسفلهای‌ حُفره‌ فرود خواهی‌ شد.",
			"16": "<span>16 </span> &nbsp;آنانی‌ كه‌ تو را بینند بر تو چشم‌ دوخته‌ و در تو تأمّل‌ نموده‌، خواهند گفت‌: «آیا این‌ آن‌ مرد است‌ كه‌ جهان‌ را متزلزل‌ و ممالك‌ را مرتعش‌ می‌ساخت‌؟",
			"17": "<span>17 </span> &nbsp;كه‌ ربع‌ مسكون‌ را ویران‌ می‌نمود و شهرهایش‌ را منهدم‌ می‌ساخت‌ و اسیران‌ خود را به‌ خانه‌های‌ ایشان‌ رها نمی‌كرد؟»",
			"18": "<span>18 </span> &nbsp;همه‌ پادشاهان‌ امّت‌ها جمیعاً هر یك‌ در خانه‌ خود با جلال‌ می‌خوابند.",
			"19": "<span>19 </span> &nbsp;امّا تو از قبر خود بیرون‌ افكنده‌ می‌شوی‌ و مثل‌ شاخه‌ مكروه‌ و مانند لباس‌ كشتگانی‌ كه‌ با شمشیر زده‌ شده‌ باشند، كه‌ به‌ سنگهای‌ حفره‌ فرو می‌روند و مثل‌ لاشه‌ پایمال‌ شده‌.",
			"20": "<span>20 </span> &nbsp;با ایشان‌ در دفن‌ متّحد نخواهی‌ بود چونكه‌ زمین‌ خود را ویران‌ كرده‌، قوم‌ خویش‌ را كشته‌ای‌. ذرّیت‌ شریران‌ تا به‌ ابد مذكور نخواهند شد.",
			"21": "<span>21 </span> &nbsp;برای‌ پسرانش‌ به‌ سبب‌ گناه‌ پدران‌ ایشان‌ قتل‌ را مهیا سازید، تا ایشان‌ برنخیزند و در زمین‌ تصرّف‌ ننمایند و روی‌ ربع‌ مسكون‌ را از شهرها پر نسازند.",
			"22": "<span>22 </span> &nbsp;و یهوه‌ صبایوت‌ می‌گوید: «من‌ به‌ ضدّایشان‌ خواهم‌ برخاست‌.» و خداوند می‌گوید: «اسـم‌ و بقیـه‌ را و نسـل‌ و ذریـت‌ را از بابـل‌ منقطـع‌ خواهـم‌ ساخت‌.",
			"23": "<span>23 </span> &nbsp;و آن‌ را نصیب‌ خارپشتها و خَلابهـای‌ آب‌ خواهـم‌ گردانیـد و آن‌ را با جاروب‌ هلاكت‌ خواهم‌ رُفت‌.» یهوه‌ صبایوت‌ می‌گوید.",
			"24": "<span>24 </span> &nbsp;یهُوَه‌ صبایوت‌ قسم‌ خورده‌، می‌گوید: «یقیناً به‌ طوری‌ كه‌ قصد نموده‌ام‌ همچنان‌ واقع‌ خواهد شد. و به‌ نهجی‌ كه‌ تقدیر كرده‌ام‌ همچنان‌ بجا آورده‌ خواهد گشت‌.",
			"25": "<span>25 </span> &nbsp;و آشور را در زمین‌ خودم‌ خواهم‌ شكست‌ و او را بر كوههای‌ خویش‌ پایمال‌ خواهم‌ كرد. و یوغ‌ او از ایشان‌ رفع‌ شده‌، بار وی‌ از گردن‌ ایشان‌ برداشته‌ خواهد شد.»",
			"26": "<span>26 </span> &nbsp;تقدیری‌ كه‌ بر تمامی زمین‌ مقدّر گشته‌، این‌ است‌. و دستی‌ كه‌ بر جمیع‌ امّت‌ها دراز شده‌، همین‌ است‌.",
			"27": "<span>27 </span> &nbsp;زیرا كه‌ یهوه‌ صبایوت‌ تقدیر نموده‌ است‌، پس‌ كیست‌ كه‌ آن‌ را باطل‌ گرداند؟ و دست‌ اوست‌ كه‌ دراز شده‌ است‌ پس‌ كیست‌ كه‌ آن‌ را برگرداند؟",
			"28": "<span>28 </span> &nbsp;در سالی‌ كه‌ آحاز پادشاه‌ مُرد این‌ وحی‌ نازل‌ شد:",
			"29": "<span>29 </span> &nbsp;ای‌ جمیع‌ فلسطین‌ شادی‌ مكن‌ از اینكه‌ عصایی‌ كه‌ تو را می‌زد شكسته‌ شده‌ است‌. زیرا كه‌ از ریشه‌ مار افعی‌ بیرون‌ می‌آید و نتیجه‌ او اژدهای‌ آتشین‌ پرنده‌ خواهد بود.",
			"30": "<span>30 </span> &nbsp;و نخست‌زادگان‌ مسكینان‌ خواهند چرید و فقیران‌ در اطمینان‌ خواهند خوابید. و ریشه‌ تو را با قحطی‌ خواهم‌ كُشت‌ و باقی‌ ماندگان‌ تو مقتول‌ خواهند شد.",
			"31": "<span>31 </span> &nbsp;ای‌ دروازه‌ وِلوِلَه‌ نما! و ای‌ شهر فریاد برآور! ای‌ تمامی فلسطین‌ تو گداخته‌ خواهی‌ شد. زیرا كه‌ از طرف‌ شمال‌ دود می‌آید و از صفوف‌ وی‌ كسی‌ دور نخواهد افتاد.",
			"32": "<span>32 </span> &nbsp;پس‌ به‌ رسولان‌ امّت‌هاچه‌ جواب‌ داده‌ شود: «اینكه‌ خداوند صهیون‌ را بنیاد نهاده‌ است‌ و مسكینان‌ قوم‌ وی‌ در آن‌ پناه‌ خواهند برد.»"
		},
		"15": {
			"1": "<span>1 </span> &nbsp;وحی‌ دربارۀ موآب‌: زیرا كه‌ در شبی‌ عار موآب‌ خراب‌ و هلاك‌ شده‌ است‌؛ زیرا در شبی‌ قیر موآب‌ خراب‌ و هلاك‌ شده‌ است‌.",
			"2": "<span>2 </span> &nbsp;به‌ بتكده‌ و دیبون‌ به‌ مكان‌های‌ بلند به‌ جهت‌ گریستن‌ برآمده‌اند. موآب‌ برای‌ نَبُو و میدَبا وِلوِلَه‌ می‌كند. بر سر هریكی‌ از ایشان‌ گَری‌ است‌ و ریشهای‌ همه‌ تراشیده‌ شده‌ است‌.",
			"3": "<span>3 </span> &nbsp;در كوچه‌های‌ خود كمر خود را به‌ پلاس‌ می‌بندند و بر پشت‌ بامها و در چهارسوهای‌ خود هركس‌ وَلوِلَه‌ می‌نماید و اشكها می‌ریزد.",
			"4": "<span>4 </span> &nbsp;و حَشْبُون‌ و اَلْعَالَه‌ فریاد برمی‌آورند. آواز ایشان‌ تا یاهَصْ مسموع‌ می‌شود. بنابراین‌ مسلّحان‌ موآب‌ ناله‌ می‌كنند و جان‌ ایشان‌ در ایشان‌ می‌لرزد.",
			"5": "<span>5 </span> &nbsp;دل‌ من‌ به‌ جهت‌ موآب‌ فریاد برمی‌آورد. فراریانش‌ تا به‌ صوغَرْ و عْجِلَتْ شَلِشیا نعره‌ می‌زنند زیرا كه‌ ایشان‌ به‌ فراز لُوحیت‌ با گریه‌ برمی‌آیند. زیرا كه‌ از راه‌ حُوْرُونایم‌ صدای‌ هلاكت‌ برمی‌آورند.",
			"6": "<span>6 </span> &nbsp;زیرا كه‌ آبهای‌ نمِریم‌ خراب‌ شده‌، چونكه‌ علف‌ خشكیده‌ و گیاه‌ تلف‌ شده‌ و هیچ‌ چیز سبز باقی‌ نمانده‌ است‌.",
			"7": "<span>7 </span> &nbsp;بنابراین‌ دولتی‌ را كه‌ تحصیل‌ نموده‌اند و اندوخته‌های‌ خود را بر وادی‌ بیدها می‌برند.",
			"8": "<span>8 </span> &nbsp;زیرا كه‌ فریاد ایشان‌ حدود موآب‌ را احاطه‌ نموده‌ و وِلوِلَه‌ ایشان‌ تا اَجْلایم‌ و وِلوِلَه‌ ایشان‌ تا بئر ایلیم‌ رسیده‌ است‌.",
			"9": "<span>9 </span> &nbsp;چونكه‌ آبهای‌ دیمون‌ از خون‌ پر شده‌زانرو كه‌ بر دیمون‌ (بلایای‌) زیاد خواهم‌ آورد یعنی‌ شیری‌ را بر فراریان‌ موآب‌ و بر بقیه‌ زمینش‌ (خواهم‌ گماشت‌)."
		},
		"16": {
			"1": "<span>1 </span> &nbsp;بره‌ها را كه‌ خراج‌ حاكم‌ زمین‌ است‌ از سالع‌ بسوی‌ بیابان‌ به‌ كوه‌ دختر صَهْیون‌ بفرستید.",
			"2": "<span>2 </span> &nbsp;و دختران‌ موآب‌ مثل‌ مرغان‌ آواره‌ و مانند آشیانه‌ ترك‌ شده‌ نزد معبرهای‌ اَرنون‌ خواهند شد.",
			"3": "<span>3 </span> &nbsp;مشورت‌ بدهید و انصاف‌ را بجا آورید، و سایه‌ خود را در وقت‌ ظهر مثل‌ شب‌ بگردان‌. رانده‌ شدگان‌ را پنهان‌ كن‌ و فراریان‌ را تسلیم‌ منما.",
			"4": "<span>4 </span> &nbsp;ای‌ موآب‌ بگذار كه‌ رانده‌شدگان‌ من‌ نزد تو مأوا گزینند. و برای‌ ایشان‌ از روی‌ تاراج‌كننده‌ پناه‌گاه‌ باش‌. زیرا ظالم‌ نابود می‌شود و تاراج‌كننده‌ تمام‌ می‌گردد و ستمكار از زمین‌ تلف‌ خواهد شد.",
			"5": "<span>5 </span> &nbsp;و كُرسی‌ به‌ رحمت‌ استوار خواهد گشت‌ و كسی‌ به‌ راستی‌ بر آن‌ در خیمه‌ داود خواهد نشست‌ كه‌ داوری‌ كند و انصاف‌ را بطلبد و به‌ جهت‌ عدالت‌ تعجیل‌ نماید.",
			"6": "<span>6 </span> &nbsp;غرور موآب‌ و بسیاری‌ تكبّر و خُیلاء و كبر و خشم‌ او را شنیدیم‌ و فخر او باطل‌ است‌.",
			"7": "<span>7 </span> &nbsp;بدین‌ سبب‌ موآب‌ به‌ جهت‌ موآب‌ وِلوِلَه‌ می‌كند و تمامی‌ ایشان‌ وِلْوِلَه‌ می‌نمایند. به‌ جهت‌ بنیادهای‌ قیر حارَسَت‌ ناله‌ می‌كنید زیرا كه‌ بالكّل‌ مضروب‌ می‌شود.",
			"8": "<span>8 </span> &nbsp;زیرا كه‌ مزرعه‌های‌ حَشْبُون‌ و مَوْهای‌ سِبْمَه‌ پژمرده‌ شد و سروران‌ امّت‌ها تاكهایش‌ را شكستند.آنها تا به‌ یعْزیر رسیده‌ بود و در بیابان‌ پراكنده‌ می‌شد و شاخه‌هایش‌ منتشر شده‌، از دریا می‌گذشت‌.",
			"9": "<span>9 </span> &nbsp;بنابراین‌ برای‌ مو سِبْمَه‌ به‌ گریه‌یعْزیر خواهم‌ گریست‌. ای‌ حَشْبون‌ و اَلْعالَه‌ شما را با اشكهای‌ خود سیراب‌ خواهم‌ ساخت‌ زیرا كه‌ برمیوه‌ها و انگورهایت‌ گلبانگ‌ افتاده‌ است‌.",
			"10": "<span>10 </span> &nbsp;شادی‌ و ابتهاج‌ از بُستانها برداشته‌ شد و در تاكستانها ترنّم‌ و آواز شادمانی‌ نخواهد بود و كسی‌ شراب‌ را در چرخُشتها پایمال‌ نمی‌كند. صدای‌ شادمانی‌ را خاموش‌ گردانیدم‌.",
			"11": "<span>11 </span> &nbsp;لهذا احشای‌ من‌ مثل‌ بربط‌ به‌ جهت‌ موآب‌ صدا می‌زند و بطن‌ من‌ برای‌ قیر حارَسْ.",
			"12": "<span>12 </span> &nbsp;و هنگامی‌ كه‌ موآب‌ در مكان‌ بلند خود حاضر شده‌، خویشتن‌ را خسته‌ كند و به‌ مكان‌ مقدّس‌ خود برای‌ دعا بیاید كامیاب‌ نخواهد شد.",
			"13": "<span>13 </span> &nbsp;این‌ است‌ كلامی‌ كه‌ خداوند دربارۀ موآب‌ از زمان‌ قدیم‌ گفته‌ است‌.",
			"14": "<span>14 </span> &nbsp;امّا الا´ن‌ خداوند تكلّم‌ نموده‌، می‌گوید كه‌ بعد از سه‌ سال‌ مثل‌ سالهای‌ مزدور، جلال‌ موآب‌ با تمامی‌ جماعت‌ كثیر او محقّر خواهد شد و بقیه‌ آن‌ بسیار كم‌ و بی‌قوّت‌ خواهند گردید."
		},
		"17": {
			"1": "<span>1 </span> &nbsp;وحی‌ دربارۀ دمشق‌: اینك‌ دمشق‌ از میان‌ شهرها برداشته‌ می‌شود و توده‌ خراب‌ خواهد گردید.",
			"2": "<span>2 </span> &nbsp;شهرهای‌ عَرُوعیر متروك‌ می‌شود و به‌ جهت‌ خوابیدن‌ گله‌ها خواهد بود و كسی‌ آنها را نخواهد ترسانید.",
			"3": "<span>3 </span> &nbsp;و حصار از افرایم‌ تلف‌ خواهد شد و سلطنت‌ از دمشق‌ و از بقیه‌ اَرام‌. و مثل‌ جلال‌ بنی‌اسرائیل‌ خواهند بود زیرا كه‌ یهوه‌ صبایوت‌ چنین‌ می‌گوید.",
			"4": "<span>4 </span> &nbsp;و در آن‌ روز جلال‌ یعقوب‌ ضعیف‌ می‌شود و فربهی‌ جسدش‌ به‌ لاغری‌ تبدیل‌ می‌گردد.",
			"5": "<span>5 </span> &nbsp;و چنان‌ خواهد بود كه‌ دروگران‌ زرع‌ را جمع‌ كنند و دستهای‌ ایشان‌ سنبله‌ها را درو كند.و خواهد بود مثل‌ وقتی‌ كه‌ در وادی‌ رفایم‌ سنبله‌ها را بچینند.",
			"6": "<span>6 </span> &nbsp;و خوشه‌های‌ چند در آن‌ باقی‌ ماند و مثل‌ وقتی‌ كه‌ زیتون‌ را بتكانند كه‌ دو یا سه‌ دانه‌ بر سر شاخه‌ بلند و چهار یا پنج‌ دانه‌ بر شاخچه‌های‌ بارور آن‌ باقی‌ ماند. یهوه‌ خدای‌ اسرائیل‌ چنین‌ می‌گوید.",
			"7": "<span>7 </span> &nbsp;در آن‌ روز انسان‌ بسوی‌ آفریننده‌ خود نظر خواهد كرد و چشمانش‌ بسوی‌ قدّوس‌ اسرائیل‌ خواهد نگریست‌.",
			"8": "<span>8 </span> &nbsp;و بسوی‌ مذبح‌هایی‌ كه‌ به‌ دستهای‌ خود ساخته‌ است‌ نظر نخواهد كرد و به‌ آنچه‌ با انگشتهای‌ خویش‌ بنا نموده‌ یعنی‌ اشیریم‌ و بتهای‌ آفتاب‌ نخواهد نگریست‌.",
			"9": "<span>9 </span> &nbsp;در آن‌ روز شهرهای‌ حصینش‌ مثل‌ خرابه‌هایی‌ كه‌ در جنگل‌ یا بر كوه‌ بلند است‌ خواهد شد كه‌ آنها را از حضور بنی‌اسرائیل‌ واگذاشتند و ویران‌ خواهد شد.",
			"10": "<span>10 </span> &nbsp;چونكه‌ خدای‌ نجات‌ خود را فراموش‌ كردی‌ و صخره‌ قوّت‌ خویش‌ را به‌ یاد نیاوردی‌، بنابراین‌ نهالهای‌ دلپذیر غرس‌ خواهی‌ نمود و قلمه‌های‌ غریب‌ را خواهی‌ كاشت‌.",
			"11": "<span>11 </span> &nbsp;در روزی‌ كه‌ غرس‌ می‌نمایی‌، آن‌ را نمّو خواهی‌ داد و در صبحْ مزروعِ خود را به‌ شكوفه‌ خواهی‌ آورد، امّا محصولش‌ در روز آفت‌ مهلك‌ و حزن‌ علاج‌ناپذیر بر باد خواهد رفت‌.",
			"12": "<span>12 </span> &nbsp;وای‌ بر شورش‌ قوم‌های‌ بسیار كه‌ مثل‌ شورش‌ دریا شورش‌ می‌نمایند، و خروش‌ طوایفی‌ كه‌ مثل‌ خروش‌ آبهای‌ زورآور خروش‌ می‌كنند.",
			"13": "<span>13 </span> &nbsp;طوایف‌ مثل‌ خروش‌ آبهای‌ بسیار می‌خروشند، امّا ایشان‌ را عتاب‌ خواهد كرد و به‌ جای‌ دور خواهند گریخت‌ و مثل‌ كاه‌ كوهها در برابر باد رانده‌ خواهند شد و مثل‌ غبار در برابرگردباد.",
			"14": "<span>14 </span> &nbsp;در وقت‌ شام‌ اینك‌ خوف‌ است‌ و قبل‌ از صبح‌ نابود می‌شوند. نصیب‌ تاراج‌كنندگان‌ ما و حصّه‌ غارت‌ نمایندگان‌ ما همین‌ است‌."
		},
		"18": {
			"1": "<span>1 </span> &nbsp;وای‌ بر زمینی‌ كه‌ در آن‌ آواز بالها است‌كه‌ به‌ آن‌ طرف‌ نهرهای‌ كوش‌ می‌باشد.",
			"2": "<span>2 </span> &nbsp;و ایلچیان‌ به‌ دریا و در كشتیهای‌ بَرْدِی‌ بر روی‌ آبها می‌فرستد و می‌گوید: ای‌ رسولان‌ تیزرو بروید نزد امّت‌ بلند قدّ و برّاق‌، نزد قومی‌ كه‌ از ابتدایش‌ تا كنون‌ مهیب‌ بوده‌اند یعنی‌ امّت‌ زورآور و پایمال‌ كننده‌ كه‌ نهرها زمین‌ ایشان‌ را تقسیم‌ می‌كند.",
			"3": "<span>3 </span> &nbsp;ای‌ تمامی‌ ساكنان‌ ربع‌ مسكون‌ و سكنه‌ جهان‌، چون‌ عَلَمی‌ بر كوهها بلند گردد بنگرید و چون‌ كَرِنّا نواخته‌ شود بشنوید.",
			"4": "<span>4 </span> &nbsp;زیرا خداوند به‌ من‌ چنین‌ گفته‌ است‌ كه‌ من‌ خواهم‌ آرامید و از مكان‌ خود نظر خواهم‌ نمود. مثل‌ گرمای‌ صاف‌ بر نباتات‌ و مثل‌ ابر شبنم‌ دار در حرارت‌ حصاد.",
			"5": "<span>5 </span> &nbsp;زیرا قبل‌ از حصاد وقتی‌ كه‌ شكوفه‌ تمام‌ شود و گل‌ به‌ انگور رسیده‌، مبدّل‌ گردد او شاخه‌ها را با ارّه‌ها خواهد برید و نهالها را بریده‌ دور خواهد افكند.",
			"6": "<span>6 </span> &nbsp;و همه‌ برای‌ مرغان‌ شكاری‌ كوهها و وحوش‌ زمین‌ واگذاشته‌ خواهد شد. و مرغان‌ شكاری‌ تابستان‌ را بر آنها بسر خواهند برد و جمیع‌ وحوش‌ زمین‌ زمستان‌ را بر آنها خواهند گذرانید.",
			"7": "<span>7 </span> &nbsp;و در آن‌ زمان‌ هدیه‌ای‌ برای‌ یهوه‌ صبایوت‌ از قوم‌ بلند قدّ و برّاق‌ و از قومی‌ كه‌ از ابتدایش‌ تا كنون‌ مهیب‌ است‌ و از امّتی‌ زورآور و پایمال‌ كننده‌ كه‌ نهرها زمین‌ ایشان‌ را تقسیم‌ می‌كند، به‌ مكان‌ اسم‌ یهوه‌ صبایوت‌ یعنی‌ به‌ كوه‌صهیون‌ آورده‌ خواهد شد."
		},
		"19": {
			"1": "<span>1 </span> &nbsp;وحی‌ دربارۀ مصر: اینك‌ خداوند بر اَبْرِ تیزرو سوار شده‌، به‌ مصر می‌آید و بتهای‌ مصر از حضور وی‌ خواهد لرزید و دلهای‌ مصریان‌ در اندرون‌ ایشان‌ گداخته‌ خواهد شد.",
			"2": "<span>2 </span> &nbsp;و مصریان‌ را بر مصریان‌ خواهم‌ برانگیخت‌. برادر با برادر خود و همسایه‌ با همسایه‌ خویش‌ و شهر با شهر و كشور با كشور جنگ‌ خواهند نمود.",
			"3": "<span>3 </span> &nbsp;و روح‌ مصر در اندرونش‌ افسرده‌ شده‌، مشورتش‌ را باطل‌ خواهم‌ گردانید و ایشان‌ از بتها و فالگیران‌ و صاحبان‌ اجنّه‌ و جادوگران‌ سؤال‌ خواهند نمود.",
			"4": "<span>4 </span> &nbsp;و مصریان‌ را به‌ دست‌ آقای‌ ستم‌كیش‌ تسلیم‌ خواهم‌ نمود و پادشاه‌ زورآور بر ایشان‌ حكمرانی‌ خواهد كرد. خداوند یهوه‌ صبایوت‌ چنین‌ می‌گوید.",
			"5": "<span>5 </span> &nbsp;و آب‌ از دریا (نیل‌) كم‌ شده‌، نهر خراب‌ و خشك‌ خواهد گردید.",
			"6": "<span>6 </span> &nbsp;و نهرها متعفّن‌ شده‌، جویهای‌ ماصور كم‌ شده‌ می‌خشكد و نی‌ و بوریا پژمرده‌ خواهد شد.",
			"7": "<span>7 </span> &nbsp;و مَرغْزاری‌ كه‌ بر كنار نیل‌ و بر دهنه‌ نیل‌ است‌ و همه‌ مزرعه‌های‌ نیل‌ خشك‌ و رانده‌ شده‌ و نابود خواهد گردید.",
			"8": "<span>8 </span> &nbsp;و ماهی‌ گیران‌ ماتم‌ می‌گیرند و همه‌ آنانی‌ كه‌ قلاّب‌ به‌ نیل‌ اندازند زاری‌ می‌كنند و آنانی‌ كه‌ دام‌ بر روی‌ آب‌ گسترانند افسرده‌ خواهند شد.",
			"9": "<span>9 </span> &nbsp;و عاملان‌ كتان‌ شانه‌ زده‌ و بافندگان‌ پارچه‌ سفید خجل‌ خواهند شد.",
			"10": "<span>10 </span> &nbsp;و اركان‌ او ساییده‌ و جمیع‌ مزدوران‌ رنجیده‌ دل‌ خواهند شد.",
			"11": "<span>11 </span> &nbsp;سروران‌ صوعن‌ بالكّل‌ احمق‌ می‌شوند ومشورت‌ مشیران‌ دانشمند فرعون‌ وحشی‌ می‌گردد. پس‌ چگونه‌ به‌ فرعون‌ می‌گویید كه‌ من‌ پسر حكما و پسر پادشاهان‌ قدیم‌ می‌باشم‌.",
			"12": "<span>12 </span> &nbsp;پس‌ حكیمان‌ تو كجایند تا ایشان‌ تو را اطّلاع‌ دهند و بدانند كه‌ یهوه‌ صبایوت‌ دربارۀ مصر چه‌ تقدیر نموده‌ است‌؟",
			"13": "<span>13 </span> &nbsp;سروران‌ صوعن‌ ابله‌ شده‌ و سروران‌ نوف‌ فریب‌ خورده‌اند و آنانی‌ كه‌ سنگ‌ زاویه‌ اسباط‌ مصر هستند آن‌ را گمراه‌ كرده‌اند.",
			"14": "<span>14 </span> &nbsp;و خداوند روح‌ خیرگی‌ در وسط‌ آن‌ آمیخته‌ است‌ كه‌ ایشان‌ مصریان‌ را در همه‌ كارهای‌ ایشان‌ گمراه‌ كرده‌اند، مثل‌ مستان‌ كه‌ در قی‌ خود افتان‌ و خیزان‌ راه‌ می‌روند.",
			"15": "<span>15 </span> &nbsp;و مصریان‌ را كاری‌ نخواهد ماند كه‌ سر یا دم‌ نخل‌ یا بوریا بكند.",
			"16": "<span>16 </span> &nbsp;در آن‌ روز اهل‌ مصر مثل‌ زنان‌ می‌باشند و از حركت‌ دست‌ یهوه‌ صبایوت‌ كه‌ آن‌ را بر مصر به‌ حركت‌ می‌آورد لرزان‌ و هراسان‌ خواهند شد.",
			"17": "<span>17 </span> &nbsp;و زمین‌ یهودا باعث‌ خوف‌ مصر خواهد شد كه‌ هركه‌ ذكر آن‌ را بشنود خواهد ترسید به‌ سبب‌ تقدیری‌ كه‌ یهوه‌ صبایوت‌ بر آن‌ مقدّر نموده‌ است‌.",
			"18": "<span>18 </span> &nbsp;در آن‌ روز پنج‌ شهر در زمین‌ مصر به‌ زبان‌ كنعان‌ متكلّم‌ شده‌، برای‌ یهوه‌ صبایوت‌ قسم‌ خواهند خورد و یكی‌ شهر هلاكت‌ نامیده‌ خواهد شد.",
			"19": "<span>19 </span> &nbsp;در آن‌ روز مذبحی‌ برای‌ خداوند در میان‌ زمین‌ مصر و ستونی‌ نزد حدودش‌ برای‌ خداوند خواهد بود.",
			"20": "<span>20 </span> &nbsp;و آن‌ آیتی‌ و شهادتی‌ برای‌ یهوه‌ صبایوت‌ در زمین‌ مصر خواهد بود. زیرا كه‌ نزد خداوند به‌ سبب‌ جفاكنندگان‌ خویش‌ استغاثه‌ خواهد نمود و او نجات‌ دهنده‌ و حمایت‌ كننده‌ای‌ برای‌ ایشان‌ خواهد فرستاد و ایشان‌ را خواهد رهانید.",
			"21": "<span>21 </span> &nbsp;و خداوند بر مصریان‌ معروف‌ خواهد شد و در آن‌ روز مصریان‌ خداوند راخواهند شناخت‌ و با ذبایح‌ و هدایا او را عبادت‌ خواهند كرد و برای‌ خداوند نذر كرده‌، آن‌ را وفا خواهند نمود.",
			"22": "<span>22 </span> &nbsp;و خداوند مصریان‌ را خواهد زد و به‌ زدن‌ شفا خواهد داد زیرا چون‌ بسوی‌ خداوند بازگشت‌ نمایند ایشان‌ را اجابت‌ نموده‌، شفا خواهد داد.",
			"23": "<span>23 </span> &nbsp;در آن‌ روز شاهراهی‌ از مصر به‌ آشور خواهد بود و آشوریان‌ به‌ مصر و مصریان‌ به‌ آشور خواهند رفت‌ و مصریان‌ با آشوریان‌ عبادت‌ خواهند نمود.",
			"24": "<span>24 </span> &nbsp;در آن‌ روز اسرائیل‌ سوم‌ مصر و آشور خواهد شد و آنها در میان‌ جهان‌ بركت‌ خواهند بود.",
			"25": "<span>25 </span> &nbsp;زیرا كه‌ یهوه‌ صبایوت‌ آنها را بركت‌ داده‌ خواهد گفت‌ قوم‌ من‌ مصر و صنعت‌ دست‌ من‌ آشور و میراث‌ من‌ اسرائیل‌ مبارك‌ باشند."
		},
		"20": {
			"1": "<span>1 </span> &nbsp;در سالی‌ كه‌ تَرْتان‌ به‌ اَشْدُود آمد هنگامی‌ كه‌ سرجون‌ پادشاه‌ آشور او را فرستاد، پس‌ با اَشْدُود جنگ‌ كرده‌، آن‌ را گرفت‌.",
			"2": "<span>2 </span> &nbsp;در آن‌ وقت‌ خداوند به‌ واسطه‌ اشعیا ابن‌ آموص‌ تكلّم‌ نموده‌، گفت‌: «برو و پلاس‌ را از كمر خود بگشا و نعلین‌ را از پای‌ خود بیرون‌ كن‌.» و او چنین‌ كرده‌، عریان‌ و پا برهنه‌ راه‌ می‌رفت‌.",
			"3": "<span>3 </span> &nbsp;و خداوند گفت‌: «چنانكه‌ بنده‌ من‌ اشعیا سه‌ سال‌ عریان‌ و پا برهنه‌ راه‌ رفته‌ است‌ تا آیتی‌ و علامتی‌ دربارۀ مصر و كوش‌ باشد،",
			"4": "<span>4 </span> &nbsp;بهمان‌ طور پادشاه‌ آشور اسیران‌ مصر و جلاء وطنان‌ كوش‌ را از جوانان‌ وپیران‌ عریان‌ و پابرهنه‌ و مكشوف‌ سرین‌ خواهد برد تا رسوایی‌ مصر باشد.",
			"5": "<span>5 </span> &nbsp;و ایشان‌ به‌ سبب‌ كوش‌ كه‌ ملجای‌ ایشان‌ است‌ و مصر كه‌ فخر ایشان‌ باشد مضطرب‌ و خجل‌ خواهند شد.",
			"6": "<span>6 </span> &nbsp;و ساكنان‌ این‌ ساحل‌ در آن‌ روز خواهند گفت‌: اینك‌ ملجای‌ ما كه‌ برای‌ اعانت‌ به‌ آن‌ فرار كردیم‌ تا از دست‌ پادشاه‌ آشور نجات‌ یابیم‌ چنین‌ شده‌ است‌، پس‌ ما چگونه‌ نجات‌ خواهیم‌ یافت‌؟»"
		},
		"21": {
			"1": "<span>1 </span> &nbsp;وحی‌ دربارۀ بیابان‌ بحر: چنانكه‌ گردباد در جنوب‌ می‌آید، این‌ نیز از بیابان‌ از زمین‌ هولناك‌ می‌آید.",
			"2": "<span>2 </span> &nbsp;رؤیای‌ سخت‌ برای‌ من‌ منكشف‌ شده‌ است‌، خیانت‌ پیشه‌ خیانت‌ می‌كند و تاراج‌ كننده‌ تاراج‌ می‌نماید. ای‌ عیلام‌ برآی‌ و ای‌ مِدْیان‌ محاصره‌ نما. تمام‌ ناله‌ آن‌ را ساكت‌ گردانیدم‌.",
			"3": "<span>3 </span> &nbsp;از این‌ جهت‌ كمر من‌ از شدّت‌ درد پر شده‌ است‌ و درد زه‌ مثل‌ درد زنی‌ كه‌ می‌زاید مرا درگرفته‌ است‌. پیچ‌ و تاب‌ می‌خورم‌ كه‌ نمی‌توانم‌ بشنوم‌، مدهوش‌ می‌شوم‌ كه‌ نمی‌توانم‌ ببینم‌.",
			"4": "<span>4 </span> &nbsp;دل‌ من‌ می‌طپید و هیبت‌ مرا ترسانید. او شب‌ لذّت‌ مرا برایم‌ به‌ خوف‌ مبدّل‌ ساخته‌ است‌.",
			"5": "<span>5 </span> &nbsp;سفره‌ را مهیا ساخته‌ و فرش‌ را گسترانیده‌ به‌ اكل‌ و شرب‌ مشغول‌ می‌باشند. ای‌ سروران‌ برخیزید و سپرها را روغن‌ بمالید.",
			"6": "<span>6 </span> &nbsp;زیرا خداوند به‌ من‌ چنین‌ گفته‌ است‌: «برو و دیده‌بان‌ را قرار بده‌ تا آنچه‌ را كه‌ بیند اعلام‌ نماید.",
			"7": "<span>7 </span> &nbsp;و چون‌ فوج‌ سواران‌ جفت‌ جفت‌ و فوج‌ الاغان‌ و فوج‌ شتران‌ را بیند آنگاه‌ به‌ دقّت‌ تمام‌ توجّه‌ بنماید.»",
			"8": "<span>8 </span> &nbsp;پس‌ او مثل‌ شیر صدا زد كه‌ «ای‌ آقا من‌ دائماً در روز بر محرس‌ ایستاده‌ام‌ و تمامی‌ شب‌ بر دیده‌بانگاه‌ خود برقرار می‌باشم‌.",
			"9": "<span>9 </span> &nbsp;و اینك‌ فوج‌مردان‌ و سواران‌ جفت‌ جفت‌ می‌آیند و او مزید كرده‌، گفت‌: بابل‌ افتاد افتاده‌ است‌ و تمامی‌ تمثال‌های‌ تراشیده‌ خدایانش‌ را بر زمین‌ شكسته‌اند.»",
			"10": "<span>10 </span> &nbsp;ای‌ كوفته‌ شده‌ من‌ و ای‌ محصول‌ خرمن‌ من‌ آنچه‌ از یهوه‌ صبایوت‌ خدای‌ اسرائیل‌ شنیدم‌ به‌ شما اعلام‌ می‌نمایم‌.",
			"11": "<span>11 </span> &nbsp;وحی‌ درباره‌ دومه‌: كسی‌ از سعیر به‌ من‌ ندا می‌كند كه‌ «ای‌ دیده‌بان‌ از شب‌ چه‌ خبر؟ ای‌ دیده‌بان‌ از شب‌ چه‌ خبر؟»",
			"12": "<span>12 </span> &nbsp;دیده‌بان‌ می‌گوید كه‌ «صبح‌ می‌آید و شام‌ نیز. اگر پرسیدن‌ می‌خواهید بپرسید و بازگشت‌ نموده‌، بیایید.»",
			"13": "<span>13 </span> &nbsp;وحی‌ دربارۀ عرب‌: ای‌ قافله‌های‌ ددانیان‌ در جنگل‌ عرب‌ منزل‌ كنید.",
			"14": "<span>14 </span> &nbsp;ای‌ ساكنان‌ زمین‌ تیما تشنگان‌ را به‌ آب‌ استقبال‌ كنید و فراریان‌ را به‌ خوراك‌ ایشان‌ پذیره‌ شوید.",
			"15": "<span>15 </span> &nbsp;زیرا كه‌ ایشان‌ از شمشیرها فرار می‌كنند، از شمشیر برهنه‌ و كمان‌ زه‌ شده‌ و از سختی‌ جنگ‌.",
			"16": "<span>16 </span> &nbsp;زانرو كه‌ خداوند به‌ من‌ گفته‌ است‌ بعد از یكسال‌ موافق‌ سالهای‌ مزدوران‌، تمامی شوكت‌ قیدار تلف‌ خواهد شد.",
			"17": "<span>17 </span> &nbsp;و بقیه‌ شماره‌ تیراندازان‌ و جبّاران‌ بنی‌ قیدار قلیل‌ خواهد شد چونكه‌ یهوه‌ خدای‌ اسرائیل‌ این‌ را گفته‌ است‌."
		},
		"22": {
			"1": "<span>1 </span> &nbsp;الا´ن‌ تو را چه‌ شد كه‌ كلّیةً بر بامها برآمدی‌؟",
			"2": "<span>2 </span> &nbsp;ای‌ كه‌ پر از شورشها هستی‌ و ای‌ شهر پرغوغا و ای‌ قریه‌ مفتخر! كشتگانت‌ كشته‌ شمشیر نیستند و در جنگ‌ هلاك‌ نشده‌اند.",
			"3": "<span>3 </span> &nbsp;جمیع‌ سرورانت‌ با هم‌ گریختند و بدون‌ تیراندازان‌ اسیر گشتند. همگانی‌ كه‌ در تو یافت‌ شدند با هم‌ اسیر گردیدند و به‌ جای‌ دور فرار كردند.",
			"4": "<span>4 </span> &nbsp;بنابراین‌ گفتم‌ نظر خود را از من‌ بگردانید زیرا كه‌ با تلخی‌ گریه‌ می‌كنم‌. برای‌ تسلّی‌ من‌ دربارۀ خرابی‌ دختر قومم‌ الحاح‌ مكنید.",
			"5": "<span>5 </span> &nbsp;زیرا خداوند یهوه‌ صبایوت‌ روز آشفتگی‌ و پایمالی‌ و پریشانی‌ای‌ در وادی‌ رؤیا دارد. دیوارها را منهدم‌ می‌سازند و صدای‌ استغاثه‌ تا به‌ كوهها می‌رسد.",
			"6": "<span>6 </span> &nbsp;و عیلام‌ با افواج‌ مردان‌ و سواران‌ تركش‌ را برداشته‌ است‌ و قیر سپر را مكشوف‌ نموده‌ است‌،",
			"7": "<span>7 </span> &nbsp;و وادیهای‌ بهترینت‌ از ارابه‌ها پر شده‌، سواران‌ پیش‌ دروازه‌هایت‌ صف‌آرایی‌ می‌نمایند؛",
			"8": "<span>8 </span> &nbsp;و پوشش‌ یهودا برداشته‌ می‌شود و در آن‌ روز به‌ اسلحه‌ خانه‌ جنگل‌ نگاه‌ خواهید كرد؛",
			"9": "<span>9 </span> &nbsp;و رخنه‌های‌ شهر داود را كه‌ بسیارند خواهید دید و آب‌ بركه‌ تحتانی‌ را جمع‌ خواهید نمود؛",
			"10": "<span>10 </span> &nbsp;و خانه‌های‌ اورشلیم‌ را خواهید شمرد و خانه‌ها را به‌ جهت‌ حصاربندی‌ دیوارها خراب‌ خواهید نمود.",
			"11": "<span>11 </span> &nbsp;و درمیان‌ دو دیوار حوضی‌ برای‌ آب‌ بركه‌ قدیم‌ خواهید ساخت‌ امّا به‌ صانع‌ آن‌ نخواهید نگریست‌ و به‌ آنكه‌ آن‌ را از ایام‌ پیشین‌ ساخته‌ است‌ نگران‌ نخواهید شد.",
			"12": "<span>12 </span> &nbsp;و در آن‌ روز خداوند یهوه‌ صبایوت‌ (شما را) به‌ گریستن‌ وماتم‌ كردن‌ و كندن‌ مو و پوشیدن‌ پلاس‌ خواهد خواند.",
			"13": "<span>13 </span> &nbsp;و اینك‌ شادمانی‌ و خوشی‌ و كشتن‌ گاوان‌ و ذبح‌ كردن‌ گوسفندان‌ و خوردن‌ گوشت‌ و نوشیدن‌ شراب‌ خواهد بود كه‌ بخوریم‌ و بنوشیم‌ زیرا كه‌ فردا می‌میریم‌.",
			"14": "<span>14 </span> &nbsp;و یهوه‌ صبایوت‌ در گوش‌ من‌ اعلام‌ كرده‌ است‌ كه‌ این‌ گناه‌ شما تا بمیرید هرگز كفّاره‌ نخواهد شد. خداوند یهوه‌ صبایوت‌ این‌ را گفته‌ است‌.",
			"15": "<span>15 </span> &nbsp;خداوند یهوه‌ صبایوت‌ چنین‌ می‌گوید: «برو و نزد این‌ خزانه‌دار یعنی‌ شبنا كه‌ ناظر خانه‌ است‌ داخل‌ شو و به‌ او بگو:",
			"16": "<span>16 </span> &nbsp;تو را در اینجا چه‌ كار است‌ و در اینجا كه‌ را داری‌ كه‌ در اینجا قبری‌ برای‌ خود كنده‌ای‌؟ ای‌ كسی‌ كه‌ قبر خود را در مكان‌ بلند می‌كَنی‌ و مسكنی‌ برای‌ خویشتن‌ در صخره‌ می‌تراشی‌.»",
			"17": "<span>17 </span> &nbsp;اینك‌ ای‌ مرد، خداوند البتّه‌ تو را دور خواهد انداخت‌ و البتّه‌ تو را خواهد پوشانید.",
			"18": "<span>18 </span> &nbsp;و البتّه‌ تو را مثل‌ گوی سخت‌ خواهد پیچید و به‌ زمین‌ وسیع‌ تو را خواهد افكند و در آنجا خواهی‌ مرد و در آنجا ارابه‌های‌ شوكت‌ تو رسوایی‌ خانه‌ آقایت‌ خواهد شد.",
			"19": "<span>19 </span> &nbsp;و تو را از منصبت‌ خواهم‌ راند و از مكانت‌ به‌ زیر افكنده‌ خواهی‌ شد.",
			"20": "<span>20 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ بنده‌ خویش‌ الیاقیم‌ بن‌ حلقیا را دعوت‌ خواهم‌ نمود.",
			"21": "<span>21 </span> &nbsp;و او را به‌ جامه‌ تو ملبّس‌ ساخته‌ به‌ كمربندت‌ محكم‌ خواهم‌ ساخت‌ و اقتدار تو را به‌ دست‌ او خواهم‌ داد و او ساكنان‌ اورشلیم‌ و خاندان‌ یهودا را پدر خواهد بود.",
			"22": "<span>22 </span> &nbsp;و كلید خانه‌ داود را بر دوش‌ وی‌ خواهم‌ نهاد و چون‌ بگشاید احدی‌ نخواهد بست‌ و چون‌ ببندد، احدی‌ نخواهد گشاد.",
			"23": "<span>23 </span> &nbsp;و اورا در جای‌ محكم‌ مثل‌ میخ‌ خواهم‌ دوخت‌ و برای‌ خاندان‌ پدر خود كرسی‌ جلال‌ خواهد بود.",
			"24": "<span>24 </span> &nbsp;و تمامی جلال‌ خاندان‌ پدرش‌ را از اولاد و احفاد و همه‌ ظروف‌ كوچك‌ را از ظروف‌ كاسه‌ها تا ظروف‌ تُنگها بر او خواهند آویخت‌.",
			"25": "<span>25 </span> &nbsp;و یهوه‌ صبایوت‌ می‌گوید كه‌ در آن‌ روز آن‌ میخی‌ كه‌ در مكان‌ محكم‌ دوخته‌ شده‌، متحرّك‌ خواهد گردید و قطع‌ شده‌، خواهد افتاد و باری‌ كه‌ بر آن‌ است‌، تلف‌ خواهد شد زیرا خداوند این‌ را گفته‌ است‌."
		},
		"23": {
			"1": "<span>1 </span> &nbsp;‌ وحی‌ دربارۀ صور: ای‌ كشتیهای‌ ترشیش‌ وِلْوِلَه‌ نمایید زیرا كه‌ بحدی‌ خراب‌ شده‌ است‌ كه‌ نه‌ خانه‌ای‌ و نه‌ مدخلی‌ باقی‌ مانده‌. از زمین‌ كتّیم‌ خبر به‌ ایشان‌ رسیده‌ است‌.",
			"2": "<span>2 </span> &nbsp;ای‌ ساكنان‌ ساحل‌ كه‌ تاجران‌ صیدون‌ كه‌ از دریا عبور می‌كنند تو را پر ساخته‌اند، آرام‌ گیرید.",
			"3": "<span>3 </span> &nbsp;و دخل‌ او از محصول‌ شیحور و حصاد نیل‌ بر آبهای‌ بسیار می‌بود، پس‌ او تجارت‌ گاه‌ امّت‌ها شده‌ است‌.",
			"4": "<span>4 </span> &nbsp;ای‌ صیدون‌ خجل‌ شو زیرا كه‌ دریا یعنی‌ قلعه‌ دریا متكلّم‌ شده‌، می‌گوید درد زه‌ نكشیده‌ام‌ و نزاییده‌ام‌ و جوانان‌ را نپرورده‌ام‌ و دوشیزگان‌ را تربیت‌ نكرده‌ام‌.",
			"5": "<span>5 </span> &nbsp;چون‌ این‌ خبر به‌ مصر برسد، از اخبار صور بسیار دردناك‌ خواهند شد.",
			"6": "<span>6 </span> &nbsp;ای‌ ساكنان‌ ساحل‌ دریا به‌ ترشیش‌ بگذرید و وِلْوِلَه‌ نمایید.",
			"7": "<span>7 </span> &nbsp;آیا این‌ شهر مفتخر شما است‌ كه‌ قدیمی‌ و از ایام‌ سلف‌ بوده‌ است‌ و پایهایش‌ او را به‌ جای‌ دور برده‌، تا در آنجا مأوا گزیند؟",
			"8": "<span>8 </span> &nbsp;كیست‌كه‌ این‌ قصد را دربارۀ صور آن‌ شهر تاج‌بخش‌ كه‌ تجّار وی‌ سروران‌ و بازرگانان‌ او شرفای‌ جهان‌ بوده‌اند نموده‌ است‌؟",
			"9": "<span>9 </span> &nbsp;یهوه‌ صبایوت‌ این‌ قصد را نموده‌ است‌ تا تكبّر تمامی‌ جلال‌ را خوار سازد و جمیع‌ شرفای‌ جهان‌ را محقّر نماید.",
			"10": "<span>10 </span> &nbsp;ای‌ دختر ترشیش‌ از زمین‌ خود مثل‌ نیل‌ بگذر زیرا كه‌ دیگر هیچ‌ بند برای‌ تو نیست‌.",
			"11": "<span>11 </span> &nbsp;او دست‌ خود را بر دریا دراز كرده‌، مملكتها را متحرّك‌ ساخته‌ است‌. خداوند دربارۀ كنعان‌ امر فرموده‌ است‌ تا قلعه‌هایش‌ را خراب‌ نمایند.",
			"12": "<span>12 </span> &nbsp;و گفته‌ است‌: ای‌ دوشیزه‌ ستم‌ رسیده‌ و ای‌ دختر صیدون‌ دیگر مفتخر نخواهی‌ شد. برخاسته‌، به‌ كتّیم‌ بگذر؛ امّا در آنجا نیز راحت‌ برای‌ تو نخواهد بود.",
			"13": "<span>13 </span> &nbsp;اینك‌ زمین‌ كلدانیان‌ كه‌ قومی‌ نبودند و آشور آن‌ را به‌ جهت‌ صحرانشینان‌ بنیاد نهاد. ایشان‌ منجنیقهای‌ خود را افراشته‌، قصرهای‌ آن‌ را منهدم‌ و آن‌ را به‌ خرابی‌ مبدّل‌ خواهند ساخت‌.",
			"14": "<span>14 </span> &nbsp;ای‌ كشتیهای‌ ترشیش‌ وِلْوِلَه‌ نمایید زیرا كه‌ قلعه‌ شما خراب‌ شده‌ است‌.",
			"15": "<span>15 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ صور، هفتاد سال‌ مثل‌ ایام‌ یك‌ پادشاه‌، فراموش‌ خواهد شد؛ و بعد از انقضای‌ هفتاد سال‌ برای‌ صور مثل‌ سرود زانیه‌ خواهد بود.",
			"16": "<span>16 </span> &nbsp;ای‌ زانیه‌ فراموش‌ شده‌ بربط‌ را گرفته‌، در شهر گردش‌ نما. خوش‌ بنواز و سرودهای‌ بسیار بخوان‌ تا به‌ یاد آورده‌ شوی‌.",
			"17": "<span>17 </span> &nbsp;و بعد از انقضای‌ هفتاد سال‌ واقع‌ می‌شود كه‌ خداوند از صور تفقّد خواهد نمود و به‌ اجرت‌ خویش‌ برگشته‌ با جمیع‌ ممالك‌ جهان‌ كه‌ بر روی‌ زمین‌ است‌ زنا خواهد نمود.",
			"18": "<span>18 </span> &nbsp;و تجارت‌ و اجرت‌ آن‌ برای‌ خداوند وقف‌ شده‌ ذخیره‌ واندوخته‌ نخواهد شد بلكه‌ تجارتش‌ برای‌ مقرّبان‌ درگاه‌ خداوند خواهد بود تا به‌ سیری‌ بخورند و لباس‌ فاخر بپوشند."
		},
		"24": {
			"1": "<span>1 </span> &nbsp;اینك‌ خداوند زمین‌ را خالی‌ و ویران می‌كند، و آن‌ را واژگون‌ ساخته‌، ساكنانش‌ را پراكنده‌ می‌سازد.",
			"2": "<span>2 </span> &nbsp;و مَثَل‌ قوم‌، مَثَل‌ كاهن‌ و مَثَل‌ بنده‌، مَثَل‌ آقایش‌ و مَثَل‌ كنیز، مَثَل‌ خاتونش‌ و مَثَل‌ مشتری‌، مَثَل‌ فروشنده‌ و مَثَل‌ قرض‌ دهنده‌، مَثَل‌ قرض‌ گیرنده‌ و مَثَل‌ سودخوار، مَثَل‌ سود دهنده‌ خواهد بود.",
			"3": "<span>3 </span> &nbsp;و زمین‌ بالكّل‌ خالی‌ و بالكّل‌ غارت‌ خواهد شد زیرا خداوند این‌ سخن‌ را گفته‌ است‌.",
			"4": "<span>4 </span> &nbsp;زمین‌ ماتم‌ می‌كند و پژمرده‌ می‌شود. ربع‌ مسكون‌ كاهیده‌ و پژمرده‌ می‌گردد، شریفان‌ اهل‌ زمین‌ كاهیده‌ می‌شوند.",
			"5": "<span>5 </span> &nbsp;زمین‌ زیر ساكنانش‌ ملوّث‌ می‌شود زیرا كه‌ از شرایع‌ تجاوز نموده‌ و فرایض‌ را تبدیل‌ كرده‌ و عهد جاودانی‌ را شكسته‌اند.",
			"6": "<span>6 </span> &nbsp;بنابراین‌ لعنت‌، جهان‌ را فانی‌ كرده‌ است‌ و ساكنانش‌ سزا یافته‌اند. لهذا ساكنان‌ زمین‌ سوخته‌ شده‌اند و مردمان‌، بسیار كم‌ باقی‌ مانده‌اند.",
			"7": "<span>7 </span> &nbsp;شیره‌ انگور ماتم‌ می‌گیرد و مو كاهیده‌ می‌گردد و تمامی‌ شاددلان‌ آه‌ می‌كشند.",
			"8": "<span>8 </span> &nbsp;شادمانی‌ دفّها تلف‌ شده‌، آواز عشرت‌كنندگان‌ باطل‌ و شادمانی‌ بربطها ساكت‌ خواهد شد.",
			"9": "<span>9 </span> &nbsp;شراب‌ را با سرودها نخواهند آشامید و مسكرات‌ برای‌ نوشندگانش‌ تلخ‌ خواهد شد.",
			"10": "<span>10 </span> &nbsp;قریه‌ خرابه‌ منهدم‌ می‌شود و هر خانه‌ بسته‌ می‌گردد كه‌ كسی‌ داخل‌ آن‌ نتواند شد.",
			"11": "<span>11 </span> &nbsp;غوغایی‌ برای‌ شراب‌ در كوچه‌ها است‌. هرگونه‌ شادمانی‌ تاریك‌ گردیده‌ و سرور زمین‌ رفع‌ شده‌ است‌.",
			"12": "<span>12 </span> &nbsp;ویرانی‌ در شهر باقی‌ است‌ و دروازه‌هایش‌ به‌ هلاكت‌ خرد شده‌ است‌.",
			"13": "<span>13 </span> &nbsp;زیراكه‌ در وسط‌ زمین‌ در میان‌ قوم‌هایش‌ چنین‌ خواهد شد مثل‌ تكانیدن‌ زیتون‌ و مانند خوشه‌هایی‌ كه‌ بعد از چیدن‌ انگور باقی‌ می‌ماند.",
			"14": "<span>14 </span> &nbsp;اینان‌ آواز خود را بلند كرده‌، ترنّم‌ خواهند نمود و دربارۀ كبریایی‌ خداوند از دریا صدا خواهند زد.",
			"15": "<span>15 </span> &nbsp;از این‌جهت‌ خداوند را در بلاد مشرق‌ و نام‌ یهوه‌ خدای‌ اسرائیل‌ را در جزیره‌های‌ دریا تمجید نمایید.",
			"16": "<span>16 </span> &nbsp;از كرانهای‌ زمین‌ سرودها را شنیدیم‌ كه‌ عادلان‌ را جلال‌ باد. امّا گفتم‌: وا حسرتا، وا حسرتا، وای‌ بر من‌! خیانت‌كاران‌ خیانت‌ ورزیده‌، خیانت‌كاران‌ به‌ شدّت‌ خیانت‌ ورزیده‌اند.",
			"17": "<span>17 </span> &nbsp;ای‌ ساكن‌ زمین‌ ترس‌ و حفره‌ و دام‌ بر تو است‌.",
			"18": "<span>18 </span> &nbsp;و واقع‌ خواهد شد كه‌ هركه‌ از آواز ترس‌ بگریزد به‌ حفره‌ خواهد افتاد و هر كه‌ از میان‌ حفره‌ برآید گرفتار دام‌ خواهد شد زیرا كه‌ روزنه‌های‌ علیین‌ باز شده‌ و اساسهای‌ زمین‌ متزلزل‌ می‌باشد.",
			"19": "<span>19 </span> &nbsp;زمین‌ بالكّل‌ منكسر شده‌. زمین‌ تماماً از هم‌ پاشیده‌ و زمین‌ به‌ شدّت‌ متحرّك‌ گشته‌ است‌.",
			"20": "<span>20 </span> &nbsp;زمین‌ مثل‌ مستان‌ افتان‌ و خیزان‌ است‌ و مثل‌ سایه‌بان‌ به‌ چپ‌ و راست‌ متحرّك‌ و گناهش‌ بر آن‌ سنگین‌ است‌. پس‌ افتاده‌ است‌ كه‌ بار دیگر نخواهد برخاست‌.",
			"21": "<span>21 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ خداوند گروه‌ شریفان‌ را بر مكان‌ بلند ایشان‌ و پادشاهان‌ زمیـن‌ را بر زمیـن‌ سـزا خواهد داد.",
			"22": "<span>22 </span> &nbsp;و ایشان‌ مثل‌ اسیران‌ در چاه‌ جمع‌ خواهنـد شـد و در زندان‌ بسته‌ خواهند گردید و بعد از روزهای‌ بسیار، ایشان‌ طلبیده‌ خواهند شد.",
			"23": "<span>23 </span> &nbsp;و مـاه‌ خجـل‌ و آفتاب‌ رسوا خواهـد گشت‌ زیرا كه‌ یهـوه‌ صبایوت‌ در كوه‌ صهیون‌ و در اورشلیم‌ و به‌ حضور مشایخ‌ خویش‌، با جلال‌ سلطنت‌ خواهد نمود."
		},
		"25": {
			"1": "<span>1 </span> &nbsp;ای‌ یهوه‌، تو خدای‌ من‌ هستی‌؛ پس‌ تو را تسبیح‌ می‌خوانم‌ و نام‌ تو را حمد می‌گویم‌، زیرا كارهای‌ عجیب‌ كرده‌ای‌ و تقدیرهای‌ قدیم‌ تو امانت‌ و راستی‌ است‌.",
			"2": "<span>2 </span> &nbsp;چونكه‌ شهری‌ را توده‌ و قریه‌ حصین‌ را خرابه‌ گردانیده‌ای‌ و قصر غریبان‌ را كه‌ شهر نباشد و هرگز بنا نگردد.",
			"3": "<span>3 </span> &nbsp;بنابراین‌ قوم‌ عظیم‌، تو را تمجید می‌نمایند و قریه‌ امّت‌های‌ ستم‌ پیشه‌ از تو خواهند ترسید.",
			"4": "<span>4 </span> &nbsp;چونكه‌ برای‌ فقیران‌ قلعه‌ و به‌ جهت‌ مسكینان‌ در حین‌ تنگی‌ ایشان‌ قلعه‌ بودی‌ و ملجا از طوفان‌ و سایه‌ از گرمی‌، هنگامی‌ كه‌ نفخه‌ ستمكاران‌ مثل‌ طوفان‌ بر دیوار می‌بود.",
			"5": "<span>5 </span> &nbsp;و غوغای‌ غریبان‌ را مثل‌ گرمی‌ در جای‌ خشك‌ فرود خواهی‌ آورد و سرود ستمكاران‌ مثل‌ گرمی‌ از سایه‌ ابر پست‌ خواهد شد.",
			"6": "<span>6 </span> &nbsp;و یهوه‌ صبایوت‌ در این‌ كوه‌ برای‌ همه‌ قوم‌ها ضیافتی‌ از لذایذ برپا خواهد نمود، یعنی‌ ضیافتی‌ از شرابهای‌ كهنه‌ از لذایذ پر مغز و از شرابهای‌ كهنه‌ مصفّا.",
			"7": "<span>7 </span> &nbsp;و در این‌ كوه‌ روپوشی‌ را كه‌ بر تمامی‌ قوم‌ها گسترده‌ است‌ و ستری‌ را كه‌ جمیع‌ امّت‌ها را می‌پوشاند تلف‌ خواهد كرد.",
			"8": "<span>8 </span> &nbsp;و موت‌ را تا ابدالا´باد نابود خواهد ساخت‌ و خداوند یهوه‌ اشكها را از هر چهره‌ پاك‌ خواهد نمود و عار قوم‌ خویش‌ را از روی‌ تمامی‌ زمین‌ رفع‌ خواهد كرد، زیرا خداوند گفته‌ است‌.",
			"9": "<span>9 </span> &nbsp;و در آن‌ روز خواهند گفت‌: «اینك‌ این‌ خدای‌ ما است‌ كه‌ منتظر او بوده‌ایم‌ و ما را نجات‌ خواهد داد. این‌ خداوند است‌ كه‌ منتظر او بوده‌ایم‌ پس‌ ازنجات‌ او مسرور و شادمان‌ خواهیم‌ شد.»",
			"10": "<span>10 </span> &nbsp;زیرا كه‌ دست‌ خداوند بر این‌ كوه‌ قرار خواهد گرفت‌ و موآب‌ در مكان‌ خود پایمال‌ خواهد شد چنانكه‌ كاه‌ در آب‌ مزبله‌ پایمال‌ می‌شود.",
			"11": "<span>11 </span> &nbsp;و او دستهای‌ خود را در میان‌ آن‌ خواهد گشاد مثل‌ شناوری‌ كه‌ به‌ جهت‌ شنا كردن‌ دستهای‌ خود را می‌گشاید و غرور او را با حیله‌های‌ دستهایش‌ پست‌ خواهد گردانید.",
			"12": "<span>12 </span> &nbsp;و قلعه‌ بلند حصارهایت‌ را خم‌ كرده‌، بزیر خواهد افكند و بر زمین‌ با غبار یكسان‌ خواهد ساخت‌."
		},
		"26": {
			"1": "<span>1 </span> &nbsp;در آن‌ روز، این‌ سرود در زمین‌ یهودا سراییده‌ خواهد شد؛ ما را شهری‌ قوی‌ است‌ كه‌ دیوارها و حصار آن‌ نجات‌ است‌.",
			"2": "<span>2 </span> &nbsp;دروازه‌ها را بگشایید تا امّت‌ عادل‌ كه‌ امانت‌ را نگاه‌ می‌دارند داخل‌ شوند.",
			"3": "<span>3 </span> &nbsp;دل‌ ثابت‌ را در سلامتی‌ كامل‌ نگاه‌ خواهی‌ داشت‌، زیرا كه‌ بر تو توكّل‌ دارد.",
			"4": "<span>4 </span> &nbsp;بر خداوند تا به‌ ابد توكّل‌ نمایید، چونكه‌ در یاه‌ یهوه‌ صخره‌ جاودانی‌ است‌.",
			"5": "<span>5 </span> &nbsp;زیرا آنانی‌ را كه‌ بر بلندیها ساكنند، فرود می‌آورد؛ و شهر رفیع‌ را به‌ زیر می‌اندازد. آن‌ را به‌ زمین‌ انداخته‌، با خاك‌ یكسان‌ می‌سازد.",
			"6": "<span>6 </span> &nbsp;پایها آن‌ را پایمال‌ خواهد كرد، یعنی‌ پایهای‌ فقیران‌ و قدمهای‌ مسكینان‌.",
			"7": "<span>7 </span> &nbsp;طریق‌ عادلان‌ استقامت‌ است‌. ای‌ تو كه‌ مستقیم‌ هستی‌، طریق‌ عادلان‌ را هموار خواهی‌ ساخت‌.",
			"8": "<span>8 </span> &nbsp;پس‌ ای‌ خداوند در طریق‌ داوریهای‌ تو انتظار تو را كشیده‌ایم‌. و جان‌ ما به‌ اسم‌ تو و ذكر تومشتاق‌ است‌.",
			"9": "<span>9 </span> &nbsp;شبانگاه‌ به‌ جان‌ خود مشتاق‌ تو هستم‌، و بامدادان‌ به‌ روح‌ خود در اندرونم‌ تو را می‌طلبم‌، زیرا هنگامی‌ كه‌ داوریهای‌ تو بر زمین‌ آید، سكنه‌ ربع‌ مسكون‌ عدالت‌ را خواهند آموخت‌.",
			"10": "<span>10 </span> &nbsp;هرچند بر شریر ترحّم‌ شود عدالت‌ را نخواهد آموخت‌. در زمین‌ راستان‌ شرارت‌ می‌ورزد و جلال‌ خداوند را مشاهده‌ نمی‌نماید.",
			"11": "<span>11 </span> &nbsp;ای‌ خداوند دست‌ تو برافراشته‌ شده‌ است‌ امّا نمی‌بینند. لیكن‌ چون‌ غیرت‌ تو را برای‌ قوم‌ ملاحظه‌ كنند، خجل‌ خواهند شد. و آتش‌ نیز دشمنانت‌ را فرو خواهد برد.",
			"12": "<span>12 </span> &nbsp;ای‌ خداوند سلامتی‌ را برای‌ ما تعیین‌ خواهی‌ نمود. زیرا كه‌ تمام‌ كارهای‌ ما را نیز برای‌ ما به‌ عمل‌ آورده‌ای‌.",
			"13": "<span>13 </span> &nbsp;ای‌ یهوه‌ خدای‌ ما آقایان‌ غیر از تو بر ما استیلا داشتند. امّا به‌ تو فقط‌ اسم‌ تو را ذكر خواهیم‌ كرد.",
			"14": "<span>14 </span> &nbsp;ایشان‌ مردند و زنده‌ نخواهند شد. خیالها گردیدند و نخواهند برخاست‌. بنابراین‌ ایشان‌ را سزا داده‌، هلاك‌ ساختی‌ و تمام‌ ذكر ایشان‌ را محو نمودی‌.",
			"15": "<span>15 </span> &nbsp;قوم‌ را افزودی‌ ای‌ خداوند ، قوم‌ را مزید ساخته‌، خویشتن‌ را جلال‌ دادی‌. و تمامی‌ حدود زمین‌ را وسیع‌ گردانیدی‌.",
			"16": "<span>16 </span> &nbsp;ای‌ خداوند ایشان‌ در حین‌ تنگی‌، تو را خواهند طلبید. و چون‌ ایشان‌ را تأدیب‌ نمایی‌ دعاهای‌ خفیه‌ خواهند ریخت‌.",
			"17": "<span>17 </span> &nbsp;مثل‌ زن‌ حامله‌ای‌ كه‌ نزدیك‌ زاییدن‌ باشد و درد او را گرفته‌، از آلام‌ خود فریاد بكند، همچنین‌ ما نیز ای‌ خداوند در حضور تو هستیم.",
			"18": "<span>18 </span> &nbsp;حامله‌ شده‌، درد زه‌ ما را گرفت‌ و باد را زاییدیم‌. و در زمین‌ هیچ‌ نجات‌ به‌ ظهور نیاوردیم‌ و ساكنان‌ ربع‌ مسكون‌نیفتادند.",
			"19": "<span>19 </span> &nbsp;مردگان‌ تو زنده‌ خواهند شد و جسدهای‌ من‌ خواهند برخاست‌. ای‌ شما كه‌ در خاك‌ ساكنید بیدار شده‌، ترنّم‌ نمایید! زیرا كه‌ شبنم‌ تو شبنم‌ نباتات‌ است‌. و زمین‌ مردگان‌ خود را بیرون‌ خواهد افكند.",
			"20": "<span>20 </span> &nbsp;ای‌ قوم‌ من‌ بیایید به‌ حجره‌های‌ خویش‌ داخل‌ شوید و درهای‌ خود را در عقب‌ خویش‌ ببندید. خویشتن‌ را اندك‌ لحظه‌ای‌ پنهان‌ كنید تا غضب‌ بگذرد.",
			"21": "<span>21 </span> &nbsp;زیرا اینك‌ خداوند از مكان‌ خود بیرون‌ می‌آید تا سزای‌ گناهان‌ ساكنان‌ زمین‌ را به‌ ایشان‌ برساند. پس‌ زمین‌ خونهای‌ خود را مكشوف‌ خواهد ساخت‌ و كشتگان‌ خویش‌ را دیگر پنهان‌ نخواهد نمود."
		},
		"27": {
			"1": "<span>1 </span> &nbsp;در آن‌ روز خداوند به‌ شمشیرِ سختِ عظیمِ محكمِ خود آن‌ مار تیز رو لِوْیاتان‌ را و آن‌ مار پیچیده‌ لِوْیاتان‌ را سزا خواهد داد و آن‌ اژدها را كه‌ در دریا است‌ خواهد كشت‌.",
			"2": "<span>2 </span> &nbsp;در آن‌ روز برای‌ آن‌ تاكستان‌ شراب‌ بسرایید.",
			"3": "<span>3 </span> &nbsp;من‌ كه‌ یهوه‌ هستم‌ آن‌ را نگاه‌ می‌دارم‌ و هر دقیقه‌ آن‌ را آبیاری‌ می‌نمایم‌. شب‌ و روز آن‌ را نگاهبانی‌ می‌نمایم‌ كه‌ مبادا احدی‌ به‌ آن‌ ضرر برساند.",
			"4": "<span>4 </span> &nbsp;خشم‌ ندارم‌. كاش‌ كه‌ خس‌ و خار با من‌ به‌ جنگ‌ می‌آمدند تا بر آنها هجوم‌ آورده‌، آنها را با هم‌ می‌سوزانیدم‌.",
			"5": "<span>5 </span> &nbsp;یا به‌ قوّت‌ من‌ متمسّك‌ می‌شد تا با من‌ صلح‌ بكند و با من‌ صلح‌ می‌نمود.",
			"6": "<span>6 </span> &nbsp;در ایام‌ آینده‌، یعقوب‌ ریشه‌ خواهد زد و اسرائیل‌ غنچه‌ و شكوفه‌ خواهد آورد. و ایشان‌ روی‌ ربع‌ مسكون‌ را از میوه‌ پر خواهند ساخت‌.",
			"7": "<span>7 </span> &nbsp;آیا او را زد بطوری‌ كه‌ دیگران‌ او را زدند؟ یا كشته‌ شد بطوری‌ كه‌ مقتولان‌ وی‌ كشته‌ شدند؟",
			"8": "<span>8 </span> &nbsp;چون‌ او را دور ساختی‌ به‌ اندازه‌ با وی‌ معارضه‌ نمودی‌. با باد سخت‌ خویش‌ او را در روز باد شرقی‌ زایل‌ ساختی‌.",
			"9": "<span>9 </span> &nbsp;بنابراین‌ گناه‌ یعقوب‌ از این‌ كفّاره‌ شده‌ و رفع‌ گناه‌ او تمامی‌ نتیجه‌ آن‌ است‌. چون‌ تمامی‌ سنگهای‌ مذبح‌ را مثل‌ سنگهای‌ آهك‌ نرم‌ شده‌ می‌گرداند، آنگاه‌ اشیریم‌ و بتهای‌ آفتاب‌ دیگر برپا نخواهد شد.",
			"10": "<span>10 </span> &nbsp;زیرا كه‌ آن‌ شهر حصین‌ منفرد خواهد شد و آن‌ مسكن‌، مهجور و مثل‌ بیابان‌ واگذاشته‌ خواهد شد. در آنجا گوساله‌ها خواهند چرید و در آن‌ خوابیده‌، شاخه‌هایش‌ را تلف‌ خواهند كرد.",
			"11": "<span>11 </span> &nbsp;چون‌ شاخه‌هایش‌ خشك‌ شود شكسته‌ خواهد شد. پس‌ زنان‌ آمده‌، آنها را خواهند سوزانید، زیرا كه‌ ایشان‌ قوم‌ بیفهم‌ هستند. لهذا آفریننده‌ ایشان‌ بر ایشان‌ ترحّم‌ نخواهد نمود و خالق‌ ایشان‌ بر ایشان‌ شفقت‌ نخواهد كرد.",
			"12": "<span>12 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ خداوند از مسیل‌ نهر (فرات‌) تا وادی‌ مصر غلّه‌ را خواهد كوبید. و شما ای‌ بنی‌اسرائیل‌ یكی‌ یكی‌ جمع‌ كرده‌ خواهید شد.",
			"13": "<span>13 </span> &nbsp;و در آن‌ روز واقع‌ خواهد شد كه‌ كَرِنّای‌ بزرگ‌ نواخته‌ خواهد شد و گم‌شدگان‌ زمین‌ آشور و رانده‌ شدگان‌ زمین‌ مصر خواهند آمد. و خداوند را در كوه‌ مقدّس‌ یعنی‌ در اورشلیم‌ عبادت‌ خواهند نمود."
		},
		"28": {
			"1": "<span>1 </span> &nbsp;وای‌ بر تاج‌ تكبّر میگساران‌ افرایم‌ و برگل‌ پژمرده‌ زیبایی‌ جلال‌ وی‌، كه‌ برسر وادی‌ بارور مغلوبان‌ شراب‌ است‌.",
			"2": "<span>2 </span> &nbsp;اینك‌ خداوند كسی‌ زورآور و توانا دارد كه‌ مثل‌ تگرگ‌ شدید و طوفان‌ مهلك‌ و مانند سیل‌ آبهای‌ زورآور سرشار، آن‌ را به‌ زور بر زمین‌ خواهد انداخت‌.",
			"3": "<span>3 </span> &nbsp;و تاج‌ تكبّر میگساران‌ افرایم‌ زیر پایها پایمال‌ خواهد شد.",
			"4": "<span>4 </span> &nbsp;و گل‌ پژمرده‌ زیبایی‌ جلال‌ وی‌ كه‌ بر سر وادی‌ بارور است‌، مثل‌ نوبر انجیرها قبل‌ از تابستان‌ خواهد بود كه‌ چون‌ بیننده‌ آن‌ را بیند وقتی‌ كه‌ هنوز در دستش‌ باشد، آن‌ را فرو می‌برد.",
			"5": "<span>5 </span> &nbsp;و در آن‌ روز یهوه‌ صبایوت‌ به‌ جهت‌ بقیه‌ قوم‌ خویش‌ تاج‌ جلال‌ و افسر جمال‌ خواهد بود.",
			"6": "<span>6 </span> &nbsp;و روح‌ انصاف‌ برای‌ آنانی‌ كه‌ به‌ داوری‌ می‌نشینند و قوّت‌ برای‌ آنانی‌ كه‌ جنگ‌ را به‌ دروازه‌ها برمی‌گردانند (خواهد بود).",
			"7": "<span>7 </span> &nbsp;ولكن‌ اینان‌ نیز از شراب‌ گمراه‌ شده‌اند و از مسكرات‌ سرگشته‌ گردیده‌اند. هم‌ كاهن‌ و هم‌ نبی‌ از مسكرات‌ گمراه‌ شده‌اند و از شراب‌ بلعیده‌ گردیده‌اند. از مسكرات‌ سرگشته‌ شده‌اند و در رؤیا گمراه‌ گردیده‌اند و در داوری‌ مبهوت‌ گشته‌اند.",
			"8": "<span>8 </span> &nbsp;زیرا كه‌ همه‌ سفره‌ها از قی‌ و نجاست‌ پر گردیده‌ و جایی‌ نمانده‌ است‌.",
			"9": "<span>9 </span> &nbsp;كدام‌ را معرفت‌ خواهد آموخت‌ و اخبار را به‌ كه‌ خواهد فهمانید؟ آیا نه‌ آنانی‌ را كه‌ از شیر باز داشته‌ و از پستانها گرفته‌ شده‌اند؟",
			"10": "<span>10 </span> &nbsp;زیرا كه‌ حكم‌ بر حكم‌ و حكم‌ بر حكم‌، قانون‌ بر قانون‌ و قانون‌ بر قانون‌ اینجا اندكی‌ و آنجا اندكی‌ خواهد بود.",
			"11": "<span>11 </span> &nbsp;زیرا كه‌ با لبهای‌ الكن‌ و زبان‌ غریب‌ با این‌ قوم‌ تكلّم‌ خواهد نمود.",
			"12": "<span>12 </span> &nbsp;كه‌ به‌ ایشان‌ گفت‌: «راحت‌ همین‌ است‌. پس‌ خسته‌ شدگان‌ رامستریح‌ سازید و آرامی‌ همین‌ است‌.» امّا نخواستند كه‌ بشنوند.",
			"13": "<span>13 </span> &nbsp;و كلام‌ خداوند برای‌ ایشان‌ حكم‌ بر حكم‌ و حكم‌ برحكم‌، قانون‌ بر قانون‌ و قانون‌ بر قانون‌، اینجا اندكی‌ و آنجا اندكی‌ خواهد بود تا بروند و به‌ پشت‌ افتاده‌، منكسر گردند و به‌ دام‌ افتاده‌، گرفتار شوند.",
			"14": "<span>14 </span> &nbsp;بنابراین‌، ای‌ مردان‌ استهزا كننده‌ و ای‌ حاكمان‌ این‌ قوم‌ كه‌ در اورشلیم‌اند كلام‌ خداوند را بشنوید.",
			"15": "<span>15 </span> &nbsp;از آنجا كه‌ گفته‌اید با موت‌ عهد بسته‌ایم‌ و با هاویه‌ همداستان‌ شده‌ایم‌، پس‌ چون‌ تازیانه‌ مهلك‌ بگذرد به‌ ما نخواهد رسید زیرا كه‌ دروغها را ملجای‌ خود نمودیم‌ و خویشتن‌ را زیر مكر مستور ساختیم‌.",
			"16": "<span>16 </span> &nbsp;بنابراین‌ خداوند یهوه‌ چنین‌ می‌گوید: «اینك‌ در صهیون‌ سنگ‌ بنیادی‌ نهادم‌ یعنی‌ سنگ‌ آزموده‌ و سنگ‌ زاویه‌ای‌ گرانبها و اساس‌ محكم‌ پس‌ هر كه‌ ایمان‌ آورد تعجیل‌ نخواهد نمود.",
			"17": "<span>17 </span> &nbsp;و انصاف‌ را ریسمان‌ می‌گردانم‌ و عدالت‌ را ترازو و تگرگ‌ ملجای‌ دروغ‌ را خواهد رُفْت‌ و آبها ستر را خواهد برد.",
			"18": "<span>18 </span> &nbsp;و عهد شما با موت‌ باطل‌ خواهد شد و میثاق‌ شما با هاویه‌ ثابت‌ نخواهد ماند و چون‌ تازیانه‌ شدید بگذرد شما از آن‌ پایمال‌ خواهید شد.",
			"19": "<span>19 </span> &nbsp;هر وقت‌ كه‌ بگذرد شما را گرفتار خواهد ساخت‌ زیرا كه‌ هر بامداد هم‌ در روز و هم‌ در شب‌ خواهد گذشت‌ و فهمیدن‌ اخبار باعث‌ هیبت‌ محض‌ خواهد شد.»",
			"20": "<span>20 </span> &nbsp;زیرا كه‌ بستر كوتاه‌تر است‌ از آنكه‌ كسی‌ بر آن‌ دراز شود و لحاف‌ تنگ‌تر است‌ از آنكه‌ كسی‌ خویشتن‌ را بپوشاند.",
			"21": "<span>21 </span> &nbsp;زیرا خداوند چنانكه‌ در كوه‌ فراصیم‌ (كرد) خواهد برخاست‌ و چنانكه‌ در وادی جبعون‌ (نمود) خشمناك‌ خواهد شد، تا كار خود یعنی‌ كار عجیب‌ خود را بجا آورد و عمل‌ خویش‌ یعنی‌ عمل‌ غریب‌ خویش‌ را به‌ انجام‌ رساند.",
			"22": "<span>22 </span> &nbsp;پس‌ الا´ن‌ استهزا منمایید مبادا بندهای‌ شما محكم‌ گردد، زیرا هلاكت‌ و تقدیری‌ را كه‌ از جانب‌ خداوند یهوه‌ صبایوت‌ بر تمامی‌ زمین‌ می‌آید شنیده‌ام‌.",
			"23": "<span>23 </span> &nbsp;گوش‌ گیرید و آواز مرا بشنوید و متوجّه‌ شده‌، كلام‌ مرا استماع‌ نمایید.",
			"24": "<span>24 </span> &nbsp;آیا برزگر، همه‌ روز به‌ جهت‌ تخم‌ پاشیدن‌ شیار می‌كند و آیا همه‌ وقت‌ زمین‌ خود را می‌شكافد و هموار می‌نماید؟",
			"25": "<span>25 </span> &nbsp;آیا بعد از آنكه‌ رویش‌ را هموار كرد، گشنیز را نمی‌پاشد و زیره‌ را نمی‌افشاند و گندم‌ را در شیارها و جو را در جای‌ معین‌ و ذرّت‌ را در حدودش‌ نمی‌گذارد؟",
			"26": "<span>26 </span> &nbsp;زیرا كه‌ خدایش‌ او را به‌ راستی‌ می‌آموزد و او را تعلیم‌ می‌دهد.",
			"27": "<span>27 </span> &nbsp;چونكه‌ گشنیز با گردون‌ تیز كوبیده‌ نمی‌شود و چرخ‌ ارابه‌ بر زیره‌ گردانیده‌ نمی‌گردد، بلكه‌ گشنیز به‌ عصا و زیره‌ به‌ چوب‌ تكانیده‌ می‌شود.",
			"28": "<span>28 </span> &nbsp;گندم‌ آرد می‌شود زیرا كه‌ آن‌ را همیشه‌ خرمن‌كوبی‌ نمی‌كند و هرچند چرخ‌ ارابه‌ و اسبان‌ خود را بر آن‌ بگرداند، آن‌ را خُرد نمی‌كند.",
			"29": "<span>29 </span> &nbsp;این‌ نیز از جانب‌ یهوه‌ صبایوت‌ كه‌ عجیب‌الّرای‌ و عظیم‌الحكمت‌ است‌، صادر می‌گردد."
		},
		"29": {
			"1": "<span>1 </span> &nbsp;وای‌ بر اَریئیل‌! وای‌ بر اَریئیل‌! شهری كه‌ داود در آن‌ خیمه‌ زد. سال‌ بر سال‌ مزید كنید و عیدها دور زنند.",
			"2": "<span>2 </span> &nbsp;و من‌ اریئیل‌ را به‌ تنگی‌ خواهم‌ انداخت‌ و ماتم‌ و نوحه‌گری‌ خواهدبود و آن‌ برای‌ من‌ مثل‌ اریئیل‌ خواهد بود.",
			"3": "<span>3 </span> &nbsp;و بر تو به‌ هر طرف‌ اردو زده‌، تو را به‌ باره‌ها محاصره‌ خواهم‌ نمود و منجنیقها بر تو خواهم‌ افراشت‌.",
			"4": "<span>4 </span> &nbsp;و به‌ زیر افكنده‌ شده‌، از زمین‌ تكلّم‌ خواهی‌ نمود و كلام‌ تو از میان‌ غبار پست‌ خواهد گردید و آواز تو از زمین‌ مثل‌ آواز جنّ خواهد بود و زبان‌ تو از میان‌ غبار زمزم‌ خواهد كرد.",
			"5": "<span>5 </span> &nbsp;امّا گروه‌ دشمنانت‌ مثل‌ گرد نرم‌ خواهند شد و گروه‌ ستم‌ كیشان‌ مانند كاه‌ كه‌ می‌گذرد. و این‌ بغتةً در لحظه‌ای‌ واقع‌ خواهد شد.",
			"6": "<span>6 </span> &nbsp;و از جانب‌ یهوه‌ صبایوت‌ با رعد و زلزله‌ و صوت‌ عظیم‌ و گردباد و طوفان‌ و شعله‌ آتش‌ سوزنده‌ از تو پرسش‌ خواهد شد.",
			"7": "<span>7 </span> &nbsp;و جمعیت‌ تمام‌ امّت‌هایی‌ كه‌ با اریئیل‌ جنگ‌ می‌كنند، یعنی‌ تمامی‌ آنانی‌ كه‌ بر او و بر قلعه‌ وی‌ مقاتله‌ می‌نمایند و او را بتنگ‌ می‌آورند مثل‌ خواب‌ و رؤیای‌ شب‌ خواهند شد.",
			"8": "<span>8 </span> &nbsp;و مثل‌ شخص‌ گرسنه‌ كه‌ خواب‌ می‌بیند كه‌ می‌خورد و چون‌ بیدار شود شكم‌ او تهی‌ است‌. یا شخص‌ تشنه‌ كه‌ خواب‌ می‌بیند كه‌ آب‌ می‌نوشد و چون‌ بیدار شود اینك‌ ضعف‌ دارد و جانش‌ مشتهی‌ می‌باشد. همچنین‌ تمامی‌ جماعت‌ امّت‌هایی‌ كه‌ با كوه‌ صهیون‌ جنگ‌ می‌كنند، خواهند شد.",
			"9": "<span>9 </span> &nbsp;درنگ‌ كنید و متحیر باشید و تمتّع‌ برید و كور باشید. ایشان‌ مست‌ می‌شوند، لیكن‌ نه‌ از شراب‌ و نوان‌ می‌گردند، امّا نه‌ از مسكرات‌.",
			"10": "<span>10 </span> &nbsp;زیرا خداوند بر شما روح‌ خواب‌ سنگین‌ را عارض‌ گردانیده‌، چشمان‌ شما را بسته‌ است‌. و انبیا و رؤسای‌ شما یعنی‌ رائیان‌ را محجوب‌ كرده‌ است‌.",
			"11": "<span>11 </span> &nbsp;و تمامی‌ رؤیا برای‌ شما مثل‌ كلام‌ تومار مختوم‌ گردیده‌ است‌ كه‌ آن‌ را به‌ كسی‌ كه‌ خواندن‌می‌داند داده‌، می‌گویند: این‌ را بخوان‌ و او می‌گوید: نمی‌توانم‌ چونكه‌ مختوم‌ است‌.",
			"12": "<span>12 </span> &nbsp;و آن‌ طومار را به‌ كسی‌ كه‌ خواندن‌ نداند داده‌، می‌گویند این‌ را بخوان‌ و او می‌گوید خواندن‌ نمی‌دانم‌.",
			"13": "<span>13 </span> &nbsp;و خداوند می‌گوید: «چونكه‌ این‌ قوم‌ از دهان‌ خود به‌ من‌ تقرّب‌ می‌جویند و به‌ لبهای‌ خویش‌ مرا تمجید می‌نمایند، امّا دل‌ خود را از من‌ دور كرده‌اند و ترس‌ ایشان‌ از من‌ وصیتی‌ است‌ كه‌ از انسان‌ آموخته‌اند؛",
			"14": "<span>14 </span> &nbsp;بنابراین‌ اینك‌ من‌ بار دیگر با این‌ قوم‌ عمل‌ عجیب‌ و غریب‌ بجا خواهم‌ آورد و حكمت‌ حكیمان‌ ایشان‌ باطل‌ و فهم‌ فهیمان‌ ایشان‌ مستور خواهد شد.»",
			"15": "<span>15 </span> &nbsp;وای‌ بر آنانی‌ كه‌ مشورت‌ خود را از خداوند بسیار عمیق‌ پنهان‌ می‌كنند و اعمال‌ ایشان‌ در تاریكی‌ می‌باشد و می‌گویند: «كیست‌ كه‌ مارا ببیند و كیست‌ كه‌ ما را بشناسد؟»",
			"16": "<span>16 </span> &nbsp;ای‌ زیر و زبر كنندگان‌ هرچیز! آیا كوزه‌گر مثل‌ گل‌ محسوب‌ شود یا مصنوع‌ دربارۀ صانع‌ خود گوید مرا نساخته‌ است‌ و یا تصویر دربارۀ مصوّرش‌ گوید كه‌ فهم‌ ندارد؟",
			"17": "<span>17 </span> &nbsp;آیا در اندك‌ زمانی‌ واقع‌ نخواهد شد كه‌ لبنان‌ به‌ بوستان‌ مبدّل‌ گردد و بوستان‌ به‌ جنگل‌ محسوب‌ شود؟",
			"18": "<span>18 </span> &nbsp;و در آن‌ روز كّران‌ كلام‌ كتاب‌ را خواهند شنید و چشمان‌ كوران‌ از میان‌ ظلمت‌ و تاریكی‌ خواهد دید.",
			"19": "<span>19 </span> &nbsp;و حلیمان‌ شادمانی‌ خود را در خداوند مزید خواهند كرد و مسكینان‌ مردمان‌ در قدّوس‌ اسرائیل‌ وجد خواهند نمود.",
			"20": "<span>20 </span> &nbsp;زیرا كه‌ ستمگران‌ نابود و استهزاكنندگان‌ معدوم‌ خواهند شد و پیروان‌ شرارت‌ منقطع‌ خواهند گردید.",
			"21": "<span>21 </span> &nbsp;كه‌ انسان‌ را به‌ سخنی‌ مجرم‌ می‌سازند و برای‌ كسی‌ كه‌ درمحكمه‌ حكم‌ می‌كند دام‌ می‌گسترانند و عادل‌ را به‌ بطالت‌ منحرف‌ می‌سازند.",
			"22": "<span>22 </span> &nbsp;بنابراین‌ خداوند كه‌ ابراهیم‌ را فدیه‌ داده‌ است‌ دربارۀ خاندان‌ یعقوب‌ چنین‌ می‌گوید كه‌ از این‌ به‌ بعد یعقوب‌ خجل‌ نخواهد شد و رنگ‌ چهره‌اش‌ دیگر نخواهد پرید.",
			"23": "<span>23 </span> &nbsp;بلكه‌ چون‌ فرزندان‌ خود را كه‌ عمل‌ دست‌ من‌ می‌باشند، در میان‌ خویش‌ بیند،آنگاه‌ ایشان‌ اسم‌ مرا تقدیس‌ خواهند نمود و قدّوس‌ یعقوب‌ را تقدیس‌ خواهند كرد و از خدای‌ اسرائیل‌ خواهند ترسید.",
			"24": "<span>24 </span> &nbsp;و آنانی‌ كه‌ روح‌ گمراهی‌ دارند فهیم‌ خواهند شد و متمّردان‌ تعلیم‌ را خواهند آموخت‌."
		},
		"30": {
			"1": "<span>1 </span> &nbsp;خداوند می‌گوید كه‌ وای‌ بر پسران‌فتنه‌انگیز كه‌ مشورت‌ می‌كنند لیكن‌ نه‌ از من‌، و عهد می‌بندند لیكن‌ نه‌ از روح‌ من‌، تا گناه‌ را بر گناه‌ مزید نمایند.",
			"2": "<span>2 </span> &nbsp;كه‌ برای‌ فرود شدن‌ به‌ مصر عزیمت‌ می‌كنند امّا از دهان‌ من‌ سؤال‌ نمی‌نمایند و به‌ قوّت‌ فرعون‌ پناه‌ می‌گیرند و به‌ سایه‌ مصر اعتماد دارند.",
			"3": "<span>3 </span> &nbsp;لهذا قوّت‌ فرعون‌ خجالت‌ و اعتماد به‌ سایه‌ مصر رسوایی‌ شما خواهد بود.",
			"4": "<span>4 </span> &nbsp;زیرا كه‌ سروران‌ او در صَوعَن‌ هستند و ایلچیان‌ وی‌ به‌ حانیس‌ رسیده‌اند.",
			"5": "<span>5 </span> &nbsp;همگی‌ ایشان‌ از قومی‌ كه‌ برای‌ ایشان‌ فایده‌ ندارند، خجل‌ خواهند شد كه‌ نه‌ معاونت‌ و نه‌ منفعتی‌ بلكه‌ خجالت‌ و رسوایی‌ نیز برای‌ ایشان‌ خواهند بود.",
			"6": "<span>6 </span> &nbsp;وحی‌ دربارۀ بَهیمُوت‌ جنوبی‌: از میان‌ زمین‌ تنگ‌ و ضیق‌ كه‌ از آنجا شیر ماده‌ و اسد وافعی‌ و مار آتشین‌ پرنده‌ می‌آید، توانگری‌ خویش‌ را بر پشت‌ الاغان‌ و گنجهای‌ خود را بر كوهان‌ شتران‌ نزد قومی‌ كه‌ منفعت‌ ندارند می‌برند.",
			"7": "<span>7 </span> &nbsp;چونكه ‌اعانت‌ مصریان‌ عبث‌ و بی‌فایده‌ است‌ از این‌ جهت‌ ایشان‌ را رَهَبُالجلوس‌ نامیدم‌.",
			"8": "<span>8 </span> &nbsp;الا´ن‌ بیا و این‌ را در نزد ایشان‌ بر لوحی‌ بنویس‌ و بر طوماری‌ مرقوم‌ ساز تا برای‌ ایام‌ آینده‌ تا ابدالا´باد بماند.",
			"9": "<span>9 </span> &nbsp;زیرا كه‌ این‌ قوم‌ فتنه‌انگیز و پسران‌ دروغگو می‌باشند. پسرانی‌ كه‌ نمی‌خواهند شریعت‌ خداوند را استماع‌ نمایند.",
			"10": "<span>10 </span> &nbsp;كه‌ به‌ رائیان‌ می‌گویند: رؤیت‌ مكنید و به‌ انبیا كه‌ برای‌ ما به‌ راستی‌ نبوّت‌ ننمایید بلكه‌ سخنان‌ شیرین‌ به‌ ما گویید و به‌ مكاید نبوّت‌ كنید.",
			"11": "<span>11 </span> &nbsp;از راه‌ منحرف‌ شوید و از طریق‌ تجاوز نمایید و قدّوس‌ اسرائیل‌ را از نظر ما دور سازید.",
			"12": "<span>12 </span> &nbsp;بنابراین‌ قدّوس‌ اسرائیل‌ چنین‌ می‌گوید: «چونكه‌ شما این‌ كلام‌ را ترك‌ كردید و بر ظلم‌ و فساد اعتماد كرده‌، بر آن‌ تكیه‌ نمودید،",
			"13": "<span>13 </span> &nbsp;از این‌ جهت‌ این‌ گناه‌ برای‌ شما مثل‌ شكاف‌ نزدیك‌ به‌ افتادن‌ كه‌ در دیوار بلند پیش‌ آمده‌ باشد و خرابی‌ آن‌ در لحظه‌ای‌ بغتةًپدید آید خواهد بود.",
			"14": "<span>14 </span> &nbsp;و شكستگی‌ آن‌ مثل‌ شكستگی‌ كوزه‌ كوزه‌گر خواهد بود كه‌ بی‌محابا خُرد می‌شود، بطوری‌ كه‌ از پاره‌هایش‌ پاره‌ای‌ به‌ جهت‌ گرفتن‌ آتش‌ از آتشدان‌ یا برداشتن‌ آب‌ از حوض‌ یافت‌ نخواهد شد.»",
			"15": "<span>15 </span> &nbsp;زیرا خداوند یهوه‌ قدّوس‌ اسرائیل‌ چنین‌ می‌گوید: «به‌ انابت‌ و آرامی‌ نجات‌ می‌یافتید و قوّت‌ شما از راحت‌ و اعتماد می‌بود؛ امّا نخواستید.",
			"16": "<span>16 </span> &nbsp;و گفتید: نی‌ بلكه‌ بر اسبان‌ فرار می‌كنیم‌، لهذا فرار خواهید كرد و بر اسبان‌ تیز رو سوار می‌شویم‌؛ لهذا تعاقب‌ كنندگان‌ شما تیز رو خواهند شد.",
			"17": "<span>17 </span> &nbsp;هزار نفر از نهیب‌ یك‌ نفر فرار خواهند كرد و شما از نهیب‌ پنج‌ نفر خواهید گریخت‌ تا مثل‌ بیرق‌ بر قلّه‌ كوه‌ و عَلَم‌ بر تلّی‌ باقی‌ مانید.»",
			"18": "<span>18 </span> &nbsp;و از این‌ سبب‌ خداوند انتظار می‌كشد تابر شما رأفت‌ نماید و از این‌ سبب‌ برمی‌خیزد تا بر شما ترحّم‌ فرماید چونكه‌ یهوه‌ خدای‌ انصاف‌ است‌. خوشابحال‌ همگانی‌ كه‌ منتظر وی‌ باشند.",
			"19": "<span>19 </span> &nbsp;زیرا كه‌ قوم‌ در صهیون‌ در اورشلیم‌ ساكن‌ خواهند بود و هرگز گریه‌ نخواهی‌ كرد و به‌ آواز فریادت‌ بر تو ترحّم‌ خواهد كرد، و چون‌ بشنود تو را اجابت‌ خواهد نمود.",
			"20": "<span>20 </span> &nbsp;و هرچند خداوند شما را نان‌ ضیق‌ و آب‌ مصیبت‌ بدهد، امّا معلّمانت‌ بار دیگر مخفی‌ نخواهند شد بلكه‌ چشمانت‌ معلّمان‌ تو را خواهد دید.",
			"21": "<span>21 </span> &nbsp;و گوشهایت‌ سخنی‌ را از عقب‌ تو خواهد شنید كه‌ می‌گوید: راه‌ این‌ است‌، در آن‌ سلوك‌ بنما هنگامی‌ كه‌ به‌ طرف‌ راست‌ یا چپ‌ می‌گردی‌.",
			"22": "<span>22 </span> &nbsp;و پوشش‌ بتهای‌ ریخته‌ نقره‌ خویش‌ را و سِتْر اصنام‌ تراشیده‌ طلای‌ خود را نجس‌ خواهید ساخت‌ و آنها را مثل‌ چیز نجس‌ دور انداخته‌، به‌ آن‌ خواهی‌ گفت‌: دور شو.",
			"23": "<span>23 </span> &nbsp;و باران‌ تخمت‌ را كه‌ زمین‌ خویش‌ را به‌ آن‌ زرع‌ می‌كنی‌ و نان‌ محصول‌ زمینت‌ را خواهد داد و آن‌ پر مغز و فراوان‌ خواهد شد و در آن‌ زمان‌ مواشی‌ تو در مرتع‌ وسیع‌ خواهند چرید.",
			"24": "<span>24 </span> &nbsp;و گاوان‌ و الاغانت‌ كه‌ زمین‌ را شیار می‌نمایند، آذوقه‌ نمك‌دار را كه‌ با غربال‌ و اوچوم‌ پاك‌ شده‌ است‌ خواهند خورد.",
			"25": "<span>25 </span> &nbsp;و در روز كشتار عظیم‌ كه‌ برجها در آن‌ خواهد افتاد، نهرها و جویهای‌ آب‌ بر هر كوه‌ بلند و به‌ هر تلّ مرتفع‌ جاری‌ خواهد شد.",
			"26": "<span>26 </span> &nbsp;و در روزی‌ كه‌ خداوند شكستگی‌ قوم‌ خود را ببندد و ضرب‌ جراحت‌ ایشان‌ را شفا دهد، روشنایی‌ ماه‌ مثل‌ روشنایی‌ آفتاب‌ و روشنایی‌ آفتاب‌ هفت‌ چندان‌ مثل‌ روشنایی‌ هفت‌ روز خواهد بود.",
			"27": "<span>27 </span> &nbsp;اینك‌ اسم‌ خداوند از جای‌ دور می‌آید، در غضب‌ خود سوزنده‌ و در ستون‌ غلیظ‌ و لبهایش‌پر از خشم‌ و زبانش‌ مثل‌ آتش‌ سوزان‌ است‌.",
			"28": "<span>28 </span> &nbsp;و نفخه‌ او مثل‌ نهر سرشار تا به‌ گردن‌ می‌رسد تا آنكه‌ امّت‌ها را به‌ غربال‌ مصیبت‌ ببیزد و دهنه‌ ضلالت‌ را بر چانه‌ قوم‌ها بگذارد.",
			"29": "<span>29 </span> &nbsp;و شما را سرودی‌ خواهد بود مثل‌ شب‌ تقدیس‌ نمودن‌ عید و شادمانی‌ دل‌ مثل‌ آنانی‌ كه‌ روانه‌ می‌شوند تا به‌ آواز نی‌ به‌ كوه‌ خداوند نزد صخره‌ اسرائیل‌ بیایند.",
			"30": "<span>30 </span> &nbsp;و خداوند جلال‌ آواز خود را خواهد شنوانید و فرود آوردن‌ بازوی‌ خود را با شدّت‌ غضب‌ و شعله‌ آتش‌ سوزنده‌ و طوفان‌ و سیل‌ و سنگهای‌ تگرگ‌ ظاهر خواهد ساخت‌.",
			"31": "<span>31 </span> &nbsp;زیرا كه‌ آشور به‌ آواز خداوند شكسته‌ خواهد شد و او را با عصا خواهد زد.",
			"32": "<span>32 </span> &nbsp;و هر ضرب‌ عصای‌ قضا كه‌ خداوند به‌ وی‌ خواهد آورد با دفّ و بربط‌ خواهد بود و با جنگهای‌ پر شورش‌ با آن‌ مقاتله‌ خواهد نمود.",
			"33": "<span>33 </span> &nbsp;زیرا كه‌ تُوفَتْ از قبل‌ مهیا شده‌ و برای‌ پادشاه‌ آماده‌ گردیده‌ است‌. آن‌ را عمیق‌ و وسیع‌ ساخته‌ است‌ كه‌ توده‌اش‌ آتش‌ و هیزم‌ بسیار است‌ و نفخه‌ خداوند مثل‌ نهر كبریت‌ آن‌ را مشتعل‌ خواهد ساخت‌."
		},
		"31": {
			"1": "<span>1 </span> &nbsp;وای‌ بر آنانی‌ كه‌ به‌ جهت‌ اعانت‌ به‌ مصر فرود آیند و بر اسبان‌ تكیه‌ نمایند و بر ارابه‌ها، زانرو كه‌ كثیرند و بر سواران‌ زانرو كه‌ بسیار قوّی‌اند، توكّل‌ كنند؛ امّا بسوی‌ قدّوس‌ اسرائیل‌ نظر نكنند و خداوند را طلب‌ ننمایند.",
			"2": "<span>2 </span> &nbsp;و او نیز حكیم‌ است‌ و بلا را می‌آورد و كلام‌ خود را برنخواهد گردانید و به‌ ضدّ خاندان‌ شریران‌ و اعانت‌ بدكاران‌ خواهد برخاست‌.",
			"3": "<span>3 </span> &nbsp;امّا مصریان‌ انسانند و نه‌ خدا و اسبان‌ ایشان‌ جسدند و نه‌ روح‌ و خداوند دست‌ خود را دراز می‌كند و اعانت‌كننده‌ را لغزان‌ و اعانت‌ كرده‌ شده‌ را افتان‌ گردانیده‌، هر دو ایشان‌ هلاك‌ خواهند شد.",
			"4": "<span>4 </span> &nbsp;زیرا خداوند به‌ من‌ چنین‌ گفته‌ است‌: چنانكه‌ شیر و شیر ژیان‌ بر شكار خود غرّش‌ می‌نماید، هنگامی‌ كه‌ گروه‌ شبانان‌ بروی‌ جمع‌ شوند و از صدای‌ ایشان‌ نترسیده‌ از غوغای‌ ایشان‌ سر فرو نمی‌آورد، همچنان‌ یهوه‌ صبایوت‌ نزول‌ می‌فرماید تا برای‌ كوه‌ صهیون‌ و تلّ آن‌ مقاتله‌ نماید.",
			"5": "<span>5 </span> &nbsp;مثل‌ مرغان‌ كه‌ در طیران‌ باشند، همچنان‌ یهوه‌ صبایوت‌ اورشلیم‌ را حمایت‌ خواهد نمود و حمایت‌ كرده‌، آن‌ را رستگار خواهد ساخت‌ و از آن‌ درگذشته‌، خلاصی‌ خواهد داد.",
			"6": "<span>6 </span> &nbsp;ای‌ بنی‌اسرائیل‌، بسوی‌ آن‌ كس‌ كه‌ بر وی‌ بینهایت‌ عصیان‌ ورزیده‌اید بازگشت‌ نمایید.",
			"7": "<span>7 </span> &nbsp;زیرا كه‌ در آن‌ روز هر كدام‌ از ایشان‌ بتهای‌ نقره‌ و بتهای‌ طلای‌ خود را كه‌ دستهای‌ شما آنها را به‌ جهت‌ گناه‌ خویش‌ ساخته‌ است‌، ترك‌ خواهند نمود.",
			"8": "<span>8 </span> &nbsp;آنگاه‌ آشور به‌ شمشیری‌ كه‌ از انسان‌ نباشد خواهد افتاد و تیغی‌ كه‌ از انسان‌ نباشد او را هلاك‌ خواهد ساخت‌ و او از شمشیر خواهد گریخت‌ و جوانانش‌ خراج‌ گذار خواهند شد.",
			"9": "<span>9 </span> &nbsp;و صخره‌ او از ترس‌ زایل‌ خواهد شد و سرورانش‌ از عَلَم‌ مبهوت‌ خواهند گردید. یهوه‌ كه‌ آتش‌ او در صهیون‌ و كوره‌ وی‌ در اورشلیم‌ است‌ این‌ را می‌گوید."
		},
		"32": {
			"1": "<span>1 </span> &nbsp;اینك‌ پادشاهی‌ به‌ عدالت‌ سلطنت‌خواهد نمود و سروران‌ به‌ انصاف‌ حكمرانی‌ خواهند كرد.",
			"2": "<span>2 </span> &nbsp;و مردی‌ مثل‌ پناه‌گاهی‌ از باد و پوششی‌ از طوفان‌ خواهد بود. و مانند جویهای‌ آب‌ در جای‌ خشك‌ و سایه‌ صخرۀعظیم‌ در زمین‌ تعب‌ آورنده‌ خواهد بود.",
			"3": "<span>3 </span> &nbsp;و چشمان‌ بینندگان‌ تار نخواهد شد و گوشهای‌ شنوندگان‌ اصغا خواهد كرد.",
			"4": "<span>4 </span> &nbsp;و دل‌ شتابندگان‌ معرفت‌ را خواهد فهمید و زبان‌ الكنان‌ كلام‌ فصیح‌ را به‌ ارتجال‌ خواهد گفت‌.",
			"5": "<span>5 </span> &nbsp;و مرد لئیم‌ بار دیگر كریم‌ خوانده‌ نخواهد شد و مرد خسیس‌ نجیب‌ گفته‌ نخواهد گردید.",
			"6": "<span>6 </span> &nbsp;زیرا مرد لئیم‌ به‌ لا´مت‌ متكلّم‌ خواهد شد و دلش‌ شرارت‌ را بعمل‌ خواهد آورد تا نفاق‌ را بجا آورده‌، به‌ ضدّ خداوند به‌ ضلالت‌ سخن‌ گوید و جان‌ گرسنگان‌ را تهی‌ كند و آب‌ تشنگان‌ را دور نماید.",
			"7": "<span>7 </span> &nbsp;آلات‌ مرد لئیم‌ نیز زشت‌ است‌ و تدابیر قبیح‌ می‌نماید تا مسكینان‌ را به‌ سخنان‌ دروغ‌ هلاك‌ نماید، هنگامی‌ كه‌ مسكینان‌ به‌ انصاف‌ سخن‌ می‌گویند.",
			"8": "<span>8 </span> &nbsp;امّا مرد كریم‌ تدبیرهای‌ كریمانه‌ می‌نماید و به‌ كَرَم‌ پایدار خواهد شد.",
			"9": "<span>9 </span> &nbsp;ای‌ زنان‌ مطمئّن‌ برخاسته‌، آواز مرا بشنوید و ای‌ دختران‌ ایمن‌ سخن‌ مرا گوش‌ گیرید.",
			"10": "<span>10 </span> &nbsp;ای‌ دختران‌ ایمن‌ بعد از یك‌ سال‌ و چند روزی‌ مضطرب‌ خواهید شد زانرو كه‌ چیدن‌ انگور قطع‌ می‌شود و جمع‌ كردن‌ میوه‌ها نخواهد بود.",
			"11": "<span>11 </span> &nbsp;ای‌ زنانِ مطمئّن‌ بلرزید و ای‌ دخترانِ ایمن‌ مضطرب‌ شوید. لباس‌ را كنده‌، برهنه‌ شوید و پلاس‌ بر میان‌ خود ببندید.",
			"12": "<span>12 </span> &nbsp;برای‌ مزرعه‌های‌ دلپسند و موهای‌ بارور سینه‌ خواهند زد.",
			"13": "<span>13 </span> &nbsp;بر زمین‌ قوم‌ من‌ خار و خس‌ خواهد رویید بلكه‌ بر همه‌ خانه‌های‌ شادمانی‌ در شهرِ مفتخر.",
			"14": "<span>14 </span> &nbsp;زیرا كه‌ قصر منهدم‌ و شهرِ معمور متروك‌ خواهد شد و عُوفَل‌ و دیده‌بانگاه‌ به‌ بیشه‌ای‌ سِباع‌ و محّل‌ تفرّج‌ خران‌ وحشی‌ و مرتع‌ گله‌ها تا به‌ ابد مبدّل‌ خواهد شد.",
			"15": "<span>15 </span> &nbsp;تا زمانی‌ كه‌ روح‌ از اعلی‌ عّلیین‌ بر ما ریخته‌ شود و بیابان‌ به‌ بوستان‌ مبدّل‌ گردد و بوستان‌ به‌ جنگل‌ محسوب‌ شود.",
			"16": "<span>16 </span> &nbsp;آنگاه‌ انصاف‌ در بیابان‌ ساكن‌ خواهد شد و عدالت‌ در بوستان‌ مقیم‌ خواهد گردید.",
			"17": "<span>17 </span> &nbsp;و عمل‌ عدالت‌ سلامتی‌ و نتیجه‌ عدالت‌ آرامی‌ و اطمینان‌ خواهد بود تا ابدالا´باد.",
			"18": "<span>18 </span> &nbsp;و قوم‌ من‌ در مسكن‌ سلامتی‌ و در مساكنِ مطمئن‌ و در منزلهای‌ آرامی‌ ساكن‌ خواهند شد.",
			"19": "<span>19 </span> &nbsp;و حین‌ فرود آمدن‌ جنگل‌ تگرگ‌ خواهد بارید و شهر به‌ دركه‌ اسفل‌ خواهد افتاد.",
			"20": "<span>20 </span> &nbsp;خوشابحال‌ شما كه‌ بر همه‌ آبها تخم‌ می‌كارید و پایهای‌ گاو و الاغ‌ را رها می‌سازید."
		},
		"33": {
			"1": "<span>1 </span> &nbsp;وای‌ بر تو ای‌ غارتگر كه‌ غارت‌ نشدی‌ و ای‌ خیانت‌ كاری‌ كه‌ با تو خیانت‌ نورزیدند. هنگامی‌ كه‌ غارت‌ را به‌ اتمام‌ رسانیدی‌، غارت‌ خواهی‌ شد و زمانی‌ كه‌ از خیانت‌ نمودن‌ دست‌ برداشتی‌، به‌ تو خیانت‌ خواهند ورزید.",
			"2": "<span>2 </span> &nbsp;ای‌ خداوند بر ما ترحّم‌ فرما زیرا كه‌ منتظر تو می‌باشیم‌ و هر بامداد بازوی‌ ایشان‌ باش‌ و در زمان‌ تنگی‌ نیز نجات‌ ما بشو.",
			"3": "<span>3 </span> &nbsp;از آواز غوغا، قوم‌ها گریختند و چون‌ خویشتن‌ را برافرازی‌ امّت‌ها پراكنده‌ خواهند شد.",
			"4": "<span>4 </span> &nbsp;و غارت‌ شما را جمع‌ خواهند كرد بطوری‌ كه‌ موران‌ جمع‌ می‌نمایند و بر آن‌ خواهند جهید بطوری‌ كه‌ ملخها می‌جهند.",
			"5": "<span>5 </span> &nbsp;خداوند متعال‌ می‌باشد زانرو كه‌ در اعلی‌علّیین‌ ساكن‌ است‌ و صَهْیون‌ را از انصاف‌ و عدالت‌ مملّو خواهد ساخت‌.",
			"6": "<span>6 </span> &nbsp;و فراوانی نجات‌ و حكمت‌ و معرفتْ استقامت‌ اوقات‌ تو خواهد شد. و ترس‌ خداوند خزینه‌ او خواهد بود.",
			"7": "<span>7 </span> &nbsp;اینك‌شجاعان‌ ایشان‌ در بیرون‌ فریاد می‌كنند و رسولانِ سلامتی‌ زار زار گریه‌ می‌نمایند.",
			"8": "<span>8 </span> &nbsp;شاهراهها ویران‌ می‌شود و راه‌گذریان‌ تلف‌ می‌گردند. عهد را شكسته‌ است‌ و شهرها را خوار نموده‌، به‌ مردمان‌ اعتنا نكرده‌ است‌.",
			"9": "<span>9 </span> &nbsp;زمین‌ ماتم‌كنان‌ كاهیده‌ شده‌ است‌ و لبنان‌ خجل‌ گشته‌، تلف‌ گردیده‌ است‌ و شارون‌ مثل‌ بیابان‌ شده‌ و باشان‌ و كَرْمَلْ برگهای‌ خود را ریخته‌اند.",
			"10": "<span>10 </span> &nbsp;خداوند می‌گوید كه‌ الا´ن‌ برمی‌خیزم‌ و حال‌ خود را برمی‌افرازم‌ و اكنون‌ متعال‌ خواهم‌ گردید.",
			"11": "<span>11 </span> &nbsp;و شما از كاه‌ حامله‌ شده‌، خس‌ خواهید زایید. و نَفَس‌ شما آتشی‌ است‌ كه‌ شما را خواهد سوزانید.",
			"12": "<span>12 </span> &nbsp;و قوم‌ها مثل‌ آهك‌ سوخته‌ و مانند خارهای‌ قطع‌ شده‌ كه‌ از آتش‌ مشتعل‌ گردد خواهند شد.",
			"13": "<span>13 </span> &nbsp;ای‌ شما كه‌ دور هستید، آنچه‌ را كه‌ كرده‌ام‌ بشنوید و ای‌ شما كه‌ نزدیك‌ می‌باشید، جبروت‌ مرا بدانید.",
			"14": "<span>14 </span> &nbsp;گناه‌كارانی‌ كه‌ در صهیون‌اند می‌ترسند و لرزه‌ منافقان‌ را فرو گرفته‌ است‌، (و می‌گویند): كیست‌ از ما كه‌ در آتش‌ سوزنده‌ ساكن‌ خواهد شد و كیست‌ از ما كه‌ در نارهای‌ جاودانی‌ ساكن‌ خواهد گردید؟",
			"15": "<span>15 </span> &nbsp;امّا آنكه‌ به‌ صداقت‌ سالك‌ باشد و به‌ استقامت‌ تكلّم‌ نماید و سود ظلم‌ را خوار شمارد و دست‌ خویش‌ را از گرفتن‌ رشوه‌ بیفشاند و گوش‌ خود را از اصغای‌ خون‌ ریزی‌ ببندد و چشمان‌ خود را از دیدن‌ بدیها بر هم‌ كُنَد؛",
			"16": "<span>16 </span> &nbsp;او در مكان‌های‌ بلند ساكن‌ خواهد شد و ملجای‌ او ملاذ صخره‌ها خواهد بود. نان‌ او داده‌ خواهد شد و آب‌ او ایمن‌ خواهد بود.",
			"17": "<span>17 </span> &nbsp;چشمانت‌ پادشاه‌ را در زیباییش‌ خواهد دید و زمین‌ بی‌پایان‌ را خواهد نگریست‌.",
			"18": "<span>18 </span> &nbsp;دل‌ تو متذكّر آن‌ خوف‌ خواهد شد (وخواهی‌ گفت‌):كجا است‌ نویسنده‌ و كجا است‌ وزن‌ كنندۀ (خراج‌) و كجا است‌ شمارنده‌ برجها؟",
			"19": "<span>19 </span> &nbsp;قوم‌ ستم‌ پیشه‌ و قوم‌ دشوار لغت‌ را كه‌ نمی‌توانی‌ شنید و الكن‌ زبان‌ را كه‌ نمی‌توانی‌ فهمید بار دیگر نخواهی‌ دید.",
			"20": "<span>20 </span> &nbsp;صهیون‌ شهر جشن‌ مقدّس‌ ما را ملاحظه‌ نما. و چشمانت‌ اورشلیم‌ مسكن‌ سلامتی‌ را خواهد دید یعنی‌ خیمه‌ای‌ را كه‌ منتقل‌ نشود و میخهایش‌ كنده‌ نگردد و هیچكدام‌ از طنابهایش‌ گسیخته‌ نشود.",
			"21": "<span>21 </span> &nbsp;بلكه‌ در آنجا خداوند ذوالجلال‌ برای‌ ما مكان‌ جویهای‌ آب‌ و نهرهای‌ وسیع‌ خواهد بود كه‌ در آن‌ هیچ‌ كشتی‌ با پاروها داخل‌ نخواهد شد و سفینه‌ بزرگ‌ از آن‌ عبور نخواهد كرد.",
			"22": "<span>22 </span> &nbsp;زیرا خداوند داور ما است‌. خداوند شریعت‌ دهنده‌ ما است‌. خداوند پادشاه‌ ما است‌ پس‌ ما را نجات‌ خواهد داد.",
			"23": "<span>23 </span> &nbsp;ریسمانهای‌ تو سست‌ بود كه‌ پایه‌ دكل‌ خود را نتوانست‌ محكم‌ نگاه‌ دارد و بادبان‌ را نتوانست‌ بگشاید، آنگاه‌ غارت‌ بسیار تقسیم‌ شد و لنگان‌ غنیمت‌ را بردند.",
			"24": "<span>24 </span> &nbsp;لیكن‌ ساكن‌ آن‌ نخواهد گفت‌ كه‌ بیمار هستم‌ و گناه‌ قومی‌ كه‌ در آن‌ ساكن‌ باشند آمرزیده‌ خواهد شد."
		},
		"34": {
			"1": "<span>1 </span> &nbsp;ای‌ امّت‌ها نزدیك‌ آیید تا بشنوید! و ای قوم‌ها اصغا نمایید! جهان‌ و پری‌ آن‌ بشنوند. ربع‌ مسكون‌ و هرچه‌ از آن‌ صادر باشد.",
			"2": "<span>2 </span> &nbsp;زیرا كه‌ غضب‌ خداوند بر تمامی‌ امّت‌ها و خشم‌ وی‌ بر جمیع‌ لشكرهای‌ ایشان‌ است‌. پس‌ ایشان‌ را به‌ هلاكت‌ سپرده‌، بقتل‌ تسلیم‌ نموده‌ است‌.",
			"3": "<span>3 </span> &nbsp;وكشتگان‌ ایشان‌ دور افكنده‌ می‌شوند و عفونت‌ لاشهای‌ ایشان‌ برمی‌آید. و از خون‌ ایشان‌ كوهها گداخته‌ می‌گردد.",
			"4": "<span>4 </span> &nbsp;و تمامی‌ لشكر آسمان‌ از هم‌ خواهند پاشید و آسمان‌ مثل‌ طومار پیچیده‌ خواهد شد. و تمامی‌ لشكر آن‌ پژمرده‌ خواهند گشت‌، بطوریكه‌ برگ‌ از مو بریزد و مثل‌ میوه‌ نارس‌ از درخت‌ انجیر.",
			"5": "<span>5 </span> &nbsp;زیرا كه‌ شمشیر من‌ در آسمان‌ سیراب‌ شده‌ است‌. و اینك‌ بر ادوم‌ و بر قوم‌ مغضوب‌ من‌ برای‌ داوری‌ نازل‌ می‌شود.",
			"6": "<span>6 </span> &nbsp;شمشیر خداوند پر خون‌ شده‌ و از پیه‌ فربه‌ گردیده‌ است‌ یعنی‌ از خون‌ بره‌ها و بزها و از پیه‌ گُردِه‌ قوچها. زیرا خداوند را در بصره‌ قربانی‌ است‌ و ذبح‌ عظیمی‌ در زمین‌ اَدوم‌.",
			"7": "<span>7 </span> &nbsp;و گاوان‌ وحشی‌ با آنها خواهند افتاد و گوساله‌ها با گاوان‌ نر، و زمین‌ ایشان‌ از خون‌ سیراب‌ شده‌، خاك‌ ایشان‌ از پیه‌ فربه‌ خواهد شد.",
			"8": "<span>8 </span> &nbsp;زیرا خداوند را روز انتقام‌ و سال‌ عقوبت‌ به‌ جهت‌ دعوی‌ صهیون‌ خواهد بود.",
			"9": "<span>9 </span> &nbsp;و نهرهای‌ آن‌ به‌ قیر و غبار آن‌ به‌ كبریت‌ مبدّل‌ خواهد شد و زمینش‌ قیر سوزنده‌ خواهد گشت‌.",
			"10": "<span>10 </span> &nbsp;شب‌ و روز خاموش‌ نشده‌، دودش‌ تا به‌ ابد خواهد برآمد. نسلاً بعد نسل‌ خراب‌ خواهد ماند كه‌ كسی‌ تا ابدالا´باد در آن‌ گذر نكند.",
			"11": "<span>11 </span> &nbsp;بلكه‌ مرغ‌ سقّا و خارپشت‌ آن‌ را تصرّف‌ خواهند كرد و بوم‌ و غراب‌ در آن‌ ساكن‌ خواهند شد و ریسمان‌ خرابی‌ و شاقول‌ ویرانی‌ را بر آن‌ خواهد كشید.",
			"12": "<span>12 </span> &nbsp;و از اشراف‌ آن‌ كسی‌ در آنجا نخواهد بود كه‌ او را به‌ پادشاهی‌ بخوانند و جمیع‌ رؤسایش‌ نیست‌ خواهند شد.",
			"13": "<span>13 </span> &nbsp;و در قصرهایش‌ خارها خواهدرویید و در قلعه‌هایش‌ خسك‌ و شتر خار و مسكن‌ گرگ‌ و خانه‌ شترمرغ‌ خواهد شد.",
			"14": "<span>14 </span> &nbsp;و وحوش‌ بیابان‌ با شغال‌ خواهند برخورد و غول‌ به‌ رفیق‌ خود ندا خواهد داد و عفریت‌ نیز در آنجا مأوا گزیده‌، برای‌ خود آرامگاهی‌ خواهد یافت‌.",
			"15": "<span>15 </span> &nbsp;در آنجا تیرمار آشیانه‌ ساخته‌، تخم‌ خواهد نهاد و بر آن‌ نشسته‌، بچه‌های‌ خود را زیر سایه‌ خود جمع‌ خواهد كرد و در آنجا كركسها با یكدیگر جمع‌ خواهند شد.",
			"16": "<span>16 </span> &nbsp;از كتاب‌ خداوند تفتیش‌ نموده‌، مطالعه‌ كنید. یكی‌ از اینها گم‌ نخواهد شد و یكی‌ جفت‌ خود را مفقود نخواهد یافت‌ زیرا كه‌ دهان‌ او این‌ را امر فرموده‌ و روح‌ او اینها را جمع‌ كرده‌ است‌.",
			"17": "<span>17 </span> &nbsp;و او برای‌ آنها قرعه‌ انداخته‌ و دست‌ او آن‌ را به‌ جهت‌ آنها با ریسمان‌ تقسیم‌ نموده‌ است‌. و تا ابدالا´باد متصرّف‌ آن‌ خواهند شد و نسلاً بعد نسل‌ در آن‌ سكونت‌ خواهند داشت‌."
		},
		"35": {
			"1": "<span>1 </span> &nbsp;بیابان‌ و زمین‌ خشك‌ شادمان‌ خواهد شد و صحرا به‌ وجد آمده‌، مثل‌ گل‌ سرخ‌ خواهد شكفت‌.",
			"2": "<span>2 </span> &nbsp;شكوفه‌ بسیار نموده‌، با شادمانی‌ و ترنّم‌ شادی‌ خواهد كرد. شوكت‌ لبنان‌ و زیبایی‌ كَرمَل‌ و شارون‌ به‌ آن‌ عطا خواهد شد. جلال‌ یهوه‌ و زیبایی‌ خدای‌ ما را مشاهده‌ خواهند نمود.",
			"3": "<span>3 </span> &nbsp;دستهای‌ سست‌ را قوی‌ سازید و زانوهای‌ لرزنده‌ را محكم‌ گردانید.",
			"4": "<span>4 </span> &nbsp;به‌ دلهای‌ خائف‌ بگویید: قوی‌ شوید و مترسید اینك‌ خدای‌ شما با انتقام‌ می‌آید. او با عقوبت‌ الهی‌ می‌آید و شما را نجات‌ خواهد داد.",
			"5": "<span>5 </span> &nbsp;آنگاه‌ چشمان‌ كوران‌ باز خواهد شد وگوشهای‌ كران‌ مفتوح‌ خواهد گردید.",
			"6": "<span>6 </span> &nbsp;آنگاه‌ لنگان‌ مثل‌ غزال‌ جست‌ و خیز خواهند نمود و زبان‌ گنگ‌ خواهد سرایید. زیرا كه‌ آبها در بیابان‌ و نهرها در صحرا خواهد جوشید.",
			"7": "<span>7 </span> &nbsp;و سراب‌ به‌ بركه‌ و مكان‌های‌ خشك‌ به‌ چشمه‌های‌ آب‌ مبدّل‌ خواهد گردید. در مسكنی‌ كه‌ شغالها می‌خوابند علف‌ و بوریا و نی‌ خواهد بود.",
			"8": "<span>8 </span> &nbsp;و در آنجا شاهراهی‌ و طریقی‌ خواهد بود و به‌ طریق‌ مقدّس‌ نامیده‌ خواهد شد و نجسان‌ از آن‌ عبور نخواهند كرد بلكه‌ آن‌ به‌ جهت‌ ایشان‌ خواهد بود. و هركه‌ در آن‌ راه‌ سالك‌ شود اگرچه‌ هم‌ جاهل‌ باشد گمراه‌ نخواهد گردید.",
			"9": "<span>9 </span> &nbsp;شیری‌ در آن‌ نخواهد بود و حیوان‌ درنده‌ای‌ بر آن‌ برنخواهد آمد و در آنجا یافت‌ نخواهد شد بلكه‌ ناجیان‌ بر آن‌ سالك‌ خواهند گشت.",
			"10": "<span>10 </span> &nbsp;و فدیه‌ شدگان‌ خداوند بازگشت‌ نموده‌، با ترنّم‌ به‌ صهیون‌ خواهند آمد و خوشی‌ جاودانی‌ بر سر ایشان‌ خواهد بود. و شادمانی‌ و خوشی‌ را خواهند یافت‌ و غم‌ و ناله‌ فرار خواهد كرد."
		},
		"36": {
			"1": "<span>1 </span> &nbsp;و در سال‌ چهاردهم‌ حزقیا پادشاه‌ واقع‌شد كه‌ سنحاریب‌ پادشاه‌ آشور بر تمامی‌ شهرهای‌ حصاردار یهودا برآمده‌، آنها را تسخیر نمود.",
			"2": "<span>2 </span> &nbsp;و پادشاه‌ آشور ربشاقی‌ را از لاكیش‌ به‌ اورشلیم‌ نزد حزقیا پادشاه‌ با موكب‌ عظیم‌ فرستاد و او نزد قنات‌ بركه‌ فوقانی‌ به‌ راه‌ مزرعه‌ گازر ایستاد.",
			"3": "<span>3 </span> &nbsp;و الیاقیم‌ بن‌ حلقیا كه‌ ناظر خانه‌ بود و شبنای‌ كاتب‌ و یوآخ‌ بن‌ آساف‌ وقایع‌نگار نزد وی‌ بیرون‌ آمدند.",
			"4": "<span>4 </span> &nbsp;و ربشاقی‌ به‌ایشان‌ گفت‌: «به‌ حزقیا بگویید سلطان‌ عظیم‌ پادشاه‌ آشور چنین‌ می‌گوید: این‌ اعتماد شما كه‌ بر آن‌ توكّل‌ می‌نمایید چیست‌؟",
			"5": "<span>5 </span> &nbsp;می‌گویم‌ كه‌ مشورت‌ و قوّت‌ جنگ‌ سخنان‌ باطل‌ است‌. الا´ن‌ كیست‌ كه‌ بر او توكّل‌ نموده‌ای‌ كه‌ به‌ من‌ عاصی‌ شده‌ای‌؟",
			"6": "<span>6 </span> &nbsp;هان‌ بر عصای‌ این‌ نی‌ خرد شده‌ یعنی‌ بر مصر توكّل‌ می‌نمایی‌ كه‌ اگر كسی‌ بر آن‌ تكیه‌ كند به‌ دستش‌ فرو رفته‌، آن‌ را مجروح‌ می‌سازد. همچنان‌ است‌ فرعون‌ پادشاه‌ مصر برای‌ همگانی‌ كه‌ بر وی‌ توكّل‌ نمایند.",
			"7": "<span>7 </span> &nbsp;و اگر مرا گویی‌ كه‌ بر یهوه‌ خدای‌ خود توكّل‌ داریم‌ آیا او آن‌ نیست‌ كه‌ حزقیا مكان‌های‌ بلند و مذبح‌های‌ او را برداشته‌ است‌ و به‌ یهودا و اورشلیم‌ گفته‌ كه‌ پیش‌ این‌ مذبح‌ سجده‌ نمایید؟",
			"8": "<span>8 </span> &nbsp;پس‌ حال‌ با آقایم‌ پادشاه‌ آشور شرط‌ ببند و من‌ دو هزار اسب‌ به‌ تو می‌دهم‌ اگر از جانب‌ خود سواران‌ بر آنها توانی‌ گذاشت‌.",
			"9": "<span>9 </span> &nbsp;پس‌ چگونه‌ روی‌ یك‌ والی‌ از كوچكترین‌ بندگان‌ آقایم‌ را خواهی‌ برگردانید و بر مصر به‌ جهت‌ ارابه‌ها و سواران‌ توكّل‌ داری‌؟",
			"10": "<span>10 </span> &nbsp;و آیا من‌ الا´ن‌ بی‌ اذن‌ یهوه‌ بر این‌ زمین‌ به‌ جهت‌ خرابی‌ آن‌ برآمده‌ام‌ ؟ یهوه‌ مرا گفته‌ است‌ بر این‌ زمین‌ برآی‌ و آن‌ را خراب‌ كن‌.»",
			"11": "<span>11 </span> &nbsp;آنگاه‌ الیقایم‌ و شبنا و یوآخ‌ به‌ ربشاقی‌ گفتند: «تمنّا اینكه‌ با بندگانت‌ به‌ زبان‌ آرامی‌ گفتگو نمایی‌ زیرا آن‌ را می‌فهمیم‌ و با ما به‌ زبان‌ یهود در گوش‌ مردمی‌ كه‌ بر حصارند گفتگو منمای‌.»",
			"12": "<span>12 </span> &nbsp;ربشاقی‌ گفت‌: «آیا آقایم‌ مرا نزد آقایت‌ و تو فرستاده‌ است‌ تا این‌ سخنان‌ را بگویم‌؟ مگر مرا نزد مردانی‌ كه‌ بر حصار نشسته‌اند نفرستاده‌، تا ایشان‌ با شما نجاست‌ خود را بخورند و بول‌ خود را بنوشند؟»",
			"13": "<span>13 </span> &nbsp;پس‌ ربشاقی‌ بایستاد و به‌ آوازبلند به‌ زبان‌ یهود صدا زده‌، گفت‌: «سخنان‌ سلطان‌ عظیم‌ پادشاه‌ آشور را بشنوید.",
			"14": "<span>14 </span> &nbsp;پادشاه‌ چنین‌ می‌گوید: حزقیا شما را فریب‌ ندهد زیرا كه‌ شما را نمی‌تواند رهانید.",
			"15": "<span>15 </span> &nbsp;و حزقیا شما را بر یهوه‌ مطمئن‌ نسازد و نگوید كه‌ یهوه‌ البتّه‌ ما را خواهد رهانید و این‌ شهر به‌ دست‌ پادشاه‌ آشور تسلیم‌ نخواهد شد.",
			"16": "<span>16 </span> &nbsp;به‌ حزقیا گوش‌ مدهید زیرا كه‌ پادشاه‌ آشور چنین‌ می‌گوید: با من‌ صلح‌ كنید و نزد من‌ بیرون‌ آیید تا هركس‌ از مو خود و هر كس‌ از انجیر خود بخورد و هر كس‌ از آب‌ چشمه‌ خود بنوشد.",
			"17": "<span>17 </span> &nbsp;تا بیایم‌ و شما را به‌ زمین‌ مانند زمین‌ خودتان‌ بیاورم‌ یعنی‌ به‌ زمین‌ غلّه‌ و شیره‌ و زمین‌ نان‌ و تاكستانها.",
			"18": "<span>18 </span> &nbsp;مبادا حزقیا شما را فریب‌ دهد و گوید یهوه‌ ما را خواهد رهانید. آیا هیچكدام‌ از خدایان‌ امّت‌ها زمین‌ خود را از دست‌ پادشاه‌ آشور رهانیده‌ است‌؟",
			"19": "<span>19 </span> &nbsp;خدایان‌ حمات‌ و ارفاد كجایند و خدایان‌ سفروایم‌ كجا و آیا سامره‌ را از دست‌ من‌ رهانیده‌اند؟",
			"20": "<span>20 </span> &nbsp;از جمیع‌ خدایان‌ این‌ زمینها كدامند كه‌ زمین‌ خویش‌ را از دست‌ من‌ نجات‌ داده‌اند تا یهوه‌ اورشلیم‌ را از دست‌ من‌ نجات‌ دهد؟»",
			"21": "<span>21 </span> &nbsp;امّا ایشان‌ سكوت‌ نموده‌، به‌ او هیچ‌ جواب‌ ندادند زیرا كه‌ پادشاه‌ امر فرموده‌ و گفته‌ بود كه‌ او را جواب‌ ندهید.",
			"22": "<span>22 </span> &nbsp;پس‌ الیاقیم‌ بن‌ حلقیا كه‌ ناظر خانه‌ بود و شبنای‌ كاتب‌ و یوآخ‌ بن‌ آساف‌ وقایع‌نگار با جامه‌ دریده‌ نزد حزقیا آمدند و سخنان‌ ربشاقی‌ را به‌ او باز گفتند."
		},
		"37": {
			"1": "<span>1 </span> &nbsp;و واقع‌ شد كه‌ چون‌ حزقیا پادشاه‌ این‌ راشنید لباس‌ خود را چاك‌ زده‌ و پلاس‌پوشیده‌، به‌ خانه‌ خداوند داخل‌ شد.",
			"2": "<span>2 </span> &nbsp;و الیاقیم‌ ناظر خانه‌ و شبنای‌ كاتب‌ و مشایخ‌ كهنه‌ را ملبّس‌ به‌ پلاس‌ نزد اشعیا ابن‌ آموص‌ نبّی‌ فرستاد،",
			"3": "<span>3 </span> &nbsp;و به‌ وی‌ گفتند: «حزقیا چنین‌ می‌گوید كه‌ امروز روز تنگی‌ و تأدیب‌ و اهانت‌ است‌ زیرا كه‌ پسران‌ بفم‌ رحم‌ رسیده‌اند و قوّت‌ زاییدن‌ نیست‌.",
			"4": "<span>4 </span> &nbsp;شاید یهوه‌ خدایت‌ سخنان‌ ربشاقی‌ را كه‌ آقایش‌ پادشاه‌ آشور او را برای‌ اهانت‌ نمودن‌ خدای‌ حی فرستاده‌ است‌ بشنود و سخنانی‌ را كه‌ یهوه‌ خدایت‌ شنیده‌ است‌ توبیخ‌ نماید. پس‌ برای‌ بقیه‌ای‌ كه‌ یافت‌ می‌شوند تضرّع‌ نما.»",
			"5": "<span>5 </span> &nbsp;و بندگان‌ حزقیا پادشاه‌ نزد اشعیا آمدند.",
			"6": "<span>6 </span> &nbsp;و اشعیا به‌ ایشان‌ گفت‌: «به‌ آقای‌ خود چنین‌ گویید كه‌ یهوه‌ چنین‌ می‌فرماید: از سخنانی‌ كه‌ شنیدی‌ كه‌ بندگان‌ پادشاه‌ آشور مرا بدانها كفر گفته‌اند، مترس‌.",
			"7": "<span>7 </span> &nbsp;همانا روحی‌ بر او می‌فرستم‌ كه‌ خبری‌ شنیده‌، به‌ ولایت‌ خود خواهد برگشت‌ و او را در ولایت‌ خودش‌ به‌ شمشیر هلاك‌ خواهم‌ ساخت‌.»",
			"8": "<span>8 </span> &nbsp;پس‌ ربشاقی‌ مراجعت‌ كرده‌، پادشاه‌ آشور را یافت‌ كه‌ با لبنه‌ جنگ‌ می‌كرد زیرا شنیده‌ بود كه‌ از لاكیش‌ كوچ‌ كرده‌ است‌.",
			"9": "<span>9 </span> &nbsp;و او درباره‌ ترهاقه‌ پادشاه‌ كوش‌ خبری‌ شنید كه‌ به‌ جهت‌ مقاتله‌ با تو بیرون‌ آمده‌ است‌. پس‌ چون‌ این‌ را شنید (باز) ایلچیان‌ نزد حزقیا فرستاده‌، گفت‌:",
			"10": "<span>10 </span> &nbsp;«به‌ حزقیا پادشاه‌ یهودا چنین‌ گویید: خدای‌ تو كه‌ به‌ او توكّل‌ می‌نمایی‌ تو را فریب‌ ندهد و نگوید كه‌ اورشلیم‌ به‌ دست‌ پادشاه‌ آشور تسلیم‌ نخواهد شد.",
			"11": "<span>11 </span> &nbsp;اینك‌ تو شنیده‌ای‌ كه‌ پادشاهان‌ آشور با همه‌ ولایتها چه‌ كرده‌ و چگونه‌ آنها را بالكّل‌ هلاك‌ ساخته‌اند و آیا تو رهایی‌ خواهی‌ یافت‌؟",
			"12": "<span>12 </span> &nbsp;و آیا خدایان‌امّت‌هایی‌ كه‌ پدران‌ من‌ آنها را هلاك‌ ساختند مثل‌ جوزان‌ و حاران‌ و رصف‌ و بنی‌ عدن‌ كه‌ در تلسّار می‌باشند ایشان‌ را نجات‌ دادند.",
			"13": "<span>13 </span> &nbsp;پادشاه‌ حمات‌ كجا است‌ و پادشاه‌ ارفاد و پادشاه‌ شهر سفروایم‌ و هینع‌ و عوّا؟»",
			"14": "<span>14 </span> &nbsp;و حزقیا مكتوب‌ را از دست‌ ایلچیان‌ گرفته‌، آن‌ را خواند و حزقیا به‌ خانه‌ خداوند درآمده‌، آن‌ را به‌ حضور خداوند پهن‌ كرد.",
			"15": "<span>15 </span> &nbsp;و حزقیا نزد خداوند دعا كرده‌، گفت‌:",
			"16": "<span>16 </span> &nbsp;«ای‌ یهوه‌ صبایوت‌ خدای‌ اسرائیل‌ كه‌ بر كروبیان‌ جلوس‌ می‌نمایی‌! تویی‌ كه‌ به‌ تنهایی‌ بر تمامی‌ ممالك‌ جهان‌ خدا هستی‌ و تو آسمان‌ و زمین‌ را آفریده‌ای‌.",
			"17": "<span>17 </span> &nbsp;ای‌ خداوند گوش‌ خود را فرا گرفته‌، بشنو و ای‌ خداوند چشمان‌ خود را گشوده‌، ببین‌ و همه‌ سخنان‌ سنحاریب‌ را كه‌ به‌ جهت‌ اهانت‌ نمودن‌ خدای‌ حی فرستاده‌ است‌ استماع‌ نما!",
			"18": "<span>18 </span> &nbsp;ای‌ خداوند راست‌ است‌ كه‌ پادشاهان‌ آشور همه‌ ممالك‌ و زمین‌ ایشان‌ را خراب‌ كرده‌.",
			"19": "<span>19 </span> &nbsp;و خدایان‌ ایشان‌ را به‌ آتش‌ انداخته‌اند زیرا كه‌ خدا نبودند بلكه‌ ساخته‌ دست‌ انسان‌ از چوب‌ و سنگ‌. پس‌ به‌ این‌ سبب‌ آنها را تباه‌ ساختند.",
			"20": "<span>20 </span> &nbsp;پس‌ حال‌ ای‌ یهوه‌ خدای‌ ما ما را از دست‌ او رهایی‌ ده‌ تا جمیع‌ ممالك‌ جهان‌ بدانند كه‌ تو تنها یهوه‌ هستی‌.»",
			"21": "<span>21 </span> &nbsp;پس‌ اشعیا ابن‌ آموص‌ نزد حزقیا فرستاده‌، گفت‌: «یهوه‌ خدای‌ اسرائیل‌ چنین‌ می‌گوید: چونكه‌ درباره‌ سنحاریب‌ پادشاه‌ آشور نزد من‌ دعا نمودی‌،",
			"22": "<span>22 </span> &nbsp;كلامی‌ كه‌ خداوند در باره‌اش‌ گفته‌ این‌ است‌: آن‌ باكره‌ دختر صهیون‌ تو را حقیر شمرده‌،استهزا نموده‌ است‌ و دختر اورشلیم‌ سر خود را به‌ تو جنبانیده‌ است‌.",
			"23": "<span>23 </span> &nbsp;كیست‌ كه‌ او را اهانت‌ كرده‌، كفر گفته‌ای‌ و كیست‌ كه‌ بر وی‌ آواز بلند كرده‌، چشمان‌ خود را به‌ علّیین‌ افراشته‌ای‌؟ مگر قدّوس‌ اسرائیل‌ نیست‌؟",
			"24": "<span>24 </span> &nbsp;به‌ واسطه‌ بندگانت‌ خداوند را اهانت‌ كرده‌، گفته‌ای‌ به‌ كثرت‌ ارابه‌های‌ خود بر بلندی‌ كوهها و به‌ اطراف‌ لبنان‌ برآمده‌ام‌ و بلندترین‌ سروهای‌ آزادش‌ و بهترین‌ صنوبرهایش‌ را قطع‌ نموده‌، به‌ بلندی‌ اقصایش‌ و به‌ درختستان‌ بوستانش‌ داخل‌ شده‌ام‌.",
			"25": "<span>25 </span> &nbsp;و من‌ حفره‌ زده‌، آب‌ نوشیدم‌ و به‌ كف‌ پای‌ خود تمامی‌ نهرهای‌ مصر را خشك‌ خواهم‌ كرد.",
			"26": "<span>26 </span> &nbsp;«آیا نشنیده‌ای‌ كه‌ من‌ این‌ را از زمان‌ سلف‌ كرده‌ام‌ و از ایام‌ قدیم‌ صورت‌ داده‌ام‌ و الا´ن‌ آن‌ را به‌ وقوع‌ آورده‌ام‌ تا تو به‌ ظهور آمده‌، و شهرهای‌ حصار دار را خراب‌ نموده‌، به‌ توده‌های‌ ویران‌ مبدّل‌ سازی‌.",
			"27": "<span>27 </span> &nbsp;از این‌ جهت‌ ساكنان‌ آنها كم‌ قوّت‌ بوده‌، ترسان‌ و خجل‌ شدند. مثل‌ علف‌ صحرا و گیاه‌ سبز و علف‌ پشت‌ بام‌ و مثل‌ مزرعه‌ قبل‌ از نمّو كردنش‌ گردیدند.",
			"28": "<span>28 </span> &nbsp;امّا من‌ نشستن‌ تو را و خروج‌ و دخولت‌ و خشمی‌ را كه‌ بر من‌ داری‌ می‌دانم‌.",
			"29": "<span>29 </span> &nbsp;چونكه‌ خشمی‌ كه‌ به‌ من‌ داری‌ و غرور تو به‌ گوش‌ من‌ برآمده‌ است‌. بنابراین‌ مهار خود را به‌ بینی‌ تو و لگام‌ خود را به‌ لبهایت‌ گذاشته‌، تو را به‌ راهی‌ كه‌ آمده‌ای‌ برخواهم‌ گردانید.",
			"30": "<span>30 </span> &nbsp;و علامت‌ برای‌ تو این‌ خواهد بود كه‌ امسال‌ غلّه‌ خودرو خواهید خورد و سال‌ دوّم‌ آنچه‌ از آن‌ بروید و در سال‌ سوّم‌ بكارید و بدروید و تاكستانها غرس‌ نموده‌، میوه‌ آنها را بخورید.",
			"31": "<span>31 </span> &nbsp;و بقیه‌ای‌ كه‌ از خاندان‌ یهودا رستگار شوند بار دیگر به‌ پایین‌ ریشه‌ خواهند زد و به‌ بالا میوه‌ خواهند آورد.",
			"32": "<span>32 </span> &nbsp;زیرا كه‌ بقیه‌ای‌ از اورشلیم‌ و رستگاران‌ از كوه‌صهیون‌ بیرون‌ خواهند آمد. غیرت‌ یهوه‌ صبایوت‌ این‌ را بجا خواهد آورد.",
			"33": "<span>33 </span> &nbsp;بنابراین‌ خداوند در باره‌ پادشاه‌ آشور چنین‌ می‌گوید كه‌ به‌ این‌ شهر داخل‌ نخواهد شد و به‌ اینجا تیر نخواهد انداخت‌ و در مقابلش‌ با سپر نخواهد آمد و منجنیق‌ در پیش‌ او برنخواهد افراشت‌.",
			"34": "<span>34 </span> &nbsp;به‌ راهی‌ كه‌ آمده‌ است‌ به‌ همان‌ برخواهد گشت‌ و به‌ این‌ شهر داخل‌ نخواهد شد. خداوند این‌ را می‌گوید.",
			"35": "<span>35 </span> &nbsp;زیرا كه‌ این‌ شهر را حمایت‌ كرده‌، به‌ خاطر خود و به‌ خاطر بنده‌ خویش‌ داود آن‌ را نجات‌ خواهم‌ داد.»",
			"36": "<span>36 </span> &nbsp;پس‌ فرشته‌ خداوند بیرون‌ آمده‌، صد و هشتاد و پنجهزار نفر از اردوی‌ آشور را زد و بامدادان‌ چون‌ برخاستند اینك‌ جمیع‌ آنها لاشهای‌ مرده‌ بودند.",
			"37": "<span>37 </span> &nbsp;و سنحاریب‌ پادشاه‌ آشور كوچ‌ كرده‌، روانه‌ گردید و برگشته‌ در نینوی‌ ساكن‌ شد.",
			"38": "<span>38 </span> &nbsp;و واقع‌ شد كه‌ چون‌ او در خانه‌ خدای‌ خویش‌ نسروك‌ عبادت‌ می‌كرد، پسرانش‌ ادرمّلك‌ و شَرْآصَر او را به‌ شمشیر زدند و ایشان‌ به‌ زمین‌ اَراراط‌ فرار كردند و پسرش‌ آسَرْحَدُّون‌ به‌ جایش‌ سلطنت‌ نمود."
		},
		"38": {
			"1": "<span>1 </span> &nbsp;در آن‌ ایام‌ حزقیا بیمار و مشرف‌ به‌ موت‌شد و اشعیا ابن‌ آموص‌ نبّی‌ نزد وی‌ آمده‌، او را گفت‌: « خداوند چنین‌ می‌گوید: تدارك‌ خانه‌ خود را ببین‌ زیرا كه‌ می‌میری‌ و زنده‌ نخواهی‌ ماند.»",
			"2": "<span>2 </span> &nbsp;آنگاه‌ حزقیا روی‌ خود را بسوی‌ دیوار برگردانیده‌، نزد خداوند دعا نمود،",
			"3": "<span>3 </span> &nbsp;و گفت‌: «ای‌ خداوند مستدعی‌ اینكه‌ بیاد آوری‌ كه‌ چگونه‌ به‌ حضور تو به‌ امانت‌ و به‌ دل‌ كامل‌ سلوك‌ نموده‌ام‌ و آنچه‌ در نظر تو پسند بوده‌ است‌ بجا آورده‌ام‌.» پس‌ حزقیا زار زار بگریست‌.",
			"4": "<span>4 </span> &nbsp;و كلام‌ خداوند براشعیا نازل‌ شده‌، گفت:",
			"5": "<span>5 </span> &nbsp;«برو و به‌ حزقیا بگو یهوه‌ خدای‌ پدرت‌ داود چنین‌ می‌گوید: دعای‌ تو را شنیدم‌ و اشكهایت‌ را دیدم‌. اینك‌ من‌ بر روزهای‌ تو پانزده‌ سال‌ افزودم‌.",
			"6": "<span>6 </span> &nbsp;و تو را و این‌ شهر را از دست‌ پادشاه‌ آشور خواهم‌ رهانید و این‌ شهر را حمایت‌ خواهم‌ نمود.",
			"7": "<span>7 </span> &nbsp;و علامت‌ از جانب‌ خداوند كه‌ خداوند این‌ كلام‌ را كه‌ گفته‌ است‌ بجا خواهد آورد این‌ است‌:",
			"8": "<span>8 </span> &nbsp;اینك‌ سایه‌ درجاتی‌ كه‌ از آفتاب‌ بر ساعت‌ آفتابی‌ آحاز پایین‌ رفته‌ است‌ ده‌ درجه‌ به‌ عقب‌ برمی‌گردانم‌.» پس‌ آفتاب‌ از درجاتی‌ كه‌ بر ساعت‌ آفتابی‌ پایین‌ رفته‌ بود، ده‌ درجه‌ برگشت‌.",
			"9": "<span>9 </span> &nbsp;مكتوب‌ حزقیا پادشاه‌ یهودا وقتی‌ كه‌ بیمار شد و از بیماریش‌ شفا یافت‌:",
			"10": "<span>10 </span> &nbsp;من‌ گفتم‌: «اینك‌ در فیروزی‌ ایام‌ خود به‌ درهای‌ هاویه‌ می‌روم‌ و از بقیه‌ سالهای‌ خود محروم‌ می‌شوم‌.",
			"11": "<span>11 </span> &nbsp;گفتم‌: خداوند را مشاهده‌ نمی‌نمایم‌. خداوند را در زمین‌ زندگان‌ نخواهم‌ دید. من‌ با ساكنان‌ عالم‌ فنا انسان‌ را دیگر نخواهم‌ دید.",
			"12": "<span>12 </span> &nbsp;خانه‌ من‌ كنده‌ گردید و مثل‌ خیمه‌ شبان‌ از من‌ برده‌ شد. مثل‌ نسّاج‌ عمر خود را پیچیدم‌. او مرا از نورد خواهد برید. روز و شب‌ مرا تمام‌ خواهی‌ كرد.",
			"13": "<span>13 </span> &nbsp;تا صبح‌ انتظار كشیدم‌. مثل‌ شیر همچنین‌ تمامی‌ استخوانهایم‌ را می‌شكند. روز و شب‌ مرا تمام‌ خواهی‌ كرد.",
			"14": "<span>14 </span> &nbsp;مثل‌ پرستوك‌ كه‌ جیك‌ جیك‌ می‌كند صدا می‌نمایم‌. و مانند فاخته‌ ناله‌ می‌كنم‌ و چشمانم‌ از نگریستن‌ به‌ بالا ضعیف‌ می‌شود. ای‌ خداوند در تنگی‌ هستم‌. كفیل‌ من‌ باش‌.",
			"15": "<span>15 </span> &nbsp;«چه‌ بگویم‌ چونكه‌ او به‌ من‌ گفته‌ است‌ و خود او كرده‌ است‌. تمامی‌ سالهای‌ خود را به‌ سبب‌ تلخی‌ جانم‌ آهسته‌ خواهم‌ رفت‌.",
			"16": "<span>16 </span> &nbsp;ای‌ خداوند به‌ این‌ چیزها مردمان‌ زیست‌ می‌كنند و به‌اینها و بس‌ حیات‌ روح‌ من‌ می‌باشد. پس‌ مرا شفا بده‌ و مرا زنده‌ نگاه‌ دار.",
			"17": "<span>17 </span> &nbsp;اینك‌ تلخی‌ سخت‌ من‌ باعث‌ سلامتی‌ من‌ شد. از راه‌ لطف‌ جانم‌ را از چاه‌ هلاكت‌ برآوردی‌ زیرا كه‌ تمامی‌ گناهانم‌ را به‌ پشت‌ سر خود انداختی‌.",
			"18": "<span>18 </span> &nbsp;زیرا كه‌ هاویه‌ تو را حمد نمی‌گوید و موت‌ تو را تسبیح‌ نمی‌خواند و آنانی‌ كه‌ به‌ حفره‌ فرو می‌روند به‌ امانت‌ تو امیدوار نمی‌باشند.",
			"19": "<span>19 </span> &nbsp;زندگانند، زندگانند كه‌ تو را حمد می‌گویند، چنانكه‌ من‌ امروز می‌گویم‌. پدران‌ به‌ پسران‌ راستی‌ تو را تعلیم‌ خواهند داد.",
			"20": "<span>20 </span> &nbsp;خداوند به‌ جهت‌ نجات‌ من‌ حاضر است‌، پس‌ سرودهایم‌ را در تمامی‌ روزهای‌ عمر خود در خانه‌ خداوند خواهیم‌ سرایید.»",
			"21": "<span>21 </span> &nbsp;و اشعیا گفته‌ بود كه‌ «قرصی‌ از انجیر بگیرید و آن‌ را بر دمل‌ بنهید كه‌ شفا خواهد یافت‌.»",
			"22": "<span>22 </span> &nbsp;و حزقیا گفته‌ بود: «علامتی‌ كه‌ به‌ خانه‌ خداوند برخواهم‌ آمد چیست‌؟»"
		},
		"39": {
			"1": "<span>1 </span> &nbsp;در آن‌ زمان‌ مَرُودَك‌ بلدان‌ بن‌ بلدان پادشاه‌ بابل‌ مكتوبی‌ و هدیه‌ای‌ نزد حزقیا فرستاد زیرا شنیده‌ بود كه‌ بیمار شده‌ و صحّت‌ یافته‌ است‌.",
			"2": "<span>2 </span> &nbsp;و حزقیا از ایشان‌ مسرور شده‌، خانه‌ خزاین‌ خود را از نقره‌ و طلا و عطریات‌ و روغن‌ معطّر و تمام‌ خانه‌ اسلحه‌ خویش‌ و هرچه‌ را كه‌ در خزاین‌ او یافت‌ می‌شد به‌ ایشان‌ نشان‌ داد و در خانه‌اش‌ و در تمامی‌ مملكتش‌ چیزی‌ نبود كه‌ حزقیا آن‌ را به‌ ایشان‌ نشان‌ نداد.",
			"3": "<span>3 </span> &nbsp;پس‌ اشعیا نبی‌ نزد حزقیا پادشاه‌ آمده‌، وی‌ را گفت‌: «این‌ مردمان‌ چه‌ گفتند و نزد تو از كجا آمدند؟» حزقیا گفت‌: «از جای‌ دور یعنی‌ از بابل‌ نزد من‌ آمدند.»",
			"4": "<span>4 </span> &nbsp;او گفت‌: «در خانه‌ تو چه‌ دیدند؟» حزقیا گفت‌:«هرچه‌ در خانه‌ من‌ است‌ دیدند و چیزی‌ در خزاین‌ من‌ نیست‌ كه‌ به‌ ایشان‌ نشان‌ ندادم‌.»",
			"5": "<span>5 </span> &nbsp;پس‌ اشعیا به‌ حزقیا گفت‌: «كلام‌ یهوه‌ صبایوت‌ را بشنو:",
			"6": "<span>6 </span> &nbsp;اینك‌ روزها می‌آید كه‌ هرچه‌ در خانه‌ تو است‌ و آنچه‌ پدرانت‌ تا امروز ذخیره‌ كرده‌اند به‌ بابل‌ برده‌ خواهد شد. و خداوند می‌گوید كه‌ چیزی‌ از آنها باقی‌ نخواهد ماند.",
			"7": "<span>7 </span> &nbsp;و بعضی‌ از پسرانت‌ را كه‌ از تو پدید آیند و ایشان‌ را تولید نمایی‌ خواهند گرفت‌ و در قصر پادشاه‌ بابل‌ خواجه‌سرا خواهند شد.»",
			"8": "<span>8 </span> &nbsp;حزقیا به‌ اشعیا گفت‌: «كلام‌ خداوند كه‌ گفتی‌ نیكو است‌ و دیگر گفت‌: هرآینه‌ در ایام‌ من‌ سلامتی‌ و امان‌ خواهد بود.»"
		},
		"40": {
			"1": "<span>1 </span> &nbsp;تسلّی‌ دهید! قوم‌ مرا تسلّی‌ دهید! خدای‌ شما می‌گوید:",
			"2": "<span>2 </span> &nbsp;سخنان‌ دلاویز به‌ اورشلیم‌ گویید و اورا ندا كنید كه‌ اجتهاد او تمام‌ شده‌ و گناه‌ وی‌ آمرزیده‌ گردیده‌، و از دست‌ خداوند برای‌ تمامی‌ گناهانش‌ دو چندان‌ یافته‌ است‌.",
			"3": "<span>3 </span> &nbsp;صدای‌ ندا كننده‌ای‌ در بیابان‌، راه‌ خداوند را مهیا سازید و طریقی‌ برای‌ خدای‌ ما در صحرا راست‌ نمایید.",
			"4": "<span>4 </span> &nbsp;هر درّه‌ای‌ برافراشته‌ و هر كوه‌ و تلّی‌ پست‌ خواهد شد؛ و كجیها راست‌ و ناهمواریها هموار خواهد گردید.",
			"5": "<span>5 </span> &nbsp;و جلال‌ خداوند مكشوف‌ گشته‌، تمامی‌ بشر آن‌ را با هم‌ خواهند دید زیرا كه‌ دهان‌ خداوند این‌ را گفته‌ است‌.",
			"6": "<span>6 </span> &nbsp;هاتفی‌ می‌گوید: «ندا كن‌.» وی‌ گفت‌: «چه‌ چیز را ندا كنم‌؟ تمامی‌ بشر گیاه‌ است‌ و همگی‌ زیبایی‌اش‌ مثل‌ گل‌ صحرا.",
			"7": "<span>7 </span> &nbsp;گیاه‌ خشك‌ و گلش‌پژمرده‌ می‌شود زیرا نفخه‌ خداوند بر آن‌ دمیده‌ می‌شود. البتّه‌ مردمان‌ گیاه‌ هستند.",
			"8": "<span>8 </span> &nbsp;گیاه‌ خشك‌ شد و گل‌ پژمرده‌ گردید، لیكن‌ كلام‌ خدای‌ ما تا ابدالا´باد استوار خواهد ماند.»",
			"9": "<span>9 </span> &nbsp;ای‌ صهیون‌ كه‌ بشارت‌ می‌دهی‌ به‌ كوه‌ بلند برآی‌! و ای‌ اورشلیم‌ كه‌ بشارت‌ می‌دهی‌ آوازت‌ را با قوّت‌ بلند كن‌! آن‌ را بلند كن‌ و مترس‌ و به‌ شهرهای‌ یهودا بگو كه‌ «هان‌ خدای‌ شما است‌!»",
			"10": "<span>10 </span> &nbsp;اینك‌ خداوند یهوه‌ با قوّت‌ می‌آید و بازوی‌ وی‌ برایش‌ حكمرانی‌ می‌نماید. اینك‌ اجرت‌ او با وی‌ است‌ و عقوبت‌ وی‌ پیش‌ روی‌ او می‌آید.",
			"11": "<span>11 </span> &nbsp;او مثل‌ شبانْ گله‌ خود را خواهد چرانید و به‌ بازوی‌ خود بره‌ها را جمع‌ كرده‌، به‌ آغوش‌ خویش‌ خواهد گرفت‌ و شیر دهندگان‌ را به‌ ملایمت‌ رهبری‌ خواهد كرد.",
			"12": "<span>12 </span> &nbsp;كیست‌ كه‌ آبها را به‌ كف‌ دست‌ خود پیموده‌ و افلاك‌ را با وجب‌ اندازه‌ كرده‌ و غبار زمین‌ را در كیل‌ گنجانیده‌ و كوهها را به‌ قپان‌ و تلّها را به‌ ترازو وزن‌ نموده‌ است‌؟",
			"13": "<span>13 </span> &nbsp;كیست‌ كه‌ روح‌ خداوند را قانون‌ داده‌ یا مشیر او بوده‌ او را تعلیم‌ داده‌ باشد.",
			"14": "<span>14 </span> &nbsp;او از كه‌ مشورت‌ خواست‌ تا به‌ او فهم‌ بخشد و طریق‌ راستی‌ را به‌ او بیاموزد؟ و كیست‌ كه‌ او را معرفت‌ آموخت‌ و راه‌ فطانت‌ را به‌ او تعلیم‌ داد؟",
			"15": "<span>15 </span> &nbsp;اینك‌ امّت‌ها مثل‌ قطره‌ دلو و مانند غبار میزان‌ شمرده‌ می‌شوند. اینك‌ جزیره‌ها را مثل‌ گَرْد برمی‌دارد.",
			"16": "<span>16 </span> &nbsp;و لبنان‌ به‌ جهت‌ هیزم‌ كافی‌ نیست‌ و حیواناتش‌ برای‌ قربانی‌ سوختنی‌ كفایت‌ نمی‌كند.",
			"17": "<span>17 </span> &nbsp;تمامی‌ امّت‌ها بنظر وی‌ هیچ‌اند و از عدم‌ و بطالت‌ نزد وی‌ كمتر می‌نمایند.",
			"18": "<span>18 </span> &nbsp;پس‌ خدا را به‌ كه‌ تشبیه‌ می‌كنید وكدام‌ شبه‌ را با او برابر می‌توانید كرد؟",
			"19": "<span>19 </span> &nbsp;صنعتگر بت‌ را می‌ریزد و زرگر آن‌ را به‌ طلا می‌پوشاند، و زنجیرهای‌ نقره‌ برایش‌ می‌ریزد.",
			"20": "<span>20 </span> &nbsp;كسی‌ كه‌ استطاعت‌ چنین‌ هدایا نداشته‌ باشد، درختی‌ را كه‌ نمی‌پوسد اختیار می‌كند و صنعتگر ماهری‌ را می‌طلبد تا بتی‌ را كه‌ متحرّك‌ نشود برای‌ او بسازد.",
			"21": "<span>21 </span> &nbsp;آیا ندانسته‌ ونشنیده‌اید و از ابتدا به‌ شما خبر داده‌ نشده‌ است‌ و از بنیاد زمین‌ نفهمیده‌اید؟",
			"22": "<span>22 </span> &nbsp;او است‌ كه‌ بر كره‌ زمین‌ نشسته‌ است‌ و ساكنانش‌ مثل‌ ملخ‌ می‌باشند. اوست‌ كه‌ آسمانها را مثل‌ پرده‌ می‌گستراند و آنها را مثل‌ خیمه‌ به‌ جهت‌ سكونت‌ پهن‌ می‌كند.",
			"23": "<span>23 </span> &nbsp;كه‌ امیران‌ را لاشی‌ می‌گرداند و داوران‌ جهان‌ را مانند بطالت‌ می‌سازد.",
			"24": "<span>24 </span> &nbsp;هنوز غرس‌ نشده‌ و كاشته‌ نگردیده‌اند و تنه‌ آنها هنوز در زمین‌ ریشه‌ نزده‌ است‌، كه‌ فقط‌ بر آنها می‌دمد و پژمرده‌ می‌شوند و گرد باد آنها را مثل‌ كاه‌ می‌رباید.",
			"25": "<span>25 </span> &nbsp;پس‌ مرا به‌ كه‌ تشبیه‌ می‌كنید تا با وی‌ مساوی‌ باشم‌؟ قدّوس‌ می‌گوید:",
			"26": "<span>26 </span> &nbsp;چشمان‌ خود را به‌ علیین‌ برافراشته‌ ببینید. كیست‌ كه‌ اینها را آفرید و كیست‌ كه‌ لشكر اینها را بشماره‌ بیرون‌ آورده‌، جمیع‌ آنها را بنام‌ می‌خواند؟ از كثرت‌ قوّت‌ و از عظمت‌ توانایی‌ وی‌ یكی‌ از آنها گم‌ نخواهد شد.",
			"27": "<span>27 </span> &nbsp;ای‌ یعقوب‌ چرا فكر می‌كنی‌ و ای‌ اسرائیل‌ چرا می‌گویی‌: «راه‌ من‌ از خداوند مخفی‌ است‌ و خدای‌ من‌ انصاف‌ مرا از دست‌ داده‌ است‌.»",
			"28": "<span>28 </span> &nbsp;آیا ندانسته‌ و نشنیده‌ای‌ كه‌ خدای‌ سرمدی‌ یهوه‌ آفریننده‌ اقصای‌ زمین‌ درمانده‌ و خسته‌ نمی‌شود و فهم‌ او را تفحّص‌ نتوان‌ كرد؟",
			"29": "<span>29 </span> &nbsp;ضعیفان‌ را قوّت‌ می‌بخشد و ناتوانان‌ را قدرت‌زیاده‌ عطا می‌نماید.",
			"30": "<span>30 </span> &nbsp;حتّی‌ جوانان‌ هم‌ درمانده‌ و خسته‌ می‌گردند و شجاعان‌ بكلّی‌ می‌افتند.",
			"31": "<span>31 </span> &nbsp;امّا آنانی‌ كه‌ منتظر خداوند می‌باشند قوّت‌ تازه‌ خواهند یافت‌ و مثل‌ عقاب‌ پرواز خواهند كرد. خواهند دوید و خسته‌ نخواهند شد. خواهند خرامید و درمانده‌ نخواهند گردید."
		},
		"41": {
			"1": "<span>1 </span> &nbsp;ای‌ جزیره‌ها به‌ حضور من‌ خاموش‌شوید! و قبیله‌ها قوّت‌ تازه‌ بهم‌ رسانند! نزدیك‌ بیایند، آنگاه‌ تكلّم‌ نمایند. با هم‌ به‌ محاكمه‌ نزدیك‌ بیاییم‌.",
			"2": "<span>2 </span> &nbsp;كیست‌ كه‌ كسی‌ را از مشرق‌ برانگیخت‌ كه‌ عدالت‌ او را نزد پایهای‌ وی‌ می‌خواند؟ امّت‌ها را به‌ وی‌ تسلیم‌ می‌كند و او را بر پادشاهان‌ مسلّط‌ می‌گرداند. و ایشان‌ را مثل‌ غبار به‌ شمشیر وی‌ و مثل‌ كاه‌ كه‌ پراكنده‌ می‌گردد به‌ كمان‌ وی‌ تسلیم‌ خواهد نمود.",
			"3": "<span>3 </span> &nbsp;ایشان‌ را تعاقب‌ نموده‌، به‌ راهی‌ كه‌ با پایهای‌ خود نرفته‌ بود، بسلامتی‌ خواهد گذشت‌.",
			"4": "<span>4 </span> &nbsp;كیست‌ كه‌ این‌ را عمل‌ نموده‌ و بجا آورده‌، و طبقات‌ را از ابتدا دعوت‌ نموده‌ است‌؟ من‌ كه‌ یهوه‌ و اوّل‌ و با آخرین‌ می‌باشم‌ من‌ هستم‌.",
			"5": "<span>5 </span> &nbsp;جزیره‌ها دیدند و ترسیدند و اقصای‌ زمین‌ بلرزیدند و تقرّب‌ جسته‌، آمدند.",
			"6": "<span>6 </span> &nbsp;هر كس‌ همسایه‌ خود را اعانت‌ كرد و به‌ برادر خود گفت‌: قوی دل‌ باش.",
			"7": "<span>7 </span> &nbsp;نجّار زرگر را و آنكه‌ با چكّش‌ صیقل‌ می‌كند سندان‌ زننده‌ را تقویت‌ می‌نماید و در باره‌ لحیم‌ می‌گوید: كه‌ خوب‌ است‌ و آن‌ را به‌ میخها محكم‌ می‌سازد تا متحرّك‌ نشود.",
			"8": "<span>8 </span> &nbsp;امّا تو ای‌ اسرائیل‌ بنده‌ من‌ و ای‌ یعقوب‌ كه‌ تو را برگزیده‌ام‌ و ای‌ ذریت‌ دوست‌ من‌ ابراهیم‌،",
			"9": "<span>9 </span> &nbsp;كه‌تو را از اقصای‌ زمین‌ گرفته‌، تو را از كرانه‌هایش‌ خوانده‌ام‌ و به‌ تو گفته‌ام‌ تو بنده‌ من‌ هستی‌، تو را برگزیدم‌ و ترك‌ ننمودم‌،",
			"10": "<span>10 </span> &nbsp;مترس‌ زیرا كه‌ من‌ با تو هستم‌ و مشوّش‌ مشو زیرا من‌ خدای‌ تو هستم‌! تو را تقویت‌ خواهم‌ نمود و البتّه‌ تو را معاونت‌ خواهم‌ داد و تو را به‌ دست‌ راست‌ عدالت‌ خود دستگیری‌ خواهم‌ كرد.",
			"11": "<span>11 </span> &nbsp;اینك‌ همه‌ آنانی‌ كه‌ بر تو خشم‌ دارند، خجل‌ و رسوا خواهند شد و آنانی‌ كه‌ با تو معارضه‌ نمایند ناچیز شده‌، هلاك‌ خواهند گردید.",
			"12": "<span>12 </span> &nbsp;آنانی‌ را كه‌ با تو مجادله‌ نمایند جستجو كرده‌، نخواهی‌ یافت‌ و آنانی‌ كه‌ با تو جنگ‌ كنند نیست‌ و نابود خواهند شد.",
			"13": "<span>13 </span> &nbsp;زیرا من‌ كه‌ یهوه‌ خدای‌ تو هستم‌ دست‌ راست‌ تو را گرفته‌، به‌ تو می‌گویم‌: مترس‌ زیرا من‌ تو را نصرت‌ خواهم‌ داد.",
			"14": "<span>14 </span> &nbsp;ای‌ كِرْم‌ یعقوب‌ و شَرذِمّه‌ اسرائیل‌ مترس‌ زیرا خداوند و قدّوس‌ اسرائیل‌ كه‌ ولّی‌ تو می‌باشد می‌گوید: من‌ تو را نصرت‌ خواهم‌ داد.",
			"15": "<span>15 </span> &nbsp;اینك‌ تو را گردون‌ تیز نو دندانه‌دار خواهم‌ ساخت‌ و كوهها را پایمال‌ كرده‌، خرد خواهی‌ نمود و تلّها را مثل‌ كاه‌ خواهی‌ ساخت‌.",
			"16": "<span>16 </span> &nbsp;آنها را خواهی‌ افشاند و باد آنها را برداشته‌، گردباد آنها را پراكنده‌ خواهد ساخت‌. لیكن‌ تو از خداوند شادمان‌ خواهی‌ شد و به‌ قدّوس‌ اسرائیل‌ فخر خواهی‌ نمود.",
			"17": "<span>17 </span> &nbsp;فقیران‌ و مسكینان‌ آب‌ را می‌جویند و نمی‌یابند و زبان‌ ایشان‌ از تشنگی‌ خشك‌ می‌شود. من‌ كه‌ یهوه‌ هستم‌ ایشان‌ را اجابت‌ خواهم‌ نمود. خدای‌ اسرائیل‌ هستم‌ ایشان‌ را ترك‌ نخواهم‌ كرد.",
			"18": "<span>18 </span> &nbsp;بر تلّهای‌ خشك‌ نهرها و در میان‌ وادیها چشمه‌ها جاری‌ خواهم‌ نمود. و بیابان‌ را به‌ بركه‌ آب‌ و زمین‌ خشك‌ را به‌ چشمه‌های‌ آب‌ مبدّل‌ خواهم‌ ساخت‌.",
			"19": "<span>19 </span> &nbsp;در بیابان‌ سرو آزاد و شطیم‌ وآس‌ و درخت‌ زیتون‌ را خواهم‌ گذاشت‌ و در صحرا صنوبر و كاج‌ و چنار را با هم‌ غرس‌ خواهم‌ نمود.",
			"20": "<span>20 </span> &nbsp;تا ببینند و بدانند و تفكّر نموده‌، با هم‌ تأمّل‌ نمایند كه‌ دست‌ خداوند این‌ را كرده‌ و قدّوس‌ اسرائیل‌ این‌ را ایجاد نموده‌ است‌.",
			"21": "<span>21 </span> &nbsp;خداوند می‌گوید: دعوی‌ خود را پیش‌ آورید و پادشاه‌ یعقوب‌ می‌گوید: براهین‌ قوّی‌ خویش‌ را عرضه‌ دارید.",
			"22": "<span>22 </span> &nbsp;آنچه‌ را كه‌ واقع‌ خواهد شد نزدیك‌ آورده‌، برای‌ ما اعلام‌ نمایند. چیزهای‌ پیشین‌ را و كیفیت‌ آنها را بیان‌ كنید تا تفكّر نموده‌، آخر آنها را بدانیم‌ یا چیزهای‌ آینده‌ را به‌ ما بشنوانید.",
			"23": "<span>23 </span> &nbsp;و چیزها را كه‌ بعد از این‌ واقع‌ خواهد شد بیان‌ كنید تا بدانیم‌ كه‌ شما خدایانید. باری‌، نیكویی‌ یا بدی‌ را بجا آورید تا ملتفت‌ شده‌، با هم‌ ملاحظه‌ نماییم‌.",
			"24": "<span>24 </span> &nbsp;اینك‌ شما ناچیز هستید و عمل‌ شما هیچ‌ است‌ و هر كه‌ شما را اختیار كند رجس‌ است‌.",
			"25": "<span>25 </span> &nbsp;كسی‌ را از شمال‌ برانگیختم‌ و او خواهد آمد و كسی‌ را از مشرق‌ آفتاب‌ كه‌ اسم‌ مرا خواهد خواند و او بر سروران‌ مثل‌ برگِلْ خواهد آمد و مانند كوزه‌گری‌ كه‌ گِل‌ را پایمال‌ می‌كند.",
			"26": "<span>26 </span> &nbsp;كیست‌ كه‌ از ابتدا خبر داد تا بدانیم‌، و از قبل‌ تا بگوییم‌ كه‌ او راست‌ است‌؟ البتّه‌ خبر دهنده‌ای‌ نیست‌ و اعلام‌ كننده‌ای‌ نی‌ و كسی‌ هم‌ نیست‌ كه‌ سخنان‌ شما را بشنود.",
			"27": "<span>27 </span> &nbsp;اوّل‌ به‌ صهیون‌ گفتم‌ كه‌ اینك‌ هان‌ این‌ چیزها (خواهد رسید) و به‌ اورشلیم‌ بشارت‌ دهنده‌ای‌ بخشیدم‌.",
			"28": "<span>28 </span> &nbsp;و نگریستم‌ و كسی‌ یافت‌ نشد و در میان‌ ایشان‌ نیز مشورت‌ دهنده‌ای‌ نبود كه‌ چون‌ از ایشان‌ سؤال‌ نمایم‌ جواب‌ تواند داد.",
			"29": "<span>29 </span> &nbsp;اینك‌ جمیع‌ ایشان‌ باطلند و اعمال‌ ایشان‌ هیچ‌ است‌ و بتهای‌ ریخته‌ شده‌ ایشان‌ باد و بطالت‌ است‌."
		},
		"42": {
			"1": "<span>1 </span> &nbsp;اینك‌ بنده‌ من‌ كه‌ او را دستگیری‌ نمودم ‌و برگزیده‌ من‌ كه‌ جانم‌ از او خشنود است‌، من‌ روح‌ خود را بر او می‌نهم‌ تا انصاف‌ را برای‌ امّت‌ها صادر سازد.",
			"2": "<span>2 </span> &nbsp;او فریاد نخواهد زد و آواز خود را بلند نخواهد نمود و آن‌ را در كوچه‌ها نخواهد شنوانید.",
			"3": "<span>3 </span> &nbsp;نی‌ خرد شده‌ را نخواهد شكست‌ و فتیله‌ ضعیف‌ را خاموش‌ نخواهد ساخت‌ تا عدالت‌ را به‌ راستی‌ صادر گرداند.",
			"4": "<span>4 </span> &nbsp;او ضعیف‌ نخواهد گردید و منكسر نخواهد شد تا انصاف‌ را بر زمین‌ قرار دهد و جزیره‌ها منتظر شریعت‌ او باشند.",
			"5": "<span>5 </span> &nbsp;خدا یهوه‌ كه‌ آسمانها را آفرید و آنها را پهن‌ كرد و زمین‌ و نتایج‌ آن‌ را گسترانید و نفس‌ را به‌ قومی‌ كه‌ در آن‌ باشند و روح‌ را بر آنانی‌ كه‌ در آن‌ سالكند می‌دهد، چنین‌ می‌گوید:",
			"6": "<span>6 </span> &nbsp;«من‌ كه‌ یهوه‌ هستم‌ تو را به‌ عدالت‌ خوانده‌ام‌ و دست‌ تو را گرفته‌، تو را نگاه‌ خواهم‌ داشت‌ و تو را عهد قوم‌ و نور امّت‌ها خواهم‌ گردانید.",
			"7": "<span>7 </span> &nbsp;تا چشمان‌ كوران‌ را بگشایی‌ و اسیران‌ را از زندان‌ و نشینندگان‌ در ظلمت‌ را از محبس‌ بیرون‌ آوری‌.",
			"8": "<span>8 </span> &nbsp;من‌ یهوه‌ هستم‌ و اسم‌ من‌ همین‌ است‌. و جلال‌ خود را به‌ كسی‌ دیگر و ستایش‌ خویش‌ را به‌ بتهای‌ تراشیده‌ نخواهم‌ داد.",
			"9": "<span>9 </span> &nbsp;اینك‌ وقایع‌ نخستین‌ واقع‌ شد و من‌ از چیزهای‌ نو اعلام‌ می‌كنم‌ و قبل‌ از آنكه‌ بوجود آید شما را از آنها خبر می‌دهم‌.»",
			"10": "<span>10 </span> &nbsp;ای‌ شما كه‌ به‌ دریا فرود می‌روید، و ای‌آنچه‌ در آن‌ است‌! ای‌ جزیره‌ها و ساكنان‌ آنها سرود نو را به‌ خداوند و ستایش‌ وی‌ را از اقصای‌ زمین‌ بسرایید!",
			"11": "<span>11 </span> &nbsp;صحرا و شهرهایش‌ و قریه‌هایی‌ كه‌ اهل‌ قیدار در آنها ساكن‌ باشند آواز خود را بلند نمایند و ساكنان‌ سالع‌ ترنّم‌ نموده‌، از قله‌ كوهها نعره‌ زنند!",
			"12": "<span>12 </span> &nbsp;برای‌ خداوند جلال‌ را توصیف‌ نمایند و تسبیح‌ او را در جزیره‌ها بخوانند!",
			"13": "<span>13 </span> &nbsp;خداوند مثل‌ جبّار بیرون‌ می‌آید و مانند مرد جنگی‌ غیرت‌ خویش‌ را برمی‌انگیزاند. فریاد كرده‌، نعره‌ خواهد زد و بر دشمنان‌ خویش‌ غلبه‌ خواهد نمود.",
			"14": "<span>14 </span> &nbsp;از زمان‌ قدیم‌ خاموش‌ و ساكت‌ مانده‌، خودداری‌ نمودم‌. الا´ن‌ مثل‌ زنی‌ كه‌ می‌زاید، نعره‌ خواهم‌ زد و دم‌ زده‌، آه‌ خواهم‌ كشید.",
			"15": "<span>15 </span> &nbsp;كوهها و تلّها را خراب‌ كرده‌، تمامی‌ گیاه‌ آنها را خشك‌ خواهم‌ ساخت‌ و نهرها را جزایر خواهم‌ گردانید و بركه‌ها را خشك‌ خواهم‌ ساخت‌.",
			"16": "<span>16 </span> &nbsp;و كوران‌ را به‌ راهی‌ كه‌ ندانسته‌اند رهبری‌ نموده‌، ایشان‌ را به‌ طریق‌هایی‌ كه‌ عارف‌ نیستند هدایت‌ خواهم‌ نمود. ظلمت‌ را پیش‌ ایشان‌ به‌ نور و كجی‌ را به‌ راستی‌ مبدّل‌ خواهم‌ ساخت‌. این‌ كارها را بجا آورده‌، ایشان‌ را رها نخواهم‌ نمود.",
			"17": "<span>17 </span> &nbsp;آنانی‌ كه‌ بر بتهای‌ تراشیده‌ اعتماد دارند و به‌ اصنام‌ ریخته‌ شده‌ می‌گویند كه‌ خدایان‌ ما شمایید، به‌ عقب‌ برگردانیده‌، بسیار خجل‌ خواهند شد.",
			"18": "<span>18 </span> &nbsp;ای‌ كران‌ بشنوید و ای‌ كوران‌ نظر كنید تا ببینید.",
			"19": "<span>19 </span> &nbsp;كیست‌ كه‌ مثل‌ بنده‌ من‌ كور باشد و كیست‌ كه‌ كر باشد مثل‌ رسول‌ من‌ كه‌ می‌فرستم‌؟كیست‌ كه‌ كور باشد، مثل‌ مُسلِم‌ من‌ و كور مانند بنده‌ خداوند ؟",
			"20": "<span>20 </span> &nbsp;چیزهای‌ بسیار می‌بینی‌ امّا نگاه‌ نمی‌داری‌. گوشها را می‌گشاید لیكن‌ خود نمی‌شنود.",
			"21": "<span>21 </span> &nbsp;خداوند را به‌ خاطر عدل‌ خود پسند آمد كه‌ شریعت‌ خویش‌ را تعظیم‌ و تكریم‌ نماید.",
			"22": "<span>22 </span> &nbsp;لیكن‌ اینان‌ قوم‌ غارت‌ و تاراج‌ شده‌اند و جمیع‌ ایشان‌ در حفره‌ها صید شده‌ و در زندانها مخفی‌ گردیده‌اند. ایشان‌ غارت‌ شده‌ و رهاننده‌ای‌ نیست‌. تاراج‌ گشته‌ و كسی‌ نمی‌گوید كه‌ باز ده‌.",
			"23": "<span>23 </span> &nbsp;كیست‌ در میان‌ شما كه‌ به‌ این‌ گوش‌ دهد و توجه‌ نموده‌، برای‌ زمان‌ آینده‌ بشنود؟",
			"24": "<span>24 </span> &nbsp;كیست‌ كه‌ یعقوب‌ را به‌ تاراج‌ و اسرائیل‌ را به‌ غارت‌ تسلیم‌ نمود؟ آیا خداوند نبود كه‌ به‌ او گناه‌ ورزیدیم‌ چونكه‌ ایشان‌ به‌ راههای‌ او نخواستند سلوك‌ نمایند و شریعت‌ او را اطاعت‌ ننمودند؟",
			"25": "<span>25 </span> &nbsp;بنابراین‌ حدّت‌ غضب‌ خود و شدّت‌ جنگ‌ را بر ایشان‌ ریخت‌ و آن‌ ایشان‌ را از هر طرف‌ مشتعل‌ ساخت‌ و ندانستند و ایشان‌ را سوزانید امّا تفكّر ننمودند."
		},
		"43": {
			"1": "<span>1 </span> &nbsp;و الا´ن‌ خداوند كه‌ آفریننده‌ تو ای یعقوب‌، و صانع‌ تو ای‌ اسرائیل‌ است‌ چنین‌ می‌گوید: «مترس‌ زیرا كه‌ من‌ تو را فدیه‌ دادم‌ و تو را به‌ اسمت‌ خواندم‌ پس‌ تو از آن‌ من‌ هستی‌.",
			"2": "<span>2 </span> &nbsp;چون‌ از آبها بگذری‌ من‌ با تو خواهم‌ بود و چون‌ از نهرها (عبورنمایی‌) تو را فرونخواهند گرفت‌. و چون‌ از میان‌ آتش‌ رَوی‌، سوخته‌ نخواهی‌ شد و شعله‌اش‌ تو را نخواهد سوزانید.",
			"3": "<span>3 </span> &nbsp;زیرا من‌ یهوه‌ خدای‌ تو و قدّوس‌ اسرائیل‌ نجات‌ دهنده‌ تو هستم‌. مصر را فدیه‌ تو ساختم‌ و حبش‌ و سبا را به‌ عوض‌ تو دادم‌.",
			"4": "<span>4 </span> &nbsp;چونكه‌ در نظر من‌ گرانبها ومكرّم‌ بودی‌ و من‌ تو را دوست‌ می‌داشتم‌ پس‌ مردمان‌ را به‌ عوض‌ تو و طوایف‌ را در عوض‌ جان‌ تو تسلیم‌ خواهم‌ نمود.",
			"5": "<span>5 </span> &nbsp;مترس‌ زیرا كه‌ من‌ با تو هستم‌ و ذریت‌ تو را از مشرق‌ خواهم‌ آورد و تو را از مغرب‌ جمع‌ خواهم‌ نمود.",
			"6": "<span>6 </span> &nbsp;به‌ شمال‌ خواهم‌ گفت‌ كه‌ \"بده‌\" و به‌ جنوب‌ كه‌ \"ممانعت‌ مكن‌\". پسران‌ مرا از جای‌ دور و دخترانم‌ را از كرانهای‌ زمین‌ بیاور.",
			"7": "<span>7 </span> &nbsp;یعنی‌ هر كه‌ را به‌ اسم‌ من‌ نامیده‌ شود و او را به‌ جهت‌ جلال‌ خویش‌ آفریده‌ و او را مصوّر نموده‌ و ساخته‌ باشم‌.»",
			"8": "<span>8 </span> &nbsp;قومی‌ را كه‌ چشم‌ دارند امّا كور هستند و گوش‌ دارند امّا كر می‌باشند بیرون‌ آور.",
			"9": "<span>9 </span> &nbsp;جمیع‌ امّت‌ها با هم‌ جمع‌ شوند و قبیله‌ها فراهم‌ آیند. پس‌ در میان‌ آنها كیست‌ كه‌ از این‌ خبر دهد و امور اوّلین‌ را به‌ ما اعلام‌ نماید؟ شهود خود را بیاورند تا تصدیق‌ شوند یا استماع‌ نموده‌، اقرار بكنند كه‌ این‌ راست‌ است‌.",
			"10": "<span>10 </span> &nbsp;یهوه‌ می‌گوید كه‌ «شما و بنده‌ من‌ كه‌ او را برگزیده‌ام‌ شهود من‌ می‌باشید. تا دانسته‌، به‌ من‌ ایمان‌ آورید و بفهمید كه‌ من‌ او هستم‌ و پیش‌ از من‌ خدایی‌ مصوّر نشده‌ و بعد از من‌ هم‌ نخواهد شد.",
			"11": "<span>11 </span> &nbsp;من‌، من‌ یهوه‌ هستم‌ و غیر از من‌ نجات‌دهنده‌ای‌ نیست‌.",
			"12": "<span>12 </span> &nbsp;من‌ اخبار نموده‌ و نجات‌ داده‌ام‌ و اعلام‌ نموده‌ و درمیان‌ شما خدای‌ غیر نبوده‌ است‌. خداوند می‌گوید كه‌ شما شهود من‌ هستید و من‌ خدا هستم‌.",
			"13": "<span>13 </span> &nbsp;و از امروز نیز من‌ او می‌باشم‌ و كسی‌ كه‌ از دست‌ من‌ تواند رهانید نیست‌. من‌ عمل‌ خواهم‌ نمود و كیست‌ كه‌ آن‌ را ردّ نماید؟»",
			"14": "<span>14 </span> &nbsp;خداوند كه‌ ولّی‌ شما و قدّوس‌ اسرائیل‌ است‌ چنین‌ می‌گوید: «بخاطر شما به‌ بابل‌ فرستادم‌ و همه‌ ایشان‌ را مثل‌ فراریان‌ فرود خواهم‌ آورد و كلدانیان‌ را نیز در كشتیهای‌ وَجْدِ ایشان‌.",
			"15": "<span>15 </span> &nbsp;من‌ خداوند قدّوس‌ شما هستم‌. آفریننده‌ اسرائیل‌ وپادشاه‌ شما.»",
			"16": "<span>16 </span> &nbsp;خداوند كه‌ راهی‌ در دریا و طریقی‌ در آبهای‌ عظیم‌ می‌سازد چنین‌ می‌گوید،",
			"17": "<span>17 </span> &nbsp;آنكه‌ ارابه‌ها و اسبها و لشكر و قوّت‌ آن‌ را بیرون‌ می‌آورد: «ایشان‌ با هم‌ خواهند خوابید و نخواهند برخاست‌ و منطفی‌ شده‌، مثل‌ فتیله‌ خاموش‌ خواهند شد.",
			"18": "<span>18 </span> &nbsp;چیزهای‌ اوّلین‌ را بیاد نیاورید و در امور قدیم‌ تفكّر ننمایید.",
			"19": "<span>19 </span> &nbsp;اینك‌ من‌ چیز نویی‌ بوجود می‌آورم‌ و آن‌ الا´ن‌ بظهور می‌آید. آیا آن‌ را نخواهید دانست‌؟ بدرستی‌ كه‌ راهی‌ در بیابان‌ و نهرها در هامون‌ قرار خواهم‌ داد.",
			"20": "<span>20 </span> &nbsp;حیوانات‌ صحرا گرگان‌ و شترمرغها مرا تمجید خواهند نمود چونكه‌ آب‌ در بیابان‌ و نهرها در صحرا بوجود می‌آورم‌ تا قوم‌ خود و برگزیدگان‌ خویش‌ را سیراب‌ نمایم.",
			"21": "<span>21 </span> &nbsp;این‌ قوم‌ را برای‌ خود ایجاد كردم‌ تا تسبیح‌ مرا بخوانند.",
			"22": "<span>22 </span> &nbsp;امّا تو ای‌ یعقوب‌ مرا نخواندی‌ و تو ای‌ اسرائیل‌ از من‌ به‌ تنگ‌ آمدی‌!",
			"23": "<span>23 </span> &nbsp;گوسفندان‌ قربانی‌های‌ سوختنی‌ خود را برای‌ من‌ نیاوردی‌ و به‌ ذبایح‌ خود مرا تكریم‌ ننمودی‌! به‌ هدایا بندگی‌ بر تو ننهادم‌ و به‌ بخور تو را به‌ تنگ‌ نیاوردم‌.",
			"24": "<span>24 </span> &nbsp;نی‌ معطّر را به‌ جهت‌ من‌ به‌ نقره‌ نخریدی‌ و به‌ پیه‌ ذبایح‌ خویش‌ مرا سیر نساختی‌. بلكه‌ به‌ گناهان‌ خود بر من‌ بندگی‌ نهادی‌ و به‌ خطایای‌ خویش‌ مرا به‌ تنگ‌ آوردی‌.",
			"25": "<span>25 </span> &nbsp;من‌ هستم‌ من‌ كه‌ بخاطر خود خطایای‌ تو را محو ساختم‌ و گناهان‌ تو را بیاد نخواهم‌ آورد.",
			"26": "<span>26 </span> &nbsp;مرا یاد بده‌ تا با هم‌ محاكمه‌ نماییم‌. حجّت‌ خود را بیاور تا تصدیق‌ شوی‌.",
			"27": "<span>27 </span> &nbsp;اجداد اوّلین‌ تو گناه‌ ورزیدند و واسطه‌های‌ تو به‌ من‌ عاصی‌ شدند.",
			"28": "<span>28 </span> &nbsp;بنابراین‌ من‌ سروران‌ قدس‌ را بی‌احترام‌ خواهم‌ساخت‌ و یعقوب‌ را به‌ لعنت‌ و اسرائیل‌ را به‌ دشنام‌ تسلیم‌ خواهم‌ نمود."
		},
		"44": {
			"1": "<span>1 </span> &nbsp;امّا الا´ن‌ ای‌ بنده‌ من‌ یعقوب‌ بشنو و ای اسرائیل‌ كه‌ تو را برگزیده‌ام‌!",
			"2": "<span>2 </span> &nbsp;خداوند كه‌ تو را آفریده‌ و تو را از رحم‌ بسرشت‌ و معاون‌ تو می‌باشد چنین‌ می‌گوید: ای‌ بنده‌ من‌ یعقوب‌ مترس‌! و ای‌ یشُرون‌ كه‌ تو را برگزیده‌ام‌!",
			"3": "<span>3 </span> &nbsp;اینك‌ بر (زمین‌) تشنه‌ آب‌ خواهم‌ ریخت‌ و نهرها برخشك‌. روح‌ خود را بر ذریت‌ تو خواهم‌ ریخت‌ و بركت‌ خویش‌ را بر اولاد تو.",
			"4": "<span>4 </span> &nbsp;و ایشان‌ در میان‌ سبزه‌ها، مثل‌ درختان‌ بید بر جویهای‌ آب‌ خواهند رویید.",
			"5": "<span>5 </span> &nbsp;یكی‌ خواهد گفت‌ كه‌ من‌ از آن‌ خداوند هستم‌ و دیگری‌ خویشتن‌ را به‌ نام‌ یعقوب‌ خواهد خواند و دیگری‌ بدست‌ خود برای‌ خداوند خواهد نوشت‌ و خویشتن‌ را به‌ نام‌ اسرائیل‌ ملقّب‌ خواهد ساخت‌.",
			"6": "<span>6 </span> &nbsp;خداوند پادشاه‌ اسرائیل‌ و یهوه‌ صبایوت‌ كه‌ ولّی‌ ایشان‌ است‌ چنین‌ می‌گوید: من‌ اوّل‌ هستم‌ و من‌ آخر هستم‌ و غیر از من‌ خدایی‌ نیست‌.",
			"7": "<span>7 </span> &nbsp;و مثل‌ من‌ كیست‌ كه‌ آن‌ را اعلان‌ كند و بیان‌ نماید و آن‌ را ترتیب‌ دهد، از زمانی‌ كه‌ قوم‌ قدیم‌ را برقرار نمودم‌. پس‌ چیزهای‌ آینده‌ و آنچه‌ را كه‌ واقع‌ خواهد شد اعلان‌ بنمایند.",
			"8": "<span>8 </span> &nbsp;ترسان‌ و هراسان‌ مباشید. آیا از زمان‌ قدیم‌ تو را اخبار و اعلام‌ ننمودم‌ و آیا شما شهود من‌ نیستید؟ آیا غیر از من‌ خدایی‌ هست‌؟ البتّه‌ صخره‌ای‌ نیست‌ و احدی‌ رانمی‌شناسم‌.",
			"9": "<span>9 </span> &nbsp;آنانی‌ كه‌ بتهای‌ تراشیده‌ می‌سازند، جمیعاً باطلند و چیزهایی‌ كه‌ ایشان‌ می‌پسندند، فایده‌ای‌ ندارد و شهود ایشان‌ نمی‌بینند و نمی‌دانند تا خجالت‌ بكشند.",
			"10": "<span>10 </span> &nbsp;كیست‌ كه‌ خدایی‌ ساخته‌ یا بتی‌ ریخته‌ باشد كه‌ نفعی‌ ندارد؟",
			"11": "<span>11 </span> &nbsp;اینك‌ جمیع‌ یاران‌ او خجل‌ خواهند شد و صنعتگران‌ از انسان‌ می‌باشند. پس‌ جمیع‌ ایشان‌ جمع‌ شده‌، بایستند تا با هم‌ ترسان‌ و خجل‌ گردند.",
			"12": "<span>12 </span> &nbsp;آهن‌ را با تیشه‌ می‌تراشد و آن‌ را در زغال‌ كار می‌كند و با چكّش‌ صورت‌ می‌دهد و با قوّت‌ بازوی‌ خویش‌ آن‌ را می‌سازد و نیز گرسنه‌ شده‌، بی‌قوّت‌ می‌گردد و آب‌ ننوشیده‌، ضعف‌ بهم‌ می‌رساند.",
			"13": "<span>13 </span> &nbsp;چوب‌ را می‌تراشد و ریسمان‌ را كشیده‌، با قلم‌ آن‌ را نشان‌ می‌كند و با رنده‌ آن‌ را صاف‌ می‌سازد و با پرگار نشان‌ می‌كند پس‌ آن‌ را به‌ شبیه‌ انسان‌ و به‌ جمال‌ آدمی‌ می‌سازد تا در خانه‌ ساكن‌ شود.",
			"14": "<span>14 </span> &nbsp;سروهای‌ آزاد برای‌ خود قطع‌ می‌كند و سندیان‌ و بلوط‌ را گرفته‌، آنها را از درختان‌ جنگل‌ برای‌ خود اختیار می‌كند و شمشاد را غرس‌ نموده‌، باران‌ آن‌ را نمّو می‌دهد.",
			"15": "<span>15 </span> &nbsp;پس‌ برای‌ شخص‌ به‌ جهت‌ سوخت‌ بكار می‌آید و از آن‌ گرفته‌، خود را گرم‌ می‌كند و آن‌ را افروخته‌ نان‌ می‌پزد و خدایی‌ ساخته‌، آن‌ را می‌پرستد و از آن‌ بتی‌ ساخته‌، پیش‌ آن‌ سجده‌ می‌كند.",
			"16": "<span>16 </span> &nbsp;بعضی‌ از آن‌ را در آتش‌ می‌سوزاند و بر بعضی‌ گوشت‌ پخته‌ می‌خورد و كباب‌ را برشته‌ كرده‌، سیر می‌شود و گرم‌ شده‌، می‌گوید: وه‌ گرم‌ شده‌، آتش‌ را دیدم‌.",
			"17": "<span>17 </span> &nbsp;و از بقیه‌ آن‌ خدایی‌ یعنی‌ بت‌ خویش‌ را می‌سازد و پیش‌ آن‌ سجده‌ كرده‌، عبادت‌ می‌كند و نزد آن‌ دعا نموده‌، می‌گوید: مرانجات‌ بده‌ چونكه‌ تو خدای‌ من‌ هستی‌.",
			"18": "<span>18 </span> &nbsp;ایشان‌ نمی‌دانند و نمی‌فهمند زیرا كه‌ چشمان‌ ایشان‌ را بسته‌ است‌ تا نبینند و دل‌ ایشان‌ را تا تعقّل‌ ننمایند.",
			"19": "<span>19 </span> &nbsp;و تفكّر ننموده‌، معرفت‌ و فطانتی‌ ندارند تا بگویند نصف‌ آن‌ را در آتش‌ سوختیم‌ و بر زغالش‌ نیز نان‌ پختیم‌ و گوشت‌ را كباب‌ كرده‌، خوردیم‌. پس‌ آیا از بقیه‌ آن‌ بتی‌ بسازیم‌ و به‌ تنه‌ درخت‌ سجده‌ نماییم‌؟",
			"20": "<span>20 </span> &nbsp;خاكستر را خوراك‌ خود می‌سازد و دل‌ فریب‌ خورده‌ او را گمراه‌ می‌كند كه‌ جان‌ خود را نتواند رهانید و فكر نمی‌نماید كه‌ آیا در دست‌ راست‌ من‌ دروغ‌ نیست‌.",
			"21": "<span>21 </span> &nbsp;ای‌ یعقوب‌ و ای‌ اسرائیل‌ اینها را بیادآور چونكه‌ تو بنده‌ من‌ هستی‌. تو را سرشتم‌ ای‌ اسرائیل‌ تو بنده‌ من‌ هستی‌ از من‌ فراموش‌ نخواهی‌ شد.",
			"22": "<span>22 </span> &nbsp;تقصیرهای‌ تو را مثل‌ ابر غلیظ‌ و گناهانت‌ را مانند ابر محو ساختم‌. پس‌ نزد من‌ بازگشت‌ نما زیرا تو را فدیه‌ كرده‌ام‌.",
			"23": "<span>23 </span> &nbsp;ای‌ آسمانها ترنّم‌ نمایید زیرا كه‌ خداوند این‌ را كرده‌ است‌! و ای‌ اسفلهای‌ زمین‌! فریاد برآورید و ای‌ كوهها و جنگلها و هر درختی‌ كه‌ در آنها باشد بسرایید! زیرا خداوند یعقوب‌ را فدیه‌ كرده‌ است‌ و خویشتن‌ را در اسرائیل‌ تمجید خواهد نمود.",
			"24": "<span>24 </span> &nbsp;خداوند كه‌ ولّی‌ تو است‌ و تو را از رحم‌ سرشته‌ است‌ چنین‌ می‌گوید: من‌ یهوه‌ هستم‌ و همه‌ چیز را ساختم‌. آسمانها را به‌ تنهایی‌ گسترانیدم‌ و زمین‌ را پهن‌ كردم‌؛ و با من‌ كه‌ بود؟",
			"25": "<span>25 </span> &nbsp;آنكه‌ آیات‌ كاذبان‌ را باطل‌ می‌سازد و جادوگران‌ را احمق‌ می‌گرداند. و حكیمان‌ را بعقب‌ برمی‌گرداند و علم‌ ایشان‌ را به‌ جهالت‌ تبدیل‌ می‌كند.",
			"26": "<span>26 </span> &nbsp;كه‌ سخنان‌ بندگان‌ خود رابرقرار می‌دارد و مشورت‌ رسولان‌ خویش‌ را به‌ انجام‌ می‌رساند، كه‌ درباره‌ اورشلیم‌ می‌گوید معمور خواهد شد و درباره‌ شهرهای‌ یهودا كه‌ بنا خواهد شد و خرابی‌های‌ آن‌ را برپا خواهم‌ داشت‌.",
			"27": "<span>27 </span> &nbsp;آنكه‌ به‌ لجّه‌ می‌گوید كه‌ خشك‌ شو و نهرهایت‌ را خشك‌ خواهم‌ ساخت‌.",
			"28": "<span>28 </span> &nbsp;و درباره‌ كورش‌ می‌گوید كه‌ او شبان‌ من‌ است‌ و تمامی‌ مسرّت‌ مرا به‌ اتمام‌ خواهد رسانید و درباره‌ اورشلیم‌ می‌گوید بنا خواهد شد و درباره‌ هیكل‌ كه‌ بنیاد تو نهاده‌ خواهد گشت‌."
		},
		"45": {
			"1": "<span>1 </span> &nbsp;خداوند به‌ مسیح‌ خویش‌ یعنی‌ به كورش‌ كه‌: دست‌ راست‌ او را گرفتم‌ تا به‌ حضور وی‌ امّت‌ها را مغلوب‌ سازم‌ و كمرهای‌ پادشاهان‌ را بگشایم‌ تا درها را به‌ حضور وی‌ مفتوح‌ نمایم‌ و دروازه‌ها دیگر بسته‌ نشود چنین‌ می‌گوید",
			"2": "<span>2 </span> &nbsp;كه‌ من‌ پیش‌ روی‌ تو خواهم‌ خرامید و جایهای‌ ناهموار را هموار خواهم‌ ساخت‌. و درهای‌ برنجین‌ را شكسته‌، پشت‌ بندهای‌ آهنین‌ را خواهم‌ برید.",
			"3": "<span>3 </span> &nbsp;و گنجهای‌ ظلمت‌ و خزاین‌ مخفی‌ را به‌ تو خواهم‌ بخشید تا بدانی‌ كه‌ من‌ یهوه‌ كه‌ تو را به‌ اسمت‌ خوانده‌ام‌ خدای‌ اسرائیل‌ می‌باشم‌.",
			"4": "<span>4 </span> &nbsp;به‌ خاطر بنده‌ خود یعقوب‌ و برگزیده‌ خویش‌ اسرائیل‌، هنگامی‌ كه‌ مرا نشناختی‌ تو را به‌ اسمت‌ خواندم‌ و ملقّب‌ ساختم‌.",
			"5": "<span>5 </span> &nbsp;من‌ یهوه‌ هستم‌ و دیگری‌ نیست‌ و غیر از من‌ خدایی‌ نی‌. من‌ كمر تو را بستم‌ هنگامی‌ كه‌ مرا نشناختی‌.",
			"6": "<span>6 </span> &nbsp;تا از مشرق‌ آفتاب‌ و مغرب‌ آن‌ بدانند كه‌ سوای‌ من‌ احدی‌ نیست‌. من‌ یهوه‌ هستم‌ و دیگری‌ نی‌.",
			"7": "<span>7 </span> &nbsp;پدید آورنده‌ نور و آفریننده‌ ظلمت‌. صانع‌ سلامتی‌ و آفریننده‌ بدی‌. من‌ یهوه‌ صانع‌ همه‌ این‌ چیزهاهستم‌.",
			"8": "<span>8 </span> &nbsp;ای‌ آسمانها از بالا ببارانید تا افلاك‌ عدالت‌ را فرو ریزد و زمین‌ بشكافد تا نجات‌ و عدالت‌ نمّو كنند و آنها را با هم‌ برویاند زیرا كه‌ من‌ یهوه‌ این‌ را آفریده‌ام‌.",
			"9": "<span>9 </span> &nbsp;وای‌ بركسی‌ كه‌ با صانع‌ خود چون‌ سفالی‌ با سفالهای‌ زمین‌ مخاصمه‌ نماید. آیا كوزه‌ به‌ كوزه‌گر بگوید چه‌ چیز را ساختی‌؟ یا مصنوع‌ تو درباره‌ تو بگوید كه‌ او دست‌ ندارد؟",
			"10": "<span>10 </span> &nbsp;وای‌ بر كسی‌ كه‌ به‌ پدر خود گوید: چه‌ چیز را تولید نمودی‌ و به‌ زن‌ كه‌ چه‌ زاییدی‌.",
			"11": "<span>11 </span> &nbsp;خداوند كه‌ قدّوس‌ اسرائیل‌ و صانع‌ آن‌ می‌باشد چنین‌ می‌گوید: درباره‌ امور آینده‌ از من‌ سؤال‌ نمایید و پسران‌ مرا و اعمال‌ دستهای‌ مرا به‌ من‌ تفویض‌ نمایید.",
			"12": "<span>12 </span> &nbsp;من‌ زمین‌ را ساختم‌ و انسان‌ را بر آن‌ آفریدم‌. دستهای‌ من‌ آسمانها را گسترانید و من‌ تمامی‌ لشكرهای‌ آنها را امر فرمودم‌.",
			"13": "<span>13 </span> &nbsp;من‌ او را به‌ عدالت‌ برانگیختم‌ و تمامی‌ راههایش‌ را راست‌ خواهم‌ ساخت‌. شهر مرا بنا كرده‌، اسیرانم‌ را آزاد خواهد نمود، امّا نه‌ برای‌ قیمت‌ و نه‌ برای‌ هدیه‌. یهوه‌ صبایوت‌ این‌ را می‌گوید.",
			"14": "<span>14 </span> &nbsp;خداوند چنین‌ می‌گوید: حاصل‌ مصر و تجارت‌ حبش‌ و اهل‌ سبا كه‌ مردان‌ بلند قدّ می‌باشند نزد تو عبور نموده‌، از آن‌ تو خواهند بود. و تابع‌ تو شده‌ در زنجیرها خواهند آمد و پیش‌ تو خم‌ شده‌ و نزد تو التماس‌ نموده‌، خواهند گفت‌: البتّه‌ خدا در تو است‌ و دیگری‌ نیست‌ و خدایی‌ نی‌.",
			"15": "<span>15 </span> &nbsp;ای‌ خدای‌ اسرائیل‌ و نجات‌ دهنده‌ یقیناً خدایی‌ هستی‌ كه‌ خود را پنهان‌ می‌كنی‌.",
			"16": "<span>16 </span> &nbsp;جمیع‌ ایشان‌ خجل‌ و رسوا خواهند شد و آنانی‌ كه‌ بتهامی‌سازند با هم‌ به‌ رسوایی‌ خواهند رفت‌.",
			"17": "<span>17 </span> &nbsp;امّا اسرائیل‌ به‌ نجات‌ جاودانی‌ از خداوند ناجی‌ خواهند شد و تا ابدالا´باد خجل‌ و رسوا نخواهند گردید.",
			"18": "<span>18 </span> &nbsp;زیرا یهوه‌ آفریننده‌ آسمان‌ كه‌ خدا است‌ كه‌ زمین‌ را سرشت‌ و ساخت‌ و آن‌ را استوار نمود و آن‌ را عبث‌ نیافرید بلكه‌ به‌ جهت‌ سكونت‌ مصوّر نمود چنین‌ می‌گوید: من‌ یهوه‌ هستم‌ و دیگری‌ نیست‌.",
			"19": "<span>19 </span> &nbsp;در خفا و در جایی‌ از زمین‌ تاریك‌ تكلّم‌ ننمودم‌. و به‌ ذریه‌ یعقوب‌ نگفتم‌ كه‌ مرا عبث‌ بطلبید. من‌ یهوه‌ به‌ عدالت‌ سخن‌ می‌گویم‌ و چیزهای‌ راست‌ را اعلان‌ می‌نمایم‌.",
			"20": "<span>20 </span> &nbsp;ای‌ رهاشدگان‌ از امّت‌ها جمع‌ شده‌، بیایید و با هم‌ نزدیك‌ شوید. آنانی‌ كه‌ چوب‌ بتهای‌ خود را برمی‌دارند و نزد خدایی‌ كه‌ نتواند رهانید دعا می‌نمایند معرفت‌ ندارند.",
			"21": "<span>21 </span> &nbsp;پس‌ اعلان‌ نموده‌، ایشان‌ را نزدیك‌ آورید تا با یكدیگر مشورت‌ نمایند. كیست‌ كه‌ این‌ را از ایام‌ قدیم‌ اعلان‌ نموده‌ و از زمان‌ سلف‌ اخبار كرده‌ است‌؟ آیا نه‌ من‌ كه‌ یهوه‌ هستم‌ و غیر از من‌ خدایی‌ دیگر نیست‌؟ خدای‌ عادل‌ و نجات‌دهنده‌ و سوای‌ من‌ نیست‌.",
			"22": "<span>22 </span> &nbsp;ای‌ جمیع‌ كرانه‌های‌ زمین‌ به‌ من‌ توجّه‌ نمایید و نجات‌ یابید زیرا من‌ خدا هستم‌ و دیگری‌ نیست‌.",
			"23": "<span>23 </span> &nbsp;به‌ ذات‌ خود قسم‌ خوردم‌ و این‌ كلام‌ به‌ عدالت‌ از دهانم‌ صادر گشته‌ برنخواهد گشت‌ كه‌ هر زانو پیش‌ من‌ خم‌ خواهد شد و هر زبان‌ به‌ من‌ قسم‌ خواهد خورد.",
			"24": "<span>24 </span> &nbsp;و مرا خواهند گفت‌ عدالت‌ و قوّت‌ فقط‌ در خداوند می‌باشد. و بسوی‌ او خواهند آمد و همگانی‌ كه‌ به‌ او خشمناكند خجل‌ خواهند گردید.",
			"25": "<span>25 </span> &nbsp;و تمامی‌ ذریت‌ اسرائیل‌ درخداوند عادل‌ شمرده‌ شده‌، فخر خواهند كرد."
		},
		"46": {
			"1": "<span>1 </span> &nbsp;بیل‌ خم‌ شده‌ و نَبُو منحنی‌ گردیده‌بتهای‌ آنها بر حیوانات‌ و بهایم‌ نهاده‌ شد. آنهایی‌ كه‌ شما برمی‌داشتید حمل‌ گشته‌ و بار حیوانات‌ ضعیف‌ شده‌ است‌.",
			"2": "<span>2 </span> &nbsp;آنها جمیعاً منحنی‌ و خم‌ شده‌، آن‌ بار را نمی‌توانند رهانید بلكه‌ خود آنها به‌ اسیری‌ می‌روند.",
			"3": "<span>3 </span> &nbsp;ای‌ خاندان‌ یعقوب‌ و تمامی‌ بقیه‌ خاندان‌ اسرائیل‌ كه‌ از بطن‌ بر من‌ حمل‌ شده‌ و از رحم‌ برداشته‌ من‌ بوده‌اید!",
			"4": "<span>4 </span> &nbsp;و تا به‌ پیری‌ شما من‌ همان‌ هستم‌ و تا به‌ شیخوخیت‌ من‌ شما را خواهم‌ برداشت‌. من‌ آفریدم‌ و من‌ برخواهم‌ داشت‌ و من‌ حمل‌ كرده‌، خواهم‌ رهانید.",
			"5": "<span>5 </span> &nbsp;مرا با كه‌ شبیه‌ و مساوی‌ می‌سازید و مرا با كه‌ مقابل‌ می‌نمایید تا مشابه‌ شویم‌؟",
			"6": "<span>6 </span> &nbsp;آنانی‌ كه‌ طلا را از كیسه‌ می‌ریزند و نقره‌ را به‌ میزان‌ می‌سنجند، زرگری‌ را اجیر می‌كنند تا خدایی‌ از آن‌ بسازد پس‌ سجده‌ كرده‌، عبادت‌ نیز می‌نمایند.",
			"7": "<span>7 </span> &nbsp;آن‌ را بر دوش‌ برداشته‌، می‌برند و به‌ جایش‌ می‌گذارند و او می‌ایستد و از جای‌ خود حركت‌ نمی‌تواند كرد. نزد او استغاثه‌ هم‌ می‌نمایند امّا جواب‌ نمی‌دهد و ایشان‌ را از تنگی‌ ایشان‌ نتواند رهانید.",
			"8": "<span>8 </span> &nbsp;این‌ را بیاد آورید و مردانه‌ بكوشید. و ای‌ عاصیان‌ آن‌ را در دل‌ خود تفكّر نمایید!",
			"9": "<span>9 </span> &nbsp;چیزهای‌ اوّل‌ را از زمان‌ قدیم‌ به‌ یاد آورید. زیرا من‌ قادر مطلق‌ هستم‌ و دیگری‌ نیست‌. خدا هستم‌ و نظیر من‌ نی‌.",
			"10": "<span>10 </span> &nbsp;آخر را از ابتدا و آنچه‌ را كه‌ واقع‌ نشده‌ از قدیم‌ بیان‌ می‌كنم‌ و می‌گویم‌ كه‌ اراده‌ من‌ برقرار خواهد ماند و تمامی‌ مسرّت‌ خویش‌ را بجاخواهم‌ آورد.",
			"11": "<span>11 </span> &nbsp;مرغ‌ شكاری‌ را از مشرق‌ و هم‌ مشورت‌ خویش‌ را از جای‌ دور می‌خوانم‌. من‌ گفتم‌ و البتّه‌ بجا خواهم‌ آورد و تقدیر نمودم‌ و البتّه‌ به‌ وقوع‌ خواهم‌ رسانید.",
			"12": "<span>12 </span> &nbsp;ای‌ سختدلان‌ كه‌ از عدالت‌ دور هستید مرا بشنوید.",
			"13": "<span>13 </span> &nbsp;عدالت‌ خود را نزدیك‌ آوردم‌ و دور نمی‌باشد و نجات‌ من‌ تأخیر نخواهد نمود و نجات‌ را به‌ جهت‌ اسرائیل‌ كه‌ جلال‌ من‌ است‌ در صهیون‌ خواهم‌ گذاشت‌."
		},
		"47": {
			"1": "<span>1 </span> &nbsp;ای‌ باكره‌ دختر بابل‌ فرود شده‌، بر خاك ‌بنشین‌ و ای‌ دختر كلدانیان‌ بر زمین‌ بی‌كرسی‌ بنشین‌ زیرا تو را دیگر نازنین‌ و لطیف‌ نخواهند خواند.",
			"2": "<span>2 </span> &nbsp;دستاس‌ را گرفته‌، آرد را خرد كن‌. نقاب‌ را برداشته‌، دامنت‌ را بر كِش‌ و ساقها را برهنه‌ كرده‌، از نهرها عبور كن‌.",
			"3": "<span>3 </span> &nbsp;عورت‌ تو كشف‌ شده‌، رسوایی‌ تو ظاهر خواهد شد. من‌ انتقام‌ كشیده‌، بر احدی‌ شفقت‌ نخواهم‌ نمود.",
			"4": "<span>4 </span> &nbsp;و امّا نجات‌ دهنده‌ ما اسم‌ او یهوه‌ صبایوت‌ و قدّوس‌ اسرائیل‌ می‌باشد.",
			"5": "<span>5 </span> &nbsp;ای‌ دختر كلدانیان‌ خاموش‌ بنشین‌ و به‌ ظلمت‌ داخل‌ شو زیرا كه‌ دیگر تو را ملكه‌ ممالك‌ نخواهند خواند.",
			"6": "<span>6 </span> &nbsp;بر قوم‌ خود خشم‌ نموده‌ و میراث‌ خویش‌ را بی‌حرمت‌ كرده‌، ایشان‌ را به‌ دست‌ تو تسلیم‌ نمودم‌. بر ایشان‌ رحمت‌ نكرده‌، یوغ‌ خود را بر پیران‌ بسیار سنگین‌ ساختی‌.",
			"7": "<span>7 </span> &nbsp;و گفتی‌ تا به‌ ابد ملكه‌ خواهم‌ بود. و این‌ چیزها را در دل‌ خود جا ندادی‌ و عاقبت‌ آنها را به‌ یاد نیاوردی‌.",
			"8": "<span>8 </span> &nbsp;پس‌ الا´ن‌ ای‌ كه‌ در عشرت‌ بسر می‌بری‌ و در اطمینان‌ ساكن‌ هستی‌ این‌ را بشنو. ای‌ كه‌ دردل خود می‌گویی‌ من‌ هستم‌ و غیر از من‌ دیگری‌ نیست‌ و بیوه‌ نخواهم‌ شد و بی‌اولادی‌ را نخواهم‌ دانست‌.",
			"9": "<span>9 </span> &nbsp;پس‌ این‌ دو چیز یعنی‌ بی‌اولادی‌ و بیوگی‌ بغتةً در یكروز به‌ تو عارض‌ خواهد شد و با وجود كثرت‌ سِحر تو و افراط‌ افسونگری‌ زیاد تو آنها بشدّت‌ بر تو استیلا خواهد یافت.",
			"10": "<span>10 </span> &nbsp;زیرا كه‌ بر شرارت‌ خود اعتماد نموده‌، گفتی‌ كسی‌ نیست‌ كه‌ مرا بیند. و حكمت‌ و عِلم‌ تو، تو را گمراه‌ ساخت‌ و در دل‌ خود گفتی‌: من‌ هستم‌ و غیر از من‌ دیگری‌ نیست‌.",
			"11": "<span>11 </span> &nbsp;پس‌ بلایی‌ كه‌ افسون‌ آن‌ را نخواهی‌ دانست‌ بر تو عارض‌ خواهد شد و مصیبتی‌ كه‌ به‌ دفع‌ آن‌ قادر نخواهی‌ شد، تو را فرو خواهد گرفت‌ و هلاكتی‌ كه‌ ندانسته‌ای‌، ناگهان‌ بر تو استیلا خواهد یافت‌.",
			"12": "<span>12 </span> &nbsp;پس‌ در افسونگری‌ خـود و كثـرت‌ سِحر خویش‌ كه‌ در آنها از طفولیت‌ مشقّت‌ كشیده‌ای‌ قائم‌ باش‌ شاید كه‌ منفعت‌ توانی‌ بـرد و شایـد كه‌ غالـب‌ توانـی‌ شـد.",
			"13": "<span>13 </span> &nbsp;از فراوانـی‌ مشورتهایت‌ خسته‌ شده‌ای‌؛ پس‌ تقسیم‌كنندگان‌ افـلاك‌ و رصـد بنـدان‌ كواكب‌ و آنانـی‌ كه‌ در غـرّه‌ ماهها اخبار می‌دهند بایستند و تو را از آنچه‌ بـر تو واقع‌ شدنی‌ است‌ نجات‌ دهند.",
			"14": "<span>14 </span> &nbsp;اینك‌ مثل‌ كاهبُنْ شده‌، آتش‌ ایشان‌ را خواهد سوزانید كه‌ خویشتن‌ را از سورت‌ زبانه‌ آن‌ نخواهند رهانید و آن‌ اخگری‌ كه‌ خود را نزد آن‌ گرم‌ سازند و آتشی‌ كه‌ در برابرش‌ بنشینند نخواهد بود.",
			"15": "<span>15 </span> &nbsp;آنانی‌ كه‌ از ایشان‌ مشقّت‌ كشیدی‌ برای‌ تو چنین‌ خواهند شد و آنانی‌ كه‌ از طفولیت‌ با تو تجارت‌ می‌كردند هر كس‌ بجای‌ خود آواره‌ خواهد گردید و كسی‌ كه‌ تو را رهایی‌ دهد نخواهد بود."
		},
		"48": {
			"1": "<span>1 </span> &nbsp;ای‌ خاندان‌ یعقوب‌ كه‌ به‌ نام‌ اسرائیل مسمّی‌ هستید و از آب‌ یهودا صادر شده‌اید، و به‌ اسم‌ یهوه‌ قسم‌ می‌خورید و خدای‌ اسرائیل‌ را ذكر می‌نمایید، امّا نه‌ به‌ صداقت‌ و راستی‌، این‌ را بشنوید.",
			"2": "<span>2 </span> &nbsp;زیرا كه‌ خویشتن‌ را از شهر مقّدس‌ می‌خوانند و بر خدای‌ اسرائیل‌ كه‌ اسمش‌ یهوه‌ صبایوت‌ است‌ اعتماد می‌دارند.",
			"3": "<span>3 </span> &nbsp;چیزهای‌ اوّل‌ را از قدیم‌ اخبار كردم‌ و از دهان‌ من‌ صادر شده‌، آنها را اعلام‌ نمودم‌ بغتةً به‌ عمل‌ آوردم‌ و واقع‌ شد.",
			"4": "<span>4 </span> &nbsp;چونكه‌ دانستم‌ كه‌ تو سخت‌ دل‌ هستی‌ و گردنت‌ بند آهنین‌ و پیشانی‌ تو برنجین‌ است‌.",
			"5": "<span>5 </span> &nbsp;بنابراین‌ تو را از قدیم‌ مخبر ساختم‌ و قبل‌ از وقوع‌ تو را اعلام‌ نمودم‌. مبادا بگویی‌ كه‌ بت‌ من‌ آنها را بجا آورده‌ و بت‌ تراشیده‌ و صنم‌ ریخته‌ شده‌ من‌ آنها را امر فرموده‌ است‌.",
			"6": "<span>6 </span> &nbsp;چونكه‌ همه‌ این‌ چیزها را شنیدی‌، آنها را ملاحظه‌ نما. پس‌ آیا شما اعتراف‌ نخواهید كرد، و از این‌ زمان‌ چیزهای‌ تازه‌ را به‌ شما اعلام‌ نمودم‌ و چیزهای‌ مخفی‌ را كه‌ ندانسته‌ بودید؟",
			"7": "<span>7 </span> &nbsp;در این‌ زمان‌ و نه‌ در ایام‌ قدیم‌ آنها آفریده‌ شد و قبل‌ از امروز آنها را نشنیده‌ بودی‌، مبادا بگویی‌ اینك‌ این‌ چیزها را می‌دانستم‌.",
			"8": "<span>8 </span> &nbsp;البتّه‌ نشنیده‌ و هر آینه‌ ندانسته‌ و البتّه‌ گوش‌ تو قبل‌ از این‌ باز نشده‌ بود. زیرا می‌دانستم‌ كه‌ بسیار خیانتكار هستی‌ و از رحم‌ (مادرت‌ ) عاصی‌ خوانده‌ شدی‌.",
			"9": "<span>9 </span> &nbsp;به‌ خاطر اسم‌ خود غضب‌ خویش‌ را به‌ تأخیر خواهم‌ انداخت‌ و به‌ خاطر جلال‌ خویش‌ بر تو شفقت‌ خواهم‌ كرد تا تو را منقطع‌ نسازم‌.",
			"10": "<span>10 </span> &nbsp;اینك‌ تو را قال‌ گذاشتم‌ امّا نه‌ مثل‌ نقره‌ و تو را در كوره‌ مصیبت‌ آزمودم‌.",
			"11": "<span>11 </span> &nbsp;به‌خاطر ذات‌ خود، به‌ خاطر ذات‌ خود این‌ را می‌كنم‌ زیرا كه‌ اسم‌ من‌ چرا باید بی‌حرمت‌ شود و جلال‌ خویش‌ را به‌ دیگری‌ نخواهم‌ داد.",
			"12": "<span>12 </span> &nbsp;ای‌ یعقوب‌ و ای‌ دعوت‌ شده‌ من‌ اسرائیل‌ بشنو! من‌ او هستم‌! من‌ اوّل‌ هستم‌ و آخر هستم‌!",
			"13": "<span>13 </span> &nbsp;به‌ تحقیق‌ دست‌ من‌ بنیاد زمین‌ را نهاد و دست‌ راست‌ من‌ آسمانها را گسترانید. وقتی‌ كه‌ آنها را می‌خوانم‌ با هم‌ برقرار می‌باشند.",
			"14": "<span>14 </span> &nbsp;پس‌ همگی‌ شما جمع‌ شده‌، بشنوید كیست‌ از ایشان‌ كه‌ اینها را اخبار كرده‌ باشد. خداوند او را دوست‌ می‌دارد، پس‌ مسرّت‌ خود را بر بابل‌ بجا خواهد آورد و بازوی‌ او بر كلدانیان‌ فرود خواهد آمد.",
			"15": "<span>15 </span> &nbsp;من‌ تكلّم‌ نمودم‌ و من‌ او را خواندم‌ و او را آوردم‌ تا راه‌ خود را كامران‌ سازد.",
			"16": "<span>16 </span> &nbsp;به‌ من‌ نزدیك‌ شده‌، این‌ را بشنوید. از ابتدا در خفا تكلّم‌ ننمودم‌ و از زمانی‌ كه‌ این‌ واقع‌ شد من‌ در آنجا هستم‌ و الا´ن‌ خداوند یهوه‌ مرا و روح‌ خود را فرستاده‌ است‌.",
			"17": "<span>17 </span> &nbsp;خداوند كه‌ ولّی‌ تو و قدّوس‌ اسرائیل‌ است‌ چنین‌ می‌گوید: من‌ یهوه‌ خدای‌ تو هستم‌ و تو را تعلیم‌ می‌دهم‌ تا سود ببری‌ و تو را به‌ راهی‌ كه‌ باید بروی‌ هدایت‌ می‌نمایم‌.",
			"18": "<span>18 </span> &nbsp;كاش‌ كه‌ به‌ اوامر من‌ گوش‌ می‌دادی‌، آنگاه‌ سلامتی‌ تو مثل‌ نهر و عدالت‌ تو مانند امواج‌ دریا می‌بود.",
			"19": "<span>19 </span> &nbsp;آنگاه‌ ذریت‌ تو مثل‌ ریگ‌ و ثمره‌ صُلب‌ تو مانند ذرّات‌ آن‌ می‌بود و نام‌ او از حضور من‌ منقطع‌ و هلاك‌ نمی‌گردید.",
			"20": "<span>20 </span> &nbsp;از بابل‌ بیرون‌ شده‌، از میان‌ كلدانیان‌ بگریزید و این‌ را به‌ آواز ترنّم‌ اخبار و اعلام‌ نمایید و آن‌ را تا اقصای‌ زمین‌ شایع‌ ساخته‌، بگویید كه‌ خداوند بنده‌ خود یعقوب‌ را فدیه‌ داده‌ است‌.",
			"21": "<span>21 </span> &nbsp;و تشنه‌ نخواهند شد اگر چه‌ ایشان‌ را در ویرانه‌ها رهبری‌ نماید، زیرا كه‌ آب‌ از صخره‌ برای‌ ایشان‌ جاری‌ خواهد ساخت‌ و صخره‌ را خواهد شكافت‌ تا آبها بجوشد.",
			"22": "<span>22 </span> &nbsp;و خداوند می‌گوید كه‌ برای‌ شریران‌ سلامتی‌ نخواهد بود."
		},
		"49": {
			"1": "<span>1 </span> &nbsp;ای‌ جزیره‌ها از من‌ بشنوید! و ای‌طوایف‌ از جای‌ دور گوش‌ دهید! خداوند مرا از رحم‌ دعوت‌ كرده‌ واز احشای‌ مادرم‌ اسم‌ مرا ذكر نموده‌ است‌.",
			"2": "<span>2 </span> &nbsp;و دهان‌ مرا مثل‌ شمشیر تیز ساخته‌، مرا زیر سایه‌ دست‌ خود پنهان‌ كرده‌ است‌. و مرا تیر صیقلی‌ ساخته‌ در تركش‌ خود مخفی‌ نموده‌ است‌.",
			"3": "<span>3 </span> &nbsp;و مرا گفت‌: ای‌ اسرائیل‌ تو بنده‌ من‌ هستی‌ كه‌ از تو خویشتن‌ را تمجید نموده‌ام‌!",
			"4": "<span>4 </span> &nbsp;امّا من‌ گفتم‌ كه‌ عبث‌ زحمت‌ كشیدم‌ و قوّت‌ خود را بی‌فایده‌ و باطل‌ صرف‌ كردم‌؛ لیكن‌ حقّ من‌ با خداوند و اجرت‌ من‌ با خدای‌ من‌ می‌باشد.",
			"5": "<span>5 </span> &nbsp;و الا´ن‌ خداوند كه‌ مرا از رحم‌ برای‌ بندگی‌ خویش‌ سرشت‌ تا یعقوب‌ را نزد او بازآورم‌ و تا اسرائیل‌ نزد وی‌ جمع‌ شوند می‌گوید (و در نظر خداوند محترم‌ هستم‌ و خدای‌ من‌ قوّت‌ من‌ است‌)؛",
			"6": "<span>6 </span> &nbsp;پس‌ می‌گوید: این‌ چیز قلیلی‌ است‌ كه‌ بنده‌ من‌ بشوی‌ تا اسباط‌ یعقوب‌ را برپا كنی‌ و ناجیان‌ اسرائیل‌ را باز آوری‌. بلكه‌ تو را نور امّت‌ها خواهم‌ گردانید و تا اقصای‌ زمین‌ نجات‌ من‌ خواهی‌ بود.",
			"7": "<span>7 </span> &nbsp;خداوند كه‌ ولّی‌ و قدّوس‌ اسرائیل‌ می‌باشد، به‌ او كه‌ نزد مردم‌ محقّر و نزد امّت‌هامكروه‌ و بنده‌ حاكمان‌ است‌ چنین‌ می‌گوید: پادشاهان‌ دیده‌ برپا خواهند شد و سروران‌ سجده‌ خواهند نمود، به‌ سبب‌ خداوند كه‌ امین‌ است‌ و قدّوس‌ اسرائیل‌ كه‌ تو را برگزیده‌ است‌.",
			"8": "<span>8 </span> &nbsp;خداوند چنین‌ می‌گوید: در زمان‌ رضامندی‌ تو را اجابت‌ نمودم‌ و در روز نجات‌ تو را اعانت‌ كردم‌. و تو را حفظ‌ نموده‌ عهد قوم‌ خواهم‌ ساخت‌ تا زمین‌ را معمور سازی‌ و نصیب‌های‌ خراب‌ شده‌ را (به‌ ایشان‌) تقسیم‌ نمایی‌.",
			"9": "<span>9 </span> &nbsp;و به‌ اسیران‌ بگویی‌: بیرون‌ روید و به‌ آنانی‌ كه‌ در ظلمتند خویشتن‌ را ظاهر سازید. و ایشان‌ در راهها خواهند چرید و مرتعهای‌ ایشان‌ بر همه‌ صحراهای‌ كوهی‌ خواهد بود.",
			"10": "<span>10 </span> &nbsp;گرسنه‌ و تشنه‌ نخواهند بود و حرارت‌ و آفتاب‌ به‌ ایشان‌ ضرر نخواهد رسانید زیرا آنكه‌ بر ایشان‌ ترحّم‌ دارد ایشان‌ را هدایت‌ خواهد كرد و نزد چشمه‌های‌ آب‌ ایشان‌ را رهبری‌ خواهد نمود.",
			"11": "<span>11 </span> &nbsp;و تمامی‌ كوههای‌ خود را طریق‌ها خواهم‌ ساخت‌ و راههای‌ من‌ بلند خواهد شد.",
			"12": "<span>12 </span> &nbsp;اینك‌ بعضی‌ از جای‌ دور خواهند آمد و بعضی‌ از شمال‌ و از مغرب‌ و بعضی‌ از دیار سینیم‌.",
			"13": "<span>13 </span> &nbsp;ای‌ آسمانها ترنّم‌ كنید! و ای‌ زمین‌ وجد نما! و ای‌ كوهها آواز شادمانی‌ دهید! زیرا خداوند قوم‌ خود را تسلّی‌ می‌دهد و بر مظلومان‌ خود ترحّم‌ می‌فرماید.",
			"14": "<span>14 </span> &nbsp;امّا صهیون‌ می‌گوید: «یهوه‌ مرا ترك‌ نموده‌ و خداوند مرا فراموش‌ كرده‌ است‌.»",
			"15": "<span>15 </span> &nbsp;آیا زن‌ بچه‌ شیر خواره‌ خود را فراموش‌ كرده‌ بر پسر رحم‌خویش‌ ترحّم‌ ننماید؟ اینان‌ فراموش‌ می‌كنند امّا من‌ تو را فراموش‌ نخواهم‌ نمود.",
			"16": "<span>16 </span> &nbsp;اینك‌ تو را بر كف‌ دستهای‌ خود نقش‌ نمودم‌ و حصارهایت‌ دائماً در نظر من‌ است‌.",
			"17": "<span>17 </span> &nbsp;پسرانت‌ به‌ تعجیل‌ خواهند آمد و آنانی‌ كه‌ تو را خراب‌ و ویران‌ كردند از تو بیرون‌ خواهند رفت‌.",
			"18": "<span>18 </span> &nbsp;چشمان‌ خود را به‌ هر طرف‌ بلند كرده‌، ببین‌ جمیع‌ اینها جمع‌ شده‌، نزد تو می‌آیند. خداوند می‌گوید به‌ حیات‌ خودم‌ قسم‌ كه‌ خود را به‌ جمیع‌ اینها مثل‌ زیور ملبّس‌ خواهی‌ ساخت‌ و مثل‌ عروس‌ خویشتن‌ را به‌ آنها خواهی‌ آراست‌.",
			"19": "<span>19 </span> &nbsp;زیرا خرابه‌ها و ویرانه‌های‌ تو و زمین‌ تو كه‌ تباه‌ شده‌ بود، امّا الا´ن‌ تو از كثرت‌ ساكنان‌ تنگ‌ خواهی‌ شد و هلاك‌ كنندگانت‌ دور خواهند گردید.",
			"20": "<span>20 </span> &nbsp;پسران‌ تو كه‌ بی‌اولاد می‌بودی‌، در سمع‌ تو (به‌ یكدیگر) خواهند گفت‌: این‌ مكان‌ برای‌ من‌ تنگ‌ است‌، مرا جایی‌ بده‌ تا ساكن‌ شوم‌.",
			"21": "<span>21 </span> &nbsp;و تو دردل‌ خود خواهی‌ گفت‌: كیست‌ كه‌ اینها را برای‌ من‌ زاییده‌ است‌ و حال‌ آنكه‌ من‌ بی‌اولاد و نازاد و جلای‌ وطن‌ و متروك‌ می‌بودم‌. پس‌ كیست‌ كه‌ اینها را پرورش‌ داد. اینك‌ من‌ به‌ تنهایی‌ ترك‌ شده‌ بودم‌ پس‌ اینها كجا بودند؟",
			"22": "<span>22 </span> &nbsp;خداوند یهوه‌ چنین‌ می‌گوید: اینك‌ من‌ دست‌ خود را بسوی‌ امّت‌ها دراز خواهم‌ كرد و عَلَم‌ خویش‌ را بسوی‌ قوم‌ها خواهم‌ برافراشت‌. و ایشان‌ پسرانـت‌ را در آغـوش‌ خود خواهند آورد و دخترانت‌ بر دوش‌ ایشان‌ برداشته‌ خواهند شد.",
			"23": "<span>23 </span> &nbsp;و پادشاهان‌ لالاهای‌ تو و ملكه‌های‌ ایشان‌ دایه‌های‌ تو خواهند بود و نزد تو رو به‌ زمین‌ افتاده‌، خاك‌ پای‌ تو را خواهند لیسید و تو خواهی‌ دانست‌ كه‌ من‌ یهوه‌ هستم‌ و آنانی‌ كه‌ منتظر من‌ باشند، خجل‌ نخواهند گردید.",
			"24": "<span>24 </span> &nbsp;آیا غنیمت‌ از جبّار گرفته‌ شود یا اسیران‌ از مرد قاهر رهانیده‌ گردند.",
			"25": "<span>25 </span> &nbsp;زیرا خداوند چنین‌ می‌گوید: اسیران‌ نیز از جبّار گرفته‌ خواهند شد و غنیمت‌ از دست‌ ستم‌ پیشه‌ رهانیده‌ خواهد گردید. زیرا كه‌ من‌ با دشمنان‌ تو مقاومت‌ خواهم‌ نمود و من‌ پسران‌ تو را نجات‌ خواهم‌ داد.",
			"26": "<span>26 </span> &nbsp;و به‌ آنانی‌ كه‌ بر تو ظلم‌ نمایند گوشت‌ خودشان‌ را خواهم‌ خورانید و به‌ خون‌ خود مثل‌ شراب‌ مست‌ خواهند شد و تمامی‌ بشر خواهند دانست‌ كه‌ من‌ یهوه‌ نجات‌دهنده‌ تو و ولّی‌ تو و قدیر یعقوب‌ هستم‌."
		},
		"50": {
			"1": "<span>1 </span> &nbsp;خداوند چنین‌ می‌گوید: طلاق‌ نامه مادر شما كه‌ او را طلاق‌ دادم‌ كجا است‌؟ یا كیست‌ از طلبكاران‌ من‌ كه‌ شما را به‌ او فروختم‌؟ اینك‌ شما به‌ سبب‌ گناهان‌ خود فروخته‌ شدید و مادر شما به‌ جهت‌ تقصیرهای‌ شما طلاق‌ داده‌ شـد.",
			"2": "<span>2 </span> &nbsp;چون‌ آمدم‌ چرا كسی‌ نبود؟ و چون‌ ندا كردم‌ چرا كسی‌ جواب‌ نداد؟ آیا دست‌ من‌ به‌ هیچ‌ وجه‌ كوتاه‌ شـده‌ كه‌ نتواند نجات‌ دهد یا در من‌ قدرتی‌ نیسـت‌ كه‌ رهایـی‌ دهـم‌؟ اینك‌ به‌ عتـاب‌ خود دریا را خشك‌ می‌كنم‌ و نهرها را بیابان‌ می‌سـازم‌ كه‌ ماهـی‌ آنها از بی‌آبی‌ متعفّن‌ شـود و از تشنگـی‌ بمیـرد.",
			"3": "<span>3 </span> &nbsp;آسمــان‌ را به‌ ظلمـت‌ ملبّس‌ می‌سازم‌ و پلاس‌ را پوشش‌ آن‌ می‌گردانم‌.»",
			"4": "<span>4 </span> &nbsp;خداوند یهوه‌ زبان‌ تلامیذ را به‌ من‌ داده‌ است‌ تا بدانم‌ كه‌ چگونه‌ خستگان‌ را به‌ كلام‌ تقویت‌ دهم‌. هر بامداد بیدار می‌كند. گوش‌ مرا بیدار می‌كند تا مثل‌ تلامیذ بشنوم‌.",
			"5": "<span>5 </span> &nbsp;خداوند یهوه‌ گوش‌ مرا گشود و مخالفت‌ نكردم‌ و به‌ عقب‌برنگشتم‌.",
			"6": "<span>6 </span> &nbsp;پشت‌ خود را به‌ زنندگان‌ و رخسار خود را به‌ مُوكَنان‌ دادم‌ و روی‌ خود را از رسوایی‌ و آب‌ دهان‌ پنهان‌ نكردم‌.",
			"7": "<span>7 </span> &nbsp;چونكه‌ خداوند یهوه‌ مرا اعانت‌ می‌كند، پس‌ رسوا نخواهم‌ شد، از این‌ جهت‌ روی‌ خود را مثل‌ سنگ‌ خارا ساختم‌ و می‌دانم‌ كه‌ خجل‌ نخواهم‌ گردید.",
			"8": "<span>8 </span> &nbsp;آنكه‌ مرا تصدیق‌ می‌كند نزدیك‌ است‌. پس‌ كیست‌ كه‌ با من‌ مخاصمه‌ نماید تا با هم‌ بایستیم‌ و كیست‌ كه‌ بر من‌ دعوی‌ نماید پس‌ او نزدیك‌ من‌ بیاید؟",
			"9": "<span>9 </span> &nbsp;اینك‌ خداوند یهوه‌ مرا اعانت‌ می‌كند؛ پس‌ كیست‌ مرا ملزم‌ سازد؟ همانا همگی‌ ایشان‌ مثل‌ رخت‌ مندرس‌ شده‌، بید ایشان‌ را خواهد خورد.",
			"10": "<span>10 </span> &nbsp;كیست‌ از شما كه‌ از خداوند می‌ترسد و آواز بنده‌ او را می‌شنود؟ هركه‌ در ظلمت‌ سالك‌ باشد و روشنایی‌ ندارد، او به‌ اسم‌ یهوه‌ توكّل‌ نماید و به‌ خدای‌ خویش‌ اعتماد بكند.",
			"11": "<span>11 </span> &nbsp;هان‌ جمیع‌ شما كه‌ آتش‌ می‌افروزید و كمر خود را به‌ مشعلها می‌بندید، در روشنایی‌ آتش‌ خویش‌ و در مشعلهایی‌ كه‌ خود افروخته‌اید سالك‌ باشید، امّا این‌ از دست‌ من‌ به‌ شما خواهد رسید كه‌ در اندوه‌ خواهید خوابید."
		},
		"51": {
			"1": "<span>1 </span> &nbsp;ای‌ پیروان‌ عدالت‌ و طالبان‌ خداوند مرا بشنوید! به‌ صخره‌ای‌ كه‌ از آن‌ قطع‌ گشته‌ و به‌ حفره‌ چاهی‌ كه‌ از آن‌ كنده‌ شده‌اید، نظر كنید.",
			"2": "<span>2 </span> &nbsp;به‌ پدر خود ابراهیم‌ و به‌ ساره‌ كه‌ شما را زایید نظر نمایید زیرا او یك‌ نفر بود حینی‌ كه‌ او را دعوت‌ نمودم‌ و او را بركت‌ داده‌، كثیر گردانیدم‌.",
			"3": "<span>3 </span> &nbsp;به‌ تحقیق‌ خداوند صهیون‌ را تسلّی‌ داده‌، تمامی‌خرابه‌هایش‌ را تسلّی‌ بخشیده‌ است‌ و بیابان‌ او را مثل‌ عدن‌ و هامون‌ او را مانند جنّت‌ خداوند ساخته‌ است‌. خوشی‌ و شادی‌ در آن‌ یافت‌ می‌شود و تسبیح‌ و آواز ترنّم‌.",
			"4": "<span>4 </span> &nbsp;ای‌ قوم‌ من‌ به‌ من‌ توجّه‌ نمایید و ای‌ طایفه‌ من‌ به‌ من‌ گوش‌ دهید. زیرا كه‌ شریعت‌ از نزد من‌ صادر می‌شود و داوری‌ خود را برقرار می‌كنم‌ تا قوم‌ها را روشنایی‌ بشود.",
			"5": "<span>5 </span> &nbsp;عدالت‌ من‌ نزدیك‌ است‌ و نجات‌ من‌ ظاهر شده‌، بازوی‌ من‌ قوم‌ها را داوری‌ خواهد نمود و جزیره‌ها منتظر من‌ شده‌، به‌ بازوی‌ من‌ اعتماد خواهند كرد.",
			"6": "<span>6 </span> &nbsp;چشمان‌ خود را بسوی‌ آسمان‌ برافرازید و پایین‌ بسوی‌ زمین‌ نظر كنید زیرا كه‌ آسمان‌ مثل‌ دود از هم‌ خواهد پاشید و زمین‌ مثل‌ جامه‌ مندرس‌ خواهد گردید و ساكنانش‌ همچنین‌ خواهند مرد. امّا نجات‌ من‌ تا به‌ ابد خواهد ماند و عدالت‌ من‌ زایل‌ نخواهد گردید.",
			"7": "<span>7 </span> &nbsp;ای‌ شما كه‌ عدالت‌ را می‌شناسید! و ای‌ قومی‌ كه‌ شریعت‌ من‌ در دل‌ شما است‌! مرا بشنوید. از مذمّت‌ مردمان‌ مترسید و از دشنام‌ ایشان‌ هراسان‌ مشوید.",
			"8": "<span>8 </span> &nbsp;زیرا كه‌ بید ایشان‌ را مثل‌ جامه‌ خواهد زد و كِرم‌ ایشان‌ را مثل‌ پشم‌ خواهد خورد امّا عدالت‌ من‌ تا ابدالا´باد و نجات‌ من‌ نسلاً بعد نسل‌ باقی‌ خواهد ماند.",
			"9": "<span>9 </span> &nbsp;بیدار شو ای‌ بازوی‌ خداوند ، بیدار شو و خویشتن‌ را با قوّت‌ ملبّس‌ ساز. مثل‌ ایام‌ قدیم‌ و دوره‌های‌ سلف‌ بیدار شو. آیا تو آن‌ نیستی‌ كه‌ رَهَب‌ را قطع‌ نموده‌، اژدها را مجروح‌ ساختی‌.",
			"10": "<span>10 </span> &nbsp;آیا تو آن‌ نیستی‌ كه‌ دریا و آبهای‌ لجّه‌ عظیم‌ را خشك‌ كردی‌ و عمق‌های‌ دریا را راه‌ ساختی‌ تا فدیه‌ شدگان‌ عبور نمایند؟",
			"11": "<span>11 </span> &nbsp;و فدیه‌ شدگان‌ خداوند بازگشت‌ نموده‌، با ترنّم‌به‌ صهیون‌ خواهند آمد و خوشی‌ جاودانی‌ بر سر ایشان‌ خواهد بود و شادمانی‌ و خوشی‌ را خواهند یافت‌ و غم‌ و ناله‌ فرار خواهد كرد.",
			"12": "<span>12 </span> &nbsp;من‌ هستم‌، من‌ كه‌ شما را تسلّی‌ می‌دهم‌. پس‌ تو كیستی‌ كه‌ از انسانی‌ كه‌ می‌میرد می‌ترسی‌ و از پسر آدم‌ كه‌ مثل‌ گیاه‌ خواهد گردید؟",
			"13": "<span>13 </span> &nbsp;و خداوند را كه‌ آفریننده‌ تو است‌ كه‌ آسمانها را گسترانید و بنیاد زمین‌ را نهاد فراموش‌ كرده‌ای‌ و دائماً تمامی‌ روز از خشم‌ ستمكار وقتی‌ كه‌ به‌ جهت‌ هلاك‌ كردن‌ مهیا می‌شود می‌ترسی‌؟ و خشم‌ ستمكار كجا است‌؟",
			"14": "<span>14 </span> &nbsp;اسیران‌ ذلیل‌ بزودی‌ رها خواهند شد و در حفره‌ نخواهند مرد و نان‌ ایشان‌ كم‌ نخواهد شد.",
			"15": "<span>15 </span> &nbsp;زیرا من‌ یهوه‌ خدای‌ تو هستم‌ كه‌ دریا را به‌ تلاطم‌ می‌آورم‌ تا امواجش‌ نعره‌ زنند، یهوه‌ صبایوت‌ اسم‌ من‌ است‌.",
			"16": "<span>16 </span> &nbsp;و من‌ كلام‌ خود را در دهان‌ تو گذاشتم‌ و تو را زیر سایه‌ دست‌ خویش‌ پنهان‌ كردم‌ تا آسمانها غرس‌ نمایم‌ و بنیاد زمینی‌ نهم‌ و صهیون‌ را گویم‌ كه‌ تو قوم‌ من‌ هستی‌.",
			"17": "<span>17 </span> &nbsp;خویشتن‌ را برانگیز ای‌ اورشلیم‌! خویشتن‌ را برانگیخته‌، برخیز! ای‌ كه‌ از دست‌ خداوند كاسه‌ غضب‌ او را نوشیدی‌ و دُرد كاسه‌ سرگیجی‌ را نوشیده‌، آن‌ را تا ته‌ آشامیدی‌.",
			"18": "<span>18 </span> &nbsp;از جمیع‌ پسرانی‌ كه‌ زاییده‌ است‌ یكی‌ نیست‌ كه‌ او را رهبری‌ كند و از تمامی‌ پسرانی‌ كه‌ تربیت‌ نموده‌، كسی‌ نیست‌ كه‌ او را دستگیری‌ نماید.",
			"19": "<span>19 </span> &nbsp;این‌ دو بلا بر تو عارض‌ خواهد شد؛ كیست‌ كه‌ برای‌ تو ماتم‌ كند؟ یعنی‌ خرابی‌ و هلاكت‌ و قحط‌ و شمشیر، پس‌ چگونه‌ تو را تسلّی‌ دهم‌.",
			"20": "<span>20 </span> &nbsp;پسران‌تو را ضعف‌ گرفته‌، به‌ سر همه‌ كوچه‌ها مثل‌ آهو در دام‌ خوابیده‌اند. و ایشان‌ از غضب‌ خداوند و از عتاب‌ خدای‌ تو مملّو شده‌اند.",
			"21": "<span>21 </span> &nbsp;پس‌ ای‌ زحمت‌ كشیده‌ این‌ را بشنو! و ای‌ مست‌ شده‌ امّا نه‌ از شراب‌!",
			"22": "<span>22 </span> &nbsp;خداوند تو یهوه‌ و خدای‌ تو كه‌ در دعوی‌ قوم‌ خود ایستادگی‌ می‌كند چنین‌ می‌گوید: اینك‌ كاسه‌ سرگیجی‌ را و دُرد كاسه‌ غضب‌ خویش‌ را از تو خواهم‌ گرفت‌ و آن‌ را بار دیگر نخواهی‌ آشامید.",
			"23": "<span>23 </span> &nbsp;و آن‌ را به‌ دست‌ آنانی‌ كه‌ بر تو ستم‌ می‌نمایند می‌گذارم‌ كه‌ به‌ جان‌ تو می‌گویند: خم‌ شو تا از تو بگذریم‌ و تو پشت‌ خود را مثل‌ زمین‌ و مثل‌ كوچه‌ به‌ جهت‌ راه‌ گذریان‌ گذاشته‌ای‌."
		},
		"52": {
			"1": "<span>1 </span> &nbsp;بیدار شو ای‌ صهیون‌! بیدار شو و قوّت‌خود را بپوش‌ ای‌ شهر مقدّس‌ اورشلیم‌! لباس‌ زیبایی‌ خویش‌ را در بر كن‌ زیرا كه‌ نامختون‌ و ناپاك‌ بار دیگر داخل‌ تو نخواهد شد.",
			"2": "<span>2 </span> &nbsp;ای‌ اورشلیم‌ خود را از گردْ بیفشان‌ و برخاسته‌، بنشین‌! و ای‌ دختر صهیون‌ كه‌ اسیر شده‌ای‌ بندهای‌ گردن‌ خود را بگشا!",
			"3": "<span>3 </span> &nbsp;زیرا خداوند چنین‌ می‌گوید: مفت‌ فروخته‌ كشتید و بی‌نقره‌ فدیه‌ داده‌ خواهید شد.",
			"4": "<span>4 </span> &nbsp;چونكه‌ خداوند یهوه‌ چنین‌ می‌گوید: كه‌ در ایام‌ سابق‌ قوم‌ من‌ به‌ مصر فرود شدند تا در آنجا ساكن‌ شوند و بعد از آن‌ آشور بر ایشان‌ بی‌سبب‌ ظلم‌ نمودند.",
			"5": "<span>5 </span> &nbsp;امّا الا´ن‌ خداوند می‌گوید: در اینجا مرا چه‌ كار است‌ كه‌ قوم‌ من‌ مجّاناً گرفتار شده‌اند. و خداوند می‌گوید: آنانی‌ كه‌ بر ایشان‌ تسلّط‌ دارند صیحه‌ می‌زنند و نام‌ من‌ دائماً هر روز اهانت‌ می‌شود.",
			"6": "<span>6 </span> &nbsp;بنابراین‌ قوم‌ من‌ اسم‌ مرا خواهند شناخت‌. و در آن‌ روز خواهند فهمید كه‌ تكلّم‌كننده‌ من‌ هستم‌، هان‌ من‌ هستم‌.",
			"7": "<span>7 </span> &nbsp;چه‌ زیبا است‌ بر كوهها پایهای‌ مبشّر كه‌ سلامتی‌ را ندا می‌كند و به‌ خیرات‌ بشارت‌ می‌دهد و نجات‌ را ندا می‌كند و به‌ صهیون‌ می‌گوید كه‌ خدای‌ تو سلطنت‌ می‌نماید.",
			"8": "<span>8 </span> &nbsp;آواز دیده‌بانان‌ تو است‌ كه‌ آواز خود را بلند كرده‌، با هم‌ ترنّم‌ می‌نمایند، زیرا وقتی‌ كه‌ خداوند به‌ صهیون‌ رجعت‌ می‌كند ایشان‌ معاینه‌ خواهند دید.",
			"9": "<span>9 </span> &nbsp;ای‌ خرابه‌های‌ اورشلیم‌ به‌ آواز بلند با هم‌ ترنّم‌ نمایید، زیرا خداوند قوم‌ خود را تسلّی‌ داده‌، و اورشلیم‌ را فدیه‌ نموده‌ است‌.",
			"10": "<span>10 </span> &nbsp;خداوند ساعد قدّوس‌ خود را در نظر تمامی‌ امّت‌ها بالا زده‌ است‌ و جمیع‌ كرانه‌های‌ زمین‌ نجات‌ خدای‌ ما را دیده‌اند.",
			"11": "<span>11 </span> &nbsp;ای‌ شما كه‌ ظروف‌ خداوند را برمی‌دارید، به‌ یك‌ سو شوید، به‌ یك‌ سو شوید و از اینجا بیرون‌ روید و چیز ناپاك‌ را لمس‌ منمایید و از میان‌ آن‌ بیرون‌ رفته‌، خویشتن‌ را طاهر سازید.",
			"12": "<span>12 </span> &nbsp;زیرا كه‌ به‌ تعجیل‌ بیرون‌ نخواهید رفت‌ و گریزان‌ روانه‌ نخواهید شد، چونكه‌ یهوه‌ پیش‌ روی‌ شما خواهد خرامید و خدای‌ اسرائیل‌ ساقه‌ شما خواهد بود .",
			"13": "<span>13 </span> &nbsp;اینك‌ بنده‌ من‌ با عقل‌ رفتار خواهد كرد و عالی‌ و رفیع‌ و بسیار بلند خواهد شد.",
			"14": "<span>14 </span> &nbsp;چنانكه‌ بسیاری‌ از تو در عجب‌ بودند (از آن‌ جهت‌ كه‌ منظر او از مردمان‌ و صورت‌ او از بنی‌آدم‌ بیشتر تباه‌ گردیده‌ بود).",
			"15": "<span>15 </span> &nbsp;همچنان‌ بر امّت‌های‌ بسیار خواهد پاشید و به‌ سبب‌ او پادشاهان‌ دهان‌ خود راخواهند بست‌ زیرا چیزهایی‌ را كه‌ برای‌ ایشان‌ بیان‌ نشده‌ بود خواهند دید و آنچه‌ را كه‌ نشنیده‌ بودند خواهند فهمید."
		},
		"53": {
			"1": "<span>1 </span> &nbsp;كیست‌ كه‌ خبر ما را تصدیق‌ نموده‌ وكیست‌ كه‌ ساعد خداوند بر او منكشف‌ شده‌ باشد؟",
			"2": "<span>2 </span> &nbsp;زیرا به‌ حضور وی‌ مثل‌ نهال‌ و مانند ریشه‌ در زمین‌ خشك‌ خواهد رویید. او را نه‌ صورتی‌ و نه‌ جمالی‌ می‌باشد. و چون‌ او را می‌نگریم‌ منظری‌ ندارد كه‌ مشتاق‌ او باشیم‌.",
			"3": "<span>3 </span> &nbsp;خوار و نزد مردمان‌ مردود و صاحب‌ غمها و رنج‌ دیده‌ و مثل‌ كسی‌ كه‌ رویها را از او بپوشانند و خوار شده‌ كه‌ او را به‌ حساب‌ نیاوردیم‌.",
			"4": "<span>4 </span> &nbsp;لكن‌ او غم‌های‌ ما را بر خود گرفت‌ و دردهای‌ ما را بر خویش‌ حمل‌ نمود. و ما او را از جانب‌ خدا زحمت‌ كشیده‌ و مضروب‌ و مبتلا گمان‌ بردیم.",
			"5": "<span>5 </span> &nbsp;و حال‌ آنكه‌ به‌ سبب‌ تقصیرهای‌ ما مجروح‌ و به‌ سبب‌ گناهان‌ ما كوفته‌ گردید. و تأدیب‌ سلامتی‌ ما بر وی‌ آمد و از زخمهای‌ او ما شفا یافتیم‌.",
			"6": "<span>6 </span> &nbsp;جمیع‌ ما مثل‌ گوسفندان‌ گمراه‌ شده‌ بودیم‌ و هریكی‌ از ما به‌ راه‌ خود برگشته‌ بود و خداوند گناه‌ جمیع‌ ما را بروی‌ نهاد.",
			"7": "<span>7 </span> &nbsp;او مظلوم‌ شد امّا تواضع‌ نموده‌، دهان‌ خود را نگشود. مثل‌ بره‌ای‌ كه‌ برای‌ ذبح‌ می‌برند و مانند گوسفندی‌ كه‌ نزد پشم‌ برنده‌اش‌ بی‌زبان‌ است‌ همچنان‌ دهان‌ خود را نگشود.",
			"8": "<span>8 </span> &nbsp;از ظلم‌ و از داوری‌ گرفته‌ شد. و از طبقه‌ او كه‌ تفكّر نمود كه‌ او از زمین‌ زندگان‌ منقطع‌ شد و به‌ جهت‌ گناه‌ قوم‌ من‌ مضروب‌ گردید؟",
			"9": "<span>9 </span> &nbsp;و قبر او را با شریران‌ تعیین‌ نمودند و بعد از مردنش‌ با دولتمندان‌. هرچندهیچ‌ ظلم‌ نكرد و در دهان‌ وی‌ حیله‌ای‌ نبود.",
			"10": "<span>10 </span> &nbsp;امّا خداوند را پسند آمد كه‌ او را مضروب‌ نموده‌، به‌ دردها مبتلا سازد. چون‌ جان‌ او را قربانی‌ گناه‌ ساخت‌، آنگاه‌ ذریت‌ خود را خواهد دید و عمر او دراز خواهد شد و مسرّت‌ خداوند در دست‌ او میسّر خواهد بود.",
			"11": "<span>11 </span> &nbsp;ثمره‌ مشقّت‌ جان‌ خویش‌ را خواهد دید و سیر خواهد شد. و بنده‌ عادل‌ من‌ به‌ معرفت‌ خود بسیاری‌ را عادل‌ خواهد گردانید زیرا كه‌ او گناهان‌ ایشان‌ را بر خویشتن‌ حمل‌ خواهد نمود.",
			"12": "<span>12 </span> &nbsp;بنابراین‌ او را در میان‌ بزرگان‌ نصیب‌ خواهم‌ داد و غنیمت‌ را با زورآوران‌ تقسیم‌ خواهد نمود، به‌ جهت‌ اینكه‌ جان‌ خود را به‌ مرگ‌ ریخت‌ و از خطاكاران‌ محسوب‌ شد و گناهان‌ بسیاری‌ را بر خود گرفت‌ و برای‌ خطاكاران‌ شفاعت‌ نمود."
		},
		"54": {
			"1": "<span>1 </span> &nbsp;ای‌ عاقره‌ای‌ كه‌ نزاییده‌ای‌ بسرا! ای‌ كه درد زه‌ نكشیده‌ای‌ به‌ آواز بلند ترنّم‌ نما و فریاد برآور! زیرا خداوند می‌گوید پسران‌ زن‌ بی‌كس‌ از پسران‌ زن‌ منكوحه‌ زیاده‌اند.",
			"2": "<span>2 </span> &nbsp;مكان‌ خیمه‌ خود را وسیع‌ گردان‌ و پرده‌های‌ مسكن‌های‌ تو پهن‌ بشود دریغ‌ مدار و طنابهای‌ خود را دراز كرده‌، میخهایت‌ را محكم‌ بساز.",
			"3": "<span>3 </span> &nbsp;زیرا كه‌ بطرف‌ راست‌ و چپ‌ منتشر خواهی‌ شد و ذریت‌ تو امّت‌ها را تصرّف‌ خواهند نمود و شهرهای‌ ویران‌ را مسكون‌ خواهند ساخت‌.",
			"4": "<span>4 </span> &nbsp;مترس‌ زیرا كه‌ خجل‌ نخواهی‌ شد و مشوّش‌ مشو زیرا كه‌ رسوا نخواهی‌ گردید. چونكه‌ خجالت‌ جوانی‌ خویش‌را فراموش‌ خواهی‌ كرد و عار بیوگی‌ خود را دیگر به‌ یاد نخواهی‌ آورد.",
			"5": "<span>5 </span> &nbsp;زیرا كه‌ آفریننده‌ تو كه‌ اسمش‌ یهوه‌ صبایوت‌ است‌ شوهر تو است‌، و قدّوس‌ اسرائیل‌ كه‌ به‌ خدای‌ تمام‌ جهان‌ مسمّی‌ است‌ ولّی‌ تو می‌باشد.",
			"6": "<span>6 </span> &nbsp;زیرا خداوند تو را مثل‌ زن‌ مهجور و رنجیده‌ دل‌ خوانده‌ است‌ و مانند زوجه‌ جوانی‌ كه‌ ترك‌ شده‌ باشد. خدای‌ تو این‌ را می‌گوید.",
			"7": "<span>7 </span> &nbsp;زیرا تو را به‌ اندك‌ لحظه‌ای‌ ترك‌ كردم‌ امّا به‌ رحمت‌های‌ عظیم‌ تو را جمع‌ خواهم‌ نمود.",
			"8": "<span>8 </span> &nbsp;و خداوند ولی تو می‌گوید: به‌ جوشش‌ غضبی‌ خود را از تو برای‌ لحظه‌ای‌ پوشانیدم‌ امّا به‌ احسان‌ جاودانی‌ برتو رحمت‌ خواهم‌ فرمود.",
			"9": "<span>9 </span> &nbsp;زیرا كه‌ این‌ برای‌ من‌ مثل‌ آبهای‌ نوح‌ می‌باشد. چنانكه‌ قسم‌ خوردم‌ كه‌ آبهای‌ نوح‌ بار دیگر بر زمین‌ جاری‌ نخواهد شد، همچنان‌ قسم‌ خوردم‌ كه‌ بر تو غضب‌ نكنم‌ و تو را عتاب‌ ننمایم‌.",
			"10": "<span>10 </span> &nbsp;هرآینه‌ كوهها زایل‌ خواهد شد و تلّها متحرّك‌ خواهد گردید، لیكن‌ احسان‌ من‌ از تو زایل‌ نخواهد شد و عهد سلامتی‌ من‌ متحرّك‌ نخواهد گردید. خداوند كه‌ بر تو رحمت‌ می‌كند این‌ را می‌گوید.",
			"11": "<span>11 </span> &nbsp;ای‌ رنجانیده‌ و مضطرب‌ شده‌ كه‌ تسلّی‌ نیافته‌ای‌، اینك‌ من‌ سنگهای‌ تو را در سنگ‌ سرمه‌ نصب‌ خواهم‌ كرد و بنیاد تو را در یاقوت‌ زرد خواهم‌ نهاد.",
			"12": "<span>12 </span> &nbsp;و مناره‌های‌ تو را از لعل‌ و دروازه‌هایت‌ را از سنگهای‌ بهرمان‌ و تمامی‌ حدود تو را از سنگهای‌ گران‌ قیمت‌ خواهم‌ ساخت‌.",
			"13": "<span>13 </span> &nbsp;و جمیع‌ پسرانت‌ از خداوند تعلیم‌ خواهند یافت‌ و پسرانت‌ را سلامتی‌ عظیم‌ خواهد بود.",
			"14": "<span>14 </span> &nbsp;در عدالت‌ ثابت‌ شده‌ و از ظلم‌ دور مانده‌،نخواهی‌ ترسید و هم‌ از آشفتگی‌ دور خواهی‌ ماند و به‌ تو نزدیكی‌ نخواهد نمود.",
			"15": "<span>15 </span> &nbsp;همانا جمع‌ خواهند شد امّا نه‌ به‌ اذن‌ من‌. آنانی‌ كه‌ به‌ ضّد تو جمع‌ شوند به‌ سبب‌ تو خواهند افتاد.",
			"16": "<span>16 </span> &nbsp;اینك‌ من‌ آهنگری‌ را كه‌ زغال‌ را به‌ آتش‌ دمیده‌، آلتی‌ برای‌ كار خود بیرون‌ می‌آورد، آفریدم‌. و من‌ نیز هلاك‌ كننده‌ را برای‌ خراب‌ نمودن‌ آفریدم‌.",
			"17": "<span>17 </span> &nbsp;هر آلتی‌ كه‌ به‌ ضدّ تو ساخته‌ شود، پیش‌ نخواهد برد و هر زبانی‌ را كه‌ برای‌ محاكمه‌ به‌ ضدّ تو برخیزد، تكذیب‌ خواهی‌ نمود. این‌ است‌ نصیب‌ بندگان‌ خداوند و عدالت‌ ایشان‌ از جانب‌ من‌. خداوند می‌گوید."
		},
		"55": {
			"1": "<span>1 </span> &nbsp;ای‌ جمیع‌ تشنگان‌ نزد آبها بیایید، و همه‌ شما كه‌ نقره‌ ندارید بیایید بخرید و بخورید. بیایید و شراب‌ و شیر را بی‌نقره‌ و بی‌قیمت‌ بخرید.",
			"2": "<span>2 </span> &nbsp;چرا نقره‌ را برای‌ آنچه‌ نان‌ نیست‌ و مشقّت‌ خویش‌ را برای‌ آنچه‌ سیر نمی‌كند صرف‌ می‌كنید. گوش‌ داده‌، از من‌ بشنوید و چیزهای‌ نیكو را بخورید تا جان‌ شما از فربهی‌ متلذّذ شود.",
			"3": "<span>3 </span> &nbsp;گوش‌ خود را فرا داشته‌، نزد من‌ بیایید و تا جان‌ شما زنده‌ گردد بشنوید و من‌ با شما عهد جاودانی‌ یعنی‌ رحمت‌های‌ امین‌ داود را خواهم‌ بست‌.",
			"4": "<span>4 </span> &nbsp;اینك‌ من‌ او را برای‌ طوایف‌ شاهد گردانیدم‌. رئیس‌ و حاكم‌ طوایف‌.",
			"5": "<span>5 </span> &nbsp;هان‌ امّتی‌ را كه‌ نشناخته‌ بودی‌ دعوت‌ خواهی‌ نمود، و امّتی‌ كه‌ تو را نشناخته‌ بودند، نزد تو خواهند دوید. به‌ خاطر یهوه‌ كه‌ خدای‌ تو است‌ و قدّوس‌ اسرائیل‌ كه‌ تو را تمجید نموده‌ است‌.",
			"6": "<span>6 </span> &nbsp;خداوند را مادامی‌ كه‌ یافت‌ می‌شود بطلبید و مادامی‌ كه‌ نزدیك‌ است‌ او را بخوانید.",
			"7": "<span>7 </span> &nbsp;شریر راه‌ خود را و گناه‌كار افكار خویش‌ را ترك‌ نماید و بسوی‌ خداوند بازگشت‌ كند و بر وی‌ رحمت‌ خواهد نمود و بسوی‌ خدای‌ ما كه‌ مغفرت‌ عظیم‌ خواهد كرد.",
			"8": "<span>8 </span> &nbsp;زیرا خداوند می‌گوید كه‌ افكار من‌ افكار شما نیست‌ و طریق‌های‌ شما طریق‌های‌ من‌ نی‌.",
			"9": "<span>9 </span> &nbsp;زیرا چنانكه‌ آسمان‌ از زمین‌ بلندتر است‌ همچنان‌ طریق‌های‌ من‌ از طریق‌های‌ شما و افكار من‌ از افكار شما بلندتر می‌باشد.",
			"10": "<span>10 </span> &nbsp;و چنانكه‌ باران‌ و برف‌ از آسمان‌ می‌بارد و به‌ آنجا برنمی‌گردد بلكه‌ زمین‌ را سیراب‌ كرده‌، آن‌ را بارور و برومند می‌سازد و برزگر را تخم‌ و خورنده‌ را نان‌ می‌بخشد،",
			"11": "<span>11 </span> &nbsp;همچنان‌ كلام‌ من‌ كه‌ از دهانم‌ صادر گردد خواهد بود. نزد من‌ بی‌ثمر نخواهد برگشت‌ بلكه‌ آنچه‌ را كه‌ خواستم‌ بجا خواهد آورد و برای‌ آنچه‌ آن‌ را فرستادم‌ كامران‌ خواهد گردید.",
			"12": "<span>12 </span> &nbsp;زیرا كه‌ شما با شادمانی‌ بیرون‌ خواهید رفت‌ و با سلامتی‌ هدایت‌ خواهید شد. كوهها و تلّها در حضور شما به‌ شادی‌ ترنّم‌ خواهند نمود و جمیع‌ درختان‌ صحرا دستك‌ خواهند زد.",
			"13": "<span>13 </span> &nbsp;به‌ جای‌ درخت‌ خار صنوبر و به‌ جای‌ خس‌ آس‌ خواهد رویید و برای‌ خداوند اسم‌ و آیت‌ جاودانی‌ كه‌ منقطع‌ نشود خواهد بود."
		},
		"56": {
			"1": "<span>1 </span> &nbsp;و خداوند چنین‌ می‌گوید: انصاف‌ را نگاه‌ داشته‌، عدالت‌ را جاری‌ نمایید، زیرا كه‌ آمدن‌ نجات‌ من‌ و منكشف‌ شدن‌ عدالت‌ من‌ نزدیك‌ است‌.",
			"2": "<span>2 </span> &nbsp;خوشابحال‌ انسانی‌ كه‌ این‌ رابجا آورد و بنی‌آدمی‌ كه‌ به‌ این‌ متمسّك‌ گردد، كه‌ سَبَّت‌ را نگاه‌ داشته‌، آن‌ را بی‌حرمت‌ نكند و دست‌ خویش‌ را از هر عمل‌ بد باز دارد.",
			"3": "<span>3 </span> &nbsp;پس‌ غریبی‌ كه‌ با خداوند مقترن‌ شده‌ باشد، تكلّم‌ نكند و نگوید كه‌ خداوند مرا از قوم‌ خود جدا نموده‌ است‌ و خصی‌ هم‌ نگوید كه‌ اینك‌ من‌ درخت‌ خشك‌ هستم‌.",
			"4": "<span>4 </span> &nbsp;زیرا خداوند درباره‌ خصیهایی‌ كه‌ سَبَّت‌های‌ مرا نگاه‌ دارند و آنچه‌ را كه‌ من‌ خوش‌ دارم‌ اختیار نمایند و به‌ عهد من‌ متمسّك‌ گردند، چنین‌ می‌گوید",
			"5": "<span>5 </span> &nbsp;كه‌ به‌ ایشان‌ در خانه‌ خود و در اندرون‌ دیوارهای‌ خویش‌ یادگاری‌ و اسمی‌ بهتر از پسران‌ و دختران‌ خواهم‌ داد. اسمی‌ جاودانی‌ كه‌ منقطع‌ نخواهد شد به‌ ایشان‌ خواهم‌ بخشید.",
			"6": "<span>6 </span> &nbsp;و غریبانی‌ كه‌ با خداوند مقترن‌ شده‌، او را خدمت‌ نمایند و اسم‌ خداوند را دوست‌ داشته‌، بنده‌ او بشوند. یعنی‌ همه‌ كسانی‌ كه‌ سَبَّت‌ را نگاه‌ داشته‌، آن‌ را بی‌حرمت‌ نسازند و به‌ عهد من‌ متمسّك‌ شوند.",
			"7": "<span>7 </span> &nbsp;ایشان‌ را به‌ كوه‌ قدس‌ خود خواهم‌ آورد و ایشان‌ را در خانه‌ عبادت‌ خود شادمان‌ خواهم‌ ساخت‌ و قربانی‌های‌ سوختنی‌ و ذبایح‌ ایشان‌ بر مذبح‌ من‌ قبول‌ خواهد شد، زیرا خانه‌ من‌ به‌ خانه‌ عبادت‌ برای‌ تمامی‌ قوم‌ها مسمّی‌ خواهد شد.",
			"8": "<span>8 </span> &nbsp;و خداوند یهوه‌ كه‌ جمع‌كننده‌ رانده‌شدگان‌ اسرائیل‌ است‌ می‌گوید كه‌ بعد از این‌ دیگران‌ را با ایشان‌ جمع‌ خواهم‌ كرد علاوه‌ برآنانی‌ كه‌ از ایشان‌ جمع‌ شده‌اند.",
			"9": "<span>9 </span> &nbsp;ای‌ تمام‌ حیوانات‌ صحرا و ای‌ جمیع‌ حیوانات‌ جنگل‌ بیایید و بخورید!",
			"10": "<span>10 </span> &nbsp;دیده‌بانان‌ او كورند، جمیع‌ ایشان‌ معرفت‌ ندارند و همگی‌ ایشان‌ سگان‌ گنگ‌اند كه‌ نمی‌توانند بانگ‌ كنند.خواب‌ می‌بینند و دراز شده‌، خفتن‌ را دوست‌ می‌دارند.",
			"11": "<span>11 </span> &nbsp;و این‌ سگان‌ حریصند كه‌ نمی‌توانند سیر بشوند و ایشان‌ شبانند كه‌ نمی‌توانند بفهمند.جمیع‌ ایشان‌ به‌ راه‌ خود میل‌ كرده‌، هر یكی‌ بطرف‌ خود طالب‌ سود خویش‌ می‌باشد.",
			"12": "<span>12 </span> &nbsp;(و می‌گویند) بیایید شراب‌ بیاوریم‌ و از مسكرات‌ مست‌ شویم‌ و فردا مثل‌ امروز روز عظیم‌ بلكه‌ بسیار زیاده‌ خواهد بود."
		},
		"57": {
			"1": "<span>1 </span> &nbsp;مرد عادل‌ تلف‌ شد و كسی‌ نیست‌ كه‌ این‌را در دل‌ خود بگذراند و مردان‌ رؤف‌ برداشته‌ شدند و كسی‌ فكر نمی‌كند كه‌ عادلان‌ از معرض‌ بلا برداشته‌ می‌شوند.",
			"2": "<span>2 </span> &nbsp;آنانی‌ كه‌ به‌ استقامت‌ سالك‌ می‌باشند، به‌ سلامتی‌ داخل‌ شده‌، بر بسترهای‌ خویش‌ آرامی‌ خواهند یافت‌.",
			"3": "<span>3 </span> &nbsp;و امّا شما ای‌ پسران‌ ساحره‌ و اولاد فاسق‌ و زانیه‌ به‌ اینجا نزدیك‌ آیید!",
			"4": "<span>4 </span> &nbsp;بر كه‌ تمسخر می‌كنید و بر كه‌ دهان‌ خود را باز می‌كنید و زبان‌ را دراز می‌نمایید؟ آیا شما اولاد عصیان‌ و ذریت‌ كذب‌ نیستید",
			"5": "<span>5 </span> &nbsp;كه‌ در میان‌ بلوطها و زیر هر درخت‌ سبز خویشتن‌ را به‌ حرارت‌ می‌آورید و اطفال‌ را در وادیها زیر شكاف‌ صخره‌ها ذبح‌ می‌نمایید؟",
			"6": "<span>6 </span> &nbsp;در میان‌ سنگهای‌ مِلسای‌ وادی‌ نصیب‌ تو است‌ همینها قسمت‌ تو می‌باشد. برای‌ آنها نیز هدیه‌ ریختنی‌ ریختی‌ و هدیه‌ آردی‌ گذرانیدی‌ آیا من‌ از اینها تسلّی‌ خواهم‌ یافت‌؟",
			"7": "<span>7 </span> &nbsp;بر كوه‌ بلند و رفیع‌ بستر خود را گستردی‌ و به‌ آنجا نیز برآمده‌، قربانی‌ گذرانیدی‌.",
			"8": "<span>8 </span> &nbsp;و پشت‌ درها و باهوها یادگار خود را واگذاشتی‌ زیرا كه‌ خود را به‌ كسی‌ دیگر غیر از من‌ مكشوف‌ ساختی‌ و برآمده‌، بستر خود را پهن‌ كردی‌ و درمیان‌ خود و ایشان‌ عهد بسته‌،بستر ایشان‌ را دوست‌ داشتی‌ جایی‌ كه‌ آن‌ را دیدی‌.",
			"9": "<span>9 </span> &nbsp;و با روغن‌ در حضور پادشاه‌ رفته‌، عطریات‌ خود را بسیار كردی‌ و رسولان‌ خود را بجای‌ دور فرستاده‌، خویشتن‌ را تا به‌ هاویه‌ پست‌ گردانیدی‌.",
			"10": "<span>10 </span> &nbsp;از طولانی‌ بودن‌ راه‌ درمانده‌ شدی‌ امّا نگفتی‌ كه‌ امید نیست‌. تازگی‌ قوّت‌ خود را یافتی‌ پس‌ از این‌ جهت‌ ضعف‌ بهم‌ نرسانیدی‌.",
			"11": "<span>11 </span> &nbsp;از كه‌ ترسان‌ و هراسان‌ شدی‌ كه‌ خیانت‌ ورزیدی‌ و مرا بیاد نیاورده‌، این‌ را در دل‌ خود جا ندادی‌؟ آیا من‌ از زمان‌ قدیم‌ نیز ساكت‌ نماندم‌ پس‌ از این‌ جهت‌ از من‌ نترسیدی‌؟",
			"12": "<span>12 </span> &nbsp;من‌ عدالت‌ و اعمال‌ تو را بیان‌ خواهم‌ نمود كه‌ تو را منفعت‌ نخواهد داد.",
			"13": "<span>13 </span> &nbsp;چون‌ فریاد برمی‌آوری‌ اندوخته‌های‌ خودت‌ تو را خلاصی‌ بدهد و لكن‌ باد جمیع‌ آنها را خواهد برداشت‌ و نفس‌ آنها را خواهد برد. امّا هر كه‌ بر من‌ توكّل‌ دارد مالك‌ زمین‌ خواهد بود و وارث‌ جبل‌ قدس‌ من‌ خواهد گردید.",
			"14": "<span>14 </span> &nbsp;و گفته‌ خواهد شد برافرازید! راه‌ را برافرازید و مهیا سازید! و سنگ‌ مصادم‌ را از طریق‌ قوم‌ من‌ بردارید!",
			"15": "<span>15 </span> &nbsp;زیرا او كه‌ عالی‌ و بلند است‌ و ساكن‌ در ابدیت‌ می‌باشد و اسم‌ او قدّوس‌ است‌ چنین‌ می‌گوید: من‌ در مكان‌ عالی‌ و مقدّس‌ ساكنم‌ و نیز با كسی‌ كه‌ روح‌ افسرده‌ و متواضع‌ دارد، تا روح‌ متواضعان‌ را احیا نمایم‌ و دل‌ افسردگان‌ را زنده‌ سازم‌.",
			"16": "<span>16 </span> &nbsp;زیرا كه‌ تا به‌ ابد مخاصمه‌ نخواهم‌ نمود و همیشه‌ خشم‌ نخواهم‌ كرد مبادا روحها و جانهایی‌ كه‌ من‌ آفریدم‌ به‌ حضور من‌ ضعف‌ به‌ هم‌ رسانند.",
			"17": "<span>17 </span> &nbsp;به‌ سبب‌ گناه‌ طمع‌ وی‌ غضبناك‌ شده‌، او را زدم‌ و خود را مخفی‌ ساخته‌، خشم‌ نمودم‌ واو به‌ راه‌ دل‌ خود رو گردانیده‌، برفت‌.",
			"18": "<span>18 </span> &nbsp;طریق‌های‌ او را دیدم‌ و او را شفا خواهم‌ داد و او را هدایت‌ نموده‌، به‌ او و به‌ آنانی‌ كه‌ با وی‌ ماتم‌ گیرند تسلّی‌ بسیار خواهم‌ داد.",
			"19": "<span>19 </span> &nbsp;خداوند كه‌ آفریننده‌ ثمره‌ لبها است‌ می‌گوید: بر آنانی‌ كه‌ دورند سلامتی‌ باد و بر آنانی‌ كه‌ نزدیكند سلامتی‌ باد و من‌ ایشان‌ را شفا خواهم‌ بخشید.",
			"20": "<span>20 </span> &nbsp;امّا شریران‌ مثل‌ دریای‌ متلاطم‌ كه‌ نمی‌تواند آرام‌ گیرد و آبهایش‌ گل‌ و لجن‌ برمی‌اندازد می‌باشند.",
			"21": "<span>21 </span> &nbsp;خدای‌ من‌ می‌گوید كه‌ شریران‌ را سلامتی‌ نیست‌."
		},
		"58": {
			"1": "<span>1 </span> &nbsp;آواز خود را بلند كن‌ و دریغ‌ مدار و آواز خود را مثل‌ كَرِنّا بلند كرده‌، به‌ قوم‌ من‌ تقصیر ایشان‌ را و به‌ خاندان‌ یعقوب‌ گناهان‌ ایشان‌ را اعلام‌ نما.",
			"2": "<span>2 </span> &nbsp;و ایشان‌ هر روز مرا می‌طلبند و از دانستن‌ طریق‌های‌ من‌ مسرور می‌باشند. مثل‌ امّتی‌ كه‌ عدالت‌ را بجا آورده‌،حكم‌ خدای‌ خود را ترك‌ ننمودند. احكام‌ عدالت‌ را از من‌ سؤال‌ نموده‌، از تقرّب‌ جستن‌ به‌ خدا مسرور می‌شوند",
			"3": "<span>3 </span> &nbsp;(و می‌گویند): چرا روزه‌ داشتیم‌ و ندیدی‌ و جانهای‌ خویش‌ را رنجانیدیم‌ و ندانستی‌. اینك‌ شما در روز روزه‌ خویش‌ خوشی‌ خود را می‌یابید و بر عمله‌های‌ خود ظلم‌ می‌نمایید.",
			"4": "<span>4 </span> &nbsp;اینك‌ به‌ جهت‌ نزاع‌ و مخاصمه‌ روزه‌ می‌گیرید و به‌ لطمه‌ شرارت‌ می‌زنید. امروز روزه‌ نمی‌گیرید كه‌ آواز خود را در اعلی‌ علیین‌ بشنوانید.",
			"5": "<span>5 </span> &nbsp;آیا روزه‌ای‌ كه‌ من‌ می‌پسندم‌ مثل‌ این‌ است‌، روزی‌ كه‌ آدمی‌ جان‌ خود را برنجاند و سر خود را مثل‌ نی‌ خم‌ ساخته‌، پلاس‌ و خاكستر زیر خود بگستراند؟ آیا این‌ را روزه‌ و روز مقبول‌ خداوند می‌خوانی‌؟",
			"6": "<span>6 </span> &nbsp;مگر روزه‌ای‌ كه‌ من‌ می‌پسندم‌ این‌ نیست‌ كه‌ بندهای‌ شرارت‌ را بگشایید و گره‌های‌ یوغ‌ را باز كنید و مظلومان‌ را آزاد سازید و هر یوغ‌ را بشكنید؟",
			"7": "<span>7 </span> &nbsp;مگر این‌ نیست‌ كه‌ نان‌ خود را به‌ گرسنگان‌ تقسیم‌ نمایی‌ و فقیران‌ رانده‌ شده‌ را به‌ خانه‌ خود بیاوری‌ و چون‌ برهنه‌ را ببینی‌ او را بپوشانی‌ و خود را از آنانی‌ كه‌ از گوشت‌ تو می‌باشند مخفی‌ نسازی‌؟",
			"8": "<span>8 </span> &nbsp;آنگاه‌ نور تو مثل‌ فجر طالع‌ خواهد شد و صحّت‌ تو بزودی‌ خواهد رویید و عدالت‌ تو پیش‌ تو خواهد خرامید و جلال‌ خداوند ساقه‌ تو خواهد بود.",
			"9": "<span>9 </span> &nbsp;آنگاه‌ دعا خواهی‌ كرد و خداوند تو را اجابت‌ خواهد فرمود و استغاثه‌ خواهی‌ نمود و او خواهد گفت‌ كه‌ اینك‌ حاضر هستم‌. اگر یوغ‌ و اشاره‌ كردن‌ به‌ انگشت‌ و گفتن‌ ناحقّ را از میان‌ خود دور كنی‌،",
			"10": "<span>10 </span> &nbsp;و آرزوی‌ جان‌ خود را به‌ گرسنگان‌ ببخشی‌ و جان‌ ذلیلان‌ را سیر كنی‌، آنگاه‌ نور تو در تاریكی‌ خواهد درخشید و تاریكی‌ غلیظ‌ تو مثل‌ ظهر خواهد بود.",
			"11": "<span>11 </span> &nbsp;و خداوند تو را همیشه‌ هدایت‌ نموده‌، جان‌ تو را در مكان‌های‌ خشك‌ سیر خواهد كرد و استخوانهایت‌ را قوّی‌ خواهد ساخت‌ و تو مثل‌ باغ‌ سیرآب‌ و مانند چشمه‌ آب‌ كه‌ آبش‌ كم‌ نشود خواهی‌ بود.",
			"12": "<span>12 </span> &nbsp;و كسان‌ تو خرابه‌های‌ قدیم‌ را بنا خواهند نمود و تو اساسهای‌ دوره‌های‌ بسیار را برپا خواهی‌ داشت‌ و تو را عمارت‌ كننده‌ رخنه‌ها و مرمّت‌ كننده‌ كوچه‌ها برای‌ سكونت‌ خواهند خواند.",
			"13": "<span>13 </span> &nbsp;اگر پای‌ خود را از سَبَّت‌ نگاه‌ داری‌ و خوشی‌ خود را در روز مقدّس‌ من‌ بجا نیاوری‌ و سَبَّت‌ را خوشی‌ و مقدّس‌ خداوند و محترم‌بخوانی‌ و آن‌ را محترم‌ داشته‌، به‌ راههای‌ خود رفتار ننمایی‌ و خوشی‌ خود را نجویی‌ و سخنان‌ خود را نگویی‌،",
			"14": "<span>14 </span> &nbsp;آنگاه‌ در خداوند متلذّذ خواهی‌ شد و تو را بر مكان‌های‌ بلند زمین‌ سوار خواهم‌ كرد. و نصیب‌ پدرت‌ یعقوب‌ را به‌ تو خواهم‌ خورانید، زیرا كه‌ دهان‌ خداوند این‌ را گفته‌ است‌."
		},
		"59": {
			"1": "<span>1 </span> &nbsp;هان‌ دست‌ خداوند كوتاه‌ نیست‌ تا نرهاند و گوش‌ او سنگین‌ نی‌ تا نشنود.",
			"2": "<span>2 </span> &nbsp;لیكن‌ خطایای‌ شما در میان‌ شما و خدای‌ شما حایل‌ شده‌ است‌ و گناهان‌ شما روی‌ او را از شما پوشانیده‌ است‌ تا نشنود.",
			"3": "<span>3 </span> &nbsp;زیرا كه‌ دستهای‌ شما به‌ خون‌ و انگشتهای‌ شما به‌ شرارت‌ آلوده‌ شده‌ است‌. لبهای‌ شما به‌ دروغ‌ تكلّم‌ می‌نماید و زبانهای‌ شما به‌ شرارت‌ تنطّق‌ می‌كند.",
			"4": "<span>4 </span> &nbsp;احدی‌ به‌ عدالت‌ دعوی‌ نمی‌كند و هیچكس‌ به‌ راستی‌ داوری‌ نمی‌نماید. به‌ بطالت‌ توكّل‌ دارند و به‌ دروغ‌ تكلّم‌ می‌نمایند. به‌ ظلم‌ حامله‌ شده‌، شرارت‌ را می‌زایند.",
			"5": "<span>5 </span> &nbsp;از تخمهای‌ افعی‌ بچه‌ برمی‌آورند و پرده‌ عنكبوت‌ می‌بافند. هركه‌ از تخمهای‌ ایشان‌ بخورد می‌میرد و آن‌ چون‌ شكسته‌ گردد افعی‌ بیرون‌ می‌آید.",
			"6": "<span>6 </span> &nbsp;پرده‌های‌ ایشان‌ لباس‌ نخواهد شد و خویشتن‌ را از اعمال‌ خود نخواهند پوشانید زیرا كه‌ اعمال‌ ایشان‌ اعمال‌ شرارت‌ است‌ و عمل‌ ظلم‌ در دستهای‌ ایشان‌ است‌.",
			"7": "<span>7 </span> &nbsp;پایهای‌ ایشان‌ برای‌ بدی‌ دوان‌ و به‌ جهت‌ ریختن‌ خون‌ بی‌گناهان‌ شتابان‌ است‌. افكار ایشان‌ افكار شرارت‌ است‌ و در راههای‌ ایشان‌ ویرانی‌ و خرابی‌ است‌.",
			"8": "<span>8 </span> &nbsp;طریق‌سلامتی‌ را نمی‌دانند و در راههای‌ ایشان‌ انصاف‌ نیست‌. جادهّهای‌ كج‌ برای‌ خود ساخته‌اند و هر كه‌ در آنها سالك‌ باشد سلامتی‌ را نخواهد دانست‌.",
			"9": "<span>9 </span> &nbsp;بنابراین‌ انصاف‌ از ما دور شده‌ است‌ و عدالت‌ به‌ ما نمی‌رسد. انتظار نور می‌كشیم‌ و اینك‌ ظلمت‌ است‌ و منتظر روشنایی‌ هستیم‌، امّا در تاریكی‌ غلیظ‌ سالك‌ می‌باشیم‌.",
			"10": "<span>10 </span> &nbsp;و مثل‌ كوران‌ برای‌ دیوار تلمّس‌ می‌نماییم‌ و مانند بی‌چشمان‌ كورانه‌ راه‌ می‌رویم‌. در وقت‌ ظهر مثل‌ شام‌ لغزش‌ می‌خوریم‌ و در میان‌ تندرستان‌ مانند مردگانیم‌.",
			"11": "<span>11 </span> &nbsp;جمیع‌ ما مثل‌ خرسها صدا می‌كنیم‌ و مانند فاخته‌ها ناله‌ می‌نماییم‌، برای‌ انصاف‌ انتظار می‌كشیم‌ و نیست‌ و برای‌ نجات‌ و از ما دور می‌شود.",
			"12": "<span>12 </span> &nbsp;زیرا كه‌ خطایای‌ ما به‌ حضور تو بسیار شده‌ و گناهان‌ ما به‌ ضدّ ما شهادت‌ می‌دهد چونكه‌ خطایای‌ ما با ما است‌ و گناهان‌ خود را می‌دانیم‌.",
			"13": "<span>13 </span> &nbsp;مرتدّ شده‌، خداوند را انكار نمودیم‌. از پیروی‌ خدای‌ خود انحراف‌ ورزیدیم‌ به‌ ظلم‌ و فتنه‌ تكلّم‌ كردیم‌ و به‌ سخنان‌ كذب‌ حامله‌ شده‌، از دل‌ آنها را تنطّق‌ نمودیم‌.",
			"14": "<span>14 </span> &nbsp;و انصاف‌ به‌ عقب‌ رانده‌ شده‌ و عدالت‌ از ما دور ایستاده‌ است‌ زیرا كه‌ راستی‌ در كوچه‌ها افتاده‌ است‌ و استقامت‌ نمی‌تواند داخل‌ شود.",
			"15": "<span>15 </span> &nbsp;و راستی‌ مفقود شده‌ است‌ و هر كه‌ از بدی‌ اجتناب‌ نماید خود را به‌ یغما می‌سپارد. و چون‌ خداوند این‌ را دید در نظر او بد آمد كه‌ انصاف‌ وجود نداشت‌.",
			"16": "<span>16 </span> &nbsp;و او دید كه‌ كسی‌ نبود و تعجّب‌ نمود كه‌ شفاعت‌ كننده‌ای‌ وجود نداشت‌؛ از این‌ جهت‌ بازوی‌ وی‌ برای‌ او نجات‌ آورد و عدالت‌ او وی‌ را دستگیری‌ نمود.",
			"17": "<span>17 </span> &nbsp;پس‌ عدالت‌را مثل‌ زره‌ پوشید و خود نجات‌ را بر سر خویش‌ نهاد. و جامه‌ انتقام‌ را به‌ جای‌ لباس‌ در بر كرد و غیرت‌ را مثل‌ ردا پوشید.",
			"18": "<span>18 </span> &nbsp;بر وفق‌ اعمال‌ ایشان‌، ایشان‌ را جزا خواهد داد. به‌ خصمان‌ خود حدّت‌ خشم‌ را و به‌ دشمنان‌ خویش‌ مكافات‌ و به‌ جزایر پاداش‌ را خواهد رسانید.",
			"19": "<span>19 </span> &nbsp;و از طرف‌ مغرب‌ از نام‌ یهوه‌ و از طلوع‌ آفتاب‌ از جلال‌ وی‌ خواهند ترسید زیرا كه‌ او مثل‌ نهر سرشاری‌ كه‌ باد خداوند آن‌ را براند خواهد آمد.",
			"20": "<span>20 </span> &nbsp;و خداوند می‌گوید كه‌ نجات‌ دهنده‌ای‌ برای‌ صهیون‌ و برای‌ آنانی‌ كه‌ در یعقوب‌ از معصیت‌ بازگشت‌ نمایند خواهد آمد.",
			"21": "<span>21 </span> &nbsp;و خداوند می‌گوید: امّا عهد من‌ با ایشان‌ این‌ است‌ كه‌ روح‌ من‌ كه‌ بر تو است‌ و كلام‌ من‌ كه‌ در دهان‌ تو گذاشته‌ام‌ از دهان‌ تو و از دهان‌ ذرّیت‌ تو و از دهان‌ ذرّیت‌ ذرّیت‌ تو دور نخواهد شد. خداوند می‌گوید: از الا´ن‌ و تا ابدالا´باد."
		},
		"60": {
			"1": "<span>1 </span> &nbsp;برخیز و درخشان‌ شو زیرا نور تو آمده ‌وجلال‌ خداوند بر تو طالع‌ گردیده‌ است‌.",
			"2": "<span>2 </span> &nbsp;زیرا اینك‌ تاریكی‌ جهان‌ را و ظلمت‌ غلیظ‌ طوایف‌ را خواهد پوشانید امّا خداوند بر تو طلوع‌ خواهد نمود و جلال‌ وی‌ بر تو ظاهر خواهد شد.",
			"3": "<span>3 </span> &nbsp;و امّت‌ها بسوی‌ نور تو و پادشاهان‌ بسوی‌ درخشندگی‌ طلوع‌ تو خواهند آمد.",
			"4": "<span>4 </span> &nbsp;چشمان‌ خود را به‌ اطراف‌ خویش‌ برافراز و ببین‌ كه‌ جمیع‌ آنها جمع‌ شده‌، نزد تو می‌آیند. پسرانت‌ از دور خواهند آمد و دخترانت‌ را در آغوش‌ خواهند آورد.",
			"5": "<span>5 </span> &nbsp;آنگاه‌ خواهی‌ دید و خواهی‌ درخشید و دل‌ تو لرزان‌ شده‌، وسیع‌ خواهدگردید، زیرا كه‌ توانگری‌ دریا بسوی‌ تو گردانیده‌ خواهد شد و دولت‌ امّت‌ها نزد تو خواهد آمد.",
			"6": "<span>6 </span> &nbsp;كثرت‌ شتران‌ و جمازگان‌ مدیان‌ و عیفه‌ تو را خواهند پوشانید. جمیع‌ اهل‌ شبع‌ خواهند آمد و طلا و بخور آورده‌، به‌ تسبیح‌ خداوند بشارت‌ خواهند داد.",
			"7": "<span>7 </span> &nbsp;جمیع‌ گله‌های‌ قیدار نزد تو جمع‌ خواهند شد و قوچهای‌ نبایوت‌ تو را خدمت‌ خواهند نمود. به‌ مذبح‌ من‌ با پذیرایی‌ برخواهند آمد و خانه‌ جلال‌ خود را زینت‌ خواهم‌ داد.",
			"8": "<span>8 </span> &nbsp;اینها كیستند كه‌ مثل‌ ابر پرواز می‌كنند و مانند كبوتران‌ بر وزنهای‌ خود؟",
			"9": "<span>9 </span> &nbsp;به‌ درستی‌ كه‌ جزیره‌ها و كشتیهای‌ ترشیش‌ اوّل‌ انتظار مرا خواهند كشید تا پسران‌ تو را از دور و نقره‌ و طلای‌ ایشان‌ را با ایشان‌ بیاورند، به‌ جهت‌ اسم‌ یهوه‌ خدای‌ تو و به‌ جهت‌ قدّوس‌ اسرائیل‌ زیرا كه‌ تو را زینت‌ داده‌ است‌.",
			"10": "<span>10 </span> &nbsp;و غریبان‌، حصارهای‌ تو را بنا خواهند نمود و پادشاهان‌ ایشان‌ تو را خدمت‌ خواهند كرد زیرا كه‌ در غضب‌ خود تو را زدم‌ لیكن‌ به‌ لطف‌ خویش‌ تو را ترحّم‌ خواهم‌ نمود.",
			"11": "<span>11 </span> &nbsp;دروازه‌های‌ تو نیز دائماً باز خواهد بود و شب‌ و روز بسته‌ نخواهد گردید تا دولت‌ امّت‌ها را نزد تو بیاورند و پادشاهان‌ ایشان‌ همراه‌ آورده‌ شوند.",
			"12": "<span>12 </span> &nbsp;زیرا هر امّتی‌ و مملكتی‌ كه‌ تو را خدمت‌ نكند تلف‌ خواهد شد و آن‌ امّت‌ها تماماً هلاك‌ خواهند گردید.",
			"13": "<span>13 </span> &nbsp;جلال‌ لبنان‌ با درختان‌ صنوبر و كاج‌ و چنار با هم‌ برای‌ تو آورده‌ خواهند شد تا مكان‌ مقدّس‌ مرا زینت‌ دهند و جای‌ پایهای‌ خود را تمجید خواهم‌ نمود.",
			"14": "<span>14 </span> &nbsp;و پسران‌ آنانی‌ كه‌ تو را ستم‌می‌رسانند خم‌ شده‌، نزد تو خواهند آمد و جمیع‌ آنانی‌ كه‌ تو را اهانت‌ می‌نمایند نزد كف‌ پایهای‌ تو سجده‌ خواهند نمود و تو را شهر یهوه‌ و صهیونِ قدّوسِ اسرائیل‌ خواهند نامید.",
			"15": "<span>15 </span> &nbsp;به‌ عوض‌ آنكه‌ تو متروك‌ و مبغوض‌ بودی‌ و كسی‌ از میان‌ تو گذر نمی‌كرد. من‌ تو را فخر جاودانی‌ و سرور دهرهای‌ بسیار خواهم‌ گردانید.",
			"16": "<span>16 </span> &nbsp;و شیر امّت‌ها را خواهی‌ مكید و پستانهای‌ پادشاهان‌ را خواهی‌ مكید و خواهی‌ فهمید كه‌ من‌ یهوه‌ نجات‌ دهنده‌ تو هستم‌ و من‌ قدیر اسرائیل‌، ولی تو می‌باشم‌.",
			"17": "<span>17 </span> &nbsp;به‌ جای‌ برنج‌، طلا خواهم‌ آورد و به‌ جای‌ آهن‌، نقره‌ و به‌ جای‌ چوب‌، برنج‌ و به‌ جای‌ سنگ‌، آهن‌ خواهم‌ آورد و سلامتی‌ را ناظران‌ تو و عدالت‌ را حاكمان‌ تو خواهم‌ گردانید.",
			"18": "<span>18 </span> &nbsp;و بار دیگر ظلم‌ در زمین‌ تو و خرابی‌ و ویرانی‌ در حدود تو مسموع‌ نخواهد شد و حصارهای‌ خود را نجات‌ و دروازه‌های‌ خویش‌ را تسبیح‌ خواهی‌ نامید.",
			"19": "<span>19 </span> &nbsp;و بار دیگر آفتاب‌ در روز نور تو نخواهد بود و ماه‌ با درخشندگی‌ برای‌ تو نخواهد تابید زیرا كه‌ یهوه‌ نور جاودانی‌ تو و خدایت‌ زیبایی‌ تو خواهد بود.",
			"20": "<span>20 </span> &nbsp;و بار دیگر آفتاب‌ تو غروب‌ نخواهد كرد و ماه‌ تو زوال‌ نخواهد پذیرفت‌ زیرا كه‌ یهوه‌ برای‌ تو نور جاودانی‌ خواهد بود و روزهای‌ نوحه‌گری‌ تو تمام‌ خواهد شد.",
			"21": "<span>21 </span> &nbsp;و جمیع‌ قوم‌ تو عادل‌ خواهند بود و زمین‌ را تا به‌ ابد متصرّف‌ خواهند شد. شاخه‌ مغروس‌ من‌ و عمل‌ دست‌ من‌، تا تمجید كرده‌ شوم‌.",
			"22": "<span>22 </span> &nbsp;صغیر هزار نفر خواهد شد و حقیر امّت‌ قوّی‌ خواهد گردید. من‌ یهوه‌ در وقتش‌ تعجیل‌ در آن‌ خواهم‌ نمود."
		},
		"61": {
			"1": "<span>1 </span> &nbsp;روح‌ خداوند یهوه‌ بر من‌ است‌ زیرا خداوند مرا مسح‌ كرده‌ است‌ تا مسكینان‌ را بشارت‌ دهم‌ و مرا فرستاده‌ تا شكسته‌ دلان‌ را التیام‌ بخشم‌ و اسیران‌ را به‌ رستگاری‌ و محبوسان‌ را به‌ آزادی‌ ندا كنم‌،",
			"2": "<span>2 </span> &nbsp;و تا از سال‌ پسندیده‌ خداوند و از یوم‌ انتقام‌ خدای‌ ما ندا نمایم‌ و جمیع‌ ماتمیان‌ را تسلّی‌ بخشم‌.",
			"3": "<span>3 </span> &nbsp;تا قرار دهم‌ برای‌ ماتمیانِ صهیون‌ و به‌ ایشان‌ ببخشم‌ تاجی‌ را به‌ عوض‌ خاكستر و روغن‌ شادمانی‌ را به‌ عوض‌ نوحه‌گری‌ و ردای‌ تسبیح‌ را به‌ جای‌ روح‌ كدورت‌ تا ایشان‌ درختان‌ عدالت‌ و مغروس‌ خداوند به‌ جهت‌ تمجید وی‌ نامیده‌ شوند.",
			"4": "<span>4 </span> &nbsp;و ایشان‌ خرابه‌های‌ قدیم‌ را بنا خواهند نمود و ویرانه‌های‌ سلف‌ را بر پا خواهند داشت‌ و شهرهای‌ خراب‌ خواهند نمود.",
			"5": "<span>5 </span> &nbsp;و غریبان‌ برپا شده‌، گلّه‌های‌ شما را خواهند چرانید و بیگانگان‌، فلاّحان‌ و باغبانان‌ شما خواهند بود.",
			"6": "<span>6 </span> &nbsp;و شما كاهنان‌ خداوند نامیده‌ خواهید شد و شما را به‌ خدّام‌ خدای‌ ما مسمّی‌ خواهند نمود. دولت‌ امّت‌ها را خواهید خورد و در جلال‌ ایشان‌ فخر خواهید نمود.",
			"7": "<span>7 </span> &nbsp;به‌ عوض‌ خجالت‌، نصیب‌ مضاعف‌ خواهند یافت‌ و به‌ عوض‌ رسوایی‌ از نصیب‌ خود مسرور خواهید شد. بنابراین‌ ایشان‌ در زمین‌ خود نصیب‌ مضاعف‌ خواهند یافت‌ و شادی‌ جاودانی‌ برای‌ ایشان‌ خواهد بود.",
			"8": "<span>8 </span> &nbsp;زیرا من‌ كه‌ یهوه‌ هستم‌، عدالت‌ را دوست‌ می‌دارم‌ و از غارت‌ و ستم‌ نفرت‌ می‌دارم‌ و اجرت‌ ایشان‌ را به‌ راستی‌ به‌ ایشان‌ خواهم‌ داد و عهد جاودانی‌ باایشان‌ خواهم‌ بست‌.",
			"9": "<span>9 </span> &nbsp;و نسل‌ ایشان‌ در میان‌ امّت‌ها و ذریت‌ ایشان‌ در میان‌ قوم‌ها معروف‌ خواهند شد. هر كه‌ ایشان‌ را بیند اعتراف‌ خواهد نمود كه‌ ایشان‌ ذریت‌ مبارك‌ خداوند می‌باشند.",
			"10": "<span>10 </span> &nbsp;در خداوند شادی‌ بسیار می‌كنم‌ و جان‌ من‌ در خدای‌ خود وجد می‌نماید زیرا كه‌ مرا به‌ جامه‌ نجات‌ ملبّس‌ ساخته‌، ردای‌ عدالت‌ را به‌ من‌ پوشانید. چنانكه‌ داماد خویشتن‌ را به‌ تاج‌ آرایش‌ می‌دهد و عروس‌، خود را به‌ زیورها زینت‌ می‌بخشد.",
			"11": "<span>11 </span> &nbsp;زیرا چنانكه‌ زمین‌، نباتات‌ خود را می‌رویاند و باغ‌، زرع‌ خویش‌ را نمّو می‌دهد، همچنان‌ خداوند یهوه‌ عدالت‌ و تسبیح‌ را پیش‌ روی‌ تمامی‌ امّت‌ها خواهد رویانید."
		},
		"62": {
			"1": "<span>1 </span> &nbsp;به‌ خاطر صهیون‌ سكوت‌ نخواهم‌ كرد و به‌ خاطر اورشلیم‌ خاموش‌ نخواهم‌ شد تا عدالتش‌ مثل‌ نور طلوع‌ كند و نجاتش‌ مثل‌ چراغی‌ كه‌ افروخته‌ باشد.",
			"2": "<span>2 </span> &nbsp;و امّت‌ها، عدالت‌ تو را و جمیع‌ پادشاهان‌، جلال‌ تو را مشاهده‌ خواهند نمود. و تو به‌ اسم‌ جدیدی‌ كه‌ دهان‌ خداوند آن‌ را قرار می‌دهد مسمّی‌ خواهی‌ شد.",
			"3": "<span>3 </span> &nbsp;و تو تاج‌ جلال‌، در دست‌ خداوند و افسر ملوكانه‌، در دست‌ خدای‌ خود خواهی‌ بود.",
			"4": "<span>4 </span> &nbsp;و تو دیگر به‌ متروك‌ مسمّی‌ نخواهی‌ شد و زمینت‌ را بار دیگر خرابه‌ نخواهند گفت‌، بلكه‌ تو را حَفصیبَه‌ و زمینت‌ را بَعولَه‌ خواهند نامید زیرا خداوند از تو مسرور خواهد شد و زمین‌ تو منكوحه‌ خواهد گردید.",
			"5": "<span>5 </span> &nbsp;زیرا چنانكه‌ مردی‌ جوان‌ دوشیزه‌ای‌ را به‌ نكاح‌ خویش‌ در می‌آورد، هم‌ چنان‌ پسرانت‌ تورا منكوحه‌ خود خواهند ساخت‌ و چنانكه‌ داماد از عروس‌ مبتهج‌ می‌گردد، هم‌ چنان‌ خدایت‌ از تو مسرور خواهد بود.",
			"6": "<span>6 </span> &nbsp;ای‌ اورشلیم‌ دیده‌بانان‌ بر حصارهای‌ تو گماشته‌ام‌ كه‌ هر روز و هرشب‌ همیشه‌ سكوت‌ نخواهند كرد. ای‌ متذكّران‌ خداوند خاموش‌ مباشید!",
			"7": "<span>7 </span> &nbsp;و او را آرامی‌ ندهید تا اورشلیم‌ را استوار كرده‌، آن‌ را در جهان‌ محلّ تسبیح‌ بسازد.",
			"8": "<span>8 </span> &nbsp;خداوند به‌ دست‌ راست‌ خود و به‌ بازوی‌ قوّی‌ خویش‌ قسم‌ خورده‌، گفته‌ است‌ كه‌ بار دیگر غلّه‌ تو را مأكول‌ دشمنانت‌ نسازم‌ و غریبان‌، شراب‌ تو را كه‌ برایش‌ زحمت‌ كشیده‌ای‌ نخواهند نوشید.",
			"9": "<span>9 </span> &nbsp;بلكه‌ آنانی‌ كه‌ آن‌ را می‌چینند آن‌ را خورده‌، خداوند را تسبیح‌ خواهند نمود و آنانی‌ كه‌ آن‌ را جمع‌ می‌كنند، آن‌ را در صحنهای‌ قدس‌ من‌ خواهند نوشید.",
			"10": "<span>10 </span> &nbsp;بگذرید از دروازه‌ها بگذرید. طریق‌ قوم‌ را مهیا سازید و شاهراه‌ را بلند كرده‌، مرتفع‌ سازید و سنگها را برچیده‌ عَلَم‌ را به‌ جهت‌ قوم‌ها برپا نمایید.",
			"11": "<span>11 </span> &nbsp;اینك‌ خداوند تا اقصای‌ زمین‌ اعلان‌ كرده‌ است‌، پس‌ به‌ دختر صهیون‌ بگویید اینك‌ نجات‌ تو می‌آید. همانا اجرت‌ او همراهش‌ و مكافات‌ او پیش‌ رویش‌ می‌باشد.",
			"12": "<span>12 </span> &nbsp;و ایشان‌ را به‌ قوم‌ مقدّس‌ و فدیه‌ شدگان‌ خداوند مسمّی‌ خواهند ساخت‌ و تو به‌ مطلوب‌ و شهر غیر متروك‌ نامیده‌ خواهی‌ شد."
		},
		"63": {
			"1": "<span>1 </span> &nbsp;این‌ كیست‌ كه‌ از ادوم‌ با لباس‌ سرخ‌ ازبُصرَه‌ می‌آید؟ یعنی‌ این‌ كه‌ به‌ لباس‌جلیل‌ خود ملبّس‌ است‌ و در كثرت‌ قوّت‌ خویش‌ می‌خرامد؟ من‌ كه‌ به‌ عدالت‌ تكلّم‌ می‌كنم‌ و برای‌ نجات‌، زورآور می‌باشم‌.",
			"2": "<span>2 </span> &nbsp;چرا لباس‌ تو سرخ‌ است‌ و جامه‌ تو مثل‌ كسی‌ كه‌ چرخشت‌ را پایمال‌ كند؟",
			"3": "<span>3 </span> &nbsp;من‌ چرخشت‌ را تنها پایمال‌ نمودم‌ و احدی‌ از قوم‌ها با من‌ نبود و ایشان‌ را به‌ غضب‌ خود پایمال‌ كردم‌ و به‌ حدّت‌ خشم‌ خویش‌ لگد كوب‌ نمودم‌ و خون‌ ایشان‌ به‌ لباس‌ من‌ پاشیده‌ شده‌، تمامی‌ جامه‌ خود را آلوده‌ ساختم‌.",
			"4": "<span>4 </span> &nbsp;زیرا كه‌ یوم‌ انتقام‌ در دل‌ من‌ بود و سال‌ فدیه‌ شدگانم‌ رسیده‌ بود.",
			"5": "<span>5 </span> &nbsp;و نگریستم‌ و اعانت‌ كننده‌ای‌ نبود و تعجّب‌ نمودم‌ زیرا دستگیری‌ نبود. لهذا بازوی‌ من‌ مرا نجات‌ داد و حدّت‌ خشم‌ من‌ مرا دستگیری‌ نمود.",
			"6": "<span>6 </span> &nbsp;و قوم‌ها را به‌ غضب‌ خود پایمال‌ نموده‌، ایشان‌ را از حدّت‌ خشم‌ خویش‌ مست‌ ساختم‌. و خون‌ ایشان‌ را بر زمین‌ ریختم‌.",
			"7": "<span>7 </span> &nbsp;احسانهای‌ خداوند و تسبیحات‌ خداوند را ذكر خواهم‌ نمود برحسب‌ هر آنچه‌ خداوند برای‌ ما عمل‌ نموده‌ است‌ و به‌ موجب‌ كثرت‌ احسانی‌ كه‌ برای‌ خاندان‌ اسرائیل‌ موافق‌ رحمت‌ها و وفور رأفت‌ خود بجا آورده‌ است‌.",
			"8": "<span>8 </span> &nbsp;زیرا گفته‌ است‌: ایشان‌ قوم‌ من‌ و پسرانی‌ كه‌ خیانت‌ نخواهند كرد می‌باشند؛ پس‌ نجات‌دهنده‌ ایشان‌ شده‌ است‌.",
			"9": "<span>9 </span> &nbsp;او در همه‌ تنگیهای‌ ایشان‌ به‌ تنگ‌ آورده‌ شد و فرشته‌ حضور وی‌ ایشان‌ را نجات‌ داد. در محبّت‌ و حلم‌ خود ایشان‌ را فدیه‌ داد و در جمیع‌ ایام‌ قدیم‌، متحمّل‌ ایشان‌ شده‌، ایشان‌ را برداشت‌.",
			"10": "<span>10 </span> &nbsp;امّا ایشان‌ عاصی‌ شده‌، روح‌ قدّوس‌ او را محزون‌ ساختند، پس‌ برگشته‌، دشمن‌ ایشان‌ شد و او خود با ایشان‌ جنگ‌ نمود.",
			"11": "<span>11 </span> &nbsp;آنگاه‌ ایام‌ قدیم‌ و موسی‌ و قوم‌ خویش‌ را بیاد آورد (و گفت‌) كجاست‌ آنكه‌ ایشان‌ را با شبان‌ گله‌ خود از دریا برآورد و كجا است‌ آنكه‌ روح‌ قدّوس‌ خود را در میان‌ ایشان‌ نهاد؟",
			"12": "<span>12 </span> &nbsp;كه‌ بازوی‌ جلیل‌ خود را به‌ دست‌ راست‌ موسی‌ خرامان‌ ساخت‌ و آبها را پیش‌ روی‌ ایشان‌ مُنْشَقّ گردانید تا اسم‌ جاودانی‌ برای‌ خویش‌ پیدا كند؟",
			"13": "<span>13 </span> &nbsp;آنكه‌ ایشان‌ را در لجّه‌ها مثل‌ اسب‌ در بیابان‌ رهبری‌ نمود كه‌ لغزش‌ نخورند.",
			"14": "<span>14 </span> &nbsp;مثل‌ بهایمی‌ كه‌ به‌ وادی‌ فرود می‌روند روح‌ خداوند ایشان‌ را آرامی‌ بخشید، هم‌ چنان‌ قوم‌ خود را رهبری‌ نمودی‌ تا برای‌ خود اسم‌ مجید پیدا نمایی‌.",
			"15": "<span>15 </span> &nbsp;از آسمان‌ بنگر و از مسكن‌ قدّوسیت‌ و جلال‌ خویش‌ نظر افكن‌. غیرت‌ جبروت‌ تو كجا است‌؟ جوشش‌ دل‌ و رحمت‌های‌ تو كه‌ به‌ من‌ نمودی‌ بازداشته‌ شده‌ است‌.",
			"16": "<span>16 </span> &nbsp;به‌ درستی‌ كه‌ تو پدر ما هستی‌ اگر چه‌ ابراهیم‌ ما را نشناسد و اسرائیل‌ ما را بجا نیاورد، امّا تو ای‌ یهوه‌، پدر ما و ولّی‌ ما هستی‌ و نام‌ تو از ازل‌ می‌باشد.",
			"17": "<span>17 </span> &nbsp;پس‌ ای‌ خداوند ما را از طریق‌های‌ خود چرا گمراه‌ ساختی‌ و دلهای‌ ما را سخت‌ گردانیدی‌ تا از تو نترسیم‌. به‌ خاطر بندگانت‌ و اسباط‌ میراث‌ خود رجعت‌ نما.",
			"18": "<span>18 </span> &nbsp;قوم‌ مقدّس‌ تو اندك‌ زمانی‌ آن‌ را متصرّف‌ بودند و دشمنان‌ ما مكان‌ قدس‌ تو را پایمال‌ نمودند.",
			"19": "<span>19 </span> &nbsp;و ما مثل‌ كسانی‌ كه‌ تو هرگز بر ایشان‌ حكمرانی‌ نكرده‌ باشی‌ و به‌ نام‌ تو نامیده‌ نشده‌ باشند گردیده‌ایم‌."
		},
		"64": {
			"1": "<span>1 </span> &nbsp;كاش‌ كه‌ آسمانها را مُنْشَقّْ ساخته‌، نازل‌می‌شدی‌ و كوهها از رؤیت‌ تو متزلزل‌ می‌گشت‌.",
			"2": "<span>2 </span> &nbsp;مثل‌ آتشی‌ كه‌ خورده‌ چوبها را مشتعل‌ سازد و ناری‌ كه‌ آب‌ را به‌ جوش‌ آورد تا نام‌ خود را بر دشمنانت‌ معروف‌ سازی‌ و امّت‌ها از رؤیت‌ تو لرزان‌ گردند.",
			"3": "<span>3 </span> &nbsp;حینی‌ كه‌ كارهای‌ هولناك‌ را كه‌ منتظر آنها نبودیم‌ بجا آوردی‌، آنگاه‌ نزول‌ فرمودی‌ و كوهها از رؤیت‌ تو متزلزل‌ گردید.",
			"4": "<span>4 </span> &nbsp;زیرا كه‌ از ایام‌ قدیم‌ نشنیدند و استماع‌ ننمودند و چشم‌ خدایی‌ را غیر از تو كه‌ برای‌ منتظران‌ خویش‌ بپردازد ندید.",
			"5": "<span>5 </span> &nbsp;تو آنانی‌ را كه‌ شادمانند و عدالت‌ را بجا می‌آورند و به‌ راههای‌ تو تو را به‌ یاد می‌آورند ملاقات‌ می‌كنی‌. اینك‌ تو غضبناك‌ شدی‌ و ما گناه‌ كرده‌ایم‌ در اینها مدّت‌ مدیدی‌ بسر بردیم‌ و آیا نجات‌ توانیم‌ یافت‌؟",
			"6": "<span>6 </span> &nbsp;زیرا كه‌ جمیع‌ ما مثل‌ شخص‌ نجس‌ شده‌ایم‌ و همه‌ اعمال‌ عادله‌ ما مانند لتّه‌ ملوّث‌ می‌باشد. و همگی‌ ما مثل‌ برگ‌، پژمرده‌ شده‌، گناهان‌ ما مثل‌ باد، ما را می‌رباید.",
			"7": "<span>7 </span> &nbsp;و كسی‌ نیست‌ كه‌ اسم‌ تو را بخواند یا خویشتن‌ را برانگیزاند تا به‌ تو متمسّك‌ شود زیرا كه‌ روی‌ خود را از ما پوشیده‌ای‌ و ما را به‌ سبب‌ گناهان‌ ما گداخته‌ای‌.",
			"8": "<span>8 </span> &nbsp;امّا الا´ن‌ ای‌ خداوند ، تو پدر ما هستی‌. ما گِلْ هستیم‌ و تو صانع‌ ما هستی‌ و جمیع‌ ما مصنوع‌ دستهای‌ تو می‌باشیم‌.",
			"9": "<span>9 </span> &nbsp;ای‌ خداوند بشدّت‌ غضبناك‌ مباش‌ و گناه‌ را تا به‌ ابد بخاطر مدار. هان‌ ملاحظه‌ نما كه‌ همگی‌ ما قوم‌ تو هستیم‌.",
			"10": "<span>10 </span> &nbsp;شهرهای‌ مقدّس‌ تو بیابان‌ شده‌. صهیون‌، بیابان‌ و اورشلیم‌، ویرانه‌ گردیده‌ است‌.",
			"11": "<span>11 </span> &nbsp;خانه‌ مقدّس‌ و زیبای‌ ما كه‌ پدران‌ ما تو را در آن‌ تسبیح‌می‌خواندند به‌ آتش‌ سوخته‌ شده‌ و تمامی نفایس‌ ما به‌ خرابی‌ مبدّل‌ گردیده‌ است‌.",
			"12": "<span>12 </span> &nbsp;ای‌ خداوند آیا با وجود این‌ همه‌، خودداری‌ می‌كنی‌ و خاموش‌ شده‌، ما را بشدّت‌ رنجور می‌سازی‌؟"
		},
		"65": {
			"1": "<span>1 </span> &nbsp;آنانی‌ كه‌ مرا طلب‌ ننمودند مرا جستند و آنانی‌ كه‌ مرا نطلبیدند مرا یافتند. و به‌ قومی‌ كه‌ به‌ اسم‌ من‌ نامیده‌ نشدند گفتم‌ لبّیك‌ لبّیك‌.",
			"2": "<span>2 </span> &nbsp;تمامی‌ روز دستهای‌ خود را بسوی‌ قوم‌ متمرّدی‌ كه‌ موافق‌ خیالات‌ خود به‌ راه‌ ناپسندیده‌ سلوك‌ می‌نمودند دراز كردم‌.",
			"3": "<span>3 </span> &nbsp;قومی‌ كه‌ پیش‌ رویم‌ غضب‌ مرا همیشه‌ بهیجان‌ می‌آورند، كه‌ در باغات‌ قربانی‌ می‌گذرانند و بر آجرها بخور می‌سوزانند.",
			"4": "<span>4 </span> &nbsp;كه‌ در قبرها ساكن‌ شده‌، در مغاره‌ها منزل‌ دارند، كه‌ گوشت‌ خنزیر می‌خورند و خورش‌ نجاسات‌ در ظروف‌ ایشان‌ است‌.",
			"5": "<span>5 </span> &nbsp;كه‌ می‌گویند: در جای‌ خود بایست‌ و نزدیك‌ من‌ میا زیرا كه‌ من‌ از تو مقدّس‌تر هستم‌. اینان‌ دود در بینی‌ من‌ می‌باشند و آتشی‌ كه‌ تمامی‌ روز مشتعل‌ است‌.",
			"6": "<span>6 </span> &nbsp;همانا این‌ پیش‌ من‌ مكتوب‌ است‌. پس‌ ساكت‌ نخواهم‌ شد بلكه‌ پاداش‌ خواهم‌ داد و به‌ آغوش‌ ایشان‌ مكافات‌ خواهم‌ رسانید.",
			"7": "<span>7 </span> &nbsp;خداوند می‌گوید درباره‌ گناهان‌ شما و گناهان‌ پدران‌ شما با هم‌ كه‌ بر كوهها بخور سوزانیدید و مرا بر تلّها اهانت‌ نمودید پس‌ جزای‌ اعمال‌ شما را اوّل‌ به‌ آغوش‌ شما خواهم‌ رسانید.",
			"8": "<span>8 </span> &nbsp;خداوند چنین‌ می‌گوید: چنانكه‌ شیره‌ در خوشه‌ یافت‌ می‌شود و می‌گویند آن‌ را فاسد مساز زیرا كه‌ بركت‌ در آن‌ است‌، همچنان‌ به‌ خاطربندگان‌ خود عمل‌ خواهم‌ نمود تا (ایشان‌ را) بالكّل‌ هلاك‌ نسازم‌.",
			"9": "<span>9 </span> &nbsp;بلكه‌ نسلی‌ از یعقوب‌ و وارثی‌ برای‌ كوههای‌ خویش‌ از یهودا به‌ ظهور خواهم‌ آورد. و برگزیدگانم‌ ورثه‌ آن‌ و بندگانم‌ ساكن‌ آن‌ خواهند شد.",
			"10": "<span>10 </span> &nbsp;و شارون‌، مرتع‌ گله‌ها و وادی‌ عاكور، خوابگاه‌ رمه‌ها به‌ جهت‌ قوم‌ من‌ كه‌ مرا طلبیده‌اند، خواهد شد.",
			"11": "<span>11 </span> &nbsp;و امّا شما كه‌ خداوند را ترك‌ كرده‌ و كوه‌ مقدّس‌ مرا فراموش‌ نموده‌اید، و مائده‌ای‌ به‌ جهت‌ پخت‌ مهیا ساخته‌ و شراب‌ ممزوج‌ به‌ جهت‌ اتّفاق‌ ریخته‌اید،",
			"12": "<span>12 </span> &nbsp;پس‌ شما را به‌ جهت‌ شمشیر مقدّر ساختم‌ و جمیع‌ شما برای‌ قتل‌ خم‌ خواهید شد زیرا كه‌ چون‌ خواندم‌ جواب‌ ندادید و چون‌ سخن‌ گفتم‌ نشنیدید و آنچه‌ را كه‌ در نظر من‌ ناپسند بود بعمل‌ آوردید و آنچه‌ را كه‌ نخواستم‌ برگزیدید.",
			"13": "<span>13 </span> &nbsp;بنابراین‌ خداوند یهوه‌ می‌گوید: هان‌ بندگان‌ من‌ خواهند خورد امّا شما گرسنه‌ خواهید بود اینك‌ بندگانم‌ خواهند نوشید امّا شما تشنه‌ خواهید بود. همانا بندگانم‌ شادی‌ خواهند كرد امّا شما خجل‌ خواهید گردید.",
			"14": "<span>14 </span> &nbsp;اینك‌ بندگانم‌ از خوشی‌ دل‌، ترنّم‌ خواهند نمود، امّا شما از كدورت‌ دل‌، فریاد خواهید نمود و از شكستگی‌ روح‌، ولوله‌ خواهید كرد.",
			"15": "<span>15 </span> &nbsp;و نام‌ خود را برای‌ برگزیدگان‌ من‌ به‌ جای‌ لعنت‌، ترك‌ خواهید نمود پس‌ خداوند یهوه‌ تو را بقتل‌ خواهد رسانید و بندگان‌ خویش‌ را به‌ اسم‌ دیگر خواهد نامید.",
			"16": "<span>16 </span> &nbsp;پس‌ هركه‌ خویشتن‌ را بروی‌ زمین‌ بركت‌ دهد، خویشتن‌ را به‌ خدای‌ حقّ بركت‌ خواهد داد؛ و هركه‌ بروی‌ زمین‌ قسم‌ خورد به‌ خدای‌ حقّ قسم‌ خواهد خورد. زیرا كه‌ تنگیهای‌ اوّلین‌ فراموش‌ شده‌ و از نظر من‌ پنهان‌گردیده‌ است‌.",
			"17": "<span>17 </span> &nbsp;زیرا اینك‌ من‌ آسمانی‌ جدید و زمینی‌ جدید خواهم‌ آفرید و چیزهای‌ پیشین‌ بیاد نخواهد آمد و بخاطر نخواهد گذشت‌.",
			"18": "<span>18 </span> &nbsp;بلكه‌ از آنچه‌ من‌ خواهم‌ آفرید، شادی‌ كنید و تا به‌ ابد وجد نمایید زیرا اینك‌ اورشلیم‌ را محلّ وجد و قوم‌ او را محّل‌ شادمانی‌ خواهم‌ آفرید.",
			"19": "<span>19 </span> &nbsp;و از اورشلیم‌ وجد خواهم‌ نمود و از قوم‌ خود شادی‌ خواهم‌ كرد و آواز گریه‌ و آواز ناله‌ بار دیگر در او شنیده‌ نخواهد شد.",
			"20": "<span>20 </span> &nbsp;و بار دیگر طفلِ كم‌ روز از آنجا نخواهد بود و نه‌ مرد پیر كه‌ عمر خود را به‌ اتمام‌ نرسانیده‌ باشد؛ زیرا كه‌ طفل‌ در سنّ صد سالگی‌ خواهد مرد لیكن‌ گناهكار صد ساله‌ ملعون‌ خواهد بود.",
			"21": "<span>21 </span> &nbsp;و خانه‌ها بنا كرده‌، در آنها ساكن‌ خواهند شد و تاكستانها غرس‌ نموده‌، میوه‌ آنها را خواهند خورد.",
			"22": "<span>22 </span> &nbsp;بنا نخواهند كرد تا دیگران‌ سكونت‌ نمایند و آنچه‌ را كه‌ غرس‌ می‌نمایند دیگران‌ نخواهند خورد. زیرا كه‌ ایام‌ قوم‌ من‌ مثل‌ ایام‌ درخت‌ خواهد بود و برگزیدگان‌ من‌ از عمل‌ دستهای‌ خود تمتّع‌ خواهند برد.",
			"23": "<span>23 </span> &nbsp;زحمت‌ بیجا نخواهند كشید و اولاد به‌ جهت‌ اضطراب‌ نخواهند زایید زیرا كه‌ اولاد بركت‌ یافتگان‌ خداوند هستند و ذریت‌ ایشان‌ با ایشانند.",
			"24": "<span>24 </span> &nbsp;و قبل‌ از آنكه‌ بخوانند من‌ جواب‌ خواهم‌ داد، و پیش‌ از آنكه‌ سخن‌ گویند من‌ خواهم‌ شنید.",
			"25": "<span>25 </span> &nbsp;گرگ‌ و بره‌ با هم‌ خواهند چرید و شیـر مثـل‌ گاو كاه‌ خواهد خورد و خوراك‌ مار خاك‌ خواهدبود. خداوند می‌گویـد كه‌ در تمامـی‌ كـوه‌ مقدّس‌ مـن‌، ضـرر نخواهنـد رسانیـد و فسـاد نخواهنـد نمـود."
		},
		"66": {
			"1": "<span>1 </span> &nbsp;خداوند چنین‌ می‌گوید: آسمانها كرسی‌ من‌ و زمین‌ پای‌ انداز من‌ است‌، پس‌ خانه‌ای‌ كه‌ برای‌ من‌ بنا می‌كنید كجا است‌؟ و مكان‌ آرام‌ من‌ كجا؟",
			"2": "<span>2 </span> &nbsp;خداوند می‌گوید: دست‌ من‌ همه‌ این‌ چیزها را ساخت‌ پس‌ جمیع‌ اینها بوجود آمد؛ امّا به‌ این‌ شخص‌ كه‌ مسكین‌ و شكسته‌ دل‌ و از كلام‌ من‌ لرزان‌ باشد، نظر خواهم‌ كرد.",
			"3": "<span>3 </span> &nbsp;كسی‌ كه‌ گاوی‌ ذبح‌ نماید مثل‌ قاتل‌ انسان‌ است‌ و كسی‌ كه‌ گوسفندی‌ ذبح‌ كند مثل‌ شخصی‌ است‌ كه‌ گردن‌ سگ‌ را بشكند. و آنكه‌ هدیه‌ای‌ بگذراند مثل‌ كسی‌ است‌ كه‌ خون‌ خنزیری‌ را بریزد و آنكه‌ بخور سوزاند مثل‌ شخصی‌ است‌ كه‌ بتی‌ را تبریك‌ نماید و ایشان‌ راههای‌ خود را اختیار كرده‌اند و جان‌ ایشان‌ از رجاسات‌ خودشان‌ مسرور است‌.",
			"4": "<span>4 </span> &nbsp;پس‌ من‌ نیز مصیبت‌های‌ ایشان‌ را اختیار خواهم‌ كرد و ترسهای‌ ایشان‌ را بر ایشان‌ عارض‌ خواهم‌ گردانید، زیرا چون‌ خواندم‌ كسی‌ جواب‌ نداد و چون‌ تكلّم‌ نمودم‌ ایشان‌ نشنیدند بلكه‌ آنچه‌ را كه‌ در نظر من‌ ناپسند بود بعمل‌ آوردند و آنچه‌ را كه‌ نخواستم‌ اختیار كردند.",
			"5": "<span>5 </span> &nbsp;ای‌ آنانی‌ كه‌ از كلام‌ خداوند می‌لرزید سخن‌ او را بشنوید. برادران‌ شما كه‌ از شما نفرت‌ دارند و شما را بخاطر اسم‌ من‌ از خود می‌رانند می‌گویند: خداوند تمجید كرده‌ شود تا شادی‌شما را ببینم‌، لیكن‌ ایشان‌ خجل‌ خواهند شد.",
			"6": "<span>6 </span> &nbsp;آواز غوغا از شهر، صدایی‌ از هیكل‌، آواز خداوند است‌ كه‌ به‌ دشمنان‌ خود مكافات‌ می‌رسانـد.",
			"7": "<span>7 </span> &nbsp;قبـل‌ از آنكه‌ درد زه‌ بكشـد، زاییـد. پیـش‌ از آنكه‌ درد او را فـرو گیـرد اولاد نرینـه‌ای‌ آورد.",
			"8": "<span>8 </span> &nbsp;كیست‌ كه‌ مثل‌ این‌ را شنیده‌ و كیست‌ كه‌ مثـل‌ ایـن‌ را دیـده‌ باشـد؟ آیـا ولایتـی‌ در یك‌ روز مولـود گـردد و قومـی‌ یكدفعـه‌ زاییـده‌ شود؟ زیرا صهیون‌ به‌ مجـرّد درد زه‌ كشیـدن‌ پسـران‌ خـود را زاییـد.",
			"9": "<span>9 </span> &nbsp;خداوند می‌گـویـد: آیـا مـن‌ بفــم‌ رحم‌ برسانم‌ و نزایانم‌؟ و خدای‌ تو می‌گویـد: «آیا من‌ كه‌ زایاننده‌ هستم‌، رحم‌ را ببندم‌؟",
			"10": "<span>10 </span> &nbsp;ای‌ همه‌ آنانی‌ كه‌ اورشلیم‌ را دوست‌ می‌دارید، با او شادی‌ كنید و برایش‌ وجد نمایید. و ای‌ همه‌ آنانی‌ كه‌ برای‌ او ماتم‌ می‌گیرید، با او شادی‌ بسیار نمایید.",
			"11": "<span>11 </span> &nbsp;تا از پستانهای‌ تسلیات‌ او بمكید و سیر شوید و بدوشید و از فراوانی‌ جلال‌ او محظوظ‌ گردید.",
			"12": "<span>12 </span> &nbsp;زیرا خداوند چنین‌ می‌گوید: اینك‌ من‌ سلامتی‌ را مثل‌ نهر و جلال‌ امّت‌ها را مانند نهر سرشار به‌ او خواهم‌ رسانید. و شما خواهید مكید و در آغوش‌ او برداشته‌ شده‌، بر زانوهایش‌ بناز پرورده‌ خواهید شد.",
			"13": "<span>13 </span> &nbsp;و مثل‌ كسی‌ كه‌ مادرش‌ او را تسلّی‌ دهد، همچنین‌ من‌ شما را تسلّی‌ خواهم‌ داد و در اورشلیم‌ تسلّی‌ خواهید یافت‌.",
			"14": "<span>14 </span> &nbsp;پس‌ چون‌ این‌ را بینید دل‌ شما شادمان‌ خواهد شد و استخوانهای‌ شما مثل‌ گیاه‌ سبز و خرّم‌ خواهد گردید و دست‌ خداوند بر بندگانش‌ معروف‌ خواهد شد امّا بر دشمنان‌ خود غضب‌ خواهد نمود.",
			"15": "<span>15 </span> &nbsp;زیرا اینك‌ خداوند باآتش‌ خواهـد آمـد و ارابه‌هـای‌ او مثـل‌ گردبـاد تا غضب‌ خود را با حدّت‌ و عتاب‌ خویش‌ را با شعلـه‌ آتش‌ به‌ انجام‌ رساند.",
			"16": "<span>16 </span> &nbsp;زیرا خداوند با آتش‌ و شمشیر خود بر تمامی‌ بشر داوری‌ خواهد نمود و مقتولان‌ خداوند بسیار خواهند بود.",
			"17": "<span>17 </span> &nbsp;و خداوند می‌گویـد: آنانی‌ كه‌ از عقب‌ یكنفر كه‌ در وسط‌ باشد خویشتن‌ را در باغات‌ تقدیس‌ و تطهیر می‌نمایند و گوشت‌ خِنزیر و رجاسات‌ و گوشت‌ موش‌ می‌خورند با هم‌ تلف‌ خواهند شد.",
			"18": "<span>18 </span> &nbsp;و من‌ اعمال‌ و خیالات‌ ایشان‌ را جزا خواهم‌ داد و آمده‌، جمیع‌ امّت‌ها و زبانها را جمع‌ خواهم‌ كرد و ایشان‌ آمده‌، جلال‌ مرا خواهند دید.",
			"19": "<span>19 </span> &nbsp;و آیتی‌ در میان‌ ایشان‌ برپا خواهم‌ داشت‌ و آنانی‌ را كه‌ از ایشان‌ نجات‌ یابند نزد امّت‌ها به‌ ترشیش‌ و فُول‌ و تیراندازانِ لُود و توبال‌ و یونان‌ و جزایر بعیده‌ كه‌ آوازه‌ مرا نشنیده‌اند و جلال‌ مرا ندیده‌اند خواهم‌ فرستاد تا جلال‌ مرا در میان‌ امّت‌ها شایع‌ سازند.",
			"20": "<span>20 </span> &nbsp;و خداوند می‌گوید كه‌ ایشان‌ جمیع‌ برادران‌ شما را از تمامی‌ اُمّت‌ها بر اسبان‌ و ارابه‌ها و تخت‌ روانها و قاطران‌ و شتران‌ به‌ كوه‌ مقدّس‌ من‌ اورشلیم‌ به‌ جهت‌ خداوند هدیه‌ خواهند آورد. چنانكه‌ بنی‌اسرائیل‌ هدیه‌ خود را در ظرف‌ پاك‌ به‌ خانه‌ خداوند می‌آورند.",
			"21": "<span>21 </span> &nbsp;و خداوند می‌گوید كه‌ از ایشان‌ نیز كاهنان‌ و لاویان‌ خواهند گرفت‌.",
			"22": "<span>22 </span> &nbsp;زیرا خداوند می‌گوید: چنانكه‌ آسمانهای‌ جدید و زمین‌ جدیدی‌ كه‌ من‌ آنها را خواهم‌ ساخت‌ در حضور من‌ پایدار خواهد ماند، همچنان‌ ذریت‌ شما و اسم‌ شما پایدار خواهد ماند.",
			"23": "<span>23 </span> &nbsp;و خداوند می‌گویـد كه‌ از غُرّه‌ مـاه‌ تا غُـرّه‌ دیگر و از سَبَّت‌ تا سَبَّت‌ دیگر تمامی‌ بشر خواهند آمد تا به‌ حضور من‌ سجده‌ نمایند.",
			"24": "<span>24 </span> &nbsp;و ایشان‌ بیرون‌ رفته‌، لاشهای‌ مردمانی‌ را كه‌ بر من‌عاصی‌ شده‌اند ملاحظه‌ خواهند كرد زیرا كِرْمِ ایشان‌ نخواهد مُرد و آتش‌ ایشان‌ خاموش‌ نخواهد شد و ایشان‌ نزد تمامی‌ بشر مكروه‌ خواهند بود."
		}
	}
};
module.exports = book;