var book = {
	"name": "1 Veigauna",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<span>1</span> Veigauna 1",
			"2": "<span>2</span> O Atama, o Seci, o Inosi,",
			"3": "<span>3</span> O Keinani, o Mealaleli, o Jareti,",
			"4": "<span>4</span> O Inoke, o Mecusela, o Lemeki,",
			"5": "<span>5</span> O Noa, o Semi, o Ame, o Jefeca.",
			"6": "<span>6</span> O iratou na luve i Jefeca tagane, o Komera, o Mekoki, o Matai, o Javana, o Tubali, o Meseki kei Tirasa.",
			"7": "<span>7</span> O iratou na luve i Komera tagane o Asekinasa, Difaca kei Tokama.",
			"8": "<span>8</span> O iratou na luve i Javana tagane, oilaisa, Tarasisa, Kitimi kei Rodanimi.",
			"9": "<span>9</span> O iratou na luve i Ame tagane, o Kusi, Misereimi, Puti kei Kenani.",
			"10": "<span>10</span> O iratou na luve i Kusi tagane, o Seba, Avila, Sabeta, Reama kei Sabiteka. Oi rau na luve i Reama tagane, o Seba kei Dedani.",
			"11": "<span>11</span> Sa tamai Nimirote o Kusi; sa tekivu me dua na tamata qaqa o koya e vuravura.",
			"12": "<span>12</span> O Misireimi na tamadratou o Lutimi, Anamimi, Lea bimi, Navituimi,",
			"13": "<span>13</span> Pacirusimi, Kasiluimi, (o koya sa vu mai kina o ira na kai Filisitia kei ira na Kapatorimi).",
			"14": "<span>14</span> O Kenani na tamai Satoni, na nona ulumatua, kei Eci;",
			"15": "<span>15</span> na Jebusaiti, na Amoraiti, kei na Qaqasaiti;",
			"16": "<span>16</span> na Aivaiti, Akaiti, kei na Sinaiti;",
			"17": "<span>17</span> Arivadaiti, na Semaraiti, kei na Amataiti.",
			"18": "<span>18</span> O iratou na luve i Semi, o Ilami, Asuri, Arivasade, Luti, Erami, Usi, Uli, Keceri, kei Meseki.",
			"19": "<span>19</span> Sa tamai Sela o Arivasade, ka sa tamai Iperi o Sela.",
			"20": "<span>20</span> Sa sucu e rua na luve i Iperi tagane: e dua e yacana o Peleki, baleta ni sa wase rua o vuravura e na nona gauna; ka sa yacai tacina o Joketani.",
			"21": "<span>21</span> O Joketani sa tamadratou o Alimotati, Selefi, Esamaveci, Jera,",
			"22": "<span>22</span> Edorami, Usali, Dikila,",
			"23": "<span>23</span> Ipali, Apimaeli, Seba,",
			"24": "<span>24</span> Opira, Avila, kei Jobaba. O iratou kece oqori na luve i Joketani.",
			"25": "<span>25</span> O semi, Arivasade, Sela,",
			"26": "<span>26</span> Iperi, Peleki, Riu,",
			"27": "<span>27</span> Seruki, Neori, Tera,",
			"28": "<span>28</span> kei Eparama, o ya o Eparaama.",
			"29": "<span>29</span> Na luve i Eparaama o Aisake kei Isimeli.",
			"30": "<span>30</span> Oqo na kedra i vola ni kawa: Na ulumatua i Isimeli o Nebajoci; kei Kitari, Adibieli, Mibi sami,",
			"31": "<span>31</span> Misima, Duma, Masa, Etati, Tema,",
			"32": "<span>32</span> Jeturi, Nafisi, kei Ketema. Oqori o iratou na luve i Isimeli.",
			"33": "<span>33</span> O iratou oqo na tagane ka vakasucuma o Ketura, o koya ka qai wati Eparaama e muri, o Simirani, Jokesani, Medani, Mitiani, Isibaki, kei Sua. Na luve i Jokesai tagane o Seba kei Dedani.",
			"34": "<span>34</span> O iratou na luve i Mitiani tagane, o Efa, Eferi, Enoke, Apaita, kei Elida. O ira kece oqori na luve i Ketura.",
			"35": "<span>35</span> Sa tamai Aisake o Eparaama. Na luve i Aisake tagane o Iso kei Isireli.",
			"36": "<span>36</span> O iratou na luve i Iso tagane, o Ilivasi, Riueli, Jeusi, Jealami kei Kora.",
			"37": "<span>37</span> Kei iratou na luve i Ilivasi tagane o Temani, Omari, Sefi, Qatami, kei Kenasi; kei Ameleki, o koya ka vakasucuma o Timina.",
			"38": "<span>38</span> O iratou na luve i Riueli tagane, o Neaca, Sira, Sama, kei Misa.",
			"39": "<span>39</span> O iratou na luve i Seira tagane, o Lotani, Sobali, Sipione, Ena, Disoni, Esa, kei Disani.",
			"40": "<span>40</span> Ia oi rau na luve i Lotani tagane, o Ori kei Omami; Na ganei Lotani o Timina.",
			"41": "<span>41</span> O iratou na luve i Sobali tagane, o Eliani, Maneaca, Ibali, Sefi, kei o Nami. Na luve i Sipione tagane o Eja kei Ena.",
			"42": "<span>42</span> Na luve i Ena tagane o Disoni. O iratou na luve i Disoni tagane o Emirani, Esabani, Icirani, kei Jerani.",
			"43": "<span>43</span> Na luve i Esa era tagane o Bilani, Sevani, kei Jekani. Oi rau na luve i Disani o Usi, kei Arani. O Ira Na Tui Kei Itomi",
			"44": "<span>44</span> Sai ira oqo na tui era a lewa tu mai na vanua o Itomi ni se bera ni lewa e dua na tui vei ra na luve i Isireli: Bela na luve i Beori, kei na yaca ni nona koro turaga o Diainaba.",
			"45": "<span>45</span> Ia ni sa mate o Bela, sa tui me kenai sosomi o Jobaba na luve i Bosira.",
			"46": "<span>46</span> Ia sa mate o Jobaba, o Usami mai na nodra vanua na Temanaiti sa tui me kenai sosomi.",
			"47": "<span>47</span> Ia sa mate o Usami, o Etati na luve i Betati, o koya ka ravuti Mitiani e na vanua nei Moape, sa lewa me kenai sosomi. Na yaca ni nona koro turaga o Evici.",
			"48": "<span>48</span> Ni sa mate o Etati, sa lewa o Semila na kai Masereka me kenai sosomi.",
			"49": "<span>49</span> Ia ni sa mate o Semila, o Saula na kai Riopoci e na bati ni uciwai sa lewa me kenai sosomi.",
			"50": "<span>50</span> Ni sa mate o Saula, o Peali-Anani na luve i Akipora sa lewa me kenai sosomi.",
			"51": "<span>51</span> Ia ni sa mate o Peali-Anani, sa tui me kenai sosomi o Etati; ka sa yaca ni nona koro turaga o Pai; ia na yaca i watina o Meatapeli na luve i Matereti yalewa, na luve i Meseaba yalewa.",
			"52": "<span>52</span> Sa mate talega o Etati. Ia o ira na turaga e Itomi o Timina, Eia, Jececi,",
			"53": "<span>53</span> O Aolibama, Ela, Pinoni,",
			"54": "<span>54</span> Kenasi, Temani, Mibisari,",
			"55": "<span>55</span> Makitieli, Irami. O ira oqori na turaga mai Itomi."
		},
		"2": {
			"1": "<span>1</span> Veigauna 2",
			"2": "<span>2</span> Sai ira oqo na luve i Isireli tagane, o Ruveni, Simione, Livai, Juta, Isakari, Sevuloni,",
			"3": "<span>3</span> Tani, Joseva, Penijamini, Navitalai, Kata kei Aseri.",
			"4": "<span>4</span> O iratou na luve i Juta tagane o Eri, Onani, kei Sela. O iratou oqo ka vakasucuma vua o koya na yalewa ka luve i Sua na kai Kenani. O Eri, na ulumatua i Juta, sa caka ca e na mata ni Kalou; Sa mani vakamatei koya na Kalou.",
			"5": "<span>5</span> Ia o Tema, na vugona, sa vakasucumi rau o Veresi kei Sira kivua. Na luve i Juta tagane taucoko e lima.",
			"6": "<span>6</span> Oi rau na luve i Veresi tagane o Esironi kei Emuli.",
			"7": "<span>7</span> O iratou na luve i Sira tagane o Simiri, Ecani, Emani, Kalikoli, kei Tara â€” e lewe lima taucoko.",
			"8": "<span>8</span> Na luve i Karimi tagane o Ekari, o koya sa dau-vakavuca e Isireli, o koya sa caka cala e na ka vakatabui.",
			"9": "<span>9</span> Na luve i Ecani tagane o Asaraia.",
			"10": "<span>10</span> O iratou talega na luve i Esironi tagane o Jeramieli, Rami, kei kelubai.",
			"11": "<span>11</span> Sa tamai Amenatave o Rami, ia o Amenatave na tamai Nasoni, na nodra i liuliu na luve i Juta;",
			"12": "<span>12</span> O Nasoni na tamai Salima, ia o Salima na tamai Poasa;",
			"13": "<span>13</span> Sa tamai Opeti o Poasa, ka tamai Jese o Opeti;",
			"14": "<span>14</span> O Jese na tamai Iliavi na nona ulumatua, Apinatave na kena i karua, Simea na kenai katolu,",
			"15": "<span>15</span> Necaneli na kenai kava, Radai na kenai kalima,",
			"16": "<span>16</span> O Semi na kenai kaono, kei Tevita na kenai kavitu.",
			"17": "<span>17</span> Ia na ganedratou o Seruwaia kei Avikali. Kei na luvei Seruwaia tagane o Avisai, Joave, kei Asaeli â€” e tolu.",
			"18": "<span>18</span> O Avikali e vakasucumi Amasa; kei na tamai Amasa o Jeca na kawa i Isimeli.",
			"19": "<span>19</span> O Kelevi na luve i Esironi e vakaluveni vei Asuba na watina, kei Jerioci. Ia oqo o iratou na luvena: Jeseri, Sobaba, kei Aridoni.",
			"20": "<span>20</span> Ni sa mate o Asuba, Sa kauti Efiraca o Kelevi me watina, o koya ka vakasucumi Ura.",
			"21": "<span>21</span> Ia o Ura sa tamai Uri, o Uri sa tamai Besaleli.",
			"22": "<span>22</span> Ni sa oti oqori sa qai curu o Esironi kivua na yalewa, na luve i Makiri, na tamai Kiliati, o koya sa vakawatitaka o koya ni sa yabaki onosagavulu; ka sa vakasucumi Sekubu na luvedrau.",
			"23": "<span>23</span> O Sekupi sa tamai Jairi, o koya sa ruasagavulu katolu na nona koro lelevu e na vanua i Kiliati.",
			"24": "<span>24</span> (E rau sa kovea o Kesura kei Siria mai vei ira na veikoro vaka-Jairi, kei Kenaca kei na kena veikoroâ€”e onosagavulu taucoko na koro.) Na veikoro kece oqori e nodra na luve i Makiri na tamai Kiliati.",
			"25": "<span>25</span> Ni sa mate o Esironi mai Kelevi-Efiraca, o Apaija na wati Esironi sa vakasucumi Asuri na luvedrau, o koya na tamai Tekoa.",
			"26": "<span>26</span> O iratou na luve i Jeramieli tagane, na ulumatua i Esironi, o Rami na ulumatua, kei Buna, Oreni, Osemi, kei Eaija.",
			"27": "<span>27</span> E dua tale na wati Jeramieli, na yacana o Atara; o koya na tinai Onami.",
			"28": "<span>28</span> O iratou na luve i Rami tagane, na ulumatua i Jeramieli, o Measi, Jamini kei Ekari.",
			"29": "<span>29</span> Oi rau na luve i Onami tagane o Samai kei Jada. Oi rau na luve i Samai tagane o Netava kei Avisuri.",
			"30": "<span>30</span> Ia na yaca i na wati Avisuri o Aviyali, ka vakasucuma na luvedrau o Abani kei Molita.",
			"31": "<span>31</span> Oi rau na luve i Netava tagane o Seleti kei Apaimi; Sa mate o Seleti ka sega na luvena.",
			"32": "<span>32</span> Na luve i Apaimi tagane o Isi, na luve i Isi tagane o Sesani, kei na luve i Sesani tagane o Alai.",
			"33": "<span>33</span> Oi rau na luve i Jada tagane, na taci Samai, o Jeca kei Jonacani; Sa mate o Jeca ka sega na luvena.",
			"34": "<span>34</span> Na luve i Jonacani tagane o Peleci kei Sasa. Oqori o iratou na luve i Jeramieli.",
			"35": "<span>35</span> Ia oqo o Sesani e sega na luvena tagane, na luvena yalewa ga. Ia sa dua na tamata cakacaka i Sesani e kai Ijipita na yacana o Jaria.",
			"36": "<span>36</span> Sa solia e dua na luvena yalewa o Sesani vei Jaria na nona tamata me watina, ka sa vakasucumi Atai na luvedrau.",
			"37": "<span>37</span> O Atai na tamai Necani, ia o Necani na tamai Savati;",
			"38": "<span>38</span> O Savati na tamai Evilali, kei Evilali na tamai Opeti;",
			"39": "<span>39</span> O Opeti na tamai Jeu, ia o Jeu na tamai Asaraia;",
			"40": "<span>40</span> O Asaraia na tamai Elesi, ia o Elesi na tamai Iliesa;",
			"41": "<span>41</span> O Iliesa na tamai Sisimai, ia o Sisimai na tamai Salumi;",
			"42": "<span>42</span> O Salumi na tamai Jekamaia, ia o Jekamaia na tamai Elisama.",
			"43": "<span>43</span> O ira oqo na kawa i Kelevi na taci Jeramieli, o Mesa na nona ulumatua, o koya na tama i Sifa, kei ira na luvei Maresa na tama i Epironi.",
			"44": "<span>44</span> O ira na luve i Epironi tagane o Kora, Tapua, Rekemi, kei Sema.",
			"45": "<span>45</span> O Sema na tamai Reama na tamai Jorokoama, ia o Rekemi na tamai Samai.",
			"46": "<span>46</span> Ia na luvei Samai tagane o Maoni, ia o Maoni na tamai Beci-suri.",
			"47": "<span>47</span> O Iva, na watina lailai o Kelevi sa vakasucumi Erani, Mosa, kei Kasese; ia o Erani na tamai Kasese.",
			"48": "<span>48</span> Ia o iratou na luve i Jadai o Rekemi, Jocame, Kesani, Peleti, Efa, kei Seafa.",
			"49": "<span>49</span> O Meaka, na watina lailai o Kelevi, e vakasucumi Sipera kei Tirana.",
			"50": "<span>50</span> E vakasucumi Seafa talega na tamai Madamana, Seva na tamai Makibena kei Kipea. Ia na luve i Kelevi yalewa o Akesa.",
			"51": "<span>51</span> Oqo o ira na kawa i Kelevi: O ira na luve i Ura, na ulumatua i Efiraca, o Sobali na tamai Kirijaci Jearimi,",
			"52": "<span>52</span> Salima na tamai Peceliema, kei Arefi na tamai Beci-Kaderi.",
			"53": "<span>53</span> Ia o Sobali na tamai Kirijaci-Jearimi era tu na nona kawa: Aroe, kei na veimama ni vuvale nei Manuoci.",
			"54": "<span>54</span> Na vuvale i Kiriaci Jearimi sai ira na Iceraiti,na Putaiti, na Sumataiti, kei na Misiraiti. Sa tubu mai vei ira oqori o ira na Soraiti.",
			"55": "<span>55</span> O iratou na luve i Salima tagane o Peceliema, Nitofataiti, Ataroci mai na vuvale nei Joave, e dua nai wase ni Manaetaiti, kei na Soraiti.",
			"56": "<span>56</span> Kei na nodra vuvale na Dau-ni-Vola Tabu era tiko mai Jepesi o ira na Tirataiti, na Simeataiti, kei na Sukataiti. O ira oqo na Kenaiti ka ra tubu mai vei Emaci, na tamadra na vuvale i Rekapi."
		},
		"3": {
			"1": "<span>1</span> Veigauna 3",
			"2": "<span>2</span> Ia oqo o iratou na luve i Tevita tagane e ratou a sucu mai Epironi: Na ulumatua o Eminoni, mai vei Ainoami na yalewa ni Jeserieli; nai karua o Taniela, mai vei Avikali na yalewa ni Kameli;",
			"3": "<span>3</span> Na kenai katolu, o Apisalome na luve i Meaka, na luve i Talemai yalewa, na tui Kesuri; nai kava, o Atonaija na luve i Akici;",
			"4": "<span>4</span> na kenai kalima, o Sefataia, mai vei Avitale; kaono, oicereama, mai vua na watina o Eqela.",
			"5": "<span>5</span> O iratou na lewe ono oqo e ratou a sucu mai Epironi. Sa lewa mai kea o koya me vitu na yabaki kei na vula ono, ka lewa mai Jerusalemi me tolusagavulu ka tolu na yabaki.",
			"6": "<span>6</span> O iratou oqo e ratou sucu mai Jerusalemi: O Simea, o Sobaba, Necani, kei Solomoni â€“ e va mai vei Pacisua (Paciseva) na luve i Amieli yalewa.",
			"7": "<span>7</span> O Iba talega, o Elisama, Elifeleti,",
			"8": "<span>8</span> Noqa, Nefeki, Jefia,",
			"9": "<span>9</span> Elisama, Eliada, kei Elifeleti â€“ era lewe ciwa taucoko.",
			"10": "<span>10</span> O ira kece oqo na luve i Tevita, era sega ni wili kina na luve ni watina lalai, kei Tema na ganedra. Na Vuvale I Solomoni",
			"11": "<span>11</span> Na luve i Solomoni tagane o Riopoama; o Apaijia na luve i Riopoama, o Esa na luve i Apaijia, o Jiosafati na luve i Esa,",
			"12": "<span>12</span> O Jorami na luve i Jiosafati, o Easaia na luve i Jorami, o Joasi na luve i Easaia,",
			"13": "<span>13</span> O Amasaia na luve i Joasi, o Asaraia na luve i Amasaia, o Jocame na luve i Asaraia,",
			"14": "<span>14</span> O Easi na luve i Jocame, o Esekaia na luve i Easi, o Manasa na luve i Esekaia,",
			"15": "<span>15</span> O Emoni na luve i Manasa, kei Josaia na luve i Emoni.",
			"16": "<span>16</span> O iratou na luve i Josaia o Joenani na ulumatua, nai karua o Jioiakimi, na kenai katolu o Setekaia, kei na kenai kava o Salumi.",
			"17": "<span>17</span> Na luve i Jioiakimi tagane o Jekonaia kei Setekaia.",
			"18": "<span>18</span> O iratou na luve i Jekonaia o Esira, o Selatieli na luvena tagane,",
			"19": "<span>19</span> kei Malakirama, Petaia, Senasari, Jecamaia, Osama, Nedabaia.",
			"20": "<span>20</span> Na luve i Petaia tagane o Serupepeli kei Simei. Na luve i Serupepeli tagane Mesulame, Ananaia, kei Selomaica na ganedrau,",
			"21": "<span>21</span> kei Asuba, Oeli, Berekaia, Asadaia, kei Jusabi- Eseti â€“ e lewe lima taucoko.",
			"22": "<span>22</span> Na luve i Ananaia tagane o Pelataia kei Jesaia, na luve i Refaia tagane, na luve i Arenani tagane, na luve i Opataia tagane, kei iratou na luve i Sekanaia.",
			"23": "<span>23</span> Na luve i Sekanaia tagane o Semaia. O iratou na luve i Semaia o Atusi, Ikali, Baraia, Nearaia, kei Safati â€“ era lewe ono taucoko.",
			"24": "<span>24</span> Na luve i Nearaia tagane o Elioenai, Esekaia, kei Asirikama â€“ e ratou lewe tolu taucoko.",
			"25": "<span>25</span> Na luve i Elioenai tagane o Odavaia, Eliasipi, Pelaia, Akuku, Joanani, Delaia, kei Anani â€“ e ratou lewe vitu taucoko."
		},
		"4": {
			"1": "<span>1</span> Veigauna 4",
			"2": "<span>2</span> O iratou na luve i Juta o Veresi, Esironi, Karimi, Ura, kei Sobali.",
			"3": "<span>3</span> Kei Reaia na luve i Sobali e tamai Jeaci, ka tamai Aumai kei Leati o Jeaci. Oqo na nodratou vuvale na Sorataiti.",
			"4": "<span>4</span> O iratou oqo na luve i tamai Etama; Jeserieli, Isima, Idabasi; kei na yacai ganedratou o Aseleliponi;",
			"5": "<span>5</span> Kei Penueli na tamai Kedore, kei Esa na tamai Usa. Oqo o ira na luve i Ura, na ulumatua o Efiraca na tamai Peceliema.",
			"6": "<span>6</span> Kei Asuri na tamai Tekoa e rua na watina, o Ela kei Neara.",
			"7": "<span>7</span> Sa vakasucumi Ausami, Efa, Temeni, kei Easitari. O iratou oqo na luve i Neara.",
			"8": "<span>8</span> Na luve i Ela o Sereci, Soari, kei Ecenani;",
			"9": "<span>9</span> Kei Kosa na tamai Anupi, Sobeba, kei na vuvale i Earaeli na luve i Arumi.",
			"10": "<span>10</span> Ia oqo o Jepesi sa dokai vakalevu cake mai vei iratou na tacina, ka sa vakayacani koya o tinana me o Jepesi, ka tukuna, \"Baleta ni'u a vakasucumi koya e na rarawa.\"",
			"11": "<span>11</span> Ia sa kaciva o Jepesi na Kalou nei Isireli ka vaka, \"Isa, Mo Ni vakalouvinakataki au vakaidina, ka vakarabailevutaka na noqu yasana, ia me tiko vata kei au na Ligamuni, ka Mo Ni taqomaki au mai na ca, ka me'u kakua ni vakavuna na rarawa!\" Sa mani solia na Kalou kivua na ka e kerea.",
			"12": "<span>12</span> O Kelupi na taci Sua sa tamai Meiri, o koya na tamai Esitoni.",
			"13": "<span>13</span> Ia o Esitoni na tamai Peci-Rafa, Pasea, kei Teina na tamai Irineasi. Oqo o ira na kai Reka.",
			"14": "<span>14</span> Oi rau na luve Kenasi tagane o Ocenieli kei Seraia. Na luve i Ocenieli tagane o Ecaci,",
			"15": "<span>15</span> kei Meonocei o koya na tamai Ofira. O Seraia na tamai Joape na tamai Qe-Arasimi, ni ra sa matai.",
			"16": "<span>16</span> O iratou na luve i Kelepi tagane na luve i Jefune o Iru, Ela, kei Neama. Na luvei Ela o Kenasi.",
			"17": "<span>17</span> O iratou luve i Jealeleli tagane o Sifi, Sifa, Tiria, kei Asareli.",
			"18": "<span>18</span> O iratou na luve i Esira tagane o Jeca, Mereti, Efa, kei Jaloni. Ia na wati Mereti sa vakasucumi Miriama, Samai, kei Isiba na tamai Esitemoa.",
			"19": "<span>19</span> (Na watina o Jeudaija sa vakasucumi Jereti na tamai Kedore, Epa na tamai Soko, kei Jekucieli na tamai Sanoa.) Sai ira oqo na luve i Bicia tagane, o koya na luve i Fero yalewa, o koya sa kauta o Mereti.",
			"20": "<span>20</span> Oi rau na luve i na wati Otaia era tagane, o koya na ganei Neama, e rau sa tamai Keila na Qamaite kei Esitemoa na Meakataite.",
			"21": "<span>21</span> Kei na luve i Saimone taganeo Eminoni, Raina, Beni- Anani, kei Tailoni.",
			"22": "<span>22</span> Na luve i Sela era tagane, o koya luve i Juta tagane o Eri, na tamai Leka, Leada na tamai Maresa, kei na vuvale era matai ni caka lineni e na vale i Asibea;",
			"23": "<span>23</span> O Jokimi talega, o ira na tamata mai Koseba, kei Joasi;Sarafi, o koya ka lewa mai Moape, kei Jasubi-Liemi. Ia nai vola tukutuku oqo sa makawa.",
			"24": "<span>24</span> Sai ira oqo na Dau- tulikuro ka ra tiko voli mai Netaimikei Ketera; era sa tiko maikea kei koya na tui me baleta na nona cakacaka.",
			"25": "<span>25</span> O iratou na luve i Simione tagane o Nemueli, Jamini, Jarivi, Sera, kei Saula,",
			"26": "<span>26</span> O Salumi na luvena tagane, o Mipisama na luvena tagane, o Misima na luvena tagane.",
			"27": "<span>27</span> Ia oiratou na luvei Misima tagane o Emueli na luvena tagane, Sakuri na luvena tagane, kei Simei na luvena tagane.",
			"28": "<span>28</span> O Simei e tinikaono na luvena tagane ka ono na luvena yalewa; ia e sega ni levu na luvedratou na tacina, e sega talega ni ra vakatubu kawa vakalevu me vakataki ira na luve i Juta.",
			"29": "<span>29</span> Era sa tiko mai Pierisepa, Molada, Esa-Suali,",
			"30": "<span>30</span> Bilia, Esemi, Tolada,",
			"31": "<span>31</span> Pecueli, Oma, Sikilaki,",
			"32": "<span>32</span> Peci-Makapoci, Esa-Susimi, Peci-Biri, kei Searaimi. Oqo na nodra veikoro me yacova na gauna i Tevita na tui.",
			"33": "<span>33</span> Kei na nodra veikoro o Etama, Aine, Raimoni, Tokeni, kei Asani â€“ e lima na koro â€“",
			"34": "<span>34</span> kei na veikoro kecega era vakavolivolita na veikoro oqori me yaco sara yani ki Peali. Oqo na nodra veitikotiko, ka ra maroroya tu na nodra i vola ni kawa:",
			"35": "<span>35</span> O Mesobaba, Jameleki, kei Josa na luve i Amasaia tagane;",
			"36": "<span>36</span> O Joeli, kei Jeu na luve i Josibia, na luve i Seraia tagane, na luve i Asieli tagane;",
			"37": "<span>37</span> O Elioenai, Jeakoba, Jesoaia, Asaia, Atieli, Jesimieli, kei Penaia;",
			"38": "<span>38</span> O Sisa na luve i Sifi tagane, na luve i Simeri tagane, na luve i Semaia tagane â€“",
			"39": "<span>39</span> O ira e cavuti oqo na yacadra era i liuliu ena nodra dui vuvale, kei na lewe ni vale i tamadra sa tubu vakalevu sara.",
			"40": "<span>40</span> Era sa mani lako ki na matamata ni koro o Ketori, me vaka ki na tokalau ni buca, ka vakasaqara na co me kedra na nodra qele ni manumanu.",
			"41": "<span>41</span> Era sa kunea na veico bulabula, ka uro, sa rabailevu vinaka na vanua, ka vanua ni vakacegu kei na galala; ni ra sa dau tiko voli e kea e liu o ira na Amaiti.",
			"42": "<span>42</span> O ira e volai tu oqo na yacadra era lako mai e na gauna i Esekaia na tui Juta; ka ra sa samuta na nodra veivale laca kei ira na Meunaiti era kune e kea, ka vakarusai ira sara vakaca, sa tu oqori me yacova mai na siga oqo. Era sa tiko ena nodra i vanua, ni sa tu maikea na kedra veico na nodra manumanu.",
			"43": "<span>43</span> Ia e so vei ira, era lewe lima na drau na tagane mai vei ira na luve i Simione, era sa lako cake ki na ulunivanua o Sia, ka sa nodra kavetani o Pelatia, Nearia, Refaia, kei Usieli, o iratou na luve i Isi ka tagane.",
			"44": "<span>44</span> Era sa vakadrukai ira na Amalekaiti era a dro. Era sa tiko voli mai kea me yacova na siga oqo."
		},
		"5": {
			"1": "<span>1</span> Veigauna 5",
			"2": "<span>2</span> Sai iratou oqo na luve i Rupeni tagane na ulumatua i Isireli â€” o koya dina na ulumatua, ia me vaka ni a vakadukadukalitaka nai davodavoi tamana, sa soli kina nai tutu vakaulumatua vei rau na luve i Joseva, na luve i Isireli tagane, sa mani sega kina volai na kawa me vaka na veitaravi ni nodra sucu;",
			"3": "<span>3</span> Ia sa gumatua vei ira na tacina o Juta, ka sa lako mai vei koya e dua na tui, e dina ga ni sa nei Joseva nai tutu vakaulumatuaâ€”",
			"4": "<span>4</span> O iratou na luve i Rupeni tagane na ulumatua i Isireli o Anoke, Palu, Esironi, kei Karimi.",
			"5": "<span>5</span> Na luve i Joeli tagane o Semaia, Koki, Simei,",
			"6": "<span>6</span> Maika, Reaia, Peali,",
			"7": "<span>7</span> kei Biera, o koya ka kauta vakavesu o Tikilaci- Pileseri na tui Asiria. O koya na nodra i liuliu na luve i Rupeni.",
			"8": "<span>8</span> Kei ira na luvena ena nodra dui vuvale, ni sa volai nai tukutuku ni nodra kawa: sa turaga o Jieli, kei Sakaraia,",
			"9": "<span>9</span> kei Bela na luve i Asasi tagane, na luve i Sema tagane, na luve i Joeli tagane, o koya ka tiko mai Aroeri, me yaco sara ki Nepo kei Peali-Meoni.",
			"10": "<span>10</span> Era sa tiko mai na tokalau e na matamata vaka ki na veikau ena yasana oqo ni uciwai na Uferetisi, baleta ni sa vakaikuritaki tikoga na levu ni nodra bulumakau e na vanua o Kiliati.",
			"11": "<span>11</span> Ia oqo ena gauna i Saula era a vala kei ira na Akiraiti, o ira era sa mate e ligadra; ka ra sa tiko e na nodra veivale laca e na vanua taucoko mai na tokalau ni vanua o Kiliati.",
			"12": "<span>12</span> O ira na vuvale i Kata era vakaitikotiko volekati ira e na vanua o Pasani vaka ki Salika:",
			"13": "<span>13</span> O Joeli sa turaga, o Safaimi e tarava, ka tarava yani o Jeanai kei Safati mai Pasani,",
			"14": "<span>14</span> kei ira na tacidra ena vale i tamadra: O Maikali, Mesulame, Seva, Jorai, Jakani, Sia, kei Ebari â€” e lewe vitu taucoko.",
			"15": "<span>15</span> O ira oqo na luve i Apieli na luve i Ura tagane, na luve i Jaroa tagane, na luve i Kiliati tagane, na luve i Maikali tagane, na luve i Jesisai tagane, na luve i Jado tagane, na luve i Busa tagane;",
			"16": "<span>16</span> O Ai na luve i Apetieli tagane, na luve i Kuni, sa turaga vei ira na lewe ni vale i tamana.",
			"17": "<span>17</span> Ka ra sa tiko mai Kiliati, mai Pasanikei na kena veikoro o ira na kawa i Kata, kei na veivanua rawarawa mai Saroni e na loma ni kenai yalayala.",
			"18": "<span>18</span> O ira kece oqo era sa volai e nai vola ni kawa e na gauna i Jocame na tui Juta, kei na gauna i Jeropoama na tui Isireli.",
			"19": "<span>19</span> O ira na luve i Rupeni, na luve i Kata, ka veimama na kawa i Manasa e vasagavulu kava na udolu, vitu na drau, onosagavulu na tamatai valu qaqa, o ira era taura rawa na nodra i seleiwau kei nai sasabai, era vanataka rawa na dakai-titi, era kena dau e nai valu, era lako ki nai valu.",
			"20": "<span>20</span> Era sa vala vata kei ira na Akiraiti, na Jeturi, Nafisi, kei na Nodabi.",
			"21": "<span>21</span> Ia era sa vukei e na nodra vala kei ira, ka ra soli na Akiraiti ki na ligadra, kei ira kecega era sa tiko vata tukuna, ni ra tagi vua na Kalou ena gauna ni veivala. Sa rogoca na nodra masu o Koya, baleta ni ra sa vakararavi Vua.",
			"22": "<span>22</span> Era sa qai kauta tani na nodra manumanu â€” e limasagavulu na udolu na Kameli, rua na drau limasagavulu na udolu na sipi, rua na udolu na asa â€” kei na dua na drau na udolu talega na nodra tamata i valu;",
			"23": "<span>23</span> e lewe levu era mate, baleta nai valu e Nona na Kalou. Ka ra sa tiko ga e na vale me yacova na veivakabobulataki.",
			"24": "<span>24</span> O ira na luvedra e veimama ni yavusa i Manasa era sa tiko e na vanua. Sa tubu na kedra i wiliwili me tekivu mai Pasani me yaco ki Peali- Erimoni, o ya mai Senira, se na ulu-ni-vanua o Erimoni.",
			"25": "<span>25</span> Oqo o ira nai liuliu ena veivale i tamadra: Eferi, Isi, Elieli, Asirieli, Jeremaia, Odavia, kei Jadieli. Era sa tamata qaqa ni valu, tamata rogo, ka ulu ni veivale i tamadra.",
			"26": "<span>26</span> Ia era sa sega ni dina vua na nodra Kalou na tamadra, ka ra sa cakava nai valavala ni yalewa ca vei ira na nodra kalou na tamata ni vanua o ya, o koya ka vakarusa oti e matadra na Kalou.",
			"27": "<span>27</span> Ia na Kalou sa qai vakauqeta na yaloi Puli na tui Asiria, o ya o Tikelaci-Pileseri na tui Asiria. Sa kauti ira na luve i Rupeni, na luve i Kata, kei na veimama ni yavusa i Manasa mera bula. Sa kauti ira ki Aleli, Apora, Ara, kei na uciwai mai Kosani me yacova na siga oqo."
		},
		"6": {
			"1": "<span>1</span> Veigauna 6",
			"2": "<span>2</span> O iratou na luve i Livai o Keresoni, Koaca, kei Merari.",
			"3": "<span>3</span> O iratou na luve i Koaca o Amirami, Isari, Epironi, kei Usieli.",
			"4": "<span>4</span> O iratou na luve i Amirai o Eroni, Mosese, kei Miriama. Ia o iratou na luve i Eroni tagane o Netava, Apaiu, Iliesa, kei Icamari.",
			"5": "<span>5</span> O Iliesa na tamai Fineasi, kei Fineasi na tamai Avisua;",
			"6": "<span>6</span> O Avisua na tamai Buki, ia o Buki na tamai Usi;",
			"7": "<span>7</span> O Usi na tamai Seraia, ia o Seraia na tamai Meraioci;",
			"8": "<span>8</span> O Meraioci na tamai Amaraia, ia o Amaraia na tamai Aitubi;",
			"9": "<span>9</span> O Aitubi na tamai Sadoki, ia o Sadoki na tamai Aimeasi;",
			"10": "<span>10</span> O Aimeasi na tamai Asaraia, ia o Asaraia na tamai Joanani;",
			"11": "<span>11</span> O Joanani na tamai Asaraia (o koya ea veiqaravi vakabete e na vale ni soro ka tara o Solomoni mai Jerusalemi);",
			"12": "<span>12</span> O Asaraia na tamai Amaraia, ia o Amaraia na tamai Aitubi;",
			"13": "<span>13</span> O Aitubi na tamai Sadoki, ia o Sadoki na tamai Salumi;",
			"14": "<span>14</span> O Salumi na tamai Ilikaia, ia oilikaia na tamai Asaraia;",
			"15": "<span>15</span> O Asaraia na tamai Seraia, ia o Seraia na tamai Jiosataki.",
			"16": "<span>16</span> O Jiosataki e a kau vakabobula e na gauna sa kauti ira kina vakabobula na Juta kei Jerusalemi o Jiova ena ligai Nepukanesa.",
			"17": "<span>17</span> O iratou na luve i Livai tagane o Keresoni, Koaca, kei Merari.",
			"18": "<span>18</span> Oqo na yacadrau na luve i Keresoni: O Livinai kei Simei.",
			"19": "<span>19</span> O iratou na luve i Koaca tagane o Amirai, Isari, Epironi, kei Usieli.",
			"20": "<span>20</span> Oi rau na luve Merari tagane Mali kei Musi. Ia oqo o ira na vei vuvale i Livai mai vei ira na tamadra:",
			"21": "<span>21</span> Mai vei Keresoni Livinai na luvena tagane, Jeaca na luvena tagane, kei Sima na luvena tagane,",
			"22": "<span>22</span> O Joana luvena tagane, Ido na luvena tagane, Sera na luvena tagane, kei Jeacerai na luvena tagane.",
			"23": "<span>23</span> Na luve i Koaca o Amenatave na luvena tagane, Kora na luvena tagane, Asira na luvena tagane,",
			"24": "<span>24</span> O Elikana na luvena tagane, Ebiasafa na luvena tagane, Asira na luvena tagane,",
			"25": "<span>25</span> O Teaca na luvena tagane, Urieli na luvena tagane, Usaia na luvena tagane, kei Saula na luvena tagane.",
			"26": "<span>26</span> Na luve i Elikana tagane o Amasai kei Aimoci.",
			"27": "<span>27</span> Ia vei Elikana, na luvena tagane o Sofai na luvena tagane, Necani na luvena tagane,",
			"28": "<span>28</span> Eliavi na luvena tagane, Jeroama na luvena tagane, kei Elikana na luvena tagane.",
			"29": "<span>29</span> Na luve i Samuela tagane o Joeli na ulumatua, kei Apaija na kenai karua.",
			"30": "<span>30</span> O iratou na luve i Merari tagane o Mali, o Livinai na luvena tagane, Simei na luvena tagane, o Usa na luvena tagane,",
			"31": "<span>31</span> O Simea na luvena tagane, Akaia na luvena tagane, kei Asaia na luvena tagane. O Ira Na Daunivakatagi Ena Vale I Jiova",
			"32": "<span>32</span> Ia oqo o ira na tagane sa lesia o Tevita mera dau qarava na sere e na vale i Jiova, ni sa mai tiko vakadua na kato.",
			"33": "<span>33</span> Era sa dauqarava tiko na sere kei nai vakatagi e na vale ni soro, me yacova ni sa tara oti o Solomoni na vale i Jiova mai Jerusalemi, ka ra sa qarava na nodra i tavi me vaka na nodra i tutu.",
			"34": "<span>34</span> Ka sai ira oqo era sa dauveiqaravi vata kei ira na luvedra tagane: Mai vei ira na luve i Koaca tagane o Emani na daunisere, na luve i Joeli tagane, na luve i Samuela tagane,",
			"35": "<span>35</span> na luve i Elikana tagane, na luve i Jeroama, na luve i Elieli tagane, na luve i Toa tagane,",
			"36": "<span>36</span> na luve i Sufa tagane, na luve i Elikana tagane, na luve i Meaca tagane, na luvei Amasai tagane,",
			"37": "<span>37</span> na luve i Elikana tagane, na luvei Joeli tagane, na luve i Asaraia tagane, na luvei Sefanaia tagane,",
			"38": "<span>38</span> na luve i Teaca tagane, na luve i Asira tagane, na luve i Ebiasava tagane, na luve i Kora tagane,",
			"39": "<span>39</span> na luve i Isa tagane, na luve i Koaca tagane, na luve i Livai tagane, na luvei Isireli tagane.",
			"40": "<span>40</span> Kei na tacina o Esava, o koya sa tu ki na ligana i matau, o Esava na luve i Berakaia tagane, na luve i Simea tagane,",
			"41": "<span>41</span> na luve i Maikali tagane, na luve i Beaseia tagane, na luve i Malakia tagane,",
			"42": "<span>42</span> na luve i Eceni tagane, na luve i Sera tagane, na luve i Adaia tagane,",
			"43": "<span>43</span> na luve i Ecani tagane, na luve i Sima tagane, na luve i Simea tagane,",
			"44": "<span>44</span> na luve i Jeaca tagane, na luve i Keresoni tagane, na luve i Livai tagane.",
			"45": "<span>45</span> O ira na tacidra, na luve i Merari era tagane, ki na ligana i mawi, o Ecani na luve i Kisi tagane, na luve i Apeti tagane, na luve i Maluki tagane,",
			"46": "<span>46</span> na luve i Asabaia tagane, na luve i Amasaia tagane, na luve i Ilikaia tagane,",
			"47": "<span>47</span> na luve i Amasi tagane, na luve i Bani tagane, na luve i Sameri tagane,",
			"48": "<span>48</span> na luve i Mali tagane, na luve i Musi tagane, na luve i Merari tagane, na luve i Livai tagane.",
			"49": "<span>49</span> Kei ira na tacidra, na Livaiti, era sa lesi ki na vei tavi kecega ena vale ni soro ni vale ni Kalou.",
			"50": "<span>50</span> Ia o Eroni kei ira na luvena era sa dau vakacabora nai soro e na cabocabo ni soro kama kei nai cabocabo ni ka boi vinaka, na cakacaka kecega ena Tikina Tabu Sara, ka me ra sorovaki ira na Isireli, me vaka na ka kecega sa vakarota vei ira O Mosese na tamata ni Kalou.",
			"51": "<span>51</span> Ia oqo oiratou na luve i Eroni tagane: O Eliesa na luvena tagane, o Fineasi na luvena tagane, Abisua na luvena tagane,",
			"52": "<span>52</span> O Buki na luvena tagane, Usi na luvena tagane, Seraia na luvena tagane,",
			"53": "<span>53</span> Meraioci na luvena tagane, Amaraia na luvena tagane, Aitubi na luvena tagane,",
			"54": "<span>54</span> Setoki na luvena tagane, kei Aimeasi na luvena tagane. Nodra I Tikotiko Na Livai",
			"55": "<span>55</span> Ia sai ira oqo na nodra i tikotiko ena nodra veikoro kecega kei na nodra i yalayala, sa soli ni ra sa rawata ena vakawiri madigi me i votavota vei ira na luve i Eroni tagane, kivei ira na vuvale i Koaca:",
			"56": "<span>56</span> Sa soli vei ira o Epironi e na vanua i Juta, vata na kena vei vanua ra taukena vata.",
			"57": "<span>57</span> Ia na veikoro vata na kena vanua ra taukena vata sa soli vei Kelevi na luve i Jefune.",
			"58": "<span>58</span> Ia era sa solia vei ira na luve i Eroni e dua na koro ni drodro, o Epironi; o Livinai talega vata na kena vanua ra taukena vata, Jatiri, Esetimoa vata na kena vanua ra taukena vata,",
			"59": "<span>59</span> O Ileni vata na kena vanua ra taukena vata, Debiri vata na kena vanua ra taukena vata,",
			"60": "<span>60</span> Asani vata na kena vanua ra taukena vata, kei Peci- Semesi vata na kena vanua ra taukena vata.",
			"61": "<span>61</span> Ia mai vei ira na yavusa i Penijamini: Keba vata na kena vanua ra taukena vata, Alemeci vata na kena vanua ra taukena vata, kei Anacoci vata na kena vanua ra taukena vata. Na veikoro e votai vei ira na vuvale kecega e tinikatolu.",
			"62": "<span>62</span> Kivei ira na vo ni vuvale ni yavusa i Koaca era sa rawata ena vakawiri madigi e tini na koro mai na veimama ni yavusa i Manasa.",
			"63": "<span>63</span> Kivei ira talega na luve i Keresoni tagane, vei ira taucoko na vei vuvale, era solia e tinikatolu na koro mai na yavusa i Isakari, na yavusa i Aseri, na yavusa i Navitalai, kei na yavusa i Manasa mai Pasani.",
			"64": "<span>64</span> Kivei ira na luvei Merari tagane, vei ira taucoko na vei vuvale, era sa solia e tinikarua na koro mai vei ira na yavusa i Rupeni, na yavusa Kata, na yavusa i Sevuloni.",
			"65": "<span>65</span> E ra sa solia na luve i Isireli na veikoro oqori, vata kei na kena veivanua ra taukena vata kivei ira na Livai.",
			"66": "<span>66</span> Ka ra sa solia e na vakawiri madigi mai vei ira na luvei Juta, mai na yavusa ni luve i Simione, kei na yavusa ni luve i Penijamini,na veikoro oqori ka sa vakayacani tu vei ira.",
			"67": "<span>67</span> Ia oqo eso vei ira na vuvale ni luve i Koaca e soli vei ira na veikoro me nodra mai vei ira na Ifereimi.",
			"68": "<span>68</span> Ka sa soli vei ira e dua na koro ni drodro, o Sikemi vata na kena veivanua ra taukena vata, e na vei ulunivanua na Ifereimi, o Keseri talega vata na kena vanua ra taukena vata,",
			"69": "<span>69</span> O Joemeama vata na kena vanua ra taukena vata, Peci-Oroni vata na kena vanua ra taukena vata,",
			"70": "<span>70</span> Aijaloni vata na kena vanua ra taukena vata, kei Kaca-Rimoni vata na kena vanua ra taukena vata.",
			"71": "<span>71</span> Ia mai na veimama ni yavusa i Manasa: Aneri vata na kena vanua ra taukena vata kei Vileama vata na kena vanua ra taukena vata, me nodra na vo ni vuvale ni luve i Koaca.",
			"72": "<span>72</span> Mai na veimama ni yavusa i Manasa: Sa soli vei ira na luve i Keresoni o Qolani mai Pasani vata na kena vanua ra taukena vata kei Asitaroci vata na kena vanua ra taukena vata.",
			"73": "<span>73</span> Mai na yavusa talega i Isakari: O Kedesi vata na kena vanua ra taukena vata, Daveraca vata na kena vanua ra taukena vata,",
			"74": "<span>74</span> Ramoci vata na kena vanua ra taukena vata, kei Anemi vata na kena vanua ra taukena vata.",
			"75": "<span>75</span> Mai na yavusa talega i Aseri: O Masali kei na kena vanua ra taukena vata, Avitoni vata na kena vanua ra taukena vata,",
			"76": "<span>76</span> Ukoki vata na kena vanua ra taukena vata, kei Reopi vata na kena vanua ra taukena vata.",
			"77": "<span>77</span> Mai na yavusa talega i Navitalai: O Kedesi mai Kalili vata na kena vanua ra taukena vata, Amoni vata na kena vanua ra taukena vata, kei Kirijaceimi vata na kena vanua ra taukena vata.",
			"78": "<span>78</span> Mai na yavusa i Sevuloni: Sa soli vei ira na vo ni luve i Merari o Rimoni vata na kena vanua ra taukena vata kei Tabori vata na kena vanua ra taukena vata.",
			"79": "<span>79</span> Ia mai na tai kadua ni Joritani, takoso me yacoi Jeriko, e na yasana ki na tokalau ni Joritani, sa soli vei ira mai na yavusa i Rupeni; o Besera e na veikau vata na kena vanua ra taukena vata, Jasa vata na kena vanua ra taukena vata,",
			"80": "<span>80</span> Ketemoce vata na kena vanua ra taukena vata, kei Mepeaca vata na kena vanua ra taukena vata.",
			"81": "<span>81</span> Mai vei ira talega na yavusa i Kata: Ramoci mai Kiliati vata na kena vanua ra taukena vata, Meanaimi vata na kena vanua bula,",
			"82": "<span>82</span> Esiponi vata na kena vanua ra taukena vata, kei Jaseri vata na kena vanua ra taukena vata."
		},
		"7": {
			"1": "<span>1</span> Veigauna 7",
			"2": "<span>2</span> Na luve i Isakari tagane o Tola, Pua, Jasuba, kei Simironi â€“ e lewe va taucoko.",
			"3": "<span>3</span> O iratou na luve i Tola tagane o Usi, Refaia, Jerieli, Jeamai, Jivisama, kei Semuela, e ratou ulu ni vuvale i tamadratou. Era tamata qaqa ni valu na luve i Tola ena nodra i tabatamata; Na kedra i wiliwili e na gauna i Tevita e ruasagavulu ka rua na udolu ka ono na drau.",
			"4": "<span>4</span> Na luve i Usi tagane o Isiraia, ia na luve i Isiraia tagane o Maikali, Opetaia, Joeli, kei Isaia. E ratou turaga kece na lima oqo.",
			"5": "<span>5</span> Ia sa wili vata kei iratou, ena nodra tabatamata, me vaka na dui vuvale i tamadra, e tolusagavulu kaono na mataivalu era sa vakarau tu ki nai valu; ni sa levu na watidra kei na luvedra tagane.",
			"6": "<span>6</span> Ia oqo o ira na veitacini ena kedra maliwa na luve i Isakari era sa tamata qaqa ni valu, sa volai e nai vola ni nodra kawa, e walusagavulu kavitu na udolu taucoko.",
			"7": "<span>7</span> O iratou na luve i Penijamini tagane o Vela, Bikari, kei Jetiaeli â€“ e tolu taucoko.",
			"8": "<span>8</span> O iratou na luve i Vela tagane o Esiboni, Usi, Usieli, Jerimoce, kei Iri â€“ e lima taucoko. E ratou sa ulu ni vuvale i tamadratou, ka ratou sa volai me vaka na nodra i tabatamata, e ruasagavulu ka rua na udolu ka tolusagavulu kava na tamata qaqa ni valu.",
			"9": "<span>9</span> O iratou na luve i Bikari tagane o Semirai, Joasi, Eliesa, Elioenai, Omeri, Jerimoce, Apaija, Anacoci, kei Alemeci. O iratou kece oqo na luve i Bikari tagane.",
			"10": "<span>10</span> Ka ratou sa volai e na kedra i vola tukutuku me vaka na nodra dui tabatamata, e ratou ulu ni vuvale i tamadratou, e ruasagavulu na udolu ka rua na drau na tamata qaqa ni valu.",
			"11": "<span>11</span> Na luve i Jetiaeli o Viliana, kei na luve i Viliana o Jeusi, Penijamini, Eude, Keneana, Secani, Arisisi, kei Aiseara.",
			"12": "<span>12</span> O iratou kece na luve i Jetiaeli oqo na ulu ni vuvale i tamadratou; era sa lewe tini ka vitu na udolu ka rua na drau na tamata qaqa ni valu era sa vakarau me lako ki nai valu ka vala.",
			"13": "<span>13</span> O Supimi kei Upimi e rau luve i Iri, kei Usimi na luve i Aeri.",
			"14": "<span>14</span> O iratou na luve i Navitalai tagane o Jasieli, Kuni, Jesari, kei Salumi, o iratou na luve i Vilia tagane. Na Vuvale I Manasa (Ra)",
			"15": "<span>15</span> O ira na kawa i Manasa: Sa vakasucuma na yalewa ni Siria ka watina lailai na luvena tagane o Makiri na tamai Kiliati, na tamai Asirieli.",
			"16": "<span>16</span> Sa kauta o Makiri me watina na ganedrau o Upimi kei Supimi, na yacana o Meaka. Na yaca i makubui Kiliati tagane o Siloveta, ia o Siloveta e vakaluveni yalewa ga.",
			"17": "<span>17</span> (O Meaka na wati Makiri sa vakasucuma e dua na luvena tagane, ka sa kaciva me yacana o Veresi. Na yaca i tacina o Seresi, ka rau luvena tagane o Ulama kei Rakema.",
			"18": "<span>18</span> Na luve i Ulama tagane o Vetani.) Oqo na kawa i Kiliati na luve i Makiri tagane, na luve i Manasa tagane.",
			"19": "<span>19</span> Na ganena o Amolekeci sa vakasucumi Isoti, Aviesa, kei Mala.",
			"20": "<span>20</span> Kei na luve i Semida tagane o Aiana, Sekemi, Liki, kei Aniama.",
			"21": "<span>21</span> O iratou na luve i Ifereimi tagane o Sucela, Bereti na luvena tagane, Teaci na luvena tagane, Elada na luvena tagane, Teaci na luvena tagane,",
			"22": "<span>22</span> Savati na luvena tagane, Sucela na luvena tagane, Esari na luvena tagane, kei Iliada na luvena tagane. O ira na kai Kaca ka ra a sucu e na vanua o ya era a vakamatei iratou baleta ni ratou a lako sobu mai ka kauta tani na nodra bulumakau.",
			"23": "<span>23</span> Sa qai tagicaki iratou o Ifreimi na tamadratou me vuqa na siga, ka ra sa lako mai na wekana me vakacegui koya.",
			"24": "<span>24</span> Ia ni sa curu vua na watina, sa bukete o koya ka vakasucuma e dua na luvena tagane; ka sa vakayacana o koya me o Veraia, baleta ni sa yacova na nona vuvale na ka ca.",
			"25": "<span>25</span> Ia oqo na luvena yalewa o Siera, o koya ka tara cake na yasana i ra kei na yasana i cake ni vanua erua o Beci-Oroni kei Useni-Siera;",
			"26": "<span>26</span> Ia o Refa na luvena tagane, o Risefa talega, kei Tela na luvena tagane, o Teana na luvena tagane,",
			"27": "<span>27</span> O Leatani na luvena tagane, Amiute na luvena tagane, Elisama na luvena tagane,",
			"28": "<span>28</span> Nuni na luvena tagane, kei Josua na luvena tagane.",
			"29": "<span>29</span> Ia oqo na nodra i yau kei na nodra i tikotiko o Peceli kei na kena veikoro: vaka ki na tokalau ki Nearani, ki na ra ni Kesari kei na kena veikoro, kei Sikemi kei na kena veikoro, me yaco sara ki Aia kei na kena veikoro;",
			"30": "<span>30</span> Ia mai na nodra i yalayala na luve i Manasa o Beci-Seani kei na kena veikoro, Teanaka kei na kena veikoro, Meqido kei na kena veikoro, Dora kei na kena veikoro. E na veivanua oqo era tiko kina na luve i Joseva, na luve i Isireli.",
			"31": "<span>31</span> O iratou na luve i Aseri tagane oimena, Iseva, Isevi, Veraia, kei na ganedratou o Sera.",
			"32": "<span>32</span> Na luve i Veraia tagane o Everi kei Malakieli, o koya na tamai Virisaici.",
			"33": "<span>33</span> Ia o Everi na tamai Jafeleti, Soma, Otame, kei na ganedratou o Sua.",
			"34": "<span>34</span> Na luvei Jafeleti tagane o Pasaki, Vimiale, kei Asavaci. Oqo o iratou na luve i Jafeleti.",
			"35": "<span>35</span> Na luve i Semari tagane o Ai, Roqa, Jeuba, kei Erami.",
			"36": "<span>36</span> Kei na luvei Elemi na tacina o Sofa, Imina, Selesa, kei Amale.",
			"37": "<span>37</span> O iratou na luve i Sofa o Sua, Arinefa, Suali, Veri, Amira,",
			"38": "<span>38</span> Veseri, Ote, Sama, Silisa, Jicirana, kei Viera.",
			"39": "<span>39</span> O iratou na luve i Jeceri o Jefune, Pisipa, kei Ara.",
			"40": "<span>40</span> Na luve i Ula o Ara, Aniela, kei Risia.",
			"41": "<span>41</span> O ira oqo na luve i Aseri, era ulu ni vei vuvale i tamadra, era tamata digitaki, tamata qaqa ni valu, ka turaga i liuliu. Era sa volai e nai tukutuku ni nodra i taba tamata ni ra wili vata kei ira na mataivalu ka ra sa tu vakarau me vala; na kedra i wiliwili e ruasagavulu kaono na udolu."
		},
		"8": {
			"1": "<span>1</span> Veigauna 8",
			"2": "<span>2</span> Ia o Penijamini na tamai Vela na nona ulumatua, o Asiveli na kenai karua, o Ara na kenai katolu,",
			"3": "<span>3</span> O Noa na kenai kava, kei Rafa na kenai kalima.",
			"4": "<span>4</span> O iratou na luve i Vela tagane o Adari, Qera, Aviuta,",
			"5": "<span>5</span> Avisua, Neamani, Aoa,",
			"6": "<span>6</span> Qera, Sefufane, kei Urama.",
			"7": "<span>7</span> Oqo o iratou na luve i Eude, e ratou ulu ni vei vuvale i tamadratou era sai taukei mai Keba, ka ra a vakasaurarataki ira mera toki ki Maneaci:",
			"8": "<span>8</span> O Neamani, Aija, kei Qera e ratou a vakasaurarataki ira mera toki. Sa tamai Usa kei Aiudi o koya.",
			"9": "<span>9</span> O Searaimi talega e vakaluveni e na vanua o Moape, ni sa vakasavi rau oti na watina o Usimi kei Beara.",
			"10": "<span>10</span> Ia o Odesa na watina sa qai vakasucumi Jobaba, Sivia, Mesa, Melicama,",
			"11": "<span>11</span> Jeusi, Sakia, kei Mirima. Oqo o ira na luvena ka ra ulu ni vuvale i tamadra.",
			"12": "<span>12</span> Ia na luvena vei Usimi o Avitube kei Elipeali.",
			"13": "<span>13</span> Na luve i Elipeali tagane o Evari, Misama, kei Simede, o koya ka tarai Ono kei Lota vata na kena veikoro;",
			"14": "<span>14</span> kei rau o Veraia kei Sema, e rau ulu ni vuvale i tamadra ka rau i taukei e Aijaloni, ka rau a vakasavi ira nai taukei e Kaca.",
			"15": "<span>15</span> O Aio, Sasake, Jeremoce,",
			"16": "<span>16</span> Savataia, Aradi, Eta,",
			"17": "<span>17</span> Maikali, Isipa, kei Joa e ratou luve i Veraia tagane.",
			"18": "<span>18</span> O Sevataia, Mesulame, Isiki, Evari,",
			"19": "<span>19</span> Isimerai, Jisilaia, kei Jobaba e ratou luve I Elipeali tagane.",
			"20": "<span>20</span> O Jakimi, Sikiri, Savedi,",
			"21": "<span>21</span> Elienai, Siletai, Elieli,",
			"22": "<span>22</span> Adaia, Veraia, kei Simiraca e ratou luve i Simei tagane.",
			"23": "<span>23</span> O Isipani, Ebari, Elieli,",
			"24": "<span>24</span> Abadoni, Sikiri, Enani,",
			"25": "<span>25</span> Ananaia, Ilama, Anatocia,",
			"26": "<span>26</span> Ifedeia, kei Penueli e ratou luve i Sasake.",
			"27": "<span>27</span> O Samaserai, Searaia, Atalaia,",
			"28": "<span>28</span> Jearesia, Ilaija, kei Sikiri e ratou luve i Jeroama.",
			"29": "<span>29</span> O ira oqo na ulu ni vei vuvale i tamadra e na nodra dui tabatamata, era turaga. Era vakaitikotiko e Jerusalemi.",
			"30": "<span>30</span> Ia na tamai Kipione, o koya e yaca i watina o Maeka, e tiko voli e Kipione.",
			"31": "<span>31</span> Ia na nona ulumatua o Avitoni, sa qai sucu o Suri, Kisi, Peali, Netava,",
			"32": "<span>32</span> Kidore, Aio, Sekari,",
			"33": "<span>33</span> kei Mikiloci, o koya ka tamai Simea. Era tiko voleka talega vei ira na wekadra e Jerusalemi, vata kei ira na veiwekani.",
			"34": "<span>34</span> O Nira na tamai Kisi, o Kisi na tamai Saula, ia o Saula na tamai Jonacani, Malikisua, Avinatave, kei Esi-Peali.",
			"35": "<span>35</span> Na luve i Jonacani tagane o Meriba-Peali, ia sa tamai Maika o Meriba-Peali.",
			"36": "<span>36</span> Na luve i Maika tagane o Paiconi, Meleki, Tarea, kei Easi.",
			"37": "<span>37</span> Ka sa tamai Jioada o Easi; o Jioada na tamai Alemeki, Asamaveci, kei Simiri; ia o Simiri na tamai Mosa.",
			"38": "<span>38</span> O Mosa na tamai Vinea, Rafa na luvena tagane, Iliesa na luvena tagane, kei Aseli na luvena tagane.",
			"39": "<span>39</span> O Aseli e ono na luvena tagane ia oqo na yacadra: Asirikama, Bokeru, Isimeli, Seraia, Opetaia, kei Enani. O ira kece oqo na luve i Aseli.",
			"40": "<span>40</span> Ia o ira na luve i Eseki na tacina o Ulami na nona ulumatua, Jeusi na kenai karua, kei Elifeleti na kenai katolu.",
			"41": "<span>41</span> Na luve i Ulami era tamata qaqa ni valu O dauvanataki dakaititi. E levu na luvedra tagane kei na makubudra tagane, e dua na drau ka limasagavulu taucoko. Oqo o ira kecega na luve i Penijamini."
		},
		"9": {
			"1": "<span>1</span> Veigauna 9",
			"2": "<span>2</span> Ia sa volai tu o ira kecega na Isireli me vaka na dui nodra i tabatamata, ka sa dina sara, ni sa volai kece tu oqori ena kedra i vola tukutuku na tui e Isireli. Ia o Juta sa kau tani yani vakabobula ki Papiloni baleta na nodra talaidredre.",
			"3": "<span>3</span> Ia o ira nai matai ni tamata mera taura na nodra i votavota ena nodra veikoro o ira na Isireli, o ira na bete, Livai kei na Necinimi.",
			"4": "<span>4</span> Ia era sa tiko e Jerusalemi na luve i Juta, kei na so na luve i Penijamini, kei na so talega na luve Ifereimi kei Manasa;",
			"5": "<span>5</span> O Ucei na luve i Amiude tagane, na luve i Omiri tagane, na luve i Imeri tagane, na luve i Bani tagane, mai vei ira na kawa i Veresi, na luve i Juta tagane.",
			"6": "<span>6</span> Vei ira na Sailonaite: Asaia na ulumatua kei ira na luvena tagane.",
			"7": "<span>7</span> Vei ira na luve i Sera tagane: Jeueli, kei ira veiwekani â€” e ono na drau ka ciwasagavulu.",
			"8": "<span>8</span> Vei ira na luve i Penijamini tagane: Salu na luve i Mesulame tagane, na luve i Odavia tagane, na luve i Asenua tagane;",
			"9": "<span>9</span> O Iveneia na luve i Jeroama tagane; Ela na luve i Usi tagane, na luve i Mikeri tagane; Mesulame na luve i Sevataia tagane, na luve i Reueli tagane, na luve i Ivenaia tagane;",
			"10": "<span>10</span> kei ira na veiwekani, me vaka na nodra i tabatamata â€“ e ciwa na drau ka limasagavulu ka ono. O ira kece oqo na ulu ni vei vuvale i tamadra ena loma ni veivale i tamadra.",
			"11": "<span>11</span> Vei ira na bete: Jedaia, Jioiarivi, kei Jakini;",
			"12": "<span>12</span> O Asaraia na luve i Ilikaia tagane, na luve i Mesulame tagane, na luve i Setoki tagane, na luve i Meraioci tagane, na luve i Aituvi tagane, na vunivola ni vale ni Kalou;",
			"13": "<span>13</span> O Adaia na luve i Jeroama tagane, na luve i Pasuri tagane, na luve i Malakia tagane; o Measai na luve i Adiela tagane, na luve i Jasera tagane, na luve i Mesulame tagane, na luve i Misilemeci tagane, na luve i Imera tagane;",
			"14": "<span>14</span> Kei ira na wekadra, era ulu ni vei vuvale i tamadra â€” e dua na udolu, vitu na drau ka onosagavulu. Era tamata gugumatua e na cakacaka ni veiqaravi e na vale ni Kalou.",
			"15": "<span>15</span> Vei ira na Livai: O Semaia na luve i Asuvi tagane, na luve i Aserikama tagane, na luve i Asavaia tagane mai vei ira na luve i Merari tagane.",
			"16": "<span>16</span> O Vakebakara, Eresi, Kalala, kei Matanaia na luve i Maika tagane, na luve i Sikiri tagane, na luve i Esava tagane.",
			"17": "<span>17</span> O Opetaia na luve i Semaia tagane, na luve i Kalala tagane, na luve i Jetucuni tagane; kei Berekaia na luve i Esa, na luve i Elikana tagane, o koya e bula voli e na veikoro vaka Nitofa. O Ira Na Livai Era I Vakatawa Ni Matamata",
			"18": "<span>18</span> O ira nai vakatawa ni matamata o Salumi, Akubu, Talimoni, Aimani, kei ira na wekadratou. E nodra turaga o Salumi.",
			"19": "<span>19</span> Me yacova mai o ya era sa dau vakatawa na matamata ni nodra keba na luve i Livai ena matamata ni Tui mai na tokalau.",
			"20": "<span>20</span> O Salumi na luve i Kore tagane, na luve i Ebiasava tagane, na luve i Kora tagane, kei ira na wekana, mai na vale i tamana, na Koraiti, era sa liutaka na cakacaka ni veiqaravi, nai vakatawa ni matamata ni vale ni soro. O ira na tamadra era sa dau i vakatawa ni matamata ki na vale i Jiova.",
			"21": "<span>21</span> Ia o Fineasi na luve i Iliesa tagane sa nodra vunivola tu mai ena veigauna sa oti; o Jiova sa dau tiko vata kei koya.",
			"22": "<span>22</span> O Sakaraia na luve i Miselemaia tagane sai vakatawa ni katuba ni vale ni soro.",
			"23": "<span>23</span> O ira kecega sa digitaki me ra i vakatawa ni matamata era lewe rua na drau ka tinikarua. Era sa volai tu me vaka na dui nodra i tabatamata, ena nodra veikoro. E rau a digitaki ira ki na nodra i tutu dokai oqo oTevita kei Samuela na parofita.",
			"24": "<span>24</span> Io o ira kei ira na luvedra era sa lewa na matamata kecega ki na vale ni Kalou, na vale ni soro, sa nodra i lesilesi.",
			"25": "<span>25</span> O ira nai vakatawa ni matamata era sa lesi ki na matanicagi e va: na tokalau, ra, vualiku, kei na ceva.",
			"26": "<span>26</span> Ia o ira na wekadra mai na loma ni koro era na dau lako vata mai kei ira ena veigauna eso me vitu na siga.",
			"27": "<span>27</span> Ia nai tutu dokai oqori e ratou taura tu e lewe va nai vakatawa i liuliu; e ratou kawa i Livai. Ka ratou lewa na veirumu kecega kei nai yau ni vale ni Kalou.",
			"28": "<span>28</span> Ka ra sa tiko vakavolivolita ga na vale ni Kalou baleta ni sa nodra i tavi, ka ra sa lewa na kena dola ena veimataka.",
			"29": "<span>29</span> Ia oqo eso vei ira era lewa nai yaya ni veiqaravi, ni ra sa dau wilika na levu ni yaya ena kena kau mai ki loma kei na kena kau lesu yani ki tuba.",
			"30": "<span>30</span> Eso vei ira sa lesi me vakaraica nai yaya kecega ni vale vakaturaga e wili kina na kena e lelevu kei na ka lalai, kei na valawa matailalai, kei na waini, kei na waiwai kei na ka boi vinaka kei nai saluwaki.",
			"31": "<span>31</span> Kei na so vei ira na luve ni bete era daucakava nai lumu vakai saluwaki.",
			"32": "<span>32</span> O Maticia na Livai, na ulumatua i Salumi na luve i Kora, sa nonai tavi me lewa na ka kecega sa tavu e nai tavuteke.",
			"33": "<span>33</span> Kei na so vei ira na veiwekani na luve i Koaca, era lewa na kena vakarautaki na madrai dauraici me baleta na veiSiga ni Vakacecegu.",
			"34": "<span>34</span> O ira oqo na daunisere, era ulu ni nodra vei vuvale na Livai, era tiko e na vei rumu e loma, ka ra sa vakalalagataki mai nai tavi tale eso; ni ra sa digitaki ki na cakacaka o ya e na siga kei na bogi.",
			"35": "<span>35</span> O ira na ulu ni nodra vei vuvale na Livai oqo era sa ulu tiko ena nodra i tabatamata kecega. Era vakaitikotiko e Jerusalemi.",
			"36": "<span>36</span> O Jeieli na tamai Kipioni, na yaca i watina o Maeka, e tiko mai Kipioni.",
			"37": "<span>37</span> Na luvena ulumatua tagane o Avitoni, qai o Suri, Kisi, Peali, Neri, Netava,",
			"38": "<span>38</span> Kedora, Aio, Sakaraia, kei Mikiloci.",
			"39": "<span>39</span> Ia o Mikiloci na tamai Semeama. E ratou tiko volekati ira talega na wekadra e Jerusalemi, kei ira na veiwekani.",
			"40": "<span>40</span> O Neri na tamai Kisi, o Kisi na tamai Saula, ia o Saula na tamai Jonacani, Malekisua, Avinatave, kei Esi-Peali.",
			"41": "<span>41</span> Na luvei Jonacani tagane o Meriba-Peali, ia o Meriba-Peali na tamai Maika.",
			"42": "<span>42</span> O iratou na luvei Maika tagane o Paiconi, Meleki, Tarea, kei Easi.",
			"43": "<span>43</span> Ia o Easi na tamai Jara; o Jara na tamai Alemeci, Asimaveci, kei Simiri; kei Simiri na tamai Mosa;",
			"44": "<span>44</span> O Mosa na tamai Vinea, o Refaia na luvena tagane, Eliasa na luvena tagane, kei Aseli na luvena tagane.",
			"45": "<span>45</span> O Aseli e ono na luvena tagane kei na yacadratou oqo: Asirikama, Bokeru, Isimeli, Searaia, Ovetaia, kei Ena; oqo o iratou na luve i Aseli tagane. Nai Cavacava Vakaciriloloma I Saula Kei Iratou"
		},
		"10": {
			"1": "<span>1</span> Veigauna 10",
			"2": "<span>2</span> Ia oqo era sa mai vala o ira na kai Filisitia kei ira na Isireli; ia era sa dro na Isireli e matadra na kai Filisitia, ka ra sa vakamatei mai na Ulunivanua o Kiliboa.",
			"3": "<span>3</span> Era sa qai vakacemuri Saula kei iratou na luvena vakaukauwa o ira na kai Filisitia. Ia o ira na kai Filisitia era sa vakamatei Jonacani, Avinatave, kei Malekisua, oiratou na luve i Saula tagane.",
			"4": "<span>4</span> Sa kaukauwa cake vei Saula nai valu. Sa vanai koya o ira na dauvanavana, ka sa mavoa o koya ena gasau ni nodra dakai na dauvanavana.",
			"5": "<span>5</span> Sa qai tukuna o Saula vua sa daucolata na nonai yaragi, \"Ucuna na nomui seleiwau, ka suaki au kina, de ra lako mai na tamata sega ni cili oqori ka vakacacani au.\" Ia sa sega ni rawa vua sa daucolata na nonai yaragi, baleta ni sa rere vakalevu. Sa mani vakaduria e dua nai seleiwau o Saula ka bale yani kina.",
			"6": "<span>6</span> Ia ni sa raici Saula na cauravou sa daucolata na nonai yaragi ni sa mate, sa baleta talega o koya na nonai seleiwau ka mate.",
			"7": "<span>7</span> Sa mani mate o Saula kei iratou na luvena tagane tolutolu, kei ira kece na nona lewe ni vale era sa mate vata kecega.",
			"8": "<span>8</span> Ia ni ra sa raica o ira na Isireli kecega era sa tiko e na buca ni ra sa dro kecega ka sa mate talega o Saula kei iratou na luvena, era sa biuta tani tu na nodra veikoro ka dro; era sa qai lako mai na kai Filisitia ka vakatawani ira.",
			"9": "<span>9</span> Ia sa yaco ena siga ka tarava, ni ra sa lako mai na kai Filisitia mera luvata na nodra i sulu na mate, era sa kunei Saula kei iratou na luvena ni ratou sa mate toka mai na Ulunivanua o Kiliboa.",
			"10": "<span>10</span> Era sa luvata na nonai sulu ka kauta na uluna kei na nonai yaragi, ka vakauta nai tukutuku ki na veivanu kece e Filisitia mera kacivaka nai tukutuku ena vei vale-ni-soro ni nodra veikalou matakau kei ira kece talega na tamata.",
			"11": "<span>11</span> Era sa qai biuta na nonai yaragi ena loma ni nodra bure kalou, ka vakarubeca na uluna ena bure kalou i Tekoni.",
			"12": "<span>12</span> Ia ni ra sa rogoca kecega mai Jepesi-Kiliati na ka kecega era sa cakava na kai Filisitia kivei Saula,",
			"13": "<span>13</span> era sa duri cake na tamata qaqa ni valu kecega ka kauta na yago i Saula kei na yagodratou na luvena; ka ra sa kauta mai ki Jepesi, ka buluta na suidratou e na ruku ni vuni oki mai Jepesi, ka lolo me vitu na siga.",
			"14": "<span>14</span> Ia sa mate o Saula e na nona talaidredre ka cakava vei Jiova, baleta ni a sega ni maroroya na vosa i Jiova, ka ni a vakarorogo talega kina nonai vakasala e dua na dauvakacuru.",
			"15": "<span>15</span> Ia e a sega ni taroga na Kalou; sa mani vakamatei koya kina o Koya, ka solia na matanitu vei Tevita na luve i Jese."
		},
		"11": {
			"1": "<span>1</span> Veigauna 11",
			"2": "<span>2</span> Era sa qai lako kece mai na Isireli vei Tevita ki Epironi, ka tukuna, \"E dina ga ni keimami sa suimu ka lewemu.",
			"3": "<span>3</span> \"Ena gauna talega sa oti, ni sa tui tiko o Saula, oi kemuni ga o ni a liutaki ira yani na Isireli ki tuba ka kauti ira lesu tale mai ki loma; ka sa tukuna vei kemuni o Jiova na nomuni Kalou, 'O na vakatawai ira na Noqu tamata na Isireli, ka lewa vei ira na Noqu tamata na Isireli.'\"",
			"4": "<span>4</span> O koya oqo era sa lako kece mai kina na qase ki vua na tui ki Epironi, ka sa cakava o Tevita e dua na veiyalayalati vata kei ira ena mata i Jiova mai Epironi. Ia era sa lumuti Tevita me tui e Isireli taucoko, me vaka na vosa i Jiova e na gusui Samuela.",
			"5": "<span>5</span> Sa qai lako o Tevita kei ira kecega na Isireli ki Jerusalemi, e kilai tu me o Jevusa, na koro era tiko kina na Jevusaiti, o ira nai taukei ni vanua.",
			"6": "<span>6</span> Ia era sa tukuna nai taukei ni vanua vei Tevita, \"Mo kakua ni lako mai ki ke!\" Ia o Tevita sa rawa na koro ni valu o Saioni (o ya, na Koro i Tevita).",
			"7": "<span>7</span> Ia oqo sa tukuna o Tevita, \"O koya ena kabai ira e liu na Jevusaiti ena turaga ka kavetani.\" Ia o Joave na luve i Seruwaia sa liu taumada, ka sa yaco me turaga.",
			"8": "<span>8</span> Sa qai tiko o Tevita ena koro qaqa; ia oqo era sa vakayacana me Koro i Tevita.",
			"9": "<span>9</span> Ka sa tara o koya na kena veikoro vakavolivolita, mai Mailo ki na vanua vakavolivolita. Sa vakavinakataka o Joave na vo ni veikoro.",
			"10": "<span>10</span> Ia sa tubu cake voli o Tevita me rogo levu, ka ni sa tiko vata kei koya o Jiova ni lewe vuqa.",
			"11": "<span>11</span> Ia oqo o ira sa liu vei ira na tamata qaqa i Tevita, era sa vakaukauwataki ira vata kei koya e na nona matanitu, kei ira kecega na Isireli, me ra buli koya me tui, me vaka na vosa i Jiova ena vuku i Isireli.",
			"12": "<span>12</span> Sai koya oqo na kedra i wiliwili na tamata qaqa sa tu vei Tevita: Jasoviama na luve i Akimonaiti tagane, sa liu vei ira na kavetani; e a laveta cake na nona moto vei ira e lewe tolu na drau, e vakamatei ira vata kece e na dua ga na gauna.",
			"13": "<span>13</span> E taravi koya o Iliesa na luve i Toto tagane, na Aoaite, o koya e dua vei iratou na lewe tolu na tamata qaqa.",
			"14": "<span>14</span> O koya e a tiko vata kei Tevita mai Pasidemimi. Ia oqo era sa soqoni vata na kai Filisitia me veivala, ka sa dua na tiki ni qele sa tubu tu kina na witi. Ia oqo era sa dro na tamata mai vei ira na kai Filisitia.",
			"15": "<span>15</span> Ia era sa la'ki tu vakadua ena loma donu ni veiwiti, era taqomaka, ka vakamatei ira na kai Filisitia. Ia o Jiova sa vakavuna e dua na qaqa levu.",
			"16": "<span>16</span> Ia oqo e le tolu vei ira na lewe tolusagavulu na turaga ni valu e ratou a lako sobu ki na vatu vei Tevita, ki na qaravatu mai Adulama; ka ra sa keba vakaivalu mai na buca o Refeimi o ira na kai Filisitia.",
			"17": "<span>17</span> O Tevita sa tiko e na gauna o ya mai na koro ni valu, ka sa tiko na mataivalu ni Filisita mai Peceliema.",
			"18": "<span>18</span> Ka sa tukuna ena nona gagadre dina o Tevita, \"Isa, ke dua mada ga me bau vagunuvi au ena wai mai na mata-ni-wai mai Peceliema, o koya e tiko e na yasa ni matamata!\"",
			"19": "<span>19</span> E ratou a curuma vakaukauwa na nodra keba na kai Filisitia, ratou takiva na wai mai na matani- wai e Peceliema ka tiko mai na matamata, ka kauta mai kivei Tevita. Ia sa sega ni gunuva o Tevita, sa sova ga yani kivei Jiova.",
			"20": "<span>20</span> Ka sa tukuna, \"Me vakayawa sara vei au, oi Kemuni na noqu Kalou, me'u cakava na ka oqo! Me'u gunuva li na nodratou dra na tamata oqo ni ratou a douvaka na mate? Ni ratou a bolea na mate me ratou kauta mai na wai.\" O koya oqo sa bese kina ni gunuva. Na veika oqo e ratou a cakava na lewe tolu na tamata qaqa.",
			"21": "<span>21</span> O Avisai na taci Joave sai liuliu vei iratou e lewe tolu tale. Sa laveta cake o koya na nona moto vei ira e le tolu na drau na tamata ni valu, vakamatei ira, ka sa rawata e dua na yaca rogo vei iratou tolutolu oqo.",
			"22": "<span>22</span> Sa dokai vakalevu cake o koya mai vei rau na lewe rua na nonai tokani. Sa yaco kina o koya me nodratou kavetani. Ia e sega ga ni rawata me liutaka na i matai ni tolu.",
			"23": "<span>23</span> O Penaia na luve i Jioiada tagane, na luvena e dua na tamata qaqa mai Kabasili, o koya sa cakava oti e vuqa na ka vinaka. E a vakamatei rau e rua na tamata yate vakalaione ni Moape. Sa lako sobu talega o koya ka vakamatea e dua na laione ena loma ni dua nai keli ena dua na siga ni uca cevata.",
			"24": "<span>24</span> Ka sa vakamatea talega e dua na kai Ijipita, e tamata levu, ka vitu veimama na fiti na kena balavu. E ligana na kai Ijipita o ya e taura tu na nona moto ka vaka tu na kaukamea ni vakadei i koya na dautalitali; ka sa lako sobu yani vua kei na dua nai titoko, ka kaliraka tani mai ligana na kai Filisitia na nona moto, ka vakamatei koya ena nona moto ga.",
			"25": "<span>25</span> Na veika oqo e a vakayacora o Penaia na luve i Jioiada, ka sa rawata kina na yaca e rogo vei iratou na tamata qaqa e tolu oqo.",
			"26": "<span>26</span> E dina ni sa dokai cake o koya vei ira na lewe tolusagavulu, ia e a sega ga ni rawata me uasivi cake vei iratou nai matai ni tolu. Ka sa digitaki koya o Tevita me liutaki ira na nona mata-yadra.",
			"27": "<span>27</span> E ratou tamata qaqa talega ni valu o Asaeli na taci Joave, o Elanani na taci Toto na kai Peceliema,",
			"28": "<span>28</span> O Samoci na Aroraite, Elesi na Pelonaite,",
			"29": "<span>29</span> na luve i Ikesi na Tekoite, Aviesa na kai Anacoci,",
			"30": "<span>30</span> Sivekai na luve i Usa, Ilai na Aoaite,",
			"31": "<span>31</span> Mearai na kai Netofa, Eledi na luve i Peana na kai Netofa,",
			"32": "<span>32</span> Icei na luve i Ribai na kai Kipea, mai vei ira na luve i Penijamini, Penaia na Piraconaite,",
			"33": "<span>33</span> O Urai mai na veimata ni wai drodro e Keasi, Avieli na kai Araba,",
			"34": "<span>34</span> Asamaveci na Bearumaite, Eliava na Sealibonaite,",
			"35": "<span>35</span> iratou na luvei Asemi tagane na Kisonaite, Jonacani na luve i Sake tagane na Araraite,",
			"36": "<span>36</span> O Ayame na luvei Sakari tagane na Araraite, Elifali na luve i Uri,",
			"37": "<span>37</span> Eferi na kai Mekera, Aija na Pelonaite,",
			"38": "<span>38</span> Esiro na Kamelaite, Nearai na luve i Esibai,",
			"39": "<span>39</span> Joeli na taci Necani, Miveri na luve i Akiri,",
			"40": "<span>40</span> Seleki na Amonaite, Nearai na Peroci (o koya sa daucolata nai yaragi i Joave na luve i Seruwaia tagane),",
			"41": "<span>41</span> Ira na Iciraite, Karevi na Iciraite,",
			"42": "<span>42</span> Uraia na Itaite, Savati na luve Alai tagane,",
			"43": "<span>43</span> Adina na luve i Sisa tagane na luve i Rupeni (e dua na turaga vei ira na kawa i Rupeni) ka lewe tolusagavulu era tu vata kei koya,",
			"44": "<span>44</span> Anani na luve i Maeka tagane, Josafati na Micinaite,",
			"45": "<span>45</span> Usaia na kai Asiteraca, Sama kei Jeieli rau na luve i Ocami tagane na Aroeraite,",
			"46": "<span>46</span> Jetiaeli na luve i Simiri tagane, kei Joa na tacina, na Tisaite,",
			"47": "<span>47</span> Elieli na Meavaite, Jeribai kei Josavaia rau na luve i Eliniama tagane, Icima na kai Moape,",
			"48": "<span>48</span> Elieli, Opeti, kei Jiasieli na Mesovaite."
		},
		"12": {
			"1": "<span>1</span> Veigauna 12",
			"2": "<span>2</span> Ia oqo o ira era a lako mai kivei Tevita ki Sikilaki ni se dro voli o koya mai vei Saula na luve Kisi tagane; era maliwai ira na tamata qaqa, era sa veivuke e nai valu,",
			"3": "<span>3</span> e nodra i yaragi na dakai titi, e kaukauwa ruarua na ligadra i matau kei nai mawi ena viritaki vatu kei na vanataki gasau ni dakai. O ira oqo mai vei Penijamini, era wekai Saula.",
			"4": "<span>4</span> Sa nodra i liuliu o Aisare, tarava o Joasi, o irau na luve i Semea na kai Kipea; o Jesieli kei Peleti e rau luve i Asimaveci; Biraka, kei Jeu na kai Anacoci;",
			"5": "<span>5</span> Isimaia na Kipionaite, e tamata qaqa vei ira na lewe tolusagavulu; ka uasivi cake vei ira na lewe tolusagavulu; Jeremaia, Jeasieli, Joanani,kei Josavati na Ketera;",
			"6": "<span>6</span> Elusai, Jerimoca, Pealaia, Semaraia, kei Sefataia na kai Arupi;",
			"7": "<span>7</span> Elikana, Jisaia, Asareli, Joesa, kei Jasobimi, na Koraite;",
			"8": "<span>8</span> kei Joela kei Sevataia e rau luve i Jeroama tagane na kai Kedori.",
			"9": "<span>9</span> Eso na luve i Kata era sa la'ki to vata kei Tevita mai na koro ni valu mai veikau, era tamata qaqa ni valu, era sa vulici tu mera vala, era kila na taura nai sasabai kei na moto, na matadra sa vaka na mata ni laione, ka ra sa totolo sara me vaka na me kila ni vei ulu-ni-vanua:",
			"10": "<span>10</span> Sa liu o Esera, nai karua o Opetaia, o Iliavi na kenai katolu,",
			"11": "<span>11</span> Misimana na kenai kava, Jeremaia nai kalima,",
			"12": "<span>12</span> Atai nai kaono, Elieli nai kavitu,",
			"13": "<span>13</span> Joanani nai kawalu, Elisavati na kenai kaciwa,",
			"14": "<span>14</span> Jeremaia nai ka tini, kei Makibanai na kenai ka tinikadua.",
			"15": "<span>15</span> O ira oqo mai vei ira na luve i Kata, era kavetani ni valu; nai wiliwili lailai duadua e dua na drau, kei nai wiliwili levu duadua e sivia na dua na udolu.",
			"16": "<span>16</span> Oqo o ira era a kosova na Joritani e nai matai ni vula, ni sa vuabale na uciwai; ka ra sa vakasavi ira kecega era tiko ena buca, vaka ki na tokalau ka vaka ki na ra.",
			"17": "<span>17</span> Era sa qai lako mai eso na luve i Penijamini tagane kivei Tevita ena koro ni valu.",
			"18": "<span>18</span> Ka sa la'ki tavaki ira o Tevita, ka vosa vei ira, ka vaka, \"Kevaka dou sa kauta mai ena yalo vinaka vei au mo dou mai vukei au, na yaloqu ena sema vata kei kemudou; ia kevaka dou sa via mai volitaki au vei ira na kequ meca, me vaka ni sa sega na cala ena ligaqu, me na qai raica ga ka lewa vei kemudou na nodra Kalou na tamada.\"",
			"19": "<span>19</span> Sa qai curumi Amisai na Yalo, o koya sa liu vei ira na kavetani, ka sa tukuna: \"Keimami sa nomuni, i Tevita; Keimami sa to vata kei kemuni, na luve i Jese tagane! Na Vakacegu, Na Vakacegu me nomuni, me ra vakacegu talega o ira sa vukei kemuni! Ni sa vukei kemuni na nomuni Kalou.\" Sa mani ciqomi ira o Tevita, ka lesi ira me ra kavetani ni vei mataivalu.",
			"20": "<span>20</span> Ka so vei ira na kawa i Manasa era sa la'ki to vata kei Tevita ni sa lako vata o koya kei ira na kai Filisitia me ra veivala kei Saula; ia era a sega ni vukei koya, ni ra sa vakatalai koya tani yani o ira sa turaga vei ira na kai Filisitia me vaka era sa duavata kina, ka vaka, \"Ena qai la'ki to vata tale kei na nona turaga o Saula ka na rawa ni tamusuka na uluda.\"",
			"21": "<span>21</span> Ni sa lako o koya ki Sikilaki, o iratou mai vei Manasa ka ratou a sa lako mai vua o Atina, Josavati, Jediaeli, Maikali, Josavati, Ilaiu, kei Siletai, e ratou kavetani ni vei udolu ka ratou a lako mai vei Manasa.",
			"22": "<span>22</span> Ka ra sa vukei Tevita me vala kei ira na mataivalu daubutako, ni ra sa tamata qaqa kecega ni valu, ka ra kavetani talega ni mataivalu.",
			"23": "<span>23</span> E na gauna o ya era sa dau lako mai vei Tevita e na veisiga kecega me ra vukei koya, me yacova ni sa dua na matai valu levu, me vaka na mataivalu ni Kalou.",
			"24": "<span>24</span> Ia oqo nai wiliwili ni veiwasewase kecega sa vakaiyaragitaki ki nai valu, ka ra sa lako mai vei Tevita ki Epironi me ra solia na matanitu i Saula kivei koya, me vaka na vosa i Jiova:",
			"25": "<span>25</span> mai vei ira na luve i Juta tagane era vakai sasabai kei na moto, era lewe ono na udolu ka walu na drau era vakaiyaragi ki nai valu;",
			"26": "<span>26</span> mai vei ira na luve i Simione tagane, na tamata qaqa era sa vakarau tu ki nai valu, e vitu na udolu dua na drau;",
			"27": "<span>27</span> mai vei ira na luve i Livai e va na udolu ono na drau;",
			"28": "<span>28</span> Jioiada, o koya sa liu vei ira na luve i Eroni tagane, e lewe tolu na udolu vitu na drau era tu vata kei koya;",
			"29": "<span>29</span> O Setoki, e dua na cauravou, e tamata qaqa ni valu, kei na ruasagavulu ka rua na kavetani mai na vale i tamana;",
			"30": "<span>30</span> mai vei ira na luve i Penijamini tagane, era wekai Saula, e tolu na udolu (me yacova mai na gauna o ya e dua nai wase levu era se dina tikoga ki na vuvale i Saula);",
			"31": "<span>31</span> mai vei ira na luve i Ifereimi tagane e lewe rusagavulu na udolu walu na drau, era tamata qaqa ni valu, era tamata rogo e na vuvale i tamadra;",
			"32": "<span>32</span> mai na veimama ni yavusa i Manasa e tinikawalu na udolu, era a kacivi e na yacadra me ra lako mai ka buli Tevita me tui;",
			"33": "<span>33</span> mai vei ira na luve i Isakari tagane ka sa matata vinaka tu vei ira na veigauna, mera kila na ka e dodonu me cakava o Isireli, e rua na drau na nodra turaga; ka ra lewa vei ira kecega na wekadra;",
			"34": "<span>34</span> mai vei Sevuloni era lewe limasagavulu na udolu ka ra lako ki nai valu, era kena dau ni valu kei nai yaragi kecega ni valu, era tamata yalodina ka dauvakarorogo;",
			"35": "<span>35</span> mai vei Navitalai e dua na udolu na kavetani, kei na tolusagavulu ka vitu na udolu era vakai sasabai kei na moto;",
			"36": "<span>36</span> mai vei Tani na tamata era rawa ni tuvai ira nai valu, e ruasagavulu na udolu ono na drau;",
			"37": "<span>37</span> mai vei Aseri, o ira era rawa ni lako i nai valu, ka e rawa ni ra tuvai ira e nai valu, e vasagavulu na udolu;",
			"38": "<span>38</span> mai vei ira na Rupeni kei na Kata kei na veimama ni yavusa i Manasa, mai na tai kadua ni Joritani, e dua na drau ruasagavulu era sa vakaiyaragi ni valu e na veimataqali yaragi kecega ni valu.",
			"39": "<span>39</span> O ira kecega na tamata ni valu oqo, ka ra rawa ni maroroya nai tutu, era lako mai ki Epironi ena yalo dina, me ra buli Tevita me tui Isireli; kei ira kecega na Isireli era sa yalovata me ra bulu Tevita me tui.",
			"40": "<span>40</span> Ka ra sa tu vata kei Tevita mai kea me tolu na siga, era kana ka gunu, ni ra sa vakarautaka vei ira o ira na wekadra.",
			"41": "<span>41</span> Ia oqo o ira era tu volekati ira, me tekivu mai na vanua yawa vakataki Isakari ki Sevuloni kei Navitalai, era sa kau kakana ni ra vakavodoka mai e na asa kei na kameli, e na meule kei na bulumakau â€“ na valawa kei na keke vuanikau kei na keke sulutana, na waini kei na waiwai kei na bulumakau kei na sipi e sa levu sara, ni sa yaco na reki e Isireli."
		},
		"13": {
			"1": "<span>1</span> Veigauna 13",
			"2": "<span>2</span> Sa qai bose o Tevita vata kei ira na kavetani ni lewe udolu kei na yadrau, vata kei ira talega nai liuliu kecega.",
			"3": "<span>3</span> Ia sa tukuna o Tevita ki vei ira nai soqosoqo kecega e Isireli, \"Kevaka sa vinaka vei kemuni, ia kevaka talega sa vu mai vei Jiova na noda Kalou, meda vakatadu mata vei ira na wekada kecega sa vo e na vanua taucoko o Isireli, kivei ira talega na bete kei na Livai era sa tiko ena nodra veikoro kei na vanua bulabula, mera na soqoni vata mai kike vei keda;",
			"4": "<span>4</span> \"ka da kauta lesu tale mai na kato ni noda Kalou vei keda, ni da se sega mada ni bau taroga tale me tekivu mai na gauna i Saula.\"",
			"5": "<span>5</span> Era sa qai tukuna nai soqosoqo taucoko ni ra na cakava vakakina, ni sa dodonu na ka o ya e na matadra na tamata kecega.",
			"6": "<span>6</span> Sa mani vakasoqoni ira kece mai na Isireli o Tevita, mai Siori e Ijipita me yaco vaka ki nai curucuru mai Emaci, me ra kauta lesu na kato ni Kalou mai na vanua o Kiriaci-Jearimi.",
			"7": "<span>7</span> Ka sa lako cake o Tevita kei ira kecega na Isireli ki Peala, ki Kiriaci-Jearimi, ka sa nei Juta, me ra kauta yani mai kea na kato ni Kalou o Jiova, o Koya sa tiko ena kedrau maliwa na Jerubimi, na vanua sa cavu kina na Yacana.",
			"8": "<span>8</span> Era sa mani kauta na kato ni Kalou ena dua na qiqi vou mai na vale i Avinatave, ia o Usa kei Aio e rau liuataka na qiqi.",
			"9": "<span>9</span> Sa qai qiria nai vakatagi o Tevita kei ira na Isireli ena mata ni Kalou ena nodra i gu taucoko, era lagasere, qiri api, nai vakatagi vakawa, na tibereli, na sibali, kei na davui.",
			"10": "<span>10</span> Ia ni ra sa yaco ki na vanua ni butu sila i Kidoni, sa dodoka na ligana o Usa me taura na Kato, ni sa tarabe na bulumakau.",
			"11": "<span>11</span> Sa qai waqa vakalevu na cudru ni Kalou vei Usa, sa mokuti koya ni sa dodoka na ligana me tara na kato; ka sa mate mai kea o koya ena mata ni Kalou.",
			"12": "<span>12</span> Ia sa rarawa sara o Tevita baleta ni sa vakamatei Usa o Jiova; sa mani vakatokai kina na vanua o ya o Veresi-Usa me yacova mai na siga oqo.",
			"13": "<span>13</span> Sa rerevaka na Kalou o Tevita e na siga o ya, ka tukuna, \"E rawa vakacava ni'u kauta mai na kato ni Kalou kivei au?\"",
			"14": "<span>14</span> Sa mani sega ni kauta vei koya o Tevita na kato ni Kalou ki na Koro i Tevita, ia sa kauta vakatikitiki ki na vale i Opeti-Itomi na Kitaite.",
			"15": "<span>15</span> Sa tiko ga na kato kei iratou na vuvale i Opeti- Itomi mai na loma ni nodratou vale me vula tolu. Ka sa vakalouvinakataka o Jiova na vale i Opeti- Itomi kei na ka kece e tu vua."
		},
		"14": {
			"1": "<span>1</span> Veigauna 14",
			"2": "<span>2</span> Ia oqo o Irami na tui Taira sa talai ira na dau-tukutuku kivei Tevita, kei na kau na sitari, kei ira na matai ni vatu kei na matai ni kau, mera tara vua e dua na vale.",
			"3": "<span>3</span> Io sa kila o Tevita ni o Jiova sa vakadeitaki koya me tui e Isireli, ka ni sa vakalevulevui cake na nona matanitu e na vukudra na Nona tamata na Isireli.",
			"4": "<span>4</span> Sa qai taura o Tevita eso tale na watina e Jerusalemi, ka sa so tale na luvena tagane kei na luvena yalewa o Tevita.",
			"5": "<span>5</span> Ia oqo na yacadra na luvena ka vakatubura mai Jerusalemi: Samua, Sobaba, Necani, Solomoni,",
			"6": "<span>6</span> Iveri, Ilaisua, Ilipeleti,",
			"7": "<span>7</span> Noka, Nefeki, Jafia,",
			"8": "<span>8</span> Ilisama, Bieliada, kei Iliveleti.",
			"9": "<span>9</span> Ia oqo ni ra sa rogoca na kai Filisitia ni sa lumuti o Tevita me tui e Isireli, era sa lako taucoko na kai Filisitia mera vakasaqarai Tevita. Ka sa rogoca o Tevita na ka o ya ka lako yani i tautuba me veivala kei ira.",
			"10": "<span>10</span> Era sa qai lako na kai Filisita ka la'ki kaba na buca o Refeimi.",
			"11": "<span>11</span> Sa taroga na Kalou o Tevita, ka vaka, \"Me'u lako cake li ka veivala kei ira na kai Filisitia? O Ni na soli ira beka ki na ligaqu?\" Sa tukuna o Jiova kivei koya, \"Lako cake, ni'u na soli ira ki na ligamu.\"",
			"12": "<span>12</span> Era sa mani lako ki Peali-Perasimi, ka sa vakadrukai ira o Tevita maikea. Sa qai tukuna o Tevita, \"Na Kalou sa curu basikati ira na kequ meca ena ligaqu me vaka ni sa kabasu nai vakaso ni wai.\" Sa mani vakayacani kina na vanua o ya oPeali-Perasimi.",
			"13": "<span>13</span> Ia ni sa biuta tu maikea na nodra kalou matakau, sa vakarota o Tevita me ra vakamai e na bukawaqa.",
			"14": "<span>14</span> Era sa baci kaba vakaivalu tale e na buca o ira na kai Filisitia.",
			"15": "<span>15</span> Sa taroga tale na Kalou o Tevita, ia sa tukuna vua na Kalou, \"Mo kakua ni lako cake vei ira; vakavolivoliti ira ga, ka sikiti ira mai na mata ni vei vuni malaberi.",
			"16": "<span>16</span> \"Ia ena yaco, ni dou sa rogoca na domo ni lako vakasotia ena tuvuka ni vei vuni malaberi oqori, dou qai lako ki nai valu, ni sa liu na Kalou vei kemudou me ravuta na keba ni kai Filisitia.\"",
			"17": "<span>17</span> Sa mani cakava o Tevita me vaka sa vakarota na Kalou vei koya, ka ra vakasavi ira lesu na mataivalu ni Filisitia mai Kipione me yaco sara ki Keseri.",
			"18": "<span>18</span> Sa qai kuruseta na vanua taucoko nai rogorogo kei Tevita, ka sa vakavuna o Jiova me ra rerevaki koya na veimatanitu kecega."
		},
		"15": {
			"1": "<span>1</span> Veigauna 15",
			"2": "<span>2</span> A sa tara o Tevita na veivale me nona ena Koro i Tevita; ka sa vakarautaka e dua na vanua me tiko kina na kato ni Kalou, ka vakaduria e dua na kena vale laca.",
			"3": "<span>3</span> Sa qai tukuna o Tevita, \"Me kakua ni dua me laveta na kato ni Kalou o ira ga na Livai, ni sa digitaki ira o Jiova me ra kauta na kato ni Kalou ka veiqaravi e matana me sega ni mudu.\"",
			"4": "<span>4</span> Ia o Tevita sa vakasoqoni ira vata mai na Isireli kecega ki Jerusalemi, mera kauta cake mai na kato ni Kalou ki na kena vale, o koya sa vakarautaka oti tu o koya.",
			"5": "<span>5</span> Sa qai vakasoqoni ira vata na luve i Eroni kei ira na Livai o Tevita:",
			"6": "<span>6</span> Mai vei ira luve i Koaca tagane, sa liu o Urieli, kei na lewe dua na drau ruasagavulu na wekana;",
			"7": "<span>7</span> Mai vei ira na luve i Merari tagane, sa liu o Asaia, kei na lewe rua na drau ruasagavulu na wekana;",
			"8": "<span>8</span> Mai vei ira na luve i Keresomi tagane, sa liu o Joeli, kei na lewe dua na drau tolusagavulu na wekana;",
			"9": "<span>9</span> Mai vei ira na luve i Ilisavani, sa liu o Semaia, kei na lewe rua na drau na wekana;",
			"10": "<span>10</span> Mai vei ira na luve i Epironi tagane, sa liu o Elieli, kei na lewe walusagavulu na wekana;",
			"11": "<span>11</span> Mai vei ira na luve i Usieli tagane, sa liu o Amenatave, kei na lewe dua na drau tinikarua na wekana.",
			"12": "<span>12</span> Ka sa kacivi rau o Setoki kei Aviaca na bete, vei ira talega na Livai: vei Urieli, Asaia, Joeli, Semaia, Elieli, kei Amenatave.",
			"13": "<span>13</span> Sa tukuna o koya kivei ira, \"Oi kemudou na ulu ni veivuvale i Livai na tamamudou; dou vakasavasavataki kemudou, kei ira talega na wekamudou, ka mo dou na kauta cake mai na kato i Jiova na Kalou i Isireli ki na vanua au sa vakarautaka oti tu.",
			"14": "<span>14</span> \"Me baleta ni dou a sega ni cakava oqo e liu, sa tawasei Koya tani kina mai vei keda o Jiova na noda Kalou, baleta ni da a sega ni tarogi Koya ena kenai tuvatuva dodonu.\"",
			"15": "<span>15</span> Ia oqo era sa vakasavasavataki ira o ira na bete kei ira na Livai me ra kauta cake na kato i Jiova na Kalou ni Isireli.",
			"16": "<span>16</span> Era sa colata na luve i Livai e domodra na kato ni Kalou, ena kenai vua, me vaka nai vakaro i Mosese e tukuna na vosa i Jiova.",
			"17": "<span>17</span> Sa qai tukuna o Tevita vei ira na nodra i liuliu na Livai me ra digitaka eso na wekadra mera dauni- sere ka ra salavata kei nai vakatagi, vakatagi vakawa, na api, sibali, e na nodra tabalaka na domodra ena reki.",
			"18": "<span>18</span> Ia o ira na Livai era sa qai digitaki Emani na luve i Joeli tagane; mai vei ira na wekana o Asafa na luve i Verekaia tagane; mai vei ira na wekana, o ira na luve i Merari, o Ecani na luve i Kusaia tagane;",
			"19": "<span>19</span> ka sa tiko vata kei ira o ira na wekadra e nai karua ni vakatagedegede: Sakaraia, Peni, Jeasieli, Semiramoce, Jeieli, Uni, Iliavi, Penaia, Measeia, Maticia, Elifele, Mikineia, Opeti-Itomi, kei Jeieli, o ira nai vakatawa ni matamata;",
			"20": "<span>20</span> na daunisere, o Emani, Asafa, kei Ecani, me ratou tukia na sibali varasa;",
			"21": "<span>21</span> Sakaraia, Asieli, Semiramoci, Jeieli, Uni, Iliavi, Measeia, kei Penaia, e nai vakatagi vakawa vakataki Alamoci;",
			"22": "<span>22</span> O Maticia, Elifele, Mikineia, Opeti-Itomi, Jeieli, kei Asasia, mera liuataka ena Api sa tiko ena Seminici;",
			"23": "<span>23</span> O Kenanaia, na nodra i liuliu na Livai, sa qasenivuli liu ni sere kei nai vakatagi, baleta ni matai dina;",
			"24": "<span>24</span> O Verekaia kei Elikana e rau i vakatawa ni katuba ni kato ni Kalou;",
			"25": "<span>25</span> O Sevanaia, Josafati, Necaneli, Amasai, Sakaraia, Penaia, kei Iliesa, o iratou na bete, e ratou na uvuca na biukila ena mata ni kato ni Kalou; ia o Opeti-Itomi kei Jeaia nai vakatawa ni katuba ni kato.",
			"26": "<span>26</span> Ia o Tevita, o ira na qase e Isireli, kei ira na kavetani ni lewe udolu era sa lako ka kauta mai na kato ni veiyalayalati i Jiova, mai na vale i Opeti- Itomi ena marau.",
			"27": "<span>27</span> Sa qai yaco ena gauna sa vukei ira kina na Livai o ira sa dau colata na kato ni veiyalayalati i Jiova, na Kalou, era sa qai vakacabora e vitu na bulumakau tagane kei na vitu na sipi tagane.",
			"28": "<span>28</span> O Tevita e sulumaka tu e dua nai ubi lineni matailalai, vakatalega kina o ira na Livai era colata tiko na kato, o ira na daunisere, kei Kenanaiti na qasenivuli ni sere vata kei ira na matasere. Sa vakaefoti lineni talega o Tevita.",
			"29": "<span>29</span> O koya oqo o ira na Isireli era sa kauta cake mai na kato ni veiyalayalati i Jiova e na kaikaila kei na domo ni davui, na biukila kei na sibali, ni ra qiria nai vakatagi vakawa kei na api.",
			"30": "<span>30</span> Ia sa yaco, ni sa kau mai na kato i Jiova ki na Koro i Tevita, sa iro yani mai na mata ni katuba leka o Maikali, na luve i Saula yalewa, ka raici Tevita ni sa meke katagi; ka sa beci koya sara e lomana."
		},
		"16": {
			"1": "<span>1</span> Veigauna 16",
			"2": "<span>2</span> Era sa kauta mai na kato ni Kalou, ka vakadabera e na loma donu ni vale ni soro sa birika o Tevita. Era sa qai cabora nai soro kama kei nai soro ni veivinakati e na mata ni Kalou.",
			"3": "<span>3</span> Ia ni sa oti na nona vakacabora nai soro kama kei nai soro ni veivinakati o Tevita, sa vosa vakalouvinakataki ira na tamata o koya e na yaca i Jiova.",
			"4": "<span>4</span> Sa qai wasea vei ira kecega na Isireli, na tagane kei na yalewa, vei ira yadua e dua nai buli madrai, dua na tiki ni lewe ni manumanu, kei na dua nai buli keke vua ni kau.",
			"5": "<span>5</span> Ka sa lesia eso na Livai me ra veiqaravi e na mata ni kato i Jiova, mera veivakananumi, vakavinavinaka, ka vakalavelaveti Jiova na Kalou nei Isireli:",
			"6": "<span>6</span> O Esava sa liu, e tarava o Sakaraia, qai o Jeieli, Semiramoci, Jeieli, Maticia, Iliavi, Penaia, kei Opeti-Itomi: O Jeieli e qiria nai vakatagi vakawa kei na Api, ia o Esava e vakatagitaka na sibali;",
			"7": "<span>7</span> O Penaia kei Jeasieli na bete e rau uvuca na biukila ena mata ni kato ni veiyalayalati ni Kalou. Na Sere Ni Vakavinavinaka I Tevita",
			"8": "<span>8</span> Ena siga o ya sa solia kina o Tevita nai matai ni same oqo vei Esava kei ira na wekana, mera vakavinavinakataki Jiova:",
			"9": "<span>9</span> O, me soli na vakavinavinaka vei Jiova! Masuta na Yacana; Vakatakila na Nona cakacaka ena kedra maliwa na Nona tamata!",
			"10": "<span>10</span> Sere Vua, seretaka na Same ki Vua; Tukuna na Nona cakacaka veivakurabuitaki kecega!",
			"11": "<span>11</span> Me vaqoroqoroi ena Yacana tabu; Mera cakava na yalo sa reki o ira sa vakasaqarai Jiova!",
			"12": "<span>12</span> Vakasaqarai Jiova kei na Nona kaukauwa; Vakasaqara tikoga na Matana!",
			"13": "<span>13</span> Vakananuma na Nona cakacaka taleitaki sa cakava oti, Na Nona cakacaka veivakurabuitaki, kei na vakatulewa ni Gusuna,",
			"14": "<span>14</span> Oi kemudou na kawa i Isireli na Nona tamata, oi kemudou na luve i Jekope, na Nona tamata digitaki!",
			"15": "<span>15</span> O Koya o Jiova na noda Kalou; Na Nona vakatulewa sa tu e vuravura taucoko.",
			"16": "<span>16</span> Vakananuma na Nona Veiyalayalati sega ni mudu, Na vosa sa vakarota o Koya, me baleta nai tabatamata e dua na udolu,",
			"17": "<span>17</span> Na veiyalayalati sa cakava vata kei Eparaama, Kei na Nona vosa bubului vei Aisake,",
			"18": "<span>18</span> Ka sa vakadeitaka vei Jekope mei lesilesi, Kivei Isireli me veiyalayalati e sega ni mudu,",
			"19": "<span>19</span> E tukuna, \"Au na solia vei iko na vanua o Kenani Me nodra i votavota na nomu kawa,\"",
			"20": "<span>20</span> Ni dou a se lewe vica walega, sa dina ni dou se lewe vica ga, ka tiko vulagi kina.",
			"21": "<span>21</span> Ni ra sa lako mai na dua na vanua ki na dua tale, Mai na dua talega na matanitu ki na dua na mata tamata,",
			"22": "<span>22</span> E sega ni vakatara o Koya vua e dua na tamata me cakava vei ira na ka ca; Io, e vunauci ira na tui e na vukudra,",
			"23": "<span>23</span> Ka vaka, \"Kakua ni tarai ira na Noqu i lumuti, Ka kakua ni vakamavoataki ira na Noqu parofita.\"",
			"24": "<span>24</span> Sere vei Jiova, na vuravura kecega; Dou tukuna nai tukutuku vinaka ni Nona veivakabulai mai na siga e dua ki na siga ka tarava.",
			"25": "<span>25</span> Vakatakila na Nona lagilagi e na kedra maliwa na veivanua, Na Nona veivakurabuitaki ena kedra maliwa na tamata kecega.",
			"26": "<span>26</span> Ni sa levu o Jiova ka sa dodonu me vakalevulevui na Nona levu; E dodonu talega o Koya me rerevaki vakalevu cake mai vei ira na kalou tani kecega.",
			"27": "<span>27</span> Ni ra sa matakau ceuceu walega na nodra kalou na tamata kecega, Ia o Jiova sa bulia na lomalagi.",
			"28": "<span>28</span> Na vakarokoroko kei na vakaturaga sa tu ga e matana; Na kaukauwa kei na marau sa tiko e na Nona i tikotiko.",
			"29": "<span>29</span> Solia vei Jiova, oi kemudou na vei vuvale ni tamata, Solia vei Jiova na lagilagi kei na kaukauwa.",
			"30": "<span>30</span> Dou solia vei Jiova na vakarokoroko e kilikili kei na Yacana; Kauta mai nai soro, ka lako mai ki na Nonai Serau. Io, dou lotu vei Jiova ena totoka ni yalosavasava!",
			"31": "<span>31</span> Dou sautaninini e Matana, na vuravura kecega. O vuravura talega sa vakadeitaki tu, E na sega ni yavalati rawa.",
			"32": "<span>32</span> Me reki o lomalagi, ka me marau o vuravura; Ka me ra tukuna ena kedra maliwa na veivanua, \"Me vakatulewa o Jiova.\"",
			"33": "<span>33</span> Me vadugu na wasawasa, kei na ka kecega e tu kina; Me reki na vei-were, kei na ka kecega sa bula kina.",
			"34": "<span>34</span> Era sa na qai reki na vei vunikau mai veikau ena mata i Jiova, Ni sa lako tiko mai o Koya me mai lewai vuravura.",
			"35": "<span>35</span> Io, dou vakavinavinaka vei Jiova, ni sa vinaka o Koya! Ni sa sega ni mudu na Nona yalololoma.",
			"36": "<span>36</span> Ka tukuna, \"Mo ni vakabulai keimami, na Kalou ni neimami vakabulai; Ni vakasoqoni keimami vata, ka vakabulai keimami mai vei ira na kai matanitu tani, Me keimami vakavinavinakataka na Yacamuni savasava, Me keimami qaqa ena Nomuni vakavinavinakataki.\"",
			"37": "<span>37</span> Me lagilagi na Yacai Jiova na Kalou ni Isireli Mai na sga ni mudu ki na sega ni mudu! Ka ra sa tukuna na tamata kecega, \"Emeni!\" ka vakavinavinakataki Jiova. Sa Vakayacori Tikoga Na Lotu Ena Veigauna",
			"38": "<span>38</span> Sa mani laivi Esava tiko kei iratou na tacina e kea ena mata ni kato ni veiyalayalati i Jiova me ratou veiqaravi voli ena mata ni kato e na veigauna kecega, me vaka na cakacaka ni veisiga e gadrevi;",
			"39": "<span>39</span> ia o Opeti-Itomi vata kei iratou na wekana e lewe onosagavulu ka walu, e wili kina o Opeti- Itomi na luve i Jeducuni tagane, kei Osa, me ratou i vakatawa ni matamata;",
			"40": "<span>40</span> ia o Setoki na bete kei ira na veitacini vaka matabete, me ra tiko ena mata ni vale ni soroi Jiova ena veivanua cecere e tiko mai Kipioni,",
			"41": "<span>41</span> mera vakacabora nai soro kama vei Jiova e nai cabocabo ni soro kama ena veimataka kei na veiyakavi, ka me ra cakava me vaka na ka kecega sa volai e nai vunau i Jiova ka a vakarota vei Isireli;",
			"42": "<span>42</span> Sa tiko vata kei ira o Emani kei Jeducuni kei ira na kena vo era sa digitaki, era sa kacivi ena dui yacadra, mera vakavinavinakataki Jiova, ni sa sega ni mudu na Nona yalo loloma;",
			"43": "<span>43</span> ia sa tu vata kei ira o Emani kei Jeducuni, me rau uvuca na biukila ka qiria na sibali kei nai yaya kece ni vakatagi vua na Kalou. Ia oqo e ratou sai vakatawa ni matamata na luve i Jeducuni.",
			"44": "<span>44</span> Era sa qai dui lako na tamata kecega, o ira yadua ki na nodra dui vale; ka sa lesu talega o Tevita ki nodratou me la'ki vosa vakalouvinakataka."
		},
		"17": {
			"1": "<span>1</span> Veigauna 17",
			"2": "<span>2</span> Ia ni sa oti o ya, ni sa tiko o Tevita e na nona vale, sa tukuna o Tevita vei Necani na parofita, \"Raica oqo, au sa tiko ena vale sitari, ia na kato ni veiyalayalati i Jiova sa tiko ena loma ni vale laca.\"",
			"3": "<span>3</span> Sa qai tukuna o Necani vei Tevita, \"Cakava na ka kecega sa tiko e yalomu, ni sa tiko vata kei iko na Kalou.\"",
			"4": "<span>4</span> Ia sa yaco ena bogi ga o ya ni sa rogo mai na vosa ni Kalou vei Necani, ka vaka,",
			"5": "<span>5</span> \"Lako ka la'ki tukuna vei Tevita na noqu tamata, 'Sa vakaoqo na ka sa tukuna o Jiova: \"O na sega ni tara vei au e dua na vale Me'u tiko kina.",
			"6": "<span>6</span> \"Ni'u a sega tale ni bau tiko e na dua na vale me tekivu mai na gauna Au vakatuburi Isireli cake kina, me yacova mai na siga oqo, ia Au a lako voli ga mai na vale laca ki na vale laca, mai na dua na valenisoro ki na dua tale.",
			"7": "<span>7</span> \"Na veivanua kece Au lakova vata kei ira na Isireli, Au bau cavuta li e dua na vosa vua e dua na turaganilewa e Isireli, o ira Au sa lesia me ra vakatawai ira na Noqu tamata, ka tukuna, 'Ena vuku ni cava dou sa sega ni tara kina vei Au e dua na vale sa caka e na sitari?\"\"",
			"8": "<span>8</span> \"Ia oqo, mo na tukuna vua na Noqu tamata o Tevita, 'Sa tukuna vakaoqo o Jiova ni lewe vuqa: \"Au a kauti iko tani mai na qele ni sipi, mai na nomu vakamuri ira voli na sipi, mo mai tui vei ira na Noqu tamata na Isireli.",
			"9": "<span>9</span> \"Ka'u sa tu vata kei iko ena veivanua kecega o lakova, ka tamusuki ira kecega na kemu meca mai matamu, ka vakayacani iko ena dua na yaca e tautauvata kei na yacadra na tamata qaqa era sa tiko e vuravura.",
			"10": "<span>10</span> \"E dina Au na lesia e dua na vanua vei ira na Noqu tamata na Isireli, Au na tei ira, ka me ra na tiko vakadua e na dua na vanua e nodra ka sega tale ni veitokiyaki; se me ra mai vakararawataki ira tale o ira na luve ni valavala ca, me vaka e liu,",
			"11": "<span>11</span> \"Me tekivu mai na gauna Au a vakaroti ira kina na daunilewa mera liutaki ira na Noqu tamata na Isireli. Au na vakamalumalumutaki ira talega na kemudou meca. Ia Au sa tukuna talega vei iko ni na tara vei iko o Jiova e dua na vale.",
			"12": "<span>12</span> \"Ia ena yaco, ni sa oti na nomu veisiga, ni o sa na lako vakaidina vei ira na na tamamu, Ni'u na vakatubura na nomu kawa me kemui sosomi, ena dua ga o koya vei ira na luvemu; ia Au na vakadeitaka na nona matanitu.",
			"13": "<span>13</span> \"Ena tara vei Au o koya e dua na Noqu vale, ia Au na vakadeitaka na nonai tikotiko vakaturaga me sega ni mudu.",
			"14": "<span>14</span> \"Au na Tamana, ka na Luvequ o koya; ia na Noqu yalololoma Au na sega ni kauta tani mai vei koya, me vaka Au a kauta tani mai vei koya sa liu vei iko.",
			"15": "<span>15</span> \"Ia Au na vakadeitaki koya ena Noqu vale kei na Noqu matanitu me sega ni mudu; kei na nonai tikotiko vakaturaga me sega ni mudu.\"'\"",
			"16": "<span>16</span> Me vaka na vosa kecega oqo ka me vaka na raivotu kecega oqo, sa vosataka o Necani vei Tevita.",
			"17": "<span>17</span> Sa qai curu yani ki loma o Tevita ka la'ki dabe sobu ena matai Jiova; ka tukuna o koya: \"O cei o au i Jiova na Kalou? Se cava mada na noqu vuvale, Mo Ni kauti au mai kina me yacova mai oqo?",
			"18": "<span>18</span> \"Ia sa dina ni sa ka lailai ga oqo ena Matamuni, noqu Kalou: ka o ni sa vosa oti tale tikoga me baleta na nona vuvale na Nomuni tamata me yacova na veigauna maimuri, ka sa raici au me vaka e dua vei ira na turaga dokai, I Jiova na Kalou.",
			"19": "<span>19</span> \"Na cava tale me tukuna vei kemuni o Tevita me baleta na Nomuni dokai koya na Nomuni tamata? Ni o ni sa kilai koya na Nomuni tamata.",
			"20": "<span>20</span> \"I Jiova, ena vukuna na Nomuni tamata, ka me vaka talega na Lomamuni, o ni sa cakava na ka cecere oqo, ni o ni sa vakatakila na veika cecere oqo.",
			"21": "<span>21</span> \"I Jiova, e sega tale ni dua e tautauvata kei kemuni, se dua tale me tu e Yasamuni, me vaka na ka kecega keimami sa dau rogoca ena daligai keimami.",
			"22": "<span>22</span> \"Ia o cei e tautauvata kei ira na Nomuni tamata na Isireli, na matanitu duadua ga e vuravura ka a lako mai na Kalou me mai volia lesu me ra Nona tamata â€“ me cakava vei Kemuni e dua na yaca rogo ena vuku ni cakacaka cecere ka veivakurabuitaki, ena nodra vakasavi tani na veimatanitu e matadra na Nomuni tamata o ni a voli ira lesu tale mai Ijipita?",
			"23": "<span>23</span> \"Ni o ni sa vakayacori ira na Nomuni tamata na Isireli mera Nomuni tamata sega ni mudu; ia oi Kemuni Jiova, sa yaco me nodra Kalou.",
			"24": "<span>24</span> \"Ia oqo, i Jiova, na vosa o ni sa cavuta me baleti koya na Nomuni tamata ka baleta na nona vuvale, me vakadeitaki ka sega ni mudu, ka Mo ni vakayacora me vaka na ka o ni a tukuna.",
			"25": "<span>25</span> \"Ia me vakadeitaki mada, me sa vakacaucautaki na Yacamuni ka sega ni mudu, ka vaka, 'O Jiova ni lewe vuqa, na Kalou nei Isireli, sa Kalou ni Isireli.' Ka vakayacora na vuvale i Tevita me tudei e Matamuni.",
			"26": "<span>26</span> \"Ni Oi Kemuni, Na noqu Kalou, sa vakatakila vua na Nomuni tamata ni o ni na tara e dua na vale me nona. O koya oqo sa kunea kina e lomana na Nomuni tamata me masu vei Kemuni.",
			"27": "<span>27</span> \"Ia oqo, Jiova, o ni sa noqu Kalou, ka o ni sa yalataka na veivakalouvinakataki oqo vua na Nomuni tamata.",
			"28": "<span>28</span> O ni sa marautaka e daidai Mo ni vakalouvinakataka na vuvale ni Nomuni tamata, ka me tu ga e Matamuni me sega ni mudu; ni o ni sa vakalouvinakataka, i Jiova, ka na vakalouvinakataki me sega ni mudu.\""
		},
		"18": {
			"1": "<span>1</span> Veigauna 18",
			"2": "<span>2</span> Ni sa oti oqo sa yaco ni sa valuti ira tale na kai Filisitia o Tevita, vakamalumalumutaki ira, ka tauri Kaca kei na kena veikoro mai na ligadra na kai Filisitia.",
			"3": "<span>3</span> Sa qai vakadrukai Moape, ka ra sa yaco na kai Moape me tamata cakacaka i Tevita, ka kauta mai nai vakacavacava.",
			"4": "<span>4</span> Ka sa vakadrukai Adadesa na tui So me yacovi Emaci, ni sa lako me la'ki vakadeitaka na nona kaukauwa mai na bati ni Uciwai na Uferetisi.",
			"5": "<span>5</span> Sa taura mai vua o Tevita e dua na udolu na qiqi ni valu, vitu na udolu na dauvodo ose, kei na ruasagavulu na udolu na sotia taubale. Sa sereki ira talega na ose o Tevita, vakavo ga o ira sa maroroya o koya me baleta na qiqi ni valu e dua na udolu.",
			"6": "<span>6</span> Ni ra sa lako mai na kai Siria mai Tamasiko mera mai vukei Adadesa na tui Soba, o Tevita sa vakamatei ira e lewe ruasagavulu na udolu na kai Siria.",
			"7": "<span>7</span> Sa qai biuta mai Siria o Tevita eso na mataivalu vakatawa; ka ra sa yaco me tamata i Tevita na kai Siria ka soli vakacavacava ki vua. Ia oqo sa maroroi Tevita o Jiova e na vanua kecega e lakova.",
			"8": "<span>8</span> Ia o Tevita sa taura nai sasabai koula e tu vua na tamata i Adadesa, ka kauta mai i Jerusalemi.",
			"9": "<span>9</span> Mai Tibaca talega kei Kuni, na veikoro i Adadesa, sa kauta mai kea o Tevita e dua nai wiliwili varasa levu, o koya e a cakava kina o Solomoni na taqe varasa levu, na veiduru, kei na veiyaya varasa.",
			"10": "<span>10</span> Ia oqo ni sa rogoca o Tou na tui Emaci ni sa vakadruka na mataivalu kecega i Adadesa na tui Soba,",
			"11": "<span>11</span> Sa talai Adorama na luvena vei Tevita na tui, me kidavaki koya ka vosa vakalouvinakataki koya, baleta ni sa veivala kei Adadesa ka sa vakadrukai koya (ni o Adadesa e a dauvala voli kei Tou); ka sa kauta o Adorama na veimataqali yaya koula kecega, na siliva kei na varasa.",
			"12": "<span>12</span> Na tui o Tevita sa vakacabora kece talega oqori vei Jiova, vakakina na siliva kei na koula e a kauta mai na veivanua oqo O mai Itomi, mai Moape, mai vei ira na tamata i Amoni, mai vei ira na kai Filisitia, kei ira talega na Ameleki.",
			"13": "<span>13</span> E dina o Avisai na luve i Seruwaia e a vakamatea e tinikawalu na udolu na kai Itomi mai na Buca ni Masima.",
			"14": "<span>14</span> Sa biuta talega o koya eso na mataivalu vakatawa mai Itomi, ka sa yaco mera tamata cakacaka kecega i Tevita o ira na kai Itomi. Ia o Jiova sa maroroi Tevita ena vanua kecega e lakova.",
			"15": "<span>15</span> E dina sa tui tiko o Tevita e Isireli, ia sa tuvana vinaka na nona lewa ka vakatauca na lewa dodonu vei ira kecega na nona tamata.",
			"16": "<span>16</span> O Joave na luve i Seruwaia tagane sa lewa na mataivalu taucoko; o Josevata luve i Ailude tagane sa dauvola nai tukutuku;",
			"17": "<span>17</span> e rau sa bete o Setoki na luve i Aitube tagane kei Avimeleki na luve i Avaiata tagane; o Savisa na vunivola;",
			"18": "<span>18</span> O Penaia na luve i Jioiada tagane sa lewai ira na Kereci kei na Peleci; ia o iratou na luve i Tevita tagane e ratou sa ena veiqaravi liu e voleka vua na tui."
		},
		"19": {
			"1": "<span>1</span> Veigauna 19",
			"2": "<span>2</span> Sa yaco ni sa oti oqo sa mate o Neasi na nodra tui na kai Amoni, ka tui me kenai sosomi na luvena tagane.",
			"3": "<span>3</span> Sa qai tukuna o Tevita, \"Au na vakaraitaka na yalololoma vei Anani na luve i Neasi tagane, baleta ni a vakaraitaka o tamana kivei au na yalololoma.\" Sa mani tala eso na nona mata o Tevita mera la'ki vakacegui koya ena vukui tamana. Ia era sa lako vei Anani na tamata i Tevita me vakacegui koya.",
			"4": "<span>4</span> Ia era sa tarogi Anani o ira era turaga vei ira na nona tamata, \"O sa nanuma li ni dokai tamamuni dina o Tevita ni sa talai ira mai na nona tamata mera mai vakacegui iko? Era sega beka ni lako mai na nona tamata mera mai rai vanua ka qara se me vuaviritaka vakacava na noda vanua?\"",
			"5": "<span>5</span> O koya oqo sa mani tauri ira na tamata i Tevita o Anani, sa tasia, ka sedrutia na nodra i sulu e lomadonu, kei na mudratou, ka vakatalai ratou tani yani.",
			"6": "<span>6</span> Era sa qai lako eso vei Tevita ka tukuna na veika sa caka vei iratou na tamata; ka sa tala o koya eso mera tavaki iratou, ni ratou sa madua vakalevu sara. Ka sa tukuna na tui, \"Dou wawa tiko mai Jeriko me yacova ni sa tubu tale na kumimudou, dou qai lesu mai.\"",
			"7": "<span>7</span> Ni ra sa raica na kai Amoni ni ra sa velavela e na matai Tevita, sa vakauta o Anani kei ira na kai Amoni e dua na udolu na taledi na siliva me voli kina eso na qiqi ni valu kei na so na dauvodo ose mai Mesopotemia, mai Meaka e Siria, kei Soba.",
			"8": "<span>8</span> Ia era sa voli rawa e tolusagavulu ka rua na qiqi-ni-valu, vata kei tui Meaka kei ira na nona tamata, era sa ma tuvai valu mai Medeba. O ira talega na kai Amoni era sa soqoni vata mai na nodra veikoro, ka lako mai ki nai valu.",
			"9": "<span>9</span> Ia oqo ni sa rogoca o Tevita, sa talai Joave kei ira kecega na mataivalu qaqa.",
			"10": "<span>10</span> Era sa qai lako mai na kai Amoni ka tuvai valu ena mata ni matamata kina koro, ka ra mai tu vakai ira e na rara ni valu o ira na tui era a lako vata mai.",
			"11": "<span>11</span> Ni sa raica o Joave ni tuva nai valu e matana ka tu talega mai dakuna, sa digitaka o koya eso na tamata e uasivi cake vei ira kecega na Isireli, ka biuti ira mera veivala kei ira na kai Siria.",
			"12": "<span>12</span> Ia o ira kecega na kena vo sa solia o koya vei Avisai na tacina me liutaka, ka ra sa tuvai valu vei ira na kai Amoni.",
			"13": "<span>13</span> Sa qai tukuna o koya, \"Kevaka era sa gumatua vei au na kai Siria, mo na qai vukei au; ia kevaka era sa gumatua vei iko na kai Amoni, au na qai vukei iko.",
			"14": "<span>14</span> \"Mo yaloqaqa, ka me daru kaukauwa ena vukudra na nodaru tamata kei na veikoro ni noda Kalou. Ka me na cakava o Jiova na ka sa dodonu e Matana.\"",
			"15": "<span>15</span> Ia sa toro voleka vei ira na kai Siria o Joave kei ira na tamata era tikovata tukuna me veivala, ka ra sa dro e matana o ira na kai Siria.",
			"16": "<span>16</span> Ni ra sa raica o ira na kai Amoni ni sa dro o ira na kai Siria, era sa dro talega ena matai Avisai na tacina, ka curu ki na koro. Sa mani lako i Jerusalemi o Joave.",
			"17": "<span>17</span> Ia oqo ni ra sa raica na kai Siria ni ra sa druka vei Isireli, era sa talai ira na dau ni tukutuku mera kau mai na kai Siria era tu mai na tai kadua ni Uciwai, kei Sovaki na turaganivalu liu i Adadesa me lako mai ki matadra.",
			"18": "<span>18</span> Ni sa mai tukuni vei Tevita, sa vakasoqoni ira vata na Isireli, era takosova na Joritani ka valuti ira, ka tuvai valu vei ira. Ia ni sa tuvai valu o Tevita vei ira na kai Siria, era sa qai veivala kei koya.",
			"19": "<span>19</span> Era sa qai dro na kai Siria e matadra na Isireli; ka sa vakamatea o Tevita e vitu na udolu na dauvodo qiqi-ni-valu kei na vasagavulu na udolu na sotia taubale, ka vakamatei Sovaki talega na turaganivalu liu.",
			"20": "<span>20</span> Ia ni ra sa raica na dauveiqaravi i Adadesa ni ra druka vei Isireli, era sa vakaveivinakatitaki ira kei Tevita ka yaco mera nona dauveiqaravi. Era sa mani sega ni via vukei ira tale vakadua na kai Amoni o ira na kai Siria."
		},
		"20": {
			"1": "<span>1</span> Veigauna 20",
			"2": "<span>2</span> Sa yaco ena gauna ni vula i tubutubu, ena gauna era dau lako kina na tui ki nai valu, sa liutaka na mataivalu o Joave ka ra vakacacana na nodra vanua na kai Amoni, ia era lako mai ka kabai Rava ka vaqeavutaka. Ia sa tiko ga mai Jerusalemi o Tevita. Ka sa vakadrukai Rava o Joave ka vaqeavutaka. 2 O Tevita e sa qai luvata nai sala vakatui (nei tui Rava) mai uluna, ka kunea ni kena bibi e dua na taledi koula (vitusagavulu-kalima na paudi koula), ka sa tu talega kina na vatu talei. Ia sa mani vakaisala kina o Tevita. Sa ka levu talega na ka sa kauta mai na koro me i toki ni valu. 3 Ka sa kauta tani mai o koya na tamata era tiko kina, ka lesi ira mera dauvarovaro, mera dauqivaqiva e nai qiva kaukamea, ka ra dau tata e nai matau. Oqori na ka kecega sa cakava o Tevita kina nodra veikoro na kai Amoni. O Tevita kei ira na tamata kecega era sa qai lesu tale ki Jerusalemi. Era Sa Vakamatei Na Tuwawa Ni Filisitia 4 Oqo sa yaco ni sa oti o ya sa kacabote nai valu kei ira na kai Filisitia mai Kesa, tuwawa tagane. Ka ra sa vakamalumalumutaki. 5 Tubu tale nai valu kei ira na kai Filisitia, sa vakamatei Lamai na taci Kolaiaci na Itaiti o Elianani na luve i Jaire tagane, ia na dia ni nona moto sa vaka na nodra kukamea na dautalitali. 6 Sa baci tubu tale nai valu mai Kaca, sa tiko kina e dua na tamata levu, e ruasagavulu kava nai qaqalo ni ligana kei na yavana, e tauya ono taucoko nai qaqalo ni ligana kei na yavana; sa sucu talega o koya ka luvena na tuwawa. 7 Ni sa bolei ira tiko na Isireli, o Jonacani na luve i Simea tagane, na tuakai Tevita, sa vakamatei koya. 8 O iratou oqo na luve i koya na tuwawa mai Kaca, ka ra a vakamatei ena ligai Tevita kei ira na nona tamata."
		},
		"21": {
			"1": "<span>1</span> Veigauna 21",
			"2": "<span>2</span> Oqo sa tucake o Setani me coqai Isireli, ka vakauqeti Tevita me wiliki Isireli.",
			"3": "<span>3</span> Sa mani tukuna o Tevita vei Joave kei ira sa liu vei ira na tamata, \"Dou lako ka wiliki ira na Isireli me tekivu mai Pierisepa me yaco ki Tani, ka kauta mai na kedra i wiliwili vei au me'u kila.\"",
			"4": "<span>4</span> Sa sauma o Joave, \"Me na vakayacori ira na Nona tamata o Jiova me vaka dua na drau taki na kedra i wiliwili. Ia, noqu turaga na tui, era sega li ni sa nomuni tamata kecega na noqu turaga? Ena vuku ni cava sa gadreva kina na noqu turaga na ka oqo? Na cava me vu kina o koya ni nodra madua na Isireli?\"",
			"5": "<span>5</span> Sa gumatua ga na vosa ni tui ki vei Joave. Sa mani tucake o Joave ka lakova na vanua taucoko o Isireli ka lesu tale mai i Jerusalemi.",
			"6": "<span>6</span> Sa qai mai solia o Joave vei Tevita nai wiliwili taucoko ni tamata. E Isireli taucoko e ra tiko kina e dua na milioni dua na drau na udolu na tamata era vakai-seleiwau, ia e Juta era tiko kina e va na drau vitusagavulu na udolu na tamata era vakaiseleiwau.",
			"7": "<span>7</span> E sega ni wiliki ira kina na Livai kei ira na Penijamini, ni sa velavela vei Joave na ka sa tukuna na tui.",
			"8": "<span>8</span> Sa sega ni marautaka na Kalou na ka oqo; ka sa mani ravuti Isireli.",
			"9": "<span>9</span> Sa tukuna o Tevita vua na Kalou, \"Au sai valavala ca vakalevu, baleta ni'u sa cakava na ka oqo; ia oqo, au sa masu, Mo ni kauta laivi na nonai valavala ca na Nomuni tamata, ni'u sai valavala vakalialia sara.\"",
			"10": "<span>10</span> Qai tukuna o Jiova vei Kata, na parofita i Tevita, ka vaka,",
			"11": "<span>11</span> \"Lako ka la'ki tukuna vei Tevita, ka vaka, 'Sa tukuna vakaoqo o Jiova: \"Au sa solia oqo vei iko e tolu na ka; digitaka ga e dua vei iratou, Me'u na vakayacora vei iko.\"'\"",
			"12": "<span>12</span> Sa lako o Kata ki vei Tevita ka tukuna vua, \"Sa tukuna vakaoqo o Jiova: 'Digitaka e dua vei iratou,",
			"13": "<span>13</span> 'E tolu na yabaki ni dausiga, se tolu na vula mera na vakadrukai iko tiko kina na kemu meca ena nodra i seleiwau ni ra sa toboki iko, se tolu na siga me yaviti iko voli nai seleiwau i Jiova â€“ na mate ca ena nomu vanua, ia na agilosi i Jiova me na vakacaca ena vanua taucoko o Isireli.' Ia oqo mo ni tukuna nai sau ni taro me'u kauta lesu tale vei koya sa talai au mai.\"",
			"14": "<span>14</span> Sa tukuna vei Kata o Tevita, \"Au sa rarawa vakalevu. Yalo vinaka mo laiva me'u lutu ki na liga i Jiova, ni sa ka levu na Nona yalololoma; ia me'u kakua ga ni lutu ki na liga ni tamata.\"",
			"15": "<span>15</span> Sa mani tauca o Jiova na mate ca e Isireli, ka sa bale kina e vitusagavulu na udolu tagane e Isireli.",
			"16": "<span>16</span> Ka sa tala e dua na agilosi o Jiova ki Jerusalemi me vakarusa. Ni sa vakarusa tiko, sa raica o Jiova ka veivutunitaka na vakacaca, ka sa tukuna vua na agilosi sa veivakarusai tiko, \"Sa rauta oqori; ia oqo mo sa tarova na ligamu.\" Ia na agilosi i Jiova sa la'ki tucake tu ena yasa ni vanua ni buturaki sila nei Orinani na Jevusaite.",
			"17": "<span>17</span> Sa qai mata cake o Tevita ka raica na agilosi i Jiova ni sa tucake tu ena maliwa ni vuravura kei na lomalagi, sa taura tu e ligana e dua nai seleiwau ka vagolea tu vaka ki Jerusalemi. Ia o Tevita kei ira na qase, era sa vakaisulu taga, ka ra sa cuva vakatoboicu.",
			"18": "<span>18</span> Ka sa tukuna o Tevita vua na Kalou, \"E sega li ni oi au a vakarota mera wiliki mai na tamata? Au sa i valavala ca dina ka sa cakava na ka ca oqo; ia o ira na sipi oqo, na cava era a cakava? Laiva na Ligamuni, au masu, i Jiova na noqu Kalou, me tarai au kei na vuvale i tamaqu, ka me kakua ga ni tarai ira na Nomuni tamata oqo me yacovi ira kina na mate ca.\"",
			"19": "<span>19</span> O koya oqo, sa vakaroti Kata kina na agilosi i Jiova me tukuna vei Tevita me la'ki cakava e dua nai cabocabo ni soro vei Jiova, ena vanua ni buturaki sila i Orinani na Jevusaiti.",
			"20": "<span>20</span> Sa mani lako cake o Tevita me vaka na vosa i Kata, sa cavuta o koya ena yaca i Jiova.",
			"21": "<span>21</span> Ia oqo sa vuki mai o Orinani ka raica na agilosi; ka ratou sa vunitaki iratou na luvena tagane e va e ratou a tiko vata tukuna, ia o Orinani e tomana tikoga na buturaki sila.",
			"22": "<span>22</span> Ia sa lako mai o Tevita kivei Orinani, ka sa rai cake o Orinani ka raici Tevita. Sa lako tani mai na vanua ni buturaki sila, ka sa cuva vakatoboicu ki na qele e matai Tevita.",
			"23": "<span>23</span> Sa qai tukuna o Tevita vei Orinani, \"Mo solia vei au na vanua ni buturaki sila oqo, me'u cakava kina e dua nai cabocabo ni soro vei Jiova.Mo na solia vei au me'u sauma na kenai voli dodonu, de na mani muduki na mate ca vei ira na tamata.\"",
			"24": "<span>24</span> Ia sa tukuna o Orinani vei Tevita, \"Taura me nomu, ka laiva na noqu turaga na tui me cakava na ka sa vinaka e matana. Raica, au sa solia talega vei kemuni na bulumakau mei soro kama, nai yaya ni buturaki sila me kena buka, kei na sila mei madrali laukana, au sa solia taucoko.\"",
			"25": "<span>25</span> Sa qai tukuna o Tevita vei Orinani, \"E sega, ia au na volia ena kenai sau dodonu, ni'u na sega ni kauta na ka sa nomu vua na Kalou, se me'u vakacabora nai soro e sega ni'u volia.\"",
			"26": "<span>26</span> Sa mani solia o Tevita vei Orinani e ono na drau na sikele na kena bibi ni koula me baleta na vanua o ya.",
			"27": "<span>27</span> Ka sa cakava mai kea o Tevita e dua nai cabocabo ni soro vei Jiova, ka sa vakacabora nai soro kama kei nai soro ni veivinakati, ka masuti Jiova; ka sa vosa o Koya vua ena bukawaqa mai lomalagi e nai cabocabo ni soro.",
			"28": "<span>28</span> Sa mani vakarota o Jiova na agilosi, ka sa vakasuka na nonai seleiwau ki na kena taga.",
			"29": "<span>29</span> Ena gauna o ya, ni sa raica o Tevita ni sa sauma o Jiova na nona masu mai na vanua ni buturaki sila i Orinani na Jevusaiti, sa vakacaboi soro mai kea o koya.",
			"30": "<span>30</span> Ni sa tiko ena gauna o ya na vale i Jiova kei nai cabocabo ni soro kama, ka a cakava o Mosese mai na veikau, mai na vanua cecere mai Kipioni.",
			"31": "<span>31</span> Ia sa sega ni lako volekata rawa o Tevita me taroga na Kalou,ni sa rerevaka nai seleiwau ni agilosi i Jiova."
		},
		"22": {
			"1": "<span>1</span> Veigauna 22",
			"2": "<span>2</span> Sa qai tukuna o Tevita, \"Oqo na vale i Jiova na Kalou, ia sai koya oqo nai cabocabo ni soro kama me baleti Isireli.\"",
			"3": "<span>3</span> Sa mani vakarota o Tevita mera kau kece mai na vulagi era tiko e na vanua o Isireli; ka sa lesia o koya eso na matai ni vatu me ra taya mai na vatu me tara kina na vale ni Kalou.",
			"4": "<span>4</span> Ka sa vakarautaka o Tevita e levu sara na kaukamea mei vako ni katuba ni matamata kei nai sema, ka levu sara na varasa e sega ni vakarautaki rawa,",
			"5": "<span>5</span> ka levu talega na vu-ni-sitari; ni ra sa kauta mai vakalevu na sitari vei Tevita o ira na kai Saitoni kei na kai Taira.",
			"6": "<span>6</span> Ia sa tukuna vakaoqo o Tevita, \"I Solomoni na luvequ tagane e se gone ga ka sega ni kila e levu na ka, kei na vale ena tara me nei Jiova e dodonu me vakaitamera, kilai ka vakaiukuuku ena veivanua kecega. Au sa na vakarautaka rawa oqo na ka kecega e baleta na vale.\" Sa mani vakarautaka vakalevu na ka kecega o Tevita ni bera na nona mate.",
			"7": "<span>7</span> Sa qai kacivi Solomoni na luvena tagane, ka vakaroti koya me tara e dua na vale i Jiova na Kalou i Isireli.",
			"8": "<span>8</span> Ka sa tukuna o Tevita vei Solomoni: \"Na luvequ, io vei au, a sa tu e lomaqu me'u tara e dua na vale ki na Yaca i Jiova na noqu Kalou;",
			"9": "<span>9</span> \"Ia sa yaco mai vei au na vosa i Jiova, ka vaka, 'Sa rui levu na dra o vakadavea ka sa levu talega na nomui valu; o na sega ni tara e dua na vale ki na Yacaqu, baleta ni sa rui levu na dra e vuravura o vakadavea e Mataqu.",
			"10": "<span>10</span> 'Raica, ena sucu mai e dua na luvemu tagane, o koya ena tamata ni sautu; ia Au na solia vua na sautu mai vei ira na kena meca kecega era tu wavolita. Ena vakatokai me yacana o Solomoni, ni'u na solia na vakacegu kei na sautu vei ira na Isireli ena nona gauna.",
			"11": "<span>11</span> 'Ena tara o koya e dua na vale ki na Yacaqu, ia ena Luvequ o koya, Au na Tamana oi Au; ia Au na vakataudeitaka na nonai tikotiko vakatui ena nona matanitu e Isireli me sega ni mudu.'",
			"12": "<span>12</span> \"Ia oqo, na luvequ, me tiko vata kei iko o Jiova; ka mo gumatua, ka tara na vale i Jiova na nomu Kalou, me vaka sa tukuna vei iko o Koya.",
			"13": "<span>13</span> \"Ia me solia vei iko o Jiova na vuku kei na yalomatua, ka solia vei iko nai vakaro me baleti Isireli, ka mo maroroya nai vunau i Jiova na nomu Kalou.",
			"14": "<span>14</span> \"O na qai gumatua, kevaka o na qarauna mo vakayacora kece sara nai vakaro kei na lewa ka cakava o Jiova vei Mosese ena vuku i Isireli. Mo kaukauwa ka qaqa; kakua ni rere se yalolailai.",
			"15": "<span>15</span> \"E dina au a oca ni vakarautaka na ka me yaga ki na vale i Jiova e dua na drau na udolu na taledi na koula kei na dua na milioni na taledi na siliva, na varasa kei na kaukamea e sega ni vakarautaki rawa, baleta ni sa rui levu. Au sa vakarautaka talega na kau kei na vatu, o na rawa ni qai vakuria.",
			"16": "<span>16</span> \"Ia oqo sa lewe vuqa talega na tamata cakacaka era tu vata kei iko: O ira na matai ni kau kei na matai ni vatu, era kena dau dina ni veicakacaka kecega.",
			"17": "<span>17</span> \"Ena vuku ni koula kei na siliva kei na varasa kei na kaukamea e na sega ni yalani. Mo tucake ka tekivutaka na cakacaka, ena qai tiko vata kei iko o Jiova.\"",
			"18": "<span>18</span> Sa vakaroti ira talega nai liuliu vei ira na Isireli me ra vukei Solomoni na luvena tagane, ka vaka,",
			"19": "<span>19</span> \"E sega li ni tiko vata kei kemudou o Jiova na nomudou Kalou? Ia e sega li ni solia vei kemudou na vakacegu ena veiyasana kecega? Ni sa soli ira oti o Koya na lewe ni vanua ki na ligaqu, ka sa vakamalumalumutaka na vanua ena matai Jiova kei na matadra na Nona tamata.",
			"20": "<span>20</span> \"Ia oqo me vakadeitaka na lomamudou kei na yalomudou me vakasaqarai Jiova na nomudou Kalou. O koya oqo mo dou ia ka tara na vale i Jiova na Kalou, ka mo na kauta mai na kato ni veiyalayalati i Jiova kei nai yaya tabu kecega ni Kalou ki na loma ni vale ena tara ki na Yacai Jiova.\""
		},
		"23": {
			"1": "<span>1</span> Veigauna 23",
			"2": "<span>2</span> Ia ni sa qase mai o Tevita ka sa vuqa na nona veisiga, sa buli Solomoni na luvena me tui vei ira na Isireli.",
			"3": "<span>3</span> Ia o koya sa vakasoqoni ira vata mai nai liuliu kecega e Isireli, kei ira kecega na bete kei ira na Livai.",
			"4": "<span>4</span> Ia oqo sa tauri na kedra i wiliwili na Livai me tekivu mai vua sa yabaki tolusagavulu se sivia; ia na kedra i wiliwili na tagane e tolusagavulu ka walu na udolu.",
			"5": "<span>5</span> Vei ira oqo, e lewe ruasagavulu kava na udolu mera vakaraica na cakacaka ena vale i Jiova, e ono na udolu mera ovisa kei na turaganilewa,",
			"6": "<span>6</span> e va na udolu mera i vakatawa ni matamata, e va na udolu era dausere ni vakavinavinaka vei Jiova kei nai vakatagi, \"O koya au a cakava,\" e tukuna o Tevita, \"Me ka ga ni vakavinavinaka.\"",
			"7": "<span>7</span> Sa wasei ira talega o Tevita kivei iratou na luve i Livai: O Keresoni, Koaca, kei Merari.",
			"8": "<span>8</span> Vei ira na luve i Keresoni tagane: O Leadani kei Simei.",
			"9": "<span>9</span> Na luve i Leadani tagane: na kenai matai o Jeieli, sa qai tarava o Secame kei Joeli â€” e lewe tolu taucoko.",
			"10": "<span>10</span> Na luve i Simei tagane: O Selomici, Asieli, kei Arana â€”e tolu taucoko. O iratou oqo na ulu ni vuvale i Leadani na tamadratou.",
			"11": "<span>11</span> Kei na luve i Simei tagane: O Jeaca, Sina, Jeusi, kei Veraia. O iratou oqo na luve i Simei tagane.",
			"12": "<span>12</span> O Jeaca na ulumatua kei Sisa na kenai karua. Ia o Jeusi kei Veraia e sega ni levu na luvedrau tagane; o koya oqo sa lesi kina vei rau me dua ga na vuvale.",
			"13": "<span>13</span> Na luve i Koaca tagane: O Amirami, Isari, Epironi, kei Usieli â€” e ratou lewe va taucoko.",
			"14": "<span>14</span> Oi rau na luve i Amirami: O Eroni kei Mosese; ka sa tawasei tani o Eroni, o koya kei ira na luvena tagane me sega ni mudu, me vakasavasavataka o koya na veika sa tabu sara, me vakama na veika boi vinaka e na matai Jiova, me veiqaravi ki Vua, ka me solia na veivakalouvinakataki e na Yacana me sega ni mudu.",
			"15": "<span>15</span> Ia oqo oi rau na luve i Mosese na tamata ni Kalou e rau sa wili vata kei ira na yavusa i Livai.",
			"16": "<span>16</span> Oi rau na luve i Mosese tagane o Keresoni kei Iliesa.",
			"17": "<span>17</span> Vei ira na luve i Keresoni tagane, e ulumatua o Sevueli.",
			"18": "<span>18</span> Vei ira na kawa i Iliesa, e ulumatua o Riavaia. Ia e sega tale ni dua na luve i Iliesa tagane, ia era lewe levu sara na luve i Riavaia tagane.",
			"19": "<span>19</span> Vei ira na luve i Isari tagane, e ulumatua o Selomici.",
			"20": "<span>20</span> Vei ira na luve i Epironi tagane, e ulumatua o Jeraia, kenai karua o Amaraia, ai katolu o Jeasieli, kei Jekameami na kenai kava.",
			"21": "<span>21</span> Vei ira na luve Usieli, e ulumatua o Maika kai karua o Jesaia.",
			"22": "<span>22</span> Oi rau na luve i Merari tagane o Mali kei Musi. Na luve i Mali o Iliesa kei Kisi.",
			"23": "<span>23</span> Ia sa mate oiliesa, ka sega na luvena tagane, ia sa vakaluveni yalewa ga; ia o ira na wekadra, na luve i Kisi tagane, era sa tauri ira mera watidra.",
			"24": "<span>24</span> Na luve i Musi tagane o Mali, Eta, kei Jeremoce â€” e ratou lewe tolu taucoko.",
			"25": "<span>25</span> Oqo o ira na luve i Livai mai na dui nodra vuvale na tamadra â€” na ulu ni veivuvale i tamadra me vaka era sa wiliki yadudua mai na veitaravi ni yacadra, o koya sa cakava na cakacaka ni vale i Jiova, me tekivu mai vei koya sa yabaki ruasagavulu ka lako cake.",
			"26": "<span>26</span> Ni sa tukuna o Tevita, \"O Jiova na Kalou i Isireli sa solia na vakacegu vei ira na Nona tamata, ka me ra na tiko e Jerusalemi ka sega ni mudu\";",
			"27": "<span>27</span> Ia vei ira talega na Livai, \"Era na sega tale ni kauta tiko na valenisoro, se dua vei ira nai yaya ni veiqaravi.\"",
			"28": "<span>28</span> Ia mai nai otioti ni vakaro i Tevita sa wili na kedra levu na Livai me tekivu vei ira na yabaki ruasagavulu ka lako cake;",
			"29": "<span>29</span> Baleta ni nodra i tavi me ra vukei ira na luve i Eroni ena veiqaravi ena vale i Jiova, ena valenisoro kei na kena vei tikini vale e loma, ena kena vakasavasavataki na ka tabu kecega kei na cakacaka ni veiqaravi ena vale ni Kalou,",
			"30": "<span>30</span> Me baleta ruarua na madrai dauraici kei na valawa qaqi vakamatailalai me ka ni soro sulutana, vata kei na keke sega ni vakai â€“ vakatubu kei na ka sa vavi e nai tavuteke, kei na ka e veiwakitaki me vaka na kenai vakarau kei na kena levu yadua;",
			"31": "<span>31</span> Me ra tucake tu ena veimataka mera vakavinavinaka ka vakalavelaveti Jiova, ka vaka talega kina ena veiyakavi;",
			"32": "<span>32</span> Kei na veigauna taucoko ni vakacaboi soro kama vei Jiova ena veiSiga ni Vakacecegu kei na veivula vou kei na veigauna ni kana magiti vakarautaki, ena kedra i wiliwili me vaka na veitaravi ni lewa vei ira, me caka ena veigauna kecega ena mata i Jiova;",
			"33": "<span>33</span> Ka mera na dau qarava na ka kecega e gadrevi ena veigauna kece ni soqoni ena vale ni soro, na ka e gadrevi ena vale tabu, kei na ka era gadreva na luve i Eroni era wekadra ena cakacaka ni vale i Jiova."
		},
		"24": {
			"1": "<span>1</span> Veigauna 24",
			"2": "<span>2</span> Ia oqo na nodra i wasewase na luve i Eroni. O iratou na luve i Eroni tagane o Netava, Avaiu, Iliesa, kei Icama.",
			"3": "<span>3</span> Ia e rau a mate o Netava kei Aviu e na matai tamadrau ni se sega na luvedrau; o koya oqo e rau sa qarava kina na cakacaka vakabete o Iliesa kei Icama.",
			"4": "<span>4</span> O Tevita kei Setoki mai vei ira na luve i Iliesa tagane, kei Aimeleki mai vei ira na luve i Icama tagane, e ratou sa qai wasei ira ki na nodra dui gauna ni veiqaravi.",
			"5": "<span>5</span> E lewe levu cake nai liuliu e kunei vei ira na luve i Iliesa tagane mai vei ira na luve i Icama tagane, ka ra sa mani wasei me vaka oqo. Vei ira na luve i Iliesa tagane e tinikaono na ulu ni vei vuvale i tamadra, ka walu na ulu ni vei vuvale i tamadra mai vei ira na luve i Icama tagane.",
			"6": "<span>6</span> Era sa wasei vakaoqo ena vakawiri madigi, e dua nai wase mai na duatale, e tu o ira na liutaka na qaravi ni vale tabu kei ira na liutaka na qaravi ni vale ni Kalou, mai vei ira na luve i Iliesa tagane kei ira na luve i Icama tagane.",
			"7": "<span>7</span> Kei na vunivola, o Semaia na luvei Nacanieli tagane, e dua vei ira na Livai, sa vola ena i vola, e na mata ni tui, o ira nai liuliu, o Setoki na bete, Aimeleki na luve i Avaiaca, kei ira na ulu ni vei vuvale i tamadra na bete kei na Livai, e dua na vuvale i tamana e wili vei Iliesa ka dua e wili vei Icama.",
			"8": "<span>8</span> Ia oqo sa dusi Jioiarivi nai matai ni madigi, na kenai karua vei Jedaia,",
			"9": "<span>9</span> nai katolu vei Arimi, nai kava vei Seorimi,",
			"10": "<span>10</span> nai kalima vei Malakia, nai kaono vei Mijamini,",
			"11": "<span>11</span> nai kavitu vei Akoso, nai kawalu vei Avaitia,",
			"12": "<span>12</span> nai kaciwa vei Jesua, nai katini vei Sekanaia,",
			"13": "<span>13</span> nai katini-kadua vei Ilaiasivi, nai katini-karua vei Jakimi,",
			"14": "<span>14</span> nai katini-katolu vei Upa, nai katini-kava vei Jesepeavi,",
			"15": "<span>15</span> nai katini-kalima vei Biliqa, nai katini-kaono vei Imara,",
			"16": "<span>16</span> nai katini-kavitu vei Esira, nai katini-kawalu vei Apisese,",
			"17": "<span>17</span> nai katini-kaciwa vei Pecaia, nai karuasagavulu vei Jiesekeli,",
			"18": "<span>18</span> nai karua-sagavulu kadua vei Jakini, nai karua-sagavulu karua vei Kamule,",
			"19": "<span>19</span> nai karua-sagavulu katolu vei Delaia, nai karua-sagavulu kava vei Measaia.",
			"20": "<span>20</span> Oqo nai tuvatuva ni nodra veiqaravi ni ra sa lako mai ki na vale i Jiova me vaka na veitabaki ni ligai Eroni na tamadra, ni sa vakaroti koya kina o Jiova na Kalou nei Isireli.",
			"21": "<span>21</span> Ia o ira na vo ni luve i Livai tagane: vei ira na luve i Amirami tagane, Suvaele; vei ira na luve i Suvaele tagane, Jedeia.",
			"22": "<span>22</span> Me baleti Riavaia, vei ira na luve i Riavaia tagane, nai matai o Isaia.",
			"23": "<span>23</span> Vei ira na luve i Isari tagane, o Selomoci; vei ira na luve i Selomoci tagane o Jevaci.",
			"24": "<span>24</span> Vei ira na luve i Epironi tagane, Jeraia nai matai, Amaraia nai karua, nai katolu o Jeasieli, kei nai kava o Jikamiama.",
			"25": "<span>25</span> Vei ira na luve i Usieli tagane, o Maika; vei ira na luve i Maika tagane, o Semira.",
			"26": "<span>26</span> Na taci Maika, o Isaia; vei ira na luve i Isaia, o Sakaraia.",
			"27": "<span>27</span> Oi rau na luve i Merari tagane o Mali kei Musi; na luve i Jeasaia tagane, o Peno.",
			"28": "<span>28</span> Na luve I Merari tagane vei Jeasaia o Peno, Soama, Sakuri, kei Iveri.",
			"29": "<span>29</span> Vei Mali: O Iliesa, o koya e sega na luvena tagane.",
			"30": "<span>30</span> Vei Kisi: na luve i Kisi tagane, o Jeramieli.",
			"31": "<span>31</span> O iratou talega na luve i Musi tagane o Mali, Eta, kei Jerimoce. Oqo o ira na luve i Livai me vaka na vei vuvale i tamadra.",
			"32": "<span>32</span> O ira talega oqo era vakawiri madigi me vakataki ira na wekadra na luve i Eroni, ena mata i Tevita na tui, o Setoki, Aimeleki, kei na veiulu ni vei vuvale i tamadra na bete kei na Livai. O ira na tamadra era sa liu era sa tautauvata kei ira na tacidra era sa gone."
		},
		"25": {
			"1": "<span>1</span> Veigauna 25",
			"2": "<span>2</span> Sa tawasea talega o Tevita kei ira na kavetani ni mataivalu ki na veiqaravi eso na luve i Esava, kei Emani, kei Jeducuni, mera na dauparofisai e na api, kei nai vakatagi vakawa kei na sibali. Ia na kedra i wiliwili na tagane era vakaitavi ena veiqaravi oqo:",
			"3": "<span>3</span> Vei ira na luve i Esava tagane: O Sakuri, Joseva, Necanaia, kei Asarela, o ira na luve i Esava era vakarorogo ki na veiliutaki i Esava, o koya sa parofisai me vaka nai tuvatuva ni tui.",
			"4": "<span>4</span> Vei Jeducuni, na luve i Jeducuni tagane: O Kidalaia, Seri, Jesaia, Simei, Asavaia, kei Maticia, e ono, ena veiliutaki i Jeducuni na tamadratou, o koya e parofisai e na api me vakavinavinaka ka vakalevulevu i Jiova.",
			"5": "<span>5</span> Vei Emani, na luve i Emani tagane: O Bukaia, Matanaia, Usieli, Sevueli, Jerimoce, Ananaia, Ananai, Josevakasa, Maloci, Ocire, kei Measioci.",
			"6": "<span>6</span> O ira kece oqo na luve i Emani tagane na nona parofita na tui ena vosa ni Kalou, me vakalevulevuya na nonai leu. Ni sa solia na Kalou vei Emani e tinikava na luvena tagane kei na tolu na luvena yalewa.",
			"7": "<span>7</span> O ira kece oqo era vakarorogo vei tamadra ena vuku ni vakatagi ena vale i Jiova, ena sibali, nai vakatagi vakawa, kei na api, ena kena qaravi na vale ni Kalou. O iratou o Esava, Jeducuni, kei Emani e ratou vakarorogo ki na lewa ni tui.",
			"8": "<span>8</span> Ia na kedra i wiliwili, vata kei ira na wekadra ka ra sa vakaroti e na vuku ni sere i Jiova, o ira kece na kena dau dina, e rua na drau walusagavulu ka walu.",
			"9": "<span>9</span> Ka ra sa vakawirimadigi ena vuku ni nodra dui tavi, o koya e lailai kei koya e levu, na qasenivuli kei na gonevuli.",
			"10": "<span>10</span> Ia oqo nai matai ni madigi i Esava sa dusi Joseva; na kenai karua vei Kidalaia, o koya kei ira na tacina kei na dui luvedra tagane, e tinikarua;",
			"11": "<span>11</span> nai katolu vei Sakuri, iratou na luve tagane kei ira na tacina, e tinikarua;",
			"12": "<span>12</span> nai kava vei Jisiri, iratou na luvena tagane kei na tacina, e tinikarua;",
			"13": "<span>13</span> nai kalima vei Necanaia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"14": "<span>14</span> nai ka ono vei Bukaia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"15": "<span>15</span> nai kavitu vei Jisarela, iratou na luvena tagane kei na tacina, e tinikarua;",
			"16": "<span>16</span> nai ka walu vei Jesaia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"17": "<span>17</span> nai kaciwa vei Matanaia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"18": "<span>18</span> nai katini vei Simei, iratou na luvena tagane kei na tacina, e tinikarua;",
			"19": "<span>19</span> nai katini-kadua vei Asareli, iratou na luvena tagane kei na tacina, e tinikarua;",
			"20": "<span>20</span> nai katini-karua vei Asavaia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"21": "<span>21</span> nai katini-katolu vei Suvaeli, iratou na luvena tagane kei na tacina, e tinikarua;",
			"22": "<span>22</span> nai katini-kava vei Maticia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"23": "<span>23</span> nai katini-kalima vei Jerimoce, iratou na luvena tagane kei na tacina, e tinikarua;",
			"24": "<span>24</span> nai katini-kaono vei Ananaia, iratou na luvena tagane kei na tacina, e tinikarua;",
			"25": "<span>25</span> nai katini-kavitu vei Josevakasa, iratou na luvena tagane kei na tacina, e tinikarua;",
			"26": "<span>26</span> nai katini-kawalu vei Ananai, iratou na luvena tagane kei na tacina, e tinikarua;",
			"27": "<span>27</span> nai katini-kaciwa vei Maloci, iratou na luvena tagane kei na tacina, e tinikarua;",
			"28": "<span>28</span> nai ka ruasagavulu vei Elaiaca, iratou na luvena tagane kei na tacina, e tinikarua;",
			"29": "<span>29</span> nai ka ruasagavulu kadua vei Ocira, iratou na luvena tagane kei na tacina, e tinikarua;",
			"30": "<span>30</span> nai ka ruasagavulu karua vei Kidaliti, iratou na luvena tagane kei na tacina, e tinikarua;",
			"31": "<span>31</span> nai ka ruasagavulu katolu vei Miasioci, iratou na luvena tagane kei na tacina, e tinikarua;",
			"32": "<span>32</span> nai ka ruasagavulu kava vei Romamiti-Esira, iratou na luvena tagane kei na tacina, e tinikarua."
		},
		"26": {
			"1": "<span>1</span> Veigauna 26",
			"2": "<span>2</span> Ena vuku ni nodra i wasewase nai vakatawa ni Matamata: mai vei ira na luve i Kora, o Miselemaia na luve i Kore tagane, vei ira na luve i Esava tagane.",
			"3": "<span>3</span> Ia o iratou na luve i Miselemaia tagane o Sakaraia e ulumatua, nai karua o Jediale, o Sevatai nai katolu, Jacinieli nai kava,",
			"4": "<span>4</span> O Elami ei kalima, Jioanani nai kaono, kei Elieoenai na kenai kavitu.",
			"5": "<span>5</span> O iratou oqo na luve i Opeti-Itomi tagane o Semaia e ulumatua, o Josevata na karua, Joa na katolu, Saka na kava, Necanieli na kalima.",
			"6": "<span>6</span> O Amieli nai kaono, Isakari nai kavitu, kei Piuletai na kenai kawalu; ni sa vakalouvinakataki koya na Kalou.",
			"7": "<span>7</span> Vei Semaia talega na luvena tagane sa sucu eso na luvena tagane era sa lewa ena vuvale i tamadra, baleta ni ra sa tamata gugumatua.",
			"8": "<span>8</span> O iratou na luve i Semaia tagane o Oceni, Ravaele, Opeti, kei Ilisavati, oi rau na tacidratou o Ilaiu kei Semakaia era sa tamata gugumatua.",
			"9": "<span>9</span> O iratou kecega oqo na luve i Opeti-Itomi tagane, oiratou kei ira na luvedratou kei na tacidratou, era tamata gugumatua ni tu vei ira na kaukauwa me ra cakava na cakacaka: era lewe onosagavulu karua na kawa i Opeti-Itomi.",
			"10": "<span>10</span> Ia o ira na luve i Miselemaia tagane kei ira na tacina, e tinikawalu na tamata gugumatua.",
			"11": "<span>11</span> O Osa talega, mai vei ira na luve i Merari, e vakaluveni tagane: O Simiri sa liu (E dina ga ni sega ni ulumatua o koya, ia sa lewa o tamana me liu o koya),",
			"12": "<span>12</span> O Ilikaia na kenai karua, o Tivalaia na kena i katolu, o Sakaraia ei kava: O ira na luve i Osa kei ira na tacina era sa lewe tinikatolu taucoko.",
			"13": "<span>13</span> Sa wasei vei ira oqo nai tavi ni veiqaravi vakai-vakatawa ni Matamata, vei ira nai liuliu, sa lesi na nodra i tavi me vakataki ira na wekadra, me ra veiqaravi ena vale i Jiova.",
			"14": "<span>14</span> Ia era sa vakawiri madigi e na vuku ni Matamata yadua, na ka lalai kei na ka lelevu talega, me vaka na dui vuvale i tamadra.",
			"15": "<span>15</span> Na madigi ni Matamata ki na Tokalau sa dusi Selemaia. Era sa qai vakawiri madigi me baleti Sakaraia na luvena tagane, e dua na daunivakasala vuku, ka sa dusi koya na madigi ni Matamata ki na Vualiku;",
			"16": "<span>16</span> Kivei Opeti-Itomi na Matamata ki na Ceva, ia kivei ira na luvena tagane na vale ni yau.",
			"17": "<span>17</span> Kivei rau o Supimi kei Osa sa nodrau na madigi ni Matamata ki na Ra, vata kei na matamata o Salojoci ena sala e cakeâ€”era veibasai na yadra.",
			"18": "<span>18</span> Me lewe ono na Livai ena Tokalau, me lewe va ena Vualiku e na veisiga yadua, e lewe va ena Ceva e na veisiga yadua, kei na vale ni yau sa tauya rua.",
			"19": "<span>19</span> Ia ki na Pariba ena Ra, e lewe va ena sala cecere kei na lewe rua ena Pariba.",
			"20": "<span>20</span> Oqo na nodra i wasewase nai vakatawa ni Matamata mai vei ira na luve i Kora tagane kei ira na luve i Merari tagane.",
			"21": "<span>21</span> Vei ira na Livai, o Aija e lewa na vale ni Yau e na vale ni Kalou kei na vale ni yau e vakatabui.",
			"22": "<span>22</span> O ira na luve i Liadani tagane, na kawa i Keresoni na luve i Liadani, era ulu ni vei vuvale i tamadra, vei Liadani na luve i Keresoni: O Jeieli.",
			"23": "<span>23</span> Na luve i Leieli tagane, o Secame kei Joeli na tacina, e rau lewa na vale ni yau e na vale i Jiova.",
			"24": "<span>24</span> Vei ira na kawa i Amirami, i Isari, i Epironi, kei Usieli:",
			"25": "<span>25</span> O Sevueli na luve i Keresoni tagane, na luve i Mosese, sa lewa na vale ni yau kecega.",
			"26": "<span>26</span> Kei iratou na tacina mai vei Iliesa o Revaia na luvena tagane, Jesaia na luvena tagane, Jorame na luvena tagane, Sikiri na luvena tagane, kei Selomeci na luvena tagane.",
			"27": "<span>27</span> O Selomeci oqo kei ira na tacina era maroroya nai yau e vakatabui ka vakacabora o Tevita na Tui kei ira na ulu ni vei vuvale i tamadra, na kavetani ni lewe udolu kei na vei yadrau, kei na kavetani ni mataivalu raraba.",
			"28": "<span>28</span> Eso nai toki ni valu e kau mai na buca ni valu era sa vakatabuya me maroroi ena na vale i Jiova.",
			"29": "<span>29</span> Kei nai yau kecega sa vakatabuya o Samuela na parofita, o Saula na luve i Kisi tagane, Apineri na luve i Neri tagane, kei Joave na luve i Seruwaia tagane, na veika kecega e vakatabui sa tu ena ligai Selomeci kei ira na tacina.",
			"30": "<span>30</span> Vei ira na luve i Isari, o Kenanaia kei ira na luvena tagane era qarava nai tavi vakaovisa kei na turaga-ni lewa e Isireli ena taudaku kei Jerusalemi.",
			"31": "<span>31</span> Vei ira na luve i Epironi, o Asavaia kei ira na tacina, e dua na udolu vitu na drau na tamata nuitaki, era raica na vanua o Isireli vaka ki na Ra ni Joritani ena vuku ni cakacaka kecega i Jiova, kei na veiqaravi vua na tui.",
			"32": "<span>32</span> Vei ira na luve i Epironi, o Jerija e ulu ni vuvale i Epironi me vaka na nodra i tuvatuva ni kawa na tamadra. E nai ka vasagavulu ni yabaki ni lewa i Tevita era sa vakasaqarai, ka sa kunei vei ira eso na tamata nuitaki ka ra tiko mai Jaseri e Kiliati.",
			"33": "<span>33</span> Kei na tacidra e rua na udolu vitu na drau na tamata nuitaki, era ulu ni vei vuvale i tamadra, o ira ka vakatikora o Tevita na Tui me ra veiqaravi vei ira na yavusa i Rupeni, na yavusa i Kata, kei na veimama ni yavusa i Manasa, ena vuku ni ka kecega me baleta na Kalou kei na ka e kecega e baleta na tui."
		},
		"27": {
			"1": "<span>1</span> Veigauna 27",
			"2": "<span>2</span> Ia o ira na luve i Isireli, ena kedra i wiliwili, o ira na ulu ni vei vuvale i tamadra, na kavetani ni lewe udolu kei na vei yadrau kei ira na kena dauveiqaravi, era qarava na tui ena ka kecega me baleta na veiwasewase ni mataivalu. O ira nai wasewase kecega oqori era lako mai ka ra lako yani mai na dua na vula ki na dua tale ena veivula kece ni yabaki, nai wasewase yadua e tauya ruasagavulu kava na udolu.",
			"3": "<span>3</span> Ki nai matai ni wasewase e nai matai ni vula o Jasoviama na luve i Savetiale tagane, ia era lewe ruasagavulu kava na udolu ena nonai wasewase;",
			"4": "<span>4</span> O koya e dua vei ira na luve i Veresi ka nodra i liuliu na kavetani kecega ni mataivalu e nai matai ni vula.",
			"5": "<span>5</span> Vei ira nai wasewase e nai karua ni vula o Todai na Aoaite, ia ena nonai wasewase e veiliutaki talega kina o Mikiloci; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"6": "<span>6</span> Nai katolu ni kavetani ni mataivalu e nai katolu ni vula o Penaia na luve i Jioiada na bete, o koya e turaga; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"7": "<span>7</span> O Penaia ga oqo ka sa kaukauwa cake vei ira na lewe tolusagavulu, ia sa liu vei ira na lewe tolusagavulu; e tiko ena nonai wasewase o Amisavati na luvena tagane.",
			"8": "<span>8</span> Nai kava ni kavetani e nai kava ni vula o Asaeli na taci Joave, kei Sevataia na luvena tagane o koya ea qai sosomitaki koya; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"9": "<span>9</span> Nai kalima ni kavetani e nai kalima ni vula o Semiuci na Isiraite; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"10": "<span>10</span> Nai kaono ni kavetani e nai kaono ni vula Ira na luve i Ikesi na Tekoite; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"11": "<span>11</span> Nai kavitu ni kavetani e nai kavitu ni vula o Elesi na Pelonaite, mai vei ira na luve i Ifereimi; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"12": "<span>12</span> Nai kawalu ni kavetani e nai kawalu ni vula o Sivekai na luve i Usa, mai vei ira na Sariaite; ena nonai wsewase era lewe ruasagavulu kava na udolu.",
			"13": "<span>13</span> Nai kaciwa ni kavetani e nai kaciwa ni vula o Aviesa na Anacoci, mai vei ira na luve i Penijamini; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"14": "<span>14</span> Nai katini ni kavetani e nai katini ni vula o Mearai na Netofaci, mai vei ira na Sariaite; ena nonai wsewase era lewe ruasagavulu kava na udolu.",
			"15": "<span>15</span> Nai katini kadua ni kavetani e nai katini kadua ni vula o Penaia na Piraconaite, mai vei ira na luve i Ifereimi; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"16": "<span>16</span> Nai katini karua ni kavetani e nai katini karua ni vula o Elidai na Netofaci, mai vei Ocenieli; ena nonai wasewase era lewe ruasagavulu kava na udolu.",
			"17": "<span>17</span> Ia oqo, kivei ira na vei yavusa e Isireli: sa lewai ira na Rupeni o Iliesa na luve i Sikiri tagane; vei ira na Simione, o Sevataia na luve i Meaka tagane;",
			"18": "<span>18</span> vei ira na Livai, o Asavaia na luve i Kemueli tagane; vei ira na luve i Eroni, o Setoki;",
			"19": "<span>19</span> vei Juta, o Ilaiu, e dua vei ira na tuakai Tevita; vei Isakari, o Omeri na luve i Maikali tagane;",
			"20": "<span>20</span> vei Sevuloni, oisimaia na i Opetaia tagane; vei Navitalai, o Jerimoce na luve i Asirieli tagane;",
			"21": "<span>21</span> vei ira na luve i Ifereimi, o Osea na luve i Asasaia tagane; na veimama ni yavusa i Manasa, o Joeli na luve i Vetaia;",
			"22": "<span>22</span> ki na veimama ni yavusa i Manasa mai Kiliati, o Ito na luve i Sakaraia tagane; vei Penijamini, o Jiasieli na luve i Apineri;",
			"23": "<span>23</span> vei Tani, o Asareli na luve i Jeroame tagane. Oqo o ira nai liuliu ni vei-yavusa e Isireli.",
			"24": "<span>24</span> Ia o Tevita e sega ni taura na kedra i wiliwili na yabaki ruasagavulu se lailai sobu, baleta ni tukuna o Jiova ni na vakalewevuqataki Isireli me vaka na kalokalo ni lomalagi.",
			"25": "<span>25</span> O Joave na luve i Seruwaia tagane sa tekivuna na wili lewe ni vanua, ia e a sega ni vakotia, ni sa yacovi Isireli na cudruvi ena vuku ni wili lewe ni vanua; e sega talega ni maroroi e nai vola ni tukutuku i Tevita na Tui.",
			"26": "<span>26</span> Ia o Asamaveci na luve i Atieli tagane sa lewa na vale ni yau ni tui; kei Jonacani na luve i Usaia tagane sa lewa na vale ni maroroi yaya mai na veiwere, veikoro lelevu, veikoro lalai, kei na veikoro ni valu.",
			"27": "<span>27</span> O Eseri na luve I Keluvi tagane sa liutaki ira era cakava na cakacaka ni kena cukiraki na qele e vanua.",
			"28": "<span>28</span> Ia o Simei na Ramaci sa lewa na veiwere ni vaini, ia o Savati na Sivimaite sa lewe na vuani vaini mai na veivaini me caka kina na waini.",
			"29": "<span>29</span> O Peali-Anani na Kederaitesa lewa na veivuni olive kei na veivuni lolo era bula tu e na vei vanua lolovira, ia o Joasi sa lewa na vale ni maroroi waiwai.",
			"30": "<span>30</span> Ia o Sitirai na Saronaite sa lewa na qele ni manumanu era kakana voli mai Saroni, ia o Savati na luve i Adilai tagane sa lewa na qele ni manumanu era tiko ena veibuca.",
			"31": "<span>31</span> O Ovili na Isimeli sa lewa na kameli kecega, o Jedeia na Meronoci sa lewai ira na asa,",
			"32": "<span>32</span> ia o Jasisi na Akiraite sa lewa na qele ni sipi kecega. O ira kece oqo nai liuliu era qarava na ka kecega sa nei Tevita na tui.",
			"33": "<span>33</span> O Jonacani talega, na tamai Tevita lailai, sa dua na daunivakasala, e tamata vuku, e dau ni Vola Tabu; ka sa dautiko vata kei iratou na luveni tui tagane o Jeieli na luve i Akimoni tagane.",
			"34": "<span>34</span> O Aicoveli sa nona daunivakasala na tui, kei Usai na Arikaite sa nonai tokani na tui.",
			"35": "<span>35</span> Ni sa cegu o Aicoveli, sa vuke o Penaia, sa tarava o Aviaca. Ia sa liutaka na mataivalu raraba ni tui o Joave."
		},
		"28": {
			"1": "<span>1</span> Veigauna 28",
			"2": "<span>2</span> Ia oqo o Tevita sa vakasoqoni ira vata mai ki Jerusalemi nai liuliu kecega e Isireli: O ira nai liuliu ni vei yavusa kei na kavetani ni veiwasewase ni mataivalu era qarava na tui, na kavetani ni lewe udolu kei na kavetani ni veiyadrau, kei ira na dauniyau ka sa lewa na veika kecega sa nona na tui kei na nodratou na luvena tagane, kei ira na daveiqaravi liu, na tamata qaqa, kei ira kecega na tamata kaukauwa ni valu.",
			"3": "<span>3</span> Sa qai tucake o Tevita na Tui ka tukuna, \"Mo ni rogoci au, na wekaqu kei na noqu tamata: Au sa nakita e lomaqu me'u tara e dua na vale me vakacegu kina na kato ni veiyalayalati i Jiova, ka mei tutu ni yava ni noda Kalou, au sa vakarau tu me'u tara.",
			"4": "<span>4</span> \"Ia sa tukuna vei au na Kalou, 'O na sega ni tara na vale ki na Yacaqu, ni o sa tamata ni valu ka sa dauvakadave dra.'",
			"5": "<span>5</span> \"Ia sa digitaki au o Jiova na Kalou ni Isireli mai vei ira kecega ena vale i tamaqu me'u tui e Isireli ka Sega ni mudu, ni sa digitaki Juta o Koya me lewa. Ia ena yavusa i Juta, ena vuvale i tamaqu, kei ira na luve i tamaqu tagane, sa marautaki au o Koya ka sa vakatikori au me'u tui e Isireli taucoko.",
			"6": "<span>6</span> \"Ia vei ira kecega na luvequ tagane (ni sa solia vei au o Koya e vuqa na luvequ tagane) sa digitaka o Koya na luvequ o Solomoni me dabeca nai cewacewa vakatui ena matanitu i Jiova na Kalou i Isireli.",
			"7": "<span>7</span> \"Ia sa tukuna vakaoqo o Koya vei au, 'Sai Solomoni na luvemu tagane ena tara na Noqu vale kei na Noqu rara vakabai; Ni'u sa digitaki koya me luvequ, ia Au na Tamana.",
			"8": "<span>8</span> 'O koya oqo Au na vakadeitaka na nona matanitu me sega ni mudu, kevaka sa tudei o koya me maroroya na Noqui vakaro kei na Noqu lewa, me vaka ena siga oqo.'",
			"9": "<span>9</span> \"Ia oqo, ena matai Isireli taucoko, nai soqosoqo i Jiova, kei na Nona rogoci na noda Kalou, mo ni qarauna mo ni vakasaqara kece sara nai vakaro i Jiova na nomuni Kalou, ka maroroya tu me nodra na luvemuni me sega ni mudu.",
			"10": "<span>10</span> \"Ia vei iko, Solomoni na luvequ, mo kila na Kalou i tamamu, ka qaravi Koya ena yalo e dinata kei na loma e vinakata; ni sa vakasaqara o Jiova na yalo kecega ka kila na nanuma ni lomada. Kevaka o na vakasaqarai Koya, o na kunei Koya; ia kevaka o na biuti Koya, ena biuti iko o Koya ka sega ni mudu.",
			"11": "<span>11</span> \"Mo nanuma mada oqo, ni sa digitaki iko o Jiova mo tara e dua na vale me vale tabu; mo kaukauwa ga, ka cakava.\"",
			"12": "<span>12</span> Sa qai solia o Tevita kivei Solomoni na luvena na droini ni varada, na kena veivale, na veivale ni yau, na veitaba vale e cake, na kena veivale e loma, kei na vanua me tiko kina nai cewacewa ni loloma;",
			"13": "<span>13</span> Kei na droini ni veika kecega e tukuna vua na Yalo, na rara vakabai ni vale i Jiova, na veivale kecega e vakavolivolita, na veivale ni yau kecega ni vale ni Kalou, kei na veivale kecega ni yau vakatabui;",
			"14": "<span>14</span> Ena vuku ni nodra i wasewase kece talega na bete kei na Livai, ena vuku ni cakacaka ni veiqaravi ni vale i Jiova, kei nai yaya ni veiqaravi kecega ena vale i Jiova.",
			"15": "<span>15</span> E solia o koya na koula sa vakarautaki na kena bibi me baleta nai yaya koula, ena vuku ni yaya taucoko e vakayagataki ki na veimataqali veiqaravi kecega; na kena bibi talega na siliva me baleta nai yaya siliva kecega, ena vuku ni yaya taucoko e yaga ki na veiqaravi kecega;",
			"16": "<span>16</span> Na bibi ni tutu ni cina koula, kei na kena kena cina koula, na kena bibi nai tutu ni cina yadua kei na kena veicina; e na vuku ni tutu ni cina siliva sa vakarautaki na kena bibi, na bibi ni tutu ni cina kei na kena cina, me vaka na kena vakayagataki na veitutu ni cina yadua.",
			"17": "<span>17</span> Sa solia talega o koya na koula sa vakarautaki na kena bibi me baleta na teveli ni madrai dauraici, ena vuku ni teveli yadua; kei na siliva me baleta na teveli siliva;",
			"18": "<span>18</span> Na koula dina talega me baleta nai cula, na beseni, na bilo e koula dina kei na sedre koula â€” sa solia o koya nai vakarau koula me baleta na sedre yadua; kei na sedre siliva, me baleta na sedre siliva yadua;",
			"19": "<span>19</span> Kei na koula sa vakatovolei oti sa vakarautaki na kena bibi me baleta nai cabocabo ni ka boi vinaka, kei na kena caka na qiqi, o ya, na jerubimi koula e rau tevuka na tabadrau me vakaruguta na kato ni veiyalayalati i Jiova.",
			"20": "<span>20</span> \"Na veika kecega oqo, \"sa tukuna o Tevita, \"O Jiova sa vakatakila vei au e nai vola, ni sa tabaki au ena Ligana, na cakacaka ni droini kecega oqo.\"",
			"21": "<span>21</span> Ka sa tukuna o Tevita vei Solomoni na luvena, \"Mo kaukauwa ka mo yalo doudou, ka cakava; kakua ni o rere se taqaya: O Jiova na Kalou â€” noqu Kalou â€” ena tiko vata kei iko. O Koya ena sega ni biuti iko se lako tani mai vei iko, me yacova sara ni o sa vakaotia na cakacaka kecega ni veiqaravi ena vale i Jiova.",
			"22": "<span>22</span> \"Oqo na nodra i wasewase yadua na bete kei na Livai ena vuku ni veiqaravi kecega ena vale ni Kalou; ia o ira kecega na matai yalodina era na tu vata kei iko ena vuku ni cakacaka vakamatai kecega, ena vuku ni veiqaravi kecega; o ira talega nai liuliu kei ira na tamata taucoko era na vakarorogo vakatabakidua ki na nomui vakaro.\""
		},
		"29": {
			"1": "<span>1</span> Veigauna 29",
			"2": "<span>2</span> Sa tukuna talega o Tevita na tui kivei ira nai soqosoqo taucoko: \"Na luvequ o Solomoni, o koya taudua ga sa digitaki koya na Kalou, e se gone ga ka lecaika; ia na cakacaka oqo sa rui ka vakaitamera, baleta na vale oqo e sega ni nona na tamata ia sa nei Jiova ga na Kalou.",
			"3": "<span>3</span> \"Ia oqo na vale ni noqu Kalou au a vakarautaka e na noqui gu taucoko: na koula me baleta nai yaya e caka ena koula, siliva me baleta nai yaya siliva, varasa me baleta nai yaya varasa, kaukamea me baleta nai yaya kaukamea, kau me baleta nai yaya kau, na vatu onikisa, na vatu me vonoti, vatu tatalivaliva ena kena duiroka, na veimataqali vatu talei, kei na vatu mavolo e ka levu sara.",
			"4": "<span>4</span> \"O koya oqo, ni'u a gadreva dina e yaloqu na vale ni noqu Kalou, au sa solia kina ki na vale ni noqu Kalou e levu ka sivia na ka au vakarautaka me tara kina na vale tabu, na noqu koula kei na siliva vakamareqeti:",
			"5": "<span>5</span> \"E tolu na udolu na taledi na koula, [dua na drau ka mani tinikarua na tani] na koula mai Ofira, kei na vitu na udolu na taledi na siliva sa vakatovolei [rua na darau onosagavulu na tani], me botani kina na lalaga ni veivale;",
			"6": "<span>6</span> \"Na koula me baleta nai yaya koula kei na siliva cakava na ligadra na matai. Ia o cei mada ena yalo me baleta nai yaya siliva, kei na cakacaka kecega e rawarawa me vakacabori koya ena siga oqo vei Jiova?\"",
			"7": "<span>7</span> Era sa qai yalo rawarawa mera vakacabori ira nai liuliu ni vei vuvale i tamadra, liuliu ni vei yavusa e Isireli, kavetani ni lewe udolu kei na veiyadrau, vata kei ira sa lewa na nona cakacaka na tui.",
			"8": "<span>8</span> Era sa solia ki na cakacaka ni vale ni Kalou e lima na udolu na taledi [dua na drau walusagavulu ka vitu na tani] kei na tini na udolu na darika na koula, e tini na udolu na taledi [tolu na drau vitusagavulu ka lima na tani] na siliva, e tinikawalu na udolu na taledi [ono na drau vitusagavulu ka lima na tani] na varasa, kei na dua na drau na udolu na taledi [tolu na udolu vitu na drau limasagavulu na tani] na kaukamea.",
			"9": "<span>9</span> Ia o koya ga sa tu vua na vatu talei sa solia ki na vale ni yau ni vale i Jiova, ki na ligai Jeieli na luve i Keresoni.",
			"10": "<span>10</span> Era sa qai reki na tamata, ni ra soli ena yalo vinaka, ena yalo e dina era sa solia kina e na yalo rawarawa vei Jiova; ia o Tevita na tui sa reki vakalevu talega.",
			"11": "<span>11</span> O koya oqo sa ia o Tevita na vakavinavinaka vei Jiova ena matadra nai soqosoqo taucoko; ka sa tukuna o Tevita: \"O ni sa kalouvinaka, i Jiova na Kalou i Isireli, na tamai keimami, me sega ni mudu.",
			"12": "<span>12</span> Sa Nomuni, i Jiova, na cecere, Na Kaukauwa kei na Lagilagi, Na qaqa kei na ka vaka-Turaga; Ni sa Nomuni na ka kecega mai Lomalagi kei Vuravura; Sa Nomuni na Matanitu, i Jiova, Ka o Ni sa laveti cake me ulu ni veika kecega.",
			"13": "<span>13</span> E rau sa lako mai vei Kemuni nai yau kei na vakarokoroko, Ia o Ni sa lewa na ka kecega. Sa tu e Ligamuni na kaukauwa kei na gumatua; E Ligamuni sa caka kina me rawa na ka levu Ka solia na kaukauwa vei ira kecega.",
			"14": "<span>14</span> \"O koya oqo, neimami Kalou, Keimami vakavinavinaka vei Kemuni Ka vakacaucautaka na Yacamuni Lagilagi.",
			"15": "<span>15</span> Ia o cei oi au, o cei talega o ira na noqu tamata, Me rawa ni keimami soli vakaoqo ena yalo vinaka? Ni sa vu mai vei Kemuni na ka kecega, Ni keimami sa solia vei Kemuni na ka ga e Nomuni.",
			"16": "<span>16</span> Ni keimami sa vulagi ka dau veitokiyaki voli e Matamuni, Me vakataki ira na tamai keimami; Na neimami veisiga e vuravura sa vaka ga na yaloyalo, Ka sa yali nai nuinui.",
			"17": "<span>17</span> \"I Jiova na neimami Kalou, nai yau vakaitamera keimami sa vakarautaka tu oqo me keimami tara kina vei Kemuni e dua na vale ki na Yacamuni Tabu sa mai na Ligamuni, ka sa Nomuni kecega.",
			"18": "<span>18</span> \"Au sa kila talega, noqu Kalou, ni o Ni sa dauvakatovolea na yalo ka sa dauvinakati ira na yalododonu. Io oi au, ena dodonu ni yaloqu au sa solia kina ena yalo rawarawa na veika oqo; ia oqo au sa qai reki ni'u sa raici ira na Nomuni tamata, ni ra sa tu eke mera solia ena yalovinaka vei Kemuni.",
			"19": "<span>19</span> \"I Jiova na Kalou i Eparaama, Aisake, kei Isireli, o iratou na tamai keimami, Mo ni vakadeitaka me Sega ni mudu na nodra lomadei e na vakanananu ni yalodra na Nomuni tamata, ka me ra vagolea na yalodra vei Kemuni.",
			"20": "<span>20</span> \"Ka solia vei Solomoni na luvequ na yalo e dina me maroroya kina na Nomuni i vakaro kei na Nomuni i vakadinadina, kei na Nomuni lesilesi, me na cakava kina na ka kecega oqo, ka me na tara na vale tabu au sa vakarautaka oti tu na kenai yaya.\"",
			"21": "<span>21</span> Sa qai tukuna o Tevita vei ira nai soqosoqo taucoko, \"Ia oqo mera vosa vakalouvinakataki Jiova na Kalou\" Tamadra o ira kecega na Isireli, era sa qai vakacuva ulu ka vakarokoroko ena matai Jiova kei na tui.",
			"22": "<span>22</span> Era sa ia na vakacaboi soro vei Jiova ka cabora nai soro kama vei Jiova ena siga e tarava: e dua na udolu na bulumakau tagane, e dua na udolu na sipi tagane, e dua na udolu na lami, kei nai madrali gunuvi; kei nai madrali e vuqa me rauti ira kecega na Isireli.",
			"23": "<span>23</span> Ia era sa kana ka gunu kecega ena matai Jiova ena marau levu ena siga o ya. Ka ra sa vakatikori Solomoni e nai karua ni gauna me tui, ka lumuti koya ena matai Jiova me nodra i liuliu, kei Setoki me bete.",
			"24": "<span>24</span> Sa qai dabeca o Solomoni nai cewacewa i Jiova me sa tui ni sa sosomitaki Tevita na tamana, ka sa tamata yaco; era sa talairawarawa vua na Isireli kecega.",
			"25": "<span>25</span> O ira nai liuliu kece sara kei ira na tamata qaqa, kei ira talega na luve i Tui o Tevita tagane, era sa soli ira kivei Solomoni na Tui.",
			"26": "<span>26</span> Ia sa vakalevulevui Solomoni vakalevu o Jiova ena matadra na Isireli taucoko, ka solia vua na ka vakaturaga e se bera ni bau soli kivua e dua na tui sa liu vei koya.",
			"27": "<span>27</span> Ia o Tevita na luve i Jese tagane sa lewa e Isireli taucoko.",
			"28": "<span>28</span> Ia na gauna taucoko ni nona lewa sa vasagavulu na yabaki; e lewa mai Epironi me vitu na yabaki, ka tolusagavulu katolu na yabaki na nona lewa mai Jerusalemi.",
			"29": "<span>29</span> Ia sa mate o koya ni sa qase sara, sa levu na nona veisiga vinaka kei na yau kei na vakarokoroko; ka sa lewa me kenai sosomi o Solomoni na luvena.",
			"30": "<span>30</span> Ia oqo na cakacaka kecega i Tevita na Tui, kenai matai ki na kenai otioti, io era sa volai kecega e nai vola i Samuela na parofita, e nai vola i Necani na parofita, kei nai vola i Kata na parofita.",
			"31": "<span>31</span> kei na nona lewa kecega kei na nona kaukauwa, kei na veika e yacovi koya, kei Isireli, kei ira na veimatanitu kecega ni veivanua."
		}
	}
};
module.exports = book;