var book = {
	"name": "लूका",
	"numChapters": 24,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> इसलिये कि बहुतों ने उन बातों को जो हमारे बीच में होती हैं इतिहास लिखने में हाथ लगाया है।",
			"2": "<sup>2</sup> जैसा कि उन्होंने जो पहिले ही से इन बातों के देखने वाले और वचन के सेवक थे हम तक पहुंचाया।",
			"3": "<sup>3</sup> इसलिये हे श्रीमान थियुफिलुस मुझे भी यह उचित मालूम हुआ कि उन सब बातों का सम्पूर्ण हाल आरम्भ से ठीक ठीक जांच करके उन्हें तेरे लिये क्रमानुसार लिखूं।",
			"4": "<sup>4</sup> कि तू यह जान ले, कि वे बातें जिनकी तू ने शिक्षा पाई है, कैसी अटल हैं॥",
			"5": "<sup>5</sup> यहूदियों के राजा हेरोदेस के समय अबिय्याह के दल में जकरयाह नाम का एक याजक था, और उस की पत्नी हारून के वंश की थी, जिस का नाम इलीशिबा था।",
			"6": "<sup>6</sup> और वे दोनों परमेश्वर के साम्हने धर्मी थे: और प्रभु की सारी आज्ञाओं और विधियों पर निर्दोष चलने वाले थे। उन के कोई भी सन्तान न थी,",
			"7": "<sup>7</sup> क्योंकि इलीशिबा बांझ थी, और वे दोनों बूढ़े थे॥",
			"8": "<sup>8</sup> जब वह अपने दलकी पारी पर परमेश्वर के साम्हने याजक का काम करता था।",
			"9": "<sup>9</sup> तो याजकों की रीति के अनुसार उसके नाम पर चिट्ठी निकली, कि प्रभु के मन्दिर में जाकर धूप जलाए।",
			"10": "<sup>10</sup> और धूप जलाने के समय लोगों की सारी मण्डली बाहर प्रार्थना कर रही थी।",
			"11": "<sup>11</sup> कि प्रभु का एक स्वर्गदूत धूप की वेदी की दाहिनी ओर खड़ा हुआ उस को दिखाई दिया।",
			"12": "<sup>12</sup> और जकरयाह देखकर घबराया और उस पर बड़ा भय छा गया।",
			"13": "<sup>13</sup> परन्तु स्वर्गदूत ने उस से कहा, हे जकरयाह, भयभीत न हो क्योंकि तेरी प्रार्थना सुन ली गई है और तेरी पत्नी इलीशिबा से तेरे लिये एक पुत्र उत्पन्न होगा, और तू उसका नाम यूहन्ना रखना।",
			"14": "<sup>14</sup> और तुझे आनन्द और हर्ष होगा: और बहुत लोग उसके जन्म के कारण आनन्दित होंगे।",
			"15": "<sup>15</sup> क्योंकि वह प्रभु के साम्हने महान होगा; और दाखरस और मदिरा कभी न पिएगा; और अपनी माता के गर्भ ही से पवित्र आत्मा से परिपूर्ण हो जाएगा।",
			"16": "<sup>16</sup> और इस्राएलियों में से बहुतेरों को उन के प्रभु परमेश्वर की ओर फेरेगा।",
			"17": "<sup>17</sup> वह एलिय्याह की आत्मा और सामर्थ में हो कर उसके आगे आगे चलेगा, कि पितरों का मन लड़के बालों की ओर फेर दे; और आज्ञा न मानने वालों को धमिर्यों की समझ पर लाए; और प्रभु के लिये एक योग्य प्रजा तैयार करे।",
			"18": "<sup>18</sup> जकरयाह ने स्वर्गदूत से पूछा; यह मैं कैसे जानूं? क्योंकि मैं तो बूढ़ा हूं; और मेरी पत्नी भी बूढ़ी हो गई है।",
			"19": "<sup>19</sup> स्वर्गदूत ने उस को उत्तर दिया, कि मैं जिब्राईल हूं, जो परमेश्वर के साम्हने खड़ा रहता हूं; और मैं तुझ से बातें करने और तुझे यह सुसमाचार सुनाने को भेजा गया हूं।",
			"20": "<sup>20</sup> और देख जिस दिन तक ये बातें पूरी न हो लें, उस दिन तक तू मौन रहेगा, और बोल न सकेगा, इसलिये कि तू ने मेरी बातों की जो अपने समय पर पूरी होंगी, प्रतीति न की।",
			"21": "<sup>21</sup> और लोग जकरयाह की बाट देखते रहे और अचम्भा करने लगे कि उसे मन्दिर में ऐसी देर क्यों लगी?",
			"22": "<sup>22</sup> जब वह बाहर आया, तो उन से बोल न सका: सो वे जान गए, कि उस ने मन्दिर में कोई दर्शन पाया है; और वह उन से संकेत करता रहा, और गूंगा रह गया।",
			"23": "<sup>23</sup> जब उस की सेवा के दिन पूरे हुए, तो वह अपने घर चला गया॥",
			"24": "<sup>24</sup> इन दिनों के बाद उस की पत्नी इलीशिबा गर्भवती हुई; और पांच महीने तक अपने आप को यह कह के छिपाए रखा।",
			"25": "<sup>25</sup> कि मनुष्यों में मेरा अपमान दूर करने के लिये प्रभु ने इन दिनों में कृपा दृष्टि करके मेरे लिये ऐसा किया है॥",
			"26": "<sup>26</sup> छठवें महीने में परमेश्वर की ओर से जिब्राईल स्वर्गदूत गलील के नासरत नगर में एक कुंवारी के पास भेजा गया।",
			"27": "<sup>27</sup> जिस की मंगनी यूसुफ नाम दाऊद के घराने के एक पुरूष से हुई थी: उस कुंवारी का नाम मरियम था।",
			"28": "<sup>28</sup> और स्वर्गदूत ने उसके पास भीतर आकर कहा; आनन्द और जय तेरी हो, जिस पर ईश्वर का अनुग्रह हुआ है, प्रभु तेरे साथ है।",
			"29": "<sup>29</sup> वह उस वचन से बहुत घबरा गई, और सोचने लगी, कि यह किस प्रकार का अभिवादन है?",
			"30": "<sup>30</sup> स्वर्गदूत ने उस से कहा, हे मरियम; भयभीत न हो, क्योंकि परमेश्वर का अनुग्रह तुझ पर हुआ है।",
			"31": "<sup>31</sup> और देख, तू गर्भवती होगी, और तेरे एक पुत्र उत्पन्न होगा; तू उसका नाम यीशु रखना।",
			"32": "<sup>32</sup> वह महान होगा; और परमप्रधान का पुत्र कहलाएगा; और प्रभु परमेश्वर उसके पिता दाऊद का सिंहासन उस को देगा।",
			"33": "<sup>33</sup> और वह याकूब के घराने पर सदा राज्य करेगा; और उसके राज्य का अन्त न होगा।",
			"34": "<sup>34</sup> मरियम ने स्वर्गदूत से कहा, यह क्योंकर होगा? मैं तो पुरूष को जानती ही नहीं।",
			"35": "<sup>35</sup> स्वर्गदूत ने उस को उत्तर दिया; कि पवित्र आत्मा तुझ पर उतरेगा, और परमप्रधान की सामर्थ तुझ पर छाया करेगी इसलिये वह पवित्र जो उत्पन्न होनेवाला है, परमेश्वर का पुत्र कहलाएगा।",
			"36": "<sup>36</sup> और देख, और तेरी कुटुम्बिनी इलीशिबा के भी बुढ़ापे में पुत्र होनेवाला है, यह उसका, जो बांझ कहलाती थी छठवां महीना है।",
			"37": "<sup>37</sup> क्योंकि जो वचन परमेश्वर की ओर से होता है वह प्रभावरिहत नहीं होता।",
			"38": "<sup>38</sup> मरियम ने कहा, देख, मैं प्रभु की दासी हूं, मुझे तेरे वचन के अनुसार हो: तब स्वर्गदूत उसके पास से चला गया॥",
			"39": "<sup>39</sup> उन दिनों में मरियम उठकर शीघ्र ही पहाड़ी देश में यहूदा के एक नगर को गई।",
			"40": "<sup>40</sup> और जकरयाह के घर में जाकर इलीशिबा को नमस्कार किया।",
			"41": "<sup>41</sup> ज्योंही इलीशिबा ने मरियम का नमस्कार सुना, त्योंही बच्चा उसके पेट में उछला, और इलीशिबा पवित्र आत्मा से परिपूर्ण हो गई।",
			"42": "<sup>42</sup> और उस ने बड़े शब्द से पुकार कर कहा, तू स्त्रियों में धन्य है, और तेरे पेट का फल धन्य है।",
			"43": "<sup>43</sup> और यह अनुग्रह मुझे कहां से हुआ, कि मेरे प्रभु की माता मेरे पास आई?",
			"44": "<sup>44</sup> और देख ज्योंही तेरे नमस्कार का शब्द मेरे कानों में पड़ा त्योंही बच्चा मेरे पेट में आनन्द से उछल पड़ा।",
			"45": "<sup>45</sup> और धन्य है, वह जिस ने विश्वास किया कि जो बातें प्रभु की ओर से उस से कही गईं, वे पूरी होंगी।",
			"46": "<sup>46</sup> तब मरियम ने कहा, मेरा प्राण प्रभु की बड़ाई करता है।",
			"47": "<sup>47</sup> और मेरी आत्मा मेरे उद्धार करने वाले परमेश्वर से आनन्दित हुई।",
			"48": "<sup>48</sup> क्योंकि उस ने अपनी दासी की दीनता पर दृष्टि की है, इसलिये देखो, अब से सब युग युग के लोग मुझे धन्य कहेंगे।",
			"49": "<sup>49</sup> क्योंकि उस शक्तिमान ने मेरे लिये बड़े बड़े काम किए हैं, और उसका नाम पवित्र है।",
			"50": "<sup>50</sup> और उस की दया उन पर, जो उस से डरते हैं, पीढ़ी से पीढ़ी तक बनी रहती है।",
			"51": "<sup>51</sup> उस ने अपना भुजबल दिखाया, और जो अपने आप को बड़ा समझते थे, उन्हें तित्तर-बित्तर किया।",
			"52": "<sup>52</sup> उस ने बलवानों को सिंहासनों से गिरा दिया; और दीनों को ऊंचा किया।",
			"53": "<sup>53</sup> उस ने भूखों को अच्छी वस्तुओं से तृप्त किया, और धनवानों को छूछे हाथ निकाल दिया।",
			"54": "<sup>54</sup> उस ने अपने सेवक इस्राएल को सम्भाल लिया।",
			"55": "<sup>55</sup> कि अपनी उस दया को स्मरण करे, जो इब्राहीम और उसके वंश पर सदा रहेगी, जैसा उस ने हमारे बाप-दादों से कहा था।",
			"56": "<sup>56</sup> मरियम लगभग तीन महीने उसके साथ रहकर अपने घर लौट गई॥",
			"57": "<sup>57</sup> तब इलीशिबा के जनने का समय पूरा हुआ, और वह पुत्र जनी।",
			"58": "<sup>58</sup> उसके पड़ोसियों और कुटुम्बियों ने यह सुन कर, कि प्रभु ने उस पर बड़ी दया की है, उसके साथ आनन्दित हुए।",
			"59": "<sup>59</sup> और ऐसा हुआ कि आठवें दिन वे बालक का खतना करने आए और उसका नाम उसके पिता के नाम पर जकरयाह रखने लगे।",
			"60": "<sup>60</sup> और उस की माता ने उत्तर दिया कि नहीं; वरन उसका नाम यूहन्ना रखा जाए।",
			"61": "<sup>61</sup> और उन्होंने उस से कहा, तेरे कुटुम्ब में किसी का यह नाम नहीं।",
			"62": "<sup>62</sup> तब उन्होंने उसके पिता से संकेत करके पूछा।",
			"63": "<sup>63</sup> कि तू उसका नाम क्या रखना चाहता है? और उस ने लिखने की पट्टी मंगाकर लिख दिया, कि उसका नाम यूहन्ना है: और सभों ने अचम्भा किया।",
			"64": "<sup>64</sup> तब उसका मुंह और जीभ तुरन्त खुल गई; और वह बोलने और परमेश्वर का धन्यवाद करने लगा।",
			"65": "<sup>65</sup> और उसके आस पास के सब रहने वालों पर भय छा गया; और उन सब बातों की चर्चा यहूदिया के सारे पहाड़ी देश में फैल गई।",
			"66": "<sup>66</sup> और सब सुनने वालों ने अपने अपने मन में विचार करके कहा, यह बालक कैसा होगा क्योंकि प्रभु का हाथ उसके साथ था॥",
			"67": "<sup>67</sup> और उसका पिता जकरयाह पवित्र आत्मा से परिपूर्ण हो गया, और भविष्यद्ववाणी करने लगा।",
			"68": "<sup>68</sup> कि प्रभु इस्राएल का परमेश्वर धन्य हो, कि उस ने अपने लोगों पर दृष्टि की और उन का छुटकारा किया है।",
			"69": "<sup>69</sup> और अपने सेवक दाऊद के घराने में हमारे लिये एक उद्धार का सींग निकाला।",
			"70": "<sup>70</sup> जैसे उस ने अपने पवित्र भविष्यद्वक्ताओं के द्वारा जो जगत के आदि से होते आए हैं, कहा था।",
			"71": "<sup>71</sup> अर्थात हमारे शत्रुओं से, और हमारे सब बैरियों के हाथ से हमारा उद्धार किया है।",
			"72": "<sup>72</sup> कि हमारे बाप-दादों पर दया करके अपनी पवित्र वाचा का स्मरण करे।",
			"73": "<sup>73</sup> और वह शपथ जो उस ने हमारे पिता इब्राहीम से खाई थी।",
			"74": "<sup>74</sup> कि वह हमें यह देगा, कि हम अपने शत्रुओं के हाथ से छुटकर।",
			"75": "<sup>75</sup> उसके साम्हने पवित्रता और धामिर्कता से जीवन भर निडर रहकर उस की सेवा करते रहें।",
			"76": "<sup>76</sup> और तू हे बालक, परमप्रधान का भविष्यद्वक्ता कहलाएगा, क्योंकि तू प्रभु के मार्ग तैयार करने के लिये उसके आगे आगे चलेगा,",
			"77": "<sup>77</sup> कि उसके लोगों को उद्धार का ज्ञान दे, जो उन के पापों की क्षमा से प्राप्त होता है।",
			"78": "<sup>78</sup> यह हमारे परमेश्वर की उसी बड़ी करूणा से होगा; जिस के कारण ऊपर से हम पर भोर का प्रकाश उदय होगा।",
			"79": "<sup>79</sup> कि अन्धकार और मृत्यु की छाया में बैठने वालों को ज्योति दे, और हमारे पांवों को कुशल के मार्ग में सीधे चलाए॥",
			"80": "<sup>80</sup> और वह बालक बढ़ता और आत्मा में बलवन्त होता गया, और इस्राएल पर प्रगट होने के दिन तक जंगलों में रहा।"
		},
		"2": {
			"1": "<sup>1</sup> उन दिनों में औगूस्तुस कैसर की ओर से आज्ञा निकली, कि सारे जगत के लोगों के नाम लिखे जाएं।",
			"2": "<sup>2</sup> यह पहिली नाम लिखाई उस समय हुई, जब क्विरिनियुस सूरिया का हाकिम था।",
			"3": "<sup>3</sup> और सब लोग नाम लिखवाने के लिये अपने अपने नगर को गए।",
			"4": "<sup>4</sup> सो यूसुफ भी इसलिये कि वह दाऊद के घराने और वंश का था, गलील के नासरत नगर से यहूदिया में दाऊद के नगर बैतलहम को गया।",
			"5": "<sup>5</sup> कि अपनी मंगेतर मरियम के साथ जो गर्भवती थी नाम लिखवाए।",
			"6": "<sup>6</sup> उन के वहां रहते हुए उसके जनने के दिन पूरे हुए।",
			"7": "<sup>7</sup> और वह अपना पहिलौठा पुत्र जनी और उसे कपड़े में लपेटकर चरनी में रखा: क्योंकि उन के लिये सराय में जगह न थी।",
			"8": "<sup>8</sup> और उस देश में कितने गड़ेरिये थे, जो रात को मैदान में रहकर अपने झुण्ड का पहरा देते थे।",
			"9": "<sup>9</sup> और प्रभु का एक दूत उन के पास आ खड़ा हुआ; और प्रभु का तेज उन के चारों ओर चमका, और वे बहुत डर गए।",
			"10": "<sup>10</sup> तब स्वर्गदूत ने उन से कहा, मत डरो; क्योंकि देखो मैं तुम्हें बड़े आनन्द का सुसमाचार सुनाता हूं जो सब लोगों के लिये होगा।",
			"11": "<sup>11</sup> कि आज दाऊद के नगर में तुम्हारे लिये एक उद्धारकर्ता जन्मा है, और यही मसीह प्रभु है।",
			"12": "<sup>12</sup> और इस का तुम्हारे लिये यह पता है, कि तुम एक बालक को कपड़े में लिपटा हुआ और चरनी में पड़ा पाओगे।",
			"13": "<sup>13</sup> तब एकाएक उस स्वर्गदूत के साथ स्वर्गदूतों का दल परमेश्वर की स्तुति करते हुए और यह कहते दिखाई दिया।",
			"14": "<sup>14</sup> कि आकाश में परमेश्वर की महिमा और पृथ्वी पर उन मनुष्यों में जिनसे वह प्रसन्न है शान्ति हो॥",
			"15": "<sup>15</sup> जब स्वर्गदूत उन के पास से स्वर्ग को चले गए, तो गड़ेरियों ने आपस में कहा, आओ, हम बैतलहम जाकर यह बात जो हुई है, और जिसे प्रभु ने हमें बताया है, देखें।",
			"16": "<sup>16</sup> और उन्होंने तुरन्त जाकर मरियम और यूसुफ को और चरनी में उस बालक को पड़ा देखा।",
			"17": "<sup>17</sup> इन्हें देखकर उन्होंने वह बात जो इस बालक के विषय में उन से कही गई थी, प्रगट की।",
			"18": "<sup>18</sup> और सब सुनने वालों ने उन बातों से जो गड़िरयों ने उन से कहीं आश्चर्य किया।",
			"19": "<sup>19</sup> परन्तु मरियम ये सब बातें अपने मन में रखकर सोचती रही।",
			"20": "<sup>20</sup> और गड़ेरिये जैसा उन से कहा गया था, वैसा ही सब सुनकर और देखकर परमेश्वर की महिमा और स्तुति करते हुए लौट गए॥",
			"21": "<sup>21</sup> जब आठ दिन पूरे हुए, और उसके खतने का समय आया, तो उसका नाम यीशु रखा गया, जो स्वर्गदूत ने उसके पेट में आने से पहिले कहा था।",
			"22": "<sup>22</sup> और जब मूसा की व्यवस्था के अनुसार उन के शुद्ध होने के दिन पूरे हुए तो वे उसे यरूशलेम में ले गए, कि प्रभु के सामने लाएं।",
			"23": "<sup>23</sup> (जैसा कि प्रभु की व्यवस्था में लिखा है कि हर एक पहिलौठा प्रभु के लिये पवित्र ठहरेगा)।",
			"24": "<sup>24</sup> और प्रभु की व्यवस्था के वचन के अनुसार पंडुकों का एक जोड़ा, या कबूतर के दो बच्चे ला कर बलिदान करें।",
			"25": "<sup>25</sup> और देखो, यरूशलेम में शमौन नाम एक मनुष्य था, और वह मनुष्य धर्मी और भक्त था; और इस्राएल की शान्ति की बाट जोह रहा था, और पवित्र आत्मा उस पर था।",
			"26": "<sup>26</sup> और पवित्र आत्मा से उस को चितावनी हुई थी, कि जब तक तू प्रभु के मसीह को देख ने लेगा, तक तक मृत्यु को न देखेगा।",
			"27": "<sup>27</sup> और वह आत्मा के सिखाने से मन्दिर में आया; और जब माता-पिता उस बालक यीशु को भीतर लाए, कि उसके लिये व्यवस्था की रीति के अनुसार करें।",
			"28": "<sup>28</sup> तो उस ने उसे अपनी गोद में लिया और परमेश्वर का धन्यवाद करके कहा,",
			"29": "<sup>29</sup> हे स्वामी, अब तू अपने दास को अपने वचन के अनुसार शान्ति से विदा करता है।",
			"30": "<sup>30</sup> क्योंकि मेरी आंखो ने तेरे उद्धार को देख लिया है।",
			"31": "<sup>31</sup> जिसे तू ने सब देशों के लोगों के साम्हने तैयार किया है।",
			"32": "<sup>32</sup> कि वह अन्य जातियों को प्रकाश देने के लिये ज्योति, और तेरे निज लोग इस्राएल की महिमा हो।",
			"33": "<sup>33</sup> और उसका पिता और उस की माता इन बातों से जो उसके विषय में कही जाती थीं, आश्चर्य करते थे।",
			"34": "<sup>34</sup> तब शमौन ने उन को आशीष देकर, उस की माता मरियम से कहा; देख, वह तो इस्राएल में बहुतों के गिरने, और उठने के लिये, और एक ऐसा चिन्ह होने के लिये ठहराया गया है, जिस के विरोध में बातें की जाएगीं --",
			"35": "<sup>35</sup> वरन तेरा प्राण भी तलवार से वार पार छिद जाएगा-- इस से बहुत हृदयों के विचार प्रगट होंगे।",
			"36": "<sup>36</sup> और अशेर के गोत्र में से हन्नाह नाम फनूएल की बेटी एक भविष्यद्वक्तिन थी: वह बहुत बूढ़ी थी, और ब्याह होने के बाद सात वर्ष अपने पति के साथ रह पाई थी।",
			"37": "<sup>37</sup> वह चौरासी वर्ष से विधवा थी: और मन्दिर को नहीं छोड़ती थी पर उपवास और प्रार्थना कर करके रात-दिन उपासना किया करती थी।",
			"38": "<sup>38</sup> और वह उस घड़ी वहां आकर प्रभु का धन्यवाद करने लगी, और उन सभों से, जो यरूशलेम के छुटकारे की बाट जोहते थे, उसके विषय में बातें करने लगी।",
			"39": "<sup>39</sup> और जब वे प्रभु की व्यवस्था के अनुसार सब कुछ निपटा चुके तो गलील में अपने नगर नासरत को फिर चले गए॥",
			"40": "<sup>40</sup> और बालक बढ़ता, और बलवन्त होता, और बुद्धि से परिपूर्ण होता गया; और परमेश्वर का अनुग्रह उस पर था।",
			"41": "<sup>41</sup> उसके माता-पिता प्रति वर्ष फसह के पर्व में यरूशलेम को जाया करते थे।",
			"42": "<sup>42</sup> जब वह बारह वर्ष का हुआ, तो वे पर्व की रीति के अनुसार यरूशलेम को गए।",
			"43": "<sup>43</sup> और जब वे उन दिनों को पूरा करके लौटने लगे, तो वह लड़का यीशु यरूशलेम में रह गया; और यह उसके माता-पिता नहीं जानते थे।",
			"44": "<sup>44</sup> वे यह समझकर, कि वह और यात्रियों के साथ होगा, एक दिन का पड़ाव निकल गए: और उसे अपने कुटुम्बियों और जान-पहचानों में ढूंढ़ने लगे।",
			"45": "<sup>45</sup> पर जब नहीं मिला, तो ढूंढ़ते-ढूंढ़ते यरूशलेम को फिर लौट गए।",
			"46": "<sup>46</sup> और तीन दिन के बाद उन्होंने उसे मन्दिर में उपदेशकों के बीच में बैठे, उन की सुनते और उन से प्रश्न करते हुए पाया।",
			"47": "<sup>47</sup> और जितने उस की सुन रहे थे, वे सब उस की समझ और उसके उत्तरों से चकित थे।",
			"48": "<sup>48</sup> तब वे उसे देखकर चकित हुए और उस की माता ने उस से कहा; हे पुत्र, तू ने हम से क्यों ऐसा व्यवहार किया? देख, तेरा पिता और मैं कुढ़ते हुए तुझे ढूंढ़ते थे।",
			"49": "<sup>49</sup> उस ने उन से कहा; तुम मुझे क्यों ढूंढ़ते थे? क्या नहीं जानते थे, कि मुझे अपने पिता के भवन में होना अवश्य है?",
			"50": "<sup>50</sup> परन्तु जो बात उस ने उन से कही, उन्होंने उसे नहीं समझा।",
			"51": "<sup>51</sup> तब वह उन के साथ गया, और नासरत में आया, और उन के वश में रहा; और उस की माता ने ये सब बातें अपने मन में रखीं॥",
			"52": "<sup>52</sup> और यीशु बुद्धि और डील-डौल में और परमेश्वर और मनुष्यों के अनुग्रह में बढ़ता गया॥"
		},
		"3": {
			"1": "<sup>1</sup> तिबिरियुस कैसर के राज्य के पंद्रहवें वर्ष में जब पुन्तियुस पीलातुस यहूदिया का हाकिम था, और गलील में हेरोदेस नाम चौथाई का इतूरैया, और त्रखोनीतिस में, उसका भाई फिलेप्पुस, और अबिलेने में लिसानियास चौथाई के राजा थे।",
			"2": "<sup>2</sup> और जब हन्ना और कैफा महायाजक थे, उस समय परमेश्वर का वचन जंगल में जकरयाह के पुत्र यूहन्ना के पास पहुंचा।",
			"3": "<sup>3</sup> और वह यरदन के आस पास के सारे देश में आकर, पापों की क्षमा के लिये मन फिराव के बपतिस्मा का प्रचार करने लगा।",
			"4": "<sup>4</sup> जैसे यशायाह भविष्यद्वक्ता के कहे हुए वचनों की पुस्तक में लिखा है, कि जंगल में एक पुकारने वाले का शब्द हो रहा हे कि प्रभु का मार्ग तैयार करो, उस की सड़कें सीधी बनाओ।",
			"5": "<sup>5</sup> हर एक घाटी भर दी जाएगी, और हर एक पहाड़ और टीला नीचा किया जाएगा; और जो टेढ़ा है सीधा, और जो ऊंचा नीचा है वह चौरस मार्ग बनेगा।",
			"6": "<sup>6</sup> और हर प्राणी परमेश्वर के उद्धार को देखेगा॥",
			"7": "<sup>7</sup> जो भीड़ की भीड़ उस से बपतिस्मा लेने को निकल कर आती थी, उन से वह कहता था; हे सांप के बच्चों, तुम्हें किस ने जता दिया, कि आने वाले क्रोध से भागो।",
			"8": "<sup>8</sup> सो मन फिराव के योग्य फल लाओ: और अपने अपने मन में यह न सोचो, कि हमारा पिता इब्राहीम है; क्योंकि मैं तुम से कहता हूं, कि परमेश्वर इन पत्थरों से इब्राहीम के लिये सन्तान उत्पन्न कर सकता है।",
			"9": "<sup>9</sup> और अब ही कुल्हाड़ा पेड़ों की जड़ पर धरा है, इसलिये जो जो पेड़ अच्छा फल नहीं लाता, वह काटा और आग में झोंका जाता है।",
			"10": "<sup>10</sup> और लोगों ने उस से पूछा, तो हम क्या करें?",
			"11": "<sup>11</sup> उस ने उन्हें उतर दिया, कि जिस के पास दो कुरते हों वह उसके साथ जिस के पास नहीं हैं बांट दे और जिस के पास भोजन हो, वह भी ऐसा ही करे।",
			"12": "<sup>12</sup> और महसूल लेने वाले भी बपतिस्मा लेने आए, और उस से पूछा, कि हे गुरू, हम क्या करें?",
			"13": "<sup>13</sup> उस ने उन से कहा, जो तुम्हारे लिये ठहराया गया है, उस से अधिक न लेना।",
			"14": "<sup>14</sup> और सिपाहियों ने भी उस से यह पूछा, हम क्या करें? उस ने उन से कहा, किसी पर उपद्रव न करना, और न झूठा दोष लगाना, और अपनी मजदूरी पर सन्तोष करना॥",
			"15": "<sup>15</sup> जब लोग आस लगाए हुए थे, और सब अपने अपने मन में यूहन्ना के विषय में विचार कर रहे थे, कि क्या यही मसीह तो नहीं है।",
			"16": "<sup>16</sup> तो यूहन्ना ने उन सब से उत्तर में कहा: कि मैं तो तुम्हें पानी से बपतिस्मा देता हूं, परन्तु वह आनेवाला है, जो मुझ से शक्तिमान है; मैं तो इस योग्य भी नहीं, कि उसके जूतों का बन्ध खोल सकूं, वह तुम्हें पवित्र आत्मा और आग से बपतिस्मा देगा।",
			"17": "<sup>17</sup> उसका सूप, उसके हाथ में है; और वह अपना खलिहान अच्छी तरह से साफ करेगा; और गेहूं को अपने खत्ते में इकट्ठा करेगा, परन्तु भूसी को उस आग में जो बुझने की नहीं जला देगा॥",
			"18": "<sup>18</sup> सो वह बहुत सी शिक्षा दे देकर लोगों को सुसमाचार सुनाता रहा।",
			"19": "<sup>19</sup> परन्तु उस ने चौथाई देश के राजा हेरोदेस को उसके भाई फिलेप्पुस की पत्नी हेरोदियास के विषय, और सब कुकर्मों के विषय में जो उस ने किए थे, उलाहना दिया।",
			"20": "<sup>20</sup> इसलिये हेरोदेस ने उन सब से बढ़कर यह कुकर्म भी किया, कि यूहन्ना को बन्दीगृह में डाल दिया॥",
			"21": "<sup>21</sup> जब सब लोगों ने बपतिस्मा लिया, और यीशु भी बपतिस्मा लेकर प्रार्थना कर रहा था, तो आकाश खुल गया।",
			"22": "<sup>22</sup> और पवित्र आत्मा शारीरिक रूप में कबूतर की नाईं उस पर उतरा, और यह आकाशवाणी हुई, कि तू मेरा प्रिय पुत्र है, मैं तुझ से प्रसन्न हूं॥",
			"23": "<sup>23</sup> जब यीशु आप उपदेश करने लगा, जो लगभग तीस वर्ष की आयु का था और (जैसा समझा जाता था) यूसुफ का पुत्र था; और वह एली का।",
			"24": "<sup>24</sup> और वह मत्तात का, और वह लेवी का, और वह मलकी का, और वह यन्ना का, और वह यूसुफ का।",
			"25": "<sup>25</sup> और वह मत्तित्याह का, और वह आमोस का, और वह नहूम का, और वह असल्याह का, और वह नोगह का।",
			"26": "<sup>26</sup> और वह मात का, और वह मत्तित्याह का, और वह शिमी का, और वह योसेख का, और वह योदाह का।",
			"27": "<sup>27</sup> और वह यूहन्ना का, और वह रेसा का, और वह जरूब्बाबिल का, और वह शालतियेल का, और वह नेरी का।",
			"28": "<sup>28</sup> और वह मलकी का, और वह अद्दी का, और वह कोसाम का, और वह इलमोदाम का, और वह एर का।",
			"29": "<sup>29</sup> और वह येशू का, और वह इलाजार का, और वह योरीम का, ओर वह मत्तात का, और वह लेवी का।",
			"30": "<sup>30</sup> और वह शमौन का, और वह यहूदाह का, और वह यूसुफ का, और वह योनान का, और वह इलयाकीम का।",
			"31": "<sup>31</sup> और वह मलेआह का, और वह मिन्नाह का, और वह मत्तता का, और वह नातान का, और वह दाऊद का।",
			"32": "<sup>32</sup> और वह यिशै का, और वह ओबेद का, और वह बोअज का, और वह सलमोन का, और वह नहशोन का।",
			"33": "<sup>33</sup> और वह अम्मीनादाब का, और वह अरनी का, और वह हिस्रोन का, और वह फिरिस का, और वह यहूदाह का।",
			"34": "<sup>34</sup> और वह याकूब का, और वह इसहाक का, और वह इब्राहीम का, और वह तिरह का, और वह नाहोर का।",
			"35": "<sup>35</sup> और वह सरूग का, और वह रऊ का, और वह फिलिग का, और वह एबिर का, और वह शिलह का।",
			"36": "<sup>36</sup> और वह केनान का, वह अरफज्ञद का, और वह शेम का, वह नूह का, वह लिमिक का।",
			"37": "<sup>37</sup> और वह मथूशिलह का, और वह हनोक का, और वह यिरिद का, और वह महललेल का, और वह केनान का।",
			"38": "<sup>38</sup> और वह इनोश का, और वह शेत का, और वह आदम का, और वह परमेश्वर का था॥"
		},
		"4": {
			"1": "<sup>1</sup> फिर यीशु पवित्रआत्मा से भरा हुआ, यरदन से लैटा; और चालीस दिन तक आत्मा के सिखाने से जंगल में फिरता रहा; और शैतान उस की परीक्षा करता रहा।",
			"2": "<sup>2</sup> उन दिनों में उस ने कुछ न खाया और जब वे दिन पूरे हो गए, तो उसे भूख लगी।",
			"3": "<sup>3</sup> और शैतान ने उस से कहा; यदि तू परमेश्वर का पुत्र है, तो इस पत्थर से कह, कि रोटी बन जाए।",
			"4": "<sup>4</sup> यीशु ने उसे उत्तर दिया; कि लिखा है, मनुष्य केवल रोटी से जीवित न रहेगा।",
			"5": "<sup>5</sup> तब शैतान उसे ले गया और उस को पल भर में जगत के सारे राज्य दिखाए।",
			"6": "<sup>6</sup> और उस से कहा; मैं यह सब अधिकार, और इन का विभव तुझे दूंगा, क्योंकि वह मुझे सौंपा गया है: और जिसे चाहता हूं, उसी को दे देता हूं।",
			"7": "<sup>7</sup> इसलिये, यदि तू मुझे प्रणाम करे, तो यह सब तेरा हो जाएगा।",
			"8": "<sup>8</sup> यीशु ने उसे उत्तर दिया; लिखा है; कि तू प्रभु अपने परमेश्वर को प्रणाम कर; और केवल उसी की उपासना कर।",
			"9": "<sup>9</sup> तब उस ने उसे यरूशलेम में ले जाकर मन्दिर के कंगूरे पर खड़ा किया, और उस से कहा; यदि तू परमेश्वर का पुत्र है, तो अपने आप को यहां से नीचे गिरा दे।",
			"10": "<sup>10</sup> क्योंकि लिखा है, कि वह तेरे विषय में अपने स्वर्गदूतों को आज्ञा देगा, कि वे तेरी रक्षा करें।",
			"11": "<sup>11</sup> और वे तुझे हाथों हाथ उठा लेंगे ऐसा न हो कि तेरे पांव में पत्थर से ठेस लगे।",
			"12": "<sup>12</sup> यीशु ने उस को उत्तर दिया; यह भी कहा गया है, कि तू प्रभु अपने परमेश्वर की परीक्षा न करना।",
			"13": "<sup>13</sup> जब शैतान सब परीक्षा कर चुका, तब कुछ समय के लिये उसके पास से चला गया॥",
			"14": "<sup>14</sup> फिर यीशु आत्मा की सामर्थ से भरा हुआ गलील को लौटा, और उस की चर्चा आस पास के सारे देश में फैल गई।",
			"15": "<sup>15</sup> और वह उन की आराधनालयों में उपदेश करता रहा, और सब उस की बड़ाई करते थे॥",
			"16": "<sup>16</sup> और वह नासरत में आया; जहां पाला पोसा गया था; और अपनी रीति के अनुसार सब्त के दिन आराधनालय में जा कर पढ़ने के लिये खड़ा हुआ।",
			"17": "<sup>17</sup> यशायाह भविष्यद्वक्ता की पुस्तक उसे दी गई, और उस ने पुस्तक खोलकर, वह जगह निकाली जहां यह लिखा था।",
			"18": "<sup>18</sup> कि प्रभु का आत्मा मुझ पर है, इसलिये कि उस ने कंगालों को सुसमाचार सुनाने के लिये मेरा अभिषेक किया है, और मुझे इसलिये भेजा है, कि बन्धुओं को छुटकारे का और अन्धों को दृष्टि पाने का सुसमाचार प्रचार करूं और कुचले हुओं को छुड़ाऊं।",
			"19": "<sup>19</sup> और प्रभु के प्रसन्न रहने के वर्ष का प्रचार करूं।",
			"20": "<sup>20</sup> तब उस ने पुस्तक बन्द करके सेवक के हाथ में दे दी, और बैठ गया: और आराधनालय के सब लोगों की आंख उस पर लगी थीं।",
			"21": "<sup>21</sup> तब वह उन से कहने लगा, कि आज ही यह लेख तुम्हारे साम्हने पूरा हुआ है।",
			"22": "<sup>22</sup> और सब ने उसे सराहा, और जो अनुग्रह की बातें उसके मुंह से निकलती थीं, उन से अचम्भा किया; और कहने लगे; क्या यह यूसुफ का पुत्र नहीं?",
			"23": "<sup>23</sup> उस ने उन से कहा; तुम मुझ पर यह कहावत अवश्य कहोगे, कि हे वैद्य, अपने आप को अच्छा कर! जो कुछ हम ने सुना है कि कफरनहूम में किया गया है उसे यहां अपने देश में भी कर।",
			"24": "<sup>24</sup> और उस ने कहा; मैं तुम से सच कहता हूं, कोई भविष्यद्वक्ता अपने देश में मान-सम्मान नहीं पाता।",
			"25": "<sup>25</sup> और मैं तुम से सच कहता हूं, कि एलिय्याह के दिनों में जब साढ़े तीन वर्ष तक आकाश बन्द रहा, यहां तक कि सारे देश में बड़ा आकाल पड़ा, तो इस्राएल में बहुत सी विधवाएं थीं।",
			"26": "<sup>26</sup> पर एलिय्याह उन में से किसी के पास नहीं भेजा गया, केवल सैदा के सारफत में एक विधवा के पास।",
			"27": "<sup>27</sup> और इलीशा भविष्यद्वक्ता के समय इस्राएल में बहुत से कोढ़ी थे, पर नामान सूरयानी को छोड़ उन में से काई शुद्ध नहीं किया गया।",
			"28": "<sup>28</sup> ये बातें सुनते ही जितने आराधनालय में थे, सब क्रोध से भर गए।",
			"29": "<sup>29</sup> और उठकर उसे नगर से बाहर निकाला, और जिस पहाड़ पर उन का नगर बसा हुआ था, उस की चोटी पर ले चले, कि उसे वहां से नीचे गिरा दें।",
			"30": "<sup>30</sup> पर वह उन के बीच में से निकलकर चला गया॥",
			"31": "<sup>31</sup> फिर वह गलील के कफरनहूम नगर में गया, और सब्त के दिन लोगों को उपदेश दे रहा था।",
			"32": "<sup>32</sup> वे उस के उपदेश से चकित हो गए क्योंकि उसका वचन अधिकार सहित था।",
			"33": "<sup>33</sup> आराधनालय में एक मनुष्य था, जिस में अशुद्ध आत्मा थी।",
			"34": "<sup>34</sup> वह ऊंचे शब्द से चिल्ला उठा, हे यीशु नासरी, हमें तुझ से क्या काम? क्या तू हमें नाश करने आया है? मैं तुझे जानता हूं तू कौन है? तू परमेश्वर का पवित्र जन है।",
			"35": "<sup>35</sup> यीशु ने उसे डांटकर कहा, चुप रह: और उस में से निकल जा: तब दुष्टात्मा उसे बीच में पटककर बिना हानि पहुंचाए उस में से निकल गई।",
			"36": "<sup>36</sup> इस पर सब को अचम्भा हुआ, और वे आपस में बातें करके कहने लगे, यह कैसा वचन है कि वह अधिकार और सामर्थ के साथ अशुद्ध आत्माओं को आज्ञा देता है, और वे निकल जाती हैं।",
			"37": "<sup>37</sup> सो चारों ओर हर जगह उस की धूम मच गई॥",
			"38": "<sup>38</sup> वह आराधनालय में से उठकर शमौन के घर में गया और शमौन की सास को ज्वर चढ़ा हुआ था, और उन्होंने उसके लिये उस से बिनती की।",
			"39": "<sup>39</sup> उस ने उसके निकट खड़े होकर ज्वर को डांटा और वह उस पर से उतर गया और वह तुरन्त उठकर उन की सेवा टहल करने लगी॥",
			"40": "<sup>40</sup> सूरज डूबते समय जिन जिन के यहां लोग नाना प्रकार की बीमारियों में पड़े हुए थे, वे सब उन्हें उसके पास ले आए, और उस ने एक एक पर हाथ रखकर उन्हें चंगा किया।",
			"41": "<sup>41</sup> और दुष्टात्मा चिल्लाती और यह कहती हुई कि तू परमेश्वर का पुत्र है, बहुतों में से निकल गईं पर वह उन्हें डांटता और बोलने नहीं देता था, क्योंकि वे जानते थे, कि यह मसीह है॥",
			"42": "<sup>42</sup> जब दिन हुआ तो वह निकलकर एक जंगली जगह में गया, और भीड़ की भीड़ उसे ढूंढ़ती हुई उसके पास आई, और उसे रोकने लगी, कि हमारे पास से न जा।",
			"43": "<sup>43</sup> परन्तु उस ने उन से कहा; मुझे और और नगरों में भी परमेश्वर के राज्य का सुसमाचार सुनाना अवश्य है, क्योंकि मैं इसी लिये भेजा गया हूं॥",
			"44": "<sup>44</sup> और वह गलील के अराधनालयों में प्रचार करता रहा॥"
		},
		"5": {
			"1": "<sup>1</sup> जब भीड़ उस पर गिरी पड़ती थी, और परमेश्वर का वचन सुनती थी, और वह गन्नेसरत की झील के किनारे पर खड़ा था, तो ऐसा हुआ।",
			"2": "<sup>2</sup> कि उस ने झील के किनारे दो नावें लगी हुई देखीं, और मछुवे उन पर से उतरकर जाल धो रहे थे।",
			"3": "<sup>3</sup> उन नावों में से एक पर जो शमौन की थी, चढ़कर, उस ने उस से बिनती की, कि किनारे से थोड़ा हटा ले चले, तब वह बैठकर लोगों को नाव पर से उपदेश देने लगा।",
			"4": "<sup>4</sup> जब वे बातें कर चुका, तो शमौन से कहा, गहिरे में ले चल, और मछिलयां पकड़ने के लिये अपने जाल डालो।",
			"5": "<sup>5</sup> शमौन ने उसको उत्तर दिया, कि हे स्वामी, हम ने सारी रात मिहनत की और कुछ न पकड़ा; तौभी तेरे कहने से जाल डालूंगा।",
			"6": "<sup>6</sup> जब उन्होंने ऐसा किया, तो बहुत मछिलयां घेर लाए, और उन के जाल फटने लगे।",
			"7": "<sup>7</sup> इस पर उन्होंने अपने साथियों को जो दूसरी नाव पर थे, संकेत किया, कि आकर हमारी सहायता करो: और उन्होंने आकर, दोनो नाव यहां तक भर लीं कि वे डूबने लगीं।",
			"8": "<sup>8</sup> यह देखकर शमौन पतरस यीशु के पांवों पर गिरा, और कहा; हे प्रभु, मेरे पास से जा, क्योंकि मैं पापी मनुष्य हूं।",
			"9": "<sup>9</sup> क्योंकि इतनी मछिलयों के पकड़े जाने से उसे और उसके साथियों को बहुत अचम्भा हुआ।",
			"10": "<sup>10</sup> और वैसे ही जब्दी के पुत्र याकूब और यूहन्ना को भी, जो शमौन के सहभागी थे, अचम्भा हुआ: तब यीशु ने शमौन से कहा, मत डर: अब से तू मनुष्यों को जीवता पकड़ा करेगा।",
			"11": "<sup>11</sup> और व नावों को किनारे पर ले आए और सब कुछ छोड़कर उसके पीछे हो लिए॥",
			"12": "<sup>12</sup> जब वह किसी नगर में था, तो देखो, वहां कोढ़ से भरा हुआ एक मनुष्य था, और वह यीशु को देखकर मुंह के बल गिरा, और बिनती की; कि हे प्रभु यदि तू चाहे हो मुझे शुद्ध कर सकता है।",
			"13": "<sup>13</sup> उस ने हाथ बढ़ाकर उसे छूआ और कहा मैं चाहता हूं तू शुद्ध हो जा: और उसका कोढ़ तुरन्त जाता रहा।",
			"14": "<sup>14</sup> तब उस ने उसे चिताया, कि किसी से न कह, परन्तु जा के अपने आप को याजक को दिखा, और अपने शुद्ध होने के विषय में जो कुछ मूसा ने चढ़ावा ठहराया है उसे चढ़ा; कि उन पर गवाही हो।",
			"15": "<sup>15</sup> परन्तु उस की चर्चा और भी फैलती गई, और भीड़ की भीड़ उस की सुनने के लिये और अपनी बिमारियों से चंगे होने के लिये इकट्ठी हुई।",
			"16": "<sup>16</sup> परन्तु वह जंगलों में अलग जाकर प्रार्थना किया करता था॥",
			"17": "<sup>17</sup> और एक दिन ऐसा हुआ कि वह उपदेश दे रहा था, और फरीसी और व्यवस्थापक वहां बैठे हुए थे, जो गलील और यहूदिया के हर एक गांव से, और यरूशलेम से आए थे; और चंगा करने के लिये प्रभु की सामर्थ उसके साथ थी।",
			"18": "<sup>18</sup> और देखो कई लोग एक मनुष्य को जो झोले का मारा हुआ था, खाट पर लाए, और वे उसे भीतर ले जाने और यीशु के साम्हने रखने का उपाय ढूंढ़ रहे थे।",
			"19": "<sup>19</sup> और जब भीड़ के कारण उसे भीतर न ले जा सके तो उन्होंने कोठे पर चढ़ कर और खप्रैल हटाकर, उसे खाट समेत बीच में यीशु के साम्हने उतरा दिया।",
			"20": "<sup>20</sup> उस ने उन का विश्वास देखकर उस से कहा; हे मनुष्य, तेरे पाप क्षमा हुए।",
			"21": "<sup>21</sup> तब शास्त्री और फरीसी विवाद करने लगे, कि यह कौन है, जो परमेश्वर की निन्दा करता है? परमेश्वर का छोड़ कौन पापों की क्षमा कर सकता है?",
			"22": "<sup>22</sup> यीशु ने उन के मन की बातें जानकर, उन से कहा कि तुम अपने मनों में क्या विवाद कर रहे हो?",
			"23": "<sup>23</sup> सहज क्या है? क्या यह कहना, कि तेरे पाप क्षमा हुए, या यह कहना कि उठ, और चल फिर?",
			"24": "<sup>24</sup> परन्तु इसलिये कि तुम जानो कि मनुष्य के पुत्र को पृथ्वी पर पाप क्षमा करने का भी अधिकार है (उस ने उस झोले के मारे हुए से कहा), मैं तुझ से कहता हूं, उठ और अपनी खाट उठाकर अपने घर चला जा।",
			"25": "<sup>25</sup> वह तुरन्त उन के साम्हने उठा, और जिस पर वह पड़ा था उसे उठाकर, परमेश्वर की बड़ाई करता हुआ अपने घर चला गया।",
			"26": "<sup>26</sup> तब सब चकित हुए और परमेश्वर की बड़ाई करने लगे, और बहुत डरकर कहने लगे, कि आज हम ने अनोखी बातें देखी हैं॥",
			"27": "<sup>27</sup> और इसके बाद वह बाहर गया, और लेवी नाम एक चुंगी लेने वाले को चुंगी की चौकी पर बैठे देखा, और उस से कहा, मेरे पीछे हो ले।",
			"28": "<sup>28</sup> तब वह सब कुछ छोड़कर उठा, और उसके पीछे हो लिया।",
			"29": "<sup>29</sup> और लेवी ने अपने घर में उसके लिये बड़ी जेवनार की; और चुंगी लेने वालों की और औरों की जो उसके साथ भोजन करने बैठे थे एक बड़ी भीड़ थी।",
			"30": "<sup>30</sup> और फरीसी और उन के शास्त्री उस के चेलों से यह कहकर कुड़कुड़ाने लगे, कि तुम चुंगी लेने वालों और पापियों के साथ क्यों खाते-पीते हो?",
			"31": "<sup>31</sup> यीशु ने उन को उत्तर दिया; कि वैद्य भले चंगों के लिये नहीं, परन्तु बीमारों के लिये अवश्य है।",
			"32": "<sup>32</sup> मैं धमिर्यों को नहीं, परन्तु पापियों को मन फिराने के लिये बुलाने आया हूं।",
			"33": "<sup>33</sup> और उन्होंने उस से कहा, यूहन्ना के चेले तो बराबर उपवास रखते और प्रार्थना किया करते हैं, और वैसे ही फरीसियों के भी, परन्तु तेरे चेले तो खाते पीते हैं!",
			"34": "<sup>34</sup> यीशु ने उन से कहा; क्या तुम बरातियों से जब तक दूल्हा उन के साथ रहे, उपवास करवा सकते हो? ।",
			"35": "<sup>35</sup> परन्तु वे दिन आएंगे, जिन में दूल्हा उन से अलग किया जाएगा, तब वे उन दिनों में उपवास करेंगे।",
			"36": "<sup>36</sup> उस ने एक और दृष्टान्त भी उन से कहा; कि कोई मनुष्य नये पहिरावन में से फाड़कर पुराने पहिरावन में पैबन्द नहीं लगाता, नहीं तो नया फट जाएगा और वह पैबन्द पुराने में मेल भी नहीं खाएगा।",
			"37": "<sup>37</sup> और कोई नया दाखरस पुरानी मशकों में नही भरता, नहीं तो नया दाखरस मशकों को फाड़कर बह जाएगा, और मशकें भी नाश हो जाएंगी।",
			"38": "<sup>38</sup> परन्तु नया दाखरस नई मशकों में भरना चाहिये।",
			"39": "<sup>39</sup> कोई मनुष्य पुराना दाखरस पीकर नया नहीं चाहता क्योंकि वह कहता है, कि पुराना ही अच्छा है॥"
		},
		"6": {
			"1": "<sup>1</sup> फिर सब्त के दिन वह खेतों में से होकर जा रहा था, और उसके चेले बालें तोड़ तोड़कर, और हाथों से मल मल कर खाते जाते थे।",
			"2": "<sup>2</sup> तब फरीसियों में से कई एक कहने लगे, तुम वह काम क्यों करते हो जो सब्त के दिन करना उचित नहीं?",
			"3": "<sup>3</sup> यीशु ने उन का उत्तर दिया; क्या तुम ने यह नहीं पढ़ा, कि दाऊद ने जब वह और उसके साथी भूखे थे तो क्या किया?",
			"4": "<sup>4</sup> वह क्योंकर परमेश्वर के घर में गया, और भेंट की रोटियां लेकर खाईं, जिन्हें खाना याजकों को छोड़ और किसी को उचित नहीं, और अपने साथियों को भी दीं?",
			"5": "<sup>5</sup> और उस ने उन से कहा; मनुष्य का पुत्र सब्त के दिन का भी प्रभु है।",
			"6": "<sup>6</sup> और ऐसा हुआ कि किसी और सब्त के दिन को वह आराधनालय में जाकर उपदेश करने लगा; और वहां एक मनुष्य था, जिस का दाहिना हाथ सूखा था।",
			"7": "<sup>7</sup> शास्त्री और फरीसी उस पर दोष लगाने का अवसर पाने के लिये उस की ताक में थे, कि देखें कि वह सब्त के दिन चंगा करता है कि नहीं।",
			"8": "<sup>8</sup> परन्तु वह उन के विचार जानता था; इसलिये उसने सूखे हाथ वाले मनुष्य से कहा; उठ, बीच में खड़ा हो: वह उठ खड़ा हुआ।",
			"9": "<sup>9</sup> यीशु ने उन से कहा; मैं तुम से यह पूछता हूं कि सब्त के दिन क्या उचित है, भला करना या बुरा करना; प्राण को बचाना या नाश करना?",
			"10": "<sup>10</sup> और उस ने चारों ओर उन सभों को देखकर उस मनुष्य से कहा; अपना हाथ बढ़ा: उस ने ऐसा ही किया, और उसका हाथ फिर चंगा हो गया।",
			"11": "<sup>11</sup> परन्तु वे आपे से बाहर होकर आपस में विवाद करने लगे कि हम यीशु के साथ क्या करें?",
			"12": "<sup>12</sup> और उन दिनों में वह पहाड़ पर प्रार्थना करने को निकला, और परमेश्वर से प्रार्थना करने में सारी रात बिताई।",
			"13": "<sup>13</sup> जब दिन हुआ, तो उस ने अपने चेलों को बुलाकर उन में से बारह चुन लिए, और उन को प्रेरित कहा।",
			"14": "<sup>14</sup> और वे ये हैं शमौन जिस का नाम उस ने पतरस भी रखा; और उसका भाई अन्द्रियास और याकूब और यूहन्ना और फिलेप्पुस और बरतुलमै।",
			"15": "<sup>15</sup> और मत्ती और थोमा और हलफई का पुत्र याकूब और शमौन जो जेलोतेस कहलाता है।",
			"16": "<sup>16</sup> और याकूब का बेटा यहूदा और यहूदा इसकिरयोती, जो उसका पकड़वाने वाला बना।",
			"17": "<sup>17</sup> तब वह उन के साथ उतरकर चौरस जगह में खड़ा हुआ, और उसके चेलों की बड़ी भीड़, और सारे यहूदिया और यरूशलेम और सूर और सैदा के समुद्र के किनारे से बहुतेरे लोग, जो उस की सुनने और अपनी बीमारियों से चंगा होने के लिय उसके पास आए थे, वहां थे।",
			"18": "<sup>18</sup> और अशुद्ध आत्माओं के सताए हुए लोग भी अच्छे किए जाते थे।",
			"19": "<sup>19</sup> और सब उसे छूना चाहते थे, क्योंकि उस में से सामर्थ निकलकर सब को चंगा करती थी॥",
			"20": "<sup>20</sup> तब उस ने अपने चेलों की ओर देखकर कहा; धन्य हो तुम, जो दीन हो, क्योंकि परमेश्वर का राज्य तुम्हारा है।",
			"21": "<sup>21</sup> धन्य हो तुम, जो अब भूखे हो; क्योंकि तृप्त किए जाओगे; धन्य हो तुम, जो अब रोते हो, क्योंकि हंसोगे।",
			"22": "<sup>22</sup> धन्य हो तुम, जब मनुष्य के पुत्र के कारण लोग तुम से बैर करेंगे, और तुम्हें निकाल देंगे, और तुम्हारी निन्दा करेंगे, और तुम्हारा नाम बुरा जानकर काट देंगे।",
			"23": "<sup>23</sup> उस दिन आनन्दित होकर उछलना, क्योंकि देखो, तुम्हारे लिये स्वर्ग में बड़ा प्रतिफल है: उन के बाप-दादे भविष्यद्वक्ताओं के साथ भी वैसा ही किया करते थे।",
			"24": "<sup>24</sup> परन्तु हाय तुम पर; जो धनवान हो, क्योंकि तुम अपनी शान्ति पा चुके।",
			"25": "<sup>25</sup> परन्तु हाय तुम पर; जो अब तृप्त हो, क्योंकि भूखे होगे: हाय, तुम पर; जो अब हंसते हो, क्योंकि शोक करोगे और रोओगे।",
			"26": "<sup>26</sup> हाय, तुम पर; जब सब मनुष्य तुम्हें भला कहें, क्योंकि उन के बाप-दादे झूठे भविष्यद्वक्ताओं के साथ भी ऐसा ही किया करते थे॥",
			"27": "<sup>27</sup> परन्तु मैं तुम सुनने वालों से कहता हूं, कि अपने शत्रुओं से प्रेम रखो; जो तुम से बैर करें, उन का भला करो।",
			"28": "<sup>28</sup> जो तुम्हें स्राप दें, उन को आशीष दो: जो तुम्हारा अपमान करें, उन के लिये प्रार्थना करो।",
			"29": "<sup>29</sup> जो तेरे एक गाल पर थप्पड़ मारे उस की ओर दूसरा भी फेर दे; और जो तेरी दोहर छीन ले, उस को कुरता लेने से भी न रोक।",
			"30": "<sup>30</sup> जो कोई तुझ से मांगे, उसे दे; और जो तेरी वस्तु छीन ले, उस से न मांग।",
			"31": "<sup>31</sup> और जैसा तुम चाहते हो कि लोग तुम्हारे साथ करें, तुम भी उन के साथ वैसा ही करो।",
			"32": "<sup>32</sup> यदि तुम अपने प्रेम रखने वालों के साथ प्रेम रखो, तो तुम्हारी क्या बड़ाई? क्योंकि पापी भी अपने प्रेम रखने वालों के साथ प्रेम रखते हैं।",
			"33": "<sup>33</sup> और यदि तुम अपने भलाई करने वालों ही के साथ भलाई करते हो, तो तुम्हारी क्या बड़ाई? क्योंकि पापी भी ऐसा ही करते हैं।",
			"34": "<sup>34</sup> और यदि तुम उसे उधार दो, जिन से फिर पाने की आशा रखते हो, तो तुम्हारी क्या बड़ाई? क्योंकि पापी पापियों को उधार देते हैं, कि उतना ही फिर पाएं।",
			"35": "<sup>35</sup> वरन अपने शत्रुओं से प्रेम रखो, और भलाई करो: और फिर पाने की आस न रखकर उधार दो; और तुम्हारे लिये बड़ा फल होगा; और तुम परमप्रधान के सन्तान ठहरोगे, क्योंकि वह उन पर जो धन्यवाद नहीं करते और बुरों पर भी कृपालु है।",
			"36": "<sup>36</sup> जैसा तुम्हारा पिता दयावन्त है, वैसे ही तुम भी दयावन्त बनो।",
			"37": "<sup>37</sup> दोष मत लगाओ; तो तुम पर भी दोष नहीं लगाया जाएगा: दोषी न ठहराओ, तो तुम भी दोषी नहीं ठहराए जाओगे: क्षमा करो, तो तुम्हारी भी क्षमा की जाएगी।",
			"38": "<sup>38</sup> दिया करो, तो तुम्हें भी दिया जाएगा: लोग पूरा नाप दबा दबाकर और हिला हिलाकर और उभरता हुआ तुम्हारी गोद में डालेंगे, क्योंकि जिस नाप से तुम नापते हो, उसी से तुम्हारे लिये भी नापा जाएगा॥",
			"39": "<sup>39</sup> फिर उस ने उन से एक दृष्टान्त कहा; क्या अन्धा, अन्धे को मार्ग बता सकता है? क्या दोनो गड़हे में नहीं गिरेंगे?",
			"40": "<sup>40</sup> चेला अपने गुरू से बड़ा नहीं, परन्तु जो कोई सिद्ध होगा, वह अपने गुरू के समान होगा।",
			"41": "<sup>41</sup> तू अपने भाई की आंख के तिनके को क्यों देखता है, और अपनी ही आंख का लट्ठा तुझे नहीं सूझता",
			"42": "<sup>42</sup> और जब तू अपनी ही आंख का लट्ठा नहीं देखता, तो अपने भाई से क्योंकर कह सकता है, हे भाई, ठहर जा तेरी आंख से तिनके को निकाल दूं? हे कपटी, पहिले अपनी आंख से लट्ठा निकाल, तब जो तिनका तेरे भाई की आंख में है, भली भांति देखकर निकाल सकेगा।",
			"43": "<sup>43</sup> कोई अच्छा पेड़ नहीं, जो निकम्मा फल लाए, और न तो कोई निकम्मा पेड़ है, जो अच्छा फल लाए।",
			"44": "<sup>44</sup> हर एक पेड़ अपने फल से पहचाना जाता है; क्योंकि लोग झाड़ियों से अंजीर नहीं तोड़ते, और न झड़बेरी से अंगूर।",
			"45": "<sup>45</sup> भला मनुष्य अपने मन के भले भण्डार से भली बातें निकालता है; और बुरा मनुष्य अपने मन के बुरे भण्डार से बुरी बातें निकालता है; क्योंकि जो मन में भरा है वही उसके मुंह पर आता है॥",
			"46": "<sup>46</sup> जब तुम मेरा कहना नहीं मानते, तो क्यों मुझे हे प्रभु, हे प्रभु, कहते हो?",
			"47": "<sup>47</sup> जो कोई मेरे पास आता है, और मेरी बातें सुनकर उन्हें मानता है, मैं तुम्हें बताता हूं कि वह किस के समान है",
			"48": "<sup>48</sup> वह उस मनुष्य के समान है, जिस ने घर बनाते समय भूमि गहरी खोदकर चट्टान की नेव डाली, और जब बाढ़ आई तो धारा उस घर पर लगी, परन्तु उसे हिला न सकी; क्योंकि वह पक्का बना था।",
			"49": "<sup>49</sup> परन्तु जो सुनकर नहीं मानता, वह उस मनुष्य के समान है, जिस ने मिट्टी पर बिना नेव का घर बनाया। जब उस पर धारा लगी, तो वह तुरन्त गिर पड़ा, और वह गिरकर सत्यानाश हो गया॥"
		},
		"7": {
			"1": "<sup>1</sup> जब वह लोगों को अपनी सारी बातें सुना चुका, तो कफरनहूम में आया।",
			"2": "<sup>2</sup> और किसी सूबेदार का एक दास जो उसका प्रिय था, बीमारी से मरने पर था।",
			"3": "<sup>3</sup> उस ने यीशु की चर्चा सुनकर यहूदियों के कई पुरनियों को उस से यह बिनती करने को उसके पास भेजा, कि आकर मेरे दास को चंगा कर।",
			"4": "<sup>4</sup> वे यीशु के पास आकर उस से बड़ी बिनती करके कहने लगे, कि वह इस योग्य है, कि तू उसके लिये यह करे।",
			"5": "<sup>5</sup> क्योंकि वह हमारी जाति से प्रेम रखता है, और उसी ने हमारे आराधनालय को बनाया है।",
			"6": "<sup>6</sup> यीशु उन के साथ साथ चला, पर जब वह घर से दूर न था, तो सूबेदार ने उसके पास कई मित्रों के द्वारा कहला भेजा, कि हे प्रभु दुख न उठा, क्योंकि मैं इस योग्य नहीं, कि तू मेरी छत के तले आए।",
			"7": "<sup>7</sup> इसी कारण मैं ने अपने आप को इस योग्य भी न समझा, कि तेरे पास आऊं, पर वचन ही कह दे तो मेरा सेवक चंगा हो जाएगा।",
			"8": "<sup>8</sup> मैं भी पराधीन मनुष्य हूं; और सिपाही मेरे हाथ में हैं, और जब एक को कहता हूं, जा, तो वह जाता है, और दूसरे से कहता हूं कि आ, तो आता है; और अपने किसी दास को कि यह कर, तो वह उसे करता है।",
			"9": "<sup>9</sup> यह सुनकर यीशु ने अचम्भा किया, और उस ने मुंह फेरकर उस भीड़ से जो उसके पीछे आ रही थी कहा, मैं तुम से कहता हूं, कि मैं ने इस्राएल में भी ऐसा विश्वास नहीं पाया।",
			"10": "<sup>10</sup> और भेजे हुए लोगों ने घर लौटकर, उस दास को चंगा पाया॥",
			"11": "<sup>11</sup> थोड़े दिन के बाद वह नाईंन नाम के एक नगर को गया, और उसके चेले, और बड़ी भीड़ उसके साथ जा रही थी।",
			"12": "<sup>12</sup> जब वह नगर के फाटक के पास पहुंचा, तो देखो, लोग एक मुरदे को बाहर लिए जा रहे थे; जो अपनी मां का एकलौता पुत्र था, और वह विधवा थी: और नगर के बहुत से लोग उसके साथ थे।",
			"13": "<sup>13</sup> उसे देख कर प्रभु को तरस आया, और उस से कहा; मत रो।",
			"14": "<sup>14</sup> तब उस ने पास आकर, अर्थी को छुआ; और उठाने वाले ठहर गए, तब उस ने कहा; हे जवान, मैं तुझ से कहता हूं, उठ।",
			"15": "<sup>15</sup> तब वह मुरदा उठ बैठा, और बोलने लगा: और उस ने उसे उस की मां को सौप दिया।",
			"16": "<sup>16</sup> इस से सब पर भय छा गया; और वे परमेश्वर की बड़ाई करके कहने लगे कि हमारे बीच में एक बड़ा भविष्यद्वक्ता उठा है, और परमेश्वर ने अपने लोगों पर कृपा दृष्टि की है।",
			"17": "<sup>17</sup> और उसके विषय में यह बात सारे यहूदिया और आस पास के सारे देश में फैल गई॥",
			"18": "<sup>18</sup> और यूहन्ना को उसके चेलों ने इन सब बातों का समचार दिया।",
			"19": "<sup>19</sup> तब यूहन्ना ने अपने चेलों में से दो को बुलाकर प्रभु के पास यह पूछने के लिये भेजा; कि क्या आनेवाला तू ही है, या हम किसी और दूसरे की बाट देखें?",
			"20": "<sup>20</sup> उन्होंने उसके पास आकर कहा, यूहन्ना बपतिस्मा देने वाले ने हमें तेरे पास यह पूछने को भेजा है, कि क्या आनेवाला तू ही है, या हम दूसरे की बाट जोहें?",
			"21": "<sup>21</sup> उसी घड़ी उस ने बहुतों को बीमारियों; और पीड़ाओं, और दुष्टात्माओं से छुड़ाया; और बहुत से अन्धों को आंखे दी।",
			"22": "<sup>22</sup> और उस ने उन से कहा; जो कुछ तुम ने देखा और सुना है, जाकर यूहन्ना से कह दो; कि अन्धे देखते हैं, लंगड़े चलते फिरते हैं, कोढ़ी शुद्ध किए जाते हैं, बहिरे सुनते हैं, मुरदे जिलाये जाते हैं; और कंगालों को सुसमाचार सुनाया जाता है।",
			"23": "<sup>23</sup> और धन्य है वह, जो मेरे कारण ठोकर न खाए॥",
			"24": "<sup>24</sup> जब यूहन्ना के भेजे हुए लोग चल दिए, तो यीशु यूहन्ना के विषय में लोगों से कहने लगा, तुम जंगल में क्या देखने गए थे? क्या हवा से हिलते हुए सरकण्डे को?",
			"25": "<sup>25</sup> तो तुम फिर क्या देखने गए थे? क्या कोमल वस्त्र पहिने हुए मनुष्य को? देखो, जो भड़कीला वस्त्र पहिनते, और सुख विलास से रहते हैं, वे राजभवनों में रहते हैं।",
			"26": "<sup>26</sup> तो फिर क्या देखने गए थे? क्या किसी भविष्यद्वक्ता को? हां, मैं तुम से कहता हूं, वरन भविष्यद्वक्ता से भी बड़े को।",
			"27": "<sup>27</sup> यह वही है, जिस के विषय में लिखा है, कि देख, मैं अपने दूत को तेरे आगे आगे भेजता हूं, जो तेरे आगे मार्ग सीधा करेगा।",
			"28": "<sup>28</sup> मैं तुम से कहता हूं, कि जो स्त्रियों से जन्मे हैं, उन में से यूहन्ना से बड़ा कोई नहीं: पर जो परमेश्वर के राज्य में छोटे से छोटा है, वह उस से भी बड़ा है।",
			"29": "<sup>29</sup> और सब साधारण लोगों ने सुनकर और चुंगी लेने वालों ने भी यूहन्ना का बपतिस्मा लेकर परमेश्वर को सच्चा मान लिया।",
			"30": "<sup>30</sup> पर फरीसियों और व्यवस्थापकों ने उस से बपतिस्मा न लेकर परमेश्वर की मनसा को अपने विषय में टाल दिया।",
			"31": "<sup>31</sup> सो मैं इस युग के लोगों की उपमा किस से दूं कि वे किस के समान हैं?",
			"32": "<sup>32</sup> वे उन बालकों के समान हैं जो बाजार में बैठे हुए एक दूसरे से पुकारकर कहते हैं, कि हम ने तुम्हारे लिये बांसली बजाई, और तुम न नाचे, हम ने विलाप किया, और तुम न रोए।",
			"33": "<sup>33</sup> क्योंकि यूहन्ना बपतिस्मा देने वाला न रोटी खाता आया, न दाखरस पीता आया, और तुम कहते हो, उस में दुष्टात्मा है।",
			"34": "<sup>34</sup> मनुष्य का पुत्र खाता-पीता आया है; और तुम कहते हो, देखो, पेटू और पियक्कड़ मनुष्य, चुंगी लेने वालों का और पापियों का मित्र।",
			"35": "<sup>35</sup> पर ज्ञान अपनी सब सन्तानों से सच्चा ठहराया गया है॥",
			"36": "<sup>36</sup> फिर किसी फरीसी ने उस से बिनती की, कि मेरे साथ भोजन कर; सो वह उस फरीसी के घर में जाकर भोजन करने बैठा।",
			"37": "<sup>37</sup> और देखो, उस नगर की एक पापिनी स्त्री यह जानकर कि वह फरीसी के घर में भोजन करने बैठा है, संगमरमर के पात्र में इत्र लाई।",
			"38": "<sup>38</sup> और उसके पांवों के पास, पीछे खड़ी होकर, रोती हुई, उसके पांवों को आंसुओं से भिगाने और अपने सिर के बालों से पोंछने लगी और उसके पांव बारबार चूमकर उन पर इत्र मला।",
			"39": "<sup>39</sup> यह देखकर, वह फरीसी जिस ने उसे बुलाया था, अपने मन में सोचने लगा, यदि यह भविष्यद्वक्ता होता तो जान लेता, कि यह जो उसे छू रही है, वह कौन और कैसी स्त्री है? क्योंकि वह तो पापिनी है।",
			"40": "<sup>40</sup> यह सुन यीशु ने उसके उत्तर में कहा; कि हे शमौन मुझे तुझ से कुछ कहना है वह बोला, हे गुरू कह।",
			"41": "<sup>41</sup> किसी महाजन के दो देनदार थे, एक पांच सौ, और दूसरा पचास दीनार धारता था।",
			"42": "<sup>42</sup> जब कि उन के पास पटाने को कुछ न रहा, तो उस ने दोनो को क्षमा कर दिया: सो उन में से कौन उस से अधिक प्रेम रखेगा।",
			"43": "<sup>43</sup> शमौन ने उत्तर दिया, मेरी समझ में वह, जिस का उस ने अधिक छोड़ दिया: उस ने उस से कहा, तू ने ठीक विचार किया है।",
			"44": "<sup>44</sup> और उस स्त्री की ओर फिरकर उस ने शमौन से कहा; क्या तू इस स्त्री को देखता है मैं तेरे घर में आया परन्तु तू ने मेरे पांव धाने के लिये पानी न दिया, पर इस ने मेरे पांव आंसुओं से भिगाए, और अपने बालों से पोंछा!",
			"45": "<sup>45</sup> तू ने मुझे चूमा न दिया, पर जब से मैं आया हूं तब से इस ने मेरे पांवों का चूमना न छोड़ा।",
			"46": "<sup>46</sup> तू ने मेरे सिर पर तेल नहीं मला; पर इस ने मेरे पांवों पर इत्र मला है।",
			"47": "<sup>47</sup> इसलिये मैं तुझ से कहता हूं; कि इस के पाप जो बहुत थे, क्षमा हुए, क्योंकि इस ने बहुत प्रेम किया; पर जिस का थोड़ा क्षमा हुआ है, वह थोड़ा प्रेम करता है।",
			"48": "<sup>48</sup> और उस ने स्त्री से कहा, तेरे पाप क्षमा हुए।",
			"49": "<sup>49</sup> तब जो लोग उसके साथ भोजन करने बैठे थे, वे अपने अपने मन में सोचने लगे, यह कौन है जो पापों को भी क्षमा करता है?",
			"50": "<sup>50</sup> पर उस ने स्त्री से कहा, तेरे विश्वास ने तुझे बचा लिया है, कुशल से चली जा॥"
		},
		"8": {
			"1": "<sup>1</sup> इस के बाद वह नगर नगर और गांव गांव प्रचार करता हुआ, और परमेश्वर के राज्य का सुसमाचार सुनाता हुआ, फिरने लगा।",
			"2": "<sup>2</sup> और वे बारह उसके साथ थे: और कितनी स्त्रियां भी जो दुष्टात्माओं से और बीमारियों से छुड़ाई गई थीं, और वे यह हैं, मरियम जो मगदलीनी कहलाती थी, जिस में से सात दुष्टात्माएं निकली थीं।",
			"3": "<sup>3</sup> और हेरोदेस के भण्डारी खोजा की पत्नी योअन्ना और सूसन्नाह और बहुत सी और स्त्रियां: ये तो अपनी सम्पत्ति से उस की सेवा करती थीं॥",
			"4": "<sup>4</sup> जब बड़ी भीड़ इकट्ठी हुई, और नगर नगर के लोग उसके पास चले आते थे, तो उस ने दृष्टान्त में कहा।",
			"5": "<sup>5</sup> कि एक बोने वाला बीज बोने निकला: बोते हुए कुछ मार्ग के किनारे गिरा, और रौंदा गया, और आकाश के पक्षियों ने उसे चुग लिया।",
			"6": "<sup>6</sup> और कुछ चट्टान पर गिरा, और उपजा, परन्तु तरी न मिलने से सूख गया।",
			"7": "<sup>7</sup> कुछ झाड़ियों के बीच में गिरा, और झाड़ियों ने साथ साथ बढ़कर उसे दबा लिया।",
			"8": "<sup>8</sup> और कुछ अच्छी भूमि पर गिरा, और उगकर सौ गुणा फल लाया: यह कहकर उस ने ऊंचे शब्द से कहा; जिस के सुनने के कान होंवह सुन ले॥",
			"9": "<sup>9</sup> उसके चेलों ने उस से पूछा, कि यह दृष्टान्त क्या है? उस ने कहा;",
			"10": "<sup>10</sup> तुम को परमेश्वर के राज्य के भेदोंकी समझ दी गई है, पर औरों को दृष्टान्तों में सुनाया जाता है, इसलिये कि वे देखते हुए भी न देखें, और सुनते हुए भी न समझें।",
			"11": "<sup>11</sup> दृष्टान्त यह है; बीज तो परमेश्वर का वचन है।",
			"12": "<sup>12</sup> मार्ग के किनरे के वे हैं, जिन्हों ने सुना; तब शैतान आकर उन के मन में से वचन उठा ले जाता है, कि कहीं ऐसा न हो कि वे विश्वास करके उद्धार पाएं।",
			"13": "<sup>13</sup> चट्टान पर के वे हैं, कि जब सुनते हैं, तो आनन्द से वचन को ग्रहण तो करते हैं, परन्तु जड़ न पकड़ने से वे थोड़ी देर तक विश्वास रखते हैं, और परीक्षा के समय बहक जाते हैं।",
			"14": "<sup>14</sup> जो झाड़ियों में गिरा, सो वे हैं, जो सुनते हैं, पर होते होते चिन्ता और धन और जीवन के सुख विलास में फंस जाते हैं, और उन का फल नहीं पकता।",
			"15": "<sup>15</sup> पर अच्छी भूमि में के वे हैं, जो वचन सुनकर भले और उत्तम मन में सम्भाले रहते हैं, और धीरज से फल लाते हैं॥",
			"16": "<sup>16</sup> कोई दीया बार के बरतन से नहीं छिपाता, और न खाट के नीचे रखता है, परन्तु दीवट पर रखता है, कि भीतर आने वाले प्रकाश पांए।",
			"17": "<sup>17</sup> कुछ छिपा नहीं, जो प्रगट न हो; और न कुछ गुप्त है, जो जाना न जाए, और प्रगट न हो।",
			"18": "<sup>18</sup> इसलिये चौकस रहो, कि तुम किस रीति से सुनते हो क्योंकि जिस के पास है, उसे दिया जाएगा; और जिस के पास नहीं है, उस से वे वह भी ले लिया जाएगा, जिसे वह अपना समझता है॥",
			"19": "<sup>19</sup> उस की माता और भाई उसके पास आए, पर भीड़ के कारण उस से भेंट न कर सके।",
			"20": "<sup>20</sup> और उस से कहा गया, कि तेरी माता और तेरे भाई बाहर खड़े हुए तुझ से मिलना चाहते हैं।",
			"21": "<sup>21</sup> उस ने उसके उत्तर में उन से कहा कि मेरी माता और मेरे भाई ये ही हैं, जो परमेश्वर का वचन सुनते और मानते हैं॥",
			"22": "<sup>22</sup> फिर एक दिन वह और उसके चेले नाव पर चढ़े, और उस ने उन से कहा; कि आओ, झील के पार चलें: सो उन्होंने नाव खोल दी।",
			"23": "<sup>23</sup> पर जब नाव चल रही थी, तो वह सो गया: और झील पर आन्धी आई, और नाव पानी से भरने लगी और वे जोखिम में थे।",
			"24": "<sup>24</sup> तब उन्होंने पास आकर उसे जगाया, और कहा; हे स्वामी! स्वामी! हम नाश हुए जाते हैं: तब उस ने उठकर आन्धी को और पानी की लहरों को डांटा और वे थम गए, और चैन हो गया।",
			"25": "<sup>25</sup> और उस ने उन से कहा; तुम्हारा विश्वास कहां था? पर वे डर गए, और अचम्भित होकर आपस में कहने लगे, यह कौन है जो आन्धी और पानी को भी आज्ञा देता है, और वे उस की मानते हैं॥",
			"26": "<sup>26</sup> फिर वे गिरासेनियों के देश में पहुंचे, जो उस पार गलील के साम्हने है।",
			"27": "<sup>27</sup> जब वह किनारे पर उतरा, तो उस नगर का एक मनुष्य उसे मिला, जिस में दुष्टात्माएं थीं और बहुत दिनों से न कपड़े पहिनता था और न घर में रहता था वरन कब्रों में रहा करता था।",
			"28": "<sup>28</sup> वह यीशु को देखकर चिल्लाया, और उसके साम्हने गिरकर ऊंचे शब्द से कहा; हे परम प्रधान परमेश्वर के पुत्र यीशु, मुझे तुझ से क्या काम! मैं तेरी बिनती करता हूं, मुझे पीड़ा न दे!",
			"29": "<sup>29</sup> क्योंकि वह उस अशुद्ध आत्मा को उस मनुष्य में से निकलने की आज्ञा दे रहा था, इसलिये कि वह उस पर बार बार प्रबल होती थी; और यद्यपि लोग उसे सांकलों और बेडिय़ों से बांधते थे, तौभी वह बन्धनों को तोड़ डालता था, और दुष्टात्मा उसे जंगल में भगाये फिरती थी।",
			"30": "<sup>30</sup> यीशु ने उस से पूछा; तेरा क्या नाम है? उसने कहा, सेना; क्योंकि बहुत दुष्टात्माएं उसमें पैठ गईं थीं।",
			"31": "<sup>31</sup> और उन्होंने उस से बिनती की, कि हमें अथाह गड़हे में जाने की आज्ञा न दे।",
			"32": "<sup>32</sup> वहां पहाड़ पर सूअरों का एक बड़ा झुण्ड चर रहा था, सो उन्होंने उस से बिनती की, कि हमें उन में पैठने दे, सो उस ने उन्हें जाने दिया।",
			"33": "<sup>33</sup> तब दुष्टात्माएं उस मनुष्य से निकल कर सूअरों में गईं और वह झुण्ड कड़ाडे पर से झपटकर झील में जा गिरा और डूब मरा।",
			"34": "<sup>34</sup> चरवाहे यह जो हुआ था देखकर भागे, और नगर में, और गांवों में जाकर उसका समाचार कहा।",
			"35": "<sup>35</sup> और लोग यह जो हुआ था उसके देखने को निकले, और यीशु के पास आकर जिस मनुष्य से दुष्टात्माएं निकली थीं, उसे यीशु के पांवों के पास कपड़े पहिने और सचेत बैठे हुए पाकर डर गए।",
			"36": "<sup>36</sup> और देखने वालों ने उन को बताया, कि वह दुष्टात्मा का सताया हुआ मनुष्य किस प्रकार अच्छा हुआ।",
			"37": "<sup>37</sup> तब गिरासेनियों के आस पास के सब लोगों ने यीशु से बिनती की, कि हमारे यहां से चला जा; क्योंकि उन पर बड़ा भय छा गया था: सो वह नाव पर चढ़कर लौट गया।",
			"38": "<sup>38</sup> जिस मनुष्य से दुष्टात्माऐं निकली थीं वह उस से बिनती करने लगा, कि मुझे अपने साथ रहने दे, परन्तु यीशु ने उसे विदा करके कहा।",
			"39": "<sup>39</sup> अपने घर को लौट जा और लोगों से कह दे, कि परमेश्वर ने तेरे लिये कैसे बड़े काम किए हैं: वह जाकर सारे नगर में प्रचार करने लगा, कि यीशु ने मेरे लिये कैसे बड़े काम किए॥",
			"40": "<sup>40</sup> जब यीशु लौट रहा था, तो लोग उस से आनन्द के साथ मिले; क्योंकि वे सब उस की बाट जोह रहे थे।",
			"41": "<sup>41</sup> और देखो, याईर नाम एक मनुष्य जो आराधनालय का सरदार था, आया, और यीशु के पांवों पर गिर के उस से बिनती करने लगा, कि मेरे घर चल।",
			"42": "<sup>42</sup> क्योंकि उसके बारह वर्ष की एकलौती बेटी थी, और वह मरने पर थी: जब वह जा रहा था, तब लोग उस पर गिरे पड़ते थे॥",
			"43": "<sup>43</sup> और एक स्त्री ने जिस को बारह वर्ष से लोहू बहने का रोग था, और जो अपनी सारी जिविका वैद्यों के पीछे व्यय कर चुकी थी और तौभी किसी के हाथ से चंगी न हो सकी थी।",
			"44": "<sup>44</sup> पीछे से आकर उसके वस्त्र के आंचल को छूआ, और तुरन्त उसका लोहू बहना थम गया।",
			"45": "<sup>45</sup> इस पर यीशु ने कहा, मुझे किस ने छूआ जब सब मुकरने लगे, तो पतरस और उसके साथियों ने कहा; हे स्वामी, तुझे तो भीड़ दबा रही है और तुझ पर गिरी पड़ती है।",
			"46": "<sup>46</sup> परन्तु यीशु ने कहा: किसी ने मुझे छूआ है क्योंकि मैं ने जान लिया है कि मुझ में से सामर्थ निकली है।",
			"47": "<sup>47</sup> जब स्त्री ने देखा, कि मैं छिप नहीं सकती, तब कांपती हुई आई, और उसके पांवों पर गिरकर सब लोगों के साम्हने बताया, कि मैं ने किस कारण से तुझे छूआ, और क्योंकर तुरन्त चंगी हो गई।",
			"48": "<sup>48</sup> उस ने उस से कहा, बेटी तेरे विश्वास ने तुझे चंगा किया है, कुशल से चली जा।",
			"49": "<sup>49</sup> वह यह कह ही रहा था, कि किसी ने आराधनालय के सरदार के यहां से आकर कहा, तेरी बेटी मर गई: गुरु को दु:ख न दे।",
			"50": "<sup>50</sup> यीशु ने सुनकर उसे उत्तर दिया, मत डर; केवल विश्वास रख; तो वह बच जाएगी।",
			"51": "<sup>51</sup> घर में आकर उस ने पतरस और यूहन्ना और याकूब और लड़की के माता-पिता को छोड़ और किसी को अपने साथ भीतर आने न दिया।",
			"52": "<sup>52</sup> और सब उसके लिये रो पीट रहे थे, परन्तु उस ने कहा; रोओ मत; वह मरी नहीं परन्तु सो रही है।",
			"53": "<sup>53</sup> वे यह जानकर, कि मर गई है, उस की हंसी करने लगे।",
			"54": "<sup>54</sup> परन्तु उस ने उसका हाथ पकड़ा, और पुकारकर कहा, हे लड़की उठ!",
			"55": "<sup>55</sup> तब उसके प्राण फिर आए और वह तुरन्त उठी; फिर उस ने आज्ञा दी, कि उसे कुछ खाने को दिया जाए।",
			"56": "<sup>56</sup> उसके माता-पिता चकित हुए, परन्तु उस ने उन्हें चिताया, कि यह जो हुआ है, किसी से न कहना॥"
		},
		"9": {
			"1": "<sup>1</sup> फिर उस ने बारहों को बुलाकर उन्हें सब दुष्टात्माओं और बिमारियों को दूर करने की सामर्थ और अधिकार दिया।",
			"2": "<sup>2</sup> और उन्हें परमेश्वर के राज्य का प्रचार करने, और बिमारों को अच्छा करने के लिये भेजा।",
			"3": "<sup>3</sup> और उस ने उससे कहा, मार्ग के लिये कुछ न लेना: न तो लाठी, न झोली, न रोटी, न रूपये और न दो दो कुरते।",
			"4": "<sup>4</sup> और जिस किसी घर में तुम उतरो, वहीं रहो; और वहीं से विदा हो।",
			"5": "<sup>5</sup> जो कोई तुम्हें ग्रहण न करेगा उस नगर से निकलते हुए अपने पांवों की धूल झाड़ डालो, कि उन पर गवाही हो।",
			"6": "<sup>6</sup> सो वे निकलकर गांव गांव सुसमाचार सुनाते, और हर कहीं लोगों को चंगा करते हुए फिरते रहे॥",
			"7": "<sup>7</sup> और देश की चौथाई का राजा हेरोदेस यह सब सुनकर घबरा गया, क्योंकि कितनों ने कहा, कि यूहन्ना मरे हुओं में से जी उठा है।",
			"8": "<sup>8</sup> और कितनों ने यह, कि एलिय्याह दिखाई दिया है: औरों ने यह, कि पुराने भविष्यद्वक्ताओं में से कोई जी उठा है।",
			"9": "<sup>9</sup> परन्तु हेरोदेस ने कहा, युहन्ना का तो मैं ने सिर कटवाया अब यह कौन है, जिस के विषय में ऐसी बातें सुनता हूं? और उस ने उसे देखने की इच्छा की॥",
			"10": "<sup>10</sup> फिर प्रेरितों ने लौटकर जो कुछ उन्होंने किया था, उस को बता दिया, और वह उन्हें अलग करके बैतसैदा नाम एक नगर को ले गया।",
			"11": "<sup>11</sup> यह जानकर भीड़ उसके पीछे हो ली: और वह आनन्द के साथ उन से मिला, और उन से परमेश्वर के राज्य की बातें करने लगा: और जो चंगे होना चाहते थे, उन्हें चंगा किया।",
			"12": "<sup>12</sup> जब दिन ढलने लगा, तो बारहों ने आकर उससे कहा, भीड़ को विदा कर, कि चारों ओर के गावों और बस्तियों में जाकर टिकें, और भोजन का उपाय करें, क्योंकि हम यहां सुनसान जगह में हैं।",
			"13": "<sup>13</sup> उस ने उन से कहा, तुम ही उन्हें खाने को दो: उन्होंने कहा, हमारे पास पांच रोटियां और दो मछली को छोड़ और कुछ नहीं: परन्तु हां, यदि हम जाकर इन सब लोगों के लिये भोजन मोल लें, तो हो सकता है: वे लोग तो पांच हजार पुरूषों के लगभग थे।",
			"14": "<sup>14</sup> तब उस ने अपने चेलों से कहा, उन्हें पचास पचास करके पांति में बैठा दो।",
			"15": "<sup>15</sup> उन्होंने ऐसा ही किया, और सब को बैठा दिया।",
			"16": "<sup>16</sup> तब उस ने वे पांच रोटियां और दो मछली लीं, और स्वर्ग की और देखकर धन्यवाद किया, और तोड़ तोड़कर चेलों को देता गया, कि लोगों को परोसें।",
			"17": "<sup>17</sup> सो सब खाकर तृप्त हुए, और बचे हुए टुकड़ों से बारह टोकरी भरकर उठाईं॥",
			"18": "<sup>18</sup> जब वह एकान्त में प्रार्थना कर रहा था, और चेले उसके साथ थे, तो उस ने उन से पूछा, कि लोग मुझे क्या कहते हैं?",
			"19": "<sup>19</sup> उन्होंने उत्तर दिया, युहन्ना बपतिस्मा देनेवाला, और कोई कोई एलिय्याह, और कोई यह कि पुराने भविष्यद्वक्ताओं में से कोई जी उठा है।",
			"20": "<sup>20</sup> उस ने उन से पूछा, परन्तु तुम मुझे क्या कहते हो? पतरस ने उत्तर दिया, परमेश्वर का मसीह।",
			"21": "<sup>21</sup> तब उस ने उन्हें चिताकर कहा, कि यह किसी से न कहना।",
			"22": "<sup>22</sup> और उस ने कहा, मनुष्य के पुत्र के लिये अवश्य है, कि वह बहुत दुख उठाए, और पुरिनए और महायाजक और शास्त्री उसे तुच्छ समझकर मार डालें, और वह तीसरे दिन जी उठे।",
			"23": "<sup>23</sup> उस ने सब से कहा, यदि कोई मेरे पीछे आना चाहे, तो अपने आप से इन्कार करे और प्रति दिन अपना क्रूस उठाए हुए मेरे पीछे हो ले।",
			"24": "<sup>24</sup> क्योंकि जो कोई अपना प्राण बचाना चाहेगा वह उसे खोएगा, परन्तु जो कोई मेरे लिये अपना प्राण खोएगा वही उसे बचाएगा।",
			"25": "<sup>25</sup> यदि मनुष्य सारे जगत को प्राप्त करे, और अपना प्राण खो दे, या उस की हानि उठाए, तो उसे क्या लाभ होगा?",
			"26": "<sup>26</sup> जो कोई मुझ से और मेरी बातों से लजाएगा; मनुष्य का पुत्र भी जब अपनी, और अपने पिता की, और पवित्र स्वर्ग दूतों की, महिमा सहित आएगा, तो उस से लजाएगा।",
			"27": "<sup>27</sup> मैं तुम से सच कहता हूं, कि जो यहां खड़े हैं, उन में से कोई कोई ऐसे हैं कि जब तक परमेश्वर का राज्य न देख लें, तब तक मृत्यु का स्वाद न चखेंगे।",
			"28": "<sup>28</sup> इन बातों के कोई आठ दिन बाद वह पतरस और यूहन्ना और याकूब को साथ लेकर प्रार्थना करने के लिये पहाड़ पर गया।",
			"29": "<sup>29</sup> जब वह प्रार्थना कर ही रहा था, तो उसके चेहरे का रूप बदल गया: और उसका वस्त्र श्वेत होकर चमकने लगा।",
			"30": "<sup>30</sup> और देखो, मूसा और एलिय्याह, ये दो पुरूष उसके साथ बातें कर रहे थे।",
			"31": "<sup>31</sup> ये महिमा सहित दिखाई दिए; और उसके मरने की चर्चा कर रहे थे, जो यरूशलेम में होनेवाला था।",
			"32": "<sup>32</sup> पतरस और उसके साथी नींद से भरे थे, और जब अच्छी तरह सचेत हुए, तो उस की महिमा; और उन दो पुरूषों को, जो उसके साथ खड़े थे, देखा।",
			"33": "<sup>33</sup> जब वे उसके पास से जाने लगे, तो पतरस ने यीशु से कहा; हे स्वामी, हमारा यहां रहना भला है: सो हम तीन मण्डप बनाएं, एक तेरे लिये, एक मूसा के लिये, और एक एलिय्याह के लिये। वह जानता न था, कि क्या कह रहा है।",
			"34": "<sup>34</sup> वह यह कह ही रहा था, कि एक बादल ने आकर उन्हें छा लिया, और जब वे उस बादल से घिरने लगे, तो डर गए।",
			"35": "<sup>35</sup> और उस बादल में से यह शब्द निकला, कि यह मेरा पुत्र और मेरा चुना हुआ है, इस की सुनो।",
			"36": "<sup>36</sup> यह शब्द होते ही यीशु अकेला पाया गया: और वे चुप रहे, और कुछ देखा था, उस की कोई बात उन दिनों में किसी से न कही॥",
			"37": "<sup>37</sup> और दूसरे दिन जब वे पहाड़ से उतरे, तो एक बड़ी भीड़ उस से आ मिली।",
			"38": "<sup>38</sup> और देखो, भीड़ में से एक मनुष्य ने चिल्ला कर कहा, हे गुरू, मैं तुझ से बिनती करता हूं, कि मेरे पुत्र पर कृपा दृष्टि कर; क्योंकि वह मेरा एकलौता है।",
			"39": "<sup>39</sup> और देख, एक दुष्टात्मा उसे पकड़ता है, और वह एकाएक चिल्ला उठता है; और वह उसे ऐसा मरोड़ता है, कि वह मुंह में फेन भर लाता है; और उसे कुचलकर कठिनाई से छोड़ता है।",
			"40": "<sup>40</sup> और मै ने तेरे चेलों से बिनती की, कि उसे निकालें; परन्तु वे न निकाल सके।",
			"41": "<sup>41</sup> यीशु न उत्तर दिया, हे अविश्वासी और हठीले लोगो, मैं कब तक तुम्हारे साथ रहूंगा, और तुम्हारी सहूंगा? अपने पुत्र को यहां ले आ।",
			"42": "<sup>42</sup> वह आ ही रहा था कि दुष्टात्मा ने उसे पटक कर मरोड़ा, परन्तु यीशु ने अशुद्ध आत्मा को डांटा और लकड़े को अच्छा करके उसके पिता को सौंप दिया।",
			"43": "<sup>43</sup> तब सब लोग परमेश्वर के महासामर्थ से चकित हुए॥",
			"44": "<sup>44</sup> परन्तु जब सब लोग उन सब कामों से जो वह करता था, अचम्भा कर रहे थे, तो उस ने अपने चेलों से कहा; ये बातें तुम्हारे कानों में पड़ी रहें, क्योंकि मनुष्य का पुत्र मनुष्यों के हाथ में पकड़वाया जाने को है।",
			"45": "<sup>45</sup> परन्तु वे इस बात को न समझते थे, और यह उन से छिपी रही; कि वे उसे जानने न पाएं, और वे इस बात के विषय में उस से पूछने से डरते थे॥",
			"46": "<sup>46</sup> फिर उन में यह विवाद होने लगा, कि हम में से बड़ा कौन है?",
			"47": "<sup>47</sup> पर यीशु ने उन के मन का विचार जान लिया : और एक बालक को लेकर अपने पास खड़ा किया।",
			"48": "<sup>48</sup> और उन से कहा; जो कोई मेरे नाम से इस बालक को ग्रहण करता है, वह मुझे ग्रहण करता है; और जो कोई मुझे ग्रहण करता है, वह मेरे भेजने वाले को ग्रहण करता है क्योंकि जो तुम में सब से छोटे से छोटा है, वही बड़ा है।",
			"49": "<sup>49</sup> तब युहन्ना ने कहा, हे स्वामी, हम ने एक मनुष्य को तेरे नाम से दुष्टात्माओं को निकालते देखा, और हम ने उसे मना किया, क्योंकि वह हमारे साथ होकर तेरे पीछे नहीं हो लेता।",
			"50": "<sup>50</sup> यीशु ने उस से कहा, उसे मना मत करो; क्योंकि जो तुम्हारे विरोध में नहीं, वह तुम्हारी ओर है॥",
			"51": "<sup>51</sup> जब उसके ऊपर उठाए जाने के दिन पूरे होने पर थे, जो उस ने यरूशलेम को जाने का विचार दृढ़ किया।",
			"52": "<sup>52</sup> और उस ने अपने आगे दूत भेजे: वे सामरियों के एक गांव में गए, कि उसके लिये जगह तैयार करें।",
			"53": "<sup>53</sup> परन्तु उन लोगों ने उसे उतरने न दिया, क्योंकि वह यरूशलेम को जा रहा था।",
			"54": "<sup>54</sup> यह देखकर उसके चेले याकूब और यूहन्ना ने कहा; हे प्रभु; क्या तू चाहता है, कि हम आज्ञा दें, कि आकाश से आग गिरकर उन्हें भस्म कर दे।",
			"55": "<sup>55</sup> परन्तु उस ने फिरकर उन्हें डांटा और कहा, तुम नहीं जानते कि तुम कैसी आत्मा के हो।",
			"56": "<sup>56</sup> क्योंकि मनुष्य का पुत्र लोगों के प्राणों को नाश करने नहीं वरन बचाने के लिये आया है: और वे किसी और गांव में चले गए॥",
			"57": "<sup>57</sup> जब वे मार्ग में चले जाते थे, तो किसी न उस से कहा, जहां जहां तू जाएगा, मैं तेरे पीछे हो लूंगा।",
			"58": "<sup>58</sup> यीशु ने उस से कहा, लोमडिय़ों के भट और आकाश के पक्षियों के बसेरे होते हैं, पर मनुष्य के पुत्र को सिर धरने की भी जगह नहीं।",
			"59": "<sup>59</sup> उस ने दूसरे से कहा, मेरे पीछे हो ले; उस ने कहा; हे प्रभु, मुझे पहिले जाने दे कि अपने पिता को गाड़ दूं।",
			"60": "<sup>60</sup> उस ने उस से कहा, मरे हुओं को अपने मुरदे गाड़ने दे, पर तू जाकर परमेश्वर के राज्य की कथा सुना।",
			"61": "<sup>61</sup> एक और ने भी कहा; हे प्रभु, मैं तेरे पीछे हो लूंगा; पर पहिले मुझे जाने दे कि अपने घर के लोगों से विदा हो आऊं।",
			"62": "<sup>62</sup> यीशु ने उस से कहा; जो कोई अपना हाथ हल पर रखकर पीछे देखता है, वह परमेश्वर के राज्य के योग्य नहीं॥"
		},
		"10": {
			"1": "<sup>1</sup> और इन बातों के बाद प्रभु ने सत्तर और मनुष्य नियुक्त किए और जिस जिस नगर और जगह को वह आप जाने पर था, वहां उन्हें दो दो करके अपने आगे भेजा।",
			"2": "<sup>2</sup> और उस ने उन से कहा; पके खेत बहुत हैं; परन्तु मजदूर थोड़े हैं: इसलिये खेत के स्वामी से बिनती करो, कि वह अपने खेत काटने को मजदूर भेज दे।",
			"3": "<sup>3</sup> जाओ; देखों मैं तुम्हें भेड़ों की नाईं भेडियों के बीच में भेजता हूं।",
			"4": "<sup>4</sup> इसलिये न बटुआ, न झोली, न जूते लो; और न मार्ग में किसी को नमस्कार करो।",
			"5": "<sup>5</sup> जिस किसी घर में जाओ, पहिले कहो, कि इस घर पर कल्याण हो।",
			"6": "<sup>6</sup> यदि वहां कोई कल्याण के योग्य होगा; तो तुम्हारा कल्याण उस पर ठहरेगा, नहीं तो तुम्हारे पास लौट आएगा।",
			"7": "<sup>7</sup> उसी घर में रहो, और जो कुछ उन से मिले, वही खाओ पीओ, क्योंकि मजदूर को अपनी मजदूरी मिलनी चाहिए: घर घर न फिरना।",
			"8": "<sup>8</sup> और जिस नगर में जाओ, और वहां के लोग तुम्हें उतारें, तो जो कुछ तुम्हारे साम्हने रखा जाए वही खाओ।",
			"9": "<sup>9</sup> वहां के बीमारों को चंगा करो: और उन से कहो, कि परमेश्वर का राज्य तुम्हारे निकट आ पहुंचा है।",
			"10": "<sup>10</sup> परन्तु जिस नगर में जाओ, और वहां के लोग तुम्हें ग्रहण न करें, तो उसके बाजारों में जाकर कहो।",
			"11": "<sup>11</sup> कि तुम्हारे नगर की धूल भी, जो हमारे पांवों में लगी है, हम तुम्हारे साम्हने झाड़ देते हैं, तौभी यह जान लो, कि परमेश्वर का राज्य तुम्हारे निकट आ पहुंचा है।",
			"12": "<sup>12</sup> मैं तुम से कहता हूं, कि उस दिन उस नगर की दशा से सदोम की दशा सहने योग्य होगी।",
			"13": "<sup>13</sup> हाय खुराजीन ! हाय बैतसैदा ! जो सामर्थ के काम तुम में किए गए, यदि वे सूर और सैदा में किए जाते, तो टाट ओढ़कर और राख में बैठकर वे कब के मन फिराते।",
			"14": "<sup>14</sup> परन्तु न्याय के दिन तुम्हरी दशा से सूर और सैदा की दशा सहने योग्य होगी।",
			"15": "<sup>15</sup> और हे कफरनहूम, क्या तू स्वर्ग तक ऊंचा किया जाएगा? तू तो अधोलोक तक नीचे जाएगा।",
			"16": "<sup>16</sup> जो तुम्हारी सुनता है, वह मेरी सुनता है, और जो तुम्हें तुच्छ जानता है, वह मुझे तुच्छ जानता है; और जो मुझे तुच्छ जानता है, वह मेरे भेजनेवाले को तुच्छ जानता है।",
			"17": "<sup>17</sup> वे सत्तर आनन्द से फिर आकर कहने लगे, हे प्रभु, तेरे नाम से दुष्टात्मा भी हमारे वश में हैं।",
			"18": "<sup>18</sup> उस ने उन से कहा; मैं शैतान को बिजली की नाईं स्वर्ग से गिरा हुआ देख रहा था।",
			"19": "<sup>19</sup> देखो, मैने तुम्हे सांपों और बिच्छुओं को रौंदने का, और शत्रु की सारी सामर्थ पर अधिकार दिया है; और किसी वस्तु से तुम्हें कुछ हानि न होगी।",
			"20": "<sup>20</sup> तौभी इस से आनन्दित मत हो, कि आत्मा तुम्हारे वश में हैं, परन्तु इस से आनन्दित हो कि तुम्हारे नाम स्वर्ग पर लिखे हैं॥",
			"21": "<sup>21</sup> उसी घड़ी वह पवित्र आत्मा में होकर आनन्द से भर गया, और कहा; हे पिता, स्वर्ग और पृथ्वी के प्रभु, मैं तेरा धन्यवाद करता हूं, कि तू ने इन बातों को ज्ञानियों और समझदारों से छिपा रखा, और बालकों पर प्रगट किया: हां, हे पिता, क्योंकि तुझे यही अच्छा लगा।",
			"22": "<sup>22</sup> मेरे पिता ने मुझे सब कुछ सौंप दिया है और कोई नहीं जानता कि पुत्र कौन है केवल पिता और पिता कौन है यह भी कोई नहीं जानता, केवल पुत्र के और वह जिस पर पुत्र उसे प्रकट करना चाहे।",
			"23": "<sup>23</sup> और चेलों की ओर फिरकर निराले में कहा, धन्य हैं वे आंखे, जो ये बातें जो तुम देखते हो देखती हैं।",
			"24": "<sup>24</sup> क्योंकि मैं तुम से कहता हूं, कि बहुत से भविष्यद्वक्ताओं और राजाओं ने चाहा, कि जो बातें तुम देखते हो देखें; पर न देखीं और जो बातें तुम सुनते हो सुनें, पर न सुनीं॥",
			"25": "<sup>25</sup> और देखो, एक व्यवस्थापक उठा; और यह कहकर, उस की परीक्षा करने लगा; कि हे गुरू, अनन्त जीवन का वारिस होने के लिये मैं क्या करूं?",
			"26": "<sup>26</sup> उस ने उस से कहा; कि व्यवस्था में क्या लिखा है तू कैसे पढ़ता है?",
			"27": "<sup>27</sup> उस ने उत्तर दिया, कि तू प्रभु अपने परमेश्वर से अपने सारे मन और अपने सारे प्राण और अपनी सारी शक्ति और अपनी सारी बुद्धि के साथ प्रेम रख; और अपने पड़ोसी से अपने समान प्रेम रख।",
			"28": "<sup>28</sup> उस ने उस से कहा, तू ने ठीक उत्तर दिया है, यही कर: तो तू जीवित रहेगा।",
			"29": "<sup>29</sup> परन्तु उस ने अपनी तईं धर्मी ठहराने की इच्छा से यीशु से पूछा, तो मेरा पड़ोसी कौन है?",
			"30": "<sup>30</sup> यीशु ने उत्तर दिया; कि एक मनुष्य यरूशलेम से यरीहो को जा रहा था, कि डाकुओं ने घेरकर उसके कपड़े उतार लिए, और मार पीट कर उसे अधमूआ छोड़कर चले गए।",
			"31": "<sup>31</sup> और ऐसा हुआ; कि उसी मार्ग से एक याजक जा रहा था: परन्तु उसे देख के कतरा कर चला गया।",
			"32": "<sup>32</sup> इसी रीति से एक लेवी उस जगह पर आया, वह भी उसे देख के कतरा कर चला गया।",
			"33": "<sup>33</sup> परन्तु एक सामरी यात्री वहां आ निकला, और उसे देखकर तरस खाया।",
			"34": "<sup>34</sup> और उसके पास आकर और उसके घावों पर तेल और दाखरस डालकर पट्टियां बान्धी, और अपनी सवारी पर चढ़ाकर सराय में ले गया, और उस की सेवा टहल की।",
			"35": "<sup>35</sup> दूसरे दिन उस ने दो दिनार निकालकर भटियारे को दिए, और कहा; इस की सेवा टहल करना, और जो कुछ तेरा और लगेगा, वह मैं लौटने पर तुझे भर दूंगा।",
			"36": "<sup>36</sup> अब तेरी समझ में जो डाकुओं में घिर गया था, इन तीनों में से उसका पड़ोसी कौन ठहरा?",
			"37": "<sup>37</sup> उस ने कहा, वही जिस ने उस पर तरस खाया: यीशु ने उस से कहा, जा, तू भी ऐसा ही कर॥",
			"38": "<sup>38</sup> फिर जब वे जा रहे थे, तो वह एक गांव में गया, और मार्था नाम एक स्त्री ने उसे अपने घर में उतारा।",
			"39": "<sup>39</sup> और मरियम नाम उस की एक बहिन थी; वह प्रभु के पांवों के पास बैठकर उसका वचन सुनती थी।",
			"40": "<sup>40</sup> पर मार्था सेवा करते करते घबरा गई और उसके पास आकर कहने लगी; हे प्रभु, क्या तुझे कुछ भी सोच नहीं कि मेरी बहिन ने मुझे सेवा करने के लिये अकेली ही छोड़ दिया है? सो उस से कह, कि मेरी सहायता करे।",
			"41": "<sup>41</sup> प्रभु ने उसे उत्तर दिया, मार्था, हे मार्था; तू बहुत बातों के लिये चिन्ता करती और घबराती है।",
			"42": "<sup>42</sup> परन्तु एक बात अवश्य है, और उस उत्तम भाग को मरियम ने चुन लिया है: जो उस से छीना न जाएगा॥"
		},
		"11": {
			"1": "<sup>1</sup> फिर वह किसी जगह प्रार्थना कर रहा था: और जब वह प्रार्थना कर चुका, तो उसके चेलों में से एक ने उस से कहा; हे प्रभु, जैसे यूहन्ना ने अपने चेलों को प्रार्थना करना सिखलाया वैसे ही हमें भी तू सिखा दे।",
			"2": "<sup>2</sup> उस ने उन से कहा; जब तुम प्रार्थना करो, तो कहो; हे पिता, तेरा नाम पवित्र माना जाए, तेरा राज्य आए।",
			"3": "<sup>3</sup> हमारी दिन भर की रोटी हर दिन हमें दिया कर।",
			"4": "<sup>4</sup> और हमारे पापों को क्षमा कर, क्योंकि हम भी अपने हर एक अपराधी को क्षमा करते हैं, और हमें परीक्षा में न ला॥",
			"5": "<sup>5</sup> और उस ने उन से कहा, तुम में से कौन है कि उसका एक मित्र हो, और वह आधी रात को उसके पास आकर उस से कहे, कि हे मित्र; मुझे तीन रोटियां दे।",
			"6": "<sup>6</sup> क्योंकि एक यात्री मित्र मेरे पास आया है, और उसके आगे रखने के लिये मेरे पास कुछ नहीं है।",
			"7": "<sup>7</sup> और वह भीतर से उत्तर दे, कि मुझे दुख न दे; अब तो द्वार बन्द है, और मेरे बालक मेरे पास बिछौने पर हैं, इसलिये मैं उठकर तुझे दे नहीं सकता",
			"8": "<sup>8</sup> मैं तुम से कहता हूं, यदि उसका मित्र होने पर भी उसे उठकर न दे, तौभी उसके लज्ज़ा छोड़कर मांगने के कारण उसे जितनी आवश्यकता हो उतनी उठकर देगा।",
			"9": "<sup>9</sup> और मैं तुम से कहता हूं; कि मांगो, तो तुम्हें दिया जाएगा; ढूंढ़ों तो तुम पाओगे; खटखटाओ, तो तुम्हारे लिये खोला जाएगा।",
			"10": "<sup>10</sup> क्योंकि जो कोई मांगता है, उसे मिलता है; और जो ढूंढ़ता है, वह पाता है; और जो खटखटाता है, उसके लिये खोला जाएगा।",
			"11": "<sup>11</sup> तुम में से ऐसा कौन पिता होगा, कि जब उसका पुत्र रोटी मांगे, तो उसे पत्थर दे: या मछली मांगे, तो मछली के बदले उसे सांप दे?",
			"12": "<sup>12</sup> या अण्डा मांगे तो उसे बिच्छू दे?",
			"13": "<sup>13</sup> सो जब तुम बुरे होकर अपने लड़के-बालों को अच्छी वस्तुएं देना जानते हो, तो स्वर्गीय पिता अपने मांगने वालों को पवित्र आत्मा क्यों न देगा॥",
			"14": "<sup>14</sup> फिर उस ने एक गूंगी दुष्टात्मा को निकाला: जब दुष्टात्मा निकल गई, तो गूंगा बोलने लगा; और लोगों ने अचम्भा किया।",
			"15": "<sup>15</sup> परन्तु उन में से कितनों ने कहा, यह तो शैतान नाम दुष्टात्माओं के प्रधान की सहायता से दुष्टात्माओं को निकालता है।",
			"16": "<sup>16</sup> औरों ने उस की परीक्षा करने के लिये उस से आकाश का एक चिन्ह मांगा।",
			"17": "<sup>17</sup> परन्तु उस ने, उन के मन की बातें जानकर, उन से कहा; जिस जिस राज्य में फूट होती है, वह राज्य उजड़ जाता है: और जिस घर में फूट होती है, वह नाश हो जाता है।",
			"18": "<sup>18</sup> और यदि शैतान अपना ही विरोधी हो जाए, तो उसका राज्य क्योंकर बना रहेगा? क्योंकि तुम मेरे विषय में तो कहते हो, कि यह शैतान की सहायता से दुष्टात्मा निकालता है।",
			"19": "<sup>19</sup> भला यदि मैं शैतान की सहायता से दुष्टात्माओं को निकालता हूं, तो तुम्हारी सन्तान किस की सहायता से निकालते हैं? इसलिये वे ही तुम्हारा न्याय चुकाएंगे।",
			"20": "<sup>20</sup> परन्तु यदि मैं परमेश्वर की सामर्थ से दुष्टात्माओं को निकालता हूं, तो परमेश्वर का राज्य तुम्हारे पास आ पहुंचा।",
			"21": "<sup>21</sup> जब बलवन्त मनुष्य हथियार बान्धे हुए अपने घर की रखवाली करता है, तो उस की संपत्ति बची रहती है।",
			"22": "<sup>22</sup> पर जब उस से बढ़कर कोई और बलवन्त चढ़ाई करके उसे जीत लेता है, तो उसके वे हथियार जिन पर उसका भरोसा था, छीन लेता है और उस की संपत्ति लूटकर बांट देता है।",
			"23": "<sup>23</sup> जो मेरे साथ नहीं वह मेरे विरोध में है और जो मेरे साथ नहीं बटोरता वह बिथराता है।",
			"24": "<sup>24</sup> जब अशुद्ध आत्मा मनुष्य में से निकल जाती है तो सूखी जगहों में विश्राम ढूंढ़ती फिरती है; और जब नहीं पाती तो कहती है; कि मैं अपने उसी घर में जहां से निकली थी लौट जाऊंगी।",
			"25": "<sup>25</sup> और आकर उसे झाड़ा-बुहारा और सजा-सजाया पाती है।",
			"26": "<sup>26</sup> तब वह आकर अपने से और बुरी सात आत्माओं को अपने साथ ले आती है, और वे उस में पैठकर वास करती हैं, और उस मनुष्य की पिछली दशा पहिले से भी बुरी हो जाती है॥",
			"27": "<sup>27</sup> जब वह ये बातें कह ही रहा था तो भीड़ में से किसी स्त्री ने ऊंचे शब्द से कहा, धन्य वह गर्भ जिस में तू रहा; और वे स्तन, जो तू ने चूसे।",
			"28": "<sup>28</sup> उस ने कहा, हां; परन्तु धन्य वे हैं, जो परमेश्वर का वचन सुनते और मानते हैं॥",
			"29": "<sup>29</sup> जब बड़ी भीड़ इकट्ठी होती जाती थी तो वह कहने लगा; कि इस युग के लोग बुरे हैं; वे चिन्ह ढूंढ़ते हैं; पर यूनुस के चिन्ह को छोड़ कोई और चिन्ह उन्हें न दिया जाएगा।",
			"30": "<sup>30</sup> जैसा यूनुस नीनवे के लोगों के लिये चिन्ह ठहरा, वैसा ही मनुष्य का पुत्र भी इस युग के लोगों के लिये ठहरेगा।",
			"31": "<sup>31</sup> दक्खिन की रानी न्याय के दिन इस समय के मनुष्यों के साथ उठकर, उन्हें दोषी ठहराएगी, क्योंकि वह सुलैमान का ज्ञान सुनने को पृथ्वी की छोर से आई, और देखो यहां वह है जो सुलैमान से भी बड़ा है।",
			"32": "<sup>32</sup> नीनवे के लोग न्याय के दिन इस समय के लोगों के साथ खड़े होकर, उन्हें दोषी ठहराएंगे; क्योंकि उन्होंने यूनुस का प्रचार सुनकर मन फिराया और देखो, यहां वह है, जो यूनुस से भी बड़ा है॥",
			"33": "<sup>33</sup> कोई मनुष्य दीया बार के तलघरे में, या पैमाने के नीचे नहीं रखता, परन्तु दीवट पर रखता है कि भीतर आने वाले उजियाला पाएं।",
			"34": "<sup>34</sup> तेरे शरीर का दीया तेरी आंख है, इसलिये जब तेरी आंख निर्मल है, तो तेरा सारा शरीर भी उजियाला है; परन्तु जब वह बुरी है, तो तेरा शरीर भी अन्धेरा है।",
			"35": "<sup>35</sup> इसलिये चौकस रहना, कि जो उजियाला तुझ में है वह अन्धेरा न हो जाए।",
			"36": "<sup>36</sup> इसलिये यदि तेरा सारा शरीर उजियाला हो, ओर उसका कोई भाग अन्धेरा न रहे, तो सब का सब ऐसा उलियाला होगा, जैसा उस समय होता है, जब दीया अपनी चमक से तुझे उजाला देता है॥",
			"37": "<sup>37</sup> जब वह बातें कर रहा था, तो किसी फरीसी ने उस से बिनती की, कि मेरे यहां भेजन कर; और वह भीतर जाकर भोजन करने बैठा।",
			"38": "<sup>38</sup> फरीसी ने यह देखकर अचम्भा दिया कि उस ने भोजन करने से पहिले स्नान नहीं किया।",
			"39": "<sup>39</sup> प्रभु ने उस से कहा, हे फरीसियों, तुम कटोरे और थाली को ऊपर ऊपर तो मांजते हो, परन्तु तुम्हारे भीतर अन्धेर और दुष्टता भरी है।",
			"40": "<sup>40</sup> हे निर्बुद्धियों, जिस ने बाहर का भाग बनाया, क्या उस ने भीतर का भाग नहीं बनाया?",
			"41": "<sup>41</sup> परन्तु हां, भीतरवाली वस्तुओं को दान कर दो, तो देखो, सब कुछ तुम्हारे लिये शुद्ध हो जाएगा॥",
			"42": "<sup>42</sup> पर हे फरीसियों, तुम पर हाय ! तुम पोदीने और सुदाब का, और सब भांति के साग-पात का दसवां अंश देते हो, परन्तु न्याय को और परमेश्वर के प्रेम को टाल देते हो: चाहिए तो था कि इन्हें भी करते रहते और उन्हें भी न छोड़ते।",
			"43": "<sup>43</sup> हे फरीसियों, तुम पर हाय ! तुम आराधनालयों में मुख्य मुख्य आसन और बाजारों में नमस्कार चाहते हो।",
			"44": "<sup>44</sup> हाय तुम पर ! क्योंकि तुम उन छिपी कब्रों के समान हो, जिन पर लोग चलते हैं, परन्तु नहीं जानते॥",
			"45": "<sup>45</sup> तब एक व्यवस्थापक ने उस को उत्तर दिया, कि हे गुरू, इन बातों के कहने से तू हमारी निन्दा करता है।",
			"46": "<sup>46</sup> उस ने कहा; हे व्यवस्थापकों, तुम पर भी हाय ! तुम ऐसे बोझ जिन को उठाना कठिन है, मनुष्यों पर लादते हो परन्तु तुम आप उन बोझों को अपनी एक उंगली से भी नहीं छूते।",
			"47": "<sup>47</sup> हाय तुम पर ! तुम उन भविष्यद्वक्तओं की कब्रें बनाते हो, जिन्हें ही तुम्हारे बाप-दादों ने मार डाला था।",
			"48": "<sup>48</sup> सो तुम गवाह हो, और अपने बाप-दादों के कामों में सहमत हो; क्योंकि उन्होंने तो उन्हें मार डाला और तुम उन की कब्रें बनाते हो।",
			"49": "<sup>49</sup> इसलिये परमेश्वर की बुद्धि ने भी कहा है, कि मैं उन के पास भविष्यद्वक्ताओं और प्रेरितों को भेजूंगी: और वे उन में से कितनों को मार डालेंगे, और कितनों को सताएंगे।",
			"50": "<sup>50</sup> ताकि जितने भविष्यद्वक्ताओं का लोहू जगत की उत्पत्ति से बहाया गया है, सब का लेखा, इस युग के लोगों से लिया जाए।",
			"51": "<sup>51</sup> हाबील की हत्या से लेकर जकरयाह की हत्या तक जो वेदी और मन्दिर के बीच में घात किया गया: मैं तुम से सच कहता हूं; उसका लेखा इसी समय के लोगों से लिया जाएगा।",
			"52": "<sup>52</sup> हाय तुम व्यवस्थापकों पर ! कि तुम ने ज्ञान की कुंजी ले तो ली, परन्तु तुम ने आप ही प्रवेश नहीं किया, और प्रवेश करने वालों को भी रोक दिया।",
			"53": "<sup>53</sup> जब वह वहां से निकला, तो शास्त्री और फरीसी बहुत पीछे पड़ गए और छेड़ने लगे, कि वह बहुत सी बातों की चर्चा करे।",
			"54": "<sup>54</sup> और उस की घात में लगे रहे, कि उसके मुंह की कोई बात पकड़ें॥"
		},
		"12": {
			"1": "<sup>1</sup> इतने में जब हजारों की भीड़ लग गई, यहां तक कि एक दूसरे पर गिरे पड़ते थे, तो वह सब से पहिले अपने चेलों से कहने लगा, कि फरीसियों के कपटरूपी खमीर से चौकस रहना।",
			"2": "<sup>2</sup> कुछ ढपा नहीं, जो खोला न जाएगा; और न कुछ छिपा है, जो जाना न जाएगा।",
			"3": "<sup>3</sup> इसलिये जो कुछ तुम ने अन्धेरे में कहा है, वह उजाले में सुना जाएगा: और जो तुम ने कोठिरयों में कानों कान कहा है, वह कोठों पर प्रचार किया जाएगा।",
			"4": "<sup>4</sup> परन्तु मैं तुम से जो मेरे मित्र हो कहता हूं, कि जो शरीर को घात करते हैं परन्तु उसके पीछे और कुछ नहीं कर सकते उन से मत डरो।",
			"5": "<sup>5</sup> मैं तुम्हें चिताता हूं कि तुम्हें किस से डरना चाहिए, घात करने के बाद जिस को नरक में डालने का अधिकार है, उसी से डरो : वरन मैं तुम से कहता हूं उसी से डरो।",
			"6": "<sup>6</sup> क्या दो पैसे की पांच गौरैयां नहीं बिकतीं? तौभी परमेश्वर उन में से एक को भी नहीं भूलता।",
			"7": "<sup>7</sup> वरन तुम्हारे सिर के सब बाल भी गिने हुए हैं, सो डरो नहीं, तुम बहुत गौरैयों से बढ़कर हो।",
			"8": "<sup>8</sup> मैं तुम से कहता हूं जो कोई मनुष्यों के साम्हने मुझे मान लेगा उसे मनुष्य का पुत्र भी परमेश्वर के स्वर्गदूतों के सामहने मान लेगा।",
			"9": "<sup>9</sup> परन्तु जो मनुष्यों के साम्हने मुझे इन्कार करे उसका परमेश्वर के स्वर्गदूतों के साम्हने इन्कार किया जाएगा।",
			"10": "<sup>10</sup> जो कोई मनुष्य के पुत्र के विरोध में कोई बात कहे, उसका वह अपराध क्षमा किया जाएगा , परन्तु जो पवित्र आत्मा की निन्दा करे उसका अपराध क्षमा न किया जायेगा ।",
			"11": "<sup>11</sup> जब लोग तुम्हें सभाओं और हाकिमों और अधिकारियों के साम्हने ले जाएं, तो चिन्ता न करना कि हम किस रीति से या क्या उत्तर दें, या क्या कहें।",
			"12": "<sup>12</sup> क्योंकि पवित्र आत्मा उसी घड़ी तुम्हें सिखा देगा, कि क्या कहना चाहिए॥",
			"13": "<sup>13</sup> फिर भीड़ में से एक ने उस से कहा, हे गुरू, मेरे भाई से कह, कि पिता की संपत्ति मुझे बांट दे।",
			"14": "<sup>14</sup> उस ने उस से कहा; हे मनुष्य, किस ने मुझे तुम्हारा न्यायी या बांटने वाला नियुक्त किया है?",
			"15": "<sup>15</sup> और उस ने उन से कहा, चौकस रहो, और हर प्रकार के लोभ से अपने आप को बचाए रखो: क्योंकि किसी का जीवन उस की संपत्ति की बहुतायत से नहीं होता।",
			"16": "<sup>16</sup> उस ने उन से एक दृष्टान्त कहा, कि किसी धनवान की भूमि में बड़ी उपज हुई।",
			"17": "<sup>17</sup> तब वह अपने मन में विचार करने लगा, कि मैं क्या करूं, क्योंकि मेरे यहां जगह नहीं, जहां अपनी उपज इत्यादि रखूं।",
			"18": "<sup>18</sup> और उस ने कहा; मैं यह करूंगा: मैं अपनी बखारियां तोड़ कर उन से बड़ी बनाऊंगा;",
			"19": "<sup>19</sup> और वहां अपना सब अन्न और संपत्ति रखूंगा: और अपने प्राण से कहूंगा, कि प्राण, तेरे पास बहुत वर्षों के लिये बहुत संपत्ति रखी है; चैन कर, खा, पी, सुख से रह।",
			"20": "<sup>20</sup> परन्तु परमेश्वर ने उस से कहा; हे मूर्ख, इसी रात तेरा प्राण तुझ से ले लिया जाएगा: तब जो कुछ तू ने इकट्ठा किया है, वह किस का होगा?",
			"21": "<sup>21</sup> ऐसा ही वह मनुष्य भी है जो अपने लिये धन बटोरता है, परन्तु परमेश्वर की दृष्टि में धनी नहीं॥",
			"22": "<sup>22</sup> फिर उस ने अपने चेलों से कहा; इसलिये मैं तुम से कहता हूं, अपने प्राण की चिन्ता न करो, कि हम क्या खाएंगे; न अपने शरीर की कि क्या पहिनेंगे।",
			"23": "<sup>23</sup> क्योंकि भोजन से प्राण, और वस्त्र से शरीर बढ़कर है।",
			"24": "<sup>24</sup> कौवों पर ध्यान दो; वे न बोते हैं, न काटते; न उन के भण्डार और न खत्ता होता है; तौभी परमेश्वर उन्हें पालता है; तुम्हारा मूल्य पक्षियों से कहीं अधिक है।",
			"25": "<sup>25</sup> तुम में से ऐसा कौन है, जो चिन्ता करने से अपनी अवस्था में एक घड़ी भी बढ़ा सकता है?",
			"26": "<sup>26</sup> इसलिये यदि तुम सब से छोटा काम भी नहीं कर सकते, तो और बातों के लिये क्यों चिन्ता करते हो?",
			"27": "<sup>27</sup> सोसनों के पेड़ों पर ध्यान करो कि वे कैसे बढ़ते हैं; वे न परिश्रम करते, न कातते हैं: तौभी मैं तुम से कहता हूं, कि सुलैमान भी, अपने सारे विभव में, उन में से किसी एक के समान वस्त्र पहिने हुए न था।",
			"28": "<sup>28</sup> इसलिये यदि परमेश्वर मैदान की घास को जो आज है, और कल भाड़ में झोंकी जाएगी, ऐसा पहिनाता है; तो हे अल्प विश्वासियों, वह तुम्हें क्यों न पहिनाएगा?",
			"29": "<sup>29</sup> और तुम इस बात की खोज में न रहो, कि क्या खाएंगे और क्या पीएंगे, और न सन्देह करो।",
			"30": "<sup>30</sup> क्योंकि संसार की जातियां इन सब वस्तुओं की खोज में रहती हैं: और तुम्हारा पिता जानता है, कि तुम्हें इन वस्तुओं की आवश्यकता है।",
			"31": "<sup>31</sup> परन्तु उसके राज्य की खोज में रहो, तो ये वस्तुऐं भी तुम्हें मिल जाएंगी।",
			"32": "<sup>32</sup> हे छोटे झुण्ड, मत डर; क्योंकि तुम्हारे पिता को यह भाया है, कि तुम्हें राज्य दे।",
			"33": "<sup>33</sup> अपनी संपत्ति बेचकर दान कर दो; और अपने लिये ऐसे बटुए बनाओ, जो पुराने नहीं होते, अर्थात स्वर्ग पर ऐसा धन इकट्ठा करो जो घटता नहीं और जिस के निकट चोर नहीं जाता, और कीड़ा नहीं बिगाड़ता।",
			"34": "<sup>34</sup> क्योंकि जहां तुम्हारा धन है, वहां तुम्हारा मन भी लगा रहेगा॥",
			"35": "<sup>35</sup> तुम्हारी कमरें बन्धी रहें, और तुम्हारे दीये जलते रहें।",
			"36": "<sup>36</sup> और तुम उन मनुष्यों के समान बनो, जो अपने स्वामी की बाट देख रहे हों, कि वह ब्याह से कब लौटेगा; कि जब वह आकर द्वार खटखटाए, तुरन्त उसके लिये खोल दें।",
			"37": "<sup>37</sup> धन्य हैं वे दास, जिन्हें स्वामी आकर जागते पाए; मैं तुम से सच कहता हूं, कि वह कमर बान्ध कर उन्हें भोजन करने को बैठाएगा, और पास आकर उन की सेवा करेगा।",
			"38": "<sup>38</sup> यदि वह रात के दूसरे पहर या तीसरे पहर में आकर उन्हें जागते पाए, तो वे दास धन्य हैं।",
			"39": "<sup>39</sup> परन्तु तुम यह जान रखो, कि यदि घर का स्वामी जानता, कि चोर किस घड़ी आएगा, तो जागता रहता, और अपने घर में सेंध लगने न देता।",
			"40": "<sup>40</sup> तुम भी तैयार रहो; क्योंकि जिस घड़ी तुम सोचते भी नहीं, उस घड़ी मनुष्य का पुत्र आ जावेगा।",
			"41": "<sup>41</sup> तब पतरस ने कहा, हे प्रभु, क्या यह दृष्टान्त तू हम ही से या सब से कहता है।",
			"42": "<sup>42</sup> प्रभु ने कहा; वह विश्वासयोग्य और बुद्धिमान भण्डारी कौन है, जिस का स्वामी उसे नौकर चाकरों पर सरदार ठहराए कि उन्हें समय पर सीधा दे।",
			"43": "<sup>43</sup> धन्य है वह दास, जिसे उसका स्वामी आकर ऐसा ही करते पाए।",
			"44": "<sup>44</sup> मैं तुम से सच कहता हूं; वह उसे अपनी सब संपत्ति पर सरदार ठहराएगा।",
			"45": "<sup>45</sup> परन्तु यदि वह दास सोचने लगे, कि मेरा स्वामी आने में देर कर रहा है, और दासों और दासियों मारने पीटने और खाने पीने और पियक्कड़ होने लगे।",
			"46": "<sup>46</sup> तो उस दास का स्वामी ऐसे दिन कि वह उस की बाट जोहता न रहे, और ऐसी घड़ी जिसे वह जानता न हो आएगा, और उसे भारी ताड़ना देकर उसका भाग अविश्वासियों के साथ ठहराएगा।",
			"47": "<sup>47</sup> और वह दास जो अपने स्वामी की इच्छा जानता था, और तैयार न रहा और न उस की इच्छा के अनुसार चला बहुत मार खाएगा।",
			"48": "<sup>48</sup> परन्तु जो नहीं जानकर मार खाने के योग्य काम करे वह थोड़ी मार खाएगा, इसलिये जिसे बहुत दिया गया है, उस से बहुत मांगा जाएगा, और जिसे बहुत सौंपा गया है, उस से बहुत मांगेंगें॥",
			"49": "<sup>49</sup> मैं पृथ्वी पर आग लगाने आया हूं; और क्या चाहता हूं केवल यह कि अभी सुलग जाती !",
			"50": "<sup>50</sup> मुझे तो एक बपतिस्मा लेना है; और जब तक वह न हो ले तब तक मैं कैसी सकेती में रहूंगा?",
			"51": "<sup>51</sup> क्या तुम समझते हो कि मैं पृथ्वी पर मिलाप कराने आया हूं? मैं तुम से कहता हूं; नहीं, वरन अलग कराने आया हूं।",
			"52": "<sup>52</sup> क्योंकि अब से एक घर में पांच जन आपस में विरोध रखेंगे, तीन दो से दो तीन से।",
			"53": "<sup>53</sup> पिता पुत्र से, और पुत्र पिता से विरोध रखेगा; मां बेटी से, और बेटी मां से, सास बहू से, और बहू सास से विरोध रखेगी॥",
			"54": "<sup>54</sup> और उस ने भीड़ से भी कहा, जब बादल को पच्छिम से उठते देखते हो, तो तुरन्त कहते हो, कि वर्षा होगी; और ऐसा ही होता है।",
			"55": "<sup>55</sup> और जब दक्खिना चलती दखते हो तो कहते हो, कि लू चलेगी, और ऐसा ही होता है।",
			"56": "<sup>56</sup> हे कपटियों, तुम धरती और आकाश के रूप में भेद कर सकते हो, परन्तु इस युग के विषय में क्यों भेद करना नहीं जानते?",
			"57": "<sup>57</sup> और तुम आप ही निर्णय क्यों नहीं कर लेते, कि उचित क्या है?",
			"58": "<sup>58</sup> जब तू अपने मुद्दई के साथ हाकिम के पास जा रहा है, तो मार्ग ही में उस से छूटने का यत्न कर ले ऐसा न हो, कि वह तुझे न्यायी के पास खींच ले जाए, और न्यायी तुझे प्यादे को सौंपे और प्यादा तुझे बन्दीगृह में डाल दे।",
			"59": "<sup>59</sup> मैं तुम से कहता हूं, कि जब तक तू दमड़ी दमड़ी भर न देगा तब तक वहां से छूटने न पाएगा॥"
		},
		"13": {
			"1": "<sup>1</sup> उस समय कुछ लोग आ पहुंचे, और उस से उन गलीलियों की चर्चा करने लगे, जिन का लोहू पीलातुस ने उन ही के बलिदानों के साथ मिलाया था।",
			"2": "<sup>2</sup> यह सुन उस ने उन से उत्तर में यह कहा, क्या तुम समझते हो, कि ये गलीली, और सब गलीलियों से पापी थे कि उन पर ऐसी विपत्ति पड़ी?",
			"3": "<sup>3</sup> मैं तुम से कहता हूं, कि नहीं; परन्तु यदि तुम मन न फिराओगे तो तुम सब भी इसी रीति से नाश होगे।",
			"4": "<sup>4</sup> या क्या तुम समझते हो, कि वे अठारह जन जिन पर शीलोह का गुम्मट गिरा, और वे दब कर मर गए: यरूशलेम के और सब रहने वालों से अधिक अपराधी थे?",
			"5": "<sup>5</sup> मैं तुम से कहता हूं, कि नहीं; परन्तु यदि तुम मन न फिराओगे तो तुम भी सब इसी रीति से नाश होगे।",
			"6": "<sup>6</sup> फिर उस ने यह दृष्टान्त भी कहा, कि किसी की अंगूर की बारी में एक अंजीर का पेड़ लगा हुआ था: वह उस में फल ढूंढ़ने आया, परन्तु न पाया।",
			"7": "<sup>7</sup> तब उस ने बारी के रखवाले से कहा, देख तीन वर्ष से मैं इस अंजीर के पेड़ में फल ढूंढ़ने आता हूं, परन्तु नहीं पाता, इसे काट डाल कि यह भूमि को भी क्यों रोके रहे।",
			"8": "<sup>8</sup> उस ने उस को उत्तर दिया, कि हे स्वामी, इसे इस वर्ष तो और रहने दे; कि मैं इस के चारों ओर खोदकर खाद डालूं।",
			"9": "<sup>9</sup> सो आगे को फले तो भला, नहीं तो उसे काट डालना।",
			"10": "<sup>10</sup> सब्त के दिन वह एक आराधनालय में उपदेश कर रहा था॥",
			"11": "<sup>11</sup> और देखो, एक स्त्री थी, जिसे अठारह वर्ष से एक र्दुबल करने वाली दुष्टात्मा लगी थी, और वह कुबड़ी हो गई थी, और किसी रीति से सीधी नहीं हो सकती थी।",
			"12": "<sup>12</sup> यीशु ने उसे देखकर बुलाया, और कहा हे नारी, तू अपनी र्दुबलता से छूट गई।",
			"13": "<sup>13</sup> तब उस ने उस पर हाथ रखे, और वह तुरन्त सीधी हो गई, और परमेश्वर की बड़ाई करने लगी।",
			"14": "<sup>14</sup> इसलिये कि यीशु ने सब्त के दिन उसे अच्छा किया था, आराधनालय का सरदार रिसयाकर लोगों से कहने लगा, छ: दिन हैं, जिन में काम करना चाहिए, सो उन ही दिनों में आकर चंगे होओ; परन्तु सब्त के दिन में नहीं।",
			"15": "<sup>15</sup> यह सुन कर प्रभु ने उत्तर देकर कहा; हे कपटियों, क्या सब्त के दिन तुम में से हर एक अपने बैल या गदहे को थान से खोलकर पानी पिलाने नहीं ले जाता?",
			"16": "<sup>16</sup> और क्या उचित न था, कि यह स्त्री जो इब्राहीम की बेटी है जिसे शैतान ने अठारह वर्ष से बान्ध रखा था, सब्त के दिन इस बन्धन से छुड़ाई जाती?",
			"17": "<sup>17</sup> जब उस ने ये बातें कहीं, तो उसके सब विरोधी लज्ज़ित हो गए, और सारी भीड़ उन महिमा के कामों से जो वह करता था, आनन्दित हुई॥",
			"18": "<sup>18</sup> फिर उस ने कहा, परमेश्वर का राज्य किस के समान है और मैं उस की उपमा किस से दूं?",
			"19": "<sup>19</sup> वह राई के एक दाने के समान है, जिसे किसी मनुष्य ने लेकर अपनी बारी में बोया: और वह बढ़कर पेड़ हो गया; और आकाश के पक्षियों ने उस की डालियों पर बसेरा किया।",
			"20": "<sup>20</sup> उस ने फिर कहा; मैं परमेश्वर के राज्य कि उपमा किस से दूं?",
			"21": "<sup>21</sup> वह खमीर के समान है, जिस को किसी स्त्री ने लेकर तीन पसेरी आटे में मिलाया, और होते होते सब आटा खमीर हो गया॥",
			"22": "<sup>22</sup> वह नगर नगर, और गांव गांव होकर उपदेश करता हुआ यरूशलेम की ओर जा रहा था।",
			"23": "<sup>23</sup> और किसी ने उस से पूछा; हे प्रभु, क्या उद्धार पाने वाले थोड़े हैं?",
			"24": "<sup>24</sup> उस ने उन से कहा; सकेत द्वार से प्रवेश करने का यत्न करो, क्योंकि मैं तुम से कहता हूं, कि बहुतेरे प्रवेश करना चाहेंगे, और न कर सकेंगे।",
			"25": "<sup>25</sup> जब घर का स्वामी उठकर द्वार बन्द कर चुका हो, और तुम बाहर खड़े हुए द्वार खटखटाकर कहने लगो, हे प्रभु, हमारे लिये खोल दे, और वह उत्तर दे कि मैं तुम्हें नहीं जानता, तुम कहां के हो?",
			"26": "<sup>26</sup> तब तुम कहने लगोगे, कि हम ने तेरे साम्हने खाया पीया और तू ने हमारे बजारों में उपदेश किया।",
			"27": "<sup>27</sup> परन्तु वह कहेगा, मैं तुम से कहता हूं, मैं नहीं जानता तुम कहां से हो, हे कुकर्म करनेवालो, तुम सब मुझ से दुर हो।",
			"28": "<sup>28</sup> वहां रोना और दांत पीसना होगा: जब तुम इब्राहीम और इसहाक और याकूब और सब भविष्यद्वक्ताओं को परमेश्वर के राज्य में बैठे, और अपने आप को बाहर निकाले हुए देखोगे।",
			"29": "<sup>29</sup> और पूर्व और पच्छिम; उत्तर और दक्खिन से लोग आकर परमेश्वर के राज्य के भोज में भागी होंगे।",
			"30": "<sup>30</sup> और देखो, कितने पिछले हैं वे प्रथम होंगे, और कितने जो प्रथम हैं, वे पिछले होंगे॥",
			"31": "<sup>31</sup> उसी घड़ी कितने फरीसियों ने आकर उस से कहा, यहां से निकलकर चला जा; क्योंकि हेरोदेस तुझे मार डालना चाहता है।",
			"32": "<sup>32</sup> उस ने उन से कहा; जाकर उस लोमड़ी से कह दो, कि देख मैं आज और कल दुष्टात्माओं को निकालता और बिमारों को चंगा करता हूं और तीसरे दिन पूरा करूंगा।",
			"33": "<sup>33</sup> तौभी मुझे आज और कल और परसों चलना अवश्य है, क्योंकि हो नही सकता कि कोई भविष्यद्वक्ता यरूशलेम के बाहर मारा जाए।",
			"34": "<sup>34</sup> हे यरूशलेम ! हे यरूशलेम ! तू जो भविष्यद्वक्ताओं को मार डालती है, और जो तेरे पास भेजे गए उन्हें पत्थरवाह करती है; कितनी बार मैं ने यह चाहा, कि जैसे मुर्गी अपने बच्चों को अपने पंखो के नीचे इकट्ठे करती है, वैसे ही मैं भी तेरे बालकों को इकट्ठे करूं, पर तुम ने यह न चाहा।",
			"35": "<sup>35</sup> देखो, तुम्हारा घर तुम्हारे लिये उजाड़ छोड़ा जाता है, और मैं तुम से कहता हूं; जब तक तुम न कहोगे, कि धन्य है वह, जो प्रभु के नाम से आता है, तब तक तुम मुझे फिर कभी न देखोगे॥"
		},
		"14": {
			"1": "<sup>1</sup> फिर वह सब्त के दिन फरीसियों के सरदारों में से किसी के घर में रोटी खाने गया: और वे उस की घात में थे।",
			"2": "<sup>2</sup> और देखो, एक मनुष्य उसके साम्हने था, जिसे जलन्धर का रोग था।",
			"3": "<sup>3</sup> इस पर यीशु ने व्यवस्थापकों और फरीसियों से कहा; क्या सब्त के दिन अच्छा करना उचित है, कि नहीं परन्तु वे चुपचाप रहे।",
			"4": "<sup>4</sup> तब उस ने उसे हाथ लगा कर चंगा किया, और जाने दिया।",
			"5": "<sup>5</sup> और उन से कहा; कि तुम में से ऐसा कौन है, जिस का गदहा या बैल कुएं में गिर जाए और वह सब्त के दिन उसे तुरन्त बाहर न निकाल ले?",
			"6": "<sup>6</sup> वे इन बातों का कुछ उत्तर न दे सके॥",
			"7": "<sup>7</sup> जब उस ने देखा, कि नेवताहारी लोग क्योंकर मुख्य मुख्य जगहें चुन लेते हैं तो एक दृष्टान्त देकर उन से कहा।",
			"8": "<sup>8</sup> जब कोई तुझे ब्याह में बुलाए, तो मुख्य जगह में न बैठना, कहीं ऐसा न हो, कि उस ने तुझ से भी किसी बड़े को नेवता दिया हो।",
			"9": "<sup>9</sup> और जिस ने तुझे और उसे दोनों को नेवता दिया है: आकर तुझ से कहे, कि इस को जगह दे, और तब तुझे लज्ज़ित होकर सब से नीची जगह में बैठना पड़े।",
			"10": "<sup>10</sup> पर जब तू बुलाया जाए, तो सब से नीची जगह जा बैठ, कि जब वह, जिस ने तुझे नेवता दिया है आए, तो तुझ से कहे कि हे मित्र, आगे बढ़कर बैठ; तब तेरे साथ बैठने वालों के साम्हने तेरी बड़ाई होगी।",
			"11": "<sup>11</sup> और जो कोई अपने आप को बड़ा बनाएगा, वह छोटा किया जाएगा; और जो कोई अपने आप को छोटा बनाएगा, वह बड़ा किया जाएगा॥",
			"12": "<sup>12</sup> तब उस ने अपने नेवता देने वाले से भी कहा, जब तू दिन का या रात का भोज करे, तो अपने मित्रों या भाइयों या कुटुम्बियों या धनवान पड़ोसियों न बुला, कहीं ऐसा न हो, कि वे भी तुझे नेवता दें, और तेरा बदला हो जाए।",
			"13": "<sup>13</sup> परन्तु जब तू भोज करे, तो कंगालों, टुण्डों, लंगड़ों और अन्धों को बुला।",
			"14": "<sup>14</sup> तब तू धन्य होगा, क्योंकि उन के पास तुझे बदला देने को कुछ नहीं, परन्तु तुझे धमिर्यों के जी उठने पर इस का प्रतिफल मिलेगा।",
			"15": "<sup>15</sup> उसके साथ भोजन करने वालों में से एक ने ये बातें सुनकर उस से कहा, धन्य है वह, जो परमेश्वर के राज्य में रोटी खाएगा।",
			"16": "<sup>16</sup> उस ने उस से कहा; किसी मनुष्य ने बड़ी जेवनार की और बहुतों को बुलाया।",
			"17": "<sup>17</sup> जब भोजन तैयार हो गया, तो उस ने अपने दास के हाथ नेवतहारियों को कहला भेजा, कि आओ; अब भोजन तैयार है।",
			"18": "<sup>18</sup> पर वे सब के सब क्षमा मांगने लगे, पहिले ने उस से कहा, मैं ने खेत मोल लिया है; और अवश्य है कि उसे देखूं: मैं तुझ से बिनती करता हूं, मुझे क्षमा करा दे।",
			"19": "<sup>19</sup> दूसरे ने कहा, मैं ने पांच जोड़े बैल मोल लिए हैं: और उन्हें परखने जाता हूं : मैं तुझ से बिनती करता हूं, मुझे क्षमा करा दे।",
			"20": "<sup>20</sup> एक और ने कहा; मै ने ब्याह किया है, इसलिये मैं नहीं आ सकता।",
			"21": "<sup>21</sup> उस दास ने आकर अपने स्वामी को ये बातें कह सुनाईं, तब घर के स्वामी ने क्रोध में आकर अपने दास से कहा, नगर के बाजारों और गलियों में तुरन्त जाकर कंगालों, टुण्डों, लंगड़ों और अन्धों को यहां ले आओ।",
			"22": "<sup>22</sup> दास ने फिर कहा; हे स्वामी, जैसे तू ने कहा था, वैसे ही किया गया है; फिर भी जगह है।",
			"23": "<sup>23</sup> स्वामी ने दास से कहा, सड़कों पर और बाड़ों की ओर जाकर लोगों को बरबस ले ही आ ताकि मेरा घर भर जाए।",
			"24": "<sup>24</sup> क्योंकि मैं तुम से कहता हूं, कि उन नेवते हुओं में से कोई मेरी जेवनार को न चखेगा।",
			"25": "<sup>25</sup> और जब बड़ी भीड़ उसके साथ जा रही थी, तो उस ने पीछे फिरकर उन से कहा।",
			"26": "<sup>26</sup> यदि कोई मेरे पास आए, और अपने पिता और माता और पत्नी और लड़के बालों और भाइयों और बहिनों बरन अपने प्राण को भी अप्रिय न जाने, तो वह मेरा चेला नहीं हो सकता।",
			"27": "<sup>27</sup> और जो कोई अपना क्रूस न उठाए; और मेरे पीछे न आए; वह भी मेरा चेला नहीं हो सकता।",
			"28": "<sup>28</sup> तुम में से कौन है कि गढ़ बनाना चाहता हो, और पहिले बैठकर खर्च न जोड़े, कि पूरा करने की बिसात मेरे पास है कि नहीं?",
			"29": "<sup>29</sup> कहीं ऐसा न हो, कि जब नेव डालकर तैयार न कर सके, तो सब देखने वाले यह कहकर उसे ठट्ठों में उड़ाने लगें।",
			"30": "<sup>30</sup> कि यह मनुष्य बनाने तो लगा, पर तैयार न कर सका?",
			"31": "<sup>31</sup> या कौन ऐसा राजा है, कि दूसरे राजा से युद्ध करने जाता हो, और पहिले बैठकर विचार न कर ले कि जो बीस हजार लेकर उसका साम्हना कर सकता हूं, कि नहीं?",
			"32": "<sup>32</sup> नहीं तो उसके दूर रहते ही, वह दूतों को भेजकर मिलाप करना चाहेगा।",
			"33": "<sup>33</sup> इसी रीति से तुम में से जो कोई अपना सब कुछ त्याग न दे, तो वह मेरा चेला नहीं हो सकता।",
			"34": "<sup>34</sup> नमक तो अच्छा है, परन्तु यदि नमक का स्वाद बिगड़ जाए, तो वह किस वस्तु से स्वादिष्ट किया जाएगा।",
			"35": "<sup>35</sup> वह न तो भूमि के और न खाद के लिये काम में आता है: उसे तो लोग बाहर फेंक देते हैं: जिस के सुनने के कान हों वह सुन ले॥"
		},
		"15": {
			"1": "<sup>1</sup> सब चुंगी लेने वाले और पापी उसके पास आया करते थे ताकि उस की सुनें।",
			"2": "<sup>2</sup> और फरीसी और शास्त्री कुड़कुड़ा कर कहने लगे, कि यह तो पापियों से मिलता है और उन के साथ खाता भी है॥",
			"3": "<sup>3</sup> तब उस ने उन से यह दृष्टान्त कहा।",
			"4": "<sup>4</sup> तुम में से कौन है जिस की सौ भेड़ें हों, और उन में से एक खो जाए तो निन्नानवे को जंगल में छोड़कर, उस खोई हुई को जब तक मिल न जाए खोजता न रहे?",
			"5": "<sup>5</sup> और जब मिल जाती है, तब वह बड़े आनन्द से उसे कांधे पर उठा लेता है।",
			"6": "<sup>6</sup> और घर में आकर मित्रों और पड़ोसियों को इकट्ठे करके कहता है, मेरे साथ आनन्द करो, क्योंकि मेरी खोई हुई भेड़ मिल गई है।",
			"7": "<sup>7</sup> मैं तुम से कहता हूं; कि इसी रीति से एक मन फिरानेवाले पापी के विषय में भी स्वर्ग में इतना ही आनन्द होगा, जितना कि निन्नानवे ऐसे धमिर्यों के विषय नहीं होता, जिन्हें मन फिराने की आवश्यकता नहीं॥",
			"8": "<sup>8</sup> या कौन ऐसी स्त्री होगी, जिस के पास दस सिक्के हों, और उन में से एक खो जाए; तो वह दीया बारकर और घर झाड़ बुहार कर जब तक मिल न जाए, जी लगाकर खोजती न रहे?",
			"9": "<sup>9</sup> और जब मिल जाता है, तो वह अपने सखियों और पड़ोसिनियों को इकट्ठी करके कहती है, कि मेरे साथ आनन्द करो, क्योंकि मेरा खोया हुआ सिक्का मिल गया है।",
			"10": "<sup>10</sup> मैं तुम से कहता हूं; कि इसी रीति से एक मन फिराने वाले पापी के विषय में परमेश्वर के स्वर्गदूतों के साम्हने आनन्द होता है॥",
			"11": "<sup>11</sup> फिर उस ने कहा, किसी मनुष्य के दो पुत्र थे।",
			"12": "<sup>12</sup> उन में से छुटके ने पिता से कहा कि हे पिता संपत्ति में से जो भाग मेरा हो, वह मुझे दे दीजिए। उस ने उन को अपनी संपत्ति बांट दी।",
			"13": "<sup>13</sup> और बहुत दिन न बीते थे कि छुटका पुत्र सब कुछ इकट्ठा करके एक दूर देश को चला गया और वहां कुकर्म में अपनी संपत्ति उड़ा दी।",
			"14": "<sup>14</sup> जब वह सब कुछ खर्च कर चुका, तो उस देश में बड़ा अकाल पड़ा, और वह कंगाल हो गया।",
			"15": "<sup>15</sup> और वह उस देश के निवासियों में से एक के यहां जा पड़ा : उस ने उसे अपने खेतों में सूअर चराने के लिये भेजा।",
			"16": "<sup>16</sup> और वह चाहता था, कि उन फलियों से जिन्हें सूअर खाते थे अपना पेट भरे; और उसे कोई कुछ नहीं देता था।",
			"17": "<sup>17</sup> जब वह अपने आपे में आया, तब कहने लगा, कि मेरे पिता के कितने ही मजदूरों को भोजन से अधिक रोटी मिलती है, और मैं यहां भूखा मर रहा हूं।",
			"18": "<sup>18</sup> मैं अब उठकर अपने पिता के पास जाऊंगा और उस से कहूंगा कि पिता जी मैं ने स्वर्ग के विरोध में और तेरी दृष्टि में पाप किया है।",
			"19": "<sup>19</sup> अब इस योग्य नहीं रहा कि तेरा पुत्र कहलाऊं, मुझे अपने एक मजदूर की नाईं रख ले।",
			"20": "<sup>20</sup> तब वह उठकर, अपने पिता के पास चला: वह अभी दूर ही था, कि उसके पिता ने उसे देखकर तरस खाया, और दौड़कर उसे गले लगाया, और बहुत चूमा।",
			"21": "<sup>21</sup> पुत्र ने उस से कहा; पिता जी, मैं ने स्वर्ग के विरोध में और तेरी दृष्टि में पाप किया है; और अब इस योग्य नहीं रहा, कि तेरा पुत्र कहलाऊं।",
			"22": "<sup>22</sup> परन्तु पिता ने अपने दासों से कहा; फट अच्छे से अच्छा वस्त्र निकालकर उसे पहिनाओ, और उसके हाथ में अंगूठी, और पांवों में जूतियां पहिनाओ।",
			"23": "<sup>23</sup> और पला हुआ बछड़ा लाकर मारो ताकि हम खांए और आनन्द मनावें।",
			"24": "<sup>24</sup> क्योंकि मेरा यह पुत्र मर गया था, फिर जी गया है : खो गया था, अब मिल गया है: और वे आनन्द करने लगे।",
			"25": "<sup>25</sup> परन्तु उसका जेठा पुत्र खेत में था : और जब वह आते हुए घर के निकट पहुंचा, तो उस ने गाने बजाने और नाचने का शब्द सुना।",
			"26": "<sup>26</sup> और उस ने एक दास को बुलाकर पूछा; यह क्या हो रहा है?",
			"27": "<sup>27</sup> उस ने उस से कहा, तेरा भाई आया है; और तेरे पिता ने पला हुआ बछड़ा कटवाया है, इसलिये कि उसे भला चंगा पाया है।",
			"28": "<sup>28</sup> यह सुनकर वह क्रोध से भर गया, और भीतर जाना न चाहा : परन्तु उसका पिता बाहर आकर उसे मनाने लगा।",
			"29": "<sup>29</sup> उस ने पिता को उत्तर दिया, कि देख; मैं इतने वर्ष से तरी सेवा कर रहा हूं, और कभी भी तेरी आज्ञा नहीं टाली, तौभी तू ने मुझे कभी एक बकरी का बच्चा भी न दिया, कि मैं अपने मित्रों के साथ आनन्द करता।",
			"30": "<sup>30</sup> परन्तु जब तेरा यह पुत्र, जिस ने तेरी संपत्ति वेश्याओं में उड़ा दी है, आया, तो उसके लिये तू ने पला हुआ बछड़ा कटवाया।",
			"31": "<sup>31</sup> उस ने उस से कहा; पुत्र, तू सर्वदा मेरे साथ है; और जो कुछ मेरा है वह सब तेरा ही है।",
			"32": "<sup>32</sup> परन्तु अब आनन्द करना और मगन होना चाहिए क्योंकि यह तेरा भाई मर गया था फिर जी गया है; खो गया था, अब मिल गया है॥"
		},
		"16": {
			"1": "<sup>1</sup> फिर उस ने चेलों से भी कहा; किसी धनवान का एक भण्डारी था, और लोगों ने उसके साम्हने उस पर यह दोष लगाया कि यह तेरी सब संपत्ति उड़ाए देता है।",
			"2": "<sup>2</sup> सो उस ने उसे बुलाकर कहा, यह क्या है जो मै तेरे विषय में सुन रहा हूं? अपने भण्डारीपन का लेखा दे; क्योंकि तू आगे को भण्डारी नहीं रह सकता।",
			"3": "<sup>3</sup> तब भण्डारी सोचने लगा, कि अब मैं क्या करूं क्योंकि मेरा स्वामी अब भण्डारी का काम मुझ से छीन ले रहा है: मिट्टी तो मुझ से खोदी नहीं जाती: और भीख मांगने से मुझे लज्ज़ा आती है।",
			"4": "<sup>4</sup> मैं समझ गया, कि क्या करूंगा: ताकि जब मैं भण्डारी के काम से छुड़ाया जाऊं तो लोग मुझे अपने घरों में ले लें।",
			"5": "<sup>5</sup> और उस ने अपने स्वामी के देनदारों में से एक एक को बुलाकर पहिले से पूछा, कि तुझ पर मेरे स्वामी का क्या आता है?",
			"6": "<sup>6</sup> उस ने कहा, सौ मन तेल; तब उस ने उस से कहा, कि अपनी खाता-बही ले और बैठकर तुरन्त पचास लिख दे।",
			"7": "<sup>7</sup> फिर दूसरे से पूछा; तुझ पर क्या आता है? उस ने कहा, सौ मन गेहूं; तब उस ने उस से कहा; अपनी खाता-बही लेकर अस्सी लिख दे।",
			"8": "<sup>8</sup> स्वामी ने उस अधर्मी भण्डारी को सराहा, कि उस ने चतुराई से काम किया है; क्योंकि इस संसार के लोग अपने समय के लोगों के साथ रीति व्यवहारों में ज्योति के लोगों से अधिक चतुर हैं।",
			"9": "<sup>9</sup> और मैं तुम से कहता हूं, कि अधर्म के धन से अपने लिये मित्र बना लो; ताकि जब वह जाता रहे, तो वे तुम्हें अनन्त निवासों में ले लें।",
			"10": "<sup>10</sup> जो थोड़े से थोड़े में सच्चा है, वह बहुत में भी सच्चा है: और जो थोड़े से थोड़े में अधर्मी है, वह बहुत में भी अधर्मी है।",
			"11": "<sup>11</sup> इसलिये जब तुम अधर्म के धन में सच्चे न ठहरे, तो सच्चा तुम्हें कौन सौंपेगा।",
			"12": "<sup>12</sup> और यदि तुम पराये धन में सच्चे न ठहरे, तो जो तुम्हारा है, उसे तुम्हें कौन देगा?",
			"13": "<sup>13</sup> कोई दास दो स्वामियों की सेवा नहीं कर सकता: क्योंकि वह तो एक से बैर और दूसरे से प्रेम रखेगा; या एक से मिल रहेगा और दूसरे को तुच्छ जानेगा: तुम परमेश्वर और धन दोनों की सेवा नहीं कर सकते॥",
			"14": "<sup>14</sup> फरीसी जो लोभी थे, ये सब बातें सुन कर उसे ठट्ठों में उड़ाने लगे।",
			"15": "<sup>15</sup> उस ने उन से कहा; तुम तो मनुष्यों के साम्हने अपने आप को धर्मी ठहराते हो: परन्तु परमेश्वर तुम्हारे मन को जानता है, क्योंकि जो वस्तु मनुष्यों की दृष्टि में महान है, वह परमेश्वर के निकट घृणित है।",
			"16": "<sup>16</sup> व्यवस्था और भविष्यद्वक्ता यूहन्ना तक रहे, उस समय से परमेश्वर के राज्य का सुसमाचार सुनाया जा रहा है, और हर कोई उस में प्रबलता से प्रवेश करता है।",
			"17": "<sup>17</sup> आकाश और पृथ्वी का टल जाना व्यवस्था के एक बिन्दु के मिट जाने से सहज है।",
			"18": "<sup>18</sup> जो कोई अपनी पत्नी को त्यागकर दूसरी से ब्याह करता है, वह व्यभिचार करता है, और जो कोई ऐसी त्यागी हुई स्त्री से ब्याह करता है, वह भी व्यभिचार करता है।",
			"19": "<sup>19</sup> एक धनवान मनुष्य था जो बैंजनी कपड़े और मलमल पहिनता और प्रति दिन सुख-विलास और धूम-धाम के साथ रहता था।",
			"20": "<sup>20</sup> और लाजर नाम का एक कंगाल घावों से भरा हुआ उस की डेवढ़ी पर छोड़ दिया जाता था।",
			"21": "<sup>21</sup> और वह चाहता था, कि धनवान की मेज पर की जूठन से अपना पेट भरे; वरन कुत्ते भी आकर उसके घावों को चाटते थे।",
			"22": "<sup>22</sup> और ऐसा हुआ कि वह कंगाल मर गया, और स्वर्गदूतों ने उसे लेकर इब्राहीम की गोद में पहुंचाया; और वह धनवान भी मरा; और गाड़ा गया।",
			"23": "<sup>23</sup> और अधोलोक में उस ने पीड़ा में पड़े हुए अपनी आंखें उठाई, और दूर से इब्राहीम की गोद में लाजर को देखा।",
			"24": "<sup>24</sup> और उस ने पुकार कर कहा, हे पिता इब्राहीम, मुझ पर दया करके लाजर को भेज दे, ताकि वह अपनी उंगुली का सिरा पानी में भिगो कर मेरी जीभ को ठंडी करे, क्योंकि मैं इस ज्वाला में तड़प रहा हूं।",
			"25": "<sup>25</sup> परन्तु इब्राहीम ने कहा; हे पुत्र स्मरण कर, कि तू अपने जीवन में अच्छी वस्तुएं ले चुका है, और वैसे ही लाजर बुरी वस्तुएं: परन्तु अब वह यहां शान्ति पा रहा है, और तू तड़प रहा है।",
			"26": "<sup>26</sup> और इन सब बातों को छोड़ हमारे और तुम्हारे बीच एक भारी गड़हा ठहराया गया है कि जो यहां से उस पार तुम्हारे पास जाना चाहें, वे न जा सकें, और न कोई वहां से इस पार हमारे पास आ सके।",
			"27": "<sup>27</sup> उस ने कहा; तो हे पिता मैं तुझ से बिनती करता हूं, कि तू उसे मेरे पिता के घर भेज।",
			"28": "<sup>28</sup> क्योंकि मेरे पांच भाई हैं, वह उन के साम्हने इन बातों की गवाही दे, ऐसा न हो कि वे भी इस पीड़ा की जगह में आएं।",
			"29": "<sup>29</sup> इब्राहीम ने उस से कहा, उन के पास तो मूसा और भविष्यद्वक्ताओं की पुस्तकें हैं, वे उन की सुनें।",
			"30": "<sup>30</sup> उस ने कहा; नहीं, हे पिता इब्राहीम; पर यदि कोई मरे हुओं में से उन के पास जाए, तो वे मन फिराएंगे।",
			"31": "<sup>31</sup> उस ने उस से कहा, कि जब वे मूसा और भविष्यद्वक्ताओं की नहीं सुनते, तो यदि मरे हुओं में से कोई भी जी उठे तौभी उस की नहीं मानेंगे॥"
		},
		"17": {
			"1": "<sup>1</sup> फिर उस ने अपने चेलों से कहा; हो नहीं सकता कि ठोकरें न लगें, परन्तु हाय, उस मनुष्य पर जिस के कारण वे आती हैं!",
			"2": "<sup>2</sup> जो इन छोटों में से किसी एक को ठोकर खिलाता है, उसके लिये यह भला होता, कि चक्की का पाट उसके गले में लटकाया जाता, और वह समुद्र में डाल दिया जाता।",
			"3": "<sup>3</sup> सचेत रहो; यदि तेरा भाई अपराध करे तो उसे समझा, और यदि पछताए तो उसे क्षमा कर।",
			"4": "<sup>4</sup> यदि दिन भर में वह सात बार तेरा अपराध करे और सातों बार तेरे पास फिर आकर कहे, कि मैं पछताता हूं, तो उसे क्षमा कर॥",
			"5": "<sup>5</sup> तब प्रेरितों ने प्रभु से कहा, हमारा विश्वास बढ़ा।",
			"6": "<sup>6</sup> प्रभु ने कहा; कि यदि तुम को राई के दाने के बराबर भी विश्वास होता, तो तुम इस तूत के पेड़ से कहते कि जड़ से उखड़कर समुद्र में लग जा, तो वह तुम्हारी मान लेता।",
			"7": "<sup>7</sup> पर तुम में से ऐसा कौन है, जिस का दास हल जोतता, या भेंड़ें चराता हो, और जब वह खेत से आए, तो उस से कहे तुरन्त आकर भोजन करने बैठ?",
			"8": "<sup>8</sup> और यह न कहे, कि मेरा खाना तैयार कर: और जब तक मैं खाऊं-पीऊं तब तक कमर बान्धकर मेरी सेवा कर; इस के बाद तू भी खा पी लेना।",
			"9": "<sup>9</sup> क्या वह उस दास का निहोरा मानेगा, कि उस ने वे ही काम किए जिस की आज्ञा दी गई थी?",
			"10": "<sup>10</sup> इसी रीति से तुम भी, जब उन सब कामों को कर चुको जिस की आज्ञा तुम्हें दी गई थी, तो कहा, हम निकम्मे दास हैं; कि जो हमें करना चाहिए था वही किया है॥",
			"11": "<sup>11</sup> और ऐसा हुआ कि वह यरूशलेम को जाते हुए सामरिया और गलील के बीच से होकर जा रहा था।",
			"12": "<sup>12</sup> और किसी गांव में प्रवेश करते समय उसे दस कोढ़ी मिले।",
			"13": "<sup>13</sup> और उन्होंने दूर खड़े होकर, ऊंचे शब्द से कहा, हे यीशु, हे स्वामी, हम पर दया कर।",
			"14": "<sup>14</sup> उस ने उन्हें देखकर कहा, जाओ; और अपने तई याजकों को दिखाओ; और जाते ही जाते वे शुद्ध हो गए।",
			"15": "<sup>15</sup> तब उन में से एक यह देखकर कि मैं चंगा हो गया हूं, ऊंचे शब्द से परमेश्वर की बड़ाई करता हुआ लौटा।",
			"16": "<sup>16</sup> और यीशु के पांवों पर मुंह के बल गिरकर, उसका धन्यवाद करने लगा; और वह सामरी था।",
			"17": "<sup>17</sup> इस पर यीशु ने कहा, क्या दसों शुद्ध न हुए? तो फिर वे नौ कहां हैं?",
			"18": "<sup>18</sup> क्या इस परदेशी को छोड़ कोई और न निकला, जो परमेश्वर की बड़ाई करता?",
			"19": "<sup>19</sup> तब उस ने उस से कहा; उठकर चला जा; तेरे विश्वास ने तुझे चंगा किया है॥",
			"20": "<sup>20</sup> जब फरीसियों ने उस से पूछा, कि परमेश्वर का राज्य कब आएगा? तो उस ने उन को उत्तर दिया, कि परमेश्वर का राज्य प्रगट रूप से नहीं आता।",
			"21": "<sup>21</sup> और लोग यह न कहेंगे, कि देखो, यहां है, या वहां है, क्योंकि देखो, परमेश्वर का राज्य तुम्हारे बीच में है॥",
			"22": "<sup>22</sup> और उस ने चेलों से कहा; वे दिन आएंगे, जिन में तुम मनुष्य के पुत्र के दिनों में से एक दिन को देखना चाहोगे, और नहीं देखने पाओगे।",
			"23": "<sup>23</sup> लोग तुम से कहेंगे, देखो, वहां है, या देखो यहां है; परन्तु तुम चले न जाना और न उन के पीछे हो लेना।",
			"24": "<sup>24</sup> क्योंकि जैसे बिजली आकाश की एक ओर से कौन्धकर आकाश की दूसरी ओर चमकती है, वैसे ही मनुष्य का पुत्र भी अपने दिन में प्रगट होगा।",
			"25": "<sup>25</sup> परन्तु पहिले अवश्य है, कि वह बहुत दुख उठाए, और इस युग के लोग उसे तुच्छ ठहराएं।",
			"26": "<sup>26</sup> जैसा नूह के दिनों में हुआ था, वैसा ही मनुष्य के पुत्र के दिनों में भी होगा।",
			"27": "<sup>27</sup> जिस दिन तक नूह जहाज पर न चढ़ा, उस दिन तक लोग खाते-पीते थे, और उन में ब्याह-शादी होती थी; तब जल-प्रलय ने आकर उन सब को नाश किया।",
			"28": "<sup>28</sup> और जैसा लूत के दिनों में हुआ था, कि लोग खाते-पीते लेन-देन करते, पेड़ लगाते और घर बनाते थे।",
			"29": "<sup>29</sup> परन्तु जिस दिन लूत सदोम से निकला, उस दिन आग और गन्धक आकाश से बरसी और सब को नाश कर दिया।",
			"30": "<sup>30</sup> मनुष्य के पुत्र के प्रगट होने के दिन भी ऐसा ही होगा।",
			"31": "<sup>31</sup> उस दिन जो कोठे पर हो; और उसका सामान घर में हो, वह उसे लेने को न उतरे, और वैसे ही जो खेत में हो वह पीछे न लौटे।",
			"32": "<sup>32</sup> लूत की पत्नी को स्मरण रखो।",
			"33": "<sup>33</sup> जो कोई अपना प्राण बचाना चाहे वह उसे खोएगा, और जो कोई उसे खोए वह उसे जीवित रखेगा।",
			"34": "<sup>34</sup> मैं तुम से कहता हूं, उस रात दो मनुष्य एक खाट पर होंगे, एक ले लिया जाएगा, और दूसरा छोड़ दिया जाएगा।",
			"35": "<sup>35</sup> दो स्त्रियां एक साथ चक्की पीसती होंगी, एक ले ली जाएगी, और दूसरी छोड़ दी जाएगी।",
			"36": "<sup>36</sup> दो जन खेत में होंगे एक ले लिया जाएगा और दूसरा छोड़ा जाएगा।",
			"37": "<sup>37</sup> यह सुन उन्होंने उस से पूछा, हे प्रभु यह कहां होगा? उस ने उन से कहा, जहां लोथ हैं, वहां गिद्ध इकट्ठे होंगे॥"
		},
		"18": {
			"1": "<sup>1</sup> फिर उस ने इस के विषय में कि नित्य प्रार्थना करना और हियाव न छोड़ना चाहिए उन से यह दृष्टान्त कहा।",
			"2": "<sup>2</sup> कि किसी नगर में एक न्यायी रहता था; जो न परमेश्वर से डरता था और न किसी मनुष्य की परवाह करता था।",
			"3": "<sup>3</sup> और उसी नगर में एक विधवा भी रहती थी: जो उसके पास आ आकर कहा करती थी, कि मेरा न्याय चुकाकर मुझे मुद्दई से बचा।",
			"4": "<sup>4</sup> उस ने कितने समय तक तो न माना परन्तु अन्त में मन में विचारकर कहा, यद्यपि मैं न परमेश्वर से डरता, और न मनुष्यों की कुछ परवाह करता हूं।",
			"5": "<sup>5</sup> तौभी यह विधवा मुझे सताती रहती है, इसलिये मैं उसका न्याय चुकाऊंगा कहीं ऐसा न हो कि घड़ी घड़ी आकर अन्त को मेरा नाक में दम करे।",
			"6": "<sup>6</sup> प्रभु ने कहा, सुनो, कि यह अधर्मी न्यायी क्या कहता है?",
			"7": "<sup>7</sup> सो क्या परमेश्वर अपने चुने हुओं का न्याय न चुकाएगा, जो रात-दिन उस की दुहाई देते रहते; और क्या वह उन के विषय में देर करेगा?",
			"8": "<sup>8</sup> मैं तुम से कहता हूं; वह तुरन्त उन का न्याय चुकाएगा; तौभी मनुष्य का पुत्र जब आएगा, तो क्या वह पृथ्वी पर विश्वास पाएगा?",
			"9": "<sup>9</sup> और उस ने कितनो से जो अपने ऊपर भरोसा रखते थे, कि हम धर्मी हैं, और औरों को तुच्छ जानते थे, यह दृष्टान्त कहा।",
			"10": "<sup>10</sup> कि दो मनुष्य मन्दिर में प्रार्थना करने के लिये गए; एक फरीसी था और दूसरा चुंगी लेने वाला।",
			"11": "<sup>11</sup> फरीसी खड़ा होकर अपने मन में यों प्रार्थना करने लगा, कि हे परमेश्वर, मैं तेरा धन्यवाद करता हूं, कि मैं और मनुष्यों की नाईं अन्धेर करने वाला, अन्यायी और व्यभिचारी नहीं, और न इस चुंगी लेने वाले के समान हूं।",
			"12": "<sup>12</sup> मैं सप्ताह में दो बार उपवास करता हूं; मैं अपनी सब कमाई का दसवां अंश भी देता हूं।",
			"13": "<sup>13</sup> परन्तु चुंगी लेने वाले ने दूर खड़े होकर, स्वर्ग की ओर आंखें उठाना भी न चाहा, वरन अपनी छाती पीट-पीटकर कहा; हे परमेश्वर मुझ पापी पर दया कर।",
			"14": "<sup>14</sup> मैं तुम से कहता हूं, कि वह दूसरा नहीं; परन्तु यही मनुष्य धर्मी ठहराया जाकर अपने घर गया; क्योंकि जो कोई अपने आप को बड़ा बनाएगा, वह छोटा किया जाएगा; और जो अपने आप को छोटा बनाएगा, वह बड़ा किया जाएगा॥",
			"15": "<sup>15</sup> फिर लोग अपने बच्चों को भी उसके पास लाने लगे, कि वह उन पर हाथ रखे; और चेलों ने देख कर उन्हें डांटा।",
			"16": "<sup>16</sup> यीशु ने बच्चों को पास बुलाकर कहा, बालकों को मेरे पास आने दो, और उन्हें मना न करो: क्योंकि परमेश्वर का राज्य ऐसों ही का है।",
			"17": "<sup>17</sup> मैं तुम से सच कहता हूं, कि जो कोई परमेश्वर के राज्य को बालक की नाईं ग्रहण न करेगा वह उस में कभी प्रवेश करने न पाएगा॥",
			"18": "<sup>18</sup> किसी सरदार ने उस से पूछा, हे उत्तम गुरू, अनन्त जीवन का अधिकारी होने के लिये मैं क्या करूं?",
			"19": "<sup>19</sup> यीशु ने उस से कहा; तू मुझे उत्तम क्यों कहता है? कोई उत्तम नहीं, केवल एक, अर्थात परमेश्वर।",
			"20": "<sup>20</sup> तू आज्ञाओं को तो जानता है, कि व्यभिचार न करना, हत्या न करना, और चोरी न करना, झूठी गवाही न देना, अपने पिता और अपनी माता का आदर करना।",
			"21": "<sup>21</sup> उस ने कहा, मैं तो इन सब को लड़कपन ही से मानता आया हूं।",
			"22": "<sup>22</sup> यह सुन, यीशु ने उस से कहा, तुझ में अब भी एक बात की घटी है, अपना सब कुछ बेच कर कंगालों को बांट दे; और तुझे स्वर्ग में धन मिलेगा, और आकर मेरे पीछे हो ले।",
			"23": "<sup>23</sup> वह यह सुनकर बहुत उदास हुआ, क्योंकि वह बड़ा धनी था।",
			"24": "<sup>24</sup> यीशु ने उसे देख कर कहा; धनवानों का परमेश्वर के राज्य में प्रवेश करना कैसा कठिन है?",
			"25": "<sup>25</sup> परमेश्वर के राज्य में धनवान के प्रवेश करने से ऊंट का सूई के नाके में से निकल जाना सहज है।",
			"26": "<sup>26</sup> और सुनने वालों ने कहा, तो फिर किस का उद्धार हो सकता है?",
			"27": "<sup>27</sup> उस ने कहा; जो मनुष्य से नहीं हो सकता, वह परमेश्वर से हो सकता है।",
			"28": "<sup>28</sup> पतरस ने कहा; देख, हम तो घर बार छोड़कर तेरे पीछे हो लिये हैं।",
			"29": "<sup>29</sup> उस ने उन से कहा; मैं तुम से सच कहता हूं, कि ऐसा कोई नहीं जिस ने परमेश्वर के राज्य के लिये घर या पत्नी या भाइयों या माता पिता या लड़के-बालों को छोड़ दिया हो।",
			"30": "<sup>30</sup> और इस समय कई गुणा अधिक न पाए; और परलोक में अनन्त जीवन॥",
			"31": "<sup>31</sup> फिर उस ने बारहों को साथ लेकर उन से कहा; देखो, हम यरूशलेम को जाते हैं, और जितनी बातें मनुष्य के पुत्र के लिये भविष्यद्वक्ताओं के द्वारा लिखी गई हैं वे सब पूरी होंगी।",
			"32": "<sup>32</sup> क्योंकि वह अन्यजातियों के हाथ में सौंपा जाएगा, और वे उसे ठट्ठों में उड़ाएंगे; और उसका अपमान करेंगे, और उस पर थूकेंगे।",
			"33": "<sup>33</sup> और उसे कोड़े मारेंगे, और घात करेंगे, और वह तीसरे दिन जी उठेगा।",
			"34": "<sup>34</sup> और उन्होंने इन बातों में से कोई बात न समझी: और यह बात उन में छिपी रही, और जो कहा गया था वह उन की समझ में न आया॥",
			"35": "<sup>35</sup> जब वह यरीहो के निकट पहुंचा, तो एक अन्धा सड़क के किनारे बैठा हुआ भीख मांग रहा था।",
			"36": "<sup>36</sup> और वह भीड़ के चलने की आहट सुनकर पूछने लगा, यह क्या हो रहा है?",
			"37": "<sup>37</sup> उन्होंने उस को बताया, कि यीशु नासरी जा रहा है।",
			"38": "<sup>38</sup> तब उस ने पुकार के कहा, हे यीशु दाऊद की सन्तान, मुझ पर दया कर।",
			"39": "<sup>39</sup> जो आगे जाते थे, वे उसे डांटने लगे कि चुप रहे: परन्तु वह और भी चिल्लाने लगा, कि हे दाऊद की सन्तान, मुझ पर दया कर।",
			"40": "<sup>40</sup> तब यीशु ने खड़े होकर आज्ञा दी कि उसे मेरे पास लाओ, और जब वह निकट आया, तो उस ने उस से यह पूछा।",
			"41": "<sup>41</sup> तू क्या चाहता है, कि मैं तेरे लिये करूं? उस ने कहा; हे प्रभु यह कि मैं देखने लगूं।",
			"42": "<sup>42</sup> यीशु ने उससे कहा; देखने लग, तेरे विश्वास ने तुझे अच्छा कर दिया है।",
			"43": "<sup>43</sup> और वह तुरन्त देखने लगा; और परमेश्वर की बड़ाई करता हुआ उसके पीछे हो लिया, और सब लोगों ने देख कर परमेश्वर की स्तुति की॥"
		},
		"19": {
			"1": "<sup>1</sup> वह यरीहो में प्रवेश करके जा रहा था।",
			"2": "<sup>2</sup> और देखो, ज़क्कई नाम एक मनुष्य था जो चुंगी लेने वालों का सरदार और धनी था।",
			"3": "<sup>3</sup> वह यीशु को देखना चाहता था कि वह कौन सा है परन्तु भीड़ के कारण देख न सकता था। क्योंकि वह नाटा था।",
			"4": "<sup>4</sup> तब उस को देखने के लिये वह आगे दौड़कर एक गूलर के पेड़ पर चढ़ गया, क्योंकि वह उसी मार्ग से जाने वाला था।",
			"5": "<sup>5</sup> जब यीशु उस जगह पहुंचा, तो ऊपर दृष्टि कर के उस से कहा; हे ज़क्कई झट उतर आ; क्योंकि आज मुझे तेरे घर में रहना अवश्य है।",
			"6": "<sup>6</sup> वह तुरन्त उतर कर आनन्द से उसे अपने घर को ले गया।",
			"7": "<sup>7</sup> यह देख कर सब लोगे कुड़कुड़ा कर कहने लगे, वह तो एक पापी मनुष्य के यहां जा उतरा है।",
			"8": "<sup>8</sup> ज़क्कई ने खड़े होकर प्रभु से कहा; हे प्रभु, देख मैं अपनी आधी सम्पत्ति कंगालों को देता हूं, और यदि किसी का कुछ भी अन्याय करके ले लिया है तो उसे चौगुना फेर देता हूं।",
			"9": "<sup>9</sup> तब यीशु ने उस से कहा; आज इस घर में उद्धार आया है, इसलिये कि यह भी इब्राहीम का एक पुत्र है।",
			"10": "<sup>10</sup> क्योंकि मनुष्य का पुत्र खोए हुओं को ढूंढ़ने और उन का उद्धार करने आया है॥",
			"11": "<sup>11</sup> जब वे ये बातें सुन रहे थे, तो उस ने एक दृष्टान्त कहा, इसलिये कि वह यरूशलेम के निकट था, और वे समझते थे, कि परमेश्वर का राज्य अभी प्रगट हुआ चाहता है।",
			"12": "<sup>12</sup> सो उस ने कहा, एक धनी मनुष्य दूर देश को चला ताकि राजपद पाकर फिर आए।",
			"13": "<sup>13</sup> और उस ने अपने दासों में से दस को बुलाकर उन्हें दस मुहरें दीं, और उन से कहा, मेरे लौट आने तक लेन-देन करना।",
			"14": "<sup>14</sup> परन्तु उसके नगर के रहने वाले उस से बैर रखते थे, और उसके पीछे दूतों के द्वारा कहला भेजा, कि हम नहीं चाहते, कि यह हम पर राज्य करे।",
			"15": "<sup>15</sup> जब वह राजपद पाकर लौट आया, तो ऐसा हुआ कि उस ने अपने दासों को जिन्हें रोकड़ दी थी, अपने पास बुलवाया ताकि मालूम करे कि उन्होंने लेन-देन से क्या क्या कमाया।",
			"16": "<sup>16</sup> तब पहिले ने आकर कहा, हे स्वामी तेरे मोहर से दस और मोहरें कमाई हैं।",
			"17": "<sup>17</sup> उस ने उस से कहा; धन्य हे उत्तम दास, तुझे धन्य है, तू बहुत ही थोड़े में विश्वासी निकला अब दस नगरों पर अधिकार रख।",
			"18": "<sup>18</sup> दूसरे ने आकर कहा; हे स्वामी तेरी मोहर से पांच और मोहरें कमाई हैं।",
			"19": "<sup>19</sup> उस ने कहा, कि तू भी पांच नगरों पर हाकिम हो जा।",
			"20": "<sup>20</sup> तीसरे ने आकर कहा; हे स्वामी देख, तेरी मोहर यह है, जिसे मैं ने अंगोछे में बान्ध रखी।",
			"21": "<sup>21</sup> क्योंकि मैं तुझ से डरता था, इसलिये कि तू कठोर मनुष्य है: जो तू ने नहीं रखा उसे उठा लेता है, और जो तू ने नहीं बोया, उसे काटता है।",
			"22": "<sup>22</sup> उस ने उस से कहा; हे दुष्ट दास, मैं तेरे ही मुंह से तुझे दोषी ठहराता हूं: तू मुझे जानता था कि कठोर मनुष्य हूं, जो मैं ने नहीं रखा उसे उठा लेता, और जो मैं ने नहीं बोया, उसे काटता हूं।",
			"23": "<sup>23</sup> तो तू ने मेरे रूपये कोठी में क्यों नहीं रख दिए, कि मैं आकर ब्याज समेत ले लेता?",
			"24": "<sup>24</sup> और जो लोग निकट खड़े थे, उस ने उन से कहा, वह मोहर उस से ले लो, और जिस के पास दस मोहरें हैं उसे दे दो।",
			"25": "<sup>25</sup> (उन्होंने उस से कहा; हे स्वामी, उसके पास दस मोहरें तो हैं)।",
			"26": "<sup>26</sup> मैं तुम से कहता हूं, कि जिस के पास है, उसे दिया जाएगा; और जिस के पास नहीं, उस से वह भी जो उसके पास है ले लिया जाएगा।",
			"27": "<sup>27</sup> परन्तु मेरे उन बैरियों को जो नहीं चाहते थे कि मैं उन पर राज्य करूं, उन को यहां लाकर मेरे सामने घात करो॥",
			"28": "<sup>28</sup> ये बातें कहकर वह यरूशलेम की ओर उन के आगे आगे चला॥",
			"29": "<sup>29</sup> और जब वह जैतून नाम पहाड़ पर बैतफगे और बैतनियाह के पास पहुंचा, तो उस ने अपने चेलों में से दो को यह कहके भेजा।",
			"30": "<sup>30</sup> कि साम्हने के गांव में जाओ, और उस में पहुंचते ही एक गदही का बच्चा जिस पर कभी कोई सवार नहीं हुआ, बन्धा हुआ तुम्हें मिलेगा, उसे खोल कर लाओ।",
			"31": "<sup>31</sup> और यदि कोई तुम से पूछे, कि क्यों खोलते हो, तो यह कह देना, कि प्रभु को इस का प्रयोजन है।",
			"32": "<sup>32</sup> जो भेजे गए थे; उन्होंने जाकर जैसा उस ने उन से कहा था, वैसा ही पाया।",
			"33": "<sup>33</sup> जब वे गदहे के बच्चे को खोल रहे थे, तो उसके मालिकों ने उन से पूछा; इस बच्चे को क्यों खोलते हो?",
			"34": "<sup>34</sup> उन्होंने कहा, प्रभु को इस का प्रयोजन है।",
			"35": "<sup>35</sup> वे उस को यीशु के पास ले आए और अपने कपड़े उस बच्चे पर डालकर यीशु को उस पर सवार किया।",
			"36": "<sup>36</sup> जब वह जा रहा था, तो वे अपने कपड़े मार्ग में बिछाते जाते थे।",
			"37": "<sup>37</sup> और निकट आते हुए जब वह जैतून पहाड़ की ढलान पर पहुंचा, तो चेलों की सारी मण्डली उन सब सामर्थ के कामों के कारण जो उन्होंने देखे थे, आनन्दित होकर बड़े शब्द से परमेश्वर की स्तुति करने लगी।",
			"38": "<sup>38</sup> कि धन्य है वह राजा, जो प्रभु के नाम से आता है; स्वर्ग में शान्ति और आकाश मण्डल में महिमा हो।",
			"39": "<sup>39</sup> तब भीड़ में से कितने फरीसी उस से कहने लगे, हे गुरू अपने चेलों को डांट।",
			"40": "<sup>40</sup> उस ने उत्तर दिया, कि तुम से कहता हूं, यदि ये चुप रहें, तो पत्थर चिल्ला उठेंगे॥",
			"41": "<sup>41</sup> जब वह निकट आया तो नगर को देखकर उस पर रोया।",
			"42": "<sup>42</sup> और कहा, क्या ही भला होता, कि तू; हां, तू ही, इसी दिन में कुशल की बातें जानता, परन्तु अब वे तेरी आंखों से छिप गई हैं।",
			"43": "<sup>43</sup> क्योंकि वे दिन तुझ पर आएंगे कि तेरे बैरी मोर्चा बान्धकर तुझे घेर लेंगे, और चारों ओर से तुझे दबाएंगे।",
			"44": "<sup>44</sup> और तुझे और तेरे बालकों को जो तुझ में हैं, मिट्टी में मिलाएंगे, और तुझ में पत्थर पर पत्थर भी न छोड़ेंगे; क्योंकि तू ने वह अवसर जब तुझ पर कृपा दृष्टि की गई न पहिचाना॥",
			"45": "<sup>45</sup> तब वह मन्दिर में जाकर बेचने वालों को बाहर निकालने लगा।",
			"46": "<sup>46</sup> और उन से कहा, लिखा है; कि मेरा घर प्रार्थना का घर होगा: परन्तु तुम ने उसे डाकुओं की खोह बना दिया है॥",
			"47": "<sup>47</sup> और वह प्रति दिन मन्दिर में उपदेश करता था: और महायाजक और शास्त्री और लोगों के रईस उसे नाश करने का अवसर ढूंढ़ते थे।",
			"48": "<sup>48</sup> परन्तु कोई उपाय न निकाल सके; कि यह किस प्रकार करें क्योंकि सब लोग बड़ी चाह से उस की सुनते थे।"
		},
		"20": {
			"1": "<sup>1</sup> एक दिन ऐसा हुआ कि जब वह मन्दिर में लोगों को उपदेश देता और सुसमाचार सुना रहा था, तो महायाजक और शास्त्री, पुरनियों के साथ पास आकर खड़े हुए।",
			"2": "<sup>2</sup> और कहने लगे, कि हमें बता, तू इन कामों को किस अधिकार से करता है, और वह कौन है, जिस ने तुझे यह अधिकार दिया है?",
			"3": "<sup>3</sup> उस ने उन को उत्तर दिया, कि मैं भी तुम में से एक बात पूछता हूं; मुझे बताओ।",
			"4": "<sup>4</sup> यूहन्ना का बपतिस्मा स्वर्ग की ओर से था या मनुष्यों की ओर से था?",
			"5": "<sup>5</sup> तब वे आपस में कहने लगे, कि यदि हम कहें स्वर्ग की ओर से, तो वह कहेगा; फिर तुम ने उस की प्रतीति क्यों न की?",
			"6": "<sup>6</sup> और यदि हम कहें, मनुष्यों की ओर से, तो सब लोग हमें पत्थरवाह करेंगे, क्योंकि वे सचमुच जानते हैं, कि यूहन्ना भविष्यद्वक्ता था।",
			"7": "<sup>7</sup> सो उन्होंने उत्तर दिया, हम नहीं जानते, कि वह किस की ओर से था।",
			"8": "<sup>8</sup> यीशु ने उन से कहा, तो मैं भी तुम को नहीं बताता, कि मैं ये काम किस अधिकार से करता हूं।",
			"9": "<sup>9</sup> तब वह लोगों से यह दृष्टान्त कहने लगा, कि किसी मनुष्य ने दाख की बारी लगाई, और किसानों को उसका ठेका दे दिया और बहुत दिनों के लिये परदेश चला गया।",
			"10": "<sup>10</sup> समय पर उस ने किसानों के पास एक दास को भेजा, कि वे दाख की बारी के कुछ फलों का भाग उसे दें, पर किसानों ने उसे पीटकर छूछे हाथ लौटा दिया।",
			"11": "<sup>11</sup> फिर उस ने एक और दास को भेजा, ओर उन्होंने उसे भी पीटकर और उसका अपमान करके छूछे हाथ लौटा दिया।",
			"12": "<sup>12</sup> फिर उस ने तीसरा भेजा, और उन्होंने उसे भी घायल करके निकाल दिया।",
			"13": "<sup>13</sup> तब दाख की बारी के स्वामी ने कहा, मैं क्या करूं? मैं अपने प्रिय पुत्र को भेजूंगा क्या जाने वे उसका आदर करें।",
			"14": "<sup>14</sup> जब किसानों ने उसे देखा तो आपस में विचार करने लगे, कि यह तो वारिस है; आओ, हम उसे मार डालें, कि मिरास हमारी हो जाए।",
			"15": "<sup>15</sup> और उन्होंने उसे दाख की बारी से बाहर निकालकर मार डाला: इसलिये दाख की बारी का स्वामी उन के साथ क्या करेगा?",
			"16": "<sup>16</sup> वह आकर उन किसानों को नाश करेगा, और दाख की बारी औरों को सौंपेगा: यह सुनकर उन्होंने कहा, परमेश्वर ऐसा न करे।",
			"17": "<sup>17</sup> उस ने उन की ओर देखकर कहा; फिर यह क्या, लिखा है, कि जिस पत्थर को राजमिस्त्रियों ने निकम्मा ठहराया था, वही कोने का सिरा हो गया।",
			"18": "<sup>18</sup> जो कोई उस पत्थर पर गिरेगा वह चकनाचूर हो जाएगा, और जिस पर वह गिरेगा, उसे वह पीस डालेगा॥",
			"19": "<sup>19</sup> उसी घड़ी शास्त्रियों और महायाजकों ने उसे पकड़ना चाहा, क्योंकि समझ गए, कि उस ने हम पर यह दृष्टान्त कहा, परन्तु वे लोगों से डरे।",
			"20": "<sup>20</sup> और वे उस की ताक में लगे और भेदिये भेजे, कि धर्म का भेष धरकर उस की कोई न कोई बात पकड़ें, कि उसे हाकिम के हाथ और अधिकार में सौंप दें।",
			"21": "<sup>21</sup> उन्होंने उस से यह पूछा, कि हे गुरू, हम जानते हैं कि तू ठीक कहता, और सिखाता भी है, और किसी का पक्षपात नहीं करता; वरन परमेश्वर का मार्ग सच्चाई से बताता है।",
			"22": "<sup>22</sup> क्या हमें कैसर को कर देना उचित है, कि नहीं।",
			"23": "<sup>23</sup> उस ने उन की चतुराई को ताड़कर उन से कहा; एक दीनार मुझे दिखाओ।",
			"24": "<sup>24</sup> इस पर किस की मूर्ति और नाम है उन्होंने कहा, कैसर का।",
			"25": "<sup>25</sup> उस ने उन से कहा; तो जो कैसर का है, वह कैसर को दो और जो परमेश्वर का है, वह परमेश्वर को दो।",
			"26": "<sup>26</sup> वे लोगों के साम्हने उस बात को पकड़ न सके, वरन उसके उत्तर से अचम्भित होकर चुप रह गए।",
			"27": "<sup>27</sup> फिर सदूकी जो कहते हैं, कि मरे हुओं का जी उठना है ही नहीं, उन में से कितनों ने उसके पास आकर पूछा।",
			"28": "<sup>28</sup> कि हे गुरू, मूसा ने हमारे लिये यह लिखा है, कि यदि किसी का भाई अपनी पत्नी के रहते हुए बिना सन्तान मर जाए, तो उसका भाई उस की पत्नी को ब्याह ले, और अपने भाई के लिये वंश उत्पन्न करे।",
			"29": "<sup>29</sup> सो सात भाई थे, पहिला भाई ब्याह करके बिना सन्तान मर गया।",
			"30": "<sup>30</sup> फिर दूसरे और तीसरे ने भी उस स्त्री को ब्याह लिया।",
			"31": "<sup>31</sup> इसी रीति से सातों बिना सन्तान मर गए।",
			"32": "<sup>32</sup> सब के पीछे वह स्त्री भी मर गई।",
			"33": "<sup>33</sup> सो जी उठने पर वह उन में से किस की पत्नी होगी, क्योंकि वह सातों की पत्नी हो चुकी थी।",
			"34": "<sup>34</sup> यीशु ने उन से कहा; कि इस युग के सन्तानों में तो ब्याह शादी होती है।",
			"35": "<sup>35</sup> पर जो लोग इस योग्य ठहरेंगे, कि उस युग को और मरे हुओं में से जी उठना प्राप्त करें, उन में ब्याह शादी न होगी।",
			"36": "<sup>36</sup> वे फिर मरने के भी नहीं; क्योंकि वे स्वर्गदूतों के समान होंगे, और जी उठने के सन्तान होने से परमेश्वर के भी सन्तान होंगे।",
			"37": "<sup>37</sup> परन्तु इस बात को कि मरे हुए जी उठते हैं, मूसा न भी झाड़ी की कथा में प्रगट की है, कि वह प्रभु को इब्राहीम का परमेश्वर, और इसहाक का परमेश्वर, और याकूब का परमेश्वर कहता है।",
			"38": "<sup>38</sup> परमेश्वर तो मुरदों का नहीं परन्तु जीवतों का परमेश्वर है: क्योंकि उसके निकट सब जीवित हैं।",
			"39": "<sup>39</sup> तब यह सुनकर शास्त्रियों में से कितनों ने कहा, कि हे गुरू, तू ने अच्छा कहा।",
			"40": "<sup>40</sup> और उन्हें फिर उस से कुछ और पूछने का हियाव न हुआ॥",
			"41": "<sup>41</sup> फिर उस ने उन से पूछा, मसीह को दाऊद का सन्तान क्योंकर कहते हैं।",
			"42": "<sup>42</sup> दाऊद आप भजनसंहिता की पुस्तक में कहता है, कि प्रभु ने मेरे प्रभु से कहा।",
			"43": "<sup>43</sup> मेरे दाहिने बैठ, जब तक कि मैं तेरे बैरियों को तेरे पांवों के तले न कर दूं।",
			"44": "<sup>44</sup> दाऊद तो उसे प्रभु कहता है; तो फिर वह उस की सन्तान क्योंकर ठहरा?",
			"45": "<sup>45</sup> जब सब लोग सुन रहे थे, तो उस ने अपने चेलों से कहा।",
			"46": "<sup>46</sup> शास्त्रियों से चौकस रहो, जिन को लम्बे लम्बे वस्त्र पहिने हुए फिरना भला है, और जिन्हें बाजारों में नमस्कार, और सभाओं में मुख्य आसन और जेवनारों में मुख्य स्थान प्रिय लगते हैं।",
			"47": "<sup>47</sup> वे विधवाओं के घर खा जाते हैं, और दिखाने के लिये बड़ी देर तक प्रार्थना करते रहते हैं: ये बहुत ही दण्ड पाएंगे॥"
		},
		"21": {
			"1": "<sup>1</sup> फिर उस ने आंख उठाकर धनवानों को अपना अपना दान भण्डार में डालते देखा।",
			"2": "<sup>2</sup> और उस ने एक कंगाल विधवा को भी उस में दो दमडिय़ां डालते देखा।",
			"3": "<sup>3</sup> तब उस ने कहा; मैं तुम से सच कहता हूं कि इस कंगाल विधवा ने सब से बढ़कर डाला है।",
			"4": "<sup>4</sup> क्योंकि उन सब ने अपनी अपनी बढ़ती में से दान में कुछ डाला है, परन्तु इस ने अपनी घटी में से अपनी सारी जीविका डाल दी है॥",
			"5": "<sup>5</sup> जब कितने लोग मन्दिर के विषय में कह रहे थे, कि वह कैसे सुन्दर पत्थरों और भेंट की वस्तुओं से संवारा गया है तो उस ने कहा।",
			"6": "<sup>6</sup> वे दिन आएंगे, जिन में यह सब जो तुम देखते हो, उन में से यहां किसी पत्थर पर पत्थर भी न छूटेगा, जो ढाया न जाएगा।",
			"7": "<sup>7</sup> उन्होंने उस से पूछा, हे गुरू, यह सब कब होगा और ये बातें जब पूरी होने पर होंगी, तो उस समय का क्या चिन्ह होगा?",
			"8": "<sup>8</sup> उस ने कहा; चौकस रहो, कि भरमाए न जाओ, क्योंकि बहुतेरे मेरे नाम से आकर कहेंगे, कि मैं वही हूं; और यह भी कि समय निकट आ पहुंचा है: तुम उन के पीछे न चले जाना।",
			"9": "<sup>9</sup> और जब तुम लड़ाइयों और बलवों की चर्चा सुनो, तो घबरा न जाना; क्योंकि इन का पहिले होना अवश्य है; परन्तु उस समय तुरन्त अन्त न होगा।",
			"10": "<sup>10</sup> तब उस ने उन से कहा, कि जाति पर जाति और राज्य पर राज्य चढ़ाई करेगा।",
			"11": "<sup>11</sup> और बड़ें बड़ें भूईडोल होंगे, और जगह जगह अकाल और मरियां पड़ेंगी, और आकाश में भयंकर बातें और बड़े बड़े चिन्ह प्रगट होंगे।",
			"12": "<sup>12</sup> परन्तु इन सब बातों से पहिले वे मेरे नाम के कारण तुम्हें पकड़ेंगे, और सताएंगे, और पंचायतों में सौपेंगे, और बन्दीगृह मे डलवाएंगे, और राजाओं और हाकिमों के साम्हने ले जाएंगे।",
			"13": "<sup>13</sup> पर यह तुम्हारे लिये गवाही देने का अवसर हो जाएगा।",
			"14": "<sup>14</sup> इसलिये अपने अपने मन में ठान रखो कि हम पहिले से उत्तर देने की चिन्ता न करेंगे।",
			"15": "<sup>15</sup> क्योंकि मैं तुम्हें ऐसा बोल और बुद्धि दूंगा, कि तुम्हारे सब विरोधी साम्हना या खण्डन न कर सकेंगे।",
			"16": "<sup>16</sup> और तुम्हारे माता पिता और भाई और कुटुम्ब, और मित्र भी तुम्हें पकड़वाएंगे; यहां तक कि तुम में से कितनों को मरवा डालेंगे।",
			"17": "<sup>17</sup> और मेरे नाम के कारण सब लोग तुम से बैर करेंगे।",
			"18": "<sup>18</sup> परन्तु तुम्हारे सिर का एक बाल भी बांका न होगा।",
			"19": "<sup>19</sup> अपने धीरज से तुम अपने प्राणों को बचाए रखोगे॥",
			"20": "<sup>20</sup> जब तुम यरूशलेम को सेनाओं से घिरा हुआ देखो, तो जान लेना कि उसका उजड़ जाना निकट है।",
			"21": "<sup>21</sup> तब जो यहूदिया में हों वह पहाड़ों पर भाग जाएं, और जो यरूशलेम के भीतर हों वे बाहर निकल जाएं; और जो गावों में हो वे उस में न जांए।",
			"22": "<sup>22</sup> क्योंकि यह पलटा लेने के ऐसे दिन होंगे, जिन में लिखी हुई सब बातें पूरी हो जाएंगी।",
			"23": "<sup>23</sup> उन दिनों में जो गर्भवती और दूध पिलाती होंगी, उन के लिये हाय, हाय, क्योंकि देश में बड़ा क्लेश और इन लोगों पर बड़ी आपत्ति होगी।",
			"24": "<sup>24</sup> वे तलवार के कौर हो जाएंगे, और सब देशों के लोगों में बन्धुए होकर पहुंचाए जाएंगे, और जब तक अन्य जातियों का समय पूरा न हो, तब तक यरूशलेम अन्य जातियों से रौंदा जाएगा।",
			"25": "<sup>25</sup> और सूरज और चान्द और तारों में चिन्ह दिखाई देंगें, और पृथ्वी पर, देश देश के लोगों को संकट होगा; क्योंकि वे समुद्र के गरजने और लहरों के कोलाहल से घबरा जाएंगे।",
			"26": "<sup>26</sup> और भय के कारण और संसार पर आनेवाली घटनाओं की बाट देखते देखते लोगों के जी में जी न रहेगा क्योंकि आकाश की शक्तियां हिलाई जाएंगी।",
			"27": "<sup>27</sup> तब वे मनुष्य के पुत्र को सामर्थ और बड़ी महिमा के साथ बादल पर आते देखेंगे।",
			"28": "<sup>28</sup> जब ये बातें होने लगें, तो सीधे होकर अपने सिर ऊपर उठाना; क्योंकि तुम्हारा छुटकारा निकट होगा॥",
			"29": "<sup>29</sup> उस ने उन से एक दृष्टान्त भी कहा कि अंजीर के पेड़ और सब पेड़ों को देखो।",
			"30": "<sup>30</sup> ज्योंहि उन की कोंपलें निकलती हैं, तो तुम देखकर आप ही जान लेते हो, कि ग्रीष्म काल निकट है।",
			"31": "<sup>31</sup> इसी रीति से जब तुम ये बातें होते देखो, तब जान लो कि परमेश्वर का राज्य निकट है।",
			"32": "<sup>32</sup> मैं तुम से सच कहता हूं, कि जब तक ये सब बातें न हो लें, तब तक इस पीढ़ी का कदापि अन्त न होगा।",
			"33": "<sup>33</sup> आकाश और पृथ्वी टल जाएंगे, परन्तु मेरी बातें कभी न टलेंगी॥",
			"34": "<sup>34</sup> इसलिये सावधान रहो, ऐसा न हो कि तुम्हारे मन खुमार और मतवालेपन, और इस जीवन की चिन्ताओं से सुस्त हो जाएं, और वह दिन तुम पर फन्दे की नाईं अचानक आ पड़े।",
			"35": "<sup>35</sup> क्योंकि वह सारी पृथ्वी के सब रहने वालों पर इसी प्रकार आ पड़ेगा।",
			"36": "<sup>36</sup> इसलिये जागते रहो और हर समय प्रार्थना करते रहो कि तुम इन सब आने वाली घटनाओं से बचने, और मनुष्य के पुत्र के साम्हने खड़े होने के योग्य बनो॥",
			"37": "<sup>37</sup> और वह दिन को मन्दिर में उपदेश करता था; और रात को बाहर जाकर जैतून नाम पहाड़ पर रहा करता था।",
			"38": "<sup>38</sup> और भोर को तड़के सब लोग उस की सुनने के लिये मन्दिर में उसके पास आया करते थे।"
		},
		"22": {
			"1": "<sup>1</sup> अखमीरी रोटी का पर्व्व जो फसह कहलाता है, निकट था।",
			"2": "<sup>2</sup> और महायाजक और शास्त्री इस बात की खोज में थे कि उस को क्योंकर मार डालें, पर वे लोगों से डरते थे॥",
			"3": "<sup>3</sup> और शैतान यहूदा में समाया, जो इस्करियोती कहलाता और बारह चेलों में गिना जाता था।",
			"4": "<sup>4</sup> उस ने जाकर महायाजकों और पहरूओं के सरदारों के साथ बातचीत की, कि उस को किस प्रकार उन के हाथ पकड़वाए।",
			"5": "<sup>5</sup> वे आनन्दित हुए, और उसे रूपये देने का वचन दिया।",
			"6": "<sup>6</sup> उस ने मान लिया, और अवसर ढूंढ़ने लगा, कि बिना उपद्रव के उसे उन के हाथ पकड़वा दे॥",
			"7": "<sup>7</sup> तब अखमीरी रोटी के पर्व्व का दिन आया, जिस में फसह का मेम्ना बली करना अवश्य था।",
			"8": "<sup>8</sup> और यीशु ने पतरस और यूहन्ना को यह कहकर भेजा, कि जाकर हमारे खाने के लिये फसह तैयार करो।",
			"9": "<sup>9</sup> उन्होंने उस से पूछा, तू कहां चाहता है, कि हम तैयार करें?",
			"10": "<sup>10</sup> उस ने उन से कहा; देखो, नगर में प्रवेश करते ही एक मनुष्य जल का घड़ा उठाए हुए तुम्हें मिलेगा, जिस घर में वह जाए; तुम उसके पीछे चले जाना।",
			"11": "<sup>11</sup> और उस घर के स्वामी से कहो, कि गुरू तुझ से कहता है; कि वह पाहुनशाला कहां है जिस में मैं अपने चेलों के साथ फसह खाऊं?",
			"12": "<sup>12</sup> वह तुम्हें एक सजी सजाई बड़ी अटारी दिखा देगा; वहां तैयारी करना।",
			"13": "<sup>13</sup> उन्होंने जाकर, जैसा उस ने उन से कहा था, वैसा ही पाया, और फसह तैयार किया॥",
			"14": "<sup>14</sup> जब घड़ी पहुंची, तो वह प्रेरितों के साथ भोजन करने बैठा।",
			"15": "<sup>15</sup> और उस ने उन से कहा; मुझे बड़ी लालसा थी, कि दुख-भोगने से पहिले यह फसह तुम्हारे साथ खाऊं।",
			"16": "<sup>16</sup> क्योंकि मैं तुम से कहता हूं, कि जब तक वह परमेश्वर के राज्य में पूरा न हो तब तक मैं उसे कभी न खाऊंगा।",
			"17": "<sup>17</sup> तब उस ने कटोरा लेकर धन्यवाद किया, और कहा, इस को लो और आपस में बांट लो।",
			"18": "<sup>18</sup> क्योंकि मैं तुम से कहता हूं, कि जब तक परमेश्वर का राज्य न आए तब तक मैं दाख रस अब से कभी न पीऊंगा।",
			"19": "<sup>19</sup> फिर उस ने रोटी ली, और धन्यवाद करके तोड़ी, और उन को यह कहते हुए दी, कि यह मेरी देह है, जो तुम्हारे लिये दी जाती है: मेरे स्मरण के लिये यही किया करो।",
			"20": "<sup>20</sup> इसी रीति से उस ने बियारी के बाद कटोरा भी यह कहते हुए दिया कि यह कटोरा मेरे उस लोहू में जो तुम्हारे लिये बहाया जाता है नई वाचा है।",
			"21": "<sup>21</sup> पर देखो, मेरे पकड़वाने वाले का हाथ मेरे साथ मेज पर है।",
			"22": "<sup>22</sup> क्योंकि मनुष्य का पुत्र तो जैसा उसके लिये ठहराया गया जाता ही है, पर हाय उस मनुष्य पर, जिस के द्वारा वह पकड़वाया जाता है!",
			"23": "<sup>23</sup> तब वे आपस में पूछ पाछ करने लगे, कि हम में से कौन है, जो यह काम करेगा?",
			"24": "<sup>24</sup> उन में यह वाद-विवाद भी हुआ; कि हम में से कौन बड़ा समझा जाता है",
			"25": "<sup>25</sup> उस ने उन से कहा, अन्यजातियों के राजा उन पर प्रभुता करते हैं; और जो उन पर अधिकार रखते हैं, वे उपकारक कहलाते हैं।",
			"26": "<sup>26</sup> परन्तु तुम ऐसे न होना; वरन जो तुम में बड़ा है, वह छोटे की नाईं और जो प्रधान है, वह सेवक की नाईं बने।",
			"27": "<sup>27</sup> क्योंकि बड़ा कौन है; वह जो भोजन पर बैठा या वह जो सेवा करता है? क्या वह नहीं जो भोजन पर बैठा है? पर मैं तुम्हारे बीच में सेवक की नाईं हूं।",
			"28": "<sup>28</sup> परन्तु तुम वह हो, जो मेरी परीक्षाओं में लगातार मेरे साथ रहे।",
			"29": "<sup>29</sup> और जैसे मेरे पिता ने मेरे लिये एक राज्य ठहराया है,",
			"30": "<sup>30</sup> वैसे ही मैं भी तुम्हारे लिये ठहराता हूं, ताकि तुम मेरे राज्य में मेरी मेज पर खाओ-पिओ; वरन सिंहासनों पर बैठकर इस्त्राएल के बारह गोत्रों का न्याय करो।",
			"31": "<sup>31</sup> शमौन, हे शमौन, देख, शैतान ने तुम लोगों को मांग लिया है कि गेंहूं की नाईं फटके।",
			"32": "<sup>32</sup> परन्तु मैं ने तेरे लिये बिनती की, कि तेरा विश्वास जाता न रहे: और जब तू फिरे, तो अपने भाइयों को स्थिर करना।",
			"33": "<sup>33</sup> उस ने उस से कहा; हे प्रभु, मैं तेरे साथ बन्दीगृह जाने, वरन मरने को भी तैयार हूं।",
			"34": "<sup>34</sup> उस ने कहा; हे पतरस मैं तुझ से कहता हूं, कि आज मुर्ग बांग न देगा जब तक तू तीन बार मेरा इन्कार न कर लेगा कि मैं उसे नहीं जानता॥",
			"35": "<sup>35</sup> और उस ने उन से कहा, कि जब मैं ने तुम्हें बटुए, और झोली, और जूते बिना भेजा था, तो क्या तुम को किसी वस्तु की घटी हुई थी? उन्होंने कहा; किसी वस्तु की नहीं।",
			"36": "<sup>36</sup> उस ने उन से कहा, परन्तु अब जिस के पास बटुआ हो वह उसे ले, और वैसे ही झोली भी, और जिस के पास तलवार न हो वह अपने कपड़े बेचकर एक मोल ले।",
			"37": "<sup>37</sup> क्योंकि मैं तुम से कहता हूं, कि यह जो लिखा है, कि वह अपराधियों के साथ गिना गया, उसका मुझ में पूरा होना अवश्य है; क्योंकि मेरे विषय की बातें पूरी होने पर हैं।",
			"38": "<sup>38</sup> उन्होंने कहा; हे प्रभु, देख, यहां दो तलवारें हैं: उस ने उन से कहा; बहुत हैं॥",
			"39": "<sup>39</sup> तब वह बाहर निकलकर अपनी रीति के अनुसार जैतून के पहाड़ पर गया, और चेले उसके पीछे हो लिए।",
			"40": "<sup>40</sup> उस जगह पहुंचकर उस ने उन से कहा; प्रार्थना करो, कि तुम परीक्षा में न पड़ो।",
			"41": "<sup>41</sup> और वह आप उन से अलग एक ढेला फेंकने के टप्पे भर गया, और घुटने टेक कर प्रार्थना करने लगा।",
			"42": "<sup>42</sup> कि हे पिता यदि तू चाहे तो इस कटोरे को मेरे पास से हटा ले, तौभी मेरी नहीं परन्तु तेरी ही इच्छा पूरी हो।",
			"43": "<sup>43</sup> तब स्वर्ग से एक दूत उस को दिखाई दिया जो उसे सामर्थ देता था।",
			"44": "<sup>44</sup> और वह अत्यन्त संकट में व्याकुल होकर और भी ह्रृदय वेदना से प्रार्थना करने लगा; और उसका पसीना मानो लोहू की बड़ी बड़ी बून्दों की नाईं भूमि पर गिर रहा था।",
			"45": "<sup>45</sup> तब वह प्रार्थना से उठा और अपने चेलों के पास आकर उन्हें उदासी के मारे सोता पाया; और उन से कहा, क्यों सोते हो?",
			"46": "<sup>46</sup> उठो, प्रार्थना करो, कि परीक्षा में न पड़ो॥",
			"47": "<sup>47</sup> वह यह कह ही रहा था, कि देखो एक भीड़ आई, और उन बारहों में से एक जिस का नाम यहूदा था उनके आगे आगे आ रहा था, वह यीशु के पास आया, कि उसका चूमा ले।",
			"48": "<sup>48</sup> यीशु ने उस से कहा, हे यहूदा, क्या तू चूमा लेकर मनुष्य के पुत्र को पकड़वाता है?",
			"49": "<sup>49</sup> उसके साथियों ने जब देखा कि क्या होनेवाला है, कहा; हे प्रभु, क्या हम तलवार चलाएं?",
			"50": "<sup>50</sup> और उन में से एक ने महायाजक के दास पर चलाकर उसका दाहिना कान उड़ा दिया।",
			"51": "<sup>51</sup> इस पर यीशु ने कहा; अब बस करो: और उसका कान छूकर उसे अच्छा किया।",
			"52": "<sup>52</sup> तब यीशु ने महायाजकों; और मन्दिर के पहरूओं के सरदारों और पुरनियों से, जो उस पर चढ़ आए थे, कहा; क्या तुम मुझे डाकू जानकर तलवारें और लाठियां लिए हुए निकले हो?",
			"53": "<sup>53</sup> जब मैं मन्दिर में हर दिन तुम्हारे साथ था, तो तुम ने मुझ पर हाथ न डाला; पर यह तुम्हारी घड़ी है, और अन्धकार का अधिकार है॥",
			"54": "<sup>54</sup> फिर वे उसे पकड़कर ले चले, और महायाजक के घर में लाए और पतरस दूर ही दूर उसके पीछे पीछे चलता था।",
			"55": "<sup>55</sup> और जब वे आंगन में आग सुलगाकर इकट्ठे बैठे, तो पतरस भी उन के बीच में बैठ गया।",
			"56": "<sup>56</sup> और एक लौंडी उसे आग के उजियाले में बैठे देखकर और उस की ओर ताककर कहने लगी, यह भी तो उसके साथ था।",
			"57": "<sup>57</sup> परन्तु उस ने यह कहकर इन्कार किया, कि हे नारी, मैं उसे नहीं जानता।",
			"58": "<sup>58</sup> थोड़ी देर बाद किसी और ने उसे देखकर कहा, तू भी तो उन्हीं में से है: पतरस ने कहा; हे मनुष्य मैं नहीं हूं।",
			"59": "<sup>59</sup> कोई घंटे भर के बाद एक और मनुष्य दृढ़ता से कहने लगा, निश्चय यह भी तो उसके साथ था; क्योंकि यह गलीली है।",
			"60": "<sup>60</sup> पतरस ने कहा, हे मनुष्य, मैं नहीं जानता कि तू क्या कहता है! वह कह ही रहा था कि तुरन्त मुर्ग ने बांग दी।",
			"61": "<sup>61</sup> तब प्रभु ने घूमकर पतरस की ओर देखा, और पतरस को प्रभु की वह बात याद आई जो उस ने कही थी, कि आज मुर्ग के बांग देने से पहिले, तू तीन बार मेरा इन्कार करेगा।",
			"62": "<sup>62</sup> और वह बाहर निकलकर फूट फूट कर रोने लगा॥",
			"63": "<sup>63</sup> जो मनुष्य यीशु को पकड़े हुए थे, वे उसे ठट्ठों में उड़ाकर पीटने लगे।",
			"64": "<sup>64</sup> और उस की आंखे ढांपकर उस से पूछा, कि भविष्यद्वाणी करके बता कि तुझे किसने मारा।",
			"65": "<sup>65</sup> और उन्होंने बहुत सी और भी निन्दा की बातें उसके विरोध में कहीं॥",
			"66": "<sup>66</sup> जब दिन हुआ तो लोगों के पुरिनए और महायाजक और शास्त्री इकट्ठे हुए, और उसे अपनी महासभा में लाकर पूछा,",
			"67": "<sup>67</sup> यदि तू मसीह है, तो हम से कह दे! उस ने उन से कहा, यदि मैं तुम से कहूं तो प्रतीति न करोगे।",
			"68": "<sup>68</sup> और यदि पूछूं, तो उत्तर न दोगे।",
			"69": "<sup>69</sup> परन्तु अब से मनुष्य का पुत्र सर्वशक्तिमान परमेश्वर की दाहिनी और बैठा रहेगा।",
			"70": "<sup>70</sup> इस पर सब ने कहा, तो क्या तू परमेश्वर का पुत्र है? उस ने उन से कहा; तुम आप ही कहते हो, क्योंकि मैं हूं।",
			"71": "<sup>71</sup> तब उन्होंने कहा; अब हमें गवाही का क्या प्रयोजन है; क्योंकि हम ने आप ही उसके मुंह से सुन लिया है॥"
		},
		"23": {
			"1": "<sup>1</sup> तब सारी सभा उठकर उसे पीलातुस के पास ले गई।",
			"2": "<sup>2</sup> और वे यह कहकर उस पर दोष लगाने लगे, कि हम ने इसे लोगों को बहकाते और कैसर को कर देने से मना करते, और अपने आप को मसीह राजा कहते हुए सुना है।",
			"3": "<sup>3</sup> पीलातुस ने उस से पूछा, क्या तू यहूदियों का राजा है? उस ने उसे उत्तर दिया, कि तू आप ही कह रहा है।",
			"4": "<sup>4</sup> तब पीलातुस ने महायाजकों और लोगों से कहा, मैं इस मनुष्य में कुछ दोष नहीं पाता।",
			"5": "<sup>5</sup> पर वे और भी दृढ़ता से कहने लगे, यह गलील से लेकर यहां तक सारे यहूदिया में उपदेश दे दे कर लोगों को उकसाता है।",
			"6": "<sup>6</sup> यह सुनकर पीलातुस ने पूछा, क्या यह मनुष्य गलीली है?",
			"7": "<sup>7</sup> और यह जानकर कि वह हेरोदेस की रियासत का है, उसे हेरोदेस के पास भेज दिया, क्योंकि उन दिनों में वह भी यरूशलेम में था॥",
			"8": "<sup>8</sup> हेरोदेस यीशु को देखकर बहुत ही प्रसन्न हुआ, क्योंकि वह बहुत दिनों से उस को देखना चाहता था: इसलिये कि उसके विषय में सुना था, और उसका कुछ चिन्ह देखने की आशा रखता था।",
			"9": "<sup>9</sup> वह उस से बहुतेरी बातें पूछता रहा, पर उस ने उस को कुछ भी उत्तर न दिया।",
			"10": "<sup>10</sup> और महायाजक और शास्त्री खड़े हुए तन मन से उस पर दोष लगाते रहे।",
			"11": "<sup>11</sup> तब हेरोदेस ने अपने सिपाहियों के साथ उसका अपमान करके ठट्ठों में उड़ाया, और भड़कीला वस्त्र पहिनाकर उसे पीलातुस के पास लौटा दिया।",
			"12": "<sup>12</sup> उसी दिन पीलातुस और हेरोदेस मित्र हो गए। इसके पहिले वे एक दूसरे के बैरी थे॥",
			"13": "<sup>13</sup> पीलातुस ने महायाजकों और सरदारों और लोगों को बुलाकर उन से कहा।",
			"14": "<sup>14</sup> तुम इस मनुष्य को लोगों का बहकाने वाला ठहराकर मेरे पास लाए हो, और देखो, मैं ने तुम्हारे साम्हने उस की जांच की, पर जिन बातों का तुम उस पर दोष लगाते हो, उन बातों के विषय में मैं ने उस में कुछ भी दोष नहीं पाया है।",
			"15": "<sup>15</sup> न हेरोदेस ने, क्योंकि उस ने उसे हमारे पास लौटा दिया है: और देखो, उस से ऐसा कुछ नहीं हुआ कि वह मृत्यु के दण्ड के योग्य ठहराया जाए।",
			"16": "<sup>16</sup> इसलिये मैं उसे पिटवाकर छोड़ देता हूं।",
			"17": "<sup>17</sup> तब सब मिलकर चिल्ला उठे,",
			"18": "<sup>18</sup> इस का काम तमाम कर, और हमारे लिये बरअब्बा को छोड़ दे।",
			"19": "<sup>19</sup> यही किसी बलवे के कारण जो नगर में हुआ था, और हत्या के कारण बन्दीगृह में डाला गया था।",
			"20": "<sup>20</sup> पर पीलातुस ने यीशु को छोड़ने की इच्छा से लोगों को फिर समझाया।",
			"21": "<sup>21</sup> परन्तु उन्होंने चिल्लाकर कहा: कि उसे क्रूस पर चढ़ा, क्रूस पर।",
			"22": "<sup>22</sup> उस ने तीसरी बार उन से कहा; क्यों? उस ने कौन सी बुराई की है? मैं ने उस में मृत्यु दण्ड के योग्य कोई बात नहीं पाई! इसलिये मैं उसे पिटवाकर छोड़ देता हूं।",
			"23": "<sup>23</sup> परन्तु वे चिल्ला-चिल्ला कर पीछे पड़ गए, कि वह क्रूस पर चढ़ाया जाए, और उन का चिल्लाना प्रबल हुआ।",
			"24": "<sup>24</sup> सो पीलातुस ने आज्ञा दी, कि उन की बिनती के अनुसार किया जाए।",
			"25": "<sup>25</sup> और उस ने उस मनुष्य को जो बलवे और हत्या के कारण बन्दीगृह में डाला गया था, और जिसे वे मांगते थे, छोड़ दिया; और यीशु को उन की इच्छा के अनुसार सौंप दिया॥",
			"26": "<sup>26</sup> जब वे उसे लिए जाते थे, तो उन्होंने शमौन नाम एक कुरेनी को जो गांव से आ रहा था, पकड़कर उस पर क्रूस को लाद दिया कि उसे यीशु के पीछे पीछे ले चले॥",
			"27": "<sup>27</sup> और लोगों की बड़ी भीड़ उसके पीछे हो ली: और बहुत सी स्त्रियां भी, जो उसके लिये छाती-पीटती और विलाप करती थीं।",
			"28": "<sup>28</sup> यीशु ने उन की ओर फिरकर कहा; हे यरूशलेम की पुत्रियों, मेरे लिये मत रोओ; परन्तु अपने और अपने बालकों के लिये रोओ।",
			"29": "<sup>29</sup> क्योंकि देखो, वे दिन आते हैं, जिन में कहेंगे, धन्य हैं वे जो बांझ हैं, और वे गर्भ जो न जने और वे स्तन जिन्हों ने दूध न पिलाया।",
			"30": "<sup>30</sup> उस समय वे पहाड़ों से कहने लगेंगे, कि हम पर गिरो, और टीलों से कि हमें ढाँप लो।",
			"31": "<sup>31</sup> क्योंकि जब वे हरे पेड़ के साथ ऐसा करते हैं, तो सूखे के साथ क्या कुछ न किया जाएगा?",
			"32": "<sup>32</sup> वे और दो मनुष्यों को भी जो कुकर्मी थे उसके साथ घात करने को ले चले॥",
			"33": "<sup>33</sup> जब वे उस जगह जिसे खोपड़ी कहते हैं पहुंचे, तो उन्होंने वहां उसे और उन कुकिर्मयों को भी एक को दाहिनी और और दूसरे को बाईं और क्रूसों पर चढ़ाया।",
			"34": "<sup>34</sup> तब यीशु ने कहा; हे पिता, इन्हें क्षमा कर, क्योंकि ये नहीं जानते कि क्या कर रहें हैं और उन्होंने चिट्ठियां डालकर उसके कपड़े बांट लिए।",
			"35": "<sup>35</sup> लोग खड़े खड़े देख रहे थे, और सरदार भी ठट्ठा कर करके कहते थे, कि इस ने औरों को बचाया, यदि यह परमेश्वर का मसीह है, और उसका चुना हुआ है, तो अपने आप को बचा ले।",
			"36": "<sup>36</sup> सिपाही भी पास आकर और सिरका देकर उसका ठट्ठा करके कहते थे।",
			"37": "<sup>37</sup> यदि तू यहूदियों का राजा है, तो अपने आप को बचा।",
			"38": "<sup>38</sup> और उसके ऊपर एक पत्र भी लगा था, कि यह यहूदियों का राजा है।",
			"39": "<sup>39</sup> जो कुकर्मी लटकाए गए थे, उन में से एक ने उस की निन्दा करके कहा; क्या तू मसीह नहीं तो फिर अपने आप को और हमें बचा।",
			"40": "<sup>40</sup> इस पर दूसरे ने उसे डांटकर कहा, क्या तू परमेश्वर से भी नहीं डरता? तू भी तो वही दण्ड पा रहा है।",
			"41": "<sup>41</sup> और हम तो न्यायानुसार दण्ड पा रहे हैं, क्योंकि हम अपने कामों का ठीक फल पा रहे हैं; पर इस ने कोई अनुचित काम नहीं किया।",
			"42": "<sup>42</sup> तब उस ने कहा; हे यीशु, जब तू अपने राज्य में आए, तो मेरी सुधि लेना।",
			"43": "<sup>43</sup> उस ने उस से कहा, मैं तुझ से सच कहता हूं; कि आज ही तू मेरे साथ स्वर्गलोक में होगा॥",
			"44": "<sup>44</sup> और लगभग दो पहर से तीसरे पहर तक सारे देश में अन्धियारा छाया रहा।",
			"45": "<sup>45</sup> और सूर्य का उजियाला जाता रहा, और मन्दिर का परदा बीच में फट गया।",
			"46": "<sup>46</sup> और यीशु ने बड़े शब्द से पुकार कर कहा; हे पिता, मैं अपनी आत्मा तेरे हाथों में सौंपता हूं: और यह कहकर प्राण छोड़ दिए।",
			"47": "<sup>47</sup> सूबेदार ने, जो कुछ हुआ था देखकर, परमेश्वर की बड़ाई की, और कहा; निश्चय यह मनुष्य धर्मी था।",
			"48": "<sup>48</sup> और भीड़ जो यह देखने को इकट्ठी हुई भी, इस घटना को, देखकर छाती- पीटती हुई लौट गई।",
			"49": "<sup>49</sup> और उसके सब जान पहचान, और जो स्त्रियां गलील से उसके साथ आई थीं, दूर खड़ी हुई यह सब देख रही थीं॥",
			"50": "<sup>50</sup> और देखो यूसुफ नाम एक मन्त्री जो सज्जन और धर्मी पुरूष था।",
			"51": "<sup>51</sup> और उन के विचार और उन के इस काम से प्रसन्न न था; और वह यहूदियों के नगर अरिमतीया का रहनेवाला और परमेश्वर के राज्य की बाट जोहनेवाला था।",
			"52": "<sup>52</sup> उस ने पीलातुस के पास जाकर यीशु की लोथ मांग ली।",
			"53": "<sup>53</sup> और उसे उतारकर चादर में लपेटा, और एक कब्र में रखा, जो चट्टान में खोदी हुई थी; और उस में कोई कभी न रखा गया था।",
			"54": "<sup>54</sup> वह तैयारी का दिन था, और सब्त का दिन आरम्भ होने पर था।",
			"55": "<sup>55</sup> और उन स्त्रियों ने जो उसके साथ गलील से आईं थीं, पीछे पीछे जाकर उस कब्र को देखा, और यह भी कि उस की लोथ किस रीति से रखी गई है।",
			"56": "<sup>56</sup> और लौटकर सुगन्धित वस्तुएं और इत्र तैयार किया: और सब्त के दिन तो उन्होंने आज्ञा के अनुसार विश्राम किया॥"
		},
		"24": {
			"1": "<sup>1</sup> परन्तु सप्ताह के पहिले दिन बड़े भोर को वे उन सुगन्धित वस्तुओं को जो उन्होंने तैयार की थीं, ले कर कब्र पर आईं।",
			"2": "<sup>2</sup> और उन्होंने पत्थर को कब्र पर से लुढ़का हुआ पाया।",
			"3": "<sup>3</sup> और भीतर जाकर प्रभु यीशु की लोथ न पाई।",
			"4": "<sup>4</sup> जब वे इस बात से भौचक्की हो रही थीं तो देखो, दो पुरूष झलकते वस्त्र पहिने हुए उन के पास आ खड़े हुए।",
			"5": "<sup>5</sup> जब वे डर गईं, और धरती की ओर मुंह झुकाए रहीं; तो उन्होंने उन ने कहा; तुम जीवते को मरे हुओं में क्यों ढूंढ़ती हो?",
			"6": "<sup>6</sup> वह यहां नहीं, परन्तु जी उठा है; स्मरण करो; कि उस ने गलील में रहते हुए तुम से कहा था।",
			"7": "<sup>7</sup> कि अवश्य है, कि मनुष्य का पुत्र पापियों के हाथ में पकड़वाया जाए, और क्रूस पर चढ़ाया जाए; और तीसरे दिन जी उठे।",
			"8": "<sup>8</sup> तब उस की बातें उन को स्मरण आईं।",
			"9": "<sup>9</sup> और कब्र से लौटकर उन्होंने उन ग्यारहों को, और, और सब को, ये बातें कह सुनाईं।",
			"10": "<sup>10</sup> जिन्हों ने प्रेरितों से ये बातें कहीं, वे मरियम मगदलीनी और योअन्ना और याकूब की माता मरियम और उन के साथ की और स्त्रियां भी थीं।",
			"11": "<sup>11</sup> परन्तु उन की बातें उन्हें कहानी सी समझ पड़ीं, और उन्होंने उन की प्रतीति न की।",
			"12": "<sup>12</sup> तब पतरस उठकर कब्र पर दौड़ गया, और झुककर केवल कपड़े पड़े देखे, और जो हुआ था, उस से अचम्भा करता हुआ, अपने घर चला गया॥",
			"13": "<sup>13</sup> देखो, उसी दिन उन में से दो जन इम्माऊस नाम एक गांव को जा रहे थे, जो यरूशलेम से कोई सात मील की दूरी पर था।",
			"14": "<sup>14</sup> और वे इन सब बातों पर जो हुईं थीं, आपस में बातचीत करते जा रहे थे।",
			"15": "<sup>15</sup> और जब वे आपस में बातचीत और पूछताछ कर रहे थे, तो यीशु आप पास आकर उन के साथ हो लिया।",
			"16": "<sup>16</sup> परन्तु उन की आंखे ऐसी बन्द कर दी गईं थी, कि उसे पहिचान न सके।",
			"17": "<sup>17</sup> उस ने उन से पूछा; ये क्या बातें हैं, जो तुम चलते चलते आपस में करते हो? वे उदास से खड़े रह गए।",
			"18": "<sup>18</sup> यह सुनकर, उनमें से क्लियुपास नाम एक व्यक्ति ने कहा; क्या तू यरूशलेम में अकेला परदेशी है; जो नहीं जानता, कि इन दिनों में उस में क्या क्या हुआ है?",
			"19": "<sup>19</sup> उस ने उन से पूछा; कौन सी बातें? उन्होंने उस से कहा; यीशु नासरी के विषय में जो परमेश्वर और सब लोगों के निकट काम और वचन में सामर्थी भविष्यद्वक्ता था।",
			"20": "<sup>20</sup> और महायाजकों और हमारे सरदारों ने उसे पकड़वा दिया, कि उस पर मृत्यु की आज्ञा दी जाए; और उसे क्रूस पर चढ़वाया।",
			"21": "<sup>21</sup> परन्तु हमें आशा थी, कि यही इस्त्राएल को छुटकारा देगा, और इन सब बातों के सिवाय इस घटना को हुए तीसरा दिन है।",
			"22": "<sup>22</sup> और हम में से कई स्त्रियों ने भी हमें आश्चर्य में डाल दिया है, जो भोर को कब्र पर गई थीं।",
			"23": "<sup>23</sup> और जब उस की लोथ न पाई, तो यह कहती हुई आईं, कि हम ने स्वर्गदूतों का दर्शन पाया, जिन्हों ने कहा कि वह जीवित है।",
			"24": "<sup>24</sup> तब हमारे साथियों में से कई एक कब्र पर गए, और जैसा स्त्रियों ने कहा था, वैसा ही पाया; परन्तु उस को न देखा।",
			"25": "<sup>25</sup> तब उस ने उन से कहा; हे निर्बुद्धियों, और भविष्यद्वक्ताओं की सब बातों पर विश्वास करने में मन्दमतियों!",
			"26": "<sup>26</sup> क्या अवश्य न था, कि मसीह ये दुख उठाकर अपनी महिमा में प्रवेश करे?",
			"27": "<sup>27</sup> तब उस ने मूसा से और सब भविष्यद्वक्ताओं से आरम्भ करके सारे पवित्र शास्त्रों में से, अपने विषय में की बातों का अर्थ, उन्हें समझा दिया।",
			"28": "<sup>28</sup> इतने में वे उस गांव के पास पहुंचे, जहां वे जा रहे थे, और उसके ढंग से ऐसा जान पड़ा, कि वह आगे बढ़ना चाहता है।",
			"29": "<sup>29</sup> परन्तु उन्होंने यह कहकर उसे रोका, कि हमारे साथ रह; क्योंकि संध्या हो चली है और दिन अब बहुत ढल गया है। तब वह उन के साथ रहने के लिये भीतर गया।",
			"30": "<sup>30</sup> जब वह उन के साथ भोजन करने बैठा, तो उस ने रोटी लेकर धन्यवाद किया, और उसे तोड़कर उन को देने लगा।",
			"31": "<sup>31</sup> तब उन की आंखे खुल गईं; और उन्होंने उसे पहचान लिया, और वह उन की आंखों से छिप गया।",
			"32": "<sup>32</sup> उन्होंने आपस में कहा; जब वह मार्ग में हम से बातें करता था, और पवित्र शास्त्र का अर्थ हमें समझाता था, तो क्या हमारे मन में उत्तेजना न उत्पन्न हुई?",
			"33": "<sup>33</sup> वे उसी घड़ी उठकर यरूशलेम को लौट गए, और उन ग्यारहों और उन के साथियों को इकट्ठे पाया।",
			"34": "<sup>34</sup> वे कहते थे, प्रभु सचमुच जी उठा है, और शमौन को दिखाई दिया है।",
			"35": "<sup>35</sup> तब उन्होंने मार्ग की बातें उन्हें बता दीं और यह भी कि उन्होंने उसे रोटी तोड़ते समय क्योंकर पहचाना॥",
			"36": "<sup>36</sup> वे ये बातें कह ही रहे ये, कि वह आप ही उन के बीच में आ खड़ा हुआ; और उन से कहा, तुम्हें शान्ति मिले।",
			"37": "<sup>37</sup> परन्तु वे घबरा गए, और डर गए, और समझे, कि हम किसी भूत को देखते हैं।",
			"38": "<sup>38</sup> उस ने उन से कहा; क्यों घबराते हो और तुम्हारे मन में क्यों सन्देह उठते हैं?",
			"39": "<sup>39</sup> मेरे हाथ और मेरे पांव को देखो, कि मैं वहीं हूं; मुझे छूकर देखो; क्योंकि आत्मा के हड्डी मांस नहीं होता जैसा मुझ में देखते हो।",
			"40": "<sup>40</sup> यह कहकर उस ने उन्हें अपने हाथ पांव दिखाए।",
			"41": "<sup>41</sup> जब आनन्द के मारे उन को प्रतीति न हुई, और आश्चर्य करते थे, तो उस ने उन से पूछा; क्या यहां तुम्हारे पास कुछ भोजन है?",
			"42": "<sup>42</sup> उन्होंने उसे भूनी मछली का टुकड़ा दिया।",
			"43": "<sup>43</sup> उस ने लेकर उन के साम्हने खाया।",
			"44": "<sup>44</sup> फिर उस ने उन से कहा, ये मेरी वे बातें हैं, जो मैं ने तुम्हारे साथ रहते हुए, तुम से कही थीं, कि अवश्य है, कि जितनी बातें मूसा की व्यवस्था और भविष्यद्वक्ताओं और भजनों की पुस्तकों में, मेरे विषय में लिखी हैं, सब पूरी हों।",
			"45": "<sup>45</sup> तब उस ने पवित्र शास्त्र बूझने के लिये उन की समझ खोल दी।",
			"46": "<sup>46</sup> और उन से कहा, यों लिखा है; कि मसीह दु:ख उठाएगा, और तीसरे दिन मरे हुओं में से जी उठेगा।",
			"47": "<sup>47</sup> और यरूशलेम से लेकर सब जातियों में मन फिराव का और पापों की क्षमा का प्रचार, उसी के नाम से किया जाएगा।",
			"48": "<sup>48</sup> तुम इन सब बातें के गवाह हो।",
			"49": "<sup>49</sup> और देखो, जिस की प्रतिज्ञा मेरे पिता ने की है, मैं उस को तुम पर उतारूंगा और जब तक स्वर्ग से सामर्थ न पाओ, तब तक तुम इसी नगर में ठहरे रहो॥",
			"50": "<sup>50</sup> तब वह उन्हें बैतनिय्याह तक बाहर ले गया, और अपने हाथ उठाकर उन्हें आशीष दी।",
			"51": "<sup>51</sup> और उन्हें आशीष देते हुए वह उन से अलग हो गया और स्वर्ग से उठा लिया गया।",
			"52": "<sup>52</sup> और वे उस को दण्डवत करके बड़े आनन्द से यरूशलेम को लौट गए।",
			"53": "<sup>53</sup> और लगातार मन्दिर में उपस्थित होकर परमेश्वर की स्तुति किया करते थे॥"
		}
	}
};
module.exports = book;