var book = {
	"name": "1. Ljetopisa",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adam, Sit, Enos,",
			"2": "<sup>2</sup> Kajinan, Maleleilo, Jared,",
			"3": "<sup>3</sup> Enoh, Matusal, Lameh,",
			"4": "<sup>4</sup> Noje, Sim, Ham i Jafet.",
			"5": "<sup>5</sup> Sinovi Jafetovi: Gomer i Magog i Madaj i Javan i Tovel i Meseh i Tiras.",
			"6": "<sup>6</sup> A sinovi Gomerovi: Ashenas i Rafat i Togarma.",
			"7": "<sup>7</sup> A sinovi Javanovi: Elisa i Tarsis, Kitim i Dodanim,",
			"8": "<sup>8</sup> A sinovi Hamovi: Hus i Misraim, Fut i Hanan.",
			"9": "<sup>9</sup> A sinovi Husovi: Sava i Avila i Savata i Regma i Savataka. A sinovi Regmini: Sava i Dedan.",
			"10": "<sup>10</sup> I Hus rodi Nevroda. On prvi bi silan na zemlji.",
			"11": "<sup>11</sup> A Misraim rodi Ludeje i Anameje i Leaveje i Naftuheje,",
			"12": "<sup>12</sup> I Patruseje i Hasluheje, od kojih izidoše Filisteji i Kaftoreji.",
			"13": "<sup>13</sup> A Hanan rodi Sidona prvenca svojega, i Heta,",
			"14": "<sup>14</sup> I Jevuseje i Amoreje i Gergeseje,",
			"15": "<sup>15</sup> I Jeveje i Arukeje i Aseneje,",
			"16": "<sup>16</sup> I Arvadeje i Samareje i Amateje.",
			"17": "<sup>17</sup> Sinovi Simovi: Elam i Asur i Arfaksad i Lud i Aram i Uz i Ul i Geter i Meseh.",
			"18": "<sup>18</sup> A Arfaksad rodi Salu, a Sala rodi Evera.",
			"19": "<sup>19</sup> A Everu se rodiše dva sina; jednome beše ime Falek, jer se u njegovo vreme razdijeli zemlja; a ime bratu njegovu Jektan.",
			"20": "<sup>20</sup> A Jektan rodi Almodada i Salefa i Asarmota i Jaraha,",
			"21": "<sup>21</sup> I Adorama i Uzala i Diklu,",
			"22": "<sup>22</sup> I Evala i Avimaila i Savu,",
			"23": "<sup>23</sup> I Ofira i Evilu i Jovava: ti svi bjehu sinovi Jektanovi.",
			"24": "<sup>24</sup> Sim, Arfaksad, Sala,",
			"25": "<sup>25</sup> Ever, Falet, Ragav,",
			"26": "<sup>26</sup> Seruh, Nahor, Tara,",
			"27": "<sup>27</sup> Avram, to je Avraam.",
			"28": "<sup>28</sup> Sinovi Avramovi: Isak i Ismailo.",
			"29": "<sup>29</sup> Ovo je pleme njihovo: prvenac Ismailov Navajot, pa Kidar i Avdeilo i Mivsam.",
			"30": "<sup>30</sup> Misma i Duma, Masa, Adad i Tema,",
			"31": "<sup>31</sup> Jetur, Nafis i Kedma; to su sinovi Ismailovi.",
			"32": "<sup>32</sup> A sinovi Heture inoče Avramove: ona rodi Zemrana i Joksana i Madana i Madijana i Jesvoka i Suja. A sinovi Joksanovi: Sava i Dedan.",
			"33": "<sup>33</sup> A sinovi Madijanovi: Gefa i Efer i Enoh i Avida i Eldaga. Ti svi bjehu sinovi Heturini.",
			"34": "<sup>34</sup> Tako Avram rodi Isaka; a sinovi Isakovi bjehu Isav i Izrailj.",
			"35": "<sup>35</sup> Sinovi Isavovi: Elifas, Raguilo i Jeus i Jeglom i Korej.",
			"36": "<sup>36</sup> Sinovi Elifasovi: Teman i Omar, Sofar i Gotom, Kenez i Tamna i Amalik.",
			"37": "<sup>37</sup> Sinovi Raguilovi: Nahat, Zara i Soma i Moza.",
			"38": "<sup>38</sup> A sinovi Sirovi: Lotan i Soval i Sevegon i Ana i Dison i Eser i Disan.",
			"39": "<sup>39</sup> A sinovi Lotanovi: Horije i Eman; a sestra Lotanova Tamna.",
			"40": "<sup>40</sup> Sinovi Sovalovi: Elan i Manahat i Eval, Sefija i Onam. A sinovi Sevegonovi: Aja i Ana.",
			"41": "<sup>41</sup> Sinovi Anini: Dison, i sinovi Disonovi Amram i Asvan i Itran i Haran.",
			"42": "<sup>42</sup> Sinovi Eserovi: Valan i Zavan i Jakan. Sinovi Disanovi: Uz i Aran.",
			"43": "<sup>43</sup> A ovo su carevi koji carovaše u zemlji Edomskoj prije nego se zacari car nad sinovima Izrailjevijem: Valak sin Veorov, a gradu mu bješe ime Denava.",
			"44": "<sup>44</sup> A kad umrije Valak, zacari se na njegovo mjesto Jovav sin Zarin od Vosore.",
			"45": "<sup>45</sup> A kad umrije Jovav, zacari se na njegovo mjesto Asom od zemlje Temanovske.",
			"46": "<sup>46</sup> A kad umrije Asom, zacari se na njegovo mjesto Adad sin Varadov, koji isiječe Madijance u polju Moavskom; a gradu mu bješe ime Getem.",
			"47": "<sup>47</sup> A kad umrije Adad, zacari se na njegovo mjesto Samada iz Masekasa.",
			"48": "<sup>48</sup> A kad umrije Samada, zacari se na njegovo mjesto Saul iz Rovota na riječi.",
			"49": "<sup>49</sup> A kad umrije Saul, zacari se na njegovo mjesto Valenon sin Ahovorov.",
			"50": "<sup>50</sup> A kad umrije Valenon, zacari se na njegovo mjesto Adad; a grad mu se zvaše Fogor, a ženi mu bješe ime Meteveila kći Matraide kćeri Mezovove.",
			"51": "<sup>51</sup> A kad umrije Adad, nastaše knezovi u Edomskoj: knez Tamna, knez Alva, knez Jetet,",
			"52": "<sup>52</sup> Knez Olivema, knez Ila, knez Finon,",
			"53": "<sup>53</sup> Knez Kenez, knez Teman, knez Mivsar,",
			"54": "<sup>54</sup> Knez Magedilo, knez Iram. To bjehu knezovi Edomski."
		},
		"2": {
			"1": "<sup>1</sup> Ovo su sinovi Izrailjevi: Ruvim, Simeun, Levije i Juda, Isahar i Zavulon,",
			"2": "<sup>2</sup> Dan, Josif i Venijamin, Neftalim, Gad i Asir.",
			"3": "<sup>3</sup> Sinovi Judini: Ir i Avnan i Sila; ta mu tri rodi kći Suvina Hananejka; ali Ir prvenac Judin bješe nevaljao pred Gospodom, te ga ubi.",
			"4": "<sup>4</sup> A Tamara snaha njegova rodi mu Faresa i Zaru. Svega pet bješe sinova Judinijeh.",
			"5": "<sup>5</sup> Sinovi Faresovi: Esron i Amul.",
			"6": "<sup>6</sup> A sinovi Zarini: Zimrije i Etan i Eman i Halkol i Dara; svega pet.",
			"7": "<sup>7</sup> I sinovi Harmijini: Ahan, koji smuti Izrailja ogriješiv se o stvari proklete.",
			"8": "<sup>8</sup> A sinovi Etanovi: Azarija.",
			"9": "<sup>9</sup> A sinovi Esronovi, što mu se rodiše: Jerameilo i Aram i Halev.",
			"10": "<sup>10</sup> A Aram rodi Aminadava, a Aminadav rodi Nasona, kneza sinova Judinijeh;",
			"11": "<sup>11</sup> A Nason rodi Salmona; a Salmon rodi Voza;",
			"12": "<sup>12</sup> A Voz rodi Ovida; a Ovid rodi Jeseja;",
			"13": "<sup>13</sup> A Jesej rodi prvenca svojega Elijava, i Avinadava drugoga i Samu trećega,",
			"14": "<sup>14</sup> Natanaila četvrtoga, Radaja petoga,",
			"15": "<sup>15</sup> Osema šestoga, Davida sedmoga,",
			"16": "<sup>16</sup> I sestre mu Seruju i Avigeju. A sinovi Serujini bjehu Avisaj i Joav i Asailo, trojica.",
			"17": "<sup>17</sup> A Avigeja rodi Amasu; a otac Amasin bješe Jeter Ismailjac.",
			"18": "<sup>18</sup> A Halev sin Esronov rodi s Azuvom ženom svojom i s Jeriotom sinove, i sinovi mu bjehu Jeser i Sovav i Ardon.",
			"19": "<sup>19</sup> A kad umrije Azuva, Halev se oženi Efratom, koja mu rodi Ora;",
			"20": "<sup>20</sup> A Or rodi Uriju, a Urija rodi Veseleila.",
			"21": "<sup>21</sup> Potom otide Esron ka kćeri Mehira oca Galadova, i oženi se njom kad mu bijaše šezdeset godina, i ona mu rodi Seguva.",
			"22": "<sup>22</sup> A Seguv rodi Jaira, koji imaše dvadeset i tri grada u zemlji Galadskoj.",
			"23": "<sup>23</sup> Jer uze Gesurejima i Sircima sela Jairova i Kenat sa selima njegovijem, šezdeset gradova. To sve uzeše sinovi Mahira oca Galadova.",
			"24": "<sup>24</sup> I kad umrije Esron u Halev-Efrati, žena Esronova Avija rodi mu Ashora oca Tekojanima.",
			"25": "<sup>25</sup> A sinovi Jerameila prvenca Esronova bjehu: prvenac Aram, pa Vuna i Orem i Osem i Ahija.",
			"26": "<sup>26</sup> Imaše i drugu ženu Jerameilo, po imenu Ataru, ona je mati Onamova.",
			"27": "<sup>27</sup> A sinovi Arama prvenca Jerameilova bjehu: Mas i Jamin i Eker.",
			"28": "<sup>28</sup> I sinovi Onamovi bjehu: Samaj i Jadaj; a sinovi Samajevi: Nadav i Avisur.",
			"29": "<sup>29</sup> I ime ženi Avisurovoj bješe Avihaila, koja mu rodi Avana i Molida.",
			"30": "<sup>30</sup> A sinovi Nadavovi: Seled i Apaim; ali Seled umrije bez djece.",
			"31": "<sup>31</sup> A sinovi Apailovi: Jesej; a sinovi Jesejevi: Sisan, i kći Sisanova Alaja.",
			"32": "<sup>32</sup> A sinovi Jadaja brata Samajeva: Jeter i Jonatan; ali Jeter umrije bez djece.",
			"33": "<sup>33</sup> A sinovi Jonatanovi: Falet i Zaza. Ti bijahu sinovi Jerameilovi.",
			"34": "<sup>34</sup> A Sisan ne imaše sinova nego kćeri; i imaše Sisan slugu Misirca po imenu Jaraju.",
			"35": "<sup>35</sup> Zato Sisan dade kćer svoju Jaraji sluzi svojemu za ženu; a ona mu rodi Ataja.",
			"36": "<sup>36</sup> A Ataj rodi Natana; a Natan rodi Zavada;",
			"37": "<sup>37</sup> A Zavad rodi Eflala; a Eflal rodi Ovida;",
			"38": "<sup>38</sup> A Ovid rodi Juja; a Juj rodi Azariju;",
			"39": "<sup>39</sup> A Azarija rodi Helisa; a Helis rodi Eleasu;",
			"40": "<sup>40</sup> A Eleasa rodi Sisamaja; a Sisamaj rodi Saluma;",
			"41": "<sup>41</sup> A Salum rodi Jekamiju; a Jekamija rodi Elisama.",
			"42": "<sup>42</sup> A sinovi Haleva brata Jerameilova bjehu: Misa prvenac njegov, otac Zifejima; pa sinovi Marise oca Hevronova.",
			"43": "<sup>43</sup> A sinovi Hevronovi: Korej i Tafuja i Rekem i Sema.",
			"44": "<sup>44</sup> A Sema rodi Rama oca Jorkoamova; a Rekem rodi Samaja.",
			"45": "<sup>45</sup> A sin Samajev bješe Maon, a taj Maon bi otac Vet-Suru.",
			"46": "<sup>46</sup> I Gefa inoča Halevova rodi Harana i Mosu i Gazesa.",
			"47": "<sup>47</sup> A sinovi Jadajevi: Regem i Jotam i Gisan i Felet i Gefa i Sagaf.",
			"48": "<sup>48</sup> Maha inoča Halevova rodi Severa i Tirhanu.",
			"49": "<sup>49</sup> Rodi i Sagafa oca Madmancima i Sevu oca Mahvincima i Gavajanima; a kći Halevova bijaše Ahsa.",
			"50": "<sup>50</sup> Ovo bijahu sinovi Haleva sina Ora prvenca Efratina; Soval otac Kirijat-Jarimu,",
			"51": "<sup>51</sup> Salma otac Vitlejemu, Aref otac Vet-Gaderu.",
			"52": "<sup>52</sup> A imaše sinove Soval otac Kirijat-Jarimu: Aroja i Asi-Amenuhota.",
			"53": "<sup>53</sup> A porodice Kirijat-Jarimske bjehu: Jetrani i Fućani i Sumaćani i Misrajani. Od njih izidoše Saraćani i Estaoljani.",
			"54": "<sup>54</sup> Sinovi Salmini: Vitlejemci i Netofaćani, Ataroćani od doma Joavova, i Zorani, polovina Manahaćana,",
			"55": "<sup>55</sup> I porodice pisarske u Javisu, Tiraćani, Simeaćani, Suhaćani; to su Keneji, koji se narodiše od Emata, oca doma Rihavova."
		},
		"3": {
			"1": "<sup>1</sup> A ovo su sinovi Davidovi koji mu se rodiše u Hevronu: prvenac Amnon od Ahinoame Jezraeljanke, drugi Danilo od Avigeje Karmilke,",
			"2": "<sup>2</sup> Treći Avesalom, sin Mahe kćeri Talmaja cara Gesurskoga, četvrti Adonija, sin Agitin,",
			"3": "<sup>3</sup> Peti Sefatija od Avitale, šesti Itram od Egle žene njegove.",
			"4": "<sup>4</sup> Šest mu se rodi u Hevronu, gdje carova sedam godina i šest mjeseca, a trideset i tri carova u Jerusalimu.",
			"5": "<sup>5</sup> A ovi mu se rodiše u Jerusalimu: Samaja i Sovav i Natan i Solomun, četiri, od Vitsaveje kćeri Amilove.",
			"6": "<sup>6</sup> I Jevar i Elisama i Elifalet,",
			"7": "<sup>7</sup> I Noga i Nefeg i Jafija,",
			"8": "<sup>8</sup> I Elisama i Elijada i Elifelet, devet,",
			"9": "<sup>9</sup> Sve sinovi Davidovi osim sinova inoča njegovijeh i osim Tamare sestre njegove.",
			"10": "<sup>10</sup> A sin Solomunov bijaše Rovoam, a njegov sin Avija, a njegov sin Asa, a njegov sin Josafat,",
			"11": "<sup>11</sup> A njegov sin Joram, a njegov sin Ohozija, a njegov sin Joas.",
			"12": "<sup>12</sup> A njegov sin Amasija, a njegov sin Azarija, a njegov sin Jotam,",
			"13": "<sup>13</sup> A njegov sin Ahaz, a njegov sin Jezekija, a njegov sin Manasija,",
			"14": "<sup>14</sup> A njegov sin Amon, a njegov sin Josija.",
			"15": "<sup>15</sup> A sinovi Josijini: prvenac Joanan, drugi Joakim, treći Sedekija, četvrti Salum.",
			"16": "<sup>16</sup> A sinovi Joakimovi: Jehonija sin mu, i njegov sin Sedekija.",
			"17": "<sup>17</sup> A sinovi Jehonije sužnja: Salatilo sin mu.",
			"18": "<sup>18</sup> A njegovi sinovi Malhiram i Fedaja i Senasar, Jekamija, Osama i Nedavija.",
			"19": "<sup>19</sup> A sinovi Fedajini: Zorovavelj i Simej; a sinovi Zorovaveljevi: Mesulam i Ananija i sestra im Selometa.",
			"20": "<sup>20</sup> A sinovi Mesulamovi: Asuva i Oilo i Varahija i Asadija i Jusavesed, pet.",
			"21": "<sup>21</sup> A sinovi Ananijini: Felatija i Jesaja, sinovi Refajini, sinovi Arnanovi, sinovi Ovadijini, sinovi Sehanijini.",
			"22": "<sup>22</sup> A sinovi Sehanijini: Semaja, i sinovi Semajini: Hatus i Igeal i Varija i Nearija i Safat, šest.",
			"23": "<sup>23</sup> A sinovi Nearijini: Elioinaj i Jezekija i Azrikam, tri.",
			"24": "<sup>24</sup> A sinovi Elioinajevi: Odaja i Eliasiv i Felaja i Akuv i Joanan i Dalaja i Anan, sedam."
		},
		"4": {
			"1": "<sup>1</sup> Sinovi Judini bjehu: Fares, Esron i Harmija i Or i Soval.",
			"2": "<sup>2</sup> A Reaja sin Sovalov rodi Jata, a Jat rodi Ahumaja i Lada. To su porodice Saratske.",
			"3": "<sup>3</sup> A ovo su od oca Itama: Jezrael i Jesma i Jedvas, a sestri njihovoj bješe ime Aselelfonija.",
			"4": "<sup>4</sup> I Fanuilo bješe otac Gedoru, i Eser otac Husin. To bijahu sinovi Ora prvenca Efrate oca Vitlejemcima.",
			"5": "<sup>5</sup> A Ashor otac Tekujanima imaše dvije žene, Elu i Naru.",
			"6": "<sup>6</sup> I Nara mu rodi Ahuzama i Efera i Temana i Ahastara. To su sinovi Narini.",
			"7": "<sup>7</sup> A sinovi Elini: Sered, Jesoar i Etnan.",
			"8": "<sup>8</sup> A Kos rodi Anuva i Sovivu i porodice Aharila sina Arumova.",
			"9": "<sup>9</sup> A Javis bješe slavniji od svoje braće, i mati mu nadje ime Javis govoreći: rodih ga s bolom.",
			"10": "<sup>10</sup> I Javis prizva Boga Izrailjeva govoreći: o da bi me blagoslovio i raširio međe moje, i ruka tvoja da bi bila sa mnom, i da bi me sačuvao oda zla da me ne ucvijeli! I učini Bog za što ga moli.",
			"11": "<sup>11</sup> A Heluv brat Sujin rodi Mehira. On bi otac Estonov.",
			"12": "<sup>12</sup> A Eston rodi Vet-Rafu i Feseju i Tehinu oca gradu Nasu. To su ljudi Rihavovi.",
			"13": "<sup>13</sup> A sinovi Kenezovi bjehu Gotonilo i Seraja. A sinovi Gotonilovi: Atat.",
			"14": "<sup>14</sup> A Meonotaj rodi Ofru; a Seraja rodi Joava, oca onijeh što žive u dolini drvodjeljskoj, jer bijahu drvodjelje.",
			"15": "<sup>15</sup> A sinovi Haleva sina Jefonijina bijahu Ir, Ila i Nam. A sin Ilin bješe Kenez.",
			"16": "<sup>16</sup> A sinovi Jeleleilovi: Zif i Zifa, Tirija i Asareilo.",
			"17": "<sup>17</sup> A sinovi Ezrini: Jeter i Mered i Efer i Jalon; a žena Meredova rodi Marijama i Samaja i Jesvu, oca Estemoji.",
			"18": "<sup>18</sup> A žena njegova Judija rodi Jereda oca Gedoru, i Evera oca Sohotu, i Jekutila oca Zanoji. A to bijahu sinovi Vitije kćeri Faraonove, kojom se oženi Mered.",
			"19": "<sup>19</sup> A sinovi žene Odijine sestre Nama oca Keili bijahu Garmija i Estemoja Mahaćanin.",
			"20": "<sup>20</sup> A sinovi Simonovi: Amion i Rina i Ven-Anan i Tilon. A sinovi Jesejevi: Zohet i Ven-Zohet.",
			"21": "<sup>21</sup> A sinovi Sile sina Judina: Ir otac Lihu, i Lada otac Marisi, i porodice doma platnarskoga, doma Asvejina.",
			"22": "<sup>22</sup> I Jokim i Hasivljani, i Joas i Saraf, koji vladahu u Moavskoj, i Jasuvi-Lehem. Ali to su stare stvari.",
			"23": "<sup>23</sup> To bijahu lončari i življahu u sadovima i zabranima, i bijahu ondje kod cara za njegove poslove.",
			"24": "<sup>24</sup> Sinovi Simeunovi: Nemuilo i Jamin, Jariv, Zera, Saul;",
			"25": "<sup>25</sup> A njegov sin Salum, a njegov sin Mivsam, a njegov sin Misma.",
			"26": "<sup>26</sup> A sinovi Mismini: Amuilo sin mu, i njegov sin Zahur, i njegov sin Simej.",
			"27": "<sup>27</sup> A Simej imaše šesnaest sinova i šest kćeri; a braća njegova nemahu mnogo sinova, i sve porodice njihove ne bješe tako mnogo kao sinova Judinijeh.",
			"28": "<sup>28</sup> A življahu u Virsaveji i Moladi i Asar-Sualu,",
			"29": "<sup>29</sup> I u Vali i u Asemu i u Toladu,",
			"30": "<sup>30</sup> I u Vetuilu i u Ormi i u Siklagu,",
			"31": "<sup>31</sup> I u Vet-Marhavotu I u Asar-Susimu i u Vet-Vireju i u Saraimu. To bjehu gradovi njihovi do cara Davida.",
			"32": "<sup>32</sup> A sela njihova bijahu Itam i Ajin, Rimon i Tohen i Asan, pet gradova,",
			"33": "<sup>33</sup> I sva sela njihova što bijahu oko tijeh gradova do Vala. To bijahu stanovi njihovi po rodu njihovu.",
			"34": "<sup>34</sup> A Mesovav i Jamlih i Josa sin Amasijin,",
			"35": "<sup>35</sup> I Joilo i Juj sin Josivije sina Seraje sina Asilova,",
			"36": "<sup>36</sup> I Elioinaj i Jakova i Jesohaja i Asaja i Adilo i Jesimilo i Venaja,",
			"37": "<sup>37</sup> I Zisa sin Sifija sina Alona sina Jedaje sina Simrija sinova Semajina;",
			"38": "<sup>38</sup> Ti imenovani bijahu knezovi u porodicama svojim, i domovi otaca njihovijeh umnožiše se veoma.",
			"39": "<sup>39</sup> I zato otidoše u Gedor do istočne strane doline da traže pašu stoci svojoj.",
			"40": "<sup>40</sup> I nađoše pašu obilatu i dobru i zemlju prostranu i mirnu i rodnu, jer prije ondje življahu koji bijahu od Hama.",
			"41": "<sup>41</sup> Ti dakle zapisani po imenu došavši za Jezekije cara Judina razbiše šatore njihove i stanovnike koji se nađoše ondje, i pobiše ih kao proklete da ne osta nijedan do danas, i naseliše se mjesto njih, jer ondje bijaše paše za stoku njihovu.",
			"42": "<sup>42</sup> A između njih, sinova Simeunovijeh, izide na goru Sir pet stotina ljudi, a poglavice im bijahu Felatija i Nearija i Refaja i Ozilo, sinovi Jesejevi.",
			"43": "<sup>43</sup> I pobiše ostatak što bijahu utekli između Amalika, i naseliše se ondje do današnjega dana."
		},
		"5": {
			"1": "<sup>1</sup> A sinovi Ruvima prvenca Izrailjeva, jer on bijaše prvenac, ali kad oskrvni postelju oca svojega, dano bi njegovo prvenaštvo sinovima Josifa sina Izrailjeva, ali ne tako da se broji prvenac,",
			"2": "<sup>2</sup> Jer Juda bi najsilniji među braćom svojom i od njega je vođ, ali prvenaštvo dobi Josif;",
			"3": "<sup>3</sup> Sinovi Ruvima prvenca Izrailjeva bijahu: Anoh i Faluj, Esron i Harmija,",
			"4": "<sup>4</sup> Sinovi Joilovi: Semaja sin mu, a njegov sin Gog, a njegov sin Simej,",
			"5": "<sup>5</sup> A njegov sin Miha, a njegov sin Reaja, a njegov sin Val,",
			"6": "<sup>6</sup> A njegov sin Veira, kojega odvede Telgat-Felnasar car Asirski; on bijaše knez plemena Ruvimova.",
			"7": "<sup>7</sup> A braći njegovoj po porodicama njihovijem kad se izbrojiše po koljenima svojim, bješe knez Jeilo i Zaharija,",
			"8": "<sup>8</sup> A Vela sin Azaza sina Seme sina Joilova; on življaše u Aroiru i do Nevona i Valmeona.",
			"9": "<sup>9</sup> Potom življaše k istoku dori do pustinje od rijeke Efrata, jer im se stoka umnoži u zemlji Galadskoj.",
			"10": "<sup>10</sup> I za vremena Saulova vojevaše na Agarene, koji izgiboše od ruke njihove; i tako se naseliše u šatorima njihovijem po svemu istočnom kraju zemlje Galadske.",
			"11": "<sup>11</sup> A sinovi Gadovi življahu prema njima u zemlji Vasanskoj do Salhe.",
			"12": "<sup>12</sup> Joilo im bijaše poglavar, a Safan drugi, pa Janaj i Safat u Vasanu.",
			"13": "<sup>13</sup> A braća njihova po domovima otačkim bijahu: Mihailo i Mesulam i Seva i Joraj i Jahan i Zije i Ever, sedmorica.",
			"14": "<sup>14</sup> Ti bijahu sinovi Avihaila sina Urija sina Jaroje sina Galada sina Mihaila sina Jesisaja sina Jadona sina Vuzova.",
			"15": "<sup>15</sup> Ahije sin Avdila sina Gunijeva bijaše poglavar u domu otaca njihovijeh.",
			"16": "<sup>16</sup> I življahu u Galadu, u Vasanu i selima njegovijem i u svijem podgrađima Saronskim do međa njegovijeh.",
			"17": "<sup>17</sup> Svi ovi biše izbrojeni za vremena Jotama cara Judina i za vreme Jerovoama cara Izrailjeva.",
			"18": "<sup>18</sup> Sinova Ruvimovijeh i Gadovijeh i polovine plemena Manasijina, hrabrijeh ljudi, što nošahu štit i mač, i zatezahu luk, i vještijeh boju, bijaše četrdeset i četiri tisuće i sedam stotina i šezdeset, koji iđahu na vojsku;",
			"19": "<sup>19</sup> I vojevaše na Agarene, na Jetureje i Nafiseje i Nodaveje,",
			"20": "<sup>20</sup> I dođe im pomoć suprot njih, i Agareni im biše dani u ruke i sve što imahu; jer zavapiše Bogu u boju, i usliši ih, jer se pouzdaše u nj.",
			"21": "<sup>21</sup> I zaplijeniše stoku njihovu, pedeset tisuća kamila i dvjesta i pedeset tisuća ovaca i dvije tisuće magaraca i sto tisuća duša ljudskih.",
			"22": "<sup>22</sup> A ranjenijeh mnogo pade; jer taj boj bi od Boga; i nastavaše na mjestu njihovu do seobe svoje.",
			"23": "<sup>23</sup> A sinovi polovine plemena Manasijina življahu u toj zemlji od Vasana do Val-Ermona i Senira, do gore Ermona; bijahu se umnožili.",
			"24": "<sup>24</sup> A ovo bijahu poglavari u domu otaca njihovijeh: Efer i Jesej i Elilo i Azrilo i Jeremija i Odavija i Jadilo, ljudi hrabri i na glasu, poglavari u domu otaca svojih.",
			"25": "<sup>25</sup> Ali kad zgriješiše Bogu otaca svojih i činiše preljubu za Bogovima naroda one zemlje, koje Bog istrijebi ispred njih,",
			"26": "<sup>26</sup> Podiže Bog Izrailjev duh Fula cara Asirskoga i duh Telgat-Felnasara cara Asirskoga, i preseliše pleme Ruvimovo i pleme Gadovo i polovinu plemena Manasijina, i odvedoše ih u Alu i u Avor i u Aru, i na rijeku Gozan, gdje ostaše do danas."
		},
		"6": {
			"1": "<sup>1</sup> Sinovi Levijevi bijahu: Girson, Kat i Merarije.",
			"2": "<sup>2</sup> A sinovi Katovi: Amram, Isar i Hevron i Ozilo.",
			"3": "<sup>3</sup> A sinovi Amramovi: Aron i Mojsije, i kći Marija. A sinovi Aronovi: Nadav i Avijud i Eleazar i Itamar.",
			"4": "<sup>4</sup> Eleazar rodi Finesa; a Fines rodi Avisuju;",
			"5": "<sup>5</sup> A Avisuja rodi Vukija; a Vukije rodi Oziju;",
			"6": "<sup>6</sup> A Ozija rodi Zeraju; a Zeraja rodi Merajota;",
			"7": "<sup>7</sup> A Merajot rodi Amariju; a Amarija rodi Ahitova;",
			"8": "<sup>8</sup> A Ahitov rodi Sadoka; a Sadok rodi Ahimasa;",
			"9": "<sup>9</sup> A Ahimas rodi Azariju; a Azarija rodi Joanana;",
			"10": "<sup>10</sup> A Joanan rodi Azariju, koji bi sveštenik u domu što sazida Solomun u Jerusalimu;",
			"11": "<sup>11</sup> A Azarija rodi Amariju; a Amarija rodi Ahitova;",
			"12": "<sup>12</sup> A Ahitov rodi Sadoka; a Sadok rodi Saluma;",
			"13": "<sup>13</sup> A Salum rodi Helkiju; a Helkija rodi Azariju;",
			"14": "<sup>14</sup> A Azarija rodi Seraju; a Seraja rodi Josedeka.",
			"15": "<sup>15</sup> A Josedek otide kad Gospod preseli Judeje i Jerusalim rukom Navuhodonosorovom.",
			"16": "<sup>16</sup> Sinovi Levijevi bijahu: Girson, Kat i Merarije.",
			"17": "<sup>17</sup> A ovo su imena sinovima Girsonovijem: Lovenije i Simej.",
			"18": "<sup>18</sup> A sinovi Katovi bjehu: Amram i Isar i Hevron i Ozilo.",
			"19": "<sup>19</sup> Sinovi Merarijevi: Molija i Musija. I ovo su porodice Levitske po ocima svojim:",
			"20": "<sup>20</sup> Girsonova: Levenije sin mu, a njegov sin Jat, a njegov sin Zima,",
			"21": "<sup>21</sup> A njegov sin Joah, a njegov sin Ido, a njegov sin Zera, a njegov sin Jetraj.",
			"22": "<sup>22</sup> Sinovi Katovi: Aminadav sin mu, a njegov sin Korej, a njegov sin Asir.",
			"23": "<sup>23</sup> A njegov sin Elkana, a njegov sin Evijasaf, a njegov sin Asir,",
			"24": "<sup>24</sup> A njegov sin Tahat, a njegov sin Urilo, a njegov sin Ozija, a njegov sin Saul.",
			"25": "<sup>25</sup> A sinovi Elkanini: Amasaj i Ahimot,",
			"26": "<sup>26</sup> Elkana; sinovi Elkanini: Sufija sin mu, a njegov sin Mahat;",
			"27": "<sup>27</sup> A njegov sin Elijav, a njegov sin Jeroam, a njegov sin Elkana.",
			"28": "<sup>28</sup> A sinovi Samuilovi: Vasnija prvenac, pa Avija.",
			"29": "<sup>29</sup> Sinovi Merarijevi: Molija, a njegov sin Lovenije, a njegov sin Simej, a njegov sin Uza,",
			"30": "<sup>30</sup> A njegov sin Simaja, a njegov sin Agija, a njegov sin Asaja.",
			"31": "<sup>31</sup> A ovo su koje postavi David da pjevaju u domu Gospodnjem, kad se namjesti kovčeg,",
			"32": "<sup>32</sup> Koji služahu pred šatorom od sastanka pjevajući, dok ne sazida Solomun dom Gospodnji u Jerusalimu, i stajahu po redu svom u službi svojoj;",
			"33": "<sup>33</sup> Ovi su što stajahu i sinovi njihovi: od sinova Katovijeh Eman pjevač, sin Joila sina Samuila,",
			"34": "<sup>34</sup> Sina Elkane, sina Jeroama, sina Elila, sina Toje,",
			"35": "<sup>35</sup> Sina Sufa, sina Elkane, sina Mata, sina Amasaja,",
			"36": "<sup>36</sup> Sina Elkane, sina Joila, sina Azarije, sina Sofonije,",
			"37": "<sup>37</sup> Sina Tahate, sina Asira, sina Aviasafa, sina Koreja,",
			"38": "<sup>38</sup> Sina Isara, sina Kata, sina Levija, sina Izrailjeva.",
			"39": "<sup>39</sup> I brat mu Asaf, koji mu stajaše s desne strane; a Asaf bijaše sin Varahije, sina Simeje,",
			"40": "<sup>40</sup> Sina Mihaila, sina Vasije, sina Malhije,",
			"41": "<sup>41</sup> Sina Etnija, sina Zera, sina Adaje,",
			"42": "<sup>42</sup> Sina Etana, sina Zime, sina Simeja,",
			"43": "<sup>43</sup> Sina Jata, sina Girsona, sina Levijeva.",
			"44": "<sup>44</sup> A sinovi Merarijevi, braća njihova, stajahu s lijeve strane: Etan sin Kisije, sina Avdija, sina Maluha,",
			"45": "<sup>45</sup> Sina Asavije, sina Amasije, sina Helkije,",
			"46": "<sup>46</sup> Sina Amsije, sina Vanije, sina Samira,",
			"47": "<sup>47</sup> Sina Molije, sina Musije, sina Merarije, sina Levijeva.",
			"48": "<sup>48</sup> A braća njihova, ostali Leviti, biše određeni na svu službu i šatoru doma Božijega.",
			"49": "<sup>49</sup> A Aron i sinovi njegovi paljahu na oltaru za žrtve paljenice i na oltaru kadionom, vršeći sve poslove u svetinji nad svetinjama, i čineći očišćenje za Izrailja po svemu kako je zapovjedio Mojsije sluga Božji.",
			"50": "<sup>50</sup> A ovo su sinovi Aronovi: Eleazar sin mu, a njegov sin Fines, a njegov sin Avisuja,",
			"51": "<sup>51</sup> A njegov sin Vukije, a njegov sin Ozije, a njegov sin Zeraja,",
			"52": "<sup>52</sup> A njegov sin Merajot, a njegov sin Amarija, a njegov sin Ahitov,",
			"53": "<sup>53</sup> A njegov sin Sadok, a njegov sin Ahimas.",
			"54": "<sup>54</sup> A ovo su im stanovi po gradovima njihovijem u međama njihovijem: Sinovima Aronovijem od porodice Katove dopade ždrijebom,",
			"55": "<sup>55</sup> I dadoše im Hevron u zemlji Judinoj, i podgrađa njegova oko njega;",
			"56": "<sup>56</sup> A polje gradsko i sela njegova dadoše Halevu sinu Jefonijinu;",
			"57": "<sup>57</sup> Dadoše dakle sinovima Aronovijem gradove Judine: Hevron utočište, i Livnu i podgrađa njezina i Jatir i Estemoju i podgrađa njezina,",
			"58": "<sup>58</sup> I Ilon i podgrađa njegova, Devir i podgrađa njegova,",
			"59": "<sup>59</sup> I Asan i podgrađa njegova, i Vet-Semes i podgrađa njegova;",
			"60": "<sup>60</sup> A od plemena Venijaminova: Gavaju i podgrađa njezina, i Alemet i podgrađa njegova i Anatot i podgrađa njegova; svega gradova njihovijeh trinaest po porodicama njihovijem.",
			"61": "<sup>61</sup> I ostalijem sinovima Katovijem od porodice toga plemena daše ždrijebom od polovine plemena, od polovine Manasijine deset gradova.",
			"62": "<sup>62</sup> A sinovima Girsonovijem po porodicama njihovijem trinaest gradova od plemena Isaharova i od plemena Asirova i od plemena Neftalimova i od plemena Manasijina u Vasanu.",
			"63": "<sup>63</sup> Sinovima Merarijevim po porodicama njihovijem daše ždrijebom od plemena Ruvimova i od plemena Gadova i od plemena Zavulonova dvanaest gradova.",
			"64": "<sup>64</sup> Tako daše sinovi Izrailjevi Levitima te gradove i podgrađa njihova.",
			"65": "<sup>65</sup> I dade ždrijebom od plemena sinova Judinijeh i od plemena sina Simeunovijeh i od plemena sinova Venijaminovijeh te gradove, koje imenovaše poimence.",
			"66": "<sup>66</sup> I onima koji bijahu od roda sinova Katovijeh a gradovi njihovi i međe njihove bijahu u plemenu Jefremovu,",
			"67": "<sup>67</sup> Njima daše grad za utočište Sihem i podgrađa njegova u gori Jefremovoj, i Gezer i podgrađa njegova,",
			"68": "<sup>68</sup> I Jokneam i podgrađa njegova, i Vet-Oron i podgrađa njegova,",
			"69": "<sup>69</sup> I Ejalon i podgrađa njegova, i Gat-Rimon i podgrađa njegova,",
			"70": "<sup>70</sup> I od polovine plemena Manasijina Anir i podgrađa njegova, i Vileam, i podgrađa njegova, porodicama sinova Katovijeh ostalijem.",
			"71": "<sup>71</sup> A sinovima Girsonovijem daše od porodice polovine plemena Manasijina Golan u Vasanu i podgrađa njegova, i Asarot i podgrađa njegova;",
			"72": "<sup>72</sup> A od plemena Isaharova Kedes i podgrađa njegova, Davrat i podgrađa njegova,",
			"73": "<sup>73</sup> I Ramot i podgrađa njegova, i Anim i podgrađa njegova;",
			"74": "<sup>74</sup> A od plemena Asirova Masal i podgrađa njegova, i Avdon i podgrađa njegova,",
			"75": "<sup>75</sup> I Hukok i podgrađa njegova, i Reov i podgrađa njegova;",
			"76": "<sup>76</sup> A od plemena Neftalimova Kedes u Galileji i podgrađa njegova, i Amon i podgrađa njegova, i Kirijatajim i podgrađa njegova.",
			"77": "<sup>77</sup> Sinovima Merarijevim ostalijem daše od plemena Zavulonova Rimon i podgrađa njegova, Tavor i podgrađa njegova;",
			"78": "<sup>78</sup> A preko Jordana prema Jerihonu na istočnu stranu Jordana, od plemena Ruvimova Vosor u pustinji i podgrađa njegova, i Jasu i podgrađa njezina,",
			"79": "<sup>79</sup> I Kedimot i podgrađa njegova, i Mifat i podgrađa njegova;",
			"80": "<sup>80</sup> I od plemena Gadova Ramot u Galileji i podgrađa njegova, i Mahanaim i podgrađa njegova,",
			"81": "<sup>81</sup> I Esevon i podgrađa njegova, i Jazir i podgrađa njegova."
		},
		"7": {
			"1": "<sup>1</sup> A sinovi Isaharovi bijahu: Tolam i Fuja i Jasuf i Simron, četvorica.",
			"2": "<sup>2</sup> A sinovi Tolini: Ozije i Refaja i Jerilo i Jamaj i Jefsam i Samuilo, poglavari otačkih domova svojih od Tole, hrabri ljudi u porodicama svojim; bješe ih za vremena Davidova na broj dvadeset i dvije tisuće i šest stotina.",
			"3": "<sup>3</sup> A sinovi Ozijevi: Izraja, i sinovi Izrajini: Mihailo i Ovadija i Joilo i Jesija, skupa pet poglavara.",
			"4": "<sup>4</sup> I s njima u porodicama njihovijem po domovima otaca njihovijeh bješe vojnika trideset i šest tisuća, jer imahu mnogo žena i sinova.",
			"5": "<sup>5</sup> I braće njihove po svijem domovima Isaharovijem, hrabrijeh ljudi, bijaše osamdeset i sedam tisuća, svega izbrojenijeh.",
			"6": "<sup>6</sup> Sinovi Venijaminovi: Vela i Veher i Jediailo, trojica.",
			"7": "<sup>7</sup> A sinovi Velini: Esvon i Ozije i Ozilo i Jerimot i Irije, pet poglavara domova otačkih, hrabri ljudi; na broj ih bješe dvadeset i dvije tisuće i trideset i četiri.",
			"8": "<sup>8</sup> A sinovi Veherovi: Zemira i Joas i Elijezer i Elioinaj i Amrije i Jerimot i Avija i Anatot i Alamet, svi sinovi Veherovi.",
			"9": "<sup>9</sup> I izbrojenijeh po porodicama svojim, po poglavarima otačkoga doma svojega, bješe ih dvadeset tisuća i dvjesta hrabrijeh ljudi.",
			"10": "<sup>10</sup> A sinovi Jediailovi: Valan, i sinovi Valanovi: Jeus i Venijamin i Ehud i Hanana i Zitan i Tarsis i Ahisar.",
			"11": "<sup>11</sup> Svijeh ovijeh sinova Jediailovih po poglavarima porodica otačkih, hrabrijeh ljudi, bješe sedamdeset tisuća i dvjesta, koji iđahu na vojsku.",
			"12": "<sup>12</sup> I Sufeji i Upeji bijahu sinovi Irovi, i Useji sinovi Ahirovi.",
			"13": "<sup>13</sup> Sinovi Neftalimovi: Jasilo i Gunije i Jeser i Salum, sinovi Valini.",
			"14": "<sup>14</sup> Sinovi Manasijini: Azrilo, kojega mu žena rodi; inoča njegova Sirka rodi Mahira, oca Galadova;",
			"15": "<sup>15</sup> A Mahir se oženi od Upeja i Sufeja, a ime sestri njihovoj bješe Maha; a ime drugomu bješe Salpad; a Salpad imaše kćeri.",
			"16": "<sup>16</sup> A Maha žena Mahirova rodi sina, kojemu nadje ime Fares, a bratu mu nadje ime Seres, a njegovi sinovi bjehu Ulam i Rakem.",
			"17": "<sup>17</sup> I sinovi Ulamovi: Vedan. To su sinovi Galada sina Mahira sina Manasijina.",
			"18": "<sup>18</sup> A sestra njegova Amoleketa rodi Isuda i Avijezera i Malu.",
			"19": "<sup>19</sup> A sinovi Semidini bejahu Ahijan i Sihem i Lihija i Anijam.",
			"20": "<sup>20</sup> A sinovi Jefremovi: Sutala, a njegov sin Vered, a njegov sin Tahat, a njegov sin Eleada, a njegov sin Tahat,",
			"21": "<sup>21</sup> A njegov sin Zavad, a njegov sin Sutala i Eser i Elead. A njih ubiše ljudi iz Gata, rođeni u zemlji, jer siđoše da im uzmu stoku.",
			"22": "<sup>22</sup> Zato tužaše Jefrem otac njihov dugo vreme, i dođoše braća njegova da ga tješe.",
			"23": "<sup>23</sup> Potom leže sa ženom svojom, a ona zatrudnje i rodi sina, i on mu nadje ime Verija, jer nesreća zadesi dom njihov.",
			"24": "<sup>24</sup> I kći mu bješe Sera, koja sazida Vet-Oron donji i gornji i Uzen-Seru.",
			"25": "<sup>25</sup> I sin mu bješe Refa i Resef, a njegov sin bješe Tela, a njegov sin Tahan,",
			"26": "<sup>26</sup> A njegov sin Ladan, a njegov sin Amijud, a njegov sin Elisama,",
			"27": "<sup>27</sup> A njegov sin Non, a njegov sin Isus.",
			"28": "<sup>28</sup> A dostojanje njihovo i naselje bješe Vetilj i sela njegova, i s istoka Naran, a sa zapada Gezer i sela njegova, i Sihem i sela njegova do Gaze i sela njezinijeh.",
			"29": "<sup>29</sup> I pokraj sinova Manasijinih Vet-San i sela njegova, Tanah i sela njegova, Megidon i sela njegova, Dor i selova njegova; tu nastavahu sinovi Josifa sina Izrailjeva.",
			"30": "<sup>30</sup> Sinovi Asirovi bijahu Jemna i Jesva i Jesvaj i Verija, i Sera sestra njihova.",
			"31": "<sup>31</sup> I sinovi Verijini: Ever i Malhilo; ovaj je otac Virzavitov.",
			"32": "<sup>32</sup> A Ever rodi Jaflita i Somira i Hotama i Suju sestru njihovu.",
			"33": "<sup>33</sup> A sinovi Jaflitovi bijahu: Fasah i Vital i Asvat; to bijahu sinovi Jaflitovi.",
			"34": "<sup>34</sup> A sinovi Somirovi: Ahije i Roga, Jehuva i Aram.",
			"35": "<sup>35</sup> A sinovi Elema brata njegova: Sofa i Jemna i Selis i Amal.",
			"36": "<sup>36</sup> Sinovi Sofini: Suja i Arnefer i Sogal i Verije i Jemra,",
			"37": "<sup>37</sup> I Vosor i Od i Sama i Silisa i Itran i Veira.",
			"38": "<sup>38</sup> A sinovi Jeterovi: Jefonija i Fispa i Ara.",
			"39": "<sup>39</sup> I sinovi Ulini: Arah i Anilo i Risija.",
			"40": "<sup>40</sup> Svi ovi bijahu sinovi Asirovi, poglavari domova otačkih, izabrani, hrabri ljudi, poglavari među knezovima. Bješe ih za vojsku na broj dvadeset i šest tisuća ljudi."
		},
		"8": {
			"1": "<sup>1</sup> A Venijamin rodi Velu prvenca svojega, Asvila drugoga, i Aru trećega,",
			"2": "<sup>2</sup> Noja četvrtoga, i Rafu petoga.",
			"3": "<sup>3</sup> A sinovi Velini bijahu: Adar i Gira i Avijud,",
			"4": "<sup>4</sup> I Avisuja i Naman i Ahoja,",
			"5": "<sup>5</sup> I Gira i Sefuvan i Uram.",
			"6": "<sup>6</sup> A ovi bijahu sinovi Ehudovi, bijahu poglavari domova otačkih onima koji življahu u Gavaji, te ih preseliše u Manahat:",
			"7": "<sup>7</sup> Naman i Ahija i Gira, on ih preseli; i rodi Uzu i Ahijuda.",
			"8": "<sup>8</sup> A Sarajim, pošto ih posla, rodi sinove u zemlji Moavskoj s Usimom i Varom ženama svojim.",
			"9": "<sup>9</sup> Rodi s Odesom ženom svojom Jovava i Siviju i Misu i Malhama,",
			"10": "<sup>10</sup> I Jeusa i Sahiju i Mirmu; ti bijahu sinovi njegovi, poglavari domova otačkih.",
			"11": "<sup>11</sup> A s Usimom rodi Avitova i Elfala.",
			"12": "<sup>12</sup> I sinovi Elfalovi bjehu: Ever i Misam i Samed; on sazida Onon i Lod i sela njegova;",
			"13": "<sup>13</sup> I Verija i Sema, koji bijahu poglavari domova otačkih onima koji življahu u Ejalonu; on istjeraše stanovnike Gatske;",
			"14": "<sup>14</sup> A Ahio, Sasak i Jeremot,",
			"15": "<sup>15</sup> I Zevadija i Arad i Ader,",
			"16": "<sup>16</sup> I Mihailo i Jespa i Joha bjehu sinovi Verijini;",
			"17": "<sup>17</sup> A Zavadija i Mesulam i Ezekije i Ever,",
			"18": "<sup>18</sup> I Ismeraj i Jezlija i Jovav bjehu sinovi Elfalovi;",
			"19": "<sup>19</sup> A Jakim i Zihrije i Zavdije,",
			"20": "<sup>20</sup> I Elinaj i Ziltaj i Elilo,",
			"21": "<sup>21</sup> I Adaja i Veraja i Simrat bjehu sinovi Semini;",
			"22": "<sup>22</sup> A Jesvan i Ever i Elilo,",
			"23": "<sup>23</sup> I Avdon i Zihrije i Anan,",
			"24": "<sup>24</sup> I Ananija i Elam i Antonija,",
			"25": "<sup>25</sup> I Jefedija i Fanuilo bjehu sinovi Sasakovi;",
			"26": "<sup>26</sup> A Samseraj i Searija i Gotolija,",
			"27": "<sup>27</sup> I Jeresija i Ilija i Zihrije bjehu sinovi Jeroamovi.",
			"28": "<sup>28</sup> To bjehu poglavari domova otačkih po porodicama svojim, i nastavahu u Jerusalimu.",
			"29": "<sup>29</sup> A u Gavaonu nastavaše otac Gavaonu; a ženi mu bješe ime Maha.",
			"30": "<sup>30</sup> A sin prvenac njegov bješe Avdon, pa Sur i Kis i Val i Nadav,",
			"31": "<sup>31</sup> I Gedor i Ahijo i Zaher,",
			"32": "<sup>32</sup> I Miklot, koji rodi Simeju. I oni življahu prema braći svojoj u Jerusalimu s braćom svojom.",
			"33": "<sup>33</sup> A Nir rodi Kisa; a Kis rodi Saula; a Saul rodi Jonatana i Malhisuja i Avinadava i Esvala.",
			"34": "<sup>34</sup> A sin Jonatanov bješe Merival; a Merival rodi Mihu;",
			"35": "<sup>35</sup> A Mišini sinovi bjehu: Fiton i Meleh i Tareja i Ahaz.",
			"36": "<sup>36</sup> A Ahaz rodi Joadu; a Joada rodi Alemeta i Azmaveta i Zimrija. A Zimrije rodi Mosu;",
			"37": "<sup>37</sup> A Mosa rodi Vineju; a njegov sin bješe Rafa, a njegov sin Eleasa, a njegov sin Asilo.",
			"38": "<sup>38</sup> A Asilo imaše šest sinova, kojima su imena: Azrikam, Voheruj i Ismailo i Searija, i Ovadija i Anan. Ti svi bjehu sinovi Asilovi.",
			"39": "<sup>39</sup> A sinovi Iseka brata njegova: Ulam prvenac mu, Jeus drugi, i Elifelet treći.",
			"40": "<sup>40</sup> I bijahu sinovi Ulamovi junaci, koji natezahu luk, i imahu mnogo sinova i unuka, sto i pedeset. Svi ti bjehu od sinova Venijaminovijeh."
		},
		"9": {
			"1": "<sup>1</sup> I tako sav Izrailj bi izbrojen, i eto zapisani su u knjizi o carevima Izrailjevijem i Judinijem; i biše preseljeni u Vavilon za bezakonje svoje.",
			"2": "<sup>2</sup> A koji prije nastavahu na dostojanju svojem po gradovima svojim, Izrailjci, sveštenici, Leviti i Netineji,",
			"3": "<sup>3</sup> Nastavahu u Jerusalimu i od sinova Judinijeh i sinova Venijaminovijeh i od sinova Jefremovijeh i Manasijinih:",
			"4": "<sup>4</sup> Gutaj sin Amijuda sina Amrija, sina Imrija, sina Venija, od sinova Faresa sina Judina;",
			"5": "<sup>5</sup> I od sinova Silonovijeh: Asaja prvenac i sinovi njegovi;",
			"6": "<sup>6</sup> A od sinova Zerinijeh Jeuilo i braće njegove šest stotina i devedeset;",
			"7": "<sup>7</sup> A od sinova Venijaminovijeh Saluj sin Mesuama sina Oduje, sina Asenujina,",
			"8": "<sup>8</sup> I Jevnija sin Jeroamov, i Ila sin Ozija sina Mahrijeva, i Mesulam sin Sefatije sina Raguila, sina Ivnijina;",
			"9": "<sup>9</sup> I braće njihove po porodicama svojim devet stotina pedeset i šest; svi bjehu ljudi poglavari od porodica po domovima otaca svojih.",
			"10": "<sup>10</sup> A od sveštenika: Jedaja i Jojariv i Jahin,",
			"11": "<sup>11</sup> I Azarija sin Helkije sina Mesulama, sina Sadoka sina Merajota, sina Ahitovova, starješina u domu Gospodnjem,",
			"12": "<sup>12</sup> I Adaja sin Jeroama sina Pashora, sina Malhijina, i Masaj sin Adila sina Jazire, sina Mesulama, sina Mesilemota, sina Imirova;",
			"13": "<sup>13</sup> I braće njihove, poglavara otačkih domova svojih, tisuća i sedam stotina i šezdeset ljudi vrednijih na poslu u službi u domu Gospodnjem.",
			"14": "<sup>14</sup> A od Levita Semija sin Asuva, sina Azrikama, sina Asavijina, između sinova Merarijevih;",
			"15": "<sup>15</sup> I Vakvakar i Eres i Galal i Matanija, sin Mihe sina Zihrija, sina Asafova;",
			"16": "<sup>16</sup> I Ovadija sin Semeje sina Galala, sina Jedutunova, i Varahija sin Ase sina Elkanina, koji stanovaše u selima Netofatskim.",
			"17": "<sup>17</sup> I vratari: Salum i Akuv i Talmon i Ahiman, i braća njihova; a Salum bješe poglavar.",
			"18": "<sup>18</sup> On do sada bijaše na vratima carskim k istoku; to bijahu vratari po četama sinova Levijevih.",
			"19": "<sup>19</sup> A Salum sin Koreja sina Evijasafa, sina Korejeva, i braća njegova od doma oca njegova, sinovi Korejevi, u poslu službenom čuvahu pragove kod šatora, kao što oci njihovi u okolu Gospodnjem čuvahu ulazak;",
			"20": "<sup>20</sup> A nad njima bješe starješina Fines sin Eleazarov, i Gospod bješe s njim.",
			"21": "<sup>21</sup> Zaharija sin Meselemijin bijaše vratar šatora od sastanka.",
			"22": "<sup>22</sup> Svijeh ovijeh izabranijeh za vratare na pragovima bijaše dvjesta i dvanaest; biše popisani po selima svojim; David i Samuilo vidjelac postaviše ih radi vjernosti njihove,",
			"23": "<sup>23</sup> Da oni i sinovi njihovi čuvaju stražu na vratima doma Gospodnjega, doma od šatora.",
			"24": "<sup>24</sup> Na četiri strane bijahu vratari: na istoku, na zapadu, na jugu i na sjeveru.",
			"25": "<sup>25</sup> I braća njihova po selima svojim dolažahu svakih sedam dana za svoje vrijeme da su s njima.",
			"26": "<sup>26</sup> Jer u službi bijahu svagda četiri prva vratara, Levita, i bijahu postavljeni nad klijetima i nad riznicama doma Božijega.",
			"27": "<sup>27</sup> I oko doma Božijega noćivahu, jer na njima bijaše straža i dužni bijahu otvorati svako jutro.",
			"28": "<sup>28</sup> I neki od njih bijahu nad posuđem službenijem, jer ga na broj unošahu i na broj iznošahu.",
			"29": "<sup>29</sup> A neki od njih bijahu postavljeni nad drugim stvarima i nad svijem stvarima posvećenijem, nad brašnom i vinom i uljem i kadom i mirisima.",
			"30": "<sup>30</sup> A neki sinovi sveštenički gotovljahu mast od tijeh mirisa.",
			"31": "<sup>31</sup> A Matatija između Levita prvenac Salumov od porodice Korejeve, bijaše nad stvarima koje se peku u tavi.",
			"32": "<sup>32</sup> A između sinova Katovijeh, braće njihove, bijahu neki nad hljebom postavljenijem, gotoveći ga svake subote.",
			"33": "<sup>33</sup> Između njih bijahu i pjevači poglavari domova otačkih među Levitima, koji stanovahu po klijetima bez drugoga posla, jer dan i noć bijahu u svom poslu.",
			"34": "<sup>34</sup> To su poglavari domova otačkih među Levitima, po porodicama svojim, poglavari, i življahu u Jerusalimu.",
			"35": "<sup>35</sup> A u Gavaonu stanovaše Jehilo otac Gavaonu; a ime ženi njegovoj bješe Maha;",
			"36": "<sup>36</sup> A sin mu prvenac bješe Avdon, pa Sur i Kis i Val i Nir i Nadav,",
			"37": "<sup>37</sup> I Gedor, i Ahajo i Zaharija i Miklot;",
			"38": "<sup>38</sup> A Miklot rodi Simeama; i oni nastavahu prema braći svojoj u Jerusalimu s braćom svojom.",
			"39": "<sup>39</sup> A Nir rodi Kisa; a Kis rodi Saula; a Saul rodi Jonatana i Malhisuja i Avinadava i Esvala.",
			"40": "<sup>40</sup> I sin Jonatanov bješe Merival; a Merival rodi Mihu.",
			"41": "<sup>41</sup> A sinovi Mihini bjehu: Fiton i Meleh i Tareja.",
			"42": "<sup>42</sup> A Ahaz rodi Jaru; a Jara rodi Alemeta i Azmaveta i Zimrija; a Zimrije rodi Mosu;",
			"43": "<sup>43</sup> A Mosa rodi Vineju; a njegov sin bješe Refaja, a njegov sin Eleasa, a njegov sin Asilo.",
			"44": "<sup>44</sup> Asilo pak imaše šest sinova, kojima su imena: Azrikam, Voheruj i Ismailo i Searija i Ovadija i Anan. To su sinovi Asilovi."
		},
		"10": {
			"1": "<sup>1</sup> A Filisteji se pobiše s Izrailjem, i pobjegoše Izrailjci ispred Filisteja, i padahu mrtvi na gori Gelvuji.",
			"2": "<sup>2</sup> I stigoše Filisteji Saula i sinove njegove, i pogubiše Filisteji Jonatana i Avinadava i Malhisuja, sinove Saulove.",
			"3": "<sup>3</sup> I boj posta žešći oko Saula, i nađoše ga strijelci, i on se uplaši od strijelaca.",
			"4": "<sup>4</sup> I reče Saul momku koji mu nošaše oružje: izvadi mač svoj, i probodi me da ne dođu ti neobrezani i narugaju mi se. Ali ne htje momak koji mu nošaše oružje, jer ga bješe vrlo strah. Tada Saul uze mač i baci se na nj.",
			"5": "<sup>5</sup> A kad momak koji mu nošaše oružje vidje gdje umrije Saul, baci se i on na svoj mač, i umrije.",
			"6": "<sup>6</sup> Tako pogibe Saul; i tri sina njegova i sva čeljad njegova pogiboše zajedno.",
			"7": "<sup>7</sup> A kad svi Izrailjci koji bijahu u dolini vidješe gdje pobjegoše Izrailjci i gdje pogibe Saul i sinovi njegovi, ostaviše gradove svoje i pobjegoše te dođoše Filisteji i ostaše u njima.",
			"8": "<sup>8</sup> A sjutradan dođoše Filisteji da svlače mrtve, i nađoše Saula i sinove njegove gdje leže na gori Gelvuji.",
			"9": "<sup>9</sup> I svukavši ga uzeše glavu njegovu i oružje njegovo, i poslaše u zemlju Filistejsku na sve strane da se objavi kod lažnijeh bogova njihovijeh i po narodu.",
			"10": "<sup>10</sup> I ostaviše oružje njegovo u kući bogova svojih, a glavu mu objesiše u kući Dagonovoj.",
			"11": "<sup>11</sup> A kad čuše svi u Javisu Galadskom sve što učiniše Filisteji od Saula,",
			"12": "<sup>12</sup> Podigoše se svi junaci i uzeše tijelo Saulovo i tjelesa sinova njegovijeh, i donesoše ih u Javis, i pogreboše kosti njihove pod hrastom u Javisu, i postiše sedam dana.",
			"13": "<sup>13</sup> I tako pogibe Saul za bezakonje svoje, koje učini Gospodu što ne sluša riječi Gospodnje i što traži da pita duh vračarski,",
			"14": "<sup>14</sup> A ne pita Gospoda; zato ga ubi, i prenese carstvo na Davida sina Jesejeva."
		},
		"11": {
			"1": "<sup>1</sup> I sabraše se svi Izrailjci k Davidu u Hevron, i rekoše: evo, mi smo kost tvoja i tijelo tvoje.",
			"2": "<sup>2</sup> I pređe dok Saul bijaše car ti si odvodio i dovodio Izrailja; i Gospod Bog tvoj rekao ti je: ti ćeš pasti narod moj Izrailja; i ti ćeš biti vođ narodu mojemu Izrailju.",
			"3": "<sup>3</sup> Tako dođoše sve starješine Izrailjeve k caru u Hevron, i učini s njima David vjeru u Hevronu pred Gospodom, i pomazaše Davida za cara nad Izrailjem kao što bješe rekao Gospod preko Samuila.",
			"4": "<sup>4</sup> Potom otide David sa svijem Izrailjem na Jerusalim, a to je Jevus, jer ondje bijahu Jevuseji, koji življahu u onoj zemlji.",
			"5": "<sup>5</sup> I rekoše Jevušani Davidu: nećeš ući ovamo. Ali David uze kulu Sion, to je grad Davidov.",
			"6": "<sup>6</sup> Jer David reče: ko prvi nadbije Jevuseje, biće knez i vojvoda. I Joav sin Serujin izide prvi, i posta knez.",
			"7": "<sup>7</sup> Poslije seđaše David u tom gradu, zato ga prozvaše grad Davidov.",
			"8": "<sup>8</sup> I sazida grad unaokolo, od Milona unaokolo; a Joav opravi ostatak grada.",
			"9": "<sup>9</sup> I David jednako napredovaše i siljaše se, jer Gospod nad vojskama bješe s njim.",
			"10": "<sup>10</sup> A ovo su poglavice među junacima Davidovijem, koji junački radiše uza nj za carstvo njegovo sa svijem Izrailjem da bude car nad Izrailjem po riječi Gospodnjoj;",
			"11": "<sup>11</sup> I ovo je broj junaka Davidovijeh: Jasoveam sin Ahmonijev, prvi između trideset; on mahnu kopljem svojim na tri stotine, i pobi ih ujedanput.",
			"12": "<sup>12</sup> A za njim Eleazar sin Dodov Ahošanin, on bijaše jedan od tri junaka.",
			"13": "<sup>13</sup> On bijaše s Davidom u Fas-Damimu, kad se Filisteji skupiše na boj; i ondje bijaše njiva puna ječma, i narod pobježe od Filisteja,",
			"14": "<sup>14</sup> A oni stadoše usred njive, i odbraniše je pobivši Filisteje; i Gospod dade izbavljenje veliko.",
			"15": "<sup>15</sup> I ta tri prva između trideset sidoše ka stijeni k Davidu u pećinu Odalamsku, kad vojska Filistejska stajaše u okolu u dolini Rafajskoj.",
			"16": "<sup>16</sup> A David bijaše onda u gradu, a straža Filistejska bijaše tada u Vitlejemu.",
			"17": "<sup>17</sup> I David zaželje i reče: ko bi mi donio vode da pijem iz studenca Vitlejemskoga što je kod vrata?",
			"18": "<sup>18</sup> Tada ta trojica prodriješe kroz oko Filistejski, i zahvatiše vode iz studenca Vitlejemskoga koji je kod vrata, i donesoše i dadoše Davidu; a David ne htje piti, nego je izli Gospodu.",
			"19": "<sup>19</sup> I reče: ne dao mi Bog moj da to učinim! Eda li ću piti krv tijeh ljudi koji ne mariše za život svoj? Jer je donesoše ne mareći za život svoj. I ne hte je piti. To učiniše ta tri junaka.",
			"20": "<sup>20</sup> I Avisaj brat Joavov bješe prvi između trojice. I on mahnu kopljem svojim na tri stotine, i pobi ih, i proslavi se među trojicom;",
			"21": "<sup>21</sup> Među trojicom bješe slavniji od druge dvojice i bješe im poglavica; ali one trojice ne stiže.",
			"22": "<sup>22</sup> Venaja sin Jodajev, sin čovjeka junaka iz Kavseila, koji učini velika djela, on pogubi dva junaka Moavska, i sišav ubi lava u jami kad bješe snijeg.",
			"23": "<sup>23</sup> On ubi i nekoga Misirca visoka pet lakata. Imaše Misirac u ruci koplje kao vratilo, a on izide na nj sa štapom, i istrže Misircu koplje iz ruke, i ubi ga njegovijem kopljem.",
			"24": "<sup>24</sup> To učini Venaja sin Jodajev, i bi slavan među ova tri junaka.",
			"25": "<sup>25</sup> Bješe najslavniji između tridesetorice, ali one trojice ne stiže. I David ga postavi nad pratiocima svojim.",
			"26": "<sup>26</sup> Junaci između vojnika bijahu: Asailo brat Joavov, Elhanan sin Dodov iz Vitlejema,",
			"27": "<sup>27</sup> Samot Arorarin, Helis Felonjanin,",
			"28": "<sup>28</sup> Ira sin Ikisov iz Tekoje, Avijezer iz Anatota,",
			"29": "<sup>29</sup> Sivehaj iz Husata, Ilaj iz Ahoha,",
			"30": "<sup>30</sup> Maraj iz Netofata, Heled sin Vanin iz Netofata,",
			"31": "<sup>31</sup> Itaj sin Rivajev iz Gavaje sinova Venijaminovih, Venaja iz Faratona,",
			"32": "<sup>32</sup> Uraj od potoka Gaskih, Avilo iz Arvata,",
			"33": "<sup>33</sup> Azmavet iz Varuma, Elijava iz Salvona,",
			"34": "<sup>34</sup> Sinovi Asima Gizonjanina, Jonatan sin Sagijin Araranin,",
			"35": "<sup>35</sup> Ahijam sin Saharov Araranin, Elifar sin Urov,",
			"36": "<sup>36</sup> Efer iz Mehirata, Ahija iz Felona,",
			"37": "<sup>37</sup> Esro Karmilac, Narav sin Esvajev,",
			"38": "<sup>38</sup> Joilo brat Natanov, Mivar sin Agirijev,",
			"39": "<sup>39</sup> Selek Amonac, Naraj Viroćanin, koji nošaše oružje Joavu sinu Serujinu,",
			"40": "<sup>40</sup> Ira Jetranin, Gariv Jetranin,",
			"41": "<sup>41</sup> Urija Hetejin, Zavad sin Alajev,",
			"42": "<sup>42</sup> Adina sin Sizin od sinova Ruvimovijeh, poglavar sinova Ruvimovijeh, i trideset s njim,",
			"43": "<sup>43</sup> Anan sin Mašin, i Josafat iz Mitne,",
			"44": "<sup>44</sup> Ozija iz Asterota, Sama i Jehilo sinovi Hotana Aroiranina,",
			"45": "<sup>45</sup> Jediailo sin Simrijev i Joha brat mu iz Tise,",
			"46": "<sup>46</sup> Elilo Mavljanin, i Jerivaj i Josavija sinovi Elnamovi, i Jetema Moavac,",
			"47": "<sup>47</sup> Elilo i Ovid i Jasilo iz Mesovaje."
		},
		"12": {
			"1": "<sup>1</sup> A ovo su koji dođoše Davidu u Siklag dok se još krijaše od Saula sina Kisova, i bijahu među junacima pomažući u ratu,",
			"2": "<sup>2</sup> Naoružani lukom, iz desne ruke i iz lijeve gađahu kamenjem i iz luka strijelama, između braće Saulove, od plemena Venijaminova:",
			"3": "<sup>3</sup> Poglavar Ahijezer i Joas sinovi Semaje iz Gavaje, Jezilo i Felet sinovi Azmavetovi, i Veraha i Juj iz Anatota,",
			"4": "<sup>4</sup> I Ismaja Gavaonjanin, junak među tridesetoricom i nad tridesetoricom, i Jeremija i Jazilo i Joanan i Jozavad od Gedirota,",
			"5": "<sup>5</sup> Eluzaj i Jerimot i Valija i Semarija i Sefatija od Arufa,",
			"6": "<sup>6</sup> Elkana i Jesija i Azareilo i Joezer i Jasoveam Korijani,",
			"7": "<sup>7</sup> I Joila i Zevadija sinovi Jeroamovi iz Gedora.",
			"8": "<sup>8</sup> I od plemena Gadova prebjegoše Davidu u grad u pustinju hrabri junaci, vješti boju, naoružani štitom i kopljem, kojima lice bijaše kao lice lavovsko i bijahu brzi kao srne po gorama:",
			"9": "<sup>9</sup> Eser prvi, Ovadija drugi, Elijav treći,",
			"10": "<sup>10</sup> Mismana četvrti, Jeremija peti,",
			"11": "<sup>11</sup> Ataj šesti, Elilo sedmi,",
			"12": "<sup>12</sup> Joanan osmi, Elzaval deveti,",
			"13": "<sup>13</sup> Jeremija deseti, Mohvanaj jedanaesti.",
			"14": "<sup>14</sup> To bijahu između sinova Gadovijeh poglavari u vojsci, najmanji nad stotinom a najveći nad tisućom.",
			"15": "<sup>15</sup> Oni prijeđoše preko Jordana prvoga mjeseca kad se razli preko svijeh bregova svojih, i otjeraše sve iz dolina na istok i na zapad.",
			"16": "<sup>16</sup> A dođoše i od sinova Venijaminovijeh i Judinijeh k Davidu u grad.",
			"17": "<sup>17</sup> I izide im David na susret, i govoreći reče im: ako mira radi idete k meni, da mi pomožete, srce će se moje združiti s vama; ako li ste došli da me izdate neprijateljima mojim, nepravde nema na meni, neka vidi Gospod otaca naših i neka sudi.",
			"18": "<sup>18</sup> Tada duh dođe na Amasaja poglavara među vojvodama, te reče: tvoji smo, Davide, i s tobom ćemo biti, sine Jesejev; mir, mir tebi i pomagačima tvojim, jer ti pomaže Bog tvoj. Tako ih primi David, i postavi ih među poglavare nad četama.",
			"19": "<sup>19</sup> I od sinova Manasijinih neki prebjegoše Davidu kad iđaše s Filistejima na Saula u boj, ali im ne pomogoše; jer knezovi Filistejski dogovorivši se vratiše ga govoreći: na pogibao našu prebjegnuće gospodaru svojemu Saulu.",
			"20": "<sup>20</sup> Kad se vraćaše u Siklag, prebjegoše k njemu iz plemena Manasijina: Adna i Jozavad i Jediailo i Mihailo i Jozavad i Eliuj i Saltaj, tisućnici u plemenu Manasijinu.",
			"21": "<sup>21</sup> I oni pomagahu Davidu protivu četi, jer hrabri junaci bjehu svi, te postaše vojvode u njegovoj vojsci.",
			"22": "<sup>22</sup> Jer svaki dan dolažahu k Davidu u pomoć, dokle posta velika vojska kao vojska Božija.",
			"23": "<sup>23</sup> A ovo je broj ljudi naoružanijeh za vojsku koji dođoše k Davidu u Hevron da prenesu carstvo Saulovo na nj po riječi Gospodnjoj:",
			"24": "<sup>24</sup> Sinova Judinijeh koji nošahu štit i koplje šest tisuća i osam stotina naoružanijeh za vojsku;",
			"25": "<sup>25</sup> Sinova Simeunovijeh hrabrijeh vojnika sedam tisuća i sto;",
			"26": "<sup>26</sup> Sinova Levijevih četiri tisuće i šest stotina;",
			"27": "<sup>27</sup> I Jodaj poglavar između sinova Aronovijeh i s njim tri tisuće i sedam stotina;",
			"28": "<sup>28</sup> I Sadok mladić, hrabar junak, i od doma oca njegova dvadeset i dva kneza;",
			"29": "<sup>29</sup> I sinova Venijaminovih, braće Saulove, tri tisuće; jer ih se mnogi još držahu doma Saulova;",
			"30": "<sup>30</sup> I sinova Jefremovijeh dvadeset tisuća i osam stotina hrabrijeh junaka, ljudi na glasu u porodicama otaca svojih;",
			"31": "<sup>31</sup> A od polovine plemena Manasijina osamnaest tisuća, koji biše imenovani poimence da dođu da postave Davida carem;",
			"32": "<sup>32</sup> I sinova Isaharovijeh, koji dobro razumijevahu vremena da bi znali šta će činiti Izrailj, knezova njihovijeh dvjesta, i sva braća njihova slušahu ih;",
			"33": "<sup>33</sup> Sinova Zavulonovijeh, koji iđahu na vojsku i naoružani bijahu za boj svakojakim oružjem, pedeset tisuća, koji se postavljahu u vrste pouzdana srca;",
			"34": "<sup>34</sup> A od plemena Neftalimova tisuća poglavara i s njima trideset i sedam tisuća sa štitovima i kopljima;",
			"35": "<sup>35</sup> A od plemena Danova dvadeset i osam tisuća i šest stotina naoružanijeh za boj;",
			"36": "<sup>36</sup> A od plemena Asirova četrdeset tisuća vojnika vještijeh postaviti se za boj;",
			"37": "<sup>37</sup> A onijeh ispreko Jordana od plemena Ruvimova i Gadova i od polovine plemena Manasijina, sto i dvadeset tisuća sa svakojakim oružjem ubojitijem.",
			"38": "<sup>38</sup> Svi ovi vojnici u vojničkom redu dođoše cijela srca u Hevron da postave Davida carem nad svijem Izrailjem. A i ostali svi Izrailjci bijahu složni da carem postave Davida.",
			"39": "<sup>39</sup> I bijahu ondje s Davidom tri dana jedući i pijući, jer im braća bijahu pripravila.",
			"40": "<sup>40</sup> A i oni koji bijahu blizu njih, dori do Isahara i Zavulona i Neftalima, donošahu hljeba na magarcima i na kamilama i na mazgama i na volovima, jela, brašna, smokava i suhoga grožđa i vina i ulja, volova, ovaca izobila; jer bijaše radost u Izrailju."
		},
		"13": {
			"1": "<sup>1</sup> I David učini vijeće s tisućnicima i sa stotinicima i sa svijem vojvodama,",
			"2": "<sup>2</sup> I reče David svemu zboru Izrailjevu: ako vam je po volji, i ako je od Gospoda Boga našega, da pošaljemo na sve strane k braći svojoj ostaloj po svijem krajevima Izrailjevijem, i k sveštenicima i Levitima po gradovima i podgrađima njihovijem da se skupe k nama,",
			"3": "<sup>3</sup> Pa da donesemo k sebi kovčeg Boga svojega, jer ga ne tražismo za vremena Saulova.",
			"4": "<sup>4</sup> A sav zbor reče da se tako učini, jer to bi po volji svemu narodu.",
			"5": "<sup>5</sup> I tako David sabra sav narod Izrailjev od Sihora Misirskoga dori do Emata, da donesu kovčeg Božiji iz Kirijat-Jarima.",
			"6": "<sup>6</sup> I otide David sa svijem Izrailjem u Valu, u Kirijat-Jarim Judin, da prenesu odande kovčeg Boga Gospoda, koji sjedi na heruvimima, čije se ime prizivlje.",
			"7": "<sup>7</sup> I povezoše kovčeg Božiji na novijem kolima iz kuće Avinadavove; a Uza i Ahijo upravljahu kolima.",
			"8": "<sup>8</sup> A David i sav narod Izrailjev igrahu pred Bogom iz sve snage pjevajući i udarajući u gusle i u psaltire i u bubnje i u kimvale i trubeći u trube.",
			"9": "<sup>9</sup> A kad dođoše do gumna Hidonova, Uza se maši rukom da prihvati kovčeg, jer volovi potegoše na stranu.",
			"10": "<sup>10</sup> I Gospod se razgnjevi na Uzu, i udari ga što se maši rukom za kovčeg, te umrije ondje pred Bogom.",
			"11": "<sup>11</sup> I ožalosti se David što Gospod ubi Uzu: zato se prozva ono mjesto Fares-Uza do danas.",
			"12": "<sup>12</sup> I uplaši se David od Boga u onaj dan, i reče: kako ću donijeti k sebi kovčeg Božji?",
			"13": "<sup>13</sup> I ne prenese David kovčega k sebi u grad Davidov, nego ga skloni u kuću Ovid-Edoma Getejina.",
			"14": "<sup>14</sup> I osta kovčeg Božji kod porodice Ovid-Edomove u kući njegovoj tri mjeseca. I Gospod blagoslovi dom Ovid-Edomov i sve što imaše."
		},
		"14": {
			"1": "<sup>1</sup> A Hiram car Tirski posla poslanike k Davidu i drva kedrovijeh i zidara i drvodjelja da mu sagrade kuću.",
			"2": "<sup>2</sup> I razumje David da ga je Gospod utvrdio za cara nad Izrailjem i da se carstvo njegovo podiže visoko radi naroda njegova Izrailja.",
			"3": "<sup>3</sup> I David uze jošte žena u Jerusalimu i izrodi još sinova i kćeri.",
			"4": "<sup>4</sup> I ovo su imena onijeh koji mu se rodiše u Jerusalimu: Samuja i Sovav, Natan i Solomun,",
			"5": "<sup>5</sup> I Jevar i Elisuja i Elfalet,",
			"6": "<sup>6</sup> I Noga i Nefeg i Jafija,",
			"7": "<sup>7</sup> I Elisama i Velijada i Elifalet.",
			"8": "<sup>8</sup> A Filisteji čuvši da je David pomazan za cara nad svijem Izrailjem, izidoše svi Filisteji da traže Davida; a David čuvši to izide pred njih.",
			"9": "<sup>9</sup> I Filisteji došavši raširiše se po dolini Rafajskoj.",
			"10": "<sup>10</sup> Tada David upita Gospoda govoreći: hoću li izaći na Filisteje? I hoćeš li ih dati u moje ruke? A Gospod mu reče: izađi, i daću ih u tvoje ruke.",
			"11": "<sup>11</sup> Tada otidoše u Val-Ferasim, i pobi ih ondje David, i reče David: prodrije Bog neprijatelje moje mojom rukom, kao što voda prodire. Otuda se prozva mjesto Val-Ferasim.",
			"12": "<sup>12</sup> I ostaviše ondje bogove svoje; a David zapovjedi, te ih spališe ognjem.",
			"13": "<sup>13</sup> A Filisteji opet po drugi put raširiše se po onom dolu.",
			"14": "<sup>14</sup> I David opet upita Boga, a Bog mu reče: ne idi za njima, nego se vrati od njih, pa udari na njih prema dudovima.",
			"15": "<sup>15</sup> I kad čuješ da zašušti po vrhovima od dudova, tada izidi u boj; jer će poći Bog pred tobom da pobije vojsku Filistejsku.",
			"16": "<sup>16</sup> I učini David kako mu zapovjedi Bog i pobi vojsku Filistejsku od Gavaona do Gezera.",
			"17": "<sup>17</sup> I razglasi se ime Davidovo po svijem zemljama; i Gospod zadade strah od njega svijem narodima."
		},
		"15": {
			"1": "<sup>1</sup> I načini sebi David kuće u gradu Davidovu, i spremi mjesto za kovčeg Božji, i razape mu šator.",
			"2": "<sup>2</sup> Tada reče David: ne valja da nosi kovčeg Božji niko osim Levita, jer je njih izabrao Gospod da nose kovčeg Božji i da mu služe dovijeka.",
			"3": "<sup>3</sup> I skupi David sve sinove Izrailjeve u Jerusalim da prenesu kovčeg Gospodnji na mjesto njegovo, koje mu bješe spremio.",
			"4": "<sup>4</sup> Skupi David i sinove Aronove i Levite,",
			"5": "<sup>5</sup> Od sinova Katovijeh: Urila poglavara i braće njegove sto i dvadeset;",
			"6": "<sup>6</sup> Od sinova Merarijevih: Asaju poglavara i braće njegove dvjesta i dvadeset;",
			"7": "<sup>7</sup> Od sinova Girsonovijeh: Joila poglavara i braće njegove sto i trideset;",
			"8": "<sup>8</sup> Od sinova Elisafanovih: Semaju poglavara i braće njegove dvjesta;",
			"9": "<sup>9</sup> Od sinova Hevronovijeh: Elila poglavara i braće njegove osamdeset;",
			"10": "<sup>10</sup> Od sinova Ozilovijeh: Aminadava poglavara i braće njegove sto i dvanaest.",
			"11": "<sup>11</sup> Tada dozva David Sadoka i Avijatara sveštenike, i Levite i Urila i Asaju i Joila i Samaju i Elila i Aminadava,",
			"12": "<sup>12</sup> I reče im: vi ste poglavari porodica otačkih među Levitima; osveštajte sebe i braću svoju da donesete kovčeg Gospoda Boga Izrailjeva na mjesto koje sam mu spremio.",
			"13": "<sup>13</sup> Jer što pređe ne učiniste toga, Gospod Bog naš prodrije nas, jer ga ne tražismo kako treba.",
			"14": "<sup>14</sup> I osveštaše se sveštenici i Leviti da prenesu kovčeg Gospoda Boga Izrailjeva.",
			"15": "<sup>15</sup> I nosiše sinovi Levitski kovčeg Božiji kao što je zapovjedio Mojsije po riječi Gospodnjoj na ramenima svojim i polugama.",
			"16": "<sup>16</sup> I reče David poglavarima Levitskim da postave između braće svoje pjevače sa spravama muzickim, sa psaltirima i guslama i kimvalima, da pjevaju u glas veselo.",
			"17": "<sup>17</sup> I postaviše Leviti Emana sina Joilova, i od braće njegove Asafa sina Varahijina, i od sinova Merarijevih, braće njihove, Etana sina Kisajina;",
			"18": "<sup>18</sup> I s njima braću njihovu drugoga reda: Zahariju i Vena i Jazila i Semiramota i Jehila i Unija i Elijava i Venaju i Masiju i Matatiju i Elifela i Mikneju i Ovid-Edoma i Jeila, vratare.",
			"19": "<sup>19</sup> I pjevači Eman i Asaf i Etan udarahu u kimvale mjedene;",
			"20": "<sup>20</sup> A Zaharija i Ozilo i Semiramot i Jehilo i Unije i Elijav i Masija i Venaja u psaltire visoko,",
			"21": "<sup>21</sup> A Matatija i Elifel i Mikneja, i Ovid-Edom, i Jeilo i Azazija u gusle nisko.",
			"22": "<sup>22</sup> A Henanija poglavar među Levitima koji nošahu kovčeg uređivaše kako će se nositi, jer bješe vješt.",
			"23": "<sup>23</sup> A Varahija i Elkana bjehu vratari kod kovčega.",
			"24": "<sup>24</sup> A Sevanija i Josafat i Natanailo i Amasaj i Zaharija i Venaja i Elijezer, sveštenici, trubljahu u trube pred kovčegom Božjim; a Ovid-Edom i Jehija bjehu vratari kod kovčega.",
			"25": "<sup>25</sup> I tako David i starješine Izrailjeve i tisućnici iđahu prateći kovčeg zavjeta Gospodnjega iz kuće Ovid-Edomove s veseljem.",
			"26": "<sup>26</sup> I kad Bog pomože Levitima koji nošahu kovčeg zavjeta Gospodnjega prinesoše sedam volova i sedam ovnova.",
			"27": "<sup>27</sup> I David bijaše ogrnut plaštem od tankoga platna, tako i svi Leviti koji nošahu kovčeg i pjevači, Henanija koji upravljaše nosiocima među pjevačima. I David imaše na sebi oplećak lanen.",
			"28": "<sup>28</sup> I tako sav narod Izrailjev praćaše kovčeg zavjeta Gospodnjega klikujući i trubeći u trube i u rogove i udarajući u kimvale i u psaltire i u gusle.",
			"29": "<sup>29</sup> A kad kovčeg zavjeta Gospodnjega ulažaše u grad Davidov, Mihala kći Saulova gledajući s prozora vide cara Davida gdje skače i igra, i podrugnu mu se u srcu svom."
		},
		"16": {
			"1": "<sup>1</sup> I kad donesoše kovčeg Božji, namjestiše ga usred šatora, koji mu razape David; i prinesoše žrtve paljenice i žrtve zahvalne pred Bogom.",
			"2": "<sup>2</sup> Potom prinesavši David žrtve paljenice i žrtve zahvalne, blagoslovi narod u ime Gospodnje.",
			"3": "<sup>3</sup> I razdade svijem Izrailjcima, i ljudima i ženama, svakom po jedan hljeb i komad mesa i vrč vina.",
			"4": "<sup>4</sup> Potom postavi pred kovčegom Gospodnjim sluge između Levita da pominju i slave i hvale Gospoda Boga Izrailjeva:",
			"5": "<sup>5</sup> Asafa poglavara, a drugoga za njim Zahariju, i Jeila i Semiramota i Jehila i Matatiju i Elijava i Venaju i Ovid-Edoma; i Jeilo udaraše u psaltire i gusle, a Asaf u kimvale,",
			"6": "<sup>6</sup> A Venaja i Jazilo sveštenici bijahu jednako s trubama pred kovčegom zavjeta Gospodnjega.",
			"7": "<sup>7</sup> U taj dan naredi David prvi put da hvale Gospoda Asaf i braća njegova:",
			"8": "<sup>8</sup> Hvalite Gospoda; glasite ime njegovo; javljajte po narodima djela njegova.",
			"9": "<sup>9</sup> Pjevajte mu, slavite ga, kazujte sva čudesa njegova.",
			"10": "<sup>10</sup> Hvalite se svetijem imenom njegovijem; neka se veseli srce onijeh koji traže Gospoda.",
			"11": "<sup>11</sup> Tražite Gospoda i silu njegovu; tražite lice njegovo bez prestanka.",
			"12": "<sup>12</sup> Pamtite čudesa njegova, koja je učinio, znake njegove i sudove usta njegovijeh.",
			"13": "<sup>13</sup> Sjeme Izrailjevo sluge su njegove, sinovi Jakovljevi izbrani njegovi.",
			"14": "<sup>14</sup> On je Gospod Bog naš, po svoj su zemlji sudovi njegovi.",
			"15": "<sup>15</sup> Pamtite uvijek zavjet njegov, riječ koju je dao na tisuću koljena.",
			"16": "<sup>16</sup> Što je zavjetovao Avramu i zašto se kleo Isaku,",
			"17": "<sup>17</sup> To je postavio Jakovu za zakon i Izrailju za zavjet vječni,",
			"18": "<sup>18</sup> Govoreći: tebi ću dati zemlju Hanansku u našljedni dio.",
			"19": "<sup>19</sup> Tada vas bijaše malo na broj, bijaše vas malo, i bijaste došljaci.",
			"20": "<sup>20</sup> Iđahu od naroda do naroda, i iz jednoga carstva k drugomu plemenu.",
			"21": "<sup>21</sup> Ne dade nikomu da im naudi, i karaše za njih careve:",
			"22": "<sup>22</sup> Ne dirajte u pomazanike moje, i prorocima mojim ne činite zla.",
			"23": "<sup>23</sup> Pjevaj Gospodu, sva zemljo! javljajte od dana na dan spasenje njegovo.",
			"24": "<sup>24</sup> Kazujte po narodima slavu njegovu, po svijem plemenima čudesa njegova.",
			"25": "<sup>25</sup> Jer je velik Gospod i valja ga hvaliti veoma; strašniji je od svijeh bogova.",
			"26": "<sup>26</sup> Jer su svi bogovi u naroda ništa; a Gospod je nebesa stvorio.",
			"27": "<sup>27</sup> Slava je i veličanstvo pred njim, sila i radost u stanu njegovu.",
			"28": "<sup>28</sup> Dajte Gospodu, plemena narodna, dajte Gospodu slavu i čast.",
			"29": "<sup>29</sup> Dajte Gospodu slavu prema imenu njegovu, nosite dare i idite preda nj, poklonite se Gospodu i svetoj krasoti.",
			"30": "<sup>30</sup> Strepi pred njim, sva zemljo; zato je vasiljena tvrda i neće se pomjestiti.",
			"31": "<sup>31</sup> Nek se vesele nebesa i zemlja se raduje; i neka govore po narodima: Gospod caruje.",
			"32": "<sup>32</sup> Neka pljeska more i što je u njemu; neka skače polje i sve što je na njemu.",
			"33": "<sup>33</sup> Tada neka se raduju drveta šumska pred Gospodom, jer ide da sudi zemlji.",
			"34": "<sup>34</sup> Hvalite Gospoda, jer je dobar, jer je dovijeka milost njegova.",
			"35": "<sup>35</sup> I recite: spasi nas, Bože spasenja našega, i skupi nas i izbavi nas od naroda da slavimo sveto ime tvoje, da se hvalimo tvojom slavom.",
			"36": "<sup>36</sup> Blagosloven Gospod Bog Izrailjev od vijeka i do vijeka. Tada sav narod reče: amin; i hvališe Gospoda.",
			"37": "<sup>37</sup> I ostavi ondje pred kovčegom zavjeta Gospodnjega Asafa i braću njegovu da služe pred kovčegom bez prestanka kao što treba od dana na dan,",
			"38": "<sup>38</sup> I Ovid-Edoma i braću njegovu, šezdeset i osam, Ovid-Edoma sina Jedutunova i Osu, da budu vratari;",
			"39": "<sup>39</sup> A Sadoka sveštenika i braću njegovu sveštenike pred šatorom Gospodnjim na visini u Gavaonu,",
			"40": "<sup>40</sup> Da prinose žrtve paljenice Gospodu na oltaru za žrtve paljenice bez prestanka jutrom i večerom, i da čine sve što je napisano u zakonu Gospodnjem što je zapovjedio Izrailju,",
			"41": "<sup>41</sup> I s njima Emana i Jedutuna i druge izabrane, koji biše poimenice imenovani da hvale Gospoda, jer je dovijeka milost njegova,",
			"42": "<sup>42</sup> S njima Emana i Jedutuna, da trube u trube i udaraju u kimvale i u druge sprave muzičke Bogu; a sinove Jedutunove da budu vratari.",
			"43": "<sup>43</sup> Potom se razide sav narod, svak svojoj kući, a David se vrati da blagoslovi dom svoj."
		},
		"17": {
			"1": "<sup>1</sup> A kad David sjeđaše kod kuće svoje, reče David Natanu proroku: gle, ja stojim u kući kedrova drveta, a kovčeg zavjeta Gospodnjega pod zavjesima.",
			"2": "<sup>2</sup> A Natan reče Davidu: što ti je god u srcu, čini, jer je Bog s tobom.",
			"3": "<sup>3</sup> Ali onu noć dođe riječ Božija Natanu govoreći:",
			"4": "<sup>4</sup> Idi i reci Davidu sluzi mojemu: ovako veli Gospod: ti mi nećeš sazidati kuće da u njoj nastavam.",
			"5": "<sup>5</sup> Kad nijesam nastavao u kući otkad izvedoh Izrailja do danas, nego sam išao od šatora do šatora i od naslona do naslona,",
			"6": "<sup>6</sup> Kuda sam god hodio sa svijem Izrailjem, jesam li jednu riječ rekao kome između sudija Izrailjevijeh kojima zapovijedah da pasu narod moj, i kazao: zašto mi ne načinite kuću od kedra?",
			"7": "<sup>7</sup> Ovako dakle reci sluzi mojemu Davidu: ovako veli Gospod nad vojskama: ja te uzeh od tora, od ovaca, da budeš vođ narodu mojemu Izrailju.",
			"8": "<sup>8</sup> I bjeh s tobom kuda si god išao, i istrijebih sve neprijatelje tvoje ispred tebe, i stekoh ti ime kao što je ime velikih ljudi na zemlji.",
			"9": "<sup>9</sup> I odrediću mjesto narodu svojemu Izrailju, i posadiću ga, te će nastavati u svom mjestu, i neće se više pretresati; niti će im više dosađivati nepravednici kao prije,",
			"10": "<sup>10</sup> I od onoga dana kada sam postavio sudije nad narodom svojim Izrailjem; i pokorih sve neprijatelje tvoje; nego ti javljam da će ti Gospod sazidati kuću.",
			"11": "<sup>11</sup> I kad se navrše dani tvoji da otideš k ocima svojim, podignuću sjeme tvoje nakon tebe, koje će biti između sinova tvojih, i utvrdiću carstvo njegovo.",
			"12": "<sup>12</sup> Oni će mi sazidati dom, i utvrdiću prijesto njegov dovijeka.",
			"13": "<sup>13</sup> Ja ću mu biti otac, i on će mi biti sin; a milosti svoje neću ukloniti od njega kao što sam uklonio od onoga koji bješe prije tebe.",
			"14": "<sup>14</sup> Nego ću ga utvrditi u domu svom i u carstvu svom dovijeka, i prijesto će njegov stajati dovijeka.",
			"15": "<sup>15</sup> Po svijem ovijem riječima i po svoj ovoj utvari kaza Natan Davidu.",
			"16": "<sup>16</sup> Tada dođe car David i stade pred Gospodom, i reče: ko sam ja, Gospode Bože, i šta je dom moj, te si me doveo dovde?",
			"17": "<sup>17</sup> I to ti se čini malo, Bože, nego si govorio i za dom sluge svojega na dugo vremena, i postarao si se za tu slavu zakonom čovječijim, Gospode Bože.",
			"18": "<sup>18</sup> Šta će još David da ti govori o časti sluge tvojega, kad ti znaš slugu svojega?",
			"19": "<sup>19</sup> Gospode, sluge svojega radi i po srcu svojemu činiš svu ovu veliku stvar, obznanjujući sve ove velike stvari.",
			"20": "<sup>20</sup> Gospode, nema takvoga kakav si ti, i nema Boga osim tebe, po svemu što čusmo svojim ušima.",
			"21": "<sup>21</sup> Jer koji je narod na zemlji kao tvoj narod Izrailj? radi kojega je Bog išao da ga iskupi da mu bude narod, i da steče sebi ime velikim i strašnim djelima izgoneći narode ispred naroda svojega, koji si iskupio iz Misira",
			"22": "<sup>22</sup> Jer si učinio narod svoj Izrailja svojim narodom dovijeka; a ti si im, Gospode, Bog.",
			"23": "<sup>23</sup> I tako, Gospode, riječ koju si obrekao sluzi svojemu i domu njegovu neka bude tvrda dovijeka, i učini kako si rekao.",
			"24": "<sup>24</sup> Neka bude tvrda, da se veliča ime tvoje dovijeka i da se govori: Gospod nad vojskama, Bog Izrailjev jest Bog nad Izrailjem, i dom Davida sluge tvojega neka stoji tvrdo pred tobom.",
			"25": "<sup>25</sup> Jer si ti, Bože moj, javio sluzi svojemu da ćeš mu sazidati dom, zato se sluga tvoj usudi da ti se pomoli.",
			"26": "<sup>26</sup> Tako Gospode, ti si Bog, i obrekao si sluzi svojemu to dobro.",
			"27": "<sup>27</sup> Budi dakle voljan i blagoslovi dom sluge svojega da bude dovijeka pred tobom; jer kad ti, Gospode, blagosloviš, biće blagosloven dovijeka."
		},
		"18": {
			"1": "<sup>1</sup> A poslije toga pobi David Filisteje i pokori ih, i uze Gat i sela njegova iz ruku Filistejskih.",
			"2": "<sup>2</sup> Pobi i Moavce, i postaše Moavci sluge Davidove i plaćahu mu danak.",
			"3": "<sup>3</sup> Razbi David i Adad-Ezera cara Sovskoga u Ematu izašav da raširi vlast svoju do rijeke Efrata.",
			"4": "<sup>4</sup> I uze mu David tisuću kola i sedam tisuća konjika i dvadeset tisuća pješaka, i podreza David žile svijem konjima kolskim, samo ostavi za sto kola.",
			"5": "<sup>5</sup> A bijahu došli Sirci iz Damaska u pomoć Adad-Ezeru caru Sovskomu, i David pobi dvadeset i dvije tisuće Siraca.",
			"6": "<sup>6</sup> I namjesti David vojsku u Siriji što je pod Damaskom, i Sirci postaše sluge Davidove plaćajući mu danak. I Gospod čuvaše Davida kuda god iđaše.",
			"7": "<sup>7</sup> I David uze zlatne štitove koje imahu sluge Adad-Ezerove, i donese ih u Jerusalim.",
			"8": "<sup>8</sup> I iz Tivata i iz Huna gradova Adad-Ezerovijeh odnese David silnu mjed, od koje Solomun načini more mjedeno i stupove i posuđe mjedeno.",
			"9": "<sup>9</sup> A kad ču Toja car Ematski da je David pobio svu vojsku Adad-Ezera cara Sovskoga,",
			"10": "<sup>10</sup> Posla Adorama sina svojega k caru Davidu da ga pozdravi i da mu čestita što je vojevao na Adad-Ezera i ubio ga, jer Toja imaše rat sa Adad-Ezerom i svakojakih zaklada zlatnijeh i srebrnijeh i mjedenijeh.",
			"11": "<sup>11</sup> Pa i to car David posveti sa srebrom i zlatom što bješe uzeo od svijeh naroda, od Edomaca i od Moavaca i od sinova Amonovijeh i od Filisteja i od Amalika.",
			"12": "<sup>12</sup> I Avisaj sin Serujin pobi osamnaest tisuća Idumejaca u slanoj dolini.",
			"13": "<sup>13</sup> I namjesti vojsku po Idumeji, i svi Idumejci postaše sluge Davidove. I Gospod čuvaše Davida kuda god iđaše.",
			"14": "<sup>14</sup> Tako carova David nad svijem Izrailjem sudeći i dajući pravdu svemu narodu svojemu.",
			"15": "<sup>15</sup> I Joav sin Serujin bješe nad vojskom, a Josafat sin Ahiludov pametar,",
			"16": "<sup>16</sup> A Sadok sin Ahitovov i Avimeleh sin Avijatarov sveštenici, a Susa pisar,",
			"17": "<sup>17</sup> A Venaja sin Jodajev bješe nad Heretejima i Feletejima, a sinovi Davidovi prvi do cara."
		},
		"19": {
			"1": "<sup>1</sup> A poslije toga umrije Nas car sinova Amonovijeh, i zacari se sin njegov na njegovo mjesto.",
			"2": "<sup>2</sup> I reče David: da učinim milost Anunu sinu Nasovu, jer je otac njegov načinio meni milost. I posla David poslanike da ga potješe za ocem. I dođoše sluge Davidove u zemlju sinova Amonovijeh k Anunu da ga potješe.",
			"3": "<sup>3</sup> A knezovi sinova Amonovijeh rekoše Anunu gospodaru svojemu: misliš da je David zato poslao ljude da te potješe što je rad učiniti čast ocu tvojemu? a nijesu zato došle sluge njegove k tebi da promotre i uhode i obore zemlju?",
			"4": "<sup>4</sup> Tada Anun uhvati sluge Davidove, i obrija ih i otsiječe im haljine po pole do zadnjice, i opravi ih natrag.",
			"5": "<sup>5</sup> A kad neki otidoše, te javiše Davidu za te ljude, on posla pred njih, jer ljudi bijahu grdno osramoćeni, i poruči im car: sjedite u Jerihonu dok vam naraste brada, pa onda dođite natrag.",
			"6": "<sup>6</sup> A kad vidješe sinovi Amonovi gdje se omraziše s Davidom, onda Anun i sinovi Amonovi poslaše tisuću talanata srebra da najme kola i konjika iz Mesopotamije i iz Sirije Mahe i iz Sove.",
			"7": "<sup>7</sup> I najmiše trideset i dvije tisuće kola i cara od Mahe s narodom njegovijem, koji dođoše i stadoše u oko prema Medevi. A i sinovi Amonovi skupiše se iz gradova svojih i dođoše na boj.",
			"8": "<sup>8</sup> A David kad to ču, posla Joava sa svom hrabrom vojskom.",
			"9": "<sup>9</sup> I izidoše sinovi Amonovi i uvrstaše se pred vratima gradskim; a carevi koji dođoše bijahu za se u polju.",
			"10": "<sup>10</sup> I Joav vidjeći namještenu vojsku prema sebi sprijed i sastrag, uze odabrane iz sve vojske Izrailjske, i namjesti ih prema Sircima.",
			"11": "<sup>11</sup> A ostali narod predade Avisaju bratu svojemu; i namjestiše ih prema sinovima Amonovijem.",
			"12": "<sup>12</sup> I reče: ako Sirci budu jači od mene, dođi mi u pomoć; ako li sinovi Amonovi budu jači od tebe, ja ću tebi doći u pomoć.",
			"13": "<sup>13</sup> Budi junak i držimo se junački za svoj narod i za gradove Boga svojega; a Gospod neka učini što mu je drago.",
			"14": "<sup>14</sup> Tada Joav i narod koji bijaše s njim primakoše se da udare na Sirce; ali oni pobjegoše ispred njih.",
			"15": "<sup>15</sup> A sinovi Amonovi kad vidješe gdje pobjegoše Sirci, pobjegoše i oni ispred Avisaja brata njegova i uđoše u svoj grad. Potom se Joav vrati u Jerusalim.",
			"16": "<sup>16</sup> Ali Sirci kad vidješe gdje ih nadbiše Izrailjci, poslaše poslanike te dovedoše Sirce ispreko rijeke; a Sofak vojvoda Adad-Ezerov iđaše pred njima.",
			"17": "<sup>17</sup> Kad to javiše Davidu, on skupi sve Izrailjce, i prijeđe preko Jordana, i dođe k njima i namjesti vojsku prema njima; a kad namjesti David vojsku prema njima, pobiše se s njim.",
			"18": "<sup>18</sup> Ali pobjegoše Sirci ispred Izrailja, i pobi David Siraca sedam tisuća kola i četrdeset tisuća pješaka, i Sofaka vojvodu pogubi.",
			"19": "<sup>19</sup> I kad vidješe sluge Adad-Ezerove da ih razbi Izrailj, učiniše mir s Davidom i služahu mu; i ne htješe više Sirci pomagati sinovima Amonovijem."
		},
		"20": {
			"1": "<sup>1</sup> A kad prođe godina, u vrijeme kad carevi idu na vojsku, Joav izvede vojsku, pa potiraše zemlju sinova Amonovijeh, i došavši opkoli Ravu; a David bješe ostao u Jerusalimu. I Joav osvoji Ravu i raskopa je.",
			"2": "<sup>2</sup> I David uze caru njihovu s glave krunu, i nađe da poteže talanat zlata, i drago kamenje bješe u njoj; i metnuše je na glavu Davidu, i odnese iz grada plijen vrlo velik.",
			"3": "<sup>3</sup> A narod što bijaše u njemu izvede i isiječe ih pilama i gvozdenijem branama i sjekirama. I tako učini David svijem gradovima sinova Amonovijeh. Potom se vrati David sa svijem narodom u Jerusalim.",
			"4": "<sup>4</sup> A poslije toga nasta rat u Gezeru u Filistejima; tada Sivehaj Husaćanin ubi Sifaja koji bijaše roda divovskoga, i biše pokoreni.",
			"5": "<sup>5</sup> Nasta opet rat s Filistejima, u kom Elhanan sin Jairov ubi Lamiju brata Golijata Getejina, kojemu kopljača bijaše kao vratilo.",
			"6": "<sup>6</sup> I opet nasta rat u Gatu, gdje bijaše jedan čovjek vrlo visok, koji imaše po šest prsta, skupa dvadeset i četiri; i on bijaše roda divovskoga.",
			"7": "<sup>7</sup> I ružaše Izrailja, te ga ubi Jonatan sin Samaje brata Davidova.",
			"8": "<sup>8</sup> Ti bijahu sinovi istoga diva iz Gata, i pogiboše od ruke Davidove i od ruke sluga njegovijeh."
		},
		"21": {
			"1": "<sup>1</sup> Ali usta sotona na Izrailja i navrati Davida da izbroji Izrailja.",
			"2": "<sup>2</sup> I reče David Joavu i knezovima narodnijem: idite, izbrojte sinove Izrailjeve od Virsaveje dori do Dana, pa mi javite da znam koliko ih ima.",
			"3": "<sup>3</sup> Ali Joav reče: neka doda Gospod narodu svojemu koliko ga je sada još sto puta toliko; nijesu li, care gospodaru moj, svi sluge gospodaru mojemu? zašto traži to gospodar moj? zašto da bude na grijeh Izrailju?",
			"4": "<sup>4</sup> Ali riječ careva nadjača Joava. I tako otide Joav i obiđe svega Izrailja, pa se vrati u Jerusalim.",
			"5": "<sup>5</sup> I dade Joav broj prepisanoga naroda Davidu; i bijaše svega naroda Izrailjeva tisuća tisuća i sto tisuća ljudi koji mahahu mačem, a naroda Judina četiri stotine i sedamdeset tisuća ljudi koji mahahu mačem.",
			"6": "<sup>6</sup> A plemena Levijeva i Venijaminova ne izbroji s njima, jer mrska bješe Joavu zapovijest careva.",
			"7": "<sup>7</sup> A ne bješe mila Bogu ta stvar; zato udari Izrailja.",
			"8": "<sup>8</sup> I David reče Bogu: sagriješih veoma što to uradih; ali uzmi bezakonje sluge svojega, jer veoma ludo radih.",
			"9": "<sup>9</sup> A Gospod reče Gadu vidiocu Davidovu govoreći:",
			"10": "<sup>10</sup> Idi kaži Davidu i reci: ovako veli Gospod: troje ti dajem, izaberi jedno da ti učinim.",
			"11": "<sup>11</sup> I dođe Gad k Davidu i reče mu: tako veli Gospod, biraj:",
			"12": "<sup>12</sup> Ili glad za tri godine, ili tri mjeseca da bježiš od neprijatelja svojih i mač neprijatelja tvojih da te stiže, ili tri dana mač Gospodnji i pomor da bude u zemlji i anđeo Gospodnji da ubija po svijem krajevima Izrailjevijem. Sada dakle gledaj što ću odgovoriti onomu koji me je poslao.",
			"13": "<sup>13</sup> A David reče Gadu: u tjeskobi sam ljutoj; ali neka zapadnem Gospodu u ruke, jer je veoma velika milost njegova; a ljudima da ne zapadnem u ruke.",
			"14": "<sup>14</sup> I tako pusti Gospod pomor na Izrailja, te pade Izrailja sedamdeset tisuća ljudi.",
			"15": "<sup>15</sup> I posla Gospod anđela u Jerusalim da ga ubija; i kad ubijaše, pogleda Gospod i sažali mu se sa zla; i reče anđelu koji ubijaše: dosta, spusti ruku svoju. A anđeo Gospodnji stajaše kod gumna Ornana Jevusejina,",
			"16": "<sup>16</sup> A David podiže oči svoje i vidje anđela Gospodnjega gdje stoji između zemlje i neba a u ruci mu go mač, kojim bješe zamahnuo na Jerusalim; i pade David i starješine ničice, obučeni u kostrijet.",
			"17": "<sup>17</sup> I reče David Bogu: nijesam li ja zapovjedio da se izbroji narod? ja sam dakle zgriješio i zlo učinio; a te ovce šta su učinile? Gospode Bože moj, neka se ruka tvoja obrati na me i na dom oca mojega; ali ne na taj narod da ga potre.",
			"18": "<sup>18</sup> Tada anđeo Gospodnji reče Gadu da kaže Davidu da izide gore i načini oltar Gospodu na gumnu Ornana Jevusejina.",
			"19": "<sup>19</sup> I izide David po riječi Gadovoj, koju mu reče u ime Gospodnje.",
			"20": "<sup>20</sup> A Ornan okrenuv se ugleda anđela, i sakri se sa četiri sina svoja. Jer Ornan vrsijaše pšenicu.",
			"21": "<sup>21</sup> Utom dođe David do Ornana; i pogledav Ornan kad vidje Davida izide iz gumna i pokloni se Davidu licem do zemlje.",
			"22": "<sup>22</sup> Tada reče David Ornanu: daj mi to gumno da načinim na njemu oltar Gospodu; za novce koliko vrijedi daj mi ga, da bi prestao pomor u narodu.",
			"23": "<sup>23</sup> A Ornan reče Davidu: uzmi i neka čini gospodar moj car što mu je drago; evo dajem i volove za žrtve paljenice, i kola za drva, i pšenicu za dar; sve to dajem.",
			"24": "<sup>24</sup> A car David reče Ornanu: ne, nego ću kupiti za novce šta vrijedi, jer neću da prinesem Gospodu što je tvoje ni da prinesem žrtve paljenice poklonjene.",
			"25": "<sup>25</sup> I dade David Ornanu za ono mjesto na mjeru šest stotina sikala zlata.",
			"26": "<sup>26</sup> I ondje načini David oltar Gospodu, i prinese žrtve paljenice i žrtve zahvalne; i prizva Gospoda, i usliši ga spustivši oganj s neba na oltar žrtve paljenice.",
			"27": "<sup>27</sup> I zapovjedi Gospod anđelu, te vrati mač svoj u korice.",
			"28": "<sup>28</sup> U ono vrijeme vidjev David da ga Gospod usliši na gumnu Ornana Jevusejina prinošaše žrtve ondje.",
			"29": "<sup>29</sup> A šator Gospodnji, koji načini Mojsije u pustinji, i oltar za žrtve paljenice bijaše u to vrijeme na visini u Gavaonu.",
			"30": "<sup>30</sup> I David ne može ići k njemu da traži Bogu, jer se uplaši od mača anđela Gospodnjega."
		},
		"22": {
			"1": "<sup>1</sup> I reče David: ovo je kuća Gospoda Boga i ovo je oltar za žrtvu paljenicu Izrailju.",
			"2": "<sup>2</sup> I zapovjedi David da se skupe inostranci koji bijahu u zemlji Izrailjevoj, i odredi kamenare da tešu kamen da se gradi dom Božji.",
			"3": "<sup>3</sup> I gvožđa mnogo za kline na krila vratima i na sastavke pripravi David, i mjedi mnogo bez mjere,",
			"4": "<sup>4</sup> I drva kedrovijeh bez broja; jer dovožahu Sidonci i Tirci mnogo drva kedrovijeh Davidu.",
			"5": "<sup>5</sup> Jer David govoraše: Solomun je sin moj dijete mlado, a dom koji treba zidati Gospodu treba da bude vrlo velik za slavu i diku po svijem zemljama; zato ću mu pripraviti što treba. I pripravi David mnoštvo prije smrti svoje.",
			"6": "<sup>6</sup> Potom dozva sina svojega Solomuna i zapovjedi mu da sazida dom Gospodu Bogu Izrailjevu.",
			"7": "<sup>7</sup> I reče David Solomunu: sine! bio sam naumio da sazidam dom imenu Gospoda Boga svojega.",
			"8": "<sup>8</sup> Ali mi dođe riječ Gospodnja govoreći: mnogo si krvi prolio i velike si ratove vodio; nećeš ti sazidati doma imenu mojemu, jer si mnogo krvi prolio na zemlju preda mnom.",
			"9": "<sup>9</sup> Evo, rodiće ti se sin, on će biti miran čovjek i smiriću ga od svijeh neprijatelja njegovijeh unaokolo; zato će mu biti ime Solomun; i mir i pokoj daću Izrailju za njegova vremena.",
			"10": "<sup>10</sup> On će sazidati dom imenu mojemu, i on će mi biti sin, a ja njemu otac, i utvrdiću prijesto carstva njegova nad Izrailjem dovijeka.",
			"11": "<sup>11</sup> Zato, sine, Gospod će biti s tobom, i bićeš srećan, te ćeš sazidati dom Gospoda Boga svojega kao što je govorio za te.",
			"12": "<sup>12</sup> Samo da ti da Gospod razum i mudrost kad te postavi nad Izrailjem da držiš zakon Gospoda Boga svojega.",
			"13": "<sup>13</sup> Tada ćeš biti srećan, ako uzdržiš i ustvoriš uredbe i zakone koje je zapovjedio Gospod preko Mojsija Izrailju. Budi slobodan i hrabar, ne boj se i ne plaši se.",
			"14": "<sup>14</sup> I evo, u nevolji svojoj pripravio sam za dom Gospodnji sto tisuća talanata zlata i tisuću tisuća talanata srebra; a mjedi i gvožđa bez mjere, jer ga ima mnogo, također i drva i kamenja pripravio sam; a ti dodaj još.",
			"15": "<sup>15</sup> A imaš i poslenika mnogo, kamenara i zidara i drvodjelja, i svakojakih ljudi vještijeh svakom poslu.",
			"16": "<sup>16</sup> Ima zlata, srebra, i mjedi i gvožđa bez mjere; nastani dakle i radi, i Gospod će biti s tobom.",
			"17": "<sup>17</sup> Potom zapovjedi David svijem knezovima Izrailjevijem da pomažu Solomunu sinu njegovu:",
			"18": "<sup>18</sup> Nije li s vama Gospod Bog vaš, koji vam je dao mir od svuda? jer je dao u ruke moje stanovnike ove zemlje, i zemlja je pokorena Gospodu i narodu njegovu.",
			"19": "<sup>19</sup> Sada dakle upravite srce svoje i dušu svoju da tražite Gospoda Boga svojega; nastanite i zidajte svetinju Gospodu Bogu da unesete kovčeg zavjeta Gospodnjega i sveto posuđe Božije u dom koji će se sazidati imenu Gospodnjemu."
		},
		"23": {
			"1": "<sup>1</sup> I tako David star i sit života postavi Solomuna sina svojega carem nad Izrailjem.",
			"2": "<sup>2</sup> I sabra sve knezove Izrailjeve i sveštenike i Levite.",
			"3": "<sup>3</sup> I biše izbrojani Leviti od trideset godina i više, i bješe ih na broj s glave na glavu trideset i osam tisuća ljudi.",
			"4": "<sup>4</sup> Između njih bijaše dvadeset četiri tisuće određenijeh na posao u domu Gospodnjem, a šest tisuća upravitelja i sudija;",
			"5": "<sup>5</sup> A četiri tisuće vratara i četiri tisuće koji hvaljahu Gospoda uz oruđa koja načini za hvalu.",
			"6": "<sup>6</sup> I razdijeli ih David u redove po sinovima Levijevim, Girsonu, Katu i Merariju.",
			"7": "<sup>7</sup> Od Girsona bijahu: Ladan i Simej;",
			"8": "<sup>8</sup> Sinovi Ladanovi: poglavar Jehilo i Zetam i Joilo, trojica;",
			"9": "<sup>9</sup> (Sinovi Simejevi: Selomit i Azilo i Haran, trojica.) To su poglavari otačkih porodica Ladanovijeh.",
			"10": "<sup>10</sup> A sinovi Simejevi: Jat, Zina i Jeus i Verija, ta su četvorica sinovi Simejevi.",
			"11": "<sup>11</sup> A Jat bijaše poglavar, a Ziza drugi; a Jeus i Verija nemahu mnogo djece, zato se brojahu u jedan dom otački.",
			"12": "<sup>12</sup> Sinovi Katovi: Amram, Isar, Hevron i Ozilo, četvorica.",
			"13": "<sup>13</sup> Sinovi Amramovi: Aron i Mojsije. Ali Aron bi odvojen da osvećuje svetinju nad svetinjama, on i sinovi njegovi dovijeka, da kade pred Gospodom, da mu služe i da blagosiljaju u ime njegovo dovijeka.",
			"14": "<sup>14</sup> A sinovi Mojsija čovjeka Božijega broje se u pleme Levijevo.",
			"15": "<sup>15</sup> Sinovi Mojsijevi: Girsam i Eliezer.",
			"16": "<sup>16</sup> Sinovi Girsamovi: Sevuilo poglavar.",
			"17": "<sup>17</sup> A sinovi Eliezerovi. Reavija poglavar. A nemaše Eliezer više sinova, nego se sinovi Reavijini umnožiše veoma.",
			"18": "<sup>18</sup> Sinovi Isarovi: Selomit poglavar.",
			"19": "<sup>19</sup> Sinovi Hevronovi: Jerija prvi, Amarija drugi, Jazilo treći, i Jekameam četvrti.",
			"20": "<sup>20</sup> Sinovi Ozilovi: Miha prvi i Jesija drugi.",
			"21": "<sup>21</sup> Sinovi Merarijevi: Malije i Musije. Sinovi Malijevi: Eleazar i Kis.",
			"22": "<sup>22</sup> A Eleazar umrije i ne imaše sinova nego samo kćeri, i njima se oženiše sinovi Kisovi, braća njihova.",
			"23": "<sup>23</sup> Sinovi Musijevi: Malije i Eder i Jeremot, trojica.",
			"24": "<sup>24</sup> To su sinovi Levijevi po otačkim domovima svojim, poglavari domova otačkih, koji biše izbrojeni po broju imena s glave na glavu, koji rađahu posao za službu u domu Gospodnjem, od dvadeset godina i više.",
			"25": "<sup>25</sup> Jer David reče: mir dade Gospod Bog Izrailjev narodu svojemu, i nastavaće u Jerusalimu dovijeka.",
			"26": "<sup>26</sup> I Leviti neće više nositi šatora i posuđa njegova za službu njegovu.",
			"27": "<sup>27</sup> Jer po pošljednjoj naredbi Davidovoj biše izbrojeni sinovi Levijevi od dvadeset godina i više;",
			"28": "<sup>28</sup> Jer određeni bijahu da pomažu sinovima Aronovijem u službi u domu Gospodnjem u trijemovima i u klijetima, i da čiste sve svete stvari i da rade oko službe u domu Gospodnjem,",
			"29": "<sup>29</sup> I oko hljebova postavljenijeh, i oko bijeloga brašna za dar i oko kolača prijesnijeh i oko tavica, i oko svega što se prži, i oko svake mjere,",
			"30": "<sup>30</sup> I da stoje jutrom i hvale i slave Gospoda, i tako večerom,",
			"31": "<sup>31</sup> I kad se god prinose žrtve paljenice Gospodu, u subote, i na mladine i praznike, u broju po redu svom svagda pred Gospodom,",
			"32": "<sup>32</sup> I da rade što treba raditi u šatoru od sastanka i u svetinji, i za sinove Aronove, braću svoju, u službi u domu Gospodnjem."
		},
		"24": {
			"1": "<sup>1</sup> A među sinovima Aronovijem ovo su redovi: sinovi Aronovi bjehu Nadav i Avijud, Eleazar i Itamar.",
			"2": "<sup>2</sup> Ali Nadav i Avijud umriješe prije oca svojega i nemahu djece; zato biše sveštenici Eleazar i Itamar.",
			"3": "<sup>3</sup> I razdjeli ih David: Sadoka, koji bijaše od sinova Eleazarovijeh, i Ahimeleha, koji bijaše od sinova Itamarovijeh, po redu njihovu u službi njihovoj.",
			"4": "<sup>4</sup> I nađe se sinova Eleazarovijeh više poglavica nego sinova Itamarovijeh, kad ih razdijeliše: od sinova Eleazarovijeh bijaše poglavica po domovima otačkim šesnaest, a od sinova Itamarovijeh osam po domovima otačkim.",
			"5": "<sup>5</sup> I biše razdijeljeni ždrijebom i jedan i drugi; jer poglavari u svetinji i poglavari pred Bogom bijahu i od sinova Eleazarovijeh i od sinova Itamarovijeh.",
			"6": "<sup>6</sup> I popisa ih Semaja sin Natanailov pisar od plemena Levijeva pred carem i knezovima i Sadokom sveštenikom i Ahimelehom sinom Avijatarovijem i pred poglavarima porodica otačkih među sveštenicima i Levitima, jedan dom otački uzevši za Eleazara a jedan za Itamara.",
			"7": "<sup>7</sup> I pade prvi ždrijeb na Jojariva, drugi na Jedaju,",
			"8": "<sup>8</sup> Treći na Harima, četvrti na Seorima,",
			"9": "<sup>9</sup> Peti na Malhiju, šesti na Mejamina,",
			"10": "<sup>10</sup> Sedmi na Akosa, osmi na Aviju,",
			"11": "<sup>11</sup> Deveti na Isuja, deseti na Sehaniju,",
			"12": "<sup>12</sup> Jedanaesti na Elijasiva, dvanaesti na Jakima,",
			"13": "<sup>13</sup> Trinaesti na Ufu, četrnaesti na Jesevava,",
			"14": "<sup>14</sup> Petnaesti na Vilgu, šesnaesti na Imira,",
			"15": "<sup>15</sup> Sedamnaesti na Ezira, osamnaesti na Afisisa,",
			"16": "<sup>16</sup> Devetnaesti na Petaju, dvadeseti na Jezekila,",
			"17": "<sup>17</sup> Dvadeset prvi na Jahina, dvadeset drugi na Gamula,",
			"18": "<sup>18</sup> Dvadeset treći na Delaju, dvadeset četvrti na Maziju.",
			"19": "<sup>19</sup> To je red njihov za službu njihovu, kojim idu u dom Gospodnji na posao svoj po naredbi Arona oca svojega, kako mu bješe zapovjedio Gospod Bog Izrailjev.",
			"20": "<sup>20</sup> A od ostalijeh sinova Levijevih bijaše od sinova Amramovijeh Suvailo; od sinova Suvailovijeh Jedaja,",
			"21": "<sup>21</sup> Od Reavije, od sinova Reavijinih poglavar Jesija;",
			"22": "<sup>22</sup> Od sinova Isarovijeh Selomot, od sinova Selomotovijeh Jat;",
			"23": "<sup>23</sup> A od sinova Jerijinih Amarija drugi, Jazilo treći, Jekameam četvrti;",
			"24": "<sup>24</sup> Od sinova Uzilovijeh Miha; od sinova Mišinijeh Samir;",
			"25": "<sup>25</sup> Brat Mišin Jesija; od sinova Jesinijih Zaharija.",
			"26": "<sup>26</sup> Sinovi Merarijevi: Malije i Musije; od sinova Jazijinih Veno.",
			"27": "<sup>27</sup> Sinovi Merarijevi od Jazije: Veno i Soam i Zahur i Ivrije;",
			"28": "<sup>28</sup> Od Malija Eleazar, koji nemaše sinova;",
			"29": "<sup>29</sup> Od Kisa, od sinova Kisovijeh: Jerameilo;",
			"30": "<sup>30</sup> I od sinova Musijevih Malije i Eder i Jerimot. To bijahu sinovi Levitski po domovima otaca svojih.",
			"31": "<sup>31</sup> I oni bacaše ždrijeb prema braći svojoj, sinovima Aronovijem, pred Davidom i Sadokom i Ahimelehom i poglavarima domova otačkih među sveštenicima i Levitima, od domova otačkih svaki poglavar prema bratu svojemu mlađemu."
		},
		"25": {
			"1": "<sup>1</sup> I odvoji David s vojvodama za službu sinove Asafove i Emanove i Jedutunove, koji će prorokovati uz gusle i psaltire i kimvale; i biše između njih izbrojani ljudi za posao u svojoj službi:",
			"2": "<sup>2</sup> Od sinova Asafovijeh: Zahur i Josif i Netanija i Asarila, sinovi Asafovi, pod rukom Asafa, koji prorokovaše po naredbi carevoj;",
			"3": "<sup>3</sup> Od Jedutuna: šest sinova Jedutunovijeh: Gedalija i Sorije i Jesaija, Asavija i Matatija i Simej pod rukom oca svojega Jedutuna, koji prorokovaše uz gusle hvaleći i slaveći Gospoda;",
			"4": "<sup>4</sup> Od Emana: sinovi Emanovi: Vukija, Matanija, Ozilo, Sevuilo i Jerimot, Ananija i Ananije, Elijata, Gidaltija i Romamti-Ezer, Josvekasa, Malotije, Otir i Maziot.",
			"5": "<sup>5</sup> Ti svi bijahu sinovi Emana vidioca careva u riječima Božijim da se uzvišuje rog; jer Bog dade Emanu četrnaest sinova i tri kćeri.",
			"6": "<sup>6</sup> Svi oni bijahu pod rukom oca svojega pjevajući u domu Gospodnjem uz kimvale i psaltire i gusle, za službu u domu Božijem, kako car naređivaše Asafu i Jedutunu i Emanu.",
			"7": "<sup>7</sup> I bješe ih na broj s braćom njihovom obučenom pjesmama Gospodnjim, dvjesta i osamdeset i osam, samijeh vještaka.",
			"8": "<sup>8</sup> I baciše ždrijeb za službu svoju, mali kao veliki, učitelj kao učenik;",
			"9": "<sup>9</sup> I pade prvi ždrijeb za Asafa na Josifa, drugi na Gedaliju s braćom i sinovima njegovijem, njih dvanaest;",
			"10": "<sup>10</sup> Treći na Zahura, sinove njegove i braću njegovu, njih dvanaest;",
			"11": "<sup>11</sup> Četvrti na Iserija, sinove njegove i braću njegovu, njih dvanaest;",
			"12": "<sup>12</sup> Peti na Netaniju, sinove njegove i braću njegovu, njih dvanaest;",
			"13": "<sup>13</sup> Šesti na Vukiju, sinove njegove i braću njegovu, njih dvanaest;",
			"14": "<sup>14</sup> Sedmi na Jesarilu, sinove njegove i braću njegovu, njih dvanaest;",
			"15": "<sup>15</sup> Osmi na Jesaiju, sinove njegove i braću njegovu, njih dvanaest;",
			"16": "<sup>16</sup> Deveti na Mataniju, sinove njegove i braću njegovu, njih dvanaest;",
			"17": "<sup>17</sup> Deseti na Simeja, sinove njegove i braću njegovu, njih dvanaest;",
			"18": "<sup>18</sup> Jedanaesti na Azareila, sinove njegove i braću njegovu, njih dvanaest;",
			"19": "<sup>19</sup> Dvanaesti na Asaviju, sinove njegove i braću njegovu, njih dvanaest;",
			"20": "<sup>20</sup> Trinaesti na Savuila, sinove njegove i braću njegovu, njih dvanaest;",
			"21": "<sup>21</sup> Četrnaesti na Matatiju, sinove njegove i braću njegovu, njih dvanaest;",
			"22": "<sup>22</sup> Petnaesti na Jeremota, sinove njegove i braću njegovu, njih dvanaest;",
			"23": "<sup>23</sup> Šesnaesti na Ananiju, sinove njegove i braću njegovu, njih dvanaest;",
			"24": "<sup>24</sup> Sedamnaesti na Josvekasu, sinove njegove i braću njegovu, njih dvanaest;",
			"25": "<sup>25</sup> Osamnaesti na Ananija, sinove njegove i braću njegovu, njih dvanaest;",
			"26": "<sup>26</sup> Devetnaesti na Malotija, sinove njegove i braću njegovu, njih dvanaest;",
			"27": "<sup>27</sup> Dvadeseti na Elijatu, sinove njegove i braću njegovu, njih dvanaest;",
			"28": "<sup>28</sup> Dvadeset prvi na Otira, sinove njegove i braću njegovu, njih dvanaest;",
			"29": "<sup>29</sup> Dvadeset drugi na Gidaltiju, sinove njegove i braću njegovu, njih dvanaest;",
			"30": "<sup>30</sup> Dvadeset treći na Maziota, sinove njegove i braću njegovu, njih dvanaest;",
			"31": "<sup>31</sup> Dvadeset četvrti na Romamti-Ezera, sinove njegove i braću njegovu, njih dvanaest;"
		},
		"26": {
			"1": "<sup>1</sup> A redovi vratarski bijahu: od sinova Korejevih bijaše Meselemija sin Korejev između sinova Asafovijeh;",
			"2": "<sup>2</sup> A sinovi Meselemijini: Zaharija prvenac, Jedailo drugi, Zavadija treći, Jatnilo četvrti,",
			"3": "<sup>3</sup> Elam peti, Joanan šesti, Elioinaj sedmi;",
			"4": "<sup>4</sup> I Ovid-Edomovi sinovi: Semaja prvenac, Jozavad drugi, Joah treći i Sihar četvrti i Natanailo peti,",
			"5": "<sup>5</sup> Amilo šesti, Isahar sedmi, Feultaj osmi; jer ga blagoslovi Bog.",
			"6": "<sup>6</sup> I Semaji sinu njegovu rodiše se sinovi, koji starješovahu u domu oca svojega, jer bijahu dobri junaci.",
			"7": "<sup>7</sup> Sinovi Semajini: Gotnije i Rafailo i Ovid i Elzavad braća njegova, hrabri ljudi, Eliuj i Semahija.",
			"8": "<sup>8</sup> Svi ovi bijahu od sinova Ovid-Edomovijeh, i oni i sinovi njihovi i braća njihova, svi hrabri ljudi, krjepki za službu, bijaše ih šezdeset i dva od Ovid-Edoma.",
			"9": "<sup>9</sup> A Meselemijinih sinova i braće, hrabrijeh ljudi, bijaše osamnaest.",
			"10": "<sup>10</sup> A Osini sinovi, od sinova Merarijevih: Simrije poglavar, premda ne bješe prvenac, ali ga otac postavi poglavarem;",
			"11": "<sup>11</sup> Helkija drugi, Tevalija treći, Zaharija četvrti; svijeh sinova i braće Osine bijaše trinaest.",
			"12": "<sup>12</sup> Od njih bijahu redovi vratarski po poglavarima da čuvaju stražu naizmjence s braćom svojom služeći u domu Gospodnjem.",
			"13": "<sup>13</sup> Jer metaše ždrijeb za maloga kao i za velikoga po domovima svojih otaca, za svaka vrata.",
			"14": "<sup>14</sup> I pade ždrijeb na istok Selimiji; a Zahariji sinu njegovu, mudromu savjetniku, kad baciše ždrijeb, pade mu ždrijeb na sjever;",
			"15": "<sup>15</sup> A Ovid-Edomu na jug, a sinovima njegovijem na riznicu;",
			"16": "<sup>16</sup> Sufimu i Osi na zapad s vratima Salehetskim na putu koji ide gore; straža bješe prema straži:",
			"17": "<sup>17</sup> S istoka šest Levita; sa sjevera četiri na dan; s juga na dan četiri; a kod riznice po dva;",
			"18": "<sup>18</sup> Na Parvaru sa zapada četiri na putu, dva kod Parvara.",
			"19": "<sup>19</sup> To su redovi vratarski među sinovima Korejevim i sinovima Merarijevim.",
			"20": "<sup>20</sup> I ovi još bijahu Leviti: Ahija nad blagom doma Božijega, nad blagom od posvećenijeh stvari.",
			"21": "<sup>21</sup> Od sinova Ladanovijeh između sinova Girsonovijeh od Ladana, između glavara domova otačkih od Ladana sina Girsonova bijaše Jehilo,",
			"22": "<sup>22</sup> Sinovi Jehilovi: Zetam i Joilo brat mu bijahu nad blagom doma Gospodnjega;",
			"23": "<sup>23</sup> Od sinova Amramovijeh, Isarovijeh, Hevronovijeh, Ozilovijeh,",
			"24": "<sup>24</sup> Bijaše Sevuilo sin Girsona sina Mojsijeva starješina nad blagom.",
			"25": "<sup>25</sup> A braća njegova od Elijezera: Reavija sin mu, a njegov sin Jesaija, a njegov sin Joram, a njegov sin Zihrije, a njegov sin Selomit;",
			"26": "<sup>26</sup> Ovaj Selomit i braća njegova bijahu nad svijem blagom od posvećenijeh stvari, koje posveti car David i poglavari domova otačkih i tisućnici i stotinici i vojvode;",
			"27": "<sup>27</sup> Od ratova i od plijena posvetiše da se opravi dom Gospodnji;",
			"28": "<sup>28</sup> I što god bješe posvetio Samuilo vidjelac i Saul sin Kisov i Avenir sin Nirov i Joav sin Serujin; ko god posvećivaše, davaše u ruke Selomitu i braći njegovoj.",
			"29": "<sup>29</sup> Od sinova Isarovijeh: Henanija i sinovi njegovi bijahu nad spoljašnjim poslovima u Izrailju, upravitelji i sudije.",
			"30": "<sup>30</sup> Od sinova Hevronovijeh Asavija i braća njegova, tisuća i sedam stotina hrabrijeh ljudi, bijahu nad Izrailjem s ovu stranu Jordana na istoku za svaki posao Gospodnji i za službu carsku.",
			"31": "<sup>31</sup> Između sinova Hevronovijeh bijaše Jerija poglavar sinovima Hevronovijem po porodicama njihovijem i domovima otačkim. Četrdesete godine carovanja Davidova potražiše ih i nađoše među njima hrabrijeh junaka u Jaziru Galadskom.",
			"32": "<sup>32</sup> I braće njegove, hrabrijeh ljudi, bijaše dvije tisuće i sedam stotina glavara u domovima otačkim; i postavi ih car David nad sinovima Ruvimovijem i Gadovijem i polovinom plemena Manasijina za sve poslove Božije i carske."
		},
		"27": {
			"1": "<sup>1</sup> A sinovi Izrailjevi po svom broju, glavari domova otačkih, tisućnici i stotinici i upravitelji njihovi, služahu caru u svakom poslu u redovima, koji dolažahu i odlažahu od mjeseca do mjeseca, svakoga mjeseca u godini; a u svakom redu bijaše ih dvadeset i četiri tisuće.",
			"2": "<sup>2</sup> Nad prvijem redom prvoga mjeseca bijaše Jasoveam sin Zavdilov, i u njegovu redu bijaše dvadeset i četiri tisuće.",
			"3": "<sup>3</sup> Od sinova Faresovijeh bijaše poglavar nad svijem starješinama u vojsci prvoga mjeseca.",
			"4": "<sup>4</sup> A nad redom drugoga mjeseca bijaše Dodaj Ahošanin, i vojvoda u njegovu redu bješe Miklot; i u njegovu redu bješe dvadeset i četiri tisuće.",
			"5": "<sup>5</sup> Treći vojvoda trećega mjeseca bješe Venaja sin Jodaja sveštenika, poglavar; i u njegovu redu bješe dvadeset i četiri tisuće.",
			"6": "<sup>6</sup> Ovaj Venaja bješe junak među tridesetoricom i nad tridesetoricom; i nad redom njegovijem bješe Amnizavad sin mu.",
			"7": "<sup>7</sup> Četvrti, četvrtoga mjeseca Asailo brat Joavov, a za njim Zevadija sin mu; i u redu njegovu bješe dvadeset i četiri tisuće.",
			"8": "<sup>8</sup> Peti, petoga mjeseca, poglavar Samut Jezrajanin, i u njegovu redu bješe dvadeset i četiri tisuće.",
			"9": "<sup>9</sup> Šesti, šestoga mjeseca, Ira sin Ikisov Tekojanin, a u njegovu redu bješe dvadeset i četiri tisuće.",
			"10": "<sup>10</sup> Sedmi, sedmoga mjeseca, Helis Felonjanin od sinova Jefremovijeh, a u njegovu redu bješe dvadeset i četiri tisuće.",
			"11": "<sup>11</sup> Osmi, osmoga mjeseca, Sivehaj Husaćanin od sinova Zarinijeh, i u njegovu redu bješe dvadeset i četiri tisuće.",
			"12": "<sup>12</sup> Deveti, devetoga mjeseca, Avijezer Anatoćanin od sinova Venijaminovih, i u njegovu redu bješe dvadeset i četiri tisuće.",
			"13": "<sup>13</sup> Deseti, desetoga mjeseca, Maraj Netofaćanin od sinova Zarinijeh, i u njegovu redu bješe dvadeset i četiri tisuće.",
			"14": "<sup>14</sup> Jedanaesti, jedanaestog mjeseca, Venaja Faratonjanin od sinova Jefremovijeh, i u njegovu redu bješe dvadeset i četiri tisuće.",
			"15": "<sup>15</sup> Dvanaesti, dvanaestog mjeseca, Heldaj Netofaćanin od Gotonila, a u njegovu redu bješe dvadeset i četiri tisuće.",
			"16": "<sup>16</sup> A nad plemenima Izrailjevijem bijahu: nad plemenom Ruvimovijem knez Elijezer sin Zihrijev; nad Simeunovijem Sefatija sin Mašin;",
			"17": "<sup>17</sup> Nad Levijevim Asavija sin Kemuilov; nad Aronovijem Sadok;",
			"18": "<sup>18</sup> Nad Judinijem Eliuj od braće Davidove; nad Isaharovijem Amrije sin Mihailov;",
			"19": "<sup>19</sup> Nad Zavulonovijem Ismaja sin Ovadijin; nad Neftalimovim Jerimot sin Azrilov;",
			"20": "<sup>20</sup> Nad sinovima Jefremovijem Isus sin Azazijev; nad polovinom plemena Manasijina Joilo sin Fedajin;",
			"21": "<sup>21</sup> Nad drugom polovinom plemena Manasijina u Galadu Ido sin Zaharijin; nad Venijaminovim Jasilo sin Avenirov;",
			"22": "<sup>22</sup> Nad Danovijem Azareilo sin Jeroamov. To bijahu knezovi plemena Izrailjevijeh.",
			"23": "<sup>23</sup> A ne izbroji David onijeh koji imahu manje od dvadeset godina, jer Gospod bješe rekao da će umnožiti sinove Izrailjeve kao zvijezde nebeske.",
			"24": "<sup>24</sup> Joav sin Serujin poče brojiti, ali ne dovrši; jer dođe gnjev Božji za to na Izrailja; zato taj broj ne dođe u knjigu o caru Davidu.",
			"25": "<sup>25</sup> Nad blagom carevijem bijaše Azmavet sin Adilov, a nad blagom po zemlji, po gradovima i po selima i po kulama Jonatan sin Ozijin.",
			"26": "<sup>26</sup> A nad ratarima, koji rađahu zemlju, bijaše Ezrije sin Heluvov;",
			"27": "<sup>27</sup> A nad vinogradarima Simej Ramaćanin; a nad rodom vinogradskim i nad pivnicama Zavdije Sifmejin;",
			"28": "<sup>28</sup> A nad maslinama i smokvama po poljima Val-Anan Gederanin; a nad uljem Joas;",
			"29": "<sup>29</sup> Nad govedima što pasijahu u Saronu Sitraj Saronjanin, a nad govedima po dolinama Safat Adlajev;",
			"30": "<sup>30</sup> Nad kamilama Ovil Ismailjac; nad magarcima Jedaja Meronoćanin;",
			"31": "<sup>31</sup> A nad ovcama Jaziz Agarejin. Svi ovi bijahu nastojnici nad blagom cara Davida.",
			"32": "<sup>32</sup> A Jonatan stric Davidov bijaše savetnik, mudar čovjek i književnik; on i Jehilo sin Ahmonijev bijahu sa sinovima carevijem.",
			"33": "<sup>33</sup> I Ahitofel bijaše savjetnik carev, i Husaj Arhijanin prijatelj carev.",
			"34": "<sup>34</sup> A poslije Ahitofela bijaše Jodaj sin Venajin i Avijatar, a vojvoda carev bješe Joav."
		},
		"28": {
			"1": "<sup>1</sup> I David sabra u Jerusalim sve poglavare Izrailjeve, poglavare plemenske i poglavare od redova koji služahu caru, i tisućnike i stotinike, i koji bijahu nad svijem blagom i stokom carevom, i sinove svoje i dvorane i junake i sve ljude hrabre,",
			"2": "<sup>2</sup> I ustavši car David na noge reče: čujte me, braćo moja i narode moj! ja bih naumio da sazidam dom gdje bi počivao kovčeg Gospodnji i da bude podnožje nogama Boga našega, i pripravih što treba za zidanje.",
			"3": "<sup>3</sup> Ali mi Bog reče: nećeš sazidati doma imenu mojemu, jer si ratnik i krv si proljevao.",
			"4": "<sup>4</sup> Ali Gospod Bog Izrailjev izabra me iz svega doma oca mojega da budem car nad Izrailjem dovijeka; jer Judu izabra za vođa, i iz doma Judina dom oca mojega; i između sinova oca mojega bi mu volja da mene postavi carem nad svijem Izrailjem.",
			"5": "<sup>5</sup> A tako između svijeh sinova mojih (jer mnogo sinova dade mi Gospod) izabra Solomuna sina mojega da sjedi na prijestolu carstva Gospodnjega nad Izrailjem.",
			"6": "<sup>6</sup> I reče mi: Solomun sin tvoj sazidaće moj dom i trijemove moje, jer njega izabrah sebi za sina, i ja ću mu biti otac.",
			"7": "<sup>7</sup> I utvrdiću carstvo njegovo dovijeka, ako bude postojan da izvršuje zapovijesti moje i zakone moje kao danas.",
			"8": "<sup>8</sup> Sada dakle pred svijem Izrailjem, zborom Gospodnjim, i da čuje Bog naš, kažem vam: držite i tražite sve zapovijesti Gospoda Boga svojega, da biste držali ovu dobru zemlju i ostavili je u našljedstvo sinovima svojim nakon sebe dovijeka.",
			"9": "<sup>9</sup> A ti, Solomune sine, znaj Boga oca svojega, i služi mu cijelim srcem i dušom dragovoljnom; jer sva srca ispituje Gospod i svaku pomisao zna; ako ga ustražiš, naći ćeš ga; ako li ga ostaviš, odbaciće te zasvagda.",
			"10": "<sup>10</sup> Vidi sada da te je Gospod izabrao da sazidaš dom za svetinju; budi hrabar i radi.",
			"11": "<sup>11</sup> Tada predade David Solomunu sinu svojemu sliku od trijema i od kuća njegovijeh, i od riznica i od soba i od klijeti unutrašnjih, i od mjesta za zaklopac očišćenja,",
			"12": "<sup>12</sup> I sliku od svega što bješe smislio za trijemove doma Gospodnjega i za sve klijeti unaokolo i za riznicu doma Božijega i za riznicu od svetijeh stvari,",
			"13": "<sup>13</sup> I za redove svešteničke i Levitske i za svaki posao u službi u domu Gospodnjem i za sve posuđe u domu Gospodnjem;",
			"14": "<sup>14</sup> Zlato pod mjeru za sudove zlatne za svaku službu, i srebro za sve sudove srebrne pod mjeru, za sve sudove za svaku službu;",
			"15": "<sup>15</sup> Pod mjeru za svijetnjake zlatne i žiške zlatne, po težini svakoga svijetnjaka i žižaka njegovijeh, i za svijetnjake srebrne po težini svakoga svijetnjaka i žižaka njegovijeh, prema potrebi svakoga svijetnjaka;",
			"16": "<sup>16</sup> I zlata pod mjeru za stolove na kojima će stajati hljebovi postavljeni, za svaki sto; i srebra za stolove srebrne;",
			"17": "<sup>17</sup> I za viljuške i za kotliće i za zdjele zlata čistoga, i za zlatne čaše pod mjeru, za svaku čašu, i za srebrne čaše srebra pod mjeru za svaku;",
			"18": "<sup>18</sup> I za oltar kadioni čistoga zlata pod mjeru; i sliku od kola zlatnijeh heruvima, koji će raširivši krila zaklanjati kovčeg zavjeta Gospodnjega.",
			"19": "<sup>19</sup> To mi je sve došlo pisano rukom Gospodnjom da bih znao sve kako što treba uraditi.",
			"20": "<sup>20</sup> I tako reče David Solomunu sinu svojemu: budi slobodan i hrabar, i radi, ne boj se i ne plaši se, jer će Gospod Bog, Bog moj, biti s tobom, neće te ostaviti niti će otstupiti od tebe, dokle ne svršiš sav posao za službu u domu Gospodnjem.",
			"21": "<sup>21</sup> A evo redovi sveštenički i Levitski za svaku službu u domu Božijem; i imaš uza se za svaki posao ljudi dragovoljnijeh i vještijeh svakom poslu, i poglavare i sav narod za svaku zapovijest svoju."
		},
		"29": {
			"1": "<sup>1</sup> Potom reče David svemu zboru: jednoga Solomuna sina mojega izabrao je Gospod, mlado dijete, a ovo je velik posao; jer neće biti čovjeku taj dom nego Gospodu Bogu.",
			"2": "<sup>2</sup> Ja koliko mogoh pripravih za dom Boga svojega zlata za stvari zlatne, srebra za srebrne, mjedi za mjedene, gvožđa za gvozdene, i drva za drvene, kamenja onihova, i kamenja za ukivanje, i kamenja za nakit i za vez, i svakojakoga dragoga kamenja, i kamena mramora izobila.",
			"3": "<sup>3</sup> I još iz ljubavi k domu Boga svojega, što imam svoga zlata i srebra, osim svega što sam pripravio za dom sveti, dajem i to na dom Boga svojega:",
			"4": "<sup>4</sup> Tri tisuće talanata zlata Ofirskoga, i sedam tisuća talanata čistoga srebra, da se oblože zidovi domovima.",
			"5": "<sup>5</sup> Zlato za zlatne stvari, a srebro za srebrne i za svako djelo ruku umjetničkih. A bi li jošte ko htio dragovoljno što danas priložiti Gospodu?",
			"6": "<sup>6</sup> Tada dragovoljno priložiše knezovi domova otačkih i knezovi plemena Izrailjevijeh i tisućnici i stotinici i knezovi nad poslovima carskim.",
			"7": "<sup>7</sup> I dadoše za službu u domu Božijem zlata pet tisuća talanata i deset tisuća zlatica, i srebra deset tisuća talanata, i mjedi osamnaest tisuća talanata, i gvožđa sto tisuća talanata.",
			"8": "<sup>8</sup> I kamenja u koga god bijaše svi dadoše u riznicu doma Gospodnjega u ruke Jehila od sinova Girsonovijeh.",
			"9": "<sup>9</sup> I radovaše se narod što dragovoljno prilagahu, jer prilagahu cijelijem srcem Gospodu; i car se David radovaše veoma.",
			"10": "<sup>10</sup> Potom David blagoslovi Gospoda pred svijem zborom, i reče David: blagosloven si Gospode Bože Izrailja oca našega od vijeka do vijeka.",
			"11": "<sup>11</sup> Tvoje je, Gospode, veličanstvo i sila i slava i vječnost i čast, i sve što je na nebu i na zemlji; tvoje je, Gospode, carstvo, i ti si uzvišen svrh svega poglavar;",
			"12": "<sup>12</sup> Bogatstvo i slava od tebe je, i ti vladaš svijem, i u tvojoj je ruci moć i sila, i u tvojoj je ruci uzvisiti i ukrijepiti sve.",
			"13": "<sup>13</sup> Sada dakle, Bože naš, hvalimo te i slavimo ime tvoje slavno.",
			"14": "<sup>14</sup> Jer ko sam ja i šta je moj narod da bi smo mogli ovoliko prinijeti tebi dragovoljno? jer je od tebe sve, i iz tvojih ruku primivši dasmo ti.",
			"15": "<sup>15</sup> Jer smo došljaci pred tobom i gosti kao svi oci naši; dani su naši na zemlji kao sjen i nema stajanja.",
			"16": "<sup>16</sup> Gospode Bože naš, sve ovo blago što ti pripravismo za građenje doma imenu tvojemu svetome, iz tvoje je ruke, i sve je tvoje.",
			"17": "<sup>17</sup> Ali znam, Bože moj, da ti ispituješ srca i što je pravo hoćeš; ja pravijem srcem dragovoljno prinesoh sve ovo, i s radošću vidjeh narod tvoj koji je ovdje kako ti dragovoljno prinosi.",
			"18": "<sup>18</sup> Gospode Bože Avrama, Isaka i Izrailja, otaca naših, sačuvaj dovijeka ovu volju i pomisao srdačnu naroda svojega, i upravljaj srce njihovo k sebi.",
			"19": "<sup>19</sup> I Solomunu sinu mojemu podaj srce pravo da bi držao zapovijesti tvoje, svjedočanstva tvoja i uredbe tvoje, i da bi otvorio sve i da bi sazidao dvor ovaj za koji sam pripravio.",
			"20": "<sup>20</sup> Potom reče David svemu zboru: blagoslovite sada Gospoda Boga svojega. I sav zbor blagoslovi Gospoda Boga otaca svojih, i savivši se pokloniše se Gospodu i caru.",
			"21": "<sup>21</sup> I prinesoše Gospodu žrtve, i prinesoše Gospodu žrtve paljenice sjutradan: tisuću volova, tisuću ovnova, tisuću jaganjaca s naljevima njihovijem, i drugih žrtava mnogo za sav narod.",
			"22": "<sup>22</sup> I jedoše i piše pred Gospodom onaj dan veseleći se veoma. I postaviše drugom Solomuna sina Davidova carem, i pomazaše ga Gospodu za vođa a Sadoka za sveštenika.",
			"23": "<sup>23</sup> I tako sjede Solomun na prijesto Gospodnji da caruje mjesto Davida oca svojega, i bijaše srećan, i slušaše ga sav Izrailj.",
			"24": "<sup>24</sup> I svi knezovi i junaci i svi sinovi cara Davida dadoše ruke da će biti pokorni caru Solomunu.",
			"25": "<sup>25</sup> I Gospod uzvisi veoma Solomuna pred svijem Izrailjem i dade mu slavu carsku kakve nijedan car prije njega nije imao u Izrailju.",
			"26": "<sup>26</sup> Tako David sin Jesejev carova nad svijem Izrailjem.",
			"27": "<sup>27</sup> A vremena za koje carova nad Izrailjem bijaše četrdeset godina: a u Hevronu carova sedam godina, a u Jerusalimu carova trideset i tri godine.",
			"28": "<sup>28</sup> I umrije u dobroj starosti, sit života, bogatstva i slave; i zacari se Solomun sin njegov na njegovo mjesto.",
			"29": "<sup>29</sup> A djela cara Davida prva i pošljednja eno su zapisana u knjizi Samuila vidioca i u knjizi Natana proroka i u knjizi Gada vidioca,",
			"30": "<sup>30</sup> Sa svijem carovanjem njegovijem i silom njegovom i s vremenima koja prođoše preko njega i Izrailja i svijeh carevina zemaljskih."
		}
	}
};
module.exports = book;