var book = {
	"name": "1. Ljetopisa",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adam, Set, Enoš,",
			"2": "<sup>2</sup> Kainan, Mahalalel, Jared,",
			"3": "<sup>3</sup> Henok, Metuselah, Lamek,",
			"4": "<sup>4</sup> Noa, Šem, Ham i Jafet,",
			"5": "<sup>5</sup> Sinovi Jafetovi jesu Gomer, Magog, Madaj, Javan, Tubal, Mešek i Tiras.",
			"6": "<sup>6</sup> Sinovi Gomerovi jesu Aškenaz, Rifat i Togarma.",
			"7": "<sup>7</sup> Sinovi Javanovi jesu Eliša i Taršiš, Kitimi i Dodanimi,",
			"8": "<sup>8</sup> Sinovi Hamovi jesu Kuš, Mizraim, Put i Kanaan.",
			"9": "<sup>9</sup> Sinovi Kušovi jesu Saba, Havila, Sabta, Raama i Sabteka. Sinovi Raamini jesu Šeba i Dedan.",
			"10": "<sup>10</sup> Kušu se rodi Nimroda, Ovaj je bio prvi vladar na zemlji.",
			"11": "<sup>11</sup> Od Mizraima vuku lozu Ludimi, Anamimi, Lehabimi, Neftuhimi,",
			"12": "<sup>12</sup> Patrusimi, Kasluhimi, od kojih proizađoše Filisteji, i Kaftorimi.",
			"13": "<sup>13</sup> Od Kanaana vuku lozu Sidon, prvorođenac njegov, i Hiteji,",
			"14": "<sup>14</sup> Onda Jebuseji, Amoreji i Girgašeji,",
			"15": "<sup>15</sup> Hiveji, Arkeji i Sineji,",
			"16": "<sup>16</sup> Arvadeji, Semareji i Hamateji,",
			"17": "<sup>17</sup> Sinovi Šemovi jesu Elam, Ašur, Arfaksad, Lud, Aram, Us, Hul, Geter i Mešek.",
			"18": "<sup>18</sup> Arfaksadu se rodi Šelah, a Šelahu se rodi Eber.",
			"19": "<sup>19</sup> Eberu se rodiše dva sina, jedan se zvao Peleg; jer se u njegovo vrijeme razdijeli čovječanstvo. Brat njegov zvao se Joktan.",
			"20": "<sup>20</sup> Od Joktana vuku lozu Almodad, Šelef, Hasarmavet i Jerah,",
			"21": "<sup>21</sup> Hadoram, Uzal, i Dikla,",
			"22": "<sup>22</sup> Ebal, Abimael i Šeba,",
			"23": "<sup>23</sup> Ofir, Havila i Jobab. Svi ovi sinovi su Joktanovi.",
			"24": "<sup>24</sup> Šem, Arfaksad, Sale,",
			"25": "<sup>25</sup> Eber, Peleg, Reu,",
			"26": "<sup>26</sup> Serug, Nahor, Tare,",
			"27": "<sup>27</sup> Abram, to jest Abraham.",
			"28": "<sup>28</sup> Sinovi Abrahamovi jesu Izak i Išmael.",
			"29": "<sup>29</sup> Ovo je rodovnik njihov: \"Prvorođenac Išmaelov Nabajot, dalje Kedar, Adbeel, Mibsam,",
			"30": "<sup>30</sup> Mišma, Duma, Masa, Hadad, Tema,",
			"31": "<sup>31</sup> Jetur, Nafiš i Kedma. To su sinovi Išmaelovi.",
			"32": "<sup>32</sup> Sinovi Keture, inoče Abrahamove: \"Ona rodi Zimrana, Jokšana, Medana, Midjana, Jišbaka i Šuaha. Sinovi Jokšanovi jesu Šeba i Dedan.",
			"33": "<sup>33</sup> Sinovi Midjanovi jesu Efa, Efer, Henok, Abida i Eldaa. Ti su svi potomci Keturini.",
			"34": "<sup>34</sup> Abrahamu se rodi Izak, Sinovi Izakovi jesu Ezav i Izrael.",
			"35": "<sup>35</sup> Sinovi Ezavovi jesu Elifaz, Reuel, Jeuš, Jalam i Korah.",
			"36": "<sup>36</sup> Sinovi Elifazovi jesu Teman, Omar, Sefi, Gatan, Kenaz, Timna i Amalek.",
			"37": "<sup>37</sup> Sinovi Reuelovi jesu Nahat, Zerah, Šamah i Mizah.",
			"38": "<sup>38</sup> Sinovi Seirovi jesu Lotan, Šobal, Sibeon, Ana, Dišon, Ezer i Dišan.",
			"39": "<sup>39</sup> Sinovi Lotanovi jesu Hori i Homam; Lotanova sestra bila je Timna.",
			"40": "<sup>40</sup> Sinovi Šobalovi jesu Alian, Manahat, Ebal, Šefi i Onam. Sinovi Sibeonovi jesu Aja i Ana.",
			"41": "<sup>41</sup> Sin Anin jest Dišon. Sinovi Dišonovi jesu Hamram, Ešban, Jitran i Keran.",
			"42": "<sup>42</sup> Sinovi Ezerovi jesu Bilhan, Zaavan i Jakan. Sinovi Dišanovi jesu Uz i Aran,",
			"43": "<sup>43</sup> Slijede kraljevi, koji su vladali u zemlji edomskoj, prije nego zavlada kralj Izraelaca: Bela, sin Beorov; njegov stolni grad zvao se je Dinhaba,",
			"44": "<sup>44</sup> Kad umrije Bela, postade kralj mjesto njega Jobab, sin Zerahov iz Bosre.",
			"45": "<sup>45</sup> Kad umrije Jobab, postade kralj mjesto njega Hušam iz zemlje Temanićana.",
			"46": "<sup>46</sup> Kad umrije Hušam, postade kraj mjesto njega Hadad, sin Bedadov, koji pobi Midjance na visoravni moapskoj; njegov stolni grad zvao se je Avit.",
			"47": "<sup>47</sup> Kad umrije Hadad, postade kralj mjesto njega Samla iz Masreke.",
			"48": "<sup>48</sup> Kad umrije Samla, postade kralj mjesto njega Saul iz Rehobota na Rijeci.",
			"49": "<sup>49</sup> Kad umrije Saul, postade kralj mjesto njega Baal-Hanan, sin Akborov.",
			"50": "<sup>50</sup> Kad umrije Baal-Hanan, postade kralj mjesto njega Hadad; njegov stolni grad zvao se je Pai; njegova žena Mehetabela; ona je bila kći Matrede, kćeri Mezahabove",
			"51": "<sup>51</sup> Kad umrije Hadad, bili su knezovi u Edomu: knez Tamna, knez Alva, knez Jetet,",
			"52": "<sup>52</sup> Knez Oholibama, knez Ela, knez Pinon,",
			"53": "<sup>53</sup> Knez Kenaz, knez Teman, knez Mibsar,",
			"54": "<sup>54</sup> Knez Magdiel i knez Hiram. To su knezovi Edoma."
		},
		"2": {
			"1": "<sup>1</sup> Ovo su sinovi Izraelovi Ruben, Simeon, Levi, Juda, Isakar, Zebulun,",
			"2": "<sup>2</sup> Dan, Josip, Benjamin, Naftali, Gad i Ašer,",
			"3": "<sup>3</sup> Sinovi Judini jesu Er, Onan i Šelah, Ova mu se trojica rodiše od kćeri Šuine, Kananejke. Ali Er, prvorođenac Judin, bio je nevaljao pred Gospodom, te ga ubi.",
			"4": "<sup>4</sup> Njegova snaha Tamara rodi mu Peresa i Zeraha. Svega pet bilo je sinova Judinih.",
			"5": "<sup>5</sup> Sinovi Peresovi jesu Hesron i Hamul.",
			"6": "<sup>6</sup> Sinovi Zerahovi jesu Zimri, Etan, Heman, Kalkol i Dara, Svega pet.",
			"7": "<sup>7</sup> Sin Karmijev jest Akar, koji Izraela baci u nesreću, kad se ogriješi ukravši stvari proklete.",
			"8": "<sup>8</sup> Sin Etanov jest Azarija.",
			"9": "<sup>9</sup> Sinovi Hesronovi, što mu se rodiše, jesu Jerahmeel, Ram i Kelubaj.",
			"10": "<sup>10</sup> Ramu se rodi Aminadab, a Aminadabu se rodi Nahšon, kneza sinova Judinih.",
			"11": "<sup>11</sup> Nahšonu se rodi Salmon, a Salmonu se rodi Boaz.",
			"12": "<sup>12</sup> Boazu se rodi Obed, a Obedu se rodi Jesej.",
			"13": "<sup>13</sup> Jeseju se rodi prvorođenac svoj Eliaba, drugoga svojega sina Abinadaba, svojega trećega Šimeu,",
			"14": "<sup>14</sup> Svojega četvrtoga Netanela, svojega petoga Radaja,",
			"15": "<sup>15</sup> Svojega šestoga Osema i svojega sedmoga Davida",
			"16": "<sup>16</sup> Njihove sestre bile su Seruja i Abigaila. Sinovi Serujini jesu Abišaj, Joab i Asahel, trojica.",
			"17": "<sup>17</sup> Abigaila rodi Amasu, Otac Amasin bio je Išmaelac Jeter.",
			"18": "<sup>18</sup> Kaleb, sin Hesronov, rodi s Azubom, ženom svojom, i s Jeriotom sinove; ovo su sinovi njihovi: \"Ješer, Šobab i Ardon.",
			"19": "<sup>19</sup> Kad umrije Azuba, Kaleb se oženi Efratu. Ova mu rodi Hura.",
			"20": "<sup>20</sup> Huriu se rodi Uri, a Uriu se rodi Bezaleel.",
			"21": "<sup>21</sup> Potom je imao Hesron odnose s kćerju Makira, oca Gileadova. On se oženi njom, kad mu je bilo šezdeset godina. Ona mu rodi Seguba.",
			"22": "<sup>22</sup> Segubu se rodi Jair. Ovaj je imao dvadeset i tri grada u zemlji Gileadu.",
			"23": "<sup>23</sup> Gešureji i Sirijci oduzeše im šatorska sela Jairova, Kenat i mjesta njegova, šezdeset gradova. To su sve sinovi Makira, oca Gileadova.",
			"24": "<sup>24</sup> Poslije smrti Hesronove u Kaleb Efrati, Hesronova žena bila je Abija. Ona mu rodi Ašura, oca Tekoina.",
			"25": "<sup>25</sup> Sinovi Jerahmeela, prvorođenca Hesronova, bili su: prvorođenac Ram, pa Buna, Oren, Osem i Ahija.",
			"26": "<sup>26</sup> Jerahmeel je imao još drugu ženu po imenu Atara. Ona je bila mati Onamova.",
			"27": "<sup>27</sup> Sinovi Rama, prvorođenca Jerahmeelova, bili su Maas, Jamin i Eker.",
			"28": "<sup>28</sup> Sinovi Onamovi bili su Šamaj i Jada, sinovi Šamajevi Nadab i Abišur.",
			"29": "<sup>29</sup> Žena Abišurova zvala se je Abihaila. Ona mu rodi Ahbana i Molida.",
			"30": "<sup>30</sup> Sinovi Nadabovi jesu Seled i Apaim. Seled umrije bez djece.",
			"31": "<sup>31</sup> Sin Apaimov bio je Iši, od Išea Šešan, od Šešana Ahlaj,",
			"32": "<sup>32</sup> Sinovi Jade, brata Šamajeva, jesu Jeter i Jonatan. I Jeter umrije bez djece.",
			"33": "<sup>33</sup> Sinovi Jonatanovi jesu Pelet i Ziza. To su potomci Jerahmeelovi.",
			"34": "<sup>34</sup> Šešan nije imao sinova, nego samo kćeri. A imao je Šešan roba egipatskoga po imenu Jarhu.",
			"35": "<sup>35</sup> I dade Šešan robu svojemu Jarhi kćer svoju za ženu. Ona mu rodi Ataja.",
			"36": "<sup>36</sup> Ataju se rodi Natan, i Natanu se rodi Zabad.",
			"37": "<sup>37</sup> Zabadu se rodi Eflal, Eflalu se rodi Obed.",
			"38": "<sup>38</sup> Obedu se rodi Jehua, i Jehui se rodi Azarija.",
			"39": "<sup>39</sup> Azariji se rodi Heles, Helesu se rodi Elas,",
			"40": "<sup>40</sup> Eleasa se rodi Sismaj, i Sismaju se rodi Šalum.",
			"41": "<sup>41</sup> Šalumu se rodi Jekamja, i Jekamji se rodi Elišam.",
			"42": "<sup>42</sup> Potomci Kaleba, brata Jerahmeelova, jesu njegov prvorođenac Meša, to jest otac Zifov, i sinovi Mareše, oca Hebronova.",
			"43": "<sup>43</sup> Sinovi Hebronovi jesu Korah, Tapuah, Rekem i Šema.",
			"44": "<sup>44</sup> Šamaj se rodi Raham, oca Jorkoamova, i Rekemu se rodi Šamaj.",
			"45": "<sup>45</sup> Sin Šamajev bio je Maon, i Maon bio je otac Bet-Surov.",
			"46": "<sup>46</sup> Kalebova inoča Efa rodi Harana; Mozu i Gazeza. Haranu se rodi Gazez.",
			"47": "<sup>47</sup> Sinovi Johdajevi jesu Regem, Jotam, Gešan, Pelet, Efa i Šaaf.",
			"48": "<sup>48</sup> Kalebova inoča Maaka rodi Šebera i Tirhanu.",
			"49": "<sup>49</sup> Ona rodi Šaafa, oca Madmanina, Ševu, oca Makbenina, i oca Gibee. Kalebova sestra bila je Aksa.",
			"50": "<sup>50</sup> Ovo su potomci Kalebovi: sinovi Hura, prvorođenca Efratina, Šobal, otac Kirjat-Jearima,",
			"51": "<sup>51</sup> Salma, otac Betlehema, i Haref, otac Bet-Gadera.",
			"52": "<sup>52</sup> Šobal, otac Kirjat-Jearima, imao je za sinove Haroeha i polovicu Manuhoteja,",
			"53": "<sup>53</sup> Porodice Kirjat-Jearima, Jitreje, Puteje, Šumateje i Mišrateje. Od ovih vuku lozu Zoratateji i Eštaolateji.",
			"54": "<sup>54</sup> Sinovi Salmini jesu Betlehem i Netofaćani, Atrot-Bet-Joab i polovina Manahaćana, Zorateji,",
			"55": "<sup>55</sup> Porodice književnika, što stanuju u Jabezu, Tiraćani, Šimeaćani i Sukaćani. To su Keneji, što su potekli od Hamata, oca doma Rekabova."
		},
		"3": {
			"1": "<sup>1</sup> Ovo su bili sinovi Davidovi, što su mu se bili rodili u Hebronu: prvorođenac Amnon od Ahinoame iz Jezreela, drugi Daniel od Abigaile is Karmela,",
			"2": "<sup>2</sup> Treći Absalom, sin Maake, kćeri kralja Talmaja od Gešura, četvrti Adonija, sin Hagitin,",
			"3": "<sup>3</sup> Peti Šefatija od Abitale, šesti Jitream od njegove žene Egle.",
			"4": "<sup>4</sup> Šest mu se rodi u Hebronu. Ovdje je vladao sedam godina i šest mjeseci. Onda je vladao trideset i tri godine u Jerusalemu.",
			"5": "<sup>5</sup> U Jerusalemu rodiše mu se ovi: Šimea, Šobab, Natan i Salomon, četiri od Batšebe, kćeri Amielove,",
			"6": "<sup>6</sup> Nadalje Ibhar, Elišama, Elifelet,",
			"7": "<sup>7</sup> Nogah, Nefeg i Jafija,",
			"8": "<sup>8</sup> Elišama, Eliada, Elifelet, devet.",
			"9": "<sup>9</sup> To su svi sinovi Davidovi, osim sinova od inoča njegovih. Tamara je bila sestra njihova.",
			"10": "<sup>10</sup> Sin Salomonov bio je Roboam. Njegov je sin bio Abija, a njegov sin Asa, a njegov sin Jošafat,",
			"11": "<sup>11</sup> A njegov sin Joram, a njegov sin Ahazija, a njegov sin Joaš,",
			"12": "<sup>12</sup> A njegov sin Amazija, a njegov sin Azarija, a njegov sin Jotam,",
			"13": "<sup>13</sup> A njegov sin Ahaz, a njegov sin Ezekija, a njegov sin Manaseh,",
			"14": "<sup>14</sup> A njegov sin Amon, a njegov sin Josija,",
			"15": "<sup>15</sup> Sinovi Josijini jesu prvorođenac Johanan, drugi Jojakim, treći Zedekija, četvrti Šalum.",
			"16": "<sup>16</sup> Sinovi Jojakimovi jesu njegov sin Jehonija, i njegov sin Zedekija.",
			"17": "<sup>17</sup> Sinovi Jekonije, Zasužnjenoga, jesu njegov Asir, njegov sin Šealtiel,",
			"18": "<sup>18</sup> Malkiram, Pedaja, Šenasar, Jekamja, Hošama i Nedabija.",
			"19": "<sup>19</sup> Sinovi Pedajini jesu Zerubabel i Šimej. Sinovi Zerubabelovi jesu Mešulam i Hananija; Šelomita bila je sestra njihova;",
			"20": "<sup>20</sup> Nadalje Hašuba, Ohel, Berekija, Hasadja, Jušab-Hesed, njih pet.",
			"21": "<sup>21</sup> Sinovi Hananijini jesu Pelatja i Ješaja, sinovi Refajini, sinovi Arnanovi, sinovi Obadijini, sinovi Šekanijini.",
			"22": "<sup>22</sup> Sin Šekanijin jest Šemaja, sinovi Šemajini jesu Hatuš, Jigal, Barijah, Nearja i Šafat, njih šest.",
			"23": "<sup>23</sup> Sinovi Nearjini jesu Elioenaj, Ezekija i Azrikam, njih trojica.",
			"24": "<sup>24</sup> Sinovi Elioenajevi jesu Hodavja, Elijašib, Pelaja, Akub, Johanan, Delaja i Anani, njih sedmorica."
		},
		"4": {
			"1": "<sup>1</sup> Sinovi Judini jesu Peres, Hesron, Karmi, Hur i Šobal.",
			"2": "<sup>2</sup> Raji, sinu Šobalovu rodi se Jahat, a Jahatu se rodi Ahumaja i Lahada. To su porodice Zoratejaca.",
			"3": "<sup>3</sup> Ovo su od oca Etama: Jezreel, Išma i Idbaš. Sestra se njihova zvala Haslelponija.",
			"4": "<sup>4</sup> Penuel je bio otac Gedoru, i Ezer otac Hušin. To su sinovi Hura, prvorođenca Efrate, oca Betlehema.",
			"5": "<sup>5</sup> Ašur, otac Tekoe, imao je dvije žene, Helahu i Naarahu.",
			"6": "<sup>6</sup> Naaraha mu rodi Ahuzama, Hefera, Temenija i Ahaštara; to su sinovi Naarahini.",
			"7": "<sup>7</sup> Sinovi Halahini jesu Zeret, Zohar i Etnan.",
			"8": "<sup>8</sup> Kosu se rodi Anuba i Zobeba i porodice Aharhela, sina Harumova,",
			"9": "<sup>9</sup> Jabez je bio ugledniji od svoje braće. Njegova mati nadjenu mu ime Jabez, kad reče: \"Rodila ga s bolom.\"",
			"10": "<sup>10</sup> Jabez prizva Boga Izraelova i reče: \"Ako me blagosloviš i raširiš područje moje, pomogneš mi i milostiv me očuvaš od zla, da me ne stigne bol.\" I Bog usliši molbu njegovu.",
			"11": "<sup>11</sup> Kelubovu, bratu Šuhinu, se rodi Mehira, ovome se rodi Ešton.",
			"12": "<sup>12</sup> Eštonu se rodi Bet-Rafa, Paseah i Tehina, oca gradu Ir-Nahaša. To su ljudi Rekini.",
			"13": "<sup>13</sup> Sinovi Kenazovi jesu Otniel i Seraja, sin Otnielov jest Hatat.",
			"14": "<sup>14</sup> Meonotaju se rodi Ofru, Seraja rodi Joaba, oca dolini zanatlijskoj; jer su bili zanatlije.",
			"15": "<sup>15</sup> Sinovi Kaleba, sina Jefuneova, jesu Iru, Ela i Naam, sin Elin Kenaz.",
			"16": "<sup>16</sup> Sinovi Jehalelelovi jesu Zif, Zifa, Tirja i Asrael.",
			"17": "<sup>17</sup> Sinovi Ezrini jesu Jeter, Mered, Efer i Jalon; a žena Meredova, Egipćanka, rodi Mirjamu, Šamaja i Išbaha, oca Eštemou.",
			"18": "<sup>18</sup> Njegova druga žena, Judejka, rodi Jereda, oca Gedoru, Hebera, oca Sokova, i Jekutiela, oca Zanoahova, To su sinovi Bitje, kćeri faraonove, koju je bio Mered uzeo za ženu.",
			"19": "<sup>19</sup> Sinovi žene Hodije, sestre Nahama, jesu otac Keilah, Garmeja, i Eštemeja, Maakateja.",
			"20": "<sup>20</sup> Sinovi Šimonovi jesu Amnon, Ben-Hananov sin Rina i Tilon. Sinovi Išijevi jesu Zohet i sin Zohetov.",
			"21": "<sup>21</sup> Sinove Šele, sina Judina, jesu Er, otac Lekin, i Laada, otac Marešin, i porodice platnarskih radnika kuće Ašbeine,",
			"22": "<sup>22</sup> Nadalje Jokim i ljudi od Kozebe i Joaš i Saraf, koji postadoše gospodari Moaba i onda se povratiše u Jašubi-Lahemu. Ali to su stare povijesti.",
			"23": "<sup>23</sup> Oni su bili lončari i stanovali su u Netaimu i Gederah; tamo su boravili kod kralja u službi njegovoj.",
			"24": "<sup>24</sup> Sinovi Simeonovi jesu Nemuel, Jamin, Jarib, Zerah i Šaul.",
			"25": "<sup>25</sup> A njegov sin Šalum, a njegov sin Mibsam, a njegov sin Mišma.",
			"26": "<sup>26</sup> Potomci Mišmini jesu njegov sin Hamuel, i njegov sin Zakur, i njegov sin Šimej.",
			"27": "<sup>27</sup> Šimej je imao šesnaest sinova i šest kćeri. A braća njegova nijesu imala mnogo djece. Sve porodice njihove nijesu se množile toliko, koliko sinovi Judini.",
			"28": "<sup>28</sup> Svoja naselja imali su u Beeršebi, Moladi, Hasar-Šualu,",
			"29": "<sup>29</sup> U Bilhi, Esemu, Toladu,",
			"30": "<sup>30</sup> U Betuelu, Hormi, Siklagu,",
			"31": "<sup>31</sup> U Bet-Markabotu, Hasar-Susimu, Bet-Biraju i Šaarajimu. To su gradovi njihovi do vremena kralja Davida.",
			"32": "<sup>32</sup> Sela njihova bila su Etam, Ain, Rimon, Token i Ašan: \"pet mjesta,",
			"33": "<sup>33</sup> Nadalje sva sela njihova, što su bila u okolici spomenutih mjesta, sve do Baala. To su bila njihova naselja. Imali su svoj vlastiti popis porodica.",
			"34": "<sup>34</sup> Mešobad, Jamlek, Amazijin sin Joša,",
			"35": "<sup>35</sup> Joel, Jehu, sin Jošibje, sina Seraje, sina Asielova,",
			"36": "<sup>36</sup> Elioenaj, Jaakoba, Ješohaja, Asaja, Adiel, Jesimiel, Benaja",
			"37": "<sup>37</sup> I Ziza, sin Šifija, sina Alona, sina Jedaje, sina Šimrijeva, sina Šemajina:",
			"38": "<sup>38</sup> Ovi po imenu navedeni bili su knezovi u porodicama svojim. Obitelji njihove bile su se jako raširile.",
			"39": "<sup>39</sup> Oni otidoše do ulaza u Gedor, do na istok doline, da traže pašu stoci svojoj.",
			"40": "<sup>40</sup> I nađoše pašu obilatu i dobru. Zemlja je pružala na sve strane dosta prostora i mirnu, bezbrižnu sigurnost; jer koji su prije prebivali tamo, bili su Hamovi potomci.",
			"41": "<sup>41</sup> U vrijeme Ezekije, kralja Judina, dođoše po imenu gore zapisani, uništiše šatore njihove i Meuneje, koji su živjeli tamo, i izvršiše na njima prokletstvo do današnjega dana. Naseliše se mjesto njih; jer je tamo bilo pašnjaka za stoku njihovu.",
			"42": "<sup>42</sup> Jedan dio njih, sinova Simeonovih, pet stotina ljudi otide u goru Seir. Vodili su ih Pelatija, Nearja, Refaja i Uziel, sinovi Išijevi.",
			"43": "<sup>43</sup> Oni pobiše posljednje ostatke Amalečana i naseliše se ondje do današnjega dana."
		},
		"5": {
			"1": "<sup>1</sup> Sinovi Rubena, prvorođenca Izraelova, - jer je on bio prvorođenac. Ali jer je on bio oskvrnio postelju oca svojega, prijeđe pravo njegova prvorođenja na sinove Josipa, sina Izraelova, ali ne tako da se on u rodoslovlju broji kao prvorođenac.",
			"2": "<sup>2</sup> Jer Juda je imao prvenstvo među braćom svojom: od njega je bio uzet jedan za kneza, a pravo prvorođenja dođe na Josipa -",
			"3": "<sup>3</sup> Sinovi Rubena, prvorođenca Izraelova, jesu Henok, Palu, Hesron i Karmi.",
			"4": "<sup>4</sup> Sinovi Joelovi jesu njegov sin Šemaja, a njegov sin Gog, a njegov sin Šimej,",
			"5": "<sup>5</sup> A njegov sin Mikah, a njegov sin Reaja, a njegov sin Baal,",
			"6": "<sup>6</sup> A njegov sin Beera, kojega odvede u sužanjstvo asirski kralj Tiglat-Pileser; on je bio knez Rubenovaca.",
			"7": "<sup>7</sup> Braća njegova po porodicama svojim, kad su se unosila u popis porodični, jesu: prvi Jeiel, Zaharija,",
			"8": "<sup>8</sup> Bela, sin Azaza, sina Šeme, sina Joelova. Ovaj je stanovao u Aroeru i do Neba i Baal-Meona.",
			"9": "<sup>9</sup> Na istok nastanio se je do ulaza u pustinju, što se proteže od Eufrata, jer su imali u Gileadu mnogu stoku.",
			"10": "<sup>10</sup> U vrijeme Saulovo zaratiše se s Hagrejima. Kad su ovi bili od njih pobijeni, naseliše se u šatorima njihovim po svem istočnom kraju Gileada.",
			"11": "<sup>11</sup> Sinovi Gadovi stanovali su naprema njima u Bašanu do Salke:",
			"12": "<sup>12</sup> Joel, poglavar, Šafan, drugi, Janaj i Šafat u Bašanu.",
			"13": "<sup>13</sup> Braća njihova po obiteljima svojim jesu Mihael. Mešulam, Šeba, Joraj, Jakan, Zija i Eber: njih sedmorica.",
			"14": "<sup>14</sup> To su bili sinovi Abihaila, sina Hurija, sina Jaroaha, sina Gileada, sina Mihaela, sina Ješišaja, sina Jahdoa, sina Buzova,",
			"15": "<sup>15</sup> Ahi, sin Abdiela, sina Gunija; bio je poglavar njihovim obiteljima.",
			"16": "<sup>16</sup> Stanovali su u Gileadu, u Bašanu i u njihovim selima i na svim pašnjacima Šarona do međa njihovih.",
			"17": "<sup>17</sup> Oni svi bili su uneseni u rodovnike za vremena Jotama, kralja Judina, i za vremena Jeroboama, kralja Izraelova.",
			"18": "<sup>18</sup> Rubenovi, Gadovi i polovina plemena Manasehova - bili su hrabri ljudi, koji su nosili štit i mač, zatezali luk i bili vješti boju, četrdeset i četiri tisuće i sedam stotina i šezdeset sposobnih za ratnu službu -",
			"19": "<sup>19</sup> Oni su ratovali s Hagrejima i s Jeturom, Nafišom i Nodabom,",
			"20": "<sup>20</sup> Oni nađoše u boju proti njima pomoć, i Hagareji sa svojim saveznicima padoše u njihove ruke. U boju naime bili su zavapili Gospodu, i on ih usliši, jer se pouzdaše u njega.",
			"21": "<sup>21</sup> Zaplijeniše stoku njihovu: pedeset tisuća deva, dvjesta i pedeset tisuća ovaca i dvije tisuće magaraca i sto tisuća ljudi.",
			"22": "<sup>22</sup> Jer su bili pali mnogi pogođeni, jer je to bio rat od Boga podupiran. I oni se naseliše na mjestu njihovu sve do odvedenja u progonstvo.",
			"23": "<sup>23</sup> Pripadnici polovine plemena Manasehova stanovali su u zemlji od Bašana do Baal-Hermona i do Senira i gore Hermona. Njihov broj bio je velik.",
			"24": "<sup>24</sup> Ovo su bili poglavari njihovih obitelji: Efer, Jiši, Eliel, Azriel, Jeremija, Hodavja i Jahdiel, hrabri ratnici, slavni ljudi, poglavari u svojim obiteljima.",
			"25": "<sup>25</sup> Ali su bili nevjerni Bogu otaca svojih i činili su idolsku službu naroda one zemlje, koje je Bog bio istrijebio ispred njih.",
			"26": "<sup>26</sup> Tada pobudi Bog Izraelov jarost asirskoga kralja Pula i asirskoga kralja Tiglat-Pilesera. Ovaj odvede u progonstvo Rubenovce, Gadovce i polovinu plemena Manasehova i dovede ih u Halahu, Habor, Haru i na rijeku Gozan do današnjega dana."
		},
		"6": {
			"1": "<sup>1</sup> Sinovi Levijevi jesu Geršon, Kohat i Merari.",
			"2": "<sup>2</sup> Sinovi Kohatovi jesu Amram, Izhar, Hebron i Uziel.",
			"3": "<sup>3</sup> Sinovi Amramovi jesu Aron, Mojsije i Mirjama. Sinovi Aronovi jesu Nadab, Abihu, Eleazar i Itamar.",
			"4": "<sup>4</sup> Eleazaru se rodi Finehasa, Finehasu se rodi Abišua,",
			"5": "<sup>5</sup> Abišuu se rodi Buki, Bukiu se rodi Uzi,",
			"6": "<sup>6</sup> Uziu se rodi Zerahija, Zerahiji se rodi Merajot,",
			"7": "<sup>7</sup> Merajotu se rodi Amarija, Amariju se rodi Ahitub,",
			"8": "<sup>8</sup> Ahitubu se rodi Sadok, Sadoku se rodi Ahimaasa,",
			"9": "<sup>9</sup> Ahimaasu se rodi Azarija, Azariji se rodi Johanan,",
			"10": "<sup>10</sup> Johananu se rodi Azarija. Ovaj je vršio svećeničku službu u hramu, što ga sagradi Salomon u Jerusalemu.",
			"11": "<sup>11</sup> I Azariji se rodi Amarija, Amariju se rodi Ahituba,",
			"12": "<sup>12</sup> Ahitubu se rodi Sadoka, Sadoku se rodi Šalum,",
			"13": "<sup>13</sup> Šalumu se rodi Hilkija, Hilkiju se rodi Azarija,",
			"14": "<sup>14</sup> Azariji se rodi Seraja, Seraji se rodi Josadak.",
			"15": "<sup>15</sup> Josadak otide, kad Gospod odvede u progonstvo Judu i Jerusalem rukom Nebukadnezarovom.",
			"16": "<sup>16</sup> Sinovi Levijevi jesu Geršon, Kohat i Merari,",
			"17": "<sup>17</sup> Ovo su imena sinovima Geršonovim: Libni i Šimej.",
			"18": "<sup>18</sup> Sinovi Kohatovi jesu Amram, Izhar, Hebron i Uziel.",
			"19": "<sup>19</sup> Sinovi Merarijevi jesu Mahli i Muši. Ovo su porodice levitske po svojim obiteljima:",
			"20": "<sup>20</sup> Geršonova: \"Libni, sin njegov, a njegov sin Jahat, a njegov sin Zima,",
			"21": "<sup>21</sup> A njegov sin Joah, a njegov sin Ido, a njegov sin Zerah, a njegov sin Jeatraj.",
			"22": "<sup>22</sup> Sinovi Kohatovi: \"Aminadab, sin njegov, a njegov sin Korah, a njegov sin Asir,",
			"23": "<sup>23</sup> A njegov sin Elkana, a njegov sin Ebjasaf, a njegov sin Asir,",
			"24": "<sup>24</sup> A njegov sin Tahat, a njegov sin Uriel, a njegov sin Uzijah, a njegov sin Šaul.",
			"25": "<sup>25</sup> Sinovi Elkanini: Amasa i Ahimot,",
			"26": "<sup>26</sup> Elkana. Sinovi Elkanini: Zofaj, sin njegov, a njegov sin Nahat,",
			"27": "<sup>27</sup> A njegov sin Eliab, a njegov sin Jeroham, a njegov sin Elkana.",
			"28": "<sup>28</sup> Sinovi Samuelovi: prvorođenac Joel i drugi Abija,",
			"29": "<sup>29</sup> Sinovi Merarijevi: Mahli, a njegov sin Libni, a njegov sin Šimej, a njegov sin Uza,",
			"30": "<sup>30</sup> A njegov sin Šimea, a njegov sin Hagija, a njegov sin Asaja.",
			"31": "<sup>31</sup> Ovo su oni, koje postavi David za pjevanje u hramu Gospodnjem, izakako se je bio smjestio kovčeg.",
			"32": "<sup>32</sup> Oni su vršili službu pred šatorom svjedočanstva kao pjevači, dok nije bio Salomon sagradio hram Gospodnji u Jerusalemu. Po svojim službenim propisima oni su vršili službu svoju.",
			"33": "<sup>33</sup> Ovi su, što su vršili službu, i sinovi njihovi od sinova Kohatovih Heman, hramski pjevač, sin Joela, sina Samuela,",
			"34": "<sup>34</sup> Sina Elkane, sina Jerohama, sina Eliela, sin Toaha,",
			"35": "<sup>35</sup> Sina Zufa, sina Elkane, sina Mahata, sina Amasa,",
			"36": "<sup>36</sup> Sina Elkane, sina Joela, sina Azarije, sina Sefanije,",
			"37": "<sup>37</sup> Sina Tahata, sina Asira, sina Abjasafa, sina Koraha,",
			"38": "<sup>38</sup> Sina Izhara, sina Kohata, sina Levija, sina Izraelova.",
			"39": "<sup>39</sup> Njegov brat bio je Asaf, koji je vršio službu s desne njegove, Asaf, sin Berekija, sina Šimee,",
			"40": "<sup>40</sup> Sina Mihaela, sina Baaseja, sina Malkije,",
			"41": "<sup>41</sup> Sina Etnija, sina Zerahe, sina Adaje,",
			"42": "<sup>42</sup> sina Etana, sina Zime, sina Šimeja,",
			"43": "<sup>43</sup> Sina Jahata, sina Geršoma, sina Levijeva.",
			"44": "<sup>44</sup> Sinovi Merarijevi Braća njihova, stajali su s lijeve strane: Etan, sin Kišija, sina Abdija, sina Maluka,",
			"45": "<sup>45</sup> Sina Hašabje, sina Amazije, sina Hilkije,",
			"46": "<sup>46</sup> Sina Amazija, sina Bania, sina Šamera,",
			"47": "<sup>47</sup> Sina Mahlija, sina Mušia, sina Merarija, sina Levijeva,",
			"48": "<sup>48</sup> Drugovi njihovi, Leviti, bili su određeni za svu službu u šatoru hrama Božjega.",
			"49": "<sup>49</sup> A Aron i sinovi njegovi vršili su službu na žrtveniku za žrtve paljenice i na žrtveniku kadionom, svu službu u Svetinji nad svetinjama i službu pomirenja za Izraela točno po propisima, što ih je bio dao Mojsije, sluga Božji.",
			"50": "<sup>50</sup> Ovo su potomci Aronovi: Eleazar, sin njegov, a njegov sin Finehas, a njegov sin Abišua,",
			"51": "<sup>51</sup> A njegov sin Buki, a njegov sin Uzi, a njegov sin Zerahija,",
			"52": "<sup>52</sup> A njegov sin Merajot, a njegov sin Amarija, a njegov sin Ahitub,",
			"53": "<sup>53</sup> A njegov sin Sadok, a njegov sin Ahimaas.",
			"54": "<sup>54</sup> Ovo su im stanovi po mjestima njihovim u području njihovu: Aronovi, porodica Kohatovaca, - na njih pade ždrijeb -",
			"55": "<sup>55</sup> Dobiše Hebron u zemlji Judinoj i pašnjake njegove unaokolo.",
			"56": "<sup>56</sup> Međe grada i sela njegova dadoše Kalebu Jefuneovu.",
			"57": "<sup>57</sup> Nadalje dadoše Aronovim sinovima gradove za utočište Hebron i Libnu i pašnjake njezine, Jatir i Eštemou i pašnjake njegove,",
			"58": "<sup>58</sup> Hilen i pašnjake njegove, Debir i pašnjake njegove,",
			"59": "<sup>59</sup> Ašan i pašnjake njegove i Bet-Šemeš i pašnjake njegove.",
			"60": "<sup>60</sup> Nadalje od plemena Benjaminova: Gebu i pašnjake njegove, Alemet i pašnjake njegove i Anatot i pašnjake njegove. Ukupni broj gradova njihovih iznosio je trinaest po porodicama njihovim.",
			"61": "<sup>61</sup> Kohatove porodice, koje su bile još preostale od porodica toga plemena, dobiše ždrijebom od polovine plemena Manasehova deset gradova.",
			"62": "<sup>62</sup> Sinovi Geršonovi dobiše po porodicama svojim od plemena Isakar, Ašer, Naftali i Manaseh u Bašanu trinaest gradova.",
			"63": "<sup>63</sup> Merarijevi dobiše ždrijebom po porodicama svojim od plemena Ruben, Gad i Zebulun dvanaest gradova.",
			"64": "<sup>64</sup> Tako dadoše sinovi Izraelovi Levitima te gradove i pašnjake njihove.",
			"65": "<sup>65</sup> Ždrijebom odstupiše od plemena Judejaca, Simeonovaca i Benjaminovaca te po imenu označene gradove.",
			"66": "<sup>66</sup> Ostatku od porodica Kohatovaca bili su odstupljeni gradovi njihova područja od plemena Efraimova.",
			"67": "<sup>67</sup> Njima dadoše gradove za utočište Šekem i pašnjake njegove u gori Efraimovoj, Gezer i pašnjake njegove,",
			"68": "<sup>68</sup> Jokmeam i pašnjake njegove, Bet-Horon i pašnjake njegove,",
			"69": "<sup>69</sup> Ajalon i pašnjake njegove, Gat-Rimon i pašnjake njegove.",
			"70": "<sup>70</sup> Od polovine plemena Manasehova: Aner i pašnjake njegove, Balaam i pašnjake njegove - za porodice preostalih Kohatovaca.",
			"71": "<sup>71</sup> Geršonovi sinovi dobiše od porodica polovine plemena Manasehova: Golan i Bašan i pašnjake njegove i Aštarot i pašnjake njegove.",
			"72": "<sup>72</sup> Od plemena Isakarova: Kedeš i pašnjake njegove, Daberat i pašnjake njegove,",
			"73": "<sup>73</sup> Ramot i pašnjake njegove i Anem i pašnjake njegove.",
			"74": "<sup>74</sup> Od plemena Ašerova: Mašal i pašnjake njegove, Abdon i pašnjake njegove,",
			"75": "<sup>75</sup> Hukok i pašnjake njegove, Rehob i pašnjake njegove.",
			"76": "<sup>76</sup> Od plemena Naftalijeva: Kedeš u Galileji i pašnjake njegove, Hamon i pašnjake njegove, Kirjatajim i pašnjake njegove.",
			"77": "<sup>77</sup> Merarijevi sinovi, što još preostaše, dobiše od plemena Zebulunova: Rimon i pašnjake njegove, Tabor i pašnjake njegove.",
			"78": "<sup>78</sup> Nadalje s onu stranu Jordana naprema Jerihu u istočnoj zemlji jordanskoj od plemena Rubenova: Bezer u pustinji i pašnjake njegove, Jahasu i pašnjake njezine,",
			"79": "<sup>79</sup> Kedemot i pašnjake njegove, Mefaat i pašnjake njegove.",
			"80": "<sup>80</sup> Od plemena Gadova: Ramot u Gileadu i pašnjake njegove, Mahanaim i pašnjake njegove",
			"81": "<sup>81</sup> Hešbon i pašnjake njegove, Jazer i pašnjake njegove."
		},
		"7": {
			"1": "<sup>1</sup> Sinovi Isakarovi jesu Tola, Puah, Jašub i Šimron: četvorica",
			"2": "<sup>2</sup> Sinovi Tolini jesu Uzi, Refaja, Jeriel, Jahmaj, Jibsam i Samuel, poglavari svojih obitelji od Tole, hrabri ljudi po porodicama svojim. U vrijeme Davidovo bilo ih je na broj dvadeset i dvije tisuće i šest stotina.",
			"3": "<sup>3</sup> Potomci Uzijevi jesu Izrahija, od Izrahije vuku lozu Mihael, Obadija, Joel i Jišija, svega ih pet poglavara.",
			"4": "<sup>4</sup> Njima su pripadali po porodicama njihovim, po obiteljima njihovim za boj spremne ratničke čete: trideset i šest tisuća ljudi. Oni su naime imali mnogo žena i djece.",
			"5": "<sup>5</sup> Njihove braće u svim porodicama Isakarovim bili su hrabri ratnici. Bilo ih svega na broj osamdeset i sedam tisuća.",
			"6": "<sup>6</sup> Sinovi Benjaminovi jesu Bela, Beker i Jediael: njih trojica.",
			"7": "<sup>7</sup> Sinovi Belini jesu Esbon, Uzi, Uziel, Jerimot i Iri, pet obiteljskih poglavara, hrabri ratnici. Bilo ih je na broj dvadeset i dvije tisuće i trideset i četiri.",
			"8": "<sup>8</sup> Sinovi Bekerovi jesu Zemira, Joaš, Eliezer, Elioenaj, Omri, Jerimot, Abija, Anatot i Alemet: svi su to sinovi Bekerovi.",
			"9": "<sup>9</sup> Izbrojenih po porodicama svojim, po obiteljskim poglavarima, po hrabrim ratnicima bilo ih je dvadeset tisuća i dvjesta.",
			"10": "<sup>10</sup> Sin Jediaelovi jest Bilhan. Sinovi Bilhanovi jesu Jeuš, Benjamin, Ehud, Kenaanah, Zetan, Taršiš i Ahišahar.",
			"11": "<sup>11</sup> Sve su to sinovi Jediaelovi po obiteljskim poglavarima, valjani ratnici: sedamdeset tisuća i dvjesta sposobnih za ratnu službu.",
			"12": "<sup>12</sup> Šupim i Hupim jesu sinovi Irovi, Hušim sinovi Aherovi.",
			"13": "<sup>13</sup> Sinovi Naftalijevi jesu Jahaziel, Guni, Jezer i Šalum, potomci Belini,",
			"14": "<sup>14</sup> Sinovi Manasehovi jesu Asriel, kojega mu žena rodi; inoča njegova Sirijka rodi Makira, oca Gileadova.",
			"15": "<sup>15</sup> Makir oženi sinove svoje Hupima i Šupima. Sestra njegova zvala se je Maaka. Drugi se je zvao Selofhad. Selofhad je imao kćeri.",
			"16": "<sup>16</sup> Maaka, žena Makirova, rodi sina. Ona ga prozva Pereš, Brat njegov zvao se Šareš. Njegovi sinovi Ulam i Rakem.",
			"17": "<sup>17</sup> Sin Ulamov jest Bedan. To su sinovi Gileada, sina Makira, sina Manasehova.",
			"18": "<sup>18</sup> Sestra njegova Hamoleketa rodi Išhoda, Abiezera i Mahlahu.",
			"19": "<sup>19</sup> A sinovi Šemidini bili su Ahian, Šekem, Likhi i Aniam.",
			"20": "<sup>20</sup> Potomci Efraimovi jesu Šutelah, a njegov sin Bared, a njegov sin Tahat, a njegov sin Elada, a njegov sin Tahat,",
			"21": "<sup>21</sup> A njegov sin Zabad, a njegov sin Šutelah, Ezer i Elad. Ljudi is Gata, domoroci zemlje, ubiše ih, jer su bili izašli, da im ugrabe stoku.",
			"22": "<sup>22</sup> Njihov otac Efraim tugovao je dugo za njima, i dođoše braća njegova da ga tješe.",
			"23": "<sup>23</sup> Tada uđe k ženi svojoj, i ona zatrudnje i rodi sina. On ga nazva Berija, jer se to dogodi u nesreći kuće njegove.",
			"24": "<sup>24</sup> Kći njegova bila je Šeera. Ona sagradi Donji i Gornji Bet-Horon i Uze-Šeeru.",
			"25": "<sup>25</sup> Sin njegov bio je Refah, i Rešef, a njegov sin Telah, a njegov sin Tahan,",
			"26": "<sup>26</sup> A njegov sin Laadan, a njegov sin Amihud, a njegov sin Elišama,",
			"27": "<sup>27</sup> A njegov sin Nun, a njegov sin Jošua.",
			"28": "<sup>28</sup> Njihov nasljedni posjed i njihovi stanovi jesu: Betel i sela njegova, na istok Naaran, na zapad Gezer i sela njegova, Šekem i sela njegova do Gaze i sela njezinih.",
			"29": "<sup>29</sup> U rukama Manasehovaca bili su Bet-Šean i sela njegova, Taanak i sela njegova, Megido i sela njegova, Dor i sela njegova. Ovdje su stanovali sinovi Josipa, sina Izraelova.",
			"30": "<sup>30</sup> Sinovi Ašerovi jesu Jimna, Jišva, Jišvi, Berija i Serah, sestra njihova.",
			"31": "<sup>31</sup> Sinovi Berijini jesu Heber i Malkiel. Ovaj je otac Birzajitov.",
			"32": "<sup>32</sup> Heberu se rodi Jaflet, Šomer, Hotam i sestra njihova Šua.",
			"33": "<sup>33</sup> Sinovi Jafletovi jesu Pasak, Bimhal i Ašvat; to su sinovi Jafletovi.",
			"34": "<sup>34</sup> Sinovi Šomerovi jesu Ahi, Rohga, Huba i Aram,",
			"35": "<sup>35</sup> Sinovi njegova brata Helema jesu Zofah, Jimna, Šeleš i Amal.",
			"36": "<sup>36</sup> Sinovi Zofahovi jesu Suah, Harnefer, Šual, Beri i Jimra,",
			"37": "<sup>37</sup> Bezer, Hod, Šama, Šilša, Jitran i Bera.",
			"38": "<sup>38</sup> Sinovi Jeterovi jesu Jefune, Pispa i Ara.",
			"39": "<sup>39</sup> Sinovi Ulini jesu Arah, Haniel i Risja,",
			"40": "<sup>40</sup> Svi su ovi sinovi Ašerovi, poglavari svojih obitelji, izabrani hrabri ratnici, glavari među knezovima. Bilo ih je za vojsku na broj dvadeset i šest tisuća ljudi,"
		},
		"8": {
			"1": "<sup>1</sup> Benjaminu se rodi svoj prvorođenac Bela, drugoga Ašbela, trećega Ahraha,",
			"2": "<sup>2</sup> Četvrtoga Nohu i petoga Rafu.",
			"3": "<sup>3</sup> Sinovi Belini bili su Adar, Gera, Abihud,",
			"4": "<sup>4</sup> Abišua, Naaman, Ahoah,",
			"5": "<sup>5</sup> Gera, Šefufan i Huram.",
			"6": "<sup>6</sup> Ovo su sinovi Ehudovi: - poglavari u obiteljima, što su živjeli u Gebi, i oni ih preseliše u Manahat - :",
			"7": "<sup>7</sup> Naaman, Ahija i Gera; ovaj je, koji ih preseli, i njemu se rodi Uzu i Ahihud.",
			"8": "<sup>8</sup> Šaharaimu se rode sinove u zemlji moapskoj, izakako je bio otpustio svoje žene Hušimu i Baru,",
			"9": "<sup>9</sup> I to sa svojom ženom Hodešom rodi Jobaba, Sibiju, Mešu, Malkama,",
			"10": "<sup>10</sup> Jeusa, Sakju i Mirmu. To su sinovi njegovi, poglavari u obiteljima.",
			"11": "<sup>11</sup> S Hušimom je bio rodio Abituba i Elpaala.",
			"12": "<sup>12</sup> Sinovi Elpaalovi jesu Eber, Mišam i Šamed. Ovaj sagradi Ono i Lod i sela njegova.",
			"13": "<sup>13</sup> Berija i Šema jesu poglavari obiteljski stanovnika u Ajalonu. Oni istjeraše stanovnike gatske.",
			"14": "<sup>14</sup> Ahio, Šašak, Jerimot,",
			"15": "<sup>15</sup> Zebadija, Arad, Eder,",
			"16": "<sup>16</sup> Mihael, Ispah i Joha jesu sinovi Berijini.",
			"17": "<sup>17</sup> Zebadija, Mešulam, Hizki, Heber,",
			"18": "<sup>18</sup> Jišmeraj, Jizlia i Jobab jesu sinovi Elpaalovi.",
			"19": "<sup>19</sup> Jakim, Zikri, Zabdi,",
			"20": "<sup>20</sup> Elijenaj, Ziletaj, Eliel,",
			"21": "<sup>21</sup> Adaja, Beraja i Šimrat jesu sinovi Šimejevi.",
			"22": "<sup>22</sup> Jišpan, Eber, Eliel,",
			"23": "<sup>23</sup> Abdon, Zikri, Hanan,",
			"24": "<sup>24</sup> Hananija, Elam, Antotija,",
			"25": "<sup>25</sup> Jifdeja i Penuel jesu sinovi Šašakovi",
			"26": "<sup>26</sup> Šamšeraj, Šeharja, Atalija,",
			"27": "<sup>27</sup> Jaarešja, Elija i Zikri jesu sinovi Jerohamovi,",
			"28": "<sup>28</sup> To su poglavari obiteljski po porodicama svojim. Kao glavari stanovali su u Jerusalemu.",
			"29": "<sup>29</sup> U Gibeonu stanovali su otac Gibeona - žena njegova zvala se je Maaka.",
			"30": "<sup>30</sup> Njegov prvorođeni sin Abdon, pa Zur, Kiš, Baal, Nadab,",
			"31": "<sup>31</sup> Gedor, Ahio i Zaker.",
			"32": "<sup>32</sup> Miklot, kojemu se rodi Šimea. I ovi su stanovali kod braće svoje u Jerusalemu, naprema braći svojoj.",
			"33": "<sup>33</sup> Neru se rodi Kiša, Kišu se rodi Saul, Saulu se rodi Jonatan, Malki-Šua, Abinadaba i Eš-Baala.",
			"34": "<sup>34</sup> Sin Jonatanov jest Merib-Baal; Merib-Baalu se rodi Mika.",
			"35": "<sup>35</sup> Sinovi Mikini jesu Piton, Melek, Tarea i Ahaz.",
			"36": "<sup>36</sup> Ahazu se rodi Jehoad, Jehoadu se rodi Alemeta, Azmaveta se i Zimria. Zamriu se rodi Moza,",
			"37": "<sup>37</sup> Mozu se rodi Bineu, A njegov sin jest Rafa, a njegov sin Eleasa, a njegov sin Asel.",
			"38": "<sup>38</sup> Asel je imao šest sinova. Oni su se zvali Azrikam, Bokru, Išmael, Šearja, Obadija i Hanan; sve su to sinovi Aselovi.",
			"39": "<sup>39</sup> Sinovi njegova brata Ešeka jesu njegov prvorođenac Ulam, drugi Jehuš i treći Elifelet.",
			"40": "<sup>40</sup> Sinovi Ulamovi bili su valjani ratnici, koji su mogli natezati luk. I imali su mnogo sinova i unuka, sto i pedeset, Svi su ovi sinovi Benjaminovi."
		},
		"9": {
			"1": "<sup>1</sup> Svi Izraelci bili su zapisani u rodoslovlje. Zapisani su u knjizi o kraljevima Izraelovim. Judejci su bili odvedeni u progonstvo u Babilon zbog bezakonja svojih.",
			"2": "<sup>2</sup> Prvi stanovnici, koji su tada živjeli na vlastitom posjedu u gradovima svojim, bili su Izraelci, svećenici, Leviti i Netineji.",
			"3": "<sup>3</sup> U Jerusalemu stanovali su od sinova Judinih, od sinova Benjaminovih, od sinova Efraimovih i od sinova Manasehovih.",
			"4": "<sup>4</sup> Utaj, sin Amihuda, sina Omrija, sina Imrija, sina Bania, od potomaka Peresa, sina Judina.",
			"5": "<sup>5</sup> Nadalje od Šilonita prvorođenac Asaja i sinovi njegovi.",
			"6": "<sup>6</sup> Od sinova Zerahovih Jeuel i braća njihova: šest stotina i devedeset.",
			"7": "<sup>7</sup> Od sinova Benjaminovih: Salu, sin Mešulama, sina Hodavje, sina Hasenuina,",
			"8": "<sup>8</sup> Nadalje Ibneja, sin Jerohamov, i Ela, sin Uzije, sina Mikrijeva, i Mešulam, sin Šefatije, sina Reuela, sina Ibnijina,",
			"9": "<sup>9</sup> I braća njihova po porodicama svojim: devet stotina i pedeset i šest. Svi ovi ljudi bili su obiteljski poglavari u svojim obiteljima.",
			"10": "<sup>10</sup> Od svećenika: Jedaja, Jehojarib, Jakin",
			"11": "<sup>11</sup> I Azarija, sin Hilkije, sina Mešulama, sina Sadoka, sina Merajota, sina Ahituba, kneza u hramu Božjem;",
			"12": "<sup>12</sup> Nadalje Adaja, sin Jerohama, sina Pašhura, sina Malkijina, i Masaj, sin Adiela, sina Jahzere, sina Mešulama, sina Mešilemita, sina Imerova,",
			"13": "<sup>13</sup> I braća njihova, poglavari svojih obitelji: tisuća i sedam stotina i šezdeset ljudi, ljudi valjanih u službi kod hrama Božjega",
			"14": "<sup>14</sup> Od Levita: Šemaja, sin Hašuba, sina Azrikama, sina Hašabjina, od sinova Merarijevih,",
			"15": "<sup>15</sup> Bakbakar, Heres, Galal, Matanija, sin Mike, sina Zikrija, sina Asafova,",
			"16": "<sup>16</sup> Obadija, sin Šemaje, sina Galala, sina Jedutunova, i Berekija, sin Ase, sina Elkanina, koji je stanovao u selima Netofatskim.",
			"17": "<sup>17</sup> Nadalje vratari: Šalum, Akub, Talmon, Ahiman i braća njihova; Šalum je bio poglavar.",
			"18": "<sup>18</sup> On je do sada bio na vratima kraljevskim prema istoku. To su vratari u taboru Levita.",
			"19": "<sup>19</sup> Šalum, sin Korea, sina Abjasafa, sina Korahova, i braća njegova iz obitelji njegove, Korahovci, vrše službu u šatoru kao čuvari pragova. Već oci njihovi bili su u taboru Gospodnjem čuvari ulaza.",
			"20": "<sup>20</sup> Finehas, sin Eleazarov, bio je otprije glavar njihov - Gospod nek je s njim!",
			"21": "<sup>21</sup> Zaharija, sin Mešelemjin, bio je vratar kod šatora svjedočanstva.",
			"22": "<sup>22</sup> Svih ovih, koji su bili izabrani za vratare na pragovima, bilo je dvjesta i dvanaest. U selima njihovim bili su uneseni u popis porodica, David i vidjelac Samuel postaviše ih u njihovu službu.",
			"23": "<sup>23</sup> Oni su sa sinovima svojim kao straža stajali na vratima u hramu Gospodnjem, u šatoru.",
			"24": "<sup>24</sup> Prema četiri vjetra stajali su vratari, prema istoku, zapadu, sjeveru i Jugu.",
			"25": "<sup>25</sup> Od vremena do vremena morala su braća njihova iz njihovih sela doći za sedam dana, da im pomognu.",
			"26": "<sup>26</sup> Jer četiri prva vratara - to su bili Leviti - bila su svagda u službi. Oni su imali nadzor nad ćelijama i riznicama hrama Božjega.",
			"27": "<sup>27</sup> Preko noći stajali su u okolici hrama Božjega; jer na njima je bila straža i morali su otvarati svako jutro.",
			"28": "<sup>28</sup> Neki od njih bili su nad posuđem službenim: oni su ga na broj unosili i na broj iznosili.",
			"29": "<sup>29</sup> Drugi od njih bili su postavljeni nad drugim stvarima i nad svim predmetima posvećenim: nad brašnom, vinom, uljem, kadom i mirisima.",
			"30": "<sup>30</sup> Neki sinovi svećenički priređivali su mast od tih mirisa.",
			"31": "<sup>31</sup> Matitja, jedan od Levita, prvorođenac Koraheja Šaluma, bio je nad stvarima, što se peku na tavi.",
			"32": "<sup>32</sup> Između Kohatovaca, braće njihove, bili su neki nad kruhom postavljenim, pa bi ga priređivali svake subote.",
			"33": "<sup>33</sup> To su pjevači, obiteljski poglavari Levita, koji su samo u ćelijama bili slobodni od službe; jer su inače dan i noć bili zaposleni.",
			"34": "<sup>34</sup> To su obiteljski poglavari Levita, poglavari po porodicama svojim. Oni su stanovali u Jerusalemu.",
			"35": "<sup>35</sup> U Gibeonu stanovali su Jehiel, otac Gibeonov - žena njegova zvala se je Maaka -",
			"36": "<sup>36</sup> Njegov prvorođeni sin Abdon, pa Sur, Kiš, Baal, Ner, Nadab,",
			"37": "<sup>37</sup> Gedor, Ahio, Zaharija i Miklot.",
			"38": "<sup>38</sup> Miklotu se rodi Šimeama. I oni su stanovali kod braće svoje u Jerusalemu, naprema braći svojoj.",
			"39": "<sup>39</sup> Neru se rodi Kiš, Kišu se rodi Saul, Saulu se rodi Jonatan, Malki-Šua, Abinadab i Eš-Baal.",
			"40": "<sup>40</sup> Sin Jonatanov jest Merib-Baal. Merib-Baalu se rodi Mika.",
			"41": "<sup>41</sup> Sinovi Mikini jesu Piton, Melek, Tahrea i Ahaz.",
			"42": "<sup>42</sup> Ahazu se rodi Jar, Jaru se rodi Alemet, Azmavet i Zimri, Zimriju se rodi Moza,",
			"43": "<sup>43</sup> Mozu se rodi Bineu. A njegov sin jest Refaj, a njegov sin Eleasa, a njegov sin Asel.",
			"44": "<sup>44</sup> Asel je imao šest sinova, kojima su bila ova imena: Azrikam, Bokru, Išmael, Šearja, Obadija i Hanan. To su sinovi Aselovi."
		},
		"10": {
			"1": "<sup>1</sup> Između Izraelaca i Filisteja bilo je došlo do boja. A ljudi Izraelovi pobjegoše ispred Filisteja, i padali su mrtvi na gori Gilboi.",
			"2": "<sup>2</sup> Filisteji se natisnuše za Saulom i sinovima njegovim. Pritom pogubiše Filisteji Jonatana, Abinadaba, Malki-Šuu, sinove Saulove.",
			"3": "<sup>3</sup> I za Saula posta boj veoma težak. Napokon ga stigoše strijelci, i on bi ranjen od strijelaca.",
			"4": "<sup>4</sup> Tada zapovjedi Saul momku, koji mu je nosio oružje: \"Izvadi mač svoj i probodi me, da ne dođu ti pogani i narugaju mi se!\" Ali ne htjede momak, koji mu je nosio oružje, jer se je strašio veoma. I tako Saul sam uze mač i baci se na njega.",
			"5": "<sup>5</sup> Kad momak, koji mu je nosio oružje, vidje, gdje umrije Saul, baci se i on na svoj mač i umrije.",
			"6": "<sup>6</sup> Tako pogibe Saul sa tri sina svoja. Sva kuća njegova pogibe tako zajedno.",
			"7": "<sup>7</sup> A kad ljudi Izraelovi, što su stanovali u ravnici, vidješe, gdje pobjegoše oni i gdje Saul i sinovi njegovi pogiboše, ostaviše gradove svoje i nagnuše u bijeg. I dođoše Filisteji i naseliše se u njima.",
			"8": "<sup>8</sup> Sutradan dođoše Filisteji, da opljačkaju pobijene. Kad nađoše Saula i tri sina njegova gdje leže na gori Gilboi,",
			"9": "<sup>9</sup> Oplijeniše ga i uzeše sa sobom glavu njegovu i oružje njegovo. Tada poslaše po zemlji filistejskoj unaokolo, da dojave idolima svojim i narodu vijest o pobjedi.",
			"10": "<sup>10</sup> Oružje njegovo staviše u hram boga svojega, a glavu mu objesiše na hramu Dagonovu.",
			"11": "<sup>11</sup> A kad čuše svi stanovnici Jabeša u Gileadu sve, što su bili Filisteji učinili od Saula,",
			"12": "<sup>12</sup> Podigoše se svi oboružani i uzeše tijelo Saulovo i tjelesa sinova njegovih. Donesoše ih u Jabeš, pokopaše kosti njihove pod terebintom u Jabešu, i postili su sedam dana.",
			"13": "<sup>13</sup> Tako pogibe Saul zbog prestupaka, što ih je bio počinio proti Gospodu, jer nije držao zapovijedi Gospodnje i jer je bio pitao duh mrtvaca, da dobije obavijest,",
			"14": "<sup>14</sup> Mjesto da upita Gospoda. Zato ga ubi Gospod i prenese kraljevstvo njegovo na Davida, sina Jesejeva."
		},
		"11": {
			"1": "<sup>1</sup> Svi Izraelci skupiše se kod Davida u Hebronu i rekoše: \"Evo, mi smo od tvojega mesa i od tvoje krvi.",
			"2": "<sup>2</sup> Već prije, kad je još Saul bio kralj, ti si bio onaj, koji je u boj odvodio i opet kući dovodio Izraela. Tebi je obećao Gospod, Bog tvoj: \"Ti ćeš pasti narod moj Izraela, i ti ćeš biti knez nad pukom mojim Izraelom.\"",
			"3": "<sup>3</sup> Kad su se tako bile našle kod kralja u Hebronu sve starješine Izraelaca, sklopi David s njima u Hebronu pred Gospodom savez. Tada pomazaše Davida za kralja nad Izraelom po naputku Gospodnjem, što je bio izašao preko Samuela.",
			"4": "<sup>4</sup> Jednoga dana otide David sa svim Izraelom u Jerusalem, to jest Jebus. Tamo su bili Jebuseji, koji su živjeli u onoj zemlji.",
			"5": "<sup>5</sup> Stanovnici u Jebusu rekoše Davidu: \"Nećeš ući ovamo.\" Ipak David osvoji tvrđavu Sion, to je kasniji grad Davidov.",
			"6": "<sup>6</sup> Tada reče David: \"Tko prvi nadvlada Jebuseje, bit će vrhovni zapovjednik vojske.\" Joab, sin Serujin, uzađe prvi i posta vrhovni zapovjednik.",
			"7": "<sup>7</sup> David uze stan u tvrđavi. Zato je prozvaše gradom Davidovim.",
			"8": "<sup>8</sup> On sazida grad unaokolo od Mila do obzida. Joab popravi ostali grad.",
			"9": "<sup>9</sup> Sve je više rasla moć Davidova, jer je Gospod nad vojskama bio s njim.",
			"10": "<sup>10</sup> Ovo su najodličniji junaci Davidovi, koji su vjerno držali s njim za njegova kraljevanja nad Izraelom i pomogli mu, da bude kralj po obećanju Gospodnjem Izraelu.",
			"11": "<sup>11</sup> Ovo je popis junaka Davidovih: Jašobam, sin Hakmonijev, glavar trojici. On zavitla svoje koplje na tri stotine i pobi ih ujedanput.",
			"12": "<sup>12</sup> Za njim je dolazio između ta tri junaka Ahošanin Eleazar, sin Dodov.",
			"13": "<sup>13</sup> On je bio s Davidom u Pas Damimu, kad se tamo skupiše Filisteji na boj. Tamo je bio komad zemlje s ječmom. A kad su ljudi bježali od Filisteja,",
			"14": "<sup>14</sup> Stadoše oni usred polja, obraniše ga i pobiše Filisteje. Tako im dade Gospod veliku pobjedu.",
			"15": "<sup>15</sup> Jedanput siđoše ta tri najodličnija između tridesetorice k hridini, k Davidu, u pećinu Adulam, kad je četa Filisteja taborila u dolini Refaimovoj.",
			"16": "<sup>16</sup> David je bio upravo u tvrđavi. A u Betlehemu je onda bila straža filistejska.",
			"17": "<sup>17</sup> I kad je David zaželio: \"Tko bi mi donio vode da pijem iz studenca na vratima Betlehema?\"",
			"18": "<sup>18</sup> Tada ona tri junaka prodriješe kroz tabor Filisteja, zahvatiše vode iz studenca na vratima Betlehema i donesoše je Davidu. Ali on je ne htjede piti, nego je proli Gospodu u čast",
			"19": "<sup>19</sup> I reče: \"Ne daj mi, Gospode, da tako što učinim! Zar da pijem krv tih ljudi, koji prodriješe tamo uz opasnost života? Jer je donesoše ne mareći za život svoj.\" Zato je ne htjede piti. To učiniše ta tri junaka.",
			"20": "<sup>20</sup> Abišaj, brat Joabov, bio je glavar tridesetorici. On zavitla svoje koplje na tri stotine i pobi ih. Bio je ugledan kod trojice.",
			"21": "<sup>21</sup> Jer se odlikova među tridesetoricom, posta im vođa, ali one trojice ne stiže.",
			"22": "<sup>22</sup> Benaja, sin Jehojadov, hrabar čovjek, velik na djelima, iz Kabseela. On pogubi oba Ariela iz Moaba. Također ubi dolje kod jednog studenca lava, kad je bio pao snijeg.",
			"23": "<sup>23</sup> On ubi i nekoga Egipćanina, gorostasa visoka pet lakata. Premda je Egipćanin imao u ruci koplje, debelo kao vratilo, on izađe na njega sa štapom, istrže Egipćaninu koplje iz ruke i ubi ga njegovim vlastitim kopljem.",
			"24": "<sup>24</sup> To učini Benaja, sin Jehojadov. Kod trojice junaka imao je visok ugled.",
			"25": "<sup>25</sup> Bio je slavniji od tridesetorice, ali one trojice ne stiže. David ga stavi na čelo tjelesnoj straži.",
			"26": "<sup>26</sup> Hrabri junaci bili su Joabov brat Asahel, Elhanan iz Betlehema, sin Dodov,",
			"27": "<sup>27</sup> Šamot iz Harora, Heles iz Pelona,",
			"28": "<sup>28</sup> Ira, sin Ikešov iz Tekoe, Abiezer iz Anatota,",
			"29": "<sup>29</sup> Sibekaj iz Hušata, Ilaj iz Ahoha,",
			"30": "<sup>30</sup> Maharaj iz Netofe, Heled iz Netofe, sin Baanin,",
			"31": "<sup>31</sup> Itaj, sin Ribajev, iz Gibee u plemenu Benjaminovu, Benaja iz Piratona,",
			"32": "<sup>32</sup> Huraj iz potoka Gaaša, Abiel iz Arbe,",
			"33": "<sup>33</sup> Azmavet iz Baharuma, Eliaba iz Šaalbona,",
			"34": "<sup>34</sup> Sinovi Hašemovi iz Gizona, Jonatan, sin Šagehov, iz Harara,",
			"35": "<sup>35</sup> Ahiam, sin Sakarov, iz Harara, Elifal, sin Urov,",
			"36": "<sup>36</sup> Hefer iz Mekera, Ahija iz Pelona,",
			"37": "<sup>37</sup> Hesro iz Karmela, Naaraj sin Ezbajev,",
			"38": "<sup>38</sup> Joel, brat Natanov, Mibhar, sin Hagrijev,",
			"39": "<sup>39</sup> Selek, Amonac, Nahraj iz Berota, koji je nosio oružje Joabu, sinu Serujinu,",
			"40": "<sup>40</sup> Ira, Itranin, Gareb, Itranin,",
			"41": "<sup>41</sup> Urija, Hitejin, Zabad, sin Ahlajev,",
			"42": "<sup>42</sup> Adina, sin Šizin, iz plemena Rubenova, poglavar Rubenovaca, i s njim trideset ljudi,",
			"43": "<sup>43</sup> . Hanan, sin Maakin, i Jošafat iz Mitne,",
			"44": "<sup>44</sup> Uzija iz Aštarota, Šama i Jeiel, sinovi Hotama iz Aroera,",
			"45": "<sup>45</sup> Jediael, sin Šimrijev, i njegov brat Joha, Tizejin,",
			"46": "<sup>46</sup> Eliel, Mahavejin, Jeribaj i Jošavja, sinovi Elnaamovi, i Itma, Moabac,",
			"47": "<sup>47</sup> Eliel, Obed i Jaasiel iz Mezobaja."
		},
		"12": {
			"1": "<sup>1</sup> Ovi dođoše k Davidu u Siklag; kad je još bježao ispred Saula, sina Kišova. I oni su pripadali junacima, koji su pomagali u boju,",
			"2": "<sup>2</sup> Naoružani lukom, uvježbani desnom i lijevom bacati kamenje iz praćke i iz luka odapinjati strijele. Od plemena Benjaminova, između suplemenika Saulovih, bili su ovi:",
			"3": "<sup>3</sup> Poglavar Ahiezer i Joaš, sinovi Šemaje iz Gibee, Jeziel i Pelet, sinovi Azmavetovi, Beraka i Jehu iz Anatota,",
			"4": "<sup>4</sup> Išmaja iz Gibeona, junak među tridesetoricom i vođa tridesetorice, Jeremija, Jahaziel, Johanan i Jozabad iz Gederahe,",
			"5": "<sup>5</sup> Eluzaj, Jerimot, Bealja, Šemarja i Šefatija iz Harufa,",
			"6": "<sup>6</sup> Elkana, Jišija, Azarel, Joezer, Jašobam iz Koraha,",
			"7": "<sup>7</sup> Joela i Zebadija, sinovi Jerohamovi iz Gedora.",
			"8": "<sup>8</sup> I od plemena Gadova prijeđoše k Davidu u tvrđavu u pustinji hrabri ljudi, uvježbani ratnici, naoružani štitom i kopljem, kojima je lice bilo kao lavovsko, i brzi kao srne po gorama:",
			"9": "<sup>9</sup> Prvak je bio Ezer, drugi Obadija, treći Eliab,",
			"10": "<sup>10</sup> Četvrti Mišmana, peti Jeremija,",
			"11": "<sup>11</sup> Šesti Ataj, sedmi Eliel,",
			"12": "<sup>12</sup> Osmi Johanan, deveti Elzabad,",
			"13": "<sup>13</sup> Deseti Jeremija, jedanaesti Makbanaj.",
			"14": "<sup>14</sup> Ovi dođoše od sinova Gadovih. Bili su veoma hrabri ratnici. Najmanji ogledao bi se sa stotinom, najvaljaniji s tisućom.",
			"15": "<sup>15</sup> Ovi su bili, koji u prvom mjesecu prijeđoše preko Jordana, kad se razlio preko svih obala svojih, i natjeraše u bijeg sve stanovnike u nizinama na istok i na zapad.",
			"16": "<sup>16</sup> Dođoše jedanput i neki sinovi Benjaminovi i Judini k Davidu u tvrđavu.",
			"17": "<sup>17</sup> David izađe pred njih i reče im ovo: \"Ako dolazite k meni u mirnoj namjeri, da mi pomognete, ja ću se pošteno združiti s vama. Ako li dolazite, da me izdate neprijateljima mojim, premda na rukama mojim nema nepravde, neka vidi Bog otaca naših i neka sudi!\"",
			"18": "<sup>18</sup> Tada dođe duh na prvaka tridesetorice, na Amasaja. On povika: \"Tvoji su, Davide, i s tobom, sine Jesejev! Mir, mir tebi, i mir onima, koji ti pomažu, jer tebi pomaže Bog tvoj.\" Tako ih primi David i postavi ih poglavarima nad četama ratnim.",
			"19": "<sup>19</sup> I sinovi Manasehovi prijeđoše k Davidu, kad je s Filistejima išao u boj proti Saulu, ali im nije pomogao; jer knezovi filistejski vratiše ga, izakako su se bili posvjetovali, govorili su naime među se: \"On bi na račun glava naših mogao prebjeći k Saulu, gospodaru svojemu.\"",
			"20": "<sup>20</sup> Kad se je vraćao u Siklag, prijeđoše k njemu od plemena Manasehova Adnah, Jozabad, Jediael, Mihael, Jozabad, Elihu i Ziletaj, tisućnici u plemenu Manasehovu,",
			"21": "<sup>21</sup> Oni su pomagali Davidu proti razbojničkih četa, jer su svi bili hrabri ludi, te postadoše vođe u vojsci.",
			"22": "<sup>22</sup> Iz dan u dan dolazili su tako ljudi k Davidu, da stupe njemu u službu, dokle postade velika vojska kao vojska Božja.",
			"23": "<sup>23</sup> Ovo je broj odjeljaka naoružanih za službu ratnu, što dođoše k Davidu u Hebron, da prenesu kraljevstvo Saulovo na njega po zapovijedi Gospodnjoj:",
			"24": "<sup>24</sup> Od sinova Judinih, koji su nosili štit i koplje, bilo je šest tisuća i osam stotina naoružanih za službu ratnu;",
			"25": "<sup>25</sup> Od sinova Simeonovih sedam tisuća i što hrabrih ratnika;",
			"26": "<sup>26</sup> Od sinova Levijevih četiri tisuće i šest stotina",
			"27": "<sup>27</sup> S Jehojadom, knezom sinova Aronovih, pod kojim je stajalo tri tisuće i sedam stotina ljudi,",
			"28": "<sup>28</sup> I sa Sadokom, mladim junakom, čija je obitelj stavila dvadeset i dva kneza;",
			"29": "<sup>29</sup> Od sinova Benjaminovih, suplemenika Saulovih, tri tisuće ljudi; jer dotada većina ih se držalo uz kuću Saulovu;",
			"30": "<sup>30</sup> Od sinova Efraimovih dvadeset tisuća i osam stotina hrabrih, u svojim obiteljima veoma uglednih ljudi;",
			"31": "<sup>31</sup> Od polovine plemena Manasehova osamnaest tisuća, što su bili po imenu određeni da dođu, da učine Davida kraljem;",
			"32": "<sup>32</sup> Od sinova Isakarovih, koji su razumijevali prilike vremena, tako da su znali, što ima da čini Izrael, dvjesta stotnika i pod njihovim zapovjedništvom svi njihovi suplemenici.",
			"33": "<sup>33</sup> Od sinova Zebulunovih pedeset tisuća sposobnih za rat, potpuno za rat naoružanih ljudi, da pomognu vjerno;",
			"34": "<sup>34</sup> Od plemena Naftalijeva tisuća poglavara i s njima trideset i sedam tisuća ljudi sa štitom i kopljem;",
			"35": "<sup>35</sup> Od plemena Danova dvadeset i osam tisuća i šest stotina za boj naoružanih ljudi;",
			"36": "<sup>36</sup> Od plemena Ašerova četrdeset tisuća za rat sposobnih, za boj spremnih ljudi;",
			"37": "<sup>37</sup> Od onih s onu stranu Jordana: od plemena Rubenova, Gadova i od polovine plemena Manasehova sto i dvadeset tisuća ljudi s potpunim oružjem ratnim.",
			"38": "<sup>38</sup> Svi ovi ratnici dođoše, u redove uređeni, jednodušni u Hebron, da učine Davida kraljem nad svim Izraelom. I ostali svi Izraelci bili su složni da Davida učine kraljem.",
			"39": "<sup>39</sup> Oni ostadoše tri dana tamo kod Davida, i jeli su i pili su, jer njihovi zemljaci bili su se pobrinuli za njih.",
			"40": "<sup>40</sup> I zemljaci njihovi sve od Isakara, Zebuluna i Naftalija donijeli su na magarcima, devama, mazgama i volovima živežnih namirnica: brašna, smokava, suhoga grožđa, vina, ulja, volova i ovaca u izobilju, jer je bila radost u Izraelu."
		},
		"13": {
			"1": "<sup>1</sup> Tada se posvjetova David s tisućnicima i sa stotnicima i sa svim knezovima.",
			"2": "<sup>2</sup> Nato reče David svemu pučkom zboru Izraelovu: \"Ako je vama pravo i ako je volja Gospoda, Boga našega, onda ćemo poslati k braći svojoj ostaloj po svim krajevima Izraelovim i k svećenicima i Levitima u gradovima, gdje su pašnjaci njihovi, da se nađu kod nas,",
			"3": "<sup>3</sup> Pa da donesemo k sebi kovčeg Boga svojega, jer za vladanja Saulova nijesmo se brinuli za njega.\"",
			"4": "<sup>4</sup> Sav skup izjavi se sporazumnim, da se to učini; jer se taj prijedlog dopade svemu narodu,",
			"5": "<sup>5</sup> I tako David skupi sav narod Izraelov od Šihora na putu u Egipat do ceste u Hamat, da donesu kovčeg Božji iz Kirjat-Jearima",
			"6": "<sup>6</sup> Potom otide David sa svim Izraelom u Baalu, to jest Kirjat-Jearim, što pripada Judi, da odatle prenesu kovčeg Božji, koji nosi ime: \"Gospod, koji vlada nad kerubinima\".",
			"7": "<sup>7</sup> Metnuše kovčeg Božji na nova kola i povezoše ga iz kuće Abinadabove. Uza i Ahio vodili su kola.",
			"8": "<sup>8</sup> David i svi Izraelci igrali su puni revnosti pred Bogom, i pjevale se pjesme uz pratnju citara, harfa, bubnjeva, zvončića i cimbala.",
			"9": "<sup>9</sup> Kad dođoše do gumna Kidonova, pruži Uza ruku svoju, da pridrži čvrsto kovčeg, jer volovi potegoše na stranu",
			"10": "<sup>10</sup> Tada se raspali gnjev Gospodnji na Uzu, i udari ga ondje, jer se je bio rukom mašio za kovčeg. On umrije ondje pred Bogom.",
			"11": "<sup>11</sup> David se vrlo ožalosti, što Gospod ubi Uzu. Zato se prozva ono mjesto Perez Uza do današnjega dana.",
			"12": "<sup>12</sup> Jer se David u onaj dan uplaši od Boga i pomisli: \"Kako mogu donijeti k sebi kovčeg Božji,",
			"13": "<sup>13</sup> Ne dade David odvesti kovčeg k sebi u grad Davidov, nego ga skloni u kuću Obed-Edoma iz Gitejina.",
			"14": "<sup>14</sup> I tako osta kovčeg Božji tri mjeseca u kući Obed-Edoma kod obitelji njegove. Gospod blagoslovi kuću Obed-Edoma i sve imanje njegovo."
		},
		"14": {
			"1": "<sup>1</sup> Hiram, kralj Tira, posla k Davidu poslanike s drvima cedrovim, s kamenarima i drvodjeljama, da mu grade palaču.",
			"2": "<sup>2</sup> David spoznade, da ga je Gospod potvrdio kao kralja nad Izraelom, jer kraljevstvo njegovo bilo se je podiglo do visokog ugleda radi njegova naroda Izraela.",
			"3": "<sup>3</sup> David uze sebi u Jerusalemu još daljne žene, i Davidu se rodi još više sinova i kćeri.",
			"4": "<sup>4</sup> Ovo su imena sinova, što mu se rodiše u Jerusalemu: Šamua, Šobab, Natan, Salomon,",
			"5": "<sup>5</sup> Ibhar, Elišua, Elpelet,",
			"6": "<sup>6</sup> Nogah, Nefeg, Jafija,",
			"7": "<sup>7</sup> Elišama, Beelijada i Elifelet.",
			"8": "<sup>8</sup> Kad čuše Filisteji, da je David bio pomazan za kralja nad svim Izraelom, izađoše svi Filisteji, da uhvate Davida. Čim David doču to izađe pred njih.",
			"9": "<sup>9</sup> Filisteji dođoše i raširiše se u ravnici Refaimskoj,",
			"10": "<sup>10</sup> David upita Boga: \"Hoću li izaći proti Filistejima? Hoćeš li ih dati u moje ruke?\" Gospod mu odgovori: \"Izađi! Dat ću ih u tvoje ruke!\"",
			"11": "<sup>11</sup> Kad oni otidoše u Baal-Perasim, pobi ih ondje David. David povika: \"Bog neprijatelje moje prodrije mojom rukom, kao što prodire voda.\" Zato se prozva ono mjesto Baal-Perasim.",
			"12": "<sup>12</sup> Idole, što ih ondje ostaviše, dade David spaliti.",
			"13": "<sup>13</sup> Kad još jednom dođoše Filisteji i raširiše se po ravnici,",
			"14": "<sup>14</sup> Upita opet David Boga, i Bog mu odgovori: \"Ne idi za njima, nego ih zaobiđi, pa udari na njih od dudovih stabala!",
			"15": "<sup>15</sup> Čim čuješ da zašušti po vrhovima dudovih stabala, tada izađi i napadni, jer tada ide pred tobom Bog, da pobije vojsku filistejsku!\"",
			"16": "<sup>16</sup> David učini tako, kako mu je bio zapovjedio Bog. Oni pobiše vojsku filistejsku od Gibeona do Gezera.",
			"17": "<sup>17</sup> I raširi se slava Davidova po svim zemljama, i Gospod zadade strah od njega svim narodima."
		},
		"15": {
			"1": "<sup>1</sup> Potom dade sebi David posagraditi kuće u gradu Davidovu. I spremi mjesto za kovčeg Božji i razape mu šator.",
			"2": "<sup>2</sup> Tada zapovjedi David, da nitko ne smije nositi kovčeg Božji osim Levita, jer je njih izabrao Gospod da nose kovčeg Božji i da mu služe uvijek.",
			"3": "<sup>3</sup> I dozva David sve sinove Izraelove u Jerusalem, da prenesu kovčeg Gospodnji na mjesto, koje mu je bio spremio.",
			"4": "<sup>4</sup> I tako skupi David potomke Aronove i Levite:",
			"5": "<sup>5</sup> Od sinova Kohatovih poglavara Joela i njegovih sto i dvadeset suplemenika;",
			"6": "<sup>6</sup> Od sinova Merarijevih poglavara Asaju i njegovih dvjesta i dvadeset suplemenika:",
			"7": "<sup>7</sup> Od sinova Geršonovih poglavara Joela i njegovih sto i trideset suplemenika;",
			"8": "<sup>8</sup> Od potomaka Elisafanovih poglavara Šemaju i njegovih dvjesta suplemenika;",
			"9": "<sup>9</sup> Od potomaka Hebronovih poglavara Eliela i njegovih osamdeset suplemenika;",
			"10": "<sup>10</sup> Od potomaka Uzielovih poglavara Aminadaba i njegovih sto i dvanaest suplemenika.",
			"11": "<sup>11</sup> Tada dozva David svećenike Sadoka i Abiatara i Levite Uriela, Asaju, Joela, Šemaju, Eliela i Aminadaba",
			"12": "<sup>12</sup> I zapovjedi im: \"Vi ste obiteljski poglavari Levita. Posvetite sebe i svoje suplemenike! Vi imate donijeti kovčeg Gospoda, Boga Izraelova, na mjesto, koje sam mu spremio.",
			"13": "<sup>13</sup> Jer prijašnji put nijeste bili nazočni, zato nas je udario Gospod, Bog naš, jer nijesmo imali dužno štovanje prema njemu.\"",
			"14": "<sup>14</sup> Tada se posvetiše svećenici i Leviti, da donesu kovčeg Gospoda, Boga Izraelova",
			"15": "<sup>15</sup> Onda digoše Leviti kovčeg Božji, kao što je bio naredio Mojsije po zapovijedi Gospodnjoj, motkama na ramena svoja.",
			"16": "<sup>16</sup> Potom zapovjedi David poglavarima levitskim, da postave svoje suplemenike, pjevače, s njihovim glazbalima, harfama, citrama i cimbalima, da uskliknu radosnim glasom visokim.",
			"17": "<sup>17</sup> I postaviše Leviti sina Joelova, Hemana, i od njegovih suplemenika Asafa, sina Berekijina, i od njihovih suplemenika, sinova Merarijevih, Etana, sina Kušajina,",
			"18": "<sup>18</sup> S njima drugove njihove, drugoga reda Zahariju, sina, Jaaziela, Šemiramota, Jehiela, Unija, Eliaba, Benaju, Maaseju, Matitju, Elifeleha, Mikneju, Obed-Edoma i Jehiela, vratare.",
			"19": "<sup>19</sup> Pjevači Heman, Asaf i Etan imali su udarati glasno u cimbale mjedene,",
			"20": "<sup>20</sup> Zaharija, Aziel, Šemiramot, Jehiel, Uni, Eliab, Maaseja i Benaja u harfe visoko,",
			"21": "<sup>21</sup> Matitja, Elifeleha, Mikneja, Obed-Edoma, Jehiela i Azazju u citare duboko, da vode pjevanje.",
			"22": "<sup>22</sup> Kenanija bio je poglavar Levita kod prenošenja. On je vodio prenošenje, jer se je u to razumio.",
			"23": "<sup>23</sup> Berekija i Elkana išli su kao vratari pred kovčegom.",
			"24": "<sup>24</sup> Svećenici Šebanija, Jošafat, Netanel, Amasaj, Zaharija, Benaja i Eliezer trubili su u trube pred kovčegom Božjim. Tada su dolazili Obed-Edom i Jehija kao vratari kod kovčega.",
			"25": "<sup>25</sup> I tako je pošao David sa starješinama Izraelovim i tisućnicima, da s velikim veseljem prenesu kovčeg zavjeta Gospodnjega iz kuće Obed-Edomove.",
			"26": "<sup>26</sup> I Bog je bio milostiv Levitima, nosiocima kovčega zavjeta Gospodnjega. Zato oni žrtvovaše sedam volova i sedam ovnova.",
			"27": "<sup>27</sup> David je pritom bio ogrnut plaštem od finoga lana, tako i svi Leviti, koji su nosili kovčeg, i pjevači i Kenanija, poglavar prijenosa i pjevač. David je imao još na sebi oplećak lanen.",
			"28": "<sup>28</sup> Tako je sav Izrael nosio kovčeg zavjeta Gospodnjega kličući i trubeći u trube i u rogove i udarajući u cimbale, harfe i citare.",
			"29": "<sup>29</sup> Tako uđe kovčeg zavjeta Gospodnjega u grad Davidov. Mikala, kći Saulova, gledala je s prozora van. Kad vidje kralja Davida gdje skače i igra, prezre ga u srcu svojem."
		},
		"16": {
			"1": "<sup>1</sup> Izakako su bili unijeli kovčeg Božji i namjestili ga u šatoru, što ga je bio David podigao za njega, prinesoše pred Bogom žrtve paljenice i žrtve mirotvorne.",
			"2": "<sup>2</sup> Kad prinese David žrtve paljenice i žrtve mirotvorne, blagoslovi narod u ime Gospoda",
			"3": "<sup>3</sup> I razdijeli svima Izraelcima, ljudima i ženama, svakome po jedan kruh, po komad mesa i kolač od grožđa.",
			"4": "<sup>4</sup> Potom postavi neke Levite, da služe kod kovčega Gospodnjega. Oni su morali prinositi Gospodu, Bogu Izraelovu, hvalu, čast i slavu:",
			"5": "<sup>5</sup> Asaf kao poglavar i Zaharija kao drugi u redu za njim, onda Jeiel, Šemiramot, Jehiel, Matitja, Eliab, Benaja, Obed-Edom i Jeiel sa svojim glazbalima, harfama i citrama, Asaf je imao udarati u cimbale,",
			"6": "<sup>6</sup> Svećenici Benaja i Jahaziel jednako trubiti u trube pred kovčegom zavjeta Božjega.",
			"7": "<sup>7</sup> Tada prvi put naredi David, da Asaf i braća njegova hvale Gospoda:",
			"8": "<sup>8</sup> \"Hvalite Gospoda! Zazivajte ime njegovo! Javljajte narodima djela njegova!",
			"9": "<sup>9</sup> Pjevajte mu! Svirajte mu! Sva čudesa njegova navješćujte!",
			"10": "<sup>10</sup> Hvalite se svetim imenom njegovim! Neka se veseli srce onih, koji traže Gospoda!",
			"11": "<sup>11</sup> Tražite Gospoda i slavu njegovu! Tražite lice njegovo vazda!",
			"12": "<sup>12</sup> Spominjite se čudesa njegovih, koja je učinio, znakova njegovih, sudova usta njegovih!",
			"13": "<sup>13</sup> Ti, Izraela, sluge njegova, rode, vi, Jakova odabranika njegova, sinovi!",
			"14": "<sup>14</sup> On, Gospod, nama je Bog! U sav svijet idu sudovi njegovi.",
			"15": "<sup>15</sup> Zavjeta svojega vječno se on spominje, riječi, koju je dao za tisuću koljena,",
			"16": "<sup>16</sup> Zavjeta, što ga je sklopio s Abrahamom, i zakletve svoje Izaku.",
			"17": "<sup>17</sup> To je postavio Jakovu za zakon, za vječni zavjet Izraelu,",
			"18": "<sup>18</sup> Ovako govoreći: Kanaan ću dati tebi kao uže baštinsko vaše!",
			"19": "<sup>19</sup> Tada ste još bili maleni brojem, samo vas malo i stranci u njem.",
			"20": "<sup>20</sup> Još ste išli od naroda do naroda, od jednoga kraljevstva do drugoga.",
			"21": "<sup>21</sup> Ali on ne dade, da im se naudi. Radi njih uputi kraljeve:",
			"22": "<sup>22</sup> Ne dirajte u pomazanike moje, prorocima mojim ne učinite ništa na žao!",
			"23": "<sup>23</sup> Pjevajte tako Gospodu, sve zemlje! Iz dan u dan navješćujete spasenje njegovo!",
			"24": "<sup>24</sup> Navješćujte narodima slavu njegovu, svakome puku čudesa njegova!",
			"25": "<sup>25</sup> Jer je velik Gospod i veoma dostojan hvale, strahovit iznad svih bogova.",
			"26": "<sup>26</sup> Jer su svi bogovi u naroda idoli, a Gospod je stvorio nebesa,",
			"27": "<sup>27</sup> Slava je i veličanstvo pred njim, sila i radost u svetištu njegovu.",
			"28": "<sup>28</sup> I dajte Gospodu, plemena naroda, dajte Gospodu slavu i čast!",
			"29": "<sup>29</sup> Dajte Gospodu slavu prema imenu njegovu! Prinosite dare, dolazite pred lice njegovo! Poklonite se Gospodu u svetome nakitu!",
			"30": "<sup>30</sup> Neka strepi pred njim sav svijet! Čvrsto stoji nadzemaljski svijet - ne pomjesti se nikad!",
			"31": "<sup>31</sup> Nebo se veseli, zemlja se raduje. Navijestite narodima: Gospod je kralj!",
			"32": "<sup>32</sup> More buči i što je u njemu, skače polje i što je na njemu.",
			"33": "<sup>33</sup> I tada se obraduje drveće šumsko pred Gospodom, kad on dolazi da sudi zemlju.",
			"34": "<sup>34</sup> Hvalite Gospoda, jer je dobar, jer milost njegova traje dovijeka.",
			"35": "<sup>35</sup> Ovako govorite: Pomozi nam, Bože spasenja našega! Skupi nas, spasi nas od naroda! Da mognemo slaviti sveto ime tvoje, da se smijemo hvaliti slavom tvojom.",
			"36": "<sup>36</sup> Neka je hvaljen Gospod, Bog Izraelov! Odvijeka dovijeka!\" I sav narod reče: \"Amen!\"! \"Hvala neka je Gospodu!",
			"37": "<sup>37</sup> Tada ostavi ondje pred kovčegom zavjeta Gospodnjega Asafa i suplemenike njegove, da služe pred kovčegom bez prestanka onako, kako to bude tražio svaki dan.",
			"38": "<sup>38</sup> Obed-Edoma i njegovih šezdeset i osam suplemenika i Obed-Edoma, sina Jedutunova, i Hosu postavi vratarima.",
			"39": "<sup>39</sup> Svećenika Sadoka i njegove suplemenike, svećenike, namjesti za stan Gospodnji na visini u Gibeonu",
			"40": "<sup>40</sup> Oni su imali Gospodu redovito jutrom i večerom prinositi žrtve paljenice na žrtveniku za žrtve paljenice, točno onako, kako stoji napisano u zakonu Gospodnjem, što ga je dao Izraelu.",
			"41": "<sup>41</sup> S njima su bili Heman i Jedutun uz ostale, što su bili izabrani i poimence označeni, da prinose Gospodu hvalospjev: Vječno traje milost njegova.",
			"42": "<sup>42</sup> Oni, Heman i Jedutun, čuvali su trube i cimbale za glazbenike i glazbala za pjesme Božje. Sinovi Jedutunovi vršili su službu vratarsku.",
			"43": "<sup>43</sup> Potom se svi vratiše kući. A David ode da pozdravi obitelj svoju."
		},
		"17": {
			"1": "<sup>1</sup> Kad se je David bio uselio u svoju palaču, reče David proroku Natanu: \"Evo, ja stojim u palači od cedra, a kovčeg zavjeta Gospodnjega stoji pod pokrovom šatorskim.\"",
			"2": "<sup>2</sup> Natan odgovori Davidu: \"Izvedi sve, što ti je u srcu, jer je Gospod s tobom.\"",
			"3": "<sup>3</sup> Ali još u istoj noći dođe riječ Gospodnja Natanu:",
			"4": "<sup>4</sup> \"Idi, reci slugi mojemu Davidu: Ovako veli Gospod: Nećeš mi ti graditi palaču da u njoj stanujem.",
			"5": "<sup>5</sup> A ja nijesam nikada stanovao u palači odonda, kad izvedoh sinove Izraelove, do današnjega dana. Dapače sam putovao od jednoga šatora i stana do drugoga.",
			"6": "<sup>6</sup> Jesam li, dok sam sa svim Izraelom okolo hodio, kojemu od sudaca Izraelovih, kojima zapovjedih, da pasu narod moj Izraela, ikada jednu riječ rekao: Zašto mi ne sagradiste palaču od cedra?",
			"7": "<sup>7</sup> Zato reci sada slugi mojemu Davidu: Ovako veli Gospod nad vojskama: Ja te uzeh s paše iza stada, da budeš knez mojemu narodu Izraelu.",
			"8": "<sup>8</sup> Bio sam s tobom kod svega, što si preduzimao, i uništio sam ispred tebe sve tvoje neprijatelje. Ja ću ti steći ime, kako ga imaju, samo najveći na zemlji.",
			"9": "<sup>9</sup> Ja ću odrediti mjesto svojemu narodu Izraelu i tamo ga posaditi, da mirno stanuje na svom mjestu i da se više ne uznemiruje. Silnici neće ga više mučiti kao prije,",
			"10": "<sup>10</sup> Od onoga vremena, kad postavih suce nad svojim narodom Izraelom. Ponizit ću sve tvoje neprijatelje, i javljam ti, da će ti Gospod sagraditi kuću",
			"11": "<sup>11</sup> Kad se jedanput navrše dani tvoji i ti počineš kod otaca svojih, odredit ću potomstvo tvoje, jednoga od tvojih sinova, nasljednikom tvojim i njemu ću utvrditi kraljevstvo njegovo.",
			"12": "<sup>12</sup> On će mi tada sagraditi kuću, i ja ću učvrstiti njegovo prijestolje za sva vremena.",
			"13": "<sup>13</sup> Ja ću mu biti otac, on neka mi bude sin! Nikada mu neću uskratiti naklonost svoju, kao što sam je uskratio prethodniku njegovu.",
			"14": "<sup>14</sup> Zauvijek postavit ću ga nad kućom svojom i kraljevstvom svojim, i prijestolje će njegovo tvrdo stajati dovijeka.\"",
			"15": "<sup>15</sup> Kad je Natan po svim ovim riječima i po svoj ovoj objavi bio govorio s Davidom,",
			"16": "<sup>16</sup> Ode kralj David, pade pred Gospodom i pomoli se: \"Tko sam ja, Gospode, Bože moj? Što je moja kuća, da si me doveo dovle?",
			"17": "<sup>17</sup> I nije ti to još bilo dosta, Bože! Ti si dapače kući sluge svojega dao obećanja za daleka vremena. Ti si postupao s menom kao s velikim čovjekom, Gospode, Bože moj!",
			"18": "<sup>18</sup> Što će još David da ti kaže o slavi, kojom si obasuo slugu svojega? Ta ti znaš slugu svojega.",
			"19": "<sup>19</sup> Gospode, radi sluge svojega i po srcu svojemu učinio si sve ovo veliko i objavio si sve ovo sjajno.",
			"20": "<sup>20</sup> Gospode, tebi nitko nije jednak! Osim tebe nema Boga po svemu, što smo čuli svojim ušima.",
			"21": "<sup>21</sup> Gdje je narod na zemlji kao tvoj narod Izraelov, radi kojega je Bog došao, da ga iskupi sebi za narod, da pribavi tebi ime velikim i čudesnim djelima i da ispred naroda tvojega, što si ga izbavio iz Egipta, protjera druge narode?",
			"22": "<sup>22</sup> Ti si sebi odredio svoj narod Izraela za sva vremena kao narod, i ti si, Gospode, postao Bog njihov!",
			"23": "<sup>23</sup> Pa daj, Gospode, da se obećanje, što si ga dao slugi svojemu i kući njegovoj, ispuni za sva vremena, i učini, što si obrekao!",
			"24": "<sup>24</sup> Kad se ono ispuni, tvoje će ime biti veliko dovijeka, i govorit će se: Gospod nad vojskama jest Bog Izraelov, jest Bog u Izraelu, i kuća sluge tvojega Davida stajat će pred tobom.",
			"25": "<sup>25</sup> Jer ti si, Bože moj, objavio slugi svojemu, da ćeš mu sagraditi kuću. Zato se sluga tvoj usudi, da ti upravi ovu molitvu.",
			"26": "<sup>26</sup> Tako, Gospode, ti si Bog. Pošto si slugi svojemu učinio ovo sjajno obećanje,",
			"27": "<sup>27</sup> Budi sada voljan i blagoslovi kuću sluge svojega, da postoji pred tobom dovijeka, jer što ti blagosloviš, Gospode, blagoslovljeno je dovijeka!\""
		},
		"18": {
			"1": "<sup>1</sup> Poslije toga pobijedi David Filisteje i podjarmi ih, I Gat i kćeri njegove uze iz ruku filistejskih.",
			"2": "<sup>2</sup> Nadalje pobi Moapce. Tako postadoše Moapci Davidu podložni i moradoše plaćati danak.",
			"3": "<sup>3</sup> David pobijedi i Hadadezera, kralja sobskoga, koja leži u pravcu prema Hamatu, kad se je ovaj spremio da utvrdi svoju vlast na Eufratu.",
			"4": "<sup>4</sup> Od njega uze David tisuću kola, sedam tisuća kolskih boraca i dvadeset tisuća pješaka. Sve konje dade David ohromiti; samo stotinu konja ostavi.",
			"5": "<sup>5</sup> Kad Sirijci od Damaska dođoše u pomoć kralju Hadadezeru sobskomu, pobi David dvadeset i dvije tisuće Sirijaca.",
			"6": "<sup>6</sup> Potom namjesti David namjesnika u Siriji od Damaska, i Sirijci postadoše podložni Davidu i obvezani na danak. Tako je pomagao Gospod Davidu u svemu, što bi preduzimao.",
			"7": "<sup>7</sup> David zaplijeni zlatne štitove, što su ih imale sluge Hadadezerove, i donese ih u Jerusalem.",
			"8": "<sup>8</sup> U Tibhatu i Kunu, gradovima Hadadezerovim, zaplijeni David veoma mnogo mjedi, od čega Salomon dade načiniti more mjedeno, stupove i posuđe mjedeno.",
			"9": "<sup>9</sup> Kad Tou, kralj hamatski, doznade, da je David pobio svu vojsku kralja Hadadezera sobskoga,",
			"10": "<sup>10</sup> Posla on svojega sina Adorama kralju Davidu, da ga pozdravi i sreću mu zaželi, što se je borio proti Hadadezeru i pobijedio ga. Hadadezer bio je naime ratni protivnik Touov. On donese sa sobom svakojakih sprava od srebra, zlata i mjedi.",
			"11": "<sup>11</sup> I to posveti kralj David Gospodu zajedno sa srebrom i zlatom, što je bio uzeo od svih naroda, od Edomaca, Moabaca, Amonaca, Filisteja i Amalečana.",
			"12": "<sup>12</sup> Abišaj, sin Serujin, pobi osamnaest tisuća Edomaca u slanoj dolini.",
			"13": "<sup>13</sup> On namjesti u Edomu namjesnike, i sav Edom bio je podložan Davidu. Tako je pomagao Gospod Davidu u svemu, što bi preduzimao.",
			"14": "<sup>14</sup> I David je vladao nad svim Izraelom i davao svemu narodu svojemu pravdu i pravicu.",
			"15": "<sup>15</sup> Vrhovni zapovjednik vojske bio je Joab, sin Serujin. Pečatnik je bio Jošafat, sin Ahiludov,",
			"16": "<sup>16</sup> Svećenici su bili Sadok, sin Ahitubov, i Abimelek, sin Abiatarov. Šavša je bio državni pisar.",
			"17": "<sup>17</sup> Zapovjednik nad Keretejima i Peletejima bio je Benaja, sin Jehojadov. Sinovi Davidovi bili su prvi iza kralja."
		},
		"19": {
			"1": "<sup>1</sup> Poslije toga umrije Nahaš, kralj sinova Amonovih. Njegov sin uziđe mjesto njega na prijestolje.",
			"2": "<sup>2</sup> David pomisli: \"Imat ću prijateljske odnose s Hanunom, sinom Nahašovim, jer je i otac njegov stajao s menom u dobrom dogovoru.\" I tako posla David poslanike i dade mu izreći svoju sućut radi oca njegova. Kad su bili poslanici Davidovi došli k Hanunu u zemlju sinova Amonovih, da mu izreknu sućut,",
			"3": "<sup>3</sup> Rekoše knezovi sinova Amonovih Hanunu: \"Misliš li, da David hoće da počasti oca tvojega, kad ti šalje poslanike, da te utješe? Dapače njegovi poslanici dođoše k tebi samo zato, da promotre zemlju, da je uhode i onda opustoše.\"",
			"4": "<sup>4</sup> Tada dade Hanun poslanike Davidove uhvatiti, obrijati ih i odsjeći im haljine po pole sve do bokova. Tako ih otpremi.",
			"5": "<sup>5</sup> Kad javiše Davidu za te ljude, posla im u susret glasnike, jer su ljudi bili teško pogrđeni. Kralj im poruči: \"Ostanite u Jerihu, dok vam opet naraste brada; onda dođite kući!\"",
			"6": "<sup>6</sup> Kad sinovi Amonovi uvidješe, da su se omrazili s Davidom, poslaše Hanun i sinovi Amonovi tisuću talenata srebra, da najme kola i kolske borce od Sirijaca u Mezopotamiji i od Sirijaca u Maaki i Sobi,",
			"7": "<sup>7</sup> Oni najmiše trideset i dvije tisuće kola, usto kralja od Maake s vojskom njegovom. Ovi dođoše i utaboriše se pred Medebom. I sinovi Amonovi skupiše se iz gradova svojih i izađoše u boj.",
			"8": "<sup>8</sup> Čim to doču David, posla Joaba sa svom vojskom, samih hrabrih ratnika.",
			"9": "<sup>9</sup> Sinovi Amonovi izađoše i postaviše se pred gradom u bojni red. Kraljevi, koji su bili došli, uzeše mjesto za se u polju.",
			"10": "<sup>10</sup> Kad vidje Joab, da mu prijeti napadaj sprijeda i otraga, izabra sebi najodabranije Izraelce i namjesti se prema Sirijcima.",
			"11": "<sup>11</sup> Ostatak vojske predade svojemu bratu Abišaju, da se namjeste prema sinovima Amonovim.",
			"12": "<sup>12</sup> On reče: \"Ako Sirijci budu jači od mene, moraš mi doći u pomoć. Ako li sinovi Amonovi budu jači od tebe, ja ću doći tebi u pomoć.",
			"13": "<sup>13</sup> Samo budi hrabar! Borimo se za svoj narod i a gradove Boga svojega. Gospod neka učini, što mu se čini dobro!\"",
			"14": "<sup>14</sup> I kad Joab s četama, kojima je zapovijedao, izađe da udari na Sirijce, pobjegoše oni ispred njega,",
			"15": "<sup>15</sup> A čim sinovi Amonovi vidješe da Sirijci nagnuše u bijeg, dadoše se i oni u bijeg ispred njegova brata Abišaja i povukoše se u grad. Nato se Joab vrati kući u Jerusalem.",
			"16": "<sup>16</sup> Kad vidješe Sirijci, da ih nadbiše Izraelci, poslaše poslanike, te dovedoše Sirijce preko rijeke, Šofak, vojskovođa Hadadezerov, imao je vrhovno zapovjedništvo.",
			"17": "<sup>17</sup> Kada to javiše Davidu, podiže on sve Izraele, prijeđe preko Jordana, pođe proti njima i namjesti se prema njima. I kad se je David bio namjestio prema Sirijcima za boj, pobiše se oni s njim.",
			"18": "<sup>18</sup> Ali Sirijci moradoše nagnuti u bijeg ispred Izraelaca. David uništi od Sirijaca sedam tisuća kolskih boraca i četrdeset tisuća pješaka. I vojskovođu Šofaka pogubi.",
			"19": "<sup>19</sup> Kad vidješe podanici Hadadezerovi, da ih razbiše Izraelci, sklopiše mir s Davidom i pokoriše se njemu. A Sirijci su se čuvali, da ubuduće još pomažu sinovima Amonovim."
		},
		"20": {
			"1": "<sup>1</sup> Iduće godine u vrijeme, kad kraljevi obično idu u rat, povede Joab vojsku u boj i opustoši zemlju sinova Amonovih. Tada ode i opkoli Rabu, a David ostade u Jerusalemu. Kad je Joab bio osvojio i razorio Rabu,",
			"2": "<sup>2</sup> Uze David kralju njihovu s glave krunu. Imala je prema procjeni svojoj vrijednost od jednog talenta na zlatu i na dragom kamenju, što je bilo u njoj, i pristajala je glavi Davidovoj. On odnese iz grada plijen vrlo bogat.",
			"3": "<sup>3</sup> Narod, što je stanovao u njemu, dade odvesti, staviti ga na pile, na gvozdene brane i sjekire. Tako isto učini David sa svim ostalim gradovima sinova Amonovih. Tada se vrati David sa svom vojskom u Jerusalem.",
			"4": "<sup>4</sup> Potom dođe u Gezeru opet do boja s Filistejima. Pritom Hušanin Sibekaj ubi Sipaja, koji je pripadao rodu divova. Tako su bili oni pokoreni,",
			"5": "<sup>5</sup> Kad onda opet dođe do boja s Filistejima, ubi Elhanan, sin Jairov, Lahmija, brata Golijata iz Giteja, kojemu je koplje bilo kao vratilo.",
			"6": "<sup>6</sup> Opet onda dođe do boja kod Gata. Tu je bio jedan čovjek gorostas, koji je imao šest prsta na rukama i šest prsta na nogama, svega dvadeset i četiri. I on je pripadao rodu divova.",
			"7": "<sup>7</sup> Kad je on pogrdio Izraele, ubi ga Jonatan, sin Šimea, brata Davidova.",
			"8": "<sup>8</sup> To su bili potomci divova u Gatu i padoše od ruke Davidove i njegovih ratnika."
		},
		"21": {
			"1": "<sup>1</sup> Sotona ustade proti Izraelcima i navrati Davida, da broji Izraela.",
			"2": "<sup>2</sup> I tako zapovjedi David Joabu i vojskovođama: \"Idite, izbrojte sinove Izraelove od Beeršebe do Dana, pa mi javite, da znam broj njihov!\"",
			"3": "<sup>3</sup> Ali Joab reče: \"Gospod neka bi dao, da narod tvoj, koliko ga je sada, bude još što puta toliki A oni su svi, moj gospodaru i kralju, podložni tebi, gospodaru mojemu. Zašto moj gospodar tako što hoće? Zašto da se okrivi Izrael takvim grijehom?\"",
			"4": "<sup>4</sup> Ali zapovijed kraljeva primora Joaba. I tako se Joab podiže na put i obiđe svega Izraela. Kad se je Joab bio opet vratio u Jerusalem,",
			"5": "<sup>5</sup> Priopći Joab Davidu broj popisanoga naroda, i bilo je svega naroda Izraelova milijun i sto tisuća ljudi teško naoružanih, a naroda Judina četiri stotine i sedamdeset tisuća ljudi teško naoružanih.",
			"6": "<sup>6</sup> Plemena Levijeva i Benjaminova nije bio s njima izbrojio, jer je mrska bila Joabu zapovijed kraljeva.",
			"7": "<sup>7</sup> Kako ta stvar nije bila mila Bogu, zato on udari Izraela.",
			"8": "<sup>8</sup> David se je doduše bio pomolio Bogu: \"Teško sam se ogriješio, što to učinih. Ali oduzmi slugi svojemu krivnju zlodjela njegovih; jer sam bio posve zaslijepljen!\"",
			"9": "<sup>9</sup> A Gospod je bio zapovjedio Gadu, vidiocu Davidovu:",
			"10": "<sup>10</sup> \"Idi i kaži Davidu: Ovako veli Gospod: Troje ti predlažem; izaberi sebi jedno od toga, da ti učinim!\"",
			"11": "<sup>11</sup> Gad pođe k Davidu i navijesti mu: \"Ovako veli Gospod: Biraj sebi",
			"12": "<sup>12</sup> Ili tri godine gladi ili tri mjeseca bijega pred svojim neprijateljima, a pritom da te stigne mač neprijatelja tvojih, ili tri dana mač Gospodnji, kuga, u zemlji, a pritom da ubija anđeo Gospodnji po svim krajevima Izraelovim. Sad promisli, što ću odgovoriti onome, koji me je poslao!\"",
			"13": "<sup>13</sup> David odgovori Gadu: \"U muci sam velikoj. Radije ću pasti u ruke Gospodnje, jer je milosrđe njegovo veliko. A u ruke ljudima ne bih htio pasti.\"",
			"14": "<sup>14</sup> Tako pusti Gospod kugu na Izraela, te pomrije Izraela sedamdeset tisuća ljudi.",
			"15": "<sup>15</sup> I Bog posla anđela u Jerusalem, da ga uništi, Kad je on ubijao u njemu, pogleda Gospod na njega, i sažali mu se sa zla. Zato zapovjedi anđelu, koji je ubijao: \"Dosta je. Povuci natrag ruku svoju!\" Anđeo Gospodnji stajao je upravo kod gumna Jebusejina Ornana (Arauna).",
			"16": "<sup>16</sup> Kad David podiže oči svoje, vidje anđela Gospodnjega gdje stoji između zemlje i neba, u ruci mu gol mač, što je bio ispružen proti Jerusalemu. Tada padoše David i starješine ničice na svoje lice, obučeni u kostrijet,",
			"17": "<sup>17</sup> I David se pomoli Bogu: \"Ja sam, koji je zapovjedio brojenje naroda. Ja sam, koji je sagriješio i zlo učinio. A ove, ovce, Što su učinile? Pa okreni, Gospode, Bože moj, ruku svoju na mene i na obitelj moju, ali ne na svoj narod ovim pomorom!\"",
			"18": "<sup>18</sup> Tada zapovjedi anđeo Gospodnji Gadu da kaže Davidu, neka bi David izašao gore, da načini žrtvenik Gospodu na gumnu Jebusejina Ornana (Arauna).",
			"19": "<sup>19</sup> David izađe na zapovijed Gadovu, koju mu je ovaj bio upravio u ime Gospodnje.",
			"20": "<sup>20</sup> I Ornan, kad se je okrenuo, vidje anđela. I on se skrije, on i njegova četiri sina. Ornan je upravo vršio pšenicu.",
			"21": "<sup>21</sup> David pođe dakle k Ornanu. Kad je Ornan pogledao i kralja vidio, izađe iz gumna i pokloni se pred Davidom licem do zemlje.",
			"22": "<sup>22</sup> David reče Ornanu: \"Daj mi to gumno, da načinim na njemu žrtvenik Gospodu! Za novce koliko vrijedi daj mi ga, da se pomor ukloni od naroda!\"",
			"23": "<sup>23</sup> Ornan odgovori Davidu: \"Uzmi ga sebi! Moj gospodar i kralj neka čini, što mu je drago! Evo, dajem ti i volove za žrtve paljenice i vršeće saone za drva i pšenicu za prinos. Sve ti to darujem.\"",
			"24": "<sup>24</sup> A kralj David reče Ornanu: \"Ne! Ja ću to samo kupiti za novce što vrijedi, jer neću to, što je tvoje, da prinesem Gospodu ni da prinesem žrtve paljenice poklonjene.\"",
			"25": "<sup>25</sup> I dade David Ornanu za ono mjesto šest stotina šekela zlata.",
			"26": "<sup>26</sup> David načini tamo Gospodu žrtvenik i prinese žrtve paljenice i žrtve mirotvorne. Kad je prizvao Gospoda, odgovori mu on ognjem, što ga spusti s neba na žrtvenik za žrtve paljenice.",
			"27": "<sup>27</sup> A anđelu zapovjedi Gospod, da zatakne opet mač svoj u korice.",
			"28": "<sup>28</sup> Kad je onda David vidio, da ga je Gospod uslišio na gumnu Jebusejina Ornana (Arauna), prinese ondje žrtve.",
			"29": "<sup>29</sup> Šator Gospodnji, što ga je bio Mojsije načinio u pustinji, i žrtvenik za žrtve paljenice bili su u ono vrijeme na visini u Gibeonu.",
			"30": "<sup>30</sup> A David se ne usudi više ići tamo k njemu, da časti Boga, jer se je bio uplašio od mača anđela Gospodnjega."
		},
		"22": {
			"1": "<sup>1</sup> I tako odredi David: \"Ovo ovdje ima da bude kuća Boga, Gospoda, i ovdje ima da stoji žrtvenik za žrtve paljenice Izraelu.\"",
			"2": "<sup>2</sup> Potom zapovjedi David, da se skupe stranci u zemlji Izraelovoj, i odredi kamenare za gradnju hrama Božjega, koji će tesati kamen.",
			"3": "<sup>3</sup> I pripravi David mnogo gvožđa za kline na krila vratima i za sastavke, tako isto mjedi toliko, da se nije moglo mjeriti.",
			"4": "<sup>4</sup> Isto tako cedrovih drva bez broja; jer su Sidonci i Tirci dovozili Davidu mnogo drva cedrovih.",
			"5": "<sup>5</sup> David je naime u sebi mislio: \"Moj sin Salomon mlad je i nježan; a hram, koji treba podignuti Gospodu, mora biti vrlo velik za slavu i čast po svim zemljama. Zato ću mu već sada pripraviti što treba.\" I tako pripravi David još prije svoje smrti veliku množinu građe.",
			"6": "<sup>6</sup> Nato dozva svojega sina Salomona i naloži mu, da sagradi hram Gospodu, Bogu Izraelovu.",
			"7": "<sup>7</sup> David reče Salomonu: \"Sine moj, ja sam bio naumio da sagradim hram imenu Gospoda, Boga svojega.",
			"8": "<sup>8</sup> Ali mi dođe riječ Gospodnja: Mnogo si krvi prolio i velike si ratove vodio. Ne smiješ ti sagraditi hrama imenu mojemu, jer si mnogo krvi prolio na zemlju preda mnom.",
			"9": "<sup>9</sup> Ali eto, sin ti se je rodio. On će biti čovjek mira, i ja ću mu pri baviti mir od svih neprijatelja njegovih unaokolo. Ta njegovo je ime Salomon. Mir i pokoj dat ću Izraelu u dane njegove.",
			"10": "<sup>10</sup> On će sagraditi hram imenu mojemu. On će mi biti sin, a ja ću mu biti otac. Njegov kraljevsko prijestolje utvrdit ću dovijeka nad Izraelom.",
			"11": "<sup>11</sup> I sada, sine moj, neka bude Gospod s tobom, da ti uspije sagraditi hram Gospoda, Boga tvojega, kao što ti je obećao!",
			"12": "<sup>12</sup> I neka ti Gospod, kad te postavi nad Izraelom, dadne razum i mudrost, da držiš zakon Gospoda, Boga svojega!",
			"13": "<sup>13</sup> Tada će ti to uspjeti, ako brižno uzdržiš zakone i odredbe, što ih je Gospod zapovjedio Izraelcima preko Mojsija. Budi srčan i jak! Ne boj se i ne plaši se!",
			"14": "<sup>14</sup> Evo, u svoj nevolji svojoj pripravio sam za hram Gospodnji sto tisuća talenata zlata i milijun talenata srebra, usto mjedi i gvožđa, što se ne može izmjeriti, toliko ga ima. I za drvo i kamen pobrinuo sam se, a ti ćeš pridodati još više.",
			"15": "<sup>15</sup> A imaš mnogo i poslenika, kamenara, zidara i drvodjelja, onda ljudi vještih svakome poslu",
			"16": "<sup>16</sup> U zlatu, srebru, mjedi i gvožđu, bez broja. Prihvati se posla! Gospod neka je s tobom!\"",
			"17": "<sup>17</sup> Tada dade David svim knezovima Izraelovim naputak, da pomažu njegovu sinu Salomonu:",
			"18": "<sup>18</sup> \"Nije li bio s vama Gospod, Bog vaš, i nije li vam dao mir odasvuda? On dade u ruke moje prijašnje stanovnike ove zemlje. Sad je zemlja pokorena Gospodu i narodu njegovu.",
			"19": "<sup>19</sup> Pa upravite sad srce i pamet na to, da vjerno služite Gospodu, Bogu svojemu! Pođite na posao i sagradite svetište Boga, Gospoda, da se mogne unijeti kovčeg Gospodnji i sveto posuđe Božje u hram, koji će se sagraditi imenu Gospodnjemu!\""
		},
		"23": {
			"1": "<sup>1</sup> Kad je David bio star i sit života, postavi svojega sina Salomona kraljem nad Izraelom.",
			"2": "<sup>2</sup> Usto skupi sve knezove Izraelove i svećenike i Levite.",
			"3": "<sup>3</sup> Kad su bili izbrojeni Leviti od trideset godina i više, bilo ih je na broj s glave na glavu trideset i osam tisuća ljudi.",
			"4": "<sup>4</sup> David odredi: \"Od ovih imaju dvadeset i četiri tisuće služiti u hramu Gospodnjem. Šest tisuća bit će upravitelji i suci,",
			"5": "<sup>5</sup> Četiri tisuće vratari, i četiri tisuće hvalit će Gospoda uz glazbala, što ih dadoh načiniti za slavu.\"",
			"6": "<sup>6</sup> Tada ih razdijeli David u redove po sinovima Levijevim Geršonu, Kohatu i Merariju.",
			"7": "<sup>7</sup> Sinovima Geršonovim pripadali su Ladan i Šimej.",
			"8": "<sup>8</sup> Sinovi Ladanovi bili su knez Jehiel i Zetam i Joel, skupa tri.",
			"9": "<sup>9</sup> Sinovi Šimejevi bili su Šelomit, Haziel i Haran, skupa tri. To su bili obiteljski poglavari Ladanovaca.",
			"10": "<sup>10</sup> Daljni sinovi Šimejevi bili su Jahat, Zina, Jeuš i Berija, To su bili sinovi Šimejevi, skupa četiri.",
			"11": "<sup>11</sup> Jahat je bio poglavar, Zina drugi. Jeuš i Berija nijesu imali mnogo djece. Zato su tvorili samo jednu obitelj i jedan ured.",
			"12": "<sup>12</sup> Sinovi Kohatovi bili su Amram, Izhar, Hebron i Uziel, skupa četiri.",
			"13": "<sup>13</sup> Sinovi Amramovi bili su Aron i Mojsije. Aron bi odvojen i sa svojim sinovima zauvijek posvećen za Svetinju nad svetinjama, da vječno pred Gospodom prinose kad, da mu služe i da u ime njegovo blagoslivljaju.",
			"14": "<sup>14</sup> A Mojsije bio je čovjek Božji, Sinovi njegovi bili su ubrojeni u pleme Levijevo.",
			"15": "<sup>15</sup> Sinovi Mojsijevi bili su Geršom i Eliezer.",
			"16": "<sup>16</sup> Sin Geršonov bio je Šebuel, poglavar.",
			"17": "<sup>17</sup> Sin Eliezerov bio je Rehabja, poglavar. Drugih sinova nije imao Eliezer. Ali se sinovi Rehabjini umnožiše veoma.",
			"18": "<sup>18</sup> Sin Izharov bio je Šelomit, poglavar.",
			"19": "<sup>19</sup> Sinovi Hebronovi bili su Jerija, poglavar, Amarija drugi, Jahaziel treći, Jekamam četvrti.",
			"20": "<sup>20</sup> Sinovi Uzielovi bili su Mika, poglavar, Ješija drugi.",
			"21": "<sup>21</sup> Sinovi Merarijevi bili su Mahli i Muši. Sinovi Mahlijevi bili su Eleazar i Kiš.",
			"22": "<sup>22</sup> Eleazar umrije, a ne ostavi iza sebe sirova; imao je samo kćeri.",
			"23": "<sup>23</sup> Sinovi Mušijevi bili su Mahli, Eder i Jerimot, skupa tri.",
			"24": "<sup>24</sup> To su bili, po svojim obiteljima poredani, Leviti, obiteljski poglavari, koji su bili izbrojeni po broju imena s glave na glavu, koji su bili zaposleni kod službe u hramu Gospodnjem, od dvadeset godina i više.",
			"25": "<sup>25</sup> Jer David pomisli: \"Gospod, Bog Izraelov, dade mir narodu svojemu. Sad će stanovati za sva vremena u Jerusalemu.\"",
			"26": "<sup>26</sup> Pa tako i Leviti nijesu više trebali nositi sveti šator i sve za njegovu službu potrebno posuđe.",
			"27": "<sup>27</sup> Jer po posljednjim zapovijedima Davidovim bili su Leviti izbrojeni od dvadeset godina i više.",
			"28": "<sup>28</sup> Njihova uredovna dužnost bila je sad u tom, da pomažu potomcima Aronovim kod službe u hramu Gospodnjem kao nadzornici nad predvorjima i ćelijama, i da drže u čistoći sve stvari svete i da rade oko službe u hramu Božjem.",
			"29": "<sup>29</sup> Osim toga imali su brigu oko kruhova postavljenih i oko brašna za prinose i oko nekvasnih kolača, oko tavica i oko svega, što se peče, i oko svakoga utega i mjere.",
			"30": "<sup>30</sup> I morali su svako jutro pristupiti da obave molitve hvale i zahvale u čast Gospodu, i tako uveče.",
			"31": "<sup>31</sup> I morali su služiti, kad se god prinose žrtve paljenice Gospodu, u subote, mjesece mlađake i blagdane, u broju po redu svojemu svagda pred Gospodom.",
			"32": "<sup>32</sup> Tako su imali službu obavljati u šatoru zavjeta i službu u svetinji i službu da pomognu potomcima Aronovim, suplemenicima svojim, kod njihova posla u hramu Gospodnjem."
		},
		"24": {
			"1": "<sup>1</sup> I potomci Aronovi imali su svoje redove, Sinovi Aronovi bili su Nadab, Abihu, Eleazar i Itamar.",
			"2": "<sup>2</sup> Nadab i Abihu umriješe prije oca svojega, a ne ostaviše iza sebe sinova. Zato su imali svećeničku službu samo Eleazar i Itamar,",
			"3": "<sup>3</sup> I David ih razdijeli: Sadoka od potomaka Eleazarovih i Ahimeleka od potomaka Itamarovih po redu njihovu u službi njihovoj.",
			"4": "<sup>4</sup> A jer se je našlo, da su potomci Eleazarovi brojili više obiteljskih poglavara, negoli potomci Itamarovi, zato ih razdijeliše tako, da je na potomke Eleazarove došlo šesnaest, a na potomke Itamarove osam obiteljskih poglavara.",
			"5": "<sup>5</sup> A razdijeliše ih ždrijebom, i jedne i druge, jer je bilo knezova svetinje i knezova Božjih i među potomcima Eleazarovim i među potomcima Itamarovim.",
			"6": "<sup>6</sup> Šemaja, sin Netanelov, pisar među Levitima, popisa ih u nazočnosti kralja i knezova i svećenika Sadoka i Ahimeleka, sina Abiatarova, i obiteljskih poglavara svećenika i Levita. Po jedna obitelj bila je izabrana za Eleazara i po jedna za Itamara.",
			"7": "<sup>7</sup> Prvi ždrijeb pade na Jehojariba, drugi na Jedaja,",
			"8": "<sup>8</sup> Treći na Harima, četvrti na Seorima,",
			"9": "<sup>9</sup> Peti na Malkiju, šesti na Mijamina,",
			"10": "<sup>10</sup> Sedmi na Hakosa, osmi na Abiju,",
			"11": "<sup>11</sup> Deveti na Ješuu, deseti na Šekaniju,",
			"12": "<sup>12</sup> Jedanaesti na Elijašiba, dvanaesti na Jakima.",
			"13": "<sup>13</sup> Trinaesti na Hupu, četrnaesti na Ješebaba,",
			"14": "<sup>14</sup> Petnaesti na Bilgu, šesnaesti na Imera,",
			"15": "<sup>15</sup> Sedamnaesti na Hezira, osamnaesti na Hapisesa,",
			"16": "<sup>16</sup> Devetnaesti na Petahju, dvadeseti na Ezekiela,",
			"17": "<sup>17</sup> Dvadeset prvi na Jakina, dvadeset drugi na Gamula,",
			"18": "<sup>18</sup> Dvadeset treći na Delaju, dvadeset četvrti na Maazju.",
			"19": "<sup>19</sup> To je red njihov za službu njihovu, kojim bi ulazili u hram Gospodnji po odredbi izdanoj od njihova oca Arona, kako mu je bio zapovjedio Gospod, Bog Izraelov.",
			"20": "<sup>20</sup> A što se tiče ostalih potomaka Levijevih, to je vukao lozu od potomaka Amramovih Šubael i od potomaka Šubaelovih Jehdeja,",
			"21": "<sup>21</sup> Od potomaka Rehabjinih poglavar Jišija,",
			"22": "<sup>22</sup> Od Izharovaca Šelomot, od potomaka Šelomotovih Jahat.",
			"23": "<sup>23</sup> Potomci Hebronovi bili su Jerija, drugi Amarija, treći Jahaziel, četvrti Jekaman.",
			"24": "<sup>24</sup> Potomci Uzielovi bili su Mika, od potomaka Mikinih Šamir.",
			"25": "<sup>25</sup> Brat Mikin bio je Jišija; od potomaka Jišijinih Zaharija.",
			"26": "<sup>26</sup> Potomci Merarijevi bili su Mahli i Muši, potomci njegova sina Jaazije.",
			"27": "<sup>27</sup> Merarijevci od njegova sina Jaazije bili su Šoham, Zakur i Ibri.",
			"28": "<sup>28</sup> Od Mahlija vukao je lozu Eleazar. Ovaj nije imao sinova,",
			"29": "<sup>29</sup> Od Kiša vukao je lozu potomak Kišov, Jerahmeel.",
			"30": "<sup>30</sup> Potomci Mušijevi bili su Mahli, Eder i Jerimot. To su bili potomci Levijevi po svojim obiteljima.",
			"31": "<sup>31</sup> I oni baciše ždrijeb kao njihovi suplemenici, potomci Aronovi, u nazočnosti kralja Davida, Sadoka i Ahimeleka i obiteljskih poglavara svećenika i Levita, i to obiteljski poglavari kao i njihovi mladi suplemenici."
		},
		"25": {
			"1": "<sup>1</sup> Nadalje odvoji David u društvu sa zapovjednicima vojske od sinova Asafovih, Hemanovih i Jedutunovih one, koji će se baviti svetom glazbom uz citare, harfe i cimbale. Popis onih, kojima je bila povjerena ova služba, ovaj je:",
			"2": "<sup>2</sup> Od sinova Asafovih: Zakur, Josip, Netanija i Asarela, sinovi Asafovi, da pomažu Asafu, kojemu je po naredbi kraljevoj bila povjerena sveta glazba.",
			"3": "<sup>3</sup> Od Jedutuna: sinovi Jedutunovi Gedalija, Zeri, Ješaja, Šimej, Hašabja, Matitja i , skupa šest, da pomažu svojega oca Jedutuna, kojemu je bilo povjereno udaranje uz citare u slavu Gospodu.",
			"4": "<sup>4</sup> Od Hemana: sinovi Hemanovi Bukija, Matanija, Uziel, Šebuel, Jerimot, Hananija, Hanani, Eliata, Gidalti, Romamti-Ezer, Jošbekaša, Maloti, Hotir, Mahaziot.",
			"5": "<sup>5</sup> Svi ovi bili su sinovi Hemana, vidioca kraljeva u božanskim stvarima. Da uzvisi moć njegovu, bio je Bog dao Hemanu četrnaest sinova i tri kćeri.",
			"6": "<sup>6</sup> Ovi svi bili su po odredbi kraljevoj zaposleni službom u hramu Božjem, da pomažu svojim ocima Asafu, Jedutunu i Hemanu kod pjevanja u hramu Gospodnjem uz cimbale, harfe i citare.",
			"7": "<sup>7</sup> Bilo ih je na broj dvjesta i osamdeset i osam skupa s drugovima njihovim, koji su bili u pjesmama Gospodnjim dobro izvježbani i vješti.",
			"8": "<sup>8</sup> I kad, da odrede red u službi, baciše ždrijeb, mlađi kao i stariji, učitelji skupa s učenicima,",
			"9": "<sup>9</sup> Pade od Asafa prvi ždrijeb na Josipa, drugi na Gedaliju s braćom i sinovima njegovim, skupa dvanaest;",
			"10": "<sup>10</sup> Treći na Zakura sa sinovima i braćom njegovom, skupa dvanaest;",
			"11": "<sup>11</sup> Četvrti na Jizrija sa sinovima i braćom njegovom, skupa dvanaest;",
			"12": "<sup>12</sup> Peti na Netaniju sa sinovima i braćom njegovom, skupa dvanaest;",
			"13": "<sup>13</sup> Šesti na Bukiju sa sinovima i braćom njegovom, skupa dvanaest;",
			"14": "<sup>14</sup> Sedmi na Ješarelu sa sinovima i braćom njegovom, skupa dvanaest;",
			"15": "<sup>15</sup> Osmi na Ješaju sa sinovima i braćom njegovom, skupa dvanaest;",
			"16": "<sup>16</sup> Deveti na Mataniju sa sinovima i braćom njegovom, skupa dvanaest;",
			"17": "<sup>17</sup> Deseti na Šimeju sa sinovima i braćom njegovom, skupa dvanaest;",
			"18": "<sup>18</sup> Jedanaesti na Azarela sa sinovima i braćom njegovom, skupa dvanaest;",
			"19": "<sup>19</sup> Dvanaesti na Hašabju sa sinovima i braćom njegovom, skupa dvanaest;",
			"20": "<sup>20</sup> Trinaesti na Šubaela sa sinovima i braćom njegovom, skupa dvanaest;",
			"21": "<sup>21</sup> Četrnaesti na Matitju sa sinovima i braćom njegovom, skupa dvanaest;",
			"22": "<sup>22</sup> Petnaesti na Jerimota sa sinovima i braćom njegovom, skupa dvanaest;",
			"23": "<sup>23</sup> Šesnaesti na Hananiju sa sinovima i braćom njegovom, skupa dvanaest;",
			"24": "<sup>24</sup> Sedamnaesti na Jošbekaša sa sinovima i braćom njegovom, skupa dvanaest;",
			"25": "<sup>25</sup> Osamnaesti na Hananija sa sinovima i braćom njegovom, skupa dvanaest;",
			"26": "<sup>26</sup> Devetnaesti na Malotija sa sinovima i braćom njegovom, skupa dvanaest;",
			"27": "<sup>27</sup> Dvadeseti na Elijatu sa sinovima i braćom njegovom, skupa dvanaest;",
			"28": "<sup>28</sup> Dvadeset prvi na Hotira sa sinovima i braćom njegovom, skupa dvanaest;",
			"29": "<sup>29</sup> Dvadeset drugi na Gidaltija sa sinovima i braćom njegovom, skupa dvanaest;",
			"30": "<sup>30</sup> Dvadeset treći na Mahaziota sa sinovima i braćom njegovom, skupa dvanaest;",
			"31": "<sup>31</sup> Dvadeset četvrti na Romamti-Ezera sa sinovima i braćom njegovom, skupa dvanaest."
		},
		"26": {
			"1": "<sup>1</sup> Redovi vratara bili su: od sinova Korahovih bio je Mešelemja, sin Korahov, između sinova Asafovih.",
			"2": "<sup>2</sup> Sinovi Mešelemjini bili su Zaharija, prvorođenac, Jediael drugi, Zebadija treći, Jatniel četvrti,",
			"3": "<sup>3</sup> Elam peti, Johanan šesti, Elijehonaj sedmi.",
			"4": "<sup>4</sup> Sinovi Obed-Edomovi bili su Šemaja, prvorođenac, Jozabad drugi, Joah treći, Sakar četvrti, Netanel peti,",
			"5": "<sup>5</sup> Amiel šesti, Isakar sedmi, Peuletaj osmi. Tako ga je bio blagoslovio Bog.",
			"6": "<sup>6</sup> I njegovu sinu Šemaji bili su se rodili sinovi, koji postaše obiteljski poglavari; jer su bili valjani ljudi.",
			"7": "<sup>7</sup> Sinovi Šemajini bili su Otni, Rafael, Obed, Elzabad i braća njegova, valjani ljudi, Elihu i Semakija.",
			"8": "<sup>8</sup> Ovi svi bili su potomci Obed-Edomovi, oni i njihovi sinovi i braća, valjani ljudi, sposobni za službu, šezdeset i dva od Obed-Edoma.",
			"9": "<sup>9</sup> I Mešelemja imao je sinova i braće, valjani ljudi, skupa osamnaest.",
			"10": "<sup>10</sup> Sinovi Hosini koji su pripadali potomcima Merarijevim, bili su Šimri, poglavar - premda nije bio prvorođenac, bio ga je otac njegov postavio za poglavara -",
			"11": "<sup>11</sup> Hilkija drugi, Tebalija treći, Zaharija četvrti. Svih sinova i braće Hosine bilo je trinaest.",
			"12": "<sup>12</sup> Ovim redovima vratara, i to obiteljskim poglavarima, dopadoše isto tako kao njihovim suplemenicima službeni poslovi u službi hrama Gospodnjega.",
			"13": "<sup>13</sup> Kad po obiteljima, za mlađe kao i za starije, baciše ždrijeb za pojedina vrata,",
			"14": "<sup>14</sup> Pade ždrijeb za istočnu stranu na Šelemju. I za njegova sina Zahariju, mudroga savjetnika, baciše ždrijeb, i njegov ždrijeb pade na sjevernu stranu,",
			"15": "<sup>15</sup> Za Obed-Edoma na južnu stranu i za sinove njegove na riznicu;",
			"16": "<sup>16</sup> Za Šupima i Hosu na zapadnu stranu kod vrata Šaleket na cesti, što se penje gore, jedna straža uz drugu.",
			"17": "<sup>17</sup> Na istočnoj strani bilo je šest Levita, na sjevernoj strani svaki dan četiri, na južnoj strani svaki dan četiri, kod riznice po dva,",
			"18": "<sup>18</sup> Kod prigrade na zapadnoj strani četiri za cestu i dvije za prigradu.",
			"19": "<sup>19</sup> To su bili redovi vratara među potomcima sinova Korahovih i Merarijevih.",
			"20": "<sup>20</sup> Njihovi suplemenici, Leviti, imali su nadzor nad blagom hrama Božjega i nad blagom od svetih darova.",
			"21": "<sup>21</sup> Potomci Ladanovi, to jest potomci sinova Geršonovih, koji vuku lozu od Ladana, obiteljski poglavari Ladana, sina Geršonova Jehieli,",
			"22": "<sup>22</sup> Potomci Jehielovi, Zetam i njegov brat Joel, imali su odgovornost nad blagom u hramu Gospodnjem.",
			"23": "<sup>23</sup> što Be tiče sinova Amramovih, Izharovih, Hebronovih i Uzielovih,",
			"24": "<sup>24</sup> To je bio Šubael, potomak Geršoma, sina Mojsijeva, vrhovni nadzornik nad blagom.",
			"25": "<sup>25</sup> Što se tiče njegovih suplemenika od Eliezera, to je bio njegov sin Rehabja, a njegov sin Izaija, a njegov sin Joram, a njegov sin Zikri, a njegov sin Šelomit.",
			"26": "<sup>26</sup> Ovaj Šelomit i braća njegova vodili su nadzor nad svim blagom od svetih darova, što su ih bili po svetili kralj David i obiteljski poglavari i tisućnici i stotnici i vojskovođe.",
			"27": "<sup>27</sup> Oni su ih bili posvetili od ratnoga plijena, da se opravi hram Gospodnji.",
			"28": "<sup>28</sup> I sve, što su bili posvetili vidjelac Samuel i Saul, sin Kišov, i Abner, sin Nerov, i Joab, sin Serujin, i sve drugo posvećeno stajalo je pod nadzorom Šelomita i braće njegove.",
			"29": "<sup>29</sup> Od sinova Izharovih bili su Konanija i sinovi njegovi namješteni kao upravitelji i suci za vanjske poslove u Izraelu.",
			"30": "<sup>30</sup> Od sinova; Hebronovih Hašabja i njegovi suplemenici, valjani ljudi, tisuća i sedam stotina na broj, bili su postavljeni nad upravom Izraela zapadno od Jordana za sve poslove Gospodnje i za službu kraljevu.",
			"31": "<sup>31</sup> Sinovima Hebronovim pripadao je Jerija, poglavar porodicama i obiteljima sinova Hebronovih U četrdesetoj godini vladanja Davidova potražiše ih, i nađe se među njima u Jazeru u Gileadu valjanih ljudi.",
			"32": "<sup>32</sup> Braću njegovu, valjane ljude, dvije tisuće i sedam stotina obiteljskih poglavara, postavi kralj David nad sinovima Rubenovim, Gadovim i polovinom plemena Manasehova za sve poslove Božje i kraljeve."
		},
		"27": {
			"1": "<sup>1</sup> Ovo je popis izraelskih poglavara obiteljskih i tisućnika i stotnika i činovnika, koji su služili kralju u svim poslovima redova, koji su dolazili i odlazili od mjeseca do mjeseca, svakoga mjeseca u godini. Svaki je red brojio dvadeset i četiri tisuće momaka.",
			"2": "<sup>2</sup> Nad prvim redom u prvom mjesecu bio je Jašobam, sin Zabdielov. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"3": "<sup>3</sup> On je pripadao potomcima Peresovim i bio je poglavar svih vojskovođa za prvi mjesec.",
			"4": "<sup>4</sup> Nad redom drugoga mjeseca bio je Dodaj, Ahošanin, i to nad svojim redom i nad redom kneza Miklota. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"5": "<sup>5</sup> Vođa treće vojske za treći mjesec bio je poglavar Benaja, sin svećenika Jehojade. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"6": "<sup>6</sup> Ovaj Benaja bio je junak među tridesetoricom i nad tridesetoricom, Na čelu njegova reda stajao je njegov sin Amizabad.",
			"7": "<sup>7</sup> Četvrti za četvrti mjesec bio je Joabov brat Asahel, i za njim njegov sin Zebadija. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"8": "<sup>8</sup> Peti za peti mjesec bio je knez Šamhut, Izrahejin. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"9": "<sup>9</sup> Šesti za šesti mjesec bio je Ira, sin Ikešov, iz Tekoe. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"10": "<sup>10</sup> Sedmi za sedmi mjesec bio je Heles, Pelonjanin, od sinova Efraimovih. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"11": "<sup>11</sup> Osmi za osmi mjesec bio je Hušatejin Sibekaj, od sinova Zarhijevih. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"12": "<sup>12</sup> Deveti za deveti mjesec bio je Abiezer iz Anatota, od sinova Benjaminovih. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"13": "<sup>13</sup> Deseti za deseti mjesec bio je Maharaj iz Netofe, od sinova Zarhijevih. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"14": "<sup>14</sup> Jedanaesti za jedanaesti mjesec bio je Benaja iz Piratona, od sinova Efraimovih. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"15": "<sup>15</sup> Dvanaesti za dvanaesti mjesec bio je Heldaj iz Netofe, od sinova Otnielovih. U njegovu redu bilo je dvadeset i četiri tisuće momaka.",
			"16": "<sup>16</sup> Knezovi plemena Izraelovih bili su: \"kod sinova Rubenovih knez Eliezer, sin Zikrijev; kod sinova Simeonovih Šefatija, sin Maakin;",
			"17": "<sup>17</sup> Kod sinova Levijevih Hašabja, sin Kemuelov; kod sinova Aronovih Sadok;",
			"18": "<sup>18</sup> Kod sinova Judinih Elihu, brat Davidov; kod sinova Isakarovih Omri, sin Mihaelov;",
			"19": "<sup>19</sup> Kod sinova Zebulunovih Išmaja, sin Obadijin; kod sinova Naftalijevih Jerimot sin Azrielov;",
			"20": "<sup>20</sup> Kod sinova Efraimovih Hošea, sin Azazjin; kod polovine plemena Manasehova Joel, sin Pedajin;",
			"21": "<sup>21</sup> Kod polovine plemena Manasehova u Gileadu Ido, sin Zaharijin; kod sinova Benjaminovih Jaasiel, sin Abnerov;",
			"22": "<sup>22</sup> Kod sinova Danovih Azarel, sin Jerohamov. To su bili knezovi plemena Izraelovih.",
			"23": "<sup>23</sup> David nije bio dao uzeti broj onih, koji su bili ispod dvadeset godina, jer je bio Gospod obećao, da će umnožiti Izraela kao zvijezde na nebu,",
			"24": "<sup>24</sup> Joab, sin Serujin, bio je počeo popis naroda, ali ga nije dovršio, jer je bio došao na Izraela gnjev Božji. Zato taj broj nije bio unesen u knjigu povijesti kralja Davida.",
			"25": "<sup>25</sup> Nad blagom kraljevim vodio je nadzor Azmavet, sin Adielov, a nad posjedima po zemlji, po gradovima, selima i kulama Jonatan, sin Uzijin.",
			"26": "<sup>26</sup> Nad ratarima, koji su obrađivali zemlju, vodio je nadzor Ezri, sin Kelubov;",
			"27": "<sup>27</sup> Nad vinogradima Šimej, iz Rame, nad posuđem u pivnicama Zabdi, Šifmejac;",
			"28": "<sup>28</sup> Nad maslinicima i smokvama u nizini Hanan, Gederanin; nad sudovima za ulje Joaš;",
			"29": "<sup>29</sup> Nad govedima, što su pasla u šaronskoj ravnici, Šitraj, Šaronjanin; nad govedima u dolinama Šafat, sin Adlajev.",
			"30": "<sup>30</sup> Nad devama Obil, Išmaelac; nad magarcima Jehdeja, Meronoćanin;",
			"31": "<sup>31</sup> Nad ovcama Jaziz, Hagerejin. Svi ovi bili su upravitelji imetka, što ga je posjedovao kralj David.",
			"32": "<sup>32</sup> Jonatan, stric Davidov, mudar i vješt čovjek, bio je savjetnik. Jehiel, sin Hakmonijev, bio je odgojitelj sinova kraljevih.",
			"33": "<sup>33</sup> Ahitofel bio je kraljevski savjetnik, i Hušaj, Arkijanin, prijatelj kraljev.",
			"34": "<sup>34</sup> Poslije Ahitofela bio je Jehojada, sin Benajin, i Abiatar. Joab je bio vojskovođa kraljev."
		},
		"28": {
			"1": "<sup>1</sup> David skupi u Jerusalemu sve odlične Izraelce, naime plemenske knezove, poglavare redova, što su stajali u službi kraljevoj, tisućnike i stotnike, upravitelje svega imanja i stada kralja i sinova njegovih i dvorane, junake i sve ljude ratnike valjane.",
			"2": "<sup>2</sup> Tada se diže kralj sa svojega sjedala i reče: \"Čujte me, braćo moja i narode moj! Ja sam bio sam naumio da sagradim dom za kovčeg zavjeta Gospodnjega i za podnožje nogama Boga našega. Ja sam učinio i priprave za gradnju.",
			"3": "<sup>3</sup> Ali mi Bog reče: \"Nećeš ti graditi kuće imenu mojemu; jer si ratnik i krv si prolijevao.",
			"4": "<sup>4</sup> Mene je Gospod, Bog Izraelov, bio izabrao iz sve kuće oca mojega, da budem kralj nad Izraelom dovijeka, jer Judu izabra za kneza i u plemenu Judinu kuću oca mojega, i nađe za dobro, da između sinova oca mojega učini mene kraljem nad svim Izraelom.",
			"5": "<sup>5</sup> A između svih sinova mojih - jer Gospod mi dade mnogo sinova - izabra mojega sina Salomona, da zasjedne prijestolje kraljevstva Gospodnjega nad Izraelom.",
			"6": "<sup>6</sup> On mi reče: Tvoj sin Salomon sagradit će kuću moju i trijemove moje, jer njega izabrah sebi za sina njemu ću biti otac.",
			"7": "<sup>7</sup> Zauvijek podignut ću kraljevstvo njegovo, ako ovako, kako je to danas, bude stajao na tom, da izvršuje moje zapovijedi i zakone.",
			"8": "<sup>8</sup> I sada opominjem vas pred očima svega Izraela, pred zajednicom Gospodnjom, i pred ušima Boga našega: \"Držite brižno sve zapovijedi Gospoda, Boga svojega, da ostanete u posjedu ove lijepe zemlje i da je ostavite u baštinu potomcima svojim za sva vremena!",
			"9": "<sup>9</sup> A ti, sine moj Salomone, drži s Bogom oca svojega i služi mu cijelim srcem i dušom dragovoljnom; jer Gospod ispituje sva srca i zna svaku pomisao i želju! Ako ga budeš tražio, on će dati da ga nađeš. Ako li ga ostaviš, odbacit će te zauvijek.",
			"10": "<sup>10</sup> Pa vidi sada, da te je Gospod izabrao, da mu sagradiš kuću za svetinju pođi na posao odvažno!\"",
			"11": "<sup>11</sup> Potom predade David svojemu sinu Salomonu sliku od trijema i njegovih prostorija, od riznica, gornjih soba i nutarnjih prostorija, i od prostora za pomirilište,",
			"12": "<sup>12</sup> Tako isto sliku svega, što je bio smislio: \"liku od trijemova u hramu Gospodnjem i od ćelija unaokolo, sliku od riznica kuće Božje i od riznica za svete darove,",
			"13": "<sup>13</sup> Nadalje sliku za redove svećeničke i levitske i za poslužne poslove u hramu Gospodnjem i za sve posuđe za službu u hramu Gospodnjem.",
			"14": "<sup>14</sup> Zlata mu predade toliko, koliko je trebalo za svaki pojedini sud u službi Božjoj, tako isto srebra za sve srebrno posuđe,",
			"15": "<sup>15</sup> Nadalje koliko mu je trebalo novaca za svijećnjake zlatne i za njihove žiške zlatne, po određenoj težini njihovoj; isto tako koliko mu je trebalo srebra za razne srebrne svijećnjake i žiške prema njihovoj težini i potrebi;",
			"16": "<sup>16</sup> Nadalje koliko mu je trebalo zlata za stolove kruhova postavljenih, za svaki pojedini što, i srebro za stolove srebrne,",
			"17": "<sup>17</sup> Čistoga zlata za viljuške, škropionice i kotliće, za razne vrčeve zlatne, po težini njihovoj, i za razne vrčeve srebrne, po težini njihovoj;",
			"18": "<sup>18</sup> Nadalje čistoga zlata za žrtvenik kadioni, po težini. I sliku od kola predade mu, naime od kola zlatnih kerubina, koji raširenim krilima prekrivaju kovčeg zavjeta Gospodnjega,",
			"19": "<sup>19</sup> \"O svemu tome,\" reče David, \"o svim radovima za izvedbu te građevne osnove poučio me je Gospod pismom iz ruke njegove.\"",
			"20": "<sup>20</sup> Tada reče David svojemu sinu Salomonu: \"Pođi na posao odvažno i jako! Ne boj se i ne plaši se, jer Bog Gospod, Bog moj bit će s tobom. On te neće ostaviti, dok se ne dovrše svi radovi za službu u hramu Gospodnjem.",
			"21": "<sup>21</sup> Već su spremni redovi svećenički i levitski za svu službu u hramu Božjem. Imaš uza se za svaki posao ljudi, koji su voljni i vješti svakoj službi. I knezovi i sav narod stoje ti uvijek na službu.\""
		},
		"29": {
			"1": "<sup>1</sup> Potom reče kralj David svoj zajednici: \"Moj sin Salomon, jedini, kojega je Bog izabrao, još je mlad i nježan, a djelo je veliko, jer nije za čovjeka određen ovaj dvor, nego za Boga, Gospoda.",
			"2": "<sup>2</sup> I ja svom snagom svojom pripravih za hram Boga svojega zlata za sudove zlatne, srebra za srebrne, mjedi za mjedene, gvožđa za gvozdene i drva za drvene, kamena oniksova i crnosjajna i šarolika kamenja za okvire i drugoga dragoga kamenja svake vrste i mramora izobilja.",
			"3": "<sup>3</sup> I još iz ljubavi prema hramu Boga mojega ono, što imam u vlastitom posjedu zlata i srebra, dat ću, osim svega toga, što sam već pripravio za svetu kuću:",
			"4": "<sup>4</sup> Tri tisuće zlatnih talenata od ofirskoga zlata i sedam tisuća talenata čistoga srebra, da se tim oblože zidovi hramskim prostorijama,",
			"5": "<sup>5</sup> I za zlatno i srebrno posuđe svake vrste i za sve radove ruku umjetničkih. A tko je još spreman da koji dar daruje za Gospoda?\"",
			"6": "<sup>6</sup> l Tada darovaše dragovoljno obiteljski poglavari, knezovi plemena Izraelovih, tisućnici i stotnici i činovnici u službi kraljevoj.",
			"7": "<sup>7</sup> Oni dadoše za gradnju hrama Božjega pet tisuća talenata zlata, deset tisuća drahmi, deset tisuća talenata srebra, osamnaest tisuća talenata mjedi i sto tisuća talenata gvožđa.",
			"8": "<sup>8</sup> Tko je imao dragoga kamenja, predade ga za blago hrama Gospodnjega Geršonovcu Jehielu.",
			"9": "<sup>9</sup> Narod se je radovao dragovoljnim darovima, jer su pobožnim srcem dragovoljno prinosili Gospodu darove svoje. I kralj David radovao se veoma.",
			"10": "<sup>10</sup> I blagoslovio je David Gospoda pred svom zajednicom. David je molio: \"Blagoslovljen neka si, Gospode, Bože oca Izraela, odvijeka dovijeka!",
			"11": "<sup>11</sup> Tvoja je, Gospode, veličina, moć, slava, hvala i visost, jer tvoje je sve na nebu i na zemlji. Tvoje je, Gospode, gospodstvo, i ti se uzdižeš kao Gospod nada sve.",
			"12": "<sup>12</sup> Bogatstvo i čast dolaze od tebe, i ti si vladar nad svim. U tvojoj je ruci snaga i moć i u tvojoj je vlasti da učiniš velikim i moćnim kogagod hoćeš.",
			"13": "<sup>13</sup> I zahvaljujemo ti, Bože naš, i častimo ime tvoje slavno.",
			"14": "<sup>14</sup> A tko sam ja, i što je narod moj, da smo mogli u takovoj množini prinijeti svojih darova? Ne, od tebe dolazi sve, i iz tvoje ruke darovasmo tebi.",
			"15": "<sup>15</sup> Ta mi smo samo tuđini i gosti pred tobom kao svi oci naši: Kao sjena dani su našega života na zemlji i bez nadanja.",
			"16": "<sup>16</sup> Gospode, Bože naš, sve ovo blago, što ga pripravismo, da ti sagradimo kuću za sveto ime tvoje, dolazi iz tvoje ruke, i sve je to tvoje",
			"17": "<sup>17</sup> Ali ja znam, Bože moj, da ti ispituješ srca i da voliš iskrenost. I tako ja sve ovo darovah iskrenim srcem i s radošću vidjeh, kako ti je ovaj narod tvoj darivao darove.",
			"18": "<sup>18</sup> Gospode, Bože naših otaca Abrahama, Izaka i Izraela, uzdrži uvijek u srcu naroda svojega takvo mišljenje i volju takvu i upravljaj srce njihovo k sebi!",
			"19": "<sup>19</sup> Podaj mojemu sinu Salomonu poslušno srce, da drži tvoje zapovijedi, tvoje odredbe i zakone, i da čini sve to i sagradi sjajnu kuću, koju sam pripravio!\"",
			"20": "<sup>20</sup> Tada reče David svoj zajednici: \"Blagoslivljajte Gospoda, Boga svojega!\" I sva je zajednica blagoslivljala Gospoda, Boga otaca svojih. Oni se nakloniše i baciše se ničice pred Gospodom i pred kraljem.",
			"21": "<sup>21</sup> Zaklaše Gospodu žrtve i drugo jutro prinesoše Gospodu žrtve paljenice: \"tisuću junaca, tisuću ovnova, tisuću janjaca s naljevima njihovim, osim toga žrtava zaklanica mnogo za sav narod Izraelov.",
			"22": "<sup>22</sup> Jeli su i pili su onaj dan pred Gospodom u velikoj radosti. Tada učiniše Salomona, sina Davidova, po drugi put kraljem i pomazaše njega Gospodu za kneza, a Sadoka za svećenika",
			"23": "<sup>23</sup> Tako je sjedio Salomon kao kralj na prijestolju Gospodnjem na mjesto svojega oca Davida, i imao je sreću. Sav ga je Izrael slušao.",
			"24": "<sup>24</sup> Svi knezovi i junaci i svi sinovi kralja Davida podvrgoše se kralju Salomonu.",
			"25": "<sup>25</sup> Gospod dade, te Salomon dođe do veoma visokog ugleda kod svega Izraela i podijeli mu tako sjajno kraljevanje, kako to nije bio imao prije njega niti jedan kralj nad Izraelom.",
			"26": "<sup>26</sup> David, sin Jesejev, bio je vladao nad svim Izraelom.",
			"27": "<sup>27</sup> A vremena, za koje je bio kralj nad Izraelom, bilo je četrdeset godina U Hebronu je vladao sedam godina, u Jerusalemu trideset i tri godine.",
			"28": "<sup>28</sup> On umrije u visokoj starosti, bogat danima života, imovinom i čašću. Njegov sin Salomon postade kralj mjesto njega.",
			"29": "<sup>29</sup> Povijest kralja Davida, prijašnja i potonja, zapisana je u povijesti vidioca Samuela i u povijesti proroka Natana i u povijesti vidioca Gada,",
			"30": "<sup>30</sup> Sa svim njegovim kraljevanjem, pobjedama njegovim i događajima, što prođoše preko njega i Izraela i preko svih drugih kraljevina zemaljskih."
		}
	}
};
module.exports = book;