var book = {
	"name": "1. Ljetopisa",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adam, Set, Enoš,",
			"2": "<sup>2</sup> Kenan, Mahalel, Jered,",
			"3": "<sup>3</sup> Henoh, Metušelat, Lamek,",
			"4": "<sup>4</sup> Noe, Sem, Šam i Jafet.",
			"5": "<sup>5</sup> Sinovi Jafetovi: Gomer, Magog, Madaj, Javan, Tubal, Mešeh i Tiraz.",
			"6": "<sup>6</sup> Gomerovi sinovi: Aškenaz, Difat i Togarma .",
			"7": "<sup>7</sup> Javanovi sinovi: Eliša, Tarsis, Hitim i Rodanim.",
			"8": "<sup>8</sup> Sinovi Šamovi: Kuš, Misraim, Put i Kanaan.",
			"9": "<sup>9</sup> Sinovi Kušovi: Seva, Havila, Savta, Raema i Savteka. Sinovi Raemovi: Saba i Dedan.",
			"10": "<sup>10</sup> Kuš rodi Nemroda. On bi prvi junak na zemlji.",
			"11": "<sup>11</sup> Misraim porodi ljude iz Luda, Einaima i Naftuaha,",
			"12": "<sup>12</sup> ljude iz Patrosa, one iz Kasluaha, otkud iziđoše Filistinci, i oni iz Kaftora.",
			"13": "<sup>13</sup> Kanaan rodi Sidona svojeg prvijenca i Heta,",
			"14": "<sup>14</sup> Jebuzita, *Amorita, Girgašita,",
			"15": "<sup>15</sup> Hivita, Arkita, Sinita,",
			"16": "<sup>16</sup> Arvadita, Semarita, Hamatita.",
			"17": "<sup>17</sup> Sinovi Semovi: Elam, Asur, Arpahšad, Lud, Aram, Us, Hul, Geter i Mešek.",
			"18": "<sup>18</sup> Arpahšad rodi Šelaha, a Šelah rodi Hebera.",
			"19": "<sup>19</sup> Heberu se rodiše dva sina. Prvi se zvao Peleg, jer u njegovo doba zemlja bi podijeljena  a njegov se brat zvaše Joktan.",
			"20": "<sup>20</sup> Joktan rodi Almodada, Šelefa, Hasarmaveta, Jeraha,",
			"21": "<sup>21</sup> Hadorama, Uzala, Dikla,",
			"22": "<sup>22</sup> Ebala, Abmaela, Saba",
			"23": "<sup>23</sup> Ofira, Havila, Jobaba. To su svi sinovi Joktanovi.",
			"24": "<sup>24</sup> Sem, Arpakšad, Šelah,",
			"25": "<sup>25</sup> Eber, Peleg, Reu,",
			"26": "<sup>26</sup> Serug, Nahor, Terah,",
			"27": "<sup>27</sup> Abram, koji je Abraham.",
			"28": "<sup>28</sup> Sinovi Abrahamovi: Izak i Ismael.",
			"29": "<sup>29</sup> Evo njihovih obitelji: Nabajot prvorođenac Ismaelov, Kedar, Adbel i Mivsam,",
			"30": "<sup>30</sup> Mišma i Duma, Masa, Hadad i Tema,",
			"31": "<sup>31</sup> Jetur, Nafiš i Kedma. To su sinovi Ismaelovi.",
			"32": "<sup>32</sup> Sinovi Keture, konkubine  Abrahamove: ona rodi Zimrana, Jokšana, Medana, Madiana, Jišbaka i Šuaha. Sinovi Jokšanovi: Saba i Dedan.",
			"33": "<sup>33</sup> Sinovi Madianovi: Eifa, Efer, Hanok, Abida, i Eldaa. To su sve sinovi od Keture.",
			"34": "<sup>34</sup> Abraham porodi Izaka. Sinovi Izakovi: Ezav i Izrael.",
			"35": "<sup>35</sup> Sinovi Ezavljevi: Elifaz, Reuel, Jeuš, Jaelam i Korah.",
			"36": "<sup>36</sup> Sinovi Elifazovi: Teman, Omar, Sefi, Gaetam, Kenaz, Timna, i Amalek.",
			"37": "<sup>37</sup> Sinovi Reuelovi: Nahat, Zerah, Šama i Miza.",
			"38": "<sup>38</sup> Sinovi Seirovi: Lotan, Šoval, Siveon, Ana, Dišon, Eser i Dišan.",
			"39": "<sup>39</sup> Sinovi Lotanovi: Hori i Homam. Sestra Lotanova: Timna.",
			"40": "<sup>40</sup> Sinovi Šovalovi: Alijan, Manahat, Ebal, Šefi i Onam. Sinovi Siveonovi: Aja i Ana.",
			"41": "<sup>41</sup> Sin Anaov Dišon. Sinovi Dišonovi: Hamran, Ešban, Jitran i Keran.",
			"42": "<sup>42</sup> Sinovi Eserovi: Bilhan, Zavan, Jakan,. Sinovi Dišonovi: Us i Aran. :",
			"44": "<sup>44</sup> Bela umrije, a Jobab, sin Zerahov iz Bosre, zavlada na njegovom mjestu.",
			"45": "<sup>45</sup> Jobab umrije, a Hušam, iz zemlje Temanita, zavlada na njegovom mjestu.",
			"46": "<sup>46</sup> Hušam umrije, a Hadad, sin Bedadov, zavlada na njegovom mjestu. On izgradi Median u polju Moab; ime njegovog grada bijaše Avit.",
			"47": "<sup>47</sup> Hadad umrije, a Samla iz Masreka zavlada na njegovom mjestu.",
			"48": "<sup>48</sup> Samla umrije, a Šaul iz Rehobota na Eufratu zavlada na njegovom mjestu.",
			"49": "<sup>49</sup> Šaul umrije a Bal-Hanan, sin Akborov, zavlada na njegovom mjestu.",
			"50": "<sup>50</sup> BalHanan umrije, a Hadad zavlada na njegovom mjestu; ime njegovog grada bijaše Paj. Ime njegove žene bijaše Mehetavel, kćer Matrede, kćeri MeZahava.",
			"51": "<sup>51</sup> Hadad umrije. Poglavari Edomovi biše: poglavar Timna, poglavar Alja, poglavar Jetet,",
			"52": "<sup>52</sup> poglavar Oholivama, poglavar Ela, poglavar Pinon,",
			"53": "<sup>53</sup> poglavar Kenaz, poglavar Teman, poglavar Mivsar,",
			"54": "<sup>54</sup> poglavar Magdiel, poglavar Iram. To su poglavari Edomovi."
		},
		"2": {
			"1": "<sup>1</sup> Evo sinova Izraelovih: Ruben Simeon, Levi i Juda, Isakar, i Zabulon,",
			"2": "<sup>2</sup> Dan, Josip i Benjamin, Neftali, Gad i Ašer.",
			"3": "<sup>3</sup> Sinovi Judini: Er, Onan i Šela. Sve trojica njemu rodiše se od kćeri Šue Kanaanke. Ali Er, prvorođenac Judin, bi kriv u očima GOSPODOVIM koji ga usmrti.",
			"4": "<sup>4</sup> Tamar, njegova snaha, mu rodi Peresa i Zeraha, Sinovi Judini biše petorica sveukupno.",
			"5": "<sup>5</sup> Sinovi Peresovi: Hesron i Hemul.",
			"6": "<sup>6</sup> Sinovi Zerahovi: Zimri, Etan, Heman, Kalkol i Dara, petorica ukupno.",
			"7": "<sup>7</sup> Sinovi Karmijevi: Akar koji donese nesreću Izraelu učinivši se krivim od jedne nevjernosti glede zabrane .",
			"8": "<sup>8</sup> Sin Etanov: Azarija.",
			"9": "<sup>9</sup> Sinovi koji se rodiše Hesronu: Jerahmel, Ram i Kelubaj.",
			"10": "<sup>10</sup> Ram porodi AmiNadava, AmiNadav porodi Našhona, poglavara sinova Judinih.",
			"11": "<sup>11</sup> Našhon porodi Salmaa, Salma porodi Booza.",
			"12": "<sup>12</sup> Booz porodi Obeda. Obed porodi Jesea.",
			"13": "<sup>13</sup> Jese porodi Eliaba svog prvorođenog, AmiNadav drugi, Šimea, treći,",
			"14": "<sup>14</sup> Netanel četvrti, Radaj peti,",
			"15": "<sup>15</sup> Osem šesti, David sedmi.",
			"16": "<sup>16</sup> Njihove sestre bijahu Seruja i Abigail. Sinovi Serujini bijahu na broju trojica: Abšaj, Joab i Azahel.",
			"17": "<sup>17</sup> Abigail rodi Amaza, a otac Amazov bijaše Jeter Ismaelit.",
			"18": "<sup>18</sup> Kaleb, sin Hesronov izrodi sinove s Azuvaom, svojom ženom, i Jeriotom; evo njegovih sinova: Ješer, Šobab i Ardon.",
			"19": "<sup>19</sup> Azuva umrije, a Kaleb uze Efratu za ženu, a ona mu rodi Hura.",
			"20": "<sup>20</sup> Hur porodi Urija. Uri porodi Besalela.",
			"21": "<sup>21</sup> Potom, Hesron se sjedini s kćeri Makirovom, ocem Galaada, i oženi ju kad imaše 60 godina; ona mu rodi Seguba,",
			"22": "<sup>22</sup> Segub porodi Jaira, a ovaj imade 23 grada, u zemlji Galaad.",
			"23": "<sup>23</sup> Ali Gešur i Aram njima uzeše taborište Jair, Kenat i njihov okoliš: 60 gradova. Svi oni bijahu sinovi Makirovi, oca Galaada.",
			"24": "<sup>24</sup> Poslije smrti Hesronove, Kaleb se sjedini s Efratom, ženom Hesronovom, svojeg oca, a ona mu rodi Ašehura, oca Tekoa.",
			"25": "<sup>25</sup> Sinovi Jerahmela, prvorođenog Hesronovog, biše Ram, prvorođeni, Buna, Oren, Osem, Ahija.",
			"26": "<sup>26</sup> Jerahmel imade jednu drugu ženu, imenom Atara. Ona bi majka Onamova.",
			"27": "<sup>27</sup> Sinovi Ramovi, prvorođenog Jerahmeelovog, biše Maas, Jamin i Eker.",
			"28": "<sup>28</sup> Sinovi Onamovi biše Šamaj i Jada, i sinovi Šamajevi: Nadav i Abišur.",
			"29": "<sup>29</sup> Ime ženi Abišurovoj bijaše Abihail, i ona mu rodi Ahbana i Molida.",
			"30": "<sup>30</sup> Sinovi Nadavovi: Seled i Apaim. Seled umruije i ne imade sinova.",
			"31": "<sup>31</sup> Sinovi Apaimovi: Jišej. Sin Jišejev: Šešan. Sin Šešanov: Ahlaj.",
			"32": "<sup>32</sup> Sinovi Jadaovi, brata Šamajeva: Jeter i Jonatan. Jeter umrije i ne imade sinova.",
			"33": "<sup>33</sup> Sinovi Jonatanovi: Pelet i Zaza. To biše sinovi Jerahmelovi.",
			"34": "<sup>34</sup> Šešan ne imade sinove već kćeri. Šešan imaše jednog egipatskog roba, imenom Jarha.",
			"35": "<sup>35</sup> Šešan dade svoju kćer za ženu Jarhau svojem robu, a ona mu rodi Ataja.",
			"36": "<sup>36</sup> Ataj rodi Natana, Natan rodi Zavada.",
			"37": "<sup>37</sup> Zavad rodi Efala. Efal rodi Obeda.",
			"38": "<sup>38</sup> Obed rodi Jehua. Jehu rodi Azarija.",
			"39": "<sup>39</sup> Azarija rodi Helesa. Heles rodi Eleaza.",
			"40": "<sup>40</sup> Eleaz rodi Sismaja. Sismaj rodi Šaluma.",
			"41": "<sup>41</sup> Šalum rodi Jekmaja. Jekmaja rodi Elišama.",
			"42": "<sup>42</sup> Sinovi Kalebovi, brata Jerahmelovog: Meša,njegov prvorođeni, koji bi otac Zifu, i sinovi Mareša, oca Hebronova.",
			"43": "<sup>43</sup> Sinovi Hebronovi: Korah, Tapuah, Rekem i Šema.",
			"44": "<sup>44</sup> Šema porodi Rahama, oca Jorkeamova, Rezkem rodi Šamaja.",
			"45": "<sup>45</sup> Sinovi Šamajevi: Maon, a Maon bi ocem BetSuru.",
			"46": "<sup>46</sup> Eifa, suložnica Kalebova, porodi Harana, Mosa i Gazeza. Haran porodi Gazeza.",
			"47": "<sup>47</sup> Sinovi Jahdajevi: Regem, Jotam, Gešan, Pelet, Eifa i Šaaf.",
			"48": "<sup>48</sup> SWuložnica Kalebova, Maaka, porodi Šebera i Tirana.",
			"49": "<sup>49</sup> A ona rodi Šaafa, oca Madmanaova, i Šebaa, oca Makbenova i oca Giveavljeva. Kćer Kalebova bijaše Aksa.",
			"50": "<sup>50</sup> To biše sinovi Kalebovi. Sinovi Hura, prvorođenog od Efratae: Šobal, otac KirijatJearima:",
			"51": "<sup>51</sup> Salma, otac Betlehema; Haref, otac Bet-Gadera.",
			"52": "<sup>52</sup> Šobal, otac KirijatJearima, imade sinove: Haroea, polovicu Manahtita",
			"53": "<sup>53</sup> i obitelji iz KirijatJearima: Jitrite, Putite, Šumanite i Mišraite. Od njih su potekli Soreatiti i Eštauliti.",
			"54": "<sup>54</sup> Sinovi Salmaevi: Betlehem, Netofatiti, AtrotBetJoab, polovica Manahtita, Soreiti,",
			"55": "<sup>55</sup> i obitelji Sofrita, koji stanuju u Jaebesu, Tireatiti, Šimeatiti, Sukatiti. To su Keniti, koji potječu od Hamata, oca kuće Rekabljeve."
		},
		"3": {
			"1": "<sup>1</sup> Evo sinova Davidovih koji mu se rodiše u Hebronu: prvorođeni Amnon, od Ahinoam iz Izreela; drugi Daniel, od Abigaile iz Karmela;",
			"2": "<sup>2</sup> treći, Absalom sin od Maakae, kćeri Talmaja, kralja Gešura; četvrti Adonias, sin Haguite;",
			"3": "<sup>3</sup> peti, Šeftaja, od Abitale; šesti, Jitream od Eglae njegove žene.",
			"4": "<sup>4</sup> Svih šest rodi se njemu u Hebronu. On vladaše ondje sedam godina i šest mjeseci, i tijekom 33 godine, on vladaše u Jeruzalemu.",
			"5": "<sup>5</sup> Evo onih koji se njemu rodiše u Jeruzalemu: Šimea, Šobab, Natan i Solomon, sve četvorica od BatSuae, kćeri Amielove.",
			"6": "<sup>6</sup> Potom Jihbar, Elišua, Elifelet,",
			"7": "<sup>7</sup> Nogah, Nefeg, Jafija,",
			"8": "<sup>8</sup> Elišama, Eliada i Elifelet: devetorica sveukupno.",
			"9": "<sup>9</sup> To su svi sinovi Davidovi, osim sinova od suložnica . Tamar bijaše njegova sestra.",
			"10": "<sup>10</sup> Sinovi Solomonovi: Roboam, Abija njegov sin, Aza njegov sin, Jozafat njegov sin,",
			"11": "<sup>11</sup> Joram njegov sin, Akhazias njegov sin, Joas njegov sin,",
			"12": "<sup>12</sup> Amazias njegov sin, Azarias njegov sin, Jotam njegov sin,",
			"13": "<sup>13</sup> Akhaz njegov sin, Ezekias njegov sin, Manase njegov sin,",
			"14": "<sup>14</sup> Amon njegov sin, Jozias njegov sin: prvorođeni Johanan,",
			"15": "<sup>15</sup> drugi Jojakim, treći Sedecias, četvrti Šalum.",
			"16": "<sup>16</sup> Sin Jojakinov: Jekonija njegov sin, Sedecias njegov sin.",
			"17": "<sup>17</sup> Sin Jekonija zatvorenika: Šaltiel njegov sin,",
			"18": "<sup>18</sup> Malkiram, Pedaja, Šenasar, Jekamija, Hošama i Nedabija.",
			"19": "<sup>19</sup> Siniov Pedajavljevi: Zorobabel i Šimej; sinovi Zorobabelovi: Mešulam, Hananija i Šelomit njihova sestra.",
			"20": "<sup>20</sup> Sinovi Mešulamovi: Hašuba, Ohel, Berekia, Hasadija, JušavHezd: petorica ukupno.",
			"21": "<sup>21</sup> Sinovi Hananijini: Pelatija i Ješaja, sinovi Refajavljevi, sinovi Arnanovi, sinovi Obadijini, sinovi Šekanijavljevi.",
			"22": "<sup>22</sup> sin Šekanijavljev: Šemaja. Sinovi Šemajavljevi: Hatuš, Jiguel, Bariah, Nearija i Šafat: šestorica ukupno.",
			"23": "<sup>23</sup> Sinovi Nerajavljevi: Elioenaj, Hizkija i Azrikam: trojica ukupno.",
			"24": "<sup>24</sup> Sinovi Elioenajevi: Hadavjahu, Eliašib, Pelaja, Akub, Johanan, Delaja i Anani: sedmorica sveukupno."
		},
		"4": {
			"1": "<sup>1</sup> Sinovi Judini: Peres, Hesron, Karmi, Hur i Šoval.",
			"2": "<sup>2</sup> Reaja, sin Šivalov, rodi Jahata. Jahat porodi Ahumaja i Lahada: to su obitelji Soreatita.",
			"3": "<sup>3</sup> Evo sinova Hurovih: otac Etamov, Izreelov, Jišmajev i Jidbašev; njihova se sestra zvaše Haseleelponi.",
			"4": "<sup>4</sup> Potom Penuel otac Gedorov, i Ezer otac Hušavljev. To bijahu sinovi Hurovi, provorođenog u Efrataa, oca Betlehemova.",
			"5": "<sup>5</sup> Ašehur, otac Tekoavljev, imade dvije žene: Helau i Naarau.",
			"6": "<sup>6</sup> Naara mu rodi Ahuzama, Hefera, Temanite i Ahaštarite. Bijahu to sinovi Naaraovi.",
			"7": "<sup>7</sup> Sinovi Heleavljevi: Seret, Sohar i Etnan.",
			"8": "<sup>8</sup> Kos porodi Anuba i Hasobeba i obitelji Aharhel ove, sina Harumova.",
			"9": "<sup>9</sup> Jaebes bijaše ugledniji od svoje braće, a njegova ga majka bijaše zvala imenom Jaebes govoreći: ” Rodila sam u bolovima .“",
			"10": "<sup>10</sup> Jaebes zazva Boga Izraelovog govoreći: ” Ako me ti uistinu blagosiljaš, tad ćeš uvećati moju teritoriju, tvoja će ruka biti sa mnom i ti ćeš udaljiti nesreću da ja ne budem u bolovima.“ A Bog ispuni ono što je on tražio.",
			"11": "<sup>11</sup> Kelub, brat Šuhavljev, porodi Mehira koji bi otac Eštonov.",
			"12": "<sup>12</sup> Ešton porodi BetRafaa, Paseaha i Tehinnaa, oca Ir-Nahaša. To su ljudi iz Rekaa.",
			"13": "<sup>13</sup> Sinovi Kenazovi: Otniel i Seraja. Sin Otnielov: Hatat.",
			"14": "<sup>14</sup> Meonotaj porodi Joaba, oca Ge-Harašima, jer oni bijahu zanatlije .",
			"15": "<sup>15</sup> Sinovi Kalebovi, sina Jefuneova: Iru, Ela i Naam. Sin Elavljev: Kenaz.",
			"16": "<sup>16</sup> Sinovi Jehalelovi: Zif, Zifa, Tiria, i Azareel.",
			"17": "<sup>17</sup> Sinovi Ezravljevi: Jeter, Mered, Efer i Jalon. Ona zače  Mirijam, Šamaja i Jišbaha, oca Eštemoa.",
			"18": "<sup>18</sup> Njegova žena, Judejka, porodi Jereda, oca Gedora, Hebera oca Sokoova, i Jekutiela, oca Zanoahova; to su sinovi Bitije, kćeri *Faraonove, koju bijaše uzeo Mered.",
			"19": "<sup>19</sup> Sinovi žene Hodijavljeve, sestre Nahamove: oca Keilavljeva, Garmita, i Eštememoa Makatita.",
			"20": "<sup>20</sup> Sinovi Šimonovi: Amnon, Rina, BenHanan i Tilon. Sinovi Jišejevi: Zohet i sin Zohetov.",
			"21": "<sup>21</sup> Sin Šelavljevi, sinovi Jude: Er, otac Lekavljev, Laeda otac Marešavljev, obitelji kuće gdje se pravila morska svila, u BetAšbea,",
			"22": "<sup>22</sup> Jokim, ljudi Kozevavljevi, Joaš i Saraf, koji biše gospodarima Moaba  i vratiše u Betlehem to su stare stvari;",
			"23": "<sup>23</sup> to bijahu lončari i stanovnici nasada i zabrana . Oni stanovaše ondje, s kraljem, u službi njegovoj.",
			"24": "<sup>24</sup> Sinovi Simeonovi: Nemuel, Jamin, Jarib, Zerah, Šaul;",
			"25": "<sup>25</sup> Šalum njegov sin, Mivsam njegov sin.",
			"26": "<sup>26</sup> Sin Mišmajev: Hamuel njegov sin, Zakur njegov sin, Šimej njegov sin.",
			"27": "<sup>27</sup> Šimej imade šesnaest sinova i šest kćeri; ali njegova braća ne imadoše mnogo sinova, sve njihove obitelji ne biše tako brojne kao sinovi Judini.",
			"28": "<sup>28</sup> Oni nastaniše BerŠebu, Molad, Hasar-Šaual .",
			"29": "<sup>29</sup> Bilhu, Esem, Tolad,",
			"30": "<sup>30</sup> Betuel, Hormu, Siklag,",
			"31": "<sup>31</sup> BetMarkavot, Hasar-Suzim, BetBirej, Šaaraim. To biše njihovi gradovi sve do vladavine Davidove",
			"32": "<sup>32</sup> i njihova sela: Etam, Ain, Rimon, Token, Ašan: pet gradova,",
			"33": "<sup>33</sup> i sva njihova sela koja bijahu uokolo tih gradova sve do Baala. To biše njihova previvališta i njihove vlastite liste rodoslovne.",
			"34": "<sup>34</sup> Mešobab, Jamlek, Joša sin Amasijavljev,",
			"35": "<sup>35</sup> Joel, Jehu sin Jošibijavljev, sin Serajavljev, sin Azielov,",
			"36": "<sup>36</sup> Elioenaj, Jaakoba, Ješohaja, Azaja, Adiel, Jezimiel, Benaja,",
			"37": "<sup>37</sup> Ziza, sin Šifejev, sin Alonov, sin Jedajavljev, sin Šimrijev, sin Šemajavljev:",
			"38": "<sup>38</sup> Oni, koji su upravo imenovani, biše poglavarima u svojim obiteljima, a njihove se obitelji mnogo uvećaše.",
			"39": "<sup>39</sup> Oni odoše na ulaz Gedera sve do na istok doline, u potrazi za ispašom za svoja stada;",
			"40": "<sup>40</sup> oni nađoše tuste i dobre ispaše a zemlja bijaše prostrana, mirna i spokojna, jer oni koji ju nekada nastanjivaše potjecaše od Šama .",
			"41": "<sup>41</sup> Ti ljudi, koji biše upravo spomenuti, dođoše dakle u vrijeme Ezekiasa, kralja Jude, uništiše njihove šatore i zaklone koji se nalaziše ondje i posvetiše ih zabrani  sve do ovog dana. Oni stanovaše na njihovom mjestu, jer bijaše ondje ispaše za njihovu sitnu stoku.",
			"42": "<sup>42</sup> Nekji od Simeonovih sinova odoše u planinu Seir: 500 ljudi s, na čelu, Pelatijaom, Nearijaom, Refaijaom i Uzielom, sinovima Jišejevim.",
			"43": "<sup>43</sup> Oni potukoše ostatak preživjelih Amaleka  i stanovaše ondje sve do ovog dana."
		},
		"5": {
			"1": "<sup>1</sup> Sin Rubenov, prvorođenog Izraelovog on bijaše prvorođeni ali kad bi oskrnavio postelju oca  svog, njegovo pravo prvorođenstva bi dano sinovima Josipovim, sina Izaelova, i on bi smatran kao ona gubitnik prava prvorođenstva.",
			"2": "<sup>2</sup> Naime, Juda bi najveći među braćom svojom i, od njega, je potekao onaj koji postade princem, ali pravo prvorodstva bi Josipovo.",
			"3": "<sup>3</sup> Sinovi Rubenovi: prvorođenog Izraelovog: Hanok, Palu, Hesron i Karmi.",
			"4": "<sup>4</sup> Sinovi Joelovi: Šemaja njegov sin; Gog njegov sin, Šimej njegov sin,",
			"5": "<sup>5</sup> Mika njegov sin, Reaja njegov sin, Baal njegov sin,",
			"6": "<sup>6</sup> Beera njegov sin, kojeg izgna Tilgat-Pilnezer, kralj Asirije; on bijaše princem Rubenitima.",
			"7": "<sup>7</sup> Njegova braća, prema svojim obiteljima zabilježenim po svojim rodoslovima: na čelu Jeiel, Zekarijau,",
			"8": "<sup>8</sup> Bela sin Azazov, sin Šemajev, sin Joelov; oni stanovaše u Aroeru i iđaše sve do Neboa i BaalMeona.",
			"9": "<sup>9</sup> Na istoku, oni stanovaše sve do ulaza u pustinju, od rijeke Eufrat, jer njihova stada bijahu brojna u zemlji Galaad.",
			"10": "<sup>10</sup> U Saulovo vrijeme, oni biše u ratu s Hagritima koji padoše u njihove ruke; oni stanovaše u svojim šatorima na svoj površini istočno od Galaada.",
			"11": "<sup>11</sup> Sinoviu Gadovi, nasuprot njima, stanovaše u zemlji Bašan, sve do salka:",
			"12": "<sup>12</sup> na čelu Joel, Šafam drugi, Jaenaj i Šafat, u Bašanu.",
			"13": "<sup>13</sup> Njihova braća prema svojim obiteljima biše: Mikael, Mešulam, Šeba, Joraj, Jaekan, Zia i Eber: sedam.",
			"14": "<sup>14</sup> Evo sinova Abigailovih, sina Hurijeva, sina Jaroahova, sina Galaada, sina Mikaelova, sina Ješišajeva, sina Jahdoova, sina Buzova.",
			"15": "<sup>15</sup> Ahi, sin Abdielov, sin Guniev, bijaše poglavar njihovih ovitelji.",
			"16": "<sup>16</sup> Oni stanovaše u Galaadu, u Bašanu i u njegovim okružjima, u svim ispasištima Šarona, sve do njegovih krajina.",
			"17": "<sup>17</sup> Svi, oni biše popisani u vrijeme Jotama, kralja Jude i u vrijeme Jeroboama, kralja Izraela.",
			"18": "<sup>18</sup> Sinovi Rubenovi, Galaditi, i plupleme Manaseovo bijahu dio srčanih ljudi, koji nisiše štit i mač, strijeljaše iz luka i ratovaše 44.760 ljudi sposobnih za poći u vojni pohod.",
			"19": "<sup>19</sup> Oni zaratiše Hagritima, Jaturom, Nafišem i Nodabom.",
			"20": "<sup>20</sup> Oni primiše pomoć protiv njih; Hagriti biše izručeni u njihove ruke; kao i svi oni koji bijahu s njima, jer za vrijeme boja oni vapiše prema Bogu, koji ih usliša, jer imaše oni povjerenja u njega.",
			"21": "<sup>21</sup> Oni zarobiše njihova stada: 50.000 deva, 250.000 glava sitne stoke, 2.000 magaraca, kao i 100.000 osoba.",
			"22": "<sup>22</sup> Mnogi ljudi padoše, smrtno udareni, jer rat dođe od Boga. Oni stanovaše na njihovom mjestu sve do izlaska.",
			"23": "<sup>23</sup> Sinov poluplemena Manasejaeva stanovaše u zemlji od Bašana sve do BaalHermona. Bijahu oni brojni.",
			"24": "<sup>24</sup> Evo poglavara obitelji: Efer, Jiše, Eliel, Azriel, Jirmeja, Hodavija i Jahdiel. Bojahu to smioni ljudi, ljudi od ugleda, glave svojih obitelji.",
			"25": "<sup>25</sup> Oni biše nevjerni spram Boga svoji otaca i odaše se bludu s bogovima domaćih ljudi koje Bog bijaše uništavao pred njima.",
			"26": "<sup>26</sup> Tada Bog Izraelov pobudi duh Pulov, kralja Asirije, i duh Tigat-Pilnezera, kralja Asirije, koji ih izgna Rubenite, Gadite i polupleme Manaseovo i, odvede ih u Halah, Habor, Harau i k rijeci Gozan, sve do ovog dana.",
			"27": "<sup>27</sup> Sinovi Levijevi: Geršon, kehat i Merari.",
			"28": "<sup>28</sup> Sinovi Kehatovi: Amram, Jicehar, Hebron i Uziel.",
			"29": "<sup>29</sup> Sinovi Amranovi: Aaron, Mojsije i Mirijam. Sinovi Aaronovi: Nadav, Abihu, Eleazar i Itamar.",
			"30": "<sup>30</sup> Eleazar porodi Pinhasa; Pinhas porodi Abišua;",
			"31": "<sup>31</sup> Abišua porodi Bukija; Buki porodi Uzia;",
			"32": "<sup>32</sup> Uzi porodi Zerahijaa; Zerahija porodi Merajota;",
			"33": "<sup>33</sup> Merajo porodi Amarija; Amarija porodi Ahituba,",
			"34": "<sup>34</sup> Ahitub porodi Sadoka, Sadok porodi Ahimasa,",
			"35": "<sup>35</sup> Ahimas porodi Azarijaa, Azarija porodi Johanana;",
			"36": "<sup>36</sup> Johanan porodi Azarijaa. To je onaj koji bi velikim svećenikom u Kući koju izgradi Solomon u Jeruzalemu.",
			"37": "<sup>37</sup> Azarija porodi Amarijaa, Amarija porodi Ahituba;",
			"38": "<sup>38</sup> Ahitub porodi Sadoka; Sadok porodi Šaluma;",
			"39": "<sup>39</sup> Šalum porodi Hilkijaa; Hilkija porodi Azarijaa;",
			"40": "<sup>40</sup> Azarija porodi Serajaa; Seraja porodi Jehosadaka;",
			"41": "<sup>41</sup> Jehosadak pođe kad GOSPOD izgna Judu i Jeruzalem, rukom Nabukodonozorovom."
		},
		"6": {
			"1": "<sup>1</sup> Sinovi Levijevi : Geršom, ketat iMerari.",
			"2": "<sup>2</sup> Evo imena sinova Geršomovih: Livni i Šimej.",
			"3": "<sup>3</sup> Sinovi Ketatovi: Amram, Jicehar, Hebron i Uziel.",
			"4": "<sup>4</sup> Sinovi Merarijevi:Mahli i Muši. To su obitelji Levijeve, prema očevima njihovim.",
			"5": "<sup>5</sup> Geršomovi: Livni njegov sin, Jahat njegov sin, Zimma njegov sin,",
			"6": "<sup>6</sup> Joah njegov sin, Iddo njegov sin, Zerah njegov sin, Jetraj njegov sin.",
			"7": "<sup>7</sup> Sinovi Ketatovi: AmiNadav njegov sin, Kore njegov sin Asir njegov sin,",
			"8": "<sup>8</sup> Elkana njegov sin,Ebiasaf njegov sin, Asir njegov sin,",
			"9": "<sup>9</sup> Tahat njegov sin, Uriel njegov sin, Uzija njegov sin, Šaul njegov sin.",
			"10": "<sup>10</sup> Sinovi Elkanaovi: Amasaj i Ahimot,",
			"11": "<sup>11</sup> Elkana njegov sin, Sofaj njegov sin, Nahat njegov sin,",
			"12": "<sup>12</sup> Eliab njegov sin, Samuel njegov sin.",
			"13": "<sup>13</sup> Sinovi Samuelovi: prvorođeni Joel, i drugi Abija.",
			"14": "<sup>14</sup> Sinovi Merarijevi: Mahli, Livni i njegov sin, Šimej njegov sin, Uza njegov sin,",
			"15": "<sup>15</sup> Šimea njegov sin, Hagija njegov sin, Azajanjegov sin.",
			"16": "<sup>16</sup> Evo onih Evo onih kojima David povjeri zadaću pjevanja u Kući GOSPODOVOJ, čim *kovčeg imade mjesto za počinak.",
			"17": "<sup>17</sup> Oni biše sluge za pjevanje, pred *boravištem šatrom susretanja sve dok, Solomon ne bi sagradio Kuću GOSPODOVU u Jeruzalemu; oni ispunjavaše službu svoju prema pravilu svome.",
			"18": "<sup>18</sup> Evo onih koji izvršavaše tu službu, kao i njihovih sinova. Između sinova Ketatita: Heman, pjevač, sin Joelov, sin Samuelov,",
			"19": "<sup>19</sup> sin Elkanov, sin sin Jerohamov, sin sin Elielov, sin Toahov,",
			"20": "<sup>20</sup> sin Sufov, sin Elkanov, sin Mahatov, sin Amazajev,",
			"21": "<sup>21</sup> sin Elkanov, sin Joelov, sin Azarijavljev, sin Sefanijavljev,",
			"22": "<sup>22</sup> sin Tahatov, sin Asirov, sin Evaziasafov, sin Korejev,",
			"23": "<sup>23</sup> sin Jiseharov, sin Kehatov, sinovi Levijevi, sin Izraelov.",
			"24": "<sup>24</sup> Potom, njegov brat Asaf koji stajaše njemu s desna: Asaf, sin Berekijahuov, sin Šimejavljev,",
			"25": "<sup>25</sup> sin Mikaelov, sin Bazejavljev, sin Malkijavljev,",
			"26": "<sup>26</sup> sin Etnijev, sin Zerahov, sin Adajavljev,",
			"27": "<sup>27</sup> sin Etanov, sin Zimmaov, sin Šimejev,",
			"28": "<sup>28</sup> sin Jahatov, sin Geršomov, sinovi Levijevi.",
			"29": "<sup>29</sup> Sinovi Merarijevi, njihova braća, s lijeva: Etan, sin Kišijev, sin Abdijev, sin Malukov,",
			"30": "<sup>30</sup> sin Hašabijavljev, sin Amasajavljev, sin Hilkijavljev,",
			"31": "<sup>31</sup> sin Amsijev, sin Banijev, sin Šemerov,",
			"32": "<sup>32</sup> sin Mahlijev, sin Mušijev, sin Merarijev, sin Levijev.",
			"33": "<sup>33</sup> Njihova braća, *leviti, bijahu raspoređeni na svaku službu u Kući Božjoj.",
			"34": "<sup>34</sup> Aaron i njegovi sinovi obavljahu dimljenje *žrtava na *oltaru holokausta i na oltaru mirisa, baviše se svime što se ticalo *presvetog mjesta, i obavljali obred odrješenja u korist Izraela, prema svemu što o tom bijaše zapovjedio Mojsije, sluga Božji.",
			"35": "<sup>35</sup> Evo sinova Aaronovih: Eleazar njegov sin, Pinhas njegov sin, Abišua njegov sin,",
			"36": "<sup>36</sup> Buki njegov sin, Uzi njegov sin, Zeraja njegov sin,",
			"37": "<sup>37</sup> Merajot njegov sin, Amarija njegov sin, Ahitub njegov sin,",
			"38": "<sup>38</sup> Sadok njegov sin, Ahimas njegov sin.",
			"39": "<sup>39</sup> A evo njihovih obitavališta, prema njihovim taborištima u njihovim područjima: sinovima Aaronovim iz obitelji Kehatita jer im pripade ždrijebom na prvom mjestu",
			"40": "<sup>40</sup> dade se Hebron u Zemlji Judiu s općinskim posjedima koji ih okružuju.",
			"41": "<sup>41</sup> Ali gradska polja i njihova sela, on se dadoše Kalebu, sinu Jefuneovu.",
			"42": "<sup>42</sup> Dade se sinovima Aaronovim kao gradove utičišta: Hebron, Livna i njihovi posjedi, Jatir, Eštoma i njihovi posjedi,",
			"43": "<sup>43</sup> Hilez i njegovi posjedi, Devir i njegovi posjedi,",
			"44": "<sup>44</sup> Ašan njegovi posjedi, BetŠemeš i njegovi posjedi,",
			"45": "<sup>45</sup> i, na pleme Benjaminovo: Geva i njegovi posjedi, Alemet i njegovi posjedi, Anatot i njegovi posjedi. Ukupno njihovih gradova: Trinaest gradova  za njihove obitelji.",
			"46": "<sup>46</sup> Drugi sinovi Kehatovi, prema njihovim obiteljima, primiše ždrijebom deset gradova plemena Efraim, i plemena Dan i poluplemena Manase;",
			"47": "<sup>47</sup> sinovi Geršomovi, prema njihovim obiteljima, primiše trinaest gradova plemena Isakar, plemena Ašer, plemna Neftali i plemena Manase u Bašanu;",
			"48": "<sup>48</sup> sinovi Merarijevi, prema njihovim obiteljima, primiše ždrijebom dvanaest gradova plemena Ruben, plemena Gad i plemena Zabulon.",
			"49": "<sup>49</sup> Sinovi Izraelovi dadoše *levitima te gradove i njihove općinske posjede.",
			"50": "<sup>50</sup> Od plemena sinova Judinih, plemena sinova Simeonovih i plemena sinova Benjaminovih, oni dadoše te gradove ždrijebom označene njihovim imenima.",
			"51": "<sup>51</sup> Druge obitelji sinova Kehatovih imaše područja svojih gradova u plemenu Efraim.",
			"52": "<sup>52</sup> Dade im se kao gradove utočišta: Sišem i njegove posjede u planini Efraim, Gezer i njihove posjede,",
			"53": "<sup>53</sup> Jokmeam i njegove općinske posjede, BetHoron i njegove općinske posjede,",
			"54": "<sup>54</sup> Ajalon i njegove općinske posjede, GatRimon i njegove općinske posjede;",
			"55": "<sup>55</sup> i u poluplemenu Manase: Aner i njegove općinske posjede, Bileam i njegove općinske posjede. Sve to bijaše za obitelji drugih sinova Kehatovih.",
			"56": "<sup>56</sup> Sinovima Geršomovim, prema njihovim posjedima, dade se u poluplemenu Manaseovom: Golan u Bašanu s njegovim općinskim posjedima, Aštarot i njegove općinske posjede;",
			"57": "<sup>57</sup> u plemenu Isakar: Kedeš i njegove općinske posjede, Daverot i njegove općinske posjede,",
			"58": "<sup>58</sup> Ramot i njegove općinske posjede; Anem i njegove općinske posjede;",
			"59": "<sup>59</sup> u plemenu Ašerovom: Mašal i njegove općinske posjede, Abdon i njegove općinske posjede,",
			"60": "<sup>60</sup> Hukok i njegove općinske posjede, Rehob i njegove općinske posjede;",
			"61": "<sup>61</sup> i u plemenu Neftali; Kedeš u Galileji i njegove općinske posjede, Hammon i njegove općinske posjede, Kirijataim i njegove općinske posjede.",
			"62": "<sup>62</sup> Drugima, sinovima Merarijevim, dade se u plemenu Zabulon: Rimono i njegove općinske posjede, Tabor i njegove općinske posjede.",
			"63": "<sup>63</sup> S one strane Jordana, pokraj Jerihona, na istoku Jordana, u plemenu Rubenovom: Beser, u pustinji, i njegove općinske posjede, Jahas i njegove općinske posjede,",
			"64": "<sup>64</sup> Kedemot i njegove općinske posjede, Mefat i njegove općinske posjede;",
			"65": "<sup>65</sup> i u plemenu Gadovom: Ramot Galadski i njegove općinske posjede, Mahanaim i njegove općinske posjede,",
			"66": "<sup>66</sup> Hešbon i njegove općinske posjede, Jazer i njegove općinske posjede."
		},
		"7": {
			"1": "<sup>1</sup> Za sinove Isakarove: Tola, Pua, Jašuba i Šimrona: svega četiri.",
			"2": "<sup>2</sup> Sinovi Tolavljevi: Uzi, Refaja, Jeriel, Jahmaj, Jibsam i Šemuel koji bijahu poglavari obitelji za Tola smioni ljudi čije potomstvo bijaše, u vrijeme Davidovo, na broju od 22.600.",
			"3": "<sup>3</sup> Sinovi Uzijevi: Jizrahja; sinovi Jizrahjavljevi: Mikael, Obadija, Joel, Jišija: svega petorica, svi poglavari.",
			"4": "<sup>4</sup> Prema njihovom potomstvu po obiteljima, oni imaše na svom teretu postrojbe vojske za rat na broju od 36.000 ljudi, i imaše mnogo žena i djece.",
			"5": "<sup>5</sup> Njihova braća, za sve ob itelji Isakarove, bijahu 87.000 smionih ljudi, prema ukupnom popisu.",
			"6": "<sup>6</sup> Benjamin: Bela, Beker, Jediael: 3.",
			"7": "<sup>7</sup> Sinovi Belavljevi: Esbon, Uzi, Uziel, Jerimot i Iri, pet poglavara obitelji, smioni ljudi, kojima popis primisivaše 22.034 potomka.",
			"8": "<sup>8</sup> Sinovi Bekerovi: Zemira, Joaš, Eliezer, Elioenaj, Omri, Jeremot, Abija, Anatot i Alemet. To su sinovi Bekerovi.",
			"9": "<sup>9</sup> Popis potomstva tih poglavara obitelji davaše 20.200 smionih ljudi.",
			"10": "<sup>10</sup> Sin Jediaelov: Bilhan; sinovi Bilhanovi: Jeuš, Benjamin, Ehud, Kenana, Zetan, Tarsis i Ahišasar.",
			"11": "<sup>11</sup> To su svi sinovi Jediaelovi, poglavari obitelji: 17.200 smionih ljudi, u vojsci, spremnih za boj.",
			"12": "<sup>12</sup> Šupim i Hupim bijahu sinovi Irovi; Hušim, sin Aherov.",
			"13": "<sup>13</sup> Sinovi Neftalijevi: Jahasiel, Guni, Jeser, i Šalum. Oni bijahu sinovi Bilhaevi.",
			"14": "<sup>14</sup> Sinovi Manaseovi: Azriel kojeg bijaše rodila njegova suložnica  Aramejka; ona porodi Makira, oca Galaada.",
			"15": "<sup>15</sup> Makir uze jednu ženu za Hupim i Šupim. Ime njegove sestre bijaše Maaka. Imed drugom bijaše Selofehad, a Gelofehad imade samo kćeri.",
			"16": "<sup>16</sup> Maaka, žena Makirova, porodi jednog sina kojeg nazva imenom Pereš; ime njegovom bratu bijaše Šereš, a njegovi sinovi biše Ulam i Rekem.",
			"17": "<sup>17</sup> Sin Ulamov: Bedan To su sinovi Galaadovi, sina Makirova, sina Manaseova.",
			"18": "<sup>18</sup> Njegova sestra Moleket porodi Išehoda, Abiezera i Mahla.",
			"19": "<sup>19</sup> Sinovi Šemidovi biše Ahijan, Šekem, Likhi i Aniam.",
			"20": "<sup>20</sup> Sinovi Efraimovi: Šutelah, Bered njegov sin, Tahat njegov sin, Eleada njegov sin, Tahat njegov sin,",
			"21": "<sup>21</sup> Zavad njegov sin, Šutelah njegov sin, Ezer i Elead. Ljudi iz Gata, rođeni u zavičaju, njih ubiše, jer bijahu sišli uzeti stada njihova.",
			"22": "<sup>22</sup> Efraim, njihov otac, bi u žalosti tijekom brojnih dana, a njegova braća  dođoše ga tješiti.",
			"23": "<sup>23</sup> On ode k svojoj ženi; ona zače i porodi jednog sina kojeg on nazva imenom Beria, jer ona bijaše ostala kod njega u nesreći njegovoj .",
			"24": "<sup>24</sup> Njegova kćer bi Šeera, koja sagradi BetHoron, donji grad i gornji grad, i UzenŠeera.",
			"25": "<sup>25</sup> Potom, Refat njegov sin, Rešef, Telah njegov sin, Tahan njegov sin,",
			"26": "<sup>26</sup> Laedan njegov sin, Amihud njegov sin, Elišama njegov sin,",
			"27": "<sup>27</sup> Nun njegov sin i Jozue njegov sin.",
			"28": "<sup>28</sup> Njihov posjed i njihovo boravište bijahu Betel i njegovo okružje, Sišem i njegovo okružje, na istoku Naran, na zapadu Gezer i njegovo okružje, Sišem i njegovo okružje sva do Ajaa i njegovog okružja.",
			"29": "<sup>29</sup> U rukama sinova Manaseovih bijahu Bet-Šean i njegovo okružje, Tanak i njegovo okružje, Megido i njegovo okružje, Dor i njegovo okružje. U tim gradovima stanovahu sinovi Josipovi, sinovi Izraelovi.",
			"30": "<sup>30</sup> Sinovi Ašerovi : Jima, Jišva, Jišvi, Beria i Serah njihova sestra.",
			"31": "<sup>31</sup> Sinovi Berijavljevi: Heber i Mikael koji bi ocem Birzaitu.",
			"32": "<sup>32</sup> Heber porodi Jafleta, Šomera, Hotama i Šuau njihovu sestru.",
			"33": "<sup>33</sup> Sinovi Jafletovi; Pazak, Bimhal i Ašvat. To su sinovi Jafletovi.",
			"34": "<sup>34</sup> Sinovi njegovog brata Šemera: Roga, Jehuba i Aram.",
			"35": "<sup>35</sup> Sinovi njegovog brata Helema: Sofah, Jimna, Šeleš i Amal.",
			"36": "<sup>36</sup> Sinovi Sofahovi: Šuah, Harnefer, Šual, Beri, Jimra,",
			"37": "<sup>37</sup> Beser, Hod, Šama, Šilša, Jitran i Beera.",
			"38": "<sup>38</sup> Sinovi Jeterovi: Jefune, Pispa i Ara.",
			"39": "<sup>39</sup> Sin Ulajev: Arah, Haniel i Risija.",
			"40": "<sup>40</sup> Svi oni bijahu sinovi Ašerovi, poglavari obitelji, ljudi ugledni, smioni, poglavari prinčevima, a popis njihovih potomaka u vojsku, za rat, dosezao je broj od 26.000 ljudi."
		},
		"8": {
			"1": "<sup>1</sup> Benjamin porodi Belaa, svog prvorođenog, Ašbela drugog, Ahraa trećeg,",
			"2": "<sup>2</sup> Noha četvrtog i Rafa petog.",
			"3": "<sup>3</sup> Bela imade sinove: Adara, Gera, oca Ehudova,",
			"4": "<sup>4</sup> Abišua, Namana, Ahoaha,",
			"5": "<sup>5</sup> Gera, Šefutana i Hurama.",
			"6": "<sup>6</sup> Evo sinova Ehudovih to su oni koji biše poglavari obitelji stanovnika Gevaa i iseliše u Manahat:",
			"7": "<sup>7</sup> Naman, Ahija i Gera on je taj koji ih iseli i koji porodi Uza i Ahihuda.",
			"8": "<sup>8</sup> Šaharaim imade sinove, u polju Moab, nakon što je otpustio svoje žene Hušimu i Baarau.",
			"9": "<sup>9</sup> On porodi od Hodeše svoje žene: Jobaba, Sivia, Mešaa, Malkama,",
			"10": "<sup>10</sup> Jeusa, Sakija i Mirma.To su njegovi sinovi, poglavari obitelji.",
			"11": "<sup>11</sup> Od Hušime, on bijaše porodio Abituba i Elpala.",
			"12": "<sup>12</sup> Sinovi Elpalovi: Eber, Mišeam i Šemed. On je taj koji izgradi Ono i Lod s njihovim okružjem.",
			"13": "<sup>13</sup> Beria i Šema bijahu poglavari obitelji stanovnika Ajalona. Oni su ti kojinagnaše u biujeg stanovnike Gata.",
			"14": "<sup>14</sup> Njihova braća bijahu Šašak i Jeremot.",
			"15": "<sup>15</sup> Zebadija, Arad, Eder, 16 Mikael, Jišpa, i Joha: sinovi Beriavljevi.",
			"17": "<sup>17</sup> Zebadija, Mešulam, Hizki, Heber,",
			"18": "<sup>18</sup> Jišmeraj, Jizlija i Jobab; sinovi Elpalovi.",
			"19": "<sup>19</sup> Jakim, Zikriu, Zabdi,",
			"20": "<sup>20</sup> Elienaj, Siletaj, Eliel,",
			"21": "<sup>21</sup> Adaja, Beraja i Šimrat: sinovi Šimejevi.",
			"22": "<sup>22</sup> Jišpan, Eber-Eliel,",
			"23": "<sup>23</sup> Abdon, Zikri, Hanan,",
			"24": "<sup>24</sup> Hananija, Elam, Antotija,",
			"25": "<sup>25</sup> Fifdeja, i Penuel: sinovi Šašakovi.",
			"26": "<sup>26</sup> Šamšeraj, Šeharija, Atalija,",
			"27": "<sup>27</sup> Jarešija, Elija, i Zikri: sinovi Jerohamovi.",
			"28": "<sup>28</sup> To bijahu poglavari obitelji, poglavari po njihovim rodoslovljima. oni stanovahu u Jeruzalemu.",
			"29": "<sup>29</sup> U Gabaonu, stanovaše otac Gabaona, čija žena se zvaše Maaka,",
			"30": "<sup>30</sup> njegov sin prvorođeni Abdon i Sur, Kiš, Baal, Nadav,",
			"31": "<sup>31</sup> Gedor, Ahio, i Zeker.",
			"32": "<sup>32</sup> Miklot porodi Šimea. Oni također, po primjeru svoje braće, stanovahu u Jeruzalemu s njima.",
			"33": "<sup>33</sup> Ner porodi Kiša; Kiš porodi Saula; Saul porodi Jonatana, MalkiŠua, AbiNadava i Ešbala .",
			"34": "<sup>34</sup> Sin Jonatanov bi Meribal . Meribal porodi Mikaa.",
			"35": "<sup>35</sup> Sinovi Mikavljevi biše Piton, Melek, Tarea i Ahaz.",
			"36": "<sup>36</sup> Ahaz porodi Jehoadaa; Jehoadaa porodi Alemeta, Azmaveta i Zimrija; Zimri porodi Mosa.",
			"37": "<sup>37</sup> Mosa porodi Bineaa, Rafaa njegovog sina, Eleasa njegovog sina. Asela njegovog sina.",
			"38": "<sup>38</sup> Asel imade šest sinova kojim evo imena: Azrikam, Bokru, Jišmael, Šeraja, Obadija i Hanan. To su sve sinovi Aselovi.",
			"39": "<sup>39</sup> Sinovi Ešekovi, njegovog brata: Ulam njegov provorođeni, Jeuš drugi, Elifelet treći.",
			"40": "<sup>40</sup> Sinovi Ulamovi biše smioni ratnici vješti ciljanju lukom. Oni imaše mnogo sinova i unuka: 150. Ti svi tamo činiše dio sinova Benjaminovih."
		},
		"9": {
			"1": "<sup>1</sup> Svi Izraeliti su bili popisani i upisani u knjigu kraljeva Izraelovih. Oni iz Jude su bili izgnani u Babiloniju zbog svoje nevjernosti.",
			"2": "<sup>2</sup> Prvi stanovnici koji imaše svoje posjede i svoje gradove su Izraeliti, svećenici,*leviti i posluživači :.",
			"3": "<sup>3</sup> U Jeruzalemu, stanovaše nekoliki od Judinih sinova, sinova Benjaminovih, sinova Efraimovih i Manaseovih.",
			"4": "<sup>4</sup> Utaj sin Amihudov, sin Omrijev, sin Imrijev, sin Banijev između sinova Peresovih, sinovi Jude.",
			"5": "<sup>5</sup> Među Silonitima: Azaja prvorođeni, i njegovi sinovi;",
			"6": "<sup>6</sup> između sinova Zerahovih: Jeuel. S njegovom braćom: 690.",
			"7": "<sup>7</sup> Između sinova Benjaminovih: Salu, sin Mešulamov, sin Hodavijavljev, sin HaSenuavljev;",
			"8": "<sup>8</sup> Jibneja, sin Jerohamov; Ela, sin Uzijev, sin Mikrijev, i Mešulam, sin Šefatijavljev, sin Reuelov, sin Jibnijavljev.",
			"9": "<sup>9</sup> S njihovom braćom, prema njihovim rodoslovljima: 956. Svi ti ljudi bijahu poglavari obitelji u svojim obiteljima.",
			"10": "<sup>10</sup> Između svećenika: Jedaja, Jehojarib, Jakin,",
			"11": "<sup>11</sup> Azarija, sin Hilkijavljev, sin Mešulamov, sin Sadokov, sin Merajotov, sin Ahituvljev, poglavar Kuće Božje;",
			"12": "<sup>12</sup> Adaja sin Jerohamov, sin Pašehurov, sin Malkijavljev; i Masaj sin Adielov, sin Jahzeravljev, sin Mešulamov, sin Mešilemitov, sin Imerov.",
			"13": "<sup>13</sup> Sa svojom braćom, poglavarima svojih obitelji 1.760, ljudi srčanih za obavljati službu u Kući Božjoj.",
			"14": "<sup>14</sup> Među levitima: Šemaja sin Hašubovljev, sin Azsrikamov, sin Hašabijavljev, između sinova Merarijevih;",
			"15": "<sup>15</sup> Bakbakar, Hereš, Galal, i Matanija sin Mikavljev, sin Zikrijev, sin Asafov;",
			"16": "<sup>16</sup> Obadija sin Šemajavljev, sin Galalov, sin Jedutunov i Berekija sin Azavljev, sin Elkanavljev koji stanovahu u selima Netofatita.",
			"17": "<sup>17</sup> Vratari: Šalum, Akub, Talmon, Ahiman, i njihova braća. Šalum bijaše njima poglavarem.",
			"18": "<sup>18</sup> Sve do ovog dana, stojeći na kraljevim vratima, na istoku, to su oni koji su vratari za tabore sinova Levijevih.",
			"19": "<sup>19</sup> Šalum, sin Korea, sin Ebiazafa, sin Korea, i njegova braća iz iste obitelji, Koreiti, bijahu zaduženi službom, kao čuvari praga *Šatora; njihovi očevi bijahu bili zaduženi čuvati ulaz u tabor GOSPODOV.",
			"20": "<sup>20</sup> Pinhas, sin Eleazarov, bijaše nekada njihovim poglavarem; gospod bijaše s njim.",
			"21": "<sup>21</sup> Zekarija sin Mešelemijavljev bijaše vratar na ulazu u šator susretanja.",
			"22": "<sup>22</sup> Svi ovi koji bijahu bili izabrani kao vratari pragova bijahu brojem 212. Oni bijahu bili popisani u svojim selima. Dadid i Samuel vidovnjak  su ti koji ih bijahu postavili u njihovu stalnu službu.",
			"23": "<sup>23</sup> Oni i njihovi sinovi bijahu raspoređeni na čuvanje vrata Kuće GOSPODOVE, tj. Kuće šatorske.",
			"24": "<sup>24</sup> Vratari bijahu na četiri glavne strane svijeta: na istoku, na zapadu, na sjeveru i na jugu.",
			"25": "<sup>25</sup> Njihova braća, koja bijahu u svojim selima, moraše dolaziri s vremena na vrijeme s njima na sedam dana;",
			"26": "<sup>26</sup> ali četiri glavna vratara, oni, ostajahu tu stalno. To bijahu leviti koji bijahu zaduženi za odaje i riznice Kuće Božje.",
			"27": "<sup>27</sup> Oni provodiše noć okolo Kuće Božje, jer bijahu zaduženi za njeno čuvanje i otvaranje svakog jutra.",
			"28": "<sup>28</sup> Neki između njih bijahu zaduženi za obredne predmete koje prebrajahu svaki put kad ulaziše ili izlaziše.",
			"29": "<sup>29</sup> Neki između njih bijahu nadglednici za vaze, naročito za svete vaze, najbolje brašno, vino, ulje, *tamjan i mirise.",
			"30": "<sup>30</sup> Ali, sinovi svećenika bijahu ti koji su pripremali mješavine za mirise.",
			"31": "<sup>31</sup> Matitija, između levita, onaj koji bijaše prvorođeni Šaluma Koreita, bijaše zadužen za stalno spremanje pečenih pogača.",
			"32": "<sup>32</sup> Među sinovima Kehatita, neki od njihove braće bijahu zaduženi za spravljanje kruha ponude  za svaki *šabat.",
			"33": "<sup>33</sup> Pjevači, poglavari levitskih obitelji, bijahu smješteni u odaje i oslobođeni svaje službe, jer, danju i noću, oni bijahu raspoređeni na svoju službu.",
			"34": "<sup>34</sup> To bijahu poglavari obitelji za levite, poglavari prema svojim rodosolovljima. Oni stanovahu u Jeruzalemu.",
			"35": "<sup>35</sup> U Gabaonu, stanovaše otac Gabaona, Jeiel, čija žena imaše za ime Maaka,",
			"36": "<sup>36</sup> njegov sin prvorođeni Abdon, i Sur, Kiš, Baal, Ner, Nadav,",
			"37": "<sup>37</sup> Gedor, Ahio, Zekarija i Miklot. 38 Miklot porodi Šimeama. Oni također, po primjeru svoje braće, stanovahu u Jeruzalemu s njima.",
			"39": "<sup>39</sup> Ner porodi Kiša; Kiš porodi Saula; Saul porodi Jonatana, MalkiŠuaa, AbiNadava i Ešbala.",
			"40": "<sup>40</sup> Sin Jonatanov bi Meribal. Meribal porodi Mikaa.",
			"41": "<sup>41</sup> Sinovi Mikavljevi biše Piton, Melek, Tahrea.",
			"42": "<sup>42</sup> Ahaz porodi Jaeraa; Jaera porodi Alemeta, Azmaveta i Zimrija; Zimri porodi Mosaa;",
			"43": "<sup>43</sup> Mosa porodi Beineaa, Refaja njegovog sina, Asela njegovog sina.",
			"44": "<sup>44</sup> Asel imade šest sinova kojima evo imena: Azrikam, Bokru, Jišmael, Šeraja, Obadija i Hanan. To su sinovi Aselovi."
		},
		"10": {
			"1": "<sup>1</sup> Filistince zavojevaše protiv Izraela; ljudi Izraelovi pobjegoše pred Filistincima i popadaše, smrtno udareni na planini Gilboa .",
			"2": "<sup>2</sup> Filistinci pritijsniše Saula i njegove sinove. Filistinci oboriše Jonatana, AbiNadava i Malki-Šuaa, sinove Saulove.",
			"3": "<sup>3</sup> Teret boja se prenese na Saula; strijelci ga otkriše i on zadrhta pri pogledu na strijelce.",
			"4": "<sup>4</sup> Saul reče svojem štitonoši: ” Izvuci svoj mač i probodi me da ne bi neobrezani došli poigravati se sa mnom“; ali njegov štitonoša odbi, jer se veoma bojao. Tad Saul uze mač i baci se na njega.",
			"5": "<sup>5</sup> Videći da Saul bijaše mrtva, njegiov se štitonoša također baci na mač, i umrije.",
			"6": "<sup>6</sup> Saul umrije, kao i tgrojica njegovih sinova; sva kuća njegova  umrije u isto vrijeme.",
			"7": "<sup>7</sup> Videći poraz Izraelov i smrt Saulovu i njegovih sinova, Izraeliti napustiše svoja sela i pobjezgoše. Filistinci dođoše i nastaniše se tu.",
			"8": "<sup>8</sup> Sutradan, Filistinci dođoše pljačkati svoje žrtve. Oni nađoše Saula i njegove sinove ležeći na brdu Gilboa.",
			"9": "<sup>9</sup> Oni ih opljačkaše, odnesoše njihove glave i njihovo oružje i pustiše kolati novost po zemlji Filistinaca, oglašavajući ju svojim idolima i puku.",
			"10": "<sup>10</sup> Oni staviše njegovo oružje u kuću svojeg boga, i prikovaše njegovu lubanju na kuću Dagonovu :.",
			"11": "<sup>11</sup> Svi ovi od Javeša Galaadskog  doznaše sve ovo što Filistinci bijahu uradili Saulu.",
			"12": "<sup>12</sup> Svi srčani ratnici digoše se, uzeše tijelo Saulovo i tijela njegovih sinova, i odnesoše ih u Javeš; potom oni *postiše sedam dana.",
			"13": "<sup>13</sup> Saul umrije zbog nevjernosti koju bijaše počinio prema GOSPODU jer ne bijaše pazio riječ GOSPODOVU, i također jer bijaše ispitivao duh jednog mrtvaca : da bi ga savjetovao,",
			"14": "<sup>14</sup> umjesto da pita za savjet GOSPODA. Tako umrije on i prepusti kraljevstvo Davidu, sinu Jeseovu."
		},
		"11": {
			"1": "<sup>1</sup> Sav Izrael se sakupi kod Davida u Hebronu, govoreći: ” Evo mi smo tvoja kost i tvoje meso .",
			"2": "<sup>2</sup> Ima dugo vremena već, čak kad još Saul bijaše kraljem, ti bijaše taj koji izvodiše i vraćaše Izrael. A GOSPOD tvoj Bog ti je rekao: Ti si taj koji će napasati Izrael moj narod i ti si taj koji će biti glava nad Izraelom  mojim narodom.“",
			"3": "<sup>3</sup> Sve *starješine Izraelove dođoše pronaći kralja u Hebronu, i David zaključi u njihovu korist jedan savez u Hebronu, pred GOSPODOM. Oni *pomazaše Davida kao kralja nad Izraelom, prema riječu GOSPODOVOJ prenesenoj po Samuelu.",
			"4": "<sup>4</sup> A David, kao i sav Izrael, ode u Jeruzalem tj. Jebuz gdje bijahu Jebuziti  koji nastanjivaše zemlju.",
			"5": "<sup>5</sup> Stanovnici Jebuza rekoše Davidu: ” Ti nećeš ući ovdje.“ Ali David se domože tvrđave *Sion: to je Grad Davidov.",
			"6": "<sup>6</sup> On bijaše naime rekao: ” Prvi koji potuče Jebuzite postat će poglavar i princ“: Joab sin Serujavljev uspe se prvi i postade poglavar.",
			"7": "<sup>7</sup> David se smjesti u tvrđavi; zato se ona naziva Grad Davidov.",
			"8": "<sup>8</sup> Potom on izgradi grad svud uokolo, počev od Miloa : sve do okružja, a Joab obnovi ostatak grada.",
			"9": "<sup>9</sup> David postajaše sve više i više velik a GOSPOD, svemogući, bijaše s njim.",
			"10": "<sup>10</sup> Evo poglavara ratnika Davidovih koji ga odrješito podupiraše tijekom sve njegove vladavine, kao i sav Izrael, da bi vladao preema riječi GOSPODOVOJ nad Izraelom.",
			"11": "<sup>11</sup> Evo liste ratnika Davidovih: Jašobeam, sin Hakmonijev, poglavar Trojice . On je taj koji vitlaše svojim kopljem na 300 ljudi odjednom: oni biše pobijeni.",
			"12": "<sup>12</sup> A poslije njega, Eleazar, sin Dodoa Ahohita, koji bijaše među Trojicom ratnika.",
			"13": "<sup>13</sup> On je taj koji bijaše s Davidom u Pas-Damimu, kad Filistinci se tu sakupiše za boj; bijaše ondje jedno polje pokriveno ječmom, a narod bježaše pred Filistincima;",
			"14": "<sup>14</sup> oni se postaviše u sredinu polja, oslobodiše ga i potukoše Filistince; GOSPOD ostvari jednu veliku pobjedu.",
			"15": "<sup>15</sup> Trojica od Tridesetorice poglavara siđoše niz stijenu k Davidu, u pećinu Adulamovu, dok tabor Filistinaca bijaše postavljen u dolini Refaita .",
			"16": "<sup>16</sup> David bijaše tada u svom skloništu, a jedna filistinska straža nalazila se tada u Bethléemu.",
			"17": "<sup>17</sup> David izrazi ovu želju: ” Tko mi će dati piti vode iz žusterne koja je na vratima Bethléema?“",
			"18": "<sup>18</sup> Trojica upadoše u tabor Filistinaca, natočiše vode u žusterni pokraj vrata Bethléema, odnesoše ju i ponudiše Davidu. Ali David ju nije htio piti već načini libaciju  GOSPODU.",
			"19": "<sup>19</sup> On reče: ”Nek me moj Bog kazni ako ja to učinim! Hoću li ja piti *krv tih ljudi u opasnosti po njihov život? Jer po cijenu života svog oni su ju išli donijeti!“ I on odbi popiti ju. Eto što učiniše Trojica ratnika.",
			"20": "<sup>20</sup> Abišaj, brat Joabov, bijaše zapovjednikom Tridesetorice. On je taj što vitlaše kopljem na 300 ljudi koji biše probodeni, ali on si ne načini *ime među Trojicom .",
			"21": "<sup>21</sup> On bi dvostruko počasđ ćen, više nego Tridesetorica, i on postade njihovim zapovjednikom, ali ne dostiže Trojicu.",
			"22": "<sup>22</sup> Benaja, sin Jehodajavljev, sin jednog istaknutog čovjeka, po brojnim podvizima, podrijetlom iz Kavsela. On bijaše onaj koji ubi dva junaka  iz Moaba; on je onaj koji dođe ubiti lava u žusterni za vrijeme snijega;",
			"23": "<sup>23</sup> on je također onaj koji ubi jednog Egipćanina, čovjeka rasta od pet lakata; Egipćanin imaše u ruci jedno koplje kao tkalačka greda. On siđe k njemu, naoružan jednim štapom, istrgnu koplje iz ruke Egipćaninu i ubi ga njegovim vlastitim kopljem.",
			"24": "<sup>24</sup> Eto što učini Benajahu, sin Jehojadajev; 0n si načini jedno ime između Tridesetorice  ratnika;",
			"25": "<sup>25</sup> on imaše više ugleda no Tridesetorica, ali on ne nadmaši Trojice. David ga uvrsti u svoju osobnu gardu.",
			"26": "<sup>26</sup> Srčani ratnici: Azahel, brat Joabov; Elhanan, sin Dodoov, iz Betlehema;",
			"27": "<sup>27</sup> Šamot Harorit, Heles Pelonit;",
			"28": "<sup>28</sup> Ira, sin Ikeša Tekoita; Abiezer Anatotit;",
			"29": "<sup>29</sup> Sibekaj Hušatit; Ilaj Ahohit;",
			"30": "<sup>30</sup> Mahrai Netofatit;",
			"31": "<sup>31</sup> Itaj, sin Rivaja iz Givea, od sinova Benjaminovih; Benaja Pireatonit;",
			"32": "<sup>32</sup> Huraj iz Potoka Gaša; Abiel Arbatit;",
			"33": "<sup>33</sup> Azmavet Baharumit; Eliahba Šalbonit;",
			"34": "<sup>34</sup> BeneHašem Gizonit; Jonatan, sin Šaguea Hararita;",
			"35": "<sup>35</sup> Ahiam, sin Zakara Hararita; Elifal, sin Urov;",
			"36": "<sup>36</sup> Hefer Mekeratit; Ahija Pelonit;",
			"37": "<sup>37</sup> Hesro Karmelit; Naraj, sin Ezbajev;",
			"38": "<sup>38</sup> Joel, brat Natanov; Mibhar, sin Hagrijev;",
			"39": "<sup>39</sup> Selek Amonit; Nahraj Berotit, štitonoša Joaba, sina Serujeva;",
			"40": "<sup>40</sup> Ira Jitrit; Garev Jitrit;",
			"41": "<sup>41</sup> Uri Hitit. Zabad :, sin Ahlajev;",
			"42": "<sup>42</sup> Adina, sin Šizaa Rubenita, poglavara Rubenita i, s njim, 30;",
			"43": "<sup>43</sup> Hanan, sin Maaka i Jošafat Mitnita;",
			"44": "<sup>44</sup> Uzija Ašteratit; Šama i Jeiel, sin Hotama Aroerita;",
			"45": "<sup>45</sup> Jediael, sin Šimrijev, i Joha, njegov brat, Tisit;",
			"46": "<sup>46</sup> Eliel Hahavim, Jeribaj i Jošavija, sin Elnamaov, i Jitma Moabit;",
			"47": "<sup>47</sup> Eliel, Obed i Jaziel iz Sova."
		},
		"12": {
			"1": "<sup>1</sup> Evo onih koji dođoše k Davidu u Siklag, kad on još bijaše zadržan daleko od Saula, sina Kišova. Ubrojani među ratnike, oni sudjelovahu u boju.",
			"2": "<sup>2</sup> Oni bijahu naoružani lukovima, služiše se desnom rukom, a lijevom rukom za bacati kamenje i strijeljati strijelom iz luka. Među braćom  Saulovom iz Benjamina:",
			"3": "<sup>3</sup> poglavar Ahiezer i Joaš, sin Šemaa Givetita; Jeziel i Pelet, sinovi Azamavetovi; Beraka i Jehu Anatotit;",
			"4": "<sup>4</sup> Jišmaja Gabaonit, ratnik između Tridesetorice, i poglavar Tridesetorice;",
			"5": "<sup>5</sup> Jirmeja, Jahaziel, Johanan, i Jozabad Gedaratit;",
			"6": "<sup>6</sup> Eleuzaj, Jerimot, Bealija, Šemarijahu, i Šefatijahu Harafit",
			"7": "<sup>7</sup> Elkana Jišijahu, Azarel, Joezer, Jašobeam, Koreiti;",
			"8": "<sup>8</sup> Joela i Zebadija, sinovi Jerohama, iz Gedora.",
			"9": "<sup>9</sup> Gaditi se odvojiše za otići k Davidu u sklonište u pustinji. To bijahu srčani ljudi, ljudi rata odgojeni za boj, opremljeni štitom i kopljem, hrabri kao lavovi i brzi kao gazele na planinama.",
			"10": "<sup>10</sup> Ezer bijaše poglavar, Obadija drugi, Eliab treći,",
			"11": "<sup>11</sup> Mišmana četvrti, Jirmeja peti,",
			"12": "<sup>12</sup> Ataj šesti, Eliel sedmi,",
			"13": "<sup>13</sup> Johanan osmi, Elzabad deveti,",
			"14": "<sup>14</sup> Jirmejahu deseti, Makbanaj jedanaesti.",
			"15": "<sup>15</sup> To bijahu, među sinovima Gadovim, poglavari vojske; najmanji između njih vrijedio je stotinu; najveći je vrijedio tisuću .",
			"16": "<sup>16</sup> To bijahu oni koji bijahu prešli Jordan u prvom mjesecu, kad je prelijevao svoje obale, i koji nagnaše u bijeg sve one iz doline, na istok i na zapad.",
			"17": "<sup>17</sup> Sinovi Benjaminovi i Judini dođoše do skloništa Davidova.",
			"18": "<sup>18</sup> On iziđe pred njih, uputi im riječ i reče im: ” Ako je to za mir što ste vi došli k meni za pomoći mi, ja ću biti spreman svim srcem združiti se s vama; ali ako je to za pre variti me u korist mojih neprijatelja, tad u mojim rukama nema nasilja, nek Bog naših otaca to vidi i nek presudi!“",
			"19": "<sup>19</sup> Tad duh okrijepi Amazaja, poglavara Tridesetorice: ” Mi smo tvoji, Davide, i s tobom, sine Jeseov! Mir, mir tebi i mir onome tko ti pomaže! Jer je tvoj Bog koji ti pomaže!“ David ih primi i postavi među poglavare postrojbe.",
			"20": "<sup>20</sup> Ljudi iz Manasea se pridružiše Davidu kad on dođe s Filistincima za tući se protiv Saula ali ti ljudi mu ne mogoše pomoći, jer, savjetujući se, prinčevi Filistine otpustiše Davida govoreći: ” Po cijenu naših glava on će se pridružiti svom gospodaru Saulu !“",
			"21": "<sup>21</sup> Kad David pođe za Siklag, ljudi Manaseovi se pridružiše njemu: Adnah, Jozabad, Jediael, Mikael, Jozabad, Elihu i Siletaj, poglavari tisuća u Manaseu.",
			"22": "<sup>22</sup> To su oni koji pomogoše Davidu i postrojbi, jer oni bijahu svi ljudi smioni i postadoše oni poglavari vojske.",
			"23": "<sup>23</sup> Naime, svaki dan ljudi dolaziše k davidu za pomoći, tako da tabor postade velik kao jedan tabor Božji .",
			"24": "<sup>24</sup> Evo brojki po glavama ljudi opremljenih za vojsku, koji dođoše k Davidu u Hebron, za prenijeti njemu kraljevstvo Saulovo, prema zapovijedi GOSPODOVOJ:",
			"25": "<sup>25</sup> Sinovi Judini, koji nose štit i koplje: 6.800 ljudi opremljenih za vojsku;",
			"26": "<sup>26</sup> sinovi Simeonovi, ljudi srčanih za vojsku: 7.100;",
			"27": "<sup>27</sup> sinovi Levijevi: 4.600,",
			"28": "<sup>28</sup> i još, Jehojada, zapovjednik ljudima Aaronovim, i s njim 3.700; i još,",
			"29": "<sup>29</sup> Sadok mlad smion čovjek, i 22 poglavara njegove obitelji.",
			"30": "<sup>30</sup> Sinovi Benjaminovi, braća Saulova: 3.000, koji sve do tada bijahu većinom ostali u službi Saulovoj.",
			"31": "<sup>31</sup> Sinovi Efraimovi: 20.800 smionih ljudi, ljudi od ugleda u svojim obiteljima.",
			"32": "<sup>32</sup> Polupleme Manaseovo: 18.000 koji biše označeni po svojim imenima za doći postaviti Davida kraljem.",
			"33": "<sup>33</sup> Sinovi Isakarovi, koji znaše razlikovati vrijeme da bi spoznali što je trebao činiti Izrael: 200 poglavara i sva njihova braća pod njihovim zapovjedništvom.",
			"34": "<sup>34</sup> Iz Zabulona; 50.000 ljudi spremnih poći u vojni pohod sa svim svojim oružjem i boriti se čistog srca.",
			"35": "<sup>35</sup> Iz Neftalija: tisuću poglavara, s 37.000 ljudi naoružanihštitovima i kopljima.",
			"36": "<sup>36</sup> Od Danita: 28.600, spremnih za boj.",
			"37": "<sup>37</sup> Iz Ašera: 40.000 spremnih poći u pohod.",
			"38": "<sup>38</sup> Od onih s one strane Jordana, Rubenita, Gadita i poluplemena Manaseova: 120.000, sa svim bojnim oružjem.",
			"39": "<sup>39</sup> Svi ovi ljudi ratnici, spremni staviti se u bojni poredak, dođoše čistog srca u Hebron za postaviti Davida kraljem nad svim Izraelom. Sav ostatak Izraela dođe također jednodušno za postaviti Davida kao kralja.",
			"40": "<sup>40</sup> Oni provedoše ondje tri dana s Davidom jedući i pijući, jer njihova braća bijahu pripremila za njih hranu i piće.",
			"41": "<sup>41</sup> Ljudi iz oblasti, sve do Isakara, Zabulona i Neftalija, donesoše također živeži na magarcima, devama, mulama i govedima: brašno, kolače od smokava, suho grožđe, vino, ulje, sitnu i krupnu stoku u izobilju, jer bijaše radost u Izraelu."
		},
		"13": {
			"1": "<sup>1</sup> David održa savjetovanje s poglavarima tisuća i stotina, i sa svim odličnicima.",
			"2": "<sup>2</sup> On reče svem saboru Izraelovom: ” Ako vi to nalazite dobrim i ako to dolazi od GOSPODA našeg Boga, uputimo jednu poruku našoj braći ostaloj u svim područjima Izraelovim, kao i svećenicima i *levitima u njihovim gradovima prebivališta, da se pridruže nama.",
			"3": "<sup>3</sup> Potom mi ćemo dovesti k nama *kovčeg našeg Boga, jer mi nismo se bavili njime u vrijeme Saulovo.“",
			"4": "<sup>4</sup> Sav sabor dade pristanak za postupiti tako, jer stvar se činila pravednom u očima sveg naroda.",
			"5": "<sup>5</sup> David sakupi sav Izrael, od potoka egipatskog sve do ulaza u Hamat, za dovesti kovčeg Božji u KirijatJearim .",
			"6": "<sup>6</sup> Sa svim Izraelom uspe se on u Baala, u KirijatJearim koji je u Judi, za dovesti kovčeg Božji, GOSPODOV koji sjedi na *kereubinima, ondje gdje je zazvano njegovo *ime.",
			"7": "<sup>7</sup> Natovari se kovčeg Božji na jedna nova kola, od kuće AmiNadavove. Uza i Ahio voziše kola.",
			"8": "<sup>8</sup> David i sav Izrael plesaše svom svojom snagom pred Bogom praćeni pjesmama, kitarama, harfama, bubnjevima, cimbalima i trubljama.",
			"9": "<sup>9</sup> Oni stigoše na gumno Kidon .Uza ispruži ruku za zadržati kovčeg, jer ga goveda zamalo prevrnuše.",
			"10": "<sup>10</sup> Gnjev Božji zapali se proiv Uzaa i udari ga jer bijaše ispružio ruku na kovčeg. On umrije ondje pred Bogom.",
			"11": "<sup>11</sup> David bi potresen što GOSPOD bi otvorio jednu brešu udarajući Uza, naziva se to mjesto ”Uzova Breša“ sve do ovog dana.",
			"12": "<sup>12</sup> David se uplaši Boga, u onaj dan, i reče: ” Kako ću ja dovesti k sebi kovčeg Božji?“",
			"13": "<sup>13</sup> Tad David ne prenese kovčeg kod sebe, u *Grad Davidov, već ga dade odvesti u kuću ObedEdoma, Gitita .",
			"14": "<sup>14</sup> Kovčeg Božji ostade tri mjeseca kod ObedEdoma, i sve što bijaše njegovo."
		},
		"14": {
			"1": "<sup>1</sup> Hiram, kralj Tira, posla glasonoše Davidu, s cedrovim drvetom, kamenoklesar i tesare za izgraditi jednu kuću.",
			"2": "<sup>2</sup> Tad David znade da ga GOSPOD bijaše postavio kraljem nad Izraelom i da njegovo kraljevanje bijaše ponosno proslavljeno zbog Izraela njegovog naroda.",
			"3": "<sup>3</sup> David uze još nekoliko žena u Jeruzalemu i porodi još sinova i kćeri.",
			"4": "<sup>4</sup> Evo imena onih koji mu se rodiše u u Jeruzalemu: Šamua, Šobab, Natan i Solomon,",
			"5": "<sup>5</sup> Jibhar, Elišua i Elpalet,",
			"6": "<sup>6</sup> Nogah, Nefeg i Jafija,",
			"7": "<sup>7</sup> Elišama, Belijada i Elifelet.",
			"8": "<sup>8</sup> Filistinci doznadoše da David bijaše *pomazan kao kralj nad svim Izraelom. Svi Filistinci krenuše u potragu za Davidom. David to saznade i iziđe pred njih.",
			"9": "<sup>9</sup> Filistigoše i osvojiše dolinu Refaita .",
			"10": "<sup>10</sup> David upita Boga: ” Trebam li se ja uspeti protiv Filistinaca i hoćeš li ih ti predati u ruke mojeđ“ GOSPOD mu odgovori : ” Pođi i i ja ću ih izručiti tebi u ruke.“",
			"11": "<sup>11</sup> Tad se oni uspeše u BaalPerasim, i ondje ih David potuče. David reče: ” Bog je putem moje ruke načinio jednu brešu kod mojih protivnika, kao brešu otvorenu za vode.“ Zato je tom mjestu dano ime BaalPerasim.",
			"12": "<sup>12</sup> Oni ondje napustiše njihove bogove, a David reče: Nek budu spaljeni vatrom!“",
			"13": "<sup>13</sup> Ponovo Filistinci navališe na dolinu.",
			"14": "<sup>14</sup> David opet ispitiviaše Boga, a Bog njemu reče: ” Ne uspinji se za proganjati ih: napravi jedan zaobilazak daleko od njih i doći ćeš k njim, nasuprot koprivištima.",
			"15": "<sup>15</sup> Kad budeš začuo buku koraka povrh koprivića, tad ćeš izići u boj, jer Bog će već biti izišao pred tobom, za udariti tabor Filistinaca. “",
			"16": "<sup>16</sup> David postupi kako Bog bijaše njemu zapovjedio, i oni tukoše tabor Filistinaca, od Gabaona sve do Gezera .",
			"17": "<sup>17</sup> Ugled se Davidov proširi po svim zemljama, a GOSPOD ga učini strašnim za sve narode. :"
		},
		"15": {
			"1": "<sup>1</sup> David sebi načini kuće u *Gradu Davidovi; on odredi jedno mjesto za *kovčeg Božji i podiže za njega jedan šator.",
			"2": "<sup>2</sup> Tad on reče: ” Za nositi kovčeg Božji, bit će samo *leviti, jer GOSPOD ih je izabrao za nositi kovčeg GOSPODOV i za služiti ga zauvijek .“",
			"3": "<sup>3</sup> David dade sakupiti sav Izrael u Jeruzalemu za uspeti kovčeg GOSPODOV k mjestu koje bijaše određeno za njega.",
			"4": "<sup>4</sup> Oni sastavi također sinove Aaronove i levite:",
			"5": "<sup>5</sup> Za sinove Kahatove: poglavar Uriel i njegova braća, 120;",
			"6": "<sup>6</sup> za sinove Merarijeve: poglavar Azaja i njegova braća, 220;",
			"7": "<sup>7</sup> za sinove Geršomove: poglavar Joel i njegova braća, 130;",
			"8": "<sup>8</sup> za sinove Elisafanove: poglavar Šemaja i njegova braća: 200;",
			"9": "<sup>9</sup> za sinove Hebronove: poglavar Eliel i njegova braća, 80;",
			"10": "<sup>10</sup> za sinove Uzielove: poglavar AmiNadav i njegova braća: 112.",
			"11": "<sup>11</sup> David pozva svećenike Sadoka i Abiatara, i levite: Uriela, Azajaa, Joela, Šemajaa, Eliela i AmiNadava.",
			"12": "<sup>12</sup> On im reče: ” Vi ste poglavari levitskih obitelji. *Posvetite se, vi i vaša braća, i uspnite kovčeg GOSPODOV, Boga Izraelovog, k mjestu koje sam ja odredio za njega.",
			"13": "<sup>13</sup> Naime, pošto vi niste prvi put bili ondje, GOSPOD Bog naš udario nas je, jer ga mi nismo tražili prema pravilima . “",
			"14": "<sup>14</sup> Svećenici i leviti se tada posvetiše za uspeti kovčeg GOSPODOV, Boga Izraelovog.",
			"15": "<sup>15</sup> Levitski sinovi, kako to bijaše naredio Mojsije  prema riječi GOSPODOVOJ, nosiše kovčeg Božji na svojim plećima s prečkama.",
			"16": "<sup>16</sup> A David reče levitskim poglavarima da postave u službu svoju braću, pjevače, s glazbalima; lutnjama, lirama i cimbalima, da odjekuju snažno u znak radovanja.",
			"17": "<sup>17</sup> Leviti postaviše u službu njihovu Hemlana, sina Joelovog, i među njegovom braćom, Asafa, sina Berekijahuova; između sinova Merarijevih, njihove braće, Etana, sin Kušajahuova;",
			"18": "<sup>18</sup> i s njima, kao druge, njihovu braću: Zekarijahua, Bena, Jaziela, Šemiramota, Jehiela, Unija, Eliaba, Benajahua, Mazejahua, Matitijahua, Eliflehua i Miknejahua, ObedEdoma i Jeiela, nosače.",
			"19": "<sup>19</sup> Između pjevača: Heman, Asaf i Etan imaše mjedena cimbala da odjekuju;",
			"20": "<sup>20</sup> Zekarija, Aziel, Šemiramot, Jehiel, Uni, Eliab, Mazejahu i Benajahu imaše lutnje za sopranski glas;",
			"21": "<sup>21</sup> Matitijahu, Eliflehu, Miknejahu, ObedEdom, Jeiel i Azazijahu imaše lire oktavske, za uspjeravati pjesmu.",
			"22": "<sup>22</sup> A Kenanijahu, poglavar levitski za prijevoz, uredi prijevoz, jer bijaše sposoban za to.",
			"23": "<sup>23</sup> Berekija i Elkana bijahu nosači kovčega.",
			"24": "<sup>24</sup> Svećenici Šebanijahu, Jošafat, Netanel, Amasaj, Zekarijahu, Benajahui Eliezer sviraše u trube ispred kovčega Božjeg. Obed-Edom i Jehija bijahu nosači kovčega.",
			"25": "<sup>25</sup> Tada, u radosti, David, *starješine Izraelove i poglavari tisuća  pođoše za uspeti *kovčeg saveza GOSPODOVOG od kuće ObedEdomove.",
			"26": "<sup>26</sup> I da Bog dadne svoju pomoć *levitima koji nosiše kovčeg saveza GOSPODOVOG, ponudiše se u žrtve sedam bikova i sedam ovnova.",
			"27": "<sup>27</sup> David bijaše ogrnut jednim ogrtačem od morske svile, kao i svi leviti koji su nosili kovčeg, pjevači i Kenanija, zapovjednik prijevoza, David imaše također, na sebi, jedan laneni efod .",
			"28": "<sup>28</sup> Sav Izrael uznese kovčeg saveza GOSPODOVOG u klicanjima i jeci rogova, trubalja, i cimbala, i odjekivanju su lutnja i lira.",
			"29": "<sup>29</sup> Kad kovčeg saveza GOSPODOVOG stiže u *Grad Davidov, Mikal, kćer Saulova, nagnu se kroz prozor. Ona vidje kralja Davida koji poskakivaše i plesaše i ona ga prezrije u srcu svom."
		},
		"16": {
			"1": "<sup>1</sup> Oni unesoše kovčeg Božji i odložiše ga u sred šatora koji David bijaše razapeo za njega, potom oni ponudiše holokauste  i žrtve mira, pred Bogom.",
			"2": "<sup>2</sup> Kad David bi završio nuditi holokauste i žrtve mira, on blagoslovi narod u ime GOSPODOVO.",
			"3": "<sup>3</sup> Potom on razdijeli svakom Izraelitu čovjeku ili ženi svakom po jedan komad kruha, jedan kolač od datulja i jedan kolač od grožđa . :",
			"4": "<sup>4</sup> David postavi pred *kovčeg GOSPODOV stanovit broj *levita koji obavljaše službu, da bi se podsjetili, slavili i hvalili GOSPODA, Boga Izraelovog:",
			"5": "<sup>5</sup> Asaf, poglavar i njegov drugi, Zekarija; potom Aziel, Šemiramot, Jehiel, Matitija, Eliab, Benajahu, i ObedEdom; Jehiel imaše lutnje i lire; a Asaf činiše da odjekuju cimbala.",
			"6": "<sup>6</sup> Svećenici Benajahu i Jahaziel trubiše stalno trubama ispred kovčega saveza Božjeg.",
			"7": "<sup>7</sup> To bi u taj dan da David zaduži, po prvi put, Asafa i njegovu braću slaviti GOSPODA.",
			"8": "<sup>8</sup> SLAVITE gospoda, objavljujte njegovo *ime, obznanjujte njegove podvige među narodima.",
			"9": "<sup>9</sup> Pjevajte za njega, svirajte za njega; prepričavajte sva čuda.",
			"10": "<sup>10</sup> Budite ponosni njegovim svetim imenom i radosni, vi koji tražite GOSPODA!",
			"11": "<sup>11</sup> TRAŽITE gospoda i njegovu snagu, tražite uvijek njegovo lice.",
			"12": "<sup>12</sup> Sjetite se čuda što ih je on učinio, njegovih čuda i presuda izišliuh na njegova usta,",
			"13": "<sup>13</sup> vi, lozo Izraela sluge njego vog, vi, sinovi Jakovljevi  izabranici njegovi!",
			"14": "<sup>14</sup> On je GOSPOD naš Bog koji upravlja svom zemljom.",
			"15": "<sup>15</sup> Sjećajte se za uvijek njegovo *saveza, zapovijedi za tisuću pokoljenja;",
			"16": "<sup>16</sup> onog koji je sklopio s Ab- rahamom, potvrđenog prisegom Izaku,",
			"17": "<sup>17</sup> koji je podigao u naredbu za Jakova, vječni savez za Izrael,",
			"18": "<sup>18</sup> kad je rekao: ” Ja ti dajem zemlju Kanaanovu; to je dio koji vi baštinite!“",
			"19": "<sup>19</sup> Tada vas se moglo prebro jati, vi bijaste jedna pregršt useljenika.",
			"20": "<sup>20</sup> Oni iđaše i dolaziše iz na roda u narod, iz jednog kraljevstva prema drugom nekom narodu.",
			"21": "<sup>21</sup> Ali on ne dopusti nikome njih tlačiti, on pokažnjava kraljeve zbog njih:",
			"22": "<sup>22</sup> ” Ne dirajte mojih *pos lanika :; ne nasite zlo mojim *prorocima!“",
			"23": "<sup>23</sup> Pjevajte GOSPODU, cijela zem ljo, proglašavajte njegov spas iz dana u dan;",
			"24": "<sup>24</sup> oglašavajte slavu među narodima, njegova čuda među pučanstvima.",
			"25": "<sup>25</sup> Jer GOSPOD je velik i obasut hvalospjevima, on je strašan i nadmoćan sim bogovima:",
			"26": "<sup>26</sup> sva božanstva pučanstava su ispraznosti{ !",
			"27": "<sup>27</sup> Sjaj bljesak su pre licem njegovi; sila i veličanstvo{  u njegovom : mjestu.",
			"28": "<sup>28</sup> Dajte GOSPODU, obitelji naroda! Dajte GOSPODU slavu i snagu;",
			"29": "<sup>29</sup> dajte GOSPODU slavu imena njegovog. Donosite svoje milodare, uđite pred lice njegovo; poklonite se pred GOS PODOM, kad zablješti njegova *svetost{ .",
			"30": "<sup>30</sup> Drhtite pred njim, zemljo cijela. Da, svijet ostaje postojan i nepokolebljiv.",
			"31": "<sup>31</sup> Nek se nebesa raduju, nek zemlja kliče; govorite među narodima: GOSPOD je kralj!“",
			"32": "<sup>32</sup> Nek grmi more i njegova bogatstva; nek polje sve u cijelosti bude u svetkovini,",
			"33": "<sup>33</sup> nek drveta šuma kriče tad od radosti pred GOSPODOM, jer on dolazi za upravljati zemljom.",
			"34": "<sup>34</sup> Slavite GOSPODA, jer on je dobar, jer njegova je vjernost za uvijek.",
			"35": "<sup>35</sup> I recite: ” Spasi nas, Bože našeg spasenja, sakupi nas i oslobodi nas iz sredine naroda za slaviti tvoje sveto ime, proslavljajući se da tebe hvalimo.",
			"36": "<sup>36</sup> Blagoslovljen budi GOSPOD, Bog Izraelov, oduvijek i zauvijek!“ A sav narod govori: *”Amen!“ I : ” Hvalite GOSPODA!“",
			"37": "<sup>37</sup> David ostavi ondje, pred kovčegom saveza GOSPODOVOG, Asafa i njegovu braću, koji moraše osiguravati stalnu službu pred kovčegom prema redu predviđenom za svaki dan;",
			"38": "<sup>38</sup> i, kao nosači, Obed-Edom sin Jeditunov i Hoza.",
			"39": "<sup>39</sup> David pusti svećenika Sadoka i svećenike, njegovu braću, pred boravište GOSPODOVO na *visokom mjestu Gabaona,",
			"40": "<sup>40</sup> za nuditi neprekidno holokauste  GOSPODU, na *oltaru holokausta, jutrom i s večeri, a za činiti sve što je pisano u Zakonu što ga je GOSPOD propisao Izraelu.",
			"41": "<sup>41</sup> S njima, bijahu Heman i Jedutun, i ostatak onih koji bijahu bili izabrani i označeni svojim imenima za slaviti GOSPODA: ” Jer njegova je vjernost za uvijek.“",
			"42": "<sup>42</sup> I s njima Heman i Jedutun, oni imaše trube, zvučna cimbalaza pratiti pjesme Bogu. Sinovi Jedutunovi bijahu starješine vrata.",
			"43": "<sup>43</sup> Sav narod ode svatko k sebi, a David se vrati za blagosloviti kuću svoju ."
		},
		"17": {
			"1": "<sup>1</sup> Dok David bijaše u svojoj kući, on reče *proroku Natanu: ”Evo ja stanujem u jednoj kući od cedrovine, a *kovčeg saveza Božjeg je pod platnima šatora .",
			"2": "<sup>2</sup> Natan odgovori Davidu: ” Sve što ti imaš namjeru činiti, učini, jer GOSPOD je s tobom.“",
			"3": "<sup>3</sup> No, te noći, riječ Božja bi upućena Natanu, ovim riječima:",
			"4": "<sup>4</sup> ” Idi reći mom sluzi Davidu: Ovako govori GOSPOD: Nisi ti taj koji će mi sagraditi Kuću da ja tu stanujem.",
			"5": "<sup>5</sup> Jer ja nisam stanovao u nekoj kući od dana kad sam uspeo Izrael pa sve do ovog dana: ja sam bivao od šatora do šatora i od boravišta do boravišta.",
			"6": "<sup>6</sup> U svakom mjestu gdje sam se našao među svim Izralom, jesam li ja uputio bar samo jednu riječ nekom od sudaca Izraelovih, kome sam zapovjedio da napasa moj narod, za reći: Zašto mi niste izgradili jednu kuću od cedrovine?",
			"7": "<sup>7</sup> Sada dakle, ti ćeš ovako govoriti mojem sluzi Davidu: Ovako govori GOSPOD, svemogući: Ja sam taj koji te uzeo s ispaše, iza stada, da postaneš glava nad Izraelom, narodom mojim.",
			"8": "<sup>8</sup> Ja sam bio s tobom posvuda gdje si ti išao: ja sam uništio sve tvoje neprijatelje pred tvojim licem i ja ću učiniti tvoje ime kao što su velikani zemlje.",
			"9": "<sup>9</sup> Ja ću odrediti jedno mjesto Izraelu mojem puku, ja ću ga posaditi i on će ostati na svom mjestu; on neće više drhtati i zločinci neće vise započinjati proždirati ga kao nekada.",
			"10": "<sup>10</sup> I od dana kad sam postavio sudce nad Izraelom mojim pukom. Ja sam pokorio sve neprijatelje tvoje, i najavio ti da će ti GOSPOD sagraditi jednu kuću .",
			"11": "<sup>11</sup> Kad se napune tvoji dani za otići s ocima svojim, ja ću podići tvoje potomstvo poslije tebe, to će biti jedan od tvojih sino i ja ću učvrstiti kraljevstvo njegovo .",
			"12": "<sup>12</sup> On je taj koji će meni sagraditi jednu Kuću, a ja ću za uvijek postaviti njegovo prijestolje.",
			"13": "<sup>13</sup> Ja ću biti za njega jedan otac i on će biti za mene jedan sin; ja neću povući od njega vjernost svoju kao što sam je povukao od onoga koji bijaše prije tebe .",
			"14": "<sup>14</sup> Ja ću učiniti da istrajava zauvijek u mojoj Kući i u mom kraljevstvu, a njegovo prijestolje bit će zauvijek učvršćeno.“",
			"15": "<sup>15</sup> Ovo je poslije svih tih riječi i poslije sveg tog viđenja što Natan reče Davidu.",
			"16": "<sup>16</sup> Kralj David dođe sjesti u nazočnost GOSPODOVU, i izjavi: ”Tko sam ja, GOSPODE Bože, i što je kuća  moja, da si me ti doveo dovde?",
			"17": "<sup>17</sup> A to još bijaše premalo u tvojim očima, moj Bože, i ti si govorio također za kuću sluge svoga, za dugo vremena unaprijed. Ti si me gledao kao jednog čovjeka visokog položaja, GOSPODE Bože.",
			"18": "<sup>18</sup> Što bi još mogao David reći više, glede slave sluge tvoga? Ti, ti dobro poznaješ slugu svog.",
			"19": "<sup>19</sup> GOSPODE, to je zbog tvog sluge i prema tvom srcu što si ti izvršio ovo veliko djelo da dadeš upoznati sve svoje veličine.",
			"20": "<sup>20</sup> GOSPODE, nema nikog ravnog tebi, i prema svemu onom što smo mi vlastitim ušima čuli, nema drugog Boga, osim tebe.",
			"21": "<sup>21</sup> Ima li na zemlje i samo jedan narod jednak Izraelu tvom puku, tom narodu kojeg je Bog išao otkupiti da bi ga učinio svojim narodom, za dati tebi jedno *ime veliko strašno, protjerujući neznabošce pred tvojim narodom kojeg si ti iskupio iz Egipta?",
			"22": "<sup>22</sup> Ti si sebi dao Izrael puk svoj za zauvijek ga učiniti narodom svojim, i GOSPODE, ti si postao njihov Bog.",
			"23": "<sup>23</sup> A sada, GOSPODE, nek riječ koju si ti izgovorio o tvom sluzi i njegovoj kući bude istinita zauvijek. Postupi kao što si rekao!",
			"24": "<sup>24</sup> Nek on bude istinita, tvoje ime bude slavljeno zauvijek, i nek se kaže: ” GOSPOD, svemogući, Bog je Izraelov, on je Bog za Izrael; i nek kuća tvog sluge Davida ostane trajno u tvojoj nazočnosti!",
			"25": "<sup>25</sup> Naime, ti si taj, moj Bože koji je upozorio svog slugu govoreći za reći da ćeš mu ti podići jednu kuću. Evo zašto je tvoj sluga našao smjelosti uputiti ti ovu molitvu.",
			"26": "<sup>26</sup> A sada GOSPODE, ti si Bog, i ti si govorio o toj sreći sluzi svojem.",
			"27": "<sup>27</sup> Uzhtjedni sada blagosloviti kuću sluge svojeg, da bi zauvijek bila u nazočnosti tvojoj, jer ti, GOSPODE, ti blagoslovljaš i ti si blagoslovljen :zauvijek."
		},
		"18": {
			"1": "<sup>1</sup> Poslije toga, David potuče Filistince i pokori ih. David uze Gat iz ruku Filistincima .",
			"2": "<sup>2</sup> On potuče Moabite, i Moabiti postadoše za Davida sluge podvrgnute plaćanju danka.",
			"3": "<sup>3</sup> David porazi Hadadezera, kralja Sova, kod Hamata, kad ovaj postavi svoju vlast na na rijeku Eufrat.",
			"4": "<sup>4</sup> A David mu uze tisuću kola, 7.000 konjanika i 20.000 pješaka. David posiječe potkoljenice svim zapregama ostavljajući samo jednu stotinu tih zaprega.",
			"5": "<sup>5</sup> *Aramenci iz Damaska dođoše u pomoć Hadadezeru, kralju Sova, ali David ubi 22.000 ljudi među Aramencima.",
			"6": "<sup>6</sup> David tad postavi upravitelja  u Aramu damaškom. Aramenci postadoše za Davida sluge podvrgnute nametu. GOSPOD dade dakle pobjedu Davidu svugdje gdje on iđaše.",
			"7": "<sup>7</sup> David se domože zlatnih štitova koje nosiše sluge Hadadezerove i donese ih u Jeruzalem.",
			"8": "<sup>8</sup> A u gradovima Hadadezerovim, Tivatu i Kunu, David se dočepa jedne veila velike količine bronce: Solomon od toga načini more od bronce, stubove i pribor od bronce .",
			"9": "<sup>9</sup> Tu, kralj Hamata, doznade da David bijaše pobio svu vojsku Hadadezeru, kralju Sova.",
			"10": "<sup>10</sup> On dakle posla svog sina Hadorama kralju Davidu za pozdraviti ga i čestitati mu za rat da ga je potukao jer Hadadezer bijaše neprijatelj Tuu i donijeti mu razne vrste predmeta od zlata, srebra i bronce.",
			"11": "<sup>11</sup> Te predmete također, kralj David posveti GOSPODU, uz srebro i zlato koje bijaše uzeo od svih pokorenih naroda Edoma, Moaba, od sinova Amonovih, od Filistinaca i od Amaleka .",
			"12": "<sup>12</sup> Abšaj, sin Serujavljev, pobijedi Aramejce u dolini Sol, njih 18.000 na broju.",
			"13": "<sup>13</sup> On postavi u Edomu upravitelje, i svi Edomiti postadoše sluge Davidu. GOSPOD dade dakle pobjedu Davidu kud god on iđaše. :",
			"14": "<sup>14</sup> David zavlada nad svim Izraelom i vršiše pravo i pravdu nad svim svojim narodom.",
			"15": "<sup>15</sup> Joab, sin Serujev, zapovijedaše vojskom; Jehošafat, sin Ahiludov, bijaše poklisar;",
			"16": "<sup>16</sup> Sadok, sin Ahituvljev, i Ahimelek, sin Abiatarov, bijahu svećenici; Šavša bi zakonoznanac;",
			"17": "<sup>17</sup> Benajahu, sin Jehojadavljev, zapovijedaše Keretincima i Peletincima; i sinovi Davidovi bijahu prvi do kralja. :"
		},
		"19": {
			"1": "<sup>1</sup> Poslije toga Nahaš, kralj sinova Amonovih, a njegov sin postade kraljem na njegovom mjestu.",
			"2": "<sup>2</sup> David tada reče: ” Ja ću postupiti prema Hanunu, sinu Nahaševu, s vjernostđću jer otac njegov je s vjernošću postupao prema meni. “ David mu dakle posla, glasonoše da ga utješe zbog oca njegovog i sluge Davidove stigoše u zemlju sinova Amonovih, kod Hanuna za utješiti ga.",
			"3": "<sup>3</sup> Ali prinčevi sinova Amonovih rekoše Hanunu: ” Ti zamišljaš da je David htio štovati oca tvoga kad ti je poslao te ljude za utješiti te? Nije li to za istražiti zemlju, za uhoditi ju i uzbuniti, što su sluge njegove došle k tebi? “",
			"4": "<sup>4</sup> Hanun zatvori sluge Davidove, obrija ih, razreza njihovu odjeću po pola sve do butina i otposla ih.",
			"5": "<sup>5</sup> Izvijestiše Davida o sudbini tih ljudi i on posla nekoga njima u susret, jer ljudi bijahu osramoćeni . Kralj im dakle dade reći: ” Ostanite u Jerihonu sve dok vaše brade ponovo ne porastu. Tek tada ćete se vratiti. “ :",
			"6": "<sup>6</sup> Sinovi Amonovi vidješe da su postali nepodnošljivi Davidu, i Hanun i sinovi Amonovi poslaše tisuću talenata srebra za unajmiti u službu svoju kod ljudi iz AramDvijuRijeka i kod Aramejaca iz Maka i iz Sova, kola i konjanika.",
			"7": "<sup>7</sup> Oni uzeše u svoju službu 32.000 kola, kao i kralja Maaka i njegov narod, koji dođoše taborovati nasuprot Madabi  potom se sinovi Amonovi sakupiše izlaziće iz svojih gradova i stigošeu boj.",
			"8": "<sup>8</sup> David to doznade i posla Joaba i svu vojsku ratnika.",
			"9": "<sup>9</sup> Sinovi Amonovi iziđoše i stadoše u bojni poredak na vratima grada, ali kraljevi koji bijahu došli stajaše po strani, u polju.",
			"10": "<sup>10</sup> Joab vidje da se treba suprostaviti se i naprijed i otpozada; on izabra ljude iz sveg cvijeta Izraelovog i postavi jedan red sučelice Aramejcima.",
			"11": "<sup>11</sup> On povjeri ostatak postrojbe svom bratu Abišaju i oni uspostaviše jedan red sučelice sinovima Amonovim.",
			"12": "<sup>12</sup> Potom on reče: ” Ako su Aramejci jači nego ja, ti ćeš doći meni u pomoć. A ako su sinovi Amonovi jači od tebe, ja ću doći tebi u pomoć.",
			"13": "<sup>13</sup> Budi snažan, i pokažimo se snažnima, za naš puk i za gradove naše Boga. I nek GOSPOD učini što mu godi.“",
			"14": "<sup>14</sup> Tad Joab i njegova postrojba se primaknuše za tući Aramejce. Ovi pak pobjegoše pred njima.",
			"15": "<sup>15</sup> Kad sinovi Amonovi vidješe Aramejce u bijegu, oni i sami pobjegoše ispred Abišaja, brata Joabova i vratiše se u grad. Joab se vrati u Jeruzalem.",
			"16": "<sup>16</sup> Aramejci vidješe da bijahu pobijeđeni od Izraela. Oni poslaše glasonoše i dovedoše sve Aramejce s one strane Rijeke. Šovak, povjednik vojske Hadadezerove, bijaše im na čelu.",
			"17": "<sup>17</sup> Javiše to Davidu. On pokupi sav Izrael, prijeđe Jordan i stiže k njima i stavi se nasuprot njima David se stavi u bojni poredak nasuprot Aramejcima i zapodjenu se boj.",
			"18": "<sup>18</sup> Ali Aramejci pobjegoše ispred Izraela. I David pobi Aramejcima 7.000 ljudi iz zaprega i 40.000 pješaka. On uništi također Šovaka, zapovjednika vojske.",
			"19": "<sup>19</sup> Sluge Hadadezerove, vidješe da bijahu potučeni pred Izraelom; oni dakle sklopiše mir s Izraelom i služiše mu. Aramejci ne htjedoše priteći u pomoć sinovima Amonovim."
		},
		"20": {
			"1": "<sup>1</sup> U času povratka godine, u vrijeme kad kraljevi se stavljaju u pohod, Joab povede vojsku i opustoši zemlju sinova Amonovih. Potom on dođe staviti opsadu pred Rabu, dok David boraviše u Jeruzalemu. Joab potuče Rabu i uništi ju.",
			"2": "<sup>2</sup> A David skide krunu koja bijaše na glavi Milkomovoj i nađe u njoj težinu od jednog talenta zlata; ona nosiše jedan dragulj. Ona bi stavljena na Davidovu glavu, i odnese on iz grada jednu veliku količinu plijena.",
			"3": "<sup>3</sup> On jednako izgna pučanstvo koje bijaše u gradu i osudi ga da rukuje pilom, željeznim klinovima i željeznim sjekirama. Tako David učini sa svim gradovima sinova Amonovih; potom David i sav narod vratiše se u Jeruzalem.",
			"4": "<sup>4</sup> Poslije toga bio je jedan boj u Gezru protiv Filistinaca. To je tada kad Zibekaj iz Huša potuče Zipajan jednog od djece Refaita, i oni mu biše pokoreni.",
			"5": "<sup>5</sup> Bijaše još jedan boj protiv Filistinaca, i Elhanan, sin Jairov, iz Betlehema, ubi brata Golijatova iz Gata, koji imaše koplje od drveta jednako gredi s tkalačkog stana.",
			"6": "<sup>6</sup> Bijaše još jedna bitka u Gatu. Bijaše jedan div koji je imao po šest prstiju, ukupno 24. On također bijaše jedan potomak Harafaov.",
			"7": "<sup>7</sup> On baci izazov Izraelu, a Jehonatan, sin Šimeavljev, brat Davidov, ga ubi. Ti ljudi bijahu potomci Harafavljevi, iz Gatu; oni padoše pod udarcima Davida i njegovih slugu. :"
		},
		"21": {
			"1": "<sup>1</sup> *Sotona se podiže Izraela i on pobudi Davida da popiše Izrael.",
			"2": "<sup>2</sup> David reče Joabu, i poglavarima narodnim: ” Idite, prebrojte Izrael od Ber-Ševe do Dana  potom načinite mi jedno izvješće da mu znadem broj. “",
			"3": "<sup>3</sup> Joab reče kralju: ” Nek GOSPOD poveća narod stostruko! Nisu li oni svi, moj gospodare kralju, sluge mog gospodarađ Zašto moj gospodar hoće to istraživanjeđ Zašto bi Izrael bio krivcem ? “",
			"4": "<sup>4</sup> Zapovijed kraljeva se ipak nametnu Joabu, Joab pođe i prođe sav Izrael, potom se vrati u Jeruzalem.",
			"5": "<sup>5</sup> I Joab dade Davidu brojke popisa naroda: sav Izrael brojaše 1.100.000 ljudi sposobnih povući mač, a Juda 470.000 ljudi sposobnih pvući mač.",
			"6": "<sup>6</sup> Ne bijaše popisivanja među onima Levija i Benjamina, jer riječ kraljeva duboko onemili Joabu. :",
			"7": "<sup>7</sup> To bi jedna loša stvar u očima Božjim, i on udari Izrael.",
			"8": "<sup>8</sup> Tad David reče Bogu: ” To je jedan ozbiljan grijeh što sam počinio. Sada dakle, udostoji se oprostiti grješku sluge svoga, jer sam ja postupio stvarno kao jedna luda! “",
			"9": "<sup>9</sup> GOSPOD progovori Gadu, vidovnjaku  Davidovom, ovim riječima:",
			"10": "<sup>10</sup> ” Idi reći Davidu: Ovako govori GOSPOD: predlažem ti tri stvari: izaberi jednu od njih i jaću ju izvršiti. “",
			"11": "<sup>11</sup> Gaad dakle ode naći Davida i reče mu: ” Ovako govori GOSPOD: Na tebi je da prihvatiš:",
			"12": "<sup>12</sup> ili tri godine gladi, ili tri mjeseca poraza pod udarcima mača neprijatelja svojih, ili tijekom tri dana, mač GOSPODOV i kugu u svojoj zemlji, *anđela istrijebitelja GOSPODOVOG na svoj teritoriji Izraela. Sada dakle, vidi što trebam ja odgovoriti onome koji me poslao. “",
			"13": "<sup>13</sup> A David reče Gadu: ” Ja sam u velikom strahu! Nek ja dakle padnem radije u ruke GOSPODU, jer njegovo milosrđe veliko, ali da ne padnem u ruke ljudima!",
			"14": "<sup>14</sup> “ GOSPOD dakle posla kugu u Izrael, i ona obori 70.000 ljudi u Izraelu.",
			"15": "<sup>15</sup> Bog posla jednog anđela u Jeruzalem za opustošiti ga, i kako on izvrši pustošenje, gospod pogleda i bi rastužen tom nesrećom. On reče anđelu istrijebitelju: ” Dosta! sada, opusti ruku svoju!“ A anđeo GOSPODOV bijaše pokraj gumna Ornana Jebuzitskog .",
			"16": "<sup>16</sup> David podiže oči i vidje anđela GOSPODOVOG koji se držaše između neba i zemlje, imajući u ruci svoj isukani mač, okrenut prema Jeruzalemu. David i *starješine, pokriveni *kostrijetima padoše na lice svoje.",
			"17": "<sup>17</sup> David reče Bogu: ” Nisam li ja taj koji je rekao da se popisuje narodđ Ja sam taj koji je zgriješio i počinio zlo. Ali, te ovčice, što su one učinile? GOSPODE moj Bože, nek tvoja ruka bude nada mnom i mojom obitelji, ali da, na tvom narodu, ona ne bude jedna nevolja!“ :",
			"18": "<sup>18</sup> *Anđeo GOSPODOV reče Gaduda ode reći Davidu: ” Nek se uspne David za podići jedan *oltar GOSPODU na gumnu Ornana Jebuzitskog. “",
			"19": "<sup>19</sup> David se uspe prema riječi koju Gad bijaše rekao u ime GOSPODOVO.",
			"20": "<sup>20</sup> Ornan se bijaše okrenuo i vidio anđela, a njegova su se četiri sina koji bijahu s njim sakrila. Ornan je mlatio žito.",
			"21": "<sup>21</sup> David dođe k Ornanu, a Ornan pogleda i vidje Davida; potom iziđe s gumna i pokloni se pred Davidom, licem prema zemlji.",
			"22": "<sup>22</sup> A David reče Ornanu: ”Daj mi površinu ovog gumna i ja ću na njemu izgraditi jedan oltar GOSPODU. Daj mi ga za punu vrijednost u srebru; tako će nevolja biti zadržana daleko od naroda. “",
			"23": "<sup>23</sup> Ornan reče Davidu: ”Uzmi ga za sebe, i nek moj gospodar kralj čini ono što mu se sviđa. Ti vidiš: ja dajem goveda za holokauste, saonice i zapregu volovska za drva, pšenicu za ponude; ja dajem sve!“",
			"24": "<sup>24</sup> Ali kralj David reče Ornanu: ”Ne, ja hoću to kupiti po cijeni srebra. Ja neću uzeti za GOSPODa, ono što je tvoje, nudeći jedzn holokaust koji ne košta ništa!“",
			"25": "<sup>25</sup> Tad David Ornanu za to zemljište, jednu težinu zlata od 600 sikala.",
			"26": "<sup>26</sup> Ondje, David izgradi jedan oltar GOSPODU i on ponudi holokauste i žrtve mira. On zazva GOSPODA koji mu odgovori vatrom došlom s nebesa na oltar holokausta.",
			"27": "<sup>27</sup> Potom GOSPOD reče anđelu da vrati svoj mač u korice.",
			"28": "<sup>28</sup> U ono vrijeme kad David vidje da GOSPOD njemu bijaše odgovorio na gumnu Ornana Jebuzita, on tu ponudi žrtve.",
			"29": "<sup>29</sup> A boravište GOSPODOVO koje Mojsije bijaše načino u pustinji i oltar za holokaust bijahu u to vrijeme na *visokom mjestu Gabaonovom,",
			"30": "<sup>30</sup> ali David nije mogao otići pitati za savjet Boga, jer bijaše užasnut mačem anđela GOSPODOVOG."
		},
		"22": {
			"1": "<sup>1</sup> I David reče: “ Ovo je kuća GOSPODA Boga, i evo oltara za holokaust za Izrael!“ :",
			"2": "<sup>2</sup> David zapovijedi da se sakupe stranci  koji bijahu u zemlji Izraelovoj i on označi kamenolome za pripremati klesano kamenje, da bi sagradili Kuću Božju.",
			"3": "<sup>3</sup> David također pripremi željeza u velikoj količini, za čavle na krilima vrata i za stege, bronce u tolikoj količini da se nije mogla izmjeriti,",
			"4": "<sup>4</sup> i cedrovog drveta bez broja, jer Sidonijanci i Tirijanci : bijahu donijeli Davidu mnoštvo cedrovog drveta.",
			"5": "<sup>5</sup> David je rekao: ” Moj je sin Solomon još mlad i nejak, a kuća zaizgraditi GOSPODU mora biti ugledna u svim zemljama sa svoje veličine i sjaja. Ja ću dakle obaviti pripreme za njega.“ Tako, prije svoje smrti, David obavi velike pripreme.",
			"6": "<sup>6</sup> On pozva Solomona, sina svog, i zapovijedi mu da izgradi jednu Kuću za GOSPODA, Boga Izraelovog.",
			"7": "<sup>7</sup> David reče Solomonu: ” Sine moj, ja imam na srcu, ja osobno, izgraditi jednu Kuću za *ime GOSPODOVO, mog Boga.",
			"8": "<sup>8</sup> Ali riječ GOSPODOVA bi mi upućena ovim izrazima: Ti si prosuo mnogo krvi i vodio si velike ratove. Ti nećes đ graditi Kuću za moje ime, jer ti si prosuo mnogo krvi na zemlji preda mnom.",
			"9": "<sup>9</sup> Evo,rođen ti je sin koji će biti, on, jedan čovjek spokoja i kojem ću ja dati odmor nasuprot svim njegovim neprijateljima uokolo njega, jer Solomon će biti ime njegovo, a ja ću dati mir  i spokoj Izraelu tijom njegovih dana.",
			"10": "<sup>10</sup> On je taj koji će izgraditi Kuću za moje ime. On će za mene biti sin a ja ću učvrstiti njegov kraljevski prijestol nad Izraelom zauvijek®",
			"11": "<sup>11</sup> sada dakle nek GOSPOD bude stobom, sine moj, da ti izgradiš s uspjehom Kuc[u GOSPODA, svog Boga, kao što je on to rekao glede tebe!",
			"12": "<sup>12</sup> Samo, nek ti Gospodin da razboritosti i umnosti kad te bude postavio nad Izraelom, za čuvati Zakon GOSPODA tvog Boga!“",
			"13": "<sup>13</sup> Dakle, ti ćeš napredovati ako budeš pazio, za primijeniti ih, propise i naredbe koje je GOSPOD naložio Mojsiju glede Izraela. Budi jak i smion! Budi bez straha i bez bojazni.",
			"14": "<sup>14</sup> Evo, usprkos mojem siromaštvu, ja sam pripremio, za Kuću GOSPODOVU, 100.000 talenata  zlata i 1,000.000 talenata srebra. Za broncu i željezo, njih se ne može ni izmjeriti, toliko su u izobilju. Ja sam također pripremio drveta i kamenja, a ti ćeš još imati.",
			"15": "<sup>15</sup> Ti imaš u izobilju radnika; kamenara, kamenoklesara i tesara, svih vrsta vještih ljudi u svim zanatima.",
			"16": "<sup>16</sup> Za zlato, srebro, broncu i željezo, ne može ih se vrjednovati. Digni se, postupaj i nek Bog bude s tobom!“",
			"17": "<sup>17</sup> David zapovjedi svim poglavarima Izraelovim da pomognu njegovog sina Solomona:",
			"18": "<sup>18</sup> ” GOSPOD vaš Bog nije li s vama? Nije li vam dao mir sa svih stranađ Naime, on je predao u moje ruke stanovnike zemlje koja je bila podvrgnuta GOSPODU i njegovom puku.",
			"19": "<sup>19</sup> Sad, prionite svim srcem i svojim životom traženju GOSPODA svog Boga. Dignite se i sagradite *svetište GOSPODU svom Bogu za donijeti *kovčeg saveza GOSPODOVOG i posvećene predmete Božje u Kuću izgrađenu za ime GOSPODOVO.“"
		},
		"23": {
			"1": "<sup>1</sup> David bijaše star i nasićen danima, kad postavi svog sina Solomona kao kralja nad Izraelom.",
			"2": "<sup>2</sup> Potom on sakupi sve poglavare Izraela, svećenike i *levite.",
			"3": "<sup>3</sup> Leviti od 30 godina i više biše tada prebrojani.Njihov broj, računajući po glavi, bi 38.000 ljudi,",
			"4": "<sup>4</sup> to jest među njima: 24.000 za voditi radove Kiuće GOSPODOVE, 6.000 kao pisari i sudci,",
			"5": "<sup>5</sup> 4000 kao vratari i 4.000 za hvaliti GOSPODA glazbalima ”što ja načinih za hvaliti ga“ (reče David).",
			"6": "<sup>6</sup> David njih podijeli u razrede, prema sinovima Levijevim: Geršon, Kehat i Merari.",
			"7": "<sup>7</sup> Za Geršonite: Laedan i Šimei.",
			"8": "<sup>8</sup> Sinovi Laedanovi: Jehiel, poglavar, Zetam i Joel: trojica.",
			"9": "<sup>9</sup> Sinovi Šimejevi: Šelimot, Haziel i Haran: trojica. To su poglavari obitelji Laedan.",
			"10": "<sup>10</sup> Sinovi Šimejevi: Jahat, Ziza, Jeuš i Beria: to su četiri sina Šimejeva.",
			"11": "<sup>11</sup> A Jahat bijaše prvi, Ziza drugi, ali Jeuš i Berija ne imadoše puno sinova i činiše jednu obitelj za jedinstvenu obvezu.",
			"12": "<sup>12</sup> Sinovi Kehatovi: Amran, Jicehar, Hebroj i Uziel: četiri.",
			"13": "<sup>13</sup> Sinovi Amranovi: Aaron i Mojsije. Aaron bi stavljen na stranu za posvetiti se službi u *presvetom mjestu, on i njegovi sinovi zauvijek, za nuditi miris pred GOSPODOM, za služiti i za davati blagoslov u njegovo ime zauvijek.",
			"14": "<sup>14</sup> Mojsije bi čovjek Božji; njegovi sinovi biše ubrojani u pleme Levi.",
			"15": "<sup>15</sup> Sinovi Mojsijevi:",
			"16": "<sup>16</sup> sinovi Geršomovi: Šebuel, poglavar.",
			"17": "<sup>17</sup> Sinovi Eliezerovi: Rehabija, glavar; Eliezer ne imade drugih sinova a sinovi Rehabijavljevi biše veoma brojni.",
			"18": "<sup>18</sup> Sin Jiceharov: Selimot glavar.",
			"19": "<sup>19</sup> Sinovi Hebronovi: Jerijahu prvi, Amarija drugi, Jahaziel treći, i Jekameam četvrti.",
			"20": "<sup>20</sup> Sinovi Uzielovi Mika prvi i Jišija drugi.",
			"21": "<sup>21</sup> Sinovi Merarijevi: Mahli i Muši. Sinovi Mahlijevi: Eleazar i Kiš.",
			"22": "<sup>22</sup> Eleazar umrije i ne imade sinova, već kćeri, a sinovi Kišovi, njihova braća :, uzeše ih za žene.",
			"23": "<sup>23</sup> Sinovi Mušijevi: Mahli, Eder i Jeremet: trojica.",
			"24": "<sup>24</sup> To biše sinovi Levijevi prema svojim obiteljima, glave obiteljske prema svojim zaduženjima, po poimenicđ nom nabrajanju, po glavama; oni izvršavaše svoj rad u službi Kuće GOSPODOVE, počev od doba od dvadeset godina i iznad toga.",
			"25": "<sup>25</sup> David, naime, bijaše rekao: ” GOSPOD, Bog Izraelov, je dao spokoj svom narodu i on boravi u Jeruzalemu zauvijek.",
			"26": "<sup>26</sup> Tako, Leviti neće više nositi *Boravište i sve predmete namijenjene njegovoj službi.“",
			"27": "<sup>27</sup> Ali, prema posljednjih Davidovim riječima, takvo bi prebrajanje sinova Levijevih, počev od dvadeset godine i iznad.",
			"28": "<sup>28</sup> Oni se moraše držati zapovijedi sinova Aaronovih za službu u Kući GOSPODOVOJ, glede*trijemova, odaja, *očišćenja svake posvećene stvari, i rada u službi Kuće Božje;",
			"29": "<sup>29</sup> oni se moraju baviti kruhom ponude, brašnom za milodare, beskvasnim pogačama, pečenim kolačima, miješanim kolačima i svime što se tiče sprava zapremine i mjere;",
			"30": "<sup>30</sup> oni se imaju pripremiti, svako jutro, za slaviti i hvaliti GOSPODA, i isto navečer,",
			"31": "<sup>31</sup> za sve holokauste ponuđene GOSPODU, za *šabate, mlade mjesece  i blagdane, prema broju koji je njima bio propisan zauvijek, pred GOSPODOM.",
			"32": "<sup>32</sup> Oni će imati također obvezu da se bave *šatorom susretanja, onim što je posvećeno i sinovi Aaronovi, njihova braća, za sluzđ bu Kuće GOSPODOVE."
		},
		"24": {
			"1": "<sup>1</sup> Za sinove Aaronove, evo njihovih razreda: sinovi Aaronovi: Nadav i Abihu, Eleazar i Itamar.",
			"2": "<sup>2</sup> Nadav i Abihu umriješe prije svojeg oca ne imavši sinova; svećeničke službe biše biše tada povjerene Eleazaru i Itamaru.",
			"3": "<sup>3</sup> David, kao i Sadok, sinovi Eleazarov, i Ahimelek, sinovi Itamarovin njih podijeliše u razrede prema njihovoj zadaći u službi njihovoj.",
			"4": "<sup>4</sup> A sinovi Eleazarovi se nađoše brojniji u ljudstvu nego sinovi Itamarovi; podijeliše ih, za sinove Eleazarove, na šesnaest glava obiteljskih, i za sinove Itamarove na osam poglavara obiteljskih.",
			"5": "<sup>5</sup> Podijeli ih se kako jedne, tako i druge, ždrijebanjem, jer bijaše tu prinčeva *svetišta i prinčeva Božjih  među sinovima Eleazarovim kao i sinovima Itamarovim.",
			"6": "<sup>6</sup> Šemaja, sin Netanelov, zakonoznanac među *levitima, njih upisa u nazočnosti kraljevoj, prinčeva, svećenika Sadoka, Ahimelekovoj, sinova Abiatarovih i poglavara svećeničkih i levitskih: jedna obitelj bi ždrijebana za Eleazara, potom jedna druga, tok samo jedna jedina bi ždrijebana za Itamara .",
			"7": "<sup>7</sup> Prvi ždrijeb bi za Jehojariba a drugi za Jedajaa;",
			"8": "<sup>8</sup> treći za Harima, četvrti za Seorima,",
			"9": "<sup>9</sup> peti za Malkijaa, šesti za Mijamina,",
			"10": "<sup>10</sup> sedmi za Hakosa, osmi za Abijaa,",
			"11": "<sup>11</sup> deveti za Ješuaa, deseti za Šekanijahua,",
			"12": "<sup>12</sup> jedanaesti za Eliašiba, dvanaesti za Jakima,",
			"13": "<sup>13</sup> trinaesti za Hupaa, četrnaesti za Ješebeaba,",
			"14": "<sup>14</sup> petnaesti za Bilga, šesnaesti za Imera,",
			"15": "<sup>15</sup> sedamnaesti za Hezira, osamnaesti za za Hapisesa,",
			"16": "<sup>16</sup> devetnaesti za Petahija, dvadeseti za Jehezkela,",
			"17": "<sup>17</sup> dvadeset i prvi za Jakina, dvadeset i drugi za Gamula,",
			"18": "<sup>18</sup> dvadeset i treći za Delajahua, dvadeset i četvrti za Mazijahua.",
			"19": "<sup>19</sup> Takva bi njihova raspodjela u službu njihovu, za ulaziti u Kuću GOSPODOVU, prema pravilu danom po njihovom ocu Aaronu, kako to njemu bijaše naložio GOSPOD, Bog Izraelov. :",
			"20": "<sup>20</sup> Glede sinova Levijevih koji preostadoše: za sinove Amranove, to bijaše Šubael; za sinove Šubaelove; to bijaše Jehdejahu;",
			"21": "<sup>21</sup> za Rehabijahua i za sinove Rehabijahuvljeve, to bijaše Jišija, glava;",
			"22": "<sup>22</sup> za Jiceharite, to bijaše Selomot; za sinove Selomotove, to bijaše Jahat;",
			"23": "<sup>23</sup> za sinove Hebronove, to bijahu Jerijahu, prvi, Amarijahu, drugi, Jahaziel, treći, Jekameam, četvrti;",
			"24": "<sup>24</sup> sinovi Uzielovi: Mika; za sinove Mikavljeve, to bijaše Šamir;",
			"25": "<sup>25</sup> brat Mikavljev: Jišija; za sinove Jišijavljeve, to bijaše Zekarijahu;",
			"26": "<sup>26</sup> sinovi Merarijevi: Mahli i Muši, sinovi Jazijahua, njegovog sina;",
			"27": "<sup>27</sup> sinovi Merarijevi za Jazijahua njegovog sina: Šoham, Zakur i Ivri;",
			"28": "<sup>28</sup> za Mahlija to bijaše Eleazar koji ne imade sinova;",
			"29": "<sup>29</sup> za Kiša i Kišove sinove, to bijaše, to bijaše Jerhamel;",
			"30": "<sup>30</sup> sinovi Mušijevi: Mahli, Eder i Jerimot. To bijahu sinovi *levitski prema njihovim obiteljima.",
			"31": "<sup>31</sup> Oni također ždrijebahu kao i njihova braća, sinovi Aaronovi, u nazočnosti kralja Davida, Sadoka, Ahimeleka i obiteljskih svećeničkih i levitskih poglavara, obitelji poglavara kao i obitelji njegovog najmlađeg brata."
		},
		"25": {
			"1": "<sup>1</sup> David i vojni poglavari staviše na sranuza službu sinove Asafljeve, Hemanove i Jedutunove koji *prorokovaše uz citre, harfe i cimbala. Evo broja ljudi koji su vrsiđli tu službu:",
			"2": "<sup>2</sup> Za sinove Asafove: Zakur, Josip, Netanija i Azarela. Sinovi Asafovi bijahu pod ravnanjem Asafovim koji prorokovaše pod ravnateljstvom kraljevim.",
			"3": "<sup>3</sup> Za Jedutuna, sinovi Jedutunovi: Gedalijahu, Seri, Ješajahu, Hašabijahu, Matitijahu i Šimej, šestorica, pod ravnanjem njihovog oca Jedutuna koji prorokovaše uz kitaru za slaviti i hvaliti GOSPODA.",
			"4": "<sup>4</sup> Za Hemana, sinovi Hemanovi: Bukijahu, Matanijahu, Uziel, Šebuel, Jerimot, Hananija, Hanani, Eliata, Gidalti, RomamtiEzer, Jošbekaša, Maloti, Hotir, Mahaziot; svi oni bijahu sinovi Hemana, vidovnjaka kraljevog, koji njima prenosiše riječi Božje za uzdizati njegovu moć; Bog dade Hemanu četrnaest sinova i tri kćeri:",
			"6": "<sup>6</sup> oni bijahu svi pod ravnanjem svog oca za pjevati o Kući GOSPODOVOJ, uz cimbala, harfe i kitare, za službu Kuće Božje, pod ravnanjem kraljevim, Asafljevim, Jedutunovim i Hemanovim.",
			"7": "<sup>7</sup> Njihov broj, s njihovom braćom obrazovanom za pjevati GOSPODU svih s majstorstvom bijaše 288.",
			"8": "<sup>8</sup> Oni ždrijebaše red službe, za male kao i za velike, za učitelja kao i za učenika.",
			"9": "<sup>9</sup> A prvi ždrijeb bi za Asafa, na Josipa. Drugi, Gedalijahu; on, njegovi sinovi i njegova braća: dvanaest.",
			"10": "<sup>10</sup> Treći, Zajur; njegovi sinovi i njegova braća: dvanaest.",
			"11": "<sup>11</sup> Četvrti, Jiceri; njegovi sinovi i njegova braća: dvanaest.",
			"12": "<sup>12</sup> Peti, Netanijahu; njegovi sinovi i njegova braća: dvanaest.",
			"13": "<sup>13</sup> Šesti, Bukijahu; njegovi sinovi i njegova braća: dvanaest.",
			"14": "<sup>14</sup> Sedmi, Jezarela; njegovi sinovi i njegova braća: dvanaest.",
			"15": "<sup>15</sup> Osmi, Ješajau; njegovi sinovi i njegova braća: dvanaest.",
			"16": "<sup>16</sup> Deveti, Matanijahu; njegovi sinovi i njegova braća: dvanaest.",
			"17": "<sup>17</sup> Deseti, Šimej; njegovi sinovi i njegova braća: dvanaest.",
			"18": "<sup>18</sup> Jedanaesti, Azarel: njegovi sinovi i njegova braće: dvanaest.",
			"19": "<sup>19</sup> Dvanaesti, Hašabija; njegovi sinovi i njegova braća: dvanaest.",
			"20": "<sup>20</sup> Trinaesti, Šubael; njegovi sinovi i njegova braća: dvanaest.",
			"21": "<sup>21</sup> Četrnaesti, Matitijahu; njegovi sinovi i njegova braća: dvanaest.",
			"22": "<sup>22</sup> Petnaesti, Jeremot; njegovi sinovi i njegova braća: dvanaest. 23 Šesnaesti, Hananijahu; njegovi sinovi i njegova braća: dvanaest.",
			"24": "<sup>24</sup> Sedamnaesti, Jošbekaša; njegovi sinovi i njegova braća: dvanaest.",
			"25": "<sup>25</sup> Osamnaesti, Hanani; njegovi sinovi i njegova braća: dvanaest.",
			"26": "<sup>26</sup> Devetnaesti, Maloti; njegovi sinovi i njegova braća: dvanaest.",
			"27": "<sup>27</sup> Dvadeseti, Eliata; njegovi sinovi i njegova braća: dvanaest.",
			"28": "<sup>28</sup> Dvadeset i prvi, Hotir; njegovi sinovi i njegova braća: dvanaest.",
			"29": "<sup>29</sup> Dvadeset i drugi, Gidalti; : njegovi sinovi i njegova braća: dvanaest.",
			"30": "<sup>30</sup> Dvadeset i treći, Mahaziot; njegovi : sinovi i njegova braća: dvanaest.",
			"31": "<sup>31</sup> Dvadeset i četvrti, RomantiEzer; njegovi sinovi i njegova braća: dvanaest."
		},
		"26": {
			"1": "<sup>1</sup> Za razred vratara: Za Koreite: Mešelemijahu, sin Kojeov, između sinova Asafovih.",
			"2": "<sup>2</sup> Mešelemijahu imade za sinove: Zekarijahu prvorođeni, Jediael drugi, Zebadijahu, treći, Jatniel, četvrti,",
			"3": "<sup>3</sup> Elam peti, Jehohanan šesti, Eliehoenaj sedmi.",
			"4": "<sup>4</sup> Obed-Edomimade za sinove: Šemaja prvorođeni, Jehozabad drugi, Joah treći, Zakar četvrti, netanel peti,",
			"5": "<sup>5</sup> Amiel šesti, Isakar sedmi, Peoletaj osmi, jer ga Bog bijaše blagoslovio.",
			"6": "<sup>6</sup> Njegom sinu Šemaju rodiše se sinovi koji imaše vlast u obiteljisvojoj, jer bijahu ljudi od vrijednosti.",
			"7": "<sup>7</sup> Sinovi Šemajavljevi: Otni Refael, Obed, Elzabad, i njegova braća, ljudi od vrijednosti, Elihu i Zemakijahu.",
			"8": "<sup>8</sup> Svi ovi bijahu sinovi ObedEdoma; oni, njihovi sinovi i njihova braća bijahu ljudi od vrijednosti zbog snage koju pokazivaše u svojoj službi. Oni bijahu 62 za ObedEdoma.",
			"9": "<sup>9</sup> Mešelemijahu imade sinove i braću, osamnaest ljudi od vrijednosti.",
			"10": "<sup>10</sup> Hoza, sinovi Merarijevih, imade sinove: Šimri, glava; on nije bio prvorođeni, ali ga je njegov otac postavio kao glavara;",
			"11": "<sup>11</sup> Hilkijahu drugi; Tevalijahu treći, Zekarijahu četvrti; svi sinovi i braća Hozavljeva bijahu na broju od trinaest.",
			"12": "<sup>12</sup> Tim razredima vratara, poglavarima ljudi, pripade kao i njihovoj braći služba da služe u kući GOSPODOVOJ.",
			"13": "<sup>13</sup> Od najmanjeg do najvećeg, ždrijebalo se prema njihovim obiteljima, za svaka od vrata.",
			"14": "<sup>14</sup> Za istok, ždrijeb pade na Šelemijahua. Za Zekarijahua njegovog sina koji bijaše jedan mudar savjetnik, ždrijeb njemu dodijeli sjever.",
			"15": "<sup>15</sup> ObedEdomu, to bi jug, i njegovim sinovima, prodavaonice.",
			"16": "<sup>16</sup> Šupimu i Hozau, istok s vratima Šaleketovim  na strmoj cesti. Različite službe bijahu:",
			"17": "<sup>17</sup> na istoku šest *levita dnevno; na jugu, četvorica dnevno; i za prodavaonice dva po dva;",
			"18": "<sup>18</sup> za Parbar  na zapadu, četiri za cestu, dva za Parbar.",
			"19": "<sup>19</sup> Takvi bijahu razredi vratara za sinove Koreita i sinove Merarijeve.",
			"20": "<sup>20</sup> Izvjesni *leviti, njihova braća, bijahu raspoređeni u riznice Kuće Božje i u riznice svetih stvari.",
			"21": "<sup>21</sup> Sinovi Laedanovi sinovi Geršonita po Laedanu, poglavari obitelji Laedana Geršonita bijahu Jehieliti.",
			"22": "<sup>22</sup> Sinovi Jehielita, Zetam i Joel njegov brat, bijahu raspoređeni riznicama Kuće GOSPODOVE.",
			"23": "<sup>23</sup> Za Amramite, Jicehariti, Hebroniti, Uzieliti",
			"24": "<sup>24</sup> to bijaše to bijaše Šebuel sin Geršomov, sina Mojsijeva, koji bijaše odgovorni riznica.",
			"25": "<sup>25</sup> Njegova braća, po Eliezeru, bijahu Rehabijahu njegov sin, koji bijaše za sinove Joramove, koji imade za sina Zikrija, koji imade za sina Šelomita.",
			"26": "<sup>26</sup> Taj Šelomit, kao i njegova braća, bijaše zadužen za sve riznice *svetih stvari koje bijahu posvećene kralju Davidu, poglavari obitelji, glavari tisuća  i stotina, i poglavari vojske.",
			"27": "<sup>27</sup> Oni njih bijahu posvetili, na ratnom plijenu, održavanju Kuće GOSPODOVE.",
			"28": "<sup>28</sup> I sve što biješe posvetio Samue vidovnjak, Saul sin Kišov, Abner sin Nerov i Joab sin Serujavljev, sve to što bijaše posvećeno bi povjereno Šelomitu i njegovoj braći.",
			"29": "<sup>29</sup> Za Jiceharite, Kenanijahu i njegovi sinovi bijahu raspoređeni vanjskim poslovima Izraelovim, kao pisari i kao sudci.",
			"30": "<sup>30</sup> Za Hebronite, Hazabijahu i njegova braća, ljudi od vrijednosti na broju od 1.700, bijahu zaduženi nadzirati Izrael sa strane zapadno od Jordana, za sve poslove GOSPODOVE i službu kraljevu.",
			"31": "<sup>31</sup> Za Hebronite, Jerija bijaše glava za Hebronite bi se izvršli istraživanje rodoslovlja njihovih obitelji, tijekom četrdesete godine vladavine Davidove i bijaše se našlo između njih ljudi od vrijednosti, u Jazeru iz Galaada .",
			"32": "<sup>32</sup> On i njegova braća bijaše ih 2.700 ljudi od vrijednosti, glave obitelji, koje kralj David bijaše postavio nad Rubenitima, Gaditima i polovicom plemena Manaseova, za sve poslove Božje i one kraljeve."
		},
		"27": {
			"1": "<sup>1</sup> Sinovi Izraelovi, prema svojem broju, glave obitelji, poglavari tisuća  i stotina, časnici u službi kraljevoj za sve što se tiče divizija, one koje dolaze i one koje odlaze, mjesec po mjesec, svakog mjeseca u godini. Jedna divizija brojaše 24.000 ljudi.",
			"2": "<sup>2</sup> Nad prvom divizijom, za prvi mjesec, bijaše Jašobeam, sin Zabdielov, i njegova divizija brojaše 24.000 ljudi.",
			"3": "<sup>3</sup> On pripadaše sinovima Peresovim, i zapovijedaše svim vojnim poglavarima za prvi mjesec.",
			"4": "<sup>4</sup> Nad divizijom drugog mjeseca bijaše Dodaj Ahohit; njegova divizija koja bijaše pod zapovjedništvom Miklotovim brojaše 24.000 ljudi.",
			"5": "<sup>5</sup> Poglavar treće armija, za treći mjesec, bijaše Benajahu, sin velikog svećenika Jehojadaa; njegova divizija brojaše 24.000 ljudi;",
			"6": "<sup>6</sup> taj Benajahu bijaše jedan junak između Tridesetorice i poglavar Tridesetorice. Njegovoj diviziji pripadaše također Amizabad, njegov sin.",
			"7": "<sup>7</sup> Četvrti, za četvrti mjesec, bijaše Azahel brat Joabov i Zebadija njegov sin poslije njega; njegova divizija brojaše 24.000 ljudi. za Zabulon, Jišmzajahu.",
			"8": "<sup>8</sup> Peti, za peti mjesec, bijaše poglavar Šamhut Jizrahit; njegova divizija brojaše 24.000 ljudi.",
			"9": "<sup>9</sup> Šesti, za šesti mjesec, bijaše Ira sin Ikeša Tekoita; njegova divizija brojaše 24.000 ljudi.",
			"10": "<sup>10</sup> Sedmi, sa sedmi mjesec, bijaše Heles Pelonit, između sinova Efraimovih; njegova divizija brojaše 24.000 ljudi.",
			"11": "<sup>11</sup> Osmi, za osmi mjesec, bijaše Zibekaj Hušatit, od Zarhita; njegova divizija brojaše 24.000 ljudi.",
			"12": "<sup>12</sup> Deveti, za deveti mjesec, bijaše Abiezer Anatotit, od Benjaminita; njegova divizija brojaše 24.000 ljudi.",
			"13": "<sup>13</sup> Deseti, za deseti mjesec, bijaše Mahraj Netofatit, od Zarhita; njegova divizija brojaše 24.000 ljudi.",
			"14": "<sup>14</sup> Jedanaesti, za jedanaesti mjesec, bijaše Benaja Pireatonit, između sinova Efraimovih; njegova divizija brojaše 24.000 ljudi.",
			"15": "<sup>15</sup> Dvanaesti, za dvanaesti mjesec, bijaše Heldaj Netofatit, iz obitelji Otnielove; njegova divizija brojaše 24.000 ljudi.",
			"16": "<sup>16</sup> Nad plemenima Izraelovim, zapovjednik za Rubenite bijaše Eliezer sin Zikrija; za Simeonite, Šefatijahu sin Maakavljev;",
			"17": "<sup>17</sup> za Levite Hašabija sin Kemuelov; za Aarona, Sadok;",
			"18": "<sup>18</sup> za Judu, Elihu od braće Davidove; za Isakar, Omri sin Mikaelov; za Zabulon Jišmajahu sin Obadijahuvljev; za Neftali, Jerimot sin Azrielov:",
			"20": "<sup>20</sup> za sinove Efraimove, Hošea sin Azazijahuvljev; za polupleme Manaseovo, Joel sin Pedajahuvljev;",
			"21": "<sup>21</sup> za polupleme Manaseovo u Galaadu, Iddo sin Zekarijahuvljev; za Benjamin, Jaziel sin Abnerov;",
			"22": "<sup>22</sup> za Dan, Azarel sin Jerohamov. To bijahu glave plemena Izraelovih.",
			"23": "<sup>23</sup> David ne bijaše popisao broj onih koji bijahu stariji od dvadeset godina i iznad, jer GOSPOD bijaše učinio Izrael tako brojnim kako su zvijezde na nebu.",
			"24": "<sup>24</sup> Joab, sin Serujavljev bijaše i njih računati, ali ne završi, jer zbog tog se obori gnjev na Izrael, također njihov broj ne postoji ni u *Godišnjacima kralja Davida.",
			"25": "<sup>25</sup> Nad kraljevskim riznicama bijaše Azmavet sin, Adielov. Nad poljskim zalihama, u gradovima, u selima i u kulama, bijaše Jehonatan, sin Uzijahuvljev.",
			"26": "<sup>26</sup> Nad radnicima poljaskim za obrađivati tle, bijaše Ezri, sin Keluvljev;",
			"27": "<sup>27</sup> nad vinogradima, Šimej Ramatit; nas onima koji bijahu u vinogradima za zalihe vina, Zavdi Šifmit;",
			"28": "<sup>28</sup> nad maslinjacima i sikomorama u *Niziji, Baal-Hanan, Gederit; na zalihama ulja, Joaš;",
			"29": "<sup>29</sup> nad krupnom stokom koja se napasaše u Šaronu, Šitraj Šaronit; nad krupnom stokom u dolinama, Šafat, sin Adlajev;",
			"30": "<sup>30</sup> nad devama, Ovil Ismaelit; nad magaricama, Jehdejahu Meronotit;",
			"31": "<sup>31</sup> nad sitnom stokom, Jaziz Hagrit. Svi oni bijahu nadstojnici dobara koja pripadahu kralju Davidu.",
			"32": "<sup>32</sup> Jehonatan, stric Davidov, bijaše savjetnikom; bijaše to jedan uman čovjek i on bijaše zakonoznanac. Jehiel, sin Hakmonijev, bijaše uz sinove kraljeve.",
			"33": "<sup>33</sup> Ahitofel bijaše kraljevim savjetnikom, a Hušaj Arkit bijaše prijatelj kraljev .",
			"34": "<sup>34</sup> Ahitofela naslijediše Jehojada, sin Benajahuvljev, i Abiatar. Glava vojsci bijaše Joab."
		},
		"28": {
			"1": "<sup>1</sup> David sakupi u Jeruzalemu sve glavare Israelove, one plemenske i one divizijske koji služiše kralju, one tisuća  i stotina i one svih dobara i stada koja pripadaše kralju i njegovim sinovima, s *eunusima, ratnicima i svim ljudima od vrijednosti.",
			"2": "<sup>2</sup> Kralj David, uspravljajući se, reče njima: ” Slušajte me, braćo moja i narode moj. Ja sam imao na srcu sagraditi jenu Kuću : gdje bi počivao *kovčeg saveza GOSPODOVOG i podnožje našem Bogu, i ja učinih pripreme za izgraditi ju.",
			"3": "<sup>3</sup> Ali Bog mi je rekao: ” Ti nećeš izgraditi kuću za moje *ime, jer ti si čovjek rata i ti si prolio krv.",
			"4": "<sup>4</sup> GOSPOD, Bog Izraelov, me izabrao, u svoj mojoj obitelji, za biti kraljem nad Izraelom zauvijek, jer on je kao vođu izabrao Judu i, u kući Judinoj, kuću oca moga i, među sinovima mog oca, svidjelo mu se meni dati vladati nad svim Izraelom.",
			"5": "<sup>5</sup> Između svih mojih sinova jer GOSPOD mi je dao brojne sinove on je izabrao mog sina Solomona za sjedjeti na prijestolju kraljevstva GOSPODOVOG nad Izraelom.",
			"6": "<sup>6</sup> Potom on mi je rekao: ” Tvoje je sin Solomon koji će izgraditi moju Kuću i moje *trijemove, jer ja sam ga izabrao kao sina a ja, bit ću za njega otac.",
			"7": "<sup>7</sup> Ja sam pripremio njegovo kraljevanje zauvijek ako, kao što je to danas, ostane postojan u primjeni mojih zapovijedi i mojih naredbi.",
			"8": "<sup>8</sup> A sada, na očigled sveg Izraela, sabora GOSPODOVOG i u nazočnosti našeg Boga: pazite i istražujtesve zapovijedi GOSPODA svojeg Boga, da biste uzeli u posjed ovu dobru zemlju i učinili ju baštiniti vašim sinovim poslije vas, zauvijek.",
			"9": "<sup>9</sup> A ti, sine moj Solomone, poznaji Boga svojeg oca, i služi ga poštenim srcem i gorljive duše, jer GOSPOD ispituje sva *srca i razlikuje sve vrste misli. Ako li ga ti tražiš, on će se pustiti naći od tebe, ali ako ga ti napustiš, on će te odbaciti zauvijek.",
			"10": "<sup>10</sup> Gledaj sada: Gospod te izabrao za izgraditi jednu Kuću kao *svetište, budi odlučan i djeluj!“",
			"11": "<sup>11</sup> David dade svom sinu Solomonu nacrt trijema, njegovih ostava, njegovih gornjih odaja, njegovih donjih dvorana i pomirbene odaje,",
			"12": "<sup>12</sup> i nacrt svega što je imao namjeru učiniti za *trijem Kuće GOSPODOVE i za sve okolne odaje, za riznice Kuće Božje i riznice svetih predmeta;",
			"13": "<sup>13</sup> za razrede svećenika i *levita i za sva pitanja službe Kuće GOSPODOVE;",
			"14": "<sup>14</sup> za zlato, s težinom u zlatu svih predmeta svake službe, i za sve predmete od srebra s težinom predmeta svake službe;",
			"15": "<sup>15</sup> za svijećnjake od zlata i njihove svjetiljke od zlata, s težinom svakog svijećnjaka i njegovih svjetiljki; za svijećnjake od srebra, s težinom svijećnjaka i njegovih svjetiljki, prema službi svakog svijećnjaka;",
			"16": "<sup>16</sup> težinu u zlatu za svaki stol nuđenja  i srebra za srebrene stolove,",
			"17": "<sup>17</sup> kuke, plitice i pehare od čistog zlata, zlatne kupe s težinom svake kupe, i srebrene kupe s težinom svake kupe;",
			"18": "<sup>18</sup> za *oltar mirisa, od čistog zlata s njegovom težinom, i za nacrt kola, *kerubina od zlata raširenih krila koja pokrivaju *kovčeg saveza GOSPODOVOG .",
			"19": "<sup>19</sup> Sve se ovo nalazi u spisu iz ruke GOSPODOVE, koji mi je dao shvatiti sva djela nacrta.",
			"20": "<sup>20</sup> Tad David reče svom sinu Solomonu: ” postupaj odlučno i smjelo! Budi bez straha i ne boj se, jer GOSPOD Bog, moj Bog, s tobom je. On te neće ostaviti i napustiti, sve do izvršenja sveg posla za službu Kuće GOSPODOVE.",
			"21": "<sup>21</sup> Evo razreda svećenika i levita za svaku službu Kuće Božje; i s tobom, u svem ovom poslu, bit će ljudi dobre volje i punih znanja u svakom poslu; a svi poglavari i sav narod bit će pod tvojim zapovijedima.“"
		},
		"29": {
			"1": "<sup>1</sup> Kralj David reče svem saboru: ” Moj sin Solomon, jedini kojeg je Bog izabrao, mlad je i nejak, a djelo je veliko, jer ta palača nije namijenjena jednom čovjeku, već GOSPODU Bogu.",
			"2": "<sup>2</sup> Svom svojom snagom ja sam pripremao za Kuću svog Boga zlato za ono što će biti od zlata, srebro za ono što će biti od srebra, broncu za ono što će biti od bronce, željezo za ono što će biti od željeza, drvo za ono što će biti od drveta, oniks kamen i kamen za optočiti, kamenje crno i u boji, sve vrste dragog kamenja i veliku količinu alabastra.",
			"3": "<sup>3</sup> Osim toga, jer mi je mila Kuća mog Boga, zlato i srebro koje osobno posjedujem, ja ga dajem za Kuću mog Boga, osim svega onog što sam pripremio za tu svetu Kuću:",
			"4": "<sup>4</sup> tri tisuće talenata zlata, zlata iz Ofira; 7.000 talenata pročišćenog srebra, za pokriti zidove građevinama;",
			"5": "<sup>5</sup> za sve što je od zlata, za sve što je od srebra i za sav rad ruku radnika. Tko je danas još spreman dobrovoljno dati za GOSPODA?“",
			"6": "<sup>6</sup> Tad poglavari obitelji, onih u plemena Izraelovih, oni od tisuća : i stotina i oni koji se baviše kraljevim polsovima ponudiše dobrovoljne darove",
			"7": "<sup>7</sup> i dadoše ih za službu Kuće Božju: u zlatu 5.000 talenata i 10.000 darika :, u srebru 10.000 talenata, u bronci 18.000 i u željezu 100.000 talenata.",
			"8": "<sup>8</sup> Oni kod kojih se nađe dragog kamenja predadoše ga za riznicu Kuće Božje u ruke Jehielu Geršonitu.",
			"9": "<sup>9</sup> A narod se obradova njihovim dragovoljnim prilozima, jer to bijaše dano jednim čistim srcem za GOSPODA. Kralj David zbog toga osjeti jednu ogromnu radost.",
			"10": "<sup>10</sup> David blagoslovi GOSPODA naočigled sveg sabora, govoreći: ” Blagoslovljen budi ti GOSPODE, Bože Izraelov, naš oče oduvijek i zauvijek.",
			"11": "<sup>11</sup> Tebi, GOSPODE, veličina, snaga; sjaj, slava i veličanstvo, jer sve na zemlji tvoje je. Tebi, GOSPODE, kraljevstvo i vlast nad svim bićima.",
			"12": "<sup>12</sup> Bogatstvo i slava dolaze od tebe, i ti si taj koji gospoduješ nad svim. U tvojoj ruci su moć i snaga; u tvojoj ruci, moć da sve uzdigneš i sve učvrstiš.",
			"13": "<sup>13</sup> A sada, Bože naš, mi ti iskazujemo zahvalnost i hvalimo *ime tvoje slave;",
			"14": "<sup>14</sup> jer tko sam ja i tko je narod moj da bismo imali moć ponuditi dobrovoljne darove kao što su ovi ovdje? Sve dolazi od tebe, i što smo ti mi ponudili dolazi iz tvoje ruke.",
			"15": "<sup>15</sup> Jer mi smo stranci pred tobom, gosti kao svi naši očevi; naši dani na zemlji su kao sjena, i bez nade.",
			"16": "<sup>16</sup> GOSPODE naš Bože sva ova množina stvari koje smo pripravili za izgraditi jednu Kuću za tvoje sveto ime, sve to dolazi iz tvoje ruke i tebi pripada.",
			"17": "<sup>17</sup> Ja znam, moj Bože, da ti ispituješ *srca i da ti prihvaćaš pravednost; za mene, to je u pravednosti mojeg srca da ja dragovoljno nudim sve ovo, i sada, tvoj narod koji se nalazi ovdje, ja ga taklođer vidim s radošću nuditi dragovoljne darove.",
			"18": "<sup>18</sup> GOSPODE, Bože Abrahama, Izaka i Izraela, naših otaca, čuvaj zauvijek naklonost srca svojeg naroda i usmjeravaj čvrsto njegovo srce k sebi.",
			"19": "<sup>19</sup> Mojem sinu Solomonu, daj jedno pošteno srce za čuvati tvoje zapovijedi, tvoje propise i tvoje zakone da sve izvrši i izgradi palaču koju sam ja pripravio.“ :",
			"20": "<sup>20</sup> Potom reče David svem saboru: ” Blagoslovite GOSPODA svojeg Boga.“ I sav sabor blagoslovi GOSPODA, Boga njihovih otaca. Oni se pokloniše i nakloniše pred GOSPODOM i pred kraljem.",
			"21": "<sup>21</sup> Sutradan od tog dana, oni zaklaše *žrtve GOSPODU i ponudiše mu holokauste: tisuću bikova, tisucu ovnova, tisuću janjaca s njiohopvim libacijama, i žrtava u izobilju za sav Izrael.",
			"22": "<sup>22</sup> Oni jedoše i piše u nazočnosti GOSPODOVOJ, tog dana, u jednoj velikoj radosti, i po drugi put :, oni proglasiše kralja Solomona, sina Davidova, i *pomazaše ga kao glavara za GOSPODA, i Sadoka kao svećenika.",
			"23": "<sup>23</sup> Solomon se posjednu na prijestol GOSPODOV, kao kralj na mjestu Davida svojeg oca, i on tu uznapredova.Sav mu Izrael bi pokoran.",
			"24": "<sup>24</sup> Svi glavari, svi smioni ljudi, i također svi sinovi Davidovi biše podvrgnuti kralju Solomonu.",
			"25": "<sup>25</sup> GOSPOD uzveliča veoma visoko Solomona u očima sveg Izraela, i dade mu slavu jednog kraljevstva kakve nije bilo prije njega u ni jednog kralja Izraelovog.“",
			"26": "<sup>26</sup> David, sin Jeseov, bijaše vladaše nad svim Izraelom.",
			"27": "<sup>27</sup> Trajanje njegove vladavine nad Izraelom bi 40 godina: on vladaše sedam godina u Hebronu i 33 godine u Jeruzalemu.",
			"28": "<sup>28</sup> On umrije u jednoj sretnoj starosti, nasićen danima, bogatstvima i slavom, a njegov sin Solomon, zavlada na njegovom mjestu.",
			"29": "<sup>29</sup> Djela kralja Davida, prva kao i posljednja, nalaze se zapisana u Djelima Samuela vidovnjaka, u onima proroka Natana i onima Gaadovim, vidovnjaka,",
			"30": "<sup>30</sup> kao i sva njegova vladavina i njegova moć, i kušnje koje se oboriše na njega, na Izrael i na sva kraljevstva zemalja."
		}
	}
};
module.exports = book;