var book = {
	"name": "Fyrri kroníkubók",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adam, Set, Enos,",
			"2": "<sup>2</sup> Kenan, Mahalalel, Jared,",
			"3": "<sup>3</sup> Henok, Metúsala, Lamek,",
			"4": "<sup>4</sup> Nói, Sem, Kam og Jafet.",
			"5": "<sup>5</sup> Synir Jafets voru Gómer og Magóg og Madaí og Javan og Túbal og Mesek og Tíras.",
			"6": "<sup>6</sup> Synir Gómers voru Askenas og Rífat og Tógarma.",
			"7": "<sup>7</sup> Synir Javans voru Elísa og Tarsis, Kittar og Ródanítar.",
			"8": "<sup>8</sup> Synir Kams voru Kús og Mísraím, Pút og Kanaan.",
			"9": "<sup>9</sup> Synir Kúss voru Seba og Havíla og Sabta og Raema og Sabteka. Og synir Raema voru Séba og Dedan.",
			"10": "<sup>10</sup> Og Kús gat Nimrod. Hann varð fyrsta hetjan á jörðinni.",
			"11": "<sup>11</sup> Og Mísraím gat Lúdíta og Anamíta og Lehabíta og Naftúbíta,",
			"12": "<sup>12</sup> og Patrúsíta og Kaslúkíta, sem Filistear eru komnir af, enn fremur Kaftóríta.",
			"13": "<sup>13</sup> Og Kanaan gat Sídon, frumburð sinn, og Het",
			"14": "<sup>14</sup> og Jebúsíta og Amoríta og Gírgasíta",
			"15": "<sup>15</sup> og Hevíta og Arkíta og Síníta",
			"16": "<sup>16</sup> og Arvadíta og Semaríta og Hamatíta.",
			"17": "<sup>17</sup> Synir Sems voru Elam og Assúr og Arpaksad og Lúd og Aram og Ús og Húl og Geter og Mas.",
			"18": "<sup>18</sup> Arpaksad gat Sela og Sela gat Eber",
			"19": "<sup>19</sup> og Eber fæddust tveir synir. Annar hét Peleg, því að á hans dögum var jörðinni skipt, en bróðir hans hét Joktan.",
			"20": "<sup>20</sup> Og Joktan gat Almódad og Selef og Hasarmavet og Jera",
			"21": "<sup>21</sup> og Hadóram og Úsal og Dikla",
			"22": "<sup>22</sup> og Ebal og Abímael og Séba",
			"23": "<sup>23</sup> og Ófír og Havíla og Jóbab. Allir þessir menn voru synir Joktans.",
			"24": "<sup>24</sup> Sem, Arpaksad, Selam,",
			"25": "<sup>25</sup> Eber, Peleg, Reú,",
			"26": "<sup>26</sup> Serúg, Nahor, Terak,",
			"27": "<sup>27</sup> Abram, það er Abraham.",
			"28": "<sup>28</sup> Synir Abrahams voru Ísak og Ísmael.",
			"29": "<sup>29</sup> Þetta er niðjatal þeirra: Nebajót var frumburður Ísmaels, þá Kedar og Adbeel og Míbsam",
			"30": "<sup>30</sup> og Misma og Dúma og Massa og Hadad og Tema",
			"31": "<sup>31</sup> og Jetúr og Nafis og Kedma. Þeir voru synir Ísmaels.",
			"32": "<sup>32</sup> Þetta eru synir Ketúru, hjákonu Abrahams. Hún ól Símran og Joksan og Medan og Midían og Jísbak og Súa. Og synir Joksans voru Séba og Dedan",
			"33": "<sup>33</sup> og synir Midíans: Efa og Efer og Hanok og Abída og Eldaa. Allir þessir menn voru synir Ketúru.",
			"34": "<sup>34</sup> Og Abraham gat Ísak. Synir Ísaks voru Esaú og Ísrael.",
			"35": "<sup>35</sup> Synir Esaú voru Elífas, Regúel og Jehús og Jaelam og Kóra.",
			"36": "<sup>36</sup> Synir Elífasar voru Teman og Ómar, Sefí og Gaetam, Kenas og Timna og Amalek.",
			"37": "<sup>37</sup> Synir Regúels voru Nahat, Sera, Samma og Missa.",
			"38": "<sup>38</sup> Og synir Seírs voru Lótan og Sóbal og Síbeon og Ana og Díson og Eser og Dísan.",
			"39": "<sup>39</sup> Og synir Lótans voru Hóri og Hómam og systir Lótans var Timna.",
			"40": "<sup>40</sup> Synir Sóbals voru Aljan og Manahat og Ebal, Sefí og Ónam. Og synir Síbeons voru Aja og Ana.",
			"41": "<sup>41</sup> Sonur Ana var Díson. Og synir Dísons voru Hamran og Esban og Jítran og Keran.",
			"42": "<sup>42</sup> Synir Esers voru Bilhan og Saavan og Jaakan. Synir Dísons voru Ús og Aran.",
			"43": "<sup>43</sup> Og þetta eru konungarnir sem ríktu í landi Edóms áður en konungar ríktu yfir Ísraelsmönnum: Bela Beórsson. Borg hans hét Dínhaba.",
			"44": "<sup>44</sup> Þegar Bela dó varð Jóbab Serason frá Borsa konungur eftir hann.",
			"45": "<sup>45</sup> Þegar Jóbab dó varð Húsam frá landi Temaníta konungur eftir hann.",
			"46": "<sup>46</sup> Þegar Húsam dó varð Hadad Bedadsson konungur eftir hann. Hann sigraði Midíaníta á Móabsvöllum. Borg hans hét Avít.",
			"47": "<sup>47</sup> Þegar Hadad dó varð Samla frá Masreka konungur eftir hann.",
			"48": "<sup>48</sup> Þegar Samla dó varð Sál frá Rehóbót við fljótið konungur eftir hann.",
			"49": "<sup>49</sup> Þegar Sál dó varð Baal Hanan Akborsson konungur eftir hann.",
			"50": "<sup>50</sup> Þegar Baal Hanan dó varð Hadad konungur eftir hann. Borg hans hét Pagí. Kona hans hét Mehetabeel, dóttir Matredar, dóttur Mesahabs.",
			"51": "<sup>51</sup> Þegar Hadad dó tóku ættarhöfðingjar völdin í Edóm: höfðingjarnir frá Timma, Alva, Jetet,",
			"52": "<sup>52</sup> Oholíbama, Ela, Pínon,",
			"53": "<sup>53</sup> Kenas, Teman, Mibsar,",
			"54": "<sup>54</sup> Magdíel og Íram. Þetta voru ættarhöfðingjar Edómíta."
		},
		"2": {
			"1": "<sup>1</sup> Synir Ísraels voru: Rúben, Símeon, Leví og Júda, Íssakar og Sebúlon,",
			"2": "<sup>2</sup> Dan, Jósef og Benjamín, Naftalí, Gað og Asser.",
			"3": "<sup>3</sup> Synir Júda voru Er og Ónan og Sela. Kanverska konan, dóttir Súa, ól honum þessa þrjá syni. En Er, frumburður Júda, var illur í augum Drottins svo að hann lét hann deyja.",
			"4": "<sup>4</sup> Og Tamar, tengdadóttir Júda, ól honum Peres og Serak. Júda átti alls fimm syni.",
			"5": "<sup>5</sup> Synir Peresar voru Hesrón og Hamúl.",
			"6": "<sup>6</sup> Synir Seraks voru Simrí og Etan og Heman og Kalkól og Dara, alls fimm.",
			"7": "<sup>7</sup> Og sonur Simrí var Karmí og sonur Karmí var Akar sem steypti Ísrael í ógæfu með því að stela því sem hafði verið helgað banni.",
			"8": "<sup>8</sup> Og sonur Etans var Asarja.",
			"9": "<sup>9</sup> Hesrón fæddust synirnir Jerahmeel og Ram og Kelúbaí.",
			"10": "<sup>10</sup> Og Ram gat Ammínadab og Ammínadab gat Nahson, höfðingja Júdamanna,",
			"11": "<sup>11</sup> og Nahson gat Salma og Salma gat Bóas",
			"12": "<sup>12</sup> og Bóas gat Óbeð og Óbeð gat Ísaí",
			"13": "<sup>13</sup> og Ísaí gat Elíab, frumburð sinn, og þá Abínadab og Símea hinn þriðja,",
			"14": "<sup>14</sup> Netaneel hinn fjórða, Raddaí hinn fimmta,",
			"15": "<sup>15</sup> Ósem hinn sjötta, Davíð hinn sjöunda.",
			"16": "<sup>16</sup> Og systur þeirra voru Serúja og Abígail. Og synir Serúju voru Abísaí og Jóab og Asahel, þeir voru þrír.",
			"17": "<sup>17</sup> Og Abígail ól Amasa og faðir Amasa var Jeter Ísmaelíti.",
			"18": "<sup>18</sup> Og Kaleb Hesrónsson gat dótturina Jeríót við Asúbu, konu sinni, og þetta voru synir hennar: Jeser og Sóbab og Ardon.",
			"19": "<sup>19</sup> En þegar Asúba var dáin tók Kaleb Efrat sér fyrir konu og hún ól honum Húr.",
			"20": "<sup>20</sup> Og Húr gat Úrí og Úrí gat Besaleel.",
			"21": "<sup>21</sup> Hesrón gekk síðan inn til dóttur Makírs, föður Gíleaðs. Hann hafði tekið hana sér fyrir konu þegar hann var sextíu ára. Hún ól honum Segúb",
			"22": "<sup>22</sup> og Segúb gat Jaír. Hann átti tuttugu og þrjár borgir í landi Gíleaðs",
			"23": "<sup>23</sup> en Gesúr og Aram tóku af þeim þorp Jaírs ásamt Kenat og þorpunum umhverfis, alls sextíu borgir. Allir þessir menn voru synir Makírs, föður Gíleaðs.",
			"24": "<sup>24</sup> Og eftir andlát Hesróns gekk Kaleb inn til Efrötu, konu Hesróns, föður hans. Og hún ól honum Asúr, föður Tekóa.",
			"25": "<sup>25</sup> Og synir Jerahmeels, frumburðar Hesróns, voru frumburðurinn Ram og Búna og Óren og Ósem og Ahía.",
			"26": "<sup>26</sup> Og Jerahmeel átti aðra konu sem hét Atara. Hún var móðir Ónams.",
			"27": "<sup>27</sup> Og synir Rams, frumburðar Jerahmeels, voru Maas og Jamín og Eker.",
			"28": "<sup>28</sup> Og synir Ónams voru Sammaí og Jada. Og synir Sammaí voru Nadab og Abísúr.",
			"29": "<sup>29</sup> Og kona Bísúrs hét Abíhaíl og hún fæddi honum Akban og Mólíd.",
			"30": "<sup>30</sup> Og synir Nadabs voru Seled og Appaín en Seled dó barnlaus.",
			"31": "<sup>31</sup> Sonur Appaíms var Jíseí og sonur Jíseí Sesan. Og sonur Sesans var Ahelaí.",
			"32": "<sup>32</sup> Og synir Jada, bróður Sammaí, voru Jeter og Jónatan en Jeter dó barnlaus.",
			"33": "<sup>33</sup> Og synir Jónatans voru Pelet og Sasa. Þetta voru synir Jerahmeels.",
			"34": "<sup>34</sup> Sesan átti enga syni, aðeins dætur. En Sesan átti egypskan þræl, Jarha að nafni.",
			"35": "<sup>35</sup> Sesan gaf Jarha, þræli sínum, dóttur sína fyrir konu og hún ól honum Attaí.",
			"36": "<sup>36</sup> Og Attaí gat Natan og Natan gat Sabat",
			"37": "<sup>37</sup> og Sabat gat Eflal og Eflal gat Óbeð",
			"38": "<sup>38</sup> og Óbeð gat Jehú og Jehú gat Asarja",
			"39": "<sup>39</sup> og Asarja gat Heles og Heles gat Eleasa",
			"40": "<sup>40</sup> og Eleasa gat Sísemaí og Sísemaí gat Sallúm",
			"41": "<sup>41</sup> og Sallúm gat Jekamja og Jekamja gat Elísama.",
			"42": "<sup>42</sup> Og synir Kalebs, bróður Jerahmeels, voru Mesa, frumburður hans, hann var faðir Sífs, og næstelsti sonur hans var Maresa, faðir Hebrons.",
			"43": "<sup>43</sup> Og synir Hebrons voru Kóra og Tappúa og Rekem og Sema.",
			"44": "<sup>44</sup> Og Sema gat Raham, föður Jorkeams, og Rekem gat Sammaí",
			"45": "<sup>45</sup> og sonur Sammaí var Maon og Maon var faðir Bet Súrs.",
			"46": "<sup>46</sup> Og Efa, hjákona Kalebs, ól Haran og Mósa og Gases. Og Haran gat Jehdaí.",
			"47": "<sup>47</sup> Og synir Jehdaí voru Regem og Jótam og Gesan og Pelet og Efa og Saaf.",
			"48": "<sup>48</sup> Og Maaka, hjákona Kalebs, ól Seber og Tírkana",
			"49": "<sup>49</sup> og hún ól einnig Saaf, föður Madmanna og Sefa, föður Makbena og Gíbea. Og Aksa var dóttir Kalebs.",
			"50": "<sup>50</sup> Þetta voru synir Kalebs. Synir Húrs, frumburðar Efrata, voru Sóbal, faðir Kirjat Jearíms,",
			"51": "<sup>51</sup> Salma, faðir Betlehems, Haref, faðir Bet Gaders.",
			"52": "<sup>52</sup> Og Sóbal, faðir Kirjat Jearíms, átti að sonum Reaja og helming Manaktíta",
			"53": "<sup>53</sup> og helming ættbálkanna frá Kirjat Jearím, Jítríta og Pútíta og Súmatíta og Misraíta. Sóreatítar og Estoalítar eru komnir af þeim.",
			"54": "<sup>54</sup> Synir Salma, föður Betlehems, voru Netófatítar, Atrót Bet Jóab og helmingur Manatíta, það er Sóreítar,",
			"55": "<sup>55</sup> ættir Sifríta, sem búa í Jabes, Tíreatíta, Símeatíta og Súkatíta. Þeir voru Kínítar, komnir frá Hammat, föður Rekabs ættar."
		},
		"3": {
			"1": "<sup>1</sup> Þetta eru synir Davíðs sem fæddust honum í Hebron: Ammón, frumburðurinn sem hann átti með Akínóam frá Jesreel, annar var Daníel, með Abígail frá Karmel,",
			"2": "<sup>2</sup> þriðji Absalon, sonur Maöku, dóttur Talmaí, konungs í Gesúr, fjórði Adónía, sonur Haggítar,",
			"3": "<sup>3</sup> fimmti Sefatja, með Abítal, sjötti Jitream, með Eglu, konu sinni.",
			"4": "<sup>4</sup> Honum fæddust sex synir í Hebron þar sem hann var konungur sjö ár og sex mánuði. Hann var konungur í Jerúsalem þrjátíu og þrjú ár",
			"5": "<sup>5</sup> og þetta eru synirnir sem fæddust honum í Jerúsalem: Símea og Sóbab og Natan og Salómon. Þessa fjóra átti hann með Batsúu Ammíelsdóttur.",
			"6": "<sup>6</sup> Og hann eignaðist enn fremur Jíbhar og Elísama og Elífelet",
			"7": "<sup>7</sup> og Nóga og Nefeg og Jafía",
			"8": "<sup>8</sup> og Elísama og Eljada og Elífelet, alls níu.",
			"9": "<sup>9</sup> Þetta voru allir synir Davíðs nema synir hjákvennanna og Tamar var systir þeirra.",
			"10": "<sup>10</sup> Og sonur Salómons var Rehabeam, sonur hans var Abía, sonur hans Asa, sonur hans Jósafat,",
			"11": "<sup>11</sup> sonur hans Jóram, sonur hans Ahasía, sonur hans Jóas,",
			"12": "<sup>12</sup> sonur hans Amasía, sonur hans Asaría, sonur hans Jótam,",
			"13": "<sup>13</sup> sonur hans Akas, sonur hans Hiskía, sonur hans Manasse,",
			"14": "<sup>14</sup> sonur hans Amón og sonur hans Jósía.",
			"15": "<sup>15</sup> Og synir Jósía voru Jóhanan, frumburðurinn, annar Jójakím, þriðji Sedekía, fjórði Sallúm.",
			"16": "<sup>16</sup> Og synir Jójakíms voru Jekonja, sonur hans, og Sedekía, sonur hans.",
			"17": "<sup>17</sup> Og synir Jekonja, sem fluttur var í útlegð, voru Sealtíel,",
			"18": "<sup>18</sup> Malkíram, Pedaja, Seneasser, Jekamja, Hósama og Nedabja.",
			"19": "<sup>19</sup> Og synir Pedaja voru Serúbabel og Símeí. Og synir Serúbabels voru Mesúllam og Hananja og Selómúí var systir þeirra.",
			"20": "<sup>20</sup> Synir Mesúllams voru Hasúba og Óhel og Berekía og Hasadja og Júsab Hesed, alls fimm.",
			"21": "<sup>21</sup> Og synir Hananja voru Pelatja og Jesaja og Refaja og Arnan og Óbadja og Sekanja.",
			"22": "<sup>22</sup> Og synir Sekanja voru Semaja og Hattúa og Jígeal og Baría og Nearja og Safat, alls sex.",
			"23": "<sup>23</sup> Og synir Nearja voru Eljóenaí og Hiskía og Asríkam, alls þrír.",
			"24": "<sup>24</sup> Og synir Eljóenaí voru Hódavja og Eljasíb og Pelaja og Akkúb og Jóhanan og Delaja og Ananí, alls sjö."
		},
		"4": {
			"1": "<sup>1</sup> Synir Júda voru Peres, Hesrón og Karmí og Húr og Sóbal.",
			"2": "<sup>2</sup> Og Reaja, sonur Sóbals, gat Jahat og Jahat gat Ahúmaí og Lahad. Þetta eru ættir Sóreatíta.",
			"3": "<sup>3</sup> Og þetta voru synir Etams: Jesreel og Jisma og Jídbas og Haselelpóní var systir þeirra.",
			"4": "<sup>4</sup> Og Penúel var faðir Gedórs og Eser faðir Húsa. Þetta voru synir Húrs, frumburðar Efrata, föður Betlehems.",
			"5": "<sup>5</sup> Og Ashúr, faðir Tekóa, átti tvær konur, Heleu og Naeru.",
			"6": "<sup>6</sup> Og Naera ól honum Ahússam og Hefer og Temní og Ahastaríta. Þeir voru synir Naeru.",
			"7": "<sup>7</sup> Synir Heleu voru Seret, Jísehar og Etnan.",
			"8": "<sup>8</sup> Og Kós gat Anúb og Sóbeba og ættir Aharhels, sonar Harúms.",
			"9": "<sup>9</sup> Og Jaebes var í mestum metum af bræðrum sínum. Móðir hans nefndi hann Jaebes því að hún sagði: „Ég ól hann með kvölum.“",
			"10": "<sup>10</sup> En Jaebes hrópaði til Guðs Ísraels og sagði: „Blessaðu mig. Auktu við land mitt. Hönd þín sé með mér og bægðu frá mér böli svo að ég þurfi ekki að líða kvalir.“ Guð veitti honum það sem hann bað um.",
			"11": "<sup>11</sup> Og Kelúb, bróðir Súha, gat Mehír. Hann var faðir Estóns",
			"12": "<sup>12</sup> og Estón gat Bet Rafa og Pasea og Tehinna, föður Nahasar. Þetta eru mennirnir frá Reka.",
			"13": "<sup>13</sup> Og synir Kenasar voru Otníel og Seraja. Og synir Otníels voru Hatat og Meontaí.",
			"14": "<sup>14</sup> Og Meontaí gat Ofra. Og Seraja gat Jóab, föður Ge Harasíms því að þeir voru smiðir.",
			"15": "<sup>15</sup> Og synir Kalebs Jefúnnesonar voru Ír og Ela og Naam. Og sonur Ela var Kenas.",
			"16": "<sup>16</sup> Og synir Jehaleels voru Síf og Sífa og Tirja og Asareel.",
			"17": "<sup>17</sup> Og synir Esra voru Jeter og Mered og Efer og Jalon. Jeter gat Mirjam og Sammaí og Jísba, föður Estemóa.",
			"18": "<sup>18</sup> Og Mered átti tvær konur, aðra frá Egyptalandi, hina frá Júda. Og egypska konan ól honum Jered, föður Gedors, og Heber, föður Sókó, og Jekútíel, föður Sanóa. Þetta voru synir Bitju, dóttur faraós, sem Mered hafði tekið sér fyrir konu,",
			"19": "<sup>19</sup> og synir hans með konu sinni frá Júda, systur Nahams, föður Kegílu, voru Garmíti og Maakíti Estemóa.",
			"20": "<sup>20</sup> Og synir Símonar voru Amnon og Rinna, Ben Hanan og Tílon. Og synir Jíseí voru Sóhet og Ben Sóhet.",
			"21": "<sup>21</sup> Synir Sela, sonar Júda, voru Er, faðir Leka, og Laeda, faðir Maresa, og ættir línvefaranna í vefstofunni í Asbea,",
			"22": "<sup>22</sup> einnig Jókím og mennirnir frá Kóseba og Jóas og Saraf sem höfðu farið með völd í Móab en sneru aftur til Betlehem eins og segir í gömlum frásögnum.",
			"23": "<sup>23</sup> Þeir voru leirkerasmiðir og bjuggu í Netaím og Gedera. Þeir unnu fyrir konunginn og bjuggu hjá honum.",
			"24": "<sup>24</sup> Synir Símeons voru Nemúel og Jamín og Jaríb og Sera og Sál.",
			"25": "<sup>25</sup> Sonur hans var Sallúm, sonur hans Mibsam og sonur hans Misma.",
			"26": "<sup>26</sup> Og synir Misma voru Hammúel, sonur hans, Sakkúr, sonur hans og Símeí, sonur hans.",
			"27": "<sup>27</sup> Símeí átti sextán syni og sex dætur. En bræður hans voru ekki barnmargir og allar ættir þeirra voru ekki jafnfjölmennar og Júdamenn.",
			"28": "<sup>28</sup> Þeir bjuggu í Beerseba og Mólada og Hasar Súal",
			"29": "<sup>29</sup> og Bílha og Esem og Tólad",
			"30": "<sup>30</sup> og Betúel og Harma og Siklag",
			"31": "<sup>31</sup> og Bet Markabót og Hasar Súsím og Bet Bíreí og Saaraím. Þetta voru borgir þeirra þar til Davíð varð konungur",
			"32": "<sup>32</sup> og þorp þeirra voru Etam og Aín, Rimmon og Tóken og Asan, fimm borgir",
			"33": "<sup>33</sup> og öll þau þorp, sem voru umhverfis, allt til Baal. Á þessum stöðum bjuggu þeir og þeir skráðu sína eigin ættartölu.",
			"34": "<sup>34</sup> Og Mesóbab og Jamlek og Jósa Amasjason",
			"35": "<sup>35</sup> og Jóel og Jehú Jósibjason, Serajasonar, Asíelssonar",
			"36": "<sup>36</sup> og Eljóenaí og Jaakóba og Jesóhaja og Asaja og Adíel og Jesímíel og Benaja",
			"37": "<sup>37</sup> og Sísa Sífeíson, Allonssonar, Jedajasonar, Simrísonar, Semajasonar.",
			"38": "<sup>38</sup> Þeir sem hér voru nefndir voru höfðingjar í ættum sínum og urðu ættir þeirra mjög fjölmennar.",
			"39": "<sup>39</sup> Þeir héldu því þaðan í áttina til Gedór, alveg austur fyrir dalinn, til að leita haglendis fyrir fénað sinn.",
			"40": "<sup>40</sup> Og þeir fundu grösugt og gott haglendi og landrými var þar mikið. Þar var kyrrt og friðsælt því að þar höfðu niðjar Kams búið áður.",
			"41": "<sup>41</sup> Á dögum Hiskía, konungs í Júda, komu þeir sem hér voru nefndir, eyddu tjöldum þeirra og drápu Meúníta sem þar voru fyrir og helguðu þá banni. Þeir hafa búið þar síðan því að þar var haglendi fyrir fénað þeirra.",
			"42": "<sup>42</sup> Og fimm hundruð af niðjum Símeons fóru til Seírfjalla og fóru fyrir þeim Pelatja og Nearja og Refaja og Ússíel, synir Jíseí.",
			"43": "<sup>43</sup> Þeir drápu síðustu leifar Amalekíta og þar hafa þeir búið allt til þessa dags."
		},
		"5": {
			"1": "<sup>1</sup> Rúben var frumburður Ísraels. Hann var frumburðurinn. En þar sem hann hafði vanhelgað hvílu föður síns var frumburðarrétturinn gefinn sonum Jósefs, sonar Ísraels. Hann var þó ekki skráður sem frumburður í ættartölum",
			"2": "<sup>2</sup> því að Júda varð voldugastur bræðra sinna og afkomandi hans varð þjóðhöfðingi þó að Jósef hlyti frumburðarréttinn.",
			"3": "<sup>3</sup> Synir Rúbens, frumburðar Ísraels, voru Henok og Pallú, Hesrón og Karmí.",
			"4": "<sup>4</sup> Synir Jóels voru Semaja, sonur hans, Góg, sonur hans, Símeí, sonur hans,",
			"5": "<sup>5</sup> Míka, sonur hans, Reaja, sonur hans, Baal, sonur hans, og",
			"6": "<sup>6</sup> Beera, sonur hans, sem Tílgat Pilneser Assýríukonungur flutti í útlegð. Hann var höfðingi niðja Rúbens.",
			"7": "<sup>7</sup> Þegar bræður hans létu skrá sig í ættartölur eftir ættum sínum töldust þeir til fjölskyldu hans. Fyrstur þeirra var Jeíel, þá Sakaría",
			"8": "<sup>8</sup> og Bela Asasson, Semasonar, Jóelssonar. Niðjar Rúbens bjuggu í Aróer og allt til Nebó og Baal Meon.",
			"9": "<sup>9</sup> Þeir bjuggu á svæði sem lá til austurs, allt að eyðimörkinni sem er vestur frá Efrat, því að þeir áttu miklar hjarðir í landi Gíleaðs.",
			"10": "<sup>10</sup> Á dögum Sáls áttu þeir í ófriði við Hagríta. Þegar þeir höfðu fallið þeim í hendur settust þeir að í tjöldum þeirra sem voru um allan austurhluta Gíleaðs.",
			"11": "<sup>11</sup> Synir Gaðs bjuggu andspænis þeim í Basanlandi, allt til Salka.",
			"12": "<sup>12</sup> Jóel var höfðinginn og Safam næstur honum, þá Jaenaí og Safat í Basan.",
			"13": "<sup>13</sup> Og bræður þeirra voru eftir fjölskyldum: Míkael og Mesúllam og Seba og Jóraí og Jaekan og Sía og Eber, alls sjö.",
			"14": "<sup>14</sup> Þeir voru synir Abíhaíls Húrísonar, Jaróasonar, Gíleaðssonar, Míkaelssonar, Jesísaísonar, Jahdósonar, Bússonar.",
			"15": "<sup>15</sup> Akí Abdíelsson, Gúnísonar var höfðingi fjölskyldna þeirra.",
			"16": "<sup>16</sup> Þeir bjuggu í Gíleað, í Basan og þorpunum umhverfis, og í öllum beitilöndum Sarons, svo langt sem þau náðu.",
			"17": "<sup>17</sup> Þeir voru allir skráðir í ættartölur á dögum Jótams Júdakonungs og á dögum Jeróbóams Ísraelskonungs.",
			"18": "<sup>18</sup> Þeir af sonum Rúbens og Gaðs og hálfum ættbálki Manasse, sem voru hermenn, báru skjöld og sverð og spenntu boga og voru þjálfaðir í hernaði. Þeir voru fjörutíu og fjögur þúsund sex hundruð og sjötíu vopnfærir menn.",
			"19": "<sup>19</sup> Þeir áttu í ófriði við Hagríta og Jetúr og Nafís og Nódab.",
			"20": "<sup>20</sup> Þeir hlutu hjálp og Hagrítar og allir bandamenn þeirra voru seldir þeim í hendur. Þar sem þeir höfðu hrópað til Guðs á hjálp á meðan bardaginn stóð bænheyrði hann þá af því að þeir treystu honum.",
			"21": "<sup>21</sup> Þeir tóku með sér hjarðir Hagríta sem herfang, fimmtíu þúsund úlfalda, tvö hundruð og fimmtíu þúsund sauðfjár og tvö þúsund asna. Einnig tóku þeir hundrað þúsund manns.",
			"22": "<sup>22</sup> Margir voru lagðir sverði og féllu því að bardaginn var háður að ráði Guðs. Synir Gaðs bjuggu síðan í landi þeirra allt til útlegðarinnar.",
			"23": "<sup>23</sup> Synir hálfs ættbálks Manasse voru margir og bjuggu í landinu sem nær frá Basan til Baal Hermon og til Seír og Hermonfjalls.",
			"24": "<sup>24</sup> Þetta voru höfðingjar ætta þeirra: Efer og Jíseí og Elíel og Asríel og Jeremía og Hódavja og Jahdíel. Þeir voru miklir kappar og nafnkunnir menn, höfðingjar í ættum sínum.",
			"25": "<sup>25</sup> En þeir sviku Guð feðra sinna og tóku fram hjá með guðum þjóðanna sem bjuggu í landinu og Guð hafði rutt úr vegi fyrir þeim.",
			"26": "<sup>26</sup> Þá blés Guð Ísraels því í brjóst Púl Assýríukonungi, það er Tílgat Pilneser, að flytja niðja Rúbens, Gaðs og hálfan ættbálk Manasse í útlegð. Hann flutti þá til Hala og Habór og Hara og Gósanfljóts. Þar hafa þeir verið allt til þessa dags.",
			"27": "<sup>27</sup> Synir Leví voru Gersom, Kahat og Merarí.",
			"28": "<sup>28</sup> Og synir Kahats voru Amram, Jísehar og Hebron og Ússíel.",
			"29": "<sup>29</sup> Og börn Amrams voru Aron og Móse og Mirjam. Og synir Arons voru Nadab og Abíhú, Eleasar og Ítamar.",
			"30": "<sup>30</sup> Eleasar gat Pínehas, Pínehas gat Abísúa,",
			"31": "<sup>31</sup> Abísúa gat Búkkí og Búkkí gat Ússí",
			"32": "<sup>32</sup> og Ússí gat Serahja og Serahja gat Merajót",
			"33": "<sup>33</sup> og Merajót gat Amarja og Amarja gat Ahítúb",
			"34": "<sup>34</sup> og Ahítúb gat Sadók og Sadók gat Akímaas",
			"35": "<sup>35</sup> og Akímaas gat Asarja og Asarja gat Jóhanan",
			"36": "<sup>36</sup> og Jóhanan gat Asarja. Hann gegndi prestsþjónustu í musterinu sem Salómon reisti í Jerúsalem.",
			"37": "<sup>37</sup> Og Asarja gat Amarja og Amarja gat Ahítúb",
			"38": "<sup>38</sup> og Ahítúb gat Sadók og Sadók gat Sallúm",
			"39": "<sup>39</sup> og Sallúm gat Hilkía og Hilkía gat Asarja",
			"40": "<sup>40</sup> og Asarja gat Seraja og Seraja gat Jósadak.",
			"41": "<sup>41</sup> Og Jósadak fór í útlegð þegar Drottinn lét Nebúkadnesar flytja íbúana í Júda og Jerúsalem í útlegð."
		},
		"6": {
			"1": "<sup>1</sup> Synir Leví voru Gersom, Kahat og Merarí.",
			"2": "<sup>2</sup> Og þetta eru nöfn sona Gersoms: Lební og Símeí.",
			"3": "<sup>3</sup> Og synir Kahats voru Amram og Jísehar og Hebron og Ússíel.",
			"4": "<sup>4</sup> Synir Merarí voru Mahelí og Músí. Þetta eru ættir Levíta, taldar frá ættfeðrum þeirra.",
			"5": "<sup>5</sup> Frá Gersom: Libní, sonur hans, Jahat, sonur hans, Simma, sonur hans,",
			"6": "<sup>6</sup> Jóa, sonur hans, Íddó, sonur hans, Sera, sonur hans, og Jeatraí, sonur hans.",
			"7": "<sup>7</sup> Synir Kahats voru Ammínadab, sonur hans, Kóra, sonur hans, Assír, sonur hans,",
			"8": "<sup>8</sup> Elkana, sonur hans, Ebjasaf, sonur hans, Assír, sonur hans,",
			"9": "<sup>9</sup> Tahat, sonur hans, Úríel, sonur hans, Ússía, sonur hans, og Sál, sonur hans.",
			"10": "<sup>10</sup> Og synir Elkana voru Amasaí og Ahímót,",
			"11": "<sup>11</sup> Elkana, sonur hans, Sofaí, sonur hans, Nahat, sonur hans,",
			"12": "<sup>12</sup> Elíab, sonur hans, Jeróham, sonur hans, Elkana, sonur hans, og Samúel, sonur hans.",
			"13": "<sup>13</sup> Og synir Samúels voru Jóel, frumburðurinn, og Abía, næstur honum.",
			"14": "<sup>14</sup> Synir Merarí voru Mahlí, Libní, sonur hans, Simeí, sonur hans, Ússa, sonur hans,",
			"15": "<sup>15</sup> Símea, sonur hans, Haggía, sonur hans, og Asaja, sonur hans.",
			"16": "<sup>16</sup> Þetta eru þeir menn sem Davíð setti til að stjórna söng í húsi Drottins þegar örkinni hafði verið búinn þar staður.",
			"17": "<sup>17</sup> Þeir höfðu gegnt þjónustu með söng fyrir framan bústað Drottins í samfundatjaldinu þar til Salómon reisti Drottni hús í Jerúsalem. Gegndu þeir þjónustu sinni samkvæmt þeim reglum sem þeim voru settar.",
			"18": "<sup>18</sup> Þeir sem gegndu þessari þjónustu ásamt sonum sínum voru: Af sonum Kahatíta: söngvarinn Heman Jóelsson, Samúelssonar,",
			"19": "<sup>19</sup> Elkanasonar, Jeróhamssonar, Elíelssonar, Tóasonar,",
			"20": "<sup>20</sup> Súfssonar, Elkanasonar, Mahatssonar, Amasaísonar,",
			"21": "<sup>21</sup> Elkanasonar, Jóelssonar, Asaríasonar, Sefaníasonar,",
			"22": "<sup>22</sup> Tahatssonar, Assírssonar, Ebjasafssonar, Kórasonar,",
			"23": "<sup>23</sup> Jíseharssonar, Kahatssonar, Levísonar, Ísraelssonar.",
			"24": "<sup>24</sup> Bróðir hans var Asaf sem stóð honum til hægri handar, Asaf Berekíason, Símeasonar,",
			"25": "<sup>25</sup> Míkaelssonar, Maasejasonar, Malkíasonar,",
			"26": "<sup>26</sup> Etnísonar, Serasonar, Adajasonar,",
			"27": "<sup>27</sup> Etanssonar, Simmasonar, Símmeísonar,",
			"28": "<sup>28</sup> Jahatssonar, Gersomssonar, Levísonar.",
			"29": "<sup>29</sup> Af sonum Merarí, bræðrum þeirra, stóðu honum til vinstri handar: Etan Kísíson, Abdísonar, Mallúkssonar,",
			"30": "<sup>30</sup> Hasabjasonar, Amasjasonar, Hilkíasonar,",
			"31": "<sup>31</sup> Amsísonar, Banísonar, Semerssonar,",
			"32": "<sup>32</sup> Mahelísonar, Músísonar, Merarísonar, Levísonar.",
			"33": "<sup>33</sup> Og bræðrum þeirra, Levítunum, var ætluð öll þjónusta í bústað Drottins í húsi Guðs.",
			"34": "<sup>34</sup> En Aron og synir hans færðu fórnir á brennifórnaraltarinu og reykelsisaltarinu. Þeir gegndu allri þjónustu í hinu allra helgasta og friðþægðu fyrir Ísrael að öllu leyti eins og Móse, þjónn Guðs, hafði lagt fyrir.",
			"35": "<sup>35</sup> Þetta voru synir Arons: sonur hans Eleasar, sonur hans Pínehas, sonur hans Abísúa,",
			"36": "<sup>36</sup> sonur hans Búkkí, sonur hans Ússí, sonur hans Serahja,",
			"37": "<sup>37</sup> sonur hans Merajót, sonur hans Amaría, sonur hans Ahítúb,",
			"38": "<sup>38</sup> sonur hans Sadók og sonur hans Akímaas.",
			"39": "<sup>39</sup> Dvalarstaðir Levíta, taldir eftir afgirtum tjaldbúðum á landsvæði þeirra, voru: Sonum Arons af ætt Kahatíta fengu þeir Hebron í landi Júda ásamt beitilandinu umhverfis því að fyrsti hluturinn féll á þá.",
			"41": "<sup>41</sup> En akurland borgarinnar ásamt þorpunum, sem henni heyrðu til, fengu þeir Kaleb Jefúnnesyni.",
			"42": "<sup>42</sup> Þeir fengu sonum Arons griðaborgina Hebron, enn fremur Líbna ásamt beitilöndum, Jattír og Estamóa ásamt beitilöndum",
			"43": "<sup>43</sup> og Hólon ásamt beitilöndum og Debír ásamt beitilöndum",
			"44": "<sup>44</sup> og Asan ásamt beitilöndum og Bet Semes ásamt beitilöndum.",
			"45": "<sup>45</sup> Frá ættbálki Benjamíns fengu þeir þeim Geba ásamt beitilöndum og Allemet ásamt beitilöndum og Anatót ásamt beitilöndum. Alls voru borgir þeirra þrettán ásamt beitilöndum.",
			"46": "<sup>46</sup> Aðrar ættir sona Kahats hlutu tíu borgir með hlutkesti frá ættum Efraímsættbálks og Dansættbálks og hálfum Manasseættbálki.",
			"47": "<sup>47</sup> Ættir sona Gersoms hlutu þrettán borgir með hlutkesti frá ættbálki Íssakars og ættbálki Assers og ættbálki Naftalí og hálfum ættbálki Manasse í Basan.",
			"48": "<sup>48</sup> Ættir sona Merarí hlutu tólf borgir með hlutkesti frá ættbálki Rúbens og ættbálki Gaðs og ættbálki Sebúlons.",
			"49": "<sup>49</sup> Ísraelsmenn fengu Levítunum þessar borgir ásamt beitilöndum þeirra.",
			"50": "<sup>50</sup> Þeir létu þær af hendi með hlutkesti. Synir Arons fengu þessar borgir, sem nefndar hafa verið, með hlutkesti frá ættbálki sona Júda og ættbálki sona Símeons og ættbálki sona Benjamíns.",
			"51": "<sup>51</sup> Ættir þeirra sona Kahats, sem voru Levítar, fengu þessar borgir frá ættbálki Efraíms:",
			"52": "<sup>52</sup> Þeim var fengin griðaborgin Síkem ásamt beitilöndum hennar á Efraímsfjöllum og Geser ásamt beitilöndum",
			"53": "<sup>53</sup> og Jokmeam ásamt beitilöndum og Bet Hóron ásamt beitilöndum",
			"54": "<sup>54</sup> og Ajalon ásamt beitilöndum og Gat Rimmon ásamt beitilöndum.",
			"55": "<sup>55</sup> Og frá hálfum ættbálki Manasse fengu þeir Aner ásamt beitilöndum og Jíbleam ásamt beitilöndum. Þessar borgir hlutu ættir annarra sona Kahats.",
			"56": "<sup>56</sup> Synir Gersoms fengu frá ættunum í hálfum Manasseættbálki: Gólan í Basan ásamt beitilöndum og Astarót ásamt beitilöndum.",
			"57": "<sup>57</sup> Og frá ættbálki Íssakars fengu þeir Kedes ásamt beitilöndum og Dabrat ásamt beitilöndum",
			"58": "<sup>58</sup> og Ramót ásamt beitilöndum og Anem ásamt beitilöndum.",
			"59": "<sup>59</sup> Og frá ættbálki Assers fengu þeir Masal ásamt beitilöndum og Abdón ásamt beitilöndum",
			"60": "<sup>60</sup> og Húkok ásamt beitilöndum og Rehób ásamt beitilöndum.",
			"61": "<sup>61</sup> Og frá ættbálki Naftalí fengu þeir Kedes í Galíleu ásamt beitilöndum og Hammót ásamt beitilöndum og Kirjataím ásamt beitilöndum.",
			"62": "<sup>62</sup> Synir Merarí, sem enn voru eftir, fengu þessar borgir frá ættbálki Sebúlons: Rimmónó ásamt beitilöndum og Tabór ásamt beitilöndum.",
			"63": "<sup>63</sup> Frá ættbálki Rúbens fengu þeir Beser í eyðimörkinni ásamt beitilöndum, Jahsa ásamt beitilöndum,",
			"64": "<sup>64</sup> Kedemót ásamt beitilöndum og Mefaat ásamt beitilöndum í landinu handan við Jórdan.",
			"65": "<sup>65</sup> Og frá ættbálki Gaðs fengu þeir Ramót í Gíleað ásamt beitilöndum, Mahanaím ásamt beitilöndum,",
			"66": "<sup>66</sup> Hesbon ásamt beitilöndum og Jaser ásamt beitilöndum."
		},
		"7": {
			"1": "<sup>1</sup> Og synir Íssakars voru Tóla og Púa og Jasúb og Simron, fjórir að tölu.",
			"2": "<sup>2</sup> Og synir Tóla voru Ússí og Refaja og Jeríel og Jahemaí og Jíbsam og Samúel. Þeir voru höfðingjar yfir ættkvíslum Tóla, hraustir hermenn. Samkvæmt ættartölum þeirra voru þeir tuttugu og tvö þúsund og sex hundruð á dögum Davíðs.",
			"3": "<sup>3</sup> Og synir Ússí voru Jísrahja og Míkael og Óbadía og Jóel og Jissía, fimm að tölu. Þeir voru allir höfðingjar.",
			"4": "<sup>4</sup> Og til þeirra töldust eftir ættum þeirra, eftir frændliði þeirra, hermannasveitir, þrjátíu og sex þúsund manns, því að þeir áttu margar konur og börn.",
			"5": "<sup>5</sup> Bræður þeirra úr öllum ættkvíslum Íssakars voru hraustir hermenn. Þeir voru alls áttatíu og sjö þúsund þegar þeir voru skráðir í ættartölur.",
			"6": "<sup>6</sup> Synir Benjamíns voru Bela og Beker og Jedíael, þrír að tölu.",
			"7": "<sup>7</sup> Og synir Bela voru Esbón og Ússí og Ússíel og Jerímót og Írí, fimm að tölu. Þeir voru höfðingjar ættkvísla sinna, hraustir hermenn. Þeir voru tuttugu og tvö þúsund þrjátíu og fjórir þegar þeir voru skráðir í ættartölur.",
			"8": "<sup>8</sup> Og synir Bekers voru Semíra og Jóas og Elíeser og Eljóenaí og Omrí og Jeremót og Abía og Anatót og Alemet. Þetta voru allir synir Bekers.",
			"9": "<sup>9</sup> Þegar þeir voru skráðir í ættartölur sínar eftir höfðingjum ættkvísla sinna voru þeir tuttugu þúsund og tvö hundruð, hraustir hermenn.",
			"10": "<sup>10</sup> Og sonur Jedíaels var Bílhan og synir Bílhans voru Jeús og Benjamín og Ehúð og Kenaana og Setan og Tarsis og Ahísahar.",
			"11": "<sup>11</sup> Þetta voru allir synir Jedíaels. Þeir voru höfðingjar ættkvísla sinna, hraustir hermenn, sautján þúsund og tvö hundruð, sem fylktu liði til orrustu.",
			"12": "<sup>12</sup> Og Súppím og Húppím voru synir Írs og Húsím sonur Akers.",
			"13": "<sup>13</sup> Synir Naftalí voru Jahsíel og Gúní og Jeser og Sallúm, synir Bílu.",
			"14": "<sup>14</sup> Sonur Manasse var Asríel sem arameísk hjákona hans ól. Hún ól einnig Makír, föður Gíleaðs.",
			"15": "<sup>15</sup> Og Makír tók konu handa Húppím og Súppím. Og systir hans hét Maaka og næstelsti sonur hans Selófhað. Og Selófhað átti aðeins dætur.",
			"16": "<sup>16</sup> Og Maaka, kona Makírs, ól son og nefndi hann Peres. En bróðir hans hét Seres og synir hans voru Úlam og Rekem.",
			"17": "<sup>17</sup> Og sonur Úlams var Bedan. Þetta voru synir Gíleaðs Makírssonar, Manassesonar.",
			"18": "<sup>18</sup> Og Móleket, systir hans, ól Íshód og Abíeser og Mahela.",
			"19": "<sup>19</sup> Og synir Semída voru Ahjan og Sekem og Líkhí og Aníam.",
			"20": "<sup>20</sup> Og sonur Efraíms var Sútela og sonur hans var Bered og sonur hans Tahat og sonur hans Eleada og sonur hans Tahat",
			"21": "<sup>21</sup> og sonur hans Sóbad og synir hans Sútela, Eser og Elead. Menn frá Gat, sem fæddir voru í landinu, drápu þá af því að þeir höfðu farið suður eftir til að ræna búpeningi þeirra.",
			"22": "<sup>22</sup> Efraím, faðir þeirra, syrgði þá lengi og bræður hans komu til að hugga hann.",
			"23": "<sup>23</sup> Síðan gekk hann inn til konu sinnar og hún varð þunguð og ól son. Hann nefndi hann Bería því að ógæfa hafði hent heimili hans.",
			"24": "<sup>24</sup> Og dóttir hans var Seera. Hún byggði bæði neðra og efra Bet Hóron og Ússen Seera.",
			"25": "<sup>25</sup> Og synir hans voru Refa og Resef og sonur hans var Telak og sonur hans var Tahan,",
			"26": "<sup>26</sup> Laedan var sonur hans, sonur hans Ammíhúd, sonur hans Elísama,",
			"27": "<sup>27</sup> sonur hans Nún og sonur hans Jósúa.",
			"28": "<sup>28</sup> Eignarlönd þeirra og bústaðir voru Betel ásamt þorpum hennar austur að Naaran og vestur að Geser ásamt þorpum hennar, enn fremur Síkem og þorp hennar til Aja og þorpa hennar.",
			"29": "<sup>29</sup> Í höndum sona Manasse voru Bet Sean ásamt þorpunum umhverfis, Taana ásamt þorpunum umhverfis, Megiddó ásamt þorpunum umhverfis og Dór ásamt þorpunum umhverfis. Í þessum borgum bjuggu synir Jósefs, sonar Ísraels.",
			"30": "<sup>30</sup> Synir Assers voru Jímna og Jísva og Jísví og Bería og systir þeirra var Seera.",
			"31": "<sup>31</sup> Og synir Bería voru Heber og Malkíel. Hann var faðir Birsaíts.",
			"32": "<sup>32</sup> Og Heber gat Jaflet og Semer og Hótam og Súu, systur þeirra.",
			"33": "<sup>33</sup> Og synir Jaflets voru Pasak og Bímhal og Asvat. Þetta voru synir Jaflets.",
			"34": "<sup>34</sup> Og synir Semers, bróður hans, voru Róhga og Húbba og Aram.",
			"35": "<sup>35</sup> Og synir Helems, bróður hans, voru Sófa og Jímna og Seles og Amal.",
			"36": "<sup>36</sup> Og synir Sófa voru Súa og Harnefer og Súal og Berí og Jímra",
			"37": "<sup>37</sup> og Beser og Hód og Samma og Silsa og Jítran og Beera.",
			"38": "<sup>38</sup> Og synir Jeters voru Jefúnne og Pispa og Ara.",
			"39": "<sup>39</sup> Og synir Úlla voru Ara og Hanníel og Risja.",
			"40": "<sup>40</sup> Þetta voru allt synir Assers. Þeir voru höfðingjar yfir ættkvíslum, afburðamenn, fyrirmenn meðal höfðingja. Töldust þeir er skráðir voru til herþjónustu tuttugu og sex þúsund menn."
		},
		"8": {
			"1": "<sup>1</sup> Og Benjamín gat Bela, frumburð sinn, Asbel var annar, þriðji Ahra,",
			"2": "<sup>2</sup> fjórði Nóha og fimmti Rafa.",
			"3": "<sup>3</sup> Og Bela átti syni: Addar og Gera, föður Ehúðs,",
			"4": "<sup>4</sup> og Abísúa og Naaman og Ahóa",
			"5": "<sup>5</sup> og Gera og Sefúfan og Húram.",
			"6": "<sup>6</sup> Þetta voru synir Ehúðs. Þeir voru höfðingjar yfir ættkvíslum íbúanna í Geba og þeir voru fluttir í útlegð til Manahat.",
			"7": "<sup>7</sup> En Naaman og Ahía voru fluttir í útlegð af Gera. Og Gera gat Ússa og Ahíhúð.",
			"8": "<sup>8</sup> Saharaím gat syni á Móabsvöllum eftir að hann hafði skilið við konur sínar, Húsím og Baöru.",
			"9": "<sup>9</sup> Við konu sinni, Hódes, gat hann Jóbab og Síbja og Mesa og Malkam",
			"10": "<sup>10</sup> og Jeús og Sokja og Mirma. Þetta voru synir hans, ættarhöfðingjar.",
			"11": "<sup>11</sup> Og við Húsím gat hann Abítúb og Elpaal.",
			"12": "<sup>12</sup> Og synir Elpaals voru Eber og Míseam og Semer. Hann byggði Ónó og Lód ásamt dótturborgum þeirra.",
			"13": "<sup>13</sup> Og Bería og Sema voru höfðingjar yfir ættkvíslum íbúanna í Ajalon. Þeir hröktu íbúana í Gat í burtu.",
			"14": "<sup>14</sup> Og bræður þeirra voru Elpaal og Sasak og Jeremót.",
			"15": "<sup>15</sup> Sebadja og Arad og Eder",
			"16": "<sup>16</sup> og Míkael og Jispa og Jóha voru synir Bería.",
			"17": "<sup>17</sup> Og Sebadja og Mesúllam og Hiskí og Heber",
			"18": "<sup>18</sup> og Jísmeraí og Jíslía og Jóbab voru synir Elpaals.",
			"19": "<sup>19</sup> Og Jakím og Sikrí og Sabdí",
			"20": "<sup>20</sup> og Elíenaí og Silletaí og Elíel",
			"21": "<sup>21</sup> og Adaja og Beraja og Simrat voru synir Símeí.",
			"22": "<sup>22</sup> Og Jíspan og Eber og Elíel",
			"23": "<sup>23</sup> og Abdón og Síkrí og Hanan",
			"24": "<sup>24</sup> og Hananja og Elam og Antótía",
			"25": "<sup>25</sup> og Jífdeja og Penúel voru synir Sasaks.",
			"26": "<sup>26</sup> Og Samseraí og Seharja og Atalja",
			"27": "<sup>27</sup> og Jaaresja og Elía og Síkrí voru synir Jeróhams.",
			"28": "<sup>28</sup> Þessir menn voru ættarhöfðingjar og höfðingjar í ættum sínum samkvæmt ættartölum. Þeir bjuggu í Jerúsalem.",
			"29": "<sup>29</sup> Faðir Gíbeons, Jegúel, bjó í Gíbeon. Kona hans hét Maaka.",
			"30": "<sup>30</sup> Frumgetinn sonur hans var Abdón, þá Súr og Kís og Baal og Ner og Nadab",
			"31": "<sup>31</sup> og Gedór og Ahjó og Seker.",
			"32": "<sup>32</sup> Og Míklót gat Símea. Þeir settust einnig að í Jerúsalem eins og bræður þeirra og bjuggu hjá bræðrum sínum.",
			"33": "<sup>33</sup> Og Ner gat Kís og Kís gat Sál og Sál gat Jónatan og Malkísúa og Abínadab og Esbaal.",
			"34": "<sup>34</sup> Og sonur Jónatans var Meríbaal og Meríbaal gat Míka.",
			"35": "<sup>35</sup> Og synir Míka voru Píton og Melek og Tarea og Akas.",
			"36": "<sup>36</sup> Og Akas gat Jóadda og Jóadda gat Alemet og Asmavet og Simrí. Og Simrí gat Mósa",
			"37": "<sup>37</sup> og Mósa gat Bínea. Sonur hans var Rafa og sonur hans Eleasa og sonur hans Asel.",
			"38": "<sup>38</sup> Og Asel átti sex syni. Þeir hétu Asríkam, Bokrú og Ísmael og Searja og Óbadía og Hanan. Þetta voru allir synir Asels.",
			"39": "<sup>39</sup> Og synir Eseks, bróður hans, voru Úlam, frumburðurinn, Jeús, næstur honum, og Elífelet hinn þriðji.",
			"40": "<sup>40</sup> Og synir Úlams voru hraustir hermenn, bogaskyttur. Þeir áttu marga syni og sonarsyni, hundrað og fimmtíu alls. Þetta voru allt synir Benjamíns."
		},
		"9": {
			"1": "<sup>1</sup> Þannig voru allir Ísraelsmenn skráðir í ættartölur. Þeir voru skráðir í bók Ísraelskonunga. Júdamenn voru fluttir í útlegð til Babel vegna svika þeirra.",
			"2": "<sup>2</sup> Þeir fyrstu, sem settust að í landi þeirra og borgum, voru Ísraelsmenn, prestar, Levítar og musterisþjónar.",
			"3": "<sup>3</sup> Og í Jerúsalem bjuggu eftirtaldir af niðjum Júda og Benjamíns og Efraíms og Manasse:",
			"4": "<sup>4</sup> Útaí Ammíhúdsson, Omrísonar, Imrísonar, Banísonar. Hann var afkomandi Peresar Júdasonar.",
			"5": "<sup>5</sup> Og af Sílonítum: frumburðurinn Asaja og synir hans.",
			"6": "<sup>6</sup> Og af niðjum Sera: Jegúel. Og bræður þeirra voru sex hundruð og níutíu.",
			"7": "<sup>7</sup> Og af niðjum Benjamíns: Sallúm Mesúllamsson, Hódavjasonar, Semíasonar.",
			"8": "<sup>8</sup> Og Jíbneja Jeróhamsson og Ela Ússíson, Míkrísonar og Mesúllam Sefatjason, Regúelssonar, Jíbnejasonar.",
			"9": "<sup>9</sup> Og bræður þeirra eftir ættartölum þeirra voru níu hundruð fimmtíu og sex. Þeir voru allir ættarhöfðingjar.",
			"10": "<sup>10</sup> Og af prestunum: Jedaja og Jójaríb og Jakín",
			"11": "<sup>11</sup> og Asarja Hilkíason, Mesúllamssonar, Sadókssonar, Merajótssonar, Ahítúbssonar, höfðingja yfir húsi Guðs.",
			"12": "<sup>12</sup> Og Adaja Jeróhamsson, Pashúrssonar, Malkíasonar, og Maesí Adíelsson, Jahserasonar, Mesúllamssonar, Mesillemítssonar, Immerssonar.",
			"13": "<sup>13</sup> Og bræður þeirra, ættarhöfðingjar, voru sautján hundruð og sextíu talsins. Þeir voru mjög hæfir menn til að gegna þjónustu í húsi Guðs.",
			"14": "<sup>14</sup> Og af Levítunum: Semaja Hassúbsson, Asríkamssonar, Hasabjasonar af niðjum Merarí.",
			"15": "<sup>15</sup> Og Bakbakkar, Heres og Galal og Mattanja Míkason, Síkrísonar, Asafssonar",
			"16": "<sup>16</sup> og Óbadía Semajason, Galalssonar, Jedútúnssonar og Berekía Asason, Elkanasonar sem bjó í þorpum Netófatíta.",
			"17": "<sup>17</sup> Og hliðverðirnir voru Sallúm og Akkúb og Talmon og Ahíman og bræður þeirra. Var Sallúm, bróðir þeirra, æðstur",
			"18": "<sup>18</sup> og gætir enn konungshliðsins gegnt austri. Þetta voru hliðverðirnir í búðum Levíta.",
			"19": "<sup>19</sup> Og Sallúm Kóreson, Ebjasafssonar, Kórasonar og bræður hans, af ættkvísl hans, Kóraítar, voru þröskuldsverðir í tjaldinu. Feður þeirra höfðu gætt dyranna í herbúðum Drottins.",
			"20": "<sup>20</sup> Og Pínehas Eleasarsson var áður yfirmaður þeirra, Drottinn sé með honum.",
			"21": "<sup>21</sup> Og Sakaría Meselemjason gætti dyra samfundatjaldsins.",
			"22": "<sup>22</sup> Alls voru þeir sem valdir höfðu verið til að vera hliðverðir við þröskuldana tvö hundruð og tólf. Þeir höfðu látið skrá sig í ættartölur í þorpum sínum. Davíð og sjáandinn Samúel höfðu sett þá í embætti vegna þess að þeim var treystandi.",
			"23": "<sup>23</sup> Þeir og synir þeirra gættu hliðanna að húsi Drottins, að húsi tjaldbúðarinnar.",
			"24": "<sup>24</sup> Hliðverðirnir sneru í átt að heimshornunum fjórum: austri, vestri, norðri og suðri.",
			"25": "<sup>25</sup> Bræður þeirra, sem bjuggu í þorpum sínum, áttu að koma við og við og gegna þjónustunni með þeim, sjö daga í senn,",
			"26": "<sup>26</sup> þar sem aðeins fjórir æðstu hliðverðirnir höfðu fast starf á hendi. Þeir voru Levítar. Þeir höfðu og umsjón með herbergjum og fjárhirslum í húsi Guðs.",
			"27": "<sup>27</sup> Um nætur héldu þeir til við hús Guðs því að þeim var falin gæsla og höfðu lykil til að ljúka upp á hverjum morgni.",
			"28": "<sup>28</sup> Nokkrir þeirra áttu að sjá um þjónustuáhöldin og telja þau er þeir báru þau út og inn.",
			"29": "<sup>29</sup> Nokkrir skyldu sjá um áhöldin, öll hin helgu áhöld, hveitimjölið, vínið og olíuna, reykelsið og kryddjurtirnar.",
			"30": "<sup>30</sup> Nokkrir af sonum prestanna áttu að gera smyrsl úr kryddjurtunum.",
			"31": "<sup>31</sup> En Mattitja, einum af Levítunum, frumburði Sallúms Kóraíta, var falinn pönnubaksturinn.",
			"32": "<sup>32</sup> Nokkrir af Kahatítum, bræðrum þeirra, áttu að sjá um skoðunarbrauðin og leggja þau fram á hverjum hvíldardegi.",
			"33": "<sup>33</sup> Söngvararnir, ættarhöfðingjar Levíta, búa í herbergjunum, lausir við önnur störf því að þeir hafa starfs að gæta dag og nótt.",
			"34": "<sup>34</sup> Þetta voru ættarhöfðingjar Levíta og höfðingjar ætta þeirra. Þeir bjuggu í Jerúsalem.",
			"35": "<sup>35</sup> Faðir Gíbeons, Jeíel, bjó í Gíbeon. Kona hans hét Maaka",
			"36": "<sup>36</sup> og frumgetinn sonur hans var Abdón, þá Súr og Kís og Baal og Ner og Nadab",
			"37": "<sup>37</sup> og Gedór og Ahjó og Sakaría og Miklót.",
			"38": "<sup>38</sup> Og Miklót gat Símeam. Þeir settust einnig að í Jerúsalem eins og bræður þeirra og bjuggu hjá þeim.",
			"39": "<sup>39</sup> Og Ner gat Kís og Kís gat Sál og Sál gat Jónatan og Malkísúa og Abínadab og Esbaal.",
			"40": "<sup>40</sup> Og sonur Jónatans var Meríbaal og Meríbaal gat Míka.",
			"41": "<sup>41</sup> Og synir Míka voru Píton og Melek og Tahrea og Akas.",
			"42": "<sup>42</sup> Og Akas gat Jaera og Jaera gat Alemet og Asmavet og Simrí. Og Simrí gat Mósa",
			"43": "<sup>43</sup> og Mósa gat Bínea. Sonur hans var Refaja og sonur hans Elasa og sonur hans Asel.",
			"44": "<sup>44</sup> Og Asel átti sex syni og hétu þeir: Asríkam og Bokrú og Ísmael og Searja og Óbadía og Hanan. Þetta voru synir Asels."
		},
		"10": {
			"1": "<sup>1</sup> Filistear réðust á Ísrael og flýðu Ísraelsmenn undan þeim en margir lágu fallnir á Gilbóafjalli.",
			"2": "<sup>2</sup> Filistear þrengdu mjög að Sál og sonum hans og felldu Jónatan, Abínadab og Malkísúa, syni hans.",
			"3": "<sup>3</sup> Var nú gerð hörð atlaga að Sál. Nokkrar bogaskyttur höfðu komið auga á hann, hæft hann og var hann mikið særður.",
			"4": "<sup>4</sup> Þá sagði Sál við skjaldsvein sinn: „Dragðu sverð þitt úr slíðrum og rektu mig í gegn. Annars koma þeir óumskornu, reka mig í gegn og fara háðulega með mig.“ En skjaldsveinninn vildi ekki gera þetta því að hann var mjög hræddur. Þá tók Sál sjálfur sverð sitt og lét fallast á það.",
			"5": "<sup>5</sup> Þegar skjaldsveinninn sá að Sál var látinn lét hann sig einnig falla á sverð sitt og dó með Sál.",
			"6": "<sup>6</sup> Þannig lét Sál lífið og synir hans þrír og öll fjölskylda hans í einu.",
			"7": "<sup>7</sup> Þegar Ísraelsmennirnir, sem bjuggu handan við sléttuna og austan við Jórdan, gerðu sér grein fyrir að Ísraelsmenn voru flúnir og Sál og synir hans fallnir yfirgáfu þeir borgir sínar og flýðu. Síðan komu Filistear og settust að í þeim.",
			"8": "<sup>8</sup> Daginn eftir, þegar Filistear komu til að ræna valinn, fundu þeir Sál og syni hans þrjá á Gilbóafjalli.",
			"9": "<sup>9</sup> Þeir hjuggu af honum höfuðið og rændu vopnum hans. Síðan sendu þeir boðbera um land Filistea til að flytja skurðgoðum sínum og fólkinu fregnir af sigrinum.",
			"10": "<sup>10</sup> Vopnum Sáls komu þeir fyrir í húsi guða sinna en hauskúpu hans festu þeir upp í húsi Dagóns.",
			"11": "<sup>11</sup> Þegar íbúarnir í Jabes í Gíleað fréttu hvernig Filistearnir höfðu farið með Sál",
			"12": "<sup>12</sup> héldu allir vopnfærir menn af stað, sóttu lík Sáls og sona hans og fluttu þau til Jabes. Því næst grófu þeir bein þeirra undir eikinni í Jabes og föstuðu í sjö daga.",
			"13": "<sup>13</sup> Sál lét lífið vegna svika sinna við Drottin og vegna þess að hann hafði ekki hlýtt fyrirmælum Drottins. Hann hafði jafnvel leitað úrskurðar hjá framliðnum",
			"14": "<sup>14</sup> í stað þess að leita úrskurðar Drottins. Drottinn lét hann því deyja og afhenti Davíð Ísaísyni konungdæmið."
		},
		"11": {
			"1": "<sup>1</sup> Allur Ísrael safnaðist nú saman hjá Davíð í Hebron og sagði: „Við erum hold þitt og bein.",
			"2": "<sup>2</sup> Áður fyrr, á meðan Sál var konungur, varst það alltaf þú sem leiddir Ísrael í hernað og heim aftur. Auk þess hefur Drottinn, Guð þinn, sagt við þig: Þú skalt vaka yfir þjóð minni, Ísrael. Þú skalt verða þjóðhöfðingi Ísraels.“",
			"3": "<sup>3</sup> Þegar allir öldungar Ísraels voru komnir til konungsins í Hebron gerði Davíð konungur sáttmála við þá frammi fyrir augliti Drottins. Því næst smurðu þeir Davíð til konungs yfir Ísrael samkvæmt þeim boðskap Drottins sem Samúel hafði flutt.",
			"4": "<sup>4</sup> Davíð hélt nú ásamt öllum Ísrael til Jerúsalem, það er að segja til Jebús. Þar voru fyrir Jebúsítar sem þá bjuggu í landinu.",
			"5": "<sup>5</sup> Þeir sögðu við Davíð: „Þú kemst ekki inn í borgina.“ Samt tók Davíð klettavirkið Síon sem varð borg Davíðs.",
			"6": "<sup>6</sup> Þá sagði Davíð: „Sá sem fyrstur sigrar Jebúsíta skal verða foringi og hershöfðingi.“ Jóab Serújuson fór fyrstur upp og varð hershöfðingi.",
			"7": "<sup>7</sup> Davíð settist nú að í virkinu. Þess vegna var það nefnt borg Davíðs.",
			"8": "<sup>8</sup> Hann lét síðan reisa víggirðingu umhverfis virkið frá Milló og allt í kring en Jóab lét lagfæra aðra hluta borgarinnar.",
			"9": "<sup>9</sup> Davíð varð nú sífellt voldugri og Drottinn hersveitanna var með honum.",
			"10": "<sup>10</sup> Þetta eru helstu kappar Davíðs. Þeir studdu konungdóm hans ásamt öllum Ísrael og gerðu hann að konungi yfir Ísrael samkvæmt orði Drottins.",
			"11": "<sup>11</sup> Þetta er skrá yfir kappa Davíðs: Jasóbeam Hakmóníson var foringi hinna þriggja. Hann sveiflaði spjóti sínu yfir þrjú hundruð mönnum og felldi þá.",
			"12": "<sup>12</sup> Næstur honum var Ahóhítinn Eleasar Dódóson. Hann var einn hinna þriggja kappa.",
			"13": "<sup>13</sup> Hann var með Davíð í Pas Dammím þegar Filistear söfnuðust þar saman til bardaga. Þar var akurspilda alsprottin byggi. Þegar herinn hafði flúið",
			"14": "<sup>14</sup> nam hann staðar á miðjum akrinum, varði hann og hjó Filisteana niður. Þannig veitti Drottinn honum mikinn sigur.",
			"15": "<sup>15</sup> Einhverju sinni þegar herbúðir Filistea voru á Refaímsléttu fóru þrír af köppunum þrjátíu niður af klettinum til Davíðs í Adúllamhelli.",
			"16": "<sup>16</sup> Davíð dvaldist þá í fjallavirkinu en framvarðarsveit Filistea í Betlehem.",
			"17": "<sup>17</sup> Þá þyrsti Davíð mjög og hann spurði: „Hver vill sækja mér vatn úr brunninum við borgarhliðið í Betlehem?“",
			"18": "<sup>18</sup> Þá brutust kapparnir þrír inn í herbúðir Filistea, jusu vatni úr brunninum við borgarhliðið í Betlehem, tóku það með sér og færðu Davíð. Hann vildi þó ekki drekka vatnið heldur dreypti því í dreypifórn handa Drottni",
			"19": "<sup>19</sup> og sagði: „Guð minn forði mér frá að gera slíkt. Á ég að drekka blóð þessara manna sem hættu lífi sínu til að sækja vatnið?“ Þess vegna vildi hann ekki drekka það. Þetta afrek unnu kapparnir þrír.",
			"20": "<sup>20</sup> Abísaí, bróðir Jóabs, var foringi hinna þrjátíu. Hann sveiflaði spjóti sínu yfir þrjú hundruð mönnum og felldi þá. Hann var virtur af köppunum þremur.",
			"21": "<sup>21</sup> Hann var mikils metinn af hinum þrjátíu og varð foringi þeirra en hann jafnaðist ekki á við kappana þrjá.",
			"22": "<sup>22</sup> Benaja Jójadason, sem var frá Kapseel, var hraustur maður og vann mörg afrek. Hann drap báða syni Aríels frá Móab. Eitt sinn þegar snjóaði fór hann niður í gryfju og drap þar ljón.",
			"23": "<sup>23</sup> Hann felldi einnig risavaxinn Egypta sem var fimm álnir á hæð. Egyptinn hafði spjót í hendi, digurt sem vefjarrif. En Benaja réðst á hann með staf, reif spjótið úr hendi Egyptans og drap hann með hans eigin spjóti.",
			"24": "<sup>24</sup> Slík afrek vann Benaja Jójadason og var virtur af köppunum þremur.",
			"25": "<sup>25</sup> Hann var mikils metinn af hinum þrjátíu en hann jafnaðist ekki á við kappana þrjá. Davíð setti hann yfir lífvörð sinn.",
			"26": "<sup>26</sup> Hraustar stríðshetjur voru: Asahel, bróðir Jóabs, Elhanan Dódóson frá Betlehem,",
			"27": "<sup>27</sup> Sammót frá Harór, Heles frá Palon,",
			"28": "<sup>28</sup> Íra Íkkesson frá Tekóa, Abíeser frá Anatót,",
			"29": "<sup>29</sup> Sibbekaí frá Húsa, Ílaí frá Ahó,",
			"30": "<sup>30</sup> Maharí frá Netófa, Heled Baanason frá Netófa,",
			"31": "<sup>31</sup> Íttaí Ríbaíson frá Gíbeu í Benjamín, Benaja frá Píraton,",
			"32": "<sup>32</sup> Húraí frá Nahale Gaas, Abíel frá Araba,",
			"33": "<sup>33</sup> Asmavet frá Bahúrím, Eljahba frá Saalbón,",
			"34": "<sup>34</sup> Hasem frá Gíson, Jónatan Sageson frá Harar,",
			"35": "<sup>35</sup> Ahíam Sakarsson frá Harar, Elífal Úrsson,",
			"36": "<sup>36</sup> Hefer frá Mekera, Ahía frá Palon,",
			"37": "<sup>37</sup> Hesró frá Karmel, Naaraí Esbaíson,",
			"38": "<sup>38</sup> Jóel, bróðir Natans, Míbhar Hagríson,",
			"39": "<sup>39</sup> Selek Ammóníti, Nahraí Beerótíti, skjaldsveinn Jóabs Serújusonar,",
			"40": "<sup>40</sup> Íra frá Jattír, Gareb frá Jattír,",
			"41": "<sup>41</sup> Úría Hetíti, Sabad Ahlaíson,",
			"42": "<sup>42</sup> Adína Sísason, af ættbálki Rúbens, höfðingi Rúbensniðja og foringi þrjátíu manna liðs,",
			"43": "<sup>43</sup> Hanan Maakason og Jósafat frá Meten,",
			"44": "<sup>44</sup> Ússía frá Astera, Sama og Jeíel Hótamssynir frá Aróer,",
			"45": "<sup>45</sup> Jedíael Simríson og Jóha, bróðir hans, frá Tís,",
			"46": "<sup>46</sup> Elíel frá Mahanaím og Jeríbaí og Jósavja Elnaamssynir og Jítma Móabíti,",
			"47": "<sup>47</sup> Elíel og Óbeð og Jaasíel frá Mesóbaja."
		},
		"12": {
			"1": "<sup>1</sup> Þetta eru þeir sem komu til Davíðs í Siklag þegar hann var enn útilokaður frá þjónustu við Sál Kísson. Voru þeir meðal þeirra kappa sem studdu hann í bardögum.",
			"2": "<sup>2</sup> Þeir voru vopnaðir bogum í hægri hendi og gátu kastað grjóti og skotið ör af boga jafnt með hægri hendi sem vinstri. Af ættmennum Sáls komu eftirtaldir frá Benjamín:",
			"3": "<sup>3</sup> Ahíeser, höfðingi þeirra, einnig Jóas Semaasson frá Gíbeu, Jesíel og Pelet Asmavetssynir, Beraka og Jehú frá Anatót,",
			"4": "<sup>4</sup> Jismaja frá Gíbeon, einn hinna þrjátíu kappa og foringi þeirra,",
			"5": "<sup>5</sup> Jeremía, Jehasíel, Jóhanan og Jósabad frá Gedera,",
			"6": "<sup>6</sup> Elúsaí, Jerímót, Baalja, Semarja og Sefatja frá Haríf,",
			"7": "<sup>7</sup> Elkana, Jissía, Asareel, Jóeser og Jasóbeam, sem voru Kóraítar,",
			"8": "<sup>8</sup> Jóela og Sebadja Jeróhamssynir frá Gedór.",
			"9": "<sup>9</sup> Einnig gengu til liðs við Davíð hraustir kappar af ættbálki Gaðs og komu í virki hans í eyðimörkinni. Þeir voru þjálfaðir hermenn, vopnaðir skjöldum og spjótum. Þeir líktust ljónum og voru fljótir í förum eins og skógargeitur í fjöllum.",
			"10": "<sup>10</sup> Eser var foringi þeirra, Óbadía var annar, Elíab þriðji,",
			"11": "<sup>11</sup> Mismanna fjórði, Jeremía fimmti,",
			"12": "<sup>12</sup> Attaí sjötti, Elíel sjöundi,",
			"13": "<sup>13</sup> Jóhanan áttundi, Elsabad níundi,",
			"14": "<sup>14</sup> Jeremía tíundi og Makbannaí var sá ellefti.",
			"15": "<sup>15</sup> Þessir menn af ættbálki Gaðs voru foringjar hersins. Sá aumasti þeirra var hundrað manna maki en sá sterkasti þúsund manna maki.",
			"16": "<sup>16</sup> Það voru þeir sem fóru yfir Jórdan í fyrsta mánuðinum þegar hún flæddi yfir bakka sína og hafði einangrað dalina í austri og vestri.",
			"17": "<sup>17</sup> Einnig komu menn úr ættbálkum Benjamíns og Júda til Davíðs í virkinu.",
			"18": "<sup>18</sup> Davíð gekk út til þeirra, ávarpaði þá og sagði: „Ef þið komið til mín með friði og ætlið að styðja mig er ég fús til samstarfs við ykkur. En ef þið ætlið að svíkja mig í hendur fjandmanna minna þó að ég hafi ekkert illvirki framið, þá sér Guð feðra okkar það og refsar ykkur.“",
			"19": "<sup>19</sup> Þá kom andi yfir Amasaí, foringja hinna þrjátíu, og hann svaraði: Þínir erum við, Davíð. Með þér erum við, sonur Ísaí. Heill, heill sé þér. Heill þeim sem styður þig því að Guð þinn styður þig. Þá tók Davíð við þeim og setti þá meðal foringja liðs síns.",
			"20": "<sup>20</sup> Nokkrir menn úr ættbálki Manasse gengu einnig til liðs við Davíð þegar hann átti að fara með Filisteum í orrustu gegn Sál. Hann varð þeim þó ekki að neinu liði því að höfðingjar Filistea sendu hann burt þegar þeir höfðu ráðið ráðum sínum og komist að þessari niðurstöðu: „Hann mun að nýju ganga í lið með Sál, húsbónda sínum, og það mun kosta okkur höfuðið.“",
			"21": "<sup>21</sup> Þegar hann kom aftur til Siklag gengu þessir menn úr ættbálki Manasse í lið með honum: Adna, Jósabad, Jedíael, Míkael, Jósabad, Elíhú og Silletaí. Þeir voru foringjar þúsund manna liða sem komu frá Manasse",
			"22": "<sup>22</sup> og studdu Davíð gegn ránsflokknum því að þeir voru allir miklir bardagamenn og urðu foringjar í hernum.",
			"23": "<sup>23</sup> Á hverjum degi komu menn til Davíðs til að styðja hann þannig að herbúðir hans urðu mjög fjölmennar.",
			"24": "<sup>24</sup> Þetta er skrá yfir þá hermenn sem komu til Davíðs í Hebron með alvæpni til þess að fela honum konungdóm Sáls eftir boði Drottins:",
			"25": "<sup>25</sup> Af ættbálki Júda komu 6800 menn sem báru skildi og spjót, allt menn með alvæpni,",
			"26": "<sup>26</sup> af ættbálki Símeons komu 7100 hraustir hermenn",
			"27": "<sup>27</sup> og af ættbálki Leví 4600 menn.",
			"28": "<sup>28</sup> Auk þess kom Jójada, höfðingi ættar Arons, ásamt",
			"29": "<sup>29</sup> 3700 mönnum og Sadók, ungur og hraustur stríðsmaður, ásamt 22 foringjum úr fjölskyldu hans.",
			"30": "<sup>30</sup> Af ættbálki Benjamíns, frændum Sáls, komu 3000 en meirihluti þeirra hélt þá enn trúnaði við ætt Sáls.",
			"31": "<sup>31</sup> Af ættbálki Efraíms komu 20.800 hraustir hermenn, mikils metnir í fjölskyldum sínum,",
			"32": "<sup>32</sup> og af hálfum ættbálki Manasse komu 18.000 menn sem höfðu verið kallaðir saman með nafnakalli til þess að koma og taka Davíð til konungs.",
			"33": "<sup>33</sup> Af öllum ættbálki Íssakars, sem kunni skil á sérhverri tíð og vissi hvað Ísrael bar að gera, komu 200 foringjar ásamt öllum þeim ættmennum sínum sem voru undir þeirra stjórn,",
			"34": "<sup>34</sup> af Sebúlon kom 50.000 manna her, fullbúinn til orrustu og skipaði sér heils hugar í fylkinguna,",
			"35": "<sup>35</sup> af Naftalí komu þúsund höfðingjar ásamt 37.000 mönnum vopnuðum skjöldum og spjótum,",
			"36": "<sup>36</sup> af Dan komu 28.600 menn albúnir til orrustu,",
			"37": "<sup>37</sup> af Asser kom 40.000 manna her, einnig búinn til bardaga,",
			"38": "<sup>38</sup> af ættbálkunum handan við Jórdan, af ættbálkum Rúbens, Gaðs og hálfum ættbálki Manasse, kom 120.000 manna her með alvæpni, búinn til bardaga.",
			"39": "<sup>39</sup> Allir þessir hermenn skipuðu sér heils hugar í fylkinguna og komu til Hebron til að gera Davíð að konungi yfir öllum Ísrael og aðrir Ísraelsmenn voru einnig einhuga um að gera Davíð að konungi.",
			"40": "<sup>40</sup> Þeir dvöldust nú þrjá daga hjá Davíð og neyttu matar og drykkjar því að ættmenn þeirra sáu þeim fyrir vistum.",
			"41": "<sup>41</sup> Jafnvel nágrannar þeirra, sem komu alla leið frá Íssakar, Sebúlon og Naftalí, færðu þeim mat á ösnum, úlföldum, múldýrum og nautum. Þeir færðu þeim mjöl, fíkjukökur og rúsínukökur, vín, olíu og mikinn fjölda nauta og fjár því að gleði ríkti í Ísrael."
		},
		"13": {
			"1": "<sup>1</sup> Þegar Davíð hafði ráðgast við foringja þúsund og hundrað manna liðssveita, við alla leiðtogana,",
			"2": "<sup>2</sup> ávarpaði hann allan söfnuð Ísraels svofelldum orðum: „Ef það er ykkur að skapi og ef það er vilji Drottins, Guðs okkar, skulum við senda boðbera til bræðra okkar, sem eru um kyrrt í öllum héruðum Ísraels, ásamt prestunum og Levítunum, sem búa í borgum sínum og beitilöndunum umhverfis, til að stefna þeim saman hingað til okkar.",
			"3": "<sup>3</sup> Því næst skulum við flytja örk Guðs okkar hingað en við létum okkur hana engu varða í stjórnartíð Sáls.“",
			"4": "<sup>4</sup> Allur söfnuðurinn féllst á að þetta yrði gert því að tillagan var góð að mati alls fólksins.",
			"5": "<sup>5</sup> Davíð safnaði nú saman öllum Ísrael frá Síhor í Egyptalandi til Lebó Hamat til þess að sækja örkina til Kirjat Jearím.",
			"6": "<sup>6</sup> Davíð fór upp til Baala, það er að segja til Kirjat Jearím, sem er í Júda, ásamt öllum Ísrael til þess að sækja þangað örk Guðs sem ber nafn Drottins herskaranna sem situr í hásæti yfir kerúbunum.",
			"7": "<sup>7</sup> Þeir sóttu örk Guðs á nýjum vagni úr húsi Abínadabs. Ússa og Ahjó stjórnuðu vagninum",
			"8": "<sup>8</sup> en Davíð og allur Ísrael dönsuðu með miklum tilþrifum frammi fyrir augliti Guðs, sungu og léku á sítara, hörpur, pákur, bjöllur og lúðra.",
			"9": "<sup>9</sup> Þegar þeir komu á þreskivöll Kídons rétti Ússa út hönd sína til að styðja örkina því að uxarnir höfðu hrasað.",
			"10": "<sup>10</sup> Þá blossaði reiði Drottins upp gegn Ússa og laust hann Ússa samstundis banahögg af því að hann hafði gripið til arkarinnar með hendi sinni. Hann lét lífið þarna fyrir augliti Guðs.",
			"11": "<sup>11</sup> Davíð var mjög brugðið vegna þess að Drottinn hafði hrifið Ússa svo snögglega í burtu. Hann nefndi þennan stað Peres Ússa, og það heitir hann enn í dag.",
			"12": "<sup>12</sup> Davíð hræddist Drottin á þessum degi og spurði: „Hvernig get ég flutt örk Guðs heim til mín?“",
			"13": "<sup>13</sup> Davíð vildi því ekki láta flytja örkina heim til sín í borg Davíðs heldur sneri með hana að húsi Óbeðs Edóms frá Gat.",
			"14": "<sup>14</sup> Örk Guðs var í húsi Óbeðs Edóms í þrjá mánuði og Drottinn blessaði Óbeð Edóm og allt sem hann átti."
		},
		"14": {
			"1": "<sup>1</sup> Híram, konungur í Týrus, sendi menn til Davíðs með sedrusvið og einnig sendi hann steinsmiði og trésmiði til þess að byggja hús handa honum.",
			"2": "<sup>2</sup> Þá skildi Davíð að Drottinn hafði fest hann í sessi sem konung yfir Ísrael því að hann hafði hafið konungdóm hans til vegs vegna þjóðar sinnar, Ísraels.",
			"3": "<sup>3</sup> Davíð tók sér fleiri konur í Jerúsalem og eignaðist fleiri börn.",
			"4": "<sup>4</sup> Þetta eru nöfn sonanna sem hann eignaðist í Jerúsalem: Sammúa, Sóbab, Natan, Salómon,",
			"5": "<sup>5</sup> Jíbhar, Elísúa, Elpalet,",
			"6": "<sup>6</sup> Nóga, Nefeg, Jafía,",
			"7": "<sup>7</sup> Elísama, Beeljada og Elífelet.",
			"8": "<sup>8</sup> Þegar Filistear fréttu að Davíð hefði verið smurður til konungs yfir öllum Ísrael héldu allir Filistear upp eftir til þess að taka Davíð til fanga. Um leið og Davíð frétti þetta fór hann á móti þeim.",
			"9": "<sup>9</sup> Filistear voru þá komnir og höfðu dreift sér um Refaímsléttu.",
			"10": "<sup>10</sup> Þá spurði Davíð Guð: „Á ég að fara gegn Filisteunum? Muntu selja þá mér í hendur?“ Drottinn svaraði honum: „Farðu upp eftir því að ég mun selja þá þér í hendur.“",
			"11": "<sup>11</sup> Þá fór hann upp eftir til Baal Perasím, sigraði þá þar og sagði: „Guð hefur rofið fylkingar fjandmanna minna með hendi minni líkt og vatn sem brýtur sér leið.“ Þess vegna er þessi staður nefndur Baal Perasím, það er Herra rofsins.",
			"12": "<sup>12</sup> Filistearnir skildu skurðgoð sín þar eftir og Davíð skipaði að þau skyldu brennd í eldi.",
			"13": "<sup>13</sup> Nú dreifðu Filistear sér aftur um Refaímsléttu.",
			"14": "<sup>14</sup> Davíð spurði þá Guð á ný og hann svaraði: „Þú skalt ekki fara á eftir þeim. Farðu á svig við þá og gerðu árás á þá frá bakatrjánum.",
			"15": "<sup>15</sup> Þegar þú heyrir þrusk skóhljóði líkast í hæstu greinum bakatrjánna skaltu bregðast skjótt við því að þá fer Guð á undan þér til þess að sigra her Filistea.“",
			"16": "<sup>16</sup> Davíð gerði eins og Guð hafði fyrir hann lagt og sigraði Filistea á öllu svæðinu frá Gíbeon til Geser.",
			"17": "<sup>17</sup> Frægð Davíðs barst um öll lönd og Drottinn lét allar þjóðir óttast hann."
		},
		"15": {
			"1": "<sup>1</sup> Þegar hann hafði látið byggja sér hús í borg Davíðs lét hann einnig búa örk Guðs stað og reisa tjald fyrir hana.",
			"2": "<sup>2</sup> Þá skipaði Davíð svo fyrir: „Enginn má bera örk Guðs nema Levítarnir því að Drottinn hefur valið þá til að bera örk Drottins og til að þjóna sér ævinlega.“",
			"3": "<sup>3</sup> Því næst kallaði Davíð allan Ísrael saman í Jerúsalem til þess að flytja örk Drottins þangað upp eftir á staðinn sem hann hafði búið henni.",
			"4": "<sup>4</sup> Davíð stefndi saman niðjum Arons og Levítunum.",
			"5": "<sup>5</sup> Af niðjum Kahats kom höfðinginn Úríel og bræður hans, 120 menn.",
			"6": "<sup>6</sup> Af niðjum Merarí kom höfðinginn Asaja og bræður hans, 220 menn.",
			"7": "<sup>7</sup> Af niðjum Gersons kom höfðinginn Jóel og bræður hans, 130 menn.",
			"8": "<sup>8</sup> Af niðjum Elísafans kom höfðinginn Semaja og bræður hans, 200 menn.",
			"9": "<sup>9</sup> Af niðjum Hebrons kom höfðinginn Elíel og bræður hans, 80 menn.",
			"10": "<sup>10</sup> Af niðjum Ússíels kom höfðinginn Ammínadab og bræður hans, 112 menn.",
			"11": "<sup>11</sup> Davíð kallaði til sín prestana Sadók og Abjatar og Levítana Úríel, Asaja, Jóel, Semaja, Elíel og Ammínadab",
			"12": "<sup>12</sup> og gaf þeim þessi fyrirmæli: „Þið eruð ættarhöfðingjar Levítanna. Helgið ykkur nú ásamt bræðrum ykkar og flytjið örk Drottins, Guðs Ísraels, til staðarins sem ég hef búið henni.",
			"13": "<sup>13</sup> Vegna þess að þið voruð ekki með okkur í fyrsta skiptið hefur Drottinn, Guð okkar, steypt yfir okkur ófarnaði. Við leituðum ekki úrskurðar hans eins og okkur bar.“",
			"14": "<sup>14</sup> Prestarnir og Levítarnir helguðu sig nú til þess að flytja örk Drottins, Guðs Ísraels, upp eftir.",
			"15": "<sup>15</sup> Því næst tóku niðjar Leví örk Guðs á axlir sér með burðarstöngum hennar eins og Móse hafði gefið fyrirmæli um að boði Drottins.",
			"16": "<sup>16</sup> Davíð skipaði höfðingjum Levítanna að láta ættbræður þeirra, söngvarana, taka sér stöðu með hljóðfæri sín, hörpur, sítara og bjöllur, til þess að leika undir fagnaðarsöngnum.",
			"17": "<sup>17</sup> Þá létu þeir Heman Jóelsson og Asaf Berekíason, ættbróður þeirra, taka sér stöðu, enn fremur ættbróður þeirra, Etan Kúsajason af niðjum Merarí.",
			"18": "<sup>18</sup> Þeir létu einnig nokkra af bræðrum þeirra úr öðrum þjónustuflokki taka sér stöðu með þeim: Sakaría, Jaasíel, Semíramót, Jehíel, Únní, Elíab, Benaja, Maaseja, Mattitja, Elífala, Mikneja, Óbeð Edóm og Jeíel.",
			"19": "<sup>19</sup> Söngvararnir Heman, Asaf og Etan léku á tvö klukkuspil úr eir,",
			"20": "<sup>20</sup> Sakaría, Jaasíel, Semíramót, Jehíel, Únní, Elíab, Maaseja og Benaja léku á hörpur að elamítískum hætti.",
			"21": "<sup>21</sup> Mattitja, Elífala, Mikneja, Óbeð Edóm, Jeíel og Asasja léku á gígjur að símjónítískum hætti til að stjórna söngnum.",
			"22": "<sup>22</sup> Kenanja, höfðingi Levítanna, stjórnaði flutningi tónlistarinnar. Hann stjórnaði flutningnum af því að hann hafði kunnáttu til þess.",
			"23": "<sup>23</sup> Berekía og Elkana voru hliðverðir arkarinnar.",
			"24": "<sup>24</sup> Prestarnir Sebanja, Jósafat, Netaneel, Amasaí, Sakaría, Benaja og Elíeser blésu í lúðra frammi fyrir örk Guðs. Óbeð Edóm og Jehía voru hliðverðir arkarinnar.",
			"25": "<sup>25</sup> Davíð, öldungar Ísraels og foringjar þúsund manna sveitanna fóru nú glaðir í bragði til þess að flytja sáttmálsörk Drottins upp eftir úr húsi Óbeðs Edóms.",
			"26": "<sup>26</sup> Sjö nautum og sjö hrútum var fórnað af því að Guð styrkti Levítana sem báru sáttmálsörk Drottins.",
			"27": "<sup>27</sup> Davíð bar yfirhöfn frá Byssos, einnig allir Levítarnir sem báru örkina, söngvararnir og Kenanja, stjórnandi söngvaranna, en Davíð bar línhökul.",
			"28": "<sup>28</sup> Þannig flutti allur Ísrael sáttmálsörk Drottins upp eftir með fagnaðarópum og hafurshornablæstri, með lúðrablæstri, bjölluhljómi, hörpuleik og gígjuleik.",
			"29": "<sup>29</sup> Þegar sáttmálsörk Drottins kom til borgar Davíðs varð Míkal, dóttur Sáls, litið út um glugga. Þegar hún sá Davíð konung hoppa og dansa frammi fyrir augliti Drottins fyrirleit hún hann í hjarta sínu."
		},
		"16": {
			"1": "<sup>1</sup> Örk Guðs var nú borin inn og henni komið fyrir í miðju tjaldinu sem Davíð hafði látið setja upp fyrir hana. Því næst voru brennifórnir og heillafórnir færðar frammi fyrir augliti Guðs.",
			"2": "<sup>2</sup> Eftir að Davíð hafði fært brennifórnina og heillafórnina blessaði hann fólkið í nafni Drottins.",
			"3": "<sup>3</sup> Loks lét hann úthluta öllum Ísraelsmönnum, körlum og konum, hverjum fyrir sig, einum brauðhleif, einni döðluköku og einni rúsínuköku.",
			"4": "<sup>4</sup> Davíð skipaði Levíta til þjónustu frammi fyrir örk Drottins til þess að lofa, þakka og vegsama Drottin, Guð Ísraels.",
			"5": "<sup>5</sup> Hann skipaði Asaf stjórnanda, Sakaría næstan honum, enn fremur Jeíel, Semíramót, Jehíel, Mattitja, Elíab, Benaja, Óbeð Edóm og Jehíel. Þeir áttu að leika á hljóðfæri, hörpur og gígjur, en Asaf á málmgjöll.",
			"6": "<sup>6</sup> Að auki áttu prestarnir Benaja og Jehasíel stöðugt að þeyta lúðra frammi fyrir sáttmálsörk Guðs.",
			"7": "<sup>7</sup> Á þessum degi lét Davíð Asaf og embættisbræður hans flytja þessa lofgjörð til Drottins í fyrsta skipti:",
			"8": "<sup>8</sup> Þakkið Drottni, ákallið nafn hans. Gerið máttarverk hans kunn meðal þjóðanna.",
			"9": "<sup>9</sup> Syngið honum lof, leikið fyrir hann, talið um öll hans máttarverk.",
			"10": "<sup>10</sup> Hrósið yður af hans heilaga nafni. Hjarta þeirra, sem leita Drottins, gleðjist.",
			"11": "<sup>11</sup> Leitið Drottins og máttar hans, leitið sífellt eftir augliti hans.",
			"12": "<sup>12</sup> Minnist dásemdarverkanna sem hann vann, tákna hans og dómanna sem hann kvað upp.",
			"13": "<sup>13</sup> Þér, niðjar Ísraels, þjóns hans, synir Jakobs, sem hann útvaldi.",
			"14": "<sup>14</sup> Hann, Drottinn, er Guð vor, um víða veröld gilda boð hans.",
			"15": "<sup>15</sup> Hann minnist að eilífu sáttmála síns, fyrirheitanna sem hann gaf þúsund kynslóðum,",
			"16": "<sup>16</sup> sáttmálans sem hann gerði við Abraham og eiðsins sem hann sór Ísak",
			"17": "<sup>17</sup> og setti sem lög fyrir Jakob, ævarandi sáttmála fyrir Ísrael.",
			"18": "<sup>18</sup> Hann sagði: „Yður fæ ég Kanaansland, það skal verða erfðahlutur yðar,“",
			"19": "<sup>19</sup> þegar þér voruð fámennur hópur og bjugguð þar sem fáliðaðir útlendingar.",
			"20": "<sup>20</sup> Þeir reikuðu frá einni þjóð til annarrar, frá einu konungsríki til annars.",
			"21": "<sup>21</sup> Hann leið engum að kúga þá en hegndi konungum þeirra vegna.",
			"22": "<sup>22</sup> „Snertið eigi mína smurðu og gerið eigi spámönnum mínum mein.“",
			"23": "<sup>23</sup> Syngið Drottni, öll lönd, kunngjörið hjálpráð hans dag eftir dag.",
			"24": "<sup>24</sup> Segið frá dýrð hans meðal þjóðanna, frá dásemdarverkum hans meðal allra lýða,",
			"25": "<sup>25</sup> því að mikill er Drottinn og mjög vegsamlegur, óttalegri en allir guðir.",
			"26": "<sup>26</sup> Því að allir guðir þjóðanna eru falsguðir en Drottinn hefur gert himininn,",
			"27": "<sup>27</sup> dýrð og hátign eru frammi fyrir honum, máttur og gleði á helgistað hans.",
			"28": "<sup>28</sup> Tignið Drottin, þér ættbálkar þjóðanna, tignið Drottin með dýrð og mætti,",
			"29": "<sup>29</sup> tignið Drottin, heiðrið nafn hans, berið fram fórnargjafir, komið fyrir auglit hans, tilbiðjið Drottin í helgum skrúða.",
			"30": "<sup>30</sup> Öll jörðin skjálfi frammi fyrir honum. Hann hefur fest jörðina, hún riðar ekki.",
			"31": "<sup>31</sup> Himinninn gleðjist og jörðin fagni. Kunngjörið þjóðunum: „Drottinn er konungur.“",
			"32": "<sup>32</sup> Hafið drynji og allt sem í því er, foldin fagni og allt sem á henni er,",
			"33": "<sup>33</sup> tré skógarins hrópi af gleði frammi fyrir augliti Drottins því að hann kemur til að ríkja á jörðu.",
			"34": "<sup>34</sup> Þakkið Drottni því að hann er góður, því að miskunn hans varir að eilífu,",
			"35": "<sup>35</sup> og biðjið: „Hjálpa þú, Guð hjálpræðis vors. Sameina oss og frelsa undan framandi þjóðum svo að vér getum lofað þitt heilaga nafn og fagnandi sungið þér lof:",
			"36": "<sup>36</sup> Lofaður sé Drottinn, Ísraels Guð, frá eilífð til eilífðar. Allur lýðurinn sagði: „Amen“ og „lof sé Drottni.“",
			"37": "<sup>37</sup> Davíð lét Asaf og embættisbræður hans verða eftir frammi fyrir sáttmálsörk Drottins. Þeir áttu stöðugt að annast hina daglegu þjónustu frammi fyrir örkinni.",
			"38": "<sup>38</sup> Óbeð Edóm og bræður hans, sextíu og átta menn, voru á meðal þeirra en Óbeð Edóm Jedútúnsson og Hósa voru hliðverðir.",
			"39": "<sup>39</sup> Sadók presti og embættisbræðrum hans, prestunum, fól hann þjónustu frammi fyrir bústað Drottins á fórnarhæðinni í Gíbeon.",
			"40": "<sup>40</sup> Þeir áttu að færa Drottni brennifórnir á brennifórnaraltarinu kvölds og morgna dag hvern og uppfylla allt sem ritað er í lögmáli Drottins sem hann hefur boðið Ísrael að halda.",
			"41": "<sup>41</sup> Á meðal þeirra voru Heman og Jedútún og hinir sem höfðu verið valdir og nefndir með nafni til að þakka Drottni því að miskunn hans varir að eilífu.",
			"42": "<sup>42</sup> Heman og Jedútún höfðu lúðra og málmgjöll handa hljóðfæraleikurunum og önnur hljóðfæri fyrir sálma guðsþjónustunnar. Niðjar Jedútúns voru ráðnir til að gæta hliðanna.",
			"43": "<sup>43</sup> Því næst hélt allt fólkið aftur heim til sín. Davíð fór einnig heim til þess að heilsa fjölskyldu sinni."
		},
		"17": {
			"1": "<sup>1</sup> Þegar Davíð var sestur að í húsi sínu sagði hann við Natan spámann: „Nú bý ég í húsi úr sedrusviði en sáttmálsörk Drottins er undir tjalddúk.“",
			"2": "<sup>2</sup> Natan svaraði Davíð: „Gerðu það sem þú hefur í huga því að Guð er með þér.“",
			"3": "<sup>3</sup> Þá um nóttina kom orð Guðs til Natans:",
			"4": "<sup>4</sup> „Far þú og seg við þjón minn, Davíð: Svo segir Drottinn: Það ert ekki þú sem átt að byggja handa mér húsið sem ég mun búa í.",
			"5": "<sup>5</sup> Frá þeim degi, er ég leiddi Ísrael hingað og þar til nú, hef ég ekki búið í húsi. Hins vegar hef ég sífellt verið á ferð frá tjaldi til tjalds og frá bústað til bústaðar.",
			"6": "<sup>6</sup> Hef ég nokkurn tíma spurt einhvern af leiðtogum Ísraels sem ég setti sem hirða þjóðar minnar þann tíma sem ég hef verið á ferð með Ísraelsmönnum: Hvers vegna hafið þið ekki byggt mér hús úr sedrusviði?",
			"7": "<sup>7</sup> Þess vegna skaltu nú segja við þjón minn, Davíð: Svo segir Drottinn herskaranna: Ég sótti þig í haglendið þar sem þú gættir fjár og gerði þig að höfðingja yfir lýð mínum, Ísrael.",
			"8": "<sup>8</sup> Ég hef verið með þér á öllum ferðum þínum og ég hef tortímt öllum fjandmönnum þínum. Ég mun gera nafn þitt jafnfrægt og nöfn frægustu manna á jörðinni.",
			"9": "<sup>9</sup> Ég mun fá lýð mínum, Ísrael, stað og ég mun rótfesta hann svo að hann geti búið þar öruggur og óttalaus um alla framtíð. Ofbeldismenn skulu ekki kúga hann framar eins og fyrrum,",
			"10": "<sup>10</sup> jafnvel eftir að ég setti dómara yfir lýð minn, Ísrael. Ég mun gersigra alla fjandmenn þína. Hér með kunngjöri ég þér: Drottinn mun reisa þér hús.",
			"11": "<sup>11</sup> Þegar dagar þínir eru allir og þú ert genginn til feðra þinna mun ég upphefja afkomanda þinn, einn sona þinna, til að vera eftirmaður þinn og ég mun styðja konungdóm hans.",
			"12": "<sup>12</sup> Hann á að byggja mér hús og ég mun ævinlega styðja hásæti hans.",
			"13": "<sup>13</sup> Ég verður mér sonur. Trúfesti mína skal ég ekki frá honum taka eins og ég tók hana frá þeim sem var á undan þér.",
			"14": "<sup>14</sup> Ég mun setja hann yfir hús mitt og konungsríki um aldur og hásæti hans skal ævinlega stöðugt standa.“",
			"15": "<sup>15</sup> Natan flutti Davíð öll þessi orð og þessa opinberun.",
			"16": "<sup>16</sup> Þá gekk Davíð konungur inn, settist frammi fyrir augliti Drottins og sagði: „Hver er ég, Drottinn Guð, og hvað er ætt mín fyrst þú hefur látið mig ná svona langt?",
			"17": "<sup>17</sup> Og samt nægði það þér ekki, Guð, því að þú hefur að auki gefið ætt minni, sem er þjónn þinn, fyrirheit til fjarlægrar framtíðar. Þú hefur látið mig sjá framtíðina betur en menn geta skilið, Drottinn Guð.",
			"18": "<sup>18</sup> Hvað getur Davíð sagt frekar um þann heiður sem þú hefur sýnt þjóni þínum? Þú þekkir þjón þinn,",
			"19": "<sup>19</sup> Drottinn. Vegna mín, þjóns þíns, hefur þú unnið þetta stórvirki samkvæmt vilja þínum og skýrt frá því.",
			"20": "<sup>20</sup> Enginn jafnast á við þig og enginn er Guð nema þú eins og við höfum heyrt með okkar eigin eyrum.",
			"21": "<sup>21</sup> Er nokkur þjóð á þessari jörð sem jafnast á við lýð þinn, Ísrael? Hvenær hefur Guð farið og leyst nokkra þjóð úr ánauð, víðfrægt nafn hennar og unnið fyrir hana mikil og ógnvekjandi verk? Þetta gerðir þú þegar þú hraktir á braut aðrar þjóðir undan lýð þínum sem þú hafðir leyst úr ánauð í Egyptalandi.",
			"22": "<sup>22</sup> Þú hefur gert lýð þinn, Ísrael, að eignarlýð þínum um aldur og ævi og þú, Drottinn, hefur gerst Guð hans.",
			"23": "<sup>23</sup> Drottinn, haltu ævinlega það fyrirheit sem þú gafst þjóni þínum og ætt hans. Gerðu það sem þú hefur lofað.",
			"24": "<sup>24</sup> Þá mun nafn þitt reynast traust og þá verður það ævinlega mikið og þá munu menn segja: Drottinn hersveitanna er Guð Ísraels. Ætt Davíðs, þjóns þíns, mun þá jafnan standa fyrir augliti þínu.",
			"25": "<sup>25</sup> Þú, Guð minn, þú hefur opinberað þjóni þínum þetta og sagt: Ég mun reisa þér hús. Þess vegna hef ég, þjónn þinn, vogað mér að bera þessa bæn fram fyrir þig.",
			"26": "<sup>26</sup> Drottinn, þú einn ert Guð. Þú hefur heitið mér, þjóni þínum, þessari farsæld.",
			"27": "<sup>27</sup> Blessaðu nú ætt þjóns þíns svo að hún sé ævinlega fyrir augliti þínu. Þar sem þú sjálfur, Drottinn, hefur blessað hana verður hún ævinlega blessuð.“"
		},
		"18": {
			"1": "<sup>1</sup> Eftir þetta vann Davíð sigur á Filisteum. Hann braut þá undir sig og náði Gat og þorpunum umhverfis hana undan yfirráðum Filistea.",
			"2": "<sup>2</sup> Einnig sigraði hann Móabíta sem urðu skattskyldir þegnar Davíðs.",
			"3": "<sup>3</sup> Davíð sigraði einnig Hadadeser, konung í Sóba, í nánd við Hamat þar sem Hadadeser var í leiðangri til að endurreisa veldi sitt við Efratfljót.",
			"4": "<sup>4</sup> Davíð vann af honum þúsund vagna, sjö þúsund vagnliða og tuttugu þúsund fótgönguliða. Hann lét skera í sundur hásinarnar á öllum hestunum, nema á hundrað sem hann hélt eftir.",
			"5": "<sup>5</sup> Þegar Aramearnir frá Damaskus komu Hadadeser, konungi í Sóba, til hjálpar felldi Davíð tuttugu og tvö þúsund Aramea.",
			"6": "<sup>6</sup> Því næst setti Davíð landstjóra yfir ríki Aramea í Damaskus og urðu þeir skattskyldir þegnar Davíðs. Þannig studdi Drottinn Davíð hvert sem hann fór.",
			"7": "<sup>7</sup> Gullskildina, sem menn Hadadesers áttu, tók Davíð herfangi og flutti til Jerúsalem.",
			"8": "<sup>8</sup> Davíð tók einnig mjög mikinn eir að herfangi úr borgum Hadadesers, Tebak og Kún. Úr því lét Salómon gera eirhafið, súlurnar og áhöld úr eir.",
			"9": "<sup>9</sup> Þegar Tóú, konungur í Hamat, frétti að Davíð hefði sigrað allan her Hadadesers, konungs í Sóba,",
			"10": "<sup>10</sup> sendi hann Haóram, son sinn, með gripi úr gulli, silfri og eir til Davíðs konungs. Hann lét flytja honum kveðju og heillaóskir vegna þess að hann hafði barist við Hadadeser og sigrað hann en Tóú hafði átt í stríði við Hadadeser.",
			"11": "<sup>11</sup> Davíð helgaði gripina Drottni ásamt silfrinu og gullinu sem hann hafði tekið að herfangi frá öllum þjóðunum, frá Edóm, Móab, Ammónítum, Filisteum og Amalek.",
			"12": "<sup>12</sup> Þegar Davíð sneri heim sigraði hann Edóm í Saltdalnum og felldi átján þúsund menn.",
			"13": "<sup>13</sup> Því næst setti hann landstjóra yfir Edóm og allir Edómítar urðu þegnar Davíðs. Þannig studdi Drottinn Davíð hvert sem hann fór.",
			"14": "<sup>14</sup> Davíð ríkti yfir öllum Ísrael. Hann lét alla þjóð sína njóta réttar og réttlætis.",
			"15": "<sup>15</sup> Jóab Serújuson var settur yfir herinn, Jósafat Ahílúðsson var kallari konungs,",
			"16": "<sup>16</sup> Sadók Akítúbsson og Abímelek Abjatarsson voru prestar og Savsa var ríkisritari.",
			"17": "<sup>17</sup> Benaja Jójadason var foringi Kreta og Pleta. Synir Davíðs voru fremstir þeirra sem voru konungi til aðstoðar."
		},
		"19": {
			"1": "<sup>1</sup> Nokkru síðar bar svo við að Nahas, konungur Ammóníta, dó og sonur hans varð konungur eftir hann.",
			"2": "<sup>2</sup> Þá sagði Davíð: „Ég ætla að sýna Hanún Nahassyni vináttu eins og faðir hans sýndi mér.“ Davíð sendi því menn til að færa honum samúðarkveðju vegna fráfalls föður hans. Þegar þjónar Davíðs komu til lands Ammóníta til Hanúns til þess að samhryggjast honum",
			"3": "<sup>3</sup> sögðu höfðingjar Ammóníta við Hanún: „Heldurðu að Davíð hafi það eitt í huga að heiðra minningu föður þíns þegar hann sendir boðbera með samúðarkveðjur? Eru þjónar hans ekki öllu heldur komnir til þín til að afla upplýsinga um landið og njósna um það til að geta eytt það?“",
			"4": "<sup>4</sup> Hanún lét þá grípa þjóna Davíðs, raka þá og skera föt þeirra til hálfs upp að þjóhnöppum og sendi þá frá sér gangandi.",
			"5": "<sup>5</sup> Þegar Davíð hafði verið tilkynnt hvernig komið var fyrir mönnunum sendi hann menn til móts við þá. Þar sem mennirnir höfðu verið niðurlægðir á svívirðilegan hátt lét konungur skila þessu til þeirra: „Verið um kyrrt í Jeríkó þar til skegg ykkar er sprottið aftur. Þá skuluð þið koma heim.“",
			"6": "<sup>6</sup> Ammónítum varð nú ljóst að þeir höfðu gert Davíð að hatursmanni sínum. Þá sendi Hanún ásamt Ammónítum þúsund talentur silfurs til þess að leigja stríðsvagna og vagnliða hjá Arameum handan við Efrat og Arameum í Maaka og Sóba.",
			"7": "<sup>7</sup> Þeir leigðu þrjátíu og tvö þúsund stríðsvagna og konunginn í Maaka ásamt her hans. Þeir komu og settu upp herbúðir framan við Medeba. Ammónítarnir söfnuðust einnig saman frá borgum sínum og komu til orrustunnar.",
			"8": "<sup>8</sup> Þegar Davíð frétti þetta sendi hann Jóab af stað ásamt öllum hernum, öllum vopnfærum mönnum.",
			"9": "<sup>9</sup> Þá héldu Ammónítar út úr borginni og fylktu sér til orrustu fyrir framan borgarhliðið en konungarnir, sem höfðu komið, stóðu einir sér úti á völlunum.",
			"10": "<sup>10</sup> Þegar Jóab varð ljóst að árásarlið ógnaði honum í bak og fyrir valdi hann menn úr úrvalsliði Ísraels og fylkti þeim gegn Arameum.",
			"11": "<sup>11</sup> Það sem eftir var af hernum setti hann undir stjórn Abísaí, bróður síns, sem fylkti honum gegn Ammónítum.",
			"12": "<sup>12</sup> Þá sagði Jóab: „Ef Aramear reynast öflugri en ég verðurðu að koma mér til hjálpar. En ef Ammónítar reynast þér yfirsterkari kem ég þér til hjálpar.",
			"13": "<sup>13</sup> Sýndu nú hugrekki. Við skulum berjast fyrir þjóð okkar og borgir Guðs okkar en Drottinn gerir það sem honum þóknast.“",
			"14": "<sup>14</sup> Jóab og lið hans réðst nú til atlögu gegn Arameum en þeir flýðu undan honum.",
			"15": "<sup>15</sup> Þegar Ammónítar sáu að Aramear voru flúnir lögðu þeir einnig á flótta undan Abísaí, bróður Jóabs, og tókst að komast inn í borgina. Því næst sneri Jóab heim til Jerúsalem.",
			"16": "<sup>16</sup> Þegar Arameum varð ljóst að þeir höfðu beðið ósigur fyrir Ísraelsmönnum sendu þeir menn til að kalla til herþjónustu Arameana sem bjuggu handan við Efratfljót. Sófak, hershöfðingi Hadadesers, var foringi þeirra.",
			"17": "<sup>17</sup> Jafnskjótt og Davíð var sagt þetta safnaði hann saman öllum Ísrael, fór yfir Jórdan, hélt á móti þeim og fylkti liði gegn þeim. Þar fylktu Aramear liði gegn Davíð og réðust til atlögu við hann.",
			"18": "<sup>18</sup> En Aramear urðu að flýja undan Ísraelsmönnum. Davíð eyddi sjö þúsund stríðsvögnum Aramea og felldi fjörutíu þúsund fótgönguliða. Hann felldi einnig Sófak, hershöfðingja þeirra.",
			"19": "<sup>19</sup> Þegar mönnum Hadadesers var orðið ljóst að þeir höfðu verið ofurliði bornir af Ísraelsmönnum sömdu þeir frið við Davíð og gengu honum á hönd. Þaðan í frá þorðu Aramear ekki að hjálpa Ammónítum."
		},
		"20": {
			"1": "<sup>1</sup> Um áramót, þegar konunga er siður að halda í hernað, fór Jóab með allan herinn í leiðangur. Hann eyddi land Ammóníta, settist um Rabba, en Davíð hélt kyrru fyrir í Jerúsalem. Jóab vann Rabba og reif hana niður.",
			"2": "<sup>2</sup> Því næst tók Davíð kórónu Ammónítakonungs af höfði hans og reyndist hún vera ein talenta gulls að þyngd, skreytt gimsteini, og var hún sett á höfuð Davíðs. Hann tók mjög mikið herfang úr borginni",
			"3": "<sup>3</sup> og flutti borgarbúa burt og lét þá vinna með steinsögum, járnhökum og járnöxum. Þannig fór hann með allar aðrar borgir Ammóníta. Því næst sneri Davíð ásamt öllum hernum heim til Jerúsalem.",
			"4": "<sup>4</sup> Eftir þetta kom til ófriðar við Filistea við Geser. Þá drap Sibbekaí frá Húsa Sippaí, sem var af ætt Refaíta, og voru þeir ofurliði bornir.",
			"5": "<sup>5</sup> Enn á ný kom til bardaga við Filistea. Elkana Jaírsson drap þá Lahmí, bróður Golíats frá Gat, en spjótskaft hans var eins og vefjarrifur í vefstól.",
			"6": "<sup>6</sup> Þá varð enn bardagi við Gat. Þar var risavaxinn maður sem hafði tólf fingur og tólf tær. Einnig hann var af ætt Refa.",
			"7": "<sup>7</sup> Er hann hæddist að Ísrael drap Jónatan hann en hann var sonur Símea, bróður Davíðs.",
			"8": "<sup>8</sup> Þessir menn voru afkomendur Refa frá Gat. Þeir féllu allir fyrir hendi Davíðs og manna hans."
		},
		"21": {
			"1": "<sup>1</sup> Satan reis upp gegn Ísrael. Hann æsti Davíð til að láta telja Ísraelsmenn.",
			"2": "<sup>2</sup> Davíð skipaði því Jóab og höfðingjum þjóðarinnar svo: „Haldið af stað og teljið Ísraelsmenn frá Beerseba til Dan. Færið mér svo niðurstöðuna því að ég vil vita hve margir þeir eru.“",
			"3": "<sup>3</sup> Þá sagði Jóab við konung: „Drottinn fjölgi þjóð sinni hundraðfalt frá því sem nú er. En eru þeir ekki allir þegnar þínir, herra minn og konungur? Hvers vegna viltu þetta, herra? Hvers vegna á þetta að leiða sekt yfir Ísrael?“",
			"4": "<sup>4</sup> Skipun konungs mátti sín meira en skoðun Jóabs. Jóab hélt því af stað, fór um allt Ísraelsland og kom síðan heim til Jerúsalem.",
			"5": "<sup>5</sup> Hann tilkynnti Davíð niðurstöðu manntalsins: Í öllum Ísrael voru ein milljón og eitt hundrað þúsund menn sem gátu brugðið sverði en í Júda voru fjögur hundruð og sjötíu þúsund menn sem gátu brugðið sverði.",
			"6": "<sup>6</sup> En hann hafði ekki talið Leví og Benjamín með hinum af því að hann hafði andstyggð á skipun konungs.",
			"7": "<sup>7</sup> Þetta verk var illt í augum Guðs. Þess vegna laust hann Ísrael.",
			"8": "<sup>8</sup> Davíð bað því til Drottins á þessa leið: „Ég hef brotið alvarlega af mér með því að vinna þetta verk. Drottinn, fyrirgefðu nú þræli þínum því að ég hef farið heimskulega að ráði mínu.“",
			"9": "<sup>9</sup> Þá talaði Drottinn við Gað, sjáanda Davíðs, og sagði:",
			"10": "<sup>10</sup> „Far þú og seg við Davíð: Svo segir Drottinn: Ég set þér þrjá kosti: Veldu einn þeirra og ég mun láta það yfir þig ganga.“",
			"11": "<sup>11</sup> Því næst gekk Gað fyrir Davíð og sagði við hann: „Svo segir Drottinn: Veldu eitt af þessu:",
			"12": "<sup>12</sup> Hungursneyð í þrjú ár, flótta í þrjá mánuði undan óvinum þínum og sverðshöggum fjandmanna þinna eða sverð Drottins, það er drepsótt í landinu í þrjá daga. Þá mun engill Drottins herja á öllu landsvæði Ísraels. Íhugaðu nú vandlega hvaða svar ég á að færa þeim sem sendi mig.“",
			"13": "<sup>13</sup> Davíð svaraði Gað: „Nú er úr vöndu að ráða. Helst vil ég falla í hendur Drottins því að mikil er miskunn hans en í hendur manna vil ég ekki falla.“",
			"14": "<sup>14</sup> Þá sendi Drottinn drepsótt yfir Ísrael og dóu þá sjötíu þúsund manns í Ísrael.",
			"15": "<sup>15</sup> Guð sendi engil til Jerúsalem til þess að eyða borgina. En þegar hann var í þann veginn að eyða hana sá Drottinn það og iðraðist þessa böls og sagði við engilinn sem eyddi fólkinu: „Nú er nóg komið. Dragðu að þér höndina.“ Engill Drottins stóð þá við þreskivöll Jebúsítans Ornans.",
			"16": "<sup>16</sup> Þegar Davíð leit upp sá hann engil Drottins standa milli himins og jarðar með brugðið sverð í hendi og var því beint gegn Jerúsalem. Davíð og öldungarnir féllu þá fram á ásjónur sínar, klæddir hærusekkjum.",
			"17": "<sup>17</sup> Davíð sagði við Guð: „Er það ekki ég sem hef gefið skipun um að þjóðin skuli talin? Það er ég sem hef syndgað, það er ég sem hef brotið af mér. En hvað hefur þessi hjörð hérna gert? Drottinn, Guð minn. Lyftu hendi þinni gegn mér og fjölskyldu minni en greiddu þjóð þinni ekki högg.“",
			"18": "<sup>18</sup> Nú skipaði engill Drottins Gað að segja Davíð að fara upp eftir til þess að reisa Drottni altari á þreskivelli Jebúsítans Ornans.",
			"19": "<sup>19</sup> Davíð gekk þá upp eftir eins og Gað hafði boðið í nafni Drottins.",
			"20": "<sup>20</sup> Ornan var að þreskja hveiti, hafði snúið sér við og séð engil Drottins en fjórir synir hans, sem voru með honum, höfðu falið sig.",
			"21": "<sup>21</sup> Þegar Davíð kom til Ornans og hann leit upp og sá Davíð gekk hann út af þreskivellinum og féll fram á ásjónu sína frammi fyrir Davíð.",
			"22": "<sup>22</sup> Þá sagði Davíð við Ornan: „Láttu mig fá landið sem þreskivöllurinn er á. Þar ætla ég að reisa Drottni altari. Seldu mér það fullu verði svo að drepsóttinni létti af þjóðinni.“",
			"23": "<sup>23</sup> Ornan svaraði Davíð: „Taktu það. Þú, herra minn og konungur, skalt gera það sem þú vilt. Ég gef þér hér með nautin í brennifórnir, þreskisleðana sem eldivið og hveitið í kornfórn. Allt þetta gef ég þér.“",
			"24": "<sup>24</sup> Davíð konungur svaraði Ornan: „Nei, ég vil kaupa það af þér fullu verði. Ég get ekki tekið eign þína og fært Drottni brennifórn sem ég hef ekki greitt.“",
			"25": "<sup>25</sup> Því næst greiddi Davíð Ornan sex hundruð sikla gulls fyrir landið.",
			"26": "<sup>26</sup> Þarna reisti Davíð Drottni altari og færði brennifórnir og heillafórn. Hann ákallaði Drottin og hann svaraði honum með eldi sem kom á brennifórnaraltarið niður af himni.",
			"27": "<sup>27</sup> Þá skipaði Drottinn englinum að slíðra sverð sitt.",
			"28": "<sup>28</sup> Þegar Davíð hafði komist að því að Drottinn bænheyrði hann á þreskivelli Ornans gat hann fært honum fórnir þar.",
			"29": "<sup>29</sup> Bústaður Drottins, sem Móse hafði gert í eyðimörkinni, og brennifórnaraltarið voru á þessum tíma í Gíbeon.",
			"30": "<sup>30</sup> En Davíð gat ekki gengið fyrir auglit Guðs til að leita svara hans af því að hann óttaðist sverð engils Drottins."
		},
		"22": {
			"1": "<sup>1</sup> Þá sagði Davíð: „Þetta er hús Drottins Guðs og þetta er brennifórnaraltari Ísraels.“",
			"2": "<sup>2</sup> Því næst skipaði Davíð að safna skyldi saman aðkomumönnunum í landi Ísraels. Hann gerði þá að steinsmiðum sem áttu að höggva til steina til þess að reisa hús Guðs.",
			"3": "<sup>3</sup> Davíð lét einnig taka til járn í naglana í vængjahurðir hliðanna og í slárnar og svo mikinn eir að ekki varð veginn.",
			"4": "<sup>4</sup> Einnig lét hann vinna mikið magn sedrusbjálka sem íbúar í Sídon og Týrus höfðu sent honum.",
			"5": "<sup>5</sup> Davíð hugsaði með sér: „Salómon, sonur minn, er ungur og óreyndur en húsið, sem reisa á Drottni, skal verða mikið og stórfenglegt og vekja aðdáun um öll lönd. Þess vegna ætla ég að undirbúa byggingu þess.“ Davíð var vel á veg kominn með undirbúninginn áður en hann dó.",
			"6": "<sup>6</sup> Síðan kallaði hann Salómon, son sinn, fyrir sig og fól honum að reisa Drottni, Guði Ísraels, hús.",
			"7": "<sup>7</sup> Davíð sagði við Salómon: „Sonur minn. Ég hef sjálfur haft í huga að reisa nafni Drottins, Guðs míns, hús.",
			"8": "<sup>8</sup> En svohljóðandi orð Drottins kom til mín: Þú hefur úthellt miklu blóði og háð mikil stríð. Þú skalt ekki reisa nafni mínu hús af því að þú hefur úthellt miklu blóði á jörðina fyrir augliti mínu.",
			"9": "<sup>9</sup> Hins vegar hefur þér fæðst sonur. Hann verður maður friðarins: Ég mun veita honum frið fyrir öllum fjandmönnum hans allt umhverfis. Nafn hans er Salómon og ég mun veita Ísrael hvíld og velgengni á stjórnartíma hans.",
			"10": "<sup>10</sup> Hann skal reisa nafni mínu hús og hann verður mér sonur og ég verð honum faðir. Ég mun ævinlega styðja hans konunglega hásæti.",
			"11": "<sup>11</sup> Sonur minn. Nú sé Drottinn með þér svo að þér megi takast að reisa hús Drottins, Guðs þíns, eins og hann hefur sagt um þig að þú munir gera.",
			"12": "<sup>12</sup> Drottinn gefi þér skynsemi og skilning. Hann mun setja þig yfir Ísrael og hjálpa þér að halda lögmál Drottins, Guðs þíns.",
			"13": "<sup>13</sup> Þér mun takast þetta ef þú gætir þess að fara eftir lögum og reglum sem Drottinn fól Móse handa Ísrael. Vertu hugrakkur og sterkur. Óttastu ekki og láttu ekki hugfallast.",
			"14": "<sup>14</sup> Þrátt fyrir erfiðleika mína hef ég lagt fram hundrað þúsund talentur gulls og milljón talentur silfurs til húss Drottins, auk þess svo mikinn eir og járn að ekki verður vegið. Ég hef einnig unnið við og steina og þú munt geta bætt við það.",
			"15": "<sup>15</sup> Þú hefur fjölmarga handverksmenn, steinsmiði, múrara, trésmiði og óteljandi reynda hagleiksmenn sem geta unnið að allri smíði",
			"16": "<sup>16</sup> úr gulli, silfri, eir og járni. Byrjaðu því verkið þegar í stað og Drottinn sé með þér.“",
			"17": "<sup>17</sup> Því næst skipaði Davíð öllum höfðingjum Ísraels að hjálpa Salómon, syni sínum:",
			"18": "<sup>18</sup> „Hefur Drottinn, Guð ykkar, ekki verið með ykkur og veitt ykkur frið allt um kring? Hann hefur selt íbúa landsins í hendur mér svo að landið hefur verið lagt undir Drottin og lýð hans.",
			"19": "<sup>19</sup> Einbeitið ykkur nú að því að leita Drottins, Guðs ykkar. Takið að reisa helgidóm Drottins, Guðs, til þess að unnt verði að flytja sáttmálsörk Drottins og hin heilögu áhöld Guðs í húsið sem reist verður nafni Drottins.“"
		},
		"23": {
			"1": "<sup>1</sup> Þegar Davíð var orðinn gamall og saddur lífdaga gerði hann Salómon, son sinn, að konungi yfir Ísrael.",
			"2": "<sup>2</sup> Hann safnaði því saman öllum leiðtogum Ísraels, prestum og Levítum.",
			"3": "<sup>3</sup> Levítarnir, þrítugir og eldri, höfðu verið taldir. Var fjöldi þeirra þrjátíu og átta þúsund.",
			"4": "<sup>4</sup> Tuttugu og fjögur þúsund þeirra áttu að stjórna vinnunni í húsi Drottins, sex þúsund áttu að vera embættismenn og dómarar,",
			"5": "<sup>5</sup> fjögur þúsund hliðverðir og fjögur þúsund áttu að lofa Drottin með hljóðfærunum sem Davíð hafði látið gera til lofgjörðarinnar.",
			"6": "<sup>6</sup> Davíð skipti Levítunum í flokka eftir sonum Leví: Gerson, Kahat og Merarí.",
			"7": "<sup>7</sup> Til niðja Gersons töldust Laedan og Símeí.",
			"8": "<sup>8</sup> Jehíel var elstur af sonum Laedans, þá Setam og Jóel, alls þrír.",
			"9": "<sup>9</sup> Synir Jehíels voru Selómót, Hasíel og Haran, alls þrír. Þeir voru ættarhöfðingjar Laedans.",
			"10": "<sup>10</sup> Synir Símeí voru Jahat, Sína, Jeús og Bería. Þetta voru fjórir synir Símeí.",
			"11": "<sup>11</sup> Jahat var elstur og Sísa næstur. Jeús og Bería áttu aðeins fáa syni. Þeir mynduðu því aðeins eina fjölskyldu, einn þjónustuflokk.",
			"12": "<sup>12</sup> Synir Kahats voru Amram, Jísehar, Hebron og Ússíel, alls fjórir.",
			"13": "<sup>13</sup> Aron og Móse voru synir Amrams. Aron var sérstaklega valinn til þess að sjá ævinlega um hið háheilaga ásamt sonum sínum, brenna reykelsisfórn frammi fyrir augliti Drottins og þjóna honum og blessa í nafni hans um aldur.",
			"14": "<sup>14</sup> En synir guðsmannsins Móse töldust til ættbálks Leví.",
			"15": "<sup>15</sup> Synir Móse voru Gersóm og Elíeser.",
			"16": "<sup>16</sup> Sebúel var elstur af sonum Gersóms.",
			"17": "<sup>17</sup> Rehabja höfðingi var sonur Elíesers. Elíeser átti ekki aðra syni en synir Rehabja voru mjög margir.",
			"18": "<sup>18</sup> Selomít var elsti sonur Jísehars.",
			"19": "<sup>19</sup> Synir Hebrons voru Jería, sem var elstur, Amarja næstur, Jakasíel þriðji og Jekameam fjórði.",
			"20": "<sup>20</sup> Elsti sonur Ússíels var Míka og Jessía næstur honum.",
			"21": "<sup>21</sup> Synir Merarí voru Mahlí og Músí. Synir Mahlí voru Eleasar og Kís.",
			"22": "<sup>22</sup> Þegar Eleasar dó hafði hann ekki eignast neina syni, aðeins dætur. Synir Kíss, frændur þeirra, tóku þær sér fyrir konur.",
			"23": "<sup>23</sup> Synir Músí voru Mahlí, Eder og Jeremót, alls þrír.",
			"24": "<sup>24</sup> Þetta voru niðjar Leví, flokkaðir eftir fjölskyldum sínum, ættarhöfðingjar þeirra eins og þeir voru skráðir með nafni og taldir hver um sig. Þeir sem voru tuttugu ára og eldri sáu um þjónustuna í húsi Drottins.",
			"25": "<sup>25</sup> Davíð hafði sagt: „Drottinn, Guð Ísraels, hefur veitt þjóð sinni frið og er sestur að í Jerúsalem um alla framtíð.",
			"26": "<sup>26</sup> Levítarnir þurfa því ekki lengur að bera tjaldið og öll áhöldin sem þarf til þjónustunnar þar,“",
			"27": "<sup>27</sup> því að samkvæmt síðustu fyrirmælum Davíðs voru Levítarnir taldir tuttugu ára og eldri.",
			"28": "<sup>28</sup> Þeir hlutu stöðu við hlið sona Arons við þjónustu í húsi Drottins, til að sjá um forgarðana og herbergin og hreinsun alls þess sem heilagt er. Einnig eiga þeir að þjóna í húsi Drottins,",
			"29": "<sup>29</sup> sjá um skoðunarbrauðin, fínmalað mjöl í kornfórnir og ósýrð brauð, pönnur og bakstur. Þeir skulu einnig sjá um vökvamál og lengdarmál.",
			"30": "<sup>30</sup> Þeim ber að taka sér stöðu kvölds og morgna til að þakka Drottni og lofa hann.",
			"31": "<sup>31</sup> Við allar brennifórnir, sem færðar eru Drottni á hvíldardögum, tunglkomudögum og hátíðum, skulu þeir jafnan þjóna allir frammi fyrir Drottni eins og þeim ber.",
			"32": "<sup>32</sup> Þeir skulu hafa á hendi þjónustu í opinberunartjaldinu, í helgidóminum og við syni Arons, embættisbræður sína, þegar þeir þjóna í húsi Drottins."
		},
		"24": {
			"1": "<sup>1</sup> Sonum Arons var einnig skipað í flokka. Synir Arons voru Nadab, Abíhú, Eleasar og Ítamar.",
			"2": "<sup>2</sup> Nadab og Abíhú dóu á undan föður sínum. Þar sem þeir áttu enga syni tóku Eleasar og Ítamar við prestsembættinu.",
			"3": "<sup>3</sup> Davíð skipaði þeim í þjónustuflokka ásamt Sadók, sem var afkomandi Eleasars, og Ahímelek sem var af Ítamar kominn.",
			"4": "<sup>4</sup> Þar sem fleiri höfðingjar reyndust vera meðal niðja Eleasars en meðal niðja Ítamars var sextán ættarhöfðingjum úthlutað til niðja Eleasars en aðeins átta til niðja Ítamars.",
			"5": "<sup>5</sup> Allir flokkarnir voru valdir með hlutkesti því að hinir heilögu höfðingjar og höfðingjar Guðs voru bæði niðjar Eleasars og Ítamars.",
			"6": "<sup>6</sup> Semaja Netaneelsson, skrifari af ætt Leví, skrifaði þá upp í viðurvist konungs, höfðingjanna, Sadóks prests, Abjatars Ahímelekssonar og ættarhöfðingja prestanna og niðja Leví. Voru tvær fjölskyldur valdar hverju sinni af ætt Eleasars en ein af ætt Ítamars.",
			"7": "<sup>7</sup> Fyrsta hlutkestið féll á Jójaríb, annað á Jejdaja,",
			"8": "<sup>8</sup> þriðja á Harím, fjórða á Seórím,",
			"9": "<sup>9</sup> fimmta á Malkía, sjötta á Mijamín,",
			"10": "<sup>10</sup> sjöunda á Hakkos, áttunda á Abía,",
			"11": "<sup>11</sup> níunda á Jesúa, tíunda á Sekanja,",
			"12": "<sup>12</sup> ellefta á Eljasíb, tólfta á Jakím,",
			"13": "<sup>13</sup> þrettánda á Húppa, fjórtánda á Jesebeab,",
			"14": "<sup>14</sup> fimmtánda á Bilga, sextánda á Immer,",
			"15": "<sup>15</sup> sautjánda á Hesír, átjánda á Happísses,",
			"16": "<sup>16</sup> nítjánda á Pelashja, tuttugasta á Jeheskel,",
			"17": "<sup>17</sup> tuttugasta og fyrsta á Jakín, tuttugasta og annað á Gamúl,",
			"18": "<sup>18</sup> tuttugasta og þriðja á Delaja, tuttugasta og fjórða á Maasja.",
			"19": "<sup>19</sup> Þetta var skipting þjónustu þeirra. Eftir henni áttu þeir að koma til húss Drottins samkvæmt boði því sem þeir höfðu fengið frá Aron, föður sínum, og Drottinn, Guð Ísraels, hafði lagt fyrir hann.",
			"20": "<sup>20</sup> Varðandi aðra niðja Leví, þá var Súbael einn sona Amrams. Jehdeja var einn af sonum Súbaels.",
			"21": "<sup>21</sup> Jissía var elstur af sonum Rehabja.",
			"22": "<sup>22</sup> Selómót var einn af niðjum Jísehars. Jahat var einn af sonum Selómóts.",
			"23": "<sup>23</sup> Amarja var einn af sonum Jería, annar var Jehasíel, þriðji Jekameam.",
			"24": "<sup>24</sup> Míka var sonur Ússíels, Samír var einn af sonum Míka.",
			"25": "<sup>25</sup> Jissía var bróðir Míka. Sakaría var einn af sonum Jissía.",
			"26": "<sup>26</sup> Mahlí og Músí voru synir Merarí. Sonur Jaasía var Baní.",
			"27": "<sup>27</sup> Niðjar Merarí, komnir af Jaasía, voru Baní, Sóham, Sakkúr og Íbrí.",
			"28": "<sup>28</sup> Eleasar, sem var barnlaus, var af Maklí kominn.",
			"29": "<sup>29</sup> Af Kís var Jerahmeel, sonur Kíss.",
			"30": "<sup>30</sup> Synir Músí voru Mahlí, Eder og Jerímót. Þetta voru niðjar Leví, flokkaðir eftir fjölskyldum sínum.",
			"31": "<sup>31</sup> Þeir vörpuðu einnig hlutkesti eins og bræður þeirra, synir Arons, í viðurvist Davíðs konungs, Sadóks og Ahímeleks og ættarhöfðingja prestanna og Levítanna, bæði fjölskyldna hins fremsta þeirra og yngsta bróðurins."
		},
		"25": {
			"1": "<sup>1</sup> Davíð og herforingjarnir völdu niðja Asafs, Hemans og Jedútúns til að annast lofgjörð að hætti spámanna og leika á gígjur, hörpur og málmgjöll. Hér á eftir fer skrá yfir þá menn sem önnuðust þessa þjónustu:",
			"2": "<sup>2</sup> Af niðjum Asafs: Sakkúr, Jósef, Netanja og Asarela. Synir Asafs voru undir stjórn Asafs sem lék að hætti spámanna undir stjórn konungs.",
			"3": "<sup>3</sup> Af Jedútún: synir Jedútúns, þeir Gedalja, Serí, Jesaja, Símeí, Hasabja og Mattitja, alls sex. Þeir voru undir stjórn Jedútúns, föður síns, sem lék Drottni þökk og lof á gígju að hætti spámanna.",
			"4": "<sup>4</sup> Af Heman: synir Hemans, þeir Búkkía, Mattanja, Ússíel, Sebúel, Jerímót, Hananja, Hananí, Elíata, Giddaltí, Rómamtí Eser, Josbekasa, Mallótí, Hótír og Mahasíót.",
			"5": "<sup>5</sup> Þeir voru allir synir Hemans, sjáanda konungs, samkvæmt fyrirheiti Guðs um að efla vald hans. Guð gaf Heman fjórtán syni og þrjár dætur.",
			"6": "<sup>6</sup> Allir þessir menn voru undir stjórn feðra sinna við sönginn í húsi Drottins. Þeir léku á málmgjöll, hörpur og gígjur við þjónustuna í húsi Guðs að fyrirmælum konungs.",
			"7": "<sup>7</sup> Þeir voru tvö hundruð áttatíu og átta talsins ásamt embættisbræðrum sínum sem hafði verið kennt að syngja Drottni söngva. Allt voru þetta þjálfaðir menn.",
			"8": "<sup>8</sup> Þeir köstuðu hlutkesti um skipan þjónustunnar, jafnt fyrir unga sem aldna, þá sem voru fullnuma og hina sem voru nemar.",
			"9": "<sup>9</sup> Fyrsti hluturinn féll á Jósef, annar á Gedalja, bræður hans og syni, tólf alls,",
			"10": "<sup>10</sup> þriðji á Sakkúr, syni hans og bræður, tólf alls,",
			"11": "<sup>11</sup> fjórði á Serí, syni hans og bræður, tólf alls,",
			"12": "<sup>12</sup> fimmti á Netanja, syni hans og bræður, tólf alls,",
			"13": "<sup>13</sup> sjötti á Búkkía, syni hans og bræður, tólf alls,",
			"14": "<sup>14</sup> sjöundi á Jesarela, syni hans og bræður, tólf alls,",
			"15": "<sup>15</sup> áttundi á Jesaja, syni hans og bræður, tólf alls,",
			"16": "<sup>16</sup> níundi á Mattanja, syni hans og bræður, tólf alls,",
			"17": "<sup>17</sup> tíundi á Símeí, syni hans og bræður, tólf alls,",
			"18": "<sup>18</sup> ellefti á Asareel, syni hans og bræður, tólf alls,",
			"19": "<sup>19</sup> tólfti á Hasabja, syni hans og bræður, tólf alls,",
			"20": "<sup>20</sup> þrettándi á Súbael, syni hans og bræður, tólf alls,",
			"21": "<sup>21</sup> fjórtándi á Mattitja, syni hans og bræður, tólf alls,",
			"22": "<sup>22</sup> fimmtándi á Jerímót, syni hans og bræður, tólf alls,",
			"23": "<sup>23</sup> sextándi á Hananja, syni hans og bræður, tólf alls,",
			"24": "<sup>24</sup> sautjándi á Josbekasa, syni hans og bræður, tólf alls,",
			"25": "<sup>25</sup> átjándi á Hananí, syni hans og bræður, tólf alls,",
			"26": "<sup>26</sup> nítjándi á Mallótí, syni hans og bræður, tólf alls,",
			"27": "<sup>27</sup> tuttugasti á Elíjata, syni hans og bræður, tólf alls,",
			"28": "<sup>28</sup> tuttugasti og fyrsti á Hótír, syni hans og bræður, tólf alls,",
			"29": "<sup>29</sup> tuttugasti og annar á Giddaltí, syni hans og bræður, tólf alls,",
			"30": "<sup>30</sup> tuttugasti og þriðji á Mahasíót, syni hans og bræður, tólf alls,",
			"31": "<sup>31</sup> tuttugasti og fjórði á Rómamti Eser, syni hans og bræður, tólf alls."
		},
		"26": {
			"1": "<sup>1</sup> Um flokka hliðvarðanna er þetta að segja: Einn af Kóraítum, Meselemja Kóreson af Asafsætt, var einn þeirra.",
			"2": "<sup>2</sup> Synir Meselemja voru frumburðurinn Sakaría, Jedíael var annar, Sebadja var sá þriðji, Jatníel sá fjórði,",
			"3": "<sup>3</sup> Elam sá fimmti, Jóhanan sá sjötti og Elíóenaí sá sjöundi.",
			"4": "<sup>4</sup> Óbeð Edóm átti einnig syni og voru þeir frumburðurinn Semaja, Jósabad var annar, Jóa var sá þriðji, Sakar fjórði, Netaneel fimmti,",
			"5": "<sup>5</sup> Ammíel sjötti, Íssakar sjöundi, Pegúlletaí áttundi því að Guð hafði blessað hann.",
			"6": "<sup>6</sup> Semaja, syni hans, fæddust einnig synir. Þeir urðu leiðtogar ætta sinna því að þeir voru dugandi menn.",
			"7": "<sup>7</sup> Synir Semaja voru Otní, Refael, Óbeð og Elsabat. Bræður Elsabats voru Elíhú og Semakja sem einnig voru dugandi menn.",
			"8": "<sup>8</sup> Allir þessir menn voru niðjar Óbeðs Edóms og dugandi menn, eins og synir þeirra og bræður, og hæfir til þjónustunnar, alls sextíu og tveir niðjar Óbeðs Edóms.",
			"9": "<sup>9</sup> Meselemja átti einnig dugandi syni og bræður, alls átján.",
			"10": "<sup>10</sup> Hósa, sem var afkomandi Merarí, fæddust einnig synir. Simrí var þeirra fremstur en faðir hans hafði gert hann að höfðingja þótt hann væri ekki frumburður.",
			"11": "<sup>11</sup> Hilkía var annar, Tebalja var sá þriðji og Sakaría fjórði. Hósa átti alls þrettán syni og bræður.",
			"12": "<sup>12</sup> Þessir flokkar hliðvarða gegndu þjónustu undir stjórn höfðingja sinna. Þeir þjónuðu í húsi Drottins ásamt bræðrum sínum",
			"13": "<sup>13</sup> og vörpuðu hlutkesti um hvert hlið fyrir sig, hvort heldur fjölskylda þeirra var lítil eða stór.",
			"14": "<sup>14</sup> Hluturinn fyrir austurhliðið féll á Selemja. Þeir vörpuðu einnig hlutkesti fyrir Sakaría, son hans, sem var skynsamur ráðgjafi, og kom norðurhliðið í hlut hans",
			"15": "<sup>15</sup> en suðurhliðið í hlut Óbeðs Edóms og geymsluhúsið í hlut sona hans.",
			"16": "<sup>16</sup> Vesturhliðið kom í hlut Hósa og Sallekhliðið við götuna upp eftir. Allar varðstöður voru eins:",
			"17": "<sup>17</sup> Daglega stóðu sex menn vörð gegnt austri, fjórir gegnt norðri, fjórir gegnt suðri og tveir við hvort geymsluhúsanna.",
			"18": "<sup>18</sup> Við Parbar gegnt vestri stóðu fjórir vörð við götuna en tveir við Parbar.",
			"19": "<sup>19</sup> Þetta voru flokkar hliðvarðanna, niðjar Kóra og Merarí.",
			"20": "<sup>20</sup> Levítarnir, ættbræður þeirra, sem gættu fjársjóða húss Guðs og helgigjafanna,",
			"21": "<sup>21</sup> voru synir Laedans, afkomanda Gersons. Ættarhöfðingjar Laedans, afkomanda Gersons, Jehíel og synir hans",
			"22": "<sup>22</sup> ásamt Setam og Jóel, bróður hans, gættu fjársjóða húss Guðs.",
			"23": "<sup>23</sup> Af niðjum Amrams, Jísehars, Hebrons og Ússíels",
			"24": "<sup>24</sup> var Sebúel Gersómsson Mósesonar yfirvörður fjársjóðanna.",
			"25": "<sup>25</sup> Ættbræður hans, niðjar Elíesers, voru Rehabja, sonur hans, Jesaja, sonur hans, Jóram, sonur hans, Sikrí, sonur hans, og Selómít, sonur hans.",
			"26": "<sup>26</sup> Selómít þessi og bræður hans gættu allra fjársjóða helgigjafanna sem Davíð konungur hafði helgað ásamt höfðingjum ættanna, foringjum þúsund manna liða, foringjum hundrað manna liða og foringjum hersins.",
			"27": "<sup>27</sup> Þeir höfðu tekið nokkuð af herfangi og helgað það til þess að það kæmi að notum í húsi Drottins.",
			"28": "<sup>28</sup> Þeir gættu einnig alls þess sem sjáandinn Samúel, Sál Kísson, Abner Nersson og Jóab Serújuson höfðu helgað. Allt sem helgað hafði verið var fengið Selómít og bræðrum hans til varðveislu.",
			"29": "<sup>29</sup> Kenanja og synir hans voru ráðnir til að sinna störfum utan helgidómsins sem embættismenn og dómarar.",
			"30": "<sup>30</sup> Hasabja og bræðrum hans, sem voru niðjar Hebrons, sautján hundruð dugandi mönnum, var falin umsjón með málefnum Drottins og þjónustu við konunginn í Ísrael, vestan við Jórdan.",
			"31": "<sup>31</sup> Jería var höfðingi niðja Hebrons. Á fertugasta stjórnarári Davíðs voru skrár yfir fjölskyldur niðja Hebrons kannaðar og fundust þá dugandi menn í Jasír og Gíleað.",
			"32": "<sup>32</sup> Bræður Jería voru dugandi menn, höfðingjar tvö þúsund og sjö hundruð ætta. Í öllum þeim málum, sem snertu Guð og konunginn, setti Davíð konungur þá yfir niðja Rúbens, Gaðs og hálfan ættbálk Manasse."
		},
		"27": {
			"1": "<sup>1</sup> Þetta er skrá yfir fjölda þeirra Ísraelsmanna, ættarhöfðingja, foringja þúsund og hundrað manna herflokka og alla þá embættismenn sem þjónuðu konunginum í öllu sem snerti flokkana. Þeir komu og fóru mánuð eftir mánuð, alla mánuði ársins. Í hverjum flokki voru 24.000 menn.",
			"2": "<sup>2</sup> Jasóbeam Sabdíelsson var fyrir fyrsta flokknum í fyrsta mánuðinum. Í flokki hans voru 24.000 menn.",
			"3": "<sup>3</sup> Hann var einn af niðjum Peresar og yfirmaður allra foringja hersins í fyrsta mánuðinum.",
			"4": "<sup>4</sup> Dódaí, af ætt Ahóhí, var fyrir flokknum í öðrum mánuði. Miklót var honum næstur en í flokki hans voru 24.000 menn.",
			"5": "<sup>5</sup> Benaja, sonur Jójada æðsta prests, var þriðji hershöfðinginn í þriðja mánuðinum. Í flokki hans voru 24.000 menn.",
			"6": "<sup>6</sup> Benaja þessi var hetja, einn hinna 30, og foringi hinna 30 og flokks síns. Sonur hans var Ammísabad.",
			"7": "<sup>7</sup> Asael, bróðir Jóabs, var fjórði hershöfðinginn í fjórða mánuðinum og á eftir honum Sebadja, sonur hans. Í flokki hans voru 24.000 menn.",
			"8": "<sup>8</sup> Samhút liðsforingi, afkomandi Seraks, var sá fimmti í fimmta mánuðinum. Í flokki hans voru 24.000 menn.",
			"9": "<sup>9</sup> Íra Íkkesson frá Tekóa var sá sjötti í sjötta mánuðinum. Í flokki hans voru 24.000 menn.",
			"10": "<sup>10</sup> Heles frá Pelet, afkomandi Efraíms, var sá sjöundi í sjöunda mánuðinum. Í flokki hans voru 24.000 menn.",
			"11": "<sup>11</sup> Sibbekaí frá Húsa, afkomandi Seraks, var sá áttundi í áttunda mánuðinum. Í flokki hans voru 24.000 menn.",
			"12": "<sup>12</sup> Abíeser frá Anatót, afkomandi Benjamíns, var sá níundi í níunda mánuðinum. Í flokki hans voru 24.000 menn.",
			"13": "<sup>13</sup> Maharaí frá Netófa, afkomandi Seraks, var sá tíundi í tíunda mánuðinum. Í flokki hans voru 24.000 menn.",
			"14": "<sup>14</sup> Benaja frá Píraton, afkomandi Efraíms, var sá ellefti í ellefta mánuðinum. Í flokki hans voru 24.000 menn.",
			"15": "<sup>15</sup> Heldaí frá Netófa, afkomandi Otníels, var sá tólfti í tólfta mánuðinum. Í flokki hans voru 24.000 menn.",
			"16": "<sup>16</sup> Höfðingjar ættbálka Ísraels: Elíeser Síkríson var höfðingi yfir niðjum Rúbens, Sefatja Maakason var yfir niðjum Símeons,",
			"17": "<sup>17</sup> Hasabja Kemúelsson var yfir Leví og Sadók var yfir Aron.",
			"18": "<sup>18</sup> Elíhú, einn af bræðrum Davíðs, var yfir Júda, Omrí Míkaelsson var yfir Íssakar,",
			"19": "<sup>19</sup> Jismaja Óbadíason var yfir Sebúlon, Jerímót Asríelsson var yfir Naftalí,",
			"20": "<sup>20</sup> Hósea Asasjason var yfir Efraím, Jóel Pedajason var yfir öðrum helmingi ættbálks Manasse",
			"21": "<sup>21</sup> en Jiddó Sakaríason var yfir hinum helmingi ættbálks Manasse í Gíleað og",
			"22": "<sup>22</sup> Asarel Jeróhamsson yfir Dan. Þetta voru höfðingjar ættbálka Ísraels.",
			"23": "<sup>23</sup> Davíð hafði ekki látið skrá fjölda þeirra sem voru tvítugir og yngri því að Drottinn hafði heitið því að gera Ísrael jafnfjölmennan og stjörnur himins.",
			"24": "<sup>24</sup> Jóab Serújuson hafði byrjað talninguna en ekki lokið henni því að refsidómur hafði komið yfir Ísrael vegna hennar. Þess vegna var endanleg tala ekki skráð í annála Davíðs konungs.",
			"25": "<sup>25</sup> Asmavett Adíelsson var settur yfir birgðageymslur konungs og Jónatan Ússíason yfir birgðageymslurnar úti á landsbyggðinni, í borgum, þorpum og virkjum.",
			"26": "<sup>26</sup> Esrí Kelúbsson var settur yfir verkamennina sem unnu á ökrunum.",
			"27": "<sup>27</sup> Símeí frá Rama var settur yfir víngarðana og Sabdí frá Sefan yfir vínbirgðirnar í víngörðunum.",
			"28": "<sup>28</sup> Baal Hanan frá Geder var settur yfir olíuviðarlundi og mórberjafíkjutré á Sefelasvæðinu. Jóas var settur yfir olíubirgðirnar.",
			"29": "<sup>29</sup> Sítraí frá Saron hafði umsjón með nautgripum sem gengu í haga á Saronssléttunni og Safat Adlaíson með nautgripum í dölunum.",
			"30": "<sup>30</sup> Óbíl, afkomandi Ísmaels, hafði umsjón með úlföldum og Jehdeja frá Meronót með ösnunum.",
			"31": "<sup>31</sup> Jasís, afkomandi Hagar, hafði umsjón með sauðfé og geitum. Allir þessir menn voru umsjónarmenn með eignum Davíðs konungs.",
			"32": "<sup>32</sup> Jónatan, föðurbróðir Davíðs, var ráðgjafi hans. Hann var skynsamur maður og lærður. Jehíel Hakmóníson var með sonum konungs.",
			"33": "<sup>33</sup> Akítófel var ráðgjafi konungs og Húsaí Arkíti var vinur konungs.",
			"34": "<sup>34</sup> Jójada Benajason tók við af Akítófel. Abjatar var prestur og Jóab hershöfðingi."
		},
		"28": {
			"1": "<sup>1</sup> Davíð stefndi öllum leiðtogum Ísraels til Jerúsalem, höfðingjum ættbálkanna og foringjum flokkanna sem þjónuðu konunginum, foringjum þúsund manna liða og foringjum hundrað manna liða og umsjónarmönnum allra eigna konungs og fjáreignar hans og sona hans. Einnig stefndi hann þangað embættismönnum hirðarinnar, úrvalsliðinu og öllum dugandi hermönnum.",
			"2": "<sup>2</sup> Davíð konungur reis á fætur og sagði: „Hlýðið á mig, bræður mínir og þjóð mín. Ég hef haft í hyggju að reisa sáttmálsörk Drottins, fótskör Guðs okkar, hvíldarstað. En þegar ég hafði undirbúið bygginguna",
			"3": "<sup>3</sup> sagði Guð við mig: Þú átt ekki að reisa nafni mínu hús því að þú ert stríðsmaður og hefur úthellt blóði.",
			"4": "<sup>4</sup> Drottinn, Guð Ísraels, hefur valið mig úr allri ætt minni til þess að vera konungur yfir Ísrael um aldur og ævi. Hann hafði valið Júda til að fara með forystu ættbálkanna og ætt föður míns úr Júda. Af sonum föður míns þóknaðist honum að velja mig og gera mig að konungi yfir öllum Ísrael.",
			"5": "<sup>5</sup> Af öllum sonum mínum, en Drottinn hefur gefið mér marga syni, hefur hann valið Salómon til þess að sitja í hinu konunglega hásæti Drottins yfir Ísrael.",
			"6": "<sup>6</sup> Hann sagði við mig: Salómon, sonur þinn, skal reisa hús mitt og forgarða mína því að ég hef valið hann mér að syni og honum mun ég verða faðir.",
			"7": "<sup>7</sup> Ég mun styðja konungdóm hans um aldur og ævi ef hann kappkostar að framfylgja boðum mínum og reglum eins og hann gerir nú.",
			"8": "<sup>8</sup> Ég áminni ykkur frammi fyrir öllum Ísrael, söfnuði Drottins og í áheyrn Guðs okkar: Fylgið öllum boðum Drottins, Guðs okkar, og breytið eftir þeim svo að þið fáið að eiga þetta góða land og getið látið það ganga að erfðum til niðja ykkar um alla framtíð.",
			"9": "<sup>9</sup> En þú, Salómon, sonur minn, lærðu að þekkja Guð föður þíns og þjónaðu honum af heilum hug og af fúsum vilja því að Drottinn rannsakar öll hjörtu og þekkir allar hugsanir. Ef þú leitar hans lætur hann þig finna sig en ef þú yfirgefur hann útskúfar hann þér um alla framtíð.",
			"10": "<sup>10</sup> Gættu nú að. Drottinn hefur valið þig til að reisa sér hús sem helgidóm. Vertu hughraustur og gakk djarfur til verks.“",
			"11": "<sup>11</sup> Að svo mæltu fékk Davíð Salómon, syni sínum, grunnmynd af forsalnum og musterishúsinu ásamt fjárhirslum þess, loftherbergjum, innri herbergjum og hásæti náðarinnar.",
			"12": "<sup>12</sup> Hann fékk honum einnig uppdrætti af öllu sem honum hafði verið blásið í brjóst, uppdrætti af forgörðum húss Drottins og af öllum herbergjunum umhverfis þá. Þau voru fyrir fjársjóðina, sem heyrðu til húsi Guðs, og helgigjafirnar,",
			"13": "<sup>13</sup> einnig þjónustuflokka prestanna og Levítanna, alla þjónustu í húsi Drottins og öll áhöld til þjónustunnar í húsi Drottins.",
			"14": "<sup>14</sup> Hann fékk honum einnig teikningar af gull- og silfurgripunum til þjónustunnar og gull og silfur í þá eftir þyngd hvers þeirra.",
			"15": "<sup>15</sup> Hann fékk honum einnig uppdrætti af gullljósastikunum ásamt gulllömpunum, sem heyrðu þeim til, og gull í hverja ljósastiku eins og þurfti ásamt lömpum hennar og silfur í silfurljósastikurnar eins og þurfti í hverja þeirra ásamt lömpunum, sem heyrðu þeim til, eftir því til hvers ljósastikurnar voru ætlaðar.",
			"16": "<sup>16</sup> Hann fékk honum einnig vegið gull í borðin fyrir skoðunarbrauðin og silfur í silfurborðin,",
			"17": "<sup>17</sup> einnig skíragull í gafflana, skálarnar og könnurnar ásamt gulli í gullbikarana eins og þurfti í hvern þeirra og silfur í silfurbikarana eins og þurfti í hvern bikar.",
			"18": "<sup>18</sup> Hann fékk honum einnig skíragull í reykelsisaltarið eftir þyngd þess og gull í hásætisvagninn, kerúbana sem breiddu út vængi sína og huldu sáttmálsörk Drottins.",
			"19": "<sup>19</sup> Allt þetta var skráð á bók sem hann hafði tekið við úr hendi Drottins og skýrði allt verkið sem vinna átti eftir uppdrættinum.",
			"20": "<sup>20</sup> Þá sagði Davíð við Salómon, son sinn: „Vertu djarfur og hughraustur. Nú skaltu hefjast handa. Óttastu ekki og hikaðu ekki því að Drottinn Guð, Guð minn, er með þér. Hann mun hvorki láta þig missa þrótt né yfirgefa þig fyrr en öllu verki í þágu þjónustunnar í húsi Drottins er lokið.",
			"21": "<sup>21</sup> Flokkar prestanna og Levítanna standa þarna reiðubúnir til sérhverrar þjónustu í húsi Guðs. Þú hefur þjálfaða kunnáttumenn til allra verka og allir höfðingjarnir og öll þjóðin fer í öllu að fyrirmælum þínum.“"
		},
		"29": {
			"1": "<sup>1</sup> Davíð konungur ávarpaði nú allan söfnuðinn: „Salómon, sonur minn, sem Guð hefur valið, er enn ungur og óharðnaður. En verkið er mikið því að ekki er byggingin ætluð mönnum heldur Drottni Guði.",
			"2": "<sup>2</sup> Ég hef því af öllum mætti dregið að húsi Guðs míns gull í gulláhöldin, silfur í silfuráhöldin, eir í eiráhöldin, járn í járnáhöldin og við í tréáhöldin ásamt sjóamsteinum, steina til að setja í umgjarðir, steinlím, mislita steina og alls konar aðra dýra steina og mikið af alabastri.",
			"3": "<sup>3</sup> Af því að ég hef mætur á húsi Guðs míns gef ég hér með einkaeign mína af gulli og silfri til húss Guðs míns, auk alls þess sem ég hef þegar lagt hinu heilaga húsi til:",
			"4": "<sup>4</sup> þrjú þúsund talentur af gulli, Ófírgulli, og sjö þúsund talentur af hreinu silfri. Með því á að þekja veggi hússins",
			"5": "<sup>5</sup> alls staðar, þar sem þörf er á, gulli og silfri og nota það í alls konar handverk listasmiða. En hver er nú reiðubúinn að færa Drottni gjöf?“",
			"6": "<sup>6</sup> Þá sýndu ættarhöfðingjarnir og höfðingjar ættbálka Ísraels og höfðingjar þúsund manna og hundrað manna liða og ráðsmenn hinna konunglegu embætta örlæti sitt.",
			"7": "<sup>7</sup> Þeir gáfu fé til að greiða kostnaðinn við vinnuna við hús Guðs: fimm þúsund gulltalentur, tíu þúsund Daríusdali úr gulli, tíu þúsund silfurtalentur, átján þúsund eirtalentur og hundrað þúsund járntalentur.",
			"8": "<sup>8</sup> Hver sá sem átti í fórum sínum dýra steina gaf þá í fjársjóð húss Drottins, í vörslu Jehíels Gersoníta.",
			"9": "<sup>9</sup> Fólkið gladdist yfir örlæti þeirra því að þeir höfðu fært Drottni gjafirnar fúslega og af heilum hug. Davíð konungur gladdist einnig mjög.",
			"10": "<sup>10</sup> Þá lofaði Davíð Drottin frammi fyrir öllum söfnuðinum og sagði: „Lofaður sért þú, Drottinn, Guð föður vors, Ísraels, frá eilífð til eilífðar.",
			"11": "<sup>11</sup> Þín er tignin, Drottinn, mátturinn, dýrðin, vegsemdin og hátignin því að allt er þitt á himni og jörðu. Drottinn, þitt er konungdæmið og þú ert hafinn yfir allt.",
			"12": "<sup>12</sup> Auður og sæmd koma frá þér, þú ríkir yfir öllu. Í hendi þér er máttur og megin, í hendi þér er vald til að efla og styrkja hvern sem vera skal.",
			"13": "<sup>13</sup> Og nú, Guð vor, þökkum vér þér. Vér lofum þitt dýrlega nafn.",
			"14": "<sup>14</sup> En hver er ég og hver er lýður minn, að vér vorum færir um að gefa slíkar gjafir? Því að allt er frá þér og vér höfum fært þér það sem vér höfum þegið úr hendi þér.",
			"15": "<sup>15</sup> Frammi fyrir þér erum vér aðeins aðkomumenn og leiguliðar eins og allir forfeður vorir. Ævidagar vorir eru sem skuggi á jörðinni og engin von er til.",
			"16": "<sup>16</sup> Drottinn, Guð vor, allur þessi auður, sem vér höfum dregið saman til þess að reisa þér hús, þínu heilaga nafni, er þeginn úr hendi þér, allt er þitt.",
			"17": "<sup>17</sup> Ég veit, Guð minn, að þú rannsakar hjartað og gleðst yfir hreinskilni. Ég hef gefið þetta allt af heilum hug og fúsu geði. Nú hefur það einnig glatt mig að sjá að lýður þinn, sem hér er saman kominn, hefur fúslega gefið þér gjafir.",
			"18": "<sup>18</sup> Drottinn, Guð feðra vorra, Abrahams, Ísaks og Ísraels, varðveittu þetta hugarfar ævinlega í hjarta lýðs þíns. Beindu hjörtum þeirra til þín.",
			"19": "<sup>19</sup> Gefðu Salómon, syni mínum, einlægt hjarta svo að hann fylgi boðum þínum, fyrirmælum og lögum og geti þannig framkvæmt allt og reist bygginguna sem ég hef undirbúið.“",
			"20": "<sup>20</sup> Því næst ávarpaði Davíð allan söfnuðinn: „Lofið Drottin, Guð ykkar.“ Allur söfnuðurinn lofaði þá Drottin, Guð feðra sinna. Þeir hneigðu sig og vörpuðu sér niður frammi fyrir Drottni og konungi.",
			"21": "<sup>21</sup> Daginn eftir færðu þeir Drottni sláturfórnir og brennifórnir, þúsund naut, þúsund hrúta og þúsund lömb ásamt þeim dreypifórnum sem við áttu, svo og fjölmargar sláturfórnir fyrir allan Ísrael.",
			"22": "<sup>22</sup> Þennan dag átu þeir og drukku frammi fyrir Drottni og voru mjög glaðir. Því næst gerðu þeir Salómon, son Davíðs, öðru sinni að konungi. Þeir smurðu hann til þjóðhöfðingja Drottins og Sadók til æðsta prests.",
			"23": "<sup>23</sup> Salómon settist nú í hásæti Drottins í stað Davíðs, föður síns. Hann var auðnumaður og allur Ísrael hlýddi honum.",
			"24": "<sup>24</sup> Allir höfðingjarnir, hetjurnar og synir Davíðs konungs sýndu Salómon konungi hollustu.",
			"25": "<sup>25</sup> Drottinn gerði Salómon mjög voldugan í augum alls Ísraels. Hann veitti konungdómi hans slíka hátign sem enginn konungur yfir Ísrael hafði notið á undan honum.",
			"26": "<sup>26</sup> Davíð Ísaíson var konungur yfir öllum Ísrael.",
			"27": "<sup>27</sup> Hann ríkti yfir Ísrael fjörutíu ár, sjö ár í Hebron og þrjátíu og þrjú ár í Jerúsalem.",
			"28": "<sup>28</sup> Hann dó í hárri elli, saddur lífdaga, auðæfa og virðingar. Salómon, sonur hans, varð konungur eftir hann.",
			"29": "<sup>29</sup> Saga Davíðs konungs, bæði um fyrri hluta og síðari hluta ævi hans, er skráð í sögu sjáandans Samúels, í sögu Natans spámanns og í sögu sjáandans Gaðs.",
			"30": "<sup>30</sup> Þessar heimildir segja frá allri stjórnartíð hans og afrekum og því sem henti hann og Ísrael og öll konungsríki í öðrum löndum."
		}
	}
};
module.exports = book;