var book = {
	"name": "1ნეშტ",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ადამი, შეთი, ენოში,",
			"2": "<sup>2</sup> კენანი, მაჰალალელი, იარედი,",
			"3": "<sup>3</sup> ხანოქი, მეთუშელახი, ლამექი,",
			"4": "<sup>4</sup> ნოე, სემი, ქამი, იაფეთი.",
			"5": "<sup>5</sup> იაფეთის ძენი: გომერი, მაგოგი, მადაი, იავანი, თუბალი, მეშექი და თირასი.",
			"6": "<sup>6</sup> გომერის ძენი: აშქენაზი, რიფათი და თოგარმა.",
			"7": "<sup>7</sup> იავანის ძენი: ელიშა, თარშიშა, ქითიმი და როდანიმი,",
			"8": "<sup>8</sup> ქამის ძენი: ქუში, მიცრაიმი, ფუტი და ქანანი.",
			"9": "<sup>9</sup> ქუშის ძენი: სება, ხავილა, საბთა, რაყმა, საბთექა. რაყმას ძენი: შება და დედანი.",
			"10": "<sup>10</sup> ქუშმა შვა ნიმროდი. მან დაიწყო ძალის გამოჩენა ქვეყანაზე.",
			"11": "<sup>11</sup> მიცრაიმმა შვა ლუდელნი და ყანამელნი, ლეჰაბელნი და ნაფთუხელნი,",
			"12": "<sup>12</sup> ფათრუსელნი და ქასლუხელნი, რომელთაგანც ფილისტიმელები და ქაფთორელნი გამოვიდნენ.",
			"13": "<sup>13</sup> ქანაანმა შვა ციდონი, თავისი პირმშო, და ხეთი.",
			"14": "<sup>14</sup> იებუსელნი, ამორეველნი, გირგაშელნი,",
			"15": "<sup>15</sup> ხიველნი, ყარკელნი, სინელნი,",
			"16": "<sup>16</sup> არვადელნი, ცემარელნი და ხამათელნი.",
			"17": "<sup>17</sup> სემის ძენი: ყელამი, აშური, არფაქშადი, ლუდი, არამი, ყუცი, ხული, გეთერი და მეშექი.",
			"18": "<sup>18</sup> არფაქშადმა შვა შელახი, შელახმა შვა ყებერი.",
			"19": "<sup>19</sup> ყებერს ორი ვაჟი შეეძინა. ერთის სახელი იყო ფელეგი, რადგან მის დროს გაიყო ქვეყანა. მისი ძმის სახელი იყო იოკტანი.",
			"20": "<sup>20</sup> იოკტანმა შვა ალმოდადი, შალეფი, ხაცარ-მავეთი, იარახი,",
			"21": "<sup>21</sup> ჰადორამი, უზალი, დიკლა,",
			"22": "<sup>22</sup> ღებალი, აბიმაელი, შება,",
			"23": "<sup>23</sup> ოფირი, ხავილა, იობაბი; ყველა ესენი იოკტანის ძენი არიან.",
			"24": "<sup>24</sup> სემი, არფაქშადი, შელახი,",
			"25": "<sup>25</sup> ყებერი, ფელეგი, რეღუ,",
			"26": "<sup>26</sup> სერუგი, ნახორი, თერახი.",
			"27": "<sup>27</sup> აბრამი, იგივე აბრაამი.",
			"28": "<sup>28</sup> აბრაამის ძენი: ისაკი და ისმაელი.",
			"29": "<sup>29</sup> ეს არის მათი მოდგმა: ისმაელის პირმშო ნებაიოთი, კედარი, ადბეელი, მიბსამი,",
			"30": "<sup>30</sup> მიშმაყი, დუმა, მასა, ხადადი, თემა,",
			"31": "<sup>31</sup> იეტური, ნაფიში და კედემა. ესენი არიან ისმაელის ძენი.",
			"32": "<sup>32</sup> კეტურას, აბრაამის ხარჭის, ძენი: მან შვა ზიმრანი, იაკშანი, მედანი, მიდიანი, იშბაკი და შუახი. იაკშანის ძენი: შება და დედანი.",
			"33": "<sup>33</sup> მიდიანის ძენი: ღეფა, ყეფერი, ხანოქი, აბიდაყი და ელდაყა. ყველა ესენი კეტურას ძენი არიან.",
			"34": "<sup>34</sup> აბრაამმა შვა ისაკი. ისაკის ძენი: ესავი და ისრაელი.",
			"35": "<sup>35</sup> ესავის ძენი: ელიფაზი, რეღუელი, იეყუში, იაყლამი და კორახი.",
			"36": "<sup>36</sup> ელიფაზის ძენი: თემანი, ომარი, ცეფი, გაყთამი, კენაზი, თიმნაყი, ყამალეკი.",
			"37": "<sup>37</sup> რეღუელის ძენი: ნახათი, ზერახი, შამა და მიზა",
			"38": "<sup>38</sup> სეყირის ძენი: ლოტანი, შობალი, ციბყონი, ყანა, დიშონი, ეცერი და დიშანი.",
			"39": "<sup>39</sup> ლოტანის ძენი: ხორი, ჰომამი. ლოტანის და იყო თიმნაყი.",
			"40": "<sup>40</sup> შობალის ძენი: ყალიანი, მანახათი, ღებალი, შეფი, ონამი. ციბყონის ძენი: აია და ყანა.",
			"41": "<sup>41</sup> ყანას ძე: დიშონი. დიშონის ძენი: ხამრანი, ეშბანი, ითრანი, ქერანი.",
			"42": "<sup>42</sup> ეცერის ძენი: ბილჰანი, ზაყავანი, იაყაკანი. დიშონის ძენი: ყუცი და არანი.",
			"43": "<sup>43</sup> აჰა, ის მეფენი, ედომის ქვეყნად რომ მეფობდნენ, ვიდრე ისრაელის ძეთა შორის გამეფდებოდა მეფე ბელაყი, ძე ბეყორისა. მისი ქალაქის სახელი იყო დინჰაბა.",
			"44": "<sup>44</sup> და მოკვდა ბელაყი და მის ნაცვლად გამეფდა იობაბ ზერახის ძე – ბოცრელი.",
			"45": "<sup>45</sup> და მოკვდა იობაბი და მის ნაცვლად გამეფდა ხუშამი – თემანის ქვეყნიდან.",
			"46": "<sup>46</sup> და მოკვდა ხუშანი და მის ნაცვლად გამეფდა ჰადად ბედადის ძე, რომელმაც მოაბის ველზე მიდიანელნი გაანადგურა. მისი ქალაქის სახელი იყო ყავითი.",
			"47": "<sup>47</sup> და მოკვდა ჰადადი და მის ნაცვლად სამლა მასრეკელი გამეფდა.",
			"48": "<sup>48</sup> და მოკვდა სამლა და მის ნაცვლად შაული გამეფდა – მდინარისპირა რეხობოთიდან.",
			"49": "<sup>49</sup> და მოკვდა შაული და მის ნაცვლად ბაყალ-ხანანი გამეფდა, ყაქბორის ძე.",
			"50": "<sup>50</sup> და მოკვდა ბაყალ-ხანანი და მის ნაცვლად ჰადადი გამეფდა. მისი ქალაქის სახელი იყო ფაყი. სახელი მისი ცოლისა მეჰეტაბელი, ასული მატრედისა, ასული მე-ზაჰაბისა.",
			"51": "<sup>51</sup> და მოკვდა ჰადადი. ედომის მთავრები იყვნენ: მთავარი თიმნაყი, მთავარი ყალია, მთავარი იეთეთი,",
			"52": "<sup>52</sup> მთავარი ოჰოლიბამა, მთავარი ელა, მთავარი ფინონი,",
			"53": "<sup>53</sup> მთავარი კენაზი, მთავარი თემანი, მთავარი მიბცარი,",
			"54": "<sup>54</sup> მთავარი მაგდიელი, მთავარი ყირამი. ესენი იყვნენ ედომის მთავრები."
		},
		"2": {
			"1": "<sup>1</sup> ესენი არიან ისრაელის ძენი: რეუბენი, სიმონი, ლევი, იუდა, ისაქარი, ზებულუნი,",
			"2": "<sup>2</sup> დანი, იოსები, ბენიამინი, ნაფთალი, გადი და აშერი.",
			"3": "<sup>3</sup> იუდას ძენი: ყერი, ონანი და შელა; ქანაანელი შუაყის ასულისაგან მას სამი შეეძინა. იუდას პირმშო ყერი, ბოროტი იყო უფლის თვალში, ამიტომ მოაკვდინა იგი უფალმა.",
			"4": "<sup>4</sup> თამარმა, მისმა რძალმა, შვა ფერეცი და ზერახი. იუდას სულ ხუთი ვაჟი ჰყავდა.",
			"5": "<sup>5</sup> ფერეცის ძენი: ხეცრონი და ხამული.",
			"6": "<sup>6</sup> ზერახის ძენი: ზიმრი, ეთანი, ჰემანი, ქალქოლი და დარაყი – ხუთნი.",
			"7": "<sup>7</sup> ქარმის ძენი: ყაქარი, ფიცი რომ გატეხა და ისრაელს უბედურება შეამთხვია.",
			"8": "<sup>8</sup> ეთანის ძენი: ყაზარია. –",
			"9": "<sup>9</sup> და ხეცრონის ძენი, მას რომ შეეძინენ: იერახმეელი, რამი და ქელუბაი.",
			"10": "<sup>10</sup> რამმა შვა ყამინადაბი. ყამინადაბმა შვა ნახშონი – იუდას ძეთა წინამძღოლი,",
			"11": "<sup>11</sup> ნახშონმა შვა სალმა. სალმამ შვა ბოყაზი,",
			"12": "<sup>12</sup> ბოყაზმა შვა ყობედი. ყობედმა შვა იესე,",
			"13": "<sup>13</sup> იესემ შვა თავისი პირმშო ელიაბი, მეორე – აბინადაბი, მესამე – შიმყა,",
			"14": "<sup>14</sup> მეოთხე – ნეთანეელი, მეხუთე – რადაი,",
			"15": "<sup>15</sup> მეექვსე – ოცემი, მეშვიდე – დავითი.",
			"16": "<sup>16</sup> მათი დები: ცერუია და აბიგაილი. ცერუიას ძენი: აბიშაი, იოაბი და ყასაელი – სამნი.",
			"17": "<sup>17</sup> აბიგაილმა შვა ყამასა. ყამასას მამა იეთერი იყო, ისმაელიანი.",
			"18": "<sup>18</sup> ქალებმა, ხეცრონის ძემ, ყაზუბას, თავის ცოლს, და იერიყოთს აშობინა; აჰა, მისი ძენი: იეშერი, შობაბი და არდონი.",
			"19": "<sup>19</sup> და მოკვდა ყაზუბა და ქალებმა ეფრათი შეირთო ცოლად, უშვა მას ხური.",
			"20": "<sup>20</sup> ხურმა შვა ური. ურიმ შვა ბეცალელი.",
			"21": "<sup>21</sup> მერე ხეცრონი გილყადის მამის ასულთან შევიდა და ცოლად შეირთო. მაშინ იგი სამოცი წლისა იყო. უშვა მას სეგუბი.",
			"22": "<sup>22</sup> სეგუბმა შვა იაირი. გილყადის ქვეყანაში მას ოცდასამი ქალაქი ჰქონდა.",
			"23": "<sup>23</sup> გეშურმა და არამმა წაართვეს მათ იაირის დაბები, კენათი თავის ქალაქებიანად – სულ სამოცი ქალაქი. ყველა ესენი გილყადის მამის, მაქირის ძენი არიან.",
			"24": "<sup>24</sup> მას შემდეგ რაც ხეცრონი ქალებ-ეფრათში მოკვდა, ხეცრონის ცოლმა აბიამმა უშვა მას აშხური, თეკოაყის მამა.",
			"25": "<sup>25</sup> ხეცრონის პირმშოს იერახმეელის ძენი იყვნენ: პირმშო რამი, ბუნა, ორენი, ოცემი და ახია.",
			"26": "<sup>26</sup> იერახმეელს სხვა ცოლიც ჰყავდა, სახელად ყატარა, ის იყო ონამის დედა.",
			"27": "<sup>27</sup> იერახმეელის პირმშოს რამის ძენი იყვნენ: მაყაცი, იამინი და ყეკერი.",
			"28": "<sup>28</sup> ონამის ძენი იყვნენ: შამაი და იდაყი. შამაის ძენი: ნადაბი და აბიშური.",
			"29": "<sup>29</sup> აბიშურის ცოლს ერქვა აბიხაილი. მან შვა ახბანი და მოლიდი.",
			"30": "<sup>30</sup> ნადაბის ძენი: სელედი და აფაიმი. სელედი უშვილოდ მოკვდა.",
			"31": "<sup>31</sup> აფაიმს ჰყავდა ძე – იშყი, იშყის ძე იყო შეშანი. შეშანის ძე იყო ახლაი.",
			"32": "<sup>32</sup> იადაყის, შამაის ძმის, ძენი: ახი, შამი, იეთერი და იონათანი. იეთერი უშვილოდ მოკვდა.",
			"33": "<sup>33</sup> იონათანის ძენი: ფელეთი, ზაზა. ესენი იერახმეელის ძენი იყვნენ.",
			"34": "<sup>34</sup> შეშანს ვაჟები არ ჰყოლია, მხოლოდ ასულები ჰყავდა. შეშანს ეგვიპტელი ყმა ჰყავდა, სახელად იარხაყი.",
			"35": "<sup>35</sup> შეშანმა თავისი ასული თავის ყმას იარხაყს მისცა ცოლად. და უშვა მას ყათაი.",
			"36": "<sup>36</sup> ყათაიმ შვა ნათანი, ნათანმა შვა ზაბადი,",
			"37": "<sup>37</sup> ზაბადმა შვა ეფლალი, ეფლალმა შვა ყობედი,",
			"38": "<sup>38</sup> ყობედმა შვა იეჰუ, იეჰუმ შვა ყაზარია,",
			"39": "<sup>39</sup> ყაზარიამ შვა ხალეცი, ხალეცმა შვა ელყასა,",
			"40": "<sup>40</sup> ელყასამ შვა სისმაი, სისმაიმ შვა შალუმი,",
			"41": "<sup>41</sup> შალუმმა შვა იეკამია, იეკამიამ შვა ელიშამაყი.",
			"42": "<sup>42</sup> იერახმეელის ძმის, ქალების ძენი: პირმშო – მეშაყი, ეს არის ზიფის მამა; და მარეშას, ხებრონის მამის, ძენი.",
			"43": "<sup>43</sup> ხებრონის ძენი: კორახი, თაფუახი, რეკემი, შემაყი.",
			"44": "<sup>44</sup> შემაყმა შვა რახამი, იორკეყამის მამა. რეკემმა შვა შამაი.",
			"45": "<sup>45</sup> შამაის ძე იყო მაყონი. მაყონი იყო მამა ბეთ-ცურისა.",
			"46": "<sup>46</sup> ქალების ხარჭას, ყეფას, შეეძინა: ხარანი, მოცა და გაზეზი. ხარანმა შვა გაზეზი.",
			"47": "<sup>47</sup> იაჰდაის ძენი: რეგემი, იოთამი, გეშანი, ფელეტი, ყეფა და შაყაფი.",
			"48": "<sup>48</sup> ქალების ხარჭას, მაყაქას, შეეძინა: შებერი, თირხანა.",
			"49": "<sup>49</sup> და შვა მადმანას მამა შაყაფი, მაქბენასა და გიბყას მამა – შევა. ქალების ასული იყო ყაქსა.",
			"50": "<sup>50</sup> ესენი იყვნენ ქალების ძენი: ხურას ძე შობალი, ეფრათას პირმშო, მამა კირიათ-იეყარიმისა,",
			"51": "<sup>51</sup> სალმა, მამა ბეთ-ლეხემისა, და ხარეფი, მამა ბეთ-გადერისა.",
			"52": "<sup>52</sup> შობალს, კირიათ-იეყარიმის მამას, ჰყავდა ვაჟები: ჰაროე, ხაცი, მენუხოთი.",
			"53": "<sup>53</sup> კირიათ-იეყარიმის საგვარეულონი: ითრელი, ფუთელი, შუმათელი და მიშრაყელი. მათგან გამოვიდნენ: ცორყათელი და ეშთაოლელი.",
			"54": "<sup>54</sup> სალმას ძენი: ბეთ-ლეხემი, ნეტოფათელი, ყატაროთ-ბეთ-იოაბი, მანახთიელთა ნახევარი, და ცორყელნი.",
			"55": "<sup>55</sup> იაყბეცში მცხოვრებ მწიგნობართა საგვარეულონი: თირყათელნი, შიმყათელნი, სუქათელნი, ესენი კინელნი არიან, ბეთ-რექაბის მამის ხამათის შთამომავლები."
		},
		"3": {
			"1": "<sup>1</sup> ესენი იყვნენ დავითის ძენი, რომლებიც ხებრონში შეეძინენ მას: პირმშო ყამნონი, ახინოყამ იზრეყელელისგან, მეორე – დანიელი, აბიგაილ ქარმელელისაგან.",
			"2": "<sup>2</sup> მესამე – აბესალომი, გეშურის მეფე თალმაის ასულის, მაყაქას ვაჟი. მეოთხე – ადონია, ხაგითის ვაჟი.",
			"3": "<sup>3</sup> მეხუთე – შეფატია, აბიტალისაგან, მეექვსე – ითრაყამი, ყეგლასგან, თავისი ცოლისაგან.",
			"4": "<sup>4</sup> ექვსნი შეეძინენ მას ხებრონში. შვიდი წელი და ექვსი თვე იქ იმეფა, ოცდაცამეტი წელი კი იერუსალიმში.",
			"5": "<sup>5</sup> ესენი შეეძინენ იერუსალიმში: შიმყა, შობაბი, ნათანი, სოლომონი – ოთხნი, ბათ-შუაყისგან, ყამიელის ასულისაგან.",
			"6": "<sup>6</sup> იბხარი, ელიშამაყი, ელიფალეტი,",
			"7": "<sup>7</sup> ნოგა, ნეფეგი, იაფიაყი,",
			"8": "<sup>8</sup> ელიშამაყი, ელიადაყი, ელიფელეტი – ცხრანი.",
			"9": "<sup>9</sup> სულ ესენი არიან დავითის ძენი, ხარჭების ძეთა გარდა. თამარიც მათი და იყო.",
			"10": "<sup>10</sup> სოლომონის ძე რეხაბყამი, მისი ძე აბია, მისი ძე ასა, მისი ძე იოშაფატი,",
			"11": "<sup>11</sup> მისი ძე იორამი, მისი ძე ახაზიაჰუ, მისი ძე იოაში, მისი ძე ამაციაჰუ,",
			"12": "<sup>12</sup> მისი ძე ყაზარია, მისი ძე იოთამი,",
			"13": "<sup>13</sup> მისი ძე ახაზი, მისი ძე ხიზკიაჰუ, მისი ძე მენაშე,",
			"14": "<sup>14</sup> მისი ძე ამონი, მისი ძე იოშიაჰუ.",
			"15": "<sup>15</sup> იოშიაჰუს ძენი: პირმშო – იოხანანი, მეორე – იეჰოიაკიმი, მესამე – ციდკიაჰუ, მეოთხე – შალუმი.",
			"16": "<sup>16</sup> იეჰოიაკიმის ძენი: მისი ძე იექონია, მისი ძე ციდკიაჰუ.",
			"17": "<sup>17</sup> იექონიას ძენი: ასირი, მისი ძე შეალთიელი,",
			"18": "<sup>18</sup> მალქირამი, ფედაია, შენაცარი, იეკამია, ჰოშამაყი, ნედაბია.",
			"19": "<sup>19</sup> ფედაიას ძენი: ზერუბაბელი, შიმყი. ზერუბაბელის ძენი: მეშულამი, ხანანია, მათი და შელომითი,",
			"20": "<sup>20</sup> ხაშუბა, ოჰელი, ბერექია, ხასადია, და იუშაბ-ხესედი – ხუთნი.",
			"21": "<sup>21</sup> ხანანიას ძენი: ფელატია და ესაია, რეფაიას ძენი, არნანის ძენი, ყობადიას ძენი, შექანიას ძენი.",
			"22": "<sup>22</sup> და შექანიას ძე შემაყია. შემაყიას ძენი: ხატუში, იგეალი, ბარიახი, ნეყარია, შაფატი – ექვსნი.",
			"23": "<sup>23</sup> ნეყარიას ძენი: ელიოყენაი, ხიზკია, ყაზრიკამი – სამნი.",
			"24": "<sup>24</sup> ელიოყენაის ძენი: ჰოდავიაჰუ, ელიაშიბი, ფელაია, ყაკუბი, იოხანანი, დელაია, ყანანი – შვიდნი."
		},
		"4": {
			"1": "<sup>1</sup> იუდას ძენი: ფერეცი, ხეცრონი, ქარმი, ხური და შობალი.",
			"2": "<sup>2</sup> შობალის ძემ რეაიამ შვა იახათი, იახათმა შვა ახუმაი და ლაჰადი. აჰა, ცორყათელთა საგვარეულონი.",
			"3": "<sup>3</sup> აჰა, ყეტამის მამის ძენი: იზრეყელი, იშმა, იდბაში. მათ დას ჰაცელელფონი ერქვა.",
			"4": "<sup>4</sup> ფენუელი, გედორის მამა და ყეზერი, ხუშას მამა, ესენი არიან ხურის ძენი, ეფრათას პირმშოსი, ბეთლეხემის მამისა.",
			"5": "<sup>5</sup> აშხურს, თეკოაყის მამას, ორი ცოლი ჰყავდა: ხელა და ნაყარა.",
			"6": "<sup>6</sup> ნაყარასგან შეეძინა: ახუზამი, ხეფერი, თემნი და ჰაახაშთარი. ესენი იყვნენ ნაყარას ძენი.",
			"7": "<sup>7</sup> ხელას ძენი: ცერეთი, იეცხარი და ეთნანი.",
			"8": "<sup>8</sup> კოცმა შვა ყანუბი, ჰაცობება და ახარხელ ჰარუმის ძის საგვარეულონი.",
			"9": "<sup>9</sup> იაყბეცი თავის ძმებზე უფრო პატივდებული იყო. იაყბეცი დედამისმა დაარქვა, თქვა: რადგან ტკივილით გავაჩინეო.",
			"10": "<sup>10</sup> მოუხმო იაყბეცმა ისრაელის ღმერთს და უთხრა: „ნეტა კურთხევით მაკურთხებდე და საზღვრებს გამიდიდებდე, შენი ხელი ჩემთან იქნებოდეს და ბოროტებისაგან დამიცავდე, რომ არ გავიტანჯო.“ და მისცა ღმერთმა, რასაც ითხოვდა.",
			"11": "<sup>11</sup> შუხას ძმამ ქელუბმა შვა მეხირი, ეშთონის მამა.",
			"12": "<sup>12</sup> ეშთონმა შვა ბეთ-რაფა, ფასეახი და ყირ-ნახაშის მამა თეხინა. აჰა, რექას მცხოვრებნი.",
			"13": "<sup>13</sup> კენაზის ძენი: ყათნიელი და სერაია. ყათნიელის ძე იყო ხათათი.",
			"14": "<sup>14</sup> მეყონოთაიმ შვა ყოფრა. სერაიამ შვა იოაბი, მამა ხუროთა ველისა, რადგან ხურონი იყვნენ.",
			"15": "<sup>15</sup> ქალების, იეფუნეს ძის, ძენი: ყირუ, ელა და ნაყამი, ელას ძე იყო კენაზი.",
			"16": "<sup>16</sup> იეჰალელის ძენი: ზიფი და ზიფა, თირია და ასარელი.",
			"17": "<sup>17</sup> ყეზრას ძენი: იეთერი, მერედი, ყეფერი და იალონი. იეთერმა შვა მირიამი, შამაი და იშბახი, ეშთემოაყის მამა.",
			"18": "<sup>18</sup> მის იუდაელ ცოლს შეეძინა იერედი – მამა გედორისა, და ხებერი – მამა სოხოსი, და იეკუთიელი – მამა ზანოახისა.",
			"19": "<sup>19</sup> ჰოდიას ცოლის, ნახამის დის ვაჟები: კეყილას მამა გარმელი და ეშთემოაყ მაყაქათელი.",
			"20": "<sup>20</sup> შიმონის ძენი: ყამნონი, რინა, ბენ-ხანანი და თილონი. იშყის ძენი: ზოხეთი და ბენ-ზოხეთი.",
			"21": "<sup>21</sup> იუდას ძის შელას ვაჟები: ყერი – ლექას მამა, ლაყდა – მარეშას მამა და სელის საქმის საგვარეულოები აშბეაყის სახლიდან.",
			"22": "<sup>22</sup> იოკიმი და ქოზებას კაცები, იოაში და სარაფი მოაბსა და იაშუბი-ლახემს ფლობდნენ. ეს ძველი ამბებია.",
			"23": "<sup>23</sup> ისინი მეთუნეები იყვნენ და. ნეტაყიმსა და გედერაში სახლობდნენ, მეფესთან ცხოვრობდნენ და მისთვის შრომობდნენ.",
			"24": "<sup>24</sup> სიმონის ძენი: ნემუელი, იამინი, იარიბი, ზერახი და შაული;",
			"25": "<sup>25</sup> მისი ძე შალუმი, მისი ძე მიშმაყი.",
			"26": "<sup>26</sup> მიშმაყის ძენი: მისი ძე ხამუელი, მისი ძე ზაქური, მისი ძე შიმყი.",
			"27": "<sup>27</sup> შიმყის თექვსმეტი ვაჟი და ექვსი ქალი ჰყავდა. მის ძმებს ბევრი ვაჟები არ ჰყოლიათ და არც მათი საგვარეულო იყო იუდას ძეთა საგვარეულოსავით მრავალრიცხოვანი.",
			"28": "<sup>28</sup> ისინი ცხოვრობდნენ ბეერ-შებაყში, მოლედასა და ხაცარ-შუყალში,",
			"29": "<sup>29</sup> ბილჰაში, ყეცემში, თოლადში,",
			"30": "<sup>30</sup> ბეთუელში, ხორმაში, ციკლაგში,",
			"31": "<sup>31</sup> ბეთ-მარქაბოთში, ხაცარ-სუსიმში, ბეთ-ბირსა და შაყარაიმში. აჰა, მათი ქალაქები დავითის გამეფებამდე.",
			"32": "<sup>32</sup> მათი დასახლებები: ყეტამი, ყაინი, რიმონი, თოქენი, ყაშანი – ხუთი ქალაქი.",
			"33": "<sup>33</sup> ასევე ყველა ის სოფლები, რომელნიც ამ ქალაქებს ეკვროდნენ ვიდრე ბაყალამდე. აჰა, მათი საცხოვრებელი და მათი საგვარტომო ნუსხა.",
			"34": "<sup>34</sup> მეშობაბი, იამლექი, იოშა ამაციას ძე,",
			"35": "<sup>35</sup> იოელი და იეჰუ, ძე იოშიბიასი, სერაიას ძისა, ყასიელის ძისა.",
			"36": "<sup>36</sup> ელიოყენაი, იაყაკობა, იშოხაია, ყასაია, ყადიელი, იშიმიელი, ბენაია.",
			"37": "<sup>37</sup> ზიზა, ძე შიფყისა, ძისა ალონისა, ძისა იედაიასი, ძისა შიმრისა.",
			"38": "<sup>38</sup> ესენი, სახელებით რომ არიან ჩამოთვლილნი, თავ-თავიანთი საგვარეულოების მთავრები იყვნენ. მათი მამის სახლი ძალიან გაიზარდა.",
			"39": "<sup>39</sup> მათ გედორისაკენ გასწიეს, ველის აღმოსავლეთით, თავიანთი ცხვრისთვის საძოვრების საძებნელად",
			"40": "<sup>40</sup> და პოხიერსა და კარგ საძოვარს მიაგნეს. ვრცელი ქვეყანა იყო, თან მშვიდი და მყუდრო, რადგან უწინ აქ ქამის შთამომავლები ცხოვრობდნენ.",
			"41": "<sup>41</sup> ისინი, სახელებით რომ იყვნენ ჩამოწერილნი, იუდას მეფის იეხიზკიაჰუს დღეებში მოვიდნენ, და გაანადგურეს მათი კარვები და მაყონელნი, იქ რომ იყვნენ. იმ დღეს ერთიანად ამოწყვიტეს ისინი და მათ ნაცვლად დასახლდნენ, რადგან იქ იყო მათი ცხვრის საძოვრები.",
			"42": "<sup>42</sup> მათგან – სიმონის ძეთაგან – ხუთასი კაცი სეყირის მთისკენ გაეშურა იშყის ძეთა: ფელატიას, ნეყარიას, რეფაიას და ყუზიელის მეთაურობით.",
			"43": "<sup>43</sup> და გაანადგურეს ყამალეკის დარჩენილი ნატამალი და დღემდე იქ ცხოვრობენ."
		},
		"5": {
			"1": "<sup>1</sup> ძენი რეუბენის, ისრაელის პირმშოსი; იგი პირმშო იყო, მაგრამ, როცა მამის სარეცელი წაბილწა, მისი პირმშოობა გადაეცა, ისრაელის ძის იოსების ძეთ არა იმისათვის, რომ პირმშოდ ჩაწერილიყვნენ;",
			"2": "<sup>2</sup> იუდა ძლიერი იყო თავის ძმებში და წინამძღვარიც მისგან იყო, პირმშოობა კი იოსებმა მიიღო.",
			"3": "<sup>3</sup> რეუბენის, ისრაელის პირმშოს, ძენი: ხანოქი, ფალუ, ხეცრონი და ქარმი.",
			"4": "<sup>4</sup> იოელის ძენი: მისი ძე შემაყია, მისი ძე გოგი, მისი ძე შიმყი,",
			"5": "<sup>5</sup> მისი ძე მიქა, მისი ძე რეაია, მისი ძე ბაყალი,",
			"6": "<sup>6</sup> მისი ძე ბეერა, რომელიც ტყვედ წაიყვანა აშურის მეფემ, თიგლათ-ფილესერმა, ის იყო რეუბენის ძეთა წინამძღოლი.",
			"7": "<sup>7</sup> მისი ძმები, მათი საგვარეულოებისა და საგვარტომო ნუსხის მიხედვით იყვნენ: უფროსი იეყიელი, ზაქარია,",
			"8": "<sup>8</sup> ბელაყი, ძე ყაზაზისა, ძე შემაყისა, ძე იოელისა. იგი ყაროყერში ცხოვრობდა, ნებომდე და ბაყალ-მეყონამდე.",
			"9": "<sup>9</sup> აღმოსავლეთით ცხოვრობდა მდინარე ევფრატიდან მოყოლებული უდაბნოს პირამდე, რადგან დიდძალი საქონელი ჰყავდათ გილყადის ქვეყანაში.",
			"10": "<sup>10</sup> საულის დროს აგარელებს ეომებოდნენ და მათ ხელში ჩაცვივდნენ, და დასახლდნენ ისინი მათ კარვებში, გილყადის მთელ აღმოსავლეთ მხარეში.",
			"11": "<sup>11</sup> გადის ძენი მათ მეზობლად ცხოვრობდნენ ბაშანის ქვეყანაში სალქამდე,",
			"12": "<sup>12</sup> უფროსი – იოელი, მომდევნონი: შაფამი, იაყნაი და შაფატი ბაშანში ცხოვრობდნენ.",
			"13": "<sup>13</sup> მათი ძმები, თავ-თავიანთი მამის სახლიდან: მიქაელი, მეშულამი, შებაყი, იორაი, იაყქანი, ზიაყი, ყებერი და შიბყა.",
			"14": "<sup>14</sup> აჰა, ძენი აბიხაილისა, ხურის ძისა, იაროახის ძისა, გილყადის ძისა, მიქაელის ძისა, იეშიშაის ძისა, იახდოს ძისა, ბუზის ძისა.",
			"15": "<sup>15</sup> ახი, ძე ყაბდიელისა, ძე გუნისა, მათი მამისსახლის თავკაცი.",
			"16": "<sup>16</sup> ისინი ცხოვრობდნენ გილყადში, ბაშანსა და მის კუთვნილ ქალაქებში, ასევე შარონის მთელ სანახებში, ვიდრე გამოვიდოდნენ.",
			"17": "<sup>17</sup> ყველანი აღირიცხნენ იუდას მეფის, იოთამის, დროს და იერობყამის ისრაელის მეფის, დროს.",
			"18": "<sup>18</sup> რეუბენის ძეებში, გადის ძეებში და მენაშეს ნახევარ ტომში იყვნენ გულადი ვაჟკაცები, რომელნიც ფარსა და მახვილს ატარებდნენ, მშვილდოსნობდნენ და ბრძოლაში იყვნენ ნაწრთობნი. მათგან რიცხვით ორმოცდაოთხი ათას შვიდასსამოცი გამოდიოდა საომრად.",
			"19": "<sup>19</sup> აგარელებთან, იეტურთან, ნაფიშთან და ნოდაფთან ჰქონდათ ომი.",
			"20": "<sup>20</sup> და მიეხმარნენ მათ წინააღმდეგ ბრძოლაში და მათ ხელში ჩაცვივდნენ აგარელები, მთელი მათი საბადებლითურთ. ეს იმიტომ, რომ ომში ღმერთს მოუხმეს და მანაც უსმინა მათ, რაკი მისი იმედი ჰქონდათ.",
			"21": "<sup>21</sup> და წამოასხეს მათი საქონელი: ორმოცდაათი ათასი აქლემი, ორასორმოცდაათი ათასი წვრილფეხა, ორი ათასი ვირი და ასი ათასი ტყვე;",
			"22": "<sup>22</sup> მრავალი მოკლული დაეცა, რადგან ღვთისაგან იყო ეს ომი. და სახლობდნენ ისინი მათს ადგილებზე, სანამ ტყვეობაში მოხვდებოდნენ.",
			"23": "<sup>23</sup> და მენაშეს ნახევარი ტომიც დასახლდა იმ ქვეყანაში, ბაშანიდან მოყოლებული ვიდრე ბაყალ-ხერმონამდე, სენირამდე და ხერმონის მთამდე. მრავალრიცხოვანნი იყვნენ ისინი.",
			"24": "<sup>24</sup> ესენია მათი მამისსახლის თავკაცები: ყეფერი, იშყი, ელიელი, ყაზრიელი, იერემია, ჰოდავია, იახდიელი – მამაცი ვაჟკაცები, მამისსახლის სახელოვანი თავკაცები.",
			"25": "<sup>25</sup> როცა მათ ზურგი აქციეს მამათა ღმერთს და გზააბნეულნი გაჰყვნენ იმ ქვეყნის ხალხის ღმერთებს, მათგან რომ განდევნა ღმერთმა,",
			"26": "<sup>26</sup> მაშინ აღძრა ისრაელის ღმერთმა ფულის, აშურის მეფის, სული და აშურის მეფის თიგლათ-ფილესერის სული და გაასახლა რეუბენის ძენი, გადის ძენი და მენაშეს ნახევარი ტომი, და ჩაასახლა ისინი ხალახში, ხაბორში, ჰარაში. და მდინარე გოზანთან, დღემდე.",
			"27": "<sup>27</sup> ლევის ძენი: გერშონი, კეჰათი და მერარი.",
			"28": "<sup>28</sup> კეჰათის ძენი: ყამრამი, იცჰარი, ხებრონი და ყუზიელი.",
			"29": "<sup>29</sup> ყამრამის ძენი: აარონი, მოსე, მირიამი. აარონის ძენი: ნადაბი, აბიჰუ, ელეაზარი და ითამარი,",
			"30": "<sup>30</sup> ელეაზარმა შვა ფინხასი, ფინხასმა შვა აბიშუაყი,",
			"31": "<sup>31</sup> აბიშუაყმა შვა ბუკი, ბუკმა შვა ყუზი,",
			"32": "<sup>32</sup> ყუზმა შვა ზერახია, ზერახიამ შვა მერაიოთი,",
			"33": "<sup>33</sup> მერაიოთმა შვა ამარია, ამარიამ შვა ახიტუბი.",
			"34": "<sup>34</sup> ახიტუბმა შვა ცადოკი, ცადოკმა შვა ახიმაყაცი,",
			"35": "<sup>35</sup> ახიმაყაცმა შვა ყაზარია, ყაზარიამ შვა იოხანანი.",
			"36": "<sup>36</sup> იოხანანმა შვა ყაზარია – ეს იმ ტაძარში მღვდლობდა, რომელიც სოლომონმა ააშენა იერუსალიმში, –",
			"37": "<sup>37</sup> ყაზარიამ შვა ამარია, ამარიამ შვა ახიტუბი,",
			"38": "<sup>38</sup> ახიტუბმა შვა ცადოკი, ცადოკმა შვა შალუმი,",
			"39": "<sup>39</sup> შალუმმა შვა ხილკია, ხილკიამ შვა ყაზარია,",
			"40": "<sup>40</sup> ყაზარიამ შვა სერაია, სერაიამ შვა იეჰოცადაკი.",
			"41": "<sup>41</sup> იეჰოცადაკი წავიდა [ტყვეობაში], როცა უფალმა იუდა და იერუსალიმი ნაბუქოდონოსორის ხელით გაასახლა."
		},
		"6": {
			"1": "<sup>1</sup> ლევის ძენი: გერშომი, კეჰათი და მერარი;",
			"2": "<sup>2</sup> გერშომის ძეთა სახელები: ლიბნი და შიმყი.",
			"3": "<sup>3</sup> კეჰათის ძენი: ყამრამი იცჰარი, ხებრონი და ყუზიელი.",
			"4": "<sup>4</sup> მერარის ძენი: მახლი და მუში. აჰა, ლევის საგვარეულონი, თავ-თავიანთი მამისსახლის მიხედვით.",
			"5": "<sup>5</sup> გერშომს ჰყავდა მისი ძე ლიბნი, მისი ძე იახათი, მისი ძე ზიმა,",
			"6": "<sup>6</sup> მისი ძე იოახი, მისი ძე ყიდო, მისი ძე ზერახი, მისი ძე იეათრაი.",
			"7": "<sup>7</sup> კეჰათის ძენი: მისი ძე ყამინადაბი, მისი ძე კორახი, მისი ძე ასირი,",
			"8": "<sup>8</sup> მისი ძე ელკანა, მისი ძე აბიასაფი, მისი ძე ასირი,",
			"9": "<sup>9</sup> მისი ძე თახათი, მისი ძე ურიელი, მისი ძე ყუზია, მისი ძე შაული.",
			"10": "<sup>10</sup> ელკანას ძენი: ყამასაი და ახიმოთი.",
			"11": "<sup>11</sup> მისი ძე ელკანა, მისი ძე ცოფაი, მისი ძე ნახათი,",
			"12": "<sup>12</sup> მისი ძე ელიაბი, მისი ძე იეროხამი, მისი ძე ელკანა.",
			"13": "<sup>13</sup> სამუელის ძენი: იოელი – პირმშო და მომდევნო აბია.",
			"14": "<sup>14</sup> მერარის ძენი: მახლი, მისი ძე ლიბნი, მ",
			"15": "<sup>15</sup> მისი ძე შიმყა, მისი ძე ხაგია, მისი ძე ყასაია.",
			"16": "<sup>16</sup> აჰა, ისინი, მგალობლებად რომ დააყენა დავითმა უფლის სახლში, კიდობნის დადგმის შემდეგ.",
			"17": "<sup>17</sup> სადღესასწაულო კარავთან გალობით ასრულებდნენ მსახურებას, სანამ სოლომონი იერუსალიმში უფლის სახლს ააშენებდა. ისინი თავიანთი მსახურების წესისამებრ იდგნენ.",
			"18": "<sup>18</sup> აჰა, ისინი, მსახურებას ვინც ეწეოდნენ, და მათი ძენი: კეჰათის ძეთაგან ჰემანი – მგალობელი, იოელის ძე, ძისა სამუელისა,",
			"19": "<sup>19</sup> ძისა ელკანასი, ძისა იეროხამისა, ძისა ელიელისა, ძისა თოახისა,",
			"20": "<sup>20</sup> ძისა ცუფისა, ძისა ელკანასი, ძისა მახათისა, ძისა ყამასაისი,",
			"21": "<sup>21</sup> ძისა ელკანასი, ძისა იოელისა, ძისა ყაზარიასი, ძისა ცეფანიასი,",
			"22": "<sup>22</sup> ძისა თახათისა, ძისა ასირისა, ძისა აბიასაფისა, ძისა კორახისა,",
			"23": "<sup>23</sup> ძისა იცჰარისა, ძისა კეჰათისა, ძისა ლევისა, ძისა ისრაელისა,",
			"24": "<sup>24</sup> მისი ძმა ასაფი, მის მარჯვნივ მდგომი, ასაფ ბერექიაჰუს ძე, შიმყას ძისა,",
			"25": "<sup>25</sup> ძისა მიქაელისა, ძისა ბაყასეიასი, ძისა მალქიასი,",
			"26": "<sup>26</sup> ძისა ეთნისა, ძისა ზერახისა, ძისა ყადაიასი,",
			"27": "<sup>27</sup> ძისა ეთანისა, ძისა ზიმასი, ძისა შიმყისა,",
			"28": "<sup>28</sup> ძისა იახათისა, ძისა გერშომისა, ძისა ლევისა.",
			"29": "<sup>29</sup> მერარის ძენი, მათი ძმები, მარცხნივ რომ იდგნენ: ეთან კიშის ძე, ძისა ყაბდისა, ძისა მალუქისა,",
			"30": "<sup>30</sup> ძისა ხაშაბიასი, ძისა ამციასი, ძისა ხილკიასი,",
			"31": "<sup>31</sup> ძისა ამაცისა, ძისა ბანისა, ძისა შამერისა,",
			"32": "<sup>32</sup> ძისა მახლისა, ძისა მუშისა, ძისა მერარისა, ძისა ლევისა.",
			"33": "<sup>33</sup> მათი ძმები, ლევიანები, ღვთის სახლის სავანის ყველანაირი სამსახურისათვის იყვნენ დაყენებულნი.",
			"34": "<sup>34</sup> აარონი და მისი ძენი სრულადდასაწველის სამსხვერპლოზე და საკმევლის სამსხვერპლოზე აკმევდნენ, წმიდათა წმიდაში ყველა საქმეს აკეთებდნენ და ყველაფერს ისე ასრულებდნენ ისრაელის განსაწმედად, როგორც ბრძანა მოსემ, ღვთის მსახურმა.",
			"35": "<sup>35</sup> აჰა, აარონის ძენი: მისი ძე ელეაზარი, მისი ძე ფინხასი, მისი ძე აბიშუაყი,",
			"36": "<sup>36</sup> მისი ძე ბუკი, მისი ძე ყუზი, მისი ძე ზერახია,",
			"37": "<sup>37</sup> მისი ძე მერაიოთი, მისი ძე ამარია, მისი ძე ახიტუბი,",
			"38": "<sup>38</sup> მისი ძე ცადოკი, მისი ძე ახიმაყაცი.",
			"39": "<sup>39</sup> ეს მათი საცხოვრებელი, მათი დაბებისა და საზღვრის მიხედვით ერგოთ აარონის ძეთ, კეჰათელთა საგვარეულოდან წილისყრით მათ ეკუთვნოდათ.",
			"40": "<sup>40</sup> და მისცეს მათ ხებრონი, იუდას ქვეყანაში, თავისი სანახებითა და შემოგარენითურთ.",
			"41": "<sup>41</sup> ამ ქალაქის მინდორ-ველი და სოფლები ქალებს მისცეს, იეფუნეს ძეს.",
			"42": "<sup>42</sup> აარონის ძეთ მისცეს თავშესაფარი ქალაქები: ხებრონი, ლიბნა თავისი სანახებით, იათირი და ეშთემოაყი თავისი სანახებით,",
			"43": "<sup>43</sup> ხილენი თავისი სანახებით, დებირი თავისი სანახებით,",
			"44": "<sup>44</sup> ყაშანი თავისი სანახებით, ბეთ-შემეში თავისი სანახებით.",
			"45": "<sup>45</sup> ბენიამინის ტომიდან: გებაყი თავისი სანახებით, ყალემეთი თავისი სანახებით, ყანათოთი თავისი სანახებით. მათ საგვარეულოებში სულ ცამეტი ქალაქი იყო.",
			"46": "<sup>46</sup> ხოლო დანარჩენი კეჰათიანებს, ამ ტომის საგვარეულოდან, მენაშეს ნახევარტომის ნახევრიდან ათი ქალაქი ერგოთ წილისყრით.",
			"47": "<sup>47</sup> გერშომიანებს, თავ-თავიანთი საგვარეულოებისდა მიხედვით, ისაქარის, აშერის, ნაფთალისა და მენაშეს ტომებიდან ცამეტი ქალაქი ერგოთ ბაშანში.",
			"48": "<sup>48</sup> მერარიანებს, თავ-თავიანთი საგვარეულოებისდა მიხედვით, რეუბენის, გადისა და ზებულუნის ტომებიდან თორმეტი ქალაქი შეხვდათ წილისყრით.",
			"49": "<sup>49</sup> ეს ქალაქები მისცეს თავისი სანახებით ისრაელის ძეებმა ლევიანებს.",
			"50": "<sup>50</sup> წილისყრით მისცეს იუდას, სიმონისა და ბენიამინის ძეთა ტომებიდან ის ქალაქები, რომელთაც სახელები ერქვათ.",
			"51": "<sup>51</sup> კეჰათიანთა საგვარეულოებმა თავიანთი სასაზღვრო ქალქები ეფრემის ტომისაგან მიიღეს.",
			"52": "<sup>52</sup> მათვე მისცეს თავშესაფარი ქალაქები: შექემი, ეფრემის მთაზე, თავისი სანახებით, გეზერი თავისი სანახებით,",
			"53": "<sup>53</sup> იაკმეყამი თავისი სანახებით, ბეთ-ხორონი თავისი სანახებით,",
			"54": "<sup>54</sup> აიალონი თავისი სანახებით და გათ-რიმონი თავისი სანახებით.",
			"55": "<sup>55</sup> მენაშეს ნახევარტომიდან – ყანერი თავისი სანახებით და ბილყამი თავისი სანახებით მისცეს კეჰათიანთა დანარჩენ საგვარეულოს.",
			"56": "<sup>56</sup> გერშომიანებს, მენაშეს ნახევარტომის საგვარეულოდან, ბაშანში მისცეს: გოლანი თავისი სანახებით და ყაშთაროთი თავისი სანახებით.",
			"57": "<sup>57</sup> ისაქარის ტომიდან მისცეს კედეში თავისი სანახებით, დაბრათი თავისი სანახებით,",
			"58": "<sup>58</sup> რამოთი თავისი სანახებით, ყანემი თავისი სანახებით.",
			"59": "<sup>59</sup> აშერის ტომიდან მისცეს: მაშალი თავისი სანახებით, ყაბდონი თავისი სანახებით.",
			"60": "<sup>60</sup> ხუკოკი თავისი სანახებით, რეხობი თავისი სანახებით.",
			"61": "<sup>61</sup> ნაფთალის ტომიდან მისცეს კედეში გალილეაში თავისი სანახებით, ხამონი თავისი სანახებით და კირიათაიმი თავისი სანახებით.",
			"62": "<sup>62</sup> დანარჩენ მერარიანებს კი ზებულუნის ტომიდან მისცეს რიმონო თავისი სანახებით და თაბორი თავისი სანახებით.",
			"63": "<sup>63</sup> იორდანეს გადაღმა, იერიხოსთან, იორდანეს აღმოსავლეთით რეუბენის ტომიდან მისცეს: ბეცერი უდაბნოში თავისი სანახებით, და იაჰცა თავისი სანახებით.",
			"64": "<sup>64</sup> კედმოთი თავისი სანახებით და მეფაყათი თავისი სანახებით.",
			"65": "<sup>65</sup> გადის ტომიდან მისცეს რამოთი გილყადში თავისი სანახებით,",
			"66": "<sup>66</sup> და ხეშბონი თავისი სანახებით და იაყზერი თავისი სანახებით."
		},
		"7": {
			"1": "<sup>1</sup> ისაქარის ძენი: თოლაყი, ფუა, იაშუბი, შიმრონი – ოთხნი.",
			"2": "<sup>2</sup> თოლაყის ძენი: ყუზი, რეფაია, იერიელი, იახმაი, იაბსამი, სამუელი – თოლაყის მამისსახლის თავკაცები, თავ-თავიანთი მოდგმის მაგარი ვაჟკაცები. დავითის დროს მათი რიცხვი ოცდაორიათას ექვსასი იყო.",
			"3": "<sup>3</sup> ყუზის ძე იზრახია. იზრახიას ძენი: მიქაელი, ყობადია, იოელი, იშია – სულ ხუთი თავკაცი.",
			"4": "<sup>4</sup> თავ-თავიანთი მოდგმიდან და მამიისსახლიდან ოცდათექვსმეტიათასიანი საბრძოლო რაზმი გამოჰყავდათ ომში, რადგან ბევრი ცოლ-შვილი ჰყავდათ.",
			"5": "<sup>5</sup> მათი ძმები, ისაქარის ყველა საგვარეულოს მაგარი ვაჟკაცები, ოთხმოცდაშვიდი ათასნი იყვნენ. ყველანი საგვარტომო ნუსხაში იყვნენ ჩაწერილნი.",
			"6": "<sup>6</sup> ბენიამინიანები: ბელაყი, ბექერი და იედიყაელი – სამნი.",
			"7": "<sup>7</sup> ბელაყის ძენი: ეცბონი, ყუზი, ყუზიელი, იერიმოთი, ყირი – ხუთნი, მამისსახლის თავკაცები. საგვარტომო ნუსხაში ოცდაორი ათას ოცდათოთხმეტნი არიან ჩაწერილნი.",
			"8": "<sup>8</sup> ბექერის ძენი: ზემირა, იოყაში, ელიეზერი, ელიოყენაი, ყომრი, ირემოთი, აბია, ყანათოთი და ყალამეთი. ყველა ისინი ბექერის ძენი არიან.",
			"9": "<sup>9</sup> საგვარტომო ნუსხაში ისინი თავ-თავიანთ მამისსახლთა თავკაცების მიხედვით არიან ჩაწერილნი – სულ ოციათას ორასი ძლიერი მებრძოლი.",
			"10": "<sup>10</sup> იედიყაელის ძენი: ბილჰანი. ბილჰანის ძენი: იეყუში, ბენიამინი, ეჰუდი, ქენაყანა, ზეთანი, თარშიში და ახიშახარი.",
			"11": "<sup>11</sup> ყველა ესენი იედიყაელის ძენი არიან, მამისსახლთა თავკაცები. მათგან ჩვიდმეტიათას ორასი მებრძოლი გამოდიოდა საბრძოლველად.",
			"12": "<sup>12</sup> შუფიმი და ხუფიმი ყირის ძენი იყვნენ, ხოლო ხუშიმი ახერის ძე იყო.",
			"13": "<sup>13</sup> ნაფთალის ძენი: იახაციელი, გუნი, იეცერი და შალუმი ბილჰას შვილები იყვნენ.",
			"14": "<sup>14</sup> მენაშეს ძენი: ასრიელი, რომელიც მისმა არამელმა ხარჭამ გააჩინა; მასვე ეყოლა მაქირი, გილყადის მამა.",
			"15": "<sup>15</sup> მაქირმა ცოლები მოუყვანა ხუფიმს და შუფიმს. მათ დას მაყაქა ერქვა, ვაჟს – ცელაფხადი. ცელაფხადს ასულები ჰყავდა.",
			"16": "<sup>16</sup> მაყაქამ, მაქირის ცოლმა, ვაჟი გააჩინა და ფერეში დაარქვა. მის ძმას შარეში ერქვა. მისი ვაჟები იყვნენ: ულამი და რაკემი.",
			"17": "<sup>17</sup> ულამის ძე – ბედანი. აჰა, ძენი გილყადისა, მაქირის ძისა, მენაშეს ძისა.",
			"18": "<sup>18</sup> მისმა დამ, ჰამოლექეთმა შვა იშჰოდი, აბიყეზერი და მახლა.",
			"19": "<sup>19</sup> შემიდაყის ძენი იყვნენ: ახიანი, შექემი, ლიკხი და ანიყამი.",
			"20": "<sup>20</sup> ეფრემის ძენი: შუთალახი, მისი ძე ბერედი, მისი ძე თახათი, მისი ძე ელყადა, მისი ძე თახათი,",
			"21": "<sup>21</sup> მისი ძე ზაბადი, მისი ძე შუთელახი, ყეზერი და ელყადი. ისინი გათის ხალხმა ამოხოცა ამ ქვეყნის მკვიდრებმა, რადგან ისინი საქონლის წასართმევად დაესხნენ თავს.",
			"22": "<sup>22</sup> დიდხანს გლოვობდა ეფრემი, მათი მამა. მის სანუგეშოდ მისი ძმები მოვიდნენ.",
			"23": "<sup>23</sup> და მიუდგა იგი თავის ცოლს, დაორსულდა და ბიჭი გაუჩნდა, სახელად ბერიყა დაარქვეს, რადგან მის სახლში უბედურება დატრიალდა.",
			"24": "<sup>24</sup> შეერა მისი ასული იყო, და მან ააშენა ზემო და ქვემო ბეთ-ხორონი და უზენ-შეერა.",
			"25": "<sup>25</sup> მისი ძე იყო რეფახი, მისი ძე რეშეფი და თელახი, და მისი ძე თახანი,",
			"26": "<sup>26</sup> მისი ძე ლაყდანი, მისი ძე ყამიჰუდი, მისი ძე ელიშამაყი,",
			"27": "<sup>27</sup> მისი ძე ნონი, მისი ძე იესო.",
			"28": "<sup>28</sup> მათი სამფლობელო და საცხოვრებელი იყო: ბეთ-ელი და მისი დაბა-სოფლები. აღმოსავლეთით ნაყარანი და დასავლეთით გეზერი თავისი დაბა-სოფლებით, შექემი თავისი დაბა-სოფლებით ღაზამდე და მის დაბა-სოფლებამდე.",
			"29": "<sup>29</sup> მენაშეს ძეთა ხელში იყო: ბეთ-შეანი თავისი დაბა-სოფლებით, თაყნაქი თავისი დაბა-სოფლებით, მეგიდო თავისი დაბა-სოფლებით, დორი თავისი დაბა-სოფლებით. ამ ქალაქებში ცხოვრობდნენ იოსების, ისრაელის ძის, ძენი.",
			"30": "<sup>30</sup> აშერიანები: იმნა, იშვა, იშვი, ბერიყა და მათი და სერახი.",
			"31": "<sup>31</sup> ბერიყას ძენი: ხებერი და მალქიელი, ბირზაითის მამა.",
			"32": "<sup>32</sup> ხებერმა შვა იაფლეტი, შომერი, ხოთამი და მათი და შუაყა.",
			"33": "<sup>33</sup> იაფლეტის ძენი: ფასაქი, ბიმჰალი და ყაშვათი. ესენი არიან იაფლეტის ძენი.",
			"34": "<sup>34</sup> შამერის ძენი: ახი, როჰგა, იეხუბა და არამი.",
			"35": "<sup>35</sup> მისი ძმის, ჰელემის ძენი: ცოფახი, იმნაყი, შელეში და ყამალი.",
			"36": "<sup>36</sup> ცოფახის ძენი: სუახი, ხარნეფერი, შუყალი, ბერი და იმრა,",
			"37": "<sup>37</sup> ბეცერი და ჰოდი, შამა და შილშა, ითრანი და ბეერა.",
			"38": "<sup>38</sup> იეთერის ძენი: იეფუნე, ფისფა და არა.",
			"39": "<sup>39</sup> ყულას ძენი: არახი, ხანიელი და რიცია.",
			"40": "<sup>40</sup> ყველა ესენი აშერის ძენი არიან, მამისსახლთა თავკაცები, რჩეული და ძლიერი მებრძოლები, მთავართა თავკაცები. მათ საგვარტომო ნუსხაში რიცხვით ოცდაექვსი ათასი ომში მებრძოლი კაცია ჩაწერილი."
		},
		"8": {
			"1": "<sup>1</sup> ბენიამინმა შვა თავისი პირმშო ბელაყი, მეორე – აშბელი, მესამე – ახრახი,",
			"2": "<sup>2</sup> მეოთხე – ნოხა, მეხუთე – რაფა.",
			"3": "<sup>3</sup> ბალაყს ჰყავდა ძეები: ადარი, გერა, აბიჰუდი,",
			"4": "<sup>4</sup> აბიშუაყი, ნაყამანი, ახოახი,",
			"5": "<sup>5</sup> გერა, შეფუფანი, ხურამი,",
			"6": "<sup>6</sup> ესენი არიან ახუდის ძენი – მამისსახლთა თავკაცები, რომელნიც გებაყში ცხოვრობდნენ და მანახათში გადაასახლეს.",
			"7": "<sup>7</sup> ნაყამანი, ახია და გერა, რომელმაც გადაასახლა ისინი; მას შეეძინა ყუზა და ახიხუდი.",
			"8": "<sup>8</sup> შახარაიმს მოაბის ველზე შეეძინა შვილები მას შემდეგ, რაც ხუშიმი და ბაყარა, თავისი ცოლები, გაუშვა.",
			"9": "<sup>9</sup> ხოდეშისგან, თავისი ცოლისგან, მას შეეძინა: იობაბი, ციბია, მეშა, მალქამი,",
			"10": "<sup>10</sup> იეყუცი, საქია და მირმა. ესენი არიან მამისსახლთა თავკაცები.",
			"11": "<sup>11</sup> და ხუშიმისაგან შვა აბიტუმი და ელფაყალი.",
			"12": "<sup>12</sup> ელფაყალის ძენი: ყებერი, მიშყამი, შემერი. მან ააშენა ონო და ლოდი თავისი დაბა-სოფლებით.",
			"13": "<sup>13</sup> ბერიყა და შემაყი მამისსახლთა თავკაცები იყვნენ და აიალონში ცხოვრობდნენ, მათ განდევნეს გათელები.",
			"14": "<sup>14</sup> ახიო, შაშაკი, ირემოთი,",
			"15": "<sup>15</sup> ზებადია, ყარადი, ყადერი,",
			"16": "<sup>16</sup> მიქაელი, იშფა, იოხა – ბერიყას ძენი.",
			"17": "<sup>17</sup> ზებადია, მეშულამი, ხიზკი, ხაბერი,",
			"18": "<sup>18</sup> იშმერაი, იზლია, იობაბი – ელფაყალის ძენი",
			"19": "<sup>19</sup> იაკიმი, ზიქრი, ზაბდი,",
			"20": "<sup>20</sup> ელიყენაი, ცილთაი, ელიელი,",
			"21": "<sup>21</sup> ყადაია, ბერაია, შიმრათი – შიმყის ძენი.",
			"22": "<sup>22</sup> იშფანი ყებერი, ელიელი,",
			"23": "<sup>23</sup> ყაბდონი, ზიქრი, ხანანი,",
			"24": "<sup>24</sup> ხანანია, ყელამი, ყანთოთია,",
			"25": "<sup>25</sup> იფდეია, ფენუელი – შაშაკის ძენი.",
			"26": "<sup>26</sup> შამშერაი, შეხარია, ყათალია,",
			"27": "<sup>27</sup> იაყარეშია, ელია, ზიქრი – იეროხამის ძენი.",
			"28": "<sup>28</sup> ესენი არიან მამისსახლთა თავკაცები, თავკაცები თავიანთ გვარტომთა. ესენი იერუსალიმში ცხოვრობდნენ.",
			"29": "<sup>29</sup> გაბაონში ცხოვრობდა: გაბაონის მამა, რომლის ცოლსაც მაყაქა ერქვა,",
			"30": "<sup>30</sup> მისი პირმშო და ძე იყო ყაბდონი, მას მოსდევდნენ ცური, კიში, ბაყალი, ნადაბი,",
			"31": "<sup>31</sup> გედორი, ახიო, ზექერი.",
			"32": "<sup>32</sup> მიკლოთმა შვა შიმეა. ისინი იერუსალიმში თავიანთი ძმების მოპირისპირე მხარეს ცხოვრობდნენ, ძმებთან ერთად.",
			"33": "<sup>33</sup> ნერმა შვა კიში, კიშმა შვა საული, საულმა შვა იონათანი, მალქიშუაყი, აბინადაბი და ეშბაყალი.",
			"34": "<sup>34</sup> იონათანის ძე მერიბ-ბაყალი იყო. მერიბ-ბაყალმა შვა მიქა.",
			"35": "<sup>35</sup> მიქას ძენი: ფითონი, მელექი, თარეაყი, ახაზი.",
			"36": "<sup>36</sup> ახაზმა შვა იეჰოყადა, იეჰოყადამ შვა ყალამეთი, ყაზმავეთი და ზიმრი. ზიმრიმ შვა მოცა,",
			"37": "<sup>37</sup> მოცამ შვა ბინყა. მისი ძე იყო რაფა, მისი ძე იყო ელყასა, მისი ძე იყო აცელი.",
			"38": "<sup>38</sup> აცელს ექვსი ვაჟი ჰყავდა. აჰა, მათი სახელები: ყაზრიკამი, ბოქრუ, იშმაყელი, შეყარია, ყობადია და ხანანი – ყველა ესენი აცელის ძენი-არიან.",
			"39": "<sup>39</sup> მისი ძმის, ყეშეკის ძენი: მისი პირმშო ულამი, მეორე – იეყუში, მესამე – ელიფელეტი.",
			"40": "<sup>40</sup> ულამის ძენი მაგარი ვაჟკაცები იყვნენ, მშვილდოსნები და ასორმოცდაათი შვილისა და შვილიშვილის პატრონები. ყველა ესენი ბენიამინის ძეთაგან იყვნენ."
		},
		"9": {
			"1": "<sup>1</sup> მთელი ისრაელი აღნუსხეს და ისრაელის მეფეთა წიგნში ჩაწერეს, იუდაელები კი ბაბილონს გაასახლეს, მათი უკეთურების გამო.",
			"2": "<sup>2</sup> პირველი მკვიდრნი, თავიანთ სამფლობელოებსა და ქალაქებში რომ ცხოვრობდნენ, იყვნენ: ისრაელიანები, მღვდლები, ლევიანები, ნეთინიმელნი და ტაძარში მომსახურენი.",
			"3": "<sup>3</sup> იერუსალიმში იუდას, ბენიამინის, ეფრემისა და მენაშეს ძეთაგან ცხოვრობდნენ:",
			"4": "<sup>4</sup> ყუთაი, ძე ყამიჰუდისა, ძე ყომრისა, ძე იმრისა, ძე ბანისა, ფერეცის, იუდას ძის, ძეთაგან;",
			"5": "<sup>5</sup> შილონისგან იყვნენ: მისი პირმშო ყასაია და მისი ძენი:",
			"6": "<sup>6</sup> ზერახის ძეთაგან: იეყუელი და მისი ძმები – ექვსას ოთხმოცდაათი;",
			"7": "<sup>7</sup> ბენიამინის ძეთაგან: სალუ, ძე მეშულამისა, ძე ჰოდავიასი, ძე ჰასენუასი;",
			"8": "<sup>8</sup> იბნეია იეროხამის ძე, ელა, ძე ყუზისა, მიქრის ძისა, მეშულამი, ძე შეფატიასი, ძე რეღუელისა, ძე იბნიასი.",
			"9": "<sup>9</sup> მათი ძმები თავ-თავიანთ გვარ-ტომთა მიხედვით ცხრაას ორმოცდათექვსმეტნი იყვნენ. ყველა ეს ვაჟკაცი მამისსახლის თავკაცი იყო.",
			"10": "<sup>10</sup> მღვდელთაგან იყვნენ: იედაყია, იეჰოიარიბი, იაქინი,",
			"11": "<sup>11</sup> ყაზარია, ძე ხილკიასი, ძე მეშულამისა, ძე ცადოკისა, ძე მერაიოთისა, ძე ახიტუბისა – ღვთის სახლის წინამძღვარი;",
			"12": "<sup>12</sup> ყადაია, ძე იეროხამისა, ძე ფაშხურისა, ძე მალქიასი, და მაყასაი ძე ყადიელისა, ძე იახზერასი, ძე მეშულამისა, ძე მეშილემითისა, ძე იმერისა.",
			"13": "<sup>13</sup> მათი ძმები, მამისსახლთა თავკაცები: ათას შვიდას სამოცნი, – ღვთის სახლის მსახურის საქმეში ნაცადი ხალხი.",
			"14": "<sup>14</sup> ლევიანთაგან: შემაყია, ძე ხაშუბისა, ძე ყაზრიკამისა, ძე ხაშაბიასი – მერარის ძეთაგან,",
			"15": "<sup>15</sup> ბაკბაკარი, ხერეში, გალალი, მათანია, ძე მიქასი, ძე ზიქრისა, ძე ასაფისა.",
			"16": "<sup>16</sup> ყობადია, შემაყიას ძე, ძე გალალისა, ძე იედუთუნისა, ბერექია ძე ასასი, ძე ელკანასი – ნეტოფათელთა დაბა-სოფლებში მცხოვრებნი.",
			"17": "<sup>17</sup> კარიბჭის მცველნი: შალუმი, ყაკუბი, ტალმონი, ახიმანი და ძმანი მისნი. შალუმი თავკაცი იყო.",
			"18": "<sup>18</sup> და დღემდე ის დგას სამეფო კარიბჭეში, აღმოსავლეთით. ესენი არიან კარისმცველნი ლევიანთა ბანაკიდან.",
			"19": "<sup>19</sup> შალუმი, ძე კორესი, ძისა აბიასაფისა, ძისა კორახისა, და მისი ძმები. მისი მამის სახლიდან, კორახელნი, სამსახურს ეწეოდნენ და კარვის ზღურბლებს იცავდნენ; ხოლო მათი მამები უფლის ბანაკთან მისასვლელს დარაჯობდნენ;",
			"20": "<sup>20</sup> ფინხას ელეაზარის ძე უწინ მათი წინამძღოლი იყო და უფალიც მასთან იყო.",
			"21": "<sup>21</sup> ზაქარია მეშელემიას ძე სადღესასწაულო კარვის კარის მცველი იყო.",
			"22": "<sup>22</sup> ზღურბლების მცველებად ამორჩეულნი მთლიანად ორასთორმეტნი იყვნენ, ისინი საგვარტომო ნუსხაში თავ-თავიანთ დაბა-სოფლების მიხედვით არიან ჩაწერილნი. ისინი დავითმა და სამუელ მხილველმა დააყენეს ერთგულების გამო.",
			"23": "<sup>23</sup> ისინი თავიანთ ძეებთან ერთად დარაჯობდნენ უფლის სახლის – კარვის სახლის – კარიბჭეებს.",
			"24": "<sup>24</sup> კარისმცველები ოთხივ მხრივ იდგნენ: აღმოსავლეთით, დასავლეთით, ჩრდილოეთით და სამხრეთით.",
			"25": "<sup>25</sup> მათი ძმები თავიანთ დაბა-სოფლებში იყვნენ, რათა დრო და დრო შვიდი დღით მისულიყვნენ მათთან.",
			"26": "<sup>26</sup> რადგან კარიბჭეების მცველთა ის ოთხი მეთაური, რომლებიც ლევიანები იყვნენ, მუდამ ოთახებთან და ღვთის სახლის საგანძურებთან იდგნენ.",
			"27": "<sup>27</sup> ისინი ღვთის სახლის ირგვლივ ათევდნენ ღამეს, რადგან მათ ებარათ დაცვა და ყოველ დილით კარიბჭის გაღება.",
			"28": "<sup>28</sup> ერთ მათგანს სამსახურებელი ჭურჭელი ებარა, ვინაიდან თვლით შეჰქონდათ და თვლით გაჰქონდათ.",
			"29": "<sup>29</sup> მათგან სხვებს ჩაბარდათ ჭურჭლეული, ყველა წმინდა ჭურჭელი, გამტკიცული ფქვილი, ღვინო, ზეთი, გუნდრუკი და სურნელებანი.",
			"30": "<sup>30</sup> სამღვდელონი კი ზეთს ამზადებდნენ ნელსაცხებლისაგან.",
			"31": "<sup>31</sup> მათითიას, ლევიანთაგან ერთ-ერთს, შალუმ კორახელის პირმშოს, ტაფებზე მომზადება მიენდო.",
			"32": "<sup>32</sup> ზოგიერთ მათ ძმათაგანს, კეჰათიანთაგან, საწირი პური ჩააბარეს, რათა შაბათობით მოემზადებინათ.",
			"33": "<sup>33</sup> ხოლო მგალობელნი, ლევიანთა მამისსახლთა თავკაცნი, ტაძრის ოთახებში საქმიანობისგან გათავისუფლებულნი იყვნენ. რადგან ისინი დღე და ღამ თავიანთი საქმით იყვნენ დაკავებულნი.",
			"34": "<sup>34</sup> ესენი არიან ლევიანების მამისსახლთა თავკაცები, თავ-თავიანთ გვარ-ტომთა თავკაცები. ისინი იერუსალიმში ცხოვრობდნენ.",
			"35": "<sup>35</sup> გაბაონში ცხოვრობდნენ: გაბაონის მამა იეყიელი. მის ცოლს სახელად მაყაქა ერქვა.",
			"36": "<sup>36</sup> მისი ძე პირმშო ყაბდონი, მერე – ცური, კიში, ბაყალი, ნერი, ნადაბი,",
			"37": "<sup>37</sup> გედორი, ახიო, ზაქარია და მიკლოთი.",
			"38": "<sup>38</sup> მიკლოთმა შვა შიმამი. ისინი თავიანთი ძმების მეზობლად, თავიანთ ძმებთან ერთად ცხოვრობდნენ იერუსალიმში.",
			"39": "<sup>39</sup> ნერმა შვა კიში, კიშმა შვა საული. საულმა შვა იონათანი, მალქიშუაყი, აბინადაბი და ეშბაყალი.",
			"40": "<sup>40</sup> იონათანის ძე იყო მერიბ-ბაყალი. მერიბ-ბაყალმა შვა მიქა.",
			"41": "<sup>41</sup> მიქას ძენი: ფითონი, მელექი და თახრეაყი.",
			"42": "<sup>42</sup> ახაზმა შვა იაყრა, იაყრამ შვა ყალამეთი, ყაზმავეთი და ზიმრი. ზიმრიმ შვა მოცა,",
			"43": "<sup>43</sup> მოცამ შვა ბინყა, მისი ძე რეფაია, მისი ძე ელყასა, მისი ძე აცელი.",
			"44": "<sup>44</sup> აცელს ექვსი ძე ჰყავდა. აჰა, მათი სახელები: ყაზრიკამი, ბოქრუ, იშმაყელი, შეყარია, ყობადია, ხანანი – ესენი არიან აცელის ძენი."
		},
		"10": {
			"1": "<sup>1</sup> და შეებრძოლენ ფილისტიმელები ისრაელს და გაექცნენ ისრაელის ძენი ფილისტიმელებს. და გილბოაყის მთაზე ეცემოდნენ დახოცილნი,",
			"2": "<sup>2</sup> და მიეწივნენ ფილისტიმელები საულს და მის ვაჟებს და დახოცეს ფილისტიმელებმა იონათანი, აბინადაბი, და მალქიშუაყი – საულის შვილები.",
			"3": "<sup>3</sup> და გაუძნელდა ბრძოლა საულს. ნიშანში ამოიღეს იგი მშვილდოსნებმა და მძიმედ დაჭრეს.",
			"4": "<sup>4</sup> და უთხრა საულმა თავის საჭურველთმტვირველს: „იშიშვლე მახვილი და გამიყარე, რომ იმ წინადაუცვეთელებმა არ შეურაცხმყონ“. და ვერ გაბედა საჭურველთმტვირთველმა, რადგან ძალიან ეშინოდა. მაშინ საულმა აიღო მახვილი და ზედ დაეგო.",
			"5": "<sup>5</sup> როცა დაინახა საჭურველთმტვირთველმა რომ მოკვდა საული, ისიც ზედ დაეგო მახვილს და მოკვდა.",
			"6": "<sup>6</sup> და ერთად დაიხოცნენ საული, მისი სამი ვაჟიშვილი და მთელი მისი სახლი.",
			"7": "<sup>7</sup> როცა ველზე მყოფმა ისრაელის ძეთ დაინახეს, რომ უკუიქცნენ ისრაელის ძენი და, რომ დაიხოცნენ საული და მისი ვაჟიშვილები, დააგდეს ქალაქები და გადაიხვეწნენ. და მოვიდნენ ფილისტიმელები და ჩასახლდნენ იქ.",
			"8": "<sup>8</sup> მეორე დღეს მოვიდნენ ფილისტიმელები დახოცილთა გასაძარცვავად და, როცა ნახეს გილბოაყის მთაზე დაცემული საული და მისი ვაჟიშვილები,",
			"9": "<sup>9</sup> გაძარცვეს იგი, აიღეს მისი თავი და საჭურველი და მოატარეს ფილისტიმელთა ქვეყნის ირგვლივ, რათა ეხარებინათ თავიანთი საკერპოებისათვის და ხალხისათვის.",
			"10": "<sup>10</sup> მისი საჭურველი თავიანთი ღმერთის ტაძარში დადეს, ხოლო მისი თავის ქალა დაგონის სახლში მიაჭედეს.",
			"11": "<sup>11</sup> როცა გაიგეს გილყადის იაბეშელებმა, როგორ მოექცნენ ფილისტიმელნი საულს,",
			"12": "<sup>12</sup> ადგა ყველა, ვინც ვაჟკაცი იყო, და წამოიღეს საულისა და მისი ვაჟიშვილების გვამები და მიიტანეს იაბეშში, და დამარხეს მათი ძვლები იაბეშში მუხის ქვეშ. მერე შვიდი დღის მანძილზე მარხულობდნენ.",
			"13": "<sup>13</sup> ასე მოკვდა საული თავისი ორგულების გამო, რომლითაც ღალატობდა იგი უფალს და უფლის სიტყვას არ იცავდა და გამოსაკითხად მესულთანეს მიმართავდა.",
			"14": "<sup>14</sup> უფალს არ ეკითხებოდა. ამიტომ მოკლა იგი უფალმა და მისი სამეფო დავითს, იესეს ძეს, მისცა."
		},
		"11": {
			"1": "<sup>1</sup> მოვიდა მთელი ისრაელი დავითთან ხებრონში და უთხრეს: „აჰა, შენი ძვალი და ხორცი ვართ.",
			"2": "<sup>2</sup> გუშინაც და იმის უწინაც, როცა საული მეფობდა, შენ გამოგყავდა და შეგყავდა ისრაელი, უფალმა, შენმა ღმერთმა, გითხრა: ‘შენ დამწყემსავ ჩემს ერს, ისრაელს, შენ იქნები ჩემი ერის, ისრაელის, მთავარი.’“",
			"3": "<sup>3</sup> და მოვიდა ისრაელის მთელი უხუცესობა მეფესთან ხებრონს და შეკრა დავითმა მათთან კავშირი ხებრონში უფლის წინაშე, და სცხეს ისრაელზე მეფედ დავითი, უფლის სიტყვის თანახმად, სამუელის ხელით.",
			"4": "<sup>4</sup> და გაილაშქრეს დავითმა და ისრაელის ძეთ იერუსალიმისაკენ, ანუ იებუსისკენ. იქ იყვნენ იებუსელნი, იმ ქვეყნის მკვიდრნი.",
			"5": "<sup>5</sup> და უთხრეს იებუსის მკვიდრებმა დავითს: „აქ ვერ შემოხვალ“. მაგრამ აიღო დავითმა სიონის ციხე-სიმაგრე. ეს არის დავითის ქალაქი.",
			"6": "<sup>6</sup> და თქვა დავითმა: „ვინც ყველაზე უწინ შემუსრავს იებუსელთ, ის გახდება თავკაცი და სარდალი.“ ყველაზე უწინ იოაბი, ცერუიას ძე, ავიდა და გახდა სარდალი.",
			"7": "<sup>7</sup> და დადგა დავითი იმ ციხე-სიმაგრეში. ამიტომ უწოდეს მას დავითის ქალაქი.",
			"8": "<sup>8</sup> მან შემოაშენა ქალაქი გარშემო, მილოდან შემოგარენამდე. იოაბმა კი დანარჩენი ქალაქი აღადგინა.",
			"9": "<sup>9</sup> და სულ მაღლა და მაღლა მიდიოდა დავითი და ცაბაოთ უფალი იყო მასთან.",
			"10": "<sup>10</sup> აჰა, დავითის მოყმეთა მთავარნი, რომელნიც მხარს უმაგრებდნენ მას მის სამეფოში, ისრაელთან ერთად, მის გასამეფებლად, უფლის სიტყვისამებრ.",
			"11": "<sup>11</sup> აჰა, დავითის მოყმეთა რიცხვი: იაშობყამ ხაქმონის ძე, ოცდაათეულის თავკაცი. მან თავისი შუბის ერთი მოქნევით სამასი კაცი მოკლა.",
			"12": "<sup>12</sup> მას შემდეგ ელეაზარი, ძე დოდო ახოხელისა, ერთ-ერთი იმ სამ მოყმეთაგანი.",
			"13": "<sup>13</sup> იგი დავითს ახლდა ფას-დამიმში, სადაც ფილისტიმელნი საომრად შეიყარნენ. იყო იქ ყანის ნაკვეთი, სადაც ქერი ეთესა, და უკუიქცა ხალხი ფილისტიმელთაგან.",
			"14": "<sup>14</sup> ისინი ჩადგნენ შუა ყანაში, დაიცვეს იგი და მუსრი გაავლეს ფილისტიმელებს. და დიდი ხსნა მოიტანა უფალმა.",
			"15": "<sup>15</sup> ოცდაათი თავკაციდან ეს სამნი ავიდნენ კლდეზე დავითთან, ყადულამის გამოქვაბულში. ხოლო ფილისტიმელთა ურდო რეფაიმის ველზე იყო დაბანაკებული.",
			"16": "<sup>16</sup> მაშინ დავითი საფარში იყო, ხოლო ფილისტიმელთა ყარაული ბეთლემში იდგა.",
			"17": "<sup>17</sup> და ინება დავითმა და თქვა: „ვინ შემასმევს ბეთლემის ჭის წყალს, კარიბჭესთან რომ არის?“",
			"18": "<sup>18</sup> და გაარღვია ამ სამმა ფილისტიმელთა ბანაკი, და ამოიღეს წყალი ბეთლემის ჭიდან, კარიბჭესთან რომ არის, და წამოიღეს და დავითს მიუტანეს. არ ინდომა დავითმა მისი შესმა და უფალს დაუღვარა.",
			"19": "<sup>19</sup> და თქვა: „შორს იყოს ჩემგან, უფალო, ასეთი საქმე! ნუმც შემესვას ამ ვაჟკაცთა სისხლი, თავის გასაწირად რომ წავიდნენ!“ და აღარ ინდომა დავითმა წყლის შესმა. ასე მოიქცა ეს სამი მოყმე.",
			"20": "<sup>20</sup> აბიშაი, იოაბის ძმა, ამ სამის უფროსი იყო. მან სამას კაცს მოუქნია შუბი და განგმირა, მას მეტი სახელი ჰქონდა ამ სამში.",
			"21": "<sup>21</sup> ამ სამზე მეტად იყო პატივდებული და მათ მეთაურობდა, მაგრამ იმ პირველ სამს ვერ უტოლდებოდა.",
			"22": "<sup>22</sup> ბენაია იეჰოიადაყის ძე მაგარი ვაჟკაცი იყო, დიდ საქმეთა მქმნელი, კაბცეელიდან. მან დაამარცხა ორი მოაბელი ლომკაცი, ჩავიდა ორმოში და მოკლა ლომი თოვლიან დღეს.",
			"23": "<sup>23</sup> მანვე მოკლა ეგვიპტელი კაცი, ხუთი წყრთის სიმაღლისა: შუბი ისე ეჭირა ეგვიპტელს ხელში, როგორც ფეიქარს საქსელავი. და ჩაუხტა კეტით და ხელიდან გამოსტაცა ეგვიპტელს შუბი და მისივე შუბით განგმირა.",
			"24": "<sup>24</sup> ეს გააკეთა ბენაია იეჰოიადაყის ძემ და სახელი მოიხვეჭა სამ მოყმეს შორის.",
			"25": "<sup>25</sup> ოცდაათისგან გამოირჩეოდა, მაგრამ იმ სამს ვერ უტოლდებოდა. თავის მცველთა მეთაურად დანიშნა იგი დავითმა.",
			"26": "<sup>26</sup> მებრძოლთა მეთაურნი იყვნენ: ყასაელი, იოაბის ძმა, ელხანანი, დოდოს ძე, ბეთლემელი,",
			"27": "<sup>27</sup> შამოთ ჰარორელი, ხელეც ფელონელი,",
			"28": "<sup>28</sup> ყირა, ყიკეშის ძე, თეკოაყელი, აბიყეზერ ყანათოთელი,",
			"29": "<sup>29</sup> სიბქაი ხუშათელი, ყილაი ახოხელი,",
			"30": "<sup>30</sup> მაჰარაი ნეტოფათელი, ხელედ ბაყანას ძე, ნეტოფათელი,",
			"31": "<sup>31</sup> ითაი, რიბაის ძე, ბენიამინის გიბყადან, ბენაია ფირყათონელი,",
			"32": "<sup>32</sup> ხურაი გაყაშის ხევიდან, აბიელ ყარბათელი,",
			"33": "<sup>33</sup> ყაზმავეთ ბახარუმელი, ელიახბა შაყალბონელი.",
			"34": "<sup>34</sup> ჰაშემ გიზონელის ძენი; იონათან შაგეს ძე, ჰარარელი,",
			"35": "<sup>35</sup> ახიამ საქარის ძე, ჰარარელი, ელიფალ ურის ძე,",
			"36": "<sup>36</sup> ხეფერ მექერათელი, ახია ფელონელი,",
			"37": "<sup>37</sup> ხეცრო ქარმელელი, ნაყარაი ეზბაის ძე,",
			"38": "<sup>38</sup> იოელ, ნათანის ძმა, მიბხარ ჰაგრის ძე,",
			"39": "<sup>39</sup> ცელეკ ყამონელი, ნახარაი ბეროთელი, იოაბ ცერუიას ძის საჭურველთმტვირთველი,",
			"40": "<sup>40</sup> ყირა ითრელი, გარებ ითრელი,",
			"41": "<sup>41</sup> ურია ხეთელი, ზაბად ახალაის ძე,",
			"42": "<sup>42</sup> ყადინა შიზას ძე, რეუბენელი, რეუბენის ძეთა თავკაცი, და ოცდაათი მასთან ერთად.",
			"43": "<sup>43</sup> ხანან მაყაქას ძე, იოშაფატ მითნელი,",
			"44": "<sup>44</sup> ყუზია ყაშთერათელი, შამაყი და იეყიელი – ხოთამის ძენი – ყაროყერელნი.",
			"45": "<sup>45</sup> იედიყაელ შიმრის ძე, მისი ძმა იოხა, მისი ძმა თიციელი,",
			"46": "<sup>46</sup> ელიელ მახაველი, ირიბაი, იოშავია – ელნაყამის ძენი – და ითმა მოაბელი.",
			"47": "<sup>47</sup> ელიელი, ყობედი, იაყასიელ მეცობაელი."
		},
		"12": {
			"1": "<sup>1</sup> აჰა ისინი, ციკლაგში რომ მივიდნენ დავითთან, როცა იგი ჯერ კიდევ საულ კიშის ძეს ემალებოდა. ისინი ომში შემწე მოყმეთა შორის იყვნენ.",
			"2": "<sup>2</sup> მშვილდისრით შეიარაღებულნი, მარჯვენა-მარცხენით ქვისა და მშვილდისრის მსროლელნი, საულის ძმათაგანნი, ბენიამინიდან.",
			"3": "<sup>3</sup> მეთაური ახიყეზერი და იოაში – შემაყა გიბყათელის ძენი, იეზიელი და ფელეტი – ყაზმავეთის ძენი, ბერაქა და იეჰუ ყანათოთელნი,",
			"4": "<sup>4</sup> იშმაყია გაბაონელი – ერთ-ერთი მოყმე ოცდაათში და ოცდაათის მეთაური, იერემია, იახაზიელი, იოხანანი, იოზაბედ გედერათელი,",
			"5": "<sup>5</sup> ელყუზაი, იერიმოთი, ბეყალია, შემარიაჰუ, შეფატია ხარუფელი,",
			"6": "<sup>6</sup> ელკანა, იშიაჰუ, ყაზარელი, იოყეზერი, იაშაბყამი – კორახელნი,",
			"7": "<sup>7</sup> იოყელა და ზებადია – იეროხამ გედორელის ძენი.",
			"8": "<sup>8</sup> გადელებს ჩამოშორდნენ და დავითთან უდაბნოს საფარში მივიდნენ მამაცი მოყმენი, საომრად გაწვრთნილი ვაჟკაცები, ფარ-შუბით შეიარაღებულნი, სახით ლომის მსგავსნი და მთის არჩვივით სწრაფნი.",
			"9": "<sup>9</sup> მეთაური ყეზერი, მეორე – ყობადია, მესამე – ელიაბი,",
			"10": "<sup>10</sup> მეოთხე – მიშმანა, მეხუთე – იერემია,",
			"11": "<sup>11</sup> მეექვსე – ყათაი, მეშვიდე – ელიელი,",
			"12": "<sup>12</sup> მერვე – იოხანანი, მეცხრე – ელზაბადი,",
			"13": "<sup>13</sup> მეათე – იერემიაჰუ, მეთერთმეტე – მაქბანაი.",
			"14": "<sup>14</sup> ესენი იყვნენ გადის ძეთაგანნი, მხედრობის მეთაურნი. ყველაზე ნაკლები ასსა უფროსობდა, ყველაზე დიდი – ათასს.",
			"15": "<sup>15</sup> მათ პირველ თვეში გადალახეს იორდანე, როდესაც ადიდებული მდინარე ნაპირებიდან გადმოდიოდა. მათ აღმოსავლეთით და დასავლეთით განდევნეს ამ ველის მკვიდრნი.",
			"16": "<sup>16</sup> ბენიამინისა და იუდას ძეთაგანაც მივიდნენ დავითთან საფარში.",
			"17": "<sup>17</sup> და გამოეგება მათ დავითი, და გამოელაპარაკა და უთხრა: „თუ სამშვიდობოდ და დამხმარედ მოხვედით ჩემთან, გიერთგულებთ. თუ იმისთვის მოხვდით, რომ ჩემი მტრის ხელს მზაკვრულად მიმცეთ, აჰა, ჩემი ხელი სუფთაა, დაე, ჩვენი მამა-პაპის ღმერთმა იხილოს ეს და განსაჯოს.“",
			"18": "<sup>18</sup> მაშინ სულმა მოიცვა ყამასაი, ოცდაათის მეთაური, და თქვა: „მშვიდობა შენდა, დავით, იესეს ძეო! მშვიდობა შენ და მშვიდობა შენს შემწეთ, რადგან ღმერთია შენი შემწე.“ და მიიღო ისინი დავითმა და რაზმის მეთაურობა ჩააბარა.",
			"19": "<sup>19</sup> მენაშესაგანაც მიემხრენ დავითს, როდესაც იგი ფილისტიმელებით მიდიოდა საულთან საომრად, მაგრამ არ მიხმარებიან ისინი, რადგან ფილისტიმელთა მთავრები შეითქვნენ და უკან გამოაბრუნეს დავითი, თქვეს: ჩვენდა საზიანოდ საულს, თავის ბატონს, მიემხრობაო.",
			"20": "<sup>20</sup> როცა ციკლაგს მივიდა, მენაშესგან შეუერთდნენ: ყადნახი, იოზაბადი, იდიყაელი, მიქაელი, იოზაბადი, ელიჰუ, ცილათი – მენაშელთა ათასისთავები.",
			"21": "<sup>21</sup> ისინი ეხმარებოდნენ დავითს ურდოთა წინააღმდეგ ბრძოლაში, რადგან ყველანი მამაცი მოყმენი და ჯარის მეთაურები იყვნენ.",
			"22": "<sup>22</sup> დღიდან დღემდე მოედინებოდნენ, ვიდრე მისი ლაშქარი ღვთის მხედრობასავით არ გაიზარდა.",
			"23": "<sup>23</sup> აჰა, რიცხვი საომრად აღკაზმული იმ მეთაურებისა, რომელნიც დავითთან ხებრონში მივიდნენ, რათა უფლის სიტყვისამებრ, საულის სამეფო გადაეცათ მისთვის.",
			"24": "<sup>24</sup> იუდას ძენი, ფარ-შუბის მტვირთველნი, ექვსიათას რვაასნი იყვნენ საომრად აღჭურვილნი.",
			"25": "<sup>25</sup> სიმონის ძეთაგან შვიდიათას ასი მამაცი მოყმე იყო.",
			"26": "<sup>26</sup> ლევის ძეთაგან – ოთხიათას ექვსასი.",
			"27": "<sup>27</sup> ხოლო იეჰოიადაყს, აარონელთა წინამძღოლს, თან სამიათას შვიდასი ახლდა.",
			"28": "<sup>28</sup> ცადოკს, მამაც ყმაწვილს და მის მამისსახლს, ოცდაორი მეთაური ჰყავდა.",
			"29": "<sup>29</sup> საულის ძმები, ბენიამინის ძეთაგან, სამიათასნი იყვნენ. მათგან დიდი წილი საულის სახლში დარჩა.",
			"30": "<sup>30</sup> ეფრემის ძენი ოციათას რვაასნი იყვნენ – მამაცი მოყმენი და თავ-თავიანთ მამისსახლთა სახელოვანი ვაჟკაცები.",
			"31": "<sup>31</sup> მენაშეს ნახევარტომიდან თვრამეტი ათასნი იყვნენ, რომელნიც სახელებით გამოიხმეს, რათა დავითის გასამეფებლად მოსულიყვნენ.",
			"32": "<sup>32</sup> ისაქარის ძეთაგან ორასი მეთაური იყო. რომელთაც იცოდნენ დრო-ჟამთა გულისხმისყოფა და უწყოდნენ, თუ რა უნდა გაეკეთებინა ისრაელს. ყველა მათი ძმა მათი დარიგებით იქცეოდა.",
			"33": "<sup>33</sup> ზებულუნიდან გამოდიოდნენ და საომრად იყვნენ დარაზმული ყველანაირი საბრძოლო აღჭურვილობით ორმოცდაათი ათასნი, რათა უორგულოდ დახმარებოდნენ.",
			"34": "<sup>34</sup> ნაფთალიდან ათასი სარდალი იყო, ოცდაჩვიდმეტი ათასი ფარ-შუბოსნის თანხლებით.",
			"35": "<sup>35</sup> დანიდან იყვნენ ოცდარვა ათას ექვსასნი, საომრად დარაზმულნი.",
			"36": "<sup>36</sup> აშერიდან საბრძოლველად ორმოცი ათასი საომრად დარაზმული გიმოდიოდა.",
			"37": "<sup>37</sup> იორდანეს გაღმა მხრიდან გამოდიოდნენ: რეუბენიანთაგანნი, გადელნი, მენაშეს ნახევარტომელნი, ყველანაირი საბრძოლო საჭურველით, სულ ასოცი ათასი.",
			"38": "<sup>38</sup> ყველა ეს მებრძოლი ვაჟკაცი მწყობრად დარაზმული, წრფელი გულით მოვიდა ხებრონში დავითის გასამეფებლად მთელს ისრაელზე. ასევე მთელი დანარჩენი ისრაელი ერთსულოვანი იყო დავითის გასამეფებლად.",
			"39": "<sup>39</sup> სამ დღეს დარჩნენ ისინი იქ, ჭამდნენ და სვამდნენ, რადგან მათ ძმებს ყველაფერი მოემზადებინათ მათთვის.",
			"40": "<sup>40</sup> ასევე მათ მეზობლებს ისაქარის, ზებულუნისა და ნაფთალის ტომებიდანაც კი მოჰქონდათ ვირებით, აქლემებით, ჯორებითა და ხარებით სურსათ-სანოვაგე: ფქვილეული, ლეღვი, ჩირი, ღვინო, ზეთი, უამრავი საქონელი და ცხვარი, ეს იმიტომ, რომ სიხარული იყო ისრაელში."
		},
		"13": {
			"1": "<sup>1</sup> და ეთათბირა დავითი ათასისთავებს, ასისთავებს და წინამძღოლებს.",
			"2": "<sup>2</sup> და უთხრა დავითმა ისრაელის მთელ კრებულს: „თუ თქვენ თანახმა იქნებით და თუ ეს უფლის, ჩვენი ღვთის, ნება იქნება, ხალხი გავუგზავნოთ ისრაელის ყველა მხარეში ჩვენს ძმებს, მღვდლებს, ლევიანებს მათს შემოგარენ ქალაქებში, რათა შემოგვიერთდნენ,",
			"3": "<sup>3</sup> და ჩვენთან გადმოვასვენოთ ჩვენი ღვთის კიდობანი, რადგან საულის დროს მისთვის არ მიგვიმართავს.“",
			"4": "<sup>4</sup> და თქვა მთელმა კრებულმა: „ასე იყოს“, ვინაიდან ეს საქმე მოსაწონი იყო ხალხის თვალში.",
			"5": "<sup>5</sup> და შეყარა დავითმა მთელი ისრაელი ეგვიპტის შიხორიდან ხამათის მისადგომამდე, კირიათ-იეყარიმიდან ღვთის კიდობნის გადმოსასვენებლად.",
			"6": "<sup>6</sup> და ავიდა დავითი და მთელი ისრაელი ბაყალათაში, კირიათ-იეყარიმში, იუდას რომ ეკუთვნის, რათა იქიდან წამოესვენებინათ კიდობანი ღვთისა, ქერუბიმებზე მჯდომარე უფლისა, რომელიც ამ სახელით იწოდება.",
			"7": "<sup>7</sup> და შედგეს ღვთის კიდობანი ახალ ურემზე და წამოასვენეს აბინადაბის სახლიდან. და ურემს ყუზა და ახიო მიჰყვებოდნენ.",
			"8": "<sup>8</sup> დავითი და მასთან ერთად მთელი ისრაელი როკავდა ღვთის წინაშე მთელი ძალით. და მღეროდნენ და უკრავდნენ ქნარებს, ბობღნებს, დაფდაფებს, ჩანგებსა და წინწილებს.",
			"9": "<sup>9</sup> როცა ქიდონის კალოსთან მივიდნენ, ყუზამ ხელი შეაშველა კიდობანს, რადგან დააყირავეს იგი ხარებმა.",
			"10": "<sup>10</sup> და აენთო უფლის რისხვა ყუზაზე და იქვე დასცა იგი ღმერთმა კიდობანზე ხელის მიშველების გამო, და იქვე მოკვდა ღვთის წინაშე.",
			"11": "<sup>11</sup> და შეწუხდა დავითი, რომ დაღუპა უფალმა ყუზა, და დღემდე იმ ადგილს ეწოდება ფერეც-ყუზა.",
			"12": "<sup>12</sup> და შეეშინდა დავითს ღვთისა იმ დღეს და თქვა: „როგორ მოვა ჩემამდე ღვთისკიდობანი?“",
			"13": "<sup>13</sup> და არ ინდომა დავითმა უფლის კიდობნის თავისათნ მიტანა დავითის ქალაქში და გითელი ყობედ-ედომის სახლისკენ გადაახვევინა.",
			"14": "<sup>14</sup> სამ თვეს ესვენა ღვთის კიდობანი გითელი ყობედ-ედომის სახლში და აკურთხა უფალმა ყობედ-ედომის სახლი და მთელი მისი საბადებელი."
		},
		"14": {
			"1": "<sup>1</sup> გაუგზავნა ხირამმა, ცორის მეფემ, დავითს მოციქულები და ფიჭვის ძელები, კედლის მშენებლები, ხუროები მისთვის სახლის ასაშენებლად.",
			"2": "<sup>2</sup> და მიხვდა დავითი, რომ მტკიცედ ჰყავდა დადგენილი იგი უფალს ისრაელის მეფედ, და რომ აღამაღლა მისი სამეფო თავისი ერის, ისრაელის, გამო.",
			"3": "<sup>3</sup> და კიდევ მოიყვანა დავითმა იერუსალიმში ცოლები და კიდევ შეეძინა დავითს ვაჟები და ასულები.",
			"4": "<sup>4</sup> აჰა, იმათი სახელები, რომელნიც იერუსალიმში შეეძინა: შამუაყი, შობაბი, ნათანი, სოლომონი,",
			"5": "<sup>5</sup> იბხარი, ელიშუაყი, ელიფალეტი.",
			"6": "<sup>6</sup> ნოგა, ნეფეგი, იაფიაყი,",
			"7": "<sup>7</sup> ელიშამაყი, ბეყელიადაყი, ელიფალეტი.",
			"8": "<sup>8</sup> და შეიტყვეს ფილისტიმელებმა, რომ მთელი ისრაელის მეფედ აკურთხეს დავითი, და დაიძრნენ ფილისტიმელნი დავითის შესაპყრობად. და შეიტყო ეს დავითმა და გამოვიდა მათ წინააღმდეგ.",
			"9": "<sup>9</sup> და მოვიდნენ ფილისტიმელები და რეფაიმის ველზე განლაგდნენ.",
			"10": "<sup>10</sup> დავითი ღმერთს დაეკითხა: „გავილაშქრო ფილისტიმელებზე? ჩამიგდებ მათ ხელში?“ და უთხრა მას უფალმა: „გაილაშქრე, რადგან ხელში ჩაგიგდებ ფილისტიმელებს.“",
			"11": "<sup>11</sup> და ავიდნენ ბაყალ-ფერაციმში, და იქ სძლია დავითმა და თქვა: „წალეკა უფალმა ჩემი მტრები ჩემივე ხელით, როგორც წყალი წალეკავს.“ ამიტომ ეწოდა იმ ადგილს ბაყალ-ფერაციმი.",
			"12": "<sup>12</sup> და იქ მიატოვეს თავიანთი კერპები. დავითმა ბრძანა და ცეცხლში დაწვეს ისინი.",
			"13": "<sup>13</sup> და კვლავ გამოილაშქრეს ფილისტიმელებმა და ველზე განლაგდნენ.",
			"14": "<sup>14</sup> და კვლავ დაეკითხა დავითი ღმერთს. და უთხრა ღმერთმა: „უკან ნუ გაეკიდები, გარს მოუარე და ბალზამის ხეების მხრიდან მიადექი.",
			"15": "<sup>15</sup> და, როგორც კი ბალზამის ხის კენწეროებიდან შრიალის ხმა შემოგესმება, იმ წამსვე შეიმართე საომრად, რადგან მაშინ წაგიძღვება ღმერთი ფილისტიმელთა ბანაკის შესამუსრად.“",
			"16": "<sup>16</sup> ისე მოიქცა დავითი, როგორც ღმერთმა დაარიგა, და გაბაონიდან გეზერამდე მუსრი გაავლო ფილისტიმელთა ურდოს.",
			"17": "<sup>17</sup> დავითმა მთელ ქვეყანაზე გაითქვა სახელი და უფალმა მისი შიში მისცა ყველა ხალხს."
		},
		"15": {
			"1": "<sup>1</sup> და აიშენა მან სახლები დავითის ქალაქში. და მოამზადა ადგილი ღვთის კიდობნისთვის და კარავი გაშალა.",
			"2": "<sup>2</sup> მაშინ თქვა დავითმა: „ლევიანთა გარდა არავინ ატაროს კიდობანი, რადგან ისინი ამოირჩია უფალმა ღვთის კიდობნის მტარებლებად და სამარადისო მსახურებად.“",
			"3": "<sup>3</sup> და შეყარა დავითმა მთელი ისრაელი იერუსალიმში, რომ დაესვენებინათ უფლის კიდობანი იმ ადგილზე, რომელიც მისთვის იყო გამზადებული.",
			"4": "<sup>4</sup> და შეყარა დავითმა აარონის ძენი და ლევიანები.",
			"5": "<sup>5</sup> კეჰათის ძეთაგან: სარდალი ურიელი და მისი მოძმენი – ასოცი,",
			"6": "<sup>6</sup> მერარის ძეთაგან: სარდალი ყასაია და მისი მოძმენი – ორასოცი კაცი,",
			"7": "<sup>7</sup> გერშომის ძეთაგან: სარდალი იოელი და მისი ძმები – ასოცდაათი კაცი,",
			"8": "<sup>8</sup> ელიცაფანის ძეთაგან: სარდალი შემაყია და მისი ძმანი – ორასნი,",
			"9": "<sup>9</sup> ხებრონის ძეთაგან: სარდალი ელიელი და მისი ძმანი – ოთხმოცნი,",
			"10": "<sup>10</sup> ყუზიელის ძეთაგან: სარდალი ყამინდაბი და მისი ძმანი – ასთორმეტნი;",
			"11": "<sup>11</sup> და მოუხმო დავითმა მღვდლებს: ცადოკსა და აბიათარს და ლევიანებს: ურიელს, ყასაიას, იოელს, შემაყიას, ელიელს, ყამინადაბს,",
			"12": "<sup>12</sup> და უთხრა: „თქვენა ხართ ლევიანთა მამისსახლების თავკაცები. განიწმიდენით თქვენ და თქვენი ძმანი, და დაასვენეთ უფლის, ისრაელის ღვთის, კიდობანი მისთვის მომზადებულ ადგილას.",
			"13": "<sup>13</sup> რაკი უწინ ამას არ ასრულებდით, უფალმა, ჩვენმა ღმერთმა, შეგვმუსრა, რადგან წესისიმებრ არ ვეკითხებოდით მას.“",
			"14": "<sup>14</sup> და განიწმიდნენ მღვდლები და ლევიანები, რათა აეწიათ უფლის, ისრაელის ღვთის, კიდობანი.",
			"15": "<sup>15</sup> და ასწიეს ლევიანებმა ღვთის კიდობანი და, როგორც მოსეს ჰქონდა ნაბრძანები უფლის სიტყვისამებრ, კეტებით შეიდგეს მხრებზე.",
			"16": "<sup>16</sup> დავითმა ლევიანთა მთავრებს უბრძანა, რომ მათი ძმები დაეყენებინათ მგალობლებად საკრავი იარაღებით, ბობღნებით, ქნარებით, წინწილებით, რათა სიხარულის ხმები გამოეცათ.",
			"17": "<sup>17</sup> და დააყენეს ლევიანებმა: ჰემან იოელის ძე, მის ძმათაგან ასაფ ბერექიაჰუს ძე, ხოლო მათი ძმების – მერარის ძეთაგან ეთან კუშაიჰუს ძე.",
			"18": "<sup>18</sup> მათთან ერთად მათი ძმანი, მეორე რიგის: ზაქარიაჰუ, ბენი, იაყაზიელი, შემირამოთი, იეხიელი, ყუნი. ელიაბი, ბენიაჰუ, მაყასიაჰუ, მათითიაჰუ, ელიფელეჰუ, მიკნეიაჰუ, ყობედ-ედომი, იეყიელი – კარისმცველნი,",
			"19": "<sup>19</sup> მგალობელნი: ჰემანი, ასაფი და ეთანი სპილენძის წინწილებს ახმიანებდნენ.",
			"20": "<sup>20</sup> ხოლო ზაქარია, იაყაზიელი, შემირამოთი, იეხიელი, ყუნი, ელიაბი, მაყასიაჰუ და ბენაიჰუ – ქნარს ქალწულურ ჰანგზე.",
			"21": "<sup>21</sup> ხოლო მათითიაჰუ, ელიფელეჰუ, მიკნეიაჰუ, ყობედ-ედომი, იეყიელი, ყაზაზიაჰუ – ებნებს, რვასიმიანს, სალოტბაროდ.",
			"22": "<sup>22</sup> ხოლო ქენანიაჰუ, ლევიანთა მთავარი, სახიობის ხელმძღვანელად განაწესეს, ვინაიდან მცოდნე იყო.",
			"23": "<sup>23</sup> ბერექია და ელკანა კიდობნის კარისმცველები იყვნენ.",
			"24": "<sup>24</sup> ხოლო მღვდლები შებანიაჰუ, იოშაფატი, ნეთანელი, ყამასაი, ზაქარიაჰუ, ბენაიჰუ და ელიეზერი საყვირს უკრავდნენ ღვთის კიდობნის წინაშე. ყობედ-ედომი და იეხია კი კიდობნის კარისმცველნი იყვნენ.",
			"25": "<sup>25</sup> და გაემართნენ დავითი, ისრაელის უხუცესობა და ათასისთავები სიხარულით უფლის აღთქმის კიდობნის წამოსასვენებლად ყობედ-ედომის სახლიდან.",
			"26": "<sup>26</sup> ღვთის შეწევნით იყო რომ გადაასვენეს ლევიანებმა უფლის აღთქმის კიდობანი და შვიდი ხარი და შვიდი ვერძი შესწირეს",
			"27": "<sup>27</sup> დავითს ბისონის მოსასხამი ემოსა და ყველა ლევიანს, კიდობანს რომ მიასვენებდნენ, მგალობლებს და ქენანიას, მგალობელთა ლოტბარს. დავითს ამასთან ერთად სელის ეფოდიც ემოსა.",
			"28": "<sup>28</sup> მთელი ისრაელი მიაცილებდა უფლის აღთქმის კიდობანს ყიჟინით, ბუკისცემით, საყვირების, წინწილების, ბობღნებისა და ქნარების დაკვრით.",
			"29": "<sup>29</sup> და როცა უფლის კიდობანმა დავითის ქალაქს მიაღწია, სარკმლიდან გამოიხედა საულის ასულმა მიქალმა და დაინახა მოხტუნავე და მროკველი მეფე დავითი და გულში დასცინა."
		},
		"16": {
			"1": "<sup>1</sup> მოაბრძანეს უფლის კიდობანი და დაასვენეს შუაგულ კარავში, რომელიც მისთვის ჰქონდა დავითს გაშლილი. მერე კი სრულადდასაწველი და სამშვიდობო მსხვერპლი შესწირეს ღმერთს.",
			"2": "<sup>2</sup> და მოამთავრა დავითმა სრულადდასაწველი და სამშვიდობო მსხვერპლის შეწირვა და უფლის სახელით დალოცა ერი.",
			"3": "<sup>3</sup> და ჩამოურიგა ისრაელის მთელ ერს, ისრაელის მთელ კრებულს, კაცსა და ქალს, თითო პურის კვერი, თითო ხორცის წილობი და თითო ქადა.",
			"4": "<sup>4</sup> და დააყენა ლევიანები უფლის კიდობნის წინაშე სამსახურის გასაწევად, რათა მოეხსენებინათ, ედიდებინათ და ექოთ უფალი, ღმერთი ისრაელისა.",
			"5": "<sup>5</sup> ასაფი, მათი მეთაური, მისი მომდევნონი: ზაქარია, იეყიელი, შემირამოთი, იეხიელი, მათითია, ელიაბი, ბენაიაჰუ, ყობედ-ედომი და იყიელი – ბობღნებსა და ქნარებზე. ხოლო ასაფი წინწილების დამკვრელად.",
			"6": "<sup>6</sup> მღვდლები: ბენაიჰუ და იახაზიელი კი საყვირებზე მუდამ უფლის აღთქმის კიდობნის წინ.",
			"7": "<sup>7</sup> შემდეგ, იმ დღეს, შეასხა დავითმა პირველად ქება უფალს ასაფისა და მისი ძმების ხელით:",
			"8": "<sup>8</sup> „ადიდეთ უფალი, მოუხმეთ მის სახელს, აუწყეთ ხალხებს საქმენი მისნი.",
			"9": "<sup>9</sup> უმღერეთ მას, უგალობეთ მას, ილაპარაკეთ ყველა მის სასწაულზე.",
			"10": "<sup>10</sup> დაიკვეხეთ მისი წმიდა სახელით, გამხიარულდეს გული უფლის მეძიებელთა.",
			"11": "<sup>11</sup> ეძიეთ უფალი და ძლიერება მისი, ეძიეთ პირი მისი მარადის.",
			"12": "<sup>12</sup> გაიხსენეთ მისი ნამოქმედარი სასწაულები, საოცრებანი და მისი ბაგის სამართალნი.",
			"13": "<sup>13</sup> შთამომავალნო ისრაელისა, ძენო იაკობისა, რჩეულნო მისნო.",
			"14": "<sup>14</sup> ის არის უფალი, ღმერთი ჩვენი. მთელ ქვეყნიერებაზეა სამართალნი მისნი.",
			"15": "<sup>15</sup> ახსოვს საუკუნოდ აღთქმა თვისი, სიტყვა, რომელიც ბრძანა ათასი თაობისთვის,",
			"16": "<sup>16</sup> რომელიც დაუდო აბრაამს, და თავისი ფიცი – ისაკთან.",
			"17": "<sup>17</sup> და დაუდგინა იაკობს იგი წესად და ისრაელს – საუკუნო აღთქმად.",
			"18": "<sup>18</sup> უთხრა: ‘შენ მოგცემ ქანაანის ქვეყანას წილად თქვენი მემკვიდრეობისა.’",
			"19": "<sup>19</sup> როცა ისინი, თითზე ჩამოსათვლელნი, ცოტანი და იქ შეხიზნულნი.",
			"20": "<sup>20</sup> იარებოდნენ ერთი ხალხიდან მეორესთან, სამეფოდან – სხვა ერთან,",
			"21": "<sup>21</sup> არავის ანებებდა მათს დაჩაგვრას და მიუთითა მათზე მეფეებს:",
			"22": "<sup>22</sup> ‘არ შეეხოთ ჩემს ცხებულებს და ჩემს წინასწარმეტყველებს არ უბოროტოთ.’",
			"23": "<sup>23</sup> უგალობე უფალს, მთელო ქვეყნიერებავ! ამახარობლეთ დღითიდღე ხსნა მისმიერი.",
			"24": "<sup>24</sup> მოუთხრეთ ხალხებს მისი ღირსება, ყოველ ერს მისი სასწაულები.",
			"25": "<sup>25</sup> რადგან დიდია უფალი ყველა ღმერთზე საშიშია ის.",
			"26": "<sup>26</sup> რადგან ხალხთა ყველა ღმერთი კერპებია, ხოლო უფალმა ცანი შეჰქმნა.",
			"27": "<sup>27</sup> მშვენიერება და ბრწყინვალებაა მის წინაშე, სიძლიერე და მომხიბლაობა – მის ტაძარში.",
			"28": "<sup>28</sup> შესწირეთ უფალს, ტომებო ხალხთა, შესწირეთ უფალს ქება და დიდება.",
			"29": "<sup>29</sup> შესწირეთ უფალს ქება მისი სახელისა. წაუღეთ ძღვენი და მიდით მის ეზოებში.",
			"30": "<sup>30</sup> ათრთოლდი მის წინაშე, მთელო ქვეყნიერებავ! მტკიცეა სამყარო, არ შეირყევა.",
			"31": "<sup>31</sup> გამხიარულდებიან ცანი და მოილხენს ქვეყანა. უთხარით ხალხებს: ‘უფალი მეფობს!’",
			"32": "<sup>32</sup> იხმაურებს ზღვა და სავსება მისი. გაიხარებს ველი და ყოველი, რაც მასზეა,",
			"33": "<sup>33</sup> მაშინ იზეიმებენ ყოველნი ხენი ტევრისანი, რადგან მოვა ქვეყნიერების განსასჯელად.",
			"34": "<sup>34</sup> აქეთ უფალი, რადგან კეთილია იგი. რადგან უკუნისამდეა წყალობა მისი.",
			"35": "<sup>35</sup> და თქვით: ‘გვიხსენი, შეგვკრიბე და დაგვიცავ ხალხებისგან, რათა დავიდოთ შენი წმიდა სახელი, დავიქადნო შენი ქება-დიდებით’.",
			"36": "<sup>36</sup> კურთხეულ არს უფალი, ღმერთი ისრაელისა უკუნითი უკუნისამდე.“ და მიუგო ერმა: „ამენ! დიდება უფალს!“",
			"37": "<sup>37</sup> და დატოვა დავითმა უფლის აღთქმის კიდობნის წინაშე ასაფი და მისი ძმები, რათა კიდობნის წინაშე სამსახური გაეწიოთ მუდმივად, ყოველდღიურად,",
			"38": "<sup>38</sup> და ყობედ-ედომი და მისი ძმები – სამოცდარვანი. ყობედ-ედომ იედითუნის ძე და ხოსა კი კარისმცველებად,",
			"39": "<sup>39</sup> მღვდელი ცადოკი და მისი მღვდელმსახური ძმები უფლის სავანის წინ, გაბაონში რომ ბორცვია, იქ დააყენა,",
			"40": "<sup>40</sup> რომ მუდამ, დილა-საღამოობით, სრულადდასაწველი აღევლინათ უფლისათვის სრულადდასაწველის სამსხვერპლოზე, და რომ ყოველივე შეესრულებინათ, რაც უფლის რჯულში სწერია და რაც მან დაუწესა ისრაელს.",
			"41": "<sup>41</sup> მათთან იყვნენ: ჰემანი, იედუთუნი და სხვა რჩეულნი, რომელნიც სახელებით იყვნენ დადგენილნი, რათა ექოთ უფლის სახელი, რადგან უკუნისამდეა წყალობა მისი.",
			"42": "<sup>42</sup> მათთან ერთად ჰემანი და იედუთუნი საყვირებით, წინწილებითა და საკრავებით ღვთის მგალობლებად იყვნენ დადგენილნი, იედუთენის ძენი კი კარიბჭესთან.",
			"43": "<sup>43</sup> ყველანი თავ-თავიანთ სახლებში წავიდ-წამოვიდნენ. და დავითიც თავისი სახლის დასალოცად გაბრუნდა."
		},
		"17": {
			"1": "<sup>1</sup> როგორც კი დავითი თავის სახლში დამკვიდრდა, უთხრა ნათან წინასწარმეტყველს: „შეხედე, მე კედარის სახლში ვცხოვრობ, უფლის აღთქმის კიდობანი კი ფარდებსქვეშ არის.“",
			"2": "<sup>2</sup> უთხრა ნათანმა დავითს: „ყოველივე, რაც გულში გაქვს, გააკეთე: რადგან შენთანაა უფალი.“",
			"3": "<sup>3</sup> იმ ღამეს იყო, რომ ჩაესმა ნათანს უფლის სიტყვა:",
			"4": "<sup>4</sup> „წადი, უთხარი ჩემს მსახურს დავითს, ასე ამბობს-თქო უფალი: ‘განა შენ უნდა ამიშენო სახლი ჩემს სამყოფელად?",
			"5": "<sup>5</sup> მე ხომ არ ვიმყოფებოდი სახლში იმ დროიდან, რაც ისრაელი გამოვიყვანე, და დღემდე, კარვიდან კარავში და სავანიდან სავანეში ვარ.",
			"6": "<sup>6</sup> ნუთუ მთელ ისრაელთან მოარულს ოდესმე სიტყვა მითქვამს ისრაელის რომელიმე მსაჯულისათვის, ვისთვისაც ჩემი ერის მწყემსობა მიბრძანებია, კედარის სახლს რატომ არ მიშენებთ-მეთქი?’",
			"7": "<sup>7</sup> ახლა ასე უთხარი ჩემს მსახურს დავითს, ასე ამბობს-თქო ცაბაოთ უფალი: ‘ცხვრის ფარიდან წამოგიყვანე, რომ ჩემი ერის, ისრაელის, წინამძღოლი ყოფილიყავი.",
			"8": "<sup>8</sup> ყველგან შენთან ვიყავი, სადაც არ უნდა წასულიყავი. ვანადგურებდი შენს მტრებს შენს წინაშე. განვადიდე შენი სახელი, მსგავსად დიდებულთა სახელისა ამ ქვეყანაზე.",
			"9": "<sup>9</sup> ადგილი დავუდგინე ჩემს ერს, ისრაელს და დავაფუძნე იგი. ახლა თავისთვის იცხოვრებს უშიშრად და ბოროტმოქმედნი აღარ შეაჭირვებენ წინანდებურად.",
			"10": "<sup>10</sup> იმ დროიდან, რაც მე მსაჯულები დავუყენე ჩემს ერს, ისრაელს, მოსვენებული მყავხარ ყველა მტრისაგან. გამოგიცხადე, რომ სახლს აგიშენებს უფალი.",
			"11": "<sup>11</sup> და როცა დრო მოვა და შენს მამებთან განისვენებ, მე აღვადგენ შენს შემდგომ შენს თესლს, რომელიც იქნება შენი ძეთაგანი, და მის მეფობას განვამტკიცებ.",
			"12": "<sup>12</sup> ის ამიშენებს სახლს და მე განვამტკიცებ მის ტახტს უკუნისამდე,",
			"13": "<sup>13</sup> მე მამა ვიქნები მისთვის, ის კი შვილი იქნება ჩემთვის, წყალობას არ წავართმევ, როგორც შენს წინამორბედს წავართვი.",
			"14": "<sup>14</sup> და დავაყენებ მას ჩემს სახლში და ჩემს სამეფოში უკუნისამდე, მყარი იქნება მისი ტახტი უკუნისამდე,“",
			"15": "<sup>15</sup> თანახმად ამ სიტყვებისა და ამ ხილვისა ელაპარაკა ნათანი დავითს.",
			"16": "<sup>16</sup> და მივიდა დავით მეფე და დადგა უფლის წინაშე და თქვა: „ვინა ვარ მე, უფალო ღმერთო? ვინ არის ჩემი სახლი, რომ აქამდე აღმაზევე?",
			"17": "<sup>17</sup> ესეც მცირე გეჩვენა თვალში, ღმერთო, რომ სამომავლოდაც აღუთქვამ დაპირებას შენი მსახურის სახლს. შენ მიმზერ, როგორც განდიდებულ ადამიანს, უფალო ღმერთო ჩემო.",
			"18": "<sup>18</sup> კიდევ რა უდნა გითხრას დავითმა, შენი მსახურის დიდებისათვის? შენ თავად იცნობ შენს მსახურს, უფალო ღმერთო.",
			"19": "<sup>19</sup> უფალო, შენი მსახურისათვის და შენი გულის თანახმად აკეთებ ამ დიდებულ საქმეს, რათა გამოაცხადო მთელი შენი სიდიადე.",
			"20": "<sup>20</sup> უფალო, არავინაა შენი მსგავსი. არ არის ღმერთი შენს გარდა, თანახმად ყველაფრისა, რაც ჩვენი ყურით მოვისმინეთ.",
			"21": "<sup>21</sup> თუ არის ამ ქვეყნად თუნდაც ერთი ხალხი შენი ერის, ისრაელის, მსგავსი, რომელსაც მოვლენოდეს ღმერთი, რომ თავისთვის გამოესყიდოს იგი, რომ ექციოს თავის ერად, რომ სახელოვანი გაეხადოს და ჩაედინოს დიადი და საკვირველი ამბები, ხალხების განდევნით შენი ერის წინაშე, რომელიც გამოისყიდე ეგვიპტიდან და ხალხებისაგან?",
			"22": "<sup>22</sup> შენ შეჰქმენ შენი ერი, ისრაელი, რომ შენი ერი ყოფილიყო უკუნისამდე და შენ, უფალო, მათი ღმერთი ყოფილიყავი.",
			"23": "<sup>23</sup> ახლა კი, უფალო, ეს სიტყვა, რომელიც თქვი შენს მსახურზე და მის სახლზე, განმტკიცდეს უკუნისამდე, და ქენი ისე, როგორც ნაბრძანები გაქვს.",
			"24": "<sup>24</sup> განმტკიცდეს და იდიდოს შენმა სახელმა უკუნისამდე. თქვან: ‘უფალი, ღმერთი ისრაელისა ღმერთობს ისრაელზე!’ მტკიცე იყოს შენს წინაშე შენი მსახურის დავითის სახელი,",
			"25": "<sup>25</sup> რადგან შენ გამოუცხადე, ღმერთო, შენს მსახურს, სახლს აგიშენებო. ამიტომ გაუწია გულმა შენს მსახურს ელოცა შენს მიმართ.",
			"26": "<sup>26</sup> ახლა, უფალო ჩემო, ღმერთი ხარ შენ და სასიკეთოდ ელაპარაკე შენს მსახურს.",
			"27": "<sup>27</sup> ახლა შენ დალოცე შენი მსახურის სახლი, რათა საუკუნოდ იყოს შენს წინაშე, რადგან შენ აკურთხე, უფალო, და კურთხეული იქნება საუკუნოდ.“"
		},
		"18": {
			"1": "<sup>1</sup> ამის შემდეგ იყო, რომ დაამარცხა დავითმა ფილისტიმელები, და დაიმორჩილა ისინი და ხელიდან გამოგლიჯა ფილისტიმელებს გათი თავის ქალაქებიანად.",
			"2": "<sup>2</sup> და დაამარცხა მოაბიც. და გახდა მოაბი დავითის მსახური და მოხარკე.",
			"3": "<sup>3</sup> და დაამარცხა დავითმა ჰადარ-ყეზერი, ცობას მეფე, ხამათში, როცა ის ევფრატზე ბატონობის განსამტკიცებლად მიდიოდა,",
			"4": "<sup>4</sup> და წაართვა მას დავითმა ათასი ეტლი, შვიდი ათასი ცხენოსანი და ოცი ათასი ქვეითი. ეტლის ცხენებს ძარღვები გადაუჭრა დავითმა, ასი ეტლი დატოვა მათგან.",
			"5": "<sup>5</sup> და მოვიდნენ დამასკოს არამელები ცობას მეფის ჰადარ-ყეზერის დასახმარებლად და მუსრი გაავლო დავითმა არამელთაგან ოცდაორიათას კაცს.",
			"6": "<sup>6</sup> და დაადგინა დავითმა ხელისუფალნი დამასკოს არამში და გახდნენ არამელები დავითის მსახურნი და მოხარკენი. და სადაც არ უნდა წასულიყო დავითი, ყველგან უფალი შეეწეოდა.",
			"7": "<sup>7</sup> და აიღო დავითმა ოქროს ფარები, ჰადარ-ყეზერის მსახურებს რომ ჰქოდნათ, და იერუსალიმში მიიტანა.",
			"8": "<sup>8</sup> დიდძალი სპილენძი წამოიღო დავითმა ტიბხათიდან და ქუნიდან, ჰადარ-ყეზერის ქალაქებიდან, რომლისგანაც შემდეგ სოლომონმა გააკეთა სპილენძის ჭურჭელი.",
			"9": "<sup>9</sup> და შეიტყო თოყუმ, ხამათის მეფემ, რომ სძლია დავითმა ჰადარ-ყეზერს, და ცობას მეფეს,",
			"10": "<sup>10</sup> და გაგზავნა თავისი ძე ჰადორამი მეფე დავითთან მოსაკითხად და მისალოცად, რომ შეებრძოლა ჰადარ-ყეზერს და სძლია მას, რადგან თოყუს მოსისხლე იყო ჰადარ-ყეზერი. თან გაატანა ოქრო-ვერცხლისა და სპილენძის ჭურჭელი.",
			"11": "<sup>11</sup> მეფე დავითმა ესეც უფალს შესწირა იმ ოქრო-ვერცხლთან ერთად, დამორჩილებულ ხალხებს – ედომს, მოაბს, ყამონის ძეთ, ფილისტიმელთ და ყამალეკს რომ წაართვა.",
			"12": "<sup>12</sup> აბიშაი ცერუიას ძემ თვრამეტი ათასი ედომელი ამოწყვიტა მარილოვან ველზე,",
			"13": "<sup>13</sup> და დააყენა ედომში ხელისუფალნი და გახდა მთელი ედომი დავითის მსახური. და ყველგან, სადაც არ უნდა წასულიყო, უფალი შეეწეოდა დავითს.",
			"14": "<sup>14</sup> და მეფობდა დავითი მთელს ისრაელზე და წესსა და სამართალს აძლევდა თავის ერს.",
			"15": "<sup>15</sup> იოაბი, ცერუიას ძე, ჯარს სარდლობდა, ხოლო იოშაფატ ახილუდის ძე მემატიანე იყო.",
			"16": "<sup>16</sup> ცადოკ ახიტუბის ძე და აბიმელექ აბიათარის ძე მღვდლები იყვნენ, ხოლო შავშა მწერალი იყო.",
			"17": "<sup>17</sup> ბენაიაჰუ იეჰოიადაყის ძე ქერეთელებსა და ფერეთელებს გამგებლობდა, ხოლო დავითის ძენი პირველნი იყვნენ მეფის გვერდით."
		},
		"19": {
			"1": "<sup>1</sup> ამის შემდეგ იყო, რომ მოკვდა ყამონის ძეთა მეფე – ნახაშ ყამონის ძე – და გამეფდა მისი ძე მის ნაცვლად.",
			"2": "<sup>2</sup> და თქვა დავითმა: „წყალობას ვუყოფ ხანუნ ნახაშის ძეს, როგორც მამამისი მწყალობდა“. და გაგზავნა დავითმა მოციქულები სანუგეშოდ მამის გამო; და მივიდნენ დავითის მსახურნი ყამონის ძეთა ქვეყანაში, ხანუნთან, სანუგეშებლად.",
			"3": "<sup>3</sup> და უთხრეს ყამონის ძეთა მთავრებმა ხანუნს: „დავიჯერო მამაშენის პატივისცემის გამო გიგზავნის დავითი ნუგეშისმცემლებს? იქნებ იმისათვის გამოგზავნა დავითმა თავისი მსახურნი, რომ დაზვეროს ქვეყანა, მიათვალ-მოათვალიეროს და მერე დააქციოს?“",
			"4": "<sup>4</sup> და შეიპყრო ხანუნმა დავითის მსახურნი და ნახევარი წვერი მოპარსა. წელამდე შემოაჭრა სანახევროდ სამოსელი და გაისტუმრა.",
			"5": "<sup>5</sup> და წავიდნენ. და შეატყობინეს დავითს ამ კაცების ამბავი, და გაგზავნა კაცები მათ შესაგებებლად, რადგან ძალზე შეურაცხყოფილნი იყვნენ. და უთხრა მეფემ: „იერიხოში დარჩით, ვიდრე წვერი მოგეზრდებოდეთ და მერე დაბრუნდით.“",
			"6": "<sup>6</sup> და დაინახეს ყამონის ძეთ, რომ თავი შეაძულეს დავითს, და გაგზავნეს ხანუნმა და ყამონის ძეთ ათასი ქანქარი ვერცხლი ეტლებისა და მხედრების დასაქირავებლად შუამდინარეთიდან, მაყაქას არამიდან და ცობადან.",
			"7": "<sup>7</sup> და იქირავეს ოცდათორმეტი ათასი ეტლი და მეფე მაყაქა თავისი ხალხით. და მივიდნენ და მედიბას წინ დაბანაკდნენ. ყამონის ძენი კი ერთად შეიყარნენ და საომრად გამოემართნენ.",
			"8": "<sup>8</sup> და შეიტყო ეს დავითმა და გაგზავნა იოაბი უმამაცესთა მთელი ლაშქარითურთ.",
			"9": "<sup>9</sup> და გამოვიდნენ ყამონის ძენი და გაეწყვნენ საბრძოლველად ქალაქის კარიბჭესთან. ხოლო ის მეფენი, რომელნიც მოვიდნენ, ცალ-ცალკე დადგნენ მინდორში.",
			"10": "<sup>10</sup> როცა იოაბმა დაინახა, რომ მტერი წინიდან და ზურგიდან იყო მისკენ მიმართული, გამოარჩია ისრაელის ძეთა შორის ურჩეულესნი და დააწყო არამელთა წინააღმდეგ.",
			"11": "<sup>11</sup> დანარჩენი ხალხი თავის ძმას, აბიშაის, ჩააბარეს და მანაც დააწყო იგი ყამონის ძეთა წინააღმდეგ.",
			"12": "<sup>12</sup> და თქვა: „თუ არამელები მე მძლევენ, შენ მომეშველები, ხოლო თუ შენ გძლევენ, მე მოვალ შენს მოსაშველებლად.",
			"13": "<sup>13</sup> გამაგრდი და მტკიცედ იდექი ჩვენი ხალხისათვის და ჩვენი უფლის, ჩვენი ღვთის ქალაქებისათვის. და უფალმა ქმნას, რაც ენებოს.“",
			"14": "<sup>14</sup> და გავიდნენ იოაბი და მისი ხალხი არამელებთან საბრძოლველად და უკუიქცნენ არამელები.",
			"15": "<sup>15</sup> როცა ყამონის ძეთ დაინახეს, რომ გარბოდნენ არამელები, თავადაც გაექცნენ აბიშაის და ქალაქში შეცვივდნენ. იოაბი გაეცალა ყამონის ძეთ და მივიდა იერუსალიმში.",
			"16": "<sup>16</sup> და დაინახეს არამელებმა, რომ მარცხდებიან ისრაელთან და კაცები გაუგზავნეს მდინარისგაღმელ არამელებს და გადმოიყვანეს. მათ წინამძღოლობდა ჰადარ-ყეზერის სარდალი შოფახი.",
			"17": "<sup>17</sup> და შეატყობინეს ეს დავითს და მანაც შეყარა მთელი ისრაელი. და გადალახა იორდანე, და მივიდა მათთან და გაეწყო მათ წინააღმდეგ. ბრძოლა გაუმართა დავითმა არამელებს და ისინიც შეებნენ მას.",
			"18": "<sup>18</sup> და გაექცნენ არამელები ისრაელს. და დაუხოცა დავითმა არამელებს შვიდი ათასი ეტლოსანი და ორმოცი ათასი ქვეითი. და მათი სარდალი შოფახიც მოკლულ იქნა.",
			"19": "<sup>19</sup> როცა ჰადარ-ყეზერის მსახურებმა დაინახეს, რომ მარცხდებოდნენ ისრაელთან, დაეზავნენ დავითს და დაემორჩილნენ. არამელებმა ვეღარ გაბედეს ყამონის ძეთა დახმარება."
		},
		"20": {
			"1": "<sup>1</sup> წლის შემოქცევისას, მეფეთა გამოსვლის ჟამს, იოაბი წინ წაუძღვა ლაშქარს და გაანადგურა ყამონის ძეთა ქვეყანა. და მივიდა და რაბას შემოადგა. დავითი კი იერუსალიმში დარჩა. და აიღო იოაბმა რაბა და დაანგრია.",
			"2": "<sup>2</sup> აიღო დავითმა მათი მეფის გვირგვინი მისი თავიდან, და ნახა რომ ერთი ტალანტი ოქროს წონისა იყო და ძვირფასი თვლებით მოოჭვილი, და თავზე დაირქვა იგი. აურაცხელი ნადავლი წამოიღო დავითმა ქალაქიდან.",
			"3": "<sup>3</sup> და გამოიყვანა იქ მყოფი ხალხი და იწყო მათი ხოცვა ხერხებით, რკინის საცეხვებითა და რკინის ცულებით. ასე მოექცა დავითი ყამონის ძეთა ქალაქს და დაბრუნდა დავითი თავისი ხალხითურთ იერუსალიმში,",
			"4": "<sup>4</sup> ამის შემდეგ იყო, რომ ფილისტიმელებთან ომი ატყდა გეზერში. მაშინ სიბექაი ხუშათელმა მოკლა სიფაი, რაფიმელთა ერთი ძე, და დაზავდნენ.",
			"5": "<sup>5</sup> და კვლავ ატყდა ფილისტიმელებთან ომი. მაშინ ელხანან იაყირის ძემ დაამარცხა ლახმი, გოლიათის ძმა, რომლის შუბის ბუნი მქსოველთა ლილვისოდენა იყო.",
			"6": "<sup>6</sup> და კვლავ ატყდა ომი გათში. იყო უზარმაზარი კაცი, რომელსაც ხელ-ფეხზე ექვს-ექვსი თითი ესხა – სულ ოცდაოთხი. ისიც ბუმბერაზთა ნაშიერი იყო.",
			"7": "<sup>7</sup> და დასცინოდა ისრაელს, მაგრამ განგმირა იგი იონათან შიმყას ძემ – დავითის ძმისწულმა.",
			"8": "<sup>8</sup> ესენი შეეძინნენ რაფას გათში და ყველანი დავითისა და მის მსახურთა ხელით დაეცნენ."
		},
		"21": {
			"1": "<sup>1</sup> სატანა აღდგა ისრაელზე და აღძრა დავითი ისრაელის აღსარიცხად.",
			"2": "<sup>2</sup> და უთხრა დავითმა იოაბსა და ხალხის მთავრებს: „წადით და აღრიცხეთ ისრაელი ბეერ-შებაყიდან დანამდე და წარმომიდგინეთ, რომ ვიცოდე მათი რიცხვი.“",
			"3": "<sup>3</sup> და თქვა იოაბმა: „რაც არის, ასმაგად გაამრავლოს უფალმა თავისი ერი. განა ყველა ისინი, ჩემო ბატონო მეფევ, ჩემი ბატონის მსახურნი არ არიან? მაშ ამას რაღად ითხოვს ჩემი ბატონი? რატომ დაედოს ცოდვა ისრაელს?“",
			"4": "<sup>4</sup> მაგრამ მეფის სიტყვა კანონი იყო იოაბისთვის. და წავიდა იოაბი და მთელი ისრაელი მოიარა და იერუსალიმში დაბრუნდა.",
			"5": "<sup>5</sup> და მიართვა იოაბმა დავითს აღრიცხვის წიგნი და აღმოჩნდა მთელს ისრაელში მილიონ ასი ათასი მახვილის მაშიშვლებელი კაცი, ხოლო იუდაში ოთხას-სამოცდაათი ათასი მახვილის მაშიშვლებელი კაცი.",
			"6": "<sup>6</sup> ლევიანები და ბენიამინი მათ შორის არ აღურიცხავს, რადგან არ მოეწონა იოაბს მეფის სიტყვა.",
			"7": "<sup>7</sup> ბოროტი იყო ეს საქმე ღვთის თვალში, ამიტომ დაამარცხა ისრაელი.",
			"8": "<sup>8</sup> და უთხრა დავითმა ღმერთს: „დიდად შევცოდე, ეს რომ გავაკეთე. ახლა მიუტევე, უფალო, შეცოდება შენს მსახურს, რადგან უგუნურად მოვიქეცი.“",
			"9": "<sup>9</sup> და დაელაპარაკა უფალი გადს, დავითის ნათელმხედველს, და უთხრა:",
			"10": "<sup>10</sup> „წადი და ელაპარაკე დავითს: ასე ამბობს-თქო უფალი: ‘სამ სასჯელს შემოგთავაზებ და ერთ-ერთი ამოირჩიე, რომ დაგსაჯო.’“",
			"11": "<sup>11</sup> და მივიდა გადი დავითთან და უთხრა: „ასე ამბობს უფალი: ‘ამოირჩიე:",
			"12": "<sup>12</sup> სამი წლის შიმშილობა გირჩევნია, სამი თვის დევნილობა შენი მტრისგან და შენი მტრის მახვილი რომ მოგწვდეს, თუ სამი დღის მანძილზე უფლის მახვილი, შავი ჭირი და უფლის შემმუსვრელი ანგელოზი, ისრაელის მთელ სამკვიდრებელში.’“",
			"13": "<sup>13</sup> და უთხრა დავითმა გადს: „ძალიან მიჭირს. მაგრამ მიჯობს უფლის ხელში ჩავვარდე, რადგან დიდია მისი მოწყალება. ოღონდაც კაცის ხელში ნუ ჩავვარდები.“",
			"14": "<sup>14</sup> და მოავლინა უფალმა შავი ჭირი ისრაელში და სამოცდაათი ათასი კაცი გაწყდა ისრაელში.",
			"15": "<sup>15</sup> და გაგზავნა ღმერთმა იერუსალიმში ანგელოზი მის გასანადგურებლად. და როდესაც მან განადგურებას მიჰყო ხელი, მოხედა უფალმა და აკმარა უბედურება, და უთხრა შემმუსვრელ ანგელოზს: „კმარა, ახლა უკან წაიღე ხელი!“ უფლის ანგელოზი ორნან იებუსელის კალოსთან იდგა.",
			"16": "<sup>16</sup> და გაიხედა და დაინახა დავითმა ცისა და დედამიწის შუა მდგარი უფლის ანგელოზი; ხელში თავისი გაშიშვლებული მახვილი ეჭირა, იერუსალიმზე დაღირებული. დავითი და უხუცესნი. სახეშებურვილნი, პირქვე დაემხნენ.",
			"17": "<sup>17</sup> და უთხრა დავითმა ღმერთს: „ხალხის აღრიცხვა ხომ მე ვბრძანე? მე შევცოდე, მე ჩავიდინე ბოროტება, ამ ცხვრებმა რაღა დააშავეს? უფალო, ღმერთო ჩემო! ჩემზე და მამაჩემის სახლზე იყოს შენი ხელი, ოღონდ შენს ერზე ნუ იქნება მის დასაღუპად.“",
			"18": "<sup>18</sup> და უთხრა უფლის ანგელოზმა დავითისთვის სათქმელად გადს: „ადგეს და უფლის სამსხვერპლო ააგოს დავითმა ორნან იებუსელის კალოზე“.",
			"19": "<sup>19</sup> და წავიდა დავითი გადის სიტყვისამებრ, რომელიც უფლის სახელით ჰქონდა ნათქვამი.",
			"20": "<sup>20</sup> და გამოიხედა ორნანმა დაინახა ანგელოზი და თავის ოთხ ვაჟთან ერთად დაიმალა. ორნანი იმ დროს ხორბალს ფქვავდა.",
			"21": "<sup>21</sup> და მივიდა დავითი ორნანთან. და გამოიხედა ორნანმა და დაინახა დავითი, კალოდან გამოეგება და მდაბლად თაყვანი სცა დავითს.",
			"22": "<sup>22</sup> და უთხრა დავითმა ორნანს: „მომეცი შენი კალოს ადგილი და უფლის სამსხვერპლოს ავაშენებ მასზე, სრულ საფასურად მომეცი, რათა განერიდოს ხალხს ჟამი.“",
			"23": "<sup>23</sup> და უთხრა ორნანმა დავითს: „აიღოს და გააკეთოს მეფე ბატონმა, რაც ენებოს. აჰა, ხარები სრულადდასაწველად მომირთმევია, კევრები შეშად, ხორბალი საძღვნოდ. ყველაფერს ვიძლევი.“",
			"24": "<sup>24</sup> და უთხრა მეფე დავითმა ორნანს: „არა, სრულ საფასურად უნდა ვიყიდო შენგან, რადგან უფალს ვერც შენს საკუთრებას შევწირავ და ვერც მუქთა აღსავლენს.“",
			"25": "<sup>25</sup> ამ ადგილის საფასურად დავითმა ექვსასი შეკელი ოქრო მისცა ორნანს.",
			"26": "<sup>26</sup> იმ ადგილას აუგო დავითმა უფალს სამსხვერპლო, და მიიტანა სრულადდასაწველი და სამშვიდობო მსხვერპლი. და მოუხმო უფალს. მან კი სრულადდასაწველის სამსხვერპლოზე ზეციდან ცეცხლის მოცემით უპასუხა.",
			"27": "<sup>27</sup> და უბრძანა უფალმა ანგელოზს და მანაც თავისი მახვილი ქარქაშში ჩააგო.",
			"28": "<sup>28</sup> იმ დროს, როცა დავითმა დაინახა, რომ უფალმა უპასუხა ორნან იებუსელის კალოზე, იქ შესწირა მსხვერპლი.",
			"29": "<sup>29</sup> უფლის სავანე, მოსემ უდაბნოში რომ გააკეთა, და სრულადდასაწველის სამსხვერპლო იმჟამად გაბაონის ბორცვზე იდგა.",
			"30": "<sup>30</sup> და არ შეეძლო დავითს იქ მისვლა ღვთისთვის შესაკითხად, რადგან უფლის ანგელოზის მახვილის ეშინოდა."
		},
		"22": {
			"1": "<sup>1</sup> და თქვა დავითმა: „აჰა, უფალი ღმერთის სახლი და აჰა, სრულადდასაწველის სამსხვერპლო ისრაელისათვის.“",
			"2": "<sup>2</sup> და ბრძანება გასცა დავითმა, რომ თავი მოეყარათ ხიზნებისათვის, რომელნიც ისრაელის ქვეყნად იყვნენ, და განაწესა ისინი ქვისმთლელებად, რომ ღვთის სახლის ასაშენებლად ქვები დაეთალათ.",
			"3": "<sup>3</sup> დიდძალი რკინა გაიღო დავითმა კარიბჭის ლურსმნებისა და კაუჭების დასამზადებლად, დახარჯულ სპილენძს ხომ წონა არ ჰქონდა.",
			"4": "<sup>4</sup> არც ნაძვის ძელებს ჰქონდა თვლა, რადგან ციდონელებსა და ცორელებს მოჰქონდათ დავითისთვის.",
			"5": "<sup>5</sup> და თქვა დავითმა: „ჩემი ძე სოლომონი ყმაწვილია და სუსტი, უფლისთვის ასაშენებელი სახლი კი ყველაზე დიდებული, სახელგანთქმული და მშვენიერი უნდა იყოს მთელ ქვეყანაზე. ამიტომ ყველაფერს მოვამზადებ მისთვის“. და ბევრი გაამზადა დავითმა სიკვდილის წინ.",
			"6": "<sup>6</sup> და დაუძახა სოლომონს, თავის ძეს, და უფლისთვის, ისრაელის ღვთისთვის, სახლის აგება უანდერძა.",
			"7": "<sup>7</sup> და უთხრა დავითმა თავის ძეს, სოლომონს: „შვილო, გულით მეწადა უფლის, ღვთის სახელისთვის სახლი ამეგო.",
			"8": "<sup>8</sup> და იყო უფლის სიტყვა ჩემს მიმართ თქმული: ‘ბევრი სისხლი გაქვს დაღვრილი და დიდი ომები გაქვს გადახდილი. ამიტომ შენ არ ააშენებ ჩემს სახელზე სახლს, რადგან ბევრი სისხლი დაღვარე ქვეყნად ჩემს წინაშე.",
			"9": "<sup>9</sup> აჰა, ძე შეგეძინება და მშვიდი კაცი იქნება. ყველა მტრისაგან მოვასვენებ, ამიტომაც სოლომონი იქნება მისი სახელი და მის დღეებში მშვიდობას მივცემ ისრაელს.",
			"10": "<sup>10</sup> ის ააშენებს სახლს ჩემს სახელზე, ის იქნება ჩემთვის ძე და მე ვიქნები მისთვის მამა, და განვამტკიცებ მის სამეფო ტახტს ისრაელში უკუნისამდე.’",
			"11": "<sup>11</sup> ახლა კი, შვილო ჩემო, უფალი იყოს შენთან, რათა ხელი მოგემართოს და უფლის, შენი ღვთის სახლი ააშენო, როგორც თქვა უფალმა შენზე.",
			"12": "<sup>12</sup> უფალმა მოგცეს აზრი და გონიერება და დაგაყენოს ისრაელზე უფლის, ღვთის რჯულის დასაცავად.",
			"13": "<sup>13</sup> მაშინ იქნები წარმატებული, თუ იმ წესებსა და კანონებს დაიცავ, რომელნიც მოსეს უბრძანა უფალმა ისრაელისათვის. მტკიცედ დეგ და გამაგრდი, ნუ შიშობ და ნუ თრთი.",
			"14": "<sup>14</sup> აჰა, ჩემს სიღარიბეში უფლის სახლისათვის გავამზადე ასიათასი ქანქარი ოქრო, მილიონი ქანქარი ვერცხლი, სპილენძსა და რკინას წონა არა აქვს, რადგან უამრავია. ხე-ტყე და ქვაც გავამზადე, მაგრამ შენც შეგიძლია მიუმატო.",
			"15": "<sup>15</sup> ბევრი ხელოსანი მყავს: ტყისმჭრელები, ქვისმთლელები, ხუროები და ყველანაირი ხელობის მცოდნენი.",
			"16": "<sup>16</sup> ოქროს, ვერცხლს, სპილენძს თვლა არა აქვს. ახლა ადექი და საქმეს შეუდექი და უფალი შენთან იქნება.“",
			"17": "<sup>17</sup> და ბრძანა დავითმა, რომ ისრაელის ყველა მთავარი მიხმარებოდა მის ძეს, სოლომონს:",
			"18": "<sup>18</sup> „განა თქვენთან არ არის უფალი, ჩვენი ღმერთი? განა მან არ მოგცათ ირგვლივ მშვიდობა? მან ჩემს ხელში ჩაყარა ამ ქვეყნის მკვიდრნი, და დამორჩილდა ეს ქვეყანა უფლისა და მისი ერის წინაშე.",
			"19": "<sup>19</sup> ახლა კი გაუხსენით თქვენი გული და სული უფალს, თქვენს ღმერთს. ადექით და დაიწყეთ უფლის, ღვთის საწმიდრის შენება, რათა უფლის აღთქმის კიდობანი და ღვთის ჭურჭლეული უფლის სახელზე აგებულ სახლში გადააბრძანოთ.“"
		},
		"23": {
			"1": "<sup>1</sup> და დავითი მოხუცდა და გაძღა წლებით, და თავისი ვაჟი სოლომონი გაამეფა ისრაელზე.",
			"2": "<sup>2</sup> და თავი მოუყარა ისრაელის მთავრებს, მღვდლებსა და ლევიანებს.",
			"3": "<sup>3</sup> და აღრიცხეს ოცდაათ წელს გადაცილებული ლევიანები. და გამოვიდა სულ ოცდათვრამეტი ათასი კაცი.",
			"4": "<sup>4</sup> აქედან ოცდაოთხი ათასი უფლის სახლის საქმეს უძღვებოდა, ექვსი ათასი კი მწიგნობრები და მსაჯულები იყვნენ.",
			"5": "<sup>5</sup> ოთხიათასი კარისმცველი იყო, ოთხიათასი კი იმ საკრავებით ადიდებდა უფალს, რომელნიც დავითმა გააკეთა.",
			"6": "<sup>6</sup> და დაჰყო დავითმა ისინი ლევის, გერშონის, კეჰათის და მერარის ძეთა დასებად.",
			"7": "<sup>7</sup> გერშონელთაგან იყვნენ: ლაყდანი და შიმყი.",
			"8": "<sup>8</sup> ლაყდანის ძენი: უფროსი იეხიელი, ზეთამი და იოელი – სამნი.",
			"9": "<sup>9</sup> შიმყის ძენი: შელომითი, ხაზიელი და ჰარანი – სამნი. ესენი არიან ლაყდანის მამისსახლთა თავკაცები.",
			"10": "<sup>10</sup> შიმყის ძენი: იახათი, ზინა, იეყუში და ბერიყა. ესენი არიან შიმყის ძენი – ოთხნი.",
			"11": "<sup>11</sup> იახათი უფროსი იყო, ზიზა – მომდევნო. იეყუშსა და ბერიყას ძალიან ცოტა შვილები ჰყავდათ. ამიტომ აღრიცხვისას ერთ მამისსახლს შეადგენდნენ.",
			"12": "<sup>12</sup> კეჰათის ძენი: ყამრამი, იცჰარი, ხებრონი და ყუზიელი – ოთხნი.",
			"13": "<sup>13</sup> ყამრამის ძენი: აარონი და მოსე. აარონი იმისთვის გამოირჩია, რომ წმიდათა წმიდისთვის ეკურთხებინათ იგი და მისი ძენი უკუნისამდე, რათა მათ ეკმიათ უფლის წინაშე, რათა სამსახური გაეწიათ მისთვის და მისი სახელით უკუნისამდე ეკურთხებინათ.",
			"14": "<sup>14</sup> მოსე ღვთისკაცი იყო და მისი ძენი ლევიანთა ტომს მიათვალეს.",
			"15": "<sup>15</sup> მოსეს ძენი: გერშომი, ელიეზერი.",
			"16": "<sup>16</sup> გერშომის ძენი: შებუელი – უფროსი.",
			"17": "<sup>17</sup> ელიეზერის ძენი იყვნენ: რეხაბია – უფროსი. ელიეზერს მეტი შვილი არც ჰყოლია, რეხაბიას ძენი კი ძალიან გამრავლდნენ.",
			"18": "<sup>18</sup> იცჰარის ძენი: შელომითი – უფროსი.",
			"19": "<sup>19</sup> ხებრონის ძენი: იერიაჰუ – უფროსი, ამარია – მეორე, იახაზიელი – მესამე, იეკამყამი – მეოთხე,",
			"20": "<sup>20</sup> ყუზიელის ძენი: მიქა – უფროსი, იშია – მეორე.",
			"21": "<sup>21</sup> მერარის ძენი: მახლი და მუში, მახლის ძენი: ელეაზარი და კიში.",
			"22": "<sup>22</sup> ელეაზარი ისე მოკვდა, რომ ვაჟი არ ჰყოლია, მხოლოდ ასულები დარჩა. ისინი კიშის ძეებმა, ბიძაშვილებმა, შეირთეს.",
			"23": "<sup>23</sup> მუშის ძენი: მახლი, ყედერი და იერემოთი – სამნი.",
			"24": "<sup>24</sup> აჰა, ლევის ძენი, თავ-თავიანთი მამისსახლების მიხედვით, მამისსახლთა თავკაცები, სახელებისა და სულადობის რიცხვის მიხედვით აღრიცხულნი, რომელნიც უფლის სახლის სამსახურის საქმეს უძღვებოდნენ, ოც წელს გადაცილებულნი.",
			"25": "<sup>25</sup> რადგან თქვა დავითმა: „უფალმა ღმერთმა მისცა მშვიდობა ისრაელს, თავის ერს, და იერუსალიმში დამკვიდრდა იგი უკუნისამდე.",
			"26": "<sup>26</sup> და აღარც ლევიანებს დასჭირდებათ სამსახურის გასაწევად სავანისა და მისი ჭურჭლეულის ტარება.“",
			"27": "<sup>27</sup> დავითის უკანასკნელი სიტყვების მიხედვით აღრიცხეს ოც წელს გადაცილებული ლევიანები,",
			"28": "<sup>28</sup> რათა ისინი მდგარიყვნენ აარონის ძეთა გვერდით, ემსახურათ უფლის სახლში, ეზოებში და ოთახებში, სუფთად შეენახათ ყოველი სიწმიდე და აღესრულებინათ ღვთის სახლის სამსახური.",
			"29": "<sup>29</sup> და ეზრუნათ საწინაშეო პურებზე, ძღვენზე, უფუარ კვერებზე, ტაფებზე და შესაწვავზე, ყველაფრის არწყვა-აწონაზე,",
			"30": "<sup>30</sup> დილა-საღამოობით უფლის ქება-დიდებაზე,",
			"31": "<sup>31</sup> და ყველა სრულადდასაწველზე უფლისათვის შაბათობით, ახალმთვარობების დღესასწაულების განსაზღვრულ რიცხვებში, როგორც მათთვისაა დადგენილი, უფლის წინაშე მარად აღსასრულებლად,",
			"32": "<sup>32</sup> რომ დაიცვან დადგენილება სადღესასწაულო კარვის შესახებ, დადგენილება საწმიდრის შესახებ და დადგენილება აარონის ძეთა – მათი ძმების – შესახებ, უფლის სახლში სამსახურის გასაწევად."
		},
		"24": {
			"1": "<sup>1</sup> აარონის ძეთ თავიანთი დასები აქვთ, აარონის ძენი: ნადაბი, აბიჰუ, ელეაზარი და ითამარი.",
			"2": "<sup>2</sup> ნადაბი და აბიჰუ მამაზე ადრე დაიხოცნენ; ისე, რომ ვაჟები არ ჰყოლიათ. ელეაზარი და ითამარი შეუდგნენ მღვდელმსახურებას.",
			"3": "<sup>3</sup> და გაამწესა დავითმა ცადოკი, ელეაზარის ძეთაგან, და ახიმელექი, ითამარის ძეთაგან, თავ-თავიანთ სამსახურში, თავ-თავიანთი მოვალეობისდა მიხედვით.",
			"4": "<sup>4</sup> და აღმოჩნდა, რომ ელეაზარის შვილებში კაცები უფრო მეტნი იყვნენ, ვიდრე ითამარის შვილებში, და დაანაწილეს მათ ისინი: ელეაზარის ძეთა მამისსახლს თექვსმეტი თავკაცი არგუნეს, ხოლო ითამარის ძეთა მამისსახლს – რვა თავკაცი.",
			"5": "<sup>5</sup> ერთმანეთში წილისყრით დაინაწილეს, რადგან საწმიდრის წინამძღოლები ელეაზარისა და ითამარის ძეთაგან იყვნენ.",
			"6": "<sup>6</sup> და აღნუსხა ისინი შემაყია ნათანელის ძემ, ლევიანთა მწიგნობარმა, მეფისა და მთავართა წინაშე, ცადოკ მღვდლისა და ახიმელექ აბიათარის ძის წინაშე, მღვდელთა და ლევიანების მამისსახლთა თავკაცების წინაშე. ერთი მამისსახლი ელეაზარს უყრიდა წილს და ერთი ითამარს უყრიდა წილს.",
			"7": "<sup>7</sup> და პირველი წილი იეჰოიარიბს შეხვდა, მეორე – იედაყიას,",
			"8": "<sup>8</sup> მესამე – ხარიმს, მეოთხე – სეყორიმს.",
			"9": "<sup>9</sup> მეხუთე – მალქიას, მეექვსე – მიამინს,",
			"10": "<sup>10</sup> მეშვიდე – ჰაკოცს, მერვე – აბიას,",
			"11": "<sup>11</sup> მეცხრე – იესოს, მეათე – შექანიაჰუს,",
			"12": "<sup>12</sup> მეთერთმეტე – ელიაშიბს, მეთორმეტე – იაკიმს,",
			"13": "<sup>13</sup> მეცამეტე – ხუფას, მეთოთხმეტე – იეშებაბს,",
			"14": "<sup>14</sup> მეთხუთმეტე – ბილგას. მეთექვსმეტე – იმერს.",
			"15": "<sup>15</sup> მეჩვიდმეტე – ხეზირს, მეთვრამეტე – ჰაფიცეცს,",
			"16": "<sup>16</sup> მეცხრამეტე – ფეთახიას, მეოცე – იეხეზკელს,",
			"17": "<sup>17</sup> ოცდამეერთე – იაქინს, ოცდამეორე – გამულს,",
			"18": "<sup>18</sup> ოცდამესამე – დელაიაჰუს, ოცდამეოთხე – მაყაზიაჰუს.",
			"19": "<sup>19</sup> ეს არის მათი განაწესი, თუ როგორ უნდა მისულიყვნენ უფლის სახლში იმ სამსახურის გასაწევად, მათი მამის, აარონის, ხელით რომ იყო დაწესებული ისე, როგორც უფალმა, ისრაელის ღმერთმა უბრძანა მას.",
			"20": "<sup>20</sup> დანარჩენი ლევიანები: ყამრამის ძეთაგან: შუბაელი, შუბაელის ძეთაგან: იეხდეიაჰუ.",
			"21": "<sup>21</sup> რეხაბიაჰუსაგან: რეხაბიაჰუს ძეთაგან იშია იყო თავკაცი.",
			"22": "<sup>22</sup> იცჰარელთაგან: შელომოთი. შელომოთის ძეთაგან: იახათი.",
			"23": "<sup>23</sup> ხებრონის ძეთაგან: იერიაჰუ, ამარიაჰუ – მეორე, იახაზიელი – მესამე, იეკამყამი – მეოთხე.",
			"24": "<sup>24</sup> ყუზიელის ძეთაგან: მიქა. მიქას ძეთაგან: შამირი.",
			"25": "<sup>25</sup> მიქას ძმა იყო იშია. იშიას ძეთაგან: ზაქარია.",
			"26": "<sup>26</sup> მერარის ძენი: მახლი და მუში. იაყაზიაჰუს ძეთაგან: ბენო.",
			"27": "<sup>27</sup> მერარის ძენი იაყაზიაჰუსგან: ბენო, შოჰამი, ზაქური და ყიბრი.",
			"28": "<sup>28</sup> მახლისგან: ელეაზარი, რომელსაც ვაჟები არ ჰყოლია.",
			"29": "<sup>29</sup> კიშისგან: კიშის ძე იერახმეელი.",
			"30": "<sup>30</sup> მუშის ძენი: მახლი, ყედერი, იერიმოთი. აჰა, ლევიანები, თავ-თავიანთი მამისსახლებისდა მიხედვით.",
			"31": "<sup>31</sup> მამისსახლთა თავკაცებმა უმცროსი ძმების თანასწორად ყარეს წილი თავიანთი ძმების – აარონიანთა მსგავსად, მეფე დავითის, ცადოკის, ახიმელექის, მღვდლებისა და ლევიანების მამისსახლთა თავკაცების წინაშე."
		},
		"25": {
			"1": "<sup>1</sup> დავითმა და მისმა მხედართმთავრებმა სამსახურისათვის დაანაწილეს ასაფის, ჰემანის და იედუთუნის ძენი, რომელნიც ბობღნებზე, ქნარებსა და წინწილებზე უკრავდნენ. იმათი რიცხვი, ვინც თავიანთ სამსახურის საქმეს ეწეოდნენ, იყო:",
			"2": "<sup>2</sup> ასაფის ძეთაგან: ზაქური, იოსები, ნეთანია, ასარელა – ასაფის ძენი, რომელნიც ასაფის ლოტბარობითა და მეფის ხელმძღვანელობით უკრავდნენ.",
			"3": "<sup>3</sup> იედუთუნისგან, იედუთუნის ძენი: გედალიაჰუ, ცერი, იეშაყიაჰუ, ხაშაბიაჰუ, მათითიაჰუ, შიშა – ექვსნი. თავიანთი მამის, იედუთუნის, ხელქვეით იყვნენ, რომელიც ბობღანზე უკრავდა უფლის საქებ-სადიდებელს.",
			"4": "<sup>4</sup> ჰემანისგან, ჰემანის ძენი: ბუკიაჰუ, მათანიაჰუ, ყუზიელი, შებუელი, იერიმოთი, ხანანია, ხანანი, ელიათა, გიდალთი, რომამთი-ყეზერი, იოშბეკაშა, მალოთი, ჰოთირი, მახაზიოთი.",
			"5": "<sup>5</sup> ყველა ესენი იმ ჰემანის ძენი არიან, რომელიც მეფის ნათელმხედველი იყო, უფლის სიტყვების გამოსაცხადებლად და მისი დიდების რქის ასამაღლებლად. ღმერთმა ჰემანს თორმეტი ვაჟი და სამი ასული მისცა.",
			"6": "<sup>6</sup> ყველა ესენი მათი მამის ლოტბარობით გალობდნენ უფლის სახლში წინწილებით, ქნარებით, ბობღნებით, ღვთის სახლის სამსახურის გასაწევად მეფის, ასაფის, იედუთუნისა და ჰემანის ხელმძღვანელობით.",
			"7": "<sup>7</sup> უფლისათვის გალობაში განსწავლულთა და ყველა მცოდნეთა რიცხვი, თავიანთ ძმებთან ერთად, იყო ორასოთხმოცდარვა.",
			"8": "<sup>8</sup> სამსახურის რიგზე წილი თანასწორად ყარეს, დიდმა და პატარამ, სწავლულმა და მოსწავლემ.",
			"9": "<sup>9</sup> პირველი წილი ერგო ასაფს იოსებისათვის, მეორე – გედალიაჰუს, იგი, მისი ძმები და შვილები – თორმეტნი.",
			"10": "<sup>10</sup> მესამე – ზაქურს, მისი ძენი, და ძმები – თორმეტნი.",
			"11": "<sup>11</sup> მეოთხე – იცრის, მისი ძენი და ძმანი – თორმეტნი.",
			"12": "<sup>12</sup> მეხუთე – ნეთანიაჰუს, მისი ძენი და ძმანი – თორმეტნი.",
			"13": "<sup>13</sup> მეექვსე – ბუკიაჰუს, მისი ძენი და ძმანი – თორმეტნი.",
			"14": "<sup>14</sup> მეშვიდე – იესარელას, მისი ძენი და ძმანი – თორმეტნი.",
			"15": "<sup>15</sup> მერვე – ესაიას, მისი ძენი და ძმანი – თორმეტნი.",
			"16": "<sup>16</sup> მეცხრე – მათანიაჰუს, მისი ძენი და ძმანი – თორმეტნი.",
			"17": "<sup>17</sup> მეათე – შიმყის, მისი ძენი და ძმანი – თორმეტნი.",
			"18": "<sup>18</sup> მეთერთმეტე – ყაზარელს, მისი ძენი და ძმანი – თორმეტნი.",
			"19": "<sup>19</sup> მეთორმეტე – ხაშაბიას, მისი ძენი და ძმანი – თორმეტნი.",
			"20": "<sup>20</sup> მეცამეტე – შუბაელს, მისი ძენი და ძმანი – თორმეტნი.",
			"21": "<sup>21</sup> მეთოთხმეტე – მათითიაჰუს, მისი ძენი და ძმანი – თორმეტნი.",
			"22": "<sup>22</sup> მეთხუთმეტე – იერემოთს, მისი ძენი და ძმანი – თორმეტნი.",
			"23": "<sup>23</sup> მეთექვსმეტე – ხანანიაჰუს, მისი ძენი და ძმანი – თორმეტნი.",
			"24": "<sup>24</sup> მეჩვიდმეტე – იოშბეკაშას, მისი ძენი და ძმანი – თორმეტნი.",
			"25": "<sup>25</sup> მეთვრამეტე – ხანანის, მისი ძენი და ძმანი – თორმეტნი.",
			"26": "<sup>26</sup> მეცხრამეტე – მალოთის, მისი ძენი და ძმანი – თორმეტნი.",
			"27": "<sup>27</sup> მეოცე – ელიათას, მისი ძენი და ძმანი – თორმეტნი.",
			"28": "<sup>28</sup> ოცდამეერთე – ჰოთირს, მისი ძენი და ძმანი – თორმეტნი.",
			"29": "<sup>29</sup> ოცდამეორე – გიდალთის, მისი ძენი და ძმანი – თორმეტნი.",
			"30": "<sup>30</sup> ოცდამესამე – მახაზიოთს, მისი ძენი და ძმანი – თორმეტნი.",
			"31": "<sup>31</sup> ოცდამეოთხე – რომამთი-ყეზერს, მისი ძენი და ძმანი – თორმეტნი."
		},
		"26": {
			"1": "<sup>1</sup> კარისმცველთა დასები: კორახელთაგან: მეშელემიაჰუ, კორეს ძე, ასაფის ძეთაგან.",
			"2": "<sup>2</sup> მეშელემიაჰუს ძენი: ზაქარია – პირმშო, იედიყაელი – მეორე, ზებადიაჰუ – მესამე, იათნიელი – მეოთხე,",
			"3": "<sup>3</sup> ყელამი – მეხუთე, იეჰოხანანი – მეექვსე, ელიეჰოყენაი – მეშვიდე.",
			"4": "<sup>4</sup> ყობედ-ედომის ძენი; შემაყია – პირმშო, იეჰოზაბადი – მეორე, იოახი – მესამე, საქარი – მეოთხე, ნეთანელი – მეხუთე,",
			"5": "<sup>5</sup> ყამიელი – მეექვსე, ისაქარი – მეშვიდე, ფეყულთაი – მერვე, რადგან ღმერთმა აკურთხა იგი.",
			"6": "<sup>6</sup> მის ძეს, შემაყიას, ვაჟები შეეძინა, რომელნიც მამისსახლს განაგებდნენ, ვინაიდან მაგარი ვაჟკაცები იყვნენ.",
			"7": "<sup>7</sup> შემაყიას ძენი: ყოთნი, რეფაელი, ყობედი, ელზაბადი, მისი ძმანი, მაგარი ხალხი, ელიჰუ და სემაქიაჰუ.",
			"8": "<sup>8</sup> ყველანი ყობედ-ედომის ძენი იყვნენ. ისინი თავიანთი ძეებითა და ძმებით მაგარი ვაჟკაცები იყვნენ და სამსახურის გაწევის უნარი ჰქონდათ. ყობედ-ედომს ასეთები სულ სამოცდაორი ჰყავდა.",
			"9": "<sup>9</sup> მეშელემიაჰუს თვრამეტი ვაჟი და ძმა ჰყავდა, მაგარი ვაჟკაცები.",
			"10": "<sup>10</sup> ხოსას, მერარის ერთ-ერთი ძეთაგანის, ვაჟები: უფროსი შიმრი იყო – თუმცა პირმშო არ ყოფილა – მამამისმა არგუნა მას უფროსობა;",
			"11": "<sup>11</sup> ხილკიაჰუ მომდევნო იყო, ტებალიაჰუ – მესამე, ზაქარია – მეოთხე. ხოსას სულ ცამეტი ვაჟი და ძმა ჰყავდა.",
			"12": "<sup>12</sup> კარისმცველთა დასს, მოყმეთა თავკაცებს, ჩაბარდათ დაცვა, თავიანთ ძმებთან ერთად, უფლის სახლში სამსახურის გასაწევად.",
			"13": "<sup>13</sup> ყველა კარიბჭეზე წილი ყარეს, როგორც დიდებმა, ისე პატარებმა, თავ-თავიანთი მამისსახლების მიხედვით.",
			"14": "<sup>14</sup> აღმოსავლეთის მხარე შელემიაჰუს ერგო წილად. მის ვაჟს ზაქარიას, გონიერ მრჩეველს, უყარეს წილი და მას წილად ჩრდილოეთის მხარე შეხვდა.",
			"15": "<sup>15</sup> ყობედ-ედომს სამხრეთის მხარე ერგო წილად, მის ძეებს – საკუჭნაოებთან.",
			"16": "<sup>16</sup> შუფიმსა და ხოსას დასავლეთის მხარე შეხვდათ, შალექეთის ჭიშკართან, საიდანაც გზა ადის და დაცვა დაცვის პირისპირ დგას.",
			"17": "<sup>17</sup> აღმოსავლეთით ექვსი ლევიანი იდგა, ჩრდილოეთით – დღეში ოთხი, სამხრეთით – დღეში ოთხი, საკუჭნაოებთან – ორ-ორი.",
			"18": "<sup>18</sup> ფარბარის დასავლეთით, გზაზე, ოთხნი იდგნენ, თავად ფარბართან – ორნი.",
			"19": "<sup>19</sup> აჰა. კორესა და მერარის ძეების კარისმცველთა დასები.",
			"20": "<sup>20</sup> ლევიანთაგან: ახია ღვთის სახლის საგანძურსა და შენაწირი ნივთების საგანძურს დარაჯობდა.",
			"21": "<sup>21</sup> ლაყდანის ძენი, გერშონელთა ძენი – ლაყდანისგან. მამისსახლის თავკაცები ლაყდან გერშონელისგან: იეხიელი.",
			"22": "<sup>22</sup> იეხიელის ძენი: ზეთამი და მისი ძმა იოელი უფლის სახლის საგანძურს დარაჯობდნენ,",
			"23": "<sup>23</sup> ყამრამელებთან, იცჰარელებთან, ხებრონელებთან და ყუზიელებთან ერთად.",
			"24": "<sup>24</sup> შებუელი ძე გერშომისა, მოსეს ძისა, საგანძურის მთავარი ზედამხედველი იყო.",
			"25": "<sup>25</sup> მისი ძმები ელიეზერისგან; მისი ძე რეხაბიაჰუ, მისი ძე იეშაყიაჰუ, მისი ძე იორამი, მისი ძე ზიქრი, მისი ძე შელომითი.",
			"26": "<sup>26</sup> ეს შელომითი თავისი ძმებითურთ დარაჯობდა შემოწირულობათა მთელ საგანძურს, რომელიც მეფე დავითმა, მამისსახლის თავკაცებმა, ათასისთავებმა, ასისთავებმა და სარდლებმა შესწირეს.",
			"27": "<sup>27</sup> ომიდან და ნაალაფრიდან სწირავდნენ უფლის სახლის შესაკეთებლად.",
			"28": "<sup>28</sup> ყველაფერი, რაც სამუელ წინასწარმეტყველმა, საულ კიშის ძემ, აბნერ ნერის ძემ, იოაბ ცერუიას ძემ შესწირეს, ყველა ეს შენაწირი შელომითისა და მისი ძმების ხელში იყო.",
			"29": "<sup>29</sup> იცჰარელთაგან ქენანიაჰუ და მისი ძენი ისრაელში საგარეო სამსახურზე იყვნენ განწესებულნი მზირებად და მსაჯულებად.",
			"30": "<sup>30</sup> ხებრონელთაგან: ხაშაბიაჰუ და მისი ძმები, მაგარი ვაჟკაცები, ათას შვიდასნი, დანიშნულნი იყვნენ ისრაელზე იორდანეს გაღმიდან დასავლეთამდე, უფლის ყოველგვარი საქმისათვის და მეფის ყოველგვარი სამსახურისათვის.",
			"31": "<sup>31</sup> ხებრონელებს იერია ხებრონელი ჰყავდათ თავკაცად, თავიანთ მოდგმასა და მამისსახლში, დავითის მეფობის მეორმოცე წელს გამოიკითხეს და მათ შორის მაგარი ვაჟკაცები აღმოაჩინეს გილყადის იაყზერში.",
			"32": "<sup>32</sup> მათი ძმანი, მაგარი ვაჟკაცები, ორიათას შვიდასნი იყვნენ – მამისსახლის თიავკაცნი. დავით მეფემ ისინი რეუბენიანებში, გადელებში და მენაშეს ნახევარტომში გაამწესა ღვთის ყველა საქმისთვის და სამეფო საქმისთვის."
		},
		"27": {
			"1": "<sup>1</sup> ესენი არიან ისრაელის ძენი მათი რიცხვის მიხედვით, თავ-თავიანთ მამისსახლთა თავკაცნი, ათასისთავები, ასისთავები და მათი მზირები, რომელნიც მეფეს ემსახურებოდნენ დასების ყველა საქმეში, მიდი-მოდიოდნენ ყოველთვიურად, წელიწადის ყოველ თვეში. თითოეული დასი ოცდაოთხი ათასისგან შესდგებოდა.",
			"2": "<sup>2</sup> პირველ დასზე პირველი თვისთვის დადგენილი იყო იოშაბყამ ზაბდიელის ძე; მის დასში ოცდაოთხი ათასი იყო.",
			"3": "<sup>3</sup> იგი იყო ფერეცის შვილთაგანი, პირველი თვის ყველა მხედართმთავრის მეთაური.",
			"4": "<sup>4</sup> მეორე თვის დასზე იყო დოდაი ახოხელი, მის დასშივე იყო მთავარი მიკლოთი. მის დასში ოცდაოთხი ათასი იყო.",
			"5": "<sup>5</sup> მესამე თვის მესამე მხედართმთავარი იყო ბენაიაჰუ, იეჰოიადაყ მღვდლის ვაჟი, მის. დასშიც ოცდაოთხი ათასი იყო.",
			"6": "<sup>6</sup> ეს ბენაიაჰუ ოცდაათ მოყმეთაგანი და მათი მეთაური იყო. მისი ძე ყამიზაბადიც მის დასში იყო.",
			"7": "<sup>7</sup> მეოთხე თვეში მეოთხე იყო ყასაელი, იოაბის ძმა. მის შემდეგ კი მისი ძე ზებადია. მის დასშიც ოცდაოთხი ათასი იყო.",
			"8": "<sup>8</sup> მეხუთე თვეში მეხუთე მხედართმთავარი იყო შამუთ იზრახელი. მის დასშიც ოცდაოთხი ათასი იყო.",
			"9": "<sup>9</sup> მეექვსე თვეში მეექვსე იყო ყირა ყიკეშის ძე, თეკოაყელი, მის დასშიც ოცდაოთხი ათასი იყო.",
			"10": "<sup>10</sup> მეშვიდე თვეში მეშვიდე იყო ხელეც ფელონელი, ეფრემის ძეთაგან. მის დასშიც ოცდაოთხი ათასი იყო.",
			"11": "<sup>11</sup> მერვე თვეში მერვე იყო სიბექაი ხუშათელი, ზარხელთაგან. მის დასშიც ოცდაოთხი ათასი იყო.",
			"12": "<sup>12</sup> მეცხრე თვეში მეცხრე იყო აბიყეზერ ყანეთოთელი, ბენიამინელთაგან. მის დასშიც ოცდაოთხი ათასი იყო.",
			"13": "<sup>13</sup> მეათე თვეში მეათე იყო მაჰარაი ნეტოფათელი, ზარხელთაგან. მის დასშიც ოცდაოთხი ათასი იყო.",
			"14": "<sup>14</sup> მეთერთმეტე თვეში მეთერთმეტე იყო ბენაია ფირყათონელი, ეფრემელთაგან, მის დასშიც ოცდაოთხი ათასი იყო.",
			"15": "<sup>15</sup> მეთორმეტე თვეში მეთორმეტე იყო ხელდაი ნეტოფათელი, ყათნიელთაგან. მის დასშიც ოცდაოთხი ათასი იყო.",
			"16": "<sup>16</sup> ისრაელის შტოებზე: რეუბენის ძეთა წინამძღოლი იყო ელიეზერ ზიქრის ძე; სიმონელებისა – შეფატიაჰუ მაყაქას ძე;",
			"17": "<sup>17</sup> ლევიანებისა – ხაშაბია კემუელის ძე; აარონისა – ცადოკი;",
			"18": "<sup>18</sup> იუდასი – ელიჰუ, დავითის ძმათაგანი; ისაქარისა – ყომრი, ძე მიქაელისა;",
			"19": "<sup>19</sup> ზებულუნისა – იშმაყიაჰუ, ყობადიაჰუს ძე; ნაფთალისა – იერიმოთ ყაზრიელის ძე;",
			"20": "<sup>20</sup> ეფრემის ძეთა – ჰოშეაყ ყაზაზიაჰუს ძე, მენაშეს ნახევარტომისა – იოელ ფედიაჰუს ძე;",
			"21": "<sup>21</sup> მენაშეს ნახევარტომისა – იდო ზაქარიას ძე. ბენიამინისა – იაყასიელ აბნერის ძე;",
			"22": "<sup>22</sup> დანისა – ყაზარელ იეროხამის ძე. ესენი არიან ისრაელის ტომთა მთავარნი.",
			"23": "<sup>23</sup> ოცი წლისანი და მის ქვემოთ არ აღურიცხავს დავითს, ვინაიდან უფალმა ისრაელს ცის ვარსკვლავებივით გამრავლება აღუთქვა.",
			"24": "<sup>24</sup> იოაბ ცერუიას ძემ დაიწყო აღრიცხვა, მაგრამ არ დაამთავრა. ამის გამო ისრაელს რისხვა დაატყდა. ეს აღრიცხვა დავით მეფის მატიანის აღრიცხვაშიც არ შესულა.",
			"25": "<sup>25</sup> სამეფო საუნჯეებზე იყო ყაზმავეთ ყადიელის ძე. ხოლო იმ საუნჯეებზე, რომელიც მინდორში, ქალაქებში და ციხე-სიმაგრეებში იყო – იეჰონათან ყუზიაჰუს ძე.",
			"26": "<sup>26</sup> მინდვრის სამუშაოების შემსრულებლებზე, მიწას რომ ამუშავებდნენ – ყეზრი ქელუბის ძე.",
			"27": "<sup>27</sup> ვენახებზე – შიმყი რამათელი, ყურძნისა და ღვინის მარაგზე – ზაბდი შიფმელი,",
			"28": "<sup>28</sup> ზეთისხილებზე და ლეღვებზე, დაბლობში რომ არის – ბაყალ-ხანან გედერელი; ზეთის მარაგზე – იოყაში.",
			"29": "<sup>29</sup> ხოლო იმ ნახირზე, შარონში რომ სძოვდა – შიტრაი შარონელი, ნახირზე ხეობებში – შაფატ ყადლაის ძე,",
			"30": "<sup>30</sup> აქლემებზე – იობილ ისმაელიანი, სახედრებზე – იეხდეიაჰუ მერონოთელი,",
			"31": "<sup>31</sup> ცხვარზე – იაზიზ ჰაგერელი. ყველა ესენი დავით მეფის ქონების მოურავები იყვნენ,",
			"32": "<sup>32</sup> იონათანი, დავითის ბიძა, მრჩეველი იყო, კაცი გონიერი და მწიგნობარი. იეხიელი ხაქმონის ძე მეფისწულებთან იყო.",
			"33": "<sup>33</sup> ახითოფელი მეფის მრჩეველი იყო, ხუშაი არქელი კი – მეფის მეგობარი.",
			"34": "<sup>34</sup> ახითოფელის შემდეგ იყვნენ იეჰოიადაყ ბენაიაჰუს ძე და აბიათარი. მეფის მხედართმთავარი იყო იოაბი."
		},
		"28": {
			"1": "<sup>1</sup> და შეყარა დავითმა იერუსალიმში ისრაელის ყველა მთავარი. შტოთა მთავარნი, დასების მეთაურები, მეფის სამსახურში მყოფნი, ათასისთავები, ასისთავები, მეფისა და მის ძეთა მთელი საქონ-საყოლის მოურავები, სახლთუხუცესები და ყველა მაგარი ვაჟკაცი.",
			"2": "<sup>2</sup> და ფეხზე წამოდგა დავითი და თქვა: „ყური დამიგდეთ, ჩემო ძმებო და ჩემო ხალხო! გულით მეწადა ამეშენებინა განსასვენებელი სახლი უფლის აღთქმის კიდობნისათვის, ჩვენი ღვთის ფერხთა დასადგმელი, და მოვემზადე ასაშენებლად,",
			"3": "<sup>3</sup> მაგრამ ღმერთმა მითხრა: ‘ჩემს სახელზე სახლს ნუ ააშენებ, რადგან მეომარი კაცი ხარ და სისხლი გაქვს დაღვრილი.’",
			"4": "<sup>4</sup> და მაინც უფალმა, ისრაელის ღმერთმა, მამაჩემის სახლიდან ამომირჩია, რათა მთელი ისრაელის მეფე გავმხდარიყავი საუკუნოდ. რადგან იუდა ამოირჩია მთავრად, იუდას სახლიდან კი მამაჩემის სახლი ამოირჩია, მამაჩემის ძეთაგან კი ჩემი გამეფება ინება მთელ ისრაელზე.",
			"5": "<sup>5</sup> ყველა ჩემს ვაჟს შორის კი, რადგან მრავალი შვილი მომცა უფალმა, ჩემი ძე სოლომონი ამოირჩია, რათა დამჯდარიყო უფლის სამეფოს ტახტზე, ისრაელზე.",
			"6": "<sup>6</sup> და მითხრა: ‘შენი ძე, სოლომონი, ააშენებს ჩემს სახლსა და ეზოებს, რადგან იგი ძედ ამოვირჩიე და მე ვიქნები მისი მამა.",
			"7": "<sup>7</sup> უკუნისამდე გავამყარებ მის სამეფოს, თუ იგი მტკიცედ დაიცავს ჩემს მცნებებს და რჯულდებას, როგორც დღემდე იცავდა.’",
			"8": "<sup>8</sup> ახლა კი მთელი ისრაელის, უფლის კრებულის, თვალწინ ჩვენი ღმერთის გასაგონად გეუბნებით: დაიცავით და გამოიკითხეთ უფლის, თქვენი ღმერთის, ყველა მცნება, რათა დოვლათიან ქვეყანას დაეპატრონოთ და თქვენს შემდეგ თქვენს შვილებს დაუმკვიდროთ უკუნისამდე,",
			"9": "<sup>9</sup> ხოლო შენ, სოლომონ, ძეო ჩემო, იცოდე მამაშენის ღმერთი მთელი გულითა და ხალისიანი სულით ემსახურე, რადგან ყოველთა გულის მხილველია უფალი და ყველა ფიქრისა და ზრახვის მცოდნეა. ძებნას თუ დაუწყებ მას, იპოვი კიდეც; ხოლო თუ მიატოვებ, ისიც სამუდამოდ მიგატოვებს.",
			"10": "<sup>10</sup> ახლა უყურე, რაკი უფალმა გამოგარჩია უფლის საწმიდრის ამშენებლად, მტკიცედ დეგ და გააკეთე.“",
			"11": "<sup>11</sup> და მისცა დავითმა სოლომონს, თავის ძეს, გეგმა კარიბჭისა, მისი სახლებისა, განძთსაცავებისა, ქორედებისა, შიდა ოთახებისა და კიდობნის სახლისა.",
			"12": "<sup>12</sup> ასევე ყველაფრის გეგმა, რისი გაკეთებაც გულში ჰქონდა უფლის სახლის ეზოებისთვის, ირგვლივ განლაგებული ყველა ოთახისთვის, ღვთის სახლის საგანძურისა და შემოწირულობათა საგანძურისათვის.",
			"13": "<sup>13</sup> მღვდელთა და ლევიანთა დასებისთვის, უფლის სახლის სამსახურის ყველა საქმისთვის, უფლის სახლის სამსახურისთვის საჭირო ყველა ჭურჭლისთვის,",
			"14": "<sup>14</sup> ოქროულობისთვის, ოქროს წონით, ყველა სამსახურებელი ჭურჭლისთვის, ვერცხლის ყველა ჭურჭლისთვის, წონასთან ერთად, ყველა სამსახურებელი ჭურჭლისთვის.",
			"15": "<sup>15</sup> წონა ოქროს სასანთლეებისა და მისი ოქროს ლამპრებისა, თითოეული სასანთლისა და ლამპრების წონასთან ერთად; ასევე ვერცხლის სასანთლეები, მისი სასანთლეებისა და ლამპრების წონასთან ერთად, როგორც თითოეული სასანთლის სამსახურის წესია.",
			"16": "<sup>16</sup> ოქროს წონა საკურთხის ტაბლებისათვის და ყველა მაგიდისთვის, ასევე ვერცხლის ტაბლებისთვის.",
			"17": "<sup>17</sup> ხალასი ოქრო ჩანგლებისთვის, ჯამებისთვის, ოქროს ფიალებისთვის, თითოეული ფიალის წონასთან ერთად, აგრეთვე ვერცხლის ფიალებისთვის, თითოეული ფიალის წონასთან ერთად.",
			"18": "<sup>18</sup> საკმევის სამსხვერპლოსათვის გასუფთავებული ოქრო წონასთან ერთად, და გეგმა ეტლისა და ოქროს ქერუბიმებისა, რომელთა ფრთებიც გადაშლილი და გადაფარებული იყო უფლის აღთქმის კიდობანზე.",
			"19": "<sup>19</sup> „ეს ყველაფერი უფლის ხელით არის ჩაწერილი, მან ჩამაგონა გეგმის ყოველი წვრილმანი,“ – [თქვა დავითმა.]",
			"20": "<sup>20</sup> და უთხრა დავითმა თავის ძეს სოლომონს: „მტკიცედ დეგ და გამხნევდი, და შეუდექი საქმეს. ნუ შიშობ, და ნუ ძრწი, რადგან უფალი ღმერთი, ჩემი ღმერთი, შენთან არის, არ განგიდგება იგი და არ მიგატოვებს, ვიდრე არ შეასრულებ უფლის სახლის სამსახურის ყველა საქმეს.",
			"21": "<sup>21</sup> აჰა, მღვდელთა და ლევიანთა დასები, ღვთის სახლის ყოველგვარი სამსახურის გასაწევად. მხარში გიდგანან ყველა საქმეში ბეჯითნი და ყველა სამუშაოს მცოდნენი. მთავრები და მთელი ერი შენს ყველა ბრძანებაზეა.“"
		},
		"29": {
			"1": "<sup>1</sup> და უთხრა დავით მეფემ მთელ კრებულს: „სოლომონი, ჩემი ძე, რომელიც ღმერთმა ამოირჩია, ნორჩია და სუსტი, ეს საქმე კი ძალზე დიდია, რადგან ეს შენობა კაცისთვის კი არ არის, არამედ უფალი ღვთისთვის.",
			"2": "<sup>2</sup> რაც შემეძლო, ყველაფერი მოვამზადე უფლის სახლისთვის: ოქრო – ოქროს ნივთებისთვის, ვერცხლი – ვერცხლის ნივთებისთვის, სპილენძი – სპილენძეულისთვის, რკინა რკინეულისათვის, ძელი ძელეულისათვის, აგრეთვე ონიქსის ქვები, ბუდეებში ჩასმული ქვები, ლამაზი და ფერად-ფერადი ქვები, ყველანაირი ძვირფასი ქვები და მარმარილოს ლოდები უამრავი.",
			"3": "<sup>3</sup> ამასთან ერთად, ჩემი უფლის სახლის სიყვარულის გამო მაქვს საკუთარი ოქროსა და ვერცხლის საუნჯე, რომელსაც ღვთის სახლს ვწირავ, ყოველივე იმის გარდა, რაც წმიდა სახლისთვის მაქვს მომზადებული:",
			"4": "<sup>4</sup> სამი ათასი ქანქარი ოქრო, ოფირის ოქრო, შვიდი ათასი ქანქარი გამოხურვებული ვერცხლი, სახლის კედლების მოსაპირკეთებლად,",
			"5": "<sup>5</sup> ოქროს ნივთებისთვის, ვერცხლის ნაკეთობებისთვის, ყველა საოქრომჭედლო საქმისთვის. კიდევ ვინ შესწირავს დღეს უფალს სავსე ხელით?“",
			"6": "<sup>6</sup> და გაიღეს შესაწირავი მამისსახლთა თავკაცებმა, ისრაელის შტოთა თავკაცებმა, ათასისთავებმა და მეფის მოურავებმა.",
			"7": "<sup>7</sup> და გამოიღეს უფლის სახლის სამსახურისთვის ხუთი ათასი ქანქარი ოქრო, ათი ათასი დრამა ოქრო, ათი ათასი ქანქარი ვერცხლი, თვრამეტი ათასი ქანქარი სპილენძი, ასი ათასი ქანქარი რკინა.",
			"8": "<sup>8</sup> ვისაც კი ქვები აღმოაჩნდა უფლის სახლის საგანძურისთვის იეხიელ გერშონელს ჩააბარეს.",
			"9": "<sup>9</sup> ხალხი ხარობდა შესაწირავის გამო, რადგან მთელი გულით სწირავდნენ უფალს, ასევე მეფე დავითიც დიდი სიხარულით ხარობდა.",
			"10": "<sup>10</sup> და აკურთხა დავითმა უფალი, მთელი კრებულის თვალწინ და თქვა დავითმა: „კურთხეულ ხარ, უფალო, ღმერთო ისრაელისა, და ჩვენს მამათა, უკუნითი უკუნისამდე!",
			"11": "<sup>11</sup> შენია, უფალო, დიდება, ძლიერება, ქება, ძლევა და დიდებულება, ისევე როგორც ყოველივე ცაზე და ქვეყანაზე! შენი, უფალო, მეფობა, ყველაზე აღმატებული ხარ მთავრად!",
			"12": "<sup>12</sup> სიმდიდრე და დიდება შენგან მოდის, შენ განაგებ ყოველივეს, შენს ხელშია ძალა და ძლიერება, შენს ხელშია ყოველივეს განდიდება და განმტკიცება.",
			"13": "<sup>13</sup> აწ გაქებთ, ღმერთო ჩვენო, და ვადიდებთ შენს დიდებულ სახელს.",
			"14": "<sup>14</sup> ვინა ვარ მე, ან ვინ არის ჩემი ერი, ამდენის შემოწირვა რომ შევიძელით? მაგრამ ყოველივე ეს შენგან არის, ეს ყოველივე შენი ხელით [მოცემული] შემოგწირეთ.",
			"15": "<sup>15</sup> ვინაიდან მდგმურნი და ხიზანნი ვართ შენს წინაშე, ჩვენს მამათა მსგავსად. აჩრდილივით არის ჩვენი დღენი ამ ქვეყნად, არაფერია საიმედო.",
			"16": "<sup>16</sup> უფალო, ღმერთო ჩვენო! მთელი ეს სიმრავლე, რაც ჩვენ მოვამზადეთ, შენს წმიდა სახელზე სახლის ასაგებად, შენი ხელით არის მოცემული და შენია ყოველი.",
			"17": "<sup>17</sup> ღმერთო ჩემო, ვიცი, რომ გულთამხილველი და სიმართლისმოყვარე ხარ. ეს ყველაფერი ჩემი წრფელი გულით შემოგწირე. ახლა კი გახარებულს ვხედავ აქ მყოფ შენს ერს, რომელიც შესაწირავს გიძღვნის.",
			"18": "<sup>18</sup> უფალო, ღმერთო აბრაამისა, ისაკისა და ისრაელისა, ჩვენს მამათა, სამარადისოდ დაიცავი ეს შენი ერის გულის ფიქრები და ზრახვანი და შენსკენ წარმართე მათი გულები.",
			"19": "<sup>19</sup> და სოლომონს, ჩემს ძეს, მიეცი მრთელი გული, რომ დაიცვას შენი მცნებები, შენი წესები და კანონები, რათა ყველაფერი გააკეთოს იმ შენობის ასაგებად, რომლისთვისაც მე დავიჭირე თადარიგი.“",
			"20": "<sup>20</sup> და უთხრა დავითმა მთელ კრებულს: „აკურთხეთ უფალი, ღმერთი თქვენი!“ და აკურთხა მთელმა კრებულმა უფალი, თავის მამათა ღმერთი. და დაეცნენ და თაყვანი სცეს უფალსა და მეფეს.",
			"21": "<sup>21</sup> მეორე დღეს შესწირეს უფალს მსხვერპლები, და სრულადდასაწველი აღუვლინეს უფალს: ათასი მოზვერი, ათასი ვერძი, ათასი კრავი, თავისი საღვრელით, და უამრავი მსხვერპლი მთელი ისრაელისათვის.",
			"22": "<sup>22</sup> იმ დღეს დიდი სიხარულით ჭამდნენ და სვამდნენ უფლის წინაშე. მეორე დღეს კი სოლომონი გაამეფეს დავითის ძე. უფლის წინაშე იგი წინამძღოლად სცხეს, ხოლო ცადოკი – მღვდლად.",
			"23": "<sup>23</sup> და დაჯდა სოლომონი საუფლო ტახტზე მეფედ დავითის, მამამისის, ნაცვლად. წარმატებული იყო და მთელი ისრაელი ემორჩილებოდა მას.",
			"24": "<sup>24</sup> ყველა მთავარი, მოყმე და მეფე დავითის ყველა ძე დაემორჩილა სოლომონ მეფეს.",
			"25": "<sup>25</sup> და დიდად აღამაღლა უფალმა სოლომონი მთელი ისრაელის თვალში და ისეთი მეფური დიდება უბოძა, ისრაელის არც ერთ წინამორბედ მეფეს რომ არ ჰქონია.",
			"26": "<sup>26</sup> და დავითი, იესეს ძე, მეფობდა მთელ ისრაელზე.",
			"27": "<sup>27</sup> და ორმოც წელიწადს იმეფა მან ისრაელზე, აქედან შვიდი წელიწადი ხებრონში იმეფა, ოცდაცამეტი – იერუსალიმში.",
			"28": "<sup>28</sup> და მოკვდა კეთილ სიბერეში, მაძღარი წლებით, სიმდიდრითა და დიდებით. და მის ნაცვლად სოლომონი, მისი ძე, გამეფდა.",
			"29": "<sup>29</sup> დავით მეფის საქმენი, ადრინდელნი და უკანასკნელნი, სამუელ მხედველის სიტყვებში, ნათან წინასწარმეტყველის სიტყვებში და გად მხილველის სიტყვებშია აღწერილი",
			"30": "<sup>30</sup> მთელს მის მეფობასთან, მის სიქველესთან და იმ ამბებთან ერთად, რომლებიც თავად მას, ისრაელს და სხვა ქვეყანათა სამეფოებს გადახდა."
		}
	}
};
module.exports = book;