var book = {
	"name": "ეზრ",
	"numChapters": 10,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> სპარსეთის მეფის, კიროსის მეფობის პირველ წელს, რათა აღსრულებულიყო იერემიას პირით თქმული უფლის სიტყვა, აღძრა უფალმა სული კიროსისა, სპარსეთის მეფისა, და მან გამოაცხადა მთელ თავის სამეფოში, და წერილობითაც განაცხადა და თქვა:",
			"2": "<sup>2</sup> „ასე ბრძანებს კიროსი, სპარსეთის მეფე: ‘დედამიწის მთელი სამეფონი მომცა მე უფალმა, ცათა ღმერთმა, და მიბრძანა ავუშენო მას სახლი იერუსალიმში, იუდაში რომ არის.",
			"3": "<sup>3</sup> ვინ არის თქვენს შორის მისი ხალხიდან, ღმერთი იყოს მასთან, და ავიდეს იერუსალიმს, იუდაში რომ არის, და ააშენოს უფლის, ისრაელის ღმერთის, სახლი. ეს არის ღმერთი, რომელიც იერუსალიმშია!",
			"4": "<sup>4</sup> და ყველა დანარჩენს: ყველგან, სადაც ის ცხოვრობს, ხელი გაუმართონ იმ ადგილის კაცებმა ვერცხლით, ოქროთი და საქონლით, ნებაყოფლობით ძღვენთან ერთად, უფლის სახლისთვის, რომელიც იერუსალიმშია!’“",
			"5": "<sup>5</sup> და ადგნენ იუდასა და ბენიამინის მამის სახლთა თავკაცები, მღვდლები და ლევიანები, ყველანი, ვისაც კი სული აღუძრა ღმერთმა, რათა ამოსულიყვნენ იერუსალიმში უფლის სახლის ასაშენებლად.",
			"6": "<sup>6</sup> და ყველამ, ვინც მათ გარშემო იყო, ხელი გაუმართა მათ ვერცხლის ჭურჭლით, ოქროთი და საქონლით და ძვირფასეულობით, გარდა ყველაფერ იმისა, რაც ნებაყოფლობით შესწირეს.",
			"7": "<sup>7</sup> ხოლო მეფე კიროსმა გამოიტანა უფლის სახლის ჭურჭელი, რომელიც ნაბუქოდონოსორმა წამოიღო იერუსალიმიდან და დააწყო თავისი ღმერთების სახლში, –",
			"8": "<sup>8</sup> და გამოიტანა იგი კიროსმა, სპარსეთის მეფემ, მითრიდატე მეგანძურის ხელით, და თვლით ჩააბარა შეშბაცარს, იუდას მთავარს.",
			"9": "<sup>9</sup> და აი მათი რაოდენობა: ოქროს ლანგარი – ოცდაათი, ვერცხლის ლანგარი – ათასი, ოცდაცხრა დანა,",
			"10": "<sup>10</sup> ოქროს ბარძიმი – ოცდაათი, ვერცხლის ორმაგი ბარძიმი – ოთხას ათი, და სხვა ჭურჭელი – ათასი.",
			"11": "<sup>11</sup> ოქროს და ვერცხლის ჭურჭელი სულ იყო ხუთი ათას ოთხასი. ეს ყოველივე ამოიტანა შეშბაცარმა, როცა გადასახლებულები ბაბილონონიდან იერუსალიმში დააბრუნეს."
		},
		"2": {
			"1": "<sup>1</sup> და აჰა, იმ ქვეყნის ძენი, რომელნიც გადასახლების ტყვეობიდან ამოვიდნენ, რომლებიც გადასახლებული იყვნენ ბაბილონში, ბაბილონის მეფის, ნაბუქოდონოსორის მიერ, და დაბრუნდნენ იერუსალიმსა და იუდაში, თითოეული თავის ქალაქში;",
			"2": "<sup>2</sup> რომელნიც მოვიდნენ ზერუბაბელთან, იესოსთან, ნეემიასთან, სერაიასთან, რეყლაიასთან, მორდექაისთან, ბილშანთან, მისფართან, ბიგვაისთან, რეხუმთან და ბაყანასთან ერთად.",
			"3": "<sup>3</sup> ფარყოშის ძენი – ორი ათას ასსამოცდათორმეტი,",
			"4": "<sup>4</sup> შეფატიას ძენი – სამას სამოცდათორმეტი,",
			"5": "<sup>5</sup> არახის ძენი – შვიდას სამოცდათხუთმეტი,",
			"6": "<sup>6</sup> ფახათ-მოაბის ძენი, იესოს ძეთაგან და იოაბის ძეთაგან – ორი ათას რვაას თორმეტი,",
			"7": "<sup>7</sup> ყელამის ძენი – ათას ორას ორმოცდათოთხმეტი,",
			"8": "<sup>8</sup> ზათუს ძენი – ცხრაას ორმოცდახუთი,",
			"9": "<sup>9</sup> ზაქაის ძენი – შვიდას სამოცი,",
			"10": "<sup>10</sup> ბანის ძენი – ექვსას ორმოცდაორი,",
			"11": "<sup>11</sup> ბებაის ძენი – ექვსას ოცდასამი,",
			"12": "<sup>12</sup> ყაზგადის ძენი – ათას ორას ოცდაორი,",
			"13": "<sup>13</sup> ადონიკამის ძენი – ექვსას სამოცდაექვსი",
			"14": "<sup>14</sup> ბიგვაის ძენი – ორი ათას ორმოცდათექვსმეტი,",
			"15": "<sup>15</sup> ყადინის ძენი – ოთხას ორმოცდათოთხმეტი,",
			"16": "<sup>16</sup> ატერის ძენი იხისკიად წოდებულისა – ოთხმოცდათვრამეტი,",
			"17": "<sup>17</sup> ბეცაის ძენი – სამას ოცდასამი,",
			"18": "<sup>18</sup> იორას ძენი – ას თორმეტი,",
			"19": "<sup>19</sup> ხაშუმის ძენი – ორას ოცდასამი,",
			"20": "<sup>20</sup> გიბარის ძენი – ოთხმოცდათხუთმეტი,",
			"21": "<sup>21</sup> ბეთლემის კაცები – ას ოცდასამი,",
			"22": "<sup>22</sup> ნეტოფას კაცები – ორმოცდათექვსმეტი,",
			"23": "<sup>23</sup> ყანათოთის კაცები – ასოცდარვა,",
			"24": "<sup>24</sup> ყაზმავეთის ძენი – ორმოცდაორი,",
			"25": "<sup>25</sup> კირიათ-ყარიმის, ქეფირას და ბეროთის ძენი – შვიდას ორმოცდასამი,",
			"26": "<sup>26</sup> რამას და გაბაყის ძენი – ექვსას ოცდაერთი,",
			"27": "<sup>27</sup> მიქმასის კაცები – ას ოცდაორი,",
			"28": "<sup>28</sup> ბეთელისა და ჰაღაის კაცები – ორას ოცდასამი,",
			"29": "<sup>29</sup> ნებოს ძენი – ორმოცდათორმეტი,",
			"30": "<sup>30</sup> მაგბიშის ძენი – ას ორმოცდათექვსმეტი,",
			"31": "<sup>31</sup> ყელამის სხვა ძენი – ათას ორას ორმოცდათოთხმეტი,",
			"32": "<sup>32</sup> ხარიმის ძენი – სამას ოცი,",
			"33": "<sup>33</sup> ლოდის, ხადიდის და ონოს ძენი – შვიდას ოცდახუთი,",
			"34": "<sup>34</sup> იერიხოს კაცები – სამას ორმოცდახუთი,",
			"35": "<sup>35</sup> სენაას ძენი – სამი ათას ექვსას ოცდაათი,",
			"36": "<sup>36</sup> მღვდლები: იედაყიას ძენი იესოს სახლიდან – ცხრაას სამოცდაცამეტი,",
			"37": "<sup>37</sup> იმერის ძენი – ათას ორმოცდათორმეტი,",
			"38": "<sup>38</sup> ფაშხურის ძენი – ათას ორას ორმოცდაშვიდი,",
			"39": "<sup>39</sup> ხარიმის ძენი – ათას ჩვიდმეტი,",
			"40": "<sup>40</sup> ლევიანები: იესოს და კადმიელის ძენი ჰოდავიას ძეთაგან – სამოცდათოთხმეტი;",
			"41": "<sup>41</sup> მგალობელნი: ასაფის ძენი – ას ოცდარვა;",
			"42": "<sup>42</sup> კარისმცველთა ძენი: შალუმის, ატერის, ტალმონის, ყაკუბის, ხატიტას და შობაის სულ – ას ოცდაცხრამეტი;",
			"43": "<sup>43</sup> ტაძრის მსახურთა ძენი: ციხას, ხაშუფას, ტაბაყოთის,",
			"44": "<sup>44</sup> კეროსის, სიაყას, ფადონის,",
			"45": "<sup>45</sup> ლებანას, ხაგაბას, ყაკუბის,",
			"46": "<sup>46</sup> ხაგაბის, შამლაის, ხანანის,",
			"47": "<sup>47</sup> გიდელის, გახარის, რეაიას,",
			"48": "<sup>48</sup> რეცინის, ნეკოდას, გაზამის,",
			"49": "<sup>49</sup> ყუზას, ფასეახის, ბესაის,",
			"50": "<sup>50</sup> ასნას, მეყუნიმის, ნეფუსიმის,",
			"51": "<sup>51</sup> ბაკბუკის, ხაკუფას, ხარხურის,",
			"52": "<sup>52</sup> ბაცლუთის, მეხიდას, ხარშას,",
			"53": "<sup>53</sup> ბარკოსის, სისერას, თამახის,",
			"54": "<sup>54</sup> ნეციახის და ხატიფას ძენი;",
			"55": "<sup>55</sup> სოლომონის მსახურთა ძენი: სოტაის, სოფერეთის, ფერუდას,",
			"56": "<sup>56</sup> იაყლას, დარკონის, გიდელის,",
			"57": "<sup>57</sup> შეფატიას, ხატილის, ფოქერეთ-ცებაიმის და ამის ძენი;",
			"58": "<sup>58</sup> სულ ტაძრის მსახურნი და სოლომონის მსახურთა ძენი – სამას ოთხმოცდათორმეტი.",
			"59": "<sup>59</sup> და ესენი გამოვიდნენ თელ-მელახიდან, თელ-ხარშადან, ქერუბიდან, ადანიდან, იმერიდან, მაგრამ ვერ შეძლეს გაერკვიათ თავიანთი მამისსახლები და მოდგმა, ისრაელიდან თუ იყვნენ.",
			"60": "<sup>60</sup> დელაიას, ტობიას და ნეკოდას ძენი – სულ ექვსას ორმოცდათორმეტი.",
			"61": "<sup>61</sup> და მღვდლების ძეთაგან: ძენი ხაბაიას, ჰაკოცისა, ბარზილაისა, რომელმაც გილყადელი ბარზილაის ასულთაგანი შეირთო ცოლად და მათივე სახელი ეწოდა.",
			"62": "<sup>62</sup> ესენი ეძებდნენ თავიანთ საგვარტომო ნუსხას და ვერ იპოვეს, და განეყენენ მღვდლობიდან, როგორც შებღალულნი.",
			"63": "<sup>63</sup> და უთხრა მათ განმგებელმა, რომ არ ეჭამათ წმიდათა წმიდადან, ვიდრე არ დადგებოდა მღვდელი ურიმითა და თუმიმით.",
			"64": "<sup>64</sup> მთელი კრებული ერთად შეადგენდა ორმოცდაორი ათას სამას სამოც კაცს,",
			"65": "<sup>65</sup> გარდა მათი მსახურებისა და მათი მხევლებისა, ესენი იყვნენ შვიდი ათას სამას ოცდაჩვიდმეტი; და მათთან – ორასი მგალობელი კაცი და მგალობელი ქალი.",
			"66": "<sup>66</sup> მათი ცხენები – შვიდას ოცდათექვსმეტი, ჯორები მათი – ორას ორმოცდახუთი,",
			"67": "<sup>67</sup> მათი აქლემები – ოთხას ოცდათხუთმეტი, სახედრები – ექვსი ათას შვიდას ოცი.",
			"68": "<sup>68</sup> და მამისსახლთა თავკაცებმა უფლის სახლთან მისვლისას, იერუსალიმში, ნებაყოფლობით შესწირეს ღვთის სახლს, რათა აღდგენილიყო იგი თავის საძირკველზე.",
			"69": "<sup>69</sup> თავიანთი შეძლებისამებრ საგანძურში შეიტანეს საქმისათვის: სამოცდაერთი ათასი დრაქმა ოქრო, ხუთი ათასი მანა ვერცხლი და ასი ხელი სამღვდელო შესამოსელი.",
			"70": "<sup>70</sup> და დასახლდნენ მღვდლები, ლევიანები, ხალხთაგანნი, მგალობლები, კარისმცველები, ტაძრის მსახურნი და მთელი ისრაელი, თავ-თავიანთ ქალაქებში."
		},
		"3": {
			"1": "<sup>1</sup> როცა დადგა მეშვიდე თვე და ისრაელის ძენი უკვე თავ-თავიანთ ქალაქებში ესახლნენ, შეიკრიბა ერი იერუსალიმში, როგორც ერთი კაცი.",
			"2": "<sup>2</sup> და ადგნენ იესო იეჰოცადაკის ძე, მისი ძმები, მღვდლები და ზერუბაბელ შეალთიელის ძე და მისი ძმები და აუგეს სამსხვერპლო ისრაელის ღმერთს, რათა მასზე აღევლინათ სრულადდასაწველნი, როგორც ეს სწერია მოსეს, ღვთისკაცის, რჯულში.",
			"3": "<sup>3</sup> და დადგეს სამსხვერპლო თავის საძირკველზე, რადგან შიში ჰქონდათ მათ სხვა ქვეყანათა ხალხისა; და აღავლენდნენ მასზედ სრულადდასაწველებს უფლისათვის, სრულადდასაწველებს დილით და საღამოთი.",
			"4": "<sup>4</sup> და გამართეს კარვობის დღესასწაული დაწერილისამებრ და სწირავდნენ დღიურ სრულადდასაწველებს განსაზღვრულ რიცხვში, წესისამებრ, ყოველდღიური საქმისამებრ.",
			"5": "<sup>5</sup> და ამის შემდეგ სწირავდნენ მუდმივ სრულადდასაწველს ახალმთვარეობებისა და უფლის ყოველი კურთხეული დღესასწაულისათვის, და ყველასათვის, ვისაც ნებაყოფლობითი ძღვენი მიჰქონდა უფლისათვის.",
			"6": "<sup>6</sup> მეშვიდე თვის პირველივე დღიდან დაიწყეს მათ უფლისათვის სრულადდასაწველთა აღვლენა. ხოლო უფლის ტაძრის საძირკველი ჯერ არ იყო ჩაყრილი.",
			"7": "<sup>7</sup> და მისცეს ვერცხლი ქვისმთლელებს და ხუროებს, და საჭმელ-სასმელი, და ზეთი – ციდონელებს და ცორელებს, რათა მოეზიდათ კედრის მორები ზღვით ლიბანიდან იაფოში, იმ ნებართვის მიხედვით, რომელიც მისცა მათ კიროსმა, სპარსეთის მეფემ.",
			"8": "<sup>8</sup> და ღვთის სახლთან – იერუსალიმში მათი მისვლის მეორე წლის მეორე თვეს შეუდგნენ საქმეს ზერუბაბელ შეალთიელის ძე, იესო იეჰოცადაკის ძე და დანარჩენი მათი ძმები, მღვდლები – ლევიანები, ვინც ტყვეობიდან იერუსალიმში დაბრუნდა, და დააყენეს ლევიანები, ოცი წლიდან და ზევით, უფლის სახლის სამუშაოზე ზედამხედველებად.",
			"9": "<sup>9</sup> და დადგნენ იესო და მისი ძენი და ძმანი, და კადმიელი და მისი ძენი, ჰოდავიას ძენი, და აგრეთვე ძენი ხენადადისა, მათი ძენი და მათი ძმანი, ლევიანები, როგორც ერთი კაცი, ღვთის სახლში მომუშავეთა ზედამხედველებად.",
			"10": "<sup>10</sup> და როცა მშენებლებმა უფლის ტაძარს საძირკველი ჩაუყარეს, დააყენეს შემოსილი მღვდლები საყვირებით, და ლევიანები, ასაფის ძენი, წინწილებით, რათა ედიდებინათ უფალი დავითის, ისრაელის მეფის, წესის მიხედვით.",
			"11": "<sup>11</sup> და შეჰღაღადეს ქებითა და მადლობით უფალს: „რადგან კეთილია, რადგან საუკუნოა წყალობა მისი ისრაელზე!“ და მთელი ხალხი უფლის ტაძრის დაფუძნების გამო მაღალი ხმით ყიჟინებდა უფლის ქება-დიდებას.",
			"12": "<sup>12</sup> და მრავალნი მღვდელთაგან და ლევიანთაგან, მამისსახლთა თავკაცთაგან, მოხუცებულთაგან, რომელთაც ნანახი ჰქონდათ პირველი ტაძარი, ხმამაღლა ტიროდნენ, როცა მათ თვალწინ საძირკველი ეყრებოდა ტაძარს, ბევრმა კი სიხარულის ყიჟინა დასცა.",
			"13": "<sup>13</sup> და ხალხი ვეღარ არჩევდა სიხარულის ყიჟინას და ტირილის ხმას, რადგან ხალხი დიდი ხმით ყიჟინებდა და ხმა შორს ისმოდა."
		},
		"4": {
			"1": "<sup>1</sup> და გაიგეს იუდასა და ბენიამინის მტრებმა, რომ ტყვედყიფილნი ტაძარს უშენებდნენ უფალს, ისრაელის ღმერთს,",
			"2": "<sup>2</sup> და მივიდნენ ზერუბაბელთან და მამის სახლთა თავკაცებთან, და უთხრეს მათ: „ჩვენც თქვენთან ერთად ავაშენებთ, რადგან ჩვენც თქვენსავით ვეძებთ თქვენს ღმერთს და ჩვენც ვწირავთ მას მსხვერპლს აშურის მეფის, ასარხადონის დღეთაგან, რომელმაც აქ ამოგვიყვანა ჩვენ.“",
			"3": "<sup>3</sup> და უთხრეს მათ ზერუბაბელმა და იესომ და ისრაელის დანარჩენ მამისსახლთა თავკაცებმა: „არ იქნება, რომ თქვენ და ჩვენ ერთად ავაშენოთ ჩვენი ღმერთის სახლი; რადგან მარტო ჩვენ უნდა ავუშენოთ სახლი უფალს, ისრაელის ღმერთს, როგორც ნაბრძანები აქვს ჩვენთვის მეფე კიროსს, სპარსთა მეფეს.“",
			"4": "<sup>4</sup> და იმ ქვეყნის ხალხი ხელს უშლიდა იუდას ხალხს და აფრთხობდნენ მათ შენებისაგან.",
			"5": "<sup>5</sup> და ქირაობდნენ მათ წინააღმდეგ მრჩევლებს, რათა ჩაეფუშათ მათი განზრახვა სპარსეთის მეფის კიროსის მეფობის მთელ დღეებში, სპარსეთის მეფის, დარიოსის გამეფებამდე.",
			"6": "<sup>6</sup> და ახაშვეროშის მეფობაში, მისი მეფობის დასაწყისში, დაწერეს ცილისწამება იუდასა და იერუსალიმის მცხოვრებლებზე.",
			"7": "<sup>7</sup> და ართახშასთას დღეებში მისწერეს ართახშასთას, სპარსეთის მეფეს, ბიშლამმა, მითრიდატემ, ტაბეელმა და სხვა მათმა თანამზრახველებმა. წერილი დაწერილი იყო არამეული ასოებით არამეულ ენაზე.",
			"8": "<sup>8</sup> რეხუმ მმართველმა და შიმშაი მწერალმა იერუსალიმის წინააღმდეგ მეფე ართახშასთას ასეთი წერილი მისწერეს:",
			"9": "<sup>9</sup> „და დაწერეს რეხუმ მმართველმა და შიმშაი მწერალმა, და სხვა მათმა თანამოაზრეებმა, მსაჯულებმა, განმგებლებმა, სპარსელმა მოხელეებმა, ერეხელებმა, ბაბილონელებმა, შუშანელებმა, ანუ ყელამელებმა",
			"10": "<sup>10</sup> და დანარჩენმა ხალხმა, რომელნიც აჰყარა დიდმა და სახელოვანმა ოსნაფარმა და დაასახლა ისინი ქალაქ სამარიაში და სხვა ადგილებში მდინარის გაღმა“ და სხვა.",
			"11": "<sup>11</sup> აჰა, პირი ამ წერილისა, რომელიც გაუგზავნეს მას: „ართახშასთა მეფეს, შენი მსახურნი, მდინარის გაღმელნი“ და სხვა.",
			"12": "<sup>12</sup> იცოდეს მეფემ, რომ იუდაელნი, რომელნიც ამოვიდნენ შენგან, მოვიდნენ ჩვენთან, იერუსალიმს, და აშენებენ მეამბოხე და ბოროტ ქალაქს; და კედლებს ასრულებენ და ბურჯებს ამაგრებენ.",
			"13": "<sup>13</sup> ახლა იცოდეს მეფემ, თუ ეს ქალაქი აშენდა და კედლები გასრულდა, ისინი გადასახადს, ხარკსა და ბეგარას აღარ გადაიხდიან და სამეფო ხაზინა დაზარალდება.",
			"14": "<sup>14</sup> და ახლა, რაკი ჩვენ სასახლის მარილსა ვჭამთ, მეფის ლანძღვის ყურება არ შეგვფერის, ამიტომ ვაგზავნით წერილს და ვატყობინებთ მეფეს:",
			"15": "<sup>15</sup> მოჩხრიკონ შენი მამების მატიანეში და შენ ნახავ და გაიგებ, რომ ეს ქალაქი მეამბოხე ქალაქია, საზიანოა მეფეთათვის და ქვეყნისათვის; რომ ძველთაგანვე ამბოხება ხდებოდა იქ, ამისათვის იყო ეს ქალაქი დანგრეული.",
			"16": "<sup>16</sup> ვატყობინებთ ჩვენს მეფეს, რომ თუ ეს ქალაქი აშენდა და კედლები გასრულდა, მაშინ მდინარის გაღმა წილი აღარ დაგრჩება!“",
			"17": "<sup>17</sup> პასუხი გამოუგზავნა მეფემ რეხუმ მმართველსა და შიმშაი მწერალს და მათ სხვა თანამოაზრეთ, რომლებიც ცხოვრობდნენ სამარიაში და დანარჩენი მდინარის გაღმა მხარეში: „მშვიდობა!“ და ასე და ასე.",
			"18": "<sup>18</sup> „წერილი, რომელიც გამომიგზავნეთ, წაკითხულ და თარგმნილ იქნა ჩემს წინაშე.",
			"19": "<sup>19</sup> და ჩემს მიერ გაიცა ბრძანება, და გამოჩხრიკეს და აღმოაჩინეს რომ ეს ქალაქი ძველთაგანვე უჯანყდებოდა მეფეებს და ამბოხება და არეულობა ხდებოდა იქ.",
			"20": "<sup>20</sup> და ძლიერი მეფენი განაგებდნენ იერუსალიმს, და ეპყრათ მათ მდინარის მთელი გაღმა მხარე და გადასახადი, ხარკი და ბეგარა გაიღებოდა მათგან.",
			"21": "<sup>21</sup> და ახლა გაეცით ბრძანება, რომ შეწყვიტონ მუშაობა ამ კაცებმა, რომ არ აშენდეს ეს ქალაქი, ვიდრე ჩემგან არ გამოვა ბრძანება.",
			"22": "<sup>22</sup> და ფრთხილად იყავით, არაფერი გამოგეპაროთ ამ საქმეში. რად უნდა გამრავლდეს ზარალი მეფის საზიანოდ?“",
			"23": "<sup>23</sup> როგორც კი მეფე ართახშასთას წერილის პირი წაკითხულ იქნა რეხუმის და შიმშაი მწერლისა და მათ თანამოაზრეთა წინაშე, დაუყოვნებლივ წავიდნენ ისინი იერუსალიმში იუდაელებთან და იძულებით შეაწყვეტინეს მუშაობა.",
			"24": "<sup>24</sup> მაშინ შეწყდა ღვთის სახლის სამუშაო იერუსალიმში და შეწყვეტილი იყო დარიოსის, სპარსეთის მეფის, მეფობის მეორე წლამდე."
		},
		"5": {
			"1": "<sup>1</sup> და წინასწარმეტყველებდნენ ისრაელის ღმერთის სახელით წინასწარმეტყველნი: ანგია და ზაქარია, ყიდოს ძე, იუდაელებზე, რომელნიც იუდასა და იერუსალიმში ცხოვრობდნენ.",
			"2": "<sup>2</sup> მაშინ ადგნენ ზერუბაბელ შეალთიელის ძე და იესო იეჰოცადაკის ძე, და შეუდგნენ ღვთის სახლის შენებას იერუსალიმში. და მათთან იყვნენ ღვთის წინასწარმეტყველნი, რომელნიც მხარს უჭერდნენ მათ.",
			"3": "<sup>3</sup> იმ ხანად მოვიდნენ მათთან თათნაი, მდინარისგაღმელი განმგებელი, და შეთარ-ბოზნაი, და მათი თანამოაზრენი და ასე უთხრეს მათ: „ვინ მოგცათ თქვენ ბრძანება ამ სახლის აშენებისა და ამ კედლების გასრულებისა?“",
			"4": "<sup>4</sup> მაშინ ვუთხარით მათ იმათი სახელები, ვინც ამ შენობას აგებდნენ.",
			"5": "<sup>5</sup> და მათი ღვთის თვალი იყო იუდაელთა უხუცესებზე, და არ შეუწყვეტიათ სამუშაო, ვიდრე დარიოსს არ შეატყობინეს ამბავი და საპასუხო წერილი არ მიიღეს ამ საქმეზე.",
			"6": "<sup>6</sup> აჰა, პირი იმ წერილისა, რომელიც მეფე დარიოსს გაუგზავნეს თათნაიმ, მდინარისგაღმელმა განმგებელმა და შეთარ-ბოზნაიმ და მისმა თანამოაზრეებმა, აფარსახელებმა, რომელნიც მდინარის გაღმა იყვნენ.",
			"7": "<sup>7</sup> დაბეზღება გაუგზავნეს მას, რომელშიც ასე ეწერა: „მეფე დარიოსს სრული მშვიდობა!",
			"8": "<sup>8</sup> იცოდეს მეფემ, რომ ჩვენ ვიყავით იუდას მხარეში, დიდი ღმერთის სახლთან. იგი შენდება დიდრონი ქვებისაგან, კედლებში ძელებს ატანენ, მუშაობა სწრაფად მიდის და წარმატებით მათ ხელში.",
			"9": "<sup>9</sup> მაშინ ვკითხეთ მათ უხუცესებს და ვუთხარით ასე: ‘ვინ მოგცათ თქვენ ბრძანება ამ ტაძრის შენებისა და ამ კედლების აგებისა?’",
			"10": "<sup>10</sup> და გამოვიკითხეთ მათი სახელები, რომ გვეცნობებინა შენთვის და დაგვეწერა იმათი სახელებიც, რომელნიც სათავეში უდგანან მათ.",
			"11": "<sup>11</sup> მათ ასეთი პასუხი გაგვცეს: ‘ჩვენ ვართ მსახურნი ცისა და მიწის ღმერთისა; თავიდან ვაშენებთ ტაძარს, რომელიც მრავალი წლის წინ აშენდა, და ისრაელის დიდი მეფე აშენებდა მას და დაასრულა იგი.",
			"12": "<sup>12</sup> მაგრამ, რაც ჩვენმა მამებმა განარისხეს ცათა ღმერთი, მან ბაბილონის მეფეს ნაბუქოდონოსორს, ქალდეველს, ჩაუგდო ისინი ხელში, რომელმაც ეს ტაძარი დაანგრია და ხალხი ბაბილონში გადაასახლა.",
			"13": "<sup>13</sup> მაგრამ კიროსის ბაბილონის მეფის, მეფობის პირველ წელს კიროს მეფემ გასცა ბრძანება ღვთის სახლის ასაშენებლად.",
			"14": "<sup>14</sup> და ღვთის სახლის ოქროსა და ვერცხლის ჭურჭელი, რაც ნაბუქოდონოსორმა გაიტანა იერუსალიმის ტაძრიდან და წაიღო ბაბილონის ტაძარში, უკან გამოიტანა კიროს მეფემ და გადასცა იგი შეშბაცარს, რომელიც განმგებლად ჰყავდა დანიშნული.",
			"15": "<sup>15</sup> და უთხრა მას: აიღე ეს ჭურჭელი, წადი და წაიღე იერუსალიმის ტაძარში, და აშენდეს ღვთის სახლი თავის ადგილზეო.",
			"16": "<sup>16</sup> მაშინ მოვიდა ის შეშბაცარი და საძირკველი ჩაუყარა ღვთის სახლს იერუსალიმში; და იმ დროიდან მოკიდებული აქამდე შენდება იგი, მაგრამ ჯერ არ გასრულებულა.’",
			"17": "<sup>17</sup> და ახლა, თუ მეფე ინებებს, მოჩხრიკონ მეფის საგანძური იქ, ბაბილონში, ნამდვილად გასცა თუ არა კიროს მეფემ განკარგულება ამ ღვთის სახლის ასაშენებლად იერუსალიმში. მეფემ გამოგვიგზავნოს გადაწყვეტილება ამ საქმის თაობაზე. ამის შესახებ წარმოიგზავნოს ჩვენთან.“"
		},
		"6": {
			"1": "<sup>1</sup> მაშინ დარიოს მეფემ გასცა ბრძანება და მოჩხრიკეს წიგნთსაცავები ბაბილონში, სადაც საგანძური ინახებოდა.",
			"2": "<sup>2</sup> და იპოვეს ახმეთაში, მიდიის მხარის ციხე-ქალაქში, ერთი გრაგნილი, რომელზედაც ასე ეწერა: ძეგლისწერა –",
			"3": "<sup>3</sup> მეფე კიროსის მეფობის პირველ წელს მეფე კიროსმა გასცა განკარგულება: ღვთის სახლის თაობაზე იერუსალიმში: აშენდეს ტაძარი იმ ადგილზე, სადაც შეიწირება მსხვერპლი; და მტკიცე საძირკველი ჩაეყაროს. სიმაღლე მისი სამოცი წყრთა იყოს, სიგანე მისი – სამოცი წყრთა;",
			"4": "<sup>4</sup> სამი წყება დიდრონი ქვებისაგან იყოს, და ერთი წყება – ძელებისაგან; და ხარჯებს მეფის სასახლე გაასწორებს.",
			"5": "<sup>5</sup> ღვთის სახლის ჭურჭელიც, ოქროსი და ვერცხლისა, რომლებიც ნაბუქოდონოსორმა გაიტანა იერუსალიმის ტაძრიდან და ბაბილონში წაიღო, უკან დაბრუნდეს თავის ადგილზე, იერუსალიმის ტაძარში, და ღვთის სახლში დაბინავდეს.",
			"6": "<sup>6</sup> და ახლა, თათნაი – მდინარისგაღმელო განმგებელო, შეთარ-ბოზნაი და მათო თანამოაზრენო, მდინარისგაღმელო აფარსახელნო, დაეხსენით მათ!",
			"7": "<sup>7</sup> დაანებეთ ღვთის სახლზე მუშაობა! იუდას განმგებელმა და იუდაელთა უხუცესებმა ააშენონ ეს ღვთის სახლი თავის ადგილზე.",
			"8": "<sup>8</sup> და ვიძლევი განკარგულებას იმის თაობაზე, თუ რა უნდა გაუკეთოთ იუდას უხუცესებს, ღვთის სახლის ასაგებად: სამეფო ხაზინიდან მდინარისგაღმის მხარის შემოსავლიდან დაუყოვნებლივ მიეცეს იმ ხალხს სახარჯოდ.",
			"9": "<sup>9</sup> და რაც საჭიროა, ხბორები, ვერძები თუ კრავები ცათა ღმერთისათვის სრულადდასაწველის აღსავლენად; ხორბლეული, მარილი, ღვინო და ზეთი, როგორც იერუსალიმის მღვდლები იტყვიან, მიეცეს მათ ყოველდღიურად, შეუფერხებლად,",
			"10": "<sup>10</sup> რათა შესწირონ სურნელოვანი მსხვერპლები ცათა ღმერთს და ილოცონ მეფის და მისი ძეების სიცოცხლისათვის.",
			"11": "<sup>11</sup> და ვიძლევი განკარგულებას, რომ თუ ვინმე შეცვლის ამ ბრძანებას, გამოიღონ ძელი მისი სახლიდან, ზედ ჩამოკიდონ იგი; მისი სახლი კი სანაგვედ აქციონ ამის გამო.",
			"12": "<sup>12</sup> და ღმერთმა, რომლის სახელიც იქ არის დავანებული, დაამხოს ყოველი მეფე და ხალხი, რომელიც ხელს აღმართავს ამ ბრძანების შესაცვლელად, ან ამ ღვთის სახლის დასაზიანებლად, რომელიც იერუსალიმშია. მე, დარიოსი, ვიძლევი ბრძანებას: ზედმიწევნით შესრულდეს!",
			"13": "<sup>13</sup> მაშინ თათნაიმ, მდინარისგაღმელმა განმგებელმა, შეთარ-ბოზნაიმ და მათმა თანამოაზრეებმა, დარიოს მეფის მიერ გამოგზავნილი სიტყვისამებრ, ყველაფერი ზედმიწევნით შეასრულეს.",
			"14": "<sup>14</sup> და იუდას უხუცესნი აშენებდნენ და ხელი ემართებოდათ თანახმად ანგია წინასწარმეტყველისა და ზაქარია ყიდოს ძის წინასწარმეტყველებისა. ააშენეს და გაასრულეს ისრაელის ღმერთის ბრძანებისამებრ და კიროსის, დარიოსის და ართახშასთას – სპარსეთის მეფეების, განკარგულებისამებრ.",
			"15": "<sup>15</sup> და დასრულდა ეს სახლი ადარის თვის მესამე დღეს; ეს იყო მეფე დარიოსის მეფობის მეექვსე წელი.",
			"16": "<sup>16</sup> და ისრაელის ძეთ, მღვდლებმა, ლევიანებმა და დანარჩენმა გადანასახლებმა ხალხმა სიხარულით აღასრულეს ღვთის სახლის სატფურება.",
			"17": "<sup>17</sup> და შესწირეს ღვთის ამ სახლის სატფურებისათვის ასი მოზვერი, ორასი ვერძი, ოთხასი კრავი; და თორმეტი ვაცი – მთელი ისრაელის ცოდვათა განსატევებლად, ისრაელის ტომთა რიცხვის მიხედვით.",
			"18": "<sup>18</sup> და დააყენეს მღვდლები თავიანთი ხარისხების მიხედვით და ლევიანები თავიანთი რიგის მიხედვით ღვთის სამსახურისათვის იერუსალიმში, როგორც სწერია მოსეს წიგნში.",
			"19": "<sup>19</sup> და აღასრულა გადანასახლებმა ხალხმა პასექი პირველი თვის მეთოთხმეტე დღეს.",
			"20": "<sup>20</sup> რადგან განიწმიდნენ მღვდლები და ლევიანები, როგორც ერთი კაცი, ყველანი წმიდები გახდნენ, და დაკლეს საპასექო მსხვერპლი ყველა გადანასახლები ხალხისათვის, თავიანთი მოძმე მღვდლებისათვის და თავიანთთვის.",
			"21": "<sup>21</sup> და ჭამეს პასექი გადასახლებიდან დაბრუნებულმა ისრაელის ძეთ და ყველამ, ვინც განუდგა ქვეყნის ხალხის ბიწიერებას, რათა შეერთებოდნენ მათ, უფლის, ისრაელის ღმერთის, საძებნელად.",
			"22": "<sup>22</sup> და იზეიმეს უფუარობის დღესასწაული შვიდი დღე, რადგან გაახარა ისინი უფალმა და გული მოაბრუნებინა მათკენ აშურის მეფეს, რათა ხელი გაემაგრებინა მათთვის ღვთის სახლის, ისრაელის ღმერთის სახლის, საქმეში."
		},
		"7": {
			"1": "<sup>1</sup> და ამ ამბების შემდეგ სპარსეთის მეფის, ართახშასთას მეფობაში ეზრა, ძე სერაიასი, ძისა ყაზარიასი, ძისა ხილკიასი,",
			"2": "<sup>2</sup> ძისა შალუმისა, ძისა ცადოკისა, ძისა ახიტუბისა,",
			"3": "<sup>3</sup> ძისა ამარიასი, ძისა ყაზარიასი, ძისა, მერაიოთისა,",
			"4": "<sup>4</sup> ძისა ზერახიასი, ძისა ყუზისა, ძისა ბუკისა,",
			"5": "<sup>5</sup> ძისა აბიშუაყისა, ძისა ფინხასისა, ძისა ელეაზარისა, ძისა აარონისა, მღვდელმთავრისა, –",
			"6": "<sup>6</sup> ეს ეზრა გამოვიდა ბაბილონიდან. და ის იყო მწიგნობარი, მოსეს რჯულში ჩახედული, რომელიც მისცა უფალმა ისრაელის ღმერთმა. და მისცა მას მეფემ ყოველივე, რაც ითხოვა, რადგან უფლის, მისი ღმერთის, ხელი იყო მასზე.",
			"7": "<sup>7</sup> და ამოვიდნენ იერუსალიმში ისრაელის ძეთაგან, მღვდელთაგან, ლევიანთაგან, მგალობელთაგან, კარისმცველთაგან და ტაძრის მსახურთაგან ართახშასთა მეფის მეშვიდე წელს.",
			"8": "<sup>8</sup> და მივიდა ის იერუსალიმში მეფის მეშვიდე წლის მეხუთე თვეს.",
			"9": "<sup>9</sup> რადგან მან პირველი თვის პირველ დღეს დაიწყო გამოსვლა ბაბილონიდან და მეხუთე თვის პირველ დღეს მივიდა იერუსალიმში, რადგან მისი ღმერთის კეთილი ხელი იყო მასზე.",
			"10": "<sup>10</sup> რადგან ეზრამ გულს იდვა უფლის რჯულის ძიება, მისი შესრულება და წესისა და სამართლის სწავლება ისრაელში.",
			"11": "<sup>11</sup> და აჰა, პირი წერილისა, რომელიც მისცა მეფე ართახშასთამ ეზრას, მღვდელს, მწიგნობარს, განსწავლულს უფლის მცნებების სიტყვებში და მის წესებში ისრაელზე.",
			"12": "<sup>12</sup> ართახშასთა, მეფეთა მეფე, ეზრა მღვდელს, ცათა ღმერთის რჯულის მწიგნობარს, სრული მშვიდობა! და სხვა.",
			"13": "<sup>13</sup> განკარგულება გავეცი, რომ თუ ვინმეს ჩემს სამეფოში სურს ისრაელის ერიდან, მათი მღვდლებიდან და ლევიანებიდან იერუსალიმში წასვლა, შენთან ერთად წავიდეს.",
			"14": "<sup>14</sup> რადგან შენ იგზავნები მეფისაგან და მისი შვიდი მრჩევლისგან, რათა მოიხილო იუდა და იერუსალიმი შენი ღმერთის რჯულის მიხედვით, რომელიც შენს ხელთაა;",
			"15": "<sup>15</sup> რომ წაიღო ვერცხლი და ოქრო, რომელიც მეფემ და მისმა მრჩევლებმა შესწირეს ისრაელის ღმერთს, რომლის სავანეც იერუსალიმშია,",
			"16": "<sup>16</sup> და მთელი ვერცხლი და ოქრო, რასაც შენ შეაგროვებ მთელი ბაბილონის მხარეში, იმ ერისკაცთა და მღვდელთა ნებაყოფლობით შემოწირულობასთან ერთად, რაც ნებაყოფლობით შესწირეს თავიანთი ღვთის სახლს იერუსალიმში.",
			"17": "<sup>17</sup> ამიტომ დაუყოვნებლივ იყიდე ამ ვერცხლით ხარები, ცხვრები, კრავები, მათი საძღვნო შესაწირავები და საღვრელები და შესწირე იგი თქვენი ღვთის ტაძრის სამსხვერპლოზე, იერუსალიმში.",
			"18": "<sup>18</sup> და როგორც გაგეხარდებათ შენ და შენს მოძმეებს, ისე მოიხმარეთ დანარჩენი ვერცხლი და ოქრო, თქვენი ღმერთის ნებით.",
			"19": "<sup>19</sup> და ჭურჭელი, რომელიც მოგეცა შენი ღვთის სახლის სამსახურისათვის, უკლებლივ მიიტანე იერუსალიმის ღმერთის წინაშე.",
			"20": "<sup>20</sup> და დანარჩენი, რაც საჭიროა შენი ღვთის სახლისათვის, რისი გაცემაც მოგესურვება, გაეცი სამეფო საგანძურიდან.",
			"21": "<sup>21</sup> და მე, ახაშვეროშ მეფე, ვიძლევი განკარგულებას მდინარისგაღმის ყველა მეგანძურის მიმართ, რომ რასაც მოგთხოვთ ეზრა მღვდელი, ზეცის ღვთის მწიგნობარი, დაუყოვნებლად შესრულდეს.",
			"22": "<sup>22</sup> ვერცხლი ას ტალანტამდე, ხორბალი ას ქორამდე, ღვინო ას ბათამდე, ზეთი ას ბათამდე და მარილი უზომოდ.",
			"23": "<sup>23</sup> ყოველი, რაც ნაბრძანებია ცათა ღმერთისაგან, აღსრულდეს ზუსტად ცათა ღვთის სახლისათვის, რათა არ დაატყდეს რისხვა სამეფოს, მეფეს და უფლისწულებს.",
			"24": "<sup>24</sup> და გაცნობებთ, რომ არცერთ მღვდელს, ლევიანს, მგალობელს, კარისმცველს, ტაძრის მსახურსა და ამ ღვთის სახლის მსახურს არ უნდა დაეკისროს ხარკი, გადასახადი და ბეგარა.",
			"25": "<sup>25</sup> შენ, ეზრა, შენი ღვთის სიბრძნისამებრ, რომელიც შენს ხელთაა, დანიშნე მსაჯულნი და მოსამართლენი, რათა განსაჯონ მათ მთელი მდინარისგაღმელი ხალხი და ყველა, ვინც იცის შენი ღმერთის კანონები. ხოლო, ვინც არ იცის, ასწავლეთ!",
			"26": "<sup>26</sup> და ვინც არ აღასრულებს შენი ღვთის კანონს და მეფის კანონს დაუყოვნებლივ აღასრულონ მასზე სამართალი: სიკვდილი ან განკვეთა, ჯარიმა ან ტუსაღობა.",
			"27": "<sup>27</sup> კურთხეული იყოს უფალი, ჩვენი მამების ღმერთი, რომელმაც ეს მეფეს ჩაუდო გულში, რათა შეემკო უფლის სახლი იერუსალიმში.",
			"28": "<sup>28</sup> და მან მიბოძა მოწყალება მეფის, მისი მრჩევლების, მეფის მამაცი მთავრების წინაშე; მეც გავმაგრდი, შევკრიბე ისრაელის ძეთა მთავრები, რათა ჩემთან ერთად წამოსულიყვნენ."
		},
		"8": {
			"1": "<sup>1</sup> და აჰა, მათი მამისსახლის თავკაცთა საგვარტომო ნუსხა, რომელნიც წამოვიდნენ ჩემთან ერთად ბაბილონიდან ართახშასთას მეფობაში:",
			"2": "<sup>2</sup> ფინხასის ძეთაგან: გერშომი; ითამარის ძეთაგან: დანიელი; დავითის ძეთაგან: ხატუში;",
			"3": "<sup>3</sup> შექანიას ძეთაგან და ფარყოშის ძეთაგან: ზაქარია და მასთან მითვლილი ას ორმოცდაათი მამროვანი;",
			"4": "<sup>4</sup> ფახათ-მოაბის ძეთაგან: ელიეჰოყენაი, ზერახიას ძე, და მასთან ერთად ორასი მამროვანი;",
			"5": "<sup>5</sup> [ზათუს] ძეთაგან: შექანია, იახაზიელის ძე, და მასთან ერთად სამასი მამროვანი;",
			"6": "<sup>6</sup> ყადინის ძეთაგან: ყებედი, იონათანის ძე, და მასთან ერთად ორმოცდაათი მამროვანი;",
			"7": "<sup>7</sup> ყელამის ძეთაგან: ესაია, ყათალიას ძე, და მასთან ერთად სამოცდაათი მამროვანი;",
			"8": "<sup>8</sup> შეფატიას ძეთაგან: ზებადია, მიქაელის ძე, და მასთან ერთად ოთხმოცი მამროვანი;",
			"9": "<sup>9</sup> იოაბის ძეთაგან: ყობადია, იეხიელის ძე, და მასთან ერთად ორას თვრამეტი მამროვანი;",
			"10": "<sup>10</sup> ბანის ძეთაგან: შელომითი, იოსიფიას ძე, და მასთან ერთად ას სამოცი მამროვანი;",
			"11": "<sup>11</sup> ბებაის ძეთაგან: ზაქარია, ბებაის ძე, და მასთან ერთად ოცდარვა მამროვანი;",
			"12": "<sup>12</sup> ყაზგადის ძეთაგან: იოხანანი, ჰაკატანის ძე, და მასთან ერთად ას ათი მამროვანი;",
			"13": "<sup>13</sup> ადონიკამის ძეთაგან უკანასკნელნი, და აჰა, მათი სახელები: ელიფელეტი, იეყიელი და შემაყია, და მათთან ერთად სამოცი მამროვანი;",
			"14": "<sup>14</sup> ბიგვაის ძეთაგან: ყუთაი და ზაბუდი, და მათთან ერთად სამოცდაათი მამროვანი.",
			"15": "<sup>15</sup> და შევყარე ისინი მდინარესთან, რომელიც ერთვის აჰავას, და ვიდექით იქ სამი დღე. გამოვიკვლიე ერი და სამღვდელოება, მაგრამ მათ შორის ლევიანთაგან იქ ვერ ვპოვე.",
			"16": "<sup>16</sup> და დავიბარე ელიეზერი, არიელი, შემაყია, ელნათანი, იარიბი, ელნათანი, ნათანი, ზაქარია, მეშულამი – თავკაცები, და იოიარიბი და ელნათანი – სწავლულები.",
			"17": "<sup>17</sup> გავგზავნე ისინი იდოსთან, ქასიფიას ადგილის უფროსთან, და შევუთვალე მათი პირით იდოს და მის ძმებს, ტაძრის მსახურებს, ქასიფიას ადგილში მყოფთ, რათა მოეყვანათ ჩვენთვის მსახურნი ჩვენი ღმერთის სახლისათვის.",
			"18": "<sup>18</sup> რადგან კეთილი ხელი ჩვენი ღმერთისა ჩვენზე იყო, მათ მოიყვანეს ჩვენთან ერთი გონიერი კაცი მახლის ძეთაგან, ლევის ძისა, ისრაელის ძისა, სახელად შერებია და მისი ვაჟები და თვრამეტი კაცი;",
			"19": "<sup>19</sup> და ხაშაბია, და მასთან ესაია მერარის ძეთაგან, მისი ძმები და მისი ვაჟები – ოცი კაცი.",
			"20": "<sup>20</sup> და ტაძრის მსახურთაგან, რომელნიც დავითსა და მთავრებს მიცემული ჰყავდათ ლევიანთა სამსახურებლად – ორას ოცი ტაძრის მსახური; ყველა მათგანი სახელებით იყო ხმობილი.",
			"21": "<sup>21</sup> და გამოვაცხადე იქ მარხვა, მდინარე აჰავაზე, რათა თავი დაგვემდაბლებინა ჩვენი ღმერთის წინაშე, და გვეკითხა მისგან სწორი გზა ჩვენთვის და ჩვენი წვრილშვილისა და მთელი ჩვენი მონაგარისათვის,",
			"22": "<sup>22</sup> რადგან შემრცხვა მეთხოვა მეფისთვის ლაშქარი და მხედრები, მტრისაგან გზაზე თავის დასაცავად, რადგან ნათქვამი გვქონდა მეფისათვის: ‘ჩვენი ღმერთის ხელი სასიკეთოა ყველასთვის, ვინც ეძებს მას, ხოლო მისი ძალა და რისხვა თავს ატყდება ყველას, ვინც მიატოვებს მას!’",
			"23": "<sup>23</sup> და ვიმარხულეთ, და ვთხოვეთ ჩვენს ღმერთს ეს და შეისმინა მან ჩვენი.",
			"24": "<sup>24</sup> და გამოვყავი თორმეტი კაცი მღვდლების უფროსთაგან: შერებია, ხაშაბია და მათთან ერთად ათი კაცი მათი ძმებიდან.",
			"25": "<sup>25</sup> და ავუწონე მათ ვერცხლი, ოქრო, და ჭურჭელი, ჩვენი ღვთის სახლის შეწირულობა, რაც შესწირეს მეფემ, მისმა მრჩევლებმა, მთავრებმა და იქ მყოფმა მთელმა ისრაელმა.",
			"26": "<sup>26</sup> და მივუწონე მათ ხელში ექვსას ორმოცდაათი ტალანტი ვერცხლი, და ვერცხლის ჭურჭელი ასი ტალანტისა, და ასი ტალანტი ოქრო.",
			"27": "<sup>27</sup> და ოცი თასი ათას დრაქმიანი და პრიალა სპილენძის ორი საუკეთესო ჭურჭელი, ოქროსავით ძვირფასი.",
			"28": "<sup>28</sup> და ვუთხარი მათ: „თქვენ წმიდა ხართ უფლისათვის და ეს ჭურჭელიც წმიდაა; და ვერცხლი და ოქრო ნებაყოფლობით შეწირულობაა უფლის, თქვენი მამების ღმერთისადმი.",
			"29": "<sup>29</sup> ფრთხილად იყავით და მოუფრთხილდით, ვიდრე მიუწონიდეთ მღვდლების, ლევიანების და ისრაელიანთა ტომის მთავრების უფროსებს იერუსალიმში, უფლის სახლის სალოცავებში რომ არიან.“",
			"30": "<sup>30</sup> და ჩაიბარეს მღვდლებმა და ლევიანებმა ვერცხლი და ოქრო, და ჭურჭელი წონით, რათა წაეღოთ იერუსალიმში, ჩვენი ღვთის სახლში.",
			"31": "<sup>31</sup> და დავიძარით მდინარე აჰავადან იერუსალიმში წასასვლელად პირველი თვის მეთორმეტე დღეს; და ჩვენი ღმერთის ხელი იყო ჩვენზე და გვიხსნიდა ჩვენი მტრის ხელიდან და გზაზე ჩასაფრებულთაგან.",
			"32": "<sup>32</sup> და მივედით იერუსალიმში, და დავყავით იქ სამი დღე.",
			"33": "<sup>33</sup> და მეოთხე დღეს ვერცხლი და ოქრო, და ჭურჭელი ჩვენი ღვთის სახლში მიწონილ იქნა მერემოთის, ურია მღვდლის ვაჟის ხელში; და მასთან ერთად იყო ელეაზარ ფინხასის ძე, და მათთან ერთად იყვნენ იოზაბადი იესოს ძე და ნოადია ბინუის ძე, ლევიანები.",
			"34": "<sup>34</sup> ყველაფერი დაითვალა და აიწონა; და ჩაიწერა მთელი ეს წონა იმ დროს.",
			"35": "<sup>35</sup> ტყვეობიდან მოსულებმა და გადასახლებიდან დაბრუნებულებმა სრულადდასაწველი შესწირეს ისრაელის ღმერთს: თორმეტი მოზვერი მთელი ისრაელისათვის, ოთხმოცდათექვსმეტი ვერძი, სამოცდაჩვიდმეტი კრავი, თორმეტი ვაცი ცოდვისათვის, ყოველივე სრულადდასაწველად უფლისათვის.",
			"36": "<sup>36</sup> და გადასცეს მეფისნაცვლებს და მდინარისგაღმელ განმგებელთ; და მათაც ხელი შეუწყვეს ხალხსა და ღვთის სახლს."
		},
		"9": {
			"1": "<sup>1</sup> და ეს ყოველივე რომ დასრულდა, მეახლნენ მე მთავრები და მითხრეს: „ისრაელის ხალხი, მღვდლები და ლევიანები არ განდგომიან ქვეყნების ხალხებს – ქანაანელთა, ხეთელთა, ფერიზელთა, იებუსელთა, ყამონელთა, მოაბელთა, ეგვიპტელთა და ამორეველთა სიბილწეებს,",
			"2": "<sup>2</sup> რადგან მოიყვანეს მათი ასულები თავიანთთვის და თავიანთი ვაჟებისათვის, და შეერია წმიდა თესლი ამ ქვეყნების ხალხებს; და მთავარი ხელი ამ ურჯულოებაში მთავრებისა და წინამძღოლებისა იყო.“",
			"3": "<sup>3</sup> და ეს ამბავი რომ გავიგე, შემოვიხიე სამოსელი და მოსასხამი, და ვიგლეჯდი თმა-წვერს და ვიჯექი გაოგნებული.",
			"4": "<sup>4</sup> და თავი მოიყარეს ჩემთან ყველამ, ვისაც ეშინოდა ისრაელის ღმერთის სიტყვებისა გადანასახლებთა ურჯულოების გამო; მე კი ვიჯექი გაოგნებული საღამოს მსხვერპლშეწირვამდე.",
			"5": "<sup>5</sup> და საღამოს მსხვერპლშეწირვისას ავდექი ჩემი მარხულობისაგან, და ჩემი შემოხეული სამოსლითა და ჩემი მოსასხამით დავემხე მუხლებზე და ხელი აღვაპყრე უფლის, ჩემი ღვთის, მიმართ.",
			"6": "<sup>6</sup> და ვთქვი: „ღმერთო ჩემო! მრცხვენია და მერიდება სახის მოპყრობა შენს მიმართ, რადგან ჩვენი ურჯულოებანი თავზე გადაგვდის და ჩვენი დანაშაული გაიზარდა ცამდე.",
			"7": "<sup>7</sup> ჩვენი მამების დროიდან დღემდე დიდ დანაშაულში ვართ; და ჩვენი ურჯულოების გამო ჩავვარდით ჩვენ, ჩვენი მეფენი და ჩვენი მღვდლები უცხო ქვეყნების მეფეთა ხელში, მახვილის ქვეშ, ტყვეობაში, ძარცვაში და სირცხვილში, როგორც დღესა ვართ.",
			"8": "<sup>8</sup> და ახლა წამიერად იქნა მოცემული ჩვენთვის წყალობა უფლისაგან, ჩვენი ღმერთისაგან, რათა დაეტოვებინა ჩვენთვის გადარჩენილი ნატამალი და მოეცა სამსჭვალი თავის წმიდა ადგილზე, რომ გაენათებინა ჩვენი თვალები ჩვენს ღმერთს და ცოტათი მაინც გავეცოცხლებინეთ ჩვენს მონობაში.",
			"9": "<sup>9</sup> რადგან მონები ვართ ჩვენ და ჩვენს მონობაშიც არ მივუტოვებივართ ჩვენს ღმერთს და განავრცო ჩვენზე წყალობა სპარსეთის მეფეთა წინაშე, რომ გავეცოცხლებინეთ, რომ აღგვემართა ჩვენი ღვთის სახლი; აღგვედგინა ნანგრევებიდან, რომ ზღუდე მოგვცემოდა იუდასა და იერუსალიმში.",
			"10": "<sup>10</sup> და ახლა კი რა უნდა ვთქვათ, ღმერთო ჩვენო, ამის შემდეგ? რადგან მივატოვეთ შენი მცნებანი,",
			"11": "<sup>11</sup> შენი მსახურების, შენი წინასწარმეტყველების ხელით რომ გვაუწყე და გვითხარი: ‘ქვეყანა, რომლის დასამკვიდრებლადაც მიდიხართ, უწმიდური ქვეყანაა იმ ქვეყნების ხალხების უწმიდურებისა და სიბილწის გამო, რომელთაც კიდით კიდემდე აქვთ ავსებული თავიანთი სიბინძურით.",
			"12": "<sup>12</sup> და ახლა თქვენი ასულები არ გაატანოთ მათ ვაჟებს და მათი ასულები არ მოუყვანოთ თქვენს ვაჟებს. არ ეძებოთ მათთვის მშვიდობა და სიკეთე უკუნისამდე, რათა გაძლიერდეთ და ჭამოთ ქვეყნის დოვლათი და სამარადისოდ დაუმკვიდროთ იგი თქვენს ძეებს!’",
			"13": "<sup>13</sup> და ყოველივე ამის შემდეგ, რაც თავს დაგვატყდა ჩვენი ავი საქმეებისა და ჩვენი დიდი დანაშაულის გამო, რადგან შენ, ღმერთო ჩვენო, იმაზე ნაკლებად დაგვსაჯე, ვიდრე ამას ჩვენი უკეთურებანი იმსახურებდნენ და მოგვამადლე გადარჩენილი ნატამალი, როგორც ესაა.",
			"14": "<sup>14</sup> ნუთუ კვლავ დავარღვევთ შენს მცნებებს და დავუმოყვრდებით ამ ბილწ ხალხებს? მაშინ ხომ განგვირისხდები მოსასპობად ისე, რომ აღარც ნატამალი იქნება და აღარც ვინმე გადარჩენილი.",
			"15": "<sup>15</sup> უფალო, ღმერთო ისრაელისა! სამართლიანი ხარ, რადგან გადარჩენილი ნატამალი ვართ დღესდღეობით. აჰა, ჩვენ შენს წინაშე ვართ ჩვენი დანაშაულითურთ, თუმცა არავის ძალუძს შენს წინაშე დგომა ამის გამო.“"
		},
		"10": {
			"1": "<sup>1</sup> და როცა ლოცულობდა ეზრა და აღსარებას ამბობდა მტირალი და ღვთის სახლის წინ განრთხმული, თავი მოიყარა მის გარშემო ისრაელიდან დიდძალმა კრებულმა – კაცებმა, ქალებმა და ბალღებმა – რადგან მწარედ ტიროდა ხალხიც.",
			"2": "<sup>2</sup> და მიუგო შეხანიამ, იეხიელის ძემ, ყელამის ძეთაგან, და უთხრა ეზრას: „ვუღალატეთ ჩვენს ღმერთს და მოვიყვანეთ უცხოელი ცოლები ამ ქვეყნის ხალხთაგან; და ახლა არის იმედი ისრაელისათვის მიუხედავად ამისა.",
			"3": "<sup>3</sup> და ახლა აღვუთქვათ ჩვენს ღმერთს, რომ ყველანი გავუშვებთ ცოლებს და მათ მონაგარს ჩემი ბატონისა და ჩვენი ღვთის მცნებათა მოშიში ხალხის რჩევით; რჯულისამებრ იყოს.",
			"4": "<sup>4</sup> ადექი, რადგან შენზეა ეს საქმე და ჩვენც შენთან ვართ. გამხნევდი და იმოქმედე!“",
			"5": "<sup>5</sup> და ადგა ეზრა და დააფიცა მღვდლების, ლევიანებისა და მთელი ისრაელის უფროსები, რომ ასე მოიქცეოდნენ. და მათაც დაიფიცეს.",
			"6": "<sup>6</sup> და ადგა ეზრა ღვთის სახლიდან და წავიდა იოხანანის, ელიაშიბის ძის, ოთახისაკენ. იქ კი მივიდა, მაგრამ პური არ უჭამია და წყალი არ დაულევია, რადგან გადანასახლები ხალხის ურჯულოების გამო გლოვობდა.",
			"7": "<sup>7</sup> და გამოუცხადეს იუდასა და იერუსალიმში მყოფ ყველა გადანასახლებს, რათა იერუსალიმში მოეყარათ თავი.",
			"8": "<sup>8</sup> და ყველას, ვინც სამ დღეში არ მოვიდოდა, მთავართა და უხუცესთა გადაწყვეტილებით ჩამოერთმეოდა მთელი ქონება და მოკვეთილი იქნებოდა გადანასახლებთა კრებულიდან.",
			"9": "<sup>9</sup> და სამ დღეში თავი მოიყარეს იერუსალიმში ბენიამინისა და იუდას ყველა კაცმა; და ეს იყო მეცხრე თვეს, თვის მეოცე დღეს. იჯდა მთელი ხალხი ღვთის სახლის მოედანზე და კანკალებდა ამ ამბისა და თავსხმა წვიმის გამო.",
			"10": "<sup>10</sup> და ადგა ეზრა მღვდელი და უთხრა მათ: „თქვენ ღალატი ჩაიდინეთ და უცხო ცოლები მოიყვანეთ, რომ გაამრავლოთ ისრაელის დანაშაული.",
			"11": "<sup>11</sup> და ახლა აღსარება უთხარით უფალს, თქვენი მამების ღმერთს და აღასრულეთ მისი ნება: განუდექით ამ ქვეყნის ხალხებს და უცხოელ ცოლებს!“",
			"12": "<sup>12</sup> და მიუგო მთელმა კრებულმა და უთხრა დიდი ხმით: „ასე, შენი სიტყვისამებრ მოვიქცევით!",
			"13": "<sup>13</sup> მაგრამ ბევრია ხალხი და წვიმიანობის დროა. არ შეგვიძლია გარეთ დგომა. თანაც ეს ერთი ან ორი დღის ამბავი არ არის, რადგან ჩვენ ბევრი შეგვიცოდავს ამ საქმეში.",
			"14": "<sup>14</sup> დადგნენ ჩვენი მთავრები მთელი კრებულის ნაცვლად, ხოლო ყველანი, ვისაც ჩვენ ქალაქში უცხოელი ცოლები ჰყავთ, მოვიდნენ დანიშნულ დროს თავიანთი ქალაქების უხუცესებთან და მსაჯულებთან ერთად, ვიდრე აგვაცილებენ ჩვენი ღვთის რისხვას ამ ამბის გამო.“",
			"15": "<sup>15</sup> მხოლოდ იონათანი, ძე ყასაელისა და იახზეია, თიკვას ძე, გამოვიდნენ ამის წინააღმდეგ; მეშულამი და შაბეთაი, ლევიანი, უჭერდნენ მათ მხარს.",
			"16": "<sup>16</sup> მაგრამ გადანასახლები ხალხი ასე მოიქცა. და ეზრა მღვდელმა გამოარჩია კაცები, თავ-თავიანთი მამისსახლების მიხედვით, ყველანი სახელდებით იყვნენ ხმობილნი. და დასხდნენ ისინი მეათე თვის პირველ დღეს ამ საქმის გამოსაძიებლად.",
			"17": "<sup>17</sup> და მორჩნენ პირველი თვის პირველი დღისათვის ყველა იმ მამაკაცის საქმეს, რომელთაც უცხოელი ცოლები ჰყავდათ.",
			"18": "<sup>18</sup> და მღვდლების შთამომავლებში, რომელთაც უცხოელი ცოლები ჰყავდათ მოყვანილი აღმოჩნდნენ: იესო იოცადაკის ძის ვაჟები და მისი ძმები: მაყასეია, ელიეზერი, იარიბი და გედალია.",
			"19": "<sup>19</sup> და მათ ხელი მისცეს, რომ გაუშვებდნენ თავიანთ ცოლებს, და დამნაშავეებმა შესწირეს ვერძი ფარიდან თავიანთი დანაშაულის გამო.",
			"20": "<sup>20</sup> და იმერის ძეთაგან: ხანანი და ზებადია;",
			"21": "<sup>21</sup> და ხარიმის ძეთაგან: მაყასეია, ელია, შემაყია, იეხიელი, ყუზია;",
			"22": "<sup>22</sup> და ფაშხურის ძეთაგან: ელიოყენაი, მაყასეია, ისმაელი, ნეთანელი, იოზაბადი და ელყასა;",
			"23": "<sup>23</sup> და ლევიანთაგან: იოზაბადი, შიმყი, კელაია, იგივე კელიტა, ფეთახია, იუდა და ელიეზერი.",
			"24": "<sup>24</sup> და მგალობელთაგან: ელიაშიბი. და კარის მცველთაგან: შალუმი, ტელემი და ური.",
			"25": "<sup>25</sup> და ისრაელიდან: ფარყოშის ძეთაგან: რამია, იეზია, მალქია, მიამინი, ელეაზარი, მალქია, ბენაია;",
			"26": "<sup>26</sup> ყელამის ძეთაგან: მათანია, ზაქარია, იეხიელი, ყაბდი, იერემოთი, ელია;",
			"27": "<sup>27</sup> ზათუს ძეთაგან: ელიოყენაი, ელიაშიბი, მათანია, იერემოთი, ზაბადი, ყაზიზა;",
			"28": "<sup>28</sup> ბებაის ძეთაგან: იეჰოხანანი, ხანანია, ზაბაი, ყათლაი;",
			"29": "<sup>29</sup> ბანის ძეთაგან: მეშულამი, მალუქი, ყადაია, იაშუბი, შეალი, იერამოთი;",
			"30": "<sup>30</sup> და ფახათ-მოაბის ძეთაგან: ყადნა, ქელალი, ბენაია, მაყასეია, მათანია, ბეცალელი, ბინუი და მენაშე;",
			"31": "<sup>31</sup> ხარიმის ძეთაგან: ელიეზერი, იშია, მალქია, შემაყია, შიმყონი;",
			"32": "<sup>32</sup> ბენიამინი, მალუქი, შემარია;",
			"33": "<sup>33</sup> ხაშუმის ძეთაგან: მათენაი, მათათა, ზაბადი, ელიფელეტი, იერემაი, მენაშე, შიმყი;",
			"34": "<sup>34</sup> ბანის ძეთაგან: მაყადაი, ყამრამი, უელი,",
			"35": "<sup>35</sup> ბენაია, ბედეია, ქელუჰუ,",
			"36": "<sup>36</sup> ვანია, მერემოთი, ელიაშიბი,",
			"37": "<sup>37</sup> მათანია, მათენაი და იაყასაი;",
			"38": "<sup>38</sup> ბანი, ბინუი შიმყი,",
			"39": "<sup>39</sup> შელემია, ნათანი, ყადაია,",
			"40": "<sup>40</sup> მაქნადბაი, შაშაი, შარაი,",
			"41": "<sup>41</sup> ყაზარელი, შელემიაჰუ, შემარია,",
			"42": "<sup>42</sup> შალუმი, ამარია, იოსები,",
			"43": "<sup>43</sup> ნებოს ძეთაგან: იეყიელი, მათითია, ზაბადი, ზებინა, იადაი, იოელი და ბენაია.",
			"44": "<sup>44</sup> ყველას უცხოელი ცოლები ჰყავდათ მოყვანილი; ზოგიერთებს მათგან ვაჟებიც შესძენოდათ."
		}
	}
};
module.exports = book;