var book = {
	"name": "ლუკ",
	"numChapters": 24,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> რაკი უკვე ბევრმა მიჰყო ხელი ჩვენს შორის აღსრულებულ ამბავთა თანმიმდევრულ აღწერას,",
			"2": "<sup>2</sup> როგორც გადმოგვცეს თავიდანვე თვითმხილველებმა და სიტყვის მსახურებმა,",
			"3": "<sup>3</sup> მეც მივიჩნიე, რომ მას შემდეგ, რაც გულდასმით გამოვიკვლიე ყოველივე თავიდან, თანმიმდევრულად აგიწერო, ღირსეულო თეოფილე,",
			"4": "<sup>4</sup> რომ შეიცნო იმ მოძღვრების ჭეშმარიტება, რომელიც გისწავლია.",
			"5": "<sup>5</sup> იუდეის მეფის, ჰეროდეს, დღეებში იყო მღვდელი ვინმე, სახელად ზაქარია, აბიას რიგიდან, ხოლო მისი ცოლი იყო აარონის ასულთაგანი, სახელად ელისაბედი.",
			"6": "<sup>6</sup> ისინი ორივენი მართლები იყვნენ ღვთის წინაშე, უმწიკვლოდ იქცეოდნენ უფლის ყოველი მცნებითა და წესდებით.",
			"7": "<sup>7</sup> მათ არ ჰყავდათ შვილი, ვინაიდან ელისაბედი ბერწი იყო, და ორივენი უკვე ხანგადასულნი იყვნენ.",
			"8": "<sup>8</sup> ერთხელ, როცა თავისი ყოველდღიური რიგისამებრ, მსახურებდა ღვთის წინაშე,",
			"9": "<sup>9</sup> მღვდელთა სამსახურის წესით, მას წილად ხვდა კმევა და შევიდა უფლის ტაძარში.",
			"10": "<sup>10</sup> უამრავი ხალხი ლოცულობდა გარეთ კმევის დროს, –",
			"11": "<sup>11</sup> ეჩვენა მას უფლის ანგელოზი, საკმევლის სამსხვერპლოს მარჯვნივ მდგარი.",
			"12": "<sup>12</sup> და შეკრთა მისი ხილვით ზაქარია და შიში დაეცა მას.",
			"13": "<sup>13</sup> უთხრა მას ანგელოზმა: „ნუ გეშინია, ზაქარია, ვინაიდან შესმენილია შენი ლოცვა, და შენი ცოლი ელისაბედი გიშობს ძეს და დაარქმევ მას სახელად იოანეს.",
			"14": "<sup>14</sup> გექნება შენ სიხარული და მხიარულება და მრავალი გაიხარებს მისი შობით.",
			"15": "<sup>15</sup> ვინაიდან დიდი იქნება ის უფლის წინაშე, ღვინოსა და თაფლუჭს არ დალევს და სულიწმიდით აღივსება დედის მუცლიდანვე.",
			"16": "<sup>16</sup> მრავალ ისრაელიანს მოაქცევს უფლისაკენ – მათი ღვთისაკენ.",
			"17": "<sup>17</sup> და ის ივლის მის წინაშე ელიას სულით და ძალით, რომ მოაქციოს მამათა გულები შვილთა მიმართ, ხოლო ურჩნი მართალთა გონებაზე და წარუდგინოს უფალს მომზადებული ხალხი.“",
			"18": "<sup>18</sup> უთხრა ზაქარიამ ანგელოზს: „რითი გავიგებ ამას? მე ხომ მოხუცებული ვარ და ჩემი ცოლიც უკვე ხანგადასულია.“",
			"19": "<sup>19</sup> მიუგო ანგელოზმა და უთხრა მას: „მე ვარ გაბრიელი, ღვთის წინაშე მდგომელი. წარმოგზავნილი ვარ, რათა გელაპარაკო და გახარო ეს.",
			"20": "<sup>20</sup> და აჰა, დადუმდები და ვერ შეძლებ ლაპარაკს იმ დღემდე, ვიდრე ეს ახდება, რაკი არ ერწმუნე ჩემს სიტყვებს, რომლებიც აღსრულდება თავის დროზე.“",
			"21": "<sup>21</sup> ხალხი კი იდგა და ელოდა ზაქარიას და უკვირდათ მისი დაყოვნება ტაძარში.",
			"22": "<sup>22</sup> როცა გამოვიდა, მათთვის ხმის გაცემა ვერ შეძლო. და მიხვდნენ, რომ ჩვენება იხილა ტაძარში. ანიშნებდა მათ და დარჩა უტყვად.",
			"23": "<sup>23</sup> და როცა დასრულდა მისი მსახურების დღენი, შინ წავიდა.",
			"24": "<sup>24</sup> იმ დღეების შემდეგ დაორსულდა ელისაბედი, მისი ცოლი. ხუთ თვეს იმალებოდა და ამბობდა:",
			"25": "<sup>25</sup> „ასე მიყო უფალმა ამ დღეებში, როცა მომხედა, რომ მოეშორებინა ჩემთვის სირცხვილი ადამიანთა თვალში.“",
			"26": "<sup>26</sup> მეექვსე თვეს კი წარმოიგზავნა ღვთისაგან გაბრიელ ანგელოზი გალილეის ქალაქს, რომლის სახელია ნაზარეთი,",
			"27": "<sup>27</sup> ქალწულთან, რომელიც დანიშნული იყო კაცზე, სახელად იოსებზე, დავითის სახლიდან, და ქალწულის სახელი იყო მარიამი.",
			"28": "<sup>28</sup> და შევიდა მასთან და უთხრა: „გიხაროდეს, მიმადლებულო, შენთანაა უფალი. კურთხეული ხარ შენ დედათა შორის.“",
			"29": "<sup>29</sup> ის შეშფოთდა ამ სიტყვებზე და ჩაფიქრდა: რას უნდა ნიშნავდესო ეს მისალმება?",
			"30": "<sup>30</sup> და უთხრა მას ანგელოზმა: „ნუ გეშინია, მარიამ, ვინაიდან მადლი ჰპოვე ღმერთთან.",
			"31": "<sup>31</sup> აჰა, დაორსულდები და შობ ძეს და სახელად იესოს დაარქმევ.",
			"32": "<sup>32</sup> დიდი იქნება ის და უზენაესის ძედ იწოდება. და მისცემს მას უფალი ღმერთი მისი მამის, დავითის, ტახტს.",
			"33": "<sup>33</sup> და საუკუნოდ იმეფებს იაკობის სახლზე და მის მეფობას არ ექნება დასასრული.“",
			"34": "<sup>34</sup> უთხრა მარიამმა ანგელოზს: „როგორ იქნება ეს, როცა მე მამაკაცი არ ვიცი?“",
			"35": "<sup>35</sup> მიუგო ანგელოზმა და უთხრა: „სულიწმიდა გადმოვა შენზე და მაღლის ძალა მოგიჩრდილავს. ამიტომ წმიდა შობილი იწოდება ღვთის ძედ.",
			"36": "<sup>36</sup> აჰა, შენს ნათესავ ელისაბედსაც სიბერეში ჩაესახა ძე და მეექვსე თვეშია უკვე.",
			"37": "<sup>37</sup> ვინაიდან ღმერთთან არავითარი სიტყვა არ დარჩება შეუძლებელი.“",
			"38": "<sup>38</sup> თქვა მარიამმა: „აჰა, უფლის მხევალი ვარ. მეყოს მე შენი სიტყვისამებრ.“ და განეშორა მას ანგელოზი. „ადიდებს სული ჩემი უფალსა!“",
			"39": "<sup>39</sup> ადგა მარიამი იმ დღეებში და საჩქაროდ წავიდა მთიან მხარეში, იუდეის ქალაქში.",
			"40": "<sup>40</sup> და შევიდა ზაქარიას სახლში და მიესალმა ელისაბედს.",
			"41": "<sup>41</sup> როცა ელისაბედმა მარიამის მისალმება მოისმინა, ყრმა შეუტოკდა მუცელში. და აღივსო ელისაბედი სულიწმიდით.",
			"42": "<sup>42</sup> და დაიძახა ხმამაღლა და თქვა: „კურთხეული ხარ დედათა შორის, და კურთხეულია შენი მუცლის ნაყოფი!",
			"43": "<sup>43</sup> ვინა ვარ მე, რომ ჩემი უფლის დედა მოვიდა ჩემთან?",
			"44": "<sup>44</sup> ვინაიდან, როცა შენი მოსალმების ხმა მოსწვდა ჩემს ყურს, სიხარულით შეტოკდა ყრმა ჩემს მუცელში.",
			"45": "<sup>45</sup> ნეტარია, ვინც ირწმუნა, ვინაიდან აღსრულდება უფლის მიერ თქმული მის მიმართ.“",
			"46": "<sup>46</sup> და თქვა მარიამმა: „ადიდებს სული ჩემი უფალსა,",
			"47": "<sup>47</sup> და იზეიმა სულმა ჩემმა ღმერთში, ჩემს მაცხოვარში.",
			"48": "<sup>48</sup> ვინაიდან მოხედა თავისი მხევლის სიმდაბლეს და ამიერიდან ნეტარად ჩამთვლის მე ყოველი თაობა.",
			"49": "<sup>49</sup> ვინაიდან დიდი საქმე მიყო ძლიერმა და წმიდაა სახელი მისი.",
			"50": "<sup>50</sup> და მისი წყალობა მის მოშიშებს თაობიდან თაობამდე.",
			"51": "<sup>51</sup> ქმნა სიმტკიცე თავისი მკლავით; გაფანტა ამპარტავანნი მათი გულისზრახვებთან ერთად;",
			"52": "<sup>52</sup> ტახტებიდან გადმოყარა ძლიერნი და აამაღლა თავმდაბალნი;",
			"53": "<sup>53</sup> მშიერნი სიკეთით აღავსო და მდიდარნი ხელცარიელი გაუშვა;",
			"54": "<sup>54</sup> შეეწია თავის მსახურს, ისრაელს, გაიხსენა წყალობა,",
			"55": "<sup>55</sup> როგორც ეუბნებოდა ჩვენს მამებს – აბრაამს და მის მოდგმას საუკუნოდ.“",
			"56": "<sup>56</sup> დარჩა მარიამი მასთან სამიოდე თვეს და დაბრუნდა თავის სახლში.",
			"57": "<sup>57</sup> ელისაბედს კი დაუდგა მშობიარობის ჟამი და შვა ვაჟი.",
			"58": "<sup>58</sup> და ესმათ მის მეზობლებსა და ნათესავებს, რომ განადიდა უფალმა თავისი წყალობა მის მიმართ და მასთან ერთად ხარობდნენ.",
			"59": "<sup>59</sup> მერვე დღეს მივიდნენ ბავშვის წინადასაცვეთად და უნდოდათ დაერქმიათ მამამისის სახელი – ზაქარია.",
			"60": "<sup>60</sup> მიუგო დედამისმა და თქვა: „არა, იოანე ერქმევა მას.“",
			"61": "<sup>61</sup> უთხრეს მას, რომ შენს ნათესაობაში არავინ არის, რომ ეგ სახელი ერქვას.",
			"62": "<sup>62</sup> ნიშნებით ჰკითხეს მამამისს, რისი დარქმევა სურდა.",
			"63": "<sup>63</sup> და ფიცარი მოითხოვა და დაწერა: „იოანეა მისი სახელი.“ და ყველას გაუკვირდა ეს.",
			"64": "<sup>64</sup> უცებ გაეხსნა მას პირი და ენა, ალაპარაკდა და ადიდა ღმერთი.",
			"65": "<sup>65</sup> შიშმა მოიცვა ყველა, ვინც მათ ირგვლივ ცხოვრობდა. და ყოველივე ამაზე დიდი მითქმა-მოთქმა იყო მთელი იუდეის მთიანეთში.",
			"66": "<sup>66</sup> ყველამ, ვინც მოისმინა ეს, გულში ჩაიდო და თქვა: „რა იქნება ეს ყრმა?“ და უფლის ხელი იყო მასთან.",
			"67": "<sup>67</sup> მამამისი ზაქარია აღივსო სულიწმიდით და წინასწარმეტყველებდა და ამბობდა:",
			"68": "<sup>68</sup> „კურთხეულ არს უფალი, ღმერთი ისრაელისა, რადგან მოხედა თავის ხალხს და გაუმზადა მას გამოსყიდვა.",
			"69": "<sup>69</sup> აღგვიდგინა რქა ხსნისა თავისი მსახურის, დავითის სახლში,",
			"70": "<sup>70</sup> როგორც ელაპარაკებოდა ძველთაგანვე თავის წმიდა წინასწარმეტყველთა პირით,",
			"71": "<sup>71</sup> რომ გვიხსნიდა ჩვენი მტრებისა და ყველა ჩვენი მოძულის ხელიდან;",
			"72": "<sup>72</sup> წყალობას უყოფდა ჩვენს მამებს და გაიხსენებდა თავის წმიდა აღთქმას;",
			"73": "<sup>73</sup> ფიცს, რომლითაც შეჰფიცა ჩვენს მამას, აბრაამს, რომ შეგვაძლებინებდა ჩვენ,",
			"74": "<sup>74</sup> ჩვენი მტრების ხელიდან თავდახსნილთ, უშიშრად",
			"75": "<sup>75</sup> გვემსახურა მისთვის სიწმიდით და სიმართლით მის წინაშე, მთელი ჩვენი სიცოცხლე.",
			"76": "<sup>76</sup> და შენ, ყრმაო, დაგერქმევა მაღლის წინასწარმეტყველი, ვინაიდან იწინამძღვრებ უფლის წინაშე მისი გზების გასამზადებლად,",
			"77": "<sup>77</sup> რომ მისცე მის ხალხს ხსნის ცოდნა მათი ცოდვების მიტევებით,",
			"78": "<sup>78</sup> ჩვენი ღვთის გულმოწყალებით, რომლითაც გადმოგვხედა აღმოსავლეთმა მაღლიდან,",
			"79": "<sup>79</sup> ბნელში და სიკვდილის ჩრდილში მსხდომარეთათვის გასანათებლად, ჩვენს ფერხთა წასამართავად სამშვიდობო გზაზე.“",
			"80": "<sup>80</sup> ყრმა კი იზრდებოდა და სულით მტკიცდებოდა, და უდაბნოში იყო იმ დღეებამდე, ვიდრე ისრაელს გამოეცხადებოდა."
		},
		"2": {
			"1": "<sup>1</sup> იმ დღეებში გამოვიდა კეისარ ავგუსტუსის ბრძანება: აღეწერათ მთელი მსოფლიო.",
			"2": "<sup>2</sup> ეს აღწერა პირველი იყო სირიაში კვირენიოსის მმართველობისას.",
			"3": "<sup>3</sup> ყველანი მიდიოდნენ ჩასაწერად, თითოეული თავის ქალაქში.",
			"4": "<sup>4</sup> იოსებიც წავიდა გალილეიდან, ქალაქ ნაზარეთიდან იუდეაში, დავითის ქალაქში, რომელსაც ეწოდება ბეთლემი, ვინაიდან იგი დავითის სახლისა და ტომისა იყო.",
			"5": "<sup>5</sup> რათა ჩაწერილიყო მარიამთან ერთად, რომელიც მასზე იყო დანიშნული და ორსულად იყო.",
			"6": "<sup>6</sup> მათი იქ ყოფნისას შეუსრულდა მარიამს მშობიარობის დღეები",
			"7": "<sup>7</sup> და შვა ძე, თავისი პირმშო, შეახვია და მიაწვინა იგი ბაგაში, ვინაიდან სასტუმროში არ იყო ადგილი მათთვის.",
			"8": "<sup>8</sup> იმ მხარეში მწყემსები იყვნენ მინდვრად და ღამით დარაჯობდნენ თავიანთ სამწყსოს.",
			"9": "<sup>9</sup> და აჰა, უფლის ანგელოზი დაადგა მათ, უფლის დიდება გამოუბრწყინდათ და შეშინდნენ დიდი შიშით.",
			"10": "<sup>10</sup> და უთხრა მათ ანგელოზმა: „ნუ გეშინიათ. აჰა, გახარებთ დიდ სიხარულს, რომელიც მთელი ხალხისა იქნება:",
			"11": "<sup>11</sup> ვინაიდან დღეს დავითის ქალაქში თქვენთვის იშვა მაცხოვარი, რომელიც არის ქრისტე უფალი.",
			"12": "<sup>12</sup> და ნიშნად ეს გექნებათ: იპოვით ყრმას შეხვეულს და ბაგაში მწოლარეს.“",
			"13": "<sup>13</sup> და უეცრად გაჩნდა ანგელოზთან ერთად დიდძალი ციური ლაშქარი და აქებდნენ ღმერთს და ამბობდნენ:",
			"14": "<sup>14</sup> „დიდება მაღალთა შინა ღმერთს, მშვიდობა ქვეყანად და კაცთა შორის სათნოება!“",
			"15": "<sup>15</sup> როცა ანგელოზები ზეცად ამაღლდნენ, მწყემსებმა უთხრეს ერთმანეთს: „წავიდეთ ბეთლემს და ვნახოთ მომხდარი ამბავი, უფალმა რომ გვაუწყა.“",
			"16": "<sup>16</sup> გაჩქარებულნი მივიდნენ და ნახეს მარიამი და იოსები, და ბაგაში მწოლარე ყრმა.",
			"17": "<sup>17</sup> როცა დაინახეს, მოუთხრეს ამბავი, რაც ემცნოთ მათ ამ ყრმაზე.",
			"18": "<sup>18</sup> ვინც კი ისმენდა, ყველას უკვირდა, რასაც მწყემსები ეუბნებოდნენ მათ.",
			"19": "<sup>19</sup> მარიამმა კი ჩაიმარხა ყოველი სიტყვა და ჩაიდო ისინი გულში.",
			"20": "<sup>20</sup> და დაბრუნდნენ მწყემსები და ადიდებდნენ და აქებდნენ ღმერთს ყოველივე იმის გამო, რაც ესმათ და იხილეს, როგორც ემცნოთ მათ.",
			"21": "<sup>21</sup> და როცა შესრულდა რვა დღე, რათა წინადაეცვითათ მისთვის, დაერქვა მისი სახელი იესო, ანგელოზის მიერ დარქმეული მუცელში ჩასახვამდე.",
			"22": "<sup>22</sup> და როცა გასრულდა მათი განწმედის დღეები მოსეს რჯულისამებრ, აიყვანეს იგი იერუსალიმს უფლის წინაშე წარსადგენად.",
			"23": "<sup>23</sup> როგორც უფლის რჯულში სწერია: „ყოველ მამროვანს, რომელმაც განაპოს საშო, უფლის წმიდა ეწოდოს,",
			"24": "<sup>24</sup> და შესწირონ მსხვერპლად, როგორც უფლის რჯულშია ნათქვამი, ორი გვრიტი ან ორი მართვე მტრედისა“",
			"25": "<sup>25</sup> და აჰა, იყო იერუსალიმში კაცი, სახელად სიმონი, კაცი მართალი და ღვთისმოშიში, რომელიც მოელოდა ისრაელის ნუგეშისცემას, და სულიწმიდა იყო მასზე.",
			"26": "<sup>26</sup> სულიწმიდისგან ჰქონდა ნაუწყები, რომ არ იხილავდა სიკვდილს უფლის ცხებულის ხილვამდე.",
			"27": "<sup>27</sup> შევიდა სულით ტაძარში და, როცა მშობლებმა მიიყვანეს ყრმა იესო, რათა მოქცეოდნენ მას რჯულის წესისამებრ,",
			"28": "<sup>28</sup> მან მიირქვა ყრმა მკლავებზე, ადიდა ღმერთი და თქვა:",
			"29": "<sup>29</sup> „ახლა კი გამიშვი, ბატონო, შენი მონა მშვიდობით, როგორც ნათქვამი გაქვს.",
			"30": "<sup>30</sup> ვინაიდან იხილა ჩემმა თვალმა შენი ხსნა,",
			"31": "<sup>31</sup> რომელიც შენ განამზადე ყოველი ხალხის წინაშე,",
			"32": "<sup>32</sup> წარმართთათვის ნათლის გამოსაცხადებლად და შენი ხალხის, ისრაელის, დიდებად.“",
			"33": "<sup>33</sup> იოსებს და მის დედას კი უკვირდათ მასზე ნათქვამი.",
			"34": "<sup>34</sup> და აკურთხა სიმონმა ისინი და უთხრა მარიამს, მის დედას: „აჰა, განწესებულია ეს მრავალთა დამხობად და აღდგენად ისრაელში და საცილობელ ნიშნად.",
			"35": "<sup>35</sup> და თვით შენს სულშიც გაივლის მახვილი, რათა გაცხადდეს მრავალ გულთაგან ზრახვები.“",
			"36": "<sup>36</sup> და იყო ანა წინასწარმეტყველი, ფენუელის ასული, აშერის ტომიდან, ღრმად მოხუცებული, თავისი ქალწულობის შემდეგ შვიდ წელიწადს ნაცხოვრები ქმართან.",
			"37": "<sup>37</sup> იყო იგი ოთხმოცდაოთხი წლის ქვრივი, არ შორდებოდა ტაძარს, მარხვით და ვედრებით ემსახურებოდა დღე და ღამ.",
			"38": "<sup>38</sup> ისიც იმავე ჟამს იყო მისული და ადიდებდა ღმერთს და ელაპარაკებოდა მასზე ყველას, ვინც კი გამოსყიდვას მოელოდა იერუსალიმში.",
			"39": "<sup>39</sup> როცა აღასრულეს ყოველივე უფლის რჯულისამებრ, დაბრუნდნენ გალილეაში, თავიანთ ქალაქ ნაზარეთში.",
			"40": "<sup>40</sup> ხოლო ყრმა იზრდებოდა, მტკიცდებოდა სულით და ივსებოდა სიბრძნით და ღვთის მადლი იყო მასზე.",
			"41": "<sup>41</sup> და დადიოდნენ მისი მშობლები ყოველ წელიწადს იერუსალიმში პასექის დღესასწაულზე.",
			"42": "<sup>42</sup> და როცა თორმეტი წლისა გახდა, ავიდნენ იერუსალიმში ჩვეულებისამებრ, სადღესასწაულოდ.",
			"43": "<sup>43</sup> ხოლო დღესასწაულის დამთავრების შემდეგ, უკან რომ დაბრუნდნენ, ყრმა იესო დარჩა იერუსალიმში, და არ იცოდნენ იოსებმა და მისმა დედამ.",
			"44": "<sup>44</sup> ეგონათ, რომ მგზავრთა შორის იყო და ერთი დღის გზა რომ გაიარეს, დაუწყეს ძებნა ნათესავებსა და ნაცნობებს შორის.",
			"45": "<sup>45</sup> რომ ვერ იპოვეს იგი, დაბრუნდნენ იერუსალიმში და ეძებდნენ მას.",
			"46": "<sup>46</sup> სამი დღის შემდეგ იპოვეს ტაძარში მჯდომი მოძღვართა შორის, უსმენდა და ეკითხებოდა მათ.",
			"47": "<sup>47</sup> და გაოცებული იყო ყველა მისი მსმენელი მისი გონებითა და პასუხებით.",
			"48": "<sup>48</sup> რომ დაინახეს იგი, გაუკვირდათ, და დედამისმა უთხრა მას: „ეს რა გვიყავი, შვილო? აჰა, მამაშენი და მე შეწუხებულნი დაგეძებთ.“",
			"49": "<sup>49</sup> მან უთხრა მათ: „რატომ მეძებდით? ნუთუ არ იცით, რომ მამის სახლში უნდა ვიყო?“",
			"50": "<sup>50</sup> ხოლო მათ ვერ გაიგეს სიტყვა, რაც უთხრა მათ.",
			"51": "<sup>51</sup> და გაჰყვა მათ და მივიდა ნაზარეთს და ემორჩილებოდა მათ. დედამისმა კი გულში ჩაიმარხა ყოველივე ეს.",
			"52": "<sup>52</sup> ხოლო იესოს ემატებოდა სიბრძნე და ტანი, და მადლი ღვთისა და ადამიანთა წინაშე."
		},
		"3": {
			"1": "<sup>1</sup> ტიბერიოს კეისრის მთავრობის მეთხუთმეტე წელს, პონტოელი პილატეს განმგებლობისას იუდეაში, და ჰეროდეს ტეტრარქობისას გალილეაში, მისი ძმის, ფილიპეს, ტეტრარქობისას იტურეასა და ტრახონიტის მხარეში, და ლისანიასის ტეტრარქობისას აბილენეში,",
			"2": "<sup>2</sup> ანას და კაიაფას მღვდელმთავრობის დროს იყო სიტყვა ღვთისა იოანესადმი, ზაქარიას ძისადმი, უდაბნოში.",
			"3": "<sup>3</sup> დადიოდა იგი იორდანეს მთელს არემარეში და ქადაგებდა სინანულის ნათლისღებას ცოდვათა მისატევებლად,",
			"4": "<sup>4</sup> როგორც სწერია ესაია წინასწარმეტყველის სიტყვათა წიგნში: „ხმა მღაღადებლისა უდაბნოში: განუმზადეთ გზა უფალს, მოასწორეთ მისი ბილიკები.",
			"5": "<sup>5</sup> ყოველი ხევი ამოივსება, ყოველი მთა და ბორცვი დადაბლდება, მრუდე გასწორდება და ოღროჩოღრო გზა გავაკდება.",
			"6": "<sup>6</sup> და ყოველი ხორციელი იხილავს ხსნას ღვთისაგან.“",
			"7": "<sup>7</sup> მოსანათლავად მისულ ხალხს იოანე ეუბნებოდა: „იქედნეთა ნაშობნო, ვინ გაგაფრთხილათ, რომ გაექცეთ მომავალ რისხვას?",
			"8": "<sup>8</sup> მაშ, გამოიღეთ მონანიების ღირსი ნაყოფნი და ნუ მოჰყვებით ერთმანეთში ლაპარაკს: მამად აბრაამი გვყავსო. ვინაიდან, გეუბნებით თქვენ, რომ ღმერთს შეუძლია ამ ქვებიდან აღუდგინოს აბრაამს შვილები.",
			"9": "<sup>9</sup> ცულიც უკვე დევს ხეთა ფესვთან. ყოველ ხეს, რომელიც კეთილ ნაყოფს არ გამოიღებს, ჭრიან და ცეცხლში აგდებენ.“",
			"10": "<sup>10</sup> ეკითხებოდა მას ხალხი და ეუბნებოდა: აბა, რაღა ვქნათო?",
			"11": "<sup>11</sup> მიუგებდა და ეტყოდა მათ: „ვისაც ორი მოსასხამი აქვს, მისცეს უქონელს. ვისაც საზრდო აქვს, ისიც ასე მოიქცეს.“",
			"12": "<sup>12</sup> მოვიდნენ მებაჟენიც მოსანათლავად და უთხრეს მას: „მოძღვარო, რა ვქნათ?“",
			"13": "<sup>13</sup> მან უთხრა მათ: „თქვენთვის დაწესებულის მეტს ნურაფერს მოითხოვთ.“",
			"14": "<sup>14</sup> ჯარისკაცებიც ეკითხებოდნენ: „ჩვენ რაღა ვქნათ?“ უთხრა მათ: „ნურავის შეავიწროვებთ, ცილს ნუ დასწამებთ და თქვენს ჯამაგირს დასჯერდით.“",
			"15": "<sup>15</sup> როცა ხალხი მოლოდინში იყო და ყველა თავის გულში ფიქრობდა იოანეზე, ქრისტე ხომ არ არისო,",
			"16": "<sup>16</sup> მიუგებდა და ეტყოდა ყველას იოანე: „მე წყლით გნათლავთ, მაგრამ მოდის ჩემზე ძლიერი, რომლის სანდლის თასმის გახსნის ღირსიც არა ვარ. ის მოგნათლავთ თქვენ სულიწმიდით და ცეცხლით,",
			"17": "<sup>17</sup> რომლის ხელშია ნიჩაბი, რათა გაწმიდოს თავისი კალო და თავის ბეღელში მოაგროვოს ხორბალი, ბზე კი დაწვას ჩაუქრობელი ცეცხლით.“",
			"18": "<sup>18</sup> კიდევ ბევრ სხვა რამესაც ახარებდა და შეაგონებდა ხალხს.",
			"19": "<sup>19</sup> ხოლო ჰეროდემ, ტეტრარქმა, რომელსაც იგი ამხილებდა მისი ძმის ცოლის ჰეროდიას გამო და ყოველივე იმის გამო, რა ბოროტებაც ჩაედინა,",
			"20": "<sup>20</sup> ყველაფერს ამას ისიც მიუმატა, რომ იოანე საპყრობილეში ჩასვა.",
			"21": "<sup>21</sup> როცა მთელი ხალხი ინათლებოდა და იესოც მოინათლა და ლოცულობდა, გაიხსნა ცა",
			"22": "<sup>22</sup> და გადმოვიდა მასზე სულიწმიდა ხორციელი სახით, როგორც მტრედი, და გაისმა ზეცით ხმა: „შენ ხარ ჩემი საყვარელი ძე, რომელიც მოვიწონე მე.“",
			"23": "<sup>23</sup> თვით იესო, როცა იწყებდა, იქნებოდა ოცდაათი წლისა და, როგორც ფიქრობდნენ, იყო ძე იოსებისა, ყელისა,",
			"24": "<sup>24</sup> მათათისა, ლევისა, მალქისა, იანაისა, იოსებისა,",
			"25": "<sup>25</sup> მათითიასი, ამოცისა, ნახუმისა, ხესლისა, ნაგაისა,",
			"26": "<sup>26</sup> მახათისა, მათითიასი, შიმყისა, იოსებისა, იოდასი,",
			"27": "<sup>27</sup> იოხანანისა, რეშასი, ზერუბაბელისა, შეალთიელისა, ნერისა,",
			"28": "<sup>28</sup> მალქისა, ადისა, კოსამისა, ელმადამისა, ყერისა,",
			"29": "<sup>29</sup> იესოსი, ელეაზარისა, იორიმისა, მათათისა, ლევისა,",
			"30": "<sup>30</sup> სიმონისა, იუდასი, იოსებისა, იონამისა, ელიაკიმისა,",
			"31": "<sup>31</sup> მალეასი, მინასი, მათათასი, ნათანისა, დავითისა,",
			"32": "<sup>32</sup> იესესი, ყობედისა, ბოყაზისა, სალმონისა, ნახშონისა,",
			"33": "<sup>33</sup> ყამინადაბისა, ადმინისა, არნისა, ხეცრონისა, ფერეცისა, იუდასი,",
			"34": "<sup>34</sup> იაკობისა, ისაკისა, აბრაამისა, თერახისა, ნახორისა,",
			"35": "<sup>35</sup> სერუგისა, რეყუსი, ფელეგისა, ყებერისა, შალახისა,",
			"36": "<sup>36</sup> კენანისა, არფაქშადისა, სემისა, ნოესი, ლამექისა,",
			"37": "<sup>37</sup> მეთუშელახისა, ხანოქისა, იერედისა, მაჰალალელისა, კენანისა,",
			"38": "<sup>38</sup> ენოშისა, შეთისა, ადამისა, ღვთისა."
		},
		"4": {
			"1": "<sup>1</sup> იესო, სულიწმიდით აღვსილი დაბრუნდა იორდანედან და სულით გაყვანილ იქნა უდაბნოში",
			"2": "<sup>2</sup> და ორმოც დღეს სცდიდა მას ეშმაკი და არაფერი უჭამია იმ დღეებში, და მათი გასვლის შემდეგ მოშივდა.",
			"3": "<sup>3</sup> უთხრა მას ეშმაკმა: „თუ ღვთის ძე ხარ, უთხარი ამ ქვას, რომ პურად იქცეს.“",
			"4": "<sup>4</sup> მიუგო მას იესომ: „დაწერილია, რომ არა მხოლოდ პურით უნდა ცოცხლობდეს კაცი, არამედ ღვთის ყოველი სიტყვით.“",
			"5": "<sup>5</sup> და აიყვანა იგი მაღალ მთაზე და უჩვენა ერთ წამში მსოფლიოს ყველა სამეფო.",
			"6": "<sup>6</sup> და უთხრა მას ეშმაკმა: „მოგცემ მთელს ამ ხელმწიფებას და მათ დიდებას, ვინაიდან მე მაქვს მოცემული და, ვისაც მინდა, იმას ვაძლევ.",
			"7": "<sup>7</sup> თუ თაყვანსა მცემ, შენი იქნება ყოველივე!“",
			"8": "<sup>8</sup> მიუგო იესომ და უთხრა მას: „დაწერილია: ‘უფალს, შენს ღმერთს, ეცი თაყვანი და მხოლოდ მას ემსახურე.’“",
			"9": "<sup>9</sup> წაიყვანა იგი იერუსალიმს, დააყენა ტაძრის ფრთაზე და უთხრა: „თუ ღვთის ძე ხარ, თავი გადაიგდე აქედან ძირს.",
			"10": "<sup>10</sup> ვინაიდან დაწერილია, რომ ‘თავის ანგელოზებს უბრძანებს შენზე, რომ დაგიცვან’",
			"11": "<sup>11</sup> და რომ ‘ხელში აყვანილი გატარონ, რათა ქვას ფეხი არ წამოჰკრა.’“",
			"12": "<sup>12</sup> და მიუგო და უთხრა მას იესომ: „ნათქვამია: ‘არ გამოსცადო უფალი, ღმერთი შენი.’“",
			"13": "<sup>13</sup> და როცა დაამთავრა ყველა გამოცდა, ეშმაკი გაშორდა მას სხვა ჟამის მოლოდინში.",
			"14": "<sup>14</sup> დაბრუნდა იესო სულის ძალით გალილეაში და მისი ამბავი მოედო მთელ არემარეს.",
			"15": "<sup>15</sup> ასწავლიდა მათ სინაგოგებში და განდიდებულ იყო ყველას მიერ.",
			"16": "<sup>16</sup> და მივიდა ნაზარეთს, სადაც აღიზარდა. და შევიდა შაბათ დღეს, თავისი ჩვეულებისამებრ, სინაგოგაში და დადგა წასაკითხავად.",
			"17": "<sup>17</sup> და მიაწოდეს მას ესაია წინასწარმეტყველის წიგნი, და გაშალა წიგნი და იპოვა ადგილი, სადაც დაწერილი იყო:",
			"18": "<sup>18</sup> „უფლის სულია ჩემზე, ვინაიდან მან მცხო გლახაკთა სახარებლად, მომავლინა გულშემუსვრილთა განსაკურნებლად, ტყვეთათვის თავისუფლების და ბრმათათვის თვალის ახელის გამოსაცხადებლად, ჩაგრულთა გასათავისუფლებლად,",
			"19": "<sup>19</sup> უფლის შეწყალების წლის გამოსაცხადებლად.“",
			"20": "<sup>20</sup> და დახურა წიგნი, გადასცა მსახურს და დაჯდა. ყველას თვალი სინაგოგაში მისკენ იყო მიპყრობილი.",
			"21": "<sup>21</sup> და დაუწყო ლაპარაკი მათ: „დღეს აღსრულდა ეს წერილი, თქვენ რომ მოისმინეთ.“",
			"22": "<sup>22</sup> და ყველამ დაუმოწმა მას და გაოცებულნი იყვნენ მისი პირიდან გამოსული მადლის სიტყვებით და ამბობდნენ: „ეს იოსების ძე არ არის?“",
			"23": "<sup>23</sup> უთხრა მათ: „ნამდვილად მეტყვით ამ ანდაზას: ‘მკურნალო, განიკურნე შენი თავი. ყველაფერი, რაც კი კაპერნაუმში მოხდა, როგორც გავიგეთ, მოახდინე აქაც, შენს სამშობლოში.’“",
			"24": "<sup>24</sup> უთხრა: „ჭეშმარიტად გეუბნებით თქვენ, რომ არც ერთი წინასწარმეტყველი არაა მიღებული თავის სამშობლოში.",
			"25": "<sup>25</sup> ჭეშმარიტებით გეუბნებით თქვენ, მრავალი ქვრივი იყო ისრაელში ელიას დროს, როცა დაიხშო ცა სამ წელიწადსა და ექვს თვეს ისე, რომ დიდი შიმშილობა იყო მთელ ქვეყანაზე.",
			"26": "<sup>26</sup> და არც ერთ მათგანთან არ იქნა მივლენილი ელია, გარდა ქვრივი დედაკაცისა ციდონის ცარფათში.",
			"27": "<sup>27</sup> კეთროვანიც მრავალი იყო ისრაელში ელისე წინასწარმეტყველის დროს, მაგრამ არც ერთი მათგანი არ გაწმენდილა, გარდა ნეემან სირიელისა.“",
			"28": "<sup>28</sup> ეს რომ მოისმინეს სინაგოგაში მყოფთ, ყველანი მძვინვარებით აღივსნენ.",
			"29": "<sup>29</sup> წამოდგნენ და გააძევეს იგი ქალაქიდან, და მიიყვანეს იმ მთის წვერამდე, რომელზეც მათი ქალაქი იყო გაშენებული, რათა გადაეგდოთ იგი.",
			"30": "<sup>30</sup> მაგრამ მან გაიარა მათ შორის და წავიდა.",
			"31": "<sup>31</sup> და ჩავიდა გალილეის ქალაქ კაპერნაუმში და იქ ასწავლიდა მათ შაბათობით",
			"32": "<sup>32</sup> და გაოცებულნი იყვნენ მისი მოძღვრებით, ვინაიდან ძალმოსილი იყო მისი სიტყვა.",
			"33": "<sup>33</sup> სინაგოგაში იყო კაცი, უწმიდური სულით შეპყრობილი, და იყვირა ხმამაღლა:",
			"34": "<sup>34</sup> „რა გინდა ჩვენგან, იესო ნაზარეველო? ჩვენს დასაღუპავად მოხვედი? გიცნობ, ვინცა ხარ, ღვთის წმიდაო.“",
			"35": "<sup>35</sup> იესომ შერისხა იგი და უთხრა: „გაჩუმდი და გადი მაგისგან!“ და დასცა იგი ეშმაკმა მათ შუაში და გამოვიდა მისგან, და არაფერი უვნია მისთვის.",
			"36": "<sup>36</sup> ყველანი განცვიფრდნენ და ამბობდნენ ერთმანეთში: „ეს რა სიტყვაა, რომ ხელმწიფებითა და ძალით უწმიდურ სულებს უბრძანებს და ისინიც გამოდიან?“",
			"37": "<sup>37</sup> და მოეფინა ხმა მის შესახებ იმ მხარის ყოველ ადგილს.",
			"38": "<sup>38</sup> ადგა სინაგოგიდან და შევიდა სიმონის სახლში. ხოლო სიმონის სიდედრი შეპყრობილი იყო დიდი სიცხით და სთხოვეს მისთვის.",
			"39": "<sup>39</sup> და დაადგა თავზე და შერისხა სიცხე. მანაც მიატოვა იგი. მყისვე წამოდგა და ემსახურებოდა მათ.",
			"40": "<sup>40</sup> მზის ჩასვლისას ყველამ, ვისაც ჰყავდა სხვადასხვა სნეულებებით დაავადებულნი, მასთან მიიყვანა, და მან ყოველ მათგანს ხელები დაასხა და განკურნა.",
			"41": "<sup>41</sup> ბევრისგან ეშმაკებიც გამოდიოდნენ, ყვიროდნენ და ამბობდნენ: „შენ ხარ ქრისტე, ძე ღვთისა!“ ის კი რისხავდა და უკრძალავდა ლაპარაკს, რადგან იცოდნენ მათ, რომ ქრისტეა იგი.",
			"42": "<sup>42</sup> დღე რომ გათენდა, გამოვიდა იქიდან და წავიდა უდაბურ ადგილას. ხალხმა მოძებნა და მივიდნენ მასთან და აკავებდნენ, რომ არ წასულიყო მათგან.",
			"43": "<sup>43</sup> მან კი უთხრა მათ: „სხვა ქალაქებსაც უნდა ვახარო ღვთის სასუფეველი, ვინაიდან ამისთვის ვარ მოვლენილი.“",
			"44": "<sup>44</sup> და ქადაგებდა გალილეის სინაგოგებში."
		},
		"5": {
			"1": "<sup>1</sup> ერთხელ, როცა ხალხის ბრბო აწყდებოდა მას, რომ ღვთის სიტყვა მოესმინათ, და ის იდგა გენესარეთის ტბასთან,",
			"2": "<sup>2</sup> დაინახა ტბასთან მდგომი ორი ნავი. ნავებიდან გადმოსული მებადურები ბადეებს რეცხავდნენ.",
			"3": "<sup>3</sup> ავიდა ერთ ნავზე, რომელიც სიმონისა იყო. სთხოვა ნაპირს ცოტა გაშორებოდა, ჩაჯდა და ნავიდან ასწავლიდა ხალხს.",
			"4": "<sup>4</sup> როცა ლაპარაკი დაამთავრა, უთხრა სიმონს: „შეცურდი ღრმად და მოისროლეთ ბადეები თევზის დასაჭერად.“",
			"5": "<sup>5</sup> მიუგო სიმონმა და უთხრა: „მოძღვარო, მთელი ღამე ვიშრომეთ და ვერაფერი დავიჭირეთ. მაგრამ შენი სიტყვით ვისვრი ბადეს.“",
			"6": "<sup>6</sup> ასეც მოიქცნენ. იმდენი თევზი მოიმწყვდიეს, რომ ბადეები ეხეოდათ.",
			"7": "<sup>7</sup> ნიშანი მისცეს ამხანაგებს მეორე ნავში, რომ მოსულიყვნენ მოსაშველებლად. მოვიდნენ და ისე აავსეს ორივე ნავი, რომ ლამის იძირებოდნენ.",
			"8": "<sup>8</sup> ეს რომ დაინახა სიმონ-პეტრემ, დაეცა იესოს მუხლებთან და უთხრა: „გამშორდი, უფალო, ვინაიდან ცოდვილი კაცი ვარ.“",
			"9": "<sup>9</sup> რადგან შიშმა შეიპყრო იგი და ყველა მასთან მყოფი იმ თევზისჭერის გამო, მათ რომ დაიჭირეს.",
			"10": "<sup>10</sup> ასევე იაკობი და იოანე, ზებედეს ძენი, სიმონის მოზიარენი. და უთხრა სიმონს იესომ: „ნუ გეშინია, ამიერიდან ადამიანთა დამჭერი იქნები.“",
			"11": "<sup>11</sup> მიაყენეს ნავები მიწას, ყველაფერი მიატოვეს და გაჰყვნენ მას.",
			"12": "<sup>12</sup> ერთხელ, ერთ-ერთ ქალაქში მისი ყოფნისას, აჰა, მოვიდა კეთრით დაფარული კაცი. დაინახა იესო და დაემხო, ევედრებოდა და ეუბნებოდა: „უფალო, თუ გსურს, შეგიძლია ჩემი განწმედა.“",
			"13": "<sup>13</sup> და გაიწოდა ხელი, შეახო მას და თქვა: „მსურს, განიწმიდე!“ და მყისვე მოშორდა კეთრი.",
			"14": "<sup>14</sup> და უბრძანა მას: „არავის უთხრა, არამედ წადი და ეჩვენე მღვდელს და შესწირე შენი განწმედისთვის, როგორც დაუდგინა მათ მოსემ მოწმობად.“",
			"15": "<sup>15</sup> და უფრო და უფრო ვრცელდებოდა სიტყვა მის შესახებ და დიდძალი ხალხი დაჰყვებოდა მას, რათა მოესმინათ და განკურნებულიყვნენ თავიანთ სნეულებათაგან.",
			"16": "<sup>16</sup> ის კი უდაბურ ადგილებში მიდიოდა და ლოცულობდა.",
			"17": "<sup>17</sup> ერთ დღეს, როცა ის ასწავლიდა, ისხდნენ იქ ფარისეველნი და რჯულის მოძღვარნი, რომელნიც მოსულიყვნენ გალილეისა და იუდეის ყოველი კუთხიდან და იერუსალიმიდან, და უფლის ძალა იყო მასთან განსაკურნავად,",
			"18": "<sup>18</sup> აჰა, კაცებმა საწოლით მოიყვანეს ადამიანი, რომელიც დავრდომილი იყო, და ცდილობდნენ, შიგნით შეეყვანათ და იესოს წინ დაეწვინათ.",
			"19": "<sup>19</sup> და რომ ვერ მოახერხეს მისი შეყვანა ხალხმრავლობის გამო, ერდოზე ავიდნენ და კრამიტებს შორის საწოლიანად ჩაუშვეს შუაგულ ოთახში იესოს წინ.",
			"20": "<sup>20</sup> რომ დაინახა მათი რწმენა, უთხრა: „ადამიანო, მოგეტევა შენი ცოდვები.“",
			"21": "<sup>21</sup> და იწყეს მწიგნობრებმა და ფარისევლებმა მსჯელობა ურთიერთშორის და ამბობდნენ: „ვინ არის ეს, ღმერთს რომ გმობს? ვის ძალუძს ცოდვების მიტევება ერთი ღვთის გარდა?“",
			"22": "<sup>22</sup> იესო მიხვდა მათ ზრახვებს, და მიუგო და უთხრა: „რას კითხულობთ თქვენს გულში?",
			"23": "<sup>23</sup> რა უფრო ადვილია, თქმა: შენი ცოდვები მოგეტევა, თუ თქმა: ადექ და გაიარე?",
			"24": "<sup>24</sup> რათა იცოდეთ, რომ კაცის ძეს მიწაზე აქვს ცოდვების მიტევების ხელმწიფება, – ეუბნება დავრდომილს: შენ გეუბნები, ადექ, აიღე შენი საწოლი და შინ წადი.“",
			"25": "<sup>25</sup> და მყისვე წამოდგა მათ წინაშე, აიღო, რაზეც იწვა, და წავიდა თავის სახლში და ადიდებდა ღმერთს.",
			"26": "<sup>26</sup> და განცვიფრებამ შეიპყრო ყველა და ადიდებდნენ ღმერთს და შეშინებულები ამბობდნენ: საოცრებანი ვიხილეთო დღეს.",
			"27": "<sup>27</sup> ამის შემდეგ გამოვიდა და დაინახა მებაჟე, სახელად ლევი, რომელიც იჯდა საბაჟოში, და უთხრა მას: „გამომყევი მე.“",
			"28": "<sup>28</sup> მანაც მიატოვა ყოველივე, წამოდგა და გაჰყვა მას.",
			"29": "<sup>29</sup> და მოუმზადა ლევიმ მას დიდი ნადიმი თავის სახლში. და იყო იქ ბევრი მებაჟე და სხვანი, რომლებიც მათთან ერთად უსხდნენ სუფრას.",
			"30": "<sup>30</sup> მათი მწიგნობარნი და ფარისეველნი დრტვინავდნენ და ეუბნებოდნენ მის მოწაფეებს: „რატომ ჭამთ და სვამთ მებაჟეებთან და ცოდვილებთან ერთად?“",
			"31": "<sup>31</sup> და მიუგო იესომ და უთხრა მათ: „მკურნალი ჯანმრთელებს კი არა, სნეულებს სჭირდებათ.",
			"32": "<sup>32</sup> მოვედი არა მართალთა მოსაწოდებლად, არამედ ცოდვილთა, რათა მოინანიონ.“",
			"33": "<sup>33</sup> მათ კი უთხრეს მას: „იოანეს მოწაფეები ხშირად მარხულობენ და ლოცვებს აღავლენენ, ასევე ფარისეველთანიც. შენები კი ჭამენ და სვამენ.“",
			"34": "<sup>34</sup> იესომ უთხრა მათ: „როგორ აიძულებთ მექორწილეებს, იმარხულონ, ვიდრე ნეფე მათთან არის?",
			"35": "<sup>35</sup> მაგრამ დადგება დღეები, როცა ნეფე წაერთმევათ და მაშინ იმარხულებენ, იმ დღეებში.“",
			"36": "<sup>36</sup> იგავიც უთხრა მათ: „არავინ მოხევს საკერებელს ახალ სამოსელს და არ დაადებს ძველ სამოსელს, თორემ ახალსაც დახევს და ძველსაც არ გამოადგება ახალი სამოსლის საკერებელი.",
			"37": "<sup>37</sup> არავინ ჩაასხამს ახალ ღვინოს ძველ ტიკებში, თორემ ახალი ღვინო ტიკებს დახეთქავს და ღვინოც დაიღვრება და ტიკებიც დაიღუპება.",
			"38": "<sup>38</sup> არამედ ახალი ღვინო ახალ ტიკებში უნდა ჩაისხას.",
			"39": "<sup>39</sup> არც ერთი ძველის მსმელი არ მოისურვებს ახალს, რადგან იტყვის: ძველი სჯობიაო.“"
		},
		"6": {
			"1": "<sup>1</sup> ერთხელ, შაბათს, ყანებზე გადავლა მოუხდა; და მისი მოწაფეები წყვეტდნენ თავთავებს, ხელით ფშვნეტდნენ და ჭამდნენ.",
			"2": "<sup>2</sup> ფარისეველთაგან ზოგიერთებმა უთხრეს მათ: „რატომ აკეთებთ იმას, რისი გაკეთებაც შაბათს არაა ნებადართული?“",
			"3": "<sup>3</sup> და მიუგო და უთხრა მათ იესომ: „ნუთუ არ წაგიკითხავთ, რა ქნა დავითმა, როცა მოშივდა მას და მასთან მყოფებს?",
			"4": "<sup>4</sup> როგორ შევიდა ღვთის სახლში, აიღო და შეჭამა საწინაშეო პურები და თავისთან მყოფთაც დაურიგა, რომლის ჭამის ნებაც არავის ჰქონდა, გარდა მღვდლებისა?“",
			"5": "<sup>5</sup> და უთხრა მათ: „შაბათის ბატონიც არის კაცის ძე.“",
			"6": "<sup>6</sup> და მოხდა, რომ ერთ სხვა შაბათსაც შევიდა სინაგოგაში და ასწავლიდა მათ. იქ იყო კაცი, რომელსაც მარჯვენა ხელი გამხმარი ჰქონდა.",
			"7": "<sup>7</sup> მწიგნობარნი და ფარისეველნი უთვალთვალებდნენ მას, აბა, შაბათს თუ განკურნავსო, რათა ეპოვათ როგორ დაედოთ ბრალი მისთვის.",
			"8": "<sup>8</sup> მან იცოდა მათი ზრახვები და უთხრა კაცს, რომელსაც ხელი გამხმარი ჰქონდა: „ადექ და გამოდი შუაში!“ ისიც ადგა და დადგა შუაში.",
			"9": "<sup>9</sup> უთხრა იესომ მათ: „გეკითხებით თქვენ: შაბათ დღეს კეთილის კეთება შეიძლება თუ ბოროტისა? სულის ხსნა შეიძლება თუ დაღუპვა?“",
			"10": "<sup>10</sup> და თვალი მოავლო ყველას და უთხრა მას: „გაიწოდე შენი ხელი!“ ასეც მოიქცა და მოურჩა ხელი.",
			"11": "<sup>11</sup> ისინი კი რისხვით აღივსნენ და ურთიერთშორის მსჯელობდნენ, რა ექნათ იესოსთვის.",
			"12": "<sup>12</sup> იმ დღეებში წავიდა იგი მთაზე სალოცავად და მთელი ღამე ღვთისადმი ლოცვაში გაატარა.",
			"13": "<sup>13</sup> დღე რომ დადგა, დაუძახა თავის მოწაფეებს და ამოირჩია მათგან თორმეტი, რომელთაც მოციქულები უწოდა:",
			"14": "<sup>14</sup> სიმონი, რომელსაც პეტრე შეარქვა, მისი ძმა ანდრია, იაკობი და იოანე, ფილიპე და ბართოლომე,",
			"15": "<sup>15</sup> მათე და თომა, იაკობ ალფესი და სიმონი, მოშურნედ წოდებული.",
			"16": "<sup>16</sup> იუდა იაკობის ძე და იუდა ისკარიოტელი, რომელიც გამცემი გახდა.",
			"17": "<sup>17</sup> და ჩავიდა მათთან ერთად და დადგა ვაკე ადგილას. იქ იყო მრავალი მისი მოწაფე და ბევრი ხალხი იუდეიდან და იერუსალიმიდან, და ზღვისპირეთიდან – ტვიროსიდან და ციდონიდან,",
			"18": "<sup>18</sup> რომელნიც მოვიდნენ, რომ მისთვის მოესმინათ და თავიანთი სნეულებებისგან განკურნებულიყვნენ – უწმიდური სულით გატანჯულნიც – და იკურნებოდნენ.",
			"19": "<sup>19</sup> მთელი ხალხი ცდილობდა შეხებოდა მას, ვინაიდან მისგან გამოდიოდა ძალა და ყველას კურნავდა.",
			"20": "<sup>20</sup> აღმართა მზერა თავის მოწაფეებზე და უთხრა: „ნეტარნი ხართ, გლახაკნი, ვინაიდან თქვენია ღვთის სასუფეველი.",
			"21": "<sup>21</sup> ნეტარნი ხართ, ვისაც ახლა გშიათ, ვინაიდან გაძღებით. ნეტარნი ხართ, ვინც ახლა ტირით, ვინაიდან გაიცინებთ.",
			"22": "<sup>22</sup> ნეტარნი ხართ, როცა შეგიძულებენ ადამიანები, როცა გაგყრიან და გაგლანძღავენ, ხოლო თქვენს სახელს განდევნიან, როგორც ბოროტს, კაცის ძის გამო.",
			"23": "<sup>23</sup> გაიხარეთ იმ დღეს და გამხიარულდით, ვინაიდან, აჰა, დიდია თქვენი საზღაური ცაში, ვინაიდან ასევე ექცეოდნენ წინასწარმეტყველთ მათი მამები.",
			"24": "<sup>24</sup> მაგრამ, ვაი თქვენ, მდიდარნო, ვინაიდან უკვე მიიღეთ თქვენი ნუგეშისცემა.",
			"25": "<sup>25</sup> ვაი თქვენ, მაძღარნო, აწ, ვინაიდან მოგშივდებათ. ვაი თქვენ, ვინც ახლა იცინით, ვინაიდან იგლოვებთ და იტირებთ.",
			"26": "<sup>26</sup> ვაი თქვენ, როცა ყველანი კარგს ილაპარაკებენ თქვენზე. ასევე ექცეოდნენ ცრუწინასწარმეტყველთ მათი მამები.",
			"27": "<sup>27</sup> ხოლო თქვენ, ვინც მისმენთ, გეტყვით: გიყვარდეთ თქვენი მტრები და სიკეთე უყავით თქვენს მოძულეებს.",
			"28": "<sup>28</sup> აკურთხებდეთ თქვენს მაწყევრებს და ლოცულობდეთ თქვენსავე შეურაცხმყოფელთათვის.",
			"29": "<sup>29</sup> ვინც ლოყაში გაგარტყას, მეორეც მიუშვირე. ვინც სამოსელი წაგართვას, მოსასხამსაც ნუ დაუჭერ.",
			"30": "<sup>30</sup> ყველას, ვინც გთხოვოს, მიეცი და ვინც გამოგართმევს, უკან ნუღარ მოსთხოვ.",
			"31": "<sup>31</sup> როგორც თქვენ გინდათ, რომ მოგექცნენ ადამიანები, თქვენც ისევე მოექეცით მათ.",
			"32": "<sup>32</sup> თუ თქვენი მოყვარულნი გეყვარებათ, თქვენთვის რა მადლია? ვინაიდან ცოდვილთაც უყვართ თავიანთი მოყვარულნი.",
			"33": "<sup>33</sup> თუ სიკეთეს უზამთ თქვენს კეთილისმყოფელთ, თქვენთვის რა მადლია? ცოდვილნიც ასევე აკეთებენ.",
			"34": "<sup>34</sup> თუ სესხს აძლევთ იმას, ვისი იმედიც გაქვთ, რომ უკან დაგიბრუნებთ, ეს რაღა მადლია? ცოდვილნიც ასესხებენ ცოდვილთ, რომ დაიბრუნონ იმდენივე.",
			"35": "<sup>35</sup> ხოლო თქვენ გიყვარდეთ თქვენი მტრები, სიკეთე უყავით და ასესხეთ და ნურაფერს მოელით, და დიდი იქნება თქვენი საზღაური: მაღლის ძენი იქნებით, ვინაიდან იგი კეთილია უმადურთა და ბოროტთა მიმართაც.",
			"36": "<sup>36</sup> იყავით გულმოწყალენი, ისევე როგორც თქვენი მამაა გულმოწყალე.",
			"37": "<sup>37</sup> ნუ განიკითხავთ და არ განიკითხებით; მსჯავრს ნუ დასდებთ და არ დაგედებათ მსჯავრი; მიუტევეთ და მოგეტევებათ.",
			"38": "<sup>38</sup> მიეცით და თქვენც მოგეცემათ: კარგი საწყაო, დატკეპნილი, გატენილი და პირთამდე სავსე, მოგეცემათ თქვენსავე კალთაში. რადგან რომელი საწყაოთიც მიუწყავთ, იმავეთი მოგეწყვებათ.“",
			"39": "<sup>39</sup> იგავიც უთხრა მათ: „განა შეუძლია ბრმას ბრმის მეგზურობა? განა ორივენი ორმოში არ ჩაცვივდებიან?",
			"40": "<sup>40</sup> მოწაფე მასწავლებელზე მეტი არ არის, ხოლო ყველა სწავლაგასრულებული, ისეთივე იქნება, როგორც მისი მასწავლებელი.",
			"41": "<sup>41</sup> რად უყურებ წველს შენი ძმის თვალში, საკუთარ თვალში კი დირეს ვერ ამჩნევ?",
			"42": "<sup>42</sup> ანდა როგორ შეგიძლია უთხრა შენს ძმას: ძმაო, მიმიშვი, ამოგიღო თვალიდან წველი, საკუთარ თვალში კი დირეს ვერ ხედავ? თვალთმაქცო, ჯერ შენი თვალიდან ამოიღე დირე და მერე დაინახავ, თუ როგორ უნდა ამოუღო შენს ძმას თვალიდან წველი.",
			"43": "<sup>43</sup> არ არსებობს კარგი ხე, რომელმაც ცუდი ნაყოფი გამოიღოს; არც ცუდი ხე, რომელმაც კარგი ნაყოფი გამოიღოს.",
			"44": "<sup>44</sup> ვინაიდან ყოველი ხე თავისი ნაყოფით შეიცნობა: ეკალზე ხომ ვერ მოკრეფენ ლეღვს და არც მაყვლის ბუჩქებზე იკრიფება ყურძენი.",
			"45": "<sup>45</sup> კეთილი კაცი თავისი გულის კეთილი საუნჯიდან სიკეთეს გამოიღებს, ბოროტი კაცი კი ბოროტისგან ბოროტს გამოიღებს. ვინაიდან გულის სავსებისაგან მეტყველებს მისი პირი.",
			"46": "<sup>46</sup> რატომ მიწოდებთ: ‘უფალო, უფალო!’ და იმას კი არ აკეთებთ, რასაც გეუბნებით?",
			"47": "<sup>47</sup> ყველა, ვინც ჩემთან მოდის, ჩემს სიტყვებს ისმენს და მათ ასრულებს, გიჩვენებთ ვისაცა ჰგავს.",
			"48": "<sup>48</sup> იგი ჰგავს სახლის მშენებელ კაცს, რომელმაც მოთხარა, ჩააღრმავა და კლდეზე დადო საძირკველი. წყალდიდობისას მდინარე ეკვეთა იმ სახლს და ძვრა ვერ უყო, ვინაიდან კარგად იყო ნაშენი.",
			"49": "<sup>49</sup> ხოლო ვინც ისმენს და არ ასრულებს, იგი ჰგავს კაცს, რომელმაც სახლი ააშენა მიწაზე უსაძირკვლოდ და, როცა მდინარე ეკვეთა, უმალ დაინგრა და ძალზე დიდი იყო იმ სახლის ჩამოქცევა.“"
		},
		"7": {
			"1": "<sup>1</sup> როცა მან დაასრულა ყველა თავისი სიტყვა ხალხის სასმენელთა მიმართ, შევიდა კაპერნაუმში.",
			"2": "<sup>2</sup> ერთ ასისთავს ავად ჰყავდა და უკვდებოდა მონა, რომელიც მისთვის ძვირფასი იყო,",
			"3": "<sup>3</sup> იესოზე რომ მოისმინა, გაგზავნა მასთან იუდეველთა უხუცესები სათხოვნელად, რათა მოსულიყო და განეკურნა მისი მონა.",
			"4": "<sup>4</sup> ისინი მივიდნენ იესოსთან, სთხოვეს მხურვალედ და უთხრეს: „იგი ღირსია, რომ ეს გაუკეთო,",
			"5": "<sup>5</sup> ვინაიდან უყვარს ჩვენი ხალხი და სინაგოგა მან აგვიშენა.“",
			"6": "<sup>6</sup> იესო წაჰყვა მათ და უკვე იმ სახლის შორი-ახლოს იყვნენ, როცა ასისთავმა გამოუგზავნა მეგობრები მისთვის სათქმელად: „უფალო, ნუ გაირჯები, ვინაიდან ღირსი არა ვარ, რომ ჩემს ჭერქვეშ შემოხვიდე.",
			"7": "<sup>7</sup> ამიტომ ჩემი თავიც არ მივიჩნიე შენთან მოსვლის ღირსად. მხოლოდ სიტყვა თქვი და განიკურნება ჩემი მონა.",
			"8": "<sup>8</sup> რადგან მეც, ძალაუფლებას დამორჩილებულს, ჩემს ხელქვეით მყვანან ჯარისკაცები. ერთს ვეუბნები: ‘წადი!’ და მიდის. და სხვას: ‘მოდი!’ და მოდის. ჩემს მონას: ‘გააკეთე ეს!’ და აკეთებს.“",
			"9": "<sup>9</sup> ეს რომ გაიგონა, იესოს გაუკვირდა მისი, მიუბრუნდა თავის უკან მომავალ ხალხს და უთხრა: „გეუბნებით, რომ ისრაელშიც ვერ ვპოვე მე ასეთი რწმენა.“",
			"10": "<sup>10</sup> წარგზავნილებმა სახლში დაბრუნებისას გამოკეთებული ნახეს ის მონა.",
			"11": "<sup>11</sup> ამის შემდეგ იესო წავიდა ქალაქში, რომელსაც ერქვა ნაინი, და თან მიჰყვებოდნენ მისი მოწაფენი და დიდძალი ხალხი.",
			"12": "<sup>12</sup> ქალაქის კარიბჭეს რომ მიუახლოვდა, აჰა, მოასვენებდნენ მიცვალებულს, დედისერთა ვაჟს, ხოლო დედა ქვრივი იყო. დიდძალი ხალხი მოსდევდა მას ქალაქიდან.",
			"13": "<sup>13</sup> ქვრივის დანახვისას უფალს შეეცოდა იგი და უთხრა: „ნუ სტირი.“",
			"14": "<sup>14</sup> მივიდა და შეეხო ცხედარს. გამსვენებლები შედგნენ და მან თქვა: „ჭაბუკო, შენ გეუბნები, ადექ!“",
			"15": "<sup>15</sup> მკვდარი წამოჯდა და იწყო ლაპარაკი, და იესომ ჩააბარა იგი თავის დედას.",
			"16": "<sup>16</sup> ყველანი შიშმა შეიპყრო, ღმერთს ადიდებდნენ და ამბობდნენ: „დიდი წინასწარმეტყველი აღმდგარა ჩვენს შორის და ღმერთმა მოხედა თავის ხალხს“.",
			"17": "<sup>17</sup> ეს სიტყვა მოედო მთელ იუდეას და მთელ შემოგარენს.",
			"18": "<sup>18</sup> აუწყეს იოანეს მისმა მოწაფეებმა ყოველივე ეს.",
			"19": "<sup>19</sup> იოანემ დაიბარა ორი თავისი მოწაფე და იესოსთან გაგზავნა შესაკითხავად: „შენა ხარ ის, ვინც უნდა მოსულიყო, თუ სხვას უნდა ველოდოთ?“",
			"20": "<sup>20</sup> მივიდნენ ისინი მასთან და უთხრეს: „იოანე ნათლისმცემელმა გამოგვგზავნა შენთან საკითხავად: შენა ხარ ის, ვინც უნდა მოსულიყო, თუ სხვას უნდა ველოდოთ?“",
			"21": "<sup>21</sup> იმჟამად მან ბევრი განკურნა სნეულებათა და სატკივართაგან და ავი სულებისაგან, და მრავალ ბრმას უწყალობა მხედველობა.",
			"22": "<sup>22</sup> მიუგო და უთხრა მათ: „წადით და უთხარით იოანეს, რაც ნახეთ და მოისმინეთ: ბრმებს მხედველობა უბრუნდებათ, ხეიბრები დადიან, კეთროვანები განიწმიდებიან, ყრუებს ესმით, მკვდრები დგებიან და ღატაკებს ეხარებათ.",
			"23": "<sup>23</sup> ნეტარია ვინც არ შეცდება ჩემში.“",
			"24": "<sup>24</sup> როცა იოანეს მოციქულები წავიდნენ, დაიწყო ლაპარაკი ხალხთან იოანეზე: „რის სანახავად გახვედით უდაბნოში? ქარისაგან შერხეული ლერწმისა?",
			"25": "<sup>25</sup> მაშ, რის სანახავად გახვედით – კაცისა, რომელსაც ფაფუკი სამოსელი აცვია? აჰა, ისინი, ვინც მდიდრულად არიან შემოსილნი და ფუფუნებით ცხოვრობენ, სამეფო კარზე იმყოფებიან.",
			"26": "<sup>26</sup> რიღას სანახავად წახვედით? წინასწარმეტყველისა? დიახ, გეუბნებით თქვენ, წინასწარმეტყველზე მეტისაც.",
			"27": "<sup>27</sup> ვინაიდან ეს არის ის, რომლის შესახებაც დაწერილია: ‘აჰა, მე ვგზავნი ჩემს ანგელოზს შენი სახის წინაშე, რომელიც განამზადებს შენს გზას შენს წინაშე.’",
			"28": "<sup>28</sup> ვინაიდან გეუბნებით თქვენ: დედაკაცთა ნაშობთა შორის არავინ არის იოანეზე დიდი, მაგრამ ღვთის სასუფეველში უმცირესიც კი მასზე უფრო დიდია.“",
			"29": "<sup>29</sup> მთელმა ხალხმა, რომელიც მას უსმენდა, და მებაჟეებმაც კი, განადიდეს ღმერთი და იოანეს ნათლობით მოინათლნენ.",
			"30": "<sup>30</sup> ხოლო ფარისევლებმა და რჯულის მცოდნეებმა უარყვეს ღვთის რჩევა მათ მიმართ და არ ინათლებოდნენ მისგან.",
			"31": "<sup>31</sup> მაშინ უფალმა თქვა: „ვის ვამსგავსო ეს მოდგმა და ვისი მსგავსნი არიან ისინი?",
			"32": "<sup>32</sup> ისინი ჰგვანან ყრმებს, რომლებიც მოედნებზე სხედან და ერთმანეთს გასძახიან: ‘სალამურს გიკრავდით და არ ცეკვავდით, გიგოდებდით და არ სტიროდით.’",
			"33": "<sup>33</sup> ვინაიდან მოვიდა იოანე ნათლისმცემელი და არც პურს ჭამს, არც ღვინოს სვამს და თქვენ ამბობთ: ეშმაკი ჰყავსო.",
			"34": "<sup>34</sup> მოვიდა კაცის ძე და ჭამს და სვამს და ამბობთ: აჰა კაცი, მჭამელი და ღვინის მსმელი, მებაჟეთა და ცოდვილთა მეგობარიო.",
			"35": "<sup>35</sup> გამართლებულია სიბრძნე მისი ყველა შვილის მიერ.“",
			"36": "<sup>36</sup> ვინმე ფარისეველთაგანი სთხოვდა მას, მასთან ეჭამა პური. შევიდა იმ ფარისევლის სახლში და მიუჯდა სუფრას.",
			"37": "<sup>37</sup> იმ ქალაქში იყო ცოდვილი დედაკაცი. როგორც კი გაიგო, იგი ფარისევლის სახლში ზისო, მოიტანა ნელსაცხებელი ალებასტრონის ჭურჭლით.",
			"38": "<sup>38</sup> და დადგა უკან, მის ფერხთით, ცრემლებით უსველებდა ფეხებს და თავისი თმებით უხოცავდა, ფეხებს უკოცნიდა და ნელსაცხებელს სცხებდა.",
			"39": "<sup>39</sup> ფარისეველმა, რომელმაც იესო მიიწვია, ამის დანახვაზე თავისთვის თქვა: „ეს რომ წინასწარმეტყველი იყოს, შეიტყობდა ვინ და რანაირი დედაკაცი ეხება მას, რადგან ცოდვილია იგი.“",
			"40": "<sup>40</sup> მიუგო იესომ და უთხრა მას: „სიმონ, რაღაც მაქვს შენთან სათქმელი,“ მან კი უთხრა: „თქვი, მოძღვარო.“",
			"41": "<sup>41</sup> თქვა: „ერთ მევახშეს ორი მოვალე ჰყავდა: ერთს ხუთასი დინარი ემართა, მეორეს – ორმოცდაათი.",
			"42": "<sup>42</sup> მაგრამ რაკი არაფერი ჰქონდათ, რომ გადაეხადათ, ორივეს მიუტევა. ახლა მითხარი, რომელს უფრო მეტად შეუყვარდება იგი?“",
			"43": "<sup>43</sup> მიუგო სიმონმა და თქვა: „მე ვფიქრობ, რომელსაც უფრო მეტი მიუტევა.“ ხოლო მან უთხრა მას: „სწორად განსაჯე.“",
			"44": "<sup>44</sup> მიბრუნდა დედაკაცისაკენ და სიმონს უთხრა: „ხომ ხედავ ამ დედაკაცს? შენს სახლში შემოვედი და წყალი არ მომეცი ფერხთათვის, ამან კი ცრემლით დამისველა ფერხნი და თავისი თმებით შემიხოცა.",
			"45": "<sup>45</sup> შენ არ გიკოცნია ჩემთვის, ამას კი, რაც აქ შემოვიდა, არ შეუწყვეტია ჩემს ფერხთა კოცნა.",
			"46": "<sup>46</sup> შენ ზეთი არ გიცხია ჩემს თავზე, ამან კი ნელსაცხებელი სცხო ჩემს ფერხთ.",
			"47": "<sup>47</sup> ამის გამო გეუბნები: მაგას მიეტევა მრავალი თავისი ცოდვა, რადგან ძლიერ შემიყვარა. ხოლო ვისაც ცოტა მიეტევა, ცოტათი უყვარს.“",
			"48": "<sup>48</sup> და უთხრა მას: „მიგეტევა შენი ცოდვანი.“",
			"49": "<sup>49</sup> მასთან მსხდომებმა იწყეს ლაპარაკი თავისთვის: „ვინ არის ეს, ცოდვებსაც რომ მიუტევებს?“",
			"50": "<sup>50</sup> მან კი უთხრა დედაკაცს: „შენმა რწმენამ გიხსნა შენ. წადი მშვიდობით.“"
		},
		"8": {
			"1": "<sup>1</sup> ამის შემდეგ დადიოდა ქალაქიდან ქალაქში და სოფლიდან სოფელში, ქადაგებდა და ახარებდა ღვთის სასუფეველს. და თორმეტნიც მასთან იყვნენ,",
			"2": "<sup>2</sup> ასევე ზოგი დედაკაციც, რომელნიც განიკურნენ ავი სულებისა და სნეულებებისაგან: მარიამი, მაგდალელად წოდებული, რომლისგანაც შვიდი ეშმაკი გავიდა;",
			"3": "<sup>3</sup> იოანა, ჰეროდეს მოურავის ქუზას ცოლი, სუსანა და ბევრი სხვა, რომელნიც ემსახურებოდნენ მას თავიანთი ქონებით.",
			"4": "<sup>4</sup> როცა დიდძალი ხალხი მოგროვდა მასთან და ქალაქის ყველა მცხოვრები მიეახლა, მან დაიწყო იგავით ლაპარაკი:",
			"5": "<sup>5</sup> „გამოვიდა მთესველი თავისი თესლის დასათესად. და თესვისას ზოგი გზის პირას დაცვივდა, გაითელა და ცის ფრინველებმა აკენკეს.",
			"6": "<sup>6</sup> ზოგი კლდოვანზე დაცვივდა, აღმოცენდა და გახმა, ვინაიდან სინოტივე აკლდა.",
			"7": "<sup>7</sup> ზოგი ეკლებში ჩაცვივდა, ეკალი გაიზარდა და გააჩანაგა.",
			"8": "<sup>8</sup> ზოგი კი კარგ ნიადაგზე დაცვივდა, აღმოცენდა და ასმაგი ნაყოფი გამოიღო.“ ეს თქვა და დაიძახა: „ვისაც ყურები აქვს სასმენად, ისმინოს!“",
			"9": "<sup>9</sup> ჰკითხეს მას მისმა მოწაფეებმა, რას ნიშნავდა ეს იგავი.",
			"10": "<sup>10</sup> მან თქვა: „თქვენ მოგეცათ, რომ იცოდეთ ღვთის სასუფევლის საიდუმლოებანი, ხოლო სხვებს იგავებით მიეცათ, რომ უყურებდნენ და ვერ ხედავდნენ, ისმენდნენ და ვერ იგებდნენ.",
			"11": "<sup>11</sup> ეს იგავი კი ამას ნიშნავს: ის თესლი ღვთის სიტყვაა.",
			"12": "<sup>12</sup> გზასთან დაცვენილები კი ისინი არიან, ვისაც ესმით, მერე მოდის ეშმაკი და მიაქვს სიტყვა მათი გულიდან, რომ არ იწამონ და არ გადარჩნენ.",
			"13": "<sup>13</sup> ესენი კი, კლდეზე რომ არიან, ისინია, ვინც მოისმენენ სიტყვას და სიხარულით მიიღებენ, მაგრამ ფესვი არა აქვთ და მხოლოდ მცირე ხანს სწამთ, განსაცდელის ჟამს კი განდგებიან.",
			"14": "<sup>14</sup> ხოლო ეკლებში ჩაცვენილნი ისინი არიან, ვინც ისმენენ, მაგრამ ცხოვრების გზაზე საზრუნავი, სიმდიდრე და განცხრომანი აჩანაგებენ მათ და ნაყოფი ვეღარ მწიფდება.",
			"15": "<sup>15</sup> ხოლო კარგ მიწაზე დაცვენილნი ისინი არიან, ვინც კეთილი და წრფელი გულით ისმენენ სიტყვას, იცავენ და მოთმინებით იძლევიან ნაყოფს.“",
			"16": "<sup>16</sup> „არავინ ანთებს სანთელს, რომ ჭურჭელი დაახუროს ზედ ან საწოლ ქვეშ შეედგას, არამედ სასანთლეზე დადებს, რათა შემომსვლელებმა იხილონ ნათელი.",
			"17": "<sup>17</sup> ვინაიდან არ არსებობს დაფარული, რომელიც ცხადი არ გახდეს, არც დამალული, რომელიც არ გამჟღავნდეს და არ გაცხადდეს.",
			"18": "<sup>18</sup> ამიტომ დაუკვირდით, როგორ ისმენთ: ვისაც აქვს, მას მიეცემა. ვისაც არა აქვს, ისიც წაერთმევა, რაც ჰგონია, რომ ექნება.“",
			"19": "<sup>19</sup> მივიდნენ მასთან მისი დედა და ძმები და ვერ შეძლეს ახლოს მისვლა ხალხის გამო.",
			"20": "<sup>20</sup> შეატყობინეს მას: დედაშენი და შენი ძმები გარეთ დგანან, შენი ნახვა სურთო.",
			"21": "<sup>21</sup> ხოლო მან მიუგო და უთხრა მათ: „დედაჩემი და ჩემი ძმები ისინი არიან, ვინც ღვთის სიტყვას ისმენენ და ასრულებენ.“",
			"22": "<sup>22</sup> მოხდა ისე, რომ ერთ დღეს იგი ჩაჯდა ნავში თავის მოწაფეებთან ერთად, და უთხრა მათ: „გადავიდეთ ტბის გაღმა,“ და გაცურეს.",
			"23": "<sup>23</sup> როცა მიცურავდნენ, მას ჩაეძინა, და ტბაზე ქარიშხალი ამოვარდა. ნავი წყლით ევსებოდათ და საფრთხეში იყვნენ.",
			"24": "<sup>24</sup> მივიდნენ, გააღვიძეს და უთხრეს: „მოძღვარო, მოძღვარო, ვიღუპებით.“ და მან, გაღვიძებულმა, შერისხა ქარი და წყლის ღელვა, და დაცხრნენ და ჩამოვარდა მყუდროება.",
			"25": "<sup>25</sup> უთხრა მათ: „სად არის თქვენი რწმენა?“ ისინი შიშითა და გაოცებით ეუბნებოდნენ ერთიმეორეს: „ვინ არის ეს, ქარებსაც რომ უბრძანებს და წყალსაც, და ემორჩილებიან მას?“",
			"26": "<sup>26</sup> გადაცურეს გერასენელთა ქვეყნისკენ, რომელიც გალილეის გაღმა მხარეს არის.",
			"27": "<sup>27</sup> როცა გავიდა ხმელეთზე, წინ შემოხვდა ერთი კაცი ქალაქიდან. მას ეშმაკები დაუფლებოდნენ დიდი ხანის წინ და არც სამოსელს იცვამდა და არც სახლში ცხოვრობდა, არამედ სამარხებში.",
			"28": "<sup>28</sup> როგორც კი დაინახა იესო, დაიკივლა, მის წინ დაეცა და ხმამაღლა იყვირა: „რა გინდა ჩემგან, იესო, ძეო მაღალი ღვთისა? გევედრები, ნუ მტანჯავ.“",
			"29": "<sup>29</sup> ვინაიდან უბრძანა არაწმიდა სულს ამ კაცისგან გამოსულიყო (ვინაიდან დიდ ხანს სტანჯავდა მას, ჰკრავდნენ ჯაჭვებით და ბორკილებით, რომ დაეკავებინათ იგი. მაგრამ ის წყვეტდა ბორკილებს და იდევნებოდა ეშმაკის მიერ უდაბნოებში).",
			"30": "<sup>30</sup> ჰკითხა მას იესომ: „რა არის შენი სახელი?“ მან უთხრა: „ლეგიონი,“ ვინაიდან ბევრი ეშმაკი იყო მასში შესული.",
			"31": "<sup>31</sup> და ევედრებოდნენ იესოს, რომ არ ებრძანებინა მათთვის უფსკრულში ჩასვლა.",
			"32": "<sup>32</sup> იქ იყო ღორების დიდი კოლტი, რომელიც მთაზე ძოვდა. ეშმაკები შეევედრნენ იესოს, რომ ნება დაერთო შესულიყვნენ მათში, და მან დართო ნება.",
			"33": "<sup>33</sup> გამოვიდნენ ეშმაკები იმ კაციდან და შევიდნენ ღორებში. კოლტი კბოდედან გადაეშვა ტბაში და ჩაიხრჩო.",
			"34": "<sup>34</sup> ეს ამბავი რომ დაინახეს მწყემსებმა, გაიქცნენ და შეატყობინეს ქალაქსა და სოფლებს.",
			"35": "<sup>35</sup> გამოვიდნენ მომხდარის სანახავად. მოვიდნენ იესოსთან და ნახეს კაცი, რომლისგანაც გავიდნენ ეშმაკები. იგი შემოსილი და გონსმოგებული იჯდა იესოს ფერხთით, და შეეშინდათ.",
			"36": "<sup>36</sup> მხილველებმა უამბეს, როგორ გადარჩა ეშმაკეული.",
			"37": "<sup>37</sup> ევედრებოდა გერასენელთა სანახების მთელი ხალხი, რათა გასცლოდა მათ, ვინაიდან დიდმა შიშმა მოიცვა ისინი. ისიც ჩაჯდა ნავში და გაბრუნდა.",
			"38": "<sup>38</sup> ის კაცი, რომლისგანაც ეშმაკები გავიდნენ, შეევედრა იესოს, თავისთან დაეტოვებინა, მაგრამ მან გაუშვა და უთხრა:",
			"39": "<sup>39</sup> „დაბრუნდი შინ და უამბე, რაც გიყო ღმერთმა.“ ისიც წავიდა და მთელ ქალაქს ამცნობდა, რა გაუკეთა იესომ.",
			"40": "<sup>40</sup> როცა იესო დაბრუნდა, ხალხმა მიიღო იგი, ვინაიდან ყველანი ელოდნენ მას.",
			"41": "<sup>41</sup> და აჰა, მოვიდა კაცი სახელად იაიროსი, რომელიც სინაგოგის მთავარი იყო. დაემხო იესოს ფერხთით და ევედრებოდა, შინ გაჰყოლოდა.",
			"42": "<sup>42</sup> ვინაიდან ერთადერთი ასული ჰყავდა, თორმეტი წლისა, და უკვდებოდა. იესო რომ მიდიოდა, ხალხი ზედ აწყდებოდა მას.",
			"43": "<sup>43</sup> იყო ერთი ქალი, რომელიც თორმეტი წელი სისხლის დენით იტანჯებოდა, მთელი ქონება ექიმებს დაახარჯა და ვერავინ განკურნა.",
			"44": "<sup>44</sup> უკნიდან მიეპარა და მისი ტანსაცმლის კალთას შეეხო. იმწამსვე შეუწყდა სისხლის დენა.",
			"45": "<sup>45</sup> თქვა იესომ: „ვინ შემეხო?“ როცა ყველამ იუარა, პეტრემ და მასთან მყოფებმა უთხრეს: „მოძღვარო, ხალხი გარს გახვევია და ზედ გაწყდება, შენ კი ამბობ, ვინ შემეხოო?“",
			"46": "<sup>46</sup> ხოლო იესომ თქვა: „ვიღაც შემეხო მე, რადგან ვგრძნობ, რომ ძალა გავიდა ჩემგან.“",
			"47": "<sup>47</sup> ქალმა, რაკი დაინახა, რომ ვერ დაიმალა, მიუახლოვდა ძრწოლით, დაემხო მის წინაშე და საჯაროდ განაცხადა, რა მიზეზითაც შეეხო და როგორ უცბად განიკურნა.",
			"48": "<sup>48</sup> უთხრა მას: „ასულო, შენმა რწმენამ გადაგარჩინა. წადი მშვიდობით!“",
			"49": "<sup>49</sup> ჯერ სიტყვა არ დაემთავრებინა, რომ ვიღაც მოვიდა სინაგოგის მთავრის სახლიდან და უთხრა: „შენი ასული მოკვდა. ნუღარ შეაწუხებ მოძღვარს.“",
			"50": "<sup>50</sup> იესომ, ეს რომ მოისმინა, მიუგო მას: „ნუ გეშინია, მხოლოდ ირწმუნე და განიკურნება.“",
			"51": "<sup>51</sup> სახლში რომ მივიდნენ, არავის დართო ნება შესულიყო, პეტრეს, იაკობის, იოანეს და გოგონას დედ-მამის გარდა.",
			"52": "<sup>52</sup> ყველა მოთქვამდა და დასტიროდა მას, მან კი უთხრა: „ნუ სტირით. ის არ მომკვდარა, სძინავს მხოლოდ.“",
			"53": "<sup>53</sup> ისინი კი დასცინოდნენ მას, ვინაიდან იცოდნენ, რომ მკვდარი იყო.",
			"54": "<sup>54</sup> ხელი ხელში ჩასჭიდა, შესძახა და თქვა: „გოგონავ, ადექ!“",
			"55": "<sup>55</sup> და დაუბრუნდა სული. უცებ წამოდგა იგი და მან ბრძანა, საჭმელი მიეცათ გოგონასთვის.",
			"56": "<sup>56</sup> და გაუკვირდათ მის მშობლებს. მან უბრძანა მათ, არავისთვის ეთქვათ მომხდარის შესახებ."
		},
		"9": {
			"1": "<sup>1</sup> მოუხმო თორმეტს, მისცა მათ ძალა და ხელმწიფება ყველა ეშმაკზე, რომ განეკურნათ სნეულებანი.",
			"2": "<sup>2</sup> მიავლინა ისინი ღვთის სასუფევლის საქადაგებლად და სნეულთა განსაკურნავად.",
			"3": "<sup>3</sup> უთხრა მათ: „არაფერი წაიღოთ გზაში, არც ხელჯოხი, არც გუდა, არც პური, არც ვერცხლი და არც ორი მოსასხამი იქონიოთ.",
			"4": "<sup>4</sup> რომელ სახლშიც შეხვალთ, იქ დარჩით და იქიდანვე გაემგზავრეთ.",
			"5": "<sup>5</sup> ხოლო თუ სადმე არ მიგიღონ, იმ ქალაქიდან გამოსვლისას თქვენს ფერხთაგან მტვერი ჩამოიბერტყეთ მათ წინააღმდეგ დასამოწმებლად.“",
			"6": "<sup>6</sup> წავიდნენ და იწყეს სოფელ-სოფელ სიარული, ახარებდნენ და ყველგან კურნავდნენ.",
			"7": "<sup>7</sup> მოისმინა ჰეროდემ – ტეტრარქმა, ყოველი მისი ნამოქმედარი, და გაოგნდა, ვინაიდან ერთნი იმას ამბობდნენ: იოანე აღდგაო მკვდრეთით,",
			"8": "<sup>8</sup> მეორენი – ელია გამოჩნდაო. სხვები ამბობდნენ: ერთი ძველი წინასწარმეტყველთაგანი აღდგაო მკვდრეთით.",
			"9": "<sup>9</sup> ჰეროდემ თქვა: „იოანეს მე მოვკვეთე თავი, ეს კი ვინ არის, ვისზეც ასეთი რამ მესმის?“ და უნდოდა მისი ნახვა.",
			"10": "<sup>10</sup> დაბრუნდნენ მოციქულები და უამბეს მას, რაც გააკეთეს. მან წაიყვანა ისინი და განმარტოვდა, ბეთსაიდად წოდებული ქალაქისაკენ.",
			"11": "<sup>11</sup> შეიტყო დიდძალმა ხალხმა და გაჰყვა მას. ის ღებულობდა მათ და ელაპარაკებოდა ღვთის სასუფეველზე, ხოლო, ვისაც განკურნება ესაჭიროებოდა, კურნავდა მათ.",
			"12": "<sup>12</sup> როცა დღემ იწყო გადახრა, მივიდნენ მასთან თორმეტნი და უთხრეს: „გაუშვი ეს ხალხი, რომ წავიდნენ გარემო სოფლებსა და დაბებში ღამის გასათევად და საზრდოს საშოვნელად, ვინაიდან აქ უდაბურ ადგილას ვიმყოფებით.“",
			"13": "<sup>13</sup> უთხრა მათ: „მიეცით საჭმელი.“ მათ უთხრეს: „ხუთი პურისა და ორი თევზის მეტი არაფერი გაგვაჩნია, თუ არ წავალთ და საჭმელს არ ვუყიდით მთელ ამ ხალხს.“",
			"14": "<sup>14</sup> რადგან იქ ხუთი ათასამდე კაცი იქნებოდა. მან უთხრა თავის მოწაფეებს: „დასხით ისინი ჯგუფებად, თითოში ორმოცდაათამდე იყოს.“",
			"15": "<sup>15</sup> ასე მოიქცნენ და დასხეს ყველანი.",
			"16": "<sup>16</sup> მან აიღო ხუთი პური და ორი თევზი, ზეცას ახედა, აკურთხა, დატეხა და მისცა მოწაფეებს, რომ ჩამოერიგებინათ ხალხისთვის.",
			"17": "<sup>17</sup> ყველამ ჭამა და გაძღა, ხოლო დარჩენილი ნატეხებით თორმეტი გოდორი გაავსეს.",
			"18": "<sup>18</sup> ერთხელ, როცა განმარტოებული ლოცულობდა და მოწაფეებიც მასთან იყვნენ, იესომ ჰკითხა მათ: „რას ამბობს ხალხი, ვინა ვარ მე?“",
			"19": "<sup>19</sup> მათ მიუგეს და თქვეს: იოანე ნათლისმცემელიო; სხვები – ელიაო, სხვები კი – ვინმე ძველი, აღმდგარი წინასწარმეტყველიო.",
			"20": "<sup>20</sup> იესომ უთხრა: „თქვენ კი რაღას ამბობთ: ვინა ვარ მე?“ პეტრემ პასუხად უთხრა: „ღვთის ცხებული.“",
			"21": "<sup>21</sup> მაგრამ მან აუკრძალა მათ. უბრძანა, არავისთვის გაემხილათ ეს.",
			"22": "<sup>22</sup> უთხრა მათ: „კაცის ძის ხვედრია, მრავალი ტანჯვა აიტანოს და უარყოფილ იქნეს უხუცესთა, მღვდელმთავართა და მწიგნობართა მიერ, მოკლულ იქნეს და მესამე დღეს აღდგეს.“",
			"23": "<sup>23</sup> ყველას გასაგონად კი ასე თქვა: „თუ ვინმეს სურს გამომყვეს, უარყოს თავისი თავი, ყოველდღე იტვირთავდეს თავის ჯვარს და მომყვებოდეს.",
			"24": "<sup>24</sup> ვინაიდან, ვისაც სურს თავისი სული იხსნას, იგი დაღუპავს მას. ხოლო ვინც ჩემი გულისთვის დაღუპავს მას, იგი იხსნის მას.",
			"25": "<sup>25</sup> რადგან რას არგებს ადამიანს, თუ მთელს სოფელს მოიგებს, თავის თავს კი დაღუპავს ან ავნებს?",
			"26": "<sup>26</sup> რადგან, ვისაც შერცხვება ჩემი და ჩემი სიტყვებისა, კაცის ძესაც შერცხვება მისი, როცა მოვა თავისი მამისა და წმიდა ანგელოზების დიდებით.",
			"27": "<sup>27</sup> ჭეშმარიტად გეუბნებით თქვენ: არიან ზოგიერთები აქ მდგომთაგან, რომლებიც არ იგემებენ სიკვდილს, რომ უკვე იხილავენ ღვთის სასუფეველს.“",
			"28": "<sup>28</sup> ამ სიტყვებიდან რვა დღის შემდეგ პეტრე, იოანე და იაკობი წაიყოლა თან და მთაზე ავიდა სალოცავად.",
			"29": "<sup>29</sup> როცა ლოცულობდა, მისი სახის იერი შეიცვალა და მისი სამოსელი თეთრად მოქათქათე შეიქნა.",
			"30": "<sup>30</sup> და აჰა, მასთან საუბრობდა ორი კაცი, ესენი იყვნენ მოსე და ელია.",
			"31": "<sup>31</sup> ისინი გამოჩნდნენ დიდებით და ლაპარაკობდნენ მის წასვლაზე, რომელიც იერუსალიმში უნდა აღსრულებულიყო.",
			"32": "<sup>32</sup> პეტრე და მასთან მყოფნი დამძიმებულნი იყვნენ ძილით და, როცა გაეღვიძათ, იხილეს მისი დიდება და მასთან მდგომარე ორი კაცი.",
			"33": "<sup>33</sup> ხოლო როცა ისინი შორდებოდნენ მას, პეტრემ უთხრა იესოს: „მოძღვარო, რა კარგია ჩვენთვის აქ ყოფნა. გავაკეთებთ აქ სამ კარავს: ერთი შენ, ერთი მოსეს და ერთი ელიას.“ ვინაიდან არ იცოდა, რას ლაპარაკობდა.",
			"34": "<sup>34</sup> ამას რომ ამბობდა, გამოჩნდა ღრუბელი და მიუჩრდილა მათ. ღრუბელში რომ შევიდნენ, შეეშინდათ.",
			"35": "<sup>35</sup> ღრუბლიდან გაისმა ხმა, რომელიც ამბობდა: „ეს არის ძე ჩემი საყვარელი, მას უსმინეთ.“",
			"36": "<sup>36</sup> ეს ხმა რომ გაისმა, იესო მარტო დარჩა, ისინი კი დადუმდნენ და იმ დღეებში არავისთვის უთქვამთ, რაც იხილეს.",
			"37": "<sup>37</sup> მეორე დღეს, როცა მთიდან ჩამოვიდნენ, ბევრი ხალხი შეეგება მას.",
			"38": "<sup>38</sup> და აჰა, იმ ხალხში ვიღაც კაცმა შეჰყვირა: „მოძღვარო, გევედრები, მოხედე ჩემს ძეს, რადგან ეს ერთადერთი მყავს.",
			"39": "<sup>39</sup> სულსა ჰყავს შეპყრობილი; უეცრად წამოაყვირებს, აქეთ-იქით ახეთქებს მას, დუჟს ადენს, და როცა დააოსებს, ძლივსძლივობით გაეცლება.",
			"40": "<sup>40</sup> ვთხოვე შენს მოწაფეებს, რომ გამოედევნათ, მაგრამ ვერ შეძლეს.“",
			"41": "<sup>41</sup> მიუგო იესომ და თქვა: „ჰოი, ურწმუნო და უკუღმართო მოდგმავ, როდემდე ვიყო თქვენთან და გითმინოთ? აქ მომიყვანე შენი ძე.“",
			"42": "<sup>42</sup> ვიდრე მოვიდოდა, ეშმაკმა დაახეთქა და დაუწყო კრუნჩხვა. იესომ შერისხა არაწმიდა სული, განკურნა ყრმა და ჩააბარა თავის მამას.",
			"43": "<sup>43</sup> ყველანი გაოცებულნი იყვნენ ღვთის სიდიადით. როცა ყველას უკვირდა ყოველივე, რასაც ის აკეთებდა, უთხრა თავის მოწაფეებს:",
			"44": "<sup>44</sup> „ყურად იღეთ ეს სიტყვები: ძე კაცისა გადაეცემა კაცთა ხელში.“",
			"45": "<sup>45</sup> მაგრამ ისინი ვერ მიხვდნენ ამ ნათქვამს, და იგი დაფარული იყო მათთვის, ისე რომ ვერ ჩასწვდნენ მას. და ეშინოდათ, ეკითხათ მისთვის ამ სიტყვების გამო.",
			"46": "<sup>46</sup> ერთი ფიქრი კი დაებადათ: ვინ უფრო დიდი იყო მათ შორის?",
			"47": "<sup>47</sup> იესომ, რომელიც ხვდებოდა მათი გულის ზრახვებს, მოიყვანა ბავშვი და თავის წინ დააყენა.",
			"48": "<sup>48</sup> უთხრა მათ: „ვინც მიიღებს ამ ბავშვს ჩემი სახელით, ის მე მიმიღებს. ხოლო ვინც მე მიმიღებს, ის ჩემს მომავლინებელსაც მიიღებს. ხოლო ვინც თქვენს შორის უმცირესია, ის არის დიდი.“",
			"49": "<sup>49</sup> მიუგო იოანემ და თქვა: „მოძღვარო, ჩვენ ვნახეთ კაცი, რომელიც შენი სახელით ეშმაკებს დევნის, და ჩვენ დავუშალეთ მას, რადგან არ დაგვყვება ჩვენ.“",
			"50": "<sup>50</sup> უთხრა მას იესომ: „ნუ დაუშლით, ვინაიდან ის, ვინც თქვენს წინააღმდეგ არ არის, ის თქვენთანაა.“",
			"51": "<sup>51</sup> როცა მოახლოვდა მისი ამაღლების დღეები, იბრუნა პირი იერუსალიმში ასასვლელად.",
			"52": "<sup>52</sup> წინდაწინ მიავლინა მაცნეები. და ისინი წავიდნენ და შევიდნენ სამარიელთა სოფელში, რათა გაემზადებინათ მისთვის.",
			"53": "<sup>53</sup> მაგრამ იქ არ მიიღეს იგი, ვინაიდან პირი მიქცეული ჰქონდა იერუსალიმში წასასვლელად.",
			"54": "<sup>54</sup> დაინახეს ეს მისმა მოწაფეებმა, იაკობმა და იოანემ და უთხრეს: „უფალო, თუ გინდა, ვიტყვით, რომ ცეცხლი ჩამოვიდეს ციდან და მოსპოს ისინი, როგორც ელიამ გააკეთა.“",
			"55": "<sup>55</sup> მაგრამ ის მიბრუნდა მათკენ, გაუწყრა და უთხრა: „არ იცით, ვისი სულისანი ხართ?",
			"56": "<sup>56</sup> ვინაიდან ძე კაცისა მოვიდა არა ადამიანთა სულების დასაღუპავად, არამედ სახსნელად!“ და წავიდნენ სხვა სოფელში.",
			"57": "<sup>57</sup> გზაზე რომ მიდიოდნენ, ვიღაცამ უთხრა მას: „გამოგვყვები, სადაც კი წახვალ.“",
			"58": "<sup>58</sup> იესომ უთხრა მას: „მელიებს სოროები აქვთ და ცის ფრინველთ – ბუდეები; კაცის ძეს კი არა აქვს თავის მისადრეკი.“",
			"59": "<sup>59</sup> მეორეს კი უთხრა: „გამომყევი.“ მან თქვა: „უფალო, ნება მომეცი, ჯერ წავიდე და მამაჩემი დავმარხო.“",
			"60": "<sup>60</sup> მან კი უთხრა მას: „დაანებე მკვდრებს თავიანთი მკვდრების დამარხვა. შენ კი წადი და იქადაგე ღვთის სასუფეველი.“",
			"61": "<sup>61</sup> სხვამაც თქვა: „მე გამოგყვები, უფალო, ოღონდ ჯერ ნება მომეცი, ჩემს შინაურებს დავემშვიდობო.“",
			"62": "<sup>62</sup> უთხრა მას იესომ: „არავინ გამოდგება ღვთის სასუფევლისათვის, ვისაც გუთანი უჭირავს ხელში და უკან იყურება.“"
		},
		"10": {
			"1": "<sup>1</sup> ამის შემდეგ ამოარჩია უფალმა სხვა სამოცდაათი და წინასწარ მიავლინა ორ-ორი ყოველ ქალაქსა და სოფელში, სადაც მისვლას აპირებდა.",
			"2": "<sup>2</sup> უთხრა მათ: „სამკალი ბევრია, მუშაკი – ცოტა. ამიტომ ევედრეთ სამკლის პატრონს, რომ გამოგზავნოს მუშაკნი თავის სამკალში.",
			"3": "<sup>3</sup> წადით, აჰა გაგზავნით თქვენ, როგორც კრავებს მგლებს შორის.",
			"4": "<sup>4</sup> არც ქისა წაიღოთ თან, არც გუდა, არც ფეხსაცმელი, არც არავინ მოიკითხოთ გზაში.",
			"5": "<sup>5</sup> რომელ სახლშიც შეხვიდეთ, უწინ ეს სთქვით: მშვიდობა ამ სახლს!",
			"6": "<sup>6</sup> თუ იქნება იქ მშვიდობის ძე, მასზე დაივანებს თქვენი მშვიდობა. თუ არა და, თქვენვე დაგიბრუნდებათ.",
			"7": "<sup>7</sup> იმავე სახლში დარჩით, ჭამეთ და სვით, რაც მათ აქვთ, ვინაიდან მუშაკი ღირსია თავისი საზღაურისა. ერთი სახლიდან მეორე სახლში ნუ გადახვალთ.",
			"8": "<sup>8</sup> რომელ ქალაქშიც მიხვალთ და მიგიღებენ, ჭამეთ, რაც შემოგთავაზონ.",
			"9": "<sup>9</sup> განკურნეთ იქ მყოფი სნეულნი და უთხარით: ‘ღვთის სასუფეველი მოგიახლოვდათ.’",
			"10": "<sup>10</sup> ხოლო თუ რომელიმე ქალაქში შესვლისას არ მიგიღებენ, ქუჩაში გამოსვლისას თქვით:",
			"11": "<sup>11</sup> ‘თქვენი ქალაქის მტვერს, რომელიც ჩვენს ფეხებს მიეკრო, ვიბერტყავთ თქვენს მიმართ. ოღონდ ესეც იცოდეთ, რომ გიახლოვდებათ ღვთის სასუფეველი.’",
			"12": "<sup>12</sup> გეუბნებით, სოდომს უფრო გაუადვილდება იმ დღეს, ვიდრე იმ ქალაქს.",
			"13": "<sup>13</sup> ვაი შენ, ქორაზინო, და ვაი შენ, ბეთსაიდავ! ვინაიდან ტვიროსსა და ციდონში რომ მომხდარიყო ძალთაქმედებანი, რომელნიც თქვენთან მოხდა, ისინი დიდი ხნის წინ ძაძებსა და ნაცარში მსხდომარენი მოინანიებდნენ.",
			"14": "<sup>14</sup> მაგრამ ტვიროსსა და ციდონს უფრო გაუადვილდება განკითხვისას, ვიდრე თქვენ.",
			"15": "<sup>15</sup> შენ კი, კაპერნაუმო, განა ცამდე ამაღლდები? ჯოჯოხეთამდე დაეშვები!",
			"16": "<sup>16</sup> ვინც თქვენ მოგისმენთ, მეც მომისმენს; თქვენი უარმყოფელი მეც უარმყოფს. ჩემი უარმყოფელი უარყოფს ჩემს მომავლინებელს!“",
			"17": "<sup>17</sup> სიხარულით დაბრუნდნენ სამოცდაათნი და უთხრეს: „უფალო, შენი სახელით ეშმაკებიც კი გვემორჩილებიან.“",
			"18": "<sup>18</sup> მან კი უთხრა მათ: „დავინახე სატანა, ელვასავით ციდან გადმოვარდნილი.",
			"19": "<sup>19</sup> აჰა, გაძლევთ თქვენ ხელმწიფებას გველთა და მორიელთა დასათრგუნავად, და მტრის ყოველი ძალისა. თქვენ კი არაფერი გევნებათ.",
			"20": "<sup>20</sup> ოღონდ ნუ ხარობთ, რომ სულები გემორჩილებიან. არამედ გიხაროდეთ, რომ თქვენი სახელები ცაშია ჩაწერილი.“",
			"21": "<sup>21</sup> იმ დროს სულში გაიხარა იესომ და თქვა: „გადიდებ შენ, მამაო, უფალო ცისა და მიწისა, რადგან დაუფარე ეს ბრძენთა და გონიერთ, ჩვილებს კი განუცხადე. ჰეი, მამაო, ვინაიდან ასე იყო მოსაწონი შენს წინაშე.",
			"22": "<sup>22</sup> ყოველივე ჩემი მამისგან გადმომეცა, ხოლო არავინ იცის, ვინ არის მამა, გარდა ძისა. და არავინ იცის ვინ არის ძე, გარდა მამისა, და იმისა, ვისაც ძეს სურს, რომ განუცხადოს.“",
			"23": "<sup>23</sup> მიუბრუნდა მოწაფეებს ცალკე და უთხრა: „ნეტარია თვალები, რომელნიც ხედავენ, რასაც თქვენ ხედავთ.",
			"24": "<sup>24</sup> რადგან გეტყვით, რომ მრავალ წინასწარმეტყველსა და მეფეს სურდა ენახა, რასაც თქვენ ხედავთ, მაგრამ ვერ იხილა, და ესმინა, რასაც თქვენ ისმენთ, და ვერ მოისმინა.“",
			"25": "<sup>25</sup> აჰა, ერთი რჯულის მცოდნე, წამოდგა და გამოსაცდელად თქვა: „რა უნდა გავაკეთო, მოძღვარო, რომ საუკუნო სიცოცხლე დავიმკვიდრო?“",
			"26": "<sup>26</sup> მან უთხრა მას: „რჯულში როგორ სწერია? შენ როგორ კითხულობ?“",
			"27": "<sup>27</sup> მან მიუგო და უთხრა: „შეიყვარე უფალი, ღმერთი შენი, მთელი შენი გულით, მთელი შენი სულით, მთელი შენი ძალითა და მთელი შენი გონებით, და მოყვასი შენი, როგორც თავი შენი.“",
			"28": "<sup>28</sup> უთხრა მას: „სწორად მიპასუხე. ეგრე მოიქეცი და იცოცხლებ.“",
			"29": "<sup>29</sup> მაგრამ მას თავის გამართლება უნდოდა და უთხრა იესოს: „ვინ არის ჩემი მოყვასი?“",
			"30": "<sup>30</sup> უპასუხა იესომ და უთხრა: „ერთი კაცი მიდიოდა იერუსალიმიდან იერიხოს და დაესხნენ ყაჩაღები, ტანსაცმელი გახადეს, დაჭრეს და წავიდნენ. ის კი ცოცხალ-მკვდარი მიატოვეს.",
			"31": "<sup>31</sup> შემთხვევით ერთმა მღვდელმა ჩაიარა იმ გზაზე, დაინახა იგი და გვერდი აუქცია.",
			"32": "<sup>32</sup> ასევე ლევიანმა ჩაიარა იმ ადგილას, დაინახა და გვერდი აუქცია.",
			"33": "<sup>33</sup> მერე ვიღაც სამარიელმა გამოიარა, დაინახა და შეიცოდა.",
			"34": "<sup>34</sup> მივიდა მასთან, ჭრილობები შეუხვია, ზეთი და ღვინო დაასხა, შესვა თავის პირუტყვზე, მიიყვანა სასტუმროში და იზრუნა მასზე.",
			"35": "<sup>35</sup> მეორე დღეს ამოიღო ორი დინარი, მისცა სასტუმროს პატრონს და უთხრა: იზრუნე მასზე და, თუ ზედმეტი ხარჯი მოგივა, რომ დავბრუნდები, მოგცემო.",
			"36": "<sup>36</sup> რას ფიქრობ, იმ სამიდან რომელი უფრო იყო ყაჩაღების ხელში ჩავარდნილის მოყვასი?“",
			"37": "<sup>37</sup> მან თქვა: „რომელმაც წყალობა უყო მას.“ უთხრა მას იესომ: „წადი და შენც ასევე მოიქეცი.“",
			"38": "<sup>38</sup> განაგრძეს გზა და შევიდა იგი ერთ სოფელში და ერთმა დედაკაცმა, სახელად მართამ, მიიწვია იგი სახლში.",
			"39": "<sup>39</sup> მას ჰყავდა და, სახელად მარიამი, რომელიც იესოს ფერხთით დაჯდა და ისმენდა მის სიტყვას.",
			"40": "<sup>40</sup> მართა კი ზრუნავდა გულუხვად გამასპინძლებაზე. მივიდა მასთან და უთხრა: „უფალო, არ განაღვლებს, რომ ჩემმა დამ მარტოდ დამტოვა სამასპინძლოდ? უთხარი, მომეხმაროს.“",
			"41": "<sup>41</sup> და უთხრა მას უფალმა პასუხად: „მართა, მართა! შენ ბევრ რამეზე ზრუნავ და წუხხარ,",
			"42": "<sup>42</sup> საჭირო კი მხოლოდ ერთია. მარიამმა უკეთესი წილი ამოირჩია, რომელიც არ წაერთმევა მას.“"
		},
		"11": {
			"1": "<sup>1</sup> ერთხელ, როცა იგი ერთ ადგილას ლოცულობდა, და დაასრულა ლოცვა, ერთმა მოწაფეთაგანმა მიმართა: „უფალო, გვასწავლე ლოცვა, როგორც იოანე ასწავლიდა თავის მოწაფეებს.“",
			"2": "<sup>2</sup> უთხრა მათ: „როცა ლოცულობთ, თქვით: მამაო ჩვენო, რომელი ხარ ცათა შინა, წმიდა იყოს სახელი შენი, მოვიდეს სუფევა შენი, იყოს ნება შენი, ვითარცა ცათა შინა, ეგრეცა ქვეყანასა ზედა.",
			"3": "<sup>3</sup> პური ჩვენი არსობისა მოგვეც ჩვენ დღითი დღე.",
			"4": "<sup>4</sup> და მოგვიტევე ჩვენი ცოდვები, რადგან ჩვენც მივუტევებთ ყველა ჩვენს თანამდებს და ნუ შეგვიყვან ჩვენ განსაცდელში.“",
			"5": "<sup>5</sup> და უთხრა მათ: „რომელიმე თქვენგანს რომ მეგობარი ჰყავდეს და შუაღამისას მიადგეს და უთხრას: ‘მეგობარო, მასესხე სამი პური.",
			"6": "<sup>6</sup> ვინაიდან მეგობარი მომივიდა ნამგზავრი და არაფერი გამაჩნია, წინ რომ დავუდო.’",
			"7": "<sup>7</sup> და მან შიგნიდან გამოსძახოს: ‘ნუ მაწუხებ, კარი დაკეტილია, ბავშვებიც ლოგინში მიწვანან, არ შემიძლია წამოდგომა და პურის მოცემა.’",
			"8": "<sup>8</sup> გეუბნებით თქვენ: თუ არ ადგება და მეგობრობის გამო არ მისცემს პურს, მისი აბეზრობის გამო მაინც ადგება და მისცემს იმდენ პურს, რამდენიც სჭირდება.",
			"9": "<sup>9</sup> და მე გეუბნებით თქვენ: ითხოვეთ და მოგეცემათ. ეძიეთ და იპოვით. დარეკეთ და გაგეღებათ.",
			"10": "<sup>10</sup> ვინაიდან ყველა, ვინც ითხოვს, ღებულობს, და ვინც ეძიებს, პოულობს, და ვინც რეკს, გაუღებენ.",
			"11": "<sup>11</sup> რომელი მამა იქნება თქვენში, რომ შვილმა პური სთხოვოს და ქვა მისცეს? ანდა თევზი სთხოვოს და თევზის წილ გველი მისცეს?",
			"12": "<sup>12</sup> ანდა, კვერცხი რომ სთხოვოს, მორიელი მისცეს?",
			"13": "<sup>13</sup> ჰოდა, თუ თქვენ, ბოროტებმა, იცით კეთილ მისაცემთა მიცემა თქვენი შვილებისათვის, მეტადრე ზეციერი მამა მისცემს სულიწმიდას იმათ, ვინც სთხოვს.“",
			"14": "<sup>14</sup> დევნიდა იგი ეშმაკს კაცისაგან რომელიც მუნჯი იყო. და, როცა ეშმაკი გავიდა, მუნჯი ალაპარაკდა და გაოცდა ხალხი.",
			"15": "<sup>15</sup> ზოგიერთი ამბობდა: ბელზებულით, ეშმაკთა მთავრით, დევნისო ეშმაკებს.",
			"16": "<sup>16</sup> ზოგი კი გამოსაცდელად მოითხოვდა მისგან ნიშანს ციდან.",
			"17": "<sup>17</sup> ხოლო მან იცოდა მათი ზრახვები და უთხრა მათ: „ყოველი სამეფო, რომელიც თავისავე წინააღმდეგაა გაყოფილი, გაპარტახდება და სახლი სახლზე დაემხობა.",
			"18": "<sup>18</sup> და თუ სატანა გაყოფილია თავისავე წინააღმდეგ, როგორღა გაძლებს მისი სამეფო? თქვენ კი ამბობთ, ვითომც მე ბელზებულით ვდევნიდე ეშმაკებს.",
			"19": "<sup>19</sup> თუ მე ბელზებულით ვდევნი ეშმაკებს, თქვენი შვილები ვიღათი დევნიან? ამიტომ ისინი იქნებიან თქვენი მსაჯულები.",
			"20": "<sup>20</sup> და თუ მე ღვთის თითით ვდევნი ეშმაკებს, აბა, დაგდგომიათ ღვთის სასუფეველი.",
			"21": "<sup>21</sup> როცა ძლიერი კაცი იარაღით იცავს თავის კარმიდამოს, მაშინ მისი ქონება მშვიდობით არის.",
			"22": "<sup>22</sup> ხოლო, როცა მასზე ძლიერი თავს დაესხმის და დაამარცხებს, წაართმევს იარაღს, რომლის იმედიცა ჰქონდა, და ნადავლს დაარიგებს.",
			"23": "<sup>23</sup> ვინც ჩემთან არ არის, ის ჩემს წინააღმდეგაა, და ვინც ჩემთან არ აგროვებს, ის ფანტავს.",
			"24": "<sup>24</sup> როცა უწმიდური სული გამოვა ადამიანისგან, დადის ურწყულებში, ეძებს სიმშვიდეს, მაგრამ ვერ პოულობს. მაშინ ამბობს: დავბრუნდები ჩემს სახლში, საიდანაც გამოვედიო.",
			"25": "<sup>25</sup> მივა და იპოვის მას, დაგვილსა და მოვლილს.",
			"26": "<sup>26</sup> მაშინ წავა და მოიყვანს შვიდ სხვა სულს, თავისზე უბოროტესს, შევლენ და დასახლდებიან იქ. და იმ კაცისთვის უკანასკნელი პირველზე უარესი იქნება.“",
			"27": "<sup>27</sup> სანამ ის ამას ლაპარაკობდა, ერთმა დედაკაცმა ხმა აღიმაღლა ხალხიდან და წარმოთქვა: „ნეტარია მუცელი, რომელმაც შენ გატარა და ძუძუნი, რომელთაც სწოვდი.“",
			"28": "<sup>28</sup> მან კი თქვა: „ნეტარ არიან ისინი, ვინც მოისმენენ და დაიცავენ ღვთის სიტყვას.“",
			"29": "<sup>29</sup> როცა ხალხი მოაწყდა, და იწყო, მან ლაპარაკი: „ეს მოდგმა ბოროტი მოდგმაა: ითხოვს ნიშანს და ნიშანი არ მიეცემა მას, გარდა იონას ნიშნისა;",
			"30": "<sup>30</sup> ვინაიდან, როგორც იონა იყო ნიშანი ნინეველთათვის, ასევე იქნება ამ მოდგმისათვის ძე კაცისა.",
			"31": "<sup>31</sup> სამხრეთის დედოფალი წამოდგება სამსჯავროზე ამ მოდგმის ადამიანებთან ერთად და მსჯავრს დასდებს მათ, ვინაიდან იგი დედამიწის კიდიდან მოვიდა სოლომონის სიბრძნის მოსასმენად და აჰა, აქ არის სოლომონზე დიდი.",
			"32": "<sup>32</sup> ნინეველები აღდგებიან სამსჯავროზე ამ მოდგმასთან ერთად და მსჯავრს დასდებენ მას, ვინაიდან მათ მოინანიეს იონას ქადაგებით და, აჰა, აქ არის იონაზე დიდი.“",
			"33": "<sup>33</sup> „არავინ ანთებს სანთელს, რომ დადგას მოფარებულში ანდა ჭურჭლის ქვეშ, არამედ სასანთლეზე, რათა შემოსულები ხედავდნენ ნათელს.",
			"34": "<sup>34</sup> სხეულის სანთელი შენი თვალია. როცა შენი თვალი სუფთაა, მთელი შენი სხეულიც ნათელია. მაგრამ როცა იგი ბოროტია, შენი სხეულიც ბნელია.",
			"35": "<sup>35</sup> ამიტომ უყურე: ნათელი, რომელიც შენშია, ბნელი ხომ არ არის?",
			"36": "<sup>36</sup> თუ კი მთელი შენი სხეული ნათელია და ბნელი არაფერი აქვს, მთლიანად ნათელი იქნება იგი, როგორც სანთელი ელვარებით გინათებდეს.“",
			"37": "<sup>37</sup> ამას რომ ლაპარაკობდა, ერთმა ფარისეველმა სთხოვა, მასთან ეჭამა პური. შევიდა და სუფრას მიუჯდა.",
			"38": "<sup>38</sup> ფარისეველს გაუკვირდა, როცა დაინახა, რომ მან არ დაიბანა ჭამის წინ.",
			"39": "<sup>39</sup> უთხრა მას უფალმა: „ახლა თქვენ, ფარისეველნო, სასმისსა და თეფშს გარედან ასუფთავებთ, თქვენი შინაგანი კი სავსეა მტაცებლობით და ბოროტებით.",
			"40": "<sup>40</sup> უგუნურნო, განა გარეგანის შემქმნელმა არ შექმნა შინაგანიც?",
			"41": "<sup>41</sup> არამედ გაეცით მოწყალება, რაც შიგნითაა, და აჰა, ყველაფერი სუფთა გექნებათ.",
			"42": "<sup>42</sup> „მაგრამ, ვაი თქვენ, ფარისეველნო, რომ გამოჰყოფთ პიტნის, ტეგანისა და ყოველნაირი ბოსტნეულის მეათედს და გვერდს უქცევთ ღვთის სამართალსა და სიყვარულს! ეს უნდა გაგეკეთებინათ და არც ის უნდა დაგეტოვებინათ.",
			"43": "<sup>43</sup> ვაი თქვენ, ფარისეველნო, რომ გიყვართ წინა ადგილზე ჯდომა სინაგოგებში და მოსალმებანი მოედნებზე!",
			"44": "<sup>44</sup> ვაი თქვენ, რადგან ხართ უჩინარი სამარხებივით, რომლებზეც ადამიანები დადიან და არ კი იციან!“",
			"45": "<sup>45</sup> ამაზე ერთ-ერთმა რჯულის მცოდნემ უთხრა: „მოძღვარო, ამას რომ ამბობ, ამით ჩვენც გვაყენებ შეურაცხყოფას.“",
			"46": "<sup>46</sup> მან კი უთხრა: „ვაი თქვენც, რჯულის მცოდნენო, რომ აჰკიდებთ ადამიანებს მძიმე ტვირთს, თვითონ კი იმ ტვირთს თითსაც არ აკარებთ.",
			"47": "<sup>47</sup> ვაი თქვენ, სამარხებს რომ უშენებთ წინასწარმეტყველთ, რომელნიც თქვენმა მამებმა დახოცეს!",
			"48": "<sup>48</sup> ამით თქვენ ემოწმებით და ეთანხმებით თქვენი მამების საქმეებს, ვინაიდან მათ დახოცეს წინასწარმეტყველნი, თქვენ კი მათ სამარხებს უშენებთ.",
			"49": "<sup>49</sup> ამიტომ თქვა ღვთის სიბრძნემ: ‘მივუვლენ მათ წინასწარმეტყველებსა და მოციქულებს, და მათგან ზოგს დახოცავენ, ზოგს განდევნიან,’",
			"50": "<sup>50</sup> რათა მოიკითხოს ყველა წინასწარმეტყველის სისხლი, რაც სოფლის შექმნიდან დაღვრილა –",
			"51": "<sup>51</sup> აბელის სისხლიდან მოყოლებული ზაქარიას სისხლამდე, რომელიც სამსხვერპლოსა და ტაძარს შორის დაიღუპა. აჰა, გეუბნებით, ამ მოდგმას მოჰკითხავენ.",
			"52": "<sup>52</sup> ვაი თქვენ, რჯულის მცოდნენო, რომ წარიტაცეთ ცოდნის გასაღები! არც თქვენ შეხვედით და შემსვლელებიც დააბრკოლეთ.“",
			"53": "<sup>53</sup> როცა ამას ეუბნებოდა მათ, მწიგნობრებმა და ფარისევლებმა დაუწყეს დიდად შევიწროება და ბევრზე სალაპარაკოდ გამოწვევა.",
			"54": "<sup>54</sup> რათა მახე დაეგოთ, რაიმე დაეცდენინებინათ და ბრალი დაედოთ მისთვის."
		},
		"12": {
			"1": "<sup>1</sup> ამასობაში ათასობით ხალხი შეიყარა, ისე რომ, ერთმანეთს თელავდნენ. ჯერ დაიწყო ლაპარაკი თავისი მოწაფეების მიმართ: „მოერიდეთ ფარისეველთა საფუარს, რომელიც პირმოთნეობაა.",
			"2": "<sup>2</sup> არაფერია დაფარული, რომ არ გაცხადდეს, და დამალული, რომ არ გამჟღავნდეს.",
			"3": "<sup>3</sup> ამიტომ, რაც ბნელში გითქვამთ, ნათელში მოისმენენ, და რაც ყურში იჩურჩულეთ ოთახებში, ერდო-ბანებზე გახმაურდება.",
			"4": "<sup>4</sup> გეუბნებით თქვენ, ჩემო მეგობრებო, ნუ გეშინიათ მათი, ვინც სხეულსა კლავს და ამის მეტის არაფრის გაკეთება შეუძლია.",
			"5": "<sup>5</sup> მაგრამ გაჩვენებთ, ვისი უნდა გეშინოდეთ: გეშინოდეთ მისი, ვისაც მოკვლის შემდეგ აქვს ხელმწიფება გეენაში ჩაგდებისა. აჰა, გეუბნებით, მისი გეშინოდეთ.",
			"6": "<sup>6</sup> ანდა, განა ხუთი ბეღურა ორი ასარი არა ღირს? და არც ერთი მათგანი დავიწყებული არ არის ღვთის წინაშე.",
			"7": "<sup>7</sup> თქვენს თავზე თმებიც დათვლილია. ჰოდა, ნუ გეშინიათ, თქვენ ბევრ ბეღურაზე უკეთესნი ხართ.",
			"8": "<sup>8</sup> ამასაც გეტყვით: ყველას, ვინც მე მაღიარებს ადამიანთა წინაშე, კაცის ძე აღიარებს ღვთის ანგელოზთა წინაშე.",
			"9": "<sup>9</sup> ხოლო, ვინც უარმყოფს ადამიანთა წინაშე, ისიც უარყოფილი იქნება ღვთის ანგელოზთა წინაშე.",
			"10": "<sup>10</sup> ყველას, ვინც იტყვის სიტყვას კაცის ძის წინააღმდეგ, მიეტევება, მაგრამ სულიწმიდის წინააღმდეგ გმობა არ მიეტევება.",
			"11": "<sup>11</sup> როცა შეგიყვანენ სინაგოგებში მთავართა და ხელისუფალთა წინაშე, ნუ იზრუნებთ იმაზე, როგორ ან რა უპასუხოთ, ან რა ილაპარაკოთ,",
			"12": "<sup>12</sup> ვინაიდან სულიწმიდა გასწავლით მაშინ, რაც უნდა ილაპარაკოთ.“",
			"13": "<sup>13</sup> ხალხიდან ვიღაცამ უთხრა მას: „მოძღვარო, უთხარი ჩემს ძმას, რომ გამიყოს მემკვიდრეობა.“",
			"14": "<sup>14</sup> მან უთხრა მას: „ადამიანო, ვინ დამაყენა თქვენს მსაჯულად ან გამყოფად?“",
			"15": "<sup>15</sup> უთხრა მათ: „დაუკვირდით და დაიცავით თავი ყოველი ანგარებისაგან, ვინაიდან ადამიანის სიცოცხლე მისი ქონების სიუხვეზე როდია დამოკიდებული.“",
			"16": "<sup>16</sup> და უთხრა მათ იგავი: „ერთ მდიდარ კაცს ყანაში კარგი მოსავალი მოუვიდა.",
			"17": "<sup>17</sup> იფიქრა თავისთვის: ‘რა ვქნა, არაფერი მაბადია, რომ სადმე დავაბინავო ჩემი მოსავალი?’",
			"18": "<sup>18</sup> თქვა: ‘აი, რას ვიზამ: ჩემს ბეღლებს დავანგრევ, უფრო დიდებს ავაშენებ და იქ შევაგროვებ მთელ ჩემს ხორბალს და დოვლათს,",
			"19": "<sup>19</sup> და ვეტყვი ჩემს სულს: სულო, ბევრი სიკეთე გაქვს დაგროვილი მრავალი წლისათვის; მოისვენე, ჭამე, სვი, იმხიარულე.’",
			"20": "<sup>20</sup> მაგრამ უთხრა მას ღმერთმა: ‘უგუნურო, ამაღამ სულს მოგთხოვენ და, რაც დაიმზადე, ვიღას დარჩება?’",
			"21": "<sup>21</sup> ისიც ასევეა, ვინც თავისთვის იუნჯებს და ღმერთში არ მდიდრდება.“",
			"22": "<sup>22</sup> უთხრა თავის მოწაფეებს: „ამიტომ გეუბნებით თქვენ: ნუ ზრუნავთ თქვენი თავისათვის – რა ჭამოთ, ნურც თქვენი სხეულისათვის – რით შეიმოსოთ.",
			"23": "<sup>23</sup> ვინაიდან სული საზრდოზე მეტია და სხეული – სამოსელზე.",
			"24": "<sup>24</sup> დააკვირდით ყორნებს: ისინი არც თესავენ, არც მკიან, არც ბეღელი აქვთ და არც საწყობი, და ღმერთი არჩენს მათ. თქვენ კი რამდენად სჯობიხართ ფრინველებს?",
			"25": "<sup>25</sup> რომელ თქვენგანს შეუძლია ზრუნვით თავის სიმაღლეს ერთი წყრთა შემატოს?",
			"26": "<sup>26</sup> ჰოდა, თუ უმცირესიც არ შეგიძლიათ, სხვაზე რაღას ზრუნავთ?",
			"27": "<sup>27</sup> შეხედეთ შროშნებს, როგორ იზრდებიან: არც შრომობენ, არც ართავენ. მაგრამ გეუბნებით თქვენ, რომ სოლომონიც კი, მთელი თავისი დიდებით, არ შემოსილა ისე, როგორც ერთი ამათგანი.",
			"28": "<sup>28</sup> თუკი ველის ბალახს, რომელიც დღეს არის, ხვალ კი თონეში ჩაიყრება, ღმერთი ამგვარად მოსავს, თქვენ უფრო მეტად არ შეგმოსავთ, მცირედ მორწმუნენო?",
			"29": "<sup>29</sup> თქვენც ნუ ეძებთ, რა ჭამოთ და რა სვათ, და ნუ შფოთავთ.",
			"30": "<sup>30</sup> ვინაიდან ყოველივე ამას ეძიებს ამ სოფლის ყველა ხალხი. თქვენმა მამამ იცის, რომ გჭირდებათ ესენი.",
			"31": "<sup>31</sup> არამედ ეძიეთ ღვთის სასუფეველი და ყოველივე ეს მოგეცემათ თქვენ.",
			"32": "<sup>32</sup> ნუ გეშინია, მცირე სამწყსოვ, ვინაიდან თქვენმა ზეციერმა მამამ მოისურვა თქვენთვის სასუფევლის მოცემა.",
			"33": "<sup>33</sup> გაყიდეთ თქვენი ქონება და გაიღეთ მოწყალება. დაიმზადეთ უცვეთელი ქისები, საუკუნოდ ულეველი საუნჯე ზეცაში, სადაც ქურდი ვერ მიეკარება და ჩრჩილი ვერ ავნებს,",
			"34": "<sup>34</sup> ვინაიდან, სადაც თქვენი საუნჯეა, თქვენი გულიც იქ იქნება.",
			"35": "<sup>35</sup> „იყოს თქვენი წელი შემოსარტყლული და სასანთლენი თქვენი – ანთებული.",
			"36": "<sup>36</sup> თქვენ კი იყავით მსგავსნი იმ ადამიანებისა, რომლებიც ელიან თავიანთი ბატონის დაბრუნებას ქორწილიდან, რომ როცა მოვა და დარეკს, იმწამსვე გაუღონ.",
			"37": "<sup>37</sup> ნეტარ არიან ის მონები, რომლებსაც შინ დაბრუნებული ბატონი ფხიზლებს ნახავს. ჭეშმარიტად გეტყვით, რომ ის შემოირტყამს სარტყელს და დასხამს მათ, მივა და მოემსახურება.",
			"38": "<sup>38</sup> თუ მეორე და თუნდაც მესამე გუშაგობისას მოვიდა და ასე ნახა, ნეტარ არიან ის მონები.",
			"39": "<sup>39</sup> ეს კი იცოდეთ: სახლის პატრონმა რომ იცოდეს, რომელ საათზე მოვა ქურდი, იფხიზლებდა და თავისი სახლის ძირის გამოთხრას არ დაანებებდა.",
			"40": "<sup>40</sup> ამიტომ თქვენც მზად იყავით, ვინაიდან, რომელ ჟამზეც არ ფიქრობთ, სწორედ მაშინ მოვა ძე კაცისა.“",
			"41": "<sup>41</sup> უთხრა მას პეტრემ: „უფალო, ამ იგავს ჩვენ გვეუბნები, თუ ყველას?“",
			"42": "<sup>42</sup> უფალმა უთხრა: „ვინ არის ერთგული და გონიერი მნე, რომელსაც დააყენებს ბატონი თავის მსახურებზე, რათა დროულად მისცეს მათ ულუფა?",
			"43": "<sup>43</sup> ნეტარია მონა, რომლის ბატონი მოვა და ამის მოქმედს ნახავს მას.",
			"44": "<sup>44</sup> ჭეშმარიტად გეუბნებით თქვენ, რომ მთელ თავის ქონებაზე დააყენებს მას.",
			"45": "<sup>45</sup> ხოლო თუ ეს მონა თავის გუნებაში იტყვის: აგვიანებს ჩემი ბატონი მოსვლასო, და დაიწყებს მსახურებისა და მხევლების ცემას, ჭამა-სმას და ლოთობას,",
			"46": "<sup>46</sup> მოვა იმ მონის ბატონი იმ დღეს, როცა არ ელის, და იმ ჟამს, როცა არ იცის, და შუაზე გაჰკვეთს მას და ურწმუნოებთან დაუდებს წილს.",
			"47": "<sup>47</sup> ხოლო მონა, რომელმაც იცოდა თავისი ბატონის ნება და არ მოემზადა, ან არ მოიქცა მისი ნებისამებრ, მაგარ ცემას მიიღებს.",
			"48": "<sup>48</sup> ხოლო რომელმაც არ იცოდა და ისე მოიქცა, რომ ცემა დაიმსახურა, ნაკლებად იცემება. ყველას, ვისაც მეტი მიეცა, მეტს მოსთხოვენ, და ვისაც მეტს მიანდობენ, მას მეტსაც მოჰკითხავენ.",
			"49": "<sup>49</sup> „ამქვეყნად ცეცხლის მოსატანად მოვედი და როგორ მინდა, რომ ენთოს.",
			"50": "<sup>50</sup> ნათლობით უნდა მოვინათლო, და როგორ ვარ შეჭირვებული, ვიდრე ეს აღსრულდება.",
			"51": "<sup>51</sup> გგონიათ, ვითომ ქვეყნად მშვიდობის მოსაფენად მოვედი? არა, გეუბნებით თქვენ: გასაყოფად.",
			"52": "<sup>52</sup> ვინაიდან ამიერიდან ხუთნი გაიყოფიან ერთ სახლში: სამი ორთაგან და ორი სამთაგან.",
			"53": "<sup>53</sup> გაიყოფა მამა ძისაგან და ძე – მამისაგან, დედა ასულისაგან და ასული – დედისაგან, დედამთილი – თავისი რძლისაგან და რძალი დედამთილისაგან.“",
			"54": "<sup>54</sup> ხალხსაც უთხრა: „როცა დასავლეთით ამომავალ ღრუბელს დაინახავთ, ამბობთ იმწამსვე: წვიმა მოვაო, და ასეც ხდება.",
			"55": "<sup>55</sup> ხოლო, როცა სამხრეთის ქარი უბერავს, ამბობთ: სიცხე იქნებაო, და ხდება.",
			"56": "<sup>56</sup> თვალთმაქცნო! ცისა და მიწის პირის გამოცნობა შეგიძლიათ, ეს ჟამი კი როგორ ვერ გამოგიცვნიათ?",
			"57": "<sup>57</sup> თავად რატომ არ განსჯით, რა არის სამართლიანი?",
			"58": "<sup>58</sup> როცა შენს მოდავესთან ერთად მთავართან მიდიხარ, ეცადე გზაშივე გათავისუფლდე მისგან, რომ მან არ მიგიყვანოს მოსამართლესთან, მოსამართლემ არ მიგცეს ბოქაულს და ბოქაულმა არ ჩაგაგდოს საპყრობილეში.",
			"59": "<sup>59</sup> გეუბნები: ვერ გამოხვალ იქიდან, ვიდრე უკანასკნელ ლეპტას არ გადაიხდი.“"
		},
		"13": {
			"1": "<sup>1</sup> ამ დროს მოვიდნენ ზოგიერთები და უამბეს მას გალილეველთა შესახებ, რომელთა სისხლი პილატემ შეურია მათ მსხვერპლებს.",
			"2": "<sup>2</sup> მიუგო იესომ და უთხრა მათ: „თქვენ გგონიათ, ვითომ ეს გალილეველები ყველა გალილეველზე უფრო ცოდვილები იყვნენ, ასე რომ ევნენ?",
			"3": "<sup>3</sup> არა, გეუბნებით, მაგრამ თუ არ მოინანიებთ, ყველანი მათსავით დაიღუპებით.",
			"4": "<sup>4</sup> ან რა გგონიათ, ის თვრამეტი კაცი, რომელთაც შილოახის კოშკი დაეცა ზედ და ამოწყვიტა, უფრო დამნაშავენი იყვნენ, ვიდრე ყველა ის ადამიანი, იერუსალიმში რომ ცხოვრობს?",
			"5": "<sup>5</sup> არა, გეუბნებით: მაგრამ თუ არ მოინანიებთ, ყველანი მათსავით დაიღუპებით!“",
			"6": "<sup>6</sup> უთხრა ეს იგავი: „ერთ კაცს თავის ვენახში ლეღვი ედგა. მივიდა, რომ ზედ ნაყოფი მოეძებნა და ვერ იპოვა.",
			"7": "<sup>7</sup> უთხრა მევენახეს: ‘აჰა, სამი წელია მოვდივარ ამ ლეღვის ნაყოფის საძებნელად და ვერ ვპოულობ. მოჭერი იგი, რად უნდა ფიტავდეს მიწას?’",
			"8": "<sup>8</sup> მან კი მიუგო და უთხრა მას: ‘ბატონო, დატოვე ამ წელიწადსაც, ვიდრე ძირს შემოვუთოხნიდე და ნაკელს დავუყრიდე,",
			"9": "<sup>9</sup> იქნებ მოისხას ნაყოფი. თუ არა და, მოჭრი მას.’“",
			"10": "<sup>10</sup> ასწავლიდა მათ ერთ სინაგოგაში შაბათს.",
			"11": "<sup>11</sup> და აჰა, იქ იყო დედაკაცი, რომელიც თვრამეტ წელიწადს შეპყრობილი იყო სნეულების სულით, და იყო დაკრუნჩხული და წელში ვერ იმართებოდა.",
			"12": "<sup>12</sup> დაინახა იგი იესომ, დაუძახა და უთხრა მას: „დედაკაცო, თავისუფალი ხარ შენი სნეულებისაგან.“",
			"13": "<sup>13</sup> დაადო ხელები და მაშინვე გაიმართა იგი, და ადიდებდა ღმერთს.",
			"14": "<sup>14</sup> განრისხდა სინაგოგის მთავარი, შაბათს რომ განკურნა იესომ, და უთხრა ხალხს: „ექვსი დღეა, როცა შეიძლება რაიმეს კეთება. ამ დღეებში მოდით და განიკურნეთ, ოღონდ შაბათს არა.“",
			"15": "<sup>15</sup> მიუგო მას უფალმა და უთხრა: „თვალთმაქცო, განა ყოველი თქვენგანი არ უშვებს შაბათობით ბაგიდან თავის ხარს ან ვირს და არ მიჰყავს წყლის დასალევად?",
			"16": "<sup>16</sup> ხოლო ეს აბრაამის ასულია, რომელიც უკვე თვრამეტი წელიწადია შეკრული ჰყავდა სატანას. შაბათ დღეს არ შეიძლებოდა გამოხსნილიყო ამ ხუნდებიდან?“",
			"17": "<sup>17</sup> როცა ამას ლაპარაკობდა, ყველა მის მოწინააღმდეგეს რცხვენოდა. ხოლო მთელი ხალხი ხარობდა მის დიდებულებათა გამო, რასაც სჩადიოდა.",
			"18": "<sup>18</sup> ის ამბობდა: „რას შეჰგავს ღვთის სასუფეველი და რას ვამსგავსო იგი?",
			"19": "<sup>19</sup> იგი შეჰგავს მდოგვის მარცვალს, რომელიც აიღო კაცმა და დათესა თავის ბოსტანში. გაიზარდა იგი და იქცა ხედ და მის ტოტებში ცის ფრინველებმა დაიბუდეს.“",
			"20": "<sup>20</sup> და კიდევ თქვა: „რას ვამსგავსო ღვთის სასუფეველი?",
			"21": "<sup>21</sup> იგი შეჰგავს საფუარს, რომელიც დედაკაცმა აიღო და ჩაფლა სამ საწყაო ფქვილში, ვიდრე მთლიანად გაფუვდებოდა.“",
			"22": "<sup>22</sup> მიმოდიოდა ქალაქებსა და სოფლებში, ასწავლიდა და თან იერუსალიმის გზას ადგა.",
			"23": "<sup>23</sup> ვიღაცამ უთხრა მას: „უფალო, ნუთუ ცოტანი გადარჩებიან?“ მან კი უთხრა მათ:",
			"24": "<sup>24</sup> „იღვაწეთ, რათა შეხვიდეთ ვიწრო კარიდან. რადგან, გეუბნებით, მრავალი ეცდება შესვლას და ვერ შეძლებს.",
			"25": "<sup>25</sup> როცა სახლის პატრონი ადგება და დაკეტავს კარს, მაშინ თქვენ, გარეთ მდგომნი, დაიწყებთ კარზე რეკვას და ეტყვით: ‘ბატონო, ბატონო, გაგვიღე!’ მაგრამ ის მოგიგებთ და გეტყვით თქვენ: ‘არ გიცნობთ, სადაურები ხართ.’",
			"26": "<sup>26</sup> მაშინ დაუწყებთ ლაპარაკს: ‘შენს წინაშე ვჭამდით და ვსვამდით, და ჩვენს ქუჩებში ასწავლიდი.’",
			"27": "<sup>27</sup> და გეტყვით: ‘გეუბნებით, არ გიცნობთ, სადაურები ხართ. გამშორდით ყველანი, უსამართლობის მოქმედნო.’",
			"28": "<sup>28</sup> იქნება იქ ტირილი და კბილთა ღრჭენა, როცა იხილავთ აბრაამს და ისაკს და იაკობს და ყველა წინასწარმეტყველს ღვთის სასუფეველში, თქვენს თავს კი – გარეთ განდევნილებს.",
			"29": "<sup>29</sup> მოვლენ აღმოსავლეთიდან და დასავლეთიდან, ჩრდილოეთიდან და სამხრეთიდან, და ინახად დასხდებიან ღვთის სასუფეველში.",
			"30": "<sup>30</sup> და აჰა, არიან უკანასკნელნი, რომელნიც პირველნი იქნებიან, და არიან პირველნი, რომელნიც უკანასკნელნი იქნებიან.“",
			"31": "<sup>31</sup> ამ დროს მოვიდნენ ზოგიერთნი ფარისეველთაგან და უთხრეს მას: „წადი, გაეცალე აქაურობას, რადგან ჰეროდეს შენი მოკვლა უნდა.“",
			"32": "<sup>32</sup> და უთხრა მათ: „წადით და უთხარით იმ მელას: აჰა, ვდევნი ეშმაკებს და ვკურნავ დღესა და ხვალ, მესამე დღეს კი დავასრულებ.",
			"33": "<sup>33</sup> მაგრამ მე უნდა ვიარო დღეს, ხვალ და ზეგ, ვინაიდან არ იქნება, რომ წინასწარმეტყველი იერუსალიმის გარეთ დაიღუპოს! „იერუსალიმო, იერუსალიმო!“",
			"34": "<sup>34</sup> „იერუსალიმო, იერუსალიმო, დამხოცველო წინასწარმეტყველთა და ჩამქოლავო შენთან მოვლინებულთა! რამდენჯერ მინდოდა შენი შვილების შეკრება, როგორც ფრინველი იკრებს ფრთებქვეშ თავის ბარტყებს, მაგრამ თქვენ არ ისურვეთ.",
			"35": "<sup>35</sup> აჰა, სახლი აოხრებული გრჩებათ. გეუბნებით: ვეღარ მიხილავთ ამიერიდან, ვიდრე არ იტყვით: ‘კურთხეულია უფლის სახელით მომავალი!’“"
		},
		"14": {
			"1": "<sup>1</sup> ერთხელ, როცა შევიდა იგი ფარისეველთა რომელიღაც მთავრის სახლში შაბათს პურის საჭმელად და ისინი უთვალთვალებდნენ მას,",
			"2": "<sup>2</sup> აჰა, მის წინაშე წარდგა ერთი კაცი, წყალმანკით დაავადებული.",
			"3": "<sup>3</sup> მიუგო იესომ და უთხრა რჯულის მცოდნეებსა და ფარისევლებს: „შეიძლება თუ არა განკურნება შაბათს?“",
			"4": "<sup>4</sup> ისინი დუმდნენ. და შეეხო მას, განკურნა და გაუშვა.",
			"5": "<sup>5</sup> და მათ უთხრა: „რომელიმე თქვენგანს ვირი ან ხარი რომ ჩაუვარდეს ჭაში, განა იმ წამსვე არ ამოიყვანს, შაბათი დღეც რომ იყოს?“",
			"6": "<sup>6</sup> და ვერ შეძლეს ამაზე პასუხის მიცემა.",
			"7": "<sup>7</sup> უთხრა წვეულებს იგავი, როცა შეამჩნია, რომ წინა ადგილებს ირჩევდნენ, და უთხრა მათ:",
			"8": "<sup>8</sup> „თუ ვინმემ ქორწილში მიგიწვიოს, ნუ დაჯდები წინა ადგილზე, რადგან შეიძლება შენზე უფრო საპატიო სხვა ჰყავდეს მოწვეული,",
			"9": "<sup>9</sup> რომ არ მოვიდეს შენი და მისი მაწვევარი და არ გითხრას: ‘დაუთმე ამას ადგილი!’ მაშინ შერცხვენილმა უნდა დაიკავო უკანასკნელი ადგილი.",
			"10": "<sup>10</sup> მაგრამ როცა მოგიწვევენ, მიდი და დაჯექი ბოლო ადგილზე, რომ მოვიდეს შენი მაწვევარი და გითხრას: ‘მეგობარო, გადაბრძანდი უფრო მაღლა!’ მაშინ გექნება მეტი დიდება ყოველი თანამეინახის წინაშე.",
			"11": "<sup>11</sup> ვინაიდან ყველა, ვინც თავს აიმაღლებს, დამდაბლდება, და ვინც თავს დაიმდაბლებს – ამაღლდება.“",
			"12": "<sup>12</sup> თავის მაწვევარსაც უთხრა: „როცა სადილს ან ვახშამს გამართავ, ნუ მოიწვევ შენს მეგობრებს, ნურც შენს ძმებს, ნურც შენს ნათესავებს, ნურც მდიდარ მეზობლებს, რათა მერე იმათაც არ მიგიწვიონ და არ მიიღო სამაგიერო,",
			"13": "<sup>13</sup> არამედ, როცა ლხინს გამართავ, მოიწვიე გლახაკნი, ხეიბრები, კოჭლები და ბრმები.",
			"14": "<sup>14</sup> ნეტარი იქნები, რადგან მათ არაფერი აქვთ, რომ მოგიზღან. მაგრამ შენ მართალთა აღდგომისას მოგეზღვება.“",
			"15": "<sup>15</sup> ეს რომ გაიგონა ერთმა მასთან მჯდომმა, უთხრა მას: „ნეტარია, ვინც ჭამს პურს ღვთის სასუფეველში.“",
			"16": "<sup>16</sup> მან კი უთხრა მას: „ერთმა კაცმა დიდი პურობა გამართა და ბევრი მოიწვია.",
			"17": "<sup>17</sup> პურობის დრომ რომ მოაღწია, გაგზავნა თავისი მონა, რათა წვეულთათვის ეთქვა: მობრძანდით, ყველაფერი მზად არისო.",
			"18": "<sup>18</sup> ყველამ ერთხმად დაიწყო მობოდიშება. პირველმა თქვა: ყანა ვიყიდე და მის სანახავად უნდა წავიდე, გთხოვ, მაპატიოო;",
			"19": "<sup>19</sup> მეორემ უთხრა: ხუთი უღელი ხარი ვიყიდე და მათ გამოსაცდელად მივდივარ, გთხოვ, მაპატიოო;",
			"20": "<sup>20</sup> მესამემ უთხრა: ცოლი შევირთე და ამის გამო არ შემიძლია მოსვლაო.",
			"21": "<sup>21</sup> დაბრუნდა მონა უკან და აუწყა ეს თავის ბატონს. განრისხდა მაშინ მასპინძელი და უთხრა თავის მონას: ‘გადი სასწრაფოდ, ქალაქის მოედნებზე და ქუჩებში, მომიყვანე გლახაკნი, ხეიბრები, ბრმები და კოჭლები.’",
			"22": "<sup>22</sup> და უთხრა მონამ: ‘ბატონო, აღსრულებულია რაც ბრძანე, და კიდევ დარჩა ადგილი.’",
			"23": "<sup>23</sup> და უთხრა ბატონმა მონას: ‘გადი გზებზე და ორღობეებში, დაიყოლიე ისინი, რომ მოვიდნენ, რათა აივსოს ჩემი სახლი,",
			"24": "<sup>24</sup> რადგან გეუბნებით: ვერავინ იმ წვეულთაგან ვერ იგემებს ჩემს პურობას!’“",
			"25": "<sup>25</sup> მივიდა მასთან დიდძალი ხალხი და ის მიუბრუნდა მათ და უთხრა:",
			"26": "<sup>26</sup> „თუ ვინმე მოვა ჩემთან და არ მოიძულებს თავის მამას და დედას, ცოლსა და შვილებს, ძმებსა და დებს, საკუთარ სულსაც კი, იგი ვერ გახდება ჩემი მოწაფე.",
			"27": "<sup>27</sup> ვინც არ ატარებს თავის ჯვარს და ისე არ გამომყვება, ვერ გახდება ჩემი მოწაფე.",
			"28": "<sup>28</sup> რადგან რომელიმე თქვენგანმა რომ კოშკის აშენება მოისურვოს, განა თავდაპირველად არ დაჯდება და არ იანგარიშებს იმ ფასს, რაც მის დასამთავრებლად იქნება საჭირო?",
			"29": "<sup>29</sup> რადგან საძირკველს თუ ჩაყრის და ვერ დაამთავრებს, ყველა, ვინც დაინახავს, დაცინვას დაუწყებს,",
			"30": "<sup>30</sup> და იტყვის: ამ კაცმა შენება დაიწყო და ვეღარ დაამთავრაო.",
			"31": "<sup>31</sup> ანდა, რომელი მეფეა, რომ მეორე მეფის წინააღმდეგ ომის განმზრახველი, ჯერ სათათბიროდ არ დაჯდეს, შეუძლია თუ არა ათი ათასით დაუხვდეს ოცი ათას მომხვდურს?",
			"32": "<sup>32</sup> თუ არა და, ვიდრე ჯერ კიდევ შორს არის იგი, ელჩობას მიუგზავნის, და ზავსა სთხოვს.",
			"33": "<sup>33</sup> ასევეა ყოველი თქვენგანი. ვინც უარს არ იტყვის ყველაფერზე, რაც აბადია, ვერ შეძლებს, ჩემს მოწაფეობას.",
			"34": "<sup>34</sup> მარილი კარგია, მაგრამ, თუ მარილი განქარდა, რიღათი დამარილდეს?",
			"35": "<sup>35</sup> არც მიწისთვის გამოდგება, არც ნაკელისთვის, ამიტომ შორს გადაყრიან. ვისაც ყურები აქვს მოსასმენად, ისმინოს!“"
		},
		"15": {
			"1": "<sup>1</sup> ახლოს მიდიოდა მასთან ყველა მებაჟე და ცოდვილი და უსმენდა მას.",
			"2": "<sup>2</sup> ფარისეველნი და მწიგნობარნი ბუზღუნებდნენ და ამბობდნენ: ცოდვილებს ღებულობს და მათთან ჭამსო.",
			"3": "<sup>3</sup> უთხრა მათ ეს იგავი:",
			"4": "<sup>4</sup> „რომელიმე თქვენგანს რომ ასი ცხვარი ჰყავდეს და ერთი დაეკარგოს, განა არ დატოვებს ოთხმოცდაცხრამეტს უდაბნოში და არ წავა დაკარგულის საძებრად, ვიდრე არ იპოვის?",
			"5": "<sup>5</sup> რომ იპოვის, სიხარულით მხრებზე შეისვამს.",
			"6": "<sup>6</sup> შინ რომ დაბრუნდება, დაუძახებს მეგობრებს, მეზობლებს და ეტყვის: თქვენც იხარეთ ჩემთან ერთად, დაკარგული ცხვარი ვიპოვეო.",
			"7": "<sup>7</sup> თქვენ გეუბნებით, რომ ასევე მეტი სიხარული იქნება ზეცაში ერთი ცოდვილის გამო, რომელმაც მოინანია, ვიდრე იმ ოთხმოცდაცხრამეტი მართლის გამო, რომელთაც მონანიება არ სჭირდებათ.",
			"8": "<sup>8</sup> „ანდა დედაკაცი, რომელსაც ათი დრაქმა აქვს, ერთი დრაქმა რომ დაეკარგოს, განა არ აანთებს სანთელს და არ გამოგვის სახლს და გულმოდგინეთ არ დაუწყებს ძებნას, ვიდრე არ იპოვის?",
			"9": "<sup>9</sup> როცა იპოვის, დაუძახებს მეგობრებს და მეზობლებს და ეტყვის: ჩემთან ერთად იხარეთ, ვინაიდან ვიპოვე დაკარგული დრაქმაო.",
			"10": "<sup>10</sup> თქვენ გეუბნებით: ასევე იქნება სიხარული ღვთის ანგელოზებთან ერთი მომნანიებელი ცოდვილის გამო.“",
			"11": "<sup>11</sup> თქვა: „ერთ კაცს ორი ვაჟი ჰყავდა.",
			"12": "<sup>12</sup> უმცროსმა ვაჟმა უთხრა მამას: ‘მომეცი ჩემი კუთვნილი წილი ქონებისა.’ და მამამ გაუყო მათ ქონება.",
			"13": "<sup>13</sup> რამდენიმე დღის შემდეგ უმცროსმა ვაჟმა მოაგროვა ყველაფერი, წავიდა შორეულ ქვეყანაში და გაფლანგა თავისი ქონება თავაშვებული ცხოვრებით.",
			"14": "<sup>14</sup> როცა ყველაფერი შემოეხარჯა, იმ ქვეყანაში დიდი შიმშილობა ჩამოვარდა, და მას გაუჭირდა.",
			"15": "<sup>15</sup> წავიდა და იმ ქვეყნის ერთ მცხოვრებს მიეკედლა, რომელმაც თავის მინდვრებში გაგზავნა ღორების საძოვებლად.",
			"16": "<sup>16</sup> ენატრებოდა, მუცელი იმ რკოთი მაინც ამოეყორა, რომელსაც ღორები ჭამდნენ, მაგრამ არავინ არაფერს აძლევდა.",
			"17": "<sup>17</sup> როცა გონს მოეგო, თქვა: ‘მამაჩემთან რამდენ მოჯამაგირეს პური თავსაყრელი აქვს, მე კი შიმშილით ვკვდები.",
			"18": "<sup>18</sup> ავდგები, წავალ მამაჩემთან და ვეტყვი: მამა, შევცოდე ზეცის მიმართ და შენს წინაშე,",
			"19": "<sup>19</sup> ღირსი აღარა ვარ, რომ შენს ძედ ვიწოდებოდე. მიმიღე როგორც ერთი შენი მოჯამაგირეთაგანი!’",
			"20": "<sup>20</sup> ადგა და წავიდა თავის მამასთან. როცა ჯერ კიდევ შორს იყო, მამამ დაინახა და შეებრალა. გაიქცა, ყელზე მოეხვია და აკოცა.",
			"21": "<sup>21</sup> ვაჟმა კი უთხრა: ‘მამა! შევცოდე ზეცის მიმართ და შენს წინაშე. ღირსი აღარა ვარ, რომ შენს ძედ ვიწოდებოდე.’",
			"22": "<sup>22</sup> უთხრა მამამ თავის მონებს: ‘მოუტანეთ საუკეთესო სამოსელი და ჩააცვით, გაუკეთეთ ბეჭედი ხელზე და სანდლები ჩააცვით ფეხებზე.",
			"23": "<sup>23</sup> მოიყვანეთ ნასუქალი ხბო და დაკალით. ვჭამოთ და ვიმხიარულოთ.",
			"24": "<sup>24</sup> ვინაიდან ეს ჩემი ვაჟი მკვდარი იყო და გაცოცხლდა, დაკარგული იყო და გამოჩნდა.’ და დაიწყეს მხიარულება.",
			"25": "<sup>25</sup> უფროსი ვაჟი კი მინდორში იმყოფებოდა. რომ დაბრუნდა და სახლს მიუახლოვდა, სიმღერისა და ცეკვა-თამაშის ხმა შემოესმა.",
			"26": "<sup>26</sup> დაუძახა ერთ მსახურთაგანს და ჰკითხა: ‘ეს რა ამბავია?’",
			"27": "<sup>27</sup> მან უთხრა მას: ‘შენი ძმა მოვიდა და მამაშენმა ნასუქალი ხბო დაკლა, რაკი მრთელი დაუბრუნდა.’",
			"28": "<sup>28</sup> ის კი გაბრაზდა და შესვლა აღარ უნდოდა. მამამისი გამოვიდა და იწვევდა.",
			"29": "<sup>29</sup> მან მიუგო და უთხრა თავის მამას: ‘აგერ, რამდენი წელია გემსახურები, არასოდეს შენს ბრძანებას არ გადავსულვარ და ერთხელაც არ მოგიცია ციკანი, რომ ჩემს მეგობრებთან მემხიარულა.",
			"30": "<sup>30</sup> ხოლო, როცა მოვიდა ეს შენი ვაჟი, რომელმაც თავისი ქონება მეძავებთან გაფლანგა, ნასუქალი ხბო დაუკალი.’",
			"31": "<sup>31</sup> მან უთხრა მას: ‘შვილო, შენ მუდამ ჩემთან ხარ და, რაც კი მაქვს, შენია.",
			"32": "<sup>32</sup> მაგრამ უნდა ვხარობდეთ და ვმხიარულებდეთ, ვინაიდან შენი ძმა მკვდარი იყო და გაცოცხლდა, დაკარგული იყო და გამოჩნდა.’“"
		},
		"16": {
			"1": "<sup>1</sup> უთხრა თავის მოწაფეებსაც: „იყო ერთი მდიდარი კაცი და ჰყავდა მნე, რომელზეც ამბავი მიუტანეს, რომ მის ქონებას ანიავებდა.",
			"2": "<sup>2</sup> დაიბარა და უთხრა: ‘ეს რა მესმის შენზე? ჩამაბარე შენი სამნეოს ანგარიში, ვინაიდან ვეღარ იქნები მნე.’",
			"3": "<sup>3</sup> მნემ თქვა თავისთვის: ‘როგორ მოვიქცე? ჩემი ბატონი მართმევს სამნეოს. ბარვა არ შემიძლია, მათხოვრობის მრცხვენია.",
			"4": "<sup>4</sup> ვიცი, როგორც უნდა მოვიქცე, რომ მიმიღონ თავიანთ სახლებში, როცა მნეობას ჩამომაშორებენ.’",
			"5": "<sup>5</sup> დაიბარა თავისი ბატონის ყველა მოვალე. პირველს უთხრა: ‘რამდენი გმართებს ჩემი ბატონისა?’",
			"6": "<sup>6</sup> მან თქვა: ‘ასი საწყაული ზეთი.’ უთხრა მას: ‘მიიღე შენი ხელწერილი, დაჯექი სასწრაფოდ და დაწერე – ორმოცდაათი.’",
			"7": "<sup>7</sup> მერე სხვას უთხრა: ‘შენ რამდენი გმართებს?’ მან თქვა: ‘ასი კოდი ხორბალი.’ მან უთხრა: ‘მიიღე შენი ხელწერილი და დაწერე – ოთხმოცი.’",
			"8": "<sup>8</sup> შეაქო ბატონმა უსამართლო მნე, ხერხიანად რომ მოიქცა. რადგან ამ სოფლის ძენი ნათლის შვილებზე მოხერხებულნი არიან თავიანთ მოდგმაში.",
			"9": "<sup>9</sup> და მეც გეუბნებით თქვენ: შეიძინეთ მეგობრები უსამართლობის მამონისგან, რომ, როცა გაღარიბდებით, მიგიღონ საუკუნო სავანეში.",
			"10": "<sup>10</sup> მცირედში ერთგული ბევრშიც ერთგულია; და ვინც მცირედშიც უსამართლოა, ბევრშიც უსამართლოა.",
			"11": "<sup>11</sup> ჰოდა, თუ უსამართლო მამონას არ უერთგულეთ, ჭეშმარიტს ვინ განდობთ?",
			"12": "<sup>12</sup> და თუ სხვისას არ უერთგულეთ, თქვენსას ვინ მოგცემთ?",
			"13": "<sup>13</sup> არც ერთ მსახურს არ შეუძლია ორ ბატონს ემსახუროს: ან ერთი უნდა სძულდეს და მეორე უყვარდეს. ან იმ ერთს შეეთვისოს და მეორე დაამციროს. ვერ შეძლებთ ღმერთსაც ემსახუროთ და მამონასაც!“",
			"14": "<sup>14</sup> ყოველივე ეს ესმოდათ ფარისევლებსაც, რომლებიც ვერცხლის მოყვარულნი იყვნენ, და დასცინოდნენ მას.",
			"15": "<sup>15</sup> და უთხრა მათ: „თქვენ თავს იმართლებთ ადამიანთა წინაშე, მაგრამ ღმერთმა იცის თქვენი გული, ვინაიდან, რაც კაცთა შორის მაღალია, ღვთის წინაშე სისაძაგლეა.",
			"16": "<sup>16</sup> რჯული და წინასწარმეტყველნი იოანემდე იყო. მას შემდეგ ღვთის სასუფეველი იხარება და ყველა ძალით შედის მასში.",
			"17": "<sup>17</sup> უმალ ცა და მიწა გადაივლის, ვიდრე ერთი წერტილი ამოვარდება რჯულიდან.",
			"18": "<sup>18</sup> ყოველი, ვინც თავის ცოლს გაუშვებს და სხვას შეირთავს, მრუშობს; და ვინც ქმრის მიერ გაშვებულს შეირთავს, მრუშობს.“",
			"19": "<sup>19</sup> „იყო ერთი მდიდარი კაცი, ძოწეულსა და ბისონს იცვამდა, ყოველდღე დიდებულად ნადიმობდა.",
			"20": "<sup>20</sup> ასევე იყო ერთი გლახაკი, სახელად ლაზარე, რომელიც მის ჭიშკართან იწვა დაწყლულებული.",
			"21": "<sup>21</sup> ენატრებოდა მდიდრის სუფრიდან გადმოცვენილი ნამცეცებით გაძღომა. ძაღლები მოდიოდნენ და წყლულებს ულოკავდნენ.",
			"22": "<sup>22</sup> მოკვდა ის გლახაკი და ანგელოზებმა მიიყვანეს აბრაამის წიაღში. მდიდარიც მოკვდა და დამარხეს.",
			"23": "<sup>23</sup> და ჯოჯოხეთში, ტანჯვა-წამებაში მყოფმა, აახილა თვალები და დაინახა შორიდან აბრაამი და ლაზარეც მის წიაღში.",
			"24": "<sup>24</sup> შესძახა და თქვა: ‘მამაო აბრაამ, შემიწყალე და მომივლინე ლაზარე, რომ დაისველოს თავისი თითის წვერი წყალში და ენა გამიგრილოს, ვინაიდან ვიტანჯები ამ ალში.’",
			"25": "<sup>25</sup> უთხრა აბრაამმა: ‘შვილო, გაიხსენე, რომ უკვე მიიღე შენს სიცოცხლეში სიკეთე და ლაზარემ კი – სიავე. ახლა ის აქ ნუგეშცემულია, შენ კი იტანჯები.",
			"26": "<sup>26</sup> ყოველივე ამის გარდა ჩვენსა და თქვენს შორის დიდი უფსკრულია დამყარებული, ასე რომ გადმოსვლის მსურველები ვერც ჩვენგან თქვენსკენ გადმოვლენ და ვერც თქვენგან ჩვენსკენ.’",
			"27": "<sup>27</sup> თქვა: ‘რაკი ასეა, გეხვეწები, მამაო, მიავლინე ეგ მამაჩემის სახლში.’",
			"28": "<sup>28</sup> ვინაიდან ხუთი ძმა მყავს, რათა მათ დაუმოწმოს, რომ არ მოხვდნენ ამ სატანჯველ ადგილას.’",
			"29": "<sup>29</sup> უთხრა მას აბრაამმა: ‘მათ ჰყავთ მოსე და წინასწარმეტყველნი. იმათ უსმინონ.’",
			"30": "<sup>30</sup> მან თქვა: ‘არა, მამაო აბრაამ, თუ ვინმე მკვდართაგანი მივა მათთან, მოინანიებენ.’",
			"31": "<sup>31</sup> უთხრა მას: ‘თუ მოსესა და წინასწარმეტყველთ არ მოუსმენენ, მკვდრეთითაც რომ აღდგეს ვინმე, არ ირწმუნებენ.’“"
		},
		"17": {
			"1": "<sup>1</sup> უთხრა თავის მოწაფეებს: „შეუძლებელია არ მოვიდეს ცდუნებები, მაგრამ ვაი მას, ვისი მეშვეობითაც მოვა.",
			"2": "<sup>2</sup> მისთვის უმჯობესი იქნებოდა წისქვილის ქვა დაეკიდათ კისერზე და ზღვაში გადაეგდოთ, ვიდრე ერთი ამ მცირეთაგანი ეცდუნებინა.",
			"3": "<sup>3</sup> ფრთხილად იყავით: თუ შენმა ძმამ შეგცოდოს, გაუწყერი მას. თუ მოინანიოს, მიუტევე.",
			"4": "<sup>4</sup> დღეში შვიდჯერ რომ შეგცოდოს და შვიდჯერვე მოიქცეს და გითხრას, ვინანიებო, მიუტევე.“",
			"5": "<sup>5</sup> უთხრეს მოციქულებმა უფალს: „მოგვიმატე რწმენა.“",
			"6": "<sup>6</sup> თქვა უფალმა: „მდოგვის მარცვლისოდენა რწმენაც რომ გქონდეთ და უთხრათ ამ ლეღვის ხეს: აღმოიფხვერი და ზღვაში გადაინერგეო, გაგიგონებდათ.",
			"7": "<sup>7</sup> რომელიმე თქვენგანს მონა რომ ჰყავდეს – მხვნელი ანდა მწყემსი, მინდვრიდან დაბრუნებისას ნუთუ ეტყვის: წადი ჩქარა, სუფრას მიუჯექიო?",
			"8": "<sup>8</sup> განა არ ეტყვის: მომიმზადე ვახშამი და სარტყელშემორტყმული მემსახურე, ვიდრე ვჭამ და ვსვამ, მერე კი შენც ჭამე და სვიო?",
			"9": "<sup>9</sup> ნუთუ მადლობას ეტყვის მონას იმისათვის, რომ მისი ბრძანება შეასრულა? არა მგონია.",
			"10": "<sup>10</sup> ასევე თქვენც, როცა შეასრულებთ ყველაფერს, რაც ნაბრძანები გქონდათ, თქვით: უბრალო მონები ვართ, რადგან გავაკეთეთ ის, რაც უნდა გაგვეკეთებინა.“",
			"11": "<sup>11</sup> ერთხელ იერუსალიმს მიმავალმა სამარიასა და გალილეას შორის გაიარა.",
			"12": "<sup>12</sup> ერთ სოფელში რომ შედიოდა, ათი კეთროვანი კაცი შემოხვდა, რომლებიც მოშორებით გაჩერდნენ.",
			"13": "<sup>13</sup> ხმა აღიმაღლეს და ამბობდნენ: „შეგვიწყალე, იესო მოძღვარო!“",
			"14": "<sup>14</sup> ისინი რომ დაინახა, უთხრა: „წადით, ეჩვენეთ მღვდლებს.“ როგორც კი წავიდნენ, განიწმიდნენ.",
			"15": "<sup>15</sup> ერთი მათგანი, რომ დაინახა განვიკურნეო, მობრუნდა და მაღალი ხმით ადიდებდა ღმერთს.",
			"16": "<sup>16</sup> პირქვე დაემხო მის ფერხთით და მადლი შესწირა. ის კაცი სამარიელი იყო.",
			"17": "<sup>17</sup> მიუგო იესომ და თქვა: „ნუთუ ათივენი არ განიწმიდნენ? ცხრა სადღა არის?",
			"18": "<sup>18</sup> როგორ მოხდა, რომ უცხოტომელის გარდა არავინ დაბრუნდა ღვთის სადიდებლად?“",
			"19": "<sup>19</sup> უთხრა მას: „ადექი და წადი! შენმა რწმენამ გიხსნა.“",
			"20": "<sup>20</sup> ჰკითხეს მას ფარისევლებმა: „როდის მოვა ღვთის სასუფეველი?“ მიუგო და უთხრა მათ: „ღვთის სასუფეველი არ მოვა თვალსაჩინოდ.",
			"21": "<sup>21</sup> არც იტყვიან: აგერ, აქ არისო, ან: აგერ, იქ არისო. ვინაიდან, აჰა, თვითონ თქვენშია ღვთის სასუფეველი.“",
			"22": "<sup>22</sup> უთხრა მოწაფეებს: „დადგება დღეები, როცა ინატრებთ, თუნდაც ერთი დღით იხილოთ ძე კაცისა და ვერ იხილავთ.",
			"23": "<sup>23</sup> გეტყვიან: აგერ, აქ არისო, ან: აგერ, იქ არისო. ნუ წახვალთ და ნუ გაედევნებით.",
			"24": "<sup>24</sup> ვინაიდან, როგორც ელვა ელავს ცის ერთი კიდიდან და ანათებს მეორე კიდემდე, ასევე იქნება ძე კაცისა თავის დღეს.",
			"25": "<sup>25</sup> ოღონდ თავდაპირველად მისი ხვედრია მრავალი ტანჯვის ატანა და უარყოფა ამ მოდგმის მიერ.",
			"26": "<sup>26</sup> და როგორც ნოეს დღეებში იყო, ასევე იქნება კაცის ძის დღეებშიც:",
			"27": "<sup>27</sup> ჭამდნენ, სვამდნენ, ცოლს ირთავდნენ, თხოვდებოდნენ იმ დღემდე, ვიდრე ნოე კიდობანში შევიდოდა. მერე მოვიდა წარღვნა და ყველა დაღუპა.",
			"28": "<sup>28</sup> ისევე, როგორც ლოტის დროს იყო: ჭამდნენ, სვამდნენ, ყიდდნენ, ყიდულობდნენ, რგავდნენ და აშენებდნენ.",
			"29": "<sup>29</sup> ხოლო იმ დღეს, როცა ლოტი სოდომიდან გავიდა, ცეცხლმა და გოგირდმა იწვიმა ზეციდან და ყველა დაღუპა.",
			"30": "<sup>30</sup> ასევე იქნება იმ დღესაც, როცა ძე კაცისა გამოჩნდება.",
			"31": "<sup>31</sup> იმ დღეს, ვინც ერდოზე იქნება, ხოლო მისი ნივთები სახლში, ნუ ჩამოვა ასაღებად; ვინც ყანაში იქნება, უკან ნუ გაბრუნდება.",
			"32": "<sup>32</sup> გაიხსენეთ ლოტის ცოლი.",
			"33": "<sup>33</sup> ვინც ცდილობს თავისი სული შეინარჩუნოს, დაღუპავს მას. ხოლო ვინც დაღუპავს თავის სულს, შეინარჩუნებს მას.",
			"34": "<sup>34</sup> თქვენ გეუბნებით: იმ ღამეს ორნი იქნებიან ერთ საწოლზე: ერთს წაიყვანენ და მეორე დარჩება.",
			"35": "<sup>35</sup> ორნი დაფქვავენ ერთად: ერთს წაიყვნენ, მეორე დარჩება.",
			"36": "<sup>36</sup> ორნი იქნებიან მინდორში: ერთს წაიყვანენ, მეორე დარჩება.“",
			"37": "<sup>37</sup> მიუგეს და უთხრეს მას: „სად, უფალო?“ ხოლო მან უთხრა მათ: „ლეში სადაც არის, იქ მოგროვდებიან ორბები.“"
		},
		"18": {
			"1": "<sup>1</sup> უთხრა მათ იგავი იმის შესახებ, რომ ყოველთვის უნდა ილოცონ და არ მობეზრდეთ.",
			"2": "<sup>2</sup> თქვა: „ერთ ქალაქში იყო ერთი მსაჯული, ღვთისა არ ეშინოდა და ხალხისა არ რცხვენოდა.",
			"3": "<sup>3</sup> იმავე ქალაქში ცხოვრობდა ერთი ქვრივი. მივიდა მასთან და უთხრა: ‘დამიცავი ჩემი მოქიშპისაგან.’",
			"4": "<sup>4</sup> დიდხანს უარობდა. მერე თავისთვის თქვა: ‘მართალია, ღვთისა არ მეშინია და ხალხისა არ მრცხვენია,",
			"5": "<sup>5</sup> მაგრამ, რაკი ეს ქვრივი მოსვენებას არ მაძლევს, დავიცავ, რომ აღარ მოვიდეს და თავი არ მომაბეზროს.’“",
			"6": "<sup>6</sup> თქვა უფალმა: „გესმით, რას ამბობს უსამართლო მსაჯული?",
			"7": "<sup>7</sup> განა ღმერთი არ დაიცავს თავის რჩეულთ, რომელნიც დღედაღამ მას შეღაღადებენ, და არ იქნება სულგრძელი მათდამი?",
			"8": "<sup>8</sup> თქვენ გეუბნებით: მალე დაიცავს მათ. მაგრამ, როცა ძე კაცისა მოვა, ნეტა ჰპოვებს ამქვეყნად რწმენას?“",
			"9": "<sup>9</sup> ზოგიერთებს, ვისაც სჯეროდა, რომ მართალნი იყვნენ, სხვებს კი ამცირებდნენ, უთხრა ეს იგავი:",
			"10": "<sup>10</sup> „ორი კაცი შევიდა ტაძარში სალოცავად – ერთი ფარისეველი და მეორე მებაჟე.",
			"11": "<sup>11</sup> ფარისეველი იდგა და თავისთვის ლოცულობდა: ‘გმადლობ, შენ, ღმერთო, რომ არა ვარ, როგორც სხვა ადამიანები არიან – მძარცველები, უსამართლონი, მრუშნი, ან როგორც ეს მებაჟეა.",
			"12": "<sup>12</sup> კვირაში ორჯერ ვმარხულობ და მეათედს ვიძლევი ყოველი ჩემი შენაძენიდან.’",
			"13": "<sup>13</sup> ხოლო მებაჟე მოშორებით იდგა და ვერც კი ბედავდა, რომ ცისკენ აღეპყრო თვალი. მკერდში მჯიღს იცემდა და ამბობდა: ‘ღმერთო, შემიწყალე მე, ცოდვილი.’",
			"14": "<sup>14</sup> თქვენ გეუბნებით: ეს უფრო გამართლებული წავიდა შინ, ვიდრე იგი, ვინაიდან ყველა, ვინც თავს აიმაღლებს, დამდაბლდება, და ვინც თავს დაიმდაბლებს, ამაღლდება.“",
			"15": "<sup>15</sup> მოუყვანეს მას ყრმებიც, რომ შეხებოდა მათ. ეს რომ მოწაფეებმა დაინახეს, აჩერებდნენ მათ.",
			"16": "<sup>16</sup> იესომ დაუძახა მათ და თქვა: „გამოუშვით ბავშვები და ხელს ნუ უშლით ჩემთან მოსასვლელად, ვინაიდან მაგათნაირებისაა ღვთის სასუფეველი.",
			"17": "<sup>17</sup> ჭეშმარიტად გეუბნებით თქვენ: ვინც ისე არ მიიღებს ღვთის სასუფეველს, როგორც ბავშვი, ვერ შევა შიგ.“",
			"18": "<sup>18</sup> ჰკითხა მას ერთმა მთავართაგანმა და უთხრა: „კეთილო მოძღვარო, რა ვქნა, რომ საუკუნო სიცოცხლე დავიმკვიდრო?“",
			"19": "<sup>19</sup> უთხრა მას იესომ: „რატომ მიწოდებ მე კეთილს? არავინ არის კეთილი, გარდა ერთი ღვთისა.",
			"20": "<sup>20</sup> იცი მცნებანი: არ იმრუშო, არ მოკლა, არ მოიპარო, არ იყო ცრუმოწმე, პატივი ეცი შენს მამას და დედას.“",
			"21": "<sup>21</sup> მან თქვა: „ყოველივე ამას ჩემი სიჭაბუკიდანვე ვიცავდი.“",
			"22": "<sup>22</sup> ეს რომ მოისმინა, იესომ უთხრა: „ერთიღა გაკლია: რაც გაქვს, ყველაფერი გაყიდე და მიეცი ღარიბებს, და გექნება საუნჯე ზეცაში. მოდი და გამომყევი.“",
			"23": "<sup>23</sup> ამის გაგონებაზე იგი დაღონდა, რადგან ძალიან მდიდარი იყო.",
			"24": "<sup>24</sup> შეხედა მას იესომ და თქვა: „რაოდენ ძნელია სიმდიდრის მქონეთა შესვლა ღვთის სასუფეველში!",
			"25": "<sup>25</sup> ვინაიდან უმალ აქლემი გაძვრება ნემსის ყუნწში, ვიდრე მდიდარი შევა ღვთის სასუფეველში.“",
			"26": "<sup>26</sup> მსმენელებმა თქვეს: „ვიღას შეუძლია გადარჩენა?“",
			"27": "<sup>27</sup> მან თქვა: „კაცთათვის შეუძლებელი ღვთისთვის შესაძლებელია.“",
			"28": "<sup>28</sup> თქვა პეტრემ: „აჰა, ჩვენ ყველაფერი მივატოვეთ და შენ გამოგყევით.“",
			"29": "<sup>29</sup> მან უთხრა მათ: „ჭეშმარიტად გეუბნებით თქვენ: არავინ არის, რომ ღვთის სასუფევლის გულისათვის დატოვოს სახლი ან ცოლი, ან მშობლები, ან ძმები, ან შვილები,",
			"30": "<sup>30</sup> და ახლავე არ მიიღოს მრავალგზის მეტი, ხოლო მომავალში – საუკუნო სიცოცხლე.“",
			"31": "<sup>31</sup> წაიყვანა თავისი თორმეტი მოწაფე და უთხრა მათ: „აჰა, ავდივართ იერუსალიმს და აღსრულდება წინასწარმეტყველთა ყოველი დაწერილი კაცის ძეზე.",
			"32": "<sup>32</sup> რადგან წარმართებს გადაეცემა, შეაგინებენ, შეურაცხყოფენ, შეაფურთხებენ,",
			"33": "<sup>33</sup> გაშოლტავენ, მოკლავენ და მესამე დღეს აღდგება.“",
			"34": "<sup>34</sup> მათ კი აქედან ვერაფერი გაიგეს და ეს სიტყვა დაფარული იყო მათთვის და ვერ ხვდებოდნენ ნათქვამს.",
			"35": "<sup>35</sup> იერიხოს რომ მიუახლოვდნენ, ვიღაც ბრმა იჯდა გზაზე და მოწყალებას ითხოვდა.",
			"36": "<sup>36</sup> გამვლელი ხალხის ხმა გაიგონა და იკითხა: „რა ამბავია?“",
			"37": "<sup>37</sup> უთხრეს: იესო ნაზარეველი მოდისო.",
			"38": "<sup>38</sup> მაშინ მან დაიყვირა: „იესო, დავითის ძეო, შემიწყალე!“",
			"39": "<sup>39</sup> წინ მიმავალნი გაუწყრნენ, რომ გაეჩუმებინათ, მაგრამ ის კიდევ უფრო აყვირდა: „დავითის ძეო, შემიწყალე!“",
			"40": "<sup>40</sup> შეჩერდა იესო და ბრძანა, მასთან მოეყვანათ. როცა იგი მიუახლოვდა, ჰკითხა:",
			"41": "<sup>41</sup> „რა გინდა, რომ გაგიკეთო?“ უთხრა: „უფალო, რომ თვალი ამეხილოს.“",
			"42": "<sup>42</sup> იესომ უთხრა მას: „აახილე! შენმა რწმენამ გიხსნა შენ.“",
			"43": "<sup>43</sup> და იმწამსვე აეხილა. მიჰყვებოდა მას და ადიდებდა ღმერთს. მთელი ხალხი ხედავდა ამას და აქებდა ღმერთს."
		},
		"19": {
			"1": "<sup>1</sup> შევიდა და გაიარა იერიხოზე.",
			"2": "<sup>2</sup> და აჰა, იყო კაცი ვინმე, სახელად ზაქე – მებაჟეთა უფროსი და მდიდარი.",
			"3": "<sup>3</sup> სურდა მას იესოს ხილვა, რომ გაეგო, ვინ იყო იგი, მაგრამ ვერ შეძლო ხალხის სიმრავლის გამო, ვინაიდან ტანად დაბალი იყო.",
			"4": "<sup>4</sup> წინ გაიქცა და ლეღვის ხეზე ავიდა, რათა დაენახა, რადგან იესოს აქეთ უნდა გამოევლო.",
			"5": "<sup>5</sup> იმ ადგილას რომ მივიდა, იესომ აიხედა მაღლა და უთხრა: „ზაქე, ჩამოდი სწრაფად, დღეს შენს სახლში უნდა დავრჩე.“",
			"6": "<sup>6</sup> სწრაფად ჩამოვიდა და სიხარულით მიიღო იგი.",
			"7": "<sup>7</sup> ვინც კი დაინახა, ყველანი ბუზღუნს მოჰყვნენ და ამბობდნენ: ცოდვილ კაცთან შევიდაო.",
			"8": "<sup>8</sup> ზაქე წამოდგა და უთხრა უფალს: „აჰა, უფალო, ჩემი ქონების ნახევარს ღარიბებს მივცემ და, თუ ვინმესთვის რამე გამომიძალავს, ოთხმაგად მივაგებ.“",
			"9": "<sup>9</sup> უთხრა მას იესომ: „დღეს მოხდა ამ სახლის ხსნა, რაკი ისიც აბრაამის ძეა.",
			"10": "<sup>10</sup> რადგან ძე კაცისა მოვიდა დაკარგულის მოსაძებნად და სახსნელად.“",
			"11": "<sup>11</sup> როცა ისინი ისმენდნენ ამას, მან დაურთო იგავი, რადგან ახლოს იყო იერუსალიმთან და მათ ეგონათ, უეცრივ გამოჩნდებოდა ღვთის სასუფეველი.",
			"12": "<sup>12</sup> ამის გამო თქვა: „ერთი დიდგვაროვანი კაცი წავიდა შორეულ ქვეყანაში, რომ მეფობა მიეღო და უკან დაბრუნებულიყო.",
			"13": "<sup>13</sup> დაუძახა თავის ათ მონას, მისცა ათი მნა და უთხრა: ვიდრე დავბრუნდებოდე, ივაჭრეთო.",
			"14": "<sup>14</sup> მაგრამ მის მოქალაქეებს სძულდათ იგი და უკან ელჩობა დაადევნეს და დააბარეს: ‘არა გვსურს, რომ მაგან იმეფოს ჩვენზე’.",
			"15": "<sup>15</sup> როცა მეფობა მიიღო და უკან დაბრუნდა, ბრძანა მისთვის მოეგვარათ ის მონები, რომლებსაც ვერცხლი მისცა, რათა გაეგო, ვინ რა ივაჭრა.",
			"16": "<sup>16</sup> მოვიდა პირველი და უთხრა: ‘ბატონო, შენმა მნამ ათი მნა მოიგო’.",
			"17": "<sup>17</sup> უთხრა მას: ‘კარგი, კეთილო მონავ, რაკი მცირედზე ერთგული იყავი, ათ ქალაქზე გქონდეს ხელმწიფება’.",
			"18": "<sup>18</sup> მოვიდა მეორე და უთხრა: ‘ბატონო, შენმა მნამ ხუთი მნა მოიტანა’.",
			"19": "<sup>19</sup> მასაც უთხრა: ‘შენც ხუთ ქალაქზე იყავ’.",
			"20": "<sup>20</sup> მესამე მოვიდა და უთხრა: ‘ბატონო, აჰა, შენი მნა, რომელიც ხელსახოცში მქონდა გამოკრული,",
			"21": "<sup>21</sup> ვინაიდან შემეშინდა, რადგან სასტიკი ხარ: იღებ, რაც არ დაგიდია, და იმკი, რაც არ დაგითესავს’.",
			"22": "<sup>22</sup> უთხრა მას: ‘შენივე პირით განგიკითხავ, ბოროტო მონავ. იცოდი, რომ სასტიკი კაცი ვარ: ვიღებ, რაც არ დამიდია, და ვიმკი, რაც არ დამითესია.",
			"23": "<sup>23</sup> აბა, რაღატომ არ გაუშვი ჩემი ვერცხლი ბრუნვაში, რომ დაბრუნებულს სარგებლითურთ მიმეღო?’",
			"24": "<sup>24</sup> და იქ მდგომთ უთხრა: ‘წაართვით ამას ის ერთი მნა და მიეცით ათი მნის მქონეს’.",
			"25": "<sup>25</sup> მათ უთხრეს: ‘უფალო, მას ათი მნა აქვს’.",
			"26": "<sup>26</sup> ‘გეუბნებით თქვენ, რომ ყოველ მქონეს მიეცემა, ხოლო უქონელს ისიც წაერთმევა, რაცა აქვს.",
			"27": "<sup>27</sup> ხოლო ჩემი მტრები, რომლებსაც არ სურდათ, რომ მათზე მემეფა, მომგვარეთ აქ და დახოცეთ ჩემს წინაშე.’“",
			"28": "<sup>28</sup> თქვა ეს და წინ წავიდა იერუსალიმში ასასვლელად.",
			"29": "<sup>29</sup> როცა მიუახლოვდა ბეთბაგეს და ბეთანიას, მთას, რომელსაც ზეთისხილის მთა ეწოდება, მიავლინა ორი მოწაფე.",
			"30": "<sup>30</sup> უთხრა მათ: „წადით მეზობელ სოფელში. რომ შეხვალთ, იპოვით დაბმულ ჩოჩორს, რომელზეც ჯერ კაცი არ მჯდარა. აუშვით და მოიყვანეთ.",
			"31": "<sup>31</sup> თუ ვინმემ გკითხოთ, რატომ უშვებთო, ასე უთხარით: უფალს სჭირდება-თქო.“",
			"32": "<sup>32</sup> წავიდნენ წარგზავნილები და იპოვეს, როგორც მან უთხრა.",
			"33": "<sup>33</sup> და როცა ჩოჩორს უშვებდნენ, მისმა პატრონებმა უთხრეს: „რატომ უშვებთ ამ ჩოჩორს?“",
			"34": "<sup>34</sup> მათ თქვეს: „იმიტომ, რომ უფალს სჭირდება.“",
			"35": "<sup>35</sup> მიჰგვარეს იგი იესოს, ტანისამოსი დააფინეს ჩოჩორს და ზედ შესვეს იესო.",
			"36": "<sup>36</sup> როცა იგი მიდიოდა, ხალხი თავის ტანისამოსს გზაზე უფენდა.",
			"37": "<sup>37</sup> როცა ზეთისხილის მთის დაღმართს მიუახლოვდა, მოწაფეთა მთელმა სიმრავლემ სიხარულით იწყო ხმამაღლა ღვთის ქება-დიდება ყველა სასწაულისათვის, რაც მათ იხილეს.",
			"38": "<sup>38</sup> ამბობდნენ: „კურთხეულია უფლის სახელით მომავალი მეფე! მშვიდობა ცაზე და დიდება მაღალთა შინა!“",
			"39": "<sup>39</sup> ხალხიდან ზოგიერთმა ფარისეველმა უთხრა მას: „მოძღვარო, გაუწყერი შენს მოწაფეებს.“",
			"40": "<sup>40</sup> მან მიუგო და უთხრა: „თქვენ გეუბნებით, ესენი რომ დადუმდნენ, ქვები იღაღადებენ.“",
			"41": "<sup>41</sup> როცა მიუახლოვდა და დაინახა ქალაქი, ატირდა მის გამო.",
			"42": "<sup>42</sup> და თქვა: „ნეტავ ამ დღეს მაინც გცოდნოდა, რაც სამშვიდობოა შენთვის, მაგრამ ახლა ეს დაფარულია შენს თვალთაგან.",
			"43": "<sup>43</sup> რადგან დაგიდგება დღეები, როცა მტრები შენს წინააღმდეგ აღმართავენ ზვინულს, შემოგერტყმიან და შეგავიწროებენ ყოველი მხრიდან.",
			"44": "<sup>44</sup> მიწასთან გაგასწორებენ შენ და შენს შვილებს შენში. ქვას ქვაზე აღარ დატოვებენ შენში, რადგან ვერ გაიგე შენი მოხედვის ჟამი.“",
			"45": "<sup>45</sup> შევიდა ტაძარში და დაიწყო მოვაჭრეთა გამორეკვა.",
			"46": "<sup>46</sup> უთხრა მათ: „დაწერილია: ‘ჩემი სახლი სამლოცველო სახლი უნდა იყოს.’ თქვენ კი ავაზაკთა ბუნაგად აქციეთ იგი.“",
			"47": "<sup>47</sup> და ასწავლიდა ყოველდღე ტაძარში. ხოლო მღვდელმთავარნი, მწიგნობარნი და ერისმთავარნი ცდილობდნენ მის დაღუპვას.",
			"48": "<sup>48</sup> მაგრამ ვერაფერი მოიგონეს, რა უნდა ექნათ, რადგან მთელი ხალხი მოუშორებლად უსმენდა მას."
		},
		"20": {
			"1": "<sup>1</sup> ერთ დღეს, როცა ის ხალხს ასწავლიდა ტაძარში და ახარებდა, მიუცვივდნენ მღვდელმთავარნი და მწიგნობარნი უხუცესებთან ერთად",
			"2": "<sup>2</sup> და უთხრეს მას: „გვითხარი, რომელი ხელმწიფებით აკეთებ ამას ან ვინ მოგცა შენ ეს ხელმწიფება?“",
			"3": "<sup>3</sup> მიუგო და უთხრა მათ: „მეც გკითხავთ ერთ სიტყვას და მითხარით:",
			"4": "<sup>4</sup> იოანეს ნათლისცემა ზეციდან იყო თუ კაცთაგან?“",
			"5": "<sup>5</sup> ისინი ერთმანეთში მსჯელობდნენ: „თუ ვიტყვით, რომ ზეციდანო, იტყვის: მაშ, რაღატომ არ იწამეთო მისი?",
			"6": "<sup>6</sup> თუ ვიტყვით კაცთაგან, მთელი ხალხი ჩაგვქოლავს, ვინაიდან სჯერათ, რომ იოანე წინასწარმეტყველი იყო.“",
			"7": "<sup>7</sup> და მიუგეს: „არ ვიცით საიდან.“",
			"8": "<sup>8</sup> იესომ უთხრა მათ: „მეც არ გეტყვით, რომელი ხელმწიფებით ვაკეთებ ამას.“",
			"9": "<sup>9</sup> დაუწყო ხალხს ამ იგავის მოყოლა: „ერთმა კაცმა ვაზი ჩაყარა და მიწისმუშაკთ ჩააბარა. თვითონ კი სხვა ქვეყანაში წავიდა კარგა ხნით.",
			"10": "<sup>10</sup> როცა დრო მოვიდა, მონა გაუგზავნა მიწისმუშაკთ, რომ მიეცათ ვენახის ნაყოფთაგან. მაგრამ მიწისმუშაკებმა სცემეს მას და ხელცარიელი გაუშვეს.",
			"11": "<sup>11</sup> კვლავ გაუგზავნა მათ სხვა მონა. მასაც სცემეს, გალანძღეს და ხელცარიელი გაუშვეს.",
			"12": "<sup>12</sup> მერე მესამეც გაგზავნა. დაჭრეს იგი და გააგდეს გარეთ.",
			"13": "<sup>13</sup> მაშინ ვენახის პატრონმა თქვა: ‘როგორ მოვიქცე? ჩემს საყვარელ ძეს გავუგზავნი, იქნებ მისი მოერიდოთ.’",
			"14": "<sup>14</sup> მაგრამ მიწისმუშაკებმა, ის რომ დაინახეს, ერთმანეთში მოითათბირეს და თქვეს: ‘ეს მემკვიდრეა. მოდი, მოვკლათ და მემკვიდრეობა ჩვენ დაგვრჩება.’",
			"15": "<sup>15</sup> გააგდეს ვენახის გარეთ და მოკლეს. რას უზამს მათ ვენახის პატრონი?",
			"16": "<sup>16</sup> მოვა, ამოწყვეტს იმ მიწისმუშაკთ და ვენახს სხვებს ჩააბარებს.“ ვინც მოისმინეს, თქვეს: „ნუმც მოხდება.“",
			"17": "<sup>17</sup> მან კი შეხედა მათ და თქვა: „რას ნიშნავს ეს დაწერილი: ‘ქვა, რომელიც მშენებლებმა დაიწუნეს, ქვაკუთხედად იქცა’?",
			"18": "<sup>18</sup> ყველა, ვინც იმ ქვაზე დაეცემა, დაილეწება. ხოლო ვისაც ის დაეცემა, გასრესს.“",
			"19": "<sup>19</sup> მღვდელმთავარნი და მწიგნობარნი სწორედ ამ დროს შეეცადნენ, რომ ხელი დაედოთ მისთვის, მაგრამ ხალხისა ეშინოდათ, რადგან იცოდნენ, რომ მათზე თქვა ეს იგავი.",
			"20": "<sup>20</sup> უთვალთვალებდნენ მას და მიუგზავნეს მსტოვრები, რომლებიც თავს მართალ ადამიანებად აჩვენებდნენ, რათა სიტყვაში დაეჭირათ და მთავრობისათვის და განმგებლის ხელმწიფებაში გადაეცათ.",
			"21": "<sup>21</sup> ჰკითხეს მას და უთხრეს: „მოძღვარო, ვიცით, რომ სიმართლეს ლაპარაკობ და ასწავლი, არავის მიჰკერძებ, არამედ ჭეშმარიტებით ასწავლი ღვთის გზას.",
			"22": "<sup>22</sup> შეიძლება თუ არა მივცეთ ხარკი კეისარს?“",
			"23": "<sup>23</sup> იგი მიუხვდა მათ მზაკვრობას და უთხრა:",
			"24": "<sup>24</sup> „მიჩვენეთ დინარი: ვისია ეს ხატი და წარწერა?“ თქვეს: კეისრისაო.",
			"25": "<sup>25</sup> უთხრა მათ: „ჰოდა, მიეცით კეისრისა კეისარს და ღვთისა – ღმერთს.“",
			"26": "<sup>26</sup> ვერ შეძლეს მისი სიტყვაში დაჭერა ხალხის წინაშე. გაუკვირდათ მისი პასუხი და დადუმდნენ.",
			"27": "<sup>27</sup> მიუახლოვდა რამდენიმე სადუკეველი, რომლებიც ამბობდნენ, აღდგომა არ არისო, და ჰკითხეს მას:",
			"28": "<sup>28</sup> „მოძღვარო, მოსემ დაგვიწერა: თუ ვინმეს მოუკვდება ძმა, რომელსაც ცოლი ჰყავდა, მაგრამ უშვილოდ გადაეგო, მისმა ძმამ შეირთოს მისი ცოლი და აღუდგინოს თესლი თავის ძმას.",
			"29": "<sup>29</sup> იყვნენ შვიდნი ძმანი. პირველმა შეირთო ცოლი და მოკვდა უშვილოდ.",
			"30": "<sup>30</sup> და მეორემაც",
			"31": "<sup>31</sup> და მესამემაც შეირთეს იგი. შვიდთაგან არცერთს არ დარჩენია შვილები, და დაიხოცნენ.",
			"32": "<sup>32</sup> ბოლოს დედაკაციც მოკვდა.",
			"33": "<sup>33</sup> ჰოდა, აღდგომისას, რომელი ერთის ცოლი იქნება, რადგან შვიდივეს ჰყავდა ცოლად?“",
			"34": "<sup>34</sup> და უთხრა მათ იესომ: „ამ საუკუნის შვილები ირთავენ ცოლებს და თხოვდებიან;",
			"35": "<sup>35</sup> ხოლო ვინც ღირსნი არიან მოიპოვონ ის საუკუნე და მკვდრეთით აღდგომა, არც ცოლს ირთავენ, არც თხოვდებიან,",
			"36": "<sup>36</sup> ვინაიდან აღარც სიკვდილი შეუძლიათ მათ, რაკი ანგელოზთა სწორნი და ღვთის ძენი არიან ისინი, ძენი აღდგომის.",
			"37": "<sup>37</sup> ხოლო მკვდრები რომ აღდგებიან, ეს მოსემაც მიანიშნა მაყვლის ბუჩქით, როცა აბრაამის ღმერთს, ისაკის ღმერთს და იაკობის ღმერთს უწოდა უფალი.",
			"38": "<sup>38</sup> ხოლო ღმერთი მკვდრებისა კი არ არის, არამედ ცოცხლებისა, ვინაიდან მისთვის ყველა ცოცხალია.“",
			"39": "<sup>39</sup> მიუგო ზოგიერთმა მწიგნობარმა და უთხრა: კარგად თქვიო, მოძღვარო.",
			"40": "<sup>40</sup> და ვეღარ გაბედეს, რამე ეკითხათ მისთვის, ხოლო მან უთხრა მათ:",
			"41": "<sup>41</sup> „როგორ ამბობენ, რომ ქრისტე დავითის ძეა?",
			"42": "<sup>42</sup> თვითონ დავითი კი ამბობს წიგნში: ‘უთხრა უფალმა ჩემს უფალს: დაჯექი ჩემს მარჯვნივ,",
			"43": "<sup>43</sup> ვიდრე შენს მტრებს დავამხობდე შენს ფერხთა კვარცხლბეკად.’",
			"44": "<sup>44</sup> აჰა, დავითი უფალს უწოდებს მას და როგორღა არის მისი ძე?“",
			"45": "<sup>45</sup> და როცა მთელი ხალხი უსმენდა, უთხრა თავის მოწაფეებს:",
			"46": "<sup>46</sup> „ერიდეთ მწიგნობრებს, რომლებსაც სურთ გრძელი სამოსლით სიარული და მოედნებზე მოკითხვანი, სინაგოგებში წინა სავარძლები და წინა ადგილები ნადიმებზე;",
			"47": "<sup>47</sup> რომელნიც ჭამენ ქვრივთა სახლებს და მოსაჩვენებლად დიდხანს ლოცულობენ. ისინი უფრო დიდ სასჯელს მიიღებენ.“"
		},
		"21": {
			"1": "<sup>1</sup> აიხედა და დაინახა, რომ საგანძურში ჰყრიდნენ მდიდრები თავიანთ შესაწირავს.",
			"2": "<sup>2</sup> დაინახა აგრეთვე ღარიბი ქვრივი, რომელმაც ორი ლეპტა ჩააგდო.",
			"3": "<sup>3</sup> თქვა: „ჭეშმარიტად გეუბნებით თქვენ, რომ ამ ღარიბმა ქვრივმა ყველაზე მეტი ჩადო,",
			"4": "<sup>4</sup> ვინაიდან ყველამ თავისი მოჭარბებულისგან ჩადო, მან კი თავისი სიდუხჭირისაგან ჩადო მთელი სარჩო, რაც ებადა.“",
			"5": "<sup>5</sup> როცა ზოგიერთები ამბობდნენ ტაძარზე, ძვირფასი ქვებით და შეწირულებებით არისო დამშვენებული, იესომ თქვა:",
			"6": "<sup>6</sup> „დადგება დღეები, როცა ამათგან, რასაც აქ ხედავთ, ქვა ქვაზე არ დარჩება, რომ არ ჩამოიშალოს.“",
			"7": "<sup>7</sup> ისინი ეუბნებოდნენ მას და ეკითხებოდნენ: „მოძღვარო, როდის იქნება ეს და რა იქნება ნიშანი, ეს რომ უნდა მოხდეს?“",
			"8": "<sup>8</sup> მან თქვა: „გაფრთხილდით, რომ არ შეცდეთ, ვინაიდან ბევრი მოვა ჩემი სახელით და იტყვის: მე ვარო და ის ჟამი მოახლოებულიაო. თქვენ ნუ გაჰყვებით მათ.",
			"9": "<sup>9</sup> როცა მოისმენთ ომებსა და შფოთებზე, ნუ შეძრწუნდებით, ვინაიდან ჯერ ეს უნდა მოხდეს, რადგან უცებ არ იქნება აღსასრული.“",
			"10": "<sup>10</sup> მაშინ უთხრა მათ: „აღდგება ხალხი ხალხის წინააღმდეგ და სამეფო – სამეფოს წინააღმდეგ.",
			"11": "<sup>11</sup> იქნება დიდი მიწისძვრანი, ადგილ-ადგილ მოსრვა და შიმშილობანი, საშინელებანი და დიდი ნიშნები ზეციდან.",
			"12": "<sup>12</sup> ამის უწინარეს ხელს დაგადებენ თქვენ და დაგიწყებენ დევნას, გადაგცემენ სინაგოგებსა და საპყრობილეებს, მეფეთა და განმგებელთა წინაშე მიგიყვანენ ჩემი სახელის გამო.",
			"13": "<sup>13</sup> ეს გექნებათ თქვენ დასამოწმებლად.",
			"14": "<sup>14</sup> ამიტომ თქვენს გულებში ჩაიდეთ, რომ წინასწარ არ იზრუნოთ, რითი იმართლოთ თავი,",
			"15": "<sup>15</sup> ვინაიდან მე მოგცემთ თქვენ პირსა და სიბრძნეს, რომელთაც ვერ დაუპირისპირდებიან და ვერც შეეპასუხებიან თქვენი მოწინააღმდეგენი.",
			"16": "<sup>16</sup> ასევე გაცემულ იქნებით მშობლებისა და ძმების, ნათესავებისა და მეგობრების მიერ, ხოლო ზოგიერთ თქვენგანს მოკლავენ.",
			"17": "<sup>17</sup> და ყველა მოგიძულებთ ჩემი სახელის გამო.",
			"18": "<sup>18</sup> მაგრამ ბეწვიც კი არ დაიკარგება თქვენი თავიდან.",
			"19": "<sup>19</sup> მოთმინებით მოიპოვეთ თქვენი სული.",
			"20": "<sup>20</sup> და როცა იხილავთ იერუსალიმს, ლაშქრით გარსშემორტყმულს, მაშინ იცოდეთ, რომ მოახლოვდა მისი აოხრება.",
			"21": "<sup>21</sup> მაშინ იუდეაში მყოფნი მთებში გაიხიზნონ; ვინც იერუსალიმშია, გარეთ გავიდეს; ვინც სოფლად არის, ნუღარ შევა მასში.",
			"22": "<sup>22</sup> ვინაიდან ეს შურისგების დღეებია, რათა აღსრულდეს ყოველივე დაწერილი.",
			"23": "<sup>23</sup> ხოლო, ვაი ორსულთ და მეძუძურთ იმ დღეებში, ვინაიდან დიდი იქნება უბედურება მიწაზე და რისხვა ამ ხალხზე.",
			"24": "<sup>24</sup> დაეცემიან მახვილის პირით, ტყვედ წაასხამენ ყველა ხალხში. იერუსალიმი გათელილი იქნება წარმართთა მიერ, ვიდრე დასრულდება წარმართთა ჟამი.",
			"25": "<sup>25</sup> და იქნება ნიშნები მზეზე და მთვარეზე და ვარსკვლავებზე. ხოლო მიწაზე, ხალხები შეშფოთდებიან თავგზააბნეულნი ზღვის ხმაურისა და ბობოქრობის გამო.",
			"26": "<sup>26</sup> ხალხს გული შეუწუხდება შიშით და მსოფლიოზე მოსავალთა მოლოდინით, ვინაიდან ციური ძალები შეირყევიან.",
			"27": "<sup>27</sup> მაშინ იხილავენ ძეს კაცისას, ღრუბელზე მომავალს, დიადი ძალითა და დიდებით.",
			"28": "<sup>28</sup> ხოლო როცა ამის ახდენა დაიწყება, გაიმართეთ და თავი ასწიეთ, რადგან ახლოვდება თქვენი ხსნა.“",
			"29": "<sup>29</sup> უთხრა მათ იგავი: „შეხედეთ ლეღვს და ყველა ხეს.",
			"30": "<sup>30</sup> როცა ისინი უკვე იფოთლებიან, შეხედავთ და იცით, რომ ახლოსაა ზაფხული.",
			"31": "<sup>31</sup> ასევე თქვენ, როცა იხილავთ ყოველივე ამის ახდენას, იცოდეთ, რომ ახლოსაა ღვთის სასუფეველი.",
			"32": "<sup>32</sup> ჭეშმარიტად გეუბნებით თქვენ: არ გადაივლის ეს მოდგმა, ვიდრე ყველაფერი არ ახდება.",
			"33": "<sup>33</sup> ცა და მიწა გადაივლიან, ჩემი სიტყვები კი არ გადაივლიან.",
			"34": "<sup>34</sup> „გაფრთხილდით, არ დამძიმდეს თქვენი გულები ღრეობით, ლოთობითა და სიცოცხლეზე ზრუნვით, და იმ დღემ არ გიწიოთ უცაბედად,",
			"35": "<sup>35</sup> რადგან ის, ეწევა, როგორც მახე, ყველა მკვიდრს მთელს მიწის პირზე.",
			"36": "<sup>36</sup> მაშ, ყოველჟამს იფხიზლეთ და ილოცეთ, რათა შეგეძლოთ ყოველგვარ ამ მოსახდენთაგან თავის დაღწევა და წარდგომა კაცის ძის წინაშე.“",
			"37": "<sup>37</sup> დღისით იესო ტაძარში იმყოფებოდა და ასწავლიდა. ღამ-ღამობით კი ადიოდა მთაზე, რომელსაც ზეთისხილისა ეწოდება.",
			"38": "<sup>38</sup> მთელი ხალხი დილიდან მიდიოდა მასთან ტაძარში მის მოსასმენად."
		},
		"22": {
			"1": "<sup>1</sup> ახლოვდებოდა პასექად წოდებული უფუარობის დღესასწაული.",
			"2": "<sup>2</sup> და დაეძებდნენ მას მღვდელმთავარნი და მწიგნობარნი, რომ როგორმე მოეკლათ იგი, მაგრამ ხალხისა ეშინოდათ.",
			"3": "<sup>3</sup> შევიდა სატანა იუდაში, ისკარიოტელად წოდებულში, რომელიც ერთი თორმეტთაგანი იყო.",
			"4": "<sup>4</sup> წავიდა, ელაპარაკა მღვედლმთავრებსა და მცველთა უფროსებს, როგორ მიეცა მათთვის იესო.",
			"5": "<sup>5</sup> მათ გაუხარდათ და დათანხმდნენ, მიეცათ მისთვის ვერცხლი.",
			"6": "<sup>6</sup> მანაც აღუთქვა და ეძებდა მარჯვე შემთხვევას, რომ ხალხის დაუსწრებლად მიეცა იგი მათთვის.",
			"7": "<sup>7</sup> დადგა უფუარობის დღე, როცა უნდა დაკლულიყო პასექი.",
			"8": "<sup>8</sup> მიავლინა პეტრე და იოანე და უთხრა: „წადით, მოამზადეთ ჩვენთვის პასექი, რომ ვჭამოთ.“",
			"9": "<sup>9</sup> მათ უთხრეს მას: „სად გინდა, რომ მოვამზადოთ?“",
			"10": "<sup>10</sup> მან უთხრა მათ: „აჰა, ქალაქში შესვლისას შემოგხვდებათ კაცი, რომელსაც წყალი მიაქვს კოკით. მიჰყევით მას იმ სახლამდე, სადაც შევა,",
			"11": "<sup>11</sup> და უთხარით სახლის პატრონს: მოძღვარი გეუბნება: სად არის სასტუმრო ოთახი, რომ ჩემს მოწაფეებთან ერთად ვჭამოო პასექი?",
			"12": "<sup>12</sup> ის გიჩვენებთ დიდ მოფარდაგებულ ზემოთვალს. იქ მოამზადეთ.“",
			"13": "<sup>13</sup> წავიდნენ და იპოვეს, როგორც მან უთხრა, და მოამზადეს პასექი.",
			"14": "<sup>14</sup> როცა მოაღწია ჟამმა, დაჯდა იგი და მოციქულებიც მასთან ერთად.",
			"15": "<sup>15</sup> უთხრა მათ: „სურვილით მსურდა თქვენთან ერთად მეჭამა ეს პასექი ჩემი ტანჯვის წინ.",
			"16": "<sup>16</sup> რადგან გეუბნებით თქვენ, რომ აღარ ვჭამ მას, ვიდრე არ აღსრულდება ღვთის სასუფეველში.“",
			"17": "<sup>17</sup> აიღო სასმისი, მადლი შესწირა და უთხრა მათ: „მიიღეთ და გაიყავით ერთმანეთს შორის.",
			"18": "<sup>18</sup> რადგან გეტყვით თქვენ: ამიერიდან აღარ შევსვამ ვაზის ნაყოფისგან, ვიდრე არ მოვა ღვთის სასუფეველი.“",
			"19": "<sup>19</sup> აიღო პური, მადლი შესწირა, დატეხა, დაურიგა მათ და უთხრა: „ეს ჩემი სხეულია, რომელიც თქვენთვის მიეცემა. ეს გააკეთეთ ჩემს მოსაგონებლად.“",
			"20": "<sup>20</sup> ასევე აიღო სასმისი სერობის შემდეგ და თქვა: „ეს სასმისი არის ახალი აღთქმა ჩემს სისხლში, თქვენთვის რომ იღვრება.",
			"21": "<sup>21</sup> და აჰა, ჩემი გამცემის ხელი ჩემთან ერთად არის ამ სუფრაზე;",
			"22": "<sup>22</sup> რადგან ძე კაცისა მიდის განჩინებისამებრ, მაგრამ ვაი იმ კაცს, ვის მიერაც გაიცემა იგი.“",
			"23": "<sup>23</sup> და მათ დაიწყეს ერთმანეთის გამოკითხვა: რომელი მათგანი გააკეთებდა ამას.",
			"24": "<sup>24</sup> ასევე იყო დავა მათ შორის: რომელი მათგანი უნდა ჩათვლილიყო დიდად.",
			"25": "<sup>25</sup> მან კი უთხრა მათ: „ხალხთა მეფეები ბატონობენ მათზე და მათი ხელმწიფენი მწყალობლებად იწოდებიან.",
			"26": "<sup>26</sup> თქვენ კი ასე როდი ხართ. თქვენს შორის უფროსი უმცროსივით იყოს, ხოლო ვინც უფროსობს – მსახურივით.",
			"27": "<sup>27</sup> ვინაიდან ვინ არის უფრო დიდი: მეინახე თუ მსახური? განა მეინახე არა? მე კი თქვენ შორის ვარ, როგორც მსახური,",
			"28": "<sup>28</sup> თქვენ კი ისინი ხართ, რომელნიც ჩემთან დარჩით ჩემს განსაცდელში.",
			"29": "<sup>29</sup> მეც ისევე აღგითქვით, როგორც მე აღმითქვა ჩემმა მამამ სასუფეველი,",
			"30": "<sup>30</sup> რომ ჭამოთ და სვათ ჩემს სუფრაზე ჩემს სასუფეველში და დასხდეთ ტახტებზე ისრაელის თორმეტი ტომის განსაკითხავად.“",
			"31": "<sup>31</sup> თქვა უფალმა: „სიმონ, სიმონ! აჰა, სატანამ გამოგითხოვათ თქვენ, რომ ხორბალივით გაგცხრილოთ.",
			"32": "<sup>32</sup> მაგრამ მე ვილოცე შენთვის, რომ არ დაილიოს შენი რწმენა და, როცა კვლავ მოიქცევი, გაამხნევე შენი ძმები.“",
			"33": "<sup>33</sup> მან კი უთხრა მას: „უფალო, შენთან მე მზად ვარ საპყრობილეშიც წამოვიდე და სასიკვდილოდაც,“",
			"34": "<sup>34</sup> ხოლო მან თქვა: „შენ გეუბნები, პეტრე, მამალი არ იყივლებს დღეს, სანამ სამჯერ არ უარყოფ, რომ მიცნობ.“",
			"35": "<sup>35</sup> უთხრა მათ: „როცა ქისის, გუდის და ფეხსაცმლის გარეშე გგზავნიდით, რაიმე გაკლდათ?“ თქვეს: არაფერიო.",
			"36": "<sup>36</sup> უთხრა მათ: „ახლა კი, ვისაც ქისა აქვს, აიღოს, ასევე გუდა. ხოლო ვისაც არა აქვს, თავისი ტანსაცმელი გაყიდოს და მახვილი იყიდოს.",
			"37": "<sup>37</sup> თქვენ გეუბნებით, რომ ეს დაწერილი ჩემზე უნდა აღსრულდეს: ‘ბოროტმოქმედებთან შეირაცხა.’ რადგან მას, რაც ჩემს შესახებაა, დასასრული აქვს.“",
			"38": "<sup>38</sup> მათ თქვეს: „უფალო, აჰა, აქ ორი მახვილია.“ მან კი უთხრა მათ: „კმარა.“",
			"39": "<sup>39</sup> გამოვიდა იქიდან და, ჩვეულებისამებრ, წავიდა ზეთისხილის მთაზე. მოწაფეებიც გაჰყვნენ მას.",
			"40": "<sup>40</sup> ადგილზე მისვლისთანავე უთხრა მათ: „ილოცეთ, რომ არ ჩაცვივდეთ განსაცდელში.“",
			"41": "<sup>41</sup> თვითონ განშორდა მათ ქვის სასროლ მანძილზე, მუხლი მოიყარა და ლოცულობდა.",
			"42": "<sup>42</sup> ამბობდა: „მამაო! თუ გსურს, ამაცდინე ეს სასმისი, მაგრამ ჩემი ნება კი არა, შენი იყოს.“",
			"43": "<sup>43</sup> ეჩვენა მას ციდან ანგელოზი და აძლიერებდა.",
			"44": "<sup>44</sup> ამ აგონიაში მყოფი უმხურვალესად ლოცულობდა. და გახდა მისი ოფლი, როგორც სისხლის მსხვილი წვეთები, რომლებიც მიწაზე ცვიოდა.",
			"45": "<sup>45</sup> ლოცვის შემდეგ წამოდგა, მივიდა მოწაფეებთან და ნახა ისინი მწუხარებისაგან ჩაძინებულები.",
			"46": "<sup>46</sup> უთხრა მათ: „რას გძინავთ? ადექით, ილოცეთ, რომ არ ჩაცვივდეთ განსაცდელში.“",
			"47": "<sup>47</sup> ჯერ სიტყვა არ დაემთავრებინა, რომ აჰა, მოვიდა ხალხი და მათ წინ მოუძღვის ერთი თორმეტთაგანი, სახელად იუდა, და მივიდა იესოსთან საამბორებლად.",
			"48": "<sup>48</sup> იესომ უთხრა მას: „იუდა, ამბორით გასცემ კაცის ძეს?“",
			"49": "<sup>49</sup> მასთან მყოფებმა რომ დაინახეს, რაც ხდებოდა, უთხრეს: „უფალო, დავკრათ მახვილი?“",
			"50": "<sup>50</sup> ერთმა მათგანმა დასცა მღვდელმთავრის მონას და მარჯვენა ყური ჩამოათალა.",
			"51": "<sup>51</sup> მიუგო იესომ და თქვა: „თავი დაანებეთ, საკმარისია!“ და შეეხო ყურს და გაუმრთელა იგი.",
			"52": "<sup>52</sup> უთხრა იესომ მის წინააღმდეგ მოსულ მღვდელმთავრებს, ტაძრის მცველთა უფროსებს და უხუცესებს: „როგორც ავაზაკის წინააღმდეგ, მახვილებით და კეტებით გამოხვედით ჩემს შესაპყრობად?",
			"53": "<sup>53</sup> ყოველდღე თქვენთან ერთად ვიყავი ტაძარში და ჩემზე ხელი არ აგიმართავთ. მაგრამ ეს თქვენი ჟამია და სიბნელის ხელმწიფება.“",
			"54": "<sup>54</sup> და შეიპყრეს იგი, წაიყვანეს და მღვდელმთავრის სახლში შეიყვანეს. პეტრე კი მოშორებით მიჰყვებოდა.",
			"55": "<sup>55</sup> როცა შუა ეზოში ცეცხლი დაანთეს და გარს შემოუსხდნენ, პეტრეც მათ შორის დაჯდა.",
			"56": "<sup>56</sup> ერთმა მხევალმა იგი ცეცხლთან მჯდომარე რომ დაინახა, დააკვირდა და თქვა: „ესეც იმასთან იყო.“",
			"57": "<sup>57</sup> მაგრამ მან უარყო და თქვა: „დედაკაცო, არ ვიცნობ იმას.“",
			"58": "<sup>58</sup> ცოტა მოგვიანებით სხვამ დაინახა იგი და თქვა: „შენც იმათგანი ხარ.“ მაგრამ პეტრემ უთხრა: „ადამიანო, არა ვარ.“",
			"59": "<sup>59</sup> თითქმის ერთი საათი იყო გასული, რომ ვიღაც სხვამ დაიწყო მტკიცება და ამბობდა: „ჭეშმარიტად, ესეც იმასთან იყო, ვინაიდან გალილეველია.“",
			"60": "<sup>60</sup> თქვა პეტრემ: „ადამიანო, არ ვიცი, რას ამბობ.“ და უცებ, როცა ამას ამბობდა, მამალმა იყივლა.",
			"61": "<sup>61</sup> და მობრუნდა უფალი და შეხედა პეტრეს. და გაახსენდა პეტრეს უფლის სიტყვა, რომ უთხრა: დღეს სანამ მამალი იყივლებდეს, სამჯერ უარმყოფო,",
			"62": "<sup>62</sup> და გავიდა გარეთ და მწარედ ატირდა.",
			"63": "<sup>63</sup> ხოლო კაცები, რომლებსაც შეპყრობილი ჰყავდათ იესო, აგინებდნენ და სცემდნენ მას.",
			"64": "<sup>64</sup> თავი დაუფარეს, სახეში სცემდნენ და ეკითხებოდნენ: „აბა, იწინასწარმეტყველე, ვინ დაგარტყა“?",
			"65": "<sup>65</sup> და კიდევ მრავალ სხვა გმობას ამბობდნენ მის წინააღმდეგ.",
			"66": "<sup>66</sup> როცა გათენდა, შეიკრიბნენ ხალხის უხუცესთა საბჭო, მღვდელმთავარნი და მწიგნობარნი, და მიიყვანეს იგი თავიანთ სინედრიონში",
			"67": "<sup>67</sup> და უთხრეს: „თუ ქრისტე ხარ, გვითხარი.“ უთხრა მათ: „რომ გითხრათ, არ დამიჯერებთ,",
			"68": "<sup>68</sup> და თუ შეგეკითხებით, არ მიპასუხებთ.",
			"69": "<sup>69</sup> ამიერიდან ძე კაცისა იქნება ღვთის ძალის მარჯვნივ მჯდომარე.“",
			"70": "<sup>70</sup> თქვა ყველამ: „მაშ, შენა ხარ ძე ღვთისა?“ მან უთხრა მათ: „თქვენ ამბობთ, რომ მე ვარ.“",
			"71": "<sup>71</sup> მათ თქვეს: „მეტი რაღა მოწმობა გვჭირდება? ვინაიდან ჩვენ თვითონ მოვისმინეთ მისი პირიდან.“"
		},
		"23": {
			"1": "<sup>1</sup> და წამოიშალა მთელი ეს ხალხი და წაიყვანეს იგი პილატესთან.",
			"2": "<sup>2</sup> შეუდგნენ მის დადანაშაულებას და ამბობდნენ: „ჩვენ ვნახეთ, რომ ეს რყვნის ჩვენს ხალხს და კრძალავს კეისრისათვის ხარკის მიცემას, თავის თავზე კი ამბობს ქრისტე მეფე ვარო.“",
			"3": "<sup>3</sup> პილატემ ჰკითხა მას: „შენ ხარ იუდეველთა მეფე?“ ხოლო მან მიუგო მას და თქვა: „შენ ამბობ.“",
			"4": "<sup>4</sup> პილატემ უთხრა მღვდელმთავრებს და ხალხს: „მე ვერავითარ დანაშაულს ვერ ვპოულობ ამ კაცში.“",
			"5": "<sup>5</sup> მაგრამ ისინი დაჟინებით ამბობდნენ: „ეგ აშფოთებს ხალხს, ასწავლის მთელ იუდეაში, გალილეიდან მოყოლებული აქამდე.“",
			"6": "<sup>6</sup> პილატემ ეს რომ გაიგონა, იკითხა: „განა გალილეველია ეს კაცი?“",
			"7": "<sup>7</sup> როცა შეიტყო, რომ ჰეროდეს სამფლობელოდან იყო, გაუგზავნა იგი ჰეროდეს, რადგან იმ დღეებში ისიც იერუსალიმში იმყოფებოდა.",
			"8": "<sup>8</sup> ჰეროდემ, იესო რომ დაინახა, ძალიან გაუხარდა, რადგან დიდი ხნიდან სურდა მისი ხილვა, რაკი გაგონილი ჰქონდა მასზე და იმედი ჰქონდა, რაიმე სასწაულს ნახავდა მისგან.",
			"9": "<sup>9</sup> ბევრს ეკითხებოდა, მაგრამ იგი არაფერს პასუხობდა.",
			"10": "<sup>10</sup> მღვდელმთავარნი და მწიგნობარნი კი იდგნენ და გაცხარებით ბრალს სდებდნენ მას.",
			"11": "<sup>11</sup> ხოლო ჰეროდე თავის ჯარისკაცებითურთ ამცირებდა მას და დასცინოდა, ჩააცვა ბრწყინვალე სამოსელი და უკან გაუგზავნა პილატეს.",
			"12": "<sup>12</sup> იმ დღეს პილატე და ჰეროდე დამეგობრდნენ, თუმცა ადრე მტრობდნენ ერთიმეორეს.",
			"13": "<sup>13</sup> პილატემ მოუხმო მღვდელმთავრებს, მთავრებს და ხალხს.",
			"14": "<sup>14</sup> და უთხრა მათ: „თქვენ მოიყვანეთ ჩემთან ეს კაცი, როგორც ხალხის გამრყვნელი, და აჰა, მე თქვენს წინაშე გამოვიძიე და ვერავითარი დანაშაული ვერ ვპოვე ამ კაცში, რაშიც თქვენ ადანაშაულებთ მას,",
			"15": "<sup>15</sup> და ვერც ჰეროდემ, რადგან უკან გამოგვიგზავნა იგი. და აჰა, სიკვდილის ღირსი არაფერი ჩაუდენია მას.",
			"16": "<sup>16</sup> ამიტომ ჭკუას ვასწავლი და გავუშვებ.“",
			"17": "<sup>17</sup> მას კი სწორედ ერთი უნდა გაეშვა სადღესასწაულოდ.",
			"18": "<sup>18</sup> მაგრამ მთელი ხალხი აუყვირდა: მოაშორე ეგ და გაგვითავისუფლე ბარაბაო,",
			"19": "<sup>19</sup> რომელიც ჩაგდებული იყო საპყრობილეში ქალაქში მომხდარი რაღაც შფოთისა და მკვლელობისათვის.",
			"20": "<sup>20</sup> პილატემ კვლავ მიმართა მათ, ვინაიდან სურდა იესოს გაშვება.",
			"21": "<sup>21</sup> მაგრამ ისინი გაჰყვიროდნენ: „ჯვარს აცვი, ჯვარს აცვი ეგ.“",
			"22": "<sup>22</sup> მესამედ უთხრა მათ: „რა ბოროტება აქვს ჩადენილი? მე ვერაფერი სასიკვდილო ბრალი ვერ ვპოვე მასში. ამიტომ ჭკუას ვასწავლი და გავუშვებ.“",
			"23": "<sup>23</sup> ისინი კი დაჟინებით ხმამაღლა მოითხოვდნენ, რომ ჯვარს ეცვათ იგი. და იმძლავრა მათმა ხმამ და მღვდელმთავრების ხმამ.",
			"24": "<sup>24</sup> და პილატემ გადაწყვიტა, შეესრულებინა მათი მოთხოვნა:",
			"25": "<sup>25</sup> გაუშვა შფოთისა და მკვლელობისათვის საპყრობილეში ჩაგდებული, რომელსაც ისინი მოითხოვდნენ, იესო კი მათ ნებას გადასცა.",
			"26": "<sup>26</sup> როცა იგი მიჰყავდათ, გზაში დაიჭირეს მინდვრიდან მომავალი ვინმე სიმონ კვირინელი, და აჰკიდეს ჯვარი, რომ ეტარებინა იესოს კვალდაკვალ.",
			"27": "<sup>27</sup> მისდევდნენ მას დიდძალი ხალხი და დედაკაცები, რომლებიც ტიროდნენ და მოთქვამდნენ მის გამო.",
			"28": "<sup>28</sup> იესო მიუბრუნდა მათ და უთხრა: „იერუსალიმის ასულნო, ნუ სტირით ჩემზე, არამედ თქვენ თავზე და თქვენს შვილებზე იტირეთ.",
			"29": "<sup>29</sup> რადგან, აჰა, მოდის დღეები, როცა იტყვიან: ‘ნეტარ არიან უნაყოფონი და საშონი, რომელთაც არ უშვიათ, და ძუძუნი, რომელთაც არ უწოვებიათ!’",
			"30": "<sup>30</sup> მაშინ ეტყვიან მთებს: ‘დაგვემხეთ ზედ!’ და ბორცვებს: ‘დაგვფარეთ ჩვენ!’",
			"31": "<sup>31</sup> ვინაიდან თუ ნედლ ხეს ასე ექცევიან, ხმელს რაღას უზამენ?“",
			"32": "<sup>32</sup> მასთან ერთად სასიკვდილოდ მიჰყავდათ სხვებიც – ორი ავაზაკი.",
			"33": "<sup>33</sup> როცა იმ ადგილას მივიდნენ, რომელსაც თხემის ადგილი ჰქვია, იქ ჯვარს აცვეს ისიც და ბოროტმოქმედნიც – ერთი მის მარჯვნივ და ერთი მის მარცხნივ.",
			"34": "<sup>34</sup> იესო ამბობდა: „მამაო, მიუტევე ამათ, რადგან არ იციან, რას აკეთებენ.“ და გაიყვეს მისი სამოსელი წილისყრით.",
			"35": "<sup>35</sup> იდგა ხალხი და შეჰყურებდა. მთავრებიც დასცინოდნენ და ამბობდნენ: „სხვებს იხსნიდა, ახლა თავისი თავი იხსნას, თუ ეგ ქრისტეა, ღვთის რჩეული.“",
			"36": "<sup>36</sup> ასევე ჯარისკაცებიც დასცინოდნენ მას, მიდიოდნენ და ძმარი მიჰქონდათ მასთან,",
			"37": "<sup>37</sup> ეუბნებოდნენ: „თუ იუდეველთა მეფე ხარ, იხსენი შენი თავი.“",
			"38": "<sup>38</sup> მის ზემოთ იყო წარწერა ბერძნული, რომაული და ებრაული სიტყვებით დაწერილი: „ეს არის იუდეველთა მეფე.“",
			"39": "<sup>39</sup> ერთი ჩამოკიდებული ბოროტმოქმედი გმობდა მას: „განა შენ ქრისტე არა ხარ? იხსენი შენი თავი და ჩვენც.“",
			"40": "<sup>40</sup> მიუგო მეორემ, გაუწყრა და თქვა: „ნუთუ ღვთისა არ გეშინია, როცა შენც იმავე სასჯელში ხარ?",
			"41": "<sup>41</sup> ჩვენ სამართლიანად დავიმსახურეთ, ვინაიდან ჩვენ რაც გავაკეთეთ, იმისი საკადრისი მივიღეთ, მაგას კი არაფერი უჯერო არ ჩაუდენია.“",
			"42": "<sup>42</sup> და თქვა: „იესო, გამიხსენე, როცა შენს სასუფეველში მიხვალ.“",
			"43": "<sup>43</sup> და უთხრა მას: „ჭეშმარიტად გეუბნები შენ: დღესვე ჩემთან ერთად იქნები სამოთხეში.“",
			"44": "<sup>44</sup> დღის ექვსი საათი იქნებოდა და მთელი მიწა ბნელმა მოიცვა ცხრა საათამდე.",
			"45": "<sup>45</sup> დაბნელდა მზე და ტაძრის ფარდა შუაზე გაიპო.",
			"46": "<sup>46</sup> და შესძახა იესომ დიდი ხმით და თქვა: „მამაო, შენს ხელთ გაბარებ ჩემს სულს.“ ეს თქვა და სული განუტევა.",
			"47": "<sup>47</sup> ასისთავმა რომ იხილა მომხდარი, ღმერთი ადიდა და თქვა: „ჭეშმარიტად, ეს კაცი მართალი იყო.“",
			"48": "<sup>48</sup> ამ სანახაობაზე შეკრებილი მთელი ხალხი, რომ ხედავდა, რაც ხდებოდა, გულში მჯიღის ცემით ბრუნდებოდა უკან.",
			"49": "<sup>49</sup> ყველა, ვინც მას იცნობდა, და დედაკაცები, გალილეიდან რომ მოჰყვნენ, მოშორებით იდგნენ და უყურებდნენ ამას.",
			"50": "<sup>50</sup> აჰა, ერთი კაცი, სახელად იოსები, საბჭოს წევრი, კეთილი და მართალი კაცი,",
			"51": "<sup>51</sup> რომელიც არ დაეთანხმა მათ თათბირსა და საქმიანობას, იუდეველთა ქალაქ არიმათიიდან, რომელიც მოელოდა ღვთის სასუფეველს,",
			"52": "<sup>52</sup> მივიდა პილატესთან და გამოითხოვა იესოს გვამი.",
			"53": "<sup>53</sup> გარდამოხსნა იგი, გაახვია ტილოში და დაასვენა კლდეში გამოკვეთილ სამარხში, სადაც ჯერ კიდევ არავინ დაესვენებინათ.",
			"54": "<sup>54</sup> ის დღე პარასკევი იყო და შაბათი დგებოდა.",
			"55": "<sup>55</sup> უკან გაჰყვნენ დედაკაცებიც, რომელნიც იესოსთან ერთად მოვიდნენ გალილეიდან, და უყურებდნენ სამარხს, როგორ დაიდო მისი გვამი.",
			"56": "<sup>56</sup> ამის შემდეგ დაბრუნდნენ, მოამზადეს სურნელებანი და ნელსაცხებელი. შაბათს კი მოისვენეს მცნებისამებრ."
		},
		"24": {
			"1": "<sup>1</sup> ერთშაბათს, უთენია მოვიდნენ სამარხთან და მოიტანეს სურნელებანი, რომლებიც მომზადებული ჰქონდათ.",
			"2": "<sup>2</sup> და ნახეს ლოდი, სამარხიდან გადაგორებული.",
			"3": "<sup>3</sup> მაგრამ რომ შევიდნენ, ვერ ნახეს უფალ იესოს გვამი.",
			"4": "<sup>4</sup> ამის გამო შეფიქრიანდნენ და, აჰა, მათ წინაშე წარსდგა ორი მამაკაცი, ბრწყინვალე სამოსლით მოსილი.",
			"5": "<sup>5</sup> რაკი შეძრწუნდნენ და სახე მიწად დახარეს, მათ უთხრეს: „რად ეძებთ ცოცხალს მკვდრებს შორის?",
			"6": "<sup>6</sup> არ არის აქ, არამედ აღდგა. გაიხსენეთ, რას გეუბნებოდათ, როცა ჯერ კიდევ გალილეაში იყო,",
			"7": "<sup>7</sup> რომ ძე კაცისა ცოდვილ კაცთა ხელთ უნდა გადაეცეს, ჯვარს ეცვას და მესამე დღეს აღდგესო.“",
			"8": "<sup>8</sup> და მათ გაიხსენეს მისი სიტყვები.",
			"9": "<sup>9</sup> დაბრუნდნენ სამარხიდან და ყოველივე ეს უამბეს იმ თერთმეტს და ყველა დანარჩენს.",
			"10": "<sup>10</sup> ესენი იყვნენ მარიამ მაგდალელი, იოანა და იაკობის დედა მარიამი, და დანარჩენები მათთან ერთად, რომლებმაც მოუთხრეს მოციქულებს ეს ამბავი.",
			"11": "<sup>11</sup> მაგრამ ეს სიტყვები ფუჭად ეჩვენათ და არ დაუჯერეს მათ.",
			"12": "<sup>12</sup> ხოლო პეტრე ადგა, გაიქცა სამარხისკენ და, რომ დაიხარა, მხოლოდ ტილოები დაინახა, და დაბრუნდა მომხდარის გამო გაკვირვებული.",
			"13": "<sup>13</sup> და აჰა, იმავე დღეს ორი მათგანი მიდიოდა ერთ სოფელში, რომელიც სამოცი სტადიონით იყო დაშორებული იერუსალიმიდან და ერქვა ემაუსი.",
			"14": "<sup>14</sup> ისინი ერთმანეთში ლაპარაკობდნენ ყოველივე ამის გამო.",
			"15": "<sup>15</sup> ამ ლაპარაკსა და მსჯელობაში რომ იყვნენ, თავად იესო მიუახლოვდა და დაემგზავრა მათ.",
			"16": "<sup>16</sup> მაგრამ თვალები დაბმული ჰქონდათ და ვერ იცნეს იგი.",
			"17": "<sup>17</sup> უთხრა მათ: „რაზე მსჯელობდით გზაზე ერთმანეთში?“ ისინი შეჩერდნენ დაღონებულნი.",
			"18": "<sup>18</sup> მიუგო ერთმა მათგანმა, სახელად კლეოპამ და უთხრა მას: „ნუთუ ერთი შენა ხარ უცხო იერუსალიმში, რომ არ იცი, რაც იქ ამ დღეებში მოხდა?“",
			"19": "<sup>19</sup> და უთხრა მათ: „რა მოხდა?“ მათ უთხრეს მას: „იესო ნაზარეველზე ვლაპარაკობთ, რომელიც იყო კაცი წინასწარმეტყველი, ძლიერი საქმითა და სიტყვით ღვთისა და მთელი ხალხის წინაშე,",
			"20": "<sup>20</sup> როგორ გადასცეს იგი ჩვენმა მღვდელმთავრებმა და მთავრებმა სიკვდილის მისასჯელად და ჯვარს აცვეს.",
			"21": "<sup>21</sup> ჩვენ კი იმედი გვქონდა, რომ იგი სწორედ ის იყო, ვისაც უნდა გამოეხსნა ისრაელი. ყველაფერ ამასთან ერთად, უკვე მესამე დღეა, რაც ეს მოხდა.",
			"22": "<sup>22</sup> მაგრამ ზოგიერთმა ჩვენიანმა დედაკაცმა გაგვაოცა. ისინი დილაადრიან იყვნენ სამარხთან",
			"23": "<sup>23</sup> და ვერ იპოვეს მისი გვამი. მოვიდნენ და თქვეს, რომ იხილეს ჩვენება ანგელოზთა, რომელთაც უთქვამთ, ცოცხალიაო.",
			"24": "<sup>24</sup> ზოგიერთი ჩვენთაგანიც წავიდა სამარხისაკენ და მართლაც, იგივე დაინახეს, რასაც დედაკაცები ამბობდნენ. ის კი ვერ ნახეს.“",
			"25": "<sup>25</sup> მან უთხრა მათ: „ჰოი, უგუნურნო და გულით მძიმენო, ყველაფრის იმის სარწმუნებლად, რასაც წინასწარმეტყველები ამბობდნენ!",
			"26": "<sup>26</sup> განა ასე არ უნდა დატანჯულიყო ქრისტე და შესულიყო თავის დიდებაში?“",
			"27": "<sup>27</sup> და დაიწყო მოსედან და ყველა წინასწარმეტყველიდან და განუმარტავდა მათ ყოველ წერილში მასზე ნათქვამს.",
			"28": "<sup>28</sup> მიუახლოვდნენ იმ სოფელს, სადაც მიდიოდნენ, ის კი თითქოს გზის გაგრძელებას აპირებდა.",
			"29": "<sup>29</sup> მაგრამ ისინი აკავებდნენ მას და ეუბნებოდნენ: „დარჩი ჩვენთან, ვინაიდან დღე უკვე მიიწურა და მწუხრი ჩამოდგა.“ ისიც შევიდა, რომ დარჩენილიყო მათთან.",
			"30": "<sup>30</sup> როცა მათთან იჯდა, აიღო პური, აკურთხა, გატეხა და მიაწოდა მათ.",
			"31": "<sup>31</sup> მაშინ თვალი აეხილათ და იცნეს. მაგრამ ის უჩინარი შეიქნა მათთვის.",
			"32": "<sup>32</sup> უთხრეს ერთმანეთს: „განა არ გიზგიზებდა ჩვენი გული ჩვენში, როცა გზაში გველაპარაკებოდა და როცა წერილებს განგვიმარტავდა?“",
			"33": "<sup>33</sup> ადგნენ იმავე ჟამს და დაბრუნდნენ იერუსალიმში, მონახეს შეკრებილნი თერთმეტნი და მათთან მყოფნი,",
			"34": "<sup>34</sup> რომელნიც ამბობდნენ, რომ უფალი ჭეშმარიტად აღდგა და ეჩვენა სიმონს.",
			"35": "<sup>35</sup> მათაც უამბეს გზაში მომხდარი და ისიც, როგორ იცნეს იგი პურის გატეხისას.",
			"36": "<sup>36</sup> ამ ლაპარაკში რომ იყვნენ, თვითონ იესო ჩადგა მათ შუა და უთხრა: „მშვიდობა თქვენდა!“",
			"37": "<sup>37</sup> შეძრწუნებულებსა და შეშინებულებს ეგონათ, რომ სულს ხედავდნენ.",
			"38": "<sup>38</sup> მაგრამ მან უთხრა: „რატომ შეძრწუნდით და რატომ გებადებათ გულში ასეთი ფიქრები?",
			"39": "<sup>39</sup> შეხედეთ ჩემს ხელებსა და ჩემს ფეხებს. ეს მე ვარ თვითონ. შემეხეთ მე და ნახეთ, რადგან სულს არა აქვს ხორცი და ძვლები, მე კი მაქვს, როგორც ხედავთ.“",
			"40": "<sup>40</sup> ეს რომ უთხრა, უჩვენა მათ ხელები და ფეხები.",
			"41": "<sup>41</sup> რადგან სიხარულისაგან მათ ჯერ კიდევ არ ეჯერებოდათ და გაკვირვებულნი იყვნენ, უთხრა მათ: „გაქვთ აქ რამე საჭმელი?“",
			"42": "<sup>42</sup> მათ მიაწოდეს მას შემწვარი თევზის ნაჭერი და თაფლის გოლეული.",
			"43": "<sup>43</sup> გამოართვა და შეჭამა მათ წინაშე. ხოლო მორჩენილი აიღო და მისცა მათ.",
			"44": "<sup>44</sup> უთხრა მათ: „ეს არის ჩემი სიტყვები, რომელთაც გეუბნებოდით, ვიდრე თქვენთან ვიყავი: უნდა შესრულდეს ყოველივე, რაც დაწერილია ჩემზე მოსეს რჯულში, წინასწარმეტყველებში და ფსალმუნებში.“",
			"45": "<sup>45</sup> მაშინ გაუხსნა მათ გონება წერილთა გასაგებად",
			"46": "<sup>46</sup> და უთხრა მათ: „ასე სწერია, რომ ქრისტე უნდა დაიტანჯოს და მკვდრეთით აღდგეს მესამე დღეს;",
			"47": "<sup>47</sup> და მისი სახელით ნაქადაგები უნდა იყოს მონანიება ცოდვათა მისატევებლად ყველა ხალხში, იერუსალიმიდან მოყოლებული.",
			"48": "<sup>48</sup> თქვენ კი ამის მოწმენი ხართ.",
			"49": "<sup>49</sup> და აჰა, მე მოვავლენ თქვენზე მამაჩემის აღთქმულს. თქვენ კი დარჩით ქალაქში, ვიდრე მაღლიდან ძალით შეიმოსებოდეთ.“",
			"50": "<sup>50</sup> და გაიყვანა ისინი ბეთანიამდე და აღაპყრო ხელები, და აკურთხა ისინი.",
			"51": "<sup>51</sup> როგორც კი აკურთხა, განშორდა მათ და ამაღლდა ზეცაში.",
			"52": "<sup>52</sup> მათ თაყვანი სცეს მას და დაბრუნდნენ იერუსალიმში დიდი სიხარულით.",
			"53": "<sup>53</sup> და სულმუდამ ტაძარში იყვნენ, ღვთის მადიდებელნი და მაკურთხეველნი. ამინ."
		}
	}
};
module.exports = book;