var book = {
	"name": "Levitų",
	"numChapters": 27,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Viešpats, pasišaukęs Mozę, jam kalbėjo iš Susitikimo palapinės:",
			"2": "<sup>2</sup> “Sakyk izraelitams: ‘Jei kas iš jūsų norės aukoti Viešpačiui, teaukoja iš galvijų ir avių.",
			"3": "<sup>3</sup> Jei jo deginamoji auka bus iš galvijų bandos, teatveda sveiką patiną prie Susitikimo palapinės įėjimo laisva valia Viešpaties akivaizdon,",
			"4": "<sup>4</sup> teuždeda ranką ant aukojamo gyvulio galvos, kad auka būtų priimta ir jis būtų sutaikintas.",
			"5": "<sup>5</sup> Jis papjaus veršį Viešpaties akivaizdoje, o Aarono sūnūs, kunigai, šlakstys kraują aplink aukurą, esantį ties Susitikimo palapinės įėjimu.",
			"6": "<sup>6</sup> Po to, nulupęs aukai odą, sukapos ją į gabalus.",
			"7": "<sup>7</sup> Aarono sūnūs, kunigai, sukraus malkas ant aukuro ir, užkūrę ugnį,",
			"8": "<sup>8</sup> ant viršaus dės sukapotas aukos dalis, galvą bei taukus,",
			"9": "<sup>9</sup> o vidurius ir kojas nuplaus vandeniu. Kunigas visa tai sudegins ant aukuro; tai yra deginamoji auka­malonus kvapas Viešpačiui.",
			"10": "<sup>10</sup> Jei auka būtų iš smulkesnių gyvulių­avių arba ožių,­jis aukos sveiką patiną.",
			"11": "<sup>11</sup> Papjaus jį aukuro šiauriniame šone Viešpaties akivaizdoje; jo kraują Aarono sūnūs šlakstys ant aukuro ir aplink jį.",
			"12": "<sup>12</sup> Sukapotą gyvulį, galvą ir taukus kunigas uždės ant malkų, kurios degs ant aukuro.",
			"13": "<sup>13</sup> O vidurius ir kojas nuplaus vandeniu, ir kunigas sudegins visa tai ant aukuro. Tai yra deginamoji auka­malonus kvapas Viešpačiui.",
			"14": "<sup>14</sup> Jei deginamoji auka Viešpačiui aukojama iš paukščių, tegu ji bus iš laukinių ir jaunų balandžių.",
			"15": "<sup>15</sup> Kunigas nuneš šią auką prie aukuro, nusuks jai galvą ir sudegins ant aukuro, o kraują išvarvins ant aukuro briaunos.",
			"16": "<sup>16</sup> Gurklį ir plunksnas numes rytinėje aukuro pusėje, kur paprastai pilami pelenai,",
			"17": "<sup>17</sup> sparnus sulaužys, bet neatskirs, ir sudegins ją ant aukure degančių malkų. Tai yra deginamoji auka­malonus kvapas Viešpačiui’ ”."
		},
		"2": {
			"1": "<sup>1</sup> “Jei kas nori aukoti Viešpačiui duonos auką, tegu ji bus iš smulkių miltų, apipiltų aliejumi, ir smilkalų.",
			"2": "<sup>2</sup> Ją atneš Aarono sūnums, kunigams, kurių vienas ims pilną saują smulkių miltų su aliejumi ir visus smilkalus ir kaip atminimą sudegins ant aukuro, kad būtų malonus kvapas Viešpačiui.",
			"3": "<sup>3</sup> Likusi aukos dalis priklausys Aaronui ir jo sūnums kaip švenčiausia aukų Viešpačiui dalis.",
			"4": "<sup>4</sup> Duonos auka, kepta krosnyje, turi būti nerauginti papločiai iš smulkių miltų, sumaišytų su aliejumi, ir nerauginti ragaišiai, aptepti aliejumi.",
			"5": "<sup>5</sup> O jei tavo duonos auka būtų kepta ant skardos, ji turi būti iš smulkių miltų, sumaišytų su aliejumi, be raugo;",
			"6": "<sup>6</sup> ją sulaužysi į gabaliukus ir užpilsi aliejumi: tai yra duonos auka.",
			"7": "<sup>7</sup> O jei duonos auka būtų kepta keptuvėje, ji turi būti smulkių miltų ir sumaišyta su aliejumi.",
			"8": "<sup>8</sup> Aukodamas ją Viešpačiui, paduosi kunigui į rankas.",
			"9": "<sup>9</sup> Dalį aukos jis sudegins atminimui ant aukuro, kad būtų malonus kvapas Viešpačiui,",
			"10": "<sup>10</sup> o kas liks, bus Aarono ir jo sūnų švenčiausia dalis iš aukos Viešpačiui.",
			"11": "<sup>11</sup> Kiekviena duonos auka, aukojama Viešpačiui, turi būti be raugo; raugas ir medus neturi būti deginama kaip auka Viešpačiui.",
			"12": "<sup>12</sup> Jei aukosi pirmųjų vaisių dovanas Viešpačiui, jos nebus dedamos ant aukuro kaip malonus kvapas.",
			"13": "<sup>13</sup> Visas duonos aukas pasūdysi druska; tavo Dievo sandoros druskos neturi pritrūkti duonos aukose. Druską aukosi su kiekviena auka.",
			"14": "<sup>14</sup> Jei aukotum Viešpačiui duonos auką iš pirmųjų javų, dar tebežaliuojančių varpų, sudžiovinsi jas, smulkiai sugrūsi,",
			"15": "<sup>15</sup> užpilsi ant viršaus aliejaus ir pridėsi smilkalų. Tai yra duonos auka.",
			"16": "<sup>16</sup> Kunigas dalį sugrūstų grūdų su aliejumi ir visus smilkalus sudegins: tai bus auka Viešpačiui”."
		},
		"3": {
			"1": "<sup>1</sup> “Jei kas aukotų padėkos auką iš galvijų, teaukoja Viešpačiui sveiką patiną ar patelę.",
			"2": "<sup>2</sup> Jis turi uždėti ranką aukai ant galvos ir papjauti prie Susitikimo palapinės įėjimo, o Aarono sūnūs, kunigai, šlakstys kraują apie aukurą.",
			"3": "<sup>3</sup> Kaip padėkos auką jis aukos Viešpačiui visus taukus, dengiančius vidurius,",
			"4": "<sup>4</sup> abu inkstus su taukais, paslėpsnių taukus ir kepenų tinklelį.",
			"5": "<sup>5</sup> Aarono sūnūs tai sudegins ant aukuro kaip deginamąją auką, kad auka Viešpačiui būtų malonus kvapas.",
			"6": "<sup>6</sup> Jei kas aukotų padėkos auką iš avių, turi atvesti sveiką patiną ar patelę.",
			"7": "<sup>7</sup> Jei kas aukotų Viešpaties akivaizdoje ėriuką,",
			"8": "<sup>8</sup> uždės ranką ant galvos savo aukai, papjaus ją prieš Susitikimo palapinę, o Aarono sūnūs šlakstys jos kraują aplinkui aukurą.",
			"9": "<sup>9</sup> Padėkos auka Viešpačiui bus vidurių taukai, uodega,",
			"10": "<sup>10</sup> inkstai, ant jų esantys taukai, paslėpsnių taukai ir kepenų tinklelis.",
			"11": "<sup>11</sup> Kunigas tai sudegins ant aukuro kaip auką Viešpačiui.",
			"12": "<sup>12</sup> Jei jis aukotų ožką, atves ją Viešpaties akivaizdon,",
			"13": "<sup>13</sup> uždės ranką ant galvos ir papjaus prie Susitikimo palapinės įėjimo; Aarono sūnūs šlakstys jos kraują aplink aukurą.",
			"14": "<sup>14</sup> Viešpaties aukai ims jos vidurių taukus,",
			"15": "<sup>15</sup> abu inkstus su taukais, paslėpsnių taukus ir kepenų tinklelį.",
			"16": "<sup>16</sup> Kunigas visa tai sudegins ant aukuro, kad būtų malonus kvapas Viešpačiui.",
			"17": "<sup>17</sup> Tai amžinas įstatas jūsų kartoms kur jūs begyventumėte: jūs nevalgysite nei kraujo, nei taukų”."
		},
		"4": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk izraelitams: ‘Jei kas dėl nežinojimo sulaužytų Dievo įsakymą ir nusikalstų, padarydamas, kas uždrausta,",
			"3": "<sup>3</sup> tai, jei taip nusidėtų pateptasis kunigas ir užtrauktų kaltę tautai, jis aukos už savo nuodėmę Viešpačiui sveiką veršį.",
			"4": "<sup>4</sup> Jis atves jį prie Susitikimo palapinės durų Viešpaties akivaizdon, uždės ranką ant galvos ir jį papjaus.",
			"5": "<sup>5</sup> Pateptasis kunigas, pasiėmęs veršio kraują, įneš Susitikimo palapinėn.",
			"6": "<sup>6</sup> Padažęs pirštą kraujyje, juo pašlakstys septynis kartus Viešpaties akivaizdoje uždangą prieš Švenčiausiąją",
			"7": "<sup>7</sup> ir tuo pačiu krauju pateps ragus Viešpaties smilkalų aukuro, kuris yra Susitikimo palapinėje; visą likusį kraują išlies deginamųjų aukų aukuro papėdėje prie palapinės.",
			"8": "<sup>8</sup> Už nuodėmę jis aukos veršio vidurių taukus,",
			"9": "<sup>9</sup> abu inkstus, ant jų ir prie paslėpsnių esančius taukus ir kepenų tinklelį.",
			"10": "<sup>10</sup> Visa tai kunigas sudegins ant deginamųjų aukų aukuro.",
			"11": "<sup>11</sup> Odą, visą mėsą su galva ir kojomis, vidurius ir mėšlą",
			"12": "<sup>12</sup> išgabens už stovyklos į nesuteptą vietą, kur paprastai išpilami aukų pelenai, padės ant malkų krūvos ir sudegins toje vietoje, kur išpilami pelenai.",
			"13": "<sup>13</sup> Jei Izraelio tauta dėl nežinojimo nusikalstų Dievui ir nesąmoningai sulaužytų Viešpaties įsakymą, darydama tai, ko neturėtų daryti,",
			"14": "<sup>14</sup> bet vėliau suprastų savo kaltę, aukos už savo nusidėjimą veršį. Jį atves prie Susitikimo palapinės įėjimo.",
			"15": "<sup>15</sup> Tautos vyresnieji uždės rankas ant veršio galvos Viešpaties akivaizdoje ir papjaus veršį Viešpaties akivaizdoje.",
			"16": "<sup>16</sup> Pateptasis kunigas įneš jo kraują Susitikimo palapinėn",
			"17": "<sup>17</sup> ir, padažęs pirštą, septynis kartus šlakstys Viešpaties akivaizdoje prieš uždangą;",
			"18": "<sup>18</sup> ir tuo pačiu krauju pateps Viešpaties akivaizdoje Susitikimo palapinėje esančio aukuro ragus. Likusį kraują išlies deginamųjų aukų aukuro, kuris yra prie Susitikimo palapinės įėjimo, papėdėje.",
			"19": "<sup>19</sup> Veršio taukus sudegins ant aukuro;",
			"20": "<sup>20</sup> padarys visa tai, kas daroma aukojant veršį aukai už nuodėmę. Kunigas juos sutaikys, ir Viešpats jiems atleis.",
			"21": "<sup>21</sup> Patį veršį išgabens už stovyklos ir sudegins kaip anksčiau minėtąjį veršį, nes jis yra auka už tautos nuodėmę.",
			"22": "<sup>22</sup> Jei kunigaikštis nežinodamas sulaužytų kokį nors Viešpaties įstatymą,",
			"23": "<sup>23</sup> bet paskui suprastų savo nuodėmę, jis aukos Viešpačiui sveiką ožį kaip auką už nuodėmę.",
			"24": "<sup>24</sup> Jis uždės ranką ant jo galvos ir jį papjaus toje vietoje, kur paprastai aukojamos deginamosios aukos Viešpaties akivaizdoje, nes tai yra auka už nuodėmę.",
			"25": "<sup>25</sup> Kunigas padažys pirštą aukos kraujyje ir juo pateps deginamųjų aukų aukuro ragus. Likusį kraują išlies jo papėdėje.",
			"26": "<sup>26</sup> Taukus sudegins ant aukuro, kaip tai daroma su padėkos aukomis. Kunigas įvykdys sutaikinimą už jį dėl jo nuodėmės, ir jam bus atleista.",
			"27": "<sup>27</sup> Jei kas iš eilinių žmonių nusidėtų nežinodamas ir padarytų, kas uždrausta Viešpaties įstatymu,",
			"28": "<sup>28</sup> o vėliau suprastų savo nuodėmę, jis aukos sveiką ožką.",
			"29": "<sup>29</sup> Jis uždės ranką ant gyvulio galvos, jį papjaus ir aukos deginamųjų aukų vietoje.",
			"30": "<sup>30</sup> Kunigas, padažęs pirštą aukos kraujyje, juo pateps deginamųjų aukų aukuro ragus, o likusį kraują išlies aukuro papėdėje.",
			"31": "<sup>31</sup> Po to, paėmęs visus taukus, sudegins juos ant aukuro, kaip tai daroma su padėkos aukomis, kad būtų malonus kvapas Viešpačiui. Kunigas sutaikins jį, ir jam bus atleista.",
			"32": "<sup>32</sup> O jei auka už nuodėmę bus iš avių, tai ji turi būti sveika.",
			"33": "<sup>33</sup> Jis uždės ranką ant jos galvos ir papjaus ją toje vietoje, kur paprastai pjaunamos deginamosios aukos.",
			"34": "<sup>34</sup> Kunigas pateps deginamųjų aukų aukuro ragus savo pirštu, padažytu aukos kraujyje. Likusį kraują išlies aukuro papėdėje.",
			"35": "<sup>35</sup> Visus taukus, kaip tai daroma su padėkos auka, sudegins ant aukuro Viešpačiui; taip sutaikins jį su Viešpačiu, ir jo nuodėmė bus jam atleista”."
		},
		"5": {
			"1": "<sup>1</sup> “Jei kas girdi keikimą, bet atsisako liudyti, ką yra matęs ir žino, jis bus kaltas.",
			"2": "<sup>2</sup> Kas paliestų kokį nešvarų daiktą: nešvaraus žvėries, gyvulio ar roplio dvėseną, tačiau to nežinotų, jis bus nešvarus ir kaltas.",
			"3": "<sup>3</sup> Jei kas nežinodamas prisiliestų prie žmogaus kūno nešvarumų, kokie jie bebūtų, kai jis sužinos apie tai, jis bus kaltas.",
			"4": "<sup>4</sup> Jei kas nors prisiekia, neapgalvotai savo lūpomis pasižadėdamas padaryti pikta ar gera,­kas tai bebūtų, ką žmogus patvirtina priesaika,­ir tai liktų paslėpta nuo jo, kai jis tai supras, jis bus kaltas.",
			"5": "<sup>5</sup> Jei žmogus kaltas kuo nors iš šių dalykų, jis turi išpažinti, kad nusidėjo,",
			"6": "<sup>6</sup> ir aukoti auką už kaltę Viešpačiui, už nuodėmę, kuria nusidėjo,­jauną avį ar ožką,­ir kunigas sutaikins jį.",
			"7": "<sup>7</sup> O jei kas negali aukoti avies ar ožkos, teaukoja Viešpačiui du balandžius ar du jaunus karvelius: vieną aukai už nuodėmę, o antrą­deginamajai aukai.",
			"8": "<sup>8</sup> Jis teatneša juos kunigui, kuris, aukodamas pirmąjį už nuodėmę, nusuks jam galvą, tačiau jos visiškai nenutrauks;",
			"9": "<sup>9</sup> jo krauju apšlakstys aukuro šoną, o likusį kraują išvarvins aukuro papėdėje, nes tai auka už nuodėmę.",
			"10": "<sup>10</sup> Antrą sudegins kaip deginamąją auką, kaip paprastai daroma. Kunigas sutaikins jį, ir jo nuodėmė bus jam atleista.",
			"11": "<sup>11</sup> O jei jis negalės duoti dviejų balandžių ar dviejų jaunų karvelių, teaukoja už savo nuodėmę dešimtą dalį efos miltų; tegu nepila į juos aliejaus ir neprideda smilkalų, nes tai auka už nuodėmę.",
			"12": "<sup>12</sup> Jis teatneša juos kunigui, kuris, paėmęs jų pilną saują, sudegins ant aukuro atminimui.",
			"13": "<sup>13</sup> Kunigas įvykdys sutaikinimą už nuodėmę, kuria jis nusidėjo, ir jam bus atleista. Likusią aukos dalį kunigas pasiims kaip duonos auką”.",
			"14": "<sup>14</sup> Ir Viešpats kalbėjo Mozei:",
			"15": "<sup>15</sup> “Jei kas apsirikęs paimtų, kas Viešpačiui paskirta, ir tuo nusidėtų, aukos už savo kaltę sveiką aviną, kokį galima nupirkti už du šekelius pagal šventyklos šekelį.",
			"16": "<sup>16</sup> Jis atlygins padarytą nuostolį ir pridės penktąją dalį viršaus kunigui, kuris sutaikins jį aukodamas aviną, ir jam bus atleista.",
			"17": "<sup>17</sup> Jei kas nusikalstų, nesąmoningai sulaužydamas Viešpaties įstatymą, ir, padaręs nuodėmę, suprastų savo kaltę,",
			"18": "<sup>18</sup> tegu jis ima iš savo bandos sveiką aviną aukai už kaltę ir atiduoda jį kunigui. Ir kunigas sutaikins jį, nes jis nusikalto nežinodamas, ir jam bus atleista.",
			"19": "<sup>19</sup> Tai yra auka už kaltę, kuria jis nusikalto Viešpačiui”."
		},
		"6": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Kas nusidėtų Viešpačiui, nenorėdamas sugrąžinti savo artimui to, ką tas buvo jam patikėjęs ar paskolinęs, arba ką nors prievarta atimtų, arba apgautų savo artimą,",
			"3": "<sup>3</sup> arba, radęs pamestą daiktą, išsigintų, arba melagingai prisiektų, arba padarytų kitą nuodėmę,",
			"4": "<sup>4</sup> kadangi jis nusidėjo ir yra kaltas, turi grąžinti tai, ką atėmė ar apgaule įsigijo, ar buvo gavęs pasaugoti, ar surado,",
			"5": "<sup>5</sup> ar melagingai prisiekė. Jis turi atlyginti ir pridėti penktąją dalį savininkui tą dieną, kai aukoja auką už kaltę.",
			"6": "<sup>6</sup> Ir jis aukos auką Viešpačiui už kaltę, aviną iš bandos, pagal nusikaltimo dydį: tegul atveda jį kunigui,",
			"7": "<sup>7</sup> kuris sutaikins jį su Viešpačiu, ir jam bus atleistas jo nusikaltimas”.",
			"8": "<sup>8</sup> Ir Viešpats kalbėjo Mozei, sakydamas:",
			"9": "<sup>9</sup> “Paskelbk Aaronui ir jo sūnums įstatymą apie deginamąją auką. Deginamoji auka turi pasilikti ant aukuro visą naktį iki ryto­ir aukuro ugnis turi degti.",
			"10": "<sup>10</sup> Kunigas, apsivilkęs drobine jupa ir drobinėmis kelnėmis, išims sudegintos aukos pelenus ir juos supils šalia aukuro.",
			"11": "<sup>11</sup> Po to persirengęs išneš ir išpils pelenus už stovyklos švarioje vietoje.",
			"12": "<sup>12</sup> Ugnis ant aukuro degs visada; ją prižiūrės kunigas, pridėdamas kas rytą malkų, kad ugnis neužgestų. Pirmiausia aukos deginamąją auką, o po to degins padėkos aukų taukus.",
			"13": "<sup>13</sup> Ugnis ant aukuro neužges niekados.",
			"14": "<sup>14</sup> Šis yra duonos aukos įstatymas. Ją aukos Aarono sūnūs Viešpaties akivaizdoje.",
			"15": "<sup>15</sup> Kunigas ims saują smulkių miltų, apšlakstytų aliejumi, bei smilkalus ir sudegins ant aukuro kaip malonų kvapą, kaip atminimą Viešpačiui.",
			"16": "<sup>16</sup> Likusią dalį miltų suvalgys Aaronas su savo sūnumis. Jie tai valgys neraugintą šventoje vietoje, palapinės kieme.",
			"17": "<sup>17</sup> Duona bus nerauginta, nes dalis jos buvo aukojama kaip smilkalai Viešpačiui. Ji yra šventa, kaip ir auka už nuodėmę bei auka už kaltę.",
			"18": "<sup>18</sup> Tik Aarono giminės vyrai ją valgys. Tai amžinas įstatymas jūsų kartoms apie aukas Viešpačiui. Kas prie jų prisilies, tas bus šventas”.",
			"19": "<sup>19</sup> Ir Viešpats kalbėjo Mozei:",
			"20": "<sup>20</sup> “Tai turi aukoti Aaronas ir jo sūnūs Viešpačiui jų patepimo dieną. Dešimtą dalį efos smulkių miltų jie aukos kaip duonos auką­pusę rytą ir pusę vakare.",
			"21": "<sup>21</sup> Juos sumaišys su aliejumi ir iškeps skardoje. Tai duonos auka. Ją aukos, kad būtų malonus kvapas Viešpačiui.",
			"22": "<sup>22</sup> Kunigas, kuris bus pateptas Aarono vietoje, visa sudegins ant aukuro.",
			"23": "<sup>23</sup> Visa duonos auka, kurią aukoja kunigas, bus sudeginta­niekas jos nevalgys”.",
			"24": "<sup>24</sup> Ir Viešpats kalbėjo Mozei:",
			"25": "<sup>25</sup> “Paskelbk Aaronui ir jo sūnums įstatymą apie auką už nuodėmę. Auka turi būti papjauta Viešpaties akivaizdoje toje vietoje, kur pjaunama deginamoji auka; ji yra labai šventa.",
			"26": "<sup>26</sup> Kunigas, kuris ją aukoja, valgys ją šventoje vietoje, palapinės kieme.",
			"27": "<sup>27</sup> Kas palies tos aukos mėsą, bus šventas. Jei aukos krauju būtų aptaškytas apdaras, jis turi būti plaunamas šventoje vietoje.",
			"28": "<sup>28</sup> Jei auką virsi moliniame inde, jį sudaužysi, o jei variniame­jį išvalysi ir vandeniu išplausi.",
			"29": "<sup>29</sup> Kiekvienas kunigų giminės vyras valgys tą mėsą. Ji yra labai šventa.",
			"30": "<sup>30</sup> Tačiau auka už nuodėmę, kurios kraujo dalis įnešama į Susitikimo palapinę sutaikinimui, nebus valgoma; ji bus visa sudeginta”."
		},
		"7": {
			"1": "<sup>1</sup> “Štai aukos už kaltę įstatymas. Ji yra labai šventa.",
			"2": "<sup>2</sup> Kur pjaunama deginamoji auka, ten pjaunama ir auka už kaltę. Jos kraujas bus šlakstomas aplink aukurą.",
			"3": "<sup>3</sup> Aukosite nuo jos visus taukus: uodegą, vidurių taukus,",
			"4": "<sup>4</sup> abu inkstus ir ant jų esančius taukus, paslėpsnių taukus ir kepenis su tinkleliu.",
			"5": "<sup>5</sup> Kunigas visa tai sudegins ant aukuro; tai auka Viešpačiui už kaltę.",
			"6": "<sup>6</sup> Kiekvienas kunigų giminės vyras valgys tos mėsos šventoje vietoje, nes ji yra labai šventa.",
			"7": "<sup>7</sup> Auka už kaltę aukojama taip pat, kaip ir auka už nuodėmę; joms yra vienas įstatymas. Kunigas, kuris atlieka sutaikinimą, gauna ją.",
			"8": "<sup>8</sup> Kunigas, kuris aukoja bet kokio žmogaus deginamąją auką, pasiima sau tos aukos odą.",
			"9": "<sup>9</sup> Kiekviena duonos auka, iškepta krosnyje, ant skardos ar keptuvėje, priklauso kunigui, kuris ją aukoja;",
			"10": "<sup>10</sup> Kiekviena duonos auka, aplieta aliejumi ar sausa, priklauso visiems Aarono sūnums, kaip vienam, taip ir kitam.",
			"11": "<sup>11</sup> Šis yra įstatymas padėkos aukų, kurias aukosite Viešpačiui.",
			"12": "<sup>12</sup> Jei kas, norėdamas atsidėkoti, aukoja dėkojimo auką, kartu su ja teaukoja neraugintus papločius, aplaistytus aliejumi, neraugintus ragaišius, apteptus aliejumi, ir smulkių miltų papločius, aplaistytus aliejumi.",
			"13": "<sup>13</sup> Kartu su papločiais teaukoja raugintą duoną kaip dėkojimo auką.",
			"14": "<sup>14</sup> Vieną iš visos aukos skirs pakėlimo aukai Viešpačiui, ir tai priklausys kunigui, kuris šlakstys padėkos aukos kraują.",
			"15": "<sup>15</sup> Dėkojimo aukos mėsa turi būti suvalgyta tą pačią dieną­nieko iš jos neleistina palikti rytojui.",
			"16": "<sup>16</sup> Įžadų ir laisvos valios auka valgoma tą pačią dieną. Kas lieka, leidžiama valgyti ir kitą dieną.",
			"17": "<sup>17</sup> Kas lieka trečiai dienai­sudeginama.",
			"18": "<sup>18</sup> Jei kas valgytų padėkos aukos mėsą trečią dieną, ji nebūtų priimtina ir nebūtų įskaityta tam, kuris aukoja,­tai pasibjaurėjimas. Kas valgytų, būtų kaltas.",
			"19": "<sup>19</sup> Mėsa, paliesta kuo nors suteptu, nebus valgoma­ji bus sudeginama. Kas nesuteptas, gali valgyti aukos mėsą.",
			"20": "<sup>20</sup> Jei kas, būdamas nešvarus, valgytų padėkos aukos mėsą, aukojamą Viešpačiui, bus išnaikintas iš savo tautos.",
			"21": "<sup>21</sup> Kas, palietęs žmogaus, galvijo ar kokio daikto nešvarumą, valgytų padėkos aukos mėsą, bus išnaikintas iš savo tautos”.",
			"22": "<sup>22</sup> Ir Viešpats kalbėjo Mozei:",
			"23": "<sup>23</sup> “Sakyk izraelitams, kad jie nevalgytų avies, jaučio ir ožkos taukų.",
			"24": "<sup>24</sup> Nustipusio ar žvėries sudraskyto gyvulio taukus naudokite įvairiems reikalams, bet ne valgiui.",
			"25": "<sup>25</sup> Jei kas valgytų gyvulio taukus, kurie turi būti paaukojami Viešpačiui, bus išnaikintas iš savo tautos.",
			"26": "<sup>26</sup> Nevalgykite jokio kraujo­ar jis būtų paukščių, ar gyvulių.",
			"27": "<sup>27</sup> Kiekvienas, kuris valgys kraują, bus išnaikintas iš savo tautos”.",
			"28": "<sup>28</sup> Ir Viešpats kalbėjo Mozei:",
			"29": "<sup>29</sup> “Sakyk Izraelio vaikams: ‘Kas aukoja Viešpačiui padėkos auką, dalį aukos turi atnešti Viešpačiui.",
			"30": "<sup>30</sup> Savo rankomis turi atnešti aukos dalį, skirtą sudeginimui. Taukus ir krūtinę jis privalo atnešti, kad krūtinė būtų siūbuojama Viešpaties akivaizdoje.",
			"31": "<sup>31</sup> Kunigas sudegins taukus ant aukuro, krūtinė teks Aaronui ir jo sūnums.",
			"32": "<sup>32</sup> Dešinysis padėkos aukos petys yra kunigo dalis.",
			"33": "<sup>33</sup> Kuris Aarono sūnus aukos kraują ir taukus, tas gaus dešinį petį.",
			"34": "<sup>34</sup> Nes padėkos aukų krūtinę ir petį paėmiau iš Izraelio vaikų ir atidaviau kunigui Aaronui ir jo sūnums. Tai amžinas nuostatas Izraelio tautai’ ”.",
			"35": "<sup>35</sup> Tai yra pateptojo Aarono ir jo pateptųjų sūnų dalis iš Viešpačiui sudeginamų aukų nuo tos dienos, kai jie buvo paskirti eiti kunigų tarnystę.",
			"36": "<sup>36</sup> Dalis, kurią Viešpats Izraelio vaikams įsakė atiduoti jiems per visas kartas nuo tos dienos, kai jie buvo patepti.",
			"37": "<sup>37</sup> Toks yra deginamųjų, duonos, nuodėmės, kaltės, įšventinimo ir padėkos aukų įstatymas.",
			"38": "<sup>38</sup> Jį Viešpats davė Mozei Sinajaus kalne, kai liepė izraelitams aukoti aukas Viešpačiui Sinajaus dykumoje."
		},
		"8": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Imk Aaroną ir jo sūnus, jų apdarus, patepimo aliejaus, veršį aukai už nuodėmę, du avinus ir pintinę neraugintos duonos",
			"3": "<sup>3</sup> ir sušauk izraelitų susirinkimą prie Susitikimo palapinės įėjimo”.",
			"4": "<sup>4</sup> Mozė padarė, kaip Viešpats liepė. Sušaukęs izraelitus prie palapinės įėjimo,",
			"5": "<sup>5</sup> kalbėjo jiems: “Štai ką Viešpats įsakė padaryti”.",
			"6": "<sup>6</sup> Mozė, pašaukęs Aaroną bei jo sūnus, apiplovė juos vandeniu,",
			"7": "<sup>7</sup> apvilko Aaroną drobine jupa, apjuosė juosta, aprengė mėlyna tunika, uždėjo efodą ir sujuosė jį efodo juosta, kad efodas laikytųsi ant jo,",
			"8": "<sup>8</sup> uždėjo ant efodo krūtinės skydelį, į kurį įdėjo Urimą ir Tumimą.",
			"9": "<sup>9</sup> Galvą pridengė mitra ir ant jos, ties kakta, pritaisė auksinę plokštelę, šventą vainiką, kaip Viešpats įsakė Mozei.",
			"10": "<sup>10</sup> Po to Mozė ėmė patepimo aliejaus, juo patepė palapinę ir visus jos daiktus.",
			"11": "<sup>11</sup> Apšlakstė aukurą aliejumi septynis kartus ir patepė visus jo reikmenis; be to, pašventino ir patepė aliejumi praustuvę ir jos stovą.",
			"12": "<sup>12</sup> Ir užpylė Aaronui ant galvos patepimo aliejaus, kad pateptų ir pašventintų jį.",
			"13": "<sup>13</sup> Atvedęs jo sūnus, apvilko drobinėmis jupomis, sujuosė juostomis ir uždėjo gobtuvus, kaip Viešpats įsakė Mozei.",
			"14": "<sup>14</sup> Tada atvedė veršį aukai už nuodėmę. Aaronas ir jo sūnūs uždėjo aukai ant galvos rankas.",
			"15": "<sup>15</sup> Mozė papjovė jį, paėmęs jo kraujo, pamirkė pirštą ir patepė aukuro ragus; apvalęs ir pašventinęs aukurą, išpylė likusį kraują jo papėdėje;",
			"16": "<sup>16</sup> vidurių taukus, kepenų tinklelį ir abu inkstus su jų taukais sudegino ant aukuro,",
			"17": "<sup>17</sup> o veršio odą, mėsą ir mėšlus sudegino už stovyklos, kaip Viešpats įsakė Mozei.",
			"18": "<sup>18</sup> Mozė atvedė aviną deginamajai aukai. Aaronas ir jo sūnūs uždėjo jam ant galvos rankas.",
			"19": "<sup>19</sup> Mozė jį papjovė ir, jo krauju apšlakstęs aukurą,",
			"20": "<sup>20</sup> sukapojo aviną į gabalus; jo galvą, gabalus, taukus,",
			"21": "<sup>21</sup> nuplautus vidurius ir kojas sudegino. Taip visas avinas buvo sudegintas, nes tai buvo deginamoji malonaus kvapo auka Viešpačiui, kaip Viešpats įsakė Mozei.",
			"22": "<sup>22</sup> Paskui aukojo antrą aviną, įšventinimo aviną. Aaronas ir jo sūnūs uždėjo ant jo galvos rankas,",
			"23": "<sup>23</sup> o Mozė jį papjovė. Jis jo krauju patepė dešinę Aarono ausį, dešinės rankos ir dešinės kojos nykštį.",
			"24": "<sup>24</sup> Tada jis liepė prieiti ir Aarono sūnums. Patepęs papjautojo avino krauju kiekvieno jų dešinę ausį, dešinės rankos ir kojos nykščius, likusį kraują šlakstė aplink aukurą.",
			"25": "<sup>25</sup> Taukus, uodegą, abu inkstus su taukais ir vidurių taukus, kepenų tinklelį ir dešinį petį atskyrė.",
			"26": "<sup>26</sup> Iš neraugintos duonos pintinės, pastatytos Viešpaties akivaizdoje, ėmė neraugintos duonos, aliejumi apšlakstytą paplotį bei ragaišį, padėjo ant taukų ir avino dešiniojo peties",
			"27": "<sup>27</sup> ir viską padavė Aaronui ir jo sūnums; jie visa tai, pasiūbavę Viešpaties akivaizdoje,",
			"28": "<sup>28</sup> grąžino Mozei, kuris tai sudegino ant deginamųjų aukų aukuro. Tai buvo įšventinimo auka, kad būtų malonus aukos kvapas Viešpačiui.",
			"29": "<sup>29</sup> Mozė, pasiūbavęs įšventinimo avino krūtinę Viešpaties akivaizdoje, pasiėmė ją kaip jam Viešpaties skirtą aukos dalį.",
			"30": "<sup>30</sup> Mozė, paėmęs patepimo aliejaus ir buvusio ant aukuro kraujo, šlakstė Aarono ir jo sūnų apdarus, taip pašventindamas Aaroną, jo drabužius, jo sūnus ir sūnų drabužius.",
			"31": "<sup>31</sup> Ir Mozė tarė Aaronui ir jo sūnums: “Virkite mėsą prie Susitikimo palapinės įėjimo ir valgykite ją su duona, kuri yra įšventinimo pintinėje, kaip Viešpats man įsakė.",
			"32": "<sup>32</sup> Tai, kas liks nesuvalgyta,­sudeginkite.",
			"33": "<sup>33</sup> Nuo palapinės įėjimo nesitraukite septynias dienas, kol pasibaigs jūsų pašventinimo laikas, nes jūs turite būti šventinami septynias dienas.",
			"34": "<sup>34</sup> Tai, kas buvo padaryta šiandien, Viešpats įsakė atlikti jūsų sutaikinimui.",
			"35": "<sup>35</sup> Todėl septynias dienas ir septynias naktis pasilikite prie palapinės įėjimo atlikti Viešpaties sargybą, kad nemirtumėte, nes taip man įsakė Viešpats”.",
			"36": "<sup>36</sup> Aaronas ir jo sūnūs darė visa, ką Viešpats įsakė Mozei."
		},
		"9": {
			"1": "<sup>1</sup> Aštuntą dieną Mozė pasišaukė Aaroną, jo sūnus bei Izraelio vyresniuosius",
			"2": "<sup>2</sup> ir tarė Aaronui: “Imk iš bandos veršį aukai už nuodėmę ir aviną deginamajai aukai, abu sveikus, ir juos aukok Viešpačiui.",
			"3": "<sup>3</sup> O Izraelio vaikams sakyk: ‘Imkite aukai už nuodėmę ožį, metinį veršį bei avinėlį deginamajai aukai,",
			"4": "<sup>4</sup> padėkos aukai jautį ir aviną, kad aukotumėte juos Viešpaties akivaizdoje, ir duonos auką, sumaišytą su aliejumi, nes šiandien jums pasirodys Viešpats’ ”.",
			"5": "<sup>5</sup> Jie atgabeno prie Susitikimo palapinės visa, ką Mozė liepė. Ir visa tauta priartėjo ir atsistojo prieš Viešpatį.",
			"6": "<sup>6</sup> Mozė tarė: “Tai padaryti įsakė Viešpats, ir Viešpaties šlovė pasirodys jums”.",
			"7": "<sup>7</sup> Tada Mozė sakė Aaronui: “Eik prie aukuro ir aukok savo auką už nuodėmę ir deginamąją auką, ir atlik sutaikinimą už save, ir aukok tautos auką, ir sutaikink ją, kaip Viešpats įsakė”.",
			"8": "<sup>8</sup> Aaronas priėjo prie aukuro ir papjovė veršį aukai už savo nuodėmę.",
			"9": "<sup>9</sup> Jo sūnūs padavė jam aukos kraujo, o jis, pamirkęs jame pirštą, patepė aukuro ragus ir likusį kraują išliejo prie jo papėdės.",
			"10": "<sup>10</sup> Aukos už nuodėmę taukus, inkstus ir kepenų tinklelį sudegino ant aukuro, kaip Viešpats įsakė Mozei,",
			"11": "<sup>11</sup> o mėsą ir odą sudegino už stovyklos.",
			"12": "<sup>12</sup> Po to papjovė deginamąją auką. Jo sūnūs padavė jam jos kraują, kurį jis šlakstė aplink aukurą.",
			"13": "<sup>13</sup> Padavė jam taip pat sukapotą į gabalus pačią auką ir jos galvą. Visa tai jis sudegino ant aukuro.",
			"14": "<sup>14</sup> Apiplovęs vandeniu vidurius ir kojas, taip pat sudegino ant aukuro.",
			"15": "<sup>15</sup> Po to jis aukojo tautos auką. Paėmęs ožį, aukojo jį už tautos nuodėmę, ir papjovė jį, kaip ir pirmąją auką už nuodėmę.",
			"16": "<sup>16</sup> Ir jis aukojo deginamąją auką, kaip įsakyta.",
			"17": "<sup>17</sup> Ir atnešė duonos auką, ir, paėmęs jos saują, sudegino ant aukuro, neskaitant rytinės deginamosios aukos.",
			"18": "<sup>18</sup> Papjovė taip pat jautį ir aviną kaip tautos padėkos auką. Aarono sūnūs padavė jam kraują, kurį jis šlakstė aplink aukurą.",
			"19": "<sup>19</sup> Jaučio ir avino uodegą, inkstus su taukais bei kepenų tinklelį",
			"20": "<sup>20</sup> padėjo ant krūtinių ir sudegino taukus ant aukuro,",
			"21": "<sup>21</sup> o krūtines ir dešinius pečius Aaronas paėmęs siūbavo Viešpaties akivaizdoje, kaip Mozė buvo įsakęs.",
			"22": "<sup>22</sup> Tada Aaronas, ištiesęs ranką į tautą, palaimino ją. Baigęs aukoti aukas už nuodėmes, deginamąsias ir padėkos aukas, pasitraukė nuo aukuro.",
			"23": "<sup>23</sup> Po to Mozė ir Aaronas įėjo Susitikimo palapinėn. Išėję palaimino tautą. Viešpaties šlovė pasirodė visiems žmonėms.",
			"24": "<sup>24</sup> Atėjusi nuo Viešpaties ugnis prarijo deginamąją auką ir buvusius ant aukuro taukus. Tai išvydusi, minia šaukė ir parpuolė savo veidais į žemę."
		},
		"10": {
			"1": "<sup>1</sup> Aarono sūnūs Nadabas ir Abihuvas ėmė smilkytuvus, įsidėjo ugnies bei smilkalų ir aukojo Viešpačiui svetimą ugnį, ko Jis jiems nebuvo įsakęs.",
			"2": "<sup>2</sup> Tada išsiveržusi iš Viešpaties ugnis juodu prarijo; jie mirė Viešpaties akivaizdoje.",
			"3": "<sup>3</sup> Mozė tarė Aaronui: “Taip kalbėjo Viešpats: ‘Pasirodysiu šventas tuose, kurie prie manęs artinasi, ir būsiu pašlovintas visos tautos akivaizdoje’ ”. Tai girdėdamas, Aaronas tylėjo.",
			"4": "<sup>4</sup> Mozė, pasišaukęs Aarono dėdės Uzielio sūnus Mišaelį ir Elcafaną, jiems tarė: “Eikite, paimkite jūsų brolius iš šventyklos ir išneškite juos už stovyklos”.",
			"5": "<sup>5</sup> Juodu priėję paėmė juos, apvilktus drobinėmis jupomis, ir išnešė laukan, kaip jiems buvo liepta.",
			"6": "<sup>6</sup> Mozė sakė Aaronui ir jo sūnums Eleazarui ir Itamarui: “Nenudenkite savo galvų ir neperplėškite drabužių, kad kartais nemirtumėte ir bausmė nekristų visiems izraelitams. Jūsų broliai ir visi izraelitai teaprauda sudeginimą, kurį Viešpats siuntė.",
			"7": "<sup>7</sup> Jūs nesitraukite nuo palapinės, kad nežūtumėte, kadangi esate patepti šventu aliejumi”. Jie darė visa, ką Mozė įsakė.",
			"8": "<sup>8</sup> Po to Viešpats tarė Aaronui:",
			"9": "<sup>9</sup> “Tu ir tavo sūnūs vyno ir stipraus gėrimo negerkite, eidami Susitikimo palapinėn, kad nemirtumėte; tai yra amžinas įsakymas visoms jūsų kartoms,",
			"10": "<sup>10</sup> kad skirtumėte, kas šventa ir nešventa, kas švaru ir kas nešvaru,",
			"11": "<sup>11</sup> ir galėtumėte mokyti izraelitus visų mano įstatymų, kuriuos daviau per Mozę”.",
			"12": "<sup>12</sup> Mozė kalbėjo Aaronui ir likusiems sūnums Eleazarui bei Itamarui: “Imkite aukos dalį, kuri lieka iš duonos aukos Viešpačiui, ir ją neraugintą valgykite prie aukuro, nes ji šventa.",
			"13": "<sup>13</sup> Valgykite šventoje vietoje, kas tau ir tavo sūnums duota iš aukų Viešpačiui, nes taip man įsakyta;",
			"14": "<sup>14</sup> taip pat krūtinę, kuri buvo siūbuota, ir aukos petį valgykite nesuteptoje vietoje tu, tavo sūnūs ir dukterys, nes tai duota tau ir tavo vaikams iš izraelitų padėkos aukų.",
			"15": "<sup>15</sup> Petys ir krūtinė, kurie buvo atnešti su aukos taukais ir siūbuojami Viešpaties akivaizdoje, priklauso tau. Tai amžinas Viešpaties duotas įstatymas”.",
			"16": "<sup>16</sup> Mozė stropiai ieškojo ožio, kuris buvo aukotas už nuodėmę, ir rado jį sudegintą. Jis, supykęs ant abiejų Aarono sūnų Eleazaro ir Itamaro, paklausė:",
			"17": "<sup>17</sup> “Kodėl nesuvalgėte šventoje vietoje aukos už nuodėmę, kurią Dievas jums atidavė, kad pašalintumėte tautos kaltes ir atliktumėte sutaikinimą už juos Viešpaties akivaizdoje?",
			"18": "<sup>18</sup> Aukos kraujas nebuvo įneštas į šventyklą, jūs turėjote ją valgyti šventoje vietoje, kaip man įsakyta”.",
			"19": "<sup>19</sup> Aaronas atsakė: “Šiandien jie aukojo auką už nuodėmę ir deginamąją auką Viešpaties akivaizdoje, ir man taip atsitiko. Jei aš šiandien valgyčiau auką už nuodėmę, ar tai būtų priimtina Viešpačiui?”",
			"20": "<sup>20</sup> Tai girdėdamas, Mozė priėmė pasiteisinimą."
		},
		"11": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"2": "<sup>2</sup> “Paskelbkite izraelitams: ‘Tai žemės gyvuliai, kuriuos jums leista valgyti.",
			"3": "<sup>3</sup> Galite valgyti kiekvieną gyvulį, kuris turi skeltą nagą ir gromuliuoja;",
			"4": "<sup>4</sup> nevalgysite ir laikysite nešvariu tą, kuris gromuliuoja, bet turi neskeltą nagą kaip kupranugaris. Jis gromuliuoja, bet turi neskeltą nagą, todėl yra nešvarus.",
			"5": "<sup>5</sup> Nešvarus yra barsukas, nes jis gromuliuoja, bet turi neskeltą nagą;",
			"6": "<sup>6</sup> taip pat kiškis, nors jis gromuliuoja, bet turi neskeltą nagą.",
			"7": "<sup>7</sup> Ir kiaulė, nors ji turi skeltą nagą, bet negromuliuoja.",
			"8": "<sup>8</sup> Tų gyvulių mėsos nevalgysite ir neliesite jų maitos. Jie jums yra nešvarūs.",
			"9": "<sup>9</sup> Iš vandens gyvūnų jums leista valgyti visus, kurie turi pelekus ir žvynus, ar jie būtų jūroje, ar upėje, ar tvenkiniuose.",
			"10": "<sup>10</sup> Tais, kurie kruta ir gyvena vandenyje, bet neturi pelekų ir žvynų, jūs bjaurėsitės,",
			"11": "<sup>11</sup> nevalgysite jų mėsos ir nepaliesite jų maitos.",
			"12": "<sup>12</sup> Visi gyviai, kurie gyvena vandenyje ir neturi pelekų ir žvynų, bus jums nešvarūs.",
			"13": "<sup>13</sup> Paukščiai, kurių jūs nevalgysite, bet bjaurėsitės, yra: erelis, grifas, jūros erelis;",
			"14": "<sup>14</sup> peslys ir vanagėlis su visa jo gimine;",
			"15": "<sup>15</sup> visa varnų giminė;",
			"16": "<sup>16</sup> strutis ir pelėda, žuvėdra, vanagas ir jo giminė;",
			"17": "<sup>17</sup> apuokas, kormoranas ir ibis;",
			"18": "<sup>18</sup> gulbė, pelikanas ir gervė;",
			"19": "<sup>19</sup> gandras ir visa jo giminė; taip pat tutlys ir šikšnosparnis.",
			"20": "<sup>20</sup> Visais sparnuotais vabzdžiais, kurie vaikščioja keturiomis kojomis, jūs bjaurėsitės.",
			"21": "<sup>21</sup> Jums leista valgyti tuos keturkojus vabzdžius, kurių paskutinės kojos ilgesnės ir jie šokinėja ant žemės:",
			"22": "<sup>22</sup> visa skėrių giminė ir didieji žiogai su visomis jų giminėmis.",
			"23": "<sup>23</sup> Bet jūs nevalgysite kitų vabzdžių, kurie vaikščioja keturiomis kojomis.",
			"24": "<sup>24</sup> Jei kas paliestų juos negyvus, bus nešvarus iki vakaro;",
			"25": "<sup>25</sup> jei kam reikėtų nešti kurį nors iš jų negyvą, tas plaus savo rūbus ir bus nešvarus iki vakaro.",
			"26": "<sup>26</sup> Kiekvienas gyvulys, kuris turi neskeltą nagą ir negromuliuoja, bus laikomas nešvariu­kas prisiliestų jo maitos, bus nešvarus.",
			"27": "<sup>27</sup> Gyvuliai, kurie turi keturias kojas ir eina letenomis, bus jums nešvarūs­kas prisiliestų prie jų maitos, bus nešvarus iki vakaro;",
			"28": "<sup>28</sup> kas neštų jų maitą, plaus savo drabužius ir bus nešvarus iki vakaro, nes tai yra jums nešvaru.",
			"29": "<sup>29</sup> Iš roplių ir gyvūnų, kurie juda ant žemės, bus laikomi nešvariais šie: žebenkštis, pelė ir krokodilas su visa jo gimine;",
			"30": "<sup>30</sup> laukinė pelė ir chameleonas; salamandra, žaliasis driežas ir kurmis.",
			"31": "<sup>31</sup> Visi jie yra nešvarūs. Kas prisiliestų prie jų negyvų, bus nešvarus iki vakaro.",
			"32": "<sup>32</sup> Jei kas iš jų negyvas užkristų ant ko nors, ar tai būtų medinis indas, ar apdaras, ar kailis, ar ašutinė, ar šiaip kuriam nors reikalui vartojamas daiktas, tai bus sutepta. Jis bus panardintas vandenyje ir laikomas suteptu iki vakaro, po to bus švarus.",
			"33": "<sup>33</sup> Jei kas iš jų įkristų į molinį indą, jis bus suteptas ir turės būti sudaužytas.",
			"34": "<sup>34</sup> Kiekvienas maistas, kurį valgysite, jei ant jo bus užpilta iš tokio indo vandens, bus suteptas; kiekvienas skystis, geriamas iš tokio indo, bus nešvarus;",
			"35": "<sup>35</sup> visa, ant ko užkristų kas nors iš tokios maitos, bus sutepta; krosnis ir katilas turi būti sudaužyti, nes sutepti.",
			"36": "<sup>36</sup> Tik šaltiniai ir šuliniai bus nesutepti; bet kas prisiliestų iš jų išimtos maitos, bus nešvarus.",
			"37": "<sup>37</sup> Jei toks pastipęs gyvis užkristų ant sėklos, jos nesuteps,",
			"38": "<sup>38</sup> bet, jei sėklą kas apipiltų vandeniu ir po to ją paliestų maita, ji bus sutepta.",
			"39": "<sup>39</sup> Jei nustiptų gyvulys, kurį jums leista valgyti, kas jo prisiliestų, bus nešvarus iki vakaro.",
			"40": "<sup>40</sup> Kas valgytų ar neštų ką nors iš jo, plaus savo drabužius ir bus nešvarus iki vakaro.",
			"41": "<sup>41</sup> Visais gyvūnais, kurie šliaužia ant žemės, jūs bjaurėsitės ir jų nevalgysite.",
			"42": "<sup>42</sup> Nevalgysite šliaužiančių ant pilvo, nei ropojančių keturiomis, nei turinčių daugiau kojų, nes jie yra jums pasibjaurėjimas.",
			"43": "<sup>43</sup> Nesusitepkite jais ir nieko iš jų nepalieskite, kad nebūtumėte nešvarūs.",
			"44": "<sup>44</sup> Aš esu Viešpats, jūsų Dievas; būkite šventi, nes Aš esu šventas. Nesusitepkite jokiu ropliu, kuris kruta ant žemės.",
			"45": "<sup>45</sup> Aš esu Viešpats, kuris jus išvedžiau iš Egipto žemės, kad būčiau jūsų Dievas. Būkite šventi, nes Aš esu šventas.",
			"46": "<sup>46</sup> Tai yra įstatymas apie gyvulius, paukščius ir visus gyvius, kurie kruta vandenyje ir gyvena žemėje,",
			"47": "<sup>47</sup> kad žinotumėte skirtumą tarp švaraus ir nešvaraus, kas leista valgyti ir kas neleistina’ ”."
		},
		"12": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Paskelbk izraelitams: ‘Jei moteris pagimdytų berniuką, ji bus septynias dienas nešvari, kaip mėnesinių metu.",
			"3": "<sup>3</sup> Aštuntąją dieną kūdikis bus apipjaustytas,",
			"4": "<sup>4</sup> bet ji pasiliks dar trisdešimt tris dienas apsivalymui. Nieko, kas šventa, ji nepalies ir neis į šventyklą, iki pasibaigs jos apsivalymo dienos.",
			"5": "<sup>5</sup> O jei pagimdytų mergaitę, bus nešvari dvi savaites, kaip mėnesinių metu, ir šešiasdešimt šešias dienas tęsis apsivalymas.",
			"6": "<sup>6</sup> Kai pasibaigs jos apsivalymo dienos už sūnų ar dukterį, atves prie Susitikimo palapinės įėjimo metinį avinėlį deginamajai aukai ir jauną karvelį ar balandį aukai už nuodėmę ir atiduos kunigui,",
			"7": "<sup>7</sup> kuris tai aukos Viešpaties akivaizdoje ir sutaikins ją su Viešpačiu, ir taip ji bus apvalyta. Toks yra įstatymas apie pagimdžiusią berniuką ar mergaitę.",
			"8": "<sup>8</sup> O jei neturėtų iš ko ir negalėtų aukoti avinėlio, ims du balandžius ar du jaunus karvelius­vieną deginimo aukai, o kitą aukai už nuodėmę,­kunigas sutaikins ją, ir ji bus apvalyta’ ”."
		},
		"13": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"2": "<sup>2</sup> “Žmogus, ant kurio kūno odos atsirastų kitokios spalvos taškas ar šašas ar blizganti vieta, turinti raupsų ligos požymį, bus atvestas pas kunigą Aaroną arba pas kurį iš jo sūnų.",
			"3": "<sup>3</sup> Jei tas pamatys šašą ant odos ir pastebės, kad plaukai yra pabalę ir vieta, turinti raupsų požymį, lyginant ją su sveiko kūno oda, yra įdubusi, tai įrodymas, jog tai yra raupsų liga; kunigas apžiūrės jį ir paskelbs nešvariu.",
			"4": "<sup>4</sup> O jei ant odos bus blizgantis baltas taškas, bet nebus įdubęs, lyginant su kita oda, ir plaukai bus pirmykštės spalvos, kunigas uždarys jį septynioms dienoms,",
			"5": "<sup>5</sup> o septintą dieną jį patikrins. Jei tariami raupsai nebus padidėję ir nebus peržengę pirmykščių ribų odoje, uždarys jį dar septynioms dienoms.",
			"6": "<sup>6</sup> Septintą dieną vėl patikrins. Jei taškas bus tamsesnis ir nepadidėjęs, paskelbs žmogų esant švariu, nes tai šašai; jis išplaus savo drabužius ir bus švarus.",
			"7": "<sup>7</sup> Bet, jei po to, kai jį matė kunigas ir pripažino švariu, šašas padidėtų, jis bus pas jį vėl atvestas; kunigas jį patikrins, ir, jei žaizda padidėjusi,",
			"8": "<sup>8</sup> jį paskelbs nešvariu,­tai raupsai.",
			"9": "<sup>9</sup> Jei žmogui atsirastų raupsų žymių, jis bus atvestas pas kunigą.",
			"10": "<sup>10</sup> Tas jį apžiūrės. Jei plaukų spalva bus pabalus ir pasikeitus ir matysis gyva mėsa,",
			"11": "<sup>11</sup> tai ženklas, kad raupsai įsisenėję ir įaugę į odą. Kunigas paskelbs jį nešvariu, bet neuždarys, nes jo nešvarumas aiškus.",
			"12": "<sup>12</sup> Jei raupsai išsiplėstų taip, kad apdengtų visą odą nuo galvos iki kojų, ką galima matyti ir akimis,",
			"13": "<sup>13</sup> kunigas jį apžiūrės ir nutars, kad jo raupsai yra švarūs, nes jie visai pabalo, ir todėl žmogus yra švarus.",
			"14": "<sup>14</sup> O jei pasirodys gyva mėsa,",
			"15": "<sup>15</sup> tada bus kunigo paskelbtas nešvariu, nes gyva mėsa nešvari; tai yra raupsai.",
			"16": "<sup>16</sup> O jei vėl pabals ir padengs visą žmogų,",
			"17": "<sup>17</sup> kunigas jį apžiūrės ir paskelbs švariu.",
			"18": "<sup>18</sup> Jei kam odoje atsirastų votis ir pagytų,",
			"19": "<sup>19</sup> bet voties vietoje pasirodytų baltas ar rausvas randas, toks žmogus bus atvestas pas kunigą.",
			"20": "<sup>20</sup> Šis, jei rastų tariamų raupsų vietą, lyginant su kita oda, įdubusią ir plaukus pabalusius, paskelbs jį nešvariu, nes votyje atsirado raupsų liga.",
			"21": "<sup>21</sup> Bet, jei plaukai bus pirmykštės spalvos, o randas apytamsis ir vieta neįdubusi, uždarys jį septynioms dienoms;",
			"22": "<sup>22</sup> jei po to randas padidėtų, pripažins žmogų nešvariu,­tai raupsai.",
			"23": "<sup>23</sup> O jei bus pasilikęs toje pačioje vietoje, tai ženklas, jog tai tik voties randas,­žmogus švarus.",
			"24": "<sup>24</sup> Jei oda, nudeginta ugnimi, sugijus turėtų baltą ar rausvą randą,",
			"25": "<sup>25</sup> kunigas ją apžiūrės; jei pabalusioji vieta bus įdubusi, paskelbs žmogų nešvariu, nes tai raupsų liga.",
			"26": "<sup>26</sup> Jei plaukų spalva nebus pasikeitusi ir sužeistoji vieta nebus įdubusi, bet bus neryški, uždarys jį septynioms dienoms",
			"27": "<sup>27</sup> ir apžiūrės septintą dieną. Jei dėmė odoje padidėtų, paskelbs jį nešvariu, nes tai raupsai.",
			"28": "<sup>28</sup> O jei baltumas pasiliks savo vietoje ir bus neryškus, tai yra tik nusideginimo žaizda; žmogus bus laikomas švariu.",
			"29": "<sup>29</sup> Jei kuriam vyrui ar moteriai atsirastų raupsai ant galvos ar ant smakro, jį apžiūrės kunigas.",
			"30": "<sup>30</sup> Jei žaizda bus įdubusi, plaukai pageltę ir plonesni negu paprastai, paskelbs nešvariu, nes tai yra galvos ir smakro raupsai.",
			"31": "<sup>31</sup> O jei matys, kad nesveikoji vieta neįdubusi ir plaukai natūralūs, uždarys septynioms dienoms",
			"32": "<sup>32</sup> ir septintą dieną vėl apžiūrės. Jei nesveikoji vieta nebus padidėjusi nė įdubusi, plaukai tos pačios spalvos,",
			"33": "<sup>33</sup> tai žmogaus galva bus nuskusta, išskyrus tą vietą, ir jis bus uždarytas kitoms septynioms dienoms.",
			"34": "<sup>34</sup> Jei septintą dieną pasirodys, kad viskas kaip buvo, paskelbs jį švariu. Jis, išplovęs savo drabužius, bus švarus.",
			"35": "<sup>35</sup> O jei, paskelbus jį švariu, nesveikoji vieta odoje padidės,",
			"36": "<sup>36</sup> kunigas daugiau nebetyrinės, nes aišku­žmogus nešvarus.",
			"37": "<sup>37</sup> Bet, jei nesveikoji vieta nebus padidėjusi ir plaukai bus natūralūs, aišku, kad žmogus pagijęs, ir paskelbs jį švariu.",
			"38": "<sup>38</sup> Jei kuriam vyrui ar moteriai atsirastų balta dėmė odoje,",
			"39": "<sup>39</sup> kunigas jį apžiūrės. Jei blizgantis odoje baltumas yra apytamsis, težino, kad tai ne raupsai, bet baltos spalvos taškas,­žmogus švarus.",
			"40": "<sup>40</sup> Jei kuriam vyrui slenka nuo galvos plaukai, jis tampa plikagalvis, bet yra švarus.",
			"41": "<sup>41</sup> Jei plaukai nuslinko nuo kaktos, jis pasidarė plikakaktis, bet švarus.",
			"42": "<sup>42</sup> O jei nuplikusi galva ar kakta pabalo ar paraudo,",
			"43": "<sup>43</sup> tai kunigas patikrinęs paskelbs jį raupsuotu.",
			"44": "<sup>44</sup> Jis serga raupsais ir yra nešvarus, kunigas paskelbs jį nešvariu; ant jo galvos raupsai.",
			"45": "<sup>45</sup> Raupsuotasis turi persiplėšti drabužį, atidengti galvą, burną laikyti uždengtą ir šaukti: ‘Nešvarus, nešvarus!’",
			"46": "<sup>46</sup> Visą laiką, kol bus raupsuotas ir nešvarus, gyvens vienas už stovyklos.",
			"47": "<sup>47</sup> Jei vilnonis ar drobinis drabužis",
			"48": "<sup>48</sup> ar kailis, ar kas nors padaryta iš kailio",
			"49": "<sup>49</sup> turėtų baltos ar rausvos spalvos taškus, tie rūbai bus laikomi apkrėsti raupsais. Jie bus parodyti kunigui,",
			"50": "<sup>50</sup> kuris, juos apžiūrėjęs, uždarys septynioms dienoms.",
			"51": "<sup>51</sup> Jei kunigas, apžiūrėjęs septintą dieną, atras padidėjusias dėmes, tai bus raupsai. Jis pripažins tą drabužį nešvariu, nes ant jo yra plintantys raupsai.",
			"52": "<sup>52</sup> Todėl jis sudegins tą drabužį, nes tai yra plintantys raupsai.",
			"53": "<sup>53</sup> O jei matys tašką nepadidėjusį,",
			"54": "<sup>54</sup> lieps išplauti tą raupsuotą rūbą ir uždarys jį kitoms septynioms dienoms.",
			"55": "<sup>55</sup> Jei patikrinęs matys, kad jo pirmykštė išvaizda nesugrįžo, nors raupsai ir nepadidėjo, pripažins nešvariu ir sudegins, nes raupsai įsigraužė į apdaro paviršių.",
			"56": "<sup>56</sup> Jei, drabužį išplovus, raupsų vieta bus tamsesnė, ją atplėš ir atskirs nuo drabužio.",
			"57": "<sup>57</sup> Bet, jei dėmės pasirodys tose vietose, kurios pirma buvo švarios, vadinasi, raupsai plinta; tuo atveju drabužis bus sudegintas.",
			"58": "<sup>58</sup> Jei dėmių nebeatsiras, išplaus drabužį antrą kartą ir jis bus švarus.",
			"59": "<sup>59</sup> Tai yra įstatymas apie raupsus vilnoniame ir drobiniame drabužyje, audiniuose ir kailio apdaruose, kada juos pripažinti švariais ir kada nešvariais”."
		},
		"14": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Tai įstatymas raupsuotajam, kai jis paskelbiamas švariu. Jis bus atvestas pas kunigą,",
			"3": "<sup>3</sup> kuris turės išeiti iš stovyklos. Radęs jį išgijusį nuo raupsų,",
			"4": "<sup>4</sup> įsakys jam paimti du sveikus, švarius paukščius, kedro medžio, raudonų siūlų ir yzopo.",
			"5": "<sup>5</sup> Vieną paukštį kunigas lieps papjauti moliniame inde virš tekančio vandens,",
			"6": "<sup>6</sup> kitą gyvą kartu su kedro medžiu, raudonais siūlais ir yzopu padažys papjauto paukščio kraujyje",
			"7": "<sup>7</sup> ir juo pašlakstys septynis kartus apvalomąjį, kad būtų paskelbtas švariu. Gyvą paukštį paleis skristi į laukus.",
			"8": "<sup>8</sup> Po to žmogus išplaus savo drabužius, nusiskus plaukus ir apsiplaus vandeniu; taip apvalytasis įeis į stovyklą, bet savo palapinėn neis dar septynias dienas.",
			"9": "<sup>9</sup> Septintą dieną jis nusiskus galvos plaukus, barzdą ir antakius, dar kartą išplaus darbužius ir nusiplaus kūną.",
			"10": "<sup>10</sup> Aštuntą dieną ims du sveikus avinėlius, metinę avelę, duonos aukai tris dešimtąsias efos smulkių miltų, sumaišytų su aliejumi, ir vieną logą aliejaus.",
			"11": "<sup>11</sup> Apvalymo apeigas atliekąs kunigas pastatys jį ir visa tai Viešpaties akivaizdoje prie Susitikimo palapinės įėjimo.",
			"12": "<sup>12</sup> Po to ims avinėlį ir aukos jį už kaltę; ims taip pat aliejaus logą ir visa tai siūbuos Viešpaties akivaizdoje.",
			"13": "<sup>13</sup> Avinėlį papjaus šventoje vietoje, kur aukojama deginamoji auka ir auka už nuodėmę. Auka už nuodėmę ir auka už kaltę priklauso kunigui ir yra šventa.",
			"14": "<sup>14</sup> Kunigas, ėmęs aukos už kaltę kraują, pateps juo apvalomojo dešinę ausį ir dešinės rankos ir kojos nykščius.",
			"15": "<sup>15</sup> Iš aliejaus logo dalį įsipils į savo kairiosios rankos delną",
			"16": "<sup>16</sup> ir, padažęs jame dešinės rankos rodomąjį pirštą, pašlakstys Viešpaties akivaizdoje septynis kartus.",
			"17": "<sup>17</sup> Likusį kairės rankos delne aliejų išlies ant apvalomojo dešinės ausies, ant dešinės rankos ir kojos nykščių",
			"18": "<sup>18</sup> ir ant jo galvos. Ir kunigas sutaikins jį su Viešpačiu.",
			"19": "<sup>19</sup> Po to kunigas, atlikdamas sutaikinimą, aukos auką už nuodėmę ir deginamąją auką,",
			"20": "<sup>20</sup> padėdamas ją ant aukuro kartu su duonos auka; taip žmogus bus sutaikintas ir apvalytas.",
			"21": "<sup>21</sup> O jei jis neturtingas ir neišgali duoti minėtų dalykų, ims aukai už kaltę avinėlį, kad kunigas sutaikintų jį, dešimtą dalį efos smulkių miltų, sumaišytų su aliejumi, duonos aukai, logą aliejaus",
			"22": "<sup>22</sup> ir du balandžius ar du jaunus karvelius: vieną aukai už nuodėmę, o antrą deginamajai aukai.",
			"23": "<sup>23</sup> Visa tai jis atneš aštuntą savo apsivalymo dieną kunigui prie Susitikimo palapinės įėjimo Viešpaties akivaizdon.",
			"24": "<sup>24</sup> Kunigas, paėmęs avinėlį aukai už kaltę ir aliejų, pasiūbuos tai Viešpaties akivaizdoje.",
			"25": "<sup>25</sup> Papjovęs avinėlį, jo krauju pateps dešinę apvalomojo ausį, dešinės rankos ir kojos nykščius.",
			"26": "<sup>26</sup> Ir kunigas dalį aliejaus įsipils į savo kairės rankos delną,",
			"27": "<sup>27</sup> padažęs dešinės rankos pirštą, pašlakstys septynis kartus Viešpaties akivaizdoje,",
			"28": "<sup>28</sup> pateps dešinę apvalomojo ausį ir dešinės rankos bei kojos nykščius toje vietoje, kur buvo patepta aukos už kaltę krauju.",
			"29": "<sup>29</sup> Likusią aliejaus dalį, esančią rankoje, išpils ant apvalomojo galvos, kad sutaikintų jį su Viešpačiu.",
			"30": "<sup>30</sup> Aukos taip pat vieną balandį ar jauną karvelį,­iš to, ką pajėgs gauti,­",
			"31": "<sup>31</sup> aukai už nuodėmę, o kitą­deginamajai aukai, ir duonos auką, sumaišytą su aliejumi.",
			"32": "<sup>32</sup> Tai auka nepasiturinčio raupsuotojo, kuris neišgali daugiau aukoti savo apsivalymui”.",
			"33": "<sup>33</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"34": "<sup>34</sup> “Jei, įėjus į Kanaano žemę, kurią jums duosiu paveldėti, atsirastų raupsų liga namų sienose,",
			"35": "<sup>35</sup> jų savininkas privalo pranešti kunigui, kad, jo nuomone, raupsų liga yra jo namuose.",
			"36": "<sup>36</sup> Kunigas, prieš patikrindamas namus, ar jie raupsuoti, lieps išnešti iš jų viską, kad visi juose esą daiktai nebūtų sutepti. Paskui įeis ir apžiūrės namų raupsus.",
			"37": "<sup>37</sup> Pamatęs ant sienų įdubusius taškus žalsvos ar rausvos spalvos,",
			"38": "<sup>38</sup> išeis iš namų pro duris ir tuojau juos uždarys septynioms dienoms.",
			"39": "<sup>39</sup> Septintą dieną sugrįžęs, juos apžiūrės, ir, jei ras raupsus padidėjusius,",
			"40": "<sup>40</sup> lieps išlupti akmenis, ant kurių yra raupsai, ir juos išmesti už miesto suteptoje vietoje,",
			"41": "<sup>41</sup> namų vidaus sienas nugramdyti ir nuoskutas išpilti už miesto suteptoje vietoje,",
			"42": "<sup>42</sup> išluptųjų akmenų vietoje įdėti kitus ir namus ištepti moliu.",
			"43": "<sup>43</sup> Jei, išplėšus akmenis, nuskutus sienas ir moliu ištepus,",
			"44": "<sup>44</sup> kunigas pamatys vėl pasirodžiusius raupsus ir sienas taškuotas, reiškia raupsai yra pasilikę ir namai nešvarūs.",
			"45": "<sup>45</sup> Tokius namus sugriaus, o jų akmenis ir medžius išmes suteptoje vietoje už miesto.",
			"46": "<sup>46</sup> Kas įeitų į uždarytus namus, bus nešvarus iki vakaro;",
			"47": "<sup>47</sup> kas juose miegotų ar valgytų, plaus savo drabužius.",
			"48": "<sup>48</sup> Jei kunigas, įėjęs į namus, nepamatys po ištinkavimo išplitusių raupsų, paskelbs namus švariais.",
			"49": "<sup>49</sup> Jiems apvalyti ims du paukščius, kedro medžio ir raudonų siūlų bei yzopo.",
			"50": "<sup>50</sup> Papjovęs vieną paukštį moliniame inde virš tekančio vandens,",
			"51": "<sup>51</sup> ims kedro medį, yzopą, raudonų siūlų ir gyvą paukštį, padažys viską papjauto paukščio kraujyje ir tekančiame vandenyje ir apšlakstys namus septynis kartus.",
			"52": "<sup>52</sup> Paukščio kraujas, tekantis vanduo, kedro medis, yzopas bei raudoni siūlai ir gyvas paukštis apvalys namus.",
			"53": "<sup>53</sup> Gyvą paukštį jis išneš už miesto ir paleis skristi į laukus. Taip namas bus apvalytas.",
			"54": "<sup>54</sup> Tai yra įstatymas apie visokius raupsus ir piktšašius,",
			"55": "<sup>55</sup> raupsus drabužiuose ir namuose;",
			"56": "<sup>56</sup> randus, nušašimus, blizgančius taškus ir įvairius pakitimus,",
			"57": "<sup>57</sup> kad žinotumėte, kas švaru ar nešvaru. Tai yra įstatymas apie raupsus”."
		},
		"15": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"2": "<sup>2</sup> “Paskelbkite izraelitams: ‘Vyras, turįs plūdimą iš savo kūno, yra nešvarus.",
			"3": "<sup>3</sup> Jis bus laikomas nešvariu, jei turi plūdimą iš savo kūno ar plūdimas susilaiko jo kūne, nes tai yra jo nešvara.",
			"4": "<sup>4</sup> Kiekvienas patalas, kur jis miegotų, ir vieta, kur sėdėtų, bus sutepta.",
			"5": "<sup>5</sup> Jei kas iš žmonių prisiliestų prie jo patalo, plaus savo drabužius, pats apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"6": "<sup>6</sup> Jei kas sėdėtų, kur anas sėdėjo, privalės išplauti savo drabužius, pats apsiplauti vandeniu ir bus nešvarus iki vakaro.",
			"7": "<sup>7</sup> Jei kas prisiliestų prie jo kūno, plaus savo drabužius, pats apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"8": "<sup>8</sup> Jei toks žmogus spjautų ant nesutepto, tas plaus savo rūbus, apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"9": "<sup>9</sup> Balnas, ant kurio jis sėdėtų, bus suteptas.",
			"10": "<sup>10</sup> Kas paliestų ką nors, kas buvo po juo, bus nešvarus iki vakaro. Kas neštų tokius daiktus, plaus savo drabužius, pats apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"11": "<sup>11</sup> Prie ko jis prisiliestų nenusiplovęs rankų, tas plaus savo rūbus, apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"12": "<sup>12</sup> Molinis indas, prie kurio prisiliestų suteptasis, turi būti sudaužytas, medinis gi indas bus išplautas vandeniu.",
			"13": "<sup>13</sup> Kai turintis plūdimą iš savo kūno pasveiksta, suskaičiuos septynias dienas apsivalymui, išsiplaus drabužius, nusimaudys tekančiame vandenyje ir bus švarus.",
			"14": "<sup>14</sup> Aštuntą dieną ims du balandžius arba du jaunus karvelius ir, atėjęs Viešpaties akivaizdon prie Susitikimo palapinės įėjimo, paduos juos kunigui.",
			"15": "<sup>15</sup> Tas vieną aukos kaip auką už nuodėmę, o kitą­kaip deginamąją auką ir sutaikins jį Viešpaties akivaizdoje, kad būtų apvalytas nuo ligos.",
			"16": "<sup>16</sup> Vyras, kuriam išsilieja sėkla, apsiplaus vandeniu visą kūną ir bus nešvarus iki vakaro.",
			"17": "<sup>17</sup> Jis plaus vandeniu drabužį ar kailį, ant kurio pateko sėkla, ir visa tai bus sutepta iki vakaro.",
			"18": "<sup>18</sup> Moteris, su kuria jis miegotų, kai jam išsilieja sėkla, apsiplaus vandeniu ir bus nešvari iki vakaro.",
			"19": "<sup>19</sup> Moteris, turinti mėnesines, bus atskirta septynioms dienoms. Kiekvienas, kas prie jos prisiliestų, bus nešvarus iki vakaro.",
			"20": "<sup>20</sup> Ir visa, ant ko ji gulėtų arba sėdėtų tomis dienomis, bus sutepta.",
			"21": "<sup>21</sup> Kas prisiliestų prie jos patalo, plaus savo rūbus, pats apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"22": "<sup>22</sup> Jei kas nors prisiliestų prie bet kurio daikto, ant kurio ji sėdėjo, plaus savo rūbus, pats apsiplaus vandeniu ir bus nešvarus iki vakaro.",
			"23": "<sup>23</sup> Jei kas paliestų tai, kas buvo ant jos patalo ar vietą, kur ji sėdėjo, bus nešvarus iki vakaro.",
			"24": "<sup>24</sup> Jei miegotų su ja vyras ir ant jo patektų jos nešvarumų, jis bus nešvarus septynias dienas; ir patalas, ant kurio jis miegotų, bus irgi suteptas.",
			"25": "<sup>25</sup> Moteris, kraujuojanti ilgesnį laiką ne mėnesinių metu arba kuriai mėnesinės užsitęsia ilgiau negu įprasta, bus nešvari visą tą laiką.",
			"26": "<sup>26</sup> Patalas, ant kurio ji miegotų, ir tai, ant ko ji atsisėstų, bus sutepta.",
			"27": "<sup>27</sup> Kas prie jų prisiliestų, plaus savo rūbus, pats nusiplaus vandeniu ir bus nešvarus iki vakaro.",
			"28": "<sup>28</sup> Jei kraujavimas sustotų, po septynių dienų ji bus švari.",
			"29": "<sup>29</sup> Aštuntą dieną ji paims du balandžius arba du jaunus karvelius ir atneš kunigui prie Susitikimo palapinės įėjimo.",
			"30": "<sup>30</sup> Tas vieną aukos kaip auką už nuodėmę, o kitą­kaip deginamąją auką ir sutaikins ją Viešpaties akivaizdoje dėl plūdimo nešvaros’.",
			"31": "<sup>31</sup> Judu mokysite izraelitus saugotis susitepimo, kad nemirtų dėl savo nešvarumo ir kad nesuteptų mano palapinės.",
			"32": "<sup>32</sup> Tai yra įstatymas tam, kuris turi plūdimą iš savo kūno, tam, kuriam išsilieja sėkla ir jį sutepa,",
			"33": "<sup>33</sup> moteriai, turinčiai mėnesinį plūdimą, vyrui ar moteriai, turintiems plūdimą, ir tam, kuris guli prie nešvarios moters”."
		},
		"16": {
			"1": "<sup>1</sup> Mirus dviem Aarono sūnums kai jie artinosi prie Viešpaties su svetima ugnimi, Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk savo broliui Aaronui, kad neitų bet kada į Švenčiausiąją, už uždangos, prie dangčio, esančio ant skrynios, kad nenumirtų, nes Aš pasirodysiu debesyje virš dangčio.",
			"3": "<sup>3</sup> Prieš įeidamas jis privalo aukoti veršį aukai už nuodėmę ir aviną deginamajai aukai.",
			"4": "<sup>4</sup> Jis apsivilks drobine jupa, užsimaus drobines kelnes, apsijuos drobine juosta, ant galvos užsidės drobinį gobtuvą. Šitie apdarai yra šventi. Prieš apsivilkdamas apsiplaus kūną.",
			"5": "<sup>5</sup> Jis ims iš izraelitų du ožius aukai už nuodėmę ir aviną deginamajai aukai.",
			"6": "<sup>6</sup> Aaronas aukos veršį aukai už savo nuodėmę ir sutaikins save ir savo namus.",
			"7": "<sup>7</sup> Jis pastatys du ožius Viešpaties akivaizdoje prie Susitikimo palapinės įėjimo",
			"8": "<sup>8</sup> ir, mesdamas burtus, skirs vieną Viešpačiui, o antrą­išvarymui.",
			"9": "<sup>9</sup> Kuriam kris burtas Viešpačiui, tą aukos už nuodėmę;",
			"10": "<sup>10</sup> kuris gi skirtas išvarymui, tą statys gyvą Viešpaties akivaizdoje, kad atliktų ant jo sutaikinimą ir išvarytų jį į dykumą.",
			"11": "<sup>11</sup> Kai Aaronas paaukos veršį už savo ir savo šeimos nuodėmę,",
			"12": "<sup>12</sup> jis ims smilkytuvą, kurį pripildys aukuro žarijomis, į saują paims sumaišytų smilkalų ir, įėjęs į Švenčiausiąją,",
			"13": "<sup>13</sup> užpils ant ugnies smilkalų Viešpaties akivaizdoje, kad kvapūs dūmai apdengtų Liudijimo skrynios dangtį ir kad jis nemirtų.",
			"14": "<sup>14</sup> Tuomet ims veršio kraują ir savo pirštu septynis kartus pašlakstys prieš dangtį ir septynis kartus ant jo.",
			"15": "<sup>15</sup> Paskui aukos ožį už tautos nuodėmę, įneš jo kraują už uždangos, pašlakstys juo septynis kartus prieš dangtį ir septynis kartus ant jo, kaip tai darė su veršio krauju.",
			"16": "<sup>16</sup> Taip bus apvalyta Švenčiausioji nuo izraelitų nešvarumo, jų nusikaltimų ir visų nuodėmių. Tą patį darys ir Susitikimo palapinei, kuri stovi tarp jų palapinių.",
			"17": "<sup>17</sup> Nė vieno žmogaus neturi būti Susitikimo palapinėje, kai kunigas įeina į Švenčiausiąją atlikti sutaikinimo dėl savęs, savo šeimos ir viso Izraelio.",
			"18": "<sup>18</sup> Aaronas, išėjęs prie aukuro, esančio kieme, apvalys jį, veršio ir ožio krauju patepdamas aukuro ragus",
			"19": "<sup>19</sup> ir pašlakstydamas aukurą krauju septynis kartus. Taip jį apvalys nuo izraelitų nešvarumo ir pašventins.",
			"20": "<sup>20</sup> Kai baigs Švenčiausiosios, Susitikimo palapinės ir aukuro apvalymą, atves gyvąjį ožį",
			"21": "<sup>21</sup> ir, uždėjęs jam ant galvos abi rankas, išpažins visas izraelitų nedorybes, visus jų nusikaltimus ir nuodėmes, sukraudamas jas ant ožio galvos. Tada tam skirtas asmuo išves ožį į dykumą.",
			"22": "<sup>22</sup> Ir ožys išneš visus jų nusikaltimus ant savęs į negyvenamas vietoves ir bus paleistas dykumoje.",
			"23": "<sup>23</sup> Aaronas sugrįš į Susitikimo palapinę, nusivilks apdarus, kuriais buvo apsirengęs, įeidamas į Švenčiausiąją, ir, juos ten palikęs,",
			"24": "<sup>24</sup> nuplaus savo kūną šventoje vietoje ir apsivilks savo rūbais. Po to išėjęs aukos savo ir tautos deginamąją auką, atlikdamas sutaikinimą už save ir tautą.",
			"25": "<sup>25</sup> Jis degins ant aukuro aukos už nuodėmę taukus.",
			"26": "<sup>26</sup> O tas, kuris išvedė išvarymo ožį, plaus savo drabužius ir kūną vandeniu ir tik po to įeis į stovyklą.",
			"27": "<sup>27</sup> Veršį ir ožį, kurie buvo paaukoti už nuodėmę ir kurių kraujas buvo įneštas į Švenčiausiąją sutaikinimui, išgabens už stovyklos ir viską sudegins: odas, mėsą ir mėšlą.",
			"28": "<sup>28</sup> O tas, kuris tai atliks, plaus savo drabužius ir kūną vandeniu ir tik po to įeis į stovyklą.",
			"29": "<sup>29</sup> Tai yra jums amžinas įstatymas. Septinto mėnesio dešimtą dieną varginsite savo sielas ir jokio darbo nedirbsite nei jūs, nei tarp jūsų gyveną ateiviai.",
			"30": "<sup>30</sup> Ta diena bus jūsų sutaikinimo ir apsivalymo diena nuo visų nuodėmių Viešpaties akivaizdoje.",
			"31": "<sup>31</sup> Tai yra sabatas poilsiui; jūs varginsite savo sielas, vykdydami amžinąjį įstatymą.",
			"32": "<sup>32</sup> Jus sutaikins kunigas, pateptas ir pašventintas eiti kunigo tarnystę vietoje savo tėvo. Jis apsivilks drobiniais rūbais, šventaisiais apdarais,",
			"33": "<sup>33</sup> apvalys Švenčiausiąją, Susitikimo palapinę ir aukurą, taip pat sutaikins kunigus ir visą tautą.",
			"34": "<sup>34</sup> Tai bus jums amžinas įstatymas, kad Izraelio vaikai būtų sutaikinti dėl visų savo nuodėmių vieną kartą per metus”. Jis atliko viską, ką Viešpats buvo įsakęs Mozei."
		},
		"17": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk Aaronui, jo sūnums ir visiems izraelitams: ‘Taip sako Viešpats:",
			"3": "<sup>3</sup> ‘Kiekvienas izraelitas, kuris papjautų jautį, avį ar ožką stovykloje ar už stovyklos",
			"4": "<sup>4</sup> ir neaukotų aukos Viešpačiui prie palapinės įėjimo, bus kaltas, nes praliejo kraują. Jis bus išnaikintas iš savo tautos.",
			"5": "<sup>5</sup> Todėl izraelitai savo gyvulius, užuot pjovę juos lauke, privalo atvesti pas kunigą, kad jie būtų pašvęsti Viešpačiui prie Susitikimo palapinės įėjimo, ir juos aukoti Viešpačiui kaip padėkos auką.",
			"6": "<sup>6</sup> Kunigas išlies kraują ant Viešpaties aukuro prie Susitikimo palapinės įėjimo ir sudegins taukus, kad būtų malonus kvapas Viešpačiui’.",
			"7": "<sup>7</sup> Tuo būdu izraelitai nebeaukos daugiau savo aukų piktosioms dvasioms. Tai bus amžinas įstatymas jiems ir jų palikuonims.",
			"8": "<sup>8</sup> Jiems sakyk, kad kiekvienas izraelitas ar ateivis, gyvenantis tarp jūsų, kuris aukotų deginamąją ar kitokią auką",
			"9": "<sup>9</sup> ir jos neatvestų prie Susitikimo palapinės įėjimo paaukoti Viešpačiui, bus išnaikintas iš savo tautos.",
			"10": "<sup>10</sup> Jei kas iš izraelitų ar gyvenančių tarp jūsų ateivių valgytų kraują, Aš atgręšiu savo veidą į jį ir jį išnaikinsiu iš jo tautos.",
			"11": "<sup>11</sup> Kūno gyvybė yra kraujyje; Aš jums jį daviau, kad juo ant aukuro atliktumėte sutaikinimą už savo sielas, nes kraujas sutaikina sielą.",
			"12": "<sup>12</sup> Todėl įsakau izraelitams, kad niekas iš jūsų nė iš ateivių, gyvenančių tarp jūsų, nevalgytų kraujo.",
			"13": "<sup>13</sup> Jei izraelitas ar ateivis pagautų medžiodamas kokį žvėrį ar paukštį, kuriuos leista valgyti, teišlieja jo kraują ant žemės ir jį pakasa,",
			"14": "<sup>14</sup> nes kiekvieno kūno gyvybė yra kraujyje; todėl įsakau izraelitams nevalgyti kraujo iš jokio kūno. Kas jį valgys, bus išnaikintas.",
			"15": "<sup>15</sup> Kas iš vietinių gyventojų ar ateivių valgytų pastipusį ar žvėries sudraskytą, plaus savo drabužius ir pats apsiplaus vandeniu; jis bus nešvarus iki vakaro, o po to bus švarus,",
			"16": "<sup>16</sup> bet, jei neplaus savo drabužio ir kūno, nešios savo kaltę’ ”."
		},
		"18": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Aš esu Viešpats, jūsų Dievas.",
			"3": "<sup>3</sup> Nesielkite, kaip elgiasi egiptiečiai, tarp kurių jūs gyvenote, ir nesilaikykite papročių Kanaano šalies, kurion jus įvesiu, nevaikščiokite pagal jų nuostatus.",
			"4": "<sup>4</sup> Vykdykite mano paliepimus ir pagal mano nuostatus elkitės. Aš esu Viešpats, jūsų Dievas.",
			"5": "<sup>5</sup> Laikykitės mano įstatymų ir paliepimų, kuriuos vykdydamas žmogus bus gyvas. Aš esu Viešpats.",
			"6": "<sup>6</sup> Nesiartinkite prie artimo giminaičio, kad atidengtumėte jo nuogumą. Aš esu Viešpats.",
			"7": "<sup>7</sup> Neatidenk savo tėvo nei motinos nuogumo. Ji yra tavo motina­tu neturi atidengti jos nuogumo.",
			"8": "<sup>8</sup> Neatidenk savo tėvo žmonos nuogumo, nes tai yra tavo tėvo nuogumas.",
			"9": "<sup>9</sup> Neatidenk nuogumo savo sesers, kuri yra tavo tėvo ar motinos duktė, gimusi namie ar kur kitur.",
			"10": "<sup>10</sup> Neatidenk savo sūnaus dukros ar dukters dukros nuogumo, nes tai tavo paties nuogumas.",
			"11": "<sup>11</sup> Neatidenk nuogumo savo tėvo žmonos dukros, kurią ji pagimdė tavo tėvui, nes ji yra tavo sesuo.",
			"12": "<sup>12</sup> Neatidenk savo tėvo sesers nuogumo, nes tai yra tavo tėvo artima giminaitė.",
			"13": "<sup>13</sup> Neatidenk savo motinos sesers nuogumo, nes tai tavo motinos artima giminaitė.",
			"14": "<sup>14</sup> Neatidenk savo dėdės žmonos nuogumo, nes ji yra tavo teta.",
			"15": "<sup>15</sup> Neatidenk savo sūnaus žmonos nuogumo.",
			"16": "<sup>16</sup> Neatidenk savo brolio žmonos nuogumo, nes tai tavo brolio nuogumas.",
			"17": "<sup>17</sup> Neatidenk nuogumo moters ir jos dukros arba jos sūnaus dukros, nes jie yra jos artimi giminaičiai; tai nusikaltimas.",
			"18": "<sup>18</sup> Neimk savo žmonos sesers į suguloves ir neatidenk jos nuogumo žmonai gyvai esant.",
			"19": "<sup>19</sup> Nesiartink prie moters, turinčios mėnesines, kad atidengtum jos nuogumą.",
			"20": "<sup>20</sup> Nesigulk su savo artimo žmona, kad susiterštum su ja.",
			"21": "<sup>21</sup> Neaukok savo vaikų sudeginti Molechui ir neišniekink mano vardo, nes Aš esu Viešpats.",
			"22": "<sup>22</sup> Nesugulk su vyru kaip su moterimi, nes tai pasibjaurėjimas.",
			"23": "<sup>23</sup> Nesantykiauk su gyvuliu save suteršdamas, ir moteris neturi stotis prieš gyvulį, kad su juo santykiautų, nes tai iškrypimas.",
			"24": "<sup>24</sup> Nesusitepkite minėtais būdais, nes taip susitepė tautos, kurias Aš išvarau nuo jūsų.",
			"25": "<sup>25</sup> Kraštas buvo suteptas. Aš užtrauksiu jų nusikaltimus ant jų, ir kraštas išvems savo gyventojus.",
			"26": "<sup>26</sup> Jūs ir tarp jūsų pasiliekantys ateiviai laikykitės mano įstatymų ir įsakymų ir nedarykite nė vienos minėtų bjaurysčių.",
			"27": "<sup>27</sup> Visas tas bjaurystes darė krašto gyventojai, gyvenę pirma jūsų, ir jį sutepė.",
			"28": "<sup>28</sup> Saugokitės, kad kraštas ir jūsų neišspjautų, jei jį suteršite, kaip išspjovė pirma jūsų buvusias tautas.",
			"29": "<sup>29</sup> Kiekvienas žmogus, kuris darytų bet kurią iš tų bjaurysčių, bus išnaikintas iš savo tautos.",
			"30": "<sup>30</sup> Laikykitės mano nuostatų. Nedarykite to, ką darė pirma jūsų gyvenusieji. Aš esu Viešpats, jūsų Dievas”."
		},
		"19": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk visam Izraeliui: ‘Būkite šventi, nes Aš, Viešpats, jūsų Dievas, esu šventas.",
			"3": "<sup>3</sup> Kiekvienas turi gerbti savo tėvą ir motiną. Laikykitės mano sabato. Aš esu Viešpats, jūsų Dievas.",
			"4": "<sup>4</sup> Nesikreipkite į stabus ir neliekite sau dievų. Aš esu Viešpats, jūsų Dievas.",
			"5": "<sup>5</sup> Jei aukosite padėkos auką Viešpačiui, aukokite ją laisva valia.",
			"6": "<sup>6</sup> Auką suvalgykite tą pačią dieną. Leidžiama ją valgyti ir antrą dieną, o kas liks trečiai dienai, sudeginkite.",
			"7": "<sup>7</sup> Jei kas ją valgytų trečią dieną, nusikals ir auka bus Dievo nepriimta.",
			"8": "<sup>8</sup> Jis bus kaltas, nes sutepė Viešpačiui skirtą auką. Toks žmogus bus išnaikintas iš jo tautos.",
			"9": "<sup>9</sup> Pjaudamas savo žemės javus, jų nekirsk iki pat lauko krašto ir nerink nukritusių varpų.",
			"10": "<sup>10</sup> Taip pat ir vynuogyne nenuskink paliktų uogų. Jos telieka beturčiams ir ateiviams. Aš esu Viešpats, jūsų Dievas.",
			"11": "<sup>11</sup> Nevokite. Nemeluokite. Neapgaudinėkite vieni kitų.",
			"12": "<sup>12</sup> Neprisiekite neteisingai mano vardu ir nesutepkite savo Dievo vardo. Aš esu Viešpats.",
			"13": "<sup>13</sup> Nespauskite ir neišnaudokite savo artimo. Samdinio algos nesulaikykite iki ryto.",
			"14": "<sup>14</sup> Nekeikite kurčio ir nedėkite kliūties aklajam ant kelio, bet bijokite Dievo, nes Aš esu Viešpats.",
			"15": "<sup>15</sup> Teisme teiskite teisingai, neatsižvelgdami, ar tai beturtis, ar žymus asmuo.",
			"16": "<sup>16</sup> Neliežuvaukite, vaikščiodami tarp savųjų. Netykokite savo artimo kraujo. Aš esu Viešpats.",
			"17": "<sup>17</sup> Nelaikykite širdyje neapykantos prieš savo brolį. Sudrauskite savo artimą, kad jums nebūtų nuodėmės dėl jo.",
			"18": "<sup>18</sup> Nekeršykite ir atleiskite savo artimui. Mylėkite savo artimą kaip patys save. Aš esu Viešpats.",
			"19": "<sup>19</sup> Laikykitės mano įstatymų. Neleiskite apsivaisinti skirtingos rūšies gyvuliams. Savo lauko neapsėkite mišria sėkla. Nedėvėkite drabužio, austo iš vilnų ir linų.",
			"20": "<sup>20</sup> Jei vyras gulėtų su verge, kuri yra pažadėta vyrui, bet dar neišpirkta, abu bus nuplakti, bet neužmušti, nes ji nebuvo laisva.",
			"21": "<sup>21</sup> Už savo kaltę jis atves Viešpačiui aviną prie Susitikimo palapinės.",
			"22": "<sup>22</sup> Kunigas sutaikins jį su Viešpačiu, už jo nuodėmę paaukodamas aviną aukai už kaltę, ir jam bus atleista nuodėmė, kuria jis nusidėjo.",
			"23": "<sup>23</sup> Kai įeisite į žemę ir pasodinsite vaismedžių, pirmųjų trejų metų vaisiai bus jums sutepti, jų nevalgysite;",
			"24": "<sup>24</sup> ketvirtaisiais metais visi vaisiai bus pašvęsti Viešpaties garbei.",
			"25": "<sup>25</sup> Penktaisiais gi metais skinsite ir valgysite vaisius. Aš esu Viešpats, jūsų Dievas.",
			"26": "<sup>26</sup> Nevalgykite kraujo. Nežyniaukite ir neburkite.",
			"27": "<sup>27</sup> Nekirpkite plaukų aplink galvą ir nekarpykite barzdos.",
			"28": "<sup>28</sup> Mirusio atminimui nedarykite įrėžimų nei kitokių vaizdų ar ženklų savo kūne. Aš esu Viešpats.",
			"29": "<sup>29</sup> Neparduokite savo dukterų būti paleistuvėmis, kad nesuteptumėte žemės ir ji netaptų pilna bjaurių nusikaltimų.",
			"30": "<sup>30</sup> Laikykitės mano sabatų ir gerbkite šventyklą. Aš esu Viešpats.",
			"31": "<sup>31</sup> Nesikreipkite į mirusiųjų dvasių iššaukėjus ir nieko nesiteiraukite žynių, kad per juos nesusiteptumėte. Aš esu Viešpats, jūsų Dievas.",
			"32": "<sup>32</sup> Atsistokite prieš žilą galvą, gerbkite senus žmones, bijokite savo Dievo. Aš esu Viešpats.",
			"33": "<sup>33</sup> Neišnaudokite jūsų krašte gyvenančių ateivių.",
			"34": "<sup>34</sup> Tegyvena jie tarp jūsų kaip vietiniai; mylėkite juos kaip patys save, nes ir jūs buvote ateiviai Egipto žemėje. Aš esu Viešpats, jūsų Dievas.",
			"35": "<sup>35</sup> Būkite teisingi teisme, matuodami, sverdami ir seikėdami.",
			"36": "<sup>36</sup> Naudokite teisingus svarsčius ir teisingas svarstykles, teisingą efą ir hiną. Aš esu Viešpats, jūsų Dievas, kuris jus išvedžiau iš Egipto žemės.",
			"37": "<sup>37</sup> Laikykitės visų mano įstatymų bei nuostatų ir juos vykdykite. Aš esu Viešpats’ ”."
		},
		"20": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk izraelitams: ‘Jei izraelitas ar ateivis, gyvenantis Izraelyje, aukotų vaiką Molechui, bus baudžiamas mirtimi: krašto žmonės užmuš jį akmenimis.",
			"3": "<sup>3</sup> Aš atgręšiu savo veidą į jį ir išnaikinsiu jį iš jo tautos, nes jis, duodamas savo vaiką Molechui, sutepė mano šventyklą ir paniekino mano šventą vardą.",
			"4": "<sup>4</sup> Jei krašto žmonės, apsimesdami nežiną, paleistų žmogų, aukojusį vaiką Molechui, ir nenorėtų jo užmušti,",
			"5": "<sup>5</sup> Aš bausiu jį, jo šeimą ir visus, kurie pritarė jo paleistuvystei su Molechu,­išnaikinsiu juos iš jų tautos.",
			"6": "<sup>6</sup> Jei kas kreiptųsi į mirusiųjų dvasių iššaukėjus bei žynius ir su jais paleistuvautų, Aš atsigręšiu į juos ir išnaikinsiu iš jų tautos.",
			"7": "<sup>7</sup> Pasišventinkite ir būkite šventi, nes Aš esu Viešpats, jūsų Dievas.",
			"8": "<sup>8</sup> Laikykitės mano įsakymų ir vykdykite juos. Aš esu Viešpats, kuris darau jus šventus.",
			"9": "<sup>9</sup> Kas keiktų savo tėvą ar motiną, bus mirtim nubaustas; jis keikė tėvą ar motiną ir jo kraujas yra ant jo.",
			"10": "<sup>10</sup> Kas svetimautų su kito moterimi, su savo artimo žmona, abu­ svetimautojas ir svetimautoja­ bus baudžiami mirtimi.",
			"11": "<sup>11</sup> Jei kas sugultų su savo pamote, abu turi mirti; jie patys kalti dėl savo mirties.",
			"12": "<sup>12</sup> Jei kas sugultų su savo marčia, abu turi mirti, nes tai iškrypimas; jų kraujas yra ant jų.",
			"13": "<sup>13</sup> Jei vyras sugultų su vyru kaip su moterimi, abu bjauriai nusikalstų ir abu turi mirti.",
			"14": "<sup>14</sup> Jei kas, vedęs dukterį, vestų dar ir jos motiną, padarytų didelį nusikaltimą. Toks asmuo bus sudegintas kartu su abiem moterim, kad tarp jūsų nebūtų tokių nusikaltimų.",
			"15": "<sup>15</sup> Kas paleistuvautų su gyvuliu, bus pasmerktas mirti; gyvulį taip pat užmuškite.",
			"16": "<sup>16</sup> Jei moteris paleistuvautų su gyvuliu, ji bus užmušta akmenimis kartu su gyvuliu; jos kraujas bus ant jos.",
			"17": "<sup>17</sup> Jei kas imtų savo seserį, savo tėvo ar motinos dukterį ir matytų jos nuogumą ir ji matytų jo nuogumą, abu bus padarę bjaurų nusikaltimą; jie bus išnaikinti savo tautos akivaizdoje; jie patys atsakys už savo kaltę.",
			"18": "<sup>18</sup> Kas sugultų su moterimi jos mėnesinių metu, abu bus išnaikinti iš savo tautos.",
			"19": "<sup>19</sup> Neatidenk nuogumo savo tėvo ar motinos sesers. Kas taip daro, nusikalsta ir abu bus nubausti.",
			"20": "<sup>20</sup> Jei žmogus sugultų su savo dėdės žmona­jis atidengtų dėdės nuogumą; abu bus baudžiami už savo nuodėmę­jie mirs bevaikiai.",
			"21": "<sup>21</sup> Jei vyras paimtų savo brolio žmoną, tai bjauru, nes jis atidengė savo brolio nuogumą,­jie mirs bevaikiai.",
			"22": "<sup>22</sup> Laikykitės mano įstatymų ir įsakymų ir juos vykdykite, kad jūsų neišspjautų žemė, kurioje apsigyvensite.",
			"23": "<sup>23</sup> Nesielkite pagal papročius tautų, kurias išvarysiu iš to krašto. Jie visa tai darė, ir Aš jais pasibjaurėjau.",
			"24": "<sup>24</sup> Jums gi sakau: paveldėkite jų žemę, kurią jums duosiu, žemę, plūstančią pienu ir medumi. Aš, Viešpats, jūsų Dievas, kuris jus išskyriau iš kitų tautų.",
			"25": "<sup>25</sup> Tad skirkite švarų gyvulį nuo nešvaraus ir švarų paukštį nuo nešvaraus, kad nesusiteptumėte gyvuliais, paukščiais ir visais gyviais, krutančiais žemėje, kuriuos jums nurodžiau esant nešvarius.",
			"26": "<sup>26</sup> Jūs privalote būti man šventi, nes Aš esu šventas Viešpats. Aš jus išskyriau iš kitų tautų, kad būtumėte mano.",
			"27": "<sup>27</sup> Vyras ar moteris, kurie užsiima mirusiųjų dvasių iššaukimu ir žyniavimu, turi mirti; juos užmuškite akmenimis; jie patys kalti dėl savo mirties’ ”."
		},
		"21": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei: “Sakyk Aarono sūnums, kunigams, kad jie nesusiteptų savo mirusiu tautiečiu,",
			"2": "<sup>2</sup> nebent būtų artimi giminės, tai yra: tėvas, motina, sūnus, duktė ar brolis",
			"3": "<sup>3</sup> ir netekėjusi sesuo, kuri yra jam artima.",
			"4": "<sup>4</sup> Jis neturi susitepti, nes jis yra vyresnysis savo tautoje.",
			"5": "<sup>5</sup> Kunigai neskus savo galvos nei barzdos ir neraižys savo kūno.",
			"6": "<sup>6</sup> Jie turi būti šventi savo Dievui ir nesutepti savo Dievo vardo, nes jie aukoja man smilkalus ir duonos auką, todėl privalo būti šventi.",
			"7": "<sup>7</sup> Jiems draudžiama vesti paleistuvę ir išsiskyrusią, kadangi jie yra pašvęsti man",
			"8": "<sup>8</sup> ir aukoja padėtinę duoną. Jie turi būti šventi, nes Aš esu šventas Viešpats, kuris jus darau šventus.",
			"9": "<sup>9</sup> Jei kunigo duktė paleistuvautų ir tuo suteptų savo tėvo vardą, bus sudeginta.",
			"10": "<sup>10</sup> Vyriausiasis kunigas, ant kurio galvos išlieta patepimo aliejaus ir kuris įšventintas kunigystei bei apvilktas šventais apdarais, nenudengs savo galvos, nepersiplėš drabužių",
			"11": "<sup>11</sup> ir neprisilies prie jokio mirusio, net prie savo tėvo ar motinos.",
			"12": "<sup>12</sup> Jis neišeis iš šventyklos ir nesuteps jos, nes yra pateptas šventu aliejumi; Aš esu Viešpats.",
			"13": "<sup>13</sup> Jis turi vesti mergaitę.",
			"14": "<sup>14</sup> Jis negali vesti našlės, išsiskyrusios, paniekintos nei paleistuvės, bet tik mergaitę iš savo tautos,",
			"15": "<sup>15</sup> kad nesuterštų savo giminės kraujo, nes Aš, Viešpats, jį pašventinu”.",
			"16": "<sup>16</sup> Viešpats kalbėjo Mozei:",
			"17": "<sup>17</sup> “Sakyk Aaronui, kad, jei kas nors iš jo palikuonių turi trūkumų, tegul nesiartina prie Dievo, kad aukotų Jam duoną.",
			"18": "<sup>18</sup> Jeigu jis yra aklas, raišas, iškreiptu veidu ar nesveikų sąnarių",
			"19": "<sup>19</sup> ar yra sulaužyta jo ranka arba koja,",
			"20": "<sup>20</sup> ar kuprotas, ar neūžauga, ar sergąs akių liga, ar šašuotas, ar niežuotas, ar eunuchas,­",
			"21": "<sup>21</sup> nė vienas iš kunigo Aarono palikuonių, turintis trūkumų, nesiartins aukoti Viešpačiui,",
			"22": "<sup>22</sup> tačiau valgys maistą, aukojamą šventykloje, šventą ir labai šventą.",
			"23": "<sup>23</sup> Bet jis neis į Švenčiausiąją ir nesiartins prie aukuro, nes turi trūkumų, kad nesuteptų mano šventyklos. Aš esu Viešpats, kuris juos pašventinu”.",
			"24": "<sup>24</sup> Mozė tai paskelbė Aaronui, jo sūnums ir visam Izraeliui."
		},
		"22": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk Aaronui ir jo sūnums, kad jie pagarbiai laikytų daiktus, kurie pašvęsti aukai, ir nesuteptų jų. Aš esu Viešpats.",
			"3": "<sup>3</sup> Sakyk jiems ir jų palikuonims: ‘Jei kuris būdamas nešvarus paliestų tai, kas izraelitų pašvęsta aukoti Viešpačiui, bus išnaikintas iš Viešpaties akivaizdos. Aš esu Viešpats.",
			"4": "<sup>4</sup> Kas iš Aarono palikuonių būtų raupsuotas ar nešvarus, nevalgys to, kas man pašvęsta, iki apsivalys. Kas prisiliestų prie mirusio arba prie turėjusio sėklos išsiliejimą,",
			"5": "<sup>5</sup> arba prie šliaužiančio gyvio, arba kurio kito nešvaraus dalyko, kurio prisilietimas sutepa,",
			"6": "<sup>6</sup> bus nešvarus iki vakaro ir nevalgys pašvęstųjų valgių, kol neapsiplaus vandeniu.",
			"7": "<sup>7</sup> Nusileidus saulei, bus švarus ir galės valgyti pašvęstą maistą, nes tai jo maistas.",
			"8": "<sup>8</sup> Jiems nevalia valgyti nugaišusio nei žvėries sudraskyto gyvulio, nes jais susiteps. Aš esu Viešpats.",
			"9": "<sup>9</sup> Tegu jie laikosi mano įsakymų, kad nenusikalstų ir nemirtų. Aš esu Viešpats, kuris juos pašventinu.",
			"10": "<sup>10</sup> Joks ateivis nevalgys švento maisto; jo nevalgys taip pat kunigo įnamis ir samdinys.",
			"11": "<sup>11</sup> Bet kunigo vergas, pirktas už pinigus arba gimęs jo namuose, galės jį valgyti.",
			"12": "<sup>12</sup> Jei kunigo duktė ištekės ne už kunigo, ji nevalgys švento maisto.",
			"13": "<sup>13</sup> Bet, jei ji liktų našlė ar išsiskirtų ir neturėtų vaikų, sugrįžusi į savo tėvo namus, valgys tą maistą, kaip tai darydavo būdama mergaitė. Niekas iš pašalinių neturi teisės to maisto valgyti.",
			"14": "<sup>14</sup> Kas nežiniomis valgytų šventą maistą, pridės penktą dalį prie to, ką suvalgė, ir atiduos kunigui.",
			"15": "<sup>15</sup> Kunigai tenesutepa izraelitų šventų aukų, kurias jie aukoja Viešpačiui,",
			"16": "<sup>16</sup> kad nenusikalstų valgydami šventas dovanas. Aš esu Viešpats, kuris juos pašventinu’ ”.",
			"17": "<sup>17</sup> Viešpats kalbėjo Mozei:",
			"18": "<sup>18</sup> “Sakyk Aaronui, jo sūnums ir visiems izraelitams: ‘Jei kas iš izraelitų ir gyvenančių tarp jų ateivių norėtų aukoti Viešpačiui, ar tai būtų įžado, ar laisvos valios auka, teatneša deginamajai Viešpaties aukai",
			"19": "<sup>19</sup> sveiką patinėlį iš galvijų, avių ar ožkų.",
			"20": "<sup>20</sup> Jei auka turėtų kokią ydą, jūs jos neaukokite, nes tai nepriimtina.",
			"21": "<sup>21</sup> Jei kas aukotų Viešpačiui padėkos auką, ištesėdamas įžadą, ar laisvos valios auką, ji turi būti sveika iš galvijų ar avių.",
			"22": "<sup>22</sup> Aklo, luošo, turinčio žaizdos randą, išberto, šašuoto ar su niežais gyvulio neaukokite Viešpačiui ir nieko iš jo nedeginkite ant aukuro.",
			"23": "<sup>23</sup> Galviją ar avį su ilgesniu ar trumpesniu sąnariu leidžiama aukoti laisva valia, bet įžado aukai­jis nepriimtinas.",
			"24": "<sup>24</sup> Neaukokite Viešpačiui jokio iškastruoto gyvulio; šito niekada nedarykite savo žemėje.",
			"25": "<sup>25</sup> Taip pat nepriimkite ir neaukokite Dievui tokių gyvulių iš ateivio rankos, nes tai yra sugedę ir sutepta; tokia auka nepriimtina’ ”.",
			"26": "<sup>26</sup> Viešpats kalbėjo Mozei:",
			"27": "<sup>27</sup> “Veršiukas, ėriukas ar ožiukas septynias dienas liks prie savo motinos; aštuntą dieną ar vėliau galės būti aukojamas Viešpačiui.",
			"28": "<sup>28</sup> Nepjaukite karvės nei avies tą pačią dieną su jos jaunikliu.",
			"29": "<sup>29</sup> Kai aukosite padėkos auką Viešpačiui, aukokite ją laisva valia.",
			"30": "<sup>30</sup> Ją suvalgykite tą pačią dieną ir nieko nepalikite kitos dienos rytui. Aš esu Viešpats.",
			"31": "<sup>31</sup> Laikykitės mano įsakymų ir juos vykdykite. Aš esu Viešpats.",
			"32": "<sup>32</sup> Nesutepkite mano švento vardo. Aš esu šventas tarp izraelitų ir pašventinu jus.",
			"33": "<sup>33</sup> Aš išvedžiau jus iš Egipto žemės, kad būčiau jūsų Dievas. Aš esu Viešpats”."
		},
		"23": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Paskelbk izraelitams Viešpaties šventes, per kurias turite sušaukti susirinkimą.",
			"3": "<sup>3</sup> Šešias dienas dirbkite; septintoji diena yra sabatas, skirta poilsiui ir šventam susirinkimui. Nieko nedirbkite tą dieną, nes tai yra sabatas Viešpačiui, kur jūs begyventumėte.",
			"4": "<sup>4</sup> Šios yra iškilmingos Viešpaties šventės ir jas švęskite joms skirtu metu:",
			"5": "<sup>5</sup> pirmo mėnesio keturioliktos dienos vakare yra Viešpaties Pascha,",
			"6": "<sup>6</sup> o penkioliktą to paties mėnesio dieną yra Neraugintos duonos šventė Viešpačiui. Neraugintą duoną valgykite septynias dienas.",
			"7": "<sup>7</sup> Pirmoji diena bus jums iškilmingiausia ir šventa. Tą dieną nedirbkite jokio darbo.",
			"8": "<sup>8</sup> Septynias dienas aukokite Viešpačiui deginamąją auką. Septintoji susirinkimo diena bus iškilminga ir šventa; tą dieną neturite nieko dirbti”.",
			"9": "<sup>9</sup> Viešpats kalbėjo Mozei:",
			"10": "<sup>10</sup> “Sakyk izraelitams, kad, įėję į kraštą, kurį jiems duosiu, ir pradėję pjauti javus, atneštų pirmąjį pjūties pėdą kunigui,",
			"11": "<sup>11</sup> kuris pirmą dieną po sabato siūbuos pėdą Viešpaties akivaizdoje, kad Jis būtų jums malonus.",
			"12": "<sup>12</sup> Tą pačią dieną, kai pėdas siūbuojamas, papjaukite sveiką metinį avinėlį deginamajai aukai Viešpačiui.",
			"13": "<sup>13</sup> Drauge su juo aukokite duonos auką iš dviejų dešimtųjų efos smulkių miltų, apšlakstytų aliejumi, kad būtų malonus kvapas Viešpačiui; taip pat geriamajai aukai aukokite vyno ketvirtadalį hino.",
			"14": "<sup>14</sup> Nei duonos, nei paskrudintų grūdų, nei kruopų nevalgykite, kol nepaaukosite Dievui. Tai amžinas įsakymas visoms kartoms, kur jūs begyventumėte.",
			"15": "<sup>15</sup> Atskaitykite septynias savaites po to sabato, pradėdami pirmąja diena, kai atnešėte aukoti pirmąją derliaus pėdą.",
			"16": "<sup>16</sup> Penkiasdešimtąją dieną­pirmąją dieną po septinto sabato­vėl aukokite Viešpačiui duonos auką.",
			"17": "<sup>17</sup> Iš savo gyvenviečių atneškite du raugintos duonos kepalus, iškeptus iš dviejų dešimtųjų efos smulkių miltų; tai yra pirmieji vaisiai Viešpačiui.",
			"18": "<sup>18</sup> Kartu su duonos kepalais aukokite septynis sveikus metinius avinėlius, vieną veršį ir du avinus,­tai bus deginamoji auka Viešpačiui, drauge su duonos ir geriamąja auka, kad būtų malonus kvapas.",
			"19": "<sup>19</sup> Aukokite taip pat ožį aukai už nuodėmę ir du metinius avinėlius padėkos aukai.",
			"20": "<sup>20</sup> Kunigas juos siūbuos drauge su pirmųjų vaisių duonos kepalais Viešpaties akivaizdoje; po to visa tai bus šventa ir priklausys kunigams.",
			"21": "<sup>21</sup> Paskelbkite šitą dieną iškilminga ir šventa susirinkimo diena; tą dieną nedirbkite jokio darbo. Tai bus amžinas įstatymas visoms jūsų kartoms, kur jūs begyventumėte.",
			"22": "<sup>22</sup> Pjaudami savo javus, nenupjaukite jų iki lauko krašto ir nerinkite nukritusių varpų; jas palikite beturčiams ir ateiviams. Aš esu Viešpats, jūsų Dievas”.",
			"23": "<sup>23</sup> Viešpats kalbėjo Mozei:",
			"24": "<sup>24</sup> “Sakyk izraelitams, kad septinto mėnesio pirmoji diena bus jums iškilminga poilsio diena­sabatas. Trimitų garsai jums tai primins, ji bus švento susirinkimo diena.",
			"25": "<sup>25</sup> Nedirbkite tą dieną jokio darbo ir aukokite Viešpačiui deginamąją auką”.",
			"26": "<sup>26</sup> Viešpats kalbėjo Mozei:",
			"27": "<sup>27</sup> “Dešimtoji to paties mėnesio diena bus iškilminga sutaikinimo diena. Tai bus švento susirinkimo diena; varginkite savo sielas tą dieną ir aukokite Viešpačiui deginamąją auką.",
			"28": "<sup>28</sup> Jokio darbo nedirbkite, nes tai sutaikinimo diena, kad jūs būtumėte sutaikinti su Viešpačiu, jūsų Dievu.",
			"29": "<sup>29</sup> Kiekvienas žmogus, kuris nevargintų savo sielos tą dieną, bus išnaikintas iš savo tautos.",
			"30": "<sup>30</sup> Kas dirbtų bet kokį darbą tą dieną, bus išnaikintas iš savo tautos.",
			"31": "<sup>31</sup> Todėl nieko tą dieną nedirbkite. Tai bus amžinas įstatymas visoms jūsų kartoms, kur jūs begyventumėte.",
			"32": "<sup>32</sup> Tai bus jums poilsio sabatas, kurio metu varginsite savo sielas. Nuo devintos mėnesio dienos vakaro iki kito vakaro švęsite sabatą”.",
			"33": "<sup>33</sup> Viešpats kalbėjo Mozei:",
			"34": "<sup>34</sup> “Sakyk izraelitams, kad nuo penkioliktos to paties mėnesio dienos septynias dienas truks Palapinių šventė.",
			"35": "<sup>35</sup> Pirmoji diena bus iškilminga ir šventa; tą dieną nedirbsite jokio darbo.",
			"36": "<sup>36</sup> Septynias dienas aukosite Viešpačiui deginamąsias aukas. Aštuntoji diena bus šventa. Tuomet aukosite Viešpačiui deginamąją auką, nes tai susirinkimo diena; nedirbsite tą dieną jokio darbo.",
			"37": "<sup>37</sup> Šitos yra Viešpačiui skirtos šventės, per kurias sušauksite šventą susirinkimą ir aukosite Viešpačiui deginamąsias, duonos ir geriamąsias aukas, kiekvieną jai nustatytu metu,",
			"38": "<sup>38</sup> neskaitant sabato, įžado ir laisvos valios aukų, kurias atnešate Viešpačiui.",
			"39": "<sup>39</sup> Taigi nuo septinto mėnesio penkioliktos dienos, kai surinksite visą savo laukų derlių, paskirkite Viešpaties garbei septynias dienas; pirmoji ir aštuntoji diena bus sabatas.",
			"40": "<sup>40</sup> Pirmą dieną imkite gražiausių medžių vaisių, palmių šakelių, lapuoto medžio ir paupio gluosnių šakų ir linksminkitės Viešpaties, jūsų Dievo, akivaizdoje.",
			"41": "<sup>41</sup> Kasmet iškilmingai švęskite Viešpačiui septynias dienas; tai bus amžinas įstatymas jūsų kartoms. Tą šventę švęskite septintą mėnesį.",
			"42": "<sup>42</sup> Visi izraelitai turi gyventi palapinėse septynias dienas,",
			"43": "<sup>43</sup> kad jūsų palikuonys žinotų, jog buvau apgyvendinęs izraelitus palapinėse, kai juos išvedžiau iš Egipto žemės. Aš Viešpats, jūsų Dievas”.",
			"44": "<sup>44</sup> Taip Mozė paskelbė izraelitams šventes Viešpaties garbei."
		},
		"24": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Įsakyk izraelitams, kad jie atneštų tyriausio alyvmedžių aliejaus, kad lempos nuolat degtų",
			"3": "<sup>3</sup> Susitikimo palapinėje šiapus uždangos į Švenčiausiąją. Jas uždegs Aaronas kiekvieną vakarą ir prižiūrės, kad jos degtų iki ryto Viešpaties akivaizdoje. Ši tarnystė bus amžinas įstatymas visoms jūsų kartoms.",
			"4": "<sup>4</sup> Lempos stovės ant žvakidės iš gryno aukso Viešpaties akivaizdoje.",
			"5": "<sup>5</sup> Imk smulkių miltų ir iš jų iškepk dvylika duonos kepalėlių, kiekvieną iš dviejų dešimtųjų efos,",
			"6": "<sup>6</sup> juos padėk ant gryno aukso stalo Viešpaties akivaizdoje dviem eilėm, po šešis kiekvienoje eilėje.",
			"7": "<sup>7</sup> Ant kiekvienos eilės padėk tyriausių smilkalų, kad jie būtų ant duonos, kuri sudeginama kaip atminimas Viešpačiui.",
			"8": "<sup>8</sup> Kas sabatą ji bus keičiama. Izraelitams tai bus amžinas įstatymas.",
			"9": "<sup>9</sup> Visa tai priklausys Aaronui ir jo sūnums, jie valgys ją šventoje vietoje, nes tai švenčiausias dalykas iš Viešpaties deginamųjų aukų pagal amžinąjį įstatymą”.",
			"10": "<sup>10</sup> Izraelitės moters sūnus, kurio tėvas buvo egiptietis, susiginčijo su izraelitu stovykloje.",
			"11": "<sup>11</sup> Jį atvedė pas Mozę, kadangi jis piktžodžiavo ir keikė Dievą. Jo motina buvo Šelomita, Dibrio duktė iš Dano giminės.",
			"12": "<sup>12</sup> Jį uždarė ir laukė Viešpaties nurodymo.",
			"13": "<sup>13</sup> Viešpats įsakė Mozei:",
			"14": "<sup>14</sup> “Išvesk piktžodžiautoją iš stovyklos. Visi, kurie girdėjo jį piktžodžiaujant, teuždeda savo rankas ant jo galvos, o visi žmonės teužmuša jį akmenimis.",
			"15": "<sup>15</sup> Izraelitams sakyk: ‘Kas keikia Dievą, atsakys už savo nusikaltimą.",
			"16": "<sup>16</sup> Kas piktžodžiaus Viešpaties vardui, turės mirti­jis bus užmuštas akmenimis, ar jis bus vietinis, ar ateivis. Kas piktžodžiaus Viešpaties vardui, bus baudžiamas mirtimi.",
			"17": "<sup>17</sup> Kas nužudo žmogų, taip pat turi mirti.",
			"18": "<sup>18</sup> Jei kas užmuša gyvulį, atlygins gyvuliu už gyvulį.",
			"19": "<sup>19</sup> Kas sužalotų savo artimą, jam bus padaryta taip, kaip jis padarė:",
			"20": "<sup>20</sup> sulaužymas už sulaužymą, akis už akį, dantis už dantį.",
			"21": "<sup>21</sup> Užmušęs gyvulį, atlygins; užmušęs žmogų, bus baudžiamas mirtimi.",
			"22": "<sup>22</sup> Tas pats įstatymas galioja ateiviui ir vietiniam, nes Aš esu Viešpats, jūsų Dievas’ ”.",
			"23": "<sup>23</sup> Po to izraelitai išvedė piktžodžiautoją iš stovyklos ir užmušė jį akmenimis. Jie padarė, kaip Viešpats įsakė Mozei."
		},
		"25": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei Sinajaus kalne:",
			"2": "<sup>2</sup> “Sakyk izraelitams, kad, įėję į šalį, kurią jiems duosiu, septintaisiais metais leistų žemei ilsėtis.",
			"3": "<sup>3</sup> Šešerius metus apsėkite dirvą, karpykite vynuogyną ir rinkite vaisius.",
			"4": "<sup>4</sup> Septintieji metai bus sabato poilsis žemei, metai, paskirti Viešpačiui. Tada laukų neapsėkite ir vynuogyno nekarpykite.",
			"5": "<sup>5</sup> Ką žemė savaime užaugins, nepjaukite ir vynuogių nuo neapkarpytų vynmedžio šakelių nerinkite, nes tai yra poilsio metai žemei.",
			"6": "<sup>6</sup> Maistas jums, tarnams, tarnaitėms, samdiniams ir ateiviams, kurie pas jus gyvena,",
			"7": "<sup>7</sup> taip pat jūsų gyvuliams bus visa, kas savaime užaugs.",
			"8": "<sup>8</sup> Skaičiuok septynis kartus po septynerius metus, iš viso keturiasdešimt devynerius metus.",
			"9": "<sup>9</sup> Paskutinių metų septinto mėnesio dešimtą dieną, sutaikinimo dieną, trimituokite visame krašte",
			"10": "<sup>10</sup> ir paskelbkite, kad penkiasdešimtieji metai yra jubiliejaus metai ir visiems krašto gyventojams suteikiama laisvė. Kiekvienas turės teisę atgauti nuosavybę ir grįžti į savo šeimą.",
			"11": "<sup>11</sup> Nesėkite ir nepjaukite, kas savaime užaugs dirvose, uogų nerinkite neapkarpytuose vynuogynuose,",
			"12": "<sup>12</sup> nes tai iškilmingi jubiliejaus metai. Valgykite tai, kas savaime užaugs.",
			"13": "<sup>13</sup> Jubiliejaus metais kiekvienas atgaus savo buvusią nuosavybę.",
			"14": "<sup>14</sup> Parduodant ar perkant ką nors iš kaimyno, neskriauskite vienas kito,",
			"15": "<sup>15</sup> bet pirkite, atsižvelgdami į metų skaičių, praėjusį nuo jubiliejaus metų.",
			"16": "<sup>16</sup> Kuo daugiau bus likę derliaus metų, tuo aukštesnė bus kaina; kuo mažiau derliaus metų, tuo mažesnė bus pardavimo kaina, nes jis tau parduos pagal pjūčių skaičių.",
			"17": "<sup>17</sup> Neskriauskite vienas kito, būkite sąžiningi, nes Aš esu Viešpats, jūsų Dievas.",
			"18": "<sup>18</sup> Laikykitės mano įsakymų ir paliepimų, vykdykite juos, kad gyventumėte be baimės",
			"19": "<sup>19</sup> ir žemė duotų vaisių, kuriais pasisotintumėte ir laimingai gyventumėte.",
			"20": "<sup>20</sup> Gal klausite: ‘Ką valgysime septintaisiais metais, jei nesėsime ir nerinksime vaisių?’",
			"21": "<sup>21</sup> Aš palaiminsiu šeštuosius metus, ir jie duos jums trejų metų derlių.",
			"22": "<sup>22</sup> Sėsite aštuntaisiais metais, maitinsitės turimomis atsargomis ligi devintųjų metų, kol naujas derlius užaugs.",
			"23": "<sup>23</sup> Žemė nebus parduodama visam laikui, nes ji mano, o jūs esate tik svečiai ir ateiviai.",
			"24": "<sup>24</sup> Todėl visa jūsų žemė bus parduodama su išpirkimo teise.",
			"25": "<sup>25</sup> Jei suvargęs tavo brolis parduotų nuosavybės dalį, jo giminaitis gali ją išpirkti.",
			"26": "<sup>26</sup> O jei jis neturėtų giminaičio, bet pats galėtų išpirkti,",
			"27": "<sup>27</sup> bus nustatyta kaina už tą laiką, kai ji buvo parduota, o kas lieka, sugrąžins pirkėjui, ir taip atgaus savo nuosavybę.",
			"28": "<sup>28</sup> Jei neturės reikiamos sumos žemei išpirkti, pirkėjas turės, ką nupirko, ligi jubiliejaus metų; tais metais nuosavybė sugrįš pirmajam šeimininkui.",
			"29": "<sup>29</sup> Kas parduotų namus miesto ribose, tam bus leista išsipirkti juos per vienerius metus.",
			"30": "<sup>30</sup> Jei neišsipirks per metus, jie pasiliks pirkėjui ir jo palikuonims; jų nereikės grąžinti jubiliejaus metais.",
			"31": "<sup>31</sup> O jei namai būtų kaime, kuris neturi apsaugos sienų, bus parduodami kaip laukai; jei nebus išpirkti anksčiau, jubiliejaus metais sugrįš buvusiam savininkui.",
			"32": "<sup>32</sup> Levitų namai, esantys jų miestuose, visada gali būti išpirkti.",
			"33": "<sup>33</sup> Jei nebus išpirkti, jubiliejaus metais sugrįš savininkams, nes namai levitų miestuose yra jų nuosavybė tarp izraelitų.",
			"34": "<sup>34</sup> Jų priemiesčio laukai neparduodami, nes tai jų amžina nuosavybė.",
			"35": "<sup>35</sup> Jei tavo brolis izraelitas taptų beturčiu, jį priimk ir juo rūpinkis, nors jis tau būtų svetimas.",
			"36": "<sup>36</sup> Neimk iš jo palūkanų, bijok Dievo. Tegul tavo brolis gyvena pas tave.",
			"37": "<sup>37</sup> Neskolink jam pinigų už palūkanas ir neduok maisto, siekdamas pasipelnyti.",
			"38": "<sup>38</sup> Aš, Viešpats, jūsų Dievas, kuris jus išvedžiau iš Egipto šalies, kad jums duočiau Kanaano žemę ir būčiau jūsų Dievas.",
			"39": "<sup>39</sup> Jei vargo priverstas tavo brolis pats tau parsiduotų, nesielk su juo kaip su vergu.",
			"40": "<sup>40</sup> Tetarnauja jis tau kaip samdinys ir įnamis ligi jubiliejaus metų.",
			"41": "<sup>41</sup> Tuomet jis su savo vaikais grįš pas savo giminę į tėvų namus.",
			"42": "<sup>42</sup> Izraelitai yra mano tarnai, Aš juos išvedžiau iš Egipto; jie neturi būti parduodami kaip vergai.",
			"43": "<sup>43</sup> Neišnaudokit jų, bijokit Dievo.",
			"44": "<sup>44</sup> Vergą ir vergę pirkite iš aplink jus esančių tautų,",
			"45": "<sup>45</sup> iš ateivių, pasilikusių gyventi tarp jūsų, ir iš jūsų žemėje gimusių vergų; šiuos galėsite turėti vergais.",
			"46": "<sup>46</sup> Juos galėsite palikti kaip paveldėjimą savo palikuonims per amžius. Savo brolių izraelitų nespauskite be gailesčio.",
			"47": "<sup>47</sup> Jei tarp jūsų pralobtų ateivis ar svetimtautis ir jam ar kuriam nors iš jo palikuonių parsiduotų nuskurdęs tavo brolis,",
			"48": "<sup>48</sup> jis galės būti išpirktas. Jei norės, jį galės išpirkti kas nors iš jo brolių:",
			"49": "<sup>49</sup> dėdė, brolėnas arba artimas giminaitis; o jei pajėgs, ir jis pats galės išsipirkti.",
			"50": "<sup>50</sup> Jis skaitys metus nuo parsidavimo ligi jubiliejaus metų ir atskaitys iš sumos, už kurią buvo parsidavęs, lyg būtų buvęs samdinys.",
			"51": "<sup>51</sup> Jei bus likę daug metų ligi jubiliejaus, pagal juos atsilygins šeimininkui.",
			"52": "<sup>52</sup> Jei metų skaičius mažesnis, apskaičiuos su juo pagal likusių metų skaičių ir sumokės išpirkimo kainą.",
			"53": "<sup>53</sup> Jis turi būti kaip samdinys visus tuos metus ir šeimininkas nesielgs su juo be gailesčio tavo akivaizdoje.",
			"54": "<sup>54</sup> O jei jis nebus išpirktas, jis išeis jubiliejaus metais kartu su savo vaikais.",
			"55": "<sup>55</sup> Izraelitai yra mano tarnai, kuriuos išvedžiau iš Egipto. Aš esu Viešpats, jūsų Dievas”."
		},
		"26": {
			"1": "<sup>1</sup> “Nedirbkite sau stabų nei drožinių, nestatykite savo žemėje statulų nei akmeninių atvaizdų ir jų negarbinkite, nes Aš esu Viešpats, jūsų Dievas.",
			"2": "<sup>2</sup> Švęskite sabatus ir gerbkite mano šventyklą. Aš esu Viešpats.",
			"3": "<sup>3</sup> Jei elgsitės pagal mano įstatymus, laikysitės jų ir juos vykdysite,",
			"4": "<sup>4</sup> duosiu jums lietaus tinkamu metu, ir žemė duos derlių, ir medžiai bus pilni vaisių.",
			"5": "<sup>5</sup> Javų kūlimas tęsis ligi vynuogių rinkimo ir vynuogių rinkimas­ligi sėjos; valgysite duonos sočiai ir gyvensite saugiai krašte.",
			"6": "<sup>6</sup> Aš duosiu taiką jūsų žemei ir jūs gulsite nieko nebijodami. Pašalinsiu plėšriuosius žvėris ir karo nebus jūsų krašte.",
			"7": "<sup>7</sup> Vysite priešus, ir jie kris jūsų akivaizdoje.",
			"8": "<sup>8</sup> Penkiese vysite šimtą, ir šimtas­dešimt tūkstančių; jūsų priešai kris nuo kardo.",
			"9": "<sup>9</sup> Aš pažvelgsiu į jus, padarysiu jus vaisingus ir padauginsiu jus. Aš patvirtinsiu su jumis savo sandorą.",
			"10": "<sup>10</sup> Metų derliaus užteks iki naujos pjūties; likutį turėsite pašalinti, kad turėtumėte vietos naujam derliui.",
			"11": "<sup>11</sup> Gyvensiu tarp jūsų ir mano siela nesibjaurės jumis.",
			"12": "<sup>12</sup> Vaikščiosiu tarp jūsų ir būsiu jūsų Dievas, o jūs būsite mano tauta.",
			"13": "<sup>13</sup> Aš, Viešpats, jūsų Dievas, kuris jus išvedžiau iš Egipto žemės, kad nevergautumėte. Aš sutraukiau jūsų jungo pančius, kad jūs vaikščiotumėte atsitiesę.",
			"14": "<sup>14</sup> O jei manęs neklausysite ir nevykdysite mano įsakymų,",
			"15": "<sup>15</sup> niekinsite mano įstatymus, nenorėdami daryti to, kas mano įsakyta, ir taip sulaužysite mano sandorą,",
			"16": "<sup>16</sup> štai ką Aš jums padarysiu: išgąstis ir ligos apakins ir naikins jus. Veltui sėsite, nes derlių suvalgys jūsų priešai.",
			"17": "<sup>17</sup> Atgręšiu į jus savo veidą, ir jūs būsite žudomi savo priešų akivaizdoje. Jus pavergs tie, kurių jūs nekenčiate, ir jūs bėgsite niekam jūsų nevejant.",
			"18": "<sup>18</sup> O jei ir tada neklausysite, padidinsiu jums bausmę septyneriopai",
			"19": "<sup>19</sup> ir palaušiu jūsų išdidumą. Dangų virš jūsų padarysiu kaip geležį ir žemę­kaip varį.",
			"20": "<sup>20</sup> Niekais nueis jūsų darbas: žemė neduos derliaus ir medžiai neneš vaisių.",
			"21": "<sup>21</sup> Jei ir toliau man priešinsitės ir nenorėsite klausyti, dar septyneriopai padidinsiu jums bausmę pagal jūsų nuodėmes.",
			"22": "<sup>22</sup> Siųsiu laukinius žvėris, ir jie žudys jūsų vaikus ir gyvulius taip, kad keliai ištuštės.",
			"23": "<sup>23</sup> O jei ir tada nesikeisite ir vis priešinsitės,",
			"24": "<sup>24</sup> tai ir Aš jums priešinsiuos ir bausiu jus dar septynis kartus stipriau;",
			"25": "<sup>25</sup> leisiu kardui naikinti jus už sandoros laužymą. Kai subėgsite į miestus, siųsiu marą, ir jūs būsite priversti pasiduoti priešui.",
			"26": "<sup>26</sup> Kai Aš sulaužysiu jūsų duonos ramstį, dešimt moterų keps duoną vienoje krosnyje ir dalins ją pagal svorį; valgysite ir nepasisotinsite.",
			"27": "<sup>27</sup> O jei ir tada manęs neklausysite ir priešinsitės,",
			"28": "<sup>28</sup> tai mano bausmė bus dar septyneriopai didesnė negu anksčiau.",
			"29": "<sup>29</sup> Tada jūs valgysite savo sūnų ir dukterų kūnus.",
			"30": "<sup>30</sup> Išardysiu jūsų stabų garbinimo aukštąsias vietas ir numesiu jūsų lavonus ant jūsų stabų liekanų, ir bjaurėsiuos jumis.",
			"31": "<sup>31</sup> Jūsų miestus paversiu tyrais, šventyklas padarysiu tuščias ir daugiau nebepriimsiu jūsų aukų.",
			"32": "<sup>32</sup> Sunaikinsiu jūsų kraštą taip, kad net jūsų priešai, kurie jame gyvens, stebėsis.",
			"33": "<sup>33</sup> Karais jus išsklaidysiu tarp tautų; jūsų kraštas bus paverstas dykuma, miestai sugriauti.",
			"34": "<sup>34</sup> Tada žemė džiaugsis sabato poilsiu, kai bus apleista jums esant priešų žemėje.",
			"35": "<sup>35</sup> Ji ilsėsis, kol bus apleista, nes nepailsėjo jūsų sabato metu, kai joje gyvenote.",
			"36": "<sup>36</sup> Priešų krašte išlikusiųjų širdis pripildysiu baime; juos gąsdins krintančio lapo šlamėjimas ir jie bėgs kaip nuo kardo niekam nesivejant,",
			"37": "<sup>37</sup> ir sukniubs vieni ant kitų, lyg bėgdami iš kovos lauko. Niekas iš jūsų nedrįs priešintis,",
			"38": "<sup>38</sup> žūsite išsklaidyti priešų tautose, ir svetima žemė jus sunaikins.",
			"39": "<sup>39</sup> Išlikusieji merdės priešų žemėje ir kentės už savo tėvų ir savąsias nuodėmes,",
			"40": "<sup>40</sup> kol neišpažins savųjų ir savo tėvų nedorybių ir nusikaltimų ir kad jie priešinosi man,",
			"41": "<sup>41</sup> už ką Aš priešinausi jiems ir išvedžiau juos į priešų žemę. Jei jų neapipjaustytos širdys nusižemins ir priims bausmę už savo kaltes,",
			"42": "<sup>42</sup> Aš prisiminsiu sandorą, kurią padariau su Jokūbu, Izaoku ir Abraomu, ir prisiminsiu žemę,",
			"43": "<sup>43</sup> kuri jų palikta džiaugiasi sabato poilsiu. Jie gi atsitrauks nuo savo nuodėmių, kai supras, kad buvo atmetę mano įsakymus ir paniekinę įstatymus.",
			"44": "<sup>44</sup> Tačiau, jiems esant priešų žemėje, Aš neišsižadėsiu jų ir nesibjaurėsiu taip, kad jie būtų visiškai sunaikinti, ir savo sandoros su jais neišardysiu. Aš esu Viešpats, jų Dievas.",
			"45": "<sup>45</sup> Tada atsiminsiu dėl jų sandorą, kurią sudariau su jų protėviais, kai juos išvedžiau iš Egipto, kad būčiau jų Dievas, nes Aš esu Viešpats”.",
			"46": "<sup>46</sup> Šituos nuostatus ir įstatymus izraelitams Viešpats davė per Mozę Sinajaus kalne."
		},
		"27": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk izraelitams: ‘Jei kas padarytų įžadą ir pasižadėtų tarnauti Dievui, mokės nustatytą kainą.",
			"3": "<sup>3</sup> Vyras nuo dvidešimties ligi šešiasdešimties metų mokės penkiasdešimt šekelių sidabro pagal šventyklos šekelį;",
			"4": "<sup>4</sup> moteris­trisdešimt.",
			"5": "<sup>5</sup> Vyras nuo penkerių ligi dvidešimties metų­dvidešimt šekelių sidabro, o moteris­dešimt.",
			"6": "<sup>6</sup> Už berniuką nuo vieno mėnesio ligi penkerių metų­penkis šekelius sidabro, už mergaitę­tris.",
			"7": "<sup>7</sup> Vyras šešiasdešimties metų ir vyresnis mokės penkiolika šekelių sidabro, moteris­dešimt.",
			"8": "<sup>8</sup> Jei bus beturtis ir paskirtos sumos negalės sumokėti, kreipsis į kunigą, kuris nustatys, kiek tas asmuo gali duoti.",
			"9": "<sup>9</sup> Jei kas pažadėtų duoti gyvulį, tinkamą aukoti Viešpačiui, tai tas pažadėtasis gyvulys",
			"10": "<sup>10</sup> negalės būti pakeistas nei geresniu, nei blogesniu. O jei pakeistų, ir pakeistasis, ir pirmykštis priklausys Viešpačiui.",
			"11": "<sup>11</sup> Jei kas pažadėtų duoti nešvarų gyvulį, kuris netinka aukai Viešpačiui, jis bus atvestas pas kunigą,",
			"12": "<sup>12</sup> kuris nuspręs, ar jis tinka, ir nustatys jo kainą.",
			"13": "<sup>13</sup> Jei aukojantis norės jį išpirkti, pridės prie įkainojimo sumos dar penktą dalį.",
			"14": "<sup>14</sup> Jei kas pažadėtų aukoti namus, pašvęsdamas juos Viešpačiui, kunigas apžiūrės, ar jie tinkami, ir nustatys jų vertę.",
			"15": "<sup>15</sup> O jei aukotojas norėtų juos išpirkti, pridės prie nustatytos sumos dar penktą dalį ir namai bus jo.",
			"16": "<sup>16</sup> Jei kas pažadėtų aukoti savo paveldėtą žemę, pašvęsdamas ją Viešpačiui, jos vertė bus nustatyta pagal sėją. Jei žemė apsėjama vienu homeru miežių, ji bus įkainota penkiasdešimt šekelių sidabro.",
			"17": "<sup>17</sup> Jei jis pašvenčia dirvą tuojau po jubiliejaus metų, jos vertė bus visa.",
			"18": "<sup>18</sup> O jei ji pašvenčiama kuriam laikui praėjus po jubiliejaus metų, kunigas apskaičiuos jos vertę pagal metų skaičių, likusį ligi jubiliejaus metų.",
			"19": "<sup>19</sup> Jei aukotojas panorės išpirkti ją, pridės penktą dalį prie paskirtos kainos ir ją atsiims.",
			"20": "<sup>20</sup> Jei nenorėtų išpirkti ir ji bus parduota kitam, aukotojas nebegalės vėliau jos išpirkti,",
			"21": "<sup>21</sup> nes, atėjus jubiliejaus metams, ji bus pašvęsta Viešpačiui, o pašvęsti dalykai priklauso kunigams.",
			"22": "<sup>22</sup> Jei pirktinė, o ne paveldėta dirva būtų pažadėta Viešpačiui,",
			"23": "<sup>23</sup> kunigas apskaičiuos jos vertę pagal skaičių metų ligi jubiliejaus, ir aukotojas tą sumą paaukos Viešpačiui.",
			"24": "<sup>24</sup> Jubiliejaus metais ji grįš pirmajam savininkui, kuris ją valdė kaip savo paveldo dalį.",
			"25": "<sup>25</sup> Kiekvienas įkainojimas bus daromas pagal šventyklos šekelį. Šekelį sudaro dvidešimt gerų.",
			"26": "<sup>26</sup> Pirmagimių gyvulių, kurie priklauso Viešpačiui, niekas negalės pašvęsti: ar tai bus jautis, ar avis,­jie yra Viešpaties.",
			"27": "<sup>27</sup> Jei gyvulys yra nešvarus, aukotojas išpirks jį pagal įkainojimą ir pridės penktą kainos dalį; jei išpirkti nenorėtų, bus parduotas už tiek, kiek buvo įkainotas.",
			"28": "<sup>28</sup> Visa, kas Viešpačiui pašvęsta,­ar tai žmogus, ar gyvulys, ar dirva­nebus parduodama ir negali būti išpirkta. Visi Viešpačiui pašvęsti dalykai yra Jo.",
			"29": "<sup>29</sup> Nieko iš žmonių, kurie yra pašvęsti, negalima išpirkti, bet jie turi mirti.",
			"30": "<sup>30</sup> Visos javų ir vaisių derliaus dešimtinės yra Viešpaties ir Jam priklauso.",
			"31": "<sup>31</sup> Jei kas norėtų išpirkti savo dešimtines, pridės penktą jų vertės dalį.",
			"32": "<sup>32</sup> Galvijų, avių, ožkų ir viso, kas piemenų ganoma, kiekviena dešimta galva bus pašvęsta Viešpačiui.",
			"33": "<sup>33</sup> Gyvuliai nebus atrenkami ir nebus vienas kitu pakeičiami. Jei kas pakeistų, tai abu gyvuliai bus pašvęsti Viešpačiui ir nebus išperkami’ ”.",
			"34": "<sup>34</sup> Šituos įsakymus Viešpats davė Mozei dėl Izraelio vaikų Sinajaus kalne."
		}
	}
};
module.exports = book;