var book = {
	"name": "Skaičių",
	"numChapters": 36,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Antraisiais metais, izraelitams išėjus iš Egipto, antro mėnesio pirmą dieną Viešpats kalbėjo Mozei Sinajaus dykumoje Susitikimo palapinėje:",
			"2": "<sup>2</sup> “Suskaičiuokite visus izraelitų vyrus pagal jų kilmę ir šeimas,",
			"3": "<sup>3</sup> visus dvidešimties metų ir vyresnius vyrus, tinkamus karui. Tu ir Aaronas suskaičiuokite juos pagal jų būrius.",
			"4": "<sup>4</sup> Jums padės kiekvienos giminės vyresnieji.",
			"5": "<sup>5</sup> Jų vardai: iš Rubeno­Šedeūro sūnus Elicūras,",
			"6": "<sup>6</sup> iš Simeono­Cūrišadajo sūnus Šelumielis,",
			"7": "<sup>7</sup> iš Judo­Aminadabo sūnus Naasonas,",
			"8": "<sup>8</sup> iš Isacharo­Cuaro sūnus Netanelis,",
			"9": "<sup>9</sup> iš Zabulono­Helono sūnus Eliabas,",
			"10": "<sup>10</sup> iš Juozapo sūnų: iš Efraimo­Amihudo sūnus Elišama ir iš Manaso­ Pedacūro sūnus Gamelielis,",
			"11": "<sup>11</sup> iš Benjamino­Gideonio sūnus Abidanas,",
			"12": "<sup>12</sup> iš Dano­Amišadajo sūnus Ahiezeras,",
			"13": "<sup>13</sup> iš Ašero­Ochrano sūnus Pagielis,",
			"14": "<sup>14</sup> iš Gado­Deuelio sūnus Eljasafas,",
			"15": "<sup>15</sup> iš Neftalio­Enano sūnus Ahyra”.",
			"16": "<sup>16</sup> Šitie yra tautos išrinktieji, atskirų giminių kunigaikščiai ir Izraelio tūkstančių vadai.",
			"17": "<sup>17</sup> Mozė ir Aaronas su šiais išvardintais vyrais",
			"18": "<sup>18</sup> antrojo mėnesio pirmą dieną surinko vyrus pagal jų gimines ir šeimas, visus turėjusius dvidešimt metų ir vyresnius,",
			"19": "<sup>19</sup> kaip Viešpats buvo įsakęs Mozei, ir suskaičiavo juos Sinajaus dykumoje.",
			"20": "<sup>20</sup> Izraelio pirmagimio Rubeno giminės vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"21": "<sup>21</sup> buvo suskaičiuota keturiasdešimt šeši tūkstančiai penki šimtai.",
			"22": "<sup>22</sup> Iš Simeono sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"23": "<sup>23</sup> buvo suskaičiuota penkiasdešimt devyni tūkstančiai trys šimtai.",
			"24": "<sup>24</sup> Iš Gado sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"25": "<sup>25</sup> buvo suskaičiuota keturiasdešimt penki tūkstančiai šeši šimtai penkiasdešimt.",
			"26": "<sup>26</sup> Iš Judo sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"27": "<sup>27</sup> buvo suskaičiuota septyniasdešimt keturi tūkstančiai šeši šimtai.",
			"28": "<sup>28</sup> Iš Isacharo sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"29": "<sup>29</sup> buvo suskaičiuota penkiasdešimt keturi tūkstančiai keturi šimtai.",
			"30": "<sup>30</sup> Iš Zabulono sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"31": "<sup>31</sup> buvo suskaičiuota penkiasdešimt septyni tūkstančiai keturi šimtai.",
			"32": "<sup>32</sup> Iš Juozapo giminės, Efraimo sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"33": "<sup>33</sup> buvo suskaičiuota keturiasdešimt tūkstančių penki šimtai.",
			"34": "<sup>34</sup> Iš Manaso sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"35": "<sup>35</sup> buvo suskaičiuota trisdešimt du tūkstančiai du šimtai.",
			"36": "<sup>36</sup> Iš Benjamino sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"37": "<sup>37</sup> buvo suskaičiuota trisdešimt penki tūkstančiai keturi šimtai.",
			"38": "<sup>38</sup> Iš Dano sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"39": "<sup>39</sup> buvo suskaičiuota šešiasdešimt du tūkstančiai septyni šimtai.",
			"40": "<sup>40</sup> Iš Ašero sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"41": "<sup>41</sup> buvo suskaičiuota keturiasdešimt vienas tūkstantis penki šimtai.",
			"42": "<sup>42</sup> Iš Neftalio sūnų vyrų, dvidešimties metų ir vyresnių, tinkančių eiti į karą, pagal jų kilmę ir šeimas",
			"43": "<sup>43</sup> buvo suskaičiuota penkiasdešimt trys tūkstančiai keturi šimtai.",
			"44": "<sup>44</sup> Tai vyrai, kuriuos suskaičiavo Mozė, Aaronas ir dvylika Izraelio vyresniųjų, kiekvieną pagal jų kilmę, giminę ir šeimas,",
			"45": "<sup>45</sup> dvidešimties metų ir vyresni, tinkantys eiti į karą.",
			"46": "<sup>46</sup> Iš viso jų buvo suskaičiuota šeši šimtai trys tūkstančiai penki šimtai penkiasdešimt.",
			"47": "<sup>47</sup> Nebuvo priskaičiuoti tik levitai,",
			"48": "<sup>48</sup> nes Viešpats kalbėjo Mozei:",
			"49": "<sup>49</sup> “Levio giminės vyrų neskaičiuok kartu su Izraelio sūnumis,",
			"50": "<sup>50</sup> bet paskirk juos šventai tarnystei prie Susitikimo palapinės. Jie nešios palapinę ir visus jos daiktus ir tarnaus prie jos bei gyvens aplink palapinę.",
			"51": "<sup>51</sup> Keliantis į kitą vietą, levitai išardys palapinę, o sustojus­vėl išties ją. Jei kas pašalinis prisiartintų prie jos, bus baudžiamas mirtimi.",
			"52": "<sup>52</sup> Izraelitai statys savo palapines kiekvienas savoje stovykloje bei jiems paskirtoje vietoje,",
			"53": "<sup>53</sup> o levitai statys savo palapines aplink Susitikimo palapinę, kad Izraelio vaikai neiššauktų mano rūstybės. Levitai eis sargybą prie Susitikimo palapinės”.",
			"54": "<sup>54</sup> Izraelitai padarė visa, ką Viešpats buvo įsakęs Mozei."
		},
		"2": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"2": "<sup>2</sup> “Izraelitai privalo ištiesti savo palapines aplink Sandoros palapinę pagal giminės eilę, kiekviena giminė su savo vėliava.",
			"3": "<sup>3</sup> Rytų pusėje savo būrių palapines išties Judas, jo vadas yra Aminadabo sūnus Naasonas.",
			"4": "<sup>4</sup> Jo giminės vyrų, tinkančių eiti į karą, yra septyniasdešimt keturi tūkstančiai šeši šimtai.",
			"5": "<sup>5</sup> Šalia jo įrengs savo stovyklą Isacharo giminė, jos vadas yra Cuaro sūnus Netanelis.",
			"6": "<sup>6</sup> Jo karių yra penkiasdešimt keturi tūkstančiai keturi šimtai.",
			"7": "<sup>7</sup> Zabulono giminės vadas yra Helono sūnus Eliabas.",
			"8": "<sup>8</sup> Jo giminės vyrų, tinkančių karui, yra penkiasdešimt septyni tūkstančiai keturi šimtai.",
			"9": "<sup>9</sup> Viso Judo stovykloje yra šimtas aštuoniasdešimt šeši tūkstančiai keturi šimtai vyrų. Jie eis pirmieji.",
			"10": "<sup>10</sup> Pietinėje pusėje Rubeno stovyklai vadovaus Šedeūro sūnus Elicūras.",
			"11": "<sup>11</sup> Jo vyrų, tinkančių karui, yra keturiasdešimt šeši tūkstančiai penki šimtai.",
			"12": "<sup>12</sup> Šalia jo įrengs stovyklą Simeono giminė, kurios vadas yra Cūrišadajo sūnus Šelumielis.",
			"13": "<sup>13</sup> Jo tinkančių karui vyrų skaičius yra penkiasdešimt devyni tūkstančiai trys šimtai.",
			"14": "<sup>14</sup> Gado giminės vadas yra Deuelio sūnus Eljasafas.",
			"15": "<sup>15</sup> Jo karių skaičius yra keturiasdešimt penki tūkstančiai šeši šimtai.",
			"16": "<sup>16</sup> Viso Rubeno stovykloje yra šimtas penkiasdešimt vienas tūkstantis keturi šimtai penkiasdešimt vyrų. Rubeno būriai eis antri.",
			"17": "<sup>17</sup> Levitai žygiuos viduryje ir neš Susitikimo palapinę. Jie eis ta tvarka, kaip apsistoja, kiekvienas prie savo vėliavos.",
			"18": "<sup>18</sup> Vakaruose bus Efraimo sūnų stovykla. Jų vadas yra Amihudo sūnus Elišama.",
			"19": "<sup>19</sup> Jo vyrų, tinkančių eiti į karą, yra keturiasdešimt tūkstančių penki šimtai.",
			"20": "<sup>20</sup> Šalia jų bus Manaso giminė. Jos vadas yra Pedacūro sūnus Gamelielis.",
			"21": "<sup>21</sup> Jo vyrų, tinkančių karui, yra trisdešimt du tūkstančiai du šimtai.",
			"22": "<sup>22</sup> Benjamino giminės vadas yra Gideonio sūnus Abidanas.",
			"23": "<sup>23</sup> Jo vyrų, tinkančių karui, yra trisdešimt penki tūkstančiai keturi šimtai.",
			"24": "<sup>24</sup> Viso Efraimo stovykloje yra šimtas aštuoni tūkstančiai šimtas karių. Efraimo būriai eis treti.",
			"25": "<sup>25</sup> Šiaurėje bus Dano stovykla. Jos vadas yra Amišadajo sūnus Ahiezeras.",
			"26": "<sup>26</sup> Jo vyrų, tinkančių karui, yra šešiasdešimt du tūkstančiai septyni šimtai.",
			"27": "<sup>27</sup> Šalia jo išties savo palapines Ašero giminė, kurios vadas yra Ochrano sūnus Pagielis.",
			"28": "<sup>28</sup> Jo vyrų, tinkančių karui, yra keturiasdešimt vienas tūkstantis penki šimtai.",
			"29": "<sup>29</sup> Šalia jų stovyklaus Neftalio giminė. Jos vadas yra Enano sūnus Ahyra.",
			"30": "<sup>30</sup> Jo vyrų, tinkančių karui, yra penkiasdešimt trys tūkstančiai keturi šimtai.",
			"31": "<sup>31</sup> Viso Dano stovykloje yra šimtas penkiasdešimt septyni tūkstančiai šeši šimtai karių. Jie eis paskutiniai”.",
			"32": "<sup>32</sup> Visų izraelitų vyrų pagal jų gimines, šeimas bei kariuomenės būrius buvo šeši šimtai trys tūkstančiai penki šimtai penkiasdešimt.",
			"33": "<sup>33</sup> Levitai nebuvo suskaičiuoti kartu su izraelitais, kaip Viešpats įsakė Mozei.",
			"34": "<sup>34</sup> Izraelitai viską padarė taip, kaip Viešpats įsakė. Jie sustodavo ir žygiuodavo būriais, giminėmis ir šeimomis."
		},
		"3": {
			"1": "<sup>1</sup> Tuo metu, kai Viešpats kalbėjo Sinajaus kalne, Aarono ir Mozės palikuonys buvo:",
			"2": "<sup>2</sup> Aarono pirmagimis sūnus Nadabas ir Abihuvas, Eleazaras bei Itamaras.",
			"3": "<sup>3</sup> Jie buvo patepti ir pašvęsti kunigų tarnystei.",
			"4": "<sup>4</sup> Bet Nadabas ir Abihuvas, aukoję svetimą ugnį Viešpačiui Sinajaus dykumose, mirė, nepalikdami vaikų. Aaronui gyvam esant, kunigų tarnystę ėjo Eleazaras ir Itamaras.",
			"5": "<sup>5</sup> Viešpats kalbėjo Mozei:",
			"6": "<sup>6</sup> “Pakviesk Levio giminę pas kunigą Aaroną, kad jam tarnautų.",
			"7": "<sup>7</sup> Jie atliks visus darbus, susijusius su Susitikimo palapine,",
			"8": "<sup>8</sup> prižiūrės jos daiktus ir tarnaus prie palapinės tarp Izraelio vaikų.",
			"9": "<sup>9</sup> Atiduok levitus Aaronui ir jo sūnums; jie visi yra atiduoti jam iš Izraelio vaikų.",
			"10": "<sup>10</sup> Aaroną ir jo sūnus paskirk kunigais. Jei kas iš pašalinių artinsis, bus baudžiamas mirtimi”.",
			"11": "<sup>11</sup> Viešpats kalbėjo Mozei:",
			"12": "<sup>12</sup> “Aš paėmiau levitus izraelitų pirmagimių vietoje.",
			"13": "<sup>13</sup> Kiekvienas pirmagimis yra mano nuo to laiko, kai išžudžiau pirmagimius Egipte; tuomet pašvenčiau sau izraelitų ir jų gyvulių pirmagimius. Jie yra mano. Aš esu Viešpats”.",
			"14": "<sup>14</sup> Izraelitams esant Sinajaus dykumoje, Viešpats kalbėjo Mozei:",
			"15": "<sup>15</sup> “Suskaičiuok levitus pagal jų šeimas, visus vyriškos giminės asmenis, nuo vieno mėnesio amžiaus”.",
			"16": "<sup>16</sup> Mozė suskaitė juos, kaip Viešpats buvo įsakęs.",
			"17": "<sup>17</sup> Štai Levio sūnų vardai: Geršonas, Kehatas ir Meraris.",
			"18": "<sup>18</sup> Geršono sūnūs: Libnis ir Šimis.",
			"19": "<sup>19</sup> Kehato sūnūs: Amramas ir Iccharas, Hebronas ir Uzielis.",
			"20": "<sup>20</sup> Merario sūnūs: Machlis ir Mušis. Tai yra Levio giminė pagal savo šeimas.",
			"21": "<sup>21</sup> Iš Geršono kilo dvi šeimos: libnių ir šimių.",
			"22": "<sup>22</sup> Jų, vyriškos lyties nuo vieno mėnesio amžiaus, suskaityta septyni tūkstančiai penki šimtai asmenų.",
			"23": "<sup>23</sup> Geršono šeimos turėjo statyti savo palapines už Susitikimo palapinės vakarų pusėje,",
			"24": "<sup>24</sup> vadovaujami Laelio sūnaus Eljasafo.",
			"25": "<sup>25</sup> Jie buvo atsakingi už Susitikimo palapinę, jos uždangalus, palapinės įėjimo užuolaidą,",
			"26": "<sup>26</sup> kiemo užkabas, įėjimo į kiemą užkabą, palapinės virves bei visus jos reikmenis.",
			"27": "<sup>27</sup> Kehato giminei priklausė amramų, iccharų, hebronų ir uzielitų šeimos. Tai yra kehatų šeimos.",
			"28": "<sup>28</sup> Jų, vyriškos lyties nuo vieno mėnesio amžiaus, suskaityta aštuoni tūkstančiai šeši šimtai asmenų. Jie prižiūrėjo šventyklą.",
			"29": "<sup>29</sup> Kehato sūnų šeimos statydavo palapines pietinėje Susitikimo palapinės pusėje.",
			"30": "<sup>30</sup> Jiems vadovavo Uzielio sūnus Elicafanas.",
			"31": "<sup>31</sup> Jie turėjo saugoti skrynią, stalą, žvakidę, aukurus, šventyklos indus, naudojamus tarnavimo metu, uždangą ir visus reikmenis.",
			"32": "<sup>32</sup> Levitų vadų vyriausias buvo Eleazaras, kunigo Aarono sūnus. Jis prižiūrės tuos, kurie rūpinasi šventykla.",
			"33": "<sup>33</sup> Iš Merario yra kilusios machlių ir mušių šeimos.",
			"34": "<sup>34</sup> Jų, vyriškos lyties nuo vieno mėnesio amžiaus, buvo šeši tūkstančiai du šimtai asmenų.",
			"35": "<sup>35</sup> Jų vadas buvo Abihailo sūnus Cūrielis, jų stovykla buvo šiaurinėje Susitikimo palapinės pusėje.",
			"36": "<sup>36</sup> Jie saugojo palapinės lentas, kartis, stulpus, jų pakojus,",
			"37": "<sup>37</sup> kiemo stulpus, statomus aplinkui, su jų pakojais, kuoleliais ir virvėmis.",
			"38": "<sup>38</sup> Priešais palapinę, rytinėje pusėje, pasistatydavo palapinę Mozė ir Aaronas su savo sūnumis. Jie saugojo šventyklą tarp Izraelio vaikų, nes jei prisiartintų kas iš pašalinių, turėjo būti baudžiamas mirtimi.",
			"39": "<sup>39</sup> Viešpačiui įsakius, Mozė ir Aaronas suskaitė visus levitus vyriškos lyties nuo vieno mėnesio amžiaus. Jų buvo dvidešimt du tūkstančiai asmenų.",
			"40": "<sup>40</sup> Viešpats tarė Mozei: “Dabar suskaičiuok visus izraelitų pirmagimius vyriškos lyties nuo vieno mėnesio amžiaus.",
			"41": "<sup>41</sup> Atskirk levitus man­Aš esu Viešpats­vietoj visų Izraelio vaikų pirmagimių ir levitų gyvulius vietoj izraelitų galvijų pirmagimių”.",
			"42": "<sup>42</sup> Mozė suskaitė izraelitų pirmagimius, kaip Viešpats buvo įsakęs.",
			"43": "<sup>43</sup> Visų vyriškos lyties pirmagimių nuo vieno mėnesio amžiaus buvo dvidešimt du tūkstančiai du šimtai septyniasdešimt trys.",
			"44": "<sup>44</sup> Viešpats kalbėjo Mozei:",
			"45": "<sup>45</sup> “Imk levitus vietoje izraelitų pirmagimių ir levitų gyvulius vietoje izraelitų gyvulių. Levitai bus mano. Aš esu Viešpats.",
			"46": "<sup>46</sup> Išpirk du šimtus septyniasdešimt tris izraelitų pirmagimius, nes jų yra daugiau negu levitų;",
			"47": "<sup>47</sup> imk nuo kiekvieno penkis šekelius sidabro pagal šventyklos šekelį. Šekelis susideda iš dvidešimt gerų.",
			"48": "<sup>48</sup> Atiduok pinigus Aaronui ir jo sūnums už tuos, kurie viršija levitų skaičių”.",
			"49": "<sup>49</sup> Mozė paėmė išpirkimo pinigus už tuos, kurie viršijo levitų skaičių,",
			"50": "<sup>50</sup> iš izraelitų pirmagimių paėmė pinigus: tūkstantį tris šimtus šešiasdešimt penkis šekelius sidabro pagal šventyklos šekelį.",
			"51": "<sup>51</sup> Juos atidavė Aaronui ir jo sūnums, vykdydamas Viešpaties įsakymą."
		},
		"4": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"2": "<sup>2</sup> “Suskaičiuok Kehato sūnus levitus pagal jų kilmę ir šeimas,",
			"3": "<sup>3</sup> visus nuo trisdešimties iki penkiasdešimties metų amžiaus; jie prižiūrės Susitikimo palapinę.",
			"4": "<sup>4</sup> Tokios bus kehatų pareigos Susitikimo palapinėje ir Švenčiausiojoje.",
			"5": "<sup>5</sup> Aaronas ir jo sūnūs, prieš iškeliaujant, nuims uždangą, kabančią prieš Švenčiausiąją, ir ja apdengs Liudijimo skrynią,",
			"6": "<sup>6</sup> ant viršaus užties opšrų kailiais ir mėlynu audiniu, ir įvers kartis.",
			"7": "<sup>7</sup> Taip pat padėtinės duonos stalą apdengs mėlyna drobe, sudės smilkytuvus ir indus, taures ir puodelius geriamosioms aukoms pilti, o padėtinę duoną uždės viršuje,",
			"8": "<sup>8</sup> užties raudona drobe, kurią apdengs opšrų kailių uždangalu, ir įvers kartis.",
			"9": "<sup>9</sup> Ims mėlyną audinį, juo apdengs žvakidę, lempas, gnybtuvus, indus nuognaiboms ir visus aliejaus indus;",
			"10": "<sup>10</sup> tai įvynios į opšrų kailių uždangalą ir įvers kartis.",
			"11": "<sup>11</sup> Auksinį aukurą apdengs mėlyna drobe, užties opšrų kailių uždangalu ir įvers kartis.",
			"12": "<sup>12</sup> Visus apeigoms naudojamus daiktus įvynios į mėlyną audinį, ant viršaus užties opšrų kailių uždangalu ir uždės ant neštuvų.",
			"13": "<sup>13</sup> Iš aukuro išims pelenus ir jį apdengs violetine drobe.",
			"14": "<sup>14</sup> Sudėję visus reikmenis, kurie vartojami prie aukuro: indus anglims, šakutes, semtuvėlius ir dubenis, užties opšrų kailių uždangalu ir įvers kartis.",
			"15": "<sup>15</sup> Aaronui ir jo sūnums apdengus šventyklą ir visus jos daiktus, keliantis stovyklai, kehatai atėję juos paims. Jiems negalima prisiliesti prie šventų daiktų, kad nemirtų. Tai yra kehatų pareiga Susitikimo palapinėje.",
			"16": "<sup>16</sup> Kunigo Aarono sūnus Eleazaras rūpinsis aliejumi lempoms, kvapniais smilkalais, padėtine duona, patepimo aliejumi ir viskuo, ko reikia šventyklos apeigoms, taip pat visais šventykloje esančiais daiktais”.",
			"17": "<sup>17</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"18": "<sup>18</sup> “Neleiskite kehatų giminei žūti.",
			"19": "<sup>19</sup> Tai darykite, kad jie liktų gyvi ir nemirtų, kai ateina prie šventų daiktų. Aaronas ir jo sūnūs paskirs kiekvienam darbą, kas ką turi nešti.",
			"20": "<sup>20</sup> Jiems negalima žiūrėti, kas yra Švenčiausiojoje, kol ji neapdengta, kad nenumirtų”.",
			"21": "<sup>21</sup> Viešpats kalbėjo Mozei:",
			"22": "<sup>22</sup> “Suskaičiuok taip pat Geršono sūnus pagal jų kilmę ir šeimas",
			"23": "<sup>23</sup> nuo trisdešimties iki penkiasdešimties metų amžiaus, visus, kurie tarnauja Sandoros palapinėje.",
			"24": "<sup>24</sup> Geršonų pareigos yra:",
			"25": "<sup>25</sup> nešti palapinės uždangalus, užuolaidą, pakabintą Susitikimo palapinės įėjime,",
			"26": "<sup>26</sup> kiemo, kuris yra aplinkui aukurą ir palapinę, užkabas, virves ir prie jų naudojamus įrankius; tai yra jų tarnavimas.",
			"27": "<sup>27</sup> Geršonai neš, ką lieps Aaronas ir jo sūnūs. Darbai jiems bus nurodyti ir paskirstyti.",
			"28": "<sup>28</sup> Tokia bus Geršono sūnų tarnystė Susitikimo palapinėje; jie bus kunigo Aarono sūnaus Itamaro žinioje.",
			"29": "<sup>29</sup> Suskaičiuok taip pat Merario sūnus pagal jų kilmę ir šeimas",
			"30": "<sup>30</sup> nuo trisdešimties ligi penkiasdešimties metų amžiaus, visus, kurie tarnauja Susitikimo palapinėje.",
			"31": "<sup>31</sup> Jų darbas bus nešti palapinės lentas, jos užkaiščius, stulpus ir pakojus;",
			"32": "<sup>32</sup> taip pat kiemo stulpus, statomus aplinkui, jų pakojus, kuolelius, virves ir visus jų reikmenis. Kiekvienam paskirsite, ką jis turi nešti.",
			"33": "<sup>33</sup> Tai yra Merario sūnų giminės pareiga ir tarnystė Susitikimo palapinėje; jie bus kunigo Aarono sūnaus Itamaro žinioje”.",
			"34": "<sup>34</sup> Mozė, Aaronas ir vyresnieji suskaitė kehatus pagal jų kilmę",
			"35": "<sup>35</sup> nuo trisdešimties ligi penkiasdešimties metų amžiaus, kurie gali tarnauti Susitikimo palapinėje.",
			"36": "<sup>36</sup> Jų buvo du tūkstančiai septyni šimtai penkiasdešimt.",
			"37": "<sup>37</sup> Juos suskaitė Mozė ir Aaronas, kaip Viešpats buvo įsakęs Mozei.",
			"38": "<sup>38</sup> Buvo suskaityta taip pat ir Geršono sūnūs pagal jų kilmę ir šeimas",
			"39": "<sup>39</sup> nuo trisdešimties ligi penkiasdešimties metų amžiaus, kurie gali tarnauti Susitikimo palapinėje.",
			"40": "<sup>40</sup> Jų buvo du tūkstančiai šeši šimtai trisdešimt.",
			"41": "<sup>41</sup> Geršono sūnus suskaitė Mozė ir Aaronas, kaip Viešpats buvo įsakęs.",
			"42": "<sup>42</sup> Buvo suskaityta taip pat ir Merario sūnūs pagal jų kilmę ir šeimas",
			"43": "<sup>43</sup> nuo trisdešimties ligi penkiasdešimties metų amžiaus, kurie gali tarnauti Susitikimo palapinėje.",
			"44": "<sup>44</sup> Jų buvo trys tūkstančiai du šimtai.",
			"45": "<sup>45</sup> Merario sūnus suskaitė Mozė ir Aaronas, kaip Viešpats buvo įsakęs Mozei.",
			"46": "<sup>46</sup> Visų levitų, kuriuos Mozė, Aaronas ir Izraelio vyresnieji suskaitė pagal jų kilmę ir šeimas",
			"47": "<sup>47</sup> nuo trisdešimties ligi penkiasdešimties metų amžiaus, galinčių tarnauti ir nešti Susitikimo palapinę,",
			"48": "<sup>48</sup> buvo aštuoni tūkstančiai penki šimtai aštuoniasdešimt asmenų.",
			"49": "<sup>49</sup> Juos suskaitė Mozė, kaip Viešpats buvo įsakęs, kiekvieną pagal jo tarnystę ir naštą."
		},
		"5": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Įsakyk izraelitams pašalinti iš stovyklos visus raupsuotus, visus turinčius plūdimą ir susitepusius mirusiu.",
			"3": "<sup>3</sup> Pašalinkite tokius vyrus ir tokias moteris, kad jie nesuteptų stovyklos, kurios viduryje Aš gyvenu”.",
			"4": "<sup>4</sup> Izraelitai taip ir padarė, ir juos apgyvendino už stovyklos ribų, kaip Viešpats buvo įsakęs Mozei.",
			"5": "<sup>5</sup> Viešpats kalbėjo Mozei:",
			"6": "<sup>6</sup> “Sakyk izraelitams: ‘Jei vyras ar moteris padarytų kurią nors nuodėmę, peržengtų Viešpaties įsakymą ir taip nusikalstų,",
			"7": "<sup>7</sup> tai jis prisipažins kaltas ir atlygins tam, kuriam nusikalto, atiduos tą patį daiktą ir, be to, pridės penktą dalį jo vertės.",
			"8": "<sup>8</sup> O jei nebūtų to, kuriam nusikalto, atiduos viską Viešpačiui, ir tai priklausys kunigui, neskaitant avino, kuris turi būti aukojamas, kad jis būtų sutaikintas.",
			"9": "<sup>9</sup> Visos aukos, kurias aukoja izraelitai, priklauso kunigui.",
			"10": "<sup>10</sup> Ką pavieniai asmenys aukoja šventykloje ir atiduoda kunigui, yra jo nuosavybė’ ”.",
			"11": "<sup>11</sup> Viešpats kalbėjo Mozei:",
			"12": "<sup>12</sup> “Sakyk izraelitams: ‘Jei kieno žmona nusidėtų, būdama neištikima savo vyrui,",
			"13": "<sup>13</sup> ir kitas vyras gulėtų su ja ir ją suterštų, tačiau tai įvyktų slaptai ir būtų paslėpta nuo jos vyro akių, ir nebūtų liudytojų, nes ji nebūtų sugauta,",
			"14": "<sup>14</sup> ir jei pavydo dvasia apimtų vyrą, nepaisant ar žmona iš tiesų susitepusi, ar tai tik neteisingas įtarimas,",
			"15": "<sup>15</sup> jis ją turi atvesti pas kunigą ir aukoti už ją dešimtą efos dalį miežinių miltų; nepils ant jų aliejaus ir nedės smilkalų, nes tai pavydo auka, primenanti nedorybę.",
			"16": "<sup>16</sup> Kunigas ją atves ir pastatys Viešpaties akivaizdoje.",
			"17": "<sup>17</sup> Jis, paėmęs švento vandens moliniame inde, įmes į jį truputį dulkių nuo palapinės aslos.",
			"18": "<sup>18</sup> Kai moteris atsistos Viešpaties akivaizdoje, jis atidengs jos galvą ir paduos jai į ranką pavydo auką; pats gi laikys kartaus vandens, kuris neša prakeikimą,",
			"19": "<sup>19</sup> ir prisaikdins ją, sakydamas: ‘Jei neturėjai santykių su svetimu vyru, tau nekenks šitas kartus vanduo.",
			"20": "<sup>20</sup> Bet jei buvai neištikima savo vyrui ir susitepei gulėdama su kitu,",
			"21": "<sup>21</sup> Viešpats padarys tave prakeikimu tavo tautoje, ir tavo šlaunys ims pūti, o tavo pilvas išsipūs.",
			"22": "<sup>22</sup> Šitas vanduo, nešantis prakeikimą, teįeina į tavo vidų, kad tavo pilvas išsipūstų ir šlaunys imtų pūti’. Moteris atsakys: ‘Amen, amen’.",
			"23": "<sup>23</sup> Kunigas surašys tuos prakeikimus ir juos nuplaus karčiuoju vandeniu.",
			"24": "<sup>24</sup> Jis duos moteriai gerti kartaus vandens, nešančio prakeikimą, kad tas vanduo patektų į moters vidų.",
			"25": "<sup>25</sup> Tada kunigas paims iš jos rankų pavydo auką, ją siūbuos Viešpaties akivaizdoje ir aukos ant aukuro.",
			"26": "<sup>26</sup> Jis ims saują aukojamųjų miltų ir sudegins ant aukuro. Po to vėl duos moteriai gerti kartaus vandens.",
			"27": "<sup>27</sup> Kai ji išgers, jei ji yra nusikaltusi svetimavimu, prakeikimo vanduo, įėjęs į ją, išpūs jos pilvą, ir jos šlaunys ims pūti, ir moteris bus prakeikimas savo tautoje.",
			"28": "<sup>28</sup> O jei ji bus nekalta, jai nepakenks, ir ji galės pastoti.",
			"29": "<sup>29</sup> Toks yra pavydo įstatymas.",
			"30": "<sup>30</sup> Jei vyras apimtas pavydo dvasios atvestų žmoną Viešpaties akivaizdon ir kunigas padarytų visa, kas čia parašyta,",
			"31": "<sup>31</sup> vyras bus nekaltas, o moteris susilauks bausmės, jeigu bus nusikaltusi’ ”."
		},
		"6": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk izraelitams: ‘Jei vyras ar moteris padarys nazarėno įžadą, kad pasišvęstų Viešpačiui,",
			"3": "<sup>3</sup> jis susilaikys nuo vyno ir stipraus gėrimo. Negers vynuogių sulčių nei jokio kito gėrimo, kas iš vynuogių išspaudžiama, nevalgys nei šviežių, nei džiovintų vynuogių.",
			"4": "<sup>4</sup> Visą laiką, kol yra įžadu pasišventęs Viešpačiui, nevalgys nieko, kas iš vynuogių, net sėklų ar odelės.",
			"5": "<sup>5</sup> Padaręs įžadą neskus galvos, kol pasibaigs įžado laikas. Visą savo pasišventimo laiką augins galvos plaukus.",
			"6": "<sup>6</sup> Per visą savo įžado laiką nepalies mirusio,",
			"7": "<sup>7</sup> net savo tėvu, motina, broliu ar seserimi nesusiteps, jei jie numirtų, nes įžado metu bus pasižadėjęs Dievui.",
			"8": "<sup>8</sup> Visą savo įžado laiką jis yra šventas Viešpačiui.",
			"9": "<sup>9</sup> Jei kas staiga mirtų šalia jo ir jis susiteptų, tada jis turės nusiskusti galvą septintą, apsivalymo, dieną.",
			"10": "<sup>10</sup> Aštuntą dieną atneš du balandžius ar du jaunus karvelius ir prie Susitikimo palapinės paduos juos kunigui.",
			"11": "<sup>11</sup> Kunigas vieną jų aukos už nuodėmę, o antrą­deginamąja auka, kad sutaikintų jį, nes jis susitepė mirusiu. Tą dieną jis pakartos įžadą",
			"12": "<sup>12</sup> ir atves metinį avinėlį aukai už kaltę. Ankstesnės dienos nebus įskaitytos, nes jo pasišventimas buvo suteptas.",
			"13": "<sup>13</sup> Tas yra nazarėno įstatymas. Pasibaigus įžado laikui, jį atves prie Susitikimo palapinės durų,",
			"14": "<sup>14</sup> kur jis aukos auką Viešpačiui: metinį sveiką avinėlį deginamajai aukai, metinę sveiką avelę aukai už nuodėmę ir sveiką aviną padėkos aukai,",
			"15": "<sup>15</sup> taip pat apšlakstytą aliejumi neraugintos duonos pintinę, neraugintų bandelių, apteptų aliejumi, ir geriamąją auką.",
			"16": "<sup>16</sup> Kunigas aukos Viešpačiui auką už nuodėmę ir deginamąją auką.",
			"17": "<sup>17</sup> Aviną aukos kaip padėkos auką Viešpačiui, kartu su neraugintos duonos pintine ir geriamąja auka.",
			"18": "<sup>18</sup> Tada prie Susitikimo palapinės durų nuskus nazarėnui plaukus; ir kunigas jo plaukus sudegins su padėkos auka.",
			"19": "<sup>19</sup> Kunigas, paėmęs išvirtą avino petį, vieną neraugintą bandelę iš pintinės bei neraugintą paplotį, įdės į nazarėno rankas po to, kai jo galva bus nuskusta.",
			"20": "<sup>20</sup> Paskui, paėmęs iš nazarėno rankų, jis siūbuos viską Viešpaties akivaizdoje. Visi pašvęstieji daiktai priklausys kunigui. Po to nazarėnas galės gerti vyną.",
			"21": "<sup>21</sup> Toks yra nazarėno, kuris davė įžadą, įstatymas ir auka Viešpačiui, neskaičiuojant to, ką jis aukos pagal savo įžadą, kad išpildytų pasišventimo įstatymą’ ”.",
			"22": "<sup>22</sup> Viešpats kalbėjo Mozei:",
			"23": "<sup>23</sup> “Pasakyk Aaronui ir jo sūnums laiminti izraelitus tokiais žodžiais:",
			"24": "<sup>24</sup> ‘Viešpats telaimina ir tesaugoja tave.",
			"25": "<sup>25</sup> Viešpats teparodo tau savo veidą ir tebūna tau maloningas.",
			"26": "<sup>26</sup> Viešpats teatgręžia savo veidą į tave ir tesuteikia tau ramybę’.",
			"27": "<sup>27</sup> Jie šauksis mano vardo izraelitams, ir Aš juos laiminsiu”."
		},
		"7": {
			"1": "<sup>1</sup> Tą dieną, kai pabaigė statyti palapinę ir Mozė patepė ir pašventino ją ir visus jos reikmenis, taip pat aukurą ir visus jo daiktus,",
			"2": "<sup>2</sup> Izraelio giminių kunigaikščiai aukojo",
			"3": "<sup>3</sup> Viešpačiui šešis dengtus vežimus ir dvylika jaučių. (Po vežimą nuo dviejų kunigaikščių ir nuo kiekvieno po jautį.) Visa tai jie atgabeno prie palapinės.",
			"4": "<sup>4</sup> Viešpats sakė Mozei:",
			"5": "<sup>5</sup> “Imk tas aukas palapinės reikalams ir skirstyk levitams pagal jų pareigas”.",
			"6": "<sup>6</sup> Mozė paskirstė vežimus ir jaučius levitams.",
			"7": "<sup>7</sup> Du vežimus ir keturis jaučius jis davė Geršono sūnums.",
			"8": "<sup>8</sup> Keturis vežimus ir aštuonis jaučius davė Merario sūnums. Jų vyresnysis buvo kunigo Aarono sūnus Itamaras.",
			"9": "<sup>9</sup> Kehato sūnums nedavė vežimų nei jaučių, nes jiems patikėta Švenčiausioji turi būti nešama ant pečių.",
			"10": "<sup>10</sup> Aukuro patepimo dieną giminių kunigaikščiai aukojo aukuro reikalams.",
			"11": "<sup>11</sup> Viešpats tarė Mozei: “Kunigaikščiai kiekvienas savo dieną teaukoja aukuro pašventimui skirtas aukas”.",
			"12": "<sup>12</sup> Pirmą dieną aukojo Aminadabo sūnus Naasonas iš Judo giminės.",
			"13": "<sup>13</sup> Tai buvo sidabrinis dubuo, sveriąs šimtą trisdešimt šekelių, ir sidabrinė taurė, sverianti septyniasdešimt šekelių pagal šventyklos šekelį,­abu indai buvo pilni smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"14": "<sup>14</sup> be to, auksinis indelis, sveriąs dešimt šekelių, pilnas smilkalų;",
			"15": "<sup>15</sup> jautis, avinas ir metinis avinėlis deginamajai aukai;",
			"16": "<sup>16</sup> ožys aukai už nuodėmę;",
			"17": "<sup>17</sup> du jaučiai, penki avinai, penki ožiai ir penki metiniai avinėliai padėkos aukai. Tai buvo Aminadabo sūnaus Naasono auka.",
			"18": "<sup>18</sup> Antrą dieną aukojo Cuaro sūnus Netanelis, Isacharo giminės kunigaikštis:",
			"19": "<sup>19</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"20": "<sup>20</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"21": "<sup>21</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"22": "<sup>22</sup> ožį aukai už nuodėmę;",
			"23": "<sup>23</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Cuaro sūnaus Netanelio auka.",
			"24": "<sup>24</sup> Trečią dieną aukojo Zabulono sūnų kunigaikštis, Helono sūnus Eliabas:",
			"25": "<sup>25</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"26": "<sup>26</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"27": "<sup>27</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"28": "<sup>28</sup> ožį aukai už nuodėmę;",
			"29": "<sup>29</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius, penkis metinius avinėlius. Tai buvo Helono sūnaus Eliabo auka.",
			"30": "<sup>30</sup> Ketvirtą dieną aukojo Rubeno sūnų kunigaikštis, Šedeūro sūnus Elicūras:",
			"31": "<sup>31</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"32": "<sup>32</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"33": "<sup>33</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"34": "<sup>34</sup> ožį aukai už nuodėmę;",
			"35": "<sup>35</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Šedeūro sūnaus Elicūro auka.",
			"36": "<sup>36</sup> Penktą dieną aukojo Simeono sūnų kunigaikštis, Cūrišadajo sūnus Šelumielis:",
			"37": "<sup>37</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"38": "<sup>38</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"39": "<sup>39</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"40": "<sup>40</sup> ožį aukai už nuodėmę;",
			"41": "<sup>41</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Cūrišadajo sūnaus Šelumielio auka.",
			"42": "<sup>42</sup> Šeštą dieną aukojo Gado sūnų kunigaikštis, Deuelio sūnus Elja-safas:",
			"43": "<sup>43</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"44": "<sup>44</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"45": "<sup>45</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"46": "<sup>46</sup> ožį aukai už nuodėmę;",
			"47": "<sup>47</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Deuelio sūnaus Eljasafo auka.",
			"48": "<sup>48</sup> Septintą dieną aukojo Efraimo sūnų kunigaikštis, Amihudo sūnus Elišama:",
			"49": "<sup>49</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"50": "<sup>50</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"51": "<sup>51</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"52": "<sup>52</sup> ožį aukai už nuodėmę;",
			"53": "<sup>53</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Amihudo sūnaus Elišamos auka.",
			"54": "<sup>54</sup> Aštuntą dieną aukojo Manaso sūnų kunigaikštis, Pedacūro sūnus Gamelielis:",
			"55": "<sup>55</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"56": "<sup>56</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"57": "<sup>57</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"58": "<sup>58</sup> ožį aukai už nuodėmę;",
			"59": "<sup>59</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Pedacūro sūnaus Gamelielio auka.",
			"60": "<sup>60</sup> Devintą dieną aukojo Benjamino sūnų kunigaikštis, Gideonio sūnus Abidanas:",
			"61": "<sup>61</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"62": "<sup>62</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"63": "<sup>63</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"64": "<sup>64</sup> ožį aukai už nuodėmę;",
			"65": "<sup>65</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Gideonio sūnaus Abidano auka.",
			"66": "<sup>66</sup> Dešimtą dieną aukojo Dano sūnų kunigaikštis, Amišadajo sūnus Ahiezeras:",
			"67": "<sup>67</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"68": "<sup>68</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"69": "<sup>69</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"70": "<sup>70</sup> ožį aukai už nuodėmę;",
			"71": "<sup>71</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Amišadajo sūnaus Ahiezero auka.",
			"72": "<sup>72</sup> Vienuoliktą dieną aukojo Ašero sūnų kunigaikštis, Ochrano sūnus Pagielis:",
			"73": "<sup>73</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"74": "<sup>74</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"75": "<sup>75</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"76": "<sup>76</sup> ožį aukai už nuodėmę;",
			"77": "<sup>77</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Ochrano sūnaus Pagielio auka.",
			"78": "<sup>78</sup> Dvyliktą dieną aukojo Neftalio sūnų kunigaikštis, Enano sūnus Ahyra:",
			"79": "<sup>79</sup> sidabrinį dubenį, sveriantį šimtą trisdešimt šekelių, sidabrinę taurę, sveriančią septyniasdešimt šekelių,­abu pilnus smulkių, aliejumi apšlakstytų miltų duonos aukai;",
			"80": "<sup>80</sup> auksinį indelį, sveriantį dešimt šekelių, pilną smilkalų;",
			"81": "<sup>81</sup> jautį, aviną ir metinį avinėlį deginamajai aukai;",
			"82": "<sup>82</sup> ožį aukai už nuodėmę;",
			"83": "<sup>83</sup> padėkos aukai du jaučius, penkis avinus, penkis ožius ir penkis metinius avinėlius. Tai buvo Enano sūnaus Ahyros auka.",
			"84": "<sup>84</sup> Tos buvo Izraelio kunigaikščių aukos aukuro pašventinimo proga: dvylika sidabrinių dubenių, dvylika sidabrinių taurių, dvylika auksinių indelių.",
			"85": "<sup>85</sup> Kadangi dubuo svėrė šimtą trisdešimt šekelių sidabro ir kiekviena taurė septyniasdešimt šekelių, tai visi sidabriniai indai svėrė du tūkstančius keturis šimtus šekelių pagal šventyklos šekelį.",
			"86": "<sup>86</sup> Dvylika auksinių indelių, kurie buvo pilni smilkalų, kiekvienas svėrė po dešimt šekelių; viso aukso buvo šimtas dvidešimt šekelių.",
			"87": "<sup>87</sup> Jaučių deginamajai aukai buvo dvylika, avinų dvylika, metinių avinėlių dvylika kartu su duonos auka; aukai už nuodėmę buvo dvylika ožių.",
			"88": "<sup>88</sup> Padėkos aukai buvo dvidešimt keturi jaučiai, šešiasdešimt avinų, šešiasdešimt ožių ir šešiasdešimt metinių avinėlių. Tos aukos buvo duotos aukuro pašventinimui po to, kai jis buvo pateptas.",
			"89": "<sup>89</sup> Kai Mozė įėjo į Susitikimo palapinę kalbėtis su Dievu, jis girdėjo balsą nuo dangčio, kuris buvo ant Sandoros skrynios tarp dviejų cherubų. Iš ten Dievas kalbėjo su juo."
		},
		"8": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk Aaronui, kad jis sustatytų lempas taip, kad visos degtų žvakidės priekyje”.",
			"3": "<sup>3</sup> Aaronas sustatė lempas, kad jos degtų žvakidės priekyje, kaip Viešpats įsakė Mozei.",
			"4": "<sup>4</sup> Visa žvakidė buvo nukalta iš aukso, pagal Viešpaties Mozei parodytą pavyzdį.",
			"5": "<sup>5</sup> Viešpats kalbėjo Mozei, sakydamas:",
			"6": "<sup>6</sup> “Imk levitus iš Izraelio vaikų ir apvalyk juos,",
			"7": "<sup>7</sup> apšlakstydamas apvalomuoju vandeniu, liepk nusiskusti plaukus nuo viso kūno ir išsiplauti drabužius, tai jie bus apvalyti.",
			"8": "<sup>8</sup> Jie ims veršį ir duonos aukai­ aliejumi apšlakstytų smulkių miltų, ir kitą veršį­aukai už nuodėmę.",
			"9": "<sup>9</sup> Sušauk izraelitus ir pastatyk levitus prie Susitikimo palapinės.",
			"10": "<sup>10</sup> Tu pastatysi levitus prieš Viešpatį, ir izraelitai uždės ant jų rankas.",
			"11": "<sup>11</sup> Aaronas paaukos levitus Viešpačiui, kaip Izraelio vaikų auką, kad jie tarnautų Jam.",
			"12": "<sup>12</sup> Levitai uždės rankas ant veršių galvų, kurių vieną paaukosi kaip auką už nuodėmę, o kitą­kaip deginamąją auką; taip levitai bus sutaikinti.",
			"13": "<sup>13</sup> Pastatyk levitus prieš Aaroną ir jo sūnus ir paaukok juos Viešpačiui;",
			"14": "<sup>14</sup> išskirk levitus iš izraelitų, kad jie būtų mano.",
			"15": "<sup>15</sup> Po to jie tarnaus man Susitikimo palapinėje. Taip jie bus apvalyti ir pašvęsti man,",
			"16": "<sup>16</sup> nes jie visi yra man atiduoti vietoje izraelitų pirmagimių,",
			"17": "<sup>17</sup> kadangi visi izraelitų pirmagimiai yra mano­žmonės ir gyvuliai. Aš juos pašvenčiau sau nuo tos dienos, kai išžudžiau egiptiečių pirmagimius.",
			"18": "<sup>18</sup> Aš paėmiau levitus vietoje izraelitų pirmagimių",
			"19": "<sup>19</sup> ir pavedžiau juos Aarono ir jo sūnų priežiūrai, kad tarnautų man Susitikimo palapinėje ir sutaikintų izraelitus. Taip izraelitai bus apsaugoti nuo sunaikinimo, kuris ištiktų juos, jei kas iš jų išdrįstų artintis prie šventyklos”.",
			"20": "<sup>20</sup> Mozė, Aaronas ir izraelitai pasielgė su levitais taip, kaip Viešpats įsakė Mozei:",
			"21": "<sup>21</sup> juos apvalė, jie išsiplovė savo drabužius, o Aaronas paaukojo juos kaip auką Viešpačiui ir sutaikino.",
			"22": "<sup>22</sup> Po to levitai ėjo į Susitikimo palapinę ir atliko jiems skirtą tarnystę Aarono ir jo sūnų priežiūroje.",
			"23": "<sup>23</sup> Viešpats kalbėjo Mozei:",
			"24": "<sup>24</sup> “Toks yra įstatymas levitams: dvidešimt penkerių metų ir vyresni tarnaus Susitikimo palapinėje,",
			"25": "<sup>25</sup> o, sulaukę penkiasdešimties metų amžiaus, jie nebetarnaus.",
			"26": "<sup>26</sup> Jie padės savo broliams saugoti Susitikimo palapinę, bet nedirbs jokio darbo. Tai įsakymas levitams, liečiąs jų tarnystę”."
		},
		"9": {
			"1": "<sup>1</sup> Izraelitams išėjus iš Egipto, antraisiais metais, pirmą mėnesį Viešpats kalbėjo Mozei Sinajaus dykumoje:",
			"2": "<sup>2</sup> “Tegul izraelitai švenčia Paschą jai skirtu metu,",
			"3": "<sup>3</sup> šio mėnesio keturioliktos dienos vakare, laikydamiesi visų apeigų ir nurodymų”.",
			"4": "<sup>4</sup> Mozė pasakė izraelitams, kad jie turi švęsti Paschą.",
			"5": "<sup>5</sup> Jie ją šventė pirmo mėnesio keturioliktos dienos vakare prie Sinajaus kalno. Izraelitai darė visa, kaip Viešpats buvo įsakęs Mozei.",
			"6": "<sup>6</sup> Kai kurie buvo susitepę žmogaus lavonu ir tą dieną negalėjo švęsti Paschos. Jie atėjo pas Mozę bei Aaroną ir jiems tarė:",
			"7": "<sup>7</sup> “Mes susitepę mirusiu. Ar dėl to mes neturime teisės aukoti Viešpačiui kartu su visais izraelitais?”",
			"8": "<sup>8</sup> Mozė jiems atsakė: “Palaukite, aš paklausiu, ką Viešpats pasakys šiuo reikalu”.",
			"9": "<sup>9</sup> Ir Viešpats kalbėjo Mozei:",
			"10": "<sup>10</sup> “Jei kas iš izraelitų susiteptų mirusiu ar būtų kelionėje, tešvenčia Paschą",
			"11": "<sup>11</sup> antro mėnesio keturioliktos dienos vakare, valgydamas neraugintą duoną su karčiomis žolėmis;",
			"12": "<sup>12</sup> nieko nepaliks ligi ryto ir nė vieno avinėlio kaulo nesulaužys; laikysis visų Paschos nuostatų.",
			"13": "<sup>13</sup> O jei kas nesuteptas ir nekeliaudamas nešvęstų Paschos, tas bus išnaikintas iš savo tautos, nes neaukojo Viešpačiui skirtu laiku; jis atsakys už savo nuodėmę.",
			"14": "<sup>14</sup> Jei tarp jūsų būtų svetimšalis ar ateivis, kuris norėtų švęsti Paschą, tešvenčia ją Viešpačiui, laikydamasis tų pačių apeigų ir nurodymų. Jums ir ateiviui yra tas pats įstatymas”.",
			"15": "<sup>15</sup> Tą dieną, kai buvo pastatyta Liudijimo palapinė, ją apdengė debesis, o naktį virš palapinės buvo tarsi ugnies liepsna.",
			"16": "<sup>16</sup> Taip buvo visą laiką: dieną ją dengė debesis, o naktį­tarsi liepsna.",
			"17": "<sup>17</sup> Kai palapinę dengiąs debesis pakildavo, izraelitai iškeliaudavo; toje vietoje, kur debesis nusileisdavo, izraelitai statydavo stovyklą.",
			"18": "<sup>18</sup> Pagal Viešpaties įsakymą jie keliaudavo ir Jam įsakius ištiesdavo palapines. Kol debesis dengė palapinę, jie pasilikdavo toje pačioje vietoje.",
			"19": "<sup>19</sup> Jei debesis virš palapinės pasilikdavo ilgą laiką, izraelitai nekeliaudavo.",
			"20": "<sup>20</sup> Kartais debesis virš palapinės likdavo tik kelias dienas. Pagal Viešpaties įsakymą jie ištiesdavo palapines ir pagal Jo įsakymą iškeliaudavo.",
			"21": "<sup>21</sup> Jei debesis stovėdavo virš palapinės tik vieną naktį ir, dienai brėkštant, pakildavo, jie keliaudavo toliau; jei debesis pakildavo, jie iškeliaudavo ar dieną, ar naktį.",
			"22": "<sup>22</sup> O jei dvi dienas ar visą mėnesį, ar metus debesis pasilikdavo ant palapinės, izraelitai pasilikdavo toje pačioje vietoje; kai tik debesis pakildavo, jie keldavosi iš stovyklos.",
			"23": "<sup>23</sup> Pagal Viešpaties įsakymą jie ištiesdavo palapines ir pagal Jo įsakymą iškeliaudavo. Jie laikėsi Viešpaties per Mozę duoto įsakymo."
		},
		"10": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Padaryk du sidabrinius trimitus, kad galėtum sušaukti žmones ir jiems pranešti, kai reikės keltis iš stovyklos.",
			"3": "<sup>3</sup> Sutrimitavus abiem trimitais, visa tauta susirinks pas tave prie Susitikimo palapinės.",
			"4": "<sup>4</sup> Jei sutrimituos vienu trimitu, pas tave susirinks izraelitų kunigaikščiai.",
			"5": "<sup>5</sup> Kai trimituos pavojų, pirmieji pakils tie, kurie stovyklauja rytų pusėje.",
			"6": "<sup>6</sup> Trimituojant pavojų antrą sykį, pajudės pietuose esančios stovyklos. Pavojaus ženklą trimituosite skelbdami kelionės pradžią.",
			"7": "<sup>7</sup> Sušaukdami tautos susirinkimą, trimituosite, bet ne pavojaus signalą.",
			"8": "<sup>8</sup> Trimituos Aarono sūnūs, kunigai; tai bus amžinas įstatymas jūsų kartoms.",
			"9": "<sup>9</sup> Jei eisite į karą savo žemėje prieš tuos, kurie jus spaudžia, trimituokite pavojų. Viešpats, jūsų Dievas, prisimins jus ir išgelbės iš jūsų priešų.",
			"10": "<sup>10</sup> Jūsų džiaugsmo dieną, šventės dieną ir pirmą mėnesio dieną, trimituokite prie deginamųjų ir padėkos aukų kaip atminimą Dievui. Aš, Viešpats, jūsų Dievas”.",
			"11": "<sup>11</sup> Antraisiais metais, antro mėnesio dvidešimtą dieną debesis pakilo nuo Liudijimo palapinės,",
			"12": "<sup>12</sup> ir izraelitai iškeliavo būriais iš Sinajaus dykumos. Debesis nusileido Parano dykumoje.",
			"13": "<sup>13</sup> Tai buvo pirmoji jų kelionė, kaip Viešpats buvo įsakęs Mozei.",
			"14": "<sup>14</sup> Pirmieji ėjo Judo giminės žmonės tvarkingais būriais. Jų vadas buvo Aminadabo sūnus Naasonas.",
			"15": "<sup>15</sup> Isacharo giminės pulkų vadas buvo Cuaro sūnus Netanelis.",
			"16": "<sup>16</sup> Zabulono giminės pulkų vadas buvo Helono sūnus Eliabas.",
			"17": "<sup>17</sup> Po to buvo išardyta palapinė, kuria nešini ėjo Geršono ir Merario sūnūs.",
			"18": "<sup>18</sup> Paskui juos keliavo Rubeno pulkai. Jų vadas buvo Šedeūro sūnus Elicūras.",
			"19": "<sup>19</sup> Simeono giminės pulkų vadas buvo Cūrišadajo sūnus Šelumielis.",
			"20": "<sup>20</sup> Gado giminės pulkų vadas buvo Deuelio sūnus Eljasafas.",
			"21": "<sup>21</sup> Po to ėjo Kehato sūnūs, kurie nešė Švenčiausiąją. Kai jie nuėjo, palapinė buvo jau pastatyta.",
			"22": "<sup>22</sup> Toliau ėjo Efraimo giminės pulkai, kuriems vadovavo Amihudo sūnus Elišama.",
			"23": "<sup>23</sup> Manaso giminės pulkų vadas buvo Pedacūro sūnus Gamelielis.",
			"24": "<sup>24</sup> Benjamino giminės pulkų vadas buvo Gideonio sūnus Abidanas.",
			"25": "<sup>25</sup> Paskutiniai iš stovyklos iškeliavo Dano pulkai. Jiems vadovavo Amišadajo sūnus Ahiezeras.",
			"26": "<sup>26</sup> Ašero giminės pulkų vadas buvo Ochrano sūnus Pagielis.",
			"27": "<sup>27</sup> Neftalio giminės pulkų vadas buvo Enano sūnus Ahyra.",
			"28": "<sup>28</sup> Tokia tvarka ėjo Izraelio vaikų pulkai, kai keldavosi iš vienos vietos į kitą.",
			"29": "<sup>29</sup> Mozė sakė savo žmonos broliui Hobabui, midjaniečio Reuelio sūnui: “Mes eisime į vietą, kurią Viešpats mums pažadėjo. Keliauk su mumis, mes tau gera darysime, nes Viešpats pažadėjo Izraeliui gerovę”.",
			"30": "<sup>30</sup> Tas jam atsakė: “Aš nekeliausiu su jumis, bet grįšiu į savo šalį, kurioje gimiau”.",
			"31": "<sup>31</sup> Mozė atsakė: “Nepalik mūsų, nes tu geriausiai žinai, kaip mes turime įsikurti dykumoje; tu būsi mums vietoje akių.",
			"32": "<sup>32</sup> Jei pasiliksi su mumis, duosime tau, ką mums Viešpats duos”.",
			"33": "<sup>33</sup> Jie keliavo nuo Sinajaus kalno tris dienas, ir Viešpaties Sandoros skrynia buvo nešama pirma jų į stovyklai paskirtą vietą.",
			"34": "<sup>34</sup> Viešpaties debesis rodė jiems kelią, keliaujant dienos metu.",
			"35": "<sup>35</sup> Sandoros skrynią pakeliant kelionei, Mozė tardavo: “Viešpatie! Teišsisklaido Tavo priešai ir bėga nuo Tavo veido tie, kurie Tavęs nekenčia”.",
			"36": "<sup>36</sup> Kai sustodavo, jis tardavo: “Sugrįžk, Viešpatie, pas Izraelio tūkstančių tūkstančius!”"
		},
		"11": {
			"1": "<sup>1</sup> Izraelitai murmėjo prieš Viešpatį, ir Viešpačiui tai nepatiko. Tai išgirdęs, Viešpats užsirūstino, Jo ugnis užsidegė tarp jų ir ėmė naikinti stovyklos pakraštį.",
			"2": "<sup>2</sup> Tauta šaukėsi Mozės ir, kai Mozė pasimeldė Viešpačiui, ugnis užgeso.",
			"3": "<sup>3</sup> Jie praminė tą vietą Tabera, nes Viešpaties ugnis degė tarp jų.",
			"4": "<sup>4</sup> Kartu keliavo daug svetimšalių, kurie pasidavė geiduliams; su jais drauge verkė ir izraelitai, sakydami: “Kas duos mums mėsos?",
			"5": "<sup>5</sup> Atsimename žuvis, kurias valgėme Egipte ir jos mums nieko nekainavo; taip pat prisimename agurkus, melionus, porus, svogūnus ir česnakus.",
			"6": "<sup>6</sup> Dabar mūsų sielos išseko, nes mūsų akys nieko kito nemato­tiktai maną”.",
			"7": "<sup>7</sup> Mana buvo geltona ir panaši į kalendros sėklas.",
			"8": "<sup>8</sup> Žmonės vaikščiojo aplinkui ir ją rinkosi, malė girnomis ar susitrindavo grūstuvuose, virė puoduose ir kepė iš jos papločius; jos skonis buvo panašus į ragaišio su aliejumi skonį.",
			"9": "<sup>9</sup> Nusileidžiant naktį ant stovyklos rasai, krisdavo ir mana.",
			"10": "<sup>10</sup> Mozė išgirdo, kaip žmonės verkia savo šeimose prie palapinių angų. Viešpaties rūstybė smarkiai užsidegė, Mozė taip pat buvo nepatenkintas.",
			"11": "<sup>11</sup> Jis skundėsi Viešpačiui: “Kam vargini savo tarną? Kodėl nerandu malonės Tavo akyse? Ir kodėl uždėjai man visos šitos tautos naštą?",
			"12": "<sup>12</sup> Argi šita tauta yra mano vaikai, ar aš ją pagimdžiau, kad man sakai: ‘Nunešk juos, paėmęs į glėbį, kaip auklė nešioja kūdikį, į žemę, kurią Tu pažadėjai jų tėvams?’",
			"13": "<sup>13</sup> Iš kur aš imsiu mėsos, kad pasotinčiau tokią daugybę? Jie rauda ir šaukia: ‘Duok mums mėsos!’",
			"14": "<sup>14</sup> Aš negaliu vienas nešti visos šitos tautos, nes man tai per sunku.",
			"15": "<sup>15</sup> Jeigu taip su manimi elgiesi, geriau nužudyk mane, jei atradau malonę Tavo akyse, kad nebūčiau varginamas tokių didelių bėdų”.",
			"16": "<sup>16</sup> Viešpats atsakė Mozei: “Surink septyniasdešimt Izraelio vyresniųjų, apie kuriuos žinai, kad jie yra tautos vadovai, nuvesk juos prie Susitikimo palapinės ir atsistok ten kartu su jais.",
			"17": "<sup>17</sup> Aš nužengsiu ir kalbėsiu su tavimi, paimsiu dvasios, kuri yra ant tavęs, ir suteiksiu jiems, kad jie kartu su tavimi neštų tautos naštą ir tau nereikėtų nešti vienam.",
			"18": "<sup>18</sup> O tautai sakyk: ‘Pasišventinkite ir rytoj valgysite mėsos, nes Viešpats išgirdo jūsų verksmą, kai sakėte: ‘Kas mums duos mėsos? Gera mums buvo Egipte’. Viešpats jums duos mėsos, ir jūs valgysite.",
			"19": "<sup>19</sup> Ne vieną dieną, ne dvi dienas, ne penkias dienas, ne dešimt dienų ir ne dvidešimt dienų,",
			"20": "<sup>20</sup> bet visą mėnesį, kol nebegalėsite į ją net pažiūrėti; jūs paniekinote Viešpatį, kuris yra tarp jūsų, ir raudojote Jo akivaizdoje, sakydami: ‘Kodėl mes išėjome iš Egipto?’ ”",
			"21": "<sup>21</sup> Mozė tarė: “Šitos tautos yra šeši šimtai tūkstančių pajėgių vyrų, ir Tu sakai, kad duosi jiems visą mėnesį valgyti mėsos?",
			"22": "<sup>22</sup> Argi mes turėsime papjauti avis ir jaučius, kad užtektų visiems? Ar susirinks visos jūros žuvys, kad juos pasotintų?”",
			"23": "<sup>23</sup> Viešpats atsakė jam: “Argi Viešpaties ranka sutrumpėjo? Dabar matysi, ar mano žodžiai išsipildys, ar ne”.",
			"24": "<sup>24</sup> Mozė nuėjo ir pasakė tautai Viešpaties žodžius. Jis surinko septyniasdešimt izraelitų vyresniųjų ir juos sustatė prie palapinės.",
			"25": "<sup>25</sup> Viešpats nužengė debesyje ir jam kalbėjo. Ir Jis paėmė dvasios, kuri buvo ant Mozės, ir davė septyniasdešimčiai vyresniųjų. Kai ant jų nusileido dvasia, jie pradėjo pranašauti ir nepaliovė.",
			"26": "<sup>26</sup> Stovykloje buvo pasilikę du vyrai, vienas vardu Eldadas, o kitas­Medadas. Ir jie gavo tos dvasios, nes ir juodu buvo įtraukti į sąrašą, bet nebuvo atėję prie palapinės. Jie pradėjo pranašauti stovykloje.",
			"27": "<sup>27</sup> Vienas jaunuolis atbėgo ir pranešė Mozei: “Eldadas ir Medadas pranašauja stovykloje!”",
			"28": "<sup>28</sup> Nūno sūnus Jozuė, Mozės tarnas, tarė: “Mano valdove Moze, uždrausk jiems!”",
			"29": "<sup>29</sup> Bet jis atsakė: “Ar tu pavydi dėl manęs? O, kad visa Viešpaties tauta pranašautų ir kad Viešpats duotų kiekvienam savo dvasios!”",
			"30": "<sup>30</sup> Mozė ir Izraelio vyresnieji sugrįžo į stovyklą.",
			"31": "<sup>31</sup> Pakilo Viešpaties siųstas vėjas ir atnešė putpelių. Jų tiek prikrito aplinkui stovyklą, kiek galima apeiti per dvi dienas ir dvi uolektys nuo žemės.",
			"32": "<sup>32</sup> Žmonės rinko putpeles visą tą dieną ir naktį ir dar kitą dieną, ir kiekvienas prisirinko nemažiau kaip dešimt homerų; jas džiovino aplink stovyklą.",
			"33": "<sup>33</sup> Mėsa tebebuvo jiems tarp dantų, ir jie dar nebuvo jos suvalgę, kai Viešpaties rūstybė užsidegė prieš tautą, ir Jis ištiko juos dideliu maru.",
			"34": "<sup>34</sup> Ta vieta buvo pavadinta Kibrot Taavos kapinėmis, nes ten palaidojo žmones, kurie buvo pasidavę geiduliams.",
			"35": "<sup>35</sup> Išėję iš Kibrot Taavos kapinių, jie nukeliavo į Hacerotą ir ten sustojo."
		},
		"12": {
			"1": "<sup>1</sup> Mirjama ir Aaronas priekaištavo Mozei dėl jo vedybų, nes jis buvo vedęs etiopę.",
			"2": "<sup>2</sup> Jie sakė: “Argi Viešpats kalbėjo tik per vieną Mozę? Argi Jis nekalbėjo taip pat ir per mus?” Viešpats tai išgirdo.",
			"3": "<sup>3</sup> Mozė gi buvo labai romus, romiausias iš visų žmonių žemėje.",
			"4": "<sup>4</sup> Viešpats staiga prabilo į Mozę, Aaroną bei Mirjamą: “Jūs trys išeikite prie Susitikimo palapinės”. Jiems išėjus,",
			"5": "<sup>5</sup> Viešpats nužengė debesies stulpe ir, stovėdamas palapinės įėjime, pašaukė Aaroną ir Mirjamą. Kai juodu priėjo,",
			"6": "<sup>6</sup> Jis tarė jiems: “Klausykite! Jei kas tarp jūsų yra Viešpaties pranašas, Aš jam apsireiškiu regėjime arba kalbu sapne.",
			"7": "<sup>7</sup> Ne taip yra su mano tarnu Moze, kuris yra ištikimas visuose mano namuose.",
			"8": "<sup>8</sup> Su juo Aš kalbuosi veidas į veidą, atvirai, o ne neaiškiais žodžiais, ir jis mato mano pavidalą. Kaip judu nebijote kalbėti prieš mano tarną Mozę?”",
			"9": "<sup>9</sup> Užsirūstinęs Viešpats pasitraukė.",
			"10": "<sup>10</sup> Pasitraukė ir debesis nuo palapinės. Staiga Mirjamą išbėrė raupsai, ji pabalo kaip sniegas. Aaronas, pažvelgęs į ją ir pamatęs ją apdengtą raupsais,",
			"11": "<sup>11</sup> tarė Mozei: “Maldauju, mano valdove, tenepasilieka ant mudviejų ši nuodėmė, kurią padarėme per savo kvailumą.",
			"12": "<sup>12</sup> Tenebūna ji kaip kūdikis, negyvas gimęs, kurio kūno dalis jau sugedus”.",
			"13": "<sup>13</sup> Mozė šaukėsi Viešpaties: “Maldauju, o Dieve, išgydyk ją”.",
			"14": "<sup>14</sup> Viešpats jam atsakė: “Jei jos tėvas būtų spjovęs jai į veidą, argi ji nebūtų turėjusi rausti bent septynias dienas? Tebūna atskirta septynias dienas nuo stovyklos ir paskui bus pašaukta atgal”.",
			"15": "<sup>15</sup> Mirjama buvo septynias dienas atskirta nuo stovyklos, ir tauta nepajudėjo iš vietos, kol Mirjama nebuvo pašaukta atgal.",
			"16": "<sup>16</sup> Po to izraelitai iškeliavo iš Haceroto ir apsistojo Parano dykumoje."
		},
		"13": {
			"1": "<sup>1</sup> Ir Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Siųsk vyrus, po vieną vyresnįjį iš kiekvienos giminės, išžvalgyti Kanaano žemės, kurią duosiu izraelitams”.",
			"3": "<sup>3</sup> Mozė padarė, ką Viešpats įsakė. Jis išsiuntė iš Parano dykumos kiekvienos giminės vyresnįjį:",
			"4": "<sup>4</sup> Rubeno giminės­Zakūro sūnų Šamūvą,",
			"5": "<sup>5</sup> Simeono giminės­Horio sūnų Šafatą,",
			"6": "<sup>6</sup> Judo giminės­Jefunės sūnų Kalebą,",
			"7": "<sup>7</sup> Isacharo giminės­Juozapo sūnų Igalą,",
			"8": "<sup>8</sup> Efraimo giminės­Nūno sūnų Ozėją,",
			"9": "<sup>9</sup> Benjamino giminės­Rafuvo sūnų Paltį,",
			"10": "<sup>10</sup> Zabulono giminės­Sodžio sūnų Gadielį,",
			"11": "<sup>11</sup> iš Juozapo sūnų, Manaso giminės­ Susio sūnų Gadį,",
			"12": "<sup>12</sup> Dano giminės­Gemalio sūnų Amielį,",
			"13": "<sup>13</sup> Ašero giminės­Mykolo sūnų Setūrą,",
			"14": "<sup>14</sup> Neftalio giminės­Vofsio sūnų Nachbį,",
			"15": "<sup>15</sup> Gado giminės­Machio sūnų Geuelį.",
			"16": "<sup>16</sup> Tai vardai vyrų, kuriuos Mozė pasiuntė išžvalgyti kraštą. Nūno sūnų Ozėją jis pavadino Jozue.",
			"17": "<sup>17</sup> Mozė, siųsdamas juos į Kanaano šalį, tarė jiems: “Pradėkite šalies pietuose ir, nuėję į kalnus,",
			"18": "<sup>18</sup> apžiūrėkite žemę ir žmones, kurie ten gyvena: ar jie galingi, ar silpni, ar jų mažai, ar daug;",
			"19": "<sup>19</sup> ar pati žemė gera, ar bloga; kokie miestai, ar turi mūro sienas, ar ne;",
			"20": "<sup>20</sup> ar žemė derlinga, ar nualinta, ar miškinga, ar ne. Būkite drąsūs ir mums atneškite tos žemės vaisių”. Buvo metas, kada skinamos pirmosios vynuogės.",
			"21": "<sup>21</sup> Jie nuėję išžvalgė žemę nuo Cino dykumos iki Rehobo, kur einama į Lebo Hamatą.",
			"22": "<sup>22</sup> Jie patraukė į pietus ir atėjo į Hebroną, kur gyveno Anako sūnūs Ahimanas, Šešajas ir Talmajas. Hebronas įkurtas septyneriais metais anksčiau už Egipto miestą Coaną.",
			"23": "<sup>23</sup> Nuėję prie Eškolo upelio, nupjovė šaką su viena keke vynuogių, ir du vyrai ją nešė ant karties. Taip pat paėmė granato vaisių ir figų iš tos vietos,",
			"24": "<sup>24</sup> kuri buvo pavadinta Eškolu, nes ten Izraelio vaikai nupjovė vynuogių kekę.",
			"25": "<sup>25</sup> Žvalgai, apėję visą šalį, sugrįžo po keturiasdešimties dienų",
			"26": "<sup>26</sup> pas Mozę, Aaroną ir izraelitus į Kadešą, į Parano dykumą. Jie papasakojo visiems, ką sužinojo, ir parodė to krašto vaisius.",
			"27": "<sup>27</sup> Jie sakė: “Buvome nuėję į žemę, į kurią mus siuntei. Ji tikrai plūsta pienu ir medumi, ir štai jos vaisiai;",
			"28": "<sup>28</sup> bet jos gyventojai yra stiprūs, miestai dideli, apjuosti mūro sienomis. Ten matėme ir Anako palikuonių.",
			"29": "<sup>29</sup> Amalekiečiai gyvena pietuose, hetitai, jebusiečiai ir amoritai kalnuose, kanaaniečiai prie jūros ir Jordano slėnyje”.",
			"30": "<sup>30</sup> Bet Kalebas nuramino žmones prieš Mozę ir sakė: “Eikime ir užimkime tą žemę, nes mes esame pajėgūs juos nugalėti”.",
			"31": "<sup>31</sup> Bet vyrai, buvę su juo, sakė: “Mes negalime eiti prieš tas tautas, nes jos už mus stipresnės”.",
			"32": "<sup>32</sup> Ir jie skleidė tarp Izraelio vaikų blogus atsiliepimus apie tą žemę, kurią buvo išžvalgę, kalbėdami: “Žemė, kurią išžvalgėme, ryja savo gyventojus, o žmonės, kuriuos matėme, yra labai aukšto ūgio.",
			"33": "<sup>33</sup> Ten matėme milžinus iš Anako giminės, ir mes buvome prieš juos kaip žiogai, ir tokie mes buvome jų akyse”."
		},
		"14": {
			"1": "<sup>1</sup> Visa tauta pakėlė balsus ir raudojo tą naktį.",
			"2": "<sup>2</sup> Jie murmėjo prieš Mozę ir Aaroną: “Verčiau būtume mirę Egipte arba žuvę šioje dykumoje.",
			"3": "<sup>3</sup> Kodėl Viešpats atvedė mus į šią žemę? Ar tam, kad žūtume nuo kardo, o mūsų žmonos ir vaikai taptų priešo grobiu? Ar ne geriau būtų grįžti į Egiptą?”",
			"4": "<sup>4</sup> Jie tarėsi: “Išsirinkime vadą ir grįžkime į Egiptą”.",
			"5": "<sup>5</sup> Mozė ir Aaronas puolė ant žemės prieš visą Izraelio vaikų susirinkimą.",
			"6": "<sup>6</sup> Nūno sūnus Jozuė ir Jefunės sūnus Kalebas iš tų, kurie žvalgė kraštą, perplėšė savo rūbus",
			"7": "<sup>7</sup> ir kalbėjo izraelitams: “Žemė, kurią apėjome ir išžvalgėme, labai gera.",
			"8": "<sup>8</sup> Jei Viešpats bus maloningas, Jis mus įves į tą žemę, plūstančią pienu ir medumi.",
			"9": "<sup>9</sup> Tik nesukilkite prieš Viešpatį ir nebijokite to krašto žmonių. Mes juos valgysime kaip duoną, jie neturi apsaugos, o Viešpats yra su mumis, nebijokime!”",
			"10": "<sup>10</sup> Bet žmonės ketino juodu užmušti akmenimis. Ir Viešpaties šlovė pasirodė visiems izraelitams virš Susitikimo palapinės.",
			"11": "<sup>11</sup> Viešpats tarė Mozei: “Ar dar ilgai šita tauta niekins mane? Kodėl jie netiki manimi, matydami visus stebuklus, kuriuos padariau jų akivaizdoje?",
			"12": "<sup>12</sup> Aš juos bausiu maru ir sunaikinsiu, o iš tavęs padarysiu didesnę ir galingesnę tautą už šitą”.",
			"13": "<sup>13</sup> Mozė kalbėjo Viešpačiui: “Tada egiptiečiai, iš kurių Tu išvedei šią tautą, išgirs tai, ką Tu padarei tautai,",
			"14": "<sup>14</sup> ir pasakys šio krašto gyventojams, kurie girdėjo, kad Tu, Viešpatie, esi su mumis, kad Tu esi regimas veidu į veidą, kad Tavo debesis yra virš šios tautos ir kad Tu eini pirma mūsų dienos metu debesies stulpe ir naktį­ ugnies stulpe;",
			"15": "<sup>15</sup> todėl jei Tu išžudysi savo žmones, tautos, kurios girdėjo apie Tavo šlovę, sakys:",
			"16": "<sup>16</sup> ‘Kadangi Viešpats nepajėgė įvesti šitos tautos į žemę, kurią jiems prisiekdamas pažadėjo, tai išžudė juos dykumoje’.",
			"17": "<sup>17</sup> Viešpatie, meldžiu Tave, parodyk savo galią, kaip esi pasakęs:",
			"18": "<sup>18</sup> ‘Viešpats yra kantrus ir kupinas gailestingumo, atleidžiantis neteisybes ir nusikaltimus, tačiau nepaliekantis kalto nenubausto, bet baudžiantis už tėvų nusikaltimus vaikus iki trečios ir ketvirtos kartos’.",
			"19": "<sup>19</sup> Maldauju, atleisk šitos tautos nusikaltimus dėl Tavo didelio gailestingumo, kaip atleisdavai jai nuo išėjimo iš Egipto iki šiol”.",
			"20": "<sup>20</sup> Viešpats atsakė: “Atleidžiu, kaip prašei.",
			"21": "<sup>21</sup> Kaip Aš gyvas, visa žemė bus pilna Viešpaties šlovės.",
			"22": "<sup>22</sup> Kadangi visi žmonės, kurie matė mano šlovę ir stebuklus, kuriuos dariau Egipte ir dykumoje, mane gundė jau dešimt kartų ir neklausė mano balso,",
			"23": "<sup>23</sup> jie neišvys žemės, kurią pažadėjau jų tėvams. Niekas iš tų, kurie mane pykdė, nematys jos.",
			"24": "<sup>24</sup> Savo tarną Kalebą, kuris kupinas kitokios dvasios ir iki galo sekė manimi, įvesiu į tą žemę, į kurią jis buvo nuėjęs, ir jo palikuonys ją paveldės.",
			"25": "<sup>25</sup> Kadangi amalekiečiai ir kanaaniečiai gyvena slėniuose, rytoj iš stovyklos visi keliaukite į dykumą Raudonosios jūros link”.",
			"26": "<sup>26</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"27": "<sup>27</sup> “Kiek dar šita pikta tauta murmės prieš mane? Aš girdžiu Izraelio vaikų murmėjimą, kai jie murma prieš mane.",
			"28": "<sup>28</sup> Sakyk jiems: ‘Kaip Aš gyvas,­ sako Viešpats,­padarysiu jums taip, kaip jūs kalbėjote.",
			"29": "<sup>29</sup> Šioje dykumoje liks jūsų lavonai. Visi, kurie buvote suskaičiuoti, dvidešimties metų ir vyresni, ir murmėjote prieš mane,",
			"30": "<sup>30</sup> neįeisite į žemę, kurią jums pažadėjau, išskyrus Jefunės sūnų Kalebą ir Nūno sūnų Jozuę.",
			"31": "<sup>31</sup> Jūsų vaikus, apie kuriuos sakėte, kad jie bus priešų grobis, įvesiu į tą žemę, kurią jūs paniekinote.",
			"32": "<sup>32</sup> Jūsų lavonai kris šioje dykumoje.",
			"33": "<sup>33</sup> Jūsų vaikai klajos dykumoje keturiasdešimt metų dėl jūsų paleistuvystės, kol jūsų lavonai pasiliks dykumoje.",
			"34": "<sup>34</sup> Pagal skaičių dienų, kai jūs žvalgėte žemę, už keturiasdešimt dienų jūs nešiosite savo kaltes keturiasdešimt metų, už kiekvieną dieną­metai, ir jūs pažinsite, ką reiškia būti mano atmestiems.",
			"35": "<sup>35</sup> Kaip kalbėjau, taip ir padarysiu visai šiai piktai tautai, kuri sukilo prieš mane­visi mirs šioje dykumoje’ ”.",
			"36": "<sup>36</sup> Vyrai, kuriuos Mozė buvo išsiuntęs išžvalgyti žemę, ir kurie grįžę sukurstė tautą murmėti prieš Viešpatį, blogai kalbėdami apie kraštą",
			"37": "<sup>37</sup> ir skleisdami blogus atsiliepimus apie tą žemę, buvo ištikti Viešpaties akivaizdoje ir mirė.",
			"38": "<sup>38</sup> Iš visų, kurie buvo išėję žemę išžvalgyti, gyvi liko tik Nūno sūnus Jozuė ir Jefunės sūnus Kalebas.",
			"39": "<sup>39</sup> Kai Mozė pasakė visus šiuos žodžius izraelitams, jie labai nuliūdo.",
			"40": "<sup>40</sup> Atsikėlę anksti rytą, jie užlipo ant kalno, sakydami: “Eisime į žemę, kurią Viešpats pažadėjo, nes mes nusikaltome”.",
			"41": "<sup>41</sup> Mozė jiems atsakė: “Kodėl neklausote Viešpaties įsakymo? Jūs nieko gero nelaimėsite.",
			"42": "<sup>42</sup> Neikite, nes Viešpats neis su jumis, jūs žūsite nuo priešų.",
			"43": "<sup>43</sup> Amalekiečiai ir kaananiečiai yra prieš jus. Jūs žūsite nuo kardo, nes neklausėte Viešpaties, todėl Viešpats nebus su jumis”.",
			"44": "<sup>44</sup> Bet jie nusprendė eiti į kalnus. Tačiau nei Viešpaties Sandoros skrynia, nei Mozė nepajudėjo iš stovyklos.",
			"45": "<sup>45</sup> Atėję amalekiečiai ir kaananiečiai, gyvenantys kalnuose, juos sumušė ir vijosi iki Hormos."
		},
		"15": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk izraelitams: ‘Kai būsite įėję ir apsigyvenę pažadėtoje žemėje, kurią jums duosiu,",
			"3": "<sup>3</sup> ir aukosite Viešpačiui auką iš bandos ar kaimenės­deginamąją, vykdydami įžadus, laisva valia aukodami ar savo iškilmėse, kad būtų malonus kvapas Viešpačiui,",
			"4": "<sup>4</sup> kiekvienas, kuris aukos, atsineš duonos aukai dešimtą efos dalį smulkių miltų, sumaišytų su vienu ketvirtadaliu hino aliejaus,",
			"5": "<sup>5</sup> ir prie deginamosios ar kitos gyvulinės aukos pridės ketvirtą hino dalį vyno prie kiekvieno avinėlio geriamajai aukai.",
			"6": "<sup>6</sup> Prie kiekvieno avino duonos aukai pridėkite dvi dešimtąsias efos smulkių miltų, sumaišytų su trečdaliu hino aliejaus,",
			"7": "<sup>7</sup> ir geriamajai aukai trečią dalį hino vyno, kad būtų malonus kvapas Viešpačiui.",
			"8": "<sup>8</sup> Kai aukosite jautį deginamajai aukai ar įžadui įvykdyti, ar padėkos aukoms,",
			"9": "<sup>9</sup> duokite prie kiekvieno jaučio tris dešimtąsias efos smulkių miltų, sumaišytų su puse hino aliejaus,",
			"10": "<sup>10</sup> ir pusę hino vyno geriamajai aukai, kad būtų malonus kvapas Viešpačiui.",
			"11": "<sup>11</sup> Taip darykite prie kiekvieno jaučio, avino, avinėlio ar ožio",
			"12": "<sup>12</sup> pagal aukų skaičių, kurias aukosite.",
			"13": "<sup>13</sup> Vietiniai gyventojai taip darys aukodami auką, kad būtų malonus kvapas Viešpačiui;",
			"14": "<sup>14</sup> ir jeigu tarp jūsų esantis ateivis norės aukoti auką, jis darys taip pat, kaip ir jūs.",
			"15": "<sup>15</sup> Tas pats nuostatas galioja ir jums, ir ateiviams, gyvenantiems jūsų žemėje, nes kaip jūs, taip ir ateiviai bus prieš Viešpatį.",
			"16": "<sup>16</sup> Toks pat įstatymas bus jums ir ateiviams, gyvenantiems tarp jūsų’ ”.",
			"17": "<sup>17</sup> Viešpats kalbėjo Mozei:",
			"18": "<sup>18</sup> “Sakyk izraelitams: ‘Kai būsite pažadėtoje žemėje, kurią jums duosiu,",
			"19": "<sup>19</sup> ir valgysite to krašto duonos, aukokite Viešpačiui auką.",
			"20": "<sup>20</sup> Aukokite paplotį iš pirmos tešlos, kaip aukojate derliaus pirmienas.",
			"21": "<sup>21</sup> Iš pirmos tešlos aukokite Viešpačiui per visas jūsų kartas.",
			"22": "<sup>22</sup> Jei netyčia ko nors nevykdytumėte iš to, ką Aš įsakiau Mozei",
			"23": "<sup>23</sup> ir per jį paskelbiau, nuo tos dienos, kai daviau savo įsakymus, per visas jūsų kartas,",
			"24": "<sup>24</sup> jei tai padarysite dėl neapsižiūrėjimo, aukokite man veršį deginamajai aukai, kad būtų malonus kvapas, kartu su duonos ir geriamąja auka, pagal visus nuostatus, ir ožį aukai už nuodėmę.",
			"25": "<sup>25</sup> Kunigas sutaikins izraelitus, ir jiems bus atleista, nes jie nusikalto netyčia, tačiau jie aukos deginamąją auką Viešpačiui ir auką už savo nuodėmę.",
			"26": "<sup>26</sup> Bus atleista Izraelio tautai ir tarp jų gyvenantiems ateiviams, nes nusikaltimas įvyko dėl nežinojimo.",
			"27": "<sup>27</sup> Jei vienas žmogus nusikalstų nežinodamas, teaukoja metinę ožką už savo nusikaltimą.",
			"28": "<sup>28</sup> Kunigas sutaikins jį, nes nusikalto Viešpačiui nežinodamas; jis bus sutaikintas, ir kaltė jam bus atleista.",
			"29": "<sup>29</sup> Vietiniams gyventojams ir ateiviams, netyčia nusikaltus, bus taikomas tas pats įstatymas.",
			"30": "<sup>30</sup> Bet jei žmogus nusikalstų sąmoningai ir taip paniekintų Viešpatį, nežiūrint ar jis bus vietinis, ar ateivis, jis bus išnaikintas iš savo tautos,",
			"31": "<sup>31</sup> nes paniekino Viešpaties žodį ir Jo įsakymą. Todėl ta siela bus sunaikinta. Jis atsakys už savo nusikaltimą’ ”.",
			"32": "<sup>32</sup> Izraelitams būnant dykumoje, jie rado žmogų, renkantį malkas sabato dieną.",
			"33": "<sup>33</sup> Jie atvedė jį pas Mozę ir Aaroną,",
			"34": "<sup>34</sup> ir uždarė jį, nes nežinojo, ką su juo daryti.",
			"35": "<sup>35</sup> Viešpats tarė Mozei: “Tas žmogus turi mirti, visi žmonės tegul užmuša jį akmenimis už stovyklos ribų”.",
			"36": "<sup>36</sup> Išvedę už stovyklos, užmušė jį akmenimis, kaip Viešpats buvo įsakęs.",
			"37": "<sup>37</sup> Viešpats kalbėjo Mozei:",
			"38": "<sup>38</sup> “Įsakyk izraelitams pasidaryti savo apdarų kraštuose kutus su mėlynomis juostelėmis.",
			"39": "<sup>39</sup> Pažvelgę į juosteles, atsiminkite Viešpaties įsakymus ir nesekite savo širdimis ir akimis, kurios veda į paleistuvystę.",
			"40": "<sup>40</sup> Atsiminkite mano įsakymus, juos vykdykite ir būkite šventi.",
			"41": "<sup>41</sup> Aš esu Viešpats, jūsų Dievas, kuris jus išvedžiau iš Egipto žemės”."
		},
		"16": {
			"1": "<sup>1</sup> Korachas, Levio sūnaus Kehato sūnaus Iccharo sūnus, ir iš Rubeno sūnų Eliabo sūnūs Datanas ir Abiramas bei Peleto sūnus Onas",
			"2": "<sup>2</sup> sukilo prieš Mozę. Prie jų prisidėjo du šimtai penkiasdešimt izraelitų, tautos kunigaikščių, žinomų bendruomenėje ir gerbiamų vyrų.",
			"3": "<sup>3</sup> Jie susirinkę kalbėjo prieš Mozę ir Aaroną: “Gana judviem; visi izraelitai yra šventi ir Viešpats yra tarp jų. Kodėl judu keliatės aukščiau Viešpaties tautos?”",
			"4": "<sup>4</sup> Tai išgirdęs, Mozė puolė ant žemės",
			"5": "<sup>5</sup> ir kalbėjo Korachui ir su juo esantiems: “Rytoj Viešpats parodys, kas yra šventas ir kas yra Jo. Tiems, kuriuos Jis išsirinko, Jis leis prisiartinti prie Jo.",
			"6": "<sup>6</sup> Kiekvienas imkite savo smilkytuvą: tu, Korachai, ir visas tavo būrys,",
			"7": "<sup>7</sup> pasiėmę ugnies, užberkite ant jos smilkalų Viešpaties akivaizdoje. Kurį Jis išsirinks, tas bus šventas. Jūs, Levio sūnūs, keliatės per aukštai!”",
			"8": "<sup>8</sup> Mozė sakė Korachui: “Klausykite, Levio sūnūs!",
			"9": "<sup>9</sup> Argi jums dar maža, kad Izraelio Dievas jus išsirinko iš visos tautos ir pašaukė eiti tarnystę Viešpaties palapinėje ir tarnauti tautai?",
			"10": "<sup>10</sup> Jis pašaukė savo tarnystei tave ir tavo brolius levitus, tai kodėl dar reikalaujate kunigystės?",
			"11": "<sup>11</sup> Kodėl sukilote tu ir tavo pasekėjai prieš Viešpatį? Kas gi yra Aaronas, kad prieš jį murmate?”",
			"12": "<sup>12</sup> Mozė pasiuntė pašaukti abu Eliabo sūnus Dataną ir Abiramą. Juodu atsakė: “Mudu neisime.",
			"13": "<sup>13</sup> Ar tau dar maža, kad mus išvedei iš žemės, plūstančios pienu ir medum, kad nužudytum dykumoje? Ar dar nori viešpatauti mums?",
			"14": "<sup>14</sup> Ar nuvedei mus į žemę, kurioje teka pienas ir medus, ir ar davei mums paveldėti laukus ir vynuogynus? Ar ir akis nori šiems žmonėms išlupti? Mudu neateisime!”",
			"15": "<sup>15</sup> Mozė, labai įpykęs, meldė Viešpatį: “Nežiūrėk į jų aukas; aš neėmiau iš jų nė asilaičio ir nė vieno nenuskriaudžiau”.",
			"16": "<sup>16</sup> Mozė sakė Korachui: “Rytoj tu ir visas tavo būrys stokite Viešpaties akivaizdoje: tu, jie ir Aaronas.",
			"17": "<sup>17</sup> Imkite kiekvienas savo smilkytuvą, įdėkite į juos smilkalų ir atneškite smilkytuvus Viešpaties akivaizdon­du šimtus penkiasdešimt smilkytuvų; taip pat tu ir Aaronas atneškite savo smilkytuvus”.",
			"18": "<sup>18</sup> Visi įsidėjo ugnies į smilkytuvus ir užbėrę ant jos smilkalų atsistojo prie Susitikimo palapinės įėjimo, kartu su Moze su Aaronu.",
			"19": "<sup>19</sup> Korachas sušaukė visus izraelitus prie Susitikimo palapinės, ir visiems matant pasirodė Viešpaties šlovė.",
			"20": "<sup>20</sup> Viešpats tarė Mozei ir Aaronui:",
			"21": "<sup>21</sup> “Pasitraukite iš šio susirinkimo, Aš juos bematant sunaikinsiu”.",
			"22": "<sup>22</sup> Juodu puolė ant žemės, prašydami: “Dieve, Tu kiekvieno kūno dvasios Dievas. Argi vienam nusidėjus Tavo rūstybė sunaikins visus?”",
			"23": "<sup>23</sup> Viešpats atsakė Mozei:",
			"24": "<sup>24</sup> “Įsakyk visiems pasitraukti nuo Koracho, Datano ir Abiramo palapinių”.",
			"25": "<sup>25</sup> Mozė ėjo prie Datano ir Abiramo, jį sekė Izraelio vyresnieji.",
			"26": "<sup>26</sup> Jis tarė tautai: “Pasitraukite nuo šių piktadarių palapinių ir nelieskite nieko, kas jiems priklauso, kad ir jūs nežūtumėte dėl jų nuodėmių”.",
			"27": "<sup>27</sup> Visiems pasitraukus nuo Koracho, Datano ir Abiramo palapinių, Datanas ir Abiramas išėję stovėjo savo palapinių angose kartu su žmonomis ir vaikais.",
			"28": "<sup>28</sup> Mozė tarė: “Dabar matysite, kad Viešpats siuntė mane visa tai daryti ir kad aš nieko nedariau savo valia.",
			"29": "<sup>29</sup> Jei jie mirs paprasta mirtimi, kaip miršta visi žmonės, tai Viešpats manęs nesiuntė;",
			"30": "<sup>30</sup> bet jei Viešpats padarys naują dalyką, kad žemė atsivers ir prarys juos ir visa, kas jiems priklauso, ir jie gyvi pateks į mirusiųjų buveinę, žinokite, kad jie piktžodžiavo Viešpačiui”.",
			"31": "<sup>31</sup> Kai tik jis baigė kalbėti, žemė prasiskyrė po jų kojomis",
			"32": "<sup>32</sup> ir atsivėrusi prarijo juos su jų palapinėmis, žmonėmis ir visu lobiu.",
			"33": "<sup>33</sup> Jie gyvi nugrimzdo į mirusiųjų buveinę, ir žemė apdengė juos, ir jie buvo išnaikinti iš susirinkusiųjų.",
			"34": "<sup>34</sup> O visi izraelitai, kurie stovėjo aplinkui, žūstantiems šaukiant, pradėjo bėgti, nes jie sakė: “Kad tik ir mūsų žemė neprarytų”.",
			"35": "<sup>35</sup> Viešpaties siųsta ugnis sunaikino tuos du šimtus penkiasdešimt vyrų, kurie aukojo smilkalus.",
			"36": "<sup>36</sup> Po to Viešpats kalbėjo Mozei:",
			"37": "<sup>37</sup> “Įsakyk Aarono sūnui kunigui Eleazarui surinkti smilkytuvus iš degėsių, ir išsklaidyti ugnį, nes jie pašventinti.",
			"38": "<sup>38</sup> Smilkytuvus tų, kurie nusidėjo prieš savo sielas, tegul perkala į skardas ir jomis apdengia aukurą, nes jie buvo aukojami prieš Viešpatį ir tapo pašventinti. Tai bus izraelitams atsiminimo ženklas”.",
			"39": "<sup>39</sup> Kunigas Eleazaras surinko varinius smilkytuvus, kuriuose aukojo gaisre žuvę, perkalė į skardas ir apdengė jomis aukurą,",
			"40": "<sup>40</sup> kaip atminimą Izraelio vaikams, kaip Viešpats jam sakė per Mozę, kad svetimasis, kuris nėra Aarono palikuonis, nesiartintų aukoti smilkalų Viešpačiui ir nežūtų kaip Korachas ir visas jo būrys.",
			"41": "<sup>41</sup> Kitą dieną visi izraelitai murmėjo prieš Mozę ir Aaroną: “Judu nužudėte Viešpaties žmones”.",
			"42": "<sup>42</sup> Žmonėms susirinkus prieš Mozę ir Aaroną, jie atsigręžė į Susitikimo palapinę, ir štai ją apdengė debesis ir pasirodė Viešpaties šlovė.",
			"43": "<sup>43</sup> Mozė ir Aaronas nuėjo prie Susitikimo palapinės.",
			"44": "<sup>44</sup> Viešpats tarė Mozei:",
			"45": "<sup>45</sup> “Pasitraukite nuo šių žmonių. Aš juos tuojau sunaikinsiu”. Juodu puolė ant žemės.",
			"46": "<sup>46</sup> Mozė sakė Aaronui: “Imk smilkytuvą, pasisemk žarijų nuo aukuro, užberk ant jų smilkalų ir skubiai eik prie žmonių, kad juos sutaikintum, nes jau pasireiškė Viešpaties rūstybė ir prasidėjo maras”.",
			"47": "<sup>47</sup> Ir Aaronas padarė, kaip Mozė įsakė, ir nubėgo į žmonių vidurį, tarp kurių jau buvo prasidėjęs maras. Jis užbėrė smilkalų ir sutaikino tautą.",
			"48": "<sup>48</sup> Jis stovėjo tarp mirusiųjų ir gyvųjų. Maras liovėsi.",
			"49": "<sup>49</sup> Mirė keturiolika tūkstančių septyni šimtai, neskaitant žuvusiųjų Koracho maište.",
			"50": "<sup>50</sup> Kai maras liovėsi, Aaronas sugrįžo pas Mozę prie Susitikimo palapinės."
		},
		"17": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Sakyk izraelitams, kad kiekviena giminė duotų lazdą; iš visų jų kunigaikščių pagal jų giminę dvylika lazdų. Užrašyk kiekvieno vardą ant jo lazdos.",
			"3": "<sup>3</sup> Ant Levio lazdos užrašyk Aarono vardą. Nuo giminės kunigaikščio bus po vieną lazdą.",
			"4": "<sup>4</sup> Jas sudėk Susitikimo palapinėje ties liudijimu, kur jums apsireiškiu.",
			"5": "<sup>5</sup> Kurį iš jų išsirinksiu, to lazda pražys, taip padarysiu galą izraelitų murmėjimui, kuriuo jie prieš judu murma”.",
			"6": "<sup>6</sup> Mozė pranešė tai izraelitams. Kiekvienos giminės kunigaikštis davė lazdą. Buvo dvylika lazdų, tarp jų ir Aarono lazda.",
			"7": "<sup>7</sup> Mozė jas padėjo Viešpaties akivaizdoje Liudijimo palapinėje.",
			"8": "<sup>8</sup> Mozė, įėjęs kitą dieną, rado žaliuojančią Aarono, Levio giminės, lazdą. Pumpurai išsiskleidė žiedais, sužaliavo lapeliais ir subrandino migdolus.",
			"9": "<sup>9</sup> Mozė išnešė visas lazdas iš šventyklos prie izraelitų. Jie apžiūrėjo jas ir kiekvienas atsiėmė savo lazdą.",
			"10": "<sup>10</sup> Tuomet Viešpats tarė Mozei: “Įnešk atgal Aarono lazdą prie liudijimo, kad ji būtų ženklas maištaujantiems, kad pasibaigtų jų murmėjimas prieš mane, ir jie nemirtų”.",
			"11": "<sup>11</sup> Mozė padarė, kaip Viešpats įsakė.",
			"12": "<sup>12</sup> Izraelitai sakė Mozei: “Štai mes mirštame, mes visi žūvame.",
			"13": "<sup>13</sup> Kas tik artinasi prie Viešpaties palapinės, tas miršta. Argi visi būsime sunaikinti?”"
		},
		"18": {
			"1": "<sup>1</sup> Viešpats kalbėjo Aaronui: “Tu ir tavo sūnūs būsite atsakingi už šventyklą ir kunigų tarnystę.",
			"2": "<sup>2</sup> Tavo broliai iš Levio giminės, tavo tėvo palikuonys, prisidės prie tavęs ir tarnaus tau, bet tu ir tavo sūnūs atliksite kunigų tarnystę Liudijimo palapinėje.",
			"3": "<sup>3</sup> Levitai tarnaus tau ir atliks visus palapinės darbus, tačiau jie nelies šventyklos indų ir aukuro, kad jie ir jūs nežūtumėte.",
			"4": "<sup>4</sup> Tebūna jie su tavimi ir tetarnauja Susitikimo palapinėje, atlikdami visus jos darbus. Svetimasis neturi būti tarp jūsų.",
			"5": "<sup>5</sup> Prižiūrėkite šventyklą ir aukurą, kad nekiltų mano rūstybė prieš izraelitus.",
			"6": "<sup>6</sup> Aš jums daviau jūsų brolius levitus iš izraelitų kaip dovaną, kad jie tarnautų Susitikimo palapinėje.",
			"7": "<sup>7</sup> Tu ir tavo sūnūs atlikite kunigų tarnystę. Visa, kas priklauso aukurui ir kas yra už uždangos, bus kunigų aptarnaujama; jei kas svetimas artinsis, bus baudžiamas mirtimi”.",
			"8": "<sup>8</sup> Viešpats kalbėjo Aaronui: “Aš tau duodu savo aukų dalį iš visko, ką izraelitai pašvenčia. Duodu tai tau ir tavo sūnums, nes esate patepti. Tas įstatymas bus amžinas.",
			"9": "<sup>9</sup> Šitos aukos, aukojamos Viešpačiui, priklausys tau: duonos auka, auka už nuodėmę ir auka už kaltę. Jos yra šventos ir teks tau ir tavo sūnums.",
			"10": "<sup>10</sup> Jas valgysite šventoje vietoje, tik vyrai valgys tas aukas, nes jos šventos.",
			"11": "<sup>11</sup> Visas izraelitų siūbuojamąsias aukas duodu tau, tavo sūnums ir dukterims amžina teise: kas tavo namuose nesuteptas, valgys jas.",
			"12": "<sup>12</sup> Geriausio aliejaus, vynuogių ir javų pirmojo derliaus, aukojamų Viešpačiui, duodu tau.",
			"13": "<sup>13</sup> Visi pirmieji jūsų krašto vaisiai, atnešami Viešpačiui, bus tavo ir, kas tavo namuose nesuteptas, juos valgys.",
			"14": "<sup>14</sup> Visa, kas Izraelyje pašvęsta, bus tavo.",
			"15": "<sup>15</sup> Visi pirmagimiai, kurie aukojami Viešpačiui, ar tai būtų žmonės, ar gyvuliai, priklausys tau. Žmogaus pirmagimis turi būti išpirktas ir kiekvieno nešvaraus gyvulio pirmagimį aukotojas privalės išpirkti.",
			"16": "<sup>16</sup> Kūdikis vieno mėnesio amžiaus bus išperkamas už penkis šekelius sidabro pagal šventyklos šekelį, kurį sudaro dvidešimt gerų.",
			"17": "<sup>17</sup> Galvijų, avių ir ožkų pirmagimių negalima išpirkti, nes jie paskirti Viešpačiui: jų kraują išliesi ant aukuro ir taukus sudeginsi, kad būtų malonus kvapas Viešpačiui.",
			"18": "<sup>18</sup> Jų mėsa bus tavo kaip ir siūbuojamosios aukos krūtinė ir dešinysis petys.",
			"19": "<sup>19</sup> Visas šventas aukas, kurias izraelitai aukoja Viešpačiui, duodu tau ir tavo sūnums bei dukterims amžina teise. Tai amžina druskos sandora prieš Viešpatį tau ir tavo vaikams”.",
			"20": "<sup>20</sup> Viešpats kalbėjo Aaronui: “Izraelitų žemėje nieko nepaveldėsi ir neturėsi tarp jų dalies: Aš­tavo dalis ir paveldėjimas.",
			"21": "<sup>21</sup> Levitams atiduodu visas Izraelio dešimtines už tarnystę, kurią jie man atlieka Susitikimo palapinėje,",
			"22": "<sup>22</sup> kad izraelitai nesiartintų prie palapinės, nenusidėtų ir nemirtų.",
			"23": "<sup>23</sup> Tik levitai tarnaus Susitikimo palapinėje ir bus už ją atsakingi; tai bus amžinas įstatymas visoms jūsų kartoms. Jie nieko nepaveldės tarp izraelitų.",
			"24": "<sup>24</sup> Izraelio vaikų dešimtines, atnešamas Viešpačiui, atidaviau levitams. Todėl jiems pasakiau: ‘Jūs nieko nepaveldėsite tarp izraelitų’ ”.",
			"25": "<sup>25</sup> Po to Viešpats kalbėjo Mozei:",
			"26": "<sup>26</sup> “Taip kalbėk levitams: ‘Gavę iš izraelitų dešimtines, tų dešimtinių dešimtą dalį aukokite Viešpačiui.",
			"27": "<sup>27</sup> Tai bus jums įskaityta kaip grūdai iš klojimo ar dalis nuo vynuogių spaustuvo.",
			"28": "<sup>28</sup> Taip ir jūs aukosite auką Viešpačiui iš surinktų dešimtinių, atiduodami ją kunigui Aaronui.",
			"29": "<sup>29</sup> Iš visko, kas duodama jums, aukokite aukas Viešpačiui, iš visko, kas geriausia, pašvęstąją dalį’.",
			"30": "<sup>30</sup> Todėl sakyk jiems: ‘Kai atnešite iš visko tai, kas geriausia, bus tai įskaityta levitams kaip gauta iš klojimo ir vynuogių spaustuvo.",
			"31": "<sup>31</sup> Jūs ir jūsų šeimos valgys tai bet kur, nes tai užmokestis už tarnystę, kurią atliekate Susitikimo palapinėje.",
			"32": "<sup>32</sup> Nenusikalskite, pasilaikydami sau geriausius dalykus, ir nesutepkite izraelitų aukų, kad nemirtumėte’ ”."
		},
		"19": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei ir Aaronui:",
			"2": "<sup>2</sup> “Įsakyk izraelitams atvesti sveiką žalą karvę, dar nekinkytą į jungą,",
			"3": "<sup>3</sup> ir duokite ją kunigui Eleazarui. Ji bus išvesta iš stovyklos ir papjauta jo akivaizdoje.",
			"4": "<sup>4</sup> Tada Eleazaras, padažęs jos kraujyje pirštą, pašlakstys septynis kartus Susitikimo palapinės įėjimo link.",
			"5": "<sup>5</sup> Paskui ji bus sudeginta jo akivaizdoje: oda, mėsa, kraujas ir mėšlas.",
			"6": "<sup>6</sup> Kunigas įmes į ugnį, kurioje dega karvė, kedro medžio, yzopo ir raudonų siūlų.",
			"7": "<sup>7</sup> Po to kunigas išplaus savo drabužius, apsiplaus vandeniu ir grįš į stovyklą, ir jis bus nešvarus iki vakaro.",
			"8": "<sup>8</sup> Tas, kuris karvę sudegins, išplaus savo drabužius, išsimaudys ir bus nešvarus iki vakaro.",
			"9": "<sup>9</sup> Kas nors nesusitepęs susems karvės pelenus ir juos išpils už stovyklos švarioje vietoje, kad jie būtų izraelitų laikomi apvalymo vandeniui padaryti, nes karvė sudeginta kaip auka už nuodėmę.",
			"10": "<sup>10</sup> Kuris susems karvės pelenus, plaus savo drabužius ir bus nešvarus iki vakaro. Tai bus izraelitams ir tarp jų gyvenantiems ateiviams amžinas įstatymas.",
			"11": "<sup>11</sup> Kas paliečia žmogaus lavoną, bus nešvarus septynias dienas.",
			"12": "<sup>12</sup> Jis apsivalys šiuo vandeniu trečią ir septintą dieną ir bus švarus. Jei trečią dieną neapsivalys, tai septintą dieną nebus švarus.",
			"13": "<sup>13</sup> Kuris palies mirusio žmogaus lavoną ir nebus apšlakstytas minėtu vandeniu, suteps Viešpaties palapinę ir bus išnaikintas iš izraelitų; kadangi jis neapšlakstytas apvalomuoju vandeniu, jis liks nešvarus.",
			"14": "<sup>14</sup> Šitas yra įstatymas apie žmogų, mirusį palapinėje. Visi, kurie įeina į jo palapinę, ir visi ten esantieji bus nešvarūs septynias dienas.",
			"15": "<sup>15</sup> Indas, neturintis dangčio, bus suteptas.",
			"16": "<sup>16</sup> Jei kas lauke paliestų užmušto kardu ar savaime mirusio žmogaus lavoną, kaulą ar karstą, bus nešvarus septynias dienas.",
			"17": "<sup>17</sup> Dėl nešvaraus žmogaus tegul ima karvės, sudegintos apvalyti nuo nuodėmės, pelenų, įberia į indą ir užpila tekančio vandens ant jų.",
			"18": "<sup>18</sup> Nesusitepęs žmogus, padažęs jame yzopą, teapšlaksto visą palapinę ir visus daiktus bei susitepusius prisilietimu žmones.",
			"19": "<sup>19</sup> Nesusitepęs apšlakstys nešvarųjį trečią ir septintą dieną, ir jis apsivalys septintą dieną, išplaus savo drabužius, pats išsimaudys ir bus nešvarus iki vakaro.",
			"20": "<sup>20</sup> Jei kas nebus tuo būdu apvalytas, jis bus išnaikintas iš izraelitų, nes sutepė Viešpaties šventyklą; jis nebuvo apšlakstytas apvalomuoju vandeniu, todėl yra nešvarus.",
			"21": "<sup>21</sup> Tai amžinas nuostatas jiems, kad tas, kuris šlakstė vandeniu, plautų savo drabužius, ir kiekvienas, palietęs apvalomąjį vandenį, bus nešvarus iki vakaro.",
			"22": "<sup>22</sup> Ką nešvarus žmogus palies­suteps, kas prisiliestų suteptų daiktų­bus nešvarus iki vakaro”."
		},
		"20": {
			"1": "<sup>1</sup> Izraelitai atvyko į Cino dykumą pirmą mėnesį ir sustojo Kadeše. Ten mirė Mirjama ir buvo palaidota.",
			"2": "<sup>2</sup> Toje vietoje nebuvo vandens. Izraelitai, susirinkę pas Mozę ir Aaroną,",
			"3": "<sup>3</sup> priekaištaudami kalbėjo: “Geriau būtume žuvę su savo broliais Viešpaties akivaizdoje.",
			"4": "<sup>4</sup> Kodėl jūs atvedėte Viešpaties susirinkimą į šitą dykumą, kad mes ir mūsų gyvuliai čia numirtume?",
			"5": "<sup>5</sup> Kam mus išvedėte iš Egipto ir atvedėte į šitą netikusią vietą, kur negalime sėti ir neauga nei figos, nei vynuogės, nei granatai ir nėra net vandens?”",
			"6": "<sup>6</sup> Mozė ir Aaronas, palikę minią, įėjo į Susitikimo palapinę ir puolė ant žemės. Jiems pasirodė Viešpaties šlovė.",
			"7": "<sup>7</sup> Viešpats tarė Mozei:",
			"8": "<sup>8</sup> “Imk lazdą, abu su Aaronu surinkite žmones ir jų akyse kalbėkite uolai, ir ji duos vandens. Tu išgausi jiems vandens iš uolos, ir atsigers žmonės bei jų galvijai”.",
			"9": "<sup>9</sup> Mozė ėmė Viešpaties akivaizdoje buvusią lazdą, kaip Jis jam įsakė.",
			"10": "<sup>10</sup> Mozė ir Aaronas surinko žmones prie uolos ir jiems kalbėjo: “Klausykite, maištininkai! Ar mudu turime iš šitos uolos išgauti jums vandens?”",
			"11": "<sup>11</sup> Mozė pakėlė ranką, smogė du kartus lazda į uolą, ir pasipylė apsčiai vandens; atsigėrė žmonės ir jų gyvuliai.",
			"12": "<sup>12</sup> Bet Viešpats tarė Mozei ir Aaronui: “Kadangi manimi netikėjote ir neparodėte mano šventumo izraelitų akivaizdoje, judu neįvesite šitos tautos į žemę, kurią jiems duosiu”.",
			"13": "<sup>13</sup> Tai Meriba, kur izraelitai murmėjo prieš Viešpatį ir Jis parodė jiems savo šventumą.",
			"14": "<sup>14</sup> Mozė išsiuntė pasiuntinius iš Kadešo pas Edomo karalių, kad pasakytų: “Taip sako tavo brolis Izraelis: ‘Tu žinai visus mūsų vargus,",
			"15": "<sup>15</sup> žinai, kaip mūsų tėvai nuėjo į Egiptą ir mes ilgai ten gyvenome, kaip egiptiečiai spaudė mus ir mūsų tėvus.",
			"16": "<sup>16</sup> Kai šaukėmės Viešpaties, Jis mus išklausė ir siuntė angelą, kuris mus išvedė iš Egipto. Esame sustoję Kadešo mieste, kuris yra prie pat tavo krašto sienos.",
			"17": "<sup>17</sup> Maldaujame, leisk mums pereiti per tavo žemę. Mes neisime dirvomis ar vynuogynais, negersime vandens iš tavo šulinių, bet trauksime vieškeliu, nenukrypdami nei į dešinę, nei į kairę, kol pereisime tavo šalį’ ”.",
			"18": "<sup>18</sup> Karalius jiems atsakė: “Neisite per mano žemę, o jei eisite, aš jus pasitiksiu su ginklu”.",
			"19": "<sup>19</sup> Izraelitai sakė: “Mes eisime vieškeliu, o jei mes ir mūsų gyvuliai gersime tavo vandens, atsilyginsime, tiktai leisk pereiti per tavo žemę”.",
			"20": "<sup>20</sup> Bet jis atsakė: “Neleidžiu!” Ir edomitų gausi kariuomenė išėjo prieš izraelitus.",
			"21": "<sup>21</sup> Kadangi edomitai atsisakė praleisti izraelitus, tai jie pasuko kitais keliais.",
			"22": "<sup>22</sup> Pakilę iš Kadešo, izraelitai atėjo prie Horo kalno.",
			"23": "<sup>23</sup> Ten, prie Edomo žemės sienos, Viešpats kalbėjo Mozei ir Aaronui:",
			"24": "<sup>24</sup> “Aaronas susijungs su savo tauta, nes jis neįeis į žemę, kurią pažadėjau izraelitams, dėl to, kad netikėjote mano žodžiais prie Meribos.",
			"25": "<sup>25</sup> Imk Aaroną ir jo sūnų Eleazarą ir nuvesk juodu ant Horo kalno.",
			"26": "<sup>26</sup> Aaronas tegul nusivelka savo drabužius ir jais apvilk jo sūnų Eleazarą. Aaronas mirs tenai”.",
			"27": "<sup>27</sup> Mozė padarė, kaip Viešpats įsakė. Jie užlipo į Horo kalną, visiems susirinkusiems matant.",
			"28": "<sup>28</sup> Mozė nurengė nuo Aarono drabužius ir jais apvilko jo sūnų Eleazarą. Aaronas mirė kalno viršūnėje. Mozė su Eleazaru grįžo nuo kalno.",
			"29": "<sup>29</sup> Izraelitai, pamatę, kad Aaronas mirė, apraudojo jį trisdešimt dienų."
		},
		"21": {
			"1": "<sup>1</sup> Kanaaniečių karalius Aradas, kuris gyveno krašto pietuose, išgirdęs, kad izraelitai ateina iš Atarimo, išėjo į kovą prieš juos ir kelis paėmė į nelaisvę.",
			"2": "<sup>2</sup> Izraelitai padarė įžadą Viešpačiui: “Jei atiduosi šitą tautą į mūsų rankas, sunaikinsime jos miestus”.",
			"3": "<sup>3</sup> Viešpats išklausė izraelitų prašymo ir atidavė jiems kanaaniečius, kuriuos jie nugalėjo, sunaikino juos ir jų miestus. Jie praminė aną vietą Horma.",
			"4": "<sup>4</sup> Nuo Horo kalno jie ėjo keliu, vedančiu Raudonosios jūros link, kad aplenktų Edomo žemę. Kelionėje tauta nerimavo,",
			"5": "<sup>5</sup> ji kalbėjo prieš Dievą ir Mozę: “Kodėl mus išvedėte iš Egipto numirti dykumoje? Nėra duonos, nėra vandens, mums įgriso tas menkavertis maistas”.",
			"6": "<sup>6</sup> Tada Viešpats siuntė nuodingas gyvates. Jos gėlė žmones, ir daugelis nuo jų mirė.",
			"7": "<sup>7</sup> Jie kreipėsi į Mozę, sakydami: “Nusidėjome, kalbėdami prieš Viešpatį ir tave, melsk, kad pašalintų nuo mūsų gyvates”. Mozė meldėsi už tautą.",
			"8": "<sup>8</sup> Viešpats sakė Mozei: “Padaryk varinę gyvatę ir iškelk ją ant stulpo; kas įgeltas į ją pažvelgs, liks gyvas”.",
			"9": "<sup>9</sup> Mozė padirbdino varinę gyvatę ir iškėlė ją ant stulpo. Į ją pažvelgę, įgeltieji likdavo gyvi.",
			"10": "<sup>10</sup> Izraelitai keliavo toliau ir sustojo Obote.",
			"11": "<sup>11</sup> Išėję iš Oboto, ištiesė palapines Ije Abarimo dykumoje, kuri yra Moabo rytų pusėje.",
			"12": "<sup>12</sup> Iš ten pasitraukę, atėjo į Zeredo slėnį.",
			"13": "<sup>13</sup> Jį palikę, sustojo prie Arnono upės, tekančios dykumoje šiapus amoritų sienos. Arnono upė yra Moabo siena ir skiria moabitus nuo amoritų.",
			"14": "<sup>14</sup> Todėl Viešpaties kovų knygoje pasakyta: “Kaip padarė Raudonojoje jūroje, taip padarys ir prie Arnono upės,",
			"15": "<sup>15</sup> kuri pasisukus pasiekia Aro miestą ir priartėja prie moabitų sienos”.",
			"16": "<sup>16</sup> Iš čia izraelitai priartėjo prie šulinio, apie kurį Viešpats kalbėjo Mozei: “Surink tautą, ir Aš duosiu jai vandens”.",
			"17": "<sup>17</sup> Tada izraelitai giedojo: “Šuliny, duok vandens! Giedosime tau,",
			"18": "<sup>18</sup> šuliny, kurį kunigaikščiai ir kilnieji iškasė skeptru ir lazdomis”. Iš tos dykumos jie atėjo į Mataną,",
			"19": "<sup>19</sup> iš Matanos į Nahalielį, iš Nahalielio į Bamotą",
			"20": "<sup>20</sup> ir iš Bamoto­į slėnį Moabo šalyje arti Pisgos kalno, kuris yra ties dykuma.",
			"21": "<sup>21</sup> Izraelis nusiuntė pas amoritų karalių Sihoną pasiuntinius su prašymu:",
			"22": "<sup>22</sup> “Leisk mums pereiti per tavo žemę, mes nenukrypsime į dirvas ir vynuogynus, negersime vandens iš tavo šulinių, eisime vieškeliu, kol pereisime per tavo kraštą”.",
			"23": "<sup>23</sup> Bet Sihonas neleido izraelitams eiti per savo kraštą. Surinkęs savo kariuomenę, išėjo prieš juos į dykumą ir prie Jahaco kovojo su izraelitais.",
			"24": "<sup>24</sup> Izraelitai sumušė jį ir užėmė Sihono kraštą nuo Arnono iki Jaboko upių ir ligi Amono krašto, kurio sienos buvo ginamos stiprios sargybos.",
			"25": "<sup>25</sup> Izraelitai užėmė visus amoritų miestus ir apsigyveno juose, Hešbone ir jam priklausančiuose miesteliuose.",
			"26": "<sup>26</sup> Hešbonas buvo sostinė amoritų karaliaus Sihono, kuris buvo kovojęs su buvusiu Moabo karaliumi ir užėmęs visą jam priklausančią šalį iki Arnono upės.",
			"27": "<sup>27</sup> Todėl dainoje sakoma: “Ateikite į Hešboną, karaliaus Sihono miestą, kuris bus atstatytas ir sustiprintas!",
			"28": "<sup>28</sup> Ugnis iš Hešbono sunaikino moabitų miestą Arą ir Arnono aukštumų viešpačius.",
			"29": "<sup>29</sup> Vargas tau, Moabai! Žuvai, Kemošo tauta! Jis atidavė savo sūnus ir dukteris į nelaisvę amoritų karaliui Sihonui.",
			"30": "<sup>30</sup> Jie sunaikinti nuo Hešbono iki Dibono ir iki Nofacho prie Medebos”.",
			"31": "<sup>31</sup> Taip izraelitai apsigyveno amoritų žemėje.",
			"32": "<sup>32</sup> Mozė išsiuntė išžvalgyti Jazerą, ir izraelitai užėmė jam priklausančius miestelius ir išvijo amoritus.",
			"33": "<sup>33</sup> Jie traukė Bašano keliu; ten juos pasitiko Bašano karalius Ogas su visa savo kariuomene prie Edrėjo.",
			"34": "<sup>34</sup> Viešpats kalbėjo Mozei: “Nebijok jo, nes Aš atidaviau tau jį, visą jo tautą ir žemę; padaryk su juo, kaip padarei su amoritų karaliumi Sihonu Hešbone”.",
			"35": "<sup>35</sup> Izraelitai sunaikino jį, jo sūnus ir visą jo tautą taip, kad nė vieno neliko, ir užėmė šalį."
		},
		"22": {
			"1": "<sup>1</sup> Izraelitai keliavo ir sustojo Moabo lygumoje, anoje pusėje Jordano, ties Jerichu.",
			"2": "<sup>2</sup> Ciporo sūnus Balakas matė visa, ką Izraelis padarė amoritams.",
			"3": "<sup>3</sup> Moabitai labai bijojo izraelitų, nes jų buvo labai daug.",
			"4": "<sup>4</sup> Moabas sakė midjaniečių vyresniesiems: “Šita tauta sunaikins mus visus, čia gyvenančius, kaip jautis sunaikina žolę iki šaknų”. O Ciporo sūnus Balakas tuo metu buvo moabitų karalius.",
			"5": "<sup>5</sup> Jis siuntė pasiuntinius pas Beoro sūnų Balaamą į Petoro miestą prie upės, kad jį pakviestų, sakydami: “Iš Egipto išėjo tauta, kuri apdengė žemės paviršių ir dabar sustojo prie mano krašto sienų.",
			"6": "<sup>6</sup> Ateik ir prakeik tą tautą, nes ji galingesnė už mane. Gal tada galėsiu kaip nors ją sumušti ir išvyti iš savo žemės, nes žinau, kad kurį tu laimini, tas yra palaimintas ir, kurį prakeiki, yra prakeiktas”.",
			"7": "<sup>7</sup> Moabo ir Midjano vyresnieji išėjo laikydami rankose užmokestį už žyniavimą. Kai jie atėjo pas Balaamą ir jam perdavė Balako žodžius,",
			"8": "<sup>8</sup> jis atsakė: “Apsinakvokite pas mane, ir aš atsakysiu, ką man pasakys Viešpats”. Ir Moabo vyresnieji pasiliko pas Balaamą.",
			"9": "<sup>9</sup> Atėjo Dievas pas Balaamą ir klausė: “Kas yra šitie žmonės?”",
			"10": "<sup>10</sup> Balaamas atsakė Dievui: “Moabitų karalius, Ciporo sūnus Balakas, atsiuntė juos pas mane",
			"11": "<sup>11</sup> ir pranešė, kad išėjusi iš Egipto tauta apdengė žemės paviršių. Jis prašė: ‘Ateik ir prakeik ją, gal tada aš įstengsiu nugalėti juos ir išvaryti iš savo krašto’ ”.",
			"12": "<sup>12</sup> Dievas atsakė Balaamui: “Neik su jais ir neprakeik tautos, nes ji yra palaiminta”.",
			"13": "<sup>13</sup> Balaamas, atsikėlęs rytą, tarė Balako kunigaikščiams: “Grįžkite į savo žemę, nes Viešpats uždraudė man eiti su jumis”.",
			"14": "<sup>14</sup> Ir Moabo kunigaikščiai pakilo ir, sugrįžę pas Balaką, pranešė: “Balaamas atsisakė eiti su mumis”.",
			"15": "<sup>15</sup> Balakas vėl siuntė daugiau ir aukštesnės kilmės pasiuntinių.",
			"16": "<sup>16</sup> Jie, atėję pas Balaamą, tarė: “Ciporo sūnus Balakas taip sako: ‘Skubiai ateik pas mane.",
			"17": "<sup>17</sup> Pagerbsiu tave ir darysiu, ką įsakysi, tik ateik ir prakeik šitą tautą’ ”.",
			"18": "<sup>18</sup> Balaamas atsakė Balako pasiuntiniams: “Jei Balakas duotų pilnus savo namus sidabro ir aukso, aš negalėčiau peržengti Viešpaties, mano Dievo, žodžio, ir padaryti daugiau ar mažiau.",
			"19": "<sup>19</sup> Prašau, pasilikite šią naktį pas mane, kad sužinočiau, ką dar Viešpats man sakys”.",
			"20": "<sup>20</sup> Naktį atėjo Dievas pas Balaamą ir jam tarė: “Jei tie žmonės tave kviečia, eik su jais, bet daryk taip, kaip tau įsakysiu”.",
			"21": "<sup>21</sup> Balaamas, atsikėlęs rytą, pabalnojo savo asilę ir iškeliavo su moabitų kunigaikščiais.",
			"22": "<sup>22</sup> Viešpats užsirūstino, kad Balaamas išėjo. Jiems keliaujant, Viešpaties angelas užstojo kelią Balaamui, kuris jojo ant asilės. Su juo buvo du tarnai.",
			"23": "<sup>23</sup> Asilė, pamačiusi angelą stovintį su nuogu kardu, pasuko iš kelio ir ėjo lauku. Balaamas mušė ją, norėdamas grąžinti į kelią.",
			"24": "<sup>24</sup> Angelas atsistojo siauroje vietoje tarp dviejų sienų, kuriomis buvo aptverti vynuogynai.",
			"25": "<sup>25</sup> Pamačiusi jį, asilė šliejosi prie sienos ir prispaudė Balaamo koją. Jis vėl ją mušė.",
			"26": "<sup>26</sup> Viešpaties angelas nuėjo į dar siauresnę vietą, kur nebuvo galima pasukti nei dešinėn, nei kairėn, ir atsistojo.",
			"27": "<sup>27</sup> Asilė, pamačiusi stovintį angelą, sukniubo po Balaamu. Balaamas įpykęs mušė ją lazda.",
			"28": "<sup>28</sup> Viešpats atvėrė asilės nasrus, ir ji kalbėjo Balaamui: “Ką aš tau padariau? Kodėl mane muši jau trečią kartą?”",
			"29": "<sup>29</sup> Balaamas atsakė asilei: “Kad tyčiojiesi iš manęs. Jei turėčiau kardą, aš tave užmuščiau”.",
			"30": "<sup>30</sup> Asilė atsakė Balaamui: “Argi aš ne tavo asilė, kuria visada jodinėdavai? Pasakyk, ar aš tau kada nors taip dariau?” Jis atsakė: “Niekados!”",
			"31": "<sup>31</sup> Tada Viešpats atvėrė Balaamui akis. Ir jis, pamatęs Viešpaties angelą, stovintį kelyje su nuogu kardu, krito veidu į žemę.",
			"32": "<sup>32</sup> Viešpaties angelas jam tarė: “Kodėl muši jau trečią kartą savo asilę? Aš atėjau tau sukliudyti, nes nepritariu tavo kelionei.",
			"33": "<sup>33</sup> Jei asilė nebūtų pasukusi iš kelio tris kartus ir nebūtų pasitraukusi, būčiau tave užmušęs, o ji būtų išlikusi gyva”.",
			"34": "<sup>34</sup> Balaamas tarė Viešpaties angelui: “Nusidėjau, nežinodamas, kad tu stovi prieš mane, o dabar, jei tau nepatinka, aš sugrįšiu”.",
			"35": "<sup>35</sup> Viešpaties angelas tarė: “Eik su jais ir kalbėk tiktai tą, ką tau įsakysiu”. Balaamas nuėjo su Balako kunigaikščiais.",
			"36": "<sup>36</sup> Tai išgirdęs, Balakas išėjo pasitikti Balaamo ligi miesto Aro, kuris yra Moabo pasienyje prie Arnono upės,",
			"37": "<sup>37</sup> ir tarė Balaamui: “Aš siunčiau pasiuntinius tave pakviesti, kodėl neatėjai? Ar manai, kad aš negaliu tavęs tinkamai pagerbti?”",
			"38": "<sup>38</sup> Balaamas atsakė Balakui: “Štai aš atėjau pas tave. Bet ar galiu aš ką nors pasakyti? Žodį, kurį Dievas įdės į mano lūpas, tą kalbėsiu”.",
			"39": "<sup>39</sup> Balaamas su Balaku nuėjo į Kirjat Hucotą.",
			"40": "<sup>40</sup> Balakas aukojo jaučius bei avis ir nusiuntė dalį aukos Balaamui ir atvykusiems su juo kunigaikščiams.",
			"41": "<sup>41</sup> Rytui išaušus, Balakas nuvedė Balaamą į Baalo aukštumas, iš kur jis matė dalį izraelitų tautos."
		},
		"23": {
			"1": "<sup>1</sup> Tada Balaamas kalbėjo Balakui: “Pastatydink man čia septynis aukurus ir paruošk man septynis veršius ir septynis avinus”.",
			"2": "<sup>2</sup> Balakas padarė, kaip Balaamas sakė. Tada juodu aukojo po veršį ir aviną ant kiekvieno aukuro.",
			"3": "<sup>3</sup> Balaamas tarė Balakui: “Pastovėk prie savo deginamosios aukos, o aš pasitrauksiu ir lauksiu Viešpaties žodžio: aš tau pasakysiu, ką Jis lieps”. Jis nuėjo į nuošalią aukštumą.",
			"4": "<sup>4</sup> Ten jį sutiko Dievas. Balaamas Jam kalbėjo: “Pastatydinau septynis aukurus ir aukojau ant jų po veršį ir aviną”.",
			"5": "<sup>5</sup> Viešpats įdėjo žodį į Balaamo lūpas ir sakė: “Grįžk pas Balaką ir taip kalbėk”.",
			"6": "<sup>6</sup> Sugrįžęs rado stovintį Balaką prie savo deginamosios aukos su visais Moabo kunigaikščiais.",
			"7": "<sup>7</sup> Jis kalbėjo: “Iš Aramo mane atvedė Balakas, moabitų karalius, iš rytų šalies kalnų pašaukė mane: ‘Ateik ir prakeik Jokūbą, linkėk pikta Izraeliui’.",
			"8": "<sup>8</sup> Kaipgi aš keikčiau, ko Dievas nekeikia? Kaipgi aš pasmerkčiau, ko Viešpats nesmerkia?",
			"9": "<sup>9</sup> Aš matau juos nuo uolų viršūnių ir į juos žiūriu nuo kalnų. Ta tauta gyvena atskirai ir neprisideda prie kitų tautų.",
			"10": "<sup>10</sup> Kas galėtų suskaityti Jokūbo dulkes ir suskaičiuoti ketvirtadalį Izraelio? O, kad galėčiau mirti teisiųjų mirtimi! O, kad mirčiau ramybėje kaip jie!”",
			"11": "<sup>11</sup> Balakas sakė Balaamui: “Ką tu man padarei? Aš tave pasišaukiau, kad prakeiktum mano priešus, o tu juos palaiminai”.",
			"12": "<sup>12</sup> Tas jam atsakė: “Argi aš neturiu kalbėti to, ką Viešpats įdeda į mano lūpas?”",
			"13": "<sup>13</sup> Balakas sakė jam: “Eikš su manimi į kitą vietą, kur matysi izraelitų dalį, bet visų negalėsi matyti; iš ten juos prakeiksi”.",
			"14": "<sup>14</sup> Kai jį nuvedė ant Pisgos kalno viršūnės, jis ten pastatydino septynis aukurus ir ant kiekvieno aukojo po veršį ir aviną.",
			"15": "<sup>15</sup> Tada Balaamas tarė Balakui: “Stovėk čia prie deginamosios aukos, o aš eisiu pasitikti Viešpaties”.",
			"16": "<sup>16</sup> Viešpats sutiko Balaamą ir įdėjo žodį į jo lūpas, sakydamas: “Eik vėl pas Balaką ir pasakyk tai”.",
			"17": "<sup>17</sup> Sugrįžęs jis rado Balaką stovintį prie savo deginamosios aukos kartu su Moabo kunigaikščiais. Balakas klausė jo: “Ką tau kalbėjo Viešpats?”",
			"18": "<sup>18</sup> Balaamas atsakė: “Balakai, klausykis ir išgirsk, Ciporo sūnau!",
			"19": "<sup>19</sup> Dievas ne žmogus, kad meluotų, ir ne žmogaus sūnus, kad pakeistų savo nuomonę. Ar Jis pasakė ir nepadarys? Ar Jis kalbėjo ir neįvykdys?",
			"20": "<sup>20</sup> Man įsakyta laiminti; Jis palaimino, ir aš negaliu to pakeisti.",
			"21": "<sup>21</sup> Jis nerado nedorybės Jokūbe ir neįžiūrėjo neteisybės Izraelyje. Viešpats, jų Dievas, yra su jais, ir karaliaus šauksmas girdimas tarp jų.",
			"22": "<sup>22</sup> Dievas juos išvedė iš Egipto, jų galybė kaip stumbro.",
			"23": "<sup>23</sup> Nėra užkeikimo prieš Jokūbą nei ištarmės prieš Izraelį; ateis laikas, kai apie Izraelį sakys: ‘Štai ką padarė Dievas’.",
			"24": "<sup>24</sup> Tai tauta, kuri pakils kaip liūtė ir atsistos kaip liūtas, neatsiguls, kol nesurys grobio ir neišgers užmuštųjų kraujo”.",
			"25": "<sup>25</sup> Balakas tarė Balaamui: “Tu jų neprakeik, bet ir nelaimink”.",
			"26": "<sup>26</sup> Balaamas atsakė Balakui: “Argi aš tau nesakiau, kad ką man Viešpats lieps, tą turėsiu daryti!”",
			"27": "<sup>27</sup> Ir Balakas tarė Balaamui: “Eime, nuvesiu tave į kitą vietą, gal patiks Dievui, kad iš ten juos prakeiktum”.",
			"28": "<sup>28</sup> Balakas užvedė Balaamą į Peoro kalno viršūnę, kuris yra prie dykumos.",
			"29": "<sup>29</sup> Čia Balaamas sakė Balakui: “Pastatydink man čia septynis aukurus ir paruošk tiek pat veršių ir avinų aukai”.",
			"30": "<sup>30</sup> Balakas padarė, kaip Balaamas įsakė. Jie aukojo po veršį ir po aviną ant kiekvieno aukuro."
		},
		"24": {
			"1": "<sup>1</sup> Balaamas, matydamas, kad Viešpačiui patinka laiminti Izraelį, nebėjo, kaip pirma eidavo ieškoti žyniavimo, bet atgręžė veidą į dykumą",
			"2": "<sup>2</sup> ir, pakėlęs akis, pamatė izraelitus, stovyklaujančius giminėmis savo palapinėse. Dievo dvasia nužengė ant jo,",
			"3": "<sup>3</sup> ir jis kalbėjo: “Kalba žmogus, Beoro sūnus Balaamas, kurio atvertos akys,",
			"4": "<sup>4</sup> kuris girdi Dievo žodžius, mato Visagalio regėjimus ir krinta atvertomis akimis.",
			"5": "<sup>5</sup> Kokios gražios tavo palapinės, Jokūbai, ir tavo buveinės, Izraeli!",
			"6": "<sup>6</sup> Jos yra kaip besitęsią slėniai, kaip sodai paupiuose, kaip Viešpaties sodinti alavijų medžiai, lyg kedrai prie vandens!",
			"7": "<sup>7</sup> Vanduo tekės upėmis, ir jų slėniuose viskas augs. Izraelitų karalius bus žymesnis už Agagą, jo karalystė bus išaukštinta.",
			"8": "<sup>8</sup> Dievas išvedė jį iš Egipto, jo galybė kaip stumbro; jis suvalgys priešų tautas, sulaužys jų kaulus, pervers juos savo strėlėmis.",
			"9": "<sup>9</sup> Jis atsiguls kaip liūtas, kurio niekas nedrįs pažadinti. Kas tave laimina, bus palaimintas; kas keikia, bus prakeiktas”.",
			"10": "<sup>10</sup> Balakas, supykęs ant Balaamo, suplojo rankomis ir tarė: “Aš tave pasišaukiau prakeikti mano priešus, o tu juos tris kartus palaiminai.",
			"11": "<sup>11</sup> Grįžk skubiai į savo kraštą! Maniau tave didžiai pagerbti, bet Viešpats atėmė iš tavęs tau skirtą pagarbą”.",
			"12": "<sup>12</sup> Balaamas atsakė Balakui: “Argi aš nesakiau tavo pasiuntiniams, kuriuos atsiuntei pas mane:",
			"13": "<sup>13</sup> ‘Jei Balakas man duotų pilnus savo namus sidabro ir aukso, aš negaliu peržengti Viešpaties įsakymo ir daryti gera ar bloga savo noru. Kalbėsiu tai, ką Viešpats man įsakys’.",
			"14": "<sup>14</sup> Prieš grįždamas pas savo tautą, paskelbsiu tau, ką ši tauta padarys tavo tautai ateityje”.",
			"15": "<sup>15</sup> Balaamas toliau kalbėjo: “Kalba žmogus, Beoro sūnus Balaamas, kurio akys atvertos,",
			"16": "<sup>16</sup> kuris girdi Dievo žodžius, pažįsta Aukščiausiojo mokslą, mato Visagalio regėjimus ir krinta atvertomis akimis.",
			"17": "<sup>17</sup> Aš jį matysiu, bet ne dabar, į jį žiūrėsiu, bet ne iš arti. Žvaigždė užtekės iš Jokūbo giminės, skeptras pakils Izraelyje, jis užims Moabą ir sunaikins Seto giminę.",
			"18": "<sup>18</sup> Edomas ir Seyras taps priešų nuosavybe, Izraelis parodys savo jėgą.",
			"19": "<sup>19</sup> Jokūbo ainiai viešpataus ir sunaikins priešų likučius”.",
			"20": "<sup>20</sup> Balaamas, pažiūrėjęs į amalekiečius, kalbėjo: “Amalekas yra pirmas tarp tautų, bet jis bus sunaikintas amžiams”.",
			"21": "<sup>21</sup> Pamatęs kainitus, tarė: “Stipri, saugi tavo buveinė, kaip ant uolos sukrautas lizdas.",
			"22": "<sup>22</sup> Bet ir jūs, kainitai, būsite išsklaidyti, jūsų palikuonis Ašūras ištrems.",
			"23": "<sup>23</sup> Ir kas išliks gyvas, kai Dievas visa tai darys?",
			"24": "<sup>24</sup> Laivai atvyks iš Kitimo, pavergs ašūriečius ir sunaikins Ebero kraštą, pagaliau patys žus”.",
			"25": "<sup>25</sup> Po to Balaamas sugrįžo į savo tėviškę; taip pat ir Balakas grįžo tuo keliu, kuriuo buvo atėjęs."
		},
		"25": {
			"1": "<sup>1</sup> Izraelitams gyvenant Šitime, tauta pradėjo paleistuvauti su Moabo dukterimis.",
			"2": "<sup>2</sup> Jos kvietė izraelitus į aukojimo šventes. Ten jie valgė ir lenkėsi prieš jų dievus.",
			"3": "<sup>3</sup> Izraelitai garbino Baal Peorą. Užsirūstinęs Viešpats",
			"4": "<sup>4</sup> tarė Mozei: “Surink visus tautos vadus ir juos pakark saulės kaitroje, kad mano rūstybė nepaliestų Izraelio tautos”.",
			"5": "<sup>5</sup> Mozė įsakė Izraelio teisėjams užmušti visus Baal Peoro garbintojus.",
			"6": "<sup>6</sup> Vienas izraelitas atsivedė midjanietę į savo palapinę Mozei ir visiems izraelitams matant, tuo metu, kai jie raudojo prie Susitikimo palapinės įėjimo.",
			"7": "<sup>7</sup> Tai išvydęs, kunigo Aarono sūnaus Eleazaro sūnus Finehasas pakilo iš susirinkusiųjų ir, pagriebęs ietį,",
			"8": "<sup>8</sup> įėjo į palapinę paskui izraelitą, ir perdūrė juos abu kiaurai­izraelitą ir moterį per jos pilvą. Tada liovėsi maras tarp Izraelio sūnų.",
			"9": "<sup>9</sup> Iš viso nuo maro mirė dvidešimt keturi tūkstančiai žmonių.",
			"10": "<sup>10</sup> Viešpats tarė Mozei:",
			"11": "<sup>11</sup> “Kunigo Aarono sūnaus Eleazaro sūnus Finehasas išgelbėjo izraelitus nuo mano rūstybės; jis buvo uolus dėl manęs, kad Aš nesunaikinčiau izraelitų, apimtas pavydo.",
			"12": "<sup>12</sup> Todėl sakyk jam, kad Aš darau su juo taikos sandorą:",
			"13": "<sup>13</sup> jam ir jo palikuonims priklausys kunigystė kaip amžina sandora, nes jis buvo uolus dėl Dievo ir sutaikino izraelitus”.",
			"14": "<sup>14</sup> Izraelitas, kurį nužudė kartu su midjaniete, buvo Saluvo sūnus Zimris, Simeono giminės kunigaikštis,",
			"15": "<sup>15</sup> o nužudytoji midjanietė, vardu Kozbė, buvo Midjano giminės kunigaikščio Cūro duktė.",
			"16": "<sup>16</sup> Viešpats kalbėjo Mozei:",
			"17": "<sup>17</sup> “Išžudykite midjaniečius,",
			"18": "<sup>18</sup> nes jie pasielgė su jumis klastingai, suvedžiodami jus Baalo garbinimu ir midjaniečių kunigaikščio dukterimi Kozbe, savo seserimi, kuri buvo nužudyta dėl Peoro maro dieną”."
		},
		"26": {
			"1": "<sup>1</sup> Viešpats tarė Mozei ir Aarono sūnui kunigui Eleazarui:",
			"2": "<sup>2</sup> “Suskaičiuokite visus izraelitus vyrus, dvidešimties metų ir vyresnius pagal jų gimines, tinkamus kariuomenei”.",
			"3": "<sup>3</sup> Mozė ir kunigas Eleazaras Moabo lygumoje prie Jordano, ties Jerichu, kalbėjo tiems, kurie buvo",
			"4": "<sup>4</sup> dvidešimties metų ir vyresni, kaip Viešpats buvo liepęs. Iš Egipto išėjusieji buvo:",
			"5": "<sup>5</sup> Rubenas, Izraelio pirmagimis. Jo sūnūs buvo Henochas, iš kurio kilo henochai, Paluvas, iš kurio­paluvai,",
			"6": "<sup>6</sup> Hecronas, iš jo­hecronai ir Karmis, iš jo­karmiai.",
			"7": "<sup>7</sup> Rubeno giminės buvo keturiasdešimt trys tūkstančiai septyni šimtai trisdešimt.",
			"8": "<sup>8</sup> Paluvo sūnus buvo Eliabas;",
			"9": "<sup>9</sup> jo sūnūs Nemuelis, Datanas ir Abiramas; Datanas ir Abiramas prisidėjo prie Koracho maišto, kuris buvo sukeltas prieš Mozę ir Aaroną; tai buvo maištas prieš Viešpatį.",
			"10": "<sup>10</sup> Atvėrusi žemė prarijo juos kartu su Korachu. Du šimtai penkiasdešimt žmonių buvo sunaikinti ugnimi. Tai buvo ženklas izraelitams.",
			"11": "<sup>11</sup> Tačiau Koracho sūnūs nežuvo.",
			"12": "<sup>12</sup> Simeono sūnų šeimos: Nemuelio­nemuelitai, Jamino­jaminai, Jachino­jachinai,",
			"13": "<sup>13</sup> Zeracho­zerachai, Sauliaus­ sauliai.",
			"14": "<sup>14</sup> Simeono giminės buvo dvidešimt du tūkstančiai du šimtai.",
			"15": "<sup>15</sup> Gado sūnų šeimos: Cefono, iš jo­cefonai, Hagio­hagiai, Šūnio­šūniai,",
			"16": "<sup>16</sup> Oznio­ozniai, Erio­eriai,",
			"17": "<sup>17</sup> Arodo­arodai, Arelio­arelitai.",
			"18": "<sup>18</sup> Gado giminės buvo keturiasdešimt tūkstančių penki šimtai.",
			"19": "<sup>19</sup> Judo sūnūs buvo Eras ir Onanas. Jie mirė Kanaano žemėje.",
			"20": "<sup>20</sup> Kitų Judo sūnų šeimos: Šelos­šelai, Pereco­perecai, Zeracho­zerachai.",
			"21": "<sup>21</sup> Pereco giminės sūnų: Esromo­ esromitai ir Hamulo­hamulai.",
			"22": "<sup>22</sup> Judo giminės buvo septyniasdešimt šeši tūkstančiai penki šimtai.",
			"23": "<sup>23</sup> Isacharo sūnų: Tolos­tolai, Pūvos­pūvai,",
			"24": "<sup>24</sup> Jašubo­jašubai, Šimrono­šimronai.",
			"25": "<sup>25</sup> Isacharo giminės buvo šešiasdešimt keturi tūkstančiai trys šimtai.",
			"26": "<sup>26</sup> Zabulono sūnų: Seredo­seredai, Elono­elonai, Jachleelio­ jachlelitai.",
			"27": "<sup>27</sup> Zabulono giminės buvo šešiasdešimt tūkstančių penki šimtai.",
			"28": "<sup>28</sup> Juozapo sūnūs Manasas ir Efraimas pagal savo šeimas:",
			"29": "<sup>29</sup> Manasui gimė Machyras, iš kurio kilo machyrai, iš Machyro sūnaus Gileado­gileadiečiai.",
			"30": "<sup>30</sup> Šitie yra Gileado sūnūs, iš kurių kilo: iš Jezero­jezerai, iš Heleko­helekai,",
			"31": "<sup>31</sup> iš Asrielio­asrielitai, iš Šechemo­šechemai,",
			"32": "<sup>32</sup> iš Šemidos­šemidai ir iš Hefero­heferai.",
			"33": "<sup>33</sup> O Hefero sūnus Celofhadas neturėjo sūnų, tik dukteris, kurių vardai yra: Machla, Noja, Hogla, Milka ir Tirca.",
			"34": "<sup>34</sup> Manaso giminės buvo penkiasdešimt du tūkstančiai septyni šimtai.",
			"35": "<sup>35</sup> Efraimo sūnų šeimos: Šutelacho­šutelachai, Bechero­becherai, Tahano­tahanai.",
			"36": "<sup>36</sup> Šutelacho sūnaus Erano­eranai.",
			"37": "<sup>37</sup> Efraimo giminės buvo trisdešimt du tūkstančiai penki šimtai. Šitos yra Juozapo giminės šeimos.",
			"38": "<sup>38</sup> Benjamino sūnų šeimos: Belos­belai, Ašbelio­ašbelitai, Ahiramo­ahiramai,",
			"39": "<sup>39</sup> Šefufamo­šefufamai, Hufamo­hufamai.",
			"40": "<sup>40</sup> Belos sūnūs buvo Ardas ir Naamanas. Iš Ardo­ardai, iš Naamano­naamanai.",
			"41": "<sup>41</sup> Benjamino giminės buvo keturiasdešimt penki tūkstančiai šeši šimtai.",
			"42": "<sup>42</sup> Dano sūnų šeimos: Šuhamo­ šuhamai. Tai Dano giminės šeimos.",
			"43": "<sup>43</sup> Šuhamų buvo šešiasdešimt keturi tūkstančiai keturi šimtai.",
			"44": "<sup>44</sup> Ašero sūnų šeimos: Imnos­imnai, Išvio­išviai, Berijos­berijai.",
			"45": "<sup>45</sup> Iš Berijos sūnų: Hebero­heberai ir Malkielio­malkielitai.",
			"46": "<sup>46</sup> Ašero dukters vardas buvo Seracha.",
			"47": "<sup>47</sup> Ašero giminės buvo penkiasdešimt trys tūkstančiai keturi šimtai.",
			"48": "<sup>48</sup> Neftalio sūnų šeimos: Jachceelio­jachceelitai, Gūnio­gūniai,",
			"49": "<sup>49</sup> Jecero­jecerai, Šilemo­šilemai.",
			"50": "<sup>50</sup> Neftalio giminės buvo keturiasdešimt penki tūkstančiai keturi šimtai.",
			"51": "<sup>51</sup> Izraelitų, kurie buvo surašyti, buvo šeši šimtai vienas tūkstantis septyni šimtai trisdešimt.",
			"52": "<sup>52</sup> Viešpats kalbėjo Mozei:",
			"53": "<sup>53</sup> “Jiems bus išdalinta žemė paveldėjimui pagal vardų skaičių.",
			"54": "<sup>54</sup> Didesnėms šeimoms duosi didesnę dalį, o mažesnėms­mažesnę, kiekvienam bus duotas paveldėjimas pagal tuos, kurie buvo suskaičiuoti.",
			"55": "<sup>55</sup> Žemė bus padalyta tarp giminių burtų keliu.",
			"56": "<sup>56</sup> Burtų keliu bus padalinta nuosavybė tiems, kurių yra daug, ir tiems, kurių yra mažai.",
			"57": "<sup>57</sup> Levio giminės sūnų šeimos: Geršono­geršonų šeima, Kehato­kehatų šeima, Merario­merarių šeima.",
			"58": "<sup>58</sup> Šitos yra Levio šeimos: libniai, hebronai, machliai, mušiai ir koriai. Kehato sūnaus Amramo",
			"59": "<sup>59</sup> žmona, Levio duktė Jochebeda, gimusi Egipte, pagimdė Aaroną ir Mozę bei jų seserį Mirjamą.",
			"60": "<sup>60</sup> Aarono sūnūs buvo: Nadabas, Abihuvas, Eleazaras ir Itamaras.",
			"61": "<sup>61</sup> Nadabas ir Abihuvas mirė, aukodami svetimą ugnį Viešpačiui.",
			"62": "<sup>62</sup> Visų Levio giminės suskaitytų vyrų, vieno mėnesio amžiaus ir vyresnių, buvo dvidešimt trys tūkstančiai. Jie nebuvo suskaityti kartu su izraelitais, nes jiems nebuvo duota dalis tarp Izraelio vaikų.",
			"63": "<sup>63</sup> Tai izraelitai, kuriuos suskaičiavo Mozė ir kunigas Eleazaras Moabo lygumoje prie Jordano, ties Jerichu.",
			"64": "<sup>64</sup> Tarp jų nebuvo nė vieno iš tų, kurie buvo anksčiau Mozės ir Aarono suskaičiuoti Sinajaus dykumoje,",
			"65": "<sup>65</sup> nes Viešpats buvo pasakęs, kad jie visi mirs dykumoje. Nė vienas neišliko, išskyrus Jefunės sūnų Kalebą ir Nūno sūnų Jozuę."
		},
		"27": {
			"1": "<sup>1</sup> Machla, Noja, Hogla, Milka ir Tirca, dukterys Celofhado, sūnaus Hefero, sūnaus Gileado, sūnaus Machyro, sūnaus Manaso, sūnaus Juozapo, atėjo",
			"2": "<sup>2</sup> prie Susitikimo palapinės įėjimo, kur stovėjo Mozė, kunigas Eleazaras ir visi tautos kunigaikščiai, ir tarė:",
			"3": "<sup>3</sup> “Mūsų tėvas mirė dykumoje. Jis nedalyvavo maište, kuris buvo Koracho sukeltas prieš Viešpatį, bet mirė už savo nusikaltimus. Jis neturėjo sūnų.",
			"4": "<sup>4</sup> Kodėl jo vardas turėtų išnykti iš jo šeimos? Ar dėl to, kad jis neturėjo sūnaus? Duokite mums paveldėjimą tarp mūsų tėvo brolių”.",
			"5": "<sup>5</sup> Mozė kreipėsi tuo reikalu į Viešpatį.",
			"6": "<sup>6</sup> Viešpats atsakė Mozei:",
			"7": "<sup>7</sup> “Celofhado dukterys kalba teisingai. Duok joms paveldėjimą tarp jų tėvo brolių, tepaveldi jos jo nuosavybę.",
			"8": "<sup>8</sup> Izraelio sūnums įsakyk: ‘Jei kas miršta nepalikdamas sūnaus, jo nuosavybę tepaveldi jo duktė.",
			"9": "<sup>9</sup> Jei neturės dukters, įpėdiniais bus mirusiojo broliai.",
			"10": "<sup>10</sup> O jei nebus nė brolių, jo nuosavybę paveldės jo tėvo broliai.",
			"11": "<sup>11</sup> Jeigu neturės nė tėvo brolių, paveldas atiteks tiems, kurie yra jo artimiausi giminės’ ”. Tai amžinas įstatymas izraelitams, Viešpaties duotas Mozei.",
			"12": "<sup>12</sup> Viešpats kalbėjo Mozei: “Užlipk į Abarimo kalną ir iš ten pažiūrėk į kraštą, kurį duosiu izraelitams.",
			"13": "<sup>13</sup> Jį pamatęs, tu susijungsi su savo tauta, kaip susijungė tavo brolis Aaronas,",
			"14": "<sup>14</sup> nes judu nepaklausėte manęs Cino dykumoje tautos prieštaravimo metu, kad būtų parodytas mano šventumas jų akivaizdoje prie Meribos vandenų šaltinio, Cino dykumoje Kadeše”.",
			"15": "<sup>15</sup> Mozė atsakė Viešpačiui:",
			"16": "<sup>16</sup> “Viešpatie, visų gyvųjų Dieve, paskirk žmogų šitai tautai vadovauti,",
			"17": "<sup>17</sup> kuris eitų su jais, juos išvestų ir įvestų, kad Viešpaties susirinkimas nebūtų kaip avys be piemens”.",
			"18": "<sup>18</sup> Viešpats atsakė Mozei: “Imk Nūno sūnų Jozuę, kuriame yra mano dvasia, ir ant jo uždėk savo ranką.",
			"19": "<sup>19</sup> Jis teatsistoja kunigo Eleazaro ir visos tautos akivaizdoje, o tu duok jam paliepimus",
			"20": "<sup>20</sup> ir suteik jam savo garbės, kad jo klausytų visi izraelitai.",
			"21": "<sup>21</sup> Tuo atveju, kai reikės pagalbos, tegul kunigas Eleazaras kreipiasi į Viešpatį patarimo. Tuo būdu Jozuė galės vadovauti izraelitams”.",
			"22": "<sup>22</sup> Mozė padarė, kaip Viešpats buvo įsakęs: pastatė Jozuę kunigo Eleazaro ir visos tautos akivaizdoje,",
			"23": "<sup>23</sup> uždėjo jam ant galvos rankas ir suteikė jam valdžią, kaip Viešpats liepė."
		},
		"28": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Įsakyk izraelitams skirtu laiku aukoti duonos ir deginamąsias aukas kaip malonų kvapą.",
			"3": "<sup>3</sup> Šitos aukos bus: kasdien du sveiki metiniai avinėliai nuolatinei deginamajai aukai;",
			"4": "<sup>4</sup> vieną aukosite rytą, o antrą­vakare;",
			"5": "<sup>5</sup> ir duonos aukai dešimtą efos dalį smulkių miltų, sumaišytų su ketvirtadaliu hino tyriausio aliejaus.",
			"6": "<sup>6</sup> Tai bus nuolatinė deginamoji auka, kaip buvo įsakyta Sinajaus kalne, malonus kvapas Viešpačiui.",
			"7": "<sup>7</sup> Ir geriamoji auka bus ketvirtadalis hino vyno; šventykloje išliesite Viešpačiui vyną kaip geriamąją auką.",
			"8": "<sup>8</sup> Antrąjį avinėlį aukosite vakare, kaip ir rytmetinę auką, su duonos ir geriamąja auka, kad būtų malonus kvapas.",
			"9": "<sup>9</sup> Sabato dieną aukosite du sveikus metinius avinėlius, o duonos aukai­ dvi dešimtąsias efos smulkių miltų, sumaišytų su aliejumi, taip pat ir geriamąją auką.",
			"10": "<sup>10</sup> Tai bus nuolatinė sabato deginamoji auka, priedas prie kasdieninių deginamųjų ir geriamųjų aukų.",
			"11": "<sup>11</sup> Kiekvieno mėnesio pradžioje privalote aukoti deginamąją auką Viešpačiui: du sveikus veršius, vieną aviną ir septynis metinius avinėlius.",
			"12": "<sup>12</sup> Duonos aukai: prie kiekvieno veršio tris dešimtąsias efos smulkių miltų, sumaišytų su aliejumi; prie kiekvieno avino dvi dešimtąsias efos smulkių miltų, sumaišytų su aliejumi,",
			"13": "<sup>13</sup> ir dešimtą dalį smulkių miltų, sumaišytų su aliejumi, duonos aukai prie kiekvieno avinėlio. Tai bus malonaus kvapo deginamoji auka Viešpačiui.",
			"14": "<sup>14</sup> Geriamosios vyno aukos prie kiekvienos deginamosios aukos bus: pusė hino prie kiekvieno veršio, trečdalis­ prie avino, ketvirtadalis­prie kiekvieno avinėlio. Tai kiekvieno mėnesio geriamoji auka.",
			"15": "<sup>15</sup> Taip pat aukosite Viešpačiui ožį aukai už nuodėmę, neskaičiuojant nuolatinių deginamųjų ir geriamųjų aukų.",
			"16": "<sup>16</sup> Pirmo mėnesio keturioliktą dieną yra Viešpaties Pascha,",
			"17": "<sup>17</sup> o penkioliktą dieną­iškilmės. Neraugintą duoną valgysite septynias dienas.",
			"18": "<sup>18</sup> Pirmoji diena yra izraelitams šventa diena­tą dieną nedirbsite jokio darbo,",
			"19": "<sup>19</sup> aukosite deginamajai aukai du sveikus veršius, vieną aviną, septynis metinius avinėlius",
			"20": "<sup>20</sup> ir duonos aukai smulkių miltų, sumaišytų su aliejumi, tris dešimtąsias efos prie veršio, dvi dešimtąsias efos prie avino",
			"21": "<sup>21</sup> ir vieną dešimtąją efos prie kiekvieno avinėlio,",
			"22": "<sup>22</sup> taip pat vieną ožį aukai už nuodėmę, kad būtumėte sutaikinti,",
			"23": "<sup>23</sup> neskaičiuojant rytmetinės deginamosios aukos, kuri nuolat aukojama.",
			"24": "<sup>24</sup> Taip darykite septynias dienas, kad būtų malonus kvapas Viešpačiui.",
			"25": "<sup>25</sup> Septintoji diena bus jums šventa: tą dieną nedirbkite jokio darbo.",
			"26": "<sup>26</sup> Pirmųjų vaisių šventės dieną aukosite Viešpačiui naujo derliaus aukas. Ta diena bus šventa ir iškilminga, tada nedirbsite jokio darbo.",
			"27": "<sup>27</sup> Deginamajai aukai aukosite du veršius, vieną aviną bei septynis metinius avinėlius;",
			"28": "<sup>28</sup> duonos aukai­su aliejumi sumaišytų smulkių miltų tris dešimtąsias efos prie kiekvieno veršio, dvi­prie avino",
			"29": "<sup>29</sup> ir dešimtą dalį­prie kiekvieno avinėlio;",
			"30": "<sup>30</sup> taip pat ožį, kuris aukojamas sutaikinimui.",
			"31": "<sup>31</sup> Šias aukas aukosite neskaitant nuolatinių deginamųjų aukų ir kartu su jomis aukojamų duonos ir geriamųjų aukų”."
		},
		"29": {
			"1": "<sup>1</sup> “Pirmoji septinto mėnesio diena bus jums iškilminga ir šventa. Tą dieną nedirbsite jokio darbo­tai bus trimitavimo diena.",
			"2": "<sup>2</sup> Aukosite deginamajai aukai, kaip malonų kvapą Viešpačiui, sveikus gyvulius: vieną veršį, vieną aviną ir septynis metinius avinėlius;",
			"3": "<sup>3</sup> duonos aukai­tris dešimtąsias efos smulkių miltų, sumaišytų su aliejumi, prie kiekvieno veršio, dvi dešimtąsias efos prie avino,",
			"4": "<sup>4</sup> vieną dešimtą dalį efos prie kiekvieno avinėlio",
			"5": "<sup>5</sup> ir ožį aukai už nuodėmę, kad būtumėte sutaikinti,",
			"6": "<sup>6</sup> neskaičiuojant mėnesinės deginamosios bei duonos aukos ir kasdieninės deginamosios, duonos ir geriamosios aukos, kurias aukosite pagal jų nuostatus, kad būtų malonus kvapas Viešpačiui.",
			"7": "<sup>7</sup> Taip pat septinto mėnesio dešimtoji diena bus jums šventa ir iškilminga. Tą dieną varginsite savo sielas ir nedirbsite jokio darbo.",
			"8": "<sup>8</sup> Deginamajai Viešpaties aukai kaip malonų kvapą aukosite sveikus gyvulius: jauną veršį, aviną ir septynis metinius avinėlius;",
			"9": "<sup>9</sup> jų duonos aukai tris dešimtąsias efos smulkių miltų, sumaišytų su aliejumi, prie kiekvieno veršio, dvi dešimtąsias efos prie kiekvieno avino,",
			"10": "<sup>10</sup> dešimtą dalį efos prie kiekvieno avinėlio",
			"11": "<sup>11</sup> ir ožį aukai už nuodėmę, neskaitant aukos už nuodėmę sutaikinimui ir nuolatinės deginamosios aukos su duonos ir geriamosiomis aukomis.",
			"12": "<sup>12</sup> Septinto mėnesio penkioliktoji diena bus jums šventa ir iškilminga, nedirbsite tą dieną jokio darbo. Septynias dienas švęsite Viešpaties garbei.",
			"13": "<sup>13</sup> Aukosite deginamąją auką kaip malonų kvapą Viešpačiui: trylika sveikų jaunų veršių, du avinus, keturiolika metinių avinėlių;",
			"14": "<sup>14</sup> taip pat duonos auką: smulkių, su aliejumi sumaišytų miltų po tris dešimtąsias efos prie kiekvieno veršio, kurių bus trylika, po dvi dešimtąsias efos prie kiekvieno avino, jų bus du,",
			"15": "<sup>15</sup> dešimtą dalį efos prie kiekvieno avinėlio, kurių bus keturiolika,",
			"16": "<sup>16</sup> ir ožį aukai už nuodėmę, be to, nuolatines deginamąsias, duonos ir geriamąsias aukas.",
			"17": "<sup>17</sup> Antrą dieną aukosite: dvylika jaunų veršių, du avinus, keturiolika metinių avinėlių.",
			"18": "<sup>18</sup> Prie kiekvieno veršio, avino ir avinėlio aukosite jų duonos ir geriamąją auką, kaip įsakyta,",
			"19": "<sup>19</sup> ir ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos ir geriamosios aukos.",
			"20": "<sup>20</sup> Trečią dieną aukosite: vienuolika veršių, du avinus, keturiolika metinių avinėlių",
			"21": "<sup>21</sup> ir, kaip įsakyta, prie kiekvieno veršio, avino ir avinėlio duonos ir geriamąsias aukas.",
			"22": "<sup>22</sup> ir ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos ir geriamosios aukos.",
			"23": "<sup>23</sup> Ketvirtą dieną aukosite: dešimt veršių, du avinus, keturiolika metinių avinėlių",
			"24": "<sup>24</sup> ir prie kiekvieno jų duonos bei geriamąsias aukas, kaip įsakyta,",
			"25": "<sup>25</sup> ir ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos bei geriamosios aukų.",
			"26": "<sup>26</sup> Penktą dieną aukosite: devynis veršius, du avinus, keturiolika metinių avinėlių,",
			"27": "<sup>27</sup> prie kiekvieno jų duonos bei geriamąsias aukas, kaip įsakyta,",
			"28": "<sup>28</sup> ir ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos bei geriamosios aukų.",
			"29": "<sup>29</sup> Šeštą dieną aukosite: aštuonis veršius, du avinus ir keturiolika metinių avinėlių,",
			"30": "<sup>30</sup> prie kiekvieno jų duonos bei geriamąsias aukas, kaip įsakyta,",
			"31": "<sup>31</sup> ir ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos bei geriamosios aukų.",
			"32": "<sup>32</sup> Septintą dieną aukosite: septynis veršius, du avinus ir keturiolika metinių avinėlių,",
			"33": "<sup>33</sup> prie kiekvieno jų duonos ir geriamąsias aukas, kaip įsakyta,",
			"34": "<sup>34</sup> bei ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos ir geriamosios aukų.",
			"35": "<sup>35</sup> Aštunta diena yra iškilminga­nedirbsite tą dieną jokio darbo",
			"36": "<sup>36</sup> ir aukosite deginamąją auką kaip malonų kvapą Viešpačiui: veršį, aviną, septynis metinius avinėlius,",
			"37": "<sup>37</sup> prie kiekvieno jų duonos bei geriamąsias aukas, kaip įsakyta,",
			"38": "<sup>38</sup> ir ožį aukai už nuodėmę, neskaičiuojant nuolatinės deginamosios, duonos ir geriamosios aukų. Visi aukojamieji gyvuliai turi būti sveiki.",
			"39": "<sup>39</sup> Tai aukosite švenčių metu kaip priedą prie jūsų įžadų ir laisvos valios aukų, o taip pat deginamųjų, duonos, geriamųjų bei padėkos aukų”.",
			"40": "<sup>40</sup> Mozė perdavė izraelitams visa, ką jam Viešpats kalbėjo."
		},
		"30": {
			"1": "<sup>1</sup> Ir Mozė kalbėjo izraelitų giminių vadams: “Štai ką Viešpats įsakė:",
			"2": "<sup>2</sup> ‘Jei kuris vyras duoda Viešpačiui įžadą ar pasižada su priesaika, negalės savo žodžio laužyti, turės įvykdyti, ką pažadėjo.",
			"3": "<sup>3</sup> Jei įžadą Viešpačiui duotų ir save priesaika suvaržytų jauna moteris, kuri gyvena savo tėvo namuose,",
			"4": "<sup>4</sup> ir jei tėvas, sužinojęs apie įžadą ir priesaiką, tylėtų, ji turi laikytis įžado ir privalo įvykdyti, ką pažadėjo ir prisiekė.",
			"5": "<sup>5</sup> O jei tėvas išgirdęs uždraustų tą pačią dieną, jos įžadas ir priesaika bus panaikinta, nes tėvas uždraudė, ir Viešpats jai atleis.",
			"6": "<sup>6</sup> Jei ištekėtų, davusi įžadą ar priesaiką,",
			"7": "<sup>7</sup> ir jos vyras sužinojęs neuždraustų jai to daryti tą pačią dieną, ji laikysis įžado ir jį įvykdys.",
			"8": "<sup>8</sup> O jei jis išgirdęs tuojau uždraustų ir tuo būdu panaikintų jos įžadus, Viešpats jai atleis.",
			"9": "<sup>9</sup> Našlė arba išsiskyrusi privalo įvykdyti, ką pažadėjo.",
			"10": "<sup>10</sup> Jei žmona, gyvendama su vyru, padarytų įžadą arba prisiektų",
			"11": "<sup>11</sup> ir jei jos vyras išgirdęs tylėtų ir neprieštarautų, ji vykdys savo įžadą.",
			"12": "<sup>12</sup> O jei išgirdęs tuojau uždraustų, nereikės vykdyti pažado, nes vyras uždraudė, ir Viešpats jai atleis.",
			"13": "<sup>13</sup> Kiekvieną jos įžadą ir kiekvieną priesaiką varginti savo sielą vyras gali patvirtinti arba panaikinti.",
			"14": "<sup>14</sup> O jei vyras išgirdęs tylėtų ir atidėtų sprendimą kitai dienai, ji vykdys, ką pažadėjo, nes jis tylėdamas patvirtino jos įžadus.",
			"15": "<sup>15</sup> Jei žinodamas vėliau prieštarautų, jis bus atsakingas už jos kaltę’ ”.",
			"16": "<sup>16</sup> Tai yra Viešpaties duoti Mozei įsakymai, kurių turi laikytis vyras ir žmona, tėvas ir duktė, kuri yra netekėjusi ir gyvena tėvo namuose."
		},
		"31": {
			"1": "<sup>1</sup> Viešpats tarė Mozei:",
			"2": "<sup>2</sup> “Atkeršyk midjaniečiams už izraelitams padarytas skriaudas ir tada susijungsi su savo tauta”.",
			"3": "<sup>3</sup> Mozė įsakė apginkluoti vyrus, kurie vykdys Viešpaties kerštą midjaniečiams,",
			"4": "<sup>4</sup> ir pasiųsti po tūkstantį vyrų iš kiekvienos giminės.",
			"5": "<sup>5</sup> Buvo atskirta po tūkstantį vyrų iš kiekvienos giminės, iš viso dvylika tūkstančių, pasirengusių kovai.",
			"6": "<sup>6</sup> Mozė juos išsiuntė su kunigo Eleazaro sūnumi Finehasu, kuriam pavedė šventus daiktus ir trimitus.",
			"7": "<sup>7</sup> Jie nugalėjo midjaniečius ir išžudė visus jų vyrus, kaip Viešpats buvo įsakęs Mozei,",
			"8": "<sup>8</sup> taip pat ir jų karalius: Evį, Rekemą, Cūrą, Hūrą ir Rebą. Užmušė kardu ir Beoro sūnų Balaamą.",
			"9": "<sup>9</sup> Jie paėmė į nelaisvę jų moteris, vaikus, visus galvijus ir visą midjaniečių lobį,",
			"10": "<sup>10</sup> sudegino jų miestus ir stovyklas.",
			"11": "<sup>11</sup> Belaisvius, gyvulius ir visą karo grobį",
			"12": "<sup>12</sup> izraelitai atgabeno pas Mozę ir kunigą Eleazarą į izraelitų stovyklą Moabo lygumose prie Jordano, ties Jerichu.",
			"13": "<sup>13</sup> Mozė, kunigas Eleazaras ir visi izraelitų kunigaikščiai išėjo jų pasitikti už stovyklos.",
			"14": "<sup>14</sup> Užsirūstinęs ant kariuomenės vadų, tūkstantininkų ir šimtininkų, kurie grįžo iš kovos lauko,",
			"15": "<sup>15</sup> Mozė tarė: “Kodėl palikote gyvas moteris?",
			"16": "<sup>16</sup> Argi ne jos suvedžiojo izraelitus, patariant Balaamui, nusikalsti Viešpačiui Baal Peore, dėl ko tauta buvo nubausta maru.",
			"17": "<sup>17</sup> Nužudykite visus berniukus bei moteris, kurios gyveno su vyrais!",
			"18": "<sup>18</sup> Mergaites, kurios nėra pažinusios vyro, pasilaikykite sau.",
			"19": "<sup>19</sup> Pasilikite už stovyklos septynias dienas. Kas užmušė žmogų ar užmuštąjį palietė, apsivalykite patys ir jūsų belaisviai trečią ir septintą dieną.",
			"20": "<sup>20</sup> Reikia apvalyti ir visą grobį: ar tai būtų drabužiai, ar indai, ar bet koks daiktas, padarytas iš odos, ožkų vilnų ar iš medžio”.",
			"21": "<sup>21</sup> Kunigas Eleazaras kalbėjo kovoje dalyvavusiems kariams: “Štai Viešpaties nurodymas:",
			"22": "<sup>22</sup> auksą, sidabrą, varį, geležį, šviną, ciną",
			"23": "<sup>23</sup> ir visa, kas nedega liepsnoje, apvalykite ugnimi ir apvalomuoju vandeniu, kas dega ugnyje, apvalykite vandeniu.",
			"24": "<sup>24</sup> Septintą dieną išplaukite savo drabužius ir, taip apsivalę, įeikite į stovyklą”.",
			"25": "<sup>25</sup> Viešpats kalbėjo Mozei:",
			"26": "<sup>26</sup> “Tu, kunigas Eleazaras ir tautos vyresnieji suskaičiuokite visą grobį­ žmones ir galvijus.",
			"27": "<sup>27</sup> Padalykite grobį į dvi lygias dalis kovojusiems ir kitiems izraelitams.",
			"28": "<sup>28</sup> Iš tų, kurie kovojo kare ir gavo karo grobio, atskirk Viešpaties daliai po vieną iš penkių šimtų žmonių, taip pat iš galvijų, asilų ir avių;",
			"29": "<sup>29</sup> viską atiduok kunigui Eleazarui, nes tai yra auka Viešpačiui.",
			"30": "<sup>30</sup> O iš nekariavusių izraelitų grobio imk kiekvieną penkiasdešimtą žmogų, taip pat galviją, asilą, avį ir kitų gyvulių ir atiduok levitams, kurie tarnauja Viešpačiui”.",
			"31": "<sup>31</sup> Mozė ir Eleazaras padarė, kaip Viešpats įsakė.",
			"32": "<sup>32</sup> Karo grobis buvo: šeši šimtai septyniasdešimt penki tūkstančiai avių,",
			"33": "<sup>33</sup> septyniasdešimt du tūkstančiai galvijų,",
			"34": "<sup>34</sup> šešiasdešimt vienas tūkstantis asilų,",
			"35": "<sup>35</sup> trisdešimt du tūkstančiai nekaltų mergaičių.",
			"36": "<sup>36</sup> Pusė buvo atiduota kare dalyvavusiems: trys šimtai trisdešimt septyni tūkstančiai penki šimtai avių;",
			"37": "<sup>37</sup> iš jų Viešpačiui atskirta šeši šimtai septyniasdešimt penkios avys.",
			"38": "<sup>38</sup> Trisdešimt šeši tūkstančiai galvijų; iš jų Viešpačiui atskirta septyniasdešimt du;",
			"39": "<sup>39</sup> trisdešimt tūkstančių penki šimtai asilų, iš kurių atskirta Viešpačiui šešiasdešimt vienas asilas.",
			"40": "<sup>40</sup> Šešiolika tūkstančių žmonių, iš kurių Viešpačiui teko trisdešimt du.",
			"41": "<sup>41</sup> Mozė atidavė, kaip buvo liepta, Viešpaties aukos dalį Eleazarui.",
			"42": "<sup>42</sup> Izraelitams, nedalyvavusiems kare, buvo duota:",
			"43": "<sup>43</sup> trys šimtai trisdešimt septyni tūkstančiai penki šimtai avių,",
			"44": "<sup>44</sup> trisdešimt šeši tūkstančiai galvijų,",
			"45": "<sup>45</sup> trisdešimt tūkstančių penki šimtai asilų,",
			"46": "<sup>46</sup> šešiolika tūkstančių belaisvių.",
			"47": "<sup>47</sup> Mozė ėmė kas penkiasdešimtą gyvulį ir belaisvį ir atidavė levitams, tarnaujantiems prie Viešpaties palapinės, kaip Viešpats buvo įsakęs.",
			"48": "<sup>48</sup> Kariuomenės vadai, tūkstantininkai ir šimtininkai, priėję prie Mozės, kalbėjo:",
			"49": "<sup>49</sup> “Mes, tavo tarnai, suskaičiavome kareivius, kuriuos turime savo žinioje, ir nė vieno nepasigedome.",
			"50": "<sup>50</sup> Todėl kiekvienas atnešame kaip dovaną Viešpačiui visus auksinius papuošalus, kuriuos paėmėme karo grobiu: grandinėles, apyrankes, žiedus, auskarus ir kaklo papuošalus, kad sutaikintum mus su Viešpačiu”.",
			"51": "<sup>51</sup> Mozė ir kunigas Eleazaras priėmė auką Viešpačiui iš tūkstantininkų ir šimtininkų, visus atneštus auksinius papuošalus,",
			"52": "<sup>52</sup> kurie svėrė šešiolika tūkstančių septynis šimtus penkiasdešimt šekelių.",
			"53": "<sup>53</sup> Eiliniai kareiviai grobį pasilaikė sau.",
			"54": "<sup>54</sup> Paimtą auksą iš tūkstantininkų ir šimtininkų Mozė ir kunigas Eleazaras įnešė į Susitikimo palapinę, kad Viešpats atmintų izraelitus."
		},
		"32": {
			"1": "<sup>1</sup> Rubeno ir Gado giminės turėjo daug gyvulių. Jie matė Jazero ir Gileado žemes, tinkamas gyvuliams auginti.",
			"2": "<sup>2</sup> Atėję pas Mozę, kunigą Eleazarą ir izraelitų kunigaikščius, jie tarė:",
			"3": "<sup>3</sup> “Ataroto, Dibono, Jazero, Nimros, Hešbono, Elealės, Sebamo, Nebojo ir Beono žemės,",
			"4": "<sup>4</sup> kurios, Viešpačiui padedant, buvo užimtos izraelitų, labai tinka gyvuliams auginti, o mes, tavo tarnai, turime gyvulių.",
			"5": "<sup>5</sup> Taigi jei radome malonę tavo akyse, prašome, kad mums, tavo tarnams, atiduotum ją nuosavybėn ir nevestum mūsų per Jordaną”.",
			"6": "<sup>6</sup> Mozė jiems atsakė: “Argi, kai jūsų broliai kariaus, jūs čia sėdėsite?",
			"7": "<sup>7</sup> Kodėl atkalbinėjate izraelitus, kad jie neitų į žemę, kurią Viešpats jiems atidavė?",
			"8": "<sup>8</sup> Taip elgėsi jūsų tėvai, kai siunčiau iš Kadeš Barnėjos apžiūrėti kraštą.",
			"9": "<sup>9</sup> Jie, nuėję iki Eškolo slėnio ir apžiūrėję visą kraštą, įbaugino izraelitus, kad neitų į šalį, kurią Viešpats jiems pažadėjo.",
			"10": "<sup>10</sup> Tada Viešpats užsirūstinęs prisiekė:",
			"11": "<sup>11</sup> ‘Šitie žmonės, kurie išėjo iš Egipto, dvidešimties metų ir vyresni, neišvys žemės, kurią pažadėjau Abraomui, Izaokui ir Jokūbui, nes jie nesekė manimi iki galo,",
			"12": "<sup>12</sup> išskyrus kenazą Jefunės sūnų Kalebą ir Nūno sūnų Jozuę, kurie iki galo sekė Viešpačiu’.",
			"13": "<sup>13</sup> Viešpats, užsirūstinęs ant izraelitų, leido jiems klaidžioti dykumoje, kol išmirė visa karta, kuri buvo nusikaltusi Viešpačiui.",
			"14": "<sup>14</sup> Dabar jūs stojate savo tėvų vieton, kad dar labiau padidintumėte Viešpaties įtūžimą prieš Izraelį.",
			"15": "<sup>15</sup> Jei nenorite Jo klausyti, Jis vėl paliks tautą dykumoje, ir jūs pražudysite visą šią tautą”.",
			"16": "<sup>16</sup> Jie, prisiartinę prie Mozės, tarė: “Pastatysime tvartus galvijams, taip pat mūsų vaikams miestus;",
			"17": "<sup>17</sup> mes gi patys apsiginklavę eisime į kovą izraelitų priekyje, kol įvesime juos į jų žemes. Tuo tarpu mūsų vaikai gyvens apmūrytuose miestuose dėl šio krašto gyventojų.",
			"18": "<sup>18</sup> Negrįšime į savo namus, kol visi izraelitai gaus savo dalį,",
			"19": "<sup>19</sup> nieko nereikalausime anoje pusėje Jordano, nes mūsų dalis yra šiapus Jordano”.",
			"20": "<sup>20</sup> Mozė jiems atsakė: “Jei taip darysite ir stosite į kovą Viešpaties akivaizdoje,",
			"21": "<sup>21</sup> ir visi karui tinkami vyrai apsiginklavę pereis Jordaną, iki priešas bus nugalėtas",
			"22": "<sup>22</sup> ir visa žemė bus paimta, tuomet nenusikalsite nei Viešpačiui, nei Izraeliui ir ši žemė bus jūsų Viešpaties akivaizdoje.",
			"23": "<sup>23</sup> Jei nedarysite, ką sakote, tai nusidėsite Viešpačiui ir žinokite, kad būsite nubausti.",
			"24": "<sup>24</sup> Taigi statykite miestus savo vaikams, tvartus avims bei galvijams ir įvykdykite, ką pažadėjote”.",
			"25": "<sup>25</sup> Gaditai ir rubenai atsakė Mozei: “Mes, tavo tarnai, darysime, ką mūsų valdovas liepia.",
			"26": "<sup>26</sup> Savo vaikus ir moteris, avis ir galvijus paliksime Gileado miestuose,",
			"27": "<sup>27</sup> mes gi, tavo tarnai, visi apsiginklavę trauksime į karą, kaip tu, valdove, sakai”.",
			"28": "<sup>28</sup> Mozė įsakė kunigui Eleazarui, Nūno sūnui Jozuei ir izraelitų giminių vadams:",
			"29": "<sup>29</sup> “Jei gaditai ir rubenai kartu su jumis pereis Jordaną su ginklais ir žemė bus paimta, duokite jiems paveldėti Gileadą.",
			"30": "<sup>30</sup> Jei nenorės eiti kartu su jumis apsiginklavę į Kanaano žemę, tegul pasilieka tarp jūsų”.",
			"31": "<sup>31</sup> Gaditai ir rubenai atsakė: “Kaip Viešpats įsakė savo tarnams, taip mes darysime.",
			"32": "<sup>32</sup> Mes eisime apsiginklavę Viešpaties akivaizdoje įs Kanaano žemę, kad gautume dalį šiapus Jordano”.",
			"33": "<sup>33</sup> Mozė davė gaditams ir rubenams bei pusei Juozapo sūnaus Manaso giminės amoritų karaliaus Sihono ir Bašano karaliaus Ogo karalysčių žemes su jų miestais.",
			"34": "<sup>34</sup> Gaditai atstatė sutvirtintus Dibono, Ataroto, Aroero,",
			"35": "<sup>35</sup> Atroto, Šofano, Jazero, Jogbohos,",
			"36": "<sup>36</sup> Bet Nimros, Bet Harano miestus ir pastatė savo galvijams tvartus.",
			"37": "<sup>37</sup> Rubenai atstatė Hešboną, Elealę, Kirjataimą,",
			"38": "<sup>38</sup> Neboją, Baal Meoną ir Sibmą, pakeisdami jų vardus.",
			"39": "<sup>39</sup> Manaso sūnaus Machyro sūnūs patraukė į Gileadą ir jį užėmė, išvydami jo gyventojus amoritus.",
			"40": "<sup>40</sup> Mozė davė Gileado žemes Manaso sūnui Machyrui, kuris ten apsigyveno.",
			"41": "<sup>41</sup> O Manaso sūnus Jayras nuėjęs užėmė krašto miestelius ir juos pavadino Havot Jayru.",
			"42": "<sup>42</sup> Taip pat Nobachas nuėjęs užėmė Kenatą su jo kaimais ir jį pavadino savo vardu­Nobachas."
		},
		"33": {
			"1": "<sup>1</sup> Izraelitų sustojimo vietos, išėjus jiems iš Egipto, vadovaujant Mozei ir Aaronui.",
			"2": "<sup>2</sup> Mozė jas surašė pagal stovyklų vietas, kurias jie, Viešpačiui įsakant, keisdavo.",
			"3": "<sup>3</sup> Jie išėjo iš Ramzio pirmo mėnesio penkioliktą dieną, kitą dieną po Paschos, galingos rankos vedami visiems egiptiečiams matant,",
			"4": "<sup>4</sup> laidojant jiems Viešpaties išžudytus pirmagimius. Ir jų dievams Viešpats įvykdė teismą.",
			"5": "<sup>5</sup> Izraelitai, iškeliavę iš Ramzio, pasistatė stovyklas Sukote.",
			"6": "<sup>6</sup> Iš Sukoto atvyko į Etamą, esantį dykumos pakraštyje.",
			"7": "<sup>7</sup> Iš ten atvyko prie Pi Hahiroto, kuris yra priešais Baal Cefoną, ir sustojo prie Migdolo.",
			"8": "<sup>8</sup> Iškeliavę iš Pi Hahiroto, perėjo jūros dugnu į dykumą ir, keliavę tris dienas per Etamo dykumą, pasistatė stovyklą Maroje.",
			"9": "<sup>9</sup> Išėję iš Maros, pasiekė Elimą, kur buvo dvylika vandens šaltinių bei septyniasdešimt palmių, ir sustojo.",
			"10": "<sup>10</sup> Išėję iš Elimo, ištiesė palapines prie Raudonosios jūros.",
			"11": "<sup>11</sup> Iš čia išėję, sustojo Sino dykumoje.",
			"12": "<sup>12</sup> Iš Sino dykumos atkeliavo į Dofką,",
			"13": "<sup>13</sup> išėję iš Dofkos sustojo Aluše.",
			"14": "<sup>14</sup> Iškeliavę iš Alušo, ištiesė palapines Refidime, kur stigo vandens.",
			"15": "<sup>15</sup> Palikę Refidimą, sustojo Sinajaus dykumoje.",
			"16": "<sup>16</sup> Išėję iš Sinajaus dykumos, atėjo į Kibrot Taavos kapines.",
			"17": "<sup>17</sup> Iškeliavę iš čia, pasistatė stovyklą Hacerote.",
			"18": "<sup>18</sup> Iš Haceroto atėjo į Ritmą,",
			"19": "<sup>19</sup> o iš Ritmos­į Rimon Perecą.",
			"20": "<sup>20</sup> Išėję iš Rimon Pereco, atvyko į Libną,",
			"21": "<sup>21</sup> o išėję iš Libnos, sustojo Risoje.",
			"22": "<sup>22</sup> Išėję ir Risos, atvyko į Kehelatą,",
			"23": "<sup>23</sup> o išėję iš Kehelato, sustojo prie Sefero kalno.",
			"24": "<sup>24</sup> Pasitraukę nuo Šefero kalno, atėjo į Haradą.",
			"25": "<sup>25</sup> Išėję iš Harados, pasistatė stovyklą Makhelote.",
			"26": "<sup>26</sup> Iškeliavę iš Makheloto, atėjo į Tahatą,",
			"27": "<sup>27</sup> o išėję iš Tahato, sustojo Terache.",
			"28": "<sup>28</sup> Išėję ir Teracho, ištiesė palapines Mitkoje,",
			"29": "<sup>29</sup> o išėję iš Mitkos, pasistatė stovyklą Hašmonoje.",
			"30": "<sup>30</sup> Išėję iš Hašmonos, atvyko į Moserotą,",
			"31": "<sup>31</sup> o išėję iš Moseroto, pasistatė stovyklą Bene Jaakane.",
			"32": "<sup>32</sup> Išėję iš Bene Jaakano, atkeliavo į Hor Gidgadą,",
			"33": "<sup>33</sup> o išėję iš čia, pasistatė stovyklą Jotbatoje.",
			"34": "<sup>34</sup> Iš Jotbatos atėjo į Abroną,",
			"35": "<sup>35</sup> išėję iš Abronos, ištiesė palapines Ecjon Gebere,",
			"36": "<sup>36</sup> o iš Ecjon Gebero atvyko į Cino dykumą, prie Kadešo.",
			"37": "<sup>37</sup> Išėję iš Kadešo, jie pasistatė stovyklas prie Horo kalno Edomo krašto pasienyje.",
			"38": "<sup>38</sup> Kunigas Aaronas, Viešpačiui liepiant užlipo ant Horo kalno ir mirė penkto mėnesio pirmą dieną, praėjus keturiasdešimčiai metų nuo izraelitų išėjimo iš Egipto,",
			"39": "<sup>39</sup> būdamas šimto dvidešimt trejų metų amžiaus.",
			"40": "<sup>40</sup> Kanaaniečių karalius Aradas, kuris gyveno Kanaano pietuose, sužinojo, kad ateina izraelitai.",
			"41": "<sup>41</sup> Pasitraukę nuo Horo kalno, jie sustojo Calmone.",
			"42": "<sup>42</sup> Išėję iš Calmono, jie atėjo į Punoną,",
			"43": "<sup>43</sup> o išėję iš Punono, sustojo Obote.",
			"44": "<sup>44</sup> Iš Oboto atėjo į Ije Abarimą, prie moabitų sienos,",
			"45": "<sup>45</sup> o išėję iš Ije Abarimo, ištiesė palapines Dibon Gade.",
			"46": "<sup>46</sup> Išėję iš Dibon Gado, sustojo Almon Diblataimoje,",
			"47": "<sup>47</sup> o iš čia jie atėjo iki Abarimo kalnų ties Neboju.",
			"48": "<sup>48</sup> Keliaudami nuo Abarimo kalnų, jie pasiekė Moabo lygumas prie Jordano ties Jerichu",
			"49": "<sup>49</sup> ir pasistatė stovyklą prie Jordano nuo Bet Ješimoto ligi Abel Šitimo, moabitų lygumose.",
			"50": "<sup>50</sup> Moabitų lygumose Viešpats kalbėjo Mozei:",
			"51": "<sup>51</sup> “Kai pereisite Jordaną ir įeisite į Kanaano žemę,",
			"52": "<sup>52</sup> išvykite visus to krašto gyventojus, sunaikinkite jų atvaizdus, sulaužykite stabus, išgriaukite visas aukštąsias stabų garbinimo vietas",
			"53": "<sup>53</sup> ir apsigyvenkite šalyje, kurią Aš jums daviau paveldėti.",
			"54": "<sup>54</sup> Pasidalykite ją burtų keliu: tiems, kurių bus daugiau, duokite didesnį žemės plotą, o kurių mažiau­mažesnį. Žemę išdalinkite burtų keliu giminėms ir šeimoms.",
			"55": "<sup>55</sup> Jei neišvarysite žemės gyventojų, jie bus jums krislai akyse ir dygliai šonuose ir vargins jus krašte, kuriame jūs gyvensite.",
			"56": "<sup>56</sup> Ir tada, ką buvau sumanęs padaryti jiems, jums padarysiu”."
		},
		"34": {
			"1": "<sup>1</sup> Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Kai įeisite į Kanaano šalį ir ją užimsite, jos sienos bus:",
			"3": "<sup>3</sup> pietinė dalis prasidės nuo Cino dykumos, esančios šalia Edomo, jos rytų siena bus Druskos jūra, pradedant pietine dalimi.",
			"4": "<sup>4</sup> Pietinė siena eis išilgai Akrabimo aukštumos ir tęsis iki Sinajaus, pietuose pasieks Kadeš Barnėją. Iš čia siena eis ligi Hacar Adaro ir toliau iki Acmono.",
			"5": "<sup>5</sup> Nuo Acmono siena pasisuks, sieks Egipto upę ir baigsis jūros krantu.",
			"6": "<sup>6</sup> Vakarų siena bus jums Didžioji jūra.",
			"7": "<sup>7</sup> Šiaurėje siena prasidės nuo Didžiosios jūros ir tęsis iki Horo kalno,",
			"8": "<sup>8</sup> iš čia į Lebo Hamatą iki Cedado apylinkės,",
			"9": "<sup>9</sup> toliau siena tęsis iki Zifrono ir Hacar Enano.",
			"10": "<sup>10</sup> Rytų siena eis nuo Hacar Enano ligi Šefamo,",
			"11": "<sup>11</sup> nuo Šefamo tęsis iki Riblos į rytus nuo Aino, iš čia eis Jordano rytų puse iki Kinereto ežero",
			"12": "<sup>12</sup> ir toliau Jordanu iki Sūriosios jūros. Tai bus jūsų krašto sienos”.",
			"13": "<sup>13</sup> Mozė kalbėjo izraelitams: “Tai žemė, kurią paveldėsite burtų keliu. Ją Viešpats liepė išdalinti devynioms ir pusei giminės.",
			"14": "<sup>14</sup> Rubeno, Gado ir pusė Manaso giminės gavo savo dalį.",
			"15": "<sup>15</sup> Dvi giminės ir pusė gavo savo dalį šioje Jordano pusėje ties Jerichu”.",
			"16": "<sup>16</sup> Viešpats tarė Mozei:",
			"17": "<sup>17</sup> “Tai yra vardai vyrų, kurie jums padalins žemę: kunigas Eleazaras, Nūno sūnus Jozuė",
			"18": "<sup>18</sup> ir iš kiekvienos giminės po vieną kunigaikštį.",
			"19": "<sup>19</sup> Jų vardai: iš Judo giminės­Jefunės sūnus Kalebas,",
			"20": "<sup>20</sup> iš Simeono­Amihudo sūnus Samuelis,",
			"21": "<sup>21</sup> iš Benjamino­Kislono sūnus Elidadas,",
			"22": "<sup>22</sup> iš Dano­Joglio sūnus Bukis,",
			"23": "<sup>23</sup> iš Juozapo palikuonių, Manaso giminės­Efodo sūnus Hanielis",
			"24": "<sup>24</sup> ir iš Efraimo­Šiftano sūnus Kemuelis,",
			"25": "<sup>25</sup> iš Zabulono­Parnacho sūnus Elicafanas,",
			"26": "<sup>26</sup> iš Isacharo­Azano sūnus Paltielis,",
			"27": "<sup>27</sup> iš Ašero­Šelomio sūnus Ahihudas,",
			"28": "<sup>28</sup> iš Neftalio­Amihudo sūnus Pedahelis”.",
			"29": "<sup>29</sup> Šitiems vyrams Viešpats įsakė padalyti Kanaano žemę izraelitams."
		},
		"35": {
			"1": "<sup>1</sup> Moabo lygumose prie Jordano, ties Jerichu, Viešpats kalbėjo Mozei:",
			"2": "<sup>2</sup> “Pasakyk izraelitams duoti levitams iš savo paveldėjimo miestus gyventi ir jų apylinkes ganykloms;",
			"3": "<sup>3</sup> jie gyvens miestuose, jų bandos ganysis miestų apylinkėse,",
			"4": "<sup>4</sup> kurios tęsis tūkstantį uolekčių aplink miestų mūrus.",
			"5": "<sup>5</sup> Ganyklos sieks po du tūkstančius uolekčių visomis kryptimis: į rytus, į pietus, į vakarus ir į šiaurę, o miestai bus viduryje.",
			"6": "<sup>6</sup> Iš tų miestų, kuriuos duosite levitams, atskirkite šešis prieglaudai, kad juose rastų apsaugą nusikaltę žmogžudyste. Be šitų duokite levitams dar keturiasdešimt du miestus.",
			"7": "<sup>7</sup> Iš viso duokite levitams keturiasdešimt aštuonis miestus su jų ganyklomis.",
			"8": "<sup>8</sup> Miestų, kuriuos duosite iš izraelitų nuosavybės levitams, daugiau paimsite iš tų giminių, kurios daugiau turi, ir mažiau iš tų, kurios mažiau turi; kiekvienas duos levitams miestų pagal savo paveldo dydį”.",
			"9": "<sup>9</sup> Viešpats tarė Mozei:",
			"10": "<sup>10</sup> “Pasakyk izraelitams, kad, įėję į Kanaano žemę,",
			"11": "<sup>11</sup> paskirtų prieglaudos miestus netyčia nužudžiusiems žmogų.",
			"12": "<sup>12</sup> Tokie miestai bus jums prieglauda nuo keršytojo, kad tas, kuris užmušė žmogų, nemirtų, kol nebus stojęs prieš bendruomenės teismą.",
			"13": "<sup>13</sup> Paskirkite šešis miestus prieglaudai,",
			"14": "<sup>14</sup> iš kurių trys bus Jordano rytų pusėje ir trys bus Kanaano žemėje.",
			"15": "<sup>15</sup> Tie šeši miestai bus prieglauda izraelitams, taip pat ateiviams ir svetimšaliams, kad kiekvienas, netyčia nužudęs žmogų, galėtų į juos atbėgti.",
			"16": "<sup>16</sup> Jei kas geležimi užmuštų žmogų, bus kaltinamas žmogžudyste ir baudžiamas mirtimi.",
			"17": "<sup>17</sup> Jei kas akmeniu užmuštų žmogų, jis bus nubaustas mirtimi.",
			"18": "<sup>18</sup> Jei kas bus nužudytas mediniu įrankiu, užmušėjas bus baudžiamas mirtimi.",
			"19": "<sup>19</sup> Kraujo keršytojas pats užmuš žudiką, kai tik jį sutiks.",
			"20": "<sup>20</sup> Jei kas iš neapykantos pastumtų žmogų ar, mesdamas kuo, jį užmuštų,",
			"21": "<sup>21</sup> arba ranka suduotų taip, kad jis numirtų, nusikaltėlis bus laikomas žmogžudžiu ir baudžiamas mirtimi. Kraujo keršytojas užmuš žudiką, kai tik sutiks.",
			"22": "<sup>22</sup> Jei kas neturėdamas priešiškumo kitą pastumtų, ar mestų į jį ką nors, prieš tai netykojęs,",
			"23": "<sup>23</sup> ar užgautų akmeniu, kokiu galima užmušti, jo nepastebėjęs, taip, kad šis numirtų, tačiau prieš tai nebuvo jo priešas ir netykojo jam pakenkti,",
			"24": "<sup>24</sup> tai bendruomenė darys teismą tarp žudiko ir kraujo keršytojo pagal šiuos nurodymus.",
			"25": "<sup>25</sup> Teismas išvaduos žudiką iš kraujo keršytojo rankų ir grąžins į prieglaudos miestą, kuriame jis pasiliks iki vyriausiojo kunigo, patepto šventu aliejumi, mirties.",
			"26": "<sup>26</sup> Jei žudikas išeis už prieglaudos miesto, į kurį pabėgo, sienų",
			"27": "<sup>27</sup> ir sutikęs kraujo keršytojas jį užmuš, jis bus nekaltas,",
			"28": "<sup>28</sup> nes anas privalėjo likti mieste ligi vyriausiojo kunigo mirties; po kunigo mirties žmogžudys sugrįš prie savo nuosavybės.",
			"29": "<sup>29</sup> Šį nuostatą privalo vykdyti visos jūsų kartos, kur jūs begyventumėte.",
			"30": "<sup>30</sup> Žmogžudys bus nubaustas, apklausus liudytojus; tačiau vieno liudytojo neužtenka, kad pasmerktų žmogų mirčiai.",
			"31": "<sup>31</sup> Žmogžudys negali būti išpirktas, jis turi mirti.",
			"32": "<sup>32</sup> Pabėgėliai negalės išsipirkti ir grįžti prie savo nuosavybės prieš vyriausiojo kunigo mirtį.",
			"33": "<sup>33</sup> Nesutepkite savo žemės, nes ji sutepama krauju ir negali būti apvalyta kitaip, kaip tik krauju to, kuris praliejo kraują.",
			"34": "<sup>34</sup> Nesutepkite žemės, kurią paveldėsite ir kurioje Aš gyvenu, nes Aš, Viešpats, gyvenu tarp Izraelio vaikų”."
		},
		"36": {
			"1": "<sup>1</sup> Manaso sūnaus Machyro sūnaus Gileado šeimų vadai iš Juozapo giminės atėjo ir kalbėjo Mozei, Izraelio vadams girdint:",
			"2": "<sup>2</sup> “Tau, mūsų valdove, Viešpats įsakė, kad burtų keliu padalytum žemę tarp izraelitų ir kad mūsų brolio Celofhado dalį duotum jo dukterims.",
			"3": "<sup>3</sup> Jei jas ves kitos giminės vyrai, jų dalis pereis kitai giminei ir bus atimta iš mums kritusios paveldėjimo dalies.",
			"4": "<sup>4</sup> Atėjus jubiliejaus metams, jų dalis prisidės prie tos giminės žemių, į kurią jos išėjo; taip jų dalis bus atplėšta nuo mūsų giminei tekusios dalies”.",
			"5": "<sup>5</sup> Mozė atsakė izraelitams pagal Viešpaties žodį: “Juozapo sūnų giminė kalba teisingai.",
			"6": "<sup>6</sup> Štai ką sako Viešpats apie Celofhado dukteris: ‘Tegul jos teka už ko nori, bet tik už savo giminės vyrų,",
			"7": "<sup>7</sup> kad izraelitų nuosavybė nepereitų iš vienos giminės kitai giminei.",
			"8": "<sup>8</sup> Visos mergaitės, turinčios paveldėjimo teisę, privalo tekėti už tos pačios giminės vyrų, kad Izraelio vaikai paveldėtų kiekvienas savo tėvų dalį.",
			"9": "<sup>9</sup> Nuosavybė iš vienos giminės nepereis kitai; visos izraelitų giminės išlaikys savo paveldo dalį’ ”.",
			"10": "<sup>10</sup> Kaip Viešpats įsakė Mozei, taip padarė Celofhado dukterys:",
			"11": "<sup>11</sup> Machla, Tirca, Hogla, Milka ir Noja ištekėjo už savo pusbrolių",
			"12": "<sup>12</sup> iš giminės Manaso, kuris buvo Juozapo sūnus. Joms paskirtoji dalis pasiliko jų giminėje ir šeimoje.",
			"13": "<sup>13</sup> Šituos nuostatus ir įsakymus Viešpats davė izraelitams per Mozę Moabo lygumose prie Jordano ties Jerichu."
		}
	}
};
module.exports = book;