var book = {
	"name": "1 शमूएल",
	"numChapters": 31,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> एल्कानाका परिवारले शीलोमा आराधाना गर्छन्\nत्यहाँ एल्काना नाउँ भएका एकजना मानिस थिए। तिनी एप्रैमको पहाडी प्रदेश रामा शहरमा बस्थे। तिनी सूपी परिवारका थिए। तिनका पिताको नाउँ यरोहाम, बाजेको नाउँ एलीहू र बराजुको नाउँ तोहू थियो। तोहू चाहिँ सूपका छोरा थिए। सूप एप्रेम परिवार समूहबाटका थिए।",
			"2": "<sup>2</sup> एल्कानाका दुइवटी पत्नी थिए। एउटीको नाउँ हन्ना र अर्कीको नाउँ पनिन्ना थियो। पनिन्नाका नानीहरू थिए, तर हन्नाका नानीहरू थिएनन्।",
			"3": "<sup>3</sup> परमप्रभु सर्वशक्तिमानको अघि आराधना गर्न र बलि चढाउन प्रत्येक बर्ष एल्काना रामाबाट शीलोमा जाने गर्थे। शीलोमा परमप्रभुका पूजाहारीको रूपमा होप्नी र पीनहासले सेवा गर्थे। यी दुइ एलीका छोरा थिए।",
			"4": "<sup>4</sup> जब एल्काना बलि चढाउने गर्थे, उसले उनकी पत्नी पनिन्ना र उनका छोरा-छोरीलाई भाग दिने गर्थे।",
			"5": "<sup>5</sup> उनले हन्नालाई जहिले पनि बराबरी भाग भोजन दिन्थे उनले हन्नालाई बढी माया गर्थे किनभने परमेश्वरले उनको कोख बन्द गरिदिनु भएको थियो।",
			"6": "<sup>6</sup> हन्नालाई पनिन्नाले सताउँथी\nपरमप्रभुले हन्नालाई बाँझी पार्नु भएको कारण पनिन्नाले तिनलाई रिस उठाउने गर्थी।",
			"7": "<sup>7</sup> शीलोमा प्रत्येक बर्ष जब त्यो परिवार परमप्रभुलाई आराधना गर्न र बलिदान चढाउन जान्थे तब पनिन्नाहले हन्नालाई दुःख दिने गर्थिन् र खसालेर कुरा गर्ने र तिनलाई रूवाएर खाना नखानेसम्म बनाउँथिइन्।",
			"8": "<sup>8</sup> यो सब देखेर एल्काना उनका पतिले भन्थे, “हन्ना तिमी किन रून्छौ? किन केही खाँदिनौ? तिमी किन यति दुःखी छौ? म तिम्रो लोग्ने हुँ र म तिम्रै हुँ। तिमीले सोच्नु पर्छ कि म दशजना छोराहरू भन्दा बढ्ता नै छु।”",
			"9": "<sup>9</sup> हन्नाले प्रार्थना गर्छिन्\nत्यसपछि हन्ना चुपचाप उठिन् र परमप्रभुको सामु प्रार्थना गर्न गइन्। त्यस समय पूजाहारी एली परमप्रभुको पवित्र आराधनालयको दैलोकै छेउमा एउटा कुर्सीमा बसिरहेका थिए।",
			"10": "<sup>10</sup> हन्ना साह्रै नै दुःखी थिइन्। यसर्थ जब तिनले प्रार्थना गर्न शुरू गरिन तिनी साह्रै रोइन्।",
			"11": "<sup>11</sup> तिनले परमेश्वरलाई भाकल गर्दै भनिन्, “हे सर्वशक्तिमान परमप्रभु म अफसोसमा छु। तपाईले मलाई दया गर्नुहोस्। ममाथि कृपालु हुनुहोस् म तपाईंकी दासी हुँ। यदि तपाईंले मलाई एउटा छोरा दिनुभयो भने त्यो बाँचुन्जेलसम्म म त्यसलाई तपाईंकै सेवामा अर्पण गर्नेछु। त्यस बालकले कहिले पनि दाखरस पिउने छैन र त्यसको कपाल पनि काटीने छैन्।”",
			"12": "<sup>12</sup> हन्नाले धेरै बेरसम्म परमप्रभुलाई प्रार्थना गरिरहिनन्। प्रार्थना गर्दा एलीले हन्नाका ओंठ आवाज बिनै चलिरहेका देखे।",
			"13": "<sup>13</sup> हन्नाले ओठ चलाए पनि आवाज ननिकालिकन साँच्चै उनको हृदयमा प्रार्थना गरिरहेकी थिइन्।",
			"14": "<sup>14</sup> हन्नाले ज्यादै मद्यपान गरेकी ठानेर एलीले भने, “तिमीले ज्यादै पिएकी छौ। यो दाखरस छोडने समय हो।”",
			"15": "<sup>15</sup> हन्नाले उत्तर दिइन्, “महाशय, मैले कुनै प्रकारको मद्यपान गरेकी छैन। म साह्रै दुःखमा परेकी छु। म त परमप्रभुसँग मेरो दुःखहरू पो बिन्ति गर्दैथिएँ।",
			"16": "<sup>16</sup> तपाईंले मलाई नराम्री स्त्री ठान्नु हुँदैन। म साह्रै ठूलो कष्टमा परेकी छु र साह्रै दुःखी भएकी छु। मैले धेरै बेरसम्म परमेश्वरलाई प्रार्थना चढाएकी हुँ।”",
			"17": "<sup>17</sup> एलीले भने, “यदि त्यसो हो भने शान्तिसित जाऊ। इस्राएलका परमेश्वरले तिम्रो प्रार्थना अवश्य सुन्नु हुनेछ, अनि तिम्रो विन्ती पूरा गर्नु हुनेछ।”",
			"18": "<sup>18</sup> हन्नाले भनिन्, “म आशा गर्छु तपाईं पनि मसँग खुशी हुनुहुन्छ।” तब हन्ना गइन् र केही खाने कुरा खाइन्। तिनी फेरि दुःखीत भइनन्।",
			"19": "<sup>19</sup> भोलिपल्ट बिहानै एल्कानाका परिवारका सदस्यहरू उठे अनि परमप्रभुको आराधना गरे र आफ्नो घर रामामा फर्के।\nशमूएलको जन्म\nएल्कानाले आफ्नी पत्नी हन्नासित सहवास गरे। परमप्रभुले पछि हन्नालाई सम्झनु भयो।",
			"20": "<sup>20</sup> तिनी गर्भवती भइन्। समय पुगेपछि हन्नाले एउटा छोरो जन्माइन्। तिनले छोराको नाउँ शमूएल  राखिन्। तिनले भनिन्, “यसको नाउँ शामूएल हो किनभने यसलाई मैले परमप्रभुसित मागेकी हुँ।”",
			"21": "<sup>21</sup> त्यस बर्ष एल्कानाले परमेश्वरसित राखेको भाकल पूरा गर्न र बलि चढाउन (शीलोमा) गए। उनका परिवारका अरू सदस्यहरू पनि गए।",
			"22": "<sup>22</sup> तर हन्ना गइनन्। तिनले एल्कानालाई भनिन्, “बालकले जब दूध खान छोड्छ र ठोस आहार खान थाल्छ, म यसलाई लिएर शीलोमा जानेछु। तब म बालकलाई परमप्रभुमा अर्पण गर्नेछु। बालक सधैँलाई शीलोमा नै रहनेछ।”",
			"23": "<sup>23</sup> हन्नाका लोग्ने एल्कानाले भने, “तिमीलाई जे राम्रो लाग्छ त्यही गर बालकले ठोस भोजन गर्नसक्ने नभइन्जेल तिमी घरैमा बस। परमप्रभुले तिम्रो वचन  पूरा गरिदिउन्।” यसर्थ तिनी छोराले आमाको दूध नछाडेसम्म घरैमा बसिन्।",
			"24": "<sup>24</sup> हन्नाले शमूएललाई शीलोमा एली भएको ठाउँमा लैजान्छिन\nजब नानी ठूलो भयो अनि ठोस भोजन खान थाल्यो हन्नाले नानीलाई परमप्रभुको मन्दिर शीलोमा लगिन्। हन्नाले आफूसँग एउटा तीन बर्षको साँढे, चार पाथी पीठो र एक मसक दाखरस पनि लिएर गइन्।",
			"25": "<sup>25</sup> तिनीहरूले परमप्रभुको आराधना गरे। त्यसपछि साँढेलाई बलि चढाए पश्चात तिनीहरूले नानी पूजाहारी एलीलाई दिए।",
			"26": "<sup>26</sup> हन्नाले बालकलाई एलीको अघिल्तिर लिएर गइन् र भनिन्, “महाशय, मलाई क्षमा गर्नु होला। म त्यही स्त्री हुँ, जो परमप्रभुलाई प्रार्थना गर्दागर्दै तपाईंका सामु उभिएकी थिएँ। मैले साँचो भनिरहेको छु।",
			"27": "<sup>27</sup> मैले बालकको निम्ति प्रार्थना गरेकी थिएँ अनि परमप्रभुले मलाई बालक दिनुभयो र आभारी तुल्याउनु भयो।",
			"28": "<sup>28</sup> अब यस बालकलाई परमप्रभुमा अर्पण गर्दैछु। सारा जीवन नै परमप्रभुको भएर यो यही रहनेछ।”\nतब हन्नाले बालकलाई त्यही छोडीदिइन् अनि परमप्रभुको आराधना गरिन्।"
		},
		"2": {
			"1": "<sup>1</sup> हन्नाले धन्यवाद दिन्छिन्\nहन्नाले प्रार्थना गरिन्,\n“परमप्रभुमा, म खुशी छु!\nपरमप्रभुमा म विजयको अनुभव गर्छु।\nतपाईंको मुक्तिमा म खुशी छु,\nअनि मेरो शत्रुहरू माथि धाक जमाउँछु।",
			"2": "<sup>2</sup> परमप्रभु जस्तो पवित्र कोही छैन।\nतपाईं बाहेक कोही परमेश्वर छैन।\nहाम्रो परमेश्वर बाहेक यहाँ अरू कुनै आश्रय छैन।",
			"3": "<sup>3</sup> अब अहंकार हुदैन!\nअहंकारपूर्ण वचनहरू अब छैन,\nकारण परमप्रभु परमेश्वर सर्वज्ञानी हुनुहुन्छ।\nपरमेश्वरले मानिसका कार्यहरू तौलनु हुन्छ।",
			"4": "<sup>4</sup> शूरबीर र योद्धाहरूका धनु भाँचिएका छन्\nअनि दुर्बलहरू बलिया भएका छन्।",
			"5": "<sup>5</sup> विगतमा धेरै भोजन प्राप्त गर्नेहरू\nअब काम नगरी भोजन नपाउने भएका छन्।\nतर विगतमा जो भोक-भोकै थिए,\nतिनीहरू अहिले भोजन खाएर मोटाएका छन्।\nआमा बन्ने सौभाग्य नपाएकी स्त्रीहरू\nअहिले सातवटाकी आमा बन्न सफल छिन्!\nतर जुन स्त्रीका धेरै सन्तानहरू थिए,\nतिनीहरू सबै गएकाले ती स्त्री दुःखी छिन्।",
			"6": "<sup>6</sup> परमप्रभुले कुनै मानिसहरूलाई मृत्युमा पार्नुहुन्छ,\nअनि परमप्रभुले नै कुनैलाई जीवन दिनुहुन्छ.\nउहाँले कोही मानिसलाई तल पातालमा पठाउनुहुन्छ\nअनि अरू कोहीलाई जीउनको निम्ति उठाउनुहुन्छ र जीवन दिनुहुन्छ।",
			"7": "<sup>7</sup> परमप्रभुले मानिसहरूलाई गरीब तुल्याउनु हुन्छ,\nअनि उहाँले नै धनी बनाउनुहुन्छ,\nपरमप्रभुले मानिसहरूलाई तल पार्नु हुन्छ\nर फेरि उच्च आसनमा बसाउनुहुन्छ।",
			"8": "<sup>8</sup> परमप्रभुले कंगालहरूलाई धूलोबाट उठाउँनुहुन्छ,\nअनि दुःख हटाई दिनुहुन्छ।\nपरमप्रभुले कंगालीहरूलाई राजा सँग-सँगै बसाउनुहुन्छ।\nपरमप्रभुले कंगालीहरूलाई सम्मानको सिंहासनमा राख्नुहुन्छ।\nपरमप्रभु उनी हुन जसले संसारको जग बसाल्नु भयो\nअनि त्यसमाथि संसार बनाउनु भयो।",
			"9": "<sup>9</sup> परमप्रभुले आफ्ना पवित्र मानिसहरूको रक्षा गर्नुहुन्छ,\nठेस लागेर लड्नबाट उनीहरूलाई बँचाउनु हुन्छ,\nतर पापीहरू अन्धकारमा नष्ट भएर डुब्ने छन्।\nतिनीहरूका शक्ति विजय प्राप्त गर्न\nसहायक सिद्ध हुनेछैन।",
			"10": "<sup>10</sup> परमप्रभुले आफ्ना शत्रुहरूलाई नष्ट पार्नुहुन्छ।\nसर्वोच्च परमेश्वर स्वर्गबाट उहाँको दुश्मनहरूका विरूद्ध गर्जनुहुन्छ।\nउहाँले सारा पृथ्वीको न्याय गर्नुहुन्छ,\nपरमप्रभुले आफ्ना राजालाई बलियो पार्नुहुन्छ।\nउहाँले चुनेका राजालाई बलियो पार्नुहुन्छ।”",
			"11": "<sup>11</sup> एल्काना र तिनका परिवारका सदस्यहरू सबै रामामा आफ्नो घर फर्के, तर त्यो बालक चाहिँ शीलोमा नै परमप्रभुको सेवा गर्न पूजाहारीको रेखदेखमा रह्यो।",
			"12": "<sup>12</sup> एलीका दुष्ट छोराहरू\nएलीका छोराहरू दुष्ट थिए। परमप्रभु प्रति तिनीहरूको कुनै वास्ता थिएन।",
			"13": "<sup>13</sup> यसरी तिनीहरूले मानिसहरूलाई दुर्व्यवहार गर्थे। पूजाहारीहरूको कर्त्तव्य यो थियोः जब कोही मानिस बलिदान चढाउँथ्यो तब पूजाहारीले एउटा भाँडाको उम्लीएको पानीमा मासु पकाउनु पर्थ्यो। तब पूजाहारीको सेवकले त्रिशूल लिनु पर्थ्यो।",
			"14": "<sup>14</sup> पूजाहारीको सेवकले हातमा तिखो त्रिशूल लिएर भाँडाको मासुलाई काँटाले घोच्नु पर्थ्यो। सेवकहरूले काँटाले घोचे जतिमात्र मासु पूजाहारीको भाग हुन्थ्यो। पूजाहारीले उनै इस्राएलीहरूका निम्ति यसो गर्नुपर्थ्यो जो शीलोमा बलिदान चढाउन आउँथे।",
			"15": "<sup>15</sup> तर एलीका छोराहरूले यसो गरेनन्। बोसोलाई वेदीमा जलाउनु अघि नै पूजाहारीका सेवक आएर बलिदानको पवित्र भेटि यसरी भनेर सोध्ने गर्थ्यो, “पूजाहारीको लागि पकाउनलाई केहि मासु देऊ, पूजाहारीले पकाएको मासु ग्रहण गर्दैनन् खाली काँचो मासु मात्र।”",
			"16": "<sup>16</sup> बलि चढाउने मानिसले भन्थ्यो, “पहिले बोसोलाई जलाउँ त्यसपछि तिमीलाई चाहिए जति लैजाऊ।” यदि यस्तो परिस्थितिमा भने पूजाहारीको सेवकले भन्थ्यो, “होइन, त्यो मलाई अहिले नै मासु दिइहाल, यदि दिंदैनौ भने म खोसेर लैजानेछु।”",
			"17": "<sup>17</sup> यस्तो प्रकारले ती दुइ पूजाहारी होप्नी र पीनहासले परमप्रभु प्रति चढाइएको बलिदानहरूमा श्रद्धा देखाएनन्। तिनीहरूले परमप्रभुको विरूद्धमा महा-पाप गरे।",
			"18": "<sup>18</sup> तर शमूएलले एउटा सहयोगीको रूपमा परमप्रभुको सेवा गर्थ्यो। शमूएलले एउटा विशेष प्रकारको मलमलको पोशाक लगाउथ्यो जसलाई एपोद भनिन्छ।",
			"19": "<sup>19</sup> प्रत्येक बर्ष शमूएलकी आमाले उसलाई एउटा सानो भोटो बनाइ दिन्थिन्। प्रत्येक बर्ष लोग्नेसित जब बलि चढाउन शीलो जान्थिन् त्यो भोटो शमूएलाई पुर्याइ दिन्थिन्।",
			"20": "<sup>20</sup> एलीले एल्काना र तिनकी पत्नीलाई आशीर्वाद दिन्थे। एलीले भने, “परमप्रभुले यो बालक जसलाई तिमीले परमप्रभुको सेवाको लागि माग्यौ यसको ठाउँमा अरू धेरै नानीहरू परमप्रभु तिनीहरूका साथ आशिष दिउन्।”\nएल्काना र हन्ना घर फर्किए अनि",
			"21": "<sup>21</sup> परमप्रभुको निगाह हन्ना माथि थियो। तिनले तीनवटा छोरा र दुइवटी छोरी जन्माइन्। शमूएल भने परमप्रभुको सामुन्ने बढ्न थाल्यो।",
			"22": "<sup>22</sup> एलीले दुष्ट छोराहरूलाई तह लगाउन सकेनन्\nएली साह्रै वृद्ध भएका थिए। उसले आफ्ना छोराहरू बार-बार शीलोमा इस्राएलीहरूसित गरेका व्यवहार सुने। भेट हुने पालको ढोकामा सेवा गर्ने स्त्रीहरूसँग तिनीहरूले सहवास गरेको कुरा पनि एलीले सुने।",
			"23": "<sup>23</sup> एलीले आफ्ना छोराहरूलाई भने, “तिमीहरूले गरेका दुष्ट कार्य मानिसहरूले मलाई सुनाउँछन्। त्यस्ता दुष्ट कार्य किन गर्छौ?",
			"24": "<sup>24</sup> छोरा हो, त्यस्तो खराब कार्य नगर। परमप्रभुका मानिसहरूले तिमीहरूका बारेमा नराम्रो कुरा गर्दैछन्।",
			"25": "<sup>25</sup> मानिसले मानिसको विरूद्धमा दुष्ट कार्य गरेकोमा परमेश्वरले त्यसको न्याय गर्नु हुनेछ। तर कसैले परमप्रभुको विरूद्धमा पाप गर्छ भने, कसले उसलाईं परमेश्वरसित सहायता गर्नेछ?”\nतर एलीका दुष्ट छोराहरूले पिताका कुरा सुन्न अस्वीकार गरे। तर परमेश्वरले पहिले नै निर्णय लिइसक्नु भएको थियो कि तिनीहरू मर्ने थिए।",
			"26": "<sup>26</sup> बालक शमूएल भने दिन दिनै ठूलो हुँदै गयो। उसले परमेश्वर र मानिसहरूलाई प्रसन्न पार्थ्यो।",
			"27": "<sup>27</sup> एलीको परिवारमा डरलाग्दो अगमवाणी\nपरमेश्वरका एकजना दास आएर एलीलाई भने, “परमप्रभु भन्नुहुन्छ, ‘म तेरो पिता-पुर्खाहरू मिश्रमा फिरऊनको दासत्वमा रहेको समयमा म तेरो पिताको परिवारमा प्रकट भएँ।",
			"28": "<sup>28</sup> इस्राएल भरिका कुलहरू मध्ये तेरो पिताको कुललाई मेरो पूजाहारीको रूपमा चुनेको थिएँ। मैले तिनीहरूलाई मेरो वेदीमाथि धूप बाल्न र मेरो उपस्थितिमा एपोद लाउन छानेको थिएँ। इस्राएलीहरूले चढाएका बलिको भाग उनीहरूसित मैले बाँडे।",
			"29": "<sup>29</sup> तर तैंले ती बलि र भेटीहरूमा मेरो आदेश अनुसार आदर गरिनस्। मलाई भन्दा ज्यादा तैंले तेरो छोराहरूलाई सम्मान दिइस्। इस्राएलका मेरा मानिसहरूले मलाई चढाएको पवित्र बलिको भोजन खाएर तँ मोटाएकोछस्।’",
			"30": "<sup>30</sup> “परमप्रभु इस्राएलका परमेश्वरले प्रतिज्ञा गर्नुभयो तँ अनि तेरा पिताको सन्तानले सदा-सर्वदा मेरो सेवा गर्नेछन्। तर परमप्रभु अब यसो भन्नुहुन्छ, ‘यस्तो कहिले हुँदैन! मलाई जसले सम्मान गर्छन् म तिनीहरूको सम्मान गर्छु। तर जसले मेरो अनादर गर्छ ऊ अपमानित हुनेछ।",
			"31": "<sup>31</sup> समय आउँदैछ जब म तेरो सन्तानलाई नष्ट गर्नेछु। तेरो परिवारमा कसैले पनि बुढो भइन्जेलसम्म जीउने छैनन।",
			"32": "<sup>32</sup> इस्राएलको निम्ति असल दिन आउने छ, तर तैले घरमा खराब दिन देख्नेछस्। तेरो परिवारमा कोही पनि बुढो हुन्जेल बाँच्ने छैन।",
			"33": "<sup>33</sup> तर तेरा सन्तानको एकजना व्यक्ति मेरो वेदीको पूजाहारीको रूपमा रहनेछ। उसको आँखाको शक्ति रहुन्जेल र शारीरिक शाक्ति कम्ती नहुन्जेल सम्म ऊ रहनेछ। तेरा सन्तान तरवारले काटिएको उसले देख्नेछ।",
			"34": "<sup>34</sup> म तँलाई यो चिन्ह दिनेछु जसले गर्दा तैंले यो साँचो हो भनी जान्नेछस्। तेरा दुवै छोरा होप्नी र पीनहास एकै दिनमा मर्नेछन्।",
			"35": "<sup>35</sup> म एउटा विश्वासयोग्य पूजाहारी नियुक्त गर्नेछु, जसलाई मैले आदेश गरे अनुसार काम गर्नेछ। म त्यस पूजाहारीको परिवारलाई शक्तिशाली बनाउनेछु। उसले जीवनभरी मैले चुनेको राजाको सामु सेवा गर्नेछ।",
			"36": "<sup>36</sup> त्यसपछि तेरो परिवारमा कोही बाँचे भने तिनीहरूले पूजाहारीको अघि आएर झुक्नेछन् र भन्नेछन्, “दया गरी मलाई पूजाहारीको रूपमा नियुक्त गर्नुहोस् ताकि म सँग केही खाने चीज होस्।”’”"
		},
		"3": {
			"1": "<sup>1</sup> परमेश्वरले शमूएललाई बोलाउनु भयो\nएलीको अधीनमा रहेर बालक शमूएलले परमप्रभुको सेवा गर्थे। त्यस बेला परमप्रभु मानिसहरूसँग सिधै धेरै जस्तो बोल्नुहुन्थेन। उहाँले धेरै मानिसहरूलाई दर्शनहरू दिनु भएन।",
			"2": "<sup>2</sup> एली लगभग अन्धा जस्तै भइसकेका थिए। एक रात तिनी ओछ्यानमा पल्टिरहेका थिए।",
			"3": "<sup>3</sup> शमूएल चाहिँ पवित्र सन्दूक भएको परमप्रभुको मन्दिरमा सुतिरहेका थिए। परमप्रभुको बत्ती जलिरहेकै थियो।",
			"4": "<sup>4</sup> परमप्रभुले शमूएललाई बोलाउनु भयो। “म यहाँ हाजीर छु” शमूएलले उत्तर दिए।",
			"5": "<sup>5</sup> शमूएलले सोचे एलीले उसलाई बोलाएको हो। यसकारण शमूएल हतारीएर एलीकहाँ गए। शमूएलले आफ्नो उपस्थिति जाहेर गरेर भने, “तपाईंले मलाई बोलाउनुभयो, म यहाँ छु।”\nतर एलीले भने, “मैले तिमीलाई बोलाएको होइनँ। आफ्नो ओछ्यानमा गएर सुत।”\nशमूएल आफ्नो ओछ्यानमा गए।",
			"6": "<sup>6</sup> परमप्रभुले फेरि बोलाउनु भयो, “शमूएल!” शमूएल फेरि एलीको ओछ्यानमा गएर भने, “तपाईंले मलाई बोलाउनु भएको हो, म यहाँ छु।”\nएलीले भने, “मैले बोलाएको छैन मेरो छोरा! जाऊ, गएर सुत।”",
			"7": "<sup>7</sup> अहिलेसम्म शमूएलले व्यक्तिगत रूपमा परमप्रभुलाई चिनेका थिएनन्। परमप्रभुले शमूएल सँग सिधै बातचित गर्नु भएको थिएन।",
			"8": "<sup>8</sup> परमप्रभुले शमूललाई तेस्रो चोटि बोलाउनु भयो। शमूएल उठेर फेरि एलीको छेउ पुगे। शमूएलले भने, “म यहाँ छु, तपाईंले मलाई बोलाउँनु भयो।”\nत्यतिबेला भने एलीले बुझे कि परमप्रभुले शमूएललाई बोलाउँदै हुनुहुन्थ्यो।",
			"9": "<sup>9</sup> एलीले शमूएललाई भने, “ओछ्यानमा जाऊ! यदि फेरि बेलाउँनु भयो भने भन्नु, ‘परमप्रभु भन्नुहोस्। म तपाईंको दास हुँ र तपाईंले भन्नु भएको सुन्दैछु।’”\nयसकारण शमूएल ओछ्यानमा गए।",
			"10": "<sup>10</sup> परमप्रभु त्यहाँ प्रकट हुनुभयो र पहिलेको जस्तै बोलाउनु भयो, “शमूएल, शमूएल!”\nशमूएलले भने, “भन्नुहोस्! परमप्रभु म तपाईंको दास हुँ अनि सुन्दैछु।”",
			"11": "<sup>11</sup> परमप्रभुले शमूएललाई भन्नुभयो, “म इस्राएलमा केही अचम्मको काम गर्नेछु। जसले यस विषयमा सुन्छ तिनीहरू साह्रै अचम्मीत हुनेछन्।",
			"12": "<sup>12</sup> मैले एली र त्यसको परिवारको विरूद्धमा जे-जे गर्छु भनेको थिएँ ती सब गर्नेछु। म शुरू देखि अन्त्यसम्म सबै गर्नेछु।",
			"13": "<sup>13</sup> मैले एलीलाई भनेकै छु कि म त्यसको परिवारलाई सधैँको लागि दण्ड दिनेछु। म त्यसो यसकारणले गर्दैछु भनि एलीलाई थाह छ। त्यसका छोराहरूले परमेश्वरलाई निन्दा गरे अनि उहाँसित खराब आचरण गरे। तर उसले तिनीहरूलाई शिक्षा दिएनन्।",
			"14": "<sup>14</sup> यसैकारण मैले एलीको परिवारलाई श्राप दिएको छु कि बलिभेटी र अन्नबलिले तिनीहरूलाई पापबाट छुटकारा दिन सक्दैनन्।”",
			"15": "<sup>15</sup> बिहानसम्म शमूएल आफ्नो ओछ्यानमै पल्टिरहे उज्यालो हुन साथ झट्पट् उठेर परमप्रभुका मन्दिरका ढोकाहरू खोले। दर्शनको विषयमा एलीलाई भन्न तिनी डराएका थिए।",
			"16": "<sup>16</sup> तर एलीले नै शमूएललाई भने, “शमूएल, मेरो छोरा।”\nशमूएलले जवाफ दिए, “भन्नुहोस्, म यहाँ छु।”",
			"17": "<sup>17</sup> एलीले सोधे, “परमप्रभुले तिमीलाई के भन्नु भयो! मलाई लुकाउने काम छैन। यदि परमेश्वरको सन्देश सबैलाई नभनेर लुकायो भने परमेश्वरले दण्ड दिनुहुनेछ।”",
			"18": "<sup>18</sup> शमूएलले केही पनि नलुकाई सबै बताए। शमूएलले एलीबाट केही पनि लुकाएनन्.\nएलीले भने, “उहाँ परमप्रभु हुनुहुन्छ। उहाँलाई जे इच्छा लाग्छ त्यसै गर्नुहुन्छ।”",
			"19": "<sup>19</sup> शमूएल अझ ठूलो हुँदै गए। परमप्रभु शमूएलसित हुनु हुन्थ्यो। परमप्रभुले शमूएलको कुनै सन्देशलाई पनि झूटो हुने मौका दिनुभएन।",
			"20": "<sup>20</sup> त्यसबेला सारा इस्राएलका, (दान देखि बेर्शेबा सम्मका) मानिसहरूले शमूएल परमप्रभुका साँचो अगमवक्ता भएको चाल पाए।",
			"21": "<sup>21</sup> शीलोमा परमप्रभु शमूएलकहाँ प्रकट हुनु भयो। उहाँ आफैं शमूएलको अघि परमप्रभुको वचन जस्तै प्रकट हुनु भयो।"
		},
		"4": {
			"1": "<sup>1</sup> इस्राएलभरी शमूएलको विषयमा समाचार फैलिन थाल्यो। तर एली भने साह्रै बुढो भइसकेका थिए। तर उनका छोराहरूले भने अझै पनि परमप्रभुका अघि दुष्ट कार्य गरिनै रहे।\nपलिश्तीहरूले इस्राएलीहरूलाई हराए\nत्यस समय, इस्राएलीहरू पलिश्तीहरूका विरूद्धमा युद्ध गर्नको निम्ति तयार भए। इस्राएलीले एबेन-एजेरमा छाउनी हाले अनि पलिश्तीहरूले अपेकमा छाउनी हाले।",
			"2": "<sup>2</sup> पलिश्तीहरू इस्राएलीहरू माथि हमला गर्न तयार भए।\nपलिश्तीहरूले इस्राएलीहरूलाई हराए। पलिश्तीहरूले इस्राएलका 4,000 सैनिकहरू मारिदिए।",
			"3": "<sup>3</sup> बाँकी इस्राएली सैनिकहरू फर्केर छाउनीमा पुगे। इस्राएलका बूढा प्रधानले सोधे, “परमप्रभुले पलिश्तीहरूद्वारा हामीलाई किन हराउन दिनुभयो? हामीले शीलोबाट परमप्रभुका करारको सन्दूक उठाएर ल्याऊ। यसो गरेपछि परमेश्वर हामीसँग युद्ध भूमिमा जानुहुनेछ र हामीलाई हाम्रो शत्रुबाट बचाउँनु हुनेछ।”",
			"4": "<sup>4</sup> त्यसकारण तिनीहरूले शीलोमा मानिसहरू पठाए। सर्वशक्तिमान परमप्रभुको करारको सन्दूक लिएर मानिसहरू आए। सन्दूक माथि करूब स्वर्गदूत विराजमान थिए अनि तिनीहरू त्यस्तो प्रकारले बसेका छन् जसमाथि परमप्रभुले आसन लिनुहुन्थ्यो। एलीका छोरा होप्नी र पीनहास जो सन्दूकको रेखदेख गर्दथे तिनीहरू पनि उक्त सन्दूकसँगै आए।",
			"5": "<sup>5</sup> जब परमप्रभुको करारको सन्दूक छाउनी भित्र ल्याइयो, इस्राएलीहरूले ठूलो हल्ला गरे। त्यस हल्लाले भूमि नै हल्लियो।",
			"6": "<sup>6</sup> पलिश्तीहरूले पनि इस्राएलीहरूका हल्ला सुनें। तिनीहरूले सोधे, “हिब्रूहरूका छाउनीमा यति साह्रौ हल्ला किन हुँदैछ?”\nपछि पलिश्तीहरूलाई थाहा भयो कि इस्राएलीहरूका छाउनीमा करारको सन्दूक आएको छ।",
			"7": "<sup>7</sup> पलिश्तीहरू डराएर भने, “परमेश्वर नै तिनीहरूका छाउनीमा आए! अब हाम्रो के हुने हो। यस्तो पहिले कहिले भएको थिएन।",
			"8": "<sup>8</sup> हामी चिन्तित भयौ यस्तो बलियो देवताबाट अब हामीलाई कसले बचाउँछ? यी तिनै देवता हुन् जसले मिश्रीहरूलाई जंगलमा महामारी र भंयकर रोगहरू दिएका थिए।",
			"9": "<sup>9</sup> पलिश्तीहरू हो सहासी बन! मर्द झैं काम गर, नत्र ता विगतमा हिब्रूहरू हाम्रा दास थिए, भने अब लडेनौं भने तिमीहरू उनीहरूका दास हुनेछौ।”",
			"10": "<sup>10</sup> पलिश्तीहरूले वीरताका साथ लडे र इस्राएलीहरूलाई परास्त पारे। प्रत्येक इस्राएली सेना भागेर आफ्ना छाउनीमा लुक्न पुगे। इस्राएलको यो साह्रै नराम्रो पराजय थियो। जम्मा 30,000 इस्राएली सैनिकहरू मारिएका थिए।",
			"11": "<sup>11</sup> पलिश्तीहरूले परमेश्वरको पवित्र सन्दूक हात पारे र एलीका दुवै छोरा, होप्नी र पीनहासलाई मारे।",
			"12": "<sup>12</sup> त्यस दिन बिन्यामिनी कूलका एकजना मानिस युद्ध भूमिबाट भागेर हारेको शोक प्रकट गर्न शरीरका बस्त्रहरू च्यातेर टाउकोभरी धूलै-धूलो खन्याए र शीलोमा पुगे।",
			"13": "<sup>13</sup> त्यस समय एली शहरको फाटकहरूको मूल बाटोमा आफ्नो आसनमा बसिरहेका थिए। तिनी परमेश्वरको पवित्र सन्दूकको विषयमा ज्यादै चिन्तित थिए र समाचारको प्रतिक्षा गरिरहेका थिए। त्यो बिन्यामीनहरूले आए र दुःखी समाचार शहरलाई दियो। अनि सबै शहरवासी जोडले रून थाले।",
			"14": "<sup>14</sup> एली अन्ठानब्बे बर्षका बूढा थिए अनि तिनी अन्धा थिए। जब एलीले रोएको स्वर सुने तिनले सोधे, “मानिसहरू किन यसरी चिच्याइ-चिच्याइ रुँदैछन्?”\nउनै बिन्यामिनी मानिस एलीका सामुने पुगेर सारा घटना बताए।",
			"16": "<sup>16</sup> तिनले भने, “म युद्धभूमिबाट भागेर आएको हुँ!”\nएलीले फेरि सोधे, “के भयो हँ छोरा?”",
			"17": "<sup>17</sup> बिन्यामिनी मानिसले सार वृत्तान्त बताए, “इस्राएलीहरू पलिश्तीहरूका अघि टिक्न नसकेर भागे इस्राएली पक्षका धेरै सेना युद्धमा मारिए। तपाईंका दुवै छोरा, होप्नी र पीनहास पनि मारिए अनि परमेश्वरको पवित्र सन्दूक पनि पलिश्तीहरूले कब्जामा पारेका छन्।”",
			"18": "<sup>18</sup> जब बिन्यामिनी मानिसले परमेश्वरको पवित्र सन्दूकको घटना बताए, एली ऊ बसेको कुर्सीबाट पछिल्तिर लडे, र तिनको मुन्टो भाँचियो, कारण एली साह्रै बूढा थिए र साह्रै मोटा थिए, उनी त्यही मरे। एलीले चालिस बर्षसम्म इस्राएलाई अगुवाइ गरेका थिए।",
			"19": "<sup>19</sup> महिमाको अन्त भयो\nएलीकी बुहारी, पीनहासकी पत्नी त्यस समय गर्भवती थिईन्। तिनीको नानी जन्माउने समय पुगिसकेको थियो, परमेश्वरको पवित्र सन्दूक कब्जा गरेको घटना साथै ससुरा र लोग्नेको पनि मृत्यु भएको खबर सुन्नेबित्तीकै प्रसववेदना शुरू भयो, यसकारण उसले घुँडा टेकी र तत्कालै एउटा छोरा जन्माईन्।",
			"20": "<sup>20</sup> जब तिनी मर्नै लागेकी थिईन्, तिनलाई स्याहार्ने आइमाईहरूले तिनलाई भने, “नडराऊ! तिम्रो एउटा छोरा भयो।”\nतर एलीकी बुहारीलाई त्यस दुःखको खबरले कुनै असर गरेन।",
			"21": "<sup>21</sup> तर इस्राएलको महिमा हराएको सुनेर तिनले छोराको नाउँ “ईकावोद” राखिन्, जसको अर्थ “इस्राएलको महिमा लगियो!” उनले यो नाउँ राखीन् कारण परमेश्वरको पवित्र सन्दूक लुटिएको र आफ्ना ससुरा अनि लोग्नेको मृत्यु भएको कुराको संकेत गराउँछ।",
			"22": "<sup>22</sup> तिनले भनिन्, “इस्राएलको महिमा हरायो।” कारण पलिश्तीहरूले परमेश्वरको पवित्र सन्दूक कब्जा गरे।"
		},
		"5": {
			"1": "<sup>1</sup> पवित्र सन्दूक पलिश्तीहरूका निम्ति समस्या बन्यो\nपलिश्तीहरूले परमेश्वरको पवित्र सन्दूकलाई एबेन-एजेरबाट अश्दोद पुर्याए।",
			"2": "<sup>2</sup> पलिश्तीहरूले परमेश्वरको पवित्र सन्दूकलाई दागोनको मन्दिरमा लगेर दागोनकै मूर्तिको छेउमा राखे।",
			"3": "<sup>3</sup> भोलिपल्ट बिहान अश्दोदवासीहरूले दागोनको मूर्ति घोप्टो परेर परमेश्वरको पवित्र सन्दूक अघि भूईमा लडिरहेको देखे।\nअश्दोदका मानिसहरूले फेरि त्यो मूर्ति त्यसको ठाउँमा उभ्याए।",
			"4": "<sup>4</sup> अश्दोदवासीहरूले दागोनको मूर्तिलाई फेरि त्यहि ठाउँमा राखे। तर फेरि अर्को बिहान अश्दोदवासीहरूले दागोनको मूर्ति त्यसरी नै परमेश्वरको पवित्र सन्दूक अघिल्तिर लडिरहेको भेटे। यस बेला दागोनको टाउको र हातहरू भाँचिएर संघारको अघि लडिरहेको र जीउ मात्र त्यहाँ लडिरहेको भेटियो।",
			"5": "<sup>5</sup> त्यही व्यवहार आजसम्म चल्दैछ कि अश्दोदमा दागोनका पुरोहित र मन्दिरभित्र पस्नेहरू कसैले पनि दागोनको संघारमा पाइला राख्दैनन्।",
			"6": "<sup>6</sup> परमप्रभुले अश्दोदका मानिसहरू साथै छिमेकीहरूका जीवन पनि कष्ट पूर्ण बनाइदिनुभयो। परमप्रभुले तिनीहरूलाई असाध्यै दुःख दिनुभयो। गिर्खाको रूढी फैलाइ दिनुभयो। हुलका हुल मूसाहरू पठाउँनुभयो। मूसाहरू जहाज र जमिन जताततै दगुरिरहन्थे। शहरवासीहरू सबै डराउन थाले।",
			"7": "<sup>7</sup> जब अश्दोदवासीहरूले यो देखे तिनीहरूले भने, “इस्राएलको परमेश्वरको सन्दूक यहाँ राख्न सकिन्न। परमेश्वरले हाम्रा देवता दागोनलाई मात्र होइन तर उहाँले हामीलाई पनि पूरा दण्ड दिनुभयो।”",
			"8": "<sup>8</sup> अश्दोदका मानिसहरूले पश्लितीका सबै शासकहरूलाई बोलाए। अश्दोदवासीहरूले ती शासकहरूलाई सोधे, “हामीले इस्राएलका परमेश्वरको पवित्र सन्दूकलाई अब के गर्नु पर्ने हो?”\nशासकहरूले भने, “इस्राएलका परमेश्वरको पवित्र सन्दूकलाई अब गात शहरमा पुर्याइयोस्।” यसरी पलिश्तीहरूले परमेश्वरको पवित्र सन्दूक हटाए।",
			"9": "<sup>9</sup> फेरि परमेश्वरको पवित्र सन्दूक जब गातमा पुग्यो तब देखि नै परमप्रभुले त्यस शहरलाई सजाय दिन थाल्नुभयो। मानिसहरूमा खैलाबैला भयो। परमप्रभुले के बालक के वृद्ध सबलाई दुःख दिनथाल्नु भयो। परमेश्वरले गातवासीहरूमा गिर्खाको रूढी फैलाइ दिनुभयो।",
			"10": "<sup>10</sup> यसकारण पलिश्तीहरूले परमेश्वरको सन्दूकलाई एक्रोन पठाए।\nजति बेला परमेश्वरको सन्दूक एक्रोन आइपुग्यो मानिसहरू कराउन थाले, “इस्राएलका परमेश्वरको सन्दूक किन हाम्रो शहर एक्रोनमा ल्याएको? के तिमीहरूले हामीलाई र हाम्रा मानिसहरूलाई मार्न चाहन्छौ?”",
			"11": "<sup>11</sup> एक्रोनका मानिसहरूले पलिश्ती शासकहरूलाई एकसाथ बोलाएर भने, “इस्राएलका परमेश्वरको सन्दूकलाई छिट्टो भन्दा छिट्टो आफ्नै ठाउँमा पठाइदिनु होस्। नत्र भने यसले हामीहरूलाई नष्ट पार्नेछ।”\nएक्रोनका मानिसहरू साह्रै डराएका थिए। परमेश्वरले त्यस शहरवासीहरूको जीवन कष्टमय बनाइदिनु भयो।",
			"12": "<sup>12</sup> मानिसहरू धेरै संख्यामा मरे। जो मरेका थिएनन् तिनीहरूलाई गिर्खाको रूढीले सताउन थाल्यो र एक्रोनका मानिसहरूको रोदन स्वर्गसम्मै पुग्यो।"
		},
		"6": {
			"1": "<sup>1</sup> परमेश्वरको पवित्र सन्दूक फेरि घर लगियो\nपलिश्तीहरूले पवित्र सन्दूकलाई आफ्नो देशमा सात महीनासम्म राखे।",
			"2": "<sup>2</sup> पलिश्तीहरूले आफ्ना पुरोहितहरू र ज्योतिषीहरूलाई बोलाएर भने, “हामीले परमप्रभुको पवित्र सन्दूकलाई के गर्नु पर्ने हो? सुझावदिनुहोस्, कसरी सन्दूकलाई फेरि यसको आफ्नै घरमा पठाउन सकिन्छ?”",
			"3": "<sup>3</sup> पुरोहितहरू र ज्योतिषीहरूले सुझाव दिए, “यदि तिमीहरूले इस्राएलका परमेश्वरको पवित्र सन्दूकलाई पठाउनु भयो भने सौगात नराखी नपठाऊ। तिमीहरूले केही उपहार चढाउनै पर्छ, जसले तिमीहरूको पाप मोचन हुनेछ। त्यसपछि तिमीहरू स्वस्थ्य र पवित्र हुनेछौ। तिमीहरूले यसो गर्नैपर्छ अनि मात्र परमेश्वरले तिमीहरूलाई दण्डित हुनबाट बचाउँनु हुनेछ।”",
			"4": "<sup>4</sup> पलिश्तीहरूले सोधे, “इस्राएलका परमेश्वरलाई कस्तो प्रकारका भेटीहरू दिएमा हामीलाई क्षमा मिल्ने छ?”\nपुरोहितहरू र ज्योतिषीहरूले भने, “यहाँ पलिश्तीहरूका शहरका एक-एकजना प्रमुख गरेर जम्मा पाँचजना प्रमुख छन्। तिमी जस्तै तिम्रो सबै प्रमुखहरूले एउटै विमार भोग्नु परिरहेछ। यसर्थ तिमीहरूले पाँचवटा सुनका गिर्खाको नमूना तयार गर। त्यसरी नै पाँचवटा सुनका मूसाको नमूना पनि तयार गर।",
			"5": "<sup>5</sup> गिर्खाहरू र मूसाका नमूनाहरू बनाऊ त्यसले देशलाई नष्ट गरिरहेको छ। इस्राएलका परमेश्वरलाई ती सुनका नमूनाहरू भुक्तानीको रूपमा प्रदान गर। त्यसपछि यो हुन सक्ला इस्राएलका परमेश्वरले तिमीहरू, तिमीहरूका देवता र तिमीहरूको देशमा दण्ड दिन बन्द गर्नु हुनेछ।",
			"6": "<sup>6</sup> फिरऊन र मिश्रीहरू जस्तै हठी स्वभावका नबन। परमेश्वरले मिश्रीहरूलाई दण्ड दिनुभयो। यसो गर्दा मात्र मिश्रीहरूले इस्राएलीहरूलाई मिश्र छोड्नदिए।",
			"7": "<sup>7</sup> “तिमीहरूले एउटा गाडा बनाउनु पर्छ र कहिले खेत नजोतेका दुइवटा दुहुना गाई बन्दोबस्त गर्नुपर्छ। ती दुइवटा गाईलाई गाडा तान्ने गरी नारेर त्यसका बाछाहरूलाई भने गोठभित्र थुनिदिनुपर्छ। त्यसो गर्दा बाछाले माऊलाई पछ्याउँन पाउँदैनन्।",
			"8": "<sup>8</sup> परमप्रभुको पवित्र सन्दूकलाई त्यस गाडा भित्र राखिदिनु। त्यसैको छेउमा नमूनाको थैलो पनि राखि दिनु। त्यो नमूना चाँहि तिम्रो पाप मोचनका निम्ति परमेश्वरलाई भेटी हो। बन्द गाडालाई सोझो बाटो लगाई देऊ।",
			"9": "<sup>9</sup> त्यसपछि गाडा कता जान्छ ख्याल राख। यदि गाडा बेत-शेमेशपट्टि अघि बढेर इस्राएलको भूमितिर गयो भने त्यसले यो संकेत दिन्छ, परमप्रभुले नै हामीलाई त्यस्तो ठूलो रोग दिनुभएको हो। तर गाई बेत-शेमेश तिर गएन भने हामीले बुझ्नेछौं कि त्यो दण्ड स्वरूप भनिएको देशान इस्राएलका परमेश्वरबाट भएको होइन। हामीले त्यो बिमार आफैं फैलिएको हो भनेर जान्नुपर्यो।”",
			"10": "<sup>10</sup> पलिश्तीहरूले पूरोहित र ज्योतिषीहरूले भने झैं गरे। पलिश्तीहरूले भरखरै ब्याएका दुइवटा गाई खोजेर ल्याए। गाडामा ती गाईहरू नारे र बाछाहरूलाई गोठमा थुनिदिए।",
			"11": "<sup>11</sup> त्यसपछि पलिश्तीहरूले गाडामा परमप्रभुको पवित्र सन्दूक राखे। सुनका मूसाहरू र गिर्खाको नमूनाहरू हालेको थैलो गाडामा (सन्दुको छेउमा) राखे।",
			"12": "<sup>12</sup> गाई गाडा तान्दै सोझै बेत-शेमेशतिर लगे। गाईहरू कराउँदै सडकदेखि दाहिने देब्रे कतै नताकी अघि बढिरहे। पलिश्ती शासकहरू पनि बेत-शेमेशको सीमासम्म पछि-पछि गए।",
			"13": "<sup>13</sup> बेत-शेमेशका मानिसहरू बेंसीमा धमाधम गहूँको फसल काटिरहेका थिए। तिनीहरूले शिर उठाएर हेर्दा पवित्र सन्दूक देखे। तिनीहरूले त्यस सन्दूकलाई फेरि देख्दा साह्रै रमाए र दौडेर लिन गए।",
			"14": "<sup>14</sup> गाडा सोझै बेत-शेमेशका यहोशूको खेतमा एउटा ठूलो चट्टान अगाडि रोकियो।\nबेत-शेमेशका मानिसहरूले त्यो गाडा टुक्रा-टुक्रा पारे। गाईहरूलाई मारेर परमप्रभुलाई बलिको रूपमा चढाए।\nलेवी कुल समूहका मानिसहरूले परमप्रभुको पवित्र सन्दूक उठाए। तिनीहरूले सन्दूक र सुनका नमूनाहरू राखिएका थौला उठाएर त्यो चट्टानमाथि राखे।\nत्यस दिन बेत-शेमेशका मानिसहरूले परमप्रभुलाई होमबलि चढाए।",
			"16": "<sup>16</sup> पलिश्ती शासकहरूले बेत-शेमेशमा घटेका सम्पूर्ण घटना हेरेर त्यही दिन एक्रोनतिर लागे।",
			"17": "<sup>17</sup> यस प्रकार पलिश्तीहरूले आफूले गरेका पापबाट क्षमा पाउनलाई परमप्रभुमा गिर्खाको सुनको नमूना उपहार स्वरूप पठाए। तिनीहरूले पाँचवटा सुनका गिर्खाको नमूना प्रत्येक पलिश्ती शहरमा एक एकवटा पठाए। ती शहरहरू हुन् अश्दोद, गाजा, अश्कलोन, गात र एक्रोन।",
			"18": "<sup>18</sup> पलिश्तीहरूले सुनका मूसाहरूको नमूना पनि पठाए। सुनका मूसाहरूको नमूना पनि त्यति नै संख्यामा थियो जति ती पाँचै पलिश्ती शासकहरूको शहरको संख्या थियो। ती शहरहरूका वरिपरि पर्खालहरू थिए अनि प्रत्येक शहरका वरिपरि बस्तीहरू थिए।\nबेत-शेमेशका मानिसहरूले परमप्रभुको पवित्र सन्दूकलाई जुन चट्टानमा राखे, त्यो चट्टान अहिले पनि बेत-शेमेशका यहोशूको खेतमा छ।",
			"19": "<sup>19</sup> जब बेत-शेमेशका मानिसहरूले परमप्रभुको पवित्र सन्दूक देखे त्यो हेरचाह गर्न तिनीहरूलाई सिकाउने त्यहाँ कुनै पूजाहारी थिएन। यसकारणले बेत-शेमेशेका सत्तरीजना मानिसहरूलाई परमेश्वरले मार्नुभयो। परमप्रभुले कठोर दण्ड दिनुभयो भनेर बेत-शेमेशका मानिसहरूले अलाप विलाप गर्न थाले।",
			"20": "<sup>20</sup> यसैले बेत-शेमेशका मानिसहरूले भने, “यस पवित्र सन्दूकलाई हेरचाह गर्ने पूजाहारी कहाँ छन्? अब यहाँबाट सन्दूक कता लैजाने हो?”",
			"21": "<sup>21</sup> किर्यात्-यारीममा एकजना पूजाहारी छन्। बेत-शेमेशका मानिसहरूले किर्यात्-यारीमका मानिसहरू भएतिर दूत पठाए। दूतले गएर भने, “पलिश्तीहरूले परमप्रभुको पवित्र सन्दूक फिर्ता गरिदिए। तल आएर तपाईंहरूले त्यो आफ्नो शहरमा लानु होस्।”"
		},
		"7": {
			"1": "<sup>1</sup> किर्यात्-यारीमका मानिसहरू आए र परमप्रभुको पवित्र सन्दूक लिएर गए। तिनीहरूले परमप्रभुको सन्दूकलाई पहाडमा भएको अबीनादाबको घरमा लगे। तिनीहरूले अबीनादाबको छोरा एलाजारलाई परमप्रभुको सन्दूकको रक्षा गर्ने तयारीको सम्बन्धमा एउटा विशेष उत्सव मनाए।",
			"2": "<sup>2</sup> किर्यात्-यारीमा उक्त सन्दूक बीस बर्षसम्म रह्यो।\nपरमप्रभुले इस्राएलीहरूलाई रक्षा गर्नुहुन्छ\nइस्राएलका मानिसहरूले परमेश्वरको निम्ति चाहना गरे र फेरि परमप्रभुकै अनुशरण गर्न शुरू गरे।",
			"3": "<sup>3</sup> शमूएलले इस्राएलका मानिसहरूलाई भने, “यदि तिमीहरू साँचो हृदयले परमप्रभुको शरणमा फर्कन्छौ भने तिमीहरूका माझबाट विदेशी देवताहरूलाई फ्याकी दिनुपर्छ। तिमीहरूले अश्तारोतका मूर्ति फ्याँकि देऊ। परमप्रभुमा सम्पूर्ण रूपले समर्पित गर्नुपर्छ। तिनीहरूले परमप्रभु कै मात्र सेवा गर्नुपर्छ! त्यसपछि परमप्रभुले तिमीहरूलाई पलिश्तीहरूका हातबाट बचाउँनु हुनेछ।”",
			"4": "<sup>4</sup> यसरी इस्राएलीहरूले बाल अनि अश्तारोतका मूर्तिहरू फ्याँकिदिए। इस्राएलीहरूले परमप्रभुकै मात्र सेवा गर्नथाले।",
			"5": "<sup>5</sup> शमूएलले भने, “सबै इस्राएलका मानिसहरूलाई मिस्पामा भेला गर। म तिमीहरूका निम्ति परमप्रभुसँग प्रार्थना गर्छु।”",
			"6": "<sup>6</sup> इस्राएलका मानिसहरू मिस्पामा भेला भए। तिनीहरू परमप्रभुको सामु पानी खन्याएर चोखिए। यसरी तिनीहरूले आफ्नो पाप स्वीकार गरे अनि दिनभरी उपवास बसेर तिनीहरूले भने, “हामीले परमप्रभुका विरूद्धमा पाप काम गर्यौं।” यसरी शमूएल इस्राएलको न्यायकर्त्ता र नायक भए।",
			"7": "<sup>7</sup> पलिश्तीहरूले इस्राएलीहरू मिस्पामा भेला हुँदैछन् भन्ने कुरो थाहा पाए। पलिश्ती शासकहरू इस्राएलीहरू विरूद्ध युद्ध लड्न गए। इस्राएलीहरू पनि पलिश्तीहरू आउँदैछन् भन्ने सुनेर डराउन थाले।",
			"8": "<sup>8</sup> इस्राएलीहरूले शमूएललाई भने, “परमप्रभु हाम्रा परमेश्वरसित प्रार्थना गर्नुहोस्। हामीहरूलाई पलिश्तीहरूबाट रक्षा गर्नुहोस् भनि परमप्रभुसित भन्नुहोस्।”",
			"9": "<sup>9</sup> शमूएलले एउटा भेंडाको पाठो लिए। उनले परमप्रभुलाई होमबलि अर्पण गर्दै भेंडाको पाठोलाई पूर्ण रूपले जलाई दिए। शमूएलले इस्राएलका परमप्रभुलाई प्रार्थना चढाए। परमप्रभुले शमूएलको प्रार्थनाको जवाफ दिनुभयो।",
			"10": "<sup>10</sup> जब शमूएलले होमबलि अर्पण गर्दैथिए पलिश्तीहरू युद्ध गर्न अघि बढे। तर परमप्रभुले पलिश्तीहरूका छेवैमा प्रचण्ड गर्जन उत्पन्न गराउनुभयो। पलिश्तीहरूले बुझ्नै सकेनन्। त्यस गर्जनले पलिश्तीहरू डराए र तितर-बितर भए। तिनीहरूका शासकले पनि त्यो अवस्थालाई नियन्त्रण गर्न सकेनन्। यसरी इस्राएलीहरूले पलिश्तीहरूलाई हराए।",
			"11": "<sup>11</sup> इस्राएलका मानिसहरूले मिस्पाबाट पलिश्तीहरूलाई खेद्‌दै गए। तिनीहरूले पलिश्तीहरूलाई आक्रमण गरे अनि बेत-करसम्मै खेदे। बाटै भरी तिनीहरूले पलिश्ती सैनिकहरूलाई मारे।",
			"12": "<sup>12</sup> इस्राएलमा शान्ति फर्कियो\nत्यस पछि शमूएलले एउटा विशेष ढुङ्गालाई स्मारकको रूपमा मिस्पा र शेनको गाऊँको बीचमा राखे र “सहायताको चट्टान”  नाउँ दिए। शमूएलले भने, “परमप्रभुले यस ठाउँसम्म बाटोभरी नै हामीलाई सहायता गर्नुभयो।”",
			"13": "<sup>13</sup> पलिश्तीहरूको पूरै हार भयो। तिनीहरूले फेरि इस्राएलका भूमिमा प्रवेश गरेनन्। शमूएलको जीवनभरी नै परमप्रभु पलिश्तीहरूको विरूद्धमा रहनुभयो।",
			"14": "<sup>14</sup> पलिश्तीहरूले इस्राएलका शहरहरू लिएका थिए। एक्रोन देखि गातसम्मका शहरहरू र त्यसका छेउ छाउका क्षेत्रहरू पनि फिर्ता लिए।\nत्यहाँ अमोरीतास र इस्राएली बीच पनि शान्ति थियो।",
			"15": "<sup>15</sup> शमूएलले आफ्नो सम्पूर्ण जीवनभरी नै इस्राएलीहरूलाई मार्गदर्शन दिए।",
			"16": "<sup>16</sup> शमूएलले इस्राएलका एक ठाउँ देखि अर्को ठाउँ सम्मका सम्पूर्ण मानिसहरूलाई न्याय दिलाए। प्रत्येक बर्ष देशको एक कूनादेखि अर्को कुनासम्म यात्रा गरे। उनी गिलगल, बेतेल र मिस्पाको फेरो मार्थे। यसरी शमूएलले इस्राएलमा कुशल र न्याय पूर्वक शासन गरे।",
			"17": "<sup>17</sup> शमूएलको घर भने रामामा थियो। यसर्थ तिनी सधैँ त्यहाँ जान्थे। शमूएलले त्यस शहर देखि नै इस्राएलको न्याय र शासन गर्थे। परमप्रभुका निम्ति शमूएलले रामामा एउटा वेदी बनाए।"
		},
		"8": {
			"1": "<sup>1</sup> इस्राएलीहरूले राजाको माग गरे\nशमूएल जब वृद्ध भए, तिनले आफ्ना छोराहरूलाई इस्राएलका न्यायकर्त्ता नियुक्त गरे।",
			"2": "<sup>2</sup> शमूएलका जेठा छोराको नाउँ योएल थियो। माहिला छोराको नाउँ चाहिं अबियाह थियो। योएल र अबियाह बेर्शबामा न्यायकर्ता थिए।",
			"3": "<sup>3</sup> तर शमूएलका छोराहरू बाबुको चालमा हिडेनन् योएल र अबियाहले घूस खाने गर्थे। चुपचाप घूस लिएर न्यायलयमा अन्याय गर्थे।",
			"4": "<sup>4</sup> यसर्थ इस्राएलका सबै बूढा-प्रधानहरू शमूएललाई भेट्न रामामा भेला भए।",
			"5": "<sup>5</sup> बूढा-प्रधानहरूले शमूएललाई भने, “तपाईं बूढा हुनुभयो, तपाईंका छोराहरू तपाईं जस्ता ठीक बाटामा छैनन्। अब, हामीलाई शासन अनि न्याय गर्न अरू देशहरूको जस्तै राजा दिनुहोस्।”",
			"6": "<sup>6</sup> यसरी बूढा-प्रधानहरूले तिनीहरूलाई मार्ग दर्शन गर्ने एउटा राजा मागे। शमूएलले ठाने यो युक्ति गलत छ। यसर्थ शमूएलले परमप्रभुमा प्रार्थना गरे।",
			"7": "<sup>7</sup> शमूएललाई परमप्रभुले भन्नुभयो, “मानिसहरूले जसो भन्छन् त्यसै गर। तिनीहरूले तँलाई अस्वीकार गरेका छैनन्। बरू मलाई अस्वीकार गर्दैछन्। तिनीहरूले मलाई तिनीहरूको राजा हुन अस्वीकार गरिरहेका छन्।",
			"8": "<sup>8</sup> तिनीहरूले सधैँ जे गर्थे त्यही गर्दैछन्। मैले तिनीहरूलाई मिश्रबाट बाहिर ल्याएँ। तर तिनीहरूले आज मलाई छाडेर अन्य देवताहरूको सेवा गरे। तिनीहरूले तँलाई पनि त्यस्तै गर्दैछन्।",
			"9": "<sup>9</sup> यसर्थ मानिसहरूका कुरा सुनेर तिनीहरूले भने झैं गर। तर तिनीहरूलाई एउटा चेतावनी भने दिनु। तिनीहरूलाई भनिदिनु राजाले तिनीहरूलाई के गर्छ। एउटा राजाले मानिसहरूलाई कसरी शासन गर्छन्।”",
			"10": "<sup>10</sup> ती मानिसहरूले एउटा राजाको माग गरे। यसकारण परमप्रभुले भन्नु भएका सम्पूर्ण कुराहरू शमूएलले मानिसहरूलाई बताए।",
			"11": "<sup>11</sup> शमूएलले भने, “यदि तिमीहरू तिमीहरूमाथि शासन गर्ने राजा चाहान्छौ भने राजाले यसो गर्नेछन्। उसले तिमीहरूको छोराहरूलाई लाने छन् अनि उसको सेवा गर्न तिनीहरूलाई विवश पार्नेछन्। राजाले तिमीहरूका छोराहरूलाई सैनिक बनाएर उनका रथ सँगसँगै दौडन लगाउँने छन् र घोडचढी बनाएर आफ्नो सैनिकमा राख्नेछन्। तिमीहरूका छोरालाई आफ्ना रथको अघि दौडने रक्षक बनाउने छन्।",
			"12": "<sup>12</sup> “राजाले तिम्रो छोराहरूलाई सैनिक बन्न वाध्य गराउने छन्। त्यसमध्ये कोही 1,000 मानिसहरूका अधिकारी बन्ने छन अनि अरू 50 जना भन्दा ज्यादा मानिसका।\n“राजाले तिम्रा छोरालाई उनको खेत जोत्न र फसल काट्न बाध्य गराउनेछन्। कतिलाई फेरि युद्धको निम्ति हातहतियार बनाउन लाउनेछन्। कतिलाई रथको चीजहरू बनाउँन लाउँनेछन्।",
			"13": "<sup>13</sup> “राजाले तिमीहरूका छोरीहरूलाई पनि लानेछन्। तिनीहरूमध्ये कतिलाई खाना पकाउन र रोटी पकाउन विवश गर्नेछन्। अनि कसैलाई अत्तर बनाउन बाध्य गर्नेछन्।",
			"14": "<sup>14</sup> “राजाले तिमीहरूको सबभन्दा असल खेतहरू, दाखको बगैंचा र भद्राक्षका रूखहरू लानेछन्। अनि तिनीहरूलाई उसको सेवकहरूको हातमा सुम्पिदिनेछन्।",
			"15": "<sup>15</sup> तिमीहरूको अन्न र दाखको फसलको दशौ भाग राजाले लिनेछन्। अनि अधिकारीहरू अनि सेवकहरूको हातमा सुम्पिदिनेछन्।",
			"16": "<sup>16</sup> “राजाले तिमीहरूका कमारा-कमारीहरूलाई पनि लैजाने छन्। उनले असल जातका पशु र गधाहरू पनि तिमीहरूबाट लिनेछन्। यी सबै लगेर आफ्नै प्रयोगमा लगाउँने छन्।",
			"17": "<sup>17</sup> तिमीहरूका भेंडा बाख्राको दशौ भाग पनि तिनले लिनेछन्।\n“अनि तिमी आफैं पनि राजाको दास बनिनेछौ।",
			"18": "<sup>18</sup> तब तिनीहरू आफूले चुनेको राजाबाट छुटकाराको निम्ति रूनेछौ। तर त्यस समय परमप्रभुले कुनै जवाफ दिनुहुने छैन।”",
			"19": "<sup>19</sup> तर मानिसहरूले शमूएलको कुरा सुन्दैसुनेन्। तिनीहरूले भने, “होइन! हामीलाई शासन गर्ने एउटा राजा नै चाहिन्छ।",
			"20": "<sup>20</sup> तब हामी पनि अरू देश सरह हुनेछौ, हामीलाई नेतृत्व दिने, हाम्रो अघि युद्ध लड्ने, हाम्रा निम्ति युद्ध गर्ने हाम्रा राजा हुनेछन्।”",
			"21": "<sup>21</sup> शमूएलले मानिसहरूको भनाइ सुने पछि शमूएलले परमप्रभुलाई तिनीहरूका कुरा दोहोर्याइदिए।",
			"22": "<sup>22</sup> परमप्रभुले जवाफ दिनु भयो, “तिनीहरूले चाहे जस्तै! तिनीहरूलाई एउटा राजा देऊ।”\nतब शमूएलले इस्राएलका मानिसहरूलाई भने, “ठीकै छ! तिमीहरूले एउटा राजा पाउँने भयौ। अहिले सबै तिमीहरूको शहरमा जाऊ।”"
		},
		"9": {
			"1": "<sup>1</sup> शाऊलले पिताका गधाहरूको खोजी गर्छ\nकीश बिन्यामिनी कुलका एकजना महत्वपूर्ण मानिस थिए। कीश अबीएलका छोरा थिए। अबीएल जरोरका छोरा थिए। जरोर बकोरतका छोरा थिए। बकोरत, बिन्यामीन परिवारका मानिस ओपीहकका छोरा थिए।",
			"2": "<sup>2</sup> कीशका एकजना शाऊल नाउँ गरेका छोरा थिए। शाऊल अत्यन्तै सुन्दर थिए। त्यहाँ शाऊल जस्तो सुन्दर कोही पनि थिएनन्। उभिँदा शाऊल इस्राएलमा अन्य मानिसहरू भन्दा अग्ला थिए।",
			"3": "<sup>3</sup> एक दिन शाऊलका पिता कीशले आफ्ना गधाहरू हराए। यसकारण कीशले आफ्ना छोरा शाऊललाई भने, “एकजना नोकरलाई साथमा लिएर गधा खोज्न जा।”",
			"4": "<sup>4</sup> शाऊलले गधाहरू खोज्न शुरू गरे। शाऊल एप्रैमका पहाडी देश हुदै शलीशको इलाकातिर पुगे। तर शाऊल र नोकरले कीशका गधाहरूको पत्तो लगाउन सकेनन्। ती दुइजना गधाहरू खोज्दै शालेमका चारैतिर घुमे। तर त्यहाँ पनि गधाहरू थिएनन्। यसकारण तिनीहरूले बिन्यामिनीको प्रदेशमा पनि गधा खोज्दै यात्रा गरे तर त्यहाँ पनि गधाहरू भेट्टाउन सकेनन्।",
			"5": "<sup>5</sup> अन्त्यमा, शाऊल र उनका नोकर सूप नाउँका शहरमा पुगे। शाऊलले आफ्ना नोकरलाई भने, “हामीहरू फर्कौ। अब पिताजीलाई गधाहरूको भन्दा हाम्रो चिन्ता ज्यादा भइसक्यो होला।”",
			"6": "<sup>6</sup> तर नोकरले भन्यो, “यस शहरमा परमेश्वरका एकजना मानिस हुनुहुन्छ। मानिसहरूले उहाँलाई धेरै मान्छन्। उहाँले जे भन्नुहुन्छ त्यो पुग्छ। यसकारण हामीहरू शहरमा जाऔं। हुनसक्छ परमेश्वरका मानिसले हामीलाई अब कता जानुपर्ने हो भनिदिनु हुन्छ होला।”",
			"7": "<sup>7</sup> शाऊलले आफ्ना नोकरलाई भने, “ठीकै हो, हामी शहरमा जानसक्छौ। तर हामीले त्यस मानिसलाई के दिनसक्छौं र? हामीले बोकेका थैलाका भोजन पनि त सकिसक्यो। हाम्रोमा परमेश्वरका मानिसलाई दिने केही भेंटी पनि छैन। हामीसँग दिने लायकको के छ र!”",
			"8": "<sup>8</sup> फेरि पनि त्यस नोकरले शाऊललाई भन्यो, “हेर्नुहोस्, मसँग अलिकति धनको रूपमा चाँदीको एक चौथाइ शेकेल छ। यही हामीले ती परमेश्वरका मानिसलाई चढायौं भने उहाँले हामीलाई कता जानुपर्ने हो बताउनुहुन्छ।”",
			"9": "<sup>9</sup> शाऊलले आफ्ना नोकरलाई भने, “त्यो असल सुझाव हो! हामी जाऔं!” यसरी तिनीहरू शहरमा गए जहाँ परमेश्वरका मानिस थिए।\nशाऊल र तिनका नोकर पहाड उक्लँदै शहरतिर जाँदै थिए। तिनीहरूले केही जवान स्त्रीहरूलाई बाटोमा भेटे जो पानी भर्न आएका थिए। शाऊल र तिनका नोकरले ती ठिटीहरूलाई सोधे, “के दर्शी घरैमा हुनुहुन्छ?” (प्राचीन समयमा अगमवक्तालाई “दर्शी” भन्थे।)",
			"12": "<sup>12</sup> ठिटीहरूले जवाफ दिए, “ज्यू, उहाँ हुनुहुन्छ। उहाँ तपाईंहरू भन्दा अघि जादै हुनहुन्छ। उहाँ आजै शहरमा आउनुभयो। मानिसहरूले भेला भएर उच्च स्थानहरूमा मेलबलि चढाउने भएका छन्।",
			"13": "<sup>13</sup> कृपया जानुहोस् र उहाँलाई शहरमा भेट्नुहुन्छ। यदि तपाईंहरूले चाँडो गर्नुभयो भने उहाँ दर्शीलाई आराधनालयमा खान जानुभन्दा पहिले भेटिहाल्नुहुन्छ। किनभने दर्शीले बलिदानलाई आशीर्वाद दिनुपर्छ। मानिसहरूले उहाँ त्यहाँ नपुगन्जेल भोजन ग्रहण गर्दैनन्। यसकारण चाँडो जानुहोस्।”",
			"14": "<sup>14</sup> शाऊल र तिनका नोकर शहर पुग्न पहाडको बाटो उक्लन थाले। जस्तो तिनीहरू शहरभित्र पस्दै थिए, त्यस्तै शमूएल उनीहरूका अघिल्तिर देखा परे। शमूएल आराधना स्थानतिर जानलाग्दै थिए।",
			"15": "<sup>15</sup> एक दिन अगाडि परमप्रभुले शमूएललाई भन्नुभएको थियो,",
			"16": "<sup>16</sup> “भोलि म यही समय पारेर एउटा मानिसलाई तँ कहाँ पठाउनेछु। यो मानिस बिन्यामिनी कुलको सदस्य हो। तैंले त्यसलाई अभिषेक गरिदिनुपर्छ। त्यसपछि त्यो मानिस मेरा इस्राएली मानिसहरूका प्रमुख हुनेछ। त्यस मानिसले हाम्रा मानिसहरूलाई पलिश्तीहरूबाट बचाउँने छ। मैले मेरा मानिसहरूको कष्ट देखे र रोदन सुनेकोछु।”",
			"17": "<sup>17</sup> शमूएलले शाऊललाई देखे अनि परमप्रभुले भन्नु भयो, “मैले तँलाई भनेको मानिस यही हो। यसले नै मेरो मानिसहरूलाई शासन गर्नेछ।”",
			"18": "<sup>18</sup> शाऊलले मूल-द्वारमा शमूएल कहाँ आएर भने, “कृपया दर्शी कहाँ हुनुहुन्छ, बताइ दिनुहुन्छ कि?”",
			"19": "<sup>19</sup> शमूएलले भने, “म नै दर्शी हुँ। म भन्दा पहिले नै आराधनाको ठाउँमा जाऊ। तिमी र तिम्रा सेवकले आज मसित खाना खानेछौ। म तिमीलाई भोलि बिहान घर जानदिनेछु र जे सोध्नु छ बिहानै सोध्नु।",
			"20": "<sup>20</sup> ती गधाहरूको चिन्ता नगर जो तीन दिन पहिले हराएका थिए। ती गधाहरू फेला परिसकेका छन्। अब तिमीलाई सारा इस्राएलले नै खोजिरहेकोछ। तिनीहरूले तिमी र तिम्रा पिताका परिवारलाई नै चाहान्छन्।”",
			"21": "<sup>21</sup> शाऊलले जवाफ दिए, “तर म त बिन्यामिनी कुलको सदस्य हुँ। हाम्रो परिवार इस्राएलको सबै भन्दा सानो परिवार समूह हो। त्यस माथि पनि बिन्यामिनी कुलभित्र पनि हाम्रो परिवार समूह सबैभन्दा सानो छ। तपाई किन भन्नुहुन्छ कि इस्राएलले मलाई चाहन्छ?”",
			"22": "<sup>22</sup> त्यसपछि शमूएलले शाऊल र तिनका नोकरलाई भोजनालयतिर लिएर गए। लगभग तीस जना मानिसहरू एकसाथ खाना खान र बलिदानमा भाग गर्न निम्त्याइएका थिए। शमूएलले शाऊल र तिनका नोकरलाई महात्वपूर्ण स्थानमा बसाए।",
			"23": "<sup>23</sup> शमूएलले भान्सेलाई भनें, “जो मैले तिमीलाई दिएको थिएँ त्यो मासु ल्याउ जुन मासुको अंश मैले तिमीलाई सुरक्षित राख्न भनेंको थिएँ।”",
			"24": "<sup>24</sup> भान्सेले फिला ल्यायो अनि शाऊलको अघि टेबलमा राख्यो। शमूएलले भने, “यो त्यही मासु हो जुन मैले तिम्रो निम्ति सुरक्षित राखेको थिएँ। यो खाऊ कारण यो यस्तै विशेष दिनको निम्ति तिम्रो सम्मानको लागि सुरक्षित राखेको थियो।” यसरी त्यस दिन शाऊलले शमूएलसँगै खाना खाए।",
			"25": "<sup>25</sup> खाना खाइसके पछि आराधनालयबाट झर्दै तिनीहरू शहरतिर लागे। शमूएलले शाऊलका निम्ति कौसीमा ओछ्यान लगाए शाऊल त्यहीं सुतें।",
			"26": "<sup>26</sup> भोलिपल्ट बिहानै शमूएलले शाऊललाई उठाउँदै भनें, “उठ, म तिमीलाई बाटो देखाइदिन्छु।” शाऊल उठे अनि शमूएलकासाथ घरबाट बाहिरिए।",
			"27": "<sup>27</sup> शहरको किनारतिर शाऊल, तिनका नोकर र शमूएल अघि बढ्दै थिए। शमूएलले शाऊललाई भने, “तपाईंको नोकरलाई हामी भन्दा अघि हिँड्न भन्नुहोस्। मैले तपाईंलाई परमेश्वरको संदेश सुनाउन बाँकी नै छ।” नोकर तिनीहरू भन्दा केही अघि हिँड्न थाल्यो।"
		},
		"10": {
			"1": "<sup>1</sup> शमूएलले शाऊललाई अभिषेक गरिदिए\nशमूएलले एक ढुँग्रो विशेष तेल लिए र शाऊलको टाउकोमा खन्याई दिए। शमूएलले म्वाई खाँदै भने, “परमप्रभुले तपाईंलाई आफ्ना मानिसहरूका अगुवाको रूपमा अभिषेक गर्नुभएको छ। तपाईंले परमप्रभुका मानिसहरूलाई शासन गर्नुहुनेछ तपाईंले तिनीहरूलाई चारैतिरका शत्रुबाट रक्षा गर्नुपर्छ। परमप्रभुले तपाईंलाई आफ्नो मानिसहरूको शासकको रूपमा अभिषेक गर्नुभएको हो। यहाँ एउटा संकेत छ त्यसले यो सत्य हो भनि प्रमाण दिइने छ।",
			"2": "<sup>2</sup> आज तपाईंले मबाट बिदा भएर जानु भएपछि बिन्यामिनीको सिमानामा भएको राहेलको चिहाननेर सेलमहमा दुइजना मानिसहरू भेट्टाउनु हुनेछ। ती दुइजना मानिसहरूले भन्नेछन्, ‘तपाईंले खोजिरहनु भएको गधाहरू कसैले पाएको छ। तपाईंका पिता गधाहरूबारे चिन्तित हुन छाडेर तपाईंको निम्ति सुर्ता गर्दै भन्दै हुनुहुन्छ: अब म मेरो छोराको लागि के गरूँ?’”",
			"3": "<sup>3</sup> तब शमूएलले भने, “तपाईंहरू ताबेरमा बज्राँठको रूख नआइन्जेलसम्म अघि बढ्नुहोस्। त्यहाँ तीनजना मानिसहरू भेट्नुहुनेछ। तिनीहरू बेतेलमा परमेश्वरको आराधना गर्न गइरहेका हुनेछन्। ती मध्येको पहिलोले तीनवटा पाठा बोकेको हुनेछ। दोस्रोले तीनवटा रोटी र तेस्रोले एक मशक दाखरस बोकेको हुनेछन्।",
			"4": "<sup>4</sup> ती तीनै जना मानिसले तपाईंलाई अभिवादन गर्नेछन्। तिनीहरूले दुइवटा रोटी दिनेछन्। ती दुइवटै रोटी तपाईंले स्वीकार गर्नुपर्छ।",
			"5": "<sup>5</sup> त्यहाँबाट तपाईं गिबियत एलोहिम जानुपर्छ। त्यहाँ पलिश्तीहरूको एउटा चौकी छ। जब तपाईं त्यस शहरमा पुग्नुहुन्छ केही अगमवक्ताहरू निक्लदै गरेको भेट्नु हुनेछ। ती अगमवक्ताहरू आराधनास्थानबाट झर्दै गरेका हुनेछन्। तिनीहरूले अगमवाणीहरू बोलिरहेका हुनेछन्। तिनीहरूले खैंजडी, ढोलक, बाँसुरी र वीणा बजाउँदै गरेका हुनेछन्।",
			"6": "<sup>6</sup> त्यति बेला परमप्रभुको आत्मा तपाईंमाथि उत्रनुहुनेछ र तपाईंलाई शक्ति दिनुहुनेछ, तपाईं परिवर्तन हुनुहुनेछ। तपाईं भिन्न मानिस हुनुहुनेछ। तपाईं पनि ती अगमवक्ताहरूसितै अगमवाणी बोल्नसक्ने हुनुहुन्छ।",
			"7": "<sup>7</sup> यी घटनाहरू घटिसके पछि तपाईं जे चाहनुहुन्छ गर्न सक्नुहुन्छ। किनकी परमेश्वर तपाईंसित हुनुहुनेछ।",
			"8": "<sup>8</sup> “म भन्दा पहिले गिलगाल जानुहोस्। त्यसपछि म तपाईं भएकोमा आउने छु। म त्यहाँ होमबलि र मेलबलि चढाउँनेछु। तर तपाईंले सात दिन पर्खनु पर्नेछ। तब म आउनेछु अनि तपाईंले के गर्नुपर्छ म बताउँनेछु।”",
			"9": "<sup>9</sup> शाऊल अगमवक्ताको रूपमा\nजब शाऊल शमूएलबाट बिदा लिन फर्के, परमेश्वरले शाऊलको जीवन नै अर्कै बनाइदिनुभयो। त्यही दिन यी सबै घटनाहरू घटे।",
			"10": "<sup>10</sup> शाऊल र तिनको नोकर गिबियथ एलोहिम गए। त्यहाँ शाऊलले अगमवक्ताहरूको एक समूहलाई भेटे। परमेश्वरको आत्मा शक्तिसाथ शाऊलमाथि तुरून्तै उत्रियो अनि शाऊलले पनि ती अगमवक्ताहरूको समूहमा अगमवाणी गर्नुभयो।",
			"11": "<sup>11</sup> ति-जसले शाऊललाई पहिले देखि चिन्थे उनीहरूले शाऊललाई अगमवाणी गरेका देखे। यसर्थ तिनीहरूले एक अर्कासित प्रश्न गर्न थाले, “कीशको छोरालाई के भयो? के शाऊल पनि अगमवक्ताहरू मध्ये एउटा हो?”",
			"12": "<sup>12</sup> गिबियथ एलोहिमका एकजनाले भने, “हो! यस्तो देखिंदैछ, ऊ नै तिनीहरूका गुरू हुन्।” यसैकारण यो भनाइ प्रचलित छः “के शाऊल पनि एउटा अगमवक्ता नै हो?”",
			"13": "<sup>13</sup> शाऊल घर पुग्छन्\nशाऊलले अगमवाणी बोल्न छोडेपछि घरकै छेउको स्थानमा आराधना गर्न गए।",
			"14": "<sup>14</sup> शऊलका काकाले शाऊललाई र त्यस नोकरलाई भने, “कता गएका थियौ?”\nशाऊलले भने, “हामी गधाका खोजीमा थियौं। जब हामीहरूले तिनीहरूलाई पाउँन सकेनौ त्यसपछि हामी शमूएललाई भेट्न गयौं।”",
			"15": "<sup>15</sup> शाऊलका काकाले भने, “मलाई भन त, शमूएलले के-के भने?”",
			"16": "<sup>16</sup> शाऊलेले भने, “शमूएलले हामीलाई गधाहरू पाइसकेको छ भने।” शाऊलले प्रत्येक कुराहरू बताएनन्। राज्यको बारेमा शमूएलले भनेको कुरा बताएनन्।",
			"17": "<sup>17</sup> शमूएलले शाऊललाई राजा घोषित गर्छन्\nशमूएलले सबै इस्राएलका मानिसहरूलाई मिस्पामा परमप्रभुकहाँ भेला हुन बोलाए।",
			"18": "<sup>18</sup> शमूएलले इस्राएलका मानिसहरूलाई भने, “परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ, ‘मैले इस्राएललाई मिश्रबाट बाहिर ल्याएँ। मैले तिमीहरूलाई मिश्रको अधीनबाट बचाएँ र अन्य राष्ट्रहरूबाट पनि बचाएँ जसले तिमीहरूलाई चोट पुर्याउन चाहन्थे।’",
			"19": "<sup>19</sup> तर आज तिमीहरूले आफ्नो परमेश्वरलाई अस्वीकार गर्यौ। तिमीहरूका परमेश्वरले तिमीहरूलाई सबै दुःख र कष्टहरूबाट बचाउनुभयो। तर तिमीहरू भन्छौ, ‘होइन, हामीलाई शासन गर्ने एउटा राजा चाहिन्छ।’ यसैकारणले अब परमप्रभुको अगाडि परिवारका सदस्यहरू र परिवार समूहहरू लिएर उपस्थित होऊ।”",
			"20": "<sup>20</sup> शमूएलले इस्राएलका सबै परिवार समूहलाई उपस्थित गराए। त्यसपछि शमूएलले नयाँ राजा छान्न थाले। पहिलो बिन्यामिनी कुल समूह चुनियो।",
			"21": "<sup>21</sup> शमूएलले बिन्यामिनी कुलसमूहका प्रत्येकलाई एक-एकजना गरेर अगाडि जानु भने। त्यसपछि मन्त्रीको परिवारालाई चुनियो। त्यसपछि त्यही मन्त्री परिवारको प्रत्येकलाई एक एकजना गरेर अगाडि जानु भने। यसरी कीशका छोरा शाऊललाई चुनियो।\nजब मानिसहरूले शाऊललाई खोजे, त्यहाँ शाऊललाई भेट्टाएनन्।",
			"22": "<sup>22</sup> अनि तिनीहरूले परमप्रभुसित सोधे, “शाऊल यहाँ अझै आइपुगेकै छैनन्?”\nपरमप्रभुले भन्नुभयो, “त्यो त्यहाँ भेटी चढाइएका सामानहरूको पछि लुकेकोछ।”",
			"23": "<sup>23</sup> मानिसहरूले शाऊललाई सामानहरूको पछाडिबाट खोजेर निकाले। शाऊल मानिसहरूका माझमा खडा भए। त्यहाँ शाऊल अरू भन्दा अग्ला थिए।",
			"24": "<sup>24</sup> शमूएलले सबै मानिसहरूलाई भने, “परमप्रभुले चुन्नु भएको मानिसलाई हेर। मानिसहरूमा शाऊल जस्तो मानिस अर्को कोही छैन।”\nत्यसपछि मानिसहरूले नारा लगाए, “हाम्रा राजा दीर्घायु रहुन्।”",
			"25": "<sup>25</sup> शमूएलले राज्यका नियमहरू मानिसहरूलाई बुझाए। यो एउटा पुस्तकमा पनि लेखे र यस व्यवस्थाको पुस्तकलाई परमप्रभुको अघि राखे। तब तिनले मानिसहरूलाई घर जानु भने।",
			"26": "<sup>26</sup> शाऊल पनि गिबामा आफ्ना घर गए। अनि केही बीर पुरूषहरू शाऊलसित गए। अनि परमेश्वरले यी वीर पुरूषहरूको हृदय छुनुभयो।",
			"27": "<sup>27</sup> तर कोही बद्‌माशहरूले भने, “यो मानिसले हामीलाई कसरी रक्षा गर्छ?” तिनीहरूले शाऊलको विरूद्धमा नराम्रो भन्न थाले र उपहार नदिनेभए। तर शाऊलबाट कुनै प्रतिक्रिया भएन।\nअम्मोनीहरूका राजा नाहाश\nअम्मोनीका राजा नाहाशले गाद र रूबेन कुल समूहरूलाई सताईरहेका थिए। नाहाशले प्रत्येक पुरूषको एउटा दाहिने आँखा निकाल्न लगायो। नाहाशले तिनीहरू कसैलाई पनि सहायता गर्न दिएनन्। अम्मोनीका राजा नाहाशले यर्दन नदीको पूर्वतिर बस्ने प्रत्येक इस्राएली मानिसहरूको दाहिने आँखा निकाले। तर 7,000 इस्राएली मानिसहरू अम्मोनीबाट भागेर याबेश गिलाद आए।"
		},
		"11": {
			"1": "<sup>1</sup> झण्डै एक महिना पछि अम्मोनी नाहाश र तिनका सेनाहरूले याबेश गिलादलाई चारैतिरबाट घेरे। याबेशका सबै मानिसहरूले नाहाशलाई भने, “यदि तपाईंले हामीसँग सन्धि गर्नुभयो भने हामी तपाईंका प्रजा बन्न तयार छौ।”",
			"2": "<sup>2</sup> तर अम्मोनी नाहाशले भने, “यदि मैले जम्मै इस्राएलीहरूका दाहिने आँखा घोंचेर निकाल्न सकें भने म तिमीहरूसित सन्धि गर्नेछु। तब मात्र सारा इस्राएलको बेइज्जत हुनेछ।”",
			"3": "<sup>3</sup> याबेशका प्रमुखले भने, “हामीलाई सात दिनको समय दिनुहोस्। सारा इस्राएलमा हामी दूत पठाउँछौ। यदि कोही पनि सहायता गर्न आएनन् भने हामी आएर तपाईंको अघि समर्पित हुनेछौं।”",
			"4": "<sup>4</sup> शाऊलले याबेश गिलादलाई बचाउँछन्\nती दूतहरू गिबामा आए जहाँ शाऊल बस्थे। तिनीहरूले मानिसहरूलाई समाचार दिए। मानिसहरू समाचार सुनेर रून थाले।",
			"5": "<sup>5</sup> शाऊल आफ्ना खेतबाट गाईहरू लिएर फर्कंदै थिए। शाऊलले खेतको अघि आउँदा मानिसहरू रोइरहेका सुने। शाऊलले भने, “मानिसहरूलाई के कष्ट भयो? किन रोइरहेकाछन्।”\nमानिसहरूलाई याबेशका दूतहरूले जे खबर ल्याएका थिए शाऊललाई बताए।",
			"6": "<sup>6</sup> शाऊले ध्यान दिएर सबै सुने। त्यति नै बेला शाऊलमाथि परमेश्वरको आत्मा उत्रिनुभयो। शाऊल साह्रै रिसाए।",
			"7": "<sup>7</sup> शाऊलले एक जोडी गाई ल्याए अनि टुक्रा-टुक्रा पारे। अनि ती मासुका टुक्राहरू सबै ती खबर ल्याउने दूतहरूलाई दिए। तिनले ती सबै मासुको टुक्राहरू इस्राएलभरी पुर्याउन दूतहरूलाई आदेश दिए। यो सन्देश इस्राएलीहरूलाई दिनुभनि तिनीहरूलाई भने, “आऊ अनि शाऊल र शमूएललाई अनुशरण गर। यदि कसैले यसो गर्दैन भनि तिनीहरूलाई पनि उसको गाईलाई गरेको व्यवहार गरिनेछ!”\nपरमप्रभुबाट मानिसहरूमा ज्यादै डर पस्यो। यसकारण मानिसहरू एकसाथ सबै एउटै मानिसजस्ता भई निस्के।",
			"8": "<sup>8</sup> शाऊलले सबै मानिसहरूलाई बेजेकमा एकसाथ भेला गराए। त्यहाँ इस्राएलका 3,00,000 र यहूदाका 30,000 मानिसभेला भए।",
			"9": "<sup>9</sup> शाऊल र तिनका सैनिकहरूले याबेशका दूतहरूलाई भने, “गिलादमा याबेशका मानिसहरूलाई भन भोलि मंध्यान्ह भित्र तिमीहरूको उद्धार हुनेछ।”\nदूतहरूले शाऊलको सु-सन्देश याबेशका मानिसहरूलाई दिए। याबेशका मानिसहरू अतिनै खुशी भए।",
			"10": "<sup>10</sup> त्यसपछि याबेशका मानिसहरूले अम्मोनी नाहाशलाई भने, “भोलि हामी तपाईंकहाँ आउनेछौं। तपाईंलाई जे इच्छा लाग्छ हमीलाई त्यही गर्नुहोस्।”",
			"11": "<sup>11</sup> भोलिपल्ट शाऊलले आफ्ना सैनिकहरूलाई तीन खण्डमा बाँडे। सूर्योदयको समयपछि शाऊल र तिनका सैनिकहरू अम्मोनीहरूको छाउनी भित्र पसे। जब शाऊलले आक्रमण गरे यो रातमा बस्ने रक्षक दलको अदला-बदली गर्ने समय थियो, शाऊल र तिनका सैनिकहरूले अम्मोनीहरूलाई मध्याह्र भन्दा पहिले नै हराए। सबै अम्मोनी सैनिकहरू तितर-वितर भए। दुइजना अम्मोनी सैनिक एकसाथ हुने मौका कतै पाएनन्।",
			"12": "<sup>12</sup> तब मानिसहरूले शमूएललाई भने, “ती मानिसहरू कहाँ छन् जसले शाऊललाई राजाको रूपमा शासन गरेको हेर्न चाहेनन्। ती मानिसहरू हाम्रा सामु झिकाईउन्। हामी तिनीहरूलाई मार्छौ।”",
			"13": "<sup>13</sup> तर शाऊलले भने, “होइन, आज कसैलाई मार्नु हुँदैन। आज परमप्रभुले इस्राएलको रक्षा गर्नु भएको छ।”",
			"14": "<sup>14</sup> त्यसपछि शमूएलले मानिसहरूलाई भने, “आऊ, हामी गिलगाल जाऊँ। गिलगालमा हामी शाऊललाई फेरि राजाकोरूपमा घोषणा गरौं।”",
			"15": "<sup>15</sup> यसरी सबै गिलगाल गए। त्यहाँ परमप्रभुको अगाडि शाऊललाई मानिसहरूले राजा बनाए। तिनीहरूले परमप्रभुलाई मेलबलि पनि चढाए। शाऊल र सबै इस्राएलीहरूले ठूलो हर्षमा उत्सव मनाए।"
		},
		"12": {
			"1": "<sup>1</sup> शमूएलले राजाबारे बताए\nशमूएलले सबै इस्राएलीहरूलाई भने, “तिमीहरूले भनेको प्रत्येक कुरा मैले गरे। तिमीहरू माथि राजा नियुक्त गरें।",
			"2": "<sup>2</sup> अब तिमीहरूलाई अगुवाई गर्नको निम्ति राजा हुनुहुन्छ। म बुढो भएँ तर मेरा छोराहरू तिमीहरूसित छन्। जब म एउटा ठीटो थिएँ, त्यस बेला देखि तिमीहरूका अगुवा थिएँ, त्यस बेला देखि तिमीहरूका अगुवा भएर आएँ।",
			"3": "<sup>3</sup> अहिले म यहाँ छु। यदि मैले कुनै भूलचुक गरेको भए परमप्रभुको अघि अनि परमप्रभुले चुन्नु भएको राजाअघि तिमीहरूले भन्नुपर्छ। के मैले कसैको गाई अथवा गधा चोरें? कसैलाई ठगे वा कष्ट पुर्याएँ? के मैले पैसा अथवा एक जोडी जुत्ता कहिले घुस लिएकोछु? यदि मैले यस्तो केही गरेको भए त्यसलाई म सच्याउनेछु।”",
			"4": "<sup>4</sup> सबै इस्राएलीहरूले जवाफ दिए, “होइन! तपाईंले हामी कसैलाई पनि केही नराम्रो गर्नुभएन। हामीलाई कहिले पनि ठग्नुभएन र केही पनि लिनुभएन।”",
			"5": "<sup>5</sup> शमूएलले इस्राएलीहरूलाई भने, “परमप्रभु र उहाँले चुन्नु भएको राजा गवाही हुनुहुन्छ। तिमीहरूले जे-जे भन्दैछौ उहाँ दुइजनाले सुन्दै हुनुहुन्छ। उहाँहरूलाई पनि थाहा भयो तिमीहरूले मलाई कुनै दोष दिएनौ।” सबै मानिसहरूले एकसाथ जवाफ दिए, “साँच्चै हो! परमप्रभु नै गवाही हुनुहुन्छ!”",
			"6": "<sup>6</sup> तब शमूएलले मानिसहरूलाई भने, “हो परमप्रभु हाम्रो गवाही हुनुहुन्छ। परमप्रभुले नै मोशा र हारूनलाई चुन्नुभयो। अनि उहाँ नै हुनुहुन्छ जसले तिम्रा पिता-पुर्खाहरूलाई मिश्रबाट बाहिर ल्याउनु भयो।",
			"7": "<sup>7</sup> अब तिमीहरू त्यहाँ खडा होऊ, म तिमीहरूलाई परमप्रभुले तिम्रा पिता-पुर्खाहरू र तिमीहरूका लागि गर्नुभएका ती राम्रा अर्थहरू बताउँछु।",
			"8": "<sup>8</sup> “याकूब मिश्र गए पछि मिश्र बासीहरूले उसका सन्तानहरूको जीवनलाई धेरै कष्टमय बनाए। यसर्थ तिनीहरूले परमप्रभुलाई सहायताको निम्ति रोइकराइ गरे। परमप्रभुले मोशा र हारूनलाई पठाउनु भयो। मोशा र हारूनले तिमीहरूका पिता-पुर्खाहरूलाई मिश्रबाट बाहिर लिएर आए अनि यही ठाउँमा बस्ने व्यवस्था मिलाई दिए।",
			"9": "<sup>9</sup> “तर तिम्रा पिता-पुर्खाहरूले परमप्रभु परमेश्वरलाई त्यागे। यसकारण परमप्रभुले तिनीहरूलाई सीसराका दास हुन दिनुभयो। सीसरा हासोरका सेनाका सेनापति थिए। त्यसपछि परमप्रभुले तिनीहरूलाई पलिश्ती र मोआबका राजाका हातमा सुम्पिदिनुभयो। तिनीहरू सबै तिम्रा पिता-पुर्खाहरूको विरूद्ध लडे।",
			"10": "<sup>10</sup> तर तिमीहरूका पिता-पुर्खाहरूले रूँदै परमप्रभुलाई सहायता मागे। तिनीहरूले भने, ‘हामीले पाप गर्यौ। हामीले परमप्रभुलाई छाडेर झूटो देवता बाल र देवी अश्तारोतको सेवा गर्यौ। अब हामीलाई हाम्रा शत्रुहरूबाट बचाउनुहोस्, हामी तपाईंका सेवक बन्नेछौ।’",
			"11": "<sup>11</sup> “यसकारण परमप्रभुले यरूब्बाल, बाराक, यिप्तह अनि शमूएललाई त्यहाँ पठाउनुभयो। परमप्रभुले तिमीहरूको चारैतिरका शत्रुहरूबाट तिमीहरूलाई रक्षा गर्नुभयो यसकारण तिमीहरू सुरक्षित रह्यौ।",
			"12": "<sup>12</sup> त्यस समयमा पनि अम्मोनीको राजा नाहाश तिमीहरूको विरूद्ध लड्न आएको देख्यौ। तिमीहरूले भन्यौ, ‘होइन, हामीहरूमाथि शासन गर्ने राजा नै चाहिन्छ।’ तिमीहरूले यही भन्यौ यद्यपि परमप्रभु तिमीहरूका परमेश्वर नै राजा हुनुहुन्थ्यो।",
			"13": "<sup>13</sup> अब तिमीहरूले चुनेका राजा यहाँ छन्। परमप्रभुले तिमीहरूमाथि राजालाई नियुक्ति गर्नुभयो।",
			"14": "<sup>14</sup> यदि तिमीहरूले उहाँलाई सम्मान गर्यौ भने परमप्रभुले तिमीहरूको रक्षा गर्नु हुन्छ। तर तिमीहरूले परमप्रभुको डर राख्दै सम्मान गर्नु पर्नेछ। आदेश पालनगर्दै सेवा गर्नुपर्ने छ। उहाँको विरूद्धमा जाने छैनौ। तिमीहरूका साथै तिमीहरूलाई शासन गर्ने राजाले पनि परमप्रभु तिमीहरूका परमेश्वरको अनुशरण गर्नुपर्छ। यदि तिमीहरूलाई भनेका कुराहरू पालन गर्छौ भने परमेश्वरले तिमीहरूलाई रक्षा गर्नुहुनेछ।",
			"15": "<sup>15</sup> तर यदि परमप्रभुले भनेका तिमीहरूले मानेनौ र उहाँको विरूद्धमा गयौ भने, उहाँ पनि तिमीहरू र तिमीहरूका शासकको विरूद्धमा जानुहुनेछ।",
			"16": "<sup>16</sup> “अब अझै चुपचाप उभिएर त्यो अचम्मको कामलाई हेर, जुन परमप्रभुले तिमीहरूका आँखा अगाडि गर्नुहुनेछ।",
			"17": "<sup>17</sup> यो गहुँको फसल काट्ने समय हो। म परमप्रभुसँग प्रार्थना गर्नेछु। म उहाँसँग बिजुली चम्कँदै पानी पार्ने बिन्ती गर्नेछु। तब तिमीहरूलाई यो थाहा लाग्ने छ कि तिमीहरूले परमप्रभुको विरूद्धमा राजा मागेर भूल काम गरेका छौ।”",
			"18": "<sup>18</sup> यसकारण शमूएलले परमप्रभुसँग प्रार्थना गरे। त्यही दिन परमप्रभुले मेघ गर्जनसित पानी बर्षाउनु भयो। यसकारणले गर्दा मानिसहरू परमप्रभु र शमूएलसँग साह्रै डराए।",
			"19": "<sup>19</sup> सबै मानिसहरूले शमूएललाई भने, “दया गरेर परमप्रभु तपाईंका परमेश्वरलाई हाम्रो निम्ति प्रार्थना गरिदिनु होस्, तपाईंका सेवकहरूका निम्ति हामीहरूलाई नष्ट हुन नदिनु होस्। हामीले धेरै चोटी पाप गरेका छौ। हामीले राजा मागेर फेरि अर्को पाप थपेका छौं।”",
			"20": "<sup>20</sup> शमूएलले जवाफ दिए, “नडराऊ! यो सत्य हो! तिमीहरूले त्यस्ता नराम्रा कामहरू गर्यौ। तर परमप्रभुलाई पछ्उन नछोड। हृदय देखि नै परमप्रभुको सेवा गर।",
			"21": "<sup>21</sup> देवताका मूर्तिहरू केवल मूर्ति मात्र हुन्। त्यसले तिमीहरूलाई सहायता गर्दैन। यसर्थ त्यसको पूजा नगर। देवताका मूर्तिहरूले न त तिमीहरूलाई सहायता गर्न सक्छन् न त रक्षा नै गर्न सक्छन्। तिनीहरू केहि होइनन्।",
			"22": "<sup>22</sup> “तर परमप्रभुले आफ्ना मानिसहरूलाई छोडनु हुँदैन। परमप्रभु तिनीहरूलाई आफ्नो मानिस बनाएर प्रसन्न हुनुहुन्थयो। यसर्थ उहाँले आफ्नो नाउँको खातिर तिनीहरूलाई त्याग्नु हुनेछैन।",
			"23": "<sup>23</sup> अनि म तिमीहरूका निम्ति प्रार्थना गर्न छोड्ने छैन। यदि मैले तिमीहरूका लागि प्रार्थना गरिनँ भने परमप्रभु विरूद्ध मेरो पाप हुनेछ। म तिमीहरूलाई शिक्षा दिइरहन्छु ताकि तिमीहरूले राम्रो र सही जीवन जिउनेछौ।",
			"24": "<sup>24</sup> तर तिमीहरूले परमप्रभुको डर अनि आदर गर्नैपर्छ। तिमीहरूको सम्पूर्ण हृदयले परमप्रभुको सेवा हुनुपर्छ। उहाँले तिमीहरूका निम्ति गर्नुभएको अचम्मका कार्यहरूलाई सम्झौं।",
			"25": "<sup>25</sup> तर यदि तिमीहरूले हठी र लगातार दुष्ट कार्य गरिरह्यौ भनेः परमेश्वरले तिमीहरू र तिमीहरूका राजालाई नष्ट गर्नु हुनेछ।”"
		},
		"13": {
			"1": "<sup>1</sup> शऊलले पहिलो भूल गर्छन्\nत्यसबेला शाऊलले राजाको रूपमा एक बर्ष विताइसकेका थिए। जब इस्राएलमा शासन गरेको दुइ बर्ष भएको थियो,",
			"2": "<sup>2</sup> तिनले इस्राएलबाट 3,000 पुरूषलाई छाने। त्यसमध्ये 2,000 पुरूष मिकमाशमा बेतेलको पहाडी प्रदेशमा रहनथाले। त्यहाँ 1,000 मानिसहरू बिन्यामीनको अन्तरगत गिबामा जोनाथनसित रहनथाले। बाँकी रहेका सैनिकहरूलाई घर पठाइदिए।",
			"3": "<sup>3</sup> जोनाथन गिबामा गए र पलिश्तीहरूलाई तिनीहरूकै छाउनीमा हराए। पलिश्तीहरूले यो सुने। तिनीहरूले भने, “हिब्रूहरूले विद्रोह गरे।”\nशाऊलले मानिसहरूलाई सारा इस्राएल भरी तुरही फुक्न आज्ञा गरे र भने, “जे भएको छ हिब्रूहरूले सुनोस्।”",
			"4": "<sup>4</sup> सबै इस्राएलीहरूले यो खबर सुने। तिनीहरूले भने, “शाऊलले पलिश्तीका अगुवाहरूलाई मारे। अब भने पलिश्तीहरूले साँच्चै इस्राएलीहरूलाई घृणा गर्नेछन्।”\nइस्राएलीहरूलाई शाऊलसँग भेट्नका निम्ति गिलगालमा बोलाइयो।",
			"5": "<sup>5</sup> पलिश्तीहरू इस्राएलसित लड्नलाई भेला हुनथाले। पलिश्तीहरूसँग 3,000 रथ र 6,000 घोडसवारी थिए। बालुवाका कण किनारमा थुप्रिए झैं पलिश्ती सैनिकहरू थुप्रिएका थिए। मिकमाशमा पलिश्तीहरूले छाउनी हालेका थिए। (मिकमाश बेत-आवेनको पूर्वतिर पर्छ।)",
			"6": "<sup>6</sup> इस्राएलीहरूले बुझे कि तिनीहरू खतरामा थिए। तिनीहरूलाई पलिश्तीहरूले धेरा हालेका थिए अनि तिनीहरू गुफा चर्कीएको चट्टानमा दगुरेर गए कुवा र अरू दुलोहरू तिर लुके।",
			"7": "<sup>7</sup> कोही हिब्रूहरू त यर्दन नदी पार गरेर गाद अथवा भागेर गिलाद तिर पुगे। शाऊल भने गिलगालमा नै थिए। तिनका सम्पूर्ण सैनिकहरू डरले कामिरहेका थिए।",
			"8": "<sup>8</sup> शमूएलले भने उनी शाऊलसित गिलगालमा भेट्ने छन्। शाऊलले सात दिनसम्म पर्खे। तर शमूएल गिलगाल जाँदै गएनन् यसर्थ शाऊललाई सैनिकहरूले छोड्न शुरू गरे।",
			"9": "<sup>9</sup> त्यति बेला शाऊलले भने, “मेरो निम्ति होमबलि र मेलबलि जुटाइदेऊ।” त्यसपछि शाऊलले होमबलि चढाए।",
			"10": "<sup>10</sup> तब शाऊलले बलि भेटी चढाऊन सके, शमूएल पनि त्यहाँ आइपुगे। शाऊल तिनीलाई भेट्न गए।",
			"11": "<sup>11</sup> शमूएलले सोधे, “तपाईंले यो के गर्नुभयो?”\nशाऊलले जवाफ दिए, “धमाधम सैनिकहरूले मलाई छोड्नलागे। तपाईं पनि यहाँ हुनुहुन्न थियो, अनि पलिश्तीहरू मिकमाशमा भरिएका थिए।",
			"12": "<sup>12</sup> मैले सोचें, ‘पलिश्तीहरू यहाँ गिलगालमा आएर मलाई आक्रमण गर्नेछन्। मैले अहिलेसम्म परमप्रभुलाई सहायताको निम्ति बिन्ती पनि गरिन। अन्त्यमा बाध्य भएर मैले होमबलि चढाएँ।’”",
			"13": "<sup>13</sup> शमूएलले भने, “तपाईंले मूर्खता पूर्ण काम गर्नुभयो। तपाईंले परमप्रभु तपाईंका परमेश्वरको आदेशको पालन गर्नुभएन! यदि तपाईंले परमेश्वरको आदेशको उङ्घन नगरेको भए परमेश्वरले तपाईंको परिवारलाई सदाको निम्ति इस्राएलको शासन दिनुहुन्थ्यो।",
			"14": "<sup>14</sup> अब तपाईंको शासन अझ अघि बडने छैन। परमप्रभुले यस्तो मानिस खोज्नु भएको थियो जसले उहाँको आदेश पालन गर्छ। परमप्रभुले उहाँले चाहेको जस्तो मानिस भेट्टाउनु भएको छ र परमप्रभुले त्यही मानिसलाई आफ्ना मानिसहरूको प्रमुख बनाउँनु हुनेछ। तपाईंले परमप्रभुको आदेश पालन गर्नु भएन यसकारण उहाँले अर्को नया राजा छान्नु हुनेछ।”",
			"15": "<sup>15</sup> त्यसपछि शमूएल उठे र गिलगाल छाडेर गए।\nमिकमाशको युद्ध\nशाऊल र उसका केही बँचेका सैनिकहरूले गिलगाल छोडे। तिनीहरू बिन्यामीनको गिबातिर गए। शाऊलले अहिलेसम्म आफ्ना साथ नछोडने मानिसहरूको गन्ती गरे। तिनीहरू प्रायः 600 जति रहेछन्।",
			"16": "<sup>16</sup> शाऊल, तिनका छोरा जोनाथन र उनीसित भएका सैनिकहरू बिन्यामीन कहाँ गिबामा गए।\nपलिश्तीहरूले मिकमाशमा छाउनी हालेका थिए।",
			"17": "<sup>17</sup> पलिश्तीहरूले त्यहाँ रहेका इस्राएलीहरूलाई दण्ड दिने निश्चय गरे। तिनीहरूका शक्तिशाली सैनिकहरूले आक्रमण शुरू गरे। पलिश्ती सैनिकहरू तीन भागमा बाँडिएका थिए। पहिलो दल उत्तरतिरको ओप्रा जाने सडकबाट शूआल तर्फ लागे।",
			"18": "<sup>18</sup> दोस्रो दक्षिण पूर्व बेथ-होरोन जाने सडक तर्फ लागे तेस्रो दल चाहि पूर्वतिरको सीमासम्म पुग्ने सडकतिर लागे। यो सडक सबोइमको बेंसीमा मरुभूमि तर्फ फैलिएर गएको थियो।",
			"19": "<sup>19</sup> इस्राएलका मानिसहरूले फलामका हात हतियार बनाउन जान्दैनथे। त्यस समय इस्राएलमा लोहार पाइँदैनथ्यो। पलिश्तीहरूले इस्राएलीहरूलाई फलामको हात हतियार बनाउन सिकाएनन् कारण इस्राएलीहरू आफैंले फलामको हात हतियार बनाए भने पलिश्तीहरूलाई खतरा हुन्छ।",
			"20": "<sup>20</sup> पलिश्तीहरूले मात्र हात हतियारमा उध्याउँथे। यदि इस्राएलीहरूलाई आ-आफ्ना हात हतियार जस्तै, फाली, कोदालो, बञ्चरो र हँसिया उध्याउँनु पर्यो भने तिनीहरू पलिश्तीहरू भएको मै जानुपर्थ्यो।",
			"21": "<sup>21</sup> पलिश्ती लोहारहरूले फाली र कोदालोमा साँध लगाउन शेकेलको दुइ-तिहाई चाँदी लिन्थे भने बञ्चरो र गोरू धपाउने अंकुसे मिलाउनलाई शेकेलको एक-तिहाईं लाग्थ्यो।",
			"22": "<sup>22</sup> यसर्थ युद्धका दिनहरूमा शाऊलका सैनिकसित न त फलामको तरवार थियो न त भाला नै। खालि शाऊल र तिनका छोरा जोनाथनसित फलामको हतियार थियो।",
			"23": "<sup>23</sup> पलिश्तीहरूको एक दल सैनिकले मिकमाशको बेंसीलाई पूर्ण सुरक्षित तुल्याइरहेका थिए।"
		},
		"14": {
			"1": "<sup>1</sup> जोनाथनले पलिश्तीहरूमाथि आक्रमण गर्छन्\nएक दिन शाऊलका छोरा जोनाथनले तिनका हतियार बोक्ने युवकलाई भने, “हिंडे र बेंसीको पल्लो पट्टि पलिश्तीहरूको छाउनीमा जाऔं।” तर जोनाथनले यो कुरा आफ्ना पितालाई भनेन।",
			"2": "<sup>2</sup> शाऊल पहाडको किनारा मिग्रोनमा एउटा अनारको रूखमुनि बसिरहेका थिए। छेवैमा खला पनि थियो। शाऊलसँग प्राय 600 मानिसहरू थिए।",
			"3": "<sup>3</sup> एकजनाको नाउँ अहियाह थियो। अहिया ईकाबोदका भाइ अहीतूबका छोरा थिए। ईकाबोद पीनहासका छोरा थिए। पीनहास एलीका छोरा थिए। अहिले एली शीलोमा परमप्रभुको पूजाहारी थिए। अहियाह पूजाहारी थिए अनि तिनले एपोद लगाएका थिए।\nयी मानिसहरूले जोनाथन गइसकेको कुरा चाल पाएनन्।",
			"4": "<sup>4</sup> वाटोको दुवै पट्टि ठूला-ठूला चट्टानहरू थिए। जोनाथनले त्यही बेंसीको बाटो भएर पलिश्तीहरूको छाउनीमा पुग्ने निश्चय गरे। बाटोको एकापट्टि बोसेस नाउँ भएको एउटा ठूला चट्टान थियो र अर्को तर्फ सेन नाउँ गरेको अर्को चट्टान थियो।",
			"5": "<sup>5</sup> एउटा ठूलो चट्टान भने उत्तरतिर मिकमाशलाई हेरिरहे झैं देखिन्थ्यो। अर्को चट्टानले भने दक्षिणतिर गिबालाई हेरिरहे झैं देखिन्थ्यो।",
			"6": "<sup>6</sup> जोनाथनले आफ्नो हतियार बोक्ने युवा सहयोगीलाई भने, “आऊ, ती विदेशीहरूको छाउनीमा जाऔ। हुन सक्छ, परमप्रभुले तिनीहरूलाई डराउनको निम्ति हामीलाई प्रयोग गर्नुहुनेछ। हामीसँग ज्यादा सैनिक होस् कि नहोस् परमप्रभुलाई कसैले रोक्न सक्दैन।”",
			"7": "<sup>7</sup> जोनाथनको हतियार बोक्ने युवकले भन्यो, “तपाईंलाई जे राम्रो लाग्छ त्यहि गर्नुहोस्। म त तपाईंको साथमा छँदैछु।”",
			"8": "<sup>8</sup> जोनाथनले भन्यो, “जाऔ! त्यो बेंसीबाट भएर सिधै पलिश्ती रक्षकहरू भएतिर जाऔं। हामी तिनीहरूले देख्ने गरी अघि बढौं।",
			"9": "<sup>9</sup> यदि तिनीहरूले हामीलाई, ‘तिमीहरू त्यही उभिनु जबसम्म हामी त्यहाँ आइपुग्दैनौं’ भन्छन् भने हामी जहिंको तहीं उभिनेछौं। हामी अघि बढ्दैनौं।",
			"10": "<sup>10</sup> तर यदि पलिश्तीहरूले हामीहरूलाई ‘अगाडि बढ’ भने, हामी तिनीहरू भएसम्म चढ्छौं। किनभने यो परमेश्वरको चिन्ह हो यो चिन्हको अर्थ हुनेछ परमप्रभुले तिनीहरूलाई हराउनु हुनेछ।”",
			"11": "<sup>11</sup> यसर्थ जोनाथन र तिनका सहयोगी पलिश्तीहरूले देख्ने गरी उभिए। पलिश्ती रक्षकहरूले भने, “हेर! हिब्रूहरू लुकेको खाल्टोबाट निस्केर आउँदैछन्।”",
			"12": "<sup>12</sup> किल्लाको रक्षक पलिश्तीहरूले जोनाथन र तिनका सहायकलाई कराएर भने, “यहाँ आऊ, तिमीहरूलाई हामी पाठ सिकाउँने छौं।”\nजोनाथनले आफ्नो सहयोगीलाई भन्यो, “पहाड चढ्दा मेरो पछि आउनु, परमेश्वरले यसरी मिलाउनु भएको छ कि इस्राएलीहरूले पलिश्तीहरूलाई हराउने छ।”",
			"13": "<sup>13</sup> जोनाथन हात खुट्टा टेकेर पहाडमाथि उक्ले। तिनका सहायक पनि पछि लागे। जोनाथन र तिनका सहायकले पलिश्तीहरूलाई आक्रमण गरे। पहिलो आक्रमणमा तिनीहरूले बीसजना पलिश्तीहरूलाई आधा एकर जमीनमा मारे। जोनाथनले उसको अघाडि आएकाहरूसित लडाई गरिनै रहे। पछाडिबाट तिनका सहायक आए अनि घाईतेहरूलाई मारे।",
			"15": "<sup>15</sup> मैदान, छाउनीमा र किल्लामा भएका सबै पलिश्ती सैनिकहरू डराउन थाले। यहाँसम्म भयो कि पलिश्तीहरूका वीरभन्दा वीर सैनिक पनि डराए। जमीन काम्यो र पलिश्ती सैनिकहरू साह्रै नै डराए।",
			"16": "<sup>16</sup> शाऊलका रक्षकहरूले बिन्यामीन देशको गिबामा भएका पलिश्ती सैनिकहरू चारै दिशातिर भाग-भाग गरेको देखे।",
			"17": "<sup>17</sup> शाऊलले आफ्ना छेवैमा भएका सैनिकहरूलाई भने, “सैनिकहरूको गन्ती गर। म यो जान्न चाहन्छु कि कसले छाउनी छोडे।”\nतिनीहरूले गन्ती गरे। छाउनीमा जोनाथन र तिनका सहायक थिएनन्।",
			"18": "<sup>18</sup> शाऊलले अहियाहलाई भने, “परमेश्वरको पवित्र सन्दूक ल्याऊ!” (त्यस समय सन्दूक इस्राएलीहरूसँग नै थियो।)",
			"19": "<sup>19</sup> शाऊल पूजाहारी अहियाहसँग कुरा गर्दैथिए। शाऊलले परमेश्वरको सुझाव पर्खिरहेका थिए। तर पलिश्तीहरूको छाउनीमा होहल्ला र अशान्ती अझै बढ्दै थियो। शाऊलको धैर्य खतम भइसकेको थियो। अन्त्यमा पूजाहारी अहियालाई शाऊलले भने, “अब भयो प्रार्थना गर्न बन्द गर, हात तल झार।”",
			"20": "<sup>20</sup> शाऊलले आफ्ना सैनिकहरूलाई एकत्रित पारे र युद्धको निम्ति निस्किए। पलिश्ती सेनाहरू साँच्चै नै अलमल्ल परिरहेका थिए। तिनीहरूले एक-अर्कामा तिनीहरूको तरवार लिएर झगडा़ गर्न शुरू गरे।",
			"21": "<sup>21</sup> केही हिब्रूहरू पनि पलिश्तीहरूसित तिनीहरूका नोकरहरू जस्तै त्यही छाउनीमा थिए। तिनीहरूले पनि पलिश्तीहरूलाई आक्रमण गरे। तिनीहरू पनि इस्राएली शाऊल र जोनाथनसित मिले।",
			"22": "<sup>22</sup> पलिश्ती सैनिकहरू भाग्दै छन् भन्ने खबर एप्रैमका पहाडी प्रदेशमा लुकेका सबै इस्राएलीहरूले सुने। यसैले ती इस्राएलीहरू पनि युद्धमा सामेल भएर पलिश्तीहरूलाई खेद्न थाले।",
			"23": "<sup>23</sup> यसरी परमप्रभुले इस्राएलीहरूलाई त्यस दिन रक्षा गर्नुभयो। तिनीहरूले बेत-आवेनको पारिसम्मै लडाँईं लडे। सबै सैनिकहरू शाऊलका साथ थिए त्यहाँ झण्डै 10,000 पुरूष थिए। एप्रैमका पहाडी देशको प्रत्येक शहरमा युद्ध फैलियो।",
			"24": "<sup>24</sup> शाऊलले दोस्रो चोटि भूल गर्छन्\nत्यस दिन शाऊलले ठूलो भूल गरे। इस्राएलीहरू भोका र थकित थिए, कारण शाऊलले शपथ खान लगाएका थिए, शाऊलको शपथ थियो, साँझ अघि कसैले केही खाने छैन। “अथवा मैले मेरो शत्रुहरूलाई पराजित नगरून्जेलसम्म खानेकुरा खाए त्यसलाई दण्ड दिइनेछ।” यसर्थ कुनै इस्राएली सैनिकले केही खाएनन्।",
			"25": "<sup>25</sup> युद्धको कारणले मानिसहरू जंगलमा गए। तिनीहरूले भुईमा महको चाको देखे। मह भएसम्म तिनीहरू पुगेर पनि मह खाएनन्। शपथ तोडिने हुनाले डराए।",
			"27": "<sup>27</sup> तर जोनाथनलाई शपथ बारे केही थाह थिएन। जोनाथनले आफ्ना पिताले जबरजस्ती मानिसहरूलाई शपथ खुवाएको कुरा सुनेका थिएनन्। एउटा लट्टी जोनाथनसँग थियो। उनले महको चाकामा लट्टी घुसारे र लठ्ठीमा मह ल्याएर केही मह खाए र फूर्तिलो अनुभव गरे।",
			"28": "<sup>28</sup> एउटा सैनिकले जोनाथनलाई भन्यो, “तपाईंका पिताले मानिसहरूलाई एउटा शपथ खान लगाए। तपाईंका पिताले मानिसहरूलाई केही खाएमा दण्डित गरिने शपथ खुवाए। यसैले मानिसहरू भोकभोकै छन्। यसैले गर्दा मानिसहरू कमजोरी भए।”",
			"29": "<sup>29</sup> जोनाथनले भने, “मेरो पिताले देशलाई समस्यामा हाल्नुभयो। हेर त मैले अलिकति मह चाखेको मात्र थिएँ कस्तो फूर्तिलो भएँ।",
			"30": "<sup>30</sup> आज मानिसहरूले शत्रु पक्षबाट जे-जे भोजन ल्याएका थिए, ती सब खान पाएका भए कति राम्रो हुन्थ्यो। हामीले अझ धेरै पलिश्तीहरूलाई मार्न सक्थ्यौं।”",
			"31": "<sup>31</sup> त्यस दिन इस्राएलीहरूले पलिश्तीहरूलाई हराए। तिनीहरू मिकमाश देखि अय्यालोन सम्मका बाटोभरी लडे। यसैले मानिसहरू थकित र भोका थिए।",
			"32": "<sup>32</sup> तिनीहरूले पलिश्तीहरूबाट भेंडा, गाई र बाछाहरू खोसेका थिए। त्यस बेला इस्राएलका मानिसहरू यति भोका थिए कि तिनीहरूले ती पशुहरूलाई त्यही जमिनमा मारे र खाए। जबसम्म पशुहरूमा रगत थियो।",
			"33": "<sup>33</sup> शाऊललाई एउटा मानिसले भन्यो, “हेर्नुहोस्! मानिसहरूले परमप्रभुको विरूद्धमा पाप गर्दैछन्। तिनीहरूले रगतै समेत मासु खाँदैछन्!”\nशाऊलले भने, “तिमीहरूले पाप गर्दैछौ। यहाँ एउटा ठूलो ढुङ्गालाई पल्टाऊ।”",
			"34": "<sup>34</sup> फेरि शाऊलले भने, “मानिसहरूलाई गएर भन कि प्रत्येक मानिसले साँढे र भेंडा लिएर मकहाँ आओस्। यहाँ तिनीहरूले आफ्ना साँढे र भेंडा मार्नुपर्छ। परमप्रभु विरूद्ध पाप काम नगर! रगतै समेतको मासु नखाओ।”\nत्यस रात प्रत्येकले आ-आफ्नो पशुहरू ल्याएर त्यहाँ मारे।",
			"35": "<sup>35</sup> शाऊलले परमप्रभुको निम्ति वेदी बनाए। शाऊल आफैंले वेदी बनाउन शुरू गरे।",
			"36": "<sup>36</sup> शाऊलले भने, “आज राती हामी पलिश्तीहरूलाई लखेट्ने छौं। हामीले तिनीहरूबाट प्रत्येक बस्तु खोस्ने छौं। हामीले तिनीहरू सबैलाई मार्ने छौं।”\nसैनिकहरूले जवाफ दिए, “तपाईंलाई जे राम्रो लाग्छ, त्यही गर्नुहोस्।”\nतर पूजाहारीले भने, “परमेश्वरलाई सोध।”",
			"37": "<sup>37</sup> यसकारण शाऊलले परमेश्वरलाई सोधे, “के म पलिश्तीहरूलाई पछ्याउँदै खेदौं? के मलाई पलिश्तीहरूलाई हराउन दिनुहुन्छ?” तर परमेश्वरबाट कुनै जवाफ आएन।",
			"38": "<sup>38</sup> यसले गर्दा शाऊलले भने, “सबै नेताहरूलाई मेरो समक्ष बोलाउनु, आज कसले पाप काम गर्यो, बुझौ।",
			"39": "<sup>39</sup> म इस्राएलको रक्षा गर्ने परमप्रभुको शपथ खाँदै यो प्रतिज्ञा गर्छु, यहाँ सम्म कि मेरो छोरा जोनाथनले नै पाप गरेको छ भने पनि छोडिने छैन मर्ने छ।” तर कसैले पनि उनलाई केही भनेनन्।",
			"40": "<sup>40</sup> यसकारण शाऊलले सबै इस्राएलीहरूलाई भने, “तिमीहरू सबै एकतिर उभिनु। म र मेरो छोरा जोनाथन अर्कोतिर उभिन्छौ।”\nसबै सैनिकले भने, “तपाईंको जो इच्छा हजुर।”",
			"41": "<sup>41</sup> तब शाऊलले प्रार्थना गर्न लागे, “परमप्रभु, इस्राएलको परमेश्वर, तपाईंले दासलाई आज किन जवाफ दिनुभएन, यदि मैले अथवा मेरो छोरा जोनाथनले कुनै पाप गरेको भए परमप्रभु इस्राएलका परमेश्वर ऊरीम दिनुहोस्। यदि तपाईंका इस्राएली मानिसहरूले पाप गरेका भए तुम्मीम दिनुहोस्।”\nयस प्रकारले शाऊल र जोनाथन चुनिएका थिए अन्य मानिसहरू चुनिएका थिएनन्।",
			"42": "<sup>42</sup> शाऊलले भने, “अब म र मेरो छोरामा कसलाई दोष दिनु पर्ने हो? को दोषी हो?” जोनाथन चुनिएका थिए।",
			"43": "<sup>43</sup> शाऊलले जोनाथनलाई भने, “मलाई भन्, तैले के गरिस्।”\nजोनाथनले शाऊललाई भन्यो, “मैले अलि कति लट्टीमा लागेको मह चाखें। यत्तिले नै म मर्नु पर्ने?”",
			"44": "<sup>44</sup> शाऊलले भने, “यदि मैले गरेको प्रतिज्ञा पूरा गरिनँ भने परमेश्वरले मलाई खूबै नराम्रो गर्नुहुनेछ। यसर्थ जोनाथन मर्नै पर्छ।”",
			"45": "<sup>45</sup> तर सबै सैनिकाहरूले भने, “आज जोनाथनले इस्राएललाई विजयी गरायो। के जोनाथन मर्नै पर्ने? होइन! हामी जीवित परमेश्वरको नाउँमा शपथ खान्छौ कि जोनाथनलाई कसैले हानि गर्न सक्दैन, उसको शिरको एउटा केशसम्म कसैले हल्लाउन सक्ने छैन। परमेश्वरले आज पलिश्तीहरूसँगको युद्धमा जोनाथनलाई सहायता गर्नुभएको छ।” यसरी मानिसहरूले जोनाथनलाई बचाए। तिनलाई मृत्युदण्ड दिइएन।",
			"46": "<sup>46</sup> शाऊलले पलिश्तीहरूलाई खेदेनन्। पलिश्तीहरू आफ्नै ठाउँमा फर्किए।",
			"47": "<sup>47</sup> शाऊल इस्राएलका शत्रुसँग लडे\nशऊलले इस्राएलमा पूरा अधिकार जमाए। शाऊल इस्राएल वरिपरिका शत्रुहरूसित लडे। शाऊल मोआबी, अम्मोनी, एदोमी, सीबाका राजा र पलिश्तीहरूसँग लडे। जहाँ-जहाँ गएर लडे शाऊलले इस्राएलका शत्रुहरूलाई हराए।",
			"48": "<sup>48</sup> शाऊल अत्यन्तै साहसी थिए। शाऊलले इस्राएलका मानिसहरूबाट सामान चीज-बीज खोस्न खोज्नहरूको हातबाट पनि इस्राएलीहरूलाई बचाए, शाऊलले यहाँ सम्म कि अमालेकीहरूलाई पनि हराए।",
			"49": "<sup>49</sup> शाऊलको छोराहरू जोनाथन, यिश्वी र मल्कीशूअ थिए। शाऊलकी जेठी छोरीको नाउँ मेराब र कान्छी छोरीको नाउँ मीकल थियो।",
			"50": "<sup>50</sup> शाऊलकी पत्नीको नाउँ अहीनोम थियो। अहीनोमचाहि अहीमासकी छोरी थिइन्।\nशाऊलका सेनापति नेरका छोरा अबनेर थिए। नेरचाहिं शाऊलका काका थिए।",
			"51": "<sup>51</sup> शाऊलका पिता कीश र अबनेरका पिता नेरचाहि अबीएलका छोराहरू थिए।",
			"52": "<sup>52</sup> शाऊल जीवनभरी नै साहसी थिए। पलिश्तीहरू विरूद्ध कठिन युद्ध लडे। जहिले पनि कुनै वीरलाई देखेमा शाऊलले आफ्नो सैनिक दलमा त्यसलाई सामेल गर्थे र आफ्नो छेवैमा राखेर राजाको रक्षक बनाउँथे।"
		},
		"15": {
			"1": "<sup>1</sup> शाऊलले अमालेकीहरूलाई नष्ट गरे\nएक दिन शमूएलले शाऊललाई भने, “परमप्रभुले मलाई उहाँका इस्राएली मानिसहरूको राजाको रूपमा तिमीलाई अभिषेक गर्न पठाउनु भएको थियो। अब परमप्रभुको भनाइ सुन।",
			"2": "<sup>2</sup> सर्वशक्तिमान परमप्रभु भन्नुहुन्छ, ‘जब इस्राएलीहरू मिश्रबाट बाहिर आए तब अमालेकीहरूले तिनीहरूलाई कनान जानलाग्दा रोक्ने चेष्टा गरे। अमालेकीहरूले जे गरे मैले देखे।",
			"3": "<sup>3</sup> अब गएर अमालेकीहरूसित युद्ध गर। अमालेकीहरूसित जे जति छ सम्पूर्ण रूपले नष्ट गर। कसैलाई जिउँदो नछाड। तिमीहरूले ती सबलाई मार्नु पर्छ, लोग्ने मानिसहरू होस् कि स्त्रीहरू केटा-केटीहरू होस् कि काखका नानी। सबलाई मार गाई-गोरू, भेंडा ऊँट गधा केही बाँकी नराख।’”",
			"4": "<sup>4</sup> शाऊलले सबै सेनाहरूलाई तेलैममा भेला गराए। त्यहाँ 2,00,000 पैदल सेना र 10,000 अन्य मानिसहरू थिए। यसमा यहूदाका मानिसहरू पनि थिए।",
			"5": "<sup>5</sup> त्यसपछि शाऊल अमालेक शहरतिर लागे र बेंसीमा पर्खेर बसे।",
			"6": "<sup>6</sup> शाऊलले केनीहरूलाई भने, “तिमीहरू सबैले अमालेकीहरूलाई छोडी देऊ। म तिमीहरूलाई अमालेकीहरूसित मार्दिन। कारण तिमीहरूले मिश्रबाट आउँदा इस्राएलीहरूलाई दया गरेका थियौ।” यसपछि केनीहरूले अमालेकीहरूलाई छोडे।",
			"7": "<sup>7</sup> शाऊलले अमालेकीहरूलाई हराए, तिनले हवीलाबाट शूरतक मिश्रको सीमानासम्म अमालेकीहरूलाई खद्‌दै गए।",
			"8": "<sup>8</sup> शाऊलले अमालेकीहरूका राजा अगागलाई जिउँदै पक्रे र अगागका सबै सैनिकलाई मारे।",
			"9": "<sup>9</sup> शाऊल र इस्राएली सैनिकहरूले सम्पूर्ण कुराहरू नष्ट गर्दा नराम्रो मन गरे। यसर्थ तिनीहरूले अगागलाई मारेनन् र बलिया गाईहरू राम्रा जातका भेंडा र पाठाहरू पनि बचाएर राखे। राख्न योग्यका सामानहरू राखेर काम नलाग्ने जति नष्ट गरे।",
			"10": "<sup>10</sup> शमूएलले शाऊलबाट भएको पाप बताइदिए\nशमूएलले परमप्रभुबाट खबर पाए।",
			"11": "<sup>11</sup> परमप्रभुले भन्नुभयो, “शाऊलले मलाई पछ्याउन छोडीदियो। यसर्थ म शाऊललाई राजा बनाएर आफैं दुःखी छु। म जे भन्छु त्यसले त्यो गर्दैन।” शमूएल धेरै रिसाए र रातभरी परमप्रभुसित प्रार्थना गरे।",
			"12": "<sup>12</sup> शमूएल भोलिपल्ट बिहान चाँडै नै उठे र शाऊललाई भेट्न गए। तर मानिसलहरूले शमूएललाई भने, “शाऊल यहूदाको कर्मेल भन्ने ठाउँमा गएका छन्। शाऊल त्यहाँ उसको सम्मानमा एउटा स्मारक स्थापित गर्न गएका छन्। त्यसपछि धेरै ठाउँ भ्रमण गरेर तिनी अन्त्यमा गिलगाल जानेछन्।”\nत्यसपछि शमूएल शाऊल भएको ठाउँमा गए। अमालेकीहरूबाट लिएका चीजबिजको पहिलो अंश शाऊलले भेंटीको रूपमा चढाए, शाऊलले तिनीहरूलाई परमप्रभुको अघि एउटा होमबलि जस्तै चढाए।",
			"13": "<sup>13</sup> शमूएल शाऊलको नजिक गए। शाऊलले स्वागत जनाएर भने, “परमप्रभुको आशीर्वाद तपाईंमा रहोस्। मैले परमप्रभुको आज्ञा पालन गरें।”",
			"14": "<sup>14</sup> शमूएलले भने, “त्यसो हो भने म यो के सुन्दैछु। किन मैले भेंडा र पशुहरूको आवाज सुनेको?”",
			"15": "<sup>15</sup> शाऊलले भन्यो, “सैनिकहरूले अमालेकीहरूबाट पशुहरू लिए। सैनिकहरूले राम्रा भेंडा र पशुहरू परमप्रभु तपाईंको परमेश्वरमा बलिचढाउन भनेर बचाए। तर हामीले अरू सबै चीज नष्ट पार्यौ।”",
			"16": "<sup>16</sup> शमूएलले शाऊललाई भने, “चुप लाग! हिजो राती परमप्रभुले मलाई के भन्नुभयो त्यो म तपाईंलाई बताउनेछु।”\nशाऊलले भने, “ठीक छ, के भन्नु भयो भन्नुहोस्।”",
			"17": "<sup>17</sup> शमूएलले भने, “विगतमा, तपाईंले आफैंलाई तुच्छ सम्झनु हुन्थ्यो। तर त्यसपछि तपाईं इस्राएलका कुलहरूका अगुवा हुनुभयो। इस्राएलको राजाको रूपमा परमप्रभुले तपाईंलाई चुन्नुभयो।",
			"18": "<sup>18</sup> परमप्रभुले तपाईंलाई एउटा विशेष सेवाकार्यको निम्ति पठाउनु भयो। परमप्रभुले भन्नुभयो, ‘अमालेकीहरूकहाँ जाऊ, अनि तिनीहरूलाई नष्ट पार किनभने तिनीहरू खराब मानिस हुन्।’",
			"19": "<sup>19</sup> तपाईंले परमप्रभुको कुरा सुन्नु भएन। किन तपाईंले ती चीजहरू राख्न चाहनुहुन्छ। तपाईंको यो कार्य परमप्रभुको इच्छा विरूद्धको कार्य भयो।”",
			"20": "<sup>20</sup> शऊलले भने, “तर मैले त परमप्रभुको सबै कुरा मानें मलाई जहाँ पठाउँनु भयो त्यहीं गएँ। मैले अमालेकीहरू सबैलाई नष्ट गरे। मैले खाली साथमा एकजना मानिस ल्याएँ तिनीहरूका राजा अगाग।",
			"21": "<sup>21</sup> गिलगालमा परमप्रभु तपाईंका परमेश्वरलाई बलि चढाउन भनेर। सैनिकहरूले राम्रा-राम्रा भेंडा र पशुहरू लिए।”",
			"22": "<sup>22</sup> शमूएलले जवाफ दिए, “परमप्रभुलाई के ले खुशी पार्छ होमबलि र बलिदानहरू चढाएर कि उहाँको आज्ञालाई पालन गरेर? उहाँलाई बलि भेट दिएर भन्दा उहाँको प्रत्येक आदेशलाई पालन गर्नु राम्रो हो। भेंडाको बोसो चढाउनु भन्दा पहिले परमेश्वरको वचन सुन्नु नै राम्रो हो।",
			"23": "<sup>23</sup> आज्ञा पालन नगर्नु चाहि तन्त्र-मन्त्रको पाप जस्तै हो। हठी स्वभावको हुनु र आफ्नै इच्छानुसार हिँड्नुचाहिँ मूर्ति पूजा गरे झैं पाप हो। तपाईंले परमप्रभुको आज्ञा मान्न इन्कार गर्नुभयो। यही कारणले, अब परमप्रभुले पनि तपाईंलाई राजाको रूपमा इन्कार गर्नुभयो।”",
			"24": "<sup>24</sup> शाऊलले शमूएललाई भने, “मैले पाप गरें। मैले परमप्रभुको आज्ञा तपाईंले भन्नु भए जस्तै गरिनँ। म मानिसहरूसित साह्रै डराएँ अनि तिनीहरूले जे भने माने।",
			"25": "<sup>25</sup> अब म बिन्ती गर्दछु यो पाप गरेको निम्ति मलाई क्षमा गरिदिनुहोस्। मतिर फर्किनु होस्, यसकारण म परमप्रभुको आराधाना गर्न सकौं।”",
			"26": "<sup>26</sup> तर शमूएलले शाऊललाई भने, “म तपाईंसँग जाँदिन। तपाईंले परमप्रभुको वचनलाई उलंघन गर्नुभयो अनि अहिले परमप्रभुले पनि राजा अस्वीकार गरिदिनुभएको छ।”",
			"27": "<sup>27</sup> जब शमूएले शाऊललाई छोडेर हिंड्न खोजे, शाऊलले शमूएलको खास्टोको किनार समाते र त्यो च्यातियो।",
			"28": "<sup>28</sup> शमूएलले शाऊललाई भने, “जसरी तपाईंले मेरो खास्टो च्याति दिनुभयो। यसरी नै परमप्रभुले आज इस्राएलको राज्य तपाईंबाट च्यात्नु भएकोछ। परमप्रभुले इस्राएलको राज्य तपाईंको साथीहरू मध्ये एकजनालाई दिनुभयो। त्यो मानिस तपाईं भन्दा असल छ।",
			"29": "<sup>29</sup> परमप्रभु इस्राएलका परमेश्वर हुनुहुन्छ। परमप्रभु शाश्वत हुनुहुन्छ। परमप्रभु न त झूटो बोल्नु हुन्छ न त विचार बदल्नु हुन्छ। परमप्रभु मानिस जस्तै छिन-छिनमा विचार बदल्ने हुनुहुन्न।”",
			"30": "<sup>30</sup> शाऊलले भने, “ठीक छ, मैले पाप गरें। दया गरी मसित आइदिनुहोस्। इस्राएलका नेताहरू र मानिसहरूका अघि मेरो केही इज्जत राखिदिनु होस्। मसित आइदिनु होस्। ताकि म परमप्रभु तपाईंको परमेश्वरलाई आराधना गर्न सकुँ।”",
			"31": "<sup>31</sup> शमूएल शाऊलसँग गए अनि शाऊलले परमप्रभुलाई आराधना गरे।",
			"32": "<sup>32</sup> शमूएलले भने, “अमालेकीहरूका राजा अगागलाई मेरो सामु ल्याऊ।”\nअगागलाई शमूएलको अघि ल्याइयो। साङ्गलाले बाँधिएको अगागको मनमा थियो, “मलाई निश्चय पनि मार्दैनन् होला भनेर।”",
			"33": "<sup>33</sup> तर शमूएलले अगागलाई भने, “तेरो तरवारले नानीहरूलाई आमाको काखबाट खोस्यो। त्यसकारण अब तेरो आमाले तिनका छोरा-छोरीहरू गुमाउने छन्।” यति भनेर गिलगालमा परमप्रभुको अघि शामूएलले अगागलाई टुक्रा-टुक्रा बनाएर काटिदिए।",
			"34": "<sup>34</sup> त्यसपछि शमूएल त्यहाँबाट हिंडे र रामा गए। शाऊल चाहि गिबामा आफ्नो घर फर्के।",
			"35": "<sup>35</sup> त्यसपछि शमूएलले शाऊललाई जीवनभरी कहिले भेटेनन्। शमूएल शाऊलसँग साह्रै दुःखी थिए। परमप्रभु पनि शाऊललाई इस्राएलका राजा बनाएकोमा दुःखी हुनुहुन्थ्यो।"
		},
		"16": {
			"1": "<sup>1</sup> शमूएल बेतलेहेम जान्छन्\nपरमप्रभुले शमूएललाई भन्नुहुन्छ, “कति दिन शाऊलको निम्ति दुःखीत भइरहन्छस्? शाऊललाई मैले इस्राएलको राजपदबाट हटाइसकें तरै पनि त्यसको दुःख मानिरहन्छस्। तँसित भएको सींङ तेलले भर र बेतलेहेम जा। म तँलाई बेतलेहेमको यिशै नाउँको मानिसकहाँ पठाउँदैछु। यिशै बेतलेहेममा बस्छ। मैले त्यसकै एकजना छोरालाई नयाँ राजाको रूपमा चुनें।”",
			"2": "<sup>2</sup> शमूएलले भने, “यदि म गएँ भने यो समाचार शाऊलले सुन्ने छ र मलाई मार्ने छ।”\nपरमप्रभुले भन्नुभयो, “बेतलेहेममा जा साथमा एउटा बाछा पनि लानु र भन्नु, ‘परमप्रभुलाई बलि चढाउन आएको।’",
			"3": "<sup>3</sup> यिशैलाई बलि चढाउन बोलाउनु। त्यसपछि म तँलाई के गर्नु पर्छ भन्ने छु। तैंले त्यस मानिसलाई अभिषेक गर्नु जुन म तँलाई देखाउँनेछु।”",
			"4": "<sup>4</sup> शमूएलले त्यसै गरे जो परमप्रभुले तिनलाई गर्नु भन्नु भएको थियो। शमूएल बेतलेहेम गए। बेतलेहेमका बूढा-प्रधानहरू डरले कामे। तिनीहरूले शमूएललाई भेटे र सोधे, “के तपाईं शान्ति पूर्वक आउँनु भयो?”",
			"5": "<sup>5</sup> शमूएलले भने, “ज्यू, म शान्ति पूर्वक आएँ। म परमप्रभुलाई बलिदान चढाउन आएको हुँ। तपाईंहरू पनि पूजाका निम्ति तयार भएर बलिदान चढाउन मसित आउनु होस्।” शमूएलले यिशै र तिनका छोराहरूलाई तयार पारे। त्यस पछि शमूएलले तिनीहरूलाई बलिदान चढाउन बोलाए।",
			"6": "<sup>6</sup> जब यिशै र तिनका छोराहरू आइ पुगे, शमूएलले एलीआबलाई देखे। शमूएलले सोचे, “निश्चय पनि परमप्रभुले छान्नु भएको मानिस यिनै हुनु पर्छ।”",
			"7": "<sup>7</sup> तर परमप्रभुले शमूएललाई भन्नु भयो, “एलीआब अग्लो र सुन्दर छ। तर यस्तो विचार गर्नु हुदैन। परमेश्वरले ती चीजहरू हेर्नु हुन्न जो मानिसहरूले हेर्छन्। मानिसले बाहिरी रूप मात्र हेर्छ, तर परमप्रभुले उसको हृदय हेर्नुहुन्छ। एलीआब त्यो ठीक मानिस होइन।”",
			"8": "<sup>8</sup> त्यसपछि यिशैले तिनका दोस्रो छोरा अबीनादाबलाई बोलाए। अबीनादाब शमूएलकै छेउ भएर हिंडे। तर शमूएलले भने, “होइन, यसलाई पनि परमप्रभुले चुन्नु भएको छैन।”",
			"9": "<sup>9</sup> त्यसपछि यिशैले शम्मालाई शमूएलको छेउमा बोलाए। तर शमूएलले भने, “होइन, यसलाई पनि परमप्रभुले चुन्नु भएको होइन।”",
			"10": "<sup>10</sup> यिशैले सातौं छोरा देखाए। तर शमूएलले यिशैलाई भने, “परमप्रभुले यिनीहरू मध्ये कसैलाई पनि चुन्नु भएको छैन।”",
			"11": "<sup>11</sup> शमूएले यिशैलाई सोधे, “के तपाईंका छोराहरू यति नै हुन्?”\nयिशैले भने, “होइन अर्को छोरा छ सब भन्दा कान्छो, तर अहिले भेंडा चराउँदैछ।”\nशमूएलले भने, “उसलाई यहाँ तुरून्तै लिन पठाऊ। ऊ नआउँन्जेल सम्म हामी खान बस्ने छैनौं।”",
			"12": "<sup>12</sup> यिशैले कान्छो छोरा लिन तुरून्तै कसैलाई पठाए। त्यो छोरा हेर्दै राम्रो थियो, रातो वर्णको युवक। ऊ साँच्चै सुन्दर थियो।\nपरमप्रभुले शमूएललाई भन्नु भयो, “उठेर उसलाई अभिषेक गर। त्यहि एउटा हो।”",
			"13": "<sup>13</sup> शमूएलले सींङबाट तेल निकालेर यिशैको कान्छा छोराको शिरमा दाजूहरूका अगाडी त्यो खन्याई दिए। परमप्रभुको आत्मा एकदमै शक्तिको साथ दाऊदमाथि त्यस दिन देखि उत्रियो। त्यस पछि शमूएल रामा घरतिर फर्के।",
			"14": "<sup>14</sup> शाऊललाई दुष्ट आत्माले सताउन थाल्यो\nपरमप्रभुको आत्माले शाऊललाई त्यागि दियो। परमप्रभुले दुष्ट आत्मा शाऊल कहाँ पठाउँनु भयो। त्यसले शाऊललाई साह्रै सतायो।",
			"15": "<sup>15</sup> शाऊलको नोकरले भन्यो, “परमेश्वरद्वारा पठाउँनु भएको दुष्ट आत्माले तपाईंलाई साह्रै दुःख दियो।",
			"16": "<sup>16</sup> हामीलाई आदेश दिनुहोस् हामीले कुनै त्यस्तो व्यक्ति खोज्न सकौं जसले वीणा बजाउन सक्छ। यदि परमप्रभुबाट आएको त्यस दुष्ट आत्माले तपाईंलाई सताउन थालेछ भने त्यस मानिसले वीणा बजाउन थाल्नेछ र तपाईंलाई आराम अनुभव हुनेछ।”",
			"17": "<sup>17</sup> शाऊलले नोकर-चाकरहरूलाई भने, “एउटा मानिसलाई मेरो निम्ति खोज जसले असल तरिकाले सँगीत बजाउँछ। त्यसलाई म कहाँ ल्याऊ।”",
			"18": "<sup>18</sup> एउटा नोकरले भन्यो, “एउटा यिशै नाउँका मानिस छन्। बेतलेहेममा बस्छन्। मैले देखेको छु यिशैको छोरा। उसले कसरी वीणा बजाउनु पर्छ जान्दछ। ऊ साह्रै वीर र युद्ध गर्न जान्दछ। ऊ साह्रै फुर्तिलो र सुन्दर छ। परमप्रभु पनि उसैको साथ हुनुहुन्छ।”",
			"19": "<sup>19</sup> यसकारण शाऊलले समाचार दिने मानिसलाई यिशैकहाँ पठाए। तिनीहरूले यिशैलाई भने, “तिम्रो दाऊद नाउँको छोरा छ। जसले तिम्रा भेंडाहरू चराउँछ। उसलाई मकहाँ पठाऊ।”",
			"20": "<sup>20</sup> यिशैले शाऊलका निम्ति उपहारको बन्दोबस्त गर्यो। यिशैले उपहारको रूपमा एउटा गधा, केही रोटी, एक मशक दाखरस र एउटा बाख्राको पाठो तयार पार्यो। यिशैले ती चीजहरू दाऊदका साथ लगाएर शाऊल कहाँ पठायो।",
			"21": "<sup>21</sup> यसरी दाऊद शाऊल कहाँ गयो र तिनका अगाडि हाजिर भयो। शाऊलले दाऊदलाई धेरै प्रेम गरे। दाऊद शाऊलको अस्त्र बोक्ने सहयोगी भए।",
			"22": "<sup>22</sup> शाऊलले यिशैलाई खबर पठाए, “दाऊद बसेर मेरो सेवा गरोस्। ऊ मलाई असाध्यै मन पर्यो।”",
			"23": "<sup>23</sup> जब परमेश्वरले पठाएको दुष्ट आत्माले शाऊललाई दुःख दिन थाल्थ्यो, दाऊदले हातमा वीणा लिएर बजाउँन थाल्दथे र दुष्ट आत्माले शाऊललाई छोडि हाल्थ्यो र शाऊल असल हुन्थे।"
		},
		"17": {
			"1": "<sup>1</sup> गोल्यतको इस्राएली माथि चुनौती\nपलिश्तीहरूले आफ्ना सैनिकहरूलाई युद्धको निम्ति भेला गराए। तिनीहरू यहूदा स्थित सोकोहमा भेला भए। तिनीहरूको छाउनी सोकोह र अजेकाको बीचमा एपेसदम्मीम नाउँका शहरमा थियो।",
			"2": "<sup>2</sup> शाऊल र इस्राएली सैनिकहरू पनि एक ठाउँमा भेला भए। तिनीहरूको छाउनी एला बेंसीमा थियो। शाऊलका सैनिकहरूले पंक्ति बाँधे पलिश्तीहरूसित युद्ध गर्न तयार थिए।",
			"3": "<sup>3</sup> पलिश्तीहरू एउटा पहाड माथि थिए, र इस्राएलीहरू अर्को पहाड माथि थिए। दुवै पक्षका बीचमा बेंसी थियो।",
			"4": "<sup>4</sup> पलिश्तीहरूको गोल्यात नाउँ भएको एउटा वीर योद्धा थियो। गोल्यात गात निवासी थियो। गोल्यात झण्डै नौ फीट अग्लो थियो। गोल्यत पलिश्तीहरूका छाउनीबाट बाहिरियो।",
			"5": "<sup>5</sup> शिरमा काँसाको टोपी लगाएको थियो। शरीरमा माछाको कत्ला जस्तै कवच कोट लगाएको थियो। त्यो रक्षा कवच काँसाको नै थियो यसको ओजन 5,000 शेकेलको थियो।",
			"6": "<sup>6</sup> गोल्यतले खुट्टातिर पनि काँसाकै कवच लगाएको थियो। काँसाकै भाला पिठ्यूँमा बोकेको।",
			"7": "<sup>7</sup> भालाको लट्टी जुलाहाको लुगा बुन्ने डण्डा जत्रो थियो। भालाको ओजन 600 शेकेलको थियो। गोल्यतको हात-हतियार बोक्ने तिनको अघि-अघि हिँड्थ्यो।",
			"8": "<sup>8</sup> प्रत्येक दिन गोल्यत बाहिर निस्कन्थ्यो र इस्राएली सैनिकहरूलाई चुनौती दिन्थ्यो। उसको चुनौति थियो, “किन तिमीहरूले युद्धको निम्ति पंक्ति कसेको? तिमीहरू शाऊलका नोकर हौ म त पलिश्ती हुँ। एउटा मानिस छानेर मेरो विरूद्धमा लडनलाई पठाऊ।",
			"9": "<sup>9</sup> यदि त्यस मानिसले मलाई मार्यो भने तब हामी सबै पलिश्तीहरू तिमीहरूका दास बन्नेछौ। तर यदि मैले त्यस मानिसलाई मारे भने मैले जितेको हुनेछ र तिमीहरू हाम्रो दास हुनेछौ। तिमीहरूले हाम्रा सेवा गर्नुपर्छ।”",
			"10": "<sup>10</sup> पलिश्तीले यसो पनि भन्यो, “आज म इस्राएली सेनाहरूलाई गिल्ला गर्छु। एउटा मानिस पठाइदेऊ ताकि म त्यससित लड्न सकुँ।”",
			"11": "<sup>11</sup> शाऊल र इस्राएली सैनिकहरूले पलिश्तीले भनेको यी सब सुन्थे र साह्रै डराउँथे।",
			"12": "<sup>12</sup> दाऊद रणभूमिमा जान्छन्\nदाऊद यिशैका छोरा थिए। यिशै इफ्रातीका छोरा थिए जो बेतलेहेमको, यहूदाका थिए। यिशैका आठजना छोराहरू थिए। शाऊलका समयमा यिशै बूढा भइसकेका थिए।",
			"13": "<sup>13</sup> यिशैका जेठा, माहिला र साहिंला छोराहरू शाऊलसित रणभूमिमा गएका थिए। जेठा छोराको नाउँ एलीआब थियो। माहिला छोराको नाउँ अबीनादाब र साँहिला छोराको नाउँ शम्मा थियो।",
			"14": "<sup>14</sup> दाऊद चाहिं कान्छो छोरा थिए। जेठा, माहिला र साँहिला चाहिं शाऊलको सेनामा थिए।",
			"15": "<sup>15</sup> कान्छा छोरा दाऊद शाऊलको छाउनी र बेतलेहेममा आफ्ना पिताका भेंडाहरू हेरचाह गर्न आउने जानेगर्थे।",
			"16": "<sup>16</sup> त्यो पलिश्ती गोल्यत प्रत्येक दिन बिहानै बेलुकी इस्राएलीहरूका छाउनीमा आएर ठट्टा गर्थ्यो। 40 दिनसम्म त्यसले सधैँ त्यस्तै गर्यो।",
			"17": "<sup>17</sup> एक दिन यिशैले दाऊदलाई भने, “यस फुर्लुङमा चार पाथी (एक एपा) चिउरा र दशवटा रोटी छन् तिम्रा दाज्यूहरूलाई छाउनीमा लगि देऊ।",
			"18": "<sup>18</sup> यो 10 टुक्रा पनीर पनि लिए जाऊ। तिम्रा दाज्यूहरूको 1,000 सेनाको समूहलाई आदेश गर्ने अधिकारीलाई देऊ। दाज्यूहरूले के गर्दै छन् हेर। दाज्यूहरू ठीक-ठाक नै छन् भन्ने प्रमाण पनि लिएर आउनु।",
			"19": "<sup>19</sup> तिम्रो दाज्यूहरू र सबै इस्राएली सैनिकहरू शाऊलसित एला बेंसीमा छन्। तिनीहरू त्यहाँ पलिश्तीहरूसँग लड्दैछन्।”",
			"20": "<sup>20</sup> बिहानै दाऊदले अर्को एउटा गोठालोलाई भेंडाहरू जिम्मा लगायो। दाऊदले भोजन लियो र यिशैले भने अनुसार हिंड्‌यो। दाऊदले गाडा सोझै छाउनीमा लिएर गयो। सैनिकहरू युद्ध पंक्तिमा गएका थिए अनि युद्ध गर्न लागे झैं हल्ला गरिरहेका थिए।",
			"21": "<sup>21</sup> इस्राएली र पलिश्ती दुवैतिरका सैनिकहरू युद्ध गर्न तयार रहे।",
			"22": "<sup>22</sup> दाऊदले भोजन र सामानहरू भण्डारेको हातमा सुम्पियो र इस्राएली सैनिकहरू भए तिर अगाडिको पंक्तिमा दगुर्यो अनि आफ्नो दाजूहरूको विषयमा सोध्यो।",
			"23": "<sup>23</sup> जस्तो दाऊदले आफ्ना दाज्यूहरूसँग कुरा गरेको बेला त्यो श्रेष्ठ योद्धा पलिश्ती गोल्यत पलिश्ती सैनिकबाट निस्केर आयो। गोल्यतले सधैँ झैं इस्राएलीलाई गाली दिन थाल्यो। दाऊदले पनि गोल्यातले भनेको कुरा सुन्यो।",
			"24": "<sup>24</sup> इस्राएली सैनिकहरू त गोल्यतलाई देखे पछि भाग्न थाले। सबै नै भयभित थिए।",
			"25": "<sup>25</sup> एकजना इस्राएलीले भने, “त्यसलाई देख्यौ त्यसले के गरिरहेछ राम्ररी हेर? त्यो गोल्यत घरि-घरि आएर इस्राएलीहरूलाई यसरी नै गाली गर्छ। जसले यसलाई मार्छ त्यो धेरै धनी हुनेछ। राजा शाऊलले धेरै सम्पत्ति दिनुहुनेछ। शाऊलले उसकी छोरी पनि विवाह गर्न दिनेछन् जसले त्यसलाई मार्न सक्छ। शाऊलले उसको परिवारलाई इस्राएलमा स्वतन्त्र बनाउनेछन्।”",
			"26": "<sup>26</sup> दाऊदले छेवैका मानिसलाई सोध्यो, “त्यसले के भन्यो? यदि कसैले यो पलिश्तीलाई मार्छ अनि इस्राएलको इज्जत बचाउँछ भने एउटा विदेशी मात्र त हो, उसले के इनाम पाउनेछ? यो गोल्यत के हो र? गोल्यत एकजना पलिश्ती भन्दा बढी केही होइन। त्यसले यो के सोचिरहेछ? उसले जीवित परमेश्वरका सैनिकहरूलाई धम्काउन सक्छ।”",
			"27": "<sup>27</sup> यसैले इस्राएलीहरूले गोल्यतलाई मार्नेको इनामबारे दाऊदलाई बताउँछन्।",
			"28": "<sup>28</sup> दाऊदका जेठा दाज्यू एलीआबले दाऊद सैनिकहरूसित बात गर्दै गरेको सुने। एलीआब दाऊदसँग रिसाउँछ। एलीआबले दाऊदलाई भग्ने, “तँ यहाँ किन आइस्? भेंडाहरू मरुभूमिमा कसलाई छोडेर आइस्? तँ यहाँ आउनुको कारण मैले जाने। तँलाई जे गर्न पठाइएको हो त्यो गर्न तँ चाहँदैनस्। तँ यहाँ खालि युद्ध हेर्नलाई मात्र आउन चाहन्थिस्।”",
			"29": "<sup>29</sup> दाऊदले भन्यो, “मैले त्यस्तो के गरें र? मैले कुनै नराम्रो काम गरिनँ म खालि कुरा गर्दै थिएँ।”",
			"30": "<sup>30</sup> दाऊद चारैतिर दौडिएर अरू सेनाहरूसित त्यही प्रश्नहरू सोध्न थाल्यो। तिनीहरूले पनि दाऊदलाई त्यही पहिले कै जस्तो उत्तर दिए।",
			"31": "<sup>31</sup> केही मानिसहरूले दाऊदलाई कुरा गर्दै गरेको सुने। तिनीहरूले दाऊदलाई शाऊल कहाँ पुर्याएर दाऊदले गरेको कुरा सबैलाई सुनाए।",
			"32": "<sup>32</sup> दाऊदले शाऊललाई भन्यो, “त्यस पलिश्ती गोल्यतको कारण तपाईं हतास नहुनुहोस्। म तपाईंको सेवक हुँ। त्यस पलिश्तीसँग म लड्ने छु।”",
			"33": "<sup>33</sup> शाऊलले भने, “तिमी गएर त्यस पलिश्तीसँग लड्न सक्ने छैनौ। तिमी सैनिक पनि त होइनौ। तिमी सानै छौ तर गोल्यत सानै देखि युद्धभूमिमा लड्दै आएको हो।”",
			"34": "<sup>34</sup> तर दाऊदले शाऊललाई फेरि भन्यो, “म, तपाईंको सेवक हुँ र मेरो पिताको भेंडा पनि चराउने गर्छु। भेंडाका बथानबाट सिंह र भालू आएर भेंडा लैजान्थे।",
			"35": "<sup>35</sup> म त्यस्ता जंगली पशुहरूलाई खेद्दै जान्थे र तिनीहरूका मुखबाट भेंडा खोसेर ल्याउँथे। यदि जंगली जन्तुले मलाई आक्रमण गरे त्यसको मुख मुनिको भुत्ला समात्थें र त्यसलाई हिर्काउथें।",
			"36": "<sup>36</sup> मैले सिंह र भालू मारेको छु। म त्यस विदेशी गोल्यतलाई त्यसरी नै मार्नेछु। गोल्यत मर्नु नै पर्छ किनकि त्यसले जीवित परमेश्वरका सेनाहरूको ठट्टा गरेको छ।",
			"37": "<sup>37</sup> परमप्रभुले मलाई सिंह र भालूहरूबाट बचाउनु भएको छ। परमप्रभुले यी पलिश्ती गोल्यतबाट पनि बचाउनु हुनेछ।”\nशाऊलले दाऊदलाई भने, “ठीक छ जाऊ? परमप्रभु तिम्रो साथमा रहुन्।”",
			"38": "<sup>38</sup> शाऊलले दाऊदलाई आफ्नै पोशाक पहिराइ दिए। शाऊलले काँसाको टोपी दाऊदको शिरमा अनि आफ्नो हातहतियर शरीरमा लगाइ दिए।",
			"39": "<sup>39</sup> दाऊदले तरवार भिरेर यताउता हिँड्ने चेष्टा गर्यो। यसरी शाऊलको पोशाकले दाऊदलाई साह्रै असजिलो बनायो। यस्तो पोशाकको अभ्यास दाऊदलाई थिएन।\nदाऊदले शाऊललाई भन्यो, “म यसरी लड्न सक्दिनँ मैले तिनीहरूको प्रयोग गरेको छैन।” यसर्थ दाऊदले सबै फूकाले।",
			"40": "<sup>40</sup> दाऊदले हातमा एउटा टेक्ने लौरो लियो र खोलाबाट चिल्लो पाँचवटा ढुङ्गा टिप्यो। गोठालाको झोलाभित्रको थैलीमा उक्त पाँचवटा ढुङ्गा हाल्यो र घुँयेत्रो हातमा बोक्यो। त्यसपछि त्यो पलिश्ती गोल्यतलाई हाँक दिन बढ्‌यो।",
			"41": "<sup>41</sup> दाऊदले गोल्यतलाई मार्छन्\nपलिश्ती गोल्यत विस्तारै दाऊदकहाँ आयो। गोल्यतको सहायक अघि-अघि ढाल बोकेर हिंड्दै थियो।",
			"42": "<sup>42</sup> गोल्यतले दाऊदलाई हेर्यो र हाँस्यो। गोल्यतले सोच्यो यो सैनिक होइन, खालि सुन्दर र रातोवर्णको केटो हो।",
			"43": "<sup>43</sup> गोल्यतले दाऊदलाई भन्यो, “यो लौरो केको निम्ति हो? कुकुरलाई जस्तै मलाई खेद्न चाहन्छौ?” अनि गोल्यतले आफ्ना देवताहरूको नाउँ उच्चारण गरेर दाऊदलाई सराप्न थाल्यो।",
			"44": "<sup>44</sup> गोल्यतले दाऊदलाई भन्यो, “यता आइज, म तेरो मासु चराहरू र जंगली जनावरहरूलाई खुवाउँनेछु।”",
			"45": "<sup>45</sup> दाऊदले पलिश्ती गोल्यतलाई भन्यो, “तँ मकहाँ तरवार, बर्छा र भाला प्रयोग गर्न आइस्। तर म तँ कहाँ इस्राएलका सेनाहरूका परमेश्वर सर्वशक्तिमान परमप्रभुका नाउँ मात्र लिएर आएको छु। तैंले उहाँको विरूद्धमा नराम्रो वचन बोलीस्।",
			"46": "<sup>46</sup> आज परमप्रभुले मलाई तँमाथि विजय दिलाउनु हुन्छ! म तँलाई मार्छु। आज तेरो टाउको काटेर चरा र जंगली जनावरहरूलाई खुवाउँछु। हामी अन्य पलिश्तीहरूलाई पनि यसै गर्छौं। त्यसपछि संसारले नै बुझ्नेछ इस्राएलमा परमेश्वर हुनुहुन्छ भनेर।",
			"47": "<sup>47</sup> यहाँ जम्मा भएका सबै मानिसहरूले बुझ्ने छन् कि परमप्रभुलाई आफ्ना मानिसहरूलाई बचाउँनका निम्ति तरवार र भाला चांहिदैन भनेर। युद्ध परमप्रभुको हो अनि तिमी पलिश्तीहरूलाई हराउनमा परमप्रभुले हामीलाई सहायता गर्नुहुन्छ।”",
			"48": "<sup>48</sup> पलिश्ती गोल्यत दाऊदलाई आक्रमण गर्न सुरियो। र बिस्तारै दाऊद अघि बढ्न थाल्यो। त्यत्ति नै बेला दाऊद दौडिएर लड्न चाँडै अघि सर्यो।",
			"49": "<sup>49</sup> दाऊदले आफ्नो झोलाबाट एउटा ढुङ्गा निकाल्यो र घुँयेत्रोमा राखेर मच्चायो। घुँयेत्रोको ढुङ्गा गएर गोल्यतको आँखा बिच निधारमा लाग्यो। गोल्यत घोप्टो परेर भूईंमा लड्यो।",
			"50": "<sup>50</sup> यसरी दाऊदले पलिश्तीलाई घुँयेत्रोको ढुङ्गाले हिर्काएर हरायो। उसले ढुङ्गाले हिर्कायो अनि पलिश्ती गोल्यतलाई मार्यो। दाऊदसँग तरवार थिएन।",
			"51": "<sup>51</sup> यसर्थ ऊ दौडिएर पलिश्तीको छेउमा गयो। त्यसपछि दाऊदले गोल्यतकै म्यानबाट तरवार झिकेर गोल्यतको टाउको छिनायो। यसरी दाऊदले गोल्यतलाई मार्यो।\nजब अरू पलिश्तीले उनीहरूको वीर नै मरेको देखे अनि डरले भाग्न थाले।",
			"52": "<sup>52</sup> इस्राएल र यहूदाका सैनिकहरूले जयघोषणा गर्दै दगुर्दै पलिश्तीहरूलाई खेद्न थाले। इस्राएलीहरूले पलिश्तीहरूलाई खेद्दै-खेद्दै गातको सिमाना र एक्रोनको ढोकासम्म पुर्याए र धेरै संख्यामा पलिश्तीहरू मारे। पलिश्तीहरूको लाश गात र एक्रोनको शारेमसम्म सडकभरी छरपष्ट थियो।",
			"53": "<sup>53</sup> पलिश्तीहरूलाई खेद्दै सिमाना कटाए पछि इस्राएलीहरू फर्केर पलिश्तीहरूका छाउनीमा पसे र त्यहाँ धेरै सामानहरू फेला पारे।",
			"54": "<sup>54</sup> दाऊदले पलिश्तीको टाउको यरूशलेममा नै पुर्यायो। पलिश्तीको हात-हतियार पनि आफ्नै पालमा राख्यो।",
			"55": "<sup>55</sup> शाऊल दाऊदसँग डराउन थाल्छन्\nशाऊलले दाऊदलाई गोल्यतसँग लड्न गएको देखेका थिए। शाऊलले सेनाका सेनापति अबनेरसँग सोधे, “अबनेर, त्यस केटोको बाबु चाहिं को हुन्?”\nअबनेरले भने, “मेरो राजा, शपथ लिएर भन्दैछु, मलाई थाहा छैन।”",
			"56": "<sup>56</sup> राजा शाऊले भने, “त्यसको बाबुको चाहिंको रहेछ खोजी गर।”",
			"57": "<sup>57</sup> जब दाऊद गोल्यतलाई मारेर फर्के, अबनेरले उसलाई शाऊल कहाँ लग्यो। दाऊदको हातमा अझै पनि त्यस पलिश्तीको टाउको झुण्डिरहेको थियो।",
			"58": "<sup>58</sup> शाऊलले उसलाई सोधे, “युवक, तिम्रो बाबु को हुन्?”\nदाऊदले जवाफ दिए, “म तपाईं कै सेवक बेतलेहेमका यिशैको छोरा हुँ।”"
		},
		"18": {
			"1": "<sup>1</sup> दाऊद र जोनाथनको घनिष्ट मित्रता\nदाऊदले शाऊलसँग कुरा गरिसके पछि जोनाथन र दाऊदको बीचमा मित्रमा शुरू भयो। जोनाथनले दाऊदलाई त्यति नै प्रेम गर्थ्यो जति उसले आफूलाई गर्थ्यो।",
			"2": "<sup>2</sup> शाऊलले दाऊदलाई त्यस दिनदेखि आफूसँगै राख्न थाल्यो। शाऊलले दाऊदलाई आफ्नो बाबुको घर जान दिएन",
			"3": "<sup>3</sup> जोनाथनले दाऊदलाई असाध्यै प्रेम गरको हुनाले उसले दाऊदसित एउटा विशेष सन्धि गर्यो।",
			"4": "<sup>4</sup> आफूले लगारहेको कोट फुकालेर जोनाथनले दाऊदलाई दियो। जोनाथनले आफ्नो वर्दि, धनु, तरवार र पेटीसमेत दाऊदलाई दियो।",
			"5": "<sup>5</sup> दाऊदको सफलतामा शाऊलको ध्यान\nशाऊलले दाऊदलाई अरू धेरै युद्धहरूमा पठाए। प्रत्येक युद्धमा दाऊदले नै सफलता पाए। त्यसपछि शाऊलले दाऊदलाई सैनिकहरूको जिम्मा दिए। यसो हुँदा सबै खुशी भए, यहाँ सम्म शाऊलका सबै अधिकारी पनि खुशी भए।",
			"6": "<sup>6</sup> दाऊद पलिश्तीहरू विरूद्ध युद्ध लड्न जाने गर्थ्यो। युद्ध भूमिबाट घरतिर फर्कंदा दाऊदलाई इस्राएलको प्रत्येक शहरबाट स्त्रीहरू स्वागत जनाउन आउँथे। ती स्त्रीहरू नाँच्थे, गाउँथे, सितारा र ढोलक बजाउँथे। तिनीहरूले शाऊलकै छेउमा यसो गरे।",
			"7": "<sup>7</sup> स्त्रीहरूले यसरी गाउँथे,\n“शाऊलले हजार शत्रुहरू मारे\nतर दाऊदले दश हजार मारे।”",
			"8": "<sup>8</sup> ती स्त्रीहरूका गीतले शाऊललाई खिन्न बनाउँथ्यो र तिनी रिसाउँथे। शाऊलले सोचे, “ती स्त्रीहरूले भन्छन् दाऊदले दश हजार शत्रु मार्यो र मैले खालि हजार मात्र मारे।”",
			"9": "<sup>9</sup> त्यसपछि शाऊलले दाऊदको गतिविधिलाई धेरै नजिकबाट ख्याल राख्न थाले।",
			"10": "<sup>10</sup> शाऊल दाऊदसँग भयभीत भए\nअर्को दिन परमेश्वरले पठाउनु भएको दुष्ट आत्माले शाऊललाई पक्रियो। शाऊलले आफ्नै घरमा अगमवाणी गर्दै थिए। दाऊदले सधैँ झै वीणा बजाए।",
			"11": "<sup>11</sup> तर शाऊलको हातमा भाला थियो। शाऊलले सोचे, “म दाऊदलाई भित्तैमा टँसाई दिन्छु।” शाऊलले दुइ पल्ट भाला हाने तर दाऊद दुवै चोटि तर्किए।",
			"12": "<sup>12</sup> परमप्रभु दाऊदसित हुनुहुन्थ्यो अनि परमप्रभुले शाऊललाई त्याग्नु भएको थियो। यसकारण शाऊल दाऊद सँग भयभीत थिए।",
			"13": "<sup>13</sup> शाऊलले दाऊदलाई आफू देखि पर गर्न चाहे र 1,000 सैनिकका सेनापति बनाइ दिए। दाऊदले आफ्नो सैनिकलाई युद्धमा अगुवाई गरे।",
			"14": "<sup>14</sup> परमप्रभु दाऊदसित हुनुहुन्थ्यो यसकारण दाऊद प्रत्येक कुरामा सफल भए।",
			"15": "<sup>15</sup> शाऊलले बुझे दाऊद सफल र योग्य थिए जसको कारण शाऊल दाऊदसित झन् ज्यादा भयभीत हुनथाले।",
			"16": "<sup>16</sup> तर इस्राएल र यहूदाका सबै मानिसहरूले दाऊदलाई प्रेम गर्थे किनभने उसले तिनीहरूलाई युद्धमा डोह्राए अनि तिनीहरूको निम्ति युद्ध गरे।",
			"17": "<sup>17</sup> शाऊलले आफ्नी छोरीको विवाह दाऊदसँग गरिदिन चाहे\nतर शाऊलले दाऊदलाई मार्नै चाहन्थे, शाऊलले दाऊदलाई एउटा जालमा पार्ने योजना सोचे। शाऊलले दाऊदलाई भने, “म मेरी जेठी छोरी मेराबको विवाह तिमीसित गरिदिने छु। त्यस पछि तिमी एउटा शक्तिशाली सैनिक हुनेछौ। तिमी मेरो छोरा जस्तै छौ। त्यस पछि तिमीले परमप्रभुको युद्धहरूमा सैनिक भएर लड्ने छौ।” यो खालि धुर्त्याईँ मात्र थियो। शाऊलले साँच्चै सोच्दै थिए, “अब मैले दाऊदलाई मार्नु पर्दैन। म उसलाई मेरो निम्ति पलिश्तीहरूलाई मार्न लगाउँछु।”",
			"18": "<sup>18</sup> तर दाऊदले भने, “म कुनै महत्वपूर्ण सन्तानबाटको अनि महत्वपूर्ण व्यक्ति होइन। म राजाकी छोरीलाई विवाह गर्न सक्दिनँ।”",
			"19": "<sup>19</sup> जब शाऊलकी छोरी मेराबको विवाह दाऊदसँग गर्ने दिन आयो तब दाऊदले महोलका अद्रीएलसँग मेराबको विवाह गरिदिए।",
			"20": "<sup>20</sup> शाऊलकी अर्की छोरी मीकलले दाऊदसँग प्रेम गर्थी। केही मानिसहरूले शाऊललाई मीकल र दाऊदको प्रेमको विषयमा सुनाए। यस खबरले शाऊल आनन्दीत भए।",
			"21": "<sup>21</sup> शाऊलले सोचे, “म दाऊदलाई फसाउन मीकाललाई लगाउँनेछु। म दाऊदलाई मीकलसँग विवाह गर्न दिनेछु। त्यस पछि पलिश्तीहरूद्वारा उसलाई मार्नेछु।” यसर्थ शाऊलले दाऊदलाई दोस्रो पल्ट भने, “आज नै तिमीले मेरी छोरीसँग विवाह गर्न सक्छौ।”",
			"22": "<sup>22</sup> शाऊलले आफ्ना अधिकारीहरूलाई आदेश दिएर भने, “दाऊदलाई एकान्त पारेर भन्नु, ‘हेर! राजाले तिमीलाई साह्रै मन पराउँनु हुन्छ। उहाँका अधिकारीहरूले पनि तिमीलाई मन पराउँछन्। तिमीले उहाँकी छोरीलाई विवाह गर्नु पर्छ।’”",
			"23": "<sup>23</sup> शाऊलका अधिकारीहरूले दाऊदलाई त्यही भने। तर दाऊदले जवाफ दिए, “के तपाईंहरू सोच्नुहुन्छ राजाको ज्वाइँ बन्न सजिलो छ? मेरो त्यत्ति धन छैन, जो म राजाकी छोरीलाई दिऊँ। म त एउटा साधारण गरीब मानिस मात्र हुँ।”",
			"24": "<sup>24</sup> अधिकारीहरूले राजालाई दाऊदको सबै कुरा बताइदिए।",
			"25": "<sup>25</sup> शाऊलले फेरि भने, “दाऊदलाई भन, ‘दाऊद, राजाले उसका छोरीका निम्ति तिमीबाट दुलहीको दाम लिन चाहनु हुन्न, तर राजा आफ्ना शत्रुहरूसित बद्ला लिन चाहनुहुन्छ। यसर्थ राजाले रूपियाँको सट्टा एक सय पलिश्तीहरूका खलड्डी मात्र तिमीसँग माग्नुहुन्छ।’” यो शाऊलको धुर्त षड्‌यन्त्र थियो। शाऊलले सोच्थे पलिश्तीहरूले दाऊदलाई मार्ने छन् भनेर।",
			"26": "<sup>26</sup> अधिकारीहरूले दाऊदलाई सबै कुरो बताए। दाऊद खुशी भए। राजाको ज्वाइँ हुने यो राम्रो अवसरलाई छोपेर दाऊदले त्यो कार्य गरे।",
			"27": "<sup>27</sup> दाऊद र अरू मानिसहरू पलिश्तीहरूसँग युद्ध गर्न हिँडे। तिनीहरूले 200 पलिश्तीहरूलाई मारे। दाऊदले पलिश्तीहरूका खलड्डी शाऊल कहाँ पुर्याए। राजाका ज्वाइँ हुनलाई दाऊदले यस्तो गरे।\nशाऊलले मीकलको विवाह दाऊदसँग गराए।",
			"28": "<sup>28</sup> शाऊलले बुझे कि परमप्रभु दाऊदसँग हुनुहुन्छ। शाऊलले यो पनि बुझे, छोरी मीकलले दाऊदलाई प्रेम गर्थिन।",
			"29": "<sup>29</sup> यसर्थ शाऊल झन्-झन् दाऊदसँग डराउन थाले। शाऊल सधैँ दाऊदको विरोधी भए।",
			"30": "<sup>30</sup> पलिश्ती सेनापतिले बारबार इस्राएलीहरू माथि लडाईं गर्थे। तर प्रत्येक पल्ट नै दाऊदले तिनीहरूलाई हराइदिन्थे। दाऊद शाऊलका अधिकारीहरू मध्ये सबैभन्दा सफल अधिकारी थिए यसैले दाऊद प्रसिद्ध भए।"
		},
		"19": {
			"1": "<sup>1</sup> जोनाथनले दाऊदलाई सहायता गरे\nशाऊलले आफ्ना छोरा जोनाथन र उसका सबै अधिकारीहरूलाई दाऊदको हत्या गर्नु भने। तर जोनाथनले दाऊदलाई एक्दमै प्रेम गर्थे।",
			"2": "<sup>2</sup> जोनाथनले दाऊदलाई होसियार गराए, “सावधान बस! शाऊलले तिमीलाई मार्ने मौका खेज्दैछन्। बिहानै खेतमा गएर लुक्नु। म मेरो पितासँग खेतमा आउँछु। हामीहरू तिमी लुकेकै छेउको खेतमा उभिने छौं। म पितासँग तिम्रो बारेमा चर्चा गर्छु। त्यसपछि जे थाहा पाउँछु तिमीलाई बताउँछु।”",
			"4": "<sup>4</sup> जोनाथनले आफ्ना पिता शाऊलसँग कुरा गरे। जोनाथनले दाऊदको राम्रो पक्षको चर्चा गरे। जोनाथनले भने, “तपाईं राजा हुनुहुन्छ। दाऊद खालि तपाईंको सेवक मात्र हो। दाऊदले तपाईंलाई कुनै रीतिले हानि गरेको छैन। तपाईंले पनि उसको हानि गर्नु हुँदैन। दाऊदले सधै तपाईंको पक्षमा काम गरे।",
			"5": "<sup>5</sup> पलिश्ती गोलियतलाई मार्दा पनि दाऊदले आफ्नो जीवन खतरामा हालेका थिए। परमप्रभुले सबै इस्राएललाई महान विजय दिलाउनु भयो। यो सब देखेर तपाईं खुशी हुनुभयो। अब दाऊदलाई किन मार्न चाहनुहुन्छ! दाऊद निर्दोष छ। दाऊदको हत्या गर्ने कुनै कारण नै छैन।”",
			"6": "<sup>6</sup> शाऊलले जोनाथनको कुरा सुन्यो। शाऊलले शपथ खाए, “परमप्रभु जीवित रहनु भए झैं दाऊद मारिने छैन।”",
			"7": "<sup>7</sup> यसर्थ जोनाथनले दाऊदलाई बोलाए र सबै कुरा सुनाए। त्यसपछि जोनाथनले दाऊदलाई शाऊल कहाँ लगे। यस पछि शाऊल र दाऊद पहिलेको जस्तै भए।",
			"8": "<sup>8</sup> शाऊलले फेरि दाऊदलाई मार्न चाहे\nयुद्ध फेरि शुरू भयो अनि दाऊद पलिश्तीहरूसँग लडन गए। दाऊदले पलिश्तीहरूलाई परास्त गरे र तिनीहरू भागे।",
			"9": "<sup>9</sup> परमप्रभुबाटको दुष्ट आत्माले शाऊलमा प्रेवश गर्यो। शाऊल घरैमा बसिरहेका थिए। शाऊलको हातमा भाला थियो। दाऊदले वीणा बजाउँदै थिए।",
			"10": "<sup>10</sup> शाऊलले भाला हानेर दाऊदलाई भित्तैमा उनिदिने प्रयास गरे। तर दाऊद उफ्रिएर बाँचे। भाला गएर भित्तामा गाडियो। त्यही रात दाऊद त्यहाँबाट भागे।",
			"11": "<sup>11</sup> शाऊलले दाऊदको घरमा मानिस पठाए! मानिसहरूले दाऊदको घरलाई पहरा दिए। तिनीहरू रातभरी त्यही बसे। बिहानी पख दाऊदलाई मारौंला भने। तर दाऊदकी पत्नी मीकलले उसलाई सावधानी गराउँदै भनिन्, “आज राती भाग्नु होस् र आफ्नो ज्यान बचाउँनु होस्। यदि भाग्नु भएन भने भोलि विहान तपाईं मारिनु हुनेछ।”",
			"12": "<sup>12</sup> त्यसपछि मीकलले झ्यालबाट झरेर भाग्न लगाईन्।",
			"13": "<sup>13</sup> मीकलले एउटा परिवारिक मूर्तिलाई ओध्यानमा सुताएर टाउकोमा बाख्राको रौं राखेर लुगाले ढाकिदिईन्।",
			"14": "<sup>14</sup> शाऊलले दाऊदलाई बन्दी बनाउनलाई दूत पठाए। तर मीकलले भनी, “दाऊद बिरामी हुनुहुन्छ।”",
			"15": "<sup>15</sup> शाऊलले दूतलाई फेरि दाऊद पछि पठाए र भने, “दाऊदलाई यहाँ ल्याउँनु यदि तिमीले उसलाई आफ्नो आछ्यानमा सुतिरहेको अवस्थामा ल्याऊ ता की म उसलाई मार्न सकूँ।”",
			"16": "<sup>16</sup> ती दूत फेरि दाऊदको घर गए। तिनीहरू दाऊदलाई पक्रन भनेर भित्र गए। तर त्यहाँ गएर देखे ओछ्यानमा त एउटा मूर्तिको कपाल बाख्राको रौं पो रहेछ।",
			"17": "<sup>17</sup> शाऊलले मीकललाई सोधे, “तैंले मलाई यसरी धोका किन दिइस्? तैंले मेरो शत्रुलाई भगाइस्। दाऊद भाग्यो।”\nमीकलले शाऊललाई भनी, “यदि मैले दाऊदलाई भाग्नमा सहायता गरिन भने मलाई नै मार्छु भन्यो।”",
			"18": "<sup>18</sup> दाऊद भागेर रामा डेरामा पुगे\nदाऊद भागेर शमूएल भएको ठाउँ रामामा पुगे। दाऊदले तिनी माथि शाऊलले गरेको प्रत्येक कुरा शमूएललाई सुनाए। त्यसपछि दाऊद र शमूएल दुवै अगमवक्ताहरू बसेका डेरामा पुगे। दाऊद त्यहीं बस्नथाले।",
			"19": "<sup>19</sup> शाऊलले सुने कि दाऊद रामाको एउटा डेरामा बस्दैछन्।",
			"20": "<sup>20</sup> शाऊलले दाऊदलाई पक्रिन मानिस पठाए। तर त्यस डेरामा मानिसहरूले अगमवक्तहरू अगमवाणी गरिरहेका देखे। त्यस दलको माझमा उभिएर शमूएलले नेतृत्व दिंदै थिए। परमेश्वरको आत्मा शाऊलका दूतहरूमा उत्रियो र तिनीहरूले पनि अगमवाणी गर्नथाले।",
			"21": "<sup>21</sup> शाऊलले यो खबर पनि सुने र अरू दूतहरू पठाए। तर तिनीहरूले पनि अगमवाणी गर्न थाले। तेस्रो चोटि पनि शाऊलले दूतहरू पठाए तर तिनीहरूले पहिलो तरिका अप्नाए अनि अगमवाणी गर्न लागे।",
			"22": "<sup>22</sup> अन्त्यमा, शाऊल आफैं रामा गए। शाऊल सेकूको खलाको छेऊमा एउटा ठूलो कूवा भएतिर आइपुगे र “शमूएल र दाऊद कहाँ छन्” भनी सोधे।\nमानिसहरूले जवाफ दिए, “रामाको छाउनी छेऊमा।”",
			"23": "<sup>23</sup> त्यसपछि शाऊल रामाको त्यस डेरामा पुगे। परमेश्वरको आत्मा पनि शाऊलमा उत्रियो। शाऊलले पनि अगमवाणी गर्न थाले। शाऊलले बाटोदेखि रामाको डेरा सम्मै अगमवाणी बोले।",
			"24": "<sup>24</sup> त्यसपछि शाऊलले आफ्नो लुगाहरू खोल्न थाले। शमूएलको अघि पनि शाऊलले अगमवाणी गरिरहे। शाऊल त्यहाँ दिन रात नाङ्गै लडिरहे।\nयही कारणले मानिसहरू भन्ने गर्छन्, “के शाऊल पनि अगमवक्ताहरू मध्ये कै एकजना हो?”"
		},
		"20": {
			"1": "<sup>1</sup> दाऊद र जोनाथनले एउटा सन्धि गर्छन्\nरामाको छेऊ डेराबाट दाऊद दगुर्दै जोनाथनकहाँ गए। दाऊदले जोनाथनलाई भने, “मैले के भूल गरें? मेरो दोष के हो? तिम्रा पिताले मलाई किन मार्न चाहनु हुन्छ?”",
			"2": "<sup>2</sup> जोनाथनले भने, “त्यो सत्य हुन सक्दैन। तिमी मारिने छैनौ। मेरो पिताले मलाई नबताई कुनै काम पनि गर्नु हुँदैन। उहाँले मलाई मुख्य अनि साधारण सूचना भन्नुहुन्छ। यसकारण मेरो पिताले यदि तिमीलाई मार्ने योजना बनाएको भए मलाई किन भन्नुहुन्न र? यो हुनै सक्दैन।”",
			"3": "<sup>3</sup> तर दाऊदले भने, “म तिम्रो मित्र हुँ, भन्ने कुरा तिम्रो पितालाई राम्ररी थाह छ। तिम्रा पिताले सायद सोच्नुहुन्छ होला, ‘जोनाथनले यो कुरा थाह पाउँनु हुँदैन। यदि त्यसले थाहा पायो भने दाऊदलाई भन्छ।’ परमप्रभु र तपाईं जीवित रहेसम्म, नै यो सत्य हो मेरो मृत्यु नजिकै छ।”",
			"4": "<sup>4</sup> जोनाथनले भने, “तपाईंले मलाई जसो भन्नुहुन्छ म त्यसैगर्छु।”",
			"5": "<sup>5</sup> त्यसपछि दाऊदले भने, “हेर भोलि औंसीको चाड छ। मैले राजासँग खाना खानुपर्छ। तर साँझ सम्म मलाई खेतमा लुकेर बस्न देऊ।",
			"6": "<sup>6</sup> यदि तिम्रा बुबाले म यहाँ उपस्थित छैन भन्ने थाहा पाउनुभयो भने, त्यत्तिबेला तिमीले भन्नु, ‘दाऊद बेतलेहेम घरमा जान चाहन्थ्यो। तिनका परिवारले नै मासिक बलि चढाउने कुरा छ। यसकारण दाऊदले मलाई बेतलेहेम झर्न र परिवारसँग भेट्न देऊ भने।’",
			"7": "<sup>7</sup> यदि तिम्रा पिताले, ‘ठीक छ’ भन्नु भयो भने म बाँचे। तर रिसाउनु भयो भने बुझ्नुहोस कि उहाँले मलाई घात गर्न चाहनुहुन्छ।",
			"8": "<sup>8</sup> म माथि दयागर जोनाथन, म तिम्रो सेवक हुँ। परमप्रभुको अगाडि तिमीले मसँग सम्झौता गरेका छौ। यदि म दोषी छु भने तब तपाईंले नै मलाई मार्नु होस्। तर मलाई तिम्रा बुबा कहाँ नलैजाऊ।”",
			"9": "<sup>9</sup> जोनाथनले भने, “त्यसो त कहिले पनि होइन। यदि मेरा पिताले तिमीलाई घात गर्न खोज्नु, भएको मैले थाह पाएँ भने म तिमीलाई सावधान गराउनेछु।”",
			"10": "<sup>10</sup> दाऊदले भने, “यदि तपाईंलाई तपाईंका पिताले गाली गरे भने मलाई सावधान कसले गराउँछ?”",
			"11": "<sup>11</sup> तब जोनाथनले भने, “हिंड खेततिर जाऔं।” त्यसपछि दुवैजनासँगै खेततिर लागे।",
			"12": "<sup>12</sup> जोनाथनले दाऊदलाई भने, “म इस्राएलका परमेश्वर परमप्रभुको अगाडि प्रतिज्ञा गर्छु। मेरा पिताको तपाईंको बारेमा कस्तो सोचाइ छ म त्यो थाह गर्ने प्रतिज्ञा गर्छु। साँच्चै खराबै सोच्नु भएको छ कि असल सोच्नु भएको छ तीन दिनभित्र म तपाईंलाई खेतमा खबर पठाउँछु।",
			"13": "<sup>13</sup> यदि मेरा पिताले तपाईंलाई चोट पुर्याउन चाहनुभएको हो भने म तपाईंलाई भन्नेछु म यहाँबाट तपाईंलाई सुरक्षित जानदिनेछु। प्रतिज्ञा गरेको काम मैले गरिन भने परमप्रभुले मलाई सजाय दिनुहुन्छ। परमप्रभु तिमीसँग रहून् जसरी उहाँ मेरा पितासँग हुनुहुन्छ।",
			"14": "<sup>14</sup> जबसम्म म जीवित रहन्छु मलाई दया गर्नु अनि म मरेपछि",
			"15": "<sup>15</sup> मेरा सन्तान माथि दया गर्न नछोड। परमप्रभुले पृथ्वीमा भएका तपाईंका सबै शत्रुहरूलाई नष्ट गर्नुहुनेछ।",
			"16": "<sup>16</sup> त्यस समय यदि जोनाथनको परिवार दाऊदबाट अलग्गै हुन्छ भने पनि होस्। परमप्रभुले चाहिं दाऊदका शत्रुहरूलाई सजाय दिउन्।”",
			"17": "<sup>17</sup> त्यसपछि जोनाथनले दाऊदलाई तिनीहरूको बीचमा भएको प्रेमको प्रतिज्ञालाई नवीकरण गर्नु भनि सोधे किनकी जोनाथनले दाऊदलाई आफू भन्दा ज्यादै प्रेम गर्थे।",
			"18": "<sup>18</sup> जोनाथनले दाऊदलाई भने, “भोलि औंसीको चाड हो। तपाईंको ठाउँ रित्तो देखेर मेरा पिताले तपाईं कहाँ जानुभयो भनि सोच्नुहुनेछ।",
			"19": "<sup>19</sup> तेस्रो दिनमा पनि तपाईं जानुहोस् अनि त्यही ठाउँमा लुक्नु जहाँ तपाईं पहिला लुक्नु भएको थियो जब यो समस्या शुरू हुन्छ त्यहीं पहाडको छेउमा पर्खिन्छु।",
			"20": "<sup>20</sup> तेस्रो दिन म त्यही पहाडमा निशान हान्ने बहनामा आउनेछु र म काँड हान्ने छु।",
			"21": "<sup>21</sup> म एउटा केटालाई काँड खोज्न पठाउँनेछु। यदि सबै ठीक-ठाक छ भने म केटोलाई भन्ने छु, ‘हेर काँड नजिकै छ! तँ धेरै पर गइस्। मेरो नजिक काँड खोज।’ त्यस बेला लुकेको ठाउँबाट तिमी निस्कन सक्छौ। शपत खाएर भन्छु कि जसरी परमप्रभु सत्य र जीवित हुनुहुन्छ, तिमी सुरक्षित छौ। त्यहाँ कुनै खतरा हुनेछैन।",
			"22": "<sup>22</sup> तर यदि स्थिति ठीक छैन भने म केटोलाई भन्नेछु ‘काँड धेरै पर पुगेको छ। उता गएर खोजेर आइज्।’ यदि मैले यसो भनेर अह्राएँ भने तिमी भाग्नै पर्छ। परमप्रभुले नै तिमीलाई टाढा पठाउनु भएको हो।",
			"23": "<sup>23</sup> दया गरेर तिम्रो र मेरो बीचको सम्झौता चाहि नभुल्नू। परमप्रभु सधैँ भरी हाम्रो साक्षी हुनुहुन्छ।”",
			"24": "<sup>24</sup> त्यसपछि दाऊद खेतमा लुक्न गए।\nउत्सवमा शाऊलको मनोभाव\nऔंसीको चाड आयो। अनि राजा भोजनको निम्ति आफ्ना आसनमा बसे।",
			"25": "<sup>25</sup> राजा पर्खाल कै छेउमा सधैँ झैं बसे। उनको ठीक अगाडि जोनाथन बसे। अबनेर राजाको अलिक उता बसे र दाऊदको ठाउँ चाहि रित्तो थियो।",
			"26": "<sup>26</sup> त्यस दिन शाऊलले केही पनि भनेनन्। उनले सोचे, “दाऊदलाई केही भयो होला अथवा रीतिपूर्वक शुद्ध छैन होला।”",
			"27": "<sup>27</sup> तर भोलिपल्ट महिनाको दोस्रो दिन, दाऊदको ठाँउ अझै खाली नै थियो। त्यसपछि शाऊलले आफ्ना छोरा जोनाथनलाई सोधे, “हिजो र आज औंसीको चाडमा यिशैको छोरा किन उपस्थित भएन?”",
			"28": "<sup>28</sup> जोनाथनले भने, “दाऊदले बेतलेहेम घर जान्छु भनेर मसित अनुमति माग्यो।",
			"29": "<sup>29</sup> उसले भन्यो, ‘मेरो परिवारले हाम्रो शहरमा बलिदान चढाउँदैछन् अरे, यसकारण मलाई घरमा जान दिनुहोस्। मलाई त्यहाँ जानु भनी दाज्यूले हुकूम दिएका छन् यदि म तपाईंको साथी हुँ भने जान दिनुहोस् र दाजूहरूसँग भेट्न दिनुहोस्।’ त्यही कारणले दाऊद यहाँ राजाको भोजमा आएन।”",
			"30": "<sup>30</sup> शाऊल जोनाथनसँग साह्रै क्रोधित भए। उसले जोनाथनलाई भने, “तँ दुष्ट आइमाईको छोरा होस, जसले भनेको मान्दैन। अनि त्यो तेरो स्वभाव पनि हो मलाई थाहा छ तैंले दाऊदको पक्ष लिएको छस्। तँ तेरो जन्म दिने आमा र तेरो आफ्नै लागि पनि लाज पार्छस्।",
			"31": "<sup>31</sup> जब सम्म यिशैको छोरा जिउँदो छ तबसम्म तँ राजा हुने छैनस् नत तेरो राज्य पनि हुने छ। अब दाऊदलाई मकहाँ लिएर आइज। त्यो एउटा मरेको मानिस हो।”",
			"32": "<sup>32</sup> जोनाथनले पितालाई भने, “दाऊदलाई चाहिं किन मार्ने? उसले के भूल गरेको छ?”",
			"33": "<sup>33</sup> तर शाऊलले उसको छोरोलाई हत्या गर्नुको लागि भालाले हान्यो। यसबाट जोनाथनलाई थाहा लाग्यो कि उसका पिताले दाऊदलाई मार्ने चाहन्थ्यो।",
			"34": "<sup>34</sup> जोनाथन रिसाएर टेबिल छोडेर हिँडे। जोनाथनलाई साह्रै अशान्ति लाग्यो कि आफ्नो पितासित रिसाएर दोस्रो दिनको भोजन पनि उसले खाएनन्। उसको पिताले उसलाई अपमान गरे। ती उसका पितासित साह्रै क्रोधित थिए अनि एकदमै दुःखी भए किनभने शाऊलले दाऊदलाई मार्न चाहन्थे।",
			"35": "<sup>35</sup> जोनाथनसँग दाऊदको बिदावारी\nभोलिपल्ट बिहान जोनाथन खेतमा गए। जब तिनीहरू एक मत भएका थिए जोनाथन एउटा केटालाई साथमा लिएर दाऊद भए कहाँ गए।",
			"36": "<sup>36</sup> जोनाथनले केटोलाई भने, “कुद्‌दै गएर मैले हानेको काँड लिएर आउँनु।” जब केटो कुद्न थाल्यो जोनाथनले त्यस केटाको टाउको मास्तिर आकाशतिर काँड हाने।",
			"37": "<sup>37</sup> काँड झरेको तिर केटो दगुर्यो। तर जोनाथनले कराएर भने, “काँड तँ भएदेखि धेरै पर पुगेको छ।”",
			"38": "<sup>38</sup> फेरि जोनाथन चिच्यायो, “छिटो गर। काँड लिन जा। त्यहाँ उभिने होइन।” केटोले काँड ल्याएर आफ्नो मालिकलाई दियो।",
			"39": "<sup>39</sup> यी सब के भइरहेछ केटोलाई केही थाहा भएन। खालि जोनाथन र दाऊदलाई मात्र थाहा थियो।",
			"40": "<sup>40</sup> जोनाथनले धनुकाँड केटोलाई बोक्न दिए। त्यसपछि जोनाथनले केटोलाई “शहर जा” भनेर पठाए।",
			"41": "<sup>41</sup> केटो गयो अनि पहाडको अर्को पट्टिबाट दाऊद आए। दाऊदले शिर भूईतिर झुकाएर तीन पटक जोनाथनलाई अभिवादन गरे। त्यसपछि दाऊद र जोनाथनले एक-अर्कामा म्वाई खाए र दुवै रोए। जोनाथन भन्दा दाऊद ज्यादा रोए।",
			"42": "<sup>42</sup> जोनाथनले दाऊदलाई भने, “शान्तिसित जाऊ। हामीले परमप्रभुको नाउँमा एका-दोस्रोलाई इमानदारी साथी हौं भनि प्रतिज्ञा गरिरहेका छौ। हामी र हाम्रा सन्तानहरूका निम्ति पनि परमप्रभु सधैँ भरी साक्षी रहनु हुनेछ।”"
		},
		"21": {
			"1": "<sup>1</sup> पूजाहारी अहिमेलेकसँग दाऊदको भेट\nत्यसपछि दाऊद गए जोनाथन शहरमा फर्किए। दाऊद पूजाहारी अहीमेलेकलाई भेट्न नोब नाउँको शहरमा गए।\nअहीमेलेक दाऊदलाई भेट्न बाहिर गए। अहीमेलेक डरले कामिरहेका थिए। अहीमेलेकले दाऊदलाई भने, “तपाईं किन एकलै हुनुहुन्छ? तपाईंसित कोही पनि छैनन्?”",
			"2": "<sup>2</sup> दाऊदले अहीमेलेकलाई जवाफ दिए, “राजाले मलाई विशेष आदेश दिनुभएको छ। उहाँले भन्नु भएको छ, ‘यो यात्राको उद्देश्य कसैले पनि थाहा नपाओस्। कुनै पनि मानिसले थाहा नपाओस्।’ जो मैले भनें। मैले आफ्ना मानिसहरूलाई कहाँ भेट्नुपर्ने हो बताइदिएको छु।",
			"3": "<sup>3</sup> अब यो भन ‘तिम्रोमा खानेकुरो के छ?’ मलाई पाँचवटा रोटी वा जे छ देऊ।”",
			"4": "<sup>4</sup> पूजाहारीले दाऊदलाई भने, “मेरोमा त्यस्तो साधारण रोटी छैन। तर मसँग केही पवित्र रोटी छन्, यदि तपाईंका अधिकारीहरूले कुनै स्त्रीहरूसँग सहवास गरेका छैनन्, भने यो रोटी खान सक्छन्।”",
			"5": "<sup>5</sup> दाऊदले पूजाहारीलाई जवाफ दिए, “आज भोलि हामीले कुनै स्त्री साथमा राखेका छैनौ। युद्धमा जाँदा हाम्रा मानिसहरू सधैँ पवित्र भएर जान्छन् यहाँसम्म साधारण उद्देश्य सोध्न जाँदा पनि। आज त यो कुरा झन् साँचो हो कारण अति विशेष कुरा सोध्नु छ।”",
			"6": "<sup>6</sup> त्यहाँ पवित्र रोटी बाहेक अरू कुनै रोटी थिएन। यसर्थ पूजाहारीले दाऊदलाई त्यही रोटी दिए। यो त्यही रोटी थियो जो पूजाहारीले परमप्रभुमा चढाएका थिए। पूजाहारीले प्रत्येक दिन त्यो रोटी बाहिर राखेर त्यसको सट्टामा ताजा रोटी राख्थे।",
			"7": "<sup>7</sup> त्यस दिन शाऊलका अधिकारीहरू मध्ये एकजना त्यहीं थिए। उनी हुन एदोमी दोएग। दोएगलाई त्यहाँ परमप्रभुको अघि राखेको थियो।  दोएग शाऊलका गोठालाहरूका प्रमुख थिए।",
			"8": "<sup>8</sup> दाऊदले अहीमेलेकलाई सोधे, “यहाँ एउटा भाला अथवा तरवार केही छ? राजाको ज्यादै जरूरी काम छ। मलाई छिटै जानु छ। मैले तरवार अथवा अन्य कुनै शस्त्र साथमा लिएर आइनँ।”",
			"9": "<sup>9</sup> पूजाहारीले भने, “यहाँ खालि एउटा तरवार छ त्यो पनि पलिश्ती गोल्यतको हो। तपाईंले एलाको बेंसीमा त्यसलाई मारेर लिएको तरवार हो। त्यो तरवार एउटा लुगामा बेह्रेर एपोदको पछाडि राखेको छ। यदि तपाईंलाई चाहिन्छ भने लिए हुन्छ।”\nदाऊदले भने, “त्यो मलाई देऊ। गोल्यतको जस्तो तरवार अरू कसैको छैन।”",
			"10": "<sup>10</sup> दाऊद गातमा भागेर शत्रु कहाँ गए\nशाऊलबाट भागेर दाऊद गातका राजा आकीश कहाँ पुगे।",
			"11": "<sup>11</sup> आकीशका अधिकारीहरूलाई यो राम्रो लागेन। तिनीहरूले भन्छन्, “यो दाऊद हो, इस्राएली भूमिको राजा। यो त्यही व्यक्ति हो जसको गीत इस्राएलीहरू गाउँछन्। तिनीहरू नाच्दै उसको बारेमा यस्तो गाउँछन्;\n“शाऊलले हजार शत्रुहरू मारे,\nतर दाऊदले दश हजार मारे।”",
			"12": "<sup>12</sup> तिनीहरूले के भन्दैछन् दाऊदले ध्यान दिए। दाऊद गातका राजा आकीशसँग डराए।",
			"13": "<sup>13</sup> यसर्थ दाऊदले आकीश र तिनका अधिकारीहरू अघि बौलाहाको बहाना गरे। तिनीहरूका अघि दाऊदले बौलाहा भएको नाटक गरे। उनले मूल ढोकामा नै थुके र दाह्रीमा थुक चुहुनु दिए।",
			"14": "<sup>14</sup> आकीशले आफ्ना अधिकारीहरूलाई भने, “हेर यो मानिसलाई! यो त पागल हो। यस मानिसलाई मेरो उपस्थितिमा पागल कार्य गर्न यहाँ ल्याउनु अवश्यक छैन। भविष्यमा फेरि यो मानिसलाई मेरो घरमा नल्याउनु।",
			"15": "<sup>15</sup> मसँग यस्ता बौलाहा मानिसहरू धेरै छन्। मलाई तिमीहरूले यसलाई यहाँ ल्याउने कुनै काम थियो भन्ने लाग्दैन। फेरि-फेरि यसलाई यहाँ मेरो घरमा नल्याउनुँ!”"
		},
		"22": {
			"1": "<sup>1</sup> दाऊद विभिन्न ठाउँहरूमा जान्छन्\nदाऊदले गात छोडेर भागे। दाऊद आदुल्लामको गुफामा पुगे। दाऊदका दाज्यू-भाइहरू र आफन्तहरूले दाऊद आदुल्लाममा छ भनी सुने। तिनीहरू दाऊदलाई भेट्न गए।",
			"2": "<sup>2</sup> धेरै मानिसहरूले दाऊदलाई साथ दिएका थिए। दाऊदलाई साथ दिनेहरूमा तिनीहरू थिए जो केही न केही समस्यामा परेका थिए। कोही ऋणमा डुबेकाहरू थिए कोही आफ्नै जिन्दगीसँग असन्तुष्टि थिए। यिनीहरूले दाऊदलाई साथ दिए र तिनीहरूका नेता दाऊद थिए। त्यहाँ लगभग 400 पुरूषहरू दाऊदसँग थिए।",
			"3": "<sup>3</sup> मोआबमा मिस्पा जाँदा दाऊदले आदुल्लाम छोडे। मोआबका राजालाई दाऊदले भने, “मलाई परमेश्वरले के गरिरहनु भएको छ भनि नजानुन्जेलसम्म मेरा आमा बाबुलाई तपाईंले यहाँ बस्ने अनुमति दिनुहोस्।”",
			"4": "<sup>4</sup> यसप्रकार दाऊदले उसका आमा-बुबालाई मोआबका राजा कहाँ ल्याए। जबसम्म दाऊद किल्लामा रहे तबसम्म उनका आमा बाबु मोआबका राजासँग बसे।",
			"5": "<sup>5</sup> तर अगमवक्ता गादले दाऊदलाई भने, “किल्लामा नबस्नु होस्। बरू यहूदाको भूमिमा जानुहोस्।” यसर्थ दाऊद त्यहाँबाट हारेतको बन तिर गए।",
			"6": "<sup>6</sup> शाऊलले अहीमेलेकको परिवारलाई नष्ट गरे\nशाऊलले सुने कि उसका मानिसहरूले दाऊद र उनका मानिसहरू कतातिर छन् भन्ने थाहा पाए। शाऊल गिबाको टाकुरामा एउटा रूखको छाहारीमुनि बसिरहेका थिए। शाऊलले हातमा भाला लिएका थिए। तिनका सबै अधिकारीहरू वरिपरि उभिएका थिए।",
			"7": "<sup>7</sup> शाऊलले आफ्ना वरिपरि उभिएका अधिकारीहरूलाई भने, “बिन्यामीनका मानिसहरू हो सुन, तिमीहरूले विचार गरेका छौ, यिशैको छोरा दाऊदले तिमीहरूलाई खेत तथा दाखका बगान दिएको छ? तिमीहरूले यो पनि सोचेका छौ कि दाऊदले तिमीहरूको उन्नति गराउने छ अनि 1,000 र 100 मानिसहरूका अधिकारी बनाउने छ?",
			"8": "<sup>8</sup> तिमीहरू सबैले मेरो विरूद्ध षड्‌यन्त्र रच्दैछौ। तिमीहरू कसैले पनि छोरा जोनाथनले यिशैका छोरासँग सन्धि गरेको छ भनेर भनेनौ कसैले मेरो वास्ता गरेनौ। मेरो छोरा जोनाथनले दाऊद मेरो नोकरलाई सहायता गरेको कुरा कसैले पनि भनेनौ। जोनाथनले दाऊदलाई लुकेर रहनु भन्ने गर्थ्यो ताकि उसले मलाई आक्रमण गर्न सकोस्। अनि दाऊदले अहिले त्यही नै गर्दैछ।”",
			"9": "<sup>9</sup> एदोमी दोएग शाऊलका अधिकारीहरूसँगै उभिरहेको थियो। दोएगले भन्यो, “मैले यिशैका छोरा दाऊदलाई नोब भन्ने ठाउँमा देखें। दाऊद अहीतूबका छोरा अहीमेलेकलाई भेट्न आएका थिए।",
			"10": "<sup>10</sup> अहीमेलेकले परमप्रभुसँग दाऊदका लागि प्रार्थना गरे। तिनले दाऊदलाई भोजन पनि दिए। अनि अहीमेलेकले दाऊदलाई पलिश्ती गोल्यतको त्यो तरवार पनि दिए।”",
			"11": "<sup>11</sup> त्यस पछि राजा शाऊलले पूजाहारीहरूलाई हाजिर गराउनु भनेर मानिसहरूलाई आदेश दिए। शाऊलले अहीतूबका छोरा अहीमेलेकलाई तिनका आफन्तहरूसितै लिएर आउनु भन्ने आदेश दिए। अहीमेलेकका आफन्तहरू नोबमा पूजाहारी थिए। तिनीहरू सबै राजाका अगाडि हाजिर भए।",
			"12": "<sup>12</sup> शाऊलले अहीमेलेकलाई भने, “हे अहीतूबका छोरा, सुन।”\nअहीमेलेकले जवाफ दिए, “भन्नुहोस, मेरो प्रभु।”",
			"13": "<sup>13</sup> शाऊलले अहीमेलेकलाई भने, “तिमी र यिशैका छोरा दाऊदले मेरो विरूद्ध किन षड्‌यन्त्र रच्यौ? तिमीले दाऊदलाई रोटी र तरवार दियौ। परमेश्वरसँग तिमीले उसको लागि विन्तीभाउ पनि गर्यौ। अनि अहिले दाऊद सोझै मलाई आक्रमण गर्ने मौका हेर्दैछ।”",
			"14": "<sup>14</sup> अहीमेलेकले जवाफ दिए, “दाऊद तपाईंको सबभन्दा भरोसा गर्न योग्यको पात्र हो। तपाईंका कुनै अधिकारी पनि दाऊद जस्ता विश्वासी छैनन्। दाऊद त तपाईंका आफ्नै ज्वाइँ हुन्। साथै दाऊद तपाईंका अंगरक्षकहरूका कप्तान पनि हुन्। तपाईंका परिवारका सबैले दाऊदलाई आदर गर्छन्।",
			"15": "<sup>15</sup> दाऊदको लागि मैले प्रार्थना गरेको यो प्रथमचोटि होइन। मलाई र मेरा आफन्तहरूलाई दोष नदिनुहोस्। हामी त तपाईंका सेवक हौं। कहाँ के भइरहेछ मलाई केही थाहा छैन।”",
			"16": "<sup>16</sup> तर राजाले भने, “अहीमेलेक, तिमी र तिम्रा आफन्तहरू मारिनु नै पर्छ।”",
			"17": "<sup>17</sup> अनि राजाले आफ्ना छेऊमा उभिएको रक्षकलाई आदेश दिए, “जाऊ, परमप्रभुका ती पूजाहारीहरूलाई मार। यसो गर्नै पर्छ कारण तिनीहरू दाऊदका मानिसहरू हुन्। दाऊद भाग्दै थियो भन्ने जान्दा जान्दै पनि तिनीहरूले मलाई बताएनन्।”\nतर राजाका अधिकारीहरूले परमप्रभुका ती पूजाहारीहरूलाई मार्न अस्वीकार गरे",
			"18": "<sup>18</sup> यसर्थ राजाले दोएगलाई आदेश दिए। शाऊलले भने, “दोएग, तिमी गएर पूजाहारीहरूलाई मार।” एदोमी दोएग गएर पूजाहारीहरूको हत्या गरे। त्यस दिन दोएगले 85 जना पूजाहारीहरूलाई मारे।",
			"19": "<sup>19</sup> नोब पूजाहारीहरूको शहर थियो। दोएगले नोबका सम्पूर्ण मानिसहरूलाई मारे। दोएगले तरवारले पुरूष, स्त्री बालक र शिशु लगायत सबैलाई मारे। यहाँ सम्म दोएगले गाईं, गोरु, गधा र भेंडा समेत बाँकी राखेनन्।",
			"20": "<sup>20</sup> तर एबीयातार उम्केर भागे, एबीयातार अहीमेलेकका छोरा थिए। अहीमेलेक चाँहि अहीतूबका छोरा थिए। एबीयातार भागेर गए र दाऊदसँग मिले।",
			"21": "<sup>21</sup> एबीयातारले दाऊदलाई शाऊलले पूजाहारीहरू मारेको वृतान्त बताए।",
			"22": "<sup>22</sup> त्यसपछि दाऊदले एबीयातारलाई भने, “त्यस दिन नोबमा मैले एदोमी दोएगलाई देखेको थिएँ। मलाई थाहा थियो त्यसले शाऊललाई बताउने छ भनेर। तिम्रा पिताको परिवालाई मारेकोमा म आफूलाई जिममेवार ठान्छु।",
			"23": "<sup>23</sup> त्यो मानिस (शाऊलले) तपाईंलाई मार्न चाहान्छ र मलाई पनि। तिमी यहीं बस नडराऊ, तिमी यहाँ मसँग सुरक्षित रहने छौ।”"
		},
		"23": {
			"1": "<sup>1</sup> दाऊद कीलाको नगरमा\nमानिसहरूले दाऊदलाई भने, “हेर्नुहोस्, पलिश्तीहरूले कीलाको शहरमाथि आक्रमण गरिरहेका छन्। खलाबाट तिनीहरूले अन्न लूट्न शुरू गरे।”",
			"2": "<sup>2</sup> दाऊदले परमप्रभुलाई सोधे, “के मैले पलिश्तीहरू विरूद्ध युद्ध गर्न जानु?”\nपरमप्रभु परमेश्वरले दाऊदलाई भन्नु भयो, “जाऊ र पलिश्तीहरूमाथि जाइ लाग अनि कीला शहरको रक्षा गर।”",
			"3": "<sup>3</sup> तर दाऊदका मानिसहरूले भने, “हेर्नुहोस्, हामी यहाँ यहूदामा डराएर बसिरहेका छौं। साँच्चै पलिश्ती सेनाहरू अघि पुग्दा हामी झन् ज्यादा डराउने छौं।”",
			"4": "<sup>4</sup> दाऊदले फेरि परमप्रभुलाई सोधे अनि परमप्रभुले जवाफ दिनुभयो, “कीलामा जाऊ। म पलिश्तीहरूलाई हराउन म सहयोग गर्ने छु।”",
			"5": "<sup>5</sup> यसरी दाऊद र तिनका मानिसहरू कीलामा गए। दाऊद र तिनका मानिसहरू पलिश्तीसँग लडे। दाऊदका मानिसहरूले पलिश्तीहरूलाई हराए अनि तिनीहरूका पशु लिए। यसरी दाऊदले कीलाका मानिसहरूलाई बँचाए।",
			"6": "<sup>6</sup> (जब एबीयातार भागेर दाऊदकहाँ पुगे तिनले आफूसँग एउटा एपोद पनि लगेका थिए।)",
			"7": "<sup>7</sup> मानिसहरूले शाऊललाई अहिले दाऊद कीलामा छ भनेर सुनाए। शाऊलले भने, “परमेश्वरले दाऊदलाई मेरा हातमा सुम्पिनु भएको छ। दाऊद फन्दामा परेकोछ। ऊ यस्तो शहरमा पस्यो जहाँ ढोकाहरू छन् र ढोकाहरूमा ताला लगाउने बारहरू छन्।”",
			"8": "<sup>8</sup> शाऊलले आफ्ना सारा सेनाहरूलाई लडाईंकोलागि भेला गराए। ती सेनाहरू कीलामा दाऊदका मानिसहरूसँग युद्ध गर्न तयार रहे।",
			"9": "<sup>9</sup> शाऊलले दाऊदको विरूद्धमा बनाएको योजना उसलाई थाहा भयो। दाऊदले पूजाहारी एबीयातारलाई भने, “एपोद ल्याऊ।”",
			"10": "<sup>10</sup> दाऊदले प्रार्थना गरे, “हे परमप्रभु इस्राएलका परमेश्वर, मैले सुनें कि मेरो कारण शाऊलले यो शहर नष्ट गर्ने योजना बनाउँदैछ।",
			"11": "<sup>11</sup> के साँच्चै हुन आउँछ? के किलाका मानिसहरूले म र मेरो मानिसहरूलाई शाऊलको हातमा सुम्पी देलान्? परमप्रभु इस्राएलका परमेश्वर, म तपाईंको दास हुँ। मलाई बताउँनुहोस्।”\nपरमप्रभुले भन्नु भयो, “हो यो हुन आउँछ।”",
			"12": "<sup>12</sup> दाऊदले फेरि आफ्नो प्रश्न दोहोर्याए, “के कीलाका मानिसहरूले मलाई शाऊलका हातमा सुम्पिनेछन्?”\nपरमप्रभुले भन्नु भयो, “तिनीहरूले त्यसै गर्नेछन्।”",
			"13": "<sup>13</sup> त्यसर्थ दाऊद र तिनका मानिसहरूले कीला छोडिदिए। दाऊदसँगै जानेहरू प्रायः 600 मानिस थिए। दाऊदको र उसका मानिसहरू एक ठाउँ देखि अर्को ठाउँ हुँदै घुमिरहे। शाऊलले दाऊद किलाबाट भागे भन्ने थाहा पाए। यसर्थ शाऊल त्यस शहरतिर गएनन्।",
			"14": "<sup>14</sup> शाऊलले दाऊदको खेदो गर्छन्\nदाऊद मरुभूमिको किल्लामा बस्न पुगे। दाऊद जीपको मरुभूमिको पहाडी क्षेत्रमा गए। शाऊलले सधै नै दाऊदको खोजी गर्थे, तर परमप्रभुले शाऊलको हातबाट दाऊदलाई जोगाउनु हुन्थ्यो।",
			"15": "<sup>15</sup> दाऊद जीपको मरुभूमिको होरेश भन्ने ठाउँमा थिए। त्यहाँ शाऊल दाऊदललाई मार्न आइरहेकाले तिनी साह्रै डराए।",
			"16": "<sup>16</sup> तर शाऊलको छोरा जोनाथन दाऊदलाई भेट्न होरेश गए। जोनाथनले दाऊदलाई परमेश्वरलाई विश्वास गर्न अझ जोड गरे।",
			"17": "<sup>17</sup> जोनाथनले दाऊदलाई भने, “डराउनु पर्दैन। मेरा पिता शाऊलले तपाईंलाई हानि गर्ने छैनन्। तपाईं इस्राएलका राजा हुनुहुनेछ। मचाहिं तपाईं पछिको हुनेछु। यो कुरा मेरा पिताले पनि जान्दछन्।”",
			"18": "<sup>18</sup> जोनाथन र दाऊद दुवैले परमप्रभु अघि एउटा बाचा-बन्धन गरे। त्यसपछि जोनाथन घरतिर लागे अनि दाऊद होरेशमानै बसे।",
			"19": "<sup>19</sup> जीपका मानिसहरूले शाऊललाई दाऊदबारे बताउँछन्\nजीपका बासिन्दाहरू गिबामा शाऊललाई भेट्न आए। तिनीहरूले शाऊललाई भने, “दाऊद हाम्रै क्षेत्रमा लुकेरहेका छन्। ऊ हकीला पहाडको होरेश नाउँको किल्लामा छन्, जो यशीमोनको दक्षिणमा पर्छ।",
			"20": "<sup>20</sup> अब, महाराज, तपाईं जहिले आए पनि हुन्छ। दाऊदलाई तपाईंको जिम्मा लगाउने काम हाम्रो भयो।”",
			"21": "<sup>21</sup> शाऊलले जवाफ दिए, “परमप्रभुले मलाई सहायता गरेकोमा तिमीहरूलाई आशीर्वाद दिऊन्।",
			"22": "<sup>22</sup> जाऊ र उसको बारे अझ जानकारी लेऊ। दाऊद कहाँ बस्छ थाहा गर। दाऊदलाई त्यहाँ कसले देख्छ त्यो पनि बुझिराख। शाऊलले मनमनै विचारे, ‘दाऊद साह्रै चलाख छ। उसले मलाई छक्याउनँ सक्छ।’",
			"23": "<sup>23</sup> दाऊद लुकेको ठाउँहरू पत्ता लगाऊ। त्यसपछि आएर मलाई वृतान्त बताऊ। त्यस पछि म तिमीहरूसित जानेछु। यदि दाऊद ती ठाँउहरूमा कतै छ भने यहूदाको प्रत्येक घरहरूमा छापामारेर म त्यसलाई पत्तालगाउँनेछु।”",
			"24": "<sup>24</sup> अनि जीपका मानिसहरू जीपमा फर्किए पछि शाऊल त्यहाँ गए।\nदाऊद र तिनका मानिसहरू यशीमोनको दक्षिणमा भएको अराबाको माओन वनमा थिए।",
			"25": "<sup>25</sup> शाऊल र तिनका मानिसहरू दाऊदको खोजीमा गए। तर मानिसहरूले दाऊदलाई होसियार गराए। शाऊलले तिमीलाई खोज्दै छ भनेर खबर दिए। त्यसपछि दाऊद माओनको मरुभूमिको तल चट्टानतिर गए। शाऊलले दाऊद माओनको मरुभूमितिर गएको कुरा थाहा पाए। यसरी शाऊल दाऊदको खोजीमा त्यस ठाउँतिर गए।",
			"26": "<sup>26</sup> शाऊल पहाडको एकापट्टि थिए, त्यही पहाडको अर्कोपट्टि दाऊद र तिनका मानिसहरू थिए। दाऊद शाऊलको पञ्जाबाट बाँच्न हतारिदै थिए। शाऊल र उसका सिपाहीहरू दाऊदलाई पक्रन पहाडको चारैतिर घुमिरहेका थिए।",
			"27": "<sup>27</sup> त्यत्ति नै बेला एउटा समाचार-वाहक शाऊल कहाँ आयो अनि जानकारी दियो, “चाँडो आउनुहोस् पलिश्तीहरूले हामीलाई आक्रमण गर्दैछन्।”",
			"28": "<sup>28</sup> यसकारण शाऊलले दाऊदको पिछा गर्न छोडे अनि पलिश्तीहरूसँग लडन गए। यही कारण मानिसहरूले त्यस ठाउँको नाउँ “छुट्टयाउने चट्टान” भन्न थाले।",
			"29": "<sup>29</sup> दाऊद माओनको मरुभूमि त्यागेर एनगदीको छेउको किल्लामा गए।"
		},
		"24": {
			"1": "<sup>1</sup> दाऊदले शाऊललाई लाजमर्दो बनाएको\nशाऊलले पलिश्तीहरूलाई खेदिसकेपछि मानिसहरूले शाऊललाई भने, “दाऊद त एनगदीको नजिककै मरुभूमिमा छ।”",
			"2": "<sup>2</sup> सारा इस्राएलबाट शाऊलले 3,000 मानिसहरूलाई छाने। शाऊलले ती छानिएका मानिसहरू लिएर दाऊद र तिनका मानिसहरूको खोजी गर्न थाले। तिनीहरूले घोरल-चट्टानको छेउछाउमा खोजे।",
			"3": "<sup>3</sup> शाऊल बाटो छेउको भेंडा गोठमा आए। त्यहाँ छेउमा एउटा गुफा थियो। शाऊल आफैंलाई लुकाउन गुफा भित्र पसे। दाऊद र तिनका मानिसहरू धेरै पर गुफाभित्र लुकिरहेका थिए।",
			"4": "<sup>4</sup> मानिसहरूले दाऊदलाई भने, “आज परमप्रभुले भन्नुभएको दिन होइन? उहाँले तपाईंलाई भन्नु भएको थियो, ‘तिम्रो शत्रुलाई म तिम्रो हातमा सुम्पिदिनेछु, त्यसपछि तिम्रो इच्छानुसार तिम्रो शत्रुलाई जे गर्छौ गर।’”\nत्यसपछि दाऊद घस्रँदै-घस्रँदै शाऊलको छेउ पुगे। दाऊदले शाऊलको खास्टोको एक टुक्रा चाल नपाई काटिदिए।",
			"5": "<sup>5</sup> पछि दाऊदलाई शाऊलको टुक्रा खास्टो काटेकोमा नराम्रो लाग्यो।",
			"6": "<sup>6</sup> दाऊदले मानिसहरूलाई भने, “परमप्रभुले मलाई आफ्नो मालिकलाई क्षति गर्नबाट रोकुन्। शाऊल परमप्रभुले छान्नु भएको राजा हुन् र मैले त्यसो नगरको भए हुने थियो।”",
			"7": "<sup>7</sup> दाऊदले यसो भनेर आफ्ना मानिसहरूलाई रोकी राखे। उसले तिनीहरूलाई शाऊललाई हानि गर्न अनुमति दिएनन्।\nशाऊल गुफा छोडेर आफ्नै बाटो लागे।",
			"8": "<sup>8</sup> दाऊद गुफाबाट निस्किएर आए र शाऊललाई बोलाउँदै भने, “हे मेरो प्रभु! महाराजा!”\nशाऊलले पछि फर्केर हेरे। दाऊदले शिर झुकाए।",
			"9": "<sup>9</sup> दाऊदले शाऊललाई भने, “जब मानिसहरूले भन्छन्, ‘दाऊदले तपाईंलाई चोट पुर्याउने योजना बनाइरहेछ’? तपाईंले किन तिनीहरूको कुरा सुन्नुभयो।",
			"10": "<sup>10</sup> म तपाईंलाई हानि पुर्याउने छैन। तपाईं आफ्नै आँखाले देख्न सक्नुहुन्छ। परमप्रभुले तपाईंलाई आज मेरो हातमा गुफामै सुम्पिदिनु भएको थियो। तर मैले तपाईंको हत्या गरिन। तपाईंमाथि म कृपालु भएँ। मैले भनें, ‘म मेरो मालिकलाई हानि गर्ने छैन। किनभने उनी परमप्रभुले छान्नु भएको राजा हुन्।’",
			"11": "<sup>11</sup> यो लुगाको टुक्रा मेरो हातमा हेर्नुहोस्। मैले यो टुक्रा तपाईंको खास्टोबाट काटेको हुँ। म तपाईंलाई मार्न सक्थें, तर मैले त्यसो गरिनँ। यो कुरा तपाईंलाई थाहा होस् भन्ने म चाहान्छु। म तपाईंलाई यो जनाउन चाहान्छु कि तपाईंको विरूद्धमा मैले कुनै योजना बनाएको छैन। मैले तपाईंलाई कुनै अनिष्ट गरेको छैन। तर तपाईं मेरो पछि लागेर मलाई मार्न चाहानुहुन्छ।",
			"12": "<sup>12</sup> परमप्रभुले नै यसको न्याय गर्नुहुन्छ। मलाई गर्नु भएको अत्यचारको बद्ला परमप्रभुले तपाईंलाई दण्डित तुल्याउनु हुनेछ। तर म आफैं तपाईंको विरूद्धमा जाँदिन।",
			"13": "<sup>13</sup> त्यहाँ एउटा पूरानो भनाइ नै छ,\n‘खराब कुरा दुष्ट मानिसहरूबाट फैलिन्छ।’\n“मैले कुनै नराम्रो गरिनँ। म दुष्ट मानिस पनि त होइन। म तपाईंलाई हानि गर्नेछैन।",
			"14": "<sup>14</sup> तपाईंले कसलाई खेदो गर्नु भएको छ? इस्राएलका राजा कसको विरूद्धमा आक्रमणमा ओर्लिएका छन्? तपाईंले आफ्ना वास्तविक शत्रुलाई खेदिरहनु भएको छैन। तपाईं मरेको कुकुर अथवा उपियाँको पछि लाग्नु जस्तै हो।",
			"15": "<sup>15</sup> परमप्रभुले यस मामलामा न्याय गर्नुहुनेछ। उहाँले नै हामी दुइ बीचमा निर्णय गरून्। जब उहाँले तपाईंबाट मलाई जोगाउनु हुन्छ परमप्रभु आफैंले देखाउनु हुनेछ कि म सही छु।”",
			"16": "<sup>16</sup> दाऊद चुप्प लागे अनि शाऊलले सोधे, “यो तिम्रो आवाज हो, मेरो छोरा दाऊद?” त्यसपछि शाऊल रूनथाले शाऊल धेरै नै रोए।",
			"17": "<sup>17</sup> शाऊलले भने, “तिमी म भन्दा धर्मी अनि असल छौ तिमीले म प्रति असल काम गर्यौ, तर म नै तिम्रो विरूद्धमा थिएँ।",
			"18": "<sup>18</sup> मेरो लागि गरेका असल कामहरूका बारे तिमीले भन्यौ। परमप्रभुले नै मलाई तिम्रो सामु ल्याउनु भयो। तर तिमीले मलाई केही गरेनौ।",
			"19": "<sup>19</sup> यसले यो देखाउँछ म तिम्रो शत्रु होइन। मानिसले शत्रुलाई सहजै पक्रन सक्दैन र सहजै छोड्दैन पनि उसले शत्रुलाई राम्रो गर्दैन। परमप्रभुले तिमीलाई इनाम दिनुहुन्छ, कारण तिमीले मलाई केही गरेनौ।",
			"20": "<sup>20</sup> म जान्दछु तिमी राजा हुने नै छौ। तिमीले इस्राएल माथि शासन गर्ने नै छौ।",
			"21": "<sup>21</sup> अब मलाई एउटा वचन देऊ। परमप्रभुको शपथ खाएर वचन देऊ कि मेरो सन्तानहरू कसैलाई पनि मार्ने छैनौ। मेरा पिताको परिवारबाट मेरो नाउँ निशान हटाउँने छैनौ।”",
			"22": "<sup>22</sup> दाऊदले शाऊलसँग शपथ खाए। दाऊदले शाऊलका सन्ताहरू कसैलाई पनि मार्ने छैनन् भन्ने वचन सुनेर शाऊल घरतिर फर्के। दाऊद र तिनका मानिसहरू किल्लातिर लागे।"
		},
		"25": {
			"1": "<sup>1</sup> दाऊद र मूर्ख नाबाल\nशमूएलको मृत्यु भयो। सारा इस्राएलीहरू भेला भएर शमूएलको मृत्युमा दुःख प्रकट गरे। तिनीहरूले शमूएललाई तिनको आफ्नै घर रामामा गाडे।\nत्यसपछि दाऊद पारानको मरुभूमितिर लागे",
			"2": "<sup>2</sup> माओनमा एकजना असाध्यै धनी मानिस थिए। ऊसित 3,000 भेंडाहरू र 1,000 बाख्राहरू थिए। उसको कर्मेलमा धन-सम्पत्ति थियो। ऊ त्यहाँ आफ्नो भेंडाको ऊन कत्रन गयो।",
			"3": "<sup>3</sup> त्यस मानिसको नाउँ नाबाल थियो। ऊ कालेब सन्तानको थियो। नाबालकी पत्नी अबीगेल थिईन, ऊ सुन्दरी र बुद्धिमती थिईन्। तर नाबाल, अति निष्ठुर र नीच स्वभावको मानिस थियो।",
			"4": "<sup>4</sup> नाबालले भेंडाको ऊन कत्रँदैछ भन्ने खबर दाऊदले मरुभूमिको क्षेत्रमा नै सुने।",
			"5": "<sup>5</sup> दाऊदले दशजना युवकहरूलाई नाबालकहाँ पठाए। दाऊदले तिनीहरूलाई भने, “कर्मेलम जाऊ र नाबाललाई मेरो नाउँमा ‘अभिवादन’ गर।”",
			"6": "<sup>6</sup> उसलाई भन, “म आशा गर्छु कि तपाईंको परिवार र तपाईं अधिकार गर्नु भएका सबै असलै होलान्।",
			"7": "<sup>7</sup> मैले थाहाँ पाएँ कि तपाईं आफ्नो भेंडाको ऊन कत्रँदै हुनुहुन्छ। तपाईंका गोठालाहरू केही समयदेखि हामीसित थिए। हामीले तिनीहरूसित खराब व्यवहार गरेनौ। जब तिनीहरू कर्मेलमा थिए तिनीहरूको केही पनि हराएन अथवा चोरेनन्।",
			"8": "<sup>8</sup> तपाईंले सेवकहरूलाई सोध्नुहोस् अनि तिनीहरूले सत्य बोल्ने छन्। मेरो जवान मानिसहरूप्रति दया गर्नुहोस्। अहिले उत्सवको समय भएको कारण, तपाईंले जे गर्नु सक्नुहुन्छ मेरो सेनाहरूलाई गरिदिनुहोस्। मेरो निम्ति यति काम गरिदिनुहोस्, तपाईंको मित्र दाऊद।”",
			"9": "<sup>9</sup> दाऊदका मानिसहरू नाबालकहाँ गए। तिनीहरूले दाऊदको समाचार नाबाललाई सुनाए।",
			"10": "<sup>10</sup> तर नाबालले दुष्ट आचारण देखाए र भन्ने, “दाऊद को हो? यो यिशैको छोरो को हो? आज भोलि धेरै कमाराहरू आफ्ना मालिक छोडेर भाग्दैछन्।",
			"11": "<sup>11</sup> मसँग रोटी-पानी छ। म सँग मासु पनि छ जुन मैले मेरो भेंडाहरूबाट ऊन कत्रँने नोकरहरूका निम्ति तयार पारेकोछु। तर म यो मासु रोटी अनि पानी नचिनेको मानिसहरूलाई दिने छैन्।”",
			"12": "<sup>12</sup> दाऊदका मानिसहरू फर्केर गएर नाबालले भनेका सबै कुराहरू दाऊदलाई सुनाए।",
			"13": "<sup>13</sup> त्यसपछि दाऊदले आफ्ना मानिसहरूलाई भने, “आफ्नो-आफ्नो तरवार भिर।” यसरी दाऊद र उनका मानिसहरूले तरवार भिरे। झण्डै 400 मानिसहरू दाऊदसँग गए। 200 मानिसहरू मात्र सर-सामानसँग बसे।",
			"14": "<sup>14</sup> अबीगेलले विपत्ती टारिन्\nनाबालको एउटा नोकरले नाबालकी पत्नी अबीगेललाई भन्यो, “दाऊदले आफ्ना समाचार-वाहकहरूलाई हाम्रा मालिक नाबाल भए ठाउँसम्म पठाए। तर नाबाल तिनीहरू प्रति तुच्छ थिए।",
			"15": "<sup>15</sup> ती मानिसहरू हाम्रा निम्ति साह्रै असल थिए। जब हामी भेंडाहरू मैदानमा लिएर जान्थ्यौं। दाऊदका मानिसहरू हामीसितै दिनभरी हुन्थे। अनि तिनीहरूले हाम्रो कुनै नोक्सान गरेनन्। तिनीहरूले कहिले पनि केही चोरेनन्।",
			"16": "<sup>16</sup> दाऊदका मानिसहरूले दिन रात हाम्रो रक्षा गर्थे। हामीले भेंडा चराइरहेको समयमा पनि तिनीहरूले हाम्रो चारैतिरबाट पर्खालले झैं रक्षा गर्यो।",
			"17": "<sup>17</sup> यसको विषयमा सोचेर केही गर्नुहोस्। नाबाल साह्रै मूर्ख छन् ऊसित कुरा गर्न सकिंदैन अनि आउने विपत्तिको विषयमा उसलाई चेताउनी दिन सकिंदैन। अब नाबाल र तिनका सन्तानमाथि ठूलो विपत्ती आइलाग्नेछ।”",
			"18": "<sup>18</sup> अबीगेलले हतार-हतार 200 वटा रोटी, दुइ मशक दाखरस पाँचवटा पकाएको भेंडा, पाँच पाथी पकाएको चिउरा 100 झुप्पा किसमिस र सुकाएको 200 झुप्पा नेभारा तयार पारी गधाहरूमा लदाइन्।",
			"19": "<sup>19</sup> त्यसपछि नोकरहरूलाई भनिन्, “यो लिएर जाऊ। म पछि-पछि आउँदै छु।” तर यी सब तिनले आफ्नो लोग्नेलाई भनिनन्।",
			"20": "<sup>20</sup> गधामा चढेर अबीगेल त्यस पहाडको अर्को पट्टिको पखेरामा गइन्। तिनले दाऊदलाई आफ्ना मानिसहरूसँग पहाडको अर्को पट्टिबाट आउँदै गरेका भेटिन्।",
			"21": "<sup>21</sup> अबीगेललाई भेट्न भन्दा अघि दाऊदले भन्दै थिए, “मैले त्यस मरुभूमिमा नाबालको सम्पत्ति रक्षा गरें। मैले यति सुरक्षा गरें कि नाबालले केही नोक्सान भोगेनन्। मैले गरेको सबै व्यर्थ भयो। मैले भलाई गरें तर त्यसले त दुर्व्यवहार गर्यो।",
			"22": "<sup>22</sup> यदि मैले नाबालको परिवारका कुनै पनि पुरूषलाई भोलि बिहान सम्म जीवित रहन दिएँ भने मलाई परमेश्वरले दण्ड दिउन्।”",
			"23": "<sup>23</sup> त्यति नै बेला अबीगेल आइपुगिन्। अबीगेलले दाऊदलाई देख्न साथ गधाबाट ओर्लिन् र दाऊदको अघि शिर झुकाईन्।",
			"24": "<sup>24</sup> तिनको अनुहार दाऊदको अघि भूईँमा निहुराए र अबीगेलले भनिन्, “महाशय कृपया, मलाई केही भन्न दिनुहोस्। मैले भनेका कुरा सुनिदिनुहोस्। जे भयो त्यसका निम्ति मलाई दोषी ठहराउनुहोस्।",
			"25": "<sup>25</sup> तपाईंले पठाउँनु भएका मानिसहरू मैले देखिनँ। महाशय त्यो काम नलाग्ने नाबालको कुरामा ध्यान नदिनुहोस्। जस्तो उसको नाउँ छ मानिस पनि त्यस्तै छ। उसको नाउँको अर्थ ‘मूर्ख,’ भए जस्तो ऊ पनि मूर्ख नै छ।",
			"26": "<sup>26</sup> परमप्रभुले तपाईंलाई निर्दोष मानिसहरूको हत्या गर्नबाट जोगाउनु भयो। जस्तो परमप्रभु जीवित हुनुहुन्छ त्यस्तै तपाईं पनि जीवित हुनुहुन्छ। म प्रार्थना गर्छु कि तपाईंका सबै शत्रुहरू अनि जसले तपाईंलाई क्षति गराउँन चाहन्छन् तिनीहरू सबै नाबाल जस्तै होउन्।",
			"27": "<sup>27</sup> मैले तपाईंलाई केही उपहार ल्याएकी छु। तपाईंका मानिसहरूलाई दिनुहोस्।",
			"28": "<sup>28</sup> मेरो भूलमा क्षमा दिनु होस्। मलाई थाहा छ परमप्रभुले तपाईंको परिवारालाई शक्तिशाली तुल्याउनु हुनेछ र धेरै राजाहरू तपाईंकै परिवारबाट आउनेछन्। परमप्रभुले यसो गर्नु हुनेछ कारण तपाईंले उहाँको निम्ति युद्ध गर्नु भएको छ। यसर्थ तपाईं बाँचुन्जेल मानिसहरूले तपाईमा कुनै खोट पाउने छैनन्।",
			"29": "<sup>29</sup> यदि कसैले तपाईंलाई मार्न खेदो गर्यो भने परमप्रभु तपाईंको परमेश्वरले तपाईंको जीवनलाई बचाउँनु हुनेछ। तर घुँयेत्रोबाट ढुङ्गा फ्याँकिए झैं परमप्रभुले तपाईंका शत्रुहरूलाई फ्याँक्नु हुनेछ।",
			"30": "<sup>30</sup> तपाईंलाई परमप्रभुले धेरै राम्रो कार्य गर्ने वचन दिनु भएको छ। उहाँले आफ्नो वचन पूरा गर्नुहुनेछ। परमेश्वरले तपाईंलाई इस्राएलको अगुवा बनाउनुहुनेछ।",
			"31": "<sup>31</sup> तर तपाईं निर्दोष मानिसहरूको हत्या गरेर भूल नगर्नुहोस्। अझै भड्खालोमा नपस्नुहोस्। परमप्रभुले तपाईंलाई जब सफलता हासिल गराउनु हुन्छ दया गरी मलाई सम्झनुहोला।”",
			"32": "<sup>32</sup> दाऊदले अबीगेललाई जवाफ दिए, “परमप्रभु इस्राएलका परमेश्वरको प्रशंसा गर। आज उहाँले तिमीलाई म भए कहाँ पठाउनु भएको निम्ति प्रशंसा गर।",
			"33": "<sup>33</sup> तिम्रो सही विचारको निम्ति परमेश्वरले तिमीलाई आशीर्वाद दिऊन्। आज निर्दोष मानिसहरूको हत्या गर्नबाट तिमीले मलाई रोक्यौ।",
			"34": "<sup>34</sup> साँच्चै नै परमप्रभु इस्राएलका परमेश्वर जीवित हुनुहुन्छ म त्यो भन्छु। यदि तिमी ठीक समयमा नआइपुगेका भए भोलि बिहान सम्ममा नाबालका एउटै मानिस पनि जीवित रहने थिएनन्।”",
			"35": "<sup>35</sup> त्यसपछि दाऊदले उपहार स्वीकार गरे। दाऊदले भने, “शान्तिसित घर जाऊ। मैले तिम्रा सबै कुरा सुने र म तिमीले भने जस्तै गर्नेछु।”",
			"36": "<sup>36</sup> नाबालको मृत्यु\nअबीगेल घर फर्किन्। नाबाल घरैमा भोज मनाइरहेका थियो। नाबाल राजाले जस्तै भोजन खाँदै थिए। ऊ मग्न थिए अनि खूबै पिएका थिए। यसर्थ भोलि बिहान नहुन्जेल अबीगेलले केही भनिन।",
			"37": "<sup>37</sup> भोलि पल्ट बिहान नाबाल शान्त थियो। यसर्थ उसकी पत्नीले सबै बताइन्। त्यत्ति नै बेला नाबाल मुटुको वेदनाले पीडित भयो।",
			"38": "<sup>38</sup> झण्डै दश दिनपछि परमप्रभुले नाबाललाई मृत्यु दण्ड दिनुभयो।",
			"39": "<sup>39</sup> दाऊदले नाबालको मृत्युको खबर सुने। दाऊदले भने, “परमप्रभुको महिमा होस्। नाबालले मेरो विषयमा दुष्ट कुरा गरे, तर परमप्रभु परमेश्वरले मेरो पक्ष लिनुभयो। परमप्रभुले मलाई पाप गर्नबाट बचाउँनु भयो अनि परमप्रभुले नाबाललाई उसका अपराधको निम्ति मृत्यु दण्ड दिनुभयो।”\nत्यसपछि दाऊदले अबीगेललाई समाचार पठाउनु भयो अनि दाऊदले उनलाई आफ्नी पत्नी हुने प्रस्ताव राख्नुभयो।",
			"40": "<sup>40</sup> दाऊदका नोकरहरू कर्मेलमा आएर अबीगेललाई भने, “दाऊदले हामीलाई पठाउनु भएको छ। तपाईं उहाँकी पत्नी हुनुहोस् भन्नु भनी उहाँले समाचार पठाउनु भएको छ।”",
			"41": "<sup>41</sup> अबीगेलले शिर झुकाइन् र भनिन्, “म तपाईंको दासी हुँ। म तपाईंको सेवा गर्न राजी छु। म मेरो स्वामी दाऊदका सेवकहरूका खुट्टा धुन तयार छु।”",
			"42": "<sup>42</sup> हतार-हतार तयार भएर गधामा चढी ती दूतहरूसँग अबीगेल बाटो लागिन्। अबीगेलले आफूसित पाँचजना सहेंलीहरू लगिन् र आफू दाऊदकी पत्नी भइन्।",
			"43": "<sup>43</sup> दाऊदले यिज्रेलकी अहीनोमलाई पनि विवाह गरेका थिए। दाऊदका अहीनोम र अबीगेल दुवै पत्नी भए।",
			"44": "<sup>44</sup> दाऊदले शाऊलकी छोरी मीकललाई पनि विवाह गरेका थिए। तर शाऊलले तिनलाई दाऊदबाट खोसे। शाऊलले गल्लीमको लेशको छोरा पलिश्तीलाई मीकल दिएका थिए।"
		},
		"26": {
			"1": "<sup>1</sup> दाऊद र अबीशै शाऊलको छाउनीमा पसे\nजीपीका मानिसहरू शाऊललाई भेट्न गिबा गए। तिनीहरूले शाऊललाई भने, “हकीलाको पहाडमा दाऊद लुक्दैछ। यो पहाड यशीमोन देखि पारि पर्छ।”",
			"2": "<sup>2</sup> शाऊल जीपको मरुभूमितिर गए। शाऊलले 3,000 सेनाहरू इस्राएलबाट छानेर लगे। शाऊल र उसका सेनाहरूले जीपको मरुभूमितिर दाऊदलाई खोजे।",
			"3": "<sup>3</sup> शाऊलले उसको छाउनी हकीलाको पहाडमाथि बाटोको छेउमा यशीमोन तिर फर्काएर बनाए।\nदाऊद त्यही मरुभूमिमा थिए। शाऊलले पीछा गर्दैछन् भन्ने कुरा तिनलाई थाहा थियो।",
			"4": "<sup>4</sup> यसर्थ दाऊदले चेवाहरू पठाए। दाऊदलाई थाहा लाग्यो शाऊल हकीलामा छ भनेर।",
			"5": "<sup>5</sup> दाऊद शाऊलको छाउनीमा गए। शाऊल र अबनेर कहाँ सुतिरहेका थिए दाऊदले देखे। (नेरका छोरो अबनेर शाऊलका सेनाको सेनापति थिए।) शाऊल छाउनीको बीचमा सुते। सेनाहरू शाऊलको वरिपरि सुती रहेका थिए।",
			"6": "<sup>6</sup> दाऊदले हित्ती अहीमेलेक र सरूयाहका छोरा अबीशैसित बात गरे। (अबीशै योआबका भाइ थिए।) तिनले भने, “मसँग शाऊलको छाउनीमा को जान्छ?”\nअबीशैले जवाफ दिए, “म तपाईंसँग जानेछु।”",
			"7": "<sup>7</sup> जब रात पर्यो दाऊद र अबीशै शाऊलको छाउनीमा गए। शाऊल छाउनीको मध्य भागमा मस्त सुतिरहेका थिए। तिनको भाला तिनको शिर नेर भूँईमा गाडिएको थियो। अबनेर अरू सैनिकहरूसँगै शाऊलको वरिपरी मस्त सुतिरहेका थिए।",
			"8": "<sup>8</sup> अबीशैले दाऊदलाई भने, “आज परमेश्वरले तपाईंको शत्रुलाई तपाईंको हातमा सुम्पिनु भएकोछ उसको भालाले उसैलाई रोप्न मलाई दिनुहोस्। म एकै खेपमा काम सिध्याउँछु।”",
			"9": "<sup>9</sup> तर दाऊदले अबीशैलाई भने, “शाऊललाई नमार! परमेश्वरले छान्नु भएको राजालाई घात गर्दा दण्ड पाइन्छ।",
			"10": "<sup>10</sup> साँच्चै नै परमेश्वर जीवित हुनुहुन्छ परमप्रभु आफैंले शाऊललाई दण्ड दिनुहुनेछ। शाऊल युद्ध भूमिमा मरोस अथवा स्वभाविक मृत्यु होस्।",
			"11": "<sup>11</sup> तर म परमप्रभुले उहाँका चुनिएका राजालाई केही नोक्सान नपुर्याउन भनी उहाँलाई विन्ति गर्छु। शाऊलको शिरमा भएको भाला र पानीको सुराही उठाउ अनि हामी जानेछौ।”",
			"12": "<sup>12</sup> यसरी दाऊदले भाला र पानीको सुराही शाऊलको शिरकै छेउबाट लिए। त्यसपछि दाऊद र अबीशै छाउनीबाट निस्किए। तिनीहरूले के गरे कसैले पनि थाहा पाएन। न कसैले देख्यो न त कोही ब्यूँझियो। शाऊल र तिनका सैनिकहरू गहिरो निद्रामा परेको कारण परमप्रभुले नै तिनीहरूलाई गहिरो निद्रामा पारिदिनुभएको थियो।",
			"13": "<sup>13</sup> दाऊदले फेरि पनि शाऊललाई लाजमर्दो बनाए\nदाऊद बेंसी पार गरेर पारिपट्टि पुगे। शाऊलको छाउनीबाट पारी पहाडको टाकुरामा पुगे। दाऊद र शाऊलका छाउनीहरू धेरै टाढा-टाढा थिए।",
			"14": "<sup>14</sup> दाऊदले सेना र खास गरी नेरका छोरा अबनेरलाई ठूलो स्वरमा कराएर भने, “अबनेर, मलाई जवाफ देऊ!”\nअबनेरले जवाफ दिए, “तिमी को हौ? किन महाराजलाई डाकिरहेछौ?”",
			"15": "<sup>15</sup> दाऊदले भने, “तिमी मानिस हौ, कि होइनौ? अनि इस्राएल भरी सबभन्दा महान तिमी नै छौ। के यो सत्य होइन? त्यसो हो भने आफ्नो प्रभु महाराजको रक्षा किन ठिकसित गरेनौ? एउटा साधारण मानिसले तिम्रो राजालाई मार्न छाउनी भित्र आयो।",
			"16": "<sup>16</sup> तिमीले एकदमै नराम्रो काम गर्यौ। जस्तो कि परमप्रभु जीवित हुनुहुन्छ, तिमी र तिम्रा मानिसहरू मर्नु पर्छ। किनभने तिमीहरूले आफ्ना परमप्रभुका अभिषिक्त राजाको रक्षा गरेनौ। अब हेर राजाको सिरानी छेउको भाला र पानी पिउने सुराही खोई, कहाँ छन्?”",
			"17": "<sup>17</sup> शाऊलले दाऊदको स्वर चिने। शाऊलले भने, “मेरो छोरा दाऊद के यो स्वर तिम्रो हो?”\nदाऊदले जवाफ दिए, “ज्यू, यो मेरै आवाज हो, मेरो प्रभु महाराजा।”",
			"18": "<sup>18</sup> दाऊदले फेरि भने, “हे मेरा प्रभु, तपाईंले मेरो खेदो किन गर्नु हुदैछ? मैले के भूल गरें? मेरो के कसुर छ?",
			"19": "<sup>19</sup> हे मेरो मालिक राजा म तपाईंको नोकरलाई सुन्नु होस् यदि परमप्रभुले नै तपाईंलाई म माथि रिस उठाउनु भएको हो भने एउटा भेट स्वीकार गरिदिनुहोस्। तर यदि मानिसले तपाईंलाई म माथि रिस उठाउनु भएको हो भने, परमप्रभुले तिमीहरूलाई केही नराम्रो गर्नुहुनेछ। परमप्रभुले दिनुभएको भूमि त्याग्न मलाई मानिसहरूले जबरजस्ती गरे। तिनीहरूले मलाई भने, ‘जाऊ र विदेशीसँग बस र विदेशी देवतालाई पूजागर।’",
			"20": "<sup>20</sup> मलाई परमप्रभुबाट टाढा मर्न नदेऊ। इस्राएलका राजा झींङ्गाको पछि लाग्दैछ। तपाईं पहाडमा भ्यागुत्ताहरूको पछि लागेको मानिस जस्तो हुनुभयो।”",
			"21": "<sup>21</sup> तब शाऊलले भने, “मैले पाप गरें। मेरो छोरा दाऊद यता आऊ आज मलाई प्रमाण गरेर देखाइ देऊ। तिम्रो निम्ति मेरो ज्यान महत्वपूर्ण छ। अब म तिमीलाई चोट पुर्याउने छैन। मैले मूर्खले झैं ठूलो भूल गरें।”",
			"22": "<sup>22</sup> दाऊदले जवाफमा भने, “राजाको भाला यहाँ छ। यो लिन कुनै एक जना जवानलाई पठाउनुहोस्।",
			"23": "<sup>23</sup> परमप्रभुले हरेक मानिसहरूलाई उसको धार्मिकता र विश्वसनीयताको निम्ति इनाम दिनुहुन्छ। उहाँले राम्रो काम गर्ने मानिसलाई इनाम दिनुहुन्छ र नराम्रो कार्य गर्नेलाई दण्ड दिनुहुन्छ। परमप्रभुले तपाईंलाई हराउन आज मलाई मौका दिनुभयो। तर म परमप्रभुका अभिषिक्त राजालाई हानि गर्न चाहँदिन।",
			"24": "<sup>24</sup> आज मैले प्रमाण गरें कि मेरो निम्ति तपाईंको जीवन महत्वपूर्ण छ। यस्तै प्रकार परमप्रभुले पनि देख्नु हुनेछ मेरो प्राण उहाँलाई महत्वपूर्ण छ। परमप्रभुले मलाई प्रत्येक खतराबाट बचाउँनु हुनेछ।”",
			"25": "<sup>25</sup> त्यसपछि शाऊलले दाऊदलाई भने, “परमेश्वरले तिमीलाई आशीर्वाद दिऊन्, मेरो छोरा दाऊद। तिमीले ठूला-ठूला कार्यहरू गर्नेछौ र सफलता पनि पाउँनेछौ।”\nदाऊद आफ्नो बाटो लागे र शाऊल घरतिर फर्किए।"
		},
		"27": {
			"1": "<sup>1</sup> दाऊद पलिश्तीहरूसँग रहन थाले\nतर दाऊदले आफैं सोचे, “शाऊलले मलाई एक दिन पक्रिएर मार्ने छ। सबभन्दा राम्रो मलाई यो हुन्छ पलिश्तीहरू भएकोमा भागेर जानु त्यसपछि शाऊलले मलाई इस्राएलमा खोज्न छोड्ने छ। त्यसरी म शाऊलबाट उम्कन सक्नेछु।”",
			"2": "<sup>2</sup> यसरी दाऊद र तिनका 600 मानिसहरूले इस्राएल त्यागे। तिनीहरू माओकका छोरा आकीशकाँ गए। आकीश गातका राजा थिए,",
			"3": "<sup>3</sup> दाऊद, तिनका मानिसहरू र तिनका परिवार गातमा आकीशसँग बसे। दाऊदकी दुवै पत्नी तिनीसितै थिए। तिनीहरू यिज्रेली अनहीनोअम अनि कर्मेली अबीगेल थिए। अबीगेल नबालकी विधवा थिईन्।",
			"4": "<sup>4</sup> मानिसहरूले शाऊललाई भने कि दाऊद भागेर गात गए। यसर्थ शाऊलले दाऊदको पिछा गर्न छोडे।",
			"5": "<sup>5</sup> दाऊदले आकीशसँग भने, “यदि तपाईं मसँग प्रसन्न हुनुहुन्छ भने मलाई शहरमा एउटा ठाउँ तोकिदिनुहोस्। म तपाईंको सेवक मात्र हुँ। म त्यहाँ बस्नेछु, यहाँ राजधानीमा होइन।”",
			"6": "<sup>6</sup> त्यस दिन आकीशले दाऊदलाई सिकलग शहर दिए। सिकलग चाँहि त्यही दिन देखि यहूदा राजाहरूको वंशमा रहेकोछ।",
			"7": "<sup>7</sup> दाऊद पलिश्तीहरूसँग एक बर्ष चार महिना बसे।",
			"8": "<sup>8</sup> दाऊदले आकीश राजालाई छक्काए\nदाऊद र तिनका मानिसहरूले मिश्रमा अवस्थित तलैम देखि शूरका अमालेकी गशूर र गिज्रीहरूसित युद्ध गरे। दाऊदले तिनीहरूलाई हराएर सम्पत्ति लुटे।",
			"9": "<sup>9</sup> दाऊदले त्यस इलाकाका मानिसहरूलाई हराए अनि सबैलाई मारे दाऊदले त्यहाँ भेंडा, गाई-गोरू, गधा ऊँठ र लुगाहरू लुटे। ऊ फर्किए पछि यी लुटेकाहरू आकीशलाई दिए।",
			"10": "<sup>10</sup> दाऊदले धेरै पल्ट यसो गरे। प्रत्येक चोटि नै आकीशले कता कोसँग युद्ध गर्यौ भनेर सोध्थे। दाऊदले भन्थे, “मैले दक्षिणपट्टिको यहूदमाथि” अथवा “मैले दक्षिणपट्टिको यरहमेलीमा,” अथवा “दक्षिणपट्टिको केनीहरूमाथि आक्रमण गरें ।”",
			"11": "<sup>11</sup> दाऊदले गातमा कहिले पनि कुनै लोग्ने मानिस अथवा स्त्रीहरू जिउँदो ल्याएनन्। दाऊदले ठान्थे, “यदि मैले जिउँदो मानिस ल्याएँ भने वास्तवमा म के गर्दैछु, आकीशले थाहा पाउनेछ।”\nजब सम्म दाऊद पलिश्तीको भूमिमा बसे तिनले त्यसो गरे।",
			"12": "<sup>12</sup> आकीशले दाऊदलाई पत्याउन थाले। आकीशले आफैंलाई भन्थे, “अब दाऊदलाई आफ्नै मानिसहरूले घृणा गर्ने छन्। इस्राएलीहरूले दाऊदलाई साह्रै घृणा गर्नेछन्। अब दाऊदले सधैँ मेरो सेवा गर्नेछ।”"
		},
		"28": {
			"1": "<sup>1</sup> पलिश्तीहरू युद्धको निम्ति तयार\nत्यस बेला पलिश्तीहरूले इस्राएलमाथि आक्रमण गर्न सेनाहरू भेला गराए। आकीशले दाऊदलाई भने, “तपाईंले त्यो जान्नु पर्छ तपाईं र तपाईंका मानिसहरू इस्राएलको विरूद्ध युद्धमा मसँग जानुपर्छ।”",
			"2": "<sup>2</sup> दाऊदले जवाफ दिए, “निश्चय! तपाईंले देख्नुहुन्छ म के गर्नु सक्छु।”\nआकीशले भने, “राम्रो, म तपाईंलाई मेरो अंगरक्षक बनाउँछु। सधैँ मेरो रक्षा गर्नुहोस्।”",
			"3": "<sup>3</sup> शाऊल र एउटी स्त्री एन्दोरमा\nशमूएल मरे। शमूएलको मृत्युमा सम्पूर्ण इस्राएलीहरूले दुःख प्रकट गरे। तिनीहरूले शमूएललाई उनकै शहर रामामा गाडे। पहिला शाऊलले भुतप्रेत खेलाउनेहरू र ज्योतिषीहरूलाई इस्राएल छोड्न वाध्य गरेका थिए।",
			"4": "<sup>4</sup> पलिश्तीहरू युद्धको निम्ति तयार थिए। तिनीहरू शूनेममा आएर त्यहीं छाउनी बनाए। शाऊलले पनि सबै इस्राएलीहरूलाई भेला गराएर गिल्बोमा छाउनी हाल्न लगाए।",
			"5": "<sup>5</sup> शाऊल पलिश्ती सेनाहरू देखेर डराए। तिनको मुटु नै काम्यो।",
			"6": "<sup>6</sup> शाऊलले परमप्रभुमा प्रार्थना गरे तर परमप्रभुले जवाफ दिनुभएन। परमप्रभु परमेश्वरले दर्शनमा पनि केही उत्तर दिनुभएन। परमेश्वरले न त उरीम न ता अगमवक्ताद्वारा नै शाऊललाई जवाफ दिनुभयो।",
			"7": "<sup>7</sup> अन्त्यमा शाऊलले आफ्ना अधिकारीहरूलाई भने, “मलाई एउटी तंत्र-मंत्र गर्ने महिला ल्याइदेऊ, म त्यसलाई सोध्छु यो युद्धको फल के हुन्छ।”\nतिनका अधिकारीहरूले जवाफ दिए, “एन्दोरमा त्यस्ती एउटी स्त्री छे।”",
			"8": "<sup>8</sup> शाऊलले भेष बद्लाउनको निम्ति भिन्नै लुगा लगाए यसकारण उसलाई नचिनोस्। त्यो रात शाऊल दुइजना मानिस लिएर त्यस स्त्रीलाई भेट्न गए। शाऊलले त्यस स्त्रीलाई भने, “म तिमीलाई भन्नेछु मेरो भविष्य बताउने आत्मा बोलाऊ। म जसको नाउँ दिन्छु त्यसैको आत्मा जगाउनु।”",
			"9": "<sup>9</sup> तर त्यस स्त्रीले भनी, “तिमी जान्दछौ शाऊलले के गर्यो। सबै झाँक्री र ज्योतिषीहरूलाई उसले इस्राएलबाट धपायो। तिमीले मलाई अप्ठेरोमा पारेर ज्यान लिन चाहान्छौ।”",
			"10": "<sup>10</sup> शाऊलले परमप्रभुको नाउँमा शपथ खाएर त्यसलाई भने, “तिमीले कुनै दण्ड सामना गर्नु पर्ने छैन।”",
			"11": "<sup>11</sup> स्त्रीले सोधी, “तिमीले कसलाई बोलाउनु लाउँछौ?”\nशाऊलले भने, “शमूएलको आत्मालाई बोलाऊ।”",
			"12": "<sup>12</sup> शमूएलको आत्मा तिनको अघि देखा पर्यो। शमूएललाई देखेर त्यो स्त्रीले जोडले चिच्याई। तिनले शाऊललाई भनिन्, “मलाई धोखा दिनुभयो! तपाईं नै शाऊल हुनुहुन्छ।”",
			"13": "<sup>13</sup> राजाले स्त्रीलाई भने, “नडराऊ। तिमीले के देख्यौ?”\nस्त्रीले भनी, “मैले आत्मा भुइँबाट निस्किएको देखे।”",
			"14": "<sup>14</sup> शाऊलले सोधे, “को जस्तो देखिन्छ?”\nस्त्रीले जवाफ दिइ, “खास किसिमको लबेदा ओढेको एउटा वृद्ध मानिस जस्तो देखिन्छ।”\nत्यसपछि शाऊलले थाहा पाए शमूएल नै हो भनेर। शाऊलले भूइँतिर शिर झुकाए उसलाई अभिवादन गरे",
			"15": "<sup>15</sup> शमूएलले शाऊललाई भने, “मलाई किन दुःखः दिएको? मलाई किन बोलाएको?”\nशाऊलले भने, “म सङ्कटमा छु। पलिश्तीहरू म सँग युद्ध गर्न आउँदैछन्, अब परमेश्वर मसित हुनुहुन्न। परमेश्वरले मलाई केही दिनुहुन्न। न त उहाँले कुनै अगमवक्ता न सपनामा केही भन्नु हुन्छ। यसकारण तपाईंलाई बोलाएको छु मलाई भन्नुहोस् म के गरौं?”",
			"16": "<sup>16</sup> शमूएलले भने, “परमप्रभु तिमीसित हुनुहुन्न। उहाँ तिम्रो छिमेकी दाऊदसँग हुनुहुन्छ। यसकारण मलाई किन झन्झट दिएको?",
			"17": "<sup>17</sup> परमप्रभुले के गर्नु हुनेछ त्यो भन्न उहाँले मलाई एकपटक आज्ञा गर्नुभयो। अहिले परमप्रभुले त्यही गर्दै हुनुहुन्छ। परमप्रभुले तिम्रो हातबाट राजधानी खोस्दै हुनुहुन्छ। अनि उहाँले तिम्रो छिमेकीको हातमा सुम्पिदिनुहुनेछ। त्यो छिमेकी चाहिं दाऊद हो।",
			"18": "<sup>18</sup> तिमीले परमप्रभुको आज्ञा पालन गरेनौ। तिमीले अमालेकीहरूलाई पूर्ण नष्ट गरेनौ तिनीहरूलाई यो कुरा देखाउन कि तिनीहरूसँग परमेश्वर कति क्रोधित हुनुहुन्थ्यो। यसकारण आज परमप्रभुले तिमीलाई यसो गर्नु हुँदैछ।",
			"19": "<sup>19</sup> परमप्रभुले पलिश्तीहरूद्वारा तिमीलाई र इस्राएलका सेनालाई आज हराउनु हुन्छ। अनि भोलि, तिमी र तिम्रा छोरा मसित हुनेछौ।”",
			"20": "<sup>20</sup> शाऊल तत्कालै लडे र भूइँमा पसारिए। शमूएलको कुरा सुनेर शाऊल साह्रै डराए। शाऊल कमजोर भइसकेका थिए कारण हिजो देखि रात-दिन उनले केही खाएकै थिएनन्।",
			"21": "<sup>21</sup> स्त्री शाऊलको छेउमा आईन। शाऊल साह्रै डराएको देखी। तिनले भनी, “हेर्नुहोस् म तपाईंकी सेविका। मैले तपाईंको आज्ञापालन गरें। मैले मेरो ज्यानलाई जोखिममा हाले र तपाईंले भने झैं गरें।",
			"22": "<sup>22</sup> अब मेरो कुरा सुन्नुहोस्। म तपाईंलाई खाने कुरा दिन्छु। तपाईंले खानैपर्छ। त्यसपछि आफ्नो कार्य गर्न शक्ति पाउनु हुनेछ।”",
			"23": "<sup>23</sup> तर शाऊलले मानेनन्। तिनले भने, “मलाई खान चाहींदैन।”\nशाऊलका अधिकारीहरूले पनि स्त्रीसँगै केही खानु भनी अनुरोध गरे। अन्त्यमा शाऊलले तिनीहरूका कुरा माने। भुइँबाट उठेर बिस्तरामा बसे।",
			"24": "<sup>24</sup> त्यस स्त्रीले आफूसँग भएको बलियो बाछो मारी। त्यस स्त्रीले अलिकति पिठो मुछी र खमीर बिनाको रोटी पकाई।",
			"25": "<sup>25</sup> र त्यस स्त्रीले भोजन शाऊल र तिनका अधिकारीहरूलाई पस्कि। तिनीहरूले भोजन खाए र त्यही रात तिनीहरू उठेर गए।"
		},
		"29": {
			"1": "<sup>1</sup> दाऊद हामीसित आउनसक्दैन\nपलिश्तीहरूले अपेकमा तिनीहरूका सबै सैनिक भेला गराए। इस्राएलीहरूले यिज्रेलको पानीको मुहान नेर छाउनी हाले।",
			"2": "<sup>2</sup> पलिश्ती अगुवाहरूले सय र हजारको संख्यामा मानिसहरूलाई अघि बढाए। दाऊद र तिनका मानिसहरू आकीशको पछिल्तिर लागे।",
			"3": "<sup>3</sup> पलिश्ती कप्तानहरूले सोधे, “यी हिब्रूहरूले यहाँ के गर्दैछन्?”\nआकीशले ती कप्तानहरूलाई भने, “यिनी दाऊद हुन्। दाऊद शाऊलका अधिकारीहरू मध्ये एकजना थिए। दाऊद मसँग धेरै अघिदेखि छन्। शाऊललाई त्यागेर आए र मसँग बस्न थाले यता मैले कुनै भूल-त्रुटी दाऊदमा पाइनँ।”",
			"4": "<sup>4</sup> तर पलिश्ती कप्तानहरू आकीशसँग रिसाए। तिनीहरूले भने, “दाऊदलाई फर्काउनुहोस्। तपाईंले दिनुभएको शहरमा ऊ फर्कनु पर्छ। रणभूमिमा ऊ हामीसित जान सक्दैन। नत्रभने हाम्रो आफ्नै छाउनी भित्र शत्रु हुनेछ। उसले आफ्नो राजालाई खुशी पार्न हाम्रै मानिस मार्नेछ।",
			"5": "<sup>5</sup> दाऊद त्यही मानिस हो जसको विषयमा इस्राएलीहरू यसो भनेर गीत गाउँदै नाच्थेः\n‘शाऊलले हजार शत्रुहरू मारे,\nतर दाऊदले दश हजार मारे।’”",
			"6": "<sup>6</sup> यसकारण आकीशले दाऊदलाई बोलाएर भने, “जीवित परमप्रभुको नाउँमा, तिमी मेरो भक्त छौ। म अति प्रसन्नहुने थिएँ यदि तिमीले मेरा सेनामा योगदान दिएको भए। शुरूदेखि नै तिमी भरपर्दो भएका छौ। तर पलिश्ती सेनाहरूले तिमीलाई सहमति गर्न चाँहदैनन्।",
			"7": "<sup>7</sup> शान्तिसित फर्क, पलिश्ती कप्तानहरूका विरूद्धमा कुनै गलत काम नगर।”",
			"8": "<sup>8</sup> दाऊदले सोधे, “मेरो गल्ती के हो? जबदेखि म तपाईंकहाँ आएँ मेरो केही दोष पाउनु हुन्छ? मेरा मालिक मेरा परमप्रभु राजाको शत्रुहरूसित युद्धगर्न मलाई किन बाधा दिनुहुन्छ?”",
			"9": "<sup>9</sup> आकीशले जवाफमा भने, “मलाई थाहा छ तिमी असल मानिस हौ। तिमी परमेश्वरका स्वर्गदूत हौ। तर पलिश्ती सेनापतिहरूले भन्दैछन्, ‘दाऊद हामीसित युद्ध भूमिमा जान सक्दैन।’",
			"10": "<sup>10</sup> भोलि बिहानै तिमी र तिम्रा मानिसहरूसित मैले दिएको शहरमा फर्केर जाऊ। यसकरण घाम झुल्कनु अघि नै फर्केर जाउ।”",
			"11": "<sup>11</sup> यसकारण दाऊद र तिनका मानिसहरू बिहानै उठेर पलिश्ती देशतिर फर्के। बीचमा यता पलिश्तीहरू चाँहि यिज्रेल तिर बढे।"
		},
		"30": {
			"1": "<sup>1</sup> अमालेकीहरूले सिकलगमाथि आक्रमण गरे\nतेस्रो दिन दाऊद र तिनका मानिसहरू सिकलग आए अनि यो भष्म भएको देखे। जब अमालेकीहरूले नेगेभ क्षेत्रमा आक्रमण गरे? तिनीहरूले सिकलगमा आक्रमण गरे अनि शहरमा आगो लगाइदिए।",
			"2": "<sup>2</sup> तिनीहरूले सिकलगका स्त्रीहरूलाई बन्दी बनाए। बूढा अथवा जवान सबै मानिसहरूलाई समाते। तिनीहरूले कसैलाई पनि मारेनन्। बन्दी मात्र बनाए।",
			"3": "<sup>3</sup> जब दाऊद र तिनका मानिसहरू सिकलग आए शहर जलिरहेको पाए। तिनीहरूका पत्नीहरू छोरा-छोरीहरू सबै अमालेकीहरूले लगेकाले कोही थिएनन्।",
			"4": "<sup>4</sup> त्यसैले दाऊद र अरू मानिसहरू धेरै रोए।",
			"5": "<sup>5</sup> अमालेकीहरूले दाऊदका दुवै पत्नीहरू यिज्रेली अहीनोअम र कर्मेली नाबालकी विधवा अबीगेललाई पनि हरेर लगेछन्।",
			"6": "<sup>6</sup> सेनाका सबै मानिसहरू आफ्ना छोरा छोरी सबै बन्दी बनाइएकोमा दुःखी भई रिसाए। मानिसहरूले दाऊदलाई ढुङ्गाले हिर्काएर मार्ने कुरा गर्दैथे। यसले दाऊदलाई साह्रै नराम्रो लाग्यो। तर दाऊदले परमप्रभु उनका परमेश्वरबाट शक्ति पाए।",
			"7": "<sup>7</sup> दाऊदले पूजाहारी एबीयातारलाई भने, “एपोद ल्याउनुहोस्।”",
			"8": "<sup>8</sup> दाऊदले परमप्रभुलाई बिन्ती गरे, “मेरो परिवारका सदस्यहरूलाई हरण गर्नेहरूलाई के म खेदौं? म तिनीहरूलाई पक्रन सक्छु?”\nपरमप्रभुले भन्नु भयो, “तिनीहरूलाई पछ्याऊ, तिनीहरूलाई पक्रन सक्छौ र तिम्रो परिवारका सदस्यहरूलाई बँचाउन सक्छौ।”",
			"9": "<sup>9</sup> दाऊद र तिनका मानिसहरूले मिश्री दासलाई पाउँछन्\nदाऊद आफ्ना साथमा 600 मानिसहरू लिएर बसोर खोल्सामा गए। त्यो ठाउँमा झण्डै 200 मानिस त्यहीं बसे। त्यहाँ बस्नुको कारण तिनीहरू धेरै कमजोर थिए र थाकेका पनि थिए, यसकारण दाऊद 400 मानिसहरू लिएर अमालेकीहरूलाई लगातार पछ्याउन थाले।",
			"11": "<sup>11</sup> दाऊदका मानिसहरूले खेतमा एउटा मिश्रीलाई भेटे। त्यस मिश्रीलाई तिनीहरूले दाऊदका सामु हाजिर गराए। तिनीहरूले त्यस मिश्रीलाई पानी र भोजन दिए।",
			"12": "<sup>12</sup> तिनीहरूले एउटा टुक्रा नेभाराको रोटी र दुइ झुप्पा किसमिस पनि दिए। खाना खाएपछि उनी फुर्तीला भए। तीन दिन तीन रातदेखि न त खाना खाए, न पानी पिए।",
			"13": "<sup>13</sup> दाऊदले त्यस मिश्रीलाई सोधे, “तिम्रो मालिक को हो? तिमी कहाँबाट आयौ?”\nत्यस मिश्रीले जवाफ दिए, “म एकजना मिश्री हुँ। म एकजना अमालेकीको दास हुँ। तीन दिनअघि म बिरामी भएँ र मालिकले मलाई त्यागिदिए।",
			"14": "<sup>14</sup> हामीले नेगेव इलाकामा आक्रमण गर्यौं जहाँ करेतीहरू बस्थे। हामीले यहूदा र नेगेवमा आक्रमण गर्यौ जहाँ कालेबी जाति बस्थे। हामीले सिकलगलाई जलायौ।”",
			"15": "<sup>15</sup> दाऊदले भने, “के तिमी मलाई तिनीहरूकहाँ पुर्याइदिन सक्छौ जसले हाम्रा परिवारका सदस्यहरूलाई समातेका छन्?”\nत्यस मिश्रीले भने, “परमेश्वरको अघि यदि तपाईंले मलाई मार्दिन अथवा मेरो मालिकको घरमा मलाई फिर्ता दिने वचन दिनुभयो भने म तपाईंलाई सहयोग गर्छु।”",
			"16": "<sup>16</sup> दाऊदले अमालेकीहरूलाई हराउँछन्\nत्यस मिश्रीले दाऊदलाई अमालेकीहरू भएको ठाउँमा पुर्यायो। त्यसबेला अमालेकीहरू खाँदै र पिउँदै भुइँमा पल्टिरहेका थिए। तिनीहरूले पलिश्ती र यहूदाका क्षेत्रबाट जे-जे लूटेका थिए त्यहीसँग रमाइलो मनाइरहेका थिए।",
			"17": "<sup>17</sup> दाऊदले तिनीहरू माथि आक्रमण गरे र मारे। तिनीहरू घाम झुल्के देखि अर्को दिनको साँझसम्म लडे। अमालेकीहरू कोही भाग्नसकेनन्। खालि 400 सेनाहरू तिनीहरूका ऊँठमा चढेर गए।",
			"18": "<sup>18</sup> अमालेकीहरूले लुटेका जति सबै उनका दुइटी पत्नीहरू सहित दाऊदले उद्वार गरे।",
			"19": "<sup>19</sup> कोही पनि हराएनन्। बूढा र जवान सबै पुनःप्राप्त गरे। सबैका छोरा छोरी पनि पाइए। अमालेकीहरूले लुटेका प्रत्येक चीज दाऊदले पुनः प्राप्त गरे।",
			"20": "<sup>20</sup> सबै भेंडा, बाख्रा, गाई, गोरू दाऊदले फिर्ता लिए। दाऊदका मानिसहरूले ती पशुहरूलाई अघि लगाए। तिनीहरूले भने, “यी सबै दाऊदका उपहार हुन्।”",
			"21": "<sup>21</sup> भागबण्डा सबैमा बराबर हुने\nदाऊद बसोर खोल्सामा ती 200 मानिसहरू बसेको ठाउँमा आए। यी मानिसहरू साह्रै कमजोर र थाकेका हुनाले दाऊदसँग जान सकेका थिएनन्। यी मानिसहरू दाऊद र सैनिकहरूलाई भेट्न बाहिर आए। तिनीहरूले दाऊद र सैनिकहरूलाई अभिवादन गरे जब तिनीहरू नजिक आए।",
			"22": "<sup>22</sup> दाऊदसँग केही दुष्ट मानिसहरू पनि गएका थिए अनि जसले समस्या उब्जाइ रहन्थे। ती दुष्टहरूले भने, “यी 200 मानिसहरू हामीसित गएका होइनन्। यिनीहरूले आफ्ना पत्नी र छोरा-छोरीहरू सिवाय केही पनि पाउने छैनन्।”",
			"23": "<sup>23</sup> दाऊदले जवाफ दिए, “होइन, मेरा भाइ हो। त्यसो गर्नु हुँदैन। बिचार गर त परमप्रभुले हामीलाई के दिनुभयो! परमप्रभुले हामीलाई शत्रु हराउन सहयोग गर्नुभयो जसले हामी माथि आक्रमण गर्यो।",
			"24": "<sup>24</sup> तिमीले जे भन्छौ त्यसमा कसैले ध्यान दिनेछैन। जो लडाईंको मैदानमा जान्छन् र जो यहाँ बसेर माल-समानको रक्षा गर्नेले तिनीहरूको लागि बराबर भागबण्डा हुनेछ। सबैले बराबर भाग बण्डा पाउने छन्।”",
			"25": "<sup>25</sup> दाऊदले यो आदेश दिए र इस्राएलमा शासन गरे अनि आज पनि इस्राएलमा यही नियम लागू छ।",
			"26": "<sup>26</sup> दाऊद सिकलग आइपुगे। तिनले अमालेकीहरूबाट पाएका सामानहरू यहूदाका आफ्ना मित्रहरू र नेताहरूलाई पठाइदिए। दाऊदले भने, “परमप्रभुका शत्रुबाट खोसेका चीजहरू तपाईंलाई उपहार स्वरूप छ।”",
			"27": "<sup>27</sup> दाऊदले त्यसरी नै ती सामानहरू बेतेलका नेताहरूलाई, नेगेबका रमोत, यतीरमा,",
			"28": "<sup>28</sup> अरोएर, सिपमोत, एस्तमो,",
			"29": "<sup>29</sup> राकालमा भएकाहरूलाई, यरहमैलीहरू र केनीहरूका शहरहरूमा,",
			"30": "<sup>30</sup> होर्मामा, बोराशानमा, अत्ताकमा,",
			"31": "<sup>31</sup> अनि हेब्रोनमा भएकाहरूलाई पठाइदिए। दाऊदले तिनका मानिसहरू जहाँ-जहाँ गएका थिए ती सबै ठाउँका नेताहरूलाई पठाइदिए।"
		},
		"31": {
			"1": "<sup>1</sup> शाऊलको मृत्यु\nपलिश्तीहरू इस्राएलको विरूद्धमा लडे अनि इस्राएलीहरू हारेर भागे। गिल्बो पर्वतमा धेरै इस्राएलीहरू मारिए।",
			"2": "<sup>2</sup> पलिश्तीहरू शाऊल र तिनका छोराहरू विरूद्ध तीव्रता पूर्वक लडे। पलिश्तीहरूले शाऊलका छोरा जोनाथन, अबीनादाब र मल्कीशूअलाई मारे।",
			"3": "<sup>3</sup> त्यो युद्ध शाऊलको विरूद्ध चारैतिर मच्चियो। धनु-धारीको काँडले शाऊललाई हिर्कायो अनि तिनी नराम्रो घाइते भए।",
			"4": "<sup>4</sup> शाऊलले तिनको हातहतियार बोक्ने नोकरलाई भने, “तिम्रो तरवार निकालेर मलाई मार। ती विदेशीहरूको हातबाट म घाइते हुनु अथवा तिनीहरूको हाँसोको पात्र बन्न म चाहँदिन।” तर शाऊलको नोकर डराएर त्यसो गर्न सकेन। यसर्थ शाऊलले आफ्नो तरवार निकालेर आत्माहत्या गरे।",
			"5": "<sup>5</sup> नोकरले शाऊल मरेको देखेर तरवार निकालेर आफू पनि शाऊलसँगै मर्यो।",
			"6": "<sup>6</sup> यसरी शाऊल, तिनका तीनजना छोरा र हतियार बोक्ने केटो त्यस दिन सँगै मरे।",
			"7": "<sup>7</sup> पलिश्तीहरूले शाऊलको मृत्युमा आनन्द मनाए\nबेंसीको अर्कोपट्टि भएका इस्राएलीहरूले इस्राएलका सैनिकहरू भाग्दै गरेको देखे। तिनीहरूले शाऊल र तिनका छोराहरू मरेको देखे। यो देखेर ती इस्राएलीहरू आफ्नो शहर छाडेर भागे। त्यसपछि पलिश्तीहरू ती शहरहरूमा आएर बस्न लागे।",
			"8": "<sup>8</sup> अर्को दिन, पलिश्तीहरू मरेका लाशहरूबाट चीज बिज लुट्न गए। तिनीहरूले गिल्बो पर्वतमा शाऊल र तिनका छोराहरू मरेका पाए।",
			"9": "<sup>9</sup> पलिश्तीहरूले शाऊलको टाउको काटे र तिनको हतियार लगे। तिनीहरूले पलिश्ती र तिनीहरूले पूजा गर्ने देवताको मन्दिर सम्म उक्त समाचार पुर्याए।",
			"10": "<sup>10</sup> तिनीहरूले शाऊलको हात-हतियारहरू अश्तारोतको मन्दिरमा राखे। शाऊलको लास चाँहि बेतशानको भित्तामा झुण्डाए।",
			"11": "<sup>11</sup> याबेश गिलादका मानिसहरूले पलिश्तीद्वारा शाऊल माथि घटेका घटनाहरू सुने।",
			"12": "<sup>12</sup> यसैले याबेशका सबै सैनिकहरू बेत-शान गए। रातभरी तिनीहरू हिंडे। त्यसपछि तिनीहरूले बेतशानको भित्ताबाट शाऊलको लाश झिके। तिनीहरूले शाऊलका छोराहरूका लाश पनि झिके। त्यसपछि ती लाशहरू याबेश पुर्याए। याबेशका मानिसहरूले शाऊल र तिनका छोराहरूका लाशलाई जलाइ दिए।",
			"13": "<sup>13</sup> त्यसपछि तिनीहरूले शाऊल र तिनका छोराहरूका हड्डीहरू निकाले अनि याबेशमा एउटा ठूलो रूखमुनि गाडे। यसरी याबेशका मानिसहरूले तिनीहरूको दुःख व्यक्त गरे। तिनीहरूले सात दिनसम्म केही पनि खाएनन्।"
		}
	}
};
module.exports = book;