var book = {
	"name": "यर्मिया",
	"numChapters": 52,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> यी सन्देशहरू हिल्कियाहको छोरो यर्मियाका हुन्। यर्मिया एक पूजाहारी परिवारका थिए जो अनातोत शहरमा बस्दथे। त्यो शहर बिन्यामीन वंशको अधीनमा छ।",
			"2": "<sup>2</sup> योशियाह यहूदाका राजा भएको समय देखि परमप्रभु यर्मियासित बात गर्न शुरू गर्नु भयो। योशियाहले सिंहासन आरोहण गरेको तेह्रौं बर्षमा यस्तो शुरू भयो।",
			"3": "<sup>3</sup> योशियाहको छोरा यहोयाकीम यहूदाका राजा हुदाँ पनि परमप्रभुको वचन यर्मियालाई आयो। यहूदाका राजा सिद्कियाहको राज्यकालको एघारौं बर्षको पाँचौं महीना सम्म यर्मियाकहाँ परमप्रभुको वचन आइरह्यो। यहोयाकीम योशियाहको छोरो थिए। सिद्कियाह पनि योशियाको छोरो थिए। त्यस समयमा यरूशलेममा बस्ने मानिसहरूलाई निर्वासन गराईयो।",
			"4": "<sup>4</sup> परमेश्वरले यर्मियालाई बोलाउनु हुन्छ\nपरमप्रभुको यो सन्देश मकहाँ आयो:",
			"5": "<sup>5</sup> “मैले तिमीलाई तिम्रो आमाको गर्भमा,\nगर्भधारण गर्नुभन्दा अघिबाटै चिन्थें।\nतिमी जन्मनु अघिनै, म तिमीलाई जान्दथें।\nमैले तिमीलाई जाति-जातिहरूमा\nविशेष काम गराउनु अगमवक्ताको लागि रोजें।”",
			"6": "<sup>6</sup> तब मैले भने, “तर हे सेनाहरूका परमप्रभु! कसरी बोल्नु म जान्दिन्। म एउटा बालक बाहेक केही होइन।”",
			"7": "<sup>7</sup> तर परमप्रभुले मलाई भन्नुभयो,\n“त्यसो नभन, ‘म खाली एउटा बालक हुँ।’\nतिमी चारैतिर जानै पर्छ, जहाँ म तिमीलाई पठाउँछु।\nतिमीले सबै कुरा भन्नै पर्छ जो म तिमीलाई भन्नेछु।",
			"8": "<sup>8</sup> कसैसित नडराऊ!\nम तिमीसित छु, अनि म तिमीलाई उद्धार गर्नेछु।”\nयो सन्देश परमप्रभुबाट आएको हो।",
			"9": "<sup>9</sup> &nbsp;&nbsp;&nbsp;&nbsp;तब परमप्रभुले आफ्ना हातहरू तन्काएर मेरो मुखमा छुनु भयो। परमप्रभुले मलाई भन्नुभयो,\n“यर्मिया, म मेरा वचनहरू तिम्रो मुखमा राख्दैछु।",
			"10": "<sup>10</sup> आज म तिमीलाई राष्ट्रहरू र राज्य-राज्यहरूमाथिको कार्यभार सुम्पिँदैछु।\nतिनीहरूलाई उखेल्न, भत्काउन,\nनाश पार्ने र भताभुङ्ग पार्ने, बनाउन\nर स्थापित गर्ने तिम्रो जिम्मामा छ।”",
			"11": "<sup>11</sup> दुइ दृश्यहरू\nपरमप्रभुको सन्देश मकहाँ आयो। यो सन्देश परमप्रभुबाट नै आएको थियो: “यर्मिया, तिमीले के देख्छौं?”\nमैले परमप्रभुलाई भनें, “म हाडे बदामको काठबाट बनिएको एउटा लट्ठी देख्छु।”",
			"12": "<sup>12</sup> परमप्रभुले मलाई भन्नुभयो, “तिमीले ठीकै देख्यौ। मेरो सन्देशलाई म नियालि रहेछु अनि म यो गर्नेछु।”",
			"13": "<sup>13</sup> परमप्रभुको सन्देश मकहाँ फेरि आयो। परमप्रभुको सन्देश यस्तो थियो “यर्मिया, तिमीले के देख्यौ?”\nमैले परमप्रभुलाई भनें, “मैले उम्लिरहेको एउटा भाँडामा पानी देखें। त्यो भाँडा उत्तर पट्टि ढल्किरहेको छ।”",
			"14": "<sup>14</sup> परमप्रभुले मलाई भन्नुभयो, “केही भयानक उत्तरदेखि आउने छ।\nत्यो यस देशमा बस्ने सम्पूर्ण मानिसहरूमाथि घट्नेछ।",
			"15": "<sup>15</sup> म चाँडै उत्तर राज्यहरूका सम्पूर्ण मानिसहरूलाई बोलाउनेछु।”\nपरमप्रभुले ती कुराहरू भन्नुभयो।\n“ती राज्यहरूका राजाहरू आउनेछन्\nअनि यरूशलेमको प्रवेशद्वारको छेउमा आफ्ना सिंहासनहरू बसाउनेछन्।\nतिनीहरूले यरूशलेम शहरका पर्खालहरूमाथि आक्रमण गर्नेछन्।\nतिनीहरूले यहूदाका सबै शहरहरूमाथि आक्रमण गर्ने छन्।",
			"16": "<sup>16</sup> अनि म मेरा मानिसहरूका विरूद्ध न्यायको घोषणा गर्नेछु।\nम यसो गर्ने नै छु किनभने तिनीहरू दुष्ट मानिसहरू हुन्।\nतिनीहरू मेरो विरोधी भएका छन्।\nमेरा मानिसहरूले मलाई छाडे।\nतिनीहरूले अन्य देवताहरूलाई बलि चढाए।\nतिनीहरूले आफ्नै हातले बनाएका मूर्तिहरूलाई दण्डवत् गरे।",
			"17": "<sup>17</sup> “यर्मिया तिमी चाहिँ तैयार होऊ,\nउठ र मानिसहरूलाई भन।\nमैले दिएको प्रत्येक आज्ञाहरू तिनीहरूलाई भन।\nमानिसहरूसित नडाराऊ, नत्र म तिमीलाई भयभीत पार्नेछु।",
			"18": "<sup>18</sup> आज म तिमीलाई फलामको खम्बा झैं\nअनि काँसाको पर्खाल झैं बलियो\nर दृढ शहर बनाउनेछु।\nयहूदाको राज्यमा राजाहरू, नेताहरू, पूजाहारीहरू\nर सर्वसाधारण मानिस प्रत्येकको सामुन्ने\nतिमी उभिन सक्नेछौ।",
			"19": "<sup>19</sup> ती सबै मानिसहरू तिम्रो विरूद्धमा लड्नेछन्\nतर तिनीहरूले तिमीलाई पराजित गराउने छैनन्।\nकिनभने म तिमीसँग छु,\nअनि म तिमीलाई बचाउने छु।”\nयो सन्देश परमेश्वरबाट आएको हो।"
		},
		"2": {
			"1": "<sup>1</sup> यहूदा विश्वासी थिएन\nपरमेश्वरको यो सन्देश मकहाँ आयो:",
			"2": "<sup>2</sup> “जाऊ अनि यरूशलेमका मानिसहरूलाई यो सन्देश भन:\n“‘जब तिमीहरू युवा अवस्थामा थियौ,\nत्यस बेला तिमीहरूको म प्रति\nविश्वास अनि एउटी बेहुलीको झैं,\nम प्रतिको माया मलाई सम्झना छ।\nतिमीहरूले मलाई मरुभूमि भरि,\nर बीऊ र्छनलाई नजोतिएको जमीन भरि पछ्यायौ।",
			"3": "<sup>3</sup> इस्राएलका मानिसहरू परमेश्वरका निम्ति पवित्र उपहार थिए।\nतिनीहरू परमेश्वरले बटुल्नु भएको प्रथम फल थिए।\nअनि ज-जसले तिनीहरूलाई नोक्साम पुर्याए\nउनीहरूलाई दोषी साबित गरियो,\nअनि तिनीहरूमाथि कष्ट आइपर्यो।’”\nयो सन्देश परमेश्वरबाट आएको थियो।",
			"4": "<sup>4</sup> हे याकूबका घरानाहरू हो, परमप्रभुको सन्देश सुन।\nर इस्राएलका सबै परिवार समूहहरू हो, परमप्रभुको सन्देश सुन।",
			"5": "<sup>5</sup> परमप्रभुले के भन्नुहुन्छ त्यो यो हो:\n“तिम्रा पिता-पुर्खाहरूले ममाथि के दोष पाए\nताकि तिनीहरूले मलाई छाडिदिए?\nतिम्रा पिता-पुर्खाहरूले मूल्यहीन मूर्तिहरू पूजे\nअनि तिनीहरू स्वयं नै मूल्यहीन बनिए।",
			"6": "<sup>6</sup> तिम्रा पिता-पुर्खाहरूले भनेनन्,\n‘परमप्रभुले हामीलाई मिश्र बाहिर ल्याउनु भयो।\nपरमप्रभुले हामीलाई अँध्यारो,\nर जङ्गलको बाटो देखि डोर्याउँदै ल्याउनु भयो।\nपरमप्रभुले हामीलाई सुख्खा र उजाड भूमिमा डोर्याउनु भयो।\nपरमप्रभुले हामीलाई अ न्घकार,\nर भयानक जग्गामा जहाँ कुनै मानिस बस्दैन,\nर जुन ठाउँमा मानिसहरू यात्रा गर्दैनन्।\nत्यहाँ डोर्याउनु भयो।\nतिनीहरूले सोधेनन्, परमप्रभु कहाँ हुनुहुन्छ?’”",
			"7": "<sup>7</sup> परमेश्वर भन्नुहुन्छ, “मैले तिमीहरूलाई राम्रो ठाउँमा ल्याएँ,\nत्यस्तो ठाउँ जहाँ धेरै असल कुराहरूले भरिपूर्ण छ।\nमैले त्यसो गरेको कारण तिमीहरूले त्यहाँ उब्जने फल र अन्नहरू खान सक्नेछौ।\nतर तिमीहरूले मेरो धर्ती खाली ‘अशुद्ध गर्यौ।’\nमैले तिमीहरूलाई त्यो जमीन दिएँ,\nतर तिमीहरूले त्यसलाई नराम्रो जग्गा बनायौ।",
			"8": "<sup>8</sup> “पूजाहारीहरूले सोधेनन्,\n‘परमप्रभु कहाँ हुनुहुन्छ?’\nमानिसहरू जसले व्यवस्था जान्दछन्, मलाई जान्न चाहेनन्।\nइस्राएलका मानिसहरूका शासकहरू मेरो विरूद्ध खडा भए।\nअगमवक्ताहरूले झूटा देवता बालको नाउँमा अगमवाणी गरे।\nतिनीहरूले महत्वहीन मूर्तिहरूलाई दण्डवत गरे।”",
			"9": "<sup>9</sup> परमप्रभु भन्नुहुन्छ, “यसकारण म तिमीहरू\nर तिमीहरूका छोरा-नातिहरू विरूद्ध फेरि दोषारोपण गर्नेछु।",
			"10": "<sup>10</sup> समुद्र तरेर कित्तीमका टापुहरूमा जाऊ।\nकसैलाई केदारको भूमिमा पठाऊ।\nएकदमै होशियारी साथ हेर,\nकसैले त्यस्तो प्रकारको काम कहिले गरेको थियो कि?",
			"11": "<sup>11</sup> के कुनै जातिले नयाँ देवताहरू पूज्न सकूँ भनेर?\nआफ्ना पूराना देवताहरू कहिल्यै पूज्न छाडे? अँह।\nअनि तिनीहरूका देवताहरू वास्तविक देवताहरू हुँदैहोइनन्।\nतर मेरो मानिसहरू तिनीहरूका महिमामय परमेश्वरलाई पूज्न छाडेर,\nमहत्वहीन मूर्तिहरूलाई पूज्न थाले।",
			"12": "<sup>12</sup> “यस्ता कुराहरू घट्दा आकाशहरूलाई आघात होस्।\nभीषण भयले थर्थरियोस्।”\nयो सन्देश परमप्रभुबाट आएको थियो।",
			"13": "<sup>13</sup> “मेरा मानिसहरूले दुइवटा कु-कर्महरू गरे।\nतिनीहरू म देखि टाडिए म जीवित पानीको स्रोत हुँ,\nअनि तिनीहरू आफैले आफ्ना पानीको कुवाहरू खन्छन्।\nतिनीहरू अन्य देवताहरू प्रति फर्किए।\nतर तिनीहरूका कुवाहरू फुटे अनि पानी थाम्न सक्दैन्।",
			"14": "<sup>14</sup> “के इस्राएलका मानिसहरू कमारो भए?\nके तिनीहरू त्यस्ता मानिस जस्तो भए जो दास भएर जन्मेको थियो?\nकिन मानिसहरूले इस्राएलका मानिसहरूबाट धन-सम्पत्ति लान्छन्?",
			"15": "<sup>15</sup> इस्राएलका शत्रुहरू इस्राएलमाथि जवान सिंहहरू झैं गर्जन्छन्।\nतिनीहरूले इस्राएल भूमिलाई उजाड पारिदिए।\nतिनीहरूले इस्राएलका शहरहरू जलाइदिए,\nयसर्थ त्यहाँ कोही बस्दैन।",
			"16": "<sup>16</sup> नोप र तहपन्नेसबाट आएका मानिसहरूले\nतिम्रो शिरको शीर्षभाग चकनाचुर पारिदिए।",
			"17": "<sup>17</sup> यो समस्या तिम्रो आफ्नै दोषले हो।\nपरमप्रभु परमेश्वरले तिमीहरूलाई ठीक बाटोमा डोर्याउँदै हुनुहुन्थ्यो,\nतर तिमीहरूले उहाँबाट अर्को पट्टि फर्कियौ।",
			"18": "<sup>18</sup> यहूदाका मानिसहरू यस विषयमा सोच\nअब नील नदीबाट पानी पिउन किन मिश्र जान्छौ?\nपरात नदीको पानी पिउन किन अश्शूर जान्छौ?",
			"19": "<sup>19</sup> तिमीहरूले नराम्रा कामहरू गर्यौ\nअनि ती नराम्रा कुराहरूले तिमीहरूलाई केवल दण्ड मात्र ल्याउनेछ।\nसङ्कटहरू तिमीकहाँ आउनेछ।\nअनि त्यस सङ्कटले तिमीहरूलाई शिक्षा दिनेछ।\nयस विषयमा विचार गर तब तिमीहरूले बुझ्नेछौ,\nपरमेश्वरबाट विमुख हुँदा कति नराम्रो हुँदोरहेछ।\nमलाई सम्मान नगर्नु र मबाट नडराऊनु नै गल्ती हो।”\nयो सन्देश मेरो मालिक सेनाहरूका परमेश्वरबाट आएको थियो।",
			"20": "<sup>20</sup> “हे यहूदाका मानिसहरू हो!\nधेरै अघि तिमीहरूले जुवा हटायौ।\nमैले तिमीहरूलाई जुन डोरीले नियन्त्रण गेर्थें,\nत्यो तिमीहरूले छिनायौ।\nतिमीहरूले मलाई भन्यौ, ‘म तपाईंको सेवा गर्दिनँ।’\nतिमी प्रत्येक अग्लो पर्वतमाथि,\nर प्रत्येक हरियो रूखमनि सुत्ने वेश्या झैं हौ।",
			"21": "<sup>21</sup> यहूदा, मैले तिमीलाई एक खास दाखको झ्याँगलाई जस्तै रोपेको थियें।\nतिमीहरू सबै नै असल बीऊ जस्तै थियौ।\nकसरी तिमी भिन्दै प्रकारको दाखको झ्याङ्ग भयौ?\nजसले नराम्रो फल फलाउँदछ?",
			"22": "<sup>22</sup> तिमीले आफ्नो शरीर धेरै साबुनहरू दलेर\nखारयुक्त पानीले नुहाए पनि,\nअझ म तिम्रा अपराधहरूको दाग देख्नु सक्छ।”\nयो सन्देश मेरा मालिक परमप्रभुबाट आँउछ।",
			"23": "<sup>23</sup> “हे यहूदाका मानिसहरू हो!\nतिमीहरूले कसरी मलाई भन्न सक्छौ,\n‘म मैला छैन, मैले बाल मूर्तिहरू पूजेको छैन।’\nतिमीहरूको कर्महरूलाई बेंसीमा हेर।\nतिमीहरूले जे गरेका छौं त्यो स्वीकार गर।\nतिमीहरू यता र उता दगुर्ने फुर्तिला ऊँटहरू जस्ता छौ।",
			"24": "<sup>24</sup> तिमीहरू निर्जन स्थानमा बस्ने जङ्गली गधा जस्तौ छौं।\nकाम-वासनाको समयमा त्यसले हावाबाट गन्ध लिन्छ।\nत्यो त्यति कामुक रहन्छे कि उसलाई फर्काउनु कसैले सक्तैन।\nकाम-वासनाले उन्मत्त भएको समयमा उसलाई खोज्ने\nप्रत्येक बन-गधैनीले सजिलै भेट्न सक्छ।",
			"25": "<sup>25</sup> खुट्टाहरू नाङ्गो नहुन्जेल,\nअनि घाँटी नसुकुन्जेल दौडिन छोड।\nतर तिमीहरू भन्छौ,\n‘आशा छैन कारण मैले अन्य देवताहरूलाई प्रेम गरेको छु,\nअनि म तिनीहरूकै अनुशरण गर्नेछु।’",
			"26": "<sup>26</sup> “जसरी चोर पक्रा पर्दा लज्जित हुन्छ,\nत्यसरी नै इस्राएलका घराना लज्जित भए।\nतिनीहरूका राजाहरू र शासकहरू, पूजाहारीहरू\nर अगमवक्ताहरू लज्जित हुनेछन्।",
			"27": "<sup>27</sup> ती मानिसहरूले काठका टुक्राहरूसँग बात मार्छन,\nअनि भन्छन्, ‘तिमी मेरा पिता हौ।’\nती मानिसहरू ढुङ्गासँग कुरा गर्छन्\nअनि भन्छन्, ‘तिमीले मलाई जन्म दियौ।’\nतिनीहरू आफ्नो ढाड मपट्टि फर्काउँछन्,\nतर अनुहार चाँहि होइन।\nतर जब तिनीहरू अप्ठेरामा पर्छन,\nतिनीहरू मलाई भन्छन्,\n‘आउनुहोस् र हामीलाई बँचाउनुहोस्।’",
			"28": "<sup>28</sup> ती मूर्तिहरू आऊन् र तिमीहरूलाई बँचाउन।\nती मूर्तिहरू जो तिमीहरू आफैंले बनाएका थियौ ती कहाँ छन्?\nकतै आवश्यक परेको समयमा तिमीहरूलाई बचाँउछन् कि?\nयहूदा तिमीसित जति शहरहरू छन् त्यति नै मूर्तिहरू पनि छन्।",
			"29": "<sup>29</sup> “तिमीहरू सबै मसित किन वहश गर्छौ?\nतिमीहरू सबैले मसित विद्रोह गर्छौ?”\nपरमेश्वरबाट आएको वचन यही हो।",
			"30": "<sup>30</sup> “मैले तिमीहरूका नानीहरूलाई दण्ड दिएँ।\nतर त्यो वेकार भयो,\nकारण तिनीहरूले त्यो ग्रहण गरेनन्।\nजो अगमवक्तहरू तिमीहरूकहाँ आएका थिए,\nतिनीहरूलाई तिमीहरूले तरवारले काट्यौ।\nसंहार गर्ने सिंहले झैं तिमीहरूले अगमवक्ताहरूलाई मार्यौ।”",
			"31": "<sup>31</sup> यस पुस्ताका मानिसहरू,\nपरमप्रभुको सन्देश तर्फ ध्यान देऊ।\n“के म इस्राएली मानिसहरूको लागि मरुभूमि जस्तै भएको थिएँ?\nके म तिनीहरूका लागि अन्धकार र डरलाग्दो भूमि भएको थिएँ?\nमेरा मानिसहरू भन्छन्, ‘हामी आफ्नो बाटोमा जान स्वतन्त्र छौं।\nहामी तपाईंकहाँ फर्की आउने छैनौं प्रभु।’\nकिन तिनीहरूले त्यस्ता कुराहरू भन्छन्?",
			"32": "<sup>32</sup> तरूणीले आफ्ना गर-गहनाहरू भुल्दैनन्।\nएउटा दुलहीले आफ्ना वस्त्र भुल्दैन।\nतर मेरो मानिसहरूले मलाई,\nधेरै पटक भुलेका छन्।",
			"33": "<sup>33</sup> “हे यहूदा! झूटा देवताहरूको माया कसरी खोज्नु त्यो तिमी साँच्चै नै जान्दछौ।\nवास्तवमा खराब स्त्रीहरूले तिम्रा तरिकाहरू पनि सिक्छन्।",
			"34": "<sup>34</sup> तिमीले ती गरीब\nर निर्दोष मानिसहरूलाई\nतिम्रो घर फोर्दै गरेका भेट्न सकेनौ,\nतापनि तिम्रो हातमा तिनीहरूको रगत छ।",
			"35": "<sup>35</sup> तर तिमी अझ भन्छौ, ‘म निर्दोष छु।\nपरमेश्वर मसँग क्रोधित हुनुहुन्न।’\nयसकारण म तिम्रो न्याय गर्नेछु\nकारण तिमी भन्छौ कि ‘मैले पाप गरेको छैन।’",
			"36": "<sup>36</sup> तिमी लक्ष्य सजिलै बद्लिन्छौ!\nतिमी अश्शूर पट्टि फर्कियौ अनि तिनीहरूले तिमीलाई लज्जित बनाए।\nयसकारण, तिमी मिश्रपट्टि फर्कियौ,\nअनि तिनीहरूले पनि तिमीलाई लज्जित तुल्याए।",
			"37": "<sup>37</sup> यसर्थ यो सत्य हो कि तिमीले मिश्र पनि त्याग्ने छौ।\nअनि तिमी लज्जाले आफ्ना अनुहार लुकाउनेछौ।\nतिमीले भरोसा राखेका देशहरूलाई परमेश्वरले इन्कार गर्नु भएको छ,\nयसर्थ तिम्रो जीवनमा उन्नतिको लागि\nतिनीहरूले तिमीलाई सहयोग गर्न सक्तैनन्।"
		},
		"3": {
			"1": "<sup>1</sup> “यदि एकजना मानिसले आफ्नी पत्नीसित छुटानाम गर्छ,\nर त्यस पत्नीले अर्को मानिससित विवाह गरी भने\nके त्यो पहिलो मानिस त्यही स्त्रीसित फेरि सम्बन्ध राख्न आँउछ? अँह।\nयदि त्यस स्त्रीकहाँ त्यो पहिलो पुरूष फर्की आए, जमीन नै एकदमै दूषित हुनेछ।\nतिमी धेरै प्रेमीहरूसित वेश्या झैं व्यवहार गरिरहेका छौ,\nअनि अब तिमी मकहाँ फर्की आउनु चाहन्छौ।”\nयो सन्देश परमप्रभुबाट आएको हो।",
			"2": "<sup>2</sup> “माथि पहाडको चुचुराहरू हेर,\nके त्यहाँ कुनै ठाउँ छ जहाँ तिम्रा प्रेमिकाहरूसँग तिमीले प्रेम रचेका छैनौ?\nअरबी मरुभूमिमा फिरन्ते झैं,\nआफ्नो प्रेमीहरूको प्रतीक्षा तिमी बाटोको किनारमा पर्खेका थियौ।\nतिमीले भूमि ‘दूषित’ पार्यौ।\nकिनभने तिमीले थुप्रै नराम्रा कामहरू गर्यौ।\nसाथै तिमी मप्रति अभक्त भयौ।",
			"3": "<sup>3</sup> तिमीले पाप गर्यौ, यसकारण पानी परेन।\nत्यहाँ पानी पर्ने समयमा कुनै पानी परेन,\nतर अझ तिमी लज्जित हुन अस्वीकर गर्छौ।\nतिम्रो अनुहार हेर्दा एउटा वेश्याको अनुहारमा हेरे जस्तो हुन्छ जो निर्लज हुन्छे।\nतिमी पनि आफूले जे गर्यौ त्यसको लागि निर्लज्ज छौ।",
			"4": "<sup>4</sup> तर अहिले तिमीले मलाई पुकारिरहेछौ, ‘पिता’?\nतिमीले भन्यौ, ‘तपाईं मेरो युवावस्था देखिको मित्र हुनुहुन्छ।’",
			"5": "<sup>5</sup> तिमीले यसो पनि भन्यौ, ‘परमेश्वर मसित सधैँ क्रोधित रहनु हुँदैन।\nपरमेश्वरको रीस धेरै बेर रहने छैन।’\n“यहूदा, तिमीले यी त कुराहरू भन्यौ,\nतर तिमीले सके जति दुष्ट कामहरू गर्यौ।”",
			"6": "<sup>6</sup> दुइ जना नराम्रा दिदी बहिनी, इस्राएल र यहूदा\nयोशियाह राजाले यहूदामा राज गरेको समयमा परमप्रभुले मलाई भन्नुभयो, “यर्मिया, इस्राएलले गरेको पाप तिमीले देख्यौ? तिमीले देख्यौ कि ऊ मप्रति अविश्वासी छ। तिनले प्रत्येक पहाडमाथि अनि हरियो रूख मनि व्यभिचार गरिरहेकी थिई।",
			"7": "<sup>7</sup> मैले आफैंलाई भनें, ‘उसले यी सब पाप कर्म गरिसके पछि, मकहाँ फर्किन्छ होला।’ तर ऊ मकहाँ आएन। अनि इस्राएलले गरेको उसको अविश्वासी बहिनी यहूदाले देखी।",
			"8": "<sup>8</sup> इस्राएल अविश्वासी थिए अनि तिनले चालपाई कि मैले उसलाई टाढो पठाएँ। इस्राएलले चालपाई कि उसले व्यभिचार पाप गरेकीले मैले उसित छुटानाम गरें। तर यसले उसको अविश्वासी बहिनीलाई कुनै डर भएन। यहूदा पनि बाहिर गई अनि वेश्या झैं काम गर्नु लागि।",
			"9": "<sup>9</sup> यहूदाले आफू वेश्या झैं काम गरिरहेको कुनै ध्यान नै दिएन। यसकारण उसले आफ्नो देश ‘अशुद्ध’ बनाई। उसले चट्टान र काठले बनिएका मूर्तिहरू पूजा गरेर व्यभिचारी पाप गरी।",
			"10": "<sup>10</sup> इस्राएलकी अविश्वासी बहिनी यहूदा मकहाँ मन खोलेर आएन। उसले खुल्ला हृदयले आए की झैं निहुँ गरी।” यो सन्देश परमप्रभुबाट आएको हो।",
			"11": "<sup>11</sup> परमप्रभुले मलाई भन्नुभयो, “विश्वासहीन इस्राएल अविश्वासी यहूदा जस्ता खराब छैन।",
			"12": "<sup>12</sup> यर्मिया, उत्तर दिशा तिर अघि बढ अनि यो सन्देश घोषणा गर:\n“‘हे इस्राएलका विश्वासहीन मानिसहरू हो, फर्की आऊ।’\nयो सन्देश परमप्रभुबाट आएको थियो।\n‘तिमीहरू माथि आँखा तर्न बन्द गर्छु,\nकिनकि म पूर्ण कृपालु छु।’\nयो सन्देश परमप्रभुबाट आएका हो:\n‘म अब उसो तिमीसित रीस गर्ने छैन।",
			"13": "<sup>13</sup> तिमीले आफ्नो खराब काम स्वीकार गर्नु पर्छ।\nतिमीले परमप्रभु आफ्ना परमेश्वरको विरुद्ध\nअपराध गरेका छौ।\nप्रत्येक हरियो रूखमुनि तिमीले\nपराई देवताहरू पुज्यौ।\nतिमीले मेरो कुरा सुनेका छैनौं।’”\nयो सन्देश परमप्रभुबाट आएको हो।",
			"14": "<sup>14</sup> “हे विश्वासहीन सन्तान हो, मकहाँ फर्की आऊ।” यो सन्देश परमप्रभुबाट आएको हो।“म तिमीहरूका मालिक हुँ। हरेक शहरबाट तिमीहरू मध्ये एकजना र हरेक परिवारबाट दुइजना छान्नेछु अनि तिनीहरूलाई म सियोनमा लैजानेछु।",
			"15": "<sup>15</sup> तिमीहरूका निम्ति मेरै जस्तो मन भएको शासकहरू दिनेछु। तिनीहरूले तिमीहरूलाई ज्ञान र समझशक्ति सिकाउनेछन्।",
			"16": "<sup>16</sup> त्यस समयमा देशमा तिमीहरूको संख्या बढ्नेछ।” यो सन्देश परमेश्वरबाट आएको हो।\n“त्यस समयमा, परमेश्वरसित भएको करारको सन्दूक विषयमा चर्चा हुने छैन, तिनीहरूले यस विषयमा सोच्ने छैनन्। अथवा सम्झने छैनन् अथवा उम्काउने छैनन्। अनि तिनीहरूले अर्को सन्दूक बनाउने छैनन्।",
			"17": "<sup>17</sup> त्यस समयमा तिनीहरूले यरूशलेमलाई ‘परमप्रभुको सिंहासन’ भनी स्वीकार्ने छन्। परमप्रभुको सम्मान दिनलाई सबै राजाहरू यरूशलेममा आउनेछन्। तिनीहरूले आफ्नो अट्टेरी दुष्ट हृदयलाई अब उसो मान्ने छैनन्।",
			"18": "<sup>18</sup> ती दिनहरूमा, यहूदाका परिवारहरू इस्राएलका परिवारमा सम्मिलित हुनेछ। तिनीहरू उत्तरमा भएको भूमिबाटसँगै आउनेछन्। तिनीहरू मैले तिनीहरूका पिता-पुर्खाहरूलाई दिएको भूमिमा आउनेंछन्।”",
			"19": "<sup>19</sup> यो सन्देश परमप्रभुबाट आएको हो:\n“म तिमीहरूलाई आफ्नै नानीहरूलाई झैं व्यवहार गर्नु चाहन्छु।\nम तिमीहरूलाई अन्य जातिहरू भन्दा पनि अझ राम्रो भूमि दिन चाहन्छु।\nतिमीहरूले मलाई ‘पिता’ भन्नेछौ\nअनि सधैँ मेरो अनुशरण गर्नेछौ।\nतर तिमीहरू आफ्नो लोग्नेहरूसित अविश्वासी रहेका पत्नी मानिसहरू जस्तै भयौ।\nहे इस्राएलका परिवार, तिमीहरू म प्रति अविश्वासनीय भएका छौ।”\nयो सन्देश परमेश्वरबाट आएको हो।",
			"21": "<sup>21</sup> नाङ्गो डाँडाहरूमा तिमीहरूले रोइरहेको सुन्न सक्छौ।\nइस्राएलका मानिसहरू दयाको निम्ति रोइरहेछन् र प्रार्थना गरिरहेछन्।\nतिनीहरू एकदमै दुष्ट भए।\nतिनीहरूले परमप्रभु आफ्ना परमेश्वरलाई बिर्सेका छन्।",
			"22": "<sup>22</sup> “हे अविश्वासि छोराहरू हो, मकहाँ फर्की आऊ।\nतिमीहरूको विश्वासहीनता म हटाइदिनेछु।”\n“मानिसहरूले भन्नै पर्छ, ‘हामी तपाईं कहाँ आउँदैछौं,\nतपाईं परमप्रभु हाम्रो परमेश्वर हुनुहुन्छ।",
			"23": "<sup>23</sup> डाँडाहरूमाथि मूर्ति पूज्न एउटा मूर्खता थियो।\nपर्वतमा होहल्ला गर्ने दलहरू सबै गल्ती थिए।\nनिसन्देह इस्राएलको उद्धार\nपरमप्रभु परमेश्वरबाट आउँछ।",
			"24": "<sup>24</sup> हामीहरू युवास्था देखि नै झूटो देवताले\nहाम्रा पिता-पुर्खाहरूको सम्पत्ति नष्ट पारिदिए।\nतिनीहरूका भेंडा़, गाई-गोरूका बथानहरू\nअनि छोरा छोरीहरू पनि लगिदिए।",
			"25": "<sup>25</sup> आफ्नौ लाजले हामी डुबौं\nअनि हामीलाई अपमानले छोपोस्।\nकिनभने हामीले परमप्रभु हाम्रो परमेश्वरको विरूद्ध पाप गर्यौं।\nहाम्रा पिता-पुर्खाहरूले पाप गरे,\nअनि हाम्रो बाल्यकालबाट अहिले सम्म हामीले\nहाम्रा परमप्रभु परमेश्वरको आज्ञा पालन गरेका छैनौं।’”"
		},
		"4": {
			"1": "<sup>1</sup> परमप्रभुले भन्नुभयो,\n“हे इस्राएलका मानिसहरू!\nयदि तिमीहरू मकहाँ फर्की आउनु चाहन्छौ भने आऊ।\nतिमीहरूका मूर्तिहरू मेरो सामनेबाट हटाइदेऊ।\nमदेखि नबरालिओ।",
			"2": "<sup>2</sup> ‘यदि परमप्रभु जीवित हुनुहुन्छ,’ भनी शपथ खान्छौ,\nर त्यसलाई सरलता, ईमान्दारी,\nअनि धार्मिकतासित पालन गर्छौ भने,\nजाति-जातिहरू परमप्रभुको नाउँद्वारा आफैं धन्य हुनेछन्,\nअनि उहाँ तिनीहरूका परमेश्वर हुँ भनी गर्व गर्नु हुनेछ।”",
			"3": "<sup>3</sup> परमप्रभुले यही कुराहरू यहूदा र यरूशलेमलाई भन्नुभयो:\n“तिम्रा खेतहरू जोत!\nकाँढाहरू माझ बीऊहरू नछर।",
			"4": "<sup>4</sup> परमप्रभु अघि आफ्नै खतना गर\nअनि हृदयको अपवित्रता आफैं हटाऊ।\nहे यहूदा र यरूशलेमका मानिसहरू हो!\nतिमीहरू परिवर्तित भएनौ भने\nमेरो क्रोध अग्नि झैं फैलिनेछ।\nकसैले पनि त्यो आगो नियन्त्रण गर्न सक्ने छैन।\nयो तिमीहरूको दुष्टकार्यको\nकारण यस्तो हुनेछ।”",
			"5": "<sup>5</sup> उत्तरदेखि महाविपद्\n“यहूदाका मानिसहरूलाई यो सन्देश देऊ:\nयरूशलेम शहरका प्रत्येक मानिसलाई भन:\n‘राज्यका चारैतिर तुरही फुक।’\nचिच्याउँदै कराउँदै भन, ‘सँगै आऊ।\nसुरक्षाको लागि हामी सबै बलियो शहरहरू तिर भाग्यौं।’",
			"6": "<sup>6</sup> सियोन तिर संकेतको झण्डा उठाऊ।\nनपर्ख, आफ्नो जीवनको लागि भाग!\nयसै गर किनभने म उत्तरतिरबाट महा विपत्ति\nर ठूलो सत्यनाश ल्याउनेछु।”",
			"7": "<sup>7</sup> एउटा “सिंह” आफ्नो ओडारबाट बाहिर आएको छ।\nजाति-जातिहरूलाई नष्ट गर्ने आएको छ।\nतिम्रा भूमि सर्वनाश पार्नु त्यो आफ्नो ठाँउबाट बाहिर आएको छ।\nतिम्रा शहरहरू नष्ट पार्नेछ,\nअनि त्यहाँ कोही पनि बास गर्ने छैन।",
			"8": "<sup>8</sup> यसकारण भाङ्गरा लगाऊ, शोक गर अनि विरक्तकर स्वरमा चिच्याऊ।\nकिनभने परमप्रभु अझैं हामीसित रीसाउनु भएको छ।",
			"9": "<sup>9</sup> परमप्रभु भन्नुहुन्छ, “जब यस्ता घट्ना घट्छ,\nराजा अनि उसका अधिकारीहरूको हृदय दुर्बल हुनेछ,\nपूजाहारीहरू भयभीत हुन्छन्\nअनि अगमवक्तहरू अचम्मित हुनेछन्।”",
			"10": "<sup>10</sup> तब मैले भने, “हे परमप्रभु मेरो मालिक! तपाईंले साँच्चै यहूदा र यरूशलेमका मानिसहरूलाई धोका दिनुभयो। तपाईंले भन्नुभएको थियो, ‘तिमीहरूले शान्ति पाउनेछौ।’ तर तिनीहरूकै घाँटीहरूमा तरवारले हानियो!”",
			"11": "<sup>11</sup> त्यस समयमा यहूदा\nर यरूशलेमका मानिसहरूलाई सम्झाइनेछ,\n“मेरो मानिसहरू विरूद्ध नाङ्गो डाँडाहरूबाट\nतातो हावा बहने छ,\nतर तिनीहरूलाई निफन्न\nअथवा सफा पार्नु होइन।",
			"12": "<sup>12</sup> यो हावा त्यो भन्दा पनि बलियो हुनेछ,\nअनि यो मबाट आउँछ।\nअब, यहूदाका मानिसहरूका विरूद्ध\nम मेरो न्याय घोषणा गर्नेछु।”",
			"13": "<sup>13</sup> हेर! शत्रु बादल झैं आउँदैछ।\nउसको रथ हुरी झैं चल्छ,\nघोडाहरू चीलहरू भन्दा छिटा छन्।\nहामीलाई धिक्कार छ, हामी नष्ट भयौं।",
			"14": "<sup>14</sup> हे यरूशलेमका मानिसहरू!\nआफ्नो हृदयबाट पापहरू धोई पखाल\nताकि तिमीहरू बाँच्नेछौ।\nकहिल्ये सम्म आफ्नो दुष्ट्याँइसित अड्किरहन्छौ?",
			"15": "<sup>15</sup> दानका सन्देशवाहकको आवाज सुन।\nएप्रैमबाट उसले नराम्रो खबर घोषणा गर्छ:",
			"16": "<sup>16</sup> “जाति-जातिहरूलाई खबर गर।\nयरूशलेमभरि मानिसहरूमा यो खबर फैलाऊ।\nटाढोबाट घेरा हाल्ने सैन्य आइरहेको छ।\nयी शत्रुहरूले यहूदा शहरहरू विरूद्ध लडाईँको घोषणा गरिरहेका छन्।",
			"17": "<sup>17</sup> तिमीहरूले मेरो विरूद्धमा विद्रोह गरेकाले\nखेतालाहरूले खेत सुरक्षित पारे झैं\nशत्रुहरूले यरूशलेमलाई घेरेकाछन्।”\nयो सन्देश परमेश्वरबाट आएको हो।",
			"18": "<sup>18</sup> “तिमीहरूले जुन प्रकारले काम गर्यौ,\nअनि जुन तरिकाले बसो-बास गर्यौ त्यसले यो विपद् निम्त्यायो।\nयो तिम्रो लागि सजाय हो।\nयो तीक्त छ र तिम्रो मुटु छुन्छ।”",
			"19": "<sup>19</sup> यर्मियाको विलाप\nहाय मेरो वेदना, मेरो वेदना!\nम मनोव्यथामा छु!हे मेरो हृदय!\nमेरो मुटुले मभित्र प्रहार गरिरहेछ।\nम चूपचाप बस्न सक्तिनँ,\nकिनभने मैले तुरहीको आवाज सुनेको छु।\nअनि तुरहीले, सैनिकलाई युद्ध तर्फ बोलाइरहेछ।",
			"20": "<sup>20</sup> ध्वंसपछि ध्वंस आउँछ।\nसम्पूर्ण देशनै ध्वंस भयो।\nहटात मेरा पालहरू पनि ध्वंस भए।\nमेरा पर्दाहरू च्यातिए र तल झारिए।",
			"21": "<sup>21</sup> परमप्रभु, कहिले सम्म मैले युद्धको झण्डाहरू हेर्नु नै पर्छ?\nकहिले सम्म मैले युद्धको तुरहीहरू सुन्नै पर्छ?",
			"22": "<sup>22</sup> परमेश्वरले भन्नुभयो, “मेरा मानिसहरू मूर्ख छन्।\nतिनीहरूले मलाई चिन्दैनन्।\nतिनीहरू अज्ञानी नानीहरू हुन्।\nतिनीहरू बुझ्दैनन्।\nतिनीहरू दुष्टाईं गर्नमा सिपालु छन्,\nतर तिनीहरू असल काम कसरी गर्नु जान्दैनन्।”",
			"23": "<sup>23</sup> विपत्ति आईरहेछ\nमैले पृथ्वीमा हेरें यो खाली थियो;\nयसमा केही पनि थिएन्।\nमैले आकाशमा हेरें,\nअनि यसको प्रकाश धीमा भइसकेको थियो।",
			"24": "<sup>24</sup> मैले पर्वतहरूलाई हेरें,\nअनि तिनीहरू कामिरहेका थिए।\nसारा पहाडहरू हल्लिरहेका थिए।",
			"25": "<sup>25</sup> मैले हेरें, तर त्यहाँ कोही पनि थिएन।\nअकाशका सारा चरा-चुरूङ्गीहरू उडिसकेका थिए।",
			"26": "<sup>26</sup> मैले हेरें, अनि मलिलो भूमि, मरुभूमि भयो।\nत्यस भूमिमा भएका सबै शहरहरू नाश भए।\nयो परमप्रभुले नै गर्नुभयो, उहाँको क्रोधको प्रतिफल हो यो।",
			"27": "<sup>27</sup> परमप्रभुले यी कुराहरू भन्नुभयो\n“सारा देश नै नष्ट हुनेछ।\nतर म पूर्ण रूपले यो भूमिलाई नष्ट पार्ने छैन।",
			"28": "<sup>28</sup> त्यसै कारण त्यो भूमि रूनेछ,\nअनि माथि आकाश कालो हुनेछ।\nम बोलें अनि परिवर्तन हुने छैन्।\nमैले निर्णय लिएँ अनि मेरो मन बद्लिने छैन्।”",
			"29": "<sup>29</sup> प्रत्येक शहरले घोडसवारी\nर धनुर्धारीहरूको आवाज सुन्नेछ अनि भाग्नेछ!\nकेही मानिसहरू पोथ्राहरूमा लुक्नेछन्,\nकतिपय मानिसहरू चट्टानहरूमाथि चढ्नेछन्।\nसबै शहरहरू शून्य हुनेछन्,\nत्यहाँ कोही पनि बस्ने छैन।",
			"30": "<sup>30</sup> यहूदा तिमी नष्ट भएकाछौ,\nयसकारण अहिले तिमी के गरिरहेका छौ?\nतिमी किन तिम्रो सब भन्दा राम्रो\nरातो वस्त्र लगाईरहेका छौ?\nकिन तिमी तिम्रो सुनका गरगहनाहरू लगाई रहेछौ?\nतिमी किन आफैंलाई सुन्दर पारिरहेकाछौ?\nखाली यो समयको बर्बादी हो।\nतिम्रा प्रेमीहरूले तिमीलाई घृणा गर्छन।\nतिनीहरू तिम्रो ज्यान लिनु खोज्दैछन्।",
			"31": "<sup>31</sup> एउटी स्त्रीलाई प्रसुती पीडाको समयको जस्तै आवाज मैले सुनेको छु।\nपहिलो पल्ट प्रसुती पीडामा परेकी स्त्रीको आवाज झैं थियो।\nत्यो सियोनकी छोरीले हावाको लागि कष्ट श्वास फेर्दै\nर मद्दतको लागि हात फैलाउँदै गरेको आवाज थियो।\n“हाय! म बेहोश हुन लागेकि छु,\nअनि हत्याराहरू मेरा वरिपरि छन्।”"
		},
		"5": {
			"1": "<sup>1</sup> यहूदाका मानिसहरूका दुष्टता\nपरमप्रभु भन्नुहुन्छ, “यरूशलेमका सडकहरूमा हिंड। वरिपरि हेर र ती कुराहरूको विषयमा सोंच। यसका सार्वजनिक ठाँउहरू खोज। यदि यस्तो मानिस जसले जो ईमान्दार छ अनि जसले सत्यको खोजी गर्छ र तिमीले त्यस्तो मानिस भेट्टायौ भने म यरूशलेमलाई माफ गर्नेछु।",
			"2": "<sup>2</sup> मानिसहरू शपथ खान्छन् र भन्छन्, ‘परमेश्वर जीवित हुनुहुन्छ।’ तर तिनीहरू झूटा शपथ खान्छन्।”",
			"3": "<sup>3</sup> हे परमप्रभु, म जान्दछु कि तपाईंप्रति,\nमानिसहरू निष्ठावान भएको चाहनु हुन्छ।\nतपाईंले यहूदाका मानिसहरूलाई हिर्काउनु भयो\nतर तिनीहरूले अनुभव गरेनन्।\nतपाईंले तिनीहरूलाई नष्ट गर्नुभयो,\nतर तिनीहरूले त्यसबाट शिक्षा पाएनन्\nतिनीहरू साह्रै हठी थिए।\nतिनीहरूले जीवन परिवर्तन गर्न अस्वीकार गरे।",
			"4": "<sup>4</sup> तर मैले परमप्रभुलाई भनें,\n“यिनीहरू गरीबहरू मात्र छन्।\nतिनीहरू मूर्ख छन्, तिनीहरू परमप्रभुले देखाउनु भएको बाटो चिन्दैनन्,\nअर्थात् तिनीहरू आफ्नो परमेश्वरको इच्छा बुझ्दैनन्।",
			"5": "<sup>5</sup> यसकारण म यहूदाका अग्रजहरूकहाँ जानेछु,\nर तिनीहरूसँग कुरा गर्नेछु,\nकारण परमप्रभुले देखाउनु भएको बाटो तिनीहरूले चिन्दछन्,\nअनि आफ्नो परमप्रभुको व्यवस्था बुझ्दछन्।”\nतर तिनीहरू सबैले परमप्रभुको सेवाबाट\nअलग रहन एक भएका थिए।",
			"6": "<sup>6</sup> तिनीहरू परमेश्वरको विरूद्ध उठे।\nयसकारण जङ्गलबाट आएका सिंहले तिनीहरूलाई आक्रमण गर्नेछ,\nमरुभूमिबाट आएका ब्वाँसोले तिनीहरूलाई मार्नेछ।\nएउटा चितुवा तिनीहरूकै शहरहरूमा लुकेकोछ।\nत्यस चितुवाले शहरबाट बाहिर जो आउँछ त्यसलाई टुक्रा-टुक्रा पार्नेछ।\nतिनीहरूको भाग्य यस्तै हुनेछ कारण तिनीहरूले घरिघरि पाप गरे,\nअनि धरै पल्ट परमप्रभुबाट टाढा गए।",
			"7": "<sup>7</sup> परमेश्वरले भन्नुभयो, “यहूदा, मैले तिमीलाई क्षमा गर्नु पर्ने एउटा सही कारण भन?\nतिम्रा छोरा-छोरीहरूले मलाई परित्याग गरे।\nतिनीहरूले मूर्तिहरूका कसम खाए जो साँच्चो देवताहरू होइनन्।\nमैले तिनीहरूले इच्छा गरेको प्रत्येक चीज दिएँ।\nतर तिनीहरू मेरा विश्वासघाती भए।\nतिनीहरू भीडको साथ वेश्याहरूकहाँ भेला भए।",
			"8": "<sup>8</sup> तिनीहरू खूब पोसिएका घोडाहरू,\nकाम-वासनाले मस्त भएका जस्तै छन्।\nतैपनि तिनीहरूले आफ्नै छिमेकीका पत्नीहरूलाई डाक्दैछन्।",
			"9": "<sup>9</sup> यी कर्महरूको लागि के म\nसजाय नदिऊँ?”\nयो सन्देश परमप्रभुबाट आएको हो।\n“हो! म त्यस्ता जातिहरूलाई सजाय दिनेछु।",
			"10": "<sup>10</sup> “यहूदाको दाखका बोटहरूको एक किनार लागेर जाऊ,\nदाखका काट तर सम्पूर्ण प्रकारले नष्ट नगर।\nतिनीहरूका सबै हाँगाहरू काटी फाल।\nकिनभने ती हाँगाहरू परमप्रभुको सम्पत्ति होइनन्।",
			"11": "<sup>11</sup> इस्राएलका परिवारहरू\nअनि यहूदा सम्पूर्णरूपले मप्रति अविश्वासी भए।”\nयो सन्देश परमप्रभुबाट आएको हो।",
			"12": "<sup>12</sup> “तर तिनीहरूले परमप्रभुलाई अस्वीकार गरे,\nतिनीहरूले विषयमा भने,\n‘परमप्रभुले हामीलाई केही गर्नु हुने छैन।\nकुनै नराम्रो घट्ना घट्ने छैन।\nहामीले युद्ध देख्नु पर्ने हामी भोकै बस्नु पर्नेछ।’",
			"13": "<sup>13</sup> झूटा अगमवक्ताहरू सुख्खा हावा मात्र हुन्।\nपरमेश्वरको वचन तिनीहरूमा छैन।\nतिनीहरूमाथि यही हुनेछ।”",
			"14": "<sup>14</sup> परमप्रभु सेनाहरूका परमेश्वरले यी कुराहरू भन्नुभयो\n“ती मानिसहरूले भने म तिनीहरूलाई दण्ड दिने छैन तिनीहरूलाई।\nयसकारण यर्मिया, मैले तिमीलाई दिएका वचनहरू आगो जस्तै हुनेछन्,\nअनि ती मानिसहरू दाउरा जस्तै हुने छन्।\nत्यो आगोले तिनीहरूलाई पूर्ण रूपले भस्म पार्नेछ।”",
			"15": "<sup>15</sup> हे इस्राएलको घराना हो, यो सन्देश परमप्रभुबाट आएको हो,\n“म तिमीहरूमाथि आक्रमण गराउनु एउटा जाति धेरै टाढाबाट ल्याउँदैछु।\nयो एउटा सामर्थी जाति हो,\nयो एउटा प्राचीन जाति हो।\nत्यसको भाषा तिमीहरूले जान्दैनौ।\nतिनीहरूले के भन्छन् त्यो तिमीहरू बुझ्दैनौ।",
			"16": "<sup>16</sup> तिनीहरूका काँड राख्ने तरकस खुल्ला चिहान जस्तो छ।\nतिनीहरू सबै शक्तिशाली योद्धाहरू हुन्।",
			"17": "<sup>17</sup> तिनीहरूले तिमीहरूका सबै अन्नहरू\nर खाद्य जिनिसहरू खाइदिनेछन्।\nतिनीहरूले तिमीहरूका सबै छोरा-छोरीहरू ध्वंस पार्नेछन्।\nतिनीहरूले तिमीहरूका भेंडाको बथान र गाईका बथानहरू\nअनि दाखका बोटहरू र नेभाराहरू खाइदिनेछन्।\nतिनीहरूले तिमीहरूका पक्का शहरहरू तलवारले नष्ट पर्नेछन्।\nतिनीहरूले तिमीहरूका पक्का शहरहरू नष्ट पर्ने छन् जसमा तिमीहरू भरोसा गर्थ्यौ।”",
			"18": "<sup>18</sup> यो सन्देश परमप्रभुबाट आएको हो, “जब ती सङ्कटका दिनहरू तिमीहरूमाथि आइपर्नेछ, म सम्पूर्ण रूपले ध्वंस पार्नेछैन।",
			"19": "<sup>19</sup> यहूदाका मानिसहरूले तिमीलाई सोध्ने छन्, ‘यर्मिया, परमप्रभु हाम्रा परमेश्वरले हामीमाथि त्यस्तो हानीकारक कार्यहरू किन गर्ने?’ तिमीले तिनीहरूलाई उत्तर देऊ, ‘किनभने तिमीहरूले उहाँलाई त्यागेका छौ। तिमीहरूले आफ्नै भूमिमा विदेशी मूर्तिहरूका सेवा गर्यौ यसकारण, त्यो भूमिमा जुन तिम्रो होइन त्यसमा विदेशीहरूलाई अब सेवा पुर्याउनेछौ।’”",
			"20": "<sup>20</sup> परमप्रभुले भन्नुभयो, “यो सन्देश याकूबका परिवारहरूलाई देऊ,\nयो सन्देश यहूदाका राज्यमा फैलाऊ।",
			"21": "<sup>21</sup> यो सन्देश सुन,\n‘तिमी मूर्ख मानिसहरूसँग चेत नै छैन,\nतिमीहरूका आँखाहरू छन् तर तिमीहरू देख्दैनौ।\nतिमीहरूका कानहरू छन् तर तिमीहरू सुन्दैनौ।’”",
			"22": "<sup>22</sup> परमप्रभुले भन्नुभयो, “तिमीहरू किन मसित डराउँदैनौ?\nतिमीहरू मेरो अघि डरले काम्दैनौ? अँहँ।\nत्यो म नै हुँ जसले सागरको लागि सीमाना झैं तट बनाएँ।\nसागरले बालुवा ननागोस् भनी मैले यो एउटा स्थायी नियम बनाएँ।\nसागरमा छालहरू उठेर आउँछन्,\nतर तिनीहरूले नष्ट पार्न सक्तैनन्।\nछालहरू गर्जन्छन् तर तट नाग्न सक्तैनन्।",
			"23": "<sup>23</sup> यी मानिसहरू अप्ठेरा र विद्रोही छन्।\nतिनीहरूले मसित विद्रोह गरे\nअनि मलाई छोडिदिए।",
			"24": "<sup>24</sup> तर तिनीहरूले कहिल्यै भनेनन्,\n‘आऊ, हामी हाम्रा परमप्रभु परमेश्वरको डर बोकौ अनि उहाँको सम्मान गरौं।\nउहाँले वाली उठाउने समय निर्धारण गरिदिनु हुन्छ।\nउहाँले हामीलाई शरद ऋतु र वसन्तको बर्षा ठीक समयमा दिनुहुन्छ।’",
			"25": "<sup>25</sup> हे यहूदाका मानिसहरू हो!\nतिमीहरूले खराब काम गर्यौ।\nयही कारणले बर्षाकाल र बाली उठाउने समय कहिल्यै आउँदैन।\nतिम्रो पापहरूले तिमीलाई असल वस्तुहरूको आनन्दमय भोगबाट वन्चित गरायो।",
			"26": "<sup>26</sup> मेरा मानिसहरू माझमा दुष्ट मानिसहरू छन्।\nजो पासो थाप्छन् र चराको शिकार गर्नेहरू झैं लुक्छन्।\nतर तिनीहरूले मानिसहरूलाई नै पासोमा पार्छन।",
			"27": "<sup>27</sup> चराहरूले भरिएको पिँजडा जस्तै\nदुष्ट मानिसहरूका घरहरू झूटा कुराहरूले भरिएको हुन्छ।\nतिनीहरूका मिथ्या कर्मले तिनीहरूलाई धनी र शक्तिशाली बनायो।",
			"28": "<sup>28</sup> &nbsp;&nbsp;&nbsp;&nbsp;तिनीहरू आफ्नो दुष्टकर्मले मोटा अनि ठूला मानिसहरू बनिएका छन्।\nतिनीहरूको दुष्ट्याईंले सीमा नाघिसकेकोछ।\nतिनीहरू टुहुरा-टुहुरीहरूका निम्ति बोल्दैनन्।\nतिनीहरू गरीबहरूको पक्षमा निष्पक्ष न्याय गर्दैनन्।",
			"29": "<sup>29</sup> के मैले ती यहूदाका मानिसहरूलाई\nतिनीहरूका दुष्टाईंका निम्ति दण्ड नदिनु?”\nयो सन्देश परमेश्वरबाट आएको हो।\n“के म यस्तो जातिको विरूद्ध प्रतिकार खोज्दै नखोजौं?”",
			"30": "<sup>30</sup> “परमप्रभु भन्नुहुन्छ, डरलाग्दो र भयानक\nएउटा घटना यस देशमा घट्यो।",
			"31": "<sup>31</sup> अगमवक्ताहरूले झूटो अगमवाणी दिन्छन्।\nपूजाहारीहरूले आफू खुशी शासन गर्छन्,\nअनि मेरा मानिसहरूले त्यसलाई त्यसरी नै मन पराउँछन्।\nजब अन्त आउँछ तिमीहरूले के गर्नेछौ?"
		},
		"6": {
			"1": "<sup>1</sup> शत्रुले यरूशलेम घेर्दछ\n“हे बिन्यामिनी हो, आफ्नो सुरक्षाको लागि भाग।\nयरूशलेमको शहरदेखि भाग।\nतको शहरमा युद्धको तुरही फुक\nबेथक्के-रेमको शहरमा चेताउनीको झण्डा उठाऊ।\nती कुराहरू गर किनभने उत्तरदिशाबाट महासर्वनाश आईरहेछ।\nडरलाग्दो सर्वनाश तिमीहरू कहाँ आईरहेछ।",
			"2": "<sup>2</sup> सियोनकी छोरी!\nतिमी हरियो मैदान जस्तै सुन्दरी छौ।",
			"3": "<sup>3</sup> गोठालाहरू यरूशलेमा भेंडा़का बथानहरू लिएर आउँदछन्,\nतिनीहरूले आफ्नो पालहरू यसको वरिपरि बनाउँछन्\nप्रत्येक गोठालोले आफ्ना भेंडा़ बथानको हेरचाह गर्छन्।",
			"4": "<sup>4</sup> “यरूशलेमको विरूद्ध युद्ध गर्न तैयार हौ।\nउठ! मध्य-दिनमा हामी शहर आक्रमण गर्नेछौं।\nतर अति ढिलो भइसकेको छ।\nसाँझको छाँयाहरू फैलिरहेका छन्।",
			"5": "<sup>5</sup> यसकारण उठ! हामी राती शहर आक्रमण गर्नेछौ।\nआऊ, यरूशलेमको पक्का महलहरू नष्ट पारौं।”",
			"6": "<sup>6</sup> यही रीतले सेनाहरूका परमप्रभु भन्नुहुन्छ\n“रूखहरू काट र त्यही रूखहरूले यरूशलेमको वरिपरि\nछेक्ने घेराहरू बनाऊ।\nअत्याचार बाहेक त्यस शहरमा केही छैन।\nयसलाई सजाय भोगाउनै पर्छ।",
			"7": "<sup>7</sup> एउटा कुवाले आफ्नो पानी ताजा राख्छ।\nत्यस्तै प्रकारले, यरूशलेमले आफ्नो दुष्टता ताजा राख्छ।\nसधैँ शहरमा दङ्गा र ध्वंशको विषयमा सुन्छु।\nम सधैँ यरूशलेममा विराम चोटहरू देख्छु।",
			"8": "<sup>8</sup> चेताउनी तर्फ ध्यान देऊ, यरूशलेम।\nयदि तिमीहरूले सुनेनौ भने म तिमीहरूपट्टि पिठ्यूँ फर्काउँनेछु।\nम तिम्रो भूमि रित्तो मरुभूमि जस्तै पारिदिने छु।\nकोही पनि त्यहाँ बस्न योग्य हुनेछैन।”",
			"9": "<sup>9</sup> यही प्रकारले सेनाहरूका परमप्रभु भन्नुहुन्छ:\n“खेतमा दाख बटुले झैं इस्राएलमा बाँकी रहेका मानिसहरू भेला गर।\nदाखहरू टिप्नेले झैं दाखको बोटहरू फेरि नियाल।”",
			"10": "<sup>10</sup> म कोसित बोल्नु?\nमैले कसलाई चेताउनी दिनु?\nकसले मेरो कुरा सुन्छ?\nइस्राएलका मानिसहरूले आफ्ना कानहरू थुनेका छन्।\nयसकारण तिनीहरूले मेरो चेताउनी सुन्दैनन्।\nपरमप्रभुको वचन तिनीहरूका निम्ति शरमपूर्ण हुन्छ।\nतिनीहरू उहाँका कुरा सुन्नु चाहँदैनन्।",
			"11": "<sup>11</sup> यसकारण म परमप्रभुको क्रोधले भरिएको छु।\nम त्यसलाई लिएर थाकिसकेको छु।\n“परमप्रभुको क्रोध गल्लीहरूमा खेलिरहेका नानीहरूमाथि पोखाऊ।\nजवानहरूमाथि परमेश्वरको क्रोध खन्याऊ।\nलोग्ने-स्वास्नी एकै साथ सबै पक्राऊ पर्नेछन्।\nसबै बूढा-पाकाहरू पक्राऊ पर्नेछन्।",
			"12": "<sup>12</sup> तिनीहरूका घरहरू अन्य मानिसहरूलाई दिइनेछ।\nतिनीहरूका खेतहरू र पत्नीहरू अन्य मानिसहरूलाई दिइनेछ।\nम यस भूमिका मानिसहरूमाथि मेरो हात उठाउनेछु।”\nयो सन्देश परमेश्वरबाट आएको हो।",
			"13": "<sup>13</sup> “इस्राएलका सबै मानिसहरू लोभी छन्।\nमाथिल्लो तहदेखि तल्लो तहसम्मका इस्राएली मानिसहरू,\nघुसको लागि मरिमेटछन्।\nअनि अगमवक्ता देखिलिएर पूजाहारीहरूसम्म झूटो बोल्छन्।",
			"14": "<sup>14</sup> अगमवक्ताहरू र पूजाहारीहरूले तिनीहरूका घाउहरूमा मलमपट्टी गर्नु पर्छ।\nतर तिनीहरूले ती घाऊहरूलाई कोट्‌याको जस्तो मात्र ठाने।\nतिनीहरू भन्छन्, ‘प्रत्येक कुरो ठीक छ,\nअनि प्रत्येक जग्गामा शान्ति छ’, जब कि त्यहाँ शान्ति छैन।",
			"15": "<sup>15</sup> पूजाहारीहरू र अगमवक्ताहरू आफूले गरेका,\nनराम्रा कुराहरूले लज्जित हुनु पर्ने।\nतर तिनीहरू एकदमै लज्जित छैनन्।\nतिनीहरू कसरी लज्जित हुनुपर्ने जान्दैनन्।\nयसकारण तिनीहरू अरूसितै दण्डित हुनु पर्छ।\nजब म मानिसहरूलाई सजाय दिन्छु,\nतिनीहरू भूईँमा फ्याँकिनु पर्छ।”\nपरमप्रभुले यी कुराहरू भन्नुभयो।",
			"16": "<sup>16</sup> परमेश्वरले यी कुराहरू भन्नुभयो,\n“दोबाटोमा उभ अनि हेर।\nसोध पूरानो बाटो कहाँ छ।\nसोध कहाँ असल बाटो छ, अनि त्यो बाटोमा हिँड।\nयदि तिमीले त्यसो गर्यौ भने तिमी स्वयंले विश्राम पाउनेछौ।\nतर तिमीहरूले भन्यौ,\n‘हामी असल बाटोमा हिँड्ने छैनौं।’",
			"17": "<sup>17</sup> तिमीहरूमाथि निगाह राख्न मैले एकजना चौकीदार रोजेको छु।\nमैले तिनीहरूलाई बताएँ, ‘युद्धको तुरहीको आवाज सुन।’\nतर तिनीहरूले भने, ‘हामी सुन्ने छैनौ।’",
			"18": "<sup>18</sup> यसर्थ, सारा जाति-जातिहरू, सुन!\nतिनीहरूको साक्षी-प्रमाण विषयमा ध्यान देऊ।",
			"19": "<sup>19</sup> हे पृथ्वीका मानिसहरू हो! यसमा ध्यान देऊ!\nयहूदाका मानिसहरूको लागि म नष्ट ल्याइरहेको छु,\nकिनभने तिनीहरूले नराम्रो कर्मका निम्ति योजना बनाएका छन्,\nअनि तिनीहरूले मेरो सन्देशहरूलाई ध्यान दिएनन्,\nअनि मेरा शिक्षाहरू अस्वीकार गरे।”",
			"20": "<sup>20</sup> परमप्रभु भन्नुहुन्छ, “किन मेरो लागि शबाको देशबाट सुगन्धित धूपहरू ल्यायौ?\nकिन तिमीहरूले मेरो निम्ति सुदुर देशबाट सुगन्धित बेंत ल्यायौ?\nतिमीहरूले गरेका बलिहरूले मलाई खुशी बनाउँदैन।\nतिम्रो बलिदानहरूले मलाई सन्तुष्ट पार्दैन्।”",
			"21": "<sup>21</sup> यसकारण, त परमप्रभु यसो भन्नुहुन्छ,\n“म यहूदाका मानिसहरूलाई समस्याहरू दिनेछु।\nतिनीहरू ढुङ्गाहरू जस्तो हुनेछन् जसले मानिसहरूलाई पतन गराउनेछ।\nबाबु छोराहरू तिनीहरूमाथि ठोकिने छन्,\nमित्रहरू र छिमेकीहरू मर्नेछन्।”",
			"22": "<sup>22</sup> परमप्रभु यसो भन्नुहुन्छ,\n“हेर, सेना उत्तरबाट आईरहेछ।\nघेरै टाढा-टाढा ठाउँहरूबाट एउटा महान जाति पृथ्वीमा आइरहेछ।",
			"23": "<sup>23</sup> सेनाहरूले तिनीहरूसित धनु-काँड र भालाहरू ल्याउँछन्।\nतिनीहरू निष्ठूर छन्।\nतिनीहरूमा दया छैन।\nतिनीहरूका आवाज घोडाहरूको आवाज जस्तै छ।\nहे सियोनकी छोरी! यी सेनाहरू युद्धका निम्ति तत्पर छन्,\nर तिमीमाथि आक्रमण गर्नु आइरहेकाछन्।”",
			"24": "<sup>24</sup> हामीले ती सेनाहरूको बारेमा सुनेका छौं।\nहाम्रा हातहरू भयले दुर्बल भएका छन्।\nप्रसुती वेदनामा परेकी स्त्रीलाई जस्तै\nहामीलाई दु:ख र पीडाले घेरेको छ।",
			"25": "<sup>25</sup> खेतमा तिर नजाऊ।\nअनि सडकतिर नआऊ,\nकिनभने शत्रुहरूसित तरवारहरू छन्\nअनि जहीं-तहीं विपद छ।",
			"26": "<sup>26</sup> मेरा मानिसहरू हो!\nदु:ख प्रकट गर्ने पोषाक लगाऊ,\nअनि खरानीमाथि लडी-बडी गर।\nयस्तो प्रकारले विलाप गर कि,\nमानौं तिम्रो भएको एउटै मात्र बालक मरेको छ,\nकिनभने विनाश अतिशीघ्र हामीमाथि आउनेछ।",
			"27": "<sup>27</sup> “यर्मिया, मैले तिमीलाई\nमेरा मानिसहरू माझ कसी लाउने मानिस बनाएँ।\nतिमीले तिनीहरूलाई परीक्षा गर्नेछौ,\nर तिनीहरूको जीवन विषयमा चाल पाउनेछौ।",
			"28": "<sup>28</sup> तिनीहरू सबै विद्रोही हुन्\nर चारैतिर असन्तुष्टी मच्चाइरहेका छन्।\nतिनीहरू सबै काँसा र फलाम जस्तै छन्\nतिनीहरू विनाशकहरू हुन्।",
			"29": "<sup>29</sup> खँलातिले आगो तेज पार्छ र अशुद्धी हटाउँछ,\nतर मेरा मानिसहरूलाई केही गर्नु सकिंदैन,\nकारण तिनीहरूबाट खराबी हटाउनु सकिंदैन।",
			"30": "<sup>30</sup> तिनीहरूलाई ‘खोटो चाँदी’ भनिनेछ।\nतिनीहरूलाई त्यो नाउँ दिइने छ,\nकिनभने परमेश्वरले तिनीहरूलाई अस्वीकार गर्नु भयो।”"
		},
		"7": {
			"1": "<sup>1</sup> मन्दिरमा दिएको यर्मियाहको उपदेश\nयो यर्मियालाई परमप्रभुको सन्देश हो:",
			"2": "<sup>2</sup> “यर्मिया, परमेश्वरको मन्दिरको प्रवेश द्वारमा उभ। यो सन्देश प्रवेश द्वारमा प्रचार गर:\n“‘हे यहूदाका मानिसहरू! परमप्रभुको वचन सुन। परमप्रभुको पूजा गर्न जो पनि यी फाटकहरूबाट आउँछ; यो वचन सुन,",
			"3": "<sup>3</sup> सेनाहरूका परमप्रभु इस्राएलका परमेश्वरले यही भन्नुहुन्छ, “तिमीहरू आफ्नो जीवन शैली परिवर्तन गर अनि राम्रा कर्महरू गर। म तिमीहरूलाई यस भूमिमा बस्न दिनेछु।”",
			"4": "<sup>4</sup> कतिपय मानिसहरूले झूटो कुराहरू गर्छन् त्यो विश्वास नगर। तिनीहरू भन्छन्, “यो परमप्रभुको मन्दिर हो, परमप्रभुको मन्दिर, परमप्रभुको मन्दिर हो।”",
			"5": "<sup>5</sup> यदि तिमीहरूले आफ्ना जीवन परिवर्तन गरेर असल कर्महरू गर्यौ भने, म तिनीहरूलाई यस ठाउँमा बस्न दिनेछु। तिमीहरू परस्परमा ईमान्दार हुन पर्छ।",
			"6": "<sup>6</sup> तिमीहरू परदेशीहरूसँग ईमान्दार हुनु पर्छ। तिमीहरूले विधवाहरू एवं टुहुरा-टुहुरीहरूसित ठीक कुराहरू गर्नु पर्छ। निर्दोष मानिसहरूलाई नमार। अन्य देवताहरूका अनुवायी नबन्। किनभने तिनीहरूले तिमीहरूका जीवन नष्ट पारिदिने छन्।",
			"7": "<sup>7</sup> यदि तिमीहरूले मेरो आज्ञा-पालन गर्यौ भने, म तिमीहरूलाई यस ठाउँमा बस्न दिने छु। म यो भूमि सधैँ भरि तिमीहरूका पिता-पुर्खाहरूलाई दिनेछु।",
			"8": "<sup>8</sup> “‘हेर, तिमीहरू झूटो कुराहरूमाथि भरोसा गरिरहेका छौ जुन अनर्थका छन्।",
			"9": "<sup>9</sup> के तिमी चोरी गर्छौ, हत्या अनि व्यभिचार गर्छौ! के तिमी झूटो शपथ लिनेछौ? के बाललाई पूज्छौ? तिमीहरूले नजानेका देवताहरू पूज्छौ?",
			"10": "<sup>10</sup> यदि तिमीहरूले त्यस्ता पापहरू गर्यौ भने, मेरो समक्ष मेरो घरमा जुन मेरो नाउँले डाकिन्छ त्यहाँ उभिन सक्छौं भनेर सम्झिन्छौ? के तिमीहरू सम्झन्छौ कि मेरो समक्ष उभिएर यसो भन्न सक्छौ, “हामी सुरक्षित छौं,” त्यस्ता डरलाग्दा दुष्टयाईं गरिरहनलाई?",
			"11": "<sup>11</sup> यो मन्दिर मेरो नाउँद्वारा बोलाइन्छ। के यो घर तिमीहरूका लागि डाकाहरू लुक्ने ठाउँ बाहेक केही होइन जस्तो लाग्छ? म तिमीहरूलाई नियाली रहेछु।’” यो सन्देश परमप्रभुबाट आएको थियो।",
			"12": "<sup>12</sup> “‘यहूदाका मानिसहरू शीलो अब शहरमा जाऊ। त्यो ठाउँमा जाऊ जहाँ मैले मेरो नाममा प्रथम घर बनाएँ। इस्राएलका मानिसहरूले पनि पाप कामहरू गरे। जाऊ र त्यहाँ हेर, मैले के गरें किनभने तिनीहरूले पाप कामहरू गरें।",
			"13": "<sup>13</sup> किनभने तिमीहरूले यी सब गर्यौ, परमप्रभु भन्नुहुन्छ, किनभने मैले तिमीहरूलाई तत्कालै भने, तर तिमीहरूले ध्यान दिएनौ अनि मैले बोलाएँ तर जवाफ दिएनौ।",
			"14": "<sup>14</sup> यसकारण यरूशलेममा अवस्थित मेरो नाउँद्वारा बनिएको घर ध्वंस पार्नेछु। म त्यो घरलाई शीलो भत्काए झैं नष्ट पारिदिनेछु। अनि यरूशलेममा अवस्थित त्यो घर जुन मेरो नाउँद्वारा बोलाइन्छ त्यसमा तिमीहरू भरोसा गर्छौ। मैले त्यो ठाउँ तिमीहरू र तिमीहरूका पिता-पुर्खाहरूलाई दिएँ।",
			"15": "<sup>15</sup> म तिमीहरूलाई मेरो नजरबाट जबरजस्ती हटाउनेंछु, जसरी मैले तिमीहरूका दाज्यू-भाइहरू, एप्रैमका सबै मानिसहरूलाई गरें।’",
			"16": "<sup>16</sup> “सम्मान, यर्मिया यहूदाका मानिसहरूका लागि प्रार्थना नगर। तिनीहरूको निम्ति बिन्ती अथवा प्रार्थना नगर। तिनीहरूका सहायताका निम्ति मलाई अन्तर बिन्ती नगर। म तिनीहरूका निम्ति तिम्रो प्रार्थना सुन्ने छैन।",
			"17": "<sup>17</sup> यहूदाका शहरहरू र यरूशलेमका गल्लीहरूमा तिनीहरूले के गरिरहेका छन् तिमी देख्न सक्तैनौ?",
			"18": "<sup>18</sup> छोरा-छोरीहरूले दाउरा बटुल्छन् अनि बाबुहरू आगो सल्काउँछन्। स्वर्गकी रानीलाई मिठो रोटी चढाउँन स्त्रीहरू पिठो मुसिरहेका छन्। अरू देवताहरूलाई खुशी पार्न अर्घबलि चढाउने छन् मलाई रीस उठाउन तिनीहरू यसो गर्छन्।",
			"19": "<sup>19</sup> तर म त्यो होइन जसलाई यहूदाका मानिसहरूले चोट पुर्याइरहेका छन्।” यो सन्देश परमेश्वरबाट आएको हो, “तिनीहरू आफैंलाई चोट पुर्याका छन्। तिनीहरूले आफैंलाई लज्जित तुल्याइरहेका छन्।”",
			"20": "<sup>20</sup> यसकारण सेनाहरूका परमप्रभु भन्नुहुन्छ “म यो ठाउँमा, यहाँका मानिसहरू, पशुहरू, खेतका वृक्षहरू अनि अन्नहरू माथि मेरो रीस पोखाउने छु। मेरो रीस बलिरहेको आगो जस्तै हुनेछ। त्यो कसैले निभाउनु सक्नेछैन।”",
			"21": "<sup>21</sup> आज्ञाकारिता बलिदानहरू भन्दा असलहो\nसेनाहरूका परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ: “तिमीहरूका बलिदानहरूमा तिमीहरूका होमबलि थप अनि उत्सर्गमा चडाएका मासुहरू खाऊ।",
			"22": "<sup>22</sup> मैले तिमीहरूका पिता-पुर्खाहरूलाई मिश्रदेखि बाहिर ल्याएँ। मैले तिनीहरूलाई होमबलि चढाउन अथवा बलि चढाउन भनिनँ नता केही आज्ञा दिएँ।",
			"23": "<sup>23</sup> मैले केवल तिनीहरूलाई आज्ञा दिएँ, ‘मेरो आज्ञा पालन गर अनि म तिमीहरूको परमेश्वर हुनेछु अनि तिमीहरू मेरो मानिसहरू हुनेछौ तिमीहरूको हितका निम्ति मेरा सबै आज्ञाहरू पालन गर।’",
			"24": "<sup>24</sup> “तर तिमीहरूका पिता-पुर्खाहरूले मेरो कुराको वास्ता गरेनन्। तिनीहरू हठ्ठी थिए अनि तिनीहरूको दुष्ट हृदयले जे मन परायो त्यही गरे। तिनीहरू अघि बढेनन् बरू पछि सरे।",
			"25": "<sup>25</sup> तिमीहरूका पिता-पुर्खाहरूले मिश्र छाडेको दिनदेखि आज सम्म मेरा सेवकहरू तिमीहरूकहाँ पठाएँ। मेरा सेवकहरू अगमवक्ताहरू थिए। मैले तिनीहरूलाई दिन दिनै पठाँए।",
			"26": "<sup>26</sup> तर तिमीहरूका पिता-पुर्खाहरूले मेरो कुरा सुन्दै सुनेनन्। तिनीहरू अत्यन्त हठी थिए अनि आफ्ना पिताहरूले भन्दा नीच भन्दा नीच कामहरू गरे।",
			"27": "<sup>27</sup> “यर्मिया, तिमीले यहूदाका मानिसहरूलाई ती कुराहरू भन्नेछौ। तर तिनीहरूले तिम्रा कुराहरू सुन्ने नै छैनन्। तिमीले तिनीहरूलाई डाक्ने छौ, तर तिनीहरूले जवाफ दिने छैनन्।",
			"28": "<sup>28</sup> यसकारण तिमीले तिनीहरूलाई यी कुराहरू भन्नेछौ ‘यो यस्तो राज्य हो जहाँ मानिसहरूले परमप्रभु आफ्नो परमेश्वरको आज्ञा पालन गर्दैनन्। तिनीहरूले परमेश्वरले भने अनुशासन ग्रहण गरेनन्। सत्य सडेको छ, तिनीहरूको ओठबाट यो अलग गरिएको छ।’",
			"29": "<sup>29</sup> हत्याकाण्डको बेंसी\n“यर्मिया, आफ्नो केश काट र त्यसलाई फ्याकिदेऊ। पहाडको थुम्कामा चढ र शोक गीत गाऊ। किनभने परमेश्वरले यो वंश जसले उहाँलाई रीस उठायो त्यसलाई अस्वीकार र त्याग गर्नु भएको छ।",
			"30": "<sup>30</sup> त्यसै गर किनभने यहूदाका मानिसहरूले पाप कामहरू गरिरहेको मैले देखेको छु।” यो सन्देश परमेश्वरबाट आएको हो “तिनीहरूले आफ्नो मूर्तिहरू स्थापित गरेका छन्। अनि म ती मूर्तिहरू घृणा गर्दछु। तिनीहरूले मूर्तिहरू मेरो नाउँद्वारा डाकिने घरमा स्थापित गरे। तिनीहरूले मेरो घर ‘अशुद्ध’ पारे।",
			"31": "<sup>31</sup> यहूदाका मानिसहरूले बेंसीमा भएको उपत्यकामा तोपेतको उच्च स्थानहरू बनाए। ती जग्गाहरूमा मानिसहरूले आफ्ना छोरा छोरीहरूलाई मारे-तिनीहरूले बलिको रूपमा तिनीहरूलाई जलाए। यस्तो आज्ञा मैले कहिल्यै गरिनँ। यस्तो कुराहरू मेरो मनभित्र कहिल्यै पसेको थिएन।",
			"32": "<sup>32</sup> यसर्थ, म तिमीहरूलाई चेताउनी गर्छु। दिनहरू आइरहेछ,” यो सन्देश परमेश्वरबाट आएको हो, “जब मानिसहरूले यस ठाउँलाई तोपेत अथवा बेन-हिन्नोनको उपत्यका भनेर डाक्ने छैन। होइन्, तिनीहरूले यसलाई हत्याको बेंसी भनेर डाक्नेछन्। तिनीहरूले यो नाउँ दिने छन्। जब सम्म त्यहाँ एउटा चिहानको लागि मात्र पनि जग्गा रहनेछैन तब सम्म मानिसहरूले लाशहरू तोपेतमा गाड्नेछन्।",
			"33": "<sup>33</sup> लाशहरू चरा चुरूङ्गी र जङ्गली पशुहरूको खाद्य बनिनेछ। त्यहाँ चराहरू र जनावरहरूलाई धपाउनु कोही पनि जीवित रहनेछैन।",
			"34": "<sup>34</sup> म यहूदाका शहरहरूमा र यरूशलेमका गल्लीहरूमा आनन्द र खुशीयालीको ध्वनि अन्त गरिदिनेछु। यहूदा अथवा यरूशलेममा हर्ष र आनन्दको सोर अन्त गरिदिने छु। दुलहा र दुलहीको आवाज निस्कने छैन। त्यो भूमि उजाड हुनेछ।”"
		},
		"8": {
			"1": "<sup>1</sup> यो सन्देश परमेश्वरबाट आएको हो, “त्यस बेला मानिसहरूले यहूदाका राजाहरू र मुख्य शासकहरूको हड्डीहरू, अगमवक्ताहरू र पूजाहारीहरूका हड्डीहरू र यरूशलेमका समस्त मानिसहरूका हड्डीहरू उनीहरूका चिहानहरूबाट लैजानेछन्।",
			"2": "<sup>2</sup> तिनीहरूले सबै हड्डीहरू घाम, जुन र ताराहरू मुनि मैदानमा फिँजाउनेछन्। यरूशलेमका मानिसहरूले घाम, जुन अनि ताराहरूलाई माया गर्थे। तिनीहरूले तिनीहरूलाई सेवा र अनुशरण गरे। तिनीहरूले तिनीहरूसित परमर्श गरे अनि तिनीहरूको अघि घुँडा टेकेर पूजा गरे। फेरि कसैले पनि हड्डीहरू बटुल्ने छैन अनि पुर्ने छैन। यही कारणले हड्डीहरू मैदानमा फिंजाएको गाईको गोबर जस्तै हुनेछ।",
			"3": "<sup>3</sup> “म यहूदाका मानिसहरूलाई आफ्नो घरपरिवार छाड्न बाध्य गराउनेछु। ती मानिसहरूलाई विदेशमा लगिनेछ। यहूदाका केही मानिसहरू जो लडाँईमा मरेनन् तिनीहरूले लडाइँमा मरेको भए हुन्थ्यो भन्ने इच्छा गर्नेछन्।” यो परमप्रभुको सन्देश हो।",
			"4": "<sup>4</sup> पाप र दण्ड\n“यर्मिया, यो यहूदाका मानिसहरूलाई भन: ‘परमप्रभु यी कुराहरू भन्नुहुन्छ:\n“‘तिमी जान्दछौ कि जब एकजना मानिस तल झर्छ,\nफेरि माथि उठ्छ,\nयदि एकजना मानिस विग्रन्छ भने,\nफेरि सप्रिनेछ।",
			"5": "<sup>5</sup> यहूदाका मानिसहरूले भूल बाटो समाते।\nतर किन यी मानिसहरू निरन्तर गल्ती बाटोमा गईरहेछन्?\nतिनीहरू आफ्नै झूटा कुराहरू विश्वास गर्छन्।\nतिनीहरू फर्की आउन अस्वीकार गर्छन।",
			"6": "<sup>6</sup> मैले तिनीहरूलाई खुब होशियारीसित हेरेको छु अनि सुनेको छु।\nतिनीहरू सही कुरा बोल्दैनन्।\nतिनीहरू आफूले गरेको पापमा दु:खी छैनन्,\nअनि भन्दैनन्, मैले के गरें?\nयुद्ध भूमिमा दौडिरहेको घोडाहरू जस्तै तिनीहरू\nआफ्नै बाटोमा हिँडिरहेका छन्।",
			"7": "<sup>7</sup> आकाशमा उड्ने चरा-चुरूङ्गीहरूले पनि\nआफ्नो ठीक समय जान्दछन्,\nअनि साँरस, ढुकुर, गौंथली कर्याङ्ग-कुरूङ्गहरूले,\nआफ्नो नयाँ घरमा जाने समय जान्दछन्,\nतर मेरा मानिसहरू जान्दैनन्,\nपरमेश्वरले उनीहरूबाट के चाहनु हुन्छ।",
			"8": "<sup>8</sup> “‘तिमीहरू भन्छौ, “हामीसँग परमेश्वरको शिक्षाहरूछन् यसर्थ हामी ज्ञानी छौं!”\nतर यो सत्य होइन, किनभने शास्त्रीहरूले आफ्ना कलमलाई झूटो बनाएका छन्।",
			"9": "<sup>9</sup> “ज्ञानी मानिसहरूले” परमप्रभुको वचनहरू सुन्न अस्वीकार गरे।\nयसकारण तिनीहरू वास्तवमा “ज्ञानी मानिसहरू” होइनन्।\nती “ज्ञानी मानिसहरूलाई” जालमा फँसाइएका छन्,\nअपमान गराइएका छन् र लज्जित पारिएका छन्।",
			"10": "<sup>10</sup> यसकारण, म तिनीहरूका पत्नीहरू अरूहरूलाई दिनेछु।\nम तिनीहरूका खेतहरू नयाँ मालिकहरूलाई दिनेछु।\nसम्पूर्ण इस्राएलका मानिसहरूले घुस लिन्छन्।\nसबै मानिसहरू-तल्लो दर्जादेखि माथिल्लो दर्जा सम्माका एउटै व्यवहारका छन्।\nतिनीहरू अगमवक्ताहरूदेखि पूजाहारीहरूसम्म सबै कपट व्यवहार गर्छन्।",
			"11": "<sup>11</sup> मेरो मानिसहरूका गहिरो घाउहरूलाई\nतिनीहरूले केही जस्तो मानेनन्।\nतिनीहरू भन्छन्, “यो ठीकै छ, प्रत्येक कुरो नै ठीकै छ।”\nतर वास्तवमा केही पनि ठीक छैन।",
			"12": "<sup>12</sup> तिनीहरू आफ्ना नराम्रा कामहरूदेखि लज्जित हुनै पर्छ।\nतर तिनीहरू पटक्कै लज्जित छैनन्।\nकसरी लज्जित हुनु तिनीहरू जान्दैनन्।\nयसर्थ जब म तिनीहरूलाई सजाय दिनेछु\nतिनीहरू अरू मानिसहरूसितै तल झर्छन।’”\nपरमप्रभु यसो भन्नुहुन्छ।",
			"13": "<sup>13</sup> “परमप्रभुले भन्नुभयो, ‘म तिनीहरूका अन्नबलि लैजानेछु।\nतिनीहरूका झ्याङमा एउटै पनि अङ्गुर रहनेछैन,\nनेभाराको बोटमा एउटै पनि फल रहने छैन।\nपातहरू पनि ओइलिनेछन र सूख्खा हुनेछन्।\nमैले दिएका प्रत्येक वस्तु लैजानेछु।’”",
			"14": "<sup>14</sup> “किन हामी यहाँ बसिरहेका छौं? ‘आऊ, हामी बलिया शहरहरूमा जाऊँ।\nत्यहाँ हामी मृत्यु वरण गरूँ कारण परमेश्वर,\nहाम्रा परमप्रभुले हामीलाई सड्नु भनी आदेश दिनु भएकोछ।\nहामीले परमप्रभुको विरूद्ध पाप गरेकाछौं,\nयसकारण परमेश्वरले हामीलाई\nविष मिसाएको पानी पिउँनु दिनुभएको छ।",
			"15": "<sup>15</sup> हामीले ब्यार्थमा शान्ति पाउने आशा गर्यौं।\nहामीले निको हुने आशा गर्यौं तर डर मात्र हात लाग्यो।",
			"16": "<sup>16</sup> दानबाट शत्रुहरूका घोडाहरूको स्वाँ-स्वाँ आवाज\nहामी सुन्छौं।\nघोडाहरूको हिनहिनाइले सम्पूर्ण जमीन काम्छ।\nयो जमीन र यसमा भएका प्रत्येक वस्तु\nनष्ट पार्न शत्रुहरू आएका छन्।\nतिनीहरू यो शहर यसमा बसो-बास गर्ने सबै मानिसहरू\nनष्ट पार्न आएका छन्।’”",
			"17": "<sup>17</sup> “हेर, म तिमीलाई आक्रमण गराउन विषालु सर्पहरू पठाउँदैछु।\nती सर्पहरूलाई नियन्त्रणमा राख्नु सकिंदैन।\nतिनीहरूले तिमीलाई डस्नेछन्।”\nयो सन्देश परमप्रभुको हो।",
			"18": "<sup>18</sup> हे परमेश्वर, शोकले मलाई ढाक्छ र मेरो हृदय दुर्बल भएको छ।",
			"19": "<sup>19</sup> मेरा मानिसहरूको चित्कार सुन।\nराष्ट्रको बिभिन्न ठाउँहरू बाट, मानिसहरू सहायताको निम्ति चिच्याई रहेका छन्।\nतिनीहरू भन्छन्, “के परमप्रभु अझै सियोनमा हुनुहुन्छ?\nके सियोनका राजा अझ त्यहाँ हुनुहुन्छ?”\nतर परमेश्वर भन्नुहुन्छ, “यहूदाका मानिसहरूले,\nबेकारका विदेशी देवताहरूलाई पूजे।\nतिनीहरूले मलाई क्रोधित पारे।\nतिनीहरूले किन त्यसो गरे?”",
			"20": "<sup>20</sup> अनि ती मानिसहरू भन्छन्,\n“फसल कटनीको समय बितिहाल्यो।\nग्रीष्मकाल समाप्त भयो।\nअनि अझ हामी सुरक्षित भएका छैनौं।”",
			"21": "<sup>21</sup> मेरा मानिसहरूले मलाई चक्कानाचूर पारेकाले म चक्कानाचूर भएको छु।\nम शोकाकुल छु अनि त्रासले मलाई घेर्छ।",
			"22": "<sup>22</sup> नि:सन्देह गिलादमा केही औषधी छ।\nनि:सन्देह गिलादमा वैद्य छ,\nयसकारण किन मेरा मानिसहरूको चोट निको हुँदैन्?"
		},
		"9": {
			"1": "<sup>1</sup> यदि मेरो शिर पानीले भरिएको हुन्थ्यो,\nअनि यदि मेरो आँखाहरू आँसूको फोहोरा हुन्थ्यो भनें,\nम ती मेरा मानिसहरूका निम्ति जो मारिएका छन् रात दिन रूने थिएँ।",
			"2": "<sup>2</sup> यदि मरुभूमिमा यात्री झैं\nरात बिताउने ठाँउ मेरो भए\nम मेरा मानिसहरूलाई छाडिदिने थिएँ\nर तिनीहरूबाट टाढो गएको हुन्थे।\nकिनभने तिनीहरू परमेश्वरप्रति अविश्वासी छन्।\nतिनीहरू सबै उहाँका विरोधमा उठे।",
			"3": "<sup>3</sup> “ती मानिसहरू आफ्नो जिब्रो धनु झैं बङ्ग्याउँछन्,\nअनि झूटो कुराहरू मुखबाट काँड झैं निकाल्छन्।\nझूटो मात्र, सत्य हैन।\nत्यस ठाँउमा सत्य दृढ भई बढेको होइन।\nयी मानिसहरू एउटा खराब कर्म देखि अर्कोमा आफै सर्छन।\nतिनीहरूले मलाई चिन्दैनन्।”\nपरमप्रभुले यसो भन्नुभयो।",
			"4": "<sup>4</sup> “आफ्ना छिमेकीहरूलाई हेर।\nकुनै आफन्तहरूलाई विश्वास नगर।\nकिनभने हरेक आफन्तहरू कपटी छ।\nहरेक छिमेकीले तिम्रो पछाडी निन्दा गर्दछ।",
			"5": "<sup>5</sup> हरेक मानिसले आफ्नो छिमेकीसित झूटा बोल्छ।\nकसैले पनि साँच्चो बोल्दैन।\nयहूदाका मानिसहरूले आफ्नो जिब्रोलाई\nझूटो बोल्न प्रशिक्षण गराउँछन्।\nतिनीहरूले नथाकुन्जेल सम्म पाप गरे।",
			"6": "<sup>6</sup> एउटा नराम्रो कुराले अर्को नराम्रो कुरालाई पछ्‌यायो।\nअनि झूटोले झूटै पछ्‌यायो।\nमानिसहरूले मलाई चिन्नु अस्वीकार गरे।”\nपरमप्रभुले ती कुराहरू भन्नुभयो।",
			"7": "<sup>7</sup> सेनाहरूका परमप्रभु भन्नहुन्छ,\n“म तिनीहरूलाई शुद्ध पार्छु र परीक्षा गर्छु।\nमेरोमा अन्य तरिका छैन।\nमेरा मानिसहरूले पाप गरे।",
			"8": "<sup>8</sup> यहूदाका मानिसहरूको जिब्रो काँड जस्तै तीखा छन्।\nतिनीहरू मुखले झूटो बोल्दछन्।\nहरेक मानिस आफ्ना छिमेकीहरूसँग दयापूर्ण बात गर्छन्,\nतर तिनीहरूले आफ्नो छिमेकीहरूलाई धरापमा पार्ने भित्री योजनाहरू बनाँउछन्।",
			"9": "<sup>9</sup> मैले यहूदाका मानिसहरूलाई दण्ड दिनै पर्छ।”\nयो परमेश्वरबाट आएको सन्देश हो,\n“के मैले यसप्रकारको जातिलाई\nसजाय दिनु पर्दैन?”",
			"10": "<sup>10</sup> म पर्वतहरूलाई चिच्याएर कराउनेछु।\nम उजाड भूमिका निम्ति शोकमय गीत गाउनेछु\nकारण ती पर्वतहरू र मैदानहरू नष्ट गराइएका छन्,\nर त्यहाँबाट कोही पनि हिँड्दैनन्।\nगाई-गोरूहरू रमेको आवाजहरू अब त्यहाँ सुन्न सकिँदैन।\nचराचुरूङ्गी सबै उडेर गइसके,\nअनि पशुहरू पनि गइसके।",
			"11": "<sup>11</sup> परमप्रभु भन्नुहुन्छ, “म यरूशलेमलाई अशुद्धको थुप्रोमा परिवर्तन गर्नेछु।\nयो स्यालहरूको घर हुनेछ।\nम यहूदाका शहरहरू विनाश गर्नेछु\nताकि त्यहाँ कोही बस्ने छैन।”",
			"12": "<sup>12</sup> के त्यहाँ यी चीजहरू बुझ्ने कोही ज्ञानी मानिसहरू छन्?\nत्यहाँ कोही छन् ज-जसलाई परमप्रभुले भन्नु भएको छ\nर ज-जसले सन्देश व्याख्या गर्नु सक्छ?\nकिन यस भूमि नष्ट पारियो अनि\nसूख्खा मरुभूमिमा परिणत गरियो, जहाँ कोही पनि जाँदैन्?",
			"13": "<sup>13</sup> परमप्रभुले भन्नुभयो,\n“किनभने मैले तिनीहरूलाई दिएको शिक्षा त्यागे्\nअनि मेरा उपदेशहरू सुन्दैनन् अनि मैले देखाएका मार्गमा हिँड्दैनन्।",
			"14": "<sup>14</sup> यहूदाका मानिसहरूले आफ्नै तरिकाको जीवन शैली लिए।\nतिनीहरू हठ्ठी थिए।\nतिनीहरूले तिनीहरूका पिता-पुर्खाहरूले\nजो सिकाएका थिए त्यही बाललाई पूजा गरे।”",
			"15": "<sup>15</sup> यसकारण सेनाहरूका परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ,\n“म तिनीहरूलाई तीतो भोजन खाने\nर विषालु पानी पिउने बनाउनेछु।",
			"16": "<sup>16</sup> म इस्राएलका मानिसहरूलाई अन्य जातिहरूमा\nजुन न त तिनीहरू न त तिनीहरूको पिता-पुर्खाहरूले\nजान्दथे तितर-बितर पारिदिनेछु।\nतिनीहरू सम्पूर्ण रूपले नष्ट नहुन्जेलसम्म,\nम तिनीहरूको पछि तरवार पठाउनेछु।”",
			"17": "<sup>17</sup> यही नै सेनाहरूका परमेश्वर भन्नुहुन्छ\n“अब, यी कुराहरूबारे विचार गर।\nती स्त्रीहरूलाई बोलाऊ जसलाई अन्त्येष्टि क्रियामा रूनलाई पैसा दिइन्छ।\nती मानिसहरूलाई पठाऊ जो त्यस् काममा निपुण छन्।",
			"18": "<sup>18</sup> ‘तिनीहरूले हतार गरून\nर हाम्रा निम्ति रोइदिउन।\nतब हाम्रा आँखाहरू\nआँसूले भरिनेछन्,\nअनि पानीका मुहानहरू बाहिर आउनेछन्।’",
			"19": "<sup>19</sup> “विलापको स्वरहरू सियोनबाट सुनियो,\n‘हामी साँचि नै नष्ट भयौं।\nहामी लज्जित छौं\nकि हामीले आफ्नो भूमि त्याग्नु नै पर्छ,\nकिनभने हाम्रो घरहरू भत्काइयो।\nअहिले हाम्रा घरहरू ढुङ्गाका टुक्राहरू मात्र भएका छन्।’”",
			"20": "<sup>20</sup> अब, यहूदाका स्त्रीहरू, परमप्रभुबाट आएको वचन ध्यान् दिएर सुन।\nपरमप्रभुको मुखारबिन्दबाट आएको शब्दहरू सुन।\nपरमप्रभु भन्नुहुन्छ, “तिमीहरूका छोरीहरूलाई कसरी चिच्चाएर रूने भन्ने सिकाऊ।\nहरेक स्त्रीले यो शोक-गीत गाउन सिक्नै पर्छ:",
			"21": "<sup>21</sup> ‘हाम्रा झ्यालहरूबाट मृत्यु पसेको छ।\nहाम्रा किल्लाहरूमा मृत्यु आयो।\nयसले गल्लीहरूमा हाम्रा नानीहरूलाई\nर हाम्रा जवानहरूलाई शहरहरूबाट लग्यो।’”",
			"22": "<sup>22</sup> यी कुराहरूको घोषणा गर:\n“परमप्रभु भन्नुहुन्छ, ‘मतृ शरीरहरू भूईँमा गोबर जस्तै छरिएको हुनेछ।\nतिनीहरूका शरीरहरू जसरी कृषकहरूले खेतमा बाली काटेर सुताई राख्छन्\nजसलाई उठाउने कोही हुँदैनन् त्यस्तै मैदानमा लडिरहेका हुनेछन्।’”",
			"23": "<sup>23</sup> परमप्रभु भन्नुहुन्छ,\n“ज्ञानीले आफ्ना ज्ञान बारे शेखी गर्नु हुँदैन्।\nबलवानले आफ्ना शक्ति बारे घमण्ड गर्नु हुँदैन्।\nधनीले आफ्ना धन बारे अहम् गर्नु हुँदैन्।",
			"24": "<sup>24</sup> तर यदि कसैले गर्व गर्छ भने, उसले यस विषयमा गर्व गरोस्।\nम परमप्रभुलाई चिन्छु र जान्दछु\nर उहाँ नै परमप्रभु हुनुहुन्छ,\nजसले पृथ्वीमा न्याय, दया\nर र्धमिकताको व्यवहार गर्नुहुन्छ।\nकिनभने म यी कुराहरूमा नै आनन्द लिन्छु।”",
			"25": "<sup>25</sup> यो परमेश्वरबाट आएको सन्देश हो: “समय आइरहेको छ जब म ती सम्पूर्ण मानिसहरू जसले खाली शारीरिक खतना गरेका छन् उनीहरूलाई दण्ड दिनेछु।",
			"26": "<sup>26</sup> म ती मानिसहरूका बारेमा भनिरहेछु जो मिश्र देश, यहूदा, एदोम, अम्मोन, मोआबका बसिन्दा हुन् अनि तिनीहरू सबै जो जङ्गलमा बस्छन्। ती सारा देशहरूमा बस्ने मानिसहरू वास्तवमा खतना गरिएको थिएन अनि इस्राएलबाट आउने मानिसहरूका खतना तिमीहरूको हृदयमा गरिएको थिएन।”"
		},
		"10": {
			"1": "<sup>1</sup> परमप्रभु र मूर्तिहरू\nहे इस्राएलका मानिसहरू, परमप्रभुको वचन सुन।",
			"2": "<sup>2</sup> परमप्रभुले यही भन्नुहुन्छ:\n“अन्य जातिहरूबाट आएका मानिसहरूको जस्तो जीवन नजिओ।\nआकाशका विशेष चिन्हहरूसित नडराऊ।\nअन्य जातिका मानिसहरूले अकाशमा जे देख्छन् तिनीहरू डराउँछन्।\nतर तिमीहरू त्यस्ता कुराहरूमा डराउनु पर्दैन।",
			"3": "<sup>3</sup> अन्य मानिसहरूका रीति-रिवाजहरू महत्वहीन छन्।\nतिनीहरू जङ्गलमा रूख काट्छन्\nशिल्पीकारले आफ्नो बञ्चरोले आकार बनाउँछ।",
			"4": "<sup>4</sup> तिनीहरूले चाँदी र सुन लगाएर प्रतिमाहरू सुन्दर बनाउँछन्।\nतब तिनीहरूले मार्तोलहरू र काँटीहरू मूर्तिहरू अड्याउनलाई प्रयोग गर्छन्,\nताकि ती मूर्तिहरू तल नझरोस्।",
			"5": "<sup>5</sup> मूर्तिहरू खेतमा राखेको तर्साउने पुतला जस्तै हुन्।\nतिनीहरू बोल्दैनन्।\nतिनीहरूलाई बोक्नु पर्छ,\nकिनभने तिनीहरू हिंड्न सक्तैनन्।\nतिनीहरूलाई आदर नगर!\nतिनीहरूले नोक्सान\nअथवा सहायता गर्नु सक्तैन।”",
			"6": "<sup>6</sup> हे परमप्रभु, तपाईं जस्तो कोही पनि छैन!\nतपाईं महान हुनुहुन्छ,\nअनि तपाईंको नाउँ महान् र शक्तिशाली छ।",
			"7": "<sup>7</sup> हे परमेश्वर प्रत्येकले तपाईंलाई आदर गर्नै पर्छ।\nतपाईं सबै जातिहरूका महाराजा हुनुहुन्छ।\nतपाईं सम्मानको योग्य हुनुहुन्छ।\nअन्य राज्यहरूमा धेरै ज्ञानी मानिसहरू छन्,\nतर तपाईं समान कोही पनि ज्ञानी छैन।",
			"8": "<sup>8</sup> अन्य जातिका मानिसहरू लठुवा र मूर्ख छन्।\nतिनीहरूका शिक्षा काठको महत्वहीन मूर्तिहरूबाट आउँछन्।",
			"9": "<sup>9</sup> तिनीहरूले मूर्तिहरू बनाउनु तर्शीशबाट चाँदी\nअनि उपज देशबाट सुन ल्याउँछन्।\nमूर्तिकार र सुनारहरूले मूर्तिहरू बनाउँछन्\nर नीलो र बैंजनी लुगाहरू लगाइदिन्छन्।\nनिपुण “शिल्पीहरूले” यी सब “चीजहरू” बनाँउछन्।",
			"10": "<sup>10</sup> तर परमप्रभु चाँहि साँच्चो परमेश्वर हुनुहुन्छ।\nउहाँ मात्र जीवित परमेश्वर हुनुहुन्छ।\nउहाँ राजा हुनुहुन्छ, जसले सदाकाल शासन चलाउनु हुन्छ।\nपरमेश्वरको रीसमा पृथ्वी काम्छ,\nअनि विदेशीहरूले उहाँको क्रोध सहन सक्तैनन्।",
			"11": "<sup>11</sup> परमप्रभु भन्नुहुन्छ, “यो सन्देश मानिसहरूमा घोषित गर,\n‘यी झूटा देवताहरूले आकाश र पृथ्वी बनाएनन्।\nपृथ्वी र आकाश मुनि तिनीहरू लोप हुन्छन्।’”",
			"12": "<sup>12</sup> परमेश्वर नै हुनुहुन्छ, जसले पृथ्वीलाई आफ्नो शक्तिले सृष्टि गर्नुभयो।\nपरमेश्वरले आफ्नो ज्ञानले संसारलाई रच्नु भयो\nअनि उहाँले आफ्नो समझद्वारा आकाशलाई फैलाउनुभयो।",
			"13": "<sup>13</sup> उहाँको वचनको आवाजले\nआकाशमा पानी गर्जन्छ।\nपृथ्वीको अन्तबाट उहाँले बादल आउने बनाउनु हुन्छ।\nउहाँले बर्षा सित बिजुली ल्याउनु हुन्छ।\nउहाँले आफ्नो भण्डारबाट बतास ल्याउनु भयो।",
			"14": "<sup>14</sup> मानिसहरू एकदमै मूर्ख छन्।\nमूर्तिहरूले आफ्ना सृष्टिाहरूलाई मूर्ख बनाए।\nती मूर्तिहरू वास्तवमा मिथ्या बाहेक केही होइन।\nतिनीहरूमा जीवन हुँदैन।",
			"15": "<sup>15</sup> ती मूर्तिहरू महत्वहीन हुन्।\nतिनीहरू केवल खिसीका पात्र हुन्।\nन्यायको समयमा ती मूर्तिहरू सड्नेछन्।",
			"16": "<sup>16</sup> तर याकूबका परमप्रभु ती मूर्तिहरू जस्ता हुनुहुन्न।\nपरमप्रभुले सबै थोक बनाउनु भयो\nअनि परमप्रभुले इस्राएल परिवारलाई,\nपरमप्रभुले उहाँको आफ्नै मानिसहरू भनी रोज्नु भयो।\nपरमेश्वरको नाउँ “सेनाहरूका परमप्रभु” हो।",
			"17": "<sup>17</sup> विनाश आइरहेछ\nघेरामा परेका तिमी मानिसहरू,\nप्रत्येक चीज पोको पार\nअनि जानलाई तत्पर हौऊ।",
			"18": "<sup>18</sup> परमप्रभु भन्नुहुन्छ\n“यस समय म यहूदाका मानिसहरूलाई देशबाट बाहिर निकाली दिनेछु।\nम तिनीहरूमाथि सङ्कटहरू ल्याइदिनेछु\nताकि तिनीहरूलाई पक्राइनेछन्।”",
			"19": "<sup>19</sup> धिक्कार छ मलाई! मैले नराम्रो घतले चोट पाएँ।\nम साह्रै घाइते छु\nयद्यपि मैले स्वयंलाई भने, “यो मेरो बीमार हो,\nजसको लागि म पीडित हुनै पर्छ।”",
			"20": "<sup>20</sup> मेरो पाल भत्काइयो।\nसारा डोरीहरू चुँडाइयो।\nमेरा छोरा-छोरीहरूले मलाई त्यागे।\nतिनीहरू गए।\nमेरो पाल फैलाउन\nअथवा पालको पर्दा उठाउन यहाँ केही पनि छैन।",
			"21": "<sup>21</sup> गोठालाहरू मूर्ख छन्,\nअनि परमप्रभुलाई खोज्ने प्रयास गर्दैनन्।\nयसकारण तिनीहरू सफल भएनन्,\nयसकारण तिनीहरूका बथानहरू तितर-बितर भए अनि हराए।",
			"22": "<sup>22</sup> सुन्, एउटा ठूलो हल्ला सुन!\nहेर त्यो हल्ला उत्तरदेखि आइरहेछ।\nयसले यहूदाका शहरहरू नष्ट पार्नेछ।\nयहूदा एक रित्तो मरुभूमि झैं हुनेछ।\nयो स्यालहरूका गुफा हुनेछ।",
			"23": "<sup>23</sup> हे परमप्रभु! म जान्दछु कि मानिसले आफ्नो जीवन नियन्त्रण गर्दैन।\nएउटा मानिसले आफ्नै पाइला ठीकसित राख्न सक्तैन।",
			"24": "<sup>24</sup> हे परमप्रभु! निष्पक्ष भएर,\nहामीलाई सुधार्नुहोस्।\nक्रोधमा हामीलाई सजाय नदिनुहोस्\nनत्र तपाईंले हामीलाई नाश पार्नु हुनेछ।",
			"25": "<sup>25</sup> तपाईंको क्रोध ती जातिहरूमाथि पोखाउनु होस् जसले तपाईंलाई चिन्दैनन्\nअनि ती मानिसहरूमाथि पोखाउनु होस्\nज-जसले तपाईंको नाउँ लिदैनन्।\nती जातिहरूले याकूबको परिवारलाई ध्वंस पारे।\nतिनीहरूले इस्राएललाई पूर्णरूपले ध्वंस पारे।\nतिनीहरूले इस्राएलको देश ध्वंस पारे।"
		},
		"11": {
			"1": "<sup>1</sup> करार तोडियो\nयर्मियाकहाँ आएको सन्देश यही हो। यो सन्देश परमप्रभुबाट आयो",
			"2": "<sup>2</sup> “यर्मिया, यस करारको वचनहरू सुन। यहूदाका मानिसहरूलाई यी कुराहरू बताऊ। यी कुराहरू यरूशलेमा बस्ने मानिसहरूलाई भन।",
			"3": "<sup>3</sup> यही त परमप्रभु इस्राएलका परमेश्वर, भन्नुहुन्छ ‘जसले यो करारलाई पालन गर्दैन् त्यो मानिसमाथि नराम्रो कुरो परी आउनेछ।",
			"4": "<sup>4</sup> म त्यो करार विषयमा कुरा गरिरहेछु जुन मैले तिनीहरूका पिता-पुर्खाहरूसँग गरेको थिएँ। त्यो करार मैले त्यसबेला गरेको थिएँ जब मैले तिनीहरूलाई मिश्रबाट ल्याएँ। मिश्र धेरै सङ्कटहरूको ठाउँ थियो। यो फलाम गलाउने भट्टी झैं थियो।’ मैले तिनीहरूलाई भने ‘मेरा आज्ञा पालन गर अनि मैले आदेश दिएको कुराहरू गर। यदि तिमीहरूले त्यसो गर्यौ भनें, तिमीहरू मेरो मानिसहरू हुनेछौ र म तिमीहरूको परमेश्वर हुनेछु।’",
			"5": "<sup>5</sup> “तब मैले तिमीहरूका पिता-पुर्खाहरूलाई मह र दूध बगिरहने मलिलो जमीन दिन्छु भनी गरेको प्रतिज्ञा सधैँ बचाई राख्नेछु अनि आज तिमीहरू त्यही जमीनमा छौ।”\nमैले उत्तर दिएँ, “आमेन, हे परमप्रभु।”",
			"6": "<sup>6</sup> परमप्रभुले मलाई भन्नुभयो, “यर्मिया, यस सन्देश यहूदाका शहरहरू र यरूशलेमको गल्ली-गल्लीहरूमा प्रचार गर। सन्देश यो हो यस करारका कुरा सुन र पालन गर।",
			"7": "<sup>7</sup> मैले मिश्रको भूमिबाट बाहिर ल्याएकै बेला तिमीहरूका पिता-पुर्खाहरूलाई चेतावनी दिएको थिएँ। त्यही दिन देखि नै मैले तिनीहरूलाई घरि-घरि चेताउनी दिएँ। मैले तिनीहरूलाई मेरो आज्ञा पालन गर भन्दै चेतावनी दिँदै आएको हो।",
			"8": "<sup>8</sup> तर तिनीहरू हठ्ठी थिए मेरो कुराहरूलाई कुनै महत्व नै दिएनन्। अनि तिनीहरूको दुष्ट मनले जे चितायो त्यही गरे। मैले तिनीहरूलाई करार-पत्रमा रहेका आज्ञाहरू पुरा गर्न आदेश दिएँ तर तिनीहरू मानेनन्, यसकारण त्यस करार-पत्रमा भनिएको सबै सजायहरू तिनीहरूलाई दिएँ।”",
			"9": "<sup>9</sup> फेरि परमप्रभुले मलाई भन्नुभयो, “यर्मिया, म जान्दछु यहूदाका र यरूशलेममा बस्ने मानिसहरूले एक गुप्त योजना बनाएका छन्।",
			"10": "<sup>10</sup> ती मानिसहरूले जुन तिनीहरूका पिता-पुर्खाहरूले गरेका थिए त्यही पापहरू नै दोहोर्याइरहेका छन्। तिमीहरूका पिता-पुर्खाहरूले मेरो आदेश अनुसार हिँड्न अस्वीकार गरे। तिनीहरूले अरू देवताहरू पूजे अनि तिनीहरूलाई सेवा गरे। इस्राएल अनि यहूदाका सन्ताहरूले करार भङ्ग गरे जुन मैले तिनीहरूका पिता-पुर्खाहरूसँग गरेको थिएँ।”",
			"11": "<sup>11</sup> यसकारण, परमप्रभु भन्नुहुन्छ “म यहूदाका मानिसहरूमाथि आपद ल्याइरहेको छु। तिनीहरू उम्कन पाउने छैनन् तिनीहरूले मलाई मद्दतको लागि गुहार्ने छन्, तर म तिनीहरूको पुकारमा ध्यान दिँदिन।",
			"12": "<sup>12</sup> यहूदाका मानिसहरू साथै यरूशलेमका मानिसहरू गई तिनीहरूका देवताहरूलाई गुहार्नेछन् जसलाई तिनीहरू धूप चढाउँछन्। तर ती देवताहरूले आवश्यक परेको बेलामा तिनीहरूलाई मद्दत पुर्याउन सक्षम हुने छैनन्।",
			"13": "<sup>13</sup> “हे यहूदाका मानिसहरू! तिमीहरूसँग जति शहरहरू छन त्यति नै मूर्तिहरू छन्। लज्जापूर्ण बाल देवताको पूजा गर्न यरूशलेम शहरमा जति गल्लीहरू छन् त्यति नै वेदीहरू छन्।",
			"14": "<sup>14</sup> “यर्मिया, यहूदाका मानिसहरूका निम्ति प्रार्थना नगर। तिनीहरूका निम्ति वकालत नगर। तिनीहरूले सङ्कटको समय जतिबेला सहायताका निम्ति प्रार्थना गर्छन् म ध्यान दिँदिन।",
			"15": "<sup>15</sup> “उसले धेरै डरलाग्दो कामहरू गरेको जान्दा जान्दै\nकिन मैले मेरी प्रियसीलाई मेरा घरमा आदर गर्नु?\nयहूदा, के प्रतिज्ञाहरू र पशु बलिहरूले मात्र\nतिमीहरूलाई विनाशबाट बचाउनु सक्छ भन्ने तिमी सोच्छौ?”",
			"16": "<sup>16</sup> परमप्रभुले तिमीलाई नाउँ दिनुभयो।\nउहाँले तिमीलाई राम्रो फल लागेको सुन्दर जैतूनको वृक्ष भनी बोलाउनु भयो।\nतर शक्तिशाली आँधी गजर्नसितै परमप्रभुले त्यस वृक्षलाई आगोमा जलाउनु हुनेछ\nअनि त्यसका हाँगाहरू जल्नेछ।",
			"17": "<sup>17</sup> सेनाहरूका परमप्रभुले तिमीलाई रोप्नु भयो\nअनि उहाँले भन्नुभयो कि ध्वंस आउनेछ\nकिनभने इस्राएल र यहूदाका मानिसहरूले\nबाललाई सुगन्धित धूपहरू चढाएर\nअनिष्ट कामहरू गरे जुन कामले\nमलाई क्रोधित गरायो।",
			"18": "<sup>18</sup> यर्मियाको विरूद्धमा षड्‌यन्त्र\nपरमप्रभुले मलाई देखाउनु भयो अनि मैले चाल पाएँ। उहाँले तिनीहरूका खराब कामहरू मलाई देखाउनु भयो।",
			"19": "<sup>19</sup> मार्नलाई ल्याइएको एउटा निर्दोष थुमाझैं म थिएँ। तिनीहरू मेरो विरूद्ध छन् भनी म जान्दिनथें। तिनीहरूले मेरो बारेमा यी कुराहरू भने: “यो रूख यसका फलहरूसितै नष्ट पारिदिऊँ। यसलाई मारिदिउँ ताकि मानिसहरूले यसलाई भुल्नेछन्।”",
			"20": "<sup>20</sup> “तर हे परमप्रभु! तपाईं एक निष्पक्ष न्यायकर्त्ता हुनुहुन्छ। तपाईं मानिसहरूका हृदय र इच्छाहरू जाँच गर्नु हुन्छ। तपाईंले तिनीहरूलाई दिने सजाय मलाई जान्न दिनुहोस् कारण मैले मेरो मामला तपाईंलाई सुम्पेको छु।”",
			"21": "<sup>21</sup> यसकारण अनातोतका मानिसहरूको विषयमा जसले यर्मियालाई मार्ने षड्‌यन्त्र रचिरहेका थिए। ती मानिसहरूले यर्मियालाई भने, “परमप्रभुको नाउँमा अगमवाणी नगर नत्र हामी तिमीलाई मार्नेछौं।” तिनीहरूका विषयमा परमप्रभु यसो भन्नुहुन्छ।",
			"22": "<sup>22</sup> सेनाहरूका परमप्रभुले भन्नुभयो, “म तिनीहरूलाई दण्ड दिनेछु। जवान मानिसहरू युद्धमा मर्नेछन्। तिनीहरूका छोरा-छोरीहरू भोकै मर्नेछन्।",
			"23": "<sup>23</sup> अनातोतबाट आएका मानिसहरू कसैलाई पनि छाडिनेछैन। कोही पनि बाँच्नेछैन। किनभने जब म तिनीहरूलाई दण्ड भोगाउँछु म तिनीहरूमाथि विपद खनाइदिनेछु।”"
		},
		"12": {
			"1": "<sup>1</sup> यर्मियाले परमप्रभुप्रति गुनासो गर्दछ\nहे परमप्रभु! यदि मैले तपाईंसँग वकालत गरें भने,\nतपाईं सधैँ धार्मिक नै हुनुहुन्छ।\nतापनि मलाई केही भन्न दिनुहोस् जुन सही देखिँदैन।\nकिन दुष्ट मानिसहरू सफल हुन्छन्?\nकिन दुष्ट मानिसहरू सम्पन्न जीवन जिउँछन्?",
			"2": "<sup>2</sup> तपाईंले तिनीहरूलाई रोप्नु भयो\nअनि तिनीहरू शक्तिशाली भए।\nतिनीहरू बढछन् अनि फल दिन्छन्।\nतपाईं तिनीहरूका सम्मुख हुनुहुन्छ तर हृदयदेखि भने टाढा।",
			"3": "<sup>3</sup> तर हे परमप्रभु तपाईं मेरो हृदयलाई चिन्नु हुन्छ।\nतपाईंले मलाई देख्नु भएको छ अनि मेरो मनलाई जाँच्नु भएको छ।\nतिनीहरूलाई थुमाहरूलाई कसाइले अघि ताने झैं तान्नुहोस्।\nहत्या गरिने दिनको लागि तिनीहरूलाई अलग राख्नु होस्।",
			"4": "<sup>4</sup> कहिले सम्म जमीन सूख्खा रहन्छ?\nकहिले सम्म घाँस ओइलिन्छ?\nत्यहाँ बस्ने मानिसहरूको दुष्टयाईले गर्दा\nसब चरा-चुरूङ्गीहरू अनि पशुहरू लोप भइसके।\nतैपनि ती दुष्ट मानिसहरू भनिरहेका छन्,\n“यर्मियाले हामीमाथि के घट्छ देख्ने छैन।”",
			"5": "<sup>5</sup> यर्मियालाई परमप्रभुको जवाफ\n“यर्मिया, यदि तिमी मानिसहरूसँग दुगुर्दा थाक्यौ भने\nकसरी घोडाहरूसित दगुर्न सक्छौ?\nयदि तिमीले शान्तिमय ठाउँमा भरोसा गर्यो भने\nडरलाग्दो जग्गामा तिमी के गर्नेछौ?\nयर्दन नदीको किनारमा बढ़दै गरेको काँढे झाडीहरूमा\nतिमी के गर्ने छौ?",
			"6": "<sup>6</sup> ती मानिसहरू तिम्रा आफ्नै दाज्यू-भाइहरू हुन्।\nतिम्रा आफ्नै परिवारका सदस्यहरूले\nतिम्रै विरूद्धमा षड्‌यन्त्र रचिरहेकाछन्।\nतिनीहरू तिम्रो विरूद्धमा जोरसित कराउँछन्।\nतिनीहरूले मीठा कुराहरू गरे पनि तिनीहरूमाथि भरोसा नगर।",
			"7": "<sup>7</sup> परमप्रभुले यहूदीहरूलाई इन्कार गर्नुहुन्छ,\n“मैले आफ्नो घर परिवार त्यागेको छु,\nमैले आफ्नो सम्पत्ति छाडें।\nम जसलाई प्रेम गर्दछु्\nउसैलाई उसको शत्रुहरूलाई सुम्पें।",
			"8": "<sup>8</sup> मेरा मानिसहरू सिंह झैं छन्।\nतिनीहरू म पट्टि फर्की गर्जिए,\nयसकारण म तिनीहरूको शत्रु भएको छु।",
			"9": "<sup>9</sup> मेरा मानिसहरू मरेका पशुहरू जस्तै छन्।\nगिद्धहरू तिनीहरूको वरिपरि उडछन्।\nआऊ हे जङ्गली पशुहरू हो,\nखान भेला होऊ।",
			"10": "<sup>10</sup> घेरै गोठालाहरूले मेरो दाखबारीहरू नष्ट पारिदिए।\nती गोठालाहरू मेरो खेतका विरूवामाथि जथाभावी हिँडे।\nती गोठालाहरूले मेरो सुन्दर खेत बारी रित्तो मरुभूमिमा परिणत गरिदिए।",
			"11": "<sup>11</sup> तिनीहरूले मेरो खेत मरुभूमि बनाइदिए।\nयो सूख्खा र अनुर्बर छ र मेरो लागि रून्छ।\nसम्पूर्ण भूमि नै उजाड भएकोछ,\nतर कसैले वास्ता गर्दैन।",
			"12": "<sup>12</sup> मरुभूमिको पहाडमाथि ध्वंशकहरू आउँछन्।\nयो भूमिको एक कुनादेखि अर्को कुना सम्म,\nपरमप्रभुको तरवारले सम्पूर्ण नष्ट पार्नेछ।\nकुनै जीवित वस्तु सुरक्षित रहने छैन।",
			"13": "<sup>13</sup> मानिसहरूले गहूँ रोप्ने छन्\nतर तिनीहरूले काँढाहरूको बाली काट्नेछन्।\nतिनीहरू अधिक मेहनत गर्छन्\nतर त्यसबाट केही पाउँदैनन्।\nआफ्नो परिश्रमको फलमा तिनीहरू आफै लज्जित अनुभव गर्नेछन।\nपरमप्रभुको क्रोधले यी सब चीजहरू हुनेछ।”",
			"14": "<sup>14</sup> इस्राएलका छिमेकीहरूलाई परमप्रभुको प्रतिज्ञा\nयसकारण परमप्रभु भन्नुहुन्छ: “मैले मेरा इस्राएली मानिसहरूलाई दिएको भूमि नजिक बसो-बास गर्ने दुष्ट मानिसहरूलाई के गर्नेछु म तिमीलाई भन्नेछु। म ती मानिसहरूलाई तिनीहरूको ठाँउबाट उखेल्नेछु अनि तिनीहरूबाट यहूदालाई छुट्याउँनेछु।",
			"15": "<sup>15</sup> जब ती मानिसहरूलाई देश निकालाको समय पुरा हुन्छ,त्यस पछि तिमीहरूमाथि म दया गर्ने छु। म प्रत्येक मानिसलाई उसको आफ्नै पैतृक भूमिमा ल्याउनेछु।",
			"16": "<sup>16</sup> यदि ती मानिसहरूले मेरा मानिसहरूलाई बालको नाउँमा शपथ गर्न सिकाए झैं मेरो नाउँमा ‘परमप्रभु जीवित हुनुहुन्छ’ भन्न साँचो रूपमा सिके भने म तिनीहरूलाई मेरा मानिसहरूको बीचमा स्थापित गराउँछु।",
			"17": "<sup>17</sup> यदि कुनै राज्यले मेरो आज्ञा पालन गरेन भने त्यस जातिलाई पूर्ण रूपले नष्ट पार्नेछु र यसलाई सुकेको बिरूवा झैं बनाउनेंछु।” यो सन्देश परमप्रभुबाट आएको हो।"
		},
		"13": {
			"1": "<sup>1</sup> पटुकाको संकेत\nपरमप्रभुले मलाई जे भन्नुभयो त्यो यही हो: “यर्मिया, जाऊ अनि सुतीको लुगा किनेर। तिम्रो कम्मरमा वरिपरि बाँध। यसलाई भिज्न नदेऊ।”",
			"2": "<sup>2</sup> यसकारण परमप्रभुको आज्ञा अनुसार मैले सुती लुगा ल्याएँ अनि कम्मरमा वरिपरि बाँधे।",
			"3": "<sup>3</sup> त्यसपछि दोस्रो पल्ट परमप्रभुको सन्देश मकहाँ आयो।",
			"4": "<sup>4</sup> सन्देश यस्तो थियो “यर्मिया, तिमीले किनेका पटुका बाँध र परात नदीमा जाऊ। अनि त्यो पटुका चट्टानहरू चर्केको ठाउँमा लुकाऊ।”",
			"5": "<sup>5</sup> यसकारण म परात नदीतर्फ लागें परमप्रभुले दिनुभएको आदेश अनुसार पटुका लुकाँए।",
			"6": "<sup>6</sup> धेरै दिन पछि, परमप्रभुले मलाई भन्नुभयो, “अब यर्मिया, परात नदीतर्फ जाऊ। पटुका ल्याऊ जुन मैल तिमीलाई त्याहाँ लुकाउनु भनेको थिएँ।”",
			"7": "<sup>7</sup> यसकारण म परात नदीमा गएँ अनि जहाँ मैले पटुका लुकाएको थिएँ त्यो ठाँउमा खनें। मैले पटुका पाएँ तर त्यो मक्किएर काम नलाग्ने भएको थियो।",
			"8": "<sup>8</sup> त्यसपछि परमप्रभुको सन्देश मकहाँ आयो।",
			"9": "<sup>9</sup> परमप्रभुले यसो भन्नुभयो, “त्यसरी नै म यहूदाको गर्व र यरूशलेमको महागर्व नष्ट पार्नेछु।",
			"10": "<sup>10</sup> यी दुष्ट मानिसहरूले मेरा वचनहरू सुन्न अस्वीकार गर्छन्। तिनीहरू हठ्ठी छन्। तिनीहरू अन्य देवताहरूको अनुशरण गर्छन् र तिनीहरूको पूजा गर्छन्। ती मानिसहरू पटुका झैं नष्ट हुनेछन्।",
			"11": "<sup>11</sup> एकजना मानिसले आफ्नो कम्मरमा पटुका कस्सेर बाँध्छ। त्यस्तै प्रकारले, म यहूदाका र इस्राएलका मानिसहरूलाई कस्सेर बाँध्नेछु।” यो सन्देश परमप्रभुबाट हो। “मैले त्यसो गरेको कारण ती मानिसहरूलाई म पट्टि खिंच्न र आफ्नो नाउँ प्रशंसा र महिमाका निम्ति आफ्नो मानिसहरू बनाउनु हो तर तिनीहरूले सुनेन्।”",
			"12": "<sup>12</sup> यहूदालाई चेताउनी\n“तिनीहरूलाई यो सन्देश भन: ‘परमप्रभु इस्राएलका परमेश्वरले भन्नु भएको छ हरेक दाख-मशक दाखरसले भर्नु पर्छ।’ ती मानिसहरूले तिमीलाई भन्छन्, ‘वास्तवमा हामी जान्दछौं हरेक दाख-मशक दाखरसले भर्नु पर्छ।’",
			"13": "<sup>13</sup> तब तिमीहरू तिनीहरूलाई भन्नेछौ, ‘यही नै त परमप्रभुले भन्नुहुन्छ यस देशमा भएका सबै असाहयहरूलाई म जँड्याह जस्तो बनाउने छु। म ती राजाहरूको बारेमा भनिरहेछु जो दाऊदको राजगद्दीमा बस्छन्। म ती पूजाहारीहरू, अगमवक्ताहरू र यरूशलेममा बस्नेहरू सबै मानिसहरूका बारेमा भनिरहेको छु।",
			"14": "<sup>14</sup> तिनीहरूलाई एक-अर्कामा चकनाचूर पार्नेछु, बाबु छोराहरूमा पनि आपसमा चक्‌नाचुर हुनेछन्’ परमप्रभु भन्नुहुन्छ ‘तिनीहरूका निम्ति म दु:खी हुने छैन अनि म तिनीहरूमाथि क्षमा गर्ने छैन। यहूदाका मानिसहरूलाई नष्ट पार्नु म रोकिने छैन्।’”",
			"15": "<sup>15</sup> सुन र ध्यान देऊ।\nपरमप्रभु तिमीहरूसित बोल्नु भयो।\nअहंकारी नहोऊ।",
			"16": "<sup>16</sup> परमेश्वर आफ्ना परमप्रभुलाई सम्मान् गर।\nउहाँलाई प्रशंसा गर नत्रता उहाँले अन्धकार ल्याउँनु हुनेछ।\nतिमीहरू अन्धकारको पहाडहरूमा झर्न अगि प्रशंसा गर,\nयहूदाका मानिसहरू तिमीहरूले उज्यालोको आशा गरिरहेछौ।\nतर परमप्रभुले डरलाग्दो अन्धकार ल्याउँनु हुनेछ।\nउहँले उज्यालोलाई बद्लेर बाक्लो अन्धकार बनाउनु हुनेछ।",
			"17": "<sup>17</sup> यहूदाका मानिसहरू यदि तिमीहरूले परमप्रभुको कुरालाई ध्यान दिएनौ भने।\nम लुक्छु अनि रून्छु।\nतिम्रो गर्वले मलाई रूने बनाउँछ।\nम धेरै जोडले रूनेछु।\nमेरो आँखाबाट आँसु बाढी झैं बग्नेछ?\nकिनभने परमेश्वरको भेंडाको बथान  पक्राऊ पर्नेछ।",
			"18": "<sup>18</sup> यी कुराहरू राजा र राज-मातालाई भन,\n“राजगद्दीबाट तल झर तिमीहरूको गौरवशाली मुकुट,\nतिमीहरूको शिरबाट खसेको छ।”",
			"19": "<sup>19</sup> नेगब मरुभूमिमा भएको शहरहरूका प्रवेशद्वारहरू बन्द छन्,\nअनि कसैले पनि तिनीहरूलाई खोल्न सक्तैनन्।\nयहूदाका सम्पूर्ण मानिसहरूलाई निर्वासन गरियो।\nतिनीहरूलाई कैदी जस्तै लगियो।",
			"20": "<sup>20</sup> हे यरूशलेम हेर! ,\nशत्रुहरू उत्तरबाट आइरहेकाछन्।\nपरमप्रभुले दिनुभएको सुन्दर बथान,\nतिम्रो ती भेंडा़का बथानहरू कहाँ छन्?",
			"21": "<sup>21</sup> यदि बथानको विषयमा परमप्रभुले सोध्नु भए\nतिमी के भन्नेछौ?\nतिमीहरूले सिकाउनु पर्थ्यो।\nतिमीहरूले अगुवाई गरिरहेको हुनु पर्ने।\nके तिमी सुत्केरी पीडामा परेकी स्त्रीहरू झैं छैनौ?",
			"22": "<sup>22</sup> यदि तिमीहरू स्वयंलाई सोधे,\n“किन मलाई यस्तो नराम्रो कुराहरू भयो?”\nती कुराहरू\nतिमीहरूकै पापको कारणले,\nतिम्रो पोशाकहरू च्यातियो\nअनि तिमीहरू अपमानित भयौ।",
			"23": "<sup>23</sup> जसरी एउटा चितुवाले आफ्नो छालाको थोप्ले दागहरू बद्लाउनु सक्तैन\nत्यसरी नै एकजना कालो वर्णको मानिसले आफ्नो वर्ण बद्लिन सक्तैन।\nयसर्थ तिमी दुष्टकर्मीहरू\nकहिल्यै परिवर्तन हुन र असल गर्न सक्तैनौ।",
			"24": "<sup>24</sup> “मरुभूमिको हावाले बालुवा उढाए झैं\nम तिनीहरूलाई तितर-बितर पारिदिनेछु।",
			"25": "<sup>25</sup> मेरो योजनाभित्र गाभिएको\nयो तिम्रो भाग्य हो।”\n“यस्तो संयोग किन घट्यो?\nकिनभने तिमीहरूले मलाई त्याग्यौ\nअनि झूटो देवताहरूमाथि भरोसा गर्यौ।\nयो सब परमप्रभुले भन्नु भयो।",
			"26": "<sup>26</sup> यरूशलेम, म तिम्रो लुगा\nअनुहार सम्म तानी दिनेछु\nताकि प्रत्येकले तिम्रो लाज देखोस्।",
			"27": "<sup>27</sup> मैले तिमीहरूलाई घिनलाग्दा काम गरेको देखेको छु।\nमैले तिमीहरूलाई वेश्यावृति झैं पहाडमा,\nर मैदानमा आफ्नी प्रेमिकाहरूलाई,\nअति आनन्दसित व्यभिचार काममा मस्त भएको देखेको छु।”"
		},
		"14": {
			"1": "<sup>1</sup> खडेरी र झूटा अगमवक्ताहरू\nखडेरीको बारेमा यर्मियाकहाँ आएको परमप्रभुको वचन यही हो:",
			"2": "<sup>2</sup> “यहूदा शोक गर्छ,\nउसको प्रवेशद्वारहरू कमजोर भएको छन्।\nमानिसहरू भूइँमा लडेर शोक मनाउँछन्,\nअनि यरूशलेमको चित्कार आकासिन्छ।",
			"3": "<sup>3</sup> मानिसहरूका नायकहरूले आफ्ना नोकरहरूलाई पानी लिनुलाई पठाए।\nनोकरहरू इनारमा गए\nतर तिनीहरूले पानी पाएनन्।\nनोकरहरू रित्तो जगहरू लिएर फर्की आए।\nयसकरण तिनीहरू लज्जित अनि अपमानित भए।\nतिनीहरूले लाजले आफ्नो अनुहार लुकाए।",
			"4": "<sup>4</sup> खडेरी लागेको कारण,\nकिसानहरूले खेतमा हलो जोतेनन्।\nयसर्थ तिनीहरूले लाजले आफ्नो मुख छोपे।",
			"5": "<sup>5</sup> माऊ मृगले सम्म आफूले भर्खरै जन्माएको पाठा-पाठीहरू छोड्छे।\nत्यहाँ घाँस नभएकोले त्यसले त्यसो गरकी हो।",
			"6": "<sup>6</sup> जंगली गधाहरूले स्यालहरूले झैं\nपहाडमा उभिएर हावा लिदैंछन्।\nचर्ने र्खकमा हरियो नभएको\nकारण तिनीहरूको आँखा तिर्मिराउँछन्।”",
			"7": "<sup>7</sup> “हे परमप्रभु! हाम्रो अधर्मले हामीलाई खेदे तापनि,\nआफ्नो नाउँको गौरवका निम्ति केही गर्नु होस्।\nहामी भन्छौ कि हामीले धेरै पल्ट तपाईंलाई त्यागेका हौं\nअनि तपाईंको विरूद्ध पापहरू गरेका छौं।",
			"8": "<sup>8</sup> हे परमेश्वर! तपाईं इस्राएलको आशा हुनुहुन्छ।\nसङ्कटको समयमा धेरै चोटी तपाईंले इस्राएललाई बचाउनु भएको छ।\nतर अहिले यस ठाउँमा तपाईं विदेशी झैं देखिनु हुन्छ।\nतपाईंलाई रात बिताउने यात्री झैं लाग्छ।",
			"9": "<sup>9</sup> किन तपाईं भ्रममा परेको मानिस झैं देखिनु हुन्छ?\nतपाईंले कसैलाई पनि बचाउँनु हातमा नरहेका सेना झैं हुनुहुन्छ।\nतर हे परमप्रभु, तपाईं हाम्रो साथमा हुनुहुन्छ।\nहामी तपाईंको नाउँले बोलाइन्छौं।\nहामीलाई असहाय नछोडनु होस्।”",
			"10": "<sup>10</sup> यसर्थ परमप्रभुले मानिसहरूलाई भन्नुहुन्छ। “तिनीहरूले मलाई त्याग्नु र टाढा जान मन पराउँछन्। मलाई त्याग्नु तिनीहरू आफैंलाई रोक्दैनन्। यही कारणले परमप्रभुले तिनीहरूलाई स्वीकार गर्नु भएन। होइन, परमप्रभुले तिनीहरूको दुष्टकर्म स्मरण गर्नुहुन्छ र तिनीहरूको पापले गर्दा सजाय दिनु हुनेछ।”",
			"11": "<sup>11</sup> तब परमप्रभुले मलाई भन्नुभयो, “यर्मिया, यी मानिसहरूको लागि मलाई प्रार्थना नगर।",
			"12": "<sup>12</sup> यहूदाका मानिसहरूले उपवास गर्न सक्लान अनि मलाई प्रार्थना गर्लान, तर म तिनीहरूका प्रार्थनाहरू सुन्ने छैन्। यदि तिनीहरूले होमबली र अन्नबलि चढाए तापनि म ती मानिसहरूलाई स्वीकार गर्ने छैन। म यहूदाका मानिसहरूलाई युद्धले ध्वंस गर्ने छु। म तिनीहरूका भोजन लगिदिने छु, अनि यहूदाका मानिसहरू भोको हुनेछन्। अनि म तिनीहरूलाई डरलाग्दो रोगहरूले विनाश गरी दिनेछु।”",
			"13": "<sup>13</sup> तर मैले परमप्रभुलाई भनें, “हे परमप्रभु मेरो मालिक! अगमवक्ताहरूले तिनीहरूलाई भन्छन्, ‘तिमीहरूलाई शत्रुहरूको तरवारले चोट लाग्ने छैन। तिमीहरू अनिकाल ग्रस्त हुनेछैनौ। परमप्रभुले तिमीहरूलाई यस भूमिमा शान्ति प्रदान गर्नु हुनेछ।’”",
			"14": "<sup>14</sup> तब परमप्रभुले मलाई भन्नुभयो, “यर्मिया! अगमवक्ताहरूले मेरो नाउँमा झूटो प्रचार गरिरहेका छन्। मैले ती अगमवक्ताहरूलाई पठाएको हैन। मैले तिनीहरूलाई न आदेश दिएँ न त तिनीहरूसित बात नै मारें। ती अगमवक्ताहरूले झूटो दर्शन, महत्वहीन मन्त्र-तन्त्र कुराको र तिनीहरूका आफ्नै भूल विचारहरू तिमीहरूलाई दिइरहेका छन्।",
			"15": "<sup>15</sup> यसकारण यस्तै अगमवक्ताहरूको बारेमा भन्छु कि तिनीहरूले मेरो नाउँमा प्रचार गरिरहेका छन्। मैले ती अगमवक्ताहरूलाई पठाएको होइन। ती अगमवक्ताहरूले भने, ‘कुनै शत्रुले पनि आफ्ना तरवारहरूसित यस राज्यमा कहिले आक्रमण गर्ने छैन। यस देशमा कोही पनि कहिल्यै भोको रहने छैन्।’ ती अगमवक्ताहरू भोकले मर्नेछन् अनि शत्रुको तरवारद्वारा मारिने छन्।",
			"16": "<sup>16</sup> जुन मानिसहरूसित अगमवक्ताहरू बोले तिनीहरूलाई यरूशलेमको गल्लीमा फ्याँकिनेछ। ती मानिसहरू भोक र तरवारद्वारा शेष हुनेछन। त्यहाँ तिनीहरूलाई दफन गर्नेहरू केही पनि हुने छैन, तिनीहरूका पत्नीहरू अथवा तिनीहरूका छोरा-छोरीहरू पनि हुनेछैनन्। म तिनीहरूलाई तिनीहरूका दुष्ट कर्महरूका निम्ति दण्ड दिनेछु।",
			"17": "<sup>17</sup> “तिनीहरूमा यी वचनहरू घोषणा गर:\n‘मेरा आँखाहरू आँसुले भरिएका छन्।\nरात दिन मेरा आँशु बगिरहन्छन्।\nम मेरा मानिसहरूको महा-ध्वंस,\nर तिनीहरूको गहिरो चोटहरूको लागि\nलगातार रात-दिन रोइरहेको छु।",
			"18": "<sup>18</sup> यदि म देशभित्र गएँ भने,\nम तरवारद्वारा मारिएका मानिसहरू देख्छु।\nयदि म शहरभित्र पसें भने,\nम बिरामीहरू जसको खाने कुराहरू छैन तिनीहरूलाई देख्छु।\nपूजाहारीहरू र अगमवक्ताहरू त्यही भूमिबाट जान्छन्,\nतर तिनीहरूले बुझ्दैनन्।’”",
			"19": "<sup>19</sup> हे परमप्रभु, के तपाईंले यहूदा राज्यलाई सम्पूर्णरूपले त्याग्नु भयो?\nहे परमेश्वर, के तपाईंले सियोनलाई घृणा गर्नुहुन्छ?\nतपाईंले यस्तो प्रकारले हामीलाई घात गर्नुभयो कि\nफेरि हामी ठीक हुन सक्तैनौं।\nहामी शान्तिको प्रतीक्षा गरिरहेका छौं,\nतर कुनै लाभ भएको छैन।\nहामी निको हुने समय पर्खिरहेका छौं,\nतर त्रास मात्र पो आउँछ।",
			"20": "<sup>20</sup> हे परमप्रभु, हामी जान्दछौ कि हामी दुष्ट मानिसहरू हौं।\nहामी जान्दछौ कि हाम्रा पिता-पुर्खाहरूले अधर्म कामहरू गरे।\nहो, हामीले तपाईंको विरूद्ध पाप गर्यौं।",
			"21": "<sup>21</sup> हे परमप्रभु, तपाईंको नाउँको लागि\nहामीलाई तिरस्कार नगर्नुहोस्।\nमहिमामय सिंहासनमाथि अपमान नल्याउनु होस्।\nहामीसित रहेको तपाईंको करार भङ्ग नपार्नुहोस्।",
			"22": "<sup>22</sup> विदेशी मूर्तिहरूसँग पानी पार्ने शक्ति छैन।\nआकाशले पानी बर्षाउने शक्ति पाउँदैन।\nहे परमप्रभु, हाम्रा परमेश्वर! तपाईं नै हाम्रो आशा हुनुहुन्छ।\nतपाईं मात्र एकजना हुनुहुन्छ जसले यी सब कर्महरू गर्नुसक्छ।"
		},
		"15": {
			"1": "<sup>1</sup> परमप्रभुले मलाई भन्नुभयो “यर्मिया, यदि यहाँ मोशा र शमूएलले मलाई प्रार्थना गरेको भए पनि म ती मानिसहरूको लागि दु:खी हुने थिइनँ। यहूदाका मानिसहरूलाई मदेखि टाढा पठाऊ। तिनीहरूलाई जाऊ भन।",
			"2": "<sup>2</sup> ती मानिसहरूले तिमीलाई सोध्न सक्छन्, ‘हामी कहाँ जाने?’ तब तिनीहरूलाई भन यसकारण परमप्रभु भन्नुहुन्छ:\n“‘मैले कसैलाई मर्नको लागी छानेको छु,\nती मर्नु पर्नेहरू मरून्,\nजसलाई मैले तरवारले मारिन छानेको छु,\nतिनीहरू तरवारले नै मारिउन,\nअनिकालमा पर्नु पर्नेहरू अनिकालमा नै परून्,\nअनि कैदी हुनु पर्नेहरूलाई कैद नै गरिनेछ।",
			"3": "<sup>3</sup> म चार प्रकारको बिनासकहरू तिनीहरूका विरूद्ध पठाउने छु।’\nयो परमेश्वरबाट आएको सन्देश हो।\n‘म तिनीहरूलाई मार्नु तरवारहरू लिएका शत्रुहरू पठाउनेछु।\nतिनीहरूलाई घिस्याउन कुकुरहरू पठाउनेछु।\nम तिनीहरूका शरीरहरू खाओस् र नाश गरोस भनेर\nआकाशका चराहरू र जानवरहरू पठाउनेछु।",
			"4": "<sup>4</sup> यहूदाका राजा हिजकियाहका छोरो\nमनश्शेले यरूशलेममा जे गरेको छ।\nत्यसको लागि पृथ्वीका मानिसहरूका निम्ति\nयहूदाका मानिसहरूलाई\nम एक डरलाग्दो उदाहरण बनाई प्रस्तुत गर्नेछु।’",
			"5": "<sup>5</sup> “यरूशलेमको शहरमा कुनै मानिस पनि तिम्रो लागि दु:खी हुने छैन।\nकुनै मानिस पनि तिम्रो निम्ति दु:खी साथै रूने छैन\nकुनै मानिसले पनि तिमीलाई कस्तो छौ भनेर सम्म सोध्ने छैन्।",
			"6": "<sup>6</sup> यरूशलेम, तिमीले मलाई त्याग्यौ।”\nयो सन्देश परमप्रभुबाट आएको छ।\n“तिमी जाऊ।\nयसकारण म तिमीलाई नष्ट पार्छु।\nतिमीलाई दया देखाउँदा देखाउँदा म थाकिसकें।",
			"7": "<sup>7</sup> म तिनीहरूलाई अलकत्रा सोर्ने काँटाले\nयस ठाउँको शहरका फाटकहरूमा\nतितर-बितर पारिदिनेछु।\nमेरा मानिसहरू परिवर्तन भएनन यसर्थ म तिनीहरूलाई ध्वंस पार्नेछु।\nम तिनीहरूका नानीहरू लैजानेछु।",
			"8": "<sup>8</sup> धेरै स्त्रीहरूले तिनीहरूका लोग्नेहरू हराउने छन्।\nत्यहाँ बेशी विधवाहरू हुनेछन्\nम समुद्रका बालुवाहरू भन्दा मध्यदिनको विनाश ल्याउने छु,\nत्यो विनाशकले यहूदाका जवान मानिसहरूका आमाहरूलाई आक्रमण गर्नेछ।\nम यहूदाका मानिसहरूमाथि पीडा र त्रास ल्याइदिने छु,\nम यस्तो घटना अतिशीघ्र नै घटाउनेछु।",
			"9": "<sup>9</sup> शत्रुले तरवारहरू सहित आक्रमण गर्ने छ,\nअनि मानिसहरूलाई मार्ने छ।\nतिनीहरूले यहूदाका बाँकी रहेकाहरूलाई मार्नेछ।\nसातजना छोराहरूकी आमा थकित हुन्छे अनि मर्छे।\nउसका निम्ति मध्यदिनमा सूर्य अस्ताउँछ।\nउसलाई अपमानित र लज्जित पारिनेछ।\nबाँकी रहेका मानिसहरू शत्रुहरूको अघि युद्धमा मारिनेछन्,”\nपरमप्रभु भन्नुहुन्छ।",
			"10": "<sup>10</sup> यर्मियाले फेरि परमप्रभुलाई गुनासो प्रकट गर्दछ\nहे मेरी आमा, मलाई तपाईंले\nजन्म दिनु भएको निम्ति म दु:खी छु।\nयस देशमा म त्यो मानिस हुँ\nजो दोष र घृणाको पात्र बनिएको छ,\nमैले कतैबाट ऋण लिएको अथवा दिएको छैन।\nतर प्रत्येक मानिसले मलाई सराप्छन्।",
			"11": "<sup>11</sup> परमप्रभुले भन्नुभयो,\n“मैले संकष्टको समयमा निश्चय, तिमीलाई बल दिएको छु।\nमैले तिम्रा निम्ति सङ्कटको समयमा शत्रुहरूसित मध्यस्थ गरेकोछु।”",
			"12": "<sup>12</sup> यर्मियालाई परमप्रभुले जवाफ दिनुहुन्छ\n“यर्मिया! उत्तरबाट कसैले पनि फलाम भाँच्नु सक्तैन\nन त कसैले काँसा नै टुक्रा-टुक्रा पार्नु सक्छ।",
			"13": "<sup>13</sup> तिम्रो देशमा तिमीले गरेको पापले गर्दा\nतिम्रो भण्डारको सम्पत्ति\nलूटको समान झैं गरी अरूलाई दिनेछु।",
			"14": "<sup>14</sup> हे यहूदाका मानिसहरू!\nम तिमीहरूलाई तिमीहरूकै शत्रुहरूको दास बनाउनेछु।\nतिमीहरू त्यस्तो भूमिमा दासहरू हुनेछौ\nजुन तिमीहरू कहिल्यै जानेका छैनौ।\nमेरो क्रोध तातो आगो जस्तो छ र तिमीहरू त्यसमा जल्ने छौ।”",
			"15": "<sup>15</sup> हे परमप्रभु! मलाई सम्झना गर्नुहोस्\nर मेरो हेरचाह गर्नुहोस्।\nमेरा निम्ति ती मानिसहरू विरूद्ध बद्ला लिनुहोस्\nज-जसले मेरो खेदो गर्छन्।\nतिनीहरूसित धैर्य-धारण नगर्नु होस्।\nजब तपाईं उनीहरूसित धर्य धारण गरिरहनु भएको छ भने\nमलाई नलानु होस्।\nहे परमप्रभु याद गर्नु होस् मैले तपाईंका निम्ति कति लाज बोक्नु पर्यो?",
			"16": "<sup>16</sup> तपाईंको सन्देश मकहाँ आयो।\nहे सेनाहरूका परमप्रभु\nतपाईंको सन्देशले मलाई अत्यन्त आनन्द तुल्यायो।\nतपाईंको नाउँमा मलाई बोलाइँदा म खुशी भएँ।",
			"17": "<sup>17</sup> जब तिनीहरू हाँसीरहेका अनि ठट्टा गरिरहेका थिए\nम कहिल्यै भीडमा बसिन।\nम एक्लै बसें कारण तपाईंको दैविक हात ममाथि थियो\nअनि तपाईंले म भित्र क्रोध भरिदिनु भएको थियो।",
			"18": "<sup>18</sup> किन मलाई कष्ट भइरहन्छ?\nमेरो घाउ गहिरो छ\nजुन निको हुनलाई गाह्रो मानिरहेको छ?\nमेरा निम्ति तपाईं सुकेको खोल्सा हुनुहुन्छ,\nएउटा खोल्सा जसमाथि भरोसा नै छैन।",
			"19": "<sup>19</sup> तब परमप्रभुले भन्नुभयो, “यर्मिया, यदि तिमी फर्कियौ भने,\nम तिमीलाई लिनेछु र तिमीले मेरो सेवा गर्नेछौ।\nयदि तिमीले महत्वपूर्ण कुराहरू गर्यौ,\nअनि महत्वहीन शब्दहरू बोलेनौ भने,\nतिमी मेरा प्रवक्ता हुन सक्छौ।\nयर्मिया! यहूदाका मानिसहरू बद्लिनु पर्छ\nअनि तिमीकहाँ आउँनु पर्छ\nतर तिमी तिनीहरूकहाँ जानु हुँदैन।",
			"20": "<sup>20</sup> म तिमीलाई बलियो बनाउनेछु\nर ती मानिसहरूले तिमीहरूलाई\nकाँसाको पर्खाल जस्तै बलियो सम्झनेछन्।\nयहूदाका मानिसहरू तिमीसित लडनेछन्।\nतर तिनीहरूले तिमीलाई परास्त गर्नु सक्तैनन\nकारण म तिमीसित रहनेछु,\nम तिमीलाई सहायता गर्नेछु र बचाउँनेछु।”\nपरमप्रभु भन्नुहुन्छ।",
			"21": "<sup>21</sup> “म तिमीलाई दुष्ट मानिसहरूबाट जोगाउने छु।\nती मानिसहरूले तिमीलाई डर देखाउँछन् तर म तिमीलाई ती मानिसहरूबाट मुक्त पारेर ल्याउनेछु।”"
		},
		"16": {
			"1": "<sup>1</sup> महा-बिपद्को दिन\nपरमप्रभुको यो सन्देश मकहाँ आयो:",
			"2": "<sup>2</sup> “यर्मिया, यहाँ तिमीले विवाह गर्नु हुँदैन्। तिमीसँग यस ठाउँमा कुनै छोरा-छोरी हुनु हुँदैन।”",
			"3": "<sup>3</sup> परमप्रभुले ती छोरा-छोरीहरूको विषयमा भन्नुभयो जो यहूदाको भूमिमा जन्मिएका छन्। अनि परमेश्वरले ती नानीहरूका आमा-बाबुहरूको बारेमा त्यस्तो भन्नुभयो,",
			"4": "<sup>4</sup> “ती मानिसहरूले डरलाग्दो मृत्यु मर्ने छन्। कोही पनि तिनीहरूका लागि रूने छैन। कसैले पनि तिनीहरूलाई दफन गर्ने छैन। तिनीहरूको शरीरहरू गोबरहरू जस्तो भूइँमा लडिरहनेछ। ती मानिसहरू शत्रुहरूको तरवारले मर्नेछन्, अथवा भोकले मर्नेछन्। तिनीहरूका मृत शरीरहरू आकाशमा उड्ने चराहरू र पृथ्वीमा बस्ने जनावरहरूको लागि भोजन हुनेछ।”",
			"5": "<sup>5</sup> यसर्थ परमप्रभु भन्नुहुन्छ, “यर्मिया, तिमी त्यो घरमा नजाऊ जहाँ मृत मानिसको अन्त्येष्टीको भोज भैरहेको छ। मृतकको लागि विलाप गर्नु अनि रूनु नजाऊ कारण मैले त्यस मानिसबाट मेरो शान्ति, आशीर्वाद, मेरो माया अनि दया फर्काएको छु,” परमप्रभु भन्नुहुन्छ।",
			"6": "<sup>6</sup> “यहूदाको भूमिमा ठूलो तथा साना सबै मानिसहरू मर्नेछन्। कसैले पनि तिनीहरूलाई दफ्नाउने छैन, न त तिनीहरूका निम्ति रूने नै छन्। कसैले पनि तिनीहरूका निम्ति केश तथा दाह्री खौरने छैनन्।",
			"7": "<sup>7</sup> शोक गर्नेहरूलाई कसैले भोजन दिने छैनन्। तिनीहरूका बाबु अथवा आमा मर्दा सान्त्वना दिनलाई कसैले तिनीहरूलाई एक प्याला दाखरस पनि दिने छैनन्।",
			"8": "<sup>8</sup> “यर्मिया, त्यस्तो घरमा नजाऊ जहाँ मानिसहरू उत्सव मनाई रहेका हुन्छन्। त्यो घरमा पिउन, खान र बस्न नजाऊ।",
			"9": "<sup>9</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर यी कुराहरू भन्नुहुन्छ ‘अति शीध्र नै तिमीले आफ्नो जीवनकाल नै देख्नेछौ कि म यो ठाउँमा आनन्दको ध्वनिहरू र दुलहा-दुलहीको आवाजहरू बन्द गरिदिनेछ।’",
			"10": "<sup>10</sup> “यर्मिया, तिमीले यहूदाका मानिसहरूलाई यी कुराहरू भन अनि मानिसहरूले तिमीलाई सोध्ने छन्, ‘किन परमप्रभुले त्यस्ता डरलाग्दो कुराहरू हामीलाई भन्नु भएको छ? हामीले के भूल गरेका छौं? हामीले हाम्रो परमेश्वरको विरूद्ध के पाप गरेकाछौ?’",
			"11": "<sup>11</sup> तब तिमीले ती मानिसहरूलाई यी कुराहरू भन्नै पर्छ ‘कारण तिम्रा पिता-पुर्खाहरूले मलाई त्यागेकाछन्।’ परमप्रभुले घोषणा गर्नु हुन्छ, ‘अनि अन्य देवताहरूको अनुशरण गरी तिनीहरूको सेवा गरे अनि आरधना गरे। तिनीहरूले मलाई त्यागिदिए अनि मेरा व्यवस्थाहरू मान्न अस्वीकार गरे।",
			"12": "<sup>12</sup> तिम्रा पिता-पुर्खाहरूले भन्दा तिमीहरूले अझ बढी पाप गरेकाछौ। तिमीहरू अत्यन्त हठी छौ अनि तिमीहरूले त्यस्ता कुराहरू मात्र गर्यो जुन तिमीहरूका मनले सुझाव दियो। तिमीहरूले मेरो आज्ञा पालन गरेका छैनौ।",
			"13": "<sup>13</sup> यसकारण म तिमीहरूलाई देशबाट निकाली दिनेछु। म तिमीहरूलाई तिमीहरू तथा तिमीहरूका पिता-पुर्खाहरूले पनि नदेखेको विदेशमा जानु बाध्य गराउनेछु। त्यस देशमा तिमीहरू रात-दिन अरू देवताहरू पूज्नेछौ। म तिमीहरूलाई कहिल्यै सहायता गर्ने छैन।’",
			"14": "<sup>14</sup> “मानिसहरू शपथ खान्छन् र भन्छन्, ‘परमप्रभु जसरी बाँच्नु हुन्छ। उहाँले मिश्रदेशबाट इस्राएलका मानिसहरूलाई बाहिर निकालेर ल्याउनु भयो।’ तर समय आउँदैछ जति बेला मानिसहरूले यी वचनहरू भन्नेछैनन्।” यो सन्देश परमप्रभुबाट आएको हो।",
			"15": "<sup>15</sup> मानिसहरूले शपथ ग्रहण गर्नेछन्, “परमप्रभु जसरी बाँच्नु हुन्छ। परमप्रभु जसले इस्राएलका मानिसहरूलाई भूमिको उत्तरी भाग र अन्य सबै भागहरूबाट ल्याउनु भएको हो, तिनीहरू त्यहाँबाट धपाइएका थिए।” मानिसहरू ले यी वचनहरू भन्नेछन् किनभने मैले तिनीहरूका पिता-पुर्खाहरूलाई दिएको जमीनमा म तिनीहरूलाई फर्काई ल्याउनेछु।",
			"16": "<sup>16</sup> परमप्रभु भन्नुहुन्छ, “म धेरै जालहारीहरूलाई पठाउँनेछु। तिनीहरूले यहूदाका मानिसहरूलाई समात्नेछन्। तब म धेरै शिकारीहरूलाई पठाउनेछु, जसले तिनीहरूलाई पहाडमाथि प्रत्येक पर्वत अनि चर्केको चट्टानहरूमा शिकार गर्नेछन्।",
			"17": "<sup>17</sup> तिनीहरूले गर्ने प्रत्येक कुरा म हेर्छु। यहूदाका मानिसहरूले कुनै कुरा म देखि लुकाउँन सक्तैनन्। तिनीहरूले मबाट पाप लुकाउँन सक्तैनन्।",
			"18": "<sup>18</sup> म यहूदाका मानिसहरूले गरेका अधर्म कामहरूका निम्ति पुनःक्षतिपूर्ति गर्नेछु। म तिनीहरूलाई प्रत्येक पापको दोबर दण्ड दिनेछु। म तिनीहरूलाई त्यसो गर्नेछु किनभने तिनीहरूले मेरो भूमि घृणित मूर्तिहरूद्वारा दूषित पारि दिए अनि तिनीहरूले मेरो देश तिनीहरूका घृणित मूर्तिहरूले भरिदिए।”",
			"19": "<sup>19</sup> हे परमप्रभु, तपाईं मेरो बल र मेरो संरक्षण हुनुहुन्छ।\nतपाईं सङ्कटको बेलामा दौडेर जाने एक सुरक्षित ठाउँ हुनुहुन्छ।\nसारा पृथ्वीका कुना-कुनाबाट जातिहरू तपाईंकहाँ आउँछन्।\nतिनीहरू भन्छन्, “हाम्रा पिता-पुर्खाहरूकहाँ झूटा देवताहरू थिए।\nतिनीहरूले ती महत्वहीन मूर्तिहरू पूजे,\nजुन मूर्तिहरूले तिनीहरूलाई मद्दत गर्दैन।",
			"20": "<sup>20</sup> के कुनै मानिसले वास्तविक देवताहरू बनाउनुसक्छ?\nअँहँ तिनीहरू वास्तविक देवताहरू होइनन्।”",
			"21": "<sup>21</sup> परमप्रभु भन्नुहुन्छ, “यसर्थ जसले मूर्तिहरू बनाउँछन्।\nतिनीहरूलाई अघिबाटै म मेरो शक्ति\nर बल विषयमा बताउनेछु।\nतब तिनीहरूले जान्नेछन कि मेरो नाउँ परमप्रभु हो।”"
		},
		"17": {
			"1": "<sup>1</sup> हृदयमा लेखिएको दोष\n“यहूदाका मानिसहरूका पापहरू\nतिनीहरूका हृदयको पाटीमा\nअनि तिनीहरूका वेदीको सींगहरूमा\nतिखो फलामले लेखिएको थियो।",
			"2": "<sup>2</sup> तिनीहरूका नानीहरूले\nझूटा देवताहरूका बेदीहरू सम्झन्छन्।\nतिनीहरूले काठको स्तम्भहरू सम्झन्छन्\nजुन अशिराहलाई समर्पित् गरेको थियो।\nतिनीहरूले यी कुराहरू हरियो रूखहरू मुनी\nअनि पहाडहरूमाथि सम्झन्छन्।",
			"3": "<sup>3</sup> म मेरो पहाड,\nतिमीहरूका सम्पत्ति\nअनि मूल्यवान वस्तुहरू\nलूटेको समान झैं अरूलाई दिनेछु।\nतिमीहरूले तिमीहरूको सम्पूर्ण देशमा\nपापको दाम स्वरूप म ती चीजहरू दिनेछु।",
			"4": "<sup>4</sup> तिमीहरूले मैले दिएको भूमि उम्काउनेछौ।\nविदेशमा तिमीहरूले शत्रुहरूको सेवा गर्नेछौ\nकारण तिमीहरूले मेरो क्रोधलाई तातो आगो झैं बनायौ\nजुन सदा सर्वदाका निम्ति बलिरहनेछ।”",
			"5": "<sup>5</sup> मानिसहरूमा आस्था, अनि परमप्रभुमा आस्था\nपरमप्रभुले यी कुराहरू भन्नुभयो,\n“जुन मानिसले अरू मानिसमाथि भरोसा गर्छ,\nअनि शारीरिक शक्तिमाथि विश्वास गर्छ,\nर परमप्रभुबाट आफ्नो हृदय टाढो लैजान्छ त्यसलाई धिक्कार।",
			"6": "<sup>6</sup> ती मानिसहरू मरुभूमिमा उम्रने पोथ्रा झैं हुनेछन्\nअनि उन्नति तिनीहरूले देख्ने छैनन्।\nतिनीहरूले असल देख्दैनन्\nजब यो आउँछ।",
			"7": "<sup>7</sup> त्यो मानिस धन्य हो,\nजसले परमप्रभुमाथि भरोसा गर्छ,\nजसले उहाँमाथि विश्वास राख्छ।",
			"8": "<sup>8</sup> त्यो मानिस पानीको छेवमा रोपिएको रूख जस्तै हुनेछ\nजसले खोल्सा सम्म आफ्नो जराहरू फैलाएको हुन्छ।\nयसकारण त्यो वृक्ष गरम कालमा डराउँदैन।\nयसका पातहरू खडेरी पर्दा पनि\nहरियै रहन्छन्\nअनि यसमा फल फल्नु छोड्दैन।",
			"9": "<sup>9</sup> “मानिसको हृदय अन्य कुनै चीज भन्दा कपटपूर्ण हुन्छ।\nत्यो कहिल्यै निको नहुने हुन्छ।\nयसलाई कसैले बुझ्न सक्तैन।",
			"10": "<sup>10</sup> तर म हुँ,\nम एकजना मानिसको हृदय देख्न र निर्णय लिनु\nअनि उसको कर्म र योग्यता अनुसार फल दिन्छु।",
			"11": "<sup>11</sup> एकजना मानिस जसले आफ्नो अधिकार बाहिरबाट सम्पत्ति पाउँछ,\nत्यो अण्डा नपारी चिङना काडने चरा जस्तै हो।\nउसले आफ्नो सम्पत्ति जीवनको आधा अथवा अन्तिममा हराउँछ\nअनि ऊ मूर्ख बनिन्छ।”",
			"12": "<sup>12</sup> आदिदेखि हाम्रो मन्दिर\nपरमप्रभुको लागि महिमामय सिंहासन भएको हो।\nयो एकदमै मुख्य पवित्र स्थान हो।",
			"13": "<sup>13</sup> हे परमप्रभु! तपाईं इस्राएलको आशा हुनुहुन्छ,\nतिनीहरू सब जसले छोड्छन्,\nपछि तिनीहरू लज्जित हुनेछन्।\nभूमिमा जसले तपाईंलाई छोड्नेछन्।\nतिनीहरूको नाउँ माटोमा लेखिनेछ ।\nकिनभने तिनीहरूले परमप्रभुलाई,\nजीवित जलश्रोतलाई त्यागे।",
			"14": "<sup>14</sup> यर्मियाको तेस्रो गुनासो\nहे परमप्रभु, यदि तपाईंले मलाई निको पार्नुभयो भने,\nम साँच्चि नै निको हुनेछु यदि तपाईंले मलाई बचाउँनु भयो भने,\nम साँच्चि नै बाँच्ने छु।\nपरमेश्वर, म तपाईंको गुण गाउँछु।",
			"15": "<sup>15</sup> तिनीहरूले मलाई सोच्छन्,\n“परमप्रभुबाट आएको वचन कहाँ छ?\nआउन देऊ!”",
			"16": "<sup>16</sup> परमप्रभु, म तपाईंको गोठालो बनिनुबाट भागेर गईनँ।\nमैले शोकपूर्ण दिनको कहिल्यै इच्छा गरिन।\nतपाईं जान्नुहुन्छ\nतपाईंको अघि मैले के भनेको थिएँ यो तपाईंको सामु छ।",
			"17": "<sup>17</sup> मलाई डर नदेखाउनु होस्।\nतपाईं मेरो विपत्तिको दिनमा शरण स्थान हुनुहुन्छ।",
			"18": "<sup>18</sup> मलाई खेदनेहरूलाई लाज होस्,\nतर मलाई लज्जित नपार्नुहोस्।\nतिनीहरूलाई डर देखाउनु होस्\nतर मलाई होइन।\nतिनीहरूमाथि ध्वंसको दिन आवोस्।\nतिनीहरूलाई ध्वंस पार्नु होस्।\nतिनीहरूलाई फेरि ध्वंस पनि पार्नु होस्।",
			"19": "<sup>19</sup> विश्राम दिनलाई पवित्र राखी रहनु\nपरमप्रभुले यी कुराहरू मलाई भन्नुभयो “यर्मिया, प्रवेशद्वारमा जाऊ जहाँबाट यहूदाका राजाहरू भित्र जान्छन् अनि बाहिर आउँछन् त्यहाँ खडा़ भई बस्। त्यसपछि यरूशलेमको अन्य प्रवेश द्वारहरूमा जाऊ।”",
			"20": "<sup>20</sup> मानिसहरूलाई यी वचनहरू भन, “परमप्रभुका वचनहरूमा कान थाप। हे यहूदाका राजा अनि यहूदाका मानिसहरू, अनि यरूशलेमका बासिन्दाहरू। प्रत्येक मानिस जो यो फाटकबाट पस्छन् मेरो कुरा सुन।",
			"21": "<sup>21</sup> परमप्रभुले यी कुराहरू भन्नुहुन्छ ‘तिमीहरूका जीवनका निम्ति विश्राम दिनमा कुनै भारी नबोक। यरूशलेमको प्रवेशद्वार भएर विश्राम दिनमा भारी नल्याऊ।",
			"22": "<sup>22</sup> विश्राम दिन आफ्ना घरबाट कुनै भारी ननिकाल। त्यसदिन कुनै काम नगर। तिमीहरूले त्यो दिन पवित्र राख्नेछौ जस्तो मैले तिमीहरूका पिता-पुर्खाहरूलाई आदेश दिएको थिएँ।",
			"23": "<sup>23</sup> तर तिम्रा पिता-पुर्खाहरूले आज्ञा पालन गरेनन्। तिनीहरूले ममा कुनै ध्यान दिएनन्। तिम्रा पिता-पुर्खाहरू अत्यन्त हठी थिए। कुनै संशोधन तिनीहरू सुन्दैनथे अथवा ग्रहण गर्दैनथे।’”",
			"24": "<sup>24</sup> परमप्रभु भन्नुहुन्छ, “‘तर यदि तिमीहरूले मेरो कुरा सुनेर विश्रामको दिन शहरका मूल-द्वारहरूबाट कुनै भारी भित्र ल्याएनौ र विश्राम दिनलाई पवित्र मानी कुनै पनि काम गरेनौ भने।",
			"25": "<sup>25</sup> “‘यदि तिमीले मेरो आदेश पालन गर्यौ भने, तब राजाहरू अनि राजकुमारहरू जो दाऊदका सिंहासनमा बस्छन् यरूशलेमको प्रवेशद्वारबाट आउँनेछन्। ती राजाहरू रथहरू र घोडाहरूमाथि चढेर आउँनेछन्। यहूदाका मानिसहरू र यरूशलेमका बासिन्दाहरूका नायकहरू ती राजाहरूसित आउँनेछन्। सदा-सर्वदा यरूशलेमले यस प्रवेशद्वारमा मानिसहरूलाई स्वागत गर्नेछन्।",
			"26": "<sup>26</sup> यहूदाका शहरहरूबाट मानिसहरू यरूशलेम आउनेछन्। यरूशलेम वरिपरिका स-साना गाऊँहरूबाट मानिसहरू यरूशलेम आउनेछन्। मानिसहरू बिन्यामीन परिवारहरूको जमीनबाट राफेलको पहाडीदेशबाट अनि नेगेबबाट आउनेछन्। ती मानिसहरूले होमबलि, मेलबलि, अन्नबलि, धूप र धन्यवादको बलिहरू परमप्रभुको मन्दिरमा ल्याउनेछन्।",
			"27": "<sup>27</sup> “‘तर यदि तिमीहरूले मेरो कुरा सुनेनौ भने; यदि तिमीहरूले विश्रामको दिनमा यरूशलेमभित्र भारीहरू ल्यायौ भने, तिमीहरूले यस दिनलाई पवित्र दिन मानेका हुँदैनौ। यसकारण म आगो सल्काउनु शुरू गर्नेछु जुन निभाउन सकिन्दैन। त्यो आगो यरूशलेमको प्रवेशद्वारहरू देखि शुरू हुनेछ, अनि यरूशलेमको राजमहलहरू जलाउनेछ।’”"
		},
		"18": {
			"1": "<sup>1</sup> कुमाले र माटो\nयर्मियाकहाँ आएको यो परमप्रभुको सन्देश हो",
			"2": "<sup>2</sup> “कुमालेको घरमा जाऊ अनि म त्यहाँ तिमीलाई मेरा बचनहरू सुनाउनेछु।”",
			"3": "<sup>3</sup> यसकारण म कुमालेको घरतिर गएँ। कुमालेले चक्रमा माटो लिएर काम गरिरहेको मैले देखें।",
			"4": "<sup>4</sup> उसले नरम माटोबाट एउटा हाँडी बनाउँदैथ्यो। तर हाँडीमा केही भूल रह्यो। यसर्थ उसले त्यो विगार्यो अनि उसलाई जुन माटो सही लाग्यो त्यसैबाट अर्को एउटा हाँडी बनायो।",
			"5": "<sup>5</sup> तब परमेश्वरको सन्देश मकहाँ आयो",
			"6": "<sup>6</sup> “इस्राएलका घराना, तिमीहरू जान्दछौ कि म, परमेश्वर तिमीहरूसँग पनि त्यस्तै काम गर्न सक्छु। तिमीहरू कुमालेको हातको माटो जस्तै छौं। अनि म कुमाले जस्तो हुँ।",
			"7": "<sup>7</sup> एक समयमा एउटा जाति अथवा एउटा राज्यको बारेमा बोल्न सक्नेछु अनि भन्नेछु कि म यसलाई तान्नेछु र नष्ट पार्नेछु।",
			"8": "<sup>8</sup> तर त्यस राज्यका मानिसहरूले आफ्ना हृदय र जीवन शैली परिवर्तन गर्न सक्छन्। तिमीहरूले अधर्म कामहरू गर्न छाड्लान्। तब म पनि मेरो मन बदलनु सकौंला। अनि नष्ट पार्ने मेरो प्रस्ताव नलिउँला।",
			"9": "<sup>9</sup> अर्को एक समय आउन सक्ला जब म एउटा राज्य अथवा जातिको बारेमा यसलाई खडा र स्थापित गराउँछु।",
			"10": "<sup>10</sup> तर यस्तो हुन सक्छ कि त्यो राज्यका मानिसहरूले नराम्रा कर्महरू गर्न सक्छन अनि मेरा आज्ञाहरू उल्लंघन गर्न सक्छन। तब राम्रा कामहरू जो मैले गर्न योजना गरको थिएँ त्यस विषयमा मेरो मन परिवर्तन गर्नेछु।",
			"11": "<sup>11</sup> “यसकारण यर्मिया, अब यहूदाका मानिसहरू र यरूशलेममा बस्ने मानिसहरूलाई भन ‘यही नै परमेश्वर भन्नुहुन्छ म तिनीहरूको लागि सकष्टहरू तैयार पारिरहेछु अनि तिनीहरूका विरूद्ध योजनाहरू बनाइरहेछु। यसकारण तिनीहरूले अनिष्ट कामहरू गर्नु वन्द गरोस्। प्रत्येक मानिस परिर्वतन होऊ र असल कुराहरू गर।’",
			"12": "<sup>12</sup> तर यहूदाका मानिसहरूले जवाफ दिन्छन्। ‘निर्थक हो प्रयास गर्नु, हामी जे चाहन्छौं त्यही गर्नेछौ। हामी अट्टेरी अनि दुष्ट हृदयले जे सुझाव दिन्छ त्यही गर्छौ।’”",
			"13": "<sup>13</sup> यसकारण परमप्रभु भन्नुहुन्छ:\n“अन्य जातिहरूलाई यो सोध:\n‘के तिमीहरू कसैले कन्या छोरी इस्राएलले गरे झैं,\nदुष्ट कर्महरू गरेको सुनेका छौ?’\nअनि इस्राएल परमेश्वरको लागि अति प्रिय हो।\nइस्राएल परमेश्वरको लागि नव-वधु जतिकै हो।",
			"14": "<sup>14</sup> तिमीहरू जान्दछौ कि\nलबानोन पर्वतहरूबाट हिऊँ कहिल्यै अल्पिदैन\nअनि बगिरहने चिसो पानी कहिल्यै सुक्दैन।",
			"15": "<sup>15</sup> तर मेरा मानिसहरूले मलाई बिर्सिए।\nतिनीहरूले अन्य देवताहरूलाई बलिहरू चढाए।\nतिनीहरू पूरानो बाटोबाट फर्किए।\nअहिले तिनीहरू राम्रा सडकहरू छाडेर\nकिनाराका पथहरू पछ्याउँछन्।",
			"16": "<sup>16</sup> तिनीहरूको भूमि जन शून्य,\nएक निन्दाको वस्तु हुनेछ।\nत्यहाँबाट जाने पदयात्रीहरू दु:खीत हुन्छन्,\nअनि तिनीहरू आफ्नो टाउको हल्लाउँनेछन्।",
			"17": "<sup>17</sup> पूर्वबाट बतासले उडाएको चीजहरू जस्तै\nम यहूदाका मानिसहरूलाई छरपष्ट पारिदिनेछु।\nतिनीहरूले मलाई मुद्दत गर्न गएको देख्ने छैनन्\nतर मैले तिनीहरूलाई छाडेर गएको देख्नेछन्।”",
			"18": "<sup>18</sup> यर्मियाको चौथो गुनासो\nतब यर्मियाका शत्रुहरूले भने, “यर्मियाको विरूद्ध योजनाहरू तैयार गरौं। पूजाहारीले दिएको व्यवस्था अथवा ज्ञानीबाट पाइएको सल्लाह अथवा अगमवक्ताको सन्देश नाश हुनेछैन। यसकारण उसको बारेमा झूटा कुराहरू प्रचार गरौं ताकि ऊ नाश होस्। उसले भनेको कुराहरू प्रति ध्यान नदिऊँ।”",
			"19": "<sup>19</sup> हे परमप्रभु ध्यान दिनुहोस्!\nअनि सुन्नुहोस् मेरा शत्रुहरू के भन्दैछन्।",
			"20": "<sup>20</sup> के मानिसहरूले असलको सट्टामा खराब फर्काउनु पर्छ?\nअँह सम्झना गर,\nम तपाईं समक्ष उभिएँ\nअनि तिनीहरूका निम्ति तिमीमा भएको क्रोध हटाउँन\nतपाईंसित वकालत गरें।\nतर तिनीहरूले मलाई जालमा फसाउँन प्रयास गरिरहेकाछन्।",
			"21": "<sup>21</sup> यसकारण तिनीहरूका नानीहरू भोक भोकै मरून्।\nशत्रुका तरवारहरूले तिनीहरू परास्त होऊन्।\nतिनीहरूका पत्नीहरू छोरा-छोरी बिहीन होऊन्।\nयहूदाका मानिसहरू मृत्यु तिर धकेलिउन्।\nतिनीहरूका पत्नीहरू विधवाहरू होऊन्। जवानहरू युद्ध-भूमिमा मारिउन्।",
			"22": "<sup>22</sup> जब हटात तिमी तिनीहरूका विरूद्ध शत्रुहरू ल्याउँछौ\nतिनीहरूको घरमा ठूलो चित्कार होस्।\nयी आपदहरू आइलागुन् किनभने तिनीहरूले मलाई फँसाउन खाडल खने,\nअनि मेरो खुट्टा समात्न पासो थापे।",
			"23": "<sup>23</sup> हे परमप्रभु, मलाई मार्ने तिनीहरूका योजनाहरू तपाईं जान्नुहुन्छ।\nतिनीहरूका अपराधहरू क्षमा नगर्नु होस्।\nतिनीहरूका पापहरू नमेटी दिनुहोस्।\nमेरा शत्रुहरूलाई नष्ट गर्नु होस्।\nजब तपाईं क्रोधित हुनुहुन्छ।\nती मानिसहरूलाई दण्ड दिनु होस्।"
		},
		"19": {
			"1": "<sup>1</sup> फुटेको भाँडो\nपरमप्रभुले मलाई भन्नुभयो, “यर्मिया, जाऊ अनि कुमालेकोबाट माटोको भाँडो किन। मानिसहरूका केही प्रमुखहरू र पूजाहारीहरू कहाँ लैजाऊ।",
			"2": "<sup>2</sup> बेन-हिन्नोमको बेंसीमा जाऊ, जुन खपटे प्रवेशद्वारको नजिकै छ बेन मैले तिमीलाई जे भनेको छु त्यस ठाउँमा ती कुराहरू तिनीहरूलाई घोषणा गर्नु पर्छ।",
			"3": "<sup>3</sup> अनि भन, ‘परमप्रभुको वचन सुन, हे यहूदाका राजाहरू अनि यरूशलेमका मानिसहरू हो, सेनाहरूका परमप्रभु इस्राएलका परमेश्वर यसो भन्नुहुन्छ, म यस ठाउँमा सर्वनाश ल्याउँदैछु अनि यो सुनेर हरेक मानिस आश्चर्यचकित हुनेछ।",
			"4": "<sup>4</sup> म ती कुराहरू हुन दिनेछु किनभने यहूदाका मानिसहरूले मलाई त्यागेका छन्। तिनीहरूले यो ठाउँ विदेशी मूर्तिहरूले भरेकाछन्। यहूदाका मानिसहरूले अन्य देवताहरूलाई बलिहरू चढाए जसलाई न तिनीहरूका पिता-पुर्खाहरू न यहूदाका राजाहरूले चिनेका छन्। अनि तिनीहरूले यो ठाउँ निर्दोष रगतले भरिदिए।",
			"5": "<sup>5</sup> आफ्नो नानीहरू बाललाई आगोमा चढाउनु तिनीहरूले अल्गा ठाउँहरू बनाए। मैले तिनीहरूलाई यसो गर्नु भनेको थिइनँ। मैले तिनीहरूलाई आफ्नो नानीहरू बलिदान गर्नु भनेको थिइनँ। मैले यस्तो कुराहरू सोचेको पनि थिइनँ।",
			"6": "<sup>6</sup> अहिले मानिसहरूले यस ठाउँलाई तोपेत्त र बेन-हिन्नोनको खाडी भन्छन्। तर, म तिमीहरूलाई यो चेताउनी दिन्छु। यो परमप्रभुको सन्देश हो त्यस्तो दिनहरू आइरहेछ, जब मानिसहरूले यसलाई हत्याको खाडी भन्नेछन।",
			"7": "<sup>7</sup> यस ठाउँमा, म यहूदा र यरूशलेमका योजनाहरू नष्ट पारिदिनेछु। म तिनीहरूलाई आफ्नो शत्रुहरूको तरवार अघि पर्ने बनाउनेछु तिनीहरूको सम्मुख पारिदिनेछु जसले तिनीहरूको जीवन खोजिरहेका छन्। म तिनीहरूको लाशहरू जंगली जनावर र चरा चुरूङ्गिहरूको आहार बनाइदिन्छु।",
			"8": "<sup>8</sup> म पूर्णरूपले यस शहरलाई ध्वंश पारिदिनेछु अनि यरूशलेम भएर जाने पथ-यात्रीहरू यसको विनाशमा व्याकुल भएर टाउँको हल्लाउनेछन्।",
			"9": "<sup>9</sup> शत्रुहरूले शहरको चारैतिर सेना ल्याउने छन्। ती सेनाहरूले जसले तिनीहरूको जीवन खोजिरहेकाछन् तिनीहरूलाई घेरामा पार्नेछन्। म तिनीहरूलाई आफ्नै नानीहरूको र एक-अर्काको मासु खाने बनाउँछु।’",
			"10": "<sup>10</sup> “तब तिमीसित जो जो गएकाछन् तिनीहरूको उपस्थितिमा भाँडो फुटाऊ।",
			"11": "<sup>11</sup> त्यस समयमा यी वचनहरू भन ‘सेनाहरूका परमप्रभु भन्नुहुन्छ म यहूदाको राज्य र यरूशलेमको शहर, जसरी कसैले कुमालेको भाँडा फुटाउँछ र त्यो जोडिन सक्तैन। मृत शरीरहरू तोपेतमा दपनाइनेछ जब सम्म त्यहाँ जग्गा शेष हुँदैन।",
			"12": "<sup>12</sup> म यस्तो यस भूमिमा र यसका बासिन्दाहरूका निम्ति गर्नेछु। म यस शहरलाई तोपेत झैं पार्नेछु।’ यही परमप्रभुको सन्देश थियो।",
			"13": "<sup>13</sup> ‘यरूशलेमका घरहरू र यहूदाको राजाका महलहरू तोपेतको ठाउँहरू जस्तै “अशुद्ध” पार्नेछु। यस्तो चीजहरू किन हुन्छ? किनभने मानिसहरूले ती घरहरूका छानामाथि झूटो देवताहरू पूजे। तिनीहरूले ताराहरू पूजे र तिनीहरूका सम्मानमा होमबलि समर्पित गरे। तिनीहरूले अरू देवताहरूलाई अर्घबलि चढाए।’”",
			"14": "<sup>14</sup> तब यर्मिया तोपेतबाट गए जहाँ परमेश्वरले उसलाई प्रचार गर्न भन्नु भएको थियो। यर्मिया परमेश्वरको मन्दिर तिर गए अनि मन्दिरको चोकमा उभिए। यर्मियाले सबै मानिसहरूलाई भने",
			"15": "<sup>15</sup> “यो सेनाहरूका परमप्रभु इस्राएलका परमेश्वरले भन्नुभएको हो ‘म यरूशलेममा र यसको वरिपरिका गाउँहरूमा धेरै प्रकारका नराम्रा कुराहरू घटाउनेछु। यो हुने छ, किनभने तिनीहरू मेरो आदेशहरू पालन गर्न एकदमै अट्टेरी गर्छन्। म चाँडै ती कुराहरू घटाउने छु।’”"
		},
		"20": {
			"1": "<sup>1</sup> यर्मिया र पशूहरू\nपशहूर नाउँ भएको एकजना पूजाहारी थिए। उनी परमेश्वरको मन्दिरको मुख्य अधिकारी थिए। पशहूर इम्मेरको छोरो थिए। पशहूरले यर्मियाले ती कुराहरू प्रचार गरेका सुने।",
			"2": "<sup>2</sup> यसकारण उसले यर्मिया अगमवक्तलाई कुटे अनि ठिँगुरामा हाले। यो बिन्यामीनको ढोकाको माथिल्लो प्रवेशद्वारमा भएको थियो।",
			"3": "<sup>3</sup> अर्को दिन पशहूरले यर्मियालाई ठिँगुरोबाट मुक्त गरिदिए। तब यर्मियाले पशहूरलाई भने, “परमेश्वरले तिमीलाई दिएको नाउँ पशहूर होइन। अहिले तिमीलाई परमप्रभुले दिनुभएको नाउँ हो, ‘डरले घेरिएको।’",
			"4": "<sup>4</sup> यो तिम्रो नाउँ हो किनभने परमेश्वर भन्नुहुन्छ, ‘म तिमी स्वयंलाई आतङ्क बनाई दिनेछु। म चाँडै तिम्रा मित्रहरूका निम्ति तिमीलाई एक आतङ्क बनाइदिनेछु। शत्रुहरूले आफ्नो तरवारद्वारा तिम्रा साथीहरूलाई मारिरहेको तिमीहरूले देख्नेछौ म यहूदाका मानिसहरूलाई बाबेलका राजाकहाँ सुम्पने छु। उसले यहूदाका मानिसहरूलाई बाबेलमा अनि सेनाले आफ्ना तरवारहरूले यहूदाका मानिसहरूलाई मार्ने छन्।",
			"5": "<sup>5</sup> शहरका सम्पत्तिहरू, यसका नाफा, सबै बहुमूल्य वस्तुहरू र यहूदाका राजाहरूका सम्पत्तिहरू तिनीहरूकै शत्रुहरूलाई नै दिनेछु। शत्रुहरू यी सबै सम्पत्तिहरू बाबेलको राजधानीमा लानेछन्।",
			"6": "<sup>6</sup> तिमी र तिम्रो घरमा बस्ने मानिसहरू सबैलाई टाढा लगिने छ। तिमीहरूलाई बाबेल राज्यमा जान वाध्य गराइनेछ। अनि तिमीहरू विदेशी राज्यमा मर्नेछौ अनि त्यहाँ तिमीहरू दफ्नाइनेछौ। तिमीले तिम्रा साथीहरूलाई मिथ्या कुराहरू प्रचार गरेकाछौ। तर तिम्रा सारा मित्रहरू पनि मर्नेछन् र बाबेलमा दफन गरिनेछन्।’”",
			"7": "<sup>7</sup> यर्मियाको पाँचौ गुनासो\nहे परमप्रभु! तपाईंले मलाई धोका दिनुभयो, अनि म धोकामा परे।\nतपाईं बलियो हुनुहुन्छ, यसर्थ तपाईंले मलाई जित्नु भयो।\nमानिसहरू मलाई देखेर हाँस्छन्।\nअनि दिनभरि मलाई हाँसोको पात्र बनाउँछन्।",
			"8": "<sup>8</sup> जब जब म बोल्छु, चिच्याउँछु,\nम हिंसा र विनाशको विरोधमा चिच्याइरहेछु।\nम मानिसहरूलाई त्यो सन्देश भन्छु जुन परमप्रभुले मलाई भन्नुभयो।\nतर मानिसहरूले सधैँ मसित खिसी गर्छन् र मेरो हाँसो उडाउँछन्।",
			"9": "<sup>9</sup> कहिले काँही म स्वयंलाई भन्छु,\n“म परमप्रभु विषयमा सबै भुल्नेछु\nअनि म अब उप्रान्त परमेश्वरको नाउँमा बोल्ने छैन।”\nतर जब म त्यसो भन्छु,\nपरमप्रभुको वचनहरू मेरो हड्डीभित्र\nआगो जलिरहे झैं म भित्र बल्छ।\nयसले मलाई थकाउँछ अनि म सहन सक्तिनँ।",
			"10": "<sup>10</sup> म मेरो विषयमा मानिसहरू\nजहीं-तहीं कानेखुशी गरेको सुन्छु,\nतिनीहरू भन्छन्,\n“चारैतिर आतंक छ!\nयो घोषणा गरौं।\nमेरा साथीहरू पनि मलाई\nभ्रममा पार्न पर्खिरहेका छन्।\nयी मानिसहरू भन्छन्\nसायद उसलाई छक्काउनु\nअनि हामी जित्न सक्छौ।\nतब ऊ माथि हामी बद्ला लिनेछौं।”",
			"11": "<sup>11</sup> तर परमप्रभु मसँग\nबलियो सिपाही जस्तो हुनुहुन्छ।\nयसकारण मानिसहरू,\nजो मेरो पछि लागेकाछन्,\nतिनीहरू लट्पटिनेछन्,\nअनि मलाई जित्नु सक्ने छैनन्।\nतिनीहरू हारको सम्मुख हुनु पर्नेछ,\nलज्जित हुनेछन् र यो कहिल्यै भुलिनेछैन।",
			"12": "<sup>12</sup> हे सेनाहरूका परमप्रभु! तपाईंले के सही जाँच्नु हुन्छ,\nअनि मानिसको इच्छाहरू र हृदयहरूको परीक्षा गर्नुहुन्छ।\nमैले तिनीहरूको विरोधमा नालिश प्रश्तुत गरेकोछु।\nतिनीहरूमाथि तपाईंको प्रतिकार हेरौं।",
			"13": "<sup>13</sup> परमप्रभुको स्तुति गाऊ।\nपरमप्रभुको प्रशंसा गर।\nकिनभने परमप्रभुले गरीब मानिसहरूलाई\nदुष्ट मानिसहरूबाट बचाउँनु हुन्छ।",
			"14": "<sup>14</sup> यर्मियाको छैटौ गुनासो\nम जन्मेको दिन श्रापित होस्।\nत्यो दिनलाई धन्य नमान जुन दिन म आमासँग थिएँ।",
			"15": "<sup>15</sup> त्यो मानिस श्रापित होस्\nजसले मेरा बाबुलाई म जन्मेको खबर सुनायो\nउसले भन्यो, “तिमीले एउटा छोरो पायौ।”\nउसले मेरो बाबुलाई अत्यन्त खुशी बनायो\nजब उसले मेरो बाबुलाई त्यो खबर सुनायो।",
			"16": "<sup>16</sup> त्यो मानिस परमेश्वरले ध्वंस गर्नु भएको शहरहरू जस्तै होस्।\nती शहरहरूमाथि परमप्रभुको दया छैन।\nत्यस मानिसले बिहान कष्टको आवाज सुनोस्,\nअनि मध्य दिनमा युद्धको चिच्चाहट सुनोस्।",
			"17": "<sup>17</sup> किनभने जब म आमाको गर्भमा थिएँ,\nत्यस मानिसले मलाई मारेन।\nयदि त्यसले मलाई त्यस बेला मारेको भए,\nमेरो चिहान मेरी आमाहुनै हुने थिइन्।\nअनि म संसारमा जन्मनु पर्ने थिएन।",
			"18": "<sup>18</sup> यी सब शोकहरू र सङ्कटहरू भोग्न,\nअनि लज्जित भएर दिन बिताउन,\nमेरी आमाको गर्भबाट म किन जन्मे?"
		},
		"21": {
			"1": "<sup>1</sup> परमप्रभुले राजा सिदकियाहको बिन्ती रद्द गरीदिनु भयो\nयो सन्देश परमप्रभुबाट यर्मियाकहाँ आएको हो। यो सन्देश त्यस बेला आएको थियो जब सिदकियाह यहूदाका राजाले पशहूर नाउँका एक मानिस र सपन्याह नाउँका एक पूजाहारी यर्मियाकहाँ पठाएका थिए। पशहूर मलिकयाह नाउँ भएको एक मानिसको छोरो थियो। सपन्याह मासेयाह नाउँका एक मानिसको छोरो थियो। पशहूर र सपन्याहले यर्मियालाई सन्देश ल्याएका थिए।",
			"2": "<sup>2</sup> पशहूर र सपन्याहले यर्मियालाई भने, “हाम्रा निम्ति परमप्रभुलाई प्रार्थना गरिदेऊ। के हुने छ भनेर परमप्रभुलाई सोधी देऊ। हामी जान्न चाहन्छौ। किनभने बाबेलका राजा नबूकदनेस्सरले हामीलाई आक्रमण गरिरहेछन्। हुनसक्छ उहाँले बितेका दिनहरूमा गर्नु भए जस्तै परमप्रभुले हाम्रो लागि महान कार्यहरू गरिदिनु हुनेछ। हुनसक्छ परमप्रभुले हामीलाई आक्रमण गर्न नबूकदनेस्सरलाई रोक्नु हुनेछ र फर्की जानेछ।”",
			"3": "<sup>3</sup> तर यर्मियाले पशहूर र सपन्याहलाई भने। उनले भने, “राजा सिदकियाहलाई भन",
			"4": "<sup>4</sup> ‘यही नै परमेश्वर, इस्राएलका परमप्रभुले भन्नुहुन्छ: तिमीहरूसँग युद्धका हतियारहरू छन्। तिमीहरूले ती हतियारहरू आफू स्वंयलाई बचाउन बाबेलका राजा र बाबेलवासीहरू विरूद्ध चलाइरहेछौ। तर म ती हतियारहरू तिनीहरू कै विरूद्धमा चलाउनेछु।\n“‘बाबेलका सैन्य शहर वरिपरिका पर्खालहरू बाहिर छन्। त्यो सैन्य शहर वरिपरि छ। म चाँडै त्यो सैन्य यरूशलेमभित्र ल्याउनेछु।",
			"5": "<sup>5</sup> म स्वयं मेरा शक्तिशाली हात र बलियो हतियार लिएर तिमीहरूसित लड्नेछु।",
			"6": "<sup>6</sup> म यरूशलेमका बसिन्दा दुवै मानिस तथा पशुहरूलाई मार्नेछु। तिनीहरू महा-रूढीले मर्नेछन्।’”",
			"7": "<sup>7</sup> यसपछि, परमप्रभुको यो सन्देश हो, “‘म यहूदाका राजा सिदकियाह, उनका अधिकारीहरू र रूढीले नमारेका शहरमा बाँचेका मानिसहरू नबूकदनेस्सरलाई सुम्पिदिनेछु। बाबेलका राजा अनि अरूहरू जो शहरमा बाँकी रहेकाछन्, तिनीहरू सब देशान, तरवार र अनिकालले मर्नेछन्। म तिनीहरूलाई तिनीहरूकै शत्रु र तिनीहरूको जीवन खोज्नेहरूलाई सुम्पिनेछु। तिनीहरूले कुनै दया, क्षमा र सहानुभूति नराखी तरवाले मार्नेछन्।’",
			"8": "<sup>8</sup> “यरूशलेमका मानिसहरूलाई यी कुराहरू भन: ‘परमप्रभुले भन्नुहुन्छ, मैले तिनीहरूलाई जीवनको बाटो र मृत्युको बाटो बताएकोछु।",
			"9": "<sup>9</sup> ती जो यरूशलेममा बस्दछन् तिनीहरू तरवार, अनिकाल र रूढीले मारिन्छन्। तर जो यरूशलेमबाट बाहिर जान्छन् र आक्रमण गरिरहेका बाबेलका सैन्यलाई। आफैं सुम्पिन्छन्, तिनीहरू बाँच्नेछन्।",
			"10": "<sup>10</sup> मैले यरूशलेम शहरका मानिसहरूलाई नोक्सानमा पार्न निर्णय लिएँ। म शहरलाई भलाइ गर्दिनँ।’ यो परमप्रभुको सन्देश हो: ‘म यरूशलेम शहरलाई बाबेलका राजाकहाँ सुम्पनेछु। उसले यसलाई आगोले भस्म पार्नेछ।’”",
			"11": "<sup>11</sup> “यी कुराहरू यहूदाका शाही परिवारहरूलाई भन,\n‘परमप्रभुका वचनहरूलाई महत्व देऊ।",
			"12": "<sup>12</sup> हे दाऊदको घराना! परमप्रभु भन्नुहुन्छ:\nमानिसहरूलाई निष्पक्ष न्याय गर\nर अत्याचारमा परेकाहरूलाई अत्याचारीहरूबाट संरक्षण गर।\nयदि तिमीहरूले त्यसो गरेनौ भने\nरीसले म निभाउनु नसकिने आगो झैं हुनेछु।\nयो साँचो हुनेछ कारण तिमीहरूले धैरे दुष्कर्महरू गरेकाछौ।’",
			"13": "<sup>13</sup> “हे यरूशलेम! म तिम्रो विरूद्धमा छु।\nतिमी मैदानमा भन्ज्याङ्ग माथिको पहाडमाथि बस्छौ।\nतिमी यरूशलेमका मानिसहरू भन्छौ,\n‘कसैले तिमीहरूलाई आक्रमण गर्न\nर तिमीहरूको शक्तिशाली शहर जित्न सक्तैन।’\nतर परमप्रभुबाट आएको वचनहरूलाई ध्यान देऊ।”",
			"14": "<sup>14</sup> “तिमीले पाउनु पर्ने दण्ड पाउने नै छौ।\nम तिमीहरूका जङ्गलमा आगो लगाउन थाल्ने छु।\nत्यो आगोले तिमीहरू वरिपरि भएका\nसारा चीजहरू जलाई दिनेछ।”"
		},
		"22": {
			"1": "<sup>1</sup> दुष्ट राजाहरू विरूद्ध न्याय\nपरमप्रभुले भन्नुभयो “यर्मिया, राजाको महलमा जाऊ। यहूदाका राजा कहाँ जाऊ र त्यहाँ यो सन्देशको प्रचार गर",
			"2": "<sup>2</sup> ‘परमप्रभुबाट आएको यो सन्देश सुन, दाऊदको सिंहासनमा बस्ने हे यहूदाका राजा, तिम्रा अधिकारीहरू अनि यी ढोकाहरूबाट पस्ने मानिसहरू।",
			"3": "<sup>3</sup> परमप्रभु भन्नुहुन्छ निष्पक्ष र धार्मिक हौ। अत्याचारमा पारेकाहरूलाई अत्याचारीबाट रक्षा गर। विदेशमा जन्मेका नागरिकहरू टुहुरा-टुहुरी अथवा विधवाहरूमाथि नराम्रो नगर। निर्दोष मानिसहरूलाई नमार।",
			"4": "<sup>4</sup> यदि तिमीहरूले आज्ञा पालन गर्यौ भने, राजाहरू जो दाऊदको सिंहासनमाथि बस्दछन् अधिकारीहरू सहित रथहरू र घोडाहरूमाथि बसी यी प्रवेशद्वारहरूबाट बस्नेछन्।",
			"5": "<sup>5</sup> यदि तिमीहरूले ती आज्ञाहरू पालन गरेनौ भने, परमप्रभु भन्नुहुन्छ म आफ्नै नाउँमा शपथ लिन्छु म यो राजमहल नष्ट पार्नेछु।’”",
			"6": "<sup>6</sup> यसैले परमप्रभु यहूदाको राजाको राजमहलको विषयमा भन्नुहुन्छ:\n“तँ ता मेरो निम्ति गिलादको जङ्गल\nर लबानोनको पहाड जस्तै छस्।\nतर म तँलाई मरुभूमिमा परिणत गरिदिन्छु।\nकोही बसो-बास गर्ने नभएको शहर झैं तँलाई म उजाड बनाइदिन्छु।",
			"7": "<sup>7</sup> म राजदरबार नष्ट गर्नलाई\nहतियार सहित मानिस पठाउने छु।\nती नष्टगर्ने हरूले तेरा देवदारको रूखहरू काट्नेछन्\nर आगोमा फ्याँकनेछन्।",
			"8": "<sup>8</sup> “धेरै राज्यका मानिसहरू यही शहर भएर जानेछन्। तिनीहरूले एकार्कामा सोध्नेछन्, ‘किन परमप्रभुले त्यस्तो डरलाग्दो कुरा यरूशलेम प्रति गर्नुभयो? यरूशलेम त्यस्तो महान शहर थियो।’",
			"9": "<sup>9</sup> त्यो प्रश्नको उत्तर यो हुनेछ ‘परमेश्वरले यरूशलेम नष्ट पार्नुभयो किनभने तिनीहरूले तिनीहरूका परमेश्वर परमप्रभुसित गरेको करार त्यागे। तिनीहरूले अरू देवताहरूलाई पूजा गरे अनि तिनीहरूको सेवा गरे।’”",
			"10": "<sup>10</sup> राजा यहोआहाजको विरूद्ध न्याय\nमरेको राजाको लागि नरूनु।\nउसको लागि विलाप नगर्नु,\nबरू त्यो राजाको लागि रूनु\nजसले यो ठाउँ नै छोड्न बाध्य भएको छ।\nउसको निम्ति रूनु किनभने ऊ कहिल्यै फर्की आउने छैन्।\nऊ आफ्नो स्वदेश फेरि देख्ने छैन।",
			"11": "<sup>11</sup> यही कुरा शल्लूम (यहोआहाज) योशियाहको छोराको विषयमा परमप्रभु भन्नुहुन्छ। शल्लूम योशियाह राजाको मृत्यु पछि यहूदाका राजा भए। “यहोआहाज यरूशलेमबाट निस्की टाढा गए। उनी यरूशलेम फेरि कहिल्यै फर्की आउँदैनन्।",
			"12": "<sup>12</sup> यहोआहाज त्यो ठाउँमा मर्ने छ जहाँ मिश्रावासीले उनलाई लगेर गए। उनले यो देश फेरि देख्ने छैनन्।”",
			"13": "<sup>13</sup> यहोयाकीम राजाको विरूद्ध न्याय\n“हाय! त्यसलाई जसले आफ्नो मानिसहरूलाई\nतिनीहरूको श्रमको ज्याला नदिई\nबे-ईमान्दरीले महल बनायो,\nअझ ठगेर तला थप्यो।",
			"14": "<sup>14</sup> “उसले भन्छ,\n‘आफ्नो लागि म एउटा ठूलो महल बनाउनेछु।\nमेरो माथिल्लो तलामा ठूल-ठूला कोठाहरू हुनेछन्।’\nउसले देवदारूका पल्लाले झ्यालहरू बनाउँछ रातो रंग लाउँछ।",
			"15": "<sup>15</sup> “देवदारूले बनाएको तिम्रो विशाल महलले\nतिमीलाई महान राजा बनाउदैन।\nतिम्रा पिता खान र पिउनमा सन्तुष्ट थिए।\nउनले के ठीक र निष्पक्ष थियो त्यही गरे।\nयसर्थ प्रत्येक कुरो नै तिनको राम्रै भयो।",
			"16": "<sup>16</sup> तिनले गरीब\nर असाहय मानिसहरूलाई मद्दत गरे।\nयसकारण प्रत्येक कुरा\nतिनको लागि राम्रै भयो।\n‘मलाई चिन्नु नै यसको अर्थ हो।’\nपरमप्रभुले भन्नुभयो।",
			"17": "<sup>17</sup> “तिम्रो आँखाहरूले तिमीलाई के लाभदायक छ त्यो मात्र देख्छ।\nतिमी निर्दोष मानिसहरूलाई मार्न,\nमानिसहरूलाई चोट पुर्याउन र चक्नाचूर पार्न मात्र बिचार गर्छौ।”",
			"18": "<sup>18</sup> यसर्थ परमप्रभुले योशियाहका छोरो,\nराजा यहोयाकीमको विषयमा भन्नुहुन्छु,\n“यहोयाकीमको लागि यहूदाका मानिसहरू रूने छैनन्,\n‘हाय मेरो दाज्यू! हाय मेरी बहिनी!\nम यहोयाकीमको लागि अत्यन्तै दु:खीतछु भनी,\nतिनीहरू कसैले पनि परस्परमा कुरा गर्ने छैनन्।\nयहूदाका मानिसहरू यहोयाकीमको लागि रूने छैनन्।\nतिनीहरूले उनको विषयमा भन्ने छैनन्,\n‘हाय मेरा मालिक! हाय, मेरा महाराज!\nतिम्रो मृत्युमा म अत्यन्तै दु:खी छु।’",
			"19": "<sup>19</sup> यरूशलेमका मानिसहरूले यहोयाकीमलाई गधालाई जस्तै दफ्नाउनेछन्।\nतिमीहरूले उसको टाउको र शरीर तान्नेछन्,\nअनि यरूशलेमको मूल-ढोकाको बाहिर फ्याँक्नेछन्।",
			"20": "<sup>20</sup> “यहूदा, लबानोनको पर्वतमाथि जाऊ र जोडले चिच्याऊ।\nबाशान क्षेत्रमा जोडले कराऊ।\nअबारीमको पर्वतहरूमा चिच्याऊ,\nकिनभने तिम्रो ‘प्रेमीहरूलाई’ नष्ट पारिनेछ।",
			"21": "<sup>21</sup> “हे यहूदा, तिमीले आफैं सुरक्षित विचार गर्यौ।\nतर मैले तिमीलाई सावधानी रहन चेताउनी दिएँ।\nतर तिमीले भन्यौ, ‘म ध्यान दिँदिन।’\nतिमीले त्यही जीवन शैली अप्नायौ,\nजुन तिम्रो युवावस्थालमा थियो।\nतिम्रो युवावस्थालम देखिनै तिमीले मलाई मान्न अस्वीकार गर्यौ।",
			"22": "<sup>22</sup> हे यहूदा! तिम्रा भेंडा़ गोठालाहरूलाई बतासले उडाई लानेछ।\nतिम्रा प्रेमीहरू कैदी बनिनेछन्।\nतिमीलाई अपमान गरिनेछ र पक्कै पनि तिम्रा दोषपूर्ण कर्महरूले,\nतिमीलाई लज्जित पार्नेछ।",
			"23": "<sup>23</sup> “हे लबानोनका बासिन्दा\nजो देवदारूहरूको माझमा बास गर्छन्,\nजब तिमीले व्यथा पाउनेछौ तिमी चिच्याउनेछौ।\nप्रसव पीडामा परेकी स्त्री झैं व्यथाले तिमी लडीबुडी गर्नेछौ।”",
			"24": "<sup>24</sup> यहोयाकीन राजाको विरूद्ध न्याय\n“जस्तो म निश्चित रूपमा बस्छु,” यो परमप्रभुको सन्देश हो, “म यस्तो तिम्रो लागि गर्नेछु। यहोयाकीमको छोरो यहूदाको राजा, यहोयाकीन यदि तिमी मेरो दाहिने हातको छाप-औठी हुँदो हौ त म तिमीहरूलाई थुतेर फ्याँक्ने थिएँ।",
			"25": "<sup>25</sup> यहोयाकीन, म तिमीलाई बाबेलका राजा नबूकदनेस्सरलाई र बाबेलवासीहरूलाई सुम्पिनेछु। यी ती मानिसहरू नै हुन जसले तिम्रो प्राण खोजिरहेकाछन् र जोसित तिमी भयभित छौ।",
			"26": "<sup>26</sup> म तिमी र तिम्रो आमालाई अर्को देशमा फ्याँकिदिने छु जहाँ तिमीहरू दुवै जन्मेका थिएनौ। तिमी र तिम्रो आमा त्यो देशमा मर्नेछौ।",
			"27": "<sup>27</sup> तिनीहरू आफ्नो भूमिमा फर्किने इच्छा गर्छन् तर तिनीहरूलाई अनुमति दिइनेछैन।”",
			"28": "<sup>28</sup> के कोन्याह  कसैको आफलको फुटेको भाँडा जस्तो हो?\nजसलाई कसैले मन पराउँदैन।\nकिन यहोयाकीन र उसको नानीहरू फ्याँकिए?\nकिन तिनीहरू नजानेको देशमा फ्याँकिए?",
			"29": "<sup>29</sup> ए देश, ए देश!\nपरमप्रभुको सन्देश ध्यान दिएर सुन्।",
			"30": "<sup>30</sup> परमेश्वर भन्नुहुन्छ, “यहोयाकीनको बारेमा लेख,\n‘ऊ एकजना मानिस हो जसको कुनै नानीहरू छैन।\nयहोयाकीन आफ्नो जीवन कालमा सफल हुनेछैन।\nअनि उसको एउटै पनि सन्तान दाऊदको सिंहासनमा बस्ने छैन।\nकुनै पनि उसको सन्तानले यहूदामा शासन गर्ने छैन्।’”"
		},
		"23": {
			"1": "<sup>1</sup> “त्यो भेंडा़ गोठालाहरूलाई धिक्कार जसले मेरा भेंडा़हरूलाई ध्वंस र तितर-बितर पार्छ।” यो परमप्रभुबाट आएको सन्देश हो।",
			"2": "<sup>2</sup> ती गोठालाहरू (नेताहरू) मेरा मानिसका उत्तरदायीहरू हुन्। अनि इस्राएलका परमेश्वरले, यी कुराहरू ती गोठालाहरूलाई भन्नुहुन्छ “तिमी गोठालाहरूले मेरो बगाललाई चारै तर्फ भगाईदियौ। तिनीहरूलाई भाग्न वाध्य पार्यौ। अनि तिमीहरूले तिनीहरूलाई हेरचाह गरेनौ। तर मैले तिम्रो हेरचाह गरें। तिमीले गरेका दुष्ट कामहरूका निम्ति म दण्ड दिनेछु।” यो परमप्रभुबाट आएको सन्देश हो।",
			"3": "<sup>3</sup> “अब मैले अन्य देशहरू तिर खेदिएका बगाल भेला पार्छु र तिनीहरूलाई चर्ने खर्कमा ल्याउँछु। जब म मेरा बगाललाई चर्ने खर्कमा ल्याउँछु तिनीहरू लाभदायक बनिन्छन र संख्या बढछ।",
			"4": "<sup>4</sup> म मेरा बगालको लागि नयाँ गोठाहरूलाई राख्नेछु। तिनीहरूले मेरो बगालको हेरचाह गर्नेछन्। अनि मेरा बगाल डराउने अथवा भयमीत हुने छैन। मेरो कुनै पनि बगाल हराउँने छैन।” यो परमप्रभुबाट आएको सन्देश हो।",
			"5": "<sup>5</sup> धार्मिक “हाँगा”\nयो सन्देश परमप्रभुबाट आएको हो;\n“हेर अब त्यो समय आइरहेछ,\nकि म दाऊदको कुलमा एउटा धार्मिक ‘शाखा’ स्थापित गर्नेछु।\nउसको ज्ञानले शासन चलाउनेछ,\nर धर्म र न्याय गर्नेछ।",
			"6": "<sup>6</sup> त्यस समयमा, यहूदाका मानिसहरूलाई बचाइनेछ,\nअनि इस्राएलले सुरक्षित जीवन जिउनेछ।\nउहाँको नाउँ यो हुनेछ,\n‘परमप्रभु हाम्रो धार्मिकता हुनुहुन्छ’।",
			"7": "<sup>7</sup> “यसकारण ती दिनहरू आइरहेछन्,” यो सन्देश परमप्रभुबाट आएको हो, “जब मानिसहरूले भन्नेछैनन्, ‘जसरी परमप्रभु जीवित हुनुहुन्छ अनि यो उहाँ नै हुनहुन्छ, जसले इस्राएलका मानिसहरूलाई मिश्र भूमिबाट बाहिर ल्याउनु भयो।’",
			"8": "<sup>8</sup> तर मानिसहरूले केही नयाँ कुरा भन्ने छन्। तिनीहरूले भन्ने छन्, ‘जसरी परमेश्वर जीवित हुनुहुन्छ, परमेश्वर मात्र हुन् जसले इस्राएलका मानिसहरूलाई उत्तर-भूमिबाट ल्याउनु भयो। उहाँले तिनीहरूलाई सम्पूर्ण राज्यहरूबाट ल्याउनु भयो जहाँ उहाँले तिनीहरूलाई पठाउनु भएको थियो।’ त्यसपछि इस्राएलका मानिसहरू तिनीहरूका आफ्नै भूमिमा बस्नेछन्।”",
			"9": "<sup>9</sup> झूटा अगमवक्ताहरूका विरूद्ध न्याय\nअगमवक्ताहरूलाई सन्देश\nअन्तस्करणमा मेरो हृदय भाँचेको छ।\nमेरो सबै हड्डीहरू कामिरहेकाछन्।\nयो परमप्रभुको र उहाँको पवित्र वचनको कारणले हो।\nम मतवाला मानिस जस्तै भएकोछु।",
			"10": "<sup>10</sup> यहूदाका भूमि यस्ता मानिसहरूले भरिएकोछ\nजो व्यभिचारीहरू छन्।\nयसर्थ परमप्रभुले यस भूमिलाई श्राप दिनुभयो।\nअनि खर्क मरुभूमिमा परिणत भएकोछ।\nअगमवक्ताहरू दुष्ट छन्।\nती अगमवक्ताहरूले आफ्ना प्रभाव\nर शक्ति गल्ती गरी प्रयोग गर्दछन्।",
			"11": "<sup>11</sup> “अगमवक्ता अनि पूजाहारी दुवै पापीहरू छन्।\nतिनीहरूको दुष्ट्याईं मैले मेरो मन्दिरमा देखेकोछु।\nयो सन्देश परमप्रभुबाट आएको हो।",
			"12": "<sup>12</sup> यसकारण, म तिनीहरूलाई मेरो सन्देश दिन रोक्नेछु।\nतिनीहरूको बाटो अन्धकारले ढाकेको चिप्लो हुनेछ\nअनि तिनीहरू कालो अन्धकारमा झर्नेछन्।\nम ती पूजाहारीहरू\nर अगमवक्ताहरूमाथि\nविपद ल्याइ सजाय दिनेछु।”\nयो परमप्रभुबाट आएको सन्देश हो।",
			"13": "<sup>13</sup> “मैले सामरियामा अगमवक्ताहरूलाई\nअगमवाणी दिएर नराम्रो काम गरिरहेकोछु।\nबाल देवताको नाउँमा तिमीहरूले इस्राएलका मानिसहरूलाई पथभ्रष्ट गराए।",
			"14": "<sup>14</sup> मैले यहूदाका अगमवक्ताहरू डरलाग्दो कामहरू गरिरहेको देखेकोछु।\nतिनीहरूलाई व्यभिचारपूर्ण कर्ममा लिप्त छन्।\nतिनीहरू झूटो बोल्छन् अनि दुष्ट मानिसहरूलाई सहयोग दिन्छन्।\nत्यही कारणले मानिसहरूले दुष्ट काम गर्न रोकिदैनन्।\nतिनीहरू सदोमका मानिसहरू जस्ता छन्।\nअब यरूशलेम मेरो निम्ति गोमोरा जस्तै भएकोछ।”",
			"15": "<sup>15</sup> यसकारण सेनाहरूका परमप्रभुले ती अगमवक्ताहरूका बारेमा भन्नुहुन्छ\n“म ती अगमवक्ताहरूलाई तितो भोजन खाने\nर विष हालेको पानी पिउने बनाउनेछु\nकिनभने यरूशलेमका अगमवक्ताहरू देखि\nसम्पूर्ण देश नै दूषित पार्दैछन्।”",
			"16": "<sup>16</sup> सेनाहरूका परमप्रभु भन्नुहुन्छ\n“ती अगमवक्ताहरूका कुरामा ध्यान नदेऊ\nती अगमवक्ताहरूले तिमीहरूलाई मूर्ख बनाउने कोशिश गरिरहेकाछन्।\nती अगमवक्ताहरूले जुन दर्शनहरू बताइरहेका छन्\nती सब तिनीहरूको आफ्नै सृष्टिहरू हुन्।\nतिनीहरूले ती दर्शनहरू परमप्रभुबाट प्राप्त गरेका होइनन्।",
			"17": "<sup>17</sup> कतिपय मानिसहरूले परमेश्वरबाट आएको सन्देशलाई उपेक्षा गर्दछन्।\nअत ती अगमवक्ताहरूले ती मानिसहरूलाई\nबेग्ला बेग्लै सन्देश दिन्छन्।\nतिनीहरूले मलाई जसले निन्दा गर्छन् तिनीहरूलाई भन्छन्,\nपरमप्रभुले भन्नुहुन्छ, ‘तिमीले शान्ति पाउँछौ।’\nतिनीहरूले ज-जसको हृदय कठोर छ तिनीहरूलाई भन्छन्,\n‘तिमीहरूमाथि अनिष्ट हुने छैन्।’",
			"18": "<sup>18</sup> तर ती मध्ये कुनै पनि अगमवक्ताहरू दैवीय महासभामा उभिने होइन।\nती मध्ये कसैले पनि परमप्रभुको संदेश\nन देखेको छन्, सुनेको नै छ।",
			"19": "<sup>19</sup> अब परमप्रभुबाट हुने दण्ड तूफान जस्तो आउने छ।\nपरमप्रभुको क्रोध आँधी-तूफान जस्तो हुनेछ।\nयो ती दुष्ट मानिसहरूका टाउकोहरूमाथि भुमरीको झोक्का ठोकिनेछ।",
			"20": "<sup>20</sup> आफ्ना मनले चिताएको योजनाहरू सिद्ध नहोउन्जेल\nपरमप्रभुको रीस थामिने छैन।\nआउने दिनहरूमा\nतिमीहरूले त्यो छर्लङ्ग बुझ्नेछौ।",
			"21": "<sup>21</sup> मैले ती अगमवक्ताहरूलाई पठाएको होइन।\nतर तिनीहरू भागेर गए।\nतिनीहरूसँग बोलेको छैन।\nतर तिनीहरूले अगमवाणी दिए।",
			"22": "<sup>22</sup> तर यदि तिनीहरू मेरो महासभामा बसेका भए,\nतिनीहरूले मेरो यहूदाका मानिसहरूलाई मेरो सन्देशहरू भन्ने थिए।\nतिनीहरूले ती मानिसहरूलाई नराम्रो कामहरू\nगर्न देखि रोक्न सक्थे।”",
			"23": "<sup>23</sup> “म परमेश्वर हुँ, जो नजिक छु,”\nपरमप्रभु भन्नुहुन्छ,\n“म त्यो परमेश्वर होइन जो टाढोमा छ।",
			"24": "<sup>24</sup> मैले देख्न सक्तिन भन्ने विचारमा\nएउटा मानिसले आफैंलाई लुकाउनँ सक्ला।\nतर यो मूर्खता हो।\nके म स्वर्ग र पृथ्वी भर्दिनँ र?”\nपरमप्रभुले यसो भन्नुभयो:",
			"25": "<sup>25</sup> “त्यहाँ त्यस्ता अगमवक्ताहरू छन् जसले मेरो नाउँ झूटा प्रचार गरिरहेछन्। तिनीहरू भन्छन्, ‘मैले सपना देखेको छु। मैले सपना देखेको छु।’ मैले तिनीहरूले त्यस्तो कुराहरू गरेको सुनें।",
			"26": "<sup>26</sup> कहिले सम्म यी अगमवक्ताहरूले झूटो र आफ्नो माया प्रचार गरिरहने?",
			"27": "<sup>27</sup> ती अगमवक्ताहरूले मेरो नाउँ मानिसहरूबाट भुलाउनु प्रयास गरिरहेकाछन् जसरी आफ्ना पिता-पुर्खाहरूले सपनाहरू भनेर भुलेका थिए। तिनीहरूका पिता-पुर्खाहरूले बाल देवतालाई पूजे।",
			"28": "<sup>28</sup> पराल गहुँ भन्दा भिन्दै हुन्छ। अगमवक्ताहरूले जसकहाँ सपना छ त्यही सपना विषयमा बोलुन्। जसकहाँ मेरा वचनहरू छन् साँचो गरी भनुन।",
			"29": "<sup>29</sup> मेरो सन्देश आगो जस्तै छ।” यो सन्देश परमप्रभुबाट आएको हो, “यो मार्तोल जस्तो छ जसले ढुङ्गा चुरचुर पारेर फुटाउँदछ।”",
			"30": "<sup>30</sup> “यसर्थ म झूटा अगमवक्ताहरूको विरूद्धमा छु।” यो सन्देश परमप्रभुको हो “ती अगमवक्ताहरूले मेरा शब्दहरू एका-अर्काबाट चोरिरहेका छन्।",
			"31": "<sup>31</sup> म झूटो अगमवक्ताहरूको विरूद्ध छु।” यो सन्देश परमप्रभुको हो, “तिनीहरूले आफ्नै शब्दहरू चलाउँछन् र मेरोबाट आएको सन्देश भनेर बाहना गर्दछन्।",
			"32": "<sup>32</sup> म झूटा अगमवक्ताहरूका विरूद्ध छु जसले झूटो सपनाहरू प्रचार गर्छन्।” यो परमप्रभुको सन्देश हो। “तिनीहरूले मेरा मानिसहरूलाई झूटो शिक्षा दिदैं कुबाटो तिर लैजादैछन्। न त मैले तिनीहरूलाई पठाएँ न मेरो पक्षमा बोल्नु भनी आदेश दिएँ। तिनीहरूले कदापि यहूदाका मानिसहरूलाई सहयोग गर्न सक्तैनन्।” यो परमप्रभुको सन्देश हो।",
			"33": "<sup>33</sup> परमेश्वरबाट उदास सन्देश\n“यहूदाका मानिसहरू अथवा अगमवक्ताहरू, अथवा पूजाहारीहरूले तिमीलाई सोध्न सक्छ, ‘परमप्रभुले के-के भारी बोकाउनु भयो?’ तब तिमीहरूले तिनीहरूलाई उत्तर दिन्छौ र भन्छौ, ‘तिमीहरू अत्यन्तै गह्रौं बोझ हौ र म तिमीहरूलाई फ्याँक्नेछु।’ यो परमप्रभुको सन्देश हो।",
			"34": "<sup>34</sup> “यदि अगमवक्ता अथवा पूजाहारी अथवा कुनै मानिसले परमप्रभुको बोझ विषयमा कुरा गर्छ भने, ‘म उसका परिवार र उसलाई सजाय दिनेछु।’",
			"35": "<sup>35</sup> यो कुरा तिमीहरू एकार्कालाई भन्नेछौ ‘परमप्रभुले के जवाफ दिनुभयो?’ अथवा ‘उहाँले के भन्नुभयो’?",
			"36": "<sup>36</sup> तर तिमीले फेरि ‘परमप्रभुको बोझ’ भन्ने छैनौ किनभने ‘बोझ’ प्रत्येकको आफ्नै शब्द बनिएको हो। तर तिनीहरूलाई भन, ‘तिनीहरूले जीवित परमेश्वर, सेनाहरूका परमप्रभु हाम्रा परमेश्वरको वचन बङग्याएको छौ।’",
			"37": "<sup>37</sup> “यदि परमप्रभुको सन्देश तिमी सिक्न चाहन्छौ भने, अगमवक्तालाई सोध, ‘परमप्रभुले तिमीलाई के जवाफ दिनु भयो?’ अथवा ‘परमप्रभुले के भन्नुभयो?’",
			"38": "<sup>38</sup> तर तिमीले नभन परमप्रभुबाट के भारी बोझ थियो। यदि तिमीले यो शब्द प्रयोग गर्यौ भने परमप्रभुले तिमीलाई यी शब्दहरू भन्नु हुनेछ ‘तिमीले मेरो वचनलाई परमप्रभुको भारी नभन्नु पर्थ्यो। मैले तिमीलाई यो शब्द नभन्नु भनें।",
			"39": "<sup>39</sup> मैले तिमीहरू र तिम्रा पिता-पुर्खाहरूलाई दिएको शहर र तिमीलाई माथि उचालेर म देखि पर फ्याँक्ने छु।",
			"40": "<sup>40</sup> म तिनीहरूमाथि सदा-सर्वदा रहने लाज ल्याउनेछु। आफ्नो बेइज्जिती तिमीहरू कहिल्यै भुल्ने छैनौं।’”"
		},
		"24": {
			"1": "<sup>1</sup> असल र खराब नेभाराहरू\nपरमप्रभुले उहाँको मन्दिर अघाडी राखिएको दुइ डालो नेभारा देखाउनु भयो। (मैले यो दर्शन यहूदाका राजा यहोयाकीमका छोरा बाबेलका राजा नबूकदनेस्सरले यरूशलेमबाट निर्वासिन गराएर लगे पछि देखेको हो। यहोयाकीन र तिनका अधिकारीहरूलाई निपुण शिल्पकार र धातुकारहरूसितै लगियो।)",
			"2": "<sup>2</sup> एउटा डालोभित्र धेरै असल नेभाराहरू थिए। ती नेभाराहरू त्यस्ता थिए जुन समय अघाडि नै पाकेका थिए। तर अर्का डालोमा कुहेको नेभाराहरू थिए। तिनीहरू खान लायकका थिएनन्।",
			"3": "<sup>3</sup> परमप्रभुले मलाई सोध्नु भयो, “तिमीले के देख्दै छौ यर्मिया?”\nमैले जवाफ दिएँ, “ डालोमा म नेभाराहरू देख्दै छु। एउटा डालोमा नेभाराहरू एकदमै असल छन् अनि अर्को डालोमा एकदमै नराम्रा नेभाराहरू छन् जो खान लायक छैनन्।”",
			"4": "<sup>4</sup> तब परमप्रभुको यो सन्देश मकहाँ आयो",
			"5": "<sup>5</sup> परमप्रभु, इस्राएलका परमेश्वरले भन्नुहुन्छ, “यहूदाका मानिसहरूलाई आफ्ना देशबाट कैदी झैं लगियो। तिनीहरूका शत्रुहरूले तिनीहरूलाई बाबेलमा लगे। तिनीहरू असल नेभाराहरू जस्ता छन्।",
			"6": "<sup>6</sup> म तिनीहरूलाई दया देखाउनेछु। म तिनीहरूलाई यहूदाको भूमिमा फर्काई ल्याउनेछु। म तिनीहरूलाई विकास गराउनेछु, नष्ट हुन दिँदिन। म तिनीहरूलाई चुडाउँदिन तर तिनीहरूलाई जगाउँछु ताकि तिनीहरूको विकास हुन सकोस्।",
			"7": "<sup>7</sup> म तिनीहरूलाई म नै परमप्रभु हुँ भनेर चिन्ने योग्यता दिन्छु। तिनीहरू मेरा मानिसहरू हुनेछन् अनि म तिनीहरूका परमेश्वर हुँ, किनभने तिनीहरू पूर्ण-हृदयहरूले म तर्फ फर्कनेछन्।",
			"8": "<sup>8</sup> “तर यहूदाका राजा सिदकियाह कुहेको नेभारा जस्तौ हुनेछ जुन खान लायक हुँदैन। सिदकियाह, उसका अधिकारीहरू, यरूशलेममा छाडिएका मानिसहरू जो यो देशमा रहिरेका छन् अनि तिनीहरू जो मिश्रमा बसिरहेका छन् तिनीहरू कुहेको नेभाराहरू जस्तै हुन्।",
			"9": "<sup>9</sup> म तिनीहरूलाई जम्मै राज्यहरूमा डरलाग्दो र नराम्रो चीज बनाउनेछु। मैले तिनीहरूलाई जहाँ-जहाँ छरपस्ट पारेकोछु त्यहाँ तिनीहरूलाई अपमान, गिल्ला र श्राप का निम्ति पात्र बनाउँनेछु।",
			"10": "<sup>10</sup> म तरवार, अनिकाल अनि रूढी तिनीहरूका निम्ति पठाउनेछु। जब सम्म तिनीहरू निमटन्न हुँदैनन् तिनीहरू र तिनीहरूका पिता-पुर्खालाई मैले दिएको देशमा तरवार, अनिकाल र देशान पठाउनेछु। त्यस पछि मेलै तिनीहरू र तिनीहरूका पिता-पुर्खाहरूलाई दिएको जग्गामा तिनीहरू रहने छैनन्।”"
		},
		"25": {
			"1": "<sup>1</sup> यर्मियाको प्रवचन\nयो सन्देश जुन यर्मियाकहाँ आयो त्यो यहूदाका सबै मानिसहरूको विषयमा सम्बन्धित छ। यो योशियाहको पुत्र यहोयाकीम यहूदाको राज-सिंहासनमा बसेको चौथो बर्षमा भएको हो। यो नबूकदनेस्सरले बाबेलको राजमुकुट पहिरिएको प्रथम बर्षमा भएको हो।",
			"2": "<sup>2</sup> यसैले त्यही अगमवक्ता यर्मियाले यहूदाका जम्मै मानिसहरू र यरूशलेमका बासिन्दाहरूलाई भनेः",
			"3": "<sup>3</sup> यहूदाका राजा आमोनका पुत्र योशियाहको राज्यकालको तेह्रौं बर्षदेखि आजको दिनसम्म अर्थात् तेइस बर्षसम्म परमप्रभुको वचन मकहाँ आयो र मैले घरिघरि तिमीहरूलाई भनिरहेकोछु। तर तिमीहरूले यसलाई ध्यान नै दिएनौ।",
			"4": "<sup>4</sup> परमप्रभुले घरिघरि आफ्ना दास अगमवक्ता तिमीहरूकहाँ पठाउनु भएकोछ। तिमीहरूले तिनीहरूको कुरा सुनेनौ अथवा तिनीहरूलाई कुनै ध्यानै दिएनौ।",
			"5": "<sup>5</sup> ती अगमवक्ताहरूले भने, “तिमीहरूले आफ्ना जीवन शैली परिर्वतन गर। कुकर्महरू गर्नदेखि आफूलाई पर राख। जीवन परिवर्तन गर र यस देशमा जुन परमप्रभुले तिमी र तिमीहरूका पिता-पुर्खाहरूलाई सदा-सर्वदाका निम्ति दिनु भएकोछ त्यहाँ बसो बासो गर।",
			"6": "<sup>6</sup> अन्य देवताहरूको पछि नलाग। तिनीहरूका सेवा वा पूजा नगर। मानिस निर्मित मूर्तिहरू पूजा नगर। यसले मलाई रीस उठाउनेछ। यदि तिमीहरूले मानेनौ भने तब म तिमीहरूलाई नोक्सान गर्नेछु।”",
			"7": "<sup>7</sup> परमप्रभुले भन्नुहुन्छ, “तर तिमीहरूले मेरो कुरा मानेनौ। तिमीहरूका मूर्तिहरूले मलाई उत्तेजित पार्यौं अनि आफैंमा नोक्सान ल्यायौ।”",
			"8": "<sup>8</sup> यसकारण सेनाहरूका परमेश्वरले यसो भन्नुहुन्छ, “तिमीहरूले मेरो सन्देशहरूमा ध्यानै नदिएका हुनाले,",
			"9": "<sup>9</sup> म उत्तरका सबै कुलहरूका मानसिहरूलाई र मेरो दास बाबेलका राजा नबूकदनेस्सरलाई ल्याउनेछु, र तिनीहरूलाई यो देश र यसका बासिन्दा र यसका चारैतिरका सबै जातिहरूको विरूद्धमा ल्याउनेछु। म ती सारा देशहरूलाई बिलकुलै ध्वंस गर्नेछु। म ती भूमिहरू सदालाई सूख्खा मरुभूमि जस्तो बनाई दिने छु अनि तिनीहरूलाई डरलाग्दो गाली र एक स्थायी अपमानको पात्रमा परिणत गर्नेछु।",
			"10": "<sup>10</sup> म ती ठाउँहरू हर्ष र उत्साहका खुशीहरूलाई शेष पारिदिनेछु। त्यहाँ अब-उप्रान्त नव दुल्हा-दुल्हीका खुशी र आनन्दका दिनहरू हुने छैन। म मानिसहरूले पिस्ने जाँतोका आवाज सम्म उठाउँनेछु। म बत्तीको उज्यालोलाई लगिदिनेछु।",
			"11": "<sup>11</sup> त्यो सम्पूर्ण क्षेत्र नै मरुभूमि जस्तो उजाड हुनेछ। यी सबै जातिहरूका मानिसहरूले सत्तरी बर्ष सम्म बाबेलका राजाको सेवा गर्नेछन्।",
			"12": "<sup>12</sup> “तर सत्तरी बर्ष बिती सके पछि म बाबेलका राजालाई दण्ड दिनेछु। म बाबेलका जातिहरूलाई पनि दण्ड दिने छु।” यो परमेश्वरको सन्देश हो “म बाबेलवासीहरूको भूमिलाई पनि उनीहरूको पापहरूको कारण दण्ड दिनेछु। म त्यो भूमि सदाको लागि बाँझो बनाई दिनेछु।",
			"13": "<sup>13</sup> मैले त्यस देशको विषयमा जे भनेकोछु र यर्मियाले विरोधमा यसको जे अगमवाणी गरेकोछ अनि पुस्तकमा जे लेखिएकोछ म त्यो देशकोको विरूद्ध सबै ल्याउनेछु।",
			"14": "<sup>14</sup> बाबेलका मानिसहरूले धेरै राज्यहरू र महाराजहरूको सेवा गर्नु पर्नेछ। तिनीहरूले गरेको अनिष्ट कामका निम्ति तिनीहरूलाई सजाय दिइनेछ।”",
			"15": "<sup>15</sup> संसारका राज्यहरू माथि न्याय\nपरमप्रभु, इस्राएलका परमेश्वरले मलाई यी कुराहरू भन्नुभयो “यर्मिया, यो कचौरोको दाखरस मेरो हातबाट लेऊ। यो मेरो क्रोधको दाखरस हो। म तिमीलाई विभिन्न जातिहरूमा पठाई रहेछु। ती जातिहरूलाई यही प्यालाबाट पियाऊ।",
			"16": "<sup>16</sup> तिनीहरूले यो पिउँनेछन्। त्यसपछि तिनीहरू धरमराउनेछन् र पागल मानिस जस्तो व्यवहार गर्नेछन। तिनीहरूले तरवारको कारण त्यसो गर्छन् जुन तिनीहरूको विरूद्ध म पठाउँदैछु।”",
			"17": "<sup>17</sup> यसर्थ मैले परमेश्वरको हातबाट त्यो कचौरा दाखरस लिएँ। म ती जातिहरूमा गएँ अनि त्यो कचौराबाट मैले दाखरस पियाँए।",
			"18": "<sup>18</sup> मैले यो दाखरस यरूशलेम र यहूदाका शहरहरूमा खनाइदिएँ। मैले यहूदाका राजाहरू र अगुवाहरूलाई त्यो कचौराबाट दाखरस पिउँन लगाँए। तिनीहरू आज जस्तो देखन्छन, मरुभूमि झैं उजाड, डरलाग्दो चीज अनि खिल्ली र श्रापको पात्र बनाउन मैले तिनीहरूलाई पिलाएँ।",
			"19": "<sup>19</sup> मैले मिश्रका राजा फिरऊन, तिनका अधिकारीहरू, महत्वपूर्ण नायकहरू, उनका सबै मानिसलाई परमप्रभुको रीसको दाखरस पिउनु लगाँए।",
			"20": "<sup>20</sup> मैले अरबका जम्मै बासिन्दाहरू, ऊजका सबै राजाहरू, पलिश्तीहरूका सबै राजाहरू जसमा अश्कलोन, गाजा, एक्रोन, अश्दोदमा बाँकी रहेका जम्मै मानिसलाई परमप्रभुको रीसको दाखरस पिउनु लगाँए।",
			"21": "<sup>21</sup> अनि मैले एदोम, मोआब र अम्मोनिहरूका सबै राजाहरू पनि गाभिन्छन्, सबैलाई परमप्रभुको रीसको दाखरस पिउनु लगाँए।",
			"22": "<sup>22</sup> मैले सोर र सीदोनका सबै राजाहरू र जम्मै मानिसलाई परमप्रभुको रीसको दाखरस पिउनु लगाँए।\nअनि समुद्रका टापुहरूका राजाहरूलाई यो पिउन लगाँए।",
			"23": "<sup>23</sup> ददान, तेमा र बूजका अनि कनपट सम्म केश काट्ने मानिसहरूलाई मैले यो पिउन बाध्य गराँए।",
			"24": "<sup>24</sup> मैले अरब देशका र सीमान्तका सारा राजाहरू जो मरुभूमिमा बस्छन्,",
			"25": "<sup>25</sup> जिम्री, एलाम र मादैका सारा राजाहरूलाई पनि यो पिउन लगाँए।",
			"26": "<sup>26</sup> मैले सारा उत्तरका राजाहरू नजिक र टाढामा भएका राज्यहरूलाई यो पिउनु लगाँए। मैले सबै पृथ्वीका राजाहरूलाई परमप्रभुको क्रोधको कचौराबाट दाखरस पिउन लगाँए। तर “शीशकका”  राजाले पनि अन्य जातिहरूले पिए पश्चात पिउनेछन्।",
			"27": "<sup>27</sup> “यर्मिया! सेनाहरूका परमप्रभु इस्राएलका परमेश्वरले के भन्नुहुन्छ, ती जातिहरूलाई भनिदेऊ यसकारण परमप्रभुले भन्नुहुन्छ, ‘पिऊ, मात, वान्त गर र लड अनि नउठ किनभने म तिमीहरूका माझमा तरवार पठाइरहेछु।’",
			"28": "<sup>28</sup> “ती मानिसहरूले तिम्रो हातबाट कचौरा लिन अस्वीकार गर्छन। तिनीहरूले यसलाई पिउन अस्वीकार गर्छन। तर तिमीले तिनीहरूलाई भन्नेछौ, ‘सेनाहरूका परमप्रभुले यी कुराहरू भन्नुहुन्छ तिमीहरूले अवश्य नै यो कचौराबाट पिउनेछौ।",
			"29": "<sup>29</sup> मैले यरूशलेममाथि जसलाई मेरो नाउँले कहलाइन्छ अनि सायद तिमीहरूले दण्ड पाउँदैनौ भनेर सोचिरहेका होलाऊ, विपत्ति ल्याउन थालिसकेकोछु। म पृथ्वीमा भएका सारा मानिसहरू प्रति आक्रमण गर्न तरवारलाई बोलाउनेछु।’” यो सेनाहरूका परमप्रभुबाट आएको सन्देश हो।",
			"30": "<sup>30</sup> “यर्मिया! तिनीहरूलाई भन्छन्:\n‘परमप्रभु उँभोबाट गर्जनु हुन्छ।\nउहाँ आफ्नो पवित्र मन्दिरबाट गर्जनु हुन्छ।\nपरमप्रभु उहाँको भूमिका मानिसहरूलाई जोरसित गर्जनु हुन्छ।\nउहाँ दाखरस बनाउनेले अङ्गुर किच्याउँदा गरेको आवाज झैं चिच्चयाउनु हुन्छ।\nती चिच्चयाहटहरू पृथ्वीका सबै बासिन्दाहरू सम्म पुग्छ।",
			"31": "<sup>31</sup> त्यो हल्लाको प्रतिध्वनि,\nपृथ्वीको आखिर सम्म पुग्छ।\nपृथ्वीका मानिसहरूका विरूद्धमा\nपरमप्रभुको एउटा अभियोग छ।\nउहाँले मानिसहरूलाई न्याय\nर दुष्टहरूलाई तरवारले मार्नु हुनेछ।’”\nपरमप्रभु भन्नुहुन्छ।",
			"32": "<sup>32</sup> यसकारण सेनाहरूका परमप्रभु भन्नुहुन्छ,\n“हेर, महा-सर्वनाश एक देशदेखि अर्को देशमा फैलिनेछ।\nपृथ्वीको सुदूर कुनाहरूबाट\nएक सर्वनाशक आँधी आइरहेछ।”",
			"33": "<sup>33</sup> परमप्रभुले मार्नु भएको मानिसहरूको लाशहरू पृथ्वीको एक कुनादेखि अर्को कुनासम्म छरिएर रहनेछ। मर्नेहरूका निम्ति शोक मनाउने कोही हुँदैन। लाशहरू बटुलिदैन न त दफन नै गरिन्छ। ती लाशहरू खेतमा गोबर झैं छरपस्ट रहन्छन्।",
			"34": "<sup>34</sup> हे गोठालाहरू हेर, विलाप गर! हे बथानका नायकहरू हो,\nखरानीमा लडीबडी गर।\nकिनभने तिमीहरूका हत्या हुने र चिच्चाउने,\nअनि तितर-बितर हुने समय आएको छ।\nतिमीहरू झर्नेछौ र दामी जग झैं फुट्नेछौ।",
			"35": "<sup>35</sup> गोठालाहरूले आफै लुक्ने ठाँउ पाउने छैनन्।\nबथानका मालिकहरूले भाग्न सक्तैनन्।",
			"36": "<sup>36</sup> म गोठालाहरूका हाऊ-हाऊ शब्दहरू\nर बथानका मलिकहरूका चित्कार सुन्छु।\nपरमप्रभुले तिनीहरूको खर्क नै नष्ट पार्नु भयो।",
			"37": "<sup>37</sup> शान्तमय खर्कहरू नष्ट गरिए।\nपरमप्रभुको भीषण क्रोधले यस्तो गरेको हो।",
			"38": "<sup>38</sup> परमप्रभु हिंस्रक सिंह ओडारबाट निस्केको जस्तै हुनुहुन्छ।\nपरमप्रभुको बल्दै गरेको रीस र अत्याचारीहरूको रीसले,\nतिनीहरूको देश उजाड निर्जन बनिएकोछ।"
		},
		"26": {
			"1": "<sup>1</sup> मन्दिरमा यर्मियाको पाठ\nयोशियाहका छोरो यहूदाका राजा यहोयाकीमको शासनकालको शुरूमा यो वचन परमप्रभुबाट आयो।",
			"2": "<sup>2</sup> परमप्रभुले भन्नुभयो “यर्मिया, परमप्रभुको मन्दिर चोकमा तिमी उभ र यो वचन भन। मन्दिरमा आराधना गर्न आउने यहूदाका सबै बासिन्दाहरूलाई भन। मैले भनेको प्रत्येक कुरा उनीहरूलाई भन। मेरो सन्देशको कुनै भाग पनि नछोड।",
			"3": "<sup>3</sup> यो हुनसक्छ, तिनीहरूले सुन्नेछन् अनि मेरो सन्देश पालन गर्नेछन् र तिनीहरू खराब काम गर्नुबाट रोकिनेछन्। यदि तिनीहरू परिर्वतन भए भने म मेरो तिनीहरूलाई तिनीहरूको दुष्टकर्मका निम्ति सजाय दिन जुन योजना तयार पारेकोछु त्यो बदलिनेछु।",
			"4": "<sup>4</sup> तिमीले तिनीहरूलाई भन्नेछौ, ‘यही नै परमप्रभु भन्नुहुन्छ यदि तिमीहरूले मेरा कुराहरू सुनेनौ अनि मैले तिमीहरू अघि राखेको शिक्षाहरू।",
			"5": "<sup>5</sup> मेरा दास अगमवक्ताहरू जसलाई मैले तिमीहरूकहाँ समय समयमा पठाएको थिए तर तिनीहरूका वचनहरू अनुशरण गरेनौ, तिमीहरूले तिनीहरूको कुराहरू सुनेनौ।",
			"6": "<sup>6</sup> तब म यो मन्दिरलाई शिलोह  जस्तै बनाउनेछु अनि पृथ्वीभरिको मानिसहरूका निम्ति यो शहर एक श्राप झैं बनाउनेछु।’”",
			"7": "<sup>7</sup> पूजाहारीहरू, अगमवक्ताहरू अनि सारा मानिसहरूले यर्मियाले परमेश्वरको मन्दिरमा भनेको ती शब्दहरू सुने।",
			"8": "<sup>8</sup> परमप्रभुले दिनुभएको आदेश अनुसार यर्मियाले सबै कुराहरू मानिसहरूलाई भनी समाप्त गरे। तब पूजाहारीहरू, अगमवक्ताहरू र सारा मानिसहरूले यर्मियालाई समाते अनि भने, “तिमी निश्चय नै मर्नेछौ।",
			"9": "<sup>9</sup> कसरी तिमीले परमप्रभुको नाउँमा यति नराम्रो कुरा गर्ने आँट गर्यौ? कसरी परमप्रभुको यो मन्दिर शीलोहमा ध्वंस भए जस्तो हुनेछ भन्ने आँट कसरी गर्यौ? कुन आँटले तिमीले यरूशलेम निर्जन मरुभूमि झैं हुनेछ भन्यौ?” परमप्रभुको मन्दिरमा सबै मानिसहरू यर्मियाको विरूद्ध भेला भए।",
			"10": "<sup>10</sup> जब यहूदाका शासकहरूले ती अप्रिय घटनाहरूको बारेमा कुराहरू सुने तिनीहरू राजाको राजदरवारबाट बाहिर निस्के। तिनीहरू परमेश्वरको मन्दिर भएतिर उक्लिए। त्यहाँ, तिनीहरूले नयाँ प्रवेशद्वारमा आफ्ना ठाउँ लिए।",
			"11": "<sup>11</sup> त्यसपछि पूजाहारीहरू र अगमवक्ताहरूले शासकहरू र सारा अन्य मानिसहरूलाई यसो भने। तिनीहरूले भने, “यर्मिया मारिनु पर्छ। उनले यरूशलेमको बारेमा नराम्रो कुराहरू भने। तपाईंहरूले उनले त्यस्तो कुराहरू भनेको सुनेकाछौं।”",
			"12": "<sup>12</sup> तब यर्मियाले यहूदाका सबै प्रशासकहरू र मानिसहरूसँग बात गरे। “परमप्रभुले मलाई यो मन्दिर र शहर बारे यी सबै कुराहरू जो तिमीहरूले सुन्यौ, भन्न पठाउनु भयो।",
			"13": "<sup>13</sup> तिमीहरूले आफ्नो मन अनि कार्य बद्लिन नै पर्छ। तिमीहरूले आफ्नो परमप्रभु परमेश्वरको आज्ञा पालन गर्नु नै पर्छ। यदि तिमीहरूले त्यसो गर्यौ भने उहाँले आफ्नो मनलाई बद्लनु हुनेछ र खराब कामहरू जो उहाँले तिमीहरूका विरूद्ध उद्घोषणा गर्नु भएको छ त्यो टर्नेछ।",
			"14": "<sup>14</sup> मेरो विषयको कुरा र म तिमीहरूकै हातमा छु। तिमीहरूलाई जे ठीक लाग्छ त्यही गर।",
			"15": "<sup>15</sup> तर यदि तिमीहरूले मलाई मार्यौ भने, एउटा कुराको लागि निश्चित हौ। कि तिमीहरू एक निर्दोष मानिसको हत्यारा हुनेछौ। तिमीहरूले यो शहर र यसमा बस्ने सबै मानिसहरूलाई दोषी बनाउँनेछौ। किनभने उहाँ परमप्रभु नै हुनुहुन्छ, जसले मलाई ती वचनहरू तिमीहरूलाई भन्न पठाउनु भयो जो तिमीहरूले सुन्यौ।”",
			"16": "<sup>16</sup> त्यसपछि सबै प्रशासकहरू र मानिसहरू बोले। ती मानिसहरूले पूजाहारीहरू र अगमवक्ताहरूलाई भने, “यर्मियालाई मार्नु हुँदैन। जति कुराहरू यर्मियाले हामीलाई भने ती सबै परमप्रभु हाम्रा परमेश्वरबाट आएको हो।”",
			"17": "<sup>17</sup> तब कतिपय बूढा-पाकाहरू उठे र सबै मानिसहरूलाई भने।",
			"18": "<sup>18</sup> तिनीहरूले भने, “यहूदाका राजा हिजकियाहको शासनकालमा मारसेतबाट मीका नाउँ भएका एकजना अगमवक्ता आएका थिए। मीकाले यहूदाका सबै मानिसहरूलाई यी कुराहरू भने, ‘सेनाहरूका परमप्रभु भन्नुहुन्छ:\nसियोन ध्वंस हुने छ।\nयो जोतेको खेतझैं हुनेछ।\nयरूशलेम ढुङ्गाको थाक हुनेछ।\nपरमप्रभुको मन्दिर भएको डाँडा जगंल,\nर बनमाराहरूको रित्तो डाँडा  बनिनेछ।’",
			"19": "<sup>19</sup> “यहूदाका राजा हिजकियाहले मीकालाई मारेनन्। यहूदाका कुनै मानिसले मीकालाई मारेन। हिजकियाह परमप्रभुदेखि डराए अनि उहाँको दया याचना गरे। अनि परमप्रभुले तिनीहरूको विरोधमा भन्नु भएको कुराहरू विषयमा आफ्नो मन बद्लिनु भयो। तर हामीहरूले हाम्रो आफ्नो जीवनमाथि महाविपद ल्याउन आँटेकाछौं।”",
			"20": "<sup>20</sup> विगतमा त्यहाँ शमायाहको छोरो उरियाह नाउँ गरेका किर्यत्यारीमबाट आएका एकजना मानिस थिए। उसले यर्मियाले झैं यो शहर र यस भूमि विरूद्धमा परमप्रभुको सन्देश प्रचार गरे।",
			"21": "<sup>21</sup> राजा यहोयाकीम र उनका सैनिक अधिकारीहरू अनि यहूदाका प्रमुखहरूले ऊरियाहले गरेको प्रचार सुने। तिनीहरू क्रोधित भए। राजा यहोयाकीमले ऊरियाहलाई मार्न चाहन्थे। तर ऊरियाहले त्यो कुरा चाल पाए। ऊरियाह डराए र मिश्र देशमा भागेर गए।",
			"22": "<sup>22</sup> तर राजा यहोयाकिमले एलनातान नाउँ भएका एक मानिस र अन्य मानिसहरूलाई मिश्रमा पठाए। एलनातान अकबोरको छोरा थियो।",
			"23": "<sup>23</sup> तिनीहरूले ऊरियाहलाई मिश्रबाट ल्याए अनि यहोयाकीमलाई सुम्पिए। यहोयाकीमले ऊरियाहलाई तरवारले मार्नु भन्ने हुकुम दिए। ऊरियाहको मृत शरीर साधारण मानिसहरू गाड्ने चिहानमा फ्याँकिदिए।",
			"24": "<sup>24</sup> अब शापानको छोरो अहीकामले यर्मियालाई सुरक्षा दिए। यसर्थ तिनले तिनलाई मानिसहरूको हातबाट बचाए।"
		},
		"27": {
			"1": "<sup>1</sup> परमेश्वरले नबूकदनेस्सरलाई शासक बनाउनु भयो\nयहूदामा सिदकियाहको शासनकालको चौथो बर्षमा  परमप्रभुको यो वचन यर्मियाकहाँ अयो। सिदकियाह योशिहका छोरो थिए।",
			"2": "<sup>2</sup> परमप्रभुले भन्नुभयो, “यर्मिया, यी फीता र डण्डहरूबाट एउटा जुवा बनाऊ र तिम्रो काँधमा लगाऊ।",
			"3": "<sup>3</sup> त्यसपछि एदोम, मोआब, अम्मोन, सोर र सीदोमका राजाहरूकहाँ यी राजाहरूका दूतहरू जो यहूदाका राजा सिदकियाहलाई भेट्न यरूशलेममा आएका छन् तिमीहरूको माध्यम यी समाचार पठाऊ।",
			"4": "<sup>4</sup> तिनीहरूका मालिकहरूलाई यी खबरहरू पुर्याऊ भनी तिनीहरूलाई भन तिनीहरूलाई भन, ‘सेनाहरूका परमप्रभु, इस्राएलका परमेश्वरले भन्नुहुन्छ तिम्रा मालिकहरूलाई भन कि",
			"5": "<sup>5</sup> मैले पृथ्वीमा सारा पशुहरू बनाएँ। यो मैले मेरो महान शक्ति र बलिष्ठ पाखुराले गरें। म यो पृथ्वी जसलाई चाहन्छु, त्यसैलाई दिन सक्छु।",
			"6": "<sup>6</sup> अहिले तिमीहरूका सबै देशहरू मैले बाबेलका राजा नबूकदनेस्सरलाई दिएको छु। उनी मेरा दास हुन्। मैले जङ्गली पशुहरू सम्मलाई पनि उसको आज्ञा पालन गर्ने बनाउँछु।",
			"7": "<sup>7</sup> सारा जातिहरूले नबूकदनेस्सर, उनको छोरो र उनको नातिको सेवा गर्नेछन्। त्यसपछि बाबेल परास्त हुने समय आउनेछ। धेरै राज्यहरू र महान राजाहरूले बाबेललाई आफ्नो कमारो बनाउँनेछन्।",
			"8": "<sup>8</sup> “‘तर अहिले केही जातिहरू यस्ता हुनेछन् कि तिनीहरू बाबेलका राजा नबूकदनेस्सरलाई सेवा गर्न अस्वीकार गर्नेछन्। यदि कुनै जातिले आफ्नो घिच्रोमा जुवा राख्न अस्वीकार गरे म त्यसलाई तरवार, अनिकाल र डरलाग्दो बीमारहरू मार्फत सजाय दिनेछु,’” यो परमप्रभुबाट आएको सन्देश हो। “‘जब सम्म त्यो जातिलाई उसको पक्षबाट ध्वंश पार्दिन तब सम्म म यसो गर्नेछु।",
			"9": "<sup>9</sup> यसर्थ तिमीहरू अगमवक्ताहरू जोखाना हेर्नेहरू, दर्शन देख्नेहरू, ज्योतिषिहरू र जादूगरी गर्नेहरू जसले तिमीहरूलाई भन्छन्, “तिनीहरूले बाबेलका राजाको सेवा गर्नेछैनौ,” तिनीहरूलाई ध्यान नदेऊ।",
			"10": "<sup>10</sup> तर ती मानिसहरूले तिमीहरूलाई झूटो अगमवाणी दिनेछन् यसले गर्दा म तिमीहरूलाई तिनीहरूका देशबाट टाढो लखेट्नेछु र तिमीहरू मर्नेछौ।",
			"11": "<sup>11</sup> “‘ती जातिहरू ज-जसले बाबेलका राजाको सार्वभौमिकतालाई ग्रहण गर्नेछ। अनि उसको सेवा गर्नेछ, जब सम्म चाहन्छन् म तिनीहरूलाई तिनीहरूकै भूमिमा विकास हुन दिन्छु,’” परमप्रभुले घोषणा गर्नुहुन्छ।",
			"12": "<sup>12</sup> मैले सिदकियाह यहूदाका राजालाई भनें, “सिदकियाह तिमीले बाबेलका राजाको जुवा तिम्रो घिच्रोमा राख्नै पर्छ, यदि तिमीले बाबेलका राजा र उसको मानिसहरूका अस्वीकार गर्यौ भने तिमी बाँच्नेछौ।",
			"13": "<sup>13</sup> यदि तिमीले बाबेलका राजालाई सेवा गर्न राजी भएनौ भने तिमी र तिम्रा मानिसहरू शत्रुको तरवार भोक अनि भयानक रोगहरूबाट मर्नेछौ। बाबेलका राजालाई सेवा गर्न अस्वीकार गर्ने कुनै पनि जातिको सम्बन्धमा परमप्रभुले यसो भन्नुभयो।",
			"14": "<sup>14</sup> तर तिमीहरूलाई झूटो अगमवक्ताहरूले भनिरहेका छन् ‘तिमीहरू बाबेलका राजालाई सेवा गर्नेछैनौ।’\n“तिनीहरू तिमीहरूसित झूटो बोलिरहेकाछन्। यसकारण तिमीहरूले तिनीहरूको कुरा नसुन।”",
			"15": "<sup>15</sup> परमप्रभु भन्नुहुन्छ, “मैले त्यस्ता अगमवक्ताहरूलाई पठाएको होइन, तर तिनीहरू मेरो नाउँमा झूटो अगमवाणी कहिरहेका छन्। यसकारण यहूदाका मानिसहरूलाई सुदूर ठाँउमा पठाँउनेछु। तिमीहरू सड्नेछौ अनि त्यसरी नै ती अगमवक्ताहरू जसले तिमीहरूकहाँ अगमवाणी कहने तिनीहरू पनि नष्टछन्।”",
			"16": "<sup>16</sup> तब मैले ती पूजाहारीहरू अनि ती प्रजालाई सूचना दिएँ, “परमप्रभु भन्नुहुन्छ ‘ती झूटा अगमवक्ताहरूको कुरा नसुन जसले बाबेलबाट परमप्रभुको मन्दिरमा जम्मै समानहरू झट्टै फर्काईंदैछ।’ तिनीहरूले झूटो बोलिरहेका छन्।",
			"17": "<sup>17</sup> ती अगमवक्ताहरूका कुरामा ध्यान नदेऊ। बाबेलका राजालाई सेवा गर अनि बाँच। यो शहर किन नष्ट होस्?",
			"18": "<sup>18</sup> यदि ती मानिसहरू अगमवक्ता हुन भने अनि परमप्रभुको वचन तिनीहरूसँग छ भने, तिनीहरूले परमप्रभुलाई, राजाको महलमा रहेका, परमप्रभुको मन्दिरमा रहेका, र यरूशलेममा रहेका जुन चीजहरू अहिले सम्म छोडिएकाछन् बाबेलमा ल्याउन नसकियोस भनी प्रार्थना गरून्।”",
			"19": "<sup>19</sup> सेनाहरूका परमप्रभु भन्नुहुन्छ, “ती चीजहरूको बारेमा जुन अझै यरूशलेममा छाडिएका छन् त्यहाँ मन्दिरमा विशाल काँसाको स्तम्भहरू, गुडने आधारहरू र अन्य चीजहरू छन्। बाबेलका राजा नबूकदनेस्सरले ती सबैहरू यरूशलेममा छाडे।",
			"20": "<sup>20</sup> बाबेलका राजा नबूकदनेस्सरले जब यहोयाकीमको छोरा यहूदाका राजा यहोयकीन र यहूदा र यरूशलेमका जम्मै भारादारहरूलाई देश निष्कासन गरी बाबेलमा लगेका थिए, त्यस समय ती समानहरू बाबेल लगेनन्।",
			"21": "<sup>21</sup> सेनाहरूका, परमप्रभु, इस्राएलका परमेश्वर, परमेश्वरका मन्दिरमा छाडिएका अनि राजाका महल र यरूशलेममा छाडिएका चीजहरूको बारेमा भन्नुहुन्छ ती सम्पूर्ण समानहरू पनि बाबेल तर्फ लगिए।",
			"22": "<sup>22</sup> ‘ती सारा समानहरू बाबेलमा लगिन्छ र जब सम्म तिनीहरूको निम्ति आउँदिन तब सम्म ती समानहरू त्यही रहन्छ।’ यो सन्देश परमप्रभु भन्नुहुन्छ, ‘तब म ती समानहरू फर्काई ल्याउँनेछु। म ती समानहरू यो ठाउँमा फर्काई राख्नेछु।’”"
		},
		"28": {
			"1": "<sup>1</sup> हनन्याह झूटो अगमवक्ता\nत्यही साल नै जब सिदकियाह यहूदाका राजा भएको चौथो बर्षको पाँचौ महीनामा अज्जूरको छोरो अगमवक्ता हनन्याहले परमप्रभुको मन्दिरमा सबै पूजाहारीहरू र अन्य मानिसहरूको उपस्थितिमा मसित भने। हनन्याह गिबोनका बासिन्दा थिए। हनन्याहले भने,",
			"2": "<sup>2</sup> “सेनाहरूका परमप्रभु इस्राएलका मानिसहरूका परमेश्वरले भन्नुहुन्छ ‘बाबेलका राजाले यहूदाका मानिसहरूको काँधमा राखेको जुवा म भाँच्नेछु।",
			"3": "<sup>3</sup> दुइ बर्ष बित्दा नबित्दै म बाबेलका राजा नबूकदनेस्सरले परमप्रभुको मन्दिरबाट जति सरसामानहरू लगेका थिए ती सबै फर्काई ल्याउनेछु।",
			"4": "<sup>4</sup> म यहोयकीमका छोरो यहूदाका राजा यहोयाकीनलाई यस ठाउँमा फर्काइ ल्याउने छु। म ती सारा यहूदाका मानिसहरूलाई फर्काई ल्याउनेछु जसलाई नबूकदनेस्सरले आफ्ना घरहरू छोड्न बाध्य गराएर बाबेलतिर पठाएका थिए।’ परमप्रभु भन्नुहुन्छ, ‘यसकारण म बाबेलका राजाको जुवा भाँचिदिनेछु।’”",
			"5": "<sup>5</sup> तब अगमवक्ता यर्मियाले पूजाहारीहरू र अन्य मानिसहरू जो परमप्रभुको मन्दिरमा उभिरहेका थिए तिनीहरूको सम्मुख अगमवक्ता हनन्याहलाई जवाफ दिए।",
			"6": "<sup>6</sup> अगमवक्ता यर्मियाले हनन्याहलाई भने, “आमेन। म आशा गर्दछु परमप्रभुले साँच्चि नै त्यो गर्नु हुनेछ परमप्रभुले तिमीले बोलेका वचनहरू पूरा गर्नु हुनेछ। म आशा गर्दछु परमप्रभुले उहाँको मन्दिरबाट जम्मै सामानहरू र मानिसहरू पनि बाबेलबाट यस ठाउँमा फर्काई ल्याउनु हुनेछ।",
			"7": "<sup>7</sup> “हनन्याह तर मैले भनेका कुराहरू सुन। मैले भनेका कुराहरू तिमीहरू सबै मानिसहरूले सुन।",
			"8": "<sup>8</sup> हनन्याह त्यहाँ तिमी र म अगमवक्ता हुन भन्दा धेरै अघि नै अगमवक्ताहरू थिए। तिनीहरूले युद्ध, अनिकाल, भयानक रूढी धेरै देशहरू र महान राज्यहरूमा आउनेछ भनेर प्रचार गरेका थिए।",
			"9": "<sup>9</sup> तर यदि एकजना अगमवक्ताले शान्तिको अगमवाणी दिन्छ र उसको अगमवाणी सत्य हुन्छ तब मानिसहरूले जान्ने छन् कि परमप्रभुले उसलाई नै पठाउनु भएको हो।”",
			"10": "<sup>10</sup> यर्मियाले आफ्नो काँधमा जूवा भिरि रहेका थिए। तब अगमवक्ता हनन्याहले यर्मियाको काँधबाट जूवा निकाली दिए। हनन्याहले त्यो जुवा भाँचिदिए।",
			"11": "<sup>11</sup> तब हनन्याह अरू जम्मै मानिसहरूको उपस्थितिमा जोरले कराए अनि भने, “त्यो यो हो जुन परमप्रभु भन्नुहुन्छ, ‘त्यसरी नै नबूकदनेस्सर बाबेलका राजाको जुवा म सबै जातिहरूको काँधबाट दुइ बर्ष भित्रमा भाँचि दिन्छु।’”\nत्यस पछि अगमवक्ता यर्मियाले परमप्रभुको मन्दिर छोडे अनि बाहिर गए।",
			"12": "<sup>12</sup> जब हनन्याहले अगमवक्ता यर्मियाको काँधबाट जुवा निकालेर भाँचिदिए परमप्रभुको वचन यर्मियालाई आयो।",
			"13": "<sup>13</sup> परमप्रभुले यर्मियालाई भन्नुभयो, “जाऊ अनि परमप्रभुले जे भन्नुहुनछ हनन्याहलाई भन, ‘तिमीले काठको जुवाहरू भाँचिदियौ। तर म काठको सट्टामा फलामको जुवाहरू बनाउनेछु।",
			"14": "<sup>14</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, म सारा जातिहरूका काँघमा फलामको जुवा लगाइदिनेछु ताकि तिनीहरूले बाबेलका राजा नबूकदनेस्सरको सेवा गरून्। तिनीहरू राजा नबूकदनेस्सरका दास-दासीहरू हुनेछन्। म उसलाई जङ्गली जनावर माथि पनि राज गर्ने क्षमता प्रदान गर्नेछु।’”",
			"15": "<sup>15</sup> तब अगमवक्ता यर्मियाले अगमवक्ता हनन्याहलाई भने, “सुन हनन्याह! परमेश्वरले तिमीलाई पठाएको होइन। तर तिमीले यहूदाका मानिसहरूलाई झूटो कुरामा विश्वास गर्नलगायौ।",
			"16": "<sup>16</sup> यसकारण परमप्रभु यसो भन्नुहुन्छ, ‘चाँडै म तिमीलाई यस पृथ्वीबाट हटाई दिनेछु, हनन्याह। तिमी यही बर्ष मर्नेछौ। किनभने तिमीले मानिसहरूलाई परमेश्वरको विरूद्ध भड्कायौ।’”",
			"17": "<sup>17</sup> अगमवक्ता हनन्याह त्यही बर्षको सातौं महीनामा मरे।"
		},
		"29": {
			"1": "<sup>1</sup> बाबेलका कैदीहरूलाई एक पत्र\nअगमवक्ता यर्मियाले यरूशलेमबाट बूढा-प्रधानहरू, पूजाहारीहरू, अगमवक्ताहरू अनि यहूदाका सबै मानिसहरू जसलाई नबूकदनेस्सरले यरूशलेमबाट बाबेलमा ल्याएका थिए तिनीहरूलाई एउटा पत्र पठाए।",
			"2": "<sup>2</sup> (यो पत्र राजा यहोयाकीन, राजमाता, नपुंसकहरू अनि यहूदा र यरूशलेमका शासकहरू, कारीगरहरू, लोहारहरू जसलाई यरूशलेमबाट ल्याएका थिए सबैलाई पठाए।)",
			"3": "<sup>3</sup> यहूदाका राजा सिदकियाहले शापानका छोरो एलासा र हिल्कियाहका छोरो गर्मयाहलाई बाबेलका राजा नबूकदनेस्सरकहाँ पठाए। यर्मियाले ती मानिसहरूलाई बाबेलमा पत्र लैजानु भने। पत्रको अर्न्त विषय यस्तो थियो:",
			"4": "<sup>4</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वरले यरूशलेम देखि बाबेल सम्म कैद भएका ती मानिसहरूलाई यी कुराहरू भन्नु भएको थियो",
			"5": "<sup>5</sup> “घरहरू बनाऊ र त्यसमा बस्। बारीहरू बनाऊ र बिरूवाहरू रोप र फलहरू खाऊ।",
			"6": "<sup>6</sup> विवाह-बारी गरेर छोरा-छोरीहरू जन्माऊ। आफ्ना छोराहरूको लागि बुहारीहरू रोज। त्यसो गर्दा तिनीहरूले पनि छोरा-छोरीहरू जन्माउनेछन्। त्यहाँ सन्तान बढाऊ, नघटाऊ।",
			"7": "<sup>7</sup> शहरमा पनि असल कार्यहरू गर जसका निम्ति मैले तिनीहरूलाई पठाएँ। जहाँ तिमीहरू बसिरहेकाछौ त्यस शहरका निम्ति परमप्रभुसित प्रार्थना गर। किनभने यदि त्यहाँ शान्ति छाए तिमीहरू पनि शान्तिसित बस्नु सक्छौ।”",
			"8": "<sup>8</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वरले भन्नुहुन्छ “तिमीहरूका अगमवक्ताहरू र मंत्र-तंत्रगर्नेहरूले तिमीहरूलाई धोका नदिऊन्। तिनीहरूले देखेका सपनाका कुराहरूमा ध्यानै नदेऊ।",
			"9": "<sup>9</sup> तिनीहरूले मेरो नाउँमा झूटो अगमवाणी कहिरहेका छन्। अनि तिनीहरूले भनिरहेका छन्। तर मैले तिनीहरूलाई पठाएको होइन।” यो सन्देश परमप्रभुबाट आएको छ।",
			"10": "<sup>10</sup> परमप्रभु यो भन्नुहुन्छ “बाबेल सत्तरी बर्ष सम्म शक्तिशाली रहनेछ। त्यसपछि म तिमीहरूकहाँ आउनेछु र तिमीहरूलाई यस ठाउँमा फर्काई ल्याउने मेरो प्रतिज्ञा पूरा गर्नेछु।",
			"11": "<sup>11</sup> म यसो भन्छु किनभने तिमीहरूका निम्ति मैले बनाएका योजनाहरू म जान्दछु।” यो परमप्रभुबाट आएको सन्देश हो, “मसँग तिमीहरूको लागि राम्रो योजनाहरू छ। म तिमीहरूलाई चोट पुर्याउने योजना बनाउँदिन। मैले तिमीहरूलाई आशा र असल भविष्य दिने योजना गरेको छु।",
			"12": "<sup>12</sup> त्यसपछि तिमीहरूले मलाई बोलाउनेछौ, मकहाँ आऊ अनि मलाई प्रार्थना गर। म तिमीहरूलाई प्रार्थनाको उत्तर दिनेछु।",
			"13": "<sup>13</sup> तिमीहरूले मलाई खोज्नेछौ। अनि जब तिमीहरू आफ्ना तनमन लगाएर मलाई खोज्नेछौ, तिमीहरूले मलाई पाउनेछौ।",
			"14": "<sup>14</sup> म तिमीहरूलाई भेट गर्न अनुमित दिनेछु।” यो परमप्रभुबाट आएको सन्देश हो “म तिमीहरूलाई कैदबाट छुटाई ल्याउनेछु। म तिमीहरूलाई सबै जाति-जातिहरू र ठाउँ-ठाउँहरूदेखि भेला गर्नेछु जहाँ मैले तिमीहरूलाई पठाएको थिएँ।” यो परमप्रभुबाट आएको सन्देश हो। “अनि तिमीहरूलाई यस ठाँउमा फर्काई ल्याउनेछु।”",
			"15": "<sup>15</sup> तिमीहरूले भनेका छौ, “तर परमप्रभुले हामीलाई यहाँ बाबेलमा अगमवक्ताहरू दिनु भएको छ।”",
			"16": "<sup>16</sup> तर तिमीहरूका आफन्तहरू जो बाबेलमा लागिएका थिएनन् तिनीहरूको विषयमा परमप्रभु यी कुराहरू भन्नुहुन्छ। म अहिले त्यो राजा जो दाऊदको राजसिंहासनमा बसेका छन् र ती मानिसहरू जो अझ सम्म यरूशलेम शहरमा छन् तिनीहरूको विषयमा भन्दैछु।",
			"17": "<sup>17</sup> सेनाहरूका परमप्रभु भन्नुहुन्छ, “म तरवार, अनिकाल, र भयानक रूढीहरू ती मानिसहरूका विरूद्ध पठाउँदैछु जो यरूशलेममा बसिरहेका छन्। तिनीहरूलाई खान नसकिने कुहेका नेभाराहरू जस्ता बनाउने छु।",
			"18": "<sup>18</sup> म तिनीहरूलाई तरवार, अनिकाल अनि भयानक रूढीहरूले खेद्नेछु। म तिनीहरूलाई पृथ्वीका राजधानीहरूको सामुन्ने डरलाग्दो दृश्य बनाइदिनेछु, समस्त जातिहरू जहाँ म तिनीहरूलाई पठाउँछु त्यहाँ श्राप, डर, घृणा, खिसी र निन्दाको पात्र बनाउनेछु।",
			"19": "<sup>19</sup> यरूशलेममा बस्ने ती मानिसहरूले मेरो कुराहरू प्रति ध्यान नँदिंदा मैले तेस्तो अनिष्ट कुराहरू तिनीहरू प्रति हुन दिएको हो।” यो परमेश्वरबाट आएको सन्देश हो। “किनभने तिनीहरूले मेरा वचनहरू सुनेनन् जब मैले सेवक, अगमवक्ताहरू घरिघरि तिनीहरूकहाँ पठाएँ। तिनीहरूले कुनै ध्यान नै दिएनन्।” मेरो सन्देश परमप्रभुबाट आएको हो।",
			"20": "<sup>20</sup> “तिमीहरू कैदीहरू हौ। मैले तिमीहरूलाई यरूशलेमबाट बाबेल जानलाई वाध्य पारें। यसकारण परमप्रभुबाट आएको सन्देशलाई ध्यान दिएर सुन।”",
			"21": "<sup>21</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर कोलायाहको छोरो आहाब र मासेयाहको छोरो सिदकियाहको बारेमा भन्नुहुन्छ “यी दुइ जना मानिसहरू मेरो नाउँमा तिमीहरूकहाँ झूटो प्रचार गरिरहेका छन्। यसर्थ दुवै अगमवक्ताहरूलाई बाबेलका राजा नबूकदनेस्सरलाई म सुम्पने छु अनि उनले तिनीहरूलाई तिमीहरूकै सामुन्ने मार्नेछन्।",
			"22": "<sup>22</sup> सम्पूर्ण यहूदी कैदीहरूले तिनीहरूलाई श्राप दिनेछन्। ‘परमप्रभुले तिमीहरूलाई सिदकियाह र आहाबलाई जस्तै व्यवहार गरून् जसलाई बाबेलका राजाले आगोमा डढाएको थियो।’",
			"23": "<sup>23</sup> ती दुवैजना अगमवक्ताहरूले इस्राएलमा खराब कामहरू गरे। तिनीहरूले आफ्ना छिमेकीकी पत्नीहरूसँग व्यभिचार गरे अनि तिनीहरूले मेरो नाउँमा झूटो बोले। म जान्दछु तिनीहरूले के गरे। म आफैं साक्षी छु।” मैले तिनीहरूलाई त्यसो गर भनेर आज्ञा दिएको छैन।",
			"24": "<sup>24</sup> शमायाहलाई परमप्रभुको सन्देश\nनेहेलामबाट शमायाहलाई तिमीले भन्नु पर्छ।",
			"25": "<sup>25</sup> सेनाहरूका परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ, “शमायाह, तिमीले आफ्नै नाउँमा यरूशलेममा रहेका सबै मानिसहरूलाई चिट्ठी पठाएका छौ तिमीले मासेयाहको छोरो सपन्याहलाई चिट्ठीहरू पठायौ। तिमीले बाँकी सबै पूजाहारीहरूलाई पनि चिट्ठीहरू पठायौ।",
			"26": "<sup>26</sup> शमायाह, तिमीले सपन्याहलाई चिट्ठीमा भनेकाछौ, ‘सपन्याह, परमेश्वरले तिमीलाई यहोयादाको ठाउँमा पूजाहारी बनाएका छौ। तिमीले परमप्रभुको मन्दिरको परिदर्शन गर्नु पर्छ अनि जसले पागल झैं कर्म गर्छ र अगमवाणी दिन्छ त्यसता कुनै पनि मानिसलाई पक्रनु पर्छ। तिमीले त्यसलाई ठिँगुरोमा ठोक्नु पर्छ र उसको घिच्रोमा साङ्गलोले बाँध्नु पर्छ।",
			"27": "<sup>27</sup> अहिले यर्मियाले अगमवक्ता जस्तो व्यवहार गरिरहेछ। यसर्थ उसलाई तिमी किन नपक्रने?",
			"28": "<sup>28</sup> यर्मियाले यसो भन्दै यो समाचार हामीलाई बाबेलमा पठाएका थिए, तिमीहरू बाबेलमा धेरै समय सम्म बस्नेछौ। यसकारण घरहरू बनाऊ र त्यही बस्। जमीन कमाऊ र त्यसका उब्जनी खाऊ।’”",
			"29": "<sup>29</sup> पूजाहारी सपन्याह पूजाहारीले यर्मियालाई पत्र पढेर सुनाए।",
			"30": "<sup>30</sup> तब परमेश्वरबाट यर्मिया कहाँ वचन आई पुग्यो",
			"31": "<sup>31</sup> “यर्मिया, यो सन्देश बाबेलमा रहेका कैदीहरूलाई पठाऊ यसकारण नहेलाम परिवारका शमायाहको बारे परमप्रभु यसो भन्नुहुन्छ, ‘शमायाहले तिमीलाई अगमवाणी दिएको छ तर मैले उसलाई पठाएको होइन। शमायाहले तिमीहरूलाई झूटा कुराहरूमा भरोसा गरायो।",
			"32": "<sup>32</sup> यसकारण परमप्रभु भन्नुहुन्छ म उसको परिवार र उसलाई सजाय दिनेछु। म पूर्णरूपले उसको परिवारलाई नष्ट पारि दिनेछु। अनि उसले असल कुराहरूमा जुन म मेरो मानिसहरूका निम्ति गर्नेछु देख्नु पाउने छैन।’” यो परमप्रभुको सन्देश हो “‘म शमायाहलाई दण्ड दिनेछु किनभने उसले मानिसहरूलाई परमप्रभुको विरूद्ध भडकाएको छ।’”"
		},
		"30": {
			"1": "<sup>1</sup> आशाका वचनहरू\nपरमेश्वरबाट यो वचन यर्मियाकहाँ आएको हो।",
			"2": "<sup>2</sup> परमप्रभु इस्राएलका परमेश्वरले भन्नुभयो “यर्मिया, मैले भनेका सबै कुराहरू एउटा पुस्तकमा लेख।",
			"3": "<sup>3</sup> यसो गर किनभने समय आउनेछ जब म यहूदा र इस्राएलका मेरा मानिसहरूलाई कैदबाट छुटाउनेछु अनि तिनीहरूलाई त्यस देशमा ल्याउनेछु जुन मैले तिनीहरूका पिता-पुर्खाहरूलाई दिएको थिएँ। तब मेरो मानिसहरू यो देशमा मालिक बनिनेछन्।”",
			"4": "<sup>4</sup> परमेश्वरले यो सन्देश इस्राएल अनि यहूदाका मानिसहरूका बारेमा भन्नु भएको हो।",
			"5": "<sup>5</sup> उहाँले भन्नुभयो,\n“मानिसहरू डरले रोएका हामीले सुनिरहेका छौ।\nमानिसहरू तर्सेका छन्, त्यहाँ शान्ति छैन।",
			"6": "<sup>6</sup> “यो प्रश्न सोध, र यसलाई हेर:\nके एकजना पुरूषले नानी जन्माउनु सक्छ? निश्चय सक्तैन।\nतब म किन प्रत्येक लोग्ने मानिसलाई भूँडीमाथि हात राखी निहुरिएर,\nप्रसव पीडामा परेकी स्त्री भूँडीमाथि हात राखी बसेकोझैं झैं देख्छु।\nकिन हरेक मानिसको अनुहार मरेको मानिस झैं पहेंलो भएको छ?",
			"7": "<sup>7</sup> “हाय! त्यो आउने समय कति डरलाग्दो हुनेछ!\nकुनै समय पनि त्यस्तो डरलाग्दो हुने छैन।\nयाकूबको निम्ति यो अति दु:खको दिन हुनेछ,\nतर याकूबलाई बचाइनेछ।",
			"8": "<sup>8</sup> “त्यस समयमा,” परमप्रभु सर्व-शक्तिमान परमेश्वर भन्नुहुन्छ, “म तिमीहरूको काँधबाट जुवाहरू निकालिदिन्छु अनि भाँचिदिनेछु। तिमीहरूलाई बाँधेको बन्धन फुकाइदिनेछु। बिदेशीहरूले अब कहिल्यै तिनीहरूलाई दास बनाउनेछैन।",
			"9": "<sup>9</sup> तिनीहरूले परमप्रभु आफ्ना परमेश्वरलाई अनि तिनीहरूका राजा दाऊदलाई सेवा गर्नेछन्। म त्यो तिनीहरूको राजा पठाइदिनेछु।",
			"10": "<sup>10</sup> “यसकारण हे याकूब मेरो दास! नडराऊ।”\nयो परमप्रभुबाट आएको सन्देश हो।\n“हे इस्राएल! नडराऊ!\nम तिमीलाई ती सुदूरका देशहरूबाट\nजोगाएर ल्याउँछु\nअनि तिमीहरूका सन्तानहरूलाई\nकैदी बनाउने देशहरूबाट जोगाउनेछु।\nयाकूब फर्कनेछ,\nअनि फेरि शान्ति पाउनेछ\nर मेरा मानिसहरूलाई भय देखाउनु शत्रु रहेनछैन।",
			"11": "<sup>11</sup> हे इस्राएल र यहूदका मानिसहरू,\nम तिमीहरूसँग छु।”\nयो परमेश्वरबाट आएको सन्देश हो।\n“म तिमीहरूको रक्षा गर्नेछु।\nमैले तिमीहरूलाई जुन देशहरूमा तितर-बितर पारें\nती सब नष्ट पार्नेछु,\nतर तिमीहरूलाई नष्ट पार्दिन।\nयद्यपि तिमीहरूले तिमीहरूको अनिष्ट कर्मको लागि,\nसजाय भोग्ने पर्नेछ,\nतर म तिमीहरूलाई ठीकसित अनुशासित पार्ने छु।”",
			"12": "<sup>12</sup> परमप्रभु परमेश्वर भन्नुहुन्छ,\n“तिमीहरूको घाउ गहिरो र जाति हुनलाई निकै चर्को पर्छ।",
			"13": "<sup>13</sup> तिमीहरूका निम्ति बोलिदिने कोही छैन।\nतिमीहरूको घाउ निको पार्न कुनै औषधी पाँउदैनौ।",
			"14": "<sup>14</sup> तिमीहरूका प्रेमीहरूले\nतिमीहरूलाई भूलेका छन्,\nअनि अहिले तिमीहरूको चिन्ता गर्दैनन्।\nम तिमीहरूलाई शत्रु सरह घात पुर्याउनेछु।\nतिमीहरूका धेरै दुष्टकर्महरू\nर अनगन्ती पापहरूको कारणले\nम तिमीहरूलाई कडा दण्ड दिनेछु।",
			"15": "<sup>15</sup> इस्राएल र यहूदा तिमीहरूका कष्टदायक,\nघाउका निम्ति किन रोइरहेका छौ?\nत्यसको कुनै उपचार नै छैन।\nमैले तिमीहरूको धेरै दुष्ट कार्यहरू,\nर अनगन्ती पापहरूको कारणले,\nतिमीहरूलाई यस्तो पारेको छु।",
			"16": "<sup>16</sup> यसकारण ती सबै ज-जसले तिमीहरूलाई\nनष्ट पार्छन् तिनीहरूलाई पनि नष्ट परिनेछ।\nतिम्रा शत्रुहरू सबैलाई कैद गराइन्छ।\nती जाति-जातिहरू ज-जसले तिमीहरूलाई नष्ट बनाएका छन्।\nतिनीहरू आफै नष्ट हुनेछन्।\nती सबै ज-जसले तिमीहरूलाई लुटेका छन्\nअरूले तिनीहरूलाई लुटनेछन्।",
			"17": "<sup>17</sup> अनि म तिमीहरूका स्वास्थ्य निको पारिदिनेछु।\nअनि म तिमीहरूका घाउ निको पार्ने छु” यो परमप्रभुको सन्देश हो,\n“किनभने अन्य मानिसहरूले तिमीहरूलाई कसैले पनि सियोनको वास्ता गर्दैन भन्दै\nबहिष्कार गरिएको भनेका छन्।”",
			"18": "<sup>18</sup> परमप्रभु भन्नुहुन्छ:\n“हेर, याकूबका मानिसहरूको भाग्य पुर्नहाल गर्न म जाँदैछु।\nडेराहरूप्रति दयाममा दया नगर।\nमेरो याकूबको पालहरूमाथि दया रहने छ।\nध्वंश भएको ठाउँमा शहर फेरि बनिनेछ।\nअनि राजाको महल ठीक ठाउँमा नै बनिनेछ।",
			"19": "<sup>19</sup> तिनीहरूबाट धन्यवादको स्तुतिगान,\nर आनन्दको स्वर गुन्जिनेछ।\nम तिमीहरूलाई धेरै छोरा-छोरीहरूका निम्ति,\nआशीर्वाद दिनेछु।\nतिमीहरू कम्ती हुनेछैनन्।\nम तिनीहरूमा गौरव ल्याउनेछु\nर तिनीहरूलाई कैसेले तिरस्कार गर्नु पाउने छैनन्।",
			"20": "<sup>20</sup> तिनीहरूका बाल-बच्चाहरूले उहिलेका जस्तो समय पाउनेछन्।\nतिनीहरूको समुदाय मेरो अघि स्थापित हुनेछ।\nजसले तिनीहरूमाथि अत्याचार गर्छ,\nम तिमीहरूलाई दण्ड भोगाउनेछु।",
			"21": "<sup>21</sup> तिनीहरू मध्येकै एक मानिस तिनीहरूका शासक हुनेछ।\nत्यो मानिस उसका मानिसहरूबाट हुनेछ।\nम उसलाई मेरो नजिक ल्याउनेछु\nर ऊ मेरो नजिक आउनेछ।\nयदि मैले उसलाई नबोलाएँ कसले मेरो अघि आउने सहास गर्छ,\nपरमप्रभु भन्नुहुन्छ।",
			"22": "<sup>22</sup> तिमीहरू मेरो प्रजा हुनेछौ।\nअनि म तिमीहरूका परमेश्वर हुनेछु।”",
			"23": "<sup>23</sup> हेर! परमप्रभुको आँधी!\nउहाँको क्रोध बाहिर जान्छ!\nयो गर्जिरहेको आँधी हो!\nत्यो दुष्ट मानिसको शिरमा बज्रिनेछ।",
			"24": "<sup>24</sup> परमप्रभुको क्रोध तबसम्म शेष हुदैन\nजबसम्म उहाँले गर्नु भएको योजना सम्पन्न गर्नु हुन्न।\nत्यो दिन कहिले आउनेछ।\nपछिका दिनहरूमा तिमीहरूले यो सब बुझ्नेछौ।"
		},
		"31": {
			"1": "<sup>1</sup> नयाँ इस्राएल\nपरमेश्वरले यी कुराहरू भन्नुभयो, “त्यस बेला म इस्राएलका घरनामा परमप्रभु हुनेछु। अनि तिनीहरू मेरो प्रजा हुनेछन्।”",
			"2": "<sup>2</sup> परमप्रभुले यी कुराहरू भन्नुभयो:\n“मानिसहरू जो शत्रुको तरवारबाट जोगिएका छन्,\nतिनीहरूले मरुभूमिमा पनि विश्राम पाउँनेछन्\nअनि इस्राएल त्यहाँ शान्तिको खोजीमा जानेछ।”",
			"3": "<sup>3</sup> टाढाबाट परमप्रभु आफ्ना मानिसहरू अघि\nदेखा पर्नु हुन्छ।\nपरमप्रभु भन्नुहुन्छ, “मैले तिमीहरूलाई त्यस्तो प्रेमले प्रेम गरें,\nजुन सर्वदा रहीरहन्छ।\nयसर्थ मैले तिमीहरूलाई,\nमेरो विश्वासयोग्यता कायम राखेको छु।",
			"4": "<sup>4</sup> हे कुमारी इस्राएल! म तिमीलाई पुन:र्निर्माण गर्नेछु।\nतिमी पुन एक राजधानी हुनेछौ।\nतिमीहरूले फेरि आफ्नो खैंजडी उठाउने छौ,\nअनि तिमीहरू अन्य मानिसहरूसँग नाच्नेछौ।",
			"5": "<sup>5</sup> तिमीहरूले दाखरसको खेती\nफेरि सामरियाको पहाड वरिपरि लाउनेछौ।\nजसले खेती लाउनेछन् तिनीहरूले दाखको फल खानेछन्।",
			"6": "<sup>6</sup> त्यहाँ त्यस्तो समय हुने छ जब चौकीदारले यो सन्देश जोडले भन्ने छ,\n‘आऊ, सियोन माथितिर जाँऊ परमप्रभु हाम्रो परमेश्वरलाई पूज्नको लागि।’\nएप्रैमको पहाडी देश सम्मका चौकीदाहरूले त्यो सन्देश जोडले भन्नेछन्।”",
			"7": "<sup>7</sup> परमप्रभु भन्नुहुन्छ:\n“आनन्दित हौ अनि याकूबका निम्ति गाऊ।\nइस्राएलको राज्यहरूमा महानताका निम्ति हल्ला गर।\nप्रशंसाको गीत गाऊ अनि यसो भन्दै चिच्याऊ\n‘हे परमप्रभु! तपाईंका मानिसहरू जो इस्राएलबाट जीवित छोडिएका छन्\nतिनीहरूलाई सुरक्षा गर्नु होस्।’",
			"8": "<sup>8</sup> हेर, म तिनीहरूलाई उत्तरबाट\nर टाढो-टोढो देशहरूबाट ल्याउनेछु\nअनि भेला पार्नेछु।\nतिनीहरू मध्ये कतिपय मानिसहरू अन्धा र लङ्गडाहरू छन्।\nकतिपय स्त्रीहरू गर्भवती छन्,\nअनि तिनीहरू मध्ये कति जना प्रसव पीडामा छन्।\nतर धेरै-धेरै मानिसहरू फर्की आउनेछन्।",
			"9": "<sup>9</sup> तिनीहरू रूँदै आउनेछन्।\nतर म तिनीहरूलाई सान्त्वना दिनेछु र पानीको झरनातिर डोर्याई लानेछु।\nम तिनीहरूलाई सोझो बाटो डोर्याउनेछु\nताकि तिनीहरू लड्ने छैनन्।\nम तिनीहरूलाई यस्तो पथ डोर्याउँने छु\nकिनभने इस्राएलको निम्ति म पिता हुँ,\nअनि एप्रैम मेरो जेठो छोरो हो।",
			"10": "<sup>10</sup> “हे जातिहरू हो, परमप्रभुबाट आएको वचनलाई ध्यान लगाएर सुन\nयो सन्देश समुद्र, उत्तर र सुदूर भूमिहरूमा फिँजाऊ,\n‘परमप्रभुले इस्राएलका मानिसहरूलाई जताततै तितर-बितर पार्नु भएकोछ,\nतर उहाँले उनीहरूलाई फेरि भेला गरेर ल्याउनु हुनेछ\nर भेंडा़ गोठालाले आफ्नो भेंडा़ बथानहरू सुरक्षा गरे झैं सुरक्षा गर्नुहुनेछ।’",
			"11": "<sup>11</sup> परमप्रभुले याकूबलाई मोल तिरेर उकास्नु हुन्छ\nर याकूब भन्दा बलियाहरूबाट उद्धार पार्नु हुन्छ।",
			"12": "<sup>12</sup> इस्राएलका मानिसहरू सियोनको टाकुरामा आउने छन् र खुशीले चिच्याउने छन्।\nतिनीहरूका अनुहारहरू परमप्रभुको उदारताले चम्किनेछ।\nअसल कुराहरूका बारे जुन परमेश्वरले तिनीहरूलाई दिनुहुन्छ।\nपरमेश्वरले तिनीहरूलाई अन्न, नयाँ दाखरस,\nभद्राक्षेको तेल, थुमाहरू र गाई-गोरूहरू दिनु हुनेछ।\nतिनीहरूको हृदय पानीले भिजेको बारी जस्तो हुनेछ।\nइस्राएलका मानिसहरूलाई संकष्टहरूले अब पिरोलिने छैनन्।",
			"13": "<sup>13</sup> तब इस्राएलका तरूणीहरू आनन्दले मग्न हुँदै नाँच्नेछन्।\nअनि जवान र बूढा मानिसहरू त्यो नाचगानमा सम्मिलित हुने छन्।\nम तिनीहरूका उदासीपनलाई हर्ष र अनन्दामा परिवर्तन गरिदिन्छु।\nम इस्राएलका मानिसहरूलाई शोकको सट्टामा आनन्द र सान्त्वना दिनेछु।",
			"14": "<sup>14</sup> म पूजाहारीहरूलाई असल असल कुराहरूले परिपूर्ण पार्नेछु।\nमेरा मानिसहरू मैले दिएका उपहारहरूले सन्तुष्ट हुनेछन्।”\nपरमप्रभुले यी कुराहरू भन्नुभयो।",
			"15": "<sup>15</sup> परमप्रभु भन्नुहुन्छ:\n“रामामा एउटा चर्को रूवाई शोकको आवाज सुनियो।\nएकदमै नराम्रो रूवाई र दुःखको आवाज।\nराहेल आफ्ना नानीहरूका लागि रूनेछिन,\nराहेललाई सान्त्वना दिनु सकिदैन,\nकिनभने उनका नानीहरू मरेका छन्।”",
			"16": "<sup>16</sup> तर परमप्रभु भन्नुहुन्छ “रून बन्द गर,\nर आफ्ना आँखाहरू आँसुले नभर।\nतिमीहरू तिमीहरूको कामको निम्ती पुरष्कृत हुनेछौ।\nकिनभने तिम्रो काम फलवन्त हुनेछ।\nतिनीहरू आफ्ना शत्रुको देशबाट फर्किनेछन्।",
			"17": "<sup>17</sup> तिमीहरूको आशा छ।”\nयो परमप्रभुबाट आएको सन्देश हो।\n“तिमीहरूका नानीहरू आफ्नै देशमा फर्कनेछन्।",
			"18": "<sup>18</sup> मैले एप्रैमले यसो भन्दै रोएको सुनेकोछु।\n‘हे परमप्रभु तपाईंले साँच्चि नै मलाई दण्ड दिनुभयो,\nअनि मैले शिक्षा पाएँ।\nम बाछा जस्तै थिएँ, जसलाई सिकाउनु सकिदैन।\nमलाई फर्काई ल्याउनु होस्,\nअनि म तपाईंकहाँ फर्की आउनेछु।\nतपाईं मेरो लागि साँच्चि कै परमप्रभु परमेश्वर हुनुहुन्छ।",
			"19": "<sup>19</sup> परमप्रभु, म तपाईं देखि भडकिएर टाढा भएँ।\nतर आफूले गरेका अनिष्ट कुराहरूबाट मैले शिक्षा पाएँ।\nयसकारण मैले मेरो हृदय र जीवन बद्लिएँ\nआफू युवावस्थामा हुँदा आज्ञानतावश गरेको कामहरूले गर्दा\nम लज्जित र व्याकुल भएँ।’”",
			"20": "<sup>20</sup> परमप्रभु भन्नुहुन्छ,\n“तिमीहरू जान्दछौ कि एप्रैम मेरो प्यारो छोरो हो,\nम त्यो बालकलाई विशेष प्रेम गर्छु,\nहो, मैले प्राय एप्रैमलाई निन्दा गरें,\nतर म अझ उसैको बारेमा सोच्दछु,\nम उसलाई साँच्चै माया गर्छु।\nअनि म साँच्चि नै उसलाई सान्त्वना दिन चाहनन्छु।”",
			"21": "<sup>21</sup> “बाटो देखाउने चिन्हहरू र दिशा निर्देशकहरू ठीक गर।\nघरतिर डोर्याउने बाटोमा चिन्ह लगाऊ।\nबाटो नियाल।\nआफूले छाडेका बाटे याद गर।\nहे कन्ये इस्राएल! घर फर्क।\nआफ्ना शहरहरू तिर फर्केर आऊ।",
			"22": "<sup>22</sup> हे अविश्वासी छोरी,\nकहिलेसम्म तिमी मबाट यता उता गरिरहन्छौ?\n“परमेश्वरले पृथ्वीमा एउटा नयाँ कुरो सृजना गर्नुहुन्छ,\nएक स्त्रीले लोग्ने मानिसलाई सुरक्षा दिनेछे।”",
			"23": "<sup>23</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वरले भन्नुहुन्छ “म ती मानिसहरूलाई फर्काई ल्याउनेछु, जसलाई कैदी बनाएर टाढा लगिएका थिए। त्यस बेला, यहूदाको देशमा भएका मानिसहरू अनि यसका शहरहरूले पुन: एकपल्ट यी शब्दहरू प्रयोग गर्नेछन्। ‘हे धार्मिकताको वासस्थान, हे पवित्र पर्वत, परमेश्वरको आशिष तिमीहरूमा रहोस्।’",
			"24": "<sup>24</sup> “मानिसहरू यहूदामा र यसका शहरहरूमा एक साथ बस्नेछन्, खेतीवाल अनि जो आफ्नो भेंडा़का बथानहरूसित वरिपरि घुमिहिँड्छन्।",
			"25": "<sup>25</sup> थाकेकाहरूलाई म आराम र कमजोरहरूलाई बल दिनेछु।”",
			"26": "<sup>26</sup> यो सुन पछि म, उठेर चारैतिर हेरे। त्यो निद्रा आनन्दमय थियो।",
			"27": "<sup>27</sup> “हो दिनहरू आईरहेछ,” यो परमेश्वरको सन्देश “जब म इस्राएल र यहूदाका परिवारलाई उन्नति साथ भनेर मद्दत गर्नेछु। म तिनीहरूका नानीहरू र पशु प्राणीहरू बढाउनेछु।",
			"28": "<sup>28</sup> बितेका दिनमा, मैले तिमीहरूलाई उखाल्न, तल झार्न, च्यात-च्युत पार्न, नष्ट पार्न र नोक्सान गर्न नियालें। तर अहिले तिनीहरूलाई निमार्ण गर्न र तिनीहरूको र जग बासाउनँ म तिनीहरूलाई नियाली रहेछु।” यो परमप्रभुबाट आएको सन्देश हो।",
			"29": "<sup>29</sup> “ती दिनहरूमा, मानिसहरूले भन्ने छैनन्:\n‘पिता-पुर्खाहरूले अमीलो अङ्गुर खाए,\nअनि नानीहरूले अमीलो स्वाद पाए।’",
			"30": "<sup>30</sup> तर हरेक मानिस आफ्नो दुष्टताको कारणले मर्नेछ। त्यो मानिस जसले अमीलो अङ्गुरहरू खाँदछ र अमीलै स्वाद पाउँदछ।”",
			"31": "<sup>31</sup> नयाँ करार\nपरमप्रभुले ती कुराहरू भन्नुभयो, “त्यो समय आईरहेछ जब म यहूदा र इस्राएलका परिवारहरूसँग नयाँ करार स्थापित गर्नेछु।",
			"32": "<sup>32</sup> यो उनीहरूका पिता-पुर्खाहरूसँग मैले गरेको करार जस्तो हुनेछैन। मैले त्यसबेला त्यो करार गरेको थिएँ जब मैले तिनीहरूलाई तिनीहरूको हात समातेर मिश्रबाट निकाली ल्याएको थिएँ। म तिनीहरूका मालिक थिएँ तापनि तिनीहरूले मेरो करार भङ्ग गरेका थिए।” यो सन्देश परमप्रभुको थियो।",
			"33": "<sup>33</sup> “म भविष्यमा यही करार इस्राएलका मानिसहरूसित गर्नेछु। म मेरो शिक्षाहरू तिनीहरूका विवेकमा राख्नेछु अनि तिनीहरूका हृदयमा लेख्नेछु। म तिनीहरूका परमप्रभु हुनेछ अनि तिनीहरू मेरा मानिसहरू हुनेछन्।",
			"34": "<sup>34</sup> तिनीहरूका शाखा-सन्तानहरू र छिमेकीहरूलाई परमेश्वरको बारेमा सिकाउनु पर्दैन् किनभने मानिस ठूलो होस् अथवा सानो, प्रत्येकले मलाई चिन्छ।” यो परमप्रभुबाट आएको सन्देश हो। “म तिनीहरूको कुकर्महरू क्षमा गरिदिनेछु, म तिनीहरूका पापहरूलाई याद गर्ने छैन।”",
			"35": "<sup>35</sup> परमप्रभुले इस्राएललाई कहिल्यै त्याग्नु हुने छैन\nउहाँले दिनमा सूर्य उदाउने गराउनु हुन्छ,\nअनि रातमा चन्द्र र ताराहरू उदाउने गराउनु हुन्छ।\nउहाँले समुद्रलाई उत्तेजित र छालहरूलाई गर्जने पार्नु हुन्छ।\nसेनाहरूका परमप्रभु उहाँको नाउँ हो।\nपरमप्रभुले यी कुराहरू भन्नुभयो:",
			"36": "<sup>36</sup> परमप्रभुले यी कुराहरू भन्नुहुन्छ,\n“यदि मैले मेरो आफ्नो नियन्त्रण सूर्य, चन्द्र, तारा र समुद्रमाथि हटाएँ भने मात्र\nइस्राएलका भावी सन्तानहरू एक जातिमा परिणत हुन रोक्नेछन्।”",
			"37": "<sup>37</sup> परमप्रभु भन्नुहुन्छ “म इस्राएलका भावी सन्ताहनरूलाई कहिल्यै अस्वीकार गर्नेछैन।\nत्यो त्यसबेला मात्र घट्न सक्ला यदि मानिसहरूले माथि आकाश नाप्न सक्यो भने,\nअनि पृथ्वी मुनीका सारा रहस्यहरू जान्न सक्यो भने।\nत्यसबेला मात्र म इस्राएलका सन्तानहरूलाई,\nतिनीहरूले गरेको कुकर्महरूका निम्ति अस्वीकार गर्नेछु।”\nयो सन्देश परमेश्वरबाट आएको थियो।",
			"38": "<sup>38</sup> नयाँ यरूशलेम\nयो सन्देश परमप्रभुबाट आएको हो, “ती दिनहरू आईरहेछन् जब यरूशलेमको शहर परमप्रभुको लागि पुनःर्निर्माण गरिनेछ। हनानेलको धरहरादेखि कुनाको प्रवेशद्वारसम्म नै सम्पूर्ण शहर पुनःर्निर्माण गरिनेछ।",
			"39": "<sup>39</sup> जोखिने रेखा कुना त्यहाँदेखि सोझै गारेब डाँडासम्म जानेछ अनि त्यहाँबाट घुमेर गोर्हा नाउँका ठाउँसम्म जानेछ।",
			"40": "<sup>40</sup> समस्त बेंसी जहाँ लाशहरू र खरानी फ्याँकिन्छन् परमप्रभुको लागि पवित्र हुनेछ। पूर्वका सबै मैदानहरू किंद्रोन खोल्चाबाट घोडे ढोकाको कुनासम्म नै परमप्रभुका निम्ति पवित्र हुनेछ। त्यो भूमि फेरि कहिल्यै उखालिने र भत्काइने छैन।”"
		},
		"32": {
			"1": "<sup>1</sup> यर्मियाले नयाँ क्षेत्र किन्छ\nयहूदामा सिदकियाहको शासनकालको दशौं बर्षमा यो सन्देश परमप्रभुबाट यर्मियाहकहाँ आयो। सिदकियाहको दशौं बर्ष नबूकदनेस्सरको राज्यकालको अठारौं बर्ष थियो।",
			"2": "<sup>2</sup> त्यस बेला, बाबेलका राजाका सैन्याले यरूशलेमको शहर वरिपरि घेरा हालेर राखेका थिए अनि यर्मियालाई प्रहरीको चोकमा कैद गरिएको थियो जुन चोक यहूदाका राजाको महल थियो।",
			"3": "<sup>3</sup> यहूदाका राजा सिदकियाहले यर्मियालाई त्यस ठाउँमा यसो भन्दै थुनेका थिए “परमप्रभुले यो शहर बाबेलका राजालाई दिनुहुनेछ अनि उसले यसलाई नियन्त्रणमा राख्छ भनी तिमीले किन अगमवाणी गरेको?",
			"4": "<sup>4</sup> यहूदाका राजा सिदकियाह बाबेलका सैन्यदेखि बाँच्ने छैन। तर नि:सन्देह उसलाई बाबेलका राजाकहाँ सुम्पिइनेछ। अनि सिदकियाह बाबेलका राजा सँग आमने-सामने बोल्ने छ। सिदकियाहले उसलाई आफ्नै आँखाले देख्नेछ।",
			"5": "<sup>5</sup> बाबेलका राजाले सिदकियाहलाई बाबेलमा लैजानेछ। जहाँ मैले कार्वाही नगरून्जेल सम्म ऊ रहेनछ। यहूदाका सैन्यले बाबेलका सैन्यसित लडाइँ गर्नेछन् तर तिनीहरू सफल हुँदैनन्।” यो परमप्रभुबाट आएको सन्देश हो।",
			"6": "<sup>6</sup> कैदमा भएको बेला यर्मियाले भन्यो, “यो सन्देश परमप्रभुबाट मकहाँ आयो।",
			"7": "<sup>7</sup> ‘यर्मिया, तिम्रो काका शल्लूमको छोरो हनमेल तिमीकहाँ आउँदैछ। हनमेलले तिमीलाई एउटा प्रस्ताव दिनेछ कि “तिमीले अनातोत शहरको छेउमा भएको क्षेत्र किन्नु पर्नेछ कारण हनमेल उसको एकदम निकट सन्तान हो। अनि हनमेलको त्यो ठाँउ किन्ने जिम्मावारी र अधिकार तिम्रो हो।”’",
			"8": "<sup>8</sup> “तब परमप्रभुले भन्नु भए अनुसार नै घटना हुन गयो। मेरो काकाको छोरो हनमेल मकहाँ प्रहरीको चोकमा आयो। हनमेलले मलाई भन्यो, ‘यर्मिया, बिन्यामीन क्षेत्रभित्र अनातोत शहरको छेउमा भएको मेरो जमीन किन्नुहोस्। त्यो जमीन तपाईंकै लागि किन्नुहोस् यो किन्ने यसलाई विमुक्त पार्ने अधिकार तपाईंको हो। यो तपाईंकै लागि किन्नु होस्।’”\nयसर्थ मैले जाने कि यो सन्देश परमप्रभुबाट थियो।",
			"9": "<sup>9</sup> मैले मेरो काकाको छोरो हनमेलबाट अनातोतमा जमीन किनें। मैले उसको लागि सत्र शेकेल  चाँदी ओजन गरें।",
			"10": "<sup>10</sup> मैले जमीनको तमसुकमा सही गरें। अनि मोहर लगाए तमसुक मसित रह्यो। मैले कतिपय मानिसहरू त्यो जमीन किन्दा साक्षी बस्नेहरू पनि पाएँ। मैले चाँदीहरू मापदण्ड अनुसार ओजन गरें।",
			"11": "<sup>11</sup> तब तमसुक लिएँ दुवै व्यवहार र शर्तहरू सहित मोहर लगाएको अनि मोहर नलगाइएको तमसुकको प्रतिलिपि मैले लिएँ ।",
			"12": "<sup>12</sup> अनि मैले बारूकलाई दिएँ। बारूक नेरियाहको छोरो हो। मोहर लगाइएको तमसुकमा मैले जमीन किन्दा गरिएको सबै शर्तहरू समावेश थियो। जब मेरो काकाको छोरा हनमेल र अन्य मानिसहरू त्यहाँ थिए मैले बारूकलाई त्यो तमसुक दिएँ। ती साक्षीहरूले पनि तमसुकमा सही गरे। त्यहाँ चोकमा यहूदाका धेरै मानिसहरू थिए अनि तिनीहरूले मैले बारूकलाई तमसुक दिएको देखें।",
			"13": "<sup>13</sup> मैले ती सबै मानिसहरूकै समक्ष बारूकलाई भने,",
			"14": "<sup>14</sup> “सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, ‘तमसुकको दुवै प्रतिलिपिहरू मोहर लगाइएको अनि मोहर नलगाइएको तिनीहरूलाई माटोको भाँडामा राख यसो गर ताकि यो जमीनका तमसुकहरू लामो समय सम्म सुरक्षित हुनेछ।’",
			"15": "<sup>15</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वरले भन्नुहुन्छ ‘भविष्यमा मेरो मानिसहरूले फेरि एकपल्ट घरहरू खेत दाखका खेतहरू र यस बारीहरू किन्नेछन्।’”",
			"16": "<sup>16</sup> नेरियाहको छोरो बारूकलाई त्यो जमीनको तमसुक दिइसके पछि मैले परमप्रभुसँग प्रार्थना गरें:",
			"17": "<sup>17</sup> “हे परमप्रभु परमेश्वर, तपाईंले तपाईंको महान-शक्तिद्वारा अनि तपाईंले विस्तृत स्वर्ग र पृथ्वी बनाउनु भयो अनि तपाईंको लागि केही असम्भव छैन।",
			"18": "<sup>18</sup> हे परमप्रभु, तपाईं हजारौं मानिसहरूका लागि कृपालु हुनुहुन्छ, तर तपाईंले पिता-पुर्खाहरूको पापको दण्ड नानीहरूलाई दिनुभयो। हे महान एवं शक्तिशाली परमेश्वर, तपाईंको नाउँ सेनाहरूका परमप्रभु हो।",
			"19": "<sup>19</sup> तपाईंको विचार महान छ अनि महान कर्महरू गर्नुहुन्छ। हे परमप्रभु! तपाईंले मानिसहरूले जे गर्छन् त्यो हेर्नु हुन्छ अनि तिनीहरूको कर्म अनुसार फल दिनुहुन्छ।",
			"20": "<sup>20</sup> हे परमप्रभु! तपाईंले शक्तिशाली र मिश्र भूमिमा गर्नुभयो अहिले सम्म इस्राएलमा र अन्य मानिसहरूको बीचमा आश्चर्यचकित कर्महरू गरिरहनु भएको छ। तपाईं आफैंले आज प्रसिद्ध पार्नु भएकोछ।",
			"21": "<sup>21</sup> तपाईंले आफ्नो अलौकिक शक्ति प्रयोग गरी मिश्रबाट इस्राएलका मानिसहरू ल्याउनु भयो। तपाईंले यी कुराहरू तपाईंका शक्तिशाली हात विस्तृत पाखुरा अनि कार्यहरूद्वारा यस्तो गर्नुभयो।",
			"22": "<sup>22</sup> “परमप्रभु, तपाईंले यो देश इस्राएलका मानिसहरूलाई दिनुभयो। यो त्यही देश हो जुन तपाईंले धेरै अघि तिनीहरूका पिता-पुर्खाहरूलाई सुम्पिने वचन दिनु भएको थियो। यो अति असल देश हो। यो धेरै कुराहरू भएको राम्रो देश हो।",
			"23": "<sup>23</sup> इस्राएलका मानिसहरू यस देशमा आए अनि त्यसलाई बनाए। तर तिनीहरूले तपाईंको आज्ञा पालन गरेनन् अथवा तिनीहरूले तपाईंको शिक्षा अनुशरण गरेनन् अथवा तपाईंको आज्ञा पालन तिनीहरूले गरेनन्। यसकारण तपाईंले इस्राएलका मानिसहरूमाथि यी सारा सङ्कटहरू ल्याउनु भयो।",
			"24": "<sup>24</sup> “अनि अहिले, बाबेलवासीहरूले त्यो शहर कब्जा गर्न घेरेर राखेकाछन्। तिनीहरूले आफ्ना तरवारहरूद्वारा अनिकाल फैलाएर अनि भयानक रोगहरू फैलाएर यरूशलेममाथि विजय प्राप्त गर्नेछन्। त्यो शहर अहिले बाबेल सेनाहरूको आक्रमणमा छ। हे परमप्रभु! तपाईंले यस्तो होस् भन्नु भएकोले यस्तो भएकोछ।",
			"25": "<sup>25</sup> “हे परमप्रभु मेरो मालिक यो शहर कल्दीहरूको भए ता पनि यी सबै घटनाहरू घटिसके। तर अहिले मलाई भनिरहनु भएको छ। तर मलाई तपाईं भनी रहनु भएको छ, ‘यर्मिया, जमीन चाँदीले किन् अनि कतिपय मानिसहरूलाई साक्षी चुन।’ तपाईंले यो मलाई यस्तो भन्नुहुन्छ। बाबेलका सेनाले त्यो शहर अधीन पार्न तैयार भैरहेको बेला।”",
			"26": "<sup>26</sup> त्यसपछि परमप्रभुको वचन यर्मियाकहाँ आई पुग्यो",
			"27": "<sup>27</sup> “यर्मिया, म परमप्रभु हुँ। म सबै जीवधारीका परमेश्वर हुँ। के मेरो निम्ति कुनै कुरा असम्भव छैन।”",
			"28": "<sup>28</sup> परमप्रभुले तब भन्नुभयो, “म यरूशलेम शहर बाबेलका सेना र बाबेलका राजा नबूकदनेस्सरलाई सुम्पिरहेको छु। ती सेनाले शहर आफ्नो अधीनमा लिनेछन्।",
			"29": "<sup>29</sup> बाबेल सेनाले यरूशलेम शहरमाथि आक्रमण गरिरहेकाछन् तिनीहरू भित्र पस्नेछन् र आगो लाउनेछन्। तिनीहरूले जलाएर नै यो शहर भत्काउनेछन्। शहरमा यस्ता मानिसहरू छन् जो झूटा देवता बाललाई छानामाथि बाल्छन् अनि अर्घ बलिहरू अर्पण गर्छन् अनि मलाई रीस उठाउँछन्। बाबेल सेनाले यी घरहरू जलाएर खरानी पार्नेछन्।",
			"30": "<sup>30</sup> मैले इस्राएलका र यहूदाका मानिसहरूलाई नियालेकोछु। प्रत्येक कुरा जे तिनीहरूले गरेकाछन् त्यो पाप हो। तिनीहरूले बालक अवस्थामा हुँदैदेखि पाप कामहरू गरेकाछन्। इस्राएलका मानिसहरूले मलाई अति रीस उठाएका छन्। तिनीहरूले मलाई रीस उठाए किनभने तिनीहरूले आफ्नै हातद्वारा बनाएको मूर्तिहरू पूज्ने गर्छन्।” यो परमप्रभुबाट आएको सन्देश हो।",
			"31": "<sup>31</sup> “यरूशलेम शहर बनिएको समयदेखि अहिलेसम्म यस शहरका मानिसहरूले मलाई क्रोधित बनाए। यो शहरले मलाई क्रोधित पार्यो यसर्थ मेरो दृश्यबाट यसलाई हटाउनै पर्छ।",
			"32": "<sup>32</sup> म यरूशलेमलाई नष्ट गरिदिनेछु किनभने इस्राएल र यहूदाका मानिसहरूले पापमय कर्महरू गरेका छन्। यरूशलेम र यहूदाका मानिसहरूले तिनीहरूका राजाहरूले, शासकहरूले, तिनीहरूका पूजाहारीहरूले र अगमवक्ताहरूले लगाएत सम्पूर्ण मानिसहरूले मलाई क्रोधित पारेकाछन्।",
			"33": "<sup>33</sup> “ती मानिसहरूले मलाई पिठ्यूँ देखाए। मैले तिनीहरूलाई घरि-घरि शिक्षा दिन कोशिश गरें तर तिनीहरूले चाहेनन् मैले तिनीहरूलाई सुधार्ने प्रयास गरें, मेरो कुरामा ध्यानै दिएनन् तिनीहरूले मेरो अनुशासन ग्रहण गरेनन्।",
			"34": "<sup>34</sup> ती मानिसहरूले दिक्क लाग्दा मूर्तिहरू मेरो मन्दिरमा सजाए अनि त्यसलाई दूषित पारे।",
			"35": "<sup>35</sup> “बेन-हिन्नोमको बेंसीमा ती मानिसहरूले मोलेकलाई आफ्ना छोरा-छोरीहरू बलि चढाएर झूटा देवता बाललाई पूज्न उच्च स्थानको निर्माण गरे। मैले तिनीहरूलाई कहिले पनि त्यस्तो भयानक कार्यहरू गर भनेर आज्ञा गरिनँ यहूदाका मानिसहरूले यस्तो पापपूर्ण कामहरू गर्छन् भनेर कहिल्यै पनि मेरो मनमा लागेको थिएन।",
			"36": "<sup>36</sup> “तिमीहरू भनिरहेका छौ, ‘बाबेलका राजाले तरवारले अनिकाल र रूढीले यरूशलेम आफ्नो अधीनमा पार्ने छ।’ तर परमप्रभु इस्राएलका परमेश्वर यो शहरको विषयमा भन्नुहुन्छ,",
			"37": "<sup>37</sup> ‘म तिनीहरूलाई तिनीहरूको भूमिमा फर्काई ल्याउनेछु। म ती मानिसहरूसँग रीसाएको समयमा तिनीहरूलाई तितर-बितर पारेको ठाउँहरूबाट भेला पार्नेछु। तर म तिनीहरूलाई यो ठाउँमा फर्काई ल्याउनेछु। म ती मानिसहरूलाई त्यस्तै पार्नेछु। म तिनीहरूको जीवनलाई शान्त र सुरक्षित बनाउनेछु।",
			"38": "<sup>38</sup> तिनीहरू मेरो मानिसहरू हुनेछन् अनि म तिनीहरूका परमप्रभु हुनेछु।",
			"39": "<sup>39</sup> म तिनीहरूलाई संयुक्त हृदय र पथ दिनेछु ताकि तिनीहरू र आफ्ना आफ्ना बाल-बच्चाको भलाईको निम्ति मलाई सधैँ सम्मान गर्नेछन्।",
			"40": "<sup>40</sup> “‘म तिनीहरूसित एक स्थायी करार गर्नेछु। यो करार बमोजिम म तिनीहरूको सधैँ भलाई गर्नेछु। तिनीहरूले मलाई आदर गर्ने बनाउनेछु। यसकारण तिनीहरूले मलाई कहिल्यै त्याग्ने छैनन्।",
			"41": "<sup>41</sup> तिनीहरूलाई भलाई गर्नुमा म खुशी रहेनछु। म निश्चय तिनीहरूलाई यस देशमा रोप्ने छु र हुर्काउने छु। म आफ्नो जम्मै हृदय र मनले त्यसो गर्ने छु।’”",
			"42": "<sup>42</sup> यसकारण परमप्रभु भन्नुहुन्छ “मैले तिनीहरूमाथि ठूलो विपत्ति ल्याएँ। त्यसरी नै मैले जो प्रतिज्ञा गरेको छु तिनीहरूको लागि उपकारी थोकहरू ल्याउँनेछु।",
			"43": "<sup>43</sup> तिमीहरूले भनिरहेका छौ कि ‘बाबेलका सेनाले यो देश परास्त गर्यो अनि यो ता मानिस र पशुहरू बिना एक मरुभूमि भएको छ।’ तर याद गर मानिसहरूले एकपल्ट फेरि यस ठाउँमा जमीन किन्नेछन्।",
			"44": "<sup>44</sup> तिनीहरूले पैसाहरू दिएर खेतहरू किन्नेछन्। तिनीहरूले जमीनको तमसुकमा हस्ताक्षर गर्नेछन् र साक्षीहरूको उपस्थिसित मोहर लगाउनेछन्। मानिसहरूले फेरि साक्षीहरूको उपस्थितिमा बिन्यामीनको देशमा यरूशलेम वरिपरिका ठाउँहरूमा, यहूदाको शहरहरूमा पहाडी देशमा, पश्चिमी बेंसीहरू अन्तर्गत, अनि नेगेबमा जमीनहरू किन्ने छन्। त्यस्तो हुने नै छ किनभने म तिनीहरूलाई पुर्नस्थापना गर्नेछु।” यो परमप्रभुको सन्देश हो।"
		},
		"33": {
			"1": "<sup>1</sup> परमेश्वरको प्रतिज्ञा\nपरमप्रभुको सन्देश दोस्रो पल्ट यर्मियाकहाँ आयो। यर्मिया अझ प्रहरीहरूका चोकमा बन्द थियो।",
			"2": "<sup>2</sup> “परमप्रभुले पृथ्वी बनाउनु भयो अनि यसलाई सुरक्षित राख्नु भयो। उहाँको नाउँ परमप्रभु हो। परमप्रभु भन्नुहुन्छ,",
			"3": "<sup>3</sup> ‘यहूदा, मलाई प्रार्थना गर, अनि म जवाफ दिनेछु। म तिमीलाई महत्वपूर्ण गुप्त कुराहरू भन्नेछु। यी कुराहरू तिमीले पहिले कहिले सुनेका छैनौ।’",
			"4": "<sup>4</sup> परमप्रभु, इस्राएलका परमेश्वर हुनुहुन्छ: ‘परमेश्वरले ती कुराहरू यरूशलेमका घरहरू, जुन घेरामचान र लडाइँको प्रतिरोधमा नष्ट भएको थियो त्यस बारेमा र यहूदाका जग्गाहरू र राजाको महलहरू बारेमा भन्नुहुन्छ।",
			"5": "<sup>5</sup> “‘यस शहरको दुष्टकर्मले गर्दा म अब उसो यसको रक्षा गर्ने छैन। यरूशलेमको विरूद्ध युद्ध गर्न बाबेलका सेनाहरू आउनेछ र मेरो क्रोधले मारेको लाशहरूले यो शहर भनिनेछ।",
			"6": "<sup>6</sup> “‘तर म शहरमा पुनः आरोग्यता र स्वस्थ्य ल्याउनेछु। म तिनीहरूलाई सुस्वास्थमा राख्नेछु। म शान्ति र सुरक्षा ल्याउनेछु।",
			"7": "<sup>7</sup> म यहूदा र इस्राएलबाट लुटिएका समानहरूको पुन बहाली गराउनेछु। म तिनीहरूलाई विगतको जस्तै बनाई दिनेछु।",
			"8": "<sup>8</sup> मेरो विरोधमा तिनीहरूले गरेको पाप धोई पखाल्नेछु। तिनीहरूले मेरो विरूद्ध गरेको सबै पाप र विद्रोहहरू क्षमा गर्नेछु।",
			"9": "<sup>9</sup> जब मैले गरेको भलाइ कार्यहरू यस शहरका बसिन्दाहरूले सुन्छन्। तब पृथ्वीका जम्मै जातिहरू अघि यस शहरले मकहाँ प्रशंसा र महिमा ल्याउनेछ। जब ती जातिहरूले मैले यो शहरवासीहरूका निम्ति गरेको शान्ति र उन्नतिका कुराहरू सुन्छन तिनीहरू भयभीत हुनेछन् अनि काम्नेछन्।’",
			"10": "<sup>10</sup> “यसकारण परमप्रभु भन्नुहुन्छ, ‘तिमीहरू भनिरहेकाछौ, हाम्रो देश मानिस र पशुहरू बिनाको उजाड ठाउँ हो।’ अहिले यरूशलेमका गल्लीहरू र यहूदाका शहरहरू मानिसहरू र पशुहरू बिना ध्वंस भएको छ।",
			"11": "<sup>11</sup> तर चाँडै यो ठाउँमा आनन्दको मिठो, दुलहा-दुलहीहरूको खुशीयाली र परमप्रभुमा प्रशंसाको आवाजहरूले गुन्जयमान हुनेछ। ती मानिसहरूले भन्ने छन्,: ‘सेनाहरूका परमप्रभुको प्रशंसा गर। परमेश्वर भलो हुनुहुन्छ। उहाँ सधैँ कृपालु हुनुहुन्छ।’ तिनीहरूले मन्दिरमा धन्यवाद बलि चढाउनेछन् कारण यस ठाउँबाट जे-जे लुटिएको थियो म त्यो पुनः बहाली गराउनेछु अनि अघि जस्तो थियो त्यस्तै बनाउनेछु।” परमप्रभु भन्नुहुन्छ।",
			"12": "<sup>12</sup> सेनाहरूका परमेश्वर भन्नुहुन्छ, “मानिसहरू अथवा पशु प्राणीहरू नभएर अहिले यो ठाउँ उराठलाग्दो छ। तर यहूदाका सबै शहरहरूमा भेंडा़ गोठालाहरूले भेंडा़ बथानहरू चराउने र ती बथानहरूले थकाई मार्ने खर्कहरू हुनेछन्।",
			"13": "<sup>13</sup> पहाडी देशमा, पश्चिमी बेंसीमा, नेगेबमा, बिन्यामीनको शहरहरूमा, अनि यरूशलेम र यहूदाको वरिपरि सबै शहरहरूमा भेंडा़का बथानहरू हुनेछन् अनि भेंडा़ गोठालाहरू र भेंडा़हरू गन्ती गर्नेछन्,” परमप्रभु भन्नुहुन्छ।",
			"14": "<sup>14</sup> असल शाखा\nयो परमप्रभुबाट आएको सन्देश हो “मैले इस्राएल र यहूदाका मानिसहरूको लागि विशेष प्रतिज्ञा गरेकोछु, समय आइरहेछ, म ती वचनहरू पूरा गर्नेछु।",
			"15": "<sup>15</sup> त्यसबेला, म दाऊदको परिवारमा एउटा धार्मिकताको शाखा उत्पन्न गराउनेछु। उसले यो देशमा न्याय ल्याउनेछ।",
			"16": "<sup>16</sup> यो शाखाको समयमा यहूदाका मानिसहरू सुरक्षित हुने छन् अनि तिनीहरू यरूशलेममा मुक्त रहनेछन्। यो शाखाको नाउँ हो: ‘परमप्रभु हाम्रा धार्मिकता हुनुहुन्छ।’”",
			"17": "<sup>17</sup> परमप्रभु भन्नुहुन्छ, “दाऊदको परिवारबाट एकजना मानिस सधैँ सिंहासनमा बस्नेछ र इस्राएलका परिवारमाथि शासन गर्नेछ।",
			"18": "<sup>18</sup> अनि पूजाहारीहरू जो लेवी परिवारहरूबाट हुनेछन् तिनीहरू त्यहाँ मेरो अघि होमबलिहरू, अन्न बलिहरू र अरू बलिहरू चढाउन उभिन्छन्।”",
			"19": "<sup>19</sup> यो सन्देश परमप्रभुबाट यर्मिया कहाँ आयो।",
			"20": "<sup>20</sup> परमप्रभु भन्नुहुन्छ, “मसँग रात र दिनको करार छ। त्यो सदा-सर्वदा चलिरहनेछ। यदि तिमीहरूले यसलाई परिवर्तन गर्न सक्छौ भने ताकि दिन र रात आफ्नो तोकिएको समयमा नआवोस्।",
			"21": "<sup>21</sup> तब तिमीहरूले मेरा सेवक दाऊद र लेवीसँग पनि मेरो करार बद्लिन सक्छौ। दाऊदका सन्तानहरू अब राजा हुनेछैनन् र लेवीका परिवारबाट पूजाहारी हुनेछैनन्।",
			"22": "<sup>22</sup> तर म मेरो दास दाऊदका सन्तानहरूलाई र लेवीका परिवारहरूलाई जसले मेरो सेवा गरिरहेका छन् तिनीहरूको संख्या वृद्धि गराउनेछु। तिनीहरूको सन्तानको संख्या आकाशमा ताराहरू र सागरको किनारमा बालुवाको कण झैं अनगन्ती हुनेछ।”",
			"23": "<sup>23</sup> यर्मियाले यो सन्देश परमप्रभुबाट प्राप्त गरे।",
			"24": "<sup>24</sup> “यर्मिया, तिमीले सुन्यौ, मानिसहरूले के भनिरहेछन्? ती मानिसहरूले भनिरहेका छन्, ‘परमप्रभुले यहूदा र इस्राएलका परिवारहरूलाई जसलाई चुन्नुभएको थियो ती दुइलाई त्याग गर्नु भयो। तिनीहरूले मेरा मानिसहरूलाई घृणा गर्छन् अनि तिनीहरूलाई एउटा जाति भनी भन्नु चाहँदैनन्।’”",
			"25": "<sup>25</sup> परमप्रभु भन्नुहुन्छ, “यदि मैले रात र दिनसँग करार नराखेको भए अनि यदि मैले आकाश र पृथ्वीको लागि नियमहरू तैयार नबनाएको भए।",
			"26": "<sup>26</sup> तब याकूबका सन्तानहरू अस्वीकार गर्न सक्छु। अनि मेरो दास दाऊदका सन्तानहरूलाई अब्राहाम, इसहाक र याकूबको वंशहरूमाथि राज्य गर्नदिने छैन। तर तिनीहरूबाट जो खोसिएको छ त्यो पुन बहाल गरिनेछ र तिनीहरूमाथि दया गरिनेछ।”"
		},
		"34": {
			"1": "<sup>1</sup> यहूदाका राजा सिदकियाहलाई चेताउनी\nपरमप्रभुबाट यर्मियाकहाँ सन्देश आयो। सन्देश त्यतिबेला आयो जब बाबेलका राजा नबूकदनेस्सर, यरूशलेम र त्यसका वरिपरिका शहरहरूका विरूद्ध लडाइँ गरिरहेका थिए। नबूकदनेस्सरसँग उसका सारा सेना अनि आफूले शासन गरेका राज्यहरूका सेना र मानिसहरू थिए।",
			"2": "<sup>2</sup> परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ, “यर्मिया यहूदाका राजाकहाँ जाऊ अनि तिनलाई भन ‘सिदकियाह, परमप्रभु यसो भन्नुहुन्छ म यरूशलेमको यो शहर बाबेलका राजालाई दिइरहेको छु जसले यसलाई जलाएर खरानी पार्नेछ।",
			"3": "<sup>3</sup> सिदकियाह बाबेलका राजा देखि तिमी उम्कने छैनौ। तिमीलाई निसन्देह पक्रिने छ र उसलाई सुम्पने छ। तिमीले आफ्नै आँखाहरूले बाबेलका राजालाई देख्नेछौ आमने सामने कुरा गर्नेछौ। तब तिमीलाई बाबेलमा लैजानेछन्।",
			"4": "<sup>4</sup> यहूदाका राजा सिदकियाह परमप्रभुको वचन सुन। परमप्रभुले तिमीहरूलाई भन्नुहुन्छ, तिमी युद्धमा मारिने छैनौ।",
			"5": "<sup>5</sup> तिमी शान्तिसित मर्नेछौ। मानिसहरूले तिम्रो पिता-पुर्खाहरूलाई जस्तै ती राजाहरू जो तिमी भन्दा अघि आए तिम्रो लागि धूप जलाउनेछन्। तिनीहरूले तिम्रा निम्ति शोक मनाउनेछन् र दु:खीत भएर भन्नेछन्। “हे, प्रभु!” मैले मेरो कुरा तपाईंसँग भनेकोछु।’” यो सन्देश परमप्रभुबाट आएको हो।",
			"6": "<sup>6</sup> तब यर्मिया अगमवक्ताले परमेश्वरको यी सबै वचन यरूशलेममा सिदकियाहलाई भने।",
			"7": "<sup>7</sup> बाबेलका राजाले यरूशलेम अनि यहूदाको बाँकी रहेका गढीमा भएका शहरहरू लाकीश र अजेकाको विरूद्ध लडे। यी मात्र यहूदाको देशमा पर्खालले बारेका थियो।",
			"8": "<sup>8</sup> मानिसहरूले आफ्नो एउटा करार भङ्ग गरे\nराजा सिदकियाहले यरूशलेमका मानिसहरूसँग हिब्रू कमाराहरूलाई मुक्ति दिनलाई करार गरेका थिए। एउटा सिदकियाहले करार गरेपछि सन्देश परमप्रभुबाट यर्मियाकहाँ आयो।",
			"9": "<sup>9</sup> प्रत्येक मानिसले आफ्ना हिब्रू-कमाराहरूलाई स्वतंन्त्र पार्नु पर्ने हो। प्रत्येक हिब्रू कमारा-कमारीहरू स्वतंत्र गराउनु पर्ने हो। कसैले पनि यहूदा समुदायबाट कमारो नबनावोस्।",
			"10": "<sup>10</sup> यसकारण यहूदाका शासकहरू र मानिसहरूले यो करार स्वीकार गरे। प्रत्येक मानिसले आफ्ना कमारा-कमारीहरू स्वतन्त्र बनाउनेछ। प्रत्येक मानिस राजी भए र सबै कमारा-कमारीहरू स्वतंत्र गरिदिए।",
			"11": "<sup>11</sup> तर त्यसपछि, मानिसहरू ज-जसकहाँ आफ्ना कमारा-कमारीहरू थिए तिनीहरूले आफ्ना मन बद्ले। यसकारण फेरि तिनीहरू आफैंले स्वतन्त्र पारेका मानिसहरू ल्याए अनि तिनीहरूलाई आफ्नो कमारा-कमारीलाई बनाए।",
			"12": "<sup>12</sup> तब परमप्रभुबाट सन्देश यर्मियाकहाँ आयो",
			"13": "<sup>13</sup> “यही नै परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, ‘तिमीहरूका पिता-पुर्खाहरू मिश्रमा कमारा-कमारीहरू थिए मैले उनीहरूलाई मिश्रबाट निकालें अनि कमारापनबाट पनि निकालें र तिनीहरूलाई एउटा करार गरें।",
			"14": "<sup>14</sup> यो करार अनुसार प्रत्येक मानिसले आफ्ना हिब्रू कमाराहरूलाई प्रत्येक सातौं बर्षको आरम्भमा स्वतंन्त्र दिनु पर्छ। प्रत्येक हिब्रू कमारा-कमारी छ बर्षको सेवा पछि स्वतन्त्र हुनु पर्छ। तर तिमीहरूका पिता-पुर्खाहरूले मेरो कुरामा ध्यान दिएनन्।",
			"15": "<sup>15</sup> भर्खरै तिमीहरू आफूले गरेको कामका निम्ति पश्चाताप गर्यौ अनि मेरो नजरमा के सही हो त्यो गर्यौ। जुन मन्दिर मेरो नाउँले प्रसिद्ध छ त्यसमा तिमीहरूले मेरो समक्ष एउटा करार गर्यौ अनि हिब्रू कमारा-कमारीहरूलाई स्वतन्त्र दिने घोषणा गर्यौ।",
			"16": "<sup>16</sup> तर फेरि, तिमीहरूले आफ्ना मन बद्लियौ। तिमीहरूले मेरो नाउँको बेइज्जत गर्यौ। तिमीहरूले मुक्ति दिएका कमारा-कमारीहरूलाई फर्काई ल्यायौ र बलपूर्वक तिनीहरूलाई कमारा-कमारी बनायौ।’",
			"17": "<sup>17</sup> “यसकारण परमप्रभु भन्नुहुन्छ, ‘तिमीहरूले मेरो कुराको पालन गरेनौ। तिमीहरूले आफ्ना मित्र अनि छिमेकीहरूलाई स्वतंत्र पारेनौ। यसकारण तरवार, अनिकाल र भयानक महामारीद्वारा मारिनको लागि म तिमीहरूलाई मुक्ति घोषणा गर्छु। म तिमीहरूको अवस्था यति दर्दनाक बनाउनेछु कि तिमीहरूको अवस्था देखी सम्पूर्ण पृथ्वी नै भयभीत हुनेछ।",
			"18": "<sup>18</sup> ज-जसले त्यो करार भङ्ग गरेकाछन् अनि ज-जसले मेरो अघि गरेका प्रतिज्ञाहरू लत्याएका छन् तिनीहरूसित त्यही व्यवहार गर्नेछु जस्तो एउटी बाछीलाई दुइ फ्याक पार्छन् र ती दुइ टुक्राहरूको माझमा हिँड्छन्।",
			"19": "<sup>19</sup> ती मानिसहरू जो त्यो बाछीको दुइ फ्याक माझबाट हिँडे तिनीहरू यहूदा र यरूशलेमका शासकहरू, त्यो देशका मानिसहरू अनि नपुंसकहरू र पूजाहारीहरू थिए।",
			"20": "<sup>20</sup> यसकारण ती मानिसहरू तिनीहरूका शत्रुहरूलाई सुम्पनेछु जसले तिनीहरूलाई मार्न चाहन्छन्। ती मानिसका लाशहरू चरा-चुरूङ्गीहरूका लागि खाद्य अनि पशुहरूको शिकार हुनेछ।",
			"21": "<sup>21</sup> म यहूदाका राजा सिदकियाहलाई र उसका मानिसहरूलाई तिनीहरूका शत्रुहरूकहाँ सुम्पिनेछु जसले तिनीहरूलाई मार्न चाहन्छन्। ती सेनाहरू यरूशलेमबाट गइसकेका भएता पनि म तिनीहरूलाई बाबेलका राजाको सेनाहरूकहाँ सुम्पिनेछु।",
			"22": "<sup>22</sup> तर म आदेश दिनेछु अनि बाबेलका सेनाहरू फर्की आउनेछन्।’ यो परमप्रभुको सन्देश हो: ‘ती सेनाहरूले यरूशलेम विरूद्ध युद्ध गर्नेछ। बाबेलका सेनाहरूले यो शहर कब्जा गर्नेछन् र यसलाई जलाई खरानी बनाउनेछन्। म यहूदाका शहर ध्वंस पार्नेछु र यसलाई कोही नबस्ने निर्जन मरुभूमिमा परिणत गरिदिनेछु।’”"
		},
		"35": {
			"1": "<sup>1</sup> रेकाबी परिवारका असल उदाहरण\nयोशियाहको छोरो यहोयाकीम यहूदाका राजा भएको बेला, परमप्रभुबाट यो सन्देश यर्मिया कहाँ आयो। यो परमप्रभुको सन्देश थियो:",
			"2": "<sup>2</sup> “यर्मिया, रेकाबीहरूका परिवारकहाँ जाऊ। परमप्रभुको मन्दिरको एउटा छेउको कोठामा तिनीहरूलाई निम्त्याऊ। अनि तिनीहरूलाई दाखरस खुवाऊ।”",
			"3": "<sup>3</sup> यसर्थ मैले यर्मियाको  छोरो याजन्याह जो हबसिन्याहको नाति हो र उसका दाज्यू-भाइहरू र सबै छोराहरू पनि ल्याएँ। मैले रेकाबीहरूका सम्पूर्ण परिवारलाई पनि ल्याएँ।",
			"4": "<sup>4</sup> त्यसपछि मैले तिनीहरूलाई मन्दिरभित्र ल्याएँ। हामीहरू हानान जो यिग्दल्याहको छोरो हुन तिनको छोराहरूको कोठामा गयौं हानान परमप्रभुको एकजना भक्त थियो। त्यो कोठाको छेउमा अधिकारीहरूको थियो। यो कोठा शल्लूमको छोरो मासेयाह ढोकेको कोठाको छेउमा थियो। मासेयाह परमप्रभुको मन्दिरको ढोके थियो।",
			"5": "<sup>5</sup> तब मैले रेकाबी परिवारको अघाडि एक घैंटो दाखरस र केही कचौराहरू राखिदिएँ। अनि मैले तिनीहरूलाई “पिउनु भनें।”",
			"6": "<sup>6</sup> तब रेकाबी मानिसहरूले जवाफ दिए, “हामी कहिले पनि दाखरस पिउने छैनौं किनभने हाम्रा पिता-पुर्खा रेकाबको छोरो योनादाबले, हामीलाई यस्तो आज्ञा दिए ‘तिमीहरू र तिमीहरूका सन्तानहरूले कहिल्यै दाखरस पिउनु हुँदैन।",
			"7": "<sup>7</sup> उनले अझ भनेका छन् घरहरू निर्माण नगर्नु। बिऊ नरोप्नु अथवा दाख नरोप्नु। तिमीहरूले यी मध्ये कुनै एक पनि नगर्नु। केवल तम्बूहरूमा बस्नु। यदि तिमीहरूले त्यसो गर्यौ भने, जो तिमीहरू डुलुवाहरू थियौ यस देशमा धेरै कालसम्म रहने छौ।’",
			"8": "<sup>8</sup> रेकाबका छोरो हाम्रा पिता-पुर्खा योनादाबले दिनुभएको प्रत्येक आज्ञा हामीले पालन गरेकाछौं। हामी कहिले मद्यपान गर्दैनौं। हाम्रा पत्नीहरू, छोराहरू र छोरीहरूले कहिले दाखरस पिउँदैनन्।",
			"9": "<sup>9</sup> हामी बस्ने घरहरू कहिले बनाउँदैनौं। अनि हाम्रो आफ्नै दाखबारी र खेतहरू छैनन्। हामी कहिल्यै अन्न रोप्दैनौं।",
			"10": "<sup>10</sup> हामी तम्बूहरूमा बस्छौं अनि हाम्रा पिता-पुर्खा योनादाबले आज्ञा गरेको प्रत्येक कुरा पालन गर्छौ।",
			"11": "<sup>11</sup> तर जब बाबेलका राजा नबूकदनेस्सरले यहूदामाथि आक्रमण गरे, हामी यरूशलेम गयौं। हामीले एक-अर्का माझ भन्यौं, ‘हामी यरूशलेमको शहरभित्र प्रवेश गर्नै पर्छ ताकि हामी बाबेल र अरामीहरूका सेनाहरूबाट छुटकारा पाउन सक्छौं।’ यसकारण हामी यरूशलेममा बस्यौं।”",
			"12": "<sup>12</sup> तब परमप्रभुको वचन यर्मियाकहाँ आयो:",
			"13": "<sup>13</sup> “सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ ‘यर्मिया, जाऊ अनि यो सन्देश यहूदा र यरूशलेमका मानिसहरूलाई भन के तिमीहरूले शिक्षा नलिने अनि मेरो आदेश पालन नगर्ने?’",
			"14": "<sup>14</sup> परमप्रभु भन्नुहुन्छ, ‘रेकाबको छोरो योनादाबले आफ्ना छोराहरूलाई दाखरस नखानु भनेर आदेश गरे अनि त्यो आदेश पालन गरियो। आज पर्यन्त योनादाबका सन्तानहरूले आफ्ना पिता-पुर्खाहरूका आज्ञा पालन गरे। तिनीहरू दाखरस पिउँदैनन्। तर मैले तिमीहरूलाई एकोहोरो भनिरहेकोछु तर तिमीहरूले मेरो आज्ञाहरू पालन गरेका छैनौ।",
			"15": "<sup>15</sup> मैले मेरा दास अगमवक्ताहरू तिमीहरूकहाँ घरिघरि पठाएकोछु। अगमवक्ताहरूले तिमीहरूलाई भनेकाछन्, “तिनीहरू प्रत्येकले खराब कामबाट अलग रहनु पर्छ। तिमीहरू सबै असल हुनै पर्छ। तिमीहरूले अरू देवताहरूको अनुशरण र सेवा गर्नु हुँदैन। यदि मेरो आज्ञा पालन गरे यो देश जुन मैले तिमीहरू र तिमीहरूका पिता-पुर्खाहरूलाई दिएकोछु यहाँ बस्नेछौ।” तर तिमीहरूले मेरो कुरा सुनेनौ।",
			"16": "<sup>16</sup> योनादाबका सन्तानहरूले आफ्ना पिता-पुर्खाहरूले दिएका आज्ञा पालन गरे तर यी मानिसहरूले मेरो आज्ञा पालन गरेका छैनन्।’",
			"17": "<sup>17</sup> “यसकारण सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, ‘मैले यहूदा र यरूशलेमका बासिन्दाहरूकहाँ सबै सङ्कटहरू ल्याउदैछु जुन अघि मैले चेताउनी दिएको थिएँ। म ती मानिसहरूसँग बोलें, अनि तिनीहरूले मेरो कुरा सुन्न अस्वीकार गरे। मैले तिनीहरूलाई डाकें तर तिनीहरूले जवाफ दिएनन्।’”",
			"18": "<sup>18</sup> तब यर्मियाले रेकाबी मानिसहरूलाई भने, “सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, ‘तिमीहरूले आफ्ना पिता-पुर्खाहरूका आज्ञा पालन गर्यौ। तिमीहरूले योनादाबको उपदेशहरू अनुशरण गर्यौ। उनले जो आदेश दिएका थिए त्यो तिमीहरूले पालन गर्यौ।’",
			"19": "<sup>19</sup> यसकारण सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ: ‘यसकारण त्यहाँ रेकाबका छोरो योनादाबका सन्तान सधैँ रहनेछ जसले मेरो सेवा गर्छ।’”"
		},
		"36": {
			"1": "<sup>1</sup> राजा यहोयाकीमले यर्मियाको बेहेरूवा जलाउँदछ\nयोशियाहको छोरो यहोयाकीम यहूदाका राजा भएको चौथो बर्षमा परमप्रभुको यो सन्देश यर्मियाहकहाँ आयो। यो परमप्रभुबाट आएको सन्देश थियो",
			"2": "<sup>2</sup> “यर्मिया, मैले तिमीलाई भनेको जम्मै सन्देशहरू बेहेरूवामा लेख। मैले तिमीलाई इस्राएल, यहूदा र अन्य सारा जातिहरूका बारेमा भनेको छु। योशियाह राजा भए यता आज सम्मको ती सारा शब्दहरू जुन मैले भनेकोछु लेख।",
			"3": "<sup>3</sup> हुनसक्छ यहूदाका परिवारले गरेका दुर्ष्ट कर्महरू विरूद्ध मैले गरिरहेको योजना बारे यहूदाका मानिसहरूले सुन्नेछन् अनि हुनसक्छ, तिनीहरू दुष्ट कामहरू गर्नदेखि रोकिनेछन्। यदि तिनीहरूले त्यसो गरे भने म तिनीहरूले गरेका अपराधहरू र पाप क्षमा गरिदिनेछु।”",
			"4": "<sup>4</sup> यसकारण यर्मियाले नेरियाहको छोरो बारूकलाई बोलाए। बारूकले यर्मियालाई परमप्रभुले भन्नु भएको सबै कुराहरू यर्मियाको लेख्न-आदेशमा बेहेरूवामा लेखे।",
			"5": "<sup>5</sup> यर्मियाले बारूकलाई भने, “मलाई परमप्रभुको मन्दिरमा जाने अनुमति छैन।",
			"6": "<sup>6</sup> यसकारण म चाहन्छु कि तिमी परमप्रभुको मन्दिरमा उपासको दिन जाऊ अनि तिनीहरूका शहरहरूबाट आएका यहूदाका मानिसहरूलाई मेरो निर्देशनमा तिमीले बेहेरूवामा लेखेको परमप्रभुको सन्देशहरू पढेर सुनाऊ।",
			"7": "<sup>7</sup> हुनसक्छ कि मानिसहरूले परमप्रभुलाई सहयोगको लागि गुहार्नेछन् अनि हरेकले दुष्ट कामहरू गर्न देखि जोगिनेछन्। किनभने परमप्रभुले यी मानिसहरूमाथि महा रीस र क्रोध पोखाउँने भनी घोषणा गर्नु भएको छ।”",
			"8": "<sup>8</sup> यसकारण नेरियाहको छोरो बारूकले अगमवक्ता यर्मियाले अह्राएका प्रत्येक कुरा गरे। बारूकले बेहेरूवामा लेखिएका परमप्रभुको कुरा परमप्रभुको मन्दिरमा पढे।",
			"9": "<sup>9</sup> योशियाहको छोरा यहोयाकीमको राज्यकालको पाँचौ बर्षको नवौं महीनामा यरूशलेमका अनि यहूदाका शहरबाट यरूशलेममा आएका सबै मानिसहरूले परमप्रभुको नाउँमा उपवास बस्ने घोषणा गरे।",
			"10": "<sup>10</sup> त्यसबेला, बारूकले परमप्रभुको मन्दिरमा भेला भएका मानिसहरूको उपस्थितिमा यर्मियाका वचनहरू बेहेरूवाबाट पढे। तिनले परमप्रभुको मन्दिरको नयाँ प्रवेशद्वार छेउमा रहेको मथिल्लो आँगनमाथिको शापनको छोरो गमर्याहको कोठाबाट बेहेरूवा पढे। गमर्याह एकजना सचिव थिए।",
			"11": "<sup>11</sup> गमर्याहको छोरो अर्थात् शापानको नाति मिकायाहले बेहेरूवाबाट पढिएको परमप्रभुको सन्देशहरू सुने।",
			"12": "<sup>12</sup> जब मीकायाहले बेहेरूवाबाट परमप्रभुका सन्देश सुने, उनी राज महलको सचिवको कोठामा गए। सम्पूर्ण शाही अधिकारीहरू त्यहाँ राजाको दरबारमा बसिरहेका थिए। ती अधिकारीहरू हुन् सचिव एलीशामा, शमायाहको छोरो दलायाह, अकबोरको छोरो एलनातान, शापानको छोरो गमर्याह, हनन्याहको छोरो सिदकियाह अनि अन्य अधिकारीहरू पनि त्यहाँ थिए।",
			"13": "<sup>13</sup> जब बारूकले मानिसहरूलाई बेहेरूवाबाट पढी सुनाएको मिकायाहले जे सुनेका थिए तिनले प्रत्येक कुरो नै ती अधिकारीहरूलाई भने।",
			"14": "<sup>14</sup> तब सबै अधिकारीहरूले यहूदीलाई (नतन्याहको छोरो शेलेम्याहको नाती अनि कूशीको पनाति) बारूककहाँ पठाए। यहूदीले बारूकलाई भने, “तिमीले जुन बेहेरूवा मानिसहरू समक्षपठायौ त्यो लिएर मसित आऊ।”\nनेरियाहको छोरो बारूकले बेहेरूवा लिए अनि तिनीहरूकहाँ गए।",
			"15": "<sup>15</sup> तब ती अधिकारीहरूले बारूकलाई भने, “बस अनि हाम्रो लागि पढ र सुनाऊ।”\nयसर्थ तिनीहरूलाई बारूकले पढेर सुनाए।",
			"16": "<sup>16</sup> ती शाही अधिकारीहरूले बेहेरूवाबाट सारा सन्देशहरू सुने। तब तिनीहरू डराए, अनि एका अर्कालाई प्रति हेरा-हेर गरे। तिनीहरूले बारूकलाई भने, “हामीले बेहेरूवामा भएका सबै सन्देशहरू राजा यहोयाकीमलाई भन्नै पर्छ।”",
			"17": "<sup>17</sup> त्यसपछि अधिकारीहरूले बारूकलाई एउटा प्रश्न गरे, “बारूक, हामीलाई भन, तिमीले बेहेरूवामा लेखिएका सन्देशहरू कहाँबाट पायौ? के त्यो यर्मियाले भनेका कुराहरू लेखेका हौ?”",
			"18": "<sup>18</sup> “हजूर”, बारूकले जवाफ दिए। “यर्मियाले भन्यो, अनि मैले सारा सन्देशहरू बेहेरूवामा मसीले लेखें।”",
			"19": "<sup>19</sup> तब शाही अधिकारीहरूले बारूकलाई भने, “तिमी र यर्मिया भाग्नु पर्छ र लुक्नु पर्छ। तिमीहरू कहाँ लुक्छौ कसैलाई पनि नभन्नु।”",
			"20": "<sup>20</sup> तब शाही अधिकारीहरूले बेहेरूवा सचिव, एलीशामाको कोठामा राखे। तिनीहरू राजा यहोयाकीमकहाँ चोकमा गए र बेहेरूवामा भएको सबै कुरा भने।",
			"21": "<sup>21</sup> यसकारण राजा यहोयाकीमले त्यो बेहेरूवा ल्याउनु यहूदीलाई पठाए। यहूदीले सचिव, एलीशामाको कोठाबाट त्यो बेहेरूवा ल्याए। तब यहूदीले त्यो बेहेरूवा राजालाई र तिनको नजिकै उभिएका सेवकहरूलाई पढेर सुनाए।",
			"22": "<sup>22</sup> यो नवौं महीना चलिरहेको थियो, अनि राजा हिउँदमा बस्ने महलमा बसिरहको थिए। राजा यहोयाकीमको सम्मुख अगेनामा आगो दन्‌दन् बलिरहेको थियो।",
			"23": "<sup>23</sup> यहूदीले बेहेरूवा पढ्न शुरू गरे। जब उसले तीन वा चार पृष्ठ पढ्दै थिए, राजाले ती पढिसकेका पंक्ति एउटा सानो छुरीले काट्थे अनि अगेनोमा हाली दिन्थे र जब सम्म पुरै बेहेरूवा अगेनोमा जलेर नष्ट भएन तब सम्म तिनले त्यसै गरे।",
			"24": "<sup>24</sup> राजा यहोयाकीम र उनका सेवकहरू जसले बेहेरूवाबाट यी वचनहरू सुने तिनीहरू न त डराए न तिनीहरूले आफ्नो लुगा नै च्याते।",
			"25": "<sup>25</sup> एलानातान, दलायाह र गमर्याहले राजा यहोयाकीमलाई त्यो बेहेरूवा नजलाउनु होस् भनेर विन्ती गरे। तर राजाले तिनीहरूको बिन्ती सुनेनन्।",
			"26": "<sup>26</sup> राजा यहोयाकीमले आफ्नो छोरो यरहमेल, अज्रिएलको छोरो सरायाह र अबदीलको छोरो शेलेम्याहलाई सचिव बारूक र अगमवक्ता यर्मियालाई पक्रन हुकूम दिए। तर परमप्रभुले तिनीहरूलाई लुकाउनु भएको थियो।",
			"27": "<sup>27</sup> यर्मियाले भनेको अनि बारूकले लेखेको सन्देशहरू भएको बेहेरूवा राजाले जलाई सके पछि परमप्रभुको सन्देश यर्मियाकहाँ आयो।",
			"28": "<sup>28</sup> परमप्रभुले भन्नुभयो, “यर्मिया, अर्को एउटा बेहेरूवा लियर यहूदाका राजा यहोयाकीमले जलाएको पहिलो बेहेरूवामा जुन सन्देशहरू थिए सबै लेख।",
			"29": "<sup>29</sup> यहूदाका राजा यहोयाकिमको सम्बन्धमा तिमीले भन्नेछौ: ‘त्यसपछि परमप्रभुले भन्नुभयो, “तिमीले बेहेरूवा जलायौ कारण तिमीले सोच्यौ, बाबेलको राजा निश्चय आउनेछ र यो देश ध्वंस पारि मानिसहरू र पशुहरू काट्नेछ भनी सन्देशहरू किन तिमीले लेख्यौ?”",
			"30": "<sup>30</sup> यसकारण, परमप्रभुले यहूदाका राजा यहोयाकीमाको बारेमा भन्नुहुन्छ: यहोयाकीमका सन्तानहरू दाऊदको सिंहासनमा अब उसो बस्नेछैन। जब यहोयाकीम मर्दछ, उसको लाश भूइँमा फ्याँकिनेछ र दिनको गरम र रात चिसोमा त्यहीं रहनेछ।",
			"31": "<sup>31</sup> म, परमप्रभुले, यहोयाकीम र उसका नानीहरूलाई दण्ड दिनेछु। उसका अधिकारीहरूलाई पनि दण्ड दिनेछु। कारण तिनीहरू दुष्ट छन् म तिनीहरूमाथि डरलाग्दो विपत्ति ल्याउनेछु भनी मैले चेताउनी दिएको छु। त्यो दुर्भाग्य तिनीहरूमाथि मात्र होइन तर यरूशलेम र यहूदामा बस्ने सम्पूर्ण जातिलाई पनि असर गर्नेछ। मेरो चेताउनी अनुसार जम्मै अधर्मकामहरू तिनीहरूमाथि पार्नेछु तर तिनीहरूले मेरो कुरा सुन्ने छैनन्।’”",
			"32": "<sup>32</sup> त्यसपछि यर्मियाले अर्को एउटा बेहेरूवा लिए अनि त्यो नेरियाहको छोरो सचिव बारूकलाई दिए। जस्तै यर्मियाले बोले, बारूकले परमप्रभुको त्यही सन्देशहरू बेहेरूवामा लेखे जुन राजा यहोयाकीमले आगोमा जलाई दिएका थिए। ती सन्देशहरू बाहेक अन्य धेरै त्यस्तै शब्दहरू दोस्रो बेहेरूवामा थपिएका थिए।"
		},
		"37": {
			"1": "<sup>1</sup> यर्मिया कैदमा\nनबूकदनेस्सर बाबेलका राजा थिए। उनले यहोयाकीमको छोरो कोन्याहको सट्टामा सिदकियाहलाई यहूदाका राजा नियुक्त गरे। सिदकियाह योशियाहको छोरो थिए।",
			"2": "<sup>2</sup> तर सिदकियाहले परमप्रभुको सन्देश प्रति ध्यानै दिएन जुन सन्देश परमप्रभुका अगमवक्ता यर्मियालाई प्रचार गर्न भनी दिनुभएको थियो। अनि सिदकियाहको कर्मचारीहरू र यहूदाका मानिसहरूले परमप्रभुको सन्देशलाई ध्यानै दिएनन्।",
			"3": "<sup>3</sup> राजा सिदकियाहले शेलेप्याहको छोरो यहूकल नाउँका मानिस अनि मासेयाहको छोरो पूजाहारी सपन्याहलाई अगमवक्ता यर्मियाकहाँ यसो भनी पठाए। यो सन्देश तिनीहरूले यर्मियाकहाँ ल्याए, “हाम्रा निम्ति परमप्रभु हाम्रा परमेश्वरसित प्रार्थना गरिदिनु होस्।”",
			"4": "<sup>4</sup> त्यसबेला, यर्मियालाई कैदमा राखिएको थिएन, यसकारण उनी जता चाहन्थे त्यतै जान सक्दथे।",
			"5": "<sup>5</sup> फिरऊनको सेना मिश्रदेखि बाहिर आएको थियो। मिश्रका सेना तिनीहरूतिर अघि बढिरहेको सन्देश सुनेर बाबेलका सेनाहरू जो यरूशलेमलाई घेरा हाल्दै थिए त्यहाँबाट गए।",
			"6": "<sup>6</sup> परमप्रभुबाट अगमवक्ता यर्मियाकहाँ सन्देश आयो:",
			"7": "<sup>7</sup> “परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ, ‘यहूकल अनि सपन्याह! यहूदाका राजा सिदकियाहले तिमीलाई मकहाँ प्रश्नहरू सोध्न पठाएका छन्। राजा सिदकियाहलाई यसो भन मिश्रका सेना जो तिमीलाई सघाउन आइरहेका थिए, फर्की जानेछन्।",
			"8": "<sup>8</sup> त्यसपछि, बाबेलका सेना फर्केर यता आउनेछन्। तिनीहरूले यरूशलेममा आक्रमण गर्नेछन्। त्यसपछि बाबेलबाट आएका सेनाले यरूशलेमलाई आफ्नो अधीनमा पारी जलाईदिनेछ।",
			"9": "<sup>9</sup> परमप्रभु भन्नुहुन्छ “बाबेलका सेना निश्चय हामीबाट फर्केर जानेछन भनी सोचेर तिमीहरू आफैंलाई मूर्ख नबनाऊ।” तिनीहरू जाने छैनन्।",
			"10": "<sup>10</sup> यदि तिमीहरूले सम्पूर्ण बाबेली सेनालाई जितेता पनि केही घाइते सेनाहरू तम्बूमा छाडिए भने ती घाइते सेनाहरू उठनेछन् र यरूशलेमलाई जलाई दिनेछन्।’”",
			"11": "<sup>11</sup> जब बाबेलका सेना मिश्रका फिरऊनका सेनासित लडाइँ गर्नलाई यरूशलेम पुगे",
			"12": "<sup>12</sup> यर्मियाले यरूशलेमबाट बिन्यामीनको देश तर्फ यात्रा गर्न इच्छा गरे। उनी त्यहाँ आफ्नो सम्पक्तिको अंश लिनलाई गईरहेका थिए।",
			"13": "<sup>13</sup> तर जब तिनी यरूशलेमको बिन्यामीन प्रवेशद्वारमा पुगे प्रहरीहरूका अध्यक्षले तिनलाई पक्रे। त्यो अध्यक्षको नाउँ यिरियाह हो। यिरियाह शेलेम्याहका छोरा थियो। शेलेम्याह हनन्याहको छोरो थियो। अध्यक्ष यिरियाहले यर्मियालाई पक्रे अनि भने, “यर्मिया! तिमी हामीलाई छोडेर कल्दीहरूको पक्षमा गइरहेका छौ।”",
			"14": "<sup>14</sup> यर्मियाले यिरियाहलाई भने, “त्यो सत्य होइन। म कल्दीहरूकहाँ सम्मिलित हुन गएको होइन।” तर यिरियाले तिनको कुरा सुनेन अनि तिनलाई यरूशलेमको अधिकारीहरूकहाँ लगे।",
			"15": "<sup>15</sup> ती शाही अधिकारीहरू यर्मियासँग अति क्रोधित थिए। तिनीहरूले यर्मियालाई कुट्ने आदेश दिए। त्यसपछि तिनीहरूले यर्मियालाई जोनाथनको घरमा कैद गरे। जोनाथन एकजना सचिव थिए। जोनाथनको घरलाई तिनीहरूले झ्यालखाना बनाएका थिए।",
			"16": "<sup>16</sup> तिनीहरूले यर्मियालाई एक तहखानाको सानो कोठामा राखे। यर्मिया त्यहाँ धेरै समय सम्म बसे।",
			"17": "<sup>17</sup> त्यसपछि राजाले यर्मियालाई उनको राजाको महलमा ल्याउन पठाए। सिदकियाहले गुप्त रूपमा यर्मियालाई सोधे, “के परमप्रभुबाट केही सन्देश छ?”\nयर्मियाले जवाफ दिए, “ज्यू छ। तपाईंलाई बाबेलका राजाकहाँ सुम्पिनेछन्।”",
			"18": "<sup>18</sup> तब यर्मियाले राजा सिदकियाहलाई सोधे, “मैले के भूल गरेकोछु? तपाईं र तपाईंका अधिकारीहरू विरूद्ध अथवा यरूशलेमका मानिसहरूमाथि के अपराध गरें ताकि तपाईंले मलाई झ्यालखानमा राख्नु भयो?",
			"19": "<sup>19</sup> तिम्रा अगमवक्ताहरू अहिले कहाँ छन्? ती अगमवक्ताहरूले अगमवाणी गरेका थिए कि ‘बाबेलका राजाले तपाईं अथवा यहूदाको देशमाथि आक्रमण गर्ने छैन।’",
			"20": "<sup>20</sup> तर अहिले मेरो प्रभु, यहूदाका राजा, दया गरेर मेरो कुरा मान्नुहोस् अनि मलाई सचिव जोनाथनको घरमा नपठाउनु होस्, नत्र म त्यहाँ मर्नेछु।”",
			"21": "<sup>21</sup> यसकारण राजा सिदकियाहले यर्मियालाई प्रहरीहरूको चोकमा राख्ने हुकूम दिए। तिनीहरूले उनलाई शहरमा जम्मै रोटी नसकून्जेलसम्म दिनहुँ एउटा-एउटा रोटी दिए। यसकारण यर्मिया प्रहारीहरूको चोकमा बसे।"
		},
		"38": {
			"1": "<sup>1</sup> यर्मियालाई इनार भित्र फालियो\nकतिपय शाही अधिकारीहरूले जस्तै मत्तानको छोरो शपत्याह, पशहूरको छोरो गदल्याह, शेलेम्याहको छोरो यूकल अनि मल्कियाहको छोरो पशहूरले यर्मियाले सबै मानिसहरूलाई यसो भन्दैगरेको सुने,",
			"2": "<sup>2</sup> “कि परमप्रभु भन्नुहुन्छ, ‘जो यरूशलेममा बसे तिनीहरू तरवारहरूले अथवा अनिकालले अथवा डरलाग्दो रूढीले मर्नेछन् तर ज-जसले सैनिक समक्ष आत्मसमर्पण गर्नेछ तिनीहरू जीवित रहनेछन् उसले जीवन युद्ध परितोषक रूपमा पाउनेछ र मर्नेछैन।’",
			"3": "<sup>3</sup> अनि यही हो जो परमप्रभु भन्नुहुन्छ ‘यरूशलेमको शहरलाई निःसन्देह बाबेलको राजाकहाँ सुम्पिइनेछ। तिनीहरूले यसलाई कब्जा गर्नेछन्।’”",
			"4": "<sup>4</sup> तब यर्मियाले भनेका कुराहरू सुनेपछि शाही अधिकारीहरू राजा सिदकियाहकहाँ गए। तिनीहरूले राजालाई भने, “यर्मियालाई मार्नै पर्छ किनभने उसले यस्ता कुराहरू गरेर शहरमा भएका सिपाहीहरूलाई हतोत्साही बनाइरहेको छ। यर्मियाहले यस्तो कुरा गरेर प्रत्येक मानिसलाई हतोत्साही बनाइरहेको छ। यर्मियाहले हामीलाई राम्रो होस् भन्ने चाँहदैन। उसले यरूशलेमका मानिसहरू नष्ट भएको हेर्न चाहन्छ।”",
			"5": "<sup>5</sup> यसकारण राजा सिदकियाहले ती अधिकारीहरूलाई भने, “यर्मिया तिमीहरूको नियन्त्रणमा छ। म तिनीहरूलाई रोक्न केही गर्नु सक्तिन।”",
			"6": "<sup>6</sup> त्यसपछि ती अधिकारीहरूले यर्मियालाई लगे अनि राजाको छोरो मल्कियाहको पानीको इनारमा फ्याँकिदिए। त्यो इनार प्रहारी चोकामा थियो। ती अधिकारीहरूले डोरी लगाएर यर्मियालाई इनारको मुनी झारे। इनारमा पानी थिएनँ, तर हिलो थियो अनि त्यो हिलोमा यर्मिया भासिए।",
			"7": "<sup>7</sup> तर एबेदमेलेक नाउँका एकजना कूशी मानिस जो राजमहलमा नपुंसक थिए तिनले सुने कि अधिकारीहरूले यर्मियालाई पानीको इनारमा हालिदिए।",
			"8": "<sup>8</sup> राजा सिदकियाह त्यस समय बिन्यामीनको प्रवेशद्वारमा बसिरहेका थिए। तिनी राजदरवारबाट गए। तिनले राजालाई भने, “मेरो प्रभु महाराज! ती अधिकारीहरूले यर्मियासित दुष्टकर्म गरिरहेका छन्। तिनीहरूले यर्मियालाई भोकै मरोस् भनी पानीको इनारमा फ्याँकिदिए किनभने शहरमा रोटी छैन।”",
			"10": "<sup>10</sup> तब राजा सिदकियाहले कूशवासी एबेदमेलेकलाई आदेश दिए “एबेदमेलेक यहाँबाट तीनजना मानिसहरू लैजाऊ अनि मर्न अघि नै यर्मियालाई इनारबाट निकाल।”",
			"11": "<sup>11</sup> यसकारण एबेदमेलेकले आफूसँग तीनजना मानिसहरू लगेर गए। तर सर्व प्रथम उनी राजदरबारको भण्डार तर्फ गए अनि तिनले त्यहाँबाट पुराना र च्यातिएका लुगाहरू लगे। त्यसपछि डोरीमा ती थोत्रे लुगाहरू बाँधेर इनारमाभित्र यर्मियातिर झारिदिए।",
			"12": "<sup>12</sup> कूशवासी एबेदमेलेकले यर्मियालाई भने, “यी पुराना लुगाहरू आफ्नो काखीमा चेप। जब डोरीद्वारा तिमीलाई माथि तानिने छ तिम्रो काखीमा यसले पैडको काम गर्नेछ। अनि तानिन्दा डोरीहरूले तिम्रो काखीमा चोट पार्नेछैन।” यसकारण यर्मियाले एबेदमेलेकले भने जस्तै गरे",
			"13": "<sup>13</sup> ती मानिसहरूले डोरीहरूद्वारा यर्मियालाई तानेर पानीको इनारबाट बाहिर निकाले। अनि यर्मिया प्रहारीहरूको चोकमा बसे।",
			"14": "<sup>14</sup> सिदकियाहले यर्मियालाई अझ प्रश्नहरू सोध्छन्\nतब राजा सिदकियाहले यर्मियालाई ल्याउनु कसैलाई पठाए। त्यो मानिसले परमप्रभुको मन्दिरको तेस्रो द्वारमा यर्मियालाई ल्यायो। तब राजाले भने, “यर्मिया, म तिमीलाई केही प्रश्नहरू सोध्नेछु। मबाट केही पनि नलुकाऊ।”",
			"15": "<sup>15</sup> यर्मियाले सिदकियाहलाई भने, “यदि मैले उत्तर दिए भने तपाईंले मलाई मार्नु हुन्छ होला। अनि यदि मैले तपाईंलाई परामर्श दिएँ भने पनि सुन्नु हुने छैन।”",
			"16": "<sup>16</sup> तर राजा सिदकियाहले गुप्त रूपमा यर्मियासित कसम खाए र भने, “म परमप्रभु जसले हामीलाई सृष्टि गर्नु भयो अनि हामीलाई जीवन दिनुभयो, जस्तो जीवित हुनुहुन्छ त्यस्तै कसम खान्छु, म तिमीलाई न त मार्नेछु न तिमीलाई ज-जसले मार्न खोजिरहेका छन् तिनीहरूलाई नै सुम्पिन्छु।”",
			"17": "<sup>17</sup> तब यर्मियाले राजा सिदकियाहलाई भने, “सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, ‘यदि तिमीले बाबेलका राजाको अधिकारीहरू अघि आत्मा समर्पण गर्यौ भने, तिमी र तिम्रो परिवार सुरक्षित रहनेछन् अनि यरूशलेम जलेर भस्म हुनेछैन।",
			"18": "<sup>18</sup> तर यदि तिमीले आत्मा समर्पण गर्न अस्वीकार गर्यौ भने यरूशलेम बेबीलियोनका सेनालाई सुम्पिइने छ। तिनीहरूले यरूशलेमलाई जलाएर भस्म पार्ने छन्, अनि तिमी पनि तिनीहरूबाट जोगिने छैनौ।’”",
			"19": "<sup>19</sup> तर राजा सिदकियाहले यर्मियालाई भने, “तर म यहूदाका मानिसहरूसँग डराउँछु जसले अघिबाटै बाबेलका सेनाको पक्ष लिइसकेकाछन्। म डराउँछु कारण तिनीहरूले मलाई यहूदाका मानिसहरूलाई सुम्पनेछन् अनि तिनीहरूलाई मसित अति निम्न प्रकारले व्यवहार गर्ने अनुमति दिन्छन्।”",
			"20": "<sup>20</sup> तर यर्मियाले जवाफ दिए, “सिपाहीहरूले तिमीलाई ती यहूदाका मानिसहरूलाई दिने छैनन्। राजा सिदकियाह, परमप्रभुको आज्ञा पालन गर, मैले भनेको कुराहरू मान। तब सबै कुराहरू तिम्रा निम्ति राम्रै हुनेछ, अनि तिम्रो जीवन सुरक्षित रहनेछ।",
			"21": "<sup>21</sup> तर यदि तिमीले बाबेलका सेना समक्ष आत्मासमर्पण गर्न अस्वीकार गर्यौ भने, यो जो परमप्रभुले मलाई देखाउनु भएकोछ त्यो हुनेनैछ।",
			"22": "<sup>22</sup> सारा स्त्रीहरू जो यहूदाको राजाको दरबारमा छोडिएका थिए तिनीहरूलाई बाबेलको राजाका मुख्य अधिकारीहरूकहाँ ल्याइने छ। ती स्त्रीहरूले भन्ने छन्,:\n‘तिम्रा साथीहरू तिमी भन्दा बलिया छन्,\nतिम्रा मित्र जातिहरूले तिमीलाई धोका दिए,\nअनि तिमीलाई जितेकाछन्।\nतिम्रो खुट्टा हिलोमा भास्सियो,\nअनि तिमीलाई तिनीहरूले एक्लै छोडेर गए।’",
			"23": "<sup>23</sup> “तिम्रा सबै पत्नी र छोरा-छोरीहरू बाहिर ल्याइए अनि बाबेलका सेनालाई सुम्पिइयो। तिमी स्वयं पनि बाबेल सेनाबाट उम्कन पाउने छैनौ। तिमी बाबेलका राजाद्वारा लगिनेछौ अनि यरूशलेम जलेर भस्म हुनेछ।”",
			"24": "<sup>24</sup> तब सिदकियाहले यर्मियालाई भने, “हाम्रो बातचीच कसैले थाहा नपाउँन नत्र तिमी मर्नेछौ।",
			"25": "<sup>25</sup> ती अधिकारीहरूले मैले तिमीसँग गरेको कुरा थाहापाउने छन्। तब तिनीहरू तिमीकहाँ आएर सोध्नेछन्, ‘यर्मिया, हामीलाई बताऊ राजा सिदकियाहले तिमीलाई के भन्यो। अनि तिमीले राजालाई के भन्यौ सबै नै हामीलाई भन। ईमान्दारी साथ सारा कुरा हामीलाई भन नत्र तिमीलाई हामी मार्ने छौ।’",
			"26": "<sup>26</sup> यदि तिनीहरूले त्यसो भने, तिनीहरूलाई भन ‘मैले राजालाई नम्र निवेदन गरेको छु कि मलाई जोनाथनको घरमा नपठाइयोस् यदि मलाई त्यहाँ पठाउनु भयो भने, म मर्नेछु।’”",
			"27": "<sup>27</sup> तब यस्तो भयो कि ती शाही अधिकारीहरू आए अनि यर्मियालाई प्रश्न गरे। यसकारण यर्मियाले तिनीहरूलाई राजाले दिएको आज्ञा अनुसार सबै कुराहरू भने। तब ती अधिकारीहरूले यर्मियालाई एक्लै छाडेर गए किनभने कसैले पनि यर्मिया र राजा माझ भएको कुराहरू चाल पाएका थिएनन्।",
			"28": "<sup>28</sup> यसकारण यरूशलेमलाई अधीनमा लिनेदिन सम्मै यर्मिया प्रहारीहरूको चोकमानै बसे।"
		},
		"39": {
			"1": "<sup>1</sup> यरूशलेमको पतन\nयरूशलेमलाई कसरी परास्त पारियो यहाँ वर्णन गरिएको छ यहूदाका राजा सिदकियाहको शासनकालको नवौं बर्षको दशौं महीनामा बाबेलका राजा नबूकदनेस्सरले आफ्ना सम्पूर्ण सेना लिएर यरूशलेमको विरूद्ध अघि बढे अनि त्यसलाई घेरा हाले।",
			"2": "<sup>2</sup> अनि सिदकियाह राजा भएको एघारौं बर्षको चौथो महीनाको नवौ दिनमा, शहरको पर्खाल भत्काइयो।",
			"3": "<sup>3</sup> बाबेलका राजाका जम्मै भारद्वारहरू भित्र आए अनि मध्य प्रवेशद्वारमा बसे। ती त्यहाँ आउने अधिकारीहरूको नाउँ यस प्रकार छ, नेर्गल-सरेसेर, समगरनबो, मुख्य अधिकारी सर्सकीम, रब-सारीस, उच्च अधिकारी नेर्गल-सरेसर, रबमाग र अन्य मुख्य अधिकारीहरू पनि थिए।",
			"4": "<sup>4</sup> यहूदाका राजा सिदकियाहले बाबेलका अधिकारीहरूलाई देखे। यसकारण उनी आफ्ना सिपाहीहरू सहित राजाको बगैंचाको फाटकबाट राती भागे अनि दुइ पर्खालको बीच भएर यरूशलेम शहरबाट बाहिर आए। तिनीहरू अराबा तिर लगे।",
			"5": "<sup>5</sup> कल्दीहरूका सिपाहीहरूले सिदकियाह र उनका सिपाहीहरूलाई खेद्‌दै गए अनि त्यति बेला सिपाहीहरू सिदकियाहसँगै यरीहोको मैदानमा पक्राऊ परे। तिनीहरूले सिदकियाहलाई बाबेलका राजा नबूकदनेस्सर जो हमात देशको रिबला शहरमा थिए त्यहाँ पुर्याए। रिबला शहरमा बाबेलका राजा नबूकदनेस्सरले सिदकियालाई कस्तो व्यवहार गर्नु पर्ने, बताए।",
			"6": "<sup>6</sup> रिबला शहरमा बाबेलका राजाले सिदकियाहका छोरो र सबै मुख्य मानिसहरूलाई तिनकै उपस्थितिमा मारे।",
			"7": "<sup>7</sup> तब नबूकदनेस्सरले सिदकियाहको आँखा निकालिदिए र उसलाई बाबेलमा ल्याउन साङ्गलाले बाँधे।",
			"8": "<sup>8</sup> बाबेलका सेनाले राजाको महल अनि यरूशलेमको मानिसहरूको घरहरू जलाइदिए। अनि तिनीहरूले यरूशलेमको पर्खालहरू भत्काइदिए।",
			"9": "<sup>9</sup> नबूजरदान बाबेलका राजाको विशेष सैनिकका कप्तान थिए। उनले शहरमा छोडिएका बाँकी मानिसहरूलाई ज-जसले समर्पण गरेका थिए अनि अन्य जो रहेकाहरूलाई कैदी बनाई बाबेलमा ल्याए।",
			"10": "<sup>10</sup> तर कप्तान नबूजरदानले यहूदाका गरीब मानिसहरू ज-जसको यहूदा देशमा केही थिएन तिनीहरूलाई छोडिदिए। यसर्थ उनले ती मानिसहरूलाई दाखबारी र खेतहरू दिए।",
			"11": "<sup>11</sup> तर नबूकदनेस्सरले यर्मियाको बारेमा नबूजरदानलाई केही आज्ञा दिए। नबूजरदान नबूकदनेस्सरका खास रक्षकहरूको कप्तान थिए। आदेश यही थियो",
			"12": "<sup>12</sup> “यर्मियालाई लैजाऊ र उसको हेरचाह गर। उसलाई नोक्सान नपार। उसले जे माग्छ देऊ।”",
			"13": "<sup>13</sup> यसर्थ राजाका विशेष रक्षकहरूका कप्तान नबूजरदानले नबु-शजबान, रब-सारीस, रब-माग, समगरनबो, मुख्य अधिकारी सर्सकीम, नेर्गलसरसेर र सबै मुख्य अधिकारीहरू अनि बाबेल सेनाका हाकिमहरूलाई यर्मियाकहाँ पठाए।",
			"14": "<sup>14</sup> तिनीहरूले यर्मियालाई प्रहारीको चोक बाहिर निकाले। बाबेल सेनाका हाकिमहरूले यर्मियालाई अहीकामको छोरो अनि शापानको नाति गदल्याहको रेख-देखमा राखे। गदल्याहलाई दिइएको आदेश अनुसार यर्मियालाई घर लग्यो, यसर्थ उनी आफ्ना मानिसहरूसँगै बसे।",
			"15": "<sup>15</sup> एबेद-मेलेकलाई परमप्रभुको सन्देश\nमन्दिरको चोकमा जब प्रहारीहरूले यर्मियाहलाई रेख-देख गरिरहेका थिए। परमप्रभुबाट तिनलाई एउटा सन्देश आयो। यो सन्देश यसप्रकार थियो",
			"16": "<sup>16</sup> “यर्मिया तिमी कूशी मानिस एबेद-मेलेककहाँ जाऊ र यो सन्देश भन, ‘सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर यसो भन्नुहुन्छ, अति चाँडै म यरूशलेममा मेरो सन्देश सत्य बनाउने काम गर्छु। यो सन्देश सर्वनाश र नराम्रो कुराहरूबाट सत्य हुनेछ। तिमीले तिम्रै आँखाले प्रत्येक कुरो साँच्चो भएको देख्नेछौ।",
			"17": "<sup>17</sup> तर म त्यस दिन तिमीलाई जोगाउने छु, एबेद-मेलेक।’ यो परमप्रभुको सन्देश हो: ‘जुन मानिसहरूसित तिमी डराउँछौ म तिमीलाई ती मानिसहरूसँग सुम्पने छैन्।",
			"18": "<sup>18</sup> तिमी तरवारले मारिने छैनौ। म तिम्रो रक्षा गर्छु। तिमीले ममाथि भरोसा गरेकाले तिमीले तिम्रो जीवन युद्ध पारितोषक रूपमा पाउनेछौ।’” यो सन्देश परमप्रभुको हो।"
		},
		"40": {
			"1": "<sup>1</sup> यर्मियाको छुटकारा\nविशेष रक्षकहरूका कप्तान नबूजरदानले जब यर्मियालाई रामामा कैद मुक्त गरेका थिए तिनीकहाँ परमप्रभुबाट एउटा सन्देश आयो। तिनले यर्मियाहलाई यरूशलेम र यहूदाका मानिसहरूलाई कैदी बनाई बाबेलमा लगिरहेकाहरूसितै साङ्गलोले बाँधिएको भेटे।",
			"2": "<sup>2</sup> जब नबूजरदानले यर्मियालाई भेटे, उनी तिनीसँग बात गरे। उनले भने, “यर्मिया, परमप्रभु तिम्रो परमेश्वरले यस ठाउँमा विपत्ती आउनेछ भनेर घोषणा गर्नुभयो।",
			"3": "<sup>3</sup> अनि अहिले परमप्रभुले जे भन्नु भएको थियो प्रत्येक कुरो गर्नुभयो। यो सर्वनाश आइलाग्यो कारण परमप्रभुको विरूद्ध यहूदाका मानिसहरूले पाप गरेका थिए। तिमीहरूले परमप्रभुको आज्ञा पालन गरेनौ। यसकारण तिनीहरूलाई यस्तो भयो।",
			"4": "<sup>4</sup> तर अब म तिमीलाई मुक्त गरिदिनेछु। म तिमीलाई साङ्गलाबाट मुक्त पार्छु। यदि तिमी इच्छा गर्छौ भने म सँग बाबेलतिर जाऊँ, म तिम्रो राम्रो रेख-देख गर्ने छु। तर यदि तिमी मसँग आउनँ चाहँदैनौ भनें नआऊ। हेर, सारा देश नै तिम्रो सम्मुख छ अनि तिमी जहाँ जान चाहन्छौ त्यहाँ जाऊ।",
			"5": "<sup>5</sup> अथवा शापानको नाति अहीकामको छोरो गदल्याहकहाँ जाऊ। बाबेलका राजाले गदल्याहलाई यहूदाको राज्यपाल नियुक्त गरेका छन्। जाऊ गदल्याहकहाँ तिम्रा मानिसहरू माझ बस अथवा तिम्रो इच्छा अनुसार तिमी जहाँ चाहन्छौ त्यहीं जाऊ।”\nत्यसपछि नबूजरदारले यर्मियालाई केही भोजन र उपहार प्रदान गरे अनि बिदा गरे।",
			"6": "<sup>6</sup> यसकारण यर्मिया अहीकामको छोरो गदल्याहकहाँ मिस्पामा गए। यर्मिया गदल्याहसँग मानिसहरू माझ बसे जो यहूदाको देशमा छाडिएका थिए।",
			"7": "<sup>7</sup> गदल्याहको छोटो शासन\nयरूशलेम ध्वंस भए पश्चात केही सैन्य, अधिकारीहरू अनि तिनीहरूका मानिसहरू जो खेतमा बसिरहेका थिए, चाल पाए कि बाबेलका राजाले गरीब, र बाबेलमा कैदी बनाई नलगिएका मानिसहरूको कल्याणार्थ अहिकमेको छोरो गदल्याहलाई राज्यपाल नियुक्त गरेका छन्।",
			"8": "<sup>8</sup> यसकारण ती सिपाहीहरू मिस्पामा गदल्याहकहाँ आए। ती सिपाहीहरू थिए नतन्याहको छोरो इश्माएल, कारेहका दुइ छोराहरू योहानान अनि जोनाथन, पन्हूमेतको छोरो सरायाह, नतोफाबासी एपेकाका छोराहरू, अनि माकाबासीको छोरो याजन्याह र यिनीहरूका सबैका मानिसहरू।",
			"9": "<sup>9</sup> अहीकामका छोरो गदल्याहले ती सिपाहीहरूले सुरक्षित अनुभव गरून भनी तिनीहरूसित शपथ लिएका थिए गदल्याहले यही नै भने “यही ठाउँमा बस अनि बाबेलका राजालाई सेवा गर र यो तिमीहरूका निम्ति भलो हुनेछ।",
			"10": "<sup>10</sup> बाबेलवासीहरू अघि जो हामीकहाँ आउँछन् तिमीहरूको प्रतिनिधित्व गर्न म आफैं मिस्पामा बस्नेछु। दाखरस, ग्रीष्मऋतुका फल, तेल जम्मा गर अनि भाँडामा राख। तिनीहरूले आफ्नो अधीनमा लिएको शहरहरूमा बस्।”",
			"11": "<sup>11</sup> यहूदाका मानिसहरू जो मोआब, अम्मोन र एदोमका अनि अन्य देशहरूमा थिए तिनीहरूले चाल पाए कि बाबेलका राजाले बाँचेका मानिसहरूलाई यहूदामा छोडेका छन् र शापानको नाति अहीकामको छोरा गदल्याहलाई त्यस ठाउँका राज्यपाल नियुक्त गरे।",
			"12": "<sup>12</sup> यसर्थ यहूदाका मानिसहरू जो विभिन्न ठाउँमा छरिएर बसेका थिए यहूदामा फर्के अनि मिस्पामा गदल्याहलाई भेटे। तिनीहरूले धेरै मद र ग्रीष्म ऋतुका फलहरू बटुले।",
			"13": "<sup>13</sup> कारेहका छोरो योहानान अनि यहूदाका हाकिमहरू जो खेतमा थिए गदल्याहकहाँ मिस्पामा आए।",
			"14": "<sup>14</sup> योहानान र अन्य मानिसहरू आएर गदल्याहलाई भने, “तिमीलाई थाह छ कि अम्मोनवंशी राजा बालीसले तिमीलाई मार्न चाहन्छ? उसले नतन्याहको छोरो तिम्रो हत्याको लागि इश्माएललाई पठायो।” तर अहीकामको छोरो गदल्याहले ती कुराहरू विश्वास गरेनन्।",
			"15": "<sup>15</sup> तब कारेहका छोरो योहानानले गुप्त रूपमा मिस्पामा गदल्याहलाई भेटे अनि उनलाई भने, “नतन्याहको छोरो इश्माएललाई मार्न। कसैले पनि यस बारेमा केही पनि जान्दैनन्। तपाईंलाई मार्न उसलाई स्वतन्त्रता दिनु हुँदैन। यदि यस्तो भयो भेन यहूदाका मानिसहरू जो भेला भएकाछन् तितर-बितर हुनेछन् अनि यहूदाका सन्तान नष्ट हुनेछ।”",
			"16": "<sup>16</sup> तर अहीकामको छोरो गदल्याहले कारेहको छोरो योहानानलाई भने, “इश्माएललाई नमार। जुन कुराहरू तिमीहरूले इश्माएलको बारेमा बताएका छौ ती सबै असत्य हो।”"
		},
		"41": {
			"1": "<sup>1</sup> सातौं महीनामा, एलीशाको नाति नतन्याहको छोरो इश्माएल, अहीकामको छोरो गदल्याहकहाँ उसको दशजना मानिसहरूसित आई पुग्यो। ती मानिसहरू मिस्पाको शहरमा आए। इश्माएल राज-परिवारको सदस्य थिए। उनी राजा यहूदाका हाकिमहरू मध्ये एकजना थिए। इश्माएल अनि उसका मानिसहरूले गदल्याहसँग भोजन गरे।",
			"2": "<sup>2</sup> जब तिनीहरूले खाईरहेका थिए, इश्माएल र उसका मानिसहरू उठे अनि शापानको नाति अहीकामको छोरो गदल्याहलाई तरवारले मारे। गदल्याह ती मानिस हुन जसलाई राजाले बाबेलका राज्यपाल नियुक्त गरेका थिए।",
			"3": "<sup>3</sup> इश्माएलले यहूदावासी मानिसहरू अनि कल्दी सिपाहीहरू जो गदल्याहसँग मिस्पा शहरमा थिए। तिनीहरूलाई पनि मारे।",
			"4": "<sup>4</sup> गदल्याहको हत्या भए पछि त्यही दिन। कसैले यस विषयमा चाल पाउन अघि नै आफै दाह्री काटेका अनि लुगाहरू च्यातेका अनि तिनीहरू आफै काटिएकाहरू अन्नबलिहरू, अनि धूपहरू लिएर परमप्रभुको मन्दिरमा पूजा गर्न 80 जना मानिसहरू मिस्पामा आए। तिनीहरू शेकेम, शीलो र सामरियाबाट आएका थिए। ती कुनै पनि मानिसहरूलाई गदल्याहको हत्या भएको तिनीहरूलाई थाहा थिएन।",
			"6": "<sup>6</sup> इश्माएलले मिस्पा छाडे अनि ती 80 जना मानिसहरू भेट्न गए। तिनी ती 80 जना मानिसहरूसँग भेट्न जाँदा रोए। तिनले ती 80 जना मानिसहरूलाई भेटे अनि अहीकामका छोरो गदल्याहलाई भेट्न मसित जाऊँ भने।",
			"7": "<sup>7</sup> तिनीहरू शहरमा पुगेका मात्र थिए, इश्माएल र उनका मानिसहरूले ती 80 जना मानिसहरूलाई मार्न थाले अनि इनारमा फ्याँकी दिए।",
			"8": "<sup>8</sup> तर ती मध्ये दशजना मानिसहरूले इश्माएललाई भने, “हामीलाई नमार। हामीले कतिथोकहरू जमीनमा लुकाएका छौं। हामीसँग गहुँ, जौ तेल र मह छन्। हामी ती थोकहरू तिमीहरूलाई दिनेछौ।” यसकारण इश्माएल थामिए अनि तिनीहरूलाई मारेनन्।",
			"9": "<sup>9</sup> (नतन्याहको छोरो इश्माएलले ती मृत शरीरहरू इनारभित्र त्यो नभरिन्जेलसम्म फ्याँके। त्यो ठूलो इनार राजा आसाले इस्राएलका राजा बाशाबाट आफू सुरक्षित रहन बनाएका थिए।)",
			"10": "<sup>10</sup> इश्माएलले मिस्पा शहरका सारा मानिसहरूलाई आफ्नो कब्जामा पारे अनि अम्मोनी मानिसहरूको देश पार गरे। ती मानिसहरूमा राजाको छोरीहरू अनि अन्य जे मानिसहरू त्यहाँ छाडिएका थिए। तिनीहरू थिए, बाबेलका राजाका विशेष रक्षकहरू कप्तानले, नबूजरदान गदल्याहलाई ती मानिसहरूको हेरचाह गर्न भनी नियुक्त गरेका थिए।",
			"11": "<sup>11</sup> कारेहको छोरो योहानान अनि सेनाका हाकिमहरू जो उनीसँग थिए, इश्माएलले गरेका दुष्ट्याईँको बारेमा सुने।",
			"12": "<sup>12</sup> यसकारण तिनीहरूले तिनीहरूसित रहेका सारा सैन्य नतन्याहको छोरो इश्माएलसित लड्न लगे। तिनीहरूले इश्माएललाई गिबोन शहरको ठूलो दह नजिकै भेटे।",
			"13": "<sup>13</sup> कैदीहरू जसलाई इश्माएलले लगेर गएका थिए उनीहरूले योहानान र तिनका साथमा भएका सेनाका सबै हाकिमहरूलाई देखे। तिनीहरू अति खुशी भए।",
			"14": "<sup>14</sup> यसर्थ ती मानिसहरू जसलाई इश्माएलले मिस्पाबाट कैदी बनाएर लगेका थिए कारेहको छोरो योहानानकहाँ गए।",
			"15": "<sup>15</sup> तर इश्माएल र उनका आठजना मानिसहरू योहानानबाट बाँचे अनि अम्मोन मानिसहरूकहाँ भागे।",
			"16": "<sup>16</sup> यसकारण कारेहको छोरो योहानान अनि उसका सेनाका हाकिमहरूले कैदीहरूलाई मुक्त पारे जसलाई इश्माएलले गदल्याहको हत्या गरे पछि ती मानिसहरूलाई मिस्पाबाट कैदी बनाई लगेका थिए। तिनीहरूमा सिपाहीहरू, पत्नी मानिसहरू, नानीहरू अनि नपुंसकहरू थिए। योहानानले तिनीहरूलाई गिबोन शहरबाट ल्याए।",
			"17": "<sup>17</sup> मिश्रतर्फ पलायन\nयोहानान र अन्य सेनाका हाकिमहरू कल्दीहरूसँग डराएका थिए। बाबेलका राजाले यहूदाका राज्यपालका रूपमा अहीकामको छोरो गदल्याहलाई नियुक्त गरेका थिए। यसकारण तिनीहरूले मिश्र जान यात्रा शुरू गरे अनि बेतलेहेम छेउ गेरूथ-किम्हाममा शिबिर हाले।"
		},
		"42": {
			"1": "<sup>1</sup> तिनीहरू गेरूथ किम्हाममा भएको बेला, योहानान र होशायाहको छोरो याजन्याह यर्मिया अगमवक्ताकहाँ गए। जम्मै सेनाका हाकिमहरू र सानोदेखि ठूलो दर्जा सम्मका मानिसहरू योहानान र याजन्याहसित गए।",
			"2": "<sup>2</sup> ती सबै जनाले अगमवक्ता यर्मियालाई भने, “हाम्रो नम्र बिन्ति ग्रहण गर्नुहोस् र हाम्रा निम्ति तथा बाँचेका यी मानिसहरूको लागि परमप्रभु तपाँईंका परमेश्वरसित प्रार्थना गरिदिनु होस्। एकपल्ट हामीहरू बहुसंख्यामा थियौं तर अहिले तपाईं देख्नु हुन्दैछ हामी केही मात्र बाँचेका छौं।",
			"3": "<sup>3</sup> यर्मिया, परमप्रभु तपाईंको परमेश्वरसित प्रार्थना गरिदिनु होस् ताकि हामी कहाँ जानु पर्ने अनि हामीले के गर्नु पर्ने हो।”",
			"4": "<sup>4</sup> तब अगमवक्ता यर्मियाले उत्तर दिए, “तिमीहरूले के गर्नु पर्ने भनेको हो मैले बुझें। तिमीहरूले भनें झैं म परमप्रभु तिमीहरूका परमेश्वरसित प्रार्थना गरिदिने छु। परमप्रभुले भन्नु भएको सबै कुराहरू म तिमीहरूलाई भन्नेछु। कुनै थोकहरू पनि म तिमीहरूदेखि लुकाउने छैन।”",
			"5": "<sup>5</sup> तब ती मानिसहरूले यर्मियालाई भने, “यदि हामीले परमप्रभु तपाईंका परमेश्वरले चाहनु भए जस्तै कामहरू गरेनौं भने हाम्रा निम्ति परमप्रभु साँच्चो र विश्वासी साक्षी रहनु हुन्छ। हामी जान्दछौं परमप्रभु तिमीहरूका परमेश्वरले के गर्नु पर्ने तिमीहरूलाई भन्न हामीलाई पठाउन्।",
			"6": "<sup>6</sup> परमप्रभुको आदेश चाहे हाम्रो हितमा होस् अथवा नहोस् हामी पालन गर्नेछौ ताकि जब हामी परमप्रभु हाम्रा परमेश्वरको सबै आदेश पालन गर्छौ सब कुरो हाम्रो हितमा हुनेछ। हामी तिमीलाई ती सन्देश लिनका लागि परमेश्वर कहाँ पठाई रहेछौं। तब असल कुराहरू हामी प्रति हुने छ। हो, हामी परमेश्वरको सारा कुराहरू पालन गर्ने नै छौं।”",
			"7": "<sup>7</sup> दश दिनको अन्त्यमा, यर्मियाकहाँ परमप्रभुबाट सन्देश आयो।",
			"8": "<sup>8</sup> तब यर्मियाले कारेहका छोरो योहानान र तिनका साथमा भएका सेनाका सबै हाकिमहरू अनि साना ठूला दर्जाका सबै मानिसहरूलाई डाके।",
			"9": "<sup>9</sup> तब यर्मियाले तिनीहरूलाई भने, “परमप्रभु इस्राएलका परमेश्वरको सन्देश यही हो। तिमीहरूले मलाई उहाँ कहाँ पठायौ। मैले परमप्रभुलाई तिमीहरूले जे चाहन्थ्यौ त्यही सोधें। परमप्रभु भन्नुहुन्छ",
			"10": "<sup>10</sup> ‘यदि तिमीहरू साँच्चै यस देशमा बस्छौं भने, म तिमीहरूलाई बलियो बनाउँछु। म तिमीहरूलाई नष्ट गर्ने छैन। म तिमीहरूलाई जोगाउनेछु अनि तिमीहरूलाई उखाल्ने छैन कारण तिमीहरूका निम्ति मैले गरेको नराम्रो काममा म दु:खित छु।",
			"11": "<sup>11</sup> तिमीहरू बाबेलका राजासित भयभित छौ। तर अब उसँग नडराऊ। म तिमीहरूसित छु अनि म तिमीहरूलाई उबाट बचाउँने छु।",
			"12": "<sup>12</sup> म तिमीहरूलाई दया दर्शाउनेछु। बाबेलका राजाले पनि तिमीहरू प्रति दयादृष्टि राख्नेछन् अनि उसले तिमीहरूलाई आफ्नो देशमा आउने अनुमति दिनेछन्।’",
			"13": "<sup>13</sup> यदि तिमीहरू भन्छौ, ‘हामी यस देशमा बस्ने छौ,’ तब तिमीहरूले परमप्रभु तिमीहरूका परमेश्वरको आज्ञा पालन गरेनौ।",
			"14": "<sup>14</sup> तिमीहरूले भन्न सक्छौ, ‘हामी मिश्रमा जानेछौं जहाँ हामी युद्धको सम्मुखिन हुनु पर्दैन, मिश्रमा हामीले युद्धको तुरहीहरू सुन्नु पर्दैन, हामी भोक-भोकै बस्नुपर्दैन।’",
			"15": "<sup>15</sup> यदि तिमीहरूले ती कुराहरू भन्छौ भने, तब परमप्रभुको सन्देश ध्यान दिएर सुन्, हे यहूदामा बाँचेर रहेका मानिसहरू! सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, ‘यदि तिमीहरू मिश्रमा जाने निर्णय लिन्छौ अनि त्यहाँ बस्छौ भने यो तिनीहरूलाई हुनेछ।",
			"16": "<sup>16</sup> तिमीहरू तरवारसित डराउँछौ, तर तिमीहरूलाई त्यहाँ तरवारले परास्त गर्ने नै छ। तिमीहरू भोकको चिन्ता गर्छौ तर, यसले तिमीहरूलाई मिश्रमा पनि पछ्याउनेछ अनि तिमीहरू त्यहाँ मर्नेछौ।",
			"17": "<sup>17</sup> प्रत्येक मानिस जो मिश्र गएर बस्ने निर्णय लिन्छ, तरवार, अनिकाल र भयानक रोगबाट मर्नेछ। अनि कोही पनि मानिस मैले ल्याएको त्यो भयानक अनिष्टबाट बाँच्ने छैन।’",
			"18": "<sup>18</sup> “सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ ‘यरूशलेम बासिन्दाहरूमाथि मैले मेरो क्रोध देखाएँ। त्यसरी नै जो मिश्र जाँदैछन् तिनीहरूमाथि क्रोध बर्षाउनेछु। तिमीहरू श्राप, अपराध र निन्दाको पात्र बनिनेछौ अनि तिनीहरूले यहूदा फेरि कहिल्यै देख्ने छैनौ।’",
			"19": "<sup>19</sup> “यहूदामा बाँचेका मानिसहरू हो, परमप्रभुले तिमीहरूलाई भन्नु भएको छ ‘मिश्रमा नजाऊ।’ तिमीहरू निश्चय जान्दछौ कि म तिमीहरूलाई चेताउनी दिँदैछु।",
			"20": "<sup>20</sup> तिमीहरूले गल्ती गरिरहेका छौ जुन गल्ती तिमीहरूकै मृत्युको कारण बनिनेछ। तिमीहरूले मलाई परमप्रभु तिमीहरूका परमेश्वरकहाँ पठायौ र भन्यौ, ‘हाम्रो लागि परमप्रभु हाम्रा परमेश्वरसँग प्रार्थना गरिदेऊ अनि परमप्रभुले भन्नुभएको प्रत्येक कुरा हामीलाई भनिदिनु अनि हामी त्यो मान्नेछौ।’",
			"21": "<sup>21</sup> यसकारण आज मैले तिमीहरूलाई परमप्रभुबाट सन्देश भनेको छु। तर तिमीहरूले मैले बताएको कुनै कुरामा पनि परमप्रभु तिमीहरूका परमेश्वरको आज्ञा पालन गरेका छैनौ।",
			"22": "<sup>22</sup> यसर्थ निश्चित हौ कि तिमीहरूले बुझ्छौ। तिमीहरू मिश्रमा गई बस्न चाहन्छौ। तर मिश्रमा तिमीहरूमाथि यी घटनाहरू हुनेछ। तिमीहरू तरवार, अनिकाल अथवा भयानक रूढीले मर्नेछौ।”"
		},
		"43": {
			"1": "<sup>1</sup> यसकारण परमप्रभु तिनीहरूका परमेश्वरबाट आएको सन्देश यर्मियाले मानिसहरूलाई भनी सिध्याए। परमप्रभुले मानिसहरूलाई भन्नु भनी यर्मियालाई भन्नु भएको प्रत्येक कुरो तिनले भने।",
			"2": "<sup>2</sup> होशायाहको छोरो अजर्याह, कारेहको छोरो योहानान, अनि सबै अभिमानी मानिसहरू घमण्डी र हठ्ठी थिए। तिनीहरूले यर्मियालाई भने, “यर्मिया तिमी झूटो छौ! परमप्रभु हामीहरूका परमेश्वरले हामीलाई ‘तिमीहरू मिश्रमा बस्नु जानु भन्नु भनी तिमीलाई सुझाउ दिनु भएको छैन।’",
			"3": "<sup>3</sup> यर्मिया, हामी सोच्छौ कि नेरियाहको छोरो बारूकले हाम्रो विरूद्ध तिमीलाई अनुप्रेरित गरिरहेको छ। उसले चाहन्छ कि तिमीले हामीलाई बाबेलका मानिसहरूलाई सुम्पिदेऊ। उसले तिमीलाई त्यही काम गराउन चाहन्छ ताकि तिनीहरूले हामीलाई मार्न सकुन्। अथवा उसले तिमीबाट त्यसो गर्न चाहन्छ ताकि तिनीहरूले हामीलाई कैदीहरू बनाउन सक्छन् र बाबेल लैजान्छन्।”",
			"4": "<sup>4</sup> यसर्थ, कारेहको छोरो योहानान, सेना हाकिमहरू, अनि सारा मानिसहरूले परमप्रभुको आज्ञा उल्लंघन गरे। परमप्रभुले तिनीहरूलाई यहूदामा बस्न भनी आज्ञा दिनु भएको थियो।",
			"5": "<sup>5</sup> परमप्रभुको आज्ञा पालन गर्नुको सट्टा, योहानन र सेना हाकिमहरूले ती रहेका मानिसहरूलाई मिश्रमा लगे। बितेका दिनहरूमा शत्रुहरूले ती बाँचेका रहल मानिसहरूलाई अन्य देशहरूमा लगेका थिए तर तिनीहरू यहूदामा फर्केर आए।",
			"6": "<sup>6</sup> अहिले, योहानान र सबै सैन्य हाकिमहरूले सम्पूर्ण लोग्नेमानिसहरू, स्त्रीमानिसहरू, नानीहरू, राजकुमारीहरू अनि सबै अरू मानिसहरू जसलाई बाबेल राजाको विशेष रक्षाहरूका कप्तान नबूजरदानले गदल्याहको जिम्मामा छोडेका थिए तिनीहरूलाई मिश्रमा लगे। योहानानले अगमवक्ता यर्मिया अनि नेरियाहको छोरो बारूकलाई पनि लगे।",
			"7": "<sup>7</sup> ती मानिसहरूले परमप्रभुलाई ध्यानै दिएनन्। यसकारण ती सबै मानिसहरूलाई मिश्र लगे। तिनीहरू तहपनेस शहरमा गए।",
			"8": "<sup>8</sup> तहपनेस शहरमा, यर्मियाले परमप्रभुबाट सन्देश प्राप्त गरे",
			"9": "<sup>9</sup> “परमप्रभुले यर्मियालाई भने, ठूल-ठूलो ढुङ्गाहरू हातमा र तिनीहरूलाई ल्याऊ र फिरऊनको तहपनेस महलको हिलो र ईंटाले छापेको सडकमा गाडी देऊ। यहूदाका मानिसहरूले हेरिरहँदा नै तिमीहरूले त्यसो गर।",
			"10": "<sup>10</sup> तब ती मानिसहरूलाई यसो भन कि ‘सेनाहरूका परमप्रभु, इस्राएलका परमेश्वरले भन्नुहुन्छ म बाबेलका राजा नबूकदनेस्सरलाई जो मेरो दास हो यहाँ बोलाउनेछु। अनि उसको सिंहासन यहाँ मैले ढुङ्गा लुकाएको ठाउँमा खडा गराउँछु नबूकदनेस्सरले आफ्नो राज-छत्र ती ढुङ्गाहरू माथि फैलाउनेछ।",
			"11": "<sup>11</sup> नबूकदनेस्सर यहाँ आउनेछ र मिश्रमाथि आक्रमण गर्नेछ। उसले मर्नु पर्नेहरूको निम्ति मृत्यु ल्याउँनेछ। उसले तिनीहरूलाई कैद-स्थिति ल्याउने छ जो कैदमा पर्नु पर्नेछ। अनि उसले तिनीहरूको लागि तरवार ल्याउनेछ जो तरवारले मारिनु पर्नेछ।",
			"12": "<sup>12</sup> नबूकदनेस्सरले मिश्रका झूटो देवताहरूको मन्दिरहरूमा आगो जलाउनेछ। उसले ती मन्दिरहरू जलाउनेछ र ती मूर्तिहरू लैजानेछ। जसरी एकजना गोठालाले आफ्नो पोषाक सफा पार्छ। त्यसरी नै, नबूदकनेस्सरले मिश्रलाई सफा पार्ने छ। तब उसले मिश्रालाई सुरक्षित छाडिदिनेछु।",
			"13": "<sup>13</sup> नबूकदनेस्सरले मिश्रको सूर्य देवको मन्दिरमा रहेको स्मृति ढुङ्गाहरू नष्ट गर्नेछ जुन मिश्रमा सूर्य-देवताको मन्दिरमा छ। उसले मिश्रका जम्मै मन्दिरहरू र देवताहरूलाई आगो लगाइदिनेछन्।’”"
		},
		"44": {
			"1": "<sup>1</sup> मिश्रमा भएका यहूदी मानिसहरूको निम्ति परमप्रभुको सन्देश\nपरमप्रभुको सन्देश यर्मियाकहाँ आयो। यो सन्देश मिश्रमा बस्ने मिग्दोल, तहपनेस, मेम्फिस र माथिल्लो मिश्रमा बस्ने यहूदाका मानिसहरूका लागि थियो।",
			"2": "<sup>2</sup> “सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ: ‘मैले ल्याएका महा-आपतहरू तिमीहरूले यरूशलेम र यहूदाका शहरहरूमा देखेका छौ। ती शहरहरू भग्नावशेष मात्र छन् र त्यहाँ कोही मानिस बस्दैन।",
			"3": "<sup>3</sup> ती ठाउँहरू नष्ट गरिए किनभने तिनीहरूमा बस्ने मानिसहरूले दुष्ट कामहरू गरे। ती मानिसहरूले अन्य देवताहरूलाई बलिदानहरू चढाए जसलाई न तिनीहरू न तिमीहरू न तिमीहरूका पिता-पुर्खाहरूले जान्दथे, अनि तिनीहरूलाई पूजा गरेर त्यसले मलाई रीस उठायो।",
			"4": "<sup>4</sup> मैले घरिघरि तिमीहरूकहाँ अगमवक्ताहरू जो मेरो दासहरू हुन पठाँए। अगमवक्ताहरूले “यस्ता भयानक पापहरू जुन म घृणा गर्छु नगर्नु भनी तिमीहरूलाई आग्रह गरें।”",
			"5": "<sup>5</sup> तर तिनीहरूले तिनीहरूका नराम्रा कार्यहरूबाट फर्कन मानेनन् तिनीहरूको आग्रहमा कुनै ध्यानै दिए। तिनीहरूले अन्य देवताहरूलाई बलिदानहरू छाडेनन्।",
			"6": "<sup>6</sup> यही कारण मैले मेरो क्रोध यहूदा र यरूशलेमका मानिसहरूमाथि देखाएँ। मैले यहूदाका शहरहरू र यरूशलेमका गल्लीहरूलाई दण्ड दिएँ। मेरो क्रोधको कारणले यरूशलेम र यहूदाका शहरहरू भग्नावशेषमा परिणत भयो अनि आज ती शहरहरू शून्य छन्।’",
			"7": "<sup>7</sup> “यसकारण, सेनाहरूका परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ: ‘मूर्तिपूजा गरेर किन तिमीहरूले स्वयंमा यति विध्न नोक्सान ल्याइ रहेकाछौ? तिमीहरू किन यहूदा देखि किन पुरूष, स्त्री, बालक र शिशुहरूलाई अगल गरिरहेका छौ। किन तिमीहरू आफ्नै निम्ति पनि भावी सन्तानहरू राख्दैनौ?",
			"8": "<sup>8</sup> अहिले मिश्रमा झूटो देवता तिमीहरूले पुज्दै मलाई रीस उठाउँदै बसिरहेका छौ। तिमीहरू आफैंले आफैंलाई नष्ट पारिरहेका छौ। तिमीहरू आफैंले आफैंलाई अन्य जाति-जातिहरूको सम्मुख श्राप र खिसीको पात्र बनाएका छौ।",
			"9": "<sup>9</sup> के तिमीहरूले तिम्रा पिता-पुर्खाहरू, यहूदाका राजाहरू, तिनीहरूका पत्नीहरू, तिमीहरू आफ्नै अनि आफ्ना पत्नीहरूको दुष्टाँई जो तिनीहरूले यहूदाको देशमा र यरूशलेमका गल्लीहरूमा गरेका थिए, भुल्यौ?",
			"10": "<sup>10</sup> अहिले सम्म यहूदाका मानिसहरूले आफूलाई विनम्र बनाएका छैनन्। तिनीहरूले कुनै प्रकारको सम्मान म प्रति देखाएका छैनन्। ती मानिसहरूले मेरो शिक्षाहरू अनुशरण गरेनन्। तिनीहरूले व्यवस्था र विधिहरू पालन गरेनन् जुन मैले तिमीहरू अनि तिमीहरूका पिता-पुर्खाहरूका निम्ति बनाएको थिएँ।’",
			"11": "<sup>11</sup> “यसकारण, सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर यसो भन्नुहुन्छ: ‘मैले तिमीहरूमाथि भयानक घटनाहरू घटुन् भनेर निर्णय गरें। म यहूदाका सारा परिवारलाई नष्ट गर्नेछु।",
			"12": "<sup>12</sup> यहूदामा केही बाँचेकाहरू जसले मिश्रमा जाने र बस्ने निर्णय लिएका छन् तिनीहरू सबै मिश्रमा मर्नेछन्। तिनीहरू साना देखि ठूला तिनीहरू स्तरका सबै तरवार र अनिकालले मर्नेछन्। तिनीहरू श्राप, त्रास, हाँसो अनि अपमानको पात्र हुनेछन्।",
			"13": "<sup>13</sup> जो मानिसहरू मिश्रमा बस्न गएका छन् तिनीहरूलाई दण्ड दिनेछु। म तरवारहरू, अनिकाल, भयानक हैंजा फैलाएर तिनीहरूमाथि दण्ड दिनेछु। म तिनीहरूलाई यरूशलेमको शहरलाई दण्डित पारे जस्तै गर्नेछु।",
			"14": "<sup>14</sup> यहूदामा जीवितहरू रहेका जो मिश्रमा फर्कन्छन् तिनीहरू मध्ये म कसैलाई पनि जीवित राख्दिनँ। तिनीहरू मध्ये कसैले यहूदामा फर्की बस्ने इच्छा गरे पनि केही बाहेक कोही पनि बाँच्ने छैनन्।’”",
			"15": "<sup>15</sup> यहूदाका पत्नी मानिसहरू जो मिश्रमा बस्न फर्केका थिए तिनीहरूले अन्य देवताहरूलाई बलिदानहरू चढाउँथे अनि त्यो सत्य तिनीहरूका लोग्नेहरूले जान्दथे तर तिनीहरूले आफ्ना पत्नीहरूलाई त्यसो गर्न रोकेनन्। त्यहाँ यहूदाका एक ठूलो हुद्दा भेला हुन्थे। तिनीहरू दक्षिणी मिश्रमा बस्ने यहूदाका मानिसहरू थिए। ती अन्य देवताहरूलाई बलिदानहरू चढाउने स्त्रीहरूका पतिहरूले यर्मियाहलाई भने,",
			"16": "<sup>16</sup> “परमप्रभुबाट आएको सन्देश जो तिमीले हामीलाई सुनायौ त्यो हामी मान्दैनौं।",
			"17": "<sup>17</sup> हामीले स्वर्गकी रानीलाई बलिदानहरू चढाउछौ भनी प्रतिज्ञा गरेका छौं र हामीले जे प्रतिज्ञा गरेका छौं त्यही गर्ने छौं। हामी बलिदानहरू चढाएउँछौ अनि उनको पूजामा हामी उनलाई अर्घबलि खन्याउँछौं। पहिले पनि हामीले यसै गरेका थियौं हाम्रा पिता-पुर्खाहरू, हाम्रा राजाहरू तथा हाम्रा अधिकारीहरूले विगतमा त्यसै गरे। यहूदा शहर र यरूशलेमको गल्लीहरूमा पनि हामीले त्यसै गर्यौं। त्यस समयमा हामीले स्वर्गकी रानीको पूजा गर्यौ र हामीसँग प्रशस्त भोजनहरू थियो। हामी सफल भयौं हामीलाई केही भएको थिएन्।",
			"18": "<sup>18</sup> तर त्यसपछि हामीले स्वर्गकी रानीलाई बलिदानहरू चढाउन छोड्यौं। उनमाथि हामीले अर्घबलि चढाउँन छाड्यौं। जब हामीले उनलाई त्यसरी पूज्न छाड्यौं प्रत्येक कुरोको अभाव हुन थाल्यो। हामी तरवार र अनिकालले मारियौ।”",
			"19": "<sup>19</sup> तब स्वास्नीमानिसहरूले यर्मियालाई भने, “हामीहरूले जे गर्दै थियौ र जब हामीले स्वर्गकी रानीलाई बलिहरू चढायौं अनि अर्घबलिहरू अपर्ण गर्यौ, हाम्रा पतिहरूले जान्देथे। पूजा गर्नु भनी जब हामीले स्वर्गकी रानीको निम्ति तिनको मूर्ति भएको केकहरू बनायौ हाम्रा पतिहरूले जान्दथे।”",
			"20": "<sup>20</sup> तब यर्मियाले जम्मै मानिसहरू पुरूषहरू, स्त्रीहरू र ज-जसले तिनलाई उत्तर दिएका थिए तिनीहरूलाई भने।",
			"21": "<sup>21</sup> यर्मियाले तिनीहरूलाई भने, “तिमीहरूले तिमीहरूका पिता-पुर्खाहरू, राजाहरू र भारद्वारहरू अनि यहूदाको शहर र यरूशलेमको गल्लीहरूको देशका मानिसहरूले गरेका अर्पणहरू विषयमा परमप्रभुले राम्ररी जान्नु हुन्छ।",
			"22": "<sup>22</sup> तिनीहरूले गरेका कुकर्महरू परमप्रभुले जुन घृणा गर्नुहुन्छ अब सहनुहुन्न। यही कारणले तिमीहरूको देशमा कोही पनि बस्दैन र आज जस्तो छ, त्यो श्रापित, उजाड र बाँझो भएको छ.",
			"23": "<sup>23</sup> सारा ती टुष्ट कुराहरू हुन गएको कारण तिमीहरूले झूटा देवताहरू प्रति गरेको बलिदानहरूले गर्दा हो। तिमीहरूले परमप्रभुको विरूद्ध पाप गर्यौ। तिमीहरूले परमप्रभुको आज्ञा पालन गरेनौ। तिमीहरूले उहाँले दिनु भएको विधि र व्यवस्था अनुशरण गरेनौ। यसकारण आज यो महासर्वनाश आइलाग्यो।”",
			"24": "<sup>24</sup> तब यर्मियाले ती स्वास्नीमानिसहरू सहित जम्मै पुरूष मानिसहरूलाई भने, “यहूदाका सारा मानिसहरू जो अहिले मिश्रमा छौ; परमप्रभुको सन्देश सुन,",
			"25": "<sup>25</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ ‘तिमीहरू र तिमीहरूका पत्नीहरूले जे गर्छु भनेका थियौ त्यही गर। “हामी हाम्रा प्रतिज्ञा पूरा गर्छौ, भेटीहरू र अर्घबलि स्वर्गकी रानीलाई चढाउँछौं।” यसर्थ तिमीहरूको काममा अघि बढ अनि तिमीहरूको प्रतिज्ञा पूरा गर। तिमीहरूको प्रतिज्ञा राख।’",
			"26": "<sup>26</sup> तर, परमप्रभुको सन्देश सुन, मिश्रमा बसिरहेका यहूदाका मानिसहरू म परमप्रभु भन्छु कि: ‘म आफ्नो महान नाउँमा प्रतिज्ञा गर्छु कि आजदेखि उता मिश्रमा बस्ने यहूदाका मानिसहरू कसैले पनि निश्चित रूपमा “परमप्रभु हुनुहुन्छ।” भनी मेरो नाउँ लिने छैन।",
			"27": "<sup>27</sup> म ती मिश्रमा बासो-बास गर्ने यहूदाका मानिसहरूलाई नियाली रहेको छु। म तिनीहरूको रक्षाको लागि होइन तर नष्ट पार्नको लागि नियाली रहेको छु। मिश्रमा बस्ने यहूदाका मानिसहरू अनिकाल र तरवारद्वारा मारिनेछन्, जबसम्म तिनीहरू पूरा खतम हुँदैनन् तबसम्म लगातार मरि नै रहनेछन्।",
			"28": "<sup>28</sup> तरवारबाट बाँचेकाहरू थोरै मात्र यहूदामा फर्कन्छन् अनि जो यहूदामा बाँचेका छन् र मिश्रमा बस्नजान्छन् तिनीहरूले बुझ्नेछन् कि मेरो वचन कि तिनीहरूको वचन साँच्चो हुनेछ?",
			"29": "<sup>29</sup> तब म तिमीहरूलाई प्रमाण दिनेछु,’ यो परमप्रभुको सन्देश हो, ‘म मिश्रमा तिमीहरूलाई दण्ड दिनेछु अनि तिमीहरूले जान्नेछौ कि तिमीहरूलाई नोक्सान पुर्याउने मेरो वचन साँच्चो ठहरिनेछ।’",
			"30": "<sup>30</sup> परमप्रभु भन्नुहुन्छ, ‘म जे भन्छु त्यो हुनेछ। मैले यहूदाका राजा सिदकियाह जो उसको शत्रु अनि जसले उसलाई मार्न खोजिरहेको थियो बाबेलको राजा नबूकदनेस्सरलाई दिएँ। मिश्रको राजा फिरऊन होप्रोलाई उसका शत्रुहरू जसले मार्न खाजिरहेका छन्, तिनीहरूलाई सुम्पिनेछु।’”"
		},
		"45": {
			"1": "<sup>1</sup> बारूकलाई एउटा सन्देश\nयहूदाका राजा योशियाहको छोरो यहोयाकीमको राज्यकालको चौथो बर्षमा अगमवक्ता यर्मियाले बारूकलाई यी कुराहरू भने। उसले यर्मियाको बेहेरूवामा ती कुराहरू लेखे यर्मियाले बारूकलाई जे भने त्यो यही हो।",
			"2": "<sup>2</sup> “परमप्रभु इस्राएलका परमेश्वरले तिमीलाई यसो भन्नुहुन्छ, बारूक, तिमीले भनेका छौ, यो मेरो लागि अति नराम्रो हो।",
			"3": "<sup>3</sup> ‘परमप्रभुले मेरो पीडा सहित मलाई दु:ख दिनु भएको छ। म अत्यन्त थकित छु। मेरो पीडाको कारणले गर्दा म थकित मात्र होइन गलेको पनि छु। म आराम पाउन सक्तिनँ।’",
			"4": "<sup>4</sup> यर्मिया, बारूकलाई यो भने, परमप्रभुले यसो भन्नुहुन्छ ‘मैले बनाएको सबै कुरा भत्काईदिनेछु। अनि मैले जे रोपेको छु उखेल्नेछु। यहूदाको जताततै म त्यसो गर्ने छु।’",
			"5": "<sup>5</sup> परमप्रभुले भन्नुभयो, ‘बारूक तिमीले आफ्नै लागि ठूलो कुरा खोजिरहेका छौ। तर ती चीजहरू नखोज किनभने म सम्पूर्ण मानिसहरूमाथि भयानक कष्ट ल्याउनेछु।’ परमप्रभुले यी कुराहरू भन्नुभयो। ‘तिमी धेरै ठाउँमा जानु पर्नेछ। तर तिमी जहाँ जान्छौ म तिमीलाई बाँच्ने मौका दिन्छु।’”"
		},
		"46": {
			"1": "<sup>1</sup> राष्ट्रबरूकालागि परमेश्वरको सन्देशहरू\nती वचनहरू जो परमप्रभुबाट अगमवक्ता यर्मियाकहाँ आए धेरै जाति-जातिहरूको विषयमा छ।",
			"2": "<sup>2</sup> मिश्रका बारेमा सन्देशहरू\nयो सन्देश मिश्र देशको विषयमा हो। यो सन्देश मिश्रदेशका राजा फिरऊन नेकोका सेनाको बारेमा छ। उसको सेना यूफ्रेटिस नदीको किनार कर्कशमा भन्ने शहरमा पराजित भएको थियो। जब बाबेलका राजा नबूकदनेस्सरले फिरऊन नेकोको सेनालाई कर्कशमा पराजित गरे। त्यसबेला योशियाहको छोरो यहोयाकाम यहूदाका राजा भएको चार बर्ष पुगेको थियो। यो मिश्रलाई परमप्रभुको सन्देश होः",
			"3": "<sup>3</sup> “तिम्रो ठूलो र सानो ढालहरू तैयार पार।\nयुद्धको लागि अघि बढ।",
			"4": "<sup>4</sup> घोडाहरू तैयार पार सिपाहीहरू,\nआफ्नो घोडाहरूमाथि चढेर,\nयुद्धभूमिमा जाऊ।\nलडाइँको टोपहरू लगाऊ।\nभालाहरू तीखो पार,\nर झिलम लगाऊ।",
			"5": "<sup>5</sup> मैले के देखें?\nसिपाहीहरू भयभीत छन्,\nअनि भागिरहेका छन्।\nती वीर सेनाहरू,\nपरास्त भइरहेका छन्।\nपछि नहेरी तिनीहरू हतारिएर भागिरहेका छन्।\nत्यहाँ वरिपरि त्रास छ।”\nपरमप्रभुले यी कुराहरू भन्नुभयो।",
			"6": "<sup>6</sup> “फुर्तीला धावकहरू पनि भाग्न सक्तैनन्।\nबलियो सिपाहीहरू पनि उम्कन सक्तैनन्।\nतिनीहरू सबै ठेस लागेर लड्ने छन्।\nउत्तरमा यूफ्रेटिस् नदीको किनारमा यस्तो घटनाहरू हुनेछन्।",
			"7": "<sup>7</sup> नील नदी जस्तै को आइरहेछ?\nको त्यस्तो तीव्र नदीहरू झैं आइरहेछ?",
			"8": "<sup>8</sup> यो मिश्र हो जो नील नदी झैं आइरहेछ।\nयो मिश्र हो जो त्यो तीव्र नदीहरू झैं उर्लेर आईरहेछ।\nमिश्रले भन्दछ, ‘म आउने छु अनि पृथ्वीलाई ढाक्नेछु।\nम शहरहरू र त्यहाँ बस्ने मानिसहरूलाई नाश पार्नेछु।’",
			"9": "<sup>9</sup> हे घोड-सवार सिपाहीहरू, अघिबढ!\nहे रथ हाँक्नेहरू चाँडो हाँक!\nहे वीर सिपाहीहरू, अघि बढ!\nकूश र पूटबाट आएका सिपाहीहरू,\nतिमीहरू आफ्ना ढालहरू बोक।\nलूदबाट आएका सिपाहीहरू,\nतिमीहरू धनु समाल।",
			"10": "<sup>10</sup> “तर त्यस समय, हाम्रो मालिक सेनाहरूका परमप्रभु, परमेश्वरले\nआफ्नो शत्रुहरूमाथि बद्ला लिनुहुनेछ।\nत्यो बद्ला लिने दिन हुनेछ।\nतरवारले मार्नेछ अनि सन्तुष्ट हुनेछ।\nतिनीहरूको रगतले तरवार भरिनेछ।\nयो उत्तर देशको यूफ्रेटिस नदीको किनारमा मेरो मालिक,\nसेनाहरूका परमप्रभु निम्ति बलि हुनेछ।",
			"11": "<sup>11</sup> “हे मिश्र देशकी कन्ये-छोरी! गिलाद जाऊ अनि औषधि ल्याऊ।\nअनि सबै औषधिहरू जो तिमीले लगाउँछौ त्यो व्यर्थको हुनेछन्।\nतिमी निको हुने छैनौ।",
			"12": "<sup>12</sup> तिम्रो अपमान विषयमा जाति-जातिहरूले सुन्नेछन्।\nतिम्रो रूवाँई सारा पृथ्वीभरि सुनिने छ।\nएउटा ‘वीर सिपाही’ अर्को ‘वीर सिपाहीमाथि’ पल्टिनेछ\nअनि दुवै ‘वीर सिपाहीहरू’ एकैचोटी लड्छन्।”",
			"13": "<sup>13</sup> यो सन्देश अगमवक्ता यर्मियाकहाँ परमप्रभुबाट आयो जब नबूकदनेस्सर मिश्रमाथि आक्रमण गर्न अघि बढिरहेको थियो।",
			"14": "<sup>14</sup> “यो सन्देश मिश्र, मिग्दोल, मेमफिस\nर तहपन्हेसमा घोषणा गर,\nर यसो भन्, ‘युद्धको लागि तैयार होऊ।\nकिनभने तिम्रो वरिपरि तरवारले खाँदैछ।’",
			"15": "<sup>15</sup> तिम्रो गोरू किन फ्याँकिएको छ?\nके त्यो उभिन सक्तैन?\nकिनभने परमप्रभुले तिनीहरूलाई तल घचेटि दिनुहुनेछ।",
			"16": "<sup>16</sup> धेरै सिपाहीहरू ठक्करखानेछन् अनि लड्नेछन्।\nतब तिनीहरू परस्परमा भन्नेछन्,\n‘उठ, हाम्रा आफ्ना मानिसहरूकहाँ जाऊँ।\nहामी हाम्रा अत्याचारीहरूको तरवार भन्दा,\nटाढो आफ्ना देशमा फर्कौ, हामी भाग्नै पर्छ\nकिनभने हाम्रा शत्रुहरूले हामीलाई परास्त गरे।’",
			"17": "<sup>17</sup> उनीहरूको आफ्नै शहरमा ,ती सैनिकहरूले भन्ने छन्,\n‘मिश्रको राजा फिरऊन एक चर्को हल्ला मात्र हो।\nउसको महिमाको समय सक्किएको छ।’”",
			"18": "<sup>18</sup> जस्तो म जीवित छु।\nराजा भन्छन्, “यो राजाको नाउँ हो,\nसेनाहरूका परमप्रभु आउनु हुनेछ।\nउनी याबेर पर्वत अनि समुद्रको किनारको,\nकर्मेल पर्वत जस्तै महान हुनेछ।",
			"19": "<sup>19</sup> हे मिश्रकी छोरी! आफ्ना सामानहरू बाँधबुध गर,\nदासत्वको लागि तैयार हौं,\nकिनभने मेमफिस शहर नष्ट हुनेछ,\nती सहरहरू नष्टहुनेछन्।\nजन शून्य बेकामे भूमि हुनेछ।",
			"20": "<sup>20</sup> “मिश्र एउटी सुन्दरी गाई जस्तै हो,\nतर एउटा डाँस उत्तरबाट उसकहाँ आइरहेछ।",
			"21": "<sup>21</sup> मिश्रका भाडाका सिपाहीहरू पोसिएका गोरूहरू झैं,\nतिनीहरू सबै पीठ फर्काएर भागे।\nतिनीहरू आक्रमणकारीहरू समक्ष एक क्षण टिक्न सकेनन्।\nतिनीहरूको नाशको समय आईरहेछ।\nतिनीहरू दण्डित हुनेछन्।",
			"22": "<sup>22</sup> मिश्र सर्पको झैं आवाज गर्दै\nसुल्किरहेको छ।\nकिनभने उसको शत्रुहरू तेजले आएकाछन्।\nशत्रुले मिश्रलाई बन्चरोहरूले रूख काटे झैं आक्रमण गर्नेछ।”",
			"23": "<sup>23</sup> परमप्रभु भन्नुहुन्छ:\n“तिनीहरूले उसको जङ्गल,\nबाक्लो भए पनि फाँड्नेछन्।\nशत्रुका सेनाहरू सलह झैं छन्,\nअनि कसैले गन्न सक्तैन।",
			"24": "<sup>24</sup> मिश्रकी छोरी लज्जित हुनेछ।\nउत्तर दिशाबाट आएका शत्रुले उसलाई पराजित गर्नेछ।”",
			"25": "<sup>25</sup> सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ, “म थेबेसको आमोनलाई दण्ड दिनेवाला छु। अनि म फिरऊन, मिश्र र त्यसका देवताहरूलाई दण्ड दिनेछु। म मिश्रका राजाहरूलाई र फिरऊनमाथि भरोसा गर्नेहरूलाई दण्ड दिनेछु।",
			"26": "<sup>26</sup> म तिनीहरूलाई बाबेलको राजा नबूकदनेस्सर र उसको सेवकहरूको हातमा सुम्पिनेछु जसले तिनीहरूलाई मार्न खोजिरहेका छन्।”\n“यस नष्ट पछि मिश्रमा फेरि अघि झैं मानिसहरू बसो-बास गर्नेछन्।” परमप्रभुले यी कुराहरू भन्नुभयो।",
			"27": "<sup>27</sup> उत्तर इस्राएललाई सन्देश\n“हे याकूब, मेरो दास! नडराऊ।\nहे इस्राएल, होतोत्साही नबन।\nम तिमीहरू र तिम्रो नानीहरूलाई ती सुदूर,\nर तिमीहरूलाई कैदी बनाउने ठाउँबाट बचाउनेछु।\nयाकूब फर्कनेछ अनि शान्ति र सुरक्षा पाउने छ,\nअनि कसैले पनि उसलाई भयभित पार्नेछैन।”",
			"28": "<sup>28</sup> परमप्रभु भन्नुहुन्छ,\n“हे याकूब, मेरो दास! नडराऊ।\nम तिमीसँग छु।\nमैले तिमीलाई जहाँ पठाएको थिएँ,\nती जाति-जातिहरू सबै नष्ट पार्नेछु।\nतर म तिमीलाई पूर्णरूपले नष्ट गर्ने छैन।\nतिमीले गरेको गल्तीको सजाय तिमीले पाउनु नै पर्छ,\nआवश्यकता अनुसार म तिमीलाई सुधार्नेछु\nर तिमीलाई सजाय नदिइ मुक्त पार्नेछैन।”"
		},
		"47": {
			"1": "<sup>1</sup> पलिश्ती मानिसहरूका बारेमा सन्देश\nयो सन्देश पलिश्ती मानिसहरूका बारेमा अगमवक्ता यर्मियालाई परमप्रभुबाट आयो। यो सन्देश फिरऊनले गाजा शहरमा आक्रमण गर्न अघि नै आएको थियो।",
			"2": "<sup>2</sup> परमप्रभु भन्नुहुन्छ:\n“हेर, शत्रुका सिपाहीहरू उत्तरमा भेला भइरहेकाछन्।\nतिनीहरू किनारमा छालले हिर्काउँदै उर्लेको नदी जस्तै आउनेछन्।\nतिनीहरूले बाढीले झैं सारा देश शहरहरू\nर त्यहाँ बस्ने मानिसहरू ढाक्नेछन्।\nत्यस देशका मानिसहरूले विलौना गर्नेछन्\nतथा गुहार माग्नेछन्।",
			"3": "<sup>3</sup> तिनीहरूले घोडाहरू दौडेको टापहरू सुन्ने छन्,\nतिनीहरूले रथहरू दौडाइएको तेज आवाजहरू सुन्नेछन्,\nतिनीहरूले चक्काको गुडुल्किएको आवाज सुन्ने छन्।\nबाबुहरू आफ्ना नानीहरू जोगाउन असमर्थ हुनेछन्।\nतिनीहरू अति नै कमजोर हुनेछन्।",
			"4": "<sup>4</sup> सोर अनि सिदोनलाई सहायता गर्न सक्ने\nत्यहाँ रहेका प्रत्येक जिउँदालाई टुक्राउनको लागि\nपलिश्तीलाई सर्वनाश पार्ने समय आएको छ।\nपलिश्तीहरू र कप्तोर टापुमा रहेका मानिसहरू सबैलाई\nपरमप्रभुले नष्ट गर्नु हुनेछ।",
			"5": "<sup>5</sup> गाजाका मानिसहरूले आफ्नो केश खौरन्छन्।\nअश्कलोनका मानिसहरू चूपचाप रहन्छन्।\nहे बेंसीमा बाँच्नेहरू हो!\nकहिलेसम्म तिमीहरू स्वयंलाई काट्नेछौ?",
			"6": "<sup>6</sup> “हे परमप्रभुको तरवार, तिमी मुक्त भएकै छैनौ।\nकहिले सम्म तिमी लडाइँ गरिरहेने छौ?\nआफ्नो छापभित्र फर्केर जाऊ।\nरोक शान्त हौ।",
			"7": "<sup>7</sup> तर कसरी परमप्रभुको तरवार शान्त हुनसक्छ?\nपरमप्रभुले यसलाई आज्ञा दिनु भएको छ।\nउहाँले अश्कलोन र समुद्री किनारमा भएका\nशहरहरूलाई आक्रमण गर्नु आज्ञा दिनु भएको छ।”"
		},
		"48": {
			"1": "<sup>1</sup> मोआबको बारेमा सन्देश\nयो सन्देश मोआब देशसित सम्बन्धित छ। सेनाहरूका परमप्रभु इस्राएलका परमेश्वर भन्नुहुन्छ:\n“नबो पर्वतको दुर्भाग्य सामुन्ने छ।\nयो नष्ट हुनेछ।\nकिर्यातैमको शहर लज्जित हुनेछ\nअनि कब्जामा पर्नेछ।\nती बलिया किल्लाहरू लज्जित\nतथा हतोत्साही हुनेछन्।",
			"2": "<sup>2</sup> मोआबलाई फेरि प्रशंसा गरिने छैन।\nहेशबोनमा मानिसहरूले मोआबको पराजयको युक्ति रचे।\nतिनीहरूले भने, ‘आऊ, हामी मिलेर त्यस ठाउँलाई नष्ट पारौं,\nताकि अब उसो एउटा जाति नरहोस्।’\nहे मदमेन, तिमीलाई पनि चूपचाप पारिन्छ।\nतरवारले तिम्रो खेदो गर्नेछ।",
			"3": "<sup>3</sup> होरोनैमबाट आएको चिच्याहट सुन।\nतिनीहरू लुटपाट र विध्वंसले गर्दा चिच्याई रहेकाछन्।",
			"4": "<sup>4</sup> मोआब नष्ट हुने नै छ।\nउसका स-साना बालकहरू गुहार माग्दै रूने छन्।",
			"5": "<sup>5</sup> मोआबका मानिसहरू रूँदै\nलूहीतको उकालो बाटो जान्छन्।\nहोरोनैम तिर जाने ओह्रालो बाटोमा\nनष्टको परितापमा क्रन्दन सुनिन्छ।",
			"6": "<sup>6</sup> भाग जाऊ! भाग आफ्नो प्राण बचाऊ,\nमरुभूमिको जङ्गलमा हुने पोथ्रा झैं बन।",
			"7": "<sup>7</sup> “तिमीहरूले आफ्नो काम र धनमा भरोसा गर्यौ।\nयसकारण तिमीहरू कैदमा पर्यौ।\nउसका अधिकारीहरू र पूजाहारीहरूसितै,\nकमोशलाई देश निष्कासन गरियो।",
			"8": "<sup>8</sup> प्रत्येक शहर नष्ट पारिनेछ\nअनि कुनै एउटा शहर पनि बाँकी राखिनेछैन।\nहरेक खाल्टाहरू समतल पारिनेछ,\nसमतल भुमी नष्ट हुनेछ।\nपरमप्रभुले भन्नु भएको कुरा पुरा हुनेछ।\nअनि समतल देश नष्ट पारिनेछ।",
			"9": "<sup>9</sup> मोआबमा खेत बारि भरि नून छरिदेऊ\nकिनभने यो निश्चय नष्ट हुनेछ\nअनि बासिन्दा कोही नभएर\nमोआब शून्य मरुभूमि हुनेछ।",
			"10": "<sup>10</sup> श्रापित होस् त्यो मानिस जसले परमप्रभुको काम गर्न विलम्ब गरिरहेछ।\nधिक्कार छ त्यस मानिसलाई जसले ती मानिसहरू मार्न आफ्नो तरवार चलाँउदैन।",
			"11": "<sup>11</sup> “मोआबले अनिष्ट कहिल्यै देखेको छैन।\nमोआब ढुङ्ग्रोमा रहेको र एउटा भाँडादेखि\nअर्को भाँडामा नसारिएको दाखरस जस्तै छ।\nऊ कैदी भएर कहिले पनि बसेको छैन,\nयसकारण ऊ पहिल्यै जस्तै ताजा नै छ,\nअनि उसको स्वादमा बद्ली आएको छैन।”",
			"12": "<sup>12</sup> तब परमप्रभु भन्नुहुन्छ,\n“हेर, समय आइरहेछ जब म,\nउसलाई तिम्रो भाँडाबाट खन्याउँन,\nअनि खाली भाँडाहरू टुक्रा-टुक्रामा\nपरिणत गराउन मानिसहरू पठाउनेछु।”",
			"13": "<sup>13</sup> तब, मोआबका मानिसहरू कमोशले गर्दा लज्जित हुनेछन्। जसरी इस्राएलका मानिसहरूले झूटा देवता बेतेललाई विश्वास गरेका थिए। त्यही झूटा देवताले मद्दत नगर्दा तिनीहरू लज्जित भए।",
			"14": "<sup>14</sup> “तिमीहरू कसरी भन्न सक्छौ, ‘हामी बलिया सिपाहीहरू हौं\nअथवा हामी वीर योद्धाहरू हौं।’",
			"15": "<sup>15</sup> शत्रुहरूले मोआबमाथि आक्रमण गर्नेछन्,\nर शहरभित्र पसेर तिनीहरूलाई ध्वंस पार्नेछन्।\nउसका खास जवान मानिसहरू हत्या गरेर मारिनेछन्।”\nयो सन्देश महाराजाको हो,\nउहाँको नाउँ सेनाहरूका परमप्रभु हो।",
			"16": "<sup>16</sup> “मोआबको अन्त नजिकै छ।\nमोआबलाई चाँडै ध्वंस पारिनेछ।",
			"17": "<sup>17</sup> मोआबका वरिपरि बस्ने मानिसहरू\nतिमीहरू सबैले त्यो देशको लागि विलाप गर्नुपर्नेछ।\nतिमीहरू जान्दछौ मोआब कति प्रसिद्ध छ।\nयसर्थ त्यसको लागि रूनु\n‘मोआबको शक्तिशाली अनि महिमामय राजदण्ड पूर्णरूपले टुक्रियो।’",
			"18": "<sup>18</sup> “तिमीहरू दीबोनमा बसिरहेका छौ।\nआफ्नो सम्मानपूर्ण ठाउँबाट झरेर आऊ\nसुखा जमीनमा बस,\nकिनभने विनाश आइरहेछ,\nअनि उसले तिमीहरूको दरिलो शहरहरू भत्काइदिनेछ।",
			"19": "<sup>19</sup> “हे अरोएरमा बस्ने मानिसहरू हो।\nबाटोको किनारमा उभ र हेर,\nपुरूष मानिसहरू भागिरहेका हेर,\nस्त्री मानिसहरू भागिरहेका हेर,\nतिनीहरूलाई के भयो सोध।",
			"20": "<sup>20</sup> “मोआब धराशायी भएको छ\nलाजले पूर्ण भएको छ।\nविलौना गर अनि रोऊ।\nअर्नोन नदीमा गएर घोषणा गर कि मोआब ध्वंस भयो।",
			"21": "<sup>21</sup> समतल देशमाथि न्याय हुने समय आएको छ।\nहोलोन, यहसा र मेपात शहरहरूमा\nन्याय गरिने समय आएकोछ।",
			"22": "<sup>22</sup> दीबोन, नबो बेत-दिबलातैम शहरहरूमा\nन्याय गरिने समय आएकोछ।",
			"23": "<sup>23</sup> किर्यातैम, बेतगामूल, बेत-मोन शहरहरूमा\nन्याय गरिने समय आएकोछ।",
			"24": "<sup>24</sup> करियोत र बोज्रा र छेउ,\nटाढाका अनि नजिकका\nमोआब सबै शहरहरूमा\nन्याय गरिने समय आएकोछ।",
			"25": "<sup>25</sup> मोआबको सिंग काटियो\nअनि मोआबको पाखुरा भाँचिएको छ।”\nपरमप्रभुले यो कुरा भन्नुभयो।",
			"26": "<sup>26</sup> “मोआबले सम्झिएको थियो कि ऊ परमप्रभु भन्दा महान् हो।\nअत मोआबलाई पागल हुन्जेलसम्म सजाय देऊ।\nमोआब लडेर आफ्नै वान्तामा लटपटिने छ।\nमोआब मानिसहरूको हाँसोको पात्र हुनेछ।",
			"27": "<sup>27</sup> “मोआब तिमीले इस्राएलको हाँसो उडायौ\nइस्राएललाई एउटा चोरका समूहले पक्रे।\nप्रत्येक पल्ट तिमीहरूले इस्राएलको बारेमा बोल्दा आफ्नो टाउको हल्लायौ।\nअनि इस्राएलभन्दा असल छु भनी बाहना गर्यौ।",
			"28": "<sup>28</sup> मोआबका मानिसहरू हो आफ्नो शहरहरू छोड।\nजाऊ ढुङ्गाहरूमा बस,\nढुकुर जस्तो होऊ,\nजसले ओढारको मुखमा आफ्नो गुँड बनाउँछ।”",
			"29": "<sup>29</sup> “हामीले मोआबको घमण्ड सुनेकाछौं,\nऊ अत्यन्तै घमण्डी थियो।\nर उसले आफैं ठूलो हुँ भन्ने ठान्थ्यो।\nऊ आफैं गर्वाम्बित थियो।\nऊ अतिनै घमण्डी थियो।”",
			"30": "<sup>30</sup> परमप्रभु भन्नुहुन्छ, “मोआब कति रीसाह छ सो म जान्दछु,\nउसले आफ्नै प्रशंसाको गीत गाउँछ।\nतर ऊ झूटो अंहकार गर्छ\nऊ आफूले भनेको कुराहरू गर्न सक्तैन।",
			"31": "<sup>31</sup> यसर्थ म मोआबका मानिसहरूको लागि विलाप गर्दछु,\nम सम्पूर्म मोआबको लागि रून्छु।\nम कीर-हेरेसका मानिसहरूका लागि शोक गर्छु।",
			"32": "<sup>32</sup> म याजेरको मानिसहरूसित याजेरकै लागि शोक गर्छु।\nहे सिब्नाका दाखका बोट, बिगतमा तिम्रा दाखका बोटहरू\nचारैतिर फैलिंदै समुद्र पारी याजेर शहरसम्म फैलिएको थियो।\nतर विनाशले तिनीहरूका फल र दाखहरू लग्यो।",
			"33": "<sup>33</sup> मोआबको विस्तृत दाखबारीहरूबाट खुशी र आनन्द लगिएकोछ।\nमैले अङ्गुर निचोर्दा रस निक्लन बन्द गरिदिएँ।\nयसर्थ आनन्दको स्वरले तिनीहरूले अङ्गुर निचोर्दैनन्।\nस्वर छ तर आनन्दको स्वर होइन।",
			"34": "<sup>34</sup> “मानिसहरू हेशबोनदेखि एलाले अनि जहाजसम्म कराउँदैछन्। मानिसहरू विलाप गरी रहेका छन्। तिनीहरूको विलाप सोअर, होरोनैम, एग्लथ-शेलीशिया शहरहरूबाट सुनिन्छ। निम्रीमको पानी सुक्यो।",
			"35": "<sup>35</sup> म मोआबलाई उच्च स्थानहरूमा तिनीहरूको देवताहरूलाई सुगन्धित धूप बाल्न र बलि चढाउनु रोक्छु।” परमप्रभुले यो कुरा भन्नुभयो।",
			"36": "<sup>36</sup> “म मोआब र किरहरेसतका मानिसहरूको लागि उदास छु। मेरो हृदय बासुरीको शोक धुन जस्तै रून्छ किनभने तिनीहरूको जोगाड गरेको धन-सम्पति सबै नष्ट भयो।",
			"37": "<sup>37</sup> प्रत्येक मानिसको केश खौरेको छ, प्रत्येकले दाह्री काटेको छ।  प्रत्येकको हातहरू काटिएको छ। प्रत्येकले आफ्ना कम्मरमा उदासीनताको धोती बेरेको छ।",
			"38": "<sup>38</sup> घरका धुरीहरूमा तथा मोआब गल्लीहरूमा मरेर गएकाहरूको लागि मानिसहरू जता-ततै विलाप गरिरहेका छन्। समस्त भूमि नै उदासीनताले छाएको छ किनभने मोआबलाई मैले कसैलाई नचाहिने माटोको भाँडालाई झैं चकानाचूर पारेको छु।” परमप्रभुले यो कुरा भन्नुभयो।",
			"39": "<sup>39</sup> “मोआब तितर-बितर भएका छन्। मानिसहरू विलाप गरिरहेका छन्। मोआब लाजले वरिपरि घुम्छ। मानिसहरूले मोआबको खिल्ली उडाइरहे छन् ऊ आफ्नो वरिपरि प्रत्येकका निम्ति डरको पात्र बनेको छ।”",
			"40": "<sup>40</sup> परमप्रभु भन्नुहुन्छ, “हेर! एउटा चील आकाशबाट बेगहानेर आइररहेको छ।\nयसले आफ्नो पखेंटाहरू मोआब भरि फैलाई रहेछ।",
			"41": "<sup>41</sup> शहरहरू कब्जा गरिनेछ,\nबलिया किल्लाहरू घेरामा पर्नेछन्।\nत्यस समयमा मोआबका शक्तिशाली सिपाहीहरूको मुटु,\nप्रसव वेदनामा परेकी स्त्रीको जस्तो हुनेछ।",
			"42": "<sup>42</sup> मोआब देश नष्ट गराइनेछ।\nकिनभने उसले सम्झन्थ्यो कि ऊ परमप्रभु भन्दा महान् छ।”",
			"43": "<sup>43</sup> परमप्रभुले यी कुराहरू भन्नु हुन्छ:\n“मोआबका मानिसहरू हो! तिमीहरूको लागि डर, गहिरो खाडलहरू र पासोहरू छन्।",
			"44": "<sup>44</sup> मानिसहरू डराउने छन् र भाग्ने छन्,\nतर तिनीहरू गहिरो खाडलहरूमा पर्नेछन्।\nयदि तिनीहरू कसैले त्यहाँबाट उक्लिए भने पासोहरूमा फँस्नेछन्\nकिनभने म मोआबमाथि दण्डको बर्ष ल्याउनेछु।”\nपरमप्रभुले यी कुराहरू भन्नुभयो।",
			"45": "<sup>45</sup> “हेशबोनको छाँयामा शरणार्थीहरू नरहेको देखिन्छ।\nतिनीहरू रहेनन् कारण हेशबोनबाट आगो\nर सिहोनबाट ज्वाला गएको छ।\nयसले मोआबको निधार र ती अहिले आनन्द\nमनाउँदै गर्नेहरूको शिरलाई काटेर\nनास पारिदिनेछ।",
			"46": "<sup>46</sup> ए मोआब! तिम्रो निम्ति खराब हुनेछ!\nकमोशका मानिसहरू नष्ट भइरहेका छन्।\nतिम्रा छोरा-छोरीहरूलाई,\nकैदी बनाएर लगिनेछ।",
			"47": "<sup>47</sup> मोआबका मानिसहरू यसरि बधुवाईमा परेर टाढा लगिने छन्।\nतर समय आउनेछ, म मोआबबाट लागिएको फर्काउनेछु।”\nयो सन्देश परमप्रभुबाट थियो।\nयसरिनै मोआबको सजाय पूर्ण हुनेछ ।"
		},
		"49": {
			"1": "<sup>1</sup> अम्मोनवंशीहरूको बारेमा एउटा सन्देश\nयो सन्देश अम्मोनवासीहरूसित सम्बन्धित छ परमप्रभु भन्नुहुन्छ:\n“अम्मोनवासी हो, के तिमीहरू ठान्छौ कि\nइस्राएलका मानिसहरूका नानीहरू छैनन्?\nके तिनीहरूका अभिभावकहरू जमीनको उत्तराधिकारीहरू छैनन्?\nतब किन मल्कामले गादको जमीन लियो?\nकिन उसको मानिसहरू उसका शहरहरूमा बस्छन्?”",
			"2": "<sup>2</sup> परमप्रभु भन्नुहुन्छ, “अम्मोनको रब्बाले,\nयुद्धको स्वर सुन्ने समय आउनेछ।\nरब्बा एउटा नष्ट पहाड हुनेछ।\nवरिपरिका सबै गाउँहरू जलाइनेछन्\nर जसले तिनीहरूलाई बलपूर्वक निकालेका थिए\nतिनीहरूलाई इस्राएलका मानिसहरूले,\nबाहिर निकाल्नेछन्।”\nयो सन्देश परमप्रभुबाट थियो।",
			"3": "<sup>3</sup> “हे हेशबोनका मानिसहरू! विलाप गर।\nकिनभने ‘ऐ’ शहर नष्ट भएको छ।\nहे रब्बामा बस्ने अम्मोनी स्त्रीहरू शोक प्रकट गर्ने लुगाहरू लगाऊ।\nविलाप गर अनि पर्खालतिर दगुर।\nमल्कम आफ्नो पूजाहारी,\nर अधिकारीहरूसित निष्कासन हुन्छ।",
			"4": "<sup>4</sup> तिमीहरूले आफ्नो शक्तिमा घमण्ड गर्यौ।\nतर तिमीहरूको शक्ति क्षय भएर गइरहेको छ।\nहे अविश्वासी छोरी! तिमीले आफ्नो सम्पत्तिमा भरोसा गर्यौ,\nअनि ठान्यौ कि कसैले पनि तिमीमाथि आक्रमण गर्नेछैन।”",
			"5": "<sup>5</sup> सेनाहरूका परमप्रभु मेरो मालिक भन्नुहुन्छ:\n“प्रत्येक पक्षबाट म तिमीहरूका निम्ति भय ल्याउँनेछु।\nतिमीहरू सबैलाई खेदिनेछ,\nअनि शरणार्थीहरूलाई भेला पार्ने कोही हुने छैन्।”",
			"6": "<sup>6</sup> “त्यसपछि अम्मोनीहरूबाट जे खोसिएको थियो ती सबै कैदीहरूलाई फर्काएर ल्याउनेछु।” यो सन्देश परमप्रभुबाट आयो।",
			"7": "<sup>7</sup> एदोमको बारेमा एउटा सन्देश\nयो सन्देश एदोनसित सम्बन्धित छ। सेनाहरूका परमप्रभु भन्नुहुन्छ:\n“के तेमानमा केही ज्ञान बाँचेको छैन?\nके मानिसहरूको विवेकबाट परार्मश नष्ट भएर गयो?\nके तिनीहरूले आफ्नो बिवेक हराए?",
			"8": "<sup>8</sup> हे ददानका मानिसहरू हो, भाग, लुक!\nकिनभने म एसावलाई उसको कर्मको लागि दण्ड दिने छु।",
			"9": "<sup>9</sup> “जब मानिसहरूले अङ्गुर बटुल्छन्,\nतिनीहरूले केही छाडछन्।\nजब राती चोरहरू आउँछन्\nतिनीहरूलाई जति चाहिन्छ त्यति मात्र लैजान्छन्।",
			"10": "<sup>10</sup> तर एसावबाट म प्रत्येक कुराहरू लैजानेछु।\nम यसका गुप्त ठाउँहरूको पनि पत्तो लाउने छु\nअनि उ मबाट लुक्न पाउने छैन।\nउसका नानीहरू, आफन्त र छिमेकीहरू सारा सड्नेछन्,\nअनि एसाव अस्तिवहिन हुनेछ।",
			"11": "<sup>11</sup> आफ्ना अनाथहरूलाई छाडिदेऊ,\nम तिनीहरूलाई सुरक्षा दिनेछु।\nतिमीहरूका विधवाहरूले ममाथि भरोसा राखुन्।”",
			"12": "<sup>12</sup> परमप्रभु भन्नुहुन्छ, “कतिपय मानिसहरू दण्डका पात्र छैनन्, तर तिनीहरू पीडित भए। तर एदोम तिमी दण्डको पात्र हौ। यसर्थ तिमी निश्चय दण्डित हुनेछौ। तिमी यसबाट मुक्त हुने छैनौ।”",
			"13": "<sup>13</sup> परमप्रभु भन्नुहुन्छ, “म आफैं यो प्रतिज्ञा गर्छु कि बोज्रा ध्वंस हुनेछ अनि एक खिसीको पात्र हुनेछ। यो बेकामे र अभिशाप हुनेछ। ब्रोजा वरिपरिका जम्मै शहरहरू सदा-सर्वदाको निम्ति नष्ट हुनेछन्।”",
			"14": "<sup>14</sup> मैले परमप्रभुबाट यो सन्देश सुनेको छु:\nउहाँले ती जातिहरूलाई भन्न समाचारवाहक पठाउनु हुनेछ,\nउक्त संदेश यो होः\n“आफ्ना सिपाहीहरूलाई भेला गर!\nर युद्धकोलागि तयार होऊ!\nअनि एदोम विरूद्ध अघि बढ!",
			"15": "<sup>15</sup> एदोम, म तिमीलाई जाति-जातिहरू मध्ये एकदमै निम्न पार्नेछु।\nप्रत्येक मानिसले तिमीलाई घृणा गर्नेछन्।",
			"16": "<sup>16</sup> एदोम! तिमीहरूले अन्य जाति-जातिहरूलाई भयभित पार्यौ,\nअनि स्वयंलाई महान ठानेका छौं।\nतर तिमीहरू आफैंलाई मूर्ख बनायौ।\nतिम्रा घमण्डले तिमीलाई धोका दियो।\nतिमी फुटेको चट्टानमा बस्छौ, उच्च पहाडहरूमा बस्छौ।\nतर यदि तिमीले आफ्ना घर चीलको गुँड भन्दा माथि,\nबनायौ भने पनि म तिमीलाई तल झार्नेछु।”\nयो परमप्रभुले भन्नुभएको हो।",
			"17": "<sup>17</sup> “एदोम शोकको दृश्य बनिने छ\nअनि मानिसहरू जसले उजाड शहर देख्छन् आश्चर्यचकित हुन्छन्,\nअनि यसका घाऊहरूमा खिसी गर्नेछन्।",
			"18": "<sup>18</sup> एदोम शहर, सदोम र गमोरा अनि वरिपरिका शहरहरू जस्तै परास्त हुनेछन्\nअनि त्यहाँ कोही पनि बस्ने छैन्।”\nपरमप्रभुले यसो भन्नुभयो।",
			"19": "<sup>19</sup> “यर्दन नदी नजिक बाक्लो जङ्गलबाट कहिले काहीं निस्कने अनि जहाँ मानिसहरू आफ्नो खेतमा भेंडा़ र गाई वस्तुहरू राख्छन् म त्यहाँ जाने सिंह जस्तै हुँ। म एदोममा जान्छु। म मानिसहरूलाई खेदो ल्याउनेछु। जसलाई म मन पराउँछु उसलाई यहाँ नियुक्त गर्छु। म जस्तो को छ? कसले मलाई चुनौती दिन सक्छ? कुनै गोठाला मेरो अघि उभिनै सक्तैन।”",
			"20": "<sup>20</sup> एदोमका मानिसहरूका निम्ति\nपरमप्रभुले बनाउनु भएको योजना सुन।\nसुन, तेमानका मानिसहरूका निम्ति\nपरमप्रभुले के निर्णय लिनु भएको छ।\nतिनीहरूले थुमाहरू घिसार्नेछन्।\nउसले सम्पूर्ण तिमीहरूको खेत नष्ट पार्नेछ।",
			"21": "<sup>21</sup> एदोमको पतनको आवाजले पृथ्वी थर्किनेछ।\nतिनीहरूका विलाप लाल समुद्र सम्मै सुनिनेछ।",
			"22": "<sup>22</sup> अक्कासिने चील उँभो बेगले तल झरी\nआफ्नो पखेंटा बोज्रामाथि फिँजाउनेछ।\nत्यस समयमा, एदोमका सिपाहीहरूको हृदय\nप्रसव वेदनामा परेकी स्त्रीको जस्तो हुनेछ।",
			"23": "<sup>23</sup> दमिशकको बारेमा सन्देश\nयो सन्देश दमिशक शहरसित सम्बन्धित छ:\n“हमात र अर्पादका शहरहरू भयभीत छन्\nकिनभने तिनीहरूले कुलक्षिणको खबर सुनेका छन्।\nतिनीहरू डरले पग्लिए।\nतिनीहरू शान्त हुन नसक्ने समुद्र झैं कष्टमा छन्।",
			"24": "<sup>24</sup> दमिशक शहर कमजोर भयो।\nमानिसहरू भाग्न चाहन्छन्।\nआंतकले तिनीहरूलाई समातेको छ।\nतिनीहरूले शोक र प्रसव वेदनामा परेकी स्त्रीले झैं\nवेदना अनुभव गरिरहेका छन्।",
			"25": "<sup>25</sup> “मानिसहरूले प्रसिद्ध शहर,\n‘आनन्दको शहरलाई’ किन छोडेका छैनन्।",
			"26": "<sup>26</sup> यसकारण जवानहरू शहरको सार्वजनिक स्थलहरूमा।\nउसका सारा सिपाहीहरू त्यही समयमा मारिनेछन्।”\nसेनाहरूका परमप्रभुले यी कुराहरू भन्नुहुन्छ।",
			"27": "<sup>27</sup> “म दमिशकको पर्खालहरूमा आगो लगाईदिनेछु।\nत्यो आगोले बेनहददका किल्लाहरू पूर्ण रूपले जलाइदिनेछ।”",
			"28": "<sup>28</sup> केदार र हासोरका बारेमा सन्देश\nयो सन्देश केदार र हासोरको परिवार समूहको विषयमा छ। बाबेलका राजा नबूकदनेस्सरले तिमीहरूलाई पराजित पारे, परमप्रभु भन्नुहुन्छ:\n“जाऊ र केदारलाई आक्रमण गर।\nपूर्व दिशाका मानिसहरूलाई नष्ट पार।",
			"29": "<sup>29</sup> तिनीहरूको पालहरू, भेंडा़का बगालहरू,\nपालका पर्दाहरू र तिनीहरूका चीजहरू लगिनेछ।\nशत्रुले तिनीहरूका ऊँटहरू लगेर जानेछन्।\nमानिसहरू तिनीहरू प्रति चिच्याउने छन्।\nडरलाग्दा कुराहरू चारैतिर भई रहेको छ।",
			"30": "<sup>30</sup> भाग! छिटो भाग!\nहे हासोरका मानिसहरू, लुक्ने ठाउँहरू खोज\nकिनभने बाबेलको राजाले तिमीहरूलाई परास्त पार्नु\nएउटा युक्ति रचेकाछ।”\nयो सन्देश परमप्रभुबाट हो।",
			"31": "<sup>31</sup> “एउटा देश छ जसले आफू सुरक्षित छु भन्ने सम्झन्छ।\nतिमीहरूको न कुनै फाटक छ नत कुनै पर्खालनै छ।\nतिनीहरू एक्लो बस्छन्।\nयसकारण उठ अनि तिनीहरूमाथि जाई लाग।”",
			"32": "<sup>32</sup> “शत्रुहरूले तिनीहरूका ऊँटहरू अनि गाई वस्तुहरू लैजानेछन्।\nआफ्नो केश कुना मिलाएर चिटिक्क पारी काट्ने मानिसहरूलाई\nम हावामा तितर-बितर पारिदिनेछु।\nसबै तिरबाट म तिनीहरूमाथि सङ्कटहरू ल्याइदिनेछु।”\nयो परमप्रभुको सन्देश हो।",
			"33": "<sup>33</sup> “हासोरको देश स्यालहरूको बास-स्थान बनिनेछ।\nत्यहाँ एकजना मानिस पनि बस्ने छैन साथै त्यहाँबाट कोही हिँडडुल गर्ने छैन।\nसधैँका निम्ति यो एक शून्य उजाड मरुभूमि जस्तै हुनेछ।”",
			"34": "<sup>34</sup> एलामको बारेमा सन्देश\nसिदकियाह यहूदाका राजा भएको प्रारम्भिक कालमा अगमवक्ता यर्मियाले परमप्रभुबाट एउटा सन्देश प्राप्त गरे। यो सन्देश एलामको विषयमा थियो।",
			"35": "<sup>35</sup> सेनाहरूका परमप्रभु भन्नुहुन्छ,\n“हेर, म एलामको धनु भाँचिदिनेछु।\nधनु एलामको सबभन्दा श्रेष्ठ हतियार हो।",
			"36": "<sup>36</sup> म आकाशको चारै कुनाबाट,\nएलामको विरूद्ध हावा ल्याउनेछु।\nम एलामको मानिसहरूलाई पृथ्वीको सबै ठाउँहरूमा\nजहाँ चारैदिशाबाट हावा बहन्छ,\nत्यहाँ छरपष्ट पारिदिन्छु,\nकैदीहरूलाई हरेक जातिमा खेदी पठाउनेछु।",
			"37": "<sup>37</sup> म एलामलाई तिनीहरूका शत्रुहरू\nजसले तिनीहरूलाई मार्नु खोजिरहेकाछन्\nतिनीहरूकै अघि चक्नाचूर पारिदिनेछु।\nम एलाममाथि डरलाग्दो सङ्कटहरू ल्याउनेछु।\nमेरो उग्र रीस म तिनीहरूलाई देखाउनेछु।”\nयो परमप्रभुबाट आएको सन्देश हो।\n“म तिनीहरूलाई खेद्न तरवार पठाउँनेछु\nअनि त्यो तरवारले तिनीहरू सबैलाई\nशेष नपारन्जेल सम्म खेद्नेछ।",
			"38": "<sup>38</sup> म मेरो सिंहासन एलाममा स्थापित गर्नेछु\nर त्यहाँका राजा अनि भारद्वारहरूलाई ध्वंस पार्नेछु।”\nयो परमप्रभुको सन्देश हो।",
			"39": "<sup>39</sup> “तर पछिल्ला दिनमा एलामबाट जे लुटिएको छ त्यो पुनर्वहाल गर्नेछु।”\nयो परमप्रभुले भन्नुभयो।"
		},
		"50": {
			"1": "<sup>1</sup> बाबेलको बारेमा सन्देश\nयो सन्देशमा परमप्रभुले अगमवक्ता यर्मियाको मुखबाट बाबेल र यसको देश विषयमा भन्नुभयो।",
			"2": "<sup>2</sup> “प्रत्येक जाति-जातिलाई यो घोषण गर,\nझण्डा उठाऊ र सन्देश प्रचार गर।\nसम्पूर्ण सन्देश सुनाऊ र भन,\n‘बाबेलको राज्य कब्जा गरिनेछ।\nबाल देवता लाजको पात्र हुनेछ।\nमरोदक देवता भयभीत हुनेछ।\nबाबेलका मूर्तीहरू लज्जित हुनेछन्।\nउसका देवताका मूर्तिहरू त्रासित हुनेछन्।’",
			"3": "<sup>3</sup> उत्तर दिशाबाट आएर एउटा जातिले बाबेलमाथि आक्रमण गर्नेछ।\nत्यो जातिले बाबेललाई, जन-शून्य मरुभूमि तुल्य पार्ने छ,\nकुनै मानिस पनि त्यहाँ बस्ने छैन्।\nदुवै मानिस जाति र पशुहरू त्यहाँबाट भागेर जानेछन्।”",
			"4": "<sup>4</sup> परमप्रभु भन्नुहुन्छ, “त्यसबेला इस्राएलका मानिसहरू\nअनि यहूदाका मानिसहरू एकैजुट हुनेछन्।\nतिनीहरू सँग-सँगै रूने छन्।\nअनि सँगै, तिनीहरू आफ्ना परमेश्वरलाई खोज्न जानेछन्।",
			"5": "<sup>5</sup> ती मानिसहरूले सियोनमा कसरी जाने, सोध्ने छन्।\nतिनीहरू त्यही दिशातर्फ जान थाल्नेछन्।\nती मानिसहरूले भन्ने छन्, ‘आऊ, हामी परमेश्वरकहाँ सम्मिलित हौं।\nएक त्यस्तो करार गरौं जुन सदा-सर्वदा रहनेछ।\nएक त्यस्तो करार गरौं जुन कहिल्यै भूलिने छैन्।’",
			"6": "<sup>6</sup> “मेरो मानिसहरू हराएका भेंडा जस्तै भएका छन्।\nतिनीहरूका गोठालाहरूले तिनीहरूलाई कुमार्गमा डोर्याए।\nतिनीहरूका अगुवाहरूले तिनीहरूलाई,\nपर्वत र पहाडहरूमा लक्ष्यहीन घुमी हिडने बनाए।\nयसर्थ तिनीहरूले आफ्ना मूल वासस्थान पनि बिर्सिए।",
			"7": "<sup>7</sup> ज-जसले मेरो मानिसहरूलाई भेटे तिनीहरूलाई चोट पुर्याए।\nतिनीहरूका शत्रुहरूले भने,\n‘हामीले गल्ती काम गरेका छैनौ\nकिनभने ती मानिसहरूले परमेश्वरको विरूद्ध पाप गरे\nजो तिनीहरूका साँचो आश्रय (घर) थियो।\nतिनीहरूका पिताहरूले विश्वास गरेका परमप्रभु परमेश्वर हुनुहुन्थ्यो।’",
			"8": "<sup>8</sup> “बाबेलबाट भाग।\nकल्दीहरूका भूमि छाडिदेऊ।\nबगालको अघि साँढ भेंडा़ जस्तै होऊ।",
			"9": "<sup>9</sup> म उपद्रव मच्चाउनेछु\nर उत्तरबाट धेरै जातिहरू ल्याउनेछु।\nयी जातिहरूको समूह बाबेलको विरूद्ध युद्ध लड्न अघि आउनेछन्।\nतिनीहरूले बाबेललाई आफ्नो अधीनमा पार्नेछन्।\nतिनीहरूको काँड सिपालु लडाकुहरू झैं छन्\nर एउटै काँड पनि व्यर्थ जाँदैन्।",
			"10": "<sup>10</sup> ती शत्रुहरूले कल्दी मानिसहरूको सारा सम्पत्तिहरू लैजाने छन्।\nलूटेको सम्पत्तिले तिनीहरू पूर्ण हुनेछन्।”\nपरमप्रभुले ती कुराहरू भन्नुभयो।",
			"11": "<sup>11</sup> “बाबेल, तिमी उत्तेजित र खुशी छौ।\nकिनभने तिमीले मेरो पैतृक सम्पत्ति लूट्यौ।\nतिमी हरियो घाँसमाथि कोरली गाई झैं नाचिरहेका छौ।\nतिमीहरू घोडाहरू झैं हिन्न लागिरहेका छौं।",
			"12": "<sup>12</sup> तर तिमीहरूकी आमा जसले तिमीलाई जन्म दिईन\nतिनी धेरै लज्जित हुनेछन्।\nबाबेल सबै जाति-जातिहरू माझ नगन्य हुनेछ।\nऊ उदास र सूख्खा मरुभूमि हुनेछ।",
			"13": "<sup>13</sup> परमप्रभुले आफ्नो क्रोध देखाउनु हुनेछ।\nबाबेल पूर्ण रूपले उजाड हुनेछ\nअनि त्यहाँ कोही पनि बस्ने छैन।\nप्रत्येक यात्री जो बाबेल भएर जान्छ त्यो अचम्मित हुनेछ\nर त्यसको घाउहरू देखेर खिसी गर्नेछ।",
			"14": "<sup>14</sup> “बाबेलसित युद्धको लागि तैयार होऊ।\nबाबेलमाथि काँडहरू हान,\nतिमीहरूको एउटा काँड पनि नजोगाऊ,\nकिनभने यसले परमप्रभु विरूद्ध पाप गरेको छ।",
			"15": "<sup>15</sup> सिपाहीहरूले बाबेललाई घेरे\nअनि विजय ध्वनि सुनाऊ।\nबाबेलले आत्मासमर्पण गरेकोछ।\nउसका पर्खालहरू\nर धरहराहरू धराशायी गराइयो।\nयो परमप्रभुबाट प्रतिशोध हो।\nऊमाथि वैरशुद्धि गर।\nअन्य जातिहरूलाई उसले के गर्यो\nतिमीहरू उसलाई त्यसै गर।",
			"16": "<sup>16</sup> तिनीहरूलाई अन्न रोप्न नदेऊ।\nतिनीहरूलाई बाली काट्न नदेऊ।\nअहिले शत्रुका सिपाहीहरू आएका छन्।\nप्रत्येक मानिस र तिनीहरू\nआफ्नो-आफ्नो गाऊँमा जाउन,\nआफ्नो-आफ्नो देशमा जाउन।",
			"17": "<sup>17</sup> “इस्राएल भेंडा़को बथान जस्तो छ,\nजो देशभरि नै छरिएका थिए।\nइस्राएल भेंडा़को बथान जस्तो छ\nजसलाई सिंहले खेद्‌दै सबै ठाँउमा छरपष्ट पारेकोछ।\nजसले उसलाई पहिला खानेसिंह अश्शूरको राजा हो।\nअन्तिम सिंह जसले उसका हड्डीहरू तोड्यो\nत्यो बाबेलका राजा नबूकदनेस्सर हो।",
			"18": "<sup>18</sup> यसकारण सेनाहरूका परमप्रभु, इस्राएलका परमेश्वर भन्नुहुन्छ:\n‘म बाबेलका राजा र उसको देशलाई दण्ड दिनेछु,\nत्यही तरीकाले जसरी मैले अश्शूरका राजालाई दण्ड दिएको थिएँ।",
			"19": "<sup>19</sup> “‘म इस्राएललाई फेरि फर्काएर आफ्नै ठाउँमा ल्याउने छु।\nतिनीहरूले कर्मेल र बाशानको चरण भूमिमा खुवाउनेछन्।\nतिनीहरू खाएर तृप्त हुनेछन्।\nतिनीहरूले एप्रैम र गिलादका डाँडाहरूमा खाएर तृप्त हुनेछन्।’”",
			"20": "<sup>20</sup> परमप्रभु भन्नुहुन्छ, “त्यस बेला, तिनीहरूले इस्राएलको अधर्म,\nखोज्ने प्रयास गर्नेछन्।\nतर तिनीहरूले कुनै अधर्म पाउने छैनन।\nतिनीहरूले यहूदाको पापहरू खोज्ने प्रयास गर्नेछन्\nतर तिनीहरूले कुनै पापहरू पाउने छैनन\nकिनभने जसलाई मैले बचाएँ,\nतिनीहरूको पाप क्षमा गरेकोछु।”",
			"21": "<sup>21</sup> परमप्रभु भन्नुहुन्छ, “मरातैमको देशलाई आक्रमण गर।\nपकोदमा बस्ने मानिसहरूमाथि आक्रमण गर।\nतिनीहरूमाथि आक्रमण गर\nतिनीहरूलाई मार अनि सर्वनाश पार।\nमैले जुन कुरो दिएँ ती सब गर।",
			"22": "<sup>22</sup> “सारा देशभरि नै युद्धको कोलाहल सुनिन सक्छ।\nयो विनाशको कोलाहल हो।",
			"23": "<sup>23</sup> त्यो देश जसले सारा पृथ्वीलाई नै\nमार्तोलले हिर्कायो,\nत्यो कटिएको छ र टुक्रिएको छ।\nबाबेल जाति-जातिहरू माझ प्राणीहीन भएकोछ।",
			"24": "<sup>24</sup> हे बाबेल, मैले तिमीलाई पासो थापें\nअनि तिमीले थाहा पाउन अघि नै पक्राउ पर्यौ।\nतिमी परमेश्वरको विरूद्ध लड्‌यौ,\nयसकारण तिमी बन्दी भयौ।",
			"25": "<sup>25</sup> परमप्रभुले आफ्नो अस्त्र-घर उघार्नु भयो\nअनि क्रोधको हात-हतियार निकाल्नु भयो।\nमेरो मालिक, सेनाहरूका परमप्रभुले ती हतियारहरू निकाल्नु भयो,\nकिनभने उहाँले कल्दीहरूका देशमा आफ्नो काम गर्नु थियो।",
			"26": "<sup>26</sup> “सुदूरदेशबाट पनि बाबेलको विरूद्ध आऊ।\nअन्न-भण्डारहरू तोडी देऊ,\nअन्नको थुप्रो झैं बाबेललाई थुपार\nअनि बाबेललाई पूर्ण नष्ट पार,\nर कसैलाई पनि बाँच्न नदेऊ।",
			"27": "<sup>27</sup> सारा साँढेहरू मार।\nतिनीहरूलाई कसाइकहाँ जानदेऊ।\nतिनीहरूलाई धिक्कार!\nतिनीहरूका निम्ति दण्ड पाउने समय आएकोछ।",
			"28": "<sup>28</sup> मानिसहरू बाबेलबाट भागिरहेका छन्।\nमानिसहरू जो बाबेलबाट बाँचेर आएकाछन्\nतिनीहरू भन्दैछन्,\n‘परमप्रभु हाम्रा परमेश्वरले उहाँको\nर उहाँको मन्दिरको लागि प्रतिशोध लिनु भएकोछ।’",
			"29": "<sup>29</sup> “काँडहरू हान्ने मानिसहरूलाई बोलाऊ,\nतिनीहरूलाई बाबेलमाथि काँड हान्नु भन।\nशहर घेराबन्दी गर्नु भन।\nकसैलाई पनि नछोड।\nतिनीहरूका कुकर्मका फल देऊ।\nअन्य जातिहरूलाई जसो गरेकी थिई\nत्यसलाई त्यसै गर,\nकिनभने इस्राएलका एक मात्र पवित्र\nपरमप्रभु विरूद्ध बाबेलले विद्रोह गरेको हो।",
			"30": "<sup>30</sup> त्यस समय बाबेलका जवानहरूलाई गल्लीहरूमा मारिनेछ\nर सेनाहरू मर्नेछन्।”\nपरमप्रभुले यो कुरा भन्नुभयो।",
			"31": "<sup>31</sup> “बाबेल तिमी अति अंहकारी छौ\nअनि म तिम्रो विरूद्ध छु।”\nहाम्रो मालिक, सेनाहरूका परमप्रभुले भन्नुहुन्छ,\n“तिम्रो दिन आएकोछ,\nम तिमीलाई दण्ड दिनेछु।",
			"32": "<sup>32</sup> घमण्डी बाबेल ठोकिएर लड्नेछ\nअनि कसैले पनि उनलाई उठाउने छैन।\nम उसको शहरहरूमा आगो लगाउनेछु\nअनि उसको वरिपरि भएका प्रत्येक कुरो नै जल्नेछ।”",
			"33": "<sup>33</sup> सेनाहरूका परमप्रभु भन्नुहुन्छ:\n“इस्राएल र यहूदाका मानिसहरूलाई शत्रुहरूले बन्दी बनाई लगियो।\nतिनीहरूलाई कैद गर्नेहरूले तिनीहरूलाई पक्रेर राखेका छन्,\nर मुक्त पार्न अस्वीकार गरे।",
			"34": "<sup>34</sup> तर तिनीहरूका उद्धारक शक्तिशाली हुनुहुन्छ।\nउहाँको नाउँ ‘सेनाहरूका परमप्रभु’ हो।\nउहाँले तिनीहरूको मामलामा वकालत गर्नुहुनेछ,\nर पृथ्वीलाई आराम दिनुहुनेछ।\nतर बाबेलमा बस्ने मानिसहरूलाई उहाँले शान्ति दिनु हुनेछैन।”",
			"35": "<sup>35</sup> परमप्रभु भन्नुहुन्छ,\n“बाबेलका बासिन्दाहरू,\nराजाका हाकिमहरू\nर ज्ञानीहरूको विरूद्ध तरवार छ।",
			"36": "<sup>36</sup> हे तरवार! अगमवक्ताहरूलाई हिर्काऊ\nताकि तिनीहरू मूर्ख बनुन्,\nबाबेलका सिपाहीहरूलाई मार\nताकि तिनीहरू भयभीत हुन्।",
			"37": "<sup>37</sup> तरवार, बाबेलका घोडाहरू, रथहरूलाई मार।\nअन्य देशका भाडाका सिपाहीहरूलाई मार\nताकि तिनीहरू स्त्रीहरू जस्तो डराउन्।\nतरवार, बाबेलको गाडधनहरूमाथि जाइलाग\nताकि तिनीहरू लुटिउन्।",
			"38": "<sup>38</sup> तरवार! पानीको मुहानहरू काट\nताकि मुहानहरू सुकुन।\nकिनभने बाबेलमा धेरै मूर्तिहरू छन्\nअनि तिनीहरू मूर्तिका निम्ति पागल हुन्छन्।",
			"39": "<sup>39</sup> बाबेल फेरि मानिसहरूले भरिने छैन।\nजङ्गली कुकुरहरू, शुतुरमूर्गहरू, र अन्य पशु-प्राणीहरू त्यहाँ बस्नेछन्,\nतर त्यहाँ फेरि कोही मानिस रहने छैन।",
			"40": "<sup>40</sup> परमेश्वरले सदोम र गमोरालाई पूर्ण रूपले ध्वंस पार्नु भयो\nअनि त्यसका वरिपरिका शहरहरू पनि ध्वंस पारिदिनु भयो।\nअनि ती शहरहरूमा अब कोही पनि बस्ने छैन।\nत्यस्तै प्रकारले, कुनै मानिस बाबेलमा बस्ने छैन्।\nअनि कोही मानिस त्यहाँ बस्न जाने छैन।\nपरमप्रभु परमेश्वरले यो कुरा भन्नु भयो।",
			"41": "<sup>41</sup> “हेर! उत्तर दिशाबाट मानिसहरू आइरहेका छन्।\nएक शक्तिशाली जाति\nर धेरै राजाहरू पृथ्वी वरिपरिबाट आइरहेका छन्।",
			"42": "<sup>42</sup> तिनीहरूका सेनाहरूसँग धनुहरू र भालाहरू छन्।\nसिपाहीहरू निष्ठूर छन्।\nतिनीहरूमा दया छैन।\nसैनिकहरू घोडाहरूमा चढेर आउनेछन्\nअनि तिनीहरूको अवाज समुद्रको गजर्न जस्तै छ।\nतिनीहरू तिम्रो विरूद्धमा युद्ध गर्न तैयारभई पंक्तिमा बसेका छन।\nहे बाबेलकी छोरी।",
			"43": "<sup>43</sup> बाबेलका राजा ती सेनाहरूका गडगडाहट आवाज सुनेर डराए।\nउनका हातहरू चल्न छाडे।\nउसलाई डरले घेर्यो,\nअनि उसलाई प्रसव वेदनामा परेकी स्त्रीलाई झैं कष्ट भयो।”",
			"44": "<sup>44</sup> परमप्रभु भन्नुहुन्छ,\n“म एउटा त्यस्तो सिंह हुँ जो कहिले काँही,\nयर्दन नदी छेऊको घना जङ्गलबाट बाहिर निस्कन्छ।\nम हटात उसका मानिसहरूलाई खेदो गर्नेछु।\nम त्यसमाथि कसैलाई पनि नियुक्त गर्नेछु।\nम जस्तो को छ?\nमलाई कसले बोलाउन सक्छ।\nगोठालाको हो जो मेरो अघि उभिन सक्छ?”",
			"45": "<sup>45</sup> बाबेलको विरूद्ध परमप्रभुले रच्नुभएको योजना सुन।\n“बगालको स-साना थुमाहरू घिस्याएर लगिनेछ,\nर परमप्रभुले तिनीहरूको चरणभूमि\nपूर्णरूपले नष्ट पार्नु हुनेछ।",
			"46": "<sup>46</sup> बाबेलको पतन हुनेछ,\nअनि त्यो पतनको आवाजले पृथ्वी थर्काउनेछ।\nहरेक जातिहरूका मानिसहरूले\nबाबेलको विनाशको बारेमा सुन्ने छन्।”"
		},
		"51": {
			"1": "<sup>1</sup> परमप्रभु भन्नुहुन्छ,\n“म एक शक्तिशाली हावा बाबेल\nर ‘लेब कामीका’  बासिन्दाहरू माथि बहाउनेछु।",
			"2": "<sup>2</sup> म बाबेलमा विदेशीहरू पठाउनेछु\nअनि तिनीहरूले नबेललाई हावामा तितर-बितर पार्नेछ\nर उसको जमीन नष्ट पार्नेछ।\nतिनीहरू सर्वनाशको दिन प्रत्येक दिशाबाट आउनेछन्।",
			"3": "<sup>3</sup> बाबेलका सिपाहीहरूले धनु काँडहरू चलाउने छैनन्\nती सिपाहीहरूले कवच सम्म लगाउने छैन।\nबाबेलका सैनिकहरूमाथि दया नराख।\nउसको सेनाहरूलाई पूर्ण रूपमा ध्वंस पार।",
			"4": "<sup>4</sup> बाबेलका सिपाहीहरू कल्दीका भूमिमा मारिनेछन्\nतिनीहरू बाबेलका गल्लीहरूमा नराम्रो घतले घाइते हुनेछन्।”",
			"5": "<sup>5</sup> इस्राएलका एकमात्र पवित्रको अघि\nतिनीहरूको भूमि\nदोषले भरिएको भए पनि\nसेनाहरूका परमप्रभुले इस्राएल\nर यहूदालाई एक्लै छोड्नु हुन्न।\nतर तिनीहरूले उहाँलाई छोडिदिएका छन्।",
			"6": "<sup>6</sup> बाबेलदेखि भाग प्राण बचाऊ।\nबाबेलको पापको कारणले ध्वंस नहोऊ।\nयो समय परमप्रभुले बद्ला लिने समय हो।\nउसले गरेको कर्म अनुसार उहाँले बद्ला लिनुहुनेछ।",
			"7": "<sup>7</sup> बाबेल परमेश्वरको हातमा एक सुनको कचौरा थियो।\nबाबेलले सारा पृथ्वीलाई मतायो।\nसारा जाति-जातिहरूले बाबेलको मद्धपान गरे।\nयसकारण तिनीहरू पागल भए।",
			"8": "<sup>8</sup> तर बाबेल एक्कासि पतन हुनेछ अनि टुक्रिनेछ।\nउसको लागि विलाप गर,\nअनि उसको घाउमा मलहम लगाऊ।\nहुन सक्छ ऊ निको हुन्छे।",
			"9": "<sup>9</sup> हामीले बाबेललाई निको पार्ने कोशिश गर्यौ,\nतर त्यो निको हुन सकेन,\nयसकारण हामीले त्यसलाई छोडौं\nअनि हामी आफ्नै देशमा जाऊँ।\nकिनभने उहाँको न्याय स्वर्ग पुग्नेछ,\nअनि बादलसम्म पुग्नेछ।",
			"10": "<sup>10</sup> परमप्रभुले हामीलाई विजय हासिल गराउनु भयो।\nआऊ, हामी सियोन जाऊँ,\nर परमप्रभु हाम्रा परमेश्वरको कार्यहरूका विषयमा मानिसहरूलाई भनौं।",
			"11": "<sup>11</sup> काँडहरू तीखो पार ढालहरू लगाऊ।\nपरमप्रभुले मादीहरूका राजाहरूलाई उस्काउनु भएको छ।\nकिनभने उहाँले बाबेललाई ध्वंस गर्न चाहनु हुन्छ।\nबाबेलका सेनाले यरूशलेममा परमप्रभुको मन्दिर भत्काएका थिए।\nयसकारण परमप्रभुले तिनीहरूलाई दण्ड दिनुभयो जुन तिनीहरूले पाउनु नै पर्ने थियो।",
			"12": "<sup>12</sup> बाबेलको पर्खालहरूका विरूद्ध\nझण्डा माथि उठाऊ।\nअझ प्रहरीहरू ल्याऊ।\nतिनीहरूका ठीक-ठीक ठाउँमा चौकीदार राख।\nधरापहरू थाप किनभने परमप्रभुले त्यही गर्नुहुन्छ\nजुन उहाँले योजना बनाउनु भएकोछ।",
			"13": "<sup>13</sup> बाबेल, तिमी धेरै पानी भएको छेवमा छौ तिमी सम्पत्तिले सम्पन्न छौ।\nतर तिम्रो अन्त आएको छ।\nयो तिम्रो ध्वंस हुने बेला हो।",
			"14": "<sup>14</sup> सेनाहरूका परमप्रभुले यो प्रतिज्ञा आफ्नो नाउँ लिएर गर्नुभयो:\n“बाबेल म तिमीलाई धेरै शत्रुहरूले परिपूर्ण पारिदिनेछु।\nतिनीहरू सलहहरू जतिकै हुनेछन्।\nतिनीहरूले तिम्रो विरूद्ध युद्ध जित्नेछन्,\nर तिनीहरू तिमीमाथि उभिएर विजय गान गाउने छन्।”",
			"15": "<sup>15</sup> परमप्रभुले आफ्नो महान शक्ति प्रयोग गर्नुभयो अनि पृथ्वी सृष्टि गर्नु भयो।\nउहाँले आफ्नो ज्ञानले पृथ्वीलाई स्थापित गर्नु भयो\nर उहाँले आफ्नो समझ र बुद्धिले आकाशलाई फैलाउनु भयो।",
			"16": "<sup>16</sup> जब उहाँ गर्जनुहुन्छ आकाश पनि गर्जन्छ,\nउहाँले पृथ्वीको अन्तबाट कुइरो निस्केर माथि आउने पार्नुहुन्छ।\nउहाँले बर्षासँग बिजुली चम्काउनुहुन्छ।\nउहाँले आफ्नो भण्डारबाट बतास निकाल्नुहुन्छ।",
			"17": "<sup>17</sup> तर हरेक मानिसहरू मूर्ख र बुद्धिहीन छन्।\nपरमेश्वरले के गर्नुभयो सो जान्दैनन्,\nहरेक मानिस जसले मूर्ति बनाउँछ,\nउसलाई लज्जित पारिन्छ।\nकिनभने उसको मूर्तिहरू मूल्यहीन हुन्।\nतिनीहरूमा जीवन छैन।",
			"18": "<sup>18</sup> तिनीहरू मूल्याहीन छन्,\nछलको काम मात्र गर्छन्।\nजब तिनीहरूलाई सजाय दिइन्छ\nतिनीहरू सड्नेछन्।",
			"19": "<sup>19</sup> याकूबका परमेश्वर यी मूर्तिहरू जस्ता मूल्यहीन हुनुहुन्न।\nमानिसहरूले परमेश्वर बनाएनन्।\nपरमेश्वरले आफ्ना मानिसहरू बनाउनुभयो।\nउहाँले आफ्नो उत्तराधिकारी जाति सहित प्रत्येक चीज बनाउनु भयो।\nउहाँको नाउँ सेनाहरूका परमप्रभु हो।",
			"20": "<sup>20</sup> परमप्रभु भन्नुहुन्छ, “बाबेल! तिमी मेरो डण्डा हौं,\nमेरो युद्धको हतियार।\nमैले त्यस जाति जातिहरूलाई टुक्राउनु र ध्वंश पार्नुलाई चलाएँ।",
			"21": "<sup>21</sup> मैले तिमीलाई घोडा र सवारीमा प्रहार गर्न चलाएँ।\nमैले तिमीलाई रथ र सारथीहरू धूलो पार्न प्रहार गरें।",
			"22": "<sup>22</sup> मैले तिमीलाई पुरूष र स्त्री माथि प्रहार गर्न चलाएँ।\nमैले तिमीलाई, जवान र वृद्ध मानिसहरूमाथि प्रहार गर्न चलाएँ।\nमैले तिमीलाई युवा-युवती माथि प्रहार गर्न चलाएँ।",
			"23": "<sup>23</sup> मैले तिमीलाई गोठालो र भेंडा़वथान माथि प्रहार गर्न चलाएँ।\nमैले तिमीलाई किसानहरू र गाईहरूमाथि प्रहार गर्न चलाएँ।\nमैले तिमीलाई राज्यपालहरू र मुख्य हाकिमहरूमाथि प्रहार गर्न चलाएँ।",
			"24": "<sup>24</sup> तर म बाबेल र सबै कल्दीहरूलाई\nतिनीहरूले सियोन विरूद्ध गरेका जम्मै अनिष्ट कार्यहरू फर्काइदिनेछु।”\nपरमप्रभु ती कुराहरू भन्नुहुन्छ।",
			"25": "<sup>25</sup> परमप्रभु भन्नुहुन्छ,\n“बाबेल! हे पर्वत नष्ट छ जसले सारा भूमि नै नष्ट पार्छ म तिम्रो विरोधमा छु।\nम तिम्रो विरोधमा मेरो हात उठाउँछु।\nम तिमीलाई भिरालो चट्टानबाट तल झारी दिनेछु,\nम तिमीलाई डढेको पर्वत बनाईदिनेछु।",
			"26": "<sup>26</sup> आफ्नो मन्दिरको लागि कुने ढुङ्गा,\nअथवा घडेरी बनाउनको लागि मानिसहरूले तिमीहरूबाट ढुङ्गाहरू लिनेछैनन्।\nकिनभने तिमी सधैँका निम्ति काममा नआउने भूमि हुनेछौ।”\nपरमप्रभुले घोषणा गर्नु भयो।",
			"27": "<sup>27</sup> “भूमिमा युद्धको झण्डा उठाऊ।\nसबै जाति-जातिहरूमा युद्धको तुरही फुक।\nजातिहरूलाई बाबेलको विरूद्ध लडाइँ गर्नलाई तैयार पार।\nअरारत, मिन्नी र अशकनज राज्यहरूलाई\nबाबेलको विरूद्ध लडाइँ गर्न डाक।\nउसको विरोधमा सेना संचालन गर्न एकजना कप्तान नियुक्त गर।\nसंख्यामा सलहसरह घोडाहरू त्यहाँ पठाऊ।",
			"28": "<sup>28</sup> उसको विरूद्ध लडाइँ गर्न जाति-जातिहरू तयार पार।\nमादीका राजाहरूलाई, राज्यपालहरू, सारा मुख्य हाकिमहरू\nर तिनीहरू जसले बाबेल विरूद्ध शासन गरे\nती जम्मै देशहरूलाई तैयार पार।",
			"29": "<sup>29</sup> जब बाबेललाई जन शून्य,\nउजाड भूमि बनाउन परमप्रभुको काम\nधर्ती थर-थराउनेछ\nअनि बेदनामा परे झैं माडिनेछ।",
			"30": "<sup>30</sup> बाबेलका सिपाहीहरूले लडाइँ गर्न बन्द गरे।\nतिनीहरू आफ्नै दृढ किल्लाहरूमा पसे।\nतिनीहरू थकित भए।\nतिनीहरू स्त्री जस्तै भए।\nबाबेलका घरहरू जलिरहेका छन्।\nअनि प्रवेशद्वारहरू भत्काइएका छन्।",
			"31": "<sup>31</sup> एक सन्देशवाहकको पछि,\nअर्को सन्देशवाहक जान्छ,\nअनि सम्पूर्ण शहर कब्जामा पर्यो भन्ने खबर\nबाबेलका राजाकहाँ पुर्याइन्छ।",
			"32": "<sup>32</sup> चाँचरहरू घेरामा परे;\nसिमसारहरू आगो लगाइए;\nसिपाहीहरू भयग्रस्त भए।”",
			"33": "<sup>33</sup> सेनाहरूका परमप्रभु, इस्राएलहरूका परमेश्वर यसो भन्नुहुन्छ,\n“बाबेल धान कुट्ने ढिकी जस्तै भएकी छ।\nबालीको समय मानिसहरूले धान कुटेर असल र भुस छुट्याउँछन्।\nअनि बाबेलमा धान कुट्ने समय चाँडै आइरहेछ।”",
			"34": "<sup>34</sup> सियोनका मानिसहरूले भन्नेछन्,\n“बाबेलका राजा नबूकदनेस्सरले हामीलाई खायो।\nहामीलाई चोट पुर्यायो।\nहाम्रा मानिसहरूलाई टाढा पुर्यायो।\nउसले हामीलाई खाली भाँडाजस्तै बनायो।\nहाम्रा असल-असल थोकहरू सबै लग्यो।\nराक्षसलेझैं उसले हामीलाई निल्यो।\nहाम्रो राम्रो भोजनले उसले आफ्नो पेट भर्यो।\nतब उसले हामीलाई बाहिर थुक्यो।",
			"35": "<sup>35</sup> बाबेलले हामीमाथि डरलाग्दो र निन्दनीय कामहरू गर्यो।\nअब हामी बाबेलमाथि त्यही अनिष्टहरू भएको हेर्न चाहन्छौं।”\nसियोनका मानिसहरू यो कुरा भन्छन्,\n“हाम्रो रगत बाबेलका मानिसहरूमाथि परोस्।\nतिनीहरूले बिगतमा जे गरेका थिए तिनीहरूमाथि त्यस्तै दण्ड होस्।”\nयरूशलेमका वासिन्दाहरूले यो कुरा भन्छन्।",
			"36": "<sup>36</sup> यसकारण परमप्रभु भन्नुहुन्छ:\n“यहूदा! म तिमीहरूको मामला लिनेछु\nर प्रतिशोध गर्नेछु।\nम बाबेलको समुद्र सुकाइदिनेछु,\nअनि झरनाहरू सुकाइदिनेछु।",
			"37": "<sup>37</sup> बाबेल मन्दिरहरूका भग्नावशेषहरूको थुप्रो मात्र भएको छ।\nअनि त्यो जङ्गली कुकुरहरू बस्ने ठाउँ हुनेछ।\nमानिसहरू ढुङ्गाहरूको राश हेर्न डराउनेछन्।\nबाबेलको विषयमा सोचेर मानिसहरू टाउको हल्लाउने छन्।\nयो कोही नबस्ने सुनसान ठाऊँ बनिनेछ।",
			"38": "<sup>38</sup> “बाबेलका मानिसहरू गर्जने जवान सिंह जस्तै छन्।\nतिनीहरू डम्मरूहरूझैं गर्जिन्छन्।",
			"39": "<sup>39</sup> जब तिनीहरू उत्तेजित हुन्छन्\nम तिनीहरूलाई एक प्रीति भोज दिनेछु।\nम तिनीहरूलाई मताउनेछु।\nतिनीहरू हाँस्ने छन् खुशी मनाउनेछन्।\nतिनीहरू कहिल्यै नउठ्नलाई निदाउँनेछन्।”\nतिनीहरू कहिल्यै उठ्ने छैनन्।\nपरमप्रभुले यी कुराहरू भन्नुभयो।",
			"40": "<sup>40</sup> “बाबेल भेंडा़-भेंडी़ र बाख्राहरू झैं काटिनलाई पर्खिरहको हुनेछ।\nतिनीहरूलाई म कसाइखानमा डोर्याउँनेछु।",
			"41": "<sup>41</sup> “शीशक परास्त हुनेछ।\nसारा पृथ्वीको गौरव र प्रशंसाको पात्र बाबेल कैद गरिनेछ।\nअन्य जातिहरूका मानिसहरूले बाबेलमा हेर्नेछन्\nअनि त्यस देशको सर्वनाश देखेर तिनीहरू भयग्रस्त हुनेछन्।",
			"42": "<sup>42</sup> समुद्र उर्लेर बाबेलमाथि उठ्नेछ र ढाकिने छ।\nयसको गर्जने छालहरूले उसलाई ढाकी दिन्छ।",
			"43": "<sup>43</sup> बाबेलका शहरहरू नष्ट पारिनेछन्।\nबासिन्दाहरू बिना ती शहरहरू\nसुख्खा मरुभूमि र उदास हुनेछन,\nबाबेल देशबाट मानिसहरू यात्रा गर्नेछैनन्।",
			"44": "<sup>44</sup> म बाबेलको बाललाई दण्ड दिनेछु।\nउसले जे-जे निलेको छ त्यसलाई उल्टी गराउँछु।\nबाबेलमा जाति-जातिहरूका मानिसहरू अब उसो आउने छैनन्।\nबाबेलको पर्खाल ढल्नेछ।",
			"45": "<sup>45</sup> हे मेरो मानिसहरू हो।\nबाबेलका शहरबाट बाहिर निस्क र आफ्नो प्राण बचाउँन भाग।\nपरमप्रभुको भयानक रीसदेखि भाग।",
			"46": "<sup>46</sup> “हे मेरा मानिसहरू हो, नडराऊ।\nउड़न्त कुराहरू फैलिनेछ तर नडराऊ।\nयो बर्षमा एउटा उड़न्त कुरा आउँनेछ;\nअनि अर्को बर्षमा अर्को आउँनेछ,\nयस भूमिमा हिंसाको उड़न्त खबरहरू\nर शासकहरूको विरूद्ध शासकहरू लड्ने उडन्त कुरा हुनेछ।",
			"47": "<sup>47</sup> निसन्देह समय आउने छ\nजब म बाबेलका झूटा देवताहरूलाई सजाय दिनेछु\nअनि बाबेलका सारा देश लाजले पानी हुनेछ।\nबाबेलका गल्लीहरूमा\nधैरे लाशहरू लडिरहेका हुनेछन्।",
			"48": "<sup>48</sup> तब स्वर्ग र पृथ्वी\nअनि त्यहाँ भएका सबै बाबेलका बारेमा आनन्दले काराउने छन्\nकिनभने ध्वंसकहरू बाबेलसित लड्न\nउत्तर दिशाबाट आउनेछन्।”\nपरमप्रभुले यो कुरा भन्नुभयो।",
			"49": "<sup>49</sup> “बाबेलले इस्राएलबाट आएका मानिसहरू मारे।\nबाबेलले पृथ्वीमा भएका प्रत्येक ठाउँबाट आएका मानिसहरू मारे।\nयसकारण बाबेलको पतन हुनै पर्छ।",
			"50": "<sup>50</sup> तिमीहरूलाई तरवारहरूबाट बचाइएकोछ।\nएकदम चाँडो बाबेल छोड, नपर्ख।\nतिमीहरू घेरै टाढा ठाउँमा छौ\nपरमप्रभु र यरूशलेमलाई सम्झ।",
			"51": "<sup>51</sup> “हामी यहूदाका मानिसहरू लज्जित गराइएका छौं।\nहामी अपमानित भएकाछौं\nर तिरस्कारले हाम्रा अनुहारहरू ढाकिन्छ,\nकिनभने विदेशीहरू परमप्रभुको मन्दिरको पवित्र ठाउँहरूमा पसे।”",
			"52": "<sup>52</sup> परमप्रभु भन्नुहुन्छ, “समय आइरहेछ,\nजब म बाबेलका मूर्तिहरूलाई न्याय-दण्ड दिनेछु।\nत्यो देशका चारैतिर चोटहरूमा रूनेछन्।",
			"53": "<sup>53</sup> &nbsp;&nbsp;&nbsp;&nbsp;स्वर्गसम्म बाबेल बढ्न सक्छ।\nउसले उसका किल्लाहरू बलियो बनाइदिनेछ।\nतर उसको विरूद्ध ध्वंसक पठाउनेछु।”\nपरमप्रभुले यो कुरा भन्नुभयो।",
			"54": "<sup>54</sup> “हामी बाबेलमा मानिसहरूले विलाप गरिरहेका सुन्न सक्छौं।\nबाबेलको देशमा मानिसहरूले नष्ट पारिरहेको आवाज हामी सुन्छौ।",
			"55": "<sup>55</sup> परमप्रभुले बाबेललाई नष्ट गरिदिनु हुन्छ,\nउहाँले त्यो शहरको कोलाहललाई बन्द गरिदिनु हुनेछ,\nशत्रुहरू समुद्रका छालहरू जस्तै गर्जेर आउनेछन्।\nपृथ्वीभरि मानिसहरूले त्यो गर्जन सुन्नेछन्।",
			"56": "<sup>56</sup> सेना आउने छ अनि बाबेल नष्ट पार्नेछ।\nबाबेलका सिपाहीहरू तितर-बितर हुनेछन्\nतिनीहरूका धनुहरू भाँचिने छन्।\nकिनभने परमप्रभु परमेश्वर हुनुहुन्छ\nजसले परिशोधन माँग गर्नुहुन्छ।\nउहाँले निश्चय तिनीहरूलाई चुक्त पारिदिनु हुनेछ।",
			"57": "<sup>57</sup> म बाबेलका ज्ञानी मानिसहरू\nर मुख्य हाकिमहरूलाई मताईदिनेछु\nम राज्यपालहरू, हाकिमहरू\nर सिपाहीहरूलाई पनि दाखरसमा डुबाइ दिनेछु।\nतब तिनीहरू सबै सदा-सर्वदाका निम्ति सुत्नेछन्\nतिनीहरू कहिल्यै ब्यूँझिने छैनन्।”\nमहाराजाले यी कुराहरू भन्नुभयो।\nउहाँको नाउँ सेनाहरूका परमप्रभु हो।",
			"58": "<sup>58</sup> सेनाहरूका परमप्रभु भन्नुहुन्छ:\n“बाबेलको बाक्लो, बलियो पर्खाल झारिनेछ।\nउसको अग्लो प्रवेशद्वारहरू जलाइनेछ।\nबाबेलका मानिसहरू व्यर्थमा\nकठोर परिश्रम गर्दछन्।\nजाति-जातिहरूको परिश्रम खाली आगोको लागि हुनेछ।”",
			"59": "<sup>59</sup> यर्मियाले बाबेललाई एक सन्देश पठाउँछन्\nयो सन्देश अगमवक्ता यर्मियाले नेरियाहको छोरो मासेयाहको नाति सरायाहलाई दिए। सरायाह यहूदाका राजा सिदकियाहसँग बाबेलतर्फ गए। यस बेला सिदकियाहको शासनकालको चौथो बर्ष थियो। अहिले सरायाह राजभवनका मुख्य भारदार थिए।",
			"60": "<sup>60</sup> यर्मियाले त्यो पुस्तकमा ती सम्पूर्ण भयानक कुराहरू जुन बाबेलमा घट्नेवाला थिए सबै लेखिदिएका थिए।",
			"61": "<sup>61</sup> यर्मियाले सरायाहलाई भने, “जब तिमी बाबेलमा जान्छौ यी वचनहरू जोरले पढ्नु।",
			"62": "<sup>62</sup> तब भन, ‘हे परमप्रभु! तपाईंले यो बाबेल नष्ट गरिदिन्छु भन्नु भएको छ अनि यहाँ मानिसहरू र पशुहरू कोही पनि यसमा बसो-बासो गर्ने छैनन्।’",
			"63": "<sup>63</sup> यो पढिसके पछि, यसलाई ढुङ्गमा बाँधेर यूफ्रेटिस नदीमा फ्याँकिदेऊ।",
			"64": "<sup>64</sup> त्यसपछि भन, ‘यसको विरोधमा म जुन सर्वनाश ल्याउनेछु जसले गर्दा बाबेल त्यसरी भासिनेछ त्यो कदापि माथि उठ्न सक्नेछैन।’”\nयर्मियाका सन्देश यहाँ समाप्त हुन्छ।"
		},
		"52": {
			"1": "<sup>1</sup> यरूशलेमको पतन\nसिदकियाह यहूदाका राजा हुँदा उनी 12 बर्ष मात्रका थिए। सिदकियाहले यरूशलेममा एघार बर्ष राज्य गरे। उनकी आमाको नाउँ हमूतल हो तिनी यर्मियाकी छोरी थिइन्। हमतूलका परिवार लिब्ना शहरका थिए।",
			"2": "<sup>2</sup> यहोयाकीमले गरे जस्तै सिदकियाहले परमप्रभुको दृष्टिमा नै अनिष्ट कार्य गरे।",
			"3": "<sup>3</sup> डरलाग्दा घटनाहरू यरूशलेम र यहूदामा भयो किनभने परमप्रभु तिनीहरूसित रिसाउनु भएको थियो। अन्तमा, परमप्रभुले आफ्नो दृष्टिबाट यरूशलेम र यहूदाका मानिसहरूलाई फ्याँकिदिनु भयो।\nसिदकियाहले बाबेलको राजा विरूद्ध विद्रोह गरे।",
			"4": "<sup>4</sup> यसर्थ सिदकियाहको शासनकालको नवौं बर्षको दशौं महीनाको दशौं दिन बाबेलका राजा नबूकदनेस्सर आफ्ना सम्पूर्ण सेना लिएर यरूशलेम विरूद्ध अघि बढे। बाबेलका सेनाले यरूशलेम बाहिर शिबिर बनाएका थिए। तिनीहरूले शहरको वरिपरि घेरा पर्खालहरू बनाए।",
			"5": "<sup>5</sup> शहर घेराव सिदकियाहको राज्यकालको एघारौं बर्षसम्म रह्यो।",
			"6": "<sup>6</sup> चौथो महीनाको नवौं दिनमा शहरमा महा अनिकाल पर्यो। त्यस देशमा मानिसहरूका निम्ति भोजन थिएन।",
			"7": "<sup>7</sup> शहरको पर्खालमा प्वाल बनाएको थियो। यरूशलेमका सेना भागे। तिनीहरूले शहर राता-रात त्यागे। राजाको बगैंचाको दुइ पर्खालहरूको माझको प्रवेशद्वारबाट तिनीहरू गए। अहिले बाबेलीहरूले शहरलाई घेरेका थिए। तिनीहरू आराबातिर भागे।",
			"8": "<sup>8</sup> तर राजा सिदकियाहलाई बाबेलका सेनाले खेदे। तिनीहरूले यरीहोको मैदानमा तिनलाई समाते। सिदकियाहका सबै सिपाहीहरू भागे।",
			"9": "<sup>9</sup> बाबेलका सेनाले राजा सिदिकयाहलाई पक्रे अनि तिनीहरूले उसलाई हमातको देश रिब्लामा बाबेलका राजाकहाँ ल्याए। रिब्लामा बाबेलका राजाले राजा सिदकियाहमाथि आफ्नो निर्णय सुनाए।",
			"10": "<sup>10</sup> रिब्लामा बाबेलका राजाले सिदकियाहको छोरोहरू तिनको उपस्थितिमा मारिदिए। सिदकियाहलाई उनका छोराहरू उपस्थितिमा मारिएको हेर्न वाध्य पारियो। तिनीहरूले यहूदाका सारा हाकिमहरूलाई पनि मारिदिए।",
			"11": "<sup>11</sup> त्यसपछि बाबेलका राजाले सिदकियाहलाई अन्धा बनाए। उसले सिदकियाहलाई साङ्गलाले बाँधे अनि तिनलाई बाबेलमा लगेर तिनको मृत्यु सम्म कैदमा राखे।",
			"12": "<sup>12</sup> राजा नबूकदनेस्सरको राज्यकालको उन्नाईसौं बर्षको पाँचौ महीनाको दशौं दिनमा बाबेलका राजाका विशेष रक्षकहरूको कप्तान नबूजरदान यरूशलेममा आए।",
			"13": "<sup>13</sup> नबूजरदानले परमप्रभुको मन्दिरमा आगो लगाइदियो। उनले राजाको महल, सबै साधरण र महत्वपूर्ण घरहरूमा आगो लगाइदियो।",
			"14": "<sup>14</sup> बाबेलका सम्पूर्ण सेना जो तिनीहरूका कप्तानसित थिए, तिनीहरूले यरूशलेमको वरिपरिको पर्खालहरू तोडिदिए।",
			"15": "<sup>15</sup> कप्तान नबूजरदानले केही गरीब मानिसहरू अनि शहरमा बाँकी रहेका मानिसहरूलाई कैद गरे अनि बाबेलका राजासित समर्पण भइसकेकाहरूलाई बाबेल लगे। उनले कुशल कारीगरहरूलाई पनि कैद गरेर लगे।",
			"16": "<sup>16</sup> तर नबूजरदानले कतिपय गरीब मानिसहरूलाई खेती र दाखबारीमा काम गर्नलाई छोडी दिए।",
			"17": "<sup>17</sup> बाबेलीहरूले परमप्रभुको मन्दिरको काँसाको खम्बाहरू त्यसको घडेरीहरू, काँसाको ट्याँङ्का तोडे अनि काँसा बाबेलमा लगे।",
			"18": "<sup>18</sup> बाबेलका सेनाले मन्दिरबाट भाँडाहरू, बेलचाहरू, सलेदो काट्नेहरू, धुपौरोहरू, ठूलो बाटाहरू, कचौराहरू, अनि सारा काँसाका भाँडाहरू जो मन्दिरको काममा चलाइन्थ्यो सबै लगे।",
			"19": "<sup>19</sup> राजाका कप्तानका विशेष प्रहरीहरूले साना-साना धूपदानीहरू, अभिषेक गर्ने बोलहरू, भाँडाहरू सामदानहरू, चम्चाहरू जुन सुन र चाँदीबाट बनाइएका थिए अनि अर्घ बलिहरूको लागि प्रयोग गरिन्थ्यो सबै लगे।",
			"20": "<sup>20</sup> ती दुइ खम्बाहरू, एउटा ठूलो काँसाको खड्कुँलो, बाह्रवटा काँसाको गोरूहरू अनि सार्नै नसक्ने आधारहरू जो राजा सुलेमानले परमप्रभुको मन्दिरको लागि बनाएका थिए। ती सबै काँसाले बनाएका चीजहरू तौल गरी नसक्नुको थियो।",
			"21": "<sup>21</sup> प्रत्येक काँसाका स्तम्भहरू 18 हात उँचाई, 12 हात गोलाई र 4 अंगुल मोटाई अनि बीचमा खोक्रो भएको थियो।",
			"22": "<sup>22</sup> प्रत्येक स्तम्भको शिरहरू काँसाले बनिएका अनि 5 हात अग्ला थिए। यो काँसाले सजिएको जाली र काँसाको दारीमदाना बुट्टाहरूले वरिपरि सजिएको थियो। अन्य स्तम्भ पनि दारीमदाना जस्तै बुट्टाले सजिएका थिए।",
			"23": "<sup>23</sup> त्यस्का छेऊ-छाऊमा 96 वटा दारिमहरू थिए। त्यहाँ जालीको घेरा वरिपरि जम्मा 100 वटा दारिमहरू थिए।",
			"24": "<sup>24</sup> राजाको खास रक्षकहरूको अधिकारीले सरायाह र सपन्याहलाई कैदी बनाएर लगे। सरायाह मुख्य पूजाहारी र सपन्याह दोस्रो पूजाहारी थिए। त्यस बाहेक तीनजना ढोकेहरूलाई पनि कैदीहरू बनाएर लगे।",
			"25": "<sup>25</sup> तिनले यरूशलेममा भेटिएका राजाका सेनाहरूका अफिसर अनि राजाका सातजना सल्लाहकारहरूलाई पनि लगे। तिनले अधिकारीहरूका सचिव जसले मानिसहरूको नाउँ सेनामा लेखाउँथे तिनलाई, त्यस ठाउँमा भेटिएका मानिसहरू र शहरमा भेटिएका साठ्ठी जना पुरूषहरूलाई पनि लगे।",
			"26": "<sup>26</sup> विशेष रक्षकका कप्तान नबूजरदानले तिनीहरूलाई बाबेलका राजा जो रिब्लामा थिए तिनकहाँ पुर्याए। रिब्ला हमात देशमा छन्। राजाले तिनीहरू सबैलाई मार्ने आदेश दिए। यसर्थ तिनले तिनीहरूलाई मारे।\nयसरी यहूदाका मानिसहरूलाई कैदी जस्तै आफ्नो देशबाट बाहिर लगियो।",
			"28": "<sup>28</sup> नबूकदनेस्सरले यसरी मानिसहरूलाई कैद बनाएर लगे अनि तिनीहरूको संख्या यही होः\nनबूकदनेस्सर राजा भएको सातौं बर्षमा, 3,023 मानिसहरू यहूदाबाट लगियो।",
			"29": "<sup>29</sup> नबूकदनेस्सरको राज्यकालको अठारौं बर्षमा 832 यहूदीहरूलाई यरूशलेमको बाहिर लगियो।",
			"30": "<sup>30</sup> नबूकदनेस्सरको राज्यकालको तेईसौं बर्षमा प्रहरीहरूका कप्तान नबूजरदानले 745 यहूदीहरूलाई निर्वासनमा लग्यो।\nजम्मा 4,600 यहूदीहरूलाई कैदी बनाएर लगियो।",
			"31": "<sup>31</sup> यहोयाकीनलाई मुक्त गराए\nयहूदाका राजा यहोयाकीनले बाबेलमा सैंतीस बर्ष कैदी जीवन बिताए। यहोयाकीनको कैदीकालको सैंतीस बर्षमा बाबेलका राजा एवील-मरोदकले अति दया साथ यहोयाकीनलाई कैदबाट मुक्त गराए। यो त्यही बर्ष थियो जब एवील-मरोदकका राजा भएका थिए।",
			"32": "<sup>32</sup> एवील-मरोदकले यहोयाकीनसँग प्रेमपूर्वक बात गरे। अनि उनले यहोयाकीनलाई बाबेलमा उनीसँग रहेका अन्य राजाहरूलाई भन्दा उच्च स्थानको सम्मान दिए।",
			"33": "<sup>33</sup> यसकारण यहोयाकीनले आफ्ना कैदी पोषाक खोलेर उतारे। त्यसपछि उसले भोजन पनि राजाकै टेबलमा खाए।",
			"34": "<sup>34</sup> प्रत्येक दिन बाबेलका राजाले यहोयाकीनलाई भत्ता दिन थाले। यो यहोयाकीनको मृत्यु पर्यन्त लगातार चलिरह्यो।"
		}
	}
};
module.exports = book;