var book = {
	"name": "2 कोरिन्थी",
	"numChapters": 13,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> ख्रीष्ट येशूको एकजना प्रेरित पावलबाट। परमेश्वरले इच्छा गरे अनुसार मलाई एक प्रेरित रोज्नु भयो। हाम्रा ख्रीष्टमा भाइ तिमोथिबाट पनि कोरिन्थीमा बस्ने परमेश्वरको मण्डलीलाई र सारा अखैया देशमा बस्ने परमेश्वरका मानिसहरूलाई प्रणामः",
			"2": "<sup>2</sup> हाम्रा पिता परमेश्वर र प्रभु येशू ख्रीष्टबाट अनुग्रह र शान्ति।",
			"3": "<sup>3</sup> पावल परमेश्वरलाई धन्यवाद चढाउँछन्\nपरमेश्वर र हाम्रा प्रभु येशू ख्रीष्टका पितालाई धन्यवाद। परमेश्वर पिता हुनुहुन्छ, जो दयामय हुनु हुन्छ। उहाँ पूर्ण सुखमय परमेश्वर हुनुहुन्छ।",
			"4": "<sup>4</sup> जहिले-जहिले हामीलाई दुःख आइ पर्छ, उहाँले हामीलाई सान्त्वना दिनुहुन्छ ताकि हामीले पनि अरूलाई दुःख परेको बेला सान्त्वना दिन सकौं। हामी उनीहरूलाई त्यही सान्त्वना दिन सक्छौं जुन परमेश्वरले हामीलाई दिनुभयो।",
			"5": "<sup>5</sup> ख्रीष्टका धेरै कष्टहरूमा हामी सहभागी बन्छौं, त्यसरी नै, ख्रीष्टबाट हामीले धेरै सान्त्वना पाँउछौं।",
			"6": "<sup>6</sup> यदि हामीले कष्टहरू पार्यौं भने त्यो तपाईंको सान्त्वना र मुक्तिका निम्ति हो। यदि हामीले सान्त्वना पाए त्यो तिमीहरूका निम्ति हो। त्यसले तिमीहरूलाई हामीहरूमा झैं धैर्यसित कष्ट सहन सहायता दिन्छ।",
			"7": "<sup>7</sup> तिमीहरूमाथि हाम्रो दरिलो आशा छ। हामी जान्दछौं तिमीहरू हाम्रा कष्टहरूमा भागीदार हौ। त्यसैले हामी जान्दछौं कि हाम्रा सान्त्वनामा पनि तिनीहरू भागीदार बन्नेछौ।",
			"8": "<sup>8</sup> दाज्यू-भाइ र दिदी-बहिनीहरू हो। एशिया प्रदेशमा हामीले भोगेका कष्टहरूको बारेमा पनि तिमीहरूले जानिराख भन्ने हामी चाहन्छौं। त्यहाँ हामी ठूलो कष्टमा थियौं। हाम्रा सहने शक्ति भन्दा कष्ट धेरै थियो। हामीले त बाँच्ने आशा पनि छोडिसकेका थियौं।",
			"9": "<sup>9</sup> हामीले साँच्चै सोच्यौं की हामी निश्चय मर्ने छौं, यो यस्तो भयो की हामीले आफूमा भन्दा परमेश्वरमा विश्वास गर्नु पर्छ। परमेश्वरले मानिसहरूलाई मृत्युबाट ब्यूँताउँनु हुन्छ ।",
			"10": "<sup>10</sup> परमेश्वरले हामीलाई मृत्युको आपदबाट बचाउनु भयो। अनि परमेश्वरले हामीलाई सदा सर्वदा रक्षा गर्नु हुन्छ। हामीले उहाँमा आशा गर्यैं ताकि उहाँले हामीलाई बचाई राख्नु हुनेछ।",
			"11": "<sup>11</sup> अनि तिमीहरूले आफ्ना प्रार्थनाद्वारा हामीलाई सहायता गर्न सक्नेछौ। घेरै मानिसहरूले हाम्रो निम्ति परमेश्वरलाई धन्यवाद दिनेछन् किनभने तिनीहरूको प्रशस्त प्रार्थनाले गर्दा परमेश्वरले हामीलाई आशीर्वाद दिनु भयो।",
			"12": "<sup>12</sup> पावलको योजनामा परिवर्तन\nयही कुराको लागि हामी घमण्ड गर्न सक्छौं अनि हाम्रो पूर्ण हृदय खोलेर भन्न सक्छौं कि यो साँच्चो हो। संसारमा हामीले जे जति गरेकाछौं, हामीले परमेश्वरको इमान्दारी र शुद्ध हृदयबाट गरेकाछौं। अनि यो हाम्रो तिमीहरूसित व्यवहारमा अझ धेरै सत्य छ। हामीले परमेश्वरको अनुग्रहबाट गरेका हौं, तर मानव ज्ञानले होइन।",
			"13": "<sup>13</sup> हामी तिमीहरूलाई तिनै कुराहरू मात्र लेख्छौं जुन तिमीहरूले सम्पूर्ण रूपले पढ्न र बुझ्न सक्छौ। अनि म आशा गर्छु तिमीहरूले पुरै बुझ्नेछौ।",
			"14": "<sup>14</sup> तिमीहरूले हाम्रो विषयमा केही कुराहरू बुझिसकेकाछौ। त्यो दिन जब हाम्रा प्रभु येशू ख्रीष्ट आउनु हुनेछ, म आशा गर्छु तिमीहरूले बुझ्नेछौ, जब हामी तिमीहरू प्रति गर्व गरे झैं तिमीहरू पनि गर्वावित हुनेछौ।",
			"15": "<sup>15</sup> म यी कुराहरूमा ढुक्क थिएँ। त्यसैले मैले पहिले तिमीहरूकहाँ आउने योजना बनाएँ। तब तिमीहरूले दुइ पल्ट आशीर्वाद पाउने थियौ।",
			"16": "<sup>16</sup> म्यासिडोनिया जाँदा मैले तिमीहरूलाई भेट्न सोचेको थिएँ। अझ फर्कंदा पनि तिमीहरूलाई भेट्ने योजना थियो। मैले आफ्नो यहूदिया यात्रामा तिमीहरूबाट सहायता लिन सोचेको थिएँ।",
			"17": "<sup>17</sup> के तिमीहरू मैले गहिरो विचार नगरी यी योजनाहरू बनाएको सोच्छौ? अथवा तिमीहरू सम्झौला कि म संसारिक मानिस झैं योजना बनाउँछु अनि एकै चोटी “हो, हो” अनि “होइन, होइन” भन्छु।",
			"18": "<sup>18</sup> तर यदि तिमीहरू परमेश्वरमाथि विश्वास गर्न सक्छौ भने, तब हामी तिमीहरूलाई जे भन्छौं त्यो कहिल्यै पनि “हो” र “होइन” दुवै हुँदैन।",
			"19": "<sup>19</sup> परमेश्वरको पुत्र येशू ख्रीष्ट जसको बारेमा सिल्भानस, तिमोथी र मैले तिमीहरू माझ गरेको प्रचारमा दुवै “हो” र “होइन” थिएन। ख्रीष्टमा यो सधैँ “हो” हो।",
			"20": "<sup>20</sup> परमेश्वरका जम्मै प्रतिज्ञाहरू ख्रीष्टमा यो सधैँ “हो” हुन्छ। अनि यस सच्चाइले हामी ख्रीष्टद्वारा परमेश्वरको गौरवमा “आमिन” भन्छौं।",
			"21": "<sup>21</sup> अनि एक मात्र परमेश्वर हुनु हुन्छ जसले हामीलाई र तिमीहरूलाई ख्रीष्टमा दरिलो बनाउँनु हुन्छ। परमेश्वरले हामीलाई विशेष रूपले आशिष दिनु भएकोछ।",
			"22": "<sup>22</sup> हामी उहाँकै हौं भन्ने प्रमाण गर्न, उहाँले हामीमा छाप लगाउनु भएकोछ। उहाँको प्रतिज्ञाको पक्का प्रमाण स्वरूप हाम्रो हृदयमा उहाँको आत्मा दिनुभएकोछ।",
			"23": "<sup>23</sup> म तिमीहरूलाई यो भन्छु अनि यो साँच्चो हो भनी प्रमाण गर्न म परमेश्वरलाई साक्षी बसिदिनु होस् भन्छु। म कोरिन्थमा फर्केर नआएको कारण यही थियो कि म तिमीहरूलाई शास्ति दिन वा तिमीहरूको चित्त दुःखाउनु चाहन्नथें।",
			"24": "<sup>24</sup> मैले यो भन्न खोजेको होइन कि हामी तिमीहरूको विश्वासमाथि नियन्त्रण राख्न चाहन्छौ। तिमीहरू विश्वासमा दृढ छौ। तर तिमीहरूकै आनन्दको लागि हामी तिमीहरूसित काम गर्नेछौं।"
		},
		"2": {
			"1": "<sup>1</sup> यसैले, मैले निश्चय गरें कि मेरो अर्को भ्रमणले तिमीहरू दुःखी हुनु पर्दैन।",
			"2": "<sup>2</sup> मैले तिमीहरूलाई दुःखीत बनाएँ भने कसले मलाई खुशीयाली दिन्छ? केवल तिमीहरूले मात्र मलाई खुशी पार्न सक्छौ जसलाई मैले दुःखीत बनाएँ।",
			"3": "<sup>3</sup> यही कारणले मैले तिमीहरूलाई पत्र लेखें जब म भ्रमणमा आएँ जसले मलाई खुशी बनाउँछन् तिनीहरूबाट म दुःखी हुनु पर्दैन। तिमीहरूको विषयमा बेफिक्री छु। म पक्का विश्वासमा छु कि तिमीहरू मेरो प्रसन्नतालाई आफ्नो प्रसन्न सम्झन्छौ।",
			"4": "<sup>4</sup> अघि जब मैले लेखें मेरो मन खुबै विचलित र दुःखीत थियो। मैले त्यो चिट्ठी आफ्नो आँसुले लेखेको थिएँ मैले तिमीहरूलाई दुःखीत तुल्याउनु लेखेको थिइनँ। तिमीहरू प्रति मेरो मायाको गहिराई बुझाउन लेखेको थिएँ।",
			"5": "<sup>5</sup> भुल गर्नेलाई क्षमा देऊ\nतिमीहरूलाई मण्डलीमा कसैले दुःखीत पार्यो। उसले मलाई दुःखीत बनाएन तर तिमीहरू सबैलाई केही मात्रमा दुःखी बनायो, वास्तवमा त्यो जति छ म त्यसलाई अझ बढाउन चाहँदिनँ।",
			"6": "<sup>6</sup> तिमीहरूका मण्डलीबाट धेरैले उसलाई जो दण्ड दिए उसको लागि मनग्गे छ।",
			"7": "<sup>7</sup> तर अब यो अझ असल हो कि तिमीहरूले उसलाई क्षमा दिनु पर्छ उसलाई सान्तवना दिनु पर्छ। त्यसले उसलाई अति नै दुःख र एकदमै हरेस खानुबाट जोगाउँछ।",
			"8": "<sup>8</sup> यसककारण म विन्ती गर्छु कि तिमीले उसलाई तिम्रो प्रेम देखाऊ।",
			"9": "<sup>9</sup> यसैले मैले तिमीहरूलाई यो लेखेको हुँ। हरेक कुरामा तिमीहरू आज्ञाकारी छौ कि छैनौ भन्ने जाँच्न र हेर्न म चाहन्थें।",
			"10": "<sup>10</sup> कसैलाई तिमीले क्षमा दियो भने, म पनि त्यस मानिसलाई क्षमा दिन्छु। अनि मैले जे क्षमा दिएको छु-ममा क्षमा दिने केही भए-मैले त्यो ख्रीष्टको उपस्थितिमा तिम्रोलागि दिएँ।",
			"11": "<sup>11</sup> शैतानले हामीबाट केही नखोसोस भनेर मैले त्योसो गरेको हुँ। शैतानको के-के योजनाहरू छन् त्यो हामी राम्ररी जान्दछौं।",
			"12": "<sup>12</sup> परमेश्वरले हामीहरूलाई समस्याहरूमा बिजय दिनुहुन्छ\nख्रीष्टको सुसमाचार प्रचार गर्न म त्रोआसमा गएँ, प्रभुले मलाई उहाँको सेवा गर्ने राम्रो मौका दिनु भएको थियो।",
			"13": "<sup>13</sup> तर मैले मनमा शान्ति पाइनँ, किनकि मैले मेरा भाइ तीतसलाई त्यहाँ भेट्टाईनँ। त्यसैले मैले विदा लिएँ र म्यासिडोनिया तर्फ लागें।",
			"14": "<sup>14</sup> तर परमेश्वरलाई धन्यवाद होस् जसले हामीलाई सधैँ ख्रीष्टद्वारा विजयतर्फ डोर्याउनु हुन्छ। परमेश्वरले हामीलाई उहाँको ज्ञान सुगन्धित वासना जस्तो चारैतिर फिंजाउन प्रयोग गर्नुहुन्छ।",
			"15": "<sup>15</sup> परमेश्वरमा हाम्रो यही भेटीहरू छन्। कि ती जो बचाइँदैछन् अनि ती जो नष्ट गरिदैंछन् तिनीहरूको बीचमा हामी ख्रीष्टको मिठो गन्ध हौं।",
			"16": "<sup>16</sup> मानिसहरू जो नष्ट गराइदैंछ तिनीहरूको लागि हामी मृत्युको गन्ध हौं जसले मृत्यु ल्याँउछ। तर बचाइनेहरूलाई हामी जीवनका गन्ध हौं जसले जीवन ल्याउँदछ। यसकारण यस कार्यको निम्ति को योग्य छ?",
			"17": "<sup>17</sup> हामी लाभको निम्ति परमेश्वरका वचन बेच्दैनौ जस्तो धेरै अरूहरूले गर्छन्। होइन! तर ख्रीष्टमा हामी परमश्वरको अधि सत्य बोल्दछौं। हामी परमेश्वरबाट पठाइएका मानिसहरू झैं बोल्दछौं।"
		},
		"3": {
			"1": "<sup>1</sup> परमेश्वरका सेवकहरूको नयाँ करार\nफेरि के हामी हाम्रै विषयमा गर्व गर्छौं? अरूहरू जस्तै तिमीहरूलाई वा तिमीहरूबाट परिचय-पत्रको खाँचो पर्छ?",
			"2": "<sup>2</sup> तिमीहरू नै हाम्रो परिचय पत्र हौ। त्यो परिचय पत्र हाम्रो हृदयमा लेखिएको छ। त्यो सबैले जानेका र पढेका छन्।",
			"3": "<sup>3</sup> यो देखाऊ कि तिमीहरू ख्रीष्टद्वारा हामी माफर्त पठाइएका परिचय पत्र हौ। त्यो पत्र मसीले लेखिएको छैन तर शाश्वत परमेश्वरको आत्माले लेखिएकोछ। त्यो ढुङ्गाका पाटीहरूमा  लेखिएको छैन त्यो मानव हृदयहरूमा लेखिएको छ।",
			"4": "<sup>4</sup> हामी यसरी कुरा गर्न सक्छौं किनभने ख्रीष्ट मार्फत परमेश्वरको अघि हामी ढुक्क छौं।",
			"5": "<sup>5</sup> हामी भन्दैनौं कि कुनै पनि काम आफैं राम्रो गर्न सक्छौं। परमेश्वर नै शक्तिको श्रोत हुनुहुन्छ, जे हामी गर्छौं त्यो गर्न योग्य बनाँउनु हुन्छ।",
			"6": "<sup>6</sup> (परमेश्वरबाट उहाँका मानिसलाई दिइएको) नयाँ करारको सेवक बन्न परमेश्वरले हामीलाई योगय बनाउनु भएको छ। यो नयाँ करार लिखित नियम होइन। यो आत्माको हो। लिखित करारले मृत्यु ल्याँउछ, तर पवित्र आत्माले जीवन दिन्छ।",
			"7": "<sup>7</sup> नयाँ वाचाले बढी महिमा ल्याउँछ\nमृत्यु ल्याउने व्यवस्थाका अक्षरहरू ढुङ्गमा खोपिएका थिए। अनि यो परमेश्वरको महिमासँग आयो। मोशाको अनुहार यति उज्यलो भयो कि इस्राएलका मानिसहरूले उनको अनुहार हेर्न सकेनन्। त्यो महिमा पछि लुप्त भयो।",
			"8": "<sup>8</sup> पवित्र आत्माको सेवा कसरी अझ वढी महिमामय होला?",
			"9": "<sup>9</sup> मेरो भनाइको अर्थ होः व्यवस्थाले मानिसहरूलाई पापको दोष निर्णय गर्यो तर यसले महिमा लियो। त्यसैले जुन सेवाले मानिसहरूलाई धार्मिक बनाउँछ त्यसको अझै बढी महिमा हुँदछ।",
			"10": "<sup>10</sup> त्यस प्राचीन सेवाको महिमा अझ बढी थियो। तर जब नयाँ सेवाको महान महिमासँग त्यसलाई दाँजीन्छ, त्यो सेवाको महिमा हराउँछ।",
			"11": "<sup>11</sup> त्यो सेवा जुन लुप्त भयो त्यो महिमासँग आएको भए, तब यो सेवा जो सदा-सर्वदा रहनेछ त्यसको अझ विशाल महिमा हुने थियो।",
			"12": "<sup>12</sup> हामीसित यो आशा छ, यसैले हामी खुबै साहसी छौं।",
			"13": "<sup>13</sup> हामी मोशा जस्ता छैनौं। उनले आफ्ना अनुहारमा घुम्टो हालेका थिए। इस्राएलका मानिसहरूबाट अनुहार लुकाउनु मोशाले आफ्नो अनुहार छोपेका थिए। महिमा अल्पिदैथ्यो अनि मोशा चाहँदैन थिए कि तिनीहरूले त्यसको अन्त्य देखुन्।",
			"14": "<sup>14</sup> तर तिनीहरूको मन बन्द छन् तिनीहरूले बुझ्न सक्तैनन्। आज पनि जब तिनीहरू पुरानो करार पढ्छन् त्यही ढकनीले अर्थ छोपिन्छ। ढकनी खोलिएको छैन। त्यो खाली ख्रीष्टद्वारा हटाइन्छ।",
			"15": "<sup>15</sup> तर आज सम्म पनि जब ती मानिसहरू मोशाको व्यवस्था पढछन्, तिनीहरूका मनमा घुम्टो लागेको हुन्छ।",
			"16": "<sup>16</sup> तर जब कुनै मानिस बदलन्छ र प्रभुलाई पछ्याउँछ, त्यो घुम्टो हटाइन्छ।",
			"17": "<sup>17</sup> प्रभु, आत्मा हुनु हुन्छ। अनि जहाँ प्रभुको आत्मा छ त्यहाँ स्वतन्त्र हुन्छ।",
			"18": "<sup>18</sup> हाम्रो अनुहार ढाकिएको छैन। हामी सबै प्रभुको महिमा देखाउँछौं। हामी उनी जस्तै हुन परिवर्तन हुँदैछौं। यस परिवर्तनले हामीमा झन-झन महिमा ल्याउँछ।"
		},
		"4": {
			"1": "<sup>1</sup> माटाको भाँडामा आत्मिक धन\nपरमेश्वरले, आफ्नो दयाले हामीलाई यो काम गर्न दिनु भएकोछ, यसकारण हामी यो काम छोड्दैनौं।",
			"2": "<sup>2</sup> तर हामीले गुप्त र लज्जाजनक तरिका त्यागेका छौं। हामी छल कपट प्रयोग गर्दैनौं र परमेश्वरको शिक्षा परिवर्त्तन गर्दैनौं। अँहँ! ठ्याम्मै गर्दैनौं। हामी सत्य र सजिलोसित बुझाउँछौं। हामी को हौं भन्ने कुरो मानिसहरूलाई हामी यसरी नै देखाउँछौं। अनि हामी अनि परमेश्वर समक्ष हामी कस्तो मानिस हौं त्यो पनि उनीहरूले आफ्नो हृदयमा जान्न सक्छन्।",
			"3": "<sup>3</sup> हामीले प्रचार गरेको सुसमाचार लुकेको होला तर यो तिनीहरूकै लागि लुकेको हो जो अल्मलिएका छन्।",
			"4": "<sup>4</sup> यस संसारको शासनकर्त्ता जसले विश्वास गर्दैनन् ती मानिसहरूको मन अन्धा बनाइदिएको छ। यसर्थ तिनीहरूले ख्रीष्टको महिमाको सुसमाचारको प्रकाश देख्न सक्तैनन्। ख्रीष्ट नै एक हुनुहुन्छ जो एकदमै परमेश्वर जस्तै हुनुहुन्छ।",
			"5": "<sup>5</sup> हामी आफ्नो बारेमा प्रचार गर्दैनौं। तर हामी येशू ख्रीष्ट प्रभु हुनुहुन्छ भन्ने सत्य कुरा प्रचार गर्छौं। अनि हामी प्रचार गर्छौं कि येशूको निम्ति हामी तिमीहरूका सेवक हौं।",
			"6": "<sup>6</sup> परमेश्वरले एक पल्ट भन्नु भएको थियो, “अन्धकारबाट ज्योति चम्किने छ!” अनि उहाँ तिनै परमेश्वर हुनुहुन्छ जसले हाम्रो हृदयमा आफ्नो ज्योति प्रकाशित हुन दिनुभयो। ख्रीष्टको अनुहारमा परमेश्वरको महिमा झल्काएर उहाँले हामीलाई प्रकाश दिनु भयो।",
			"7": "<sup>7</sup> परमेश्वरले हामीलाई यो धन दिनुभयो। तर हामी यस्तो धन राख्न माटाका भाँडाहरू जस्ता मात्र छौं। यसले बुझाउँछ कि यो महान शक्ति परमेश्वरबाट हो, हामीबाट होइन।",
			"8": "<sup>8</sup> हामी दुःखै दुःखले घेरिएका छौं, तर हामी किचिएका छैनौं। किहले काँही हामी अल्मलमा पर्छौं र के गर्नु पर्ने जान्दैनौं, तर हामी त्याग्दैनौं।",
			"9": "<sup>9</sup> हामी सताइरहेका छौं तर परमेश्वरले हामीलाई छोड्नु हुन्न। कहिले कहीं हाम्रो चित्त दुखाइन्छ तर हामी नष्ट गरिएका छैनौं।",
			"10": "<sup>10</sup> येशूको मृत्यु हाम्रै शरीरमा छ। हामी यो प्रत्येक ठाउँ मृत्यु लिई हिंड्छौं जसमा कि येशूको जीवन हाम्रा शरीरमा पनि देख्न सकियोस्।",
			"11": "<sup>11</sup> हामी जीवित छौं तर येशूको निम्ति हामी सधैँ मृत्युको खतरामा छौं। हामीमा यस्तै हुन्छ जसमा कि हाम्रो मरण्शील शरीरमा येशूको जीवन पनि झल्कन सकोस्।",
			"12": "<sup>12</sup> यसैले मृत्युले हामीमा काम गर्दैछ, अनि जीवनले तिमीहरूमा काम गर्दैछ।",
			"13": "<sup>13</sup> धर्मशास्त्रमा यो लेखिएको छ, “मैले विश्वास गरें, यसकारण म बोलें।” हाम्रो विश्वास पनि त्यस्तै नै छ। हामी विश्वास गर्छौं र हामी बोल्छौं।",
			"14": "<sup>14</sup> परमेश्वरले प्रभु येशूलाई मृत्युबाट जीवन दिनुभयो। अनि हामी जान्दछौं परमेश्वरले हामीलाई पनि येशूसित बौराउनु हुन्छ। परमेश्वरले हामीलाई तिमीहरूसितै ल्याउनु हुनेछ अनि हामी उनका अघि उभिने छौं।",
			"15": "<sup>15</sup> यी सबै कुराहरू तिमीहरूका निम्ति हो ताकि परमेश्वरको महिमा धेरै मानिसहरूमा बाँढिदैछ। यसकारण, यसरी उहाँको महिमाका निम्ति परमेश्वरले धेरै धन्यवाद पाउनु भइरहेको छ।",
			"16": "<sup>16</sup> विश्वासमा जिउनु\nत्यसैकारणले हामी कहिल्यै कमजोर बन्दैनौं। हाम्रो भौतिक शरीर जीर्ण र कमजोर हुँदैछ तर हामीभित्रको आत्मा हरेक दिन नयाँ बनिदैछ।",
			"17": "<sup>17</sup> यस घरी केही समयको निम्ति साना साना समस्याहरू सामान गर्दैछौं। तर ती समस्याहरूबाट हामीले अनन्त महिमा पाउने सहायता पाइरेहकाछौं त्यो अनन्त महिमा समस्याहरू भन्दा धेरै महान छ।",
			"18": "<sup>18</sup> त्यसैले हामीले देख्न नसकिने कुराहरू हामी सोच्छौं तर जे देख्छौं ती विषयमा होइन। प्रत्यक्ष देखिने कुराहरू क्षणिक हुन्छन् अनि हामीले देख्न नसकिने कुराहरू चाँहि सधैँको लागि हुनेछ।"
		},
		"5": {
			"1": "<sup>1</sup> हामी जान्दछौं, यस धर्तीमा हामी जुन तम्बूमा बस्छौं त्यो नष्ट हुनेछ। तर जब त्यो नष्ट हुनेछ, हामीले बस्ने घर परमेश्वरबाट पाउनछौं। त्यो घर मानिसले बनाएको होइन। यो घर स्वर्गमा हुन्छ र अनन्त रहनेछ।",
			"2": "<sup>2</sup> हामीलाई हाम्रो वर्तमान घरमा वाक्क दिक्क लागिसकेको छ अनि स्वर्गीय घर पाउने इच्छा गरिरहेका छौं अनि परमेश्वरले हामीलाई दिनु हुनेछ।",
			"3": "<sup>3</sup> त्यो हामीलाई पहिर्याई दिइनेछ र हामी नाङ्गो हुने छैनौं।",
			"4": "<sup>4</sup> जतिन्जेल हामी यस शरीरमा बस्छौं, हामी चिन्तारग्रस्त छौं, र गन्गनाउँछौं। मेरो भनाईको अर्थ यो होइन कि हामी यस घरलाई छोड्न चाहन्छौं तर हामी हाम्रो स्वर्गीय घरमा जान चाहन्छौं अनि जब यो शरीरको अन्त हुन्छ यो जीवनले ढाकिने छ।",
			"5": "<sup>5</sup> परमेश्वरले हामीलाई यसैको लागि सृष्टि गर्नु भएको छ। अनि उहाँले वाचा स्वरूप हामीलाई आत्मा दिनु भएको छ ताकि उहाँले हामीलाई नयाँ जीवन दिनु हुनेछ।",
			"6": "<sup>6</sup> यसैकारण हामी सधैँ भरोसामा छौं। हामी जान्दछौं कि जति समय सम्म हामी यस शरीरमा रहन्छौं, हामी प्रभुबाट टाढा हुन्छौं।",
			"7": "<sup>7</sup> हामी विश्वासको मार्ग दर्शनमा बाँचेका छौं-जे देख्छौं त्यसले होइन।",
			"8": "<sup>8</sup> त्यसैले त म भन्छु हाम्रो भरोसा छ। अनि वास्तवमा हामी यस शरीरबाट टाढा रहन इच्छा गर्छौं अनि प्रभु जहाँ हुनुहुन्छ त्यहीं बस्न खोज्छौं।",
			"9": "<sup>9</sup> हाम्रो उद्देश्य परमेश्वरलाई सन्तुष्ट पार्नु हो। यस शरीरमा रहौं अथवा प्रभुसित रहौं हामी उहाँलाई सन्तुष्ट पार्न चाहन्छौं।",
			"10": "<sup>10</sup> न्यायको निम्ति हामी सबै परमेश्वरको अघि उभिनै पर्छ। प्रत्येकले यस पार्थिव शरीरमा हुदाँ जे गरेको छ, उसले त्यो पाउनै पर्छ राम्रो अथवा नराम्रो पाउने नै छ।",
			"11": "<sup>11</sup> मद्दतकारी मानिस परमेश्वरका मित्र बन्छन्\nपरमप्रभुको डरको अर्थ हामी जान्दछौं। यसरकारण मानिसलाई सत्य ग्रहण गर्न हामी सघाउँछौं। हामी वास्तवमा के हौं, परमेश्वर जान्नुहुन्छ। अनि म आशा गर्छु तिमीहरू पनि आफ्नो हृदयमा हामीलाई जान्दछौ।",
			"12": "<sup>12</sup> हामी आफूलाई तिमीहरू अघि प्रमाणित गर्ने कोशिश फेरि गर्दैनौं तर हामी तिमीहरूलाई आफ्नैं बारेमा भन्दैछौं। हाम्रा बारेमा किन गर्वित बन्ने कारणहरू तिमीहरूलाई दिंदैछौं। तब बाहिरी कुराहरूमा जो देखिन्छन् गर्व गर्नेहरूको निम्ति तिमीहरूलाई जवाफ हुनेछ। कसको हृदयमा के छ ती मानिसहरूले वास्ता राख्दैनन्।",
			"13": "<sup>13</sup> यदि हामी पागल हौं भने, त्यो परमेश्वरकोलागि हो। यदि हाम्रो मन ठीक छ भने, त्यो तिमीहरूको लागि हो।",
			"14": "<sup>14</sup> ख्रीष्टको प्रेमले हामीलाई वशमा राख्दछ। किनभने हामी जान्दछौं हामी सबैको लागि ख्रीष्टले मृत्युलाई सहनु भयो।",
			"15": "<sup>15</sup> ख्रीष्टले सबैको लागि प्राण दिनु भयो जसमा कि अब उसो जो बाँचेकाछन् तिनीहरू आफ्नोलागि मात्र बाँच्न हुँदैन। उहाँले तिनीहरूकै लागि प्राण दिनुभयो औ तिनीहरूकोलागि बौर्याइनु भयो, यसर्थ ती मानिसहरू उहाँको लागि बाँच्नु पर्छ।",
			"16": "<sup>16</sup> अब उसो संसारले मानिसहरूलाई जस्तो सम्झन्छ त्यस्तो हामी मानिसहरूलाई सम्झने छैनौं। साँच्चो हो, हामीले पनि पहिले ख्रीष्टलाई संसारले सम्झे जस्तै सम्झन्थ्यौं तर अब हामी त्यसरी सम्झँदैनौं।",
			"17": "<sup>17</sup> यदि कोही ख्रीष्टमा छ भने त्यो नयाँ मानिस बनिएको छ। पुराना कुराहरू बितेर गए, हरेक कुरो नयाँ छ।",
			"18": "<sup>18</sup> यी सब परमेश्वरबाटै हुन्। ख्रीष्टद्वारा, परमेश्वरले हामी र उहाँको माझमा शान्ति बनाउनु भयो। परमेश्वरले हामीलाई मानिसहरू बीच र उहाँसित शान्ति ल्याउने काम दिनु भयो।",
			"19": "<sup>19</sup> मेरो भन्नुको तात्पर्य संसार र आफू माझ शान्ति बनाउँदै परमेश्वर ख्रीष्टमा हुनुहुन्थ्यो। ख्रीष्टमा परमेश्वरले मानिसहरूलाई तिनीहरूको पापहरूका निम्ति दोषी ठहराऊनु भएन। अनि उहाँले मानिसहरूलाई भन्नुलाई हामीलाई यो शान्ति समाचार दिनु भयो।",
			"20": "<sup>20</sup> यसर्थ हामी ख्रीष्टको पक्षमा बोल्न पठाइएका छौं। यो मानिसहरूलाई परमेश्वरले हामी मार्फत बोलाउनु भएको जस्तै हो। हामी ख्रीष्टको निम्ति बोल्छौं अनि परमेश्वरसित शान्ति कायम गर्न तिमीहरूलाई विन्ती गर्छौं।",
			"21": "<sup>21</sup> ख्रीष्टमा पाप थिएन। तर परमेश्वरले उहाँलाई पापी बनाउनु भयो। परमेश्वरले यो हाम्रो लागि गर्नुभयो ताकि हामीहरू ख्रीष्टमा परमेश्वरसित धार्मिक रहन सकौं।"
		},
		"6": {
			"1": "<sup>1</sup> परमेश्वरका हामी सहकर्मीहरू हौं। यसैले, हामी तिमीहरूलाई विन्ती गर्छौं जुन अनुग्रह परमेश्वरबाट पाएका छौ व्यर्थ हुन नदेऊ।",
			"2": "<sup>2</sup> परमेश्वर भन्नु हुन्छः\n“ठीक समयमा मैले तिमीलाई सुनें,\nअनि मुक्तिको दिनमा मैले तिमीलाई सहायता गरें।”\nतिमीहरूलाई म भन्दछु “ठीक समय” अहिले नै हो “मुक्तिको दिन” आजै हो।",
			"3": "<sup>3</sup> मानिसहरूले हाम्रा काममा कुनै भुल नभेटुन् भन्ने हामी चाहन्छौ। त्यसैले अरू मानिसहरूकोलागि समस्या बन्ने कुनै काम हामी गर्दैनौं।",
			"4": "<sup>4</sup> तर दुःखमा, अफ्ठ्यारोमा, ठूल्ठूला समयस्याहरूमा अनि जम्मै परिस्थिहरूमा हामी आफैंलाई परमेश्वरका सेवकहरू हौं भन्ने देखाउँछौं",
			"5": "<sup>5</sup> हामी यस्तो गर्छौ, जब हामी कुटाइ खान्छौं, हामी जेलमा हुन्छौं, जब मानिसहरूले हाम्रो विरोधमा आवाज उठाउँछन्, जब हामीले कठिन परिश्रम गर्नु पर्छ, जब हामी सुत्न पाउँदैनौ अनि हाम्रोमा खाने वस्तु हुँदैन",
			"6": "<sup>6</sup> हामी हाम्र समझदारीले, धैर्यले, दया अनि पवित्र आत्मामा जीवनद्वारा परमेश्वरको कर्मीहरू हौं भनी आफैं देखाँउछौं।",
			"7": "<sup>7</sup> हामी पवित्र आत्माबाट, साँच्चो प्रेमबाट, सत्य प्रचार गर्दै र परमेश्वरको शक्ति मार्फत यस्तो देखाँउछौं। हरेक कुराबाट आफ्नो रक्षा गर्न हामी धार्मिक जीवनको उपयोग गर्र्छौं।",
			"8": "<sup>8</sup> कसैले हामीलाई आदर गर्छन् तर कसैले अनादर गर्छन। कसैले हाम्रो बारेमा प्रशंसा गर्छन् तर कसैले निन्दा गर्छन। कसैले भन्छन् हामी ठगाहा हौं तर हामी सत्य बोल्छौं।",
			"9": "<sup>9</sup> कति मानिसहरूकोलागि हामी अपरिचित छौं, तरैपनि हामी सुपरिचित छौं। हामी मर्न लागेका ठानिन्छौं, तर हेर! हामी बाँचिराखेका छौं। हामी दण्डित छौं, तर हामी मारिएका छैनौं।",
			"10": "<sup>10</sup> हामी धेरै दुःखी छौं, तर हामी खुशी मनाइरहेकै छौं। हामी गरीब हौं, तर हामीले धेरै मानिसहरूलाई धनी बनाइरहेका छौं। हामीसित केही छैन, तर पनि वास्तवमा हामीसित प्रत्येक चीज छ।",
			"11": "<sup>11</sup> कोरिन्थीवासी, तिमीहरूलाई हामीले खुल्ला साथ भनेका छौं। तिमीहरूसीत हामीले हाम्रा हृदय खोलेका छौं।",
			"12": "<sup>12</sup> तिमीहरू प्रति हाम्रो प्रेमको स्रोतमा रोकावट परेको छैन। तर हामीप्रति तिमीहरूको प्रेमभाव रोकिएको छ।",
			"13": "<sup>13</sup> तिमीहरू मेरा नानीहरू जस्तो गरी म तिमीहरूसित बोल्छु, तिमीहरूको निम्ति हामीले जे गर्यौं हाम्रो निम्ति त्यही गर! हाम्रो लागि तिमीहरूको हृदय खुल्ला राख।",
			"14": "<sup>14</sup> अन्यजातिहरूसँग जीउन वारे चेताउनी\nअविश्वासीहरू जस्ता तिमीहरू होइनौ। यसैले उनीहरूसँग सहभागी नहोऊ। असल र खराबमा के साझेदारी हुन्छ? अँध्यारो र उज्यालो एकैसाथ हुन सक्दैन।",
			"15": "<sup>15</sup> ख्रीष्ट र बेलियालले कसरी सम्झौता गर्न सक्छन्? एक विश्वासीले अविश्वासीबाट के पाउन सक्छ?",
			"16": "<sup>16</sup> परमेश्वरको मन्दिर र मूर्तिहरूसित सम्झौता हुन सक्तैन। अनि हामी जीवित परमेश्वरका मन्दिर हौं। जस्तो परमेश्वर भन्नु हुन्छ,\n“म तिनीहरूसित बस्छु,\nतिनीहरूसितै हिँड्छु,\nम तिनीहरूका परमेश्वर हुनेछु,\nअनि तिनीहरू मेरा मानिसहरू हुनेछन्।”",
			"17": "<sup>17</sup> “यसैले ती मानिसहरूबाट निस्केर आऊ,\nआफूलाई तिनीहरूबाट अलग गराऊ, प्रभु भन्नु हुन्छ,\nकेही असुद्ध कुराहरू नछोऊ,\nर म तिमीरूलाई स्वीकार गर्नेछु।”",
			"18": "<sup>18</sup> “म तिमीहरूका पिता बन्नेछु,\nअनि तिमीहरू मेरा छोरा-छोरीहरू हुनेछौ,\nसर्वशक्तिमान् परम प्रभु भन्नु हुन्छ।”"
		},
		"7": {
			"1": "<sup>1</sup> प्यारा साथीहरू! हामीले परमेश्वरबाट यी प्रतिज्ञाहरू पाएकोछौं। यसकराण हरेक किसिमको अशूद्धताबाट हाम्रा शरीर वा हाम्रा अत्मालाई पवित्र पर्छ। हाम्रो जिउने तरिकामा परिपक्क हुने कोशिश हामीले गर्नुपर्छ, किनकि हामी परमेश्वरलाई आदर गर्छौं।",
			"2": "<sup>2</sup> पावलको आनन्द\nहाम्रो लागि आफ्नो हृदय खुला राख। हामीले कसैलाई नराम्रो गरेका छैनौं, हामीले कसैको विश्वास नष्ट गरेका छैनौं। हामीले कसैलाई शोषण गरेका छैनौं।",
			"3": "<sup>3</sup> मैले यो तिमीहरूलाई दोष्याउनको लागि भनेको होइन। मैले अघि नै भनें हामी तिमीहरूलई यती धेरै माया गर्छौं कि हामी तिमीहरूको जीवन र मृत्युसँगै मिलेका छौं,",
			"4": "<sup>4</sup> म तिमीहरूसित औधि ढुक्क अनुभव गर्छु। म तिमीहरू प्रति गर्वित छु। तिमीहरू मलाई धेरै उत्साह दिन्छौ र हाम्रा सारा दु:खहरूमा पनि म बडो अनिन्दित छु।",
			"5": "<sup>5</sup> जब हामी म्यासिडोनियामा आयौं, हामीलाई विश्राम थिएन। हामीले आफ्नो चारैतिर दुःखै दुःख देख्यौं। बाहिर-बाहिर हाम्रो लडाइँ थियो तर भित्र-भित्र डर।",
			"6": "<sup>6</sup> तर दुःखमा पर्नेहरूलाई परमेश्वरले सुख दिनुहुन्छ। अनि जब तीतस आए, परमेश्वरले हामीलाई सुख दिनुभयो।",
			"7": "<sup>7</sup> तिनको आगमन अनि तिमीहरूले उनलाई दिएको सुखले हामीहरू सुखी भयौ। तीतसले भने कि तिमीहरू मलाई भेट्न चाहन्छौ। आफूले गरेको कामको लागि तिमीहरूले दुःख मान्यौ भनी उनले मलाई भने। अनि तिमीहरूले मेरो खुब याद गर्छौ भनेर पनि उनले मलाई भने। यो सुनेर म अत्यन्तै खुशी भएँ।",
			"8": "<sup>8</sup> मैले लेखेको पत्रले तिमीहरूलाई खिन्न तुल्यायो भने पनि त्यो लेखेकोमा म दुःखीत छैनँ। तर, त्यसले शुरूमा तिमीहरूलाई खिन्न बनायो नै, म पनि पहिले त त्यसको लागि दुःखी भएँ। तर त्यसले तिमीहरूलाई थोरै समयको लागि मात्र खिन्न तुल्यायो।",
			"9": "<sup>9</sup> अनि अहिले म हर्षित छु। तिमीहरूलाई खिन्न बनाएर म हर्षित भएको होइन। तर म यसकारणले हर्षित छु कि तिमीहरूको यो यस्तो दुःख हो जुन परमेश्वरले चाहनु हुन्छ। यसर्थ हामीले तिमीहरूलाई कुनै प्रकारले पनि चोट पुर्याएनौं।",
			"10": "<sup>10</sup> परमेश्वरले चाहनु भए जस्तो पीरले एक व्यक्तिको हृदय र जीवनमा परिवर्त्तन ल्याउँछ। यसले मानिसलाई मुक्ति तर्फ डोरयाउँछ र हामी त्यसको लागि कुनै पीर मान्दैनौं। तर संसारिक शोकले मृत्यु गराउँछ।",
			"11": "<sup>11</sup> तिमीले पाएको त्यो पीर चाँहि परमेश्वरले चाहेको पीर नै थियो। अब तिमी बुझ्छौ कि त्यस पीरले तिमीमा के ल्यायो। त्यस पीरले तिमीलाई अझ गम्भीर बनायो अनि आफू भूलमा भएको प्रमाण गराउन प्रेरित गरायो। त्यसले तिमीलाई क्रुद्ध र कातर बनायो। त्यसले तिमीमा मलाई भेट्ने इच्छा जगायो। त्यसले तिमीहरूलाई उत्साहित गरायो र न्याय गर्नु पर्ने रहेछ भन्ने इच्छा गरायो। त्यस चीजहरूबाट तिमी यो समस्यामा नरहेको प्रत्यक्ष बनायो।",
			"12": "<sup>12</sup> त्यो चिट्ठी मैले लेख्नुको कारण यो होइन कि व्यक्तिले भूल गर्यो तर कारण यो हो कि मानिस भूलमा थियो। वास्तवमा परमेश्वरको अघि तिमीहरू हामीलाई कति धेरै याद गर्छौ सो जान्नु त्यो चिट्ठी तिमीहरूलाई लेखिएको हो।",
			"13": "<sup>13</sup> त्यसैकारण हामी सुखी थियौं।\nहामी खुबै आनन्दित थियौं किनभने तितस धेरै आनन्दित थिए। तिमीहरूले गरेको व्यवहारमा तिनी अत्यन्तै खुशी थिए।",
			"14": "<sup>14</sup> मैले तीतससित तिमीहरूबारे गर्व गरेको थिंए। अनि मैले ठीकै गरें भन्ने तिमीहरूले प्रमाण गर्यौ। हामीले तिमीहरूलाई भनेको हरेक कुरो साँच्चो थियो। अनि तितससित हामीले गरेको गर्व पनि साँच्चो थियो भन्ने तिमीहरूले नै साबित गरिदियौ।",
			"15": "<sup>15</sup> अनि उनको तिमी प्रति प्रेम अझ ठूलो हुन्छ जब उसले सम्झन्छ कि तिमीहरू सबै आज्ञाकारी थियौ। तिमीहरूले डर र आदरिसित उनलाई स्वागत गर्यौ।",
			"16": "<sup>16</sup> म खुबै आनन्दित छु किनभने तिमीहरूमाथि म पुरै विश्वास गर्न सक्छु।"
		},
		"8": {
			"1": "<sup>1</sup> ख्रीष्टिय दान\nअनि अब दाज्यू-भाइ, दिदी-बहिनीहरू! तिमीहरूलाई अनुग्रह बारे जनाउनु चाहन्छौं जुन परमेश्वरले म्यासिडोनियामा त्यहाँका मण्डलीहरूलाई दिनुभयो।",
			"2": "<sup>2</sup> ती विश्वासीहरूलाई अत्यन्तै कष्ट दिएर परीक्षा लिइन्थ्यो।",
			"3": "<sup>3</sup> ती साह्रै गरीब मानिसहरू थिए। तथापि ठूलो हर्षसाथ तिनीहरूले मुट्ठी खोलेर दिए। म भन्न सक्छु आफ्नो गच्छेले पुगेसम्म तिनीहरूले दिए। ती विश्वासीहरूले, आफूले दिन सक्ने भन्दा पनि धेरै नै दिए। उनीहरूले उदारतापूर्वक दिए। कसैले तिनीहरूलाई त्यसो गर्न भनेका थिएन।",
			"4": "<sup>4</sup> तर तिनीहरूले हामीलाई घरि-घरि सोध्थे परमेश्वरका मानिसहरूको यस सेवामा भागीदार बन्न पाऊँ भनेर हामीलाई विन्ती गर्थे।",
			"5": "<sup>5</sup> अनि तिनीहरूले यस प्रकारले दिए कि जुन हामीले आशै गरेका थिएनौं। तिनीहरूले आफ्नो दान दिनु अघि नै आफैं प्रभुमा पहिले समर्पण भए। यो नै परमेश्वर चाहनुहुन्छ।",
			"6": "<sup>6</sup> यसैले यो विशेष पूनित कार्य सिद्धयाउन हामीले तितसलाई र तिमीहरूलाई सघाइ माग्यौ। तितस नै एकजना हुन् जसले यो काम शुरू गरे।",
			"7": "<sup>7</sup> तिमीहरू हरेक कुरामा धनी छौ-विश्वासमा, वचनमा, सहायता गर्ने चाहनामा अनि हामीबाट सिकेको प्रेम गर्नमा। र त्यसैले दान दिने काममा पनि तिमीहरू महान बन भन्ने हामी इच्छा गर्छौं।",
			"8": "<sup>8</sup> मैले दिनु भनेर तिमीहरूलाई आज्ञा गरिरहेको छैनँ। तर तिमीहरूको प्रेम सत्य प्रेम नै रहेछ कि भन्ने हेर्न खोज्दैछु। अरू मानिसहरूर मद्दत गर्न इच्छुक छन् भन्ने देखाउन मात्र म यी कुराहरू गर्छु।",
			"9": "<sup>9</sup> हाम्रा प्रभु येशू ख्रीष्टको अनुग्रह त तिमीहरू जान्दछौ। अनि तिमीहरू यो पनि जान्दछौ कि ख्रीष्ट परमेश्वरमा धनी हुनु हुथ्यो, तर तिमीहरूको निम्ति उहाँ गरीब हुनु भयो। आफू गरीब बनेर तिमीहरू धनी हुन सक भनेर नै उनले त्यसो गर्नु भएको हो।",
			"10": "<sup>10</sup> अनि त्यस विषयमा मैले मेरो राय दिएँ अनि यो मैले भनें किनभने म विश्वास गर्छु कि यो तिमीहरूको लागि राम्रो हो। पोहोर साल दान दिन इच्छा गर्नेहरूमा तिमीहरू प्रथम थियौ। साँच्चै नै तिमीहरू दान दिनमा प्रथम थियौ।",
			"11": "<sup>11</sup> यसर्थ तिमीहरूले शुरू गरेको काम अब पूरा गर। तब मात्र तिमीहरूको “गर्ने इच्छा” अनि तिमीहरूको “कर्म” समतुल्य हुनेछ। तिमीहरूसित जे छ त्यहीबाट दान देऊ।",
			"12": "<sup>12</sup> तिमीहरू दिन चाहन्छौ भने मात्र तिमीहरूको दान थापिन्छ। र तिमीसित भएकोबाट तिम्रो दान जाँचिन्छ, तर तिमीहरूसँग नभएकोबाट होइन।",
			"13": "<sup>13</sup> जब अरूहरू सुखी रहाँदा तिमीहरूलाई चाँहि कष्टमा होऊ भन्ने हामी चाहँदैनौं। हामी सबथोक बराबरी होस् भन्ने चाहन्छौं।",
			"14": "<sup>14</sup> यसधरि तिमीहरूसित प्रशस्त छ। तिमीहरूसित भएका यी कूराले अरू मानिसहरूलाई खाँचो परेको कुरो टार्न मद्दत पुग्नेछ। अनि पछि जब उनिहरूसित प्रशस्त हुन्छ उनीहरूले तिमीहरूको खाँचो टारिदिन सक्छन्। तब सबै बराबर हुनेछन्।",
			"15": "<sup>15</sup> धर्मशास्त्रमा यस्तै लेखिएको छः\n“जसले धेरै बटुल्यो उसको धेरै रहेन,\nजसले थोरै बटुल्यो उसकोमा अलिकति मात्र रहेन।”",
			"16": "<sup>16</sup> तितस र उनका साथीहरू\nम परमेश्वरलाई धन्यवाद चडउँछु किनभने उनले तितसलाई तिमीहरू प्रति त्यही प्रेम दिए जुन मेरो तिमीहरू प्रति छ।",
			"17": "<sup>17</sup> हामी उनलाई जे गराउन निवेदन गरेका थियौं, उनले त्यही स्वीकार गरे। उनी तिमीहरूकहाँ जान औधी चाहन्थे। यो उनको आफ्नै विचार थियो।",
			"18": "<sup>18</sup> तितसको साथमा हामी एक जना भाइलाई पठाउँदैछौं जसको प्रशंसा सबै मण्डलीहरूबाट भएको छ। सुसमाचार प्रचारमा उनको सेवाको कारणले यी भाइ प्रशंसित छन्।",
			"19": "<sup>19</sup> हामीले यस्तो दान लिएर हिँड्दा यी भाइ हामीसित हिँड्न मण्डलीहरूबाट छानिएका थिए। प्रभुमा महिमा ल्याउनु, हामी साँच्चै सघाउन चाहन्छौं भन्ने देखाउन हामी यो सेवा गर्दैछौं।",
			"20": "<sup>20</sup> यो घरै धन व्यावहार गर्दा कसैले हामीलाई दोष लाउन मौका नपाओस्। यसर्थ हामी खुबै सर्तक छौं।",
			"21": "<sup>21</sup> हामी ठीक काम गर्ने कोशिश गर्छौ। प्रभुले जस्तो ठीक मान्नु हुन्छ र मानिसहरूले पनि जुन ठीक ठान्छन् हामी त्यही गर्न चाहन्छौं।",
			"22": "<sup>22</sup> अर्को एकजना भाइलाई पठाउँदैछौं जो मद्दत गर्न भने पछि सधैँ अघि सर्छन्। उनको सहायता गर्ने इच्छा अनेकौ पल्ट प्रमाणित गरिसकेका छन्। अनि अहिले पनि उनी अझै धेरै सहयता गर्न चाहन्छन्, किनभने तिमीहरूमाथि उनको त्यस्तो विश्वास छ।",
			"23": "<sup>23</sup> अब तितसको बारेमा-उनी मेरा सहकर्मी हुन्। तिमीहरूलाई सहायता गर्न उनी मसँगै काम गर्दैछन्। अनि अरू भाइहरूको बारेमा तिनीहरू मण्डलीहरूबाट पठाइएका हुन् र तिनीहरूले पनि ख्रीष्टमा महिमा ल्याउँछन्।",
			"24": "<sup>24</sup> त्यसकारण यी मानिसहरू प्रति तिमीहरूले आफ्नो प्रेम देखाऊ। हामी तिमीहरू प्रति किन गर्वित छौं। त्यो तिनीहरूलाई देखाऊ ताकि जम्मै मण्डलीहरूले त्यो देख्नेछन्।"
		},
		"9": {
			"1": "<sup>1</sup> ख्रीष्टियन साथीहरूलाई सहायता गर\nपरमेश्वरका मानिसहरूलाई गरिने सहायताको विषयमा मैले तिमीहरूलाई लेख्नु पर्दैन।",
			"2": "<sup>2</sup> म जान्दछु तिमीहरू सहायता गर्न इच्छुक छौ। म्यासिडोनियाका मानिसहरूसित म यसबारे प्रशंसा गरिरहन्छु। अखैयाका बासिन्दाहरू हो, तिमीहरू पोहोरदेखि नै दान दिनु तयार थियौ भनेर मैले तिमीहरूलाई जनाइसकेको छु। अनि तिमीहरूले दान दिनु चाहेकोले, यहाँका धेरै अरू मानिसहरू उत्साहित बनिए अनि अहिले त्यसो गर्न इच्छुक छन्।",
			"3": "<sup>3</sup> तर म यी भाइहरूलाई तिमीहरूकहाँ नै पठाउँदैछु। म पक्का गराउन चाहन्छु कि तिमीहरूको बारेमा हामी जो गर्व गर्छौ यस विषयमा व्यर्थ सावित नहोस्। तिमीहरूको विषयमा जे भनियो त्यसको लागि म चाहन्छु कि तिमीहरू तयार बस।",
			"4": "<sup>4</sup> यदि कुनै मानिस जो म्यासिडोनियाबाट मसित आउँछ र तिमीहरू तयार नरहेको पाए भने हामीलाई लाजमर्दो हुनेछ। हामी लज्जित हुनेछौं कारण तिमीहरूमाथी हामीले भरोशा गरेका थियौं। अनि वास्तवमा तिमीहरू पनि लज्जित हुनेछौ।",
			"5": "<sup>5</sup> त्यसैले मैले निर्णय गरें कि हामी आउन अघि यी भाइहरूलाई तिमीहरूकहाँ पठाउनुपर्छ। तिमीहरूले वचन दिए अनुसारको यो उपहार उनीहरूले तयार पार्नेछन्। तब, जब हामी आइपुग्नेछौं उपहार तयार हुनेछ, अनि त्यो इच्छापूर्वकको उपहार हुनु पर्छ, अनि त्यो तिमीहरूले असन्तुष्ट भएर दिए जस्तो नहोस्।",
			"6": "<sup>6</sup> याद गरः जसले अलिकति छर्छ उसले अलिकति नै बाली कटनी गर्छ।",
			"7": "<sup>7</sup> हरेकले आफ्नो मनमा जति दिन्छु भनेर चिताएको छ त्यति नै देओस्, विना इच्छा कसैले नदिओस्। जबर्जस्तीमा कुनै व्यक्तिले नदिवोस् जसले खुशी भएर दान गर्छ उसलाई परमेश्वरले प्रेम गर्नूहुन्छ",
			"8": "<sup>8</sup> अनि परमेश्वरले तिमीलाई प्रशस्त मात्र आशीर्वाद दिनु हुनेछ। तब तिम्रोमा सघैं प्रशस्त रहेनछ अनि प्रत्येक शुभकर्मकोलागि मुक्तहस्तले दान दिन सक्नेछौ।",
			"9": "<sup>9</sup> धर्म शास्त्रमा यो लेखिएको छः\n“उसले उद्धारता पूर्वक गरीबहरूलाई दिंदछ,\nउसको दयाभाव सदा-सर्वदा रहिरहन्छ।”",
			"10": "<sup>10</sup> परमेश्वर नै एक हुन्, जसले रोप्नेलाई बिऊ दिनु हुन्छ अनि उहाँले खानलाई रोटी दिनुहुन्छ। अनि परमेश्वरले तिमीहरूलाई बिऊ दिनुहुन्छ र ती बिऊ बढाऊनु हुन्छ। तिम्रो दयाभावबाट उहाँले प्रशस्त बाली उठाउनु हुन्छ।",
			"11": "<sup>11</sup> परमेश्वरले तिमीलाई हरतरहले धनी बनाउनु हुनेछ जसमा कि तिमी सधैँ मुक्तभावले दान दिनु सक्नेछौ। अनि हामी मार्फत धन्यवाद चढाउनेछन्।",
			"12": "<sup>12</sup> तिमीहरूले यस सेवालाई दिइरहेको दानले परमेश्वरका मानिसहरूको खाँचो टार्छ। तर तिम्रो दयाभावले यति नै मात्र गर्दैन, यसले परमेश्वर प्रति धन्यवादका भावना उर्लेर आउछन्।",
			"13": "<sup>13</sup> यो सेवा जुन तिमी गरिरहेकाछौ त्यो तिम्रो विश्वाशको प्रमाण हो। यसकै कारणले मानिसहरूले परमेश्वरको प्रशंसा गर्नेछ। तिमीहरूले परमेश्वरको प्रशंसा गर्छन् किन कि तिमीहरू ख्रीष्टको सुसमाचारलाई मान्छौ-जुन समाचारलाई तिमीहरू विश्वास गर्छौं भन्छौ। मानिसहरूले परमेश्वरको प्रशंसा गर्नेछन् किन कि तिमीहरू मुक्तभावले तिनीहरूसित र सबैसित मिल्दछौ।",
			"14": "<sup>14</sup> अनि जब तिनीहरू तिमीहरूकोलागि प्रार्थना गर्दछन् तिनीहरू तिमीहरूलाई दिनु भएको असीम अनुग्रहले तिनीहरूलाई यस्तो लाग्छ।",
			"15": "<sup>15</sup> उनको वयान गर्न नसक्ने दानको लागि परमेश्वरलाई धन्यवाद देऊ।"
		},
		"10": {
			"1": "<sup>1</sup> पावल आफ्नो सेवकाइको प्रतिक्षा गर्छन्\nम, पावल, तिमीहरूलाई ख्रीष्टको नम्रता र दयासित विन्ती गर्छु। कसै कसैले भन्छन्, जतिन्जेल म तिमीहरूसित रहन्छु म नम्र देखिन्छु तर जब टाडा हुन्छु म साहसी बन्छु।",
			"2": "<sup>2</sup> फेरि कसैले सम्झन्छन् हामी सांसारिक तवरले बाँच्छौं। अब जब म आउँछु तिनीहरूका निम्ति साहसी बन्ने इरादा गर्छु। म बिन्ती गर्छु कि जब म आउँछु मलाई सहास देखाउने आवश्यक नपर्ला।",
			"3": "<sup>3</sup> हामी संसारमै बास गर्छौं। तर संसारिक मानिसहरूले झैं हामी लडाइँ गर्दैनौं।",
			"4": "<sup>4</sup> हामी त्यो हतियारहरू लिएर लडाई गर्छौं जुन संसारले चलाउने हतियाहरहरू भन्दा भिन्दै छ। हाम्रा हतियारहरूमा परमेश्वरको शक्ति छ। यो हतियारले शत्रुहरूको बलियो किल्ला समेत नाश पार्न सक्छ। हामी मानिसहरूका तर्कहरू नाश गर्छौं।",
			"5": "<sup>5</sup> अनि परमेश्वरको ज्ञानको विरूद्धमा उठेको हरेक अहङ्कारलाई नाश गर्छौं। अनि हामी हरेक विचारलाई पक्रन्छौं र त्यसलाई त्याग्ने वा ख्रीष्टलाई मान्ने बनाउँछौं। हरेक विचारलाई ख्रीष्टको आज्ञपालन गर्नलाई वशमा पार्छौ।",
			"6": "<sup>6</sup> तर पहिले तिमीहरूले नै पुरापुरी मान्नु पर्छ भन्ने हामी गर्छौ।",
			"7": "<sup>7</sup> तिमीहरूले आफ्ना अघिल्तिरको तथ्यलाई हेर्नु पर्छ। यदि कसैले आफू ख्रीष्टमा भएको निश्चितरूपले मान्छ भने उसले सम्झनु पर्छ हामी पनि उही जस्तो ख्रीष्ट मै छौं।",
			"8": "<sup>8</sup> त्यो साँच्चो हो, प्रभुले मलाई दिनु भएको अधिकारको कुरो लिएर म मुक्त रूपले गर्व गर्छु। तर उहाँले मलाई यो शक्ति तिमीहरूलाई दरिलो बनाउनलाई दिनु भएको हो, तिमीहरूमा चोट पुरयाउनुलाई होइन। त्यसैले म मेरो गर्व गराईमा लज्जित छैन।",
			"9": "<sup>9</sup> यो पत्रद्वारा तिमीहरूलाई तर्साउँदैछु भन्ने तिमीहरूले सोचेको म चाहँदिन।",
			"10": "<sup>10</sup> कोही मानिसहरू भन्छन्, “पावलका पत्रहरू त जोरदार र गहकिला हुन्छन्। तर जब उनी हामीसित रहन्छन्, उनी कमजोर रहन्छन्, अनि उनको भाषण योग्यता प्रशंसा गर्ने खालको रहँदैन।”",
			"11": "<sup>11</sup> यसो भन्ने मानिसहरूले यो जान्नुपर्छः हामी यस समय तिमीहरूसित छैनौं यसकारण हामी यी कुराहरू पत्रमा लेख्छौं। तर जब हामी त्यहाँ तिमीहरूसित हुन्छौं, हामी त्यही शक्ति देखाउँछौं जुन हामी आफ्ना पत्रहरूमा देखाउँछौं।",
			"12": "<sup>12</sup> हामी आफैंलाई त्यस्ता मानिसहरूको स्तरमा राख्ने आँट गर्दैनौं जो आफूलाई खुबै महत्वपूर्ण सम्झन्छन्। हामी आफूलाई तिनीहरूसित दाँज्दैनौं। तिनीहरू आपसमा आफू आफैं तुलना गर्छन्, तुलनाको स्तर तिनीहरू आफैं हुन् औ तुलनाको नापदण्ड आफैंलाई बनाउँछन्। यसले तिनीहरू केही जान्दैनन् भन्ने देखाउँछ।",
			"13": "<sup>13</sup> तर हामी सीमा नाघरे गर्व गर्दैनौ, हाम्रो गर्वको सीमा परमेश्वरले हामीलाई अह्राएको काम सम्म हुनेछ। यो काम हाम्रो तिमीहरूसिंतको काम अन्तर्गत पर्छ।",
			"14": "<sup>14</sup> हामी ज्यादा गर्व गर्दैनौ। यदि हामी तिमीहरू भएकोमा अहिले सम्म नपूगेको भए, हामी निकै गर्व गर्थ्यौं। तर हामी वास्तवमा तिमीहरूकहाँ आउपुगेकाछौं हामी तिमीहरूकहाँ ख्रीष्टको सुसमाचार लिएर आयौं।",
			"15": "<sup>15</sup> हामी आफ्नै कार्य क्षेत्रमा मात्रै आफ्नो गर्वलाई सीमित राख्छौं। अरूले गरेको काममा गर्व गर्दैनौं। हामी आशा गर्छौं, तिमीहरूको विश्वास बढ़छ, तिमीहरूले हाम्रो कामको क्षेत्र अझै व्यापक बनाउनमा सहायता गर्नेछौ।",
			"16": "<sup>16</sup> हामी तिमीहरूको शहर बाहिरी क्षेत्रमा पनि सुसमाचार प्रचार गर्न चाहन्छौं। अर्को इलाकामा अघि नै भइसकेको काममा हामी गर्व गर्दैनौं।",
			"17": "<sup>17</sup> तर “जसले गर्व गर्छ उसले प्रभुमा गर्व गर्नु पर्छ।”",
			"18": "<sup>18</sup> किनभने असल मानिस उसलाई मानिन्छ जसलाई प्रभुले ग्रहण गर्नु भएको छ, तर उसलाई होइन जसले आफैंलाई असल मान्छ।"
		},
		"11": {
			"1": "<sup>1</sup> पावल र झूटा प्रेरितहरू\nजब म मूर्ख बनिन्छु आशा छ तिमीहरू मलाई सहनेछौ। तर तिमीहरूमलाई सहिरहेका पनि छौ।",
			"2": "<sup>2</sup> मलाई तिमीहरूको ईर्ष्या लाग्छ। अनि यो ईर्ष्या परमेश्वरबाट हो। मैले तिमीहरूलाई ख्रीष्टलाई सुम्पने वचन दिएँ ताकि उहाँ तिमीहरूका एक मात्र पति बनून्। म तिमीहरूलाई चाहन्छु।",
			"3": "<sup>3</sup> तर तिमीहरूको मन भ्रष्ट हुन सक्छ अनि ख्रीष्ट प्रति तिमीहरूको सत्यता र पवित्रता डग्ला कि भन्ने मलाई डर छ। यो त्यस्तो हुनसक्छ जस्तै सर्पको धूर्तताले हव्वालाई धोका दिएको थियो।",
			"4": "<sup>4</sup> यदि कसैले तिमीहरूलाई हामीले बातएको येशू भन्दा अर्कै येशूको बारे बताउँछ भने तिमीहरू उसलाई आनन्दले ग्रहण गर्नेछौ। यदि उसले तिमीहरूलाई हामीले बताएको सुसमाचार दिएको आत्मा भन्दा अर्कै सुसमाचार र आत्मा बताँउछ भने पनि त्यसलाई तिमीहरू खुशीले स्वीकार गर्नैछौ। यसर्थ मलाई पनि त्यही इच्छा देखाऊ।",
			"5": "<sup>5</sup> तर म विश्वास र्गदिन कि ती “महान प्रेरितहरू” म भन्दा राम्रा हुन्।",
			"6": "<sup>6</sup> साँच्चो हो म सिपालु वक्ता होइन। तर मेरोमा ज्ञान छ। यो हामीले तिमीहरूलाई हरतरहले स्पष्ट गरिदिएकाछौं।",
			"7": "<sup>7</sup> सित्तैमा तिमीहरूलाई मैले परमेश्वको सुसमाचार सुनाएँ। तिमीहरूलाई माथि उठाउनु मैले आफूलाई तल झारें। त्यो भूल भयो र?",
			"8": "<sup>8</sup> मैले अरू मण्डलीहरूबाट आर्थिक सहायता लिएँ। तिमीहरूको सेवा गर्न सकुँ भनेर मैले तिनीहरूबाट पैसा लिएँ।",
			"9": "<sup>9</sup> जब म तिमीहरूसित थिएँ त्यसबेला पनि खाँचो पर्दा मैले तिमीहरू कसैलाई दुःख दिइनँ। मलाई चाहिने जति सबै म्यासिडोनियाबाट आउने भाइहरूबाट नै मैले लिएँ। तिमीहरूमाथि मैले आफूलाई कुनै किसिमको भार हुन मन पराइनँ। अनि कहिल्यै भार बन्ने छैन पनि।",
			"10": "<sup>10</sup> यस विषयमा गर्व गर्नु मलाई अखैयामा कसैले रोक्ने छैन। ममा भएका ख्रीष्टको सत्यतासित म यो भन्नसक्छु।",
			"11": "<sup>11</sup> अनि किन म भार थिइनँ? मैले तिमीहरूलाई प्रेम गरेको छैन भनेर? होइन। परमेश्वर जान्नु हुन्छ म तिमीहरूलाई प्रेम गर्छु।",
			"12": "<sup>12</sup> अहिले मैले जे गरिरहेछु त्यो म गरिनैरहने छु। म त्यसो गरिरहनेछु किनभने म आशा गर्छु ती घमण्ड गर्न मौका खोज्ने मानिसहरूलाई वाधा दिन्छु। तिनीहरू जुन कार्यको निम्ति घमण्ड गर्छन् त्यो हामीले गरेको कार्यको बराबरी हो भन्ने देखाउन तिनीहरू इच्छा गर्छन्।",
			"13": "<sup>13</sup> तिनीहरू झूटा प्रेरितहरू हुन्। तिनीहरू धोकावाजहरू हुन्। मानिसहरूलाई छक्कायाउन ख्रीष्टका प्रेरितहरूको भेस लगाएर हिँड्नेहरू मात्र हुन्।",
			"14": "<sup>14</sup> त्यसमा कुनै अचम्म छैन। किनकि शैतानले पनि मानिसलाई छक्याउन आफूलाई ज्योतिर्मय-दूतको रूप लिन्छ।",
			"15": "<sup>15</sup> यसर्थ यदि शैतानका सेवकहरू धार्मिक मानिसहरूको भेषमा आउँछन् भने हामीले अचम्म मान्ने कुरो छैन। तर अन्तमा, ती मानिसहरूले आफ्नो भूल कर्मको निम्ति दण्ड पाउनेछन्।",
			"16": "<sup>16</sup> पावल आफ्ना कष्टहरू सुनाउँछन्\nम फेरि भन्दैछु। मलाई कसैले मूर्ख सम्झनु पर्दैन। तर यदि मलाई मूर्ख नै सम्झन्छौ भने एउटा मूर्खालाई जसरी व्यवहार गर्छौ मसँग पनि त्यसरी नै व्यवहार गर। तब मेरोमा पनि गर्व गर्नु केही रहनेछ।",
			"17": "<sup>17</sup> म गर्व गर्छु किनकि म आफूमा विश्वस्त छु। तर मैले यो प्रभुले बोल्नु भए जस्तो बोलेको होइन। मैले एउटा मूर्खले जस्तो गर्व गर्दैछु।",
			"18": "<sup>18</sup> धेरैले आफ्नो संसारिक जीवन बारे घमण्ड गर्छन्। यसर्थ म पनि घमण्ड गर्छु।",
			"19": "<sup>19</sup> तिमीहरू ज्ञानी छौ यसैले मूर्खको कुराहरू पनि सहन्छौ।",
			"20": "<sup>20</sup> यदि कसैले तिमीहरूलाई जबर्जस्ती गर्छ, शोषण गर्छ, अथवा तिमीहरूमाथि मौका लिन्छ अनि आफैं घमण्ड गर्छ अथवा गालामा थप्पड हिर्काए पनि म जान्दछु तिमीहरूले सहने छौ।",
			"21": "<sup>21</sup> तिमीहरूमाथि त्योस्तो व्यवहारमा म लज्जित हुन्छु, तर हामी अति “दुर्बल” छौं।\nतर यदि कसैले गर्व गर्ने साहस गर्छ भने म पनि हिम्मती हुन्छु र गर्व गर्छु। मूर्ख झैं बात गर्दैछु।",
			"22": "<sup>22</sup> के तिनीहरू हिब्रू हुन्? म पनि हुँ। तिनीहरू इस्राएली हुन् र? म पनि हुँ। के तिनीहरू अब्राहामका सन्तान हुन् र? म पनि हुँ।",
			"23": "<sup>23</sup> तिनीहरू ख्रीष्टको सेवा गर्छन्? म अझ धेरै गर्छु। म पागल जस्तै बात गरिरहेको छु। तिनीहरूले भन्दा धैरै कठिन-कठिन कामहरू मैले गरेकोछु। म तिनीहरू भन्दा घेरै चोटी जेल भोगेको छु। मैले अझ घेरै कुटाई खाएको छु। धेरै पटक म झण्डै-झण्डै मरेको हुँ।",
			"24": "<sup>24</sup> मैले पाँच पल्ट यहूदीहरूबाट उनन्चालिस कोर्रा खाएको छु।",
			"25": "<sup>25</sup> तीन पल्ट लट्ठीले कुटाई खाएको छु। एक पल्ट त म ढुङ्गाले हानेर झण्डै मारिएँ। तिन पल्ट म चडेको जहाज डुब्यो र एक पल्ट त रातभरि र भोलि बल्ट पनि समूद्रमा बिताएँ।",
			"26": "<sup>26</sup> मैले धेरै यात्रा गरिरहेकोछु। मलाई नदीहरूबाट, चोरहरूबाट, र मेरा आफ्नै मानिसहरूबाट र अयहूदीहरूको खतरा थियो। मलाई शहरमा, निर्जन ठाउँमा र समुद्रमा खतरा थियो। म आडम्बरी भाइहरूदेखि पनि खतरामा थिएँ।",
			"27": "<sup>27</sup> मैले कठोर र अथक परिश्रम कति रात नसुती गरेकोछु। म भोजन औ पानी बिनै बस्नु पर्थ्यो। धेरै जाडोमा काम्दै बस्नु पर्थ्यो।",
			"28": "<sup>28</sup> अनि अरू धेरै समस्याहरू थिए। यी सब बाहेक पनि मण्डलीहरूको लागि मैले हेरचाह गर्नु पर्थ्यो। प्रत्येक दिन तिनीहरूको विषयमा चिन्ता गर्छु।",
			"29": "<sup>29</sup> कसैलाई कमजोर देख्दा आफूलाई कमजोर भए जस्तो लाग्थ्यो। र कोही पापमा अघि सर्दा मेरो अन्तस्कारण पिरोलिन्थ्यो।",
			"30": "<sup>30</sup> यदि मैले गर्व के कुरा गर्नेपर्छ भने म ती कुराहरू गर्छु जुन मेरा दुर्बलताहरू हुन्।",
			"31": "<sup>31</sup> परमेश्वर जान्दछन् म असत्य बोलिरहेको छैन। उहाँ परमेश्वरहुनुहुन्छ र प्रभु येशू ख्रीष्टका पिता हुनु हुन्छ जो सदा धन्यवादका पात्र हुनुहुन्छ।",
			"32": "<sup>32</sup> जब म दमीशकमा थिएँ, राजा अरितसका राज्यपालले शहरका चारैतिर पालेहरू राखेर मलाई पक्रन खोजे।",
			"33": "<sup>33</sup> तर केही शाथीहरूले मलाई एउटा टोकरीमा हालेर पर्खालको दूलोबाट तल झारिदिए र म त्यस राज्यपालको हातबाट फुत्कें।"
		},
		"12": {
			"1": "<sup>1</sup> पावलको जीवनमा विशेष आशिष\nअब मैले गर्व गर्नै पर्छ, यसैले केही फाइदा हुने त होइन। तर म प्रभुको दर्शनहरू र सत्य प्रकाशहरू बारे बोली नै रहनेछु।",
			"2": "<sup>2</sup> म ख्रीष्ट विश्वासी एक जना यस्तो मानिसलाई चिन्छु जसलाई तेस्रो स्वर्गमा सोझै उचालि लगेको थियो। यो चौध र्वष अघिको कुरो हो। त्यस मानिसलाई शरीरसितै वा शरीर बिना लगिएको थियो त्यो त म जान्दिनँ तर परमेश्वर जान्नुहुन्छ।",
			"3": "<sup>3</sup> म यति नै जान्दछु त्यस मानिसलाई स्वर्गमा लगियो शरीरसितै अथवा शरीर बिना, म जान्दिनँ। तर उसले त्यहाँ जुन कुरा सुन्यो त्यो शब्दमा वर्णन गर्न सकिँदैन। उसले जे सुन्यो त्यो बताउनु कुनै मनुष्यलाई अनुमति छैन।",
			"5": "<sup>5</sup> म त्यसतो मानिसको गर्व गर्नसक्छु। तर म आफ्नै बारेमा गर्व गर्दिनँ। आफ्नो त दुर्बलताहरूकै मात्र गर्व गर्नेछु।",
			"6": "<sup>6</sup> यदि मैले आफ्नो बारेमा गर्व गरिन भने म मूर्ख हुने छैन, मलाई मूर्ख भन्न सक्तैन कारण म सत्य बोल्छु। तर म आफ्नो बारेमा गर्व गर्दिनँ। किनकि म चाहँदिन कि तिनीहरूले मैले गरेको देख्छन् अथवा भनेको सुन्छन् त्यस भन्दा ज्यादा मेरो बारेमा सोचोस्।",
			"7": "<sup>7</sup> मैले देखेका आश्चर्यजनक कुराहरूलाई लिएर म गर्वित हुँदिन। मलाई एउटा दुःखको समस्या दिएको थियो। त्यो समस्या शैतानबाट आउने एउटा दूत हो त्यो मलाई पिट्न र घमण्ड गर्नु बाट रोक्न पठाइएको थियो।",
			"8": "<sup>8</sup> यस समस्याबाट बाँच्नलाई मैले प्रभुसित तीन पटक बिन्ती गरें।",
			"9": "<sup>9</sup> तर प्रभूले मलाई भने “मेरो अनुग्रह नै तिम्रो लागि प्रशस्त छ। दुर्वलतामा नै मेरो शक्ति सिद्ध हुन्छ।” यसैले म आफ्नो दुर्बलातको गर्व खुशीसित गर्छु। तब ख्रीष्टको शक्ति ममाथि रहनेछ।",
			"10": "<sup>10</sup> यसैले ख्रीष्टको निम्ति आफ्नो दुर्वलताहरूमा म खुशी छु। यदि कसैले मेरो निन्दा गरे पनि, जब म अप्ठेरो अवस्थामा पर्छु, सताइन्छु औ कष्टमा पर्छु, म खुशी हुन्छु। यी सब ख्रीष्टका लागि हुन्। अनि म यी सहमस्याहरूमा प्रसन्न छु। जब म दुर्वल छु, तब त्यही बेला म वास्तवमा पाउछु र बलवन्त हुन्छु।",
			"11": "<sup>11</sup> कोरिन्थिका ख्रीष्टयन प्रति पावलको प्रेम\nम मूर्ख जस्तो बोल्दै छु। तर यसो गर्न मलाई तिमीहरूले नै लगायौ। तिमीहरूले मेरो विषयमा राम्रो भन्नु पर्छ। म केही पनि होइन, तर ती “महान् प्रेरितहरू” कुनै खण्डमा पनि म भन्दा श्रेष्ट होइनन्।",
			"12": "<sup>12</sup> जब म तिमीहरूसित थिएँ मैले त्यस्तो कार्य गरें जसबाट प्रमाण भयो कि म एक प्रेरित थिएँ। मैले चिन्हहरू, आश्चर्यकर्महरू र अलौकि कार्यहरू देखाएँ। एकदमै धैर्यसित मैले ती कार्यहरू सम्पन्न गरें।",
			"13": "<sup>13</sup> कुन विषयमा तिमीहरू अन्य मण्डलीहरू भन्दा निम्न स्तरका थियौ? यसमा मात्र नत्र अन्य कुनै कुरोमा म आफैं तिमीहरूकोलागि भार भइनँ। यस विषयमा म तिमीहरू भन्दा तल भए मलाई क्षमा देऊ।",
			"14": "<sup>14</sup> तेस्रो पल्ट म तिमीहरूकहाँ आउन तयार छु। म तिमीहरूको भार हुने छैन। तिमीहरूसित भएको कुनै कुरोमा मेरो कुनै चासो छैन। मेरो चासो केवल तिमीहरू मात्र हौ। आफ्नो आमा बाबुहरूकोलागि जोगाड गर्नु केटा-केटीहरूको कर्त्तव्य होइन तर आमा-बाबुले नानीहरूको निम्ति जगेरा गर्नु पर्छ।",
			"15": "<sup>15</sup> यसकारण मसँग भएको सबै तिमीहरूलाई दिन म खुशी छु। तिमीहरूका निम्ति म आफैंलाई पनि सुम्पन्छु। यदि मैले तिमीहरूलाई अत्यन्तै माया गरें भने के तिमीहरूले मलाई कम्ती माया गर्छौ?",
			"16": "<sup>16</sup> यो प्रष्ट छ कि म तिमीहरूको भार भइदिएको छैन। तर तिमीहरू भन्न सक्छौ कि म यति चलाख कि तिमीहरूलाई फँसाउन मैले धूर्त्यंईं प्रयोग गरें।",
			"17": "<sup>17</sup> तिमीहरूकहाँ पठाइएको ती मानिसहरूद्वारा मैले तिमीहरूबाट शोषण गरें? तिमीहरू जान्दछौ, मैले तिमीहरूलाई शोषण गरिन।",
			"18": "<sup>18</sup> मैले तितसलाई तिमीहरूकहाँ पठाएँ अनि उनीसित हाम्रा भाइ पठाएँ। तीतसले तिमीहरूबाट लाभ उठाएनन्। उनले उठाए? अँहँ तिमीहरू जान्दछौ तितस र मैले यी कुराहरू एउटै मनसायले गर्यौं।",
			"19": "<sup>19</sup> के यतिन्जेल हामीले यी सब आफ्नै बचाउको लागि भन्दैथ्यौं? होइन। म परमेश्वर अघि अनि प्रभुको अघि भन्छु। तिमीहरू हाम्रा प्रिय मित्रहरू हौ। अनि जति हामी गर्छौं, तिमीहरूलाई दरिलो बनाउन गर्छौं।",
			"20": "<sup>20</sup> म यसो गर्छु किन कि मलाई डर छ, म तिमीहरूकहाँ आउँदा म जस्तो चाहन्छु त्यस्तो नपाउँला। मलाई डर छ कि तिमीहरू माझ तर्क, डाह, रिस, झगड़ा, ठूल्ठूला कुराहरू, दुष्ट कुरा, घमण्ड र गडबडी होला",
			"21": "<sup>21</sup> मलाई डर छ, म तिमीहरूकहाँ फेरि आउँदा मेरो परमेश्वले मलाई तिमीहरूका सामु लन्जित पार्नु हुनेछ । धेरै जना ज-जसले पहिले पाप गरेका थिए तर आफ्नो अनैतिकता, यौन पाप अनि लाजमर्दो कर्मको निम्ति पछुतो गर्न, आफ्नो हृदय परिवर्त्तन गरेका छैनन् र मैले तिमीहरूको लागि शोक गर्नु पर्ला।"
		},
		"13": {
			"1": "<sup>1</sup> अन्तिम चेतावनी र शुभकामना\nम तिमीहरूकहाँ फेरि आउने छु। त्यो तेस्रो पटक हुनेछ। अनि यादगर, “हरेक विवरणको लागि दुइ अथवा तीन जना सम्म मानिसहरूले प्रमाण गर्नु पर्छ।”",
			"2": "<sup>2</sup> जब म तिमीहरूसित दोस्रो पल्ट थिएँ, जसले पापहरू गरेका थिए तिनीहरूलाई मैले चेताउनी दिएको थिएँ। अहिले म तिमीहरूदेखि टाढा छु, तिनीहरूलाई र अरू रहेकाहरूलाई म चेतावनी दिन्छु। जब म फेरि तिमीहरूकहाँ आउँछु तिमीहरूको पापको लागि म दण्ड दिनेछु।",
			"3": "<sup>3</sup> ख्रीष्ट मबाट बोल्दैछन् भन्ने प्रमाण चाहन्छौ भने मेरो प्रमाण छ कि ख्रीष्ट तिमीहरूलाई दण्ड दिन दुर्वल हुनुहुन्छ। वास्तवमा उहाँ तिमीहरूको बीचमा बलियो हुनुहुन्छ",
			"4": "<sup>4</sup> यो साँच्चो हो कि ख्रीष्ट दुर्बल हुनु हुन्थ्यो। जति बेला क्रूसमा टाँगिए। तर यस घड़ी उहाँ परमेश्वरको शक्तिले जीवित हुनु हुन्छ। अनि यो पनि साँच्चो हो कि हामी ख्रीष्टमा दुर्बल छौं। तर तिमीहरूकालागि परमेश्वरको शक्तिले हामी ख्रीष्टसित रहनेछौं।",
			"5": "<sup>5</sup> तिमी आफैंलाई नियालेर हेर। तिमीहरू विश्वासमा बाँचेकाछौ कि छैनौ, जाँच गर। येशू ख्रीष्ट तिमीहरूमा छन् भन्ने कुरो जान्दछौ। तर त्यो जाँचमा सफल भएनौ भने ख्रीष्ट तिमीहरूका अन्तरमा हुनु हुन्न।",
			"6": "<sup>6</sup> तर म आशा गर्छु, तिमीहरूले जान्ने छौ कि हामी यस जाँचमा विफल बनेका छैनौं।",
			"7": "<sup>7</sup> परमेश्वरलाई हाम्रो प्रार्थना छ, तिमीहरूले कुनै नराम्रो काम गर्ने छैनौ। हामी जाँचमा सफल बनेको अरू मानिसहरूले देखुन् भन्ने कुरो हाम्रा होइन। तर ठूलो कुरो त यो छ कि यदि परीक्षमा हामी आफैं असफल भएको मानिसहरूले सोचे पनि जुन असल हो तिमीहरू त्यही काम गर।",
			"8": "<sup>8</sup> सत्यको विरूद्ध हामी केही गर्न सक्दैनौं। हामी सत्यका निम्ति जे छ त्यो गर्न मात्र सक्छौं।",
			"9": "<sup>9</sup> तिमीहरू दरिलो बन्यौ भने हामी दुर्वल हुनुमा खुशी छौं। हाम्रो प्रर्थाना छ तिमीहरू दिरिलो बन्दै जानेछौ।",
			"10": "<sup>10</sup> तिमीहरूसित नहुँदा म यी कुराहरू लेख्दैछु जसमा कि आउँदा खेरि मैले मेरो अधिकार कड़ासँग प्रयोग गर्न नपरोस्। तिमीहरूलाई विनाश गर्ने होइन तर बलियो बनाउने त्यो शक्ति प्रभुले मलाई दिनुभयो।",
			"11": "<sup>11</sup> दाज्यू-भाइ दिदी-बहिनीहरू! अब म विदा लिन्छु। परिपक्क हुने कोशिश गर। मैले तिमीहरूलाई भनेको कुरा गर। एक अर्कासित मिल र शान्तिसित बस। तब प्रेम र शान्तिका परमेश्वर तिमीहरूसित हुनुहुनेछ।",
			"12": "<sup>12</sup> एक अर्काको अभिवादन पवित्र चुम्बनले गर। परमेश्वरका सबै पवित्र जनहरूले तिमीहरूलाई अभिवादन पठाएका छन्।",
			"13": "<sup>13</sup> प्रभु येशू ख्रीष्टको अनुग्रह परमेश्वरको प्रेम अनि पवित्र आत्माको सहभागिता तिमीहरूमा सधैँ होस्।"
		}
	}
};
module.exports = book;