var book = {
	"name": "I Dikronike",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Adama, Setha, Enose,",
			"2": "<sup>2</sup> Kenane, Mahalaleele, Jerede,",
			"3": "<sup>3</sup> Henoke, Methusela, Lemeke,",
			"4": "<sup>4</sup> Nowe, Sema, Kama, le Jafeta.",
			"5": "<sup>5</sup> Bara ba Jafeta ke Gomere, Magogo, Madai, Javane, Tubale, Mesheke, le Tirase.",
			"6": "<sup>6</sup> Bara ba Gomere ke Ashkenase, Difate, le Togarma;",
			"7": "<sup>7</sup> bara ba Javane ke Elisha, Tarshishe, Kittime, le Rodanime.",
			"8": "<sup>8</sup> Bara ba Kama ke Kushe, Mitsraime, Puthe le Kanana.",
			"9": "<sup>9</sup> Bara ba Kushe ke Saba, Havila, Sabta, Raema, le Sabteka. Bara ba Raema ke Seba le Dedane.",
			"10": "<sup>10</sup> Kushe a tswala Nimrode, ya eba senatla lefatsheng.",
			"11": "<sup>11</sup> Mitsraime a tswala Baludime, Baanamime, Balehabime, le Banaftuhime,",
			"12": "<sup>12</sup> Bapatrusime, Bakasluhime bao Bafilesta ba tswileng ho bona, le Bakaftorime.",
			"13": "<sup>13</sup> Kanana a tswala Sidone, letsibolo la hae, Hethe,",
			"14": "<sup>14</sup> Bajebuse, Baamore, Bagirgashe,",
			"15": "<sup>15</sup> Baheve, Baarke, Basine,",
			"16": "<sup>16</sup> Baarvade, Batsemare, le Bahamathe.",
			"17": "<sup>17</sup> Bara ba Sema ke Elame, Assure, Arpaksade, Lude, Arame, Utse, Hule, Getere, le Mesheke.",
			"18": "<sup>18</sup> Arpaksade a tswala Shela, Shela a tswala Hebere.",
			"19": "<sup>19</sup> Hebere a tswallwa bara ba babedi; lebitso la e mong ke Pelege (karolo), hobane lefatshe le ne le arolwe tshiung tsa hae; lebitso la ngwanabo ke Jokthane.",
			"20": "<sup>20</sup> Jokthane a tswala Almodade, Shelefe, Hatsaremavethe, Jerake,",
			"21": "<sup>21</sup> Hadorame, Usale, Dikla,",
			"22": "<sup>22</sup> Ebale, Abimaele, Seba,",
			"23": "<sup>23</sup> Ofire, Havila, le Jobabe. Bao kaofela ke bara ba Jokthane.",
			"24": "<sup>24</sup> Sema, Arpaksade, Shela,",
			"25": "<sup>25</sup> Hebere, Pelege, Rehu,",
			"26": "<sup>26</sup> Seruge, Nakore, Tera,",
			"27": "<sup>27</sup> Abrame, e leng yena Abrahama.",
			"28": "<sup>28</sup> Bara ba Abrahama ke Isaaka le Ishmaele.",
			"29": "<sup>29</sup> Masika a bona ke ana: Letsibolo la Ishmaele ke Nebajothe; yaba ke Kedare, Adbeele, Mibsame,",
			"30": "<sup>30</sup> Mishma, Duma, Masa, Hadade, le Thema,",
			"31": "<sup>31</sup> Jethure, Nafishe, le Kedma. Bao ke bara ba Ishmaele.",
			"32": "<sup>32</sup> Bara ba Ketura, serethe sa Abrahama: a tswala Simrane, Jokshane, Medane, Midiane, Jishbake, le Shua. Bara ba Jokshane ke Seba le Dedane.",
			"33": "<sup>33</sup> Bara ba Midiane ke Efa, Efere, Henoke, Abida, le Eldaa. Bao kaofela ke bara ba Ketura.",
			"34": "<sup>34</sup> Abrahama yena a tswala Isaaka. Bara ba Isaaka ke Esau le Iseraele.",
			"35": "<sup>35</sup> Bara ba Esau ke Elifase, Reuele, Jeushe, Jaelame, le Kore.",
			"36": "<sup>36</sup> Bara ba Elifase ke Themane, Omare, Tsefi, Gaethame, Kenase, Thimna, le Amaleke.",
			"37": "<sup>37</sup> Bara ba Reuele ke Nahathe, Sera, Shamma, le Missa.",
			"38": "<sup>38</sup> Bara ba Seire ke Lothane, Shobale, Tsibeone, Ana, Dishone, Etsere, le Dishane.",
			"39": "<sup>39</sup> Bara ba Lothane ke Hori le Homame; kgaitsedi ya Lothane ke Thimna.",
			"40": "<sup>40</sup> Bara ba Shobale ke Aljane, Manahathe, Ebale, Shefi, le Oname. Bara ba Tsibeone ke Aja le Ana.",
			"41": "<sup>41</sup> Mora wa Ana ke Dishone; bara ba Dishone ke Hamrane, le Eshbane, le Jithrane, le Kerane.",
			"42": "<sup>42</sup> Bara ba Etsere ke Bilhane, Saavane, le Jaakane. Bara ba Dishane ke Utse le Arane.",
			"43": "<sup>43</sup> Marena a neng a buse naheng ya Edomo, ho eso ho be morena ya busang bana ba Iseraele, ke ana: Ke Bela, mora Beore; lebitso la motse wa hae e ne e le Dinhaba.",
			"44": "<sup>44</sup> Eitse hobane Bela a timele, ha busa Jobabe, mora Sera wa Botsra, tulong tsa hae.",
			"45": "<sup>45</sup> Jobabe yena mohla a timelang, ha busa Hushame wa naha ya Bathemane tulong tsa hae.",
			"46": "<sup>46</sup> Eitse hobane Hushame a timele, ha busa tulong tsa hae Hadade, mora Bedade, ya neng a hlole Bamidiane naheng ya Moabe; lebitso la motse wa hae e ne e le Avithe.",
			"47": "<sup>47</sup> Mohla Hadade a timelang, ha busa tulong tsa hae Samla wa Masreka.",
			"48": "<sup>48</sup> Mohla Samla a timelang, ha busa tulong tsa hae Saule wa Rehobothe o pela noka.",
			"49": "<sup>49</sup> Eitse hobane Saule a timele, ha busa tulong tsa hae Baale-Hanane, mora Akbore.",
			"50": "<sup>50</sup> Eitse hobane Baale-Hanane a timele, ha busa Hadade tulong tsa hae; lebitso la motse wa hae e ne e le Pai; lebitso la mosadi wa hae e ne e le Mehethabeele, moradi wa Mathrede, moradi wa Mesahabe.",
			"51": "<sup>51</sup> Hadade a timela. Mahosana a Edomo ke ana: ke kgosana Thimna, le kgosana Alia, le kgosana Jethethe,",
			"52": "<sup>52</sup> le kgosana Oholibama, le kgosana Ela, le kgosana Pinone,",
			"53": "<sup>53</sup> le kgosana Kenase, le kgosana Themane, le kgosana Mibtsare,",
			"54": "<sup>54</sup> le kgosana Magdiele, le kgosana Irame. Bao ke mahosana a Edomo."
		},
		"2": {
			"1": "<sup>1</sup> Bana ke bara ba Iseraele: Ke Rubene, Simeone, Levi, Juda, Issaskare, Sabulone,",
			"2": "<sup>2</sup> Dane, Josefa, Benjamine, Nefthali, Gade le Asere.",
			"3": "<sup>3</sup> Bara ba Juda ke Ere, Onane, le Shela; bao ba bararo a tswallwa bona ke moradi wa Shua, Mokanana. Ere, letsibolo la Juda, ya eba motho e mobe pela Jehova; yaba Jehova o a mmolaya.",
			"4": "<sup>4</sup> Tamare, ngwetsi ya hae, a mo tswalla Peretse le Sera. Bara ba Juda, ha ba le kaofela, ba bahlano.",
			"5": "<sup>5</sup> Bara ba Peretse ke Hetsrone le Hamule.",
			"6": "<sup>6</sup> Bara ba Sera ke Simri, Ethane, Hemane, Kalkole, le Dara; kaofela ha bona ba bahlano.",
			"7": "<sup>7</sup> Mora wa Karmi ke Akare, moferekanyi wa Iseraele ya kileng a tlola molao wa ntho tse neetsweng anathema.",
			"8": "<sup>8</sup> Mora wa Ethane ke Asaria.",
			"9": "<sup>9</sup> Haele bara ba Hetsrone, bao a ba tswaletsweng, ke Jerameele, Rame le Kelubai.",
			"10": "<sup>10</sup> Rame yena a tswala Amminadabe; Amminadabe a tswala Nashone, kgosana ya bana ba Juda.",
			"11": "<sup>11</sup> Nashone yena a tswala Salma, Salma a tswala Boase.",
			"12": "<sup>12</sup> Boase a tswala Obede, Obede a tswala Jese.",
			"13": "<sup>13</sup> Jese a tswala Eliabe letsibolo la hae, le Abinadabe moenae; wa boraro ke Shimea,",
			"14": "<sup>14</sup> wa bone ke Nethaneele, wa bohlano ke Raddai,",
			"15": "<sup>15</sup> wa botshelela ke Otseme, wa bosupa ke Davida;",
			"16": "<sup>16</sup> dikgaitsedi tsa bona ke Tseruja le Abigaile. Bara ba Tseruja ke Abishai, Joabe, le Asaele; bona ba bararo.",
			"17": "<sup>17</sup> Abigaile a tswala Amasa; ntata Amasa ke Jethere, Moishmaele.",
			"18": "<sup>18</sup> Haele Kalebe, mora Hetsrone, a tswala bana ka mosadi wa hae Asuba le ka Jeriothe; bara ba hae ka Asuba ke Jeshere, Shobabe, le Ardone.",
			"19": "<sup>19</sup> Eitse hobane Asuba a timele, Kalebe a inkela Efrathe; eo a mo tswalla Hure,",
			"20": "<sup>20</sup> Hure a tswala Uri, Uri a tswala Betsaleele.",
			"21": "<sup>21</sup> Hamorao, Hetsrone a kena ho moradi wa Makire, ntata Gileade, a mo nyala a le dilemo tse mashome a tsheletseng; eo a mo tswalla Segube.",
			"22": "<sup>22</sup> Segube a tswala Jaire, ya ileng a ba le metse e mashome a mabedi a metso e meraro naheng ya Gileade.",
			"23": "<sup>23</sup> Bageshure le Baarame ba ba amoha metse ya Jaire, le Kenathe ka metsana ya teng, e le metse e mashome a tsheletseng. Metse eo kaofela e ne e le ya bara ba Makire, ntata Gileade.",
			"24": "<sup>24</sup> Eitse hobane Hetsrone a timele Kaleb-Efratha, Abija, mosadi wa Hetsrone, a boela a mo tswalla Ashure, ntata Tekowa.",
			"25": "<sup>25</sup> Bara ba Jerameele, letsibolo la Hetsrone, letsibolo la hae ke Rame; ba bang ke Buna, Orene, Otseme, le Akija.",
			"26": "<sup>26</sup> Jerameele o ne a na le mosadi e mong, bitso la hae e le Athara; ke yena mma Oname.",
			"27": "<sup>27</sup> Bara ba Rame, letsibolo la Jerameele, ke Maatse, Jamine, le Ekere.",
			"28": "<sup>28</sup> Bara ba Oname ke Shammai le Jada. Bara ba Shammai ke Nadabe le Abishure.",
			"29": "<sup>29</sup> Lebitso la mosadi wa Abishure e ne e le Abikaile; a mo tswalla Akbane le Molede.",
			"30": "<sup>30</sup> Bara ba Nadabe ke Selede le Appaime; Selede yena a timela a se na bana.",
			"31": "<sup>31</sup> Mora wa Appaime ke Jishei. Mora wa Jishei ke Sheshane. Mora wa Sheshane ke Aklai.",
			"32": "<sup>32</sup> Bara ba Jada, moena Shammai, ke Jethere le Jonathane; empa Jethere a timela a se na bana.",
			"33": "<sup>33</sup> Bara ba Jonathane ke Pelethe le Sasa. Bao ke bara ba Jerameele.",
			"34": "<sup>34</sup> Haele Sheshane, o ne a se na bara, a ena le baradi feela. Sheshane o ne a na le mohlanka wa Moegepeta, eo bitso la hae e neng e le Jarha.",
			"35": "<sup>35</sup> Sheshane a nea mohlanka wa hae moradi wa hae, e be mosadi wa hae; mme eo a mo tswalla Attai.",
			"36": "<sup>36</sup> Attai a tswala Nathane, Nathane a tswala Sabade,",
			"37": "<sup>37</sup> Sabade a tswala Eflale, Eflale a tswala Obede,",
			"38": "<sup>38</sup> Obede a tswala Jehu, Jehu a tswala Asaria,",
			"39": "<sup>39</sup> Asaria a tswala Haletse, Haletse a tswala Elasa,",
			"40": "<sup>40</sup> Elasa a tswala Sismai, Sismai a tswala Shallume,",
			"41": "<sup>41</sup> Shaume a tswala Jekamia, Jekamia a tswala Elishama.",
			"42": "<sup>42</sup> Bara ba Kalebe, moena Jerameele, ke Mesha, letsibolo la hae; ke yena ntata Sife; mora wa hae ke Maresha, ntata Hebrone.",
			"43": "<sup>43</sup> Bara ba Hebrone ke Kora, Tappua, Rekeme, le Shema.",
			"44": "<sup>44</sup> Shema a tswala Rahame, ntata Jorkeame; Rekeme yena a tswala Shammai.",
			"45": "<sup>45</sup> Mora Shammai ke Maone; Maone ke ntata Bethe-Tsure.",
			"46": "<sup>46</sup> Efa, serethe sa Kalebe, a tswala Harane, Motsa, le Gasese; Harane a tswala Gasese.",
			"47": "<sup>47</sup> Bara ba Jadai ke Regeme, Jothame, Geshane, Pelethe, Efa, le Shaafe.",
			"48": "<sup>48</sup> Maaka, serethe sa Kalebe, a tswala Shebere le Tirhana.",
			"49": "<sup>49</sup> A tswala le Shaafe ntata Madmanna, Sheva ntata Makbena le Gibea; moradi wa Kalebe e ne e le Aksa.",
			"50": "<sup>50</sup> Bara ba Kalebe, mora Hure, letsibolo la Efrata, ke bana: ke Shobale ntata Kiryathe-Jearime,",
			"51": "<sup>51</sup> Salma ntata Bethlehema, Harefe, ntata Bethe-Gadere.",
			"52": "<sup>52</sup> Shobale, ntata Kiryathe-Jearime, o ne a na le bara: ke Harowe le Hatsi-Hammenuhothe.",
			"53": "<sup>53</sup> Masika a Kiryathe-Jearime ke Bajethre, Baputhe, Bashumathe, le Bamishrae; ho bona ho tswile Batsoreathe le Baeshtaole.",
			"54": "<sup>54</sup> Bara ba Salma ke Bethlehema, Banethofathe, Athrothe-Bethe-Joabe, le mothapo o mong wa Bahammanahthi, le Batsore.",
			"55": "<sup>55</sup> Haele masika a bangodi ba neng ba ahile Jabetse, ke Bathireathe, Bashimeathe, le Basukathe. Ke bona Bakene ba tswileng ho Hamathe hlooho ya ntlo ya Rekabe."
		},
		"3": {
			"1": "<sup>1</sup> Bara ba Davida, bao a ba tswaletsweng a le Hebrone ke bana: Wa letsibolo ke Amnone wa Akinoame wa Jisreele; wa bobedi ke Daniele wa Abigaile wa Karmele;",
			"2": "<sup>2</sup> wa boraro ke Absalome, mora Maaka, moradi wa Talmai, morena wa Geshure; wa bone ke Adonija, mora Haggithe;",
			"3": "<sup>3</sup> wa bohlano ke Shefatia wa Abithale; wa botshelela ke Jithreame, ka Egla, mosadi wa hae.",
			"4": "<sup>4</sup> Bao ba tsheletseng a tswallwa bona a le Hebrone; ke hona moo a kileng a busa lemo tse supileng tse nang le kgwedi tse tsheletseng; mme Jerusalema, a boela a busa lemo tse mashome a mararo a metso e meraro.",
			"5": "<sup>5</sup> Bao a ba tswaletsweng a le Jerusalema ke bana: ke Shimea, Shobabe, Nathane, le Salomone, ba bane ke ba Bathe-Shua, moradi wa Ammiele;",
			"6": "<sup>6</sup> hape, ke Jibhare, Elishama, Elifelethe,",
			"7": "<sup>7</sup> Noga, Nefege, Jafia,",
			"8": "<sup>8</sup> Elishama, Eliada, le Elifelethe; bao ba robile mono o le mong.",
			"9": "<sup>9</sup> Bao kaofela e ne e le bara ba Davida, ho sa balwe le bara ba hae ba direthe. Kgaitsedi ya bona e ne e le Tamare.",
			"10": "<sup>10</sup> Mora Salomone ke Roboame, mora wa hae ke Abija, mora wa hae ke Asa, mora wa hae ke Josafate,",
			"11": "<sup>11</sup> mora wa hae ke Jorame, mora wa hae ke Akasia, mora wa wa hae ke Joase,",
			"12": "<sup>12</sup> mora wa hae ke Amatsia, mora wa hae ke Asaria, mora wa hae ke Jothame,",
			"13": "<sup>13</sup> mora wa hae ke Akase, mora wa hae ke Esekiase, mora wa hae ke Manasse,",
			"14": "<sup>14</sup> mora wa hae ke Amone, mora wa hae ke Josiase.",
			"15": "<sup>15</sup> Bara ba Josiase ke Jokanane, wa letsibolo; wa bobedi ke Jojakime, wa boraro ke Tsedekia, wa bone ke Shallume.",
			"16": "<sup>16</sup> Bara ba Jojakime ke mora hae Jekonia; mora hae ke Tsedekia.",
			"17": "<sup>17</sup> Bara ba Jekonia ke Assire; bara ba hae ke Salathiele,",
			"18": "<sup>18</sup> Malkirame, Pedaia, Shenatsare, Jekamia, Hoshama, le Nedabia.",
			"19": "<sup>19</sup> Bara ba Pedaia ke Sorobabele, le Shimei; bara ba Sorobabele ke Meshullame, le Hanania, le kgaitsedi ya bona Shelomithe;",
			"20": "<sup>20</sup> le Hashuba, Ohele, Berekia, Hasadia, le Jushabe-Hesede: bao ba bahlano.",
			"21": "<sup>21</sup> Bara ba Hanania ke Pelethia le Esaia; hape, ke bara ba Rafaia, le bara ba Arnane, le bara ba Obadia, le bara ba Shekania.",
			"22": "<sup>22</sup> Mora wa Shekania ke Shemaia; bara ba Shemaia ke Hattushe, Jigeale, Baria, Nearia, le Shafathe: bao ba tsheletse.",
			"23": "<sup>23</sup> Bara ba Nearia ke Eleoenai, Esekiase, le Asrikame: bao ba bararo.",
			"24": "<sup>24</sup> Bara ba Eleoenai ke Hodavia, Eliashibe, Pelaia, Akkube, Johanane, Delaia, le Anani: bao ba supile."
		},
		"4": {
			"1": "<sup>1</sup> Bara ba Juda ke Peretse, Hetsrone, Karmi, Hure, le Shobale.",
			"2": "<sup>2</sup> Reaia, mora Shobale, a tswala Jahathe; Jahathe a tswala Akumai le Lahade: ke yona meloko ya Batsoreathe.",
			"3": "<sup>3</sup> Bao e leng ba Abi-Ethame ke Jisreele, Jishma, le Jidbashe; bitso la kgaitsedi ya bona ke Hatselelponi;",
			"4": "<sup>4</sup> hape ke Penuele ntata Gedore, le Esere ntata Husha. Bao ke bara ba Hure, letsibolo la Efrata, ntata Bethlehema.",
			"5": "<sup>5</sup> Ashure, ntata Tekowa, o ne a ena le basadi ba babedi, Helea le Naara.",
			"6": "<sup>6</sup> Naara a mo tswalla Akussame, Hefere, Themeni, le Ahahashtari: bao ke bara ba Naara.",
			"7": "<sup>7</sup> Bara ba Helea ke Tserethe, Tsohare, le Ethnane.",
			"8": "<sup>8</sup> Hakotse a tswala Anube, le Hatsobeba, le lesika la Akarhele, mora Harume.",
			"9": "<sup>9</sup> Jaebetse yena o ne a hlompheha ho ena le banababo; mme mmae o ne a mo reile lebitso la Jaebetse, ka ho re: Ke mo tswetse ka bohloko.",
			"10": "<sup>10</sup> Jaebetse a bitsa Modimo wa Iseraele, a re: Oho, o ke o ntlhohonolofatse; o atise naha ya ka; letsoho la hao le nne le be ho nna; hape, ako ntshireletse bobeng, bo tle bo se ke ba nkutlwisa bohloko. Yaba Modimo o mo sitsa ka seo a se kopileng.",
			"11": "<sup>11</sup> Kelube, ngwanabo Shuha, a tswala Mehire, ntata Eshthone.",
			"12": "<sup>12</sup> Eshthone a tswala Bethe-Rafa, Pasea, le Thehinna ntata Irnahashe. Bao ke banna ba ha Reka.",
			"13": "<sup>13</sup> Bara ba Kenase ke Othniele le Seraia; mora wa Othniele ke Hathathe.",
			"14": "<sup>14</sup> Meonothai a tswala Ofra; mme Seraia a tswala Joabe, ntata Ge-Harashime; bao e ne e le malala.",
			"15": "<sup>15</sup> Bara ba Kalebe, mora Jefunne, ke Iru, Ela, le Naame, le bara ba Ela, le Kenase.",
			"16": "<sup>16</sup> Bara ba Jehalleleele ke Sife, Sifa, Thiria, le Asareele.",
			"17": "<sup>17</sup> Bara ba Esdrase ke Jethere, Merede, Efere, le Jalone; mosadi wa Merede a tswala Miriame, Shammai, le Jishba, ntata Eshthemowa.",
			"18": "<sup>18</sup> Mosadi wa hae, Jehudija, a tswala Jerede ntata Gedore, le Hebere ntata Soko, le Jekuthiele ntata Sanowa. Bao ba pele ke bara ba Bithia, moradi wa Faro, ya neng a nyetswe ke Merede.",
			"19": "<sup>19</sup> Bara ba mosadi wa Hodia, kgaitsedi ya Nahame, ke ntata Kehila Mogarme, le Eshthemowa Momaakathe.",
			"20": "<sup>20</sup> Bara ba Simone ke Amnone, Rinna, Bene-Hanane, le Thilone. Bara ba Jishei ke Sohethe le Bene-Sohethe.",
			"21": "<sup>21</sup> Bara ba Shela, mora Juda, ke Ere ntata Leka, le Laeda ntata Maresha, le masika a ba neng ba sebetsa masela a lene, e le ba ntlo ya Ashbea,",
			"22": "<sup>22</sup> le Jokime, le banna ba Koseba, le Joase, le Sarafe, ba kileng ba busa Moabe, le Jashubi-Leheme. Taba tseo ke taba tsa kgale.",
			"23": "<sup>23</sup> Bao ke dibopi; ba ne ba ahile Netaime le Gedera; ba ne ba aha hammoho le morena, ba tle ba sebetse mosebetsi wa hae.",
			"24": "<sup>24</sup> Bara ba Simeone ke Nemuele, Jamine, Jeribe, Sera, le Saule;",
			"25": "<sup>25</sup> le Shallume mora hae, le Mibsame mora hae, le Mishma mora hae.",
			"26": "<sup>26</sup> Bara ba Mishma ke mora hae Hammuele, le mora hae Sakkure, le mora hae Shimei.",
			"27": "<sup>27</sup> Shimei yena o ne a na le bara ba leshome le metso e tsheletseng le baradi ba tsheletseng; empa banababo ba ne ba se na bana ba bangata, mme masika a bona ha a ka a ata, jwaloka a bana ba Juda.",
			"28": "<sup>28</sup> Ba ya haha Beerseba, Molada, Hatsare-Shuale,",
			"29": "<sup>29</sup> Bilha, Etseme, Tholade,",
			"30": "<sup>30</sup> Bethuele, Horma Tsiklage.",
			"31": "<sup>31</sup> Bethe-Markabothe, Hatsare-Susime, Bethe-Birei, le Shaaraime. Ke yona metse ya bona ho ya fihla mehleng ya puso ya Davida.",
			"32": "<sup>32</sup> Metse ya bona ke Ethame, Aine, Rimmone, Thokene, le Ashane: ke metse e mehlano;",
			"33": "<sup>33</sup> hape ke metsana ya bona kaofela e neng e kopile metse eo, ho ya fihla Baale. Madulo a bona ke ao le lesika la bona ke leo.",
			"34": "<sup>34</sup> Haele Meshobabe, Jamleke, Joshua mora Amatsia,",
			"35": "<sup>35</sup> Joele, Jehu mora Joshibia, mora Seraia, mora Asiele,",
			"36": "<sup>36</sup> le Elioenai, Jaakoba, Jeshohaia, Asaia, Adiele, Jesimiele, Benaia,",
			"37": "<sup>37</sup> le Sisa mora Shifei, mora Allone, mora Jedaia, mora Shimri, mora Shemaia,",
			"38": "<sup>38</sup> bao ha ba thonngwa ka mabitso a bona, e ne e le mahosana melokong ya bona; mme matlo a bontata bona a nna a ata haholo.",
			"39": "<sup>39</sup> Ba ya fihla pela Gedore, ho ya fihla ka nqa ho le letshehadi ho kgohlo, hore ba batle makgulo a mehlape ya bona.",
			"40": "<sup>40</sup> Ba fihlela makgulo a nonneng, a molemo, naha ya teng e le sephara e letse, e dutse ka kgotso; ke Bakama ba neng ba ahile teng mehleng ya boholoholo.",
			"41": "<sup>41</sup> Bao ba tswang ho thonngwa ka mabitso a bona ba ne ba tlile moo mehleng ya Esekiase, morena wa Juda; ba ne ba timeditse ditente tsa bona, le Bameunime, mme ba ba neela anathema; le kajeno ho ntse ho le jwalo; ba aha tulong tsa bona; hobane teng ho ne ho na le makgulo a mehlape ya bona.",
			"42": "<sup>42</sup> Ba bang ho bona, ba bara ba Simeone, e le banna ba makgolo a mahlano, ba futuhela thaba ya Seire, ba laotswe ke Pelathia, Nearia, Refaia, le Usiele, bara ba Jishei.",
			"43": "<sup>43</sup> Ba qhala masala a Baamaleke a neng a phonyohile, mme ba aha teng ho fihla tsatsing la kajeno."
		},
		"5": {
			"1": "<sup>1</sup> Bana ke bara ba Rubene, letsibolo la Iseraele. E ne e le yena letsibolo; empa kahobane a ne a silafaditse malao a ntatae, boholo ba hae ba ho tswalwa bo ne bo neilwe bara ba Josefa, mora Iseraele; leha ho le jwalo, e ne e se hore Josefa a ngolwe mangolong a meloko ka ho re o na le boholo ba ho tswalwa.",
			"2": "<sup>2</sup> Hobane ke Juda ya ileng a feta banababo, mme ke ho yena ho tswileng morena; leha ho le jwalo, ditokelo tsa boholo ba ho tswalwa e ne e le tsa Josefa.",
			"3": "<sup>3</sup> Bara ba Rubene, letsibolo la Iseraele, ke Henoke, Pallu, Hetsrone, le Karmi.",
			"4": "<sup>4</sup> Bara ba Joele ke mora hae Shemaia, le Gogo mora hae, le Shimei mora hae,",
			"5": "<sup>5</sup> le Mikea, mora hae, le Reaia mora hae, le Baale mora hae,",
			"6": "<sup>6</sup> le Beera mora hae, eo Tilgathe-Pilnesere, morena wa Assiria, a ileng a mo isa kgolehong; e ne e le kgosana ya Barubene.",
			"7": "<sup>7</sup> Meloko ya banababo, ha ho balwa lesika la meloko ya bona, e moholo e ne e le Jeiele, le Sakaria,",
			"8": "<sup>8</sup> le Bela mora Asase, mora Shema, mora Joele, ya neng a ahile Aroere, ho ya fihla Nebo le Baale-Meone;",
			"9": "<sup>9</sup> ka nqa botjhabela, o ne a ahile naheng e fihlang moeding wa lefeella, ho tloha nokeng ya Eufrate; hobane mehlape ya bona e ne e atile haholo naheng ya Gileade.",
			"10": "<sup>10</sup> Mehleng ya Saule, ba futuhela Bahagarene, ba ba bolaya, mme ba aha ditenteng tsa bona, naheng yohle e nqa botjhabela ho Gileade.",
			"11": "<sup>11</sup> Bana ba Gade bona ba ne ba ahile mabapa le Barubene, naheng ya Bashane, ho ya kgutla Salka:",
			"12": "<sup>12</sup> e moholo ke Joele, ya mo hlahlamang ke Shafame, le Jaenai, le Shafathe naheng ea Bashane.",
			"13": "<sup>13</sup> Banababo, bana ba ntlo ya bontata bona, ke Mikaele, Meshullame, Sheba, Jorai, Jaekane, Sia, le Ebere; ba ne ba supile.",
			"14": "<sup>14</sup> Ke bao bana ba Abikaele, mora Huri, mora Jarowa, mora Gileade, mora Mikaele, mora Jeshishai, mora Jahdo, mora Buse;",
			"15": "<sup>15</sup> Aki, mora Abdiele, mora Guni, ke kgosana ya matlo a bontata bona.",
			"16": "<sup>16</sup> Ba kile ba aha naheng ya Gileade, Bashane, le metseng ya teng, le merakeng yohle ya Sarone, meeding ya yona.",
			"17": "<sup>17</sup> Kaofela ha bao ba ne ba ntse ba balwa masikeng a bona mehleng ya Jothame, morena wa Juda, le mehleng ya Jeroboame, morena wa Iseraele.",
			"18": "<sup>18</sup> Bara ba Rubene, le Bagade, le ba mothapo o mong wa Manasse, bao e neng e le bahale, banna ba hlomelang thebe le sabole, ba fulang ka seqha, ba tsebang ntwa, e ne e le dikete tse mashome a mane a metso e mene tse nang le makgolo a supileng le mashome a tsheletseng, ba neng ba ka nna ba futuha.",
			"19": "<sup>19</sup> Ba hla ba futuhela Bahagarene, le Jethure, le Nafishe, le Nodabe.",
			"20": "<sup>20</sup> Ba ya ba tlatsitswe, mme Bahagarene ba neelwa matsohong a bona, le bohle bao ba neng ba na le bona; hobane bao ba ne ba howeleditse ho Modimo ba le ntweng, mme wa ba utlwa, ke hobane ba ne ba beile tshepo ya bona ho wona.",
			"21": "<sup>21</sup> Ba hapa le makgomo a bona, dikamele tse dikete tse mashome a mahlano, le dinku tse makgolo a mabedi a mashome a mahlano a dikete, le diesele tse kete tse pedi, ba bolaya le batho ba lekgolo la dikete.",
			"22": "<sup>22</sup> Ha fela ha bolawa ba bangata, hobane ntwa e ne e le ya Modimo. Mme ba aha tulong tsa bona ho fihlela mohla kgoleho.",
			"23": "<sup>23</sup> Le bona bana ba mothapo o mong wa Manasse ba aha naheng eo; ba aha ba tletse ho tloha Bashane ho ya Baale-Hermone, le Senire, le thabeng ya Hermone.",
			"24": "<sup>24</sup> Dihlooho tsa matlo a bontata bona ke Efere, le Jishei, le Eliele, le Asriele, le Jeremia, le Hodavia, le Jahdiele, e neng e le bahale, banna ba tumileng, dihlooho tsa matlo a bontata bona.",
			"25": "<sup>25</sup> Ba kwenehela Modimo wa bontata bona, mme ba latela medimo ya ditjhaba tsa naha eo, tseo Modimo o neng o di timeditse pela bona.",
			"26": "<sup>26</sup> Yaba moo Modimo wa Iseraele o susumetsang pelo ya Pule, morena wa Assiria, le pelo ya Tilgathe-Pilnesere, morena wa Assiria, mme a tlosa Barubene, le Bagade, le ba mothapo o mong wa Manasse, a ba isa Hala, le Habore, le Hara, le nokeng ya Gosane, ho fihlela kajeno."
		},
		"6": {
			"1": "<sup>1</sup> Bara ba Levi ke Gershome, Kehathe, le Merari.",
			"2": "<sup>2</sup> Bara ba Kehathe ke Amrame, Jitsehare, Hebrone, le Usiele. Bana ba Amrame ke Aarone, Moshe, le Miriame.",
			"3": "<sup>3</sup> Bara ba Aarone ke Nadabe, Abihu, Eleasare, le Ithamare.",
			"4": "<sup>4</sup> Eleasare a tswala Finease, Finease a tswala Abishua,",
			"5": "<sup>5</sup> Abishua a tswala Bukki, Bukki a tswala Ussi,",
			"6": "<sup>6</sup> Ussi a tswala Serahia, Serahia a tswala Merajothe,",
			"7": "<sup>7</sup> Merajothe a tswala Amaria, Amaria a tswala Akitube,",
			"8": "<sup>8</sup> Akitube a tswala Tsadoke, Tsadoke a tswala Akimaatse,",
			"9": "<sup>9</sup> Akimaatse a tswala Asaria, Asaria a tswala Johanane,",
			"10": "<sup>10</sup> Johanane a tswala Asaria, ya kileng a tshwara tshebetso ya boprista tempeleng e hahilweng Jerusalema ke Salomone;",
			"11": "<sup>11</sup> Asaria a tswala Amaria, Amaria a tswala Akitube,",
			"12": "<sup>12</sup> Akitube a tswala Tsadoke, Tsadoke a tswala Shallume,",
			"13": "<sup>13</sup> Shallume a tswala Hilkia, Hilkia a tswala Asaria,",
			"14": "<sup>14</sup> Asaria a tswala Seraia, Seraia a tswala Jehotsadake.",
			"15": "<sup>15</sup> Ke eo Jehotsadake ya ileng kgolehong, mohla Jehova a tlosang Juda le ba Jerusalema ka letsoho la Nebukadnesare.",
			"16": "<sup>16</sup> Bara ba Levi ke Gershome, Kehathe, le Merari.",
			"17": "<sup>17</sup> Mabitso a bara ba Gershome ke Libni le Shimei.",
			"18": "<sup>18</sup> Bara ba Kehathe ke Amrame, Jitsehare, Hebrone, le Usiele.",
			"19": "<sup>19</sup> Bara ba Merari ke Mahali le Mushi. Ke eo meloko ya Balevi ka ho ya ka bontata bona.",
			"20": "<sup>20</sup> Bara ba Gershome ke Libni, mora wa hae, le Jahathe, mora wa hae, le Simma, mora wa hae,",
			"21": "<sup>21</sup> le Jowa, mora wa hae, le Iddo, mora wa hae, le Sera, mora wa hae, le Jeathrai, mora wa hae.",
			"22": "<sup>22</sup> Bara ba Kehathe ke Amminadabe, mora wa hae, le Kore, mora wa hae, le Assire, mora wa hae,",
			"23": "<sup>23</sup> le Elkana, mora wa hae, le Ebiasafe, mora wa hae, le Assire, mora wa hae,",
			"24": "<sup>24</sup> le Thahathe, mora wa hae, le Uriele, mora wa hae, le Osiase, mora wa hae, le Saule, mora wa hae.",
			"25": "<sup>25</sup> Bara ba Elkana ke Amasai le Akimothe.",
			"26": "<sup>26</sup> Haele Elkana, mora wa Elkana ke Tsofai, mora wa hae, Nahathe, mora wa hae,",
			"27": "<sup>27</sup> Eliabe, mora wa hae, le Jerohame, mora wa hae, le Elkana, mora wa hae.",
			"28": "<sup>28</sup> Bara ba Samuele, ke Joele, letsibolo la hae, wa bobedi ke Abia.",
			"29": "<sup>29</sup> Bara ba Merari ke Mahali, le Libni, mora wa hae, le Shimei, mora wa hae, le Ussa, mora wa hae,",
			"30": "<sup>30</sup> le Shimea, mora wa hae, le Haggia, mora wa hae, le Asaia mora wa hae.",
			"31": "<sup>31</sup> Ba kileng ba bewa ke Davida ho tsamaisa tshebetso ya dipina tlung ya Jehova, mohla areka e boneng phomolo, ke bana:",
			"32": "<sup>32</sup> ba nna ba etsa tshebetso eo ka pina pela moo tabernakele ya tente ya pokano e hlomilweng teng, ho fihlela ha Salomone a haha ntlo ya Jehova Jerusalema; teng, ba boela ba tshwara tshebetso ya bona ka lenaneo la bona la ho fapana.",
			"33": "<sup>33</sup> Ba neng ba tshwara tshebetso eo hammoho le bana ba bona, ho bara ba Bakehathe e ne e le sebini Hemane, mora Joele, mora Samuele,",
			"34": "<sup>34</sup> mora Elkana, mora Jerohame, mora Eliele, mora Thowa,",
			"35": "<sup>35</sup> mora Tsufe, mora Elkana, mora Mahathe, mora Amasai,",
			"36": "<sup>36</sup> mora Elkana, mora Joele, mora Asaria, mora Sofonia,",
			"37": "<sup>37</sup> mora Thahathe, mora Assire, mora Ebiasafe, mora Kore,",
			"38": "<sup>38</sup> mora Jitsehare, mora Kehathe, mora Levi, mora Iseraele.",
			"39": "<sup>39</sup> E mong e ne e le Asafe, ngwanabo, ya neng a ema ka letsohong la hae le letona; ke Asafe, mora Berekia, mora Shimea,",
			"40": "<sup>40</sup> mora Mikaele, mora Baaseia, mora Malkija,",
			"41": "<sup>41</sup> mora Ethni, mora Sera, mora Adaia,",
			"42": "<sup>42</sup> mora Ethane, mora Simma, mora Shimei,",
			"43": "<sup>43</sup> mora Jahathe, mora Gershome, mora Levi.",
			"44": "<sup>44</sup> Haele banababo bona, bara ba Merari, ba ne ba ema ka letsohong le letshehadi: e ne e le Ethane, mora Kishi, mora Abdi, mora Malluke,",
			"45": "<sup>45</sup> mora Hashabia, mora Amatsia, mora Hilkia,",
			"46": "<sup>46</sup> mora Amtsi, mora Bani, mora Shemere,",
			"47": "<sup>47</sup> mora Mahali, mora Mushi, mora Merari, mora Levi.",
			"48": "<sup>48</sup> Banababo bona, e leng Balevi ba bang, le bona ba newa ditshebetso tsohle tsa tabernakele ya ntlo ya Modimo.",
			"49": "<sup>49</sup> Aarone le bara ba hae ba ne ba hlahisa mahlabelo aletareng ya ditjheso le dibano aletareng ya dibano; hape ba ne ba neilwe mesebetsi yohle ya sehalaledisiso, hore ba etsetse Iseraele pheko, kahohle kamoo Moshe, mohlanka wa Modimo, a neng a laetse kateng.",
			"50": "<sup>50</sup> Bara ba Aarone, ke Eleasare, mora hae, le Finease, mora hae, le Abishua, mora hae,",
			"51": "<sup>51</sup> le Bukki, mora hae, le Ussi, mora hae, le Serahia, mora hae,",
			"52": "<sup>52</sup> le Merajothe, mora hae, le Amaria, mora hae, le Akitube, mora hae,",
			"53": "<sup>53</sup> le Tsadoke, mora hae, le Akimaatse, mora hae.",
			"54": "<sup>54</sup> Madulo a bara ba Aarone, ba meloko ya Bakehathe, a metse ya bona naheng ya bona, ao ba kileng ba abelwa wona ka lotho, ke ana:",
			"55": "<sup>55</sup> Ba newa Hebrone naheng ya Juda, le mathoko a teng ka nqa tsohle.",
			"56": "<sup>56</sup> Haele masimo a motse oo le metsana ya wona, ba e nea Kalebe, mora Jefunne.",
			"57": "<sup>57</sup> Hape, bara ba Aarone ba newa metse e meng Juda: ke Hebrone, motse wa setshabelo, le Libna, ka mathoko a wona, le Jatthire le Eshthemowa ka mathoko a teng,",
			"58": "<sup>58</sup> le Hilene le mathoko a teng, le Debire le mathoko a teng,",
			"59": "<sup>59</sup> ka Ashane le mathoko a teng, le Bethe-Shemeshe ka mathoko a teng.",
			"60": "<sup>60</sup> Lelokong la Benjamine, ba newa Geba le mathoko a teng, le Allemethe le mathoko a teng, le Anathothe ka mathoko a teng. Metse yohle ya bona, melokong ya bona, e ne e le metse e leshome le metso e meraro.",
			"61": "<sup>61</sup> Bara ba Kehathe, ba neng ba setse ba mofuta wa leloko leo, ba newa metse ka lotho naheng ya leloko la Efraime, le ya leloko la Dane le ya mothapo o mong wa leloko la Manasse, ya eba metse e leshome.",
			"62": "<sup>62</sup> Bara ba Gershome bona, ka ho ya ka meloko ya bona, ba newa metse e leshome le metso e meraro lelokong la Issaskare, le lelokong la Asere, le lelokong la Nefthali, le lelokong la Manasse naheng ya Bashane.",
			"63": "<sup>63</sup> Bara ba Merari, ka ho ya ka meloko ya bona, ba newa metse e leshome le metso e mmedi ka lotho lelokong la Rubene, le lelokong la Gade, le lelokong la Sabulone.",
			"64": "<sup>64</sup> Bana ba Iseraele ba nea Balevi metse eo ka mathoko a yona.",
			"65": "<sup>65</sup> Hape, lelokong la bana ba Juda, le lelokong la bana ba Simeone, le lelokong la bana ba Benjamine, ba ba nea, ka lotho, metse eo e thontsweng ka mabitso a yona.",
			"66": "<sup>66</sup> Ba setseng ba meloko ya bara ba Kehathe ba bona metse eo ba ka ahang ho yona lelokong la Efraime.",
			"67": "<sup>67</sup> Ba ba nea metse ya setshabelo: ke Sikeme, thabeng ya Efraime, le mathoko a teng; le Gesere ka mathoko a teng,",
			"68": "<sup>68</sup> le Jokmeame ka mathoko a teng, le Bethe-Horone le mathoko a teng;",
			"69": "<sup>69</sup> le Ajalone le mathoko a teng, le Gathe-Rimmone le mathoko a teng;",
			"70": "<sup>70</sup> le mothapong o mong wa leloko la Manasse, ba setseng ba leloko la bara ba Kehathe ba newa Anere le mathoko a teng, le Bileame le mathoko a teng.",
			"71": "<sup>71</sup> Bara ba Gershome bona ba newa, mothapong o mong wa leloko la Manasse, Golane le mathoko a teng naheng ya Bashane, le Ashtarothe le mathoko a teng;",
			"72": "<sup>72</sup> lelokong la Issaskare, ba newa Kedeshe le mathoko a teng, Dabrathe le mathoko a teng,",
			"73": "<sup>73</sup> le Ramothe ka mathoko a teng, le Aneme ka mathoko a teng.",
			"74": "<sup>74</sup> Lelokong la Asere, ba newa Mashale ka mathoko a teng, le Abdone ka mathoko a teng,",
			"75": "<sup>75</sup> le Hukoke le mathoko a teng, le Rehobe ka mathoko a teng;",
			"76": "<sup>76</sup> hape, lelokong la Nefthali ba newa Kedeshe wa Galelea le mathoko a teng, le Hammone le mathoko a teng, le Kiryathaime le mathoko a teng.",
			"77": "<sup>77</sup> Ba setseng ba bana ba Merari, lelokong la Sabulone ba newa Rimmone le mathoko a teng, le Thabore le mathoko a teng;",
			"78": "<sup>78</sup> ka mose wane ho Jordane, pela Jeriko, ka nqa botjhabela ho Jordane, ba newa lelokong la Rubene Betsere o leng nahathothe le mathoko a teng, le Jahtsa le mathoko a teng,",
			"79": "<sup>79</sup> Kedemothe le mathoko a teng, le Mefaathe le mathoko a teng.",
			"80": "<sup>80</sup> Lelokong la Gade, ba newa Ramothe-Gileade le mathoko a teng, Mahamaine le mathoko a teng,",
			"81": "<sup>81</sup> le Heshbone le mathoko a teng, le Jaesere le mathoko a teng."
		},
		"7": {
			"1": "<sup>1</sup> Bara ba Issaskare ke Thola, Pua, Jashube, le Shimrone; ba bane.",
			"2": "<sup>2</sup> Bara ba Thola ke Ussi, Refaia, Jeriele, Jahmai, Jibsame, le Samuele; ke tsona dihlooho tsa matlo a bontata bona, ke ba ha Thola; e ne e le banna ba bahale melokong ya bona. Mehleng ya Davida, ba ntlo eo e ne e le banna ba dikete tse mashome a mabedi a metso e mmedi tse nang le makgolo a tsheletseng.",
			"3": "<sup>3</sup> Mora wa Ussi ke Jisraia; bara ba Jisraia ke Mikaele, Obadia, Joele, le Jishia; ba bahlano; kaofela ha bona e ne e le mahosana.",
			"4": "<sup>4</sup> Melokong ya bona, ha ho uwa ka matlo a bontata bona, ba ne ba ena le dihlopha tsa bahlabani ba lwanang, ba etsang batho ba dikete tse mashome a mararo a metso e tsheletseng; ba ne ba na le basadi le bana ba bangata.",
			"5": "<sup>5</sup> Bao e leng banababo bona melokong yohle ya Issaskare, e ne e le banna ba bahale, bao palo ya bona masikeng wohle a bona e neng e le dikete tse mashome a robileng meno e le mmedi a metso e supileng.",
			"6": "<sup>6</sup> Bara ba Benjamine ke Bela, Bekere, le Jediaele; ba bararo.",
			"7": "<sup>7</sup> Bara ba Bela ke Etsbone, Ussi, Usiele, Jerimothe, le Iri; ba bahlano; e ne e le dihlooho matlung a bontata bona, banna ba bahale; masikeng a bona ha ba balwa, e ne e le dikete tse mashome a mabedi a metso e mmedi tse nang le mashome a mararo a metso e mene.",
			"8": "<sup>8</sup> Bara ba Bekere ke Semira, Joashe, Eliesere, Eleoenai, Omri, Jerimothe, Abija, Anathothe, le Alamethe. Bao kaofela ke bara ba Bekere.",
			"9": "<sup>9</sup> Ha ba balwa ka lesika la bona le meloko ya bona, ho uwa ka dihlooho tsa matlo a bontata bona, banna ba bahale, ba etsa kete tse mashome a mabedi tse nang le makgolo a mabedi.",
			"10": "<sup>10</sup> Hape, mora wa Jediaele ke Bilhane; bara ba Bilhane ke Jeushe, Benjamine, Ehude, Kenaana, Sethane, Tarshishe, le Akishahare.",
			"11": "<sup>11</sup> Bao kaofela ke bara ba Jediaele, ha ho uwa ka dihlooho tsa matlo a bontata bona, e le banna ba bahale, banna ba dikete tse leshome le metso e supileng tse nang le makgolo a mabedi, ba loketseng ho futuha le ho lwana.",
			"12": "<sup>12</sup> Hape, ho na le Shuppime, le Huppime, bara ba Ire. Hape ke Hushime, mora wa Akere.",
			"13": "<sup>13</sup> Bara ba Nefthali ke Jahtsiele, Guni Jetsere, le Shallume; bao ke bara ba Bilha.",
			"14": "<sup>14</sup> Bara ba Manasse, ke Asriele eo mosadi wa hae a mo tswaletseng yena; serethe sa hae sa Mosiria se mo tswalla Makire, ntata Gileade;",
			"15": "<sup>15</sup> Makire a nka mosadi wa Huppime le Shuppime; lebitso la kgaitsedi ya hae ke Maaka. Bitso la mora wa hae wa bobedi ke Tselofkade; mme Tselofkade a tswala basetsana.",
			"16": "<sup>16</sup> Maaka, mosadi wa Makire, a tswala moshanyana, mme a mo rea lebitso la Pereshe; bitso la moenae ya eba Shereshe, mme bara ba hae ke Ulame le Rekeme.",
			"17": "<sup>17</sup> Mora wa Ulame ke Bedane. Bao ke bara ba Gileade, mora Makire, mora Manasse.",
			"18": "<sup>18</sup> Kgaitsedi ya hae Hammolekethe a tswala Ishode, Abiesere, le Mahala.",
			"19": "<sup>19</sup> Bara ba Shemida ke Akiane, Sikeme, Likhi, le Aniame.",
			"20": "<sup>20</sup> Bara ba Efraime ke Shuthela, le mora hae Berede, le Thahathe, mora hae, le Eleada, mora hae, le Thahathe, mora hae,",
			"21": "<sup>21</sup> le Sabade, mora hae, le Shutela, mora hae, le Esere, le Eleade, ba neng ba bolawe ke banna ba Gathe ba neng ba le naheng eo, mohla ba futuhelang ho hapa dikgomo.",
			"22": "<sup>22</sup> Ntata bona Efraime a ba llela ka matsatsi a mangata, mme banababo ba tla ho yena matshedisong.",
			"23": "<sup>23</sup> A boela a ya ho mosadi wa hae, mme a emola, a ba a tswala moshanyana, mme a mo rea lebitso la Beria (ya kotsing), kahobane ntlo ya hae e ne e tswile kotsi.",
			"24": "<sup>24</sup> Moradi wa hae ke Sheera, ya ileng a haha Bethe-Horone ya katlase, le Bethe-Horone ya kahodimo, le Ussene-Sheera.",
			"25": "<sup>25</sup> Mora Beria ke Refa, e mong ke Reshefe, le Thela, mora hae le Thahane, mora hae,",
			"26": "<sup>26</sup> le Laedane, mora hae, le Ammihude, mora hae, le Elishama, mora hae,",
			"27": "<sup>27</sup> le Nune, mora hae, le Joshua, mora hae.",
			"28": "<sup>28</sup> Fatshe la bona le neng le ruilwe ke bona le madulo a bona e ne e le Bethele le metsana ya teng, le Naarane ka nqa botjhabela, le Gesere nqa bophirimela le metsana ya teng; Sikeme le metsana ya teng, ho ya fihla Gasa le metsana ya teng;",
			"29": "<sup>29</sup> pela moedi wa bana ba Manasse ke Bethe-Sheane le metsana ya teng, Thaanake le metsana ya teng, Megiddo le metsana ya teng, Dore le metsana ya teng. Metseng eo, ho ne ho ahile bana ba Josefa, mora Iseraele.",
			"30": "<sup>30</sup> Bara ba Asere ke Jimna, Jishva, Jishvi, Beria, le Sera kgaitsedi ya bona.",
			"31": "<sup>31</sup> Bara ba Beria ke Hebere le Malkiele, ntata Birsavithe.",
			"32": "<sup>32</sup> Hebere yena a tswala Jaflethe, Shomere, Hothame, le Shua kgaitsedi ya bona.",
			"33": "<sup>33</sup> Bara ba Jaflethe ke Pasake, Bimhale, le Ashvathe. Bao ke bana ba Jaflethe.",
			"34": "<sup>34</sup> Bara ba Shamere ke Aki, Rohega, Hubba le Arame.",
			"35": "<sup>35</sup> Bara ba Heleme moenae ke Tsofa, Jimna, Sheleshe, le Amale.",
			"36": "<sup>36</sup> Bara ba Tsofa ke Sua, Harnefere, Shuale, Beri, Jimra,",
			"37": "<sup>37</sup> Betsere, Hode, Shamma, Shilsha, Jithrane, le Beera.",
			"38": "<sup>38</sup> Bara ba Jethere ke Jefunne, Pispa, le Ara.",
			"39": "<sup>39</sup> Bara ba Ulla ke Ara, Hanniele, le Ritsia.",
			"40": "<sup>40</sup> Bao kaofela ke bana ba Asere, e ne e le dihlooho matlung a bontata bona, banna ba kgethilweng, ba bahale, ba baholo hara mahosana. Ka ho ya ka lesika la bona, ba teng ba neng ba loketse ho futuha le ho lwana e ne e le ba dikete tse mashome a mabedi a metso e tsheletseng."
		},
		"8": {
			"1": "<sup>1</sup> Haele Benjamine, a tswala letsibolo la hae Bela; Ashbele ya eba wa bobedi, Akra wa boraro,",
			"2": "<sup>2</sup> Noha wa bone, le Rafa wa bohlano.",
			"3": "<sup>3</sup> Bara ba Bela ke Addare, Gera, Abihude,",
			"4": "<sup>4</sup> Abishua, Naamane, Akowa,",
			"5": "<sup>5</sup> Gera, Shefufane, le Hurame.",
			"6": "<sup>6</sup> Bana ba Ehude ke bana; e ne e le dihlooho tsa matlo a bontata baahi ba Geba, ba ba ba ba falaletsa Manahathe:",
			"7": "<sup>7</sup> ke Naamane, Akija, le Gera; Gera ke yena ya neng a ba falatse. A tswala Ussa le Akihude.",
			"8": "<sup>8</sup> Shaharaime a tswala bana naheng ya Moabe, hobane a kgutlise basadi ba hae Hushime le Baara.",
			"9": "<sup>9</sup> Ka mosadi wa hae Hodeshe, a tswala Jobabe, Tsibia, Mesha, Malkame,",
			"10": "<sup>10</sup> Jeutse, Shokia, le Mirma. Ke bona bara ba hae, dihlooho tsa matlo a bontata bona.",
			"11": "<sup>11</sup> Ka Hushime, a tswala Abithube le Elpaale.",
			"12": "<sup>12</sup> Bara ba Elpaale ke Ebere, Misheame, Shemere ya hahileng Ono, le Lode, le metsaneng ya teng.",
			"13": "<sup>13</sup> Hape, ke Beria le Shema, dihlooho tsa matlo a bontata baahi ba Ajalone, ba neng ba leleke ba ahileng Gathe;",
			"14": "<sup>14</sup> le Akio, Shashake, Jeremothe,",
			"15": "<sup>15</sup> Sebadia, Arade, Edere,",
			"16": "<sup>16</sup> Mikaele, Jisfa le Joha: ke bara ba Beria;",
			"17": "<sup>17</sup> Sebadia, Meshullame, Hiski, Hebere,",
			"18": "<sup>18</sup> le Jishmerai, Jislia, le Jobabe: ke bara ba Elpaale;",
			"19": "<sup>19</sup> hape, ke Jakime, Sikri, Sabdi,",
			"20": "<sup>20</sup> Elienai, Tsillethai, le Eliele,",
			"21": "<sup>21</sup> le Adaia, Beraia, le Shimrathe: ke bara ba Shimei;",
			"22": "<sup>22</sup> le Jishpane, Ebere, Eliele,",
			"23": "<sup>23</sup> Abdone, Sikri, Hanane,",
			"24": "<sup>24</sup> Hanania, Elame, Anthothija,",
			"25": "<sup>25</sup> Jifdeia, le Penuele: ke bara ba Shashake;",
			"26": "<sup>26</sup> le Shamsherai, Sheharia, Athalia,",
			"27": "<sup>27</sup> le Jaareshia, Elia, le Sikri: ke bara ba Jerohame.",
			"28": "<sup>28</sup> Bao ke dihlooho tsa matlo a bontata bona, melokong ya bona; ke dihlooho. Ba ne ba ahile Jerusalema.",
			"29": "<sup>29</sup> Gibeone ho ne ho ahile ntata Gibeone; lebitso la mosadi wa hae e ne e le Maaka.",
			"30": "<sup>30</sup> Mora hae wa letsibolo ke Abdone, bara ba bang ke Tsure, Kishe, Baale, Nadabe,",
			"31": "<sup>31</sup> Gedore, Akio, le Sekere.",
			"32": "<sup>32</sup> Miklothe yena a tswala Shimea. Bao ba ne ba ahile Jerusalema le banababo bona, mabapa le bona.",
			"33": "<sup>33</sup> Nere a tswala Kishe, Kishe yena a tswala Saule, Saule a tswala Jonathane, Malkishua, Abinadabe, le Eshbaale.",
			"34": "<sup>34</sup> Mora Jonathane ke Meribe-Baale; mme Meribe-Baale a tswala Mikea.",
			"35": "<sup>35</sup> Bara ba Mikea ke Pithone, Meleke, Thaerea, le Akase.",
			"36": "<sup>36</sup> Akase a tswala Jehoadda; Jehoadda a tswala Alemethe, Asmavethe, le Simri; Simri a tswala Motsa.",
			"37": "<sup>37</sup> Motsa a tswala Binea; mora hae ke Rafa, mora hae ke Eleasa, mora hae ke Atsele.",
			"38": "<sup>38</sup> Atsele yena a ba le bara ba tsheletseng; mabitso a bona ke Asrikame, Bokru, Ishmaele, Shearia, Obadia, le Hanane. Bao kaofela ke bara ba Atsele.",
			"39": "<sup>39</sup> Bara ba Esheke ngwanabo, letsibolo la hae ke Ulame, wa bobedi ke Jeushe, wa bararo ke Elifelethe.",
			"40": "<sup>40</sup> Bara ba Ulame e ne e le bahale, le ba fulang ka seqha; ba eba le bana ba bangata le ditloholo, ba etsa lekgolo le nang le mashome a mahlano. Bao kaofela ke bara ba Benjamine."
		},
		"9": {
			"1": "<sup>1</sup> Baiseraele kaofela ba balwa jwalo masikeng a bona; ba ngodilwe bukeng ya marena a Iseraele. Haele Juda, a iswa kgolehong Babilona ka baka la meikgantsho ya hae.",
			"2": "<sup>2</sup> Haele ba kileng ba aha ha bona pele, metseng ya bona, e ne e le Baiseraele, le baprista, le Balevi le Banethinime.",
			"3": "<sup>3</sup> Jerusalema, ha aha ba bang ba bara ba Juda, le ba bara ba Benjamine, le ba bara ba Efraime le Manasse,",
			"4": "<sup>4</sup> e leng Uthai, mora Ammihude, mora Omri, mora Imri, mora Bani, wa bara ba Peretse, mora Juda.",
			"5": "<sup>5</sup> Ho Bashilone, ke Asaia wa letsibolo le bara ba hae;",
			"6": "<sup>6</sup> ho bara ba Sera, ke Jeuele le banababo, e leng batho ba makgolo a tsheletseng a mashome a robileng mono o le mong.",
			"7": "<sup>7</sup> Ho bara ba Benjamine, ke Sallu, mora Meshullame, mora Hodavia, mora Assenua,",
			"8": "<sup>8</sup> le Jibneia, mora Jerohame, le Ela, mora Ussi, mora Mikri, le Meshullame, mora Shefathia, mora Reuele, mora Jibnija,",
			"9": "<sup>9</sup> le banababo, melokong ya bona, e le batho ba makgolo a robileng mono o le mong a mashome a mahlano a metso e tsheletseng. Banna bao kaofela e ne e le dihlooho tsa matlo a bontata bona, matlung a bontata bona.",
			"10": "<sup>10</sup> Ho baprista, ke Jedaeia, Jehojaribe, Jakine,",
			"11": "<sup>11</sup> le Asaria, mora Hilkia, mora Meshullame, mora Tsadoke, mora Merajothe, mora Akitube, modisa wa ntlo ya Modimo;",
			"12": "<sup>12</sup> le Adaia, mora Jerohame, mora Pashkure, mora Malkija, le Maesai, mora Adiele, mora Jahsera, mora Meshullame, mora Meshillemithe, mora Immere;",
			"13": "<sup>13</sup> le banababo bona, dihlooho tsa matlo a bontata bona, e le batho ba sekete se nang le makgolo a supileng le mashome a tsheletseng; e ne e le banna ba matla mosebetsing wa tshebetso ya ntlo ya Modimo.",
			"14": "<sup>14</sup> Ho Balevi, ke Shemaia, mora Hashube, mora Asrikame, mora Hashabia, wa bara ba Merari;",
			"15": "<sup>15</sup> le Bakbakkare, Hereshe, Galale, le Matthania, mora Mikea, mora Sikri, mora Asafe;",
			"16": "<sup>16</sup> le Obadia, mora Shemaia, mora Galale, mora Jeduthune, le Berekia, mora Asa, mora Elkana, ya neng a ahile metseng ya Banethofathe.",
			"17": "<sup>17</sup> Balebedi ba menyako ke Shallume, Akkube, Thalmone, Akimane, le banababo bona; Shallume e ne e le hlooho ya bona;",
			"18": "<sup>18</sup> ho fihlela jwale, o ne a lebeletse monyako wa morena ka nqa botjhabela; e be e le bona balebedi ba menyako dihlopheng tsa bara ba Levi.",
			"19": "<sup>19</sup> Shallume, mora Kore, mora Ebiasafe, mora Kore, a ena le banababo ba ntlo ya ntatae, e leng Bakore, ba ne ba tshwere mosebetsi wa ho lebela menyako ya tabernakele; haele bontata bona, ba neng ba disa diahelo tsa Jehova, e ne e le balebedi ba monyako wa tsona.",
			"20": "<sup>20</sup> Mehleng ya kgale, Finease, mora Eleasare, ya eba modisa wa bona, mme Jehova a nna a ba le yena.",
			"21": "<sup>21</sup> Sakaria, yena, mora Meshelemia, e ne e le molebedi wa monyako wa tente ya pokano.",
			"22": "<sup>22</sup> Bao kaofela ba neng ba kgethilwe ho ba balebedi ba menyako, e ne e le ba makgolo a mabedi a leshome le metso e mmedi. Ba ne ba badilwe ka masika a bona metsaneng ya bona, e le ba neng ba kgethetswe kabelo eo e tiileng, ke Davida le Samuele seboni.",
			"23": "<sup>23</sup> Kahoo, bona le bana ba bona ba ne ba lebela menyako ya ntlo ya Jehova, e leng ntlo ya tabernakele, ka dihlopha tsa bona.",
			"24": "<sup>24</sup> Balebedi ba ne ba le ka nqa tse nne, ka nqa botjhabela, le ka nqa bophirimela, le ka nqa leboya, le ka nqa borwa.",
			"25": "<sup>25</sup> Banababo bona ba neng ba le metsaneng ya bona, ba ne ba tshwanetse ho tla ho bona kamora tshiu tse supileng, ho ya ka dinako tse beilweng.",
			"26": "<sup>26</sup> Balevi bao ba bane, e neng e le balebedi ba baholo, ba ne ba le kabelong ya bona kamehla, mme hape, ba ne ba lebela dikamore le matlo a matlotlo a ntlo ya Modimo.",
			"27": "<sup>27</sup> Ba ne ba hlola bosiu pela ntlo ya Modimo, hobane ba ne ba beilwe ho lebela, mme ba ne ba na le kabelo ya ho e bula ka hosasa ho hong le ho hong.",
			"28": "<sup>28</sup> Ba bang ba ne ba neilwe kabelo ya ho baballa mesebetsi e sebeletsang; ba ne ba e bala ha ba e ntsha, ba ne ba e bala ha ba e busa.",
			"29": "<sup>29</sup> Ba bang ba ne ba kgethetswe ho baballa mesebetsi le diphahlo tsohle tsa sehalalelo, le lefi la phofo, le veine, le ole, le dibano, le dinoko.",
			"30": "<sup>30</sup> Dibano tse tswakilweng di ne di etswa ke bara ba baprista.",
			"31": "<sup>31</sup> Matthithia, wa Balevi, letsibolo la Shallume wa Bakore, o ne a beilwe ho baballa mosebetsi wa maqebekwane a phehwang paneng.",
			"32": "<sup>32</sup> Banababo bona ba bang, ho Bakehathe, ba ne ba baballa mahobe a pontsha, ba a lokisa ka Sabatha e nngwe le e nngwe.",
			"33": "<sup>33</sup> Ke tsona dibini, dihlooho tsa matlo a bontata Balevi, tse neng di ahile dikamoreng, di se na kabelo esele, kahobane di ne di sebetsa mosebetsi wa tsona motshehare le bosiu.",
			"34": "<sup>34</sup> Bao e ne e le dihlooho tsa matlo a bontata Balevi melokong ya bona, e ne e le dihlooho; bona ba ne ba ahile Jerusalema.",
			"35": "<sup>35</sup> Gibeone, ho ne ho ahile Jeiele ntata Gibeone; lebitso la mosadi wa hae e ne e le Maaka.",
			"36": "<sup>36</sup> Mora hae wa letsibolo ke Abdone; bara ba hae ba bang ke Tsure, Kishe, Baale, Nere, Nadabe,",
			"37": "<sup>37</sup> Gedore, Akio, Sakaria, le Miklothe.",
			"38": "<sup>38</sup> Miklothe a tswala Shimeame. Le bona ba ne ba ahile Jerusalema hammoho le banababo bona, mabapa le banababo bona.",
			"39": "<sup>39</sup> Nere a tswala Kishe, Kishe a tswala Saule; Saule a tswala Jonathane, Malkishua, Abinadabe, le Eshbaale.",
			"40": "<sup>40</sup> Mora Jonathane ke Meribe-Baale; Meribe-Baale yena a tswala Mikea.",
			"41": "<sup>41</sup> Bara ba Mikea ke Pithone, Meleke, le Thahrea.",
			"42": "<sup>42</sup> Akase a tswala Jaera, Jaera a tswala Alemethe, Asmavethe, le Simri; Simri yena a tswala Motsa;",
			"43": "<sup>43</sup> Motsa a tswala Binea; mora hae ke Refaia, mora hae ke Eleasa, mora hae ke Atsele.",
			"44": "<sup>44</sup> Atsele a ba le bara ba tsheletseng; mabitso a bona ke Asrikame, Bokru, Ishmaele, Shearia, Obadia, le Hanane; ke bao bara ba Atsele."
		},
		"10": {
			"1": "<sup>1</sup> Eitse ha Bafilesta ba lwana le Baiseraele, banna ba Iseraele ba baleha pela Bafilesta, ba wa ba hlabilwe thabeng ya Gilboa.",
			"2": "<sup>2</sup> Bafilesta ba phallela Saule le bara ba hae ka matla, mme Bafilesta ba bolaya Jonathane le Abinadabe le Malkishua, bara ba Saule.",
			"3": "<sup>3</sup> Jwale, Saule a imelwa ke ntwa; ba fulang ka diqha ba mo fihlela, mme a tsieleha haholo ke ba diqha.",
			"4": "<sup>4</sup> Yaba Saule o re ho lehlahana la hae: Pshemola sabole ya hao, o ntlhabe ka yona, esere mohlomong maqai ao a tla, mme a ntshoma. Empa lehlahana la hae la hana, kahobane le ne le tshohile haholo. Yaba Saule o nka sabole ya hae, a itihela hodima yona.",
			"5": "<sup>5</sup> Eitse ha lehlahana la hae le bona hoba Saule o shwele, le lona la itihela hodima sabole ya lona, mme la eshwa.",
			"6": "<sup>6</sup> Ha eshwa jwalo Saule le bara ba hae ba le bararo; ntlo ya hae kaofela ya eshwa hammoho.",
			"7": "<sup>7</sup> Eitse banna bohle ba Iseraele ba ahileng kgohlong, ha ba bona hoba ho balehilwe, le hoba Saule le bara ba hae ba shwele, ba furalla metse ya bona, ba baleha. Mme Bafilesta ba fihla, ba aha ho yona.",
			"8": "<sup>8</sup> Hosasane, ha Bafilesta ba tlile ho hlobodisa ba bolailweng, ba fumana Saule le bara ba hae ba wele thabeng ya Gilbowa.",
			"9": "<sup>9</sup> Yaba ba mo hlobodisa, ba nka hlooho ya hae le dihlomo tsa hae; mme ba romela kahohle naheng ya Bafilesta ho ya behela medimo ya bona le setjhaba.",
			"10": "<sup>10</sup> Ba bea dihlomo tsa hae tlung ya modimo wa bona, mme ba fanyeha hlooho ya hae tlung ya Dagone.",
			"11": "<sup>11</sup> Eitse ha bohle ba Jabeshe-Gileade ba utlwa tsohle tseo Bafilesta ba di entseng ka Saule,",
			"12": "<sup>12</sup> banna bohle ba matla ba ya, ba tlosa setopo sa Saule le ditopo tsa bara ba hae, ba di tlisa Jabeshe. Ba pata masapo a bona tlasa sefate sa terebinthe se Jabeshe, mme ba itima dijo ka matsatsi a supileng.",
			"13": "<sup>13</sup> Saule a shwa jwalo ka baka la boikgantsho ba hae, boo a neng a ikgantsheditse Jehova ka jona, bakeng sa lentswe la Jehova leo a sa kang a le boloka, le bakeng sa hobane a ne a botsitse ba bitsang bafu, hore a ipotsetse ditaba ho bona,",
			"14": "<sup>14</sup> a sa ka a botsa Jehova. Jehova a mo timetsa ke hona, mme a fetisetsa borena ho Davida, mora Jese."
		},
		"11": {
			"1": "<sup>1</sup> Yaba Baiseraele bohle ba phuthehela ho Davida, Hebrone, ba re: Bona, re masapo a hao le nama ya hao.",
			"2": "<sup>2</sup> Leha e le maoba le maobane, Saule ha e sa ntse e le morena wa rona, e be e le wena ya futuhisang Baiseraele, le ho ba orosetsa hae. Le Jehova, Modimo wa hao, o o boleletse, a re: O tla disa setjhaba sa ka sa Iseraele, o be molaodi wa setjhaba sa ka sa Iseraele.",
			"3": "<sup>3</sup> Baholo bohle ba Iseraele ba tla jwalo ho morena, Hebrone; Davida a hlaba selekane le bona Hebrone, pela Jehova; mme ba tlotsa Davida, ho mo etsa morena wa Iseraele, ka polelo eo Jehova a neng a e boletse ka Samuele.",
			"4": "<sup>4</sup> Yaba Davida le Baiseraele bohle ba futuhela Jerusalema, e leng Jebuse, moo ho neng ho le Bajebuse ba neng ba ahile naheng eo.",
			"5": "<sup>5</sup> Baahi ba Jebuse ba re ho Davida: O ke ke wa kena kwano. Empa Davida a kena qhobosheane ya Sione; ke wona motse wa Davida.",
			"6": "<sup>6</sup> Davida o ne a itse: Ya tla qhala Bajebuse pele, e tla ba kgosana le morena. Joabe, mora Tseruja, a hlwa pele, mme a etswa kgosana.",
			"7": "<sup>7</sup> Davida a aha qhobosheaneng; ya rewa ke hona lebitso la motse wa Davida.",
			"8": "<sup>8</sup> A ba a haha motse ka nqa tsohle, ho tloha Millo le ho isa ka nqa tsohle: Joabe yena a lokisa ho setseng ha motse.",
			"9": "<sup>9</sup> Mme Davida a nna a ya a hola, kahobane Jehova wa makgotla a nna a ba le yena.",
			"10": "<sup>10</sup> Ke tsena dihlooho tsa banna ba bahale ba Davida, ba nnileng ba mo emela boreneng ba hae, ba ena le Baiseraele bohle, ba tle ba mmee morena, ka polelo e neng e boletswe ke Jehova bakeng sa Iseraele.",
			"11": "<sup>11</sup> Ke ena palo ya bahale bao Davida a neng a na le bona. Jashobeame, mora Hakmoni, ke hlooho ya dinatla. A lwantsha banna ba makgolo a mararo ka lerumo la hae, a ba bolaya ka nako e le nngwe.",
			"12": "<sup>12</sup> Ya mo hlahlamang ke Eleasare, mora Dodo, wa Akoke, e mong wa dinatla tse tharo.",
			"13": "<sup>13</sup> Ke yena ya neng a emele Davida Pase-Dammime. Bafilesta ba ne ba phuthehetse teng ka ntwa; teng, ho ne ho na le lebatowa la tshimo le tletseng harese; ke mohla setjhaba se neng se balehetse Bafilesta.",
			"14": "<sup>14</sup> Ba ema hare ho tshimo, ba e tseka, mme ba qhala Bafilesta; Jehova a namolela Iseraele ka hlolo e kgolo.",
			"15": "<sup>15</sup> Ba bararo ho bao e leng dihlooho tsa dinatla tse mashome a mararo ba ne ba theohetse ho Davida seqhobong, lehaheng la Adullame; diahelo tsa Bafilesta di ne di hlomilwe kgohlong ya Barefaime.",
			"16": "<sup>16</sup> Mohlang oo, Davida o ne a le seqhobong, mme sehlopha sa Bafilesta se ne se le teng Bethlehema.",
			"17": "<sup>17</sup> Ke ha Davida a qala ho lakatsa, a re: Ke mang ya tla nnosa metsi a sediba sa Bethlehema se pela kgoro?",
			"18": "<sup>18</sup> Yaba ba bararo bao ba phunyeletsa hara diahelo tsa Bafilesta, ba ekga metsi sedibeng sa Bethlehema se pela kgoro, mme ba a nka, ba tla le wona ho Davida; empa Davida a hana ho a nwa, a mpa a a tshollela Jehova;",
			"19": "<sup>19</sup> a re: Ka Modimo wa ka, ha nka ke ka etsa hoo. Na ke be nka nwa madi a banna bao ba ileng ba tetse bophelo ba bona na? Hobane ba a tlisitse, ba sa re letho ka bophelo ba bona, A hana ho a nwa ke hona. Hoo ha etswa ke dinatla tseo tse tharo.",
			"20": "<sup>20</sup> Abishai, moena Joabe, e ne e le hlooho ya ba bararo bao. A lwantsha banna ba makgolo a mararo ka lerumo la hae, a ba bolaya, mme a iketsetsa lebitso hara ba bararo bao.",
			"21": "<sup>21</sup> Hara ba bararo, ya eba yena ya roriswang ho ena le ba babedi ba bang, mme ya eba hlooho ya bona, a mpa a sa fihle ho ba bararo ba pele.",
			"22": "<sup>22</sup> E mong ke Benaia, mora Jehojada, mora monna wa senatla wa Kabsteele, ya neng a entse diketso tse kgolo. A bolaya tau tse pedi tsa Moabe; hape, ke yena ya neng a theohe, a ya bolaya tau ka lemeneng, e le mohla lehlwa le kgethehang.",
			"23": "<sup>23</sup> A bolaya le monna wa Moegepeta; monna eo boemo ba hae e ne e le ditswe tse hlano. Moegepeta letsohong o ne a tshwere lerumo le neng le le ka ka sefate sa selohi; a theohela ho yena ka molamu, a amoha Moegepeta lerumo, mme a mmolaya ka lona lerumo leo la hae.",
			"24": "<sup>24</sup> Tseo tsa etswa ke Benaia, mora Jehojada, mme a iketsetsa lebitso hara dinatla tseo tse tharo.",
			"25": "<sup>25</sup> A roriswa ho feta ba mashome a mararo, a mpa a se ke a fihla ho ba bararo ba pele. Davida a mmea monna wa lekgotla.",
			"26": "<sup>26</sup> Haele banna ba dinatla ba makgotleng a Davida, e ne e le Asaele, moena Joabe, Elkanane, mora Dodo wa Bethlehema;",
			"27": "<sup>27</sup> Shammothe wa Harore, Heletse Mopalone;",
			"28": "<sup>28</sup> Ira, mora Ikkeshe, wa Tekowa, Abiesere wa Anathothe,",
			"29": "<sup>29</sup> Sibbekai wa Husha, Ilai wa Akowa,",
			"30": "<sup>30</sup> Maharai wa Nethofa, Helede, mora Baana, wa Nethofa,",
			"31": "<sup>31</sup> Ithai, mora Ribai, wa Gibea wa bana ba Benjamine, Benaia wa Pirathone,",
			"32": "<sup>32</sup> Hurai wa Nahale-Gaashe, Abiele wa Araba,",
			"33": "<sup>33</sup> Asmavethe wa Baharume, Eliahba wa Shaalbone,",
			"34": "<sup>34</sup> Bene-Hasheme wa Gisone, Jonathane, mora Shage, wa Harare,",
			"35": "<sup>35</sup> Akiame, mora Sakare, wa Harare, Elifale, mora Ure,",
			"36": "<sup>36</sup> Hefere wa Mekera, Akija wa Palone,",
			"37": "<sup>37</sup> Hetsro wa Karmele, Naarai, mora Esbai,",
			"38": "<sup>38</sup> Joele ngwanabo Nathane, Mibhare, mora Hagri,",
			"39": "<sup>39</sup> Tseleke Moammone, Nahrai wa Berothe, lehlahana la Joabe, mora Tseruja;",
			"40": "<sup>40</sup> Ira, wa Jethere, Garebe wa Jethere,",
			"41": "<sup>41</sup> Uria Mohethe, Sabade, mora Ahalai,",
			"42": "<sup>42</sup> Adina, mora Shisa Morubene, molaodi wa Barubene, a ena le ba mashome a mararo,",
			"43": "<sup>43</sup> Hanane, mora Maaka, le Josafete wa Mithni,",
			"44": "<sup>44</sup> Osiase wa Ashtharothe, Shama le Jehiele, bara ba Hothame, wa Aroere,",
			"45": "<sup>45</sup> Jediaele, mora Shimri, le Joha ngwanabo, wa Thitsi,",
			"46": "<sup>46</sup> Eliele wa Mahavime, le Jeribai, le Joshavia, bara ba Elnaame, le Jithma Momoabe,",
			"47": "<sup>47</sup> le Eliele, le Obede, le Jaasiele wa Metsobaia."
		},
		"12": {
			"1": "<sup>1</sup> Ba kileng ba ya ho Davida a le Tsiklage, ha a ne a ntse a ipatela Saule, mora Kishe, ke bana: le bona ba ne ba balwa le dinatla tse neng di mo tlatsa ntweng;",
			"2": "<sup>2</sup> e ne e le ba fulang ka diqha; ba ne ba betsa majwe ka letsoho le letona le ka letsoho le letshehadi, mme ba ne ba fula metsu ka diqha; e ne e le ba habo Saule, Babenjamine.",
			"3": "<sup>3</sup> E moholo ho bona ke Akiesere, e mong ke Joase, bara ba Shemaa wa Gibea; Jesiele, le Pelethe, bara ba Asmavethe, le Beraka, le Jehu wa Anathothe,",
			"4": "<sup>4</sup> le Jishmaia wa Gibeone, senatla se seholo hara ba mashome a mararo, e le motsamaisi wa ba mashome a mararo, le Jeremia, le Jahasiele, le Johanane, le Josabade wa Gedera;",
			"5": "<sup>5</sup> Elusai, Jerimothe, Bealia, Shemaria, le Shefathia wa Harufe,",
			"6": "<sup>6</sup> le Elkana, le Jishia, le Asareele, le Joesere, le Jashobeame, Bakore,",
			"7": "<sup>7</sup> le Joela, le Sebadia, bara ba Jerohame wa Gedore.",
			"8": "<sup>8</sup> Ho Bagade ha tloha bahale ba tshwanelang ntwa ho ya e lwana, ba hlometseng thebe le lerumo; mahlo a bona a ne a tshwana le a ditau, ka lebelo ba ne ba le jwaloka matsa dithabeng; ba ne ba tlile ho Davida, ha a le seqhobong:",
			"9": "<sup>9</sup> Esere ke wa pele ho bona; wa bobedi ke Obadia, wa boraro ke Eliabe;",
			"10": "<sup>10</sup> wa bone ke Mishmanna, wa bohlano ke Jeremia;",
			"11": "<sup>11</sup> wa botshelela ke Attai, wa bosupa ke Eliele;",
			"12": "<sup>12</sup> wa boroba meno e le mmedi ke Johanane, wa boroba mono o le mong ke Elsabade;",
			"13": "<sup>13</sup> wa leshome ke Jeremia, wa leshome le motso o mong ke Makbannai.",
			"14": "<sup>14</sup> E ne e le ba bara ba Gade, balaodi ba makgotla; a le mong ho bona, esita le e monyenyane, o ne a lekana le banna ba lekgolo; e moholo o ne a lekana le ba sekete.",
			"15": "<sup>15</sup> Ba ne ba tshele Jordane kgweding ya ho qala, mohla e kwahetseng mabopo a yona kaofela, mme ba leleka batho bohle ba dikgohlong ka nqa botjhabela le ka nqa bophirimela.",
			"16": "<sup>16</sup> Ho bara ba Benjamine le ba Juda, ha tla ba bang, ba fihla ho Davida seqhobong.",
			"17": "<sup>17</sup> Davida a ya ba kgahlanyetsa, a bua, a re ho bona: Ha ekaba le tlile ho nna ka kgotso ho ntlatsa, le nna pelo ya ka e tla kopana le lona; ha le mpa le tlile ho nkeka hore le nneele direng tsa ka, leha ho mpa ho se bokgopo matsohong a ka, Modimo wa bontata rona o ke o bone, mme o re ahlolele.",
			"18": "<sup>18</sup> Yaba moya o tshwara Amasai, molaodi wa dinatla, a re: Re ba hao, Davida; re a o emela, mora Jese! Kgotso, kgotso e be le wena, kgotso e be ho ba o tlatsang, etswe Modimo wa hao o o thusa! Yaba Davida o a ba amohela, a ba bea balaodi ba makgotla.",
			"19": "<sup>19</sup> Ho Bamanasse, ba bang ba theohela ho Davida, mohla a futuhelang Saule, a ena le Bafilesta; empa ha ba ka ba ba tlatsa; eitse hobane marena a Bafilesta a eletsane, a mo kgutlisa, a re: A ka fetela ho Saule, hlooho tsa rona tsa kena tsietsing.",
			"20": "<sup>20</sup> Le mohla a yang Tsiklage, Bamanasse ba ileng ba fetela ho yena ke bana: ke Adna, Josabade, Jediaele, Mikaele, Josabade, Elihu, le Tsilthai, balaodi ba dikete tsa Manasse.",
			"21": "<sup>21</sup> Ba tlatsa Davida ho lwantsha Baamaleke, etswe kaofela ha bona e ne e le bahale; mme ba bewa balaodi ba makgotla.",
			"22": "<sup>22</sup> Ka matsatsi wohle, ha nna ha fihla batho ho Davida ho mo tlatsa, ho fihlela ba ba ba etsa sera se seholo, se kang sera sa Modimo.",
			"23": "<sup>23</sup> Haele banna ba hlomeletseng ntwa, ba neng ba tle ho Davida, Hebrone, ho fetisetsa borena ba Saule ho yena, ka taelo ya Jehova, palo ya bona ka dihlooho tsa bona ke ena:",
			"24": "<sup>24</sup> Bana ba Juda, ba hlometseng thebe le lerumo, ba tla ba entse dikete tse tseletseng tse nang le makgolo a robileng meno e le mmedi, ba hlomeletseng ntwa.",
			"25": "<sup>25</sup> Bana ba Simeone, e leng bahale ntweng, ba tla ba entse kete tse supileng tse nang le lekgolo le le leng.",
			"26": "<sup>26</sup> Bana ba Levi ba tla ba le dikete tse nne tse nang le makgolo a tsheletseng.",
			"27": "<sup>27</sup> Hape, ha tla Jehojada, motsamaisi wa bana ba Aarone, a ena le batho ba kete tse tharo tse nang le makgolo a supileng.",
			"28": "<sup>28</sup> Ha tla le Tsadoke, mohlankana wa mohale, a ena le ba ntlo ya ntatae, e le mahosana a mashome a mabedi a metso e mmedi.",
			"29": "<sup>29</sup> Ho bana ba Benjamine, banababo Saule, ha tla dikete tse tharo; athe ho fihla moo, ba bangata hara bona ba ne ba ntse ba kgomaretse ntlo ya Saule.",
			"30": "<sup>30</sup> Bana ba Efraime ba tla ba le dikete tse mashome a mabedi tse nang le makgolo a robileng meno e le mmedi, e le bahale, banna ba tumileng matlung a bontata bona.",
			"31": "<sup>31</sup> Ho ba mothapo o mong wa leloko la Manasse, ha tla ba dikete tse leshome le metso e robileng meno e le mmedi, e leng ba thontsweng ka mabitso ho ya bea Davida morena.",
			"32": "<sup>32</sup> Ho bana ba Issaskare, ba neng ba ithutile ho kgetha dinako ho tseba seo Baiseraele ba tshwanetseng ho se etsa, ha tla mahosana a bona a makgolo a mabedi, hammoho le banababo wona kaofela ba leng taolong ya wona.",
			"33": "<sup>33</sup> Ho Basabulone, ha tla ba dikete tse mashome a mahlano, ba tsebang ho futuha, ba hlometseng dibetsa tsohle tsa ntwa, ba tsebang ho ema ka makgotla, ba se nang pelo tse pedi.",
			"34": "<sup>34</sup> Ho Banefthali, ha tla mahosana a sekete, a tlisitse banna ba dikete tse mashome a mararo a metso e supileng, ba hlometseng thebe le lerumo.",
			"35": "<sup>35</sup> Ho Badane, ha tla ba dikete tse mashome a mabedi a metso e robileng meno e le mmedi tse nang le makgolo a tsheletseng, ba hlomeletseng ntwa.",
			"36": "<sup>36</sup> Ho Baasere, ha tla ba dikete tse mashome a mane, ba lemaletseng ho futuha, ba tsebang ntwa.",
			"37": "<sup>37</sup> Ho ba mose ho Jordane, e leng Barubene le Bagade le ba mothapo o mong wa leloko la Manasse, ha tla ba dikete tse lekgolo le nang le mashome a mabedi, ba hlometse dibetsa tsohle tsa ntwa.",
			"38": "<sup>38</sup> Bao kaofela, e leng banna ba ntwa, ba tsebang ho ema ka ho itlhopholla, ba tla Hebrone ka pelo tse phethehileng, ho bea Davida morena wa Baiseraele bohle; le Baiseraele ba bang bohle ba ne ba le pelo e le nngwe ho bea Davida morena.",
			"39": "<sup>39</sup> Ba dula teng ho Davida matsatsi a mararo, ba ntse ba eja, ba enwa; hobane banababo bona ba ne ba ba lokiseditse tsohle.",
			"40": "<sup>40</sup> Esita le ba neng ba le haufi, jwaloka Baissaskare, le Basabulone, le Banefthali, ba tlisa dijo, ba di belesitse diesele, le dikamele, le dimulete, le dipholo, e le mahobe, le diphofo, le dinkgwa tsa difeiye tse omeletseng le tsa merara e omeletseng, le veine, le ole, le dikgomo le dinku tse ngata; hobane ho ne ho thabilwe ke Baiseraele."
		},
		"13": {
			"1": "<sup>1</sup> Yaba Davida o etsa morero le balaodi ba dikete le ba makgolo, e le mahosana wohle a setjhaba.",
			"2": "<sup>2</sup> Davida a re ho phutheho yohle ya Iseraele: Ha le ka dumela, mme ha taba eo e ka tswa ho Jehova, Modimo wa rona, a re romeleng kahohle ho banababo rona ba ahileng naheng tsohle tsa Iseraele, re romele le ho baprista le Balevi, metseng ya bona le metsaneng ya bona, hore ba tle ba phuthehe le rona.",
			"3": "<sup>3</sup> Ha re boeleng re itatele areka ya Modimo wa rona; hobane ha re a ka ra e hlokomela mehleng ya Saule.",
			"4": "<sup>4</sup> Phutheho yohle ya rera hore ho etswe jwalo; etswe taba eo e ne e bile e kgahlehang mahlong a setjhaba sohle.",
			"5": "<sup>5</sup> Yaba Davida o phutha Baiseraele kaofela, ho tloha Shihore wa Egepeta ho tla fihla pela Hamathe, hore areka ya Modimo e tle e latwe Kiryathe-Jearime.",
			"6": "<sup>6</sup> Davida le Iseraele kaofela ba nyolohela Baala, e leng Kiryathe-Jearime wa Juda, ho lata teng areka ya Modimo Jehova, ya dutseng hodima dikerubime, e leng yona areka eo lebitso la Jehova le rapelwang ho yona.",
			"7": "<sup>7</sup> Ba lata areka ya Modimo tlung ya Abinadabe ka koloi e ntjha; Ussa le Akio ba ne ba tsamaisa koloi.",
			"8": "<sup>8</sup> Davida le Baiseraele kaofela ba ne ba letsa pela Modimo ka matla wohle a bona, ba bina ka diharepa, le diviole, le meropa, le disimbala, le diterompeta.",
			"9": "<sup>9</sup> Ba fihla jwalo seotlong sa Kidone; yaba Ussa o isa letsoho ho tshwara areka, kahobane dipholo di ne di tlola.",
			"10": "<sup>10</sup> Yaba Jehova o halefela Ussa, a mmolaya, kahobane a isitse letsoho la he arekeng, mme a shwela hona moo pela Modimo.",
			"11": "<sup>11</sup> Davida a swaba ke hobane Jehova a entse sekgeo Iseraeleng ka Ussa, mme a rea nqalo ya teng Perets-Ussa (sekgeo sa Ussa); le kajeno ke la teng.",
			"12": "<sup>12</sup> Tsatsing leo, Davida a tshaba Modimo, a re: Nka isa areka ya Modimo jwang ha ka?",
			"13": "<sup>13</sup> Ka baka leo, Davida ha a ka a isa areka ha hae, motseng wa Davida, a mpa a e faposetsa tlung ya Obed-Edomo wa Gathe.",
			"14": "<sup>14</sup> Areka ya Modimo ya dula ha Obed-Edomo, tlung ya hae, kgwedi tse tharo; mme Jehova a hlohonolofatsa ntlo ya Obed-Edomo le tsohle tsa hae."
		},
		"14": {
			"1": "<sup>1</sup> Hirame, morena wa Tire, a romela manqosa ho Davida, le difate tsa dikedare, le dibetli tsa majwe, le dibetli tsa difate, ho mo hahela ntlo;",
			"2": "<sup>2</sup> Davida a tseba kahoo hoba Jehova o mo tiisitse ho ba morena wa Iseraele, le hobane o phahamisitse mmuso wa hae ka baka la setjhaba sa hae sa Iseraele.",
			"3": "<sup>3</sup> Davida a boela a nyala basadi Jerusalema, mme Davida a tswala bara le baradi.",
			"4": "<sup>4</sup> Ke ana mabitso a bao a ba tswaletsweng a le Jerusalema: ke Shammua, le Shobabe, le Nathane, le Salomone;",
			"5": "<sup>5</sup> le Jibhare, le Elishua, le Elfelethe;",
			"6": "<sup>6</sup> le Noga, le Nefege, le Jafia;",
			"7": "<sup>7</sup> le Elishama, le Beeliada, le Elifelethe.",
			"8": "<sup>8</sup> Bafilesta eitse ha ba utlwa hore Davida o tlotsitswe ho ba morena wa Baiseraele bohle, Bafilesta bohle ba futuha ba batla Davida. Davida ha a utlwa, a tswa, a ya ba kgahlanyetsa.",
			"9": "<sup>9</sup> Bafilesta ba ne ba fihlile, mme ba ne ba rasa kgohlong ya Barefaime.",
			"10": "<sup>10</sup> Yaba Davida o botsa Jehova, a re: Na ke tla ya lwantsha Bafilesta? na o tla ba neela matsohong a ka na? Jehova a re ho yena: Eya, mme ke tla ba neela matsohong a hao.",
			"11": "<sup>11</sup> Ba futuhela Baale-Peratsime; mme Davida a ba qhala hona teng, a re: Jehova o entse sekgeo ka nna direng tsa ka, jwalokaha metsi a etsa sekgeo lerakong. Ka baka leo, lebitso la teng la bitswa Baale-Peratsime.",
			"12": "<sup>12</sup> Teng, Bafilesta ba lahla ditshwantsho tsa bona; Davida a laela, tsa ba tsa tjheswa mollong.",
			"13": "<sup>13</sup> Bafilesta ba boela ba rasa kgohlong.",
			"14": "<sup>14</sup> Davida a boela a botsa Modimo, mme Modimo wa re ho yena: O se ke wa ba latela; o mpe o ba kgelohe, mme o ba seeletse maloka le difate tsa monokotswai.",
			"15": "<sup>15</sup> Etlare ha o utlwa modumo wa diqi dintlheng tsa difate tsa monokotswai, o ke o ba lwantshe, hobane etlaba Modimo o se o o eteletse pele ho ya qhala makgotla a Bafilesta.",
			"16": "<sup>16</sup> Davida a etsa jwalokaha Jehova a mo laetse, mme ba qhala makgotla a Bafilesta ho tloha Gibea ho isa Gesere.",
			"17": "<sup>17</sup> Botumo ba Davida ba hasana naheng tsohle, mme Jehova a mo etsa ya tshajwang ke ditjhaba tsohle."
		},
		"15": {
			"1": "<sup>1</sup> Davida a ikgahela matlo motseng wa Davida, mme a lokisetsa areka ya Modimo ditulo, mme a e hlomela tente.",
			"2": "<sup>2</sup> Yaba Davida o re: Ha ho ya tshwanetseng ho jara areka ya Modimo haese Balevi; hobane ke bona bao Jehova a ba kgethileng hore ba jare areka ya Modimo le ho etsa tshebeletso ya yona ka ho sa feleng.",
			"3": "<sup>3</sup> Davida a phuthela Baiseraele bohle Jerusalema ho ya lata areka ya Jehova, le ho e tlisa ditulong tseo a di lokiseditseng yona.",
			"4": "<sup>4</sup> Davida a phutha bara ba Aarone le Balevi:",
			"5": "<sup>5</sup> ho bara ba Kehathe, ya eba Uriele, hlooho, le banababo, e le batho ba lekgolo le nang le mashome a mabedi;",
			"6": "<sup>6</sup> ho bara ba Merari, e le Asaia, hlooho, le banababo, batho ba makgolo a mabedi a mashome a mabedi;",
			"7": "<sup>7</sup> ho bara ba Gershome, e le Joele, hlooho, le banababo, batho ba lekgolo le mashome a mararo;",
			"8": "<sup>8</sup> ho bara ba Elitsafane, e le Shemaia, hlooho, le banababo, batho ba makgolo a mabedi;",
			"9": "<sup>9</sup> ho bara ba Hebrone, e le Eliele, hlooho, le banababo, batho ba mashome a robileng meno e le mmedi;",
			"10": "<sup>10</sup> ho bara ba Usiele, e le Amminadabe, hlooho, le banababo, batho ba lekgolo le nang le leshome le metso e mmedi.",
			"11": "<sup>11</sup> Davida a bitsa Tsadoke le Abiathare, baprista, le Balevi Uriele, Asaia, Joele, Shemaia, Eliele, le Amminadabe,",
			"12": "<sup>12</sup> a re ho bona: Le dihlooho tsa matlo a bontata Balevi; itlhwekiseng, e le lona le banababo lona, mme le late areka ya Jehova, Modimo wa Iseraele, le e tlise moo ke e lokiseditseng teng.",
			"13": "<sup>13</sup> Kahobane le ne le le siyo lekgetlong la pele, Jehova, Modimo wa rona, o entse sekgeo hara rona, kahobane re ne re sa ka ra mmatla ka ho ya ka taelo ya hae.",
			"14": "<sup>14</sup> Yaba baprista le Balevi ba itlhwekisetsa ho ya lata areka ya Jehova, Modimo wa Iseraele.",
			"15": "<sup>15</sup> Bara ba Balevi ba jara areka ya Modimo mahetleng a bona ka dithupa tse ho yona, jwalokaha Moshe a ne a laetse, ka polelo ya Jehova.",
			"16": "<sup>16</sup> Davida a bolella balaodi ba Balevi hore ba hlopholle banababo bona, e leng dibini, hore ba eme ka diletsa, e leng diviole, le diharepa, le disimbala, tseo ba tla di letsa ka matla, ba etse modumo wa thabo ka tsona.",
			"17": "<sup>17</sup> Balevi ba emisa Hemane, mora Joele, le ho banabao Hemane, Asafe, mora Berekia, le ho bara ba Merari, banababo bona, Ethane, mora Kushaia;",
			"18": "<sup>18</sup> ba ena le banababo bona ba hlopha sa bobedi, Sakaria, Bene, Jaasiele, Shemiramothe, Jehiele, Unni, Eliabe, Benaia, Maaseia, Matthithia, le Elifele, le Mikneia, le Obed-Edomo, le Jeiele, balebedi ba menyako.",
			"19": "<sup>19</sup> Dibini Hemane, Asafe le Ethane, di ne di tshwere le disimbala tsa koporo, tseo di di letsang.",
			"20": "<sup>20</sup> Sakaria, Asiele, Shemiramothe, Jehiele, Unni, Eliabe, Maaseia, le Benaia ba ne ba tshwere diharepa tse kgole tse robileng meno e le mmedi, ho tsamaisa pina ka tsona.",
			"21": "<sup>21</sup> Matthithia, le Elifele, le Mikneia, le Obed-Edomo, le Jeiele, le Asasia, ba ne ba tshwere diharepa tse kgole tse robileng meno e le mmedi ho tsamaisa pina.",
			"22": "<sup>22</sup> Kenania, motsamaisi wa Balevi bakeng sa dipina, o ne a ruta tsona, hobane o ne a le masene ho tsona.",
			"23": "<sup>23</sup> Berekia le Elkana e ne e le balebedi ba menyako bakeng sa areka.",
			"24": "<sup>24</sup> Baprista Shebania, Josafate, Nethaneele, Amasai, Sakaria, Benaia le Eliesere, ba ne ba letsa diterompeta pela areka ya Modimo, mme Obed-Edomo le Jehia e ne e le balebedi ba menyako bakeng sa areka.",
			"25": "<sup>25</sup> Davida le baholo ba Iseraele le balaodi ba dikete ba ya lata areka ya selekane sa Jehova tlung ya Obed-Edomo ka thabo.",
			"26": "<sup>26</sup> Ke ka thuso ya Modimo Balevi ba tsebileng ho jara areka ya selekane sa Jehova; ba ntoo hlaba dipoho tse supileng le dipheleu tse supileng.",
			"27": "<sup>27</sup> Haele Davida, o ne a apere kobo ya lene e ntle, le bona Balevi bohle ba jereng areka, le dibini, le Kenania, motsamaisi wa diletsa hara dibini; Davida o ne a apere efoda ya lene.",
			"28": "<sup>28</sup> Baiseraele bohle ba lata areka ya selekane sa Jehova jwalo ka ditlatse le ka ho letsa diphala, ka diterompeta le disimbala, le ka ho letsa diviole le diharepa.",
			"29": "<sup>29</sup> Areka ya selekane sa Jehova ha e fihla motseng wa Davida, Mikale, moradi wa Saule, ha a nyarela ka fensetere, a bona morena Davida a tlala, a hobela, mme a mo nyedisa ka pelong ya hae."
		},
		"16": {
			"1": "<sup>1</sup> Ba tlisa areka ya Jehova jwalo, mme ba e bea tenteng eo Davida a e hlometseng yona; ha hlajwa mahlabelo a ditjheso le a teboho pela Modimo.",
			"2": "<sup>2</sup> Eitse hobane Davida a qetele ho hlaba mahlabelo a ditjheso le a teboho, a hlohonolofatsa setjhaba ka lebitso la Jehova;",
			"3": "<sup>3</sup> a ntoo abela Baiseraele bohle, banna le basadi, e mong le e mong polokwe ya bohobe, le sesuma sa nama, le senkgwana sa morara.",
			"4": "<sup>4</sup> Yaba o laela Balevi ba bang hore ba phethe tsa tshebeletso pela areka ya Jehova, le ho rapela, le ho boka, le ho rorisa Jehova, Modimo wa Iseraele.",
			"5": "<sup>5</sup> E moholo e ne e le Asafe; ba mo hlahlamang ke Sakaria, le Jeiele, le Shemiramothe, le Jehiele, le Matthithia, le Eliabe, le Benaia, le Obed-Edomo, le Jeiele, ba tshwere diletsa, le diviole, le diharepa; Asafe yena o ne a letsa disimbala.",
			"6": "<sup>6</sup> Baprista Benaia le Jahasiele ba ne ba ntse ba letsa diterompeta pela areka ya selekane sa Modimo.",
			"7": "<sup>7</sup> Ke tsatsing lona leo Davida a qadileng ho laela Asafe le banababo hore ba boke Jehova:",
			"8": "<sup>8</sup> Rorisang Jehova, le rapele lebitso la hae; tsebisang hara ditjhaba diketso tsa hae tse phahameng.",
			"9": "<sup>9</sup> Mmineleng, le mminele dithoko, le nahane mehlolo ya hae kaofela.",
			"10": "<sup>10</sup> Ithoriseng ka lebitso la hae le halalelang; pelo tsa ba batlang Jehova a di ke di thabe.",
			"11": "<sup>11</sup> Batlang Jehova le matla a hae, le batle sefahleho sa hae kamehla.",
			"12": "<sup>12</sup> Hopolang mehlolo eo a e entseng, le meeka ya hae, le dikahlolo tse tswileng molomong wa hae,",
			"13": "<sup>13</sup> lona, moloko wa Iseraele, mohlanka wa hae; lona, bana ba Jakobo, bakgethwa ba hae!",
			"14": "<sup>14</sup> Jehova ke yena Modimo wa rona; dikahlolo tsa hae di aparela lefatshe lohle.",
			"15": "<sup>15</sup> Le nne le hopole selekane sa hae kamehla, le lentswe leo a le laoletseng meloko e sekete;",
			"16": "<sup>16</sup> e leng selekane seo a neng a se hlabe le Abrahama, le kano eo a neng a e anetse Isaaka,",
			"17": "<sup>17</sup> eo a kileng a e etsa komelo e tiileng ho Jakobo, le selekane se sa feleng ho Iseraele,",
			"18": "<sup>18</sup> a re: Ke tla o nea lefatshe la Kanana, e be kabelo ya lefa la lona.",
			"19": "<sup>19</sup> E ne e sa le batho ba seng bakae; ba ne ba le banyenyane, e ne e sa le basele ho lona,",
			"20": "<sup>20</sup> ba ntse ba phanyaphanya hara ditjhaba le mebuso le merabe.",
			"21": "<sup>21</sup> Empa ha a ka a dumella motho ho ba tuba, mme a laya marena ka baka la bona, a re:",
			"22": "<sup>22</sup> Se ameng batlotsuwa ba ka, mme le se ke la etsa baporofeta ba ka hampe.",
			"23": "<sup>23</sup> Binelang Jehova, lona mafatshe wohle; le bolele poloko ya hae ka matsatsi wohle.",
			"24": "<sup>24</sup> Le bolele kganya ya hae hara ditjhaba, le mehlolo ya hae hara mefuta yohle.",
			"25": "<sup>25</sup> Hobane Jehova o moholo, mme o lokelwa ke ho roriswa ka ho fetisisa; o tshabeha ho fetisa medimo kaofela;",
			"26": "<sup>26</sup> hobane medimo yohle ya ditjhaba ke ditshwantsho feela, empa Jehova o entse mahodimo.",
			"27": "<sup>27</sup> Kganya le letlotlo di pela hae, matla le thabo di sehalalelong sa hae.",
			"28": "<sup>28</sup> Lona, mefuta ya ditjhaba, busetsang ho Jehova, busetsang ho Jehova kganya le matla.",
			"29": "<sup>29</sup> Busetsang ho Jehova kganya e tshwanelang lebitso la hae; tlisang dinyehelo, mme le hlahe pela hae; kgumamang pela Jehova ka bokgabane bo halalelang.",
			"30": "<sup>30</sup> Tlakasela pela hae, wena, lefatshe lohle. Lefatshe le lona le a tiya, ha le sisinyehe.",
			"31": "<sup>31</sup> Mahodimo a ke a thabe, mme lefatshe le hlasimolohe; a ho ke ho bolelwe hara ditjhaba, ho thwe: Jehova o a busa.",
			"32": "<sup>32</sup> Lewatle a le ke le dume le tsohle tse ho lona; dithota di ke di hlasimolohe le tse ho tsona kaofela!",
			"33": "<sup>33</sup> Difate tsa meru di etse ditlatse tsa thabo pela Jehova, hobane o ya tla, a tle a ahlole lefatshe.",
			"34": "<sup>34</sup> Rorisang Jehova, hobane o molemo, hobane mohau wa hae o hlola ka ho sa feleng.",
			"35": "<sup>35</sup> Ereng: Re lopolle, Modimo wa topollo ya rona, o re bokelle hammoho, o re ntshe hara ditjhaba, re tle re binele lebitso la hao le halalelang, mme re ithorise ka ho o roka.",
			"36": "<sup>36</sup> Ho roriswe Jehova, Modimo wa Iseraele, ho tloha ho sa simoloheng ho isa ho sa feleng. Setjhaba sohle sa re: Amen! sa rorisa Jehova.",
			"37": "<sup>37</sup> Davida a siya Asafe le banababo, hona teng, pela areka ya selekane sa Jehova, hore ba nne ba etse tsa tshebeletso pela areka kamehla, mosebetsi o mong le o mong ka tsatsi la wona;",
			"38": "<sup>38</sup> hape, a bea Obed-Edomo le banababo, e le batho ba mashome a tsheletseng a metso e robileng meno e le mmedi, ke re, Obed-Edomo mora Jeduthune, le Hosa, e tle e be balebedi ba menyako.",
			"39": "<sup>39</sup> Hape, a bea moprista Tsadoke, le baprista ba mofuta wa habo, pela tabernakele ya Jehova, nqalong e phahameng e leng Gibeone,",
			"40": "<sup>40</sup> hore ho yona ho nne ho hlahisetswe Jehova ditjheso aletareng ya mahlabelo, hosasa le mantsiboya, ka ho ya ka tsohle tse ngodilweng molaong wa Jehova, oo a o beetseng Iseraele.",
			"41": "<sup>41</sup> Ba ne ba na le Hemane le Jeduthune le Balevi ba bang ba kgethilweng, ba thontsweng ka mabitso a bona hore ba nne ba rorise Jehova; hobane mohau wa hae ke o sa feleng.",
			"42": "<sup>42</sup> Hemane le Jeduthune ba ne ba le ho bona, diterompeta le disimbala le diletsa tsa Modimo ba di tshwaretse ba di letsang; haele bara ba Jeduthune, ba ne ba eme monyako.",
			"43": "<sup>43</sup> Yaba setjhaba sohle se ikela e mong le e mong tlung ya hae; le yena Davida a ikela a tle a hlohonolofatse ntlo ya hae."
		},
		"17": {
			"1": "<sup>1</sup> Eitse Davida ha a se a kene tlung ya hae, Davida a re ho moporofeta Nathane: Bona hle, nna ke ahile tlung ya dikedare, athe areka ya selekane sa Modimo e dutse tlasa masela.",
			"2": "<sup>2</sup> Nathane a re ho morena: Etsa tsohle tse ka pelong ya hao, hobane Jehova o na le wena.",
			"3": "<sup>3</sup> Bosiung bona boo, lentswe la Jehova la tlela Nathane, la re:",
			"4": "<sup>4</sup> Tsamaya, o bolelle mohlanka wa ka Davida, o re: Jehova o re: Hase wena o tla nkgahela ntlo eo ke tla dula ho yona.",
			"5": "<sup>5</sup> Athe ha ke eso ho dule tlung, haesale ke nyolla Iseraele Egepeta ho fihlela jwale, ke mpa ke nnile ka tswa tenteng e nngwe ka kena ho e nngwe, ka tloha tulong se seng ka kena ho se seng.",
			"6": "<sup>6</sup> Hohle moo ke tsamaileng hammoho le Baiseraele bohle, na nkile ka bolela taba leha e le ho e mong wa baahlodi ba Iseraele, bao ke ba laetseng ho disa setjhaba sa ka, ka re: Na ha le nkgahele ntlo ya dikedare keng na?",
			"7": "<sup>7</sup> Jwale, bolella mohlanka wa ka Davida, o re: Ho itswe ke Jehova wa makgotla: Ke ne ke o late merakeng, kamora dinku, hore ke o bee motsamaisi wa setjhaba sa ka sa Iseraele.",
			"8": "<sup>8</sup> Ke nnile ka ba le wena le hohle moo o tsamaileng; ke bile ka fedisa dira tsohle tsa hao pela hao, mme ka o etsetsa lebitso le leholo le kang mabitso a ba baholo ba leng teng lefatsheng.",
			"9": "<sup>9</sup> Ke lokiseditse setjhaba sa ka sa Iseraele bodulo, mme ke se hlomile, se bile se se se dutse ha habo sona; ha se ka ke sa hlola se ferekana, le bakgopo ha ba sa tla hlola ba se fedisa, jwaloka pele,",
			"10": "<sup>10</sup> leha e le jwaloka mohla ke neng ke beile baahlodi ho disa setjhaba sa ka sa Iseraele; ke kokobeditse dira tsa hao tsohle, mme ke o tsebisitse hore Jehova o tla o hahela ntlo.",
			"11": "<sup>11</sup> Etlare mohla matsatsi a hao a phethehang a ho orohela ho bontatao, ke tla phahamisa lesika la hao kamora hao, e mong wa bara ba hao, mme ke tla tiisa mmuso wa hae.",
			"12": "<sup>12</sup> Ke yena ya tla nkgahela ntlo, mme ke tla tiisa terone ya hae ka ho sa feleng.",
			"13": "<sup>13</sup> Ke tla ba ntatae, yena e be mora ka; nke ke ka mo amoha mohau wa ka, jwalokaha ke ne ke o amohe ba neng ba le pele ho wena;",
			"14": "<sup>14</sup> ke mpa ke tla mmea tlung ya ka le mmusong wa ka ka ho sa feleng, mme terone ya hae e tla tiya ka ho sa feleng.",
			"15": "<sup>15</sup> Nathane a phetela Davida taba tseo tsohle, le tsohle tsa tshenolo eo.",
			"16": "<sup>16</sup> Yaba morena Davida o ya, a dula pela Jehova, a re: Oho, Jehova Modimo, nna ke mang, mme ntlo ya ka keng, ha u ntlisitse ho fihla mona?",
			"17": "<sup>17</sup> Le taba eo eka e nyenyane pela hao, Modimo; o bile o buile le bakeng sa ntlo ya mohlanka wa hao hodima motsotso o molelele, mme o ntadimile jwalokaha ho tadingwa motho e moholo, wena o leng Modimo Jehova!",
			"18": "<sup>18</sup> Mme Davida a ka boela a bolela dife hodima kganya eo o e neang mohlanka wa hao?",
			"19": "<sup>19</sup> O tseba mohlanka wa hao, Jehova; ke ka baka la mohlanka wa hao le la thato ya hao o entseng taba tseo tsohle tse kgolo, hore o mo tsebise tseo tsohle tse kgolo!",
			"20": "<sup>20</sup> Oho Jehova, ha ho ya kang wena, ha ho le Modimo haese wena, jwalokaha re ikutlwetse ka tsebe tsa rona.",
			"21": "<sup>21</sup> Ke sefe setjhaba se lefatsheng se kang tjhaba sa hao sa Iseraele, seo Modimo o tlileng ho itopollela sona, hore e tle e be setjhaba sa wona, le ho iketsetsa lebitso ka ho sebetsa ntho tse kgolo, tse tshabehang, ha o ntse o leleka ditjhaba pela setjhaba sa hao seo o se lopolotseng Egepeta?",
			"22": "<sup>22</sup> O beile setjhaba sa hao sa Iseraele hore e nne e be setjhaba sa hao ka ho sa feleng, mme wena, Jehova, o bile Modimo wa sona.",
			"23": "<sup>23</sup> Mme jwale, Jehova, lentswe leo o le boletseng bakeng sa mohlanka wa hao le bakeng sa ntlo ya hae, le ke le netafale ka ho sa feleng, mme o etse kamoo o boletseng kateng.",
			"24": "<sup>24</sup> Le ke le netafale, hore lebitso la hao le tle le tlotliswe kamehla ka ho re, Jehova wa makgotla ke Modimo wa Iseraele, ke Modimo ho Iseraele! Le yona ntlo ya mohlanka wa hao Davida e ke e tiiswe pela hao.",
			"25": "<sup>25</sup> Hobane ke wena ka sebele, Modimo wa ka, o senoletseng mohlanka wa hao hore o tla mo hahela ntlo: ke ka baka leo ke le mohlanka wa hao, ke betileng pelo ho, bea thapelo pela hao.",
			"26": "<sup>26</sup> Mme jwale Jehova, ke wena Modimo; o boletse taba e molemo bakeng sa mohlanka wa hao.",
			"27": "<sup>27</sup> Mme jwale, ako hlohonolofatse ntlo ya mohlanka wa hao, hore e nne e hlole e le teng ka ho sa feleng pontsheng ya hao; hobane seo o se hlohonolofatsang, Jehova, eba se hlohonolofetse ka ho sa feleng."
		},
		"18": {
			"1": "<sup>1</sup> Kamora tseo, Davida a fenya Bafilesta, a ba kokobetsa, mme a amoha Bafilesta Gathe le metsana ya naha ya teng.",
			"2": "<sup>2</sup> A hlola le Bamoabe, mme Bamoabe ya eba bafo ba Davida, ba mo ntshetsa lekgetho.",
			"3": "<sup>3</sup> Davida a hlola le Hadaresere, morena wa Tsoba pela Hamathe, ha a ne a ya hloma borena ba hae pela noka ya Eufrate;",
			"4": "<sup>4</sup> Davida a hapa makoloi a hae a sekete se le seng, le bapalami ba dikete tse supileng, le ba maoto ba dikete tse mashome a mabedi. Davida a kgaola mesifa ya maoto a dipere tsa makoloi kaofela, a ipolokela tsa makoloi a lekgolo feela.",
			"5": "<sup>5</sup> Yaba Basiria ba Damaseka ba tla, ba re ba tlatsa Hadaresere, morena wa Tsoba; Davida a bolaya Basiria ba dikete tse mashome a mabedi a metso e mmedi.",
			"6": "<sup>6</sup> Davida a ntoo bea balebedi naheng ya Siria ya Damaseka, mme Basiria ya eba bafo ba Davida, ba mo ntshetsa lekgetho. Jehova a nna a boloka Davida jwalo hohle moo a yang.",
			"7": "<sup>7</sup> Davida a hapa dithebe tsa kgauta tse neng di le ho bahlanka ba Hadaresere, mme a ya le tsona Jerusalema.",
			"8": "<sup>8</sup> Hape, Davida a nka metseng ya Hadaresere, Thibhathe le Kune, koporo e ngata haholo, eo Salomone a kileng a etsa letsha la koporo le ditshiya le dipitsa tsa koporo ka yona.",
			"9": "<sup>9</sup> Eitse ha Thou, morena wa Hamathe, a utlwa hoba Davida o hlotse makgotla wohle a Hadaresere, morena wa Tsoba,",
			"10": "<sup>10</sup> a romela mora hae Hadorame ho morena Davida ho ya mo dumedisa le ho mo leboha, kamoo a lwantshitseng Hadaresere kateng, a bileng a mo hlotse; hobane Hadaresere o ne a lwantsha Thou kamehla. Hadorame a tlisa diphahlo tse mefutafuta ya kgauta le ya silefera le ya koporo.",
			"11": "<sup>11</sup> Morena Davida a di kgethela Jehova, hammoho le silefera le kgauta tseo a neng a di hapile ditjhabeng tsohle, e leng ho Edomo, le Moabe, le bana ba Ammone, le Bafilesta, le Amaleke.",
			"12": "<sup>12</sup> Abishai mora Tseruja, a qhala Baedomo Thoteng ya Letswai, e le batho ba dikete tse leshome le metso e robileng meno e le mmedi.",
			"13": "<sup>13</sup> A bea balebedi Edomo, mme Baedomo bohle ya eba bafo ba Davida. Jehova a nna a boloka Davida jwalo hohle moo a yang.",
			"14": "<sup>14</sup> Davida ya eba morena wa Baiseraele bohle, mme a nna a etsa setjhaba sa hae kaofela ka ho loka le ka bokgabane.",
			"15": "<sup>15</sup> Joabe, mora Tseruja, e ne e le molaodi wa makgotla; Josafate, mora Akilude, e ne e le mmoloki wa mangolo a mmuso;",
			"16": "<sup>16</sup> Tsadoke, mora Akitube, le Abimeleke, mora Abiathare, e ne e le baprista; Shavsha e ne e le mongodi;",
			"17": "<sup>17</sup> Benaia, mora Jehojada, e ne e le molaodi wa Bakerethe le Bapelethe; bara ba Davida bona e ne e le ba kapele ho ba bang pela morena."
		},
		"19": {
			"1": "<sup>1</sup> Kamora tseo, Nahashe, morena wa bana ba Ammone, a timela, mme mora wa hae ya eba morena tulong tsa hae.",
			"2": "<sup>2</sup> Davida a re: Ke tla bontsha Hanune, mora Nahashe, mosa wa ka, kahobane le ntatae o kile a mpontsha mosa wa hae. Kahoo, Davida a roma bahlanka ba tle ba mo tshedise bakeng sa ho shwa ha ntatae. Bahlanka ba Davida ba fihla naheng ya bana ba Ammone ho Hanune, ba re, ba tla mo tshedisa.",
			"3": "<sup>3</sup> Empa baholo ba bana ba Ammone ba re ho Hanune: Ha Davida a rometse batshedisi ho wena, hleka o re, ke ka ho hlonepha ntatao na? Ha bahlanka ba hae ba tlile ho wena, na hase ka baka la ho tla qamakisisa motse, le ho timetsa, le ho hlwela naha na?",
			"4": "<sup>4</sup> Yaba Hanune o tshwara bahlanka ba Davida, a ba beola, a seha diaparo tsa bona kalehare ho isa lethekeng, mme a ba kgutlisa ba le jwalo.",
			"5": "<sup>5</sup> Davida a behelwa se entsweng ho banna ba hae; yaba o romela ho ba kgahlanyetsa, hobane banna bao ba ne ba le dihlong haholo; mme morena a re: Dulang Jeriko, ditedu tsa lona di be di mele, le ntoo boya.",
			"6": "<sup>6</sup> Eitse ha bana ba Ammone ba bona hobane ba ikentse ba nkgang hampe ho Davida, Hanune le bana ba Ammone ba romela ditalenta tse sekete se le seng tsa silefera ho adima makoloi le bapalami ho Basiria ba Mesopotamia, le ho Basiria ba Maaka le ba Tsoba.",
			"7": "<sup>7</sup> Ba adima makoloi a dikete tse mashome a mararo a metso e mmedi; le yena morena wa Maaka le setjhaba sohle sa hae ba tla, ba emisa pela Medeba. Bana ba Ammone ba phutheha metseng ya bona, mme ba tloha ho ya ntweng.",
			"8": "<sup>8</sup> Davida ha a utlwa, a roma Joabe le sera sohle sa banna ba ntwa.",
			"9": "<sup>9</sup> Bana ba Ammone ba tswa, ba hlopholla ntwa pela kgoro ya motse; haele marena a tlileng, a ema thoko naheng, sebakeng sa wona.",
			"10": "<sup>10</sup> Joabe ha a bona hoba o ya tlelwa ke ntwa tse pedi, e nngwe e le kapele, e nngwe e tla kamora hae, a kgetha mokgetho wohle wa Iseraele, a ba lokisa hore ba tadime ntwa ya Basiria;",
			"11": "<sup>11</sup> ba setseng ba setjhaba a ba nea Abishai, ngwanabo, mme a ba lokisa hore ba bone bana ba Ammone.",
			"12": "<sup>12</sup> A re ho yena: Ha Basiria ba ka nkimela, o tle, o tlatsetse ho nna; ha bana ba Ammone ba o imela, ke tla tlatsetsa ho wena.",
			"13": "<sup>13</sup> O iphe matla, mme re etseng ka senna sebakeng sa setjhaba sa heso le sa metse ya Modimo wa rona, mme Jehova a ke a etse kamoo a ratang kateng.",
			"14": "<sup>14</sup> Yaba Joabe le setjhaba sohle se ho yena ba atamela ho lwantsha Basiria, mme Basiria ba baleha pela hae.",
			"15": "<sup>15</sup> Eitse ha bana ba Ammone ba bona hobane Basiria ba balehile, le bona ba baleha pela Abishai, ngwanabo Joabe, mme ba kena hae motseng. Joabe yena a boela Jerusalema.",
			"16": "<sup>16</sup> Eitse ha Basiria ba bona hobane ba hlotswe ke Baiseraele, ba romela manqosa ho mema Basiria ba mose wane wa noka; Shofake, molaodi wa sera sa Hadaresere, e ne e le motsamaisi wa bona.",
			"17": "<sup>17</sup> Davida ha a tsebiswa, a phutha Baiseraele bohle, a tshela Jordane, a tla ho bona, mme a hlopholla sera sa hae ho ba lwantsha. Davida a hlopholla sera sa hae ho lwantsha Basiria, mme ba kopanya ntwa le yena.",
			"18": "<sup>18</sup> Basiria ba baleha pela Baiseraele, mme Davida a timetsa banna ba dikete tse supileng tsa makoloi a Basiria, le banna ba maoto ba dikete tse mashome a mane, a bolaya le Shofake, molaodi wa sera.",
			"19": "<sup>19</sup> Eitse ha bahlanka ba Hadaresere ba bona hoba ba hlotswe ke Baiseraele, ba etsa kgotso le Davida, mme ba mo sebeletsa. Mme Basiria ha ba ka ba hlola ba dumela ho tlatsa bana ba Ammone."
		},
		"20": {
			"1": "<sup>1</sup> Eitse hobane lemo seo se fete, ka nako eo marena a atisang ho futuha ka yona, Joabe a tsamaisa makgotla a ntwa, mme a senya lefatshe la bana ba Ammone, a ntoo ya thibella Rabba; Davida yena a sala Jerusalema. Joabe a hlola Rabba, a o heletsa.",
			"2": "<sup>2</sup> Davida a rola morena wa teng moqhaka wa hae, a o fihlela o le boima ba talenta e le nngwe ya kgauta, o bile o na le mahakwe a bohlokwa le hona, mme wa rweswa Davida hloohong. Hape, a ya le kgapo e kgolo haholo ya motse.",
			"3": "<sup>3</sup> Haele batho ba fihletsweng teng, a ba ntshetsa ntle, mme a ba harola ka disaga le ka diege tsa tshepe le ka dilepe. Davida a etsa jwalo ka metse yohle ya bana ba Ammone; Davida le setjhaba sohle ba ntoo boela Jerusalema.",
			"4": "<sup>4</sup> Kamora tseo, ntwa ya lwana le Bafilesta Gesere; teng Sibekkai Mohusha a bolaya Sippai, e mong wa bana ba Barefaime; mme teng ba kokobetswa.",
			"5": "<sup>5</sup> Ya boela ya lwana le Bafilesta. Yaba Elkanane, mora Jaire, o bolaya Lahmi, ngwanabo Goliathe wa Gathe, ya neng a na le lerumo leo theko ya lona e neng e le ka ka sefate sa selohi.",
			"6": "<sup>6</sup> Hapehape, ntwa ya lwana Gathe. Teng, ho ne ho na le monna e molelele, eo letsoho la hae le leng le le leng le leoto le leng le le leng le neng le na le menwana e tsheletseng, e etsa mashome a mabedi a metso e mene; le yena e ne e le wa mofuta wa Barefaime.",
			"7": "<sup>7</sup> Eitse ha a ntse a phephetsa Iseraele, Jonathane, mora Shimea ngwanabo Davida, a mmolaya.",
			"8": "<sup>8</sup> Bao e ne e le ba mofuta wa Barefaime ba Gathe; ba eshwa ka matsoho a Davida le a bahlanka ba hae."
		},
		"21": {
			"1": "<sup>1</sup> Yaba Satane o tsohela Iseraele matla, mme a susumetsa Davida hore a bale Baiseraele.",
			"2": "<sup>2</sup> Davida a re ho Joabe le ho balaodi ba setjhaba: Eyang, le bale Baiseraele ho qala ka Beerseba ho ya fihla Dane, mme le ntsebise palo ya bona hore ke e tsebe.",
			"3": "<sup>3</sup> Joabe a re: Jehova a ke a atise setjhaba, se ke se fete bongata ba sona ba jwale halekgolo! Oho, morena, monga ka, ha ke re, kaofela ha bona ke bahlanka ba monga ka na? Ke ka baka lang ha monga ka a batla hoo? Na o rata keng ho bea molato hodima Baiseraele?",
			"4": "<sup>4</sup> Leha ho le jwalo, taelo ya morena ya hlola Joabe. Joabe a tswa, a ya le Baiseraele bohle, a ntoo kgutlela Jerusalema.",
			"5": "<sup>5</sup> Joabe a bolella Davida palo ya bongata ba setjhaba: Iseraeleng ho ne ho le banna ba tsomolang sabole ba sekete sa dikete se nang le lekgolo le le leng la dikete; haele Juda, ho ne ho na le banna ba tsomolang sabole ba dikete tse makgolo a mane a mashome a supileng.",
			"6": "<sup>6</sup> A mpa a sa ka a bala Levi le Benjamine; hobane taelo eo ya morena e ne e le e kgesehang ho Joabe.",
			"7": "<sup>7</sup> Taba eo ya eba mpe mahlong a Modimo, mme a otla Iseraele.",
			"8": "<sup>8</sup> Davida a re ho Modimo: Ke sitilwe haholo ka seo ke se entseng. Empa jwale, oho, tlosa bokgopo ba mohlanka wa hao, hobane ke entse ka bothoto bo boholo.",
			"9": "<sup>9</sup> Yaba Jehova a bolella Gade, seboni sa Davida, a re:",
			"10": "<sup>10</sup> Eya, o bolelle Davida, o re: Jehova o re: Ke o behela taba tse tharo; ikgethele e nngwe ho tsona, ke o etse ka yona.",
			"11": "<sup>11</sup> Gade a ya ho Davida, a re ho yena: Jehova o re: Ikgethele seo o se ratang,",
			"12": "<sup>12</sup> kapa lemo tse tharo tsa tlala, kapa kgwedi tse tharo tsa ho bolawa ke dira tsa hao, o qetwe ke sabole ya ba o hloileng, kapa tshiu tse tharo tseo ka tsona lerumo la Jehova le lefu la sewa le tla senya naheng ya Baiseraele, mme lengeloi la Jehova le tla timetsa batho naheng yohle ya bona. Jwale, bona hore na ke tla busetsa karabo efe ho ya nthomileng!",
			"13": "<sup>13</sup> Davida a re ho Gade: Ke tsietswe haholo; a ke mpe ke wele matsohong a Jehova, etswe mehauhelo ya hae e le mengata; ke mpe ke se ke ka wela matsohong a batho.",
			"14": "<sup>14</sup> Jehova a romela lefu le leng la sewa Iseraeleng, mme ha eshwa batho ba dikete tse mashome a supileng ba Iseraele.",
			"15": "<sup>15</sup> Modimo wa romela lengeloi Jerusalema ho timetsa batho ba teng; la re le sa ntsane le bolaya, Jehova a bona, a swaba ke tshenyeho eo, mme a re ho lengeloi le bolayang: Ho lekane; honyetsa letsoho la hao jwale! Lengeloi la Jehova le ne le eme pela seotlo sa Ornane Mojebuse.",
			"16": "<sup>16</sup> Davida eitse ha a emisa mahlo, a bona lengeloi la Jehova le eme pakeng tsa lefatshe le lehodimo, le tshwere sabole e bolatsi letsohong la lona, le e otlolletse Jerusalema. Yaba Davida le baholo, ba apere masela a mahwashe, ba itahla fatshe ka difahleho tsa bona.",
			"17": "<sup>17</sup> Davida a re ho Modimo: Na hase nna ke laetseng hore ho balwe setjhaba na? Ke nna ke sitilweng, ke nna ke entseng bobe; dinku tseo tsona di entseng na? Jehova, Modimo wa ka, tsoho la hao le otle nna le ntlo ya ntate, mme le se ke la nna la kenya lefu setjhabeng sa hao!",
			"18": "<sup>18</sup> Yaba lengeloi la Jehova le laela Gade hore a bolelle Davida hore Davida a nyolohe, a hahele Jehova aletare seotlong sa Ornane Mojebuse.",
			"19": "<sup>19</sup> Ka polelo ya Gade, eo a e boletseng ka lebitso la Jehova, Davida a nyoloha.",
			"20": "<sup>20</sup> Ornane ha a reteleha, a bona lengeloi; mme ba ipata e le yena le bara ba hae ba bane; Ornane o ne a pola koro.",
			"21": "<sup>21</sup> Davida a fihla ho Ornane; Ornane, eitse ha a tadima, a bona Davida, mme a tswa seotlong, a inamela fatshe ka sefahleho pela Davida.",
			"22": "<sup>22</sup> Yaba Davida o re ho Ornane: Nnee baka sa seotlo sena, hore ke tle ke hahele Jehova aletare teng; nnee sona bakeng sa theko ya sona e phethehileng, hore lefu lena le tloswe hara setjhaba.",
			"23": "<sup>23</sup> Ornane a re ho Davida: Inkele sona, mme morena, monga ka, a etse kahohle kamoo a ratang kateng. Bona, ke o nea pholo tseo ho etsa ditjheso ka tsona, le mesebetsi ya ho pola ho etsa patsi, le koro ho etsa nyehelo; ke o nea tsona kaofela.",
			"24": "<sup>24</sup> Morena Davida a re ho Ornane: Tjhe, ke tla di reka ka theko ya tsona; hobane nke ke ka isetsa Jehova seo e leng sa hao, mme nke ke ka hlahisa setjheso seo ke sa se ntshetsang letho.",
			"25": "<sup>25</sup> Ha ho le jwalo, Davida a nea Ornane theko ya baka seo, a mo nea dishekele tsa kgauta tse makgolo a tsheletseng.",
			"26": "<sup>26</sup> Teng, Davida a hahela Jehova aletare, mme a hlahisa mahlabelo a ditjheso le a teboho teng; a rapela Jehova, mme a mo arabela ka ho theolela mollo aletareng ya ditjheso, o etswa lehodimong.",
			"27": "<sup>27</sup> Mme Jehova a laela lengeloi hore le busetse sabole ya lona selateng.",
			"28": "<sup>28</sup> Mohlang oo, Davida eitse ha a bona hobane Jehova o mo utlwile a le seotlong sa Ornane Mojebuse, a hlaba mahlabelo teng.",
			"29": "<sup>29</sup> Motsotsong oo, tabernakele ya Jehova, e na beng e entswe ke Moshe nahathothe, le aletare ya ditjheso, di ne di le nqalong e phahameng ya Gibeone;",
			"30": "<sup>30</sup> mme Davida o ne a sitilwe ho ya pela aletare eo ho botsa Modimo, hobane a ne a tshositswe ke sabole ya lengeloi la Jehova."
		},
		"22": {
			"1": "<sup>1</sup> Yaba Davida o re: Ke hona mona moo ntlo ya Jehova Modimo, ke hona mona moo aletare ya ditjheso tsa Iseraele.",
			"2": "<sup>2</sup> Yaba Davida o laela hore ho phuthwe baditjhaba bohle ba lefatsheng la Iseraele; a bea dibetli ho betla majwe a ho haha ntlo ya Modimo.",
			"3": "<sup>3</sup> Hape, Davida o ne a se a lokisitse tshepe e ngata ya ho etsa dipekere tsa mamati a menyako le tsa ho kopanya tse kopanngwang, le koporo e ngata eo ho seng kamoo boima ba yona bo neng bo ka lekanngwa kateng,",
			"4": "<sup>4</sup> le difate tsa dikedare tse se nang palo; hobane Basidone le Batire ba ne ba tliseditse Davida difate tsa dikedare tse ngata.",
			"5": "<sup>5</sup> Davida o ne a re: Mora ka Salomone o sa le motjha, o sa le monyenyane, mme ntlo e tla hahelwa Jehova e ka kgona e phahame haholo ka botumo le ka kganya mafatsheng wohle; ha ho le jwalo, ke tla mo lokisetsa yona. Kahoo, Davida a lokisa tse ngata, a eso ho shwe.",
			"6": "<sup>6</sup> Yaba o bitsa Salomone, mora wa hae, a mo laela ho hahela Jehova, Modimo wa Iseraele, ntlo.",
			"7": "<sup>7</sup> Davida a re ho Salomone, mora wa hae: E ne e le kgopolo ya pelo ya ka hore ke hahele lebitso la Jehova, Modimo wa ka, ntlo.",
			"8": "<sup>8</sup> Empa lentswe la Jehova le tlile ho nna, la re: O tsholotse madi a mangata, mme o lwanne ntwa tse kgolo; ha o na ho hahela lebitso la ka ntlo, etswe o tsholotse madi a mangata lefatseheng pela ka.",
			"9": "<sup>9</sup> Bona, o tla tswallwa mora; yena e tla ba motho wa kgotso; ke tla mo nea phomolo ho dira tsohle tsa hae ka nqa tsohle; lebitso la hae e tla ba Salomone (wa kgotso), mme ke tla bea kgotso le boiketlo Iseraeleng ka matsatsi a hae.",
			"10": "<sup>10</sup> Ke yena ya tla hahela lebitso la ka ntlo; e tla ba mora wa ka, nna ke be ntatae; mme ke tla tiisa terone ya borena ba hae Iseraeleng ka ho sa feleng.",
			"11": "<sup>11</sup> Jwale, mora ka, Jehova a ke a be le wena, hore o tle o atlehe, mme o hahe ntlo ya Jehova, Modimo wa hao, kamoo a felang a boletse kateng, bakeng sa hao.",
			"12": "<sup>12</sup> Feela, Jehova a ke a o nee kelello le kutlwisiso, a etse hore o rene Iseraeleng, o ntse o bolokile molao wa Jehova, Modimo wa hao.",
			"13": "<sup>13</sup> Ke hona o tla atleha, ha o iponela hore o etse ka melao le ditaelo tseo Jehova a di laetseng Moshe bakeng sa Iseraele. Iphe matla, o tiye; se tshabe, mme o se ke wa tshoha.",
			"14": "<sup>14</sup> O ka bona, bonyenyaneng ba ka, ke lokiseditse ntlo ya Jehova kgauta e ditalenta tse dikete tse lekgolo, le silefera e ditalenta tse dikete tse sekete, le koporo le tshepe tse se nang ho lekanngwa, kahobane di ngata haholo. Ke lokisitse le difate le majwe, mme o tla boela o ekeletse teng.",
			"15": "<sup>15</sup> Hape, o na le basebetsi ba bangata, dibetli tsa majwe, dihahi, le dibetli tsa difate, le batho ba bangata ba masene mesebetsing yohle.",
			"16": "<sup>16</sup> Haele kgauta, le silefera, le koporo, le tshepe, ha di na palo. Ema, o sebetse, mme Jehova a ke a be le wena.",
			"17": "<sup>17</sup> Yaba Davida o laela balaodi bohle ba Iseraele ho thusa Salomone, mora wa hae, a re:",
			"18": "<sup>18</sup> Na Jehova, Modimo wa lona, ha a na le lona na? Hape, na ha a ka a le fa phomolo ka nqa tsohle na? Hobane o fela a neile baahi ba lefatshe matsohong a ka, mme lefatshe le kokobeditswe pela Jehova le pela setjhaba sa hae.",
			"19": "<sup>19</sup> Jwale, sekamisetsang pelo tsa lona le meya ya lona ho batla Jehova, Modimo wa lona; le eme, mme le hahe sehalalelo sa Jehova Modimo, hore le tle le tlise areka ya selekane sa Jehova le diphahlo tse kgethehileng tsa Modimo tlung e tla hahelwa lebitso la Jehova."
		},
		"23": {
			"1": "<sup>1</sup> Davida o ne a se a tsofetse, a hodile haholo; yaba o bea Salomone, mora wa hae, morena wa Iseraele.",
			"2": "<sup>2</sup> A phutha balaodi bohle ba Iseraele, le baprista, le Balevi.",
			"3": "<sup>3</sup> Balevi ba balwa ho tloha ho ba dilemo tse mashome a mararo le ba kapele; ha ba balwa ka dihlooho, ha fumanwa batho ba dikete tse mashome a mararo a metso e robileng meno e le mmedi.",
			"4": "<sup>4</sup> A re: Ho bona, ho ke ho nkwe ba kete tse mashome a mabedi a metso e mene ba tla tsamaisa tshebetso ya ntlo ya Jehova; hape, ho nkwe ba kete tse tsheletseng ho ba etsa bahlahlobi le baahlodi;",
			"5": "<sup>5</sup> balebedi ba menyako ba ke ba etse dikete tse nne, mme ba kete tse nne e be ba tla rorisa Jehova ka diletsa tseo ke di entseng hore a roriswe ka tsona.",
			"6": "<sup>6</sup> Davida a ba arola dihlopha ka palo ya bara ba Levi, Gershone, Kehathe, le Merari.",
			"7": "<sup>7</sup> Ho Bagershone, ya eba Laedane le Shimei.",
			"8": "<sup>8</sup> Bara ba Laedane, e moholo ke Jehiele, ba bang ke Sethame le Joele; ba bararo.",
			"9": "<sup>9</sup> Bara ba Shimei ke Shelomithe, Hasiele, le Harane; ba bararo. Ke tsona dihlooho tsa bontata Balaedane.",
			"10": "<sup>10</sup> Bara ba Shimei ke Jahathe, Sina, Jeushe, le Beria. Bao ba bane ke bara ba Shimei.",
			"11": "<sup>11</sup> Jahathe ke e moholo; wa bobedi ke Sina; haele Jeushe le Beria, ba ne ba se na bana ba bangata; ha ho le jwalo, palong ba etsa ntlo e le nngwe ya bontata Balevi.",
			"12": "<sup>12</sup> Bara ba Kehathe ke Amrame, Jitsehare, Hebrone le Usiele; ba bane.",
			"13": "<sup>13</sup> Bara ba Amrame ke Aarone le Moshe; Aarone yena a kgethwa, hore a tle a halaletswe ka ho sa feleng ka kgalalelo e fetisang, e le yena le bara ba hae, mme ba tjhese dibano pela Jehova, ba mo sebeletse, ba hlohonolofatse setjhaba ka lebitso la hae ka ho sa feleng.",
			"14": "<sup>14</sup> Haele ka Moshe, monna wa Modimo, bara ba hae ba balwa le bara ba Levi.",
			"15": "<sup>15</sup> Bara ba Moshe ke Gershome le Eliesere.",
			"16": "<sup>16</sup> E moholo ho bara ba Gershome ke Shebuele.",
			"17": "<sup>17</sup> Bara ba Eliesere, e moholo ke Rehabia. Eliesere o ne a se na bara ba bang; empa bara ba Rehabia ba ne ba le bangata.",
			"18": "<sup>18</sup> Bara ba Jitsehare, e moholo ke Shelomithe.",
			"19": "<sup>19</sup> Ho bara ba Hebrone, e moholo ke Jeria, wa bobedi ke Amaria, wa boraro ke Jahasiele, wa bone ke Jekameame.",
			"20": "<sup>20</sup> Ho bara ba Usiele, e moholo ke Mika, wa bobedi ke Jishia.",
			"21": "<sup>21</sup> Bana ba Merari ke Mahali le Mushi; bara ba Mahali ke Eleasare le Kishe.",
			"22": "<sup>22</sup> Eleasare a shwa a se na bara, a ena le baradi feela; mme dikgaitsedi tsa bona, bara ba Kishe, tsa ba nyala.",
			"23": "<sup>23</sup> Bara ba Mushi ke Mahali, Edere, le Jeremothe; ba bararo.",
			"24": "<sup>24</sup> Ke bona bara ba Levi ka matlo a bontata bona; ke tsona dihlooho tsa matlo a bontata bona, kamoo ba kileng ba ngolwa kateng, ha ho balwa mabitso a bona ka ho ya ka dihlooho. Ba ne ba etsa mosebetsi wa tshebetso ya ntlo ya Jehova, ba qala ha ba le dilemo tse mashome a mabedi le ho ya pele;",
			"25": "<sup>25</sup> hobane Davida o ne a itse: Jehova, Modimo wa Iseraele, o neile setjhaba sa hae phomolo, mme o tla aha Jerusalema ka ho sa feleng;",
			"26": "<sup>26</sup> haele Balevi, ha ba sa na ho jara tabernakele leha e le mesebetsi yohle ya tshebetso.",
			"27": "<sup>27</sup> Ke e nngwe ya taba tseo Davida a qeteletseng ka tsona, ha ho kile ha etswa palo ya bara ba Levi, ho qala ka ba dilemo tse mashome a mabedi le ho ya pele.",
			"28": "<sup>28</sup> Hobane mosebetsi wa bona e ne e le ho tlatsa bara ba Aarone tshebetsong ya ntlo ya Jehova; ba ne ba laetswe ho baballa mabala le dikamore, le ho hlatswa ntho tsohle tse halalelang, le ho phetha mosebetsi wohle wa tshebetso ya ntlo ya Modimo;",
			"29": "<sup>29</sup> le ho bona mahobe a pontsha, le lefi la phofo ya dinyehelo, le maqebekwane a se nang ditomoso, le tse phehwang paneng, le tse beswang, le ditekanyo tsa boholo le tsa bongata.",
			"30": "<sup>30</sup> Ba ne ba tshwanetse ho itlhahisa pela Jehova ka hosasa ho hong le ho hong, ho boka le ho rorisa Jehova, le mantsiboya jwalo;",
			"31": "<sup>31</sup> le ho hlahisetsa Jehova ditjheso tsohle, mohla Disabatha, le dikgwedi tse thwasang, le mekete, ka palo ya tsona, ka ho ya ka mokgwa wa ntho tseo, ka ho sa feleng pela Modimo.",
			"32": "<sup>32</sup> Hape, ba ne ba boloka dintho tsa tente ya pokano, le tsa sehalalelo, le tsa bara ba Aarone, banababo bona, tsa ho etsa tshebetso ya ntlo ya Jehova."
		},
		"24": {
			"1": "<sup>1</sup> Haele bara ba Aarone, dikarolo tsa bona ke tsena: Bara ba Aarone e ne e le Nadabe, Abihu, Eleasare le Ithamare.",
			"2": "<sup>2</sup> Haele Nadabe le Abihu, bona ba ne ba shwe, ntata bona a sa le teng, ba se na bara; ka baka leo, Eleasare le Ithamare ba tshwara mosebetsi wa boprista.",
			"3": "<sup>3</sup> Davida hammoho le Tsadoke wa bara ba Eleasare, le Akimeleke wa bara ba Ithamare, ba arola baprista ka dikabelo tsa bona tsa tshebetso.",
			"4": "<sup>4</sup> Hara bara ba Eleasare ha fumanwa hobane mahosana a mangata ho fetisa hara bara ba Ithamare; ha ho le jwalo, ba arolwa ka mokgwa ona: ho bara ba Eleasare, ha balwa dihlooho tse leshome le metso e tsheletseng tsa matlo a bontata bona, le ho bara ba Ithamare, dihlooho tse robileng meno e le mmedi tsa matlo a bontata bona.",
			"5": "<sup>5</sup> Ba arolelwa dikabelo ka lotho, ba bang le ba bang; batsamaisi ba sehalalelo le batsamaisi ba ntlo ya Modimo ba nkwa hara bara ba Eleasare le hara bara ba Ithamare;",
			"6": "<sup>6</sup> mongodi Shemaia, mora Nethaneele, wa Balevi, a ba ngola pela morena, le mahosana, le moprista Tsadoke, le Akimeleke, mora Abiathare, le dihlooho tsa matlo a bontata baprista le Balevi; ha ho le jwalo, ha laolwa ka lotho ntlo e nngwe ya Eleasare, ha boela ha laolwa e nngwe ya Ithamare.",
			"7": "<sup>7</sup> Lotho ya pele ya wela Jehojaribe, ya bobedi Jedaia,",
			"8": "<sup>8</sup> ya boraro Harime, ya bone Seorime,",
			"9": "<sup>9</sup> ya bohlano Malkija, ya botshelela Mijamine,",
			"10": "<sup>10</sup> ya bosupa Hakkotse, ya boroba meno e le mmedi Abija,",
			"11": "<sup>11</sup> ya boroba mono o le mong Joshua, ya leshome Shekania;",
			"12": "<sup>12</sup> ya leshome le motso o mong Eliashibe, ya leshome le metso e mmedi Jakime,",
			"13": "<sup>13</sup> ya leshome le metso e meraro Huppa, ya leshome le metso e mene Jeshebeabe,",
			"14": "<sup>14</sup> ya leshome le metso e mehlano Bilga, ya leshome le metso e tsheletseng Immere,",
			"15": "<sup>15</sup> ya leshome le metso e supileng Hesire, ya leshome le metso e robileng meno e le mmedi Happitsetse,",
			"16": "<sup>16</sup> ya leshome le metso e robileng mono o le mong Pethahia, ya mashome a mabedi Esekiele,",
			"17": "<sup>17</sup> ya mashome a mabedi a motso o mong Jakine, ya mashome a mabedi a metso e mmedi Gamule,",
			"18": "<sup>18</sup> ya mashome a mabedi a metso e meraro Delaia, ya mashome a mabedi a metso e mene Maasia.",
			"19": "<sup>19</sup> Ke lona lenaneo la bona tshebetsong ya bona la ho tla ka tlung ya Jehova ka taelo e neng e bewe ke Aarone ntata bona, kamoo Jehova, Modimo wa Iseraele, a neng a mo laetse kateng.",
			"20": "<sup>20</sup> Haele bara ba bang ba Levi, dihlooho tsa bona ke tsena: Ho bara ba Amrame ke Shubaele; ho bara ba Shubaele ke Jehdia;",
			"21": "<sup>21</sup> bakeng sa Rehabia, ho bara ba hae, e moholo ke Jishia,",
			"22": "<sup>22</sup> ho ba Jitsehare ke Shelomothe, ho bara ba Shelomothe ke Jahathe.",
			"23": "<sup>23</sup> Ho bara ba Hebrone e moholo ke Jeria, wa bobedi ke Amaria, wa boraro ke Jahasiele, wa bone ke Jekameame.",
			"24": "<sup>24</sup> Ho bara ba Usiele ke Mikea; ho bara ba Mikea ke Shamire.",
			"25": "<sup>25</sup> Ngwanabo Mikea ke Jishia; ho bara ba Jishia ke Sakaria.",
			"26": "<sup>26</sup> Ho bara ba Merari, ke Mahali le Mushi, le bara ba Jaasia, mora wa hae.",
			"27": "<sup>27</sup> Haele bara ba Merari, ho mora wa hae Jaasia ho tswile Shohame, Sakkure, le Ibri.",
			"28": "<sup>28</sup> Ho Mahali ho tswile Eleasare, ya neng a se na bara.",
			"29": "<sup>29</sup> Haele Kishe, mora Kishe ke Jerahmeele.",
			"30": "<sup>30</sup> Bara ba Mushi ke Mahali, Edere, le Jerimothe.",
			"31": "<sup>31</sup> Ke bona bara ba Levi, ha ho uwa ka matlo a bontata bona.",
			"32": "<sup>32</sup> Le bona, jwaloka banababo bona, bara ba Levi ba bang, ba laolwa ka lotho pela morena Davida, le Tsadoke, le Akimeleke, le dihlooho tsa matlo a bontata baprista le Balevi. Bao e neng e le dihlooho tsa matlo, ba laolwa ka lotho jwaloka ba banyenyane hara banababo bona."
		},
		"25": {
			"1": "<sup>1</sup> Yaba Davida le balaodi ba makgotla ba kgetha bara ba bang ba Asafe, le ba Hemane, le ba Jeduthune, ba tle ba porofete ba ithusa ka diharepa le diviole le disimbala. Ke ena palo ya banna ba kentsweng mosebetsing wa tshebetso eo ya bona:",
			"2": "<sup>2</sup> Ho bara ba Asafe ke Sakkure, Josefa, Nethania, le Ashareela, bara ba Asafe, ba le tsamaisong ya Asafe, ya neng a porofeta ka taelo ya morena.",
			"3": "<sup>3</sup> Ho Jeduthune, bara ba Jeduthune ke Gedalia, Tseri, Esaia, Hashabia, le Matthithia; ba tsheletse; ba le tsamaisong ya ntata bona Jeduthune, ya neng a porofeta ka harepa, ho leboha le ho rorisa Jehova.",
			"4": "<sup>4</sup> Ho Hemane, bara ba Hemane ke Bukkia, Matthania, Usiele, Shebuele, Jerimothe, Hanania, Hanani, Eliatha, Giddalthi, le Romamthi-Esere, Joshbekasha, Mallothi, Hothire, le Mahasiothe.",
			"5": "<sup>5</sup> Bao kaofela ke bara ba Hemane, seboni sa morena se senolang mantswe a Modimo, le ho rorisa matla a wona. Modimo o ne o neile Hemane bara ba leshome le metso e mene le baradi ba bararo.",
			"6": "<sup>6</sup> Bao kaofela ba ne ba le tsamaisong ya ntata bona, ba tle ba bine tlung ya Jehova, ba ntse ba ithusa ka disimbala le diviole le diharepa, ho etsa tshebetso ya ntlo ya Modimo. Haele Asafe le Jeduthune le Hemane, ba ne ba laelwa ke morena.",
			"7": "<sup>7</sup> Mme palo ya bona, ha ba kopanngwa le banababo bona ba neng ba ithutile ho binela Jehova, e leng banna ba rutehileng kaofela, e ne e le batho ba makgolo a mabedi a mashome a robileng meno e le mmedi a metso e robileng meno e le mmedi.",
			"8": "<sup>8</sup> Ba laolelwa dikabelo tsa bona ka lotho, e monyenyane le e moholo, ya rutehileng le ya rutwang.",
			"9": "<sup>9</sup> Lotho ya pele ya ntsha Josefa wa Asafe; ya bobedi ya ntsha Gedalia; yena le banababo le bara ba hae ba ne ba le leshome le metso e mmedi.",
			"10": "<sup>10</sup> Ya boraro ya ntsha Sakkure; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"11": "<sup>11</sup> Ya bone ya ntsha Jitseri; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"12": "<sup>12</sup> Ya bohlano ya ntsha Nethania; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"13": "<sup>13</sup> Ya botshelela ya ntsha Bukkia; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"14": "<sup>14</sup> Ya bosupa ya ntsha Jesareela; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"15": "<sup>15</sup> Ya boroba meno e le mmedi ya ntsha Esaia; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"16": "<sup>16</sup> Ya boroba mono o le mong ya ntsha Matthania; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"17": "<sup>17</sup> Ya leshome ya ntsha Shimei; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"18": "<sup>18</sup> Ya leshome le motso o mong ya ntsha Asareele; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"19": "<sup>19</sup> Ya leshome le metso e mmedi ya ntsha Hashabia; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"20": "<sup>20</sup> Ya leshome le metso e meraro ya ntsha Shubaele; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"21": "<sup>21</sup> Ya leshome le metso e mene ya ntsha Matthithia; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"22": "<sup>22</sup> Ya leshome le metso e mehlano ya ntsha Jeremothe; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"23": "<sup>23</sup> Ya leshome le metso e tsheletseng ya ntsha Hanania; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"24": "<sup>24</sup> Ya leshome le metso e supileng ya ntsha Joshbekasha; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"25": "<sup>25</sup> Ya leshome le metso e robileng meno e le mmedi ya ntsha Hanani; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"26": "<sup>26</sup> Ya leshome le metso e robileng mono o le mong ya ntsha Mallothi; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"27": "<sup>27</sup> Ya mashome a mabedi ya ntsha Eliatha; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"28": "<sup>28</sup> Ya mashome a mabedi a motso o mong ya ntsha Hothire; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"29": "<sup>29</sup> Ya mashome a mabedi a metso e mmedi ya ntsha Giddalthi; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"30": "<sup>30</sup> Ya mashome a mabedi a metso e meraro ya ntsha Mahasiothe; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi.",
			"31": "<sup>31</sup> Ya mashome a mabedi a metso e mene ya ntsha Romamthi-Esere; yena le bara ba hae le banababo ba ne ba le leshome le metso e mmedi."
		},
		"26": {
			"1": "<sup>1</sup> Haele balebedi ba menyako, kamoo ba ileng ba etswa dihlopha kateng ho Bakore, e ne e le Meshelemia, mora Kore, wa bara ba Asafe.",
			"2": "<sup>2</sup> Bara ba Meshelemia, letsibolo ke Sakaria, wa bobedi Jediale, wa boraro Sebadia, wa bone Jathniele;",
			"3": "<sup>3</sup> wa bohlano Elame, wa botshelela Johanane, wa bosupa Elioenai.",
			"4": "<sup>4</sup> Haele bara ba Obed-Edomo, letsibolo la hae ke Shemaia, wa bobedi Josabade, wa boraro Jowa, wa bone Sakare, wa bohlano Nethaneele;",
			"5": "<sup>5</sup> wa botshelela Ammiele, wa bosupa Issaskare, wa boroba meno e le mmedi Peulthai; etswe Modimo o ne o mo hlohonolofaditse.",
			"6": "<sup>6</sup> Haele mora hae Shemaia, a tswallwa bara bao e bileng ba busang tlung ya ntata bona; hobane e ne e le banna ba matla.",
			"7": "<sup>7</sup> Bara ba Shemaia ke Othni, Refaele, Obede, Elsabade, le banababo bona Elihu, le Semakia, banna ba matla.",
			"8": "<sup>8</sup> Bao kaofela e ne e le bara ba Obed-Edomo; bona, le bara ba bona, le banababo bona e ne e le bahale, banna ba dinatla tshebetsong; ba Obed-Edomo ba ne ba le mashome a tsheletseng a metso e mmedi.",
			"9": "<sup>9</sup> Meshelemia yena o ne a na le bara le banababo, banna ba matla, ba le leshome le metso e robileng meno e le mmedi.",
			"10": "<sup>10</sup> Hosa, wa bara ba Merari, o ne a na le bara; hlooho ke Simri, leha empa e ne e se yena letsibolo, a mpa a beilwe hlooho ke ntatae;",
			"11": "<sup>11</sup> wa bobedi ke Hilkia, wa boraro ke Thebalia, wa bone ke Sakaria, bara bohle ba Hosa le banababo ba ne ba le leshome le metso e meraro.",
			"12": "<sup>12</sup> Dihlopha tseo tsa balebedi ba menyako, ka ho ya ka dihlooho tsa bona, tshebetso ya bona e ne e le ho lebela ntlo ya Jehova hammoho le banababo bona.",
			"13": "<sup>13</sup> Ba laolelwa monyako o mong le o mong ka lotho, ho qala ka e monyenyane ho isa ho e moholo, ka ho ya ka matlo a bontata bona.",
			"14": "<sup>14</sup> Lotho ya ntsha Shelemia ho ya monyakong o ka nqa botjhabela; mora hae, Sakaria, monna wa lekgotla, ya hlalefileng, a laolelwa ka lotho ka nqa leboya.",
			"15": "<sup>15</sup> Obed-Edomo yena a laolelwa ka nqa borwa; bara ba hae bona ba laolelwa matlo a ho bolokela dintho.",
			"16": "<sup>16</sup> Shuppime le Hosa ba newa ka nqa bophirimela, hammoho le monyako wa Shallekethe, moepeng; balebedi ba bang ba ne ba le malokana le ba bang.",
			"17": "<sup>17</sup> Ka nqa botjhabela, ho ne ho beilwe Balevi ba tsheletseng, ka nqa leboya ba le bane ka letsatsi le leng le le leng, ka nqa borwa ba le bane ka letsatsi, ba le babedi hape tlung e nngwe le e nngwe ya ho bolokela;",
			"18": "<sup>18</sup> Parbare, ka nqa bophirimela, ha bewa ba bane pela mmila, ba le babedi hape Parbare.",
			"19": "<sup>19</sup> Ke tsona dihlopha tsa balebedi ba menyako ho bara ba Bakore le ho bara ba Merari.",
			"20": "<sup>20</sup> Ho Balevi, Akija a bewa molebedi wa matlotlo a ntlo ya Modimo le wa matlotlo a ntho tse halalelang.",
			"21": "<sup>21</sup> Haele bara ba Laedane, e leng bara ba Bagershone ka Laedane, bao e neng e le dihlooho tsa matlo a bontata Laedane Mogershone,",
			"22": "<sup>22</sup> ha nkwa Jehieli; bara ba Jehieli ke Sethame, le Joele ngwanabo, bao e neng e le balebedi ba matlotlo a ntlo ya Jehova.",
			"23": "<sup>23</sup> Ho Baamrame, le Batjitsehare, le Bahebrone le Bausiele,",
			"24": "<sup>24</sup> Shebuele, mora Gershome, mora Moshe, e ne e le molebedi wa matlotlo.",
			"25": "<sup>25</sup> Bao e neng e le banababo ka Eliesere, e ne e le mora hae Rehabia, le mora hae Esaia, le mora hae Jorame, le mora hae Sikri, le mora hae Shelomithe.",
			"26": "<sup>26</sup> Shelomithe eo le banababo ba bewa balebedi ba matlotlo wohle a dintho tse halalelang tseo morena Davida, le dihlooho tsa matlo, le balaodi ba dikete le ba makgolo, le balaodi ba bang ba makgotla, ba neng ba di kgethetse Jehova;",
			"27": "<sup>27</sup> ba ne ba di nkile dintweng le kgapong, ba di kgethela kotlo ya ntlo ya Jehova;",
			"28": "<sup>28</sup> hammoho le tsohle tse neng di kgethetswe Jehova ke Samuele seboni, le Saule, mora Kishe, le Abnere, mora Nere, le Joabe, mora Tseruja, le mang le mang, tsohle tseo tse neng di kgethetswe Jehova, tsa bewa bolebeding ba Shelomithe le banababo.",
			"29": "<sup>29</sup> Ho Bajitsehare, Kenania le bara ba hae ba bewa mosebetsing o kantle, ba bewa bahlahlobi le baahlodi Iseraeleng.",
			"30": "<sup>30</sup> Ho Bahebrone, Hashabia le banababo, banna ba bohale ba sekete se nang le makgolo a supileng, ba bewa bahlahlobi ba Baiseraele ba mose o kwano ho Jordane, ka nqa bophirimela, ho sebetsa mesebetsi yohle ya Jehova le mesebetsi ya morena.",
			"31": "<sup>31</sup> Ho Bahebrone — Bahebrone bao ho ileng ha batlwa masika a bona ka ho ya ka bontata bona, selemong sa mashome a mane sa puso ya Davida, ha ba ha fumanwa hara bona banna ba matla, ba bahale, motseng wa Jasere wa Gileade, —",
			"32": "<sup>32</sup> Jeria, hlooho, le banababo e neng e le banna ba bahale, ba ne ba etsa kete tse pedi tse nang le makgolo a supileng a dihlooho tsa matlo a bontata bona. Morena Davida a ba bea bahlahlobi ba Barubene, ho sebetsa mesebetsi yohle ya Modimo le mesebetsi ya morena."
		},
		"27": {
			"1": "<sup>1</sup> Bana ke bana ba Iseraele ka palo ya bona, dihlooho tsa matlo a bontata bona, le balaodi ba dikete le ba makgolo, le bahlahlobi ba bona, ba neng ba sebeletsa morena tabeng tsohle tsa dihlopha, kamoo di neng di tshwanetse ho kena le ho tswa mosebetsing kateng, ka kgwedi e nngwe le e nngwe, kgweding tsohle tsa selemo; hlopha se seng le se seng e ne e le sa batho ba dikete tse mashome a mabedi a metso e mene.",
			"2": "<sup>2</sup> Molaodi wa hlopha sa pele, kgweding ya pele, e ne e le Jashobeame, mora Sabdiele; hlopheng sa hae o ne a na le batho ba kete tse mashome a mabedi a metso e mene.",
			"3": "<sup>3</sup> E be e le wa bara ba Peretse, hape e le hlooho ya balaodi bohle ba makgotla a ntwa ka kgwedi ya pele.",
			"4": "<sup>4</sup> Molaodi wa hlopha sa kgwedi ya bobedi, e ne e le Dodai, Moakoke; Miklothe e ne e le motsamaisi wa hlopha sa hae; mme o ne a na le batho ba dikete tse mashome a mabedi a metso e mene sehlopheng sa hae.",
			"5": "<sup>5</sup> Molaodi wa lekgotla la boraro la ntwa, ka kgwedi ya boraro, e ne e le Benaia, hlooho, mora Jehojada moprista; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"6": "<sup>6</sup> Benaia eo e ne e le ya matla hara ba mashome a mararo, a feta ba mashome a mararo; mora hae Ammisabade e ne e le motsamaisi e mong wa hlopha sa hae.",
			"7": "<sup>7</sup> Kgweding ya bone, molaodi wa bone e ne e le Asaele ngwanabo Joabe; ya mo hlahlamang ya eba mora hae Sebadia; hlopheng sa hae o ne a na le batho ba kete tse mashome a mabedi a metso e mene.",
			"8": "<sup>8</sup> Kgweding ya bohlano, molaodi wa bohlano e ne e le Shamehuthe Mojisrahe; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"9": "<sup>9</sup> Kgweding ya botshelela, molaodi wa botshelela e ne e le Ira, mora Ikkeshe wa Tekowa; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"10": "<sup>10</sup> Kgweding ya bosupa, molaodi wa bosupa e ne e le Heletse wa Pelone, wa bara ba Efraime; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"11": "<sup>11</sup> Kgweding ya boroba meno e le mmedi, molaodi wa boroba meno e le mmedi e ne e le Sibbekai wa Husha, wa Baserake; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"12": "<sup>12</sup> Kgweding ya boroba mono o le mong, molaodi wa boroba mono o le mong e ne e le Abiesere wa Anathothe, Mobenjamine; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"13": "<sup>13</sup> Kgweding ya leshome, molaodi wa leshome e ne e le Maharai wa Nethofa, wa Baserake; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"14": "<sup>14</sup> Kgweding ya leshome le motso o mong, molaodi wa leshome le motso o mong e ne e le Benaia wa Pirathone, wa bara ba Efraime; hlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"15": "<sup>15</sup> Kgweding ya leshome le metso e mmedi, molaodi wa leshome le metso e mmedi e ne e le Heldai wa Nethofa, wa ba ha Othniele; sehlopheng sa hae o ne a na le batho ba dikete tse mashome a mabedi a metso e mene.",
			"16": "<sup>16</sup> Haele bakeng sa meloko ya Iseraele, molaodi wa Barubene e ne e le Eliesere mora Sikri; wa Basimeone e ne e le Shefathia, mora Maaka;",
			"17": "<sup>17</sup> wa Balevi e ne e le Hashabia, mora Kemuele; wa Baarone e ne e le Tsadoke;",
			"18": "<sup>18</sup> wa Juda e ne e le Elihu, e mong wa banababo Davida; wa Issaskare e ne e le Omri, mora Mikaele;",
			"19": "<sup>19</sup> wa Sabulone e ne e le Jishemaia, mora Obadia; wa Nefthali e ne e le Jerimothe, mora Asriele;",
			"20": "<sup>20</sup> wa bara ba Efraime e ne e le Hosea, mora Asasia; wa mothapo o mong wa Manasse e ne e le Joele, mora Pedaia;",
			"21": "<sup>21</sup> wa mothapo wa bobedi wa Manasse, Gileade, e ne e le Jiddo, mora Sakaria; wa Benjamine e ne e le Jaasiele, mora Abnere;",
			"22": "<sup>22</sup> wa Dane e ne e le Asareele, mora Jerohame. Ke wona mahosana a meloko ya Iseraele.",
			"23": "<sup>23</sup> Davida o ne a sa ka a bala palo ya Baiseraele ba neng ba na le dilemo tse sa fihleng mashomeng a mabedi; etswe Jehova a ne a boletse hore o tla atisa Iseraele, a be ka ka dinaledi tsa lehodimo.",
			"24": "<sup>24</sup> Joabe, mora Tseruja, o ne a qadile ho ba bala, empa a sa ka a phetha, hobane Jehova o ne a halefetse Iseraele, ka baka la palo eo; ke kahoo palo e sa kang ya kena mangolong a morena Davida a ngolwang ka letsatsi le leng le le leng.",
			"25": "<sup>25</sup> Asmavethe, mora Adiele, a bewa mohlahlobi wa matlotlo a morena; Jonathane, mora Osiase, a bewa mohlahlobi wa mediko e masimong, le metseng, le metsaneng, le ditoreng.",
			"26": "<sup>26</sup> Esri, mora Kelube, a bewa mohlahlobi wa ba sebetsang masimong, ba lemang lefatshe.",
			"27": "<sup>27</sup> Mohlahlobi wa masimo a merara ya eba Shimei wa Rama; Sabdi wa Shefame ya eba mohlahlobi wa dipolokelo tsa veine masimong a merara.",
			"28": "<sup>28</sup> Baale-Hanane ya eba mohlahlobi wa mehlwaare le disikomora tse leng dithoteng; Joashe yena a hlahloba dipolokelo tsa ole.",
			"29": "<sup>29</sup> Shithrai wa Sarone a hlahloba makgomo a fulang Sarone; Shafathe, mora Adlai, yena a hlahloba makgomo a dikgutlong.",
			"30": "<sup>30</sup> Obile Moishmaele ya eba mohlahlobi wa dikamele; Jehdia wa Meronothe a hlahloba diesele.",
			"31": "<sup>31</sup> Jasise Mohagerene yena a hlahloba manku. Kaofela ha bao e ne e le bahlahlobi ba maruo a morena Davida.",
			"32": "<sup>32</sup> Haele Jonathane, maloma Davida, e ne e le monna wa lekgotla, motho ya kelello, ya rutehileng. Jehiele, mora Hakmoni, o ne a dula le bara ba morena.",
			"33": "<sup>33</sup> Akitofele e ne e le moeletsi wa morena; Hushai Moarke yena e ne e le motswalle wa morena.",
			"34": "<sup>34</sup> Kamora Akitofele, Jehojada, mora Benaia, le Abiathare, e ne e le baeletsi. Molaodi wa makgotla a morena e ne e le Joabe."
		},
		"28": {
			"1": "<sup>1</sup> Davida a phuthela Jerusalema marena wohle a Iseraele, dihlooho tsa meloko, le balaodi ba dihlopha tse sebeletsang morena, le balaodi ba dikete le balaodi ba makgolo, le bahlahlobi ba maruo le ba mehlape ya morena le ya bara ba hae, hammoho le matona, le banna ba bahale, le diqhenqha kaofela.",
			"2": "<sup>2</sup> Morena Davida a ema ka maoto, a re: Banabeso le setjhaba sa ka, mmameleng. Kgopolo ya pelo ya ka e ne e le hore ke hahele areka ya selekane sa Jehova ntlo ya phomolo, e tle e be bonamelo ba maoto a Modimo wa rona; ke ne ke se ke qadile ho lokisa tse tla haha.",
			"3": "<sup>3</sup> Empa Modimo wa re ho nna: O ke ke wa hahela lebitso la ka ntlo, hobane o monna wa ntwa, o tsholotse madi.",
			"4": "<sup>4</sup> Jehova, Modimo wa Iseraele, o ne a nkgethe hara bohle ba ntlo ya ntate hore ke be morena wa Iseraele ka ho sa feleng; hobane o fela a kgethile Juda ho mo etsa motsamaisi; tlung ya Juda o kgethile ntlo ya ntate, mme hara bara ba ntate o kgahlilwe ke nna ho nketsa morena wa Baiseraele bohle.",
			"5": "<sup>5</sup> Mme hara bara ba ka kaofela — hobane Jehova o nneile bara ba bangata — o kgethile mora ka Salomone, hore a dule teroneng ya mmuso wa Jehova Iseraeleng.",
			"6": "<sup>6</sup> O itse ho nna: Ke mora hao Salomone ya tla haha ntlo ya ka le mabala a ka; hobane ke mo kgethile hore e be mora wa ka, mme nna ke be ntatae.",
			"7": "<sup>7</sup> Ke tla tiisa mmuso wa hae ka ho sa feleng, ha a ka tiisetsa ho etsa ka melao ya ka le ka ditaelo tsa ka, jwalokaha ho le kajeno.",
			"8": "<sup>8</sup> Jwale, mahlong a Baiseraele bohle, a phutheho ya Jehova, Modimo wa rona ha o ntse o re utlwa, ke re: Bolokang, le batle ditaelo tsohle tsa Jehova, Modimo wa lona, hore le nne le rue fatshe lena le molemo, mme le le siele bara ba lona e le lefa la bona kamora lona, ka ho sa feleng.",
			"9": "<sup>9</sup> Haele wena, mora ka Salomone, hlokomela Modimo wa ntatao, mme o o sebeletse ka pelo yohle ya hao le ka moya o o ratang; hobane Jehova o botsisisa dipelo tsohle, mme o lekola merero yohle le mehopolo yohle. Ekare ha o mo batla, o tla fumanwa ke wena; ha o mpa o mo furalla, o tla o lahla ka ho sa feleng.",
			"10": "<sup>10</sup> Bona jwale hoba Jehova o o kgethile hore o hahe ntlo e tla ba sehalalelo. Iphe matla, mme o etse.",
			"11": "<sup>11</sup> Davida a nea Salomone, mora hae, setshwantsho sa mathule, le sa matlo, le sa dipolokelo, le sa dikamore tse kahodimo, le sa dikamore tse kahare, le sa ntlo ya serapello.",
			"12": "<sup>12</sup> A mo nea le setshwantsho sa ntho tsohle tseo a di hopotseng moyeng wa hae bakeng sa mabala a ntlo ya Jehova, le sa dikamore tse sikang le dipolokelo tsa matlotlo a ntlo ya Modimo le tsa matlotlo a sehalalelo,",
			"13": "<sup>13</sup> a mmolella le tsa dihlopha tsa baprista le Balevi, le tsa tshebeletso yohle ya ntlo ya Jehova, le tsa mesebetsi yohle ya tshebeletso ya ntlo ya Jehova.",
			"14": "<sup>14</sup> A mo nea le tekanyo ya mesebetsi ya kgauta, le boima ba tseo e neng e ka kgona di etswe ka kgauta, le tekanyo ya mesebetsi yohle ya mefuta yohle; a bolela le boima ba mesebetsi yohle ya silefera, le ya mesebetsi yohle ya mefuta yohle.",
			"15": "<sup>15</sup> A mo nea le boima ba dikandelare tsa kgauta le ba dilampi tsa tsona tsa kgauta, e le boima ba kandelare e nngwe le e nngwe le ba dilampi tsa yona, le ba dikandelare tsa silefera, e leng boima ba kandelare e nngwe le e nngwe le ba dilampi tsa yona, ka ho ya ka mosebetsi wa kandelare e nngwe le e nngwe.",
			"16": "<sup>16</sup> A mo nea kgauta, boima bo itseng ba ho etsa tafole tsa mahobe a pontsha, le ba tafole e nngwe le e nngwe, le silefera ya ho etsa ditafole tsa silefera;",
			"17": "<sup>17</sup> le kgauta e hlwekileng ya difereko, le ya mafiso, le ye dinwelo; le ya mehope ya kgauta, boima ba yona bo tshwanelang mohope o mong le o mong; le silefera ya mehope ya silefera, boima bo tshwanelang mohope o mong le o mong;",
			"18": "<sup>18</sup> le kgauta e hlwekileng ya aletare ya dibano, boima bo itseng; le tekanyetso ya koloi, ke ho re ya dikerubime tsa kgauta tse otlollelang dipheo tsa tsona tse kgurumetsang areka ya selekane sa Jehova.",
			"19": "<sup>19</sup> Davida a re: Tsohle tseo, Jehova o nneile kutlwisiso ya tsona ka ho di ngola ka letsoho la hae, ke re, mesebetsi yohle ya tekanyetso eo.",
			"20": "<sup>20</sup> Davida a eketsa a re ho Salomone, mora hae: Iphe matla, mme o tiye, mme o etse. Se tshabe, mme o se ke wa tshoha; hobane Jehova Modimo, Modimo wa ka, o tla nne a be le wena; a ke ke a o tlohela, a ke ke a o furulla, ho be ho phethehe mosebetsi wohle wa tshebeletso ya ntlo ya Jehova.",
			"21": "<sup>21</sup> Bona hape, dihlopha tsa baprista le tsa Balevi ke tseo, kamoo di lokiseditsweng tshebeletso yohle ya ntlo ya Modimo kateng; hape, bakeng sa mosebetsi wohle, o na le banna bohle ba pelo tse ratang, ba masene mesebetsing yohle; hapehape, dihlooho le sona setjhaba kaofela ha sona di tla ba teng ho wena, ho etsa tsohle tseo o tla di laela."
		},
		"29": {
			"1": "<sup>1</sup> Yaba morena Davida o re ho phutheho kaofela: Mora ka Salomone eo Modimo o mo kgethileng a nnotshi, o sa le motjha, hape o monyenyane; athe mosebetsi o moholo; hobane ntlohadi eo hase ya motho, e mpa e le ya Jehova Modimo.",
			"2": "<sup>2</sup> Ke lokiseditse ntlo ya Modimo wa ka, ka matla wohle a ka, kgauta ya ho etsa tsa kgauta, le silefera ya ho etsa tsa silefera, le koporo ya ho etsa tsa koporo, le tshepe ya ho etsa tsa tshepe, le difate tsa ho etsa tsa difate; le mahakwe a onikse, le mahakwe a tla bewa dikoomaneng, le mahakwe a benyang le a mebalabala, le mahakwe a mang a mefuta a bohlokwa, le majwe a maramore e tshweu a mangata.",
			"3": "<sup>3</sup> Hape, ka baka la ho rata ntlo ya Modimo wa ka, kgauta le silefera tseo e leng tsa ka ka sebele, ke di ntshetsa ntlo ya Modimo wa ka, kantle ho tsohle tseo ke li lokiseditseng ntlo ya sehalalelo,",
			"4": "<sup>4</sup> e le ditalenta tsa kgauta tse dikete tse tharo, tsa kgauta ya Ofire, le ditalenta tsa silefera e hlwekileng tse dikete tse supileng, ho tle ho manehuwe mabota a matlo:",
			"5": "<sup>5</sup> kgauta e bewe ho tse tshwanetseng ho etswa ka kgauta, le silefera ho tse tshwanetseng ho etswa ka silefera, ho etswang ke malala a dikgauta. Hape, ke mang ya ratang ho ikgethela Jehova kajeno?",
			"6": "<sup>6</sup> Yaba dihlooho tsa matlo, le dihlooho tsa meloko ya Iseraele, le balaodi ba dikete le ba makgolo, le bahlahlobi ba mesebetsi ya morena, ba ntsha ka ho rata;",
			"7": "<sup>7</sup> ba ntshetsa tshebeletso ya ntlo ya Modimo ditalenta tsa kgauta tse dikete tse hlano tse nang le didarike tse dikete tse leshome, le ditalenta tsa silefera tse dikete tse leshome, le ditalenta tsa koporo tse dikete tse leshome le metso e robileng meno e le mmedi, le ditalenta tsa tshepe tse dikete tse lekgolo.",
			"8": "<sup>8</sup> Ba neng ba na le mahakwe a bohlokwa, ba a neela polokelo ya ntlo ya Jehova, matsohong a Jehiele Mogershone.",
			"9": "<sup>9</sup> Setjhaba sa thabela tseo bao ba di ntshitseng ka ho rata; hobane efela e ne e le ka pelo yohle ba neng ba neela Jehova dimpho tsa bona tsa ho rata; le yena morena Davida a di thabela ka thabo e kgolo.",
			"10": "<sup>10</sup> Yaba Davida o boka Jehova pela phutheho kaofela, a re: Oho, Jehova, Modimo wa Iseraele ntata rona, o bokwe ka ho sa feleng le ka ho sa feleng!",
			"11": "<sup>11</sup> Oho, Jehova, boholo, le matla, le letlotlo, le hlolo, le kganya, ke tsa hao! Hobane tsohle tse mahodimong le lefatsheng ke tsa hao. Borena le bona ke ba hao, Jehova, mme o phahametse tsohle o le morena.",
			"12": "<sup>12</sup> Leruo le kganya di tswa ho wena, mme o rena hodima tsohle. Matla le bonatla di matsohong a hao, hape o na le matla letsohong la hao a ho hodisa le a ho tiisa ntho tsohle.",
			"13": "<sup>13</sup> Mme jwale, Modimo wa rona, re a o boka, re rorisa lebitso la hao le kganyang.",
			"14": "<sup>14</sup> Hobane nna keng, mme setjhaba sa ka keng, hore re tle re tsebe ho o ntshetsa ntho tsa rona jwaloka ho rata? Athe tsohle di tswa ho wena, mme ke letsoho la hao le re fileng seo re o neang sona.",
			"15": "<sup>15</sup> Re basele pela hao, re bajaki jwaloka bontata rona kaofela; ditshiu tsa rona lefatsheng e ka moriti; mme ho dula ha ho yo.",
			"16": "<sup>16</sup> Jehova, Modimo wa rona, bongata boo ba dintho tseo re di lokiseditseng ho o hahela ntlo ka tsona, ho e hahela lebitso la hao le halalelang, bo tswa letsohong la hao; tsohle ke tsa hao.",
			"17": "<sup>17</sup> Hape, Modimo wa ka, ke a tseba hoba o lekola pelo, mme o kgahlwa ke ho loka. Haele nna, ha ke o neetse ntho tseo kaofela ka ho rata, ke ka ho loka ha pelo ya ka; mme jwale ke thabile ha ke bona setjhaba sa hao se leng mona se o ntshetsa dimpho tsa sona ka ho rata.",
			"18": "<sup>18</sup> Oho, Jehova, Modimo wa bontata rona Abrahama, Isaaka le Iseraele, o nne o boloke kamehla pelong tsa setjhaba sa hao mokgwa oo wa mehopolo ya bona, mme o tiiselletse dipelo tsa bona ho wena.",
			"19": "<sup>19</sup> O nee mora ka Salomone pelo e tletseng thato ya ho boloka ditaelo tsa hao le ditemoso tsa hao le melao ya hao, hore a be a di phethe kaofela ha tsona, mme a hahe ntlohadi eo ke lokisitseng ntho tsa yona.",
			"20": "<sup>20</sup> Davida a boela a re ho phutheho kaofela: Jwale, bokang Jehova, Modimo wa lona! Ebile moo phutheho yohle e bokang Jehova, Modimo wa bontata bona; ba inama, ba kgumama pela Jehova le pela morena.",
			"21": "<sup>21</sup> Ka la hosasane, ba hlabela Jehova mahlabelo, mme ba ntshetsa Jehova ditjheso tsa dipohwana tse sekete, le tsa dipheleu tse sekete, le tsa dikonyana tse sekete, ka ditshelo tsa tsona, le mahlabelo a mangata a Baiseraele kaofela.",
			"22": "<sup>22</sup> Ba eja, ba enwa pela Jehova tsatsing lona leo ka thabo e kgolo; ba boela ba bea Salomone, mora Davida, morena la bobedi, ba mo tlotsa pela Jehova, hore e be motsamaisi wa setjhaba, mme Tsadoke a bewa moprista.",
			"23": "<sup>23</sup> Salomone a dula teroneng ya Jehova, ya eba morena tulong tsa Davida ntatae; a atleha, mme Baiseraele bohle ba mo utlwa.",
			"24": "<sup>24</sup> Dihlooho tsohle, le dinatla kaofela, le bara bohle ba morena Davida, ba ipea katlasa morena Salomone.",
			"25": "<sup>25</sup> Jehova a phahamisa Salomone haholoholo mahlong a Baiseraele bohle, mme a mo nea borena bo kganyang boo ho neng ho eso ho be morena wa Iseraele ya kileng a ba le bo jwalo pele.",
			"26": "<sup>26</sup> Davida, mora Jese, ya eba morena wa Baiseraele bohle.",
			"27": "<sup>27</sup> Ditshiu tseo a busitseng Iseraele ka tsona ke dilemo tse mashome a mane. A busa ka dilemo tse supileng a le Hebrone; Jerusalema a busa ka dilemo tse mashome a mararo a metso e meraro.",
			"28": "<sup>28</sup> A shwa a tsofetse haholo, a tletse matsatsi le maruo le kganya; mme Salomone, mora hae, ya eba morena tulong tsa hae.",
			"29": "<sup>29</sup> Haele ditaba tsa morena Davida, tsa ho qala le tsa ho qetella, bonang, di ngodilwe bukeng ya ditaba tsa Samuele seboni, le bukeng ya ditaba tsa Nathane moporofeta, le bukeng ya ditaba tsa Gade seboni,",
			"30": "<sup>30</sup> hammoho le ditaba tsohle tsa puso ya hae le diketso tsa hae tse phahameng, le ditaba tse hlahileng matsatsing a bophelo ba hae Iseraeleng le mebusong yohle ya mafatshe a mang."
		}
	}
};
module.exports = book;