var book = {
	"name": "Mathayo",
	"numChapters": 28,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> Kitabu cha ukoo wa Yesu Kristo, mwana wa Daudi, mwana wa Ibrahimu.",
			"2": "<sup>2</sup> Ibrahimu alimzaa Isaka; Isaka akamzaa Yakobo; Yakobo akamzaa Yuda na ndugu zake;",
			"3": "<sup>3</sup> Yuda akamzaa Peresi na Zera kwa Tamari; Peresi akamzaa Esromu; Esromu akamzaa Aramu;",
			"4": "<sup>4</sup> Aramu akamzaa Aminadabu; Aminadabu akamzaa Nashoni; Nashoni akamzaa Salmoni;",
			"5": "<sup>5</sup> Salmoni akamzaa Boazi kwa Rahabu; Boazi akamzaa Obedi kwa Ruthu; Obedi akamzaa Yese;",
			"6": "<sup>6</sup> Yese akamzaa mfalme Daudi. Daudi akamzaa Sulemani kwa yule mke wa Uria;",
			"7": "<sup>7</sup> Sulemani akamzaa Rehoboamu; Rehoboamu akamzaa Abiya; Abiya akamzaa Asa;",
			"8": "<sup>8</sup> Asa akamzaa Yehoshafati; Yehoshafati akamzaa Yoramu; Yoramu akamzaa Uzia;",
			"9": "<sup>9</sup> Uzia akamzaa Yothamu; Yothamu akamzaa Ahazi; Ahazi akamzaa Hezekia;",
			"10": "<sup>10</sup> Hezekia akamzaa Manase; Manase akamzaa Amoni; Amoni akamzaa Yosia;",
			"11": "<sup>11</sup> Yosia akamzaa Yekonia na ndugu zake, wakati wa ule uhamisho wa Babeli.",
			"12": "<sup>12</sup> Na baada ya ule uhamisho wa Babeli, Yekonia akamzaa Shealtieli; Shealtieli akamzaa Zerubabeli;",
			"13": "<sup>13</sup> Zerubabeli akamzaa Abihudi; Abihudi akamzaa Eliakimu; Eliakimu akamzaa Azori;",
			"14": "<sup>14</sup> Azori akamzaa Sadoki; Sadoki akamzaa Akimu; Akimu akamzaa Eliudi;",
			"15": "<sup>15</sup> Eliudi akamzaa Eleazari; Eleazari akamzaa Matani; Matani akamzaa Yakobo;",
			"16": "<sup>16</sup> Yakobo akamzaa Yusufu mumewe Mariamu aliyemzaa YESU aitwaye Kristo.",
			"17": "<sup>17</sup> Basi vizazi vyote tangu Ibrahimu hata Daudi ni vizazi kumi na vinne; na tangu Daudi hata ule uhamisho wa Babeli ni vizazi kumi na vinne; na tangu ule uhamisho wa Babeli hata Kristo ni vizazi kumi na vinne.",
			"18": "<sup>18</sup> Kuzaliwa kwake Yesu Kristo kulikuwa hivi. Mariamu mama yake alipokuwa ameposwa na Yusufu, kabla hawajakaribiana, alionekana ana mimba kwa uweza wa Roho Mtakatifu.",
			"19": "<sup>19</sup> Naye Yusufu, mumewe, kwa vile alivyokuwa mtu wa haki, asitake kumwaibisha, aliazimu kumwacha kwa siri.",
			"20": "<sup>20</sup> Basi alipokuwa akifikiri hayo, tazama, malaika wa Bwana alimtokea katika ndoto, akisema, Yusufu, mwana wa Daudi, usihofu kumchukua Mariamu mkeo, maana mimba yake ni kwa uweza wa Roho Mtakatifu.",
			"21": "<sup>21</sup> Naye atazaa mwana, nawe utamwita jina lake Yesu, maana, yeye ndiye atakayewaokoa watu wake na dhambi zao.",
			"22": "<sup>22</sup> Hayo yote yamekuwa, ili litimie neno lililonenwa na Bwana kwa ujumbe wa nabii akisema,",
			"23": "<sup>23</sup> Tazama, bikira atachukua mimba, Naye atazaa mwana; Nao watamwita jina lake Imanueli; Yaani, Mungu pamoja nasi.",
			"24": "<sup>24</sup> Naye Yusufu alipoamka katika usingizi, alifanya kama malaika wa Bwana alivyomwagiza; akamchukua mkewe;",
			"25": "<sup>25</sup> asimjue kamwe hata alipomzaa mwanawe; akamwita jina lake YESU."
		},
		"2": {
			"1": "<sup>1</sup> Yesu alipozaliwa katika Bethlehemu ya Uyahudi zamani za mfalme Herode, tazama, mamajusi wa mashariki walifika Yerusalemu, wakisema,",
			"2": "<sup>2</sup> Yuko wapi yeye aliyezaliwa mfalme wa Wayahudi? Kwa maana tuliiona nyota yake mashariki, nasi tumekuja kumsujudia.",
			"3": "<sup>3</sup> Basi mfalme Herode aliposikia hayo, alifadhaika, na Yerusalemu pia pamoja naye.",
			"4": "<sup>4</sup> Akakusanya wakuu wa makuhani wote na waandishi wa watu, akatafuta habari kwao, Kristo azaliwa wapi?",
			"5": "<sup>5</sup> Nao wakamwambia, Katika Bethlehemu ya Uyahudi; kwa maana ndivyo ilivyoandikwa na nabii,",
			"6": "<sup>6</sup> Nawe Bethlehemu, katika nchi ya Yuda, Hu mdogo kamwe katika majumbe wa Yuda; Kwa kuwa kwako atatoka mtawala Atakayewachunga watu wangu Israeli.",
			"7": "<sup>7</sup> Kisha Herode akawaita wale mamajusi faraghani, akapata kwao hakika ya muda tangu ilipoonekana ile nyota.",
			"8": "<sup>8</sup> Akawapeleka Bethlehemu, akasema, Shikeni njia, mkaulize sana mambo ya mtoto; na mkiisha kumwona, nileteeni habari, ili mimi nami niende nimsujudie.",
			"9": "<sup>9</sup> Nao waliposikia maneno ya mfalme, walishika njia; na tazama, ile nyota waliyoiona mashariki ikawatangulia, hata ikaenda ikasimama juu ya mahali alipokuwapo mtoto.",
			"10": "<sup>10</sup> Nao walipoiona ile nyota, walifurahi furaha kubwa mno.",
			"11": "<sup>11</sup> Wakaingia nyumbani, wakamwona mtoto pamoja na Mariamu mamaye, wakaanguka wakamsujudia; nao walipokwisha kufungua hazina zao, wakamtolea tunu; dhahabu na uvumba na manemane.",
			"12": "<sup>12</sup> Nao wakiisha kuonywa na Mungu katika ndoto wasimrudie Herode, wakaenda zao kwao kwa njia nyingine.",
			"13": "<sup>13</sup> Na hao walipokwisha kwenda zao, tazama, malaika wa Bwana alimtokea Yusufu katika ndoto, akasema, Ondoka, umchukue mtoto na mama yake, ukimbilie Misri, ukae huko hata nikuambie; kwa maana Herode anataka kumtafuta mtoto amwangamize.",
			"14": "<sup>14</sup> Akaondoka akamchukua mtoto na mama yake usiku, akaenda zake Misri;",
			"15": "<sup>15</sup> akakaa huko hata alipokufa Herode; ili litimie neno lililonenwa na Bwana kwa ujumbe wa nabii, akisema, Kutoka Misri nalimwita mwanangu.",
			"16": "<sup>16</sup> Ndipo Herode, alipoona ya kuwa amedhihakiwa na wale mamajusi, alighadhabika sana, akatuma watu akawaua watoto wote wanaume waliokuwako huko Bethlehemu na viungani mwake mwote, tangu wenye miaka miwili na waliopungua, kwa muda ule aliouhakikisha kwa wale mamajusi.",
			"17": "<sup>17</sup> Ndipo lilipotimia neno lililonenwa na nabii Yeremia, akisema,",
			"18": "<sup>18</sup> Sauti ilisikiwa Rama, Kilio, na maombolezo mengi, Raheli akiwalilia watoto wake, Asikubali kufarijiwa, kwa kuwa hawako.",
			"19": "<sup>19</sup> Hata alipofariki Herode, tazama, malaika wa Bwana alimtokea Yusufu katika ndoto huko Misri,",
			"20": "<sup>20</sup> akasema, Ondoka, umchukue mtoto na mamaye, ushike njia kwenda nchi ya Israeli; kwa maana wamekufa walioitafuta roho ya mtoto.",
			"21": "<sup>21</sup> Akaondoka akamchukua mtoto na mamaye, akafika nchi ya Israeli.",
			"22": "<sup>22</sup> Lakini aliposikia ya kwamba Arkelao anamiliki huko Uyahudi mahali pa Herode babaye, aliogopa kwenda huko; naye akiisha kuonywa katika ndoto, akasafiri pande za Galilaya,",
			"23": "<sup>23</sup> akaenda, akakaa katika mji ulioitwa Nazareti; ili litimie neno lililonenwa na manabii, Ataitwa Mnazorayo."
		},
		"3": {
			"1": "<sup>1</sup> Siku zile aliondokea Yohana Mbatizaji akihubiri katika nyika ya Uyahudi, na kusema,",
			"2": "<sup>2</sup> Tubuni; kwa maana ufalme wa mbinguni umekaribia.",
			"3": "<sup>3</sup> Kwa sababu huyo ndiye aliyenenwa na nabii Isaya, akisema, Sauti ya mtu aliaye nyikani, Itengenezeni njia ya Bwana, Yanyosheni mapito yake.",
			"4": "<sup>4</sup> Naye Yohana mwenyewe alikuwa na vazi lake la singa za ngamia, na mshipi wa ngozi kiunoni mwake; na chakula chake kilikuwa nzige na asali ya mwitu.",
			"5": "<sup>5</sup> Ndipo walipomwendea Yerusalemu, na Uyahudi wote, na nchi zote za kandokando ya Yordani;",
			"6": "<sup>6</sup> naye akawabatiza katika mto wa Yordani, huku wakiziungama dhambi zao.",
			"7": "<sup>7</sup> Hata alipoona wengi miongoni mwa Mafarisayo na Masadukayo wakiujia ubatizo wake, aliwaambia, Enyi wazao wa nyoka, ni nani aliyewaonya ninyi kuikimbia hasira itakayokuja?",
			"8": "<sup>8</sup> Basi zaeni matunda yapasayo toba;",
			"9": "<sup>9</sup> wala msiwaze mioyoni mwenu kwamba, Tunaye baba, ndiye Ibrahimu; kwa maana nawaambia ya kwamba Mungu aweza katika mawe haya kumwinulia Ibrahimu watoto.",
			"10": "<sup>10</sup> Na shoka limekwisha kuwekwa penye mashina ya miti; basi kila mti usiozaa matunda mazuri hukatwa na kutupwa motoni.",
			"11": "<sup>11</sup> Kweli mimi nawabatiza kwa maji kwa ajili ya toba; bali yeye ajaye nyuma yangu ana nguvu kuliko mimi, wala sistahili hata kuvichukua viatu vyake; yeye atawabatiza kwa Roho Mtakatifu na kwa moto.",
			"12": "<sup>12</sup> Ambaye pepeto lake li mkononi mwake, naye atausafisha sana uwanda wake; na kuikusanya ngano yake ghalani, bali makapi atayateketeza kwa moto usiozimika.",
			"13": "<sup>13</sup> Wakati huo Yesu akaja kutoka Galilaya mpaka Yordani kwa Yohana ili abatizwe.",
			"14": "<sup>14</sup> Lakini Yohana alitaka kumzuia, akisema, Mimi nahitaji kubatizwa na wewe, nawe waja kwangu?",
			"15": "<sup>15</sup> Yesu akajibu akamwambia, Kubali hivi sasa; kwa kuwa ndivyo itupasavyo kuitimiza haki yote. Basi akamkubali.",
			"16": "<sup>16</sup> Naye Yesu alipokwisha kubatizwa mara akapanda kutoka majini; na tazama, mbingu zikamfunukia, akamwona Roho wa Mungu akishuka kama hua, akija juu yake;",
			"17": "<sup>17</sup> na tazama, sauti kutoka mbinguni ikisema, Huyu ni Mwanangu, mpendwa wangu, ninayependezwa naye."
		},
		"4": {
			"1": "<sup>1</sup> Kisha Yesu alipandishwa na Roho nyikani, ili ajaribiwe na Ibilisi.",
			"2": "<sup>2</sup> Akafunga siku arobaini mchana na usiku, mwisho akaona njaa.",
			"3": "<sup>3</sup> Mjaribu akamjia akamwambia, Ukiwa ndiwe Mwana wa Mungu, amuru kwamba mawe haya yawe mikate.",
			"4": "<sup>4</sup> Naye akajibu akasema, Imeandikwa, Mtu hataishi kwa mkate tu, ila kwa kila neno litokalo katika kinywa cha Mungu.",
			"5": "<sup>5</sup> Kisha Ibilisi akamchukua mpaka mji mtakatifu, akamweka juu ya kinara cha hekalu,",
			"6": "<sup>6</sup> akamwambia, Ukiwa ndiwe Mwana wa Mungu jitupe chini; kwa maana imeandikwa, Atakuagizia malaika zake; Na mikononi mwao watakuchukua; Usije ukajikwaa mguu wako katika jiwe.",
			"7": "<sup>7</sup> Yesu akamwambia, Tena imeandikwa, Usimjaribu Bwana Mungu wako.",
			"8": "<sup>8</sup> Kisha Ibilisi akamchukua mpaka mlima mrefu mno, akamwonyesha milki zote za ulimwengu, na fahari yake,",
			"9": "<sup>9</sup> akamwambia, Haya yote nitakupa, ukianguka kunisujudia.",
			"10": "<sup>10</sup> Ndipo Yesu alipomwambia, Nenda zako, Shetani; kwa maana imeandikwa, Msujudie Bwana Mungu wako, umwabudu yeye peke yake.",
			"11": "<sup>11</sup> Kisha Ibilisi akamwacha; na tazama, wakaja malaika wakamtumikia.",
			"12": "<sup>12</sup> Basi Yesu aliposikia ya kwamba Yohana amefungwa, alikwenda zake mpaka Galilaya;",
			"13": "<sup>13</sup> akatoka Nazareti, akaja akakaa Kapernaumu, mji ulioko pwani, mipakani mwa Zabuloni na Naftali;",
			"14": "<sup>14</sup> ili litimie neno lililonenwa na nabii Isaya, akisema,",
			"15": "<sup>15</sup> Nchi ya Zabuloni na nchi ya Naftali, Njia ya bahari, ng’ambo ya Yordani, Galilaya ya mataifa,",
			"16": "<sup>16</sup> Watu wale waliokaa katika giza Wameona mwanga mkuu, Nao waliokaa katika nchi na uvuli wa mauti Mwanga umewazukia.",
			"17": "<sup>17</sup> Tokea wakati huo Yesu alianza kuhubiri, na kusema, Tubuni; kwa maana ufalme wa mbinguni umekaribia.",
			"18": "<sup>18</sup> Naye alipokuwa akitembea kando ya bahari ya Galilaya, aliona ndugu wawili, Simoni aitwaye Petro, na Andrea nduguye, wakitupa jarife baharini; kwa maana walikuwa wavuvi.",
			"19": "<sup>19</sup> Akawaambia, Nifuateni, nami nitawafanya kuwa wavuvi wa watu.",
			"20": "<sup>20</sup> Mara wakaziacha nyavu zao, wakamfuata.",
			"21": "<sup>21</sup> Akaendelea mbele, akaona ndugu wengine wawili, Yakobo wa Zebedayo, na Yohana nduguye, ambao walikuwamo chomboni pamoja na Zebedayo baba yao, wakizitengeneza nyavu zao; akawaita.",
			"22": "<sup>22</sup> Mara wakakiacha chombo na baba yao, wakamfuata.",
			"23": "<sup>23</sup> Naye alikuwa akizunguka katika Galilaya yote, akifundisha katika masinagogi yao, na kuihubiri Habari Njema ya ufalme, na kuponya ugonjwa na udhaifu wa kila namna katika watu.",
			"24": "<sup>24</sup> Na habari zake zikaenea katika Shamu yote; wakamletea wote waliokuwa hawawezi, walioshikwa na maradhi mbalimbali na mateso, wenye pepo, na wenye kifafa, na wenye kupooza; akawaponya.",
			"25": "<sup>25</sup> Na makutano mengi wakamfuata, kutoka Galilaya, na Dekapoli, na Yerusalemu, na Uyahudi, na ng’ambo ya Yordani"
		},
		"5": {
			"1": "<sup>1</sup> Naye alipowaona makutano, alipanda mlimani; na alipokwisha kuketi, wanafunzi wake walimjia;",
			"2": "<sup>2</sup> akafumbua kinywa chake, akawafundisha, akisema,",
			"3": "<sup>3</sup> Heri walio maskini wa roho; Maana ufalme wa mbinguni ni wao.",
			"4": "<sup>4</sup> Heri wenye huzuni; Maana hao watafarijika.",
			"5": "<sup>5</sup> Heri wenye upole; Maana hao watairithi nchi.",
			"6": "<sup>6</sup> Heri wenye njaa na kiu ya haki; Maana hao watashibishwa.",
			"7": "<sup>7</sup> Heri wenye rehema; Maana hao watapata rehema.",
			"8": "<sup>8</sup> Heri wenye moyo safi; Maana hao watamwona Mungu.",
			"9": "<sup>9</sup> Heri wapatanishi; Maana hao wataitwa wana wa Mungu.",
			"10": "<sup>10</sup> Heri wenye kuudhiwa kwa ajili ya haki; Maana ufalme wa mbinguni ni wao.",
			"11": "<sup>11</sup> Heri ninyi watakapowashutumu na kuwaudhi na kuwanenea kila neno baya kwa uongo, kwa ajili yangu.",
			"12": "<sup>12</sup> Furahini, na kushangilia; kwa kuwa thawabu yenu ni kubwa mbinguni; kwa maana ndivyo walivyowaudhi manabii waliokuwa kabla yenu.",
			"13": "<sup>13</sup> Ninyi ni chumvi ya dunia; lakini chumvi ikiwa imeharibika itatiwa nini hata ikolee? Haifai tena kabisa, ila kutupwa nje na kukanyagwa na watu.",
			"14": "<sup>14</sup> Ninyi ni nuru ya ulimwengu. Mji hauwezi kusitirika ukiwa juu ya mlima.",
			"15": "<sup>15</sup> Wala watu hawawashi taa na kuiweka chini ya pishi, bali juu ya kiango; nayo yawaangaza wote waliomo nyumbani.",
			"16": "<sup>16</sup> Vivyo hivyo nuru yenu na iangaze mbele ya watu, wapate kuyaona matendo yenu mema, wamtukuze Baba yenu aliye mbinguni.",
			"17": "<sup>17</sup> Msidhani ya kuwa nalikuja kuitangua torati au manabii; la, sikuja kutangua, bali kutimiliza.",
			"18": "<sup>18</sup> Kwa maana, amin, nawaambia, Mpaka mbingu na nchi zitakapoondoka, yodi moja wala nukta moja ya torati haitaondoka, hata yote yatimie.",
			"19": "<sup>19</sup> Basi mtu ye yote atakayevunja amri moja katika hizi zilizo ndogo, na kuwafundisha watu hivyo, ataitwa mdogo kabisa katika ufalme wa mbinguni; bali mtu atakayezitenda na kuzifundisha, huyo ataitwa mkubwa katika ufalme wa mbinguni.",
			"20": "<sup>20</sup> Maana nawaambia ya kwamba, Haki yenu isipozidi hiyo haki ya waandishi na Mafarisayo, hamtaingia kamwe katika ufalme wa mbinguni.",
			"21": "<sup>21</sup> Mmesikia watu wa kale walivyoambiwa, Usiue, na mtu akiua, itampasa hukumu.",
			"22": "<sup>22</sup> Bali mimi nawaambieni, Kila amwoneaye ndugu yake hasira itampasa hukumu; na mtu akimfyolea ndugu yake, itampasa baraza; na mtu akimwapiza, itampasa jehanum ya moto.",
			"23": "<sup>23</sup> Basi ukileta sadaka yako madhabahuni, na huku ukikumbuka ya kuwa ndugu yako ana neno juu yako,",
			"24": "<sup>24</sup> iache sadaka yako mbele ya madhabahu, uende zako, upatane kwanza na ndugu yako, kisha urudi uitoe sadaka yako.",
			"25": "<sup>25</sup> Patana na mshitaki wako upesi, wakati uwapo pamoja naye njiani; yule mshitaki asije akakupeleka kwa kadhi, na kadhi akakupeleka kwa askari, ukatupwa gerezani.",
			"26": "<sup>26</sup> Amin, nakuambia, Hutoki humo kamwe hata uishe kulipa senti ya mwisho.",
			"27": "<sup>27</sup> Mmesikia kwamba imenenwa, Usizini;",
			"28": "<sup>28</sup> lakini mimi nawaambia, Kila mtu atazamaye mwanamke kwa kumtamani, amekwisha kuzini naye moyoni mwake.",
			"29": "<sup>29</sup> Jicho lako la kuume likikukosesha, ling’oe ulitupe mbali nawe; kwa maana yakufaa kiungo chako kimoja kipotee wala mwili wako mzima usitupwe katika jehanum.",
			"30": "<sup>30</sup> Na mkono wako wa kuume ukikukosesha, ukate uutupe mbali nawe; kwa maana yakufaa kiungo chako kimoja kipotee wala mwili wako mzima usitupwe katika jehanum.",
			"31": "<sup>31</sup> Imenenwa pia, Mtu akimwacha mkewe, na ampe hati ya talaka;",
			"32": "<sup>32</sup> lakini mimi nawaambia, Kila mtu amwachaye mkewe, isipokuwa kwa habari ya uasherati, amfanya kuwa mzinzi; na mtu akimwoa yule aliyeachwa, azini.",
			"33": "<sup>33</sup> Tena mmesikia watu wa kale walivyoambiwa, Usiape uongo, ila mtimizie Bwana nyapo zako;",
			"34": "<sup>34</sup> lakini mimi nawaambia, Usiape kabisa; hata kwa mbingu, kwa maana ndicho kiti cha enzi cha Mungu;",
			"35": "<sup>35</sup> wala kwa nchi, kwa maana ndiyo pa kuwekea miguu yake; wala kwa Yerusalemu, kwa maana ndio mji wa Mfalme mkuu.",
			"36": "<sup>36</sup> Wala usiape kwa kichwa chako, maana huwezi kufanya unywele mmoja kuwa mweupe au mweusi.",
			"37": "<sup>37</sup> Bali maneno yenu yawe Ndiyo, ndiyo; Siyo, siyo; kwa kuwa yazidiyo hayo yatoka kwa yule mwovu.",
			"38": "<sup>38</sup> Mmesikia kwamba imenenwa, Jicho kwa jicho, na jino kwa jino;",
			"39": "<sup>39</sup> Lakini mimi nawaambia, Msishindane na mtu mwovu; lakini mtu akupigaye shavu la kuume, mgeuzie na la pili.",
			"40": "<sup>40</sup> Na mtu atakaye kukushitaki na kuitwaa kanzu yako, mwachie na joho pia.",
			"41": "<sup>41</sup> Na mtu atakayekulazimisha mwendo wa maili moja, nenda naye mbili.",
			"42": "<sup>42</sup> Akuombaye, mpe; naye atakaye kukopa kwako, usimpe kisogo.",
			"43": "<sup>43</sup> Mmesikia kwamba imenenwa, Umpende jirani yako, na, Umchukie adui yako;",
			"44": "<sup>44</sup> lakini mimi nawaambia, Wapendeni adui zenu, waombeeni wanaowaudhi,",
			"45": "<sup>45</sup> ili mpate kuwa wana wa Baba yenu aliye mbinguni; maana yeye huwaangazia jua lake waovu na wema, huwanyeshea mvua wenye haki na wasio haki.",
			"46": "<sup>46</sup> Maana mkiwapenda wanaowapenda ninyi mwapata thawabu gani? Hata watoza ushuru, je! Nao hawafanyi yayo hayo?",
			"47": "<sup>47</sup> Tena mkiwaamkia ndugu zenu tu, mnatenda tendo gani la ziada? Hata watu wa mataifa, je! Nao hawafanyi kama hayo?",
			"48": "<sup>48</sup> Basi ninyi mtakuwa wakamilifu, kama Baba yenu wa mbinguni alivyo mkamilifu."
		},
		"6": {
			"1": "<sup>1</sup> Angalieni msifanye wema wenu machoni pa watu, kusudi mtazamwe na wao; kwa maana mkifanya kama hayo, hampati thawabu kwa Baba yenu aliye mbinguni.",
			"2": "<sup>2</sup> Basi wewe utoapo sadaka, usipige panda mbele yako, kama wanafiki wafanyavyo katika masinagogi na njiani, ili watukuzwe na watu.",
			"3": "<sup>3</sup> Amin, nawaambieni, Wamekwisha kupata thawabu yao. Bali wewe utoapo sadaka, hata mkono wako wa kushoto usijue ufanyalo mkono wako wa kuume;",
			"4": "<sup>4</sup> sadaka yako iwe kwa siri; na Baba yako aonaye sirini atakujazi.",
			"5": "<sup>5</sup> Tena msalipo, msiwe kama wanafiki; kwa maana wao wapenda kusali hali wamesimama katika masinagogi na katika pembe za njia, ili waonekane na watu. Amin, nawaambia, Wamekwisha kupata thawabu yao.",
			"6": "<sup>6</sup> Bali wewe usalipo, ingia katika chumba chako cha ndani, na ukiisha kufunga mlango wako, usali mbele za Baba yako aliye sirini; na Baba yako aonaye sirini atakujazi.",
			"7": "<sup>7</sup> Nanyi mkiwa katika kusali, msipayuke-payuke, kama watu wa mataifa; maana wao hudhani ya kuwa watasikiwa kwa sababu ya maneno yao kuwa mengi.",
			"8": "<sup>8</sup> Basi msifanane na hao; maana Baba yenu anajua mnayohitaji kabla ninyi hamjamwomba.",
			"9": "<sup>9</sup> Basi ninyi salini hivi; Baba yetu uliye mbinguni, Jina lako litukuzwe, Ufalme wako uje,",
			"10": "<sup>10</sup> Mapenzi yako yatimizwe, hapa duniani kama huko mbinguni.",
			"11": "<sup>11</sup> Utupe leo riziki yetu.",
			"12": "<sup>12</sup> Utusamehe deni zetu, kama sisi nasi tuwasamehevyo wadeni wetu.",
			"13": "<sup>13</sup> Na usitutie majaribuni, lakini utuokoe na yule mwovu. [Kwa kuwa ufalme ni wako, na nguvu, na utukufu, hata milele. Amina.]",
			"14": "<sup>14</sup> Kwa maana mkiwasamehe watu makosa yao, na Baba yenu wa mbinguni atawasamehe ninyi.",
			"15": "<sup>15</sup> Bali msipowasamehe watu makosa yao, wala Baba yenu hatawasamehe ninyi makosa yenu.",
			"16": "<sup>16</sup> Tena mfungapo, msiwe kama wanafiki wenye uso wa kukunjamana; maana hujiumbua nyuso zao, ili waonekane na watu kuwa wanafunga. Amin, nawaambia, wamekwisha kupata thawabu yao.",
			"17": "<sup>17</sup> Bali wewe ufungapo, jipake mafuta kichwani, unawe uso;",
			"18": "<sup>18</sup> ili usionekane na watu kuwa unafunga, ila na Baba yako aliye sirini; na Baba yako aonaye sirini atakujazi.",
			"19": "<sup>19</sup> Msijiwekee hazina duniani, nondo na kutu viharibupo, na wevi huvunja na kuiba;",
			"20": "<sup>20</sup> bali jiwekeeni hazina mbinguni, kusikoharibika kitu kwa nondo wala kutu, wala wevi hawavunji wala hawaibi;",
			"21": "<sup>21</sup> kwa kuwa hazina yako ilipo, ndipo utakapokuwapo na moyo wako.",
			"22": "<sup>22</sup> Taa ya mwili ni jicho; basi jicho lako likiwa safi, mwili wako wote utakuwa na nuru.",
			"23": "<sup>23</sup> Lakini jicho lako likiwa bovu, mwili wako wote utakuwa na giza. Basi ile nuru iliyomo ndani yako ikiwa giza; si giza hilo!",
			"24": "<sup>24</sup> Hakuna mtu awezaye kutumikia mabwana wawili; kwa maana atamchukia huyu, na kumpenda huyu; ama atashikamana na huyu, na kumdharau huyu. Hamwezi kumtumikia Mungu na mali.",
			"25": "<sup>25</sup> Kwa sababu hiyo nawaambieni, Msisumbukie maisha yenu, mle nini au mnywe nini; wala miili yenu, mvae nini. Maisha je! Si zaidi ya chakula, na mwili zaidi ya mavazi?",
			"26": "<sup>26</sup> Waangalieni ndege wa angani, ya kwamba hawapandi, wala hawavuni, wala hawakusanyi ghalani; na Baba yenu wa mbinguni huwalisha hao. Ninyi je! Si bora kupita hao?",
			"27": "<sup>27</sup> Ni yupi kwenu ambaye akijisumbua aweza kujiongeza kimo chake hata mkono mmoja?",
			"28": "<sup>28</sup> Na mavazi, ya nini kuyasumbukia? Fikirini maua ya mashamba, jinsi yameavyo; hayafanyi kazi, wala hayasokoti",
			"29": "<sup>29</sup> nami nawaambia, ya kwamba hata Sulemani katika fahari yake yote hakuvikwa vizuri kama mojawapo la hayo.",
			"30": "<sup>30</sup> Basi, ikiwa Mungu huyavika hivi majani ya kondeni, yaliyopo leo, na kesho hutupwa kalibuni, je! Hatazidi sana kuwavika ninyi, enyi wa imani haba?",
			"31": "<sup>31</sup> Msisumbuke, basi, mkisema, Tule nini? Au Tunywe nini? Au Tuvae nini?",
			"32": "<sup>32</sup> Kwa maana hayo yote Mataifa huyatafuta; kwa sababu Baba yenu wa mbinguni anajua ya kuwa mnahitaji hayo yote.",
			"33": "<sup>33</sup> Bali utafuteni kwanza ufalme wake, na haki yake; na hayo yote mtazidishiwa.",
			"34": "<sup>34</sup> Basi msisumbukie ya kesho; kwa kuwa kesho itajisumbukia yenyewe. Yatosha kwa siku maovu yake."
		},
		"7": {
			"1": "<sup>1</sup> Msihukumu, msije mkahukumiwa ninyi.",
			"2": "<sup>2</sup> Kwa kuwa hukumu ile mhukumuyo, ndiyo mtakayohukumiwa; na kipimo kile mpimiacho, ndicho mtakachopimiwa.",
			"3": "<sup>3</sup> Basi, mbona wakitazama kibanzi kilicho ndani ya jicho la ndugu yako, na boriti iliyo ndani ya jicho lako mwenyewe huiangalii?",
			"4": "<sup>4</sup> Au utamwambiaje nduguyo, Niache nikitoe kibanzi katika jicho lako; na kumbe! Mna boriti ndani ya jicho lako mwenyewe?",
			"5": "<sup>5</sup> Mnafiki wewe, itoe kwanza ile boriti katika jicho lako mwenyewe; ndipo utakapoona vema kukitoa kile kibanzi katika jicho la ndugu yako.",
			"6": "<sup>6</sup> Msiwape mbwa kilicho kitakatifu, wala msitupe lulu zenu mbele ya nguruwe, wasije wakazikanyaga chini ya miguu yao, wakageuka na kuwararua.",
			"7": "<sup>7</sup> Ombeni, nanyi mtapewa; tafuteni, nanyi mtaona; bisheni, nanyi mtafunguliwa;",
			"8": "<sup>8</sup> kwa maana kila aombaye hupokea; naye atafutaye huona; naye abishaye atafunguliwa.",
			"9": "<sup>9</sup> Au kuna mtu yupi kwenu, ambaye, mwanawe akimwomba mkate, atampa jiwe?",
			"10": "<sup>10</sup> Au akiomba samaki, atampa nyoka?",
			"11": "<sup>11</sup> Basi ikiwa ninyi, mlio waovu, mnajua kuwapa watoto wenu vipawa vyema, je! Si zaidi sana Baba yenu aliye mbinguni atawapa mema wao wamwombao?",
			"12": "<sup>12</sup> Basi yo yote myatakayo mtendewe na watu, nanyi watendeeni vivyo hivyo; maana hiyo ndiyo torati na manabii.",
			"13": "<sup>13</sup> Ingieni kwa kupitia mlango ulio mwembamba; maana mlango ni mpana, na njia ni pana iendayo upotevuni, nao ni wengi waingiao kwa mlango huo.",
			"14": "<sup>14</sup> Bali mlango ni mwembamba, na njia imesonga iendayo uzimani, nao waionao ni wachache.",
			"15": "<sup>15</sup> Jihadharini na manabii wa uongo, watu wanaowajia wamevaa mavazi ya kondoo, walakini kwa ndani ni mbwa-mwitu wakali.",
			"16": "<sup>16</sup> Mtawatambua kwa matunda yao. Je! Watu huchuma zabibu katika miiba, au tini katika mibaruti?",
			"17": "<sup>17</sup> Vivyo hivyo kila mti mwema huzaa matunda mazuri; na mti mwovu huzaa matunda mabaya.",
			"18": "<sup>18</sup> Mti mwema hauwezi kuzaa matunda mabaya, wala mti mwovu kuzaa matunda mazuri.",
			"19": "<sup>19</sup> Kila mti usiozaa tunda zuri hukatwa ukatupwa motoni.",
			"20": "<sup>20</sup> Ndiposa kwa matunda yao mtawatambua.",
			"21": "<sup>21</sup> Si kila mtu aniambiaye, Bwana, Bwana, atakayeingia katika ufalme wa mbinguni; bali ni yeye afanyaye mapenzi ya Baba yangu aliye mbinguni.",
			"22": "<sup>22</sup> Wengi wataniambia siku ile, Bwana, Bwana, hatukufanya unabii kwa jina lako, na kwa jina lako kutoa pepo, na kwa jina lako kufanya miujiza mingi?",
			"23": "<sup>23</sup> Ndipo nitawaambia dhahiri, Sikuwajua ninyi kamwe; ondokeni kwangu, ninyi mtendao maovu.",
			"24": "<sup>24</sup> Basi kila asikiaye hayo maneno yangu, na kuyafanya, atafananishwa na mtu mwenye akili, aliyejenga nyumba yake juu ya mwamba;",
			"25": "<sup>25</sup> mvua ikanyesha, mafuriko yakaja, pepo zikavuma, zikaipiga nyumba ile, isianguke; kwa maana misingi yake imewekwa juu ya mwamba.",
			"26": "<sup>26</sup> Na kila asikiaye hayo maneno yangu asiyafanye, atafananishwa na mtu mpumbavu, aliyejenga nyumba yake juu ya mchanga;",
			"27": "<sup>27</sup> mvua ikanyesha, mafuriko yakaja, pepo zikavuma, zikaipiga nyumba ile, ikaanguka; nalo anguko lake likawa kubwa.",
			"28": "<sup>28</sup> Ikawa, Yesu alipoyamaliza maneno hayo, makutano walishangaa mno kwa mafundisho yake;",
			"29": "<sup>29</sup> kwa maana alikuwa akiwafundisha kama mtu mwenye amri, wala si kama waandishi wao."
		},
		"8": {
			"1": "<sup>1</sup> Naye aliposhuka mlimani, makutano mengi walimfuata.",
			"2": "<sup>2</sup> Na tazama, akaja mtu mwenye ukoma akamsujudia, akisema, Bwana, ukitaka, waweza kunitakasa.",
			"3": "<sup>3</sup> Yesu akanyosha mkono, akamgusa, akisema Nataka; takasika. Na mara ukoma wake ukatakasika.",
			"4": "<sup>4</sup> Yesu akamwambia, Angalia, usimwambie mtu; ila enenda zako, ukajionyeshe kwa kuhani, ukaitoe sadaka kama alivyoamuru Musa, iwe ushuhuda kwao.",
			"5": "<sup>5</sup> Hata alipoingia Kapernaumu, akida mmoja alimjia,",
			"6": "<sup>6</sup> akamsihi, akisema, Bwana, mtumishi wangu amelala nyumbani, mgonjwa wa kupooza, anaumwa sana.",
			"7": "<sup>7</sup> Yesu akamwambia, Nitakuja, nimponye.",
			"8": "<sup>8</sup> Yule akida akamjibu, akasema, Bwana, mimi sistahili wewe uingie chini ya dari yangu; lakini sema neno tu, na mtumishi wangu atapona.",
			"9": "<sup>9</sup> Kwa maana mimi nami ni mtu niliyewekwa chini ya mamlaka, mwenye askari chini yangu; nikimwambia huyu, Nenda, huenda; na huyu, Njoo, huja; na mtumwa wangu, Fanya hivi, hufanya.",
			"10": "<sup>10</sup> Yesu aliposikia hayo, alistaajabu, akawaambia wale waliomfuata, Amin, nawaambieni, Sijaona imani kubwa namna hii, kwa ye yote katika Israeli.",
			"11": "<sup>11</sup> Nami nawaambieni, ya kwamba wengi watakuja kutoka mashariki na magharibi, nao wataketi pamoja na Ibrahimu, na Isaka, na Yakobo katika ufalme wa mbinguni;",
			"12": "<sup>12</sup> bali wana wa ufalme watatupwa katika giza la nje, ndiko kutakuwako kilio na kusaga meno.",
			"13": "<sup>13</sup> Naye Yesu akamwambia yule akida, Nenda zako; na iwe kwako kama ulivyoamini. Mtumishi wake akapona saa ile ile.",
			"14": "<sup>14</sup> Hata Yesu alipofika nyumbani kwa Petro, akamwona mkwe wa Petro, mamaye mkewe, amelala kitandani hawezi homa.",
			"15": "<sup>15</sup> Akamgusa mkono, homa ikamwacha; naye akaondoka, akawatumikia.",
			"16": "<sup>16</sup> Hata kulipokuwa jioni, wakamletea wengi wenye pepo; akawatoa pepo kwa neno lake, akawaponya wote waliokuwa hawawezi,",
			"17": "<sup>17</sup> ili litimie lile neno lililonenwa na nabii Isaya, akisema, Mwenyewe aliutwaa udhaifu wetu, Na kuyachukua magonjwa yetu.",
			"18": "<sup>18</sup> Naye Yesu alipoona makutano mengi wakimzunguka, aliamuru kuvuka kwenda ng’ambo.",
			"19": "<sup>19</sup> Mwandishi mmoja akamwendea, akamwambia, Mwalimu, nitakufuata ko kote uendako.",
			"20": "<sup>20</sup> Yesu akamwambia, Mbweha wana pango, na ndege wa angani wana viota; lakini Mwana wa Adamu hana pa kulaza kichwa chake.",
			"21": "<sup>21</sup> Mwingine katika wanafunzi wake akamwambia, Bwana, nipe ruhusa kwanza, niende nikamzike baba yangu.",
			"22": "<sup>22</sup> Lakini Yesu akamwambia, Nifuate; waache wafu wazike wafu wao.",
			"23": "<sup>23</sup> Akapanda chomboni, wanafunzi wake wakamfuata.",
			"24": "<sup>24</sup> Kukawa msukosuko mkuu baharini, hata chombo kikafunikizwa na mawimbi; naye alikuwa amelala usingizi.",
			"25": "<sup>25</sup> Wanafunzi wake wakamwendea, wakamwamsha, wakisema, Bwana, tuokoe, tunaangamia.",
			"26": "<sup>26</sup> Akawaambia, Mbona mmekuwa waoga, enyi wa imani haba? Mara akaondoka, akazikemea pepo na bahari; kukawa shwari kuu.",
			"27": "<sup>27</sup> Wale watu wakamaka wakisema, Huyu ni mtu wa namna gani hata pepo na bahari zamtii?",
			"28": "<sup>28</sup> Naye alipofika ng’ambo, katika nchi ya Wagerasi, watu wawili wenye pepo walikutana naye, wanatoka makaburini, wakali mno, hata mtu asiweze kuipitia njia ile.",
			"29": "<sup>29</sup> Na tazama, wakapiga kelele, wakisema, Tuna nini nawe, Mwana wa Mungu? Je! Umekuja kututesa kabla ya muhula wetu?",
			"30": "<sup>30</sup> Basi, kulikuwako mbali nao kundi la nguruwe wengi wakilisha.",
			"31": "<sup>31</sup> Wale pepo wakamsihi, wakisema, Ukitutoa, tuache twende, tukaingie katika lile kundi la nguruwe.",
			"32": "<sup>32</sup> Akawaambia, Nendeni. Wakatoka, wakaingia katika nguruwe; na kumbe! Kundi lote wakatelemka kwa kasi gengeni hata baharini, wakafa majini.",
			"33": "<sup>33</sup> Lakini wachungaji walikimbia, wakaenda zao mjini, wakazieneza habari zote na habari za wale wenye pepo pia.",
			"34": "<sup>34</sup> Na tazama, mji wote ukatoka kwenda kumlaki Yesu; nao walipomwona, walimsihi aondoke mipakani mwao."
		},
		"9": {
			"1": "<sup>1</sup> Akapanda chomboni, akavuka, akafika mjini kwao.",
			"2": "<sup>2</sup> Na tazama, wakamletea mtu mwenye kupooza, amelala kitandani; naye Yesu, alipoiona imani yao, alimwambia yule mwenye kupooza, Jipe moyo mkuu, mwanangu, umesamehewa dhambi zako.",
			"3": "<sup>3</sup> Na tazama, baadhi ya waandishi wakasema nafsini mwao, Huyu anakufuru.",
			"4": "<sup>4</sup> Naye Yesu, hali akijua mawazo yao, akasema, Mbona mnawaza maovu mioyoni mwenu?",
			"5": "<sup>5</sup> Kwa maana rahisi ni lipi, ni kusema, Umesamehewa dhambi zako, au ni kusema, Ondoka, uende?",
			"6": "<sup>6</sup> Lakini mpate kujua ya kwamba Mwana wa Adamu anayo amri duniani ya kusamehe dhambi, (amwambia yule mwenye kupooza) Ondoka, ujitwike kitanda chako, uende nyumbani kwako.",
			"7": "<sup>7</sup> Akaondoka, akaenda zake nyumbani kwake.",
			"8": "<sup>8</sup> Makutano walipomwona, walishikwa na hofu, wakamtukuza Mungu, aliyewapa watu amri ya namna hii.",
			"9": "<sup>9</sup> Naye Yesu alipokuwa akipita kutoka huko aliona mtu ameketi forodhani, aitwaye Mathayo, akamwambia, Nifuate. Akaondoka, akamfuata.",
			"10": "<sup>10</sup> Ikawa alipoketi nyumbani ale chakula, tazama, watoza ushuru wengi na wenye dhambi walikuja wakaketi pamoja na Yesu na wanafunzi wake.",
			"11": "<sup>11</sup> Mafarisayo walipoona, waliwaambia wanafunzi wake, Mbona mwalimu wenu anakula pamoja na watoza ushuru na wenye dhambi?",
			"12": "<sup>12</sup> Naye aliposikia, aliwaambia, Wenye afya hawahitaji tabibu, bali walio hawawezi.",
			"13": "<sup>13</sup> Lakini nendeni, mkajifunze maana yake maneno haya, Nataka rehema, wala si sadaka; kwa maana sikuja kuwaita wenye haki, bali wenye dhambi.",
			"14": "<sup>14</sup> Wakati ule wanafunzi wake Yohana wakamwendea, wakasema, Kwa nini sisi na Mafarisayo twafunga, bali wanafunzi wako hawafungi?",
			"15": "<sup>15</sup> Yesu akawaambia, Walioalikwa arusini wawezaje kuomboleza, muda bwana arusi akiwapo pamoja nao? Lakini siku zitakuja watakapoondolewa bwana arusi; ndipo watakapofunga.",
			"16": "<sup>16</sup> Hakuna mtu atiaye kiraka cha nguo mpya katika vazi kuukuu; maana kile kilichotiwa huondoa kipande cha lile vazi, na pale palipotatuka huzidi.",
			"17": "<sup>17</sup> Wala watu hawatii divai mpya katika viriba vikuukuu; na kama wakitia, vile viriba hupasuka, divai ikamwagika, na viriba vikaharibika; bali hutia divai mpya katika viriba vipya, vikahifadhika vyote.",
			"18": "<sup>18</sup> Alipokuwa akiwaambia hayo, tazama, akaja jumbe mmoja, akamsujudia, akisema, Binti yangu sasa hivi amekufa; lakini njoo, uweke mkono wako juu yake, naye ataishi.",
			"19": "<sup>19</sup> Akaondoka Yesu, akamfuata, pamoja na wanafunzi wake.",
			"20": "<sup>20</sup> Na tazama, mwanamke aliyekuwa na ugonjwa wa kutoka damu muda wa miaka kumi na miwili, alikuja kwa nyuma, akaugusa upindo wa vazi lake.",
			"21": "<sup>21</sup> Kwa maana alisema moyoni mwake, Nikigusa tu upindo wa vazi lake nitapona.",
			"22": "<sup>22</sup> Yesu akageuka, akamwona, akamwambia, Jipe moyo mkuu, binti yangu; imani yako imekuponya. Yule mwanamke akapona tangu saa ile.",
			"23": "<sup>23</sup> Yesu alipofika nyumbani kwa yule jumbe, aliona wapiga filimbi, na makutano wakifanya maombolezo,",
			"24": "<sup>24</sup> akawaambia, Ondokeni; kwa maana kijana hakufa, amelala tu. Wakamcheka sana.",
			"25": "<sup>25</sup> Lakini makutano walipoondoshwa, aliingia, akamshika mkono; yule kijana akasimama.",
			"26": "<sup>26</sup> Zikaenea habari hizi katika nchi ile yote.",
			"27": "<sup>27</sup> Yesu alipokuwa akipita kutoka huko, vipofu wawili wakamfuata wakipaza sauti, wakisema, Uturehemu, Mwana wa Daudi.",
			"28": "<sup>28</sup> Naye alipofika nyumbani, wale vipofu walimwendea; Yesu akawaambia, Mnaamini kwamba naweza kufanya hili? Wakamwambia, Naam, Bwana.",
			"29": "<sup>29</sup> Ndipo alipowagusa macho, akasema, Kwa kadiri ya imani yenu mpate.",
			"30": "<sup>30</sup> Macho yao yakafumbuka. Naye Yesu akawaagiza kwa nguvu, akisema, Angalieni hata mtu mmoja asijue.",
			"31": "<sup>31</sup> Lakini wakatoka, wakaeneza habari zake katika nchi ile yote.",
			"32": "<sup>32</sup> Hata hao walipokuwa wakitoka, tazama, walimletea mtu bubu mwenye pepo.",
			"33": "<sup>33</sup> Na pepo alipotolewa, yule bubu alinena, makutano wakastajabu, wakasema, Haijaonekana hivi katika Israeli wakati wo wote.",
			"34": "<sup>34</sup> Lakini Mafarisayo wakasema, Kwa nguvu za mkuu wa pepo atoa pepo.",
			"35": "<sup>35</sup> Naye Yesu alikuwa akizunguka katika miji yote na vijiji, akifundisha katika masinagogi yao, na kuihubiri habari njema ya ufalme, na kuponya magonjwa yote na udhaifu wa kila aina.",
			"36": "<sup>36</sup> Na alipowaona makutano, aliwahurumia, kwa sababu walikuwa wamechoka na kutawanyika kama kondoo wasio na mchungaji.",
			"37": "<sup>37</sup> Ndipo alipowaambia wanafunzi wake, Mavuno ni mengi, lakini watenda kazi ni wachache.",
			"38": "<sup>38</sup> Basi mwombeni Bwana wa mavuno, apeleke watenda kazi katika mavuno yake."
		},
		"10": {
			"1": "<sup>1</sup> Akawaita wanafunzi wake kumi na wawili, akawapa amri juu ya pepo wachafu, wawatoe, na kupoza magonjwa yote na udhaifu wa kila aina.",
			"2": "<sup>2</sup> Na majina ya hao mitume kumi na wawili ni haya; Wa kwanza Simoni aliyeitwa Petro, na Andrea nduguye; Yakobo wa Zebedayo, na Yohana nduguye;",
			"3": "<sup>3</sup> Filipo, na Bartholomayo; Tomaso, na Mathayo mtoza ushuru; Yakobo wa Alfayo, na Thadayo;",
			"4": "<sup>4</sup> Simoni Mkananayo, na Yuda Iskariote, naye ndiye mwenye kumsaliti.",
			"5": "<sup>5</sup> Hao Thenashara Yesu aliwatuma, akawaagiza, akisema, Katika njia ya Mataifa msiende, wala katika mji wo wote wa Wasamaria msiingie.",
			"6": "<sup>6</sup> Afadhali shikeni njia kuwaendea kondoo waliopotea wa nyumba ya Israeli.",
			"7": "<sup>7</sup> Na katika kuenenda kwenu, hubirini, mkisema, Ufalme wa mbinguni umekaribia.",
			"8": "<sup>8</sup> Pozeni wagonjwa, fufueni wafu, takaseni wenye ukoma, toeni pepo; mmepata bure, toeni bure.",
			"9": "<sup>9</sup> Msichukue dhahabu, wala fedha, wala mapesa mishipini mwenu;",
			"10": "<sup>10</sup> wala mkoba wa safari, wala kanzu mbili, wala viatu, wala fimbo; maana mtenda kazi astahili posho lake.",
			"11": "<sup>11</sup> Na mji wo wote au kijiji cho chote mtakachoingia, tafuteni ni nani humo aliye mtu mwaminifu; mkae kwake hata mtakapotoka.",
			"12": "<sup>12</sup> Nanyi mkiingia katika nyumba isalimuni.",
			"13": "<sup>13</sup> Na nyumba ile ikistahili, amani yenu na iifikilie; la, kwamba haistahili, amani yenu na iwarudie ninyi.",
			"14": "<sup>14</sup> Na mtu asipowakaribisha wala kuyasikiliza maneno yenu, mtokapo katika nyumba ile, au mji ule, kung’uteni mavumbi ya miguuni mwenu.",
			"15": "<sup>15</sup> Amin, nawaambia, itakuwa rahisi nchi ya Sodoma na Gomora kustahimili adhabu ya siku ya hukumu, kuliko mji ule.",
			"16": "<sup>16</sup> Angalieni, mimi nawatuma kama kondoo kati ya mbwa-mwitu; basi iweni na busara kama nyoka, na kuwa watu wapole kama hua.",
			"17": "<sup>17</sup> Jihadharini na wanadamu; kwa maana watawapeleka mabarazani, na katika masinagogi yao watawapiga;",
			"18": "<sup>18</sup> nanyi mtachukuliwa mbele ya maliwali na wafalme kwa ajili yangu, kuwa ushuhuda kwao na kwa mataifa.",
			"19": "<sup>19</sup> Lakini hapo watakapowapeleka, msifikiri-fikiri jinsi mtakavyosema; maana mtapewa saa ile mtakayosema.",
			"20": "<sup>20</sup> Kwa kuwa si ninyi msemao, bali ni Roho wa Baba yenu asemaye ndani yenu.",
			"21": "<sup>21</sup> Na ndugu atamsaliti nduguye ili auawe, na baba atamsaliti mwana, na wana watawainukia wazazi wao, na kuwafisha.",
			"22": "<sup>22</sup> Nanyi mtakuwa mkichukiwa na watu wote kwa ajili ya jina langu; lakini mwenye kuvumilia hata mwisho, ndiye atakayeokoka.",
			"23": "<sup>23</sup> Lakini watakapowafukuza katika mji huu, kimbilieni mwingine; kwa maana ni kweli nawaambia, Hamtaimaliza miji ya Israeli, hata ajapo Mwana wa Adamu.",
			"24": "<sup>24</sup> Mwanafunzi hampiti mwalimu wake, wala mtumwa hampiti bwana wake.",
			"25": "<sup>25</sup> Yamtosha mwanafunzi kuwa kama mwalimu wake, na mtumwa kuwa kama bwana wake. Ikiwa wamemwita mwenye nyumba Beelzebuli, je! Si zaidi wale walio wa nyumbani mwake?",
			"26": "<sup>26</sup> Basi, msiwaogope, kwa maana hakuna neno lililositirika, ambalo halitafunuliwa; wala lililofichwa, ambalo halitajulikana.",
			"27": "<sup>27</sup> Niwaambialo ninyi katika giza, lisemeni katika nuru; na msikialo kwa siri, lihubirini juu ya nyumba.",
			"28": "<sup>28</sup> Msiwaogope wauuao mwili, wasiweze kuiua na roho; afadhali mwogopeni yule awezaye kuangamiza mwili na roho pia katika jehanum.",
			"29": "<sup>29</sup> Je! Mashomoro wawili hawauzwi kwa senti moja? Wala hata mmoja haanguki chini asipojua Baba yenu;",
			"30": "<sup>30</sup> lakini ninyi, hata nywele za vichwa vyenu zimehesabiwa zote.",
			"31": "<sup>31</sup> Msiogope basi; bora ninyi kuliko mashomoro wengi.",
			"32": "<sup>32</sup> Basi, kila mtu atakayenikiri mbele ya watu, nami nitamkiri mbele za Baba yangu aliye mbinguni.",
			"33": "<sup>33</sup> Bali mtu ye yote atakayenikana mbele ya watu, nami nitamkana mbele za Baba yangu aliye mbinguni.",
			"34": "<sup>34</sup> Msidhani ya kuwa nimekuja kuleta amani duniani; la! Sikuja kuleta amani, bali upanga.",
			"35": "<sup>35</sup> Kwa maana nalikuja kumfitini mtu na babaye, na binti na mamaye, na mkwe na mkwe mtu;",
			"36": "<sup>36</sup> na adui za mtu ni wale wa nyumbani mwake.",
			"37": "<sup>37</sup> Apendaye baba au mama kuliko mimi, hanistahili; wala apendaye mwana au binti kuliko mimi, hanistahili.",
			"38": "<sup>38</sup> Wala mtu asiyechukua msalaba wake akanifuata, hanistahili.",
			"39": "<sup>39</sup> Mwenye kuiona nafsi yake ataipoteza; naye mwenye kuipoteza nafsi yake kwa ajili yangu ataiona.",
			"40": "<sup>40</sup> Awapokeaye ninyi, anipokea mimi; naye anipokeaye mimi, ampokea yeye aliyenituma.",
			"41": "<sup>41</sup> Ampokeaye nabii kwa kuwa ni nabii, atapata thawabu ya nabii; naye ampokeaye mwenye haki kwa kuwa ni mwenye haki, atapata thawabu ya mwenye haki.",
			"42": "<sup>42</sup> Na mtu awaye yote atakayemnywesha mmojawapo wa wadogo hawa ngaa kikombe cha maji ya baridi, kwa kuwa ni mwanafunzi, amin, nawaambia, haitampotea kamwe thawabu yake."
		},
		"11": {
			"1": "<sup>1</sup> Ikawa Yesu alipokwisha kuwaagiza wanafunzi wake kumi na wawili, alitoka huko kwenda kufundisha na kuhubiri katika miji yao.",
			"2": "<sup>2</sup> Naye Yohana aliposikia huko gerezani matendo yake Kristo, alituma wanafunzi wake, kumwuliza,",
			"3": "<sup>3</sup> Wewe ndiwe yule ajaye, au tumtazamie mwingine?",
			"4": "<sup>4</sup> Yesu akajibu akawaambia, Nendeni mkamweleze Yohana mnayoyasikia na kuyaona;",
			"5": "<sup>5</sup> vipofu wanapata kuona, viwete wanakwenda, wenye ukoma wanatakaswa, viziwi wanasikia, wafu wanafufuliwa, na maskini wanahubiriwa habari njema.",
			"6": "<sup>6</sup> Naye heri awaye yote asiyechukizwa nami.",
			"7": "<sup>7</sup> Na hao walipokwenda zao, Yesu alianza kuwaambia makutano habari za Yohana, Mlitoka kwenda nyikani kutazama nini? Unyasi ukitikiswa na upepo?",
			"8": "<sup>8</sup> Lakini mlitoka kwenda kuona nini? Mtu aliyevikwa mavazi mororo? Tazama, watu wavaao mavazi mororo wamo katika nyumba za wafalme.",
			"9": "<sup>9</sup> Lakini kwa nini mlitoka? Ni kuona nabii? Naam, nawaambia, na aliye mkuu zaidi ya nabii.",
			"10": "<sup>10</sup> Huyo ndiye aliyeandikiwa haya, Tazama, mimi namtuma mjumbe wangu Mbele ya uso wako, Atakayeitengeneza njia yako mbele yako.",
			"11": "<sup>11</sup> Amin, nawaambieni, Hajaondokea mtu katika wazao wa wanawake aliye mkuu kuliko Yohana Mbatizaji; walakini aliye mdogo katika ufalme wa mbinguni ni mkuu kuliko yeye.",
			"12": "<sup>12</sup> Tangu siku za Yohana Mbatizaji hata sasa ufalme wa mbinguni hupatikana kwa nguvu, nao wenye nguvu wauteka.",
			"13": "<sup>13</sup> Kwa maana manabii wote na torati walitabiri mpaka wakati wa Yohana.",
			"14": "<sup>14</sup> Na ikiwa mnataka kukubali, yeye ndiye Eliya atakayekuja.",
			"15": "<sup>15</sup> Mwenye masikio, na asikie.",
			"16": "<sup>16</sup> Lakini nitakifananisha na nini kizazi hiki? Kimefanana na watoto wanaokaa sokoni, wanaowaita wenzao, wakisema,",
			"17": "<sup>17</sup> Tuliwapigia filimbi, wala hamkucheza; tuliomboleza, wala hamkulia.",
			"18": "<sup>18</sup> Maana Yohana alikuja, hali wala hanywi, wakasema, Yuna pepo.",
			"19": "<sup>19</sup> Mwana wa Adamu alikuja, akila na kunywa, wakasema, Mlafi huyu, na mlevi, rafiki yao watoza ushuru na wenye dhambi! Na hekima imejulikana kuwa ina haki kwa kazi zake.",
			"20": "<sup>20</sup> Ndipo akaanza kuikemea miji ile ambamo ndani yake ilifanyika miujiza yake iliyo mingi, kwa sababu haikutubu.",
			"21": "<sup>21</sup> Ole wako, Korazini! Ole wako, Bethsaida! Kwa kuwa kama miujiza iliyofanyika kwenu ingalifanyika katika Tiro na Sidoni, wangalitubu zamani kwa kuvaa magunia na majivu.",
			"22": "<sup>22</sup> Walakini nawaambieni, itakuwa rahisi Tiro na Sidoni kustahimili adhabu zao siku ya hukumu kuliko ninyi.",
			"23": "<sup>23</sup> Nawe Kapernaumu, je! Utakuzwa mpaka mbinguni? Utashushwa mpaka kuzimu; kwa kuwa kama miujiza iliyofanyika kwako ingalifanyika katika Sodoma, ungalikuwapo mji huo hata leo.",
			"24": "<sup>24</sup> Walakini nawaambieni, itakuwa rahisi nchi ya Sodoma kustahimili adhabu yake siku ya hukumu kuliko wewe.",
			"25": "<sup>25</sup> Wakati ule Yesu akajibu, akasema, Nakushukuru, Baba, Bwana wa mbingu na nchi, kwa kuwa mambo haya uliwaficha wenye hekima na akili, ukawafunulia watoto wachanga.",
			"26": "<sup>26</sup> Naam, Baba, kwa kuwa ndivyo ilivyopendeza mbele zako.",
			"27": "<sup>27</sup> Akasema, Nimekabidhiwa vyote na baba yangu; wala hakuna amjuaye Mwana, ila Baba; wala hakuna amjuaye Baba, ila Mwana, na ye yote ambaye Mwana apenda kumfunulia.",
			"28": "<sup>28</sup> Njoni kwangu, ninyi nyote msumbukao na wenye kulemewa na mizigo, nami nitawapumzisha.",
			"29": "<sup>29</sup> Jitieni nira yangu, mjifunze kwangu; kwa kuwa mimi ni mpole na mnyenyekevu wa moyo; nanyi mtapata raha nafsini mwenu;",
			"30": "<sup>30</sup> kwa maana nira yangu ni laini, na mzigo wangu ni mwepesi."
		},
		"12": {
			"1": "<sup>1</sup> Wakati ule Yesu alipita katika mashamba siku ya sabato; wanafunzi wake wakaona njaa, wakaanza kuvunja masuke, wakala.",
			"2": "<sup>2</sup> Na Mafarisayo walipoona, walimwambia, Tazama! Wanafunzi wako wanafanya neno ambalo si halali kulifanya siku ya sabato.",
			"3": "<sup>3</sup> Akawaambia, Hamkusoma alivyotenda Daudi, alipokuwa na njaa, yeye na wenziwe?",
			"4": "<sup>4</sup> jinsi alivyoingia katika nyumba ya Mungu, akaila mikate ile ya wonyesho, ambayo si halali kwake kuila wala kwa wale wenziwe, ila kwa makuhani peke yao?",
			"5": "<sup>5</sup> Wala hamkusoma katika torati, kwamba siku ya sabato makuhani hekaluni huinajisi sabato wasipate hatia?",
			"6": "<sup>6</sup> Lakini nawaambieni, kwamba hapa yupo aliye mkuu kuliko hekalu.",
			"7": "<sup>7</sup> Lakini kama mngalijua maana yake maneno haya, Nataka rehema, wala si sadaka, msingaliwalaumu wasio na hatia.",
			"8": "<sup>8</sup> Kwa maana Mwana wa Adamu ndiye Bwana wa sabato.",
			"9": "<sup>9</sup> Akaondoka huko, akaingia katika sinagogi lao.",
			"10": "<sup>10</sup> Na tazama, yumo mtu mwenye mkono umepooza; wakamwuliza, wakisema, Ni halali kuponya watu siku ya sabato? Wapate kumshitaki.",
			"11": "<sup>11</sup> Akawaambia, Ni mtu yupi miongoni mwenu mwenye kondoo mmoja, na yule kondoo ametumbukia shimoni siku ya sabato, asiyemshika akamwopoa?",
			"12": "<sup>12</sup> Je! Mtu ni bora kuliko kondoo mara ngapi? Basi ni halali kutenda mema siku ya sabato.",
			"13": "<sup>13</sup> Kisha akamwambia yule mtu, Nyosha mkono wako; akaunyosha, ukapona, ukawa mzima kama wa pili.",
			"14": "<sup>14</sup> Lakini wale Mafarisayo wakatoka wakafanya shauri juu yake jinsi ya kumwangamiza.",
			"15": "<sup>15</sup> Naye Yesu hali akiyafahamu hayo akaondoka huko; na watu wengi wakamfuata; akawaponya wote,",
			"16": "<sup>16</sup> akawakataza wasimdhihirishe;",
			"17": "<sup>17</sup> ili litimie neno lililonenwa na nabii Isaya, akisema,",
			"18": "<sup>18</sup> Tazama, mtumishi wangu niliyemteua; Mpendwa wangu, moyo wangu uliyependezwa naye; Nitatia roho yangu juu yake, Naye atawatangazia Mataifa hukumu.",
			"19": "<sup>19</sup> Hatateta wala hatapaza sauti yake; Wala mtu hatasikia sauti yake njiani.",
			"20": "<sup>20</sup> Mwanzi uliopondeka hatauvunja, Wala utambi utokao moshi hatauzima, Hata ailetapo hukumu ikashinda.",
			"21": "<sup>21</sup> Na jina lake Mataifa watalitumainia.",
			"22": "<sup>22</sup> Wakati ule akaletewa mtu mwenye pepo, kipofu, naye ni bubu; akamponya, hata yule bubu akanena na kuona.",
			"23": "<sup>23</sup> Makutano wote wakashangaa, wakasema, Huyu siye mwana wa Daudi?",
			"24": "<sup>24</sup> Lakini Mafarisayo waliposikia, walisema, Huyu hatoi pepo, ila kwa Beelzebuli mkuu wa pepo.",
			"25": "<sup>25</sup> Basi Yesu akijua mawazo yao, akawaambia, Kila ufalme ukifitinika juu ya nafsi yake, hufanyika ukiwa; tena mji au nyumba yo yote ikifitinika juu ya nafsi yake, haitasimama.",
			"26": "<sup>26</sup> Na Shetani akimtoa Shetani, amefitinika juu ya nafsi yake; basi ufalme wake utasimamaje?",
			"27": "<sup>27</sup> Na mimi nikitoa pepo kwa Beelzebuli, je! Wana wenu huwatoa kwa nani? Kwa sababu hiyo hao ndio watakaowahukumu.",
			"28": "<sup>28</sup> Lakini mimi nikitoa pepo kwa Roho wa Mungu, basi ufalme wa Mungu umekwisha kuwajilia.",
			"29": "<sup>29</sup> Ama awezaje mtu kuingia ndani ya nyumba ya mtu mwenye nguvu na kuviteka vyombo vyake, asipomfunga kwanza yule mwenye nguvu? Ndipo atakapoiteka nyumba yake.",
			"30": "<sup>30</sup> Mtu asiye pamoja nami yu kinyume changu; na mtu asiyekusanya pamoja nami hutapanya.",
			"31": "<sup>31</sup> Kwa sababu hiyo nawaambia, Kila dhambi na kila neno la kufuru watasamehewa wanadamu, ila kwa kumkufuru Roho hawatasamehewa.",
			"32": "<sup>32</sup> Naye mtu ye yote atakayenena neno juu ya Mwana wa Adamu atasamehewa, bali yeye atakayenena neno juu ya Roho Mtakatifu hatasamehewa katika ulimwengu wa sasa, wala katika ule ujao.",
			"33": "<sup>33</sup> Ufanyeni mti kuwa mzuri na matunda yake kuwa mazuri; au ufanyeni mti kuwa mbaya na matunda yake mabaya, kwa maana kwa matunda yake mti hutambulikana.",
			"34": "<sup>34</sup> Enyi wazao wa nyoka, mwawezaje kunena mema, mkiwa wabaya? Maana, kinywa cha mtu huyanena yaujazayo moyo wake.",
			"35": "<sup>35</sup> Mtu mwema katika akiba njema hutoa mema; na mtu mbaya katika akiba mbaya hutoa mabaya.",
			"36": "<sup>36</sup> Basi, nawaambia, Kila neno lisilo maana, watakalolinena wanadamu, watatoa hesabu ya neno hilo siku ya hukumu.",
			"37": "<sup>37</sup> Kwa kuwa kwa maneno yako utahesabiwa haki, na kwa maneno yako utahukumiwa.",
			"38": "<sup>38</sup> Hapo baadhi ya waandishi na Mafarisayo wakamjibu, wakasema, Mwalimu, twataka kuona ishara kwako.",
			"39": "<sup>39</sup> Akajibu, akawaambia, Kizazi kibaya na cha zinaa chatafuta ishara, wala hakitapewa ishara, ila ishara ya nabii Yona.",
			"40": "<sup>40</sup> Kwani kama vile Yona alivyokuwa siku tatu mchana na usiku katika tumbo la nyangumi, hivyo ndivyo Mwana wa Adamu atakavyokuwa siku tatu mchana na usiku katika moyo wa nchi.",
			"41": "<sup>41</sup> Watu wa Ninawi watasimama siku ya hukumu pamoja na watu wa kizazi hiki, nao watawahukumu kuwa wamekosa; kwa sababu wao walitubu kwa mahubiri ya Yona; na hapa yupo aliye mkuu kuliko Yona.",
			"42": "<sup>42</sup> Malkia wa kusini atasimama siku ya hukumu pamoja na watu wa kizazi hiki, naye atawahukumu kuwa wamekosa; kwa sababu yeye alikuja kutoka pande za mwisho za dunia ili asikie hekima ya Sulemani, na hapa yupo aliye mkuu kuliko Sulemani.",
			"43": "<sup>43</sup> Pepo mchafu, amtokapo mtu, hupitia mahali pasipo maji, akitafuta mahali pa kupumzika, asipate.",
			"44": "<sup>44</sup> Halafu husema, Nitarudi nyumbani kwangu nilikotoka; hata akija, aiona tupu, imefagiwa, na kupambwa.",
			"45": "<sup>45</sup> Mara huenda, akachukua pamoja naye pepo wengine saba walio waovu kuliko yeye mwenyewe, nao huingia na kukaa humo; na mtu yule hali yake ya mwisho huwa mbaya kuliko ya kwanza. Ndivyo itakavyokuwa kwa kizazi hiki kilicho kibaya.",
			"46": "<sup>46</sup> Alipokuwa katika kusema na makutano, tazama, mama yake na ndugu zake walikuwa wakisimama nje, wataka kusema naye.",
			"47": "<sup>47</sup> Mtu mmoja akamwambia, Tazama, mama yako na ndugu zako wamesimama nje, wanataka kusema nawe.",
			"48": "<sup>48</sup> Akajibu, akamwambia yule aliyempasha habari, Mama yangu ni nani? Na ndugu zangu ni akina nani?",
			"49": "<sup>49</sup> Akawanyoshea mkono wanafunzi wake, akasema, Tazama, Mama yangu na ndugu zangu!",
			"50": "<sup>50</sup> Kwa maana ye yote atakayeyafanya mapenzi ya Baba yangu aliye mbinguni, huyu ndiye ndugu yangu, na umbu langu, na mama yangu."
		},
		"13": {
			"1": "<sup>1</sup> Siku ile Yesu akatoka nyumbani, akaketi kando ya bahari.",
			"2": "<sup>2</sup> Wakamkusanyikia makutano mengi, hata akapanda chomboni, akaketi; na ule mkutano wote wakasimama pwani.",
			"3": "<sup>3</sup> Akawaambia mambo mengi kwa mifano, akisema, Tazama, mpanzi alitoka kwenda kupanda.",
			"4": "<sup>4</sup> Hata alipokuwa akipanda, mbegu nyingine zilianguka karibu na njia, ndege wakaja wakazila;",
			"5": "<sup>5</sup> nyingine zikaanguka penye miamba, pasipokuwa na udongo mwingi; mara zikaota, kwa udongo kukosa kina;",
			"6": "<sup>6</sup> na jua lilipozuka ziliungua; na kwa kuwa hazina mizizi zikanyauka.",
			"7": "<sup>7</sup> Nyingine zikaanguka penye miiba; ile miiba ikamea, ikazisonga;",
			"8": "<sup>8</sup> nyingine zikaanguka penye udongo mzuri, zikazaa, moja mia, moja sitini, moja thelathini.",
			"9": "<sup>9</sup> Mwenye masikio na asikie.",
			"10": "<sup>10</sup> Wakaja wanafunzi, wakamwambia, Kwa nini wasema nao kwa mifano?",
			"11": "<sup>11</sup> Akajibu, akawaambia, Ninyi mmejaliwa kuzijua siri za ufalme wa mbinguni, bali wao hawakujaliwa.",
			"12": "<sup>12</sup> Kwa maana ye yote mwenye kitu atapewa, naye atazidishiwa tele; lakini ye yote asiye na kitu, hata kile alicho nacho atanyang’anywa.",
			"13": "<sup>13</sup> Kwa sababu hii nasema nao kwa mifano; kwa kuwa wakitazama hawaoni, na wakisikia hawasikii, wala kuelewa.",
			"14": "<sup>14</sup> Na neno la nabii Isaya linatimia kwao, likisema, Kusikia mtasikia, wala hamtaelewa; Kutazama mtatazama, wala hamtaona.",
			"15": "<sup>15</sup> Maana mioyo ya watu hawa imekuwa mizito, Na kwa masikio yao hawasikii vema, Na macho yao wameyafumba; Wasije wakaona kwa macho yao, Wakasikia kwa masikio yao, Wakaelewa kwa mioyo yao, Wakaongoka, nikawaponya.",
			"16": "<sup>16</sup> Lakini, heri macho yenu, kwa kuwa yanaona; na masikio yenu, kwa kuwa yanasikia.",
			"17": "<sup>17</sup> Kwa maana, amin, nawaambia, Manabii wengi na wenye haki walitamani kuyaona mnayoyaona ninyi, wasiyaone; na kuyasikia mnayoyasikia ninyi, wasiyasikie.",
			"18": "<sup>18</sup> Basi ninyi sikilizeni mfano wa mpanzi.",
			"19": "<sup>19</sup> Kila mtu alisikiapo neno la ufalme asielewe nalo, huja yule mwovu, akalinyakua lililopandwa moyoni mwake. Huyo ndiye aliyepandwa karibu na njia.",
			"20": "<sup>20</sup> Naye aliyepandwa penye miamba, huyo ndiye alisikiaye lile neno, akalipokea mara kwa furaha;",
			"21": "<sup>21</sup> lakini hana mizizi ndani yake, bali hudumu kwa muda; ikitukia dhiki au udhia kwa ajili ya lile neno, mara huchukizwa.",
			"22": "<sup>22</sup> Naye aliyepandwa penye miiba, huyo ndiye alisikiaye lile neno; na shughuli za dunia, na udanganyifu wa mali hulisonga lile neno; likawa halizai.",
			"23": "<sup>23</sup> Naye aliyepandwa penye udongo mzuri, huyo ndiye alisikiaye lile neno, na kuelewa nalo; yeye ndiye azaaye matunda, huyu mia, na huyu sitini, na huyu thelathini.",
			"24": "<sup>24</sup> Akawatolea mfano mwingine, akisema, Ufalme wa mbinguni umefanana na mtu aliyepanda mbegu njema katika konde lake;",
			"25": "<sup>25</sup> lakini watu walipolala, akaja adui yake akapanda magugu katikati ya ngano, akaenda zake.",
			"26": "<sup>26</sup> Baadaye majani ya ngano yalipomea na kuzaa, yakaonekana na magugu.",
			"27": "<sup>27</sup> Watumwa wa mwenye nyumba wakaenda wakamwambia, Bwana, hukupanda mbegu njema katika konde lako? Limepata wapi basi magugu?",
			"28": "<sup>28</sup> Akawaambia, Adui ndiye aliyetenda hivi. Watumwa wakamwambia, Basi, wataka twende tukayakusanye?",
			"29": "<sup>29</sup> Akasema, La; msije mkakusanya magugu, na kuzing’oa ngano pamoja nayo.",
			"30": "<sup>30</sup> Viacheni vyote vikue hata wakati wa mavuno; na wakati wa mavuno nitawaambia wavunao, Yakusanyeni kwanza magugu, myafunge matita matita mkayachome; bali ngano ikusanyeni ghalani mwangu.",
			"31": "<sup>31</sup> Akawatolea mfano mwingine, akisema, Ufalme wa mbinguni umefanana na punje ya haradali, aliyoitwaa mtu akaipanda katika shamba lake;",
			"32": "<sup>32</sup> nayo ni ndogo kuliko mbegu zote; lakini ikiisha kumea, huwa kubwa kuliko mboga zote, ikawa mti, hata nyuni wa angani huja na kukaa katika matawi yake.",
			"33": "<sup>33</sup> Akawaambia mfano mwingine; Ufalme wa mbinguni umefanana na chachu aliyoitwaa mwanamke, akaisitiri ndani ya pishi tatu za unga, hata ukachachwa wote pia.",
			"34": "<sup>34</sup> Hayo yote Yesu aliwaambia makutano kwa mifano; wala pasipo mfano hakuwaambia neno;",
			"35": "<sup>35</sup> ili litimie neno lililonenwa na nabii, akisema, Nitafumbua kinywa changu kwa mifano, Nitayatamka yaliyositirika tangu awali.",
			"36": "<sup>36</sup> Kisha Yesu akawaaga makutano, akaingia nyumbani; wanafunzi wake wakamwendea, wakasema, Tufafanulie mfano wa magugu ya kondeni.",
			"37": "<sup>37</sup> Akajibu, akasema, Azipandaye zile mbegu njema ni Mwana wa Adamu;",
			"38": "<sup>38</sup> lile konde ni ulimwengu; zile mbegu njema ni wana wa ufalme; na yale magugu ni wana wa yule mwovu;",
			"39": "<sup>39</sup> yule adui aliyeyapanda ni Ibilisi; mavuno ni mwisho wa dunia; na wale wavunao ni malaika.",
			"40": "<sup>40</sup> Basi, kama vile magugu yakusanywavyo na kuchomwa motoni; ndivyo itakavyokuwa katika mwisho wa dunia.",
			"41": "<sup>41</sup> Mwana wa Adamu atawatuma malaika zake, nao watakusanya kutoka katika ufalme wake machukizo yote, na hao watendao maasi,",
			"42": "<sup>42</sup> na kuwatupa katika tanuru ya moto, ndiko kutakuwako kilio na kusaga meno.",
			"43": "<sup>43</sup> Ndipo wenye haki watakapong’aa kama jua katika ufalme wa Baba yao. Mwenye masikio, na asikie.",
			"44": "<sup>44</sup> Tena ufalme wa mbinguni umefanana na hazina iliyositirika katika shamba; ambayo mtu alipoiona, aliificha; na kwa furaha yake akaenda akauza alivyo navyo vyote, akalinunua shamba lile.",
			"45": "<sup>45</sup> Tena ufalme wa mbinguni umefanana na mfanya biashara, mwenye kutafuta lulu nzuri;",
			"46": "<sup>46</sup> naye alipoona lulu moja ya thamani kubwa, alikwenda akauza alivyo navyo vyote, akainunua.",
			"47": "<sup>47</sup> Tena ufalme wa mbinguni umefanana na juya, lililotupwa baharini, likakusanya samaki wa kila namna;",
			"48": "<sup>48</sup> hata lilipojaa, walilivuta pwani; wakaketi, wakakusanya walio wema vyomboni, bali walio wabaya wakawatupa.",
			"49": "<sup>49</sup> Ndivyo itakavyokuwa katika mwisho wa dunia; malaika watatokea, watawatenga waovu mbali na wenye haki,",
			"50": "<sup>50</sup> na kuwatupa katika tanuru ya moto; ndiko kutakuwako kilio na kusaga meno.",
			"51": "<sup>51</sup> Yesu aliwauliza, Mmeyafahamu hayo yote? Wakamwambia, Naam.",
			"52": "<sup>52</sup> Akawaambia, Kwa sababu hiyo, kila mwandishi mwenye elimu ya ufalme wa mbinguni amefanana na mtu mwenye nyumba atoaye katika hazina yake vitu vipya na vya kale.",
			"53": "<sup>53</sup> Ikawa Yesu alipoimaliza mifano hiyo, alitoka akaenda zake.",
			"54": "<sup>54</sup> Na alipofika nchi yake, akawafundisha katika sinagogi lao, hata wakashangaa, wakasema, Huyu amepata wapi hekima hii na miujiza hii?",
			"55": "<sup>55</sup> Huyu si mwana wa seremala? Mamaye si yeye aitwaye Mariamu? Na nduguze si Yakobo, na Yusufu, na Simoni, na Yuda?",
			"56": "<sup>56</sup> Na maumbu yake wote hawapo hapa petu? Basi huyu amepata wapi haya yote?",
			"57": "<sup>57</sup> Wakachukizwa naye. Yesu akawaambia, Nabii hakosi kupata heshima, isipokuwa katika nchi yake, na nyumbani mwake mwenyewe.",
			"58": "<sup>58</sup> Wala hakufanya miujiza mingi huko, kwa sababu ya kutokuamini kwao."
		},
		"14": {
			"1": "<sup>1</sup> Wakati ule mfalme Herode alisikia habari za Yesu, akawaambia watumishi wake,",
			"2": "<sup>2</sup> Huyo ndiye Yohana Mbatizaji; amefufuka katika wafu; na kwa hiyo nguvu hizo zinatenda kazi ndani yake.",
			"3": "<sup>3</sup> Maana Herode alikuwa amemkamata Yohana, akamfunga, akamtia gerezani, kwa ajili ya Herodia, mke wa Filipo nduguye.",
			"4": "<sup>4</sup> Kwa sababu Yohana alimwambia, Si halali kwako kuwa naye.",
			"5": "<sup>5</sup> Naye alipotaka kumwua, aliwaogopa watu, maana walimwona Yohana kuwa nabii.",
			"6": "<sup>6</sup> Hata ilipofika sikukuu ya kuzaliwa kwake Herode, binti Herodia alicheza mbele ya watu, akampendeza Herode.",
			"7": "<sup>7</sup> Hata akaahidi kwa kiapo ya kwamba atampa lo lote atakaloliomba.",
			"8": "<sup>8</sup> Naye, huku akichochewa na mamaye, akasema, Nipe hapa katika kombe kichwa cha Yohana Mbatizaji.",
			"9": "<sup>9</sup> Naye mfalme akasikitika; lakini kwa ajili ya viapo vyake, na kwa ajili ya wale walioketi chakulani pamoja naye, akaamuru apewe;",
			"10": "<sup>10</sup> akatuma mtu, akamkata kichwa Yohana mle gerezani.",
			"11": "<sup>11</sup> Kichwa chake kikaletwa katika kombe, akapewa yule kijana; akakichukua kwa mamaye.",
			"12": "<sup>12</sup> Wanafunzi wake wakaenda, wakamchukua yule maiti, wakamzika; kisha wakaenda wakampasha Yesu habari.",
			"13": "<sup>13</sup> Naye Yesu aliposikia; hayo, aliondoka huko katika chombo, akaenda mahali pasipo watu, faraghani. Na makutano waliposikia, walimfuata kwa miguu kutoka mijini mwao.",
			"14": "<sup>14</sup> Yesu akatoka, akaona mkutano mkuu, akawahurumia, akawaponya wagonjwa wao.",
			"15": "<sup>15</sup> Hata kulipokuwa jioni, wanafunzi wake walimwendea, wakasema, Mahali hapa ni nyika tupu, na saa imekwisha pita; uwaage makutano, waende zao vijijini, wakajinunulie vyakula.",
			"16": "<sup>16</sup> Yesu akawaambia, Hawana haja kwenda zao, wapeni ninyi vyakula.",
			"17": "<sup>17</sup> Wakamwambia, Hamna kitu hapa ila mikate mitano na samaki wawili.",
			"18": "<sup>18</sup> Akasema, Nileteeni hapa.",
			"19": "<sup>19</sup> Akawaagiza makutano waketi katika majani; akaitwaa ile mikate mitano na wale samaki wawili, akatazama juu mbinguni, akabariki, akaimega ile mikate, akawapa wanafunzi, wanafunzi wakawapa makutano.",
			"20": "<sup>20</sup> Wakala wote wakashiba; wakayaokota masazo ya vipande vya mikate, vikapu kumi na viwili, vimejaa.",
			"21": "<sup>21</sup> Nao waliokula walikuwa wanaume wapata elfu tano, bila wanawake na watoto.",
			"22": "<sup>22</sup> Mara akawalazimisha wanafunzi wake wapande chomboni na kutangulia mbele yake kwenda ng’ambo, wakati yeye alipokuwa akiwaaga mkutano.",
			"23": "<sup>23</sup> Naye alipokwisha kuwaaga makutano, alipanda mlimani faraghani, kwenda kuomba. Na kulipokuwa jioni, alikuwako huko peke yake.",
			"24": "<sup>24</sup> Na kile chombo kimekwisha kufika katikati ya bahari, kinataabika sana na mawimbi; maana upepo ulikuwa wa mbisho.",
			"25": "<sup>25</sup> Hata wakati wa zamu ya nne ya usiku Yesu akawaendea, akienda kwa miguu juu ya bahari.",
			"26": "<sup>26</sup> Wanafunzi walipomwona akienda juu ya bahari, wakafadhaika, wakisema, Ni kivuli; wakapiga yowe kwa hofu.",
			"27": "<sup>27</sup> Mara Yesu alinena, akawaambia, Jipeni moyo ni mimi; msiogope.",
			"28": "<sup>28</sup> Petro akamjibu, akasema, Bwana, ikiwa ni wewe, niamuru nije kwako juu ya maji.",
			"29": "<sup>29</sup> Akasema, Njoo. Petro akashuka chomboni, akaenda kwa miguu juu ya maji, ili kumwendea Yesu.",
			"30": "<sup>30</sup> Lakini alipouona upepo, akaogopa; akaanza kuzama, akapiga yowe, akisema, Bwana, niokoe.",
			"31": "<sup>31</sup> Mara Yesu akanyosha mkono wake, akamshika, akamwambia, Ewe mwenye imani haba, mbona uliona shaka?",
			"32": "<sup>32</sup> Nao walipopanda chomboni, upepo ulikoma.",
			"33": "<sup>33</sup> Nao waliokuwamo ndani ya chombo wakamsujudia, wakisema, Hakika wewe u Mwana wa Mungu.",
			"34": "<sup>34</sup> Na walipokwisha kuvuka, walifika nchi ya Genesareti.",
			"35": "<sup>35</sup> Na watu wa mahali pale walipomtambua, walituma watu kwenda nchi zile zilizo kandokando, wakamletea wote waliokuwa hawawezi;",
			"36": "<sup>36</sup> nao wakamsihi waguse hata pindo la vazi lake tu; na wote waliogusa wakaponywa kabisa."
		},
		"15": {
			"1": "<sup>1</sup> Ndipo Mafarisayo na waandishi toka Yerusalemu walipomwendea Yesu wakisema,",
			"2": "<sup>2</sup> Mbona wanafunzi wako huyahalifu mapokeo ya wazee, kwa maana hawanawi mikono walapo chakula.",
			"3": "<sup>3</sup> Akajibu, akawaambia, Mbona ninyi nanyi huihalifu amri ya Mungu kwa ajili ya mapokeo yenu?",
			"4": "<sup>4</sup> Kwa kuwa Mungu alisema, Mheshimu baba yako na mama yako, na Amtukanaye baba yake au mama yake kufa na afe.",
			"5": "<sup>5</sup> Bali ninyi husema, Atakayemwambia babaye au mamaye, Cho chote kikupasacho kusaidiwa na mimi ni wakfu,",
			"6": "<sup>6</sup> basi asimheshimu baba yake au mama yake. Mkalitangua neno la Mungu kwa ajili ya mapokeo yenu.",
			"7": "<sup>7</sup> Enyi wanafiki, ni vema alivyotabiri Isaya kwa habari zenu, akisema,",
			"8": "<sup>8</sup> Watu hawa huniheshimu kwa midomo; Ila mioyo yao iko mbali nami.",
			"9": "<sup>9</sup> Nao waniabudu bure, Wakifundisha mafundisho Yaliyo maagizo ya wanadamu.",
			"10": "<sup>10</sup> Akawaita makutano akawaambia",
			"11": "<sup>11</sup> Sikilizeni, mfahamu; Sicho kiingiacho kinywani kimtiacho mtu unajisi; bali kitokacho kinywani ndicho kimtiacho mtu unajisi.",
			"12": "<sup>12</sup> Ndipo wanafunzi wake walipomwendea, wakamwambia, Wajua ya kuwa Mafarisayo walipolisikia neno lile walichukizwa?",
			"13": "<sup>13</sup> Akajibu, akasema, Kila pando asilolipanda Baba yangu wa mbinguni litang’olewa.",
			"14": "<sup>14</sup> Waacheni; hao ni viongozi vipofu wa vipofu. Na kipofu akimwongoza kipofu mwenzake, watatumbukia shimoni wote wawili.",
			"15": "<sup>15</sup> Basi Petro akajibu, akamwambia, Tueleze mfano huo.",
			"16": "<sup>16</sup> Yesu akasema, Hata sasa ninyi nanyi mngali hamna akili?",
			"17": "<sup>17</sup> Hamjafahamu bado ya kuwa kila kiingiacho kinywani hupita tumboni, kikatupwa chooni?",
			"18": "<sup>18</sup> Bali vitokavyo kinywani vyatoka moyoni; navyo ndivyo vimtiavyo mtu unajisi.",
			"19": "<sup>19</sup> Kwa maana moyoni hutoka mawazo mabaya, uuaji, uzinzi, uasherati; wivi, ushuhuda wa uongo, na matukano;",
			"20": "<sup>20</sup> hayo ndiyo yamtiayo mtu unajisi; lakini kula kabla hajanawa mikono hakumtii mtu unajisi.",
			"21": "<sup>21</sup> Yesu akaondoka huko, akaenda kando pande za Tiro na Sidoni.",
			"22": "<sup>22</sup> Na tazama, mwanamke Mkananayo wa mipaka ile akatokea, akampazia sauti akisema, Unirehemu, Bwana, Mwana wa Daudi; binti yangu amepagawa sana na pepo.",
			"23": "<sup>23</sup> Wala yeye hakumjibu neno. Nao wanafunzi wake wakamwendea, wakamwomba, wakisema, Mwache aende zake; kwa maana anapiga kelele nyuma yetu.",
			"24": "<sup>24</sup> Akajibu, akasema, Sikutumwa ila kwa kondoo waliopotea wa nyumba ya Israeli.",
			"25": "<sup>25</sup> Naye akaja akamsujudia, akisema Bwana, unisaidie.",
			"26": "<sup>26</sup> Akajibu, akasema, Si vema kukitwaa chakula cha watoto na kuwatupia mbwa.",
			"27": "<sup>27</sup> Akasema, Ndiyo, Bwana, lakini hata mbwa hula makombo yaangukayo mezani pa bwana zao.",
			"28": "<sup>28</sup> Ndipo Yesu akajibu, akamwambia, Mama, imani yako ni kubwa; na iwe kwako kama utakavyo. Akapona binti yake tangu saa ile.",
			"29": "<sup>29</sup> Yesu akaondoka huko, akafika kando ya bahari ya Galilaya; akapanda mlimani, akaketi huko.",
			"30": "<sup>30</sup> Wakamwendea makutano mengi wakimletea viwete, vipofu, mabubu, vilema, na wengine wengi, wakawaweka miguuni pake; akawaponya;",
			"31": "<sup>31</sup> hata ule mkutano wakastaajabu, walipowaona mabubu wanasema, vilema wanakuwa wazima, viwete wanakwenda, na vipofu wanaona; wakamtukuza Mungu wa Israeli.",
			"32": "<sup>32</sup> Yesu akawaita wanafunzi wake, akasema, Nawahurumia mkutano, kwa kuwa yapata siku tatu wamekaa pamoja nami, wala hawana kitu cha kula, tena kuwaaga wakifunga sipendi, wasije wakazimia njiani.",
			"33": "<sup>33</sup> Wanafunzi wake wakamwambia, Tupate wapi mikate mingi hapa nyikani, hata kushibisha mkutano mkuu namna hii?",
			"34": "<sup>34</sup> Yesu akawaambia, Mnayo mikate mingapi? Wakasema, Saba, na visamaki vichache.",
			"35": "<sup>35</sup> Akawaagiza mkutano waketi chini;",
			"36": "<sup>36</sup> akaitwaa ile mikate saba na vile visamaki, akashukuru akavimega, akawapa wanafunzi wake, nao wanafunzi wakawapa makutano.",
			"37": "<sup>37</sup> Wakala wote wakashiba; wakayaokota masazo ya vipande vya mikate makanda saba, yamejaa.",
			"38": "<sup>38</sup> Nao waliokula walikuwa wanaume elfu nne, bila wanawake na watoto.",
			"39": "<sup>39</sup> Akawaaga makutano, akapanda chomboni, akaenda pande za Magadani."
		},
		"16": {
			"1": "<sup>1</sup> Wakamjia Mafarisayo na Masadukayo, wakamjaribu, wakamwomba awaonyeshe ishara itokayo mbinguni.",
			"2": "<sup>2</sup> Akajibu, akawaambia, [Kukiwa jioni, mwasema, Kutakuwa na kianga; kwa maana mbingu ni nyekundu.",
			"3": "<sup>3</sup> Na asubuhi, mwasema, Leo kutakuwa na dhoruba; kwa maana mbingu ni nyekundu, tena kumetanda. Enyi wanafiki, mwajua kuutambua uso wa mbingu; lakini, je! Ishara za zamani hizi hamwezi kuzitambua?]",
			"4": "<sup>4</sup> Kizazi kibaya na cha zinaa chataka ishara; wala hakitapewa ishara, isipokuwa ishara ya Yona. Akawaacha, akaenda zake.",
			"5": "<sup>5</sup> Nao wanafunzi wakaenda hata ng’ambo, wakasahau kuchukua mikate.",
			"6": "<sup>6</sup> Yesu akawaambia, Angalieni, jilindeni na chachu ya Mafarisayo na Masadukayo.",
			"7": "<sup>7</sup> Wakabishana wao kwa wao, wakisema, Ni kwa sababu hatukuchukua mikate.",
			"8": "<sup>8</sup> Naye Yesu akafahamu, akawaambia, Mbona mnabishana ninyi kwa ninyi, enyi wa imani chache, kwa sababu hamna mikate?",
			"9": "<sup>9</sup> Hamjafahamu bado, wala hamkumbuki ile mikate mitano kwa wale elfu tano, na vikapu vingapi mlivyoviokota?",
			"10": "<sup>10</sup> Wala ile mikate saba kwa watu elfu nne, na makanda mangapi mliyoyaokota?",
			"11": "<sup>11</sup> Imekuwaje hamkufahamu ya kuwa si kwa sababu ya mkate naliwaambia? Ila jilindeni na chachu ya Mafarisayo na Masadukayo.",
			"12": "<sup>12</sup> Ndipo walipofahamu ya kuwa hakuwaambia kujilinda na chachu ya mkate, bali na mafundisho ya Mafarisayo na Masadukayo.",
			"13": "<sup>13</sup> Basi Yesu akaenda pande za Kaisaria-Filipi, akawauliza wanafunzi wake akasema, Watu hunena Mwana wa Adamu kuwa ni nani?",
			"14": "<sup>14</sup> Wakasema, Wengine hunena u Yohana Mbatizaji, wengine Eliya, wengine Yeremia au mmojawapo wa manabii.",
			"15": "<sup>15</sup> Akawaambia, Nanyi mwaninena mimi kuwa ni nani?",
			"16": "<sup>16</sup> Simoni Petro akajibu akasema, Wewe ndiwe Kristo, Mwana wa Mungu aliye hai.",
			"17": "<sup>17</sup> Yesu akajibu, akamwambia, Heri wewe Simoni Bar-yona; kwa kuwa mwili na damu havikukufunulia hili, bali Baba yangu aliye mbinguni.",
			"18": "<sup>18</sup> Nami nakuambia, Wewe ndiwe Petro, na juu ya mwamba huu nitalijenga kanisa langu; wala milango ya kuzimu haitalishinda.",
			"19": "<sup>19</sup> Nami nitakupa wewe funguo za ufalme wa mbinguni; na lo lote utakalolifunga duniani, litakuwa limefungwa mbinguni; na lo lote utakalolifungua duniani, litakuwa limefunguliwa mbinguni.",
			"20": "<sup>20</sup> Ndipo alipowakataza sana wanafunzi wake wasimwambie mtu ye yote ya kwamba yeye ndiye Kristo.",
			"21": "<sup>21</sup> Tangu wakati huo Yesu alianza kuwaonya wanafunzi wake ya kwamba imempasa kwenda Yerusalemu, na kupata mateso mengi kwa wazee na wakuu wa makuhani, na waandishi, na kuuawa, na siku ya tatu kufufuka.",
			"22": "<sup>22</sup> Petro akamchukua, akaanza kumkemea, akisema, Hasha, Bwana, hayo hayatakupata.",
			"23": "<sup>23</sup> Akageuka, akamwambia Petro, Nenda nyuma yangu, Shetani; u kikwazo kwangu; maana huyawazi yaliyo ya Mungu, bali ya wanadamu.",
			"24": "<sup>24</sup> Wakati huo Yesu aliwaambia wanafunzi wake, Mtu ye yote akitaka kunifuata, na ajikane mwenyewe, ajitwike msalaba wake, anifuate.",
			"25": "<sup>25</sup> Kwa kuwa mtu atakaye kuiokoa nafsi yake, ataipoteza; na mtu atakayepoteza nafsi yake kwa ajili yangu, ataiona.",
			"26": "<sup>26</sup> Kwani atafaidiwa nini mtu akiupata ulimwengu wote, na kupata hasara ya nafsi yake? Au mtu atatoa nini badala ya nafsi yake?",
			"27": "<sup>27</sup> Kwa sababu Mwana wa Adamu atakuja katika utukufu wa Baba yake pamoja na malaika zake; ndipo atakapomlipa kila mtu kwa kadiri ya matendo yake.",
			"28": "<sup>28</sup> Amin, nawaambieni, Pana watu katika hawa wasimamao hapa, ambao hawataonja mauti kabisa, hata watakapomwona Mwana wa Adamu akija katika ufalme wake."
		},
		"17": {
			"1": "<sup>1</sup> Na baada ya siku sita Yesu akawatwaa Petro, na Yakobo, na Yohana nduguye, akawaleta juu ya mlima mrefu faraghani;",
			"2": "<sup>2</sup> akageuka sura yake mbele yao; uso wake ukang’aa kama jua, mavazi yake yakawa meupe kama nuru.",
			"3": "<sup>3</sup> Na tazama, wakatokewa na Musa na Eliya, wakizungumza naye.",
			"4": "<sup>4</sup> Petro akajibu akamwambia Yesu, Bwana, ni vizuri sisi kuwapo hapa; ukitaka, nitafanya hapa vibanda vitatu, kimoja chako wewe, na kimoja cha Musa, na kimoja cha Eliya.",
			"5": "<sup>5</sup> Alipokuwa katika kusema, tazama, wingu jeupe likawatia uvuli; na tazama, sauti ikatoka katika lile wingu, ikasema, Huyu ni Mwanangu, mpendwa wangu, ninayependezwa naye; msikieni yeye.",
			"6": "<sup>6</sup> Na wale wanafunzi waliposikia, walianguka kifulifuli, wakaogopa sana.",
			"7": "<sup>7</sup> Yesu akaja, akawagusa, akasema, Inukeni, wala msiogope.",
			"8": "<sup>8</sup> Wakainua macho yao, wasione mtu ila Yesu peke yake.",
			"9": "<sup>9</sup> Na walipokuwa wakishuka mlimani, Yesu aliwaagiza, akasema, Msimwambie mtu ye yote habari ya maono hayo, hata Mwana wa Adamu atakapofufuka katika wafu.",
			"10": "<sup>10</sup> Wanafunzi wake wakamwuliza, wakisema, Basi kwa nini waandishi hunena ya kwamba imempasa Eliya kuja kwanza?",
			"11": "<sup>11</sup> Naye akajibu, akawaambia, Kweli Eliya yuaja kwanza, naye atatengeneza yote,",
			"12": "<sup>12</sup> ila nawaambia, ya kwamba Eliya amekwisha kuja, wasimtambue, lakini wakamtenda yote waliyotaka. Vivyo hivyo Mwana wa Adamu naye yuaenda kuteswa kwao.",
			"13": "<sup>13</sup> Ndipo wale wanafunzi walipofahamu ya kuwa amesema nao habari za Yohana Mbatizaji.",
			"14": "<sup>14</sup> Nao walipoufikia mkutano, mtu mmoja akamjia, akampigia magoti, akisema,",
			"15": "<sup>15</sup> Bwana, umrehemu mwanangu, kwa kuwa ana kifafa, na kuteswa vibaya; maana mara nyingi huanguka motoni, na mara nyingi majini.",
			"16": "<sup>16</sup> Nikamleta kwa wanafunzi wako, wasiweze kumponya.",
			"17": "<sup>17</sup> Yesu akajibu, akasema, Enyi kizazi kisichoamini, kilichopotoka, nitakaa pamoja nanyi hata lini? Nitachukuliana nanyi hata lini? Mleteni huku kwangu.",
			"18": "<sup>18</sup> Yesu akamkemea pepo, naye akamtoka; yule kijana akapona tangu saa ile.",
			"19": "<sup>19</sup> Kisha wale wanafunzi wakamwendea Yesu kwa faragha wakasema Mbona sisi hatukuweza kumtoa?",
			"20": "<sup>20</sup> Yesu akawaambia, Kwa sababu ya upungufu wa imani yenu. Kwa maana, amin, nawaambia, Mkiwa na imani kiasi cha punje ya haradali mtauambia mlima huu, Ondoka hapa uende kule; nao utaondoka; wala halitakuwako neno lisilowezekana kwenu.",
			"21": "<sup>21</sup> [Lakini namna hii haitoki ila kwa kusali na kufunga.]",
			"22": "<sup>22</sup> Nao walipokuwa wakikaa Galilaya, Yesu akawaambia, Mwana wa Adamu anakwenda kutiwa katika mikono ya watu, nao watamwua, na siku ya tatu atafufuka.",
			"23": "<sup>23</sup> Wakasikitika sana.",
			"24": "<sup>24</sup> Hata walipofika Kapernaumu, wale watozao nusu-shekeli walimwendea Petro, wakasema, Je! Mwalimu wenu hatoi nusu-shekeli? Akasema, Hutoa.",
			"25": "<sup>25</sup> Naye alipoingia nyumbani, Yesu alitangulia kumwuliza, akisema, Waonaje, Simoni? Wafalme wa dunia hutwaa kodi ama ushuru kwa watu gani? Kwa wana wao au kwa wageni?",
			"26": "<sup>26</sup> Naye aliposema, Kwa wageni, Yesu alimwambia, Basi, kama ni hivyo, wana ni mahuru.",
			"27": "<sup>27</sup> Lakini tusije tukawakwaza, enenda baharini ukatupe ndoana, ukatwae samaki yule azukaye kwanza; na ukifumbua mdomo wake utaona shekeli; ichukue hiyo ukawape kwa ajili yangu na kwa ajili yako."
		},
		"18": {
			"1": "<sup>1</sup> Saa ile wanafunzi wakamwendea Yesu wakisema,",
			"2": "<sup>2</sup> Ni nani basi aliye mkuu katika ufalme wa mbinguni? Akaita mtoto mmoja, akamweka katikati yao,",
			"3": "<sup>3</sup> akasema, Amin, nawaambia, Msipoongoka na kuwa kama vitoto, hamtaingia kamwe katika ufalme wa mbinguni.",
			"4": "<sup>4</sup> Basi, ye yote ajinyenyekeshaye mwenyewe kama mtoto huyu, huyo ndiye aliye mkuu katika ufalme wa mbinguni.",
			"5": "<sup>5</sup> Na ye yote atakayempokea mtoto mmoja mfano wa huyu kwa jina langu, anipokea mimi;",
			"6": "<sup>6</sup> bali atakayemkosesha mmojawapo wa wadogo hawa waniaminio, yamfaa afungiwe shingoni jiwe kubwa la kusagia, na kutoswa katika kilindi cha bahari.",
			"7": "<sup>7</sup> Ole ni wa ulimwengu kwa sababu ya mambo ya kukosesha! Maana hayana budi kuja mambo ya kukosesha; lakini ole wake mtu yule aliletaye jambo la kukosesha!",
			"8": "<sup>8</sup> Basi mkono wako au mguu wako ukikukosesha, ukate ukautupe mbali nawe; ni afadhali kuingia katika uzima hali umepungukiwa na mkono au mguu, kuliko kuwa na mikono miwili au miguu miwili, na kutupwa katika moto wa milele.",
			"9": "<sup>9</sup> Na jicho lako likikukosesha, ling’oe, ukalitupe mbali nawe; ni afadhali kuingia katika uzima una chongo, kuliko kuwa na macho mawili, na kutupwa katika jehanum ya moto.",
			"10": "<sup>10</sup> Angalieni msidharau mmojawapo wadogo hawa; kwa maana nawaambia ya kwamba malaika wao mbinguni siku zote huutazama uso wa Baba yangu aliye mbinguni.",
			"11": "<sup>11</sup> [Kwa maana Mwana wa Adamu alikuja kukiokoa kilichopotea.]",
			"12": "<sup>12</sup> Mwaonaje? Mtu akiwa na kondoo mia, mmoja akampotea, je! Hawaachi wale tisa na tisini, akaenda milimani, na kumtafuta yule aliyepotea?",
			"13": "<sup>13</sup> Hata akipata kumwona, amin, nawaambia, amfurahia huyo zaidi, kuliko wale tisa na tisini wasiopotea.",
			"14": "<sup>14</sup> Vivyo hivyo haipendezi mbele za Baba yenu aliye mbinguni kwamba mmoja wa wadogo hawa apotee.",
			"15": "<sup>15</sup> Na ndugu yako akikukosa, enenda ukamwonye, wewe na yeye peke yenu; akikusikia, umempata nduguyo.",
			"16": "<sup>16</sup> La, kama hasikii, chukua pamoja nawe tena mtu mmoja au wawili, ili kwa vinywa vya mashahidi wawili au watatu kila neno lithibitike.",
			"17": "<sup>17</sup> Na asipowasikiliza wao, liambie kanisa; na asipolisikiliza kanisa pia, na awe kwako kama mtu wa mataifa na mtoza ushuru.",
			"18": "<sup>18</sup> Amin, nawaambieni, yo yote mtakayoyafunga duniani yatakuwa yamefungwa mbinguni; na yo yote mtakayoyafungua duniani yatakuwa yamefunguliwa mbinguni.",
			"19": "<sup>19</sup> Tena nawaambia, ya kwamba wawili wenu watakapopatana duniani katika jambo lo lote watakaloliomba, watafanyiwa na Baba yangu aliye mbinguni.",
			"20": "<sup>20</sup> Kwa kuwa walipo wawili watatu wamekusanyika kwa jina langu, nami nipo papo hapo katikati yao.",
			"21": "<sup>21</sup> Kisha Petro akamwendea akamwambia, Bwana, ndugu yangu anikose mara ngapi nami nimsamehe? Hata mara saba?",
			"22": "<sup>22</sup> Yesu akamwambia, Sikuambii hata mara saba, bali hata saba mara sabini.",
			"23": "<sup>23</sup> Kwa sababu hii ufalme wa mbinguni umefanana na mfalme mmoja aliyetaka kufanya hesabu na watumwa wake.",
			"24": "<sup>24</sup> Alipoanza kuifanya, aliletewa mtu mmoja awiwaye talanta elfu kumi.",
			"25": "<sup>25</sup> Naye alipokosa cha kulipa, bwana wake akaamuru auzwe, yeye na mkewe na watoto wake, na vitu vyote alivyo navyo, ikalipwe ile deni.",
			"26": "<sup>26</sup> Basi yule mtumwa akaanguka, akamsujudia akisema, Bwana, nivumilie, nami nitakulipa yote pia.",
			"27": "<sup>27</sup> Bwana wa mtumwa yule akamhurumia, akamfungua, akamsamehe ile deni.",
			"28": "<sup>28</sup> Mtumwa yule akatoka, akamwona mmoja wa wajoli wake, aliyemwia dinari mia; akamkamata, akamshika koo, akisema, Nilipe uwiwacho.",
			"29": "<sup>29</sup> Basi mjoli wake akaanguka miguuni pake, akamsihi, akisema, Nivumilie, nami nitakulipa yote pia.",
			"30": "<sup>30</sup> Lakini hakutaka, akaenda, akamtupa kifungoni, hata atakapoilipa ile deni.",
			"31": "<sup>31</sup> Basi wajoli wake walipoyaona yaliyotendeka, walisikitika sana, wakaenda wakamweleza bwana wao yote yaliyotendeka.",
			"32": "<sup>32</sup> Ndipo bwana wake akamwita, akamwambia, Ewe mtumwa mwovu, nalikusamehe wewe deni ile yote, uliponisihi;",
			"33": "<sup>33</sup> nawe, je! Haikukupasa kumrehemu mjoli wako, kama mimi nilivyokurehemu wewe?",
			"34": "<sup>34</sup> Bwana wake akaghadhibika, akampeleka kwa watesaji, hata atakapoilipa deni ile yote.",
			"35": "<sup>35</sup> Ndivyo na Baba yangu wa mbinguni atakavyowatenda ninyi, msiposamehe kwa mioyo yenu kila mtu ndugu yake."
		},
		"19": {
			"1": "<sup>1</sup> Ikawa Yesu alipomaliza maneno hayo, akatoka Galilaya akafika mipaka ya Uyahudi, ng’ambo ya Yordani.",
			"2": "<sup>2</sup> Makutano mengi wakamfuata, akawaponya huko.",
			"3": "<sup>3</sup> Basi Mafarisayo wakamwendea, wakamjaribu, wakimwambia, Je! Ni halali mtu kumwacha mkewe kwa kila sababu?",
			"4": "<sup>4</sup> Akajibu, akawaambia, Hamkusoma ya kwamba yeye aliyewaumba mwanzo, aliwaumba mtu mume na mtu mke,",
			"5": "<sup>5</sup> akasema, Kwa sababu hiyo, mtu atamwacha babaye na mamaye, ataambatana na mkewe; na hao wawili watakuwa mwili mmoja?",
			"6": "<sup>6</sup> Hata wamekuwa si wawili tena, bali mwili mmoja. Basi aliowaunganisha Mungu, mwanadamu asiwatenganishe.",
			"7": "<sup>7</sup> Wakamwambia, Jinsi gani basi Musa aliamuru kumpa hati ya talaka, na kumwacha?",
			"8": "<sup>8</sup> Akawaambia, Musa, kwa sababu ya ugumu wa mioyo yenu, aliwapa ruhusa kuwaacha wake zenu; lakini tangu mwanzo haikuwa hivi.",
			"9": "<sup>9</sup> Nami nawaambia ninyi, Kila mtu atakayemwacha mkewe, isipokuwa ni kwa sababu ya uasherati, akaoa mwingine, azini; naye amwoaye yule aliyeachwa azini.",
			"10": "<sup>10</sup> Wanafunzi wake wakamwambia, Mambo ya mtu na mkewe yakiwa hivyo, haifai kuoa.",
			"11": "<sup>11</sup> Lakini yeye akawaambia, Si wote wawezao kulipokea neno hili, ila wale waliojaliwa.",
			"12": "<sup>12</sup> Maana wako matowashi waliozaliwa hali hiyo toka matumboni mwa mama zao; tena wako matowashi waliofanywa na watu kuwa matowashi; tena wako matowashi waliojifanya kuwa matowashi kwa ajili ya ufalme wa mbinguni. Awezaye kulipokea neno hili, na alipokee.",
			"13": "<sup>13</sup> Ndipo akaletewa watoto wadogo ili aweke mikono yake juu yao na kuwaombea; wanafunzi wake wakawakemea.",
			"14": "<sup>14</sup> Lakini Yesu akasema, Waacheni watoto wadogo waje kwangu; wala msiwazuie; kwa maana walio mfano wa hao, ufalme wa mbinguni ni wao.",
			"15": "<sup>15</sup> Akaweka mikono yake juu yao, akatoka huko.",
			"16": "<sup>16</sup> Na tazama, mtu mmoja akamwendea akamwambia, Mwalimu, nitende jambo gani jema, ili nipate uzima wa milele?",
			"17": "<sup>17</sup> Akamwambia Kwani kuniuliza habari ya wema? Aliye mwema ni mmoja. Lakini ukitaka kuingia katika uzima, zishike amri.",
			"18": "<sup>18</sup> Akamwambia, Zipi? Yesu akasema, Ni hizi, Usiue, Usizini, Usiibe, Usishuhudie uongo,",
			"19": "<sup>19</sup> Waheshimu baba yako na mama yako, na, Mpende jirani yako kama nafsi yako.",
			"20": "<sup>20</sup> Yule kijana akamwambia, Haya yote nimeyashika; nimepungukiwa na nini tena?",
			"21": "<sup>21</sup> Yesu akamwambia, Ukitaka kuwa mkamilifu, enenda ukauze ulivyo navyo, uwape maskini, nawe utakuwa na hazina mbinguni; kisha njoo unifuate.",
			"22": "<sup>22</sup> Yule kijana alipolisikia neno lile, akaenda zake kwa huzuni; kwa sababu alikuwa na mali nyingi.",
			"23": "<sup>23</sup> Yesu akawaambia wanafunzi wake, Amin, nawaambieni, ya kwamba itakuwa shida tajiri kuingia katika ufalme wa mbinguni.",
			"24": "<sup>24</sup> Nawaambia tena, Ni rahisi zaidi ngamia kupenya tundu ya sindano, kuliko tajiri kuingia katika ufalme wa Mungu.",
			"25": "<sup>25</sup> Wanafunzi waliposikia, walishangaa mno, wakisema, Ni nani basi awezaye kuokoka?",
			"26": "<sup>26</sup> Yesu akawakazia macho, akawaambia, Kwa wanadamu hilo haliwezekani; bali kwa Mungu yote yawezekana.",
			"27": "<sup>27</sup> Ndipo Petro akajibu, akamwambia, Tazama, sisi tumeacha vyote tukakufuata; tutapata nini basi?",
			"28": "<sup>28</sup> Yesu akawaambia, Amin, nawaambia ya kwamba ninyi mlionifuata mimi, katika ulimwengu mpya, atakapoketi Mwana wa Adamu katika kiti cha utukufu wake, ninyi nanyi mtaketi katika viti kumi na viwili, mkiwahukumu kabila kumi na mbili za Israeli.",
			"29": "<sup>29</sup> Na kila mtu aliyeacha nyumba, au ndugu wa kiume au wa kike, au baba, au mama, au watoto, au mashamba, kwa ajili ya jina langu, atapokea mara mia, na kuurithi uzima wa milele.",
			"30": "<sup>30</sup> Lakini wengi walio wa kwanza watakuwa wa mwisho, na walio wa mwisho watakuwa wa kwanza."
		},
		"20": {
			"1": "<sup>1</sup> Kwa maana ufalme wa mbinguni umefanana na mtu mwenye nyumba aliyetoka alfajiri kwenda kuajiri wakulima awapeleke katika shamba lake la mizabibu.",
			"2": "<sup>2</sup> Naye alipokwisha kupatana na wakulima kuwapa kutwa dinari, aliwapeleka katika shamba lake la mizabibu.",
			"3": "<sup>3</sup> Akatoka mnamo saa tatu, akaona wengine wamesimama sokoni wasiokuwa na kazi;",
			"4": "<sup>4</sup> na hao nao akawaambia, Enendeni nanyi katika shamba langu la mizabibu, na iliyo haki nitawapa. Wakaenda.",
			"5": "<sup>5</sup> Akatoka tena mnamo saa sita na saa kenda, akafanya vile vile.",
			"6": "<sup>6</sup> Hata kama saa kumi na moja akatoka, akakuta wengine wamesimama, akawaambia, Mbona mmesimama hapa mchana kutwa bila kazi?",
			"7": "<sup>7</sup> Wakamwambia, Kwa sababu hakuna mtu aliyetuajiri. Akawaambia, enendeni nanyi katika shamba la mizabibu.",
			"8": "<sup>8</sup> Kulipokuchwa, yule bwana wa shamba akamwambia msimamizi wake, Waite wakulima, uwalipe ujira wao, ukianzia wa mwisho hata wa kwanza.",
			"9": "<sup>9</sup> Na walipokuja wale wa saa kumi na moja, walipokea kila mtu dinari.",
			"10": "<sup>10</sup> Na wale wa kwanza walipokuja, walidhani kwamba watapokea zaidi; na hao pia wakapokea kila mtu dinari.",
			"11": "<sup>11</sup> Basi wakiisha kuipokea, wakamnung’unikia mwenye nyumba,",
			"12": "<sup>12</sup> wakisema, Hao wa mwisho wametenda kazi saa moja tu, nawe umewasawazisha na sisi tuliostahimili taabu na hari za mchana kutwa.",
			"13": "<sup>13</sup> Naye akamjibu mmoja wao, akamwambia, Rafiki, sikudhulumu; hukupatana nami kwa dinari?",
			"14": "<sup>14</sup> Chukua iliyo yako, uende zako; napenda kumpa huyu wa mwisho sawa na wewe.",
			"15": "<sup>15</sup> Si halali yangu kutumia vilivyo vyangu kama nipendavyo? Au jicho lako limekuwa ovu kwa sababu ya mimi kuwa mwema?",
			"16": "<sup>16</sup> Vivyo hivyo wa mwisho watakuwa wa kwanza, na wa kwanza watakuwa wa mwisho.",
			"17": "<sup>17</sup> Hata Yesu alipokuwa akipanda kwenda Yerusalemu, aliwachukua wale wanafunzi kumi na wawili faraghani; na njiani akawaambia,",
			"18": "<sup>18</sup> Angalieni, tunapanda kwenda Yerusalemu; na Mwana wa Adamu atatiwa mikononi mwa wakuu wa makuhani na waandishi; nao watamhukumu afe;",
			"19": "<sup>19</sup> kisha watampeleka kwa Mataifa wapate kumdhihaki, na kumpiga mijeledi, na kumsulibisha; na siku ya tatu atafufuka.",
			"20": "<sup>20</sup> Ndipo mama yao wana wa Zebedayo akamwendea pamoja na wanawe, akamsujudia, na kumwomba neno.",
			"21": "<sup>21</sup> Akamwambia, Wataka nini? Akamwambia, Agiza kwamba hawa wanangu wawili waketi mmoja mkono wako wa kuume, na mmoja mkono wako wa kushoto, katika ufalme wako.",
			"22": "<sup>22</sup> Yesu akajibu akasema, Hamjui mnaloliomba. Je! Mwaweza kunywea kikombe nitakachonywea mimi? Wakamwambia, Twaweza.",
			"23": "<sup>23</sup> Akawaambia, Hakika mtakinywea kikombe changu; lakini kuketi mkono wangu wa kuume na mkono wangu wa kushoto, sina amri kuwapa, bali watapewa waliowekewa tayari na Baba yangu.",
			"24": "<sup>24</sup> Na wale kumi waliposikia, waliwakasirikia wale ndugu wawili.",
			"25": "<sup>25</sup> Lakini Yesu akawaita, akasema, Mwajua ya kuwa wakuu wa Mataifa huwatawala kwa nguvu, na wakubwa wao huwatumikisha.",
			"26": "<sup>26</sup> Lakini haitakuwa hivyo kwenu; bali mtu ye yote anayetaka kuwa mkubwa kwenu, na awe mtumishi wenu;",
			"27": "<sup>27</sup> na mtu ye yote anayetaka kuwa wa kwanza kwenu na awe mtumwa wenu;",
			"28": "<sup>28</sup> kama vile Mwana wa Adamu asivyokuja kutumikiwa, bali kutumika, na kutoa nafsi yake iwe fidia ya wengi.",
			"29": "<sup>29</sup> Hata walipokuwa wakitoka Yeriko mkutano mkubwa wakamfuata.",
			"30": "<sup>30</sup> Na tazama, vipofu wawili wameketi kando ya njia, nao waliposikia ya kwamba Yesu anapita, walipaza sauti, wakisema, Uturehemu, Bwana, mwana wa Daudi!",
			"31": "<sup>31</sup> Mkutano wakawakaripia, wanyamaze. Lakini wao wakazidi kupaza sauti, wakisema, Uturehemu, Bwana, mwana wa Daudi!",
			"32": "<sup>32</sup> Yesu akasimama, akawaita, akasema, Mnataka niwafanyie nini?",
			"33": "<sup>33</sup> Wakamwambia, Bwana, twataka macho yetu yafumbuliwe.",
			"34": "<sup>34</sup> Yesu akawahurumia, akawagusa macho yao; mara wakapata kuona, wakamfuata."
		},
		"21": {
			"1": "<sup>1</sup> Hata walipokaribia Yerusalemu, na kufika Bethfage, katika mlima wa Mizeituni, ndipo Yesu alipotuma wanafunzi wawili akiwaambia,",
			"2": "<sup>2</sup> Enendeni mpaka kijiji kile kinachowakabili, na mara mtaona punda amefungwa, na mwana-punda pamoja naye; wafungueni mniletee.",
			"3": "<sup>3</sup> Na kama mtu akiwaambia neno, semeni, Bwana anawahitaji; na mara huyo atawapeleka.",
			"4": "<sup>4</sup> Haya yote yamekuwa, ili litimie neno lililonenwa na nabii, akisema,",
			"5": "<sup>5</sup> Mwambieni binti Sayuni Tazama, mfalme wako anakuja kwako, Mpole, naye amepanda punda, Na mwana-punda, mtoto wa punda.",
			"6": "<sup>6</sup> Wale wanafunzi wakaenda zao, wakafanya kama Yesu alivyowaamuru,",
			"7": "<sup>7</sup> wakamleta yule punda na mwana-punda, wakaweka nguo zao juu yao, naye akaketi juu yake.",
			"8": "<sup>8</sup> Watu wengi katika ule mkutano wakatandaza nguo zao njiani; na wengine wakakata matawi ya miti, wakayatandaza njiani.",
			"9": "<sup>9</sup> Na makutano waliotangulia, na wale waliofuata, wakapaza sauti, wakisema, Hosana, Mwana wa Daudi; ndiye mbarikiwa, yeye ajaye kwa jina la Bwana; Hosana juu mbinguni.",
			"10": "<sup>10</sup> Hata alipoingia Yerusalemu, mji wote ukataharuki, watu wakisema, Ni nani huyu?",
			"11": "<sup>11</sup> Makutano wakasema, Huyu ni yule nabii, Yesu, wa Nazareti ya Galilaya.",
			"12": "<sup>12</sup> Yesu akaingia ndani ya hekalu, akawafukuza wote waliokuwa wakiuza na kununua hekaluni, akazipindua meza za wabadili fedha, na viti vyao waliokuwa wakiuza njiwa;",
			"13": "<sup>13</sup> akawaambia, Imeandikwa, Nyumba yangu itaitwa nyumba ya sala; bali ninyi mmeifanya kuwa pango la wanyang’anyi.",
			"14": "<sup>14</sup> Na vipofu na viwete wakamwendea mle hekaluni, akawaponya.",
			"15": "<sup>15</sup> Lakini wakuu wa makuhani na waandishi walipoyaona maajabu aliyoyafanya, na watoto waliopaza sauti zao hekaluni, wakisema, Hosana, Mwana wa Daudi! Walikasirika,",
			"16": "<sup>16</sup> wakamwambia, Wasikia hawa wasemavyo? Yesu akawaambia, Naam; hamkupata kusoma, Kwa vinywa vya watoto wachanga na wanyonyao umekamilisha sifa?",
			"17": "<sup>17</sup> Akawaacha, akatoka nje ya mji mpaka Bethania, akalala huko.",
			"18": "<sup>18</sup> Hata asubuhi alipokuwa akienda mjini, aliona njaa.",
			"19": "<sup>19</sup> Akaona mtini mmoja kando ya njia, akauendea, asione kitu juu yake ila majani tu; akauambia, Yasipatikane matunda kwako tangu leo hata milele. Mtini ukanyauka mara.",
			"20": "<sup>20</sup> Wanafunzi walipoona, walistaajabu, wakisema, Jinsi gani mtini umenyauka mara?",
			"21": "<sup>21</sup> Yesu akajibu, akawaambia, Amin, nawaambia Mkiwa na imani, msipokuwa na shaka, mtafanya si hilo la mtini tu, lakini hata mkiuambia mlima huu, Ng’oka, ukatupwe baharini, litatendeka.",
			"22": "<sup>22</sup> Na yo yote mtakayoyaomba katika sala mkiamini, mtapokea.",
			"23": "<sup>23</sup> Hata alipokwisha kuingia hekaluni, wakuu wa makuhani na wazee wa watu wakamwendea alipokuwa akifundisha, wakasema, Ni kwa amri gani unatenda mambo haya? Naye ni nani aliyekupa amri hii?",
			"24": "<sup>24</sup> Yesu akajibu akawaambia, Na mimi nitawauliza neno moja; ambalo mkinijibu, nami nitawaambia ni kwa amri gani ninatenda haya.",
			"25": "<sup>25</sup> Ubatizo wa Yohana ulitoka wapi? Ulitoka mbinguni, au kwa wanadamu? Wakahojiana wao kwa wao, wakisema, Tukisema, Ulitoka mbinguni, atatuambia, Mbona basi hamkumwamini?",
			"26": "<sup>26</sup> Na tukisema, Ulitoka kwa wanadamu, twaogopa mkutano; maana watu wote wamwona Yohana kuwa ni nabii.",
			"27": "<sup>27</sup> Wakamjibu Yesu wakasema, Hatujui. Naye akawaambia, Wala mimi siwaambii ninyi ni kwa amri gani ninatenda haya.",
			"28": "<sup>28</sup> Lakini mwaonaje? Mtu mmoja alikuwa na wana wawili; akamwendea yule wa kwanza, akasema, Mwanangu, leo nenda kafanye kazi katika shamba la mizabibu.",
			"29": "<sup>29</sup> Akajibu akasema, Naenda, Bwana; asiende.",
			"30": "<sup>30</sup> Akamwendea yule wa pili, akasema vile vile. Naye akajibu akasema, Sitaki; baadaye akatubu, akaenda.",
			"31": "<sup>31</sup> Je! Katika hao wawili ni yupi aliyefanya mapenzi ya babaye? Wakamwambia, Ni yule wa pili. Basi Yesu akawaambia, Amin nawaambia, watoza ushuru na makahaba wanatangulia mbele yenu kuingia katika ufalme wa Mungu.",
			"32": "<sup>32</sup> Kwa sababu Yohana alikuja kwenu kwa njia ya haki, ninyi msimwamini; lakini watoza ushuru na makahaba walimwamini, nanyi hata mlipoona, hamkutubu baadaye, ili kumwamini.",
			"33": "<sup>33</sup> Sikilizeni mfano mwingine. Kulikuwa na mtu mwenye nyumba, naye alipanda shamba la mizabibu, akalizungusha ugo, akachimba shimo la shinikizo ndani yake, akajenga mnara, akapangisha wakulima, akasafiri.",
			"34": "<sup>34</sup> Wakati wa matunda ulipokuwa karibu, akawatuma watumwa wake kwa wale wakulima, wapokee matunda yake.",
			"35": "<sup>35</sup> Wale wakulima wakawakamata watumwa wake, huyu wakampiga, na huyu wakamwua, na huyu wakampiga kwa mawe.",
			"36": "<sup>36</sup> Akawatuma tena watumwa wengine wengi kuliko wa kwanza wakawatenda vile vile.",
			"37": "<sup>37</sup> Mwishowe akamtuma mwanawe kwao, akisema, Watamstahi mwanangu.",
			"38": "<sup>38</sup> Lakini wale wakulima walipomwona yule mwana, wakasemezana wao kwa wao, Huyu ni mrithi; haya na tumwue, tuutwae urithi wake.",
			"39": "<sup>39</sup> Wakamkamata, wakamtupa nje ya shamba la mizabibu, wakamwua.",
			"40": "<sup>40</sup> Basi atakapokuja yule bwana wa shamba la mizabibu, atawatendaje wale wakulima?",
			"41": "<sup>41</sup> Wakamwambia, Atawaangamiza vibaya wale wabaya; na shamba la mizabibu atawapangisha wakulima wengine, watakaomlipa matunda kwa wakati wake.",
			"42": "<sup>42</sup> Yesu akawaambia, Hamkupata kusoma katika maandiko, Jiwe walilolikataa waashi, Hilo limekuwa jiwe kuu la pembeni; Neno hili limetoka kwa Bwana, Nalo ni ajabu machoni petu?",
			"43": "<sup>43</sup> Kwa sababu hiyo nawaambia, Ufalme wa Mungu utaondolewa kwenu, nao watapewa taifa lingine lenye kuzaa matunda yake.",
			"44": "<sup>44</sup> Naye aangukaye juu ya jiwe hilo atavunjika-vunjika; naye ye yote ambaye litamwangukia, litamsaga tikitiki.",
			"45": "<sup>45</sup> Wakuu wa makuhani na Mafarisayo, waliposikia mifano yake, walitambua ya kuwa anawanenea wao.",
			"46": "<sup>46</sup> Nao walipotafuta kumkamata, waliwaogopa makutano, kwa maana wao walimwona kuwa nabii."
		},
		"22": {
			"1": "<sup>1</sup> Yesu akajibu, akawaambia tena kwa mithali, akisema,",
			"2": "<sup>2</sup> Ufalme wa mbinguni umefanana na mfalme mmoja aliyemfanyia mwanawe arusi.",
			"3": "<sup>3</sup> Akawatuma watumwa wake wawaite walioalikwa kuja arusini; nao wakakataa kuja.",
			"4": "<sup>4</sup> Akatuma tena watu kwa wengine, akisema, Waambieni wale walioalikwa, Tazameni, nimeandaa karamu yangu; ng’ombe zangu na vinono vimekwisha kuchinjwa, na vyote vimekuwa tayari, njoni arusini.",
			"5": "<sup>5</sup> Lakini hawakujali, wakaenda zao, mmoja shambani kwake, mmoja kwenye biashara yake;",
			"6": "<sup>6</sup> nao waliosalia wakawakamata watumwa wake, wakawatenda jeuri, na kuwaua.",
			"7": "<sup>7</sup> Basi yule mfalme akaghadhibika; akapeleka majeshi yake, akawaangamiza wauaji wale, akauteketeza mji wao.",
			"8": "<sup>8</sup> Kisha akawaambia watumwa wake, Arusi i tayari, lakini wale walioalikwa hawakustahili.",
			"9": "<sup>9</sup> Basi enendeni hata njia panda za barabara, na wote mwaonao waiteni arusini.",
			"10": "<sup>10</sup> Watumwa wale wakatoka wakaenda njia kuu, wakakusanya wote waliowaona, waovu kwa wema; arusi ikajaa wageni.",
			"11": "<sup>11</sup> Lakini alipoingia yule mfalme ili kuwatazama wageni wake, akaona mle mtu mmoja asiyevaa vazi la arusi.",
			"12": "<sup>12</sup> Akamwambia, Rafiki, uliingiaje humu nawe huna vazi la arusi? Naye akatekewa.",
			"13": "<sup>13</sup> Mfalme akawaambia watumishi, Mfungeni mikono na miguu, mchukueni mkamtupe katika giza la nje; ndiko kutakuwako kilio na kusaga meno.",
			"14": "<sup>14</sup> Kwa maana waitwao ni wengi, bali wateule ni wachache.",
			"15": "<sup>15</sup> Ndipo Mafarisayo wakaenda zao, wakafanya shauri, jinsi ya kumtega kwa maneno.",
			"16": "<sup>16</sup> Wakatuma kwake wanafunzi wao pamoja na Maherodi, wakasema, Mwalimu, twajua ya kuwa wewe u mtu wa kweli, na njia ya Mungu waifundisha katika kweli, wala hujali cheo cha mtu awaye yote, kwa maana hutazami sura za watu.",
			"17": "<sup>17</sup> Basi utuambie, Waonaje? Ni halali kumpa Kaisari kodi, ama sivyo?",
			"18": "<sup>18</sup> Lakini Yesu akaufahamu uovu wao, akasema, Mbona mnanijaribu, enyi wanafiki?",
			"19": "<sup>19</sup> Nionyesheni fedha ya kodi. Nao wakamletea dinari.",
			"20": "<sup>20</sup> Akawaambia, Ni ya nani sanamu hii, na anwani hii?",
			"21": "<sup>21</sup> Wakamwambia, Ni ya Kaisari. Akawaambia, Basi, mlipeni Kaisari yaliyo ya Kaisari, na Mungu yaliyo ya Mungu.",
			"22": "<sup>22</sup> Waliposikia, walistaajabu, wakamwacha, wakaenda zao.",
			"23": "<sup>23</sup> Siku ile Masadukayo, watu wasemao ya kwamba hakuna kiyama, wakamwendea, wakamwuliza,",
			"24": "<sup>24</sup> wakisema, Mwalimu, Musa alisema, Mtu akifa, akiwa hana watoto, ndugu yake na amwoe yule mkewe, ili ampatie nduguye mzao.",
			"25": "<sup>25</sup> Basi, kwetu kulikuwa na ndugu saba; wa kwanza akaoa, akafariki, na kwa kuwa hana mzao, akamwachia nduguye mke wake.",
			"26": "<sup>26</sup> Vivyo hivyo wa pili naye, na wa tatu, hata wote saba.",
			"27": "<sup>27</sup> Mwisho wa wote yule mwanamke akafa naye.",
			"28": "<sup>28</sup> Basi, katika kiyama, atakuwa mke wa yupi katika wale saba? Maana wote walikuwa naye.",
			"29": "<sup>29</sup> Yesu akajibu, akawaambia, Mwapotea, kwa kuwa hamyajui maandiko wala uweza wa Mungu.",
			"30": "<sup>30</sup> Kwa maana katika kiyama hawaoi wala hawaolewi, bali huwa kama malaika mbinguni.",
			"31": "<sup>31</sup> Tena kwa habari ya kiyama ya watu, hamjalisoma neno lililonenwa na Mungu, akisema,",
			"32": "<sup>32</sup> Mimi ni Mungu wa Ibrahimu, na Mungu wa Isaka, na Mungu wa Yakobo? Mungu si Mungu wa wafu, bali wa walio hai.",
			"33": "<sup>33</sup> Na makutano waliposikia, walishangaa kwa mafunzo yake.",
			"34": "<sup>34</sup> Na Mafarisayo waliposikia ya kwamba amewafumba kinywa wale Masadukayo, walifanya shauri pamoja.",
			"35": "<sup>35</sup> Mmoja wao, mwana-sheria, akamwuliza, akimjaribu;",
			"36": "<sup>36</sup> Mwalimu, katika torati ni amri ipi iliyo kuu?",
			"37": "<sup>37</sup> Akamwambia, Mpende Bwana Mungu wako kwa moyo wako wote, na kwa roho yako yote, na kwa akili zako zote.",
			"38": "<sup>38</sup> Hii ndiyo amri iliyo kuu, tena ni ya kwanza.",
			"39": "<sup>39</sup> Na ya pili yafanana nayo, nayo ni hii, Mpende jirani yako kama nafsi yako.",
			"40": "<sup>40</sup> Katika amri hizi mbili hutegemea torati yote na manabii.",
			"41": "<sup>41</sup> Na Mafarisayo walipokusanyika, Yesu aliwauliza, akisema, Mwaonaje katika habari za Kristo?",
			"42": "<sup>42</sup> Ni mwana wa nani? Wakamwambia, Ni wa Daudi.",
			"43": "<sup>43</sup> Akawauliza, Imekuwaje basi Daudi katika Roho kumwita Bwana, akisema,",
			"44": "<sup>44</sup> Bwana alimwambia Bwana wangu, Uketi mkono wangu wa kuume, Hata niwawekapo adui zako Kuwa chini ya miguu yako?",
			"45": "<sup>45</sup> Basi, Daudi akimwita Bwana, amekuwaje ni mwanawe?",
			"46": "<sup>46</sup> Wala hakuweza mtu kumjibu neno; wala hakuthubutu mtu ye yote tangu siku ile kumwuliza neno tena."
		},
		"23": {
			"1": "<sup>1</sup> Kisha Yesu akawaambia makutano na wanafunzi wake, akasema,",
			"2": "<sup>2</sup> Waandishi na Mafarisayo wameketi katika kiti cha Musa;",
			"3": "<sup>3</sup> basi, yo yote watakayowaambia, myashike na kuyatenda; lakini kwa mfano wa matendo yao, msitende; maana wao hunena lakini hawatendi.",
			"4": "<sup>4</sup> Wao hufunga mizigo mizito na kuwatwika watu mabegani mwao; wasitake wenyewe kuigusa kwa kidole chao.",
			"5": "<sup>5</sup> Tena matendo yao yote huyatenda ili kutazamwa na watu; kwa kuwa hupanua hirizi zao, huongeza matamvua yao;",
			"6": "<sup>6</sup> hupenda viti vya mbele katika karamu, na kuketi mbele katika masinagogi,",
			"7": "<sup>7</sup> na kusalimiwa masokoni, na kuitwa na watu, Rabi.",
			"8": "<sup>8</sup> Bali ninyi msiitwe Rabi, maana mwalimu wenu ni mmoja, nanyi nyote ni ndugu.",
			"9": "<sup>9</sup> Wala msimwite mtu baba duniani; maana Baba yenu ni mmoja, aliye wa mbinguni.",
			"10": "<sup>10</sup> Wala msiitwe viongozi; maana kiongozi wenu ni mmoja, naye ndiye Kristo.",
			"11": "<sup>11</sup> Naye aliye mkubwa wenu atakuwa mtumishi wenu.",
			"12": "<sup>12</sup> Na ye yote atakayejikweza, atadhiliwa; na ye yote atakayejidhili, atakwezwa.",
			"13": "<sup>13</sup> Ole wenu waandishi na Mafarisayo, wanafiki! Kwa kuwa mnawafungia watu ufalme wa mbinguni; ninyi wenyewe hamwingii, wala wanaoingia hamwaachi waingie.",
			"14": "<sup>14</sup> [Ole wenu waandishi na Mafarisayo, wanafiki! Kwa kuwa mnakula nyumba za wajane, na kwa unafiki mnasali sala ndefu; kwa hiyo mtapata hukumu iliyo kubwa zaidi.]",
			"15": "<sup>15</sup> Ole wenu waandishi na Mafarisayo, wanafiki! Kwa kuwa mnazunguka katika bahari na nchi kavu ili kumfanya mtu mmoja kuwa mwongofu; na akiisha kufanyika, mnamfanya kuwa mwana wa jehanum mara mbili zaidi kuliko ninyi wenyewe.",
			"16": "<sup>16</sup> Ole wenu viongozi vipofu, ninyi msemao, Mtu atakayeapa kwa hekalu, si kitu; bali mtu atakayeapa kwa dhahabu ya hekalu, amejifunga.",
			"17": "<sup>17</sup> Wapumbavu ninyi na vipofu; maana ni ipi iliyo kubwa, ile dhahabu, au lile hekalu liitakasalo dhahabu?",
			"18": "<sup>18</sup> Tena, Mtu atakayeapa kwa madhabahu, si kitu; bali mtu atakayeapa kwa sadaka iliyo juu yake, amejifunga.",
			"19": "<sup>19</sup> Vipofu ninyi; maana ni ipi iliyo kubwa, ile sadaka, au ile madhabahu iitakasayo sadaka?",
			"20": "<sup>20</sup> Basi yeye aapaye kwa madhabahu, huapa kwa hiyo, na kwa vitu vyote vilivyo juu yake.",
			"21": "<sup>21</sup> Naye aapaye kwa hekalu, huapa kwa hilo, na kwa yeye akaaye ndani yake.",
			"22": "<sup>22</sup> Naye aapaye kwa mbingu, huapa kwa kiti cha enzi cha Mungu, na kwa yeye aketiye juu yake.",
			"23": "<sup>23</sup> Ole wenu waandishi na Mafarisayo, wanafiki! Kwa kuwa mnalipa zaka za mnanaa na bizari na jira, lakini mmeacha mambo makuu ya sheria, yaani, adili, na rehema, na imani; hayo imewapasa kuyafanya, wala yale mengine msiyaache.",
			"24": "<sup>24</sup> Viongozi vipofu, wenye kuchuja mbu na kumeza ngamia.",
			"25": "<sup>25</sup> Ole wenu waandishi na Mafarisayo, wanafiki! Kwa kuwa mnasafisha nje ya kikombe na chano, na ndani yake vimejaa unyang’anyi na kutokuwa na kiasi.",
			"26": "<sup>26</sup> Ewe Farisayo kipofu, safisha kwanza ndani ya kikombe, ili nje yake nayo ipate kuwa safi.",
			"27": "<sup>27</sup> Ole wenu, waandishi na Mafarisayo, wanafiki! Kwa kuwa mmefanana na makaburi yaliyopakwa chokaa, nayo kwa nje yaonekana kuwa mazuri, bali ndani yamejaa mifupa ya wafu, na uchafu wote.",
			"28": "<sup>28</sup> Vivyo hivyo ninyi nanyi, kwa nje mwaonekana na watu kuwa wenye haki, bali ndani mmejaa unafiki na maasi.",
			"29": "<sup>29</sup> Ole wenu, waandishi na Mafarisayo, wanafiki! Kwa kuwa mnayajenga makaburi ya manabii, na kuyapamba maziara ya wenye haki,",
			"30": "<sup>30</sup> na kusema, Kama sisi tungalikuwako zamani za baba zetu, hatungalishirikiana nao katika damu ya manabii.",
			"31": "<sup>31</sup> Hivi mwajishuhudia wenyewe, ya kwamba ninyi ni wana wao waliowaua manabii.",
			"32": "<sup>32</sup> Kijazeni basi kipimo cha baba zenu.",
			"33": "<sup>33</sup> Enyi nyoka, wana wa majoka, mtaikimbiaje hukumu ya jehanum?",
			"34": "<sup>34</sup> Kwa sababu hiyo, angalieni, mimi natuma kwenu manabii na wenye hekima na waandishi; na wengine wao ninyi mtawaua na kuwasulibisha, na wengine wao mtawapiga katika masinagogi yenu, na kuwafukuza mji kwa mji;",
			"35": "<sup>35</sup> hivyo ije juu yenu damu yote ya haki iliyomwagika juu ya nchi, tangu damu ya Habili, yule mwenye haki, hata damu ya Zakaria bin Barakia, mliyemwua kati ya patakatifu na madhabahu.",
			"36": "<sup>36</sup> Amin, nawaambieni, Mambo hayo yote yatakuja juu ya kizazi hiki.",
			"37": "<sup>37</sup> Ee Yerusalemu, Yerusalemu, uwauaye manabii, na kuwapiga kwa mawe wale waliotumwa kwako! Ni mara ngapi nimetaka kuwakusanya pamoja watoto wako, kama vile kuku avikusanyavyo pamoja vifaranga vyake chini ya mabawa yake, lakini hamkutaka!",
			"38": "<sup>38</sup> Angalieni, nyumba yenu mmeachiwa hali ya ukiwa.",
			"39": "<sup>39</sup> Kwa maana nawaambia, Hamtaniona kamwe tangu sasa, hata mtakaposema, Amebarikiwa ajaye kwa jina la Bwana."
		},
		"24": {
			"1": "<sup>1</sup> Yesu akaenda zake, akatoka hekaluni; wanafunzi wake wakamwendea ili kumwonyesha majengo ya hekalu.",
			"2": "<sup>2</sup> Naye akajibu akawaambia, Hamyaoni haya yote? Amin, nawaambieni, Halitasalia hapa jiwe juu ya jiwe ambalo halitabomoshwa.",
			"3": "<sup>3</sup> Hata alipokuwa ameketi katika mlima wa Mizeituni, wanafunzi wake wakamwendea kwa faragha, wakisema, Tuambie, mambo hayo yatakuwa lini? Nayo ni nini dalili ya kuja kwako, na ya mwisho wa dunia?",
			"4": "<sup>4</sup> Yesu akajibu, akawaambia, Angalieni, mtu asiwadanganye.",
			"5": "<sup>5</sup> Kwa sababu wengi watakuja kwa jina langu, wakisema, Mimi ni Kristo; nao watadanganya wengi.",
			"6": "<sup>6</sup> Nanyi mtasikia habari za vita na matetesi ya vita; angalieni, msitishwe; maana hayo hayana budi kutukia; lakini ule mwisho bado.",
			"7": "<sup>7</sup> Kwa maana taifa litaondoka kupigana na taifa, na ufalme kupigana na ufalme; kutakuwa na njaa, na matetemeko ya nchi mahali mahali.",
			"8": "<sup>8</sup> Hayo yote ndiyo mwanzo wa utungu.",
			"9": "<sup>9</sup> Wakati huo watawasaliti ninyi mpate dhiki, nao watawaua; nanyi mtakuwa watu wa kuchukiwa na mataifa yote kwa ajili ya jina langu.",
			"10": "<sup>10</sup> Ndipo wengi watakapojikwaa, nao watasalitiana, na kuchukiana.",
			"11": "<sup>11</sup> Na manabii wengi wa uongo watatokea, na kudanganya wengi.",
			"12": "<sup>12</sup> Na kwa sababu ya kuongezeka maasi, upendo wa wengi utapoa.",
			"13": "<sup>13</sup> Lakini mwenye kuvumilia hata mwisho, ndiye atakayeokoka.",
			"14": "<sup>14</sup> Tena habari njema ya ufalme itahubiriwa katika ulimwengu wote, kuwa ushuhuda kwa mataifa yote; hapo ndipo ule mwisho utakapokuja.",
			"15": "<sup>15</sup> Basi hapo mtakapoliona chukizo la uharibifu, lile lililonenwa na nabii Danieli, limesimama katika patakatifu (asomaye na afahamu),",
			"16": "<sup>16</sup> ndipo walio katika Uyahudi na wakimbilie milimani;",
			"17": "<sup>17</sup> naye aliye juu ya dari asishuke kuvichukua vitu vilivyomo nyumbani mwake;",
			"18": "<sup>18</sup> wala aliye shambani asirudi nyuma kuichukua nguo yake.",
			"19": "<sup>19</sup> Ole wao wenye mimba na wanyonyeshao siku hizo!",
			"20": "<sup>20</sup> Ombeni, ili kukimbia kwenu kusiwe wakati wa baridi, wala siku ya sabato.",
			"21": "<sup>21</sup> Kwa kuwa wakati huo kutakuwapo dhiki kubwa, ambayo haijatokea namna yake tangu mwanzo wa ulimwengu hata sasa, wala haitakuwapo kamwe.",
			"22": "<sup>22</sup> Na kama siku hizo zisingalifupizwa, asingeokoka mtu ye yote; lakini kwa ajili ya wateule zitafupizwa siku hizo.",
			"23": "<sup>23</sup> Wakati huo mtu akiwaambia, Tazama, Kristo yupo hapa, au yuko kule msisadiki.",
			"24": "<sup>24</sup> Kwa maana watatokea makristo wa uongo, na manabii wa uongo, nao watatoa ishara kubwa na maajabu; wapate kuwapoteza, kama yamkini, hata walio wateule.",
			"25": "<sup>25</sup> Tazama, nimekwisha kuwaonya mbele.",
			"26": "<sup>26</sup> Basi wakiwaambia, Yuko jangwani, msitoke; yumo nyumbani, msisadiki.",
			"27": "<sup>27</sup> Kwa maana kama vile umeme utokavyo mashariki ukaonekana hata magharibi, hivyo ndivyo kutakavyokuwa kuja kwake Mwana wa Adamu.",
			"28": "<sup>28</sup> Kwa kuwa po pote ulipo mzoga, ndipo watakapokusanyika tai.",
			"29": "<sup>29</sup> Lakini mara, baada ya dhiki ya siku zile, jua litatiwa giza, na mwezi hautatoa mwanga wake, na nyota zitaanguka mbinguni, na nguvu za mbinguni zitatikisika;",
			"30": "<sup>30</sup> ndipo itakapoonekana ishara yake Mwana wa Adamu mbinguni; ndipo mataifa yote ya ulimwengu watakapoomboleza, nao watamwona Mwana wa Adamu akija juu ya mawingu ya mbinguni pamoja na nguvu na utukufu mwingi.",
			"31": "<sup>31</sup> Naye atawatuma malaika zake pamoja na sauti kuu ya parapanda, nao watawakusanya wateule wake toka pepo nne, toka mwisho huu wa mbingu mpaka mwisho huu.",
			"32": "<sup>32</sup> Basi kwa mtini jifunzeni mfano; tawi lake likiisha kuchipuka na kuchanua majani, mwatambua ya kuwa wakati wa mavuno u karibu;",
			"33": "<sup>33</sup> nanyi kadhalika, myaonapo hayo yote, tambueni ya kuwa yu karibu, milangoni.",
			"34": "<sup>34</sup> Amin, nawaambia, Kizazi hiki hakitapita, hata hayo yote yatakapotimia.",
			"35": "<sup>35</sup> Mbingu na nchi zitapita; lakini maneno yangu hayatapita kamwe.",
			"36": "<sup>36</sup> Walakini habari ya siku ile na saa ile hakuna aijuaye, hata malaika walio mbinguni, wala Mwana, ila Baba peke yake.",
			"37": "<sup>37</sup> Kwa maana kama vile ilivyokuwa siku za Nuhu, ndivyo kutakavyokuwa kuja kwake Mwana wa Adamu.",
			"38": "<sup>38</sup> Kwa kuwa kama vile siku zile zilizokuwa kabla ya Gharika watu walivyokuwa wakila, na kunywa, wakioa na kuolewa, hata siku ile aliyoingia Nuhu katika safina,",
			"39": "<sup>39</sup> wasitambue, hata Gharika ikaja, ikawachukua wote, ndivyo kutakavyokuwa kuja kwake Mwana wa Adamu.",
			"40": "<sup>40</sup> Wakati ule watu wawili watakuwako kondeni; mmoja atwaliwa, mmoja aachwa;",
			"41": "<sup>41</sup> wanawake wawili watakuwa wakisaga; mmoja atwaliwa, mmoja aachwa.",
			"42": "<sup>42</sup> Kesheni basi; kwa maana hamjui ni siku ipi atakayokuja Bwana wenu.",
			"43": "<sup>43</sup> Lakini fahamuni neno hili; kama mwenye nyumba angaliijua ile zamu mwivi atakayokuja, angalikesha, wala asingaliiacha nyumba yake kuvunjwa.",
			"44": "<sup>44</sup> Kwa sababu hiyo ninyi nanyi jiwekeni tayari; kwa kuwa katika saa msiyodhani Mwana wa Adamu yuaja.",
			"45": "<sup>45</sup> Ni nani basi yule mtumwa mwaminifu mwenye akili, ambaye bwana wake alimweka juu ya nyumba yake, awape watu chakula kwa wakati wake?",
			"46": "<sup>46</sup> Heri mtumwa yule, ambaye bwana wake ajapo atamkuta akifanya hivyo.",
			"47": "<sup>47</sup> Amin, nawaambieni, atamweka juu ya vitu vyake vyote.",
			"48": "<sup>48</sup> Lakini mtumwa yule mbaya akisema moyoni mwake, Bwana wangu anakawia;",
			"49": "<sup>49</sup> akaanza kuwapiga wajoli wake, na kula na kunywa pamoja na walevi;",
			"50": "<sup>50</sup> bwana wake mtumwa huyo atakuja siku asiyodhani, na saa asiyojua,",
			"51": "<sup>51</sup> atamkata vipande viwili, na kumwekea fungu lake pamoja na wanafiki; ndiko kutakuwako kilio na kusaga meno."
		},
		"25": {
			"1": "<sup>1</sup> Ndipo ufalme wa mbinguni utakapofanana na wanawali kumi, waliotwaa taa zao, wakatoka kwenda kumlaki bwana arusi.",
			"2": "<sup>2</sup> Watano wao walikuwa wapumbavu, na watano wenye busara.",
			"3": "<sup>3</sup> Wale waliokuwa wapumbavu walizitwaa taa zao, wasitwae na mafuta pamoja nao;",
			"4": "<sup>4</sup> bali wale wenye busara walitwaa mafuta katika vyombo vyao pamoja na taa zao.",
			"5": "<sup>5</sup> Hata bwana arusi alipokawia, wote wakasinzia wakalala usingizi.",
			"6": "<sup>6</sup> Lakini usiku wa manane, pakawa na kelele, Haya, bwana arusi; tokeni mwende kumlaki.",
			"7": "<sup>7</sup> Mara wakaondoka wanawali wale wote, wakazitengeneza taa zao.",
			"8": "<sup>8</sup> Wale wapumbavu wakawaambia wenye busara, Tupeni mafuta yenu kidogo; maana taa zetu zinazimika.",
			"9": "<sup>9</sup> Lakini wale wenye busara wakawajibu, wakisema, Sivyo; hayatatutosha sisi na ninyi; afadhali shikeni njia mwende kwa wauzao, mkajinunulie.",
			"10": "<sup>10</sup> Na hao walipokuwa wakienda kununua, bwana arusi akaja, nao waliokuwa tayari wakaingia pamoja naye arusini; mlango ukafungwa.",
			"11": "<sup>11</sup> Halafu wakaja na wale wanawali wengine, wakasema, Bwana, Bwana, utufungulie.",
			"12": "<sup>12</sup> Akajibu akasema, Amin, nawaambia, siwajui ninyi.",
			"13": "<sup>13</sup> Basi kesheni, kwa sababu hamwijui siku wala saa.",
			"14": "<sup>14</sup> Maana ni mfano wa mtu atakaye kusafiri, aliwaita watumwa wake, akaweka kwao mali zake.",
			"15": "<sup>15</sup> Akampa mmoja talanta tano, na mmoja talanta mbili, na mmoja talanta moja; kila mtu kwa kadiri ya uwezo wake; akasafiri.",
			"16": "<sup>16</sup> Mara yule aliyepokea talanta tano akaenda, akafanya biashara nazo, akachuma faida talanta nyingine tano.",
			"17": "<sup>17</sup> Vile vile na yule mwenye mbili, yeye naye akachuma nyingine mbili faida.",
			"18": "<sup>18</sup> Lakini yule aliyepokea moja alikwenda akafukua chini, akaificha fedha ya bwana wake.",
			"19": "<sup>19</sup> Baada ya siku nyingi akaja bwana wa watumwa wale, akafanya hesabu nao.",
			"20": "<sup>20</sup> Akaja yule aliyepokea talanta tano, akaleta talanta nyingine tano, akisema, Bwana, uliweka kwangu talanta tano; tazama, talanta nyingine tano nilizopata faida.",
			"21": "<sup>21</sup> Bwana wake akamwambia, Vema, mtumwa mwema na mwaminifu; ulikuwa mwaminifu kwa machache, nitakuweka juu ya mengi; ingia katika furaha ya bwana wako.",
			"22": "<sup>22</sup> Akaja na yule aliyepokea talanta mbili, akasema, Bwana, uliweka kwangu talanta mbili; tazama, talanta nyingine mbili nilizopata faida.",
			"23": "<sup>23</sup> Bwana wake akamwambia, Vema, mtumwa mwema na mwaminifu; ulikuwa mwaminifu kwa machache, nitakuweka juu ya mengi; ingia katika furaha ya bwana wako.",
			"24": "<sup>24</sup> Akaja na yule aliyepokea talanta moja, akasema, Bwana, nalitambua ya kuwa wewe u mtu mgumu, wavuna usipopanda, wakusanya usipotawanya;",
			"25": "<sup>25</sup> basi nikaogopa, nikaenda nikaificha talanta yako katika ardhi; tazama, unayo iliyo yako.",
			"26": "<sup>26</sup> Bwana wake akajibu, akamwambia, Wewe mtumwa mbaya na mlegevu, ulijua ya kuwa navuna nisipopanda, nakusanya nisipotawanya;",
			"27": "<sup>27</sup> basi, ilikupasa kuiweka fedha yangu kwa watoao riba; nami nikija ningalipata iliyo yangu na faida yake.",
			"28": "<sup>28</sup> Basi, mnyang’anyeni talanta hiyo, mpeni yule aliye nazo talanta kumi.",
			"29": "<sup>29</sup> Kwa maana kila mwenye kitu atapewa, na kuongezewa tele; lakini asiye na kitu, hata kile alicho nacho atanyang’anywa.",
			"30": "<sup>30</sup> Na mtumwa yule asiyefaa, mtupeni mbali katika giza la nje; ndiko kutakuwako kilio na kusaga meno.",
			"31": "<sup>31</sup> Hapo atakapokuja Mwana wa Adamu katika utukufu wake, na malaika watakatifu wote pamoja naye, ndipo atakapoketi katika kiti cha utukufu wake;",
			"32": "<sup>32</sup> na mataifa yote watakusanyika mbele zake; naye atawabagua kama vile mchungaji abaguavyo kondoo na mbuzi;",
			"33": "<sup>33</sup> atawaweka kondoo mkono wake wa kuume, na mbuzi mkono wake wa kushoto.",
			"34": "<sup>34</sup> Kisha Mfalme atawaambia wale walioko mkono wake wa kuume, Njoni, mliobarikiwa na Baba yangu, urithini ufalme mliowekewa tayari tangu kuumbwa ulimwengu;",
			"35": "<sup>35</sup> kwa maana nalikuwa na njaa, mkanipa chakula; nalikuwa na kiu, mkaninywesha; nalikuwa mgeni, mkanikaribisha;",
			"36": "<sup>36</sup> nalikuwa uchi, mkanivika nalikuwa mgonjwa, mkaja kunitazama; nalikuwa kifungoni, mkanijia.",
			"37": "<sup>37</sup> Ndipo wenye haki watakapomjibu, wakisema, Bwana, ni lini tulipokuona una njaa, tukakulisha, au una kiu tukakunywesha?",
			"38": "<sup>38</sup> Tena ni lini tulipokuona u mgeni, tukakukaribisha, au u uchi, tukakuvika?",
			"39": "<sup>39</sup> Ni lini tena tulipokuona u mgonjwa, au kifungoni, tukakujia?",
			"40": "<sup>40</sup> Na Mfalme atajibu, akiwaambia, Amin, nawaambia, kadiri mlivyomtendea mmojawapo wa hao ndugu zangu walio wadogo, mlinitendea mimi.",
			"41": "<sup>41</sup> Kisha atawaambia na wale walioko mkono wake wa kushoto, Ondokeni kwangu, mliolaaniwa, mwende katika moto wa milele, aliowekewa tayari Ibilisi na malaika zake;",
			"42": "<sup>42</sup> kwa maana nalikuwa na njaa, msinipe chakula; nalikuwa na kiu, msininyweshe;",
			"43": "<sup>43</sup> nalikuwa mgeni, msinikaribishe; nalikuwa uchi, msinivike; nalikuwa mgonjwa, na kifungoni, msije kunitazama.",
			"44": "<sup>44</sup> Ndipo hao pia watajibu, wakisema, Bwana, ni lini tulipokuona wewe una njaa, au una kiu, au u mgeni, au u uchi, au u mgonjwa, au u kifungoni, tusikuhudumie?",
			"45": "<sup>45</sup> Naye atawajibu, akisema, Amin, nawaambia, Kadiri msivyomtendea mmojawapo wa hao walio wadogo, hamkunitendea mimi.",
			"46": "<sup>46</sup> Na hao watakwenda zao kuingia katika adhabu ya milele; bali wenye haki watakwenda katika uzima wa milele."
		},
		"26": {
			"1": "<sup>1</sup> Ikawa Yesu alipomaliza maneno hayo yote, aliwaambia wanafunzi wake,",
			"2": "<sup>2</sup> Mnajua ya kuwa baada ya siku mbili itakuwa Pasaka, na Mwana wa Adamu atasalitiwa asulibiwe.",
			"3": "<sup>3</sup> Wakati ule wakuu wa makuhani, na wazee wa watu, wakakusanyika katika behewa ya Kuhani Mkuu, jina lake Kayafa;",
			"4": "<sup>4</sup> wakafanya shauri pamoja, ili wamkamate Yesu kwa hila na kumwua.",
			"5": "<sup>5</sup> Lakini wakasema, Isiwe wakati wa sikukuu, isije ikatokea ghasia katika watu.",
			"6": "<sup>6</sup> Naye Yesu alipokuwapo Bethania katika nyumba ya Simoni mkoma,",
			"7": "<sup>7</sup> mwanamke mwenye kibweta cha marhamu ya thamani kubwa alimkaribia akaimimina kichwani pake alipoketi chakulani.",
			"8": "<sup>8</sup> Wanafunzi wake walipoona, wakachukiwa, wakasema, Ni wa nini upotevu huu?",
			"9": "<sup>9</sup> Maana marhamu hii ingaliweza kuuzwa kwa fedha nyingi wakapewa maskini.",
			"10": "<sup>10</sup> Yesu akatambua akawaambia, Mbona mnamtaabisha mwanamke? Maana ni kazi njema aliyonitendea mimi.",
			"11": "<sup>11</sup> Kwa maana siku zote mnao maskini pamoja nanyi; lakini mimi hamnami siku zote.",
			"12": "<sup>12</sup> Maana kwa kunimwagia mwili wangu marhamu hiyo, ametenda hivyo ili kuniweka tayari kwa maziko yangu.",
			"13": "<sup>13</sup> Amin, nawaambieni, Kila patakapohubiriwa Injili hii katika ulimwengu wote, tendo hilo alilolitenda huyu litatajwa pia kwa kumbukumbu lake.",
			"14": "<sup>14</sup> Wakati huo mmoja wa wale Thenashara, jina lake Yuda Iskariote, aliwaendea wakuu wa makuhani,",
			"15": "<sup>15</sup> akasema, Ni nini mtakachonipa, nami nitamsaliti kwenu? Wakampimia vipande thelathini vya fedha.",
			"16": "<sup>16</sup> Tokea wakati huo akawa akitafuta nafasi apate kumsaliti.",
			"17": "<sup>17</sup> Hata siku ya kwanza ya mikate isiyotiwa chachu, wanafunzi wake wakamwendea Yesu, wakamwambia, Ni wapi utakapo tukuandalie uile Pasaka?",
			"18": "<sup>18</sup> Akasema, Enendeni mjini kwa mtu fulani, mkamwambie, Mwalimu asema, Majira yangu ni karibu; kwako nitafanya Pasaka pamoja na wanafunzi wangu.",
			"19": "<sup>19</sup> Wanafunzi wakafanya kama Yesu alivyowaagiza, wakaiandaa Pasaka.",
			"20": "<sup>20</sup> Basi kulipokuwa jioni aliketi chakulani pamoja na wale Thenashara.",
			"21": "<sup>21</sup> Nao walipokuwa wakila, alisema, Amin, nawaambia, Mmoja wenu atanisaliti.",
			"22": "<sup>22</sup> Wakahuzunika sana, wakaanza kumwuliza mmoja mmoja, Ni mimi, Bwana?",
			"23": "<sup>23</sup> Akajibu akasema, Yeye aliyetia mkono wake pamoja nami katika kombe, ndiye atakayenisaliti.",
			"24": "<sup>24</sup> Mwana wa Adamu aenda zake, kama alivyoandikiwa; lakini ole wake mtu yule ambaye amsaliti Mwana wa Adamu! Ingekuwa heri kwake mtu yule kama asingalizaliwa.",
			"25": "<sup>25</sup> Yuda, yule mwenye kumsaliti, akajibu, akasema, Ni mimi, Rabi? Akamwambia, Wewe umesema.",
			"26": "<sup>26</sup> Nao walipokuwa wakila Yesu alitwaa mkate, akabariki, akaumega, akawapa wanafunzi wake, akasema, Twaeni, mle; huu ndio mwili wangu",
			"27": "<sup>27</sup> Akakitwaa kikombe, akashukuru, akawapa, akisema, Nyweni nyote katika hiki;",
			"28": "<sup>28</sup> kwa maana hii ndiyo damu yangu ya agano, imwagikayo kwa ajili ya wengi kwa ondoleo la dhambi.",
			"29": "<sup>29</sup> Lakini nawaambieni, Sitakunywa kabisa tangu sasa uzao huu wa mzabibu, hata siku ile nitakapokunywa mpya pamoja nanyi katika ufalme wa Baba yangu.",
			"30": "<sup>30</sup> Nao walipokwisha kuimba, wakatoka nje kwenda mlima wa Mizeituni.",
			"31": "<sup>31</sup> Ndipo Yesu akawaambia, Ninyi nyote mtachukizwa kwa ajili yangu usiku huu kwa kuwa imeandikwa, Nitampiga mchungaji, na kondoo wa kundi watatawanyika.",
			"32": "<sup>32</sup> Lakini baada ya kufufuka kwangu, nitawatangulia kwenda Galilaya.",
			"33": "<sup>33</sup> Petro akajibu, akamwambia, Wajapochukizwa wote kwa ajili yako, mimi sitachukizwa kamwe.",
			"34": "<sup>34</sup> Yesu akamwambia, Amin, nakuambia wewe, usiku huu kabla ya kuwika jogoo, utanikana mara tatu.",
			"35": "<sup>35</sup> Petro akamwambia, Ijaponipasa kufa nawe, sitakukana kamwe. Na wanafunzi wote wakasema vivyo hivyo.",
			"36": "<sup>36</sup> Kisha Yesu akaenda pamoja nao mpaka bustani iitwayo Gethsemane, akawaambia wanafunzi wake, Ketini hapa, hata niende kule nikaombe.",
			"37": "<sup>37</sup> Akamchukua Petro na wale wana wawili wa Zebedayo, akaanza kuhuzunika na kusononeka.",
			"38": "<sup>38</sup> Ndipo akawaambia, Roho yangu ina huzuni nyingi kiasi cha kufa; kaeni hapa, mkeshe pamoja nami.",
			"39": "<sup>39</sup> Akaendelea mbele kidogo, akaanguka kifulifuli, akaomba, akisema, Baba yangu, ikiwezekana, kikombe hiki kiniepuke; walakini si kama nitakavyo mimi, bali kama utakavyo wewe.",
			"40": "<sup>40</sup> Akawajia wale wanafunzi, akawakuta wamelala, akamwambia Petro, Je! Hamkuweza kukesha pamoja nami hata saa moja?",
			"41": "<sup>41</sup> Kesheni, mwombe, msije mkaingia majaribuni; roho i radhi, lakini mwili ni dhaifu.",
			"42": "<sup>42</sup> Akaenda tena mara ya pili, akaomba, akisema, Baba yangu, ikiwa haiwezekani kikombe hiki kiniepuke nisipokunywa, mapenzi yako yatimizwe.",
			"43": "<sup>43</sup> Akaja tena, akawakuta wamelala; maana macho yao yamekuwa mazito.",
			"44": "<sup>44</sup> Akawaacha tena, akaenda, akaomba mara ya tatu, akisema maneno yale yale.",
			"45": "<sup>45</sup> Kisha akawajia wanafunzi wake, akawaambia, Laleni sasa, mpumzike tazama, saa imekaribia, na Mwana wa Adamu anatiwa katika mikono ya wenye dhambi.",
			"46": "<sup>46</sup> Ondokeni, twende zetuni! Tazama, yule anayenisaliti amekaribia.",
			"47": "<sup>47</sup> Basi alipokuwa katika kusema, tazama, Yuda, mmoja wa wale Thenashara akaja, na pamoja naye kundi kubwa wenye panga na marungu, wametoka kwa wakuu wa makuhani na wazee wa watu.",
			"48": "<sup>48</sup> Na yule mwenye kumsaliti alikuwa amewapa ishara, akisema, Nitakayembusu, huyo ndiye; mkamateni.",
			"49": "<sup>49</sup> Mara akamwendea Yesu, akasema, Salamu, Rabi, akambusu.",
			"50": "<sup>50</sup> Yesu akamwambia, Rafiki, fanya ulilolijia. Wakaenda, wakanyosha mikono yao wakamkamata Yesu.",
			"51": "<sup>51</sup> Na tazama, mmoja wao waliokuwa pamoja na Yesu akanyosha mkono wake, akaufuta upanga wake, akampiga mtumwa wa Kuhani Mkuu, akamkata sikio.",
			"52": "<sup>52</sup> Ndipo Yesu akamwambia, Rudisha upanga wako mahali pake, maana wote waushikao upanga, wataangamia kwa upanga.",
			"53": "<sup>53</sup> Ama wadhani ya kuwa mimi siwezi kumsihi Baba yangu, naye ataniletea sasa hivi zaidi ya majeshi kumi na mawili ya malaika?",
			"54": "<sup>54</sup> Yatatimizwaje basi maandiko, ya kwamba hivyo ndivyo vilivyopasa kujiri?",
			"55": "<sup>55</sup> Saa ile Yesu akawaambia makutano, Je, mmetoka kama kumkamata mnyang’anyi, wenye panga na marungu, ili kunishika? Kila siku naliketi hekaluni nikifundisha, msinikamate.",
			"56": "<sup>56</sup> Lakini haya yote yamekuwa, ili maandiko ya manabii yatimizwe. Ndipo wanafunzi wote wakamwacha, wakakimbia.",
			"57": "<sup>57</sup> Nao waliomkamata Yesu wakampeleka kwa Kayafa, Kuhani Mkuu, walikokuwa wamekutanika waandishi na wazee.",
			"58": "<sup>58</sup> Na Petro akamfuata kwa mbali mpaka behewa ya Kuhani Mkuu, akaingia ndani, akaketi pamoja na watumishi, auone mwisho.",
			"59": "<sup>59</sup> Basi wakuu wa makuhani na baraza yote wakatafuta ushuhuda wa uongo juu ya Yesu, wapate kumwua;",
			"60": "<sup>60</sup> wasiuone wangawa walitokea mashahidi wa uongo wengi.",
			"61": "<sup>61</sup> Hata baadaye wawili wakatokea, wakasema, Huyu alisema, Naweza kulivunja hekalu la Mungu, na kulijenga kwa siku tatu.",
			"62": "<sup>62</sup> Kisha Kuhani Mkuu akasimama akamwambia, Hujibu neno? Hawa wanakushuhudia nini?",
			"63": "<sup>63</sup> Lakini, Yesu akanyamaza. Kuhani Mkuu akamwambia, Nakuapisha kwa Mungu aliye hai, utuambie kama wewe ndiwe Kristo, Mwana wa Mungu.",
			"64": "<sup>64</sup> Yesu akamwambia, Wewe umesema; lakini nawaambieni, Tangu sasa mtamwona Mwana wa Adamu ameketi mkono wa kuume wa nguvu, akija juu ya mawingu ya mbinguni.",
			"65": "<sup>65</sup> Ndipo Kuhani Mkuu akararua mavazi yake, akisema, Amekufuru; mna haja gani tena ya mashahidi? Tazameni, sasa mmesikia hiyo kufuru yake;",
			"66": "<sup>66</sup> mwaonaje ninyi? Wakajibu, wakasema, Imempasa kuuawa.",
			"67": "<sup>67</sup> Ndipo wakamtemea mate ya uso, wakampiga makonde; wengine wakampiga makofi,",
			"68": "<sup>68</sup> wakisema, Ewe Kristo, tufumbulie; ni nani aliyekupiga?",
			"69": "<sup>69</sup> Na Petro alikuwa ameketi nje behewani; kijakazi mmoja akamwendea, akasema, Wewe nawe ulikuwapo pamoja na Yesu wa Galilaya.",
			"70": "<sup>70</sup> Akakana mbele ya wote, akisema, Sijui usemalo.",
			"71": "<sup>71</sup> Naye alipotoka nje hata ukumbini, mwanamke mwingine alimwona, akawaambia watu waliokuwako huko, Huyu alikuwapo pamoja na Yesu Mnazareti.",
			"72": "<sup>72</sup> Akakana tena kwa kiapo, Simjui mtu huyu.",
			"73": "<sup>73</sup> Punde kidogo, wale waliohudhuria wakamwendea, wakamwambia Petro, Hakika wewe nawe u mmoja wao; kwa sababu hata usemi wako wakutambulisha.",
			"74": "<sup>74</sup> Ndipo akaanza kulaani na kuapa akisema, Simjui mtu huyu. Na mara akawika jogoo.",
			"75": "<sup>75</sup> Petro akalikumbuka lile neno la Yesu alilolisema, Kabla ya kuwika jogoo, utanikana mara tatu. Akatoka nje, akalia kwa majonzi."
		},
		"27": {
			"1": "<sup>1</sup> Na ilipokuwa asubuhi, wakuu wa makuhani wote na wazee wa watu wakafanya shauri juu ya Yesu, wapate kumwua;",
			"2": "<sup>2</sup> wakamfunga, wakamchukua, wakampeleka kwa Pilato aliyekuwa liwali.",
			"3": "<sup>3</sup> Kisha Yuda, yule mwenye kumsaliti, alipoona ya kuwa amekwisha kuhukumiwa, alijuta, akawarudishia wakuu wa makuhani na wazee vile vipande thelathini vya fedha, akasema, Nalikosa nilipoisaliti damu isiyo na hatia.",
			"4": "<sup>4</sup> Wakasema, Basi, haya yatupasani sisi? Yaangalie haya wewe mwenyewe.",
			"5": "<sup>5</sup> Akavitupa vile vipande vya fedha katika hekalu, akaondoka; akaenda, akajinyonga.",
			"6": "<sup>6</sup> Wakuu wa makuhani wakavitwaa vile vipande vya fedha, wakasema, Si halali kuviweka katika sanduku ya sadaka, kwa kuwa ni kima cha damu.",
			"7": "<sup>7</sup> Wakafanya shauri, wakavitumia kwa kununua konde la mfinyanzi liwe mahali pa kuzika wageni.",
			"8": "<sup>8</sup> Kwa hiyo konde lile huitwa konde la damu hata leo.",
			"9": "<sup>9</sup> Ndipo likatimia neno lililonenwa na nabii Yeremia, akisema, Wakavitwaa vipande thelathini vya fedha, kima chake aliyetiwa kima, ambaye baadhi ya Waisraeli walimtia kima;",
			"10": "<sup>10</sup> wakavitumia kwa kununua konde la mfinyanzi, kama Bwana alivyoniagiza.",
			"11": "<sup>11</sup> Naye Yesu akasimama mbele ya liwali; liwali akamwuliza, akasema, Wewe ndiwe mfalme wa Wayahudi? Yesu akamwambia, Wewe wasema.",
			"12": "<sup>12</sup> Lakini aliposhitakiwa na wakuu wa makuhani na wazee, hakujibu hata neno.",
			"13": "<sup>13</sup> Ndipo Pilato akamwambia, Husikii ni mambo mangapi wanayokushuhudia?",
			"14": "<sup>14</sup> Asimjibu hata neno moja, hata liwali akastaajabu sana.",
			"15": "<sup>15</sup> Basi wakati wa siku kuu, liwali desturi yake huwafungulia mkutano mfungwa mmoja waliyemtaka.",
			"16": "<sup>16</sup> Basi palikuwa na mfungwa mashuhuri siku zile, aitwaye Baraba.",
			"17": "<sup>17</sup> Basi walipokutanika, Pilato akawaambia, Mnataka niwafungulie yupi? Yule Baraba, au Yesu aitwaye Kristo?",
			"18": "<sup>18</sup> Kwa maana alijua ya kuwa wamemtoa kwa husuda.",
			"19": "<sup>19</sup> Na alipokuwa ameketi juu ya kiti cha hukumu, mkewe alimpelekea mjumbe, kumwambia, Usiwe na neno na yule mwenye haki; kwa sababu nimeteswa mengi leo katika ndoto kwa ajili yake.",
			"20": "<sup>20</sup> Nao wakuu wa makuhani na wazee wakawashawishi makutano ili wamtake Baraba, na kumwangamiza Yesu.",
			"21": "<sup>21</sup> Basi liwali akajibu, akawaambia, Mnataka niwafungulie yupi katika hawa wawili? Wakasema, Baraba.",
			"22": "<sup>22</sup> Pilato akawaambia, Basi, nimtendeje Yesu aitwaye Kristo? Wakasema wote, Asulibiwe.",
			"23": "<sup>23</sup> Akasema, Kwani? Ni ubaya gani alioutenda? Wakazidi sana kupiga kelele, wakisema, Na asulibiwe.",
			"24": "<sup>24</sup> Basi Pilato alipoona ya kuwa hafai lo lote, bali ghasia inazidi tu, akatwaa maji, akanawa mikono yake mbele ya mkutano, akasema, Mimi sina hatia katika damu ya mtu huyu mwenye haki; yaangalieni haya ninyi wenyewe.",
			"25": "<sup>25</sup> Watu wote wakajibu wakasema, Damu yake na iwe juu yetu, na juu ya watoto wetu.",
			"26": "<sup>26</sup> Ndipo akawafungulia Baraba; na baada ya kumpiga Yesu mijeledi, akamtoa ili asulibiwe.",
			"27": "<sup>27</sup> Ndipo askari wa liwali wakamchukua Yesu ndani ya Praitorio, wakamkusanyikia kikosi kizima.",
			"28": "<sup>28</sup> Wakamvua nguo, wakamvika vazi jekundu.",
			"29": "<sup>29</sup> Wakasokota taji ya miiba, wakaiweka juu ya kichwa chake, na mwanzi katika mkono wake wa kuume; wakapiga magoti mbele yake, wakamdhihaki, wakisema, Salamu, Mfalme wa Wayahudi!",
			"30": "<sup>30</sup> Kisha wakamtemea mate, wakautwaa ule mwanzi, wakampiga-piga kichwani.",
			"31": "<sup>31</sup> Walipokwisha kumdhihaki, wakalivua lile vazi, wakamvika mavazi yake, wakamchukua kumsulibisha.",
			"32": "<sup>32</sup> Hata walipokuwa wakitoka, wakamwona mtu Mkirene, jina lake Simoni; huyu wakamshurutisha auchukue msalaba wake.",
			"33": "<sup>33</sup> Na walipofika mahali paitwapo Golgotha, yaani, Fuvu la kichwa,",
			"34": "<sup>34</sup> wakampa kunywa divai iliyochanganyika na nyongo; lakini yeye alipoionja hakutaka kunywa.",
			"35": "<sup>35</sup> Walipokwisha kumsulibisha, waligawa mavazi yake, wakipiga kura; [ili litimie neno lililonenwa na nabii, Waligawa nguo zangu kati yao, na juu ya vazi langu walipiga kura.]",
			"36": "<sup>36</sup> Wakaketi, wakamlinda huko.",
			"37": "<sup>37</sup> Wakaweka juu ya kichwa chake mashitaka yake, yaliyoandikwa HUYU NI YESU, MFALME WA WAYAHUDI.",
			"38": "<sup>38</sup> Wakati uo huo wanyang’anyi wawili wakasulibiwa pamoja naye, mmoja mkono wake wa kuume, na mmoja mkono wake wa kushoto.",
			"39": "<sup>39</sup> Nao waliokuwa wakipita njiani wakamtukana, wakitikisa-tikisa vichwa vyao, wakisema,",
			"40": "<sup>40</sup> Ewe mwenye kulivunja hekalu na kulijenga kwa siku tatu, jiokoe nafsi yako; ukiwa ndiwe Mwana wa Mungu, shuka msalabani.",
			"41": "<sup>41</sup> Kadhalika na wale wakuu wa makuhani wakamdhihaki pamoja na waandishi na wazee, wakisema, Aliokoa wengine, hawezi kujiokoa mwenyewe.",
			"42": "<sup>42</sup> Yeye ni mfalme wa Israeli; na ashuke sasa msalabani, nasi tutamwamini.",
			"43": "<sup>43</sup> Amemtegemea Mungu; na amwokoe sasa, kama anamtaka; kwa maana alisema, Mimi ni Mwana wa Mungu.",
			"44": "<sup>44</sup> Pia wale wanyang’anyi waliosulibiwa pamoja naye walimshutumu vile vile.",
			"45": "<sup>45</sup> Basi tangu saa sita palikuwa na giza juu ya nchi yote hata saa tisa.",
			"46": "<sup>46</sup> Na kama saa tisa, Yesu akapaza sauti yake kwa nguvu akisema, Eloi, Eloi, lama sabakthani? Yaani, Mungu wangu, Mungu wangu, mbona umeniacha?",
			"47": "<sup>47</sup> Na baadhi yao waliohudhuria, waliposikia, walisema, Huyu anamwita Eliya.",
			"48": "<sup>48</sup> Mara mmoja wao akaenda mbio, akatwaa sifongo, akaijaza siki, akaitia juu ya mwanzi, akamnywesha.",
			"49": "<sup>49</sup> Wale wengine wakasema, Acha; na tuone kama Eliya anakuja kumwokoa.",
			"50": "<sup>50</sup> Naye Yesu akiisha kupaza sauti tena kwa nguvu, akaitoa roho yake.",
			"51": "<sup>51</sup> Na tazama, pazia la hekalu likapasuka vipande viwili toka juu hata chini; nchi ikatetemeka; miamba ikapasuka;",
			"52": "<sup>52</sup> makaburi yakafunuka; ikainuka miili mingi ya watakatifu waliolala;",
			"53": "<sup>53</sup> nao wakiisha kutoka makaburini mwao, baada ya kufufuka kwake, wakauingia mji mtakatifu, wakawatokea wengi.",
			"54": "<sup>54</sup> Basi yule akida, na hao waliokuwa pamoja naye wakimlinda Yesu, walipoliona tetemeko la nchi na mambo yaliyofanyika, wakaogopa sana, wakisema, Hakika huyu alikuwa Mwana wa Mungu.",
			"55": "<sup>55</sup> Palikuwa na wanawake wengi pale wakitazama kwa mbali, hao ndio waliomfuata Yesu toka Galilaya, na kumtumikia.",
			"56": "<sup>56</sup> Miongoni mwao alikuwamo Mariamu Magdalene, na Mariamu mama yao Yakobo na Yusufu, na mama yao wana wa Zebedayo.",
			"57": "<sup>57</sup> Hata ilipokuwa jioni akafika mtu tajiri wa Arimathaya, jina lake Yusufu, naye mwenyewe alikuwa mwanafunzi wa Yesu;",
			"58": "<sup>58</sup> mtu huyu alimwendea Pilato akauomba mwili wa Yesu. Ndipo Pilato akaamuru apewe.",
			"59": "<sup>59</sup> Yusufu akautwaa mwili, akauzonga-zonga katika sanda ya kitani safi,",
			"60": "<sup>60</sup> akauweka katika kaburi lake jipya, alilokuwa amelichonga mwambani; akavingirisha jiwe kubwa mbele ya mlango wa kaburi, akaenda zake.",
			"61": "<sup>61</sup> Na pale walikuwapo Mariamu Magdalene, na Mariamu yule wa pili, wameketi kulielekea kaburi.",
			"62": "<sup>62</sup> Hata siku ya pili, ndiyo iliyo baada ya Maandalio, wakuu wa makuhani, na Mafarisayo wakamkusanyikia Pilato,",
			"63": "<sup>63</sup> wakasema, Bwana, tumekumbuka kwamba yule mjanja alisema, alipokuwa akali hai, Baada ya siku tatu nitafufuka.",
			"64": "<sup>64</sup> Basi amuru kwamba kaburi lilindwe salama hata siku ya tatu; wasije wanafunzi wake wakamwiba, na kuwaambia watu, Amefufuka katika wafu; na udanganyifu wa mwisho utapita ule wa kwanza.",
			"65": "<sup>65</sup> Pilato akawaambia, Mna askari; nendeni mkalilinde salama kadiri mjuavyo.",
			"66": "<sup>66</sup> Wakaenda, wakalilinda kaburi salama, kwa kulitia lile jiwe muhuri, pamoja na wale askari walinzi."
		},
		"28": {
			"1": "<sup>1</sup> Hata sabato ilipokwisha, ikipambazuka siku ya kwanza ya juma, Mariamu Magdalene, na Mariamu yule wa pili, walikwenda kulitazama kaburi.",
			"2": "<sup>2</sup> Na tazama, palikuwa na tetemeko kubwa la nchi; kwa sababu malaika wa Bwana alishuka kutoka mbinguni, akaja akalivingirisha lile jiwe akalikalia.",
			"3": "<sup>3</sup> Na sura yake ilikuwa kama umeme, na mavazi yake meupe kama theluji.",
			"4": "<sup>4</sup> Na kwa kumwogopa, wale walinzi wakatetemeka, wakawa kama wafu.",
			"5": "<sup>5</sup> Malaika akajibu, akawaambia wale wanawake, Msiogope ninyi; kwa maana najua ya kuwa mnamtafuta Yesu aliyesulibiwa.",
			"6": "<sup>6</sup> Hayupo hapa; kwani amefufuka kama alivyosema. Njoni, mpatazame mahali alipolazwa.",
			"7": "<sup>7</sup> Nanyi nendeni upesi, mkawaambie wanafunzi wake, Amefufuka katika wafu. Tazama, awatangulia kwenda Galilaya; ndiko mtakakomwona. Haya, nimekwisha waambia.",
			"8": "<sup>8</sup> Wakaondoka upesi kutoka kaburini, kwa hofu na furaha nyingi, wakaenda mbio kuwapasha wanafunzi wake habari.",
			"9": "<sup>9</sup> Na tazama, Yesu akakutana nao, akisema, Salamu! Wakakaribia wakamshika miguu, wakamsujudia.",
			"10": "<sup>10</sup> Kisha Yesu akawaambia, Msiogope; enendeni, mkawaambie ndugu zangu waende Galilaya, ndiko watakakoniona.",
			"11": "<sup>11</sup> Nao wale walipokuwa wakienda, tazama, baadhi ya askari waliingia mjini, wakawapasha wakuu wa makuhani habari za mambo yote yaliyotendeka.",
			"12": "<sup>12</sup> Wakakusanyika pamoja na wazee, wakafanya shauri, wakawapa askari fedha nyingi,",
			"13": "<sup>13</sup> wakisema, Semeni, ya kwamba wanafunzi wake walikuja usiku, wakamwiba, sisi tulipokuwa tumelala.",
			"14": "<sup>14</sup> Na neno hili likisikilikana kwa liwali, sisi tutasema naye, nanyi tutawaondolea wasiwasi.",
			"15": "<sup>15</sup> Basi wakazitwaa zile fedha, wakafanya kama walivyofundishwa. Na neno hilo likaenea kati ya Wayahudi hata leo.",
			"16": "<sup>16</sup> Na wale wanafunzi kumi na mmoja wakaenda Galilaya mpaka mlima ule aliowaagiza Yesu.",
			"17": "<sup>17</sup> Nao walipomwona, walimsujudia; lakini baadhi yao waliona shaka.",
			"18": "<sup>18</sup> Yesu akaja kwao, akasema nao, akawaambia, Nimepewa mamlaka yote mbinguni na duniani.",
			"19": "<sup>19</sup> Basi, enendeni, mkawafanye mataifa yote kuwa wanafunzi, mkiwabatiza kwa jina la Baba, na Mwana, na Roho Mtakatifu;",
			"20": "<sup>20</sup> na kuwafundisha kuyashika yote niliyowaamuru ninyi; na tazama, mimi nipo pamoja nanyi siku zote, hata ukamilifu wa dahari."
		}
	}
};
module.exports = book;