var book = {
	"name": "1 iziKronike",
	"numChapters": 29,
	"chapters": {
		"1": {
			"1": "<sup>1</sup> O-Adamu, noSeti, no-Enoshe,",
			"2": "<sup>2</sup> noKhenani, noMahalalele, noJarede,",
			"3": "<sup>3</sup> no-Enoke, noMetushela, noLameka,",
			"4": "<sup>4</sup> noNowa, noShemi, noHamu, noJafete.",
			"5": "<sup>5</sup> Amadodana kaJafete: ngoGomere, noMagoge, noMadayi, noJavani, noThubali, noMesheke, noTirase.",
			"6": "<sup>6</sup> Amadodana kaGomere: ngo-Ashikenaze, noDifate, noThogarma.",
			"7": "<sup>7</sup> Amadodana kaJavani: ngo-Elisha, noTharishishi, noKitimi, noRodanimi.",
			"8": "<sup>8</sup> Amadodana kaHamu: ngoKushe, noMisirayimi, noPhuthi, noKhanani.",
			"9": "<sup>9</sup> Amadodana kaKushe: ngoSeba, noHavila, noSabita, noRama, noSabitheka. Amadodana kaRama: ngoSheba noDedani.",
			"10": "<sup>10</sup> UKushe wazala uNimrode; yena waqala ukuba ngonamandla emhlabeni.",
			"11": "<sup>11</sup> UMisirayimi wazala amaLudi, nama-Anami, namaLehabi, namaNafetuhi,",
			"12": "<sup>12</sup> namaPatrusi, namaKasuluhi, lapho kwavela khona amaFilisti, namaKafitori.",
			"13": "<sup>13</sup> UKhanani wazala uSidoni, izibulo lakhe, noHeti,",
			"14": "<sup>14</sup> namaJebusi, nama-Amori, namaGirigashi,",
			"15": "<sup>15</sup> namaHivi, nama-Arki, namaSini,",
			"16": "<sup>16</sup> nama-Arvadi, namaSemari, namaHamati.",
			"17": "<sup>17</sup> Amadodana kaShemi: ngo-Elamu, no-Ashuri, no-Arpakishade, noLude, no-Aramu, no-Use, noHule, noGeteri, noMesheke.",
			"18": "<sup>18</sup> U-Arpakishade wazala uShela; uShela wazala u-Ebere.",
			"19": "<sup>19</sup> U-Ebere wazala amadodana amabili: igama lomunye lalinguPhelege,* ngokuba emihleni yakhe izwe ladatshulwa. Igama lomfowabo lalinguJokothane.",
			"20": "<sup>20</sup> UJokothane wazala o-Alimodade, noShelefe, noHasaramavete, noJera,",
			"21": "<sup>21</sup> noHadoramu, no-Uzali, noDikila,",
			"22": "<sup>22</sup> no-Ebali, no-Abimayele, noSheba,",
			"23": "<sup>23</sup> no-Ofiri, noHavila, noJobabi; bonke labo babe ngamadodana kaJokothane;",
			"24": "<sup>24</sup> oShemi, no-Arpakishade, noShela,",
			"25": "<sup>25</sup> no-Ebere, noPhelege, noRehu,",
			"26": "<sup>26</sup> noSerugi, noNahori, noThera,",
			"27": "<sup>27</sup> no-Abrama ongu-Abrahama.",
			"28": "<sup>28</sup> Amadodana ka-Abrahama: ngo-Isaka no-Ishmayeli.",
			"29": "<sup>29</sup> Lezi ziyizizukulwane zabo: izibulo lika-Ishmayeli uNebayoti, noKedari, no-Adibeli, noMibisamu,",
			"30": "<sup>30</sup> noMishima, noDuma, noMasa, noHadadi, noThema,",
			"31": "<sup>31</sup> noJeturu, noNafishi, noKhedema. Labo babe ngamadodana ka-Ishmayeli.",
			"32": "<sup>32</sup> Amadodana kaKetura, isancinza sika-Abrahama: wazala oZimrani, noJokishani, noMedani, noMidiyani, no-Ishibaki, noShuwa. Amadodana kaJokishani: ngoSheba noDedani.",
			"33": "<sup>33</sup> Amadodana kaMidiyani: ngo-Efa, no-Eferi, noHanoki, no-Abida, no-Elidaha. Bonke labo babe ngamadodana kaKetura.",
			"34": "<sup>34</sup> U-Abrahama wazala u-Isaka. Amadodana ka-Isaka: ngo-Esawu no-Israyeli.",
			"35": "<sup>35</sup> Amadodana ka-Esawu: ngo-Elifazi, noRehuweli, noJewushe, noJalamu, noKora.",
			"36": "<sup>36</sup> Amadodana ka-Elifazi: ngoThemani, no-Omari, noSefi, noGathamu, noKenazi, noThimna, no-Amaleki.",
			"37": "<sup>37</sup> Amadodana kaRehuweli: ngoNahati, noZera, noShama, noMiza.",
			"38": "<sup>38</sup> Amadodana kaSeyiri: ngoLothani, noShobali, noSibeyoni, no-Ana, noDishoni, no-Eseri, noDishani.",
			"39": "<sup>39</sup> Amadodana kaLothani: ngoHori noHomamu; udadewabo kaLothani wayenguThimna.",
			"40": "<sup>40</sup> Amadodana kaShobali: ngo-Aliyani, noManahati, no-Ebali, noShefi, no-Onamu. Amadodana kaSibeyoni: ngo-Aya no-Ana.",
			"41": "<sup>41</sup> Amadodana ka-Ana: nguDishoni. Amadodana kaDishoni: ngoHamrani, no-Eshibani, noJitrani, noKerani.",
			"42": "<sup>42</sup> Amadodana ka-Eseri: ngoBilihani, noZahawana, noJahakhani. Amadodana kaDishani: ngo-Use no-Arana.",
			"43": "<sup>43</sup> Lawa angamakhosi abusa ezweni lakwa-Edomi, kungakabusi nkosi phezu kwabantwana bakwa-Israyeli: uBela indodana kaBeyori. Igama lomuzi wakhe laliyiDinihaba.",
			"44": "<sup>44</sup> Wafa uBela; uJobabi indodana kaZera waseBosira waba yinkosi esikhundleni sakhe.",
			"45": "<sup>45</sup> Wafa uJobabi; uHushamu wasezweni labakwaThemani waba yinkosi esikhundleni sakhe.",
			"46": "<sup>46</sup> Wafa uHushamu; uHadadi, indodana kaBedadi, owanqoba abakwaMidiyani ethafeni lakwaMowabi, waba yinkosi esikhundleni sakhe; igama lomuzi wakhe laliyi-Aviti.",
			"47": "<sup>47</sup> Wafa uHadadi; uSamla waseMasireka waba yinkosi esikhundleni sakhe.",
			"48": "<sup>48</sup> Wafa uSamla; uShawule waseRehoboti ngaseMfuleni* waba yinkosi esikhundleni sakhe.",
			"49": "<sup>49</sup> Wafa uShawule; uBali Hanani indodana ka-Akibori waba yinkosi esikhundleni sakhe.",
			"50": "<sup>50</sup> Wafa uBali Hanani; uHadadi waba yinkosi esikhundleni sakhe; igama lomuzi wakhe laliyiPhahi; igama lomkakhe lalinguMehetabeli indodakazi kaMatiredi, indodakazi kaMezahabi.",
			"51": "<sup>51</sup> Wafa uHadadi; abanumzane bakwa-Edomi kwakuyilaba: umnumzane uThimna, nomnumzane u-Aliya, nomnumzane uJeteti,",
			"52": "<sup>52</sup> nomnumzane u-Oholibama, nomnumzane u-Ela, nomnumzane uPhinoni,",
			"53": "<sup>53</sup> nomnumzane uKenazi, nomnumzane uThemani, nomnumzane uMibisari,",
			"54": "<sup>54</sup> nomnumzane uMagidiyeli, nomnumzane u-Iramu. Yilabo abangabanumzane bakwa-Edomi."
		},
		"2": {
			"1": "<sup>1</sup> Nanka amadodana ka-Israyeli: oRubeni, noSimeyoni, noLevi, noJuda, no-Isakare, noZebuloni,",
			"2": "<sup>2</sup> noDani, noJosefa, noBenjamini, noNafetali, noGadi, no-Asheri.",
			"3": "<sup>3</sup> Amadodana kaJuda: ngo-Eri, no-Onani, noShela; labo abathathu bazalwa nguye kuBati Shuwa, umKhananikazi. Kepha u-Eri, izibulo likaJuda, wayemubi emehlweni kaJehova; wambulala.",
			"4": "<sup>4</sup> UTamari umalokazana wakhe wamzalela oPheresi noZera. Onke amadodana kaJuda ayeyisihlanu.",
			"5": "<sup>5</sup> Amadodana kaPheresi: ngoHesironi noHamule.",
			"6": "<sup>6</sup> Amadodana kaZera: ngoZimri, no-Ethani, noHemani, noKalikoli, noDara, eyisihlanu onke.",
			"7": "<sup>7</sup> Amadodana kaKarmi: ngo-Akari, umhluphi ka-Israyeli, owenza isiphambeko ngokuqalekisiweyo.",
			"8": "<sup>8</sup> Amadodana ka-Ethani: ngu-Azariya.",
			"9": "<sup>9</sup> Amadodana kaHesironi awazalelwayo: ngoJerameli, noRamu, noKelubayi.",
			"10": "<sup>10</sup> URamu wazala u-Aminadaba; u-Aminadaba wazala uNaheshoni, isikhulu sabantwana bakwaJuda;",
			"11": "<sup>11</sup> uNaheshoni wazala uSalima; uSalima wazala uBowazi;",
			"12": "<sup>12</sup> uBowazi wazala u-Obede; u-Obede wazala uJese.",
			"13": "<sup>13</sup> UJese wazala izibulo lakhe u-Eliyabi, no-Abinadaba owesibili, noShimeya owesithathu,",
			"14": "<sup>14</sup> noNethaneli owesine, noRadayi owesihlanu,",
			"15": "<sup>15</sup> no-Osemi owesithupha, noDavide owesikhombisa;",
			"16": "<sup>16</sup> odadewabo: ngoSeruya no-Abigayili. Amadodana kaSeruya: ngo-Abishayi, noJowabe, no-Asaheli, emathathu.",
			"17": "<sup>17</sup> U-Abigayili wazala u-Amasa; uyise ka-Amasa wayenguJetheri umIshmayeli.",
			"18": "<sup>18</sup> UKalebi indodana kaHesironi wazala ku-Azuba umkakhe nakuJeriyoti; amadodana akhe: ngoJesheri, noShobabi, no-Aridoni.",
			"19": "<sup>19</sup> Wafa u-Azuba; uKalebi wathatha u-Efrati, owamzalela uHure.",
			"20": "<sup>20</sup> UHure wazala u-Uri; u-Uri wazala uBesaleli.",
			"21": "<sup>21</sup> Ngasemuva uHesironi wangena kuyo indodakazi kaMakiri uyise kaGileyadi; wamthatha eseneminyaka engamashumi ayisithupha; wamzalela uSegubi.",
			"22": "<sup>22</sup> USegubi wazala uJayire, owayenemizi engamashumi amabili nantathu ezweni lakwaGileyadi.",
			"23": "<sup>23</sup> OGeshuri no-Aramu babaphuca imizi kaJayirem, kanye neKhenati nemizi yalo, imizi engamashumi ayisithupha. Bonke labo babe ngamadodana kaMakiri uyise kaGileyadi.",
			"24": "<sup>24</sup> Emva kokuba esefile uHesironi eKalebi Efratha, u-Abiya, umkaHesironi, wamzalela u-Ashuri uyise kaThekhowa.",
			"25": "<sup>25</sup> Amadodana kaJerameli, izibulo likaHesironi, ayenguRamu izibulo, noBuna, no-Oreni, no-Osemi, no-Ahiya.",
			"26": "<sup>26</sup> UJerameli wayenomunye umfazi ogama lakhe lalingu-Athara; wayengunina ka-Onamu.",
			"27": "<sup>27</sup> Amadodana kaRamu, izibulo likaJerameli, ayengoMahasi, noJamini, no-Ekheri.",
			"28": "<sup>28</sup> Amadodana ka-Onamu ayengoShamayi noJada; amadodana kaShamayi: ngoNadabi no-Abishuri.",
			"29": "<sup>29</sup> Igama lomka-Abishuri lalingu-Abihayili; wamzalela o-Ahibani noMolidi.",
			"30": "<sup>30</sup> Amadodana kaNadabi: ngoSeledi no-Aphayimi; kepha uSeledi wafa engenabantwana.",
			"31": "<sup>31</sup> Amadodana ka-Aphayimi: ngu-Ishi; amadodana ka-Ishi: nguSheshani; amadodana kaSheshani: ngu-Ahilayi.",
			"32": "<sup>32</sup> Amadodana kaJada umfowabo kaShamayi: ngoJetheri noJonathani; uJetheri wafa engenabantwana.",
			"33": "<sup>33</sup> Amadodana kaJonathani: ngoPheleti noZaza. Labo babe ngamadodana kaJerameli.",
			"34": "<sup>34</sup> Kepha uSheshani wayengenamadodana, enamadodakazi odwa. USheshani wayenenceku, eyaseGibithe, egama layo lalinguJariha.",
			"35": "<sup>35</sup> USheshani wanika uJariha inceku yakhe indodakazi yakhe ibe ngumkakhe; wamzalela u-Athayi.",
			"36": "<sup>36</sup> U-Athayi wazala uNathani; uNathani wazala uZabadi;",
			"37": "<sup>37</sup> uZabadi wazala u-Efilali; u-Efilali wazala u-Obede;",
			"38": "<sup>38</sup> u-Obede wazala uJehu; uJehu wazala u-Azariya;",
			"39": "<sup>39</sup> u-Azariya wazala uHelesi; uHelesi wazala u-Eleyasa;",
			"40": "<sup>40</sup> u-Eleyasa wazala uSisamayi; uSisamayi wazala uShalumi;",
			"41": "<sup>41</sup> uShalumi wazala uJekamiya; uJekamiya wazala u-Elishama.",
			"42": "<sup>42</sup> Amadodana kaKalebi umfowabo kaJerameli: nguMesha izibulo lakhe, owayenguyise kaZifi, namadodana kaMaresha, uyise kaHebroni.",
			"43": "<sup>43</sup> Amadodana kaHebroni: ngoKora, noTaphuwa, noRekemi, noShema.",
			"44": "<sup>44</sup> UShema wazala uRahamu uyise kaJorikeyamu; uRekemi wazala uShamayi.",
			"45": "<sup>45</sup> Indodana kaShamayi: nguMahoni; uMahoni wayenguyise kaBeti Suri.",
			"46": "<sup>46</sup> U-Efa, isancinza sikaKalebi, wazala oHarana, noMosa, noGazezi; uHarana wazala uGazezi.",
			"47": "<sup>47</sup> Amadodana kaJadayi: ngoRegemi, noJothamu, noGeshani, noPheleti, no-Efa, noShahafi.",
			"48": "<sup>48</sup> UMahakha, isancinza sikaKalebi, wazala oSheberi noThirihana.",
			"49": "<sup>49</sup> Wazala noShahafi uyise kaMadimana, noSheva uyise kaMakibena, noyise kaGibeya; indodakazi kaKalebi yayingu-Akisa.",
			"50": "<sup>50</sup> Laba babe ngamadodana kaKalebi: amadodana kaHure izibulo lika-Efratha: nguShobali uyise kaKiriyati Jeharimi,",
			"51": "<sup>51</sup> noSalima uyise kaBetlehema, noHarefi uyise kaBeti Gaderi.",
			"52": "<sup>52</sup> NoShobali uyise kaKiriyati Jeharimi wayenamadodana: nguHarowe nenxenye kaMenuhoti.",
			"53": "<sup>53</sup> Imindeni kaKiriyati Jeharimi yayingamaJetheri, namaPhuthi, namaShumati, namaMishirayiti; kubona kwavela amaSorati nama-Eshitawoli.",
			"54": "<sup>54</sup> Amadodana kaSalima: nguBetlehema, namaNethofa, no-Atharoti BetiJowabe, nenxenye yamaManahati, amaSori.",
			"55": "<sup>55</sup> Imindeni yababhali ababehlala eJabesi: ngamaThirati, namaShimeyati, namaSukati. Labo babe ngamaKheni avela kuHamati uyise wendlu kaRekabi."
		},
		"3": {
			"1": "<sup>1</sup> Laba babe ngamadodana kaDavide azalelwa wona eHebroni: izibulo ngu-Amnoni ku-Ahinohama umHizreyelikazi; owesibili nguDaniyeli ku-Abigayili umKarmelikazi;",
			"2": "<sup>2</sup> owesithathu ngu-Abisalomu indodana kaMahakha indodakazi kaTalimayi inkosi yaseGeshuri; owesine ngu-Adoniya indodana kaHagiti;",
			"3": "<sup>3</sup> owesihlanu nguShefathiya ku-Abithali; owesithupha ngu-Itereyamu ku-Egila umkakhe.",
			"4": "<sup>4</sup> Wazalelwa ayisithupha eHebroni; wabusa khona iminyaka eyisikhombisa nezinyanga eziyisithupha; eJerusalema wabusa iminyaka engamashumi amathathu nantathu.",
			"5": "<sup>5</sup> Yilaba azalelwa bona eJerusalema: ngoShimeya, noShobabi, noNathani, noSolomoni, bebane, kuBati Shuwa indodakazi ka-Amiyeli,",
			"6": "<sup>6</sup> noJibari, no-Elishama, no-Elifeleti,",
			"7": "<sup>7</sup> noNoga, noNefegi, noJafiya,",
			"8": "<sup>8</sup> no-Elishama, no-Eliyada, no-Elifeleti, beyisishiyagalolunye.",
			"9": "<sup>9</sup> Bonke labo babe ngamadodana kaDavide ngaphandle kwamadodana ezancinza; uTamari wayengudadewabo.",
			"10": "<sup>10</sup> Indodana kaSolomoni yayinguRehobowamu; ngu-Abiya indodana yakhe; ngu-Asa indodana yakhe; nguJehoshafati indodana yakhe;",
			"11": "<sup>11</sup> nguJoramu indodana yakhe; ngu-Ahaziya indodana yakhe; nguJowashi indodana yakhe;",
			"12": "<sup>12</sup> ngu-Amasiya indodana yakhe; ngu-Azariya indodana yakhe; nguJothamu indodana yakhe;",
			"13": "<sup>13</sup> ngu-Ahazi indodana yakhe; nguHezekiya indodana yakhe; nguManase indodana yakhe;",
			"14": "<sup>14</sup> ngu-Amoni indodana yakhe; nguJosiya indodana yakhe.",
			"15": "<sup>15</sup> Amadodana kaJosiya: izibulo nguJohanani; owesibili nguJehoyakimi; owesithathu nguSedekiya; owesine nguShalumi.",
			"16": "<sup>16</sup> Amadodana kaJehoyakimi: nguJekoniya indodana yakhe noSedekiya indodana yakhe.",
			"17": "<sup>17</sup> Amadodana kaJekoniya umthunjwa: ngoSheyalitiyeli indodana yakhe,",
			"18": "<sup>18</sup> noMalikiramu, noPhedaya, noShenasari, noJekamiya, noHoshama, noNedabiya.",
			"19": "<sup>19</sup> Amadodana kaPhedaya: ngoZerubabele noShimeyi; amadodana kaZerubabele: ngoMeshulamu noHananiya uShelomiti wayengudadewabo",
			"20": "<sup>20</sup> noHashuba, no-Oheli, noBerekiya, noHasadiya, noJushabi Hesedi, beyisihlanu.",
			"21": "<sup>21</sup> Amadodana kaHananiya: ngoPhelathiya, noJeshaya, namadodana kaRefaya, namadodana ka-Arinani, namadodana ka-Obadiya, namadodana kaShekaniya.",
			"22": "<sup>22</sup> Amadodana kaShekaniya: nguShemaya; amadodana kaShemaya: ngoHathushi, no-Igali, noBariya, noNehariya, noShafati, beyisithupha.",
			"23": "<sup>23</sup> Amadodana kaNehariya: ngo-Eliyohenayi, noHezekiya, no-Azirikamu, bebathathu.",
			"24": "<sup>24</sup> Amadodana ka-Eliyohenayi: ngoHodaviya, no-Eliyashibi, noPhelaya, no-Akubi, noJohanani, noDelaya, no-Anani, beyisikhombisa."
		},
		"4": {
			"1": "<sup>1</sup> Amadodana kaJuda: ngoPheresi, noHesironi, noKarmi, noHure, noShobali.",
			"2": "<sup>2</sup> URewaya indodana kaShobali wazala uJahati; uJahati wazala o-Ahumayi noLahadi. Leyo yayiyimindeni yamaSorati.",
			"3": "<sup>3</sup> Yilaba abangabakayise ka-Etamu: oJizreyeli, no-Ishima, no-Idibashi, igama likadadewabo lalinguHaseleliponi",
			"4": "<sup>4</sup> noPhenuweli, uyise kaGedori, no-Ezeri, uyise kaHusha. Yilabo ababe ngamadodana kaHure izibulo lika-Efratha, uyise kaBetlehema.",
			"5": "<sup>5</sup> U-Ashuri uyise kaThekhowa wayenabafazi ababili, oHela noNahara.",
			"6": "<sup>6</sup> UNahara wamzalela o-Ahuzamu, noHeferi, noThemeni, noHawahashitari. Yilabo ababe ngamadodana kaNahara.",
			"7": "<sup>7</sup> Amadodana kaHela: ngoSereti, noJisihari, no-Ethinani.",
			"8": "<sup>8</sup> UKhosi wazala o-Anubi, noSobeba, nemindeni ka-Ahariheli indodana kaHarumu.",
			"9": "<sup>9</sup> UJabesi wayetuseka kunabafowabo; unina wamqamba igama lokuthi uJabesi,* ethi: “Ngokuba ngamzala ngibuhlungu.”",
			"10": "<sup>10</sup> UJabesi wabiza uNkulunkulu ka-Israyeli, wathi: “Sengathi ungangibusisa nokungibusisa, wandise umkhawulo wami, isandla sakho sibe nami, ungivikele ebubini, ngingehlelwa ngubuhlungu.” UNkulunkulu wamnika akucelayo.",
			"11": "<sup>11</sup> UKelubi umfowabo kaShuba wazala uMehiri owayenguyise ka-Eshitoni.",
			"12": "<sup>12</sup> U-Eshitoni wazala uBeti Rafa, noPhaseya, noThehina uyise ka-Iri Nahashi. Yilabo ababe ngamadodana kaRekha.",
			"13": "<sup>13</sup> Amadodana kaKenazi: ngo-Otheniyeli noSeraya; amadodana ka-Otheniyeli: nguHathati.",
			"14": "<sup>14</sup> UMehonothayi wazala u-Ofira; uSeraya wazala uJowabe uyise wesigodi sabakhandi, ngokuba babe ngabakhandi.",
			"15": "<sup>15</sup> Amadodana kaKalebi indodana kaJefune: ngo-Iru, no-Ela, noNahamu, namadodana ka-Ela, noKenazi.",
			"16": "<sup>16</sup> Amadodana kaJehaleli: ngoZifi, noZifa, noThiriya, no-Asareli.",
			"17": "<sup>17</sup> Amadodana ka-Ezra: ngoJetheri, noMeredi, no-Eferi, noJaloni; wazala oMiriyamu, noShamayi, no-Ishiba uyise ka-Eshitemowa.",
			"18": "<sup>18</sup> Umkakhe umJudakazi wazala uJeredi uyise kaGedori, noHebere uyise kaSoko, noJekuthiyeli uyise kaZanowa. Kepha abanye babe ngamadodana kaBithiya indodakazi kaFaro eyathathwa nguMeredi.",
			"19": "<sup>19</sup> Amadodana omkaHodiya udadewabo kaNahamu ayenguyise kaKeyila umGarimi no-Eshitemowa umMahakha.",
			"20": "<sup>20</sup> Amadodana kaShimoni: ngo-Amnoni, noRina, noBeni Hanani, noThiloni. Amadodana ka-Ishi: ngoZoheti noBeni Zoheti.",
			"21": "<sup>21</sup> Amadodana kaShela indodana kaJuda: ngu-Eri, uyise kaLekha, noLahada uyise kaMaresha, nemindeni yendlu yabaluka ilineni elicolekileyo bendlu ka-Ashibeya,",
			"22": "<sup>22</sup> noJokimi, nabantu baseKhozeba, noJowashi, noSarafi ababebusa kwaMowabi, noJashubi Lehemi. Ngezasendulo kodwa lezi zindaba.",
			"23": "<sup>23</sup> Labo babe ngababumbi nabakhileyo eNethayimi naseGedera; bahlala khona nenkosi ngomsebenzi wayo.",
			"24": "<sup>24</sup> Amadodana kaSimeyoni: ngoNemuweli, noJamini, noJaribi, noZera, noShawule,",
			"25": "<sup>25</sup> noShalumi indodana yakhe, noMibisamu indodana yakhe, noMishima indodana yakhe.",
			"26": "<sup>26</sup> Amadodana kaMishima: ngoHamuweli indodana yakhe, noZakuri indodana yakhe, noShimeyi indodana yakhe.",
			"27": "<sup>27</sup> UShimeyi wayenamadodana ayishumi nesithupha namadodakazi ayisithupha; kepha abafowabo babengenabantwana abaningi, nomndeni wabo awandanga njengabantwana bakwaJuda.",
			"28": "<sup>28</sup> Bahlala eBeri Sheba, naseMolada, naseHasari Shuwali,",
			"29": "<sup>29</sup> naseBiliha, nase-Esemi, naseTholadi,",
			"30": "<sup>30</sup> naseBetuweli, naseHorma, naseSikilagi,",
			"31": "<sup>31</sup> naseBeti Marikaboti, naseHasari Susimi, naseBeti Biri, naseShaharayimi. Yileyo eyayiyimizi yabo kwaze kwabusa uDavide.",
			"32": "<sup>32</sup> Imizana yabo yayiyi-Etamu, ne-Ayini, neRimoni, neThokheni, ne-Ashani, imizi eyisihlanu,",
			"33": "<sup>33</sup> kanye nayo yonke imizana eyayiyizungeza le mizi kwaze kwaba seBali. Lezo zaziyizindawo zabo, bebhalwe ngezizukulwane zabo.",
			"34": "<sup>34</sup> Futhi oMeshobabi, noJamileki, noJosha indodana ka-Amasiya,",
			"35": "<sup>35</sup> noJoweli, noJehu kaJoshibiya kaSeraya ka-Asiyeli,",
			"36": "<sup>36</sup> no-Eliyohenayi, noJahakhoba, noJeshohaya, no-Asaya, no-Adiyeli, noJesimiyeli, noBenaya,",
			"37": "<sup>37</sup> noZiza kaShifi ka-Aloni kaJedaya kaShimri kaShemaya.",
			"38": "<sup>38</sup> Labo ababizwe ngamagama babeyizikhulu emindenini yabo; izindlu zawoyise zanda kakhulu.",
			"39": "<sup>39</sup> Baya ngasekungeneni eGedori ngasohlangothini lwasempumalanga kwesigodi ukufunela izimvu zabo idlelo.",
			"40": "<sup>40</sup> Bafumana idlelo elivundileyo nelihle; izwe lalibanzi nxazombili, lizolile, linokuthula, ngokuba ababehlala khona kuqala babe ngabakwaHamu.",
			"41": "<sup>41</sup> Labo abalotshwe ngamagama bafika ezinsukwini zikaHezekiya inkosi yakwaJuda, bachitha amatende abo namaMehuni afunyanwa khona, bawaqothula kuze kube namuhla, bahlala esikhundleni sabo, ngokuba kwakukhona idlelo lezimvu zabo lapho.",
			"42": "<sup>42</sup> Abanye babo, amadodana kaSimeyoni, abantu abangamakhulu ayisihlanu, baya entabeni yaseSeyiri benezinduna zabo oPhelathiya, noNehariya, noRefaya, no-Uziyeli, amadodana ka-Ishi.",
			"43": "<sup>43</sup> Bachitha insali yama-Amaleki ephunyukileyo, bahlala khona kuze kube namuhla."
		},
		"5": {
			"1": "<sup>1</sup> Amadodana kaRubeni, izibulo lika-Israyeli ngokuba wayeyizibulo; kepha njengokuba wangcolisa umbhede kayise, ubuzibulo bakhe banikwa amadodana kaJosefa indodana ka-Israyeli, futhi akabhalwa ngezizukulwane njengezibulo.",
			"2": "<sup>2</sup> Noma uJuda ebenamandla kunabafowabo, kwavela kuye umbusi, ubuzibulo nokho babungobukaJosefa.",
			"3": "<sup>3</sup> Amadodana-ke kaRubeni, izibulo lika-Israyeli: ngoHanoki, noPhalu, noHesironi, noKarmi.",
			"4": "<sup>4</sup> Amadodana kaJoweli: nguShemaya indodana yakhe, noGogi indodana yakhe, noShimeyi indodana yakhe,",
			"5": "<sup>5</sup> noMika indodana yakhe, noRewaya indodana yakhe, noBali indodana yakhe,",
			"6": "<sup>6</sup> noBera indodana yakhe, owathunjwa nguThiligati Phileneseri inkosi yase-Asiriya; wayeyisikhulu sabakwaRubeni.",
			"7": "<sup>7</sup> Nabafowabo ngemindeni yabo, bebhalwe ngezizukulwane zabo: ngoJehiyeli inhloko noZakariya,",
			"8": "<sup>8</sup> noBela ka-Azazi kaShema kaJoweli, owahlala e-Aroweri kwaze kwaba seNebo noBali Mehoni.",
			"9": "<sup>9</sup> Ngasempumalanga wakha kwaze kwaba sekuqaleni kwehlane elisuka emfuleni u-Ewufrathe, ngokuba izinkomo zabo zanda ezweni lakwaGileyadi.",
			"10": "<sup>10</sup> Emihleni kaSawule baphaka impi kumaHagiri, awa ngesandla sabo; bahlala ematendeni abo ngasempumalanga yonke yakwaGileyadi.",
			"11": "<sup>11</sup> Amadodana kaGadi ahlala malungana nabo ezweni laseBashani kwaze kwaba seSaleka:",
			"12": "<sup>12</sup> ngoJoweli inhloko, noShafamu owesibili, noJahanayi, noShafati eBashani;",
			"13": "<sup>13</sup> nabafowabo ngezindlu zawoyise: ngoMikayeli, noMeshulamu, noSheba, noJorayi, noJakani, noZiya, no-Ebere, beyisikhombisa.",
			"14": "<sup>14</sup> Labo babe ngamadodana ka-Abihayili kaHuri kaJarowa kaGileyadi kaMikayeli kaJeshishayi kaJahido kaBuzi;",
			"15": "<sup>15</sup> u-Ahi ka-Abidiyeli kaGuni wayeyinhloko yezindlu zawoyise.",
			"16": "<sup>16</sup> Bahlala kwaGileyadi eBashani nasemizini yalo nasemadlelweni onke aseSharoni kwaze kwaba semikhawulweni yawo.",
			"17": "<sup>17</sup> Bonke labo babhalwa ngezizukulwane zabo emihleni kaJothamu inkosi yakwaJuda nasemihleni kaJerobowamu inkosi yakwa-Israyeli.",
			"18": "<sup>18</sup> Amadodana kaRubeni, nabakwaGadi, nenxenye yesizwe sakwaManase, amadoda anamandla, amadoda aphatha ihawu nenkemba, ansala umnsalo, ayefunde ukulwa, ayeyizinkulungwane ezingamashumi amane nane namakhulu ayisikhombisa namashumi ayisithupha aphuma impi.",
			"19": "<sup>19</sup> Aphaka impi kumaHagiri, noJeturu, noNafishi, noNodabi.",
			"20": "<sup>20</sup> Asizwa ekulweni nabo, amaHagiri anikelwa ezandleni zawo nabo bonke ababe nawo, ngokuba akhala kuNkulunkulu empini, amncenga, ngokuba ayemethemba.",
			"21": "<sup>21</sup> Asusa izinkomo zabo, namakamela ayizinkulungwane ezingamashumi ayisihlanu, nezimvu eziyizinkulungwane ezingamakhulu amabili namashumi ayisihlanu, nezimbongolo eziyizinkulungwane ezimbili, nabantu abayizinkulungwane eziyikhulu.",
			"22": "<sup>22</sup> Ngokuba kwawa abaningi befile, lokhu impi yayingekaNkulunkulu. Ahlala esikhundleni sabo aze athunjwa.",
			"23": "<sup>23</sup> Abantwana benxenye yesizwe sakwaManase bahlala kulelo zwe, kusukela eBashani kuze kube seBali Hermoni naseSeniri nasentabeni yaseHermoni.",
			"24": "<sup>24</sup> Nazi izinhloko zezindlu zawoyise: o-Eferi, no-Ishi, no-Eliyeli, no-Aziriyeli, noJeremiya, noHodaviya, noJahidiyeli, amaqhawe anamandla, amadoda anegama, izinhloko zezindlu zawoyise.",
			"25": "<sup>25</sup> Kepha baphambuka kuNkulunkulu wawoyise, balandela ngokuphinga onkulunkulu babantu balelo zwe, uNkulunkulu abachithayo phambi kwabo.",
			"26": "<sup>26</sup> UNkulunkulu ka-Israyeli wavusa umoya kaPhuli inkosi yase-Asiriya nomoya kaThiligati Philineseri inkosi yase-Asiriya, wabathumba: abakwaRubeni, nabakwaGadi, nenxenye yesizwe sakwaManase, wabayisa eHala, naseHabori, naseHara, nasemfuleni iGozani kuze kube namuhla."
		},
		"6": {
			"1": "<sup>1</sup> Amadodana kaLevi: ngoGereshoni, noKohati, noMerari.",
			"2": "<sup>2</sup> Amadodana kaKohati: ngo-Amramu, noJisihari, noHebroni, no-Uziyeli.",
			"3": "<sup>3</sup> Abantwana bakwa-Amramu: ngo-Aroni, noMose, noMiriyamu. Amadodana ka-Aroni: ngoNadabi, no-Abihu, no-Eleyazare, no-Itamari.",
			"4": "<sup>4</sup> U-Eleyazare wazala uFinehasi; uFinehasi wazala u-Abishuwa;",
			"5": "<sup>5</sup> u-Abishuwa wazala uBuki; uBuki wazala u-Uzi;",
			"6": "<sup>6</sup> u-Uzi wazala uZeraya; uZeraya wazala uMerayoti;",
			"7": "<sup>7</sup> uMerayoti wazala u-Amariya; u-Amariya wazala u-Ahithubi;",
			"8": "<sup>8</sup> u-Ahithubi wazala uSadoki; uSadoki wazala u-Ahimahasi;",
			"9": "<sup>9</sup> u-Ahimahasi wazala u-Azariya; u-Azariya wazala uJohanani;",
			"10": "<sup>10</sup> uJohanani wazala u-Azariya; nguye owayengumpristi ethempelini elakhiwa nguSolomoni eJerusalema;",
			"11": "<sup>11</sup> u-Azariya wazala u-Amariya; u-Amariya wazala u-Ahithubi;",
			"12": "<sup>12</sup> u-Ahithubi wazala uSadoki; uSadoki wazala uShalumi;",
			"13": "<sup>13</sup> uShalumi wazala uHilikiya; uHilikiya wazala u-Azariya;",
			"14": "<sup>14</sup> u-Azariya wazala uSeraya; uSeraya wazala uJehosadaki;",
			"15": "<sup>15</sup> kepha uJehosadaki wamuka lapho uJehova ethumba uJuda neJerusalema ngesandla sikaNebukadinesari.",
			"16": "<sup>16</sup> Amadodana kaLevi: ngoGereshomu, noKohati, noMerari.",
			"17": "<sup>17</sup> Nanka amagama amadodana kaGereshomu: oLibini noShimeyi.",
			"18": "<sup>18</sup> Amadodana kaKohati: ngo-Amramu, noJisihari, noHebroni, no-Uziyeli.",
			"19": "<sup>19</sup> Amadodana kaMerari: ngoMaheli noMushi. Nansi imindeni yamaLevi njengezindlu zawoyise:",
			"20": "<sup>20</sup> abakwaGereshomu: nguLibini indodana yakhe, noJahati indodana yakhe, noZima indodana yakhe,",
			"21": "<sup>21</sup> noJowa indodana yakhe, no-Ido indodana yakhe, noZera indodana yakhe, noJewatherayi indodana yakhe.",
			"22": "<sup>22</sup> Amadodana kaKohati: ngu-Amindaba indodana yakhe, noKora indodana yakhe, no-Asiri indodana yakhe,",
			"23": "<sup>23</sup> no-Elkana indodana yakhe, no-Ebiyasafa indodana yakhe, no-Asiri indodana yakhe,",
			"24": "<sup>24</sup> noTathi indodana yakhe, no-Uriyeli indodana yakhe, no-Uziya indodana yakhe, noShawule indodana yakhe.",
			"25": "<sup>25</sup> Amadodana ka-Elkana: ngo-Amasayi no-Ahimoti.",
			"26": "<sup>26</sup> U-Elkana-ke, amadodana ka-Elkana: nguSofayi indodana yakhe, noNahati indodana yakhe,",
			"27": "<sup>27</sup> no-Eliyabe indodana yakhe, noJerohamu indodana yakhe, no-Elkana indodana yakhe.",
			"28": "<sup>28</sup> Amadodana kaSamuweli: izibulo nguJoweli, nowesibili ngu-Abiya.",
			"29": "<sup>29</sup> Amadodana kaMerari: ngoMaheli, noLibini indodana yakhe, noShimeyi indodana yakhe, no-Uza indodana yakhe,",
			"30": "<sup>30</sup> noShimeya indodana yakhe, noHagiya indodana yakhe, no-Asaya indodana yakhe.",
			"31": "<sup>31</sup> Nampa uDavide abamisela ukuhola ukuhlabelela endlini kaJehova, umphongolo usubekiwe khona.",
			"32": "<sup>32</sup> Bakhonza ngokuhlabelela phambi kwetabernakele* letende lokuhlangana, uSolomoni waze wakha indlu kaJehova eJerusalema. Bema emsebenzini wabo njengomthetho wabo.",
			"33": "<sup>33</sup> Yilaba ababemi namadodana abo: kumadodana amaKohati: nguHemani, umhlabeleli, kaJoweli kaSamuweli",
			"34": "<sup>34</sup> ka-Elkana kaJerohamu ka-Eliyeli kaThowa",
			"35": "<sup>35</sup> kaSufi ka-Elkana kaMahati ka-Amasayi",
			"36": "<sup>36</sup> ka-Elkana kaJoweli ka-Azariya kaZefaniya",
			"37": "<sup>37</sup> kaThati ka-Asiri ka-Ebiyasafa kaKora",
			"38": "<sup>38</sup> kaJisihari kaKohati kaLevi ka-Israyeli;",
			"39": "<sup>39</sup> futhi umfowabo u-Asafa owayemi ngakwesokunene sakhe, u-Asafa kaBerekiya kaShimeya",
			"40": "<sup>40</sup> kaMikayeli kaBahaseya kaNalikhiya",
			"41": "<sup>41</sup> ka-Ethini kaZera ka-Adaya",
			"42": "<sup>42</sup> ka-Ethani kaZima kaShimeyi",
			"43": "<sup>43</sup> kaJahati kaGereshomu kaLevi.",
			"44": "<sup>44</sup> Amadodana kaMerari abafowabo ayengakwesokunene: ngu-Ethani kaKishi ka-Abidi kaMaluki",
			"45": "<sup>45</sup> kaHashabiya ka-Amasiya kaHilikiya",
			"46": "<sup>46</sup> ka-Amisi kaBani kaShemeri",
			"47": "<sup>47</sup> kaMaheli kaMushi kaMerari kaLevi.",
			"48": "<sup>48</sup> Abafowabo amaLevi banikelelwa yonke inkonzo yetabernakele* lendlu kaNkulunkulu.",
			"49": "<sup>49</sup> Kepha u-Aroni namadodana akhe bashiya iminikelo e-altare leminikelo yokushiswa nase-altare lempepho, benza nawo wonke umsebenzi wendawo engcwelengcwele, benzela u-Israyeli ukubuyisana njengakho konke ayale ngakho uMose inceku kaNkulunkulu.",
			"50": "<sup>50</sup> Nanka amadodana ka-Aroni: u-Eleyazare indodana yakhe, noFinehasi indodana yakhe, no-Abishuwa indodana yakhe,",
			"51": "<sup>51</sup> noBuki indodana yakhe, no-Uzi indodana yakhe, noZeraya indodana yakhe,",
			"52": "<sup>52</sup> noMerayoti indodana yakhe, no-Amariya indodana yakhe, no-Ahithubi indodana yakhe,",
			"53": "<sup>53</sup> noSadoki indodana yakhe, no-Ahimahasi indodana yakhe.",
			"54": "<sup>54</sup> Lezi ziyizindawo zawo zokuhlala njengamakamu* awo emikhawulweni yawo: amadodana ka-Aroni emindeni yamaKohati ngokuba yiwona adliwa yinkatho yokuqala",
			"55": "<sup>55</sup> wona bawanika iHebroni ezweni lakwaJuda namadlelo alizungezayo.",
			"56": "<sup>56</sup> Kodwa amasimu omuzi nemizana yawo bakunika uKalebi, indodana kaJefune.",
			"57": "<sup>57</sup> Banika amadodana ka-Aroni imizi yokuphephela, iHebroni neLibina namadlelo awo, neJatiri, ne-Eshitemowa namadlelo awo,",
			"58": "<sup>58</sup> neHileni namadlelo alo, neDebiri namadlelo alo,",
			"59": "<sup>59</sup> ne-Ashani namadlelo alo, neBetiShemeshi namadlelo alo,",
			"60": "<sup>60</sup> nasesizweni sakwaBenjamini, iGeba namadlelo alo, ne-Alemeti namadlelo alo, ne-Anathoti namadlelo alo. Yonke imizi yawo ngemindeni yawo yayiyimizi eyishumi nantathu.",
			"61": "<sup>61</sup> Amadodana aseleyo kaKohati anikwa ngenkatho emndenini wesizwe, enxenyeni yesizwe, inxenye kaManase, imizi eyishumi,",
			"62": "<sup>62</sup> namadodana kaGereshomu ngemindeni yawo esizweni sakwa-Isakare, nasesizweni sakwa-Asheri, nasesizweni sakwaNafetali, nasesizweni sakwaManase, eBashani, imizi eyishumi nantathu,",
			"63": "<sup>63</sup> namadodana kaMerari ngenkatho ngemindeni yawo esizweni sakwaRubeni, nasesizweni sakwaGadi, nasesizweni sakwaZebuloni imizi eyishumi nambili.",
			"64": "<sup>64</sup> Abantwana bakwa-Israyeli banika amaLevi leyo mizi namadlelo ayo.",
			"65": "<sup>65</sup> Banika ngenkatho leyo mizi ebizwe ngamagama esizweni sabantwana bakwaJuda, nasesizweni sabantwana bakwaSimeyoni, nasesizweni sabantwana bakwaBenjamini.",
			"66": "<sup>66</sup> Eminye imindeni yamadodana kaKohati yayinemizi yemikhawulo yayo esizweni sakwa-Efrayimi.",
			"67": "<sup>67</sup> Bayinika imizi yokuphephela: iShekemi ezintabeni zakwa-Efrayimi namadlelo alo, neGezeri namadlelo alo,",
			"68": "<sup>68</sup> neJokimeyamu namadlelo alo, neBeti Horoni namadlelo alo,",
			"69": "<sup>69</sup> ne-Ajaloni namadlelo alo, neGati Rimoni namadlelo alo,",
			"70": "<sup>70</sup> nasenxenyeni yesizwe sakwaManase i-Anere namadlelo alo, neBileyamu namadlelo alo, ibe ngeyomndeni oseleyo wamadodana kaKohati.",
			"71": "<sup>71</sup> Amadodana kaGereshomu anikwa emndenini wenxenye yesizwe sakwaManase iGolanu, eBashani, namadlelo alo ne-Ashitaroti namadlelo alo;",
			"72": "<sup>72</sup> nasesizweni sakwa-Isakare iKedeshi namadlelo alo, neDaberati namadlelo alo,",
			"73": "<sup>73</sup> neRamoti namadlelo alo, ne-Anemi namadlelo alo;",
			"74": "<sup>74</sup> nasesizweni sakwa-Asheri iMashali namadlelo alo, ne-Abidoni namadlelo alo,",
			"75": "<sup>75</sup> neHukoki namadlelo alo, neRehobe namadlelo alo;",
			"76": "<sup>76</sup> nasesizweni sakwaNafetali iKedeshi eGalile namadlelo alo, neHamoni namadlelo alo, neKiriyatayimi namadlelo alo.",
			"77": "<sup>77</sup> Aseleyo, amadodana kaMerari, anikwa esizweni sakwaZebuloni iRimono namadlelo alo neGhabori namadlelo alo,",
			"78": "<sup>78</sup> naphesheya kweJordani ngaseJeriko ngasempumalanga kwaseJordani esizweni sakwaRubeni iBeseri ehlane namadlelo alo, neJahasa namadlelo alo,",
			"79": "<sup>79</sup> neKedemoti namadlelo alo, neMefahati namadlelo alo;",
			"80": "<sup>80</sup> nasesizweni sakwaGadi iRamoti kwaGileyadi namadlelo alo, neMahanayimi namadlelo alo,",
			"81": "<sup>81</sup> neHeshiboni namadlelo alo, neJazeri namadlelo alo."
		},
		"7": {
			"1": "<sup>1</sup> Amadodana ka-Isakare: ngoThola, noPhuwa, noJashubi, noShimroni, bebane.",
			"2": "<sup>2</sup> Amadodana kaThola: ngo-Uzi, noRefaya, noJeriyeli, noJahemayi, no-Ibisamu, noShemuweli, izinhloko zezindlu zawoyise, abakwaThola, amaqhawe anamandla ngezizukulwane zawo; umumo wawo ngezinsuku zikaDavide wawuyizinkulungwane ezingamashumi amabili nambili namakhulu ayisithupha.",
			"3": "<sup>3</sup> Amadodana ka-Uzi: nguJizirahiya; amadodana kaJizirahiya: ngoMikayeli, no-Obadiya, noJoweli, noJishiya, beyisihlanu, beyizinhloko bonke.",
			"4": "<sup>4</sup> Kanye nawo kwakukhona ngezizukulwane zawo ngezindlu zawoyise amaviyo empi yokulwa, abayizinkulungwane ezingamashumi amathathu nesithupha, ngokuba babe nabafazi abaningi namadodana amaningi.",
			"5": "<sup>5</sup> Abafowabo kuyo yonke imindeni ka-Isakare, amaqhawe anamandla, babe yizinkulungwane ezingamashumi ayisishiyagalombili nesikhombisa, bebhalwe bonke ngezizukulwane zabo.",
			"6": "<sup>6</sup> Amadodana kaBenjamini: ngoBela, noBekeri, noJediyayeli, bebathathu.",
			"7": "<sup>7</sup> Amadodana kaBela: ngo-Esiboni, no-Uzi, no-Uziyeli, noJerimoti, no-Iri, beyisihlanu, izinhloko zezindlu zawoyise, amaqhawe anamandla, beyizinkulungwane ezingamashumi amabili nambili namashumi amathathu nane, bebhalwe ngezizukulwane zabo.",
			"8": "<sup>8</sup> Amadodana kaBekeri: ngoZemira, noJowashi, no-Eliyezeri, no-Eliyohenayi, no-Omri, noJeremoti, no-Abiya, no-Anathoti, no-Alemeti. Bonke labo babe ngamadodana kaBekeri.",
			"9": "<sup>9</sup> Babhalwa ngezizukulwane zabo, beyizinhloko zezindlu zawoyise, amaqhawe anamandla, beyizinkulungwane ezingamashumi amabili namakhulu amabili.",
			"10": "<sup>10</sup> Amadodana kaJediyayeli: nguBilihani; amadodana kaBilihani: ngoJewushe, noBenjamini, no-Ehudi, noKhenahana, noZethani, noTharishishi, no-Ahishahari.",
			"11": "<sup>11</sup> Bonke labo babe ngamadodana kaJediyayeli ngezinhloko zezindlu zawoyise, amaqhawe anamandla, beyizinkulungwane eziyishumi nesikhombisa namakhulu amabili abangaphuma impi ukuyokulwa.",
			"12": "<sup>12</sup> OShuphimi noHuphimi babe ngamadodana ka-Iri; uHushimi wayeyindodana ka-Aheri.",
			"13": "<sup>13</sup> Amadodana kaNafetali: ngoJasiyeli, noGuni, noJeseri, noShalumi, amadodana kaBiliha.",
			"14": "<sup>14</sup> Amadodana kaManase: ngu-Asiriyeli owamzalelayo umkakhe; isancinza sakhe umAramukazi sazala uMakiri uyise kaGileyadi.",
			"15": "<sup>15</sup> UMakiri wathathela oHuphimi noShuphimi abafazi; igama likadadewabo lalinguMahakha. Igama lowesibili lalinguSelofehadi; uSelofehadi wayenamadodakazi.",
			"16": "<sup>16</sup> UMahakha umkaMakiri wazala indodana, waqamba igama layo ngokuthi uPhereshi; igama lomfowabo lalinguShereshi; amadodana akhe ayengo-Ulamu noRakemi.",
			"17": "<sup>17</sup> Amadodana ka-Ulamu: nguBedani. Labo babe ngamadodana kaGileyadi kaMakiri kaManase.",
			"18": "<sup>18</sup> Udadewabo uHamoleketi wazala o-Ishihodi, no-Abiyezeri, noMahela.",
			"19": "<sup>19</sup> Amadodana kaShemida ayengo-Ahiyani, noShekemi, noLikihi, no-Aniyamu.",
			"20": "<sup>20</sup> Amadodana ka-Efrayimi: ngoShuthela, noBeredi indodana yakhe, noTahati indodana yakhe, no-Eleyada indodana yakhe, noTahati indodana yakhe,",
			"21": "<sup>21</sup> noZabadi indodana yakhe, noShuthela indodana yakhe, no-Ezeri no-Eleyadi ababulawa ngabantu baseGati ababezelwe kulelo zwe, ngokuba behla ukuthatha izinkomo zabo.",
			"22": "<sup>22</sup> U-Efrayimi uyise walila izinsuku eziningi; abafowabo beza ukumduduza.",
			"23": "<sup>23</sup> Wangena kumkakhe; wamitha, wazala indodana, waqamba igama layo ngokuthi uBeriya,* ngokuba indlu yakhe yayisengozini.",
			"24": "<sup>24</sup> Indodakazi yakhe yayinguSheyera owakha iBeti Horoni laseNzansi nelaseNhla, ne-Uzeni Sheyera.",
			"25": "<sup>25</sup> URefa wayeyindodana yakhe, noReshefi, noThela indodana yakhe, noThahani indodana yakhe,",
			"26": "<sup>26</sup> noLadani indodana yakhe, no-Amihudi indodana yakhe, no-Elishama indodana yakhe,",
			"27": "<sup>27</sup> noNuni indodana yakhe, noJoshuwa indodana yakhe.",
			"28": "<sup>28</sup> Ifa labo nezindawo zabo zokuhlala kwakuyiBethele nemizana yalo, nangasempumalanga iNaharani, nangasentshonalanga iGezeri nemizana yalo, neShekemi nemizana yalo, kwaze kwaba se-Aya nemizana yalo;",
			"29": "<sup>29</sup> nangasemikhawulweni yabantwana bakwaManase iBeti Sheyani nemizana yalo, neThahanaki nemizana yalo, neMegido nemizana yalo, neDori nemizana yalo. Kuleyo kwahlala abantwana bakaJosefa indodana ka-Israyeli.",
			"30": "<sup>30</sup> Amadodana ka-Asheri: ngoJimna, no-Ishiva, no-Ishivi, noBeriya, kanye noSera udadewabo.",
			"31": "<sup>31</sup> Amadodana kaBeriya: ngoHebere noMalikiyeli owayenguyise kaBirizayiti.",
			"32": "<sup>32</sup> UHebere wazala oJafeleti, noShomeri, noHothamu, noShuwa udadewabo.",
			"33": "<sup>33</sup> Amadodana kaJafeleti: ngoPhasaki, noBimihali, no-Ashivati. Labo babe ngabantwana bakaJafeleti.",
			"34": "<sup>34</sup> Amadodana kaShemeri: ngo-Ahi, noRohiga, noJehuba, no-Aramu.",
			"35": "<sup>35</sup> Amadodana kaHelemi umfowabo: ngoSofa, noJimna, noSheleshi, no-Amali.",
			"36": "<sup>36</sup> Amadodana kaSofa: ngoShuwa, noHarineferi, noShuhali, noBeri, no-Imra,",
			"37": "<sup>37</sup> noBeseri, noHodi, noShama, noShilisha, noJitrani, noBeyera.",
			"38": "<sup>38</sup> Amadodana kaJetheri: ngoJefune, noPhisipha, no-Ara.",
			"39": "<sup>39</sup> Amadodana ka-Ula: ngo-Ara, noHaniyeli, noRisiya.",
			"40": "<sup>40</sup> Bonke labo babe ngabantwana baka-Asheri, izinhloko zezindlu zawoyise, amaqhawe akhethekileyo anamandla, izinhloko zezikhulu. Umumo wabo ubhalwe ngezizukulwane zabo ukuba balwe empini wawungabantu abayizinkulungwane ezingamashumi amabili nesithupha."
		},
		"8": {
			"1": "<sup>1</sup> UBenjamini wazala uBela izibulo lakhe, no-Ashibeli owesibili, no-Ahara owesithathu,",
			"2": "<sup>2</sup> noNoha owesine, noRafa owesihlanu.",
			"3": "<sup>3</sup> UBela wayenamadodana: ngo-Adari, noGera, no-Abihudi,",
			"4": "<sup>4</sup> no-Abishuwa, noNahamani, no-Ahowa,",
			"5": "<sup>5</sup> noGera, noShefufani, noHuramu.",
			"6": "<sup>6</sup> Nanka amadodana ka-Ehudi, izinhloko zezindlu zawoyise zabakhe eGeba, ezathunjelwa eManahati,",
			"7": "<sup>7</sup> zathunjwa ngoNahamani, no-Ahiya, noGera owazala o-Uza no-Ahihudi.",
			"8": "<sup>8</sup> UShaharayimi wazala abantwana ezweni lakwaMowabi esemukisile oHushimi noBahara omkakhe.",
			"9": "<sup>9</sup> Wazala kuHodeshi umkakhe oJobabi, noSibiya, noMesha, noMalikamu,",
			"10": "<sup>10</sup> noJewuse, noShakiya, noMirima. Labo babe ngamadodana akhe, izinhloko zezindlu zawoyise.",
			"11": "<sup>11</sup> Wazala kuHushimi o-Abithubi no-Eliphahali.",
			"12": "<sup>12</sup> Amadodana ka-Eliphahali: ngo-Ebere, noMishamu, noShemedi, abakha i-Ono neLodi nemizana yawo,",
			"13": "<sup>13</sup> noBeriya, noShema, ababe yizinhloko zezindlu zawoyise zabakhe e-Ajaloni, abaxosha abakhe eGati;",
			"14": "<sup>14</sup> no-Ahiyo, noShashaki, noJeremoti,",
			"15": "<sup>15</sup> noZebadiya, no-Aradi, no-Edere,",
			"16": "<sup>16</sup> noMikayeli, noJishipha, noJoha, amadodana kaBeriya,",
			"17": "<sup>17</sup> noZebadiya, noMeshulamu, noHiziki, noHebere,",
			"18": "<sup>18</sup> no-Ishmerayi, noJiziliya, noJobabi, amadodana ka-Eliphahali,",
			"19": "<sup>19</sup> noJakimi, noZikiri, noZabidi,",
			"20": "<sup>20</sup> no-Eliyenayi, noSilethayi, no-Eliyeli,",
			"21": "<sup>21</sup> no-Adaya, noBeraya, noShimrati, amadodana kaShimeyi,",
			"22": "<sup>22</sup> noJishiphani, no-Ebere, no-Eliyeli,",
			"23": "<sup>23</sup> no-Abidoni, noZikiri, noHanani,",
			"24": "<sup>24</sup> noHananiya, no-Elamu, no-Anithothiya,",
			"25": "<sup>25</sup> no-Ifideya, noPhenuweli, amadodana kaShashaki,",
			"26": "<sup>26</sup> noShamusherayi, noShehariya, no-Athaliya,",
			"27": "<sup>27</sup> noJahareshiya, no-Eliya, noZikiri, amadodana kaJerohamu.",
			"28": "<sup>28</sup> Labo babe yizinhloko zezindlu zawoyise, izinhloko ezizukulwaneni zabo, bahlala eJerusalema.",
			"29": "<sup>29</sup> EGibeyoni kwahlala uyise kaGibeyoni ogama lomkakhe lalinguMahakha;",
			"30": "<sup>30</sup> izibulo lakhe lalingu-Abidoni, kanye noSuri, noKishi, noBali, noNadabi,",
			"31": "<sup>31</sup> noGedori, no-Ahiyo, noZekeri.",
			"32": "<sup>32</sup> Kepha uMikiloti wazala uShimeya. Nabo bahlala nabafowabo eJerusalema malungana nabafowabo.",
			"33": "<sup>33</sup> UNeri wazala uKishi; uKishi wazala uSawule; uSawule wazala oJonathani, noMaliki Shuwa, no-Abinadaba, no-Eshi Bali.",
			"34": "<sup>34</sup> Indodana kaJonathani yayinguMeribi Bali; uMeribi Bali wazala uMika.",
			"35": "<sup>35</sup> Amadodana kaMika: ngoPhithoni, noMeleki, noThareya, no-Ahazi.",
			"36": "<sup>36</sup> U-Ahazi wazala uJehohada; uJehohada wazala o-Alemeti, no-Azimaveti, noZimri; uZimri wazala uMosa;",
			"37": "<sup>37</sup> uMosa wazala uBineya; uRafa wayeyindodana yakhe; ngu-Eleyasa indodana yakhe, ngu-Aseli indodana yakhe;",
			"38": "<sup>38</sup> u-Aseli wayenamadodana ayisithupha; amagama awo yilawa: o-Azirikamu, noBokeru, no-Ishmayeli, noShehariya, no-Obadiya, noHanani. Bonke labo babe ngamadodana ka-Aseli.",
			"39": "<sup>39</sup> Amadodana ka-Esheki umfowabo: ngu-Ulamu izibulo lakhe, noJewushe owesibili, no-Elifeleti owesithathu.",
			"40": "<sup>40</sup> Amadodana ka-Ulamu ayengamaqhawe anamandla, ansala umnsalo; ayenamadodana amaningi namadodana amadodana amaningi, eyikhulu namashumi ayisihlanu. Labo babe ngamadodana kaBenjamini."
		},
		"9": {
			"1": "<sup>1</sup> Wabhalwa-ke wonke u-Israyeli ngezizukulwane; bheka, balotshiwe encwadini yamakhosi akwa-Israyeli; uJuda wathunjelwa eBabele ngeziphambeko zakhe.",
			"2": "<sup>2</sup> Abakhileyo kuqala emafeni abo emizini yabo babe ngu-Israyeli, nabapristi, namaLevi, namaNethini.*",
			"3": "<sup>3</sup> EJerusalema kwahlala kubantwana bakwaJuda, nakubantwana bakwaBenjamini, nakubantwana bakwa-Efrayimi noManase:",
			"4": "<sup>4</sup> ngu-Uthayi ka-Amihudi ka-Omri ka-Imri kaBani kubantwana bakwaPheresi kaJuda.",
			"5": "<sup>5</sup> KumaShiloni: ngu-Asaya izibulo namadodana akhe.",
			"6": "<sup>6</sup> Kumadodana kaZera: nguJehuweli nabafowabo, bengamakhulu ayisithupha namashumi ayisishiyagalolunye.",
			"7": "<sup>7</sup> Kumadodana kaBenjamini: nguSalu kaMeshulamu kaHodaviya kaHasenuwa,",
			"8": "<sup>8</sup> no-Ibineya indodana kaJerohamu, no-Ela ka-Uzi kaMikiri, noMeshulamu kaShefathiya kaRehuweli kaJibiniya,",
			"9": "<sup>9</sup> nabafowabo ngezizukulwane zabo, bengamakhulu ayisishiyagalolunye namashumi ayisihlanu nesithupha. Bonke labo bantu babe yizinhloko zezindlu zawoyise ngokwezindlu zawoyise.",
			"10": "<sup>10</sup> Kubapristi: ngoJedaya, noJehoyaribi, noJakini,",
			"11": "<sup>11</sup> no-Azariya kaHilikiya kaMeshulamu kaSadoki kaMerayoti ka-Ahithubi, umphathi wendlu kaNkulunkulu,",
			"12": "<sup>12</sup> no-Adaya kaJerohamu kaPhashuri kaMalikhiya noMahasayi ka-Adiyeli kaJahizera kaMeshulamu kaMeshilemiti ka-Imeri,",
			"13": "<sup>13</sup> nabafowabo, izinhloko zezindlu zawoyise, beyinkulungwane namakhulu ayisikhombisa namashumi ayisithupha, amadoda ahlakaniphile emsebenzini wenkonzo yasendlini kaNkulunkulu.",
			"14": "<sup>14</sup> KumaLevi: nguShemaya kaHashubi ka-Azirikamu kaHashabiya kumadodana kaMerari,",
			"15": "<sup>15</sup> noBakibakari, noHereshi, noGalali, noMathaniya kaMika kaZikiri ka-Asafa,",
			"16": "<sup>16</sup> no-Obadiya kaShemaya kaGalali kaJeduthuni, noBerekiya ka-Asa ka-Elkana owahlala emizaneni yamaNethofa,",
			"17": "<sup>17</sup> nabalindisango oShalumi, no-Akubi, noThalimoni, no-Ahimani, nabafowabo; uShalumi wayeyinhloko.",
			"18": "<sup>18</sup> Kwaze kwaba kuleso sikhathi babe sesangweni lenkosi ngasempumalanga; babe ngabalindisango bekamu* labantwana bakwaLevi.",
			"19": "<sup>19</sup> UShalumi kaKore ka-Ebiyasafa kaKora, nabafowabo bendlu kayise, amaKorahi, babephethe umsebenzi wenkonzo, bengabalindisango etabernakele;* oyise babemele ikamu* likaJehova bengabalindisango.",
			"20": "<sup>20</sup> UFinehasi indodana ka-Eleyazare wayengumbusi phezu kwabo ngesikhathi esidlulileyo. UJehova wayenaye.",
			"21": "<sup>21</sup> UZakariya indodana kaMeshelemiya wayengumlindisango etendeni lokuhlangana.",
			"22": "<sup>22</sup> Bonke labo ababekhethiwe ukuba babe ngabalindisango babe ngamakhulu amabili neshumi nambili. Babhalwa ngezizukulwane emizaneni yabo, bamiswa ngoDavide noSamuweli umboni ezikhundleni zabo.",
			"23": "<sup>23</sup> Bona nabantwana babo babephethe amasango endlu kaJehova, indlu yetende, belinda.",
			"24": "<sup>24</sup> Abalindi babekhona ezinhlangothini ezine: ngasempumalanga, nangasentshonalanga, nangasenyakatho, nangaseningizimu.",
			"25": "<sup>25</sup> Abafowabo emizaneni yabo babeyakungena emva kwezinsuku eziyisikhombisa njalo izikhathi ngezikhathi ukuba babe nabo,",
			"26": "<sup>26</sup> ngokuba abalindi abane abakhulu ababe ngamaLevi babe sesikhundleni njalo, bephethe amakamelo nengcebo endlini kaNkulunkulu.",
			"27": "<sup>27</sup> Bahlala ebusuku bezungeza indlu kaNkulunkulu, ngokuba kwakungokwabo ukuyilinda, kwakungokwabo futhi ukuyivula ekuseni imihla ngemihla.",
			"28": "<sup>28</sup> Abanye babephethe izitsha zenkonzo, ngokuba zangeniswa ngokubalwa, zakhishwa ngokubalwa.",
			"29": "<sup>29</sup> Abanye kubo babemiselwe impahla, nezitsha zonke zendlu engcwele, nempuphu, newayini, namafutha, nenhlaka, namakha.",
			"30": "<sup>30</sup> Abanye bamadodana abapristi bagxoba amakha aba ngamafutha.",
			"31": "<sup>31</sup> UMatithiya, omunye wamaLevi, owayeyizibulo likaShalumi umKorahi wayenesikhundla sokubheka izinto ezenziwa amakhanzini.",
			"32": "<sup>32</sup> Abanye abafowabo bamadodana amaKohati babephethe izinkwa zokubukwa ukuba bazilungise onke amasabatha.",
			"33": "<sup>33</sup> Kepha abanye, abahlabeleli, izinhloko zezindlu zawoyise zamaLevi, babe semakamelweni bekhululekile kokunye, ngokuba babe semsebenzini wabo imini nobusuku.",
			"34": "<sup>34</sup> Labo babe yizinhloko zezindlu zawoyise bamaLevi, izinhloko ezizukulwaneni zabo, bahlala eJerusalema.",
			"35": "<sup>35</sup> EGibeyoni kwahlala uyise kaGibeyoni uJehiyeli ogama lomkakhe lalinguMahakha,",
			"36": "<sup>36</sup> nendodana yakhe eyizibulo u-Abidoni, kanye noSuri, noKishi, noBali, noNeri, noNadabi,",
			"37": "<sup>37</sup> noGedori, no-Ahiyo, noZakariya, noMikiloti.",
			"38": "<sup>38</sup> UMikiloti wazala uShimeyamu. Nabo bahlala nabafowabo eJerusalema malungana nabafowabo.",
			"39": "<sup>39</sup> UNeri wazala uKishi; uKishi wazala uSawule; uSawule wazala oJonathani, noMaliki Shuwa, no-Abinadaba, no-Eshi Bali.",
			"40": "<sup>40</sup> Indodana kaJonathani yayinguMeribi Bali; uMeribi Bali wazala uMika.",
			"41": "<sup>41</sup> Amadodana kaMika: ngoPhithoni, noMeleki, noThareya, no-Ahazi.",
			"42": "<sup>42</sup> U-Ahazi wazala uJara; uJara wazala o-Alemeti, no-Azimaveti, noZimri; uZimri wazala uMosa;",
			"43": "<sup>43</sup> uMosa wazala uBineya; uRefaya wayeyindodana yakhe; ngu-Eleyasa indodana yakhe, ngu-Aseli indodana yakhe;",
			"44": "<sup>44</sup> u-Aseli wayenamadodana ayisithupha; amagama awo yilawa: o-Azirikamu, noBokeru, no-Ishmayeli, noShehariya, no-Obadiya, noHanani; labo babe ngamadodana ka-Aseli."
		},
		"10": {
			"1": "<sup>1</sup> AmaFilisti alwa no-Israyeli; abantu bakwa-Israyeli babaleka phambi kwamaFilisti, bawa begwaziwe entabeni yaseGilibowa.",
			"2": "<sup>2</sup> AmaFilisti axosha uSawule namadodana akhe; amaFilisti abulala oJonathani, no-Abinadaba, noMaliki Shuwa, amadodana kaSawule.",
			"3": "<sup>3</sup> Impi yaba nzima kuSawule; abacibishelayo bamfica; wakhathazeka ngenxa yabacibishelayo.",
			"4": "<sup>4</sup> USawule wayesethi odibini lwezikhali zakhe: Hosha inkemba yakho, ungigwaze ngayo, funa labo abangasokile bafike, bangone. Kepha aluvumanga udibi lwezikhali zakhe, ngokuba lwesaba kakhulu. Ngalokho uSawule wayithatha inkemba, wawela phezu kwayo.",
			"5": "<sup>5</sup> Udibi lwezikhali zakhe selubonile ukuthi uSawule ufile, lwawela nalo phezu kwenkemba, lwafa.",
			"6": "<sup>6</sup> Wafa-ke uSawule namadodana akhe amathathu; nendlu yakhe yonke yafa kanyekanye.",
			"7": "<sup>7</sup> Lapho bonke abantu bakwa-Israyeli ababe sesigodini bebona ukuthi babaleka nokuthi uSawule namadodana akhe bafile, bashiya imizi yabo, babaleka; amaFilisti afika, ahlala kuyo.",
			"8": "<sup>8</sup> Kwathi ngangomuso, lapho amaFilisti efika ukuphanga ababuleweyo, afumana uSawule namadodana akhe bewile entabeni yaseGilibowa.",
			"9": "<sup>9</sup> Amphanga, athatha ikhanda lakhe nezikhali zakhe, akuthuma ezweni lamaFilisti nxazonke ukukumemezela kuzo izithombe zawo nakubantu.",
			"10": "<sup>10</sup> Azibeka izikhali zakhe endlini yawonkulunkulu bawo, abethela ikhanda lakhe endlini kaDagoni.",
			"11": "<sup>11</sup> Selizwile iJabeshi Gileyadi konke amaFilisti ayekwenzile kuSawule,",
			"12": "<sup>12</sup> kwasuka onke amadoda anamandla, asithatha isidumbu sikaSawule nezidumbu zamadodana akhe, aziletha eJabeshi, awembela amathambo abo phansi kwe-oki* eJabeshi, azila ukudla izinsuku eziyisikhombisa.",
			"13": "<sup>13</sup> Wafa kanjalo-ke uSawule ngesiphambeko sakhe aphambuka ngaso kuJehova ngenxa yezwi likaJehova angaligcinanga nangokuba kade eyile ukubhula konedlozi ukuba abuze kuye,",
			"14": "<sup>14</sup> engabuzanga kuJehova; ngalokho-ke wambulala, wabuyisela umbuso kuDavide indodana kaJese."
		},
		"11": {
			"1": "<sup>1</sup> Wonke u-Israyeli wabuthana kuDavide, wathi: “Bheka, thina siyithambo lakho nenyama yakho.",
			"2": "<sup>2</sup> Nangesikhathi esidlulileyo uSawule eseyinkosi, nguwe owakhipha nowangenisa u-Israyeli; uJehova uNkulunkulu wakho wathi kuwe: ‘Wena uyakwalusa abantu bami u-Israyeli, wena uyakuba ngumbusi wabantu bami u-Israyeli.’ ”",
			"3": "<sup>3</sup> Kwase kufika onke amalunga akwa-Israyeli enkosini eHebroni; uDavide wenza isivumelwano nawo eHebroni phambi kukaJehova; amgcoba uDavide ukuba abe yinkosi kwa-Israyeli njengezwi likaJehova ngesandla sikaSamuweli.",
			"4": "<sup>4</sup> UDavide naye wonke u-Israyeli baya eJerusalema eliyiJebusi; amaJebusi ayakhe kulelo zwe ayeselapho.",
			"5": "<sup>5</sup> Abakhileyo eJebusi bathi kuDavide: “Awuyikungena lapha.” UDavide wathatha nokho inqaba yaseSiyoni engumuzi kaDavide.",
			"6": "<sup>6</sup> UDavide wathi: “Lowo ochitha amaJebusi kuqala uyakuba yinhloko nenduna.” Kwenyuka kuqala uJowabe indodana kaSeruya, waba yinhloko.",
			"7": "<sup>7</sup> UDavide wahlala enqabeni; ngalokho bayibiza ngokuthi umuzi kaDavide.",
			"8": "<sup>8</sup> Wakha umuzi nxazonke kusukela eMilo kuya nxazonke; uJowabe wavusa okuseleyo komuzi.",
			"9": "<sup>9</sup> UDavide waqhubeka njalo ngokukhula, ngokuba uJehova Sebawoti wayenaye.",
			"10": "<sup>10</sup> Yilaba abayizinhloko zamaqhawe uDavide ayenazo, zibambelela kuye ngamandla ebukhosini bakhe kanye naye wonke u-Israyeli ukumbeka inkosi njengezwi likaJehova ngo-Israyeli.",
			"11": "<sup>11</sup> Nanku umumo wamaqhawe uDavide ayenawo: uJashobehamu indodana yomHakimoni, inhloko yamashumi amathathu; waphakamisela abangamakhulu amathathu umkhonto wakhe, wababulala ngasikhathi sinye.",
			"12": "<sup>12</sup> Emva kwakhe ngu-Eleyazare indodana kaDodo umAhohi owayengomunye wamaqhawe amathathu.",
			"13": "<sup>13</sup> WayenoDavide ePhasi Damimi; amaFilisti abuthana khona ukuyokulwa, lapho kwakukhona isiqephu sezwe esigcwele ibhali;* abantu babaleka ebusweni bamaFilisti.",
			"14": "<sup>14</sup> Bema phakathi nesiqephu sezwe, basophula, bawabulala amaFilisti; uJehova wabasindisa ngokusindisa okukhulu.",
			"15": "<sup>15</sup> Kwehla abathathu kwabayizinhloko ezingamashumi amathathu, baya etsheni kuDavide emhumeni wase-Adulamu; impi yamaFilisti yayimise esiGodini samaRefa.",
			"16": "<sup>16</sup> UDavide wayesesenqabeni, ikhanda lamaFilisti laliseBetlehema.",
			"17": "<sup>17</sup> UDavide wanxanela, wathi: “Hawu, sengathi ngingaphuziswa amanzi asemthonjeni waseBetlehema owawungasesangweni.”",
			"18": "<sup>18</sup> Labo abathathu bafohla ikamu* lamaFilisti, bakha amanzi emthonjeni waseBetlehema owawungasesangweni, bawathatha, bawaletha kuDavide; kepha uDavide akavumanga ukuwaphuza, kodwa wawathululela uJehova, wathi:",
			"19": "<sup>19</sup> “UNkulunkulu wami makangenqabele ukuba ngenze lokhu; ngiyakuphuza igazi lalaba bantu ababe sengozini na?” Ngokuba bawaletha besengozini; akavumanga ukuwaphuza. Lezo zinto zenziwa yilawa maqhawe amathathu.",
			"20": "<sup>20</sup> U-Abishayi umfowabo kaJowabe wayeyinhloko yabathathu, ngokuba waphakamisela abangamakhulu amathathu umkhonto wakhe, wababulala, waba negama phakathi kwalaba abathathu.",
			"21": "<sup>21</sup> Kulaba abathathu wayetusekile kunababili, waba yisikhulu sabo, kepha akafinyelelanga kwabathathu bokuqala.",
			"22": "<sup>22</sup> NoBenaya indodana kaJehoyada, indodana yendoda enamandla, owayenze imisebenzi emikhulu, owaseKabiseyeli, wabulala amaqhawe amabili akwaMowabi; futhi wehla, wabulala ingonyama phakathi komgodi ngosuku lweqhwa.",
			"23": "<sup>23</sup> Wabulala nowaseGibithe, umuntu omude, eyizingalo eziyisihlanu; esandleni sowaseGibithe kwakukhona umkhonto onjengogongolo lomaluki; wehlela kuye ephethe induku nje, wawuhlwitha umkhonto esandleni sowaseGibithe, wambulala ngomkhonto wakhe.",
			"24": "<sup>24</sup> Lezo zinto wazenza uBenaya indodana kaJehoyada, waba negama phakathi kwalawo maqhawe amathathu.",
			"25": "<sup>25</sup> Bheka, wayetusekile kunabangamashumi amathathu, kepha akafinyelelanga kwabathathu bokuqala; uDavide wambeka phezu kwabalindi bakhe.",
			"26": "<sup>26</sup> Amaqhawe anamandla nanka: u-Asaheli umfowabo kaJowabe, no-Elihanani indodana kaDodo waseBetlehema,",
			"27": "<sup>27</sup> noShamoti waseHarori, noHelesi wasePheloni,",
			"28": "<sup>28</sup> no-Ira indodana ka-Ikheshi waseThekhowa, no-Abiyezeri wase-Anathoti,",
			"29": "<sup>29</sup> noSibekayi umHushati, no-Ilayi umAhohi,",
			"30": "<sup>30</sup> noMaharayi waseNethofa, noHeledi indodana kaBahana waseNethofa,",
			"31": "<sup>31</sup> no-Ithayi indodana kaRibayi waseGibeya labantwana bakwaBenjamini, noBenaya wasePirathoni,",
			"32": "<sup>32</sup> noHurayi wangasemifudlaneni yakwaGahashi, no-Abiyeli wase-Araba,",
			"33": "<sup>33</sup> no-Azimaveti waseBaharumi, no-Eliyaba waseShahaliboni,",
			"34": "<sup>34</sup> namadodana kaHashemi waseGizoni, noJonathani indodana kaShage waseHarari,",
			"35": "<sup>35</sup> no-Ahiyamu indodana kaSakri waseHarari, no-Elifali indodana ka-Uri,",
			"36": "<sup>36</sup> noHeferi waseMekeri, no-Ahiya wasePheloni,",
			"37": "<sup>37</sup> noHesiro waseKarmeli, noNaharayi indodana ka-Ezibayi,",
			"38": "<sup>38</sup> noJoweli umfowabo kaNathani, noMibihari indodana kaHagiri,",
			"39": "<sup>39</sup> noSeleki wakwa-Amoni, noNaharayi waseBeroti, udibi lukaJowabe indodana kaSeruya,",
			"40": "<sup>40</sup> no-Ira waseJetheri, noGarebi waseJetheri,",
			"41": "<sup>41</sup> no-Uriya umHeti, noZabadi indodana ka-Ahilayi,",
			"42": "<sup>42</sup> no-Adina indodana kaShiza wakwaRubeni, inhloko yakwaRubeni, enabangamashumi amathathu,",
			"43": "<sup>43</sup> noHanani indodana kaMahakha, noJeshoshafati umMiteni,",
			"44": "<sup>44</sup> no-Uziya wase-Ashitaroti, noShama noJehiyeli amadodana kaHothamu wase-Aroweri,",
			"45": "<sup>45</sup> noJediyayeli indodana kaShimri, noJoha umfowabo umThisi,",
			"46": "<sup>46</sup> no-Eliyeli umMahavi, noJeribayi noJoshaviya amadodana ka-Elinahama, no-Itima wakwaMowabi,",
			"47": "<sup>47</sup> no-Eliyeli, no-Obede, noJahashiyeli umMesoba."
		},
		"12": {
			"1": "<sup>1</sup> Nampa abafika kuDavide eSikilagi esagogobele ebusweni bukaSawule indodana kaKishi; babe phakathi kwamaqhawe, abasizi bakhe empini.",
			"2": "<sup>2</sup> Babehlomile iminsalo, besebenzisa isandla sokunene nesokhohlo ekuphonseni amatshe nasekucibisheleni imicibisholo ngomnsalo; ababe kubafowabo bakaSawule kwaBenjamini kwakuyilaba:",
			"3": "<sup>3</sup> inhloko u-Ahiyezeri noJowashi amadodana kaShemaha waseGibeya, noJeziyeli noPheleti amadodana ka-Azimaveti, noBerakha, noJehu wase-Anathoti,",
			"4": "<sup>4</sup> no-Ishmaya waseGibeyoni, iqhawe phakathi kwabangamashumi amathathu, ebaphethe abangamashumi amathathu, noJeremiya, noJahaziyeli, noJohanani, noJozabadi waseGedera,",
			"5": "<sup>5</sup> no-Eluzayi, noJerimoti, noBehaliya, noShemariya, noShefathiya umHarufi,",
			"6": "<sup>6</sup> no-Elkana, noJishiya, no-Azareli, noJohezeri, noJashobehamu, amaKora,",
			"7": "<sup>7</sup> noJowela noZebadiya, amadodana kaJerohamu waseGedori.",
			"8": "<sup>8</sup> KwabakwaGadi abanye bazahlukanisa baya kuDavide enqabeni ehlane, amaqhawe anamandla, abantu abazilungiselele ukulwa, abakwazi ukuphatha isihlangu nomkhonto, ababuso babo bafana nobuso bezingonyama, abanejubane njengezinsephe ezintabeni:",
			"9": "<sup>9</sup> ngu-Ezeri inhloko, no-Obadiya owesibili, no-Eliyabe owesithathu,",
			"10": "<sup>10</sup> noMishimana owesine, noJeremiya owesihlanu,",
			"11": "<sup>11</sup> no-Athayi owesithupha, no-Eliyeli owesikhombisa,",
			"12": "<sup>12</sup> noJohanani owesishiyagalombili, no-Elizabadi owesishiyagalolunye,",
			"13": "<sup>13</sup> noJeremiya oweshumi, noMakibanayi oweshumi nanye.",
			"14": "<sup>14</sup> Lawo madodana kaGadi ayeyizinhloko zempi; encinyane kuwo yayilingana nekhulu, yalingana nenkulungwane.",
			"15": "<sup>15</sup> Yilabo ababewela iJordani ngenyanga yokuqala, ligcwele liphuphuma zonke izingu zalo; baxosha bonke abasezigodini ngasempumalanga nangasentshonalanga.",
			"16": "<sup>16</sup> Kwafika nabantwana bakwaBenjamini noJuda enqabeni kuDavide.",
			"17": "<sup>17</sup> UDavide waphuma ukubahlangabeza, wathatha, wathi kubo: “Uma nize nokuthula kimi ukungisiza, inhliziyo yami iyakuba nye nani, kepha uma kungukungikhohlisa ezitheni zami, lokhu kungekho okubi ezandleni zami, uNkulunkulu wawobaba makakubheke, akujezise.”",
			"18": "<sup>18</sup> Kwase kwehla umoya phezu kuka-Amasayi owayeyinhloko yabangamashumi amathathu, wathi: “Singabakho thina, Davide, sinawe, wena ndodana kaJese; ukuthula, ukuthula makube nawe, ukuthula makube nabakusizayo, ngokuba uNkulunkulu wakho uyakusiza.” Wayesebamukela uDavide, wabenza izinhloko zeviyo.",
			"19": "<sup>19</sup> Kwafika abanye kwabakwaManase kuDavide, lapho efika namaFilisti ukulwa noSawule, kepha abawasizanga, ngokuba izinduna zamaFilisti sezilulekene zammukisa, zathi: “Uyakuhlubukela enkosini yakhe uSawule, amakhanda ethu abe sengozini.”",
			"20": "<sup>20</sup> Ekuyeni kwakhe eSikilagi kwahlubukela kuye kwabakwaManase o-Adina, noJozabadi, noJediyayeli, noMikayeli, noJozabadi, no-Elihu, noSilethayi, izinhloko zezinkulungwane zabakwaManase.",
			"21": "<sup>21</sup> Bamsiza uDavide eviyweni, ngokuba babe ngamaqhawe anamandla bonke, beyizinduna zempi.",
			"22": "<sup>22</sup> Ngokuba imihla ngemihla bafika kuDavide ukumsiza, kwaze kwaba yimpi enkulu njengempi kaNkulunkulu.",
			"23": "<sup>23</sup> Nazi izibalo zezinhloko zababehlomele ukulwa abeza kuDavide eHebroni ukuba baguqulele umbuso kaSawule kuye njengezwi likaJehova.",
			"24": "<sup>24</sup> Abantwana bakwaJuda ababephethe izihlangu nemikhonto babeyizinkulungwane eziyisithupha namakhulu ayisishiyagalombili, behlomele ukulwa.",
			"25": "<sup>25</sup> Kubantwana bakwaSimeyoni amaqhawe anamandla okulwa yizinkulungwane eziyisikhombisa nekhulu.",
			"26": "<sup>26</sup> Kubantwana bakwaLevi yizinkulungwane ezine namakhulu ayisithupha.",
			"27": "<sup>27</sup> UJehoyada wayengumholi wakwa-Aroni, enezinkulungwane ezintathu namakhulu ayisikhombisa,",
			"28": "<sup>28</sup> noSadoki insizwa eyiqhawe elinamandla, nasendlini kayise izinduna ezingamashumi amabili nambili.",
			"29": "<sup>29</sup> Kubantwana bakwaBenjamini, abafowabo bakaSawule, yizinkulungwane ezintathu, ngokuba kuze kube manje imvama yabo yayithembekile endlini kaSawule.",
			"30": "<sup>30</sup> Kubantwana bakwa-Efrayimi yizinkulungwane ezingamashumi amabili namakhulu ayisishiyagalombili, amaqhawe anamandla, abantu abanegama ezindlini zawoyise.",
			"31": "<sup>31</sup> Kuyo inxenye yesizwe sakwaManase yizinkulungwane eziyishumi nesishiyagalombili, ezabizwa ngamagama ukuba zize zimbeke uDavide inkosi.",
			"32": "<sup>32</sup> Kubantwana bakwa-Isakare ngabazi ukuqonda izikhathi ukuba bazi okufanele ukwenziwa ngu-Israyeli; izinhloko zabo zazingamakhulu amabili; bonke abafowabo balalela izwi labo.",
			"33": "<sup>33</sup> KuZebuloni abangaphuma impi, bayihlele impi benezikhali zonke zokulwa, yizinkulungwane ezingamashumi ayisihlanu, bezihlela nganhliziyonye.",
			"34": "<sup>34</sup> KuNafetali yizinduna eziyinkulungwane, zinezinkulungwane ezingamashumi amathathu nesikhombisa eziphethe isihlangu nomkhonto.",
			"35": "<sup>35</sup> KwabakwaDani abangahlela impi yizinkulungwane ezingamashumi amabili nesishiyagalombili namakhulu ayisithupha.",
			"36": "<sup>36</sup> Ku-Asheri abangaphuma impi, bayihlele impi, yizinkulungwane ezingamashumi amane.",
			"37": "<sup>37</sup> Ngaphesheya kweJordani kwabakwaGadi nasenxenyeni yesizwe sakwaManase, benezikhali zonke zempi zokulwa, yizinkulungwane eziyikhulu namashumi amabili.",
			"38": "<sup>38</sup> Onke lawo madoda empi, angahlela impi, afika eHebroni ngezinhliziyo ezipheleleyo ukubeka uDavide inkosi kwa-Israyeli; nabo bonke abaseleyo bakwa-Israyeli babenhliziyonye ukumbeka uDavide inkosi.",
			"39": "<sup>39</sup> Babe lapho noDavide izinsuku ezintathu, bedla, bephuza, ngokuba abafowabo babebalungisele.",
			"40": "<sup>40</sup> Ababe seduze nabo kuze kube kwa-Isakare, nakwaZebuloni, nakwaNafetali baletha isinkwa ngezimbongolo, nangamakamela, nangeminyuzi,* nangezinkabi, nokudla kwempuphu, nezigaxa zamakhiwane, nezigaxa zezithelo zomvini ezomileyo, newayini, namafutha, nezinkabi, nezimvu eziningi, ngokuba kwakukhona ukuthokoza kwa-Israyeli."
		},
		"13": {
			"1": "<sup>1</sup> UDavide welulekana nezinduna zezinkulungwane nezamakhulu, nabaholi bonke.",
			"2": "<sup>2</sup> UDavide wathi kulo lonke ibandla lakwa-Israyeli: “Uma kukuhle kini, uma kuvela kuJehova uNkulunkulu wethu, masihlakazeke sithumele kubafowethu abasele ezweni lonke lakwa-Israyeli kanye nabapristi namaLevi emizini yabo enamadlelo ukuba babuthane kithi,",
			"3": "<sup>3</sup> sibuyisele kithi umphongolo kaNkulunkulu wethu, ngokuba asiwufunanga ezinsukwini zikaSawule.”",
			"4": "<sup>4</sup> Lonke ibandla lathi liyakwenze njalo, ngokuba leyo nto yayilungile emehlweni abo bonke abantu.",
			"5": "<sup>5</sup> UDavide wayesebutha wonke u-Israyeli kusukela eSihori laseGibithe kuze kube sekuyeni eHamati ukususa umphongolo kaNkulunkulu eKiriyati Jeharimi.",
			"6": "<sup>6</sup> UDavide wenyukela kanye naye wonke u-Israyeli eBahala, okungukuthi iKiriyati Jeharimi elalingelakwaJuda, ukukhuphula khona umphongolo kaNkulunkulu uJehova ohlezi phezu kwamakherubi,* owawubizwa ngegama lakhe.",
			"7": "<sup>7</sup> Bawuhambisa umphongolo kaNkulunkulu ngengqukumbane entsha, bewukhipha endlini ka-Abinadaba; o-Uza no-Ahiyo bashayela ingqukumbane.",
			"8": "<sup>8</sup> UDavide naye wonke u-Israyeli bathokoza ebusweni bukaNkulunkulu ngamandla onke ngamahubo, nangamahabhu,* nangezingubhu, nangezigubhu, nangamasimbali,* nangamacilongo.",
			"9": "<sup>9</sup> Ekufikeni kwabo esibuyeni sikaKidoni u-Uza welula isandla sakhe ukubamba umphongolo, ngokuba izinkabi zashelela.",
			"10": "<sup>10</sup> Intukuthelo kaJehova yamvuthela u-Uza, wamshaya, ngokuba welulela isandla emphongolweni; wafela khona lapho phambi kukaNkulunkulu.",
			"11": "<sup>11</sup> UDavide wadabuka ngokuba uJehova emhlasele u-Uza, wayibiza leyo ndawo ngokuthi iPheresi-Uza* kuze kube namuhla.",
			"12": "<sup>12</sup> UDavide wamesaba uNkulunkulu ngalolo suku; wathi: “Ngizakuletha kanjani umphongolo kaNkulunkulu kimi na?”",
			"13": "<sup>13</sup> UDavide akawuyisanga umphongolo kuye emzini kaDavide, kepha wawubuyisela eceleni endlini ka-Obede Edomi umGiti.",
			"14": "<sup>14</sup> Umphongolo kaNkulunkulu wahlala nabendlu ka-Obede Edomi endlini yakhe izinyanga ezintathu; uJehova wayibusisa indlu ka-Obede Edomi nakho konke anakho."
		},
		"14": {
			"1": "<sup>1</sup> UHiramu inkosi yaseTire wathuma izithunywa kuDavide, nemisedari,* nabakhi, nababazi ukuba bamakhele indlu.",
			"2": "<sup>2</sup> UDavide waqonda ukuthi uJehova wayemmisile impela ukuba abe yinkosi kwa-Israyeli, ngokuba umbuso wakhe waphakanyiselwa phezulu ngenxa yabantu bakhe u-Israyeli.",
			"3": "<sup>3</sup> UDavide wathatha abanye abafazi eJerusalema, uDavide wazala amanye amadodana namadodakazi.",
			"4": "<sup>4</sup> Nanka amagama abantwana ayenabo eJerusalema: oShamuvwa, noShobabi, noNathani, noSolomoni,",
			"5": "<sup>5</sup> noJibari, no-Elishuwa, no-Elipheleti,",
			"6": "<sup>6</sup> noNoga, noNefegi, noJafiya,",
			"7": "<sup>7</sup> no-Elishama, noBeyeliyada, no-Elifeleti.",
			"8": "<sup>8</sup> AmaFilisti esezwile ukuthi uDavide ugcotshiwe ukuba abe yinkosi kwa-Israyeli wonke, onke amaFilisti enyuka ukumfuna uDavide; uDavide wakuzwa, waphuma ukuwahlasela.",
			"9": "<sup>9</sup> AmaFilisti esefikile ukungenela isiGodi samaRefa,",
			"10": "<sup>10</sup> uDavide wabuza kuNkulunkulu, wathi: “Ngenyukele ukuhlasela amaFilisti na? Uyakuwanikela esandleni sami na?” UJehova wathi kuye: “Yenyuka, ngokuba ngiyakuwanikela esandleni sakho.”",
			"11": "<sup>11</sup> Base benyukela eBali Pherasimi, uDavide wawachitha; uDavide wathi: “UNkulunkulu ufohlele izitha zami ngesandla sami njengokufohla kwamanzi.” Ngalokho baqamba igama laleyo ndawo ngokuthi iBali Pherasimi.*",
			"12": "<sup>12</sup> Ashiya khona onkulunkulu bawo; uDavide wathi mabashiswe ngomlilo.",
			"13": "<sup>13</sup> AmaFilisti abuye angenela isigodi.",
			"14": "<sup>14</sup> UDavide waphinda wabuza kuNkulunkulu; uNkulunkulu wathi kuye: “Ungenyuki emva kwawo; wagwegwesele, uwafikele malungana namajikijolo.",
			"15": "<sup>15</sup> Kuyakuthi lapho usuzwa umsindo wokuhamba ezihlokweni zamajikijolo, uphume impi, ngokuba uNkulunkulu uphumile ngaphambi kwakho ukuchitha impi yamaFilisti.”",
			"16": "<sup>16</sup> UDavide wenza njengokumyala kukaNkulunkulu, bachitha impi yamaFilisti kusukela eGibeyoni kuze kube seGezeri.",
			"17": "<sup>17</sup> Igama likaDavide laphumela emazweni onke; uJehova wehlisela izizwe zonke ingebhe ngaye."
		},
		"15": {
			"1": "<sup>1</sup> Wazakhela izindlu emzini kaDavide; walungisela umphongolo kaNkulunkulu indawo, wawumisela itende.",
			"2": "<sup>2</sup> Khona uDavide wathi: “Umphongolo kaNkulunkulu nga ungathwalwa muntu, ngamaLevi kuphela, ngokuba yiwona akhethwe nguJehova ukuthwala umphongolo kaNkulunkulu nokumkhonza kuze kube phakade.”",
			"3": "<sup>3</sup> UDavide wabuthela wonke u-Israyeli eJerusalema ukukhuphulela umphongolo kaJehova endaweni yawo awulungisele yona.",
			"4": "<sup>4</sup> UDavide wabutha amadodana ka-Aroni namaLevi;",
			"5": "<sup>5</sup> kumadodana kaKohati: ngu-Uriyeli inhloko nabafowabo, beyikhulu namashumi amabili;",
			"6": "<sup>6</sup> kumadodana kaMerari: ngu-Asaya inhloko nabafowabo, bengamakhulu amabili namashumi amabili;",
			"7": "<sup>7</sup> kumadodana kaGereshomu: nguJoweli inhloko nabafowabo, beyikhulu namashumi amathathu;",
			"8": "<sup>8</sup> kumadodana ka-Elisafani: nguShemaya inhloko nabafowabo, bengamakhulu amabili;",
			"9": "<sup>9</sup> kumadodana kaHebroni: ngu-Eliyeli inhloko nabafowabo, bengamashumi ayisihiyagalombili;",
			"10": "<sup>10</sup> kumadodana ka-Uziyeli: ngu-Aminadaba inhloko nabafowabo, beyikhulu neshumi nambili.",
			"11": "<sup>11</sup> UDavide wabiza oSadoki no-Abiyathara abapristi, namaLevi o-Uriyeli, no-Asaya, noJoweli, noShemaya, no-Eliyeli, no-Aminadaba, wathi kubo:",
			"12": "<sup>12</sup> “Nina niyizinhloko zezindlu zawoyise bamaLevi; zingcweliseni nina nabafowenu ukuba niwukhuphulele umphongolo kaJehova uNkulunkulu ka-Israyeli endaweni engiyilungisele wona.",
			"13": "<sup>13</sup> Ngokuba ngenxa yokungawuthwali kwenu kuqala uJehova uNkulunkulu wethu wafohla kithina, ngokuba singamfunanga njengomthetho.”",
			"14": "<sup>14</sup> Base bezingcwelisa abapristi namaLevi ukuba bawukhuphule umphongolo kaJehova uNkulunkulu ka-Israyeli.",
			"15": "<sup>15</sup> Abantwana bamaLevi bawuthwala umphongolo kaNkulunkulu emahlombe abo ngezinti zawo njengokuyala kukaMose ngezwi likaJehova.",
			"16": "<sup>16</sup> UDavide washo ezinduneni zamaLevi ukuba zibeke abafowabo abahlabeleli, benezinto zokubetha, izingubhu, namahabhu,* namasimbali,* bakubethe, baphakamise izwi labo ngokuthokoza.",
			"17": "<sup>17</sup> AmaLevi ayesebeka uHemani indodana kaJoweli, nakubafowabo u-Asafa indodana kaBerekiya, nakumadodana kaMerari, abafowabo, u-Ethani indodana kaKushaya,",
			"18": "<sup>18</sup> bekanye nabo abafowabo besigaba sesibili: oZakariya, noBeni, noJahaziyeli, noShemiramoti, noJehiyeli, no-Uni, no-Eliyabe, noBenaya, noMahaseya, noMatithiya, no-Elifelehu, noMikineya, no-Obede Edomi, noJehiyeli, abalindisango,",
			"19": "<sup>19</sup> nabahlabeleli oHemani no-Asafa no-Ethani ukuba bakhalise amasimbali* ethusi,",
			"20": "<sup>20</sup> noZakariya, no-Aziyeli, noShemiramoti, noJehiyeli, no-Uni, no-Eliyabe, noMahaseya, noBenaya ukuba babe nezingubhu ezilungiselwe i-Alamoti,*",
			"21": "<sup>21</sup> noMatithiya, no-Elifelehu, noMikineya, no-Obede Edomi, noJehiyeli, no-Azaziya ukuba babe namahabhu* alungiselwe iSheminiti* ukuba bahole.",
			"22": "<sup>22</sup> UKhenaniya induna yamaLevi wayephethe umsebenzi wokuthwala; wayefundisa ukuthwala ngokuba wayekwazi.",
			"23": "<sup>23</sup> OBerekiya no-Elkana babe ngabalindisango bomphongolo.",
			"24": "<sup>24</sup> OShebaniya, noJehoshafati, noNethaneli, no-Amasayi, noZakariya, noBenaya, no-Eliyezeri, abapristi, bashaya amacilongo phambi komphongolo kaNkulunkulu; o-Obede Edomi noJehiya babe ngabalindisango bomphongolo.",
			"25": "<sup>25</sup> UDavide namalunga akwa-Israyeli nezinduna zezinkulungwane baya ukuwukhuphula umphongolo wesivumelwano sikaJehova endlini ka-Obede Edomi ngokuthokoza;",
			"26": "<sup>26</sup> kwathi lapho uNkulunkulu esesizile amaLevi awuthwala umphongolo wesivumelwano sikaJehova, anikela ngezinkunzi eziyisikhombisa nangezinqama eziyisikhombisa.",
			"27": "<sup>27</sup> UDavide wayembethe ingubo yelineni elicolekileyo kanye nawo onke amaLevi ayethwala umphongolo, nabahlabeleli, noKhenaniya induna yokuthwala kwabahlabeleli; uDavide wayenengubo yamahlombe yelineni.",
			"28": "<sup>28</sup> Wonke u-Israyeli wawukhuphula kanjalo umphongolo wesivumelwano sikaJehova ngokukhamuluka nangezwi lomtshingo, nangamacilongo, nangamasimbali,* ebetha izingubhu namahabhu.*",
			"29": "<sup>29</sup> Kwathi lapho umphongolo wesivumelwano sikaJehova usungena emzini kaDavide, uMikhali indodakazi kaSawule walunguza efasiteleni, wabona inkosi uDavide isina, idlala; wayidelela enhliziyweni yakhe."
		},
		"16": {
			"1": "<sup>1</sup> Bawungenisa umphongolo kaNkulunkulu, bawubeka phakathi netende uDavide ayelimisele wona; banikela ngeminikelo yokushiswa nangeminikelo yokuthula phambi kukaNkulunkulu.",
			"2": "<sup>2</sup> UDavide eseqedile ukunikela ngomnikelo wokushiswa nangeminikelo yokuthula wababusisa abantu egameni likaJehova.",
			"3": "<sup>3</sup> Wabela bonke abantu bakwa-Israyeli, abesilisa nabesifazane, isinkwa saba sinye, nesicubu senyama, nesigaxa sezithelo zomvini ezomileyo.",
			"4": "<sup>4</sup> Wabela amaLevi athile ukukhonza phambi komphongolo kaJehova ukuba amkhumbule, ambonge, amdumise uJehova uNkulunkulu ka-Israyeli:",
			"5": "<sup>5</sup> ngu-Asafa inhloko nabesigaba sakhe sesibili, oZakariya, noJehiyeli, noShemiramoti, noJehiyeli, noMatithiya, no-Eliyabe, noBenaya, no-Obede Edomi, noJehiyeli, benezingubhu namahabhu,* kanye no-Asafa ephethe amasimbali,* ewabetha,",
			"6": "<sup>6</sup> noBenaya noJahaziyeli abapristi, benamacilongo njalo phambi komphongolo wesivumelwano sikaNkulunkulu.",
			"7": "<sup>7</sup> Ngalolo suku uDavide wayeseqala ukuhlela ukuba uJehova abongwe ngesandla sika-Asafa nabafowabo.",
			"8": "<sup>8</sup> Bongani uJehova, nibize igama lakhe, nizazise phakathi kwabantu izenzo zakhe.",
			"9": "<sup>9</sup> Hlabelelani kuye, nimhubele, nikhulume ngezimangaliso zakhe zonke.",
			"10": "<sup>10</sup> Zibongeni egameni lakhe elingcwele; mazithokoze izinhliziyo zabamfuna uJehova.",
			"11": "<sup>11</sup> Funani uJehova namandla akhe, nifunisise ubuso bakhe njalo.",
			"12": "<sup>12</sup> Khumbulani izimangaliso zakhe azenzileyo, nezibonakaliso zakhe, nezahlulelo zomlomo wakhe,",
			"13": "<sup>13</sup> nina nzalo ka-Israyeli, inceku yakhe, nina bantwana bakaJakobe, abakhethiweyo bakhe.",
			"14": "<sup>14</sup> Yena unguJehova uNkulunkulu wethu; izahlulelo zakhe zisemhlabeni wonke.",
			"15": "<sup>15</sup> Khumbulani isivumelwano sakhe kuze kube phakade, izwi ayala ngalo izizukulwane eziyinkulungwane,",
			"16": "<sup>16</sup> avumelana kuso no-Abrahama, nesifungo sakhe ku-Isaka;",
			"17": "<sup>17</sup> wasiqinisa kuJakobe, saba ngumthetho, naku-Israyeli, saba yisivumelwano esiphakade,",
			"18": "<sup>18</sup> ethi: “Ngizakukunika izwe laseKhanani libe yisabelo sefa lakho,",
			"19": "<sup>19</sup> lapho naniyingcosana ngomumo, yebo, lapho naniyingcosana, futhi naniyizihambi kulo.”",
			"20": "<sup>20</sup> Bazulazula ezizweni ngezizwe, besuka kulo mbuso, beya kwabanye abantu.",
			"21": "<sup>21</sup> Akavumelanga muntu ukubahlupha, wajezisa amakhosi ngenxa yabo,",
			"22": "<sup>22</sup> ethi: “Ningathinti abagcotshiweyo bami, ningoni abaprofethi bami.”",
			"23": "<sup>23</sup> Hlabelelani kuJehova, mhlaba wonke, nimemeze insindiso yakhe imihla ngemihla.",
			"24": "<sup>24</sup> Landani inkazimulo yakhe phakathi kwabezizwe, izimangaliso zakhe phakathi kwabantu bonke.",
			"25": "<sup>25</sup> Ngokuba mkhulu uJehova, udumisekile kakhulu; uyesabeka phezu kwabo bonke onkulunkulu.",
			"26": "<sup>26</sup> Ngokuba bonke onkulunkulu babezizwe bayizithombe, kepha uJehova walenza izulu.",
			"27": "<sup>27</sup> Udumo nobukhosi kusebusweni bakhe; amandla nokuthokoza kusendaweni yakhe.",
			"28": "<sup>28</sup> Mnikeni uJehova nina mindeni yabantu, mnikeni uJehova inkazimulo namandla.",
			"29": "<sup>29</sup> Mnikeni uJehova inkazimulo yegama lakhe, nilethe umnikelo, nize phambi kwakhe, nikhuleke kuJehova ngobuhle bobungcwele.",
			"30": "<sup>30</sup> Thuthumelani ebusweni bakhe, mhlaba wonke; izwe lihlala liqinile, alinakushukunyiswa.",
			"31": "<sup>31</sup> Malijabule izulu, umhlaba uthokoze; makuthiwe ezizweni: UJehova uyinkosi.",
			"32": "<sup>32</sup> Maluhlokome ulwandle nokugcwala kwalo, amasimu ename nakho konke okukuwo.",
			"33": "<sup>33</sup> Khona imithi yehlathi iyakuhuba ngokujabula ebusweni bukaJehova, ngokuba uyeza ukwahlulela umhlaba.",
			"34": "<sup>34</sup> Bongani uJehova, ngokuba muhle, ngokuba umusa wakhe umi phakade.",
			"35": "<sup>35</sup> Thanini: “Sisindise, Nkulunkulu wokusindiswa kwethu, usibuthe, usophule kwabezizwe ukuba sidumise igama lakho elingcwele, sethabe ekukudumiseni.”",
			"36": "<sup>36</sup> Makabongwe uJehova uNkulunkulu ka-Israyeli, kusukela phakade kuze kube phakade. Bonke abantu bathi: “Amen,” bamdumisa uJehova.",
			"37": "<sup>37</sup> Wayeseshiya khona ngaphambi komphongolo wesivumelwano sikaJehova u-Asafa nabafowabo ukuba bakhonze phambi komphongolo njalo ngokomsebenzi wemihla ngemihla,",
			"38": "<sup>38</sup> kanye no-Obede Edomi nabafowabo abangamashumi ayisithupha nesishiyagalombili u-Obede Edomi indodana kaJeduthuni noHosa babe ngabalindisango",
			"39": "<sup>39</sup> noSadoki umpristi nabafowabo abapristi ngaphambi kwetabernakele* likaJehova endaweni ephakemeyo eyayiseGibeyoni",
			"40": "<sup>40</sup> ukuba banikele njalo ngeminikelo yokushiswa phezu kwe-altare lomnikelo wokushiswa ekuseni nakusihlwa njengakho konke okulotshiweyo emthethweni kaJehova ayala ngawo u-Israyeli,",
			"41": "<sup>41</sup> bekanye nabo oHemani noJeduthuni nensali yabakhethiweyo abakhonjwa ngamagama ukuba bambonge uJehova, ngokuba umusa wakhe umi phakade;",
			"42": "<sup>42</sup> kwakukhona kubo, oHemani noJeduthuni, amacilongo namasimbali* alabo abayakuwakhalisa, nezinto zokubethwa kuNkulunkulu; amadodana kaJeduthuni ayengasesangweni.",
			"43": "<sup>43</sup> Base behamba baya, kwaba yilowo nalowo endlini yakhe; uDavide wabuya ukubusisa indlu yakhe."
		},
		"17": {
			"1": "<sup>1</sup> Kwathi lapho uDavide esehlezi endlini yakhe, uDavide wathi kuNathani umprofethi: “Bheka, mina ngihlala endlini yemisedari,* kepha umphongolo wesivumelwano sikaJehova uphansi kwezilenge.”",
			"2": "<sup>2</sup> UNathani wathi kuDavide: “Yenza konke okusenhliziyweni yakho, ngokuba uNkulunkulu unawe.”",
			"3": "<sup>3</sup> Kepha kwathi ngalobo busuku izwi likaNkulunkulu lafika kuNathani, lathi:",
			"4": "<sup>4</sup> “Hamba utshele uDavide inceku yami ukuthi: ‘Usho kanje uJehova, uthi: Awuyikungakhela indlu ukuba ngihlale kuyo,",
			"5": "<sup>5</sup> ngokuba angihlalanga ndlu kusukela osukwini engakhuphula ngalo u-Israyeli kuze kube namuhla, kepha ngangisematendeni, nasematabernakele* ngamatabernakele.",
			"6": "<sup>6</sup> Ezindaweni zonke engihambahambe kuzo naye wonke u-Israyeli ngake ngakhuluma yini izwi nakoyedwa wabahluleli bakwa-Israyeli engabayala ukuba baluse abantu bami, ngathi: “Aningakhelanga ngani indlu yemisedari* na?” ’",
			"7": "<sup>7</sup> “Ngalokho uyakusho kanje encekwini yami uDavide, uthi: ‘Usho kanje uJehova Sebawoti, uthi: Mina ngakuthatha edlelweni ekwaluseni izimvu ukuba ube ngumbusi wabantu bami u-Israyeli;",
			"8": "<sup>8</sup> nganginawe nomaphi lapho wawuya khona, nganquma zonke izitha zakho ngaphambi kwakho; ngiyakukwenza igama njengamagama abakhulu abasemhlabeni.",
			"9": "<sup>9</sup> Ngiyakumisela abantu bami u-Israyeli indawo, ngibatshale ukuba bahlale endaweni yabo, bangabe besanyakaziswa; futhi abantu ababi abasayikubachitha njengakuqala,",
			"10": "<sup>10</sup> kusukela emihleni engayaleza ngayo abahluleli ukuba babe phezu kwabantu bami u-Israyeli; ngiyakunqoba zonke izitha zakho. Futhi ngiyakutshela ukuthi uJehova uyakukwakhela indlu.",
			"11": "<sup>11</sup> Kuyakuthi lapho izinsuku zakho seziphelele ukuba uye koyihlo, ngiyakumisa inzalo yakho emva kwakho, eyakuba ngeyamadodana akho; ngiyakuqinisa umbuso wayo.",
			"12": "<sup>12</sup> Yiyo eyakungakhela indlu; ngiyakuqinisa isihlalo sayo sobukhosi kuze kube phakade.",
			"13": "<sup>13</sup> Mina ngiyakuba nguyise, yona ibe yindodana yami; angiyikuyaphuca umusa wami, njengalokho ngamaphuca owayephambi kwakho.",
			"14": "<sup>14</sup> Ngiyakuyiqinisa endlini yami nasembusweni wami kuze kube phakade, isihlalo sayo sobukhosi siqiniswe kuze kube phakade.’ ”",
			"15": "<sup>15</sup> Njengawo onke lawo mazwi nanjengawo wonke lowo mbono uNathani wakhuluma kuDavide.",
			"16": "<sup>16</sup> UDavide inkosi wayesengena, wahlala phambi kukaJehova, wathi: “Ngingubani, Jehova Nkulunkulu, nendlu yami iyini ukuba ungilethe kuze kube lapha na?",
			"17": "<sup>17</sup> Lokho kwakuyinto encane emehlweni akho, Nkulunkulu; usukhulumile ngendlu yenceku yakho isekude, wangibuka kungathi ngingumuntu ophakemeyo, Jehova Nkulunkulu.",
			"18": "<sup>18</sup> “UDavide angenezela ngokuthini kuwe ngodumo inceku yakho elwenzelweyo na? Wena uyayazi inceku yakho.",
			"19": "<sup>19</sup> Jehova, ngenxa yenceku yakho nanjengokwenhliziyo yakho wenzile konke lokho okukhulu ukuba kwaziswe bonke lobu bukhulu.",
			"20": "<sup>20</sup> “Jehova, akakho onjengawe, akukho Nkulunkulu ngaphandle kwakho njengakho konke esikuzwileyo ngezindlebe zethu.",
			"21": "<sup>21</sup> Yisiphi isizwe emhlabeni esinjengabantu bakho u-Israyeli, uNkulunkulu aya ukuzihlengela bona babe ngabantu bakhe, ukukwenzela igama ngezinto ezinkulu nezesabekayo ngokuxosha abezizwe ebusweni babantu bakho owabahlenga eGibithe, na?",
			"22": "<sup>22</sup> Ngokuba abantu bakho u-Israyeli wabenza babe ngabantu bakho kuze kube phakade, wena Jehova ube nguNkulunkulu wabo.",
			"23": "<sup>23</sup> “Manje, Jehova, izwi olikhulumile ngenceku yakho nangendlu yayo kuze kube phakade maliqiniswe, wenze njengokukhuluma kwakho.",
			"24": "<sup>24</sup> Maliqiniseke, libe likhulu igama lakho kuze kube phakade, ukuze kuthiwe: ‘UJehova Sebawoti unguNkulunkulu ka-Israyeli, yebo, uNkulunkulu ka-Israyeli,’ nendlu kaDavide inceku yakho iqinisiwe phambi kwakho.",
			"25": "<sup>25</sup> “Ngokuba wena Nkulunkulu wami wambulele endlebeni yenceku yakho ukuthi uyakuyakhela indlu; ngalokho inceku yakho ifike kulokhu ukuba ikhuleke phambi kwakho.",
			"26": "<sup>26</sup> Manje, Jehova, wena unguNkulunkulu, ukhulumile lokho okuhle encekwini yakho;",
			"27": "<sup>27</sup> kalokhu mawuvume ukubusisa indlu yenceku yakho ukuba ibe sebusweni bakho kuze kube phakade, ngokuba okubusisile wena Jehova kuyahlala kubusisekile kuze kube phakade.”"
		},
		"18": {
			"1": "<sup>1</sup> Kwathi emva kwalokho uDavide wawashaya amaFilisti, wawahlula, walithatha iGati nemizana yalo esandleni samaFilisti.",
			"2": "<sup>2</sup> Wanqoba noMowabi; abakwaMowabi baba yizinceku zikaDavide, bathela intela.",
			"3": "<sup>3</sup> UDavide washaya noHadarezeri, inkosi yaseSoba ngaseHamati, ekuhambeni kwakhe ukuqinisa isandla sakhe ngasemfuleni u-Ewufrathe.",
			"4": "<sup>4</sup> UDavide wamaphuca izinqola eziyinkulungwane, nabamahhashi abayizinkulungwane eziyisikhombisa, nemigundatshani eyizinkulungwane ezingamashumi amabili; uDavide wanquma imisipha yawo onke amahhashi ezinqola, washiya awezinqola eziyikhulu kodwa.",
			"5": "<sup>5</sup> Ama-Aramu aseDamaseku esefika ukumsiza uHadarezeri inkosi yaseSoba, uDavide wabulala kuma-Aramu abantu abayizinkulungwane ezingamashumi amabili nambili.",
			"6": "<sup>6</sup> UDavide wamisa amakhanda e-Aramu laseDamaseku; ama-Aramu aba yizinceku zikaDavide, athela intela. UJehova wamnqobisa uDavide nomaphi lapho eya khona.",
			"7": "<sup>7</sup> UDavide wathatha izihlangu zegolide ezazisezincekwini zikaHadarezeri, waziletha eJerusalema.",
			"8": "<sup>8</sup> EThibahati naseKhuni imizi kaHadarezeri, uDavide wathatha ithusi eliningi kakhulu uSolomoni enza ngalo ulwandle lwethusi, nezinsika nezitsha zethusi.",
			"9": "<sup>9</sup> Lapho uThowu inkosi yaseHamati ezwa ukuthi uDavide ushayile yonke impi kaHadarezeri inkosi yaseSoba,",
			"10": "<sup>10</sup> wathuma uHadoramu, indodana yakhe, enkosini uDavide ukuyibingelela nokuyibusisa, ngokuba yayilwile noHadarezeri, yamnqoba, ngokuba uHadarezeri wayelokhu elwa noThowu; wathuma izinhlobo zonke zezitsha zegolide nezesiliva nezethusi.",
			"11": "<sup>11</sup> Nalezo inkosi uDavide yayizingcwelisela uJehova kanye nesiliva negolide alisusa ezizweni zonke: ku-Edomi, nakuMowabi, nakubantwana bakwa-Amoni, nakumaFilisti, nakuma-Amaleki.",
			"12": "<sup>12</sup> U-Abishayi, indodana kaSeruya, wabulala kwabakwa-Edomi esiGodini sikaSawoti, izinkulungwane eziyishumi nesishiyagalombili.",
			"13": "<sup>13</sup> Wamisa amakhanda kwa-Edomi; bonke abakwa-Edomi baba yizinceku zikaDavide. UJehova wamnqobisa uDavide nomaphi lapho eya khona.",
			"14": "<sup>14</sup> UDavide wabusa phezu kuka-Israyeli wonke, waphatha ukwahlulela nokulunga kubo bonke abantu bakhe.",
			"15": "<sup>15</sup> UJowabe indodana kaSeruya wayephezu kwempi; uJehoshafati, indodana ka-Ahiludi, wayengumabhalana;",
			"16": "<sup>16</sup> uSadoki, indodana ka-Ahithubi, no-Abimeleki indodana ka-Abiyathara, babe ngabapristi; noShawusha wayengumbhali;",
			"17": "<sup>17</sup> uBenaya, indodana kaJehoyada wayephezu kwamaKhereti namaPheleti; amadodana kaDavide ayengabokuqala ngasenkosini."
		},
		"19": {
			"1": "<sup>1</sup> Kwathi emva kwalokho uNahashi inkosi yabantwana bakwa-Amoni wafa, indodana yakhe yaba yinkosi esikhundleni sakhe.",
			"2": "<sup>2</sup> UDavide wathi: “Ngiyakumenzela umusa uHanuni, indodana kaNahashi, ngokuba uyise wangenzela umusa.” UDavide wayesethuma izithunywa ukumduduza ngoyise. Zafika-ke izinceku zikaDavide ezweni labantwana bakwa-Amoni kuHanuni ukumduduza.",
			"3": "<sup>3</sup> Kepha izikhulu zabantwana bakwa-Amoni zathi kuHanuni: “Imbala ngokubona kwakho uthume abaduduzi kuwe ukuba atuse uyihlo na? Azifikanga yini izinceku zakhe kuwe ukuba ziphenye umuzi, ziwuchithe, ziwuhlole, na?”",
			"4": "<sup>4</sup> UHanuni wayesebamba izinceku zikaDavide, waziphucula, wahina izingubo zazo phakathi, kwaze kwaba sezinqeni zazo, wazimukisa.",
			"5": "<sup>5</sup> Bahamba, bamtshela uDavide ngalawo madoda. Wathuma ukuwahlangabeza, ngokuba ayenamahloni kakhulu lawo madoda. Yathi inkosi: “Hlalani eJeriko, zize zihlume izilevu zenu, bese nibuya.”",
			"6": "<sup>6</sup> Abantwana bakwa-Amoni sebebone ukuthi bazenzile ukuba banengeke kuDavide, uHanuni nabantwana bakwa-Amoni bathuma amatalenta* ayinkulungwane esiliva ukuziqashela izinqola nabamahhashi e-Aramu Naharayimi, nase-Aramu Mahakha, naseSoba.",
			"7": "<sup>7</sup> Base beziqashela izinqola eziyizinkulungwane ezingamashumi amathathu nambili kanye nenkosi yaseMahakha nabantu bayo abeza ukumisa ngaphambi kweMedeba. Abantwana bakwa-Amoni babuthana ndawonye bephuma emizini yabo; baya ukulwa.",
			"8": "<sup>8</sup> UDavide esezwile lokho wamthuma uJowabe nempi yonke yamaqhawe.",
			"9": "<sup>9</sup> Abantwana bakwa-Amoni baphuma, bahlela impi ngasesangweni lomuzi; kepha amakhosi ayefikile ayewodwa endle.",
			"10": "<sup>10</sup> UJowabe esebona ukuthi impi ibhekene naye ngaphambili nangasemuva, wakhetha kwabakhethekileyo bonke bakwa-Israyeli, wabahlela ukuba balwe nama-Aramu;",
			"11": "<sup>11</sup> abantu abaseleyo wababeka esandleni sika-Abishayi umfowabo, bazihlela ukulwa nabantwana bakwa-Amoni.",
			"12": "<sup>12</sup> Wathi: “Uma ama-Aramu engahlula, uyakungisiza; kepha uma abantwana bakwa-Amoni bekwahlula, ngiyakukusiza.",
			"13": "<sup>13</sup> Qinani, masiziqinise ngenxa yabantu bakithi nangenxa yemizi kaNkulunkulu wethu; uJehova makenze okuhle emehlweni akhe.”",
			"14": "<sup>14</sup> UJowabe nabantu ababe naye base besondela kuwo ama-Aramu ukuba balwe; kepha abaleka phambi kwakhe.",
			"15": "<sup>15</sup> Lapho abantwana bakwa-Amoni bebona ukuthi ama-Aramu abalekile, babaleka nabo phambi kuka-Abishayi umfowabo, bangena emzini; kepha uJowabe waya eJerusalema.",
			"16": "<sup>16</sup> Ama-Aramu esebonile ukuthi anqotshiwe phambi kuka-Israyeli, athuma izithunywa, akhipha ama-Aramu ayephesheya koMfula* noShofaki induna yempi kaHadarezeri phambi kwawo.",
			"17": "<sup>17</sup> UDavide watshelwa, wabutha wonke u-Israyeli, wawela iJordani, wawafikela, wahlela impi ukulwa nawo. UDavide esehlelile impi ukulwa nama-Aramu, alwa naye.",
			"18": "<sup>18</sup> Ama-Aramu abaleka phambi kuka-Israyeli; uDavide wabulala kuma-Aramu izinqola eziyizinkulungwane eziyisikhombisa nemigundatshani eyizinkulungwane ezingamashumi amane, wabulala noShofaki induna yempi.",
			"19": "<sup>19</sup> Lapho izinceku zikaHadarezeri zibonile ukuthi zinqotshiwe phambi kuka-Israyeli, zenza ukuthula noDavide, zamkhonza; ama-Aramu awathandanga ukusiza abantwana bakwa-Amoni futhi."
		},
		"20": {
			"1": "<sup>1</sup> Emva komnyaka ngesikhathi sokuhlasela kwamakhosi, uJowabe wahola amaqhawe empi, wachitha izwe labantwana bakwa-Amoni, weza wavimbezela iRaba; kepha uDavide wahlala eJerusalema. UJowabe walinqoba iRaba, walidiliza.",
			"2": "<sup>2</sup> UDavide wathatha umqhele wenkosi yabo ekhanda layo, wafumana isisindo sawo ukuthi sasiyitalenta* legolide, itshe eliyigugu likuwo; wabekwa ekhanda likaDavide; wakhipha emzini impango eningi kakhulu.",
			"3": "<sup>3</sup> Wakhipha nabantu ababe phakathi kwawo, wabanquma ngamasaha, nangezibhuqo zensimbi, nangamazembe; wenze njalo uDavide kuyo yonke imizi yabantwana bakwa-Amoni. UDavide nabo bonke abantu babuyela eJerusalema.",
			"4": "<sup>4</sup> Emva kwalokho kwavuka impi namaFilisti eGezeri; khona uSibekayi, umHushati, wabulala uSiphayi wamadodana amaRefa; anqotshwa.",
			"5": "<sup>5</sup> Kwabuye kwaba khona impi namaFilisti; u-Elihanani, indodana kaJayire, wabulala uLahimi umfowabo kaGoliyati, umGiti, oluthi lomkhonto wakhe lwalunjengogongolo lomaluki.",
			"6": "<sup>6</sup> Kwabuye kwaba khona impi eGati, lapho kwakukhona umuntu omude; iminwe yakhe nezinzwani zakhe kwakungamashumi amabili nane, isithupha esandleni sisinye, nesithupha onyaweni lulunye; naye wazalwa yiRefa.",
			"7": "<sup>7</sup> Wameyisa u-Israyeli; ngalokho uJonathani indodana kaShimeya umfowabo kaDavide wambulala.",
			"8": "<sup>8</sup> Labo bazalwa ngamaRefa eGati, bawa ngesandla sikaDavide nangesandla sezinceku zakhe."
		},
		"21": {
			"1": "<sup>1</sup> USathane wema ukulwa no-Israyeli, wavusa uDavide ukuba abale u-Israyeli.",
			"2": "<sup>2</sup> UDavide wayesethi kuJowabe nasezinduneni zabantu: “Hambani nibale u-Israyeli kusukela eBeri Sheba kuze kube kwaDani, nilethe umumo wabo kimi ukuba ngiwazi.”",
			"3": "<sup>3</sup> UJowabe wathi: “UJehova makenezele kubantu bakhe, kuphindwe kayikhulu phezu kwalokhu abayikho; kepha, nkosi yami, nkosi, abasizo izinceku zenkosi yami bonke na? Inkosi yami ifunelani le nto na? Iyakuba yicala ngani ku-Israyeli na?”",
			"4": "<sup>4</sup> Nokho izwi lenkosi lamahlula uJowabe. UJowabe waphuma, wadabula wonke u-Israyeli, wafika eJerusalema.",
			"5": "<sup>5</sup> UJowabe wanika uDavide umumo wabantu ababaliweyo; wonke u-Israyeli wayeyizinkulungwane eziyinkulungwane nezinkulungwane eziyikhulu abahloma inkemba; uJuda wayeyizinkulungwane ezingamakhulu amane namashumi ayisikhombisa.",
			"6": "<sup>6</sup> Kepha oLevi noBenjamini akababalanga phakathi kwabo, ngokuba izwi lenkosi lalinengekile kuJowabe.",
			"7": "<sup>7</sup> Kwaba kubi emehlweni kaNkulunkulu; ngalokho washaya u-Israyeli.",
			"8": "<sup>8</sup> UDavide wayesethi kuNkulunkulu: “Ngonile kakhulu ngalokho engikwenzileyo; kepha manje, Jehova, mawuthethelele ububi benceku yakho, ngokuba ngintumazele kakhulu.”",
			"9": "<sup>9</sup> UJehova wayesekhuluma kuGadi, umboni kaDavide, ethi:",
			"10": "<sup>10</sup> “Hamba ukhulume kuDavide ngokuthi: ‘UJehova usho kanje, uthi: Ngiyakubekela izinto ezintathu; khetha enye kuzo ukuba ngikwenzele yona.’ ”",
			"11": "<sup>11</sup> UGadi wayesefika kuDavide, wathi kuye: “UJehova usho kanje, uthi:",
			"12": "<sup>12</sup> ‘Zikhethele noma indlala yeminyaka emithathu, noma ukuchithwa yizitha zakho izinyanga ezintathu uficwa yinkemba yezitha zakho, noma izinsuku ezintathu zenkemba kaJehova nesifo ezweni, ingelosi kaJehova ichithe emikhawulweni yonke yakwa-Israyeli.’ Manje zibonele izwi engiyakulibuyisela kongithumileyo.”",
			"13": "<sup>13</sup> UDavide wathi kuGadi: “Nginosizi kakhulu; mangiwele esandleni sikaJehova, ngokuba umusa wakhe mkhulu kakhulu; kepha esandleni somuntu mangingaweli kuso.”",
			"14": "<sup>14</sup> UJehova wayesethuma isifo phezu kuka-Israyeli; kwafa kwa-Israyeli abantu abayizinkulungwane ezingamashumi ayisikhombisa.",
			"15": "<sup>15</sup> UNkulunkulu wathuma ingelosi eJerusalema ukulichitha; kwathi lapho isalichitha, uJehova wabuka, wazisola ngalobo bubi, wathi engelosini echithayo: “Sekwanele; buyisa isandla sakho.” Ingelosi kaJehova yayimi ngasesibuyeni sika-Orinani umJebusi.",
			"16": "<sup>16</sup> UDavide wayesephakamisa amehlo akhe, wayibona ingelosi kaJehova imi emkhathini womhlaba nezulu, inenkemba ehoshiweyo esandleni sayo, yelulelwe phezu kweJerusalema. UDavide namalunga bembethe izindwangu zamasaka bawa ngobuso babo.",
			"17": "<sup>17</sup> UDavide wathi kuNkulunkulu: “Akusimi yini owayaleza ukuba abantu babalwe na? Yimina engonileyo; ngenze kabi kakhulu, kepha lezi zimvu zenzeni na? Isandla sakho, Jehova Nkulunkulu wami, masibe phezu kwami naphezu kwendlu kababa, singabi phezu kwabantu bakho ukubashaya ngesifo.”",
			"18": "<sup>18</sup> Ingelosi kaJehova yamyala uGadi ukusho kuDavide ukuba uDavide enyuke, amisele uJehova i-altare esibuyeni sika-Orinani umJebusi.",
			"19": "<sup>19</sup> UDavide wenyuka ngezwi likaGadi alikhulumayo egameni likaJehova.",
			"20": "<sup>20</sup> U-Orinani waphenduka, wayibona ingelosi; amadodana akhe amane ayenaye acasha. U-Orinani wayebhula ukolweni.",
			"21": "<sup>21</sup> UDavide esefika ku-Orinani, u-Orinani wabuka, wambona uDavide, waphuma esibuyeni, wakhothamela uDavide, ubuso bakhe bubheke phansi.",
			"22": "<sup>22</sup> UDavide wathi ku-Orinani: “Nginike indawo yalesi sibuya ukuba ngakhele uJehova i-altare kuyo: uyakunginika yona ngemali epheleleyo ukuba lesi sifo sinqamuke kubantu.”",
			"23": "<sup>23</sup> U-Orinani wathi kuDavide: “Zithathele yona; inkosi yami, inkosi, mayenze okuhle emehlweni ayo; bheka, ngiyakupha izinkabi zibe ngezeminikelo yokushiswa, nezibhulo zibe yizinkuni, nokolweni abe ngumnikelo wempuphu; ngikunika konke.”",
			"24": "<sup>24</sup> Inkosi uDavide yathi ku-Orinani: “Qha, ngiyakukuthenga nokukuthenga ngemali epheleleyo, ngokuba angizukumthathela uJehova okungokwakho, nginikele ngomnikelo wokushiswa ngengikuthole ngeze.”",
			"25": "<sup>25</sup> UDavide wayesemnika u-Orinani ngendawo amashekeli* angamakhulu ayisithupha egolide ngesisindo.",
			"26": "<sup>26</sup> UDavide wamakhela khona uJehova i-altare, wanikela ngeminikelo yokushiswa nangeminikelo yokuthula, wambiza uJehova; wamphendula esezulwini ngomlilo e-altare lomnikelo wokushiswa.",
			"27": "<sup>27</sup> UJehova wayiyala ingelosi; yabuyisela inkemba yayo emgodleni wayo.",
			"28": "<sup>28</sup> Ngaleso sikhathi lapho uDavide esebonile ukuthi uJehova umphendulile esibuyeni sika-Orinani umJebusi, wahlaba khona.",
			"29": "<sup>29</sup> Ngokuba itabernakele* likaJehova alenza uMose ehlane ne-altare lomnikelo wokushiswa kwakusendaweni ephakemeyo eGibeyoni ngaleso sikhathi.",
			"30": "<sup>30</sup> Kepha uDavide wayengenakuya phambi kwalo ukubuza kuNkulunkulu, ngokuba wagebhezelela inkemba yengelosi kaJehova."
		},
		"22": {
			"1": "<sup>1</sup> UDavide wathi: “Le iyindlu kaJehova uNkulunkulu, leli liyi-altare lomnikelo wokushiswa ka-Israyeli.”",
			"2": "<sup>2</sup> UDavide wayala ukuba babuthe bonke abafokazi ababe sezweni lakwa-Israyeli, wabeka abakhi ukubaza amatshe axhoziweyo ukuba bakhe indlu kaNkulunkulu.",
			"3": "<sup>3</sup> UDavide walungisa insimbi eningi yezipikili zezivalo zamasango nezokuhlanganisa, kanye nethusi eliningi elingenakulinganiswa,",
			"4": "<sup>4</sup> nemisedari* engenakubalwa, ngokuba amaSidoni nabaseTire baletha imisedari eminingi kuDavide.",
			"5": "<sup>5</sup> UDavide wathi: “USolomoni indodana yami usengumfana, usathambile, nendlu eyakwakhelwa uJehova nga iba nkulu, ibatshazwe, ibe negama nodumo emazweni onke; ngizakuyilungisela.” Wayeseyilungisela kakhulu uDavide engakafi.",
			"6": "<sup>6</sup> Wayesebiza uSolomoni indodana yakhe, wamyala ukumakhela uJehova uNkulunkulu ka-Israyeli indlu.",
			"7": "<sup>7</sup> UDavide wathi kuSolomoni indodana yakhe: “Mina-ke, kwakusenhliziyweni yami ukwakhela igama likaJehova uNkulunkulu wami indlu.",
			"8": "<sup>8</sup> Kepha izwi likaJehova lafika kimi, lathi: ‘Uchithile igazi eliningi, uphake izimpi ezinkulu; awuyikwakhela igama lami indlu, ngokuba uchithe igazi eliningi emhlabathini emehlweni ami;",
			"9": "<sup>9</sup> bheka, uyakuzalelwa indodana eyakuba ngumuntu wokuthula; ngiyakumnika ukuphumula ezitheni zakhe nxazonke, ngokuba igama lakhe liyakuba nguSolomoni;* ukuthula nokuzola ngiyakumnika khona u-Israyeli emihleni yakhe;",
			"10": "<sup>10</sup> uyakwakhela igama lami indlu; uyakuba yindodana yami, mina ngibe nguyise; ngiyakuqinisa isihlalo sobukhosi sombuso wakhe kwa-Israyeli kuze kube phakade.’",
			"11": "<sup>11</sup> “Kalokhu, ndodana yami, uJehova makabe nawe, ube nempumelelo, wakhe indlu kaJehova uNkulunkulu wakho njengokukhuluma kwakhe kuwe.",
			"12": "<sup>12</sup> Kuphela, uJehova makakuphe ukuhlakanipha nokuqonda, akuyale ngo-Israyeli ukuba ugcine umthetho kaJehova uNkulunkulu wakho.",
			"13": "<sup>13</sup> Khona uyakuphumelela, uma ugcina ukwenza izimiso nezahlulelo uJehova ayala ngazo uMose ngo-Israyeli; qina, ume isibindi; ungesabi, ungabi naluvalo.",
			"14": "<sup>14</sup> “Bheka, ekuhluphekeni kwami ngilungisele indlu kaJehova amatalenta* ayizinkulungwane eziyikhulu egolide, namatalenta ayizinkulungwane eziyinkulungwane esiliva, nethusi nensimbi okungenakulinganiswa, ngokuba kuningi; ngilungisile nemithi namatshe; uyakwenezela kukho.",
			"15": "<sup>15</sup> Kukhona kuwe izisebenzi eziningi, ababazi, nezisebenzi zamatshe nezemithi, nabo bonke abayizingcweti emisebenzini yonke:",
			"16": "<sup>16</sup> eyegolide, neyesiliva, neyethusi, neyensimbi, kungenakubalwa; suka usebenze; uJehova makabe nawe.”",
			"17": "<sup>17</sup> UDavide wayala zonke izikhulu zakwa-Israyeli ukusiza uSolomoni, wathi:",
			"18": "<sup>18</sup> “UJehova uNkulunkulu wenu akanani na? Akaniphanga ukuphumula nxazonke na? Ngokuba unikele esandleni sami abakhileyo ezweni; izwe linyatheliwe phambi kukaJehova naphambi kwabantu bakhe.",
			"19": "<sup>19</sup> Kalokhu nikelani inhliziyo yenu nomphefumulo wenu ekufuneni uJehova uNkulunkulu wenu; sukani nakhe indlu engcwele kaJehova uNkulunkulu ukuba kungeniswe umphongolo wesivumelwano sikaJehova nezinto ezingcwele zikaNkulunkulu endlini eyakwakhelwa igama likaJehova.”"
		},
		"23": {
			"1": "<sup>1</sup> UDavide wayesemdala, esanelisiwe yizinsuku; wambeka uSolomoni indodana yakhe abe yinkosi kwa-Israyeli.",
			"2": "<sup>2</sup> Wabutha zonke izikhulu zakwa-Israyeli kanye nabapristi namaLevi.",
			"3": "<sup>3</sup> AmaLevi abalwa kusukela kwaneminyaka engamashumi amathathu kuya phezulu; umumo wawo ngezinhloko zawo, indoda ngendoda, wawuyizinkulungwane ezingamashumi amathathu nesishiyagalombili.",
			"4": "<sup>4</sup> Ayizinkulungwane ezingamashumi amabili nane kuwona ayengawokwengamela umsebenzi wendlu kaJehova; izinkulungwane eziyisithupha zaziyizinduna nabahluleli;",
			"5": "<sup>5</sup> izinkulungwane ezine zazingabalindisango; izinkulungwane ezine ziyakudumisa uJehova ngezinto zokubethwa engazenzayo, washo uDavide, ukuba zidumise ngazo.",
			"6": "<sup>6</sup> UDavide wabahlukanisa ngezigaba njengamadodana kaLevi: oGereshoni, noKohati, noMerari.",
			"7": "<sup>7</sup> KumaGereshoni: ngoLadani noShimeyi.",
			"8": "<sup>8</sup> Amadodana kaLadani: nguJehiyeli inhloko, noZethamu, noJoweli, bebathathu.",
			"9": "<sup>9</sup> Amadodana kaShimeyi: ngoShelomoti, noHaziyeli, noHarana, bebathathu. Labo babe yizinhloko zawoyise bakaLadani.",
			"10": "<sup>10</sup> Amadodana kaShimeyi: ngoJahati, noZina, noJewushe, noBeriya. Labo bobane babe ngamadodana kaShimeyi.",
			"11": "<sup>11</sup> UJahati wayeyinhloko, noZiza ngowesibili; kepha oJewushe noBeriya babengenamadodana amaningi; ngalokho baba yindlu inye kayise, bebalwa kanyekanye.",
			"12": "<sup>12</sup> Amadodana kaKohati: ngo-Amramu, noJisihari, noHebroni, no-Uziyeli, bebane.",
			"13": "<sup>13</sup> Amadodana ka-Amramu: ngo-Aroni noMose; u-Aroni wahlukaniswa ukuba angcwelise izinto ezingcwelengcwele, yena namadodana akhe, kuze kube phakade, ukushisa impepho phambi kukaJehova, nokumkhonza, nokubusisa egameni lakhe kuze kube phakade.",
			"14": "<sup>14</sup> Kepha uMose, lowo muntu kaNkulunkulu, amadodana akhe abizwa ngesizwe sakwaLevi.",
			"15": "<sup>15</sup> Amadodana kaMose: ngoGereshomu no-Eliyezeri.",
			"16": "<sup>16</sup> Amadodana kaGereshomu: nguShebuweli owayeyinhloko.",
			"17": "<sup>17</sup> Amadodana ka-Eliyezeri: nguRehabiya inhloko. U-Eliyezeri wayengenawo amanye amadodana; kepha amadodana kaRehabiya ayemaningi kakhulu.",
			"18": "<sup>18</sup> Amadodana kaJisihari: nguShelomiti inhloko.",
			"19": "<sup>19</sup> Amadodana kaHebroni: nguJeriya inhloko, no-Amariya owesibili, noJahaziyeli owesithathu, noJekameyamu owesine.",
			"20": "<sup>20</sup> Amadodana ka-Uziyeli: nguMika inhloko noJishiya owesibili.",
			"21": "<sup>21</sup> Amadodana kaMerari: ngoMaheli noMushi. Amadodana kaMaheli: ngo-Eleyazare noKishi.",
			"22": "<sup>22</sup> U-Eleyazare wafa engenamadodana, enamadodakazi odwa; abafowabo, amadodana kaKishi, bawathatha.",
			"23": "<sup>23</sup> Amadodana kaMushi: ngoMaheli, no-Edere, noJeremoti, bebathathu.",
			"24": "<sup>24</sup> Yilabo ababe ngamadodana kaLevi ngezindlu zawoyise, izinhloko zawoyise, bebalwa ngomumo wamagama ngezinhloko zabo, abawenza umsebenzi wenkonzo yendlu kaJehova, kusukela kwabaneminyaka engamashumi amabili kuya phezulu.",
			"25": "<sup>25</sup> Ngokuba uDavide wathi: “UJehova uNkulunkulu ka-Israyeli unike abantu bakhe ukuphumula; uhlezi eJerusalema kuze kube phakade;",
			"26": "<sup>26</sup> namaLevi awasayikuthwala itabernakele* nazo zonke izinto zalo zenkonzo yalo.”",
			"27": "<sup>27</sup> Ngokuba amadodana kaLevi abalwa ngamazwi okugcina kaDavide, kusukela kwaneminyaka engamashumi amabili kuya phezulu.",
			"28": "<sup>28</sup> Ngokuba indawo yawo yayingasesandleni samadodana ka-Aroni ukukhonza endlini kaJehova, nasemagcekeni, nasemakamelweni, nokuhlambulula zonke izinto ezingcwele, nomsebenzi wenkonzo endlini kaNkulunkulu,",
			"29": "<sup>29</sup> eyezinkwa zokubukwa, neyempuphu ecolekileyo yomnikelo wempuphu, neyemiqathane engenamvubelo, neyokuphekwe ekhanzini, neyexutshanisiweyo, neyazo zonke izilinganiso nobukhulu;",
			"30": "<sup>30</sup> nokuma njalo ekuseni ukubonga nokudumisa uJehova, kanjalo nakusihlwa;",
			"31": "<sup>31</sup> nokunikela njalo ngayo yonke iminikelo yokushiswa kuJehova ngamasabatha, nangokwethwasa kwenyanga, nanemikhosi emisiweyo ngomumo onjengomthetho wabo, phambi kukaJehova;",
			"32": "<sup>32</sup> bayakugcina imfanelo yetende lokuhlangana, nemfanelo yendawo engcwele, nemfanelo yamadodana ka-Aroni abafowabo ekukhonzeni endlini kaJehova."
		},
		"24": {
			"1": "<sup>1</sup> Nazi izigaba zamadodana ka-Aroni: amadodana ka-Aroni: ngoNadabi, no-Abihu, no-Eleyazare, no-Itamari.",
			"2": "<sup>2</sup> Kepha oNadabi no-Abihu bandulela uyise ukufa, bengenabantwana; ngalokho o-Eleyazare no-Itamari kuphela babe ngabapristi.",
			"3": "<sup>3</sup> ODavide, noSadoki wamadodana ka-Eleyazare, no-Ahimeleki wamadodana ka-Itamari babahlukanisa ngezigaba zabo njengenkonzo yabo.",
			"4": "<sup>4</sup> Kwafunyaniswa emadodaneni ka-Eleyazare amadoda amaningi ayizinhloko kunasemadodaneni ka-Itamari; bahlukaniswa kanje: emadodaneni ka-Eleyazare kwakukhona abayishumi nesithupha abayizinhloko zezindlu zawoyise, nasemadodaneni ka-Itamari babe yisishiyagalombili njengezindlu zawoyise.",
			"5": "<sup>5</sup> Bahlukaniswa ngenkatho, laba nalaba, ngokuba kwakukhona izikhulu zendlu engcwele nezikhulu zikaNkulunkulu emadodaneni ka-Eleyazare nasemadodaneni ka-Itamari.",
			"6": "<sup>6</sup> UShemaya, indodana kaNethaneli, umbhali ongowamaLevi, wabaloba phambi kwenkosi, nezikhulu, noSadoki, nezikhulu, noSadoki umpristi, no-Ahimeleki indodana ka-Abiyathara, nezinhloko zawoyise babapristi namaLevi; enye indlu yawoyise yakhishelwa u-Eleyazare, nenye yakhishelwa u-Itamari.",
			"7": "<sup>7</sup> Inkatho yokuqala yadla uJehoyaribi, neyesibili uJedaya,",
			"8": "<sup>8</sup> neyesithathu uHarimi, neyesine uSehorimi,",
			"9": "<sup>9</sup> neyesihlanu uMalikhiya, neyesithupha uMijamini,",
			"10": "<sup>10</sup> neyesikhombisa uHakhosi, neyesishiyagalombili u-Abiya,",
			"11": "<sup>11</sup> neyesishiyagalolunye uJeshuwa, neyeshumi uShekaniya,",
			"12": "<sup>12</sup> neyeshumi nanye u-Eliyashibi, neyeshumi nambili uJakimi,",
			"13": "<sup>13</sup> neyeshumi nantathu uHupha, neyeshumi nane uJeshebeyabe,",
			"14": "<sup>14</sup> neyeshumi nanhlanu uBiliga, neyeshumi nesithupha u-Imeri,",
			"15": "<sup>15</sup> neyeshumi nesikhombisa uHeziri, neyeshumi nesishiyagalombili uHaphisesi,",
			"16": "<sup>16</sup> neyeshumi nesishiyagalolunye uPhethahiya, neyamashumi amabili uJehezekeli,",
			"17": "<sup>17</sup> neyamashumi amabili nanye uJakini, neyamashumi amabili nambili uGamuli,",
			"18": "<sup>18</sup> neyamashumi amabili nantathu uDelaya, neyamashumi amabili nane uMahaziya.",
			"19": "<sup>19</sup> Lezo zaziyizigaba zabo njengenkonzo yabo ukuba bangene endlini kaJehova njengomthetho ababewunikwe ngesandla sika-Aroni uyise, njengalokho uJehova uNkulunkulu ka-Israyeli ebemyalile.",
			"20": "<sup>20</sup> Abaseleyo emadodaneni kaLevi babe yilaba: emadodaneni ka-Amramu nguShubayeli; emadodaneni kaShubayeli nguJehedeya.",
			"21": "<sup>21</sup> KuRehabiya: emadodaneni kaRehabiya: nguJishiya inhloko.",
			"22": "<sup>22</sup> KumaJisihari: nguShelomoti; emadodaneni kaShelomoti: nguJahati.",
			"23": "<sup>23</sup> Amadodana kaHebroni: nguJeriya inhloko, no-Amariya owesibili, noJahaziyeli owesithathu, noJekameyamu owesine.",
			"24": "<sup>24</sup> Amadodana ka-Uziyeli: nguMika; emadodaneni kaMika: nguShamiri.",
			"25": "<sup>25</sup> Umfowabo kaMika: nguJishiya; emadodaneni kaJishiya: nguZakariya.",
			"26": "<sup>26</sup> Amadodana kaMerari: ngoMaheli noMushi; amadodana kaJahaziya: nguBeno.",
			"27": "<sup>27</sup> Amadodana kaMerari: ngoJahaziya, noBeno, noShohamu, noZakuri, no-Ibiri.",
			"28": "<sup>28</sup> KuMaheli: ngu-Eleyazare, owayengenabantwana.",
			"29": "<sup>29</sup> KuKishi, kungamadodana kaKishi: nguJerameli.",
			"30": "<sup>30</sup> Amadodana kaMushi: ngoMaheli, no-Edere, noJerimoti. Yilabo ababe ngamadodana amaLevi ngezindlu zawoyise.",
			"31": "<sup>31</sup> Nalabo benza inkatho njengabafowabo, amadodana ka-Aroni, ebusweni bukaDavide inkosi, noSadoki, no-Ahimeleki, nezinhloko zawoyise babapristi namaLevi, inhloko yawoyise njengomfowabo omncane."
		},
		"25": {
			"1": "<sup>1</sup> UDavide nezinduna zempi base bahlukanisela inkonzo amanye amadodana ka-Asafa, nakaHemani, nakaJeduthuni, ayeyakuprofetha ngamahabhu,* nangezingubhu, nangamasimbali;* umumo wabantu balo msebenzi wenkonzo wawunje:",
			"2": "<sup>2</sup> emadodaneni ka-Asafa: ngoZakuri, noJosefa, noNethaniya, no-Asharela, amadodana ka-Asafa, phansi kwezandla zika-Asafa owaprofetha ngezandla zenkosi.",
			"3": "<sup>3</sup> KuJeduthuni: amadodana kaJeduthuni: ngoGedaliya, noSeri, noJeshaya, noShimeyi, noHashabiya, noMatithiya, bebayisithupha, phansi kwezandla zikayise uJeduthuni owaprofetha ngehabhu* ngokubonga nangokudumisa uJehova.",
			"4": "<sup>4</sup> KuHemani: amadodana kaHemani: ngoBukiya, noMathaniya, no-Uziyeli, noShebuweli, noJerimoti, noHananiya, noHanani, no-Eliyatha, noGidaliti, noRomamiti Ezeri, noJoshibekasha, noMaloti, noHothiri, noMahaziyoti;",
			"5": "<sup>5</sup> bonke labo babe ngamadodana kaHemani umboni wenkosi ngamazwi kaNkulunkulu okuphakamisa uphondo lwakhe; uNkulunkulu wamnika uHemani amadodana ayishumi nane namadodakazi amathathu.",
			"6": "<sup>6</sup> Onke lawo ayephansi kwezandla zikayise ekuhlabeleleni endlini kaJehova ngamasimbali,* nangezingubhu, nangamahabu* enkonzweni yasendlini kaNkulunkulu, o-Asafa noJeduthuni noHemani bephansi kwezandla zenkosi.",
			"7": "<sup>7</sup> Umumo wabo kanye nabafowabo ababefundisiwe ukuhlabelela kuJehova nabayizazi wawungamakhulu amabili namashumi ayisishiyagalombili nesishiyagalombili.",
			"8": "<sup>8</sup> Benza inkatho ngenkonzo, omunye ngomunye, omncane njengomkhulu, isazi nomfundi.",
			"9": "<sup>9</sup> Inkatho yokuqala yadla uJosefa ka-Asafa, neyesibili uGedaliya; yena nabafowabo namadodana akhe babe yishumi nambili;",
			"10": "<sup>10</sup> eyesithathu uZakuri, namadodana akhe nabafowabo, beyishumi nambili;",
			"11": "<sup>11</sup> eyesine uJisiri, namadodana akhe nabafowabo, beyishumi nambili;",
			"12": "<sup>12</sup> eyesihlanu uNethaniya, namadodana akhe nabafowabo, beyishumi nambili;",
			"13": "<sup>13</sup> eyesithupha uBukiya, namadodana akhe nabafowabo, beyishumi nambili;",
			"14": "<sup>14</sup> eyesikhombisa uJesharela, namadodana akhe nabafowabo, beyishumi nambili;",
			"15": "<sup>15</sup> eyesishiyagalombili uJeshaya, namadodana akhe nabafowabo, beyishumi nambili;",
			"16": "<sup>16</sup> eyesishiyagalolunye uMathaniya, namadodana akhe nabafowabo, beyishumi nambili;",
			"17": "<sup>17</sup> eyeshumi uShimeyi, namadodana akhe nabafowabo, beyishumi nambili;",
			"18": "<sup>18</sup> eyeshumi nanye u-Azareli, namadodana akhe nabafowabo, beyishumi nambili;",
			"19": "<sup>19</sup> eyeshumi nambili uHashabiya, namadodana akhe nabafowabo, beyishumi nambili;",
			"20": "<sup>20</sup> eyeshumi nantathu uShubayeli, namadodana akhe nabafowabo, beyishumi nambili;",
			"21": "<sup>21</sup> eyeshumi nane uMatithiya, namadodana akhe nabafowabo, beyishumi nambili;",
			"22": "<sup>22</sup> eyeshumi nanhlanu uJeremoti, namadodana akhe nabafowabo, beyishumi nambili;",
			"23": "<sup>23</sup> eyeshumi nesithupha uHananiya, namadodana akhe nabafowabo, beyishumi nambili;",
			"24": "<sup>24</sup> eyeshumi nesikhombisa uJoshibekasha, namadodana akhe nabafowabo, beyishumi nambili;",
			"25": "<sup>25</sup> eyeshumi nesishiyagalombili uHanani, namadodana akhe nabafowabo, beyishumi nambili;",
			"26": "<sup>26</sup> eyeshumi nesishiyagalolunye uMaloti, namadodana akhe nabafowabo, beyishumi nambili;",
			"27": "<sup>27</sup> eyamashumi amabili u-Eliyatha, namadodana akhe nabafowabo, beyishumi nambili;",
			"28": "<sup>28</sup> eyamashumi amabili nanye uHothiri, namadodana akhe nabafowabo, beyishumi nambili;",
			"29": "<sup>29</sup> eyamashumi amabili nambili uGidaliti, namadodana akhe nabafowabo, beyishumi nambili;",
			"30": "<sup>30</sup> eyamashumi amabili nantathu uMahaziyoti, namadodana akhe nabafowabo, beyishumi nambili;",
			"31": "<sup>31</sup> eyamashumi amabili nane uRomamiti Ezeri, namadodana akhe nabafowabo, beyishumi nambili."
		},
		"26": {
			"1": "<sup>1</sup> Izigaba zabalindisango zaziyilezi: kumaKorahi: nguMeshelemiya indodana kaKore emadodaneni ka-Asafa.",
			"2": "<sup>2</sup> UMeshelemiya wayenamadodana: nguZakariya izibulo, noJediyayeli owesibili, noZebadiya owesithathu, noJatiniyeli owesine,",
			"3": "<sup>3</sup> no-Elamu owesihlanu, noJehohanani owesithupha, no-Eliyohenayi owesikhombisa.",
			"4": "<sup>4</sup> U-Obede Edomi wayenamadodana: nguShemaya izibulo, noJehozabadi owesibili, noJowa owesithathu, noSakari, owesine, noNethaneli owesihlanu,",
			"5": "<sup>5</sup> no-Amiyeli owesithupha, no-Isakare owesikhombisa, noPhewuletayi owesishiyagalombili, ngokuba uNkulunkulu wambusisa.",
			"6": "<sup>6</sup> Naye uShemaya indodana yakhe wazalelwa amadodana ayebusa indlu kayise, ngokuba ayengamaqhawe anamandla.",
			"7": "<sup>7</sup> Amadodana kaShemaya: ngo-Othini, noRefayeli, no-Obede, no-Elizabadi, nabafowabo ababe ngamadoda anamandla, o-Elihu noSemakiya.",
			"8": "<sup>8</sup> Bonke labo babe ngamadodana ka-Obede Edomi, bona namadodana abo nabafowabo, amadoda aqinileyo anamandla enkonzweni, engamashumi ayisithupha nambili ka-Obede Edomi.",
			"9": "<sup>9</sup> UMeshelemiya wayenamadodana nabafowabo, amadoda anamandla, eyishumi nesishiyagalombili.",
			"10": "<sup>10</sup> Naye uHosa wabantwana bakwaMerari wayenamadodana: nguShimri inhloko noma wayengesilo izibulo, uyise wambeka waba yinhloko",
			"11": "<sup>11</sup> noHilikiya owesibili, noThebaliya owesithathu, noZakariya owesine; onke amadodana nabafowabo bakaHosa babe yishumi nantathu.",
			"12": "<sup>12</sup> Lezo zigaba zabalindisango, izinhloko zabantu, zaba nemfanelo yazo njengabafowabo ukukhonza endlini kaJehova.",
			"13": "<sup>13</sup> Benza inkatho yesango ngesango, omncane njengomkhulu, ngezindlu zawoyise.",
			"14": "<sup>14</sup> Inkatho yasempumalanga yadla uShelemiya. Base benza inkatho ngoZakariya indodana yakhe, umluleki ohlakaniphileyo; wadliwa yinkatho yasenyakatho.",
			"15": "<sup>15</sup> Eyaseningizimu yadla u-Obede Edomi namadodana akhe ukulinda inqolobane,",
			"16": "<sup>16</sup> eyasentshonalanga oShuphimi noHosa ngasesangweni laseShaleketi, ngasemendweni owenyukayo, abalindi ngabalindi.",
			"17": "<sup>17</sup> Ngasempumalanga ayekhona amaLevi ayisithupha, ngasenyakatho emane ngosuku, ngaseningizimu emane ngosuku, nasenqolobaneni emabili ngamabili.",
			"18": "<sup>18</sup> EParibari ngasentshonalanga ayemane emendweni, naseParibari uqobo ayemabili.",
			"19": "<sup>19</sup> Yilezo ezaziyizigaba zabalindisango emadodaneni amaKorahi nasemadodaneni kaMerari.",
			"20": "<sup>20</sup> KumaLevi u-Ahiya wayephethe ingcebo yendlu kaNkulunkulu nengcebo yezinto ezingcwele.",
			"21": "<sup>21</sup> Amadodana kaLadani, amadodana kaGereshoni akwaLadani, izinhloko zawoyise zakwaLadani umGereshoni, amaJehiyeli,",
			"22": "<sup>22</sup> amadodana amaJehiyeli, oZethamu noJoweli umfowabo, ayephethe ingcebo endlini kaJehova.",
			"23": "<sup>23</sup> Kuma-Amramu, nakumaJisihari, nakumaHebroni, nakuma-Uziyeli,",
			"24": "<sup>24</sup> uShebuweli indodana kaGereshomu kaMose wayengumphathisikhwama.",
			"25": "<sup>25</sup> Abafowabo: ku-Eliyezeri: nguRehabiya indodana yakhe, noJeshaya indodana yakhe, noJoramu indodana yakhe, noZikiri indodana yakhe, noShelomoti indodana yakhe.",
			"26": "<sup>26</sup> Yena uShelomoti nabafowabo babephethe yonke ingcebo yezinto ezingcwele ezangcweliswa nguDavide inkosi, nezinhloko zawoyise, nezinduna zezinkulungwane nezamakhulu, nezinduna zempi.",
			"27": "<sup>27</sup> Empini nasempangweni bakungcwelisela ukulungisa indlu kaJehova,",
			"28": "<sup>28</sup> kanye nakho konke uSamuweli umboni, noSawule indodana kaKishi, no-Abineri indodana kaNeri, noJowabe indodana kaSeruya ababekungcwelisile; yilowo nalowo owayengcwelisile okuthile, kwaba phansi kwesandla sikaShelomoti nabafowabo.",
			"29": "<sup>29</sup> KumaJisihari uKhenaniya namadodana akhe babephethe umsebenzi ongaphandle kwa-Israyeli, beyizinduna nabahluleli.",
			"30": "<sup>30</sup> KumaHebroni uHashabiya nabafowabo, amaqhawe anamandla, beyinkulungwane namakhulu ayisikhombisa, babe ngababonisi kwa-Israyeli phesheya kweJordani ngasentshonalanga emsebenzini wonke kaJehova nasenkonzweni yenkosi.",
			"31": "<sup>31</sup> KumaHebroni uJeriya wayeyinhloko, kumaHebroni njengezizukulwane zawo ngezindlu zawoyise. Ngomnyaka wamashumi amane wokubusa kukaDavide kwafunwa, kwafunyanwa phakathi kwawo amaqhawe anamandla eJazeri kwaGileyadi.",
			"32": "<sup>32</sup> Abafowabo, amaqhawe anamandla, babe yizinkulungwane ezimbili namakhulu ayisikhombisa, izinhloko zawoyise, ezenziwa ababonisi phezu kwabakwaRubeni nabakwaGadi nenxenye yesizwe sabakwaManase ezindabeni zonke zikaNkulunkulu nasezindabeni zenkosi."
		},
		"27": {
			"1": "<sup>1</sup> Nampa abantwana bakwa-Israyeli ngomumo wabo, izinhloko zawoyise, nezinduna zezinkulungwane nezamakhulu, nezinduna zabo ezazikhonza inkosi kukho konke okwezigaba, ezazingena, ziphuma inyanga ngenyanga ezinyangeni zonke zomnyaka; kuleso naleso sigaba zazikhona izinkulungwane ezingamashumi amabili nane.",
			"2": "<sup>2</sup> Phezu kwesigaba sokuqala ngenyanga yokuqala kwakunguJashobehamu indodana kaZabidiyeli; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"3": "<sup>3</sup> Wayengowabantwana bakwaPheresi, inhloko yazo zonke izinduna zempi ngenyanga yokuqala.",
			"4": "<sup>4</sup> Phezu kwesigaba senyanga yesibili kwakunguDodayi umAhohi nesigaba sakhe; kwakukhona nombusi uMikiloti; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"5": "<sup>5</sup> Induna yesithathu yempi ngenyanga yesithathu kwakunguBenaya indodana kaJehoyada umpristi, inhloko; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"6": "<sup>6</sup> Nguye lowo Benaya owayeyiqhawe labangamashumi amathathu nowayephezu kwamashumi amathathu; owesigaba sakhe kwakungu-Amizabadi indodana yakhe.",
			"7": "<sup>7</sup> Eyesine ngenyanga yesine kwakungu-Asaheli umfowabo kaJowabe noZebadiya indodana yakhe emva kwakhe; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"8": "<sup>8</sup> Eyesihlanu ngenyanga yesihlanu kwakunguShamhuti umJizirahi; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"9": "<sup>9</sup> Eyesithupha ngenyanga yesithupha kwakungu-Ira indodana ka-Ikheshi waseThekhowa; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"10": "<sup>10</sup> Eyesikhombisa ngenyanga yesikhombisa kwakunguHelesi umPheloni wabantwana bakwa-Efrayimi; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"11": "<sup>11</sup> Eyesishiyagalombili ngenyanga yesishiyagalombili kwakunguSibekayi umHushati kumaZera; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"12": "<sup>12</sup> Eyesishiyagalolunye ngenyanga yesishiyagalolunye kwakungu-Abiyezeri umAnathoti wabakwaBenjamini; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"13": "<sup>13</sup> Eyeshumi ngenyanga yeshumi kwakunguMaharayi waseNethofa kumaZera; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"14": "<sup>14</sup> Eyeshumi nanye ngenyanga yeshumi nanye kwakunguBenaya umPirathoni wabantwana bakwa-Efrayimi; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"15": "<sup>15</sup> Eyeshumi nambili ngenyanga yeshumi nambili kwakunguHelidayi waseNethofa ka-Otheniyeli; esigabeni sakhe kwakukhona izinkulungwane ezingamashumi amabili nane.",
			"16": "<sup>16</sup> Phezu kwezizwe zakwa-Israyeli kwakuyilaba: kwabakwaRubeni u-Eliyazeri indodana kaZikiri wayengumbusi; kwabakwaSimeyoni nguShefathiya indodana kaMahakha;",
			"17": "<sup>17</sup> kuLevi nguHashabiya indodana kaKemuweli; ku-Aroni nguSadoki;",
			"18": "<sup>18</sup> kuJuda ngu-Elihu wabafowabo bakaDavide; ku-Isakare ngu-Omri indodana kaMikayeli;",
			"19": "<sup>19</sup> kuZebuloni ngu-Ishmaya indodana ka-Obadiya; kuNafetali nguJeremoti indodana ka-Aziriyeli;",
			"20": "<sup>20</sup> kubantwana bakwa-Efrayimi nguHosheya indodana ka-Azaziya; enxenyeni yesizwe sakwaManase nguJoweli indodana kaPhedaya;",
			"21": "<sup>21</sup> enxenyeni kaManase wakwaGileyadi ngu-Ido indodana kaZakariya; kuBenjamini nguJahasiyeli indodana ka-Abineri;",
			"22": "<sup>22</sup> kuDani ngu-Azareli indodana kaJerohamu. Yilabo ababe yizinduna zezizwe zakwa-Israyeli.",
			"23": "<sup>23</sup> Kepha uDavide akabalanga umumo waba neminyaka engamashumi amabili nabangaphansi, ngokuba uJehova wayeshilo ukuthi uyakwandisa u-Israyeli ukuba abe ngangezinkanyezi zezulu.",
			"24": "<sup>24</sup> UJowabe indodana kaSeruya waqala ukubala, kepha akaqedanga; kwaba khona ulaka ngalokho phezu kuka-Israyeli, nomumo awufakwanga encwadini yezindaba zemihla ngemihla yenkosi uDavide.",
			"25": "<sup>25</sup> U-Azimaveti indodana ka-Adiyeli wayephethe ingcebo yenkosi; uJonathani indodana ka-Uziya wayephethe izinto emasimini, nasemizini, nasemizaneni, nasezinqabeni;",
			"26": "<sup>26</sup> u-Eziri indodana kaKelubi wayephethe abenza umsebenzi wasendle wokulima umhlabathi;",
			"27": "<sup>27</sup> uShimeyi waseRama wayephethe izivini; uZabidi waseShefamu wayephethe iwayini elibekelelweyo lithathwe ezivinini;",
			"28": "<sup>28</sup> uBali Hanani umGederi wayephethe iminqumo nemikhiwane eyayisehlanzeni; uJowashi wayephethe imigodi yamafutha;",
			"29": "<sup>29</sup> imihlambi eyayiklaba eSharoni yayiphethwe nguShitirayi umSharoni, nemihlambi eyayisezigodini yayiphethwe nguShafati indodana ka-Adilayi;",
			"30": "<sup>30</sup> u-Obili wakwa-Ishmayeli wayephethe amakamela; uJehedeya umMeronoti wayephethe izimbongolo;",
			"31": "<sup>31</sup> uJazizi umHagiri wayephethe izimvu. Bonke labo babe yizinduna zemfuyo yenkosi uDavide.",
			"32": "<sup>32</sup> UJonathani, uyisekazi kaDavide, wayengumluleki, wayengumuntu ohlakaniphileyo nombhali; uJehiyeli indodana kaHakimoni wayenamadodana enkosi;",
			"33": "<sup>33</sup> u-Ahithofeli wayengumluleki wenkosi; uHushayi umArki wayengumngane wenkosi;",
			"34": "<sup>34</sup> emva kuka-Ahithofeli kwakunguJehoyada indodana kaBenaya no-Abiyathara; uJowabe wayeyinduna yempi yenkosi."
		},
		"28": {
			"1": "<sup>1</sup> UDavide wabutha zonke izikhulu zakwa-Israyeli, izikhulu zezizwe, nezinduna zezigaba ezazikhonza inkosi, nezinduna zezinkulungwane nezamakhulu, nezinduna zayo yonke imfuyo yenkosi neyamadodana ayo, nezinduna zendlunkulu, namaqhawe, nawo onke amadoda anamandla eJerusalema.",
			"2": "<sup>2</sup> UDavide inkosi wayesema ngezinyawo, wathi: “Ngizweni, bafowethu nabantu bakwethu: mina-ke, kwakusenhliziyweni yami ukwakhela umphongolo wesivumelwano sikaJehova indlu yokuphumula neyesenabelo sikaNkulunkulu wethu; ngangizilungiselele ukwakha.",
			"3": "<sup>3</sup> Kepha wayesethi uNkulunkulu kimi: ‘Awuyikwakhela igama lami indlu, ngokuba uyindoda yempi, uchithile igazi.’",
			"4": "<sup>4</sup> “Kodwa uJehova uNkulunkulu ka-Israyeli wangikhetha endlini yonke kababa ukuba ngibe yinkosi kwa-Israyeli kuze kube phakade; ngokuba ukhethile uJuda ukuba abe ngumbusi, nasendlini kaJuda indlu kababa, naphakathi kwamadodana kababa wangenamela mina ukuba angibeke inkosi kwa-Israyeli wonke,",
			"5": "<sup>5</sup> nasemadodaneni ami onke, ngokuba uJehova ungiphe amadodana amaningi, ukhethe uSolomoni indodana yami ukuba ahlale esihlalweni sobukhosi sombuso kaJehova kwa-Israyeli.",
			"6": "<sup>6</sup> Wathi kimi: ‘USolomoni indodana yakho nguye oyakwakha indlu yami namagceke ami, ngokuba ngimkhethile ukuba abe yindodana yami, mina ngibe nguyise.",
			"7": "<sup>7</sup> Ngiyakuqinisa umbuso wakhe kuze kube phakade, uma eqinisela ukwenza imiyalo yami nezahlulelo zami njenganamuhla.’",
			"8": "<sup>8</sup> “Ngalokho emehlweni abo bonke abakwa-Israyeli, ibandla likaJehova, nasezindlebeni zikaNkulunkulu wethu funani nigcine yonke imiyalo kaJehova uNkulunkulu wenu ukuba nidle leli zwe elihle, nilishiye libe yifa labantwana benu emva kwenu kuze kube phakade.",
			"9": "<sup>9</sup> “Wena Solomoni, ndodana yami, yazi uNkulunkulu kayihlo, umkhonze ngenhliziyo epheleleyo nangenhliziyo ethokozayo, ngokuba uJehova uhlola izinhliziyo zonke, uqonda zonke izizindlo zemicabango; uma umfuna, uyakufunyanwa nguye; kepha uma umshiya, uyakukulahla kuze kube phakade.",
			"10": "<sup>10</sup> Bheka manje, ngokuba uJehova ekukhethile ukwakha indlu ibe yindlu engcwele, qina, ukwenze.”",
			"11": "<sup>11</sup> UDavide wamnika uSolomoni indodana yakhe isifanekiso sompheme nesezindlu zethempeli, nesengcebo yalo, nesamakamelo alo aphezulu, nesamakamelo alo angaphakathi, nesendlu yesihlalo somusa;",
			"12": "<sup>12</sup> nesifanekiso sakho konke ayenakho emicabangweni yakhe ngamagceke endlu kaJehova, nangamakamelo nxazonke, nangengcebo yendlu kaNkulunkulu, nangengcebo yezinto ezingcwele;",
			"13": "<sup>13</sup> nangezigaba zabapristi nezamaLevi, nangawo wonke umsebenzi wenkonzo yendlu kaJehova, nangazo zonke izitsha zenkonzo endlini kaJehova;",
			"14": "<sup>14</sup> nangegolide isisindo segolide lezinto zonke zezinkonzo ngezinkonzo, nangezinto zonke zesiliva isisindo sazo, zonke izinto zezinkonzo ngezinkonzo;",
			"15": "<sup>15</sup> nangesisindo sezinti zezibani zegolide kanye nezibani zazo zegolide, nesisindo salolo nalolo luthi lwesibani kanye nezibani zalo, nangesezinti zesiliva isisindo sothi kanye nezibani zalo njengomsebenzi walolo nalolo luthi;",
			"16": "<sup>16</sup> nangesisindo segolide lamatafula ezinkwa zokubukwa, amatafula ngamatafula, nangesiliva lamatafula esiliva;",
			"17": "<sup>17</sup> nangezingwegwe zenyama, nangezinkamba, nangezitsha zegolide elicwengekileyo, nangezindebe zonke zegolide, isisindo saleso naleso sitsha;",
			"18": "<sup>18</sup> nange-altare lempepho legolide elicwengekileyo isisindo salo, nesifanekiso senqola, namakherubi* egolide ayelula amaphiko awo, asibekela umphongolo wesivumelwano sikaJehova.",
			"19": "<sup>19</sup> “Konke lokho,” washo uDavide, “ngikuqondisiwe ngokubhalwe ngesandla sikaJehova, yebo, wonke umsebenzi walesi sifanekiso.”",
			"20": "<sup>20</sup> Wayesethi uDavide kuSolomoni indodana yakhe: “Qina, ume isibindi, ukwenze; ungesabi, ungabi naluvalo, ngokuba uJehova uNkulunkulu, yebo, uNkulunkulu wami, unawe; akayikukuyeka, akayikukushiya uze uphele wonke umsebenzi wenkonzo yendlu kaJehova.",
			"21": "<sup>21</sup> Nazi izigaba zabapristi nezamaLevi zazo zonke izinkonzo zendlu kaNkulunkulu; kuyakuba nawe emsebenzini wonke abantu abazinikela ngenhliziyo naba yizingcweti ezinkonzweni zonke; nezikhulu nabantu bonke bayakulalela konke okukhulumayo.”"
		},
		"29": {
			"1": "<sup>1</sup> UDavide inkosi wathi kulo lonke ibandla: “USolomoni indodana yami, oyedwa uNkulunkulu amkhethileyo, usengumfana, usathambile, nomsebenzi mkhulu, ngokuba ithempeli alisilo elomuntu kepha ngelikaJehova uNkulunkulu.",
			"2": "<sup>2</sup> Sengilungisele indlu kaNkulunkulu wami ngamandla ami onke igolide lokwegolide, nesiliva lokwesiliva, nethusi lokwethusi, nensimbi yokwensimbi, nemithi yokwemithi, namatshe amashohamu* nawokufakwa, namatshe ahlobisayo nanemibala eyahlukeneyo, nezinhlobo zonke zamatshe ayigugu, namatshe amhlophe amaningi.",
			"3": "<sup>3</sup> Futhi njengokuba ngiyenamela indlu kaNkulunkulu wami, nginengcebo yegolide nesiliva, ngiyanika indlu kaNkulunkulu wami yona phezu kwakho konke engikulungisele indlu engcwele,",
			"4": "<sup>4</sup> amatalenta* ayizinkulungwane ezintathu egolide, igolide lase-Ofiri, namatalenta ayizinkulungwane eziyisikhombisa esiliva elicwengekileyo ukunameka ngalo izindonga zezindlu,",
			"5": "<sup>5</sup> igolide lokwegolide, isiliva lokwesiliva, yebo, kuwo wonke umsebenzi wezandla zezingcweti. Ngubani, pho, othokozela namuhla ukuzahlukanisela uJehova na?”",
			"6": "<sup>6</sup> Zase zinikela ngenhliziyo izikhulu zawoyise, nezikhulu zezizwe zakwa-Israyeli, nezinduna zezinkulungwane nezamakhulu kanye nezinduna zomsebenzi wenkosi;",
			"7": "<sup>7</sup> zanikela enkonzweni yendlu kaNkulunkulu amatalenta* ayizinkulungwane eziyisihlanu egolide, namadarike* ayizinkulungwane eziyishumi, namatalenta ayizinkulungwane eziyishumi esiliva, namatalenta ayizinkulungwane eziyishumi nesishiyagalombili ethusi, namatalenta ayizinkulungwane eziyikhulu ensimbi.",
			"8": "<sup>8</sup> Ababe namatshe bawanikela engcebeni yendlu kaJehova phansi kwesandla sikaJehiyeli umGereshoni.",
			"9": "<sup>9</sup> Abantu bajabula ngokunikela kwabo ngenhliziyo, ngokuba banikela ngenhliziyo epheleleyo kuJehova; naye uDavide inkosi wajabula ngokujabula okukhulu.",
			"10": "<sup>10</sup> UDavide wayesebonga uJehova phambi kwebandla lonke; uDavide wathi: “Mawubongwe wena Jehova Nkulunkulu ka-Israyeli ubaba kuze kube phakade.",
			"11": "<sup>11</sup> Ngobakho, Jehova, ubukhulu, namandla, nodumo, nenkazimulo, nobukhosi, ngokuba konke okusezulwini nasemhlabeni kungokwakho; ngowakho umbuso, Jehova; wena uphakeme, uyinhloko phezu kwakho konke.",
			"12": "<sup>12</sup> Ingcebo nenkazimulo kuvela kuwe, wena ubusa phezu kwakho konke; esandleni sakho kukhona amandla nezikhwepha, esandleni sakho kukhona ukukhulisa nokunika bonke amandla.",
			"13": "<sup>13</sup> Ngalokho, Nkulunkulu wethu, siyakubonga, siyadumisa igama lakho elitusekayo.",
			"14": "<sup>14</sup> “Ngokuba ngingubani mina, nabantu bami, bayini ukuba sibe namandla okunikela ngenhliziyo engaka na? Ngokuba konke kuvela kuwe, nokuvela esandleni sakho lokhu sikunike wena.",
			"15": "<sup>15</sup> Ngokuba singabafokazi phambi kwakho nezihambi njengabo bonke obaba; izinsuku zethu emhlabeni zinjengethunzi, nethemba alikho.",
			"16": "<sup>16</sup> Jehova Nkulunkulu wethu, zonke lezi zinto esizilungisele ukwakhela igama lakho elingcwele indlu zivela esandleni sakho, zonke zingezakho.",
			"17": "<sup>17</sup> Ngiyazi, Nkulunkulu wami, ukuthi uhlola inhliziyo, uthanda ubuqotho. Mina-ke ngobuqotho benhliziyo yami nginikele ngokuthokoza zonke lezi zinto, futhi sengibonile ngokujabula abantu bakho abakhona lapha ukuthi nabo banikele kuwe ngokuthokoza.",
			"18": "<sup>18</sup> Jehova, Nkulunkulu ka-Abrahama noka-Isaka noka-Israyeli, obaba, gcina lokhu njalo ezizindlweni zemicabango yenhliziyo yabantu bakho, uqondisele inhliziyo yabo kuwe,",
			"19": "<sup>19</sup> unike uSolomoni indodana yami inhliziyo epheleleyo ukuba agcine imiyalo yakho, nobufakazi bakho, nezimiso zakho, nokwenza konke lokhu, nokwakha ithempeli engililungiseleyo.”",
			"20": "<sup>20</sup> UDavide wathi kulo lonke ibandla: “Ake nibonge uJehova uNkulunkulu wenu.” Lonke ibandla lambonga uJehova uNkulunkulu wawoyise, lakhothama, lakhuleka kuJehova nasenkosini.",
			"21": "<sup>21</sup> Bamhlabela uJehova imihlatshelo, banikela ngeminikelo yokushiswa kuJehova ngangomuso emva kwalolo suku, izinkunzi eziyinkulungwane, nezinqama eziyinkulungwane, namawundlu ayinkulungwane kanye neminikelo yakho yokuphuzwa, nemihlatshelo eminingi ngo-Israyeli wonke;",
			"22": "<sup>22</sup> badla, baphuza phambi kukaJehova ngalolo suku ngokuthokoza okukhulu. Bambeka uSolomoni indodana kaDavide inkosi ngokwesibili, bamgcobela uJehova ukuba abe ngumholi, uSadoki abe ngumpristi.",
			"23": "<sup>23</sup> USolomoni wayesehlala esihlalweni sobukhosi sikaJehova njengenkosi esikhundleni sikaDavide uyise, waba nenhlanhla; wonke u-Israyeli wamlalela.",
			"24": "<sup>24</sup> Zonke izikhulu, namaqhawe, nawo onke amadodana enkosi uDavide bazithoba phansi kukaSolomoni inkosi.",
			"25": "<sup>25</sup> UJehova wamkhulisa uSolomoni kakhulu emehlweni abo bonke abakwa-Israyeli, wamnika udumo lobukhosi olungazange lube khona nakuyiphi inkosi yakwa-Israyeli phambi kwakhe.",
			"26": "<sup>26</sup> UDavide indodana kaJese wayebusile phezu kwabo bonke abakwa-Israyeli.",
			"27": "<sup>27</sup> Isikhathi abusa ngaso phezu kuka-Israyeli sasiyiminyaka engamashumi amane; iminyaka eyisikhombisa wabusa eHebroni, nengamashumi amathathu nantathu wabusa eJerusalema.",
			"28": "<sup>28</sup> Wafa esemdala, esanelisiwe yizinsuku nengcebo nodumo; uSolomoni indodana yakhe waba yinkosi esikhundleni sakhe.",
			"29": "<sup>29</sup> Izenzo zikaDavide inkosi, ezokuqala nezokugcina, bheka, zilotshiwe encwadini yezindaba zikaSamuweli umboni, nasencwadini yezindaba zikaNathani umprofethi, nasencwadini yezindaba zikaGadi umboni,",
			"30": "<sup>30</sup> kanye nakho konke ukubusa kwakhe, namandla akhe, nezehlo ezamehlelayo yena no-Israyeli, kanye nayo yonke imibuso namazwe."
		}
	}
};
module.exports = book;